qurandict2 = {'1': [{'chapter': 1, 'verse': 1, 'text': 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\nМеҳрибон ва раҳмли Аллоҳнинг номи билан бошлайман'}, {'chapter': 1, 'verse': 2, 'text': 'ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nҲамд оламларнинг Робби — Аллоҳгадир'}, {'chapter': 1, 'verse': 3, 'text': 'ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\nУ Роҳман ва Роҳиймдир'}, {'chapter': 1, 'verse': 4, 'text': 'مَٰلِكِ يَوۡمِ ٱلدِّينِ\nЖазо-мукофот кунининг Эгасидир'}, {'chapter': 1, 'verse': 5, 'text': 'إِيَّاكَ نَعۡبُدُ وَإِيَّاكَ نَسۡتَعِينُ\nФақат Сенгагина ибодат қиламиз ва фақат Сендангина ёрдам сўраймиз'}, {'chapter': 1, 'verse': 6, 'text': 'ٱهۡدِنَا ٱلصِّرَٰطَ ٱلۡمُسۡتَقِيمَ\nБизни тўғри йўлга ҳидоят қилгин'}, {'chapter': 1, 'verse': 7, 'text': 'صِرَٰطَ ٱلَّذِينَ أَنۡعَمۡتَ عَلَيۡهِمۡ غَيۡرِ ٱلۡمَغۡضُوبِ عَلَيۡهِمۡ وَلَا ٱلضَّآلِّينَ\nЎзинг неъмат берганларнинг йўлига. Ғазабга қолганларникига ҳам эмас, адашганларникига ҳам эмас'}], '2': [{'chapter': 2, 'verse': 1, 'text': 'الٓمٓ\nАлиф. Лаам. Мийм'}, {'chapter': 2, 'verse': 2, 'text': 'ذَٰلِكَ ٱلۡكِتَٰبُ لَا رَيۡبَۛ فِيهِۛ هُدٗى لِّلۡمُتَّقِينَ\nБу китобда шак-шубҳа йўқ, у тақводорларга ҳидоятдир'}, {'chapter': 2, 'verse': 3, 'text': 'ٱلَّذِينَ يُؤۡمِنُونَ بِٱلۡغَيۡبِ وَيُقِيمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар ғайбга иймон келтирурлар, намозни тўкис ўқирлар ва Биз уларга берган ризқдан нафақа қилурлар'}, {'chapter': 2, 'verse': 4, 'text': 'وَٱلَّذِينَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ وَبِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ\nВа улар сенга ва сендан олдин нозил қилинган нарсага иймон келтирарлар ва охиратга аниқ ишонарлар'}, {'chapter': 2, 'verse': 5, 'text': 'أُوْلَـٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nАна ўшалар Роббиларидан бўлган ҳидоятдадирлар ва ана ўшалар, ўшаларгина нажот топгувчилардир'}, {'chapter': 2, 'verse': 6, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ سَوَآءٌ عَلَيۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ\nАлбатта, куфр келтирганларга қўрқитасанми-қўрқитмайсанми — бари бир, иймон келтирмаслар'}, {'chapter': 2, 'verse': 7, 'text': 'خَتَمَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَعَلَىٰ سَمۡعِهِمۡۖ وَعَلَىٰٓ أَبۡصَٰرِهِمۡ غِشَٰوَةٞۖ وَلَهُمۡ عَذَابٌ عَظِيمٞ\nАллоҳ уларнинг қалблари ва қулоқларини муҳрлаб қўйган ва кўзларида парда бор. Ва уларга улкан азоб бордир'}, {'chapter': 2, 'verse': 8, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ وَبِٱلۡيَوۡمِ ٱلۡأٓخِرِ وَمَا هُم بِمُؤۡمِنِينَ\nВа одамлардан, Аллоҳга ва охират кунига иймон келтирдик, дейдиганлари бор. Ҳолбуки, улар мўмин эмаслар'}, {'chapter': 2, 'verse': 9, 'text': 'يُخَٰدِعُونَ ٱللَّهَ وَٱلَّذِينَ ءَامَنُواْ وَمَا يَخۡدَعُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ\nУлар Аллоҳни ва иймон келтирганларни алдамоқчи бўларлар. Ва ҳолбуки, сезмасдан ўзларини алдарлар'}, {'chapter': 2, 'verse': 10, 'text': 'فِي قُلُوبِهِم مَّرَضٞ فَزَادَهُمُ ٱللَّهُ مَرَضٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡذِبُونَ\nУларнинг қалбларида касал бор. Аллоҳ касалларини зиёда қилди. Ва уларга ёлғон гапирганлари учун аламли азоб бор'}, {'chapter': 2, 'verse': 11, 'text': 'وَإِذَا قِيلَ لَهُمۡ لَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ قَالُوٓاْ إِنَّمَا نَحۡنُ مُصۡلِحُونَ\nВа агар уларга, ер юзида фасод қилманг, дейилса, биз фақат ислоҳ қилувчилармиз, дерлар'}, {'chapter': 2, 'verse': 12, 'text': 'أَلَآ إِنَّهُمۡ هُمُ ٱلۡمُفۡسِدُونَ وَلَٰكِن لَّا يَشۡعُرُونَ\nОгоҳ бўлинг, улар, фақат улар фасодчилардир, лекин сезмаслар'}, {'chapter': 2, 'verse': 13, 'text': 'وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ كَمَآ ءَامَنَ ٱلنَّاسُ قَالُوٓاْ أَنُؤۡمِنُ كَمَآ ءَامَنَ ٱلسُّفَهَآءُۗ أَلَآ إِنَّهُمۡ هُمُ ٱلسُّفَهَآءُ وَلَٰكِن لَّا يَعۡلَمُونَ\nВа агар уларга, одамлар иймон келтирганидек иймон келтиринглар, дейилса, «Эси пастлар иймон келтирганидек иймон келтирамизми?» дерлар. Огоҳ бўлинг, улар, фақат улар эси пастлардир ва лекин ўзлари билмаслар'}, {'chapter': 2, 'verse': 14, 'text': 'وَإِذَا لَقُواْ ٱلَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ إِلَىٰ شَيَٰطِينِهِمۡ قَالُوٓاْ إِنَّا مَعَكُمۡ إِنَّمَا نَحۡنُ مُسۡتَهۡزِءُونَ\nУлар иймон келтирганларни учратсалар, иймон келтирдик, дерлар, шайтонлари билан холи қолганда эса, биз сизлар биланмиз, фақат истеҳзо қиляпмиз, холос, дерлар'}, {'chapter': 2, 'verse': 15, 'text': 'ٱللَّهُ يَسۡتَهۡزِئُ بِهِمۡ وَيَمُدُّهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАллоҳ уларни истеҳзо қиладир ва туғёнларида довдирашларига қўйиб берадир'}, {'chapter': 2, 'verse': 16, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلضَّلَٰلَةَ بِٱلۡهُدَىٰ فَمَا رَبِحَت تِّجَٰرَتُهُمۡ وَمَا كَانُواْ مُهۡتَدِينَ\nАна ўшалар залолатни ҳидоятга сотиб олганлардир. Ва тижоратлари фойда келтирмади ҳамда улар ҳидоят топганлардан бўлмадилар'}, {'chapter': 2, 'verse': 17, 'text': 'مَثَلُهُمۡ كَمَثَلِ ٱلَّذِي ٱسۡتَوۡقَدَ نَارٗا فَلَمَّآ أَضَآءَتۡ مَا حَوۡلَهُۥ ذَهَبَ ٱللَّهُ بِنُورِهِمۡ وَتَرَكَهُمۡ فِي ظُلُمَٰتٖ لَّا يُبۡصِرُونَ\nУлар, мисоли, ёққан ўти энди атрофини ёритганида, Аллоҳ нурларини кетказиб, қоронғу зулматларда қолдирган кишиларга ўхшарлар. Улар кўрмаслар'}, {'chapter': 2, 'verse': 18, 'text': 'صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَرۡجِعُونَ\nУлар кар, соқов, кўрдирлар. Бас, улар қайтмаслар'}, {'chapter': 2, 'verse': 19, 'text': 'أَوۡ كَصَيِّبٖ مِّنَ ٱلسَّمَآءِ فِيهِ ظُلُمَٰتٞ وَرَعۡدٞ وَبَرۡقٞ يَجۡعَلُونَ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِم مِّنَ ٱلصَّوَٰعِقِ حَذَرَ ٱلۡمَوۡتِۚ وَٱللَّهُ مُحِيطُۢ بِٱلۡكَٰفِرِينَ\nЁки худди осмондан ёмғир ёғиб, қоронғу босиб, момақалдироқ ва чақмоқ чаққанда, яшиндан қочиб, панжаларини қулоқларига тиқиб, ўлимдан эҳтиёт бўлаётган одамга ўхшарлар. Ва Аллоҳ кофирларни ўраб олувчидир'}, {'chapter': 2, 'verse': 20, 'text': 'يَكَادُ ٱلۡبَرۡقُ يَخۡطَفُ أَبۡصَٰرَهُمۡۖ كُلَّمَآ أَضَآءَ لَهُم مَّشَوۡاْ فِيهِ وَإِذَآ أَظۡلَمَ عَلَيۡهِمۡ قَامُواْۚ وَلَوۡ شَآءَ ٱللَّهُ لَذَهَبَ بِسَمۡعِهِمۡ وَأَبۡصَٰرِهِمۡۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЧақмоқ уларнинг кўзини олиб қўяй дейди. Ёришган пайтда юришар, қоронғи бўлса, тўхташар. Агар Аллоҳ хоҳласа, уларнинг кўзу қулоқларини кетказур. Албатта, Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 2, 'verse': 21, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱعۡبُدُواْ رَبَّكُمُ ٱلَّذِي خَلَقَكُمۡ وَٱلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ\nЭй одамлар, сизларни ва сиздан олдингиларни яратган Роббингизга ибодат қилинг. Шоядки, тақводор бўлсангиз'}, {'chapter': 2, 'verse': 22, 'text': 'ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ فِرَٰشٗا وَٱلسَّمَآءَ بِنَآءٗ وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ فَلَا تَجۡعَلُواْ لِلَّهِ أَندَادٗا وَأَنتُمۡ تَعۡلَمُونَ\nУ сизларга ерни тўшаб, осмонни бино қилган ва осмондан сув тушириб, у билан сизларга меваларни ризқ қилиб чиқарган Зотдир. Билиб туриб Аллоҳга бошқаларни тенглаштирманг'}, {'chapter': 2, 'verse': 23, 'text': 'وَإِن كُنتُمۡ فِي رَيۡبٖ مِّمَّا نَزَّلۡنَا عَلَىٰ عَبۡدِنَا فَأۡتُواْ بِسُورَةٖ مِّن مِّثۡلِهِۦ وَٱدۡعُواْ شُهَدَآءَكُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ\nВа агар бандамизга туширган нарсамизга шакингиз бўлса, унга ўхшаш бир сура келтиринг ва рост сўзловчилардан бўлсангиз, Аллоҳдан ўзга гувоҳларингизни чақиринг'}, {'chapter': 2, 'verse': 24, 'text': 'فَإِن لَّمۡ تَفۡعَلُواْ وَلَن تَفۡعَلُواْ فَٱتَّقُواْ ٱلنَّارَ ٱلَّتِي وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُۖ أُعِدَّتۡ لِلۡكَٰفِرِينَ\nБас, агар қила олмасанглар, ҳеч қачон қила олмайсизлар ҳам, ёқилғиси одамлар ва тош бўлган, кофирлар учун тайёрланган ўтдан қўрқинглар'}, {'chapter': 2, 'verse': 25, 'text': 'وَبَشِّرِ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ كُلَّمَا رُزِقُواْ مِنۡهَا مِن ثَمَرَةٖ رِّزۡقٗا قَالُواْ هَٰذَا ٱلَّذِي رُزِقۡنَا مِن قَبۡلُۖ وَأُتُواْ بِهِۦ مُتَشَٰبِهٗاۖ وَلَهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَهُمۡ فِيهَا خَٰلِدُونَ\nИймон келтирган ва солиҳ амаллар қилганларга улар учун остидан анҳорлар оқиб турган жаннатлар борлигининг башоратини бер. Қачонки уларнинг мевасидан баҳраманд бўлсалар, бундан аввал ҳам ризқланган эдик, дерлар. Уларга ўхшаш нарсалар берилгандир. Уларга у ерда покиза жуфтлар бор ва улар у ерда абадий қолурлар'}, {'chapter': 2, 'verse': 26, 'text': '۞إِنَّ ٱللَّهَ لَا يَسۡتَحۡيِۦٓ أَن يَضۡرِبَ مَثَلٗا مَّا بَعُوضَةٗ فَمَا فَوۡقَهَاۚ فَأَمَّا ٱلَّذِينَ ءَامَنُواْ فَيَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۖ وَأَمَّا ٱلَّذِينَ كَفَرُواْ فَيَقُولُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلٗاۘ يُضِلُّ بِهِۦ كَثِيرٗا وَيَهۡدِي بِهِۦ كَثِيرٗاۚ وَمَا يُضِلُّ بِهِۦٓ إِلَّا ٱلۡفَٰسِقِينَ\nАлбатта, Аллоҳ чивин ёки ундан устунроқ нарсани мисол келтиришдан уялмас. Иймон келтирганлар, албатта, у Роббиларидан келган ҳақлигини билурлар. Аммо куфр келтирганлар: «Аллоҳ бу мисолдан нимани ирода қилди?» – дерлар. У ўша билан кўпчиликни залолатга кеткизар ва кўпчиликни ҳидоятга солар. У ўша билан фақат фосиқларнигина залолатга кеткизар'}, {'chapter': 2, 'verse': 27, 'text': 'ٱلَّذِينَ يَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي ٱلۡأَرۡضِۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nУлар Аллоҳнинг аҳдини боғланганидан сўнг бузадиган, Аллоҳ боғланишга буюрган нарсаларни кесадиган, ер юзида фасод қиладиган зотлардир. Ана ўшалар ютқазувчилардир'}, {'chapter': 2, 'verse': 28, 'text': 'كَيۡفَ تَكۡفُرُونَ بِٱللَّهِ وَكُنتُمۡ أَمۡوَٰتٗا فَأَحۡيَٰكُمۡۖ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ\nАллоҳга қандай куфр келтирасизлар? Ва ҳолбуки, жонсиз эдингиз, У сизга жон берди, сўнгра жонингизни оладир ва яна тирилтирадир ва сизлар Унга қайтажаксизлар'}, {'chapter': 2, 'verse': 29, 'text': 'هُوَ ٱلَّذِي خَلَقَ لَكُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا ثُمَّ ٱسۡتَوَىٰٓ إِلَى ٱلسَّمَآءِ فَسَوَّىٰهُنَّ سَبۡعَ سَمَٰوَٰتٖۚ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nУ сизлар учун ер юзидаги барча нарсани яратган Зот. Сўнгра осмонга юзланди ва уларни еттита қилиб тўғрилади. Ва У ҳар бир нарсани билувчи Зотдир'}, {'chapter': 2, 'verse': 30, 'text': 'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي جَاعِلٞ فِي ٱلۡأَرۡضِ خَلِيفَةٗۖ قَالُوٓاْ أَتَجۡعَلُ فِيهَا مَن يُفۡسِدُ فِيهَا وَيَسۡفِكُ ٱلدِّمَآءَ وَنَحۡنُ نُسَبِّحُ بِحَمۡدِكَ وَنُقَدِّسُ لَكَۖ قَالَ إِنِّيٓ أَعۡلَمُ مَا لَا تَعۡلَمُونَ\nЭсла, вақтики Роббинг фаришталарга: «Мен ер юзида халифа қилмоқчиман», – деди. Улар: «Унда фасод қиладиган, қон тўкадиган кимсани қилмоқчимисан? Ва ҳолбуки, биз Сенга тасбиҳ, ҳамд айтиб ва Сени улуғлаб турибмиз», – дедилар. У: «Мен сиз билмаганни биламан», – деди'}, {'chapter': 2, 'verse': 31, 'text': 'وَعَلَّمَ ءَادَمَ ٱلۡأَسۡمَآءَ كُلَّهَا ثُمَّ عَرَضَهُمۡ عَلَى ٱلۡمَلَـٰٓئِكَةِ فَقَالَ أَنۢبِـُٔونِي بِأَسۡمَآءِ هَـٰٓؤُلَآءِ إِن كُنتُمۡ صَٰدِقِينَ\nВа У Одамга исмларнинг барчасини ўргатди, сўнгра уларни фаришталарга рўбарў қилди. Кейин: «Агар ростгўйлардан бўлсангиз, анавиларнинг исмларини менга айтиб беринг», – деди'}, {'chapter': 2, 'verse': 32, 'text': 'قَالُواْ سُبۡحَٰنَكَ لَا عِلۡمَ لَنَآ إِلَّا مَا عَلَّمۡتَنَآۖ إِنَّكَ أَنتَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\nУлар: «Ўзинг поксан! Бизда Сен билдиргандан бошқа илм йўқ. Албатта, Сенинг Ўзинг билувчисан ва ҳикматли Зотсан», – дедилар'}, {'chapter': 2, 'verse': 33, 'text': 'قَالَ يَـٰٓـَٔادَمُ أَنۢبِئۡهُم بِأَسۡمَآئِهِمۡۖ فَلَمَّآ أَنۢبَأَهُم بِأَسۡمَآئِهِمۡ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيٓ أَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَأَعۡلَمُ مَا تُبۡدُونَ وَمَا كُنتُمۡ تَكۡتُمُونَ\n«Эй Одам, буларга у нарсаларнинг исмларини айтиб бер», – деди. Уларга ўша нарсаларнинг исмларини айтиб берган чоғида, «Мен сизларга осмонлару ернинг ғайбини биламан ва сизлар беркитмагану беркитган нарсаларни ҳам биламан демабмидим», – деди'}, {'chapter': 2, 'verse': 34, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ وَٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ\nЭсла, фаришталарга: «Одамга сажда қилинглар!» деганимизни. Бас, сажда қилдилар, магар иблис бош тортди, мутакаббирлик қилди ва кофирлардан бўлди'}, {'chapter': 2, 'verse': 35, 'text': 'وَقُلۡنَا يَـٰٓـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ وَكُلَا مِنۡهَا رَغَدًا حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِينَ\nВа: «Эй Одам, сен ўз жуфтинг ила жаннатда маскан топ. Унда нимани хоҳласаларингиз, енглар, ош бўлсин ва мана бу дарахтга яқин келманглар, бас, у ҳолда золимлардан бўласизлар», – дедик'}, {'chapter': 2, 'verse': 36, 'text': 'فَأَزَلَّهُمَا ٱلشَّيۡطَٰنُ عَنۡهَا فَأَخۡرَجَهُمَا مِمَّا كَانَا فِيهِۖ وَقُلۡنَا ٱهۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي ٱلۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ\nШайтон уларни ундан оздирди ва бўлган нарсаларидан чиқарди. Ва Биз: «Баъзингиз баъзингизга душман бўлиб тушинглар, сизларга ер юзида қароргоҳ ва бир муддат ҳузур қилиш бордир», – дедик'}, {'chapter': 2, 'verse': 37, 'text': 'فَتَلَقَّىٰٓ ءَادَمُ مِن رَّبِّهِۦ كَلِمَٰتٖ فَتَابَ عَلَيۡهِۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nБас, Одам ўз Роббисидан сўзларни қабул қилиб олди, кейин У Зот унинг тавбасини қабул қилди. Албатта, У тавбани қабул қилувчи ва раҳимли Зотдир'}, {'chapter': 2, 'verse': 38, 'text': 'قُلۡنَا ٱهۡبِطُواْ مِنۡهَا جَمِيعٗاۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَن تَبِعَ هُدَايَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nБиз: «Ундан ҳаммангиз тушинг. Бас, Мендан сизга ҳидоят келажак. Ким Менинг ҳидоятимга эргашса, уларга хавф йўқ ва улар хафа ҳам бўлмаслар», – дедик'}, {'chapter': 2, 'verse': 39, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nВа куфр келтирган ҳамда оятларимизни ёлғонга чиқарганлар эса, улар дўзах эгаларидир, улар унда абадий қолурлар'}, {'chapter': 2, 'verse': 40, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَوۡفُواْ بِعَهۡدِيٓ أُوفِ بِعَهۡدِكُمۡ وَإِيَّـٰيَ فَٱرۡهَبُونِ\nЭй Бани Исроил, сизга берган неъматларимни эсланг ва Менга берган аҳдингизга вафо қилинг. Мен ҳам сизга берган аҳдимга вафо қилурман. Ва Мендангина қўрқинг'}, {'chapter': 2, 'verse': 41, 'text': 'وَءَامِنُواْ بِمَآ أَنزَلۡتُ مُصَدِّقٗا لِّمَا مَعَكُمۡ وَلَا تَكُونُوٓاْ أَوَّلَ كَافِرِۭ بِهِۦۖ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗا وَإِيَّـٰيَ فَٱتَّقُونِ\nВа сиздаги нарсани тасдиқловчи қилиб туширган нарсамга иймон келтиринг. Унга биринчи куфр келтирувчи бўлманг. Менинг оятларимни оз баҳога сотманг ва Менгагина тақво қилинг'}, {'chapter': 2, 'verse': 42, 'text': 'وَلَا تَلۡبِسُواْ ٱلۡحَقَّ بِٱلۡبَٰطِلِ وَتَكۡتُمُواْ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ\nҲақни ботилга аралаштирманг ва ўзингиз билиб туриб ҳақни беркитманг'}, {'chapter': 2, 'verse': 43, 'text': 'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَٱرۡكَعُواْ مَعَ ٱلرَّـٰكِعِينَ\nНамозни тўкис адо қилинг, закот беринг ва рукуъ қилувчилар билан рукуъ қилинг'}, {'chapter': 2, 'verse': 44, 'text': '۞أَتَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبِرِّ وَتَنسَوۡنَ أَنفُسَكُمۡ وَأَنتُمۡ تَتۡلُونَ ٱلۡكِتَٰبَۚ أَفَلَا تَعۡقِلُونَ\nОдамларни яхшиликка чорлаб туриб, ўзингизни унутасизми? Ҳолбуки, китобни тиловат қиляпсиз-ку. Ақлни ишлатмайсизми'}, {'chapter': 2, 'verse': 45, 'text': 'وَٱسۡتَعِينُواْ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى ٱلۡخَٰشِعِينَ\nСабр ва намоз ила ёрдам сўранг. Ва албатта, у нафси синиқлардан бошқаларга жуда катта ишдир'}, {'chapter': 2, 'verse': 46, 'text': 'ٱلَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَأَنَّهُمۡ إِلَيۡهِ رَٰجِعُونَ\nУлар албатта Роббиларига рўбарў бўлишларига ва албатта Унга қайтиб боришларига ишонган зотлардир'}, {'chapter': 2, 'verse': 47, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى ٱلۡعَٰلَمِينَ\nЭй Бани Исроил, сизга берган неъматларимни ва Мен сизларни оламлардан афзал қилиб қўйганимни эсланг'}, {'chapter': 2, 'verse': 48, 'text': 'وَٱتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا شَفَٰعَةٞ وَلَا يُؤۡخَذُ مِنۡهَا عَدۡلٞ وَلَا هُمۡ يُنصَرُونَ\nБир жон бошқа жондан ҳеч нарсани адо қила олмайдиган, ундан шафоат қабул қилинмайдиган, ундан тўлов олинмайдиган ва уларга ёрдам берилмайдиган кундан қўрқинг'}, {'chapter': 2, 'verse': 49, 'text': 'وَإِذۡ نَجَّيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ يُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ\nСизни ёмон азобларга солиб, ўғилларингизни сўйиб, қизларингизни тирик қолдираётган Фиръавн аҳлидан қутқарганимизни эсланг. Ана ўшанда Роббингиздан улуғ синов бордир'}, {'chapter': 2, 'verse': 50, 'text': 'وَإِذۡ فَرَقۡنَا بِكُمُ ٱلۡبَحۡرَ فَأَنجَيۡنَٰكُمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَ وَأَنتُمۡ تَنظُرُونَ\nВа денгизни ёриб, сизни қутқариб, сизнинг кўз олдингизда Фиръавн аҳлини ғарқ қилганимизни эсланг'}, {'chapter': 2, 'verse': 51, 'text': 'وَإِذۡ وَٰعَدۡنَا مُوسَىٰٓ أَرۡبَعِينَ لَيۡلَةٗ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ\nВа Мусо билан қирқ кечага ваъдалашганимизни, у (кетгани)дан сўнг золим бўлган ҳолингизда бузоқни (худо) тутганингизни эсланг'}, {'chapter': 2, 'verse': 52, 'text': 'ثُمَّ عَفَوۡنَا عَنكُم مِّنۢ بَعۡدِ ذَٰلِكَ لَعَلَّكُمۡ تَشۡكُرُونَ\nЎшандан кейин сизни афв этдик. Шоядки, шукр қилсангиз'}, {'chapter': 2, 'verse': 53, 'text': 'وَإِذۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَٱلۡفُرۡقَانَ لَعَلَّكُمۡ تَهۡتَدُونَ\nШоядки, ҳидоят топсангиз деб, Мусога китобни ва фурқонни берганимизни эсланг'}, {'chapter': 2, 'verse': 54, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ إِنَّكُمۡ ظَلَمۡتُمۡ أَنفُسَكُم بِٱتِّخَاذِكُمُ ٱلۡعِجۡلَ فَتُوبُوٓاْ إِلَىٰ بَارِئِكُمۡ فَٱقۡتُلُوٓاْ أَنفُسَكُمۡ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ عِندَ بَارِئِكُمۡ فَتَابَ عَلَيۡكُمۡۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nМусонинг ўз қавмига: «Эй қавмим, албатта, сиз бузоқни (худо) тутишингиз ила ўзингизга ўзингиз зулм қилдингиз. Энди сизни бор қилувчи Зотга тавба қилинг, ўзингизни ўзингиз ўлдиринг, шундоқ қилмоғингиз сизни бор қилувчи Зот ҳузурида сиз учун яхшидир. Бас, У тавбангизни қабул қилди. Чунки У тавбани кўплаб қабул қилувчи ва раҳимли Зотдир», деганини эсланг'}, {'chapter': 2, 'verse': 55, 'text': 'وَإِذۡ قُلۡتُمۡ يَٰمُوسَىٰ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ نَرَى ٱللَّهَ جَهۡرَةٗ فَأَخَذَتۡكُمُ ٱلصَّـٰعِقَةُ وَأَنتُمۡ تَنظُرُونَ\n«Эй Мусо, Аллоҳни очиқ-равшан кўрмагунимизча, сенга ишонмаймиз», деганингизни, шунда сизни ўзингиз назар солиб турганингизда яшин урганини эсланг'}, {'chapter': 2, 'verse': 56, 'text': 'ثُمَّ بَعَثۡنَٰكُم مِّنۢ بَعۡدِ مَوۡتِكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\nСўнгра, ўлимингиздан кейин қайта тирилтирдик, шоядки, шукр қилсангиз'}, {'chapter': 2, 'verse': 57, 'text': 'وَظَلَّلۡنَا عَلَيۡكُمُ ٱلۡغَمَامَ وَأَنزَلۡنَا عَلَيۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nВа булутни устингиздан соя солдириб қўйдик, сизга манн ва беданани туширдик. Ризқ қилиб берган нарсаларимизнинг покларини енглар. Бизга зулм қилмадилар. Лекин ўзларига зулм қилардилар'}, {'chapter': 2, 'verse': 58, 'text': 'وَإِذۡ قُلۡنَا ٱدۡخُلُواْ هَٰذِهِ ٱلۡقَرۡيَةَ فَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ رَغَدٗا وَٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا وَقُولُواْ حِطَّةٞ نَّغۡفِرۡ لَكُمۡ خَطَٰيَٰكُمۡۚ وَسَنَزِيدُ ٱلۡمُحۡسِنِينَ\n«Ушбу қишлоққа кирингиз ва унинг хоҳлаган жойида енглар, еганингиз ош бўлсин ва эшикдан таъзим билан, ҳиттатун, деб киринг, сизнинг хатоларингизни мағфират қиламиз ва яхшиларга зиёда қиламиз», деганимизни эсланг'}, {'chapter': 2, 'verse': 59, 'text': 'فَبَدَّلَ ٱلَّذِينَ ظَلَمُواْ قَوۡلًا غَيۡرَ ٱلَّذِي قِيلَ لَهُمۡ فَأَنزَلۡنَا عَلَى ٱلَّذِينَ ظَلَمُواْ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ\nБас, зулм қилганлар уларга айтилган сўзни бошқасига алмаштирдилар. Биз зулм қилганларга фисқ қилаётганлари учун осмондан азоб туширдик'}, {'chapter': 2, 'verse': 60, 'text': '۞وَإِذِ ٱسۡتَسۡقَىٰ مُوسَىٰ لِقَوۡمِهِۦ فَقُلۡنَا ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنفَجَرَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۖ كُلُواْ وَٱشۡرَبُواْ مِن رِّزۡقِ ٱللَّهِ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nМусо ўз қавмини сероб қилишни сўраганида, «Ҳассанг билан тошни ур!» деганимизни эсланг. Бас, ундан ўн икки булоқ отилиб чиқди. Ҳар қабила ўз сувини билди. Аллоҳ берган ризқдан еб-ичинглар. Ер юзида бузғунчилик устига бузғунчилик қилманглар'}, {'chapter': 2, 'verse': 61, 'text': 'وَإِذۡ قُلۡتُمۡ يَٰمُوسَىٰ لَن نَّصۡبِرَ عَلَىٰ طَعَامٖ وَٰحِدٖ فَٱدۡعُ لَنَا رَبَّكَ يُخۡرِجۡ لَنَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ مِنۢ بَقۡلِهَا وَقِثَّآئِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۖ قَالَ أَتَسۡتَبۡدِلُونَ ٱلَّذِي هُوَ أَدۡنَىٰ بِٱلَّذِي هُوَ خَيۡرٌۚ ٱهۡبِطُواْ مِصۡرٗا فَإِنَّ لَكُم مَّا سَأَلۡتُمۡۗ وَضُرِبَتۡ عَلَيۡهِمُ ٱلذِّلَّةُ وَٱلۡمَسۡكَنَةُ وَبَآءُو بِغَضَبٖ مِّنَ ٱللَّهِۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلنَّبِيِّـۧنَ بِغَيۡرِ ٱلۡحَقِّۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ\n«Эй Мусо, бир хил таомга ҳеч чидай олмаймиз, Роббингга дуо қил, бизга ерда ўсадиган нарсалардан — сабзавот, тарра, саримсоқ, мош, пиёз чиқариб берсин», деганингизни эсланг. У: «Яхши нарсани паст нарсага алмаштиришни сўрайсизларми? Бирорта шаҳарга тушинг, сўраганингизни албатта топасиз», деди. Уларга хорлик ва мискинлик ёзилди ва Аллоҳнинг ғазабига лойиқ бўлдилар. Бу, Аллоҳнинг оятларига куфр келтирганлари, Набийларни ноҳақ қатл этганлари учундир. Бу, исёнга кетганлари ва ҳаддан ошганлари учундир'}, {'chapter': 2, 'verse': 62, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلنَّصَٰرَىٰ وَٱلصَّـٰبِـِٔينَ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта, иймон келтирганлар, яҳудий бўлганлар, насоролар ва собиийлардан, қайсилари Аллоҳга ва қиёмат кунига иймон келтирса ва яхши амалларни қилса, ўшаларга Роббилари ҳузурида ажрлар бордир. Уларга хавф йўқ ва улар хафа ҳам бўлмаслар'}, {'chapter': 2, 'verse': 63, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ\nАҳду паймонларингизни олганимизни ва устингизга Турни кўтарганимизни, «Биз сизга берган нарсани қувват билан олинг ва ундаги нарсани эсланг, шоядки, тақводор бўлсангиз», (деганимизни) эсланг'}, {'chapter': 2, 'verse': 64, 'text': 'ثُمَّ تَوَلَّيۡتُم مِّنۢ بَعۡدِ ذَٰلِكَۖ فَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَكُنتُم مِّنَ ٱلۡخَٰسِرِينَ\nСўнгра, ўшандан кейин юз ўгирдингиз. Сизга Аллоҳнинг фазли бўлмаганида, албатта ютқазганлардан бўлардингиз'}, {'chapter': 2, 'verse': 65, 'text': 'وَلَقَدۡ عَلِمۡتُمُ ٱلَّذِينَ ٱعۡتَدَوۡاْ مِنكُمۡ فِي ٱلسَّبۡتِ فَقُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ\nБатаҳқиқ, сизлардан шанба куни тажовузкорлик қилганларни билдингиз. Биз уларга: «Қувилган, хор-зор маймунга айланинг», – дедик'}, {'chapter': 2, 'verse': 66, 'text': 'فَجَعَلۡنَٰهَا نَكَٰلٗا لِّمَا بَيۡنَ يَدَيۡهَا وَمَا خَلۡفَهَا وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ\nБиз бу (ҳодиса)ни унинг олдидагиларга ва кейинидагиларга ва тақводорларга мавъиза қилдик'}, {'chapter': 2, 'verse': 67, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦٓ إِنَّ ٱللَّهَ يَأۡمُرُكُمۡ أَن تَذۡبَحُواْ بَقَرَةٗۖ قَالُوٓاْ أَتَتَّخِذُنَا هُزُوٗاۖ قَالَ أَعُوذُ بِٱللَّهِ أَنۡ أَكُونَ مِنَ ٱلۡجَٰهِلِينَ\nМусо ўз қавмига: «Аллоҳ сизга сигир сўймоқни буюрмоқда», деганини эсланг. Улар: «Бизни масхара қиляпсанми?» – дедилар. У: «Жоҳиллардан бўлишимдан Аллоҳдан паноҳ сўрайман», – деди'}, {'chapter': 2, 'verse': 68, 'text': 'قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا فَارِضٞ وَلَا بِكۡرٌ عَوَانُۢ بَيۡنَ ذَٰلِكَۖ فَٱفۡعَلُواْ مَا تُؤۡمَرُونَ\nУлар: «Роббингга дуо қил, унинг қандай эканини баён қилиб берсин», – дедилар. У: «У Зот, қари ҳам, ёш ҳам бўлмаган, икковининг ўртасидаги сигир, деяпти. Бас, буюрилган нарсани бажаринг», – деди'}, {'chapter': 2, 'verse': 69, 'text': 'قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا لَوۡنُهَاۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ صَفۡرَآءُ فَاقِعٞ لَّوۡنُهَا تَسُرُّ ٱلنَّـٰظِرِينَ\nУлар: «Роббингга дуо қил, унинг рангини баён қилиб берсин», дедилар. У: «У Зот, ранги сап-сариқ, қараганларни хурсанд қиладиган сигир, деяпти», – деди'}, {'chapter': 2, 'verse': 70, 'text': 'قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ إِنَّ ٱلۡبَقَرَ تَشَٰبَهَ عَلَيۡنَا وَإِنَّآ إِن شَآءَ ٱللَّهُ لَمُهۡتَدُونَ\nУлар: «Роббингга дуо қил, унинг қандай эканини бизга баён қилиб берсин, сигир бизга ноаниқ бўлиб қолди, биз, иншоаллоҳ, албатта, ҳидоят топувчимиз», – дедилар'}, {'chapter': 2, 'verse': 71, 'text': 'قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا ذَلُولٞ تُثِيرُ ٱلۡأَرۡضَ وَلَا تَسۡقِي ٱلۡحَرۡثَ مُسَلَّمَةٞ لَّا شِيَةَ فِيهَاۚ قَالُواْ ٱلۡـَٰٔنَ جِئۡتَ بِٱلۡحَقِّۚ فَذَبَحُوهَا وَمَا كَادُواْ يَفۡعَلُونَ\nУ: «У Зот, ўша сигир минилмаган, ер ҳайдаш, экин суғоришда ишлатилмаган, айблардан саломат, аралашган ранги йўқ, деяпти», – деди. Улар: «Ана энди ҳақни келтирдинг», – дедилар. Бас, уни сўйдилар. Сал бўлмаса, қила олмасдилар'}, {'chapter': 2, 'verse': 72, 'text': 'وَإِذۡ قَتَلۡتُمۡ نَفۡسٗا فَٱدَّـٰرَ\u2009ٰٔتُمۡ فِيهَاۖ وَٱللَّهُ مُخۡرِجٞ مَّا كُنتُمۡ تَكۡتُمُونَ\nБир жонни ўлдириб қўйиб, у ҳақида ихтилофга тушганингизни эсланг. Аллоҳ сиз беркитган нарсани чиқаргувчидир'}, {'chapter': 2, 'verse': 73, 'text': 'فَقُلۡنَا ٱضۡرِبُوهُ بِبَعۡضِهَاۚ كَذَٰلِكَ يُحۡيِ ٱللَّهُ ٱلۡمَوۡتَىٰ وَيُرِيكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\n«Бас, уни у(сигир)нинг баъзи жойи билан уринг», – дедик. Аллоҳ худди шундай қилиб ўликларни тирилтирур ва оят — аломатларини сизга кўрсатур. Шоядки, ақл ишлатсангиз'}, {'chapter': 2, 'verse': 74, 'text': 'ثُمَّ قَسَتۡ قُلُوبُكُم مِّنۢ بَعۡدِ ذَٰلِكَ فَهِيَ كَٱلۡحِجَارَةِ أَوۡ أَشَدُّ قَسۡوَةٗۚ وَإِنَّ مِنَ ٱلۡحِجَارَةِ لَمَا يَتَفَجَّرُ مِنۡهُ ٱلۡأَنۡهَٰرُۚ وَإِنَّ مِنۡهَا لَمَا يَشَّقَّقُ فَيَخۡرُجُ مِنۡهُ ٱلۡمَآءُۚ وَإِنَّ مِنۡهَا لَمَا يَهۡبِطُ مِنۡ خَشۡيَةِ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nСўнгра, шундан кейин қалбларингиз қаттиқ бўлди, улар тошдек ёки тошдан ҳам қаттиқ. Ва ҳолбуки, тошларнинг баъзиларидан анҳорлар отилиб чиқадир, баъзилари ёрилиб, ундан сув чиқадир ва баъзилари Аллоҳдан қўрққанидан қулаб тушадир. Ва Аллоҳ қилаётган ишларингиздан ғофил эмас'}, {'chapter': 2, 'verse': 75, 'text': '۞أَفَتَطۡمَعُونَ أَن يُؤۡمِنُواْ لَكُمۡ وَقَدۡ كَانَ فَرِيقٞ مِّنۡهُمۡ يَسۡمَعُونَ كَلَٰمَ ٱللَّهِ ثُمَّ يُحَرِّفُونَهُۥ مِنۢ بَعۡدِ مَا عَقَلُوهُ وَهُمۡ يَعۡلَمُونَ\nУларнинг иймон келтиришларини тамаъ қиласизларми? Ва ҳолбуки, улардан бир гуруҳлари Аллоҳнинг каломини эшитар эдилар ва сўнгра уни тушунганларидан кейин, билиб туриб бузар эдилар'}, {'chapter': 2, 'verse': 76, 'text': 'وَإِذَا لَقُواْ ٱلَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَا بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ قَالُوٓاْ أَتُحَدِّثُونَهُم بِمَا فَتَحَ ٱللَّهُ عَلَيۡكُمۡ لِيُحَآجُّوكُم بِهِۦ عِندَ رَبِّكُمۡۚ أَفَلَا تَعۡقِلُونَ\nУлар иймон келтирганларни учратсалар, иймон келтирдик, дерлар. Баъзилари баъзилари билан холи қолсалар, уларга: «Аллоҳ сизга очган нарса ҳақида Роббингиз ҳузурида ҳужжат талашишлари учун гапиряпсизми? Ақлни ишлатмайсизми?» дерлар'}, {'chapter': 2, 'verse': 77, 'text': 'أَوَلَا يَعۡلَمُونَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ\nАллоҳ уларнинг сир тутгану ошкор қилган нарсаларини албатта билишини англамайдиларми'}, {'chapter': 2, 'verse': 78, 'text': 'وَمِنۡهُمۡ أُمِّيُّونَ لَا يَعۡلَمُونَ ٱلۡكِتَٰبَ إِلَّآ أَمَانِيَّ وَإِنۡ هُمۡ إِلَّا يَظُنُّونَ\nУларнинг ичида китобдан бехабар, хомхаёлдан бошқани билмайдиган омийлар бор. Улар фақат гумон қиладилар холос'}, {'chapter': 2, 'verse': 79, 'text': 'فَوَيۡلٞ لِّلَّذِينَ يَكۡتُبُونَ ٱلۡكِتَٰبَ بِأَيۡدِيهِمۡ ثُمَّ يَقُولُونَ هَٰذَا مِنۡ عِندِ ٱللَّهِ لِيَشۡتَرُواْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَوَيۡلٞ لَّهُم مِّمَّا كَتَبَتۡ أَيۡدِيهِمۡ وَوَيۡلٞ لَّهُم مِّمَّا يَكۡسِبُونَ\nКитобни ўз қўли билан ёзиб, сўнгра уни арзон баҳога сотиш учун: «Бу Аллоҳнинг ҳузуридан», дейдиганларга ҳалокат бўлсин! Уларга қўллари билан ёзган нарсаларидан ҳалокат бўлсин! Уларга топган фойдаларидан ҳалокат бўлсин'}, {'chapter': 2, 'verse': 80, 'text': 'وَقَالُواْ لَن تَمَسَّنَا ٱلنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَةٗۚ قُلۡ أَتَّخَذۡتُمۡ عِندَ ٱللَّهِ عَهۡدٗا فَلَن يُخۡلِفَ ٱللَّهُ عَهۡدَهُۥٓۖ أَمۡ تَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nВа улар: «Бизга (дўзах) ўти ҳеч тегмас, тегса ҳам саноқли кунларгина тегадир», – дедилар. Сен: «Аллоҳдан аҳд олдингизми? Зотан, Аллоҳ аҳдига ҳеч хилоф қилмас. Ёки Аллоҳнинг номидан билмаган нарсангизни гапиряпсизми?» – деб айт'}, {'chapter': 2, 'verse': 81, 'text': 'بَلَىٰۚ مَن كَسَبَ سَيِّئَةٗ وَأَحَٰطَتۡ بِهِۦ خَطِيٓـَٔتُهُۥ فَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЙўқ! Ким гуноҳ қилса ва уни хатолари ўраб олган бўлса, ана ўшалар дўзах эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 2, 'verse': 82, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nВа иймон келтириб, яхши амалларни қилганлар, ана ўшалар жаннат эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 2, 'verse': 83, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ لَا تَعۡبُدُونَ إِلَّا ٱللَّهَ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَانٗا وَذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَقُولُواْ لِلنَّاسِ حُسۡنٗا وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ ثُمَّ تَوَلَّيۡتُمۡ إِلَّا قَلِيلٗا مِّنكُمۡ وَأَنتُم مُّعۡرِضُونَ\nАллоҳдан бошқага ибодат этмайсиз, ота-онага, қариндошларга, етимларга, мискинларга яхшилик қиласиз, одамларга яхши гаплар айтинг, намозни қоим қилинг, закот беринг, деб Бани Исроилдан аҳду паймон олганимизни эсланг. Сўнгра, озгинангиздан бошқангиз юз ўгириб, ортга қараб кетдингиз'}, {'chapter': 2, 'verse': 84, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ لَا تَسۡفِكُونَ دِمَآءَكُمۡ وَلَا تُخۡرِجُونَ أَنفُسَكُم مِّن دِيَٰرِكُمۡ ثُمَّ أَقۡرَرۡتُمۡ وَأَنتُمۡ تَشۡهَدُونَ\nБир-бирингизнинг қонларингизни тўкмайсиз ва диёрингиздан чиқармайсиз, деб сизлардан аҳду паймон олганимизни эсланг. Сўнгра, гувоҳлик бериб туриб иқрор бўлдингиз'}, {'chapter': 2, 'verse': 85, 'text': 'ثُمَّ أَنتُمۡ هَـٰٓؤُلَآءِ تَقۡتُلُونَ أَنفُسَكُمۡ وَتُخۡرِجُونَ فَرِيقٗا مِّنكُم مِّن دِيَٰرِهِمۡ تَظَٰهَرُونَ عَلَيۡهِم بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَإِن يَأۡتُوكُمۡ أُسَٰرَىٰ تُفَٰدُوهُمۡ وَهُوَ مُحَرَّمٌ عَلَيۡكُمۡ إِخۡرَاجُهُمۡۚ أَفَتُؤۡمِنُونَ بِبَعۡضِ ٱلۡكِتَٰبِ وَتَكۡفُرُونَ بِبَعۡضٖۚ فَمَا جَزَآءُ مَن يَفۡعَلُ ذَٰلِكَ مِنكُمۡ إِلَّا خِزۡيٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يُرَدُّونَ إِلَىٰٓ أَشَدِّ ٱلۡعَذَابِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nСўнгра, сиз ўзингизни ўзингиз ўлдирасиз ва бир гуруҳингизни диёридан чиқарасиз ҳамда уларга қарши гуноҳ ва душманлик йўлида ёрдам берасиз. Агар улар асир бўлсалар, қутқариб оласиз. Ва ҳолбуки, уларни чиқариш ҳаром эди. Ёки китобнинг баъзисига иймон келтириб, баъзисига куфр келтирасизми?! Сизлардан ўшандоқ қилганларнинг жазоси бу дунёда фақат расво бўлишликдир, қиёмат кунида эса, энг шиддатли азобга қайтарилурлар. Аллоҳ қилаётганингиздан ғофил эмас'}, {'chapter': 2, 'verse': 86, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا بِٱلۡأٓخِرَةِۖ فَلَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنصَرُونَ\nАна ўшалар дунё ҳаётини охиратга сотиб олганлардир. Улардан азоб енгиллатилмас ва уларга ёрдам ҳам берилмас'}, {'chapter': 2, 'verse': 87, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَقَفَّيۡنَا مِنۢ بَعۡدِهِۦ بِٱلرُّسُلِۖ وَءَاتَيۡنَا عِيسَى ٱبۡنَ مَرۡيَمَ ٱلۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ ٱلۡقُدُسِۗ أَفَكُلَّمَا جَآءَكُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُكُمُ ٱسۡتَكۡبَرۡتُمۡ فَفَرِيقٗا كَذَّبۡتُمۡ وَفَرِيقٗا تَقۡتُلُونَ\nВа батаҳқиқ, Мусога китобни бердик ва унинг ортидан Пайғамбарларни кетма-кет юбордик. Ва Ийсо ибн Марямга равшан(мўъжиза)ларни бердик ва уни Руҳул Қудус билан қўлладик. Ҳар қачон Пайғамбар сизнинг ҳавойи нафсингизга ёқмайдиган нарса келтирса, мутакаббирлик қилиб, баъзиларини ёлғончига чиқариб, баъзиларини ўлдираверасизми'}, {'chapter': 2, 'verse': 88, 'text': 'وَقَالُواْ قُلُوبُنَا غُلۡفُۢۚ بَل لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَقَلِيلٗا مَّا يُؤۡمِنُونَ\nВа улар: «Қалбимиз ғилофланган», дейишар. Йўқ, ундай эмас, куфрлари сабабли Аллоҳ уларни лаънатлаган. Бас, озгинагина иймон келтирурлар'}, {'chapter': 2, 'verse': 89, 'text': 'وَلَمَّا جَآءَهُمۡ كِتَٰبٞ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ وَكَانُواْ مِن قَبۡلُ يَسۡتَفۡتِحُونَ عَلَى ٱلَّذِينَ كَفَرُواْ فَلَمَّا جَآءَهُم مَّا عَرَفُواْ كَفَرُواْ بِهِۦۚ فَلَعۡنَةُ ٱللَّهِ عَلَى ٱلۡكَٰفِرِينَ\nУларга Аллоҳнинг ҳузуридан ўзларидаги нарсани тасдиқловчи китоб келганда, ва ҳолбуки, кофирларга қарши (ундан) ёрдам кутардилар. Бас, қачонки уларга ўзлари билган нарса келганда, унга куфр келтирдилар. Кофирларга Аллоҳнинг лаънати бўлсин'}, {'chapter': 2, 'verse': 90, 'text': 'بِئۡسَمَا ٱشۡتَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡ أَن يَكۡفُرُواْ بِمَآ أَنزَلَ ٱللَّهُ بَغۡيًا أَن يُنَزِّلَ ٱللَّهُ مِن فَضۡلِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ فَبَآءُو بِغَضَبٍ عَلَىٰ غَضَبٖۚ وَلِلۡكَٰفِرِينَ عَذَابٞ مُّهِينٞ\nУлар ўзларини сотган нарса: Аллоҳ бандаларидан хоҳлаганига Ўз фазлидан туширганига ҳасад қилиб, Аллоҳ туширган нарсага куфр келтирмоқлик, қандоқ ҳам ёмон. Улар ғазабнинг устига ғазабни лозим тутдилар. Кофирларга хор қилувчи азоб бордир'}, {'chapter': 2, 'verse': 91, 'text': 'وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ بِمَآ أَنزَلَ ٱللَّهُ قَالُواْ نُؤۡمِنُ بِمَآ أُنزِلَ عَلَيۡنَا وَيَكۡفُرُونَ بِمَا وَرَآءَهُۥ وَهُوَ ٱلۡحَقُّ مُصَدِّقٗا لِّمَا مَعَهُمۡۗ قُلۡ فَلِمَ تَقۡتُلُونَ أَنۢبِيَآءَ ٱللَّهِ مِن قَبۡلُ إِن كُنتُم مُّؤۡمِنِينَ\nАгар уларга: «Аллоҳ туширган нарсага иймон келтиринг», дейилса, «Бизга туширилган нарсага иймон келтирамиз», дерлар ва ундан кейинги нарсага куфр келтирарлар. Ҳолбуки, у улардаги нарсани тасдиқловчи ҳақдир. Айт: «Агар мўмин бўлсангиз, нимага илгари Аллоҳнинг Пайғамбарларини ўлдирдингиз?!»'}, {'chapter': 2, 'verse': 92, 'text': '۞وَلَقَدۡ جَآءَكُم مُّوسَىٰ بِٱلۡبَيِّنَٰتِ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ\nВа батаҳқиқ, Мусо сизларга равшан(мўъжиза)лар ила келди. Сўнгра, сиз ундан кейин золим бўлган ҳолингизда бузоқни (худо) тутдингиз'}, {'chapter': 2, 'verse': 93, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱسۡمَعُواْۖ قَالُواْ سَمِعۡنَا وَعَصَيۡنَا وَأُشۡرِبُواْ فِي قُلُوبِهِمُ ٱلۡعِجۡلَ بِكُفۡرِهِمۡۚ قُلۡ بِئۡسَمَا يَأۡمُرُكُم بِهِۦٓ إِيمَٰنُكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nСиздан аҳду паймонингизни олиб, устингизга Турни кўтариб: «Сизга Биз берган нарсани қувват билан тутинг ва тингланг», деганимизни эсланг. Улар: «Тингладик ва исён қилдик», дедилар. Куфрлари сабабли қалбларига бузоқ ўрнаштирилди. Айт: «Агар мўмин бўлсангиз, сизнинг иймонингиз қандай ҳам ёмон нарсага буюради!»'}, {'chapter': 2, 'verse': 94, 'text': 'قُلۡ إِن كَانَتۡ لَكُمُ ٱلدَّارُ ٱلۡأٓخِرَةُ عِندَ ٱللَّهِ خَالِصَةٗ مِّن دُونِ ٱلنَّاسِ فَتَمَنَّوُاْ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ\nАйт: «Агар Аллоҳнинг ҳузуридаги охират ҳовлиси бошқа одамларсиз холис сизга бўладиган бўлса, гапингизда ростгўй бўлсангиз, ўлимни орзу қилинг»'}, {'chapter': 2, 'verse': 95, 'text': 'وَلَن يَتَمَنَّوۡهُ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nУлар қўллари тақдим қилган нарсалар сабабидан, уни абадий орзу қилмаслар ва Аллоҳ зулм қилувчиларни билувчи Зотдир'}, {'chapter': 2, 'verse': 96, 'text': 'وَلَتَجِدَنَّهُمۡ أَحۡرَصَ ٱلنَّاسِ عَلَىٰ حَيَوٰةٖ وَمِنَ ٱلَّذِينَ أَشۡرَكُواْۚ يَوَدُّ أَحَدُهُمۡ لَوۡ يُعَمَّرُ أَلۡفَ سَنَةٖ وَمَا هُوَ بِمُزَحۡزِحِهِۦ مِنَ ٱلۡعَذَابِ أَن يُعَمَّرَۗ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ\nУларнинг ҳаётга энг ҳирс қўйган одамлар эканлигини биласан. Мушриклардан бирлари минг йил умр кўришни суядир. Аммо узоқ умр кўриши уни азобдан қутқармас. Ва Аллоҳ уларнинг қилаётганларини кўрувчидир'}, {'chapter': 2, 'verse': 97, 'text': 'قُلۡ مَن كَانَ عَدُوّٗا لِّـجِبۡرِيلَ فَإِنَّهُۥ نَزَّلَهُۥ عَلَىٰ قَلۡبِكَ بِإِذۡنِ ٱللَّهِ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَهُدٗى وَبُشۡرَىٰ لِلۡمُؤۡمِنِينَ\nЖаброилга душман бўлганларга айт, албатта, Ул у(Қуръон)ни сенинг қалбингга Аллоҳнинг изни билан, ўзидан олдингини тасдиқловчи ва мўминлар учун ҳидоят ва башорат қилиб туширди'}, {'chapter': 2, 'verse': 98, 'text': 'مَن كَانَ عَدُوّٗا لِّلَّهِ وَمَلَـٰٓئِكَتِهِۦ وَرُسُلِهِۦ وَجِبۡرِيلَ وَمِيكَىٰلَ فَإِنَّ ٱللَّهَ عَدُوّٞ لِّلۡكَٰفِرِينَ\nКим Аллоҳга, Унинг фаришталарига, Пайғамбарларига, Жаброилга ва Микоилга душман бўлса, албатта, Аллоҳ кофирларнинг душманидир'}, {'chapter': 2, 'verse': 99, 'text': 'وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكَ ءَايَٰتِۭ بَيِّنَٰتٖۖ وَمَا يَكۡفُرُ بِهَآ إِلَّا ٱلۡفَٰسِقُونَ\nБатаҳқиқ, сенга равшан оятларни туширдик, уларга фақат фосиқларгина куфр келтирур'}, {'chapter': 2, 'verse': 100, 'text': 'أَوَكُلَّمَا عَٰهَدُواْ عَهۡدٗا نَّبَذَهُۥ فَرِيقٞ مِّنۡهُمۚ بَلۡ أَكۡثَرُهُمۡ لَا يُؤۡمِنُونَ\nҲар қачон аҳд қилсалар, улардан бир гуруҳи уни бузаверадими?! Йўқ! Уларнинг кўплари иймон келтирмаслар'}, {'chapter': 2, 'verse': 101, 'text': 'وَلَمَّا جَآءَهُمۡ رَسُولٞ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ نَبَذَ فَرِيقٞ مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ كِتَٰبَ ٱللَّهِ وَرَآءَ ظُهُورِهِمۡ كَأَنَّهُمۡ لَا يَعۡلَمُونَ\nУлардаги нарсани тасдиқлаб, Аллоҳнинг ҳузуридан уларга Пайғамбар келса, китоб берилганлардан бир гуруҳи, худди билмагандек, Аллоҳнинг китобини ортларига қараб отдилар'}, {'chapter': 2, 'verse': 102, 'text': 'وَٱتَّبَعُواْ مَا تَتۡلُواْ ٱلشَّيَٰطِينُ عَلَىٰ مُلۡكِ سُلَيۡمَٰنَۖ وَمَا كَفَرَ سُلَيۡمَٰنُ وَلَٰكِنَّ ٱلشَّيَٰطِينَ كَفَرُواْ يُعَلِّمُونَ ٱلنَّاسَ ٱلسِّحۡرَ وَمَآ أُنزِلَ عَلَى ٱلۡمَلَكَيۡنِ بِبَابِلَ هَٰرُوتَ وَمَٰرُوتَۚ وَمَا يُعَلِّمَانِ مِنۡ أَحَدٍ حَتَّىٰ يَقُولَآ إِنَّمَا نَحۡنُ فِتۡنَةٞ فَلَا تَكۡفُرۡۖ فَيَتَعَلَّمُونَ مِنۡهُمَا مَا يُفَرِّقُونَ بِهِۦ بَيۡنَ ٱلۡمَرۡءِ وَزَوۡجِهِۦۚ وَمَا هُم بِضَآرِّينَ بِهِۦ مِنۡ أَحَدٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡۚ وَلَقَدۡ عَلِمُواْ لَمَنِ ٱشۡتَرَىٰهُ مَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِنۡ خَلَٰقٖۚ وَلَبِئۡسَ مَا شَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nВа Сулаймон подшолигида шайтонлар тиловат қилган нарсага эргашдилар. Сулаймон куфр келтиргани йўқ. Лекин шайтонлар одамларга сеҳрни ўргатиб, кофир бўлдилар. Ва Бобилда Ҳорут ва Морут номли икки фариштага тушган нарсага эргашдилар. Икковлари ҳатто: «Биз фитна — синов учунмиз, кофир бўлмагин», демасдан олдин ҳеч кимга ўргатмасдилар. Бас, икковларидан эр-хотиннинг ўртасини бузадиган нарсани ўрганишар эди. Ва улар у ила Аллоҳнинг изнисиз бирор кишига зарар етказувчи эмасдилар. Ва улар зарар келтирадиган, фойда келтирмайдиган нарсани ўрганурлар. Ва батаҳқиқ, биладиларки, уни сотиб олган кишига охиратда насиба йўқ. Агар билсалар, ўзларини сотган нарса қандоқ ҳам ёмон'}, {'chapter': 2, 'verse': 103, 'text': 'وَلَوۡ أَنَّهُمۡ ءَامَنُواْ وَٱتَّقَوۡاْ لَمَثُوبَةٞ مِّنۡ عِندِ ٱللَّهِ خَيۡرٞۚ لَّوۡ كَانُواْ يَعۡلَمُونَ\nАгар улар иймон келтириб, тақво қилганларида, гар билсалар, Аллоҳнинг ҳузуридаги савоб яхши эди'}, {'chapter': 2, 'verse': 104, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقُولُواْ رَٰعِنَا وَقُولُواْ ٱنظُرۡنَا وَٱسۡمَعُواْۗ وَلِلۡكَٰفِرِينَ عَذَابٌ أَلِيمٞ\nЭй, иймон келтирганлар! «Роъина» деманглар, «унзурна» денглар ва тингланглар. Ва кофирларга аламли азоб бўлур'}, {'chapter': 2, 'verse': 105, 'text': 'مَّا يَوَدُّ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَلَا ٱلۡمُشۡرِكِينَ أَن يُنَزَّلَ عَلَيۡكُم مِّنۡ خَيۡرٖ مِّن رَّبِّكُمۡۚ وَٱللَّهُ يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nАҳли китоб ва мушриклардан бўлган кофирлар сизларга Роббингиздан яхшилик туширилишини ёқтирмаслар. Ва ҳолбуки, Аллоҳ Ўз раҳматини кимга хоҳласа, ўшанга хос қиладир. Аллоҳ улуғ фазл эгасидир'}, {'chapter': 2, 'verse': 106, 'text': '۞مَا نَنسَخۡ مِنۡ ءَايَةٍ أَوۡ نُنسِهَا نَأۡتِ بِخَيۡرٖ مِّنۡهَآ أَوۡ مِثۡلِهَآۗ أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОятлардан биронтасини насх қилсак ёки унуттирсак, ундан яхшисини ёки унга ўхшашини келтирурмиз. Аллоҳ ҳар нарсага қодир эканини билмадингми'}, {'chapter': 2, 'verse': 107, 'text': 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ\nАлбатта, осмонлару ернинг мулки Аллоҳники эканини ва сизларга Аллоҳдан ўзга дўст ҳам, ёрдамчи ҳам йўқлигини билмадингми'}, {'chapter': 2, 'verse': 108, 'text': 'أَمۡ تُرِيدُونَ أَن تَسۡـَٔلُواْ رَسُولَكُمۡ كَمَا سُئِلَ مُوسَىٰ مِن قَبۡلُۗ وَمَن يَتَبَدَّلِ ٱلۡكُفۡرَ بِٱلۡإِيمَٰنِ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ\nЁки Пайғамбарингиздан, бундан аввал Мусодан сўралганидек, савол сўрашни хоҳлайсизми?! Ким иймонни куфрга алмаштирса, батаҳқиқ, тўғри йўлдан адашган бўлур'}, {'chapter': 2, 'verse': 109, 'text': 'وَدَّ كَثِيرٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ لَوۡ يَرُدُّونَكُم مِّنۢ بَعۡدِ إِيمَٰنِكُمۡ كُفَّارًا حَسَدٗا مِّنۡ عِندِ أَنفُسِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡحَقُّۖ فَٱعۡفُواْ وَٱصۡفَحُواْ حَتَّىٰ يَأۡتِيَ ٱللَّهُ بِأَمۡرِهِۦٓۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАҳли китоблардан кўплари уларга ҳақ равшан бўлганидан кейин, ўзларича ҳасад қилиб, сизларни иймонли бўлганингиздан кейин кофирликка қайтармоқчи бўлурлар. Аллоҳнинг амри келгунча афв этинг, кечиримли бўлинг. Албатта, Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 2, 'verse': 110, 'text': 'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ ٱللَّهِۗ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ\nНамозни қоим қилинг, закот беринг, ўзингиз учун қилган яхшиликларни Аллоҳнинг ҳузурида топарсиз. Албатта, Аллоҳ қилаётганингизни кўрувчидир'}, {'chapter': 2, 'verse': 111, 'text': 'وَقَالُواْ لَن يَدۡخُلَ ٱلۡجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوۡ نَصَٰرَىٰۗ تِلۡكَ أَمَانِيُّهُمۡۗ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nВа улар: «Жаннатга яҳудий ва насронийлардан бошқа ҳеч ким кирмас», – дедилар. Бу уларнинг хом хаёлларидир. Сен: «Агар ростгўй бўлсангиз, ҳужжатингизни келтиринг», – деб айт'}, {'chapter': 2, 'verse': 112, 'text': 'بَلَىٰۚ مَنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنٞ فَلَهُۥٓ أَجۡرُهُۥ عِندَ رَبِّهِۦ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nЙўқ! Кимки яхшилик қилган ҳолида юзини Аллоҳга топширса, унга Робби ҳузурида ажр бордир. Уларга хавф йўқ ва улар хафа ҳам бўлмаслар'}, {'chapter': 2, 'verse': 113, 'text': 'وَقَالَتِ ٱلۡيَهُودُ لَيۡسَتِ ٱلنَّصَٰرَىٰ عَلَىٰ شَيۡءٖ وَقَالَتِ ٱلنَّصَٰرَىٰ لَيۡسَتِ ٱلۡيَهُودُ عَلَىٰ شَيۡءٖ وَهُمۡ يَتۡلُونَ ٱلۡكِتَٰبَۗ كَذَٰلِكَ قَالَ ٱلَّذِينَ لَا يَعۡلَمُونَ مِثۡلَ قَوۡلِهِمۡۚ فَٱللَّهُ يَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nЯҳудийлар: «Насронийлар арзимас нарсададирлар», дедилар. Насронийлар: «Яҳудийлар арзимас нарсададирлар», дедилар. Ва ҳолбуки, улар китоб тиловат қилурлар. Шунингдек, билмайдиган кимсалар ҳам уларнинг гапига ўхшаш гап айтдилар. Бас, қиёмат куни ораларида Аллоҳ улар ихтилоф қилган нарса ҳақида ҳакамлик қиладир'}, {'chapter': 2, 'verse': 114, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّن مَّنَعَ مَسَٰجِدَ ٱللَّهِ أَن يُذۡكَرَ فِيهَا ٱسۡمُهُۥ وَسَعَىٰ فِي خَرَابِهَآۚ أُوْلَـٰٓئِكَ مَا كَانَ لَهُمۡ أَن يَدۡخُلُوهَآ إِلَّا خَآئِفِينَۚ لَهُمۡ فِي ٱلدُّنۡيَا خِزۡيٞ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٞ\nАллоҳнинг масжидларида Унинг исми зикр қилинишини ман этган ва уларни бузишга ҳаракат қилгандан ҳам золимроқ одам борми? Ана ўшалар у жойларга фақат қўрққан ҳолларидагина киришлари керак эди-ку! Уларга бу дунёда хорлик бордир ва уларга охиратда улуғ азоб бордир'}, {'chapter': 2, 'verse': 115, 'text': 'وَلِلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ فَأَيۡنَمَا تُوَلُّواْ فَثَمَّ وَجۡهُ ٱللَّهِۚ إِنَّ ٱللَّهَ وَٰسِعٌ عَلِيمٞ\nМашриқу мағриб Аллоҳникидир, қаёққа қарасангиз, Аллоҳнинг юзи бор. Албатта, Аллоҳ кенг қамровли, билувчи Зотдир'}, {'chapter': 2, 'verse': 116, 'text': 'وَقَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ بَل لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ\nУлар: «Аллоҳ фарзанд тутди», дерлар. У покдир. Албатта осмонлару ердаги барча нарсалар Уникидир. Ҳамма Унга бўйсунувчидир'}, {'chapter': 2, 'verse': 117, 'text': 'بَدِيعُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nУ осмонлару ерни пайдо қилувчидир. Бирор ишни қилмоқчи бўлганида, унга: «Бўл!» деса, бўлаверадир'}, {'chapter': 2, 'verse': 118, 'text': 'وَقَالَ ٱلَّذِينَ لَا يَعۡلَمُونَ لَوۡلَا يُكَلِّمُنَا ٱللَّهُ أَوۡ تَأۡتِينَآ ءَايَةٞۗ كَذَٰلِكَ قَالَ ٱلَّذِينَ مِن قَبۡلِهِم مِّثۡلَ قَوۡلِهِمۡۘ تَشَٰبَهَتۡ قُلُوبُهُمۡۗ قَدۡ بَيَّنَّا ٱلۡأٓيَٰتِ لِقَوۡمٖ يُوقِنُونَ\nБилмайдиган кимсалар: «Аллоҳ биз билан гаплашса эди ёки мўъжиза келса эди», дейишди. Улардан олдингилар ҳам уларнинг сўзига ўхшаш гапирган эдилар. Қалблари ўхшаш бўлди. Батаҳқиқ, Биз яхши билувчи қавмларга мўъжизаларни баён қилганмиз'}, {'chapter': 2, 'verse': 119, 'text': 'إِنَّآ أَرۡسَلۡنَٰكَ بِٱلۡحَقِّ بَشِيرٗا وَنَذِيرٗاۖ وَلَا تُسۡـَٔلُ عَنۡ أَصۡحَٰبِ ٱلۡجَحِيمِ\nАлбатта, Биз сени ҳақ билан суюнчли хабар берувчи ва огоҳлантирувчи қилиб юбордик. Ва дўзахийлар ҳақида сўралмассан'}, {'chapter': 2, 'verse': 120, 'text': 'وَلَن تَرۡضَىٰ عَنكَ ٱلۡيَهُودُ وَلَا ٱلنَّصَٰرَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمۡۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۗ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ ٱلَّذِي جَآءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ\nСендан яҳудийлар ҳам, насронийлар ҳам уларнинг миллатига эргашмагунингча ҳеч-ҳеч рози бўлмаслар. «Албатта, Аллоҳнинг ҳидояти ҳақиқий ҳидоятдир», деб айт. Агар сенга келган илмдан кейин ҳам уларнинг ҳавойи нафсларига эргашсанг, сени Аллоҳдан (қутқарувчи) дўст ҳам, ёрдамчи ҳам йўқдир'}, {'chapter': 2, 'verse': 121, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَتۡلُونَهُۥ حَقَّ تِلَاوَتِهِۦٓ أُوْلَـٰٓئِكَ يُؤۡمِنُونَ بِهِۦۗ وَمَن يَكۡفُرۡ بِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nБиз китоб берганлардан уни ўрнига қўйиб тиловат қиладиганлари, ана ўшалари унга иймон келтирадир. Ким унга куфр келтирса, ана ўшалар ўзлари ютқазганлардир'}, {'chapter': 2, 'verse': 122, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى ٱلۡعَٰلَمِينَ\nЭй, Бани Исроил, сизга берган неъматларимни ва Мен сизларни оламлардан афзал қилиб қўйганимни эсланг'}, {'chapter': 2, 'verse': 123, 'text': 'وَٱتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا عَدۡلٞ وَلَا تَنفَعُهَا شَفَٰعَةٞ وَلَا هُمۡ يُنصَرُونَ\nБир жон бошқа жондан ҳеч нарсани адо қила олмайдиган, ундан шафоат қабул қилинмайдиган, ундан тўлов олинмайдиган ва уларга ёрдам берилмайдиган Кундан қўрқинг'}, {'chapter': 2, 'verse': 124, 'text': '۞وَإِذِ ٱبۡتَلَىٰٓ إِبۡرَٰهِـۧمَ رَبُّهُۥ بِكَلِمَٰتٖ فَأَتَمَّهُنَّۖ قَالَ إِنِّي جَاعِلُكَ لِلنَّاسِ إِمَامٗاۖ قَالَ وَمِن ذُرِّيَّتِيۖ قَالَ لَا يَنَالُ عَهۡدِي ٱلظَّـٰلِمِينَ\nИброҳимни Роббиси калималар ила синаб кўрганда, у уларни батамом адо этганини эсла! У зот: «Мен, албатта, сени одамларга имом қилмоқчиман» – деди. У: «Зурриётимдан ҳамми?» – деди. У зот: «Золимлар аҳдимга ҳеч қачон эриша олмаслар», – деди'}, {'chapter': 2, 'verse': 125, 'text': 'وَإِذۡ جَعَلۡنَا ٱلۡبَيۡتَ مَثَابَةٗ لِّلنَّاسِ وَأَمۡنٗا وَٱتَّخِذُواْ مِن مَّقَامِ إِبۡرَٰهِـۧمَ مُصَلّٗىۖ وَعَهِدۡنَآ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ أَن طَهِّرَا بَيۡتِيَ لِلطَّآئِفِينَ وَٱلۡعَٰكِفِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ\n«Уй»ни одамлар учун масаба ва омонлик жойи қилганимизни эсла. Ва Иброҳимнинг мақомини намозгоҳ тутинг. Ва Иброҳим ва Исмоилга: «Уйимни тавоф қилувчилар, муқим турувчилар, рукуъ ва сажда қилувчилар учун поклаб қўйинглар», деб амр қилдик'}, {'chapter': 2, 'verse': 126, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ ٱجۡعَلۡ هَٰذَا بَلَدًا ءَامِنٗا وَٱرۡزُقۡ أَهۡلَهُۥ مِنَ ٱلثَّمَرَٰتِ مَنۡ ءَامَنَ مِنۡهُم بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُۥ قَلِيلٗا ثُمَّ أَضۡطَرُّهُۥٓ إِلَىٰ عَذَابِ ٱلنَّارِۖ وَبِئۡسَ ٱلۡمَصِيرُ\nИброҳимнинг: «Роббим, буни омонлик юрти қилгин ва аҳлидан Аллоҳга ва қиёмат кунига иймон келтирганларини мевалар ила ризқлантиргин», деганини эсла. У: «Ким куфр келтирса, озгина ҳузур бахш этаман, сўнгра дўзах азобига мажбур қиламан ва у қандай ҳам ёмон жой!» – деди'}, {'chapter': 2, 'verse': 127, 'text': 'وَإِذۡ يَرۡفَعُ إِبۡرَٰهِـۧمُ ٱلۡقَوَاعِدَ مِنَ ٱلۡبَيۡتِ وَإِسۡمَٰعِيلُ رَبَّنَا تَقَبَّلۡ مِنَّآۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nИброҳим билан Исмоил «Эй Роббимиз, биздан қабул эт, албатта, Сенинг Ўзинг эшитувчи, билувчи Зотсан», деб Байтнинг пойдеворларини кўтараётганини эсла'}, {'chapter': 2, 'verse': 128, 'text': 'رَبَّنَا وَٱجۡعَلۡنَا مُسۡلِمَيۡنِ لَكَ وَمِن ذُرِّيَّتِنَآ أُمَّةٗ مُّسۡلِمَةٗ لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبۡ عَلَيۡنَآۖ إِنَّكَ أَنتَ ٱلتَّوَّابُ ٱلرَّحِيمُ\n«Роббимиз, икковимизни Ўзингга мусулмон бўлганлардан қил ва зурриётимиздан ҳам Ўзингга мусулмон уммат қил, бизга ибодатларимизни кўрсат, тавбамизни қабул эт. Албатта, Сенинг Ўзинг тавбаларни кўплаб қабул этувчи, раҳимли Зотсан.»'}, {'chapter': 2, 'verse': 129, 'text': 'رَبَّنَا وَٱبۡعَثۡ فِيهِمۡ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِكَ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَيُزَكِّيهِمۡۖ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nРоббимиз, уларга ўзларининг ичидан уларга оятларингни тиловат қилиб берадиган, китобни ва ҳикматни ўргатадиган, уларни поклайдиган Пайғамбар юбор. Албатта, Сенинг Ўзинг Азийз ва ҳикматли зотсан'}, {'chapter': 2, 'verse': 130, 'text': 'وَمَن يَرۡغَبُ عَن مِّلَّةِ إِبۡرَٰهِـۧمَ إِلَّا مَن سَفِهَ نَفۡسَهُۥۚ وَلَقَدِ ٱصۡطَفَيۡنَٰهُ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ\nЎзини эси паст санаганларгина Иброҳимнинг миллатидан юз ўгирур. Албатта, Биз уни бу дунёда танлаб олдик ва, албатта, у охиратда солиҳлардан бўлгай'}, {'chapter': 2, 'verse': 131, 'text': 'إِذۡ قَالَ لَهُۥ رَبُّهُۥٓ أَسۡلِمۡۖ قَالَ أَسۡلَمۡتُ لِرَبِّ ٱلۡعَٰلَمِينَ\nЧунки Робби унга: «Мусулмон бўл!» деганида, у: «Оламларнинг Роббига мусулмон бўлдим», – деди'}, {'chapter': 2, 'verse': 132, 'text': 'وَوَصَّىٰ بِهَآ إِبۡرَٰهِـۧمُ بَنِيهِ وَيَعۡقُوبُ يَٰبَنِيَّ إِنَّ ٱللَّهَ ٱصۡطَفَىٰ لَكُمُ ٱلدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ\nИброҳим ўша(миллат)ни болаларига васият қилди. Яъқуб ҳам: «Эй болаларим, албатта, Аллоҳ сизларга динни танлади, сизлар мусулмон бўлмасдан туриб ўлмассиз», – деди'}, {'chapter': 2, 'verse': 133, 'text': 'أَمۡ كُنتُمۡ شُهَدَآءَ إِذۡ حَضَرَ يَعۡقُوبَ ٱلۡمَوۡتُ إِذۡ قَالَ لِبَنِيهِ مَا تَعۡبُدُونَ مِنۢ بَعۡدِيۖ قَالُواْ نَعۡبُدُ إِلَٰهَكَ وَإِلَٰهَ ءَابَآئِكَ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ إِلَٰهٗا وَٰحِدٗا وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\nЁки Яъқубга ўлим ҳозир бўлиб, у ўз болаларига: «Мендан кейин нимага ибодат қиласизлар?» деганида, улар: «Сенинг Илоҳингга ва оталаринг Иброҳим, Исмоил ва Исҳоқнинг Илоҳига, ёлғиз Илоҳга ибодат қиламиз ҳамда биз Унга мусулмон бўлувчимиз», деганларида гувоҳ бўлганмисиз'}, {'chapter': 2, 'verse': 134, 'text': 'تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ\nУлар бир уммат эди, ўтиб кетдилар. Уларга ўзлари касб қилганлари бўлади. Сизларга ўзингиз касб қилганингиз бўладир. Уларнинг қилган амалларидан сиз сўралмассиз'}, {'chapter': 2, 'verse': 135, 'text': 'وَقَالُواْ كُونُواْ هُودًا أَوۡ نَصَٰرَىٰ تَهۡتَدُواْۗ قُلۡ بَلۡ مِلَّةَ إِبۡرَٰهِـۧمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nУлар: «Яҳудий ёки насроний бўлинг, ҳидоят топасиз», дерлар. «Балки, Иброҳимнинг ҳаниф миллатига (динига) эргашамиз ва у мушриклардан бўлмаган», деб айт'}, {'chapter': 2, 'verse': 136, 'text': 'قُولُوٓاْ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَآ أُوتِيَ ٱلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\nАйтинглар: «Аллоҳга ва бизга туширилган нарсага, Иброҳим, Исмоил, Исҳоқ, Яъқуб, асботларга туширилган нарсага, Мусо ва Ийсога берилган нарсага ва Пайғамбарларга Роббиларидан берилган нарсага иймон келтирдик. Уларнинг орасидан бирортасини фарқламасмиз ва биз Унга мусулмонлармиз»'}, {'chapter': 2, 'verse': 137, 'text': 'فَإِنۡ ءَامَنُواْ بِمِثۡلِ مَآ ءَامَنتُم بِهِۦ فَقَدِ ٱهۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا هُمۡ فِي شِقَاقٖۖ فَسَيَكۡفِيكَهُمُ ٱللَّهُۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАгар сиз иймон келтирганга ўхшаш иймон келтирсалар, батаҳқиқ, ҳидоят топарлар. Ва агар юз ўгирсалар, бас, албатта, улар хилофдан бошқа нарсада бўлмаслар. Аллоҳ сенга улар (ёмонлиги)дан кифоя қилур. Ва У эшитувчи, билувчи Зотдир'}, {'chapter': 2, 'verse': 138, 'text': 'صِبۡغَةَ ٱللَّهِ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ صِبۡغَةٗۖ وَنَحۡنُ لَهُۥ عَٰبِدُونَ\nАллоҳнинг бўёғини (лозим тутинг). Аллоҳнинг бўёғидан чиройлироқ бўёқ бергувчи ким бор?! Ва биз Унга ибодат қилувчилармиз'}, {'chapter': 2, 'verse': 139, 'text': 'قُلۡ أَتُحَآجُّونَنَا فِي ٱللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمۡ وَلَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ وَنَحۡنُ لَهُۥ مُخۡلِصُونَ\nАйт: «Биз билан Аллоҳ ҳақида тортишасизми?! Ва ҳолбуки, У бизнинг Роббимиз ва сизнинг Роббингиз, бизнинг ўз амалларимиз бор, сизнинг ўз амалларингиз бор. Ва биз Унга мухлисмиз»'}, {'chapter': 2, 'verse': 140, 'text': 'أَمۡ تَقُولُونَ إِنَّ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطَ كَانُواْ هُودًا أَوۡ نَصَٰرَىٰۗ قُلۡ ءَأَنتُمۡ أَعۡلَمُ أَمِ ٱللَّهُۗ وَمَنۡ أَظۡلَمُ مِمَّن كَتَمَ شَهَٰدَةً عِندَهُۥ مِنَ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nЁки: «Албатта, Иброҳим, Исмоил, Исҳоқ, Яъқуб ва асботлар яҳудий ва насроний бўлган», дейсизларми?! «Сизлар билимдонроқми ёки Аллоҳми?» дегин. Ҳузуридаги Аллоҳдан бўлган гувоҳликни беркитган кимсадан ҳам золимроқ одам борми?! Ва Аллоҳ қилаётган ишларингиздан ғофил эмасдир'}, {'chapter': 2, 'verse': 141, 'text': 'تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ\nУлар бир уммат эдилар, ўтиб кетдилар. Уларга ўзлари касб қилганлари бўлур. Сизларга ўзингиз касб қилганингиз бўладир. Уларнинг қилган амалларидан сизлар сўралмассиз'}, {'chapter': 2, 'verse': 142, 'text': '۞سَيَقُولُ ٱلسُّفَهَآءُ مِنَ ٱلنَّاسِ مَا وَلَّىٰهُمۡ عَن قِبۡلَتِهِمُ ٱلَّتِي كَانُواْ عَلَيۡهَاۚ قُل لِّلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nОдамлардан эси пастлари: «Буларни қиблаларидан нима юз ўгиртирди экан?» дерлар. Сен: «Машриқу мағриб Аллоҳники, кимни хоҳласа, ўшани тўғри йўлга бошлайдир», деб айт'}, {'chapter': 2, 'verse': 143, 'text': 'وَكَذَٰلِكَ جَعَلۡنَٰكُمۡ أُمَّةٗ وَسَطٗا لِّتَكُونُواْ شُهَدَآءَ عَلَى ٱلنَّاسِ وَيَكُونَ ٱلرَّسُولُ عَلَيۡكُمۡ شَهِيدٗاۗ وَمَا جَعَلۡنَا ٱلۡقِبۡلَةَ ٱلَّتِي كُنتَ عَلَيۡهَآ إِلَّا لِنَعۡلَمَ مَن يَتَّبِعُ ٱلرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيۡهِۚ وَإِن كَانَتۡ لَكَبِيرَةً إِلَّا عَلَى ٱلَّذِينَ هَدَى ٱللَّهُۗ وَمَا كَانَ ٱللَّهُ لِيُضِيعَ إِيمَٰنَكُمۡۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٞ رَّحِيمٞ\nШунингдек, сизларни одамлар устидан гувоҳ бўлишингиз ва Пайғамбар сизларнинг устингиздан гувоҳ бўлиши учун ўрта миллат қилдик. Сен аввал юзланган қиблани, фақат, ортга бурилиб кетадиганлар ким-у, Пайғамбарга эргашувчилар ким эканини билиш учунгина қилганмиз. Гарчи бу иш Аллоҳ ҳидоятга солганлардан бошқаларга оғир бўлса ҳам. Аллоҳ иймонингизни зое қилмас. Албатта, Аллоҳ одамларга меҳрибон ва раҳимлидир'}, {'chapter': 2, 'verse': 144, 'text': 'قَدۡ نَرَىٰ تَقَلُّبَ وَجۡهِكَ فِي ٱلسَّمَآءِۖ فَلَنُوَلِّيَنَّكَ قِبۡلَةٗ تَرۡضَىٰهَاۚ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥۗ وَإِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ لَيَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ\nГоҳо юзингнинг осмонга тез-тез бурилганини кўрурмиз. Бас, албатта, сени ўзинг рози бўлган қиблага қаратурмиз. Юзингни Масжидул Ҳаром томон бур. Қаерда бўлсангиз ҳам, юзингизни у томон бурингиз. Албатта, китоб берилганлар унинг Роббиларидан бўлган ҳақ эканини билурлар. Ва Аллоҳ уларнинг қилаётган амалларидан ғофил эмасдир'}, {'chapter': 2, 'verse': 145, 'text': 'وَلَئِنۡ أَتَيۡتَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ بِكُلِّ ءَايَةٖ مَّا تَبِعُواْ قِبۡلَتَكَۚ وَمَآ أَنتَ بِتَابِعٖ قِبۡلَتَهُمۡۚ وَمَا بَعۡضُهُم بِتَابِعٖ قِبۡلَةَ بَعۡضٖۚ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم مِّنۢ بَعۡدِ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ إِنَّكَ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ\nАгар китоб берилганларга барча ҳужжатларни келтирсанг ҳам, сенинг қиблангга эргашмаслар. Сен ҳам уларнинг қибласига эргашувчи эмассан. Ва улар бир-бирларининг қибласига эргашувчи эмаслар. Агар сенга келган илмдан кейин ҳам уларнинг ҳавойи нафсларига эргашсанг, унда, албатта, золимлардан бўлурсан'}, {'chapter': 2, 'verse': 146, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمۡۖ وَإِنَّ فَرِيقٗا مِّنۡهُمۡ لَيَكۡتُمُونَ ٱلۡحَقَّ وَهُمۡ يَعۡلَمُونَ\nКитоб берилганлар уни худди болаларини танигандек танирлар. Ва, албатта, улардан бир гуруҳлари билиб туриб ҳақни беркитарлар'}, {'chapter': 2, 'verse': 147, 'text': 'ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ\nБу ҳақ Роббингдандир, ҳечҳеч шак қилувчилардан бўлма'}, {'chapter': 2, 'verse': 148, 'text': 'وَلِكُلّٖ وِجۡهَةٌ هُوَ مُوَلِّيهَاۖ فَٱسۡتَبِقُواْ ٱلۡخَيۡرَٰتِۚ أَيۡنَ مَا تَكُونُواْ يَأۡتِ بِكُمُ ٱللَّهُ جَمِيعًاۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nҲар кимнинг юзланадиган тарафи бор. У ўшанга боқадир. Яхшиликларга шошилинг. Қаерда бўлсангиз ҳам, Аллоҳ сизларни жамлайдир. Албатта, Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 2, 'verse': 149, 'text': 'وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ وَإِنَّهُۥ لَلۡحَقُّ مِن رَّبِّكَۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nҚаердан чиқсанг ҳам, юзингни Масжидул Ҳаром томон бур. Ва, албатта, бу Роббингдан бўлган ҳақдир. Ва Аллоҳ сиз қилаётган нарсадан ғофил эмасдир'}, {'chapter': 2, 'verse': 150, 'text': 'وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيۡكُمۡ حُجَّةٌ إِلَّا ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِي وَلِأُتِمَّ نِعۡمَتِي عَلَيۡكُمۡ وَلَعَلَّكُمۡ تَهۡتَدُونَ\nВа қаердан чиқсанг ҳам, юзингни Масжидул Ҳаром томон бур. Қаерда бўлсангиз ҳам, юзингизни у томонга буринг. Одамларда сизга қарши ҳужжат бўлмаслиги учун. Улардан зулм қилганларидан қўрқманглар, Мендан қўрқинглар. Ва сизга неъматимни батамом этиш учун, шоядки, ҳидоят топсангиз'}, {'chapter': 2, 'verse': 151, 'text': 'كَمَآ أَرۡسَلۡنَا فِيكُمۡ رَسُولٗا مِّنكُمۡ يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِنَا وَيُزَكِّيكُمۡ وَيُعَلِّمُكُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَيُعَلِّمُكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ\nШунингдек, ичингизга ўзингиздан, сизларга оятларимизни тиловат қиладиган, сизларни поклайдиган, сизларга китобни, ҳикматни ва билмайдиган нарсаларингизни ўргатадиган Пайғамбар юбордик'}, {'chapter': 2, 'verse': 152, 'text': 'فَٱذۡكُرُونِيٓ أَذۡكُرۡكُمۡ وَٱشۡكُرُواْ لِي وَلَا تَكۡفُرُونِ\nБас, Мени эслангиз, сизни эсларман. Ва Менга шукр қилингиз, куфр қилмангиз'}, {'chapter': 2, 'verse': 153, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱسۡتَعِينُواْ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِينَ\nЭй, иймон келтирганлар! Сабр ва намоз ила мадад сўранглар. Албатта, Аллоҳ сабрлилар биландир'}, {'chapter': 2, 'verse': 154, 'text': 'وَلَا تَقُولُواْ لِمَن يُقۡتَلُ فِي سَبِيلِ ٱللَّهِ أَمۡوَٰتُۢۚ بَلۡ أَحۡيَآءٞ وَلَٰكِن لَّا تَشۡعُرُونَ\nАллоҳнинг йўлида қатл бўлганларни «ўликлар» деманг. Балки улар тирикдирлар, лекин сиз сезмассиз'}, {'chapter': 2, 'verse': 155, 'text': 'وَلَنَبۡلُوَنَّكُم بِشَيۡءٖ مِّنَ ٱلۡخَوۡفِ وَٱلۡجُوعِ وَنَقۡصٖ مِّنَ ٱلۡأَمۡوَٰلِ وَٱلۡأَنفُسِ وَٱلثَّمَرَٰتِۗ وَبَشِّرِ ٱلصَّـٰبِرِينَ\nАлбата, Биз сизларни бир оз қўрқинч ва очлик билан, мол-мулкка, жонга, меваларга нуқсон етказиш билан синаймиз. Ва сабрлиларга башорат бер'}, {'chapter': 2, 'verse': 156, 'text': 'ٱلَّذِينَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةٞ قَالُوٓاْ إِنَّا لِلَّهِ وَإِنَّآ إِلَيۡهِ رَٰجِعُونَ\nУлар мусийбат етганда: «Албатта, биз Аллоҳникимиз ва, албатта, биз Унга қайтувчимиз», дерлар'}, {'chapter': 2, 'verse': 157, 'text': 'أُوْلَـٰٓئِكَ عَلَيۡهِمۡ صَلَوَٰتٞ مِّن رَّبِّهِمۡ وَرَحۡمَةٞۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُهۡتَدُونَ\nАна ўшаларга Роббиларидан саловотлар ва раҳмат бор. Ана ўшалар ҳидоят топганлардир'}, {'chapter': 2, 'verse': 158, 'text': '۞إِنَّ ٱلصَّفَا وَٱلۡمَرۡوَةَ مِن شَعَآئِرِ ٱللَّهِۖ فَمَنۡ حَجَّ ٱلۡبَيۡتَ أَوِ ٱعۡتَمَرَ فَلَا جُنَاحَ عَلَيۡهِ أَن يَطَّوَّفَ بِهِمَاۚ وَمَن تَطَوَّعَ خَيۡرٗا فَإِنَّ ٱللَّهَ شَاكِرٌ عَلِيمٌ\nАлбатта, Сафо ва Марва Аллоҳнинг (динининг) белгиларидандир. Ким Байтуллоҳни ҳаж ёки умра қилса, икковлари ўртасида саъйи қилиш гуноҳ эмас. Ким яхшиликни холисона қилса, бас, албатта, Аллоҳ Шакур(оз амал учун кўп савоб берувчи) ва билувчидир'}, {'chapter': 2, 'verse': 159, 'text': 'إِنَّ ٱلَّذِينَ يَكۡتُمُونَ مَآ أَنزَلۡنَا مِنَ ٱلۡبَيِّنَٰتِ وَٱلۡهُدَىٰ مِنۢ بَعۡدِ مَا بَيَّنَّـٰهُ لِلنَّاسِ فِي ٱلۡكِتَٰبِ أُوْلَـٰٓئِكَ يَلۡعَنُهُمُ ٱللَّهُ وَيَلۡعَنُهُمُ ٱللَّـٰعِنُونَ\nАлбатта, Биз нозил қилган равшан оятларни ва ҳидоятни одамларга китобда баён қилиб берганимиздан кейин беркитганларни — ўшаларни Аллоҳ лаънатлайдир. Ва лаънатловчилар лаънатлайдир'}, {'chapter': 2, 'verse': 160, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَبَيَّنُواْ فَأُوْلَـٰٓئِكَ أَتُوبُ عَلَيۡهِمۡ وَأَنَا ٱلتَّوَّابُ ٱلرَّحِيمُ\nМагар тавба қилиб, аҳли солиҳ бўлиб, баён қилганларнинг, ана ўшаларнинг тавбасини қабул қилурман. Ва Мен тавбаларни кўплаб қабул қилувчи ва раҳимлидирман'}, {'chapter': 2, 'verse': 161, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٌ أُوْلَـٰٓئِكَ عَلَيۡهِمۡ لَعۡنَةُ ٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلنَّاسِ أَجۡمَعِينَ\nАлбатта, куфр келтирган ва кофирлик ҳолида ўлганларга, ана ўшаларга Аллоҳнинг, фаришталарнинг ва одамларнинг — барчанинг лаънати бўлгай'}, {'chapter': 2, 'verse': 162, 'text': 'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ\nУнда абадий бўлурлар. Улардан азоб енгиллатилмас. Ва уларга назар ҳам солинмас'}, {'chapter': 2, 'verse': 163, 'text': 'وَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ لَّآ إِلَٰهَ إِلَّا هُوَ ٱلرَّحۡمَٰنُ ٱلرَّحِيمُ\nВа илоҳингиз бир илоҳдир. Роҳман ва Роҳийм бўлмиш Ундан ўзга ибодатга сазовор зот йўқдир'}, {'chapter': 2, 'verse': 164, 'text': 'إِنَّ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَٱلۡفُلۡكِ ٱلَّتِي تَجۡرِي فِي ٱلۡبَحۡرِ بِمَا يَنفَعُ ٱلنَّاسَ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن مَّآءٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖ وَتَصۡرِيفِ ٱلرِّيَٰحِ وَٱلسَّحَابِ ٱلۡمُسَخَّرِ بَيۡنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nАлбатта, осмонлару ернинг яратилишида, кечаю кундузнинг алмашиб туришида, одамларга манфаатли нарсалари ила денгизда юриб турган кемада, Аллоҳнинг осмондан сув тушириб, у билан ўлган ерни тирилтириши ва унда турли жондорларни таратишида, шамолларни йўналтиришида, осмону ер орасидаги итоатгўй булутда ақл юритувчи қавмлар учун белгилар бор'}, {'chapter': 2, 'verse': 165, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَتَّخِذُ مِن دُونِ ٱللَّهِ أَندَادٗا يُحِبُّونَهُمۡ كَحُبِّ ٱللَّهِۖ وَٱلَّذِينَ ءَامَنُوٓاْ أَشَدُّ حُبّٗا لِّلَّهِۗ وَلَوۡ يَرَى ٱلَّذِينَ ظَلَمُوٓاْ إِذۡ يَرَوۡنَ ٱلۡعَذَابَ أَنَّ ٱلۡقُوَّةَ لِلَّهِ جَمِيعٗا وَأَنَّ ٱللَّهَ شَدِيدُ ٱلۡعَذَابِ\nОдамлар ичида Аллоҳдан ўзга тенгдошларни тутадиганлар ва уларни Аллоҳни севгандек севадиганлар бор. Иймон келтирганларнинг Аллоҳга муҳаббатлари қаттиқдир. Зулм қилганлар азобни кўрган чоғларида, албатта, барча қувват Аллоҳга хослигини ва, албатта, Аллоҳнинг азоби шиддатли эканини билсалар эди'}, {'chapter': 2, 'verse': 166, 'text': 'إِذۡ تَبَرَّأَ ٱلَّذِينَ ٱتُّبِعُواْ مِنَ ٱلَّذِينَ ٱتَّبَعُواْ وَرَأَوُاْ ٱلۡعَذَابَ وَتَقَطَّعَتۡ بِهِمُ ٱلۡأَسۡبَابُ\nУ пайтда пешволар (ўзларига) эргашганлардан тонарлар ва улар азобни кўрарлар, уларнинг боғланишлари кесилур'}, {'chapter': 2, 'verse': 167, 'text': 'وَقَالَ ٱلَّذِينَ ٱتَّبَعُواْ لَوۡ أَنَّ لَنَا كَرَّةٗ فَنَتَبَرَّأَ مِنۡهُمۡ كَمَا تَبَرَّءُواْ مِنَّاۗ كَذَٰلِكَ يُرِيهِمُ ٱللَّهُ أَعۡمَٰلَهُمۡ حَسَرَٰتٍ عَلَيۡهِمۡۖ وَمَا هُم بِخَٰرِجِينَ مِنَ ٱلنَّارِ\nВа эргашганлар: «Қани энди бизга бир ортга қайтиш бўлса эди, биздан тонганларидек, биз ҳам улардан тонар эдик», дерлар. Шундай қилиб, Аллоҳ уларга амалларининг ўзларига ҳасрат бўлганини кўрсатур. Ва улар дўзахдан чиқувчи эмаслар'}, {'chapter': 2, 'verse': 168, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ كُلُواْ مِمَّا فِي ٱلۡأَرۡضِ حَلَٰلٗا طَيِّبٗا وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٌ\nЭй одамлар! Ер юзидаги нарсалардан ҳалол-покларини енглар. Ва шайтон изидан эргашманглар, чунки у сизларга очиқ душмандир'}, {'chapter': 2, 'verse': 169, 'text': 'إِنَّمَا يَأۡمُرُكُم بِٱلسُّوٓءِ وَٱلۡفَحۡشَآءِ وَأَن تَقُولُواْ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nАлбатта, у сизларни ёмон ва фаҳш ишларга ҳамда Аллоҳга қарши билмаган нарсангизни гапиришингизга буюрадир'}, {'chapter': 2, 'verse': 170, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُواْ مَآ أَنزَلَ ٱللَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَآ أَلۡفَيۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡقِلُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ\nУларга: «Аллоҳ нозил қилган нарсага эргашингиз», дейилса, улар: «Йўқ, биз оталаримизни нимада топсак, шунга эргашамиз», дерлар. Оталари ҳеч нарсага ақли етмаган ва ҳидоят топмаган бўлсалар ҳам-а'}, {'chapter': 2, 'verse': 171, 'text': 'وَمَثَلُ ٱلَّذِينَ كَفَرُواْ كَمَثَلِ ٱلَّذِي يَنۡعِقُ بِمَا لَا يَسۡمَعُ إِلَّا دُعَآءٗ وَنِدَآءٗۚ صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَعۡقِلُونَ\nКуфр келтирганлар мисоли худди қичқириқ ва сасдан бошқа нарсани эшитмайдиганларга бақираётганга ўхшайди. Улар кар, соқов ва кўрдирлар. Улар ақл ишлатмаслар'}, {'chapter': 2, 'verse': 172, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَٱشۡكُرُواْ لِلَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ\nЭй, иймон келтирганлар! Сизларга ризқ қилиб берганимиз пок нарсалардан енглар ва Аллоҳга шукр қилинглар, агар Унга ибодат қиладиган бўлсаларингиз'}, {'chapter': 2, 'verse': 173, 'text': 'إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ بِهِۦ لِغَيۡرِ ٱللَّهِۖ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nАлбатта, У зот сизларга фақат ўлимтикни, қонни, чўчқанинг гўштини ва Аллоҳдан бошқага сўйилганни ҳаром қилди. Кимки мажбур бўлса-ю, зулм қилмай, ҳаддан ошмай (еса), унга гуноҳ бўлмас. Албатта, Аллоҳ мағфиратли ва раҳимлидир'}, {'chapter': 2, 'verse': 174, 'text': 'إِنَّ ٱلَّذِينَ يَكۡتُمُونَ مَآ أَنزَلَ ٱللَّهُ مِنَ ٱلۡكِتَٰبِ وَيَشۡتَرُونَ بِهِۦ ثَمَنٗا قَلِيلًا أُوْلَـٰٓئِكَ مَا يَأۡكُلُونَ فِي بُطُونِهِمۡ إِلَّا ٱلنَّارَ وَلَا يُكَلِّمُهُمُ ٱللَّهُ يَوۡمَ ٱلۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ\nАлбатта, Аллоҳ китобда туширган нарсани беркитганлар ва уни озгина баҳога сотганлар, ана ўшалар қоринларига оловдан бошқа нарсани емаслар, уларга Аллоҳ қиёмат куни гапирмас ва уларни покламас. Уларга аламли азоблар бор'}, {'chapter': 2, 'verse': 175, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلضَّلَٰلَةَ بِٱلۡهُدَىٰ وَٱلۡعَذَابَ بِٱلۡمَغۡفِرَةِۚ فَمَآ أَصۡبَرَهُمۡ عَلَى ٱلنَّارِ\nАна ўшалар адашувни ҳидоятга, азобни мағфиратга сотиб олганлардир. Дўзах ўтига қандоқ чидайдилар-а'}, {'chapter': 2, 'verse': 176, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ نَزَّلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّۗ وَإِنَّ ٱلَّذِينَ ٱخۡتَلَفُواْ فِي ٱلۡكِتَٰبِ لَفِي شِقَاقِۭ بَعِيدٖ\nБунинг сабаби, албатта, Аллоҳ китобни ҳақ билан нозил қилган эди. Китоб хусусида ихтилоф қилганлар эса, тубсиз тортишувдадирлар'}, {'chapter': 2, 'verse': 177, 'text': '۞لَّيۡسَ ٱلۡبِرَّ أَن تُوَلُّواْ وُجُوهَكُمۡ قِبَلَ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَلَٰكِنَّ ٱلۡبِرَّ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلۡكِتَٰبِ وَٱلنَّبِيِّـۧنَ وَءَاتَى ٱلۡمَالَ عَلَىٰ حُبِّهِۦ ذَوِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَٱلسَّآئِلِينَ وَفِي ٱلرِّقَابِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَٱلۡمُوفُونَ بِعَهۡدِهِمۡ إِذَا عَٰهَدُواْۖ وَٱلصَّـٰبِرِينَ فِي ٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ وَحِينَ ٱلۡبَأۡسِۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ صَدَقُواْۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُتَّقُونَ\nЯхшилик юзингизни машриқ ёки мағриб томонга буришингизда эмас. Лекин яхшилик ким Аллоҳга, охират кунига, фаришталарга, китобга, Пайғамбарларга иймон келтирса ва яхши кўрган молини қариндошларга, етимларга, мискинларга, ватангадоларга, тиланчиларга, қул озод қилишга берса, намозни қоим қилса, закот берса'}, {'chapter': 2, 'verse': 178, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ ٱلۡقِصَاصُ فِي ٱلۡقَتۡلَىۖ ٱلۡحُرُّ بِٱلۡحُرِّ وَٱلۡعَبۡدُ بِٱلۡعَبۡدِ وَٱلۡأُنثَىٰ بِٱلۡأُنثَىٰۚ فَمَنۡ عُفِيَ لَهُۥ مِنۡ أَخِيهِ شَيۡءٞ فَٱتِّبَاعُۢ بِٱلۡمَعۡرُوفِ وَأَدَآءٌ إِلَيۡهِ بِإِحۡسَٰنٖۗ ذَٰلِكَ تَخۡفِيفٞ مِّن رَّبِّكُمۡ وَرَحۡمَةٞۗ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ\nЭй, иймон келтирганлар! Сизга ўлдирилганлар учун қасос фарз қилинди: ҳурга ҳур, қулга қул, аёлга аёл. Кимга ўз биродаридан бир нарса афв қилинса, бас, яхшилик ила сўралсин ва адо этиш ҳам яхшиликча бўлсин. Бу, Роббиларингиздан енгиллик ва раҳматдир. Ундан кейин ким душманлик қилса, унга аламли азоб бор'}, {'chapter': 2, 'verse': 179, 'text': 'وَلَكُمۡ فِي ٱلۡقِصَاصِ حَيَوٰةٞ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ لَعَلَّكُمۡ تَتَّقُونَ\nСизларга қасос олишда ҳаёт бор. Эй, ақл эгалари! Шоядки, тақводор бўлсангиз'}, {'chapter': 2, 'verse': 180, 'text': 'كُتِبَ عَلَيۡكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ إِن تَرَكَ خَيۡرًا ٱلۡوَصِيَّةُ لِلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِينَ\nСизларга, бирортангизга ўлим келганда, агар у бойлик қолдираётган бўлса, ота-онаси ва яқинларига тўғрилик билан васият қилиш тақводорларнинг бурчи сифатида фарз қилинди'}, {'chapter': 2, 'verse': 181, 'text': 'فَمَنۢ بَدَّلَهُۥ بَعۡدَ مَا سَمِعَهُۥ فَإِنَّمَآ إِثۡمُهُۥ عَلَى ٱلَّذِينَ يُبَدِّلُونَهُۥٓۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nКим уни эшитгандан сўнг ўзгартирса, бас, гуноҳи фақат ўзгартирганларнинг ўзига бўладир. Албатта, Аллоҳ эшитувчи ва билувчидир'}, {'chapter': 2, 'verse': 182, 'text': 'فَمَنۡ خَافَ مِن مُّوصٖ جَنَفًا أَوۡ إِثۡمٗا فَأَصۡلَحَ بَيۡنَهُمۡ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nКимки васият қилувчидан тойилиш ёки гуноҳ содир бўлишидан қўрқса, бас, уларнинг орасини ислоҳ қилса, унга гуноҳ бўлмас. Албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 2, 'verse': 183, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ ٱلصِّيَامُ كَمَا كُتِبَ عَلَى ٱلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ\nЭй, иймон келтирганлар! Сизлардан аввалгиларга фарз қилинганидек, сизларга ҳам рўза фарз қилинди. Шоядки, тақводор бўлсаларингиз'}, {'chapter': 2, 'verse': 184, 'text': 'أَيَّامٗا مَّعۡدُودَٰتٖۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۚ وَعَلَى ٱلَّذِينَ يُطِيقُونَهُۥ فِدۡيَةٞ طَعَامُ مِسۡكِينٖۖ فَمَن تَطَوَّعَ خَيۡرٗا فَهُوَ خَيۡرٞ لَّهُۥۚ وَأَن تَصُومُواْ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nСаноқли кунлар. Сизлардан ким бемор ёки мусофир бўлса, бас, саноғини бошқа кунлардан тутадир. Уни қийналиб тутадиганлар зиммасида бир мискин таоми фидя бордир. Ким яхшиликни ўз ихтиёри ила қилса, ўзи учун яхшидир. Агар билсангиз, рўза тутмоғингиз сиз учун яхшидир'}, {'chapter': 2, 'verse': 185, 'text': 'شَهۡرُ رَمَضَانَ ٱلَّذِيٓ أُنزِلَ فِيهِ ٱلۡقُرۡءَانُ هُدٗى لِّلنَّاسِ وَبَيِّنَٰتٖ مِّنَ ٱلۡهُدَىٰ وَٱلۡفُرۡقَانِۚ فَمَن شَهِدَ مِنكُمُ ٱلشَّهۡرَ فَلۡيَصُمۡهُۖ وَمَن كَانَ مَرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۗ يُرِيدُ ٱللَّهُ بِكُمُ ٱلۡيُسۡرَ وَلَا يُرِيدُ بِكُمُ ٱلۡعُسۡرَ وَلِتُكۡمِلُواْ ٱلۡعِدَّةَ وَلِتُكَبِّرُواْ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nРамазон ойики, унда одамларга ҳидоят ҳамда ҳидояту фурқондан иборат очиқ баёнотлар бўлиб Қуръон туширилгандир. Сиздан ким у ойда ҳозир бўлса, рўзасини тутсин. Ким бемор ёки сафарда бўлса, бас, саноғини бошқа кунлардан тутадир. Аллоҳ сизларга енгилликни хоҳлайди ва сизларга қийинликни хоҳламас. Саноғини мукаммал қилишингиз ва сизни ҳидоятга бошлаган Аллоҳни улуғлашингиз учун. Шоядки, шукр қилсаларингиз'}, {'chapter': 2, 'verse': 186, 'text': 'وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌۖ أُجِيبُ دَعۡوَةَ ٱلدَّاعِ إِذَا دَعَانِۖ فَلۡيَسۡتَجِيبُواْ لِي وَلۡيُؤۡمِنُواْ بِي لَعَلَّهُمۡ يَرۡشُدُونَ\nАгар бандаларим сендан Мени сўрасалар, Мен, албатта, яқинман. Дуо қилгувчи дуо қилганда, ижобат қилурман. Бас, Менга ҳам ижобат қилсинлар ва иймон келтирсинлар. Шоядки, тўғри йўлни топсалар'}, {'chapter': 2, 'verse': 187, 'text': 'أُحِلَّ لَكُمۡ لَيۡلَةَ ٱلصِّيَامِ ٱلرَّفَثُ إِلَىٰ نِسَآئِكُمۡۚ هُنَّ لِبَاسٞ لَّكُمۡ وَأَنتُمۡ لِبَاسٞ لَّهُنَّۗ عَلِمَ ٱللَّهُ أَنَّكُمۡ كُنتُمۡ تَخۡتَانُونَ أَنفُسَكُمۡ فَتَابَ عَلَيۡكُمۡ وَعَفَا عَنكُمۡۖ فَٱلۡـَٰٔنَ بَٰشِرُوهُنَّ وَٱبۡتَغُواْ مَا كَتَبَ ٱللَّهُ لَكُمۡۚ وَكُلُواْ وَٱشۡرَبُواْ حَتَّىٰ يَتَبَيَّنَ لَكُمُ ٱلۡخَيۡطُ ٱلۡأَبۡيَضُ مِنَ ٱلۡخَيۡطِ ٱلۡأَسۡوَدِ مِنَ ٱلۡفَجۡرِۖ ثُمَّ أَتِمُّواْ ٱلصِّيَامَ إِلَى ٱلَّيۡلِۚ وَلَا تُبَٰشِرُوهُنَّ وَأَنتُمۡ عَٰكِفُونَ فِي ٱلۡمَسَٰجِدِۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَقۡرَبُوهَاۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ يَتَّقُونَ\nСизларга рўза кечасида хотинларингизга яқинлик қилиш ҳалол қилинди. Улар сизга либосдир, сиз уларга либосдирсиз. Аллоҳ сизнинг ўзингизга хиёнат қилаётганингизни билди, тавбангизни қабул этди ва сизларни афв этди. Энди уларга яқинлашаверинг ва Аллоҳ сизга ёзган нарсани талаб қилинг. Тонг пайтида оқ ип қора ипдан ажрагунча еб-ичаверинг. Сўнгра, рўзани кечасигача батамом қилинг. Масжидларда, эътикофдалигингизда уларга яқинлик қилманг. Булар Аллоҳнинг чегараларидир, унга яқинлашманг. Аллоҳ одамларга Ўз оятларини шундоқ баён этадир. Шоядки, тақво қилсалар'}, {'chapter': 2, 'verse': 188, 'text': 'وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِٱلۡبَٰطِلِ وَتُدۡلُواْ بِهَآ إِلَى ٱلۡحُكَّامِ لِتَأۡكُلُواْ فَرِيقٗا مِّنۡ أَمۡوَٰلِ ٱلنَّاسِ بِٱلۡإِثۡمِ وَأَنتُمۡ تَعۡلَمُونَ\nБир-бирларингизнинг молларингизни ботил йўл билан еманг. Билиб туриб одамларнинг молларидан бир қисмини ейишингиз учун ҳокимларга гуноҳкорона ташламанг'}, {'chapter': 2, 'verse': 189, 'text': '۞يَسۡـَٔلُونَكَ عَنِ ٱلۡأَهِلَّةِۖ قُلۡ هِيَ مَوَٰقِيتُ لِلنَّاسِ وَٱلۡحَجِّۗ وَلَيۡسَ ٱلۡبِرُّ بِأَن تَأۡتُواْ ٱلۡبُيُوتَ مِن ظُهُورِهَا وَلَٰكِنَّ ٱلۡبِرَّ مَنِ ٱتَّقَىٰۗ وَأۡتُواْ ٱلۡبُيُوتَ مِنۡ أَبۡوَٰبِهَاۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\nСендан ҳилоллар ҳақида сўрарлар. Улар одамлар ва ҳаж учун вақт ўлчовидир, деб айт. Яхшилик уйларнинг орқасидан киришингизда эмас, лекин яхшилик ким тақводорлик қилса, шундадир. Ва уйларга эшикларидан киринг. Аллоҳга тақво қилинг. Шоядки, нажот топсангиз'}, {'chapter': 2, 'verse': 190, 'text': 'وَقَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ ٱلَّذِينَ يُقَٰتِلُونَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ\nСизга уруш қилаётганларга қарши Аллоҳнинг йўлида уруш қилинг. Ва тажовузкор бўлманг. Албатта, Аллоҳ тажовузкор бўлганларни хуш кўрмас'}, {'chapter': 2, 'verse': 191, 'text': 'وَٱقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡ وَأَخۡرِجُوهُم مِّنۡ حَيۡثُ أَخۡرَجُوكُمۡۚ وَٱلۡفِتۡنَةُ أَشَدُّ مِنَ ٱلۡقَتۡلِۚ وَلَا تُقَٰتِلُوهُمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ حَتَّىٰ يُقَٰتِلُوكُمۡ فِيهِۖ فَإِن قَٰتَلُوكُمۡ فَٱقۡتُلُوهُمۡۗ كَذَٰلِكَ جَزَآءُ ٱلۡكَٰفِرِينَ\nУларни топган ерингизда қатл қилинг. Ва сизларни чиқарган жойдан уларни ҳам чиқаринг. Фитна қатл этишдан ҳам ёмонроқдир. Масжидул Ҳаром олдида сизга қарши урушмагунларича, сиз урушманг. Агар урушсалар, бас, сиз ҳам урушинг. Кофирларнинг жазоси шундай'}, {'chapter': 2, 'verse': 192, 'text': 'فَإِنِ ٱنتَهَوۡاْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nАгар тўхтасалар, албатта, Аллоҳ мағфиратли ва раҳимлидир'}, {'chapter': 2, 'verse': 193, 'text': 'وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ ٱلدِّينُ لِلَّهِۖ فَإِنِ ٱنتَهَوۡاْ فَلَا عُدۡوَٰنَ إِلَّا عَلَى ٱلظَّـٰلِمِينَ\nФитна бўлмаслиги ва дин Аллоҳга бўлиши учун улар билан жанг қилинг. Агар тўхтасалар, золимлардан бошқаларга душманлик йўқ'}, {'chapter': 2, 'verse': 194, 'text': 'ٱلشَّهۡرُ ٱلۡحَرَامُ بِٱلشَّهۡرِ ٱلۡحَرَامِ وَٱلۡحُرُمَٰتُ قِصَاصٞۚ فَمَنِ ٱعۡتَدَىٰ عَلَيۡكُمۡ فَٱعۡتَدُواْ عَلَيۡهِ بِمِثۡلِ مَا ٱعۡتَدَىٰ عَلَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ\nҲаром ойга ҳаром ой. Ҳаром қилинган нарсаларда қасос бор. Ким сизга душманлик қилса, сиз ҳам унга шунга ўхшаш душманлик қилинг. Ва Аллоҳга тақво қилинг ҳамда, билингки, албатта, Аллоҳ тақводорлар биландир'}, {'chapter': 2, 'verse': 195, 'text': 'وَأَنفِقُواْ فِي سَبِيلِ ٱللَّهِ وَلَا تُلۡقُواْ بِأَيۡدِيكُمۡ إِلَى ٱلتَّهۡلُكَةِ وَأَحۡسِنُوٓاْۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُحۡسِنِينَ\nВа Аллоҳнинг йўлида нафақа қилинг. Ўзингизни ҳалокатга дучор қилманг. Эҳсон қилинг, албатта, Аллоҳ эҳсон қилувчиларни хуш кўрадир'}, {'chapter': 2, 'verse': 196, 'text': 'وَأَتِمُّواْ ٱلۡحَجَّ وَٱلۡعُمۡرَةَ لِلَّهِۚ فَإِنۡ أُحۡصِرۡتُمۡ فَمَا ٱسۡتَيۡسَرَ مِنَ ٱلۡهَدۡيِۖ وَلَا تَحۡلِقُواْ رُءُوسَكُمۡ حَتَّىٰ يَبۡلُغَ ٱلۡهَدۡيُ مَحِلَّهُۥۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ بِهِۦٓ أَذٗى مِّن رَّأۡسِهِۦ فَفِدۡيَةٞ مِّن صِيَامٍ أَوۡ صَدَقَةٍ أَوۡ نُسُكٖۚ فَإِذَآ أَمِنتُمۡ فَمَن تَمَتَّعَ بِٱلۡعُمۡرَةِ إِلَى ٱلۡحَجِّ فَمَا ٱسۡتَيۡسَرَ مِنَ ٱلۡهَدۡيِۚ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖ فِي ٱلۡحَجِّ وَسَبۡعَةٍ إِذَا رَجَعۡتُمۡۗ تِلۡكَ عَشَرَةٞ كَامِلَةٞۗ ذَٰلِكَ لِمَن لَّمۡ يَكُنۡ أَهۡلُهُۥ حَاضِرِي ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nАллоҳ учун ҳаж ва умрани тугал адо этинг. Агар ушланиб қолсангиз, муяссар бўлганича қурбонлик қилинг. Қурбонлик ўз жойига етмагунча, сочингизни олдирманг. Сиздан ким бемор бўлса ёки бошида озор берувчи нарса бўлса, рўза тутиш ёки садақа бериш ёхуд қурбонлик сўйиш ила тўлов тўласин. Агар тинч бўлсангиз, ким умрадан ҳажгача ҳузур қилган бўлса, муяссар бўлган қурбонликни қилсин. Ким топмаса, уч кун ҳажда, етти кун қайтганингизда — ҳаммаси ўн кун тўлиқ рўза тутсин. Бу аҳли Масжидул Ҳаромда яшамайдиганлар учундир. Ва Аллоҳга тақво қилинг ҳамда, билингки, албатта, Аллоҳ иқоби шиддатли Зотдир'}, {'chapter': 2, 'verse': 197, 'text': 'ٱلۡحَجُّ أَشۡهُرٞ مَّعۡلُومَٰتٞۚ فَمَن فَرَضَ فِيهِنَّ ٱلۡحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي ٱلۡحَجِّۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ يَعۡلَمۡهُ ٱللَّهُۗ وَتَزَوَّدُواْ فَإِنَّ خَيۡرَ ٱلزَّادِ ٱلتَّقۡوَىٰۖ وَٱتَّقُونِ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ\nҲаж маълум ойлардир. Кимки уларда ўзига ҳажни фарз қилса, ҳажда шаҳвоний нарсалар, фисқ ва жанжал йўқдир. Нима яхшилик қилсангиз, Аллоҳ уни биладир. Озуқа олинг, энг яхши озуқа тақводир. Менга тақво қилинг, эй, ақл эгалари'}, {'chapter': 2, 'verse': 198, 'text': 'لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡۚ فَإِذَآ أَفَضۡتُم مِّنۡ عَرَفَٰتٖ فَٱذۡكُرُواْ ٱللَّهَ عِندَ ٱلۡمَشۡعَرِ ٱلۡحَرَامِۖ وَٱذۡكُرُوهُ كَمَا هَدَىٰكُمۡ وَإِن كُنتُم مِّن قَبۡلِهِۦ لَمِنَ ٱلضَّآلِّينَ\nСизга ўз Роббингиздан фазл исташингизда гуноҳ йўқдир. Арафотдан қайтиб тушганингизда, Аллоҳни Машъарул Ҳаромда зикр қилинг. Илгари адашганлардан бўлсангиз ҳам, сизни ҳидоятга бошлагани учун Уни зикр қилинг'}, {'chapter': 2, 'verse': 199, 'text': 'ثُمَّ أَفِيضُواْ مِنۡ حَيۡثُ أَفَاضَ ٱلنَّاسُ وَٱسۡتَغۡفِرُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nСўнгра, одамлар қайтиб тушган жойдан тушинглар. Ва Аллоҳга истиғфор айтинглар. Албатта, Аллоҳ мағфиратли, раҳимли Зотдир'}, {'chapter': 2, 'verse': 200, 'text': 'فَإِذَا قَضَيۡتُم مَّنَٰسِكَكُمۡ فَٱذۡكُرُواْ ٱللَّهَ كَذِكۡرِكُمۡ ءَابَآءَكُمۡ أَوۡ أَشَدَّ ذِكۡرٗاۗ فَمِنَ ٱلنَّاسِ مَن يَقُولُ رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِنۡ خَلَٰقٖ\nИбодатларингизни тугатганингизда, Аллоҳни оталарингизни эслагандек, балки ундан ҳам кучлироқ эсланг. Одамлардан баъзилари: «Роббимиз, бизга бу дунёда бергин», дейдир ва унга охиратда насиба йўқдир'}, {'chapter': 2, 'verse': 201, 'text': 'وَمِنۡهُم مَّن يَقُولُ رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ حَسَنَةٗ وَقِنَا عَذَابَ ٱلنَّارِ\nВа улардан баъзилари: «Роббимиз, бизга бу дунёда ҳам яхшиликни, охиратда ҳам яхшиликни бергин ва бизни дўзах олови азобидан сақлагин», дейдир'}, {'chapter': 2, 'verse': 202, 'text': 'أُوْلَـٰٓئِكَ لَهُمۡ نَصِيبٞ مِّمَّا كَسَبُواْۚ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ\nАна ўшаларга қилган касбларидан насиба бор. Ва Аллоҳ тез ҳисоб қилувчидир'}, {'chapter': 2, 'verse': 203, 'text': '۞وَٱذۡكُرُواْ ٱللَّهَ فِيٓ أَيَّامٖ مَّعۡدُودَٰتٖۚ فَمَن تَعَجَّلَ فِي يَوۡمَيۡنِ فَلَآ إِثۡمَ عَلَيۡهِ وَمَن تَأَخَّرَ فَلَآ إِثۡمَ عَلَيۡهِۖ لِمَنِ ٱتَّقَىٰۗ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّكُمۡ إِلَيۡهِ تُحۡشَرُونَ\nАллоҳни саноқли кунларда эсланг. Бас, ким икки кундан кейин шошилиб, кетса, унга гуноҳ йўқ. Ким кечикиб қолса, унга гуноҳ йўқ. Тақво қилувчилар учун. Аллоҳга тақво қилинглар ва билингларки, сиз албатта Унга тўпланурсиз'}, {'chapter': 2, 'verse': 204, 'text': 'وَمِنَ ٱلنَّاسِ مَن يُعۡجِبُكَ قَوۡلُهُۥ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيُشۡهِدُ ٱللَّهَ عَلَىٰ مَا فِي قَلۡبِهِۦ وَهُوَ أَلَدُّ ٱلۡخِصَامِ\nОдамлар ичида дунё ҳаётидаги гапи сени ажаблантирадиганлари бор. У қалбидаги нарсага Аллоҳни гувоҳ ҳам қилади. Ҳолбуки, у ашаддий хусуматчидир'}, {'chapter': 2, 'verse': 205, 'text': 'وَإِذَا تَوَلَّىٰ سَعَىٰ فِي ٱلۡأَرۡضِ لِيُفۡسِدَ فِيهَا وَيُهۡلِكَ ٱلۡحَرۡثَ وَٱلنَّسۡلَۚ وَٱللَّهُ لَا يُحِبُّ ٱلۡفَسَادَ\nВа бурилиб кетганда, ер юзида фасод учун ва экин ва наслни ҳалок қилиш учун ҳаракат этадир. Ва ҳолбуки, Аллоҳ фасодни хуш кўрмас'}, {'chapter': 2, 'verse': 206, 'text': 'وَإِذَا قِيلَ لَهُ ٱتَّقِ ٱللَّهَ أَخَذَتۡهُ ٱلۡعِزَّةُ بِٱلۡإِثۡمِۚ فَحَسۡبُهُۥ جَهَنَّمُۖ وَلَبِئۡسَ ٱلۡمِهَادُ\nАгар унга, Аллоҳдан қўрққин, дейилса, уни кибру ҳавоси гуноҳга етаклар. Жаҳаннам унга етарлидир. У қандай ҳам ёмон ўрин-а'}, {'chapter': 2, 'verse': 207, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَشۡرِي نَفۡسَهُ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِۚ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ\nВа одамлардан Аллоҳнинг розилигини тилаб жонини сотадиганлар бор. Ва Аллоҳ бандаларга меҳрибондир'}, {'chapter': 2, 'verse': 208, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱدۡخُلُواْ فِي ٱلسِّلۡمِ كَآفَّةٗ وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nЭй, иймон келтирганлар! Исломга тўлиғича киринг. Ва шайтоннинг изидан эргашманг. Албатта, у сизга очиқ душмандир'}, {'chapter': 2, 'verse': 209, 'text': 'فَإِن زَلَلۡتُم مِّنۢ بَعۡدِ مَا جَآءَتۡكُمُ ٱلۡبَيِّنَٰتُ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ\nАгар очиқ баёнотлар келгандан кейин ҳам тойилсангиз, билингки, албатта, Аллоҳ Азийз ва ҳикматлидир'}, {'chapter': 2, 'verse': 210, 'text': 'هَلۡ يَنظُرُونَ إِلَّآ أَن يَأۡتِيَهُمُ ٱللَّهُ فِي ظُلَلٖ مِّنَ ٱلۡغَمَامِ وَٱلۡمَلَـٰٓئِكَةُ وَقُضِيَ ٱلۡأَمۡرُۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nУлар Аллоҳ ва фаришталар сояли булутларда келиши ҳамда ишнинг тугашидан бошқага интизор бўлмаяптилар, шекилли?! Ва барча ишлар Аллоҳга қайтажак'}, {'chapter': 2, 'verse': 211, 'text': 'سَلۡ بَنِيٓ إِسۡرَـٰٓءِيلَ كَمۡ ءَاتَيۡنَٰهُم مِّنۡ ءَايَةِۭ بَيِّنَةٖۗ وَمَن يُبَدِّلۡ نِعۡمَةَ ٱللَّهِ مِنۢ بَعۡدِ مَا جَآءَتۡهُ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nБани Исроилдан уларга қанча оятларни берганимизни сўра. Кимки ўзига келган Аллоҳнинг неъматини ўзгартирса, бас, албатта, Аллоҳ иқоби шиддатли Зотдир'}, {'chapter': 2, 'verse': 212, 'text': 'زُيِّنَ لِلَّذِينَ كَفَرُواْ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَيَسۡخَرُونَ مِنَ ٱلَّذِينَ ءَامَنُواْۘ وَٱلَّذِينَ ٱتَّقَوۡاْ فَوۡقَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ\nКуфр келтирганларга бу дунё ҳаёти зийнатланди ва улар иймон келтирганларни масхара қилурлар. Ва ҳолбуки, қиёмат куни тақво қилганлар улардан устундир. Аллоҳ кимни хоҳласа, беҳисоб ризқ берур'}, {'chapter': 2, 'verse': 213, 'text': 'كَانَ ٱلنَّاسُ أُمَّةٗ وَٰحِدَةٗ فَبَعَثَ ٱللَّهُ ٱلنَّبِيِّـۧنَ مُبَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ ٱلۡكِتَٰبَ بِٱلۡحَقِّ لِيَحۡكُمَ بَيۡنَ ٱلنَّاسِ فِيمَا ٱخۡتَلَفُواْ فِيهِۚ وَمَا ٱخۡتَلَفَ فِيهِ إِلَّا ٱلَّذِينَ أُوتُوهُ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ بَغۡيَۢا بَيۡنَهُمۡۖ فَهَدَى ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ لِمَا ٱخۡتَلَفُواْ فِيهِ مِنَ ٱلۡحَقِّ بِإِذۡنِهِۦۗ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٍ\nОдамлар бир миллат эдилар. Бас, Аллоҳ хушхабар ва огоҳлантириш берувчи Набийларни юборди ва уларга одамлар ўртасида улар ихтилоф қилган нарсаларда ҳукм қилиш учун ҳақ китоб туширди. У тўғрисида ихтилофни очиқ ҳужжатлар келгандан сўнг, кимга берилган бўлса, ўшаларгина ҳаддан ошиб қилдилар. Иймон келтирганларни ҳақ тўғрисида ихтилоф қилган нарсаларида Аллоҳ Ўз изни ила ҳидоятга соладир. Аллоҳ кимни хоҳласа, тўғри йўлга бошлайдир'}, {'chapter': 2, 'verse': 214, 'text': 'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ ٱلۡجَنَّةَ وَلَمَّا يَأۡتِكُم مَّثَلُ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۖ مَّسَّتۡهُمُ ٱلۡبَأۡسَآءُ وَٱلضَّرَّآءُ وَزُلۡزِلُواْ حَتَّىٰ يَقُولَ ٱلرَّسُولُ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ مَتَىٰ نَصۡرُ ٱللَّهِۗ أَلَآ إِنَّ نَصۡرَ ٱللَّهِ قَرِيبٞ\nСиздан аввал ўтганларга келганга ўхшаш нарса сизларга келмасдан туриб, жаннатга кирамиз, деб ўйладингизми? Уларни бало ва мусийбатлар тутган ва ларзага туширилганлар. Ҳаттоки, Пайғамбар ва у билан иймон келтирганлар: «Аллоҳнинг нусрати қачон бўладир?» деганлар. Огоҳ бўлинглар, албатта, Аллоҳнинг нусрати яқиндир'}, {'chapter': 2, 'verse': 215, 'text': 'يَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلۡ مَآ أَنفَقۡتُم مِّنۡ خَيۡرٖ فَلِلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٞ\nСендан нимани нафақа қилишни сўрарлар. «Нафақа қилган яхшилигингиздан ота-оналарга, яқин кишиларга, етимларга, мискинларга ва ватангадоларга бўлсин. Қандай яхшилик қилган бўлсангиз, албатта, Аллоҳ уни билувчидир», деб айт'}, {'chapter': 2, 'verse': 216, 'text': 'كُتِبَ عَلَيۡكُمُ ٱلۡقِتَالُ وَهُوَ كُرۡهٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَهُوَ خَيۡرٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تُحِبُّواْ شَيۡـٔٗا وَهُوَ شَرّٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nСизга ёқмаса ҳам, жанг қилишингиз фарз қилинди. Шоядки, ёқтирмаган нарсангиз сиз учун яхши бўлса. Ва шоядки, ёқтирган нарсангиз сиз учун ёмон бўлса. Аллоҳ биладир, сиз билмассиз'}, {'chapter': 2, 'verse': 217, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلشَّهۡرِ ٱلۡحَرَامِ قِتَالٖ فِيهِۖ قُلۡ قِتَالٞ فِيهِ كَبِيرٞۚ وَصَدٌّ عَن سَبِيلِ ٱللَّهِ وَكُفۡرُۢ بِهِۦ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَإِخۡرَاجُ أَهۡلِهِۦ مِنۡهُ أَكۡبَرُ عِندَ ٱللَّهِۚ وَٱلۡفِتۡنَةُ أَكۡبَرُ مِنَ ٱلۡقَتۡلِۗ وَلَا يَزَالُونَ يُقَٰتِلُونَكُمۡ حَتَّىٰ يَرُدُّوكُمۡ عَن دِينِكُمۡ إِنِ ٱسۡتَطَٰعُواْۚ وَمَن يَرۡتَدِدۡ مِنكُمۡ عَن دِينِهِۦ فَيَمُتۡ وَهُوَ كَافِرٞ فَأُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nСендан ҳаром ойда уруш қилиш ҳақида сўрарлар. «Унда уруш қилиш катта гуноҳ. Аллоҳнинг йўлидан тўсиш, Унга куфр келтириш, Масжидул Ҳаромдан тўсиш, унинг аҳлини ундан чиқариш Аллоҳнинг ҳузурида энг катта гуноҳдир. Фитна ўлдиришдан каттароқдир», деб айт. Улар, агар қодир бўлсалар, сизларни динингиздан қайтаргунларича сизга қарши уруш қилаверадилар. Сиздан кимки диндан қайтиб, кофир ҳолида ўлиб кетса, ана ўшалар бу дунёю охиратда амали ҳабата бўлганлардир. Ана ўшалар дўзах эгаларидир. Улар унда абадий қолгувчилардир'}, {'chapter': 2, 'verse': 218, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ أُوْلَـٰٓئِكَ يَرۡجُونَ رَحۡمَتَ ٱللَّهِۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nАлбатта, иймон келтирганлар, ҳижрат қилганлар ва Аллоҳнинг йўлида жидду жаҳд қилганлар — ана ўшалар Аллоҳнинг раҳматини умид қиладилар. Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 2, 'verse': 219, 'text': '۞يَسۡـَٔلُونَكَ عَنِ ٱلۡخَمۡرِ وَٱلۡمَيۡسِرِۖ قُلۡ فِيهِمَآ إِثۡمٞ كَبِيرٞ وَمَنَٰفِعُ لِلنَّاسِ وَإِثۡمُهُمَآ أَكۡبَرُ مِن نَّفۡعِهِمَاۗ وَيَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلِ ٱلۡعَفۡوَۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ\nСендан хамр ва қимор ҳақида сўрарлар. Сен: «Иккисида катта гуноҳ ва кишилар учун манфаат бор ва гуноҳлари нафларидан каттадир», деб айт. Ва сендан нимани нафақа қилишни сўрарлар. Сен: «Ортиқчасини», деб айт. Аллоҳ шундоқ қилиб сизга Ўз оятларини баён қилади. Шоядки, тафаккур қилсангиз'}, {'chapter': 2, 'verse': 220, 'text': 'فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۗ وَيَسۡـَٔلُونَكَ عَنِ ٱلۡيَتَٰمَىٰۖ قُلۡ إِصۡلَاحٞ لَّهُمۡ خَيۡرٞۖ وَإِن تُخَالِطُوهُمۡ فَإِخۡوَٰنُكُمۡۚ وَٱللَّهُ يَعۡلَمُ ٱلۡمُفۡسِدَ مِنَ ٱلۡمُصۡلِحِۚ وَلَوۡ شَآءَ ٱللَّهُ لَأَعۡنَتَكُمۡۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nБу дунё ва охират ҳақида. Сендан етимлар ҳақида сўрарлар. Сен: «Уларга ислоҳ қилиш яхшидир», деб айт. Агар уларнинг (молларини) аралаштириб юборсангиз, бас, биродарларингиз. Аллоҳ бузғунчини ҳам, ислоҳчини ҳам билади. Агар хоҳласа, Аллоҳ сизларни қийинчиликка соларди. Албатта, Аллоҳ Азийз ва ҳикматли Зотдир'}, {'chapter': 2, 'verse': 221, 'text': 'وَلَا تَنكِحُواْ ٱلۡمُشۡرِكَٰتِ حَتَّىٰ يُؤۡمِنَّۚ وَلَأَمَةٞ مُّؤۡمِنَةٌ خَيۡرٞ مِّن مُّشۡرِكَةٖ وَلَوۡ أَعۡجَبَتۡكُمۡۗ وَلَا تُنكِحُواْ ٱلۡمُشۡرِكِينَ حَتَّىٰ يُؤۡمِنُواْۚ وَلَعَبۡدٞ مُّؤۡمِنٌ خَيۡرٞ مِّن مُّشۡرِكٖ وَلَوۡ أَعۡجَبَكُمۡۗ أُوْلَـٰٓئِكَ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَٱللَّهُ يَدۡعُوٓاْ إِلَى ٱلۡجَنَّةِ وَٱلۡمَغۡفِرَةِ بِإِذۡنِهِۦۖ وَيُبَيِّنُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nМушрика аёлларни иймон келтирмагунларича никоҳингизга олманг. Шубҳасиз, мўмина чўри мушрикадан, гар у сизга ёқса ҳам, яхшидир. Ва мушриклар иймонга келмагунларича, уларга никоҳлаб берманг. Шубҳасиз, мўмин қул мушрикдан, гар у сизга ёқса ҳам, яхшидир. Анавилар дўзах ўтига чақирарлар. Аллоҳ эса, Ўз изни ила жаннатга ва мағфиратга чақирар ҳамда одамларга Ўз оятларини баён қиладир. Шоядки, эсга олсалар'}, {'chapter': 2, 'verse': 222, 'text': 'وَيَسۡـَٔلُونَكَ عَنِ ٱلۡمَحِيضِۖ قُلۡ هُوَ أَذٗى فَٱعۡتَزِلُواْ ٱلنِّسَآءَ فِي ٱلۡمَحِيضِ وَلَا تَقۡرَبُوهُنَّ حَتَّىٰ يَطۡهُرۡنَۖ فَإِذَا تَطَهَّرۡنَ فَأۡتُوهُنَّ مِنۡ حَيۡثُ أَمَرَكُمُ ٱللَّهُۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلتَّوَّـٰبِينَ وَيُحِبُّ ٱلۡمُتَطَهِّرِينَ\nСендан ҳайз ҳақида сўрарлар. Сен: «У кўнгилсиз нарсадир. Ҳайз чоғида аёллардан четда бўлинг. Уларга пок бўлмагунларича яқинлашманглар. Агар пок бўлсалар, уларга Аллоҳ амр қилган жойдан келинг. Албатта, Аллоҳ тавба қилувчиларни севади ва покланувчиларни севадир», деб айт'}, {'chapter': 2, 'verse': 223, 'text': 'نِسَآؤُكُمۡ حَرۡثٞ لَّكُمۡ فَأۡتُواْ حَرۡثَكُمۡ أَنَّىٰ شِئۡتُمۡۖ وَقَدِّمُواْ لِأَنفُسِكُمۡۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّكُم مُّلَٰقُوهُۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nАёлларингиз экинзорларингиздир. Бас, экинзорингизга хоҳлаганингизча келинг. Ўзингиз учун (яхшилик) тақдим қилинг. Ва Аллоҳга тақво қилинг ҳамда, билингки, албатта, Унга рўбарў келувчисиз. Мўминларга башорат бер'}, {'chapter': 2, 'verse': 224, 'text': 'وَلَا تَجۡعَلُواْ ٱللَّهَ عُرۡضَةٗ لِّأَيۡمَٰنِكُمۡ أَن تَبَرُّواْ وَتَتَّقُواْ وَتُصۡلِحُواْ بَيۡنَ ٱلنَّاسِۚ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nАллоҳга ичган қасамларингизни яхшилик қилиш, тақво қилиш ва одамлар орасини ислоҳ этишингизга тўсиқ қилманг. Аллоҳ эшитувчи, билувчи Зотдир'}, {'chapter': 2, 'verse': 225, 'text': 'لَّا يُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا كَسَبَتۡ قُلُوبُكُمۡۗ وَٱللَّهُ غَفُورٌ حَلِيمٞ\nАллоҳ сизларнинг беҳуда қасамларингиз учун тутмас. Лекин қалбдан касб қилганингиз учун тутадир. Ва Аллоҳ мағфиратли, раҳимли Зотдир'}, {'chapter': 2, 'verse': 226, 'text': 'لِّلَّذِينَ يُؤۡلُونَ مِن نِّسَآئِهِمۡ تَرَبُّصُ أَرۡبَعَةِ أَشۡهُرٖۖ فَإِن فَآءُو فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nХотинларидан ийлаа қилганлар учун кутиш тўрт ойдир. Агар қайтсалар, бас, албатта, Аллоҳ мағфиратли, раҳимли Зотдир'}, {'chapter': 2, 'verse': 227, 'text': 'وَإِنۡ عَزَمُواْ ٱلطَّلَٰقَ فَإِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nВа агар талоқни қасд қилсалар, бас, албатта, Аллоҳ эшитувчи, билувчи Зотдир'}, {'chapter': 2, 'verse': 228, 'text': 'وَٱلۡمُطَلَّقَٰتُ يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ ثَلَٰثَةَ قُرُوٓءٖۚ وَلَا يَحِلُّ لَهُنَّ أَن يَكۡتُمۡنَ مَا خَلَقَ ٱللَّهُ فِيٓ أَرۡحَامِهِنَّ إِن كُنَّ يُؤۡمِنَّ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنۡ أَرَادُوٓاْ إِصۡلَٰحٗاۚ وَلَهُنَّ مِثۡلُ ٱلَّذِي عَلَيۡهِنَّ بِٱلۡمَعۡرُوفِۚ وَلِلرِّجَالِ عَلَيۡهِنَّ دَرَجَةٞۗ وَٱللَّهُ عَزِيزٌ حَكِيمٌ\nТалоқ қилинган аёллар уч қуръу кутарлар. Агар Аллоҳга ва қиёмат кунига иймон келтирган бўлсалар, улар учун Аллоҳ раҳмларида халқ қилган нарсани беркитишлари ҳалол бўлмас. Ўшанда эрлари уларни, агар ислоҳни ирода қилсалар, ўзларига қайтариб олишга ҳақлироқдирлар. Ва яхшилик ила уларнинг бурчлари муқобилида ҳақлари ҳам бордир. Ва эркакларга улардан устун даража бордир. Аллоҳ Азийз ва Ҳакиймдир'}, {'chapter': 2, 'verse': 229, 'text': 'ٱلطَّلَٰقُ مَرَّتَانِۖ فَإِمۡسَاكُۢ بِمَعۡرُوفٍ أَوۡ تَسۡرِيحُۢ بِإِحۡسَٰنٖۗ وَلَا يَحِلُّ لَكُمۡ أَن تَأۡخُذُواْ مِمَّآ ءَاتَيۡتُمُوهُنَّ شَيۡـًٔا إِلَّآ أَن يَخَافَآ أَلَّا يُقِيمَا حُدُودَ ٱللَّهِۖ فَإِنۡ خِفۡتُمۡ أَلَّا يُقِيمَا حُدُودَ ٱللَّهِ فَلَا جُنَاحَ عَلَيۡهِمَا فِيمَا ٱفۡتَدَتۡ بِهِۦۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَعۡتَدُوهَاۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nТалоқ икки мартадир. Сўнгра, яхшиликча ушлаб қолиш ёки яхшиликча қўйиб юбориш. Сизга уларга берган нарсангиздан бирор нарсани олиш ҳалол бўлмас, магар икковлари Аллоҳнинг чегараларида тура олмасликдан қўрқсалар (бўлур). Бас, агар икковларининг Аллоҳнинг чегараларида тура олмаслигидан қўрқсангиз, хотин берган эвазда икковларига гуноҳ йўқдир. Ушбулар Аллоҳнинг чегараларидир. Бас, улардан тажовуз қилманг. Ва ким Аллоҳнинг чегараларидан тажовуз қилса, бас, ўшалар, ана ўшалар золимлардир'}, {'chapter': 2, 'verse': 230, 'text': 'فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُۥ مِنۢ بَعۡدُ حَتَّىٰ تَنكِحَ زَوۡجًا غَيۡرَهُۥۗ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يَتَرَاجَعَآ إِن ظَنَّآ أَن يُقِيمَا حُدُودَ ٱللَّهِۗ وَتِلۡكَ حُدُودُ ٱللَّهِ يُبَيِّنُهَا لِقَوۡمٖ يَعۡلَمُونَ\nБас, агар яна талоқ қилса, бундан кейин у аёл, токи бошқа эрга тегиб чиқмагунча, унга ҳалол бўлмас. Агар у ҳам талоқ қилса, гар Аллоҳнинг чегараларида туришга ишонсалар, бир-бирларига қайтсалар, гуноҳ бўлмас. Ушбулар Аллоҳнинг билган қавмларга баён қилаётган чегараларидир'}, {'chapter': 2, 'verse': 231, 'text': 'وَإِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ سَرِّحُوهُنَّ بِمَعۡرُوفٖۚ وَلَا تُمۡسِكُوهُنَّ ضِرَارٗا لِّتَعۡتَدُواْۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ وَلَا تَتَّخِذُوٓاْ ءَايَٰتِ ٱللَّهِ هُزُوٗاۚ وَٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ وَمَآ أَنزَلَ عَلَيۡكُم مِّنَ ٱلۡكِتَٰبِ وَٱلۡحِكۡمَةِ يَعِظُكُم بِهِۦۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nХотинларни талоқ қилганингизда, муддатлари охирига етганда, уларни яхшилик билан ушлаб қолинг ёки яхшилик билан қўйворинг. Уларни тажовуз учун, зарар етказиш юзасидан ушлаб турманг. Ким ўшандоқ қилса, шубҳасиз, ўзига зулм қилади. Аллоҳнинг оятларини ҳазил билманг. Аллоҳнинг сизга берган неъматини ва ваъз этиб туширган китоби ва ҳикматини эсда тутинг. Аллоҳга тақво қилинг ва билингки, албатта, Аллоҳ ҳар бир нарсани билувчидир'}, {'chapter': 2, 'verse': 232, 'text': 'وَإِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَلَا تَعۡضُلُوهُنَّ أَن يَنكِحۡنَ أَزۡوَٰجَهُنَّ إِذَا تَرَٰضَوۡاْ بَيۡنَهُم بِٱلۡمَعۡرُوفِۗ ذَٰلِكَ يُوعَظُ بِهِۦ مَن كَانَ مِنكُمۡ يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۗ ذَٰلِكُمۡ أَزۡكَىٰ لَكُمۡ وَأَطۡهَرُۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nХотинларни талоқ қилганингизда, муддатлари охирига етганда, яхшилик билан ўзаро ризолашинг, эрларига қайта никоҳланишларини ман қилиб, қийинлаштирманг. Ушбу ила сизлардан Аллоҳга ва охират кунига иймон келтирганлар ваъзланади. Шундоқ қилишингиз сиз учун тозароқ ва покроқдир. Ва Аллоҳ биладир, сиз эса билмассиз'}, {'chapter': 2, 'verse': 233, 'text': '۞وَٱلۡوَٰلِدَٰتُ يُرۡضِعۡنَ أَوۡلَٰدَهُنَّ حَوۡلَيۡنِ كَامِلَيۡنِۖ لِمَنۡ أَرَادَ أَن يُتِمَّ ٱلرَّضَاعَةَۚ وَعَلَى ٱلۡمَوۡلُودِ لَهُۥ رِزۡقُهُنَّ وَكِسۡوَتُهُنَّ بِٱلۡمَعۡرُوفِۚ لَا تُكَلَّفُ نَفۡسٌ إِلَّا وُسۡعَهَاۚ لَا تُضَآرَّ وَٰلِدَةُۢ بِوَلَدِهَا وَلَا مَوۡلُودٞ لَّهُۥ بِوَلَدِهِۦۚ وَعَلَى ٱلۡوَارِثِ مِثۡلُ ذَٰلِكَۗ فَإِنۡ أَرَادَا فِصَالًا عَن تَرَاضٖ مِّنۡهُمَا وَتَشَاوُرٖ فَلَا جُنَاحَ عَلَيۡهِمَاۗ وَإِنۡ أَرَدتُّمۡ أَن تَسۡتَرۡضِعُوٓاْ أَوۡلَٰدَكُمۡ فَلَا جُنَاحَ عَلَيۡكُمۡ إِذَا سَلَّمۡتُم مَّآ ءَاتَيۡتُم بِٱلۡمَعۡرُوفِۗ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ\nКим эмизишни батамом қилишни ирода қилса, оналар фарзандларни тўлиқ икки йил эмизурлар. Уларни яхшилаб едириб, кийинтириш отанинг зиммасидадир. Ҳеч бир жонга имкондан ташқари таклиф бўлмас. Она ҳам, ота ҳам боласи сабабидан зарар тортмасин. Меросхўрга ҳам худди шундоқ. Агар ўзаро рози бўлиб, сутдан ажратишни ирода қилсалар, икковларига гуноҳ бўлмас. Агар фарзандларингизга эмизувчи талаб қилишни ирода қилсангиз, берадиганингизни яхшилик билан аввалдан топширсангиз, гуноҳ бўлмас. Ва Аллоҳга тақво қилинг ҳамда, билингки, Аллоҳ қилаётганингизни албатта кўриб турувчидир'}, {'chapter': 2, 'verse': 234, 'text': 'وَٱلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ أَرۡبَعَةَ أَشۡهُرٖ وَعَشۡرٗاۖ فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ بِٱلۡمَعۡرُوفِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nСизлардан вафот этиб жуфтларини қолдирганларнинг аёллари ўзларича тўрт ойу ўн кун кутарлар. Муддатлари етганда ўзлари ҳақида яхшилик билан қилган ишларида сизга гуноҳ йўқ. Аллоҳ қилаётган амалингиздан хабардордир'}, {'chapter': 2, 'verse': 235, 'text': 'وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا عَرَّضۡتُم بِهِۦ مِنۡ خِطۡبَةِ ٱلنِّسَآءِ أَوۡ أَكۡنَنتُمۡ فِيٓ أَنفُسِكُمۡۚ عَلِمَ ٱللَّهُ أَنَّكُمۡ سَتَذۡكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّآ أَن تَقُولُواْ قَوۡلٗا مَّعۡرُوفٗاۚ وَلَا تَعۡزِمُواْ عُقۡدَةَ ٱلنِّكَاحِ حَتَّىٰ يَبۡلُغَ ٱلۡكِتَٰبُ أَجَلَهُۥۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِيٓ أَنفُسِكُمۡ فَٱحۡذَرُوهُۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَفُورٌ حَلِيمٞ\nУ аёлларга ишора ила совчилик қилишингиз ёки уни ўз ичингизда тутишингизда гуноҳ йўқ. Аллоҳ сиз уларни албатта эслашингизни биладир. Лекин улар билан хуфёна ваъдалашманг. Магар яхши гап гапирсангиз, майли. Идда ўз муддатига етмагунча, никоҳ боғлашни қасд қилманг. Ва билингки, Аллоҳ ичингиздагини албатта билади. Бас, Ундан огоҳ бўлинг. Ва билингки, албатта, Аллоҳ мағфиратли ва Ҳалиймдир'}, {'chapter': 2, 'verse': 236, 'text': 'لَّا جُنَاحَ عَلَيۡكُمۡ إِن طَلَّقۡتُمُ ٱلنِّسَآءَ مَا لَمۡ تَمَسُّوهُنَّ أَوۡ تَفۡرِضُواْ لَهُنَّ فَرِيضَةٗۚ وَمَتِّعُوهُنَّ عَلَى ٱلۡمُوسِعِ قَدَرُهُۥ وَعَلَى ٱلۡمُقۡتِرِ قَدَرُهُۥ مَتَٰعَۢا بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُحۡسِنِينَ\nАгар хотинларга қўл теккизмай туриб ёки уларни маҳрни аниқламай туриб талоқ қилсангиз, сизга гуноҳ бўлмас. Уларни бой борича, йўқ ҳолича яхшилик ила фойдалантиринг. Бу яхшилик қилувчилар бурчидир'}, {'chapter': 2, 'verse': 237, 'text': 'وَإِن طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ وَقَدۡ فَرَضۡتُمۡ لَهُنَّ فَرِيضَةٗ فَنِصۡفُ مَا فَرَضۡتُمۡ إِلَّآ أَن يَعۡفُونَ أَوۡ يَعۡفُوَاْ ٱلَّذِي بِيَدِهِۦ عُقۡدَةُ ٱلنِّكَاحِۚ وَأَن تَعۡفُوٓاْ أَقۡرَبُ لِلتَّقۡوَىٰۚ وَلَا تَنسَوُاْ ٱلۡفَضۡلَ بَيۡنَكُمۡۚ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٌ\nАгар уларга қўл тегизмай талоқ қилсангиз ва маҳрни аниқлаб қўйган бўлсангиз, аниқланганнинг ярмини берасиз, магар аёл ёки никоҳ тугуни қўлида бўлган киши афв қилса, бермайсиз. Ва афв қилишингиз тақвога яқиндир. Орангиздаги фазилатни унутманг. Албатта, Аллоҳ қилаётган амалларингизни кўриб турувчи Зотдир'}, {'chapter': 2, 'verse': 238, 'text': 'حَٰفِظُواْ عَلَى ٱلصَّلَوَٰتِ وَٱلصَّلَوٰةِ ٱلۡوُسۡطَىٰ وَقُومُواْ لِلَّهِ قَٰنِتِينَ\nНамозларни ва ўрта намозни муҳофаза этинг. Ва Аллоҳ учун хушуъ ила қоим бўлинг'}, {'chapter': 2, 'verse': 239, 'text': 'فَإِنۡ خِفۡتُمۡ فَرِجَالًا أَوۡ رُكۡبَانٗاۖ فَإِذَآ أَمِنتُمۡ فَٱذۡكُرُواْ ٱللَّهَ كَمَا عَلَّمَكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ\nАгар хавфда қолсангиз, юрган ва минган ҳолингизда, хотиржам бўлганингизда, билмаган нарсангизни қандоқ ўргатган бўлса, Аллоҳни шундоқ зикр қилинг'}, {'chapter': 2, 'verse': 240, 'text': 'وَٱلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا وَصِيَّةٗ لِّأَزۡوَٰجِهِم مَّتَٰعًا إِلَى ٱلۡحَوۡلِ غَيۡرَ إِخۡرَاجٖۚ فَإِنۡ خَرَجۡنَ فَلَا جُنَاحَ عَلَيۡكُمۡ فِي مَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ مِن مَّعۡرُوفٖۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ\nСизлардан вафот этганда жуфтларини қолдираётганлар хотинлари чиқмасдан бир йилгача фойдаланишини васият қилсинлар. Агар улар чиқсалар, ўз хусусларида яхшилик билан қилган ишларида сизга гуноҳ йўқ. Аллоҳ Азийз ва Ҳакийм Зотдир'}, {'chapter': 2, 'verse': 241, 'text': 'وَلِلۡمُطَلَّقَٰتِ مَتَٰعُۢ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِينَ\nТалоқ қилинган аёлларга фойда бериш тақводорлар бурчидир'}, {'chapter': 2, 'verse': 242, 'text': 'كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\nАллоҳ шундай қилиб сизларга Ўз оятларини баён этадир. Шоядки, ақл ишлатсангизлар'}, {'chapter': 2, 'verse': 243, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ خَرَجُواْ مِن دِيَٰرِهِمۡ وَهُمۡ أُلُوفٌ حَذَرَ ٱلۡمَوۡتِ فَقَالَ لَهُمُ ٱللَّهُ مُوتُواْ ثُمَّ أَحۡيَٰهُمۡۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ\nДиёрларидан ўлимдан қочиб чиққан мингларча кишиларнинг (хабарини) билмадингми?! Бас, Аллоҳ уларга: ўлинг, деди. Сўнгра, уларни тирилтирди. Албатта, Аллоҳ одамлар устидан фазл эгасидир. Лекин кўп одамлар шукр қилмаслар'}, {'chapter': 2, 'verse': 244, 'text': 'وَقَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nАллоҳнинг йўлида жанг қилинг. Ва билингки, албатта, Аллоҳ эшитувчи ва билувчи Зотдир'}, {'chapter': 2, 'verse': 245, 'text': 'مَّن ذَا ٱلَّذِي يُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفَهُۥ لَهُۥٓ أَضۡعَافٗا كَثِيرَةٗۚ وَٱللَّهُ يَقۡبِضُ وَيَبۡصُۜطُ وَإِلَيۡهِ تُرۡجَعُونَ\nАллоҳга яхши қарз берадиган ким бор?! Бас, токи унга берганини кўп марта кўпайтириб берса. Ва ҳолбуки, Аллоҳ (ризқни) тор қилур ва кенг қилур. Ва Унгагина қайтасизлар'}, {'chapter': 2, 'verse': 246, 'text': 'أَلَمۡ تَرَ إِلَى ٱلۡمَلَإِ مِنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ مِنۢ بَعۡدِ مُوسَىٰٓ إِذۡ قَالُواْ لِنَبِيّٖ لَّهُمُ ٱبۡعَثۡ لَنَا مَلِكٗا نُّقَٰتِلۡ فِي سَبِيلِ ٱللَّهِۖ قَالَ هَلۡ عَسَيۡتُمۡ إِن كُتِبَ عَلَيۡكُمُ ٱلۡقِتَالُ أَلَّا تُقَٰتِلُواْۖ قَالُواْ وَمَا لَنَآ أَلَّا نُقَٰتِلَ فِي سَبِيلِ ٱللَّهِ وَقَدۡ أُخۡرِجۡنَا مِن دِيَٰرِنَا وَأَبۡنَآئِنَاۖ فَلَمَّا كُتِبَ عَلَيۡهِمُ ٱلۡقِتَالُ تَوَلَّوۡاْ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nМусодан кейинги Бани Исроилдан бўлган аъёнларни билмадингми?! Вақтики ўз Набийларига: «Бизга бир подшоҳ тайин қилгин, Аллоҳнинг йўлида уруш қилайлик», – дедилар. У: «Агар сизларга уруш фарз қилинса, урушмасаларингизчи?!» – деди. Улар: «Бизга нима бўлибдики, диёримиздан ва фарзандларимиздан жудо қилинсак ҳам, Аллоҳнинг йўлида урушмасак», – дедилар. Уруш фарз қилинганда эса, озгиналаридан бошқалари юз ўгириб кетдилар. Ва Аллоҳ зулм қилувчиларни билувчидир'}, {'chapter': 2, 'verse': 247, 'text': 'وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ٱللَّهَ قَدۡ بَعَثَ لَكُمۡ طَالُوتَ مَلِكٗاۚ قَالُوٓاْ أَنَّىٰ يَكُونُ لَهُ ٱلۡمُلۡكُ عَلَيۡنَا وَنَحۡنُ أَحَقُّ بِٱلۡمُلۡكِ مِنۡهُ وَلَمۡ يُؤۡتَ سَعَةٗ مِّنَ ٱلۡمَالِۚ قَالَ إِنَّ ٱللَّهَ ٱصۡطَفَىٰهُ عَلَيۡكُمۡ وَزَادَهُۥ بَسۡطَةٗ فِي ٱلۡعِلۡمِ وَٱلۡجِسۡمِۖ وَٱللَّهُ يُؤۡتِي مُلۡكَهُۥ مَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nНабийлари уларга: «Албатта, Аллоҳ Толутни сизга подшоҳ қилиб тайинлади», – деди. Улар: «У бизга қаёқдан подшоҳ бўлсин. Ундан кўра подшоҳликка биз ҳақлироқмиз. Қолаверса, унга кўпроқ мол ҳам берилмаган», – дедилар. У: «Албатта, уни Аллоҳнинг Ўзи сизларга танлади ва илмда, жисмда кенглигини зиёда қилди. Аллоҳ подшоҳлигини кимга хоҳласа, ўшанга беради. Ва Аллоҳ кенг қамровли ва билувчидир», – деди'}, {'chapter': 2, 'verse': 248, 'text': 'وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ءَايَةَ مُلۡكِهِۦٓ أَن يَأۡتِيَكُمُ ٱلتَّابُوتُ فِيهِ سَكِينَةٞ مِّن رَّبِّكُمۡ وَبَقِيَّةٞ مِّمَّا تَرَكَ ءَالُ مُوسَىٰ وَءَالُ هَٰرُونَ تَحۡمِلُهُ ٱلۡمَلَـٰٓئِكَةُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nНабийлари уларга: «Албатта, унинг подшоҳлигининг белгиси сизларга Роббингиздан сандиқ келмоғидир, унда хотиржамлик ва Мусо ҳамда Ҳорун аҳлидан қолган қолдиқ бўлиб, фаришталар кўтариб келур. Агар мўмин бўлсаларингиз, албатта, бу нарсада сизларга белги бор», – деди'}, {'chapter': 2, 'verse': 249, 'text': 'فَلَمَّا فَصَلَ طَالُوتُ بِٱلۡجُنُودِ قَالَ إِنَّ ٱللَّهَ مُبۡتَلِيكُم بِنَهَرٖ فَمَن شَرِبَ مِنۡهُ فَلَيۡسَ مِنِّي وَمَن لَّمۡ يَطۡعَمۡهُ فَإِنَّهُۥ مِنِّيٓ إِلَّا مَنِ ٱغۡتَرَفَ غُرۡفَةَۢ بِيَدِهِۦۚ فَشَرِبُواْ مِنۡهُ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ فَلَمَّا جَاوَزَهُۥ هُوَ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ قَالُواْ لَا طَاقَةَ لَنَا ٱلۡيَوۡمَ بِجَالُوتَ وَجُنُودِهِۦۚ قَالَ ٱلَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ ٱللَّهِ كَم مِّن فِئَةٖ قَلِيلَةٍ غَلَبَتۡ فِئَةٗ كَثِيرَةَۢ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِينَ\nТолут аскар билан чиққанда: «Албатта, Аллоҳ сизни дарё билан синовчидир, ким ундан ичса, у мендан эмас. Ва ким уни тановул қилмаса, у, албатта, мендандир. Магар биров қўли билан бир ҳўплам ҳўпласа, майли», деди. Озгиналаридан бошқалари ундан ичдилар. У ва у билан бирга иймон келтирганлар у(дарё)дан ўтганларида, улар: «Бугун бизда Жолут ва унинг аскари билан жанг қилишга тоқат йўқ», дедилар. Аллоҳга рўбарў бўлишга ишонганлар: «Қанчадан-қанча оз сонли гуруҳлар Аллоҳнинг изни билан кўп сонли гуруҳларга ғолиб келган. Аллоҳ сабрлилар биландир», – дедилар'}, {'chapter': 2, 'verse': 250, 'text': 'وَلَمَّا بَرَزُواْ لِجَالُوتَ وَجُنُودِهِۦ قَالُواْ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nЖолут ва унинг аскарларига рўбарў келганларида: «Роббимиз, бизга сабр тўккин, собитқадам қилгин ва кофир қавмларга қарши нусрат бергин», – дедилар'}, {'chapter': 2, 'verse': 251, 'text': 'فَهَزَمُوهُم بِإِذۡنِ ٱللَّهِ وَقَتَلَ دَاوُۥدُ جَالُوتَ وَءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ وَٱلۡحِكۡمَةَ وَعَلَّمَهُۥ مِمَّا يَشَآءُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّفَسَدَتِ ٱلۡأَرۡضُ وَلَٰكِنَّ ٱللَّهَ ذُو فَضۡلٍ عَلَى ٱلۡعَٰلَمِينَ\nБас, Аллоҳнинг изни билан уларни енгдилар ва Довуд Жолутни қатл этди. Сўнгра, Аллоҳ унга подшоҳлик ва ҳикматни берди ва хоҳлаган нарсасини ўргатди. Агар Аллоҳнинг баъзи (одам)ларни баъзиларига дафъ қилиши бўлмаса эди, ер юзи бузиларди. Лекин Аллоҳ оламлар устидан фазл эгасидир'}, {'chapter': 2, 'verse': 252, 'text': 'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۚ وَإِنَّكَ لَمِنَ ٱلۡمُرۡسَلِينَ\nБулар Аллоҳнинг оятларидир. Уларни сенга ҳақ ила тиловат қилурмиз. Ва, албатта, сен Пайғамбарлардандирсан'}, {'chapter': 2, 'verse': 253, 'text': '۞تِلۡكَ ٱلرُّسُلُ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۘ مِّنۡهُم مَّن كَلَّمَ ٱللَّهُۖ وَرَفَعَ بَعۡضَهُمۡ دَرَجَٰتٖۚ وَءَاتَيۡنَا عِيسَى ٱبۡنَ مَرۡيَمَ ٱلۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ ٱلۡقُدُسِۗ وَلَوۡ شَآءَ ٱللَّهُ مَا ٱقۡتَتَلَ ٱلَّذِينَ مِنۢ بَعۡدِهِم مِّنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ وَلَٰكِنِ ٱخۡتَلَفُواْ فَمِنۡهُم مَّنۡ ءَامَنَ وَمِنۡهُم مَّن كَفَرَۚ وَلَوۡ شَآءَ ٱللَّهُ مَا ٱقۡتَتَلُواْ وَلَٰكِنَّ ٱللَّهَ يَفۡعَلُ مَا يُرِيدُ\nБулар Пайғамбарлар. Уларнинг баъзиларини баъзиларидан фазллик қилдик. Улардан Аллоҳ гаплашгани бор ва баъзиларининг даражасини кўтарган. Ва Ийсо ибн Марямга ойдин(мўъжиза)ларни бердик ва уни муқаддас руҳ билан қўлладик. Агар Аллоҳ хоҳлаганида, улардан кейин қолганлар ўзларига очиқ-ойдин баёнотлар келганидан сўнг урушмас эдилар. Лекин ихтилоф қилдилар. Улардан кимдир иймон келтирди, кимдир куфр келтирди. Агар Аллоҳ хоҳлаганида, урушмас эдилар. Лекин Аллоҳ нимани ирода қилса, шуни қилади'}, {'chapter': 2, 'verse': 254, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِمَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعٞ فِيهِ وَلَا خُلَّةٞ وَلَا شَفَٰعَةٞۗ وَٱلۡكَٰفِرُونَ هُمُ ٱلظَّـٰلِمُونَ\nЭй, иймон келтирганлар! Сизга ризқ қилиб берган нарсаларимиздан на олди-сотди, на ошна-оғайничилик ва на шафоатчилик йўқ Кун келмасдан олдин нафақа қилинг. Ва кофирлар — ўшалар золимлардир'}, {'chapter': 2, 'verse': 255, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡحَيُّ ٱلۡقَيُّومُۚ لَا تَأۡخُذُهُۥ سِنَةٞ وَلَا نَوۡمٞۚ لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ مَن ذَا ٱلَّذِي يَشۡفَعُ عِندَهُۥٓ إِلَّا بِإِذۡنِهِۦۚ يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۖ وَلَا يُحِيطُونَ بِشَيۡءٖ مِّنۡ عِلۡمِهِۦٓ إِلَّا بِمَا شَآءَۚ وَسِعَ كُرۡسِيُّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَۖ وَلَا يَـُٔودُهُۥ حِفۡظُهُمَاۚ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ\nАллоҳ, Ундан ўзга илоҳ йўқ. У тирикдир ва қаййумдир. Уни мудроқ ҳам, уйқу ҳам олмас. Осмонлару ердаги нарсалар Уникидир. Унинг ҳузурида Ўзининг изнисиз ҳеч ким шафоат қила олмас. У уларнинг олдиларидаги нарсани ҳам, ортларидаги нарсани ҳам билур. Унинг илмидан ҳеч нарсани иҳота қила олмаслар, магар Ўзи хоҳлаганини, холос. Унинг курсиси осмонлару ерни қамраган. У уларни муҳофаза қилишдан чарчамас. Ва У Алий ва Азийм Зотдир'}, {'chapter': 2, 'verse': 256, 'text': 'لَآ إِكۡرَاهَ فِي ٱلدِّينِۖ قَد تَّبَيَّنَ ٱلرُّشۡدُ مِنَ ٱلۡغَيِّۚ فَمَن يَكۡفُرۡ بِٱلطَّـٰغُوتِ وَيُؤۡمِنۢ بِٱللَّهِ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰ لَا ٱنفِصَامَ لَهَاۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\nДинга мажбур қилиш йўқ. Батаҳқиқ, ҳақ ботилдан ажради. Ким тоғутга куфр келтириб, Аллоҳга иймон келтирса, батаҳқиқ, узилмайдиган мустаҳкам тутқични ушлаган бўлади. Ва Аллоҳ эшитувчи, билувчи Зотдир'}, {'chapter': 2, 'verse': 257, 'text': 'ٱللَّهُ وَلِيُّ ٱلَّذِينَ ءَامَنُواْ يُخۡرِجُهُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۖ وَٱلَّذِينَ كَفَرُوٓاْ أَوۡلِيَآؤُهُمُ ٱلطَّـٰغُوتُ يُخۡرِجُونَهُم مِّنَ ٱلنُّورِ إِلَى ٱلظُّلُمَٰتِۗ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nАллоҳ иймон келтирганларнинг дўстидир. Уларни зулматлардан нурга чиқарур. Куфр келтирганларнинг дўстлари тоғутлардир. Уларни нурдан зулматларга чиқарурлар. Ана ўшалар дўзах эгаларидир. Улар унда абадийдирлар'}, {'chapter': 2, 'verse': 258, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِي حَآجَّ إِبۡرَٰهِـۧمَ فِي رَبِّهِۦٓ أَنۡ ءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ إِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّيَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ قَالَ أَنَا۠ أُحۡيِۦ وَأُمِيتُۖ قَالَ إِبۡرَٰهِـۧمُ فَإِنَّ ٱللَّهَ يَأۡتِي بِٱلشَّمۡسِ مِنَ ٱلۡمَشۡرِقِ فَأۡتِ بِهَا مِنَ ٱلۡمَغۡرِبِ فَبُهِتَ ٱلَّذِي كَفَرَۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nАллоҳ подшоҳлик бериб қўйгани сабабидан Иброҳим билан Роббиси ҳақида ҳужжат талашганни билмадингми? Иброҳим: «Менинг Роббим тирилтиради ва ўлдиради», деганда, у: «Мен тирилтираман ва ўлдираман», – деди. Иброҳим: «Албатта, Аллоҳ қуёшни Машриқдан чиқарур, бас, сен уни Мағрибдан чиқаргинг-чи», – деди. Бас, куфр келтирган лол қолди. Ва, Аллоҳ золим қавмларни ҳидоятга солмас'}, {'chapter': 2, 'verse': 259, 'text': 'أَوۡ كَٱلَّذِي مَرَّ عَلَىٰ قَرۡيَةٖ وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ يُحۡيِۦ هَٰذِهِ ٱللَّهُ بَعۡدَ مَوۡتِهَاۖ فَأَمَاتَهُ ٱللَّهُ مِاْئَةَ عَامٖ ثُمَّ بَعَثَهُۥۖ قَالَ كَمۡ لَبِثۡتَۖ قَالَ لَبِثۡتُ يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۖ قَالَ بَل لَّبِثۡتَ مِاْئَةَ عَامٖ فَٱنظُرۡ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمۡ يَتَسَنَّهۡۖ وَٱنظُرۡ إِلَىٰ حِمَارِكَ وَلِنَجۡعَلَكَ ءَايَةٗ لِّلنَّاسِۖ وَٱنظُرۡ إِلَى ٱلۡعِظَامِ كَيۡفَ نُنشِزُهَا ثُمَّ نَكۡسُوهَا لَحۡمٗاۚ فَلَمَّا تَبَيَّنَ لَهُۥ قَالَ أَعۡلَمُ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЁки шифтлари устига қулаган қишлоқдан ўтган кишига ўхшашни кўрмадингми? У: «Аллоҳ буни ўлимдан кейин қандай тирилтирур?» – деди. Бас, Аллоҳ уни юз йил ўлдирди, сўнгра, қайта тирилтирди. «Қанча ётдинг?» – деди. У: «Бир кун ёки бир куннинг баъзисича ётдим», – деди. «Балки, юз йил ётдинг, таомингга ва шаробингга назар сол, ўзгаргани йўқ ва эшагингга ҳам қара. Сени одамларга ибрат қилиш учун шундай қилдик. Ва суякларга назар сол, уларни қандоқ ҳаракатга соламиз ва уларга қандоқ гўшт кийгизамиз», – деди. Унга равшан бўлганда: «Албатта, Аллоҳ ҳар бир нарсага қодирлигини билдим», – деди'}, {'chapter': 2, 'verse': 260, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ أَرِنِي كَيۡفَ تُحۡيِ ٱلۡمَوۡتَىٰۖ قَالَ أَوَلَمۡ تُؤۡمِنۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطۡمَئِنَّ قَلۡبِيۖ قَالَ فَخُذۡ أَرۡبَعَةٗ مِّنَ ٱلطَّيۡرِ فَصُرۡهُنَّ إِلَيۡكَ ثُمَّ ٱجۡعَلۡ عَلَىٰ كُلِّ جَبَلٖ مِّنۡهُنَّ جُزۡءٗا ثُمَّ ٱدۡعُهُنَّ يَأۡتِينَكَ سَعۡيٗاۚ وَٱعۡلَمۡ أَنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nЭсла, Иброҳим: «Эй Роббим, менга ўликларни қандай тирилтиришингни кўрсат», деганда, У Зот: «Ишонмадингми?» – деди. У: Оре, лекин қалбим хотиржам бўлиши учун», – деди. У зот: «Қушдан тўртта олгин-да, ўзингга тортиб, кесиб майдала, сўнгра, улардан ҳар бир тоққа бўлакларини қўйгин, кейин уларни ўзингга чақир, ҳузурингга тезлаб келадилар», – деди. «Ва билгинки, албатта, Аллоҳ Азийз ва Ҳакийм Зотдир»'}, {'chapter': 2, 'verse': 261, 'text': 'مَّثَلُ ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ ٱللَّهِ كَمَثَلِ حَبَّةٍ أَنۢبَتَتۡ سَبۡعَ سَنَابِلَ فِي كُلِّ سُنۢبُلَةٖ مِّاْئَةُ حَبَّةٖۗ وَٱللَّهُ يُضَٰعِفُ لِمَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٌ\nМолларини Аллоҳнинг йўлида сарфлайдиганларнинг мисоли худди бир дона донга ўхшайдир. Ундан етти бошоқ ўсиб чиқадир, ҳар бошоқда юзтадан дон бор. Ва Аллоҳ кимга хоҳласа, яна кўпайтириб берадир. Ва Аллоҳ қамраб олувчи ва билувчи Зотдир'}, {'chapter': 2, 'verse': 262, 'text': 'ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ ٱللَّهِ ثُمَّ لَا يُتۡبِعُونَ مَآ أَنفَقُواْ مَنّٗا وَلَآ أَذٗى لَّهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАллоҳнинг йўлида молларини сарфлаб, сўнгра сарфлари кетидан миннат қилмай ва озор бермайдиганларнинг ажрлари Роббилари ҳузуридадир. Уларга хавф йўқ ва улар хафа ҳам бўлмаслар'}, {'chapter': 2, 'verse': 263, 'text': '۞قَوۡلٞ مَّعۡرُوفٞ وَمَغۡفِرَةٌ خَيۡرٞ مِّن صَدَقَةٖ يَتۡبَعُهَآ أَذٗىۗ وَٱللَّهُ غَنِيٌّ حَلِيمٞ\nЯхши сўз ва кечириш ортидан озор келадиган садақадан кўра яхшироқдир. Ва Аллоҳ Ғаний ва Ҳалийм Зотдир'}, {'chapter': 2, 'verse': 264, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُبۡطِلُواْ صَدَقَٰتِكُم بِٱلۡمَنِّ وَٱلۡأَذَىٰ كَٱلَّذِي يُنفِقُ مَالَهُۥ رِئَآءَ ٱلنَّاسِ وَلَا يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ فَمَثَلُهُۥ كَمَثَلِ صَفۡوَانٍ عَلَيۡهِ تُرَابٞ فَأَصَابَهُۥ وَابِلٞ فَتَرَكَهُۥ صَلۡدٗاۖ لَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّمَّا كَسَبُواْۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nЭй, иймон келтирганлар! Садақаларингизни миннат ва озор бериш билан, Аллоҳга ва охират кунига иймон келтирмаса ҳам, кишиларга риё учун молини нафақа қилганга ўхшаб, бекорга кетказманг. У мисоли устини тупроқ босган силлиқ бир тошга ўхшайдир. Бас, кучли ёмғир ёғса, силлиқ бўлиб қолади. Касб қилганларидан ҳеч нарсага қодир бўлмаслар. Ва Аллоҳ кофир қавмларни ҳидоятга бошламас'}, {'chapter': 2, 'verse': 265, 'text': 'وَمَثَلُ ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمُ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِ وَتَثۡبِيتٗا مِّنۡ أَنفُسِهِمۡ كَمَثَلِ جَنَّةِۭ بِرَبۡوَةٍ أَصَابَهَا وَابِلٞ فَـَٔاتَتۡ أُكُلَهَا ضِعۡفَيۡنِ فَإِن لَّمۡ يُصِبۡهَا وَابِلٞ فَطَلّٞۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ\nМолларини Аллоҳнинг розилигини истаб, ўзларидаги ишонч билан сарфлайдиганлар худди тепаликдаги боққа ўхшайдир. Унга қаттиқ ёмғир ёғса, мевасини икки баробар беради. Агар қаттиқ ёмғир ёғмаса, майдалаб ёққани ҳам бўлаверади. Ва Аллоҳ қилаётганларингизни кўриб турувчи Зотдир'}, {'chapter': 2, 'verse': 266, 'text': 'أَيَوَدُّ أَحَدُكُمۡ أَن تَكُونَ لَهُۥ جَنَّةٞ مِّن نَّخِيلٖ وَأَعۡنَابٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ لَهُۥ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَأَصَابَهُ ٱلۡكِبَرُ وَلَهُۥ ذُرِّيَّةٞ ضُعَفَآءُ فَأَصَابَهَآ إِعۡصَارٞ فِيهِ نَارٞ فَٱحۡتَرَقَتۡۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ\nСизлардан бирортангиз остидан анҳорлар оқиб турган хурмозор ва узумзор, турли мевали боғи бўлатуриб, ёши бир жойга етганда, заифҳол зурриёти бор пайтда унга ўтли бўрон келиб, ёниб битишини хоҳлайдими?! Аллоҳ сизларга шу тариқа Ўз оятларини баён қилур, шоядки, фикр юритсангиз'}, {'chapter': 2, 'verse': 267, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِن طَيِّبَٰتِ مَا كَسَبۡتُمۡ وَمِمَّآ أَخۡرَجۡنَا لَكُم مِّنَ ٱلۡأَرۡضِۖ وَلَا تَيَمَّمُواْ ٱلۡخَبِيثَ مِنۡهُ تُنفِقُونَ وَلَسۡتُم بِـَٔاخِذِيهِ إِلَّآ أَن تُغۡمِضُواْ فِيهِۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَنِيٌّ حَمِيدٌ\nЭй, иймон келтирганлар! Касб қилган пок нарсаларингиздан ва Биз сизларга ердан чиқариб берган нарсаларимиздан садақа қилинглар. Нопокни қасд этиб садақа қилманг. Ҳолбуки, уни ўзингиз ҳам олувчимассиз, магар кўз юмсангизгина оласиз. Ва билингки, албатта, Аллоҳ бой ва мақталган Зотдир'}, {'chapter': 2, 'verse': 268, 'text': 'ٱلشَّيۡطَٰنُ يَعِدُكُمُ ٱلۡفَقۡرَ وَيَأۡمُرُكُم بِٱلۡفَحۡشَآءِۖ وَٱللَّهُ يَعِدُكُم مَّغۡفِرَةٗ مِّنۡهُ وَفَضۡلٗاۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nШайтон сизга фақирликни ваъда қиладир ва фаҳш ишларга буюрадир. Аллоҳ сизга Ўзидан мағфират ва фазлни ваъда қиладир. Ва Аллоҳ кенг қамровли, билувчи Зотдир'}, {'chapter': 2, 'verse': 269, 'text': 'يُؤۡتِي ٱلۡحِكۡمَةَ مَن يَشَآءُۚ وَمَن يُؤۡتَ ٱلۡحِكۡمَةَ فَقَدۡ أُوتِيَ خَيۡرٗا كَثِيرٗاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nУ кимни хоҳласа, ўшанга ҳикматни берар. Кимга ҳикмат берилса, унга кўп яхшилик берилган бўлар. Фақат ақл эгаларигина эсларлар'}, {'chapter': 2, 'verse': 270, 'text': 'وَمَآ أَنفَقۡتُم مِّن نَّفَقَةٍ أَوۡ نَذَرۡتُم مِّن نَّذۡرٖ فَإِنَّ ٱللَّهَ يَعۡلَمُهُۥۗ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٍ\nҚилган нафақангизни ёки назрингизни Аллоҳ албатта биладир. Золимларга ёрдамчилар йўқдир'}, {'chapter': 2, 'verse': 271, 'text': 'إِن تُبۡدُواْ ٱلصَّدَقَٰتِ فَنِعِمَّا هِيَۖ وَإِن تُخۡفُوهَا وَتُؤۡتُوهَا ٱلۡفُقَرَآءَ فَهُوَ خَيۡرٞ لَّكُمۡۚ وَيُكَفِّرُ عَنكُم مِّن سَيِّـَٔاتِكُمۡۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nСадақани ошкора қилсангиз, қандоқ ҳам яхши. Агар махфий қилсангиз ва фақирларга берсангиз, бу сиз учун яхшидир. Сиздан гуноҳларингизни ювадир. Ва Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 2, 'verse': 272, 'text': '۞لَّيۡسَ عَلَيۡكَ هُدَىٰهُمۡ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَلِأَنفُسِكُمۡۚ وَمَا تُنفِقُونَ إِلَّا ٱبۡتِغَآءَ وَجۡهِ ٱللَّهِۚ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ\nУларни ҳидоятга солиш сенинг зиммангда эмас. Лекин Аллоҳ кимни хоҳласа, ҳидоятга бошлар. Нафақа қилган яхшиликларингиз ўз фойдангизга. Қилган нафақангиз фақат Аллоҳнинг юзини исташ учундир. Нафақа қилган яхшиликларингиз ўзингизга тўлалигича қайтарилур. Сизларга зулм қилинмас'}, {'chapter': 2, 'verse': 273, 'text': 'لِلۡفُقَرَآءِ ٱلَّذِينَ أُحۡصِرُواْ فِي سَبِيلِ ٱللَّهِ لَا يَسۡتَطِيعُونَ ضَرۡبٗا فِي ٱلۡأَرۡضِ يَحۡسَبُهُمُ ٱلۡجَاهِلُ أَغۡنِيَآءَ مِنَ ٱلتَّعَفُّفِ تَعۡرِفُهُم بِسِيمَٰهُمۡ لَا يَسۡـَٔلُونَ ٱلنَّاسَ إِلۡحَافٗاۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٌ\n(Садақа ва хайру эҳсонлар) Аллоҳнинг йўлида тутилган, ер юзида касб қилишга қодир бўлмаган, иффатлари туфайли билмаган киши уларни бой деб ўйлайдиган фақирларгадир. Уларни сиймоларидан танийсан, одамлардан хиралик қилиб сўрамаслар. Нафақа қилган яхшиликларингизни Аллоҳ албатта билгувчидир'}, {'chapter': 2, 'verse': 274, 'text': 'ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُم بِٱلَّيۡلِ وَٱلنَّهَارِ سِرّٗا وَعَلَانِيَةٗ فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nКечасию кундузи молларини яширин ва ошкора нафақа қиладиганларнинг ажрлари Роббилари ҳузуридадир. Уларга хавф йўқ ва улар хафа ҳам бўлмаслар'}, {'chapter': 2, 'verse': 275, 'text': 'ٱلَّذِينَ يَأۡكُلُونَ ٱلرِّبَوٰاْ لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ ٱلَّذِي يَتَخَبَّطُهُ ٱلشَّيۡطَٰنُ مِنَ ٱلۡمَسِّۚ ذَٰلِكَ بِأَنَّهُمۡ قَالُوٓاْ إِنَّمَا ٱلۡبَيۡعُ مِثۡلُ ٱلرِّبَوٰاْۗ وَأَحَلَّ ٱللَّهُ ٱلۡبَيۡعَ وَحَرَّمَ ٱلرِّبَوٰاْۚ فَمَن جَآءَهُۥ مَوۡعِظَةٞ مِّن رَّبِّهِۦ فَٱنتَهَىٰ فَلَهُۥ مَا سَلَفَ وَأَمۡرُهُۥٓ إِلَى ٱللَّهِۖ وَمَنۡ عَادَ فَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nРибони ейдиганлар (қабрларидан) фақат шайтон уриб, жинни бўлган кишидек довдираб турарлар. Бундай бўлиши уларнинг, тижорат ҳам рибога ўхшаш-да, деганлари учундир. Ва ҳолбуки, Аллоҳ тижоратни ҳалол, рибони ҳаром қилган. Кимки Роббидан мавъиза келганда тўхтаса, аввал ўтгани ўзига ва унинг иши Аллоҳнинг Ўзига ҳавола. Кимки, яна (рибога) қайтса, ана ўшалар дўзах эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 2, 'verse': 276, 'text': 'يَمۡحَقُ ٱللَّهُ ٱلرِّبَوٰاْ وَيُرۡبِي ٱلصَّدَقَٰتِۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ\nАллоҳ рибони доимо нуқсонга учратур ва садақаларни зиёда қилур. Ва Аллоҳ ҳар бир кофир, гуноҳкорни хуш кўрмас'}, {'chapter': 2, 'verse': 277, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта, иймон келтирган, яхши амалларни қилган, намозни қоим қилиб, закот берганларнинг ажрлари Роббилари ҳузуридадир. Уларга хавф йўқ ва улар хафа ҳам бўлмаслар'}, {'chapter': 2, 'verse': 278, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَذَرُواْ مَا بَقِيَ مِنَ ٱلرِّبَوٰٓاْ إِن كُنتُم مُّؤۡمِنِينَ\nЭй, иймон келтирганлар! Агар мўмин бўлсангиз, Аллоҳга тақво қилинг ва рибонинг сарқитини ҳам тарк қилинг'}, {'chapter': 2, 'verse': 279, 'text': 'فَإِن لَّمۡ تَفۡعَلُواْ فَأۡذَنُواْ بِحَرۡبٖ مِّنَ ٱللَّهِ وَرَسُولِهِۦۖ وَإِن تُبۡتُمۡ فَلَكُمۡ رُءُوسُ أَمۡوَٰلِكُمۡ لَا تَظۡلِمُونَ وَلَا تُظۡلَمُونَ\nАгар ундоқ қилмасангиз, Аллоҳ ва Унинг Расули томонидан бўладиган урушга ишонаверинг. Агар тавба қилсангиз, сармоянгиз ўзингизга, зулм қилмассиз ва сизга ҳам зулм қилинмас'}, {'chapter': 2, 'verse': 280, 'text': 'وَإِن كَانَ ذُو عُسۡرَةٖ فَنَظِرَةٌ إِلَىٰ مَيۡسَرَةٖۚ وَأَن تَصَدَّقُواْ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nАгар ночор бўлса, бойигунча интизор қилинг. Садақа қилмоғингиз, гар билсангиз, ўзингиз учун яхшидир'}, {'chapter': 2, 'verse': 281, 'text': 'وَٱتَّقُواْ يَوۡمٗا تُرۡجَعُونَ فِيهِ إِلَى ٱللَّهِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nАллоҳга қайтариладиган кунингиздан қўрқинг. Сўнгра, ҳар бир жон қилганига яраша тўлиқ оладур. Уларга зулм қилинмас'}, {'chapter': 2, 'verse': 282, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا تَدَايَنتُم بِدَيۡنٍ إِلَىٰٓ أَجَلٖ مُّسَمّٗى فَٱكۡتُبُوهُۚ وَلۡيَكۡتُب بَّيۡنَكُمۡ كَاتِبُۢ بِٱلۡعَدۡلِۚ وَلَا يَأۡبَ كَاتِبٌ أَن يَكۡتُبَ كَمَا عَلَّمَهُ ٱللَّهُۚ فَلۡيَكۡتُبۡ وَلۡيُمۡلِلِ ٱلَّذِي عَلَيۡهِ ٱلۡحَقُّ وَلۡيَتَّقِ ٱللَّهَ رَبَّهُۥ وَلَا يَبۡخَسۡ مِنۡهُ شَيۡـٔٗاۚ فَإِن كَانَ ٱلَّذِي عَلَيۡهِ ٱلۡحَقُّ سَفِيهًا أَوۡ ضَعِيفًا أَوۡ لَا يَسۡتَطِيعُ أَن يُمِلَّ هُوَ فَلۡيُمۡلِلۡ وَلِيُّهُۥ بِٱلۡعَدۡلِۚ وَٱسۡتَشۡهِدُواْ شَهِيدَيۡنِ مِن رِّجَالِكُمۡۖ فَإِن لَّمۡ يَكُونَا رَجُلَيۡنِ فَرَجُلٞ وَٱمۡرَأَتَانِ مِمَّن تَرۡضَوۡنَ مِنَ ٱلشُّهَدَآءِ أَن تَضِلَّ إِحۡدَىٰهُمَا فَتُذَكِّرَ إِحۡدَىٰهُمَا ٱلۡأُخۡرَىٰۚ وَلَا يَأۡبَ ٱلشُّهَدَآءُ إِذَا مَا دُعُواْۚ وَلَا تَسۡـَٔمُوٓاْ أَن تَكۡتُبُوهُ صَغِيرًا أَوۡ كَبِيرًا إِلَىٰٓ أَجَلِهِۦۚ ذَٰلِكُمۡ أَقۡسَطُ عِندَ ٱللَّهِ وَأَقۡوَمُ لِلشَّهَٰدَةِ وَأَدۡنَىٰٓ أَلَّا تَرۡتَابُوٓاْ إِلَّآ أَن تَكُونَ تِجَٰرَةً حَاضِرَةٗ تُدِيرُونَهَا بَيۡنَكُمۡ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَلَّا تَكۡتُبُوهَاۗ وَأَشۡهِدُوٓاْ إِذَا تَبَايَعۡتُمۡۚ وَلَا يُضَآرَّ كَاتِبٞ وَلَا شَهِيدٞۚ وَإِن تَفۡعَلُواْ فَإِنَّهُۥ فُسُوقُۢ بِكُمۡۗ وَٱتَّقُواْ ٱللَّهَۖ وَيُعَلِّمُكُمُ ٱللَّهُۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\nЭй, иймон келтирганлар! Маълум муддатга қарз олди-берди қилган вақтингизда, уни ёзиб қўйинг. Орангизда бир ёзувчи адолат билан ёзсин. Ҳеч бир ёзувчи Аллоҳ билдирганидек қилиб ёзишдан бош тортмасин. Бас, ёзсин ва ҳақ зиммасига тушган киши айтиб турсин, Роббига тақво қилсин ва ундан ҳеч нарсани камайтирмасин. Агар ҳақ зиммасига тушган киши эси паст, заиф ёки айтиб тура олмайдиган бўлса, унинг валийи адолат билан айтиб турсин. Эркакларингиздан икки кишини гувоҳ қилинг. Агар икки эр киши бўлмаса, ўзингиз рози бўладиган гувоҳлардан бир эр киши ва икки аёл киши бўлсин. Иккови(аёл)дан бирорталари адашса, бирлари бошқаларининг эсига солади. Ва чақирилган вақтда гувоҳлар бош тортмасинлар. Кичик бўлса ҳам, катта бўлса ҳам, муддатигача ёзишингизни малол олманг. Шундоқ қилмоғингиз, Аллоҳнинг ҳузурида адолатлироқ, гувоҳликка кучлироқ ва шубҳаланмаслигингиз учун яқинроқдир. Магар ораларингизда юриб турган нақд савдо бўлса, ёзмасангиз ҳам, сизга гуноҳ бўлмас. Олди-сотди қилганингизда, гувоҳ келтиринг ва ёзувчи ҳам, гувоҳ ҳам зарар тортмасин. Агар (аксини) қилсангиз, албатта, бу фосиқлигингиздир. Аллоҳга тақво қилинг. Ва Аллоҳ сизга илм берадир. Аллоҳ ҳар бир нарсани билувчи Зотдир'}, {'chapter': 2, 'verse': 283, 'text': '۞وَإِن كُنتُمۡ عَلَىٰ سَفَرٖ وَلَمۡ تَجِدُواْ كَاتِبٗا فَرِهَٰنٞ مَّقۡبُوضَةٞۖ فَإِنۡ أَمِنَ بَعۡضُكُم بَعۡضٗا فَلۡيُؤَدِّ ٱلَّذِي ٱؤۡتُمِنَ أَمَٰنَتَهُۥ وَلۡيَتَّقِ ٱللَّهَ رَبَّهُۥۗ وَلَا تَكۡتُمُواْ ٱلشَّهَٰدَةَۚ وَمَن يَكۡتُمۡهَا فَإِنَّهُۥٓ ءَاثِمٞ قَلۡبُهُۥۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ\nАгар сафарда бўлсангиз ва ёзувчи топа олмасангиз, бас, қўлга олинадиган гаров бўлсин. Агар баъзингиз баъзингизга ишонсангиз, бас, ишонилган омонатни адо қилсин, Аллоҳга — Роббига тақво қилсин. Гувоҳликни беркитманг! Ким уни беркитса, унинг қалби гуноҳкордир. Ва Аллоҳ қилаётган амалларингизни билувчи Зотдир'}, {'chapter': 2, 'verse': 284, 'text': 'لِّلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَإِن تُبۡدُواْ مَا فِيٓ أَنفُسِكُمۡ أَوۡ تُخۡفُوهُ يُحَاسِبۡكُم بِهِ ٱللَّهُۖ فَيَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлару ердаги нарсалар Аллоҳникидир. Агар ичингиздаги нарсаларни ошкор қилсангиз ҳам, махфий қилсангиз ҳам, сизларни Аллоҳ ҳисоб-китоб қиладир. Ва кимни хоҳласа, мағфират қилар, кимни хоҳласа, азоблар. Ва У ҳар бир нарсага қодирдир'}, {'chapter': 2, 'verse': 285, 'text': 'ءَامَنَ ٱلرَّسُولُ بِمَآ أُنزِلَ إِلَيۡهِ مِن رَّبِّهِۦ وَٱلۡمُؤۡمِنُونَۚ كُلٌّ ءَامَنَ بِٱللَّهِ وَمَلَـٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّن رُّسُلِهِۦۚ وَقَالُواْ سَمِعۡنَا وَأَطَعۡنَاۖ غُفۡرَانَكَ رَبَّنَا وَإِلَيۡكَ ٱلۡمَصِيرُ\nПайғамбар унга ўз Роббисидан туширилган нарсага иймон келтирди ва мўминлар ҳам. Аллоҳга, Унинг фаришталарига, китобларига, Пайғамбарларига ҳамма-ҳамма иймон келтирди. Унинг Пайғамбарларининг бирорталарини ажратмаймиз. Ва: «Эшитдик ва итоат қилдик, (эй Роббимиз), мағфиратингни сўраймиз ва қайтиб бориш Сенгадир», дедилар'}, {'chapter': 2, 'verse': 286, 'text': 'لَا يُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا وُسۡعَهَاۚ لَهَا مَا كَسَبَتۡ وَعَلَيۡهَا مَا ٱكۡتَسَبَتۡۗ رَبَّنَا لَا تُؤَاخِذۡنَآ إِن نَّسِينَآ أَوۡ أَخۡطَأۡنَاۚ رَبَّنَا وَلَا تَحۡمِلۡ عَلَيۡنَآ إِصۡرٗا كَمَا حَمَلۡتَهُۥ عَلَى ٱلَّذِينَ مِن قَبۡلِنَاۚ رَبَّنَا وَلَا تُحَمِّلۡنَا مَا لَا طَاقَةَ لَنَا بِهِۦۖ وَٱعۡفُ عَنَّا وَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَآۚ أَنتَ مَوۡلَىٰنَا فَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nАллоҳ ҳеч бир жонни тоқатидан ташқари нарсага таклиф қилмас. Фойдаси ҳам ўз касбидан, зарари ҳам ўз касбидан. Эй Роббимиз, агар унутсак ёки хато қилсак, (иқобга) тутмагин. Эй Роббимиз, биздан олдингиларга юклаганга ўхшаш оғирликни бизга юкламагин. Эй Роббимиз, бизга тоқатимиз етмайдиган нарсани юкламагин. Бизни афв эт, мағфират қил ва раҳим эт. Сен хожамизсан. Бас, кофир қавмларга бизни ғолиб қил'}], '3': [{'chapter': 3, 'verse': 1, 'text': 'الٓمٓ\nАлиф. Лаам. Мийм'}, {'chapter': 3, 'verse': 2, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡحَيُّ ٱلۡقَيُّومُ\nАллоҳ — Ундан ўзга илоҳ йўқ. У тирик ва қаййум Зотдир'}, {'chapter': 3, 'verse': 3, 'text': 'نَزَّلَ عَلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَأَنزَلَ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ\nУ сенга китобни ҳақ ила ўзидан аввалги нарсани тасдиқловчи қилиб туширди ҳамда Таврот ва Инжилни туширди'}, {'chapter': 3, 'verse': 4, 'text': 'مِن قَبۡلُ هُدٗى لِّلنَّاسِ وَأَنزَلَ ٱلۡفُرۡقَانَۗ إِنَّ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ لَهُمۡ عَذَابٞ شَدِيدٞۗ وَٱللَّهُ عَزِيزٞ ذُو ٱنتِقَامٍ\nУндан олдин одамларга ҳидоят қилиб. Ва Фурқонни ҳам туширди. Аллоҳнинг оятларига куфр келтирганларга шиддатли азоб бор. Аллоҳ Азийз ва интиқом олувчи Зотдир'}, {'chapter': 3, 'verse': 5, 'text': 'إِنَّ ٱللَّهَ لَا يَخۡفَىٰ عَلَيۡهِ شَيۡءٞ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ\nАлбатта, Аллоҳ Унга ерда ҳам, осмонда ҳам ҳеч нарса махфий қолмайдиган Зотдир'}, {'chapter': 3, 'verse': 6, 'text': 'هُوَ ٱلَّذِي يُصَوِّرُكُمۡ فِي ٱلۡأَرۡحَامِ كَيۡفَ يَشَآءُۚ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ сизларни бачадонларда хоҳлаган сувратга соладиган Зотдир. Ундан ўзга илоҳ йўқ. У Азийз ва Ҳакийм Зотдир'}, {'chapter': 3, 'verse': 7, 'text': 'هُوَ ٱلَّذِيٓ أَنزَلَ عَلَيۡكَ ٱلۡكِتَٰبَ مِنۡهُ ءَايَٰتٞ مُّحۡكَمَٰتٌ هُنَّ أُمُّ ٱلۡكِتَٰبِ وَأُخَرُ مُتَشَٰبِهَٰتٞۖ فَأَمَّا ٱلَّذِينَ فِي قُلُوبِهِمۡ زَيۡغٞ فَيَتَّبِعُونَ مَا تَشَٰبَهَ مِنۡهُ ٱبۡتِغَآءَ ٱلۡفِتۡنَةِ وَٱبۡتِغَآءَ تَأۡوِيلِهِۦۖ وَمَا يَعۡلَمُ تَأۡوِيلَهُۥٓ إِلَّا ٱللَّهُۗ وَٱلرَّـٰسِخُونَ فِي ٱلۡعِلۡمِ يَقُولُونَ ءَامَنَّا بِهِۦ كُلّٞ مِّنۡ عِندِ رَبِّنَاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nУ сенга китобни туширган Зотдир. Унда муҳкам — ойдин оятлар ҳам бор ва улар китобнинг аслидир ва муташобиҳ оятлар ҳам бор. Қалбларида ҳидоятдан оғиш борлар фитна мақсадида ва уни таъвийл қилиш мақсадида ундан муташобиҳ бўлганига эргашадир. Унинг таъвийлини Аллоҳдан бошқа ҳеч ким билмас. Илмда собит бўлганлар эса: Унга иймон келтирдик, барчаси Роббимиз ҳузуридандир, дерлар. Ва фақат ақл эгаларигина эсларлар'}, {'chapter': 3, 'verse': 8, 'text': 'رَبَّنَا لَا تُزِغۡ قُلُوبَنَا بَعۡدَ إِذۡ هَدَيۡتَنَا وَهَبۡ لَنَا مِن لَّدُنكَ رَحۡمَةًۚ إِنَّكَ أَنتَ ٱلۡوَهَّابُ\nЭй Роббимиз, бизни ҳидоят қилганингдан сўнг қалбларимизни оғдирмагин ва бизга Ўз ҳузурингдан раҳмат ато қилгин. Албатта Сенинг Ўзинг кўплаб ато қилувчисан'}, {'chapter': 3, 'verse': 9, 'text': 'رَبَّنَآ إِنَّكَ جَامِعُ ٱلنَّاسِ لِيَوۡمٖ لَّا رَيۡبَ فِيهِۚ إِنَّ ٱللَّهَ لَا يُخۡلِفُ ٱلۡمِيعَادَ\nЭй Роббимиз, албатта, Сен одамларни келишига шубҳа йўқ кунда йиғувчисан. Албатта, Аллоҳ ваъдага хилоф қилмас'}, {'chapter': 3, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـٔٗاۖ وَأُوْلَـٰٓئِكَ هُمۡ وَقُودُ ٱلنَّارِ\nАлбатта, куфр келтирганларнинг моллари ҳам, болалари ҳам улардан Аллоҳнинг азобини қайтара олмас. Ана ўшалар, ўзлари дўзахнинг ёқилғисидирлар'}, {'chapter': 3, 'verse': 11, 'text': 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِنَا فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۗ وَٱللَّهُ شَدِيدُ ٱلۡعِقَابِ\nФиръавн аҳлининг ва улардан олдингиларнинг ҳолига ўхшар. Улар оятларимизни ёлғонга чиқардилар. Бас, гуноҳлари туфайли Аллоҳ уларни тутди. Ва Аллоҳ иқоби шиддатли Зотдир'}, {'chapter': 3, 'verse': 12, 'text': 'قُل لِّلَّذِينَ كَفَرُواْ سَتُغۡلَبُونَ وَتُحۡشَرُونَ إِلَىٰ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمِهَادُ\nКуфр келтирганларга: «Тезда мағлуб бўласизлар ва жаҳаннамга йиғиласизлар, у қандоқ ҳам ёмон жой», деб айт'}, {'chapter': 3, 'verse': 13, 'text': 'قَدۡ كَانَ لَكُمۡ ءَايَةٞ فِي فِئَتَيۡنِ ٱلۡتَقَتَاۖ فِئَةٞ تُقَٰتِلُ فِي سَبِيلِ ٱللَّهِ وَأُخۡرَىٰ كَافِرَةٞ يَرَوۡنَهُم مِّثۡلَيۡهِمۡ رَأۡيَ ٱلۡعَيۡنِۚ وَٱللَّهُ يُؤَيِّدُ بِنَصۡرِهِۦ مَن يَشَآءُۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ\n«Сизга тўқнашган икки фирқада ибрат бор эди. Бир фирқа Аллоҳнинг йўлида уришадир. Бошқаси кофирдир. Улар ўз кўзлари билан (душман) ўзларига икки баробарлигини кўриб турардилар. Ва Аллоҳ кимни хоҳласа, ўшани Ўз нусрати ила қўллайдир. Албатта, бунда ақл эгалари учун ибрат бордир», деб айт'}, {'chapter': 3, 'verse': 14, 'text': 'زُيِّنَ لِلنَّاسِ حُبُّ ٱلشَّهَوَٰتِ مِنَ ٱلنِّسَآءِ وَٱلۡبَنِينَ وَٱلۡقَنَٰطِيرِ ٱلۡمُقَنطَرَةِ مِنَ ٱلذَّهَبِ وَٱلۡفِضَّةِ وَٱلۡخَيۡلِ ٱلۡمُسَوَّمَةِ وَٱلۡأَنۡعَٰمِ وَٱلۡحَرۡثِۗ ذَٰلِكَ مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلۡمَـَٔابِ\nОдамларга аёллардан, болалардан, тўп-тўп тилло ва кумушдан, гўзал отлардан, чорвадан, экин-тикиндан иборат шаҳватларнинг муҳаббати зийнатланди. Улар дунё ҳаётининг матоҳидир. Аллоҳнинг ҳузурида эса, ҳуснли қайтар жой бор'}, {'chapter': 3, 'verse': 15, 'text': '۞قُلۡ أَؤُنَبِّئُكُم بِخَيۡرٖ مِّن ذَٰلِكُمۡۖ لِلَّذِينَ ٱتَّقَوۡاْ عِندَ رَبِّهِمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَأَزۡوَٰجٞ مُّطَهَّرَةٞ وَرِضۡوَٰنٞ مِّنَ ٱللَّهِۗ وَٱللَّهُ بَصِيرُۢ بِٱلۡعِبَادِ\nСен: «Ўша нарсаларингиздан яхшироғининг хабарини берайми?! Тақво қилганларга Роббилари ҳузурида остидан анҳорлар оқиб турган жаннатлар бор. Унда абадий қолурлар. Покиза жуфтлар ва Аллоҳ томонидан розилик бор. Ва Аллоҳ бандаларни кўриб турувчидир», деб айт'}, {'chapter': 3, 'verse': 16, 'text': 'ٱلَّذِينَ يَقُولُونَ رَبَّنَآ إِنَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ ٱلنَّارِ\nУлар: «Роббимиз, албатта, биз иймон келтирдик, бизнинг гуноҳларимизни мағфират қилгин ва дўзах азобидан сақлагин», дейдиганлардир'}, {'chapter': 3, 'verse': 17, 'text': 'ٱلصَّـٰبِرِينَ وَٱلصَّـٰدِقِينَ وَٱلۡقَٰنِتِينَ وَٱلۡمُنفِقِينَ وَٱلۡمُسۡتَغۡفِرِينَ بِٱلۡأَسۡحَارِ\nСабр қилувчилар, содиқлар, доимий тоат қилувчилар, нафақа қилувчилар ва саҳарда истиғфор айтувчилардир'}, {'chapter': 3, 'verse': 18, 'text': 'شَهِدَ ٱللَّهُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا هُوَ وَٱلۡمَلَـٰٓئِكَةُ وَأُوْلُواْ ٱلۡعِلۡمِ قَآئِمَۢا بِٱلۡقِسۡطِۚ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАллоҳ адолат ила туриб, албатта, Ундан ўзга илоҳ йўқлигига шоҳидлик берди. Фаришталар ва илм эгалари ҳам. Ундан ўзга илоҳ йўқ. У азийз ва ҳакийм Зотдир'}, {'chapter': 3, 'verse': 19, 'text': 'إِنَّ ٱلدِّينَ عِندَ ٱللَّهِ ٱلۡإِسۡلَٰمُۗ وَمَا ٱخۡتَلَفَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۗ وَمَن يَكۡفُرۡ بِـَٔايَٰتِ ٱللَّهِ فَإِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nАлбатта, Аллоҳнинг ҳузуридаги дин Исломдир. Китоб берилганлар фақат уларга илм келгандан сўнг, ўзаро ҳасад қилишибгина ихтилоф қилдилар. Ким Аллоҳнинг оятларига куфр келтирса, бас, албатта, Аллоҳ ҳисобни тезда қилувчидир'}, {'chapter': 3, 'verse': 20, 'text': 'فَإِنۡ حَآجُّوكَ فَقُلۡ أَسۡلَمۡتُ وَجۡهِيَ لِلَّهِ وَمَنِ ٱتَّبَعَنِۗ وَقُل لِّلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَٱلۡأُمِّيِّـۧنَ ءَأَسۡلَمۡتُمۡۚ فَإِنۡ أَسۡلَمُواْ فَقَدِ ٱهۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُۗ وَٱللَّهُ بَصِيرُۢ بِٱلۡعِبَادِ\nАгар сен билан тортишсалар: «Мен юзимни Аллоҳга таслим этдим ва менга эргашганлар ҳам!« деб айт. Ва китоб берилганларга ва саводсизларга: «Исломга кирдингизми?» деб айт. Агар Исломга кирсалар, бас, батаҳқиқ, ҳидоят топдилар. Агар юз ўгирсалар, сенинг зиммангда етказиш, холос. Аллоҳ бандаларни кўриб турувчидир'}, {'chapter': 3, 'verse': 21, 'text': 'إِنَّ ٱلَّذِينَ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلنَّبِيِّـۧنَ بِغَيۡرِ حَقّٖ وَيَقۡتُلُونَ ٱلَّذِينَ يَأۡمُرُونَ بِٱلۡقِسۡطِ مِنَ ٱلنَّاسِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ\nАлбатта, Аллоҳнинг оятларига куфр келтирадиганлар, Пайғамбарларни ноҳақдан ўлдирадиганлар ва одамлардан адолатга буюрадиганларини ўлдирадиганларга аламли азобнинг башоратини бер'}, {'chapter': 3, 'verse': 22, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nАна ўшалар, амаллари бу дунё-ю охиратда ҳабата бўлганлардир ва уларга ёрдам берувчилар йўқдир'}, {'chapter': 3, 'verse': 23, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يُدۡعَوۡنَ إِلَىٰ كِتَٰبِ ٱللَّهِ لِيَحۡكُمَ بَيۡنَهُمۡ ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُمۡ وَهُم مُّعۡرِضُونَ\nКитобдан насиба берилганларга қарамайсанми?! Ораларида ҳукм юритиш учун Аллоҳнинг китобига чақирилганларида улардан бир гуруҳлари юз ўгириб ортга қайтарлар'}, {'chapter': 3, 'verse': 24, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَن تَمَسَّنَا ٱلنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَٰتٖۖ وَغَرَّهُمۡ فِي دِينِهِم مَّا كَانُواْ يَفۡتَرُونَ\nБунинг сабаби — уларнинг: «Бизга саноқли кунлардан бошқа ҳеч ўт тегмас», деганларидадир. Тўқиган нарсалари уларни динларида адаштирди'}, {'chapter': 3, 'verse': 25, 'text': 'فَكَيۡفَ إِذَا جَمَعۡنَٰهُمۡ لِيَوۡمٖ لَّا رَيۡبَ فِيهِ وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nУнда, бўлишида шубҳа йўқ кунда уларни жамлаганимизда ва ҳар бир жон зулм қилинмасдан, қилганига яраша жазосини олганда, ҳоллари қандай бўладир'}, {'chapter': 3, 'verse': 26, 'text': 'قُلِ ٱللَّهُمَّ مَٰلِكَ ٱلۡمُلۡكِ تُؤۡتِي ٱلۡمُلۡكَ مَن تَشَآءُ وَتَنزِعُ ٱلۡمُلۡكَ مِمَّن تَشَآءُ وَتُعِزُّ مَن تَشَآءُ وَتُذِلُّ مَن تَشَآءُۖ بِيَدِكَ ٱلۡخَيۡرُۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nСен: «Эй барча мулкнинг эгаси Роббим! Хоҳлаган кишингга мулк берурсан ва хоҳлаган кишингдан мулкни тортиб олурсан, хоҳлаган кишингни азиз қилурсан, хоҳлаган кишингни хор қилурсан. Барча яхшилик Сенинг қўлингда. Албатта, Сен ҳар бир нарсага қодирсан'}, {'chapter': 3, 'verse': 27, 'text': 'تُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَتُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِۖ وَتُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَتُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّۖ وَتَرۡزُقُ مَن تَشَآءُ بِغَيۡرِ حِسَابٖ\nКечани кундузга киритурсан ва кундузни кечага киритурсан, ўликдан тирикни чиқарурсан ва тирикдан ўликни чиқарурсан ҳамда хоҳлаган кишингга беҳисоб ризқ берурсан», деб айт'}, {'chapter': 3, 'verse': 28, 'text': 'لَّا يَتَّخِذِ ٱلۡمُؤۡمِنُونَ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۖ وَمَن يَفۡعَلۡ ذَٰلِكَ فَلَيۡسَ مِنَ ٱللَّهِ فِي شَيۡءٍ إِلَّآ أَن تَتَّقُواْ مِنۡهُمۡ تُقَىٰةٗۗ وَيُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ\nМўминлар мўминларни қўйиб, кофирларни дўст тутмасинлар. Ким буни қилса, бас, унга Аллоҳдан ҳеч нарса йўқ. Магар улардан сақлансангиз, Аллоҳ сизларни Ўз(азоб)идан огоҳ қиладир. Ва қайтиб бориш Аллоҳнинг Ўзигадир'}, {'chapter': 3, 'verse': 29, 'text': 'قُلۡ إِن تُخۡفُواْ مَا فِي صُدُورِكُمۡ أَوۡ تُبۡدُوهُ يَعۡلَمۡهُ ٱللَّهُۗ وَيَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nСен: «Агар қалбингиздаги нарсани махфий тутсангиз ҳам, ошкор қилсангиз ҳам Аллоҳ биладир. Ва осмонлардагию ердаги нарсаларни ҳам биладир. Аллоҳ ҳар бир нарсага қодирдир», деб айт'}, {'chapter': 3, 'verse': 30, 'text': 'يَوۡمَ تَجِدُ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ مِنۡ خَيۡرٖ مُّحۡضَرٗا وَمَا عَمِلَتۡ مِن سُوٓءٖ تَوَدُّ لَوۡ أَنَّ بَيۡنَهَا وَبَيۡنَهُۥٓ أَمَدَۢا بَعِيدٗاۗ وَيُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ\nҲар бир жон қилган яхши амалини ҳозир топадиган ва ёмон амалининг узоқ масофада бўлишини истайдиган кунда. Аллоҳ сизларни Ўз(азоб)идан огоҳ қиладир. Аллоҳ бандаларига меҳрибон Зотдир'}, {'chapter': 3, 'verse': 31, 'text': 'قُلۡ إِن كُنتُمۡ تُحِبُّونَ ٱللَّهَ فَٱتَّبِعُونِي يُحۡبِبۡكُمُ ٱللَّهُ وَيَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nСен: «Агар Аллоҳга муҳаббат қилсангиз, бас, менга эргашинг. Аллоҳ сизга муҳаббат қиладир ва сизларнинг гуноҳларингизни мағфират қиладир», деб айт. Аллоҳ Ғафур ва Роҳиймдир'}, {'chapter': 3, 'verse': 32, 'text': 'قُلۡ أَطِيعُواْ ٱللَّهَ وَٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡكَٰفِرِينَ\nАллоҳга ва Расулга итоат қилинг. Бас, агар ортга қайтсангиз, Аллоҳ, албатта, кофирларга муҳаббат қилмас'}, {'chapter': 3, 'verse': 33, 'text': '۞إِنَّ ٱللَّهَ ٱصۡطَفَىٰٓ ءَادَمَ وَنُوحٗا وَءَالَ إِبۡرَٰهِيمَ وَءَالَ عِمۡرَٰنَ عَلَى ٱلۡعَٰلَمِينَ\nАлбатта, Аллоҳ Одамни, Нуҳни, Оли Иброҳимни, Оли Имронни оламлар устидан танлаб олди'}, {'chapter': 3, 'verse': 34, 'text': 'ذُرِّيَّةَۢ بَعۡضُهَا مِنۢ بَعۡضٖۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\nБаъзилари баъзиларига зурриётдир. Аллоҳ эшитувчи, билувчи Зотдир'}, {'chapter': 3, 'verse': 35, 'text': 'إِذۡ قَالَتِ ٱمۡرَأَتُ عِمۡرَٰنَ رَبِّ إِنِّي نَذَرۡتُ لَكَ مَا فِي بَطۡنِي مُحَرَّرٗا فَتَقَبَّلۡ مِنِّيٓۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nИмроннинг хотини: «Эй Роббим! Албатта, мен қорнимдагини Ўзингга холис назр қилдим. Бас, мендан қабул эт. Албатта, Сенинг Ўзинг эшитувчи, билувчи зотсан», деганини эсла'}, {'chapter': 3, 'verse': 36, 'text': 'فَلَمَّا وَضَعَتۡهَا قَالَتۡ رَبِّ إِنِّي وَضَعۡتُهَآ أُنثَىٰ وَٱللَّهُ أَعۡلَمُ بِمَا وَضَعَتۡ وَلَيۡسَ ٱلذَّكَرُ كَٱلۡأُنثَىٰۖ وَإِنِّي سَمَّيۡتُهَا مَرۡيَمَ وَإِنِّيٓ أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ ٱلشَّيۡطَٰنِ ٱلرَّجِيمِ\nУни (Марямни) туққан чоғида: «Эй Роббим, мен буни қиз туғдим-ку!!» – деди. Ҳолбуки, Аллоҳ у нима туққанини яхши билади. «Ва ўғил қиздек эмас. Ва мен унга «Марям» деб ном қўйдим ва албатта, мен Сендан унга ва унинг зурриётига шайтонир рожим шарридан паноҳ тиларман.»'}, {'chapter': 3, 'verse': 37, 'text': 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٖ وَأَنۢبَتَهَا نَبَاتًا حَسَنٗا وَكَفَّلَهَا زَكَرِيَّاۖ كُلَّمَا دَخَلَ عَلَيۡهَا زَكَرِيَّا ٱلۡمِحۡرَابَ وَجَدَ عِندَهَا رِزۡقٗاۖ قَالَ يَٰمَرۡيَمُ أَنَّىٰ لَكِ هَٰذَاۖ قَالَتۡ هُوَ مِنۡ عِندِ ٱللَّهِۖ إِنَّ ٱللَّهَ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٍ\nБас, уни Робби жуда яхши қабул қилиб, ниҳоятда гўзал ўстирди ва Закариёни унга кафил қилди. Закариё ҳар қачон унинг олдига, меҳробга кирганида, унинг ҳузурида ризқ кўрди. У: «Эй Марям, сенга бу қаердан», – деди. «Бу, Аллоҳнинг ҳузуридан. Албатта, Аллоҳ хоҳлаган кишисига беҳисоб ризқ берур», – деди'}, {'chapter': 3, 'verse': 38, 'text': 'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُۥۖ قَالَ رَبِّ هَبۡ لِي مِن لَّدُنكَ ذُرِّيَّةٗ طَيِّبَةًۖ إِنَّكَ سَمِيعُ ٱلدُّعَآءِ\nШу чоғда Закариё Роббига дуо қилиб: «Эй Роббим, менга Ўз ҳузурингдан яхши зурриёт бергин. Албатта, Сен дуони эшитувчисан», – деди'}, {'chapter': 3, 'verse': 39, 'text': 'فَنَادَتۡهُ ٱلۡمَلَـٰٓئِكَةُ وَهُوَ قَآئِمٞ يُصَلِّي فِي ٱلۡمِحۡرَابِ أَنَّ ٱللَّهَ يُبَشِّرُكَ بِيَحۡيَىٰ مُصَدِّقَۢا بِكَلِمَةٖ مِّنَ ٱللَّهِ وَسَيِّدٗا وَحَصُورٗا وَنَبِيّٗا مِّنَ ٱلصَّـٰلِحِينَ\nМеҳробда намоз ўқиб турганида, фаришталар унга: «Албатта, Аллоҳ сенга Аллоҳдан бўлган калимани тасдиқловчи, бошлиқ, шаҳвати тийилган, солиҳ Пайғамбарлардан бўлган Яҳёнинг башоратини бермоқда», – дедилар'}, {'chapter': 3, 'verse': 40, 'text': 'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَقَدۡ بَلَغَنِيَ ٱلۡكِبَرُ وَٱمۡرَأَتِي عَاقِرٞۖ قَالَ كَذَٰلِكَ ٱللَّهُ يَفۡعَلُ مَا يَشَآءُ\nУ: «Эй Роббим, менга қандоқ ўғил бўлсин?! Ўзим улуғ ёшга кирган бўлсам, хотиним туғмас бўлса?!» – деди. У зот: «Шундоқ, Аллоҳ хоҳлаганини қиладир», – деди'}, {'chapter': 3, 'verse': 41, 'text': 'قَالَ رَبِّ ٱجۡعَل لِّيٓ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَةَ أَيَّامٍ إِلَّا رَمۡزٗاۗ وَٱذۡكُر رَّبَّكَ كَثِيرٗا وَسَبِّحۡ بِٱلۡعَشِيِّ وَٱلۡإِبۡكَٰرِ\nУ: «Эй Роббим, менга бир белги тайинла», – деди. У зот: «Сенинг белгинг — уч кун одамларга фақат ишора билан гапиришингдир. Роббингни кўп эсла ва эртаю кеч тасбиҳ айт!» – деди'}, {'chapter': 3, 'verse': 42, 'text': 'وَإِذۡ قَالَتِ ٱلۡمَلَـٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ ٱللَّهَ ٱصۡطَفَىٰكِ وَطَهَّرَكِ وَٱصۡطَفَىٰكِ عَلَىٰ نِسَآءِ ٱلۡعَٰلَمِينَ\nФаришталарнинг: «Эй Марям, албатта, Аллоҳ сени танлаб олди, поклади ва оламдаги аёллардан устунлигингни ихтиёр қилди'}, {'chapter': 3, 'verse': 43, 'text': 'يَٰمَرۡيَمُ ٱقۡنُتِي لِرَبِّكِ وَٱسۡجُدِي وَٱرۡكَعِي مَعَ ٱلرَّـٰكِعِينَ\nЭй Марям, Роббингнинг ибодатида бардавом бўл, Унга сажда қил ва рукуъ қилувчилар билан рукуъ қил», деганларини эсла'}, {'chapter': 3, 'verse': 44, 'text': 'ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهِ إِلَيۡكَۚ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يُلۡقُونَ أَقۡلَٰمَهُمۡ أَيُّهُمۡ يَكۡفُلُ مَرۡيَمَ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يَخۡتَصِمُونَ\nБу ғайб хабарлардан бўлиб, сенга ваҳий қилмоқдамиз. Улар қайсилари Марямнинг кафили бўлишига қаламларини ташлашаётганларида олдиларида эмасдинг ва тортишаётганларида ҳам олдиларида эмасдинг'}, {'chapter': 3, 'verse': 45, 'text': 'إِذۡ قَالَتِ ٱلۡمَلَـٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ ٱللَّهَ يُبَشِّرُكِ بِكَلِمَةٖ مِّنۡهُ ٱسۡمُهُ ٱلۡمَسِيحُ عِيسَى ٱبۡنُ مَرۡيَمَ وَجِيهٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمِنَ ٱلۡمُقَرَّبِينَ\nФаришталар: «Эй Марям, албатта, Аллоҳ сенга Ўзидан бўлган сўзнинг башоратини бермоқда, унинг исми Масийҳ Ийсо ибн Марям, бу дунёю охиратда обрўли ва яқин бандалардандир'}, {'chapter': 3, 'verse': 46, 'text': 'وَيُكَلِّمُ ٱلنَّاسَ فِي ٱلۡمَهۡدِ وَكَهۡلٗا وَمِنَ ٱلصَّـٰلِحِينَ\nУ одамларга бешикда ҳам, қариган чоғида ҳам гапиради ва солиҳлардандир», деганларини эсла'}, {'chapter': 3, 'verse': 47, 'text': 'قَالَتۡ رَبِّ أَنَّىٰ يَكُونُ لِي وَلَدٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞۖ قَالَ كَذَٰلِكِ ٱللَّهُ يَخۡلُقُ مَا يَشَآءُۚ إِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nУ: «Эй Роббим, менга башар қўл теккизмаган бўлса, қандай қилиб болам бўлади?!» – деди. У зот: «Шундай, Аллоҳ хоҳлаганини қилади. Бир ишни ирода қилса, «Бўл!» дейиши кифоя, бўлаверади», – деди'}, {'chapter': 3, 'verse': 48, 'text': 'وَيُعَلِّمُهُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ\nВа унга китобни, ҳикматни, Таврот ва Инжилни ўргатади'}, {'chapter': 3, 'verse': 49, 'text': 'وَرَسُولًا إِلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَنِّي قَدۡ جِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ أَنِّيٓ أَخۡلُقُ لَكُم مِّنَ ٱلطِّينِ كَهَيۡـَٔةِ ٱلطَّيۡرِ فَأَنفُخُ فِيهِ فَيَكُونُ طَيۡرَۢا بِإِذۡنِ ٱللَّهِۖ وَأُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ وَأُحۡيِ ٱلۡمَوۡتَىٰ بِإِذۡنِ ٱللَّهِۖ وَأُنَبِّئُكُم بِمَا تَأۡكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nВа Бани Исроилга Пайғамбар қилади. У: «Албатта, мен сизга Роббингиздан мўъжиза келтирдим, мен сизларга лойдан қушга ўхшаш суврат ясайман, унга пуфласам, Аллоҳнинг изни билан қуш бўлади. Аллоҳ изни билан туғма кўр ва песларни тузатаман ва ўликни тирилтираман. Сизларга нимани емоқдасизу, нимани уйингизда сақлаяпсиз — хабарини бераман. Агар мўмин бўлсангиз, албатта, бунда сизга белги бордир», дейди'}, {'chapter': 3, 'verse': 50, 'text': 'وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ ٱلتَّوۡرَىٰةِ وَلِأُحِلَّ لَكُم بَعۡضَ ٱلَّذِي حُرِّمَ عَلَيۡكُمۡۚ وَجِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nВа ўзимдан аввалги Тавротни тасдиқловчи бўлиб, сизга ҳаром қилинганнинг баъзисини ҳалол қилиш учун келдим. Сизга Роббингиздан мўъжиза билан келдим. Аллоҳга тақво қилинг ва менга итоат этинг'}, {'chapter': 3, 'verse': 51, 'text': 'إِنَّ ٱللَّهَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nАлбатта, Аллоҳ менинг Роббим ва сизнинг Роббингиздир. Бас, Унга ибодат қилинг. Бу — сиротул мустақиймдир», дейдир'}, {'chapter': 3, 'verse': 52, 'text': '۞فَلَمَّآ أَحَسَّ عِيسَىٰ مِنۡهُمُ ٱلۡكُفۡرَ قَالَ مَنۡ أَنصَارِيٓ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ ٱللَّهِ ءَامَنَّا بِٱللَّهِ وَٱشۡهَدۡ بِأَنَّا مُسۡلِمُونَ\nИйсо улардан кофирликни сезиб қолганда: «Кимлар Аллоҳ йўлида менга ёрдамчи бўлади?!» – деди. Ҳаворийлар: «Биз Аллоҳнинг ёрдамчиларимиз, Аллоҳга иймон келтирдик ва бизнинг мусулмонлигимизга гувоҳ бўл», – дедилар'}, {'chapter': 3, 'verse': 53, 'text': 'رَبَّنَآ ءَامَنَّا بِمَآ أَنزَلۡتَ وَٱتَّبَعۡنَا ٱلرَّسُولَ فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِينَ\n«Эй Роббимиз! Сен туширган нарсага иймон келтирдик ва Пайғамбарга эргашдик. Бас, бизни гувоҳлик берувчилар қаторига ёзгин»'}, {'chapter': 3, 'verse': 54, 'text': 'وَمَكَرُواْ وَمَكَرَ ٱللَّهُۖ وَٱللَّهُ خَيۡرُ ٱلۡمَٰكِرِينَ\nМакр қилдилар. Аллоҳ ҳам макр қилди. Аллоҳ макр қилувчиларнинг «зўри»дир'}, {'chapter': 3, 'verse': 55, 'text': 'إِذۡ قَالَ ٱللَّهُ يَٰعِيسَىٰٓ إِنِّي مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ ٱلَّذِينَ كَفَرُواْ وَجَاعِلُ ٱلَّذِينَ ٱتَّبَعُوكَ فَوۡقَ ٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۖ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأَحۡكُمُ بَيۡنَكُمۡ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nАллоҳ: «Эй Ийсо, мен сени вафот қилдирувчи ва Ўзимга кўтарувчиман ҳамда куфр келтирганлардан сени покловчиман ва қиёмат кунигача сенга эргашганларни куфр келтирганлардан устун қўювчиман. Сўнгра, қайтиш жойингиз Ўзимга, бас, ихтилоф қилган нарсаларингиз бўйича орангизда Ўзим ҳукм чиқарурман», деганини эсла'}, {'chapter': 3, 'verse': 56, 'text': 'فَأَمَّا ٱلَّذِينَ كَفَرُواْ فَأُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nБас, куфр келтирганларни бу дунёю у дунёда шиддатли азоб ила азоблайман. Уларга ёрдам берувчилар йўқдир'}, {'chapter': 3, 'verse': 57, 'text': 'وَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡۗ وَٱللَّهُ لَا يُحِبُّ ٱلظَّـٰلِمِينَ\nИймон келтирган ва яхши амаллар қилганларнинг ажрини тўлиқ беради. Ва Аллоҳ золимларни хуш кўрмас'}, {'chapter': 3, 'verse': 58, 'text': 'ذَٰلِكَ نَتۡلُوهُ عَلَيۡكَ مِنَ ٱلۡأٓيَٰتِ وَٱلذِّكۡرِ ٱلۡحَكِيمِ\nБу, сенга тиловат қилиб бераётган оятларимиз ва ҳаким эслатмадандир'}, {'chapter': 3, 'verse': 59, 'text': 'إِنَّ مَثَلَ عِيسَىٰ عِندَ ٱللَّهِ كَمَثَلِ ءَادَمَۖ خَلَقَهُۥ مِن تُرَابٖ ثُمَّ قَالَ لَهُۥ كُن فَيَكُونُ\nАлбатта, Аллоҳнинг ҳузурида Ийсонинг мисоли худди Одамнинг мисолига ўхшайдир. Уни тупроқдан яратиб, сўнгра унга «Бўл!» деди, бас, (у) бўлди'}, {'chapter': 3, 'verse': 60, 'text': 'ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُن مِّنَ ٱلۡمُمۡتَرِينَ\nБу ҳақиқат Роббингдандир, бас, шак келтирувчилардан бўлма'}, {'chapter': 3, 'verse': 61, 'text': 'فَمَنۡ حَآجَّكَ فِيهِ مِنۢ بَعۡدِ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ فَقُلۡ تَعَالَوۡاْ نَدۡعُ أَبۡنَآءَنَا وَأَبۡنَآءَكُمۡ وَنِسَآءَنَا وَنِسَآءَكُمۡ وَأَنفُسَنَا وَأَنفُسَكُمۡ ثُمَّ نَبۡتَهِلۡ فَنَجۡعَل لَّعۡنَتَ ٱللَّهِ عَلَى ٱلۡكَٰذِبِينَ\nСенга келган илмдан кейин, ким сен билан у ҳақида тортишса, Сен: «Келинглар, бизнинг болаларимизни ва сизнинг болаларингизни, бизнинг аёлларимизни ва сизнинг аёлларингизни, биз ўзимизни, сиз ўзингизни чақирамиз-да, сўнгра ёлбориб, ёлғончиларга Аллоҳнинг лаънати бўлишини сўраймиз», деб айтгин'}, {'chapter': 3, 'verse': 62, 'text': 'إِنَّ هَٰذَا لَهُوَ ٱلۡقَصَصُ ٱلۡحَقُّۚ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАлбатта, бу ўзи ҳақ қиссадир. Аллоҳдан ўзга ҳеч бир илоҳ йўқ. Албатта, Аллоҳ, Унинг Ўзи Азийз ва Ҳакийм Зотдир'}, {'chapter': 3, 'verse': 63, 'text': 'فَإِن تَوَلَّوۡاْ فَإِنَّ ٱللَّهَ عَلِيمُۢ بِٱلۡمُفۡسِدِينَ\nАгар юз ўгириб кетсалар, бас, албатта, Аллоҳ фасод қилувчиларни билувчидир'}, {'chapter': 3, 'verse': 64, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ تَعَالَوۡاْ إِلَىٰ كَلِمَةٖ سَوَآءِۭ بَيۡنَنَا وَبَيۡنَكُمۡ أَلَّا نَعۡبُدَ إِلَّا ٱللَّهَ وَلَا نُشۡرِكَ بِهِۦ شَيۡـٔٗا وَلَا يَتَّخِذَ بَعۡضُنَا بَعۡضًا أَرۡبَابٗا مِّن دُونِ ٱللَّهِۚ فَإِن تَوَلَّوۡاْ فَقُولُواْ ٱشۡهَدُواْ بِأَنَّا مُسۡلِمُونَ\nСен: «Эй, аҳли китоблар! Бизга ҳам, сизга ҳам баробар сўзга келинг: Аллоҳдан ўзгага ибодат қилмайлик, Унга ҳеч нарсани шерик қилмайлик ва Аллоҳни қўйиб, баъзимиз баъзимизни Робб қилиб олмайлик», дегин. Бас, агар юз ўгирсалар: «Гувоҳ бўлинглар, биз, албатта, мусулмонлармиз», деб айтинглар'}, {'chapter': 3, 'verse': 65, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تُحَآجُّونَ فِيٓ إِبۡرَٰهِيمَ وَمَآ أُنزِلَتِ ٱلتَّوۡرَىٰةُ وَٱلۡإِنجِيلُ إِلَّا مِنۢ بَعۡدِهِۦٓۚ أَفَلَا تَعۡقِلُونَ\nЭй, аҳли китоблар, нимага Иброҳим ҳақида талашасизлар. Ҳолбуки, Таврот ҳам, Инжил ҳам ундан кейин тушган-ку! Ақл юритмайсизларми'}, {'chapter': 3, 'verse': 66, 'text': 'هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ حَٰجَجۡتُمۡ فِيمَا لَكُم بِهِۦ عِلۡمٞ فَلِمَ تُحَآجُّونَ فِيمَا لَيۡسَ لَكُم بِهِۦ عِلۡمٞۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nҲой сизлар, ҳалигилар! Ўзингиз билган нарсада-ку талашдингиз, энди нимага ўзингиз билмаган нарсада талашмоқдасиз?! Аллоҳ биладир, сиз эса билмайсиз'}, {'chapter': 3, 'verse': 67, 'text': 'مَا كَانَ إِبۡرَٰهِيمُ يَهُودِيّٗا وَلَا نَصۡرَانِيّٗا وَلَٰكِن كَانَ حَنِيفٗا مُّسۡلِمٗا وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nИброҳим яҳудий ҳам, насроний ҳам бўлмаган эди. Лекин у ҳақ йўлдан тоймаган мусулмон эди. Ва мушриклардан ҳам бўлмаган эди'}, {'chapter': 3, 'verse': 68, 'text': 'إِنَّ أَوۡلَى ٱلنَّاسِ بِإِبۡرَٰهِيمَ لَلَّذِينَ ٱتَّبَعُوهُ وَهَٰذَا ٱلنَّبِيُّ وَٱلَّذِينَ ءَامَنُواْۗ وَٱللَّهُ وَلِيُّ ٱلۡمُؤۡمِنِينَ\nАлбатта, одамларнинг Иброҳимга ҳақлироғи унга эргашганлар, мана бу Пайғамбар ва иймон келтирганлардир. Аллоҳ мўминларнинг валийидир'}, {'chapter': 3, 'verse': 69, 'text': 'وَدَّت طَّآئِفَةٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ لَوۡ يُضِلُّونَكُمۡ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ\nАҳли китоблардан бир тоифалари сизни адаштиришни хоҳларлар. Улар ўзларидан бошқани адаштирмаслар ва лекин сезмаслар'}, {'chapter': 3, 'verse': 70, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَأَنتُمۡ تَشۡهَدُونَ\nЭй, аҳли китоблар, нимага ўзингиз гувоҳ бўлиб туриб, Аллоҳнинг оятларига куфр келтирасизлар'}, {'chapter': 3, 'verse': 71, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَلۡبِسُونَ ٱلۡحَقَّ بِٱلۡبَٰطِلِ وَتَكۡتُمُونَ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ\nЭй, аҳли китоблар, нимага ҳақни ботилга аралаштирасизлар ва билиб туриб ҳақни яширасизлар'}, {'chapter': 3, 'verse': 72, 'text': 'وَقَالَت طَّآئِفَةٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ ءَامِنُواْ بِٱلَّذِيٓ أُنزِلَ عَلَى ٱلَّذِينَ ءَامَنُواْ وَجۡهَ ٱلنَّهَارِ وَٱكۡفُرُوٓاْ ءَاخِرَهُۥ لَعَلَّهُمۡ يَرۡجِعُونَ\nАҳли китоблардан бир тоифалари: «Иймон келтирганларга тушган нарсага куннинг аввалида иймон келтиринглар ва куннинг охирида куфр келтиринглар, шоядки, қайтсалар'}, {'chapter': 3, 'verse': 73, 'text': 'وَلَا تُؤۡمِنُوٓاْ إِلَّا لِمَن تَبِعَ دِينَكُمۡ قُلۡ إِنَّ ٱلۡهُدَىٰ هُدَى ٱللَّهِ أَن يُؤۡتَىٰٓ أَحَدٞ مِّثۡلَ مَآ أُوتِيتُمۡ أَوۡ يُحَآجُّوكُمۡ عِندَ رَبِّكُمۡۗ قُلۡ إِنَّ ٱلۡفَضۡلَ بِيَدِ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nВа динингизга эргашганлардан бошқаларга ишонманглар, токи бирортага сизга берилганга ўхшаш нарса берилмасин ёки Роббингиз ҳузурида сиз билан ҳужжат талашмасин», дерлар. Сен: «Албатта, ҳақиқий ҳидоят Аллоҳнинг ҳидоятидир», деб айт. Сен: «Албатта фазл Аллоҳнинг қўлида — хоҳлаган кишисига берадир. Аллоҳ кенг қамровли, билувчи Зотдир'}, {'chapter': 3, 'verse': 74, 'text': 'يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nЎз раҳматини хоҳлаган кишисига хос қиладир ва Аллоҳ улуғ фазл эгасидир», деб айт'}, {'chapter': 3, 'verse': 75, 'text': '۞وَمِنۡ أَهۡلِ ٱلۡكِتَٰبِ مَنۡ إِن تَأۡمَنۡهُ بِقِنطَارٖ يُؤَدِّهِۦٓ إِلَيۡكَ وَمِنۡهُم مَّنۡ إِن تَأۡمَنۡهُ بِدِينَارٖ لَّا يُؤَدِّهِۦٓ إِلَيۡكَ إِلَّا مَا دُمۡتَ عَلَيۡهِ قَآئِمٗاۗ ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَيۡسَ عَلَيۡنَا فِي ٱلۡأُمِّيِّـۧنَ سَبِيلٞ وَيَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ يَعۡلَمُونَ\nАҳли китоблардан, агар ҳисобсиз молни омонат қўйсанг, уни сенга адо қиладиганлари бор ва бир динорни омонат қўйсанг, устида туриб олмагунингча, сенга адо қилмайдиганлари ҳам бор. Бу уларнинг: омийлар учун бизнинг устимизга йўл йўқ, дейишларидандир. Улар билиб туриб Аллоҳ ҳақида ёлғон сўзларлар'}, {'chapter': 3, 'verse': 76, 'text': 'بَلَىٰۚ مَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ وَٱتَّقَىٰ فَإِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ\nЙўқ! Балки ким аҳдига вафо қилса ва тақводор бўлса. Бас, Аллоҳ тақводорларни хуш кўради'}, {'chapter': 3, 'verse': 77, 'text': 'إِنَّ ٱلَّذِينَ يَشۡتَرُونَ بِعَهۡدِ ٱللَّهِ وَأَيۡمَٰنِهِمۡ ثَمَنٗا قَلِيلًا أُوْلَـٰٓئِكَ لَا خَلَٰقَ لَهُمۡ فِي ٱلۡأٓخِرَةِ وَلَا يُكَلِّمُهُمُ ٱللَّهُ وَلَا يَنظُرُ إِلَيۡهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nАллоҳнинг аҳдини ва ўз қасамларини арзон баҳога сотадиганлар, албатта, ана ўшаларга охиратда насиба йўқдир, қиёмат куни Аллоҳ уларга гапирмас, назар солмас ва покламас. Уларга аламли азоблар бор'}, {'chapter': 3, 'verse': 78, 'text': 'وَإِنَّ مِنۡهُمۡ لَفَرِيقٗا يَلۡوُۥنَ أَلۡسِنَتَهُم بِٱلۡكِتَٰبِ لِتَحۡسَبُوهُ مِنَ ٱلۡكِتَٰبِ وَمَا هُوَ مِنَ ٱلۡكِتَٰبِ وَيَقُولُونَ هُوَ مِنۡ عِندِ ٱللَّهِ وَمَا هُوَ مِنۡ عِندِ ٱللَّهِۖ وَيَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ يَعۡلَمُونَ\nУлардан бир гуруҳлари борки, сизлар китобдан бўлмаган нарсани китобдан деб ўйлашингиз учун китобни тилларини буриб ўқирлар. Ва: «У Аллоҳнинг ҳузуридандир», дерлар. Ҳолбуки, у Аллоҳнинг ҳузуридан эмас. Улар билиб туриб, Аллоҳ ҳақида ёлғон сўзларлар'}, {'chapter': 3, 'verse': 79, 'text': 'مَا كَانَ لِبَشَرٍ أَن يُؤۡتِيَهُ ٱللَّهُ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُواْ عِبَادٗا لِّي مِن دُونِ ٱللَّهِ وَلَٰكِن كُونُواْ رَبَّـٰنِيِّـۧنَ بِمَا كُنتُمۡ تُعَلِّمُونَ ٱلۡكِتَٰبَ وَبِمَا كُنتُمۡ تَدۡرُسُونَ\nБир кишига Аллоҳ китобни, ҳукмни ва Пайғамбарликни берганидан сўнг, у одамларга: «Аллоҳни кўйиб менга банда бўлинглар», дейиши асло мумкин эмас. Лекин одамларга: «Китобни ўргатганингиз ва ўзингиз ўрганганингиз асосида Роббонийлар бўлинг», дейди'}, {'chapter': 3, 'verse': 80, 'text': 'وَلَا يَأۡمُرَكُمۡ أَن تَتَّخِذُواْ ٱلۡمَلَـٰٓئِكَةَ وَٱلنَّبِيِّـۧنَ أَرۡبَابًاۚ أَيَأۡمُرُكُم بِٱلۡكُفۡرِ بَعۡدَ إِذۡ أَنتُم مُّسۡلِمُونَ\nУ сизларни фаришталар ва Пайғамбарларни Робб қилиб олишингизга буюрмас. У сизни мусулмон бўлганингиздан кейин куфрга буюрармиди'}, {'chapter': 3, 'verse': 81, 'text': 'وَإِذۡ أَخَذَ ٱللَّهُ مِيثَٰقَ ٱلنَّبِيِّـۧنَ لَمَآ ءَاتَيۡتُكُم مِّن كِتَٰبٖ وَحِكۡمَةٖ ثُمَّ جَآءَكُمۡ رَسُولٞ مُّصَدِّقٞ لِّمَا مَعَكُمۡ لَتُؤۡمِنُنَّ بِهِۦ وَلَتَنصُرُنَّهُۥۚ قَالَ ءَأَقۡرَرۡتُمۡ وَأَخَذۡتُمۡ عَلَىٰ ذَٰلِكُمۡ إِصۡرِيۖ قَالُوٓاْ أَقۡرَرۡنَاۚ قَالَ فَٱشۡهَدُواْ وَأَنَا۠ مَعَكُم مِّنَ ٱلشَّـٰهِدِينَ\nАллоҳ Пайғамбарлардан: «Сизларга китоб ва ҳикмат берганим учун кетингиздан сизлардаги нарсани тасдиқловчи Пайғамбар келганда, албатта унга иймон келтирасиз ва ёрдам берасиз, — деб аҳду паймонларини олиб туриб: — Иқрор бўлдингизми? Бу ҳақда ишончли аҳду паймонимни қабул қилдингизми?» деганини эсла. Улар: «Иқрор бўлдик», – дейишди. У: «Бас, гувоҳ бўлинглар, мен ҳам гувоҳ бўлувчиларданман», – деди'}, {'chapter': 3, 'verse': 82, 'text': 'فَمَن تَوَلَّىٰ بَعۡدَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nКим бундан кейин юз ўгириб кетса, бас, ана ўшалар фосиқлардир'}, {'chapter': 3, 'verse': 83, 'text': 'أَفَغَيۡرَ دِينِ ٱللَّهِ يَبۡغُونَ وَلَهُۥٓ أَسۡلَمَ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَإِلَيۡهِ يُرۡجَعُونَ\nАллоҳнинг динидан бошқани хоҳлайдиларми?! Ахир хоҳласаю хоҳламаса, осмонлару ердаги барча жонзотлар Аллоҳга бўйсуниб турибди-ку!!! Ҳамда Унга қайтариладиларку'}, {'chapter': 3, 'verse': 84, 'text': 'قُلۡ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ عَلَيۡنَا وَمَآ أُنزِلَ عَلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَٱلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\nСен: «Аллоҳга ва бизга туширилган нарсага, Иброҳим, Исмоил, Исҳоқ, Яъқуб ва асботларга туширилган нарсага, Мусо, Ийсо ва бошқа Пайғамбарларга Роббиларидан берилган нарсага иймон келтирдик, улардан бирортасини ажратмаймиз ва биз Унгагина мусулмонмиз!» деб айт'}, {'chapter': 3, 'verse': 85, 'text': 'وَمَن يَبۡتَغِ غَيۡرَ ٱلۡإِسۡلَٰمِ دِينٗا فَلَن يُقۡبَلَ مِنۡهُ وَهُوَ فِي ٱلۡأٓخِرَةِ مِنَ ٱلۡخَٰسِرِينَ\nКим Исломдан бошқа динни хоҳласа, бас, ундан бу ҳаргиз қабул қилинмас ва у охиратда зиён кўргувчилардан бўлур'}, {'chapter': 3, 'verse': 86, 'text': 'كَيۡفَ يَهۡدِي ٱللَّهُ قَوۡمٗا كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ وَشَهِدُوٓاْ أَنَّ ٱلرَّسُولَ حَقّٞ وَجَآءَهُمُ ٱلۡبَيِّنَٰتُۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nИймонидан, Пайғамбарнинг ҳақлигига гувоҳлик берганидан ва уларга очиқ баёнотлар келганидан кейин куфр кетган қавмни Аллоҳ қандай қилиб, ҳидоятга солсин. Аллоҳ золим қавмларни ҳидоятга солмас'}, {'chapter': 3, 'verse': 87, 'text': 'أُوْلَـٰٓئِكَ جَزَآؤُهُمۡ أَنَّ عَلَيۡهِمۡ لَعۡنَةَ ٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلنَّاسِ أَجۡمَعِينَ\nАна ўшаларнинг жазоси уларга Аллоҳнинг, фаришталарнинг ва барча одамларнинг лаънати бўлишидир'}, {'chapter': 3, 'verse': 88, 'text': 'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ\nУнда абадий қолурлар. Улардан азоб енгиллатилмас ва уларга назар ҳам солинмас'}, {'chapter': 3, 'verse': 89, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nМагар шундоқ бўлгандан кейин тавба қилиб, яхши амалларни қилганлар бўлса, бас, Аллоҳ мағфират қилувчи ва раҳимлидир'}, {'chapter': 3, 'verse': 90, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ ثُمَّ ٱزۡدَادُواْ كُفۡرٗا لَّن تُقۡبَلَ تَوۡبَتُهُمۡ وَأُوْلَـٰٓئِكَ هُمُ ٱلضَّآلُّونَ\nАлбатта, иймонларидан кейин куфр келтириб, сўнгра куфри зиёда бўлганларнинг тавбаси ҳеч-ҳеч қабул қилинмас. Ана ўшалар адашганлардир'}, {'chapter': 3, 'verse': 91, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يُقۡبَلَ مِنۡ أَحَدِهِم مِّلۡءُ ٱلۡأَرۡضِ ذَهَبٗا وَلَوِ ٱفۡتَدَىٰ بِهِۦٓۗ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nАлбатта, куфр келтириб, кофир ҳолида ўлганларнинг бирортасидан, агар ер юзи тўла олтин фидо қилса ҳам, асло қабул қилинмас. Ана ўшаларга аламли азоб бор. Ва уларга ёрдамчилар йўқ'}, {'chapter': 3, 'verse': 92, 'text': 'لَن تَنَالُواْ ٱلۡبِرَّ حَتَّىٰ تُنفِقُواْ مِمَّا تُحِبُّونَۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٞ\nЎзингиз суйган нарсадан нафақа қилмагунингизча, ҳаргиз яхшиликка эриша олмассиз. Нимани нафақа қилганингизни Аллоҳ билувчидир'}, {'chapter': 3, 'verse': 93, 'text': '۞كُلُّ ٱلطَّعَامِ كَانَ حِلّٗا لِّبَنِيٓ إِسۡرَـٰٓءِيلَ إِلَّا مَا حَرَّمَ إِسۡرَـٰٓءِيلُ عَلَىٰ نَفۡسِهِۦ مِن قَبۡلِ أَن تُنَزَّلَ ٱلتَّوۡرَىٰةُۚ قُلۡ فَأۡتُواْ بِٱلتَّوۡرَىٰةِ فَٱتۡلُوهَآ إِن كُنتُمۡ صَٰدِقِينَ\nТаврот тушишидан олдин Исроил ўзига ҳаром қилгандан бошқа ҳамма таом Бани Исроилга ҳалол эди. «Агар ростгўйлардан бўлсангиз, Тавротни келтириб ўқинглар», деб айт'}, {'chapter': 3, 'verse': 94, 'text': 'فَمَنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ مِنۢ بَعۡدِ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nШундан кейин ҳам, кимки Аллоҳ ҳақида ёлғон тўқиса, бас, ана ўшалар золимлардир'}, {'chapter': 3, 'verse': 95, 'text': 'قُلۡ صَدَقَ ٱللَّهُۗ فَٱتَّبِعُواْ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nАллоҳ рост айтди. Иброҳимнинг йўлдан тоймаган миллатига эргашинг. У мушриклардан бўлмаган эди'}, {'chapter': 3, 'verse': 96, 'text': 'إِنَّ أَوَّلَ بَيۡتٖ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبَارَكٗا وَهُدٗى لِّلۡعَٰلَمِينَ\nОдамларга муборак, оламларга ҳидоят қилиб қўйилган биринчи уй Баккадаги уйдир'}, {'chapter': 3, 'verse': 97, 'text': 'فِيهِ ءَايَٰتُۢ بَيِّنَٰتٞ مَّقَامُ إِبۡرَٰهِيمَۖ وَمَن دَخَلَهُۥ كَانَ ءَامِنٗاۗ وَلِلَّهِ عَلَى ٱلنَّاسِ حِجُّ ٱلۡبَيۡتِ مَنِ ٱسۡتَطَاعَ إِلَيۡهِ سَبِيلٗاۚ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ\nУнда очиқ аломатлар ва мақоми Иброҳим бор. Ким унга кирса, тинч бўладир. Одамлардан йўлини топганларига Аллоҳ учун Байтни ҳаж қилмоқ бурчдир. Кимки куфр келтирса, Аллоҳ оламлардан беҳожатдир'}, {'chapter': 3, 'verse': 98, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَٱللَّهُ شَهِيدٌ عَلَىٰ مَا تَعۡمَلُونَ\nЭй, аҳли китоблар, нега Аллоҳнинг оятларига куфр келтирасизлар?! Ҳолбуки, Аллоҳ қилаётганларингизга гувоҳ-ку'}, {'chapter': 3, 'verse': 99, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَصُدُّونَ عَن سَبِيلِ ٱللَّهِ مَنۡ ءَامَنَ تَبۡغُونَهَا عِوَجٗا وَأَنتُمۡ شُهَدَآءُۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nЭй, аҳли китоблар! Нега иймон келтирганларни Аллоҳнинг йўлидан тўсиб, қайтарасиз?! Ўзингиз шоҳид бўлиб туриб, у(йўл)нинг қинғир бўлишини хоҳлайсиз! Аллоҳ қилаётган ишларингиздан ғофил эмасдир'}, {'chapter': 3, 'verse': 100, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ فَرِيقٗا مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ يَرُدُّوكُم بَعۡدَ إِيمَٰنِكُمۡ كَٰفِرِينَ\nЭй, иймон келтирганлар! Агар китоб берилганлар тоифасига итоат қилсангиз, иймонингиздан кейин сизни кофирликка қайтарадир'}, {'chapter': 3, 'verse': 101, 'text': 'وَكَيۡفَ تَكۡفُرُونَ وَأَنتُمۡ تُتۡلَىٰ عَلَيۡكُمۡ ءَايَٰتُ ٱللَّهِ وَفِيكُمۡ رَسُولُهُۥۗ وَمَن يَعۡتَصِم بِٱللَّهِ فَقَدۡ هُدِيَ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳнинг оятлари сизга тиловат қилиниб турган бўлса-ю, Унинг Расули ичингизда бўлса-ю, қандай қилиб куфр келтирасиз?! Ким Аллоҳни маҳкам тутса, шубҳасиз, тўғри йўлга ҳидоят топган бўладир'}, {'chapter': 3, 'verse': 102, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ حَقَّ تُقَاتِهِۦ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ\nЭй, иймон келтирганлар! Аллоҳга ҳақиқий тақво ила тақво қилинг. Ва фақат мусулмон ҳолингиздагина бу дунёдан ўтинг'}, {'chapter': 3, 'verse': 103, 'text': 'وَٱعۡتَصِمُواْ بِحَبۡلِ ٱللَّهِ جَمِيعٗا وَلَا تَفَرَّقُواْۚ وَٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ كُنتُمۡ أَعۡدَآءٗ فَأَلَّفَ بَيۡنَ قُلُوبِكُمۡ فَأَصۡبَحۡتُم بِنِعۡمَتِهِۦٓ إِخۡوَٰنٗا وَكُنتُمۡ عَلَىٰ شَفَا حُفۡرَةٖ مِّنَ ٱلنَّارِ فَأَنقَذَكُم مِّنۡهَاۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَهۡتَدُونَ\nБарчангиз Аллоҳнинг арқонини маҳкам тутинг ва бўлиниб кетманг. Ва Аллоҳнинг сизга берган неъматини эсланг: бир вақтлар душман эдингиз, бас, қалбларингизни улфат қилди. Унинг неъмати ила биродар бўлдингиз. Оловли жар ёқасида эдингиз, ундан сизни қутқарди. Шундай қилиб, Аллоҳ сизга Ўз оятларини баён қилади. Шоядки, ҳидоят топсангиз'}, {'chapter': 3, 'verse': 104, 'text': 'وَلۡتَكُن مِّنكُمۡ أُمَّةٞ يَدۡعُونَ إِلَى ٱلۡخَيۡرِ وَيَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nСизлардан яхшиликка чақирадиган, амру маъруф, наҳйу мункар қиладиган бир уммат бўлсин. Ана ўшалар, ўзлари нажот топгувчилардир'}, {'chapter': 3, 'verse': 105, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ تَفَرَّقُواْ وَٱخۡتَلَفُواْ مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡبَيِّنَٰتُۚ وَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ عَظِيمٞ\nЎзларига очиқ баёнотлар келганидан кейин бўлиниб, ихтилофга тушганларга ўхшаш бўлманглар, ана ўшаларга улуғ азоб бордир'}, {'chapter': 3, 'verse': 106, 'text': 'يَوۡمَ تَبۡيَضُّ وُجُوهٞ وَتَسۡوَدُّ وُجُوهٞۚ فَأَمَّا ٱلَّذِينَ ٱسۡوَدَّتۡ وُجُوهُهُمۡ أَكَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nЮзлар оқ ва юзлар қора бўладиган кунда юзлари қора бўлганларга: «Иймонингиздан кейин кофир бўлдингизми?! Куфр келтирган бўлганингиз учун азобни татиб кўринг», дейилур'}, {'chapter': 3, 'verse': 107, 'text': 'وَأَمَّا ٱلَّذِينَ ٱبۡيَضَّتۡ وُجُوهُهُمۡ فَفِي رَحۡمَةِ ٱللَّهِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЮзлари оқ бўлганлар эса, бас, Аллоҳнинг раҳматидадирлар. Улар унда абадий қолурлар'}, {'chapter': 3, 'verse': 108, 'text': 'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۗ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعَٰلَمِينَ\nБулар Аллоҳнинг оятларидир. Уларни сенга ҳақ ила тиловат қилмоқдамиз. Аллоҳ одамларга зулмни хоҳламас'}, {'chapter': 3, 'verse': 109, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nОсмонлару ердаги нарсалар Аллоҳникидир. Ишлар Аллоҳга қайтарилажак'}, {'chapter': 3, 'verse': 110, 'text': 'كُنتُمۡ خَيۡرَ أُمَّةٍ أُخۡرِجَتۡ لِلنَّاسِ تَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَتَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَتُؤۡمِنُونَ بِٱللَّهِۗ وَلَوۡ ءَامَنَ أَهۡلُ ٱلۡكِتَٰبِ لَكَانَ خَيۡرٗا لَّهُمۚ مِّنۡهُمُ ٱلۡمُؤۡمِنُونَ وَأَكۡثَرُهُمُ ٱلۡفَٰسِقُونَ\nСиз одамлар учун чиқарилган энг яхши уммат бўлдингиз. Амру маъруф қиласиз, наҳйу мункар қиласиз ва Аллоҳга иймон келтирасиз. Агар аҳли китоблар иймон келтирганларида, ўзларига яхши бўлар эди. Улардан мўминлари бор. Кўплари фосиқдирлар'}, {'chapter': 3, 'verse': 111, 'text': 'لَن يَضُرُّوكُمۡ إِلَّآ أَذٗىۖ وَإِن يُقَٰتِلُوكُمۡ يُوَلُّوكُمُ ٱلۡأَدۡبَارَ ثُمَّ لَا يُنصَرُونَ\nУлар сизларга озор беришдан ўзга ҳеч қандай зарар етказа олмаслар. Агар сизларга қарши уруш қилсалар, ортга қараб қочарлар. Сўнгра, уларга ёрдам берилмас'}, {'chapter': 3, 'verse': 112, 'text': 'ضُرِبَتۡ عَلَيۡهِمُ ٱلذِّلَّةُ أَيۡنَ مَا ثُقِفُوٓاْ إِلَّا بِحَبۡلٖ مِّنَ ٱللَّهِ وَحَبۡلٖ مِّنَ ٱلنَّاسِ وَبَآءُو بِغَضَبٖ مِّنَ ٱللَّهِ وَضُرِبَتۡ عَلَيۡهِمُ ٱلۡمَسۡكَنَةُۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ\nАллоҳ ипи бўлмаса ёки одамлар ипи бўлмаса, уларга қаерда бўлсалар ҳам, хорлик битилди. Уларга Аллоҳнинг ғазаби лозим бўлди ва мискинлик битилди. Бундай бўлишлиги уларнинг Аллоҳнинг оятларига куфр келтиришлари ва Пайғамбарларни ноҳақдан ўлдиришлари сабабидандир. Бундай бўлишлиги уларнинг осий бўлганлари ва ҳаддан ошганлари сабабидандир'}, {'chapter': 3, 'verse': 113, 'text': '۞لَيۡسُواْ سَوَآءٗۗ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ أُمَّةٞ قَآئِمَةٞ يَتۡلُونَ ءَايَٰتِ ٱللَّهِ ءَانَآءَ ٱلَّيۡلِ وَهُمۡ يَسۡجُدُونَ\nУлар баробар эмаслар. Аҳли китоблардан Аллоҳнинг оятларини сажда қилган ҳолларида кечасида тиловат қиладиган яхши тоифалари ҳам бор'}, {'chapter': 3, 'verse': 114, 'text': 'يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَيَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَيُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِۖ وَأُوْلَـٰٓئِكَ مِنَ ٱلصَّـٰلِحِينَ\nУлар Аллоҳга, охират кунига иймон келтирурлар, амру маъруф ва наҳйу мункар қилурлар ва яхшиликларга шошилурлар. Ана ўшалар аҳли солиҳлардандир'}, {'chapter': 3, 'verse': 115, 'text': 'وَمَا يَفۡعَلُواْ مِنۡ خَيۡرٖ فَلَن يُكۡفَرُوهُۗ وَٱللَّهُ عَلِيمُۢ بِٱلۡمُتَّقِينَ\nЯхшилик қилсалар, ҳаргиз инкор этилмаслар. Аллоҳ тақводорларни яхши билувчи Зотдир'}, {'chapter': 3, 'verse': 116, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـٔٗاۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nАлбатта, куфр келтирганлардан моллари ҳам, болалари ҳам ҳаргиз Аллоҳ азобидан ҳеч нарсани қайтара олмас. Ана ўшалар дўзах эгаларидир. Улар унда абадийдирлар'}, {'chapter': 3, 'verse': 117, 'text': 'مَثَلُ مَا يُنفِقُونَ فِي هَٰذِهِ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَثَلِ رِيحٖ فِيهَا صِرٌّ أَصَابَتۡ حَرۡثَ قَوۡمٖ ظَلَمُوٓاْ أَنفُسَهُمۡ فَأَهۡلَكَتۡهُۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَٰكِنۡ أَنفُسَهُمۡ يَظۡلِمُونَ\nУларнинг бу дунё ҳаётида қиладиган нафақалари мисоли ўзига зулм қилган бир қавмнинг экинини уриб ҳалок қилган совуқ шамолга ўхшайдир. Аллоҳ уларга зулм қилмади. Лекин улар ўзларига зулм қилдилар'}, {'chapter': 3, 'verse': 118, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ بِطَانَةٗ مِّن دُونِكُمۡ لَا يَأۡلُونَكُمۡ خَبَالٗا وَدُّواْ مَا عَنِتُّمۡ قَدۡ بَدَتِ ٱلۡبَغۡضَآءُ مِنۡ أَفۡوَٰهِهِمۡ وَمَا تُخۡفِي صُدُورُهُمۡ أَكۡبَرُۚ قَدۡ بَيَّنَّا لَكُمُ ٱلۡأٓيَٰتِۖ إِن كُنتُمۡ تَعۡقِلُونَ\nЭй, иймон келтирганлар! Ўзингиздан бошқадан сирдош тутманг. Улар сизга ёмонлик қилишини қўймас. Машаққатда қолишингизни хоҳлар. Душманликлари оғизларидаги(сўзлари)дан билиниб турибди. Дилларида махфий тургани яна ҳам каттароқ. Агар ақл юритадиган бўлсангиз, Биз сизларга белгиларини баён қилдик'}, {'chapter': 3, 'verse': 119, 'text': 'هَـٰٓأَنتُمۡ أُوْلَآءِ تُحِبُّونَهُمۡ وَلَا يُحِبُّونَكُمۡ وَتُؤۡمِنُونَ بِٱلۡكِتَٰبِ كُلِّهِۦ وَإِذَا لَقُوكُمۡ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ عَضُّواْ عَلَيۡكُمُ ٱلۡأَنَامِلَ مِنَ ٱلۡغَيۡظِۚ قُلۡ مُوتُواْ بِغَيۡظِكُمۡۗ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nҲой, сизлар! Уларни яхши кўрасизлар-у, улар сизни яхши кўрмаслар. Сизлар китобнинг ҳаммасига иймон келтирасизлар-у, улар сизларни кўрганда, иймон келтирдик, дерлар, аммо холи қолганларида, сизларга бўлган қаттиқ аччиқларидан бармоқ тишларлар. «Аччиқларинг билан ўлиб кетинглар», деб айт. Албатта, Аллоҳ дилларни эгаллаган нарсаларни билувчидир'}, {'chapter': 3, 'verse': 120, 'text': 'إِن تَمۡسَسۡكُمۡ حَسَنَةٞ تَسُؤۡهُمۡ وَإِن تُصِبۡكُمۡ سَيِّئَةٞ يَفۡرَحُواْ بِهَاۖ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ لَا يَضُرُّكُمۡ كَيۡدُهُمۡ شَيۡـًٔاۗ إِنَّ ٱللَّهَ بِمَا يَعۡمَلُونَ مُحِيطٞ\nСизга яхшилик тегса, уларга ёмон бўладир. Сизларга мусийбат етса, улар хурсанд бўларлар. Агар сабр ва тақво қилсангиз, уларнинг макри сизга ҳеч зарар қилмас. Албатта, Аллоҳ қилаётган ишларини иҳота қилгувчидир'}, {'chapter': 3, 'verse': 121, 'text': 'وَإِذۡ غَدَوۡتَ مِنۡ أَهۡلِكَ تُبَوِّئُ ٱلۡمُؤۡمِنِينَ مَقَٰعِدَ لِلۡقِتَالِۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\nАҳлингдан эрталаб чиқиб, мўминларни уруш жойларига турғизганингни эсла! Аллоҳ эшитувчи ва билувчи Зотдир'}, {'chapter': 3, 'verse': 122, 'text': 'إِذۡ هَمَّت طَّآئِفَتَانِ مِنكُمۡ أَن تَفۡشَلَا وَٱللَّهُ وَلِيُّهُمَاۗ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nСизлардан икки тоифа қўрқиб заифлашмоқчи бўлганини эсла! Аллоҳ уларнинг валийидир. Мўминлар фақат Аллоҳнинг Ўзига таваккал қилсинлар'}, {'chapter': 3, 'verse': 123, 'text': 'وَلَقَدۡ نَصَرَكُمُ ٱللَّهُ بِبَدۡرٖ وَأَنتُمۡ أَذِلَّةٞۖ فَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تَشۡكُرُونَ\nБатаҳқиқ, Бадрда оз бўлсаларингиз ҳам, Аллоҳ сизларга нусрат берди. Бас, Аллоҳга тақво қилинг, шоядки, шукр қилсангиз'}, {'chapter': 3, 'verse': 124, 'text': 'إِذۡ تَقُولُ لِلۡمُؤۡمِنِينَ أَلَن يَكۡفِيَكُمۡ أَن يُمِدَّكُمۡ رَبُّكُم بِثَلَٰثَةِ ءَالَٰفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُنزَلِينَ\nМўминларга: «Сизга Роббингиз туширилган уч минг фаришталар ила мадад бермоғи кифоя қилмасми?!» деганингни эсла'}, {'chapter': 3, 'verse': 125, 'text': 'بَلَىٰٓۚ إِن تَصۡبِرُواْ وَتَتَّقُواْ وَيَأۡتُوكُم مِّن فَوۡرِهِمۡ هَٰذَا يُمۡدِدۡكُمۡ رَبُّكُم بِخَمۡسَةِ ءَالَٰفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُسَوِّمِينَ\n«Ҳа (кифоя қиладир)! Агар сабр ва тақво қилсангиз, улар шу пайт келиб қолсалар ҳам, Роббингиз сизга белгили беш минг фаришта ила мадад берадир»'}, {'chapter': 3, 'verse': 126, 'text': 'وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ لَكُمۡ وَلِتَطۡمَئِنَّ قُلُوبُكُم بِهِۦۗ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nАллоҳ буни фақат сизга суюнчилик ва қалбингиз таскин топиши учунгина қилди. Аслида, нусрат Азийз ва Ҳакийм Аллоҳнинг ҳузуридандир'}, {'chapter': 3, 'verse': 127, 'text': 'لِيَقۡطَعَ طَرَفٗا مِّنَ ٱلَّذِينَ كَفَرُوٓاْ أَوۡ يَكۡبِتَهُمۡ فَيَنقَلِبُواْ خَآئِبِينَ\nКуфр келтирганларнинг бир тарафини кесиш учун ёки хорлаш учун, токи, ноумид бўлиб қайтсинлар'}, {'chapter': 3, 'verse': 128, 'text': 'لَيۡسَ لَكَ مِنَ ٱلۡأَمۡرِ شَيۡءٌ أَوۡ يَتُوبَ عَلَيۡهِمۡ أَوۡ يُعَذِّبَهُمۡ فَإِنَّهُمۡ ظَٰلِمُونَ\nБу сенинг ишинг эмас! У зот ёки уларнинг тавбасини қабул қилар, ёки азоблар. Бас, улар золимлардир'}, {'chapter': 3, 'verse': 129, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nОсмонлару ердаги нарсалар Аллоҳникидир. Хоҳлаган кишини мағфират қиладир ва хоҳлаган кишини азоблайдир. Аллоҳ гуноҳни мағфират қилувчи ва раҳимли Зотдир'}, {'chapter': 3, 'verse': 130, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَأۡكُلُواْ ٱلرِّبَوٰٓاْ أَضۡعَٰفٗا مُّضَٰعَفَةٗۖ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй, иймон келтирганлар! Рибони бир неча баробар қилиб еманг. Аллоҳга тақво қилинг, шоядки, нажот топсангиз'}, {'chapter': 3, 'verse': 131, 'text': 'وَٱتَّقُواْ ٱلنَّارَ ٱلَّتِيٓ أُعِدَّتۡ لِلۡكَٰفِرِينَ\nВа кофирлар учун тайёрлаб қўйилган оловдан қўрқинг'}, {'chapter': 3, 'verse': 132, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ\nАллоҳга ва Расулига итоат этинг. Шоядки, раҳм қилинсангиз'}, {'chapter': 3, 'verse': 133, 'text': '۞وَسَارِعُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ أُعِدَّتۡ لِلۡمُتَّقِينَ\nРоббингиздан бўлган мағфиратга ва кенглиги осмонлару ерча бўлган, тақводорлар учун тайёрлаб қўйилган жаннатга шошилинг'}, {'chapter': 3, 'verse': 134, 'text': 'ٱلَّذِينَ يُنفِقُونَ فِي ٱلسَّرَّآءِ وَٱلضَّرَّآءِ وَٱلۡكَٰظِمِينَ ٱلۡغَيۡظَ وَٱلۡعَافِينَ عَنِ ٱلنَّاسِۗ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ\nУлар енгилликда ҳам, оғирликда ҳам нафақа қиладиганлар, ғазабини ютадиганлар ва одамларни афв қиладиганлардир. Аллоҳ яхшилик қилувчиларни ёқтирадир'}, {'chapter': 3, 'verse': 135, 'text': 'وَٱلَّذِينَ إِذَا فَعَلُواْ فَٰحِشَةً أَوۡ ظَلَمُوٓاْ أَنفُسَهُمۡ ذَكَرُواْ ٱللَّهَ فَٱسۡتَغۡفَرُواْ لِذُنُوبِهِمۡ وَمَن يَغۡفِرُ ٱلذُّنُوبَ إِلَّا ٱللَّهُ وَلَمۡ يُصِرُّواْ عَلَىٰ مَا فَعَلُواْ وَهُمۡ يَعۡلَمُونَ\nВа улар фоҳиша иш ёки ўзларига зулм қилган чоғларида Аллоҳни эслаб, гуноҳларини мағфират қилишни сўрарлар, гуноҳларини Аллоҳдан ўзга ким ҳам мағфират қиларди? Ва улар билиб туриб, қилган гуноҳларида бардавом бўлмаслар'}, {'chapter': 3, 'verse': 136, 'text': 'أُوْلَـٰٓئِكَ جَزَآؤُهُم مَّغۡفِرَةٞ مِّن رَّبِّهِمۡ وَجَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَنِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ\nАна ўшаларнинг мукофотлари — Роббиларидан бўлган мағфират ва остидан анҳорлар оқиб турган жаннатлар бўлиб, улар унда абадий қолурлар. Амал қилувчиларнинг ажри қандай ҳам яхши'}, {'chapter': 3, 'verse': 137, 'text': 'قَدۡ خَلَتۡ مِن قَبۡلِكُمۡ سُنَنٞ فَسِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\nСиздан олдин суннатлар қолган. Бас, ер юзида юриб ёлғонга чиқарувчиларнинг оқибати қандай бўлганига назар солинглар'}, {'chapter': 3, 'verse': 138, 'text': 'هَٰذَا بَيَانٞ لِّلنَّاسِ وَهُدٗى وَمَوۡعِظَةٞ لِّلۡمُتَّقِينَ\nБу, одамлар учун баён ва тақводорлар учун ҳидоят ва мавъизадир'}, {'chapter': 3, 'verse': 139, 'text': 'وَلَا تَهِنُواْ وَلَا تَحۡزَنُواْ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ إِن كُنتُم مُّؤۡمِنِينَ\nБўшашманглар! Хафа бўлманглар! Сизлар, агар мўмин бўлсаларингиз, устунсизлар'}, {'chapter': 3, 'verse': 140, 'text': 'إِن يَمۡسَسۡكُمۡ قَرۡحٞ فَقَدۡ مَسَّ ٱلۡقَوۡمَ قَرۡحٞ مِّثۡلُهُۥۚ وَتِلۡكَ ٱلۡأَيَّامُ نُدَاوِلُهَا بَيۡنَ ٱلنَّاسِ وَلِيَعۡلَمَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَيَتَّخِذَ مِنكُمۡ شُهَدَآءَۗ وَٱللَّهُ لَا يُحِبُّ ٱلظَّـٰلِمِينَ\nАгар сизга жароҳат етган бўлса, у қавмга ҳам шунга ўхшаш жароҳат етган. Бундай кунларни одамлар орасида айлантириб турамиз. Токи, Аллоҳ иймон келтирганларни билиши учун ва улардан шаҳидларни чиқариб олиши учун. Ва Аллоҳ золимларни севмас'}, {'chapter': 3, 'verse': 141, 'text': 'وَلِيُمَحِّصَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَيَمۡحَقَ ٱلۡكَٰفِرِينَ\nТоки, Аллоҳ мўминларни поклаши ва кофирларни ҳалок қилиши учун'}, {'chapter': 3, 'verse': 142, 'text': 'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ ٱلۡجَنَّةَ وَلَمَّا يَعۡلَمِ ٱللَّهُ ٱلَّذِينَ جَٰهَدُواْ مِنكُمۡ وَيَعۡلَمَ ٱلصَّـٰبِرِينَ\nЁки Аллоҳ сизлардан жиҳод қилганларни билмасдан ва сабрлиларни билмасдан туриб, жаннатга кираверамиз, деб ҳисоблайсизми'}, {'chapter': 3, 'verse': 143, 'text': 'وَلَقَدۡ كُنتُمۡ تَمَنَّوۡنَ ٱلۡمَوۡتَ مِن قَبۡلِ أَن تَلۡقَوۡهُ فَقَدۡ رَأَيۡتُمُوهُ وَأَنتُمۡ تَنظُرُونَ\nДучор бўлмасингиздан олдин ўлимни орзу қилган эдингиз. Энди уни кўриб, назар солиб турибсиз'}, {'chapter': 3, 'verse': 144, 'text': 'وَمَا مُحَمَّدٌ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُۚ أَفَإِيْن مَّاتَ أَوۡ قُتِلَ ٱنقَلَبۡتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡۚ وَمَن يَنقَلِبۡ عَلَىٰ عَقِبَيۡهِ فَلَن يَضُرَّ ٱللَّهَ شَيۡـٔٗاۚ وَسَيَجۡزِي ٱللَّهُ ٱلشَّـٰكِرِينَ\nМуҳаммад ҳам бир Пайғамбар, холос. Ундан аввал ҳам Пайғамбарлар ўтган. Агар у ўлса ёки қатл қилинса, орқангизга қайтасизми?! Кимки орқасига қайтса, Аллоҳга ҳеч зарар келтира олмас. Ва Аллоҳ шукр қилувчиларни мукофотлар'}, {'chapter': 3, 'verse': 145, 'text': 'وَمَا كَانَ لِنَفۡسٍ أَن تَمُوتَ إِلَّا بِإِذۡنِ ٱللَّهِ كِتَٰبٗا مُّؤَجَّلٗاۗ وَمَن يُرِدۡ ثَوَابَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَن يُرِدۡ ثَوَابَ ٱلۡأٓخِرَةِ نُؤۡتِهِۦ مِنۡهَاۚ وَسَنَجۡزِي ٱلشَّـٰكِرِينَ\nҲеч бир жон Аллоҳнинг изнисиз ўлмас. Бу, ёзилган ажалдир. Ким бу дунёнинг савобини хоҳласа, унга ўшандан берурмиз. Ким охиратнинг савобини хоҳласа, унга ўшандан берурмиз. Ва шукр қилувчиларни мукофотлармиз'}, {'chapter': 3, 'verse': 146, 'text': 'وَكَأَيِّن مِّن نَّبِيّٖ قَٰتَلَ مَعَهُۥ رِبِّيُّونَ كَثِيرٞ فَمَا وَهَنُواْ لِمَآ أَصَابَهُمۡ فِي سَبِيلِ ٱللَّهِ وَمَا ضَعُفُواْ وَمَا ٱسۡتَكَانُواْۗ وَٱللَّهُ يُحِبُّ ٱلصَّـٰبِرِينَ\nҚанчадан-қанча Пайғамбарлар билан бирга кўплаб роббонийлар жанг қилганлар. Улар Аллоҳ йўлида етган мусийбатдан бўшашмаганлар, заифлашмаганлар ва бўйсунмаганлар. Аллоҳ сабрлиларни севадир'}, {'chapter': 3, 'verse': 147, 'text': 'وَمَا كَانَ قَوۡلَهُمۡ إِلَّآ أَن قَالُواْ رَبَّنَا ٱغۡفِرۡ لَنَا ذُنُوبَنَا وَإِسۡرَافَنَا فِيٓ أَمۡرِنَا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nУлар: «Эй Роббимиз! Бизнинг гуноҳларимиз ва ишимиздаги исрофимизни Ўзинг кечиргин, собитқадам қилгин ва кофир қавмлар устидан бизга нусрат бергин», деган гапдан ўзга гапни айтмаганлар'}, {'chapter': 3, 'verse': 148, 'text': 'فَـَٔاتَىٰهُمُ ٱللَّهُ ثَوَابَ ٱلدُّنۡيَا وَحُسۡنَ ثَوَابِ ٱلۡأٓخِرَةِۗ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ\nБас, Аллоҳ уларга бу дунё савобини ва охиратнинг гўзал савобини берди. Аллоҳ гўзал амал қилувчиларни севадир'}, {'chapter': 3, 'verse': 149, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ ٱلَّذِينَ كَفَرُواْ يَرُدُّوكُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ\nЭй, иймон келтирганлар! Агар куфр келтирганларга итоат қилсангиз, орқангизга қайтарурлар. Бас, зиён кўргувчиларга айланиб қолурсиз'}, {'chapter': 3, 'verse': 150, 'text': 'بَلِ ٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ خَيۡرُ ٱلنَّـٰصِرِينَ\nЙўқ! Аллоҳ хўжангиздир. Ва У ёрдам берувчиларнинг яхшисидир'}, {'chapter': 3, 'verse': 151, 'text': 'سَنُلۡقِي فِي قُلُوبِ ٱلَّذِينَ كَفَرُواْ ٱلرُّعۡبَ بِمَآ أَشۡرَكُواْ بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗاۖ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَبِئۡسَ مَثۡوَى ٱلظَّـٰلِمِينَ\nТезда, куфр келтирганларнинг қалбига, Аллоҳга ҳужжатсиз нарсани ширк келтирганлари учун, қўрқувни солурмиз. Уларнинг қароргоҳи дўзахдир. Золимларнинг жойи қандай ҳам ёмон'}, {'chapter': 3, 'verse': 152, 'text': 'وَلَقَدۡ صَدَقَكُمُ ٱللَّهُ وَعۡدَهُۥٓ إِذۡ تَحُسُّونَهُم بِإِذۡنِهِۦۖ حَتَّىٰٓ إِذَا فَشِلۡتُمۡ وَتَنَٰزَعۡتُمۡ فِي ٱلۡأَمۡرِ وَعَصَيۡتُم مِّنۢ بَعۡدِ مَآ أَرَىٰكُم مَّا تُحِبُّونَۚ مِنكُم مَّن يُرِيدُ ٱلدُّنۡيَا وَمِنكُم مَّن يُرِيدُ ٱلۡأٓخِرَةَۚ ثُمَّ صَرَفَكُمۡ عَنۡهُمۡ لِيَبۡتَلِيَكُمۡۖ وَلَقَدۡ عَفَا عَنكُمۡۗ وَٱللَّهُ ذُو فَضۡلٍ عَلَى ٱلۡمُؤۡمِنِينَ\nБатаҳқиқ, Аллоҳ ваъдаси устидан чиқди. Сиз Унинг изни билан уларни қира бошладингиз. Токи заифлашиб, амр ҳақида ихтилоф қилиб ва сиз суйган нарсани кўрсатганидан сўнг, исён қилгунингизча. Орангизда дунёни хоҳлайдиган кимсалар бор ва орангизда охиратни хоҳлайдиган кимсалар бор. Сўнгра, сизларни имтиҳон қилиш учун улардан бурди. Батаҳқиқ, сизни афв этди. Аллоҳ мўминларга фазл эгаси бўлган Зотдир'}, {'chapter': 3, 'verse': 153, 'text': '۞إِذۡ تُصۡعِدُونَ وَلَا تَلۡوُۥنَ عَلَىٰٓ أَحَدٖ وَٱلرَّسُولُ يَدۡعُوكُمۡ فِيٓ أُخۡرَىٰكُمۡ فَأَثَٰبَكُمۡ غَمَّۢا بِغَمّٖ لِّكَيۡلَا تَحۡزَنُواْ عَلَىٰ مَا فَاتَكُمۡ وَلَا مَآ أَصَٰبَكُمۡۗ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nПайғамбар ортингиздан чақириб турса ҳам ҳеч кимга қарамай тирқираб қочганингизни эсланг. Ўтган нарсага ва етган мусийбатга хафа бўлмаслигингиз учун сизга ғам устига ғам берди. Аллоҳ қилаётган амалларингиздан хабардор Зотдир'}, {'chapter': 3, 'verse': 154, 'text': 'ثُمَّ أَنزَلَ عَلَيۡكُم مِّنۢ بَعۡدِ ٱلۡغَمِّ أَمَنَةٗ نُّعَاسٗا يَغۡشَىٰ طَآئِفَةٗ مِّنكُمۡۖ وَطَآئِفَةٞ قَدۡ أَهَمَّتۡهُمۡ أَنفُسُهُمۡ يَظُنُّونَ بِٱللَّهِ غَيۡرَ ٱلۡحَقِّ ظَنَّ ٱلۡجَٰهِلِيَّةِۖ يَقُولُونَ هَل لَّنَا مِنَ ٱلۡأَمۡرِ مِن شَيۡءٖۗ قُلۡ إِنَّ ٱلۡأَمۡرَ كُلَّهُۥ لِلَّهِۗ يُخۡفُونَ فِيٓ أَنفُسِهِم مَّا لَا يُبۡدُونَ لَكَۖ يَقُولُونَ لَوۡ كَانَ لَنَا مِنَ ٱلۡأَمۡرِ شَيۡءٞ مَّا قُتِلۡنَا هَٰهُنَاۗ قُل لَّوۡ كُنتُمۡ فِي بُيُوتِكُمۡ لَبَرَزَ ٱلَّذِينَ كُتِبَ عَلَيۡهِمُ ٱلۡقَتۡلُ إِلَىٰ مَضَاجِعِهِمۡۖ وَلِيَبۡتَلِيَ ٱللَّهُ مَا فِي صُدُورِكُمۡ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمۡۚ وَٱللَّهُ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nСўнгра, ғамдан кейин сиздан бир тоифангизни қамраб олган мудроқни омонлик қилиб туширди. Бошқа тоифа эса, ўзи билан овора бўлиб, Аллоҳ ҳақида ноҳақ, жоҳилият гумонини қилиб: «Бизнинг қўлимизда нима иш бор эди», дейишди. Сен: «Ишнинг барчаси Аллоҳдандир», деб айт! Сенга ошкор қилмаган нарсаларини ичларида махфий тутарлар. Улар: «Агар қўлимизда бир иш бўлганида, бу ерда қатл қилинмас эдик», дерлар. Сен: «Агар уйларингизда бўлсангиз ҳам, қатл бўлиш тақдирида ёзилганлар ўз ўлим жойларига чиқар эдилар. Бу, Аллоҳ кўксингиздаги нарсани синаши ва қалбингиздагини яхшилаб поклаши учундир. Аллоҳ кўксингиздаги нарсани билувчи Зотдир», деб айт'}, {'chapter': 3, 'verse': 155, 'text': 'إِنَّ ٱلَّذِينَ تَوَلَّوۡاْ مِنكُمۡ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ إِنَّمَا ٱسۡتَزَلَّهُمُ ٱلشَّيۡطَٰنُ بِبَعۡضِ مَا كَسَبُواْۖ وَلَقَدۡ عَفَا ٱللَّهُ عَنۡهُمۡۗ إِنَّ ٱللَّهَ غَفُورٌ حَلِيمٞ\nИкки тўп тўқнашган куни сизлардан юз ўгириб қочганларни баъзи қилган ишлари туфайли шайтон йўлдан урди, холос. Батаҳқиқ, Аллоҳ уларни афв қилди. Албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 3, 'verse': 156, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَٱلَّذِينَ كَفَرُواْ وَقَالُواْ لِإِخۡوَٰنِهِمۡ إِذَا ضَرَبُواْ فِي ٱلۡأَرۡضِ أَوۡ كَانُواْ غُزّٗى لَّوۡ كَانُواْ عِندَنَا مَا مَاتُواْ وَمَا قُتِلُواْ لِيَجۡعَلَ ٱللَّهُ ذَٰلِكَ حَسۡرَةٗ فِي قُلُوبِهِمۡۗ وَٱللَّهُ يُحۡيِۦ وَيُمِيتُۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nЭй, иймон келтирганлар! Куфр келтирганларга ва ер юзида юрган ва ғозий бўлган биродарлари ҳақида, агар бизнинг ҳузуримизда бўлганларида ўлмас эдилар ва қатл қилинмас эдилар, дейдиганларга ўхшаманг. Буни Аллоҳ қалбларида ҳасрат қилиши учун айтурлар. Аллоҳ тирилтирур ва ўлдирур. Аллоҳ қилган амалларингизни кўрувчи Зотдир'}, {'chapter': 3, 'verse': 157, 'text': 'وَلَئِن قُتِلۡتُمۡ فِي سَبِيلِ ٱللَّهِ أَوۡ مُتُّمۡ لَمَغۡفِرَةٞ مِّنَ ٱللَّهِ وَرَحۡمَةٌ خَيۡرٞ مِّمَّا يَجۡمَعُونَ\nГарчи Аллоҳ йўлида қатл қилинсангиз ҳам ёки ўлсангиз ҳам, Аллоҳдан бўладиган мағфират ва раҳмат улар жамлайдиган нарсадан яхшидир'}, {'chapter': 3, 'verse': 158, 'text': 'وَلَئِن مُّتُّمۡ أَوۡ قُتِلۡتُمۡ لَإِلَى ٱللَّهِ تُحۡشَرُونَ\nГарчи ўлсангиз ҳам ёки қатл қилинсангиз ҳам, албатта, Аллоҳ ҳузурида йиғилурсиз'}, {'chapter': 3, 'verse': 159, 'text': 'فَبِمَا رَحۡمَةٖ مِّنَ ٱللَّهِ لِنتَ لَهُمۡۖ وَلَوۡ كُنتَ فَظًّا غَلِيظَ ٱلۡقَلۡبِ لَٱنفَضُّواْ مِنۡ حَوۡلِكَۖ فَٱعۡفُ عَنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمۡ وَشَاوِرۡهُمۡ فِي ٱلۡأَمۡرِۖ فَإِذَا عَزَمۡتَ فَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَوَكِّلِينَ\nАллоҳнинг раҳмати ила уларга мулойим бўлдинг. Агар қўпол, қалби қаттиқ бўлганингда, атрофингдан тарқаб кетар эдилар. Бас, уларни афв эт. Уларга истиғфор айт ва улар билан ишларда машварат қил. Азму қарор қилганингдан сўнг Аллоҳга таваккал қил. Албатта, Аллоҳ таваккал қилувчиларни севадир'}, {'chapter': 3, 'verse': 160, 'text': 'إِن يَنصُرۡكُمُ ٱللَّهُ فَلَا غَالِبَ لَكُمۡۖ وَإِن يَخۡذُلۡكُمۡ فَمَن ذَا ٱلَّذِي يَنصُرُكُم مِّنۢ بَعۡدِهِۦۗ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nАгар сизга Аллоҳ нусрат берса, сиздан ғолиб келувчи йўқ. Агар сизни ташлаб қўйса, Ундан ўзга ким ҳам ёрдам берар эди. Мўминлар фақат Аллоҳгагина таваккал қилсинлар'}, {'chapter': 3, 'verse': 161, 'text': 'وَمَا كَانَ لِنَبِيٍّ أَن يَغُلَّۚ وَمَن يَغۡلُلۡ يَأۡتِ بِمَا غَلَّ يَوۡمَ ٱلۡقِيَٰمَةِۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nПайғамбарга хиёнат ҳеч тўғри келмас. Кимки хиёнат қилса, қиёмат куни хиёнат қилиб олган нарсаси билан келадир. Сўнгра, ҳар бир жон касбига яраша тўлиқ оладир. Уларга зулм қилинмас'}, {'chapter': 3, 'verse': 162, 'text': 'أَفَمَنِ ٱتَّبَعَ رِضۡوَٰنَ ٱللَّهِ كَمَنۢ بَآءَ بِسَخَطٖ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nАллоҳнинг розилигини излаган одам Аллоҳнинг ғазабига дучор бўлган ва борар жойи жаҳаннам бўлган одамга ўхшармиди! Ва у қандай ҳам ёмон жой'}, {'chapter': 3, 'verse': 163, 'text': 'هُمۡ دَرَجَٰتٌ عِندَ ٱللَّهِۗ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ\nУларнинг Аллоҳнинг ҳузуридаги даражалари турлича. Аллоҳ қилаётган амалларини кўриб турувчи Зотдир'}, {'chapter': 3, 'verse': 164, 'text': 'لَقَدۡ مَنَّ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ إِذۡ بَعَثَ فِيهِمۡ رَسُولٗا مِّنۡ أَنفُسِهِمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٍ\nБатаҳқиқ, Аллоҳ мўминларга ўзларидан Пайғамбар юбориб, неъмат берди. У уларга Аллоҳнинг оятларини тиловат қилиб берадир, уларни поклайдир, китоб ва ҳикматни ўргатур. Гарчи олдин очиқ-ойдин адашувда бўлсалар ҳам'}, {'chapter': 3, 'verse': 165, 'text': 'أَوَلَمَّآ أَصَٰبَتۡكُم مُّصِيبَةٞ قَدۡ أَصَبۡتُم مِّثۡلَيۡهَا قُلۡتُمۡ أَنَّىٰ هَٰذَاۖ قُلۡ هُوَ مِنۡ عِندِ أَنفُسِكُمۡۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nСен: «Сизга мусийбат етган пайтда, ҳолбуки, ўзингиз ҳам икки бараварини етказган эдингиз, бу қаердан, дейсизми?! Бу ўзингизнинг ҳузурингиздан!» деб айт. Албатта, Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 3, 'verse': 166, 'text': 'وَمَآ أَصَٰبَكُمۡ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ فَبِإِذۡنِ ٱللَّهِ وَلِيَعۡلَمَ ٱلۡمُؤۡمِنِينَ\nИкки жамоа тўқнашган куни сизга мусийбат Аллоҳнинг изни билан ва мўминларни билиш учун'}, {'chapter': 3, 'verse': 167, 'text': 'وَلِيَعۡلَمَ ٱلَّذِينَ نَافَقُواْۚ وَقِيلَ لَهُمۡ تَعَالَوۡاْ قَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ أَوِ ٱدۡفَعُواْۖ قَالُواْ لَوۡ نَعۡلَمُ قِتَالٗا لَّٱتَّبَعۡنَٰكُمۡۗ هُمۡ لِلۡكُفۡرِ يَوۡمَئِذٍ أَقۡرَبُ مِنۡهُمۡ لِلۡإِيمَٰنِۚ يَقُولُونَ بِأَفۡوَٰهِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ وَٱللَّهُ أَعۡلَمُ بِمَا يَكۡتُمُونَ\nВа мунофиқлик қилаётганларни билиш учун етди. Ва уларга: «Келинглар, Аллоҳнинг йўлида уруш қилинглар ёки мудофаа қилинглар», дейилди. Улар: «Агар жанг бўлишини аниқ билсак, сизга эргашардик», – дедилар. Улар ўша кунда иймондан кўра куфрга яқинроқ эдилар. Тиллари билан дилларида йўқ нарсани айтурлар. Аллоҳ беркитаётган нарсаларини яхши билувчи Зотдир'}, {'chapter': 3, 'verse': 168, 'text': 'ٱلَّذِينَ قَالُواْ لِإِخۡوَٰنِهِمۡ وَقَعَدُواْ لَوۡ أَطَاعُونَا مَا قُتِلُواْۗ قُلۡ فَٱدۡرَءُواْ عَنۡ أَنفُسِكُمُ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ\nУлар ўз биродарларига ва (урушга чиқмай) ўтирганларга: «Агар бизга итоат қилганларида қатл қилинмас эдилар», – дейишди. Сен: «Агар гапингиз рост бўлса, ўзингиздан ўлимни қайтариб кўринг-чи», деб айт'}, {'chapter': 3, 'verse': 169, 'text': 'وَلَا تَحۡسَبَنَّ ٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ أَمۡوَٰتَۢاۚ بَلۡ أَحۡيَآءٌ عِندَ رَبِّهِمۡ يُرۡزَقُونَ\nАллоҳнинг йўлида қатл бўлганларни ўликлар деб ҳисобламанглар. Балки, улар — тирик, Роббилари ҳузурида ризқланмоқдалар'}, {'chapter': 3, 'verse': 170, 'text': 'فَرِحِينَ بِمَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ وَيَسۡتَبۡشِرُونَ بِٱلَّذِينَ لَمۡ يَلۡحَقُواْ بِهِم مِّنۡ خَلۡفِهِمۡ أَلَّا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nУлар Аллоҳ Ўз фазлидан берган нарсалардан хурсандлар ва ҳали ортларидан ўзларига қўшилмаган биродарларига, уларга хавф йўқлигининг ҳамда хафа ҳам бўлмасликларининг хушхабарини бермоқдалар'}, {'chapter': 3, 'verse': 171, 'text': '۞يَسۡتَبۡشِرُونَ بِنِعۡمَةٖ مِّنَ ٱللَّهِ وَفَضۡلٖ وَأَنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُؤۡمِنِينَ\nУлар Аллоҳдан бўлган неъмат ва фазлнинг ҳамда, албатта, Аллоҳ мўминларнинг ажрини зое қилмаслигининг хушхабарини бермоқдалар'}, {'chapter': 3, 'verse': 172, 'text': 'ٱلَّذِينَ ٱسۡتَجَابُواْ لِلَّهِ وَٱلرَّسُولِ مِنۢ بَعۡدِ مَآ أَصَابَهُمُ ٱلۡقَرۡحُۚ لِلَّذِينَ أَحۡسَنُواْ مِنۡهُمۡ وَٱتَّقَوۡاْ أَجۡرٌ عَظِيمٌ\nУлар жароҳат етгандан кейин ҳам Аллоҳ ва Пайғамбар чақириғига жавоб берадиганлардир. Улардан яхшилик ва тақво қилганларига улуғ ажрлар бордир'}, {'chapter': 3, 'verse': 173, 'text': 'ٱلَّذِينَ قَالَ لَهُمُ ٱلنَّاسُ إِنَّ ٱلنَّاسَ قَدۡ جَمَعُواْ لَكُمۡ فَٱخۡشَوۡهُمۡ فَزَادَهُمۡ إِيمَٰنٗا وَقَالُواْ حَسۡبُنَا ٱللَّهُ وَنِعۡمَ ٱلۡوَكِيلُ\nУларга кишилар: «Албатта, одамлар сизга қарши куч тўпладилар, улардан қўрқинглар», деганда, иймонлари зиёда бўлди ва: «Бизга Аллоҳнинг Ўзи етарли ва У қандай ҳам яхши вакил», – дедилар'}, {'chapter': 3, 'verse': 174, 'text': 'فَٱنقَلَبُواْ بِنِعۡمَةٖ مِّنَ ٱللَّهِ وَفَضۡلٖ لَّمۡ يَمۡسَسۡهُمۡ سُوٓءٞ وَٱتَّبَعُواْ رِضۡوَٰنَ ٱللَّهِۗ وَٱللَّهُ ذُو فَضۡلٍ عَظِيمٍ\nБас, Аллоҳнинг неъмати ва фазли ила уларга ёмонлик етмасдан қайтдилар ва Аллоҳнинг розилигига эришдилар. Аллоҳ улуғ фазл эгасидир'}, {'chapter': 3, 'verse': 175, 'text': 'إِنَّمَا ذَٰلِكُمُ ٱلشَّيۡطَٰنُ يُخَوِّفُ أَوۡلِيَآءَهُۥ فَلَا تَخَافُوهُمۡ وَخَافُونِ إِن كُنتُم مُّؤۡمِنِينَ\nАлбатта, бундай бўлишингиз шайтондандир, у ўз яқинларидан қўрқитур. Бас, агар мўмин бўлсангиз, улардан қўрқманглар, мендан қўрқинглар'}, {'chapter': 3, 'verse': 176, 'text': 'وَلَا يَحۡزُنكَ ٱلَّذِينَ يُسَٰرِعُونَ فِي ٱلۡكُفۡرِۚ إِنَّهُمۡ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗاۚ يُرِيدُ ٱللَّهُ أَلَّا يَجۡعَلَ لَهُمۡ حَظّٗا فِي ٱلۡأٓخِرَةِۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ\nКуфрга ошиқаётганлар сени хафа қилмасин. Албатта, улар ҳаргиз Аллоҳга бирор зарар етказа олмаслар. Аллоҳ уларга охиратда насиба қилмасликни хоҳлайдир. Уларга улкан азоб бордир'}, {'chapter': 3, 'verse': 177, 'text': 'إِنَّ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلۡكُفۡرَ بِٱلۡإِيمَٰنِ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nАлбатта, куфрни иймонга сотиб олганлар Аллоҳга ҳаргиз бирор зарар етказа олмаслар. Уларга аламли азоб бордир'}, {'chapter': 3, 'verse': 178, 'text': 'وَلَا يَحۡسَبَنَّ ٱلَّذِينَ كَفَرُوٓاْ أَنَّمَا نُمۡلِي لَهُمۡ خَيۡرٞ لِّأَنفُسِهِمۡۚ إِنَّمَا نُمۡلِي لَهُمۡ لِيَزۡدَادُوٓاْ إِثۡمٗاۖ وَلَهُمۡ عَذَابٞ مُّهِينٞ\nКуфр келтирганлар уларни тек қўйганимизни ўзлари учун яхши деб ҳисобламасинлар. Биз уларни фақат гуноҳлари кўпайиши учунгина тек қўямиз. Уларга хорловчи азоб бордир'}, {'chapter': 3, 'verse': 179, 'text': 'مَّا كَانَ ٱللَّهُ لِيَذَرَ ٱلۡمُؤۡمِنِينَ عَلَىٰ مَآ أَنتُمۡ عَلَيۡهِ حَتَّىٰ يَمِيزَ ٱلۡخَبِيثَ مِنَ ٱلطَّيِّبِۗ وَمَا كَانَ ٱللَّهُ لِيُطۡلِعَكُمۡ عَلَى ٱلۡغَيۡبِ وَلَٰكِنَّ ٱللَّهَ يَجۡتَبِي مِن رُّسُلِهِۦ مَن يَشَآءُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرُسُلِهِۦۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ فَلَكُمۡ أَجۡرٌ عَظِيمٞ\nАллоҳ мўминларни покидан нопокини ажратмасдан, сиз турган ҳолда, қўйиб қўймас. Аллоҳ сизларни ғайбдан хабардор қилиб қўймас. Лекин Аллоҳ Ўз Пайғамбарларидан кимни хоҳласа, танлаб оладир. Бас, Аллоҳга ва Унинг Пайғамбарларига иймон келтиринглар. Агар иймон келтирсангиз ва тақво қилсангиз, сизларга улуғ ажр бордир'}, {'chapter': 3, 'verse': 180, 'text': 'وَلَا يَحۡسَبَنَّ ٱلَّذِينَ يَبۡخَلُونَ بِمَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ هُوَ خَيۡرٗا لَّهُمۖ بَلۡ هُوَ شَرّٞ لَّهُمۡۖ سَيُطَوَّقُونَ مَا بَخِلُواْ بِهِۦ يَوۡمَ ٱلۡقِيَٰمَةِۗ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nАллоҳ Ўз фазлидан берган нарсага бахиллик қилганлар буни ўзларига яхшилик деб ҳисобламасинлар. Аксинча, бу уларга ёмонликдир. Қиёмат куни бахиллик қилган нарсалари ила бўйинлари ўралур. Осмонлару ернинг мероси Аллоҳгадир. Ва Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 3, 'verse': 181, 'text': 'لَّقَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ فَقِيرٞ وَنَحۡنُ أَغۡنِيَآءُۘ سَنَكۡتُبُ مَا قَالُواْ وَقَتۡلَهُمُ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَنَقُولُ ذُوقُواْ عَذَابَ ٱلۡحَرِيقِ\nБатаҳқиқ, Аллоҳ: «Албатта, Аллоҳ камбағал ва биз боймиз», деганларнинг гапини эшитди. Айтганларини ва Пайғамбарларни ноҳақ ўлдирганларни ёзиб қўямиз ва: «Куйдирувчи азобни татиб кўринг!» деймиз'}, {'chapter': 3, 'verse': 182, 'text': 'ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّامٖ لِّلۡعَبِيدِ\nБундоқ бўлишлиги сизнинг ўз қўлингиз ила қилганингиз туфайлидир. Албатта, Аллоҳ бандаларига зулм қилувчи эмас'}, {'chapter': 3, 'verse': 183, 'text': 'ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ عَهِدَ إِلَيۡنَآ أَلَّا نُؤۡمِنَ لِرَسُولٍ حَتَّىٰ يَأۡتِيَنَا بِقُرۡبَانٖ تَأۡكُلُهُ ٱلنَّارُۗ قُلۡ قَدۡ جَآءَكُمۡ رُسُلٞ مِّن قَبۡلِي بِٱلۡبَيِّنَٰتِ وَبِٱلَّذِي قُلۡتُمۡ فَلِمَ قَتَلۡتُمُوهُمۡ إِن كُنتُمۡ صَٰدِقِينَ\n«Албатта, Аллоҳ биздан ўтда куядиган қурбонлик келтирмаган Пайғамбарга ишонмаслигимизга аҳд олган», дейдиганларга: «Мендан олдин ҳам сизларга Пайғамбарлар очиқ-ойдин баёнотлар ва сиз айтган нарса билан келганлар. Агар ростгўй бўлсаларингиз, нимага уларни ўлдирдингиз?» деб айт'}, {'chapter': 3, 'verse': 184, 'text': 'فَإِن كَذَّبُوكَ فَقَدۡ كُذِّبَ رُسُلٞ مِّن قَبۡلِكَ جَآءُو بِٱلۡبَيِّنَٰتِ وَٱلزُّبُرِ وَٱلۡكِتَٰبِ ٱلۡمُنِيرِ\nАгар сени ёлғончи қилсалар, сендан олдин очиқ-ойдин баёнотлар, ҳикматли китоблар ва нурли китоблар билан келган Пайғамбарларни ҳам ёлғончи қилганлар'}, {'chapter': 3, 'verse': 185, 'text': 'كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۗ وَإِنَّمَا تُوَفَّوۡنَ أُجُورَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۖ فَمَن زُحۡزِحَ عَنِ ٱلنَّارِ وَأُدۡخِلَ ٱلۡجَنَّةَ فَقَدۡ فَازَۗ وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا مَتَٰعُ ٱلۡغُرُورِ\nҲар бир жон ўлимни тотгувчидир. Қиёмат куни ажрларингизни тўлиғича олурсиз. Ким дўзахдан узоқлаштирилса ва жаннатга киритилса, шубҳасиз, ютибди. Бу дунё ҳаёти ғурур матоҳидан ўзга нарса эмас'}, {'chapter': 3, 'verse': 186, 'text': '۞لَتُبۡلَوُنَّ فِيٓ أَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ وَلَتَسۡمَعُنَّ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَمِنَ ٱلَّذِينَ أَشۡرَكُوٓاْ أَذٗى كَثِيرٗاۚ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ فَإِنَّ ذَٰلِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ\nАлбатта, молу жонда синовга учрайсиз. Албатта, сиздан олдин китоб берилганлардан ва ширк келтирганлардан кўплаб озор эшитасиз. Агар сабр қилсангиз ва тақво қилсангиз, албатта, бу салмоқли ишлардандир'}, {'chapter': 3, 'verse': 187, 'text': 'وَإِذۡ أَخَذَ ٱللَّهُ مِيثَٰقَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ لَتُبَيِّنُنَّهُۥ لِلنَّاسِ وَلَا تَكۡتُمُونَهُۥ فَنَبَذُوهُ وَرَآءَ ظُهُورِهِمۡ وَٱشۡتَرَوۡاْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَبِئۡسَ مَا يَشۡتَرُونَ\nАллоҳ китоб берилганлардан, уни одамларга албатта баён қилурсиз ва беркитмассиз, деб аҳд олганини эсла. Бас, уни ортларига қаратиб отдилар ва арзон нархга сотдилар. Сотиб олган нарсалари қандай ҳам ёмон'}, {'chapter': 3, 'verse': 188, 'text': 'لَا تَحۡسَبَنَّ ٱلَّذِينَ يَفۡرَحُونَ بِمَآ أَتَواْ وَّيُحِبُّونَ أَن يُحۡمَدُواْ بِمَا لَمۡ يَفۡعَلُواْ فَلَا تَحۡسَبَنَّهُم بِمَفَازَةٖ مِّنَ ٱلۡعَذَابِۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nҚилганларига хурсанд бўлиб, қилмаганларига мақталишни суядиганларни азобдан нажотда деб ҳисоблама. Уларга аламли азоб бор'}, {'chapter': 3, 'verse': 189, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлару ернинг мулки Аллоҳникидир. Ва Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 3, 'verse': 190, 'text': 'إِنَّ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ لَأٓيَٰتٖ لِّأُوْلِي ٱلۡأَلۡبَٰبِ\nАлбатта, осмонлару ернинг яратилишида ва кеча-кундузнинг алмашинишида ақл эгалари учун белгилар бор'}, {'chapter': 3, 'verse': 191, 'text': 'ٱلَّذِينَ يَذۡكُرُونَ ٱللَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِهِمۡ وَيَتَفَكَّرُونَ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ رَبَّنَا مَا خَلَقۡتَ هَٰذَا بَٰطِلٗا سُبۡحَٰنَكَ فَقِنَا عَذَابَ ٱلنَّارِ\nАллоҳни тик турган, ўтирган ва ёнбошлаган ҳолларида эслайдиган, осмонлару ернинг яратилишини тафаккур қиладиганлар: «Роббимиз, буни бекорга яратганинг йўқ, Ўзинг поксан, бизни ўт азобидан сақлагин'}, {'chapter': 3, 'verse': 192, 'text': 'رَبَّنَآ إِنَّكَ مَن تُدۡخِلِ ٱلنَّارَ فَقَدۡ أَخۡزَيۡتَهُۥۖ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٖ\nРоббимиз, албатта, Сен кимни дўзахга киритсанг, батаҳқиқ, уни шарманда қилибсан. Ва золимларга ёрдам берувчилар йўқ'}, {'chapter': 3, 'verse': 193, 'text': 'رَّبَّنَآ إِنَّنَا سَمِعۡنَا مُنَادِيٗا يُنَادِي لِلۡإِيمَٰنِ أَنۡ ءَامِنُواْ بِرَبِّكُمۡ فَـَٔامَنَّاۚ رَبَّنَا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَكَفِّرۡ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّنَا مَعَ ٱلۡأَبۡرَارِ\nРоббимиз, биз нидо қилувчининг «Роббингизга иймон келтиринг!» деб нидо қилаётганини эшитдик ва иймон келтирдик. Роббимиз, бизнинг гуноҳларимизни кечиргин ва ёмонликларимизни ўчиргин ҳамда яхшилар билан бирга ўлдиргин'}, {'chapter': 3, 'verse': 194, 'text': 'رَبَّنَا وَءَاتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخۡزِنَا يَوۡمَ ٱلۡقِيَٰمَةِۖ إِنَّكَ لَا تُخۡلِفُ ٱلۡمِيعَادَ\nРоббимиз, бизга Пайғамбарларингга ваъда қилган нарсаларингни бергин ва бизни қиёмат куни шарманда қилмагин. Албатта, Сен ваъдага хилоф қилмассан», дерлар'}, {'chapter': 3, 'verse': 195, 'text': 'فَٱسۡتَجَابَ لَهُمۡ رَبُّهُمۡ أَنِّي لَآ أُضِيعُ عَمَلَ عَٰمِلٖ مِّنكُم مِّن ذَكَرٍ أَوۡ أُنثَىٰۖ بَعۡضُكُم مِّنۢ بَعۡضٖۖ فَٱلَّذِينَ هَاجَرُواْ وَأُخۡرِجُواْ مِن دِيَٰرِهِمۡ وَأُوذُواْ فِي سَبِيلِي وَقَٰتَلُواْ وَقُتِلُواْ لَأُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأُدۡخِلَنَّهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ ثَوَابٗا مِّنۡ عِندِ ٱللَّهِۚ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلثَّوَابِ\nРоббилари уларнинг (дуоларини) ижобат қилиб: «Мен сизлардан эркагу аёл амал қилувчининг амалини зое қилмасман. Баъзингиз баъзингиздансиз. Ҳижрат қилганлар, диёрларидан чиқарилганлар, Менинг йўлимда азият чекканлар, жанг қилганлар ва қатл бўлганларнинг ёмонликларини ўчирурман ҳамда уларни албатта остидан анҳорлар оқиб турган жаннатларга киритурман, бу — Аллоҳнинг ҳузуридан савобдир. Ва Аллоҳ — ҳузурида гўзал савоб бор Зотдир», деди'}, {'chapter': 3, 'verse': 196, 'text': 'لَا يَغُرَّنَّكَ تَقَلُّبُ ٱلَّذِينَ كَفَرُواْ فِي ٱلۡبِلَٰدِ\nКуфр келтирганларнинг юрт кезишлари сени алдаб қўймасин'}, {'chapter': 3, 'verse': 197, 'text': 'مَتَٰعٞ قَلِيلٞ ثُمَّ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ\nБу озгина матоҳдир. Сўнгра ўринлари жаҳаннамдир. У қандай ҳам ёмон ётоқ'}, {'chapter': 3, 'verse': 198, 'text': 'لَٰكِنِ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا نُزُلٗا مِّنۡ عِندِ ٱللَّهِۗ وَمَا عِندَ ٱللَّهِ خَيۡرٞ لِّلۡأَبۡرَارِ\nЛекин Роббиларига тақво қилганлар учун остидан анҳорлар оқиб турган жаннатлар бўлиб, унда абадий қолурлар. Бу Аллоҳдан зиёфатдир. Аллоҳ ҳузуридаги нарса аброрлар учун яхшидир'}, {'chapter': 3, 'verse': 199, 'text': 'وَإِنَّ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ لَمَن يُؤۡمِنُ بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡكُمۡ وَمَآ أُنزِلَ إِلَيۡهِمۡ خَٰشِعِينَ لِلَّهِ لَا يَشۡتَرُونَ بِـَٔايَٰتِ ٱللَّهِ ثَمَنٗا قَلِيلًاۚ أُوْلَـٰٓئِكَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡۗ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nАлбатта, аҳли китоблардан Аллоҳга, сизларга туширилган нарсага, уларга туширилган нарсага иймон келтирадиганлари, Аллоҳга ўзини хор тутиб бўйин эгадиганлари, Аллоҳнинг оятларини арзон баҳога сотмайдиганлари бордир. Ана ўшаларга Роббилари ҳузурида ажр бордир. Албатта, Аллоҳ тезҳисоб Зотдир'}, {'chapter': 3, 'verse': 200, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱصۡبِرُواْ وَصَابِرُواْ وَرَابِطُواْ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй, иймон келтирганлар! Сабр қилинг. Сабр ила ғолиб келинг. Курашга бел боғлаб туринг. Аллоҳга тақво қилинг. Шоядки, ютуққа эришсангиз'}], '4': [{'chapter': 4, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمُ ٱلَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَخَلَقَ مِنۡهَا زَوۡجَهَا وَبَثَّ مِنۡهُمَا رِجَالٗا كَثِيرٗا وَنِسَآءٗۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِي تَسَآءَلُونَ بِهِۦ وَٱلۡأَرۡحَامَۚ إِنَّ ٱللَّهَ كَانَ عَلَيۡكُمۡ رَقِيبٗا\nЭй одамлар! Сизларни бир жондан яратган ва ундан унинг жуфтини яратиб, икковларидан кўплаб эркагу аёллар таратган Роббингиздан қўрқинглар! Номини ишлатиб бир-бирингиздан сўровда бўладиганингиз Аллоҳдан ва қариндошлик (алоқаларини узиш)дан қўрқинг. Албатта, Аллоҳ устингизда кузатиб турувчи Зотдир'}, {'chapter': 4, 'verse': 2, 'text': 'وَءَاتُواْ ٱلۡيَتَٰمَىٰٓ أَمۡوَٰلَهُمۡۖ وَلَا تَتَبَدَّلُواْ ٱلۡخَبِيثَ بِٱلطَّيِّبِۖ وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَهُمۡ إِلَىٰٓ أَمۡوَٰلِكُمۡۚ إِنَّهُۥ كَانَ حُوبٗا كَبِيرٗا\nВа етимларга ўз молларини беринг. Нопокни покка алмаштирманг. Уларнинг молларини ўзингизнинг молларингизга қўшиб еманг. Зеро, бу катта гуноҳ бўлур'}, {'chapter': 4, 'verse': 3, 'text': 'وَإِنۡ خِفۡتُمۡ أَلَّا تُقۡسِطُواْ فِي ٱلۡيَتَٰمَىٰ فَٱنكِحُواْ مَا طَابَ لَكُم مِّنَ ٱلنِّسَآءِ مَثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۖ فَإِنۡ خِفۡتُمۡ أَلَّا تَعۡدِلُواْ فَوَٰحِدَةً أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۚ ذَٰلِكَ أَدۡنَىٰٓ أَلَّا تَعُولُواْ\nАгар етимларга адолат қила олмасликдан қўрқсангиз, ўзингизга ёққан аёллардан иккитами, учтами, тўрттами никоҳлаб олинг. Агар адолат қила олмасликдан қўрқсангиз, биттани (олинг) ёки қўлингизда мулк бўлганлар билан (кифояланинг). Мана шу, жабр қилмаслигингизга яқинроқдир'}, {'chapter': 4, 'verse': 4, 'text': 'وَءَاتُواْ ٱلنِّسَآءَ صَدُقَٰتِهِنَّ نِحۡلَةٗۚ فَإِن طِبۡنَ لَكُمۡ عَن شَيۡءٖ مِّنۡهُ نَفۡسٗا فَكُلُوهُ هَنِيٓـٔٗا مَّرِيٓـٔٗا\nАёлларга маҳрларини кўнгилдан чиқариб беринг. Агар улар ундан бирор нарсани чин кўнгилдан тутсалар, бас, уни енглар. Ош бўлсин, офият бўлсин'}, {'chapter': 4, 'verse': 5, 'text': 'وَلَا تُؤۡتُواْ ٱلسُّفَهَآءَ أَمۡوَٰلَكُمُ ٱلَّتِي جَعَلَ ٱللَّهُ لَكُمۡ قِيَٰمٗا وَٱرۡزُقُوهُمۡ فِيهَا وَٱكۡسُوهُمۡ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا\nАллоҳ (ҳаётингизни) турғизиш (воситаси) қилган молларингизни эси пастларга берманг. Уларни ўша моллардан ризқлантиринг, кийинтиринг ва уларга яхши сўзлар айтинг'}, {'chapter': 4, 'verse': 6, 'text': 'وَٱبۡتَلُواْ ٱلۡيَتَٰمَىٰ حَتَّىٰٓ إِذَا بَلَغُواْ ٱلنِّكَاحَ فَإِنۡ ءَانَسۡتُم مِّنۡهُمۡ رُشۡدٗا فَٱدۡفَعُوٓاْ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡۖ وَلَا تَأۡكُلُوهَآ إِسۡرَافٗا وَبِدَارًا أَن يَكۡبَرُواْۚ وَمَن كَانَ غَنِيّٗا فَلۡيَسۡتَعۡفِفۡۖ وَمَن كَانَ فَقِيرٗا فَلۡيَأۡكُلۡ بِٱلۡمَعۡرُوفِۚ فَإِذَا دَفَعۡتُمۡ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡ فَأَشۡهِدُواْ عَلَيۡهِمۡۚ وَكَفَىٰ بِٱللَّهِ حَسِيبٗا\nЕтимларни то никоҳ(ёши)га етгунларича синаб туринглар. Агар уларнинг эс-ҳуши жойида эканини билсангиз, уларга ўз молларини топширинг. У молларни исроф ила ва эгаларининг катта бўлиб қолишидан қўрқиб шошилиб еманг. Бой бўлганлар иффатли бўлсинлар. Ким камбағал бўлса, тўғрилик билан есин. Уларга ўз молларини топшираётганингизда гувоҳлар келтиринг. Ҳисобчиликка Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 7, 'text': 'لِّلرِّجَالِ نَصِيبٞ مِّمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَ مِمَّا قَلَّ مِنۡهُ أَوۡ كَثُرَۚ نَصِيبٗا مَّفۡرُوضٗا\nОта-оналар ва қариндошлар қолдирган нарсада эркакларнинг насибаси бор. Ота-оналар ва қариндошлар қолдирган нарсада аёлларнинг насибаси бор. У оз бўлсин, кўп бўлсин — фарз қилинган насибадир'}, {'chapter': 4, 'verse': 8, 'text': 'وَإِذَا حَضَرَ ٱلۡقِسۡمَةَ أُوْلُواْ ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينُ فَٱرۡزُقُوهُم مِّنۡهُ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا\nАгар тақсимлашга қариндошлар, етимлар ва мискинлар ҳозир бўлсалар, уларни у(мерос)дан ризқлантиринглар ҳамда уларга яхши гаплар айтинглар'}, {'chapter': 4, 'verse': 9, 'text': 'وَلۡيَخۡشَ ٱلَّذِينَ لَوۡ تَرَكُواْ مِنۡ خَلۡفِهِمۡ ذُرِّيَّةٗ ضِعَٰفًا خَافُواْ عَلَيۡهِمۡ فَلۡيَتَّقُواْ ٱللَّهَ وَلۡيَقُولُواْ قَوۡلٗا سَدِيدًا\nЎзлари заифҳол зурриётларини ортдан қолдиришдан қандай қўрқсалар, (бошқалар ҳақида ҳам) шундай қўрқсинлар. Бас, Аллоҳдан қўрқсинлар ва тўғри сўз айтсинлар'}, {'chapter': 4, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ يَأۡكُلُونَ أَمۡوَٰلَ ٱلۡيَتَٰمَىٰ ظُلۡمًا إِنَّمَا يَأۡكُلُونَ فِي بُطُونِهِمۡ نَارٗاۖ وَسَيَصۡلَوۡنَ سَعِيرٗا\nАлбатта, етимларнинг молини зулм ила еганлар қоринларида оловни еган бўлурлар. Ва, шубҳасиз, дўзахга кирурлар'}, {'chapter': 4, 'verse': 11, 'text': 'يُوصِيكُمُ ٱللَّهُ فِيٓ أَوۡلَٰدِكُمۡۖ لِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَيَيۡنِۚ فَإِن كُنَّ نِسَآءٗ فَوۡقَ ٱثۡنَتَيۡنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۖ وَإِن كَانَتۡ وَٰحِدَةٗ فَلَهَا ٱلنِّصۡفُۚ وَلِأَبَوَيۡهِ لِكُلِّ وَٰحِدٖ مِّنۡهُمَا ٱلسُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُۥ وَلَدٞۚ فَإِن لَّمۡ يَكُن لَّهُۥ وَلَدٞ وَوَرِثَهُۥٓ أَبَوَاهُ فَلِأُمِّهِ ٱلثُّلُثُۚ فَإِن كَانَ لَهُۥٓ إِخۡوَةٞ فَلِأُمِّهِ ٱلسُّدُسُۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِي بِهَآ أَوۡ دَيۡنٍۗ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ لَا تَدۡرُونَ أَيُّهُمۡ أَقۡرَبُ لَكُمۡ نَفۡعٗاۚ فَرِيضَةٗ مِّنَ ٱللَّهِۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nАллоҳ сизга фарзандларингиз ҳақида васият этиб, бир ўғилга икки қиз насибасича беришни амр этади. Агар улар иккитадан кўп (аёл) бўлсалар, уларга у (ота) қолдирганнинг учдан иккиси. Агар қиз битта бўлса, унга ярми. Агар у(маййит)нинг боласи бўлса, у тарк қилган нарсадан ота-онасининг ҳар бирига олтидан бир. Агар унинг боласи бўлмаса ва ота-онаси меросхўр бўлса, онасига учдан бир. Агар унинг ака-укалари бўлса, онасига олтидан бир. У қилган васият ёки қарзини адо этгандан сўнг. Оталарингиз ва болаларингиздан қайси бирлари сизга манфаатлироқ эканини билмассизлар. Бу, Аллоҳ жорий қилган фарздир. Албатта, Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 12, 'text': '۞وَلَكُمۡ نِصۡفُ مَا تَرَكَ أَزۡوَٰجُكُمۡ إِن لَّمۡ يَكُن لَّهُنَّ وَلَدٞۚ فَإِن كَانَ لَهُنَّ وَلَدٞ فَلَكُمُ ٱلرُّبُعُ مِمَّا تَرَكۡنَۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِينَ بِهَآ أَوۡ دَيۡنٖۚ وَلَهُنَّ ٱلرُّبُعُ مِمَّا تَرَكۡتُمۡ إِن لَّمۡ يَكُن لَّكُمۡ وَلَدٞۚ فَإِن كَانَ لَكُمۡ وَلَدٞ فَلَهُنَّ ٱلثُّمُنُ مِمَّا تَرَكۡتُمۚ مِّنۢ بَعۡدِ وَصِيَّةٖ تُوصُونَ بِهَآ أَوۡ دَيۡنٖۗ وَإِن كَانَ رَجُلٞ يُورَثُ كَلَٰلَةً أَوِ ٱمۡرَأَةٞ وَلَهُۥٓ أَخٌ أَوۡ أُخۡتٞ فَلِكُلِّ وَٰحِدٖ مِّنۡهُمَا ٱلسُّدُسُۚ فَإِن كَانُوٓاْ أَكۡثَرَ مِن ذَٰلِكَ فَهُمۡ شُرَكَآءُ فِي ٱلثُّلُثِۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصَىٰ بِهَآ أَوۡ دَيۡنٍ غَيۡرَ مُضَآرّٖۚ وَصِيَّةٗ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِيمٌ حَلِيمٞ\nСизларга хотинларингиз қолдирган нарсанинг — агар уларнинг боласи бўлмаса — ярми тегади. Агар уларнинг боласи бўлса, сизга улар қолдирган нарсанинг чораги тегади. Улар қилган васиятни ёки қарзларини адо этгандан сўнг. Уларга сиз қолдирган нарсанинг — агар болангиз бўлмаса — чораги тегади. Агар болангиз бўлса, уларга сиз қолдирган нарсанинг саккиздан бири тегади. Сиз қилган васиятни ёки қарзингизни адо этгандан сўнг. Агар бир эркак ёки аёл калола ҳолида мерос қолдирса, унинг биродари ёки синглиси бўлса, улардан ҳар бирига олтидан бир тегур. Агар улар бундан кўп бўлсалар, учдан бирига шерикдирлар. Зарар қилмайдиган васиятни ёки қарзни адо этгандан сўнг. Булар Аллоҳнинг васиятидир. Ва Аллоҳ билувчи ҳамда Ҳалийм Зотдир'}, {'chapter': 4, 'verse': 13, 'text': 'تِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАна ўшалар Аллоҳнинг чегараларидир. Ким Аллоҳга ва Унинг Расулига итоат этса, У Зот уни остидан анҳорлар оқиб турган жаннатга абадий қолиш учун киритадир. Бу эса, улкан ютуқдир'}, {'chapter': 4, 'verse': 14, 'text': 'وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ وَيَتَعَدَّ حُدُودَهُۥ يُدۡخِلۡهُ نَارًا خَٰلِدٗا فِيهَا وَلَهُۥ عَذَابٞ مُّهِينٞ\nКим Аллоҳга ва Унинг Расулига исён қилса ва Унинг чегарасидан тажовуз қилса, У Зот уни абадий қолиш учун дўзахга киритадир. Ва унга хорловчи азоб бордир'}, {'chapter': 4, 'verse': 15, 'text': 'وَٱلَّـٰتِي يَأۡتِينَ ٱلۡفَٰحِشَةَ مِن نِّسَآئِكُمۡ فَٱسۡتَشۡهِدُواْ عَلَيۡهِنَّ أَرۡبَعَةٗ مِّنكُمۡۖ فَإِن شَهِدُواْ فَأَمۡسِكُوهُنَّ فِي ٱلۡبُيُوتِ حَتَّىٰ يَتَوَفَّىٰهُنَّ ٱلۡمَوۡتُ أَوۡ يَجۡعَلَ ٱللَّهُ لَهُنَّ سَبِيلٗا\nАёлларингиздан фоҳиша (зино) қилганларига ўзларингиздан тўртта гувоҳ келтиринг. Агар гувоҳлик берсалар, уларни то ўлим етгунча ёки Аллоҳ бир йўл кўрсатгунча уйларида тутиб туринг'}, {'chapter': 4, 'verse': 16, 'text': 'وَٱلَّذَانِ يَأۡتِيَٰنِهَا مِنكُمۡ فَـَٔاذُوهُمَاۖ فَإِن تَابَا وَأَصۡلَحَا فَأَعۡرِضُواْ عَنۡهُمَآۗ إِنَّ ٱللَّهَ كَانَ تَوَّابٗا رَّحِيمًا\nСизлардан ўша(фоҳиша) ишни қилган икки эркакка озор беринглар. Агар икковлари тавба қилсалар ва тузалсалар, уларни тек қўйинг. Албатта, Аллоҳ тавбаларни қабул қилувчи ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 17, 'text': 'إِنَّمَا ٱلتَّوۡبَةُ عَلَى ٱللَّهِ لِلَّذِينَ يَعۡمَلُونَ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ يَتُوبُونَ مِن قَرِيبٖ فَأُوْلَـٰٓئِكَ يَتُوبُ ٱللَّهُ عَلَيۡهِمۡۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nЖаҳолат-ла ёмонлик қилиб қўйиб, сўнгра тезда тавба қиладиганларнинг тавбаси Аллоҳнинг зиммасидадир. Аллоҳ ана ўшаларнинг тавбасини қабул қиладир. Ва Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 18, 'text': 'وَلَيۡسَتِ ٱلتَّوۡبَةُ لِلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ حَتَّىٰٓ إِذَا حَضَرَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ إِنِّي تُبۡتُ ٱلۡـَٰٔنَ وَلَا ٱلَّذِينَ يَمُوتُونَ وَهُمۡ كُفَّارٌۚ أُوْلَـٰٓئِكَ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا\nЁмонликларни қилиб юриб, бирларига ўлим етганда, энди тавба қилдим, деганларга ва кофир ҳолларида ўлганларга тавба йўқ. Ана ўшаларга аламли азобларни тайёрлаб қўйганмиз'}, {'chapter': 4, 'verse': 19, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَحِلُّ لَكُمۡ أَن تَرِثُواْ ٱلنِّسَآءَ كَرۡهٗاۖ وَلَا تَعۡضُلُوهُنَّ لِتَذۡهَبُواْ بِبَعۡضِ مَآ ءَاتَيۡتُمُوهُنَّ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَعَاشِرُوهُنَّ بِٱلۡمَعۡرُوفِۚ فَإِن كَرِهۡتُمُوهُنَّ فَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَيَجۡعَلَ ٱللَّهُ فِيهِ خَيۡرٗا كَثِيرٗا\nЭй, иймон келтирганлар! Сизларга аёлларни зулм-ла меросга олиш ҳалол эмас. Очиқ фоҳиша иш қилмасалар, берган нарсаларингизнинг баъзисини олиш учун уларни зўрлик-ла ушлаб турманг ва улар ила яхшиликда яшанг. Агар уларни ёқтирмасангиз, шоядки, Аллоҳ сиз ёқтирмаган нарсада кўпгина яхшиликларни қилса'}, {'chapter': 4, 'verse': 20, 'text': 'وَإِنۡ أَرَدتُّمُ ٱسۡتِبۡدَالَ زَوۡجٖ مَّكَانَ زَوۡجٖ وَءَاتَيۡتُمۡ إِحۡدَىٰهُنَّ قِنطَارٗا فَلَا تَأۡخُذُواْ مِنۡهُ شَيۡـًٔاۚ أَتَأۡخُذُونَهُۥ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا\nВа агар хотин ўрнига бошқа хотин олмоқчи бўлсангиз ва уларнинг бирига ҳаддан зиёд маҳр берган бўлсангиз ҳам, ундан ҳеч нарсани қайтариб олманг. Уни бўҳтон ва катта гуноҳ ҳолида олаверасизми'}, {'chapter': 4, 'verse': 21, 'text': 'وَكَيۡفَ تَأۡخُذُونَهُۥ وَقَدۡ أَفۡضَىٰ بَعۡضُكُمۡ إِلَىٰ بَعۡضٖ وَأَخَذۡنَ مِنكُم مِّيثَٰقًا غَلِيظٗا\nВа қандай ҳам олурсиз?! Ахир, бир-бирингизга қовушдингиз ва улар сиздан мустаҳкам аҳду паймон олганлар-ку'}, {'chapter': 4, 'verse': 22, 'text': 'وَلَا تَنكِحُواْ مَا نَكَحَ ءَابَآؤُكُم مِّنَ ٱلنِّسَآءِ إِلَّا مَا قَدۡ سَلَفَۚ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَمَقۡتٗا وَسَآءَ سَبِيلًا\nОталарингиз ўз никоҳларига олган аёлларни сиз ўз никоҳингизга олманг! Аввал ўтган бўлса, майли. Бу иш, албатта, фоҳиша, ғазабнок иш ва қандай ҳам ёмон йўлдир'}, {'chapter': 4, 'verse': 23, 'text': 'حُرِّمَتۡ عَلَيۡكُمۡ أُمَّهَٰتُكُمۡ وَبَنَاتُكُمۡ وَأَخَوَٰتُكُمۡ وَعَمَّـٰتُكُمۡ وَخَٰلَٰتُكُمۡ وَبَنَاتُ ٱلۡأَخِ وَبَنَاتُ ٱلۡأُخۡتِ وَأُمَّهَٰتُكُمُ ٱلَّـٰتِيٓ أَرۡضَعۡنَكُمۡ وَأَخَوَٰتُكُم مِّنَ ٱلرَّضَٰعَةِ وَأُمَّهَٰتُ نِسَآئِكُمۡ وَرَبَـٰٓئِبُكُمُ ٱلَّـٰتِي فِي حُجُورِكُم مِّن نِّسَآئِكُمُ ٱلَّـٰتِي دَخَلۡتُم بِهِنَّ فَإِن لَّمۡ تَكُونُواْ دَخَلۡتُم بِهِنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ وَحَلَـٰٓئِلُ أَبۡنَآئِكُمُ ٱلَّذِينَ مِنۡ أَصۡلَٰبِكُمۡ وَأَن تَجۡمَعُواْ بَيۡنَ ٱلۡأُخۡتَيۡنِ إِلَّا مَا قَدۡ سَلَفَۗ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nСизларга оналарингиз, қизларингиз, опа-сингилларингиз, аммаларингиз, холаларингиз, ака-укаларингизнинг қизлари, опа-сингилларингизнинг қизлари, эмизган оналарингиз, эмикдош опа-сингилларингиз, хотинларингизнинг оналари, ўзингиз қовушган хотинларингизнинг қарамоғингиздаги қизлари ҳаром қилинди. Агар у(хотин)лар билан қовушмаган бўлсангиз, (қизларига уйланишингизда) гуноҳ йўқ. Ва пуштингиздан бўлган ўғилларингизнинг хотинлари, опа-сингилни қўшиб олмоғингиз ҳаром қилинди. Магар аввал ўтган бўлса, майли. Албатта, Аллоҳ мағфират қилувчи ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 24, 'text': '۞وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلنِّسَآءِ إِلَّا مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۖ كِتَٰبَ ٱللَّهِ عَلَيۡكُمۡۚ وَأُحِلَّ لَكُم مَّا وَرَآءَ ذَٰلِكُمۡ أَن تَبۡتَغُواْ بِأَمۡوَٰلِكُم مُّحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَۚ فَمَا ٱسۡتَمۡتَعۡتُم بِهِۦ مِنۡهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِيضَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا تَرَٰضَيۡتُم بِهِۦ مِنۢ بَعۡدِ ٱلۡفَرِيضَةِۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nҚўлингизга мулк бўлиб тушганлардан бошқа эрли аёллар ҳам (ҳаром қилинди). Бу сизларга Аллоҳнинг ёзганидир. Ана ўшалардан бошқаларни поклик ва зино аралаштирмаган ҳолда молларингиз ила талаб қилмоғингиз ҳалол қилинди. Улардан ҳузурланишингиз сабабидан маҳрларини беринг. Бу фарздир. Фарз маҳрдан кейин ўзаро розилик ила келишганингиз гуноҳ эмас. Албатта, Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 25, 'text': 'وَمَن لَّمۡ يَسۡتَطِعۡ مِنكُمۡ طَوۡلًا أَن يَنكِحَ ٱلۡمُحۡصَنَٰتِ ٱلۡمُؤۡمِنَٰتِ فَمِن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن فَتَيَٰتِكُمُ ٱلۡمُؤۡمِنَٰتِۚ وَٱللَّهُ أَعۡلَمُ بِإِيمَٰنِكُمۚ بَعۡضُكُم مِّنۢ بَعۡضٖۚ فَٱنكِحُوهُنَّ بِإِذۡنِ أَهۡلِهِنَّ وَءَاتُوهُنَّ أُجُورَهُنَّ بِٱلۡمَعۡرُوفِ مُحۡصَنَٰتٍ غَيۡرَ مُسَٰفِحَٰتٖ وَلَا مُتَّخِذَٰتِ أَخۡدَانٖۚ فَإِذَآ أُحۡصِنَّ فَإِنۡ أَتَيۡنَ بِفَٰحِشَةٖ فَعَلَيۡهِنَّ نِصۡفُ مَا عَلَى ٱلۡمُحۡصَنَٰتِ مِنَ ٱلۡعَذَابِۚ ذَٰلِكَ لِمَنۡ خَشِيَ ٱلۡعَنَتَ مِنكُمۡۚ وَأَن تَصۡبِرُواْ خَيۡرٞ لَّكُمۡۗ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nСизлардан ким ҳур мўминаларни никоҳига олишга имкон топмаса, қўлингизда мулк бўлган мўмина қизларингиздан (уйлансин). Аллоҳ иймонингизни билувчи Зотдир. Ҳаммаларингиз бирсиз. Уларни никоҳингизга аҳлларининг изни ила олинг. Ва яхшилик билан маҳрларини беринг. Улар покиза, зинокор эмас ва ўйнаш тутмаган бўлсинлар. Эрга текканларидан сўнг фоҳиша иш қилсалар, уларга ҳур аёлларга бериладиган азобнинг ярми бериладир. Бу бузилишдан қўрққанлар учундир. Сабр қилмоғингиз сиз учун яхшидир. Аллоҳ мағфиратли ва раҳимлидир'}, {'chapter': 4, 'verse': 26, 'text': 'يُرِيدُ ٱللَّهُ لِيُبَيِّنَ لَكُمۡ وَيَهۡدِيَكُمۡ سُنَنَ ٱلَّذِينَ مِن قَبۡلِكُمۡ وَيَتُوبَ عَلَيۡكُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nАллоҳ сизларга баён қилиб беришни, сизни олдингиларнинг йўлига ҳидоят қилишни ва тавбаларингизни қабул этишни хоҳлайдир. Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 27, 'text': 'وَٱللَّهُ يُرِيدُ أَن يَتُوبَ عَلَيۡكُمۡ وَيُرِيدُ ٱلَّذِينَ يَتَّبِعُونَ ٱلشَّهَوَٰتِ أَن تَمِيلُواْ مَيۡلًا عَظِيمٗا\nАллоҳ сизнинг тавбангизни қабул қилишни хоҳлайдир. Шаҳватларга эргашадиганлар эса, улкан оғишга мойил бўлишингизни хоҳлайдир'}, {'chapter': 4, 'verse': 28, 'text': 'يُرِيدُ ٱللَّهُ أَن يُخَفِّفَ عَنكُمۡۚ وَخُلِقَ ٱلۡإِنسَٰنُ ضَعِيفٗا\nАллоҳ сиздан енгиллатмоқни хоҳлайдир. Ва инсон заиф яратилгандир'}, {'chapter': 4, 'verse': 29, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِٱلۡبَٰطِلِ إِلَّآ أَن تَكُونَ تِجَٰرَةً عَن تَرَاضٖ مِّنكُمۡۚ وَلَا تَقۡتُلُوٓاْ أَنفُسَكُمۡۚ إِنَّ ٱللَّهَ كَانَ بِكُمۡ رَحِيمٗا\nЭй, иймон келтирганлар! Бир-бирларингизнинг молларингизни ботил йўл билан еманг. Магар ўзаро розилик ила тижорат бўлса, майли. Ўзингизни ўзингиз ўлдирманг. Албатта, Аллоҳ сизларга раҳимлидир'}, {'chapter': 4, 'verse': 30, 'text': 'وَمَن يَفۡعَلۡ ذَٰلِكَ عُدۡوَٰنٗا وَظُلۡمٗا فَسَوۡفَ نُصۡلِيهِ نَارٗاۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًا\nКим ўшани тажовузкорлик ва зулм ила қилса, албатта, уни дўзахга киритурмиз. Бу эса, Аллоҳга осондир'}, {'chapter': 4, 'verse': 31, 'text': 'إِن تَجۡتَنِبُواْ كَبَآئِرَ مَا تُنۡهَوۡنَ عَنۡهُ نُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَنُدۡخِلۡكُم مُّدۡخَلٗا كَرِيمٗا\nАгар қайтарилган нарсаларнинг катталаридан четда бўлсангиз, гуноҳларингизни кечирурмиз ва сизни яхши жойга киритамиз'}, {'chapter': 4, 'verse': 32, 'text': 'وَلَا تَتَمَنَّوۡاْ مَا فَضَّلَ ٱللَّهُ بِهِۦ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖۚ لِّلرِّجَالِ نَصِيبٞ مِّمَّا ٱكۡتَسَبُواْۖ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا ٱكۡتَسَبۡنَۚ وَسۡـَٔلُواْ ٱللَّهَ مِن فَضۡلِهِۦٓۚ إِنَّ ٱللَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا\nАллоҳ баъзингизни баъзингиздан афзал этган нарсаларни (ҳасад ила) орзу этманг. Эркакларга ўз касбидан насиба бор. Аёлларга ўз касбидан насиба бор. Аллоҳдан фазлини сўранг. Албатта, Аллоҳ ҳар бир нарсани билувчи Зотдир'}, {'chapter': 4, 'verse': 33, 'text': 'وَلِكُلّٖ جَعَلۡنَا مَوَٰلِيَ مِمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَۚ وَٱلَّذِينَ عَقَدَتۡ أَيۡمَٰنُكُمۡ فَـَٔاتُوهُمۡ نَصِيبَهُمۡۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا\nОта-она ва қариндошлар қолдирган нарсага ҳар бир киши учун меросхўрлар қилдик. Қасамингиз ила боғланганларга насибасини беринг. Албатта, Аллоҳ ҳар бир нарсага шоҳиддир'}, {'chapter': 4, 'verse': 34, 'text': 'ٱلرِّجَالُ قَوَّـٰمُونَ عَلَى ٱلنِّسَآءِ بِمَا فَضَّلَ ٱللَّهُ بَعۡضَهُمۡ عَلَىٰ بَعۡضٖ وَبِمَآ أَنفَقُواْ مِنۡ أَمۡوَٰلِهِمۡۚ فَٱلصَّـٰلِحَٰتُ قَٰنِتَٰتٌ حَٰفِظَٰتٞ لِّلۡغَيۡبِ بِمَا حَفِظَ ٱللَّهُۚ وَٱلَّـٰتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَٱهۡجُرُوهُنَّ فِي ٱلۡمَضَاجِعِ وَٱضۡرِبُوهُنَّۖ فَإِنۡ أَطَعۡنَكُمۡ فَلَا تَبۡغُواْ عَلَيۡهِنَّ سَبِيلًاۗ إِنَّ ٱللَّهَ كَانَ عَلِيّٗا كَبِيرٗا\nАллоҳ баъзиларини баъзиларидан устун қилгани ва молларидан сарфлаганлари учун эркаклар аёлларга раҳбардирлар. Солиҳа аёллар — итоаткор ва Аллоҳнинг ҳифзи-ҳимояси бўйича ғойиб (эр)ларининг муҳофазасини қилувчилардир. Бош кўтаришларидан хавф қилинган аёлларга ваъз-насиҳат қилинг, ётоқларида ҳижрон қилинг ва уринг. Агар итоат қилсалар, уларга қарши бошқа йўл ахтарманг. Албатта, Аллоҳ юксак мақомли буюк Зотдир'}, {'chapter': 4, 'verse': 35, 'text': 'وَإِنۡ خِفۡتُمۡ شِقَاقَ بَيۡنِهِمَا فَٱبۡعَثُواْ حَكَمٗا مِّنۡ أَهۡلِهِۦ وَحَكَمٗا مِّنۡ أَهۡلِهَآ إِن يُرِيدَآ إِصۡلَٰحٗا يُوَفِّقِ ٱللَّهُ بَيۡنَهُمَآۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا خَبِيرٗا\nАгар икковларининг оралари бузилишидан қўрқсангиз, эрнинг аҳлидан бир ҳакам ва хотиннинг аҳлидан бир ҳакам ажратинг. Агар улар ислоҳни ирода қилсалар, Аллоҳ эр-хотиннинг орасини мувофиқлаштирур. Албатта, Аллоҳ билувчи ва хабардор Зотдир'}, {'chapter': 4, 'verse': 36, 'text': '۞وَٱعۡبُدُواْ ٱللَّهَ وَلَا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنٗا وَبِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱلۡجَارِ ذِي ٱلۡقُرۡبَىٰ وَٱلۡجَارِ ٱلۡجُنُبِ وَٱلصَّاحِبِ بِٱلۡجَنۢبِ وَٱبۡنِ ٱلسَّبِيلِ وَمَا مَلَكَتۡ أَيۡمَٰنُكُمۡۗ إِنَّ ٱللَّهَ لَا يُحِبُّ مَن كَانَ مُخۡتَالٗا فَخُورًا\nВа Аллоҳга ибодат қилинглар ва Унга ҳеч нарсани ширк келтирманглар. Ота-онага, қариндошларга, етимларга, мискинларга, яқин қўшниларга, ён қўшниларга, ёнбошдаги соҳибларга, кўчада қолганларга ва қўлингизда мулк бўлганларга яхшилик қилинглар. Албатта, Аллоҳ ўзини юқори тутувчиларни ва одамлар устидан фахр қилувчиларни хуш кўрмас'}, {'chapter': 4, 'verse': 37, 'text': 'ٱلَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِ وَيَكۡتُمُونَ مَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا\nУлар бахиллик қиладиган, одамларни бахилликка буюрадиган ва Аллоҳ уларга Ўз фазлидан берган нарсаларни беркитадиганлардир. Ва кофирларга хорловчи азобни тайёрлаганмиз'}, {'chapter': 4, 'verse': 38, 'text': 'وَٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ رِئَآءَ ٱلنَّاسِ وَلَا يُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡيَوۡمِ ٱلۡأٓخِرِۗ وَمَن يَكُنِ ٱلشَّيۡطَٰنُ لَهُۥ قَرِينٗا فَسَآءَ قَرِينٗا\nУлар молларини кишилар кўриши учун сарфларлар. Ҳамда Аллоҳга ва қиёмат кунига иймон келтирмаслар. Кимнинг яқини шайтон бўлса, у қандай ҳам ёмон яқин'}, {'chapter': 4, 'verse': 39, 'text': 'وَمَاذَا عَلَيۡهِمۡ لَوۡ ءَامَنُواْ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَأَنفَقُواْ مِمَّا رَزَقَهُمُ ٱللَّهُۚ وَكَانَ ٱللَّهُ بِهِمۡ عَلِيمًا\nАгар Аллоҳга ва қиёмат кунига иймон келтирсалар, Аллоҳ уларга ризқ қилиб берган нарсадан нафақа қилсалар, уларга нима зарар бўладир? Аллоҳ уларни яхши билувчи Зотдир'}, {'chapter': 4, 'verse': 40, 'text': 'إِنَّ ٱللَّهَ لَا يَظۡلِمُ مِثۡقَالَ ذَرَّةٖۖ وَإِن تَكُ حَسَنَةٗ يُضَٰعِفۡهَا وَيُؤۡتِ مِن لَّدُنۡهُ أَجۡرًا عَظِيمٗا\nАлбатта, Аллоҳ заррача ҳам зулм қилмас. Агар яхшилик бўлса, бир неча бор кўпайтирур ва Ўз ҳузуридан улуғ ажр берур'}, {'chapter': 4, 'verse': 41, 'text': 'فَكَيۡفَ إِذَا جِئۡنَا مِن كُلِّ أُمَّةِۭ بِشَهِيدٖ وَجِئۡنَا بِكَ عَلَىٰ هَـٰٓؤُلَآءِ شَهِيدٗا\nҲар бир умматдан бир гувоҳ келтириб, сени уларнинг ҳаммасига гувоҳ этиб келтирган чоғимизда ҳол қандоқ бўлур'}, {'chapter': 4, 'verse': 42, 'text': 'يَوۡمَئِذٖ يَوَدُّ ٱلَّذِينَ كَفَرُواْ وَعَصَوُاْ ٱلرَّسُولَ لَوۡ تُسَوَّىٰ بِهِمُ ٱلۡأَرۡضُ وَلَا يَكۡتُمُونَ ٱللَّهَ حَدِيثٗا\nКуфр келтирганлар ва Расулга исён қилганлар ўша кунда ерга қоришиб кетишни истарлар ва Аллоҳдан ҳеч гапни яшира олмаслар'}, {'chapter': 4, 'verse': 43, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقۡرَبُواْ ٱلصَّلَوٰةَ وَأَنتُمۡ سُكَٰرَىٰ حَتَّىٰ تَعۡلَمُواْ مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِي سَبِيلٍ حَتَّىٰ تَغۡتَسِلُواْۚ وَإِن كُنتُم مَّرۡضَىٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآءَ أَحَدٞ مِّنكُم مِّنَ ٱلۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ ٱلنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَٱمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُمۡۗ إِنَّ ٱللَّهَ كَانَ عَفُوًّا غَفُورًا\nЭй, иймон келтирганлар! Маст ҳолингизда — то айтаётган гапингизни биладиган бўлмагунингизча — намозга яқинлашманг ва жунуб ҳолингизда ҳам, то ғусл қилмагунингизча. Магар йўлдан ўтувчи бўлса, майли. Агар бемор ёки сафарда бўлсангиз ёки сиздан бирингиз таҳоратхонадан келса ёхуд аёлларга яқинлашган бўлсангиз-у, сув топа олмасангиз, покиза тупроқ-ла таяммум қилинг. Бас, юзингизга ва қўлларингизга масҳ тортинг. Албатта, Аллоҳ афв этувчи ва мағфират қилувчи Зотдир'}, {'chapter': 4, 'verse': 44, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يَشۡتَرُونَ ٱلضَّلَٰلَةَ وَيُرِيدُونَ أَن تَضِلُّواْ ٱلسَّبِيلَ\nКитобдан насиба берилганларнинг залолатни сотиб олишларини ва сиз йўлдан адашишингизни исташларини кўрмайсизми'}, {'chapter': 4, 'verse': 45, 'text': 'وَٱللَّهُ أَعۡلَمُ بِأَعۡدَآئِكُمۡۚ وَكَفَىٰ بِٱللَّهِ وَلِيّٗا وَكَفَىٰ بِٱللَّهِ نَصِيرٗا\nАллоҳ сизнинг душманларингизни билувчи Зотдир. Валийликка Аллоҳнинг Ўзи кифоя қилур. Нусрат берувчиликка Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 46, 'text': 'مِّنَ ٱلَّذِينَ هَادُواْ يُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَيَقُولُونَ سَمِعۡنَا وَعَصَيۡنَا وَٱسۡمَعۡ غَيۡرَ مُسۡمَعٖ وَرَٰعِنَا لَيَّۢا بِأَلۡسِنَتِهِمۡ وَطَعۡنٗا فِي ٱلدِّينِۚ وَلَوۡ أَنَّهُمۡ قَالُواْ سَمِعۡنَا وَأَطَعۡنَا وَٱسۡمَعۡ وَٱنظُرۡنَا لَكَانَ خَيۡرٗا لَّهُمۡ وَأَقۡوَمَ وَلَٰكِن لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا\nЯҳудий бўлганлардан калималарни ўз жойидан бузиб ўзгартирувчилар бор. Улар тилларини буриб ва динга таъна етказиб: «Эшитдик ва исён қилдик, эшит, эшитмай қолгур ва «Роина» дерлар. Агар улар, эшитдик, ва итоат қилдик, эшит ва бизга назар сол, деганларида, албатта, ўзларига яхши ва тўғри бўлар эди. Лекин Аллоҳ уларни куфрлари сабабли лаънатлади. Бас, озгиналаридан бошқалари иймон келтирмаслар'}, {'chapter': 4, 'verse': 47, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ ءَامِنُواْ بِمَا نَزَّلۡنَا مُصَدِّقٗا لِّمَا مَعَكُم مِّن قَبۡلِ أَن نَّطۡمِسَ وُجُوهٗا فَنَرُدَّهَا عَلَىٰٓ أَدۡبَارِهَآ أَوۡ نَلۡعَنَهُمۡ كَمَا لَعَنَّآ أَصۡحَٰبَ ٱلسَّبۡتِۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولًا\nЭй, китоб берилганлар! Юзларни бузиб, орқаларига бурмасимиздан ёхуд уларни шанба одамларини лаънатлаганимиздек лаънатламасимиздан олдин, сизлардаги нарсани тасдиқловчи бўлиб келган нарсага иймон келтиринг. Аллоҳнинг иши амалга ошгусидир'}, {'chapter': 4, 'verse': 48, 'text': 'إِنَّ ٱللَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَقَدِ ٱفۡتَرَىٰٓ إِثۡمًا عَظِيمًا\nАлбатта, Аллоҳ Ўзига ширк келтирилишини кечирмас. Ундан бошқа гуноҳни, кимни хоҳласа, кечирадир. Ким Аллоҳга ширк келтирса, шубҳасиз, катта гуноҳни тўқибдир'}, {'chapter': 4, 'verse': 49, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يُزَكُّونَ أَنفُسَهُمۚ بَلِ ٱللَّهُ يُزَكِّي مَن يَشَآءُ وَلَا يُظۡلَمُونَ فَتِيلًا\nЎзларини пок қилиб кўрсатаётганларни кўрмайсанми?! Йўқ! Аллоҳ кимни хоҳласа, шуни поклайдир. Ва қилча зулм қилинмаслар'}, {'chapter': 4, 'verse': 50, 'text': 'ٱنظُرۡ كَيۡفَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَكَفَىٰ بِهِۦٓ إِثۡمٗا مُّبِينًا\nУларнинг Аллоҳга ёлғон тўқишини кўрмайсанми?! Шунинг ўзи ҳам очиқ-ойдин гуноҳликка етарлидир'}, {'chapter': 4, 'verse': 51, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يُؤۡمِنُونَ بِٱلۡجِبۡتِ وَٱلطَّـٰغُوتِ وَيَقُولُونَ لِلَّذِينَ كَفَرُواْ هَـٰٓؤُلَآءِ أَهۡدَىٰ مِنَ ٱلَّذِينَ ءَامَنُواْ سَبِيلًا\nКитобдан насибадор бўлганларнинг ботилга ва тоғутга иймон келтиришларини ва куфр келтирганларни (кўрсатиб): «Анавилар, иймон келтирганлардан кўра ҳидоятлироқ йўлдалар», дейишларини кўрмайсанми'}, {'chapter': 4, 'verse': 52, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُۖ وَمَن يَلۡعَنِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ نَصِيرًا\nАна ўшаларни Аллоҳ лаънатлагандир. Кимни Аллоҳ лаънатласа, унга ҳеч нусратчи тополмассан'}, {'chapter': 4, 'verse': 53, 'text': 'أَمۡ لَهُمۡ نَصِيبٞ مِّنَ ٱلۡمُلۡكِ فَإِذٗا لَّا يُؤۡتُونَ ٱلنَّاسَ نَقِيرًا\nЁки улар мулкдан насибадорлар-у, шундай тақдирда ундан одамларга зиғирча ҳам бермасларми'}, {'chapter': 4, 'verse': 54, 'text': 'أَمۡ يَحۡسُدُونَ ٱلنَّاسَ عَلَىٰ مَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۖ فَقَدۡ ءَاتَيۡنَآ ءَالَ إِبۡرَٰهِيمَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَءَاتَيۡنَٰهُم مُّلۡكًا عَظِيمٗا\nЁки Аллоҳ одамларга Ўз фазлидан берган нарсаларга ҳасад қилмоқдаларми? Батаҳқиқ, Иброҳим оиласига китобни, ҳикматни ва улуғ мулкни берганмиз-ку'}, {'chapter': 4, 'verse': 55, 'text': 'فَمِنۡهُم مَّنۡ ءَامَنَ بِهِۦ وَمِنۡهُم مَّن صَدَّ عَنۡهُۚ وَكَفَىٰ بِجَهَنَّمَ سَعِيرًا\nБас, уларнинг ичида у(Муҳаммад)га иймон келтирганлар ҳам бор, ундан юз ўгириб куфр келтирганлар ҳам бор. Жаҳаннам куйдириш азобига кифоя қилур'}, {'chapter': 4, 'verse': 56, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا سَوۡفَ نُصۡلِيهِمۡ نَارٗا كُلَّمَا نَضِجَتۡ جُلُودُهُم بَدَّلۡنَٰهُمۡ جُلُودًا غَيۡرَهَا لِيَذُوقُواْ ٱلۡعَذَابَۗ إِنَّ ٱللَّهَ كَانَ عَزِيزًا حَكِيمٗا\nОятларимизга куфр келтирганларни, албатта, дўзахга киритармиз. Қачонки, терилари етиб пишганда, азобни тортишлари учун бошқа тери алмаштирармиз. Албатта, Аллоҳ Азийз ва Ҳакийм Зотдир'}, {'chapter': 4, 'verse': 57, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَنُدۡخِلُهُمۡ ظِلّٗا ظَلِيلًا\nИймон келтирган ва солиҳ амалларни қилганларни, албатта, остидан анҳорлар оқиб турган жаннатларга киритармиз. Унда абадий, боқий қолурлар. Унда уларга покиза жуфтлар бор. Ва уларни қуюқ сояларга киритармиз'}, {'chapter': 4, 'verse': 58, 'text': '۞إِنَّ ٱللَّهَ يَأۡمُرُكُمۡ أَن تُؤَدُّواْ ٱلۡأَمَٰنَٰتِ إِلَىٰٓ أَهۡلِهَا وَإِذَا حَكَمۡتُم بَيۡنَ ٱلنَّاسِ أَن تَحۡكُمُواْ بِٱلۡعَدۡلِۚ إِنَّ ٱللَّهَ نِعِمَّا يَعِظُكُم بِهِۦٓۗ إِنَّ ٱللَّهَ كَانَ سَمِيعَۢا بَصِيرٗا\nАлбатта, Аллоҳ сизларга омонатларни ўз аҳлига эриштиришни ва агар одамлар орасида ҳукм қилсангиз, адолат ила ҳукм қилишни амр қилур. Аллоҳ сизларга қандай ҳам яхши ваъз қилур! Албатта, Аллоҳ эшитувчи ва кўрувчи Зотдир'}, {'chapter': 4, 'verse': 59, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَأُوْلِي ٱلۡأَمۡرِ مِنكُمۡۖ فَإِن تَنَٰزَعۡتُمۡ فِي شَيۡءٖ فَرُدُّوهُ إِلَى ٱللَّهِ وَٱلرَّسُولِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلًا\nЭй, иймон келтирганлар! Аллоҳга итоат қилинг, Пайғамбарга ва ўзингиздан бўлган ишбошиларга итоат қилинг. Бирор нарса ҳақида тортишиб қолсангиз, агар Аллоҳ ва охират кунига иймон келтирган бўлсангиз, уни Аллоҳга ва Пайғамбарга қайтаринг. Ана шундай қилиш хайрли ва оқибати яхшидир'}, {'chapter': 4, 'verse': 60, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يَزۡعُمُونَ أَنَّهُمۡ ءَامَنُواْ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ يُرِيدُونَ أَن يَتَحَاكَمُوٓاْ إِلَى ٱلطَّـٰغُوتِ وَقَدۡ أُمِرُوٓاْ أَن يَكۡفُرُواْ بِهِۦۖ وَيُرِيدُ ٱلشَّيۡطَٰنُ أَن يُضِلَّهُمۡ ضَلَٰلَۢا بَعِيدٗا\nСенга нозил бўлган нарсага ва сендан олдин нозил бўлган нарсага иймон келтирганларини даъво қилаётганларни кўрмайсанми?! Тоғутдан ҳукм сўраб боришни истарлар. Ҳолбуки, унга куфр келтиришга буюрилганлар. Шайтон эса, уларни йўлдан бутунлай адаштиришни истайдир'}, {'chapter': 4, 'verse': 61, 'text': 'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ رَأَيۡتَ ٱلۡمُنَٰفِقِينَ يَصُدُّونَ عَنكَ صُدُودٗا\nАгар уларга: «Аллоҳ нозил қилган нарсага ва Пайғамбар ҳузурига келинглар», дейилса, мунофиқлар сендан тамоман юз ўгирганларини кўрурсан'}, {'chapter': 4, 'verse': 62, 'text': 'فَكَيۡفَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ ثُمَّ جَآءُوكَ يَحۡلِفُونَ بِٱللَّهِ إِنۡ أَرَدۡنَآ إِلَّآ إِحۡسَٰنٗا وَتَوۡفِيقًا\nЭнди, қилган нарсалари туфайли уларга мусийбат етгандан сўнг, сенга келиб, Аллоҳ ила қасам ичиб: «Биз фақат яхшилик ва тавфиқни хоҳлаган эдик», дейишлари қандай бўлди'}, {'chapter': 4, 'verse': 63, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ يَعۡلَمُ ٱللَّهُ مَا فِي قُلُوبِهِمۡ فَأَعۡرِضۡ عَنۡهُمۡ وَعِظۡهُمۡ وَقُل لَّهُمۡ فِيٓ أَنفُسِهِمۡ قَوۡلَۢا بَلِيغٗا\nАна ўшаларнинг қалбларидагини Аллоҳ биладир. Бас, уларнинг қилганидан юз ўгир! Уларга ваъз қил! Ёлғиз ўзларига етказиб сўзла'}, {'chapter': 4, 'verse': 64, 'text': 'وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا لِيُطَاعَ بِإِذۡنِ ٱللَّهِۚ وَلَوۡ أَنَّهُمۡ إِذ ظَّلَمُوٓاْ أَنفُسَهُمۡ جَآءُوكَ فَٱسۡتَغۡفَرُواْ ٱللَّهَ وَٱسۡتَغۡفَرَ لَهُمُ ٱلرَّسُولُ لَوَجَدُواْ ٱللَّهَ تَوَّابٗا رَّحِيمٗا\nҚайси бир Пайғамбарни юборган бўлсак, Аллоҳнинг изни ила фақат итоат қилиниши учун юборганмиз. Агар улар ўзларига ўзлари зулм қилган чоғларида ҳузурингга келиб, Аллоҳга истиғфор айтганларида ва Пайғамбар ҳам улар учун истиғфор айтганида эди, албатта, Аллоҳ тавбаларни қабул этувчи ва раҳимли Зот эканини топган бўлур эдилар'}, {'chapter': 4, 'verse': 65, 'text': 'فَلَا وَرَبِّكَ لَا يُؤۡمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيۡنَهُمۡ ثُمَّ لَا يَجِدُواْ فِيٓ أَنفُسِهِمۡ حَرَجٗا مِّمَّا قَضَيۡتَ وَيُسَلِّمُواْ تَسۡلِيمٗا\nЙўқ, Роббингга қасамки, сени ўз ораларида чиққан келишмовчиликларга ҳакам қилмагунларича, кейин, чиқарган ҳукмингга дилларида танглик топмасдан, бутунлай таслим бўлмагунларича, зинҳор мўмин бўла олмаслар'}, {'chapter': 4, 'verse': 66, 'text': 'وَلَوۡ أَنَّا كَتَبۡنَا عَلَيۡهِمۡ أَنِ ٱقۡتُلُوٓاْ أَنفُسَكُمۡ أَوِ ٱخۡرُجُواْ مِن دِيَٰرِكُم مَّا فَعَلُوهُ إِلَّا قَلِيلٞ مِّنۡهُمۡۖ وَلَوۡ أَنَّهُمۡ فَعَلُواْ مَا يُوعَظُونَ بِهِۦ لَكَانَ خَيۡرٗا لَّهُمۡ وَأَشَدَّ تَثۡبِيتٗا\nАгар Биз уларга, ўзларингизни ўлдиринг ёки ўзингизни диёрларингиздан чиқаринг, деб фарз қилганимизда, озгиналаридан бошқаси буни қилмас эдилар. Улар ўзларига ваъз қилинган нарсаларни қилганларида эди, ўзларига яхши ва саботлари мустаҳкам бўлар эди'}, {'chapter': 4, 'verse': 67, 'text': 'وَإِذٗا لَّأٓتَيۡنَٰهُم مِّن لَّدُنَّآ أَجۡرًا عَظِيمٗا\nУ ҳолда, Биз ҳам уларга Ўз ҳузуримиздан улуғ ажр берган бўлур эдик'}, {'chapter': 4, 'verse': 68, 'text': 'وَلَهَدَيۡنَٰهُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا\nВа уларни тўғри йўлга ҳидоят қилур эдик'}, {'chapter': 4, 'verse': 69, 'text': 'وَمَن يُطِعِ ٱللَّهَ وَٱلرَّسُولَ فَأُوْلَـٰٓئِكَ مَعَ ٱلَّذِينَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِم مِّنَ ٱلنَّبِيِّـۧنَ وَٱلصِّدِّيقِينَ وَٱلشُّهَدَآءِ وَٱلصَّـٰلِحِينَۚ وَحَسُنَ أُوْلَـٰٓئِكَ رَفِيقٗا\nКим Аллоҳга ва Расулга итоат қилса, бас, ана ўшалар Аллоҳ неъмат берган набийлар, сиддиқлар, шаҳидлар ва солиҳлар билан биргадирлар. Ва улар қандай ҳам яхши рафиқ (ҳамроҳ)лардир'}, {'chapter': 4, 'verse': 70, 'text': 'ذَٰلِكَ ٱلۡفَضۡلُ مِنَ ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ عَلِيمٗا\nБу — Аллоҳдан бўлган фазлдир ва билгувчиликда Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 71, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ خُذُواْ حِذۡرَكُمۡ فَٱنفِرُواْ ثُبَاتٍ أَوِ ٱنفِرُواْ جَمِيعٗا\nЭй, иймон келтирганлар! Ўзингизни ҳушёр тутинг ва гуруҳ-гуруҳ бўлиб қўзғалинг ёки тўп ҳолингизда қўзғалинг'}, {'chapter': 4, 'verse': 72, 'text': 'وَإِنَّ مِنكُمۡ لَمَن لَّيُبَطِّئَنَّ فَإِنۡ أَصَٰبَتۡكُم مُّصِيبَةٞ قَالَ قَدۡ أَنۡعَمَ ٱللَّهُ عَلَيَّ إِذۡ لَمۡ أَكُن مَّعَهُمۡ شَهِيدٗا\nАлбатта, ичингизда ортда қоладиганлар ҳам бор. Агар сизга мусийбат етса, у: «Аллоҳ Ўзи менга неъмат берди, яхшиямки, улар билан бирга ҳозир бўлмаганим», дейди'}, {'chapter': 4, 'verse': 73, 'text': 'وَلَئِنۡ أَصَٰبَكُمۡ فَضۡلٞ مِّنَ ٱللَّهِ لَيَقُولَنَّ كَأَن لَّمۡ تَكُنۢ بَيۡنَكُمۡ وَبَيۡنَهُۥ مَوَدَّةٞ يَٰلَيۡتَنِي كُنتُ مَعَهُمۡ فَأَفُوزَ فَوۡزًا عَظِيمٗا\nАгар сизга Аллоҳдан фазл етса, худди сиз билан унинг орасида дўстлик бўлмагандек, «Қани энди, мен ҳам улар билан бўлганимда, улуғ зафар қозонар эдим», дейди'}, {'chapter': 4, 'verse': 74, 'text': '۞فَلۡيُقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ ٱلَّذِينَ يَشۡرُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا بِٱلۡأٓخِرَةِۚ وَمَن يُقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ فَيُقۡتَلۡ أَوۡ يَغۡلِبۡ فَسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا\nОхират учун бу дунё ҳаётини сотадиганлар Аллоҳнинг йўлида жанг қилсинлар. Ким Аллоҳнинг йўлида жанг қилиб ўлдирилса ёки ғолиб келса, Биз унга, албатта, улуғ ажрни берурмиз'}, {'chapter': 4, 'verse': 75, 'text': 'وَمَا لَكُمۡ لَا تُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِ وَٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلرِّجَالِ وَٱلنِّسَآءِ وَٱلۡوِلۡدَٰنِ ٱلَّذِينَ يَقُولُونَ رَبَّنَآ أَخۡرِجۡنَا مِنۡ هَٰذِهِ ٱلۡقَرۡيَةِ ٱلظَّالِمِ أَهۡلُهَا وَٱجۡعَل لَّنَا مِن لَّدُنكَ وَلِيّٗا وَٱجۡعَل لَّنَا مِن لَّدُنكَ نَصِيرًا\nНима учун Аллоҳнинг йўлида ва: «Эй Роббимиз, бизни аҳли золим шаҳардан чиқаргин, бизга Ўз томонингдан дўст ато қилгин ва Ўз томонингдан нусратчи ато қилгин» деяётган заифҳол эркагу аёл ва болалар йўлида жанг қилмайсизлар'}, {'chapter': 4, 'verse': 76, 'text': 'ٱلَّذِينَ ءَامَنُواْ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِۖ وَٱلَّذِينَ كَفَرُواْ يُقَٰتِلُونَ فِي سَبِيلِ ٱلطَّـٰغُوتِ فَقَٰتِلُوٓاْ أَوۡلِيَآءَ ٱلشَّيۡطَٰنِۖ إِنَّ كَيۡدَ ٱلشَّيۡطَٰنِ كَانَ ضَعِيفًا\nИймон келтирганлар Аллоҳнинг йўлида жанг қилурлар. Куфр келтирганлар тоғутнинг йўлида жанг қилурлар. Бас, шайтоннинг дўстларига қарши жанг қилинг. Албатта, шайтоннинг ҳийласи заифдир'}, {'chapter': 4, 'verse': 77, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ قِيلَ لَهُمۡ كُفُّوٓاْ أَيۡدِيَكُمۡ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ فَلَمَّا كُتِبَ عَلَيۡهِمُ ٱلۡقِتَالُ إِذَا فَرِيقٞ مِّنۡهُمۡ يَخۡشَوۡنَ ٱلنَّاسَ كَخَشۡيَةِ ٱللَّهِ أَوۡ أَشَدَّ خَشۡيَةٗۚ وَقَالُواْ رَبَّنَا لِمَ كَتَبۡتَ عَلَيۡنَا ٱلۡقِتَالَ لَوۡلَآ أَخَّرۡتَنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖۗ قُلۡ مَتَٰعُ ٱلدُّنۡيَا قَلِيلٞ وَٱلۡأٓخِرَةُ خَيۡرٞ لِّمَنِ ٱتَّقَىٰ وَلَا تُظۡلَمُونَ فَتِيلًا\nҚўлингизни тийинг, намоз ўқиб, закот беринг, деб айтилган кимсаларни кўрмайсанми?! Уларга уруш фарз қилинганда, бирдан, бир гуруҳлари одамлардан худди Аллоҳдан қўрққандек ёки ундан ҳам қаттиқроқ қўрқишмоқда. Ва: «Эй Роббимиз, бизга урушни нега фарз қилдинг, бироз ортга суриб турсанг бўлмасмиди?!» демоқдалар. «Дунёнинг матоҳи оз, охират эса, тақво қилганларга яхшидир. Ва қилчалик зулм қилинмассизлар», деб айт'}, {'chapter': 4, 'verse': 78, 'text': 'أَيۡنَمَا تَكُونُواْ يُدۡرِككُّمُ ٱلۡمَوۡتُ وَلَوۡ كُنتُمۡ فِي بُرُوجٖ مُّشَيَّدَةٖۗ وَإِن تُصِبۡهُمۡ حَسَنَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِ ٱللَّهِۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِكَۚ قُلۡ كُلّٞ مِّنۡ عِندِ ٱللَّهِۖ فَمَالِ هَـٰٓؤُلَآءِ ٱلۡقَوۡمِ لَا يَكَادُونَ يَفۡقَهُونَ حَدِيثٗا\nҚаерда бўлсангиз ҳам, ҳаттоки мустаҳкам қалъаларда бўлсангиз ҳам, ўлим сизни топадир. Агар уларга яхшилик етса, бу Аллоҳдан, дерлар. Ёмонлик етганда эса, бу сендан, дерлар. «Ҳаммаси Аллоҳдан», деб айт! Анави қавмларга нима бўлдики, гапни тушунай демаслар'}, {'chapter': 4, 'verse': 79, 'text': 'مَّآ أَصَابَكَ مِنۡ حَسَنَةٖ فَمِنَ ٱللَّهِۖ وَمَآ أَصَابَكَ مِن سَيِّئَةٖ فَمِن نَّفۡسِكَۚ وَأَرۡسَلۡنَٰكَ لِلنَّاسِ رَسُولٗاۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا\nСенга етган яхшилик Аллоҳдандир. Ва сенга етган ёмонлик нафсингдандир. Биз сени одамларга Пайғамбар қилиб юбордик. Шоҳидликка Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 80, 'text': 'مَّن يُطِعِ ٱلرَّسُولَ فَقَدۡ أَطَاعَ ٱللَّهَۖ وَمَن تَوَلَّىٰ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗا\nКим Пайғамбарга итоат қилса, Аллоҳга итоат қилган бўлур. Ким юз ўгирса, Биз сени уларга қўриқчи қилиб юборганимиз йўқ'}, {'chapter': 4, 'verse': 81, 'text': 'وَيَقُولُونَ طَاعَةٞ فَإِذَا بَرَزُواْ مِنۡ عِندِكَ بَيَّتَ طَآئِفَةٞ مِّنۡهُمۡ غَيۡرَ ٱلَّذِي تَقُولُۖ وَٱللَّهُ يَكۡتُبُ مَا يُبَيِّتُونَۖ فَأَعۡرِضۡ عَنۡهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًا\nУлар, итоат, дерлар. Ҳузурингдан чиққач эса, бир тоифалари айтганингдан бошқага хуфёна тил бириктирарлар. Аллоҳ хуфёна тил бириктирган нарсаларини ёзиб қўядир. Бас, улардан юз ўгир ва Аллоҳга таваккал қил. Вакилликка Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 82, 'text': 'أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَۚ وَلَوۡ كَانَ مِنۡ عِندِ غَيۡرِ ٱللَّهِ لَوَجَدُواْ فِيهِ ٱخۡتِلَٰفٗا كَثِيرٗا\nҚуръонни тадаббур этиб кўрмайдиларми?! Агар у Аллоҳдан бошқанинг ҳузуридан бўлганида, ундан кўп ихтилофлар топар эдилар'}, {'chapter': 4, 'verse': 83, 'text': 'وَإِذَا جَآءَهُمۡ أَمۡرٞ مِّنَ ٱلۡأَمۡنِ أَوِ ٱلۡخَوۡفِ أَذَاعُواْ بِهِۦۖ وَلَوۡ رَدُّوهُ إِلَى ٱلرَّسُولِ وَإِلَىٰٓ أُوْلِي ٱلۡأَمۡرِ مِنۡهُمۡ لَعَلِمَهُ ٱلَّذِينَ يَسۡتَنۢبِطُونَهُۥ مِنۡهُمۡۗ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَٱتَّبَعۡتُمُ ٱلشَّيۡطَٰنَ إِلَّا قَلِيلٗا\nҚачонки уларга эминлик ёки ҳақ тўғрисида бир иш — хабар етса, уни таратурлар. Агар уни Пайғамбарга ва ўзларидан бўлган ишбошиларга ҳавола қилганларида эди, улардан иш негизини биладиганлари уни англаб етар эдилар. Аллоҳнинг фазли ва марҳамати бўлмаганда, озгинангиздан бошқангиз шайтонга эргашиб кетар эдингиз'}, {'chapter': 4, 'verse': 84, 'text': 'فَقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ لَا تُكَلَّفُ إِلَّا نَفۡسَكَۚ وَحَرِّضِ ٱلۡمُؤۡمِنِينَۖ عَسَى ٱللَّهُ أَن يَكُفَّ بَأۡسَ ٱلَّذِينَ كَفَرُواْۚ وَٱللَّهُ أَشَدُّ بَأۡسٗا وَأَشَدُّ تَنكِيلٗا\nАллоҳнинг йўлида жанг қил! Ўзингдан бошқадан масъул қилинмассан. Мўминларни қизиқтир. Шоядки, Аллоҳ куфр келтирганларнинг шиддатини дафъ қилса. Аллоҳ шиддати қаттиқ ва азоби ортиқ Зотдир'}, {'chapter': 4, 'verse': 85, 'text': 'مَّن يَشۡفَعۡ شَفَٰعَةً حَسَنَةٗ يَكُن لَّهُۥ نَصِيبٞ مِّنۡهَاۖ وَمَن يَشۡفَعۡ شَفَٰعَةٗ سَيِّئَةٗ يَكُن لَّهُۥ كِفۡلٞ مِّنۡهَاۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقِيتٗا\nКим яхши шафоат қилса, ундан ўз насибаси бўлур. Ким ёмон шафоат қилса, ундан ўз насибаси бўлур. Аллоҳ ҳар бир нарсанинг баҳосини берувчи Зотдир'}, {'chapter': 4, 'verse': 86, 'text': 'وَإِذَا حُيِّيتُم بِتَحِيَّةٖ فَحَيُّواْ بِأَحۡسَنَ مِنۡهَآ أَوۡ رُدُّوهَآۗ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٍ حَسِيبًا\nҚачонки сизга бир саломлашиш ила салом берилса, сиз ундан кўра яхшироқ алик олинг ёки худди ўзидек жавоб беринг. Албатта, Аллоҳ ҳар бир нарсанинг ҳисобини олувчи Зотдир'}, {'chapter': 4, 'verse': 87, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۗ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ حَدِيثٗا\nАллоҳдан ўзга ибодатга сазовор зот йўқ. У сизларни қоим бўлишида ҳеч шубҳа йўқ қиёмат куни албатта жамлайдир. Ким Аллоҳдан кўра ростгўйроқ'}, {'chapter': 4, 'verse': 88, 'text': '۞فَمَا لَكُمۡ فِي ٱلۡمُنَٰفِقِينَ فِئَتَيۡنِ وَٱللَّهُ أَرۡكَسَهُم بِمَا كَسَبُوٓاْۚ أَتُرِيدُونَ أَن تَهۡدُواْ مَنۡ أَضَلَّ ٱللَّهُۖ وَمَن يُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا\nСизга нима бўлдики, мунофиқлар ҳақида иккига бўлинасизлар?! Ҳолбуки, Аллоҳ уларни расво қилиб қўйди-ку. Аллоҳ адаштирган кимсаларни ҳидоят қилмоқчи бўласизларми?! Аллоҳ кимни адаштирган бўлса, унга йўл топа олмассан'}, {'chapter': 4, 'verse': 89, 'text': 'وَدُّواْ لَوۡ تَكۡفُرُونَ كَمَا كَفَرُواْ فَتَكُونُونَ سَوَآءٗۖ فَلَا تَتَّخِذُواْ مِنۡهُمۡ أَوۡلِيَآءَ حَتَّىٰ يُهَاجِرُواْ فِي سَبِيلِ ٱللَّهِۚ فَإِن تَوَلَّوۡاْ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَيۡثُ وَجَدتُّمُوهُمۡۖ وَلَا تَتَّخِذُواْ مِنۡهُمۡ وَلِيّٗا وَلَا نَصِيرًا\nУлар сиз ҳам худди ўзларидек кофир бўлиб, уларга тенглашишингизни хоҳларлар. Токи Аллоҳ йўлида ҳижрат қилмагунларича, улардан дўст тутманг. Агар юз ўгириб кетсалар, бас, уларни тутинг ва қаерда топсангиз, ўлдиринг. Улардан дўст ҳам, ёрдамчи ҳам тутманг'}, {'chapter': 4, 'verse': 90, 'text': 'إِلَّا ٱلَّذِينَ يَصِلُونَ إِلَىٰ قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٌ أَوۡ جَآءُوكُمۡ حَصِرَتۡ صُدُورُهُمۡ أَن يُقَٰتِلُوكُمۡ أَوۡ يُقَٰتِلُواْ قَوۡمَهُمۡۚ وَلَوۡ شَآءَ ٱللَّهُ لَسَلَّطَهُمۡ عَلَيۡكُمۡ فَلَقَٰتَلُوكُمۡۚ فَإِنِ ٱعۡتَزَلُوكُمۡ فَلَمۡ يُقَٰتِلُوكُمۡ وَأَلۡقَوۡاْ إِلَيۡكُمُ ٱلسَّلَمَ فَمَا جَعَلَ ٱللَّهُ لَكُمۡ عَلَيۡهِمۡ سَبِيلٗا\nМагар сиз билан орасида аҳднома бор қавмга етиб борсалар ёки сизлар билан ҳам, ўз қавмлари билан ҳам урушиш кўнгилларига сиғмасдан ҳузурингизга келсалар, майли. Аллоҳ хоҳлаганида, уларни сиздан устун қилиб қўярди ва улар сизларга қарши уруш қилардилар. Агар сиздан четлансалар ва сиз билан урушмасдан, тинчликни таклиф қилсалар, Аллоҳ сизларга уларнинг зиддига йўл бермас'}, {'chapter': 4, 'verse': 91, 'text': 'سَتَجِدُونَ ءَاخَرِينَ يُرِيدُونَ أَن يَأۡمَنُوكُمۡ وَيَأۡمَنُواْ قَوۡمَهُمۡ كُلَّ مَا رُدُّوٓاْ إِلَى ٱلۡفِتۡنَةِ أُرۡكِسُواْ فِيهَاۚ فَإِن لَّمۡ يَعۡتَزِلُوكُمۡ وَيُلۡقُوٓاْ إِلَيۡكُمُ ٱلسَّلَمَ وَيَكُفُّوٓاْ أَيۡدِيَهُمۡ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡۚ وَأُوْلَـٰٓئِكُمۡ جَعَلۡنَا لَكُمۡ عَلَيۡهِمۡ سُلۡطَٰنٗا مُّبِينٗا\nСиздан ҳам, ўз қавмларидан ҳам омонда бўлишни хоҳлайдиган, фитнага қайтарилган чоғларида унга шўнғийдиган бошқа бировларни ҳам кўрасизлар. Агар сиздан четланмасалар, сизга тинчликни таклиф қилмасалар ва қўлларини тиймасалар, бас, уларни тутинг ва қаерда топсангиз, ўлдиринг. Ана ўшандайлар устидан сизга очиқ ҳақ бердик'}, {'chapter': 4, 'verse': 92, 'text': 'وَمَا كَانَ لِمُؤۡمِنٍ أَن يَقۡتُلَ مُؤۡمِنًا إِلَّا خَطَـٔٗاۚ وَمَن قَتَلَ مُؤۡمِنًا خَطَـٔٗا فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖ وَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦٓ إِلَّآ أَن يَصَّدَّقُواْۚ فَإِن كَانَ مِن قَوۡمٍ عَدُوّٖ لَّكُمۡ وَهُوَ مُؤۡمِنٞ فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ وَإِن كَانَ مِن قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞ فَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦ وَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ تَوۡبَةٗ مِّنَ ٱللَّهِۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nМўмин мўминни ўлдирмас. Магар билмасдан қилиши мумкин. Кимки мўминни билмасдан ўлдириб қўйса, бас, унга мўмин қулни озод қилиш ва (ўлганнинг) аҳли кечирмасалар, хун бериш вожиб бўлур. Агар у сизга душман қавмдан бўлиб, ўзи мўмин бўлса, мўмин қулни озод қилиш вожиб бўлур. Агар у сиз билан орасида аҳдномаси бор қавмдан бўлса, аҳлига бериладиган хун ва мўмин қулни озод қилиш вожиб бўлур. Ким топмаса, Аллоҳга тавба учун кетма-кет икки ой рўза тутиш вожиб бўлур. Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 93, 'text': 'وَمَن يَقۡتُلۡ مُؤۡمِنٗا مُّتَعَمِّدٗا فَجَزَآؤُهُۥ جَهَنَّمُ خَٰلِدٗا فِيهَا وَغَضِبَ ٱللَّهُ عَلَيۡهِ وَلَعَنَهُۥ وَأَعَدَّ لَهُۥ عَذَابًا عَظِيمٗا\nКим бир мўминни қасдан ўлдирса, унинг жазоси жаҳаннамдир. Унда абадий қолур. Унга Аллоҳнинг ғазаби ва лаънати ёғилур. Ва Аллоҳ унга улкан азобни тайёрлагандир'}, {'chapter': 4, 'verse': 94, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا ضَرَبۡتُمۡ فِي سَبِيلِ ٱللَّهِ فَتَبَيَّنُواْ وَلَا تَقُولُواْ لِمَنۡ أَلۡقَىٰٓ إِلَيۡكُمُ ٱلسَّلَٰمَ لَسۡتَ مُؤۡمِنٗا تَبۡتَغُونَ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فَعِندَ ٱللَّهِ مَغَانِمُ كَثِيرَةٞۚ كَذَٰلِكَ كُنتُم مِّن قَبۡلُ فَمَنَّ ٱللَّهُ عَلَيۡكُمۡ فَتَبَيَّنُوٓاْۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nЭй, иймон келтирганлар! Аллоҳнинг йўлида жиҳодга чиқсангиз, аниқлаб олинглар ва сизга салом берган кимсага, мўмин эмассан, деманглар. Бу дунё ҳаётининг ўткинчи ўлжасини истайсизлар, Аллоҳнинг ҳузурида эса, кўплаб ўлжалар бор. Авваллари сиз ҳам шундай эдингиз, Аллоҳ сизга неъмат берди. Бас, аниқлаб олинг. Албатта, Аллоҳ нима қилаётганингиздан хабардордир'}, {'chapter': 4, 'verse': 95, 'text': 'لَّا يَسۡتَوِي ٱلۡقَٰعِدُونَ مِنَ ٱلۡمُؤۡمِنِينَ غَيۡرُ أُوْلِي ٱلضَّرَرِ وَٱلۡمُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ فَضَّلَ ٱللَّهُ ٱلۡمُجَٰهِدِينَ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ عَلَى ٱلۡقَٰعِدِينَ دَرَجَةٗۚ وَكُلّٗا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَفَضَّلَ ٱللَّهُ ٱلۡمُجَٰهِدِينَ عَلَى ٱلۡقَٰعِدِينَ أَجۡرًا عَظِيمٗا\nМўминлардан бешикаст бўла туриб ўтириб қолганлари билан Аллоҳнинг йўлида моллари ва жонлари ила жиҳод қилувчилари баробар бўлмаслар. Аллоҳ моллари ва жонлари билан жидду жаҳд қилувчиларнинг даражасини ўтириб қолувчилардан афзал қилди. Аллоҳ ҳаммага яхшиликни ваъда қилган. Аллоҳ мужоҳидларни ўтириб қолувчилардан улуғ ажр ила афзал қилган'}, {'chapter': 4, 'verse': 96, 'text': 'دَرَجَٰتٖ مِّنۡهُ وَمَغۡفِرَةٗ وَرَحۡمَةٗۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمًا\nЎзидан бўлган даражалар ила, мағфират ва раҳмат ила (афзал қилган). Ва Аллоҳ мағфират қилувчи ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 97, 'text': 'إِنَّ ٱلَّذِينَ تَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡ قَالُواْ فِيمَ كُنتُمۡۖ قَالُواْ كُنَّا مُسۡتَضۡعَفِينَ فِي ٱلۡأَرۡضِۚ قَالُوٓاْ أَلَمۡ تَكُنۡ أَرۡضُ ٱللَّهِ وَٰسِعَةٗ فَتُهَاجِرُواْ فِيهَاۚ فَأُوْلَـٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَسَآءَتۡ مَصِيرًا\nЎзига зулм қилувчи ҳолида жони олинаётганларга фаришталар: «Нима қилаётган эдинглар?» дерлар. Улар: «Ер юзида бечора эдик», дерлар. «Аллоҳнинг ери кенг эди-ку, ҳижрат қилсангиз бўлмасмиди?!» дерлар. Ана ўшаларнинг жойи жаҳаннамдир. У қандай ҳам ёмон жой'}, {'chapter': 4, 'verse': 98, 'text': 'إِلَّا ٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلرِّجَالِ وَٱلنِّسَآءِ وَٱلۡوِلۡدَٰنِ لَا يَسۡتَطِيعُونَ حِيلَةٗ وَلَا يَهۡتَدُونَ سَبِيلٗا\nФақат эркаклар, аёллар ва болалардан иборат, бирон чорага қодир бўлмаган, йўлни топа олмаган бечораларгина бундан мустасно'}, {'chapter': 4, 'verse': 99, 'text': 'فَأُوْلَـٰٓئِكَ عَسَى ٱللَّهُ أَن يَعۡفُوَ عَنۡهُمۡۚ وَكَانَ ٱللَّهُ عَفُوًّا غَفُورٗا\nАна ўшалар, шоядки, Аллоҳ уларни афв этса. Зотан, Аллоҳ афв этувчи ва мағфират қилувчи Зотдир'}, {'chapter': 4, 'verse': 100, 'text': '۞وَمَن يُهَاجِرۡ فِي سَبِيلِ ٱللَّهِ يَجِدۡ فِي ٱلۡأَرۡضِ مُرَٰغَمٗا كَثِيرٗا وَسَعَةٗۚ وَمَن يَخۡرُجۡ مِنۢ بَيۡتِهِۦ مُهَاجِرًا إِلَى ٱللَّهِ وَرَسُولِهِۦ ثُمَّ يُدۡرِكۡهُ ٱلۡمَوۡتُ فَقَدۡ وَقَعَ أَجۡرُهُۥ عَلَى ٱللَّهِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nКим Аллоҳнинг йўлида ҳижрат қилса, ер юзида кўпгина паноҳгоҳлар ва кенгчилик топадир. Ким уйидан Аллоҳ ва Расулга ҳижрат қилиб чиқса-ю, сўнгра унга ўлим етса, албатта, унинг ажри Аллоҳ зиммасига тушадир. Ва Аллоҳ мағфират қилувчи ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 101, 'text': 'وَإِذَا ضَرَبۡتُمۡ فِي ٱلۡأَرۡضِ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَقۡصُرُواْ مِنَ ٱلصَّلَوٰةِ إِنۡ خِفۡتُمۡ أَن يَفۡتِنَكُمُ ٱلَّذِينَ كَفَرُوٓاْۚ إِنَّ ٱلۡكَٰفِرِينَ كَانُواْ لَكُمۡ عَدُوّٗا مُّبِينٗا\nЕр юзида жавлон урганингизда, куфр келтирганлар сизга фитна қилишидан хавф қилсангиз, намозни қаср қилиб адо этмоғингизда гуноҳ йўқдир. Албатта, кофирлар сизга очиқ душмандирлар'}, {'chapter': 4, 'verse': 102, 'text': 'وَإِذَا كُنتَ فِيهِمۡ فَأَقَمۡتَ لَهُمُ ٱلصَّلَوٰةَ فَلۡتَقُمۡ طَآئِفَةٞ مِّنۡهُم مَّعَكَ وَلۡيَأۡخُذُوٓاْ أَسۡلِحَتَهُمۡۖ فَإِذَا سَجَدُواْ فَلۡيَكُونُواْ مِن وَرَآئِكُمۡ وَلۡتَأۡتِ طَآئِفَةٌ أُخۡرَىٰ لَمۡ يُصَلُّواْ فَلۡيُصَلُّواْ مَعَكَ وَلۡيَأۡخُذُواْ حِذۡرَهُمۡ وَأَسۡلِحَتَهُمۡۗ وَدَّ ٱلَّذِينَ كَفَرُواْ لَوۡ تَغۡفُلُونَ عَنۡ أَسۡلِحَتِكُمۡ وَأَمۡتِعَتِكُمۡ فَيَمِيلُونَ عَلَيۡكُم مَّيۡلَةٗ وَٰحِدَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ إِن كَانَ بِكُمۡ أَذٗى مِّن مَّطَرٍ أَوۡ كُنتُم مَّرۡضَىٰٓ أَن تَضَعُوٓاْ أَسۡلِحَتَكُمۡۖ وَخُذُواْ حِذۡرَكُمۡۗ إِنَّ ٱللَّهَ أَعَدَّ لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا\nУлар ичида бўлсанг-у, намоз адо қилмоқчи бўлсанг, улардан бир тоифаси сен билан турсин ва силоҳларини ўзлари билан олсинлар. Сажда қилганларидан кейин орқангизга ўтсинлар. Намоз ўқимаган бошқа тоифа келиб, сен билан намоз ўқисин. Эҳтиёт бўлиб, силоҳларини ўзлари билан олсинлар. Силоҳларингиз ва матоҳларингиздан ғофил бўлсангиз, куфр келтирганлар устингиздан бирдан ҳамла қилишни истарлар. Ёмғирдан озор етса ёки бемор бўлсангиз, силоҳларингизни қўймоғингизда гуноҳ йўқдир. Ҳушёр бўлинглар! Албатта, Аллоҳ кофирларга хорловчи азоб тайёрлагандир'}, {'chapter': 4, 'verse': 103, 'text': 'فَإِذَا قَضَيۡتُمُ ٱلصَّلَوٰةَ فَٱذۡكُرُواْ ٱللَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِكُمۡۚ فَإِذَا ٱطۡمَأۡنَنتُمۡ فَأَقِيمُواْ ٱلصَّلَوٰةَۚ إِنَّ ٱلصَّلَوٰةَ كَانَتۡ عَلَى ٱلۡمُؤۡمِنِينَ كِتَٰبٗا مَّوۡقُوتٗا\nНамозни адо этганингиздан сўнг, Аллоҳни турган, ўтирган ва ёнбошлаган ҳолингизда эсланг. Хотиржам бўлганингизда намозни тўлиқ адо этинг. Албатта, намоз мўминларга вақтида фарз қилингандир'}, {'chapter': 4, 'verse': 104, 'text': 'وَلَا تَهِنُواْ فِي ٱبۡتِغَآءِ ٱلۡقَوۡمِۖ إِن تَكُونُواْ تَأۡلَمُونَ فَإِنَّهُمۡ يَأۡلَمُونَ كَمَا تَأۡلَمُونَۖ وَتَرۡجُونَ مِنَ ٱللَّهِ مَا لَا يَرۡجُونَۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمًا\nУ қавмни қувишда бўш келманг. Агар алам чекаётган бўлсангиз, улар ҳам сиз алам чекаётганингиздек алам чекмоқдалар. Сиз эса, Аллоҳдан улар умид қилмаган нарсани умид қилмоқдасиз. Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 105, 'text': 'إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ لِتَحۡكُمَ بَيۡنَ ٱلنَّاسِ بِمَآ أَرَىٰكَ ٱللَّهُۚ وَلَا تَكُن لِّلۡخَآئِنِينَ خَصِيمٗا\nАлбатта, Биз сенга китобни ҳақ ила одамлар орасида сенга Аллоҳ кўрсатганича ҳукм қилишинг учун туширдик. Сен хоинларнинг ёнини олувчи бўлма'}, {'chapter': 4, 'verse': 106, 'text': 'وَٱسۡتَغۡفِرِ ٱللَّهَۖ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nАллоҳга истиғфор айт! Албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 107, 'text': 'وَلَا تُجَٰدِلۡ عَنِ ٱلَّذِينَ يَخۡتَانُونَ أَنفُسَهُمۡۚ إِنَّ ٱللَّهَ لَا يُحِبُّ مَن كَانَ خَوَّانًا أَثِيمٗا\nЎз жонларига хиёнат қиладиганларни мудофаа қилма. Албатта, Аллоҳ хиёнатчи ва гуноҳкор бўлган кимсани хуш кўрмас'}, {'chapter': 4, 'verse': 108, 'text': 'يَسۡتَخۡفُونَ مِنَ ٱلنَّاسِ وَلَا يَسۡتَخۡفُونَ مِنَ ٱللَّهِ وَهُوَ مَعَهُمۡ إِذۡ يُبَيِّتُونَ مَا لَا يَرۡضَىٰ مِنَ ٱلۡقَوۡلِۚ وَكَانَ ٱللَّهُ بِمَا يَعۡمَلُونَ مُحِيطًا\nОдамлардан яширарлар ва Аллоҳдан яшира олмаслар. Ҳолбуки, У Ўзи рози бўлмайдиган гапни хуфёна келишаётганларида ҳам улар ила биргадир. Ва Аллоҳ улар қилаётган амалларни иҳота қилувчи Зотдир'}, {'chapter': 4, 'verse': 109, 'text': 'هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ جَٰدَلۡتُمۡ عَنۡهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فَمَن يُجَٰدِلُ ٱللَّهَ عَنۡهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ أَم مَّن يَكُونُ عَلَيۡهِمۡ وَكِيلٗا\nҲой, сизлар! Бу ҳаёти дунёда уларнинг ёнини олдингиз. Қиёмат кунида Аллоҳ олдида уларнинг ёнини ким оладир ёки ким уларга вакил бўладир'}, {'chapter': 4, 'verse': 110, 'text': 'وَمَن يَعۡمَلۡ سُوٓءًا أَوۡ يَظۡلِمۡ نَفۡسَهُۥ ثُمَّ يَسۡتَغۡفِرِ ٱللَّهَ يَجِدِ ٱللَّهَ غَفُورٗا رَّحِيمٗا\nКим ёмонлик қилса ёки ўзига зулм этса, сўнгра Аллоҳга истиғфор айтса, Аллоҳни мағфиратли ва раҳимли Зот топадир'}, {'chapter': 4, 'verse': 111, 'text': 'وَمَن يَكۡسِبۡ إِثۡمٗا فَإِنَّمَا يَكۡسِبُهُۥ عَلَىٰ نَفۡسِهِۦۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nКим гуноҳ қилса, уни фақатгина ўз зарарига қиладир. Ва Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 112, 'text': 'وَمَن يَكۡسِبۡ خَطِيٓـَٔةً أَوۡ إِثۡمٗا ثُمَّ يَرۡمِ بِهِۦ بَرِيٓـٔٗا فَقَدِ ٱحۡتَمَلَ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا\nКим хато ёки гуноҳ қилса-ю, сўнгра уни беайбга ағдарса, шубҳасиз, бўҳтон ва очиқ гуноҳни ўз бўйнига олибдир'}, {'chapter': 4, 'verse': 113, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكَ وَرَحۡمَتُهُۥ لَهَمَّت طَّآئِفَةٞ مِّنۡهُمۡ أَن يُضِلُّوكَ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡۖ وَمَا يَضُرُّونَكَ مِن شَيۡءٖۚ وَأَنزَلَ ٱللَّهُ عَلَيۡكَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَعَلَّمَكَ مَا لَمۡ تَكُن تَعۡلَمُۚ وَكَانَ فَضۡلُ ٱللَّهِ عَلَيۡكَ عَظِيمٗا\nАгар сенга Аллоҳнинг фазли ва раҳмати бўлмаганида, улардан бир тоифаси сени адаштиришга уринган эдилар. Улар фақат ўзларини адаштирадилар, холос. Сенга ҳеч зарар етказа олмаслар. Аллоҳ сенга китобни ва ҳикматни туширди ҳамда билмаган нарсангни билдирди. Ва сенга Аллоҳнинг фазли улуғ бўлди'}, {'chapter': 4, 'verse': 114, 'text': '۞لَّا خَيۡرَ فِي كَثِيرٖ مِّن نَّجۡوَىٰهُمۡ إِلَّا مَنۡ أَمَرَ بِصَدَقَةٍ أَوۡ مَعۡرُوفٍ أَوۡ إِصۡلَٰحِۭ بَيۡنَ ٱلنَّاسِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِ فَسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا\nУларнинг кўпгина махфий суҳбатларида яхшилик йўқ, магар ким садақага, яхшиликка ва одамлар орасида ислоҳга амр қилсагина, яхшилик бор. Ким ўша ишни Аллоҳнинг розилиги учун қилса, албатта, унга улуғ ажр берурмиз'}, {'chapter': 4, 'verse': 115, 'text': 'وَمَن يُشَاقِقِ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُ ٱلۡهُدَىٰ وَيَتَّبِعۡ غَيۡرَ سَبِيلِ ٱلۡمُؤۡمِنِينَ نُوَلِّهِۦ مَا تَوَلَّىٰ وَنُصۡلِهِۦ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرًا\nКим ўзига ҳидоят равшан бўлгандан кейин Пайғамбарга хилоф қилса ва мўминларнинг йўлидан бошқа йўлга юрса, кетган томонга қўйиб қўямиз ва жаҳаннамга киритамиз. У қандоқ ҳам ёмон жой'}, {'chapter': 4, 'verse': 116, 'text': 'إِنَّ ٱللَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَقَدۡ ضَلَّ ضَلَٰلَۢا بَعِيدًا\nАлбатта, Аллоҳ Ўзига ширк келтирилишини кечирмас. Ундан бошқани, кимни хоҳласа, кечирур. Ким Аллоҳга ширк келтирса, шубҳасиз, қаттиқ адашган бўлур'}, {'chapter': 4, 'verse': 117, 'text': 'إِن يَدۡعُونَ مِن دُونِهِۦٓ إِلَّآ إِنَٰثٗا وَإِن يَدۡعُونَ إِلَّا شَيۡطَٰنٗا مَّرِيدٗا\nУлар Ундан бошқа фақат аёл санамларга ибодат қилурлар ва фақат бебош шайтонга ибодат қилурлар'}, {'chapter': 4, 'verse': 118, 'text': 'لَّعَنَهُ ٱللَّهُۘ وَقَالَ لَأَتَّخِذَنَّ مِنۡ عِبَادِكَ نَصِيبٗا مَّفۡرُوضٗا\nАллоҳ уни лаънатлади. У эса: «Қасамки, бандаларингдан маълум насибани оламан'}, {'chapter': 4, 'verse': 119, 'text': 'وَلَأُضِلَّنَّهُمۡ وَلَأُمَنِّيَنَّهُمۡ وَلَأٓمُرَنَّهُمۡ فَلَيُبَتِّكُنَّ ءَاذَانَ ٱلۡأَنۡعَٰمِ وَلَأٓمُرَنَّهُمۡ فَلَيُغَيِّرُنَّ خَلۡقَ ٱللَّهِۚ وَمَن يَتَّخِذِ ٱلشَّيۡطَٰنَ وَلِيّٗا مِّن دُونِ ٱللَّهِ فَقَدۡ خَسِرَ خُسۡرَانٗا مُّبِينٗا\nУларни адаштираман, хомхаёлларига соламан, уларга амр қилсам, чорваларнинг қулоқларини кесарлар ва уларга амр қилсам, Аллоҳнинг яратганини ўзгартирарлар», деди. Кимки Аллоҳни қўйиб шайтонни дўст тутса, батаҳқиқ, очиқ-ойдин зиёнга учрайдир'}, {'chapter': 4, 'verse': 120, 'text': 'يَعِدُهُمۡ وَيُمَنِّيهِمۡۖ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا\nУ уларга ваъда берур ва хомхаёлларга солур. Шайтон уларга ғурурдан бошқа нарсани ваъда қилмас'}, {'chapter': 4, 'verse': 121, 'text': 'أُوْلَـٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُ وَلَا يَجِدُونَ عَنۡهَا مَحِيصٗا\nАна ўшаларнинг жойлари жаҳаннамдир. Ва ундан қутулиш йўлини топа олмаслар'}, {'chapter': 4, 'verse': 122, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ وَعۡدَ ٱللَّهِ حَقّٗاۚ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ قِيلٗا\nИймон келтириб, яхши амалларни қилганларни остидан анҳорлар оқиб турган жаннатларга киритурмиз. Унда абадий — бардавом қолурлар. Аллоҳнинг ваъдаси ҳақдир. Аллоҳдан ростгўйроқ зот борми'}, {'chapter': 4, 'verse': 123, 'text': 'لَّيۡسَ بِأَمَانِيِّكُمۡ وَلَآ أَمَانِيِّ أَهۡلِ ٱلۡكِتَٰبِۗ مَن يَعۡمَلۡ سُوٓءٗا يُجۡزَ بِهِۦ وَلَا يَجِدۡ لَهُۥ مِن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا\nСизларнинг хомхаёлингизча ҳам эмас, аҳли китобларнинг хомхаёлича ҳам эмас. Ким ёмонлик қилса, унинг жазосини тортадир. Ва ўзига Аллоҳдан ўзга дўст ҳам, ёрдамчи ҳам топа олмас'}, {'chapter': 4, 'verse': 124, 'text': 'وَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ مِن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا يُظۡلَمُونَ نَقِيرٗا\nЭркакми, аёлми — ким мўмин бўлган ҳолда яхшиликлар қилса, ана ўшалар жаннатга кирурлар ва уларга қилча ҳам зулм қилинмас'}, {'chapter': 4, 'verse': 125, 'text': 'وَمَنۡ أَحۡسَنُ دِينٗا مِّمَّنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنٞ وَٱتَّبَعَ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۗ وَٱتَّخَذَ ٱللَّهُ إِبۡرَٰهِيمَ خَلِيلٗا\nЯхшилик қилувчи бўлган ҳолида юзини Аллоҳга таслим қилган ва Иброҳимнинг тўғри динига эргашган кимсанинг динидан ҳам яхшироқ дин борми? Ва Аллоҳ Иброҳимни дўст тутган эди'}, {'chapter': 4, 'verse': 126, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٖ مُّحِيطٗا\nОсмонлару ердаги нарсалар Аллоҳга хосдир ва Аллоҳ ҳар бир нарсани иҳота қилган Зотдир'}, {'chapter': 4, 'verse': 127, 'text': 'وَيَسۡتَفۡتُونَكَ فِي ٱلنِّسَآءِۖ قُلِ ٱللَّهُ يُفۡتِيكُمۡ فِيهِنَّ وَمَا يُتۡلَىٰ عَلَيۡكُمۡ فِي ٱلۡكِتَٰبِ فِي يَتَٰمَى ٱلنِّسَآءِ ٱلَّـٰتِي لَا تُؤۡتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرۡغَبُونَ أَن تَنكِحُوهُنَّ وَٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلۡوِلۡدَٰنِ وَأَن تَقُومُواْ لِلۡيَتَٰمَىٰ بِٱلۡقِسۡطِۚ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ كَانَ بِهِۦ عَلِيمٗا\nСендан аёллар ҳақида фатво сўрарлар. «Аллоҳ сизга улар ҳақида ва китобда тиловат қилинадиган, сиз уларга тайин қилинган нарсани бермай никоҳига рағбат қилганингиз аёллар ҳақида, заифҳол болалар ҳақида ва етимларга адолат қилмоғингиз ҳақида фатво берадир», деб айт. Аллоҳ қилган ҳар бир яхшилигингизни билувчи Зотдир'}, {'chapter': 4, 'verse': 128, 'text': 'وَإِنِ ٱمۡرَأَةٌ خَافَتۡ مِنۢ بَعۡلِهَا نُشُوزًا أَوۡ إِعۡرَاضٗا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يُصۡلِحَا بَيۡنَهُمَا صُلۡحٗاۚ وَٱلصُّلۡحُ خَيۡرٞۗ وَأُحۡضِرَتِ ٱلۡأَنفُسُ ٱلشُّحَّۚ وَإِن تُحۡسِنُواْ وَتَتَّقُواْ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nАгар аёл киши ўз эридан нафрат ва юз ўгириш содир бўлишидан қўрқса, икковлари ўзаро сулҳга келишишларида гуноҳ йўқ. Сулҳ яхшидир. Нафслар қизғанишга ҳозирдир. Агар яхшилик ва тақво қилсангиз, албатта, Аллоҳ нима қилаётганингиздан хабардор Зотдир'}, {'chapter': 4, 'verse': 129, 'text': 'وَلَن تَسۡتَطِيعُوٓاْ أَن تَعۡدِلُواْ بَيۡنَ ٱلنِّسَآءِ وَلَوۡ حَرَصۡتُمۡۖ فَلَا تَمِيلُواْ كُلَّ ٱلۡمَيۡلِ فَتَذَرُوهَا كَٱلۡمُعَلَّقَةِۚ وَإِن تُصۡلِحُواْ وَتَتَّقُواْ فَإِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nАгар роса ҳаракат қилсангиз ҳам, хотинлар ўртасида адолатли бўла олмассизлар. Бир йўла мойил бўлиб кетиб, (баъзисини) муаллаққа ўхшатиб қўйманг. Агар ислоҳ ва тақво қилсангиз, албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 130, 'text': 'وَإِن يَتَفَرَّقَا يُغۡنِ ٱللَّهُ كُلّٗا مِّن سَعَتِهِۦۚ وَكَانَ ٱللَّهُ وَٰسِعًا حَكِيمٗا\nАгар ажрашсалар, Аллоҳ Ўз фазлидан ҳар бирини беҳожат қилур. Аллоҳ фазли кенг ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 131, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَلَقَدۡ وَصَّيۡنَا ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَإِيَّاكُمۡ أَنِ ٱتَّقُواْ ٱللَّهَۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ غَنِيًّا حَمِيدٗا\nОсмонлару ердаги нарсалар Аллоҳникидир. Батаҳқиқ, Биз сиздан олдин китоб берилганларга ҳам, сизга ҳам: «Аллоҳга тақво қилинглар», деб васият қилдик. Агар куфр келтирсангиз, албатта, осмонлару ердаги нарсалар Аллоҳникидир. Ва Аллоҳ беҳожат ва мақталган Зотдир'}, {'chapter': 4, 'verse': 132, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًا\nОсмонлару ердаги нарсалар Аллоҳникидир. Вакил бўлишга Аллоҳ кифоядир'}, {'chapter': 4, 'verse': 133, 'text': 'إِن يَشَأۡ يُذۡهِبۡكُمۡ أَيُّهَا ٱلنَّاسُ وَيَأۡتِ بِـَٔاخَرِينَۚ وَكَانَ ٱللَّهُ عَلَىٰ ذَٰلِكَ قَدِيرٗا\nАгар У Зот хоҳласа, эй одамлар, сизларни кетказиб, бошқаларни келтирур. Ва Аллоҳ бунга қодир бўлган Зотдир'}, {'chapter': 4, 'verse': 134, 'text': 'مَّن كَانَ يُرِيدُ ثَوَابَ ٱلدُّنۡيَا فَعِندَ ٱللَّهِ ثَوَابُ ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَكَانَ ٱللَّهُ سَمِيعَۢا بَصِيرٗا\nКим бу дунё савобини истаса, билсинки, Аллоҳнинг ҳузурида ҳам бу дунёнинг, ҳам у дунёнинг савоби бордир. Ва Аллоҳ эшитувчи ва кўрувчи Зотдир'}, {'chapter': 4, 'verse': 135, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُواْ قَوَّـٰمِينَ بِٱلۡقِسۡطِ شُهَدَآءَ لِلَّهِ وَلَوۡ عَلَىٰٓ أَنفُسِكُمۡ أَوِ ٱلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَۚ إِن يَكُنۡ غَنِيًّا أَوۡ فَقِيرٗا فَٱللَّهُ أَوۡلَىٰ بِهِمَاۖ فَلَا تَتَّبِعُواْ ٱلۡهَوَىٰٓ أَن تَعۡدِلُواْۚ وَإِن تَلۡوُۥٓاْ أَوۡ تُعۡرِضُواْ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nЭй, иймон келтирганлар! Адолат ила туринг ҳамда агар ўзингиз, ота-онангиз ва қариндошларингиз зиддига бўлса ҳам, Аллоҳ учун тўғри гувоҳлик берувчи бўлинг. Агар у бой бўлса ҳам, камбағал бўлса ҳам, Аллоҳ унга яқинроқдир. Ҳавойи нафсга эргашиб, адолатсизлик қилманг. Агар тилингизни бурсангиз ёки юз ўгириб кетсангиз, албатта, Аллоҳ нима қилаётганингиздан хабардордир'}, {'chapter': 4, 'verse': 136, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ ءَامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَٱلۡكِتَٰبِ ٱلَّذِي نَزَّلَ عَلَىٰ رَسُولِهِۦ وَٱلۡكِتَٰبِ ٱلَّذِيٓ أَنزَلَ مِن قَبۡلُۚ وَمَن يَكۡفُرۡ بِٱللَّهِ وَمَلَـٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ فَقَدۡ ضَلَّ ضَلَٰلَۢا بَعِيدًا\nЭй, иймон келтирганлар! Аллоҳга, Унинг Расулига, У Ўз Расулига туширган китобга ва бундан олдин туширган китобга иймон келтиринг. Ким Аллоҳга, Унинг фаришталарига, китобларига, Пайғамбарларига ва охират кунига куфр келтирса, батаҳқиқ, қаттиқ адашган бўлади'}, {'chapter': 4, 'verse': 137, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ٱزۡدَادُواْ كُفۡرٗا لَّمۡ يَكُنِ ٱللَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ سَبِيلَۢا\nАлбатта, иймон келтирганларидан кейин куфр келтирганлар, сўнгра иймон келтириб яна куфрга кетган ва куфрида зиёда бўлганларни Аллоҳ мағфират қилмас ва тўғри йўлга ҳидоят этмас'}, {'chapter': 4, 'verse': 138, 'text': 'بَشِّرِ ٱلۡمُنَٰفِقِينَ بِأَنَّ لَهُمۡ عَذَابًا أَلِيمًا\nМунофиқларга уларга албатта аламли азоб бўлишининг башоратини бер'}, {'chapter': 4, 'verse': 139, 'text': 'ٱلَّذِينَ يَتَّخِذُونَ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۚ أَيَبۡتَغُونَ عِندَهُمُ ٱلۡعِزَّةَ فَإِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِيعٗا\nУлар мўминларни қўйиб, кофирларни дўст тутадиганлардир. Уларнинг олдида иззат, куч-қудрат излайдиларми? Албатта, иззат, куч-қудратнинг барчаси Аллоҳнинг ҳузуридадир'}, {'chapter': 4, 'verse': 140, 'text': 'وَقَدۡ نَزَّلَ عَلَيۡكُمۡ فِي ٱلۡكِتَٰبِ أَنۡ إِذَا سَمِعۡتُمۡ ءَايَٰتِ ٱللَّهِ يُكۡفَرُ بِهَا وَيُسۡتَهۡزَأُ بِهَا فَلَا تَقۡعُدُواْ مَعَهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦٓ إِنَّكُمۡ إِذٗا مِّثۡلُهُمۡۗ إِنَّ ٱللَّهَ جَامِعُ ٱلۡمُنَٰفِقِينَ وَٱلۡكَٰفِرِينَ فِي جَهَنَّمَ جَمِيعًا\nБатаҳқиқ, сизларга китобда: «Аллоҳнинг оятларига куфр келтирилган ва уларни истеҳзо қилинган чоғда, бошқа гапга ўтмагунларича, улар билан ўтирманглар», деб туширди. Агар ўтирсангиз, сиз ҳам уларга ўхшашсиз. Албатта, Аллоҳ мунофиқ ва кофирларнинг ҳаммасини жаҳаннамда жамловчидир'}, {'chapter': 4, 'verse': 141, 'text': 'ٱلَّذِينَ يَتَرَبَّصُونَ بِكُمۡ فَإِن كَانَ لَكُمۡ فَتۡحٞ مِّنَ ٱللَّهِ قَالُوٓاْ أَلَمۡ نَكُن مَّعَكُمۡ وَإِن كَانَ لِلۡكَٰفِرِينَ نَصِيبٞ قَالُوٓاْ أَلَمۡ نَسۡتَحۡوِذۡ عَلَيۡكُمۡ وَنَمۡنَعۡكُم مِّنَ ٱلۡمُؤۡمِنِينَۚ فَٱللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَلَن يَجۡعَلَ ٱللَّهُ لِلۡكَٰفِرِينَ عَلَى ٱلۡمُؤۡمِنِينَ سَبِيلًا\nУлар сизларни кузатиб турурлар. Сизларга Аллоҳ томонидан фатҳ бўлса: «Сизлар билан бирга эмасмидик?!» дерлар. Агар кофирларга насиба бўлса: «Сизларга устун келиб, мўминлардан ҳимоя қилмадикми?» дерлар. Аллоҳ қиёмат куни улар орасида ҳукм қилур. Аллоҳ ҳаргиз кофирларга мўминлар устидан йўл бермас'}, {'chapter': 4, 'verse': 142, 'text': 'إِنَّ ٱلۡمُنَٰفِقِينَ يُخَٰدِعُونَ ٱللَّهَ وَهُوَ خَٰدِعُهُمۡ وَإِذَا قَامُوٓاْ إِلَى ٱلصَّلَوٰةِ قَامُواْ كُسَالَىٰ يُرَآءُونَ ٱلنَّاسَ وَلَا يَذۡكُرُونَ ٱللَّهَ إِلَّا قَلِيلٗا\nАлбатта, мунофиқлар Аллоҳни алдамоқчи бўлурлар. Ҳолбуки, Аллоҳ уларни «алдовчи»дир. Агар намозга турсалар, дангасалик билан, хўжакўрсинга турурлар ва Аллоҳни камдан-кам зикр қилурлар'}, {'chapter': 4, 'verse': 143, 'text': 'مُّذَبۡذَبِينَ بَيۡنَ ذَٰلِكَ لَآ إِلَىٰ هَـٰٓؤُلَآءِ وَلَآ إِلَىٰ هَـٰٓؤُلَآءِۚ وَمَن يُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا\nУлар орада сарсондирлар. На анавиларга қўшила оладирлар ва на манавиларга қўшила оладирлар. Аллоҳ кимни йўлдан адаштирса, ҳаргиз унга йўл топа олмассан'}, {'chapter': 4, 'verse': 144, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۚ أَتُرِيدُونَ أَن تَجۡعَلُواْ لِلَّهِ عَلَيۡكُمۡ سُلۡطَٰنٗا مُّبِينًا\nЭй, иймон келтирганлар! Мўминларни қўйиб, кофирларни дўст тутманг. Аллоҳнинг ҳузурида ўз зиддингизга очиқ ҳужжат қилишни хоҳлайсизми'}, {'chapter': 4, 'verse': 145, 'text': 'إِنَّ ٱلۡمُنَٰفِقِينَ فِي ٱلدَّرۡكِ ٱلۡأَسۡفَلِ مِنَ ٱلنَّارِ وَلَن تَجِدَ لَهُمۡ نَصِيرًا\nАлбатта, мунофиқлар дўзахнинг энг остки қаватидадирлар ва ҳаргиз уларга бир ёрдамчини топа олмассан'}, {'chapter': 4, 'verse': 146, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَٱعۡتَصَمُواْ بِٱللَّهِ وَأَخۡلَصُواْ دِينَهُمۡ لِلَّهِ فَأُوْلَـٰٓئِكَ مَعَ ٱلۡمُؤۡمِنِينَۖ وَسَوۡفَ يُؤۡتِ ٱللَّهُ ٱلۡمُؤۡمِنِينَ أَجۡرًا عَظِيمٗا\nМагар тавба қилиб, солиҳ амаллар қилган, Аллоҳни маҳкам ушлаган ва Аллоҳ учун динга ихлос қўйганлар — ана ўшалар мўминлар ила биргадирлар. Аллоҳ, албатта, мўминларга улуғ ажр берур'}, {'chapter': 4, 'verse': 147, 'text': 'مَّا يَفۡعَلُ ٱللَّهُ بِعَذَابِكُمۡ إِن شَكَرۡتُمۡ وَءَامَنتُمۡۚ وَكَانَ ٱللَّهُ شَاكِرًا عَلِيمٗا\nАгар шукр қилсангиз ва иймон келтирсангиз, Аллоҳ сизни азоблаб нима қиладир?! Аллоҳ Шакур(оз амал учун кўп савоб берувчи) ва билувчи Зотдир'}, {'chapter': 4, 'verse': 148, 'text': '۞لَّا يُحِبُّ ٱللَّهُ ٱلۡجَهۡرَ بِٱلسُّوٓءِ مِنَ ٱلۡقَوۡلِ إِلَّا مَن ظُلِمَۚ وَكَانَ ٱللَّهُ سَمِيعًا عَلِيمًا\nАллоҳ ёмон гапни ошкор қилишни хуш кўрмас. Магар мазлумдан бўлса, майли. Аллоҳ эшитувчи ва билувчи Зотдир'}, {'chapter': 4, 'verse': 149, 'text': 'إِن تُبۡدُواْ خَيۡرًا أَوۡ تُخۡفُوهُ أَوۡ تَعۡفُواْ عَن سُوٓءٖ فَإِنَّ ٱللَّهَ كَانَ عَفُوّٗا قَدِيرًا\nАгар яхшиликни ошкора ёки махфий қилсангиз ёки ёмонликни афв этсангиз, албатта, Аллоҳ афвли ва қодир Зотдир'}, {'chapter': 4, 'verse': 150, 'text': 'إِنَّ ٱلَّذِينَ يَكۡفُرُونَ بِٱللَّهِ وَرُسُلِهِۦ وَيُرِيدُونَ أَن يُفَرِّقُواْ بَيۡنَ ٱللَّهِ وَرُسُلِهِۦ وَيَقُولُونَ نُؤۡمِنُ بِبَعۡضٖ وَنَكۡفُرُ بِبَعۡضٖ وَيُرِيدُونَ أَن يَتَّخِذُواْ بَيۡنَ ذَٰلِكَ سَبِيلًا\nАлбатта, Аллоҳга ва Унинг Пайғамбарларига куфр келтирадиганлар ва Аллоҳ ила Пайғамбарлари орасида айримчиликни ирода қиладиганлар ҳамда баъзиларига иймон ва баъзиларига куфр келтирамиз, деб ана ўшандай ора йўлни тутишни хоҳлайдиганлар'}, {'chapter': 4, 'verse': 151, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰفِرُونَ حَقّٗاۚ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا\nАна ўшалар, ҳақиқий кофирлардир. Ва кофирларга хорловчи азобни тайёрлаб қўйганмиз'}, {'chapter': 4, 'verse': 152, 'text': 'وَٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦ وَلَمۡ يُفَرِّقُواْ بَيۡنَ أَحَدٖ مِّنۡهُمۡ أُوْلَـٰٓئِكَ سَوۡفَ يُؤۡتِيهِمۡ أُجُورَهُمۡۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nАллоҳга ва Унинг Пайғамбарларига иймон келтирганларга ва уларнинг бирортасини ҳам айирмаганларга — ана ўшаларга ажрларини албатта берурмиз. Ва Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 4, 'verse': 153, 'text': 'يَسۡـَٔلُكَ أَهۡلُ ٱلۡكِتَٰبِ أَن تُنَزِّلَ عَلَيۡهِمۡ كِتَٰبٗا مِّنَ ٱلسَّمَآءِۚ فَقَدۡ سَأَلُواْ مُوسَىٰٓ أَكۡبَرَ مِن ذَٰلِكَ فَقَالُوٓاْ أَرِنَا ٱللَّهَ جَهۡرَةٗ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ بِظُلۡمِهِمۡۚ ثُمَّ ٱتَّخَذُواْ ٱلۡعِجۡلَ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ فَعَفَوۡنَا عَن ذَٰلِكَۚ وَءَاتَيۡنَا مُوسَىٰ سُلۡطَٰنٗا مُّبِينٗا\nАҳли китоблар сендан уларга осмондан китоб тушириб беришингни сўрарлар. Мусодан бундан ҳам катта нарсани сўраганлар — «Бизга Аллоҳни ошкора кўрсат», деганлар. Бас, зулмлари туфайли уларни чақмоқ урди. Сўнгра, уларга очиқ-ойдин баёнотлар келганидан кейин бузоқни (худо қилиб) олдилар. Бас, Биз буни ҳам афв қилдик ва Мусога очиқ-ойдин султон бердик'}, {'chapter': 4, 'verse': 154, 'text': 'وَرَفَعۡنَا فَوۡقَهُمُ ٱلطُّورَ بِمِيثَٰقِهِمۡ وَقُلۡنَا لَهُمُ ٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا وَقُلۡنَا لَهُمۡ لَا تَعۡدُواْ فِي ٱلسَّبۡتِ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا\nВа аҳдномаларига мувофиқ, устиларига Турни кўтардик ва уларга: «Эшикдан эгилиб киринглар» ҳамда «Шанба куни ҳаддингиздан ошманг», дедик. Ва улардан мустаҳкам аҳднома олдик'}, {'chapter': 4, 'verse': 155, 'text': 'فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ وَكُفۡرِهِم بِـَٔايَٰتِ ٱللَّهِ وَقَتۡلِهِمُ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَقَوۡلِهِمۡ قُلُوبُنَا غُلۡفُۢۚ بَلۡ طَبَعَ ٱللَّهُ عَلَيۡهَا بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا\nАҳдномаларини бузганлари, Аллоҳнинг оятларига куфр келтирганлари, Пайғамбарларни ноҳақдан ўлдирганлари, «қалбларимиз берк» деганлари учун. Йўқ! Балки, куфрлари сабабли Аллоҳ қалбларига муҳр босган, озгиналаридан бошқаси иймон келтирмаслар'}, {'chapter': 4, 'verse': 156, 'text': 'وَبِكُفۡرِهِمۡ وَقَوۡلِهِمۡ عَلَىٰ مَرۡيَمَ بُهۡتَٰنًا عَظِيمٗا\n«Ва куфрлари, Марямга қарши улкан бўҳтон айтганлари учун»'}, {'chapter': 4, 'verse': 157, 'text': 'وَقَوۡلِهِمۡ إِنَّا قَتَلۡنَا ٱلۡمَسِيحَ عِيسَى ٱبۡنَ مَرۡيَمَ رَسُولَ ٱللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمۡۚ وَإِنَّ ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِ لَفِي شَكّٖ مِّنۡهُۚ مَا لَهُم بِهِۦ مِنۡ عِلۡمٍ إِلَّا ٱتِّبَاعَ ٱلظَّنِّۚ وَمَا قَتَلُوهُ يَقِينَۢا\n«Биз Аллоҳнинг Расули Масийҳ Ийсо ибн Марямни ўлдирдик», деганлари учун (уларни лаънатладик). Ҳолбуки, уни ўлдирмадилар ҳам, осмадилар ҳам, лекин уларга шундай туюлди. У ҳақида ихтилофга тушганлар унинг ўлимидан шак-шубҳададирлар. У тўғрисида уларнинг билимлари йўқ, магар гумонга эргашарлар. Уни ўлдирмаганлари аниқдир'}, {'chapter': 4, 'verse': 158, 'text': 'بَل رَّفَعَهُ ٱللَّهُ إِلَيۡهِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا\nБалки, уни Аллоҳ Ўзига кўтарди. Аллоҳ азийз ва ҳакийм бўлган Зотдир'}, {'chapter': 4, 'verse': 159, 'text': 'وَإِن مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ إِلَّا لَيُؤۡمِنَنَّ بِهِۦ قَبۡلَ مَوۡتِهِۦۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكُونُ عَلَيۡهِمۡ شَهِيدٗا\nҲеч бир аҳли китоб йўқки, унга ўлимидан олдин иймон келтирмаса. Қиёмат куни эса, у уларнинг зиддига гувоҳ бўлур'}, {'chapter': 4, 'verse': 160, 'text': 'فَبِظُلۡمٖ مِّنَ ٱلَّذِينَ هَادُواْ حَرَّمۡنَا عَلَيۡهِمۡ طَيِّبَٰتٍ أُحِلَّتۡ لَهُمۡ وَبِصَدِّهِمۡ عَن سَبِيلِ ٱللَّهِ كَثِيرٗا\nЯҳудий бўлганларга, уларнинг зулми, Аллоҳнинг йўлидан кўп тўсганлари учун, ўзларига ҳалол қилинган нарсани ҳаром қилдик'}, {'chapter': 4, 'verse': 161, 'text': 'وَأَخۡذِهِمُ ٱلرِّبَوٰاْ وَقَدۡ نُهُواْ عَنۡهُ وَأَكۡلِهِمۡ أَمۡوَٰلَ ٱلنَّاسِ بِٱلۡبَٰطِلِۚ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ مِنۡهُمۡ عَذَابًا أَلِيمٗا\nВа қайтарилган бўлсалар ҳам, рибо олганликлари ва одамларнинг молларини ботил йўл билан еганлари учун. Ва уларнинг кофирлари учун аламли азобни тайёрлаб қўйдик'}, {'chapter': 4, 'verse': 162, 'text': 'لَّـٰكِنِ ٱلرَّـٰسِخُونَ فِي ٱلۡعِلۡمِ مِنۡهُمۡ وَٱلۡمُؤۡمِنُونَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَۚ وَٱلۡمُقِيمِينَ ٱلصَّلَوٰةَۚ وَٱلۡمُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلۡمُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ أُوْلَـٰٓئِكَ سَنُؤۡتِيهِمۡ أَجۡرًا عَظِيمًا\nЛекин, улардан илмда мустаҳкам бўлганлари ва мўминлари сенга нозил бўлган нарсага ва сендан олдин нозил бўлган нарсага иймон келтирурлар. Хусусан, намозни қоим қилувчилар, закот берувчилар, Аллоҳга ва охират кунига иймон келтирувчилар ҳам. Ана ўшаларга албатта улуғ ажр берурмиз'}, {'chapter': 4, 'verse': 163, 'text': '۞إِنَّآ أَوۡحَيۡنَآ إِلَيۡكَ كَمَآ أَوۡحَيۡنَآ إِلَىٰ نُوحٖ وَٱلنَّبِيِّـۧنَ مِنۢ بَعۡدِهِۦۚ وَأَوۡحَيۡنَآ إِلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَعِيسَىٰ وَأَيُّوبَ وَيُونُسَ وَهَٰرُونَ وَسُلَيۡمَٰنَۚ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا\nБиз сенга худди Нуҳ ва ундан кейинги Пайғамбарларга ваҳий юборганимиздек ваҳий юбордик. Биз Иброҳим, Исмоил, Исҳоқ, Яъқуб, асбот, Ийсо, Айюб, Юнус, Ҳорун ва Сулаймонга ваҳий юбордик. Довудга эса Забурни бердик'}, {'chapter': 4, 'verse': 164, 'text': 'وَرُسُلٗا قَدۡ قَصَصۡنَٰهُمۡ عَلَيۡكَ مِن قَبۡلُ وَرُسُلٗا لَّمۡ نَقۡصُصۡهُمۡ عَلَيۡكَۚ وَكَلَّمَ ٱللَّهُ مُوسَىٰ تَكۡلِيمٗا\nВа Биз сенга бундан олдин қиссаларини айтиб берган ва қиссаларини айтиб бермаган Пайғамбарларни юбордик. Ва Аллоҳ Мусо ила ўзига хос гаплашди'}, {'chapter': 4, 'verse': 165, 'text': 'رُّسُلٗا مُّبَشِّرِينَ وَمُنذِرِينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى ٱللَّهِ حُجَّةُۢ بَعۡدَ ٱلرُّسُلِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا\nБашорат берувчи ва огоҳлантирувчи Пайғамбарларни Аллоҳ ҳузурида одамларга Пайғамбарлардан сўнг ҳужжат бўлмаслиги учун юбордик. Ва Аллоҳ азийз ва ҳакийм бўлган Зотдир'}, {'chapter': 4, 'verse': 166, 'text': 'لَّـٰكِنِ ٱللَّهُ يَشۡهَدُ بِمَآ أَنزَلَ إِلَيۡكَۖ أَنزَلَهُۥ بِعِلۡمِهِۦۖ وَٱلۡمَلَـٰٓئِكَةُ يَشۡهَدُونَۚ وَكَفَىٰ بِٱللَّهِ شَهِيدًا\nЛекин Аллоҳ сенга нозил қилган нарсаси ила гувоҳлик берадир. Уни Ўз илми ила нозил қилди. Фаришталар ҳам гувоҳлик берурлар. Гувоҳликка Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 167, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ قَدۡ ضَلُّواْ ضَلَٰلَۢا بَعِيدًا\nКуфр келтирган ва Аллоҳнинг йўлидан тўсганлар, батаҳқиқ, қаттиқ адашгандирлар'}, {'chapter': 4, 'verse': 168, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَظَلَمُواْ لَمۡ يَكُنِ ٱللَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ طَرِيقًا\nАлбатта, куфр келтирганлар ва зулм қилганларни Аллоҳ зинҳор мағфират қилмас ва йўлга ҳидоят ҳам қилмас'}, {'chapter': 4, 'verse': 169, 'text': 'إِلَّا طَرِيقَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا\nМагар жаҳаннам йўлига (ҳидоят қилур) ва унда абадий қолурлар. Бу, Аллоҳ учун осондир'}, {'chapter': 4, 'verse': 170, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُمُ ٱلرَّسُولُ بِٱلۡحَقِّ مِن رَّبِّكُمۡ فَـَٔامِنُواْ خَيۡرٗا لَّكُمۡۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nЭй одамлар! Батаҳқиқ, сизларга Роббингиздан ҳақ ила Пайғамбар келди. Бас, иймон келтиринг, ўзингизга яхши бўладир. Агар куфр келтирсангиз, албатта, осмонлару ердаги нарсалар Аллоҳникидир. Ва Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 4, 'verse': 171, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ وَلَا تَقُولُواْ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ إِنَّمَا ٱلۡمَسِيحُ عِيسَى ٱبۡنُ مَرۡيَمَ رَسُولُ ٱللَّهِ وَكَلِمَتُهُۥٓ أَلۡقَىٰهَآ إِلَىٰ مَرۡيَمَ وَرُوحٞ مِّنۡهُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرُسُلِهِۦۖ وَلَا تَقُولُواْ ثَلَٰثَةٌۚ ٱنتَهُواْ خَيۡرٗا لَّكُمۡۚ إِنَّمَا ٱللَّهُ إِلَٰهٞ وَٰحِدٞۖ سُبۡحَٰنَهُۥٓ أَن يَكُونَ لَهُۥ وَلَدٞۘ لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا\nЭй, аҳли китоблар! Динингизда ҳаддан ошманг ва Аллоҳ ҳақида ҳақдан бошқани айтманг. Албатта, Масийҳ Ийсо ибн Марям Аллоҳнинг Пайғамбари, Марямга илқо қилган калимаси ва Ундан бўлган руҳдир. Бас, Аллоҳга ва Унинг Пайғамбарларига иймон келтиринг. Уни учта деманг. Тўхтанг, ўзингизга яхши бўлади. Албатта, Аллоҳ ягона илоҳдир, ўзга эмас. У Ўзининг боласи бўлишидан пок бўлди. Осмонлардаги нарсалар ва ердаги нарсалар Уникидир. Вакилликка Аллоҳнинг Ўзи кифоя қилур'}, {'chapter': 4, 'verse': 172, 'text': 'لَّن يَسۡتَنكِفَ ٱلۡمَسِيحُ أَن يَكُونَ عَبۡدٗا لِّلَّهِ وَلَا ٱلۡمَلَـٰٓئِكَةُ ٱلۡمُقَرَّبُونَۚ وَمَن يَسۡتَنكِفۡ عَنۡ عِبَادَتِهِۦ وَيَسۡتَكۡبِرۡ فَسَيَحۡشُرُهُمۡ إِلَيۡهِ جَمِيعٗا\nМасийҳ ҳам, муқарраб фаришталар ҳам Аллоҳга банда бўлишдан зинҳор бош тортмаслар. Ким Унинг ибодатидан бош тортса ва мутакаббирлик қилса, уларнинг ҳаммасини Ўзига тўплар'}, {'chapter': 4, 'verse': 173, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡ وَيَزِيدُهُم مِّن فَضۡلِهِۦۖ وَأَمَّا ٱلَّذِينَ ٱسۡتَنكَفُواْ وَٱسۡتَكۡبَرُواْ فَيُعَذِّبُهُمۡ عَذَابًا أَلِيمٗا وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا\nИймон келтириб, яхши амалларни қилганларни эса, ажрларини тўлиқ қилиб берур ва Ўз фазлидан зиёда ҳам қилур. Аммо, ор қилиб кибрга кетганларни аламли азоб билан азоблар ва улар ўзлари учун Аллоҳдан ўзга дўст ҳам, ёрдамчи ҳам топмаслар'}, {'chapter': 4, 'verse': 174, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُم بُرۡهَٰنٞ مِّن رَّبِّكُمۡ وَأَنزَلۡنَآ إِلَيۡكُمۡ نُورٗا مُّبِينٗا\nЭй одамлар! Сизга Роббингиздан бурҳон келди ва сизга аниқ нурни нозил қилдик'}, {'chapter': 4, 'verse': 175, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَٱعۡتَصَمُواْ بِهِۦ فَسَيُدۡخِلُهُمۡ فِي رَحۡمَةٖ مِّنۡهُ وَفَضۡلٖ وَيَهۡدِيهِمۡ إِلَيۡهِ صِرَٰطٗا مُّسۡتَقِيمٗا\nАллоҳга иймон келтирган ва Уни маҳкам тутганларни, шубҳасиз, Ўзидан бўлган раҳматга ва фазлга киритур ва уларни унга — тўғри йўлга ҳидоят қилур'}, {'chapter': 4, 'verse': 176, 'text': 'يَسۡتَفۡتُونَكَ قُلِ ٱللَّهُ يُفۡتِيكُمۡ فِي ٱلۡكَلَٰلَةِۚ إِنِ ٱمۡرُؤٌاْ هَلَكَ لَيۡسَ لَهُۥ وَلَدٞ وَلَهُۥٓ أُخۡتٞ فَلَهَا نِصۡفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ إِن لَّمۡ يَكُن لَّهَا وَلَدٞۚ فَإِن كَانَتَا ٱثۡنَتَيۡنِ فَلَهُمَا ٱلثُّلُثَانِ مِمَّا تَرَكَۚ وَإِن كَانُوٓاْ إِخۡوَةٗ رِّجَالٗا وَنِسَآءٗ فَلِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَيَيۡنِۗ يُبَيِّنُ ٱللَّهُ لَكُمۡ أَن تَضِلُّواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ\nСендан фатво сўрарлар. «Аллоҳ сизга калола ҳақида фатво берур», деб айт. Агар бир одам ўлса-ю, унинг боласи бўлмаса, синглиси бўлса, унга марҳумдан қолганнинг ярми берилур. Агар сингилнинг боласи бўлмаса, у ҳамма меросни олур. Агар сингил иккита бўлса, улар марҳумдан қолганнинг учдан иккисини олурлар. Агар (меросхўрлар) эркак-аёл биродарлар бўлсалар, бир эркак икки аёл ҳиссаси асосида олурлар. Адашмаслигингиз учун Аллоҳ баён қилмоқда. Аллоҳ ҳар бир нарсани билувчи Зотдир'}], '5': [{'chapter': 5, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَوۡفُواْ بِٱلۡعُقُودِۚ أُحِلَّتۡ لَكُم بَهِيمَةُ ٱلۡأَنۡعَٰمِ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡ غَيۡرَ مُحِلِّي ٱلصَّيۡدِ وَأَنتُمۡ حُرُمٌۗ إِنَّ ٱللَّهَ يَحۡكُمُ مَا يُرِيدُ\nЭй, иймон келтирганлар! Аҳдномаларга вафо қилинг! Сизга, кейин тиловат қилинадиганларидан бошқа, чорва ҳайвонлари ҳалол қилинди, эҳромдалик вақтингизда овни ҳалол санамаган ҳолингизда. Албатта, Аллоҳ нимани ирода этса, шуни ҳукм қиладир'}, {'chapter': 5, 'verse': 2, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُحِلُّواْ شَعَـٰٓئِرَ ٱللَّهِ وَلَا ٱلشَّهۡرَ ٱلۡحَرَامَ وَلَا ٱلۡهَدۡيَ وَلَا ٱلۡقَلَـٰٓئِدَ وَلَآ ءَآمِّينَ ٱلۡبَيۡتَ ٱلۡحَرَامَ يَبۡتَغُونَ فَضۡلٗا مِّن رَّبِّهِمۡ وَرِضۡوَٰنٗاۚ وَإِذَا حَلَلۡتُمۡ فَٱصۡطَادُواْۚ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ أَن صَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ أَن تَعۡتَدُواْۘ وَتَعَاوَنُواْ عَلَى ٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَلَا تَعَاوَنُواْ عَلَى ٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِۚ وَٱتَّقُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nЭй, иймон келтирганлар! Аллоҳнинг дини аломатларини, ҳаром ойни, аталган қурбонликни, осилган белгиларни ва Роббиларидан фазл ва розилик тилаб Байтул Ҳаромни қасд қилиб чиққанларни ўзингизга ҳалол ҳисобламанг. Эҳромдан чиқиб ҳалол бўлсангиз, ов қилсангиз, майли. Масжидул Ҳаромдан тўсгани учун бир қавмни ёмон кўришингиз уларга тажовузкор бўлишингизга олиб бормасин. Яхшилик ва тақво йўлида ҳамкорлик қилинг. Гуноҳ ва душманлик йўлида ҳамкорлик қилманг. Аллоҳга тақво қилинг. Албатта, Аллоҳ иқоби шиддатли Зотдир'}, {'chapter': 5, 'verse': 3, 'text': 'حُرِّمَتۡ عَلَيۡكُمُ ٱلۡمَيۡتَةُ وَٱلدَّمُ وَلَحۡمُ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦ وَٱلۡمُنۡخَنِقَةُ وَٱلۡمَوۡقُوذَةُ وَٱلۡمُتَرَدِّيَةُ وَٱلنَّطِيحَةُ وَمَآ أَكَلَ ٱلسَّبُعُ إِلَّا مَا ذَكَّيۡتُمۡ وَمَا ذُبِحَ عَلَى ٱلنُّصُبِ وَأَن تَسۡتَقۡسِمُواْ بِٱلۡأَزۡلَٰمِۚ ذَٰلِكُمۡ فِسۡقٌۗ ٱلۡيَوۡمَ يَئِسَ ٱلَّذِينَ كَفَرُواْ مِن دِينِكُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِۚ ٱلۡيَوۡمَ أَكۡمَلۡتُ لَكُمۡ دِينَكُمۡ وَأَتۡمَمۡتُ عَلَيۡكُمۡ نِعۡمَتِي وَرَضِيتُ لَكُمُ ٱلۡإِسۡلَٰمَ دِينٗاۚ فَمَنِ ٱضۡطُرَّ فِي مَخۡمَصَةٍ غَيۡرَ مُتَجَانِفٖ لِّإِثۡمٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nСизларга ўлимтик, қон, чўчқа гўшти, Аллоҳдан бошқанинг номи ила сўйилган, бўғилиб ўлган, уриб ўлдирилган, йиқилиб ўлган, сузиб ўлдирилган, йиртқич еган ҳайвонлар ҳаром қилинди. Магар, (ўлмай туриб) сўйиб олсангиз (ҳалол). Ва бутларга сўйилган ҳайвонлар ҳам, чўплар ила фол очишингиз ҳам ҳаром қилинди. Бундай қилмоғингиз фосиқликдир. Бугунги кунда куфр келтирганлар динингизга зарар етказишдан умид уздилар. Улардан қўрқманглар, Мендан қўрқинглар. Бугунги кунда Мен сизнинг динингизни мукаммал қилиб бердим. Сизга неъматимни батамом қилдим. Ва сизга Исломни дин деб, рози бўлдим. Кимки гуноҳга мойил бўлмаган ҳолида очликда музтар бўлса, албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 5, 'verse': 4, 'text': 'يَسۡـَٔلُونَكَ مَاذَآ أُحِلَّ لَهُمۡۖ قُلۡ أُحِلَّ لَكُمُ ٱلطَّيِّبَٰتُ وَمَا عَلَّمۡتُم مِّنَ ٱلۡجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ ٱللَّهُۖ فَكُلُواْ مِمَّآ أَمۡسَكۡنَ عَلَيۡكُمۡ وَٱذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَيۡهِۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nСендан уларга нима ҳалол қилинганини сўрарлар. Сен: «Сизларга пок нарсалар ҳалол қилинди. Овчи итларга ўхшатиб, Аллоҳ сизга ўргатган нарсалардан уларга ҳам ўргатган овчи ҳайвонларингиз сизга тутиб берган нарсадан енглар. Уларга Аллоҳнинг исмини зикр қилинглар. Аллоҳга тақво қилинглар. Албатта, Аллоҳ ҳисоби тезкор Зотдир», деб айт'}, {'chapter': 5, 'verse': 5, 'text': 'ٱلۡيَوۡمَ أُحِلَّ لَكُمُ ٱلطَّيِّبَٰتُۖ وَطَعَامُ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ حِلّٞ لَّكُمۡ وَطَعَامُكُمۡ حِلّٞ لَّهُمۡۖ وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلۡمُؤۡمِنَٰتِ وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّ مُحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَ وَلَا مُتَّخِذِيٓ أَخۡدَانٖۗ وَمَن يَكۡفُرۡ بِٱلۡإِيمَٰنِ فَقَدۡ حَبِطَ عَمَلُهُۥ وَهُوَ فِي ٱلۡأٓخِرَةِ مِنَ ٱلۡخَٰسِرِينَ\nБугунги кунда сизларга пок нарсалар ҳалол қилинди. Китоб берилганларнинг таоми сиз учун ҳалолдир. Сизнинг таомингиз улар учун ҳалолдир. Афийфа мўминалар ва сиздан олдин китоб берилганлардан бўлган афийфалар ҳам, агар иффатли бўлган ҳолингизда, зинокор бўлмай ва ўйнаш тутмай маҳрларини берсангиз, сизга ҳалолдир. Ким иймонни инкор қилса, унинг амали беҳуда кетади ва охиратда у зиён кўрувчилардан бўладир'}, {'chapter': 5, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا قُمۡتُمۡ إِلَى ٱلصَّلَوٰةِ فَٱغۡسِلُواْ وُجُوهَكُمۡ وَأَيۡدِيَكُمۡ إِلَى ٱلۡمَرَافِقِ وَٱمۡسَحُواْ بِرُءُوسِكُمۡ وَأَرۡجُلَكُمۡ إِلَى ٱلۡكَعۡبَيۡنِۚ وَإِن كُنتُمۡ جُنُبٗا فَٱطَّهَّرُواْۚ وَإِن كُنتُم مَّرۡضَىٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآءَ أَحَدٞ مِّنكُم مِّنَ ٱلۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ ٱلنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَٱمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُم مِّنۡهُۚ مَا يُرِيدُ ٱللَّهُ لِيَجۡعَلَ عَلَيۡكُم مِّنۡ حَرَجٖ وَلَٰكِن يُرِيدُ لِيُطَهِّرَكُمۡ وَلِيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\nЭй, иймон келтирганлар! Намозга турмоқчи бўлсаларингиз, юзларингизни ва қўлларингизни чиғаноқлари ила ювинглар. Бошларингизга масҳ тортинглар. Ва оёқларингизни тўпиқлари ила ювинглар. Агар жунуб бўлсаларингиз, покланинглар. Агар бемор ёки сафарда бўлсаларингиз ёхуд бирорталарингиз таҳоратхонадан келса ёки аёлларга яқинлик қилса-ю, сув топа олмасаларингиз, бас, покиза тупроқ ила таяммум қилинглар. Ундан юзингизга ва қўлларингизга масҳ тортинглар. Аллоҳ сизларга қийинчилик туғдиришни истамас. Аммо сизларни поклашни ва сизларга Ўз неъматини батамом қилиб беришни истайдир. Шоядки, шукр қилсангизлар'}, {'chapter': 5, 'verse': 7, 'text': 'وَٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ وَمِيثَٰقَهُ ٱلَّذِي وَاثَقَكُم بِهِۦٓ إِذۡ قُلۡتُمۡ سَمِعۡنَا وَأَطَعۡنَاۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nАллоҳнинг сизга берган неъматини ва сиздан олган аҳдини эсланг. Ўзингиз: «Эшитдик ва итоат қилдик», дегансиз-а?! Ва Аллоҳга тақво қилинг. Албатта, Аллоҳ кўкраклардаги нарсани ҳам билгувчи Зотдир'}, {'chapter': 5, 'verse': 8, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُواْ قَوَّـٰمِينَ لِلَّهِ شُهَدَآءَ بِٱلۡقِسۡطِۖ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ عَلَىٰٓ أَلَّا تَعۡدِلُواْۚ ٱعۡدِلُواْ هُوَ أَقۡرَبُ لِلتَّقۡوَىٰۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nЭй, иймон келтирганлар! Аллоҳ учун ҳақда туринг, адолатли гувоҳ бўлинг. Бир қавмни ёмон кўришингиз сизни уларга нисбатан адолат қилмасликка олиб бормасин. Адолат қилинг. Бу тақвога оид ишдир. Аллоҳга тақво қилинг. Албатта, Аллоҳ қилаётган амалингиздан хабардор Зотдир'}, {'chapter': 5, 'verse': 9, 'text': 'وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٞ\nАллоҳ иймон келтириб яхши амалларни қилганларга ваъда қилди: Уларга мағфират ва улкан ажр бўлур'}, {'chapter': 5, 'verse': 10, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nКуфр келтирганлар ва оятларимизни ёлғонга чиқарганлар — ана ўшалар дўзах эгаларидир'}, {'chapter': 5, 'verse': 11, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ هَمَّ قَوۡمٌ أَن يَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ فَكَفَّ أَيۡدِيَهُمۡ عَنكُمۡۖ وَٱتَّقُواْ ٱللَّهَۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nЭй, иймон келтирганлар! Аллоҳнинг сизга берган неъматини эсланг. Бир қавм сизга қўл узатмоқчи бўлганида, У уларнинг қўлларини сиздан қайтарган эди. Ва Аллоҳга тақво қилинг. Мўминлар Аллоҳга таваккал қилсинлар'}, {'chapter': 5, 'verse': 12, 'text': '۞وَلَقَدۡ أَخَذَ ٱللَّهُ مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَبَعَثۡنَا مِنۡهُمُ ٱثۡنَيۡ عَشَرَ نَقِيبٗاۖ وَقَالَ ٱللَّهُ إِنِّي مَعَكُمۡۖ لَئِنۡ أَقَمۡتُمُ ٱلصَّلَوٰةَ وَءَاتَيۡتُمُ ٱلزَّكَوٰةَ وَءَامَنتُم بِرُسُلِي وَعَزَّرۡتُمُوهُمۡ وَأَقۡرَضۡتُمُ ٱللَّهَ قَرۡضًا حَسَنٗا لَّأُكَفِّرَنَّ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَلَأُدۡخِلَنَّكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ فَمَن كَفَرَ بَعۡدَ ذَٰلِكَ مِنكُمۡ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ\nБатаҳқиқ, Аллоҳ Бани Исроилдан аҳднома олди. Улардан ўн иккитасини нақиб қилиб юбордик. Ва Аллоҳ: «Албатта, Мен сизлар биланман. Агар намозни қоим қилсангиз, закотни берсангиз, Пайғамбарларимга иймон келтирсангиз, уларни қўлласангиз ва Аллоҳга яхши қарз берсангиз, албатта, сизнинг хатоларингизни кечираман ҳамда сизни остидан анҳорлар оқиб турадиган жаннатларга киритаман. Бас, бундан кейин ҳам сиздан ким куфр келтирса, батаҳқиқ, тўғри йўлдан адашган бўладир», деди'}, {'chapter': 5, 'verse': 13, 'text': 'فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ لَعَنَّـٰهُمۡ وَجَعَلۡنَا قُلُوبَهُمۡ قَٰسِيَةٗۖ يُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَآئِنَةٖ مِّنۡهُمۡ إِلَّا قَلِيلٗا مِّنۡهُمۡۖ فَٱعۡفُ عَنۡهُمۡ وَٱصۡفَحۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُحۡسِنِينَ\nАҳдларини бузганлари учун уларни лаънатладик ва қалбларини қаттиқ қилдик. Улар калималарни ўз ўрнидан ўзгартирдилар. Ўзларига эслатма бўлган нарсадан кўп насибани унутдилар. Уларнинг озгиналаридан бошқаларидан мудом хиёнат кўрасан. Бас, уларни афв эт ва айбларини ўтиб юбор. Албатта, Аллоҳ яхшилик қилувчиларни севар'}, {'chapter': 5, 'verse': 14, 'text': 'وَمِنَ ٱلَّذِينَ قَالُوٓاْ إِنَّا نَصَٰرَىٰٓ أَخَذۡنَا مِيثَٰقَهُمۡ فَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦ فَأَغۡرَيۡنَا بَيۡنَهُمُ ٱلۡعَدَاوَةَ وَٱلۡبَغۡضَآءَ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۚ وَسَوۡفَ يُنَبِّئُهُمُ ٱللَّهُ بِمَا كَانُواْ يَصۡنَعُونَ\nВа, «Биз насоролармиз», деганларнинг ҳам аҳдномасини олдик. Бас, улар ўзларига эслатма бўлган нарсадан кўп насибани унутдилар. Биз улар орасида адоват ва ёмон кўришни қиёмат кунигача қўзғатдик. Аллоҳ, албатта, уларга қилмишлари ҳақида хабар берадир'}, {'chapter': 5, 'verse': 15, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ قَدۡ جَآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ كَثِيرٗا مِّمَّا كُنتُمۡ تُخۡفُونَ مِنَ ٱلۡكِتَٰبِ وَيَعۡفُواْ عَن كَثِيرٖۚ قَدۡ جَآءَكُم مِّنَ ٱللَّهِ نُورٞ وَكِتَٰبٞ مُّبِينٞ\nЭй, аҳли китоблар! Батаҳқиқ, сизларга Пайғамбаримиз келди. У, сизга китобдан беркитиб юрган кўпгина нарсаларингизни баён қилиб берур. Кўпгинасини эса, афв қилур. Батаҳқиқ, сизларга Аллоҳдан нур ва ойдин Китоб келди'}, {'chapter': 5, 'verse': 16, 'text': 'يَهۡدِي بِهِ ٱللَّهُ مَنِ ٱتَّبَعَ رِضۡوَٰنَهُۥ سُبُلَ ٱلسَّلَٰمِ وَيُخۡرِجُهُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ بِإِذۡنِهِۦ وَيَهۡدِيهِمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nУ билан Аллоҳ Ўз розилигини истаганларни салом йўлларига бошлар ва уларни Ўз изни ила зулматлардан нурга чиқарар. Ҳамда сиротул мустақиймга ҳидоят қилар'}, {'chapter': 5, 'verse': 17, 'text': 'لَّقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَۚ قُلۡ فَمَن يَمۡلِكُ مِنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ أَن يُهۡلِكَ ٱلۡمَسِيحَ ٱبۡنَ مَرۡيَمَ وَأُمَّهُۥ وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗاۗ وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۚ يَخۡلُقُ مَا يَشَآءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nБатаҳқиқ, Масийҳ ибн Марям Аллоҳдир, деганлар ҳам кофир бўлдилар. Уларга: «Агар Аллоҳ Масийҳ ибн Марямни, унинг онасини ва ер юзидаги барча кимсаларни ҳалок этишни ирода қилса, ким Уни бирон нарсадан ман қила олади? Ҳолбуки, осмонлару ер ва уларнинг орасидаги нарсалар Аллоҳникидир. У нимани хоҳласа, халқ қиладир. Ва Аллоҳ ҳар бир нарсага қодирдир», деб айт'}, {'chapter': 5, 'verse': 18, 'text': 'وَقَالَتِ ٱلۡيَهُودُ وَٱلنَّصَٰرَىٰ نَحۡنُ أَبۡنَـٰٓؤُاْ ٱللَّهِ وَأَحِبَّـٰٓؤُهُۥۚ قُلۡ فَلِمَ يُعَذِّبُكُم بِذُنُوبِكُمۖ بَلۡ أَنتُم بَشَرٞ مِّمَّنۡ خَلَقَۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ\nЯҳудий ва насоролар: «Биз Аллоҳнинг ўғилларимиз ва Унинг маҳбубларимиз», – дедилар. Уларга: «Ундай бўлса, нимага У сизларни гуноҳларингиз туфайли азоблайдир? Балки, сизлар У яратган башардирсизлар. У кимни хоҳласа, мағфират қилади. Кимни хоҳласа, азоблайдир. Осмонлару ер ва уларнинг орасидаги нарсалар Аллоҳникидир. Қайтиб бориш фақат Унинг Ўзигадир», – деб айт'}, {'chapter': 5, 'verse': 19, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ قَدۡ جَآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ عَلَىٰ فَتۡرَةٖ مِّنَ ٱلرُّسُلِ أَن تَقُولُواْ مَا جَآءَنَا مِنۢ بَشِيرٖ وَلَا نَذِيرٖۖ فَقَدۡ جَآءَكُم بَشِيرٞ وَنَذِيرٞۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЭй, аҳли китоблар! Батаҳқиқ, сизларга Пайғамбаримиз келди. У сизларга Пайғамбарлардан бўш даврда (динни) баён қиладир. Бизга башорат бергувчи ҳам, огоҳлантиргувчи ҳам келмаган, демаслигингиз учун. Бас, батаҳқиқ, сизларга башорат бергувчи ҳам, огоҳлантиргувчи ҳам келди. Ва Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 5, 'verse': 20, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ جَعَلَ فِيكُمۡ أَنۢبِيَآءَ وَجَعَلَكُم مُّلُوكٗا وَءَاتَىٰكُم مَّا لَمۡ يُؤۡتِ أَحَدٗا مِّنَ ٱلۡعَٰلَمِينَ\nМусо ўз қавмига: «Эй қавмим, Аллоҳнинг сизга берган неъматларини эсланг. Ахир, У сиздан Пайғамбарлар юборди, подшоҳлар чиқарди ва оламларда бирортага бермаган нарсаларни берди-ку», – деганини эсла'}, {'chapter': 5, 'verse': 21, 'text': 'يَٰقَوۡمِ ٱدۡخُلُواْ ٱلۡأَرۡضَ ٱلۡمُقَدَّسَةَ ٱلَّتِي كَتَبَ ٱللَّهُ لَكُمۡ وَلَا تَرۡتَدُّواْ عَلَىٰٓ أَدۡبَارِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ\nЭй қавмим, Аллоҳ сизга ёзиб қўйган муқаддас ерга киринг. Ортингизга қайтманг, бас, зиён кўргувчига айланасиз'}, {'chapter': 5, 'verse': 22, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِنَّ فِيهَا قَوۡمٗا جَبَّارِينَ وَإِنَّا لَن نَّدۡخُلَهَا حَتَّىٰ يَخۡرُجُواْ مِنۡهَا فَإِن يَخۡرُجُواْ مِنۡهَا فَإِنَّا دَٰخِلُونَ\nУлар: «Эй Мусо, унда зўравон қавм бор. Улар ундан чиқмагунларича биз ҳеч қачон кирмасмиз. Агар улар чиқсалар, биз кирувчидирмиз», – дедилар'}, {'chapter': 5, 'verse': 23, 'text': 'قَالَ رَجُلَانِ مِنَ ٱلَّذِينَ يَخَافُونَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِمَا ٱدۡخُلُواْ عَلَيۡهِمُ ٱلۡبَابَ فَإِذَا دَخَلۡتُمُوهُ فَإِنَّكُمۡ غَٰلِبُونَۚ وَعَلَى ٱللَّهِ فَتَوَكَّلُوٓاْ إِن كُنتُم مُّؤۡمِنِينَ\nУлардан У зотдан қўрқадиганлардан бўлган ва Аллоҳ уларга неъмат ато этган икки киши: «Уларнинг устига эшикдан кириб боринглар. Агар унга кирсаларинг, албатта ғолиб бўласизлар. Агар мўмин бўлсангиз, Аллоҳга таваккал қилингиз», – дедилар'}, {'chapter': 5, 'verse': 24, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِنَّا لَن نَّدۡخُلَهَآ أَبَدٗا مَّا دَامُواْ فِيهَا فَٱذۡهَبۡ أَنتَ وَرَبُّكَ فَقَٰتِلَآ إِنَّا هَٰهُنَا قَٰعِدُونَ\nУлар: «Эй Мусо, модомики, улар бор экан, биз зинҳор ва зинҳор у ерга кирмасмиз. Сен ўзинг Роббинг ила бориб икковлон уларга қарши уруш қилаверинглар. Биз бу ерда ўтириб турувчимиз», – дедилар'}, {'chapter': 5, 'verse': 25, 'text': 'قَالَ رَبِّ إِنِّي لَآ أَمۡلِكُ إِلَّا نَفۡسِي وَأَخِيۖ فَٱفۡرُقۡ بَيۡنَنَا وَبَيۡنَ ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ\nУ: «Эй Роббим, албатта, мен ўзим ва биродаримдан бошқага эга эмасман. Ўзинг биз билан фосиқ қавмнинг орасини ажратгин», – деди'}, {'chapter': 5, 'verse': 26, 'text': 'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيۡهِمۡۛ أَرۡبَعِينَ سَنَةٗۛ يَتِيهُونَ فِي ٱلۡأَرۡضِۚ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ\nУ: «Албатта, бу ер уларга қирқ йилгача ҳаромдир. Улар ер юзида ҳайрон бўлиб юрарлар. Фосиқ қавмлар учун хафа бўлма», – деди'}, {'chapter': 5, 'verse': 27, 'text': '۞وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ ٱبۡنَيۡ ءَادَمَ بِٱلۡحَقِّ إِذۡ قَرَّبَا قُرۡبَانٗا فَتُقُبِّلَ مِنۡ أَحَدِهِمَا وَلَمۡ يُتَقَبَّلۡ مِنَ ٱلۡأٓخَرِ قَالَ لَأَقۡتُلَنَّكَۖ قَالَ إِنَّمَا يَتَقَبَّلُ ٱللَّهُ مِنَ ٱلۡمُتَّقِينَ\nУларга Одамнинг икки ўғли хабарини ҳақ ила тиловат қилиб бер. Икковлари қурбонлик қилганларида, бирларидан қабул қилинди, бошқасидан қабул қилинмади. У: «Албатта, мен сени ўлдиражакман», – деди. Бириси: «Албатта, Аллоҳ тақводорлардангина қабул қиладир'}, {'chapter': 5, 'verse': 28, 'text': 'لَئِنۢ بَسَطتَ إِلَيَّ يَدَكَ لِتَقۡتُلَنِي مَآ أَنَا۠ بِبَاسِطٖ يَدِيَ إِلَيۡكَ لِأَقۡتُلَكَۖ إِنِّيٓ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَٰلَمِينَ\nАгар сен мени ўлдириш учун қўл кўтарсанг, мен сени ўлдириш учун қўл кўтармасман. Мен, албатта, оламларнинг Робби Аллоҳдан қўрқаман'}, {'chapter': 5, 'verse': 29, 'text': 'إِنِّيٓ أُرِيدُ أَن تَبُوٓأَ بِإِثۡمِي وَإِثۡمِكَ فَتَكُونَ مِنۡ أَصۡحَٰبِ ٱلنَّارِۚ وَذَٰلِكَ جَزَـٰٓؤُاْ ٱلظَّـٰلِمِينَ\nМен, албатта, менинг ҳам гуноҳимни, ўзингнинг ҳам гуноҳингни олиб кетишингни, шу сабабдан дўзах аҳлидан бўлишингни хоҳлайман. Ана ўша золимларнинг жазосидир», – деди'}, {'chapter': 5, 'verse': 30, 'text': 'فَطَوَّعَتۡ لَهُۥ نَفۡسُهُۥ قَتۡلَ أَخِيهِ فَقَتَلَهُۥ فَأَصۡبَحَ مِنَ ٱلۡخَٰسِرِينَ\nНафси унга биродарини ўлдиришни яхши кўрсатди. Бас, уни ўлдирди. Шу сабабдан зиёнкорлардан бўлди'}, {'chapter': 5, 'verse': 31, 'text': 'فَبَعَثَ ٱللَّهُ غُرَابٗا يَبۡحَثُ فِي ٱلۡأَرۡضِ لِيُرِيَهُۥ كَيۡفَ يُوَٰرِي سَوۡءَةَ أَخِيهِۚ قَالَ يَٰوَيۡلَتَىٰٓ أَعَجَزۡتُ أَنۡ أَكُونَ مِثۡلَ هَٰذَا ٱلۡغُرَابِ فَأُوَٰرِيَ سَوۡءَةَ أَخِيۖ فَأَصۡبَحَ مِنَ ٱلنَّـٰدِمِينَ\nАллоҳ ер юзида кезиб юрган қарғани унга биродарининг ўлигини қандай кўмишини кўрсатиш учун юборди. У: «Шўрим қурсин! Мана шу қарғачалик бўлиб, биродарим ўлигини кўма олмадим-а?!» – деди. Бас, у надомат қилувчилардан бўлди'}, {'chapter': 5, 'verse': 32, 'text': 'مِنۡ أَجۡلِ ذَٰلِكَ كَتَبۡنَا عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَنَّهُۥ مَن قَتَلَ نَفۡسَۢا بِغَيۡرِ نَفۡسٍ أَوۡ فَسَادٖ فِي ٱلۡأَرۡضِ فَكَأَنَّمَا قَتَلَ ٱلنَّاسَ جَمِيعٗا وَمَنۡ أَحۡيَاهَا فَكَأَنَّمَآ أَحۡيَا ٱلنَّاسَ جَمِيعٗاۚ وَلَقَدۡ جَآءَتۡهُمۡ رُسُلُنَا بِٱلۡبَيِّنَٰتِ ثُمَّ إِنَّ كَثِيرٗا مِّنۡهُم بَعۡدَ ذَٰلِكَ فِي ٱلۡأَرۡضِ لَمُسۡرِفُونَ\nШунинг учун ҳам, Бани Исроилга: «Ким бир жонни ноҳақдан ёки ер юзида фасод қилмаса ҳам ўлдирса, худди ҳамма одамларни ўлдирган бўлади. Ким уни тирилтирса, худди ҳамма одамларни тирилтиргандек бўлади», деб ёздик. Батаҳқиқ, уларга Пайғамбарларимиз баёнотлар олиб келдилар. Сўнгра, бундан кейин ҳам, улардан кўплари ер юзида исроф қилгувчилардан бўлдилар'}, {'chapter': 5, 'verse': 33, 'text': 'إِنَّمَا جَزَـٰٓؤُاْ ٱلَّذِينَ يُحَارِبُونَ ٱللَّهَ وَرَسُولَهُۥ وَيَسۡعَوۡنَ فِي ٱلۡأَرۡضِ فَسَادًا أَن يُقَتَّلُوٓاْ أَوۡ يُصَلَّبُوٓاْ أَوۡ تُقَطَّعَ أَيۡدِيهِمۡ وَأَرۡجُلُهُم مِّنۡ خِلَٰفٍ أَوۡ يُنفَوۡاْ مِنَ ٱلۡأَرۡضِۚ ذَٰلِكَ لَهُمۡ خِزۡيٞ فِي ٱلدُّنۡيَاۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٌ\nАллоҳга ва Унинг Пайғамбарига қарши уруш қилувчилар, ер юзида фасод қилувчиларнинг жазоси, албатта, ўлдирилмоқлари ёки осилмоқлари ёхуд қўл-оёқлари қарама-қарши томондан кесилмоғи ёки ер юзида сургун қилинмоқларидир. Бу, уларга бу дунёда ор бўлур. Охиратда эса, уларга улкан азоб бор'}, {'chapter': 5, 'verse': 34, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ مِن قَبۡلِ أَن تَقۡدِرُواْ عَلَيۡهِمۡۖ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nМагар, уларга қодир бўлишингиздан олдин тавба қилсалар, билингки, албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 5, 'verse': 35, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَٱبۡتَغُوٓاْ إِلَيۡهِ ٱلۡوَسِيلَةَ وَجَٰهِدُواْ فِي سَبِيلِهِۦ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй, иймон келтирганлар! Аллоҳга тақво қилинглар ва Унга васийла ахтарингиз ҳамда Унинг йўлида жиҳод қилингиз. Шоядки, ютуққа эришсангиз'}, {'chapter': 5, 'verse': 36, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ لَوۡ أَنَّ لَهُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لِيَفۡتَدُواْ بِهِۦ مِنۡ عَذَابِ يَوۡمِ ٱلۡقِيَٰمَةِ مَا تُقُبِّلَ مِنۡهُمۡۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nАгар куфр келтирганлар қўлида ер юзидаги ҳамма нарса ва яна шунча нарса бўлса-ю, уни қиёмат кунининг азобидан қутулишга тўлов қилиб берсалар ҳам, улардан қабул қилинмас. Уларга аламли азоб бор'}, {'chapter': 5, 'verse': 37, 'text': 'يُرِيدُونَ أَن يَخۡرُجُواْ مِنَ ٱلنَّارِ وَمَا هُم بِخَٰرِجِينَ مِنۡهَاۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ\nУлар дўзахдан чиқмоқчи бўлурлар. Аммо ундан чиқа олмаслар. Уларга муқим азоб бор'}, {'chapter': 5, 'verse': 38, 'text': 'وَٱلسَّارِقُ وَٱلسَّارِقَةُ فَٱقۡطَعُوٓاْ أَيۡدِيَهُمَا جَزَآءَۢ بِمَا كَسَبَا نَكَٰلٗا مِّنَ ٱللَّهِۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ\nЎғри эркак ва ўғри аёлнинг қўлларини кесинглар. Бу уларнинг қилмишига жазо ва Аллоҳдан иқобдир. Аллоҳ Азийз ва Ҳакиймдир'}, {'chapter': 5, 'verse': 39, 'text': 'فَمَن تَابَ مِنۢ بَعۡدِ ظُلۡمِهِۦ وَأَصۡلَحَ فَإِنَّ ٱللَّهَ يَتُوبُ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nКим зулмидан кейин тавба этса ва амали солиҳ қилса, Аллоҳ, албатта, тавбасини қабул қиладир. Шубҳасиз, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 5, 'verse': 40, 'text': 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ يُعَذِّبُ مَن يَشَآءُ وَيَغۡفِرُ لِمَن يَشَآءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nОсмонлару ернинг мулки Аллоҳники эканлигини, кимни хоҳласа, азоблашини, кимни хоҳласа, мағфират қилишини ва Аллоҳ ҳар бир нарсага қодир эканини билмасмидинг'}, {'chapter': 5, 'verse': 41, 'text': '۞يَـٰٓأَيُّهَا ٱلرَّسُولُ لَا يَحۡزُنكَ ٱلَّذِينَ يُسَٰرِعُونَ فِي ٱلۡكُفۡرِ مِنَ ٱلَّذِينَ قَالُوٓاْ ءَامَنَّا بِأَفۡوَٰهِهِمۡ وَلَمۡ تُؤۡمِن قُلُوبُهُمۡۛ وَمِنَ ٱلَّذِينَ هَادُواْۛ سَمَّـٰعُونَ لِلۡكَذِبِ سَمَّـٰعُونَ لِقَوۡمٍ ءَاخَرِينَ لَمۡ يَأۡتُوكَۖ يُحَرِّفُونَ ٱلۡكَلِمَ مِنۢ بَعۡدِ مَوَاضِعِهِۦۖ يَقُولُونَ إِنۡ أُوتِيتُمۡ هَٰذَا فَخُذُوهُ وَإِن لَّمۡ تُؤۡتَوۡهُ فَٱحۡذَرُواْۚ وَمَن يُرِدِ ٱللَّهُ فِتۡنَتَهُۥ فَلَن تَمۡلِكَ لَهُۥ مِنَ ٱللَّهِ شَيۡـًٔاۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ لَمۡ يُرِدِ ٱللَّهُ أَن يُطَهِّرَ قُلُوبَهُمۡۚ لَهُمۡ فِي ٱلدُّنۡيَا خِزۡيٞۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٞ\nЭй Пайғамбар! Оғизлари ила: «Иймон келтирдик» деб, қалблари иймон келтирмаганлар ва яҳудий бўлганларнинг куфрга ошиқадиганларидан хафа бўлма. Улар ёлғонни кўп эшитурлар, сенга келмаган қавмлардан ҳам эшитурлар. Сўзларни жойларидан ўзгартирурлар. Улар: «Агар сизга бу берилса, олинглар, агар бу берилмаса, ҳазир бўлинглар», дерлар. Аллоҳ кимни фитнага солишни ирода қилса, унга Аллоҳ томонидан бўладиган бирон нарсани тўса олмассан. Аллоҳ ана ўшаларнинг қалбларини поклашни ирода қилмади. Уларга бу дунёда шармандалик бор. Уларга охиратда улкан азоб бор'}, {'chapter': 5, 'verse': 42, 'text': 'سَمَّـٰعُونَ لِلۡكَذِبِ أَكَّـٰلُونَ لِلسُّحۡتِۚ فَإِن جَآءُوكَ فَٱحۡكُم بَيۡنَهُمۡ أَوۡ أَعۡرِضۡ عَنۡهُمۡۖ وَإِن تُعۡرِضۡ عَنۡهُمۡ فَلَن يَضُرُّوكَ شَيۡـٔٗاۖ وَإِنۡ حَكَمۡتَ فَٱحۡكُم بَيۡنَهُم بِٱلۡقِسۡطِۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ\nУлар ёлғонни кўп эшитувчилар, ҳаромни кўп еювчилардир. Агар сенга келсалар, ораларида ҳукм чиқар ёки улардан юз ўгир. Агар улардан юз ўгирсанг, сенга ҳеч зарар келтира олмаслар. Агар ҳукм қилсанг, ораларида адолат ила ҳукм қил. Албатта, Аллоҳ адолат қилувчиларни севадир'}, {'chapter': 5, 'verse': 43, 'text': 'وَكَيۡفَ يُحَكِّمُونَكَ وَعِندَهُمُ ٱلتَّوۡرَىٰةُ فِيهَا حُكۡمُ ٱللَّهِ ثُمَّ يَتَوَلَّوۡنَ مِنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَـٰٓئِكَ بِٱلۡمُؤۡمِنِينَ\nЎзларида Таврот бўла туриб, унда Аллоҳнинг ҳукми бўла туриб, қандай қилиб сени ҳакам қилурлар-у, сўнгра яна юз ўгириб кетурлар? Улар мўмин эмаслар'}, {'chapter': 5, 'verse': 44, 'text': 'إِنَّآ أَنزَلۡنَا ٱلتَّوۡرَىٰةَ فِيهَا هُدٗى وَنُورٞۚ يَحۡكُمُ بِهَا ٱلنَّبِيُّونَ ٱلَّذِينَ أَسۡلَمُواْ لِلَّذِينَ هَادُواْ وَٱلرَّبَّـٰنِيُّونَ وَٱلۡأَحۡبَارُ بِمَا ٱسۡتُحۡفِظُواْ مِن كِتَٰبِ ٱللَّهِ وَكَانُواْ عَلَيۡهِ شُهَدَآءَۚ فَلَا تَخۡشَوُاْ ٱلنَّاسَ وَٱخۡشَوۡنِ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗاۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰفِرُونَ\nБиз Тавротни нозил қилдик. Унда ҳидоят ва нур бор. У билан У зотга таслим бўлган Пайғамбарлар, Аллоҳнинг китобини муҳофаза қилишга мукаллаф бўлган роббонийлар ва аҳборлар яҳудий бўлганларга ҳукм юритурлар. Улар бу китобга гувоҳ қилинганлар. Одамлардан қўрқманглар, Мендан қўрқинглар. Менинг оятларимни арзон баҳога сотманглар. Ким Аллоҳ нозил қилган нарса ила ҳукм юритмаса, ўшалар кофирлардир'}, {'chapter': 5, 'verse': 45, 'text': 'وَكَتَبۡنَا عَلَيۡهِمۡ فِيهَآ أَنَّ ٱلنَّفۡسَ بِٱلنَّفۡسِ وَٱلۡعَيۡنَ بِٱلۡعَيۡنِ وَٱلۡأَنفَ بِٱلۡأَنفِ وَٱلۡأُذُنَ بِٱلۡأُذُنِ وَٱلسِّنَّ بِٱلسِّنِّ وَٱلۡجُرُوحَ قِصَاصٞۚ فَمَن تَصَدَّقَ بِهِۦ فَهُوَ كَفَّارَةٞ لَّهُۥۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nБиз унда уларга: жонга — жон, кўзга — кўз, бурунга — бурун, қулоққа — қулоқ, тишга — тиш ва жароҳатларда қасос вожиб, деб ёздик. Ким у(қасос)ни кечиб юборса, бу унга каффоротдир. Ким Аллоҳ нозил қилган нарса ила ҳукм юритмаса, ўшалар золимлардир'}, {'chapter': 5, 'verse': 46, 'text': 'وَقَفَّيۡنَا عَلَىٰٓ ءَاثَٰرِهِم بِعِيسَى ٱبۡنِ مَرۡيَمَ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلتَّوۡرَىٰةِۖ وَءَاتَيۡنَٰهُ ٱلۡإِنجِيلَ فِيهِ هُدٗى وَنُورٞ وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلتَّوۡرَىٰةِ وَهُدٗى وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ\nУларнинг изларидан Ийсо ибн Марямни ўзидан олдинги Тавротни тасдиқловчи қилиб юбордик. Унга Инжилни бердик. Унда ҳидоят ва нур бор. У ўзидан олдинги Тавротни тасдиқлагувчидир. У тақводорлар учун ҳидоят ва мавъизадир'}, {'chapter': 5, 'verse': 47, 'text': 'وَلۡيَحۡكُمۡ أَهۡلُ ٱلۡإِنجِيلِ بِمَآ أَنزَلَ ٱللَّهُ فِيهِۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nИнжил аҳли унда Аллоҳ нозил қилган нарса ила ҳукм юритсинлар. Ким Аллоҳ нозил қилган нарса ила ҳукм юритмаса, ана ўшалар фосиқлардир'}, {'chapter': 5, 'verse': 48, 'text': 'وَأَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلۡكِتَٰبِ وَمُهَيۡمِنًا عَلَيۡهِۖ فَٱحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ ٱللَّهُۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ عَمَّا جَآءَكَ مِنَ ٱلۡحَقِّۚ لِكُلّٖ جَعَلۡنَا مِنكُمۡ شِرۡعَةٗ وَمِنۡهَاجٗاۚ وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۖ فَٱسۡتَبِقُواْ ٱلۡخَيۡرَٰتِۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nВа Биз сенга китобни ҳақ ила, ўзидан олдинги Китобни тасдиқловчи ва унинг устидан назорат этгувчи қилиб нозил қилдик. Бас, улар орасида Аллоҳ нозил этган нарса ила ҳукм юрит. Ўзингга келган ҳақни қўйиб, уларнинг хоҳишларига эргашма. Сизлардан ҳар бирингизга алоҳида шариат ва йўл қилиб қўйдик. Агар Аллоҳ хоҳласа, ҳаммангизни бир уммат қилиб қўяр эди. Аммо У сизларни Ўзи ато этган нарсада синамоқ истайдир. Яхшиликка шошилингиз. Ҳаммангизнинг қайтар жойингиз Аллоҳ ҳузуридадир. Бас, ўшанда ихтилоф қилган нарсаларингиз ҳақида хабар берур'}, {'chapter': 5, 'verse': 49, 'text': 'وَأَنِ ٱحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ ٱللَّهُ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ وَٱحۡذَرۡهُمۡ أَن يَفۡتِنُوكَ عَنۢ بَعۡضِ مَآ أَنزَلَ ٱللَّهُ إِلَيۡكَۖ فَإِن تَوَلَّوۡاْ فَٱعۡلَمۡ أَنَّمَا يُرِيدُ ٱللَّهُ أَن يُصِيبَهُم بِبَعۡضِ ذُنُوبِهِمۡۗ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ لَفَٰسِقُونَ\nУлар орасида Аллоҳ нозил этган нарса ила ҳукм юрит. Уларнинг хоҳишларига эргашма ва улар сени Аллоҳ сенга нозил қилган нарсада фитнага солишларидан ҳазир бўл. Агар юз ўгирсалар, билки, Аллоҳ уларни баъзи гуноҳлари туфайли мусийбатга солишни ирода қилибдир. Албатта, одамлардан кўплари фосиқлардир'}, {'chapter': 5, 'verse': 50, 'text': 'أَفَحُكۡمَ ٱلۡجَٰهِلِيَّةِ يَبۡغُونَۚ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ حُكۡمٗا لِّقَوۡمٖ يُوقِنُونَ\nЖоҳилият ҳукмини истарларми?! Ишончи комил қавмлар учун Аллоҳдан ҳукми яхшироқ ким бор'}, {'chapter': 5, 'verse': 51, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلۡيَهُودَ وَٱلنَّصَٰرَىٰٓ أَوۡلِيَآءَۘ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَإِنَّهُۥ مِنۡهُمۡۗ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nЭй, иймон келтирганлар! Яҳудий ва насороларни ўзингизга дўст тутманг. Улар бир-бирлари билан дўстдирлар. Сиздан ким уларни дўст тутса, албатта, у улардандир. Албатта, Аллоҳ золим қавмларни ҳидоят этмас'}, {'chapter': 5, 'verse': 52, 'text': 'فَتَرَى ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يُسَٰرِعُونَ فِيهِمۡ يَقُولُونَ نَخۡشَىٰٓ أَن تُصِيبَنَا دَآئِرَةٞۚ فَعَسَى ٱللَّهُ أَن يَأۡتِيَ بِٱلۡفَتۡحِ أَوۡ أَمۡرٖ مِّنۡ عِندِهِۦ فَيُصۡبِحُواْ عَلَىٰ مَآ أَسَرُّواْ فِيٓ أَنفُسِهِمۡ نَٰدِمِينَ\nҚалбларида касаллик борларнинг «Замон айланиб бизга мусийбат етишидан қўрқамиз» деб, улар томон шошилганини кўрасан. Шоядки, Аллоҳ фатҳу нусрат берса ёки Ўз томонидан бирон иш ато қилсаю ўзларича сирлашган нарсаларига надомат қилсалар'}, {'chapter': 5, 'verse': 53, 'text': 'وَيَقُولُ ٱلَّذِينَ ءَامَنُوٓاْ أَهَـٰٓؤُلَآءِ ٱلَّذِينَ أَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ إِنَّهُمۡ لَمَعَكُمۡۚ حَبِطَتۡ أَعۡمَٰلُهُمۡ فَأَصۡبَحُواْ خَٰسِرِينَ\nИймон келтирганлар: «Ўзларини сизлар билан деб, Аллоҳнинг номи ила жон-жаҳдлари ила қасам ичганлар анавиларми?» – дейишади. Амаллари беҳуда кетди. Бас, зиён кўргувчилардан бўлдилар'}, {'chapter': 5, 'verse': 54, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ مَن يَرۡتَدَّ مِنكُمۡ عَن دِينِهِۦ فَسَوۡفَ يَأۡتِي ٱللَّهُ بِقَوۡمٖ يُحِبُّهُمۡ وَيُحِبُّونَهُۥٓ أَذِلَّةٍ عَلَى ٱلۡمُؤۡمِنِينَ أَعِزَّةٍ عَلَى ٱلۡكَٰفِرِينَ يُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ وَلَا يَخَافُونَ لَوۡمَةَ لَآئِمٖۚ ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٌ\nЭй, иймон келтирганлар! Сизлардан ким динидан қайтса, Аллоҳ албатта Ўзи севадиган ва улар ҳам Аллоҳни севадиган қавмни келтирур. Улар мўминларга хокисор, кофирларга қаттиққўл, Аллоҳнинг йўлида жиҳод қилурлар ва маломатчининг маломатидан қўрқмаслар. Бу, Аллоҳнинг фазли бўлиб, хоҳлаган одамига берадир. Аллоҳ фазли-карами кенг ва билувчи Зотдир'}, {'chapter': 5, 'verse': 55, 'text': 'إِنَّمَا وَلِيُّكُمُ ٱللَّهُ وَرَسُولُهُۥ وَٱلَّذِينَ ءَامَنُواْ ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُمۡ رَٰكِعُونَ\nАлбатта, сизнинг валийларингиз Аллоҳ, Унинг Пайғамбари ва бўйсунган ҳолларида намозни қоим қилиб, закотни адо этадиган иймон келтирганлардир'}, {'chapter': 5, 'verse': 56, 'text': 'وَمَن يَتَوَلَّ ٱللَّهَ وَرَسُولَهُۥ وَٱلَّذِينَ ءَامَنُواْ فَإِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡغَٰلِبُونَ\nКим Аллоҳни, Унинг Пайғамбарини ва иймон келтирганларни валий қилиб олса, бас, албатта, Аллоҳнинг ҳизби — ана ўшалар ғолиблардир'}, {'chapter': 5, 'verse': 57, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلَّذِينَ ٱتَّخَذُواْ دِينَكُمۡ هُزُوٗا وَلَعِبٗا مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَٱلۡكُفَّارَ أَوۡلِيَآءَۚ وَٱتَّقُواْ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِينَ\nЭй, иймон келтирганлар! Динингизни масхара ва ўйин қилиб олган сиздан олдин китоб берилганлар ва куфр келтирганларни валий — дўст тутманг. Агар мўмин бўлсангиз, Аллоҳга тақво қилинг'}, {'chapter': 5, 'verse': 58, 'text': 'وَإِذَا نَادَيۡتُمۡ إِلَى ٱلصَّلَوٰةِ ٱتَّخَذُوهَا هُزُوٗا وَلَعِبٗاۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ\nАгар намозга нидо қилсангиз, улар уни масхара ва ўйин қилишади. Бу уларнинг ақл қилмайдиган қавмлигидандир'}, {'chapter': 5, 'verse': 59, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ هَلۡ تَنقِمُونَ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ مِن قَبۡلُ وَأَنَّ أَكۡثَرَكُمۡ فَٰسِقُونَ\n«Эй, аҳли китоблар! Бизни фақат Аллоҳга ҳамда ўзимизга нозил қилинган ва авваллари нозил қилинган нарсаларга иймон келтирганимиз учун ва сизларнинг кўпчилигингиз фосиқ бўлганингиз учун айблайсизларми?!» – деб айт'}, {'chapter': 5, 'verse': 60, 'text': 'قُلۡ هَلۡ أُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكَ مَثُوبَةً عِندَ ٱللَّهِۚ مَن لَّعَنَهُ ٱللَّهُ وَغَضِبَ عَلَيۡهِ وَجَعَلَ مِنۡهُمُ ٱلۡقِرَدَةَ وَٱلۡخَنَازِيرَ وَعَبَدَ ٱلطَّـٰغُوتَۚ أُوْلَـٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ عَن سَوَآءِ ٱلسَّبِيلِ\n«Сизларга Аллоҳнинг ҳузуридаги бундан ҳам кўра ёмонроқ «савоб»нинг хабарини берайми? Ким Аллоҳнинг лаънатига учраган, ғазабига дучор бўлган, маймун ва чўчқага айлантирилган ва тоғутга ибодат қилган бўлса, ана ўшалар ёмон мартабадаги ва тўғри йўлдан бутунлай адашган кимсалардир», – деб айт'}, {'chapter': 5, 'verse': 61, 'text': 'وَإِذَا جَآءُوكُمۡ قَالُوٓاْ ءَامَنَّا وَقَد دَّخَلُواْ بِٱلۡكُفۡرِ وَهُمۡ قَدۡ خَرَجُواْ بِهِۦۚ وَٱللَّهُ أَعۡلَمُ بِمَا كَانُواْ يَكۡتُمُونَ\nУлар ҳузурингизга келсалар, иймон келтирдик, дерлар. Ҳолбуки, уларнинг куфр ила кириб, куфр ила чиқишлари шубҳасиз. Аллоҳ улар беркитган нарсани яхши биладир'}, {'chapter': 5, 'verse': 62, 'text': 'وَتَرَىٰ كَثِيرٗا مِّنۡهُمۡ يُسَٰرِعُونَ فِي ٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُواْ يَعۡمَلُونَ\nВа улардан кўпларининг гуноҳга, душманликка ва ҳаром емоққа ошиқаётганини кўрасан. Қилаётган ишлари қандай ҳам ёмон'}, {'chapter': 5, 'verse': 63, 'text': 'لَوۡلَا يَنۡهَىٰهُمُ ٱلرَّبَّـٰنِيُّونَ وَٱلۡأَحۡبَارُ عَن قَوۡلِهِمُ ٱلۡإِثۡمَ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُواْ يَصۡنَعُونَ\nРоббонийлари ва аҳборлари уларни гуноҳ айтиш ва ҳаромни ейишдан қайтарсалар бўлмасми?! Қилаётган ҳунарлари қандай ҳам ёмон-а'}, {'chapter': 5, 'verse': 64, 'text': 'وَقَالَتِ ٱلۡيَهُودُ يَدُ ٱللَّهِ مَغۡلُولَةٌۚ غُلَّتۡ أَيۡدِيهِمۡ وَلُعِنُواْ بِمَا قَالُواْۘ بَلۡ يَدَاهُ مَبۡسُوطَتَانِ يُنفِقُ كَيۡفَ يَشَآءُۚ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۚ وَأَلۡقَيۡنَا بَيۡنَهُمُ ٱلۡعَدَٰوَةَ وَٱلۡبَغۡضَآءَ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۚ كُلَّمَآ أَوۡقَدُواْ نَارٗا لِّلۡحَرۡبِ أَطۡفَأَهَا ٱللَّهُۚ وَيَسۡعَوۡنَ فِي ٱلۡأَرۡضِ فَسَادٗاۚ وَٱللَّهُ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ\nЯҳудийлар: «Аллоҳнинг қўли боғлиқдир», дедилар. Қўллари боғлансин уларнинг! Айтганлари учун лаънатга учрасинлар! Аксинча, Аллоҳнинг икки қўли очиқ, хоҳлаганича нафақа қилур. Сенга Роббингдан нозил қилинган нарса улардан кўпларининг туғён ва куфрини зиёда қилур. Уларнинг орасига қиёмат кунигача душманлик ва ёмон кўришни солдик. Улар қачон уруш оловини ёқсалар, Аллоҳ ўчирур. Улар ер юзида фасод қилиб юрарлар. Аллоҳ фасод қилгувчиларни севмас'}, {'chapter': 5, 'verse': 65, 'text': 'وَلَوۡ أَنَّ أَهۡلَ ٱلۡكِتَٰبِ ءَامَنُواْ وَٱتَّقَوۡاْ لَكَفَّرۡنَا عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأَدۡخَلۡنَٰهُمۡ جَنَّـٰتِ ٱلنَّعِيمِ\nАгар аҳли китоблар иймон келтириб, тақво қилганларида эди, уларнинг гуноҳларини ювган ва жаннати наъиймга киритган бўлардик'}, {'chapter': 5, 'verse': 66, 'text': 'وَلَوۡ أَنَّهُمۡ أَقَامُواْ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡهِم مِّن رَّبِّهِمۡ لَأَكَلُواْ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۚ مِّنۡهُمۡ أُمَّةٞ مُّقۡتَصِدَةٞۖ وَكَثِيرٞ مِّنۡهُمۡ سَآءَ مَا يَعۡمَلُونَ\nАгар улар Тавротга, Инжилга ва уларга Роббиларидан нозил бўлган нарсага амал қилганларида эди, албатта, тепаларидан ҳам, оёқлари остидан ҳам еган бўлардилар. Улардан мўътадил тоифалар ҳам бор. Уларнинг кўпларининг қилган нарсалари жуда ҳам ёмон'}, {'chapter': 5, 'verse': 67, 'text': '۞يَـٰٓأَيُّهَا ٱلرَّسُولُ بَلِّغۡ مَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَۖ وَإِن لَّمۡ تَفۡعَلۡ فَمَا بَلَّغۡتَ رِسَالَتَهُۥۚ وَٱللَّهُ يَعۡصِمُكَ مِنَ ٱلنَّاسِۗ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nЭй, Пайғамбар! Сенга Роббингдан нозил қилинган нарсани етказ. Агар шундай қилмасанг, Унинг элчилигини етказмаган бўласан. Аллоҳ сени одамлардан сақлар. Албатта, Аллоҳ кофир қавмларни ҳидоят қилмас'}, {'chapter': 5, 'verse': 68, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَسۡتُمۡ عَلَىٰ شَيۡءٍ حَتَّىٰ تُقِيمُواْ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡۗ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۖ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\n«Эй, аҳли китоблар! Таврот, Инжил ва сизга Роббингиздан нозил қилинган нарсага амал қилмагунингизча ҳеч нарсага арзимассизлар», деб айт. Сенга Роббингдан нозил қилинган нарса улардан кўпларининг туғён ва куфрларини зиёда қилур. Кофир қавмлар учун ачинма'}, {'chapter': 5, 'verse': 69, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلصَّـٰبِـُٔونَ وَٱلنَّصَٰرَىٰ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта, иймон келтирганлар, яҳудий бўлганлар, собиъунлар ва насоролардан ким Аллоҳга ва қиёмат кунига иймон келтирса ва яхши амал қилса, уларга хавф йўқ ва улар хафа ҳам бўлмаслар'}, {'chapter': 5, 'verse': 70, 'text': 'لَقَدۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَأَرۡسَلۡنَآ إِلَيۡهِمۡ رُسُلٗاۖ كُلَّمَا جَآءَهُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُهُمۡ فَرِيقٗا كَذَّبُواْ وَفَرِيقٗا يَقۡتُلُونَ\nБатаҳқиқ, Биз Бани Исроилдан мийсоқ — аҳднома олдик ва уларга Пайғамбарлар юбордик. Ҳар қачон уларнинг ҳавойи нафсларига тўғри келмайдиган нарса келтирган Пайғамбарни: бир гуруҳларини ёлғончи дедилар, бир гуруҳларини ўлдирдилар'}, {'chapter': 5, 'verse': 71, 'text': 'وَحَسِبُوٓاْ أَلَّا تَكُونَ فِتۡنَةٞ فَعَمُواْ وَصَمُّواْ ثُمَّ تَابَ ٱللَّهُ عَلَيۡهِمۡ ثُمَّ عَمُواْ وَصَمُّواْ كَثِيرٞ مِّنۡهُمۡۚ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ\nУлар, ҳеч қандай синов бўлмас, деб кўру кар бўлиб олдилар. Сўнгра, Аллоҳ уларни кечирди. Кейин улардан кўплари яна кўру кар бўлиб олдилар. Аллоҳ уларнинг қилаётганини кўрувчи Зотдир'}, {'chapter': 5, 'verse': 72, 'text': 'لَقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَۖ وَقَالَ ٱلۡمَسِيحُ يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱعۡبُدُواْ ٱللَّهَ رَبِّي وَرَبَّكُمۡۖ إِنَّهُۥ مَن يُشۡرِكۡ بِٱللَّهِ فَقَدۡ حَرَّمَ ٱللَّهُ عَلَيۡهِ ٱلۡجَنَّةَ وَمَأۡوَىٰهُ ٱلنَّارُۖ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٖ\nАллоҳ, албатта, Масийҳ ибн Марямдир, деганлар, батаҳқиқ, кофир бўлдилар. Масийҳ ибн Марям эса: «Менинг Роббим ва сизнинг Роббингиз Аллоҳга ибодат қилинг. Ким Аллоҳга ширк келтирса, албатта, Аллоҳ унга жаннатни ҳаром қилур ва унинг турар жойи дўзах бўлур. Ва золимларга нусрат берувчилар йўқдир», деди'}, {'chapter': 5, 'verse': 73, 'text': 'لَّقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ ثَالِثُ ثَلَٰثَةٖۘ وَمَا مِنۡ إِلَٰهٍ إِلَّآ إِلَٰهٞ وَٰحِدٞۚ وَإِن لَّمۡ يَنتَهُواْ عَمَّا يَقُولُونَ لَيَمَسَّنَّ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٌ\nАллоҳ учтанинг учинчисидир, деганлар, батаҳқиқ, кофир бўлдилар. Ягона илоҳдан ўзга ҳеч қандай илоҳ йўқ! Агар айтаётганларидан қайтмасалар, улардан куфр келтирганларини аламли азоб тутадир'}, {'chapter': 5, 'verse': 74, 'text': 'أَفَلَا يَتُوبُونَ إِلَى ٱللَّهِ وَيَسۡتَغۡفِرُونَهُۥۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nАллоҳга тавба қилиб, унга истиғфор айтмайдиларми?! Зотан, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 5, 'verse': 75, 'text': 'مَّا ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُ وَأُمُّهُۥ صِدِّيقَةٞۖ كَانَا يَأۡكُلَانِ ٱلطَّعَامَۗ ٱنظُرۡ كَيۡفَ نُبَيِّنُ لَهُمُ ٱلۡأٓيَٰتِ ثُمَّ ٱنظُرۡ أَنَّىٰ يُؤۡفَكُونَ\nМасийҳ ибн Марям бир Пайғамбар, холос. Ундан олдин ҳам Пайғамбарлар ўтган. Унинг онаси эса, сиддиқадир. Икковлари ҳам таом ер эдилар. Биз уларга оятларни қандай баён қилаётганимизга назар сол-у, кейин уларнинг қандай бурилиб кетаётганларига назар сол'}, {'chapter': 5, 'verse': 76, 'text': 'قُلۡ أَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا نَفۡعٗاۚ وَٱللَّهُ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nСен: «Аллоҳни қўйиб, сизга зарар ҳам, фойда ҳам бера олмайдиган нарсага ибодат қиласизларми?! Аллоҳ эшитувчи ва билувчи Зотдир», деб айт'}, {'chapter': 5, 'verse': 77, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ غَيۡرَ ٱلۡحَقِّ وَلَا تَتَّبِعُوٓاْ أَهۡوَآءَ قَوۡمٖ قَدۡ ضَلُّواْ مِن قَبۡلُ وَأَضَلُّواْ كَثِيرٗا وَضَلُّواْ عَن سَوَآءِ ٱلسَّبِيلِ\nСен: «Эй, аҳли китоблар! Динингизда ҳаддан ошиб, ноҳақлик қилманг. Ва олдин ўзлари адашиб ва кўпчиликни адаштириб, тўғри йўлдан умуман чиққанларнинг ҳавойи нафсларига эргашманг», деб айт'}, {'chapter': 5, 'verse': 78, 'text': 'لُعِنَ ٱلَّذِينَ كَفَرُواْ مِنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ عَلَىٰ لِسَانِ دَاوُۥدَ وَعِيسَى ٱبۡنِ مَرۡيَمَۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ\nБани Исроилдан куфр келтирганлари Довуд ва Ийсо ибн Марям тилида лаънатландилар. Бу эса, исён қилганлари ва тажовузкор бўлганлари учун бўлди'}, {'chapter': 5, 'verse': 79, 'text': 'كَانُواْ لَا يَتَنَاهَوۡنَ عَن مُّنكَرٖ فَعَلُوهُۚ لَبِئۡسَ مَا كَانُواْ يَفۡعَلُونَ\nУлар, қилинган ёмон ишдан бир-бирларини қайтармас эдилар. Бу қилмишлари қандай ҳам ёмон бўлди'}, {'chapter': 5, 'verse': 80, 'text': 'تَرَىٰ كَثِيرٗا مِّنۡهُمۡ يَتَوَلَّوۡنَ ٱلَّذِينَ كَفَرُواْۚ لَبِئۡسَ مَا قَدَّمَتۡ لَهُمۡ أَنفُسُهُمۡ أَن سَخِطَ ٱللَّهُ عَلَيۡهِمۡ وَفِي ٱلۡعَذَابِ هُمۡ خَٰلِدُونَ\nУлардан кўпларининг куфр келтирганларни валий — дўст тутганини кўрасан. Уларга нафслари тақдим қилган нарса қандай ҳам ёмон бўлди. Аллоҳ уларга ғазаб қилди. Ва улар азобда боқий қолувчилардир'}, {'chapter': 5, 'verse': 81, 'text': 'وَلَوۡ كَانُواْ يُؤۡمِنُونَ بِٱللَّهِ وَٱلنَّبِيِّ وَمَآ أُنزِلَ إِلَيۡهِ مَا ٱتَّخَذُوهُمۡ أَوۡلِيَآءَ وَلَٰكِنَّ كَثِيرٗا مِّنۡهُمۡ فَٰسِقُونَ\nАгар Аллоҳга, Пайғамбарга ва унга нозил қилинган нарсага иймон келтирганларида эди, уларни валий — дўст тутмас эдилар. Лекин улардан кўплари фосиқлардир'}, {'chapter': 5, 'verse': 82, 'text': '۞لَتَجِدَنَّ أَشَدَّ ٱلنَّاسِ عَدَٰوَةٗ لِّلَّذِينَ ءَامَنُواْ ٱلۡيَهُودَ وَٱلَّذِينَ أَشۡرَكُواْۖ وَلَتَجِدَنَّ أَقۡرَبَهُم مَّوَدَّةٗ لِّلَّذِينَ ءَامَنُواْ ٱلَّذِينَ قَالُوٓاْ إِنَّا نَصَٰرَىٰۚ ذَٰلِكَ بِأَنَّ مِنۡهُمۡ قِسِّيسِينَ وَرُهۡبَانٗا وَأَنَّهُمۡ لَا يَسۡتَكۡبِرُونَ\nЯҳудий ва ширк келтирганларнинг иймон келтирганларга нисбатан одамларнинг ашаддий адоватлиси эканини топасан. Улардан иймон келтирганларга дўстликка яқинроғи, биз насоролармиз, деганлар эканини топасан. Бу, уларнинг ичида диний олимлар ва роҳиблар бўлганидан ва уларнинг такаббурлик қилмасликларидандир'}, {'chapter': 5, 'verse': 83, 'text': 'وَإِذَا سَمِعُواْ مَآ أُنزِلَ إِلَى ٱلرَّسُولِ تَرَىٰٓ أَعۡيُنَهُمۡ تَفِيضُ مِنَ ٱلدَّمۡعِ مِمَّا عَرَفُواْ مِنَ ٱلۡحَقِّۖ يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِينَ\nУларни Пайғамбарга нозил қилинган нарсани тинглаганларида, ҳақни билганликларидан, кўзлари ёш тўкканини кўрасан. Улар: «Эй Роббимиз, биз иймон келтирдик, бас, бизни шоҳидлар қаторига ёзгин», – дерлар'}, {'chapter': 5, 'verse': 84, 'text': 'وَمَا لَنَا لَا نُؤۡمِنُ بِٱللَّهِ وَمَا جَآءَنَا مِنَ ٱلۡحَقِّ وَنَطۡمَعُ أَن يُدۡخِلَنَا رَبُّنَا مَعَ ٱلۡقَوۡمِ ٱلصَّـٰلِحِينَ\n«Нима учун Аллоҳга ва бизга келган ҳаққа иймон келтирмас эканмиз ва Роббимиз бизни ҳам солиҳ қавмлар қаторига киритишини тамаъ қилмас эканмиз?!» – дерлар'}, {'chapter': 5, 'verse': 85, 'text': 'فَأَثَٰبَهُمُ ٱللَّهُ بِمَا قَالُواْ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ ٱلۡمُحۡسِنِينَ\nАйтганларига яраша Аллоҳ уларга остидан анҳорлар оқиб турган жаннатларда боқий қолишни мукофот қилиб берди. Яхшилик қилувчиларнинг мукофоти шулдир'}, {'chapter': 5, 'verse': 86, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nКуфр келтирганлар ва оятларимизни ёлғонга чиқарганлар — ана ўшалар жаҳаннам эгаларидир'}, {'chapter': 5, 'verse': 87, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُحَرِّمُواْ طَيِّبَٰتِ مَآ أَحَلَّ ٱللَّهُ لَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ\nЭй, иймон келтирганлар! Аллоҳ сизга ҳалол қилган пок нарсаларни ҳаром қилманг. Ҳаддингиздан ошманг. Албатта, Аллоҳ ҳаддидан ошувчиларни севмас'}, {'chapter': 5, 'verse': 88, 'text': 'وَكُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَٰلٗا طَيِّبٗاۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ\nВа Аллоҳ сизга ризқ қилиб берган ҳалол-пок нарсалардан енглар. Ўзингиз ишонган Аллоҳга тақво қилинг'}, {'chapter': 5, 'verse': 89, 'text': 'لَا يُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا عَقَّدتُّمُ ٱلۡأَيۡمَٰنَۖ فَكَفَّـٰرَتُهُۥٓ إِطۡعَامُ عَشَرَةِ مَسَٰكِينَ مِنۡ أَوۡسَطِ مَا تُطۡعِمُونَ أَهۡلِيكُمۡ أَوۡ كِسۡوَتُهُمۡ أَوۡ تَحۡرِيرُ رَقَبَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖۚ ذَٰلِكَ كَفَّـٰرَةُ أَيۡمَٰنِكُمۡ إِذَا حَلَفۡتُمۡۚ وَٱحۡفَظُوٓاْ أَيۡمَٰنَكُمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَشۡكُرُونَ\nАллоҳ сизларни беҳуда қасамларингиз учун тутмас. Лекин қасд ила туккан қасамларингиз учун тутадир. Бас, унинг каффороти ўз аҳлингизни ўртача таомлантириш миқдорида ўнта мискинга таом ёки кийим бериш ёхуд бир қул озод қилишдир. Ким топа олмаса, бас, уч кун рўза тутсин. Ана ўша, агар қасам ичган бўлсангиз, қасамингизнинг каффоротидир. Қасамларингизни муҳофаза қилинг. Аллоҳ сизга шундай қилиб Ўз оятларини баён этади. Шоядки, шукр қилсангиз'}, {'chapter': 5, 'verse': 90, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّمَا ٱلۡخَمۡرُ وَٱلۡمَيۡسِرُ وَٱلۡأَنصَابُ وَٱلۡأَزۡلَٰمُ رِجۡسٞ مِّنۡ عَمَلِ ٱلشَّيۡطَٰنِ فَٱجۡتَنِبُوهُ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй, иймон келтирганлар! Албатта, хамр, қимор, бутлар ва (фол очадиган) чўплар ифлосдир. Шайтоннинг ишидир. Бас, ундан четда бўлинг. Шоядки, нажот топсангиз'}, {'chapter': 5, 'verse': 91, 'text': 'إِنَّمَا يُرِيدُ ٱلشَّيۡطَٰنُ أَن يُوقِعَ بَيۡنَكُمُ ٱلۡعَدَٰوَةَ وَٱلۡبَغۡضَآءَ فِي ٱلۡخَمۡرِ وَٱلۡمَيۡسِرِ وَيَصُدَّكُمۡ عَن ذِكۡرِ ٱللَّهِ وَعَنِ ٱلصَّلَوٰةِۖ فَهَلۡ أَنتُم مُّنتَهُونَ\nАлбатта, шайтон хамр ва қимор туфайли ораларингизга адоват ва ёмон кўришликни солишни ҳамда сизларни Аллоҳнинг зикридан ва намоздан тўсишни хоҳлайдир. Энди тўхтарсизлар'}, {'chapter': 5, 'verse': 92, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَٱحۡذَرُواْۚ فَإِن تَوَلَّيۡتُمۡ فَٱعۡلَمُوٓاْ أَنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАллоҳга итоат қилинг ва Пайғамбарга итоат қилинг ва ҳазир бўлинг! Агар юз ўгирадиган бўлсангиз, билингки, Пайғамбаримиз вазифаси аниқ етказиш, холос'}, {'chapter': 5, 'verse': 93, 'text': 'لَيۡسَ عَلَى ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جُنَاحٞ فِيمَا طَعِمُوٓاْ إِذَا مَا ٱتَّقَواْ وَّءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ ثُمَّ ٱتَّقَواْ وَّءَامَنُواْ ثُمَّ ٱتَّقَواْ وَّأَحۡسَنُواْۚ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ\nИймон келтирганлар учун, агар тақво қилиб, иймон келтириб, яхши амалларни қилган бўлсалар, сўнгра тақво қилиб, иймон келтирсалар, кейин тақво қилиб эҳсон қилсалар, еб-ичганларида гуноҳ йўқдир. Зотан, Аллоҳ эҳсон қилувчиларни севадир'}, {'chapter': 5, 'verse': 94, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَيَبۡلُوَنَّكُمُ ٱللَّهُ بِشَيۡءٖ مِّنَ ٱلصَّيۡدِ تَنَالُهُۥٓ أَيۡدِيكُمۡ وَرِمَاحُكُمۡ لِيَعۡلَمَ ٱللَّهُ مَن يَخَافُهُۥ بِٱلۡغَيۡبِۚ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ\nЭй, иймон келтирганлар! Аллоҳ Ундан ким ғойибона қўрқишини билиш учун сизларни қўлларингиз ва найзаларингиз етадиган овларда бир оз синаб кўрадир. Бундан кейин ким тажовуз қилса, унга аламли азоб бор'}, {'chapter': 5, 'verse': 95, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقۡتُلُواْ ٱلصَّيۡدَ وَأَنتُمۡ حُرُمٞۚ وَمَن قَتَلَهُۥ مِنكُم مُّتَعَمِّدٗا فَجَزَآءٞ مِّثۡلُ مَا قَتَلَ مِنَ ٱلنَّعَمِ يَحۡكُمُ بِهِۦ ذَوَا عَدۡلٖ مِّنكُمۡ هَدۡيَۢا بَٰلِغَ ٱلۡكَعۡبَةِ أَوۡ كَفَّـٰرَةٞ طَعَامُ مَسَٰكِينَ أَوۡ عَدۡلُ ذَٰلِكَ صِيَامٗا لِّيَذُوقَ وَبَالَ أَمۡرِهِۦۗ عَفَا ٱللَّهُ عَمَّا سَلَفَۚ وَمَنۡ عَادَ فَيَنتَقِمُ ٱللَّهُ مِنۡهُۚ وَٱللَّهُ عَزِيزٞ ذُو ٱنتِقَامٍ\nЭй, иймон келтирганлар! Эҳромдалик ҳолингизда овни ўлдирманг. Сиздан ким уни қасдан ўлдирса, жазоси сиздан икки одил киши ҳукмига мувофиқ чорва ҳайвонларидан у(ов)га тенг келадиганини ҳадйи қилиб Каъбага етказишдир. Ёхуд унинг каффороти мискинларга таом бериш ёки шу баробарида, ўз қилмишининг ёмон оқибатини татиб кўрсин учун, рўза тутишдир. Ўтганларини Аллоҳ афв қилди. Ким қайтарса, Аллоҳ ундан интиқом олур. Зотан, Аллоҳ Азийз, интиқом эгасидир'}, {'chapter': 5, 'verse': 96, 'text': 'أُحِلَّ لَكُمۡ صَيۡدُ ٱلۡبَحۡرِ وَطَعَامُهُۥ مَتَٰعٗا لَّكُمۡ وَلِلسَّيَّارَةِۖ وَحُرِّمَ عَلَيۡكُمۡ صَيۡدُ ٱلۡبَرِّ مَا دُمۡتُمۡ حُرُمٗاۗ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ\nДенгиз ови ва унинг таоми сизга ва сайр қилувчиларга ҳалол қилинди. Модомики, эҳромда экансиз, сизга қуруқлик ови ҳаром қилинди. Ҳузурида тўпланадиганингиз Аллоҳга тақво қилинг'}, {'chapter': 5, 'verse': 97, 'text': '۞جَعَلَ ٱللَّهُ ٱلۡكَعۡبَةَ ٱلۡبَيۡتَ ٱلۡحَرَامَ قِيَٰمٗا لِّلنَّاسِ وَٱلشَّهۡرَ ٱلۡحَرَامَ وَٱلۡهَدۡيَ وَٱلۡقَلَـٰٓئِدَۚ ذَٰلِكَ لِتَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَأَنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАллоҳ Каъбани — Байтул Ҳаромни, ҳаром ойни, ҳадйини ва осиладиган белгиларни одамлар учун асос қилиб қўйди. Бу эса, Аллоҳ албатта осмонлару ердаги нарсаларни билишини ва албатта Аллоҳ ҳар бир нарсани билувчи Зот эканини билишингиз учундир'}, {'chapter': 5, 'verse': 98, 'text': 'ٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ وَأَنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nБилингки, албатта, Аллоҳнинг иқоби шиддатлидир ва, албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 5, 'verse': 99, 'text': 'مَّا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُۗ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ\nПайғамбар зиммасида фақат етказиш бор, холос. Аллоҳ ошкор қилганингизни ҳам, яширганингизни ҳам биладир'}, {'chapter': 5, 'verse': 100, 'text': 'قُل لَّا يَسۡتَوِي ٱلۡخَبِيثُ وَٱلطَّيِّبُ وَلَوۡ أَعۡجَبَكَ كَثۡرَةُ ٱلۡخَبِيثِۚ فَٱتَّقُواْ ٱللَّهَ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ لَعَلَّكُمۡ تُفۡلِحُونَ\nСен: «Агар нопокнинг кўплиги сени ажаблантирса ҳам, пок билан нопок тенг бўла олмас. Эй, ақл эгалари, Аллоҳга тақво қилинг, шоядки, нажот топсангиз», деб айт'}, {'chapter': 5, 'verse': 101, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَسۡـَٔلُواْ عَنۡ أَشۡيَآءَ إِن تُبۡدَ لَكُمۡ تَسُؤۡكُمۡ وَإِن تَسۡـَٔلُواْ عَنۡهَا حِينَ يُنَزَّلُ ٱلۡقُرۡءَانُ تُبۡدَ لَكُمۡ عَفَا ٱللَّهُ عَنۡهَاۗ وَٱللَّهُ غَفُورٌ حَلِيمٞ\nЭй, иймон келтирганлар! Ҳар хил нарсаларни сўрайверманглар. Агар зоҳир бўлса, сизга ёмон бўладир. Агар Қуръон нозил қилиниб турган чоғда сўрасангиз, сизга зоҳир бўладир. Ҳолбуки, Аллоҳ уларни афв қилган эди. Аллоҳ мағфиратли ва Ҳалийм Зотдир'}, {'chapter': 5, 'verse': 102, 'text': 'قَدۡ سَأَلَهَا قَوۡمٞ مِّن قَبۡلِكُمۡ ثُمَّ أَصۡبَحُواْ بِهَا كَٰفِرِينَ\nСиздан олдин ҳам бир қавм уларни сўраган, сўнгра эса, уларга кофир бўлган эди'}, {'chapter': 5, 'verse': 103, 'text': 'مَا جَعَلَ ٱللَّهُ مِنۢ بَحِيرَةٖ وَلَا سَآئِبَةٖ وَلَا وَصِيلَةٖ وَلَا حَامٖ وَلَٰكِنَّ ٱلَّذِينَ كَفَرُواْ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَأَكۡثَرُهُمۡ لَا يَعۡقِلُونَ\nАллоҳ баҳийра, соиба, васийла ва ҳоммларни қилгани йўқ. Лекин куфр келтирганлар Аллоҳга нисбатан ёлғон тўқирлар. Уларнинг кўплари ақлсиздирлар'}, {'chapter': 5, 'verse': 104, 'text': 'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ قَالُواْ حَسۡبُنَا مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡلَمُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ\nУларга: «Аллоҳ нозил қилган нарсага, Пайғамбарга келинглар», дейилса, «Бизга ота-боболаримизни ниманинг устида топган бўлсак, ўша етарли», дерлар. Агар ота-боболари ҳеч нарсага ақли етмайдиган ва ҳидоятга эришмаган бўлсалар ҳам-а'}, {'chapter': 5, 'verse': 105, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ عَلَيۡكُمۡ أَنفُسَكُمۡۖ لَا يَضُرُّكُم مَّن ضَلَّ إِذَا ٱهۡتَدَيۡتُمۡۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nЭй, иймон келтирганлар! Ўзингизни билинг. Агар ҳидоятда бўлсангиз, адашганлари сизга зарар қилмас. Ҳаммангизнинг қайтиб борадиган жойингиз Аллоҳнинг ҳузурида. Ўшанда сизга қилиб юрган ишларингизнинг хабарини берадир'}, {'chapter': 5, 'verse': 106, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ شَهَٰدَةُ بَيۡنِكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ حِينَ ٱلۡوَصِيَّةِ ٱثۡنَانِ ذَوَا عَدۡلٖ مِّنكُمۡ أَوۡ ءَاخَرَانِ مِنۡ غَيۡرِكُمۡ إِنۡ أَنتُمۡ ضَرَبۡتُمۡ فِي ٱلۡأَرۡضِ فَأَصَٰبَتۡكُم مُّصِيبَةُ ٱلۡمَوۡتِۚ تَحۡبِسُونَهُمَا مِنۢ بَعۡدِ ٱلصَّلَوٰةِ فَيُقۡسِمَانِ بِٱللَّهِ إِنِ ٱرۡتَبۡتُمۡ لَا نَشۡتَرِي بِهِۦ ثَمَنٗا وَلَوۡ كَانَ ذَا قُرۡبَىٰ وَلَا نَكۡتُمُ شَهَٰدَةَ ٱللَّهِ إِنَّآ إِذٗا لَّمِنَ ٱلۡأٓثِمِينَ\nЭй, иймон келтирганлар! Бирортангизга ўлим келганда — васият қилар чоғида ўзингиздан бўлган икки адолатли киши, агар ер юзида сафардалик чоғингизда ўлим мусийбати етса, ўзгалардан икки киши ўртангизда гувоҳ бўлсин. Агар шубҳа қилсангиз, гувоҳларни намоздан кейин тутасиз ва улар: «Гарчи қариндошимиз учун бўлса ҳам, қасамни озгина қийматга сотмасмиз ва Аллоҳ учун гувоҳлигимизни яширмасмиз. Зотан, у ҳолда, албатта, гуноҳкорлардан бўлиб қоламиз», деб Аллоҳ номи ила қасам ичарлар'}, {'chapter': 5, 'verse': 107, 'text': 'فَإِنۡ عُثِرَ عَلَىٰٓ أَنَّهُمَا ٱسۡتَحَقَّآ إِثۡمٗا فَـَٔاخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ ٱلَّذِينَ ٱسۡتَحَقَّ عَلَيۡهِمُ ٱلۡأَوۡلَيَٰنِ فَيُقۡسِمَانِ بِٱللَّهِ لَشَهَٰدَتُنَآ أَحَقُّ مِن شَهَٰدَتِهِمَا وَمَا ٱعۡتَدَيۡنَآ إِنَّآ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ\nАгар икковларининг гуноҳлари билиниб қолса, ўринларига устиларига ноҳақлик тушган яқинлардан иккиталари ўтурлар ва Аллоҳ ила қасам ичиб: «Бизнинг гувоҳлигимиз уларнинг гувоҳликларидан тўғрироқ ва биз тажовуз қилмадик. Агар қилган бўлсак, албатта, золимлардан бўлурмиз», дейишади'}, {'chapter': 5, 'verse': 108, 'text': 'ذَٰلِكَ أَدۡنَىٰٓ أَن يَأۡتُواْ بِٱلشَّهَٰدَةِ عَلَىٰ وَجۡهِهَآ أَوۡ يَخَافُوٓاْ أَن تُرَدَّ أَيۡمَٰنُۢ بَعۡدَ أَيۡمَٰنِهِمۡۗ وَٱتَّقُواْ ٱللَّهَ وَٱسۡمَعُواْۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\nАна шундай қилмоқлик, гувоҳликни ҳаққоний адо этишларига ёки қасамларидан сўнг раддия қасам бўлишидан қўрқишларига яқинроқдир. Аллоҳга тақво қилинглар ва қулоқ осинглар. Аллоҳ фосиқ қавмларни ҳидоят қилмас'}, {'chapter': 5, 'verse': 109, 'text': '۞يَوۡمَ يَجۡمَعُ ٱللَّهُ ٱلرُّسُلَ فَيَقُولُ مَاذَآ أُجِبۡتُمۡۖ قَالُواْ لَا عِلۡمَ لَنَآۖ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُيُوبِ\nАллоҳ Пайғамбарларни жамлаган кунда: «Сизга қандай жавоб қилинди?» – деб сўрайдир. Улар: «Биз билмаймиз, албатта, Сенинг Ўзинггина ғайбларни яхши билгувчисан», дерлар'}, {'chapter': 5, 'verse': 110, 'text': 'إِذۡ قَالَ ٱللَّهُ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ ٱذۡكُرۡ نِعۡمَتِي عَلَيۡكَ وَعَلَىٰ وَٰلِدَتِكَ إِذۡ أَيَّدتُّكَ بِرُوحِ ٱلۡقُدُسِ تُكَلِّمُ ٱلنَّاسَ فِي ٱلۡمَهۡدِ وَكَهۡلٗاۖ وَإِذۡ عَلَّمۡتُكَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَۖ وَإِذۡ تَخۡلُقُ مِنَ ٱلطِّينِ كَهَيۡـَٔةِ ٱلطَّيۡرِ بِإِذۡنِي فَتَنفُخُ فِيهَا فَتَكُونُ طَيۡرَۢا بِإِذۡنِيۖ وَتُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ بِإِذۡنِيۖ وَإِذۡ تُخۡرِجُ ٱلۡمَوۡتَىٰ بِإِذۡنِيۖ وَإِذۡ كَفَفۡتُ بَنِيٓ إِسۡرَـٰٓءِيلَ عَنكَ إِذۡ جِئۡتَهُم بِٱلۡبَيِّنَٰتِ فَقَالَ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nАллоҳ: «Эй, Ийсо ибн Марям, сенга ва волидангга берган неъматимни эсла. Сени муқаддас руҳ билан қўллаганимни, одамларга бешикда ҳам, катталигингда ҳам гапирганингни эсла. Сенга китобни, ҳикматни, Тавротни ва Инжилни ўргатганимни эсла. Менинг изним ила лойдан қуш шаклини ясаганингда ва унга пуфлаганингда, Менинг изним ила қуш бўлганини, Менинг изним ила туғма кўрни ва песни тузатганингни эсла. Менинг изним ила ўликларни чиқарганингни эсла. Бани Исроилга аниқ мўъжизалар келтирганингда, улардан куфр келтирганлари: «Бу очиқ сеҳрдан ўзга ҳеч нарса эмас», деганида, сени улардан қутқарганимини эсла», деганини ёд эт'}, {'chapter': 5, 'verse': 111, 'text': 'وَإِذۡ أَوۡحَيۡتُ إِلَى ٱلۡحَوَارِيِّـۧنَ أَنۡ ءَامِنُواْ بِي وَبِرَسُولِي قَالُوٓاْ ءَامَنَّا وَٱشۡهَدۡ بِأَنَّنَا مُسۡلِمُونَ\nҲаворийларга: «Менга ва Менинг Пайғамбаримга иймон келтиринг», деб ваҳий юборганимда, улар: «Иймон келтирдик ва шоҳид бўл, биз албатта, мусулмонлармиз», деганини эсла'}, {'chapter': 5, 'verse': 112, 'text': 'إِذۡ قَالَ ٱلۡحَوَارِيُّونَ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ هَلۡ يَسۡتَطِيعُ رَبُّكَ أَن يُنَزِّلَ عَلَيۡنَا مَآئِدَةٗ مِّنَ ٱلسَّمَآءِۖ قَالَ ٱتَّقُواْ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِينَ\nҲаворийлар: «Эй, Ийсо ибн Марям, сенинг Роббинг бизга осмондан тузатилган дастурхон тушира олурми?» – деганларида, у: «Агар мўмин бўлсаларингиз, Аллоҳга тақво қилинглар», деганини эсла'}, {'chapter': 5, 'verse': 113, 'text': 'قَالُواْ نُرِيدُ أَن نَّأۡكُلَ مِنۡهَا وَتَطۡمَئِنَّ قُلُوبُنَا وَنَعۡلَمَ أَن قَدۡ صَدَقۡتَنَا وَنَكُونَ عَلَيۡهَا مِنَ ٱلشَّـٰهِدِينَ\nУлар: «Ундан емоқни, қалбларимиз таскин топмоғини, бизга рост гапирганингни билмоқни ва бунга гувоҳ бўлмоқни истармиз», дедилар'}, {'chapter': 5, 'verse': 114, 'text': 'قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ ٱللَّهُمَّ رَبَّنَآ أَنزِلۡ عَلَيۡنَا مَآئِدَةٗ مِّنَ ٱلسَّمَآءِ تَكُونُ لَنَا عِيدٗا لِّأَوَّلِنَا وَءَاخِرِنَا وَءَايَةٗ مِّنكَۖ وَٱرۡزُقۡنَا وَأَنتَ خَيۡرُ ٱلرَّـٰزِقِينَ\nИйсо ибн Марям: «Аллоҳим! Роббимиз! Бизга осмондан ясатилган дастурхон туширгин, у аввалимизгаю охиримизга байрам бўлиб қолсин. Сендан мўъжиза бўлиб қолсин. Ва бизга ризқ бергин, зотан Ўзинг энг яхши ризқ бергувчисан», деди'}, {'chapter': 5, 'verse': 115, 'text': 'قَالَ ٱللَّهُ إِنِّي مُنَزِّلُهَا عَلَيۡكُمۡۖ فَمَن يَكۡفُرۡ بَعۡدُ مِنكُمۡ فَإِنِّيٓ أُعَذِّبُهُۥ عَذَابٗا لَّآ أُعَذِّبُهُۥٓ أَحَدٗا مِّنَ ٱلۡعَٰلَمِينَ\nАллоҳ: «Мен, албатта, уни сизларга туширгувчиман. Бас, бундан кейин сизлардан ким куфр келтирса, уни оламларда ҳеч кимни азобламаган азоб ила азоблагайман», деди'}, {'chapter': 5, 'verse': 116, 'text': 'وَإِذۡ قَالَ ٱللَّهُ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ ءَأَنتَ قُلۡتَ لِلنَّاسِ ٱتَّخِذُونِي وَأُمِّيَ إِلَٰهَيۡنِ مِن دُونِ ٱللَّهِۖ قَالَ سُبۡحَٰنَكَ مَا يَكُونُ لِيٓ أَنۡ أَقُولَ مَا لَيۡسَ لِي بِحَقٍّۚ إِن كُنتُ قُلۡتُهُۥ فَقَدۡ عَلِمۡتَهُۥۚ تَعۡلَمُ مَا فِي نَفۡسِي وَلَآ أَعۡلَمُ مَا فِي نَفۡسِكَۚ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُيُوبِ\nАллоҳ: «Эй, Ийсо ибн Марям, сен одамларга, Аллоҳни қўйиб, мени ва онамни илоҳ қилиб олинглар, дедингми?» – деганини, у эса: «Ўзинг поксан! (Аллоҳим), мен ҳаққим йўқ нарсани айта олмайман-ку. Агар айтган бўлганимда, батаҳқиқ, Сен уни билар эдинг. Сен менинг дилимдагини биласан, мен Сендаги ҳеч нарсани билмасман. Албатта, Сен Ўзинг ғайбларни энг яхши билгувчи Зотсан», деганини эсла'}, {'chapter': 5, 'verse': 117, 'text': 'مَا قُلۡتُ لَهُمۡ إِلَّا مَآ أَمَرۡتَنِي بِهِۦٓ أَنِ ٱعۡبُدُواْ ٱللَّهَ رَبِّي وَرَبَّكُمۡۚ وَكُنتُ عَلَيۡهِمۡ شَهِيدٗا مَّا دُمۡتُ فِيهِمۡۖ فَلَمَّا تَوَفَّيۡتَنِي كُنتَ أَنتَ ٱلرَّقِيبَ عَلَيۡهِمۡۚ وَأَنتَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nМен уларга Ўзинг менга амр қилган нарса: «Аллоҳга, менинг Роббим ва сизнинг Роббингизга ибодат қилинг»дан бошқани айтганим йўқ. Ва, модомики, ораларида эканман, уларга гувоҳ бўлдим. Мени Ўзингга олганингдан сўнг, Сенинг Ўзинг уларга кузатувчи бўлдинг. Зотан, Сен ҳар бир нарсага гувоҳсан'}, {'chapter': 5, 'verse': 118, 'text': 'إِن تُعَذِّبۡهُمۡ فَإِنَّهُمۡ عِبَادُكَۖ وَإِن تَغۡفِرۡ لَهُمۡ فَإِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАгар уларни азоблайдиган бўлсанг, бас, албатта, улар Сенинг бандаларинг. Агар уларни мағфират қиладиган бўлсанг, бас, албатта, Сенинг Ўзинг Азийз — ғолиб ва ҳикматли Зотсан'}, {'chapter': 5, 'verse': 119, 'text': 'قَالَ ٱللَّهُ هَٰذَا يَوۡمُ يَنفَعُ ٱلصَّـٰدِقِينَ صِدۡقُهُمۡۚ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАллоҳ: «Бу кун, ростгўйларга ростгўйликлари манфаат берадиган кун. Уларга остидан сувлар оқиб турган жаннатлар бор, унда абадий — боқий бўлурлар. Аллоҳ улардан рози бўлди. Улар Аллоҳдан рози бўлди. Бу эса, улуғ ютуқдир», – деди'}, {'chapter': 5, 'verse': 120, 'text': 'لِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا فِيهِنَّۚ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرُۢ\nОсмонлару ер ва улардаги нарсалар Аллоҳнинг мулкидир. Ва У ҳар бир нарсага қодирдир'}], '6': [{'chapter': 6, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَجَعَلَ ٱلظُّلُمَٰتِ وَٱلنُّورَۖ ثُمَّ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡ يَعۡدِلُونَ\nОсмонлару ерни яратган, зулматлару нурни таратган Аллоҳга ҳамд бўлсин. Сўнгра, куфр келтирганлар ўз Роббиларига (ўзгаларни) тенглаштирурлар'}, {'chapter': 6, 'verse': 2, 'text': 'هُوَ ٱلَّذِي خَلَقَكُم مِّن طِينٖ ثُمَّ قَضَىٰٓ أَجَلٗاۖ وَأَجَلٞ مُّسَمًّى عِندَهُۥۖ ثُمَّ أَنتُمۡ تَمۡتَرُونَ\nУ сизларни лойдан яратган, сўнгра ажални белгилаб қўйган Зотдир. Белгиланган ажал эса Унинг ҳузуридадир. Шундан кейин ҳам сиз шак келтирурсиз'}, {'chapter': 6, 'verse': 3, 'text': 'وَهُوَ ٱللَّهُ فِي ٱلسَّمَٰوَٰتِ وَفِي ٱلۡأَرۡضِ يَعۡلَمُ سِرَّكُمۡ وَجَهۡرَكُمۡ وَيَعۡلَمُ مَا تَكۡسِبُونَ\nУ осмонларда ҳам, ерда ҳам Аллоҳдир. Сирингизни ҳам, ошкорингизни ҳам биладир. Қилган касбу корингизни ҳам биладир'}, {'chapter': 6, 'verse': 4, 'text': 'وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ\nМудом уларга ўз Роббилари оятларидан бир оят келса, ундан юз ўгирувчи бўлдилар'}, {'chapter': 6, 'verse': 5, 'text': 'فَقَدۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَسَوۡفَ يَأۡتِيهِمۡ أَنۢبَـٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУларга ҳақ келганда уни ҳам ёлғонга чиқардилар. Тезда уларга ўзлари истеҳзо қилган нарсанинг хабари келур'}, {'chapter': 6, 'verse': 6, 'text': 'أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ مَّكَّنَّـٰهُمۡ فِي ٱلۡأَرۡضِ مَا لَمۡ نُمَكِّن لَّكُمۡ وَأَرۡسَلۡنَا ٱلسَّمَآءَ عَلَيۡهِم مِّدۡرَارٗا وَجَعَلۡنَا ٱلۡأَنۡهَٰرَ تَجۡرِي مِن تَحۡتِهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ\nУлардан олдин қанча асрларни ҳалок қилганимизни билмасларми? Ўшаларга ер юзида сизларга бермаган имконларни берган эдик. Осмондан устларига барака ёмғири юборган ва остларидан анҳорлар оқиб турадиган қилиб қўйган эдик. Бас, гуноҳлари туфайли уларни ҳалок қилдик ва ўринларига бошқа асрларни пайдо қилдик'}, {'chapter': 6, 'verse': 7, 'text': 'وَلَوۡ نَزَّلۡنَا عَلَيۡكَ كِتَٰبٗا فِي قِرۡطَاسٖ فَلَمَسُوهُ بِأَيۡدِيهِمۡ لَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nАгар сенга қоғоз — китоб туширсак, уни қўллари билан ушлаб кўришса ҳам, куфр келтирганлар: «Бу аниқ сеҳрдан ўзга ҳеч нарса эмас», дерлар'}, {'chapter': 6, 'verse': 8, 'text': 'وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ مَلَكٞۖ وَلَوۡ أَنزَلۡنَا مَلَكٗا لَّقُضِيَ ٱلۡأَمۡرُ ثُمَّ لَا يُنظَرُونَ\nУлар: «Агар унга фаришта туширилса эди», дедилар. Агар фаришта туширсак, иш битди ҳисоб. Сўнгра уларга қараб турилмас'}, {'chapter': 6, 'verse': 9, 'text': 'وَلَوۡ جَعَلۡنَٰهُ مَلَكٗا لَّجَعَلۡنَٰهُ رَجُلٗا وَلَلَبَسۡنَا عَلَيۡهِم مَّا يَلۡبِسُونَ\nАгар уни фаришта қилсак ҳам, эркак қилган бўлар эдик ва ўзлари мушкуллаштирган нарсани уларга яна ҳам мушкуллаштирар эдик'}, {'chapter': 6, 'verse': 10, 'text': 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nСендан олдинги Пайғамбарлар ҳам истеҳзо қилинганлар. Уларни масхара қилганларни қилган истеҳзолари ўз домига тортган'}, {'chapter': 6, 'verse': 11, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ ثُمَّ ٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\n«Ер юзида сайр қилингиз, сўнгра ёлғончи қилувчиларнинг оқибати қандай бўлганига назар солингиз», деб айт'}, {'chapter': 6, 'verse': 12, 'text': 'قُل لِّمَن مَّا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُل لِّلَّهِۚ كَتَبَ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۚ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ\n«Осмонлар ва ердаги нарсалар кимники?» – деб айт! «Аллоҳники», деб айт! У Ўзига Ўзи раҳматни ёзиб қўйган. Сизларни бўлишида шубҳа йўқ қиёмат куни албатта жамлайдир. Ўзига ўзи зиён келтирувчиларгина, ўшаларгина иймон келтирмаслар'}, {'chapter': 6, 'verse': 13, 'text': '۞وَلَهُۥ مَا سَكَنَ فِي ٱلَّيۡلِ وَٱلنَّهَارِۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nКечада ва кундузда сокин бўлганлар Уникидир. У эшитгувчи ва билгувчи Зотдир'}, {'chapter': 6, 'verse': 14, 'text': 'قُلۡ أَغَيۡرَ ٱللَّهِ أَتَّخِذُ وَلِيّٗا فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَهُوَ يُطۡعِمُ وَلَا يُطۡعَمُۗ قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَكُونَ أَوَّلَ مَنۡ أَسۡلَمَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ\nСен: «Аллоҳдан ўзгани валий — дўст қилиб оламанми? У осмонлару ерни яратгувчидир. У таом беради ва таомланмайди», деб айт. «Мен аввалги таслим бўлувчи бўлмоққа амр қилиндим ва менга ҳеч мушриклардан бўлмагин, деб айтилди», деб айт'}, {'chapter': 6, 'verse': 15, 'text': 'قُلۡ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ\nСен: «Албатта, мен агар Роббимга осий бўлсам, улуғ кун азобидан қўрқаман», деб айт'}, {'chapter': 6, 'verse': 16, 'text': 'مَّن يُصۡرَفۡ عَنۡهُ يَوۡمَئِذٖ فَقَدۡ رَحِمَهُۥۚ وَذَٰلِكَ ٱلۡفَوۡزُ ٱلۡمُبِينُ\nКим ўша куни ундан четланса, бас, батаҳқиқ, Аллоҳ унга раҳим қилибди. Ана ўша аниқ ютуқдир'}, {'chapter': 6, 'verse': 17, 'text': 'وَإِن يَمۡسَسۡكَ ٱللَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يَمۡسَسۡكَ بِخَيۡرٖ فَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАгар Аллоҳ сени зарарига оладиган бўлса, Унинг Ўзидан бошқа кушойиш берадиган йўқ. Агар У сени яхшиликка оладиган бўлса, бас, У ҳар бир нарсага қодирдир'}, {'chapter': 6, 'verse': 18, 'text': 'وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ\nУ бандалари устидан ғолибдур. У ҳикмат эгаси ва ҳар бир нарсадан хабардордир'}, {'chapter': 6, 'verse': 19, 'text': 'قُلۡ أَيُّ شَيۡءٍ أَكۡبَرُ شَهَٰدَةٗۖ قُلِ ٱللَّهُۖ شَهِيدُۢ بَيۡنِي وَبَيۡنَكُمۡۚ وَأُوحِيَ إِلَيَّ هَٰذَا ٱلۡقُرۡءَانُ لِأُنذِرَكُم بِهِۦ وَمَنۢ بَلَغَۚ أَئِنَّكُمۡ لَتَشۡهَدُونَ أَنَّ مَعَ ٱللَّهِ ءَالِهَةً أُخۡرَىٰۚ قُل لَّآ أَشۡهَدُۚ قُلۡ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَإِنَّنِي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ\nСен: «Қайси нарса энг катта гувоҳликдир», деб айт. «Аллоҳ», деб айт. Сен: «У мен билан сизларнинг ўртангизда гувоҳдир. Менга ушбу Қуръонни, Унинг ила сизларни ва у етиб борганларни огоҳлантиришим учун ваҳий қилинди. Сизлар «Аллоҳ билан бирга, албатта, бошқа худолар бор», деб гувоҳлик берасизларми? «Мен гувоҳлик бермайман», деб айт. Сен: «Албатта, У битта илоҳдир ва мен сиз келтираётган ширкдан покдирман», деб айт'}, {'chapter': 6, 'verse': 20, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمُۘ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ\nБиз китоб берганлар уни ўз фарзандларини танигандек танирлар. Улар ўзларига зиён қилганлардир, ана ўшалар иймон келтирмаслар'}, {'chapter': 6, 'verse': 21, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nАллоҳга нисбатан ёлғон тўқиган ёки Унинг оятларини ёлғонга чиқарганлардан ҳам кўра золимроқ кимса борми? Албатта, золимлар нажот топмаслар'}, {'chapter': 6, 'verse': 22, 'text': 'وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُوٓاْ أَيۡنَ شُرَكَآؤُكُمُ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ\nҲаммаларини жамлайдиган, сўнгра ширк келтирганларга: «Сиз худо деб гумон қилган шерикларингиз қани?» – деб айтадиганимиз кунни эсла'}, {'chapter': 6, 'verse': 23, 'text': 'ثُمَّ لَمۡ تَكُن فِتۡنَتُهُمۡ إِلَّآ أَن قَالُواْ وَٱللَّهِ رَبِّنَا مَا كُنَّا مُشۡرِكِينَ\nСўнгра, «Роббимиз Аллоҳга қасамки, мушрик бўлмаган эдик», дейишдан бошқа фитналари бўлмас'}, {'chapter': 6, 'verse': 24, 'text': 'ٱنظُرۡ كَيۡفَ كَذَبُواْ عَلَىٰٓ أَنفُسِهِمۡۚ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nНазар сол! Улар ўзларини қандай алдадилар ва тўқиб юрган нарсалари улардан қандай йўқолди'}, {'chapter': 6, 'verse': 25, 'text': 'وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَۖ وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَاۖ حَتَّىٰٓ إِذَا جَآءُوكَ يُجَٰدِلُونَكَ يَقُولُ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nУлардан сенга қулоқ тутадиганлари ҳам бор. Биз уни фаҳмламасликлари учун қалбларига парда ва қулоқларига тўсиқ қилиб қўйдик. Барча оятларни кўрсалар ҳам, улар иймон келтирмаслар. Ҳатто, олдингга сен билан тортишувга келганларида, куфр келтирганлар: «Бу, ўтганларнинг афсоналаридан бошқа нарса эмас», дерлар'}, {'chapter': 6, 'verse': 26, 'text': 'وَهُمۡ يَنۡهَوۡنَ عَنۡهُ وَيَنۡـَٔوۡنَ عَنۡهُۖ وَإِن يُهۡلِكُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ\nВа улар (одамларни) ундан қайтарурлар ва ундан узоқлашурлар. Улар сезмаган ҳолларида ўзларини ҳалок қилурлар, холос'}, {'chapter': 6, 'verse': 27, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ وُقِفُواْ عَلَى ٱلنَّارِ فَقَالُواْ يَٰلَيۡتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِـَٔايَٰتِ رَبِّنَا وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nДўзах устида тўхтатилганларида: «Қани энди (у дунёга) қайтарилсак эди, Роббимизнинг оятларини ёлғонга чиқармас эдик ва мўминлардан бўлар эдик», дейишларини кўрсанг эди'}, {'chapter': 6, 'verse': 28, 'text': 'بَلۡ بَدَا لَهُم مَّا كَانُواْ يُخۡفُونَ مِن قَبۡلُۖ وَلَوۡ رُدُّواْ لَعَادُواْ لِمَا نُهُواْ عَنۡهُ وَإِنَّهُمۡ لَكَٰذِبُونَ\nЙўқ! Ундай эмас. Улар олдин махфий тутган нарса ўзларига ошкор бўлди, холос. Агар улар (бу дунёга) қайтарилсалар ҳам, ўзлари ман қилинган нарсага қайтурлар ва, албатта, улар ёлғончилардир'}, {'chapter': 6, 'verse': 29, 'text': 'وَقَالُوٓاْ إِنۡ هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا وَمَا نَحۡنُ بِمَبۡعُوثِينَ\nУлар: «Ҳаёт — бу дунё ҳаёти, холос, биз қайта тирилтирилмасмиз», дедилар'}, {'chapter': 6, 'verse': 30, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ وُقِفُواْ عَلَىٰ رَبِّهِمۡۚ قَالَ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۚ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nАллоҳнинг ҳузурида тўхтатилганларида, У: «Мана шу ҳақ эмасми?!» – деганида, «Роббимизга қасамки, ҳақдир», деганларини, У: «Бас, куфр келтирганингиз учун азобини чекинг», дейишини кўрсанг эди'}, {'chapter': 6, 'verse': 31, 'text': 'قَدۡ خَسِرَ ٱلَّذِينَ كَذَّبُواْ بِلِقَآءِ ٱللَّهِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمُ ٱلسَّاعَةُ بَغۡتَةٗ قَالُواْ يَٰحَسۡرَتَنَا عَلَىٰ مَا فَرَّطۡنَا فِيهَا وَهُمۡ يَحۡمِلُونَ أَوۡزَارَهُمۡ عَلَىٰ ظُهُورِهِمۡۚ أَلَا سَآءَ مَا يَزِرُونَ\nАллоҳга рўбарў бўлишни ёлғон деганлар, шубҳасиз, зиёнга учрадилар. Ҳаттоки, тўсатдан қиёмат келиб қолганда, устларига гуноҳларини орқалаб олган ҳолларида: «У(дунё)да қилган камчиликларимизга ҳасрат-надоматлар бўлсин», дерлар. Улар орқалаган нарса қандай ёмон-а'}, {'chapter': 6, 'verse': 32, 'text': 'وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا لَعِبٞ وَلَهۡوٞۖ وَلَلدَّارُ ٱلۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ\nБу дунё ҳаёти ўйин-кулгудан ўзга ҳеч нарса эмас. Тақво қилувчилар учун охират ҳовлиси яхшироқдир. Ақл юритмайсизларми'}, {'chapter': 6, 'verse': 33, 'text': 'قَدۡ نَعۡلَمُ إِنَّهُۥ لَيَحۡزُنُكَ ٱلَّذِي يَقُولُونَۖ فَإِنَّهُمۡ لَا يُكَذِّبُونَكَ وَلَٰكِنَّ ٱلظَّـٰلِمِينَ بِـَٔايَٰتِ ٱللَّهِ يَجۡحَدُونَ\nСени, албатта, уларнинг айтаётганлари хафа қилишини яхши биламиз. Улар сени ёлғончига чиқараётганлари йўқ, балки золимлар Аллоҳнинг оятларини инкор қилмоқдалар'}, {'chapter': 6, 'verse': 34, 'text': 'وَلَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَ فَصَبَرُواْ عَلَىٰ مَا كُذِّبُواْ وَأُوذُواْ حَتَّىٰٓ أَتَىٰهُمۡ نَصۡرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَٰتِ ٱللَّهِۚ وَلَقَدۡ جَآءَكَ مِن نَّبَإِيْ ٱلۡمُرۡسَلِينَ\nСендан олдин ҳам Пайғамбарлар ёлғончига чиқарилганлар. Бас, улар то нусрат келгунча ёлғончига чиқарилганларига ва берилган озорларга сабр қилганлар. Аллоҳнинг сўзларини ўзгартирувчи йўқ. Батаҳқиқ, сенга Пайғамбарларнинг хабари келган'}, {'chapter': 6, 'verse': 35, 'text': 'وَإِن كَانَ كَبُرَ عَلَيۡكَ إِعۡرَاضُهُمۡ فَإِنِ ٱسۡتَطَعۡتَ أَن تَبۡتَغِيَ نَفَقٗا فِي ٱلۡأَرۡضِ أَوۡ سُلَّمٗا فِي ٱلسَّمَآءِ فَتَأۡتِيَهُم بِـَٔايَةٖۚ وَلَوۡ شَآءَ ٱللَّهُ لَجَمَعَهُمۡ عَلَى ٱلۡهُدَىٰۚ فَلَا تَكُونَنَّ مِنَ ٱلۡجَٰهِلِينَ\nАгар сенга уларнинг юз ўгиришлари оғир ботаётган бўлса, қодир бўлсанг, ер остидан йўл қилиб ёки осмонга нарвон қўйиб, уларга оят-мўъжиза келтир. Аллоҳ хоҳлаганида, уларни ҳидоятга жамларди. Ҳаргиз жоҳиллардан бўлма'}, {'chapter': 6, 'verse': 36, 'text': '۞إِنَّمَا يَسۡتَجِيبُ ٱلَّذِينَ يَسۡمَعُونَۘ وَٱلۡمَوۡتَىٰ يَبۡعَثُهُمُ ٱللَّهُ ثُمَّ إِلَيۡهِ يُرۡجَعُونَ\nАлбатта, тинглай оладиганларгина қабул қилурлар. Ўликларни эса, Аллоҳ тирилтирур ва сўнгра Унинг Ўзига қайтарилурлар'}, {'chapter': 6, 'verse': 37, 'text': 'وَقَالُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ قَادِرٌ عَلَىٰٓ أَن يُنَزِّلَ ءَايَةٗ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nУлар: «Унга Роббидан бир оят-мўъжиза тушса эди?» – дерлар. «Албатта, Аллоҳ оят-мўъжиза туширишга қодирдир», деб айт. Лекин кўплари билмаслар'}, {'chapter': 6, 'verse': 38, 'text': 'وَمَا مِن دَآبَّةٖ فِي ٱلۡأَرۡضِ وَلَا طَـٰٓئِرٖ يَطِيرُ بِجَنَاحَيۡهِ إِلَّآ أُمَمٌ أَمۡثَالُكُمۚ مَّا فَرَّطۡنَا فِي ٱلۡكِتَٰبِ مِن شَيۡءٖۚ ثُمَّ إِلَىٰ رَبِّهِمۡ يُحۡشَرُونَ\nЕр юзидаги ҳар бир юрувчи жонзот ва икки қаноти ила учувчи қуш борки, ҳаммаси сиз каби умматлардир. Китобга ҳеч нарсани қўймай ёзганмиз. Сўнгра Роббиларига жамланурлар'}, {'chapter': 6, 'verse': 39, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا صُمّٞ وَبُكۡمٞ فِي ٱلظُّلُمَٰتِۗ مَن يَشَإِ ٱللَّهُ يُضۡلِلۡهُ وَمَن يَشَأۡ يَجۡعَلۡهُ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nОятларимизни ёлғонга чиқарганлар кар ва соқовлардир. Аллоҳ кимни хоҳласа, адаштирур ва кимни хоҳласа, тўғри йўлда барқарор қилиб қўядир'}, {'chapter': 6, 'verse': 40, 'text': 'قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ أَوۡ أَتَتۡكُمُ ٱلسَّاعَةُ أَغَيۡرَ ٱللَّهِ تَدۡعُونَ إِن كُنتُمۡ صَٰدِقِينَ\nСен: «Агар ростгўй бўлсаларингиз, айтинг-чи, сизга Аллоҳнинг азоби ёки қиёмат соати келганда, Аллоҳдан бошқага дуо қиласизларми?» – дегин'}, {'chapter': 6, 'verse': 41, 'text': 'بَلۡ إِيَّاهُ تَدۡعُونَ فَيَكۡشِفُ مَا تَدۡعُونَ إِلَيۡهِ إِن شَآءَ وَتَنسَوۡنَ مَا تُشۡرِكُونَ\nЙўқ! Фақат Унгагина дуо қилурсиз. У эса, хоҳласа, сиз дуо қилган нарсага кушойиш берур. Сиз эса, ширк келтираётган нарсангизни унутурсиз'}, {'chapter': 6, 'verse': 42, 'text': 'وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَأَخَذۡنَٰهُم بِٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ لَعَلَّهُمۡ يَتَضَرَّعُونَ\nБатаҳқиқ, Биз сендан олдинги умматларга ҳам Пайғамбарлар юборганмиз. Сўнгра, шояд, тазарруъ қилсалар деб, уларни бало ва зиёнларга тутганмиз'}, {'chapter': 6, 'verse': 43, 'text': 'فَلَوۡلَآ إِذۡ جَآءَهُم بَأۡسُنَا تَضَرَّعُواْ وَلَٰكِن قَسَتۡ قُلُوبُهُمۡ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ مَا كَانُواْ يَعۡمَلُونَ\nБизнинг баломиз уларга етганда, тазарруъ қилсалар эди! Лекин қалблари қаттиқ бўлди ва шайтон уларга қилаётган ишларини зийнатлаб кўрсатди'}, {'chapter': 6, 'verse': 44, 'text': 'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦ فَتَحۡنَا عَلَيۡهِمۡ أَبۡوَٰبَ كُلِّ شَيۡءٍ حَتَّىٰٓ إِذَا فَرِحُواْ بِمَآ أُوتُوٓاْ أَخَذۡنَٰهُم بَغۡتَةٗ فَإِذَا هُم مُّبۡلِسُونَ\nЭслатилган нарсаларни унутган чоғларида, уларга ҳамма нарсанинг эшикларини очиб қўйдик. Ўзларига берилган нарсалардан хурсанд бўлиб турганларида, уларни бирданига тутдик. Бас, қарабсизки, бутунлай ноумид бўлдилар'}, {'chapter': 6, 'verse': 45, 'text': 'فَقُطِعَ دَابِرُ ٱلۡقَوۡمِ ٱلَّذِينَ ظَلَمُواْۚ وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nБас, зулм қилган қавмларнинг кети кесилди. Оламларнинг Роббиси Аллоҳга ҳамдлар бўлсин'}, {'chapter': 6, 'verse': 46, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَخَذَ ٱللَّهُ سَمۡعَكُمۡ وَأَبۡصَٰرَكُمۡ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِهِۗ ٱنظُرۡ كَيۡفَ نُصَرِّفُ ٱلۡأٓيَٰتِ ثُمَّ هُمۡ يَصۡدِفُونَ\nУларга: «Айтинг-чи, агар Аллоҳ қулоқ ва кўзларингизни олиб қўйса, қалбларингизга муҳр босса, Аллоҳдан ўзга қайси илоҳ уни сизга беради», дегин. Биз оятларни қандай келтиришимизга, сўнгра уларнинг қандай юз ўгириб кетишига назар сол'}, {'chapter': 6, 'verse': 47, 'text': 'قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ بَغۡتَةً أَوۡ جَهۡرَةً هَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلظَّـٰلِمُونَ\nУларга: «Айтинг-чи, агар сизга Аллоҳнинг азоби бирданига ёки очиқчасига келса, золим қавмларгина ҳалок қилинадилар-ку!» – деб айт'}, {'chapter': 6, 'verse': 48, 'text': 'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۖ فَمَنۡ ءَامَنَ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nБиз Пайғамбарларни фақат башорат берувчи ва огоҳлантирувчи қилиб юборурмиз. Ким иймон келтириб, амали солиҳ қилса, уларга хавфу хатар йўқ ва улар ранжимаслар'}, {'chapter': 6, 'verse': 49, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا يَمَسُّهُمُ ٱلۡعَذَابُ بِمَا كَانُواْ يَفۡسُقُونَ\nОятларимизни ёлғонга чиқарганларни, фосиқлик қилганлари учун, азоб тутадир'}, {'chapter': 6, 'verse': 50, 'text': 'قُل لَّآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ ٱللَّهِ وَلَآ أَعۡلَمُ ٱلۡغَيۡبَ وَلَآ أَقُولُ لَكُمۡ إِنِّي مَلَكٌۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۚ قُلۡ هَلۡ يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُۚ أَفَلَا تَتَفَكَّرُونَ\nСен: «Сизларга, ҳузуримда Аллоҳнинг хазиналари бор, демасман. ўайбни биламан, демасман. Ва яна сизларга, мен фариштаман, деб ҳам айтмасман. Мен фақат ўзимга ваҳий қилинган нарсага эргашаман, холос», деб айт. «Кўр билан кўзи ўткир тенг бўла олурми? Ўйлаб кўрмайсизларми?» – деб айт'}, {'chapter': 6, 'verse': 51, 'text': 'وَأَنذِرۡ بِهِ ٱلَّذِينَ يَخَافُونَ أَن يُحۡشَرُوٓاْ إِلَىٰ رَبِّهِمۡ لَيۡسَ لَهُم مِّن دُونِهِۦ وَلِيّٞ وَلَا شَفِيعٞ لَّعَلَّهُمۡ يَتَّقُونَ\nУ билан Роббиларига тўпланишдан қўрқадиганларни огоҳлантир. Уларга ундан ўзга валий ҳам, шафоатчи ҳам йўқ. Шоядки, тақводор бўлсалар'}, {'chapter': 6, 'verse': 52, 'text': 'وَلَا تَطۡرُدِ ٱلَّذِينَ يَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ مَا عَلَيۡكَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَمَا مِنۡ حِسَابِكَ عَلَيۡهِم مِّن شَيۡءٖ فَتَطۡرُدَهُمۡ فَتَكُونَ مِنَ ٱلظَّـٰلِمِينَ\nЭртаю кеч Роббиларига Унинг юзини ирода қилиб дуо этаётганларни ҳайдама! Уларнинг ҳисобидан сенинг зиммангда ҳеч нарса йўқ. Сенинг ҳисобингдан уларнинг зиммасида ҳеч нарса йўқ. Уларни ҳайдаб, золимлардан бўлиб қолма'}, {'chapter': 6, 'verse': 53, 'text': 'وَكَذَٰلِكَ فَتَنَّا بَعۡضَهُم بِبَعۡضٖ لِّيَقُولُوٓاْ أَهَـٰٓؤُلَآءِ مَنَّ ٱللَّهُ عَلَيۡهِم مِّنۢ بَيۡنِنَآۗ أَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِٱلشَّـٰكِرِينَ\n«Орамиздан Аллоҳ неъмат берган шуларми?» – дейишлари учун шундай қилиб, баъзиларни баъзилари ила синадик. Аллоҳнинг Ўзи шукр қилувчиларни яхши билгувчи эмасми'}, {'chapter': 6, 'verse': 54, 'text': 'وَإِذَا جَآءَكَ ٱلَّذِينَ يُؤۡمِنُونَ بِـَٔايَٰتِنَا فَقُلۡ سَلَٰمٌ عَلَيۡكُمۡۖ كَتَبَ رَبُّكُمۡ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَ أَنَّهُۥ مَنۡ عَمِلَ مِنكُمۡ سُوٓءَۢا بِجَهَٰلَةٖ ثُمَّ تَابَ مِنۢ بَعۡدِهِۦ وَأَصۡلَحَ فَأَنَّهُۥ غَفُورٞ رَّحِيمٞ\nАгар ҳузурингга оятларимизга иймон келтирганлар келсалар: «Сизларга салом бўлсин, Роббингиз Ўз зиммасига раҳматни ёзди. Сиздан ким билмасдан ёмонлик қилса, сўнгра ундан кейин тавба қилиб, амали солиҳ қилса, бас, албатта, У зот мағфиратли ва раҳимли Зотдир», – деб айт'}, {'chapter': 6, 'verse': 55, 'text': 'وَكَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ وَلِتَسۡتَبِينَ سَبِيلُ ٱلۡمُجۡرِمِينَ\nШундай қилиб, жиноятчиларнинг йўли ошкор бўлиши учун, оятларни муфассал баён қилурмиз'}, {'chapter': 6, 'verse': 56, 'text': 'قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِۚ قُل لَّآ أَتَّبِعُ أَهۡوَآءَكُمۡ قَدۡ ضَلَلۡتُ إِذٗا وَمَآ أَنَا۠ مِنَ ٱلۡمُهۡتَدِينَ\nСен: «Мен Аллоҳни қўйиб, сиз ибодат қилаётганларга ибодат қилишдан қайтарилганман», – деб айт. «Мен сизнинг ҳавойи нафсларингизга эргашмасман. Зеро, адашган бўламан ва ҳидоят топганлардан бўлмай қоламан», – деб айт'}, {'chapter': 6, 'verse': 57, 'text': 'قُلۡ إِنِّي عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَكَذَّبۡتُم بِهِۦۚ مَا عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦٓۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ يَقُصُّ ٱلۡحَقَّۖ وَهُوَ خَيۡرُ ٱلۡفَٰصِلِينَ\nСен: «Албатта, мен Роббимдан берилган аниқ ҳужжатга эгаман. Сизлар уни ёлғонга чиқардингиз. Сиз шошилтираётган нарса менда йўқ. Ҳукм фақат Аллоҳнинг Ўзига хосдир. У ҳақни аниқлар. У ажрим қилувчиларнинг энг яхшиси», – деб айт'}, {'chapter': 6, 'verse': 58, 'text': 'قُل لَّوۡ أَنَّ عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦ لَقُضِيَ ٱلۡأَمۡرُ بَيۡنِي وَبَيۡنَكُمۡۗ وَٱللَّهُ أَعۡلَمُ بِٱلظَّـٰلِمِينَ\nСен: «Агар сиз шошилтираётган нарса менинг ҳузуримда бўлса эди, мен билан сизнинг орангизда иш аллақачон битган бўлар эди. Аллоҳ золимларни яхши билгувчидир», – деб айт'}, {'chapter': 6, 'verse': 59, 'text': '۞وَعِندَهُۥ مَفَاتِحُ ٱلۡغَيۡبِ لَا يَعۡلَمُهَآ إِلَّا هُوَۚ وَيَعۡلَمُ مَا فِي ٱلۡبَرِّ وَٱلۡبَحۡرِۚ وَمَا تَسۡقُطُ مِن وَرَقَةٍ إِلَّا يَعۡلَمُهَا وَلَا حَبَّةٖ فِي ظُلُمَٰتِ ٱلۡأَرۡضِ وَلَا رَطۡبٖ وَلَا يَابِسٍ إِلَّا فِي كِتَٰبٖ مُّبِينٖ\nҒайбнинг калитлари Унинг ҳузурида бўлиб, уларни Унинг Ўзидан бошқа ҳеч ким билмас. У ердаги, қуруқлик ва денгиздаги нарсаларни биладир. Битта япроқ узилиб тушмайдики, магар уни ҳам биладир. Ер тубидаги бир дона дон борми, ҳўлу қуруқ борми, барчаси Очиқ китобда бордир'}, {'chapter': 6, 'verse': 60, 'text': 'وَهُوَ ٱلَّذِي يَتَوَفَّىٰكُم بِٱلَّيۡلِ وَيَعۡلَمُ مَا جَرَحۡتُم بِٱلنَّهَارِ ثُمَّ يَبۡعَثُكُمۡ فِيهِ لِيُقۡضَىٰٓ أَجَلٞ مُّسَمّٗىۖ ثُمَّ إِلَيۡهِ مَرۡجِعُكُمۡ ثُمَّ يُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nУ кечаси сизни вафот эттирадир. У кундузи қилган касбингизни биладир. Сўнгра, белгилаган ажал тугал бўлиши учун у(кундузи)да сизни қайта тирилтирадир. Сўнгра, қайтиб боришингиз Унинг Ўзигадир. Сўнгра, нима қилиб юрганингизнинг хабарини берадир'}, {'chapter': 6, 'verse': 61, 'text': 'وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۖ وَيُرۡسِلُ عَلَيۡكُمۡ حَفَظَةً حَتَّىٰٓ إِذَا جَآءَ أَحَدَكُمُ ٱلۡمَوۡتُ تَوَفَّتۡهُ رُسُلُنَا وَهُمۡ لَا يُفَرِّطُونَ\nУ бандалари устидан ғолибдир. У устингизга ҳафазаларини юборадир. Қачонки бирортангизга ўлим келса, элчиларимиз камчиликка йўл қўймаган ҳолда уни вафот эттирадир'}, {'chapter': 6, 'verse': 62, 'text': 'ثُمَّ رُدُّوٓاْ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۚ أَلَا لَهُ ٱلۡحُكۡمُ وَهُوَ أَسۡرَعُ ٱلۡحَٰسِبِينَ\nСўнгра ҳақиқий Хожалари — Аллоҳга қайтарилурлар. Огоҳ бўлингизким, ҳукм қилиш Унинг Ўзигагина хосдир. Ва У энг тезкор ҳисоб-китоб қилувчидир'}, {'chapter': 6, 'verse': 63, 'text': 'قُلۡ مَن يُنَجِّيكُم مِّن ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ تَدۡعُونَهُۥ تَضَرُّعٗا وَخُفۡيَةٗ لَّئِنۡ أَنجَىٰنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ\nСен: «Сизни қуруқлигу денгизнинг зулматларидан қутқарадиган ким? Унга тазарруъ қилган ҳолда ва махфий ҳолда, агар мана шундан бизни қутқарса, албатта, шукр қилувчилардан бўлар эдик, дейдиганингиз ким?» – деб айт'}, {'chapter': 6, 'verse': 64, 'text': 'قُلِ ٱللَّهُ يُنَجِّيكُم مِّنۡهَا وَمِن كُلِّ كَرۡبٖ ثُمَّ أَنتُمۡ تُشۡرِكُونَ\n«Сизни улардан ва барча ғамлардан Аллоҳ қутқарур. Сўнгра эса, сиз ширк келтирасиз», – деб айт'}, {'chapter': 6, 'verse': 65, 'text': 'قُلۡ هُوَ ٱلۡقَادِرُ عَلَىٰٓ أَن يَبۡعَثَ عَلَيۡكُمۡ عَذَابٗا مِّن فَوۡقِكُمۡ أَوۡ مِن تَحۡتِ أَرۡجُلِكُمۡ أَوۡ يَلۡبِسَكُمۡ شِيَعٗا وَيُذِيقَ بَعۡضَكُم بَأۡسَ بَعۡضٍۗ ٱنظُرۡ كَيۡفَ نُصَرِّفُ ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَفۡقَهُونَ\nСен: «У сизга устингиздан ёки оёғингиз остидан азоб юборишга, турли фирқаларга аралаштириб юборишга, баъзиларингизга баъзиларингизнинг азобини тоттиришга қодирдир», – деб айт. Оятларимизни қандай тасарруф қилишимизга назар сол. Шоядки, англаб етсалар'}, {'chapter': 6, 'verse': 66, 'text': 'وَكَذَّبَ بِهِۦ قَوۡمُكَ وَهُوَ ٱلۡحَقُّۚ قُل لَّسۡتُ عَلَيۡكُم بِوَكِيلٖ\nҚавминг уни ёлғонга чиқарди. Ҳолбуки у ҳақдир. Сен: «Мен сизларга вакил эмасман'}, {'chapter': 6, 'verse': 67, 'text': 'لِّكُلِّ نَبَإٖ مُّسۡتَقَرّٞۚ وَسَوۡفَ تَعۡلَمُونَ\nҲар бир хабарнинг ўз вақти бор. Тезда буни биласизлар», деб айт'}, {'chapter': 6, 'verse': 68, 'text': 'وَإِذَا رَأَيۡتَ ٱلَّذِينَ يَخُوضُونَ فِيٓ ءَايَٰتِنَا فَأَعۡرِضۡ عَنۡهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦۚ وَإِمَّا يُنسِيَنَّكَ ٱلشَّيۡطَٰنُ فَلَا تَقۡعُدۡ بَعۡدَ ٱلذِّكۡرَىٰ مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nОятларимизни (истеҳзо қилишга) шўнғиётганларни кўрган чоғингда, то ундан бошқа гапга шўнғигунларигача улардан юз ўгир. Агар шайтон эсингдан чиқарган бўлса, эслаганингдан сўнг, золим қавмлар билан ўтирма'}, {'chapter': 6, 'verse': 69, 'text': 'وَمَا عَلَى ٱلَّذِينَ يَتَّقُونَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَلَٰكِن ذِكۡرَىٰ لَعَلَّهُمۡ يَتَّقُونَ\nТақво қиладиганлар зиммасида у (мушрик)ларнинг ҳисобидан ҳеч нарса йўқ. Лекин эслатма бор. Шоядки, тақво қилсалар'}, {'chapter': 6, 'verse': 70, 'text': 'وَذَرِ ٱلَّذِينَ ٱتَّخَذُواْ دِينَهُمۡ لَعِبٗا وَلَهۡوٗا وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ وَذَكِّرۡ بِهِۦٓ أَن تُبۡسَلَ نَفۡسُۢ بِمَا كَسَبَتۡ لَيۡسَ لَهَا مِن دُونِ ٱللَّهِ وَلِيّٞ وَلَا شَفِيعٞ وَإِن تَعۡدِلۡ كُلَّ عَدۡلٖ لَّا يُؤۡخَذۡ مِنۡهَآۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ أُبۡسِلُواْ بِمَا كَسَبُواْۖ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ\nЎз динларини ўйин-кулгу қилиб олган ва ҳаёти дунёга алданиб кетганларни тарк эт. У (Қуръон) ила эслатгинки, ҳеч бир жон қилган касби туфайли ҳалокатга дучор бўлиб, Аллоҳдан ўзга бирор дўст ва шафоатчисиз ҳолга ва барча фидо қиладиган нарсани фидо қилса ҳам, ундан қабул қилинмайдиган ҳолга тушиб қолмасин. Ана ўшалар қилган касблари туфайли ҳалокатга дучор бўлганлардир. Куфр келтирганлари учун уларга қайноқ сувдан шароб ва аламли азоб бордир'}, {'chapter': 6, 'verse': 71, 'text': 'قُلۡ أَنَدۡعُواْ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰٓ أَعۡقَابِنَا بَعۡدَ إِذۡ هَدَىٰنَا ٱللَّهُ كَٱلَّذِي ٱسۡتَهۡوَتۡهُ ٱلشَّيَٰطِينُ فِي ٱلۡأَرۡضِ حَيۡرَانَ لَهُۥٓ أَصۡحَٰبٞ يَدۡعُونَهُۥٓ إِلَى ٱلۡهُدَى ٱئۡتِنَاۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۖ وَأُمِرۡنَا لِنُسۡلِمَ لِرَبِّ ٱلۡعَٰلَمِينَ\nСен: «Аллоҳни қўйиб на фойда, на зарар бера олмайдиган нарсаларга ибодат қиламизми? Худди оғайнилари, бу ёққа кел, деб ҳидоятга чақириб турган бўлса ҳам, шайтон адаштириб, ер юзида ҳайрон бўлиб қолганга ўхшаб, Аллоҳ бизни ҳидоятга солгандан кейин орқамизга қайтамизми?» – деб айт. Сен: «Албатта, Аллоҳнинг ҳидояти ҳақиқий ҳидоятдир. Оламларнинг Роббига таслим бўлишга амр қилинганмиз», – деб айт'}, {'chapter': 6, 'verse': 72, 'text': 'وَأَنۡ أَقِيمُواْ ٱلصَّلَوٰةَ وَٱتَّقُوهُۚ وَهُوَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ\nНамозни қоим қилинг ва Аллоҳга тақво қилинг! У сизлар ҳузурига тўпланадиган Зотдир'}, {'chapter': 6, 'verse': 73, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ وَيَوۡمَ يَقُولُ كُن فَيَكُونُۚ قَوۡلُهُ ٱلۡحَقُّۚ وَلَهُ ٱلۡمُلۡكُ يَوۡمَ يُنفَخُ فِي ٱلصُّورِۚ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ\nУ, осмонлару ерни ҳақ ила яратган Зотдир. «Бўл» деган куни бўладир. Унинг гапи ҳақдир. Дуд чалинадиган куни мулк Уникидир. У, ғайбни ҳам, шоҳидни ҳам билувчи Зотдир. Ва У ҳакийм ва хабардор Зотдир'}, {'chapter': 6, 'verse': 74, 'text': '۞وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ ءَازَرَ أَتَتَّخِذُ أَصۡنَامًا ءَالِهَةً إِنِّيٓ أَرَىٰكَ وَقَوۡمَكَ فِي ضَلَٰلٖ مُّبِينٖ\nИброҳимнинг отаси Озарга: «Санамларни худолар қилиб оласанми? Мен сени ва сенинг қавмингни, албатта, очиқ-ойдин адашувда кўрмоқдаман», – деганини эсла'}, {'chapter': 6, 'verse': 75, 'text': 'وَكَذَٰلِكَ نُرِيٓ إِبۡرَٰهِيمَ مَلَكُوتَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلِيَكُونَ مِنَ ٱلۡمُوقِنِينَ\nХудди шундай қилиб, Иброҳимга, аниқ ишонувчилардан бўлиши учун, осмонлару ернинг мулкларини кўрсатурмиз'}, {'chapter': 6, 'verse': 76, 'text': 'فَلَمَّا جَنَّ عَلَيۡهِ ٱلَّيۡلُ رَءَا كَوۡكَبٗاۖ قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَآ أُحِبُّ ٱلۡأٓفِلِينَ\nУни тун ўраб олганида юлдузни кўриб: «Мана шу Роббимдир», – деди. У ботиб кетганида эса: «Ботувчиларни ёқтирмасман», – деди'}, {'chapter': 6, 'verse': 77, 'text': 'فَلَمَّا رَءَا ٱلۡقَمَرَ بَازِغٗا قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَئِن لَّمۡ يَهۡدِنِي رَبِّي لَأَكُونَنَّ مِنَ ٱلۡقَوۡمِ ٱلضَّآلِّينَ\nЧиқаётган ойни кўрганда: «Мана шу Роббимдир», – деди. У ҳам ботганида: «Агар Роббим мени тўғри йўлга бошламаса, албатта, адашувчилардан бўламан», – деди'}, {'chapter': 6, 'verse': 78, 'text': 'فَلَمَّا رَءَا ٱلشَّمۡسَ بَازِغَةٗ قَالَ هَٰذَا رَبِّي هَٰذَآ أَكۡبَرُۖ فَلَمَّآ أَفَلَتۡ قَالَ يَٰقَوۡمِ إِنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ\nЧиқаётган қуёшни кўрганда: «Мана шу Роббим. Бу каттароқ», – деди. У ҳам ботиб кетганида: «Эй қавмим, мен сиз ширк келтираётган нарсадан воз кечдим», – деди'}, {'chapter': 6, 'verse': 79, 'text': 'إِنِّي وَجَّهۡتُ وَجۡهِيَ لِلَّذِي فَطَرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ حَنِيفٗاۖ وَمَآ أَنَا۠ مِنَ ٱلۡمُشۡرِكِينَ\nАлбатта, мен юзимни осмонлару ерни яратган Зотга, тўғри йўлга мойил бўлган ҳолимда, юзлантирдим ва мен мушриклардан эмасман'}, {'chapter': 6, 'verse': 80, 'text': 'وَحَآجَّهُۥ قَوۡمُهُۥۚ قَالَ أَتُحَـٰٓجُّوٓنِّي فِي ٱللَّهِ وَقَدۡ هَدَىٰنِۚ وَلَآ أَخَافُ مَا تُشۡرِكُونَ بِهِۦٓ إِلَّآ أَن يَشَآءَ رَبِّي شَيۡـٔٗاۚ وَسِعَ رَبِّي كُلَّ شَيۡءٍ عِلۡمًاۚ أَفَلَا تَتَذَكَّرُونَ\nВа қавми у ила тортишди. У: «Мен билан Аллоҳ ҳақида тортишасизларми, ҳолбуки, У мени ҳидоят этди. Мен Унга ширк келтираётган нарсангиздан қўрқмасман. Магар Роббим бирор нарсани ирода этса, бўлиши мумкин. Роббим барча нарсани Ўз илми ила қамраб олган. Эсласангиз бўлмасми?» – деди'}, {'chapter': 6, 'verse': 81, 'text': 'وَكَيۡفَ أَخَافُ مَآ أَشۡرَكۡتُمۡ وَلَا تَخَافُونَ أَنَّكُمۡ أَشۡرَكۡتُم بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ عَلَيۡكُمۡ سُلۡطَٰنٗاۚ فَأَيُّ ٱلۡفَرِيقَيۡنِ أَحَقُّ بِٱلۡأَمۡنِۖ إِن كُنتُمۡ تَعۡلَمُونَ\n«Қандай қилиб мен сиз ширк келтирган нарсадан қўрқай. Ҳолбуки, сиз ўзингизга ҳеч ҳужжат туширмаган нарсани Аллоҳга ширк келтириб туриб қўрқмайсиз-у?» Агар билсангиз, айтинг-чи, қайси гуруҳ хотиржам бўлишга ҳақлироқ'}, {'chapter': 6, 'verse': 82, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَلَمۡ يَلۡبِسُوٓاْ إِيمَٰنَهُم بِظُلۡمٍ أُوْلَـٰٓئِكَ لَهُمُ ٱلۡأَمۡنُ وَهُم مُّهۡتَدُونَ\nИймон келтирганлар ва иймонларига зулмни аралаштирмаганлар. Ана ўшаларга хотиржамлик, омонлик бор. Улар ҳидоят топганлардир'}, {'chapter': 6, 'verse': 83, 'text': 'وَتِلۡكَ حُجَّتُنَآ ءَاتَيۡنَٰهَآ إِبۡرَٰهِيمَ عَلَىٰ قَوۡمِهِۦۚ نَرۡفَعُ دَرَجَٰتٖ مَّن نَّشَآءُۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ\nАна ўшалар, Иброҳимга қавмига қарши (тортишувда) берган ҳужжатларимиздир. Кимни хоҳласак, даражасини кўтарамиз. Албатта, Роббинг ҳикматли ва яхши билувчи Зотдир'}, {'chapter': 6, 'verse': 84, 'text': 'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۚ كُلًّا هَدَيۡنَاۚ وَنُوحًا هَدَيۡنَا مِن قَبۡلُۖ وَمِن ذُرِّيَّتِهِۦ دَاوُۥدَ وَسُلَيۡمَٰنَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَىٰ وَهَٰرُونَۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nВа Биз унга Исҳоқни ва Яъқубни бердик. Барчаларини ҳидоят қилдик. Бундан олдин Нуҳни ҳам ҳидоят қилган эдик. Ва унинг зурриётидан Довуд, Сулаймон, Айюб, Юсуф, Мусо, Ҳорунни ҳам (ҳидоят қилдик). Чиройли амал қилувчиларни ана шундай мукофотлармиз'}, {'chapter': 6, 'verse': 85, 'text': 'وَزَكَرِيَّا وَيَحۡيَىٰ وَعِيسَىٰ وَإِلۡيَاسَۖ كُلّٞ مِّنَ ٱلصَّـٰلِحِينَ\nВа Закариё, Яҳё, Ийсо ва Илёсни ҳам. Ҳаммалари аҳли солиҳлардир'}, {'chapter': 6, 'verse': 86, 'text': 'وَإِسۡمَٰعِيلَ وَٱلۡيَسَعَ وَيُونُسَ وَلُوطٗاۚ وَكُلّٗا فَضَّلۡنَا عَلَى ٱلۡعَٰلَمِينَ\nВа Исмоил, ал-Ясаъ, Юнус ва Лутни ҳам. Барчаларини бутун оламлардан афзал қилдик'}, {'chapter': 6, 'verse': 87, 'text': 'وَمِنۡ ءَابَآئِهِمۡ وَذُرِّيَّـٰتِهِمۡ وَإِخۡوَٰنِهِمۡۖ وَٱجۡتَبَيۡنَٰهُمۡ وَهَدَيۡنَٰهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nВа уларнинг оталаридан, зурриётларидан ва биродарларидан ҳам ҳидоят қилдик. Уларни танлаб олдик ва сиротул мустақиймга ҳидоят қилдик'}, {'chapter': 6, 'verse': 88, 'text': 'ذَٰلِكَ هُدَى ٱللَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَلَوۡ أَشۡرَكُواْ لَحَبِطَ عَنۡهُم مَّا كَانُواْ يَعۡمَلُونَ\nАна ўша, Аллоҳнинг ҳидояти бўлиб, Ўз бандаларидан кимни хоҳласа, ўшани унга йўллар. Агар улар ширк келтирсалар, қилган ишлари, албатта, беҳуда кетар'}, {'chapter': 6, 'verse': 89, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَۚ فَإِن يَكۡفُرۡ بِهَا هَـٰٓؤُلَآءِ فَقَدۡ وَكَّلۡنَا بِهَا قَوۡمٗا لَّيۡسُواْ بِهَا بِكَٰفِرِينَ\nАна ўшалар Биз уларга китобни, ҳукмни ва Пайғамбарликни берган зотлардир. Агар анавилар буларга куфр келтирсалар, батаҳқиқки, Биз уларга куфр келтирмайдиган қавмни вакил қилиб қўйганмиз'}, {'chapter': 6, 'verse': 90, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ هَدَى ٱللَّهُۖ فَبِهُدَىٰهُمُ ٱقۡتَدِهۡۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ هُوَ إِلَّا ذِكۡرَىٰ لِلۡعَٰلَمِينَ\nАна ўшалар Аллоҳ ҳидоят қилган зотлардир. Бас, уларнинг ҳидоятига эргаш. Сен: «Мен сизлардан бунинг учун ҳақ сўрамасман. У, оламлар учун эслатмадан иборат, холос», – деб айт'}, {'chapter': 6, 'verse': 91, 'text': 'وَمَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦٓ إِذۡ قَالُواْ مَآ أَنزَلَ ٱللَّهُ عَلَىٰ بَشَرٖ مِّن شَيۡءٖۗ قُلۡ مَنۡ أَنزَلَ ٱلۡكِتَٰبَ ٱلَّذِي جَآءَ بِهِۦ مُوسَىٰ نُورٗا وَهُدٗى لِّلنَّاسِۖ تَجۡعَلُونَهُۥ قَرَاطِيسَ تُبۡدُونَهَا وَتُخۡفُونَ كَثِيرٗاۖ وَعُلِّمۡتُم مَّا لَمۡ تَعۡلَمُوٓاْ أَنتُمۡ وَلَآ ءَابَآؤُكُمۡۖ قُلِ ٱللَّهُۖ ثُمَّ ذَرۡهُمۡ فِي خَوۡضِهِمۡ يَلۡعَبُونَ\nУлар, Аллоҳ башарга ҳеч нарса туширгани йўқ, деганларида, Аллоҳни тўғри тақдирламадилар. «Мусо кишиларга ҳидоят ва нур этиб олиб келган, уни сиз қоғозга тушириб олиб, баъзисини зоҳир, кўпини махфий қилган ва сиз ҳам, ота-боболарингиз ҳам билмаган нарсаларни сизга ўргатган китобни ким туширди?!» – деб айт. «Аллоҳ» – деб айт. Сўнгра, уларни тек қўй, шўнғиган нарсаларида ўйнайверсинлар'}, {'chapter': 6, 'verse': 92, 'text': 'وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ مُّصَدِّقُ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَلِتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَاۚ وَٱلَّذِينَ يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ يُؤۡمِنُونَ بِهِۦۖ وَهُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ\nБу, Биз туширган китоб, муборакдир, ўзидан олдингини тасдиқловчидир. Уммул Қуро (шаҳарлар онаси) ва унинг атрофини огоҳлантиришинг учундир. Охиратга иймон келтирганлар унга иймон келтирурлар. Ва улар намозларини муҳофаза қилурлар'}, {'chapter': 6, 'verse': 93, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ قَالَ أُوحِيَ إِلَيَّ وَلَمۡ يُوحَ إِلَيۡهِ شَيۡءٞ وَمَن قَالَ سَأُنزِلُ مِثۡلَ مَآ أَنزَلَ ٱللَّهُۗ وَلَوۡ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ فِي غَمَرَٰتِ ٱلۡمَوۡتِ وَٱلۡمَلَـٰٓئِكَةُ بَاسِطُوٓاْ أَيۡدِيهِمۡ أَخۡرِجُوٓاْ أَنفُسَكُمُۖ ٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَقُولُونَ عَلَى ٱللَّهِ غَيۡرَ ٱلۡحَقِّ وَكُنتُمۡ عَنۡ ءَايَٰتِهِۦ تَسۡتَكۡبِرُونَ\nАллоҳга нисбатан ёлғон тўқиган ёки унга ҳеч нарса ваҳий қилинмаса ҳам, менга ваҳий келди, деган, ёхуд Аллоҳ нозил қилган нарсага ўхшаш нарсани мен ҳам нозил қиламан, деган кимсадан ҳам золимроқ кимса борми? Золимларнинг ўлим талвасаси пайтларини, фаришталарнинг уларга қўл чўзиб: «Жонингизни чиқаринг! Бугунги кунда Аллоҳга нисбатан ноҳақ гап айтганларингиз учун ва Унинг оятларидан мутакаббирлик қилиб юрганингиз учун хорлик азоби ила жазоланурсиз», – дегандаги ҳолларини кўрсанг эди'}, {'chapter': 6, 'verse': 94, 'text': 'وَلَقَدۡ جِئۡتُمُونَا فُرَٰدَىٰ كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةٖ وَتَرَكۡتُم مَّا خَوَّلۡنَٰكُمۡ وَرَآءَ ظُهُورِكُمۡۖ وَمَا نَرَىٰ مَعَكُمۡ شُفَعَآءَكُمُ ٱلَّذِينَ زَعَمۡتُمۡ أَنَّهُمۡ فِيكُمۡ شُرَكَـٰٓؤُاْۚ لَقَد تَّقَطَّعَ بَيۡنَكُمۡ وَضَلَّ عَنكُم مَّا كُنتُمۡ تَزۡعُمُونَ\nХудди сизларни дастлаб халқ қилганимиздек, ҳузуримизга якка-якка келдингиз. Сизларга берган нарсаларимизни ортингизда қолдирдингиз. Сизлар ўзингизча шерик деб ўйлаган шафоатчиларингизни кўрмаяпмиз?! Батаҳқиқ, ораларингиз узилди. Сиз даъво қилган нарса ғойиб бўлди'}, {'chapter': 6, 'verse': 95, 'text': '۞إِنَّ ٱللَّهَ فَالِقُ ٱلۡحَبِّ وَٱلنَّوَىٰۖ يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَمُخۡرِجُ ٱلۡمَيِّتِ مِنَ ٱلۡحَيِّۚ ذَٰلِكُمُ ٱللَّهُۖ فَأَنَّىٰ تُؤۡفَكُونَ\nАлбатта, Аллоҳ донни ҳам, данакни ҳам ёрувчи Зотдир. У, ўликдан тирикни чиқарур ва тирикдан ўликни чиқаргувчидир. Ана шундай Аллоҳдир. Сиз қаёққа бурилиб кетяпсиз'}, {'chapter': 6, 'verse': 96, 'text': 'فَالِقُ ٱلۡإِصۡبَاحِ وَجَعَلَ ٱلَّيۡلَ سَكَنٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ حُسۡبَانٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\nУ, субҳни ёритгувчи Зотдир. У, тунни сукунат, қуёш ва ойни ҳисоб қилиб қўйди. Бу эса, ғолиб ва яхши билгувчи Зотнинг ўлчовидир'}, {'chapter': 6, 'verse': 97, 'text': 'وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلنُّجُومَ لِتَهۡتَدُواْ بِهَا فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nУ сизларга қуруқлик ва денгиз зулматларида йўл топишингиз учун юлдузларни қилиб қўйган Зотдир. Биладиган қавмлар учун оят-белгиларни батафсил қилиб қўйганмиз'}, {'chapter': 6, 'verse': 98, 'text': 'وَهُوَ ٱلَّذِيٓ أَنشَأَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ فَمُسۡتَقَرّٞ وَمُسۡتَوۡدَعٞۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَفۡقَهُونَ\nУ сизларни бир жондан яратган Зотдир. Бас, у (жон) қарор топадиган жой(она раҳми)да ёки сақлаб қўйиладиган жой (ота сулби)дадир. Яхши англайдиганлар учун оят-белгиларни батафсил қилиб қўйганмиз'}, {'chapter': 6, 'verse': 99, 'text': 'وَهُوَ ٱلَّذِيٓ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ نَبَاتَ كُلِّ شَيۡءٖ فَأَخۡرَجۡنَا مِنۡهُ خَضِرٗا نُّخۡرِجُ مِنۡهُ حَبّٗا مُّتَرَاكِبٗا وَمِنَ ٱلنَّخۡلِ مِن طَلۡعِهَا قِنۡوَانٞ دَانِيَةٞ وَجَنَّـٰتٖ مِّنۡ أَعۡنَابٖ وَٱلزَّيۡتُونَ وَٱلرُّمَّانَ مُشۡتَبِهٗا وَغَيۡرَ مُتَشَٰبِهٍۗ ٱنظُرُوٓاْ إِلَىٰ ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَيَنۡعِهِۦٓۚ إِنَّ فِي ذَٰلِكُمۡ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nУ, осмондан сув нозил қилган Зотдир. Бас, у (сув) ила турли набототлар чиқардик. Ундан яшил гиёҳларни чиқардик. Ундан ғужум-ғужум бошоқлар чиқарамиз. Ва хурмодан, унинг новдаларидан яқин шингиллар чиқарамиз ва узум боғлари чиқарамиз. Бир-бирига ўхшаган ва ўхшамаган зайтун ва анорларни чиқарамиз. Унинг мева қилиш пайтидаги мевасига ва пишишига назар солинг. Албатта, бундай нарсаларда иймон келтирадиган қавм учун оят — белгилар бордир'}, {'chapter': 6, 'verse': 100, 'text': 'وَجَعَلُواْ لِلَّهِ شُرَكَآءَ ٱلۡجِنَّ وَخَلَقَهُمۡۖ وَخَرَقُواْ لَهُۥ بَنِينَ وَبَنَٰتِۭ بِغَيۡرِ عِلۡمٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَصِفُونَ\nУлар жинларни Аллоҳга шерик қилдилар. Ҳолбуки, У ҳаммаларини яратган Зотдир. Илмсиз ҳолда Унга ўғиллар ва қизлар ясадилар. У, улар васф қилган нарсалардан пок ва олий бўлди'}, {'chapter': 6, 'verse': 101, 'text': 'بَدِيعُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَنَّىٰ يَكُونُ لَهُۥ وَلَدٞ وَلَمۡ تَكُن لَّهُۥ صَٰحِبَةٞۖ وَخَلَقَ كُلَّ شَيۡءٖۖ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nУ, осмонлару ерни йўқдан бор қилгувчи Зотдир. Унинг хотини бўлмай туриб, боласи қандай бўлсин?! У ҳамма нарсани Ўзи яратди ва У ҳамма нарсани яхши билгувчидир'}, {'chapter': 6, 'verse': 102, 'text': 'ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡۖ لَآ إِلَٰهَ إِلَّا هُوَۖ خَٰلِقُ كُلِّ شَيۡءٖ فَٱعۡبُدُوهُۚ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ\nАна ўша Аллоҳ сизнинг Роббингиздир. Ундан бошқа ибодатга сазовор зот йўқ. У ҳар бир нарсанинг Яратгувчисидир. Бас, Унга ибодат қилинг. У ҳар бир нарсага вакилдир'}, {'chapter': 6, 'verse': 103, 'text': 'لَّا تُدۡرِكُهُ ٱلۡأَبۡصَٰرُ وَهُوَ يُدۡرِكُ ٱلۡأَبۡصَٰرَۖ وَهُوَ ٱللَّطِيفُ ٱلۡخَبِيرُ\nКўзлар Уни идрок эта олмас, У кўзларни идрок этар. У латийф ва хабардор Зотдир'}, {'chapter': 6, 'verse': 104, 'text': 'قَدۡ جَآءَكُم بَصَآئِرُ مِن رَّبِّكُمۡۖ فَمَنۡ أَبۡصَرَ فَلِنَفۡسِهِۦۖ وَمَنۡ عَمِيَ فَعَلَيۡهَاۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ\nБатаҳқиқ, сизга Роббингиздан «кўз очувчи»лар келди. Кимки кўрса, ўзига яхши. Ким кўр бўлиб олса, ўзига зарар. Мен сизларга қўриқчи эмасман'}, {'chapter': 6, 'verse': 105, 'text': 'وَكَذَٰلِكَ نُصَرِّفُ ٱلۡأٓيَٰتِ وَلِيَقُولُواْ دَرَسۡتَ وَلِنُبَيِّنَهُۥ لِقَوۡمٖ يَعۡلَمُونَ\nШундай қилиб: «Дарс олгансан», – дейишлари учун ва билган қавмларга баён қилишинг учун оятларни тасарруф қилурмиз'}, {'chapter': 6, 'verse': 106, 'text': 'ٱتَّبِعۡ مَآ أُوحِيَ إِلَيۡكَ مِن رَّبِّكَۖ لَآ إِلَٰهَ إِلَّا هُوَۖ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ\nСенга Роббингдан ваҳий қилинган нарсага эргаш. Ундан бошқа ибодатга сазовор зот йўқ. Мушриклардан юз ўгир'}, {'chapter': 6, 'verse': 107, 'text': 'وَلَوۡ شَآءَ ٱللَّهُ مَآ أَشۡرَكُواْۗ وَمَا جَعَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ\nАгар Аллоҳ хоҳласа, ширк келтирмас эдилар. Биз сени уларга қўриқчи қилганимиз йўқ. Ва сен уларга вакил ҳам эмассан'}, {'chapter': 6, 'verse': 108, 'text': 'وَلَا تَسُبُّواْ ٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ فَيَسُبُّواْ ٱللَّهَ عَدۡوَۢا بِغَيۡرِ عِلۡمٖۗ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمۡ ثُمَّ إِلَىٰ رَبِّهِم مَّرۡجِعُهُمۡ فَيُنَبِّئُهُم بِمَا كَانُواْ يَعۡمَلُونَ\nУларнинг Аллоҳдан ўзга ибодат қиладиганларини сўкмангиз. Бас, билмасдан зулм ила Аллоҳни сўкиб юрмасинлар. Шундай қилиб, ҳар бир умматга амалини зийнатлаб қўйдик. Сўнгра эса, қайтиб борадиган жойлари Роббиларигадир. Бас, У қилиб юрган ишларининг хабарини берур'}, {'chapter': 6, 'verse': 109, 'text': 'وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَتۡهُمۡ ءَايَةٞ لَّيُؤۡمِنُنَّ بِهَاۚ قُلۡ إِنَّمَا ٱلۡأٓيَٰتُ عِندَ ٱللَّهِۖ وَمَا يُشۡعِرُكُمۡ أَنَّهَآ إِذَا جَآءَتۡ لَا يُؤۡمِنُونَ\nУлар агар бирон оят — мўъжиза келса, албатта, иймон келтиришлари ҳақида Аллоҳнинг номи билан жон-жаҳдлари ила қасам ичарлар. «Албатта, оятлар фақат Аллоҳнинг ҳузурида», – деб айт. Сизга нима билдирди? Эҳтимол, оят келса ҳам, улар иймон келтирмаслар'}, {'chapter': 6, 'verse': 110, 'text': 'وَنُقَلِّبُ أَفۡـِٔدَتَهُمۡ وَأَبۡصَٰرَهُمۡ كَمَا لَمۡ يُؤۡمِنُواْ بِهِۦٓ أَوَّلَ مَرَّةٖ وَنَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nХудди аввал иймон келтирмаганларидек, қалблари ва кўзларини буриб қўярмиз ва туғёнларида довдираган ҳолларида тек қўярмиз'}, {'chapter': 6, 'verse': 111, 'text': '۞وَلَوۡ أَنَّنَا نَزَّلۡنَآ إِلَيۡهِمُ ٱلۡمَلَـٰٓئِكَةَ وَكَلَّمَهُمُ ٱلۡمَوۡتَىٰ وَحَشَرۡنَا عَلَيۡهِمۡ كُلَّ شَيۡءٖ قُبُلٗا مَّا كَانُواْ لِيُؤۡمِنُوٓاْ إِلَّآ أَن يَشَآءَ ٱللَّهُ وَلَٰكِنَّ أَكۡثَرَهُمۡ يَجۡهَلُونَ\nАгар Биз уларга фаришталарни туширсак ҳам, уларга ўликлар гапирса ҳам ва уларнинг олдига ҳамма нарсани очиқ-ойдин тўпласак ҳам, Аллоҳ хоҳламаса, иймон келтирмаслар. Лекин кўплари жоҳилдирлар'}, {'chapter': 6, 'verse': 112, 'text': 'وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا شَيَٰطِينَ ٱلۡإِنسِ وَٱلۡجِنِّ يُوحِي بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ زُخۡرُفَ ٱلۡقَوۡلِ غُرُورٗاۚ وَلَوۡ شَآءَ رَبُّكَ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ\nШунингдек, ҳар бир Пайғамбарга инсу жин шайтонларини душман қилиб қўйдик. Уларнинг баъзилари баъзиларини ғурурга кетказишлари учун жимжимадор каломларни танларлар. Агар Роббинг хоҳласа, бундай қилмас эдилар. Бас, уларни ўзлари тўқиган нарсалари ила ёлғиз қўй'}, {'chapter': 6, 'verse': 113, 'text': 'وَلِتَصۡغَىٰٓ إِلَيۡهِ أَفۡـِٔدَةُ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ وَلِيَرۡضَوۡهُ وَلِيَقۡتَرِفُواْ مَا هُم مُّقۡتَرِفُونَ\nУнга (гапнинг гўзалига) охиратга иймон келтирмайдиганларнинг қалби мойил бўлиши, ундан рози бўлиб, қиладиган гуноҳларини бажаришлари учундир'}, {'chapter': 6, 'verse': 114, 'text': 'أَفَغَيۡرَ ٱللَّهِ أَبۡتَغِي حَكَمٗا وَهُوَ ٱلَّذِيٓ أَنزَلَ إِلَيۡكُمُ ٱلۡكِتَٰبَ مُفَصَّلٗاۚ وَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡلَمُونَ أَنَّهُۥ مُنَزَّلٞ مِّن رَّبِّكَ بِٱلۡحَقِّۖ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ\nАллоҳдан ўзгани ҳакам қилиб олайми? Ҳолбуки, У сизга китобни батафсил этиб нозил қилди. Биз уларга китоб берганлар унинг Роббингдан ҳақ ила нозил қилинганини билурлар. Бас, шак келтирувчилардан бўлма'}, {'chapter': 6, 'verse': 115, 'text': 'وَتَمَّتۡ كَلِمَتُ رَبِّكَ صِدۡقٗا وَعَدۡلٗاۚ لَّا مُبَدِّلَ لِكَلِمَٰتِهِۦۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nРоббингнинг сўзлари содиқлик ва адолат ила батамом бўлди. Унинг сўзларини ўзгартувчи йўқ. Ва У яхши эшитгувчи ва яхши билгувчи Зотдир'}, {'chapter': 6, 'verse': 116, 'text': 'وَإِن تُطِعۡ أَكۡثَرَ مَن فِي ٱلۡأَرۡضِ يُضِلُّوكَ عَن سَبِيلِ ٱللَّهِۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ\nВа агар ер юзидагиларнинг кўпларига итоат қилсанг, сени Аллоҳнинг йўлидан адаштирурлар. Улар гумондан бошқага эргашмаслар. Улар тахмин қилурлар, холос'}, {'chapter': 6, 'verse': 117, 'text': 'إِنَّ رَبَّكَ هُوَ أَعۡلَمُ مَن يَضِلُّ عَن سَبِيلِهِۦۖ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\nАлбатта, Роббинг ким Унинг йўлидан адашишини яхши билгувчидир. Ва У ҳидоят топувчиларни ҳам яхши билгувчидир'}, {'chapter': 6, 'verse': 118, 'text': 'فَكُلُواْ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَيۡهِ إِن كُنتُم بِـَٔايَٰتِهِۦ مُؤۡمِنِينَ\nАгар Унинг оятларига иймон келтиргувчилардан бўлсангиз, Аллоҳнинг исми зикр қилинган нарсадан енг'}, {'chapter': 6, 'verse': 119, 'text': 'وَمَا لَكُمۡ أَلَّا تَأۡكُلُواْ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَيۡهِ وَقَدۡ فَصَّلَ لَكُم مَّا حَرَّمَ عَلَيۡكُمۡ إِلَّا مَا ٱضۡطُرِرۡتُمۡ إِلَيۡهِۗ وَإِنَّ كَثِيرٗا لَّيُضِلُّونَ بِأَهۡوَآئِهِم بِغَيۡرِ عِلۡمٍۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِٱلۡمُعۡتَدِينَ\nСизга нима бўлдики, Аллоҳнинг номи айтиб сўйилган нарсани емас экансизлар?! У сизларга ҳаром қилган нарсаларини батафсил баён қилиб берган-ку?! Магар музтар бўлганингизда, майли. Албатта, кўпчилик билмасдан, ҳойу ҳаваслари ила адаштирурлар. Албатта, Роббинг тажовузкорларни яхши билгувчидир'}, {'chapter': 6, 'verse': 120, 'text': 'وَذَرُواْ ظَٰهِرَ ٱلۡإِثۡمِ وَبَاطِنَهُۥٓۚ إِنَّ ٱلَّذِينَ يَكۡسِبُونَ ٱلۡإِثۡمَ سَيُجۡزَوۡنَ بِمَا كَانُواْ يَقۡتَرِفُونَ\nВа ошкора ҳамда махфий гуноҳларни тарк қилинглар! Гуноҳ қилувчилар, албатта, қилган гуноҳларининг жазосини олурлар'}, {'chapter': 6, 'verse': 121, 'text': 'وَلَا تَأۡكُلُواْ مِمَّا لَمۡ يُذۡكَرِ ٱسۡمُ ٱللَّهِ عَلَيۡهِ وَإِنَّهُۥ لَفِسۡقٞۗ وَإِنَّ ٱلشَّيَٰطِينَ لَيُوحُونَ إِلَىٰٓ أَوۡلِيَآئِهِمۡ لِيُجَٰدِلُوكُمۡۖ وَإِنۡ أَطَعۡتُمُوهُمۡ إِنَّكُمۡ لَمُشۡرِكُونَ\nАллоҳнинг исми зикр қилинмаган нарсаларни еманглар. Албатта, бу иш фисқдир. Албатта, шайтонлар ўз дўстларига сизлар билан тортишишни васваса қилурлар. Агар уларга итоат қилсангиз, сизлар ҳам мушриклардан бўлурсизлар'}, {'chapter': 6, 'verse': 122, 'text': 'أَوَمَن كَانَ مَيۡتٗا فَأَحۡيَيۡنَٰهُ وَجَعَلۡنَا لَهُۥ نُورٗا يَمۡشِي بِهِۦ فِي ٱلنَّاسِ كَمَن مَّثَلُهُۥ فِي ٱلظُّلُمَٰتِ لَيۡسَ بِخَارِجٖ مِّنۡهَاۚ كَذَٰلِكَ زُيِّنَ لِلۡكَٰفِرِينَ مَا كَانُواْ يَعۡمَلُونَ\nАввал ўлик бўлган одамни тирилтириб, унга одамлар орасида юрадиган нурни бериб қўйган бўлсак, у билан зулматлар ичида ундан чиқа олмай юрувчи одам ўхшаш бўлурми? Шундай қилиб, кофирларга қилаётган амаллари зийнатлаб кўрсатилди'}, {'chapter': 6, 'verse': 123, 'text': 'وَكَذَٰلِكَ جَعَلۡنَا فِي كُلِّ قَرۡيَةٍ أَكَٰبِرَ مُجۡرِمِيهَا لِيَمۡكُرُواْ فِيهَاۖ وَمَا يَمۡكُرُونَ إِلَّا بِأَنفُسِهِمۡ وَمَا يَشۡعُرُونَ\nХудди шунга ўхшаш, ҳар бир шаҳарда катта жиноятчиларни унда макр қилиш учун қўйдик. Улар, сезмаган ҳолда, фақат ўзларига ўзлари макр қилурлар, холос'}, {'chapter': 6, 'verse': 124, 'text': 'وَإِذَا جَآءَتۡهُمۡ ءَايَةٞ قَالُواْ لَن نُّؤۡمِنَ حَتَّىٰ نُؤۡتَىٰ مِثۡلَ مَآ أُوتِيَ رُسُلُ ٱللَّهِۘ ٱللَّهُ أَعۡلَمُ حَيۡثُ يَجۡعَلُ رِسَالَتَهُۥۗ سَيُصِيبُ ٱلَّذِينَ أَجۡرَمُواْ صَغَارٌ عِندَ ٱللَّهِ وَعَذَابٞ شَدِيدُۢ بِمَا كَانُواْ يَمۡكُرُونَ\nАгар уларга оят келса, Аллоҳнинг Пайғамбарларига берилган нарсага ўхшаш нарса бизга ҳам берилмагунча зинҳор иймон келтирмасмиз, дерлар. Ўз Пайғамбарлигини қаерга қўйишни Аллоҳнинг Ўзи яхши биладир. Жиноят қилганларга қилган макрлари учун Аллоҳнинг ҳузурида, албатта, хорлик ва шиддатли азоб бордир'}, {'chapter': 6, 'verse': 125, 'text': 'فَمَن يُرِدِ ٱللَّهُ أَن يَهۡدِيَهُۥ يَشۡرَحۡ صَدۡرَهُۥ لِلۡإِسۡلَٰمِۖ وَمَن يُرِدۡ أَن يُضِلَّهُۥ يَجۡعَلۡ صَدۡرَهُۥ ضَيِّقًا حَرَجٗا كَأَنَّمَا يَصَّعَّدُ فِي ٱلسَّمَآءِۚ كَذَٰلِكَ يَجۡعَلُ ٱللَّهُ ٱلرِّجۡسَ عَلَى ٱلَّذِينَ لَا يُؤۡمِنُونَ\nКимни Аллоҳ ҳидоят қилмоқчи бўлса, унинг кўксини Исломга очиб қўядир. Кимни залолатга кетказишни ирода қилса, унинг кўксини худди осмонга кўтарилаётгандек, тор ва танг қилиб қўядир. Шундай қилиб, Аллоҳ иймон келтирмайдиганларга азобни дучор қилур'}, {'chapter': 6, 'verse': 126, 'text': 'وَهَٰذَا صِرَٰطُ رَبِّكَ مُسۡتَقِيمٗاۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَذَّكَّرُونَ\nМана шу, Роббингнинг тўғри йўлидир. Ибрат олувчи қавм учун оятларни батафсил баён қилдик'}, {'chapter': 6, 'verse': 127, 'text': '۞لَهُمۡ دَارُ ٱلسَّلَٰمِ عِندَ رَبِّهِمۡۖ وَهُوَ وَلِيُّهُم بِمَا كَانُواْ يَعۡمَلُونَ\nУларга Роббилари ҳузурида дорус-салом (тинчлик уйи) бордир. У зот қилган ишлари учун уларнинг дўстидир'}, {'chapter': 6, 'verse': 128, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ جَمِيعٗا يَٰمَعۡشَرَ ٱلۡجِنِّ قَدِ ٱسۡتَكۡثَرۡتُم مِّنَ ٱلۡإِنسِۖ وَقَالَ أَوۡلِيَآؤُهُم مِّنَ ٱلۡإِنسِ رَبَّنَا ٱسۡتَمۡتَعَ بَعۡضُنَا بِبَعۡضٖ وَبَلَغۡنَآ أَجَلَنَا ٱلَّذِيٓ أَجَّلۡتَ لَنَاۚ قَالَ ٱلنَّارُ مَثۡوَىٰكُمۡ خَٰلِدِينَ فِيهَآ إِلَّا مَا شَآءَ ٱللَّهُۚ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ\nУларнинг ҳаммасини тўплайдиган кунда: «Эй, жинлар жамоаси, инсдан (адаштирганларингизни) кўпайтирвордингиз?!» – дер. Уларнинг инсдан бўлган дўстлари: «Эй Роббимиз, биз бир-биримиздан ҳузур топдик ва бизга белгилаган ажалингга етиб келдик», – дерлар. У зот: «Қайтар жойингиз дўзахдир. Магар Аллоҳ хоҳласа (қолмайсиз)», – дер. Албатта, Роббинг ҳикматли ва билимли Зотдир'}, {'chapter': 6, 'verse': 129, 'text': 'وَكَذَٰلِكَ نُوَلِّي بَعۡضَ ٱلظَّـٰلِمِينَ بَعۡضَۢا بِمَا كَانُواْ يَكۡسِبُونَ\nШундай қилиб, қилган касбларига биноан золимларни бир-бирларига дўст қилурмиз'}, {'chapter': 6, 'verse': 130, 'text': 'يَٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ شَهِدۡنَا عَلَىٰٓ أَنفُسِنَاۖ وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كَٰفِرِينَ\n«Эй, жин ва инс жамоалари, сизларга ўзингиздан бўлган Пайғамбарлар келиб, оятларимни сизга айтиб бермадиларми, сизларни ушбу кунингизга дучор бўлишингиз ҳақида огоҳлантирмадиларми?» Улар: «Ўзимизга қарши гувоҳлик бердик», – дерлар. Уларни дунё ҳаёти ғурурга кетказди ва ўзларига қарши, кофир бўлганларини айтиб, гувоҳлик бердилар'}, {'chapter': 6, 'verse': 131, 'text': 'ذَٰلِكَ أَن لَّمۡ يَكُن رَّبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمٖ وَأَهۡلُهَا غَٰفِلُونَ\nБундай қилишининг боиси, Роббинг ғофил турган шаҳар-қишлоқ аҳолисини ҳалок қилмаслигидандир'}, {'chapter': 6, 'verse': 132, 'text': 'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ\nҲар кимга қилганларига яраша даража бордир. Ва Роббинг уларнинг қилаётган амалларидан ғофил эмасдир'}, {'chapter': 6, 'verse': 133, 'text': 'وَرَبُّكَ ٱلۡغَنِيُّ ذُو ٱلرَّحۡمَةِۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَسۡتَخۡلِفۡ مِنۢ بَعۡدِكُم مَّا يَشَآءُ كَمَآ أَنشَأَكُم مِّن ذُرِّيَّةِ قَوۡمٍ ءَاخَرِينَ\nВа Роббинг беҳожат ва раҳмат соҳиби бўлган Зотдир. Агар хоҳласа, худди сизларни бошқа қавмлар зурриётидан пайдо қилганидек, сизларни кетказиб, ортингиздан нимани хоҳласа, ўринбосар қилиб келтирур'}, {'chapter': 6, 'verse': 134, 'text': 'إِنَّ مَا تُوعَدُونَ لَأٓتٖۖ وَمَآ أَنتُم بِمُعۡجِزِينَ\nАлбатта, сизга ваъда қилинган нарса келувчидир. Сизлар эса У зотни ожиз қолдирувчи эмассиз'}, {'chapter': 6, 'verse': 135, 'text': 'قُلۡ يَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَامِلٞۖ فَسَوۡفَ تَعۡلَمُونَ مَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nСен: «Эй қавмим, ўрнингизда амалингизни қилаверинг. Мен ҳам амал қилувчиман. Тезда охират ҳовлиси оқибати кимнинг фойдасига бўлишини биласиз. Албатта, золимлар нажот топмаслар», – деб айт'}, {'chapter': 6, 'verse': 136, 'text': 'وَجَعَلُواْ لِلَّهِ مِمَّا ذَرَأَ مِنَ ٱلۡحَرۡثِ وَٱلۡأَنۡعَٰمِ نَصِيبٗا فَقَالُواْ هَٰذَا لِلَّهِ بِزَعۡمِهِمۡ وَهَٰذَا لِشُرَكَآئِنَاۖ فَمَا كَانَ لِشُرَكَآئِهِمۡ فَلَا يَصِلُ إِلَى ٱللَّهِۖ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَىٰ شُرَكَآئِهِمۡۗ سَآءَ مَا يَحۡكُمُونَ\nУлар Аллоҳга Унинг Ўзи яратган экинлар ва чорвалардан насиба ажратдилар ва ўзларича: «Бу Аллоҳга, бу эса бутларимизга», – дедилар. Бас, бутларига бўлган нарса Аллоҳга етмас. Аллоҳга бўлган нарса эса, бутларига етадир. Чиқарган ҳукмлари қандай ҳам ёмон-а'}, {'chapter': 6, 'verse': 137, 'text': 'وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٖ مِّنَ ٱلۡمُشۡرِكِينَ قَتۡلَ أَوۡلَٰدِهِمۡ شُرَكَآؤُهُمۡ لِيُرۡدُوهُمۡ وَلِيَلۡبِسُواْ عَلَيۡهِمۡ دِينَهُمۡۖ وَلَوۡ شَآءَ ٱللَّهُ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ\nШунингдек, мушриклардан кўпларига шериклари уларни ҳалок этиш ва динларини чалкаштириш учун болаларини ўлдиришни зийнатлаб кўрсатди. Агар Аллоҳ хоҳласа, бундай қилмасдилар. Уларни ва тўқиётган нарсаларини қўйиб қўй'}, {'chapter': 6, 'verse': 138, 'text': 'وَقَالُواْ هَٰذِهِۦٓ أَنۡعَٰمٞ وَحَرۡثٌ حِجۡرٞ لَّا يَطۡعَمُهَآ إِلَّا مَن نَّشَآءُ بِزَعۡمِهِمۡ وَأَنۡعَٰمٌ حُرِّمَتۡ ظُهُورُهَا وَأَنۡعَٰمٞ لَّا يَذۡكُرُونَ ٱسۡمَ ٱللَّهِ عَلَيۡهَا ٱفۡتِرَآءً عَلَيۡهِۚ سَيَجۡزِيهِم بِمَا كَانُواْ يَفۡتَرُونَ\nУлар ўзларича: «Мана бу чорва ва экинлар ҳаром, уларни фақат биз хоҳлаган кишиларгина ейдилар ва мана бу миниши ҳаром қилинган чорвалар», – дедилар. Улар маълум чорваларга Аллоҳнинг номини зикр қилмаслар. Бу эса, У зотга туҳматдир. У, тезда, қилган туҳматлари туфайли уларга жазо беражак'}, {'chapter': 6, 'verse': 139, 'text': 'وَقَالُواْ مَا فِي بُطُونِ هَٰذِهِ ٱلۡأَنۡعَٰمِ خَالِصَةٞ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰٓ أَزۡوَٰجِنَاۖ وَإِن يَكُن مَّيۡتَةٗ فَهُمۡ فِيهِ شُرَكَآءُۚ سَيَجۡزِيهِمۡ وَصۡفَهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ\nУлар, мана шу чорваларнинг қорнидаги нарсалар эркакларимизга ҳалол, аёлларимизга ҳаром, дерлар. Агар у (қориндаги нарса) ўлик бўлса, улар унга шериклардир. Тезда уларга васфлари учун Ўзи жазо беражак. Албатта, У ҳикматли ва яхши билувчи Зотдир'}, {'chapter': 6, 'verse': 140, 'text': 'قَدۡ خَسِرَ ٱلَّذِينَ قَتَلُوٓاْ أَوۡلَٰدَهُمۡ سَفَهَۢا بِغَيۡرِ عِلۡمٖ وَحَرَّمُواْ مَا رَزَقَهُمُ ٱللَّهُ ٱفۡتِرَآءً عَلَى ٱللَّهِۚ قَدۡ ضَلُّواْ وَمَا كَانُواْ مُهۡتَدِينَ\nАқли пастлик қилиб, жоҳиллик ила фарзандларини ўлдирганлар ва Аллоҳ уларга ризқ қилиб берган нарсаларни ҳаром қилганлар зиёнкор бўлдилар. Батаҳқиқ, улар адашдилар ва ҳидоятга юрувчилардан бўлмадилар'}, {'chapter': 6, 'verse': 141, 'text': '۞وَهُوَ ٱلَّذِيٓ أَنشَأَ جَنَّـٰتٖ مَّعۡرُوشَٰتٖ وَغَيۡرَ مَعۡرُوشَٰتٖ وَٱلنَّخۡلَ وَٱلزَّرۡعَ مُخۡتَلِفًا أُكُلُهُۥ وَٱلزَّيۡتُونَ وَٱلرُّمَّانَ مُتَشَٰبِهٗا وَغَيۡرَ مُتَشَٰبِهٖۚ كُلُواْ مِن ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَءَاتُواْ حَقَّهُۥ يَوۡمَ حَصَادِهِۦۖ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡرِفِينَ\nУ (ишкомларга) кўтариладиган ва кўтарилмайдиган боғларни, хурмо ва экинларни емагини хилма-хил қилиб, зайтун ва анорларни бир-бирига ўхшатиб ва ўхшатмай пайдо қилган Зотдир. Мевалаган пайтида унинг мевасидан енглар ва йиғим-терим кунида ҳаққини беринглар. Ва исроф қилманглар. Албатта, У исроф қилгувчиларни севмас'}, {'chapter': 6, 'verse': 142, 'text': 'وَمِنَ ٱلۡأَنۡعَٰمِ حَمُولَةٗ وَفَرۡشٗاۚ كُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nВа чорвалардан ҳаммолларини ва (сўйгани) ерга ётқазиладиганларини пайдо қилди. Аллоҳ сизга ризқ қилиб берган нарсалардан енглар ва шайтоннинг изидан эргашманглар. Албатта, у сизларга очиқ-ойдин душмандир'}, {'chapter': 6, 'verse': 143, 'text': 'ثَمَٰنِيَةَ أَزۡوَٰجٖۖ مِّنَ ٱلضَّأۡنِ ٱثۡنَيۡنِ وَمِنَ ٱلۡمَعۡزِ ٱثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ ٱلۡأُنثَيَيۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ ٱلۡأُنثَيَيۡنِۖ نَبِّـُٔونِي بِعِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ\nСаккиз жуфт (пайдо қилди): қўйдан иккини (эркак-урғочи) ва эчкидан иккини. «Иккала эркагини ҳаром қилдими ёки иккала урғочисиними ёхуд иккала урғочининг бачадонларида шомил бўлган нарсаними?! Агар ростгўй бўлсангизлар, илмий қилиб хабарини беринглар-чи?» – деб айт'}, {'chapter': 6, 'verse': 144, 'text': 'وَمِنَ ٱلۡإِبِلِ ٱثۡنَيۡنِ وَمِنَ ٱلۡبَقَرِ ٱثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ ٱلۡأُنثَيَيۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ ٱلۡأُنثَيَيۡنِۖ أَمۡ كُنتُمۡ شُهَدَآءَ إِذۡ وَصَّىٰكُمُ ٱللَّهُ بِهَٰذَاۚ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا لِّيُضِلَّ ٱلنَّاسَ بِغَيۡرِ عِلۡمٍۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nТуядан иккитани, қорамолдан иккини (пайдо қилди). «Иккала эркагини ҳаром қилдими ёки иккала урғочисиними ёхуд иккала урғочининг бачадонларида шомил бўлган нарсаними?! Ёки Аллоҳ сизларга буни амр қилганда шоҳид бўлиб турганмидингиз? Илмсиз ҳолда одамларни адаштириш учун Аллоҳга нисбатан ёлғон тўқигандан ҳам золимроқ одам борми?! Албатта, Аллоҳ золим қавмларни ҳидоят қилмас»'}, {'chapter': 6, 'verse': 145, 'text': 'قُل لَّآ أَجِدُ فِي مَآ أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَىٰ طَاعِمٖ يَطۡعَمُهُۥٓ إِلَّآ أَن يَكُونَ مَيۡتَةً أَوۡ دَمٗا مَّسۡفُوحًا أَوۡ لَحۡمَ خِنزِيرٖ فَإِنَّهُۥ رِجۡسٌ أَوۡ فِسۡقًا أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۚ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ رَبَّكَ غَفُورٞ رَّحِيمٞ\n«Менга ваҳий қилинган нарсада таомланувчи учун ҳаром қилинган нарсани топмаяпман. Магар ўлимтик ёки оқизилган қон ёки тўнғиз гўшти бўлса, албатта, у ифлос, ҳаромдир. Ёхуд фосиқлик ила Аллоҳдан ўзганинг номига сўйилган бўлса (ҳаромдир). Бас, киши музтар бўлиб, маза талаб қилувчи ва тажовузкор бўлмаган ҳолида (истеъмол қилса), албатта, Роббинг мағфират қилгувчи ва раҳимли Зотдир», – деб айт'}, {'chapter': 6, 'verse': 146, 'text': 'وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا كُلَّ ذِي ظُفُرٖۖ وَمِنَ ٱلۡبَقَرِ وَٱلۡغَنَمِ حَرَّمۡنَا عَلَيۡهِمۡ شُحُومَهُمَآ إِلَّا مَا حَمَلَتۡ ظُهُورُهُمَآ أَوِ ٱلۡحَوَايَآ أَوۡ مَا ٱخۡتَلَطَ بِعَظۡمٖۚ ذَٰلِكَ جَزَيۡنَٰهُم بِبَغۡيِهِمۡۖ وَإِنَّا لَصَٰدِقُونَ\nВа яҳудий бўлганларга барча (бутун) туёқлиларни ҳаром қилдик. Мол ва қўйдан эса, уларга ёғларини ҳаром қилдик. Магар орқа ва ичакларидаги ёки суякка аралашган ёғлари (ҳалолдир). Бу, зулмлари учун уларга берган жазоимиздир. Албатта, Биз ростгўймиз'}, {'chapter': 6, 'verse': 147, 'text': 'فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمۡ ذُو رَحۡمَةٖ وَٰسِعَةٖ وَلَا يُرَدُّ بَأۡسُهُۥ عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِينَ\nАгар сени ёлғончига чиқарсалар: «Роббингиз кенг раҳмат соҳибидир, аммо Унинг азоби жиноятчи қавмлардан қайтарилмас», – деб айт'}, {'chapter': 6, 'verse': 148, 'text': 'سَيَقُولُ ٱلَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ ٱللَّهُ مَآ أَشۡرَكۡنَا وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن شَيۡءٖۚ كَذَٰلِكَ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ حَتَّىٰ ذَاقُواْ بَأۡسَنَاۗ قُلۡ هَلۡ عِندَكُم مِّنۡ عِلۡمٖ فَتُخۡرِجُوهُ لَنَآۖ إِن تَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ أَنتُمۡ إِلَّا تَخۡرُصُونَ\nҲали ширк келтирганлар: «Агар Аллоҳ хоҳлаганда, биз ҳам, ота-боболаримиз ҳам ширк келтирмас эдик ва бирор нарсани ҳаром қилмас эдик», – дерлар. Улардан олдингилари ҳам азобимизни татигунларига қадар шунга ўхшаш ёлғонга чиқариб турганлар. «Сизнинг ҳузурингизда бизга чиқариб кўрсатадиган бирон илм — ҳужжат борми? Сизлар фақат гумонга эргашмоқдасиз ва сизлар фақат ёлғон гапирмоқдасизлар», – деб айт'}, {'chapter': 6, 'verse': 149, 'text': 'قُلۡ فَلِلَّهِ ٱلۡحُجَّةُ ٱلۡبَٰلِغَةُۖ فَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ\nСен: «Етук ҳужжат Аллоҳнинг Ўзидадир. Агар хоҳласа, ҳаммангизни ҳидоятга солар эди», – деб айт'}, {'chapter': 6, 'verse': 150, 'text': 'قُلۡ هَلُمَّ شُهَدَآءَكُمُ ٱلَّذِينَ يَشۡهَدُونَ أَنَّ ٱللَّهَ حَرَّمَ هَٰذَاۖ فَإِن شَهِدُواْ فَلَا تَشۡهَدۡ مَعَهُمۡۚ وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ وَهُم بِرَبِّهِمۡ يَعۡدِلُونَ\nСен: «Аллоҳ мана шуни ҳаром қилганига гувоҳлик берадиган гувоҳларингизни келтиринг», – деб айт. Бас, агар гувоҳлик берсалар, сен улар ила гувоҳлик берма. Бизнинг оятларимизни ёлғонга чиқарувчиларнинг ва охиратга иймон келтирмайдиганларнинг ҳавойи нафсларига эргашма. Улар Роббиларига ширк келтирурлар'}, {'chapter': 6, 'verse': 151, 'text': '۞قُلۡ تَعَالَوۡاْ أَتۡلُ مَا حَرَّمَ رَبُّكُمۡ عَلَيۡكُمۡۖ أَلَّا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنٗاۖ وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُم مِّنۡ إِمۡلَٰقٖ نَّحۡنُ نَرۡزُقُكُمۡ وَإِيَّاهُمۡۖ وَلَا تَقۡرَبُواْ ٱلۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَۖ وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\nКелинг, Роббингиз сизларга ҳаром қилган нарсаларни тиловат қилиб бераман: «Унга ҳеч нарсани ширк келтирманг! Ота-онангизга яхшилик қилинг! Очликдан қўрқиб, болаларингизни ўлдирманг. Биз сизларни ҳам, уларни ҳам ризқлантирурмиз. Фаҳш — бузуқ ишларнинг ошкорасига ҳам, махфийсига ҳам яқинлашманг. Аллоҳ ҳаром қилган жонни ўлдирманг. Магар ҳақ ила бўлса, майли». «Ушбуларни сизга амр қилдикки, шояд, ақл юритсангиз», – деб айт'}, {'chapter': 6, 'verse': 152, 'text': 'وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ ٱلۡكَيۡلَ وَٱلۡمِيزَانَ بِٱلۡقِسۡطِۖ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۖ وَإِذَا قُلۡتُمۡ فَٱعۡدِلُواْ وَلَوۡ كَانَ ذَا قُرۡبَىٰۖ وَبِعَهۡدِ ٱللَّهِ أَوۡفُواْۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَذَكَّرُونَ\nЕтимнинг молига то вояга етгунча яқинлашманг. Магар яхши йўл билан бўлса, майли. Ўлчов ва тарозини адолат ила тўлиқ адо этинг. Биз ҳеч бир жонни тоқатидан ташқари нарсага таклиф этмаймиз. Агар гапирсангиз, қариндошингиз бўлса ҳам, адолат қилинг. Аллоҳга берган аҳдингизга вафо қилинг. Буларни сизга амр қилдикки, шояд, эсласангизлар'}, {'chapter': 6, 'verse': 153, 'text': 'وَأَنَّ هَٰذَا صِرَٰطِي مُسۡتَقِيمٗا فَٱتَّبِعُوهُۖ وَلَا تَتَّبِعُواْ ٱلسُّبُلَ فَتَفَرَّقَ بِكُمۡ عَن سَبِيلِهِۦۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَتَّقُونَ\nАлбатта, бу Менинг тўғри йўлимдир. Бас, унга эргашинг. Ва бошқа йўлларга эргашманг. Бас, сизни Унинг йўлидан адаштирмасинлар. Мана шу сизга қилган амрки, шояд, тақво қилсангиз'}, {'chapter': 6, 'verse': 154, 'text': 'ثُمَّ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ تَمَامًا عَلَى ٱلَّذِيٓ أَحۡسَنَ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُم بِلِقَآءِ رَبِّهِمۡ يُؤۡمِنُونَ\nЯна Мусога ҳам китобни, яхшилик қилганга (неъмат) батамом бўлиши учун, ҳар бир нарса батафсил бўлиши учун, ҳидоят ва раҳмат бўлиши учун бердик. Шоядки, улар Роббиларига рўбарў бўлишларига иймон келтирсалар'}, {'chapter': 6, 'verse': 155, 'text': 'وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ فَٱتَّبِعُوهُ وَٱتَّقُواْ لَعَلَّكُمۡ تُرۡحَمُونَ\nБиз туширган бу китоб муборакдир. Бас, унга эргашинглар. Ва тақво қилинглар. Шоядки, раҳим қилинсангизлар'}, {'chapter': 6, 'verse': 156, 'text': 'أَن تَقُولُوٓاْ إِنَّمَآ أُنزِلَ ٱلۡكِتَٰبُ عَلَىٰ طَآئِفَتَيۡنِ مِن قَبۡلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمۡ لَغَٰفِلِينَ\n«Биздан аввалги икки тоифага китоб туширилган, уларнинг дарсларидан ғофилмиз», демасликларингиз учун'}, {'chapter': 6, 'verse': 157, 'text': 'أَوۡ تَقُولُواْ لَوۡ أَنَّآ أُنزِلَ عَلَيۡنَا ٱلۡكِتَٰبُ لَكُنَّآ أَهۡدَىٰ مِنۡهُمۡۚ فَقَدۡ جَآءَكُم بَيِّنَةٞ مِّن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞۚ فَمَنۡ أَظۡلَمُ مِمَّن كَذَّبَ بِـَٔايَٰتِ ٱللَّهِ وَصَدَفَ عَنۡهَاۗ سَنَجۡزِي ٱلَّذِينَ يَصۡدِفُونَ عَنۡ ءَايَٰتِنَا سُوٓءَ ٱلۡعَذَابِ بِمَا كَانُواْ يَصۡدِفُونَ\nЁки «Агар бизга китоб туширилганда, улардан кўра ҳидоятлироқ бўлар эдик», демасликларингиз учун, батаҳқиқки, сизга Роббингиздан очиқ ҳужжат, ҳидоят ва раҳмат келди. Аллоҳнинг оятларини ёлғонга чиқарган ва улардан юз ўгирган кимсадан ҳам золимроқ шахс борми? Тезда, оятларимиздан юз ўгирганларни юз ўгирганлари туфайли ёмон азоб ила жазолармиз'}, {'chapter': 6, 'verse': 158, 'text': 'هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ ٱلۡمَلَـٰٓئِكَةُ أَوۡ يَأۡتِيَ رَبُّكَ أَوۡ يَأۡتِيَ بَعۡضُ ءَايَٰتِ رَبِّكَۗ يَوۡمَ يَأۡتِي بَعۡضُ ءَايَٰتِ رَبِّكَ لَا يَنفَعُ نَفۡسًا إِيمَٰنُهَا لَمۡ تَكُنۡ ءَامَنَتۡ مِن قَبۡلُ أَوۡ كَسَبَتۡ فِيٓ إِيمَٰنِهَا خَيۡرٗاۗ قُلِ ٱنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ\nУлар фаришталар келишини, ё Роббингнинг келишини ёки Роббингнинг баъзи оятлари келишини кутмоқдалар, холос. Роббингнинг баъзи оятлари келган кунида ҳеч бир жонга, олдин иймон келтирмаган ёки иймонида яхшилик касб қилмаган бўлса, иймони наф бермас. «Кутаверинглар, биз ҳам кутгувчилармиз», – деб айт'}, {'chapter': 6, 'verse': 159, 'text': 'إِنَّ ٱلَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗا لَّسۡتَ مِنۡهُمۡ فِي شَيۡءٍۚ إِنَّمَآ أَمۡرُهُمۡ إِلَى ٱللَّهِ ثُمَّ يُنَبِّئُهُم بِمَا كَانُواْ يَفۡعَلُونَ\nДинларини тафриқа қилиб, ўзлари гуруҳбозлик қилганлар билан ҳеч бир алоқанг йўқ. Албатта, уларнинг иши Аллоҳнинг Ўзига ҳавола. Сўнгра, қилган ишларининг хабарини берур'}, {'chapter': 6, 'verse': 160, 'text': 'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ عَشۡرُ أَمۡثَالِهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَا وَهُمۡ لَا يُظۡلَمُونَ\nКим бир яхшилик қилса, унга ўн баробар қайтарилур. Ким бир ёмонлик қилса, унга фақат қилганига яраша жазо бўлур. Ва уларга зулм қилинмас'}, {'chapter': 6, 'verse': 161, 'text': 'قُلۡ إِنَّنِي هَدَىٰنِي رَبِّيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ دِينٗا قِيَمٗا مِّلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۚ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nСен: «Албатта, Мени Роббим тўғри йўлга — рост динга — тўғриликка мойил Иброҳим миллатига ҳидоят қилди. У мушриклардан бўлмаган эди», – деб айт'}, {'chapter': 6, 'verse': 162, 'text': 'قُلۡ إِنَّ صَلَاتِي وَنُسُكِي وَمَحۡيَايَ وَمَمَاتِي لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nСен: «Албатта, менинг намозим, ибодатларим, ҳаёт ва мамотим оламларнинг Робби Аллоҳ учундир'}, {'chapter': 6, 'verse': 163, 'text': 'لَا شَرِيكَ لَهُۥۖ وَبِذَٰلِكَ أُمِرۡتُ وَأَنَا۠ أَوَّلُ ٱلۡمُسۡلِمِينَ\nУнинг шериги йўқ. Ана шунга буюрилганман. Ва мен энг аввалги мусулмонларданман'}, {'chapter': 6, 'verse': 164, 'text': 'قُلۡ أَغَيۡرَ ٱللَّهِ أَبۡغِي رَبّٗا وَهُوَ رَبُّ كُلِّ شَيۡءٖۚ وَلَا تَكۡسِبُ كُلُّ نَفۡسٍ إِلَّا عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nАллоҳдан ўзгани Роббим дейми? Ҳолбуки, У ҳамма нарсанинг Роббидир. Ҳар бир жон ёмон касб қилса, фақат ўзига зиён қилур. Ҳеч бир жон бошқанинг оғирлигини кўтармас. Сўнгра, Роббиларингизга қайтишингиз бор. Бас, У сизларга ихтилоф қилган нарсаларингизнинг хабарини берур'}, {'chapter': 6, 'verse': 165, 'text': 'وَهُوَ ٱلَّذِي جَعَلَكُمۡ خَلَـٰٓئِفَ ٱلۡأَرۡضِ وَرَفَعَ بَعۡضَكُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۗ إِنَّ رَبَّكَ سَرِيعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمُۢ\nУ сизларни ер юзида халифалар қилган ва Ўзи берган нарсаларда сизни синаш учун баъзингизнинг даражасини баъзингиздан устун қилган Зотдир. Албатта, Роббинг иқоби тез Зотдир. Ва, албатта, У мағфират қилувчи ва раҳимлидир», – деб айт'}], '7': [{'chapter': 7, 'verse': 1, 'text': 'الٓمٓصٓ\nАлиф. Лаам. Мийм. Соод'}, {'chapter': 7, 'verse': 2, 'text': 'كِتَٰبٌ أُنزِلَ إِلَيۡكَ فَلَا يَكُن فِي صَدۡرِكَ حَرَجٞ مِّنۡهُ لِتُنذِرَ بِهِۦ وَذِكۡرَىٰ لِلۡمُؤۡمِنِينَ\nБу, сенга у ила огоҳлантиришинг учун ва мўминларга эслатма этиб нозил қилинган китобдир. Кўнглингда ундан танглик бўлмасин'}, {'chapter': 7, 'verse': 3, 'text': 'ٱتَّبِعُواْ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡ وَلَا تَتَّبِعُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۗ قَلِيلٗا مَّا تَذَكَّرُونَ\nСизларга Роббингиздан нозил қилинган нарсага эргашинглар! Ундан бошқа валий — дўстларга эргашманглар. Жуда оз эслайсизлар'}, {'chapter': 7, 'verse': 4, 'text': 'وَكَم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا فَجَآءَهَا بَأۡسُنَا بَيَٰتًا أَوۡ هُمۡ قَآئِلُونَ\nҚанчадан-қанча шаҳар-қишлоқларни ҳалок қилдик. Бас, азобимиз уларга тун уйқусида ёки кун уйқусида келди'}, {'chapter': 7, 'verse': 5, 'text': 'فَمَا كَانَ دَعۡوَىٰهُمۡ إِذۡ جَآءَهُم بَأۡسُنَآ إِلَّآ أَن قَالُوٓاْ إِنَّا كُنَّا ظَٰلِمِينَ\nБас, Бизнинг азобимиз келган чоғида уларнинг даъволари: «Албатта, биз золимлардан бўлган эдик», – дейишларидан бошқа нарса бўлмади'}, {'chapter': 7, 'verse': 6, 'text': 'فَلَنَسۡـَٔلَنَّ ٱلَّذِينَ أُرۡسِلَ إِلَيۡهِمۡ وَلَنَسۡـَٔلَنَّ ٱلۡمُرۡسَلِينَ\nБас, Биз, албатта, Пайғамбар юборилганлардан ҳам ва, албатта, Пайғамбарлардан ҳам сўрармиз'}, {'chapter': 7, 'verse': 7, 'text': 'فَلَنَقُصَّنَّ عَلَيۡهِم بِعِلۡمٖۖ وَمَا كُنَّا غَآئِبِينَ\nБас, уларга илмий равишда қисса қилиб берурмиз. Ҳолбуки, Биз ғойиб бўлмаган эдик'}, {'chapter': 7, 'verse': 8, 'text': 'وَٱلۡوَزۡنُ يَوۡمَئِذٍ ٱلۡحَقُّۚ فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nЎша куни (амалларни) тортиш ҳақдир. Бас, кимнинг тортилган(амал)лари оғир келса, ана ўшалар нажот топгувчилардир'}, {'chapter': 7, 'verse': 9, 'text': 'وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُم بِمَا كَانُواْ بِـَٔايَٰتِنَا يَظۡلِمُونَ\nВа кимнинг тортилган (амал)лари енгил келса, бас, ана ўшалар Бизнинг оятларимизга зулм қилиб, ўзларига зиён этганлардир'}, {'chapter': 7, 'verse': 10, 'text': 'وَلَقَدۡ مَكَّنَّـٰكُمۡ فِي ٱلۡأَرۡضِ وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَۗ قَلِيلٗا مَّا تَشۡكُرُونَ\nБатаҳқиқ, сизларга ер юзида қудрат ва имкониятлар бердик ва унда сизлар учун яшаш воситалари қилдик. қанчалар оз шукр қиласизлар-а'}, {'chapter': 7, 'verse': 11, 'text': 'وَلَقَدۡ خَلَقۡنَٰكُمۡ ثُمَّ صَوَّرۡنَٰكُمۡ ثُمَّ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ لَمۡ يَكُن مِّنَ ٱلسَّـٰجِدِينَ\nВа батаҳқиқ, Биз сизларни яратдик, кейин сизларга суврат бердик. Сўнгра эса, фаришталарга: «Одамга сажда қилинг», – дедик. Бас, улар сажда қилдилар. Магар иблис сажда қилувчилардан бўлмади'}, {'chapter': 7, 'verse': 12, 'text': 'قَالَ مَا مَنَعَكَ أَلَّا تَسۡجُدَ إِذۡ أَمَرۡتُكَۖ قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ\nУ зот: «Сенга амр этганимда сажда қилишингдан нима тўсди?» – деди. У: «Мен ундан яхшиман, мени ўтдан яратдинг ва уни лойдан яратдинг», – деди'}, {'chapter': 7, 'verse': 13, 'text': 'قَالَ فَٱهۡبِطۡ مِنۡهَا فَمَا يَكُونُ لَكَ أَن تَتَكَبَّرَ فِيهَا فَٱخۡرُجۡ إِنَّكَ مِنَ ٱلصَّـٰغِرِينَ\nУ зот: «Бас, у(жаннат)дан туш! Сен учун унда мутакаббирлик қилиб юриш йўқ. Бас, чиқ! Албатта, сен хору зор бўлгувчилардансан», – деди'}, {'chapter': 7, 'verse': 14, 'text': 'قَالَ أَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nУ: «Менга улар қайта тирилтириладиган кунгача муҳлат бер», – деди'}, {'chapter': 7, 'verse': 15, 'text': 'قَالَ إِنَّكَ مِنَ ٱلۡمُنظَرِينَ\nУ зот: «Албатта, сен муҳлат берилганлардансан», – деди'}, {'chapter': 7, 'verse': 16, 'text': 'قَالَ فَبِمَآ أَغۡوَيۡتَنِي لَأَقۡعُدَنَّ لَهُمۡ صِرَٰطَكَ ٱلۡمُسۡتَقِيمَ\nУ: «Мени иғвога учирганинг сабабли, албатта, мен уларни Тўғри йўлингда тўсиб ўтираман'}, {'chapter': 7, 'verse': 17, 'text': 'ثُمَّ لَأٓتِيَنَّهُم مِّنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ وَعَنۡ أَيۡمَٰنِهِمۡ وَعَن شَمَآئِلِهِمۡۖ وَلَا تَجِدُ أَكۡثَرَهُمۡ شَٰكِرِينَ\nСўнгра, уларнинг олдиларидан, орқаларидан, ўнг томонларидан ва чап томонларидан келаман. Ва уларнинг кўпларини шукр қилувчи ҳолда топмассан», – деди'}, {'chapter': 7, 'verse': 18, 'text': 'قَالَ ٱخۡرُجۡ مِنۡهَا مَذۡءُومٗا مَّدۡحُورٗاۖ لَّمَن تَبِعَكَ مِنۡهُمۡ لَأَمۡلَأَنَّ جَهَنَّمَ مِنكُمۡ أَجۡمَعِينَ\nУ зот: «Ундан мазамматга учраган ва қувилган ҳолингда чиқ!!! Улардан ким сенга эргашса, албатта, жаҳаннамни сизларнинг ҳаммангиз билан тўлдирурман», – деди'}, {'chapter': 7, 'verse': 19, 'text': 'وَيَـٰٓـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ فَكُلَا مِنۡ حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِينَ\nЭй Одам, сен жуфтинг билан жаннатни маскан тут. Икковингиз хоҳлаган жойингиздан енг. Аммо мана бу дарахтга яқинлашманглар. Унинг сабабидан золимлардан бўлиб қолманглар'}, {'chapter': 7, 'verse': 20, 'text': 'فَوَسۡوَسَ لَهُمَا ٱلشَّيۡطَٰنُ لِيُبۡدِيَ لَهُمَا مَا وُۥرِيَ عَنۡهُمَا مِن سَوۡءَٰتِهِمَا وَقَالَ مَا نَهَىٰكُمَا رَبُّكُمَا عَنۡ هَٰذِهِ ٱلشَّجَرَةِ إِلَّآ أَن تَكُونَا مَلَكَيۡنِ أَوۡ تَكُونَا مِنَ ٱلۡخَٰلِدِينَ\nБас, шайтон икковларининг беркитилган авратларини очиш учун уларни васваса қилди ва: «Роббингиз сизни ушбу дарахтдан фақат икковингиз фаришта бўлмаслигингиз ёки абадий қолмаслигингиз учунгина ман қилди», – деди'}, {'chapter': 7, 'verse': 21, 'text': 'وَقَاسَمَهُمَآ إِنِّي لَكُمَا لَمِنَ ٱلنَّـٰصِحِينَ\nУларга: «Албатта, мен сизларга насиҳат қилувчиларданман», – деб қасам ичди'}, {'chapter': 7, 'verse': 22, 'text': 'فَدَلَّىٰهُمَا بِغُرُورٖۚ فَلَمَّا ذَاقَا ٱلشَّجَرَةَ بَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۖ وَنَادَىٰهُمَا رَبُّهُمَآ أَلَمۡ أَنۡهَكُمَا عَن تِلۡكُمَا ٱلشَّجَرَةِ وَأَقُل لَّكُمَآ إِنَّ ٱلشَّيۡطَٰنَ لَكُمَا عَدُوّٞ مُّبِينٞ\nБас, уларни алдов ила паст(ҳолат)га туширди. Икковлари дарахтдан татиб кўришганда, авратлари ўзларига кўриниб қолди ва ўзларига жаннат баргларидан тўсиқ тўқий бошладилар. Шунда, Роббилари уларга нидо қилиб: «Сизларни ана ўша дарахтдан қайтарган эмасмидим ва, албатта, шайтон икковингизга очиқ-ойдин душмандир, демаганмидим?!» – деди'}, {'chapter': 7, 'verse': 23, 'text': 'قَالَا رَبَّنَا ظَلَمۡنَآ أَنفُسَنَا وَإِن لَّمۡ تَغۡفِرۡ لَنَا وَتَرۡحَمۡنَا لَنَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ\nИкковлари: «Эй Роббимиз, биз ўзимизга зулм қилдик. Агар Сен бизни мағфират қилмасанг ва бизга раҳим қилмасанг, албатта, зиён кўрганлардан бўламиз», – дедилар'}, {'chapter': 7, 'verse': 24, 'text': 'قَالَ ٱهۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي ٱلۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ\nУ зот: «Бир-бирингизга душман бўлган ҳолингизда тушинглар. Сизларга ер юзида қароргоҳ бор ва маълум вақтгача фойдаланиш бор», – деди'}, {'chapter': 7, 'verse': 25, 'text': 'قَالَ فِيهَا تَحۡيَوۡنَ وَفِيهَا تَمُوتُونَ وَمِنۡهَا تُخۡرَجُونَ\nУ зот: «Унда ҳаёт кечирасизлар, унда ўласизлар ва ундан қайта чиқариласизлар», – деди'}, {'chapter': 7, 'verse': 26, 'text': 'يَٰبَنِيٓ ءَادَمَ قَدۡ أَنزَلۡنَا عَلَيۡكُمۡ لِبَاسٗا يُوَٰرِي سَوۡءَٰتِكُمۡ وَرِيشٗاۖ وَلِبَاسُ ٱلتَّقۡوَىٰ ذَٰلِكَ خَيۡرٞۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِ لَعَلَّهُمۡ يَذَّكَّرُونَ\nЭй, Одам болалари, батаҳқиқ, сизларга авратингизни тўсадиган либос ва зийнат либосини нозил қилдик. Тақво либоси, ана ўша яхшидир. Ана ўшалар Аллоҳнинг оят-белгиларидандир. Шоядки, эсласалар'}, {'chapter': 7, 'verse': 27, 'text': 'يَٰبَنِيٓ ءَادَمَ لَا يَفۡتِنَنَّكُمُ ٱلشَّيۡطَٰنُ كَمَآ أَخۡرَجَ أَبَوَيۡكُم مِّنَ ٱلۡجَنَّةِ يَنزِعُ عَنۡهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوۡءَٰتِهِمَآۚ إِنَّهُۥ يَرَىٰكُمۡ هُوَ وَقَبِيلُهُۥ مِنۡ حَيۡثُ لَا تَرَوۡنَهُمۡۗ إِنَّا جَعَلۡنَا ٱلشَّيَٰطِينَ أَوۡلِيَآءَ لِلَّذِينَ لَا يُؤۡمِنُونَ\nЭй, Одам болалари, шайтон ота-онангизни ўзларига авратларини кўрсатиш учун устларидан либосларини ечиб, жаннатдан чиқарганидек, сизни ҳам фитнага солмасин. Албатта, у ва унинг ёрдамчилари сизни сиз кўрмайдиган томондан кўрадир. Биз, албатта, шайтонларни иймон келтирмайдиганларга дўст қилганмиз'}, {'chapter': 7, 'verse': 28, 'text': 'وَإِذَا فَعَلُواْ فَٰحِشَةٗ قَالُواْ وَجَدۡنَا عَلَيۡهَآ ءَابَآءَنَا وَٱللَّهُ أَمَرَنَا بِهَاۗ قُلۡ إِنَّ ٱللَّهَ لَا يَأۡمُرُ بِٱلۡفَحۡشَآءِۖ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nУлар қачон фоҳиша иш қилсалар: «Ота-боболаримиз ушбуни қилаётганини кўрдик. Буни бизга Аллоҳ буюрган», – дерлар. «Аллоҳ, албатта, фаҳш ишларга буюрмас. Аллоҳ шаънига билмайдиган нарсангизни айтасизми?!» – дегин'}, {'chapter': 7, 'verse': 29, 'text': 'قُلۡ أَمَرَ رَبِّي بِٱلۡقِسۡطِۖ وَأَقِيمُواْ وُجُوهَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَٱدۡعُوهُ مُخۡلِصِينَ لَهُ ٱلدِّينَۚ كَمَا بَدَأَكُمۡ تَعُودُونَ\nСен: «Роббим адолатга амр қилди. Ҳар бир ибодатхонада юзингизни тўғри тутинг. Унинг динига ихлос этган ҳолда дуо қилинг. Сизларни аввалда қандай яратган бўлса, ўша ҳолда қайтасизлар», – деб айт'}, {'chapter': 7, 'verse': 30, 'text': 'فَرِيقًا هَدَىٰ وَفَرِيقًا حَقَّ عَلَيۡهِمُ ٱلضَّلَٰلَةُۚ إِنَّهُمُ ٱتَّخَذُواْ ٱلشَّيَٰطِينَ أَوۡلِيَآءَ مِن دُونِ ٱللَّهِ وَيَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ\nБир гуруҳни ҳидоят қилди. Бошқа гуруҳга йўлдан озиш ҳақ бўлди. Чунки, улар Аллоҳни қўйиб шайтонларни ўзларига дўст тутдилар. Ҳамда ўзларини, албатта, ҳидоят топганлар, деб ҳисобларлар'}, {'chapter': 7, 'verse': 31, 'text': '۞يَٰبَنِيٓ ءَادَمَ خُذُواْ زِينَتَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَكُلُواْ وَٱشۡرَبُواْ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡرِفِينَ\nЭй, Одам болалари, ҳар бир ибодат чоғида ўз зийнатингизни олинг. Еб-ичинг ва исроф қилманг. Чунки, У исроф қилувчиларни севмас'}, {'chapter': 7, 'verse': 32, 'text': 'قُلۡ مَنۡ حَرَّمَ زِينَةَ ٱللَّهِ ٱلَّتِيٓ أَخۡرَجَ لِعِبَادِهِۦ وَٱلطَّيِّبَٰتِ مِنَ ٱلرِّزۡقِۚ قُلۡ هِيَ لِلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا خَالِصَةٗ يَوۡمَ ٱلۡقِيَٰمَةِۗ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nСен: «Аллоҳ Ўз бандаларига чиқарган зийнатларни ва покиза ризқларни ким ҳаром қилди?!» – деб айт. Сен: «Улар ҳаёти дунёда иймон келтирганларга, қиёмат кунида эса фақат ўзларига хосдир», – деб айт. Биладиган қавмлар учун оятларни мана шундай муфассал баён қилурмиз'}, {'chapter': 7, 'verse': 33, 'text': 'قُلۡ إِنَّمَا حَرَّمَ رَبِّيَ ٱلۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَ وَٱلۡإِثۡمَ وَٱلۡبَغۡيَ بِغَيۡرِ ٱلۡحَقِّ وَأَن تُشۡرِكُواْ بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗا وَأَن تَقُولُواْ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nСен: «Роббим фақатгина ошкора ва яширин фаҳш ишларни, гуноҳларни, ноҳақ тажовузкорликни ва Аллоҳга ҳеч қандай ҳужжат туширмаган нарсаларни шерик қилиб олишингизни ҳамда Аллоҳга нисбатан билмаган нарсани айтишни ҳаром қилган, холос», – деб айт'}, {'chapter': 7, 'verse': 34, 'text': 'وَلِكُلِّ أُمَّةٍ أَجَلٞۖ فَإِذَا جَآءَ أَجَلُهُمۡ لَا يَسۡتَأۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ\nҲар бир умматнинг ажали бор. Ажаллари келган вақтида бирор соатга кетга ҳам, олдинга ҳам сура олмаслар'}, {'chapter': 7, 'verse': 35, 'text': 'يَٰبَنِيٓ ءَادَمَ إِمَّا يَأۡتِيَنَّكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي فَمَنِ ٱتَّقَىٰ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nЭй, Одам болалари, агар сизларга ўзингиздан бўлган Пайғамбарлар келиб, оятларимни сўзлаганда, бас, ким тақво қилса ва яхши амал қилса, уларга хавф йўқдир ва улар хафа ҳам бўлмаслар'}, {'chapter': 7, 'verse': 36, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱسۡتَكۡبَرُواْ عَنۡهَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nОятларимизни ёлғонга чиқарганлар ва улардан кибр қилганлар, ана ўшалар дўзах эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 7, 'verse': 37, 'text': 'فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ أُوْلَـٰٓئِكَ يَنَالُهُمۡ نَصِيبُهُم مِّنَ ٱلۡكِتَٰبِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمۡ رُسُلُنَا يَتَوَفَّوۡنَهُمۡ قَالُوٓاْ أَيۡنَ مَا كُنتُمۡ تَدۡعُونَ مِن دُونِ ٱللَّهِۖ قَالُواْ ضَلُّواْ عَنَّا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كَٰفِرِينَ\nАллоҳга нисбатан ёлғон тўқиган ёки унинг оятларини ёлғонга чиқарган кимсадан ҳам золимроқ одам борми?! Ана ўшаларга китобдан насибалари етар. Бизнинг элчиларимиз келиб уларнинг жонларини олаётиб: «Аллоҳдан ўзга ибодат қилаётган нарсаларингиз қани?» – дерлар. Улар: «Биздан ғойиб бўлишди», – дерлар ва ўз зиёнларига, кофир бўлганларини айтиб, гувоҳлик берарлар'}, {'chapter': 7, 'verse': 38, 'text': 'قَالَ ٱدۡخُلُواْ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِكُم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِ فِي ٱلنَّارِۖ كُلَّمَا دَخَلَتۡ أُمَّةٞ لَّعَنَتۡ أُخۡتَهَاۖ حَتَّىٰٓ إِذَا ٱدَّارَكُواْ فِيهَا جَمِيعٗا قَالَتۡ أُخۡرَىٰهُمۡ لِأُولَىٰهُمۡ رَبَّنَا هَـٰٓؤُلَآءِ أَضَلُّونَا فَـَٔاتِهِمۡ عَذَابٗا ضِعۡفٗا مِّنَ ٱلنَّارِۖ قَالَ لِكُلّٖ ضِعۡفٞ وَلَٰكِن لَّا تَعۡلَمُونَ\nУ зот: «Сиздан олдин ўтган, жин ва инсдан бўлган умматлар билан дўзахга киринг», – дер. Қачонки, қайси бир уммат (дўзахга) кирса, шеригини лаънатлар. Бироқ у ерда ҳаммалари топишганларида, кейингилари аввалгиларини (маломат қилиб): «Эй Роббимиз, бизни анавилар адаштирган эдилар. Уларга дўзах азобини икки баробар қилиб бер», – дерлар. У зот: «Ҳаммага ҳам икки баробар, лекин билмайсизлар», – дер'}, {'chapter': 7, 'verse': 39, 'text': 'وَقَالَتۡ أُولَىٰهُمۡ لِأُخۡرَىٰهُمۡ فَمَا كَانَ لَكُمۡ عَلَيۡنَا مِن فَضۡلٖ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡسِبُونَ\nАввалгилар кейингиларга: «Сизларнинг биздан афзаллик томонингиз йўқ. Қилган касбингизга яраша азобни тортаверинглар», – дерлар'}, {'chapter': 7, 'verse': 40, 'text': 'إِنَّ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱسۡتَكۡبَرُواْ عَنۡهَا لَا تُفَتَّحُ لَهُمۡ أَبۡوَٰبُ ٱلسَّمَآءِ وَلَا يَدۡخُلُونَ ٱلۡجَنَّةَ حَتَّىٰ يَلِجَ ٱلۡجَمَلُ فِي سَمِّ ٱلۡخِيَاطِۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُجۡرِمِينَ\nАлбатта, оятларимизни ёлғонга чиқарганлар ва улардан кибр қилганларга осмон эшиклари очилмас ва улар туя игна тешигидан ўтмагунча жаннатга кирмаслар. Жиноятчиларни мана шундай жазолаймиз'}, {'chapter': 7, 'verse': 41, 'text': 'لَهُم مِّن جَهَنَّمَ مِهَادٞ وَمِن فَوۡقِهِمۡ غَوَاشٖۚ وَكَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ\nУларга жаҳаннамдан тўшак ва устларига чойшаб бор. Золимларни мана шундай жазолаймиз'}, {'chapter': 7, 'verse': 42, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nИймон келтириб, яхши амалларни қилганлар — Биз ҳеч бир жонни тоқатидан ташқари нарсага таклиф қилмаймиз — ана ўшалар жаннат эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 7, 'verse': 43, 'text': 'وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلّٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُۖ وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي هَدَىٰنَا لِهَٰذَا وَمَا كُنَّا لِنَهۡتَدِيَ لَوۡلَآ أَنۡ هَدَىٰنَا ٱللَّهُۖ لَقَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّۖ وَنُودُوٓاْ أَن تِلۡكُمُ ٱلۡجَنَّةُ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ\nВа кўксиларидаги ғиллу ғашларни чиқариб олдик, остиларидан анҳорлар оқиб турибди. Улар: «Бизни шунга ҳидоят қилган Аллоҳга ҳамдлар бўлсин, агар Аллоҳ бизни ҳидоят қилмаганда, ўзимиз йўл топа олмас эдик. Роббимизнинг элчилари ҳақни келтирдилар», – дедилар. Ва уларга: «Мана шу жаннатингизни қилган амалларингиз туфайли мерос қилиб олдингиз», – деб нидо қилинур'}, {'chapter': 7, 'verse': 44, 'text': 'وَنَادَىٰٓ أَصۡحَٰبُ ٱلۡجَنَّةِ أَصۡحَٰبَ ٱلنَّارِ أَن قَدۡ وَجَدۡنَا مَا وَعَدَنَا رَبُّنَا حَقّٗا فَهَلۡ وَجَدتُّم مَّا وَعَدَ رَبُّكُمۡ حَقّٗاۖ قَالُواْ نَعَمۡۚ فَأَذَّنَ مُؤَذِّنُۢ بَيۡنَهُمۡ أَن لَّعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِينَ\nВа жаннат эгалари дўзах эгаларига: «Биз Роббимиз бизга ваъда қилган нарсанинг ҳақ эканини топдик. Сизлар ҳам Роббингиз ваъда қилган нарсанинг ҳақ эканини топдингизми?!» – деб нидо қиладир. Улар: «Ҳа!» – дерлар. Бас, улар ўрталарида бир жарчи: «Золимларга Аллоҳнинг лаънати бўлсин'}, {'chapter': 7, 'verse': 45, 'text': 'ٱلَّذِينَ يَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِٱلۡأٓخِرَةِ كَٰفِرُونَ\nАллоҳнинг йўлидан тўсадиган, уни бузишни истайдиган ва охиратга кофир бўлувчиларга!!!» – деб жар соладир'}, {'chapter': 7, 'verse': 46, 'text': 'وَبَيۡنَهُمَا حِجَابٞۚ وَعَلَى ٱلۡأَعۡرَافِ رِجَالٞ يَعۡرِفُونَ كُلَّۢا بِسِيمَىٰهُمۡۚ وَنَادَوۡاْ أَصۡحَٰبَ ٱلۡجَنَّةِ أَن سَلَٰمٌ عَلَيۡكُمۡۚ لَمۡ يَدۡخُلُوهَا وَهُمۡ يَطۡمَعُونَ\nОраларида тўсиқ бор, у деворлар устидаги кишилар ҳаммаларини сиймоларидан танирлар. Тамаъ қилган ҳолларида кирмай турган жаннат эгаларига: «Сизга салом бўлсин», – деб нидо қилурлар'}, {'chapter': 7, 'verse': 47, 'text': '۞وَإِذَا صُرِفَتۡ أَبۡصَٰرُهُمۡ تِلۡقَآءَ أَصۡحَٰبِ ٱلنَّارِ قَالُواْ رَبَّنَا لَا تَجۡعَلۡنَا مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nҚачонки кўзлари дўзах эгалари томон бурилиб қолса: «Эй Роббимиз, бизни золим қавмлар билан бирга қилмагин», дерлар'}, {'chapter': 7, 'verse': 48, 'text': 'وَنَادَىٰٓ أَصۡحَٰبُ ٱلۡأَعۡرَافِ رِجَالٗا يَعۡرِفُونَهُم بِسِيمَىٰهُمۡ قَالُواْ مَآ أَغۡنَىٰ عَنكُمۡ جَمۡعُكُمۡ وَمَا كُنتُمۡ تَسۡتَكۡبِرُونَ\nАъроф эгалари сиймоларидан танийдиган кишиларига нидо қилиб: «Тўплаганингиз ва қилган кибру ҳавоингиз сизга асқотмабди-ку'}, {'chapter': 7, 'verse': 49, 'text': 'أَهَـٰٓؤُلَآءِ ٱلَّذِينَ أَقۡسَمۡتُمۡ لَا يَنَالُهُمُ ٱللَّهُ بِرَحۡمَةٍۚ ٱدۡخُلُواْ ٱلۡجَنَّةَ لَا خَوۡفٌ عَلَيۡكُمۡ وَلَآ أَنتُمۡ تَحۡزَنُونَ\nСизлар «Аллоҳ уларга раҳмат етказмас», деб қасам ичганингиз ана уларми?! (Уларга): Жаннатга киринглар! Сизга хавф йўқ ва сиз хафа ҳам бўлмайсиз, дейилди-ку!» – дерлар'}, {'chapter': 7, 'verse': 50, 'text': 'وَنَادَىٰٓ أَصۡحَٰبُ ٱلنَّارِ أَصۡحَٰبَ ٱلۡجَنَّةِ أَنۡ أَفِيضُواْ عَلَيۡنَا مِنَ ٱلۡمَآءِ أَوۡ مِمَّا رَزَقَكُمُ ٱللَّهُۚ قَالُوٓاْ إِنَّ ٱللَّهَ حَرَّمَهُمَا عَلَى ٱلۡكَٰفِرِينَ\nДўзах эгалари жаннат эгаларига: «Бизга ҳам сувдан ёки Аллоҳ сизга ризқ қилиб берган нарсадан тўкинглар», – деб нидо қиларлар. Улар: «Албатта, Аллоҳ буларни кофирларга ҳаром қилган'}, {'chapter': 7, 'verse': 51, 'text': 'ٱلَّذِينَ ٱتَّخَذُواْ دِينَهُمۡ لَهۡوٗا وَلَعِبٗا وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ نَنسَىٰهُمۡ كَمَا نَسُواْ لِقَآءَ يَوۡمِهِمۡ هَٰذَا وَمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ\nДинларини ўйин-кулги қилиб олган ва ҳаёти дунё ғурурига кетганларга», – дерлар. Бас, бу Кунда улар бугунги ҳолга дучор бўлишларини унутганлари ва оятларимизни инкор қилганларидек, Биз ҳам уларни унутамиз'}, {'chapter': 7, 'verse': 52, 'text': 'وَلَقَدۡ جِئۡنَٰهُم بِكِتَٰبٖ فَصَّلۡنَٰهُ عَلَىٰ عِلۡمٍ هُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ\nБатаҳқиқ, Биз уларга илм ила муфассал қилинган, иймон келтирган қавм учун ҳидоят ва раҳмат бўлган китобни келтирдик'}, {'chapter': 7, 'verse': 53, 'text': 'هَلۡ يَنظُرُونَ إِلَّا تَأۡوِيلَهُۥۚ يَوۡمَ يَأۡتِي تَأۡوِيلُهُۥ يَقُولُ ٱلَّذِينَ نَسُوهُ مِن قَبۡلُ قَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّ فَهَل لَّنَا مِن شُفَعَآءَ فَيَشۡفَعُواْ لَنَآ أَوۡ نُرَدُّ فَنَعۡمَلَ غَيۡرَ ٱلَّذِي كُنَّا نَعۡمَلُۚ قَدۡ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nУлар унинг таъвийлини — оқибатини кутмоқдалар, холос. Унинг таъвийли келган кунда илгари уни унутганлар: «Роббимизнинг Пайғамбарлари ҳақ ила келган эканлар. Энди бизга шафоат қилувчилар борми, шафоат қилсалар ёки ортга қайтарилсаг-у, қилган амалларимиздан бошқача амалларни қилсак», – дерлар. Батаҳқиқ, улар ўзларига зиён қилдилар ва тўқиб олган нарсалари улардан ғойиб бўлди'}, {'chapter': 7, 'verse': 54, 'text': 'إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يُغۡشِي ٱلَّيۡلَ ٱلنَّهَارَ يَطۡلُبُهُۥ حَثِيثٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ وَٱلنُّجُومَ مُسَخَّرَٰتِۭ بِأَمۡرِهِۦٓۗ أَلَا لَهُ ٱلۡخَلۡقُ وَٱلۡأَمۡرُۗ تَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\n«Албатта, осмонлару ерни олти кунда яратган, сўнгра (Зоти ва улуғлигига муносиб суратда) Аршга юксалган Роббингиз кеча ила кундузни қоплайдир. Униси бунисини шошилиб қувлайдир. Қуёш, ой ва юлдузларни Ўз амрига бўйсундирилган қилиб яратди. Огоҳ бўлинг, яратиш ва амр қилиш Унинг Ўзига хосдир. Оламларнинг Робби — Аллоҳ буюкдир'}, {'chapter': 7, 'verse': 55, 'text': 'ٱدۡعُواْ رَبَّكُمۡ تَضَرُّعٗا وَخُفۡيَةًۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ\nРоббингизга тазарруъ ила ва махфий дуо қилинг. Зотан, У ҳаддан ошувчиларни севмас'}, {'chapter': 7, 'verse': 56, 'text': 'وَلَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَا وَٱدۡعُوهُ خَوۡفٗا وَطَمَعًاۚ إِنَّ رَحۡمَتَ ٱللَّهِ قَرِيبٞ مِّنَ ٱلۡمُحۡسِنِينَ\nВа ислоҳ қилингандан кейин ер юзида фасод қилманг ва Ундан қўрқиб тамаъ ила дуо қилинг. Албатта, Аллоҳнинг раҳмати яхшилик қилувчиларга яқиндир'}, {'chapter': 7, 'verse': 57, 'text': 'وَهُوَ ٱلَّذِي يُرۡسِلُ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۖ حَتَّىٰٓ إِذَآ أَقَلَّتۡ سَحَابٗا ثِقَالٗا سُقۡنَٰهُ لِبَلَدٖ مَّيِّتٖ فَأَنزَلۡنَا بِهِ ٱلۡمَآءَ فَأَخۡرَجۡنَا بِهِۦ مِن كُلِّ ٱلثَّمَرَٰتِۚ كَذَٰلِكَ نُخۡرِجُ ٱلۡمَوۡتَىٰ لَعَلَّكُمۡ تَذَكَّرُونَ\nУ Ўз раҳматидан олдин шамолларни хушхабар қилиб юборадиган Зотдир. Қачонки, улар оғир булутларни кўтариб келтиргач, у ила ўлик юртни суғорамиз. Ундан сув тушириб, ўша ила турли мевалар чиқарамиз. Ўликларни ҳам шундай қилиб чиқарамиз. Шоядки, эсласангизлар'}, {'chapter': 7, 'verse': 58, 'text': 'وَٱلۡبَلَدُ ٱلطَّيِّبُ يَخۡرُجُ نَبَاتُهُۥ بِإِذۡنِ رَبِّهِۦۖ وَٱلَّذِي خَبُثَ لَا يَخۡرُجُ إِلَّا نَكِدٗاۚ كَذَٰلِكَ نُصَرِّفُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَشۡكُرُونَ\nПок юртнинг набототи Роббининг изни ила чиқадир. Нопок бўлгани эса, фақат қийинчилик ила чиқадир. Шукр келтирган қавмлар учун оятларимизни шундай хилма-хил баён қиламиз'}, {'chapter': 7, 'verse': 59, 'text': 'لَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ\nБатаҳқиқ, Биз Нуҳни ўз қавмига юбордик. Бас, у: «Эй қавмим, Аллоҳга ибодат қилинглар. Сизларга Ундан ўзга илоҳ йўқ. Албатта, мен сизларнинг буюк куннинг азобига гирифтор бўлишингиздан қўрқаман», – деди'}, {'chapter': 7, 'verse': 60, 'text': 'قَالَ ٱلۡمَلَأُ مِن قَوۡمِهِۦٓ إِنَّا لَنَرَىٰكَ فِي ضَلَٰلٖ مُّبِينٖ\nУнинг қавмидан бўлган зодагонлар: «Албатта, биз сенинг очиқ-ойдин залолатда эканингни кўряпмиз», – дедилар'}, {'chapter': 7, 'verse': 61, 'text': 'قَالَ يَٰقَوۡمِ لَيۡسَ بِي ضَلَٰلَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nУ: «Эй қавмим, менда залолат йўқ. Ва лекин мен оламларнинг Роббидан Пайғамбарман'}, {'chapter': 7, 'verse': 62, 'text': 'أُبَلِّغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنصَحُ لَكُمۡ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\nСизларга Роббимнинг юборганларини етказаман, насиҳат қиламан ва Аллоҳ томонидан сиз билмаган нарсани биламан'}, {'chapter': 7, 'verse': 63, 'text': 'أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡ وَلِتَتَّقُواْ وَلَعَلَّكُمۡ تُرۡحَمُونَ\nЎзингиздан бўлган бир кишига, сизларни огоҳлантириш учун ва тақво қилиб, шояд, раҳматга эришсангиз деб, Роббингиздан эслатма келганидан ажабланасизларми?!» – деди'}, {'chapter': 7, 'verse': 64, 'text': 'فَكَذَّبُوهُ فَأَنجَيۡنَٰهُ وَٱلَّذِينَ مَعَهُۥ فِي ٱلۡفُلۡكِ وَأَغۡرَقۡنَا ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمًا عَمِينَ\nБас, уни ёлғончига чиқардилар. Сўнг, Биз уни ва у билан бирга бўлганларни кемада қутқардик ва оятларимизни ёлғонга чиқарганларни ғарқ қилдик. Чунки, улар кўр қавм эдилар'}, {'chapter': 7, 'verse': 65, 'text': '۞وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ\nВа Одга ўз биродари Ҳудни (юбордик). У: «Эй қавмим, Аллоҳга ибодат қилинглар. Сизларга Ундан ўзга илоҳ йўқ. Тақво қилмайсизларми?!» – деди'}, {'chapter': 7, 'verse': 66, 'text': 'قَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦٓ إِنَّا لَنَرَىٰكَ فِي سَفَاهَةٖ وَإِنَّا لَنَظُنُّكَ مِنَ ٱلۡكَٰذِبِينَ\nУнинг қавмидан куфр келтирган зодагонлар: «Албатта, биз сени эси пастликда кўриб турибмиз ва албатта биз сени ёлғончилардан деб биламиз», – дедилар'}, {'chapter': 7, 'verse': 67, 'text': 'قَالَ يَٰقَوۡمِ لَيۡسَ بِي سَفَاهَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nУ: «Эй қавмим, менда эси пастлик йўқ. Ва лекин мен оламларнинг Роббидан Пайғамбарман'}, {'chapter': 7, 'verse': 68, 'text': 'أُبَلِّغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنَا۠ لَكُمۡ نَاصِحٌ أَمِينٌ\nСизларга Роббимнинг юборганларини етказаман ва мен сизларга ишончли насиҳатгўйман'}, {'chapter': 7, 'verse': 69, 'text': 'أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡۚ وَٱذۡكُرُوٓاْ إِذۡ جَعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ قَوۡمِ نُوحٖ وَزَادَكُمۡ فِي ٱلۡخَلۡقِ بَصۜۡطَةٗۖ فَٱذۡكُرُوٓاْ ءَالَآءَ ٱللَّهِ لَعَلَّكُمۡ تُفۡلِحُونَ\nЎзингиздан бўлган бир кишига сизларни огоҳлантириш учун Роббингиздан эслатма келганидан ажабландингизми?! Сизларни Нуҳ қавмидан кейин халифа қилганини ва хилқатингизда куч-қувватни зиёда қилганини эсланг. Аллоҳнинг неъматларини эсланг. Шоядки, нажот топсангиз», – деди'}, {'chapter': 7, 'verse': 70, 'text': 'قَالُوٓاْ أَجِئۡتَنَا لِنَعۡبُدَ ٱللَّهَ وَحۡدَهُۥ وَنَذَرَ مَا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУлар: «Сен бизга ёлғиз Аллоҳнинг Ўзига ибодат қилишимиз ва ота-боболаримиз ибодат қилган нарсаларни тарк этишимиз учун келдингми?! Агар ростгўйлардан бўлсанг, бизга ваъда қилаётган нарсангни келтир!» – дедилар'}, {'chapter': 7, 'verse': 71, 'text': 'قَالَ قَدۡ وَقَعَ عَلَيۡكُم مِّن رَّبِّكُمۡ رِجۡسٞ وَغَضَبٌۖ أَتُجَٰدِلُونَنِي فِيٓ أَسۡمَآءٖ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّا نَزَّلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٖۚ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ\nУ: «Батаҳқиқ, устингизга Роббингиз томонидан азоб ва ғазаб тушди. Мен билан, Аллоҳ ҳеч бир ҳужжат туширмаган, ўзингиз ва ота боболарингиз қўйиб олган номлар ҳақида тортишасизларми?! Бас, интизор бўлиб туринглар! Мен ҳам сизлар билан интизор бўлувчиларданман», – деди'}, {'chapter': 7, 'verse': 72, 'text': 'فَأَنجَيۡنَٰهُ وَٱلَّذِينَ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَقَطَعۡنَا دَابِرَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ وَمَا كَانُواْ مُؤۡمِنِينَ\nБас, унга ва у билан бирга бўлганларга Ўз раҳматимиз ила нажот бердик ва оятларимизни ёлғонга чиқарганларнинг кетларини уздик. Улар мўмин эмас эдилар'}, {'chapter': 7, 'verse': 73, 'text': 'وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَدۡ جَآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ هَٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٌ أَلِيمٞ\nВа Самудга ўз биродари Солиҳни (юбордик). У: «Эй қавмим, Аллоҳга ибодат қилинглар. Сизларга Ундан ўзга илоҳ йўқ. Сизга Роббингиздан ҳужжат келди. Аллоҳнинг мана бу туяси сизларга белгидир. Уни Аллоҳнинг ерида тек қўйинглар. Унга ёмонлик етказманглар. Яна сизни аламли азоб тутмасин-а'}, {'chapter': 7, 'verse': 74, 'text': 'وَٱذۡكُرُوٓاْ إِذۡ جَعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ عَادٖ وَبَوَّأَكُمۡ فِي ٱلۡأَرۡضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُورٗا وَتَنۡحِتُونَ ٱلۡجِبَالَ بُيُوتٗاۖ فَٱذۡكُرُوٓاْ ءَالَآءَ ٱللَّهِ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nВа сизларни Оддан кейин халифа қилганини ва сизга ер юзида имкон бериб, текисликларида қасрлар қуриб, тоғлардан уйлар ўйганларингизни эсланг. Аллоҳнинг неъматларини эсланг ва ер юзида фасод-ла бузғунчилик қилманг!» – деди'}, {'chapter': 7, 'verse': 75, 'text': 'قَالَ ٱلۡمَلَأُ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لِلَّذِينَ ٱسۡتُضۡعِفُواْ لِمَنۡ ءَامَنَ مِنۡهُمۡ أَتَعۡلَمُونَ أَنَّ صَٰلِحٗا مُّرۡسَلٞ مِّن رَّبِّهِۦۚ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلَ بِهِۦ مُؤۡمِنُونَ\nУнинг қавмидан мутакаббир бўлган зодагонлар заифҳол бўлганлардан иймон келтирганларига: «Солиҳни Роббидан Пайғамбар қилиб юборилган, деб билмоқдамисиз?!» – дедилар. Улар: «Албатта, биз у ила юборилган нарсага иймон келтирувчилармиз», – дедилар'}, {'chapter': 7, 'verse': 76, 'text': 'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا بِٱلَّذِيٓ ءَامَنتُم بِهِۦ كَٰفِرُونَ\nМутакаббир бўлганлар: «Албатта, биз сиз иймон келтирганга кофирмиз», – дедилар'}, {'chapter': 7, 'verse': 77, 'text': 'فَعَقَرُواْ ٱلنَّاقَةَ وَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ وَقَالُواْ يَٰصَٰلِحُ ٱئۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلۡمُرۡسَلِينَ\nБас, туяни сўйдилар, Роббилари амрида тажовузкорлик қилишди ва: «Эй Солиҳ, агар ростгўйлардан бўлсанг, бизга ваъда қилаётган нарсангни келтир!» – дедилар'}, {'chapter': 7, 'verse': 78, 'text': 'فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ\nБас, уларни шиддатли зилзила олди ва жойларида ўтириб қолдилар'}, {'chapter': 7, 'verse': 79, 'text': 'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَالَةَ رَبِّي وَنَصَحۡتُ لَكُمۡ وَلَٰكِن لَّا تُحِبُّونَ ٱلنَّـٰصِحِينَ\nКейин, улардан юз ўгирди ва: «Эй қавмим, батаҳқиқ, мен сизга Роббимнинг юборганини етказдим ва насиҳат қилдим. Лекин сиз насиҳат қилувчиларни севмас экансиз», – деди'}, {'chapter': 7, 'verse': 80, 'text': 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ\nВа Лутни (юбордик). Унинг ўз қавмига: «Сиздан олдин оламларда ҳеч ким қилмаган фоҳиша ишни қиласизларми'}, {'chapter': 7, 'verse': 81, 'text': 'إِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ\nАлбатта, сизлар аёлларни қўйиб, эркакларга шаҳват ила яқинлик қилмоқдасизлар. Йўқ! Сизлар исрофчи қавмсиз!» – деганини эсла'}, {'chapter': 7, 'verse': 82, 'text': 'وَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوهُم مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ\nҚавмнинг жавоби: «Уларни қишлоғингиздан чиқариб юборинг, чунки улар жуда ҳам покиза бўлаверадиган одамлар экан», дейишларидан бошқа нарса бўлмади'}, {'chapter': 7, 'verse': 83, 'text': 'فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ\nБас, унга ва аҳлига нажот бердик. Магар хотини ҳалок бўлгувчилардан бўлди'}, {'chapter': 7, 'verse': 84, 'text': 'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ\nВа Биз уларнинг устидан «ёмғир» ёғдирдик. Жинояткорларнинг оқибати қандай бўлганига назар сол'}, {'chapter': 7, 'verse': 85, 'text': 'وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَدۡ جَآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ فَأَوۡفُواْ ٱلۡكَيۡلَ وَٱلۡمِيزَانَ وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nВа Мадянга ўз биродарлари Шуъайбни (юбордик). У: «Эй қавмим, Аллоҳга ибодат қилинг. Сизга Ундан ўзга илоҳ йўқ. Батаҳқиқ, сизга Роббингиздан ҳужжат келди. Бас, ўлчов ва тарозуни тўлиқ адо этинг, одамларнинг нарсаларидан камайтириб қолманг ва ер юзида унинг ислоҳидан кейин бузғунчилик қилманг. Агар мўмин бўлсангиз, шундай қилмоғингиз ўзингиз учун яхшидир'}, {'chapter': 7, 'verse': 86, 'text': 'وَلَا تَقۡعُدُواْ بِكُلِّ صِرَٰطٖ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِيلِ ٱللَّهِ مَنۡ ءَامَنَ بِهِۦ وَتَبۡغُونَهَا عِوَجٗاۚ وَٱذۡكُرُوٓاْ إِذۡ كُنتُمۡ قَلِيلٗا فَكَثَّرَكُمۡۖ وَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ\nҲар кўчада ўтириб олиб қўрқитманг. Аллоҳнинг йўлидан Унга иймон келтирганларни тўсманг ва у(йўл)нинг эгри бўлишини истаманг. Оз бўлган чоғингизда сизни кўпайтириб қўйганини эсланг. Бузғунчиларнинг оқибати қандай бўлганига назар солинг'}, {'chapter': 7, 'verse': 87, 'text': 'وَإِن كَانَ طَآئِفَةٞ مِّنكُمۡ ءَامَنُواْ بِٱلَّذِيٓ أُرۡسِلۡتُ بِهِۦ وَطَآئِفَةٞ لَّمۡ يُؤۡمِنُواْ فَٱصۡبِرُواْ حَتَّىٰ يَحۡكُمَ ٱللَّهُ بَيۡنَنَاۚ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ\nАгар сиздан бир тоифа мен ила юборилган нарсага иймон келтирган ва бошқа тоифа иймон келтирмаган бўлса, бас, Аллоҳ орамизда ҳукм қилгунча сабр қилинглар. У ҳукм қилувчиларнинг яхшисидир», – деди'}, {'chapter': 7, 'verse': 88, 'text': '۞قَالَ ٱلۡمَلَأُ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لَنُخۡرِجَنَّكَ يَٰشُعَيۡبُ وَٱلَّذِينَ ءَامَنُواْ مَعَكَ مِن قَرۡيَتِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۚ قَالَ أَوَلَوۡ كُنَّا كَٰرِهِينَ\nУнинг қавмидан мутакаббир бўлган зодагонлар: «Эй Шуъайб, ёки, албатта, сени ва сен билан бирга иймон келтирганларни ўз қишлоғимиздан чиқарамиз ёки ўз миллатимизга қайтасизлар», – дедилар. У: «Гарчи ёмон кўрувчи бўлсак ҳам-а'}, {'chapter': 7, 'verse': 89, 'text': 'قَدِ ٱفۡتَرَيۡنَا عَلَى ٱللَّهِ كَذِبًا إِنۡ عُدۡنَا فِي مِلَّتِكُم بَعۡدَ إِذۡ نَجَّىٰنَا ٱللَّهُ مِنۡهَاۚ وَمَا يَكُونُ لَنَآ أَن نَّعُودَ فِيهَآ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّنَاۚ وَسِعَ رَبُّنَا كُلَّ شَيۡءٍ عِلۡمًاۚ عَلَى ٱللَّهِ تَوَكَّلۡنَاۚ رَبَّنَا ٱفۡتَحۡ بَيۡنَنَا وَبَيۡنَ قَوۡمِنَا بِٱلۡحَقِّ وَأَنتَ خَيۡرُ ٱلۡفَٰتِحِينَ\nАгар сизнинг миллатингизга Аллоҳ бизга ундан нажот бергандан сўнг қайтсак, батаҳқиқ, Аллоҳга нисбатан ёлғон тўқиган бўламиз. Биз учун унга қайтиш мутлақо мумкин эмас, магар Роббимиз — Аллоҳ хоҳласагина (мумкин). Роббимиз ҳамма нарсани ўз илми ила қамраб олгандир. Аллоҳга таваккал қилдик. Эй Роббимиз, биз билан қавмимизнинг орамизни ҳақ ила очгин. Сен очувчиларнинг яхшисидирсан», – деди'}, {'chapter': 7, 'verse': 90, 'text': 'وَقَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ لَئِنِ ٱتَّبَعۡتُمۡ شُعَيۡبًا إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ\nУнинг қавмидан бўлган, куфр келтирган зодагонлар: «Агар Шуъайбга эргашсангиз, у ҳолда, албатта, зиён кўрувчидирсиз», – дедилар'}, {'chapter': 7, 'verse': 91, 'text': 'فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ\nБас, уларни шиддатли зилзила олди ва жойларида ўтириб қолдилар'}, {'chapter': 7, 'verse': 92, 'text': 'ٱلَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَاۚ ٱلَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَانُواْ هُمُ ٱلۡخَٰسِرِينَ\nШуъайбни ёлғончига чиқарганлар, худди у ерда яшамагандек бўлдилар. Шуъайбни ёлғончига чиқарганлар, ўшалар зиён кўрувчилар бўлдилар'}, {'chapter': 7, 'verse': 93, 'text': 'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَٰلَٰتِ رَبِّي وَنَصَحۡتُ لَكُمۡۖ فَكَيۡفَ ءَاسَىٰ عَلَىٰ قَوۡمٖ كَٰفِرِينَ\nКейин улардан юз ўгирди ва «Эй қавмим, батаҳқиқ, сизга Роббимнинг юборганларини етказган эдим ҳамда сизга насиҳат қилган эдим. Қандай қилиб кофир қавмларга қайғурай», – деди'}, {'chapter': 7, 'verse': 94, 'text': 'وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّبِيٍّ إِلَّآ أَخَذۡنَآ أَهۡلَهَا بِٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ لَعَلَّهُمۡ يَضَّرَّعُونَ\nҚайси бир қишлоққа Пайғамбар юборсак, албатта, унинг аҳлини тазарруъ қилишлари учун зарарлар ва мусийбатлар ила тутганмиз'}, {'chapter': 7, 'verse': 95, 'text': 'ثُمَّ بَدَّلۡنَا مَكَانَ ٱلسَّيِّئَةِ ٱلۡحَسَنَةَ حَتَّىٰ عَفَواْ وَّقَالُواْ قَدۡ مَسَّ ءَابَآءَنَا ٱلضَّرَّآءُ وَٱلسَّرَّآءُ فَأَخَذۡنَٰهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nСўнгра ёмонликни яхшиликка алмаштирганмиз. Токи, кўпайишиб ва «Ота-боболаримизга ҳам қийинчилик ва хурсандчилик етган эди», деганларида, уларни ўзларига сездирмай, тўсатдан тутганмиз'}, {'chapter': 7, 'verse': 96, 'text': 'وَلَوۡ أَنَّ أَهۡلَ ٱلۡقُرَىٰٓ ءَامَنُواْ وَٱتَّقَوۡاْ لَفَتَحۡنَا عَلَيۡهِم بَرَكَٰتٖ مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ وَلَٰكِن كَذَّبُواْ فَأَخَذۡنَٰهُم بِمَا كَانُواْ يَكۡسِبُونَ\nАгар қишлоқлар аҳллари иймон келтирганларида ва тақво қилганларида эди, Биз, албатта, уларга осмону ердан баракотларни очиб қўяр эдик. Лекин улар ёлғонга чиқардилар, бас, уларни қилган касблари туфайли тутдик'}, {'chapter': 7, 'verse': 97, 'text': 'أَفَأَمِنَ أَهۡلُ ٱلۡقُرَىٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا بَيَٰتٗا وَهُمۡ نَآئِمُونَ\nҚишлоқлар аҳли уларга Бизнинг бало-қазойимиз тунда, ухлаётган ҳолларида келишидан хотиржаммилар'}, {'chapter': 7, 'verse': 98, 'text': 'أَوَأَمِنَ أَهۡلُ ٱلۡقُرَىٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا ضُحٗى وَهُمۡ يَلۡعَبُونَ\nҚишлоқлар аҳли уларга Бизнинг бало-қазойимиз чоштгоҳда, ўйнаётган ҳолларида келишидан хотиржаммилар'}, {'chapter': 7, 'verse': 99, 'text': 'أَفَأَمِنُواْ مَكۡرَ ٱللَّهِۚ فَلَا يَأۡمَنُ مَكۡرَ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡخَٰسِرُونَ\nАллоҳнинг макридан хотиржам бўлдиларми?! Аллоҳнинг макридан фақат зиён кўрувчи қавмларгина хотиржам бўлурлар'}, {'chapter': 7, 'verse': 100, 'text': 'أَوَلَمۡ يَهۡدِ لِلَّذِينَ يَرِثُونَ ٱلۡأَرۡضَ مِنۢ بَعۡدِ أَهۡلِهَآ أَن لَّوۡ نَشَآءُ أَصَبۡنَٰهُم بِذُنُوبِهِمۡۚ وَنَطۡبَعُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَسۡمَعُونَ\nЕрга унинг аҳлидан кейин меросхўр бўлганларга, агар хоҳласак, гуноҳлари туфайли уларга мусийбат етказишимиз ва қалбларига муҳр босиб, эшитмайдиган қилиб қўйишимиз аён бўлмадими'}, {'chapter': 7, 'verse': 101, 'text': 'تِلۡكَ ٱلۡقُرَىٰ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآئِهَاۚ وَلَقَدۡ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ مِن قَبۡلُۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلۡكَٰفِرِينَ\nБиз сенга ана ўша қишлоқларнинг хабарларини қисқа қилиб айтиб бермоқдамиз. Батаҳқиқ, уларга Пайғамбарлари очиқ-ойдин ҳужжатлар билан келдилар. Аммо, улар олдин ёлғонга чиқарган нарсаларига иймон келтирмадилар. Аллоҳ шундай қилиб кофирлар қалбига муҳр босди'}, {'chapter': 7, 'verse': 102, 'text': 'وَمَا وَجَدۡنَا لِأَكۡثَرِهِم مِّنۡ عَهۡدٖۖ وَإِن وَجَدۡنَآ أَكۡثَرَهُمۡ لَفَٰسِقِينَ\nУларнинг кўпларида аҳдга вафо кўрмадик. Агар уларнинг кўпларини топсак ҳам, фосиқликда топдик'}, {'chapter': 7, 'verse': 103, 'text': 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَظَلَمُواْ بِهَاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ\nСўнгра, уларнинг ортидан Мусони Ўз оятларимиз ила Фиръавн ва унинг зодагонларига юбордик. Бас, у(оят)ларга зулм қилдилар. Бузғунчиларнинг оқибати қандай бўлишига назар сол'}, {'chapter': 7, 'verse': 104, 'text': 'وَقَالَ مُوسَىٰ يَٰفِرۡعَوۡنُ إِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nМусо: «Эй Фиръавн, албатта, мен оламларнинг Роббидан Пайғамбарман'}, {'chapter': 7, 'verse': 105, 'text': 'حَقِيقٌ عَلَىٰٓ أَن لَّآ أَقُولَ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ قَدۡ جِئۡتُكُم بِبَيِّنَةٖ مِّن رَّبِّكُمۡ فَأَرۡسِلۡ مَعِيَ بَنِيٓ إِسۡرَـٰٓءِيلَ\nМенга Аллоҳ ҳақида ҳақдан бошқани айтмаслик лозимдир. Батаҳқиқ, мен сизларга Роббингиздан очиқ-ойдин ҳужжат келтирдим. Мен ила Бани Исроилни қўйиб юбор», – деди'}, {'chapter': 7, 'verse': 106, 'text': 'قَالَ إِن كُنتَ جِئۡتَ بِـَٔايَةٖ فَأۡتِ بِهَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУ: «Агар мўъжиза келтирган бўлсанг, ростгўйлардан бўлсанг, келтир уни», – деди'}, {'chapter': 7, 'verse': 107, 'text': 'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ\nАсосини ташлаган эди... баногоҳ у аниқ аждар бўлди'}, {'chapter': 7, 'verse': 108, 'text': 'وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّـٰظِرِينَ\nВа қўлини чиқарган эди... баногоҳ у назар солувчиларга оппоқ бўлиб кўринди'}, {'chapter': 7, 'verse': 109, 'text': 'قَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ\nФиръавн қавмидан бўлган зодагонлар: «Албатта, бу билимдон сеҳргардир'}, {'chapter': 7, 'verse': 110, 'text': 'يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُمۡۖ فَمَاذَا تَأۡمُرُونَ\nСизларни ерингиздан чиқаришни хоҳлайди. Бас, нимага амр қиласизлар?» – дедилар'}, {'chapter': 7, 'verse': 111, 'text': 'قَالُوٓاْ أَرۡجِهۡ وَأَخَاهُ وَأَرۡسِلۡ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ\nУлар: «Уни ва акасини қўйиб тургин-да, шаҳарларга тўпловчиларни юбор'}, {'chapter': 7, 'verse': 112, 'text': 'يَأۡتُوكَ بِكُلِّ سَٰحِرٍ عَلِيمٖ\nҲамма билимдон сеҳргарларни сенга олиб келсинлар», – дедилар'}, {'chapter': 7, 'verse': 113, 'text': 'وَجَآءَ ٱلسَّحَرَةُ فِرۡعَوۡنَ قَالُوٓاْ إِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَٰلِبِينَ\nСеҳргарлар Фиръавнга келиб: «Агар биз ғолиб бўлсак, албатта, мукофот олсак керак?» – дедилар'}, {'chapter': 7, 'verse': 114, 'text': 'قَالَ نَعَمۡ وَإِنَّكُمۡ لَمِنَ ٱلۡمُقَرَّبِينَ\nУ: «Ҳа. Ва, албатта, менинг яқин кишиларимдан бўлурсизлар»'}, {'chapter': 7, 'verse': 115, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ نَحۡنُ ٱلۡمُلۡقِينَ\nУлар: «Эй Мусо, ёки сен ташла, ёки биз ташловчи бўлайлик», – дедилар'}, {'chapter': 7, 'verse': 116, 'text': 'قَالَ أَلۡقُواْۖ فَلَمَّآ أَلۡقَوۡاْ سَحَرُوٓاْ أَعۡيُنَ ٱلنَّاسِ وَٱسۡتَرۡهَبُوهُمۡ وَجَآءُو بِسِحۡرٍ عَظِيمٖ\nУ: «Сизлар ташланглар», – деди. Улар ташлаганларида, одамларнинг кўзини сеҳрладилар, қўрқитдилар ва зўр сеҳр кўрсатдилар'}, {'chapter': 7, 'verse': 117, 'text': '۞وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَلۡقِ عَصَاكَۖ فَإِذَا هِيَ تَلۡقَفُ مَا يَأۡفِكُونَ\nБиз Мусога: «Асоингни ташлагин», деб ваҳий юбордик. Бирдан, у улар уйдираётган нарсани юта бошлади'}, {'chapter': 7, 'verse': 118, 'text': 'فَوَقَعَ ٱلۡحَقُّ وَبَطَلَ مَا كَانُواْ يَعۡمَلُونَ\nБас, ҳақ қарор топди ва уларнинг қилаётганлари ботил бўлди'}, {'chapter': 7, 'verse': 119, 'text': 'فَغُلِبُواْ هُنَالِكَ وَٱنقَلَبُواْ صَٰغِرِينَ\nЎша ерда енгилдилар ва беобрў бўлдилар'}, {'chapter': 7, 'verse': 120, 'text': 'وَأُلۡقِيَ ٱلسَّحَرَةُ سَٰجِدِينَ\nВа сеҳргарлар саждага ташланиб'}, {'chapter': 7, 'verse': 121, 'text': 'قَالُوٓاْ ءَامَنَّا بِرَبِّ ٱلۡعَٰلَمِينَ\n«Оламларнинг Роббига иймон келтирдик'}, {'chapter': 7, 'verse': 122, 'text': 'رَبِّ مُوسَىٰ وَهَٰرُونَ\nМусо ва Ҳоруннинг Роббига», – дедилар'}, {'chapter': 7, 'verse': 123, 'text': 'قَالَ فِرۡعَوۡنُ ءَامَنتُم بِهِۦ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّ هَٰذَا لَمَكۡرٞ مَّكَرۡتُمُوهُ فِي ٱلۡمَدِينَةِ لِتُخۡرِجُواْ مِنۡهَآ أَهۡلَهَاۖ فَسَوۡفَ تَعۡلَمُونَ\nФиръавн: «Мен сизга изн бермай туриб унга иймон келтирдингизми?! Албатта, бу сизнинг шаҳардан унинг аҳлини чиқариш учун қилган макрингиздир. Ҳали биласиз'}, {'chapter': 7, 'verse': 124, 'text': 'لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ ثُمَّ لَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ\nАлбатта, оёқ-қўлларингизни қарама-қарши томонидан кесаман ва ҳаммангизни осаман», – деди'}, {'chapter': 7, 'verse': 125, 'text': 'قَالُوٓاْ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ\nУлар: «Албатта, биз Роббимизга қайтгувчимиз'}, {'chapter': 7, 'verse': 126, 'text': 'وَمَا تَنقِمُ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِـَٔايَٰتِ رَبِّنَا لَمَّا جَآءَتۡنَاۚ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَتَوَفَّنَا مُسۡلِمِينَ\nСен биздан фақат Роббимизнинг оятлари келганда уларга иймон келтирганимиз учунгина ўч олмоқдасан! Эй Роббимиз, устимиздан сабр тўккин ва мусулмон ҳолимизда вафот эттиргин», – дедилар'}, {'chapter': 7, 'verse': 127, 'text': 'وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ أَتَذَرُ مُوسَىٰ وَقَوۡمَهُۥ لِيُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَيَذَرَكَ وَءَالِهَتَكَۚ قَالَ سَنُقَتِّلُ أَبۡنَآءَهُمۡ وَنَسۡتَحۡيِۦ نِسَآءَهُمۡ وَإِنَّا فَوۡقَهُمۡ قَٰهِرُونَ\nФиръавн қавмидан бўлган зодагонлар: «Сени ва худоларингни тарк қилса ҳам, Мусо ва унинг қавмини ер юзида бузғунчилик қилишларига йўл қўйиб берасанми?» – дедилар. У: «Ўғилларини ўлдирурмиз ва аёлларини тирик қолдирурмиз ва, албатта, биз улар устидан қаҳр ўтказувчимиз», – деди'}, {'chapter': 7, 'verse': 128, 'text': 'قَالَ مُوسَىٰ لِقَوۡمِهِ ٱسۡتَعِينُواْ بِٱللَّهِ وَٱصۡبِرُوٓاْۖ إِنَّ ٱلۡأَرۡضَ لِلَّهِ يُورِثُهَا مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ\nМусо ўз қавмига: «Аллоҳдан ёрдам сўранглар ва сабр қилинглар. Ер Аллоҳники, уни бандаларидан кимни хоҳласа, ўшанга мерос қилиб берадир. Ва оқибат тақводорларникидир», – деди'}, {'chapter': 7, 'verse': 129, 'text': 'قَالُوٓاْ أُوذِينَا مِن قَبۡلِ أَن تَأۡتِيَنَا وَمِنۢ بَعۡدِ مَا جِئۡتَنَاۚ قَالَ عَسَىٰ رَبُّكُمۡ أَن يُهۡلِكَ عَدُوَّكُمۡ وَيَسۡتَخۡلِفَكُمۡ فِي ٱلۡأَرۡضِ فَيَنظُرَ كَيۡفَ تَعۡمَلُونَ\nУлар: «Сен бизга келмасингдан олдин ҳам, келганингдан кейин ҳам озор тортдик», – дедилар. У: «Шоядки, Роббингиз душманларингизни ҳалок этиб, сизларни ер юзига халифа қилиб, қандай амал қилишингизга назар солса», – деди'}, {'chapter': 7, 'verse': 130, 'text': 'وَلَقَدۡ أَخَذۡنَآ ءَالَ فِرۡعَوۡنَ بِٱلسِّنِينَ وَنَقۡصٖ مِّنَ ٱلثَّمَرَٰتِ لَعَلَّهُمۡ يَذَّكَّرُونَ\nБиз Фиръавн аҳлини, шояд эсласалар деб, (қаҳатчилик) йиллар ва мевалар танқислиги ила тутдик'}, {'chapter': 7, 'verse': 131, 'text': 'فَإِذَا جَآءَتۡهُمُ ٱلۡحَسَنَةُ قَالُواْ لَنَا هَٰذِهِۦۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَطَّيَّرُواْ بِمُوسَىٰ وَمَن مَّعَهُۥٓۗ أَلَآ إِنَّمَا طَـٰٓئِرُهُمۡ عِندَ ٱللَّهِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nУлар ўзларига яхшилик етганда: «Бунга биз ҳақдормиз», – дедилар. Агар уларга ёмонлик етса, Мусо ва у билан бирга бўлганлардан шумландилар. Огоҳ бўлсинларким, уларнинг шумликлари Аллоҳнинг ҳузуридан бошқа жойда эмас. Лекин кўплари билмаслар'}, {'chapter': 7, 'verse': 132, 'text': 'وَقَالُواْ مَهۡمَا تَأۡتِنَا بِهِۦ مِنۡ ءَايَةٖ لِّتَسۡحَرَنَا بِهَا فَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ\nУлар: «Ҳар қанча оят келтириб бизни сеҳрламоқчи бўлсанг ҳам, биз сенга иймон келтиргувчи эмасмиз», – дедилар'}, {'chapter': 7, 'verse': 133, 'text': 'فَأَرۡسَلۡنَا عَلَيۡهِمُ ٱلطُّوفَانَ وَٱلۡجَرَادَ وَٱلۡقُمَّلَ وَٱلضَّفَادِعَ وَٱلدَّمَ ءَايَٰتٖ مُّفَصَّلَٰتٖ فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ\nБас, уларнинг устидан тўфон, чигиртка, мита, бақа ва қонларни очиқ-ойдин ва муфассал белгилар қилиб юбордик. Бас, улар мутакаббирлик қилдилар ва жиноятчи қавм бўлдилар'}, {'chapter': 7, 'verse': 134, 'text': 'وَلَمَّا وَقَعَ عَلَيۡهِمُ ٱلرِّجۡزُ قَالُواْ يَٰمُوسَى ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَۖ لَئِن كَشَفۡتَ عَنَّا ٱلرِّجۡزَ لَنُؤۡمِنَنَّ لَكَ وَلَنُرۡسِلَنَّ مَعَكَ بَنِيٓ إِسۡرَـٰٓءِيلَ\nҚачонки устиларига азоб тушса: «Эй Мусо, сенга берган аҳд ҳурмати, Роббингга дуо қил, агар биздан азобни кўтарсанг, албатта, сенга иймон келтирурмиз ва Бани Исроилни сен билан қўйиб юборурмиз», – дер эдилар'}, {'chapter': 7, 'verse': 135, 'text': 'فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلرِّجۡزَ إِلَىٰٓ أَجَلٍ هُم بَٰلِغُوهُ إِذَا هُمۡ يَنكُثُونَ\nҚачонки устиларидаги азобни ўзлари етиб борадиган муддатга кўтарсак, қарабсанки, қасамларини бузиб турибдилар'}, {'chapter': 7, 'verse': 136, 'text': 'فَٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ فِي ٱلۡيَمِّ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ\nБас, улардан интиқом олдик. Оятларимизни ёлғонга чиқарганлари ва улардан ғофил бўлганлари учун уларни денгизга ғарқ қилдик'}, {'chapter': 7, 'verse': 137, 'text': 'وَأَوۡرَثۡنَا ٱلۡقَوۡمَ ٱلَّذِينَ كَانُواْ يُسۡتَضۡعَفُونَ مَشَٰرِقَ ٱلۡأَرۡضِ وَمَغَٰرِبَهَا ٱلَّتِي بَٰرَكۡنَا فِيهَاۖ وَتَمَّتۡ كَلِمَتُ رَبِّكَ ٱلۡحُسۡنَىٰ عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ بِمَا صَبَرُواْۖ وَدَمَّرۡنَا مَا كَانَ يَصۡنَعُ فِرۡعَوۡنُ وَقَوۡمُهُۥ وَمَا كَانُواْ يَعۡرِشُونَ\nВа хўрланиб юрган қавмни эса, ўзимиз баракали қилган ернинг машриқию мағрибига меросхўр қилдик. Сабр қилганлари учун Роббингнинг гўзал сўзлари Бани Исроилга батамом бўлди. Фиръавн ва унинг қавми қурган нарсаларни ва улар юксалтирган нарсаларни вайрон қилдик'}, {'chapter': 7, 'verse': 138, 'text': 'وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡبَحۡرَ فَأَتَوۡاْ عَلَىٰ قَوۡمٖ يَعۡكُفُونَ عَلَىٰٓ أَصۡنَامٖ لَّهُمۡۚ قَالُواْ يَٰمُوسَى ٱجۡعَل لَّنَآ إِلَٰهٗا كَمَا لَهُمۡ ءَالِهَةٞۚ قَالَ إِنَّكُمۡ قَوۡمٞ تَجۡهَلُونَ\nВа Бани Исроилни денгиздан олиб ўтдик. Бас, улар ўз санамларига берилиб сиғинаётган қавм устига келдилар. Улар:» Эй Мусо, бизга ҳам худди уларнинг худоларига ўхшаш худо қилиб бер», – дедилар. У: «Албатта, сиз жоҳил қавм бўлаётибсиз'}, {'chapter': 7, 'verse': 139, 'text': 'إِنَّ هَـٰٓؤُلَآءِ مُتَبَّرٞ مَّا هُمۡ فِيهِ وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ\nАхир, анавилар ичида турган нарса ҳалокатдир ва қилаётган ишлари ботилдир», – деди'}, {'chapter': 7, 'verse': 140, 'text': 'قَالَ أَغَيۡرَ ٱللَّهِ أَبۡغِيكُمۡ إِلَٰهٗا وَهُوَ فَضَّلَكُمۡ عَلَى ٱلۡعَٰلَمِينَ\n«Сизларга Аллоҳдан ўзгани худо қилиб берайми?! Ҳолбуки, У сизларни оламлардан афзал қилган зот-ку!» – деди'}, {'chapter': 7, 'verse': 141, 'text': 'وَإِذۡ أَنجَيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ يُقَتِّلُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ\nСизларни ёмон азобларга қўяётган, ўғилларингизни ўлдириб, қизларингизни тирик қолдираётган Фиръавн аҳлидан қутқарганимизни эсланг! Ва бу ишда Роббингиздан улуғ синов бордир'}, {'chapter': 7, 'verse': 142, 'text': '۞وَوَٰعَدۡنَا مُوسَىٰ ثَلَٰثِينَ لَيۡلَةٗ وَأَتۡمَمۡنَٰهَا بِعَشۡرٖ فَتَمَّ مِيقَٰتُ رَبِّهِۦٓ أَرۡبَعِينَ لَيۡلَةٗۚ وَقَالَ مُوسَىٰ لِأَخِيهِ هَٰرُونَ ٱخۡلُفۡنِي فِي قَوۡمِي وَأَصۡلِحۡ وَلَا تَتَّبِعۡ سَبِيلَ ٱلۡمُفۡسِدِينَ\nВа Мусо билан ўттиз кечага ваъдалашдик. Кейин эса, уни ўн (кеча) билан тугал қилдик. Бас, Роббининг белгилаган вақти тўлиқ қирқ кеча бўлди. Мусо акаси Ҳорунга: «Қавмимда менинг ўрнимни ол, яхши иш қил, бузғунчиларнинг йўлига эргашма», – деди'}, {'chapter': 7, 'verse': 143, 'text': 'وَلَمَّا جَآءَ مُوسَىٰ لِمِيقَٰتِنَا وَكَلَّمَهُۥ رَبُّهُۥ قَالَ رَبِّ أَرِنِيٓ أَنظُرۡ إِلَيۡكَۚ قَالَ لَن تَرَىٰنِي وَلَٰكِنِ ٱنظُرۡ إِلَى ٱلۡجَبَلِ فَإِنِ ٱسۡتَقَرَّ مَكَانَهُۥ فَسَوۡفَ تَرَىٰنِيۚ فَلَمَّا تَجَلَّىٰ رَبُّهُۥ لِلۡجَبَلِ جَعَلَهُۥ دَكّٗا وَخَرَّ مُوسَىٰ صَعِقٗاۚ فَلَمَّآ أَفَاقَ قَالَ سُبۡحَٰنَكَ تُبۡتُ إِلَيۡكَ وَأَنَا۠ أَوَّلُ ٱلۡمُؤۡمِنِينَ\nМусо мийқотимизга келиб, Робби унга гапирганида, У: «Роббим, менга (жамолингни) кўрсатгин, Сенга назар солай», – деди. У зот: «Мени ҳеч кўра олмассан. Лекин тоққа назар сол, агар у маконида собит тура олса, бас, Мени кўрасан», – деди. Робби тоққа тажаллий қилганида, уни титилган ҳолга келтирди ва Мусо ҳушидан кетиб йиқилди. Ҳушига келгач: «Сен поксан, Сенга тавба қилдим. Мен мўминларнинг аввалгисиман», – деди'}, {'chapter': 7, 'verse': 144, 'text': 'قَالَ يَٰمُوسَىٰٓ إِنِّي ٱصۡطَفَيۡتُكَ عَلَى ٱلنَّاسِ بِرِسَٰلَٰتِي وَبِكَلَٰمِي فَخُذۡ مَآ ءَاتَيۡتُكَ وَكُن مِّنَ ٱلشَّـٰكِرِينَ\n«Эй Мусо, мен сени одамлар ичидан Пайғамбарлигим ва гаплашишим ила танлаб олдим. Бас, сенга берган нарсамни ол ва шукр қилувчилардан бўл!» – деди'}, {'chapter': 7, 'verse': 145, 'text': 'وَكَتَبۡنَا لَهُۥ فِي ٱلۡأَلۡوَاحِ مِن كُلِّ شَيۡءٖ مَّوۡعِظَةٗ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ فَخُذۡهَا بِقُوَّةٖ وَأۡمُرۡ قَوۡمَكَ يَأۡخُذُواْ بِأَحۡسَنِهَاۚ سَأُوْرِيكُمۡ دَارَ ٱلۡفَٰسِقِينَ\nВа унга лавҳларда ҳамма нарсадан мавъиза ва ҳар бир нарсанинг тафсилотини ёзиб бердик. «Бас, уларни қувват ила тут ва аҳлингга буюр, уларнинг энг яхшиларини олсинлар. Энди, сизларга фосиқларнинг диёрини кўрсатаман», – дедик'}, {'chapter': 7, 'verse': 146, 'text': 'سَأَصۡرِفُ عَنۡ ءَايَٰتِيَ ٱلَّذِينَ يَتَكَبَّرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَا وَإِن يَرَوۡاْ سَبِيلَ ٱلرُّشۡدِ لَا يَتَّخِذُوهُ سَبِيلٗا وَإِن يَرَوۡاْ سَبِيلَ ٱلۡغَيِّ يَتَّخِذُوهُ سَبِيلٗاۚ ذَٰلِكَ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ\nЭнди, ер юзида ноҳақдан мутакаббирлик қилаётганларни, барча оятларни кўрсалар ҳам, уларга иймон келтирмаётганларни, тўғрилик йўлини кўрсалар ҳам, уни ўзларига йўл тутмаётганларни, агар адашув йўлини кўрсалар, уни ўзларига йўл тутаётганларни Ўз оятларимдан бураман. Бундай бўлиши уларнинг оятларимизни ёлғонга чиқаришлари ва улардан ғофил бўлишлари сабабидандир'}, {'chapter': 7, 'verse': 147, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآءِ ٱلۡأٓخِرَةِ حَبِطَتۡ أَعۡمَٰلُهُمۡۚ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ\nОятларимизни ва охират мулоқотини ёлғонга чиқарганлар амаллари беҳуда кетганлардир. Улар фақат қилиб юрган амалларининг жазосини олурлар'}, {'chapter': 7, 'verse': 148, 'text': 'وَٱتَّخَذَ قَوۡمُ مُوسَىٰ مِنۢ بَعۡدِهِۦ مِنۡ حُلِيِّهِمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٌۚ أَلَمۡ يَرَوۡاْ أَنَّهُۥ لَا يُكَلِّمُهُمۡ وَلَا يَهۡدِيهِمۡ سَبِيلًاۘ ٱتَّخَذُوهُ وَكَانُواْ ظَٰلِمِينَ\nМусонинг қавми ундан кейин тақинчоқларидан (бўлган), бўкирадиган бузоқ жасадини (худо) тутдилар. Унинг уларга гапирмаслигини ва уларни ҳидоят қилмаслигини билмадиларми?! Уни (худо) тутдилар ва зулм қилувчилардан бўлдилар'}, {'chapter': 7, 'verse': 149, 'text': 'وَلَمَّا سُقِطَ فِيٓ أَيۡدِيهِمۡ وَرَأَوۡاْ أَنَّهُمۡ قَدۡ ضَلُّواْ قَالُواْ لَئِن لَّمۡ يَرۡحَمۡنَا رَبُّنَا وَيَغۡفِرۡ لَنَا لَنَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ\nҚўллари тушган пайтда ва ўзларининг адашганларини билганларида: «Агар Роббимиз бизга раҳим қилмаса ва мағфират этмаса, албатта, зиёнкорлардан бўламиз», – дедилар'}, {'chapter': 7, 'verse': 150, 'text': 'وَلَمَّا رَجَعَ مُوسَىٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗا قَالَ بِئۡسَمَا خَلَفۡتُمُونِي مِنۢ بَعۡدِيٓۖ أَعَجِلۡتُمۡ أَمۡرَ رَبِّكُمۡۖ وَأَلۡقَى ٱلۡأَلۡوَاحَ وَأَخَذَ بِرَأۡسِ أَخِيهِ يَجُرُّهُۥٓ إِلَيۡهِۚ قَالَ ٱبۡنَ أُمَّ إِنَّ ٱلۡقَوۡمَ ٱسۡتَضۡعَفُونِي وَكَادُواْ يَقۡتُلُونَنِي فَلَا تُشۡمِتۡ بِيَ ٱلۡأَعۡدَآءَ وَلَا تَجۡعَلۡنِي مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nҚачонки Мусо ўз қавмига ғазабланган ва афсус қилган ҳолида қайтганида: «Менинг кетимдан қандай ҳам ёмон халифа бўлдингиз. Роббингизнинг амридан (олдин) шошилдингизми?!» – деди ва лавҳларни ташлаб, акасининг бошидан тутиб, ўзига торта бошлади. У (Ҳорун): «Эй онамнинг боласи, шубҳасиз, бу қавм мени хўрлади ва ўлдириб қўйишларига сал қолди. Энди сен душманларни устимдан кулдирмагин ва мени ҳам золим қавмлардан деб билмагин», – деди'}, {'chapter': 7, 'verse': 151, 'text': 'قَالَ رَبِّ ٱغۡفِرۡ لِي وَلِأَخِي وَأَدۡخِلۡنَا فِي رَحۡمَتِكَۖ وَأَنتَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\nУ: «Роббим, мени ва акамни мағфират қилгин ва бизларни Ўз раҳматингга киритгин. Сенинг Ўзинг раҳим қилгувчиларнинг раҳимлироғисан», деди'}, {'chapter': 7, 'verse': 152, 'text': 'إِنَّ ٱلَّذِينَ ٱتَّخَذُواْ ٱلۡعِجۡلَ سَيَنَالُهُمۡ غَضَبٞ مِّن رَّبِّهِمۡ وَذِلَّةٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُفۡتَرِينَ\nАлбатта, бузоқни (худо) тутганларга Роббиларидан ғазаб ва ҳаёти дунёда хорлик етадир. Ёлғон тўқувчиларни мана шундай жазолармиз'}, {'chapter': 7, 'verse': 153, 'text': 'وَٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ ثُمَّ تَابُواْ مِنۢ بَعۡدِهَا وَءَامَنُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ\nГуноҳ ишларни қилганлар, ундан кейин тавба қилсалар ва иймон келтирсалар, албатта, Роббинг ундан сўнг мағфират қилгувчи ва меҳрибон Зотдир'}, {'chapter': 7, 'verse': 154, 'text': 'وَلَمَّا سَكَتَ عَن مُّوسَى ٱلۡغَضَبُ أَخَذَ ٱلۡأَلۡوَاحَۖ وَفِي نُسۡخَتِهَا هُدٗى وَرَحۡمَةٞ لِّلَّذِينَ هُمۡ لِرَبِّهِمۡ يَرۡهَبُونَ\nҚачонки Мусонинг ғазаби босилгач, лавҳларни олди. Уларнинг нусхасида Роббиларидан ҳақиқатда қўрқадиганлар учун ҳидоят ва раҳмат бор эди'}, {'chapter': 7, 'verse': 155, 'text': 'وَٱخۡتَارَ مُوسَىٰ قَوۡمَهُۥ سَبۡعِينَ رَجُلٗا لِّمِيقَٰتِنَاۖ فَلَمَّآ أَخَذَتۡهُمُ ٱلرَّجۡفَةُ قَالَ رَبِّ لَوۡ شِئۡتَ أَهۡلَكۡتَهُم مِّن قَبۡلُ وَإِيَّـٰيَۖ أَتُهۡلِكُنَا بِمَا فَعَلَ ٱلسُّفَهَآءُ مِنَّآۖ إِنۡ هِيَ إِلَّا فِتۡنَتُكَ تُضِلُّ بِهَا مَن تَشَآءُ وَتَهۡدِي مَن تَشَآءُۖ أَنتَ وَلِيُّنَا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَاۖ وَأَنتَ خَيۡرُ ٱلۡغَٰفِرِينَ\nВа Мусо мийқотимизга қавмидан етмиш кишини танлаб олди. Уларни даҳшатли зилзила тутган пайтда: «Эй Роббим, агар хоҳласанг, буларни ҳам, мени ҳам бундан олдин ҳалок қилсанг бўларди. Эси пастларимиз қилган иш туфайли бизни ҳалок қиласанми? Бу Сенинг синовингдан бошқа нарса эмас. У ила кимни хоҳласанг, адаштирасан ва кимни хоҳласанг, ҳидоятга соласан. Сен валийимизсан, бас, бизни мағфират қил ва раҳим эт. Сенинг Ўзинг мағфират қилувчиларнинг энг яхшисисан'}, {'chapter': 7, 'verse': 156, 'text': '۞وَٱكۡتُبۡ لَنَا فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ إِنَّا هُدۡنَآ إِلَيۡكَۚ قَالَ عَذَابِيٓ أُصِيبُ بِهِۦ مَنۡ أَشَآءُۖ وَرَحۡمَتِي وَسِعَتۡ كُلَّ شَيۡءٖۚ فَسَأَكۡتُبُهَا لِلَّذِينَ يَتَّقُونَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلَّذِينَ هُم بِـَٔايَٰتِنَا يُؤۡمِنُونَ\nВа бизга бу дунёда ҳам, охиратда ҳам яхшиликни ёзиб қўй. Биз Сенинг Ўзингга тавба қилдик», – деди. У зот: «Азобим ила кимни хоҳласам, тутаман. Раҳматим эса, ҳамма нарсадан кенгдир. Уни тақво қилганларга, закот берганларга, оятларимизга иймон келтирганларга ёзажакман'}, {'chapter': 7, 'verse': 157, 'text': 'ٱلَّذِينَ يَتَّبِعُونَ ٱلرَّسُولَ ٱلنَّبِيَّ ٱلۡأُمِّيَّ ٱلَّذِي يَجِدُونَهُۥ مَكۡتُوبًا عِندَهُمۡ فِي ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِيلِ يَأۡمُرُهُم بِٱلۡمَعۡرُوفِ وَيَنۡهَىٰهُمۡ عَنِ ٱلۡمُنكَرِ وَيُحِلُّ لَهُمُ ٱلطَّيِّبَٰتِ وَيُحَرِّمُ عَلَيۡهِمُ ٱلۡخَبَـٰٓئِثَ وَيَضَعُ عَنۡهُمۡ إِصۡرَهُمۡ وَٱلۡأَغۡلَٰلَ ٱلَّتِي كَانَتۡ عَلَيۡهِمۡۚ فَٱلَّذِينَ ءَامَنُواْ بِهِۦ وَعَزَّرُوهُ وَنَصَرُوهُ وَٱتَّبَعُواْ ٱلنُّورَ ٱلَّذِيٓ أُنزِلَ مَعَهُۥٓ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nУлар ўз ҳузурларидаги Таврот ва Инжилда ёзилган ҳолида топиладиган, уларни яхшиликка буюриб, ёмонликдан қайтарадиган, уларга покиза нарсаларни ҳалол қилиб, нопок нарсаларни ҳаром қиладиган, устиларидаги юкни енгиллатиб, кишанларни ечадиган уммий Набий, Пайғамбарга эргашурлар. Бас, унга иймон келтириб, ёрдам бериб ва уни қўллаб-қувватлаган ҳамда унга нозил бўлган нурга эргашганлар — ана ўшалар нажот топгувчилардир», – деди'}, {'chapter': 7, 'verse': 158, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنِّي رَسُولُ ٱللَّهِ إِلَيۡكُمۡ جَمِيعًا ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرَسُولِهِ ٱلنَّبِيِّ ٱلۡأُمِّيِّ ٱلَّذِي يُؤۡمِنُ بِٱللَّهِ وَكَلِمَٰتِهِۦ وَٱتَّبِعُوهُ لَعَلَّكُمۡ تَهۡتَدُونَ\nСен: «Эй одамлар, албатта, мен сизларнинг барчангизга, осмонлару ернинг мулки Уники бўлган, Ундан ўзга илоҳ йўқ бўлган ва тирилтириб ўлдирадиган Аллоҳнинг Пайғамбаридирман. Бас, Аллоҳга ҳамда Унинг Аллоҳ ва Унинг калималарига иймон келтирадиган уммий Набийсига иймон келтиринг. Ва унга эргашинг, шоядки, ҳидоят топсангиз», – деб айт'}, {'chapter': 7, 'verse': 159, 'text': 'وَمِن قَوۡمِ مُوسَىٰٓ أُمَّةٞ يَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ يَعۡدِلُونَ\nВа Мусонинг қавмидан баъзи жамоалар борки, ҳақ ила ҳидоят қилиб, у ила адолат қилурлар'}, {'chapter': 7, 'verse': 160, 'text': 'وَقَطَّعۡنَٰهُمُ ٱثۡنَتَيۡ عَشۡرَةَ أَسۡبَاطًا أُمَمٗاۚ وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ إِذِ ٱسۡتَسۡقَىٰهُ قَوۡمُهُۥٓ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنۢبَجَسَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۚ وَظَلَّلۡنَا عَلَيۡهِمُ ٱلۡغَمَٰمَ وَأَنزَلۡنَا عَلَيۡهِمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nУларни ўн икки уруғ — жамоага бўлиб юбордик. Ва қавми ундан сув талаб қилган пайтда Мусога: «Асоинг ила тошни ур!» деб ваҳий юбордик. У(тош)дан ўн иккита булоқ отилиб чиқди. Ҳамма одамлар ўз сувхонасини билди. Ва уларга булутни соябон қилдик, манн ва беданаларни нозил қилдик. «Сизларга ризқ қилиб берган нарсаларимизнинг покларини енглар» (дедик). Улар Бизга зулм қилмадилар. Лекин ўзларига зулм қилар эдилар'}, {'chapter': 7, 'verse': 161, 'text': 'وَإِذۡ قِيلَ لَهُمُ ٱسۡكُنُواْ هَٰذِهِ ٱلۡقَرۡيَةَ وَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ وَقُولُواْ حِطَّةٞ وَٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا نَّغۡفِرۡ لَكُمۡ خَطِيٓـَٰٔتِكُمۡۚ سَنَزِيدُ ٱلۡمُحۡسِنِينَ\nУларга: «Мана бу шаҳарни маскан тутинглар, унда хоҳлаган жойингизда таом енг ва «Ҳиттатун» (гуноҳларимизни кечир) деб эшикдан сажда қилган ҳолингизда кирсангиз, сизнинг хатоларингизни мағфират қиламиз, эҳсон қилувчиларга зиёда қиламиз», деганимизни эсла'}, {'chapter': 7, 'verse': 162, 'text': 'فَبَدَّلَ ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡ قَوۡلًا غَيۡرَ ٱلَّذِي قِيلَ لَهُمۡ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَظۡلِمُونَ\nБас, улардан зулм қилганлари уларга айтилгандан ўзга гапни алмаштирдилар. Бас, зулм қилганлари туфайли устларига осмондан азоб юбордик'}, {'chapter': 7, 'verse': 163, 'text': 'وَسۡـَٔلۡهُمۡ عَنِ ٱلۡقَرۡيَةِ ٱلَّتِي كَانَتۡ حَاضِرَةَ ٱلۡبَحۡرِ إِذۡ يَعۡدُونَ فِي ٱلسَّبۡتِ إِذۡ تَأۡتِيهِمۡ حِيتَانُهُمۡ يَوۡمَ سَبۡتِهِمۡ شُرَّعٗا وَيَوۡمَ لَا يَسۡبِتُونَ لَا تَأۡتِيهِمۡۚ كَذَٰلِكَ نَبۡلُوهُم بِمَا كَانُواْ يَفۡسُقُونَ\nУлардан денгиз соҳилида бўлган қишлоқ ҳақида, шанба кунида ҳаддан ошганлари, шанбалик кунларида балиқлари очиқ келиб, шанбалик қилмаган кунлари келмагани ҳақида сўра. Қилган фосиқликлари туфайли уларни шундай синаймиз'}, {'chapter': 7, 'verse': 164, 'text': 'وَإِذۡ قَالَتۡ أُمَّةٞ مِّنۡهُمۡ لِمَ تَعِظُونَ قَوۡمًا ٱللَّهُ مُهۡلِكُهُمۡ أَوۡ مُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗاۖ قَالُواْ مَعۡذِرَةً إِلَىٰ رَبِّكُمۡ وَلَعَلَّهُمۡ يَتَّقُونَ\nУлардан бир жамоат: «Аллоҳ ҳалок қилувчи ёки шиддатли азоб-ла азобловчи қавмга ваъз қилиб нима қиласиз?!» деганида, улар: «Роббингизга узр бўлиши учун ва шоядки, тақво қилсалар деб», – дедилар'}, {'chapter': 7, 'verse': 165, 'text': 'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦٓ أَنجَيۡنَا ٱلَّذِينَ يَنۡهَوۡنَ عَنِ ٱلسُّوٓءِ وَأَخَذۡنَا ٱلَّذِينَ ظَلَمُواْ بِعَذَابِۭ بَـِٔيسِۭ بِمَا كَانُواْ يَفۡسُقُونَ\nБас, улар ўзларига эслатма бўлган нарсани унутганларида, ёмонликдан қайтараётганларга нажот бердик ва зулм қилганларни қилган фосиқликлари туфайли шиддатли азоб ила тутдик'}, {'chapter': 7, 'verse': 166, 'text': 'فَلَمَّا عَتَوۡاْ عَن مَّا نُهُواْ عَنۡهُ قُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ\nБас, улар ўзлари қайтарилган нарсадан қайтмай, такаббурлик қилганларида, уларга: «Хўрланган маймунларга айланинг!» – дедик'}, {'chapter': 7, 'verse': 167, 'text': 'وَإِذۡ تَأَذَّنَ رَبُّكَ لَيَبۡعَثَنَّ عَلَيۡهِمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَن يَسُومُهُمۡ سُوٓءَ ٱلۡعَذَابِۗ إِنَّ رَبَّكَ لَسَرِيعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمٞ\nРоббинг, албатта, уларнинг устларига қиёмат кунигача уларга ёмон азобни тоттирадиганларни юборишни эълон қилганини эсла! Албатта, Роббинг иқоби тез Зотдир ва, албатта, У мағфиратли ва раҳимли Зотдир'}, {'chapter': 7, 'verse': 168, 'text': 'وَقَطَّعۡنَٰهُمۡ فِي ٱلۡأَرۡضِ أُمَمٗاۖ مِّنۡهُمُ ٱلصَّـٰلِحُونَ وَمِنۡهُمۡ دُونَ ذَٰلِكَۖ وَبَلَوۡنَٰهُم بِٱلۡحَسَنَٰتِ وَٱلسَّيِّـَٔاتِ لَعَلَّهُمۡ يَرۡجِعُونَ\nВа уларни ер юзида жамоатларга бўлиб юбордик. Улардан аҳли солиҳлари бор ва улардан ундай эмаслари ҳам бор. Шоядки, қайтсалар деб, уларни яхшиликлар ва ёмонликлар ила синадик'}, {'chapter': 7, 'verse': 169, 'text': 'فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٞ وَرِثُواْ ٱلۡكِتَٰبَ يَأۡخُذُونَ عَرَضَ هَٰذَا ٱلۡأَدۡنَىٰ وَيَقُولُونَ سَيُغۡفَرُ لَنَا وَإِن يَأۡتِهِمۡ عَرَضٞ مِّثۡلُهُۥ يَأۡخُذُوهُۚ أَلَمۡ يُؤۡخَذۡ عَلَيۡهِم مِّيثَٰقُ ٱلۡكِتَٰبِ أَن لَّا يَقُولُواْ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّ وَدَرَسُواْ مَا فِيهِۗ وَٱلدَّارُ ٱلۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ\nБас, улардан сўнг, ортларидан китобни меросга олган бир ўринбосарлар келдики, улар мана бу тубаннинг ўткинчи(матоҳи)ни оладилар ва «Бизни кечирилур», – дерлар. Агар ўшанга ўхшаш ўткинчи нарса келса, яна олаверадилар. Улардан Аллоҳга нисбатан фақат ҳақдан бошқа нарса айтмасликка Китобнинг аҳду паймони олинган эмасмиди?! Улар у(китоб)даги нарсаларни ўрганганлар-ку?! Охират ҳовлиси тақво қилганлар учун яхшидир. Ақл ишлатмайсизларми'}, {'chapter': 7, 'verse': 170, 'text': 'وَٱلَّذِينَ يُمَسِّكُونَ بِٱلۡكِتَٰبِ وَأَقَامُواْ ٱلصَّلَوٰةَ إِنَّا لَا نُضِيعُ أَجۡرَ ٱلۡمُصۡلِحِينَ\nКитобни маҳкам тутганлар ва намозни тўкис адо этганлар бўлса, албатта, Биз аҳли солиҳларнинг ажрларини зое қилмасмиз'}, {'chapter': 7, 'verse': 171, 'text': '۞وَإِذۡ نَتَقۡنَا ٱلۡجَبَلَ فَوۡقَهُمۡ كَأَنَّهُۥ ظُلَّةٞ وَظَنُّوٓاْ أَنَّهُۥ وَاقِعُۢ بِهِمۡ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ\nУларнинг устига тоғни худди соябондек кўтарганимизни, улар уни, устимизга тушиб кетади, деб ўйлаганларида, сизга берган нарсамизни қувват ила олинглар ва ундаги нарсани эсланглар, шоядки, тақво қилсангиз, деганимизни эсла'}, {'chapter': 7, 'verse': 172, 'text': 'وَإِذۡ أَخَذَ رَبُّكَ مِنۢ بَنِيٓ ءَادَمَ مِن ظُهُورِهِمۡ ذُرِّيَّتَهُمۡ وَأَشۡهَدَهُمۡ عَلَىٰٓ أَنفُسِهِمۡ أَلَسۡتُ بِرَبِّكُمۡۖ قَالُواْ بَلَىٰ شَهِدۡنَآۚ أَن تَقُولُواْ يَوۡمَ ٱلۡقِيَٰمَةِ إِنَّا كُنَّا عَنۡ هَٰذَا غَٰفِلِينَ\nРоббинг Бани Одамнинг умуртқа поғонасидан, қиёмат куни «бундан ғофил эдик» демасликларингиз учун, зурриётларини олиб, ўзларига ўзларини гувоҳ қилиб: «Роббингиз эмасманми?» – деганида, «Худди шундай! Гувоҳ бўлдик!» – деганларини эсла'}, {'chapter': 7, 'verse': 173, 'text': 'أَوۡ تَقُولُوٓاْ إِنَّمَآ أَشۡرَكَ ءَابَآؤُنَا مِن قَبۡلُ وَكُنَّا ذُرِّيَّةٗ مِّنۢ بَعۡدِهِمۡۖ أَفَتُهۡلِكُنَا بِمَا فَعَلَ ٱلۡمُبۡطِلُونَ\nЁки: «Ҳақиқатда, ширк келтирганлар олдинги ота-боболаримиз, биз, уларнинг кейинги зурриётимиз. Ботил иш қилувчиларнинг қилмиши туфайли бизларни ҳалок этасанми?» демаслигингиз учун'}, {'chapter': 7, 'verse': 174, 'text': 'وَكَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ وَلَعَلَّهُمۡ يَرۡجِعُونَ\nШояд қайтсалар деб, оятларни мана шундай батафсил баён қиламиз'}, {'chapter': 7, 'verse': 175, 'text': 'وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ ٱلَّذِيٓ ءَاتَيۡنَٰهُ ءَايَٰتِنَا فَٱنسَلَخَ مِنۡهَا فَأَتۡبَعَهُ ٱلشَّيۡطَٰنُ فَكَانَ مِنَ ٱلۡغَاوِينَ\nУларга оятларимизни берганимизда, улардан ўзини олиб қочган ва уни шайтон эргаштириб кетиб, иғвога учганлардан бўлган шахснинг хабарини тиловат қилиб бер'}, {'chapter': 7, 'verse': 176, 'text': 'وَلَوۡ شِئۡنَا لَرَفَعۡنَٰهُ بِهَا وَلَٰكِنَّهُۥٓ أَخۡلَدَ إِلَى ٱلۡأَرۡضِ وَٱتَّبَعَ هَوَىٰهُۚ فَمَثَلُهُۥ كَمَثَلِ ٱلۡكَلۡبِ إِن تَحۡمِلۡ عَلَيۡهِ يَلۡهَثۡ أَوۡ تَتۡرُكۡهُ يَلۡهَثۚ ذَّـٰلِكَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۚ فَٱقۡصُصِ ٱلۡقَصَصَ لَعَلَّهُمۡ يَتَفَكَّرُونَ\nАгар хоҳласак, уни ўша(оят)лар билан кўтарар эдик. Лекин унинг ўзи ерга ёпишди ва ҳавойи нафсига эргашди. Бас, унинг мисоли худди бир итга ўхшар, уни ҳайдасанг ҳам, тилини осилтириб тураверадир, тек қўйсанг ҳам, тилини осилтириб тураверадир. Бу, Бизнинг оятларимизни ёлғонга чиқарган қавмларнинг мисолидир. Бу қиссани ҳикоя қил, шоядки, тафаккур қилсалар'}, {'chapter': 7, 'verse': 177, 'text': 'سَآءَ مَثَلًا ٱلۡقَوۡمُ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَأَنفُسَهُمۡ كَانُواْ يَظۡلِمُونَ\nОятларимизни ёлғонга чиқарган қавмларнинг мисоли қандай ҳам ёмон бўлди! Улар ўзларига зулм қиладилар'}, {'chapter': 7, 'verse': 178, 'text': 'مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِيۖ وَمَن يُضۡلِلۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nАллоҳ кимни ҳидоятга солса, ўша ҳидоят топувчидир. Кимни адаштирса, ўшалар ўзлари зиён кўрувчилардир'}, {'chapter': 7, 'verse': 179, 'text': 'وَلَقَدۡ ذَرَأۡنَا لِجَهَنَّمَ كَثِيرٗا مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ لَهُمۡ قُلُوبٞ لَّا يَفۡقَهُونَ بِهَا وَلَهُمۡ أَعۡيُنٞ لَّا يُبۡصِرُونَ بِهَا وَلَهُمۡ ءَاذَانٞ لَّا يَسۡمَعُونَ بِهَآۚ أُوْلَـٰٓئِكَ كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡغَٰفِلُونَ\nБатаҳқиқ, жаҳаннам учун кўплаб жин ва инсларни яратдик. Уларнинг диллари бор-у, тушуна олмаслар. Кўзлари бор-у, кўра олмаслар. Қулоқлари бор-у, эшита олмаслар. Ана ўшалар чорва ҳайвонлари кабидирлар. Балки улардан ҳам баттарроқдирлар. Ана ўшалар ғофилдирлар'}, {'chapter': 7, 'verse': 180, 'text': 'وَلِلَّهِ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰ فَٱدۡعُوهُ بِهَاۖ وَذَرُواْ ٱلَّذِينَ يُلۡحِدُونَ فِيٓ أَسۡمَـٰٓئِهِۦۚ سَيُجۡزَوۡنَ مَا كَانُواْ يَعۡمَلُونَ\nАллоҳнинг гўзал исмлари бордир. Бас, Унга ўша (исм)лар ила дуо қилинг ва Унинг исмларидан оғадиганларни тек қўйинг. Яқинда қилган амалларига яраша жазоланурлар'}, {'chapter': 7, 'verse': 181, 'text': 'وَمِمَّنۡ خَلَقۡنَآ أُمَّةٞ يَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ يَعۡدِلُونَ\nВа Биз яратган кимсалар ичида ҳақ ила ҳидоят қиладиган ва у ила адолат қиладиган жамоат ҳам бор'}, {'chapter': 7, 'verse': 182, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ\nОятларимизни ёлғонга чиқарганларни эса, улар билмайдиган томондан аста-секин оламиз'}, {'chapter': 7, 'verse': 183, 'text': 'وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ\nУларга муҳлат бериб қўяман. Албатта, Менинг «макрим» метиндир'}, {'chapter': 7, 'verse': 184, 'text': 'أَوَلَمۡ يَتَفَكَّرُواْۗ مَا بِصَاحِبِهِم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ مُّبِينٌ\nУлар ўз соҳибларида мажнунлик йўқлигини тафаккур қилиб кўрмайдиларми?! У фақат ошкора огоҳлантирувчи, холос'}, {'chapter': 7, 'verse': 185, 'text': 'أَوَلَمۡ يَنظُرُواْ فِي مَلَكُوتِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا خَلَقَ ٱللَّهُ مِن شَيۡءٖ وَأَنۡ عَسَىٰٓ أَن يَكُونَ قَدِ ٱقۡتَرَبَ أَجَلُهُمۡۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَهُۥ يُؤۡمِنُونَ\nОсмонлару ернинг мулкларига, Аллоҳ яратган нарсаларга ва, ажаб эмаски, ажаллари яқинлашиб қолган бўлишига назар солмайдиларми? Бундан кейин яна қайси гапга ишонадилар'}, {'chapter': 7, 'verse': 186, 'text': 'مَن يُضۡلِلِ ٱللَّهُ فَلَا هَادِيَ لَهُۥۚ وَيَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАллоҳ кимни залолатга кетказса, унга ҳидоят қилувчи йўқдир. Уларни тек қўйиб қўяди, туғёнларида адашиб-улоқиб юраверадилар'}, {'chapter': 7, 'verse': 187, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرۡسَىٰهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ رَبِّيۖ لَا يُجَلِّيهَا لِوَقۡتِهَآ إِلَّا هُوَۚ ثَقُلَتۡ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ لَا تَأۡتِيكُمۡ إِلَّا بَغۡتَةٗۗ يَسۡـَٔلُونَكَ كَأَنَّكَ حَفِيٌّ عَنۡهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nСендан соат (қиёмат) қачон собит бўлиши ҳақида сўрарлар. Сен: «Унинг илми фақат Роббим ҳузуридадир. Уни фақат Ўзигина зоҳир қиладир. У осмонлару ерда оғир иш бўлади. Сизларга фақат тўсатдан келадир», – деб айт. Худди сен уни билишинг керакдек, сендан сўрарлар. Сен: «Унинг илми фақат Аллоҳнинг ҳузуридадир. Лекин кўп одамлар билмаслар», – деб айт'}, {'chapter': 7, 'verse': 188, 'text': 'قُل لَّآ أَمۡلِكُ لِنَفۡسِي نَفۡعٗا وَلَا ضَرًّا إِلَّا مَا شَآءَ ٱللَّهُۚ وَلَوۡ كُنتُ أَعۡلَمُ ٱلۡغَيۡبَ لَٱسۡتَكۡثَرۡتُ مِنَ ٱلۡخَيۡرِ وَمَا مَسَّنِيَ ٱلسُّوٓءُۚ إِنۡ أَنَا۠ إِلَّا نَذِيرٞ وَبَشِيرٞ لِّقَوۡمٖ يُؤۡمِنُونَ\nСен: «Ўзим учун на фойда ва на зарарга молик эмасман. Магар Аллоҳнинг хоҳлагани бўлур. Агар ғайбни билганимда, яхшиликни кўпайтириб олган бўлар эдим ва менга ёмонлик етмасди. Мен фақат иймон келтирадиган қавмлар учун огоҳлантирувчи ва башорат берувчиман, холос», – деб айт'}, {'chapter': 7, 'verse': 189, 'text': '۞هُوَ ٱلَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَجَعَلَ مِنۡهَا زَوۡجَهَا لِيَسۡكُنَ إِلَيۡهَاۖ فَلَمَّا تَغَشَّىٰهَا حَمَلَتۡ حَمۡلًا خَفِيفٗا فَمَرَّتۡ بِهِۦۖ فَلَمَّآ أَثۡقَلَت دَّعَوَا ٱللَّهَ رَبَّهُمَا لَئِنۡ ءَاتَيۡتَنَا صَٰلِحٗا لَّنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ\nУ сизларни бир жондан яратган ва ором олсин учун ундан жуфтини яратган Зотдир. Вақтики у (жуфтини) ўраганида енгил ҳомиладор бўлди. Бас, у билан юрди. У оғирлашганда эса, икковлон Роббилари Аллоҳга: «Агар бизга солиҳ (фарзанд) берсанг, албатта, шукр келтирувчилардан бўламиз», – деб дуо қилдилар'}, {'chapter': 7, 'verse': 190, 'text': 'فَلَمَّآ ءَاتَىٰهُمَا صَٰلِحٗا جَعَلَا لَهُۥ شُرَكَآءَ فِيمَآ ءَاتَىٰهُمَاۚ فَتَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ\nУларга солиҳ(фарзанд)ни берганида эса, Аллоҳ берган нарсада Унга шериклар қила бошларлар. Аллоҳ улар келтирган ширкдан олийдир'}, {'chapter': 7, 'verse': 191, 'text': 'أَيُشۡرِكُونَ مَا لَا يَخۡلُقُ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ\nҲеч нарсани ярата олмайдиган, ўзлари яратиладиган нарсаларни ширк келтирурларми'}, {'chapter': 7, 'verse': 192, 'text': 'وَلَا يَسۡتَطِيعُونَ لَهُمۡ نَصۡرٗا وَلَآ أَنفُسَهُمۡ يَنصُرُونَ\nВа уларга ёрдам беришга қодир эмаслар ҳамда ўзларига ҳам ёрдам бера олмаслар'}, {'chapter': 7, 'verse': 193, 'text': 'وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا يَتَّبِعُوكُمۡۚ سَوَآءٌ عَلَيۡكُمۡ أَدَعَوۡتُمُوهُمۡ أَمۡ أَنتُمۡ صَٰمِتُونَ\nАгар уларни ҳидоятга даъват қилсангиз, сизга эргашмаслар. Уларни даъват қиласизми ёки жим турувчи бўласизми, сиз учун бари бир'}, {'chapter': 7, 'verse': 194, 'text': 'إِنَّ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ عِبَادٌ أَمۡثَالُكُمۡۖ فَٱدۡعُوهُمۡ فَلۡيَسۡتَجِيبُواْ لَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nАлбатта, сиз Аллоҳдан ўзга ибодат қилаётганлар ўзингизга ўхшаш бандалардир. Агар ростгўй бўлсангиз, уларни чақириб кўринг, бас, сизга жавоб берсинлар-чи'}, {'chapter': 7, 'verse': 195, 'text': 'أَلَهُمۡ أَرۡجُلٞ يَمۡشُونَ بِهَآۖ أَمۡ لَهُمۡ أَيۡدٖ يَبۡطِشُونَ بِهَآۖ أَمۡ لَهُمۡ أَعۡيُنٞ يُبۡصِرُونَ بِهَآۖ أَمۡ لَهُمۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۗ قُلِ ٱدۡعُواْ شُرَكَآءَكُمۡ ثُمَّ كِيدُونِ فَلَا تُنظِرُونِ\nЁки уларнинг оёқлари бормики, юрсалар; ёки қўллари бормики, ушласалар; ёки кўзлари бормики, кўрсалар; ёки қулоқлари бормики, эшитсалар. Сен: «Шерикларингизни чақиринг-да, менга ҳеч муҳлат бермай, зиддимга ҳийла-найранг қилаверинг'}, {'chapter': 7, 'verse': 196, 'text': 'إِنَّ وَلِـِّۧيَ ٱللَّهُ ٱلَّذِي نَزَّلَ ٱلۡكِتَٰبَۖ وَهُوَ يَتَوَلَّى ٱلصَّـٰلِحِينَ\nАлбатта, менинг валийим — эгам Китобни нозил қилган Аллоҳдир. У солиҳларни дўст тутадир'}, {'chapter': 7, 'verse': 197, 'text': 'وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَطِيعُونَ نَصۡرَكُمۡ وَلَآ أَنفُسَهُمۡ يَنصُرُونَ\nУндан ўзга сиз ибодат қилаётганлар сизга ёрдам беришга қодир эмаслар ва ўзларига ҳам ёрдам бера олмаслар'}, {'chapter': 7, 'verse': 198, 'text': 'وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا يَسۡمَعُواْۖ وَتَرَىٰهُمۡ يَنظُرُونَ إِلَيۡكَ وَهُمۡ لَا يُبۡصِرُونَ\nАгар уларни ҳидоятга даъват қилсангиз, эшитмаслар. Уларни сенга назар солиб тургандек кўрасан. Ҳолбуки, улар кўрмаслар», – деб айт'}, {'chapter': 7, 'verse': 199, 'text': 'خُذِ ٱلۡعَفۡوَ وَأۡمُرۡ بِٱلۡعُرۡفِ وَأَعۡرِضۡ عَنِ ٱلۡجَٰهِلِينَ\nКечиримли бўл, яхшиликка буюр ва жоҳиллардан юз ўгир'}, {'chapter': 7, 'verse': 200, 'text': 'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۚ إِنَّهُۥ سَمِيعٌ عَلِيمٌ\nАгар сенга шайтон томонидан санчиш санчилса, Аллоҳдан паноҳ сўра. Албатта, У яхши эшитувчи ва яхши кўрувчи Зотдир'}, {'chapter': 7, 'verse': 201, 'text': 'إِنَّ ٱلَّذِينَ ٱتَّقَوۡاْ إِذَا مَسَّهُمۡ طَـٰٓئِفٞ مِّنَ ٱلشَّيۡطَٰنِ تَذَكَّرُواْ فَإِذَا هُم مُّبۡصِرُونَ\nТақво қилувчилар, агар уларга шайтондан бир шарпа етса, албатта, зикр қиларлар, бас, кўрибсанки, улар (тўғри йўлни) кўрувчи бўлиб турибдилар'}, {'chapter': 7, 'verse': 202, 'text': 'وَإِخۡوَٰنُهُمۡ يَمُدُّونَهُمۡ فِي ٱلۡغَيِّ ثُمَّ لَا يُقۡصِرُونَ\nВа у(шайтон)ларнинг биродарлари йўлдан оздиришда уларга мадад берарлар, сўнгра бунда тўхтамаслар'}, {'chapter': 7, 'verse': 203, 'text': 'وَإِذَا لَمۡ تَأۡتِهِم بِـَٔايَةٖ قَالُواْ لَوۡلَا ٱجۡتَبَيۡتَهَاۚ قُلۡ إِنَّمَآ أَتَّبِعُ مَا يُوحَىٰٓ إِلَيَّ مِن رَّبِّيۚ هَٰذَا بَصَآئِرُ مِن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُؤۡمِنُونَ\nВа агар уларга мўъжиза келтирмасанг, «Ўзинг тўқиб қўявермайсанми?» – дерлар. Сен: «Мен фақат Роббимдан ваҳий қилинган нарсага эргашаман, холос. Мана бу — иймон келтирувчи қавм учун Роббингиздан кўз очувчи, ҳидоят ва раҳматдир», деб айт'}, {'chapter': 7, 'verse': 204, 'text': 'وَإِذَا قُرِئَ ٱلۡقُرۡءَانُ فَٱسۡتَمِعُواْ لَهُۥ وَأَنصِتُواْ لَعَلَّكُمۡ تُرۡحَمُونَ\nҚуръон тиловат қилганда, уни тингланглар ва жим туринглар, шояд, раҳматга эришсангиз'}, {'chapter': 7, 'verse': 205, 'text': 'وَٱذۡكُر رَّبَّكَ فِي نَفۡسِكَ تَضَرُّعٗا وَخِيفَةٗ وَدُونَ ٱلۡجَهۡرِ مِنَ ٱلۡقَوۡلِ بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ وَلَا تَكُن مِّنَ ٱلۡغَٰفِلِينَ\nРоббингни эртаю кеч ичингда тазарруъ-ла, қўрқиб, овоз чиқариб гапирмай зикр қил ва ғофиллардан бўлма'}, {'chapter': 7, 'verse': 206, 'text': 'إِنَّ ٱلَّذِينَ عِندَ رَبِّكَ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَيُسَبِّحُونَهُۥ وَلَهُۥ يَسۡجُدُونَۤ۩\nРоббинг ҳузуридагилар, албатта, Унинг ибодатидан мутакаббирлик қилмаслар, Уни поклаб ёд этадилар ва Унга сажда қиларлар'}], '8': [{'chapter': 8, 'verse': 1, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلۡأَنفَالِۖ قُلِ ٱلۡأَنفَالُ لِلَّهِ وَٱلرَّسُولِۖ فَٱتَّقُواْ ٱللَّهَ وَأَصۡلِحُواْ ذَاتَ بَيۡنِكُمۡۖ وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥٓ إِن كُنتُم مُّؤۡمِنِينَ\nСендан ўлжалар ҳақида сўрарлар. Сен: «Ўлжалар Аллоҳ ва Пайғамбарникидир. Бас, Аллоҳга тақво қилинглар ва ўз ораларингизни ислоҳ этинглар. Агар мўмин бўлсангиз, Аллоҳга ва Унинг Пайғамбарига итоат қилинглар», деб айт'}, {'chapter': 8, 'verse': 2, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَإِذَا تُلِيَتۡ عَلَيۡهِمۡ ءَايَٰتُهُۥ زَادَتۡهُمۡ إِيمَٰنٗا وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nАлбатта, Аллоҳ зикр қилинса, қалблари титрагувчи, оятлари тиловат этилса, иймонларини зиёда қилгувчи ва Роббиларига таваккул қилгувчиларгина мўминлардир'}, {'chapter': 8, 'verse': 3, 'text': 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар намозни тўкис адо этадиганлар ва Биз ризқ қилиб берган нарсалардан инфоқ қиладиганлардир'}, {'chapter': 8, 'verse': 4, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُمۡ دَرَجَٰتٌ عِندَ رَبِّهِمۡ وَمَغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nАна ўшалар ҳақиқий мўминлардир. Уларга Роббилари ҳузурида даражалар, мағфират ва карамли ризқ бор'}, {'chapter': 8, 'verse': 5, 'text': 'كَمَآ أَخۡرَجَكَ رَبُّكَ مِنۢ بَيۡتِكَ بِٱلۡحَقِّ وَإِنَّ فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ لَكَٰرِهُونَ\nАлбатта, бу ҳолат, мўминлардан бир гуруҳи ёқтирмаган бўлсалар ҳам, Роббинг сени ҳақ ила уйингдан чиқарганга ўхшайди'}, {'chapter': 8, 'verse': 6, 'text': 'يُجَٰدِلُونَكَ فِي ٱلۡحَقِّ بَعۡدَ مَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى ٱلۡمَوۡتِ وَهُمۡ يَنظُرُونَ\nХудди ўзлари кўриб турган ҳолларида ўлимга ҳайдалаётгандек, ҳақ аён бўлгандан кейин ҳам сен билан баҳслашадилар'}, {'chapter': 8, 'verse': 7, 'text': 'وَإِذۡ يَعِدُكُمُ ٱللَّهُ إِحۡدَى ٱلطَّآئِفَتَيۡنِ أَنَّهَا لَكُمۡ وَتَوَدُّونَ أَنَّ غَيۡرَ ذَاتِ ٱلشَّوۡكَةِ تَكُونُ لَكُمۡ وَيُرِيدُ ٱللَّهُ أَن يُحِقَّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦ وَيَقۡطَعَ دَابِرَ ٱلۡكَٰفِرِينَ\nЎшанда Аллоҳ сизга икки тоифадан бири албатта сизники бўлишини ваъда қилган эди. Сизлар шавкати йўғи сизларники бўлишини суяр эдингиз. Аллоҳ эса, Ўз калималари ила ҳақни ҳақ қилишни ва кофирларнинг думини қирқишни ирода қилган эди'}, {'chapter': 8, 'verse': 8, 'text': 'لِيُحِقَّ ٱلۡحَقَّ وَيُبۡطِلَ ٱلۡبَٰطِلَ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ\nЖиноятчилар ёқтирмасалар ҳам, ҳақни ҳақ, ботилни ботил қилиш учун шундай бўлишини ирода қилган эди'}, {'chapter': 8, 'verse': 9, 'text': 'إِذۡ تَسۡتَغِيثُونَ رَبَّكُمۡ فَٱسۡتَجَابَ لَكُمۡ أَنِّي مُمِدُّكُم بِأَلۡفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُرۡدِفِينَ\nЎшанда Роббингиздан мадад сўраганингизда, сизни (сўровингизни) ижобат қилиб: «Албатта, Мен сизларга кетма-кет келадиган мингта фаришта ила мадад берувчиман», – деди'}, {'chapter': 8, 'verse': 10, 'text': 'وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ وَلِتَطۡمَئِنَّ بِهِۦ قُلُوبُكُمۡۚ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ\nАллоҳ у(мадад)ни башорат ва қалбларингиз у билан хотиржам бўлиши учунгина қилди. ўалаба эса, фақат Аллоҳнинг ҳузуридадир. Албатта, Аллоҳ ғолиб ва ҳикматли Зотдир'}, {'chapter': 8, 'verse': 11, 'text': 'إِذۡ يُغَشِّيكُمُ ٱلنُّعَاسَ أَمَنَةٗ مِّنۡهُ وَيُنَزِّلُ عَلَيۡكُم مِّنَ ٱلسَّمَآءِ مَآءٗ لِّيُطَهِّرَكُم بِهِۦ وَيُذۡهِبَ عَنكُمۡ رِجۡزَ ٱلشَّيۡطَٰنِ وَلِيَرۡبِطَ عَلَىٰ قُلُوبِكُمۡ وَيُثَبِّتَ بِهِ ٱلۡأَقۡدَامَ\nЎшанда У сизни Ўзидан омонлик қилиб, мудроқ ила ўраган эди ва сизни поклаш, сиздан шайтоннинг васвасасини кетказиш ҳамда қалбларингизни боғлаш ва қадамларингизни собит қилиш учун устингизга осмондан сув туширган эди'}, {'chapter': 8, 'verse': 12, 'text': 'إِذۡ يُوحِي رَبُّكَ إِلَى ٱلۡمَلَـٰٓئِكَةِ أَنِّي مَعَكُمۡ فَثَبِّتُواْ ٱلَّذِينَ ءَامَنُواْۚ سَأُلۡقِي فِي قُلُوبِ ٱلَّذِينَ كَفَرُواْ ٱلرُّعۡبَ فَٱضۡرِبُواْ فَوۡقَ ٱلۡأَعۡنَاقِ وَٱضۡرِبُواْ مِنۡهُمۡ كُلَّ بَنَانٖ\nЎшанда Роббингиз фаришталарга: «Мен, албатта, сиз биланман, бас, сиз иймон келтирганларни собит қилинг, тезда куфр келтирганларнинг қалбларига қўрқинч соламан. Бас, бўйинлар устидан зарба беринглар ва ҳамма бармоқларига зарба беринглар», деб ваҳий қилган эди'}, {'chapter': 8, 'verse': 13, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ ٱللَّهَ وَرَسُولَهُۥۚ وَمَن يُشَاقِقِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nБу уларнинг Аллоҳга ва Унинг Расулига қарши турганлари учундир. Ким Аллоҳга ва Унинг Расулига қарши турса, бас, албатта, Аллоҳнинг иқоби шиддатлидир'}, {'chapter': 8, 'verse': 14, 'text': 'ذَٰلِكُمۡ فَذُوقُوهُ وَأَنَّ لِلۡكَٰفِرِينَ عَذَابَ ٱلنَّارِ\nМана шу сизники, тортаверинглар! Албатта, кофирларга дўзах азоби бордир'}, {'chapter': 8, 'verse': 15, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ زَحۡفٗا فَلَا تُوَلُّوهُمُ ٱلۡأَدۡبَارَ\nЭй, иймон келтирганлар, куфр келтирганларга яқинлашиб, юзма-юз келганингизда уларга орқа ўгирмангиз'}, {'chapter': 8, 'verse': 16, 'text': 'وَمَن يُوَلِّهِمۡ يَوۡمَئِذٖ دُبُرَهُۥٓ إِلَّا مُتَحَرِّفٗا لِّقِتَالٍ أَوۡ مُتَحَيِّزًا إِلَىٰ فِئَةٖ فَقَدۡ بَآءَ بِغَضَبٖ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nКимки ўша кунда жанг ҳунари кўрсатиб ёки бошқа гуруҳга қўшилмоқчи бўлган ҳолатдан ташқари, уларга орқа ўгириб қочса, бас, батаҳқиқ, Аллоҳдан бўлган ғазаб ила қайтибди. Унинг жойи жаҳаннамдир. У қандай ҳам ёмон жой'}, {'chapter': 8, 'verse': 17, 'text': 'فَلَمۡ تَقۡتُلُوهُمۡ وَلَٰكِنَّ ٱللَّهَ قَتَلَهُمۡۚ وَمَا رَمَيۡتَ إِذۡ رَمَيۡتَ وَلَٰكِنَّ ٱللَّهَ رَمَىٰ وَلِيُبۡلِيَ ٱلۡمُؤۡمِنِينَ مِنۡهُ بَلَآءً حَسَنًاۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nУларни сиз ўлдирмадингиз, балки Аллоҳ ўлдирди. Отган пайтингда, сен отмадинг, балки Аллоҳ отди. Ва мўминларни гўзал синов ила синаш учун (шундай қилди). Албатта, Аллоҳ эшитгувчи ва билгувчи Зотдир'}, {'chapter': 8, 'verse': 18, 'text': 'ذَٰلِكُمۡ وَأَنَّ ٱللَّهَ مُوهِنُ كَيۡدِ ٱلۡكَٰفِرِينَ\nАна ўшаларнинг (сизга бўлиши) Аллоҳ, албатта, кофирларнинг ҳийласини заиф қилгувчи бўлганидандир'}, {'chapter': 8, 'verse': 19, 'text': 'إِن تَسۡتَفۡتِحُواْ فَقَدۡ جَآءَكُمُ ٱلۡفَتۡحُۖ وَإِن تَنتَهُواْ فَهُوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَعُودُواْ نَعُدۡ وَلَن تُغۡنِيَ عَنكُمۡ فِئَتُكُمۡ شَيۡـٔٗا وَلَوۡ كَثُرَتۡ وَأَنَّ ٱللَّهَ مَعَ ٱلۡمُؤۡمِنِينَ\nАгар орани очишни сўраган бўлсангиз, бас, батаҳқиқ сизга орани очиш келди. Агар тўхтасангиз, бу ўзингиз учун яхшидир. Агар сиз қайтсангиз, Биз ҳам қайтамиз. Сизнинг гуруҳингиз қанча кўп бўлса ҳам, сизга ҳеч бир ҳожатингизни чиқара олмас. Албатта, Аллоҳ мўминлар билан биргадир'}, {'chapter': 8, 'verse': 20, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَوَلَّوۡاْ عَنۡهُ وَأَنتُمۡ تَسۡمَعُونَ\nЭй, иймон келтирганлар, Аллоҳга ва Унинг Расулига итоат қилингиз ва эшитиб туриб, Ундан юз ўгириб кетмангиз'}, {'chapter': 8, 'verse': 21, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ قَالُواْ سَمِعۡنَا وَهُمۡ لَا يَسۡمَعُونَ\nВа ўзлари эшитмасалар ҳам, «эшитдик» деганлар каби бўлмангиз'}, {'chapter': 8, 'verse': 22, 'text': '۞إِنَّ شَرَّ ٱلدَّوَآبِّ عِندَ ٱللَّهِ ٱلصُّمُّ ٱلۡبُكۡمُ ٱلَّذِينَ لَا يَعۡقِلُونَ\nАллоҳнинг ҳузурида ҳайвоннинг энг ёмони, албатта, кару соқов бўлиб, ақл ишлатмайдиганлардир'}, {'chapter': 8, 'verse': 23, 'text': 'وَلَوۡ عَلِمَ ٱللَّهُ فِيهِمۡ خَيۡرٗا لَّأَسۡمَعَهُمۡۖ وَلَوۡ أَسۡمَعَهُمۡ لَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ\nАгар Аллоҳ уларда бирор яхшиликни билганида, албатта эшиттирар эди ва агар уларга эшиттирса ҳам, албатта юз ўгириб, бурилиб кетар эдилар'}, {'chapter': 8, 'verse': 24, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱسۡتَجِيبُواْ لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمۡ لِمَا يُحۡيِيكُمۡۖ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَحُولُ بَيۡنَ ٱلۡمَرۡءِ وَقَلۡبِهِۦ وَأَنَّهُۥٓ إِلَيۡهِ تُحۡشَرُونَ\nЭй, иймон келтирганлар, сизга ҳаёт берадиган нарсага чақирганларида, Аллоҳ ва Расулига жавоб беринг ва билингки, албатта, Аллоҳ киши билан унинг қалбини ажратиб қўяди ва, албатта, Унинг ҳузурида тўпланурсиз'}, {'chapter': 8, 'verse': 25, 'text': 'وَٱتَّقُواْ فِتۡنَةٗ لَّا تُصِيبَنَّ ٱلَّذِينَ ظَلَمُواْ مِنكُمۡ خَآصَّةٗۖ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nВа сизлардан фақат зулм қилганларнинг ўзигагина етмайдиган фитнадан сақланинг. Ва билингки, албатта, Аллоҳнинг иқоби шиддатлидир'}, {'chapter': 8, 'verse': 26, 'text': 'وَٱذۡكُرُوٓاْ إِذۡ أَنتُمۡ قَلِيلٞ مُّسۡتَضۡعَفُونَ فِي ٱلۡأَرۡضِ تَخَافُونَ أَن يَتَخَطَّفَكُمُ ٱلنَّاسُ فَـَٔاوَىٰكُمۡ وَأَيَّدَكُم بِنَصۡرِهِۦ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِ لَعَلَّكُمۡ تَشۡكُرُونَ\nСиз ер юзида оз сонли, заифҳол бўлиб, одамлар сизни тутиб олишидан қўрқиб юрган ҳолингизни эсланг. Бас, У зот сизга жой берди, Ўз нусрати ила қўллади ва сизларга пок нарсаларни ризқ қилиб берди. Шоядки, шукр қилсангиз'}, {'chapter': 8, 'verse': 27, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَخُونُواْ ٱللَّهَ وَٱلرَّسُولَ وَتَخُونُوٓاْ أَمَٰنَٰتِكُمۡ وَأَنتُمۡ تَعۡلَمُونَ\nЭй, иймон келтирганлар, билиб туриб, Аллоҳга, Пайғамбарга ва омонатларингизга хиёнат қилманг'}, {'chapter': 8, 'verse': 28, 'text': 'وَٱعۡلَمُوٓاْ أَنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞ وَأَنَّ ٱللَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ\nВа билингки, молларингиз ва фарзандларингиз фитнадан ўзга нарса эмас ва, албатта, Аллоҳнинг ҳузурида улуғ ажр бордир'}, {'chapter': 8, 'verse': 29, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَتَّقُواْ ٱللَّهَ يَجۡعَل لَّكُمۡ فُرۡقَانٗا وَيُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيَغۡفِرۡ لَكُمۡۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nЭй, иймон келтирганлар, агар Аллоҳга тақво қилсангиз, сизга фурқон беради, сизнинг гуноҳларингизни ювиб, мағфират қилади. Аллоҳ улуғ фазл эгасидир'}, {'chapter': 8, 'verse': 30, 'text': 'وَإِذۡ يَمۡكُرُ بِكَ ٱلَّذِينَ كَفَرُواْ لِيُثۡبِتُوكَ أَوۡ يَقۡتُلُوكَ أَوۡ يُخۡرِجُوكَۚ وَيَمۡكُرُونَ وَيَمۡكُرُ ٱللَّهُۖ وَٱللَّهُ خَيۡرُ ٱلۡمَٰكِرِينَ\nКуфр келтирганлар сени ушлаш, ўлдириш ёки чиқариб юбориш учун макр қилганларини эсла. Улар макр қилдилар ва Аллоҳ ҳам «макр» қилди. Аллоҳ «макр» қилгувчиларнинг устасидир'}, {'chapter': 8, 'verse': 31, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا قَالُواْ قَدۡ سَمِعۡنَا لَوۡ نَشَآءُ لَقُلۡنَا مِثۡلَ هَٰذَآ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nУларга оятларимиз тиловат қилинса: «Батаҳқиқ, эшитдик, агар хоҳласак, бунга ўхшашни ўзимиз ҳам айтамиз. Бу аввалгиларнинг афсонасидан ўзга нарса эмас», – дейдилар'}, {'chapter': 8, 'verse': 32, 'text': 'وَإِذۡ قَالُواْ ٱللَّهُمَّ إِن كَانَ هَٰذَا هُوَ ٱلۡحَقَّ مِنۡ عِندِكَ فَأَمۡطِرۡ عَلَيۡنَا حِجَارَةٗ مِّنَ ٱلسَّمَآءِ أَوِ ٱئۡتِنَا بِعَذَابٍ أَلِيمٖ\n«Аллоҳим! агар мана шу сенинг ҳузурингдан келган ҳақ бўлса, устимизга осмондан тош ёғдиргин ёки бизга аламли азоб юборгин», деганларини эсла'}, {'chapter': 8, 'verse': 33, 'text': 'وَمَا كَانَ ٱللَّهُ لِيُعَذِّبَهُمۡ وَأَنتَ فِيهِمۡۚ وَمَا كَانَ ٱللَّهُ مُعَذِّبَهُمۡ وَهُمۡ يَسۡتَغۡفِرُونَ\nМодомики, сен уларнинг ичида экансан, Аллоҳ уларни азобламас. Модомики, улар истиғфор айтар эканлар, Аллоҳ уларни азобловчимас'}, {'chapter': 8, 'verse': 34, 'text': 'وَمَا لَهُمۡ أَلَّا يُعَذِّبَهُمُ ٱللَّهُ وَهُمۡ يَصُدُّونَ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَمَا كَانُوٓاْ أَوۡلِيَآءَهُۥٓۚ إِنۡ أَوۡلِيَآؤُهُۥٓ إِلَّا ٱلۡمُتَّقُونَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nНега энди Аллоҳ уларни азобламас экан. Улар Масжидул Ҳаромдан тўсмоқдалар. Ҳолбуки, Унинг валийлари эмаслар. Унинг валийлари тақводорлардан ўзга ҳеч ким эмас. Лекин кўплари билмаслар'}, {'chapter': 8, 'verse': 35, 'text': 'وَمَا كَانَ صَلَاتُهُمۡ عِندَ ٱلۡبَيۡتِ إِلَّا مُكَآءٗ وَتَصۡدِيَةٗۚ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nУларнинг Байт олдидаги намозлари ҳуштак ва қарсак чалишдан иборат эди, холос. Бас, куфр келтирганингиз туфайли азобни татиб кўринг'}, {'chapter': 8, 'verse': 36, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ يُنفِقُونَ أَمۡوَٰلَهُمۡ لِيَصُدُّواْ عَن سَبِيلِ ٱللَّهِۚ فَسَيُنفِقُونَهَا ثُمَّ تَكُونُ عَلَيۡهِمۡ حَسۡرَةٗ ثُمَّ يُغۡلَبُونَۗ وَٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ يُحۡشَرُونَ\nАлбатта, куфр келтирганлар молларини Аллоҳнинг йўлидан тўсиш учун сарфларлар. Бас, уларни сарфларлар-у, сўнгра, ўзларига ҳасрат бўладир, кейин мағлуб бўлурлар. Куфр келтирганлар жаҳаннамга тўпланурлар'}, {'chapter': 8, 'verse': 37, 'text': 'لِيَمِيزَ ٱللَّهُ ٱلۡخَبِيثَ مِنَ ٱلطَّيِّبِ وَيَجۡعَلَ ٱلۡخَبِيثَ بَعۡضَهُۥ عَلَىٰ بَعۡضٖ فَيَرۡكُمَهُۥ جَمِيعٗا فَيَجۡعَلَهُۥ فِي جَهَنَّمَۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nАллоҳ покдан нопокни айириши учун ва нопокнинг баъзиси устига баъзисини тахлаб, ҳаммасини жаҳаннамга ташлаши учун. Ана ўшалар зиён кўргувчилардир'}, {'chapter': 8, 'verse': 38, 'text': 'قُل لِّلَّذِينَ كَفَرُوٓاْ إِن يَنتَهُواْ يُغۡفَرۡ لَهُم مَّا قَدۡ سَلَفَ وَإِن يَعُودُواْ فَقَدۡ مَضَتۡ سُنَّتُ ٱلۡأَوَّلِينَ\nКуфр келтирганларга айт, агар тўхтасалар, ўтган нарсалар мағфират қилинур ва агар қайтадиган бўлсалар, бас, батаҳқиқ, аввалгиларнинг суннати ўтган'}, {'chapter': 8, 'verse': 39, 'text': 'وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ ٱلدِّينُ كُلُّهُۥ لِلَّهِۚ فَإِنِ ٱنتَهَوۡاْ فَإِنَّ ٱللَّهَ بِمَا يَعۡمَلُونَ بَصِيرٞ\nВа улар билан то фитна бўлмагунча ва диннинг ҳаммаси Аллоҳга бўлгунча уруш қилинг. Агар тўхтасалар, бас, Аллоҳ, албатта, нима қилаётганларини кўриб тургувчи Зотдир'}, {'chapter': 8, 'verse': 40, 'text': 'وَإِن تَوَلَّوۡاْ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَوۡلَىٰكُمۡۚ نِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِيرُ\nАгар юз ўгирсалар, бас, билгинки, албатта, Аллоҳ хожангиздир. У қандай ҳам яхши хожа ва қандай ҳам яхши нусрат бергувчи'}, {'chapter': 8, 'verse': 41, 'text': '۞وَٱعۡلَمُوٓاْ أَنَّمَا غَنِمۡتُم مِّن شَيۡءٖ فَأَنَّ لِلَّهِ خُمُسَهُۥ وَلِلرَّسُولِ وَلِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ وَمَآ أَنزَلۡنَا عَلَىٰ عَبۡدِنَا يَوۡمَ ٱلۡفُرۡقَانِ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nВа агар Аллоҳга, фурқон куни — икки жамоат тўқнашган куни бандамизга нозил қилган нарсамизга иймон келтирган бўлсангиз, билингки, ўлжага олган нарсангиздан бешдан бири Аллоҳга, Расулига, яқинларига, етимларга, мискинларга ва мусофирларгадир. Ва Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 8, 'verse': 42, 'text': 'إِذۡ أَنتُم بِٱلۡعُدۡوَةِ ٱلدُّنۡيَا وَهُم بِٱلۡعُدۡوَةِ ٱلۡقُصۡوَىٰ وَٱلرَّكۡبُ أَسۡفَلَ مِنكُمۡۚ وَلَوۡ تَوَاعَدتُّمۡ لَٱخۡتَلَفۡتُمۡ فِي ٱلۡمِيعَٰدِ وَلَٰكِن لِّيَقۡضِيَ ٱللَّهُ أَمۡرٗا كَانَ مَفۡعُولٗا لِّيَهۡلِكَ مَنۡ هَلَكَ عَنۢ بَيِّنَةٖ وَيَحۡيَىٰ مَنۡ حَيَّ عَنۢ بَيِّنَةٖۗ وَإِنَّ ٱللَّهَ لَسَمِيعٌ عَلِيمٌ\nЎшанда сизлар (водийнинг Мадинага) яқин тарафида, улар узоқ тарафида, отлиқлар эса сиздан пастда эди. Агар ваъдалашиб олганингизда ҳам, келишилган жой ва вақтда хилоф қилардингиз. Лекин Аллоҳ қилиниши керак бўлган ишни ҳал этиши учун, ҳалок бўладиган одам очиқ-ойдин ҳужжат билан ҳалок бўлиши учун, яшаши керак бўлган одам очиқ-ойдин ҳужжат билан яшаши учун (тўқнашдингиз). Ва, албатта, Аллоҳ эшитгувчи ва билгувчи Зотдир'}, {'chapter': 8, 'verse': 43, 'text': 'إِذۡ يُرِيكَهُمُ ٱللَّهُ فِي مَنَامِكَ قَلِيلٗاۖ وَلَوۡ أَرَىٰكَهُمۡ كَثِيرٗا لَّفَشِلۡتُمۡ وَلَتَنَٰزَعۡتُمۡ فِي ٱلۡأَمۡرِ وَلَٰكِنَّ ٱللَّهَ سَلَّمَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nЎшанда Аллоҳ тушингда уларни сенга оз кўрсатди. Агар уларни сенга кўп кўрсатганида, тушкунликка учрар эдингизлар ва бу ишда ораларингизда низо чиқар эди. Лекин Аллоҳ сақлади. Албатта, У дилингиздаги нарсани билгувчи Зотдир'}, {'chapter': 8, 'verse': 44, 'text': 'وَإِذۡ يُرِيكُمُوهُمۡ إِذِ ٱلۡتَقَيۡتُمۡ فِيٓ أَعۡيُنِكُمۡ قَلِيلٗا وَيُقَلِّلُكُمۡ فِيٓ أَعۡيُنِهِمۡ لِيَقۡضِيَ ٱللَّهُ أَمۡرٗا كَانَ مَفۡعُولٗاۗ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nЎшанда, тўқнашганингизда, уларни сизнинг кўзингизга оз қилиб кўрсатганини, сизни уларнинг кўзига оз қилиб кўрсатганини эсланг. Буни Аллоҳ қилиниши керак бўлган ишни ҳал этиш учун қилди. Ва барча ишлар Аллоҳга қайтарилур'}, {'chapter': 8, 'verse': 45, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمۡ فِئَةٗ فَٱثۡبُتُواْ وَٱذۡكُرُواْ ٱللَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ\nЭй, иймон келтирганлар! (Кофир) жамоатга рўбарў келганингизда, саботли бўлинг ва Аллоҳни кўп зикр қилинг, шоядки, нажот топсангиз'}, {'chapter': 8, 'verse': 46, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَنَٰزَعُواْ فَتَفۡشَلُواْ وَتَذۡهَبَ رِيحُكُمۡۖ وَٱصۡبِرُوٓاْۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِينَ\nАллоҳга ва Унинг Расулига итоат қилинг. Ўзаро низо қилманг, у ҳолда тушкунликка учрайсиз ва куч-қувватингиз кетадир. Ва сабр қилинг. Албатта, Аллоҳ сабр қилгувчилар биландир'}, {'chapter': 8, 'verse': 47, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ خَرَجُواْ مِن دِيَٰرِهِم بَطَرٗا وَرِئَآءَ ٱلنَّاسِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِۚ وَٱللَّهُ بِمَا يَعۡمَلُونَ مُحِيطٞ\nВа диёрларидан кибру ҳаво билан, одамларга риё учун чиқиб, Аллоҳнинг йўлидан тўсадиганларга ўхшаманг. Аллоҳ нима қилаётганларини иҳота қилгувчидир'}, {'chapter': 8, 'verse': 48, 'text': 'وَإِذۡ زَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ وَقَالَ لَا غَالِبَ لَكُمُ ٱلۡيَوۡمَ مِنَ ٱلنَّاسِ وَإِنِّي جَارٞ لَّكُمۡۖ فَلَمَّا تَرَآءَتِ ٱلۡفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَيۡهِ وَقَالَ إِنِّي بَرِيٓءٞ مِّنكُمۡ إِنِّيٓ أَرَىٰ مَا لَا تَرَوۡنَ إِنِّيٓ أَخَافُ ٱللَّهَۚ وَٱللَّهُ شَدِيدُ ٱلۡعِقَابِ\nЎшанда шайтон уларга амалларини зийнатлаб кўрсатди ва: «Бу кунда одамлардан сизга ғолиб кела оладиган ҳеч ким йўқ. Мен сизнинг ҳомийингизман», – деди. Икки жамоа бир-бирини кўрганида эса, орқасига қайтди ва: «Албатта, мен сиз кўрмаётган нарсани кўрмоқдаман. Мен Аллоҳдан қўрқаман», – деди. Аллоҳ иқоби шиддатли Зотдир'}, {'chapter': 8, 'verse': 49, 'text': 'إِذۡ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ غَرَّ هَـٰٓؤُلَآءِ دِينُهُمۡۗ وَمَن يَتَوَكَّلۡ عَلَى ٱللَّهِ فَإِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nЎшанда мунофиқлар ва дилларида мараз бўлганлар: «Анавиларни дини мағрур қилиб юборди», – дедилар. Ким Аллоҳга таваккул қилса, бас, албатта, Аллоҳ ғолиб ва ҳикматли Зотдир'}, {'chapter': 8, 'verse': 50, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ يَتَوَفَّى ٱلَّذِينَ كَفَرُواْ ٱلۡمَلَـٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ وَذُوقُواْ عَذَابَ ٱلۡحَرِيقِ\nАгар фаришталар куфр келтирганларнинг жонини олатуриб, юзларига ва кетларига уриб: «Ёнғин азобини тотинг'}, {'chapter': 8, 'verse': 51, 'text': 'ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nБу, қўлингиз тақдим қилган нарса туфайлидир. Албатта, Аллоҳ бандаларига ҳеч зулм қилгувчи эмасдир», – дейишларини кўрсанг эди'}, {'chapter': 8, 'verse': 52, 'text': 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۚ إِنَّ ٱللَّهَ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ\nХудди Оли Фиръавн ва улардан олдингиларнинг қилмишларига ўхшайди. Аллоҳнинг оятига куфр келтирдилар. Бас, Аллоҳ уларни гуноҳлари туфайли тутди. Албатта, Аллоҳ кучли ва иқоби шиддатли Зотдир'}, {'chapter': 8, 'verse': 53, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ لَمۡ يَكُ مُغَيِّرٗا نِّعۡمَةً أَنۡعَمَهَا عَلَىٰ قَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡ وَأَنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nБундай бўлиши Аллоҳ бир қавмга берган неъматини, токи, улар ўзларини ўзгартирмагунларича, ўзгартирувчи бўлмаслиги ва Аллоҳ эшитгувчи ва билгувчи зот бўлганидандир'}, {'chapter': 8, 'verse': 54, 'text': 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِ رَبِّهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَۚ وَكُلّٞ كَانُواْ ظَٰلِمِينَ\nХудди Оли Фиръавн ва улардан олдингиларнинг қилмишига ўхшаб, Роббиларининг оятларини ёлғонга чиқардилар. Бас, Биз уларни гуноҳлари туфайли ҳалок қилдик ва Оли Фиръавнни ғарқ қилдик. Ҳаммалари золим эдилар'}, {'chapter': 8, 'verse': 55, 'text': 'إِنَّ شَرَّ ٱلدَّوَآبِّ عِندَ ٱللَّهِ ٱلَّذِينَ كَفَرُواْ فَهُمۡ لَا يُؤۡمِنُونَ\nАлбатта, Аллоҳнинг ҳузурида ҳайвоннинг ёмони куфр келтирганларидир. Бас, улар иймон келтирмаслар'}, {'chapter': 8, 'verse': 56, 'text': 'ٱلَّذِينَ عَٰهَدتَّ مِنۡهُمۡ ثُمَّ يَنقُضُونَ عَهۡدَهُمۡ فِي كُلِّ مَرَّةٖ وَهُمۡ لَا يَتَّقُونَ\nУлардан сен аҳдлашганлар сўнгра ҳар сафар аҳдларини бузарлар ва улар У зотдан қўрқмаслар'}, {'chapter': 8, 'verse': 57, 'text': 'فَإِمَّا تَثۡقَفَنَّهُمۡ فِي ٱلۡحَرۡبِ فَشَرِّدۡ بِهِم مَّنۡ خَلۡفَهُمۡ لَعَلَّهُمۡ يَذَّكَّرُونَ\nАгар урушда улардан зафар қозонсанг, бас, уларни ҳалок қилиш билан ортларидагиларни сарсон қилиб қўй, шоядки, эсласалар'}, {'chapter': 8, 'verse': 58, 'text': 'وَإِمَّا تَخَافَنَّ مِن قَوۡمٍ خِيَانَةٗ فَٱنۢبِذۡ إِلَيۡهِمۡ عَلَىٰ سَوَآءٍۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡخَآئِنِينَ\nАгар бир қавмнинг хиёнатидан қўрқадиган бўлсанг, уларга тенгма-тенг (аҳд бузилганини) ўртага ташла. Албатта, Аллоҳ хоинларни севмас'}, {'chapter': 8, 'verse': 59, 'text': 'وَلَا يَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ سَبَقُوٓاْۚ إِنَّهُمۡ لَا يُعۡجِزُونَ\nКуфр келтирганлар ўздик деб ўйламасинлар. Улар ҳеч қочиб қутула олмаслар'}, {'chapter': 8, 'verse': 60, 'text': 'وَأَعِدُّواْ لَهُم مَّا ٱسۡتَطَعۡتُم مِّن قُوَّةٖ وَمِن رِّبَاطِ ٱلۡخَيۡلِ تُرۡهِبُونَ بِهِۦ عَدُوَّ ٱللَّهِ وَعَدُوَّكُمۡ وَءَاخَرِينَ مِن دُونِهِمۡ لَا تَعۡلَمُونَهُمُ ٱللَّهُ يَعۡلَمُهُمۡۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فِي سَبِيلِ ٱللَّهِ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ\nУлар учун қўлингиздан келганича куч-қувват ва эгарланган отларни тайёрлаб қўйингизки, бу билан Аллоҳнинг душманини, ўз душманингизни ва улардан бошқа ўзингиз билмайдиганларни қўрқитасиз. Уларни Аллоҳ биладир. Аллоҳ йўлида нимани сарф қилсангиз, сизга тўлиқ қайтарилур. Сизга зулм қилинмас'}, {'chapter': 8, 'verse': 61, 'text': '۞وَإِن جَنَحُواْ لِلسَّلۡمِ فَٱجۡنَحۡ لَهَا وَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАгар улар тинчликка мойил бўлсалар, сен ҳам мойил бўл. Ва Аллоҳга таваккул қил. Албатта, У эшитгувчи ва билгувчи Зотдир'}, {'chapter': 8, 'verse': 62, 'text': 'وَإِن يُرِيدُوٓاْ أَن يَخۡدَعُوكَ فَإِنَّ حَسۡبَكَ ٱللَّهُۚ هُوَ ٱلَّذِيٓ أَيَّدَكَ بِنَصۡرِهِۦ وَبِٱلۡمُؤۡمِنِينَ\nАгар сени алдашни ирода қилсалар, бас, сенга Аллоҳ кифоя қиладир. У сени Ўз нусрати ва мўминлар билан қўллаган'}, {'chapter': 8, 'verse': 63, 'text': 'وَأَلَّفَ بَيۡنَ قُلُوبِهِمۡۚ لَوۡ أَنفَقۡتَ مَا فِي ٱلۡأَرۡضِ جَمِيعٗا مَّآ أَلَّفۡتَ بَيۡنَ قُلُوبِهِمۡ وَلَٰكِنَّ ٱللَّهَ أَلَّفَ بَيۡنَهُمۡۚ إِنَّهُۥ عَزِيزٌ حَكِيمٞ\nҲамда уларнинг қалбларини бирлаштирган Зотдир. Агар ер юзидаги ҳамма нарсани сарф қилсанг ҳам, уларнинг қалбларини бирлаштира олмас эдинг. Лекин Аллоҳ уларни бирлаштирди. Албатта, У ғолиб ва ҳикматли Зотдир'}, {'chapter': 8, 'verse': 64, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ حَسۡبُكَ ٱللَّهُ وَمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِينَ\nЭй Набий, сенга ва сенга эргашган мўминларга Аллоҳ кифоядир'}, {'chapter': 8, 'verse': 65, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ حَرِّضِ ٱلۡمُؤۡمِنِينَ عَلَى ٱلۡقِتَالِۚ إِن يَكُن مِّنكُمۡ عِشۡرُونَ صَٰبِرُونَ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُم مِّاْئَةٞ يَغۡلِبُوٓاْ أَلۡفٗا مِّنَ ٱلَّذِينَ كَفَرُواْ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ\nЭй Набий, мўминларни жангга чорла! Агар сиздан йигирмата сабрли бўлса, икки юзтани енгади. Агар сиздан юзта бўлса, куфр келтирганлардан мингтасини енгади. Бу, улар тушунмайдиган қавм бўлганларидандир'}, {'chapter': 8, 'verse': 66, 'text': 'ٱلۡـَٰٔنَ خَفَّفَ ٱللَّهُ عَنكُمۡ وَعَلِمَ أَنَّ فِيكُمۡ ضَعۡفٗاۚ فَإِن يَكُن مِّنكُم مِّاْئَةٞ صَابِرَةٞ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُمۡ أَلۡفٞ يَغۡلِبُوٓاْ أَلۡفَيۡنِ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِينَ\nЭнди, Аллоҳ сизлардан енгиллаштирди, сизларда ожизлик борлигини билди. Бас, агар сиздан юзта сабрли бўлса, икки юзтани енгади. Агар сиздан мингта бўлса, Аллоҳнинг изни ила икки мингтани енгади. Аллоҳ сабрлилар биландир'}, {'chapter': 8, 'verse': 67, 'text': 'مَا كَانَ لِنَبِيٍّ أَن يَكُونَ لَهُۥٓ أَسۡرَىٰ حَتَّىٰ يُثۡخِنَ فِي ٱلۡأَرۡضِۚ تُرِيدُونَ عَرَضَ ٱلدُّنۡيَا وَٱللَّهُ يُرِيدُ ٱلۡأٓخِرَةَۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ\nНабий учун ер юзида забардаст бўлмагунича асирлари бўлиши яхши эмас эди. Сизлар дунёнинг ўткинчи матоҳини истайсизлар. Аллоҳ эса, охиратни истайдир. Ва Аллоҳ ғолиб ва ҳикматли Зотдир'}, {'chapter': 8, 'verse': 68, 'text': 'لَّوۡلَا كِتَٰبٞ مِّنَ ٱللَّهِ سَبَقَ لَمَسَّكُمۡ فِيمَآ أَخَذۡتُمۡ عَذَابٌ عَظِيمٞ\nАгар Аллоҳнинг олдиндан ёзгани бўлмаганида, сизга олган нарсаларингиз туфайли улуғ азоб етар эди'}, {'chapter': 8, 'verse': 69, 'text': 'فَكُلُواْ مِمَّا غَنِمۡتُمۡ حَلَٰلٗا طَيِّبٗاۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nБас, ўлжага олган нарсаларингиздан ҳалол-пок ҳолда енглар. Аллоҳга тақво қилинглар. Албатта, Аллоҳ мағфират қилгувчи ва раҳимли Зотдир'}, {'chapter': 8, 'verse': 70, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّمَن فِيٓ أَيۡدِيكُم مِّنَ ٱلۡأَسۡرَىٰٓ إِن يَعۡلَمِ ٱللَّهُ فِي قُلُوبِكُمۡ خَيۡرٗا يُؤۡتِكُمۡ خَيۡرٗا مِّمَّآ أُخِذَ مِنكُمۡ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭй Набий, қўлингиздаги асирларга: «Агар қалбларингизда яхшилик бўлса, Аллоҳ билур ва сизга ўзингиздан олинган нарсадан кўра яхшироқ нарсани берур ва сизни мағфират қилур. Аллоҳ мағфират қилгувчи ва раҳимли Зотдир», – деб айт'}, {'chapter': 8, 'verse': 71, 'text': 'وَإِن يُرِيدُواْ خِيَانَتَكَ فَقَدۡ خَانُواْ ٱللَّهَ مِن قَبۡلُ فَأَمۡكَنَ مِنۡهُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nАгар сенга хиёнат қилишни истасалар, батаҳқиқ, бундан олдин Аллоҳга хиёнат қилган эдилар. Бас, У уларга қарши имкон топди. Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 8, 'verse': 72, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِ وَٱلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَـٰٓئِكَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَٱلَّذِينَ ءَامَنُواْ وَلَمۡ يُهَاجِرُواْ مَا لَكُم مِّن وَلَٰيَتِهِم مِّن شَيۡءٍ حَتَّىٰ يُهَاجِرُواْۚ وَإِنِ ٱسۡتَنصَرُوكُمۡ فِي ٱلدِّينِ فَعَلَيۡكُمُ ٱلنَّصۡرُ إِلَّا عَلَىٰ قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nАлбатта, иймон келтирган, ҳижрат қилган, Аллоҳнинг йўлида молу жонлари билан жиҳод қилганлар ва жой бериб, ёрдам кўрсатганлар, ана ўшалар бир-бирларига валийдирлар. Иймон келтирган, аммо ҳижрат қилмаганларга эса, то ҳижрат қилмагунларича, сиз ҳеч валий бўла олмассиз. Агар улар сиздан динда ёрдам сўрасалар, ёрдам бермоғингиз вожиб. Фақат, сиз билан ўрталарида аҳднома бор қавм зиддига эмас. Аллоҳ нима қилаётганингизни кўргувчи Зотдир'}, {'chapter': 8, 'verse': 73, 'text': 'وَٱلَّذِينَ كَفَرُواْ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٍۚ إِلَّا تَفۡعَلُوهُ تَكُن فِتۡنَةٞ فِي ٱلۡأَرۡضِ وَفَسَادٞ كَبِيرٞ\nКуфр келтирганлар бир-бирларига валийдирлар. Агар (мазкур нарсани) қилмасангиз, ер юзида фитна ва катта фасод бўладир'}, {'chapter': 8, 'verse': 74, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ وَٱلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nИймон келтирган, ҳижрат этган, Аллоҳнинг йўлида жиҳод қилганлар ва жой бериб, ёрдам кўрсатганлар, ана ўшалар ҳақ мўминлардир. Улар учун мағфират ва карамли ризқ бордир'}, {'chapter': 8, 'verse': 75, 'text': 'وَٱلَّذِينَ ءَامَنُواْ مِنۢ بَعۡدُ وَهَاجَرُواْ وَجَٰهَدُواْ مَعَكُمۡ فَأُوْلَـٰٓئِكَ مِنكُمۡۚ وَأُوْلُواْ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ ٱللَّهِۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمُۢ\nКейин иймон келтириб, ҳижрат қилган ва сизлар билан жиҳод қилганлар ҳам сизлардандир. Аллоҳнинг китобида қариндошлар бир-бирларига ҳақлироқдирлар. Албатта, Аллоҳ ҳар бир нарсани билгувчи Зотдир'}], '9': [{'chapter': 9, 'verse': 1, 'text': 'بَرَآءَةٞ مِّنَ ٱللَّهِ وَرَسُولِهِۦٓ إِلَى ٱلَّذِينَ عَٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِينَ\n(Бу) Аллоҳдан ва Унинг Расулидан мушрикларнинг сиз аҳдлашганларингизга «бароат» — ора очиқлиги (эълони)дир'}, {'chapter': 9, 'verse': 2, 'text': 'فَسِيحُواْ فِي ٱلۡأَرۡضِ أَرۡبَعَةَ أَشۡهُرٖ وَٱعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي ٱللَّهِ وَأَنَّ ٱللَّهَ مُخۡزِي ٱلۡكَٰفِرِينَ\nБас, ер юзида тўрт ой сайр қилиб юринглар ва билингларки, албатта, сиз Аллоҳни ожиз қолдирувчи эмассиз ва, албатта, Аллоҳ кофирларни хор қилгувчидир'}, {'chapter': 9, 'verse': 3, 'text': 'وَأَذَٰنٞ مِّنَ ٱللَّهِ وَرَسُولِهِۦٓ إِلَى ٱلنَّاسِ يَوۡمَ ٱلۡحَجِّ ٱلۡأَكۡبَرِ أَنَّ ٱللَّهَ بَرِيٓءٞ مِّنَ ٱلۡمُشۡرِكِينَ وَرَسُولُهُۥۚ فَإِن تُبۡتُمۡ فَهُوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَوَلَّيۡتُمۡ فَٱعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي ٱللَّهِۗ وَبَشِّرِ ٱلَّذِينَ كَفَرُواْ بِعَذَابٍ أَلِيمٍ\n(Бу) Аллоҳ ва Унинг Расулидан одамларга «ҳажжул акбар» кунидаги билдиришдир. Албатта, Аллоҳ ва Унинг Расулининг мушриклар билан оралари очиқдир. Бас, агар тавба қилсангиз, у ўзингиз учун яхшидир. Агар юз ўгириб кетадиган бўлсангиз, билингларки, албатта, сизлар Аллоҳни ожиз қолдирувчи эмассиз. Куфр келтирганларга аламли азобнинг «башоратини» беравер'}, {'chapter': 9, 'verse': 4, 'text': 'إِلَّا ٱلَّذِينَ عَٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِينَ ثُمَّ لَمۡ يَنقُصُوكُمۡ شَيۡـٔٗا وَلَمۡ يُظَٰهِرُواْ عَلَيۡكُمۡ أَحَدٗا فَأَتِمُّوٓاْ إِلَيۡهِمۡ عَهۡدَهُمۡ إِلَىٰ مُدَّتِهِمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ\nМагар мушриклардан сиз аҳдлашганингиздан кейин ҳеч нарсани нуқсон қилмаганлари ва сизга қарши бировга ёрдам бермаганларининг аҳдларини муддатларигача батамом қилинг. Албатта, Аллоҳ тақводорларни севадир'}, {'chapter': 9, 'verse': 5, 'text': 'فَإِذَا ٱنسَلَخَ ٱلۡأَشۡهُرُ ٱلۡحُرُمُ فَٱقۡتُلُواْ ٱلۡمُشۡرِكِينَ حَيۡثُ وَجَدتُّمُوهُمۡ وَخُذُوهُمۡ وَٱحۡصُرُوهُمۡ وَٱقۡعُدُواْ لَهُمۡ كُلَّ مَرۡصَدٖۚ فَإِن تَابُواْ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ فَخَلُّواْ سَبِيلَهُمۡۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nБас, ҳаром ойлар чиққанда, мушрикларни қаерда топсангиз, ўлдиринглар, тутинглар, қамал қилинглар ва уларни ҳар жойда пойланглар. Агар тавба қилсалар, намозни тўкис адо этсалар ва закотни берсалар, йўлларига қўйиб юборинглар. Албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 9, 'verse': 6, 'text': 'وَإِنۡ أَحَدٞ مِّنَ ٱلۡمُشۡرِكِينَ ٱسۡتَجَارَكَ فَأَجِرۡهُ حَتَّىٰ يَسۡمَعَ كَلَٰمَ ٱللَّهِ ثُمَّ أَبۡلِغۡهُ مَأۡمَنَهُۥۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡلَمُونَ\nАгар мушриклардан биронтаси паноҳ сўраса, унга паноҳ бер, токи у, Аллоҳнинг каломини эшитсин. Сўнгра, уни омонлик жойига етказиб қўй. Бу, уларнинг билмайдиган қавм бўлганлари учундир'}, {'chapter': 9, 'verse': 7, 'text': 'كَيۡفَ يَكُونُ لِلۡمُشۡرِكِينَ عَهۡدٌ عِندَ ٱللَّهِ وَعِندَ رَسُولِهِۦٓ إِلَّا ٱلَّذِينَ عَٰهَدتُّمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ فَمَا ٱسۡتَقَٰمُواْ لَكُمۡ فَٱسۡتَقِيمُواْ لَهُمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ\nҚандай қилиб мушрикларга Аллоҳнинг ҳузурида ва Унинг Расулининг ҳузурида аҳд бўлсин?! Магар сиз билан Масжидул Ҳаром олдида аҳдлашганлар, сизга нисбатан мудом мустақийм турсалар, сиз ҳам мустақийм туринг. Албатта, Аллоҳ тақводорларни севадир'}, {'chapter': 9, 'verse': 8, 'text': 'كَيۡفَ وَإِن يَظۡهَرُواْ عَلَيۡكُمۡ لَا يَرۡقُبُواْ فِيكُمۡ إِلّٗا وَلَا ذِمَّةٗۚ يُرۡضُونَكُم بِأَفۡوَٰهِهِمۡ وَتَأۡبَىٰ قُلُوبُهُمۡ وَأَكۡثَرُهُمۡ فَٰسِقُونَ\nҚандай қилиб (уларга аҳд бўлсинки), улар агар сиздан устун келсалар, на аҳдга ва на бурчга риоя этмаслар. Сизни оғизлари билан рози қиладилар-у, қалблари кўнмас. Уларнинг кўплари фосиқлардир'}, {'chapter': 9, 'verse': 9, 'text': 'ٱشۡتَرَوۡاْ بِـَٔايَٰتِ ٱللَّهِ ثَمَنٗا قَلِيلٗا فَصَدُّواْ عَن سَبِيلِهِۦٓۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ\nУлар Аллоҳнинг оятларини озгина баҳога сотиб, Унинг йўлидан тўсдилар. Дарҳақиқат, уларнинг қилганлари қандай ҳам ёмон бўлди'}, {'chapter': 9, 'verse': 10, 'text': 'لَا يَرۡقُبُونَ فِي مُؤۡمِنٍ إِلّٗا وَلَا ذِمَّةٗۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُعۡتَدُونَ\nУлар мўмин ҳақида на аҳдга ва на бурчга риоя қилмаслар. Ана ўшалар, ўзлари тажовузкорлардир'}, {'chapter': 9, 'verse': 11, 'text': 'فَإِن تَابُواْ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ فَإِخۡوَٰنُكُمۡ فِي ٱلدِّينِۗ وَنُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nБас, агар тавба қилсалар, намозни тўкис адо этсалар ва закотни берсалар, дин қардошингиздирлар. Биладиган қавм учун оятларимизни батафсил баён қиламиз'}, {'chapter': 9, 'verse': 12, 'text': 'وَإِن نَّكَثُوٓاْ أَيۡمَٰنَهُم مِّنۢ بَعۡدِ عَهۡدِهِمۡ وَطَعَنُواْ فِي دِينِكُمۡ فَقَٰتِلُوٓاْ أَئِمَّةَ ٱلۡكُفۡرِ إِنَّهُمۡ لَآ أَيۡمَٰنَ لَهُمۡ لَعَلَّهُمۡ يَنتَهُونَ\nАгар аҳд берганларидан кейин аҳдларини бузсалар ва динингизга таъна тошини отсалар, куфрбошиларга қарши жанг қилинг! Зеро, уларнинг аҳдлари йўқдир. Шоядки, тўхтасалар'}, {'chapter': 9, 'verse': 13, 'text': 'أَلَا تُقَٰتِلُونَ قَوۡمٗا نَّكَثُوٓاْ أَيۡمَٰنَهُمۡ وَهَمُّواْ بِإِخۡرَاجِ ٱلرَّسُولِ وَهُم بَدَءُوكُمۡ أَوَّلَ مَرَّةٍۚ أَتَخۡشَوۡنَهُمۡۚ فَٱللَّهُ أَحَقُّ أَن تَخۡشَوۡهُ إِن كُنتُم مُّؤۡمِنِينَ\nАҳдларини бузган, Расулни (юртдан) чиқаришга қасд қилган ва сизга (қарши) биринчи бўлиб (уруш) бошлаган қавм билан урушмайсизми?! Улардан қўрқасизми?! Агар мўмин бўлсангиз, қўрқишингизга лойиқ зот Аллоҳдир'}, {'chapter': 9, 'verse': 14, 'text': 'قَٰتِلُوهُمۡ يُعَذِّبۡهُمُ ٱللَّهُ بِأَيۡدِيكُمۡ وَيُخۡزِهِمۡ وَيَنصُرۡكُمۡ عَلَيۡهِمۡ وَيَشۡفِ صُدُورَ قَوۡمٖ مُّؤۡمِنِينَ\nУларга қарши жанг қилинглар! Аллоҳ уларни сизнинг қўлингиз билан азоблайдир, хорлайдир ва уларга қарши сизга ёрдам берадир. Ва мўмин қавмларнинг кўнгилларига шифо берадир'}, {'chapter': 9, 'verse': 15, 'text': 'وَيُذۡهِبۡ غَيۡظَ قُلُوبِهِمۡۗ وَيَتُوبُ ٱللَّهُ عَلَىٰ مَن يَشَآءُۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nВа қалбларидаги аччиқни кетказадир. Аллоҳ кимни хоҳласа, ўшанинг тавбасини қабул қиладир. Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 16, 'text': 'أَمۡ حَسِبۡتُمۡ أَن تُتۡرَكُواْ وَلَمَّا يَعۡلَمِ ٱللَّهُ ٱلَّذِينَ جَٰهَدُواْ مِنكُمۡ وَلَمۡ يَتَّخِذُواْ مِن دُونِ ٱللَّهِ وَلَا رَسُولِهِۦ وَلَا ٱلۡمُؤۡمِنِينَ وَلِيجَةٗۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nСиздан жиҳод қилганларни, Аллоҳдан, Унинг Расулидан ва мўминлардан ўзгани дўст тутмаганларни билмай туриб, Аллоҳ томонидан тарк этиб қўйилмоғингизни ўйладингизми? Аллоҳ нима қилаётганларингиздан хабардор Зотдир'}, {'chapter': 9, 'verse': 17, 'text': 'مَا كَانَ لِلۡمُشۡرِكِينَ أَن يَعۡمُرُواْ مَسَٰجِدَ ٱللَّهِ شَٰهِدِينَ عَلَىٰٓ أَنفُسِهِم بِٱلۡكُفۡرِۚ أُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ وَفِي ٱلنَّارِ هُمۡ خَٰلِدُونَ\nМушрикларга, ўзларининг кофир эканликларига гувоҳ бўлган ҳолларида, Аллоҳнинг масжидларини обод қилишлари жоиз эмас. Ана ўшалар амаллари ҳабата бўлганлардир ва улар дўзахда абадий қолувчилардир'}, {'chapter': 9, 'verse': 18, 'text': 'إِنَّمَا يَعۡمُرُ مَسَٰجِدَ ٱللَّهِ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَلَمۡ يَخۡشَ إِلَّا ٱللَّهَۖ فَعَسَىٰٓ أُوْلَـٰٓئِكَ أَن يَكُونُواْ مِنَ ٱلۡمُهۡتَدِينَ\nАлбатта, Аллоҳнинг масжидларини фақат Аллоҳга ва охират кунига иймон келтирганлар, намозни тўкис адо этганлар, закотни берганлар ва Аллоҳдан бошқадан қўрқмаганларгина обод қилурлар. Ажаб эмаски, ана ўшалар ҳидоят топгувчилардан бўлсалар'}, {'chapter': 9, 'verse': 19, 'text': '۞أَجَعَلۡتُمۡ سِقَايَةَ ٱلۡحَآجِّ وَعِمَارَةَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ كَمَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَجَٰهَدَ فِي سَبِيلِ ٱللَّهِۚ لَا يَسۡتَوُۥنَ عِندَ ٱللَّهِۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nҲожиларга сув беришни ва Масжидул Ҳаромни обод қилишни Аллоҳга, охират кунига иймон келтирган ва Аллоҳнинг йўлида жиҳод қилган каби қилдингизми?! Улар Аллоҳнинг ҳузурида тенг эмаслар. Аллоҳ золим қавмларни ҳидоят қилмас'}, {'chapter': 9, 'verse': 20, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ أَعۡظَمُ دَرَجَةً عِندَ ٱللَّهِۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ\nИймон келтириб, ҳижрат қилганлар ва Аллоҳнинг йўлида моллари ва жонлари билан жидду жаҳд қилганларнинг даражалари Аллоҳнинг ҳузурида жуда улуғдир. Ана ўшалар ютуққа эришувчилардир'}, {'chapter': 9, 'verse': 21, 'text': 'يُبَشِّرُهُمۡ رَبُّهُم بِرَحۡمَةٖ مِّنۡهُ وَرِضۡوَٰنٖ وَجَنَّـٰتٖ لَّهُمۡ فِيهَا نَعِيمٞ مُّقِيمٌ\nРоббилари уларга Ўз тарафидан бўлган раҳмат, розилик ва ичида уларга аталган доимий неъматлар бор жаннатларнинг башоратини берадир'}, {'chapter': 9, 'verse': 22, 'text': 'خَٰلِدِينَ فِيهَآ أَبَدًاۚ إِنَّ ٱللَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ\nУлар унда абадий қолувчилардир. Албатта, Аллоҳ — ҳузурида улуғ ажр бор Зотдир'}, {'chapter': 9, 'verse': 23, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُوٓاْ ءَابَآءَكُمۡ وَإِخۡوَٰنَكُمۡ أَوۡلِيَآءَ إِنِ ٱسۡتَحَبُّواْ ٱلۡكُفۡرَ عَلَى ٱلۡإِيمَٰنِۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nЭй, иймон келтирганлар! Агар куфрни иймондан устун кўрсалар, ота-онангиз ва ака-укаларингизни дўст тутманг. Сиздан ким уларни дўст тутса, бас, ана ўшалар, ўзлари золимлардир'}, {'chapter': 9, 'verse': 24, 'text': 'قُلۡ إِن كَانَ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ وَإِخۡوَٰنُكُمۡ وَأَزۡوَٰجُكُمۡ وَعَشِيرَتُكُمۡ وَأَمۡوَٰلٌ ٱقۡتَرَفۡتُمُوهَا وَتِجَٰرَةٞ تَخۡشَوۡنَ كَسَادَهَا وَمَسَٰكِنُ تَرۡضَوۡنَهَآ أَحَبَّ إِلَيۡكُم مِّنَ ٱللَّهِ وَرَسُولِهِۦ وَجِهَادٖ فِي سَبِيلِهِۦ فَتَرَبَّصُواْ حَتَّىٰ يَأۡتِيَ ٱللَّهُ بِأَمۡرِهِۦۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\n«Агар ота-оналарингиз, болаларингиз, ака-укаларингиз, жуфтларингиз, қариндош-уруғларингиз, касб қилган молларингиз, касод бўлишдан қўрққан тижоратларингиз ва хуш кўрган масканларингиз сизга Аллоҳдан, Унинг Расули ва Унинг йўлида жидду жаҳд қилишдан маҳбуброқ бўлса, у ҳолда, Аллоҳ Ўз амрини келтиргунча кутиб туринг. Аллоҳ фосиқ қавмни ҳидоят қилмас», – деб айт'}, {'chapter': 9, 'verse': 25, 'text': 'لَقَدۡ نَصَرَكُمُ ٱللَّهُ فِي مَوَاطِنَ كَثِيرَةٖ وَيَوۡمَ حُنَيۡنٍ إِذۡ أَعۡجَبَتۡكُمۡ كَثۡرَتُكُمۡ فَلَمۡ تُغۡنِ عَنكُمۡ شَيۡـٔٗا وَضَاقَتۡ عَلَيۡكُمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ ثُمَّ وَلَّيۡتُم مُّدۡبِرِينَ\nБатаҳқиқ, Аллоҳ сизларга кўп жойларда нусрат берди. Ҳунайн кунида ҳам. Ўшанда сизни кўплигингиз мағрур қилган эди. Бас, сизга ҳеч фойда бермади ва кенг ер сизга торлик қилиб қолди. Сўнгра, ортга қараб қочдингиз'}, {'chapter': 9, 'verse': 26, 'text': 'ثُمَّ أَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَنزَلَ جُنُودٗا لَّمۡ تَرَوۡهَا وَعَذَّبَ ٱلَّذِينَ كَفَرُواْۚ وَذَٰلِكَ جَزَآءُ ٱلۡكَٰفِرِينَ\nСўнгра, Аллоҳ сокинлигини Пайғамбарига ва мўминларга туширди ҳамда сиз кўрмайдиган лашкарларни туширди ва куфр келтирганларни азоблади. Ана ўша, кофирларнинг жазосидир'}, {'chapter': 9, 'verse': 27, 'text': 'ثُمَّ يَتُوبُ ٱللَّهُ مِنۢ بَعۡدِ ذَٰلِكَ عَلَىٰ مَن يَشَآءُۗ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nСўнгра, ўшандан кейин Аллоҳ Ўзи хоҳлаган кишиларнинг тавбасини қабул қилди. Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 9, 'verse': 28, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّمَا ٱلۡمُشۡرِكُونَ نَجَسٞ فَلَا يَقۡرَبُواْ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ بَعۡدَ عَامِهِمۡ هَٰذَاۚ وَإِنۡ خِفۡتُمۡ عَيۡلَةٗ فَسَوۡفَ يُغۡنِيكُمُ ٱللَّهُ مِن فَضۡلِهِۦٓ إِن شَآءَۚ إِنَّ ٱللَّهَ عَلِيمٌ حَكِيمٞ\nЭй, иймон келтирганлар! Албатта, мушриклар нажасдирлар, бунга шубҳа йўқ, бу йилларидан кейин Масжидул Ҳаромга яқин келмасинлар. Агар фақирликдан қўрқсангиз, Аллоҳ, Ўзи хоҳласа, сизни тезда фазли ила бой қилиб қўядир. Албатта, Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 29, 'text': 'قَٰتِلُواْ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡيَوۡمِ ٱلۡأٓخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ ٱللَّهُ وَرَسُولُهُۥ وَلَا يَدِينُونَ دِينَ ٱلۡحَقِّ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ حَتَّىٰ يُعۡطُواْ ٱلۡجِزۡيَةَ عَن يَدٖ وَهُمۡ صَٰغِرُونَ\nАллоҳга ва охират кунига иймон келтирмайдиганларга, Аллоҳ ва Унинг Расули ҳаром қилган нарсаларни ҳаром деб билмайдиган ва ҳақ динга юрмайдиган китоб берилганларга қарши, то улар бўйинсунган ҳолларида ўз қўллари билан «жизя» бермагунича, жанг қилинг'}, {'chapter': 9, 'verse': 30, 'text': 'وَقَالَتِ ٱلۡيَهُودُ عُزَيۡرٌ ٱبۡنُ ٱللَّهِ وَقَالَتِ ٱلنَّصَٰرَى ٱلۡمَسِيحُ ٱبۡنُ ٱللَّهِۖ ذَٰلِكَ قَوۡلُهُم بِأَفۡوَٰهِهِمۡۖ يُضَٰهِـُٔونَ قَوۡلَ ٱلَّذِينَ كَفَرُواْ مِن قَبۡلُۚ قَٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ يُؤۡفَكُونَ\nЯҳудийлар: «Узайр Аллоҳнинг ўғлидир», – дедилар. Насоролар: «Масийҳ Аллоҳнинг ўғлидир», – дедилар. Бу оғизлари билан айтган гапларидир. Аввалги куфр келтирганларнинг гапига ўхшатурлар. Аллоҳ уларни лаънатласин. Қаён бурилмоқдалар'}, {'chapter': 9, 'verse': 31, 'text': 'ٱتَّخَذُوٓاْ أَحۡبَارَهُمۡ وَرُهۡبَٰنَهُمۡ أَرۡبَابٗا مِّن دُونِ ٱللَّهِ وَٱلۡمَسِيحَ ٱبۡنَ مَرۡيَمَ وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُوٓاْ إِلَٰهٗا وَٰحِدٗاۖ لَّآ إِلَٰهَ إِلَّا هُوَۚ سُبۡحَٰنَهُۥ عَمَّا يُشۡرِكُونَ\nУлар Аллоҳни қўйиб ўзларининг ҳибр ва роҳибларини ҳамда Масийҳ ибн Марямни Робб тутдилар. Улар фақат битта илоҳдан бошқага ибодат қилмасликка амр қилинган эдилар. Ундан ўзга ибодатга сазовор зот йўқ. У зот улар ширк келтираётган нарсадан покдир'}, {'chapter': 9, 'verse': 32, 'text': 'يُرِيدُونَ أَن يُطۡفِـُٔواْ نُورَ ٱللَّهِ بِأَفۡوَٰهِهِمۡ وَيَأۡبَى ٱللَّهُ إِلَّآ أَن يُتِمَّ نُورَهُۥ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ\nУлар Аллоҳнинг нурини оғизлари билан ўчирмоқчи бўлурлар. Аллоҳ эса, гарчи кофирлар ёқтирмаса ҳам, Ўз нурини батамом қилишдан бошқа ҳар нарсадан бош тортадир'}, {'chapter': 9, 'verse': 33, 'text': 'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ\nУ, гарчи мушриклар ёқтирмаса ҳам, Ўз Расулини ҳидоят ва ҳақ дин билан уни ҳамма диндан устун қилиш учун юборган Зотдир'}, {'chapter': 9, 'verse': 34, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ كَثِيرٗا مِّنَ ٱلۡأَحۡبَارِ وَٱلرُّهۡبَانِ لَيَأۡكُلُونَ أَمۡوَٰلَ ٱلنَّاسِ بِٱلۡبَٰطِلِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِۗ وَٱلَّذِينَ يَكۡنِزُونَ ٱلذَّهَبَ وَٱلۡفِضَّةَ وَلَا يُنفِقُونَهَا فِي سَبِيلِ ٱللَّهِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٖ\nЭй, иймон келтирганлар! Албатта, ҳибр ва роҳибларнинг кўплари одамларнинг молларини ботил йўл билан ерлар ва Аллоҳнинг йўлидан тўсарлар. Олтин ва кумушни хазинага босадиган, Аллоҳнинг йўлида сарфламайдиганларга аламли азобнинг башоратини беравер'}, {'chapter': 9, 'verse': 35, 'text': 'يَوۡمَ يُحۡمَىٰ عَلَيۡهَا فِي نَارِ جَهَنَّمَ فَتُكۡوَىٰ بِهَا جِبَاهُهُمۡ وَجُنُوبُهُمۡ وَظُهُورُهُمۡۖ هَٰذَا مَا كَنَزۡتُمۡ لِأَنفُسِكُمۡ فَذُوقُواْ مَا كُنتُمۡ تَكۡنِزُونَ\nБир куни ўша(олтин-кумуш)ларни жаҳаннам ўтида қизитилур ва пешоналари, ёнбошлари ҳамда орқаларига босилиб: «Мана бу ўзингиз учун тўплаган нарсангиз, бас, энди тўплаб юрган нарсангизни татиб кўринг!» – дейилур'}, {'chapter': 9, 'verse': 36, 'text': 'إِنَّ عِدَّةَ ٱلشُّهُورِ عِندَ ٱللَّهِ ٱثۡنَا عَشَرَ شَهۡرٗا فِي كِتَٰبِ ٱللَّهِ يَوۡمَ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ مِنۡهَآ أَرۡبَعَةٌ حُرُمٞۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُۚ فَلَا تَظۡلِمُواْ فِيهِنَّ أَنفُسَكُمۡۚ وَقَٰتِلُواْ ٱلۡمُشۡرِكِينَ كَآفَّةٗ كَمَا يُقَٰتِلُونَكُمۡ كَآفَّةٗۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ\nАлбатта, Аллоҳнинг китобида ойларнинг сони Аллоҳнинг ҳузурида осмонлару ерни яратган куни ўн икки ой қилиб белгиланган. Улардан тўрттаси (уруш қилиш) ҳаром (ойлар)дир. Мана шу тўғри диндир. У(ой)ларда ўзингизга зулм қилманг. Мушрикларга қарши жам бўлиб, улар сизга қарши жам бўлиб урушаётгандек, уруш қилинг. Ва билингки, албатта, Аллоҳ тақводорлар биландир'}, {'chapter': 9, 'verse': 37, 'text': 'إِنَّمَا ٱلنَّسِيٓءُ زِيَادَةٞ فِي ٱلۡكُفۡرِۖ يُضَلُّ بِهِ ٱلَّذِينَ كَفَرُواْ يُحِلُّونَهُۥ عَامٗا وَيُحَرِّمُونَهُۥ عَامٗا لِّيُوَاطِـُٔواْ عِدَّةَ مَا حَرَّمَ ٱللَّهُ فَيُحِلُّواْ مَا حَرَّمَ ٱللَّهُۚ زُيِّنَ لَهُمۡ سُوٓءُ أَعۡمَٰلِهِمۡۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nАлбатта, «насийу» куфрда зиёда бўлишдан ўзга нарса эмас. Бу билан куфр келтирганлар залолатга кетказилурлар. Аллоҳ ҳаром қилган нарсанинг ададига мослаш учун уни бир йил ҳалол қилурлар, бир йил ҳаром қилурлар. Бас, бу билан Аллоҳ ҳаром қилган нарсани ҳалол қилурлар. Уларга амалларининг ёмонлиги зийнатланди. Аллоҳ кофир қавмларни ҳидоят қилмас'}, {'chapter': 9, 'verse': 38, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ مَا لَكُمۡ إِذَا قِيلَ لَكُمُ ٱنفِرُواْ فِي سَبِيلِ ٱللَّهِ ٱثَّاقَلۡتُمۡ إِلَى ٱلۡأَرۡضِۚ أَرَضِيتُم بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا مِنَ ٱلۡأٓخِرَةِۚ فَمَا مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فِي ٱلۡأٓخِرَةِ إِلَّا قَلِيلٌ\nЭй, иймон келтирганлар! Сизга нима бўлдики, Аллоҳнинг йўлида қўзғалинг, дейилса, ерга ёпишиб олдингиз. Ёки охиратни қўйиб, дунё ҳаётига рози бўлдингизми? Охират олдида бу дунё ҳаёти жуда оз-ку'}, {'chapter': 9, 'verse': 39, 'text': 'إِلَّا تَنفِرُواْ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا وَيَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّوهُ شَيۡـٔٗاۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nАгар қўзғалмасангиз, сизни аламли азоб билан азоблайдир. Ўрнингизга бошқа қавмни келтирадир. Сиз Унга зарар келтира олмассиз. Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 9, 'verse': 40, 'text': 'إِلَّا تَنصُرُوهُ فَقَدۡ نَصَرَهُ ٱللَّهُ إِذۡ أَخۡرَجَهُ ٱلَّذِينَ كَفَرُواْ ثَانِيَ ٱثۡنَيۡنِ إِذۡ هُمَا فِي ٱلۡغَارِ إِذۡ يَقُولُ لِصَٰحِبِهِۦ لَا تَحۡزَنۡ إِنَّ ٱللَّهَ مَعَنَاۖ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَيۡهِ وَأَيَّدَهُۥ بِجُنُودٖ لَّمۡ تَرَوۡهَا وَجَعَلَ كَلِمَةَ ٱلَّذِينَ كَفَرُواْ ٱلسُّفۡلَىٰۗ وَكَلِمَةُ ٱللَّهِ هِيَ ٱلۡعُلۡيَاۗ وَٱللَّهُ عَزِيزٌ حَكِيمٌ\nАгар сиз унга ёрдам бермасангиз, батаҳқиқ, Аллоҳ унга, куфр келтирганлар уни икки кишининг бири бўлган ҳолида чиқарганларида, нусрат берди. Улар икковлон ғорда турганларида, у шеригига: «Хафа бўлма, Аллоҳ, албатта, биз билан», деди. Бас, Аллоҳ унинг устидан Ўз сокинлигини нозил қилди ва сиз кўрмаган лашкарлар билан қўллади. Куфр келтирганлар калимасини паст қилди. Аллоҳнинг калимаси эса, ўзи юқори. Аллоҳ ғолиб ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 41, 'text': 'ٱنفِرُواْ خِفَافٗا وَثِقَالٗا وَجَٰهِدُواْ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ فِي سَبِيلِ ٱللَّهِۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nЕнгил бўлса ҳам, оғир бўлса ҳам, қўзғалингиз ва Аллоҳнинг йўлида молларингиз ва жонларингиз ила жидду жаҳд қилингиз. Агар билсангиз, шу ишингиз ўзингиз учун яхшидир'}, {'chapter': 9, 'verse': 42, 'text': 'لَوۡ كَانَ عَرَضٗا قَرِيبٗا وَسَفَرٗا قَاصِدٗا لَّٱتَّبَعُوكَ وَلَٰكِنۢ بَعُدَتۡ عَلَيۡهِمُ ٱلشُّقَّةُۚ وَسَيَحۡلِفُونَ بِٱللَّهِ لَوِ ٱسۡتَطَعۡنَا لَخَرَجۡنَا مَعَكُمۡ يُهۡلِكُونَ أَنفُسَهُمۡ وَٱللَّهُ يَعۡلَمُ إِنَّهُمۡ لَكَٰذِبُونَ\nАгар осон манфаат ва яқин сафар бўлганида, албатта, сенга эргашар эдилар. Лекин уларга масофа узоқ кўринди. Улар тезда Аллоҳ номи билан қасам ичиб: «Агар қодир бўлганимизда, албатта, сиз билан чиқар эдик», – дерлар. Улар ўзларини ҳалок қилурлар. Ҳолбуки, Аллоҳ улар ёлғончи эканларини албатта биладир'}, {'chapter': 9, 'verse': 43, 'text': 'عَفَا ٱللَّهُ عَنكَ لِمَ أَذِنتَ لَهُمۡ حَتَّىٰ يَتَبَيَّنَ لَكَ ٱلَّذِينَ صَدَقُواْ وَتَعۡلَمَ ٱلۡكَٰذِبِينَ\nАллоҳ сени афв қилди. Нима учун уларга, то сенга ростгўй бўлганлар равшан бўлмай туриб ва ёлғончиларни билмасингдан туриб, изн бердинг'}, {'chapter': 9, 'verse': 44, 'text': 'لَا يَسۡتَـٔۡذِنُكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۗ وَٱللَّهُ عَلِيمُۢ بِٱلۡمُتَّقِينَ\nАллоҳга ва охират кунига иймон келтирганлар сендан моллари ва жонлари билан жидду жаҳд қилишдан қолишга изн сўрамаслар. Аллоҳ тақводорларни билгувчи Зотдир'}, {'chapter': 9, 'verse': 45, 'text': 'إِنَّمَا يَسۡتَـٔۡذِنُكَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَٱرۡتَابَتۡ قُلُوبُهُمۡ فَهُمۡ فِي رَيۡبِهِمۡ يَتَرَدَّدُونَ\nАлбатта фақат Аллоҳга ва охират кунига ишонмайдиганлар ва қалблари шубҳага тўлганларгина сендан изн сўрарлар. Бас, улар шубҳаларида иккиланаверадилар'}, {'chapter': 9, 'verse': 46, 'text': '۞وَلَوۡ أَرَادُواْ ٱلۡخُرُوجَ لَأَعَدُّواْ لَهُۥ عُدَّةٗ وَلَٰكِن كَرِهَ ٱللَّهُ ٱنۢبِعَاثَهُمۡ فَثَبَّطَهُمۡ وَقِيلَ ٱقۡعُدُواْ مَعَ ٱلۡقَٰعِدِينَ\nАгар чиқишни хоҳлаганларида, унга тайёргарлик кўрар эдилар. Лекин Аллоҳ уларнинг ҳаракатланишларини ёқтирмай тутиб қолди. Уларга: «Ўтирувчилар билан ўтиринглар», деди'}, {'chapter': 9, 'verse': 47, 'text': 'لَوۡ خَرَجُواْ فِيكُم مَّا زَادُوكُمۡ إِلَّا خَبَالٗا وَلَأَوۡضَعُواْ خِلَٰلَكُمۡ يَبۡغُونَكُمُ ٱلۡفِتۡنَةَ وَفِيكُمۡ سَمَّـٰعُونَ لَهُمۡۗ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nАгар улар ичингизда чиққанларида ҳам сизга ёмонликдан бошқа нарсани зиёда қилмасдилар, тезлаб орангизга фитна солишга уринардилар. Ҳолбуки, ичингизда уларга қулоқ солувчилар бор эди. Аллоҳ золимларни билгувчи Зотдир'}, {'chapter': 9, 'verse': 48, 'text': 'لَقَدِ ٱبۡتَغَوُاْ ٱلۡفِتۡنَةَ مِن قَبۡلُ وَقَلَّبُواْ لَكَ ٱلۡأُمُورَ حَتَّىٰ جَآءَ ٱلۡحَقُّ وَظَهَرَ أَمۡرُ ٱللَّهِ وَهُمۡ كَٰرِهُونَ\nБатаҳқиқ, бундан аввал ҳам, токи улар ёқтирмасалар-да, ҳақ келиб, Аллоҳнинг иши устун бўлгунича, фитна қўзиб, сенга ишларни ағдар-тўнтар қилишар эди'}, {'chapter': 9, 'verse': 49, 'text': 'وَمِنۡهُم مَّن يَقُولُ ٱئۡذَن لِّي وَلَا تَفۡتِنِّيٓۚ أَلَا فِي ٱلۡفِتۡنَةِ سَقَطُواْۗ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِٱلۡكَٰفِرِينَ\nУлардан: «Менга изн бер, мени фитнага солма», дейдигани ҳам бор. Ажабо, улар фитнага тушмадиларми? Албатта, жаҳаннам кофирларни ўраб олгувчидир'}, {'chapter': 9, 'verse': 50, 'text': 'إِن تُصِبۡكَ حَسَنَةٞ تَسُؤۡهُمۡۖ وَإِن تُصِبۡكَ مُصِيبَةٞ يَقُولُواْ قَدۡ أَخَذۡنَآ أَمۡرَنَا مِن قَبۡلُ وَيَتَوَلَّواْ وَّهُمۡ فَرِحُونَ\nАгар сенга яхшилик етса, уларни хафа қиладир. Агар сенга мусийбат етса: «Олдиндан ишнинг олдини олган эдик», – деб хурсанд бўлиб юз ўгириб кетарлар'}, {'chapter': 9, 'verse': 51, 'text': 'قُل لَّن يُصِيبَنَآ إِلَّا مَا كَتَبَ ٱللَّهُ لَنَا هُوَ مَوۡلَىٰنَاۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nСен: «Бизга Аллоҳ ёзиб қўйган нарсадан ўзгаси зинҳор етмайди. У бизнинг хожамиздир. Мўминлар Аллоҳгагина таваккал қилсинлар», – деб айт'}, {'chapter': 9, 'verse': 52, 'text': 'قُلۡ هَلۡ تَرَبَّصُونَ بِنَآ إِلَّآ إِحۡدَى ٱلۡحُسۡنَيَيۡنِۖ وَنَحۡنُ نَتَرَبَّصُ بِكُمۡ أَن يُصِيبَكُمُ ٱللَّهُ بِعَذَابٖ مِّنۡ عِندِهِۦٓ أَوۡ بِأَيۡدِينَاۖ فَتَرَبَّصُوٓاْ إِنَّا مَعَكُم مُّتَرَبِّصُونَ\nСен: «Сиз биз учун фақат икки яхшиликдан бирини кутмоқдасиз, холос. Биз эса, Аллоҳ сизни Ўз ҳузурида бўладиган ёки бизнинг қўлимиздан етадиган азобга дучор қилишини кутмоқдамиз. Бас, кутаверинглар, биз ҳам сизлар билан бирга кутувчилармиз», – деб айт'}, {'chapter': 9, 'verse': 53, 'text': 'قُلۡ أَنفِقُواْ طَوۡعًا أَوۡ كَرۡهٗا لَّن يُتَقَبَّلَ مِنكُمۡ إِنَّكُمۡ كُنتُمۡ قَوۡمٗا فَٰسِقِينَ\nХоҳлаб ёки хоҳламай нафақа қилаверинглар. Сиздан зинҳор қабул қилинмас. Сизлар, шубҳасиз, фосиқ қавм бўлдингиз'}, {'chapter': 9, 'verse': 54, 'text': 'وَمَا مَنَعَهُمۡ أَن تُقۡبَلَ مِنۡهُمۡ نَفَقَٰتُهُمۡ إِلَّآ أَنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَبِرَسُولِهِۦ وَلَا يَأۡتُونَ ٱلصَّلَوٰةَ إِلَّا وَهُمۡ كُسَالَىٰ وَلَا يُنفِقُونَ إِلَّا وَهُمۡ كَٰرِهُونَ\nУларнинг нафақаларининг қабул этилишидан ман қилган нарса фақат Аллоҳга ва Унинг Расулига куфр келтирганлари, намозга доимо дангаса ҳолларида келганлари ва доимо ёқтирмай инфоқ қилганларидир'}, {'chapter': 9, 'verse': 55, 'text': 'فَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُعَذِّبَهُم بِهَا فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ\nУларнинг моллари ва болалари сени ажаблантирмасин. Аслида, Аллоҳ ўша нарсалар сабабли уларни ҳаёти дунёда азоблашни ва кофир ҳолларида жонлари чиқишини хоҳлайди, холос'}, {'chapter': 9, 'verse': 56, 'text': 'وَيَحۡلِفُونَ بِٱللَّهِ إِنَّهُمۡ لَمِنكُمۡ وَمَا هُم مِّنكُمۡ وَلَٰكِنَّهُمۡ قَوۡمٞ يَفۡرَقُونَ\nУлар ўзлари, албатта, сиздан эканликларига Аллоҳ номи ила қасам ичарлар. Ҳолбуки, улар сиздан эмас. Лекин улар қўрқадиган қавмдирлар'}, {'chapter': 9, 'verse': 57, 'text': 'لَوۡ يَجِدُونَ مَلۡجَـًٔا أَوۡ مَغَٰرَٰتٍ أَوۡ مُدَّخَلٗا لَّوَلَّوۡاْ إِلَيۡهِ وَهُمۡ يَجۡمَحُونَ\nАгар бирор бошпаноҳми, ғорми ёки кириб оладиган жойми топсалар, шошилиб ўша томонга юзланарлар'}, {'chapter': 9, 'verse': 58, 'text': 'وَمِنۡهُم مَّن يَلۡمِزُكَ فِي ٱلصَّدَقَٰتِ فَإِنۡ أُعۡطُواْ مِنۡهَا رَضُواْ وَإِن لَّمۡ يُعۡطَوۡاْ مِنۡهَآ إِذَا هُمۡ يَسۡخَطُونَ\nУлардан садақалар ишида сенга айб қўядиганлари ҳам бор. Агар ўша садақалардан уларга берилса, улар рози бўларлар. Агар берилмаса, кўрибсанки, ғазабланарлар'}, {'chapter': 9, 'verse': 59, 'text': 'وَلَوۡ أَنَّهُمۡ رَضُواْ مَآ ءَاتَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ وَقَالُواْ حَسۡبُنَا ٱللَّهُ سَيُؤۡتِينَا ٱللَّهُ مِن فَضۡلِهِۦ وَرَسُولُهُۥٓ إِنَّآ إِلَى ٱللَّهِ رَٰغِبُونَ\nАгар Аллоҳ ва Унинг Расули уларга берган нарсаларидан рози бўлганларида ва «Аллоҳ бизга кифоя қилур, бизга тезда Аллоҳ ва Унинг Расули ўз фазлидан беради. «Албатта, биз Аллоҳга рағбат қилувчилармиз», деганларида, яхши бўларди'}, {'chapter': 9, 'verse': 60, 'text': '۞إِنَّمَا ٱلصَّدَقَٰتُ لِلۡفُقَرَآءِ وَٱلۡمَسَٰكِينِ وَٱلۡعَٰمِلِينَ عَلَيۡهَا وَٱلۡمُؤَلَّفَةِ قُلُوبُهُمۡ وَفِي ٱلرِّقَابِ وَٱلۡغَٰرِمِينَ وَفِي سَبِيلِ ٱللَّهِ وَٱبۡنِ ٱلسَّبِيلِۖ فَرِيضَةٗ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nАлбатта, садақалар фақирларга, мискинларга, садақа ишида ишловчиларга, кўнгилларини улфат қилинадиганларга, қул озод қилишга, қарздорларга, Аллоҳнинг йўлига ва йўл ўғлиларига берилиши Аллоҳ томонидан фарз қилингандир. Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 61, 'text': 'وَمِنۡهُمُ ٱلَّذِينَ يُؤۡذُونَ ٱلنَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٞۚ قُلۡ أُذُنُ خَيۡرٖ لَّكُمۡ يُؤۡمِنُ بِٱللَّهِ وَيُؤۡمِنُ لِلۡمُؤۡمِنِينَ وَرَحۡمَةٞ لِّلَّذِينَ ءَامَنُواْ مِنكُمۡۚ وَٱلَّذِينَ يُؤۡذُونَ رَسُولَ ٱللَّهِ لَهُمۡ عَذَابٌ أَلِيمٞ\nУлардан Пайғамбарга озор берадиган ва: «У қулоқдир» дейдиганлар бор. «Сизга яхшилик нарсаларга қулоқдир. Аллоҳга иймон келтирадир, мўминларга ишонадир, сизлардан иймон келтирганларга раҳматдир. Аллоҳнинг Расулига озор берадиганларга аламли азоб бордир», – деб айт'}, {'chapter': 9, 'verse': 62, 'text': 'يَحۡلِفُونَ بِٱللَّهِ لَكُمۡ لِيُرۡضُوكُمۡ وَٱللَّهُ وَرَسُولُهُۥٓ أَحَقُّ أَن يُرۡضُوهُ إِن كَانُواْ مُؤۡمِنِينَ\nУлар сизларни рози қилиш учун Аллоҳнинг номи ила қасам ичарлар. Агар мўмин бўлсалар, Аллоҳни ва Унинг Расулини рози қилмоқлари ҳақли эди'}, {'chapter': 9, 'verse': 63, 'text': 'أَلَمۡ يَعۡلَمُوٓاْ أَنَّهُۥ مَن يُحَادِدِ ٱللَّهَ وَرَسُولَهُۥ فَأَنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدٗا فِيهَاۚ ذَٰلِكَ ٱلۡخِزۡيُ ٱلۡعَظِيمُ\nУлар кимки Аллоҳга ва Унинг Расулига хилоф қилса, албатта, унга жаҳаннам оташ бўлиб, унда абадий қолишини, ана шу катта шармандалик эканлигини билмасларми'}, {'chapter': 9, 'verse': 64, 'text': 'يَحۡذَرُ ٱلۡمُنَٰفِقُونَ أَن تُنَزَّلَ عَلَيۡهِمۡ سُورَةٞ تُنَبِّئُهُم بِمَا فِي قُلُوبِهِمۡۚ قُلِ ٱسۡتَهۡزِءُوٓاْ إِنَّ ٱللَّهَ مُخۡرِجٞ مَّا تَحۡذَرُونَ\nМунофиқлар уларга қалбларидаги нарсанинг хабарини берадиган сура нозил бўлишидан қўрқарлар. Сен: «Истеҳзо қилаверинглар. Аллоҳ сиз қўрқаётган нарсани албатта чиқаргувчидир», – деб айт'}, {'chapter': 9, 'verse': 65, 'text': 'وَلَئِن سَأَلۡتَهُمۡ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلۡعَبُۚ قُلۡ أَبِٱللَّهِ وَءَايَٰتِهِۦ وَرَسُولِهِۦ كُنتُمۡ تَسۡتَهۡزِءُونَ\nАгар улардан сўрасанг, албатта: «Биз фақат ўзимизча гап қилиб ўйнаётган эдик, холос», – дейишади. Сен: «Аллоҳни ва Унинг оятларини истеҳзо қилаётган эдингизми?» – деб айт'}, {'chapter': 9, 'verse': 66, 'text': 'لَا تَعۡتَذِرُواْ قَدۡ كَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡۚ إِن نَّعۡفُ عَن طَآئِفَةٖ مِّنكُمۡ نُعَذِّبۡ طَآئِفَةَۢ بِأَنَّهُمۡ كَانُواْ مُجۡرِمِينَ\nУзр айтманглар. Батаҳқиқ, иймонингиздан кейин куфр келтирдингиз. Агар сиздан бир тоифани афв этсак, бошқа тоифани жиноятчи бўлганлари учун азоблаймиз'}, {'chapter': 9, 'verse': 67, 'text': 'ٱلۡمُنَٰفِقُونَ وَٱلۡمُنَٰفِقَٰتُ بَعۡضُهُم مِّنۢ بَعۡضٖۚ يَأۡمُرُونَ بِٱلۡمُنكَرِ وَيَنۡهَوۡنَ عَنِ ٱلۡمَعۡرُوفِ وَيَقۡبِضُونَ أَيۡدِيَهُمۡۚ نَسُواْ ٱللَّهَ فَنَسِيَهُمۡۚ إِنَّ ٱلۡمُنَٰفِقِينَ هُمُ ٱلۡفَٰسِقُونَ\nМунофиқ эркаклар ва мунофиқ аёллар ҳаммалари бирдирлар, ёмонликка буюрурлар, яхшиликдан қайтарурлар ва қўлларини маҳкам юмарлар. Аллоҳни унутдилар, бас, У ҳам уларни унутди. Албатта, мунофиқлар — улар фосиқлардир'}, {'chapter': 9, 'verse': 68, 'text': 'وَعَدَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡكُفَّارَ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۚ هِيَ حَسۡبُهُمۡۚ وَلَعَنَهُمُ ٱللَّهُۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ\nАллоҳ мунофиқ эркакларга, мунофиқ аёлларга ва кофирларга жаҳаннам оташини ваъда қилди. Унда абадий қолурлар. Ўша улар учун етарлидир. Ҳамда Аллоҳ уларни лаънатлади. Уларга доимий азоб бордир'}, {'chapter': 9, 'verse': 69, 'text': 'كَٱلَّذِينَ مِن قَبۡلِكُمۡ كَانُوٓاْ أَشَدَّ مِنكُمۡ قُوَّةٗ وَأَكۡثَرَ أَمۡوَٰلٗا وَأَوۡلَٰدٗا فَٱسۡتَمۡتَعُواْ بِخَلَٰقِهِمۡ فَٱسۡتَمۡتَعۡتُم بِخَلَٰقِكُمۡ كَمَا ٱسۡتَمۡتَعَ ٱلَّذِينَ مِن قَبۡلِكُم بِخَلَٰقِهِمۡ وَخُضۡتُمۡ كَٱلَّذِي خَاضُوٓاْۚ أُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nЎзингиздан аввалгиларга ўхшайсиз. Улар сизлардан кўра қувватлироқ, молу дунё ва бола-чақалари кўпроқ эдилар. Бас, ўз насибалари ила маза қилдилар. Ўзингиздан олдингилар насибалари ила маза қилганларидек, сиз ҳам ўз насибангиз ила маза қилдингиз. Улар шўнғигандек сиз ҳам шўнғидингиз. Ана ўшаларнинг амаллари дунёю охиратда ҳабата бўлди. Ана ўшалар ўзлари зиён кўргувчилардир'}, {'chapter': 9, 'verse': 70, 'text': 'أَلَمۡ يَأۡتِهِمۡ نَبَأُ ٱلَّذِينَ مِن قَبۡلِهِمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَقَوۡمِ إِبۡرَٰهِيمَ وَأَصۡحَٰبِ مَدۡيَنَ وَٱلۡمُؤۡتَفِكَٰتِۚ أَتَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nУларга ўзларидан олдин ўтганларнинг, Нуҳ, Од, Самуд қавмининг ва Иброҳим қавмининг, Мадян эгаларининг ва тўнтарилган (қишлоқ)ларнинг хабари келмадими? Уларга Пайғамбарлари очиқ-ойдин ҳужжатлар билан келдилар. Бас, Аллоҳ уларга зулм қилмас эди. Лекин улар ўзларига зулм қилар эдилар'}, {'chapter': 9, 'verse': 71, 'text': 'وَٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ يَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَيُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَيُطِيعُونَ ٱللَّهَ وَرَسُولَهُۥٓۚ أُوْلَـٰٓئِكَ سَيَرۡحَمُهُمُ ٱللَّهُۗ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nМўмин эркаклар ва мўмина аёллар бир-бирларига дўстдирлар. Улар яхшиликка буюрурлар, ёмонликдан қайтарурлар, намозни тўкис адо этурлар, закотни берурлар ҳамда Аллоҳга ва Унинг Расулига итоат қилурлар. Ана ўшаларга Аллоҳ тезда раҳим қилур. Албатта, Аллоҳ ғолиб ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 72, 'text': 'وَعَدَ ٱللَّهُ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّـٰتِ عَدۡنٖۚ وَرِضۡوَٰنٞ مِّنَ ٱللَّهِ أَكۡبَرُۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАллоҳ мўмин эр ва мўмина аёлларга остидан анҳорлар оқиб турган жаннатларни, уларда абадий қолишни ва барқарорлик жаннатидан покиза масканларни ваъда қилди. Аллоҳдан бўладиган розилик эса, энг улуғидир. Ана ўша улкан ютуқдир'}, {'chapter': 9, 'verse': 73, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ جَٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَٰفِقِينَ وَٱغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nЭй Пайғамбар, кофирлар ва мунофиқларга қарши жидду жаҳд қил ва уларга нисбатан қаттиққўл бўл. Уларнинг жойи жаҳаннамдир. У қандай ҳам ёмон қайтадиган жой'}, {'chapter': 9, 'verse': 74, 'text': 'يَحۡلِفُونَ بِٱللَّهِ مَا قَالُواْ وَلَقَدۡ قَالُواْ كَلِمَةَ ٱلۡكُفۡرِ وَكَفَرُواْ بَعۡدَ إِسۡلَٰمِهِمۡ وَهَمُّواْ بِمَا لَمۡ يَنَالُواْۚ وَمَا نَقَمُوٓاْ إِلَّآ أَنۡ أَغۡنَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ مِن فَضۡلِهِۦۚ فَإِن يَتُوبُواْ يَكُ خَيۡرٗا لَّهُمۡۖ وَإِن يَتَوَلَّوۡاْ يُعَذِّبۡهُمُ ٱللَّهُ عَذَابًا أَلِيمٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَمَا لَهُمۡ فِي ٱلۡأَرۡضِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nУлар гапирмаганлари ҳақида Аллоҳнинг номи ила қасам ичарлар. Батаҳқиқ, куфр калимасини айтдилар ва Исломларидан кейин куфр келтирдилар ҳамда ета олмаган нарсаларига қасд қилдилар. Ҳолбуки, улар Аллоҳ ва Унинг Расули уларни Ўз фазлидан бой қилиб қўйганларидан бошқа «айб» топа олмадилар. Агар тавба қилсалар, ўзларига яхши бўладир. Гар юз ўгириб кетсалар, Аллоҳ уларни бу дунёю охиратда аламли азоб билан азоблайдир. Ер юзида уларга дўст ҳам, ёрдамчи ҳам йўқ'}, {'chapter': 9, 'verse': 75, 'text': '۞وَمِنۡهُم مَّنۡ عَٰهَدَ ٱللَّهَ لَئِنۡ ءَاتَىٰنَا مِن فَضۡلِهِۦ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ ٱلصَّـٰلِحِينَ\nУлардан: «Агар У зот Ўз фазлидан берса, албатта, садақа қиламиз ва, албатта, аҳли солиҳлардан бўламиз», – деб Аллоҳга аҳд берувчилар бор'}, {'chapter': 9, 'verse': 76, 'text': 'فَلَمَّآ ءَاتَىٰهُم مِّن فَضۡلِهِۦ بَخِلُواْ بِهِۦ وَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ\nУ зот Ўз фазлидан берган чоғда эса, бахиллик қилиб, юз ўгириб, орқага қараб кетарлар'}, {'chapter': 9, 'verse': 77, 'text': 'فَأَعۡقَبَهُمۡ نِفَاقٗا فِي قُلُوبِهِمۡ إِلَىٰ يَوۡمِ يَلۡقَوۡنَهُۥ بِمَآ أَخۡلَفُواْ ٱللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُواْ يَكۡذِبُونَ\nБас, Аллоҳга берган ваъдаларига хилоф қилганлари ва ёлғон гапирганлари учун Унга рўбарў бўладиган кунгача қалбларида нифоқни оқибат қилиб қўйди'}, {'chapter': 9, 'verse': 78, 'text': 'أَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ سِرَّهُمۡ وَنَجۡوَىٰهُمۡ وَأَنَّ ٱللَّهَ عَلَّـٰمُ ٱلۡغُيُوبِ\nУлар Аллоҳ уларнинг сирларини ҳам, яширин гапларини ҳам албатта билишини ва Аллоҳ, албатта, ғайбларни билгувчи Зот эканини билмасмидилар'}, {'chapter': 9, 'verse': 79, 'text': 'ٱلَّذِينَ يَلۡمِزُونَ ٱلۡمُطَّوِّعِينَ مِنَ ٱلۡمُؤۡمِنِينَ فِي ٱلصَّدَقَٰتِ وَٱلَّذِينَ لَا يَجِدُونَ إِلَّا جُهۡدَهُمۡ فَيَسۡخَرُونَ مِنۡهُمۡ سَخِرَ ٱللَّهُ مِنۡهُمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ\nМўминлардан кўнгилли бўлиб садақа қилувчиларни ва ўз кучлари етгандан бошқани топа олмайдиганларни айблайдиган ва масхара қиладиганларни Аллоҳ масхара қиладир ва уларга аламли азоб бўладир'}, {'chapter': 9, 'verse': 80, 'text': 'ٱسۡتَغۡفِرۡ لَهُمۡ أَوۡ لَا تَسۡتَغۡفِرۡ لَهُمۡ إِن تَسۡتَغۡفِرۡ لَهُمۡ سَبۡعِينَ مَرَّةٗ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡۚ ذَٰلِكَ بِأَنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَرَسُولِهِۦۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\nУлар учун истиғфор айтсанг ҳам ёки истиғфор айтмасанг ҳам, барибир. Агар улар учун етмиш марта истиғфор айтсанг ҳам, Аллоҳ уларни зинҳор мағфират қилмас. Бу, уларнинг Аллоҳ ва Унинг Расулига куфр келтирганлари учундир. Аллоҳ фосиқ қавмларни ҳидоят қилмас'}, {'chapter': 9, 'verse': 81, 'text': 'فَرِحَ ٱلۡمُخَلَّفُونَ بِمَقۡعَدِهِمۡ خِلَٰفَ رَسُولِ ٱللَّهِ وَكَرِهُوٓاْ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِ وَقَالُواْ لَا تَنفِرُواْ فِي ٱلۡحَرِّۗ قُلۡ نَارُ جَهَنَّمَ أَشَدُّ حَرّٗاۚ لَّوۡ كَانُواْ يَفۡقَهُونَ\nУлар, Аллоҳнинг Расулига хилоф қилиб ортда қолганлар ўтирган жойларида хурсанд бўлдилар ва Аллоҳнинг йўлида моллари ва жонлари билан жидду жаҳд қилишни ёқтирмадилар ҳамда: «Иссиқда қўзғалманглар», – дедилар. «Жаҳаннамнинг оташи иссиқроқ, агар тушунсалар», – деб айт'}, {'chapter': 9, 'verse': 82, 'text': 'فَلۡيَضۡحَكُواْ قَلِيلٗا وَلۡيَبۡكُواْ كَثِيرٗا جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ\nБас, қилган касблари туфайли оз кулиб, кўп йиғласинлар'}, {'chapter': 9, 'verse': 83, 'text': 'فَإِن رَّجَعَكَ ٱللَّهُ إِلَىٰ طَآئِفَةٖ مِّنۡهُمۡ فَٱسۡتَـٔۡذَنُوكَ لِلۡخُرُوجِ فَقُل لَّن تَخۡرُجُواْ مَعِيَ أَبَدٗا وَلَن تُقَٰتِلُواْ مَعِيَ عَدُوًّاۖ إِنَّكُمۡ رَضِيتُم بِٱلۡقُعُودِ أَوَّلَ مَرَّةٖ فَٱقۡعُدُواْ مَعَ ٱلۡخَٰلِفِينَ\nБас, Аллоҳ сени улардан бир тоифасига қайтарса-ю, улар сендан (урушга) чиқишга изн сўрасалар: «Мен билан ҳеч қачон чиқмайсизлар ва мен билан бирга ҳеч қачон жанг қилмайсизлар, сизлар биринчи мартада ўтиришга рози бўлдингиз, бас, ортда қолувчилар билан ўтираверинглар», – деб айт'}, {'chapter': 9, 'verse': 84, 'text': 'وَلَا تُصَلِّ عَلَىٰٓ أَحَدٖ مِّنۡهُم مَّاتَ أَبَدٗا وَلَا تَقُمۡ عَلَىٰ قَبۡرِهِۦٓۖ إِنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَرَسُولِهِۦ وَمَاتُواْ وَهُمۡ فَٰسِقُونَ\nВа ҳеч қачон улардан ўлган бирортасига ҳам (жаноза) намоз ўқима, қабри устида турма. Чунки, улар Аллоҳга ва Унинг Расулига куфр келтирдилар ҳамда фосиқ ҳолларида ўлдилар'}, {'chapter': 9, 'verse': 85, 'text': 'وَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَأَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ ٱللَّهُ أَن يُعَذِّبَهُم بِهَا فِي ٱلدُّنۡيَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ\nУларнинг мол-дунёлари ва бола-чақалари сени ажаблантирмасин. Аслида, Аллоҳ ўша нарсалар сабабли уларни дунёда азоблашни ва кофир ҳолларида жонлари чиқишини хоҳлайди, холос'}, {'chapter': 9, 'verse': 86, 'text': 'وَإِذَآ أُنزِلَتۡ سُورَةٌ أَنۡ ءَامِنُواْ بِٱللَّهِ وَجَٰهِدُواْ مَعَ رَسُولِهِ ٱسۡتَـٔۡذَنَكَ أُوْلُواْ ٱلطَّوۡلِ مِنۡهُمۡ وَقَالُواْ ذَرۡنَا نَكُن مَّعَ ٱلۡقَٰعِدِينَ\n«Аллоҳга иймон келтиринглар ва Унинг Расули билан жидду жаҳд қилинглар», – деб сура нозил қилинган пайтда улардан бой-зодагонлари: «Бизни тек қўй, ўтирувчилар билан бирга бўлайлик», – деб изн сўрайдилар'}, {'chapter': 9, 'verse': 87, 'text': 'رَضُواْ بِأَن يَكُونُواْ مَعَ ٱلۡخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ\nУлар хотин-халажлар билан бирга бўлишга рози бўлдилар ва қалбларига муҳр босилди. Бас, улар англамаслар'}, {'chapter': 9, 'verse': 88, 'text': 'لَٰكِنِ ٱلرَّسُولُ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ جَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ وَأُوْلَـٰٓئِكَ لَهُمُ ٱلۡخَيۡرَٰتُۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nЛекин Пайғамбар ва у билан бирга иймон келтирганлар моллари ва жонлари билан жидду жаҳд қилдилар. Ана ўшаларга, ўзларига яхшиликлар бўладир. Ана ўшалар, ўзлари нажот топгувчилардир'}, {'chapter': 9, 'verse': 89, 'text': 'أَعَدَّ ٱللَّهُ لَهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАллоҳ улар учун остидан анҳорлар оқиб турган жаннатларни тайёрлаб қўядир. Унда абадий қолурлар. Ана шу буюк ютуқдир'}, {'chapter': 9, 'verse': 90, 'text': 'وَجَآءَ ٱلۡمُعَذِّرُونَ مِنَ ٱلۡأَعۡرَابِ لِيُؤۡذَنَ لَهُمۡ وَقَعَدَ ٱلَّذِينَ كَذَبُواْ ٱللَّهَ وَرَسُولَهُۥۚ سَيُصِيبُ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٞ\nАъробийлардан узр айтувчилар уларга изн берилишини сўраб келдилар. Аллоҳга ва Расулига ёлғон гапирганлар эса, ўтириб қолдилар. Улардан кофир бўлганларга аламли азоб етадир'}, {'chapter': 9, 'verse': 91, 'text': 'لَّيۡسَ عَلَى ٱلضُّعَفَآءِ وَلَا عَلَى ٱلۡمَرۡضَىٰ وَلَا عَلَى ٱلَّذِينَ لَا يَجِدُونَ مَا يُنفِقُونَ حَرَجٌ إِذَا نَصَحُواْ لِلَّهِ وَرَسُولِهِۦۚ مَا عَلَى ٱلۡمُحۡسِنِينَ مِن سَبِيلٖۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЗаифҳолларга, беморларга ва сарф-харажат қиладиган нарса топа олмаётганларга, Аллоҳга ва Расулига ихлосли бўлсалар, насиҳат қилсалар, гуноҳ йўқ. Чиройли амал қилувчиларни айблашга йўл йўқ. Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 9, 'verse': 92, 'text': 'وَلَا عَلَى ٱلَّذِينَ إِذَا مَآ أَتَوۡكَ لِتَحۡمِلَهُمۡ قُلۡتَ لَآ أَجِدُ مَآ أَحۡمِلُكُمۡ عَلَيۡهِ تَوَلَّواْ وَّأَعۡيُنُهُمۡ تَفِيضُ مِنَ ٱلدَّمۡعِ حَزَنًا أَلَّا يَجِدُواْ مَا يُنفِقُونَ\nЯна, сенга улов беришинг учун келганларида: «Сизларни миндиргани нарса топа олмаяпман», деганингда ғамгин бўлиб, кўзларидан ёшлари тўкилиб, орқага қайтиб кетганларга ҳам, сарф-харажат қиладиган нарса топа олмаганлари учун гуноҳ йўқ'}, {'chapter': 9, 'verse': 93, 'text': '۞إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ وَهُمۡ أَغۡنِيَآءُۚ رَضُواْ بِأَن يَكُونُواْ مَعَ ٱلۡخَوَالِفِ وَطَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَعۡلَمُونَ\nБой-бадавлат бўла туриб сендан изн сўрайдиганларни айблашга йўл бор. Улар хотин-халажлар билан бирга бўлишга рози бўлдилар. Аллоҳ уларнинг қалбларига муҳр босди. Бас, улар билмаслар'}, {'chapter': 9, 'verse': 94, 'text': 'يَعۡتَذِرُونَ إِلَيۡكُمۡ إِذَا رَجَعۡتُمۡ إِلَيۡهِمۡۚ قُل لَّا تَعۡتَذِرُواْ لَن نُّؤۡمِنَ لَكُمۡ قَدۡ نَبَّأَنَا ٱللَّهُ مِنۡ أَخۡبَارِكُمۡۚ وَسَيَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nҲузурларига қайтиб келганингизда, сизга узр айтарлар. Сен: «Узр айтманглар, биз сизларга ҳеч қачон ишонмаймиз. Батаҳқиқ, Аллоҳ бизга сизларнинг хабарларингизни берди. Албатта, Аллоҳ ва Унинг Расули амалингизни кўрадир, сўнгра, ғайбни ва шаҳодатни билгувчи Зотга қайтариласизлар. Бас, У сизга қилиб юрган амалингизнинг хабарини берадир», – деб айт'}, {'chapter': 9, 'verse': 95, 'text': 'سَيَحۡلِفُونَ بِٱللَّهِ لَكُمۡ إِذَا ٱنقَلَبۡتُمۡ إِلَيۡهِمۡ لِتُعۡرِضُواْ عَنۡهُمۡۖ فَأَعۡرِضُواْ عَنۡهُمۡۖ إِنَّهُمۡ رِجۡسٞۖ وَمَأۡوَىٰهُمۡ جَهَنَّمُ جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ\nУларнинг ҳузурларига қайтиб келганингизда уларни айбламаслигингиз учун сизга Аллоҳнинг номи ила қасам ичарлар. Бас, улардан юз ўгиринглар! Албатта, улар нопокдирлар. Қилиб юрган касблари жазосига жойлари жаҳаннамдадир'}, {'chapter': 9, 'verse': 96, 'text': 'يَحۡلِفُونَ لَكُمۡ لِتَرۡضَوۡاْ عَنۡهُمۡۖ فَإِن تَرۡضَوۡاْ عَنۡهُمۡ فَإِنَّ ٱللَّهَ لَا يَرۡضَىٰ عَنِ ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ\nУлар сизлар улардан рози бўлишингиз учун сизга қасам ичарлар. Агарчи сиз улардан рози бўлсангиз ҳам, Аллоҳ фосиқ қавмлардан рози бўлмас'}, {'chapter': 9, 'verse': 97, 'text': 'ٱلۡأَعۡرَابُ أَشَدُّ كُفۡرٗا وَنِفَاقٗا وَأَجۡدَرُ أَلَّا يَعۡلَمُواْ حُدُودَ مَآ أَنزَلَ ٱللَّهُ عَلَىٰ رَسُولِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nАъробийлар куфр ва нифоқда ашаддийроқ ва Аллоҳ Ўз Пайғамбарига нозил қилган нарсанинг чегараларини билмасликка лойиқроқдирлар. Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 98, 'text': 'وَمِنَ ٱلۡأَعۡرَابِ مَن يَتَّخِذُ مَا يُنفِقُ مَغۡرَمٗا وَيَتَرَبَّصُ بِكُمُ ٱلدَّوَآئِرَۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nАъробийлардан қилган инфоқни зиён деб биладиган ва сизга бало-офат келишини кутиб турадиганлари ҳам бор. Ёмон балолари ўзларига урсин! Аллоҳ эшитгувчи ва билгувчи Зотдир'}, {'chapter': 9, 'verse': 99, 'text': 'وَمِنَ ٱلۡأَعۡرَابِ مَن يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَيَتَّخِذُ مَا يُنفِقُ قُرُبَٰتٍ عِندَ ٱللَّهِ وَصَلَوَٰتِ ٱلرَّسُولِۚ أَلَآ إِنَّهَا قُرۡبَةٞ لَّهُمۡۚ سَيُدۡخِلُهُمُ ٱللَّهُ فِي رَحۡمَتِهِۦٓۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nАъробийлардан Аллоҳга ва охират кунига иймон келтирадиган ҳамда инфоқ қиладиган нарсасини Аллоҳга қурбат ҳосил қилиш ва Расулнинг дуосига мушарраф этиш воситаси деб биладиганлари ҳам бор. Билсинларки, албатта, ўша нарсалар улар учун қурбатдир. Албатта, Аллоҳ уларни Ўз раҳматига киритадир. Албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 9, 'verse': 100, 'text': 'وَٱلسَّـٰبِقُونَ ٱلۡأَوَّلُونَ مِنَ ٱلۡمُهَٰجِرِينَ وَٱلۡأَنصَارِ وَٱلَّذِينَ ٱتَّبَعُوهُم بِإِحۡسَٰنٖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُ وَأَعَدَّ لَهُمۡ جَنَّـٰتٖ تَجۡرِي تَحۡتَهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nБиринчи пешқадам муҳожирлар ва ансорийлар ва уларга яхшилик билан эргашганлар. Аллоҳ улардан рози бўлди, улар ҳам Аллоҳдан рози бўлдилар. Уларга остидан анҳорлар оқиб турган жаннатларни тайёрлаб қўйди. Уларда абадий қолурлар. Ана ўша улкан ютуқдир'}, {'chapter': 9, 'verse': 101, 'text': 'وَمِمَّنۡ حَوۡلَكُم مِّنَ ٱلۡأَعۡرَابِ مُنَٰفِقُونَۖ وَمِنۡ أَهۡلِ ٱلۡمَدِينَةِ مَرَدُواْ عَلَى ٱلنِّفَاقِ لَا تَعۡلَمُهُمۡۖ نَحۡنُ نَعۡلَمُهُمۡۚ سَنُعَذِّبُهُم مَّرَّتَيۡنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٖ\nАтрофингиздаги аъробийлардан мунофиқлар бор. Мадина аҳлидан ҳам. Улар нифоқда сийқаси чиққанлардир. Сен уларни билмассан, уларни Биз билармиз. Албатта, уларни икки марта азоблармиз. Сўнгра, улкан азобга қайтарилурлар'}, {'chapter': 9, 'verse': 102, 'text': 'وَءَاخَرُونَ ٱعۡتَرَفُواْ بِذُنُوبِهِمۡ خَلَطُواْ عَمَلٗا صَٰلِحٗا وَءَاخَرَ سَيِّئًا عَسَى ٱللَّهُ أَن يَتُوبَ عَلَيۡهِمۡۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nБошқалар эса, гуноҳларини эътироф қилдилар: улар яхши ва ёмон амални аралаштириб қилганлардир. Шоядки, Аллоҳ уларнинг тавбасини қабул қилса. Албатта, Аллоҳ мағфиратли ва раҳимли Зотдир'}, {'chapter': 9, 'verse': 103, 'text': 'خُذۡ مِنۡ أَمۡوَٰلِهِمۡ صَدَقَةٗ تُطَهِّرُهُمۡ وَتُزَكِّيهِم بِهَا وَصَلِّ عَلَيۡهِمۡۖ إِنَّ صَلَوٰتَكَ سَكَنٞ لَّهُمۡۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\nУларнинг молларидан садақа ол. Бу билан уларни поклайсан, тозалайсан ва уларнинг ҳаққига дуо қил. Албатта, сенинг дуоинг улар учун сокинликдир. Аллоҳ эшитгувчи ва билгувчи Зотдир'}, {'chapter': 9, 'verse': 104, 'text': 'أَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ هُوَ يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَأۡخُذُ ٱلصَّدَقَٰتِ وَأَنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nУлар, албатта, Аллоҳ Ўз бандаларидан тавбани қабул қилишини ва садақаларни олишини ва, албатта, Аллоҳнинг Ўзи тавбаларни кўплаб қабул қилгувчи ва раҳимли Зот эканини билмасларми'}, {'chapter': 9, 'verse': 105, 'text': 'وَقُلِ ٱعۡمَلُواْ فَسَيَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ وَٱلۡمُؤۡمِنُونَۖ وَسَتُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nСен: «Амал қилинглар! Бас, албатта, Аллоҳ, Унинг Расули ва мўминлар амалингизни кўрадилар. Ва, албатта, ғайбни ва шаҳодатни билгувчи Зотга қайтариласизлар. Бас, У сизларга қилиб юрган амалларингизнинг хабарини беради», – деб айт'}, {'chapter': 9, 'verse': 106, 'text': 'وَءَاخَرُونَ مُرۡجَوۡنَ لِأَمۡرِ ٱللَّهِ إِمَّا يُعَذِّبُهُمۡ وَإِمَّا يَتُوبُ عَلَيۡهِمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nВа Аллоҳнинг амрига ҳавола қилинган бошқалар бор. Уларни ёки азоблайдир ёки тавбаларини қабул қиладир. Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 107, 'text': 'وَٱلَّذِينَ ٱتَّخَذُواْ مَسۡجِدٗا ضِرَارٗا وَكُفۡرٗا وَتَفۡرِيقَۢا بَيۡنَ ٱلۡمُؤۡمِنِينَ وَإِرۡصَادٗا لِّمَنۡ حَارَبَ ٱللَّهَ وَرَسُولَهُۥ مِن قَبۡلُۚ وَلَيَحۡلِفُنَّ إِنۡ أَرَدۡنَآ إِلَّا ٱلۡحُسۡنَىٰۖ وَٱللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ\nЗиён, куфр, мўминлар орасига тафриқа солиш ва бундан олдин Аллоҳга ва Унинг Расулига уруш очган кимсани кутиш жойи бўлиши учун масжид қурганлар, албатта, биз яхшиликдан бошқа нарсани ирода қилганимиз йўқ, деб қасам ичарлар. Аллоҳ шоҳидлик берадики, улар, албатта, ёлғончилардир'}, {'chapter': 9, 'verse': 108, 'text': 'لَا تَقُمۡ فِيهِ أَبَدٗاۚ لَّمَسۡجِدٌ أُسِّسَ عَلَى ٱلتَّقۡوَىٰ مِنۡ أَوَّلِ يَوۡمٍ أَحَقُّ أَن تَقُومَ فِيهِۚ فِيهِ رِجَالٞ يُحِبُّونَ أَن يَتَطَهَّرُواْۚ وَٱللَّهُ يُحِبُّ ٱلۡمُطَّهِّرِينَ\nСен у(масжид)да ҳеч қачон турма! Албатта, биринчи кундан тақво асосида қурилган масжидда турмоқлигинг ҳақдир. Унда покланишни севадиган кишилар бор. Аллоҳ эса, покланувчиларни севадир'}, {'chapter': 9, 'verse': 109, 'text': 'أَفَمَنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ تَقۡوَىٰ مِنَ ٱللَّهِ وَرِضۡوَٰنٍ خَيۡرٌ أَم مَّنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ شَفَا جُرُفٍ هَارٖ فَٱنۡهَارَ بِهِۦ فِي نَارِ جَهَنَّمَۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nБиносини Аллоҳга бўлган тақво ва Унинг розилиги асосида қурган кимса яхшими ёки биносини емирилаётган жар ёқасини асос қилиб қуриб, у билан бирга жаҳаннам ўтига қулаган кимса яхшими? Ва Аллоҳ золим қавмларни ҳидоят қилмас'}, {'chapter': 9, 'verse': 110, 'text': 'لَا يَزَالُ بُنۡيَٰنُهُمُ ٱلَّذِي بَنَوۡاْ رِيبَةٗ فِي قُلُوبِهِمۡ إِلَّآ أَن تَقَطَّعَ قُلُوبُهُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nБарпо қилган бинолари қалбларида шак-шубҳа ҳолида бардавом қоладир. Магар қалблар қиймаланиб кетсагина, шак-шубҳа қолмас. Аллоҳ билгувчи ва ҳикматли Зотдир'}, {'chapter': 9, 'verse': 111, 'text': '۞إِنَّ ٱللَّهَ ٱشۡتَرَىٰ مِنَ ٱلۡمُؤۡمِنِينَ أَنفُسَهُمۡ وَأَمۡوَٰلَهُم بِأَنَّ لَهُمُ ٱلۡجَنَّةَۚ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِ فَيَقۡتُلُونَ وَيُقۡتَلُونَۖ وَعۡدًا عَلَيۡهِ حَقّٗا فِي ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِيلِ وَٱلۡقُرۡءَانِۚ وَمَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ مِنَ ٱللَّهِۚ فَٱسۡتَبۡشِرُواْ بِبَيۡعِكُمُ ٱلَّذِي بَايَعۡتُم بِهِۦۚ وَذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАлбатта, Аллоҳ мўминлардан уларнинг жонлари ва молларини жаннат эвазига сотиб олди. Улар Аллоҳнинг йўлида жанг қилурлар, ўлдирурлар ва ўлдирилурлар. Бу, Тавротда, Инжилда ва Қуръонда Унинг зиммасидаги ҳақ ваъдадир. Аллоҳдан ҳам аҳдига вафодорроқ ким бор? Ким Аллоҳга берган аҳдига вафо қилса, бас, қилган савдоларингиздан шод бўлаверинг. Ана шунинг ўзи улкан ютуқдир'}, {'chapter': 9, 'verse': 112, 'text': 'ٱلتَّـٰٓئِبُونَ ٱلۡعَٰبِدُونَ ٱلۡحَٰمِدُونَ ٱلسَّـٰٓئِحُونَ ٱلرَّـٰكِعُونَ ٱلسَّـٰجِدُونَ ٱلۡأٓمِرُونَ بِٱلۡمَعۡرُوفِ وَٱلنَّاهُونَ عَنِ ٱلۡمُنكَرِ وَٱلۡحَٰفِظُونَ لِحُدُودِ ٱللَّهِۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nУлар тавба қилувчилар, ибодат қилувчилар, ҳамд айтувчилар, рўза тутувчилар, рукуъ қилувчилар, сажда қилувчилар, яхшиликка буюриб, ёмонликдан қайтарувчилар, Аллоҳнинг чегарасида турувчилардир. Ва мўминларга хушхабар бер'}, {'chapter': 9, 'verse': 113, 'text': 'مَا كَانَ لِلنَّبِيِّ وَٱلَّذِينَ ءَامَنُوٓاْ أَن يَسۡتَغۡفِرُواْ لِلۡمُشۡرِكِينَ وَلَوۡ كَانُوٓاْ أُوْلِي قُرۡبَىٰ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمۡ أَنَّهُمۡ أَصۡحَٰبُ ٱلۡجَحِيمِ\nПайғамбар ва мўминлар учун дўзах эгалари эканлиги аён бўлган мушрикларга, агар яқин қариндошлари бўлса ҳам, истиғфор айтишлари дуруст эмасдир'}, {'chapter': 9, 'verse': 114, 'text': 'وَمَا كَانَ ٱسۡتِغۡفَارُ إِبۡرَٰهِيمَ لِأَبِيهِ إِلَّا عَن مَّوۡعِدَةٖ وَعَدَهَآ إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُۥٓ أَنَّهُۥ عَدُوّٞ لِّلَّهِ تَبَرَّأَ مِنۡهُۚ إِنَّ إِبۡرَٰهِيمَ لَأَوَّـٰهٌ حَلِيمٞ\nИброҳимнинг отаси учун айтган истиғфори унга берган ваъдаси устидан эди, холос. Вақтики, унга отасининг Аллоҳга душман эканлиги аён бўлганда, ундан воз кечди. Албатта, Иброҳим тазарруъли ва ҳалиймдир'}, {'chapter': 9, 'verse': 115, 'text': 'وَمَا كَانَ ٱللَّهُ لِيُضِلَّ قَوۡمَۢا بَعۡدَ إِذۡ هَدَىٰهُمۡ حَتَّىٰ يُبَيِّنَ لَهُم مَّا يَتَّقُونَۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАллоҳ бир қавмни ҳидоятга бошлаганидан сўнг, уларга сақланишлари лозим бўлган нарсани баён қилмасдан туриб, залолатга кетказмас. Албатта, Аллоҳ ҳар бир нарсани билгувчи Зотдир'}, {'chapter': 9, 'verse': 116, 'text': 'إِنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۚ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nАлбатта, осмонлару ернинг мулки Аллоҳникидир. У тирилтирар ва ўлдирар. Сизларга Аллоҳдан бошқа дўст ҳам йўқ, ёрдамчи ҳам йўқ'}, {'chapter': 9, 'verse': 117, 'text': 'لَّقَد تَّابَ ٱللَّهُ عَلَى ٱلنَّبِيِّ وَٱلۡمُهَٰجِرِينَ وَٱلۡأَنصَارِ ٱلَّذِينَ ٱتَّبَعُوهُ فِي سَاعَةِ ٱلۡعُسۡرَةِ مِنۢ بَعۡدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٖ مِّنۡهُمۡ ثُمَّ تَابَ عَلَيۡهِمۡۚ إِنَّهُۥ بِهِمۡ رَءُوفٞ رَّحِيمٞ\nБатаҳқиқ, Аллоҳ Пайғамбарининг, қийинчилик вақтида эргашган муҳожир ва ансорларнинг тавбасини қабул қилди. Улардан баъзиларининг қалблари тойиб кетай дегандан сўнг тавбаларини қабул қилди. Албатта, У Зот уларга марҳаматли ва меҳрибондир'}, {'chapter': 9, 'verse': 118, 'text': 'وَعَلَى ٱلثَّلَٰثَةِ ٱلَّذِينَ خُلِّفُواْ حَتَّىٰٓ إِذَا ضَاقَتۡ عَلَيۡهِمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ وَضَاقَتۡ عَلَيۡهِمۡ أَنفُسُهُمۡ وَظَنُّوٓاْ أَن لَّا مَلۡجَأَ مِنَ ٱللَّهِ إِلَّآ إِلَيۡهِ ثُمَّ تَابَ عَلَيۡهِمۡ لِيَتُوبُوٓاْۚ إِنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nВа ортда қолган уч кишига ҳам. Уларга кенг ер тор бўлиб, юраклари сиқилиб, Аллоҳдан қочиб, ўзгага бориб бўлмаслигига ишонганларидан сўнг, тавба қилишлари учун тавба эшиги очилди. Албатта, Аллоҳ тавбаларни қабул қилувчи ва раҳимли Зотдир'}, {'chapter': 9, 'verse': 119, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَكُونُواْ مَعَ ٱلصَّـٰدِقِينَ\nЭй, иймон келтирганлар, Аллоҳга тақво қилинглар ва содиқлар ила бирга бўлинглар'}, {'chapter': 9, 'verse': 120, 'text': 'مَا كَانَ لِأَهۡلِ ٱلۡمَدِينَةِ وَمَنۡ حَوۡلَهُم مِّنَ ٱلۡأَعۡرَابِ أَن يَتَخَلَّفُواْ عَن رَّسُولِ ٱللَّهِ وَلَا يَرۡغَبُواْ بِأَنفُسِهِمۡ عَن نَّفۡسِهِۦۚ ذَٰلِكَ بِأَنَّهُمۡ لَا يُصِيبُهُمۡ ظَمَأٞ وَلَا نَصَبٞ وَلَا مَخۡمَصَةٞ فِي سَبِيلِ ٱللَّهِ وَلَا يَطَـُٔونَ مَوۡطِئٗا يَغِيظُ ٱلۡكُفَّارَ وَلَا يَنَالُونَ مِنۡ عَدُوّٖ نَّيۡلًا إِلَّا كُتِبَ لَهُم بِهِۦ عَمَلٞ صَٰلِحٌۚ إِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nМадина аҳлига ва уларнинг атрофидаги аъробийларга Расулуллоҳдан четда қолишлари ва ўз жонларини кўзлаб, унинг жонига эътибор қилмасликлари дуруст эмас. Зеро, уларга Аллоҳнинг йўлида ташналик, чарчоқ, очлик етса, кофирларнинг ғазабини чиқарадиган бирон қадам боссалар, душманга бирон зиён етказсалар, албатта, уларга солиҳ амал ёзиладир. Албатта, Аллоҳ яхшилик қилувчиларнинг ажрини зое қилмас'}, {'chapter': 9, 'verse': 121, 'text': 'وَلَا يُنفِقُونَ نَفَقَةٗ صَغِيرَةٗ وَلَا كَبِيرَةٗ وَلَا يَقۡطَعُونَ وَادِيًا إِلَّا كُتِبَ لَهُمۡ لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا كَانُواْ يَعۡمَلُونَ\nКичик ёки катта нафақа қилсалар ҳам, бирон водийни кесиб ўтсалар ҳам, албатта, уларга (солиҳ амал) ёзилар. Аллоҳ уларга қилган энг гўзал амалларининг мукофотини берар'}, {'chapter': 9, 'verse': 122, 'text': '۞وَمَا كَانَ ٱلۡمُؤۡمِنُونَ لِيَنفِرُواْ كَآفَّةٗۚ فَلَوۡلَا نَفَرَ مِن كُلِّ فِرۡقَةٖ مِّنۡهُمۡ طَآئِفَةٞ لِّيَتَفَقَّهُواْ فِي ٱلدِّينِ وَلِيُنذِرُواْ قَوۡمَهُمۡ إِذَا رَجَعُوٓاْ إِلَيۡهِمۡ لَعَلَّهُمۡ يَحۡذَرُونَ\nМўминларнинг ҳаммалари (жангга) қўзғалишлари лозим эмас. Ҳар жамоадан бир нафар тоифа бўлсайди. Улар динни чуқур англаб, қайтиб келган вақтларида қавмларини огоҳлантирардилар. Шоядки, улар ҳазир бўлсалар'}, {'chapter': 9, 'verse': 123, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ قَٰتِلُواْ ٱلَّذِينَ يَلُونَكُم مِّنَ ٱلۡكُفَّارِ وَلۡيَجِدُواْ فِيكُمۡ غِلۡظَةٗۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ\nЭй, иймон келтирганлар, сизга яқин бўлган кофирларга қарши жанг қилинг. Улар сиздаги шиддатни кўрсинлар. Ва билингки, албатта, Аллоҳ тақводорлар билан биргадир'}, {'chapter': 9, 'verse': 124, 'text': 'وَإِذَا مَآ أُنزِلَتۡ سُورَةٞ فَمِنۡهُم مَّن يَقُولُ أَيُّكُمۡ زَادَتۡهُ هَٰذِهِۦٓ إِيمَٰنٗاۚ فَأَمَّا ٱلَّذِينَ ءَامَنُواْ فَزَادَتۡهُمۡ إِيمَٰنٗا وَهُمۡ يَسۡتَبۡشِرُونَ\nУлардан, бирон сура нозил қилинган вақтда: «Бу сиздан кимнинг иймонини зиёда қилди?» – дейдиган кимсалар бор. Бас, иймон келтирганларнинг иймонини зиёда қилди ва улар шоду хуррам бўлдилар'}, {'chapter': 9, 'verse': 125, 'text': 'وَأَمَّا ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ فَزَادَتۡهُمۡ رِجۡسًا إِلَىٰ رِجۡسِهِمۡ وَمَاتُواْ وَهُمۡ كَٰفِرُونَ\nАммо, қалбларида касали борларнинг ифлосликлари устига ифлослик зиёда қилди ва улар кофир ҳолларида ўлурлар'}, {'chapter': 9, 'verse': 126, 'text': 'أَوَلَا يَرَوۡنَ أَنَّهُمۡ يُفۡتَنُونَ فِي كُلِّ عَامٖ مَّرَّةً أَوۡ مَرَّتَيۡنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمۡ يَذَّكَّرُونَ\nУлар ҳар йили бир ёки икки марта фитна қилинаётганларига қарамасларми? Шунда ҳам на тавба қилмаслар ва на эсламаслар'}, {'chapter': 9, 'verse': 127, 'text': 'وَإِذَا مَآ أُنزِلَتۡ سُورَةٞ نَّظَرَ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ هَلۡ يَرَىٰكُم مِّنۡ أَحَدٖ ثُمَّ ٱنصَرَفُواْۚ صَرَفَ ٱللَّهُ قُلُوبَهُم بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ\nУлар, бирон сура нозил бўлган вақтда, сизни биров кўрмадими, деб бир-бирларига назар соладилар-да, сўнгра, бурилиб кетарлар. Улар фаҳмламайдиган қавм бўлганлари учун, Аллоҳ қалбларини буриб қўйди'}, {'chapter': 9, 'verse': 128, 'text': 'لَقَدۡ جَآءَكُمۡ رَسُولٞ مِّنۡ أَنفُسِكُمۡ عَزِيزٌ عَلَيۡهِ مَا عَنِتُّمۡ حَرِيصٌ عَلَيۡكُم بِٱلۡمُؤۡمِنِينَ رَءُوفٞ رَّحِيمٞ\nБатаҳқиқ, сизларга ўзингиздан бўлган, сизнинг машаққат чекишингиз унинг учун оғир бўлган, сизнинг (саодатга етишишингизга) ташна, мўминларга марҳаматли, меҳрибон бўлган Пайғамбар келди'}, {'chapter': 9, 'verse': 129, 'text': 'فَإِن تَوَلَّوۡاْ فَقُلۡ حَسۡبِيَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَهُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ\nБас, юз ўгириб кетсалар, сен: «Менга Аллоҳнинг Ўзи етарли. Ундан ўзга ибодатга сазовор Зот йўқ, Унга таваккал қилдим, У улуғ аршнинг Роббисидир», – дегин'}], '10': [{'chapter': 10, 'verse': 1, 'text': 'الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡحَكِيمِ\nАлиф. Лаам. Ро. Булар, ҳикматли китобнинг оятларидир'}, {'chapter': 10, 'verse': 2, 'text': 'أَكَانَ لِلنَّاسِ عَجَبًا أَنۡ أَوۡحَيۡنَآ إِلَىٰ رَجُلٖ مِّنۡهُمۡ أَنۡ أَنذِرِ ٱلنَّاسَ وَبَشِّرِ ٱلَّذِينَ ءَامَنُوٓاْ أَنَّ لَهُمۡ قَدَمَ صِدۡقٍ عِندَ رَبِّهِمۡۗ قَالَ ٱلۡكَٰفِرُونَ إِنَّ هَٰذَا لَسَٰحِرٞ مُّبِينٌ\nОдамларга ўзларидан бўлган бир кишига ваҳий юбориб: «Одамларни огоҳлантир ва иймон келтирганларга Роббилари ҳузурида улар учун собитқадамлик борлигининг башоратини бер», деганимиз ажабланарли бўлдими? Кофирлар: «Албатта, бу очиқ-ойдин сеҳргардир», дедилар'}, {'chapter': 10, 'verse': 3, 'text': 'إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يُدَبِّرُ ٱلۡأَمۡرَۖ مَا مِن شَفِيعٍ إِلَّا مِنۢ بَعۡدِ إِذۡنِهِۦۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ فَٱعۡبُدُوهُۚ أَفَلَا تَذَكَّرُونَ\nАлбатта, Роббингиз осмонлару ерни олти кунда яратган, сўнг, (Зоти ва улуғлигига муносиб суратда) аршга юксалган Аллоҳдир. У ишнинг тадбирини қиладир. Унинг изнисиз ҳеч бир шафоатчи бўлмас. Ана ўша Аллоҳ Роббингиздир. Бас, Унга ибодат қилинглар. Эсламайсизларми'}, {'chapter': 10, 'verse': 4, 'text': 'إِلَيۡهِ مَرۡجِعُكُمۡ جَمِيعٗاۖ وَعۡدَ ٱللَّهِ حَقًّاۚ إِنَّهُۥ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ بِٱلۡقِسۡطِۚ وَٱلَّذِينَ كَفَرُواْ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ\nБарчангизнинг қайтишингиз Унгадир. Аллоҳнинг ваъдаси ҳақдир. Албатта, У аввал бошда (барчани) Ўзи яратадир. Сўнгра, иймон келтирган ва яхши амал қилганларни адолат-ла мукофотлаш учун уларни қайтарадир. Кофир бўлганлар эса, куфр келтирганлари учун, уларга қайноқ сувдан шароб ва аламли азоб бордир'}, {'chapter': 10, 'verse': 5, 'text': 'هُوَ ٱلَّذِي جَعَلَ ٱلشَّمۡسَ ضِيَآءٗ وَٱلۡقَمَرَ نُورٗا وَقَدَّرَهُۥ مَنَازِلَ لِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ مَا خَلَقَ ٱللَّهُ ذَٰلِكَ إِلَّا بِٱلۡحَقِّۚ يُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nУ қуёшни зиё ва ойни нур қилган ҳамда сизлар йилларнинг ададини ва ҳисобини билишингиз учун унинг манзилларини ўлчовли қилган Зотдир. Аллоҳ ўшани фақат ҳақ ила яратди. У биладиган қавмлар учун оятларини батафсил баён қиладир'}, {'chapter': 10, 'verse': 6, 'text': 'إِنَّ فِي ٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَا خَلَقَ ٱللَّهُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَّقُونَ\nАлбатта, кеча ва кундузнинг алмашиб туришида ва Аллоҳ осмонлару ерда яратган нарсаларда тақво қиладиган қавмлар учун оят — белгилар бордир'}, {'chapter': 10, 'verse': 7, 'text': 'إِنَّ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا وَرَضُواْ بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَٱطۡمَأَنُّواْ بِهَا وَٱلَّذِينَ هُمۡ عَنۡ ءَايَٰتِنَا غَٰفِلُونَ\nАлбатта, Биз ила мулоқот бўлишини умид қилмаганлар ва бу дунё ҳаётидан рози бўлиб, у билан хотиржам қолганлар ҳамда оятларимиздан ғофил бўлганлар'}, {'chapter': 10, 'verse': 8, 'text': 'أُوْلَـٰٓئِكَ مَأۡوَىٰهُمُ ٱلنَّارُ بِمَا كَانُواْ يَكۡسِبُونَ\nАна ўшаларнинг жойлари, қилган касблари туфайли, жаҳаннамдир'}, {'chapter': 10, 'verse': 9, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ يَهۡدِيهِمۡ رَبُّهُم بِإِيمَٰنِهِمۡۖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ فِي جَنَّـٰتِ ٱلنَّعِيمِ\nАлбатта, иймон келтирганлар ва яхши амаллар қилганларни Роббилари, иймонлари сабабли, ҳидоятга бошлайдир. Наъийм жаннатларидалар, остларидан анҳорлар оқиб турадир'}, {'chapter': 10, 'verse': 10, 'text': 'دَعۡوَىٰهُمۡ فِيهَا سُبۡحَٰنَكَ ٱللَّهُمَّ وَتَحِيَّتُهُمۡ فِيهَا سَلَٰمٞۚ وَءَاخِرُ دَعۡوَىٰهُمۡ أَنِ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nУ ердаги дуолари: «Аллоҳим, Сени поклаб ёд этамиз»дир. У ердаги сўрашишлари «Салом»дир. Охирги дуолари эса: «Оламларнинг Робби Аллоҳга ҳамд бўлсин»дир'}, {'chapter': 10, 'verse': 11, 'text': '۞وَلَوۡ يُعَجِّلُ ٱللَّهُ لِلنَّاسِ ٱلشَّرَّ ٱسۡتِعۡجَالَهُم بِٱلۡخَيۡرِ لَقُضِيَ إِلَيۡهِمۡ أَجَلُهُمۡۖ فَنَذَرُ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАгар Аллоҳ одамларга яхшиликни тез келтиргани каби ёмонликни ҳам тезлатса эди, ажаллари битган бўлур эди. Бас, Биз ила мулоқотда бўлишдан умиди йўқларни туғёнларида ташлаб қўямиз, адашиб-улоқиб юраверадилар'}, {'chapter': 10, 'verse': 12, 'text': 'وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ٱلضُّرُّ دَعَانَا لِجَنۢبِهِۦٓ أَوۡ قَاعِدًا أَوۡ قَآئِمٗا فَلَمَّا كَشَفۡنَا عَنۡهُ ضُرَّهُۥ مَرَّ كَأَن لَّمۡ يَدۡعُنَآ إِلَىٰ ضُرّٖ مَّسَّهُۥۚ كَذَٰلِكَ زُيِّنَ لِلۡمُسۡرِفِينَ مَا كَانُواْ يَعۡمَلُونَ\nҚачонки инсонга зарар етса, Бизга ёнбошлаган, ўтирган ёки турган ҳолида дуо қиладир. Ундан зарарини кетказсак, худди ўзига етган зарар ҳақида Бизга дуо қилмагандек, кетаверадир. Ана шундай қилиб, исрофчиларга қилаётган амаллари зийнатланиб кўрсатилди'}, {'chapter': 10, 'verse': 13, 'text': 'وَلَقَدۡ أَهۡلَكۡنَا ٱلۡقُرُونَ مِن قَبۡلِكُمۡ لَمَّا ظَلَمُواْ وَجَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ وَمَا كَانُواْ لِيُؤۡمِنُواْۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ\nБатаҳқиқ, сиздан олдинги асрларни зулм қилган чоғларида ва Пайғамбарлари очиқ-ойдин ҳужжатлар билан келганларида иймон келтирмайдиган бўлганларида ҳалок қилганмиз. Жиноятчи қавмларни ана шундай жазолаймиз'}, {'chapter': 10, 'verse': 14, 'text': 'ثُمَّ جَعَلۡنَٰكُمۡ خَلَـٰٓئِفَ فِي ٱلۡأَرۡضِ مِنۢ بَعۡدِهِمۡ لِنَنظُرَ كَيۡفَ تَعۡمَلُونَ\nСўнгра, қандай амал қилишингизга назар солишимиз учун уларнинг ортидан сизларни ер юзида ўринбосарлар қилдик'}, {'chapter': 10, 'verse': 15, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَاتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا ٱئۡتِ بِقُرۡءَانٍ غَيۡرِ هَٰذَآ أَوۡ بَدِّلۡهُۚ قُلۡ مَا يَكُونُ لِيٓ أَنۡ أُبَدِّلَهُۥ مِن تِلۡقَآيِٕ نَفۡسِيٓۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۖ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ\nҚачонки, уларга очиқ-ойдин оятларимиз тиловат қилинганида, Бизга мулоқот бўлишдан умиди йўқлар: «Бундан бошқа Қуръон келтир ёки уни алмаштир», дедилар. Сен: «Мен ўзимча уни алмаштира олмасман. Мен фақат ўзимга ваҳий қилинган нарсага эргашаман, холос. Албатта, мен Роббимга осийлик қилсам, улуғ куннинг азобидан қўрқаман», деб айт'}, {'chapter': 10, 'verse': 16, 'text': 'قُل لَّوۡ شَآءَ ٱللَّهُ مَا تَلَوۡتُهُۥ عَلَيۡكُمۡ وَلَآ أَدۡرَىٰكُم بِهِۦۖ فَقَدۡ لَبِثۡتُ فِيكُمۡ عُمُرٗا مِّن قَبۡلِهِۦٓۚ أَفَلَا تَعۡقِلُونَ\nСен: «Агар Аллоҳ хоҳлаганида, уни сизларга тиловат қилмас ва уни сизларга ўргатмас эди. Батаҳқиқ, мен орангизда ундан олдин ҳам умр кечирдим. Ақл ишлатиб кўрмайсизларми?» деб айт'}, {'chapter': 10, 'verse': 17, 'text': 'فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡمُجۡرِمُونَ\nАллоҳга ёлғон тўқиган ёки Унинг оятларини ёлғонга чиқарган кимсадан ҳам золимроқ одам борми?! Албатта, жиноятчилар нажот топмаслар'}, {'chapter': 10, 'verse': 18, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡ وَيَقُولُونَ هَـٰٓؤُلَآءِ شُفَعَـٰٓؤُنَا عِندَ ٱللَّهِۚ قُلۡ أَتُنَبِّـُٔونَ ٱللَّهَ بِمَا لَا يَعۡلَمُ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУлар Аллоҳни қўйиб, ўзларига зарар ҳам, манфаат ҳам етказа олмайдиган нарсаларга ибодат қилурлар ва: «Ана ўшалар бизнинг Аллоҳ ҳузуридаги шафоатчиларимиздир!» дерлар. Сен: «Аллоҳга У осмонлару ерда билмайдиган нарсаларнинг хабарини бермоқчимисизлар?!» деб айт. Аллоҳ улар келтираётган ширкдан пок ва юксак Зотдир'}, {'chapter': 10, 'verse': 19, 'text': 'وَمَا كَانَ ٱلنَّاسُ إِلَّآ أُمَّةٗ وَٰحِدَةٗ فَٱخۡتَلَفُواْۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡ فِيمَا فِيهِ يَخۡتَلِفُونَ\nОдамлар фақат бир уммат бўлган эдилар. Сўнгра, ихтилофга тушдилар. Агар Роббинг томонидан айтилган сўз бўлмаганида, улар ихтилоф қилаётган нарса ҳақида ораларида ҳукм қилинган бўлар эди'}, {'chapter': 10, 'verse': 20, 'text': 'وَيَقُولُونَ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۖ فَقُلۡ إِنَّمَا ٱلۡغَيۡبُ لِلَّهِ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ\nВа улар: «Унга Роббидан бир белги — мўъжиза тушса эди», дерлар. Бас, Сен: «Fайб Аллоҳникидир. Интизор бўлиб туринг, мен ҳам сиз ила интизор бўлувчиларданман», деб айт'}, {'chapter': 10, 'verse': 21, 'text': 'وَإِذَآ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ مِّنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُمۡ إِذَا لَهُم مَّكۡرٞ فِيٓ ءَايَاتِنَاۚ قُلِ ٱللَّهُ أَسۡرَعُ مَكۡرًاۚ إِنَّ رُسُلَنَا يَكۡتُبُونَ مَا تَمۡكُرُونَ\nҚачонки, одамларга уларга етган зарардан сўнг раҳматни тотдирсак, баногоҳ, уларда оятларимиз ҳақида макр бўлур. «Аллоҳ макрда тезкордир. Албатта, элчиларимиз қилаётган макрларингизни ёзиб борурлар», дегин'}, {'chapter': 10, 'verse': 22, 'text': 'هُوَ ٱلَّذِي يُسَيِّرُكُمۡ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِۖ حَتَّىٰٓ إِذَا كُنتُمۡ فِي ٱلۡفُلۡكِ وَجَرَيۡنَ بِهِم بِرِيحٖ طَيِّبَةٖ وَفَرِحُواْ بِهَا جَآءَتۡهَا رِيحٌ عَاصِفٞ وَجَآءَهُمُ ٱلۡمَوۡجُ مِن كُلِّ مَكَانٖ وَظَنُّوٓاْ أَنَّهُمۡ أُحِيطَ بِهِمۡ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ لَئِنۡ أَنجَيۡتَنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ\nУ сизларни қуруқликда ва денгизда юргизадиган Зотдир. Токи сизлар кемада бўлганингизда ва у(кема)лар яхши еллар ила уларни олиб кетганида ҳамда улар бундан хурсанд бўлганларида, қаттиқ шамол келур ва уларга ҳар томондан тўлқин келур. Ўзларининг қуршовда қолганларини англаганларида, Аллоҳга чин ихлос билан: «Агар бизни мана шундан қутқазсанг, албатта, шукр келтирувчилардан бўлурмиз», деб дуо қилурлар'}, {'chapter': 10, 'verse': 23, 'text': 'فَلَمَّآ أَنجَىٰهُمۡ إِذَا هُمۡ يَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۗ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَا بَغۡيُكُمۡ عَلَىٰٓ أَنفُسِكُمۖ مَّتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ إِلَيۡنَا مَرۡجِعُكُمۡ فَنُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nБас, қачонки У зот уларга нажот берса, кўрибсизки, улар ер юзида ноҳақдан зулм қилурлар. Эй одамлар! Бу зулмингиз фақат ўз зиёнингизгадир. Бу, ҳаёти дунё матоҳи, холос. Сўнгра, Бизга қайтишингиз бор. Бас, Биз нима амал қилганингизнинг хабарини берурмиз'}, {'chapter': 10, 'verse': 24, 'text': 'إِنَّمَا مَثَلُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ مِمَّا يَأۡكُلُ ٱلنَّاسُ وَٱلۡأَنۡعَٰمُ حَتَّىٰٓ إِذَآ أَخَذَتِ ٱلۡأَرۡضُ زُخۡرُفَهَا وَٱزَّيَّنَتۡ وَظَنَّ أَهۡلُهَآ أَنَّهُمۡ قَٰدِرُونَ عَلَيۡهَآ أَتَىٰهَآ أَمۡرُنَا لَيۡلًا أَوۡ نَهَارٗا فَجَعَلۡنَٰهَا حَصِيدٗا كَأَن لَّمۡ تَغۡنَ بِٱلۡأَمۡسِۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَتَفَكَّرُونَ\nАлбатта, дунё ҳаётининг мисоли худди Биз осмондан туширган сувга ўхшайдир. Бас, одамлар ва чорвалар ейдиган ер набототи у билан аралашиб, ер безагини олганда ва зийнатланганда ҳамда унинг аҳли, энди бунга эришдик, деб ўйлаганда, кечаси ёки кундузи унга Бизнинг амримиз келадир. Биз уни худди куни кеча яшнаб турмагандек қуритиб қўямиз. Тафаккур қиладиган қавмлар учун оятларимизни ана шундай батафсил баён қилурмиз'}, {'chapter': 10, 'verse': 25, 'text': 'وَٱللَّهُ يَدۡعُوٓاْ إِلَىٰ دَارِ ٱلسَّلَٰمِ وَيَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳ тинчлик диёрига даъват қилур ва Ўзи хоҳлаганларни сиротул мустақиймга ҳидоят қилур'}, {'chapter': 10, 'verse': 26, 'text': '۞لِّلَّذِينَ أَحۡسَنُواْ ٱلۡحُسۡنَىٰ وَزِيَادَةٞۖ وَلَا يَرۡهَقُ وُجُوهَهُمۡ قَتَرٞ وَلَا ذِلَّةٌۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nГўзал амал қилганлар учун гўзал савоб ва зиёдалик бордир. Уларнинг юзларини қаролик ҳам, хорлик ҳам қопламас. Ана ўшалар жаннат эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 10, 'verse': 27, 'text': 'وَٱلَّذِينَ كَسَبُواْ ٱلسَّيِّـَٔاتِ جَزَآءُ سَيِّئَةِۭ بِمِثۡلِهَا وَتَرۡهَقُهُمۡ ذِلَّةٞۖ مَّا لَهُم مِّنَ ٱللَّهِ مِنۡ عَاصِمٖۖ كَأَنَّمَآ أُغۡشِيَتۡ وُجُوهُهُمۡ قِطَعٗا مِّنَ ٱلَّيۡلِ مُظۡلِمًاۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЁмонликларни касб қилганларга эса, ёмонликларига яраша ёмон жазо бўлур. Уларни хорлик қоплар. Улар учун Аллоҳдан бирор сақловчи ҳам бўлмас. Юзлари худди тун зулматидан бир парча ила қопланган каби бўлур. Ана ўшалар дўзах эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 10, 'verse': 28, 'text': 'وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُواْ مَكَانَكُمۡ أَنتُمۡ وَشُرَكَآؤُكُمۡۚ فَزَيَّلۡنَا بَيۡنَهُمۡۖ وَقَالَ شُرَكَآؤُهُم مَّا كُنتُمۡ إِيَّانَا تَعۡبُدُونَ\nУ кунда барчаларини тўплармиз. Сўнгра, ширк келтирганларга: «Сизлар ҳам, шерик қилганларингиз ҳам жойингиздан қимирламангиз», дермиз. Бас, ораларини ажратармиз ва шерик қилинмишлар: «Бизга ибодат этмаган эдингиз'}, {'chapter': 10, 'verse': 29, 'text': 'فَكَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنَنَا وَبَيۡنَكُمۡ إِن كُنَّا عَنۡ عِبَادَتِكُمۡ لَغَٰفِلِينَ\nБиз билан сизнинг орангизда шоҳидликка Аллоҳ кифоядир. Биз, албатта, сизнинг ибодатингиздан ғофил эдик», дерлар'}, {'chapter': 10, 'verse': 30, 'text': 'هُنَالِكَ تَبۡلُواْ كُلُّ نَفۡسٖ مَّآ أَسۡلَفَتۡۚ وَرُدُّوٓاْ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nЎша ерда ҳар бир жон ўзи қилиб ўтган ишдан сўралур — хабардор бўлур ва ҳақиқий хожалари Аллоҳга қайтарилурлар. Ҳамда ўзларича тўқиб чиқарган нарсалари улардан ғойиб бўладир'}, {'chapter': 10, 'verse': 31, 'text': 'قُلۡ مَن يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ أَمَّن يَمۡلِكُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَمَن يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَيُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّ وَمَن يُدَبِّرُ ٱلۡأَمۡرَۚ فَسَيَقُولُونَ ٱللَّهُۚ فَقُلۡ أَفَلَا تَتَّقُونَ\nСен: «Сизларни осмонлару ерда ким ризқлантиради? Ёки қулоқ ва кўзларингизнинг эгаси ким, тирикни ўликдан, ўликни тирикдан ким чиқаради? Ишнинг тадбирини ким қилади?» деб айт. Улар, албатта: «Аллоҳ», дерлар. Бас, сен: «Тақво қилмайсизларми?» деб айт'}, {'chapter': 10, 'verse': 32, 'text': 'فَذَٰلِكُمُ ٱللَّهُ رَبُّكُمُ ٱلۡحَقُّۖ فَمَاذَا بَعۡدَ ٱلۡحَقِّ إِلَّا ٱلضَّلَٰلُۖ فَأَنَّىٰ تُصۡرَفُونَ\nБас, мана шу Аллоҳ сизнинг ҳақ Роббингиздир. Ҳақдан сўнг нима бўлади? Магар залолат бўлади. Бас, қаёққа бурилиб кетмоқдасиз'}, {'chapter': 10, 'verse': 33, 'text': 'كَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ فَسَقُوٓاْ أَنَّهُمۡ لَا يُؤۡمِنُونَ\nШундай қилиб, фосиқлик қилганларга Роббингнинг сўзи ҳақ бўлди. Улар иймон келтирмаслар'}, {'chapter': 10, 'verse': 34, 'text': 'قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۚ قُلِ ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۖ فَأَنَّىٰ تُؤۡفَكُونَ\nСен: «Сизнинг шерик келтираётганларингиз орасида махлуқотни аввал бошда яратиб, сўнгра уни қайтарадигани борми?» деб айт. Сен: «Аллоҳ махлуқотни аввал бошда яратиб, сўнгра уни қайтарар. Бас, қаёққа бурилиб кетмоқдасизлар?» деб айт'}, {'chapter': 10, 'verse': 35, 'text': 'قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَهۡدِيٓ إِلَى ٱلۡحَقِّۚ قُلِ ٱللَّهُ يَهۡدِي لِلۡحَقِّۗ أَفَمَن يَهۡدِيٓ إِلَى ٱلۡحَقِّ أَحَقُّ أَن يُتَّبَعَ أَمَّن لَّا يَهِدِّيٓ إِلَّآ أَن يُهۡدَىٰۖ فَمَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ\nСен: «Сизнинг шерик келтираётганларингиз орасида ҳаққа ҳидоят қиладигани борми?» деб айт. Сен: «Аллоҳ ҳаққа ҳидоят қиладир. Эргашишга ҳаққа ҳидоят қиладиган Зот ҳақлироқми ёки ҳидоят қилмайдиган, балки ўзи ҳидоятга муҳтож кимсами? Сизга нима бўлди?! Қандай ҳукм қилмоқдасиз?!» деб айт'}, {'chapter': 10, 'verse': 36, 'text': 'وَمَا يَتَّبِعُ أَكۡثَرُهُمۡ إِلَّا ظَنًّاۚ إِنَّ ٱلظَّنَّ لَا يُغۡنِي مِنَ ٱلۡحَقِّ شَيۡـًٔاۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَفۡعَلُونَ\nУларнинг кўплари гумонга эргашадилар, холос. Албатта, гумон ҳақ ўрнида ҳеч нарса бўла олмас. Албатта, Аллоҳ нима қилаётганларини билгувчи Зотдир'}, {'chapter': 10, 'verse': 37, 'text': 'وَمَا كَانَ هَٰذَا ٱلۡقُرۡءَانُ أَن يُفۡتَرَىٰ مِن دُونِ ٱللَّهِ وَلَٰكِن تَصۡدِيقَ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ ٱلۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ ٱلۡعَٰلَمِينَ\nУшбу Қуръон Аллоҳдан бошқа томонидан тўқиб чиқарилган эмас. Ва лекин у ўзидан олдингининг тасдиғи ва китобнинг тафсилотидир. Унда ҳеч шубҳа йўқ. У оламларнинг Роббисидандир'}, {'chapter': 10, 'verse': 38, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُواْ بِسُورَةٖ مِّثۡلِهِۦ وَٱدۡعُواْ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ\nЁки ўзи тўқиб олган дерларми? Сен: «Агар ростгўй бўлсангиз, унга ўхшаш бир сура келтиринг ва (бу ишга) Аллоҳдан бошқа, кучингиз етганича, кимни хоҳласангиз, чақиринг», деб айт'}, {'chapter': 10, 'verse': 39, 'text': 'بَلۡ كَذَّبُواْ بِمَا لَمۡ يُحِيطُواْ بِعِلۡمِهِۦ وَلَمَّا يَأۡتِهِمۡ تَأۡوِيلُهُۥۚ كَذَٰلِكَ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّـٰلِمِينَ\nБалки, улар ўзлари илмини билмаган нарсани, уларга унинг таъвийли етмай туриб, ёлғонга чиқардилар. Улардан олдингилар ҳам шундай ёлғонга чиқарган эдилар. Золимларнинг оқибати қандай бўлганига назар сол'}, {'chapter': 10, 'verse': 40, 'text': 'وَمِنۡهُم مَّن يُؤۡمِنُ بِهِۦ وَمِنۡهُم مَّن لَّا يُؤۡمِنُ بِهِۦۚ وَرَبُّكَ أَعۡلَمُ بِٱلۡمُفۡسِدِينَ\nУлардан унга иймон келтирадиганлар ҳам бор ва улардан унга иймон келтирмайдиганлар ҳам бор. Роббинг эса, бузғунчиларни яхши билгувчи Зотдир'}, {'chapter': 10, 'verse': 41, 'text': 'وَإِن كَذَّبُوكَ فَقُل لِّي عَمَلِي وَلَكُمۡ عَمَلُكُمۡۖ أَنتُم بَرِيٓـُٔونَ مِمَّآ أَعۡمَلُ وَأَنَا۠ بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ\nАгар улар сени ёлғончи қилсалар, сен: «Менга ўз ишим, сизларга ўз ишингиз, сизлар мен қиладиган ишдан поксизлар, мен сизлар қиладиган ишдан покман», деб айт'}, {'chapter': 10, 'verse': 42, 'text': 'وَمِنۡهُم مَّن يَسۡتَمِعُونَ إِلَيۡكَۚ أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ وَلَوۡ كَانُواْ لَا يَعۡقِلُونَ\nВа улардан сенга қулоқ тутадиганлари ҳам бор. Агар ақл ишлатмасалар, карқулоқларга сен эшиттира олурмисан'}, {'chapter': 10, 'verse': 43, 'text': 'وَمِنۡهُم مَّن يَنظُرُ إِلَيۡكَۚ أَفَأَنتَ تَهۡدِي ٱلۡعُمۡيَ وَلَوۡ كَانُواْ لَا يُبۡصِرُونَ\nУлардан сенга назар соладиганлари ҳам бор. Агар ўзлари (қалб кўзлари ила) кўрмасалар, кўрларни сен ҳидоят қила олурмисан'}, {'chapter': 10, 'verse': 44, 'text': 'إِنَّ ٱللَّهَ لَا يَظۡلِمُ ٱلنَّاسَ شَيۡـٔٗا وَلَٰكِنَّ ٱلنَّاسَ أَنفُسَهُمۡ يَظۡلِمُونَ\nАлбатта, Аллоҳ одамларга ҳеч зулм қилмас, ва лекин одамлар ўзларига зулм қилурлар'}, {'chapter': 10, 'verse': 45, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ كَأَن لَّمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّنَ ٱلنَّهَارِ يَتَعَارَفُونَ بَيۡنَهُمۡۚ قَدۡ خَسِرَ ٱلَّذِينَ كَذَّبُواْ بِلِقَآءِ ٱللَّهِ وَمَا كَانُواْ مُهۡتَدِينَ\nУларни тўплайдиган кунда худди наҳордан бир соатгина турганга ўхшаб, бир-бирлари билан танишурлар. Батаҳқиқ, Аллоҳнинг мулоқотини ёлғонга чиқарганлар ва ҳидоят топганлардан бўлмаганлар зиён қилурлар'}, {'chapter': 10, 'verse': 46, 'text': 'وَإِمَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ ٱللَّهُ شَهِيدٌ عَلَىٰ مَا يَفۡعَلُونَ\nЁки сенга уларга ваъда қилганимизнинг баъзисини кўрсатамиз ёки сени вафот эттирамиз. Бас, уларнинг қайтишлари Бизгадир. Сўнгра, нима қилаётганларига Аллоҳ шоҳиддир'}, {'chapter': 10, 'verse': 47, 'text': 'وَلِكُلِّ أُمَّةٖ رَّسُولٞۖ فَإِذَا جَآءَ رَسُولُهُمۡ قُضِيَ بَيۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ\nҲар умматнинг ўз Пайғамбари бордир. Қачонки уларга Пайғамбарлари келса, ораларида адолат ила ҳукм чиқарилур. Уларга зулм қилинмас'}, {'chapter': 10, 'verse': 48, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўйлардан бўлсангиз, бу ваъда қачон бўлади?» дерлар'}, {'chapter': 10, 'verse': 49, 'text': 'قُل لَّآ أَمۡلِكُ لِنَفۡسِي ضَرّٗا وَلَا نَفۡعًا إِلَّا مَا شَآءَ ٱللَّهُۗ لِكُلِّ أُمَّةٍ أَجَلٌۚ إِذَا جَآءَ أَجَلُهُمۡ فَلَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ\nСен: «Мен ўзим ҳам на зарарга ва на фойдага молик эмасман. Магар Аллоҳ нимани хоҳласа (шу бўлади). Ҳар умматнинг ўз ажали бор. Ажаллари келган вақтда бир соатга орқага ҳам, олдинга ҳам сура олмаслар», деб айт'}, {'chapter': 10, 'verse': 50, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُهُۥ بَيَٰتًا أَوۡ نَهَارٗا مَّاذَا يَسۡتَعۡجِلُ مِنۡهُ ٱلۡمُجۡرِمُونَ\nСен: «Хабар беринг-чи, агар Унинг азоби сизларга кечаси ёки кундузи келса, жиноятчилар унинг нимасига шошилмоқдалар?» деб айт'}, {'chapter': 10, 'verse': 51, 'text': 'أَثُمَّ إِذَا مَا وَقَعَ ءَامَنتُم بِهِۦٓۚ ءَآلۡـَٰٔنَ وَقَدۡ كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ\nУ воқе бўлганидан сўнг унга ишондингизми? Энди-я?! Уни, тезроқ келсин, деб шошилаётган эдингиз-ку'}, {'chapter': 10, 'verse': 52, 'text': 'ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلۡخُلۡدِ هَلۡ تُجۡزَوۡنَ إِلَّا بِمَا كُنتُمۡ تَكۡسِبُونَ\nСўнгра, зулм қилганларга: «Мангу азобни тортинглар. Фақат касб қилганингизга яраша жазо олурсизлар, холос», дейилур'}, {'chapter': 10, 'verse': 53, 'text': '۞وَيَسۡتَنۢبِـُٔونَكَ أَحَقٌّ هُوَۖ قُلۡ إِي وَرَبِّيٓ إِنَّهُۥ لَحَقّٞۖ وَمَآ أَنتُم بِمُعۡجِزِينَ\nУлар сендан: «У ҳақми?» деб сўрарлар. «Ҳа, Парвардигорим ҳаққи, албатта, у ҳақдир ва сизлар қочиб қутулувчи эмассизлар», деб айт'}, {'chapter': 10, 'verse': 54, 'text': 'وَلَوۡ أَنَّ لِكُلِّ نَفۡسٖ ظَلَمَتۡ مَا فِي ٱلۡأَرۡضِ لَٱفۡتَدَتۡ بِهِۦۗ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۖ وَقُضِيَ بَيۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ\nАгар ҳар бир зулм қилган жонга ер юзидаги ҳамма нарса берилса-ю, уни фидо қилса ҳам. Азобни кўрганларида ичларида надомат чексалар ҳам. Улар ўртасида адолат ила ҳукм чиқарилур. Уларга зулм қилинмас'}, {'chapter': 10, 'verse': 55, 'text': 'أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ أَلَآ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nОгоҳ бўлинг! Осмонлару ердаги нарсалар, албатта, Аллоҳникидир. Огоҳ бўлинг! Аллоҳнинг ваъдаси, албатта, ҳақдир. Лекин кўплари билмаслар'}, {'chapter': 10, 'verse': 56, 'text': 'هُوَ يُحۡيِۦ وَيُمِيتُ وَإِلَيۡهِ تُرۡجَعُونَ\nУ тирилтирур ва ўлдирур ва Унгагина қайтарилурсиз'}, {'chapter': 10, 'verse': 57, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَتۡكُم مَّوۡعِظَةٞ مِّن رَّبِّكُمۡ وَشِفَآءٞ لِّمَا فِي ٱلصُّدُورِ وَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ\nЭй одамлар! Сизга ўз Роббингиздан мавъиза, кўксингиздаги нарсага шифо, мўминларга ҳидоят ва раҳмат келди'}, {'chapter': 10, 'verse': 58, 'text': 'قُلۡ بِفَضۡلِ ٱللَّهِ وَبِرَحۡمَتِهِۦ فَبِذَٰلِكَ فَلۡيَفۡرَحُواْ هُوَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ\nСен: «Аллоҳнинг фазли ила ва Унинг раҳмати ила. Бас, ана шу билан хурсанд бўлсинлар. У улар жамлайдиган нарсалардан яхшидир», деб айт'}, {'chapter': 10, 'verse': 59, 'text': 'قُلۡ أَرَءَيۡتُم مَّآ أَنزَلَ ٱللَّهُ لَكُم مِّن رِّزۡقٖ فَجَعَلۡتُم مِّنۡهُ حَرَامٗا وَحَلَٰلٗا قُلۡ ءَآللَّهُ أَذِنَ لَكُمۡۖ أَمۡ عَلَى ٱللَّهِ تَفۡتَرُونَ\nСен: «Аллоҳ сизга нозил қилган ризқдан хабар беринг-чи! Уни ҳаром ва ҳалол қилиб олдингиз!» деб айт. Сен: «Аллоҳ сизга изн бердими ёки Аллоҳга нисбатан ёлғон тўқияпсизми?» деб айт'}, {'chapter': 10, 'verse': 60, 'text': 'وَمَا ظَنُّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ يَوۡمَ ٱلۡقِيَٰمَةِۗ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ\nАллоҳга нисбатан ёлғон тўқиётганларнинг қиёмат куни ҳақида нима гумонлари бор? Албатта, Аллоҳ одамлар учун фазл эгасидир. Лекин кўплари шукр қилмаслар'}, {'chapter': 10, 'verse': 61, 'text': 'وَمَا تَكُونُ فِي شَأۡنٖ وَمَا تَتۡلُواْ مِنۡهُ مِن قُرۡءَانٖ وَلَا تَعۡمَلُونَ مِنۡ عَمَلٍ إِلَّا كُنَّا عَلَيۡكُمۡ شُهُودًا إِذۡ تُفِيضُونَ فِيهِۚ وَمَا يَعۡزُبُ عَن رَّبِّكَ مِن مِّثۡقَالِ ذَرَّةٖ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ وَلَآ أَصۡغَرَ مِن ذَٰلِكَ وَلَآ أَكۡبَرَ إِلَّا فِي كِتَٰبٖ مُّبِينٍ\nҚайси ҳолда бўлсанг, Қуръондан нимани тиловат этсанг, ишлардан қай бирини қилсанг, албатта, Биз ўшанга киришаётган пайтингларда сизларга гувоҳмиз. На ер юзида ва на осмонларда зарра миқдоридаги, ундан кичикроқ ёки каттароқ нарса Роббингдан махфий бўла олмас, балки, очиқ-ойдин китобдадир'}, {'chapter': 10, 'verse': 62, 'text': 'أَلَآ إِنَّ أَوۡلِيَآءَ ٱللَّهِ لَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nОгоҳ бўлингким, Аллоҳнинг дўстларига хавф йўқдир ва улар хафа ҳам бўлмаслар'}, {'chapter': 10, 'verse': 63, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nУлар иймон келтирганлар ва тақво қилганлардир'}, {'chapter': 10, 'verse': 64, 'text': 'لَهُمُ ٱلۡبُشۡرَىٰ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۚ لَا تَبۡدِيلَ لِكَلِمَٰتِ ٱللَّهِۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nУларга ҳаёти дунёда ҳам, охиратда ҳам хушхабар бор. Аллоҳнинг сўзларини ўзгартириш йўқ. Ана ўша улуғ ютуқдир'}, {'chapter': 10, 'verse': 65, 'text': 'وَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِيعًاۚ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nУларнинг гапи сени хафа қилмасин. Албатта, азизликнинг барчаси Аллоҳникидир. У эшитгувчи ва билгувчи Зотдир'}, {'chapter': 10, 'verse': 66, 'text': 'أَلَآ إِنَّ لِلَّهِ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِۗ وَمَا يَتَّبِعُ ٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ شُرَكَآءَۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ\nОгоҳ бўлингким, осмонларда ким бўлса ва ер юзида ким бўлса, албатта, Аллоҳникидир. Аллоҳдан ўзга шерикларга илтижо қилаётганлар нимага эргашурлар? Улар фақат гумонга эргашурлар, холос. Улар фақат тахмин қилурлар, холос'}, {'chapter': 10, 'verse': 67, 'text': 'هُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ\nУ сизларга сокинлик топишингиз учун кечани ва кўрсатувчи этиб кундузни қилган Зот. Албатта, бунда эшитадиган қавмлар учун оят — аломатлар бордир'}, {'chapter': 10, 'verse': 68, 'text': 'قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ هُوَ ٱلۡغَنِيُّۖ لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ إِنۡ عِندَكُم مِّن سُلۡطَٰنِۭ بِهَٰذَآۚ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nУлар, «Аллоҳ фарзанд тутди», дедилар. У (бундан) покдир. У беҳожатдир. Осмонлардаги нарсалар ва ердаги нарсалар Уникидир. Бу ҳақда сизнинг ҳеч қандай далилингиз йўқ-ку! Аллоҳга нисбатан билмаган нарсангизни айтаверасизми'}, {'chapter': 10, 'verse': 69, 'text': 'قُلۡ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ\nСен: «Албатта, Аллоҳга нисбатан ёлғон тўқиганлар нажот топмаслар», деб айт'}, {'chapter': 10, 'verse': 70, 'text': 'مَتَٰعٞ فِي ٱلدُّنۡيَا ثُمَّ إِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ نُذِيقُهُمُ ٱلۡعَذَابَ ٱلشَّدِيدَ بِمَا كَانُواْ يَكۡفُرُونَ\nБу дунёда (бир оз) фойдаланиш, кейин Бизга қайтишлари бор. Сўнгра, Биз уларга, куфр келтириб ўтганлари учун, шиддатли азобни тоттирамиз'}, {'chapter': 10, 'verse': 71, 'text': '۞وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ نُوحٍ إِذۡ قَالَ لِقَوۡمِهِۦ يَٰقَوۡمِ إِن كَانَ كَبُرَ عَلَيۡكُم مَّقَامِي وَتَذۡكِيرِي بِـَٔايَٰتِ ٱللَّهِ فَعَلَى ٱللَّهِ تَوَكَّلۡتُ فَأَجۡمِعُوٓاْ أَمۡرَكُمۡ وَشُرَكَآءَكُمۡ ثُمَّ لَا يَكُنۡ أَمۡرُكُمۡ عَلَيۡكُمۡ غُمَّةٗ ثُمَّ ٱقۡضُوٓاْ إِلَيَّ وَلَا تُنظِرُونِ\nУларга Нуҳнинг хабарини тиловат қилиб бер. Вақтики у ўз қавмига: «Эй қавмим, агар сизларга менинг мақомим ва Аллоҳнинг оятлари ила эслатишим оғир келаётган бўлса, бас, мен Аллоҳга таваккал қилдим. Сиз шерик келтирганларингиз билан биргаликда ўз ишингизни билаверинглар. Яна, ишларингиз ўзингиз учун ноаниқ бўлиб қолмасин. Сўнгра, менга нисбатан ҳукмингизни ижро этаверинглар, менга муҳлат берманглар'}, {'chapter': 10, 'verse': 72, 'text': 'فَإِن تَوَلَّيۡتُمۡ فَمَا سَأَلۡتُكُم مِّنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ\nБас, агар юз ўгирсангиз, мен сизлардан ажр сўраганим йўқ. Менинг ажрим фақатгина Аллоҳдан. Ва мен мусулмонлардан бўлишга амр қилинганман», деди'}, {'chapter': 10, 'verse': 73, 'text': 'فَكَذَّبُوهُ فَنَجَّيۡنَٰهُ وَمَن مَّعَهُۥ فِي ٱلۡفُلۡكِ وَجَعَلۡنَٰهُمۡ خَلَـٰٓئِفَ وَأَغۡرَقۡنَا ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُنذَرِينَ\nСўнгра, уни ёлғончига чиқаришди. Бас, Биз уни ва у ила бирга бўлганларни кемада қутқардик ва уларни халифалар қилдик. Оятларимизни ёлғонга чиқарганларни ғарқ этдик. Огоҳлантирилганларнинг оқибати қандай бўлганига назар сол'}, {'chapter': 10, 'verse': 74, 'text': 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِۦ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِٱلۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ بِهِۦ مِن قَبۡلُۚ كَذَٰلِكَ نَطۡبَعُ عَلَىٰ قُلُوبِ ٱلۡمُعۡتَدِينَ\nСўнгра, ундан кейин Пайғамбарларни қавмларига юбордик. Бас, улар очиқ-ойдин ҳужжатлар ила келдилар. Аммо у (қавм)лар олдин ёлғонга чиқарган нарсаларига иймон келтирувчи бўлмадилар. Тажовузкорларнинг қалбига шундай қилиб муҳр босамиз'}, {'chapter': 10, 'verse': 75, 'text': 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ وَهَٰرُونَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ بِـَٔايَٰتِنَا فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ\nСўнгра, улардан кейин Мусо ва Ҳорунни Фиръавн ва унинг қавмига Ўз оят — мўъжизаларимиз ила юбордик. Бас, улар кибру ҳаво қилдилар ва жиноятчи қавм бўлдилар'}, {'chapter': 10, 'verse': 76, 'text': 'فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُوٓاْ إِنَّ هَٰذَا لَسِحۡرٞ مُّبِينٞ\nБизнинг ҳузуримиздан уларга ҳақ келган пайтда: «Бу, албатта, очиқ-ойдин сеҳрдир», дедилар'}, {'chapter': 10, 'verse': 77, 'text': 'قَالَ مُوسَىٰٓ أَتَقُولُونَ لِلۡحَقِّ لَمَّا جَآءَكُمۡۖ أَسِحۡرٌ هَٰذَا وَلَا يُفۡلِحُ ٱلسَّـٰحِرُونَ\nМусо: «Сизга ҳақ келганда (шундай) дейсизларми?! Шу сеҳрми?! Сеҳргарлар нажот топмаслар», деди'}, {'chapter': 10, 'verse': 78, 'text': 'قَالُوٓاْ أَجِئۡتَنَا لِتَلۡفِتَنَا عَمَّا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَا وَتَكُونَ لَكُمَا ٱلۡكِبۡرِيَآءُ فِي ٱلۡأَرۡضِ وَمَا نَحۡنُ لَكُمَا بِمُؤۡمِنِينَ\nУлар: «Сен бизни оталаримиз тутиб келаётганида топган нарсамиздан буриш учун ва икковингизга ер юзида катталик бўлиши учун келдингми? Биз икковингизга иймон келтирувчилардан эмасмиз», дедилар'}, {'chapter': 10, 'verse': 79, 'text': 'وَقَالَ فِرۡعَوۡنُ ٱئۡتُونِي بِكُلِّ سَٰحِرٍ عَلِيمٖ\nФиръавн: «Менга барча билимдон сеҳргарларни келтиринглар», деди'}, {'chapter': 10, 'verse': 80, 'text': 'فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالَ لَهُم مُّوسَىٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ\nСеҳргарлар келганида, Мусо уларга: «Сизлар ташламоқчи бўлган нарсангизни ташлангиз», деди'}, {'chapter': 10, 'verse': 81, 'text': 'فَلَمَّآ أَلۡقَوۡاْ قَالَ مُوسَىٰ مَا جِئۡتُم بِهِ ٱلسِّحۡرُۖ إِنَّ ٱللَّهَ سَيُبۡطِلُهُۥٓ إِنَّ ٱللَّهَ لَا يُصۡلِحُ عَمَلَ ٱلۡمُفۡسِدِينَ\nУлар ташлаганларида, Мусо: «Сиз келтирган нарса сеҳрдир. Албатта, Аллоҳ уни ботил қилур. Зеро, Аллоҳ бузғунчиларнинг ишини ўнгламас'}, {'chapter': 10, 'verse': 82, 'text': 'وَيُحِقُّ ٱللَّهُ ٱلۡحَقَّ بِكَلِمَٰتِهِۦ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ\nВа Аллоҳ Ўз калимаси ила, гарчи жиноятчилар ёқтирмаса ҳам, ҳақни рўёбга чиқарур», деди'}, {'chapter': 10, 'verse': 83, 'text': 'فَمَآ ءَامَنَ لِمُوسَىٰٓ إِلَّا ذُرِّيَّةٞ مِّن قَوۡمِهِۦ عَلَىٰ خَوۡفٖ مِّن فِرۡعَوۡنَ وَمَلَإِيْهِمۡ أَن يَفۡتِنَهُمۡۚ وَإِنَّ فِرۡعَوۡنَ لَعَالٖ فِي ٱلۡأَرۡضِ وَإِنَّهُۥ لَمِنَ ٱلۡمُسۡرِفِينَ\nМусога ўз қавмидан баъзи зурриётларгина Фиръавн ва амалдор-зодагонларининг фитна қилмоқларидан қўрққан ҳолларида иймон келтирдилар, холос. Албатта, Фиръавн ер юзида баланд кетган эди. Албатта, у исрофчилардан эди'}, {'chapter': 10, 'verse': 84, 'text': 'وَقَالَ مُوسَىٰ يَٰقَوۡمِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ فَعَلَيۡهِ تَوَكَّلُوٓاْ إِن كُنتُم مُّسۡلِمِينَ\nМусо: «Эй қавмим, агар Аллоҳга иймон келтирган бўлсангиз, агар мусулмон бўлсангиз, фақат Унгагина таваккал қилинг», деди'}, {'chapter': 10, 'verse': 85, 'text': 'فَقَالُواْ عَلَى ٱللَّهِ تَوَكَّلۡنَا رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nУлар: «Аллоҳгагина таваккал қилдик. Роббимиз, бизни золим қавмларга фитна қилиб қўйма'}, {'chapter': 10, 'verse': 86, 'text': 'وَنَجِّنَا بِرَحۡمَتِكَ مِنَ ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nВа Ўз раҳматинг ила бизга кофир қавмлардан нажот бергин», дедилар'}, {'chapter': 10, 'verse': 87, 'text': 'وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰ وَأَخِيهِ أَن تَبَوَّءَا لِقَوۡمِكُمَا بِمِصۡرَ بُيُوتٗا وَٱجۡعَلُواْ بُيُوتَكُمۡ قِبۡلَةٗ وَأَقِيمُواْ ٱلصَّلَوٰةَۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nБиз Мусога ва унинг биродарига: «Икковингиз қавмингиз учун Мисрда уйлар тайёрланг, уйларингизни қибла қилиб, намозни тўкис адо этинглар ва мўминларга хушхабар бер», деб ваҳий юбордик'}, {'chapter': 10, 'verse': 88, 'text': 'وَقَالَ مُوسَىٰ رَبَّنَآ إِنَّكَ ءَاتَيۡتَ فِرۡعَوۡنَ وَمَلَأَهُۥ زِينَةٗ وَأَمۡوَٰلٗا فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا رَبَّنَا لِيُضِلُّواْ عَن سَبِيلِكَۖ رَبَّنَا ٱطۡمِسۡ عَلَىٰٓ أَمۡوَٰلِهِمۡ وَٱشۡدُدۡ عَلَىٰ قُلُوبِهِمۡ فَلَا يُؤۡمِنُواْ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nМусо: «Эй Роббимиз, Сен Фиръавнга ва унинг амалдорларига зебу зийнат ва молу дунё бердинг. Роббимиз, бу Сенинг йўлингдан адаштиришлари учундир. Роббимиз, уларнинг молларини йўқ қилгин ва қалбларини қаттиқ қилгин, то аламли азобни кўрмагунларича иймонга келмасинлар», деди'}, {'chapter': 10, 'verse': 89, 'text': 'قَالَ قَدۡ أُجِيبَت دَّعۡوَتُكُمَا فَٱسۡتَقِيمَا وَلَا تَتَّبِعَآنِّ سَبِيلَ ٱلَّذِينَ لَا يَعۡلَمُونَ\nУ: «Батаҳқиқ, икковингизнинг дуоингиз қабул бўлди. Бас, собит туринглар ва билмайдиганларнинг йўлига эргашманглар», деди'}, {'chapter': 10, 'verse': 90, 'text': '۞وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡبَحۡرَ فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ وَجُنُودُهُۥ بَغۡيٗا وَعَدۡوًاۖ حَتَّىٰٓ إِذَآ أَدۡرَكَهُ ٱلۡغَرَقُ قَالَ ءَامَنتُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱلَّذِيٓ ءَامَنَتۡ بِهِۦ بَنُوٓاْ إِسۡرَـٰٓءِيلَ وَأَنَا۠ مِنَ ٱلۡمُسۡلِمِينَ\nВа Бани Исроилни денгиздан ўтказдик. Бас, Фиръавн ва унинг аскарлари ҳаддан ошган ва туғён қилган ҳолда уларни таъқиб этдилар. Токи унга ғарқ бўлиш етганида: «Бани Исроил иймон келтирган Зотдан ўзга Илоҳ йўқлигига иймон келтирдим ва мен мусулмонларданман», деди'}, {'chapter': 10, 'verse': 91, 'text': 'ءَآلۡـَٰٔنَ وَقَدۡ عَصَيۡتَ قَبۡلُ وَكُنتَ مِنَ ٱلۡمُفۡسِدِينَ\nЭндими! Олдин исён қилган ва бузғунчилардан бўлган эдинг-ку'}, {'chapter': 10, 'verse': 92, 'text': 'فَٱلۡيَوۡمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنۡ خَلۡفَكَ ءَايَةٗۚ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ عَنۡ ءَايَٰتِنَا لَغَٰفِلُونَ\nБугун сенинг баданингни қутқарамиз. Токи ўзингдан кейингиларга ибрат бўлгин. Албатта, кўп одамлар Бизнинг оятларимиздан ғофилдирлар'}, {'chapter': 10, 'verse': 93, 'text': 'وَلَقَدۡ بَوَّأۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ مُبَوَّأَ صِدۡقٖ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ فَمَا ٱخۡتَلَفُواْ حَتَّىٰ جَآءَهُمُ ٱلۡعِلۡمُۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nБатаҳқиқ, Биз Бани Исроилни ҳақиқий омонлик масканига жойлаштирдик ва уларни пок нарсалар ила ризқлантирдик. Бас, улар то ўзларига илм келмагунча ихтилоф қилмадилар. Албатта, Роббинг қиёмат куни улар орасида ихтилоф қилган нарсалари ҳақида Ўзи ҳукм чиқарур'}, {'chapter': 10, 'verse': 94, 'text': 'فَإِن كُنتَ فِي شَكّٖ مِّمَّآ أَنزَلۡنَآ إِلَيۡكَ فَسۡـَٔلِ ٱلَّذِينَ يَقۡرَءُونَ ٱلۡكِتَٰبَ مِن قَبۡلِكَۚ لَقَدۡ جَآءَكَ ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ\nАгар сенга нозил қилган нарсамиздан шакда бўлсанг, сендан олдин китобни ўқиганлардан сўра. Батаҳқиқ, сенга Роббингдан ҳақ келди. Бас, ҳеч шубҳа қилгувчилардан бўлма'}, {'chapter': 10, 'verse': 95, 'text': 'وَلَا تَكُونَنَّ مِنَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ فَتَكُونَ مِنَ ٱلۡخَٰسِرِينَ\nЗинҳор, Аллоҳнинг оятларини ёлғонга чиқарувчилардан бўлма, у ҳолда зиёнкорлардан бўлиб қоласан'}, {'chapter': 10, 'verse': 96, 'text': 'إِنَّ ٱلَّذِينَ حَقَّتۡ عَلَيۡهِمۡ كَلِمَتُ رَبِّكَ لَا يُؤۡمِنُونَ\nАлбатта, Роббингнинг калималари зиммасига вожиб бўлганлар иймонга келмаслар'}, {'chapter': 10, 'verse': 97, 'text': 'وَلَوۡ جَآءَتۡهُمۡ كُلُّ ءَايَةٍ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nАгар уларга ҳамма оят-мўъжизалар келса ҳам, то аламли азобни кўрмагунларича'}, {'chapter': 10, 'verse': 98, 'text': 'فَلَوۡلَا كَانَتۡ قَرۡيَةٌ ءَامَنَتۡ فَنَفَعَهَآ إِيمَٰنُهَآ إِلَّا قَوۡمَ يُونُسَ لَمَّآ ءَامَنُواْ كَشَفۡنَا عَنۡهُمۡ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ\nҚани энди, бирон қишлоқ иймон келтирганда, унга иймони манфаат берса эди. Фақат, Юнус қавми иймон келтирганларида, улардан ҳаёти дунёдаги хорлик азобини кушойиш қилдик ва уларни маълум вақтгача фойдалантирдик, холос'}, {'chapter': 10, 'verse': 99, 'text': 'وَلَوۡ شَآءَ رَبُّكَ لَأٓمَنَ مَن فِي ٱلۡأَرۡضِ كُلُّهُمۡ جَمِيعًاۚ أَفَأَنتَ تُكۡرِهُ ٱلنَّاسَ حَتَّىٰ يَكُونُواْ مُؤۡمِنِينَ\nАгар Роббинг хоҳласа, ер юзидаги кишиларнинг ҳаммаси иймонга келар эдилар. Ёки сен одамларни мўмин бўлишга мажбурлайсанми'}, {'chapter': 10, 'verse': 100, 'text': 'وَمَا كَانَ لِنَفۡسٍ أَن تُؤۡمِنَ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَيَجۡعَلُ ٱلرِّجۡسَ عَلَى ٱلَّذِينَ لَا يَعۡقِلُونَ\nАллоҳнинг изнисиз ҳеч бир жон мўмин бўла олмас. У ақл юргизмайдиганларга ўта ифлосликни раво кўрур'}, {'chapter': 10, 'verse': 101, 'text': 'قُلِ ٱنظُرُواْ مَاذَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَا تُغۡنِي ٱلۡأٓيَٰتُ وَٱلنُّذُرُ عَن قَوۡمٖ لَّا يُؤۡمِنُونَ\nСен: «Осмонлару ердаги нарсаларга назар солинглар. Иймон келтирмайдиган қавмларга оят-мўъжизалар ва қўрқитувлар фойда бермас», деб айт'}, {'chapter': 10, 'verse': 102, 'text': 'فَهَلۡ يَنتَظِرُونَ إِلَّا مِثۡلَ أَيَّامِ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِهِمۡۚ قُلۡ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ\nУлар фақат ўзларидан олдин ўтганларнинг кунларига ўхшаш кунларга интизор бўлмоқдаларми? Сен: «Бас, интизор бўлинглар. Албатта, мен ҳам сизлар билан интизор бўлгувчиларданман», деб айт'}, {'chapter': 10, 'verse': 103, 'text': 'ثُمَّ نُنَجِّي رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ حَقًّا عَلَيۡنَا نُنجِ ٱلۡمُؤۡمِنِينَ\nСўнгра, Пайғамбарларимизга ва иймон келтирганларга нажот берамиз. Шундай қилиб, мўминларга нажот бермоғимиз зиммамиздаги бурчдир'}, {'chapter': 10, 'verse': 104, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِي شَكّٖ مِّن دِينِي فَلَآ أَعۡبُدُ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَلَٰكِنۡ أَعۡبُدُ ٱللَّهَ ٱلَّذِي يَتَوَفَّىٰكُمۡۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\n«Эй одамлар, агар менинг динимда шак-шубҳангиз бўлса, бас, (билингки) мен сизлар ибодат қилаётган — Аллоҳдан ўзга — нарсаларга ибодат қилмасман. Лекин сизни вафот эттирадиган Аллоҳга ибодат қилурман ва мўминлардан бўлмоғимга амр қилинганман», деб айт'}, {'chapter': 10, 'verse': 105, 'text': 'وَأَنۡ أَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗا وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ\nЮзингни динга тўғри тут. Мушриклардан бўлма'}, {'chapter': 10, 'verse': 106, 'text': 'وَلَا تَدۡعُ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكَ وَلَا يَضُرُّكَۖ فَإِن فَعَلۡتَ فَإِنَّكَ إِذٗا مِّنَ ٱلظَّـٰلِمِينَ\nАллоҳдан ўзга — сенга манфаат ҳам, зарар ҳам бермайдиган — нарсага илтижо қилма. Бас, агар шундай қилсанг, унда, сен золимлардан бўласан'}, {'chapter': 10, 'verse': 107, 'text': 'وَإِن يَمۡسَسۡكَ ٱللَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يُرِدۡكَ بِخَيۡرٖ فَلَا رَآدَّ لِفَضۡلِهِۦۚ يُصِيبُ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nАгар Аллоҳ сени зарар ила тутса, Ундан ўзга ўшанга кушойиш берувчи йўқдир. Агар У сенга яхшиликни ирода қилса, Унинг фазлини рад қилувчи ҳам йўқдир. Уни бандаларидан хоҳлаганига етказур. У мағфират қилгувчи ва раҳимли Зотдир'}, {'chapter': 10, 'verse': 108, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُمُ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنَا۠ عَلَيۡكُم بِوَكِيلٖ\nСен: «Эй одамлар, сизга Роббингиздан ҳақ келди. Бас, ким ҳидоятга юрса, ўзи учун ҳидоят топадир. Ким залолатга кетса, ўз зарарига залолат топадир. Мен сизларнинг устингиздан қўриқчи эмасман», деб айт'}, {'chapter': 10, 'verse': 109, 'text': 'وَٱتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ وَٱصۡبِرۡ حَتَّىٰ يَحۡكُمَ ٱللَّهُۚ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ\nВа сенга ваҳий қилинган нарсага эргаш ва Аллоҳ ҳукм қилгунча сабр қил. У ҳукм қилгувчиларнинг яхшисидир'}], '11': [{'chapter': 11, 'verse': 1, 'text': 'الٓرۚ كِتَٰبٌ أُحۡكِمَتۡ ءَايَٰتُهُۥ ثُمَّ فُصِّلَتۡ مِن لَّدُنۡ حَكِيمٍ خَبِيرٍ\nАлиф. Лаам. Ро. (Ушбу) китоб оятлари маҳкам қилинган, сўнгра ҳикматли ва хабардор Зот томонидан муфассал (баён) қилингандир'}, {'chapter': 11, 'verse': 2, 'text': 'أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۚ إِنَّنِي لَكُم مِّنۡهُ نَذِيرٞ وَبَشِيرٞ\nАллоҳдан ўзгага ибодат қилмаслигингиз учундир. Албатта, мен сизларга Ундан келган огоҳлантирувчи ва хушхабарчиман'}, {'chapter': 11, 'verse': 3, 'text': 'وَأَنِ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُمَتِّعۡكُم مَّتَٰعًا حَسَنًا إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَيُؤۡتِ كُلَّ ذِي فَضۡلٖ فَضۡلَهُۥۖ وَإِن تَوَلَّوۡاْ فَإِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ كَبِيرٍ\nРоббингизга истиғфор айтинглар, сўнгра Унга тавба қилинглар. (Шунда) У зот сизларни маълум муддатгача яхши роҳатлар ила баҳраманд қилур ва ҳар бир фазл эгасига фазлини берур. Агар юз ўгириб кетсангиз, бас, албатта, мен сизларга улуғ куннинг азоби бўлишидан қўрқаман'}, {'chapter': 11, 'verse': 4, 'text': 'إِلَى ٱللَّهِ مَرۡجِعُكُمۡۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nАллоҳнинг Ўзига қайтишингиз бор. У ҳар бир нарсага қодир Зотдир'}, {'chapter': 11, 'verse': 5, 'text': 'أَلَآ إِنَّهُمۡ يَثۡنُونَ صُدُورَهُمۡ لِيَسۡتَخۡفُواْ مِنۡهُۚ أَلَا حِينَ يَسۡتَغۡشُونَ ثِيَابَهُمۡ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nОгоҳ бўлингким, албатта, улар Ундан яшириниш учун кўксиларига (бошларини) эгарлар. Огоҳ бўлингким, кийимларига ўралган чоғларида ҳам, У зот нимани сир тутаётганларинию нимани ошкор қилаётганларини билар. Албатта, У — қалблардаги бор нарсаларни билгувчи Зотдир'}, {'chapter': 11, 'verse': 6, 'text': '۞وَمَا مِن دَآبَّةٖ فِي ٱلۡأَرۡضِ إِلَّا عَلَى ٱللَّهِ رِزۡقُهَا وَيَعۡلَمُ مُسۡتَقَرَّهَا وَمُسۡتَوۡدَعَهَاۚ كُلّٞ فِي كِتَٰبٖ مُّبِينٖ\nЕр юзида ўрмалаган нарса борки, уларнинг ризқи Аллоҳнинг зиммасидадир. У уларнинг турар жойларини ҳам, борар жойларини ҳам билур. Ҳаммаси очиқ-ойдин китобдадир'}, {'chapter': 11, 'verse': 7, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ وَكَانَ عَرۡشُهُۥ عَلَى ٱلۡمَآءِ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۗ وَلَئِن قُلۡتَ إِنَّكُم مَّبۡعُوثُونَ مِنۢ بَعۡدِ ٱلۡمَوۡتِ لَيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nУ сизлардан қайсингизнинг амали яхшироқ эканини синаб кўриш учун олти кунда осмонлару ерни яратди. Ўшанда Арши сув устида эди. Агар сен: «Сизлар ўлимдан кейин албатта қайта тирилтириласизлар», десанг, куфр келтирганлар, албатта: «Бу очиқ-ойдин сеҳрдан ўзга нарса эмас», дерлар'}, {'chapter': 11, 'verse': 8, 'text': 'وَلَئِنۡ أَخَّرۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِلَىٰٓ أُمَّةٖ مَّعۡدُودَةٖ لَّيَقُولُنَّ مَا يَحۡبِسُهُۥٓۗ أَلَا يَوۡمَ يَأۡتِيهِمۡ لَيۡسَ مَصۡرُوفًا عَنۡهُمۡ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nАгар Биз улардан маълум муддатгача азобни кечиктирсак, улар албатта: «Уни нима тўхтатиб турибди?» дерлар. Огоҳ бўлингларким, у келган кунда улардан қайтарилмас ва уларни ўзлари истеҳзо қилиб юрган нарса ўраб олур'}, {'chapter': 11, 'verse': 9, 'text': 'وَلَئِنۡ أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ ثُمَّ نَزَعۡنَٰهَا مِنۡهُ إِنَّهُۥ لَيَـُٔوسٞ كَفُورٞ\nАгар инсонга Ўз томонимиздан роҳатни тоттириб, сўнгра ундан уни тортиб олсак, у албатта ноумид ва ношукр бўлур'}, {'chapter': 11, 'verse': 10, 'text': 'وَلَئِنۡ أَذَقۡنَٰهُ نَعۡمَآءَ بَعۡدَ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ ذَهَبَ ٱلسَّيِّـَٔاتُ عَنِّيٓۚ إِنَّهُۥ لَفَرِحٞ فَخُورٌ\nАгар унга етган қийинчиликлардан сўнг неъмат тоттирсак, албатта: «Мендан ёмонликлар кетди», дейди. Албатта, у хурсанд ва фахрлидир'}, {'chapter': 11, 'verse': 11, 'text': 'إِلَّا ٱلَّذِينَ صَبَرُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ\nМагар сабр қилганлар ва яхши амаллар қилганлар, ана ўшаларга мағфират ва катта ажр бор'}, {'chapter': 11, 'verse': 12, 'text': 'فَلَعَلَّكَ تَارِكُۢ بَعۡضَ مَا يُوحَىٰٓ إِلَيۡكَ وَضَآئِقُۢ بِهِۦ صَدۡرُكَ أَن يَقُولُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ كَنزٌ أَوۡ جَآءَ مَعَهُۥ مَلَكٌۚ إِنَّمَآ أَنتَ نَذِيرٞۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٌ\nЭҳтимол, сен уларнинг: «Унга хазина туширилса эди» ёки «У билан бирга фаришта келса эди», деганлари учун ўзингга ваҳий қилинган нарсанинг баъзисини тарк қилмоқчи бўларсан ёки ундан юрагинг сиқилар. Сен огоҳлантирувчисан, холос. Аллоҳнинг Ўзи ҳамма нарсага вакилдир'}, {'chapter': 11, 'verse': 13, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُواْ بِعَشۡرِ سُوَرٖ مِّثۡلِهِۦ مُفۡتَرَيَٰتٖ وَٱدۡعُواْ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ\nЁки: «Ўзи тўқиб олди», дерларми? Сен: «Бас, агар ростгўй бўлсаларингиз, Аллоҳдан бошқа кимни чақиришга қодир бўлсангиз, чақириб, бунга ўхшаш ўнта тўқилган сура келтиринг-чи», дегин'}, {'chapter': 11, 'verse': 14, 'text': 'فَإِلَّمۡ يَسۡتَجِيبُواْ لَكُمۡ فَٱعۡلَمُوٓاْ أَنَّمَآ أُنزِلَ بِعِلۡمِ ٱللَّهِ وَأَن لَّآ إِلَٰهَ إِلَّا هُوَۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ\nАгар жавоб бера олмасалар, бас, билингки, у фақатгина Аллоҳнинг илми ила нозил қилингандир. Ундан ўзга бирон илоҳ йўқдир. Энди мусулмон бўларсизлар'}, {'chapter': 11, 'verse': 15, 'text': 'مَن كَانَ يُرِيدُ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا وَزِينَتَهَا نُوَفِّ إِلَيۡهِمۡ أَعۡمَٰلَهُمۡ فِيهَا وَهُمۡ فِيهَا لَا يُبۡخَسُونَ\nКимки бу дунё ҳаётини ва унинг зийнатини хоҳласа, бу борадаги амаллари(самараси)ни тўлиқ берамиз. Улар бунда камситилмаслар'}, {'chapter': 11, 'verse': 16, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَيۡسَ لَهُمۡ فِي ٱلۡأٓخِرَةِ إِلَّا ٱلنَّارُۖ وَحَبِطَ مَا صَنَعُواْ فِيهَا وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ\nАна ўшаларга охиратда дўзах оловидан бошқа ҳеч вақо йўқдир. Бу дунёда қилган ишлари ҳабата бўлди ва қилган амаллари ботил бўлди'}, {'chapter': 11, 'verse': 17, 'text': 'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ وَيَتۡلُوهُ شَاهِدٞ مِّنۡهُ وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةًۚ أُوْلَـٰٓئِكَ يُؤۡمِنُونَ بِهِۦۚ وَمَن يَكۡفُرۡ بِهِۦ مِنَ ٱلۡأَحۡزَابِ فَٱلنَّارُ مَوۡعِدُهُۥۚ فَلَا تَكُ فِي مِرۡيَةٖ مِّنۡهُۚ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ\nЎз Роббидан аниқ ҳужжатга эга бўлган, унга Ўша (Робб)нинг Ўзидан гувоҳ эргашган ва у(гувоҳ)дан олдин Мусонинг китоби имом ва раҳмат ҳолида унга (шоҳид) бўлган кишига(куфр келтирадилар)ми? Ана ўшалар у(шоҳид)га иймон келтирурлар. Гуруҳлардан ким унга куфр келтирса, унга ваъда қилинган жой жаҳаннамдир. Сен у ҳақда шак-шубҳада бўлма! Албатта, у Роббингдан келган ҳақдир. Лекин одамларнинг кўплари иймон келтирмаслар'}, {'chapter': 11, 'verse': 18, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًاۚ أُوْلَـٰٓئِكَ يُعۡرَضُونَ عَلَىٰ رَبِّهِمۡ وَيَقُولُ ٱلۡأَشۡهَٰدُ هَـٰٓؤُلَآءِ ٱلَّذِينَ كَذَبُواْ عَلَىٰ رَبِّهِمۡۚ أَلَا لَعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِينَ\nАллоҳга нисбатан ёлғон тўқиган кимсадан ҳам золимроқ одам борми? Ана ўшалар Роббиларига рўбарў қилинурлар ва гувоҳлар: «Анавилар Роббилари шаънига ёлғон гапирганлардир. Огоҳ бўлингким, Аллоҳнинг лаънати золимларгадир», дерлар'}, {'chapter': 11, 'verse': 19, 'text': 'ٱلَّذِينَ يَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ\nУлар Аллоҳнинг йўлидан тўсарлар ва унинг эгри бўлишини хоҳларлар ҳамда улар охиратга куфр келтирувчи кимсалардир'}, {'chapter': 11, 'verse': 20, 'text': 'أُوْلَـٰٓئِكَ لَمۡ يَكُونُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَمَا كَانَ لَهُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِيَآءَۘ يُضَٰعَفُ لَهُمُ ٱلۡعَذَابُۚ مَا كَانُواْ يَسۡتَطِيعُونَ ٱلسَّمۡعَ وَمَا كَانُواْ يُبۡصِرُونَ\nАна ўшалар ер юзида қочиб қутулувчи бўла олмадилар, уларга Аллоҳдан ўзга дўст ҳам бўлмади, азоб бир неча баробар қилинур. Улар эшитишга қодир бўла олмадилар ва кўра олмадилар ҳам'}, {'chapter': 11, 'verse': 21, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nАна ўшалар ўзларига зиён қилганлардир ва тўқиган нарсалари улардан ғойиб бўлди'}, {'chapter': 11, 'verse': 22, 'text': 'لَا جَرَمَ أَنَّهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ\nШак-шубҳасиз, албатта, улар охиратда яна ҳам кўпроқ зиён кўргувчилардир'}, {'chapter': 11, 'verse': 23, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَأَخۡبَتُوٓاْ إِلَىٰ رَبِّهِمۡ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nАлбатта, иймон келтирганлар, яхши амаллар қилганлар ва Роббиларига ишонч ила бўйинсунганлар, ана ўшалар, жаннат эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 11, 'verse': 24, 'text': '۞مَثَلُ ٱلۡفَرِيقَيۡنِ كَٱلۡأَعۡمَىٰ وَٱلۡأَصَمِّ وَٱلۡبَصِيرِ وَٱلسَّمِيعِۚ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ أَفَلَا تَذَكَّرُونَ\nБу икки гуруҳнинг мисоли худди кару кўр ҳамда эшитувчи ва кўрувчига ўхшайдир. Улар бир-бирларига баробар бўла оладиларми? Ибрат олмайсизларми'}, {'chapter': 11, 'verse': 25, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ إِنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ\nАлбатта, Биз Нуҳни ўз қавмига: «Албатта, мен сизларга очиқ-ойдин огоҳлантирувчиман'}, {'chapter': 11, 'verse': 26, 'text': 'أَن لَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۖ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ أَلِيمٖ\nАллоҳдан ўзгага ҳеч ибодат қилманглар, албатта, мен сизларга аламли кун азоби бўлишидан қўрқаман», дедириб юбордик'}, {'chapter': 11, 'verse': 27, 'text': 'فَقَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا نَرَىٰكَ إِلَّا بَشَرٗا مِّثۡلَنَا وَمَا نَرَىٰكَ ٱتَّبَعَكَ إِلَّا ٱلَّذِينَ هُمۡ أَرَاذِلُنَا بَادِيَ ٱلرَّأۡيِ وَمَا نَرَىٰ لَكُمۡ عَلَيۡنَا مِن فَضۡلِۭ بَلۡ نَظُنُّكُمۡ كَٰذِبِينَ\nУнинг қавмидан куфр келтирган зодагонлар: «Биз сенинг ҳам фақат ўзимизга ўхшаган одам эканингни кўряпмиз, сенга ичимиздан эси паст пасткашларимизгина эргашаётганини кўряпмиз, сизларнинг биздан устун фазлингизни кўрмаяпмиз, балки сизларни ёлғончи деб ўйлаяпмиз», дедилар'}, {'chapter': 11, 'verse': 28, 'text': 'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي رَحۡمَةٗ مِّنۡ عِندِهِۦ فَعُمِّيَتۡ عَلَيۡكُمۡ أَنُلۡزِمُكُمُوهَا وَأَنتُمۡ لَهَا كَٰرِهُونَ\nУ: «Эй қавмим, айтинг-чи, агар мен Роббимдан аниқ ҳужжатга эга бўлган бўлсам ва У менга Ўз ҳузуридан раҳмат берган бўлса-ю, сизга махфий қолган бўлса, уни сизга, агар ёқтирмасангиз ҳам, мажбурлаймизми?!» деди'}, {'chapter': 11, 'verse': 29, 'text': 'وَيَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مَالًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۚ وَمَآ أَنَا۠ بِطَارِدِ ٱلَّذِينَ ءَامَنُوٓاْۚ إِنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ\n«Эй қавмим, бу (даъватим) учун сиздан мол сўрамасман. Менинг ажрим фақат Аллоҳнинг зиммасида. Мен иймон келтирганларни қувловчи эмасман. Албатта, улар Роббиларига рўбарў бўлувчилардир. Аммо мен сизларнинг жоҳил қавм эканингизни кўрмоқдаман'}, {'chapter': 11, 'verse': 30, 'text': 'وَيَٰقَوۡمِ مَن يَنصُرُنِي مِنَ ٱللَّهِ إِن طَرَدتُّهُمۡۚ أَفَلَا تَذَكَّرُونَ\nЭй қавмим, агар уларни қувсам, ким менга Аллоҳ(азоби)дан (қутулишга) ёрдам беради? Ўйлаб кўрмайсизларми'}, {'chapter': 11, 'verse': 31, 'text': 'وَلَآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ ٱللَّهِ وَلَآ أَعۡلَمُ ٱلۡغَيۡبَ وَلَآ أَقُولُ إِنِّي مَلَكٞ وَلَآ أَقُولُ لِلَّذِينَ تَزۡدَرِيٓ أَعۡيُنُكُمۡ لَن يُؤۡتِيَهُمُ ٱللَّهُ خَيۡرًاۖ ٱللَّهُ أَعۡلَمُ بِمَا فِيٓ أَنفُسِهِمۡ إِنِّيٓ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ\nМен сизларга: «Ҳузуримда Аллоҳнинг хазиналари бор», демасман. Fайбни ҳам билмасман. «Мен фариштаман», демасман. «Кўзингизга ҳақир кўринаётганларга Аллоҳ ҳаргиз яхшилик бермас», демасман. Уларнинг дилларида нима борлигини Аллоҳ яхши биладир. Акс ҳолда, мен, албатта, золимлардан бўлиб қолурман», деди'}, {'chapter': 11, 'verse': 32, 'text': 'قَالُواْ يَٰنُوحُ قَدۡ جَٰدَلۡتَنَا فَأَكۡثَرۡتَ جِدَٰلَنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУлар: «Эй, Нуҳ, батаҳқиқ, сен биз билан тортишдинг, кўп тортишдинг. Энди, агар ростгўйлардан бўлсанг, бизга ваъда қилаётган нарсангни келтир», дедилар'}, {'chapter': 11, 'verse': 33, 'text': 'قَالَ إِنَّمَا يَأۡتِيكُم بِهِ ٱللَّهُ إِن شَآءَ وَمَآ أَنتُم بِمُعۡجِزِينَ\nУ: «Уни сизларга, агар хоҳласа, фақат Аллоҳгина келтиради ва сизлар қочиб қутулувчи эмассиз'}, {'chapter': 11, 'verse': 34, 'text': 'وَلَا يَنفَعُكُمۡ نُصۡحِيٓ إِنۡ أَرَدتُّ أَنۡ أَنصَحَ لَكُمۡ إِن كَانَ ٱللَّهُ يُرِيدُ أَن يُغۡوِيَكُمۡۚ هُوَ رَبُّكُمۡ وَإِلَيۡهِ تُرۡجَعُونَ\nВа агар Аллоҳ сизларни йўлдан оздиришни истаса, мен насиҳат қилишни хоҳлаганим билан, насиҳатим сизларга манфаат бермас. У сизнинг Роббингиздир ва Унга қайтарилурсиз», деди'}, {'chapter': 11, 'verse': 35, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَعَلَيَّ إِجۡرَامِي وَأَنَا۠ بَرِيٓءٞ مِّمَّا تُجۡرِمُونَ\nЁки: «Уни тўқиб олди», дерларми? «Агар уни тўқиб олган бўлсам, жиноятим ўз зиммамда ва мен сиз қилаётган жиноятдан холидирман», деб айт'}, {'chapter': 11, 'verse': 36, 'text': 'وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُۥ لَن يُؤۡمِنَ مِن قَوۡمِكَ إِلَّا مَن قَدۡ ءَامَنَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَفۡعَلُونَ\nВа Нуҳга: «Қавмингдан аввал иймон келтирганлардан бошқа ҳеч ким иймон келтирмас ва уларнинг қилмишларидан қайғуга тушмагин'}, {'chapter': 11, 'verse': 37, 'text': 'وَٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ\nБизнинг риоятимиз ва ваҳийимиз ила кема ясагин ҳамда зулм қилганлар тўғрисида Менга гап очмагин, албатта, улар ғарқ бўлгувчилардир», деб ваҳий қилдик'}, {'chapter': 11, 'verse': 38, 'text': 'وَيَصۡنَعُ ٱلۡفُلۡكَ وَكُلَّمَا مَرَّ عَلَيۡهِ مَلَأٞ مِّن قَوۡمِهِۦ سَخِرُواْ مِنۡهُۚ قَالَ إِن تَسۡخَرُواْ مِنَّا فَإِنَّا نَسۡخَرُ مِنكُمۡ كَمَا تَسۡخَرُونَ\nУ кема ясамоқда. Ўз қавмидан бўлган зодагонлар қачон олдидан ўтсалар, уни масхара қилдилар. У: «Агар бизни масхара қилсангиз, биз ҳам худди сиз бизни масхара қилганингиздек, сизни масхара қилурмиз'}, {'chapter': 11, 'verse': 39, 'text': 'فَسَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ\nБас, яқинда кимга шарманда қилувчи азоб келишини ва кимнинг устига муқим азоб тушишини билиб олурсиз», деди'}, {'chapter': 11, 'verse': 40, 'text': 'حَتَّىٰٓ إِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ قُلۡنَا ٱحۡمِلۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ وَمَنۡ ءَامَنَۚ وَمَآ ءَامَنَ مَعَهُۥٓ إِلَّا قَلِيلٞ\nТоки Бизнинг амримиз келиб, таннур фаввора отганда: «У(кема)га ҳар нарсадан бир жуфтдан ва аҳлингни ол, магар аввал сўз кетганни (олма) ва иймон келтирганларни ҳам ол», дедик. У билан бирга иймон келтирганлар жуда оз эди'}, {'chapter': 11, 'verse': 41, 'text': '۞وَقَالَ ٱرۡكَبُواْ فِيهَا بِسۡمِ ٱللَّهِ مَجۡرٜىٰهَا وَمُرۡسَىٰهَآۚ إِنَّ رَبِّي لَغَفُورٞ رَّحِيمٞ\nВа у: «У(кема)га мининглар, унинг юриши ҳам, туриши ҳам Аллоҳнинг исми ила бўлур. Албатта, Роббим мағфиратли ва раҳимлидир», деди'}, {'chapter': 11, 'verse': 42, 'text': 'وَهِيَ تَجۡرِي بِهِمۡ فِي مَوۡجٖ كَٱلۡجِبَالِ وَنَادَىٰ نُوحٌ ٱبۡنَهُۥ وَكَانَ فِي مَعۡزِلٖ يَٰبُنَيَّ ٱرۡكَب مَّعَنَا وَلَا تَكُن مَّعَ ٱلۡكَٰفِرِينَ\nВа у (кема) уларни тоғлардек тўлқинда олиб кетаётганида, Нуҳ бир четда турган ўғлига: «Эй ўғлим, биз билан бирга мингин, кофирлар билан бирга бўлмагин», деди'}, {'chapter': 11, 'verse': 43, 'text': 'قَالَ سَـَٔاوِيٓ إِلَىٰ جَبَلٖ يَعۡصِمُنِي مِنَ ٱلۡمَآءِۚ قَالَ لَا عَاصِمَ ٱلۡيَوۡمَ مِنۡ أَمۡرِ ٱللَّهِ إِلَّا مَن رَّحِمَۚ وَحَالَ بَيۡنَهُمَا ٱلۡمَوۡجُ فَكَانَ مِنَ ٱلۡمُغۡرَقِينَ\nУ (ўғил): «Тоққа жойлашиб олсам, мени сувдан сақлайди», деди. У эса: «Бугун Аллоҳнинг амридан сақловчи йўқдир. Магар кимни раҳим қилсагина (сақлар)», деди. Шунда ораларини тўлқин тўсди ва у (ўғил) ғарқ бўлганлардан бўлди'}, {'chapter': 11, 'verse': 44, 'text': 'وَقِيلَ يَـٰٓأَرۡضُ ٱبۡلَعِي مَآءَكِ وَيَٰسَمَآءُ أَقۡلِعِي وَغِيضَ ٱلۡمَآءُ وَقُضِيَ ٱلۡأَمۡرُ وَٱسۡتَوَتۡ عَلَى ٱلۡجُودِيِّۖ وَقِيلَ بُعۡدٗا لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ\n«Эй ер, сувингни ютгин, эй осмон, ўзингни тутгин», дейилди. Сув қуриди. Фармон бажарилди ва (кема) Жудий(тоғи)га жойлашди. Ҳамда «Золим қавмлар йўқолсин!» дейилди'}, {'chapter': 11, 'verse': 45, 'text': 'وَنَادَىٰ نُوحٞ رَّبَّهُۥ فَقَالَ رَبِّ إِنَّ ٱبۡنِي مِنۡ أَهۡلِي وَإِنَّ وَعۡدَكَ ٱلۡحَقُّ وَأَنتَ أَحۡكَمُ ٱلۡحَٰكِمِينَ\nНуҳ Роббига нидо қилиб: «Роббим, албатта, ўғлим аҳлимдандир, албатта, ваъданг ҳақдир ва Сен ҳукм қилгувчиларнинг энг ҳикматлисисан», деди'}, {'chapter': 11, 'verse': 46, 'text': 'قَالَ يَٰنُوحُ إِنَّهُۥ لَيۡسَ مِنۡ أَهۡلِكَۖ إِنَّهُۥ عَمَلٌ غَيۡرُ صَٰلِحٖۖ فَلَا تَسۡـَٔلۡنِ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۖ إِنِّيٓ أَعِظُكَ أَن تَكُونَ مِنَ ٱلۡجَٰهِلِينَ\nУ зот: «Эй Нуҳ, албатта у аҳлингдан эмас. Албатта, у яхши амал эмасдир. Ўзингнинг илминг бўлмаган нарсани зинҳор сўрамагин. Мен сенга жоҳиллардан бўлмаслигингни насиҳат қиламан», деди'}, {'chapter': 11, 'verse': 47, 'text': 'قَالَ رَبِّ إِنِّيٓ أَعُوذُ بِكَ أَنۡ أَسۡـَٔلَكَ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞۖ وَإِلَّا تَغۡفِرۡ لِي وَتَرۡحَمۡنِيٓ أَكُن مِّنَ ٱلۡخَٰسِرِينَ\nУ: «Роббим, мен ўзимнинг илмим бўлмаган нарсани Сендан сўрамоқдан паноҳ тиларман. Агар мени мағфират қилмасанг ва менга раҳим кўрсатмасанг, зиён кўргувчилардан бўлурман», деди'}, {'chapter': 11, 'verse': 48, 'text': 'قِيلَ يَٰنُوحُ ٱهۡبِطۡ بِسَلَٰمٖ مِّنَّا وَبَرَكَٰتٍ عَلَيۡكَ وَعَلَىٰٓ أُمَمٖ مِّمَّن مَّعَكَۚ وَأُمَمٞ سَنُمَتِّعُهُمۡ ثُمَّ يَمَسُّهُم مِّنَّا عَذَابٌ أَلِيمٞ\n«Эй Нуҳ, Биздан Сенга ва сен билан бирга бўлган умматларга бўлган тинчлик ва баракотлар ила (кемадан) тушгин. Яна бир умматлар бўлур, уларни бир оз ҳузурлантирурмиз, сўнгра уларни Биздан бўлган аламли азоб тутар», дейилди'}, {'chapter': 11, 'verse': 49, 'text': 'تِلۡكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهَآ إِلَيۡكَۖ مَا كُنتَ تَعۡلَمُهَآ أَنتَ وَلَا قَوۡمُكَ مِن قَبۡلِ هَٰذَاۖ فَٱصۡبِرۡۖ إِنَّ ٱلۡعَٰقِبَةَ لِلۡمُتَّقِينَ\nАна шу ғайб хабарларини сенга ваҳий қилмоқдамиз. Бундан олдин уларни сен ҳам билмас эдинг ва сенинг қавминг ҳам. Бас, сабр қил, албатта, оқибат тақводорларникидир'}, {'chapter': 11, 'verse': 50, 'text': 'وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۖ إِنۡ أَنتُمۡ إِلَّا مُفۡتَرُونَ\nВа Одга ўз биродарлари Ҳудни (юбордик). У: «Эй қавмим, Аллоҳга ибодат қилинг. Сиз учун Ундан ўзга илоҳ йўқдир. Сизлар фақат тўқиб олувчиларсиз, холос'}, {'chapter': 11, 'verse': 51, 'text': 'يَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱلَّذِي فَطَرَنِيٓۚ أَفَلَا تَعۡقِلُونَ\nЭй қавмим, мен сизлардан бу (даъватим) учун ажр сўрамайман. Менинг ажрим, фақат, мени яратган Зотнинг зиммасидадир. Ақл юритмайсизларми'}, {'chapter': 11, 'verse': 52, 'text': 'وَيَٰقَوۡمِ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُرۡسِلِ ٱلسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا وَيَزِدۡكُمۡ قُوَّةً إِلَىٰ قُوَّتِكُمۡ وَلَا تَتَوَلَّوۡاْ مُجۡرِمِينَ\nЭй қавмим, Роббингизга истиғфор айтинг, сўнгра Унга тавба қилинг, шунда У устингизга осмондан барака ёмғири юборади ва қувватингизга қувватни зиёда қилади. Жиноятчи бўлган ҳолингизда юз ўгириб кетманг», деди'}, {'chapter': 11, 'verse': 53, 'text': 'قَالُواْ يَٰهُودُ مَا جِئۡتَنَا بِبَيِّنَةٖ وَمَا نَحۡنُ بِتَارِكِيٓ ءَالِهَتِنَا عَن قَوۡلِكَ وَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ\nУлар: «Эй Ҳуд, бизга очиқ-ойдин ҳужжат келтирмадинг. Биз сенинг гапинг учун худоларимизни тарк қилувчи эмасмиз ва биз сенга иймон келтирувчи ҳам эмасмиз'}, {'chapter': 11, 'verse': 54, 'text': 'إِن نَّقُولُ إِلَّا ٱعۡتَرَىٰكَ بَعۡضُ ءَالِهَتِنَا بِسُوٓءٖۗ قَالَ إِنِّيٓ أُشۡهِدُ ٱللَّهَ وَٱشۡهَدُوٓاْ أَنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ\nБиз фақат, сени баъзи худоларимиз ёмонлик ила урибди, деймиз, холос», дедилар. У: «Албатта, мен Аллоҳни гувоҳ қиламан ва сиз ҳам гувоҳ бўлингки, мен шерик келтираётганингиздан покман'}, {'chapter': 11, 'verse': 55, 'text': 'مِن دُونِهِۦۖ فَكِيدُونِي جَمِيعٗا ثُمَّ لَا تُنظِرُونِ\nУндан ўзга. Бас, ҳаммангиз менга қарши ҳийла қилаверинглар, менга муҳлат берманглар'}, {'chapter': 11, 'verse': 56, 'text': 'إِنِّي تَوَكَّلۡتُ عَلَى ٱللَّهِ رَبِّي وَرَبِّكُمۚ مَّا مِن دَآبَّةٍ إِلَّا هُوَ ءَاخِذُۢ بِنَاصِيَتِهَآۚ إِنَّ رَبِّي عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАлбатта, мен Роббим ва Роббингиз бўлмиш Аллоҳга таваккал қилдим. Ҳар бир ўрмалайдиган нарсанинг пешонаси Унинг чангалидадир. Албатта, Роббим тўғри йўлдадир'}, {'chapter': 11, 'verse': 57, 'text': 'فَإِن تَوَلَّوۡاْ فَقَدۡ أَبۡلَغۡتُكُم مَّآ أُرۡسِلۡتُ بِهِۦٓ إِلَيۡكُمۡۚ وَيَسۡتَخۡلِفُ رَبِّي قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّونَهُۥ شَيۡـًٔاۚ إِنَّ رَبِّي عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ\nАгар юз ўгириб кетсангиз, батаҳқиқ, мен сизларга нима ила юборилган бўлсам, уни сизларга етказдим. Роббим ўрнингизга сиздан бошқа қавмни келтиради ва сиз Унга ҳеч бир зарар етказа олмассиз. Албатта, Роббим ҳар бир нарсани муҳофаза қилгувчи Зотдир», деди'}, {'chapter': 11, 'verse': 58, 'text': 'وَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا هُودٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَنَجَّيۡنَٰهُم مِّنۡ عَذَابٍ غَلِيظٖ\nАмримиз келган пайтда Ҳудга ва у билан бирга иймон келтирганларга Ўз раҳматимиз ила нажот бердик ва уларни қаттиқ азобдан қутқардик'}, {'chapter': 11, 'verse': 59, 'text': 'وَتِلۡكَ عَادٞۖ جَحَدُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَعَصَوۡاْ رُسُلَهُۥ وَٱتَّبَعُوٓاْ أَمۡرَ كُلِّ جَبَّارٍ عَنِيدٖ\nАнави Оддир. Улар Роббилари оятларини инкор қилдилар. Унинг Пайғамбарларига осий бўлдилар. Ҳар хил жабрчи ва қайсарнинг ишига эргашдилар'}, {'chapter': 11, 'verse': 60, 'text': 'وَأُتۡبِعُواْ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗ وَيَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَآ إِنَّ عَادٗا كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّعَادٖ قَوۡمِ هُودٖ\nВа уларга бу дунёда ҳам, қиёмат кунида ҳам лаънат эргашур. Огоҳ бўлингким, албатта, одликлар Роббиларига куфр келтирган эдилар. Огоҳ бўлингким, Ҳуд қавми Од йўқолсин'}, {'chapter': 11, 'verse': 61, 'text': '۞وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ هُوَ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَٱسۡتَعۡمَرَكُمۡ فِيهَا فَٱسۡتَغۡفِرُوهُ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي قَرِيبٞ مُّجِيبٞ\nВа Самудга биродарлари Солиҳни (юбордик). У: «Эй қавмим, Аллоҳга ибодат қилинг. Сиз учун Ундан ўзга илоҳ йўқ. У сизларни ердан пайдо қилди ва унга сизни ободлиги учун қўйди. Бас, Унга истиғфор айтинг, сўнгра Унга тавба қилинг. Албатта, Роббим яқин ва ижобат қилгувчидир», деди'}, {'chapter': 11, 'verse': 62, 'text': 'قَالُواْ يَٰصَٰلِحُ قَدۡ كُنتَ فِينَا مَرۡجُوّٗا قَبۡلَ هَٰذَآۖ أَتَنۡهَىٰنَآ أَن نَّعۡبُدَ مَا يَعۡبُدُ ءَابَآؤُنَا وَإِنَّنَا لَفِي شَكّٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ مُرِيبٖ\nУлар: «Эй Солиҳ, бундан олдин сен ичимизда орзу қилинган эдинг. Энди бизни ота-боболаримиз ибодат қиладиган нарсага ибодат қилмоғимиздан қайтарасанми? Албатта, биз сен даъват қилаётган нарса ҳақида шак-шубҳадамиз», дедилар'}, {'chapter': 11, 'verse': 63, 'text': 'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي مِنۡهُ رَحۡمَةٗ فَمَن يَنصُرُنِي مِنَ ٱللَّهِ إِنۡ عَصَيۡتُهُۥۖ فَمَا تَزِيدُونَنِي غَيۡرَ تَخۡسِيرٖ\nУ: «Эй қавмим, хабар беринг-чи, агар мен Роббим томонидан очиқ-ойдин ҳужжатга эга бўлсам ва менга У томонидан раҳмат берилган бўлса-ю, мен Унга осий бўлсам, ким менга Аллоҳ (азоби)дан (қутулишга) ёрдам беради?! Бас, сиз менга зиёндан ўзгани зиёда қилмассизлар'}, {'chapter': 11, 'verse': 64, 'text': 'وَيَٰقَوۡمِ هَٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٞ قَرِيبٞ\nЭй қавмим, Аллоҳнинг мана бу туяси сизга белги-мўъжизадир. Бас, уни тек қўйинглар, Аллоҳнинг ерида еб юрсин, унга ёмонлик етказманглар, акс ҳолда, сизни яқин азоб ушлайди», деди'}, {'chapter': 11, 'verse': 65, 'text': 'فَعَقَرُوهَا فَقَالَ تَمَتَّعُواْ فِي دَارِكُمۡ ثَلَٰثَةَ أَيَّامٖۖ ذَٰلِكَ وَعۡدٌ غَيۡرُ مَكۡذُوبٖ\nБас, у(туя)ни сўйдилар. Сўнг у: «Диёрингизда уч кун баҳраманд бўлиб туринг. Бу, ёлғон бўлмаган ваъдадир», деди'}, {'chapter': 11, 'verse': 66, 'text': 'فَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا صَٰلِحٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَمِنۡ خِزۡيِ يَوۡمِئِذٍۚ إِنَّ رَبَّكَ هُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ\nАмримиз келган пайтда Ўз раҳматимиз ила Солиҳга ва у билан бирга иймон келтирганларга нажот бердик ва ўша куннинг шармандалигидан қутқардик. Албатта, Роббинг, Унинг Ўзи кучли ва азиздир'}, {'chapter': 11, 'verse': 67, 'text': 'وَأَخَذَ ٱلَّذِينَ ظَلَمُواْ ٱلصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيَٰرِهِمۡ جَٰثِمِينَ\nВа зулм қилганларни даҳшатли қичқириқ олди. Бас, диёрларида тўкилдилар'}, {'chapter': 11, 'verse': 68, 'text': 'كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَآ إِنَّ ثَمُودَاْ كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّثَمُودَ\nХудди унда турмаганга ўхшаб. Огоҳ бўлингким, албатта, Самуд (қавми) Роббиларига куфр келтирган эдилар. Огоҳ бўлингким, Самуд йўқолсин'}, {'chapter': 11, 'verse': 69, 'text': 'وَلَقَدۡ جَآءَتۡ رُسُلُنَآ إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞۖ فَمَا لَبِثَ أَن جَآءَ بِعِجۡلٍ حَنِيذٖ\nБатаҳқиқ, элчиларимиз Иброҳимга хушхабар келтирдилар. Улар: «Салом», дедилар. У: «Салом», деди. Ва кўп ўтмай қовурилган бузоқ келтирди'}, {'chapter': 11, 'verse': 70, 'text': 'فَلَمَّا رَءَآ أَيۡدِيَهُمۡ لَا تَصِلُ إِلَيۡهِ نَكِرَهُمۡ وَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۚ قَالُواْ لَا تَخَفۡ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمِ لُوطٖ\nУларнинг қўллари у(таом)га етмаётганини кўриб, улардан ҳайрон бўлди ва ичида хавфсиради. Улар: «Қўрқма, биз Лут қавмига юборилганмиз», дедилар'}, {'chapter': 11, 'verse': 71, 'text': 'وَٱمۡرَأَتُهُۥ قَآئِمَةٞ فَضَحِكَتۡ فَبَشَّرۡنَٰهَا بِإِسۡحَٰقَ وَمِن وَرَآءِ إِسۡحَٰقَ يَعۡقُوبَ\nТик турган хотини кулди. Бас, Биз у(хотин)га Исҳоқнинг ва Исҳоқдан кейин Яъқубнинг хушхабарини бердик'}, {'chapter': 11, 'verse': 72, 'text': 'قَالَتۡ يَٰوَيۡلَتَىٰٓ ءَأَلِدُ وَأَنَا۠ عَجُوزٞ وَهَٰذَا بَعۡلِي شَيۡخًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عَجِيبٞ\nУ: «Вой шўрим, ўзим кампир, манави эрим чол бўлса ҳам, туғаманми?! Бу қизиқ нарса-ку!» – деди'}, {'chapter': 11, 'verse': 73, 'text': 'قَالُوٓاْ أَتَعۡجَبِينَ مِنۡ أَمۡرِ ٱللَّهِۖ رَحۡمَتُ ٱللَّهِ وَبَرَكَٰتُهُۥ عَلَيۡكُمۡ أَهۡلَ ٱلۡبَيۡتِۚ إِنَّهُۥ حَمِيدٞ مَّجِيدٞ\nУлар: «Аллоҳнинг амридан ажабланасанми? Эй, аҳли байтлар, сизларга Аллоҳнинг раҳмати ва баракотлари бўлгай. Албатта, У мақталган ва буюк Зотдир», дедилар'}, {'chapter': 11, 'verse': 74, 'text': 'فَلَمَّا ذَهَبَ عَنۡ إِبۡرَٰهِيمَ ٱلرَّوۡعُ وَجَآءَتۡهُ ٱلۡبُشۡرَىٰ يُجَٰدِلُنَا فِي قَوۡمِ لُوطٍ\nИброҳимдан қўрқинч кетиб, хушхабар етгач, Биз билан Лут қавми ҳақида баҳслаша бошлади'}, {'chapter': 11, 'verse': 75, 'text': 'إِنَّ إِبۡرَٰهِيمَ لَحَلِيمٌ أَوَّـٰهٞ مُّنِيبٞ\nАлбатта, Иброҳим ҳалийм, тазаррули ва У зотга тез қайтувчидир'}, {'chapter': 11, 'verse': 76, 'text': 'يَـٰٓإِبۡرَٰهِيمُ أَعۡرِضۡ عَنۡ هَٰذَآۖ إِنَّهُۥ قَدۡ جَآءَ أَمۡرُ رَبِّكَۖ وَإِنَّهُمۡ ءَاتِيهِمۡ عَذَابٌ غَيۡرُ مَرۡدُودٖ\nЭй Иброҳим, бундан воз кеч. Чунки, Роббингнинг амри келиб бўлган. Албатта, уларга қайтарилмас азоб келгусидир'}, {'chapter': 11, 'verse': 77, 'text': 'وَلَمَّا جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗا وَقَالَ هَٰذَا يَوۡمٌ عَصِيبٞ\nЭлчиларимиз Лутга келган пайтларида, у уларни (кўриб) ёмон ҳолга тушди ва қўл-оёғи бўшашди ҳамда: «Бу қийин кундир», деди'}, {'chapter': 11, 'verse': 78, 'text': 'وَجَآءَهُۥ قَوۡمُهُۥ يُهۡرَعُونَ إِلَيۡهِ وَمِن قَبۡلُ كَانُواْ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِۚ قَالَ يَٰقَوۡمِ هَـٰٓؤُلَآءِ بَنَاتِي هُنَّ أَطۡهَرُ لَكُمۡۖ فَٱتَّقُواْ ٱللَّهَ وَلَا تُخۡزُونِ فِي ضَيۡفِيٓۖ أَلَيۡسَ مِنكُمۡ رَجُلٞ رَّشِيدٞ\nҚавми унга шошилиб, ҳаллослаб етиб келди. Улар бундан олдин гуноҳлар қилар эдилар. У: «Эй қавмим, анаву қизларим сизлар учун покроқ-ку. Бас, Аллоҳдан қўрқинг! Меҳмонларим ҳақида мени шарманда қилманг. Ичингизда бирорта тузукроқ одам йўқ-ми?!» – деди'}, {'chapter': 11, 'verse': 79, 'text': 'قَالُواْ لَقَدۡ عَلِمۡتَ مَا لَنَا فِي بَنَاتِكَ مِنۡ حَقّٖ وَإِنَّكَ لَتَعۡلَمُ مَا نُرِيدُ\nУлар: «Сенинг қизларингда бизнинг ҳаққимиз йўқлигини билгансан ва биз нимани хоҳлашимизни, албатта, сен биласан», – дедилар'}, {'chapter': 11, 'verse': 80, 'text': 'قَالَ لَوۡ أَنَّ لِي بِكُمۡ قُوَّةً أَوۡ ءَاوِيٓ إِلَىٰ رُكۡنٖ شَدِيدٖ\nУ: «Қани энди сизларга қувватим етса эди ёки мустаҳкам ерга беркинсайдим», – деди'}, {'chapter': 11, 'verse': 81, 'text': 'قَالُواْ يَٰلُوطُ إِنَّا رُسُلُ رَبِّكَ لَن يَصِلُوٓاْ إِلَيۡكَۖ فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ ٱلَّيۡلِ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٌ إِلَّا ٱمۡرَأَتَكَۖ إِنَّهُۥ مُصِيبُهَا مَآ أَصَابَهُمۡۚ إِنَّ مَوۡعِدَهُمُ ٱلصُّبۡحُۚ أَلَيۡسَ ٱلصُّبۡحُ بِقَرِيبٖ\nУлар: «Эй Лут, биз Роббингнинг элчиларимиз, булар сенга ҳаргиз тега олмаслар. Бас, кечанинг бир бўлагида аҳлинг ила юргин ва сизлардан хотинингдан бошқа ҳеч ким ўгирилмасин. Чунки, уларга етадиган мусийбат у(аёл)гада етгувчидир. Уларга тайин қилинган вақт субҳдир. Субҳ яқин эмасми?!» – дедилар'}, {'chapter': 11, 'verse': 82, 'text': 'فَلَمَّا جَآءَ أَمۡرُنَا جَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهَا حِجَارَةٗ مِّن سِجِّيلٖ مَّنضُودٖ\nАмримиз келган пайтда у ўлкани остин-устун қилдик ва устидан кетма-кет сопол тошларни ёғдирдик'}, {'chapter': 11, 'verse': 83, 'text': 'مُّسَوَّمَةً عِندَ رَبِّكَۖ وَمَا هِيَ مِنَ ٱلظَّـٰلِمِينَ بِبَعِيدٖ\nУларга Роббинг ҳузурида белги қўйилган эди ва улар золимлардан узоқ эмасдир'}, {'chapter': 11, 'verse': 84, 'text': '۞وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ وَلَا تَنقُصُواْ ٱلۡمِكۡيَالَ وَٱلۡمِيزَانَۖ إِنِّيٓ أَرَىٰكُم بِخَيۡرٖ وَإِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ مُّحِيطٖ\nВа Мадянга биродарлари Шуъайбни (юбордик). У: «Эй қавмим, Аллоҳга ибодат қилинг, сиз учун Ундан ўзга илоҳ йўқ. Ўлчовни ва тортишни камайтирманглар. Албатта, мен сизларнинг яхшиликда эканликларингизни кўриб турибман. Албатта, мен сизларга қамровли кун азобидан қўрқмоқдаман'}, {'chapter': 11, 'verse': 85, 'text': 'وَيَٰقَوۡمِ أَوۡفُواْ ٱلۡمِكۡيَالَ وَٱلۡمِيزَانَ بِٱلۡقِسۡطِۖ وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nЭй қавмим, ўлчаш ва тортишни адолат ила мукаммал қилинг. Одамларнинг нарсаларини камайтириб қолманг ва ер юзида бузғунчилик қилиб юрманг'}, {'chapter': 11, 'verse': 86, 'text': 'بَقِيَّتُ ٱللَّهِ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ\nАгар мўмин бўлсангиз, Аллоҳнинг наздида боқий қолувчи сиз учун яхшироқдир. Мен сизнинг устингизда қўриқчи эмасман», деди'}, {'chapter': 11, 'verse': 87, 'text': 'قَالُواْ يَٰشُعَيۡبُ أَصَلَوٰتُكَ تَأۡمُرُكَ أَن نَّتۡرُكَ مَا يَعۡبُدُ ءَابَآؤُنَآ أَوۡ أَن نَّفۡعَلَ فِيٓ أَمۡوَٰلِنَا مَا نَشَـٰٓؤُاْۖ إِنَّكَ لَأَنتَ ٱلۡحَلِيمُ ٱلرَّشِيدُ\nУлар: «Эй Шуъайб, сенга бизнинг оталаримиз ибодат қиладиган нарсани тарк қилмоғимизни ёки молларимизда хоҳлаганимизни қилмаслигимизни намозинг амр қилмоқдами?! Сен жуда ҳалийм ва рашид экансан-да», – дедилар'}, {'chapter': 11, 'verse': 88, 'text': 'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَرَزَقَنِي مِنۡهُ رِزۡقًا حَسَنٗاۚ وَمَآ أُرِيدُ أَنۡ أُخَالِفَكُمۡ إِلَىٰ مَآ أَنۡهَىٰكُمۡ عَنۡهُۚ إِنۡ أُرِيدُ إِلَّا ٱلۡإِصۡلَٰحَ مَا ٱسۡتَطَعۡتُۚ وَمَا تَوۡفِيقِيٓ إِلَّا بِٱللَّهِۚ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ\nУ: «Эй қавмим, хабар беринглар-чи, агар мен Роббимдан аниқ ҳужжатга эга бўлсам-чи ва мени Ўзи тарафидан яхши ризқ ила ризқлантирган бўлса-чи?! Мен сизларни ўзим қайтараётган нарсада ўзим хилоф қилмоқчи эмасман. Мен имконим борича ислоҳ қилишдан ўзга ҳеч нарсани хоҳламайман. Менинг муваффақиятим фақат Аллоҳга боғлиқ. Унгагина таваккал қилдим. Унгагина қайтаман'}, {'chapter': 11, 'verse': 89, 'text': 'وَيَٰقَوۡمِ لَا يَجۡرِمَنَّكُمۡ شِقَاقِيٓ أَن يُصِيبَكُم مِّثۡلُ مَآ أَصَابَ قَوۡمَ نُوحٍ أَوۡ قَوۡمَ هُودٍ أَوۡ قَوۡمَ صَٰلِحٖۚ وَمَا قَوۡمُ لُوطٖ مِّنكُم بِبَعِيدٖ\nЭй қавмим, менга хилоф қилишингиз сизларни ё Нуҳ қавмига, ё Ҳуд қавмига, ёки Солиҳ қавмига етган мусийбатга ўхшаш мусийбат етишига олиб бормасин. Лут қавми сизлардан узоқ эмас'}, {'chapter': 11, 'verse': 90, 'text': 'وَٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي رَحِيمٞ وَدُودٞ\nРоббингизга истиғфор айтинг, сўнгра Унга тавба қилинг. Албатта, Роббим раҳимли ва шафқатли Зотдир», – деди'}, {'chapter': 11, 'verse': 91, 'text': 'قَالُواْ يَٰشُعَيۡبُ مَا نَفۡقَهُ كَثِيرٗا مِّمَّا تَقُولُ وَإِنَّا لَنَرَىٰكَ فِينَا ضَعِيفٗاۖ وَلَوۡلَا رَهۡطُكَ لَرَجَمۡنَٰكَۖ وَمَآ أَنتَ عَلَيۡنَا بِعَزِيزٖ\nУлар: «Эй Шуъайб, айтганларингнинг кўпини яхши тушунмадик ва биз сени ичимизда заифҳол кўряпмиз, агар одамларинг бўлмаганида, сени тошбўрон қилар эдик. Сенинг ўзинг биз учун қимматли эмассан», дедилар'}, {'chapter': 11, 'verse': 92, 'text': 'قَالَ يَٰقَوۡمِ أَرَهۡطِيٓ أَعَزُّ عَلَيۡكُم مِّنَ ٱللَّهِ وَٱتَّخَذۡتُمُوهُ وَرَآءَكُمۡ ظِهۡرِيًّاۖ إِنَّ رَبِّي بِمَا تَعۡمَلُونَ مُحِيطٞ\nУ: «Эй қавмим, менинг одамларим сизлар учун Аллоҳдан кўра азизроқми?! Унга орқангизни ўгириб олдингиз. Албатта, Роббим қилаётган амалларингизни иҳота қилувчидир'}, {'chapter': 11, 'verse': 93, 'text': 'وَيَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ سَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَمَنۡ هُوَ كَٰذِبٞۖ وَٱرۡتَقِبُوٓاْ إِنِّي مَعَكُمۡ رَقِيبٞ\nЭй қавмим, имконингиздаги амални қилаверинг, мен ҳам амал қилувчиман. Кимга шарманда қиладиган азоб келишини ва ким ёлғончи эканини тезда биласизлар. Кутинглар, мен ҳам сизлар ила кутувчиман», – деди'}, {'chapter': 11, 'verse': 94, 'text': 'وَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا شُعَيۡبٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَأَخَذَتِ ٱلَّذِينَ ظَلَمُواْ ٱلصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيَٰرِهِمۡ جَٰثِمِينَ\nАмримиз келган пайтда Шуъайбга ва у билан бирга иймонга келганларга Ўз раҳматимиз ила нажот бердик. Зулм қилганларни эса, қичқириқ тутди ва диёрларида тўкилдилар'}, {'chapter': 11, 'verse': 95, 'text': 'كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَا بُعۡدٗا لِّمَدۡيَنَ كَمَا بَعِدَتۡ ثَمُودُ\nХудди у ерда яшамаганлардек. Огоҳ бўлингким, худди Самуд йўқолгандек, Мадян ҳам йўқолди'}, {'chapter': 11, 'verse': 96, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ\nБатаҳқиқ, Биз Мусони оятларимиз ва очиқ-ойдин ҳужжатимиз ила юбордик'}, {'chapter': 11, 'verse': 97, 'text': 'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَٱتَّبَعُوٓاْ أَمۡرَ فِرۡعَوۡنَۖ وَمَآ أَمۡرُ فِرۡعَوۡنَ بِرَشِيدٖ\nФиръавн ва унинг одамларига. Бас, улар Фиръавн ишига эргашдилар. Фиръавннинг иши эса, тўғри эмасди'}, {'chapter': 11, 'verse': 98, 'text': 'يَقۡدُمُ قَوۡمَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ فَأَوۡرَدَهُمُ ٱلنَّارَۖ وَبِئۡسَ ٱلۡوِرۡدُ ٱلۡمَوۡرُودُ\nУ қиёмат куни қавмининг олдига тушиб, уларни дўзахга бошлаб келтирур. У жой қандай ҳам ёмон тушиш жойидир'}, {'chapter': 11, 'verse': 99, 'text': 'وَأُتۡبِعُواْ فِي هَٰذِهِۦ لَعۡنَةٗ وَيَوۡمَ ٱلۡقِيَٰمَةِۚ بِئۡسَ ٱلرِّفۡدُ ٱلۡمَرۡفُودُ\nБу дунёда ҳам, қиёмат кунида ҳам уларга лаънат эргашур. Бу қандай ҳам ёмон ёрдамдир'}, {'chapter': 11, 'verse': 100, 'text': 'ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡقُرَىٰ نَقُصُّهُۥ عَلَيۡكَۖ مِنۡهَا قَآئِمٞ وَحَصِيدٞ\nМана булар шаҳарларнинг хабарларидан бўлиб, уларни сенга қисса қилаётирмиз. Улардан (ўз ўрнида) турганлари ҳам бор, йиғилганлари ҳам бор'}, {'chapter': 11, 'verse': 101, 'text': 'وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن ظَلَمُوٓاْ أَنفُسَهُمۡۖ فَمَآ أَغۡنَتۡ عَنۡهُمۡ ءَالِهَتُهُمُ ٱلَّتِي يَدۡعُونَ مِن دُونِ ٱللَّهِ مِن شَيۡءٖ لَّمَّا جَآءَ أَمۡرُ رَبِّكَۖ وَمَا زَادُوهُمۡ غَيۡرَ تَتۡبِيبٖ\nБиз уларга зулм қилмадик. Лекин ўзларига зулм қилдилар. Роббингнинг амри келган пайтда, Аллоҳни қўйиб илтижо қилган «худо»лари ҳеч бир нарсада уларнинг ҳожатларини чиқара олмади. Уларга зиёндан бошқани зиёда қилмадилар'}, {'chapter': 11, 'verse': 102, 'text': 'وَكَذَٰلِكَ أَخۡذُ رَبِّكَ إِذَآ أَخَذَ ٱلۡقُرَىٰ وَهِيَ ظَٰلِمَةٌۚ إِنَّ أَخۡذَهُۥٓ أَلِيمٞ شَدِيدٌ\nРоббинг золим шаҳарларни ушлаганида, ана шундай ушлайди. Албатта, Унинг ушлаши аламли ва шиддатлидир'}, {'chapter': 11, 'verse': 103, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّمَنۡ خَافَ عَذَابَ ٱلۡأٓخِرَةِۚ ذَٰلِكَ يَوۡمٞ مَّجۡمُوعٞ لَّهُ ٱلنَّاسُ وَذَٰلِكَ يَوۡمٞ مَّشۡهُودٞ\nАлбатта, бунда охират азобидан қўрққан кимса учун ибрат бордир. Бу кун одамлар жамланадиган бир кундир. Бу кун шоҳид бўлинадиган кундир'}, {'chapter': 11, 'verse': 104, 'text': 'وَمَا نُؤَخِّرُهُۥٓ إِلَّا لِأَجَلٖ مَّعۡدُودٖ\nУни Биз фақат саноқли муддатга кечиктирамиз'}, {'chapter': 11, 'verse': 105, 'text': 'يَوۡمَ يَأۡتِ لَا تَكَلَّمُ نَفۡسٌ إِلَّا بِإِذۡنِهِۦۚ فَمِنۡهُمۡ شَقِيّٞ وَسَعِيدٞ\nУ келган кунда ҳеч бир жон гапира олмас. Магар У зотнинг изни ила (гапирур). Бас, улардан бадбахтлари ва бахтлилари бордир'}, {'chapter': 11, 'verse': 106, 'text': 'فَأَمَّا ٱلَّذِينَ شَقُواْ فَفِي ٱلنَّارِ لَهُمۡ فِيهَا زَفِيرٞ وَشَهِيقٌ\nБас, бадбахт бўлганлар дўзахдалар. У ерда улар дод-фарёд қилурлар'}, {'chapter': 11, 'verse': 107, 'text': 'خَٰلِدِينَ فِيهَا مَا دَامَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۚ إِنَّ رَبَّكَ فَعَّالٞ لِّمَا يُرِيدُ\nУлар, модомики, осмонлару ер бор экан, унда абадий қолурлар. Магар Роббинг хоҳласа, (абадий қолмаслар). Албатта, Роббинг нимани хоҳласа, қилгувчи Зотдир'}, {'chapter': 11, 'verse': 108, 'text': '۞وَأَمَّا ٱلَّذِينَ سُعِدُواْ فَفِي ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا مَا دَامَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۖ عَطَآءً غَيۡرَ مَجۡذُوذٖ\nБахтли бўлганлар жаннатдадирлар. Улар унда, модомики, осмонлару ер бор экан, абадийлар. Магар Аллоҳ хоҳласа, (Ўзи билур). Бу туганмас инъомдир'}, {'chapter': 11, 'verse': 109, 'text': 'فَلَا تَكُ فِي مِرۡيَةٖ مِّمَّا يَعۡبُدُ هَـٰٓؤُلَآءِۚ مَا يَعۡبُدُونَ إِلَّا كَمَا يَعۡبُدُ ءَابَآؤُهُم مِّن قَبۡلُۚ وَإِنَّا لَمُوَفُّوهُمۡ نَصِيبَهُمۡ غَيۡرَ مَنقُوصٖ\nСен анавилар ибодат қилаётган нарсада шак-шубҳада бўлма. Улар олдин оталари ибодат қилганга ўхшаш ибодат қиладилар, холос. Ва, албатта, Биз уларга насибаларини (ҳеч бир) камайтирмай тўлиқ қилиб берамиз'}, {'chapter': 11, 'verse': 110, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ\nБатаҳқиқ, Мусога китобни бердик. Бас, у ҳақида ихтилоф қилинди. Агар Роббингдан ўтган сўз бўлмаганида, ораларида ҳукм чиқарган бўлар эди. Ва, албатта, улар ундан шак-шубҳададирлар'}, {'chapter': 11, 'verse': 111, 'text': 'وَإِنَّ كُلّٗا لَّمَّا لَيُوَفِّيَنَّهُمۡ رَبُّكَ أَعۡمَٰلَهُمۡۚ إِنَّهُۥ بِمَا يَعۡمَلُونَ خَبِيرٞ\nАлбатта, Роббинг ҳаммага амалларига яраша тўлиқ вафо қилур. Чунки, У қилаётган нарсаларидан хабардордир'}, {'chapter': 11, 'verse': 112, 'text': 'فَٱسۡتَقِمۡ كَمَآ أُمِرۡتَ وَمَن تَابَ مَعَكَ وَلَا تَطۡغَوۡاْۚ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٞ\nБас, сен ва сен билан тавба қилганлар мустақийм туринглар ва туғёнга кетманглар. Чунки, У нима қилаётганингизни кўриб тургувчидир'}, {'chapter': 11, 'verse': 113, 'text': 'وَلَا تَرۡكَنُوٓاْ إِلَى ٱلَّذِينَ ظَلَمُواْ فَتَمَسَّكُمُ ٱلنَّارُ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِيَآءَ ثُمَّ لَا تُنصَرُونَ\nЗулм қилганларга мойил бўлманглар. Яна, сизни дўзах олови тутмасин. Сиз учун Аллоҳдан ўзга дўстлар йўқ. Сўнгра ёрдам берилмайсизлар'}, {'chapter': 11, 'verse': 114, 'text': 'وَأَقِمِ ٱلصَّلَوٰةَ طَرَفَيِ ٱلنَّهَارِ وَزُلَفٗا مِّنَ ٱلَّيۡلِۚ إِنَّ ٱلۡحَسَنَٰتِ يُذۡهِبۡنَ ٱلسَّيِّـَٔاتِۚ ذَٰلِكَ ذِكۡرَىٰ لِلذَّـٰكِرِينَ\nКундузнинг икки тарафида ва кечанинг бир бўлагида намозни тўкис адо қил! Албатта, яхшиликлар ёмонликларни кетказади. Бу эса, эсловчиларга эслатмадир'}, {'chapter': 11, 'verse': 115, 'text': 'وَٱصۡبِرۡ فَإِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nВа сабр қил. Бас, албатта, Аллоҳ яхшилик қилувчиларнинг ажрини зое қилмас'}, {'chapter': 11, 'verse': 116, 'text': 'فَلَوۡلَا كَانَ مِنَ ٱلۡقُرُونِ مِن قَبۡلِكُمۡ أُوْلُواْ بَقِيَّةٖ يَنۡهَوۡنَ عَنِ ٱلۡفَسَادِ فِي ٱلۡأَرۡضِ إِلَّا قَلِيلٗا مِّمَّنۡ أَنجَيۡنَا مِنۡهُمۡۗ وَٱتَّبَعَ ٱلَّذِينَ ظَلَمُواْ مَآ أُتۡرِفُواْ فِيهِ وَكَانُواْ مُجۡرِمِينَ\nСиздан олдинги асрларда ер юзида фасоддан қайтарадиган боқийлик эгалари бўлганида эди. Магар Биз нажот берганлар озгина кишилар бўлди, холос. Зулм қилганлар эса, маишат ортидан кетдилар ва жиноятчилардан бўлдилар'}, {'chapter': 11, 'verse': 117, 'text': 'وَمَا كَانَ رَبُّكَ لِيُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمٖ وَأَهۡلُهَا مُصۡلِحُونَ\nРоббинг аҳли ислоҳчи бўлган шаҳарларни зулм ила ҳалок қилмас эди'}, {'chapter': 11, 'verse': 118, 'text': 'وَلَوۡ شَآءَ رَبُّكَ لَجَعَلَ ٱلنَّاسَ أُمَّةٗ وَٰحِدَةٗۖ وَلَا يَزَالُونَ مُخۡتَلِفِينَ\nАгар Роббинг хоҳласа, одамларни бир уммат қиларди. Улар ихтилоф қилишда бардавом бўлурлар'}, {'chapter': 11, 'verse': 119, 'text': 'إِلَّا مَن رَّحِمَ رَبُّكَۚ وَلِذَٰلِكَ خَلَقَهُمۡۗ وَتَمَّتۡ كَلِمَةُ رَبِّكَ لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِينَ\nРоббинг раҳим қилганларидан бошқалари. Уларни шунинг учун яратгандир. Роббингнинг: «Жаҳаннамни, албатта, жинлар ва одамлар билан тамомийла тўлдирурман», деган сўзи батамом бўлди'}, {'chapter': 11, 'verse': 120, 'text': 'وَكُلّٗا نَّقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ ٱلرُّسُلِ مَا نُثَبِّتُ بِهِۦ فُؤَادَكَۚ وَجَآءَكَ فِي هَٰذِهِ ٱلۡحَقُّ وَمَوۡعِظَةٞ وَذِكۡرَىٰ لِلۡمُؤۡمِنِينَ\nБиз қалбингни саботли қилиш учун сенга Пайғамбарлар хабарининг ҳаммасини қисса қилиб айтмоқдамиз. Бу(сура)да сенга ҳақ ва мўминларга мавъиза ҳамда эслатма келди'}, {'chapter': 11, 'verse': 121, 'text': 'وَقُل لِّلَّذِينَ لَا يُؤۡمِنُونَ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنَّا عَٰمِلُونَ\nИймон келтирмайдиганларга: «Имконингиз бор амалларни қилаверинг, биз ҳам амал қилувчилармиз'}, {'chapter': 11, 'verse': 122, 'text': 'وَٱنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ\nВа интизор бўлинглар, биз ҳам мунтазирмиз», деб айт'}, {'chapter': 11, 'verse': 123, 'text': 'وَلِلَّهِ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَإِلَيۡهِ يُرۡجَعُ ٱلۡأَمۡرُ كُلُّهُۥ فَٱعۡبُدۡهُ وَتَوَكَّلۡ عَلَيۡهِۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nОсмонлару ернинг ғайби(ни билмоқ) Аллоҳга хосдир. Ишнинг барчаси Унга қайтарилур. Бас, Унга ибодат қил ва Унгагина таваккал қил. Роббинг қилаётганларингиздан ғофил эмасдир'}], '12': [{'chapter': 12, 'verse': 1, 'text': 'الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ\nАлиф. Лаам. Ро. Ушбулар очиқ-ойдин китобнинг оятларидир'}, {'chapter': 12, 'verse': 2, 'text': 'إِنَّآ أَنزَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ\nБиз уни арабий Қуръон этиб нозил қилдик, шоядки, ақл ишлатсангиз'}, {'chapter': 12, 'verse': 3, 'text': 'نَحۡنُ نَقُصُّ عَلَيۡكَ أَحۡسَنَ ٱلۡقَصَصِ بِمَآ أَوۡحَيۡنَآ إِلَيۡكَ هَٰذَا ٱلۡقُرۡءَانَ وَإِن كُنتَ مِن قَبۡلِهِۦ لَمِنَ ٱلۡغَٰفِلِينَ\nБиз сенга ушбу Қуръонда ваҳий қилган нарсамиз ила энг гўзал қиссани ҳикоя қилурмиз. Агарчи ундан олдин сен бундан бехабарлардан бўлсанг ҳам'}, {'chapter': 12, 'verse': 4, 'text': 'إِذۡ قَالَ يُوسُفُ لِأَبِيهِ يَـٰٓأَبَتِ إِنِّي رَأَيۡتُ أَحَدَ عَشَرَ كَوۡكَبٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ رَأَيۡتُهُمۡ لِي سَٰجِدِينَ\nЮсуф отасига: «Эй отажоним, мен ўн битта юлдузни ҳам қуёш ва ойни кўрдим, уларнинг менга сажда қилаётганларини кўрдим», деганини эсла'}, {'chapter': 12, 'verse': 5, 'text': 'قَالَ يَٰبُنَيَّ لَا تَقۡصُصۡ رُءۡيَاكَ عَلَىٰٓ إِخۡوَتِكَ فَيَكِيدُواْ لَكَ كَيۡدًاۖ إِنَّ ٱلشَّيۡطَٰنَ لِلۡإِنسَٰنِ عَدُوّٞ مُّبِينٞ\nУ: «Эй ўғилчам, тушингни акаларингга айтмагин. Яна, улар сенга бирор ҳийла қилмасинлар. Албатта, шайтон инсонга очиқ-ойдин душмандир'}, {'chapter': 12, 'verse': 6, 'text': 'وَكَذَٰلِكَ يَجۡتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِن تَأۡوِيلِ ٱلۡأَحَادِيثِ وَيُتِمُّ نِعۡمَتَهُۥ عَلَيۡكَ وَعَلَىٰٓ ءَالِ يَعۡقُوبَ كَمَآ أَتَمَّهَا عَلَىٰٓ أَبَوَيۡكَ مِن قَبۡلُ إِبۡرَٰهِيمَ وَإِسۡحَٰقَۚ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٞ\nШундай қилиб, Роббинг сени танлаб олади, сенга тушларнинг таъбирини ўргатади ва сенга ва Яъқуб аҳлига, худди оталаринг Иброҳим ва Исҳоққа батамом қилганидек, Ўз неъматини батамом қилиб беради. Албатта, Роббинг билгувчи ва ҳикматли Зотдир», – деди'}, {'chapter': 12, 'verse': 7, 'text': '۞لَّقَدۡ كَانَ فِي يُوسُفَ وَإِخۡوَتِهِۦٓ ءَايَٰتٞ لِّلسَّآئِلِينَ\nБатаҳқиқ, Юсуф ва унинг оға-иниларида сўровчилар учун оят-ибратлар бор эди'}, {'chapter': 12, 'verse': 8, 'text': 'إِذۡ قَالُواْ لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰٓ أَبِينَا مِنَّا وَنَحۡنُ عُصۡبَةٌ إِنَّ أَبَانَا لَفِي ضَلَٰلٖ مُّبِينٍ\nЎшанда улар: «Албатта, Юсуф ва унинг укаси отамизга биздан кўра маҳбуброқлар, ҳолбуки, биз кўпчиликмиз. Албатта, отамиз очиқ-ойдин адашувдадир'}, {'chapter': 12, 'verse': 9, 'text': 'ٱقۡتُلُواْ يُوسُفَ أَوِ ٱطۡرَحُوهُ أَرۡضٗا يَخۡلُ لَكُمۡ وَجۡهُ أَبِيكُمۡ وَتَكُونُواْ مِنۢ بَعۡدِهِۦ قَوۡمٗا صَٰلِحِينَ\nЮсуфни ўлдиринглар ёки узоқ ерга олиб бориб ташланглар, шунда отангизнинг юзи сизга холи қолади. Бундан кейин солиҳ қавмдан бўлиб оласизлар», – дедилар'}, {'chapter': 12, 'verse': 10, 'text': 'قَالَ قَآئِلٞ مِّنۡهُمۡ لَا تَقۡتُلُواْ يُوسُفَ وَأَلۡقُوهُ فِي غَيَٰبَتِ ٱلۡجُبِّ يَلۡتَقِطۡهُ بَعۡضُ ٱلسَّيَّارَةِ إِن كُنتُمۡ فَٰعِلِينَ\nУлардан бир сўзловчи: «Юсуфни ўлдирманглар. Агар бирон иш қилмоқчи бўлсангиз, уни қудуқнинг қаърига ташланглар, баъзи йўловчилар олиб кетади», деди'}, {'chapter': 12, 'verse': 11, 'text': 'قَالُواْ يَـٰٓأَبَانَا مَا لَكَ لَا تَأۡمَ۬نَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُۥ لَنَٰصِحُونَ\nУлар: «Эй отамиз, сен нега Юсуфни бизга ишонмайсан? Ҳолбуки, биз унга ихлос қилувчилармиз'}, {'chapter': 12, 'verse': 12, 'text': 'أَرۡسِلۡهُ مَعَنَا غَدٗا يَرۡتَعۡ وَيَلۡعَبۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ\nЭртага уни биз билан юбор, еб-ичиб, ўйнаб келади. Албатта, биз уни муҳофаза қилувчилармиз», – дедилар'}, {'chapter': 12, 'verse': 13, 'text': 'قَالَ إِنِّي لَيَحۡزُنُنِيٓ أَن تَذۡهَبُواْ بِهِۦ وَأَخَافُ أَن يَأۡكُلَهُ ٱلذِّئۡبُ وَأَنتُمۡ عَنۡهُ غَٰفِلُونَ\nУ: «Уни олиб кетишингиз мени хафа қилади. Сиз ундан ғофил қолсангиз, уни бўри еб кетишидан қўрқаман», – деди'}, {'chapter': 12, 'verse': 14, 'text': 'قَالُواْ لَئِنۡ أَكَلَهُ ٱلذِّئۡبُ وَنَحۡنُ عُصۡبَةٌ إِنَّآ إِذٗا لَّخَٰسِرُونَ\nУлар: «Агар биз кўпчилик бўлиб туриб, уни бўри еб кетса, унда биз зиёнкорлардан эканмиз-да?!» – дедилар'}, {'chapter': 12, 'verse': 15, 'text': 'فَلَمَّا ذَهَبُواْ بِهِۦ وَأَجۡمَعُوٓاْ أَن يَجۡعَلُوهُ فِي غَيَٰبَتِ ٱلۡجُبِّۚ وَأَوۡحَيۡنَآ إِلَيۡهِ لَتُنَبِّئَنَّهُم بِأَمۡرِهِمۡ هَٰذَا وَهُمۡ لَا يَشۡعُرُونَ\nУни олиб кетиб, қудуқ қаърига ташлашга қарор қилганларида, Биз унга: «Сен, албатта, уларга бу қилмишларининг хабарини берурсан, ўшанда улар сени сезмаслар ҳам», деб ваҳий қилдик'}, {'chapter': 12, 'verse': 16, 'text': 'وَجَآءُوٓ أَبَاهُمۡ عِشَآءٗ يَبۡكُونَ\nКечқурун йиғлаган ҳолларида оталарига келиб'}, {'chapter': 12, 'verse': 17, 'text': 'قَالُواْ يَـٰٓأَبَانَآ إِنَّا ذَهَبۡنَا نَسۡتَبِقُ وَتَرَكۡنَا يُوسُفَ عِندَ مَتَٰعِنَا فَأَكَلَهُ ٱلذِّئۡبُۖ وَمَآ أَنتَ بِمُؤۡمِنٖ لَّنَا وَلَوۡ كُنَّا صَٰدِقِينَ\n«Эй отамиз, биз Юсуфни нарсаларимиз олдига қолдириб, ўзимиз қувлашиб кетсак, уни бўри еб қўйибди. Агар ростгўй бўлсак ҳам, сен бизга ишонмассан», – дедилар'}, {'chapter': 12, 'verse': 18, 'text': 'وَجَآءُو عَلَىٰ قَمِيصِهِۦ بِدَمٖ كَذِبٖۚ قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٞۖ وَٱللَّهُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ\nВа унинг ёлғон қонга бўялган кўйлагини келтирдилар. У: «Йўқ! Сизга ҳавойи нафсингиз бирор ишни зийнатлаб кўрсатибди. Энди чиройли сабр(дан бошқа чорам йўқ). Сиз васф қилаётган нарсада ёрдам сўраладиган Зот ёлғиз Аллоҳнинг Ўзи», – деди'}, {'chapter': 12, 'verse': 19, 'text': 'وَجَآءَتۡ سَيَّارَةٞ فَأَرۡسَلُواْ وَارِدَهُمۡ فَأَدۡلَىٰ دَلۡوَهُۥۖ قَالَ يَٰبُشۡرَىٰ هَٰذَا غُلَٰمٞۚ وَأَسَرُّوهُ بِضَٰعَةٗۚ وَٱللَّهُ عَلِيمُۢ بِمَا يَعۡمَلُونَ\nВа йўловчилар келиб (қудуққа) сувчиларини юбордилар. Бас, у челагини ташлади ва: «Суюнчи беринглар! Бу бола-ку!» – деди. Уни тижорат моли сифатида яшириб қўйдилар. Зотан, Аллоҳ уларнинг нима қилаётганларини билгувчи Зотдир'}, {'chapter': 12, 'verse': 20, 'text': 'وَشَرَوۡهُ بِثَمَنِۭ بَخۡسٖ دَرَٰهِمَ مَعۡدُودَةٖ وَكَانُواْ فِيهِ مِنَ ٱلزَّـٰهِدِينَ\nВа уни арзон баҳога, саноқли дирҳамларга сотдилар. Улар унга қизиқмаган эдилар'}, {'chapter': 12, 'verse': 21, 'text': 'وَقَالَ ٱلَّذِي ٱشۡتَرَىٰهُ مِن مِّصۡرَ لِٱمۡرَأَتِهِۦٓ أَكۡرِمِي مَثۡوَىٰهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗاۚ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي ٱلۡأَرۡضِ وَلِنُعَلِّمَهُۥ مِن تَأۡوِيلِ ٱلۡأَحَادِيثِۚ وَٱللَّهُ غَالِبٌ عَلَىٰٓ أَمۡرِهِۦ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nВа уни Мисрда сотиб олган (одам) ўз хотинига: «Уни яхшилаб жойлаштир, шояд, бизга манфаат берса ёки бола қилиб олсак», – деди. Мана шундай қилиб, унга тушларнинг таъбирини ўргатишимиз учун Юсуфни жойлаштириб қўйдик. Аллоҳ Ўз ишида ғолибдир. Лекин одамларнинг кўпи билмаслар'}, {'chapter': 12, 'verse': 22, 'text': 'وَلَمَّا بَلَغَ أَشُدَّهُۥٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nВояга етган вақтида унга ҳукмни ва илмни бердик. Гўзал иш қилгувчиларни шундай мукофотлармиз'}, {'chapter': 12, 'verse': 23, 'text': 'وَرَٰوَدَتۡهُ ٱلَّتِي هُوَ فِي بَيۡتِهَا عَن نَّفۡسِهِۦ وَغَلَّقَتِ ٱلۡأَبۡوَٰبَ وَقَالَتۡ هَيۡتَ لَكَۚ قَالَ مَعَاذَ ٱللَّهِۖ إِنَّهُۥ رَبِّيٓ أَحۡسَنَ مَثۡوَايَۖ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nУ уйида бўлган аёл ундан нафсини хоҳлади. Эшикларни беркитиб: «Бу ёққа кел!» – деди. У: «Аллоҳ сақласин! Ахир, у жойимни яхшилаб берган хожам-ку! Албатта, золимлар нажот топмаслар», – деди'}, {'chapter': 12, 'verse': 24, 'text': 'وَلَقَدۡ هَمَّتۡ بِهِۦۖ وَهَمَّ بِهَا لَوۡلَآ أَن رَّءَا بُرۡهَٰنَ رَبِّهِۦۚ كَذَٰلِكَ لِنَصۡرِفَ عَنۡهُ ٱلسُّوٓءَ وَٱلۡفَحۡشَآءَۚ إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُخۡلَصِينَ\nБатаҳқиқ, хотин унга интилди. У ҳам, агар Роббининг бурҳон(ҳужжат)ини кўрмаганида, у(хотин)га интилар эди. Ундан ёмонлик ва фаҳш ишларни узоқлаштириш учун шундай қилдик. Албатта, у Бизнинг мухлис бандаларимиздандир'}, {'chapter': 12, 'verse': 25, 'text': 'وَٱسۡتَبَقَا ٱلۡبَابَ وَقَدَّتۡ قَمِيصَهُۥ مِن دُبُرٖ وَأَلۡفَيَا سَيِّدَهَا لَدَا ٱلۡبَابِۚ قَالَتۡ مَا جَزَآءُ مَنۡ أَرَادَ بِأَهۡلِكَ سُوٓءًا إِلَّآ أَن يُسۡجَنَ أَوۡ عَذَابٌ أَلِيمٞ\nЭшик томон чопишди. Хотин унинг кўйлагини орқасидан юлиб олди. Эшик олдида у(хотин)нинг хожасига дуч келишди. У(хотин): «Сенинг аҳлингга ёмонлик хоҳлаганнинг жазоси фақатгина қамалмоқ ёки аламли азобдир», – деди'}, {'chapter': 12, 'verse': 26, 'text': 'قَالَ هِيَ رَٰوَدَتۡنِي عَن نَّفۡسِيۚ وَشَهِدَ شَاهِدٞ مِّنۡ أَهۡلِهَآ إِن كَانَ قَمِيصُهُۥ قُدَّ مِن قُبُلٖ فَصَدَقَتۡ وَهُوَ مِنَ ٱلۡكَٰذِبِينَ\nУ: «Унинг ўзи менинг нафсимни хоҳлади», – деди. У(хотин)нинг аҳлидан бир гувоҳ гувоҳлик берди ва: «Агар унинг кўйлаги олд томондан йиртилган бўлса, бас, хотин рост айтибдир, у ёлғончилардандир'}, {'chapter': 12, 'verse': 27, 'text': 'وَإِن كَانَ قَمِيصُهُۥ قُدَّ مِن دُبُرٖ فَكَذَبَتۡ وَهُوَ مِنَ ٱلصَّـٰدِقِينَ\nАгар унинг кўйлаги орт томондан йиртилган бўлса, бас, хотин ёлғон гапирибдир, у ростгўйлардандир», – деди'}, {'chapter': 12, 'verse': 28, 'text': 'فَلَمَّا رَءَا قَمِيصَهُۥ قُدَّ مِن دُبُرٖ قَالَ إِنَّهُۥ مِن كَيۡدِكُنَّۖ إِنَّ كَيۡدَكُنَّ عَظِيمٞ\nЭр унинг кўйлагининг орт томонидан йиртилганини кўрган пайтда: «Албатта, бу (иш) сиз хотинларнинг макрингиздир. Албатта, сиз хотинларнинг макри жуда зўрдир», – деди'}, {'chapter': 12, 'verse': 29, 'text': 'يُوسُفُ أَعۡرِضۡ عَنۡ هَٰذَاۚ وَٱسۡتَغۡفِرِي لِذَنۢبِكِۖ إِنَّكِ كُنتِ مِنَ ٱلۡخَاطِـِٔينَ\n«Эй Юсуф, сен бу ишни унут. Сен, хотин, гуноҳингга истиғфор айт. Албатта, сен хато қилгувчилардан бўлдинг», – деди'}, {'chapter': 12, 'verse': 30, 'text': '۞وَقَالَ نِسۡوَةٞ فِي ٱلۡمَدِينَةِ ٱمۡرَأَتُ ٱلۡعَزِيزِ تُرَٰوِدُ فَتَىٰهَا عَن نَّفۡسِهِۦۖ قَدۡ شَغَفَهَا حُبًّاۖ إِنَّا لَنَرَىٰهَا فِي ضَلَٰلٖ مُّبِينٖ\nШаҳардаги аёллар: «Азизнинг хотини хизматкорининг нафсини хоҳлабди. Унга жуда ҳам муҳаббат қўйиб қолган экан-да! Биз у(аёл)ни, албатта, очиқ-ойдин адашувда деб биламиз», – дейишди'}, {'chapter': 12, 'verse': 31, 'text': 'فَلَمَّا سَمِعَتۡ بِمَكۡرِهِنَّ أَرۡسَلَتۡ إِلَيۡهِنَّ وَأَعۡتَدَتۡ لَهُنَّ مُتَّكَـٔٗا وَءَاتَتۡ كُلَّ وَٰحِدَةٖ مِّنۡهُنَّ سِكِّينٗا وَقَالَتِ ٱخۡرُجۡ عَلَيۡهِنَّۖ فَلَمَّا رَأَيۡنَهُۥٓ أَكۡبَرۡنَهُۥ وَقَطَّعۡنَ أَيۡدِيَهُنَّ وَقُلۡنَ حَٰشَ لِلَّهِ مَا هَٰذَا بَشَرًا إِنۡ هَٰذَآ إِلَّا مَلَكٞ كَرِيمٞ\nУ уларнинг макри ҳақида эшитган вақтда, уларга одам юбориб чақирди. Уларга суяниб ўтирадиган жой тайёрлади. Ва ҳар бирларига пичоқ берди. Сўнгра (Юсуфга): «Уларнинг олдига чиқ!» – деди. Уни кўрган чоғларида лол қолдилар, қўлларини кесдилар. Ва: «Аллоҳ сақласин!! Бу башар эмас! Бу карамли фариштанинг ўзи-ку!» – дейишди'}, {'chapter': 12, 'verse': 32, 'text': 'قَالَتۡ فَذَٰلِكُنَّ ٱلَّذِي لُمۡتُنَّنِي فِيهِۖ وَلَقَدۡ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ فَٱسۡتَعۡصَمَۖ وَلَئِن لَّمۡ يَفۡعَلۡ مَآ ءَامُرُهُۥ لَيُسۡجَنَنَّ وَلَيَكُونٗا مِّنَ ٱلصَّـٰغِرِينَ\nУ: «Сиз мени маломат қилган нарса — шу. Ҳа, мен ундан нафсини хоҳладим, бироқ у ўзини сақлади. Агар у менинг амримни бажармаса, албатта қамалур ва хор бўлгувчилардан бўлур», – деди'}, {'chapter': 12, 'verse': 33, 'text': 'قَالَ رَبِّ ٱلسِّجۡنُ أَحَبُّ إِلَيَّ مِمَّا يَدۡعُونَنِيٓ إِلَيۡهِۖ وَإِلَّا تَصۡرِفۡ عَنِّي كَيۡدَهُنَّ أَصۡبُ إِلَيۡهِنَّ وَأَكُن مِّنَ ٱلۡجَٰهِلِينَ\nУ: «Эй Роббим, улар мени чорлаётган нарсадан кўра мен учун қамоқ маҳбуброқдир, бу(аёл)ларнинг макрини мендан Ўзинг нари қилмасанг, уларга мойил бўлиб, жоҳиллардан бўлиб қоламан», – деди'}, {'chapter': 12, 'verse': 34, 'text': 'فَٱسۡتَجَابَ لَهُۥ رَبُّهُۥ فَصَرَفَ عَنۡهُ كَيۡدَهُنَّۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nБас, Робби унинг (дуосини) ижобат қилиб, уларнинг макрини ундан нари қилди. Албатта, Унинг Ўзи эшитгувчи ва билгувчи Зотдир'}, {'chapter': 12, 'verse': 35, 'text': 'ثُمَّ بَدَا لَهُم مِّنۢ بَعۡدِ مَا رَأَوُاْ ٱلۡأٓيَٰتِ لَيَسۡجُنُنَّهُۥ حَتَّىٰ حِينٖ\nСўнгра, оят-аломатларни кўрганларидан кейин, уларга уни бир муддат қамашлари аён бўлди'}, {'chapter': 12, 'verse': 36, 'text': 'وَدَخَلَ مَعَهُ ٱلسِّجۡنَ فَتَيَانِۖ قَالَ أَحَدُهُمَآ إِنِّيٓ أَرَىٰنِيٓ أَعۡصِرُ خَمۡرٗاۖ وَقَالَ ٱلۡأٓخَرُ إِنِّيٓ أَرَىٰنِيٓ أَحۡمِلُ فَوۡقَ رَأۡسِي خُبۡزٗا تَأۡكُلُ ٱلطَّيۡرُ مِنۡهُۖ نَبِّئۡنَا بِتَأۡوِيلِهِۦٓۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِينَ\nУ билан қамоққа иккита йигит ҳам кирди. Улардан бири: «Мен тушимда шароб тайёрлаб юрибман», – деди. Бошқаси эса: «Мен бошимда нон кўтариб юрибман, қуш ундан емоқда», – деди. «Бизга бунинг таъбирини хабар қил. Биз сени, шубҳасиз, яхши амал қилгувчилардан, деб билмоқдамиз»'}, {'chapter': 12, 'verse': 37, 'text': 'قَالَ لَا يَأۡتِيكُمَا طَعَامٞ تُرۡزَقَانِهِۦٓ إِلَّا نَبَّأۡتُكُمَا بِتَأۡوِيلِهِۦ قَبۡلَ أَن يَأۡتِيَكُمَاۚ ذَٰلِكُمَا مِمَّا عَلَّمَنِي رَبِّيٓۚ إِنِّي تَرَكۡتُ مِلَّةَ قَوۡمٖ لَّا يُؤۡمِنُونَ بِٱللَّهِ وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ\nУ: «Мен сизларга ризқ бўладиган таом келгунига қадар унинг қандай эканининг хабарини берурман. Бу иш менга Роббим таълим берган нарсалардандир. Чунки, мен Аллоҳга иймон келтирмайдиган ва охиратга кофир бўлган қавмнинг миллати(дини)ни тарк этган одамман'}, {'chapter': 12, 'verse': 38, 'text': 'وَٱتَّبَعۡتُ مِلَّةَ ءَابَآءِيٓ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَۚ مَا كَانَ لَنَآ أَن نُّشۡرِكَ بِٱللَّهِ مِن شَيۡءٖۚ ذَٰلِكَ مِن فَضۡلِ ٱللَّهِ عَلَيۡنَا وَعَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ\nВа оталарим Иброҳим, Исҳоқ ва Яъқубнинг миллати (дини)га эргашдим. Биз учун Аллоҳга бирор нарсани шерик қилиш мумкин эмас эди. Бу бизга ва одамларга Аллоҳ берган фазл эди. Лекин одамларнинг кўплари шукр қилмаслар'}, {'chapter': 12, 'verse': 39, 'text': 'يَٰصَٰحِبَيِ ٱلسِّجۡنِ ءَأَرۡبَابٞ مُّتَفَرِّقُونَ خَيۡرٌ أَمِ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ\nЭй, ҳамзиндон дўстларим, турли-туман Робблар яхшими ёки ёлғизу Қаҳҳор Аллоҳми'}, {'chapter': 12, 'verse': 40, 'text': 'مَا تَعۡبُدُونَ مِن دُونِهِۦٓ إِلَّآ أَسۡمَآءٗ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِ أَمَرَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nСизлар ундан ўзга, ўзингиз ва ота-боболарингиз номлаб олган исмларга, Аллоҳ уларга бирон ҳужжат нозил қилмаган нарсаларга ибодат қилмоқдасизлар. Ҳукм қилиш фақат Аллоҳнинг Ўзига хосдир, У фақат Ўзигагина ибодат қилишингизни амр этди. Ана ўша тўғри диндир. Лекин одамларнинг кўпи билмаслар'}, {'chapter': 12, 'verse': 41, 'text': 'يَٰصَٰحِبَيِ ٱلسِّجۡنِ أَمَّآ أَحَدُكُمَا فَيَسۡقِي رَبَّهُۥ خَمۡرٗاۖ وَأَمَّا ٱلۡأٓخَرُ فَيُصۡلَبُ فَتَأۡكُلُ ٱلطَّيۡرُ مِن رَّأۡسِهِۦۚ قُضِيَ ٱلۡأَمۡرُ ٱلَّذِي فِيهِ تَسۡتَفۡتِيَانِ\nЭй, ҳамзиндон дўстларим, сизлардан бирингиз хўжасига хамр соқийлиги қиладир. Бошқаси бўлса, осилади ва қушлар унинг бошидан ейдир. Сиз билмоқчи бўлган иш битди», – деди'}, {'chapter': 12, 'verse': 42, 'text': 'وَقَالَ لِلَّذِي ظَنَّ أَنَّهُۥ نَاجٖ مِّنۡهُمَا ٱذۡكُرۡنِي عِندَ رَبِّكَ فَأَنسَىٰهُ ٱلشَّيۡطَٰنُ ذِكۡرَ رَبِّهِۦ فَلَبِثَ فِي ٱلسِّجۡنِ بِضۡعَ سِنِينَ\nВа у ўзи икковларидан нажот топгувчи деб билган шахсга: «Хўжанг ҳузурида мени эсга ол», – деди. Шайтон унга хўжасига эслатишни унуттирди. Бас, у қамоқда бир неча йил қолди'}, {'chapter': 12, 'verse': 43, 'text': 'وَقَالَ ٱلۡمَلِكُ إِنِّيٓ أَرَىٰ سَبۡعَ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعَ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖۖ يَـٰٓأَيُّهَا ٱلۡمَلَأُ أَفۡتُونِي فِي رُءۡيَٰيَ إِن كُنتُمۡ لِلرُّءۡيَا تَعۡبُرُونَ\nПодшоҳ: «Мен тушимда еттита семиз сигирни еттита ориқ сигир еяётганини ва еттита яшил бошоқни ва шунча қуруғини кўрмоқдаман. Эй аъёнлар, агар туш таъбирини қиладиган бўлсангиз, менга тушимнинг фатвосини беринг», – деди'}, {'chapter': 12, 'verse': 44, 'text': 'قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمٖۖ وَمَا نَحۡنُ بِتَأۡوِيلِ ٱلۡأَحۡلَٰمِ بِعَٰلِمِينَ\nУлар: «Бу алғов-далғов тушдир. Биз бундай тушларнинг таъбирини билувчи эмасмиз», – дедилар'}, {'chapter': 12, 'verse': 45, 'text': 'وَقَالَ ٱلَّذِي نَجَا مِنۡهُمَا وَٱدَّكَرَ بَعۡدَ أُمَّةٍ أَنَا۠ أُنَبِّئُكُم بِتَأۡوِيلِهِۦ فَأَرۡسِلُونِ\nВа иккисидан нажот топгани анча муддат ўтгандан сўнг хотирлаб: «Бунинг таъбирининг хабарини сизга мен бераман. Мени юборинг», – деди'}, {'chapter': 12, 'verse': 46, 'text': 'يُوسُفُ أَيُّهَا ٱلصِّدِّيقُ أَفۡتِنَا فِي سَبۡعِ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعِ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖ لَّعَلِّيٓ أَرۡجِعُ إِلَى ٱلنَّاسِ لَعَلَّهُمۡ يَعۡلَمُونَ\nУ: «Юсуф, эй, ростгўй зот, бизга еттита семиз сигирни еттита ориқ сигир еяётганининг ва еттита яшил бошоқнинг ва шунча қуруғининг таъбирини айт. Шоядки, одамларга қайтиб борсам, улар ҳам билсалар», – деди'}, {'chapter': 12, 'verse': 47, 'text': 'قَالَ تَزۡرَعُونَ سَبۡعَ سِنِينَ دَأَبٗا فَمَا حَصَدتُّمۡ فَذَرُوهُ فِي سُنۢبُلِهِۦٓ إِلَّا قَلِيلٗا مِّمَّا تَأۡكُلُونَ\nУ: «Кетма-кет етти йил зироат қиласизлар. Йиғиштириб олган ҳосилингизни бошоғида қолдиринг. Магар озгина ейдиганингизни (қолдирмасангиз ҳам бўлур)'}, {'chapter': 12, 'verse': 48, 'text': 'ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ سَبۡعٞ شِدَادٞ يَأۡكُلۡنَ مَا قَدَّمۡتُمۡ لَهُنَّ إِلَّا قَلِيلٗا مِّمَّا تُحۡصِنُونَ\nСўнгра, ундан кейин еттита шиддатли (йил) келур, улар олдиндан тайёрлаб қўйганингизни ейдилар. Магар, озгина асраб қўйганингизгина қолур'}, {'chapter': 12, 'verse': 49, 'text': 'ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ عَامٞ فِيهِ يُغَاثُ ٱلنَّاسُ وَفِيهِ يَعۡصِرُونَ\nСўнгра, ундан кейин бир йил келадики, унда одамлар сероб қилинурлар ва унда (меваларни) сиқурлар», – деди'}, {'chapter': 12, 'verse': 50, 'text': 'وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِي بِهِۦۖ فَلَمَّا جَآءَهُ ٱلرَّسُولُ قَالَ ٱرۡجِعۡ إِلَىٰ رَبِّكَ فَسۡـَٔلۡهُ مَا بَالُ ٱلنِّسۡوَةِ ٱلَّـٰتِي قَطَّعۡنَ أَيۡدِيَهُنَّۚ إِنَّ رَبِّي بِكَيۡدِهِنَّ عَلِيمٞ\nПодшоҳ: «Уни менга келтиринглар», – деди. Унга элчи келган пайтда: «Хўжанг ҳузурига қайтиб бор ва ундан қўлларини кесган хотинларнинг ҳоли нима бўлганини сўра, албатта, Роббим уларнинг макрини билгувчидир», – деди'}, {'chapter': 12, 'verse': 51, 'text': 'قَالَ مَا خَطۡبُكُنَّ إِذۡ رَٰوَدتُّنَّ يُوسُفَ عَن نَّفۡسِهِۦۚ قُلۡنَ حَٰشَ لِلَّهِ مَا عَلِمۡنَا عَلَيۡهِ مِن سُوٓءٖۚ قَالَتِ ٱمۡرَأَتُ ٱلۡعَزِيزِ ٱلۡـَٰٔنَ حَصۡحَصَ ٱلۡحَقُّ أَنَا۠ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ وَإِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِينَ\nУ: «Юсуфнинг нафсини хоҳлаган вақтингиздаги ишингиз нима эди?» – деди. Улар: «Аллоҳ сақласин! Биз унда бирон ёмонлик сезмадик», – дедилар. Азизнинг хотини: «Энди ҳақиқат зоҳир бўлди. Мен унинг нафсини хоҳладим. Албатта, у содиқлардандир», – деди'}, {'chapter': 12, 'verse': 52, 'text': 'ذَٰلِكَ لِيَعۡلَمَ أَنِّي لَمۡ أَخُنۡهُ بِٱلۡغَيۡبِ وَأَنَّ ٱللَّهَ لَا يَهۡدِي كَيۡدَ ٱلۡخَآئِنِينَ\nБу, мен унга йўғида орқасидан хиёнат қилмаганимни, албатта, Аллоҳ хоинларнинг макрини тўғриликка йўлламаслигини билиши учундир'}, {'chapter': 12, 'verse': 53, 'text': '۞وَمَآ أُبَرِّئُ نَفۡسِيٓۚ إِنَّ ٱلنَّفۡسَ لَأَمَّارَةُۢ بِٱلسُّوٓءِ إِلَّا مَا رَحِمَ رَبِّيٓۚ إِنَّ رَبِّي غَفُورٞ رَّحِيمٞ\nВа ўз нафсимни оқламайман. Албатта, нафс, агар Роббим раҳим қилмаса, ёмонликка ундовчидир. Албатта, Роббим мағфиратли ва билгувчи Зотдир», – деди'}, {'chapter': 12, 'verse': 54, 'text': 'وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِي بِهِۦٓ أَسۡتَخۡلِصۡهُ لِنَفۡسِيۖ فَلَمَّا كَلَّمَهُۥ قَالَ إِنَّكَ ٱلۡيَوۡمَ لَدَيۡنَا مَكِينٌ أَمِينٞ\nПодшоҳ: «Уни менга келтиринглар, уни ўзимга хос кишилардан қилиб оламан», – деди. У билан гаплашганда эса: «Албатта, сен бугунги кунда ҳузуримизда маконатли ва ишончли кишилардансан», – деди'}, {'chapter': 12, 'verse': 55, 'text': 'قَالَ ٱجۡعَلۡنِي عَلَىٰ خَزَآئِنِ ٱلۡأَرۡضِۖ إِنِّي حَفِيظٌ عَلِيمٞ\nУ: «Мени ернинг хазиналари устига қўй. Албатта, мен муҳофаза қилувчи ва билувчиман», – деди'}, {'chapter': 12, 'verse': 56, 'text': 'وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي ٱلۡأَرۡضِ يَتَبَوَّأُ مِنۡهَا حَيۡثُ يَشَآءُۚ نُصِيبُ بِرَحۡمَتِنَا مَن نَّشَآءُۖ وَلَا نُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nШундай қилиб, Юсуфга ер юзида маконат бердик, уни ўзи хоҳлаган жойга ерлаша оладиган қилдик. Ўз раҳматимизни, кимни хоҳласак, ўшанга муяссар этурмиз ва гўзал амал қилувчиларнинг ажрини зое қилмасмиз'}, {'chapter': 12, 'verse': 57, 'text': 'وَلَأَجۡرُ ٱلۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nИймон келтирган ва тақво қилганлар учун, албатта, охиратдаги ажр яхшироқ бўлур'}, {'chapter': 12, 'verse': 58, 'text': 'وَجَآءَ إِخۡوَةُ يُوسُفَ فَدَخَلُواْ عَلَيۡهِ فَعَرَفَهُمۡ وَهُمۡ لَهُۥ مُنكِرُونَ\nЮсуфнинг оға-инилари келдилар ва унинг ҳузурига кирдилар. Бас, у уларни таниди. Улар эса, уни танимадилар'}, {'chapter': 12, 'verse': 59, 'text': 'وَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ قَالَ ٱئۡتُونِي بِأَخٖ لَّكُم مِّنۡ أَبِيكُمۡۚ أَلَا تَرَوۡنَ أَنِّيٓ أُوفِي ٱلۡكَيۡلَ وَأَنَا۠ خَيۡرُ ٱلۡمُنزِلِينَ\nУларнинг жиҳозларини юклаган вақтда: «Менинг ҳузуримга ота бир инингизни олиб келинг. Кўраяпсизларми? Мен ўлчовни тўла қилмоқдаман ва мен энг яхши мезбонман'}, {'chapter': 12, 'verse': 60, 'text': 'فَإِن لَّمۡ تَأۡتُونِي بِهِۦ فَلَا كَيۡلَ لَكُمۡ عِندِي وَلَا تَقۡرَبُونِ\nАгар уни ҳузуримга олиб келмасангиз, унда сизга менинг даргоҳимда ўлчаб бериш йўқ ва менга яқин ҳам келманглар», – деди'}, {'chapter': 12, 'verse': 61, 'text': 'قَالُواْ سَنُرَٰوِدُ عَنۡهُ أَبَاهُ وَإِنَّا لَفَٰعِلُونَ\nУлар: «Уни отасидан сўраб олиб келишга уринамиз. Биз буни, албатта, қилувчимиз», – дедилар'}, {'chapter': 12, 'verse': 62, 'text': 'وَقَالَ لِفِتۡيَٰنِهِ ٱجۡعَلُواْ بِضَٰعَتَهُمۡ فِي رِحَالِهِمۡ لَعَلَّهُمۡ يَعۡرِفُونَهَآ إِذَا ٱنقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمۡ لَعَلَّهُمۡ يَرۡجِعُونَ\nУ ўз йигитларига: «Уларнинг сармояларини юклари ичига солиб қўйинглар. Шояд, улар аҳлларига борганларида буларни таниб, қайтиб келсалар», – деди'}, {'chapter': 12, 'verse': 63, 'text': 'فَلَمَّا رَجَعُوٓاْ إِلَىٰٓ أَبِيهِمۡ قَالُواْ يَـٰٓأَبَانَا مُنِعَ مِنَّا ٱلۡكَيۡلُ فَأَرۡسِلۡ مَعَنَآ أَخَانَا نَكۡتَلۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ\nОталари ҳузурига қайтиб борганларида: «Эй отамиз, бизга ўлчаб бериш ман этилди. Биз билан инимизни юбор, ўлчаб оламиз ва, албатта, биз уни муҳофаза қилувчилармиз», – дедилар'}, {'chapter': 12, 'verse': 64, 'text': 'قَالَ هَلۡ ءَامَنُكُمۡ عَلَيۡهِ إِلَّا كَمَآ أَمِنتُكُمۡ عَلَىٰٓ أَخِيهِ مِن قَبۡلُ فَٱللَّهُ خَيۡرٌ حَٰفِظٗاۖ وَهُوَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\nУ: «Уни сизларга худди акасини ишонганимдек ишониб топширайми? Аллоҳнинг Ўзи энг яхши муҳофазачи ва Унинг Ўзи раҳимлиларнинг энг раҳимлигидир», – деди'}, {'chapter': 12, 'verse': 65, 'text': 'وَلَمَّا فَتَحُواْ مَتَٰعَهُمۡ وَجَدُواْ بِضَٰعَتَهُمۡ رُدَّتۡ إِلَيۡهِمۡۖ قَالُواْ يَـٰٓأَبَانَا مَا نَبۡغِيۖ هَٰذِهِۦ بِضَٰعَتُنَا رُدَّتۡ إِلَيۡنَاۖ وَنَمِيرُ أَهۡلَنَا وَنَحۡفَظُ أَخَانَا وَنَزۡدَادُ كَيۡلَ بَعِيرٖۖ ذَٰلِكَ كَيۡلٞ يَسِيرٞ\nЮкларини очган пайтда, сармояларининг ўзларига қайтарилганини билдилар. «Эй отамиз, яна нима истаймиз? Мана, сармояларимиз ўзимизга қайтарилибди. Яна аҳлимизга зод келтирурмиз, укамизни муҳофаза қилиб, бир туя зиёда (юк) ўлчови оламиз. Бу озгина ўлчовдир», – дедилар'}, {'chapter': 12, 'verse': 66, 'text': 'قَالَ لَنۡ أُرۡسِلَهُۥ مَعَكُمۡ حَتَّىٰ تُؤۡتُونِ مَوۡثِقٗا مِّنَ ٱللَّهِ لَتَأۡتُنَّنِي بِهِۦٓ إِلَّآ أَن يُحَاطَ بِكُمۡۖ فَلَمَّآ ءَاتَوۡهُ مَوۡثِقَهُمۡ قَالَ ٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ\nУ: «Токи менга, албатта ҳузуримга қайта келтиришингиз ҳақида Аллоҳдан васийқа бермагунингизча, уни зинҳор сиз ила юбормасман. Магар (ҳалокатга) ўралиб қолсангиз, бошқа гап», – деди. Унга васийқаларини берганларида, у: «Айтган гапларимизга Аллоҳ вакилдир», – деди'}, {'chapter': 12, 'verse': 67, 'text': 'وَقَالَ يَٰبَنِيَّ لَا تَدۡخُلُواْ مِنۢ بَابٖ وَٰحِدٖ وَٱدۡخُلُواْ مِنۡ أَبۡوَٰبٖ مُّتَفَرِّقَةٖۖ وَمَآ أُغۡنِي عَنكُم مِّنَ ٱللَّهِ مِن شَيۡءٍۖ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَعَلَيۡهِ فَلۡيَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ\nУ: «Эй ўғилларим, битта эшикдан кирманглар, турли эшиклардан киринглар. Мен сизлардан Аллоҳнинг ҳеч нарсасини қайтара олмайман. Ҳукм қилиш фақат Аллоҳнинг Ўзига хосдир. Унгагина таваккал қилдим. Таваккал қилгувчилар фақат Унгагина таваккал қилсинлар», – деди'}, {'chapter': 12, 'verse': 68, 'text': 'وَلَمَّا دَخَلُواْ مِنۡ حَيۡثُ أَمَرَهُمۡ أَبُوهُم مَّا كَانَ يُغۡنِي عَنۡهُم مِّنَ ٱللَّهِ مِن شَيۡءٍ إِلَّا حَاجَةٗ فِي نَفۡسِ يَعۡقُوبَ قَضَىٰهَاۚ وَإِنَّهُۥ لَذُو عِلۡمٖ لِّمَا عَلَّمۡنَٰهُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nОталари амр этган томондан кирдилар. Зотан, бу улардан Аллоҳнинг ҳеч бир нарсасини қайтара олмас эди. Магар, Яъқубнинг кўнглидаги эҳтиёж бўлиб, уни қондирди, холос. Албатта, у Биз таълим берганимиз туфайли билим соҳиби эди. Лекин кўпчилик одамлар билмаслар'}, {'chapter': 12, 'verse': 69, 'text': 'وَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَخَاهُۖ قَالَ إِنِّيٓ أَنَا۠ أَخُوكَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَعۡمَلُونَ\nЮсуфнинг олдига кирганларида, у инисини ўзига яқин жойлаштирди ва: «Мен акангман, улар қилган нарсалардан хафа бўлма», – деди'}, {'chapter': 12, 'verse': 70, 'text': 'فَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ جَعَلَ ٱلسِّقَايَةَ فِي رَحۡلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا ٱلۡعِيرُ إِنَّكُمۡ لَسَٰرِقُونَ\nУларнинг жиҳозларини ҳозирлаган пайтда, инисининг юкига бир сув идишни солиб қўйди. Сўнгра, жарчи: «Эй, карвондагилар, сизлар ўғридирсизлар», деб жар солди'}, {'chapter': 12, 'verse': 71, 'text': 'قَالُواْ وَأَقۡبَلُواْ عَلَيۡهِم مَّاذَا تَفۡقِدُونَ\nУлар (ортларидан келганларга) қараб: «Нима йўқотдингиз?» – дедилар'}, {'chapter': 12, 'verse': 72, 'text': 'قَالُواْ نَفۡقِدُ صُوَاعَ ٱلۡمَلِكِ وَلِمَن جَآءَ بِهِۦ حِمۡلُ بَعِيرٖ وَأَنَا۠ بِهِۦ زَعِيمٞ\nУлар: «Подшоҳнинг идишини йўқотдик. Уни келтирган кишига бир туя юк. Мен унга кафилман», – деди'}, {'chapter': 12, 'verse': 73, 'text': 'قَالُواْ تَٱللَّهِ لَقَدۡ عَلِمۡتُم مَّا جِئۡنَا لِنُفۡسِدَ فِي ٱلۡأَرۡضِ وَمَا كُنَّا سَٰرِقِينَ\nУлар: «Аллоҳга қасамки, ўзингиз биласиз, биз ер юзида фасод учун келган эмасмиз, биз ўғрилар бўлган эмасмиз», – дедилар'}, {'chapter': 12, 'verse': 74, 'text': 'قَالُواْ فَمَا جَزَـٰٓؤُهُۥٓ إِن كُنتُمۡ كَٰذِبِينَ\nУлар: «Агар ёлғончи бўлиб чиқсангиз, жазоси нима?» – дедилар'}, {'chapter': 12, 'verse': 75, 'text': 'قَالُواْ جَزَـٰٓؤُهُۥ مَن وُجِدَ فِي رَحۡلِهِۦ فَهُوَ جَزَـٰٓؤُهُۥۚ كَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ\nУлар: «Унинг жазоси, кимнинг юкида топилса, ўшадир. Бас, унинг жазоси ўшадир. Золимни шундай жазолаймиз», – дедилар'}, {'chapter': 12, 'verse': 76, 'text': 'فَبَدَأَ بِأَوۡعِيَتِهِمۡ قَبۡلَ وِعَآءِ أَخِيهِ ثُمَّ ٱسۡتَخۡرَجَهَا مِن وِعَآءِ أَخِيهِۚ كَذَٰلِكَ كِدۡنَا لِيُوسُفَۖ مَا كَانَ لِيَأۡخُذَ أَخَاهُ فِي دِينِ ٱلۡمَلِكِ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ نَرۡفَعُ دَرَجَٰتٖ مَّن نَّشَآءُۗ وَفَوۡقَ كُلِّ ذِي عِلۡمٍ عَلِيمٞ\nУ инисининг юкидан олдин уларнинг юкидан (тафтиш) бошлади. Сўнгра, у(сув идиш)ни унинг юкидан чиқариб олди. Шундай қилиб, Юсуф фойдасига ҳийла қилдик. У, подшоҳнинг дини бўйича инисини олиб қола олмас эди. Магар, Аллоҳ хоҳласагина, бўлиши мумкин эди. Кимни хоҳласак, ўшанинг даражасини кўтарамиз. Ҳар бир илм эгаси устидан билувчи бор'}, {'chapter': 12, 'verse': 77, 'text': '۞قَالُوٓاْ إِن يَسۡرِقۡ فَقَدۡ سَرَقَ أَخٞ لَّهُۥ مِن قَبۡلُۚ فَأَسَرَّهَا يُوسُفُ فِي نَفۡسِهِۦ وَلَمۡ يُبۡدِهَا لَهُمۡۚ قَالَ أَنتُمۡ شَرّٞ مَّكَانٗاۖ وَٱللَّهُ أَعۡلَمُ بِمَا تَصِفُونَ\nУлар: «Агар бу ўғирлаган бўлса, бундан олдин унинг акаси ҳам ўғирлаган эди», – дедилар. Юсуф буни кўнглига тугиб қўйди, уларга ошкор қилмади. У: «Ўзингиз ёмонроқ турумдасиз. Аллоҳ сиз васф қилаётган нарсани яхши билгувчи Зотдир», – деди'}, {'chapter': 12, 'verse': 78, 'text': 'قَالُواْ يَـٰٓأَيُّهَا ٱلۡعَزِيزُ إِنَّ لَهُۥٓ أَبٗا شَيۡخٗا كَبِيرٗا فَخُذۡ أَحَدَنَا مَكَانَهُۥٓۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِينَ\nУлар: «Эй Азиз, бунинг катта ёшли чол отаси бор. Бас, биримизни унинг ўрнига ол. Биз сенинг яхшилик қилувчилардан эканлигингни кўрмоқдамиз», – дедилар'}, {'chapter': 12, 'verse': 79, 'text': 'قَالَ مَعَاذَ ٱللَّهِ أَن نَّأۡخُذَ إِلَّا مَن وَجَدۡنَا مَتَٰعَنَا عِندَهُۥٓ إِنَّآ إِذٗا لَّظَٰلِمُونَ\nУ: «Нарсамизни ҳузуридан топган одамдан бошқани олиб қолишимиздан Аллоҳ сақласин. Акс ҳолда, албатта, золимлардан бўлиб қоламиз», – деди'}, {'chapter': 12, 'verse': 80, 'text': 'فَلَمَّا ٱسۡتَيۡـَٔسُواْ مِنۡهُ خَلَصُواْ نَجِيّٗاۖ قَالَ كَبِيرُهُمۡ أَلَمۡ تَعۡلَمُوٓاْ أَنَّ أَبَاكُمۡ قَدۡ أَخَذَ عَلَيۡكُم مَّوۡثِقٗا مِّنَ ٱللَّهِ وَمِن قَبۡلُ مَا فَرَّطتُمۡ فِي يُوسُفَۖ فَلَنۡ أَبۡرَحَ ٱلۡأَرۡضَ حَتَّىٰ يَأۡذَنَ لِيٓ أَبِيٓ أَوۡ يَحۡكُمَ ٱللَّهُ لِيۖ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ\nУндан умидни узган чоғларида, четга чиқиб, хуфёна маслаҳат қилдилар. Катталари: «Отангиз сиздан Аллоҳ номи билан васийқа олганини ва бундан олдин Юсуф ҳақида қилган айбингизни билмайсизларми?! Мен токи отам менга изн бермагунча ёки Аллоҳ ҳукм чиқармагунча бу ердан қимирламасман. У, ҳукм чиқарувчиларнинг энг яхшисидир'}, {'chapter': 12, 'verse': 81, 'text': 'ٱرۡجِعُوٓاْ إِلَىٰٓ أَبِيكُمۡ فَقُولُواْ يَـٰٓأَبَانَآ إِنَّ ٱبۡنَكَ سَرَقَ وَمَا شَهِدۡنَآ إِلَّا بِمَا عَلِمۡنَا وَمَا كُنَّا لِلۡغَيۡبِ حَٰفِظِينَ\nОтангиз олдига қайтинглар, бас, унга:«Эй отамиз, ҳақиқатда ўғлинг ўғрилик қилди. Биз фақат ўзимиз билган нарсага гувоҳлик бердик, холос. Биз ғайбни билувчи эмас эдик'}, {'chapter': 12, 'verse': 82, 'text': 'وَسۡـَٔلِ ٱلۡقَرۡيَةَ ٱلَّتِي كُنَّا فِيهَا وَٱلۡعِيرَ ٱلَّتِيٓ أَقۡبَلۡنَا فِيهَاۖ وَإِنَّا لَصَٰدِقُونَ\nБиз бўлган шаҳардан сўра ва у ерда биз учраган карвондан сўра, биз, албатта, ростгўйларданмиз, денглар», деди'}, {'chapter': 12, 'verse': 83, 'text': 'قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٌۖ عَسَى ٱللَّهُ أَن يَأۡتِيَنِي بِهِمۡ جَمِيعًاۚ إِنَّهُۥ هُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\nУ: «Йўқ, сизга ҳавойи нафсингиз бир ишни яхши кўрсатмиш. Бас, чиройли сабр (қиламан). Шоядки, Аллоҳ уларнинг ҳаммасини ҳузуримга жамлаб келтирса. Албатта, Унинг Ўзи билгувчи ва ҳикматли Зотдир», деди'}, {'chapter': 12, 'verse': 84, 'text': 'وَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَـٰٓأَسَفَىٰ عَلَىٰ يُوسُفَ وَٱبۡيَضَّتۡ عَيۡنَاهُ مِنَ ٱلۡحُزۡنِ فَهُوَ كَظِيمٞ\nВа улардан юз ўгирди ҳамда: «Оҳ, Юсуф!» деб кўзларига оқ тушди. Бас, у дардини ютувчидир'}, {'chapter': 12, 'verse': 85, 'text': 'قَالُواْ تَٱللَّهِ تَفۡتَؤُاْ تَذۡكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوۡ تَكُونَ مِنَ ٱلۡهَٰلِكِينَ\nУлар: «Аллоҳга қасамки, токи мадордан кетгунингча ёки ҳалок бўлувчилардан бўлгунингча, Юсуфни зикр қилаверасан», дедилар'}, {'chapter': 12, 'verse': 86, 'text': 'قَالَ إِنَّمَآ أَشۡكُواْ بَثِّي وَحُزۡنِيٓ إِلَى ٱللَّهِ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\nУ: «Албатта, мен дарду ҳасратимдан фақат Аллоҳнинг Ўзига шикоят қилмоқдаман ва Аллоҳдан сиз билмаган нарсани билурман'}, {'chapter': 12, 'verse': 87, 'text': 'يَٰبَنِيَّ ٱذۡهَبُواْ فَتَحَسَّسُواْ مِن يُوسُفَ وَأَخِيهِ وَلَا تَاْيۡـَٔسُواْ مِن رَّوۡحِ ٱللَّهِۖ إِنَّهُۥ لَا يَاْيۡـَٔسُ مِن رَّوۡحِ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡكَٰفِرُونَ\nЭй ўғилларим, боринглар, Юсуфни ва унинг укасини яхшилаб изланглар, Аллоҳнинг раҳматидан ноумид бўлманглар. Ҳақиқатан, Аллоҳнинг раҳматидан фақат кофир қавмларгина ноумид бўлурлар», деди'}, {'chapter': 12, 'verse': 88, 'text': 'فَلَمَّا دَخَلُواْ عَلَيۡهِ قَالُواْ يَـٰٓأَيُّهَا ٱلۡعَزِيزُ مَسَّنَا وَأَهۡلَنَا ٱلضُّرُّ وَجِئۡنَا بِبِضَٰعَةٖ مُّزۡجَىٰةٖ فَأَوۡفِ لَنَا ٱلۡكَيۡلَ وَتَصَدَّقۡ عَلَيۡنَآۖ إِنَّ ٱللَّهَ يَجۡزِي ٱلۡمُتَصَدِّقِينَ\nУнинг ҳузурига кирганларида: «Эй Азиз, бизни ва аҳлимизни зарар тутди ва арзимаган сармоя келтирдик. Бас, бизга ўлчовни тўлиқ қилиб бер ва садақа ҳам бер. Аллоҳ, албатта, садақа қилувчиларни мукофотлайдир», дедилар'}, {'chapter': 12, 'verse': 89, 'text': 'قَالَ هَلۡ عَلِمۡتُم مَّا فَعَلۡتُم بِيُوسُفَ وَأَخِيهِ إِذۡ أَنتُمۡ جَٰهِلُونَ\nУ: «Жоҳиллик пайтингизда Юсуфга ва унинг инисига нима қилганларингизни биласизларми?» – деди'}, {'chapter': 12, 'verse': 90, 'text': 'قَالُوٓاْ أَءِنَّكَ لَأَنتَ يُوسُفُۖ قَالَ أَنَا۠ يُوسُفُ وَهَٰذَآ أَخِيۖ قَدۡ مَنَّ ٱللَّهُ عَلَيۡنَآۖ إِنَّهُۥ مَن يَتَّقِ وَيَصۡبِرۡ فَإِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nУлар: «Э-э, ҳали сен Юсуфмисан?!» – дедилар. У: «Мен Юсуфман, мана бу иним. Аллоҳ бизга марҳамат кўрсатди. Ҳақиқатан, ким тақво ва сабр қилса, Аллоҳ, албатта, гўзал иш қилувчиларнинг ажрини зое қилмагай», деди'}, {'chapter': 12, 'verse': 91, 'text': 'قَالُواْ تَٱللَّهِ لَقَدۡ ءَاثَرَكَ ٱللَّهُ عَلَيۡنَا وَإِن كُنَّا لَخَٰطِـِٔينَ\nУлар: «Аллоҳга қасамки, ҳақиқатан, Аллоҳ сени биздан устун қилди. Биз эса, хатокорлардан бўлдик», дедилар'}, {'chapter': 12, 'verse': 92, 'text': 'قَالَ لَا تَثۡرِيبَ عَلَيۡكُمُ ٱلۡيَوۡمَۖ يَغۡفِرُ ٱللَّهُ لَكُمۡۖ وَهُوَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\nУ: «Бугунги кунда сизларни айблаш йўқ. Сизларни Аллоҳ мағфират қилгай. У раҳим қилгувчиларнинг раҳимлироғидир», деди'}, {'chapter': 12, 'verse': 93, 'text': 'ٱذۡهَبُواْ بِقَمِيصِي هَٰذَا فَأَلۡقُوهُ عَلَىٰ وَجۡهِ أَبِي يَأۡتِ بَصِيرٗا وَأۡتُونِي بِأَهۡلِكُمۡ أَجۡمَعِينَ\n«Менинг мана бу кўйлагимни олиб бориб, отамнинг юзига ташлангиз, кўзи очилур. Сўнгра, аҳлингиз ила жам бўлиб ҳузуримга келинглар»'}, {'chapter': 12, 'verse': 94, 'text': 'وَلَمَّا فَصَلَتِ ٱلۡعِيرُ قَالَ أَبُوهُمۡ إِنِّي لَأَجِدُ رِيحَ يُوسُفَۖ لَوۡلَآ أَن تُفَنِّدُونِ\nКарвон йўлга чиққан вақтда оталари: «Мени ақлдан озган деманглар-у, албатта, мен Юсуфнинг ҳидини сезмоқдаман», деди'}, {'chapter': 12, 'verse': 95, 'text': 'قَالُواْ تَٱللَّهِ إِنَّكَ لَفِي ضَلَٰلِكَ ٱلۡقَدِيمِ\nУлар: «Аллоҳга қасамки, сен эски адашувингдасан», дедилар'}, {'chapter': 12, 'verse': 96, 'text': 'فَلَمَّآ أَن جَآءَ ٱلۡبَشِيرُ أَلۡقَىٰهُ عَلَىٰ وَجۡهِهِۦ فَٱرۡتَدَّ بَصِيرٗاۖ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيٓ أَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\nХушхабарчи келган чоғида, у(кўйлак)ни унинг юзига ташлаганида, яна кўрадиган бўлди. У: «Сизларга мен Аллоҳдан сизлар билмайдиган нарсани биламан, демаганмидим?!» – деди'}, {'chapter': 12, 'verse': 97, 'text': 'قَالُواْ يَـٰٓأَبَانَا ٱسۡتَغۡفِرۡ لَنَا ذُنُوبَنَآ إِنَّا كُنَّا خَٰطِـِٔينَ\nУлар: «Эй отамиз, гуноҳларимиз учун истиғфор айт. Албатта, биз хатокорлардан бўлган эканмиз», дедилар'}, {'chapter': 12, 'verse': 98, 'text': 'قَالَ سَوۡفَ أَسۡتَغۡفِرُ لَكُمۡ رَبِّيٓۖ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nУ: «Сизлар учун Роббимга ҳали истиғфор айтурман. Албатта, Унинг Ўзи мағфират қилгувчи ва раҳимли Зотдир», деди'}, {'chapter': 12, 'verse': 99, 'text': 'فَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَبَوَيۡهِ وَقَالَ ٱدۡخُلُواْ مِصۡرَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ\nЮсуф ҳузурига кирган чоғларида у отаси ва онасини ўзига яқинлаштирди ва: «Аллоҳнинг хоҳиши ила Мисрга омонликда киринглар!» – деди'}, {'chapter': 12, 'verse': 100, 'text': 'وَرَفَعَ أَبَوَيۡهِ عَلَى ٱلۡعَرۡشِ وَخَرُّواْ لَهُۥ سُجَّدٗاۖ وَقَالَ يَـٰٓأَبَتِ هَٰذَا تَأۡوِيلُ رُءۡيَٰيَ مِن قَبۡلُ قَدۡ جَعَلَهَا رَبِّي حَقّٗاۖ وَقَدۡ أَحۡسَنَ بِيٓ إِذۡ أَخۡرَجَنِي مِنَ ٱلسِّجۡنِ وَجَآءَ بِكُم مِّنَ ٱلۡبَدۡوِ مِنۢ بَعۡدِ أَن نَّزَغَ ٱلشَّيۡطَٰنُ بَيۡنِي وَبَيۡنَ إِخۡوَتِيٓۚ إِنَّ رَبِّي لَطِيفٞ لِّمَا يَشَآءُۚ إِنَّهُۥ هُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\nОта-онасини тахтга кўтарди. Улар унга сажда қилиб йиқилдилар. У: «Эй отажон, бу аввал кўрган тушимнинг таъбиридир, Роббим уни ҳаққа айлантирди. Дарҳақиқат, У менга яхшилик қилди. Мени қамоқдан чиқарди. Шайтон мен билан акаларимнинг орасини бузгандан сўнг, сизларни биёбондан олиб келди. Албатта, Роббим нимани хоҳласа, ўшанга латийфдир. Албатта, Унинг Ўзи билгувчи ва ҳикматли Зотдир», деди'}, {'chapter': 12, 'verse': 101, 'text': '۞رَبِّ قَدۡ ءَاتَيۡتَنِي مِنَ ٱلۡمُلۡكِ وَعَلَّمۡتَنِي مِن تَأۡوِيلِ ٱلۡأَحَادِيثِۚ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ أَنتَ وَلِيِّۦ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ تَوَفَّنِي مُسۡلِمٗا وَأَلۡحِقۡنِي بِٱلصَّـٰلِحِينَ\n«Эй Роббим, ҳақиқатан, менга мулк бердинг ва менга тушларнинг таъбирини ўргатдинг. Эй осмонлару ерни яратган Зот! Сен дунёю охиратда менинг валийимсан. Мени мусулмон ҳолимда вафот эттиргин ва солиҳларга қўшгин.»'}, {'chapter': 12, 'verse': 102, 'text': 'ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهِ إِلَيۡكَۖ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ أَجۡمَعُوٓاْ أَمۡرَهُمۡ وَهُمۡ يَمۡكُرُونَ\nБу ғайб хабарлардан бўлиб, уни сенга ваҳий қилмоқдамиз. Улар макр қилиб, ишлари ҳақида гап бириктирганларида олдиларида бўлмаган эдинг'}, {'chapter': 12, 'verse': 103, 'text': 'وَمَآ أَكۡثَرُ ٱلنَّاسِ وَلَوۡ حَرَصۡتَ بِمُؤۡمِنِينَ\nОдамларнинг кўплари, гарчи сен жуда қизиқсанг ҳам, мўмин эмаслар'}, {'chapter': 12, 'verse': 104, 'text': 'وَمَا تَسۡـَٔلُهُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nСен улардан бунинг учун ажр ҳам сўрамайсан. У фақат оламлар учун эслатма, холос'}, {'chapter': 12, 'verse': 105, 'text': 'وَكَأَيِّن مِّنۡ ءَايَةٖ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ يَمُرُّونَ عَلَيۡهَا وَهُمۡ عَنۡهَا مُعۡرِضُونَ\nУлар осмонлару ердаги қанчадан-қанча оят-белгилар қаршисидан юз ўгирган ҳолларида ўтадилар'}, {'chapter': 12, 'verse': 106, 'text': 'وَمَا يُؤۡمِنُ أَكۡثَرُهُم بِٱللَّهِ إِلَّا وَهُم مُّشۡرِكُونَ\nКўплари эса, Аллоҳга мушрик бўлган ҳолларидагина иймон келтирадилар, холос'}, {'chapter': 12, 'verse': 107, 'text': 'أَفَأَمِنُوٓاْ أَن تَأۡتِيَهُمۡ غَٰشِيَةٞ مِّنۡ عَذَابِ ٱللَّهِ أَوۡ تَأۡتِيَهُمُ ٱلسَّاعَةُ بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nУлар ўзларига Аллоҳдан қамраб олувчи азоб келишидан ёки сезмай турганларида қиёмат ногаҳон келиб қолишидан омондамилар'}, {'chapter': 12, 'verse': 108, 'text': 'قُلۡ هَٰذِهِۦ سَبِيلِيٓ أَدۡعُوٓاْ إِلَى ٱللَّهِۚ عَلَىٰ بَصِيرَةٍ أَنَا۠ وَمَنِ ٱتَّبَعَنِيۖ وَسُبۡحَٰنَ ٱللَّهِ وَمَآ أَنَا۠ مِنَ ٱلۡمُشۡرِكِينَ\nСен: «Бу — менинг йўлимдир. Мен Аллоҳга билиб — ишонч ила чақираман ва менга эргашганлар ҳам. Аллоҳ покдир. Ва мен мушриклардан эмасман», деб айт'}, {'chapter': 12, 'verse': 109, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِم مِّنۡ أَهۡلِ ٱلۡقُرَىٰٓۗ أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۗ وَلَدَارُ ٱلۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ٱتَّقَوۡاْۚ أَفَلَا تَعۡقِلُونَ\nБиз сендан илгари ҳам фақат шаҳар аҳлидан эр кишиларга ваҳий юборганмиз. Ер юзида юриб, ўзларидан олдингиларнинг оқибати нима бўлганига назар солмайдиларми? Албатта, охират диёри тақво қиладиганлар учун яхшироқдир. Ақл юритмайдиларми'}, {'chapter': 12, 'verse': 110, 'text': 'حَتَّىٰٓ إِذَا ٱسۡتَيۡـَٔسَ ٱلرُّسُلُ وَظَنُّوٓاْ أَنَّهُمۡ قَدۡ كُذِبُواْ جَآءَهُمۡ نَصۡرُنَا فَنُجِّيَ مَن نَّشَآءُۖ وَلَا يُرَدُّ بَأۡسُنَا عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِينَ\nҲаттоки Пайғамбарлар ноумид бўлиб, ўзларини ёлғончига чиқарилганлар, деб гумон қилиб қолганларида, уларга Бизнинг нусратимиз келади. Бас, Биз хоҳлаганларга нажот берилур. Жиноятчи қавмлардан Бизнинг азобимиз қайтарилмас'}, {'chapter': 12, 'verse': 111, 'text': 'لَقَدۡ كَانَ فِي قَصَصِهِمۡ عِبۡرَةٞ لِّأُوْلِي ٱلۡأَلۡبَٰبِۗ مَا كَانَ حَدِيثٗا يُفۡتَرَىٰ وَلَٰكِن تَصۡدِيقَ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ كُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ\nБатаҳқиқ, уларнинг қиссаларида ақл эгалари учун ибрат бор эди. Бу (Қуръон) тўқима гап эмас, лекин ўзидан олдин келганнинг тасдиғи ва барча нарсанинг тафсилоти, ҳидоят ҳамда иймон келтирганлар учун раҳматдир'}], '13': [{'chapter': 13, 'verse': 1, 'text': 'الٓمٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِۗ وَٱلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ ٱلۡحَقُّ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ\nАлиф. Лаам. Мийм. Ро. Ушбулар китобнинг оятларидир. Ва сенга Роббингдан нозил қилинган нарса ҳақдир. Лекин одамларнинг кўплари иймон келтирмаслар'}, {'chapter': 13, 'verse': 2, 'text': 'ٱللَّهُ ٱلَّذِي رَفَعَ ٱلسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ يُدَبِّرُ ٱلۡأَمۡرَ يُفَصِّلُ ٱلۡأٓيَٰتِ لَعَلَّكُم بِلِقَآءِ رَبِّكُمۡ تُوقِنُونَ\nАллоҳ сиз кўриб турган осмонни устунсиз кўтарган, сўнгра (Зоти ва улуғлигига муносиб суратда) аршга юксалган, қуёш ва ойни Ўзига бўйсундирган Зотдир. Ҳар бири номланган ажалгача юрадир. У ишнинг тадбирини қиладир, оятларни муфассал этадир. Шоядки, Роббингизга мулоқот бўлишга ишонч ҳосил қилсангиз'}, {'chapter': 13, 'verse': 3, 'text': 'وَهُوَ ٱلَّذِي مَدَّ ٱلۡأَرۡضَ وَجَعَلَ فِيهَا رَوَٰسِيَ وَأَنۡهَٰرٗاۖ وَمِن كُلِّ ٱلثَّمَرَٰتِ جَعَلَ فِيهَا زَوۡجَيۡنِ ٱثۡنَيۡنِۖ يُغۡشِي ٱلَّيۡلَ ٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nУ ерни чўзиб, унда баланд тоғлар ва анҳорлар қилган, ҳар хил мевалардан жуфт-жуфт қилган Зотдир. У кечани кундузга ўратадир. Бунда, албатта, тафаккур қилувчи қавмлар учун оят-белгилар бордир'}, {'chapter': 13, 'verse': 4, 'text': 'وَفِي ٱلۡأَرۡضِ قِطَعٞ مُّتَجَٰوِرَٰتٞ وَجَنَّـٰتٞ مِّنۡ أَعۡنَٰبٖ وَزَرۡعٞ وَنَخِيلٞ صِنۡوَانٞ وَغَيۡرُ صِنۡوَانٖ يُسۡقَىٰ بِمَآءٖ وَٰحِدٖ وَنُفَضِّلُ بَعۡضَهَا عَلَىٰ بَعۡضٖ فِي ٱلۡأُكُلِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nВа ер юзида қўшни бўлаклар бор. Шунингдек, узумлар, боғлар, экинзорлар, шохлаган ва шохламаган хурмолар бўлиб, бир хил сув ила суғориладилар ва уларнинг баъзиларининг мевасини баъзилариникидан афзал қилурмиз. Бунда, албатта, ақл ишлатувчи қавмлар учун оят-белгилар бордир'}, {'chapter': 13, 'verse': 5, 'text': '۞وَإِن تَعۡجَبۡ فَعَجَبٞ قَوۡلُهُمۡ أَءِذَا كُنَّا تُرَٰبًا أَءِنَّا لَفِي خَلۡقٖ جَدِيدٍۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ وَأُوْلَـٰٓئِكَ ٱلۡأَغۡلَٰلُ فِيٓ أَعۡنَاقِهِمۡۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nАгар ажабланадиган бўлсанг, уларнинг: «Агар биз тупроқ бўлиб кетсак ҳам, янгидан яратиламизми?!» дейишлари яна ҳам ажабланарлидир. Ана ўшалар Роббиларига куфр келтирганлардир. Ана ўшалар бўйинлари кишанларда бўладиганлардир. Ана ўшалар дўзах эгаларидир. Улар унда абадий қолурлар'}, {'chapter': 13, 'verse': 6, 'text': 'وَيَسۡتَعۡجِلُونَكَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِ وَقَدۡ خَلَتۡ مِن قَبۡلِهِمُ ٱلۡمَثُلَٰتُۗ وَإِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ لِّلنَّاسِ عَلَىٰ ظُلۡمِهِمۡۖ وَإِنَّ رَبَّكَ لَشَدِيدُ ٱلۡعِقَابِ\nУлар сендан яхшиликдан аввал ёмонлик келтиришни талаб қилурлар. Ҳолбуки, улардан аввал мисоллар ўтмишдир. Ва, албатта, Роббинг одамларнинг зулмига қарамай, улар учун мағфиратлидир. Ва, албатта, Роббинг иқоби шиддатлидир'}, {'chapter': 13, 'verse': 7, 'text': 'وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦٓۗ إِنَّمَآ أَنتَ مُنذِرٞۖ وَلِكُلِّ قَوۡمٍ هَادٍ\nКуфр келтирганлар: «Унга Роббидан бир мўъжиза нозил қилинса эди», дерлар. Сен фақат огоҳлантирувчисан, холос. Ва ҳар қавмнинг ҳидоятчиси бордир'}, {'chapter': 13, 'verse': 8, 'text': 'ٱللَّهُ يَعۡلَمُ مَا تَحۡمِلُ كُلُّ أُنثَىٰ وَمَا تَغِيضُ ٱلۡأَرۡحَامُ وَمَا تَزۡدَادُۚ وَكُلُّ شَيۡءٍ عِندَهُۥ بِمِقۡدَارٍ\nАллоҳ ҳар бир урғочининг ҳомиласини ҳамда бачадонлар нуқсон ёки зиёда қиладиган нарсаларни биладир. Ҳар бир нарса Унинг ҳузурида ўлчовлидир'}, {'chapter': 13, 'verse': 9, 'text': 'عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡكَبِيرُ ٱلۡمُتَعَالِ\nУ зот ғойибни ҳам, шоҳидни ҳам билгувчидир. У буюкдир, юксакдир'}, {'chapter': 13, 'verse': 10, 'text': 'سَوَآءٞ مِّنكُم مَّنۡ أَسَرَّ ٱلۡقَوۡلَ وَمَن جَهَرَ بِهِۦ وَمَنۡ هُوَ مُسۡتَخۡفِۭ بِٱلَّيۡلِ وَسَارِبُۢ بِٱلنَّهَارِ\nСиздан ким гапни сир тутса, ким уни ошкор қилса ёки ким кечаси беркинувчи бўлсаю ким кундузи ошкор юрувчи бўлса — бари бир'}, {'chapter': 13, 'verse': 11, 'text': 'لَهُۥ مُعَقِّبَٰتٞ مِّنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ يَحۡفَظُونَهُۥ مِنۡ أَمۡرِ ٱللَّهِۗ إِنَّ ٱللَّهَ لَا يُغَيِّرُ مَا بِقَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡۗ وَإِذَآ أَرَادَ ٱللَّهُ بِقَوۡمٖ سُوٓءٗا فَلَا مَرَدَّ لَهُۥۚ وَمَا لَهُم مِّن دُونِهِۦ مِن وَالٍ\nУнинг олдида ҳам, ортида ҳам таъқиб қилгувчилар бор. Улар уни Аллоҳнинг амри ила ҳифз қилиб турурлар. Албатта, то бир қавм ўзларини ўзгартирмагунларича, Аллоҳ уларнинг ҳолини ўзгартирмас. Агар Аллоҳ бирор қавмга ёмонликни ирода қилса, бас, уни қайтариб бўлмайди ва улар учун ундан ўзга валий ҳам йўқ'}, {'chapter': 13, 'verse': 12, 'text': 'هُوَ ٱلَّذِي يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنشِئُ ٱلسَّحَابَ ٱلثِّقَالَ\nУ сизларга чақмоқни хавф ва тамаъ қилиб кўрсатур ва оғир булутларни пайдо қилур'}, {'chapter': 13, 'verse': 13, 'text': 'وَيُسَبِّحُ ٱلرَّعۡدُ بِحَمۡدِهِۦ وَٱلۡمَلَـٰٓئِكَةُ مِنۡ خِيفَتِهِۦ وَيُرۡسِلُ ٱلصَّوَٰعِقَ فَيُصِيبُ بِهَا مَن يَشَآءُ وَهُمۡ يُجَٰدِلُونَ فِي ٱللَّهِ وَهُوَ شَدِيدُ ٱلۡمِحَالِ\nВа момақалдироқ Унинг ҳамди ила тасбиҳ айтадир ва фаришталар ҳам Ундан қўрққанларидан (ҳамду тасбиҳ айтурлар). У чақмоқларни юборадир ва улар ила хоҳлаган одамларини, улар Аллоҳ ҳақида талашиб турганларида, урадир. У қаттиқ ушлайдиган Зотдир'}, {'chapter': 13, 'verse': 14, 'text': 'لَهُۥ دَعۡوَةُ ٱلۡحَقِّۚ وَٱلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَجِيبُونَ لَهُم بِشَيۡءٍ إِلَّا كَبَٰسِطِ كَفَّيۡهِ إِلَى ٱلۡمَآءِ لِيَبۡلُغَ فَاهُ وَمَا هُوَ بِبَٰلِغِهِۦۚ وَمَا دُعَآءُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ\nҲақ даъвати Уникидир. Ундан ўзгага дуо қилаётганларга эса, ҳеч бир нарсада ижобат этмаслар. Магар, икки кафтини сувга узатиб, сув оғзига етишини кутган одамга ўхшарлар. Ҳолбуки, унга етмас. Кофирларнинг дуоси залолатдан ўзга ҳеч нарса эмасдир'}, {'chapter': 13, 'verse': 15, 'text': 'وَلِلَّهِۤ يَسۡجُدُۤ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَظِلَٰلُهُم بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ۩\nАллоҳга осмонлару ерлардаги кимсалар ва уларнинг соялари хоҳ истасинлар, хоҳ истамасинлар, эртаю кеч сажда қилурлар'}, {'chapter': 13, 'verse': 16, 'text': 'قُلۡ مَن رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ قُلِ ٱللَّهُۚ قُلۡ أَفَٱتَّخَذۡتُم مِّن دُونِهِۦٓ أَوۡلِيَآءَ لَا يَمۡلِكُونَ لِأَنفُسِهِمۡ نَفۡعٗا وَلَا ضَرّٗاۚ قُلۡ هَلۡ يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ أَمۡ هَلۡ تَسۡتَوِي ٱلظُّلُمَٰتُ وَٱلنُّورُۗ أَمۡ جَعَلُواْ لِلَّهِ شُرَكَآءَ خَلَقُواْ كَخَلۡقِهِۦ فَتَشَٰبَهَ ٱلۡخَلۡقُ عَلَيۡهِمۡۚ قُلِ ٱللَّهُ خَٰلِقُ كُلِّ شَيۡءٖ وَهُوَ ٱلۡوَٰحِدُ ٱلۡقَهَّـٰرُ\nСен: «Осмонлару ернинг Робби ким?» деб айт. «Аллоҳ», дегин. «Ўзингизга Ундан ўзга, ўзларига фойда ёки зарарга эга бўлмаган валийларни тутдингизми?» дегин. «Кўзи кўр билан кўрувчи баробар бўлурми ёки зулматлар билан нур баробар бўлурми?» дегин. Ёки улар Аллоҳга У яратган махлуқотларга ўхшаш махлуқотларни яратганларни шерик қилиб олдилар-у, уларга яратилган махлуқотларга ўхшаш кўринмоқдами? «Барча нарсанинг яратувчиси Аллоҳдир. Ва У Бирдир, Қаҳҳордир», дегин'}, {'chapter': 13, 'verse': 17, 'text': 'أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَسَالَتۡ أَوۡدِيَةُۢ بِقَدَرِهَا فَٱحۡتَمَلَ ٱلسَّيۡلُ زَبَدٗا رَّابِيٗاۖ وَمِمَّا يُوقِدُونَ عَلَيۡهِ فِي ٱلنَّارِ ٱبۡتِغَآءَ حِلۡيَةٍ أَوۡ مَتَٰعٖ زَبَدٞ مِّثۡلُهُۥۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ ٱلۡحَقَّ وَٱلۡبَٰطِلَۚ فَأَمَّا ٱلزَّبَدُ فَيَذۡهَبُ جُفَآءٗۖ وَأَمَّا مَا يَنفَعُ ٱلنَّاسَ فَيَمۡكُثُ فِي ٱلۡأَرۡضِۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ\nУ осмондан сув туширди. Сўнгра водийларда ўз миқдорича сел оқди. Бас, сел ўз устига чиққан кўпикни кўтариб келди. Ва зеб-зийнат, асбоб-ускуна ясаш мақсадида ўтда тобланадиган нарсаларда ҳам шунга ўхшаш кўпик бўлур. Аллоҳ ҳақ ва ботилга мана шундай зарбулмасал келтирур. Аммо кўпик ўз-ўзидан йўқ бўлиб кетур. Одамларга манфаат берадиган нарса эса, ер юзида қолур. Аллоҳ мисолларни ана шундай келтирур'}, {'chapter': 13, 'verse': 18, 'text': 'لِلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمُ ٱلۡحُسۡنَىٰۚ وَٱلَّذِينَ لَمۡ يَسۡتَجِيبُواْ لَهُۥ لَوۡ أَنَّ لَهُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡاْ بِهِۦٓۚ أُوْلَـٰٓئِكَ لَهُمۡ سُوٓءُ ٱلۡحِسَابِ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ\nРоббиларига ижобат этганларга гўзаллик бордир. Унга ижобат этмаганларга эса, агар ер юзидаги ҳамма нарсалар ва яна шунчасига эга бўлсалар-у, уни фидо қилсалар ҳам, ана ўшаларга ёмон ҳисоб бўлур ва жойлари жаҳаннамдадир. У қандай ҳам ёмон жойдир'}, {'chapter': 13, 'verse': 19, 'text': '۞أَفَمَن يَعۡلَمُ أَنَّمَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ ٱلۡحَقُّ كَمَنۡ هُوَ أَعۡمَىٰٓۚ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nСенга Роббингдан нозил қилинган нарса албатта ҳақ эканини биладиган кимса кўр одамга ўхшармиди? Буни, албатта, ақл эгаларигина эсларлар'}, {'chapter': 13, 'verse': 20, 'text': 'ٱلَّذِينَ يُوفُونَ بِعَهۡدِ ٱللَّهِ وَلَا يَنقُضُونَ ٱلۡمِيثَٰقَ\nУлар Аллоҳнинг аҳдига вафо қиладиган ва мийсоқни бузмайдиганлардир'}, {'chapter': 13, 'verse': 21, 'text': 'وَٱلَّذِينَ يَصِلُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيَخۡشَوۡنَ رَبَّهُمۡ وَيَخَافُونَ سُوٓءَ ٱلۡحِسَابِ\nУлар Аллоҳ боғланишига амр этган нарсани боғларлар, Роббиларидан чўчирлар ва ёмон ҳисобдан қўрқарлар'}, {'chapter': 13, 'verse': 22, 'text': 'وَٱلَّذِينَ صَبَرُواْ ٱبۡتِغَآءَ وَجۡهِ رَبِّهِمۡ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ أُوْلَـٰٓئِكَ لَهُمۡ عُقۡبَى ٱلدَّارِ\nУлар Роббилари юзини кўзлаб, сабр қиларлар. Намозни тўкис адо этарлар, Биз уларга ризқ қилиб берган нарсадан махфий ва ошкора инфоқ қиларлар ва ёмонликни яхшилик ила қайтарарлар. Ана ўшаларга оқибат диёри бордир'}, {'chapter': 13, 'verse': 23, 'text': 'جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّـٰتِهِمۡۖ وَٱلۡمَلَـٰٓئِكَةُ يَدۡخُلُونَ عَلَيۡهِم مِّن كُلِّ بَابٖ\nАдн жаннатларидир. Унга улар ва уларнинг оталари, жуфти ҳалоллари ва зурриётларидан солиҳ бўлганлари кирарлар. Сўнгра, уларнинг ҳузурларига ҳар эшикдан фаришталар кириб'}, {'chapter': 13, 'verse': 24, 'text': 'سَلَٰمٌ عَلَيۡكُم بِمَا صَبَرۡتُمۡۚ فَنِعۡمَ عُقۡبَى ٱلدَّارِ\n«Сабр қилганингиз учун сизларга Салом бўлсин! Бу оқибат диёри қандай ҳам яхши!» (дерлар)'}, {'chapter': 13, 'verse': 25, 'text': 'وَٱلَّذِينَ يَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي ٱلۡأَرۡضِ أُوْلَـٰٓئِكَ لَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوٓءُ ٱلدَّارِ\nАллоҳнинг аҳдини мийсоқдан кейин бузадиганлар, Аллоҳ боғланишига амр этган нарсаларни узадиганлар ва ер юзида бузғунчилик қиладиганларга, ана ўшаларга лаънат ва уларга ёмон диёр бор'}, {'chapter': 13, 'verse': 26, 'text': 'ٱللَّهُ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ وَفَرِحُواْ بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا فِي ٱلۡأٓخِرَةِ إِلَّا مَتَٰعٞ\nАллоҳ хоҳлаган кишисининг ризқини кенг қилур ва (хоҳлаганникини) тор қилур. Улар бу дунё ҳаёти ила шод бўлдилар. Ҳолбуки, бу дунё ҳаёти охират олдида арзимас матоҳ, холос'}, {'chapter': 13, 'verse': 27, 'text': 'وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَنۡ أَنَابَ\nВа куфр келтирганлар: «Унга Роббидан бир мўъжиза тушса эди», дерлар. Сен: «Албатта, Аллоҳ кимни хоҳласа, залолатга кетказур ва ўзига йўналганларини ҳидоят қилур», деб айт'}, {'chapter': 13, 'verse': 28, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَتَطۡمَئِنُّ قُلُوبُهُم بِذِكۡرِ ٱللَّهِۗ أَلَا بِذِكۡرِ ٱللَّهِ تَطۡمَئِنُّ ٱلۡقُلُوبُ\nИймон келтирганлар ва Аллоҳнинг зикри ила қалблари ором топганлар. Аё, Аллоҳнинг зикри ила қалблар ором топмасми'}, {'chapter': 13, 'verse': 29, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ طُوبَىٰ لَهُمۡ وَحُسۡنُ مَـَٔابٖ\nИймон келтирганлар ва яхши амалларни қилганларга хушнудлик ва гўзал маскан бор'}, {'chapter': 13, 'verse': 30, 'text': 'كَذَٰلِكَ أَرۡسَلۡنَٰكَ فِيٓ أُمَّةٖ قَدۡ خَلَتۡ مِن قَبۡلِهَآ أُمَمٞ لِّتَتۡلُوَاْ عَلَيۡهِمُ ٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَهُمۡ يَكۡفُرُونَ بِٱلرَّحۡمَٰنِۚ قُلۡ هُوَ رَبِّي لَآ إِلَٰهَ إِلَّا هُوَ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ مَتَابِ\nШундай қилиб, сени ўзларидан олдин ҳам умматлар ўтган бир умматга уларга ўзингга ваҳий қилган нарсамизни тиловат қилиб беришинг учун юбордик. Ҳолбуки, улар Роҳманга куфр келтирурлар. Сен: «У менинг Роббимдир, Ундан ўзга ибодатга сазовор зот йўқ. Унинг Ўзигагина таваккал қилдим. Тавбам ҳам Унинг Ўзигадир», деб айт'}, {'chapter': 13, 'verse': 31, 'text': 'وَلَوۡ أَنَّ قُرۡءَانٗا سُيِّرَتۡ بِهِ ٱلۡجِبَالُ أَوۡ قُطِّعَتۡ بِهِ ٱلۡأَرۡضُ أَوۡ كُلِّمَ بِهِ ٱلۡمَوۡتَىٰۗ بَل لِّلَّهِ ٱلۡأَمۡرُ جَمِيعًاۗ أَفَلَمۡ يَاْيۡـَٔسِ ٱلَّذِينَ ءَامَنُوٓاْ أَن لَّوۡ يَشَآءُ ٱللَّهُ لَهَدَى ٱلنَّاسَ جَمِيعٗاۗ وَلَا يَزَالُ ٱلَّذِينَ كَفَرُواْ تُصِيبُهُم بِمَا صَنَعُواْ قَارِعَةٌ أَوۡ تَحُلُّ قَرِيبٗا مِّن دَارِهِمۡ حَتَّىٰ يَأۡتِيَ وَعۡدُ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يُخۡلِفُ ٱلۡمِيعَادَ\nАгар Қуръон ила тоғлар жилдириладиган, ер парчаланадиган ёки ўликлар тилга киритиладиган бўлганида (имкони бор эди). Аммо, ишнинг барчаси Аллоҳга хосдир. Иймон келтирганлар, агар Аллоҳ хоҳласа, одамларнинг ҳаммасини ҳидоят қила олишини билмадиларми? Куфр келтирганларга, то Аллоҳнинг ваъдаси келгунча, ўз ишлари сабабли мудом ларзага солувчи мусийбатлар етар ёки уларнинг диёрлари яқинига тушиб турар. Албатта, Аллоҳ ваъдага хилоф қилмас'}, {'chapter': 13, 'verse': 32, 'text': 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَأَمۡلَيۡتُ لِلَّذِينَ كَفَرُواْ ثُمَّ أَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ\nБатаҳқиқ, сендан олдин ҳам Пайғамбарлар масхара қилинганлар. Бас, куфр келтирганларга муҳлат бердим. Сўнгра, уларни ушладим. Бас, иқобим қандоқ бўлган эди'}, {'chapter': 13, 'verse': 33, 'text': 'أَفَمَنۡ هُوَ قَآئِمٌ عَلَىٰ كُلِّ نَفۡسِۭ بِمَا كَسَبَتۡۗ وَجَعَلُواْ لِلَّهِ شُرَكَآءَ قُلۡ سَمُّوهُمۡۚ أَمۡ تُنَبِّـُٔونَهُۥ بِمَا لَا يَعۡلَمُ فِي ٱلۡأَرۡضِ أَم بِظَٰهِرٖ مِّنَ ٱلۡقَوۡلِۗ بَلۡ زُيِّنَ لِلَّذِينَ كَفَرُواْ مَكۡرُهُمۡ وَصُدُّواْ عَنِ ٱلسَّبِيلِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ\nҲар бир жоннинг қилган касби устида турувчи зот ким? Аллоҳга ширк келтирдилар. Сен: «Уларнинг номларини айтинг. Ёки Унга ер юзида У билмайдиган нарсанинг хабарини берасизларми? Балки, юзаки гапдир», дегин. Йўқ, куфр келтирганларга макрлари зийнатлаб кўрсатилди ва улар йўлдан тўсдилар. Аллоҳ кимни залолатга кетказса, унга ҳеч бир ҳидоятчи бўлмас'}, {'chapter': 13, 'verse': 34, 'text': 'لَّهُمۡ عَذَابٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَشَقُّۖ وَمَا لَهُم مِّنَ ٱللَّهِ مِن وَاقٖ\nУларга бу дунё ҳаётида азоб бор. Албатта, охират азоби машаққатлироқ. Улар учун Аллоҳдан сақловчи бўлмас'}, {'chapter': 13, 'verse': 35, 'text': '۞مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ أُكُلُهَا دَآئِمٞ وَظِلُّهَاۚ تِلۡكَ عُقۡبَى ٱلَّذِينَ ٱتَّقَواْۚ وَّعُقۡبَى ٱلۡكَٰفِرِينَ ٱلنَّارُ\nТақводорларга ваъда қилинган жаннатнинг мисоли (будир): остидан анҳорлар оқади, мевалари ва соялари доимийдир. Бу, тақво қилганларнинг оқибатидир. Кофирларнинг оқибати эса, дўзахдир'}, {'chapter': 13, 'verse': 36, 'text': 'وَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَفۡرَحُونَ بِمَآ أُنزِلَ إِلَيۡكَۖ وَمِنَ ٱلۡأَحۡزَابِ مَن يُنكِرُ بَعۡضَهُۥۚ قُلۡ إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ وَلَآ أُشۡرِكَ بِهِۦٓۚ إِلَيۡهِ أَدۡعُواْ وَإِلَيۡهِ مَـَٔابِ\nБиз китоб берганлар сенга нозил қилинган нарсадан хурсанд бўлурлар. Гуруҳлар ичида унинг баъзисини инкор қиладиган кимсалар ҳам бор. Сен: «Албатта, мен Аллоҳга ибодат қилишга, Унга ширк келтирмасликка амр қилиндим. Унгагина даъват қиламан ҳамда қайтишим ҳам Унгадир», деб айт'}, {'chapter': 13, 'verse': 37, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَٰهُ حُكۡمًا عَرَبِيّٗاۚ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِيّٖ وَلَا وَاقٖ\nШунингдек, Биз уни арабий ҳукм этиб нозил қилдик. Агар сен ўзингга келган илмдан сўнг уларнинг ҳавойи нафсларига эргашсанг, сен учун Аллоҳдан на бир дўст ва на бир сақловчи бўлмас'}, {'chapter': 13, 'verse': 38, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ وَجَعَلۡنَا لَهُمۡ أَزۡوَٰجٗا وَذُرِّيَّةٗۚ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ لِكُلِّ أَجَلٖ كِتَابٞ\nБатаҳқиқ, Биз сендан олдин ҳам Пайғамбарлар юбордик ва уларга хотинлар ва зурриётлар ато қилганмиз. Ҳеч бир Пайғамбар Аллоҳнинг изнисиз оят-мўъжиза келтирган эмас. Ҳар бир ажалнинг ўз китоби бордир'}, {'chapter': 13, 'verse': 39, 'text': 'يَمۡحُواْ ٱللَّهُ مَا يَشَآءُ وَيُثۡبِتُۖ وَعِندَهُۥٓ أُمُّ ٱلۡكِتَٰبِ\nАллоҳ нимани хоҳласа, маҳв қилур, нимани хоҳласа, собит қилур. Она китоб Унинг даргоҳидадир'}, {'chapter': 13, 'verse': 40, 'text': 'وَإِن مَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُ وَعَلَيۡنَا ٱلۡحِسَابُ\nУларга ваъда қилганимизнинг баъзисини сенга кўрсатармиз ёки (ундан олдин) сени вафот эттирармиз. Албатта, етказиш сенинг зиммангда, ҳисоб қилиш Бизнинг зиммамиздадир'}, {'chapter': 13, 'verse': 41, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا نَأۡتِي ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَاۚ وَٱللَّهُ يَحۡكُمُ لَا مُعَقِّبَ لِحُكۡمِهِۦۚ وَهُوَ سَرِيعُ ٱلۡحِسَابِ\nБиз ерга келиб, унинг атрофидан қисқартириб бораётганимизни кўрмасларми?! Ҳукмни Аллоҳ қиладир. Унинг ҳукмини таъқиб қилгувчи йўқдир. У ҳисоби тез Зотдир'}, {'chapter': 13, 'verse': 42, 'text': 'وَقَدۡ مَكَرَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَلِلَّهِ ٱلۡمَكۡرُ جَمِيعٗاۖ يَعۡلَمُ مَا تَكۡسِبُ كُلُّ نَفۡسٖۗ وَسَيَعۡلَمُ ٱلۡكُفَّـٰرُ لِمَنۡ عُقۡبَى ٱلدَّارِ\nБатаҳқиқ, улардан олдингилар ҳам макр қилдилар. Аммо, барча макрлар Аллоҳнинг (илкидадир). У ҳар бир жон нима қилишини биладир. Яқинда кофирлар оқибат диёри кимга бўлишини билажаклар'}, {'chapter': 13, 'verse': 43, 'text': 'وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَسۡتَ مُرۡسَلٗاۚ قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡ وَمَنۡ عِندَهُۥ عِلۡمُ ٱلۡكِتَٰبِ\nКуфр келтирганлар: «Пайғамбар эмассан», дерлар. Сен: «Мен билан сизнинг орангизда Аллоҳ ва Китоб илми бор кимсалар етарли гувоҳдир», деб айт'}], '14': [{'chapter': 14, 'verse': 1, 'text': 'الٓرۚ كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ لِتُخۡرِجَ ٱلنَّاسَ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ بِإِذۡنِ رَبِّهِمۡ إِلَىٰ صِرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ\nАлиф. Лаам. Ро. Бу китобни сенга одамларни Роббиларининг изни ила зулматлардан нурга, азийз ва мақталган Зотнинг йўлига чиқаришинг учун нозил қилдик'}, {'chapter': 14, 'verse': 2, 'text': 'ٱللَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَوَيۡلٞ لِّلۡكَٰفِرِينَ مِنۡ عَذَابٖ شَدِيدٍ\nАллоҳ осмонлару ердаги нарсалар Уники бўлган Зотдир. Кофирлар ҳолига шиддатли азобдан вой бўлсин'}, {'chapter': 14, 'verse': 3, 'text': 'ٱلَّذِينَ يَسۡتَحِبُّونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا عَلَى ٱلۡأٓخِرَةِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجًاۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلِۭ بَعِيدٖ\nУлар бу дунё ҳаётини охиратдан афзал кўрадиганлар, Аллоҳнинг йўлидан тўсадиганлар ва у(йўл)нинг эгри бўлишини хоҳлайдиганлардир. Ана ўшалар йироқ адашувдадирлар'}, {'chapter': 14, 'verse': 4, 'text': 'وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوۡمِهِۦ لِيُبَيِّنَ لَهُمۡۖ فَيُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nҚайсики Пайғамбарни юборган бўлсак, баён қилиб бериши учун, ўз қавми тили ила юборганмиз. Бас, Аллоҳ кимни хоҳласа, залолатга кетказади, кимни хоҳласа, ҳидоятга соладир. У Азийз ва ҳикматли Зотдир'}, {'chapter': 14, 'verse': 5, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَآ أَنۡ أَخۡرِجۡ قَوۡمَكَ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ وَذَكِّرۡهُم بِأَيَّىٰمِ ٱللَّهِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ\nБатаҳқиқ, Биз Мусони оятларимиз ила: «Қавмингни зулматлардан нурга чиқар ва уларга Аллоҳнинг кунларини эслат! Албатта, бунда ҳар бир сабрли ва шукрли учун ибратлар бордир», деб юбордик'}, {'chapter': 14, 'verse': 6, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ أَنجَىٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ وَيُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ\nЎшанда Мусо қавмига: «Аллоҳнинг сизга берган неъматларини эсланг: У сизни Оли Фиръавндан қутқарди. Улар сизга бардавом ёмон азоб берардилар. Ўғилларингизни сўйиб ташлаб, қизларингизни тирик қолдирардилар. Ана ўшанда Роббингиздан буюк синов бордир», деган эди'}, {'chapter': 14, 'verse': 7, 'text': 'وَإِذۡ تَأَذَّنَ رَبُّكُمۡ لَئِن شَكَرۡتُمۡ لَأَزِيدَنَّكُمۡۖ وَلَئِن كَفَرۡتُمۡ إِنَّ عَذَابِي لَشَدِيدٞ\n(Яна унинг): «Ва Роббингиз (сизга): «Қасамки, агар шукр қилсангиз, албатта, сизга зиёда қилурман. Агар куфр келтирсангиз, албатта, азобим шиддатлидир», деб билдирган(ини эсланг)'}, {'chapter': 14, 'verse': 8, 'text': 'وَقَالَ مُوسَىٰٓ إِن تَكۡفُرُوٓاْ أَنتُمۡ وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗا فَإِنَّ ٱللَّهَ لَغَنِيٌّ حَمِيدٌ\nМусо деди: «Агар сиз ва ер юзидаги кимсалар ҳаммангиз куфр келтирсангизлар ҳам, албатта, Аллоҳ беҳожат ва мақталган Зотдир'}, {'chapter': 14, 'verse': 9, 'text': 'أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ ٱلَّذِينَ مِن قَبۡلِكُمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمۡ لَا يَعۡلَمُهُمۡ إِلَّا ٱللَّهُۚ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَرَدُّوٓاْ أَيۡدِيَهُمۡ فِيٓ أَفۡوَٰهِهِمۡ وَقَالُوٓاْ إِنَّا كَفَرۡنَا بِمَآ أُرۡسِلۡتُم بِهِۦ وَإِنَّا لَفِي شَكّٖ مِّمَّا تَدۡعُونَنَآ إِلَيۡهِ مُرِيبٖ\nСизга ўзингиздан олдин ўтган Нуҳ, Од, Самуд қавмларининг ва улардан кейингиларнинг, Аллоҳдан ўзга ҳеч ким билмайдиганларнинг хабари келмадими? Уларга Пайғамбарлари очиқ-ойдин ҳужжатлар билан келдилар. Улар эса, қўлларини оғизларига қўйиб: «Биз сиз бизни чақираётган нарсада шубҳалантирувчи шакдадирмиз!» дедилар'}, {'chapter': 14, 'verse': 10, 'text': '۞قَالَتۡ رُسُلُهُمۡ أَفِي ٱللَّهِ شَكّٞ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَدۡعُوكُمۡ لِيَغۡفِرَ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرَكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۚ قَالُوٓاْ إِنۡ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا تُرِيدُونَ أَن تَصُدُّونَا عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتُونَا بِسُلۡطَٰنٖ مُّبِينٖ\nПайғамбарлари уларга: «Осмонлару ернинг яратгувчиси бўлмиш Аллоҳга шак борми? У сизларни гуноҳларингизни мағфират қилишга ва ўзингизни маълум муддатга қолдиришга чақирмоқда-ку!» дедилар. Улар эса: «Сиз бизга ўхшаш башардан ўзга ҳеч нарса эмассиз. Бизни оталаримиз ибодат қилиб юрган нарсадан тўсмоқчисиз. Бас, бизга очиқ-ойдин ҳужжат келтиринг», дедилар'}, {'chapter': 14, 'verse': 11, 'text': 'قَالَتۡ لَهُمۡ رُسُلُهُمۡ إِن نَّحۡنُ إِلَّا بَشَرٞ مِّثۡلُكُمۡ وَلَٰكِنَّ ٱللَّهَ يَمُنُّ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَمَا كَانَ لَنَآ أَن نَّأۡتِيَكُم بِسُلۡطَٰنٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nПайғамбарлари уларга: «Биз сизга ўхшаш башардан ўзга ҳеч нарса эмасмиз. Лекин Аллоҳ Ўз бандаларидан кимни хоҳласа, ўшанга (пайғамбарлик) инъом қилур. Биз ўзимизча сизга бирор ҳужжат келтира олмаймиз, илло Аллоҳнинг изни ила. Мўминлар фақат Аллоҳгагина таваккал қилсинлар'}, {'chapter': 14, 'verse': 12, 'text': 'وَمَا لَنَآ أَلَّا نَتَوَكَّلَ عَلَى ٱللَّهِ وَقَدۡ هَدَىٰنَا سُبُلَنَاۚ وَلَنَصۡبِرَنَّ عَلَىٰ مَآ ءَاذَيۡتُمُونَاۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ\nНима учун биз Аллоҳга таваккал қилмас эканмиз?! Ҳолбуки, У бизни йўлимизга ҳидоят қилди. Бизга берган озорларингизга, албатта, сабр қиламиз. Таваккал қилувчилар фақат Аллоҳгагина таваккал қилсинлар», дедилар'}, {'chapter': 14, 'verse': 13, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِرُسُلِهِمۡ لَنُخۡرِجَنَّكُم مِّنۡ أَرۡضِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۖ فَأَوۡحَىٰٓ إِلَيۡهِمۡ رَبُّهُمۡ لَنُهۡلِكَنَّ ٱلظَّـٰلِمِينَ\nКуфр келтирганлар ўз Пайғамбарларига: «Албатта, биз сизларни еримиздан чиқариб юборурмиз ёки, албатта, миллатимизга қайтурсизлар», дедилар. Бас, Роббилари уларга: «Албатта, Биз золимларни ҳалок қилурмиз'}, {'chapter': 14, 'verse': 14, 'text': 'وَلَنُسۡكِنَنَّكُمُ ٱلۡأَرۡضَ مِنۢ بَعۡدِهِمۡۚ ذَٰلِكَ لِمَنۡ خَافَ مَقَامِي وَخَافَ وَعِيدِ\nВа улардан сўнг у ерга сизни жойлаштирурмиз. Бу эса, мақомимдан хавф қилганлар ва таҳдидимдан қўрққанлар учундир», деб ваҳий қилди'}, {'chapter': 14, 'verse': 15, 'text': 'وَٱسۡتَفۡتَحُواْ وَخَابَ كُلُّ جَبَّارٍ عَنِيدٖ\nФатҳу нусрат сўрадилар ва ҳар бир жабрчи, саркаш кимса ноумидликка учради'}, {'chapter': 14, 'verse': 16, 'text': 'مِّن وَرَآئِهِۦ جَهَنَّمُ وَيُسۡقَىٰ مِن مَّآءٖ صَدِيدٖ\nУнинг ортида жаҳаннам бордир. Йиринг сув ила суғориладир'}, {'chapter': 14, 'verse': 17, 'text': 'يَتَجَرَّعُهُۥ وَلَا يَكَادُ يُسِيغُهُۥ وَيَأۡتِيهِ ٱلۡمَوۡتُ مِن كُلِّ مَكَانٖ وَمَا هُوَ بِمَيِّتٖۖ وَمِن وَرَآئِهِۦ عَذَابٌ غَلِيظٞ\nУни ютишга уринади-ю, юта олмас. Унга ҳар томондан ўлим келади-ю, ўла олмас. Унинг ортидан эса, қаттиқ азоб бор'}, {'chapter': 14, 'verse': 18, 'text': 'مَّثَلُ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ أَعۡمَٰلُهُمۡ كَرَمَادٍ ٱشۡتَدَّتۡ بِهِ ٱلرِّيحُ فِي يَوۡمٍ عَاصِفٖۖ لَّا يَقۡدِرُونَ مِمَّا كَسَبُواْ عَلَىٰ شَيۡءٖۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلۡبَعِيدُ\nРоббиларига куфр келтирганларнинг амалларининг мисоли, худди бўронли кунда шамол учириб кетган кулга ўхшайдир. Улар ўз касбларидан бирор нарсага эга бўлмаслар. Ана ўша узоқ адашувнинг ўзидир'}, {'chapter': 14, 'verse': 19, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ\nАллоҳ осмонлару ерни ҳақ ила яратиб қўйганини кўрмадингми? Агар истаса, сизларни кетказиб, янги халқ келтирадир'}, {'chapter': 14, 'verse': 20, 'text': 'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ\nБу эса, Аллоҳ учун қийин эмас'}, {'chapter': 14, 'verse': 21, 'text': 'وَبَرَزُواْ لِلَّهِ جَمِيعٗا فَقَالَ ٱلضُّعَفَـٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا مِنۡ عَذَابِ ٱللَّهِ مِن شَيۡءٖۚ قَالُواْ لَوۡ هَدَىٰنَا ٱللَّهُ لَهَدَيۡنَٰكُمۡۖ سَوَآءٌ عَلَيۡنَآ أَجَزِعۡنَآ أَمۡ صَبَرۡنَا مَا لَنَا مِن مَّحِيصٖ\nУларнинг ҳаммалари Аллоҳ ҳузурида пайдо бўлдилар. Бас, заифлар мутакаббирларга: «Албатта, биз сизларга тобе бўлган эдик. Энди, сизлар устимиздаги Аллоҳнинг азобидан бирор нарсани енгиллата оласизларми?» дедилар. Улар: «Агар Аллоҳ бизни ҳидоят қилганида, биз ҳам сизларни, албатта, ҳидоят қилар эдик. Биз учун энди, дод-вой қиламизми ёки сабр қиламизми — бари бир. Биз учун қочар жой йўқ», дедилар'}, {'chapter': 14, 'verse': 22, 'text': 'وَقَالَ ٱلشَّيۡطَٰنُ لَمَّا قُضِيَ ٱلۡأَمۡرُ إِنَّ ٱللَّهَ وَعَدَكُمۡ وَعۡدَ ٱلۡحَقِّ وَوَعَدتُّكُمۡ فَأَخۡلَفۡتُكُمۡۖ وَمَا كَانَ لِيَ عَلَيۡكُم مِّن سُلۡطَٰنٍ إِلَّآ أَن دَعَوۡتُكُمۡ فَٱسۡتَجَبۡتُمۡ لِيۖ فَلَا تَلُومُونِي وَلُومُوٓاْ أَنفُسَكُمۖ مَّآ أَنَا۠ بِمُصۡرِخِكُمۡ وَمَآ أَنتُم بِمُصۡرِخِيَّ إِنِّي كَفَرۡتُ بِمَآ أَشۡرَكۡتُمُونِ مِن قَبۡلُۗ إِنَّ ٱلظَّـٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ\nИш битиб бўлганда шайтон: «Албатта, Аллоҳ сизга ҳақ ваъдани қилган эди. Мен эса, сизга ваъда бердим-у, ваъдамга хилоф қилдим. Менинг устингиздан ҳеч қандай ҳукмронлигим йўқ эди. Фақат даъват қилсам, ўзингиз менга ижобат қилдингиз. Бас, мени маломат қилманг, ўзингизни маломат қилинг. Мен сизларни қутқарувчи эмасман, сизлар ҳам мени қутқарувчи эмассизлар. Энди, мен, албатта, илгари мени шерик қилганингизни инкор қиламан. Албатта, золимларга, ҳа, уларга аламли азоб бор», деди'}, {'chapter': 14, 'verse': 23, 'text': 'وَأُدۡخِلَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا بِإِذۡنِ رَبِّهِمۡۖ تَحِيَّتُهُمۡ فِيهَا سَلَٰمٌ\nВа иймон келтириб, амали солиҳ қилганлар остидан анҳорлар оқиб турган жаннатларга киритилди. Унда Роббилари изни ила абадий қолурлар. Уларнинг ундаги сўрашишлари «Салом»дир'}, {'chapter': 14, 'verse': 24, 'text': 'أَلَمۡ تَرَ كَيۡفَ ضَرَبَ ٱللَّهُ مَثَلٗا كَلِمَةٗ طَيِّبَةٗ كَشَجَرَةٖ طَيِّبَةٍ أَصۡلُهَا ثَابِتٞ وَفَرۡعُهَا فِي ٱلسَّمَآءِ\nАллоҳ яхши сўзга қандай мисол келтирганини кўрмайсанми? У худди бир яхши дарахтга ўхшайдир. Унинг асли собит турадир. Шохлари эса, осмонда'}, {'chapter': 14, 'verse': 25, 'text': 'تُؤۡتِيٓ أُكُلَهَا كُلَّ حِينِۭ بِإِذۡنِ رَبِّهَاۗ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ لِلنَّاسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nРобби изни ила ҳар доим меваларини бериб турадир. Аллоҳ, шояд эсласалар деб, одамларга мисоллар келтирадир'}, {'chapter': 14, 'verse': 26, 'text': 'وَمَثَلُ كَلِمَةٍ خَبِيثَةٖ كَشَجَرَةٍ خَبِيثَةٍ ٱجۡتُثَّتۡ مِن فَوۡقِ ٱلۡأَرۡضِ مَا لَهَا مِن قَرَارٖ\nЁмон сўзнинг мисоли, худди ёмон дарахтга ўхшайдир. У ердан узилиб қолган, унинг қарори йўқдир'}, {'chapter': 14, 'verse': 27, 'text': 'يُثَبِّتُ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ بِٱلۡقَوۡلِ ٱلثَّابِتِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَيُضِلُّ ٱللَّهُ ٱلظَّـٰلِمِينَۚ وَيَفۡعَلُ ٱللَّهُ مَا يَشَآءُ\nАллоҳ иймон келтирганларни бу дунё ҳаётида ҳам, охиратда ҳам собит сўз ила собитқадам қилур. Аллоҳ золимларни залолатга кетказур. Аллоҳ хоҳлаганини қилур'}, {'chapter': 14, 'verse': 28, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ بَدَّلُواْ نِعۡمَتَ ٱللَّهِ كُفۡرٗا وَأَحَلُّواْ قَوۡمَهُمۡ دَارَ ٱلۡبَوَارِ\nАллоҳнинг неъматини куфрга алмаштирган ва қавмларини ҳалокат диёрига туширганларни кўрмадингми'}, {'chapter': 14, 'verse': 29, 'text': 'جَهَنَّمَ يَصۡلَوۡنَهَاۖ وَبِئۡسَ ٱلۡقَرَارُ\nЖаҳаннамга кирурлар. Бу қандоқ ҳам ёмон қароргоҳ'}, {'chapter': 14, 'verse': 30, 'text': 'وَجَعَلُواْ لِلَّهِ أَندَادٗا لِّيُضِلُّواْ عَن سَبِيلِهِۦۗ قُلۡ تَمَتَّعُواْ فَإِنَّ مَصِيرَكُمۡ إِلَى ٱلنَّارِ\nУлар Аллоҳнинг йўлидан адаштириш учун Унга тенгдошлар қилдилар. Сен уларга: «Ҳузурланиб қолинг! Бас, албатта, борадиган жойингиз дўзахдир», деб айт'}, {'chapter': 14, 'verse': 31, 'text': 'قُل لِّعِبَادِيَ ٱلَّذِينَ ءَامَنُواْ يُقِيمُواْ ٱلصَّلَوٰةَ وَيُنفِقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعٞ فِيهِ وَلَا خِلَٰلٌ\nСен иймон келтирган бандаларимга айтгин, намозни тўла-тўкис адо этсинлар. Савдо-сотиқ, оғайнигарчилик йўқ бўлган Кун келмасидан олдин уларга ризқ қилиб берган нарсаларимиздан махфий ва ошкор инфоқ қилсинлар'}, {'chapter': 14, 'verse': 32, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ وَسَخَّرَ لَكُمُ ٱلۡفُلۡكَ لِتَجۡرِيَ فِي ٱلۡبَحۡرِ بِأَمۡرِهِۦۖ وَسَخَّرَ لَكُمُ ٱلۡأَنۡهَٰرَ\nАллоҳ осмонлару ерни яратган, осмондан сув тушириб, у ила меваларни сизга ризқ қилиб чиқарган, Ўз амри ила денгизда сузмоғи учун кемаларни сизга бўйсундирган ва анҳорларни сизга бўйсундирган Зотдир'}, {'chapter': 14, 'verse': 33, 'text': 'وَسَخَّرَ لَكُمُ ٱلشَّمۡسَ وَٱلۡقَمَرَ دَآئِبَيۡنِۖ وَسَخَّرَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ\nУ сизга доимий ҳаракатдаги қуёш ва ойни ҳам, шунингдек, кеча ила кундузни ҳам хизматчи қилиб қўйди'}, {'chapter': 14, 'verse': 34, 'text': 'وَءَاتَىٰكُم مِّن كُلِّ مَا سَأَلۡتُمُوهُۚ وَإِن تَعُدُّواْ نِعۡمَتَ ٱللَّهِ لَا تُحۡصُوهَآۗ إِنَّ ٱلۡإِنسَٰنَ لَظَلُومٞ كَفَّارٞ\nВа У сизга барча сўраган нарсаларингиздан берди. Агар Аллоҳнинг неъматларини санасангиз, саноғига ета олмассизлар. Албатта, инсон ўта золим ва ўта ношукрдир'}, {'chapter': 14, 'verse': 35, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِيمُ رَبِّ ٱجۡعَلۡ هَٰذَا ٱلۡبَلَدَ ءَامِنٗا وَٱجۡنُبۡنِي وَبَنِيَّ أَن نَّعۡبُدَ ٱلۡأَصۡنَامَ\nЭсла, Иброҳим шундай демишди: «Эй Роббим, бу юртни омонлик юрти қилгин, мени ва болаларимни санамларга ибодат қилишимиздан четда қилгин'}, {'chapter': 14, 'verse': 36, 'text': 'رَبِّ إِنَّهُنَّ أَضۡلَلۡنَ كَثِيرٗا مِّنَ ٱلنَّاسِۖ فَمَن تَبِعَنِي فَإِنَّهُۥ مِنِّيۖ وَمَنۡ عَصَانِي فَإِنَّكَ غَفُورٞ رَّحِيمٞ\nЭй Роббим, албатта, у(санам)лар одамлардан кўпини адаштирдилар. Бас, ким менга эргашса, у мендандир. Ким менга осий бўлса, албатта, Ўзинг мағфират қилгувчи ва раҳимлисан'}, {'chapter': 14, 'verse': 37, 'text': 'رَّبَّنَآ إِنِّيٓ أَسۡكَنتُ مِن ذُرِّيَّتِي بِوَادٍ غَيۡرِ ذِي زَرۡعٍ عِندَ بَيۡتِكَ ٱلۡمُحَرَّمِ رَبَّنَا لِيُقِيمُواْ ٱلصَّلَوٰةَ فَٱجۡعَلۡ أَفۡـِٔدَةٗ مِّنَ ٱلنَّاسِ تَهۡوِيٓ إِلَيۡهِمۡ وَٱرۡزُقۡهُم مِّنَ ٱلثَّمَرَٰتِ لَعَلَّهُمۡ يَشۡكُرُونَ\nЭй Роббимиз, ҳақиқатда мен ўз зурриётимдан Сенинг Байтул Ҳароминг ёнига, гиёҳсиз водийга жойлаштирдим. Эй Роббимиз, намозни тўкис адо этишлари учун. Бас, Ўзинг одамлардан баъзиларининг қалбларини уларга талпинадиган қилгин ва уларни мевалардан ризқлантиргин. Шояд, шукр қилсалар'}, {'chapter': 14, 'verse': 38, 'text': 'رَبَّنَآ إِنَّكَ تَعۡلَمُ مَا نُخۡفِي وَمَا نُعۡلِنُۗ وَمَا يَخۡفَىٰ عَلَى ٱللَّهِ مِن شَيۡءٖ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ\nЭй Роббимиз, албатта, Сенинг Ўзинг махфий тутган нарсамизни ҳам, ошкор қилган нарсамизни ҳам яхши билурсан. Аллоҳ учун на ердаю на осмонда бирор нарса махфий қолмас'}, {'chapter': 14, 'verse': 39, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي وَهَبَ لِي عَلَى ٱلۡكِبَرِ إِسۡمَٰعِيلَ وَإِسۡحَٰقَۚ إِنَّ رَبِّي لَسَمِيعُ ٱلدُّعَآءِ\nКексалик пайтимда менга Исмоил ва Исҳоқни ато қилган Аллоҳга ҳамд бўлсин. Албатта, Роббим дуони эшитгувчидир'}, {'chapter': 14, 'verse': 40, 'text': 'رَبِّ ٱجۡعَلۡنِي مُقِيمَ ٱلصَّلَوٰةِ وَمِن ذُرِّيَّتِيۚ رَبَّنَا وَتَقَبَّلۡ دُعَآءِ\nЭй Роббим, мени ва зурриётларимни намозни тўкис адо этадиганлардан қилгин. Эй Роббимиз, дуони қабул этгин'}, {'chapter': 14, 'verse': 41, 'text': 'رَبَّنَا ٱغۡفِرۡ لِي وَلِوَٰلِدَيَّ وَلِلۡمُؤۡمِنِينَ يَوۡمَ يَقُومُ ٱلۡحِسَابُ\nЭй Роббимиз, мени, ота-онамни ва мўминларни ҳисоб қилинадиган куни мағфират айлагин»'}, {'chapter': 14, 'verse': 42, 'text': 'وَلَا تَحۡسَبَنَّ ٱللَّهَ غَٰفِلًا عَمَّا يَعۡمَلُ ٱلظَّـٰلِمُونَۚ إِنَّمَا يُؤَخِّرُهُمۡ لِيَوۡمٖ تَشۡخَصُ فِيهِ ٱلۡأَبۡصَٰرُ\nСен ҳаргиз, Аллоҳ золимлар қилаётган нарсадан ғофил, деб гумон қилма. Фақатгина, уларни кўзлар қотиб қоладиган кунгача қўйиб қўядир, холос'}, {'chapter': 14, 'verse': 43, 'text': 'مُهۡطِعِينَ مُقۡنِعِي رُءُوسِهِمۡ لَا يَرۡتَدُّ إِلَيۡهِمۡ طَرۡفُهُمۡۖ وَأَفۡـِٔدَتُهُمۡ هَوَآءٞ\nУлар бошларини кўтарган ҳолда югурурлар. Кўзлари ўзларига қайтмас. Кўнгиллари эса, бўм-бўш бўлур'}, {'chapter': 14, 'verse': 44, 'text': 'وَأَنذِرِ ٱلنَّاسَ يَوۡمَ يَأۡتِيهِمُ ٱلۡعَذَابُ فَيَقُولُ ٱلَّذِينَ ظَلَمُواْ رَبَّنَآ أَخِّرۡنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖ نُّجِبۡ دَعۡوَتَكَ وَنَتَّبِعِ ٱلرُّسُلَۗ أَوَلَمۡ تَكُونُوٓاْ أَقۡسَمۡتُم مِّن قَبۡلُ مَا لَكُم مِّن زَوَالٖ\nСен одамларни уларга азоб келадиган Кундан огоҳлантир. Ўшанда зулм қилганлар: «Эй Роббимиз, бизни озгина вақт қўйиб тургин, Сенинг даъватингни қабул қиламиз ва Пайғамбарларга эргашамиз», дерлар. Бундан олдин сизларга завол йўқлигига қасам ичмаган эдингизми'}, {'chapter': 14, 'verse': 45, 'text': 'وَسَكَنتُمۡ فِي مَسَٰكِنِ ٱلَّذِينَ ظَلَمُوٓاْ أَنفُسَهُمۡ وَتَبَيَّنَ لَكُمۡ كَيۡفَ فَعَلۡنَا بِهِمۡ وَضَرَبۡنَا لَكُمُ ٱلۡأَمۡثَالَ\nВа ўзига зулм қилганларнинг масканларига жойлашмадингизми? Сизга уларни қандай қилганимиз равшан бўлмадими? Сизга мисоллар келтирмадикми'}, {'chapter': 14, 'verse': 46, 'text': 'وَقَدۡ مَكَرُواْ مَكۡرَهُمۡ وَعِندَ ٱللَّهِ مَكۡرُهُمۡ وَإِن كَانَ مَكۡرُهُمۡ لِتَزُولَ مِنۡهُ ٱلۡجِبَالُ\nДарҳақиқат, улар ўз макрларини қилдилар. Ҳолбуки, уларнинг макрлари, гарчи макрларидан тоғлар заволга учрайдиган бўлса ҳам, Аллоҳнинг ҳузуридадир'}, {'chapter': 14, 'verse': 47, 'text': 'فَلَا تَحۡسَبَنَّ ٱللَّهَ مُخۡلِفَ وَعۡدِهِۦ رُسُلَهُۥٓۚ إِنَّ ٱللَّهَ عَزِيزٞ ذُو ٱنتِقَامٖ\nСен, Аллоҳ Пайғамбарларига берган ваъдасига хилоф қилувчи деб, гумон қилма. Албатта, Аллоҳ Азийз ва интиқом эгасидир'}, {'chapter': 14, 'verse': 48, 'text': 'يَوۡمَ تُبَدَّلُ ٱلۡأَرۡضُ غَيۡرَ ٱلۡأَرۡضِ وَٱلسَّمَٰوَٰتُۖ وَبَرَزُواْ لِلَّهِ ٱلۡوَٰحِدِ ٱلۡقَهَّارِ\nБу, ер бошқа ерга ва осмонлар ҳам (бошқа осмонларга) алмаштириладиган ва у(одам)лар ёлғизу қаҳҳор Аллоҳ ҳузурида пайдо бўлишадиган Кунда бўладир'}, {'chapter': 14, 'verse': 49, 'text': 'وَتَرَى ٱلۡمُجۡرِمِينَ يَوۡمَئِذٖ مُّقَرَّنِينَ فِي ٱلۡأَصۡفَادِ\nВа у кунда жиноятчиларни кишанлар ила тўпланган ҳолларида кўрасан'}, {'chapter': 14, 'verse': 50, 'text': 'سَرَابِيلُهُم مِّن قَطِرَانٖ وَتَغۡشَىٰ وُجُوهَهُمُ ٱلنَّارُ\nУларнинг кийимлари қора мойдан бўлиб, юзларини олов қоплагандир'}, {'chapter': 14, 'verse': 51, 'text': 'لِيَجۡزِيَ ٱللَّهُ كُلَّ نَفۡسٖ مَّا كَسَبَتۡۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nАллоҳ ҳар бир жонга қилган касбига яраша жазо бериши учундир. Албатта, Аллоҳ тез ҳисоб қилгувчидир'}, {'chapter': 14, 'verse': 52, 'text': 'هَٰذَا بَلَٰغٞ لِّلنَّاسِ وَلِيُنذَرُواْ بِهِۦ وَلِيَعۡلَمُوٓاْ أَنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَلِيَذَّكَّرَ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nБу (Қуръон) одамлар учун, уларнинг у ила огоҳлантирилишлари, У ягона илоҳ эканини билишлари ва ақл эгалари эслашлари учун баёнотдир'}], '15': [{'chapter': 15, 'verse': 1, 'text': 'الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ وَقُرۡءَانٖ مُّبِينٖ\nАлиф. Лаам. Ро. Ушбулар китобнинг ва равшан Қуръоннинг оятларидир'}, {'chapter': 15, 'verse': 2, 'text': 'رُّبَمَا يَوَدُّ ٱلَّذِينَ كَفَرُواْ لَوۡ كَانُواْ مُسۡلِمِينَ\nҲали, куфр келтирганлар мусулмон бўлишни ҳам истаб қолурлар'}, {'chapter': 15, 'verse': 3, 'text': 'ذَرۡهُمۡ يَأۡكُلُواْ وَيَتَمَتَّعُواْ وَيُلۡهِهِمُ ٱلۡأَمَلُۖ فَسَوۡفَ يَعۡلَمُونَ\nУларни тек қўй, еб-ичиб, ҳузурланиб, умидлар ила машғул бўлиб юраверсинлар. Бас, тезда билурлар'}, {'chapter': 15, 'verse': 4, 'text': 'وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا وَلَهَا كِتَابٞ مَّعۡلُومٞ\nБиз маълум битиксиз ҳеч бир шаҳарни ҳалок этганимиз йўқ'}, {'chapter': 15, 'verse': 5, 'text': 'مَّا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ\nҲеч бир уммат ўз ажалидан ўзиб кета олмас ва ортда ҳам қола олмас'}, {'chapter': 15, 'verse': 6, 'text': 'وَقَالُواْ يَـٰٓأَيُّهَا ٱلَّذِي نُزِّلَ عَلَيۡهِ ٱلذِّكۡرُ إِنَّكَ لَمَجۡنُونٞ\nУлар: «Эй, зикр нозил қилинган киши, албатта, сен мажнунсан'}, {'chapter': 15, 'verse': 7, 'text': 'لَّوۡمَا تَأۡتِينَا بِٱلۡمَلَـٰٓئِكَةِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nАгар ростгўйлардан бўлсанг, бизга фаришталарни келтирсанг-чи!» дерлар'}, {'chapter': 15, 'verse': 8, 'text': 'مَا نُنَزِّلُ ٱلۡمَلَـٰٓئِكَةَ إِلَّا بِٱلۡحَقِّ وَمَا كَانُوٓاْ إِذٗا مُّنظَرِينَ\nБиз фаришталарни фақат ҳақ ила туширамиз, холос. У ҳолда уларга қараб турилмас'}, {'chapter': 15, 'verse': 9, 'text': 'إِنَّا نَحۡنُ نَزَّلۡنَا ٱلذِّكۡرَ وَإِنَّا لَهُۥ لَحَٰفِظُونَ\nАлбатта, Зикрни Биз нозил қилдик ва, албатта, уни Биз муҳофаза қилурмиз'}, {'chapter': 15, 'verse': 10, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي شِيَعِ ٱلۡأَوَّلِينَ\nБатаҳқиқ, сендан олдин ҳам аввалгилар гуруҳларига (Пайғамбар) юборганмиз'}, {'chapter': 15, 'verse': 11, 'text': 'وَمَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУларга қайси бир Пайғамбар келса, уни масхара қилмасдан қўймас эдилар'}, {'chapter': 15, 'verse': 12, 'text': 'كَذَٰلِكَ نَسۡلُكُهُۥ فِي قُلُوبِ ٱلۡمُجۡرِمِينَ\nШунга ўхшаш, Биз уни жиноятчилар қалбига соламиз'}, {'chapter': 15, 'verse': 13, 'text': 'لَا يُؤۡمِنُونَ بِهِۦ وَقَدۡ خَلَتۡ سُنَّةُ ٱلۡأَوَّلِينَ\nУ(Қуръон)га иймон келтирмаслар. Батаҳқиқ, аввалгиларнинг суннати — одати шундоқ ўтгандир'}, {'chapter': 15, 'verse': 14, 'text': 'وَلَوۡ فَتَحۡنَا عَلَيۡهِم بَابٗا مِّنَ ٱلسَّمَآءِ فَظَلُّواْ فِيهِ يَعۡرُجُونَ\nАгар уларга осмондан бир эшик очсаг-у, ундан чиқиб турсалар ҳам'}, {'chapter': 15, 'verse': 15, 'text': 'لَقَالُوٓاْ إِنَّمَا سُكِّرَتۡ أَبۡصَٰرُنَا بَلۡ نَحۡنُ قَوۡمٞ مَّسۡحُورُونَ\nУлар: «Албатта, кўзларимиз боғланди, балки биз сеҳрланган қавмдирмиз», дерлар'}, {'chapter': 15, 'verse': 16, 'text': 'وَلَقَدۡ جَعَلۡنَا فِي ٱلسَّمَآءِ بُرُوجٗا وَزَيَّنَّـٰهَا لِلنَّـٰظِرِينَ\nБатаҳқиқ, Биз осмонларда буржлар қилиб қўйдик ва уни назар солувчилар учун зийнатлаб қўйдик'}, {'chapter': 15, 'verse': 17, 'text': 'وَحَفِظۡنَٰهَا مِن كُلِّ شَيۡطَٰنٖ رَّجِيمٍ\nУни ҳар бир қувилган шайтондан муҳофаза қилдик'}, {'chapter': 15, 'verse': 18, 'text': 'إِلَّا مَنِ ٱسۡتَرَقَ ٱلسَّمۡعَ فَأَتۡبَعَهُۥ شِهَابٞ مُّبِينٞ\nМагар бирортаси ўғриликча қулоқ осса, равшан олов унинг ортидан тушар'}, {'chapter': 15, 'verse': 19, 'text': 'وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ شَيۡءٖ مَّوۡزُونٖ\nБиз ерни ёйиб, устига баланд тоғларни ташлаб қўйдик ва унда ўлчанган турли нарсаларни ўстириб қўйдик'}, {'chapter': 15, 'verse': 20, 'text': 'وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَ وَمَن لَّسۡتُمۡ لَهُۥ بِرَٰزِقِينَ\nВа унда сизлар учун маош воситаларини ва сиз ризқини бера олмайдиган кимсаларни қилиб қўйдик'}, {'chapter': 15, 'verse': 21, 'text': 'وَإِن مِّن شَيۡءٍ إِلَّا عِندَنَا خَزَآئِنُهُۥ وَمَا نُنَزِّلُهُۥٓ إِلَّا بِقَدَرٖ مَّعۡلُومٖ\nҲеч бир нарса йўқки, унинг хазиналари Бизнинг ҳузуримизда бўлмаса. Биз уни фақат маълум миқдор-ла нозил қилурмиз'}, {'chapter': 15, 'verse': 22, 'text': 'وَأَرۡسَلۡنَا ٱلرِّيَٰحَ لَوَٰقِحَ فَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ فَأَسۡقَيۡنَٰكُمُوهُ وَمَآ أَنتُمۡ لَهُۥ بِخَٰزِنِينَ\nБиз шамолларни урчитувчилар этиб юбордик. Бас, осмондан сув тушириб, у ила сизларни сероб қилдик. Сизлар уни тўплаб олувчи эмассиз'}, {'chapter': 15, 'verse': 23, 'text': 'وَإِنَّا لَنَحۡنُ نُحۡيِۦ وَنُمِيتُ وَنَحۡنُ ٱلۡوَٰرِثُونَ\nАлбатта, Биз ўзимиз тирилтирурмиз ва Биз меросхўрмиз'}, {'chapter': 15, 'verse': 24, 'text': 'وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَقۡدِمِينَ مِنكُمۡ وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَـٔۡخِرِينَ\nБатаҳқиқ, сиздан олдин келганларни билдик. Батаҳқиқ, кейин келганларни ҳам билдик'}, {'chapter': 15, 'verse': 25, 'text': 'وَإِنَّ رَبَّكَ هُوَ يَحۡشُرُهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ\nАлбатта, сенинг Роббинг Ўзи уларни маҳшарга жамлар. Албатта, У ҳикматли ва илмли Зотдир'}, {'chapter': 15, 'verse': 26, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ مِن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ\nБатаҳқиқ, Биз инсонни қуруқ лойдан, ўзгартирилган қора балчиқдан яратдик'}, {'chapter': 15, 'verse': 27, 'text': 'وَٱلۡجَآنَّ خَلَقۡنَٰهُ مِن قَبۡلُ مِن نَّارِ ٱلسَّمُومِ\nВа жинларни бундан олдин самум оловидан яратдик'}, {'chapter': 15, 'verse': 28, 'text': 'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ\nСен Роббингнинг фаришталарга: «Албатта, мен қуруқ лойдан, ўзгартирилган қора балчиқдан башар яратгувчидирман'}, {'chapter': 15, 'verse': 29, 'text': 'فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ\nБас, қачонки уни тўғрилаб бўлганимда ва ичига Ўз руҳимдан пуфлаганимда, унга сажда қилган ҳолингизда йиқилинг», деганини эсла'}, {'chapter': 15, 'verse': 30, 'text': 'فَسَجَدَ ٱلۡمَلَـٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ\nБас, фаришталар барчалари сажда қилдилар'}, {'chapter': 15, 'verse': 31, 'text': 'إِلَّآ إِبۡلِيسَ أَبَىٰٓ أَن يَكُونَ مَعَ ٱلسَّـٰجِدِينَ\nМагар иблис сажда қилувчилар билан бирга бўлишдан бош тортди'}, {'chapter': 15, 'verse': 32, 'text': 'قَالَ يَـٰٓإِبۡلِيسُ مَا لَكَ أَلَّا تَكُونَ مَعَ ٱلسَّـٰجِدِينَ\nУ зот: «Эй, иблис! Сенга не бўлди, сажда қилувчилар билан бирга бўлмадинг?» деди'}, {'chapter': 15, 'verse': 33, 'text': 'قَالَ لَمۡ أَكُن لِّأَسۡجُدَ لِبَشَرٍ خَلَقۡتَهُۥ مِن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ\nУ: «Сен қуруқ лойдан, ўзгартирилган қора балчиқдан яратган башарга сажда қила олмасдим», деди'}, {'chapter': 15, 'verse': 34, 'text': 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ\nУ зот: «Бас, сен ундан чиқ! Албатта, сен қувилгансан'}, {'chapter': 15, 'verse': 35, 'text': 'وَإِنَّ عَلَيۡكَ ٱللَّعۡنَةَ إِلَىٰ يَوۡمِ ٱلدِّينِ\nВа, албатта, сенга то жазо кунигача лаънат бўлур!» деди'}, {'chapter': 15, 'verse': 36, 'text': 'قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nУ: «Эй Роббим, мени улар қайта тириладиган кунгача қўйиб қўйгин», деди'}, {'chapter': 15, 'verse': 37, 'text': 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ\nУ зот: «Албатта, сен қўйиб қўйилганлардансан'}, {'chapter': 15, 'verse': 38, 'text': 'إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ\nМаълум вақт кунигача», деди'}, {'chapter': 15, 'verse': 39, 'text': 'قَالَ رَبِّ بِمَآ أَغۡوَيۡتَنِي لَأُزَيِّنَنَّ لَهُمۡ فِي ٱلۡأَرۡضِ وَلَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ\nУ: «Эй Роббим, мени залолатга кетказганинг сабабидан, албатта, мен уларга ер юзида (гуноҳларни) зийнатлаб кўрсатаман ва, албатта, уларнинг ҳаммасини иғво қиламан'}, {'chapter': 15, 'verse': 40, 'text': 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ\nМагар, улардан танланган бандаларингни (адаштира олмасман)», деди'}, {'chapter': 15, 'verse': 41, 'text': 'قَالَ هَٰذَا صِرَٰطٌ عَلَيَّ مُسۡتَقِيمٌ\nУ зот: «Ушбу зиммамдаги тўғри йўлдир'}, {'chapter': 15, 'verse': 42, 'text': 'إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٌ إِلَّا مَنِ ٱتَّبَعَكَ مِنَ ٱلۡغَاوِينَ\nАлбатта, Менинг бандаларим устида сенинг султонинг йўқдир, магар сенга эргашган гумроҳларгагина бордир'}, {'chapter': 15, 'verse': 43, 'text': 'وَإِنَّ جَهَنَّمَ لَمَوۡعِدُهُمۡ أَجۡمَعِينَ\nАлбатта, жаҳаннам уларнинг барчасининг ваъда қилинган жойидир'}, {'chapter': 15, 'verse': 44, 'text': 'لَهَا سَبۡعَةُ أَبۡوَٰبٖ لِّكُلِّ بَابٖ مِّنۡهُمۡ جُزۡءٞ مَّقۡسُومٌ\nУнинг етти эшиги бордир. Ҳар бир эшик учун улардан тақсимланган бўлаги бордир», деди'}, {'chapter': 15, 'verse': 45, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَعُيُونٍ\nАлбатта, тақводорлар жаннатлар ва чашмалардадир'}, {'chapter': 15, 'verse': 46, 'text': 'ٱدۡخُلُوهَا بِسَلَٰمٍ ءَامِنِينَ\nУларга тинчлик, омонлик ила киринглар, (дейилур)'}, {'chapter': 15, 'verse': 47, 'text': 'وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلٍّ إِخۡوَٰنًا عَلَىٰ سُرُرٖ مُّتَقَٰبِلِينَ\nУларнинг дилларидаги ғилли-ғиштларни чиқариб ташладик. Сўриларда бир-бирларига боқиб, дўст бўлган ҳолларида ўлтирурлар'}, {'chapter': 15, 'verse': 48, 'text': 'لَا يَمَسُّهُمۡ فِيهَا نَصَبٞ وَمَا هُم مِّنۡهَا بِمُخۡرَجِينَ\nУларни у жойда чарчоқ тутмас ва улар ундан чиқарилувчи ҳам эмаслар'}, {'chapter': 15, 'verse': 49, 'text': '۞نَبِّئۡ عِبَادِيٓ أَنِّيٓ أَنَا ٱلۡغَفُورُ ٱلرَّحِيمُ\nБандаларимга хабар бергил: «Албатта, Менинг Ўзим ғофуру роҳиймдирман»'}, {'chapter': 15, 'verse': 50, 'text': 'وَأَنَّ عَذَابِي هُوَ ٱلۡعَذَابُ ٱلۡأَلِيمُ\nВа, албатта, азобим аламли азобдир'}, {'chapter': 15, 'verse': 51, 'text': 'وَنَبِّئۡهُمۡ عَن ضَيۡفِ إِبۡرَٰهِيمَ\nУларга Иброҳимнинг меҳмонлари хабарини бер'}, {'chapter': 15, 'verse': 52, 'text': 'إِذۡ دَخَلُواْ عَلَيۡهِ فَقَالُواْ سَلَٰمٗا قَالَ إِنَّا مِنكُمۡ وَجِلُونَ\nУлар унинг ҳузурига кирганларида: «Салом», дедилар. У: «Албатта, биз сизлардан қўрқмоқдамиз», деди'}, {'chapter': 15, 'verse': 53, 'text': 'قَالُواْ لَا تَوۡجَلۡ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ عَلِيمٖ\nУлар: «Қўрқма, биз сенга ўта билимдон ўғилнинг хушхабарини берамиз», дедилар'}, {'chapter': 15, 'verse': 54, 'text': 'قَالَ أَبَشَّرۡتُمُونِي عَلَىٰٓ أَن مَّسَّنِيَ ٱلۡكِبَرُ فَبِمَ تُبَشِّرُونَ\nУ: «Мени кексалик тутганида, хушхабар берасизларми? Ниманинг хушхабарини беряпсизлар, ўзи?» деди'}, {'chapter': 15, 'verse': 55, 'text': 'قَالُواْ بَشَّرۡنَٰكَ بِٱلۡحَقِّ فَلَا تَكُن مِّنَ ٱلۡقَٰنِطِينَ\nУлар: «Биз сенга ҳақ ила башорат бердик. Бас, сен ноумид бўлувчилардан бўлма», дедилар'}, {'chapter': 15, 'verse': 56, 'text': 'قَالَ وَمَن يَقۡنَطُ مِن رَّحۡمَةِ رَبِّهِۦٓ إِلَّا ٱلضَّآلُّونَ\nУ: «Фақат залолатга кетган кишиларгина ўз Роббисининг раҳматидан ноумид бўлурлар», деди'}, {'chapter': 15, 'verse': 57, 'text': 'قَالَ فَمَا خَطۡبُكُمۡ أَيُّهَا ٱلۡمُرۡسَلُونَ\nУ: «Эй элчилар, нима иш ила юборилдингиз?» деди'}, {'chapter': 15, 'verse': 58, 'text': 'قَالُوٓاْ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمٖ مُّجۡرِمِينَ\nУлар: «Биз жиноятчи қавмларга юборилдик'}, {'chapter': 15, 'verse': 59, 'text': 'إِلَّآ ءَالَ لُوطٍ إِنَّا لَمُنَجُّوهُمۡ أَجۡمَعِينَ\nМагар Лут аҳлига эмас. Албатта, биз уларнинг ҳаммасини қутқаргувчилармиз'}, {'chapter': 15, 'verse': 60, 'text': 'إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَآ إِنَّهَا لَمِنَ ٱلۡغَٰبِرِينَ\nФақат унинг хотинигина истиснодир. Унинг албатта қолувчилардан бўлишини тақдир қилдик», дедилар'}, {'chapter': 15, 'verse': 61, 'text': 'فَلَمَّا جَآءَ ءَالَ لُوطٍ ٱلۡمُرۡسَلُونَ\nЭлчилар Лут аҳлига келганларида'}, {'chapter': 15, 'verse': 62, 'text': 'قَالَ إِنَّكُمۡ قَوۡمٞ مُّنكَرُونَ\nУ: «Албатта, сиз нотаниш қавмсиз», деди'}, {'chapter': 15, 'verse': 63, 'text': 'قَالُواْ بَلۡ جِئۡنَٰكَ بِمَا كَانُواْ فِيهِ يَمۡتَرُونَ\nУлар: «Йўқ! Биз сенга улар шак қилаётган нарса ила келдик'}, {'chapter': 15, 'verse': 64, 'text': 'وَأَتَيۡنَٰكَ بِٱلۡحَقِّ وَإِنَّا لَصَٰدِقُونَ\nСенга ҳақ ила келдик ва албатта, бизлар ростгўйлардурмиз'}, {'chapter': 15, 'verse': 65, 'text': 'فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ ٱلَّيۡلِ وَٱتَّبِعۡ أَدۡبَٰرَهُمۡ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٞ وَٱمۡضُواْ حَيۡثُ تُؤۡمَرُونَ\nАҳлинг ила кечанинг бир бўлагида йўлга чиқ, сен уларнинг ортидан кузатиб бор. Сизлардан ҳеч бирларингиз ортга қарамасин. Амр қилинган томонингизга кетаверинглар», дедилар'}, {'chapter': 15, 'verse': 66, 'text': 'وَقَضَيۡنَآ إِلَيۡهِ ذَٰلِكَ ٱلۡأَمۡرَ أَنَّ دَابِرَ هَـٰٓؤُلَآءِ مَقۡطُوعٞ مُّصۡبِحِينَ\nВа унга ушбу амрни ваҳий қилдик: «Албатта, анавиларнинг орқаси субҳ чоғи кесилажакдир»'}, {'chapter': 15, 'verse': 67, 'text': 'وَجَآءَ أَهۡلُ ٱلۡمَدِينَةِ يَسۡتَبۡشِرُونَ\nВа шаҳар аҳли севиниб, етиб келдилар'}, {'chapter': 15, 'verse': 68, 'text': 'قَالَ إِنَّ هَـٰٓؤُلَآءِ ضَيۡفِي فَلَا تَفۡضَحُونِ\nУ: «Булар менинг меҳмонларим, ахир. Мени шарманда қилманглар», деди'}, {'chapter': 15, 'verse': 69, 'text': 'وَٱتَّقُواْ ٱللَّهَ وَلَا تُخۡزُونِ\nВа: «Аллоҳдан қўрқинглар! Мени хор қилманглар», деди'}, {'chapter': 15, 'verse': 70, 'text': 'قَالُوٓاْ أَوَلَمۡ نَنۡهَكَ عَنِ ٱلۡعَٰلَمِينَ\nУлар: «Биз сени оламлардан қайтармаганмидик?!» дедилар'}, {'chapter': 15, 'verse': 71, 'text': 'قَالَ هَـٰٓؤُلَآءِ بَنَاتِيٓ إِن كُنتُمۡ فَٰعِلِينَ\nУ: «Агар (бу ишни) қилувчи бўлсангиз, анаву қизларим бор», деди'}, {'chapter': 15, 'verse': 72, 'text': 'لَعَمۡرُكَ إِنَّهُمۡ لَفِي سَكۡرَتِهِمۡ يَعۡمَهُونَ\nСенинг умринг ила қасамки, албатта, улар ўз сархушликларида тентирамоқдалар'}, {'chapter': 15, 'verse': 73, 'text': 'فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ مُشۡرِقِينَ\nБас, уларни қуёш чиқиш пайтида даҳшатли қичқириқ тутди'}, {'chapter': 15, 'verse': 74, 'text': 'فَجَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهِمۡ حِجَارَةٗ مِّن سِجِّيلٍ\nБас, Биз бу(шаҳар)нинг остини устин қилдик ва улар устига лойи қотган тошлар ёғдирдик'}, {'chapter': 15, 'verse': 75, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡمُتَوَسِّمِينَ\nАлбатта, бунда фаросатли кишилар учун оят-белгилар бордир'}, {'chapter': 15, 'verse': 76, 'text': 'وَإِنَّهَا لَبِسَبِيلٖ مُّقِيمٍ\nАлбатта, у (шаҳар) боқий йўл устидадир'}, {'chapter': 15, 'verse': 77, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ\nАлбатта, бунда мўминлар учун ибрат бордир'}, {'chapter': 15, 'verse': 78, 'text': 'وَإِن كَانَ أَصۡحَٰبُ ٱلۡأَيۡكَةِ لَظَٰلِمِينَ\nАлбатта, бир-бирига киришиб кетган дарахтзор эгалари золимлардан бўлганлар'}, {'chapter': 15, 'verse': 79, 'text': 'فَٱنتَقَمۡنَا مِنۡهُمۡ وَإِنَّهُمَا لَبِإِمَامٖ مُّبِينٖ\nБас, Биз улардан интиқом олдик. Албатта, уларнинг икковлари ҳам очиқ йўл устидадир'}, {'chapter': 15, 'verse': 80, 'text': 'وَلَقَدۡ كَذَّبَ أَصۡحَٰبُ ٱلۡحِجۡرِ ٱلۡمُرۡسَلِينَ\nБатаҳқиқ, Ҳижр эгалари Пайғамбарларни ёлғончига чиқардилар'}, {'chapter': 15, 'verse': 81, 'text': 'وَءَاتَيۡنَٰهُمۡ ءَايَٰتِنَا فَكَانُواْ عَنۡهَا مُعۡرِضِينَ\nВа уларга оятларимизни бердик. Бас, юз ўгирувчи бўлдилар'}, {'chapter': 15, 'verse': 82, 'text': 'وَكَانُواْ يَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُيُوتًا ءَامِنِينَ\nУлар тоғдан ўйиб уй ясаб, омонликда юрар эдилар'}, {'chapter': 15, 'verse': 83, 'text': 'فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ مُصۡبِحِينَ\nБас, субҳидам чоғида уларни қаттиқ қичқириқ тутди'}, {'chapter': 15, 'verse': 84, 'text': 'فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ\nҚилган касблари уларга бирон фойда бермади'}, {'chapter': 15, 'verse': 85, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّۗ وَإِنَّ ٱلسَّاعَةَ لَأٓتِيَةٞۖ فَٱصۡفَحِ ٱلصَّفۡحَ ٱلۡجَمِيلَ\nБиз осмонлару ерни ва улардаги нарсаларни ҳақ ила яратдик. Албатта, қиёмат келгувчидир. Бас, сен гўзал шаклда юз ўгир'}, {'chapter': 15, 'verse': 86, 'text': 'إِنَّ رَبَّكَ هُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِيمُ\nАлбатта, Роббинг яратгувчи ва билгувчи Зотдир'}, {'chapter': 15, 'verse': 87, 'text': 'وَلَقَدۡ ءَاتَيۡنَٰكَ سَبۡعٗا مِّنَ ٱلۡمَثَانِي وَٱلۡقُرۡءَانَ ٱلۡعَظِيمَ\nВа, батаҳқиқ, сенга етти такрорланувчини ва Улуғ Қуръонни бердик'}, {'chapter': 15, 'verse': 88, 'text': 'لَا تَمُدَّنَّ عَيۡنَيۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّنۡهُمۡ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَٱخۡفِضۡ جَنَاحَكَ لِلۡمُؤۡمِنِينَ\nБиз улардан баъзи гуруҳларни баҳраманд қилган нарсаларга (ҳавас-ла) назар солма! Улар учун хафа ҳам бўлма! Мўминларга қанотингни пастлат'}, {'chapter': 15, 'verse': 89, 'text': 'وَقُلۡ إِنِّيٓ أَنَا ٱلنَّذِيرُ ٱلۡمُبِينُ\nВа: «Албатта, мен очиқ-ойдин огоҳлантиргувчиман», деб айт'}, {'chapter': 15, 'verse': 90, 'text': 'كَمَآ أَنزَلۡنَا عَلَى ٱلۡمُقۡتَسِمِينَ\nХудди тақсимловчиларга туширганимизга ўхшаш'}, {'chapter': 15, 'verse': 91, 'text': 'ٱلَّذِينَ جَعَلُواْ ٱلۡقُرۡءَانَ عِضِينَ\nҚуръонни парча-парча қилганларга'}, {'chapter': 15, 'verse': 92, 'text': 'فَوَرَبِّكَ لَنَسۡـَٔلَنَّهُمۡ أَجۡمَعِينَ\nБас, Роббинг ила қасамки, албатта, уларнинг ҳаммасидан сўрармиз'}, {'chapter': 15, 'verse': 93, 'text': 'عَمَّا كَانُواْ يَعۡمَلُونَ\nҚилиб юрган ишлари ҳақида'}, {'chapter': 15, 'verse': 94, 'text': 'فَٱصۡدَعۡ بِمَا تُؤۡمَرُ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ\nБас, сенга амр этилган нарсани юзага чиқар ва мушриклардан юз ўгир'}, {'chapter': 15, 'verse': 95, 'text': 'إِنَّا كَفَيۡنَٰكَ ٱلۡمُسۡتَهۡزِءِينَ\nАлбатта, Биз сенга истеҳзо қилувчилардан кифоя қилурмиз'}, {'chapter': 15, 'verse': 96, 'text': 'ٱلَّذِينَ يَجۡعَلُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۚ فَسَوۡفَ يَعۡلَمُونَ\nУлар Аллоҳ билан бирга бошқа илоҳ (шерик) қилурлар. Бас, тезда билурлар'}, {'chapter': 15, 'verse': 97, 'text': 'وَلَقَدۡ نَعۡلَمُ أَنَّكَ يَضِيقُ صَدۡرُكَ بِمَا يَقُولُونَ\nАлбатта, Биз уларнинг айтаётганларидан сенинг дилинг сиқилишини яхши билурмиз'}, {'chapter': 15, 'verse': 98, 'text': 'فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَكُن مِّنَ ٱلسَّـٰجِدِينَ\nБас, Роббинг ҳамди ила тасбиҳ айт ва сажда қилгувчилардан бўл'}, {'chapter': 15, 'verse': 99, 'text': 'وَٱعۡبُدۡ رَبَّكَ حَتَّىٰ يَأۡتِيَكَ ٱلۡيَقِينُ\nВа сенга аниқ нарса (ўлим) келгунича Роббингга ибодат қил'}], '16': [{'chapter': 16, 'verse': 1, 'text': 'أَتَىٰٓ أَمۡرُ ٱللَّهِ فَلَا تَسۡتَعۡجِلُوهُۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nАллоҳнинг амри келди. Бас, унинг тезроқ келишини талаб қилмай қўя қолинг. У зот улар келтираётган ширклардан пок ва олий бўлди'}, {'chapter': 16, 'verse': 2, 'text': 'يُنَزِّلُ ٱلۡمَلَـٰٓئِكَةَ بِٱلرُّوحِ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ أَنۡ أَنذِرُوٓاْ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱتَّقُونِ\nУ Ўз амри ила фаришталарни руҳ — ваҳий билан бандаларидан Ўзи хоҳлаганларига: «Огоҳлантиринглар. Албатта, Мендан ўзга илоҳ йўқ. Бас, Менга тақво қилинглар», деб нозил қилур'}, {'chapter': 16, 'verse': 3, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ تَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУ осмонлару ерни ҳақ ила яратди. У улар ширк келтираётган нарсалардан олий бўлди'}, {'chapter': 16, 'verse': 4, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ مِن نُّطۡفَةٖ فَإِذَا هُوَ خَصِيمٞ مُّبِينٞ\nУ инсонни нутфадан яратди. Энди эса, у (инсон) очиқ-ойдин хусуматчи бўлиб турибди'}, {'chapter': 16, 'verse': 5, 'text': 'وَٱلۡأَنۡعَٰمَ خَلَقَهَاۖ لَكُمۡ فِيهَا دِفۡءٞ وَمَنَٰفِعُ وَمِنۡهَا تَأۡكُلُونَ\nУ чорва ҳайвонларини ҳам яратди. Уларда сиз учун иссиқлик, манфаатлар бор ва улардан ейсизлар ҳам'}, {'chapter': 16, 'verse': 6, 'text': 'وَلَكُمۡ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسۡرَحُونَ\nВа сиз учун уларда кечки пайт (ўтлоқдан) қайтаётганларида ва эрталаб (ўтлоққа) кетаётганида жамол бор'}, {'chapter': 16, 'verse': 7, 'text': 'وَتَحۡمِلُ أَثۡقَالَكُمۡ إِلَىٰ بَلَدٖ لَّمۡ تَكُونُواْ بَٰلِغِيهِ إِلَّا بِشِقِّ ٱلۡأَنفُسِۚ إِنَّ رَبَّكُمۡ لَرَءُوفٞ رَّحِيمٞ\nВа улар юкларингизни сиз жон машаққатисиз ета олмайдиган юртларгача кўтариб боради. Албатта, Роббингиз меҳрибон ва раҳимлидир'}, {'chapter': 16, 'verse': 8, 'text': 'وَٱلۡخَيۡلَ وَٱلۡبِغَالَ وَٱلۡحَمِيرَ لِتَرۡكَبُوهَا وَزِينَةٗۚ وَيَخۡلُقُ مَا لَا تَعۡلَمُونَ\nОтлар, хачирлар ва эшакларни минишингиз ва зийнат учун (яратди) ва сиз билмайдиган нарсаларни яратур'}, {'chapter': 16, 'verse': 9, 'text': 'وَعَلَى ٱللَّهِ قَصۡدُ ٱلسَّبِيلِ وَمِنۡهَا جَآئِرٞۚ وَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ\nЙўлларнинг мақсаддагиси Аллоҳга оиддир. Улардан эгриси ҳам бордир. Агар хоҳласа, албатта, ҳаммангизни ҳидоят қилган бўларди'}, {'chapter': 16, 'verse': 10, 'text': 'هُوَ ٱلَّذِيٓ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗۖ لَّكُم مِّنۡهُ شَرَابٞ وَمِنۡهُ شَجَرٞ فِيهِ تُسِيمُونَ\nУ осмондан сувни туширган Зотдир. Сизга унда шароб ва (чорваларингизни) ўтлатадиган дарахт бордир'}, {'chapter': 16, 'verse': 11, 'text': 'يُنۢبِتُ لَكُم بِهِ ٱلزَّرۡعَ وَٱلزَّيۡتُونَ وَٱلنَّخِيلَ وَٱلۡأَعۡنَٰبَ وَمِن كُلِّ ٱلثَّمَرَٰتِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَتَفَكَّرُونَ\nУ ўша (сув) ила сизга экинларни, зайтунни, хурмоларни, узумларни ва турли меваларни ўстирур. Албатта, бунда тафаккур қиладиган қавмлар учун оят-белгилар бордир'}, {'chapter': 16, 'verse': 12, 'text': 'وَسَخَّرَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ وَٱلنُّجُومُ مُسَخَّرَٰتُۢ بِأَمۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nУ сизга кечанию кундузни, қуёшнию ойни хизматкор қилди. Юлдузлар Унинг амри ила хизматкордир. Албатта, бунда ақл юритгувчи қавмлар учун оят-белгилар бордир'}, {'chapter': 16, 'verse': 13, 'text': 'وَمَا ذَرَأَ لَكُمۡ فِي ٱلۡأَرۡضِ مُخۡتَلِفًا أَلۡوَٰنُهُۥٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَذَّكَّرُونَ\nВа сиз учун ерда турли рангда қилиб яратган нарсаларини ҳам (хизматкор қилди). Албатта, бунда эслайдиган қавмлар учун оят-белги бордир'}, {'chapter': 16, 'verse': 14, 'text': 'وَهُوَ ٱلَّذِي سَخَّرَ ٱلۡبَحۡرَ لِتَأۡكُلُواْ مِنۡهُ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُواْ مِنۡهُ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ مَوَاخِرَ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nУ денгизни сиз ундан тоза гўшт ейишингиз, ундан тақадиган тақинчоқлар чиқариб олишингиз учун, унда (сувни) ёриб кетаётган кемаларни кўрасан, ҳамда Унинг фазлини талаб қилишингиз учун хизматкор қилди. Шоядки, шукр келтирсангиз'}, {'chapter': 16, 'verse': 15, 'text': 'وَأَلۡقَىٰ فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَأَنۡهَٰرٗا وَسُبُلٗا لَّعَلَّكُمۡ تَهۡتَدُونَ\nУ зот ер сизларни тебратиб юбормаслиги учун унинг устига баланд тоғларни ташлаб қўйди. Анҳорлару йўлларни ҳам. Шоядки, тўғри йўл топсангиз'}, {'chapter': 16, 'verse': 16, 'text': 'وَعَلَٰمَٰتٖۚ وَبِٱلنَّجۡمِ هُمۡ يَهۡتَدُونَ\nВа аломатлар ҳамда юлдузлар ила улар йўл топурлар'}, {'chapter': 16, 'verse': 17, 'text': 'أَفَمَن يَخۡلُقُ كَمَن لَّا يَخۡلُقُۚ أَفَلَا تَذَكَّرُونَ\nЯратадиган Зот яратмайдиган зот каби бўлурми? Ўйлаб кўрмайсизларми'}, {'chapter': 16, 'verse': 18, 'text': 'وَإِن تَعُدُّواْ نِعۡمَةَ ٱللَّهِ لَا تُحۡصُوهَآۗ إِنَّ ٱللَّهَ لَغَفُورٞ رَّحِيمٞ\nАгар Аллоҳнинг неъматларини санасангизлар, саноғига ета олмассизлар. Албатта, Аллоҳ ўта мағфиратли ва раҳимли Зотдир'}, {'chapter': 16, 'verse': 19, 'text': 'وَٱللَّهُ يَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَ\nАллоҳ нимани сир тутаётганингизни ва нимани ошкор қилаётганингизни биладир'}, {'chapter': 16, 'verse': 20, 'text': 'وَٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ لَا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ\nУларнинг Аллоҳдан ўзга ибодат қилаётганлари ҳеч нарсани ярата олмаслар. Ҳолбуки, ўзлари яратилурлар'}, {'chapter': 16, 'verse': 21, 'text': 'أَمۡوَٰتٌ غَيۡرُ أَحۡيَآءٖۖ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ\nЎликлардир, тирик эмаслар ва қачон қайта тирилтирилишларини билмаслар'}, {'chapter': 16, 'verse': 22, 'text': 'إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۚ فَٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ قُلُوبُهُم مُّنكِرَةٞ وَهُم مُّسۡتَكۡبِرُونَ\nИлоҳингиз ягона илоҳдир. Охиратга иймон келтирмайдиганларнинг қалблари инкор қилувчидир, ўзлари эса мутакаббирдирлар'}, {'chapter': 16, 'verse': 23, 'text': 'لَا جَرَمَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡتَكۡبِرِينَ\nШубҳасизки, Аллоҳ нимани сир тутаётганларини ва нимани ошкор қилаётганларини биладир. Албатта, У мутакаббирларни хуш кўрмас'}, {'chapter': 16, 'verse': 24, 'text': 'وَإِذَا قِيلَ لَهُم مَّاذَآ أَنزَلَ رَبُّكُمۡ قَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nҚачонки, уларга: «Роббингиз нимани нозил қилди?» дейилса, улар: «Аввалгиларнинг афсоналарини», дерлар'}, {'chapter': 16, 'verse': 25, 'text': 'لِيَحۡمِلُوٓاْ أَوۡزَارَهُمۡ كَامِلَةٗ يَوۡمَ ٱلۡقِيَٰمَةِ وَمِنۡ أَوۡزَارِ ٱلَّذِينَ يُضِلُّونَهُم بِغَيۡرِ عِلۡمٍۗ أَلَا سَآءَ مَا يَزِرُونَ\nҚиёмат куни ўз гуноҳларини тўлалигича ва жаҳолат ила йўлдан адаштирганларининг гуноҳларидан кўтаришлари учундир. Огоҳ бўлинг! Улар кўтарган нарса қандоқ ҳам ёмон'}, {'chapter': 16, 'verse': 26, 'text': 'قَدۡ مَكَرَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَأَتَى ٱللَّهُ بُنۡيَٰنَهُم مِّنَ ٱلۡقَوَاعِدِ فَخَرَّ عَلَيۡهِمُ ٱلسَّقۡفُ مِن فَوۡقِهِمۡ وَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ\nБатаҳқиқ, улардан олдингилар ҳам макр қилганлар. Бас, Аллоҳ биноларини пойдеворидан йиқитди. Шифт уларнинг устига қулаб тушди. Ва уларга ўзлари сезмаган томондан азоб келди'}, {'chapter': 16, 'verse': 27, 'text': 'ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يُخۡزِيهِمۡ وَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تُشَـٰٓقُّونَ فِيهِمۡۚ قَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ إِنَّ ٱلۡخِزۡيَ ٱلۡيَوۡمَ وَٱلسُّوٓءَ عَلَى ٱلۡكَٰفِرِينَ\nСўнгра қиёмат кунида У зот уларни хор қилур ва: «Сизлар улар ҳақида тортишиб юрган Менинг «шерикларим» қани?» дер. Илм берилганлар: «Бугунги кунда хорлик ва ёмонлик кофирларгадир», дерлар'}, {'chapter': 16, 'verse': 28, 'text': 'ٱلَّذِينَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡۖ فَأَلۡقَوُاْ ٱلسَّلَمَ مَا كُنَّا نَعۡمَلُ مِن سُوٓءِۭۚ بَلَىٰٓۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا كُنتُمۡ تَعۡمَلُونَ\nФаришталар уларнинг жонларини ўзларига зулм қилган ҳолларида олгандир. Улар, ҳеч бир ёмонлик қилмаган эдик, дея таслим бўлурлар. Йўқ! Албатта, Аллоҳ нима амал қилиб юрганингизни билгувчи Зотдир'}, {'chapter': 16, 'verse': 29, 'text': 'فَٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَلَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ\nБас, жаҳаннам эшикларидан, унда абадий қолгувчи бўлган ҳолингизда киринг! Бас, мутакаббирларнинг қайтар жойи қандоқ ҳам ёмон'}, {'chapter': 16, 'verse': 30, 'text': '۞وَقِيلَ لِلَّذِينَ ٱتَّقَوۡاْ مَاذَآ أَنزَلَ رَبُّكُمۡۚ قَالُواْ خَيۡرٗاۗ لِّلَّذِينَ أَحۡسَنُواْ فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٞۚ وَلَدَارُ ٱلۡأٓخِرَةِ خَيۡرٞۚ وَلَنِعۡمَ دَارُ ٱلۡمُتَّقِينَ\nВа тақво қилганларга: «Роббингиз нимани нозил қилди?», дейилди. Улар: «Яхшиликни», дедилар. Чиройли амаллар қилганлар учун бу дунёда чиройли (мукофот) бордир. Албатта, охират диёри яхшироқдир. Роббиларининг диёри нақадар яхши'}, {'chapter': 16, 'verse': 31, 'text': 'جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ لَهُمۡ فِيهَا مَا يَشَآءُونَۚ كَذَٰلِكَ يَجۡزِي ٱللَّهُ ٱلۡمُتَّقِينَ\nУ адн жаннатларидир. Уларга кирганларида остилардан анҳорлар оқиб турар, улар учун у ерда истаган нарсалари бор. Аллоҳ тақводорларни ана шундай мукофотлар'}, {'chapter': 16, 'verse': 32, 'text': 'ٱلَّذِينَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ طَيِّبِينَ يَقُولُونَ سَلَٰمٌ عَلَيۡكُمُ ٱدۡخُلُواْ ٱلۡجَنَّةَ بِمَا كُنتُمۡ تَعۡمَلُونَ\nФаришталар уларнинг жонларини пок ҳолларида ола туриб: «Сизларга салом бўлсин. Қилиб юрган ишларингиз туфайли жаннатга киринг», дерлар'}, {'chapter': 16, 'verse': 33, 'text': 'هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ ٱلۡمَلَـٰٓئِكَةُ أَوۡ يَأۡتِيَ أَمۡرُ رَبِّكَۚ كَذَٰلِكَ فَعَلَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nУлар ўзларига (ўлим) фаришталари ёки Роббингнинг (азоб) амри келишига интизор бўлиб турибдиларми? Улардан олдингилар ҳам шундай қилган эдилар. Аллоҳ уларга зулм қилмади. Лекин улар ўзларига зулм қилган эдилар'}, {'chapter': 16, 'verse': 34, 'text': 'فَأَصَابَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nБас, қилган ёмон амаллари ўзларига етди ва истеҳзо қилган нарсалари ўзларини ўради'}, {'chapter': 16, 'verse': 35, 'text': 'وَقَالَ ٱلَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ ٱللَّهُ مَا عَبَدۡنَا مِن دُونِهِۦ مِن شَيۡءٖ نَّحۡنُ وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن دُونِهِۦ مِن شَيۡءٖۚ كَذَٰلِكَ فَعَلَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ فَهَلۡ عَلَى ٱلرُّسُلِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nШирк келтирганлар: «Агар Аллоҳ хоҳлаганида, биз ҳам, ота-боболаримиз ҳам ундан ўзга ҳеч нарсага ибодат қилмас эдик ҳамда Унинг ҳукмисиз бирон нарсани ҳаром қилмаган бўлар эдик», дедилар. Улардан олдингилар ҳам шундай қилганлар. Пайғамбарлар зиммасида фақат очиқ-ойдин етказиш бор, холос'}, {'chapter': 16, 'verse': 36, 'text': 'وَلَقَدۡ بَعَثۡنَا فِي كُلِّ أُمَّةٖ رَّسُولًا أَنِ ٱعۡبُدُواْ ٱللَّهَ وَٱجۡتَنِبُواْ ٱلطَّـٰغُوتَۖ فَمِنۡهُم مَّنۡ هَدَى ٱللَّهُ وَمِنۡهُم مَّنۡ حَقَّتۡ عَلَيۡهِ ٱلضَّلَٰلَةُۚ فَسِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\nБатаҳқиқ, Биз ҳар бир умматга: «Аллоҳга ибодат қилинг ва тоғутдан четланинг», деб Пайғамбар юборганмиз. Бас, улардан баъзиларини Аллоҳ ҳидоят қилди. Баъзиларига эса, залолат ҳақ бўлди. Бас, ер юзида сайр қилиб юриб, ёлғонга чақирувчиларнинг оқибати қандай бўлганига назар солинг'}, {'chapter': 16, 'verse': 37, 'text': 'إِن تَحۡرِصۡ عَلَىٰ هُدَىٰهُمۡ فَإِنَّ ٱللَّهَ لَا يَهۡدِي مَن يُضِلُّۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nАгар сен уларнинг ҳидоятига ўта қизиқсанг ҳам, Аллоҳ залолатга кетказган кимсаларни ҳидоят қилмас. Уларга нусрат бергувчилардан бўлмас'}, {'chapter': 16, 'verse': 38, 'text': 'وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَا يَبۡعَثُ ٱللَّهُ مَن يَمُوتُۚ بَلَىٰ وَعۡدًا عَلَيۡهِ حَقّٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nУлар жон-жаҳдлари билан: «Аллоҳ ўлганларни қайта тирилтирмас», деб Аллоҳ номи ила қасам ичдилар. Йўқ! Бу, У зотнинг зиммасидаги ҳақ ваъдадир. Лекин одамларнинг кўплари билмаслар'}, {'chapter': 16, 'verse': 39, 'text': 'لِيُبَيِّنَ لَهُمُ ٱلَّذِي يَخۡتَلِفُونَ فِيهِ وَلِيَعۡلَمَ ٱلَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ كَانُواْ كَٰذِبِينَ\nУларга ихтилоф қилиб юрган нарсаларини баён қилиб бериш учун, куфр келтирганлар ўзларининг шубҳасиз ёлғончи эканларини билишлари учун (қайта тирилтирур)'}, {'chapter': 16, 'verse': 40, 'text': 'إِنَّمَا قَوۡلُنَا لِشَيۡءٍ إِذَآ أَرَدۡنَٰهُ أَن نَّقُولَ لَهُۥ كُن فَيَكُونُ\nАгар Биз бир нарсанинг бўлишини ирода қилсак, унга «бўл» демоғимиз кифоя. Бас, ўша нарса бўлур'}, {'chapter': 16, 'verse': 41, 'text': 'وَٱلَّذِينَ هَاجَرُواْ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ظُلِمُواْ لَنُبَوِّئَنَّهُمۡ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَلَأَجۡرُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nЗулмга дучор бўлганларидан сўнг Аллоҳнинг йўлида ҳижрат қилганларни бу дунёда, албатта, гўзал(жой)га жойлаштирамиз. Ва охират ажри каттароқдир, агар биладиган бўлсалар'}, {'chapter': 16, 'verse': 42, 'text': 'ٱلَّذِينَ صَبَرُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nУлар сабр қилган ва Роббиларига таваккал қилганлардир'}, {'chapter': 16, 'verse': 43, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِمۡۖ فَسۡـَٔلُوٓاْ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ\nБиз сендан олдин ҳам фақат эр кишиларни юбориб, уларга ваҳий қилганмиз. Агар билмайдиган бўлсангиз, зикр аҳлларидан сўранглар'}, {'chapter': 16, 'verse': 44, 'text': 'بِٱلۡبَيِّنَٰتِ وَٱلزُّبُرِۗ وَأَنزَلۡنَآ إِلَيۡكَ ٱلذِّكۡرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيۡهِمۡ وَلَعَلَّهُمۡ يَتَفَكَّرُونَ\nОчиқ-ойдин баёнотлар ва китоблар ила (юбордик). Ва сенга одамларга нозил қилинган нарсани ўзларига баён қилиб беришинг учун Зикрни нозил қилдик. Шоядки, тафаккур қилсалар'}, {'chapter': 16, 'verse': 45, 'text': 'أَفَأَمِنَ ٱلَّذِينَ مَكَرُواْ ٱلسَّيِّـَٔاتِ أَن يَخۡسِفَ ٱللَّهُ بِهِمُ ٱلۡأَرۡضَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ\nМакр ила ёмонликлар қилганлар Аллоҳ уларни ерга ютқазиб юборишидан ёки уларга кутмаган томонларидан азоб келиб қолишидан хотиржам бўлдиларми'}, {'chapter': 16, 'verse': 46, 'text': 'أَوۡ يَأۡخُذَهُمۡ فِي تَقَلُّبِهِمۡ فَمَا هُم بِمُعۡجِزِينَ\nЁхуд уларни ҳаракатлари асносида олишидан, улар У зотни ожиз қолдирувчи бўла олмасликларидан (хотиржаммилар)'}, {'chapter': 16, 'verse': 47, 'text': 'أَوۡ يَأۡخُذَهُمۡ عَلَىٰ تَخَوُّفٖ فَإِنَّ رَبَّكُمۡ لَرَءُوفٞ رَّحِيمٌ\nЁки У зот уларни хавфсираб турган ҳолларида олишидан ҳам (хотиржаммилар)? Албатта, Роббингиз меҳрибон ва раҳимлидир'}, {'chapter': 16, 'verse': 48, 'text': 'أَوَلَمۡ يَرَوۡاْ إِلَىٰ مَا خَلَقَ ٱللَّهُ مِن شَيۡءٖ يَتَفَيَّؤُاْ ظِلَٰلُهُۥ عَنِ ٱلۡيَمِينِ وَٱلشَّمَآئِلِ سُجَّدٗا لِّلَّهِ وَهُمۡ دَٰخِرُونَ\nАхир улар Аллоҳ яратган ҳар бир нарсанинг соялари ўнгга, чапга мойил бўлиб, Аллоҳга сажда қилиб ётганини ва улар бўйсунувчи эканини кўрмайдиларми'}, {'chapter': 16, 'verse': 49, 'text': 'وَلِلَّهِۤ يَسۡجُدُۤ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ مِن دَآبَّةٖ وَٱلۡمَلَـٰٓئِكَةُ وَهُمۡ لَا يَسۡتَكۡبِرُونَ\nОсмонлару ердаги барча жонзотлар ва фаришталар фақат Аллоҳга сажда қилурлар. Улар мутакаббирлик қилмаслар'}, {'chapter': 16, 'verse': 50, 'text': 'يَخَافُونَ رَبَّهُم مِّن فَوۡقِهِمۡ وَيَفۡعَلُونَ مَا يُؤۡمَرُونَ۩\nУлар устиларидаги Роббиларидан қўрқурлар ва ўзларига амр қилинган нарсаларни қилурлар'}, {'chapter': 16, 'verse': 51, 'text': '۞وَقَالَ ٱللَّهُ لَا تَتَّخِذُوٓاْ إِلَٰهَيۡنِ ٱثۡنَيۡنِۖ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ فَإِيَّـٰيَ فَٱرۡهَبُونِ\nАллоҳ: «Иккита илоҳ тутмангиз. Ҳақиқатда, У ёлғиз илоҳдир. Бас, Мендангина қўрқингиз», деди'}, {'chapter': 16, 'verse': 52, 'text': 'وَلَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَهُ ٱلدِّينُ وَاصِبًاۚ أَفَغَيۡرَ ٱللَّهِ تَتَّقُونَ\nОсмонлару ердаги нарсалар Уникидир. Дин ҳам бардавом Уникидир. Аллоҳдан ўзгадан қўрқасизларми'}, {'chapter': 16, 'verse': 53, 'text': 'وَمَا بِكُم مِّن نِّعۡمَةٖ فَمِنَ ٱللَّهِۖ ثُمَّ إِذَا مَسَّكُمُ ٱلضُّرُّ فَإِلَيۡهِ تَجۡـَٔرُونَ\nСизда қайсики неъмат бўлса, Аллоҳдандир. Сўнгра, қачонки сизга ёмонлик етса, Унгагина ёлворасиз'}, {'chapter': 16, 'verse': 54, 'text': 'ثُمَّ إِذَا كَشَفَ ٱلضُّرَّ عَنكُمۡ إِذَا فَرِيقٞ مِّنكُم بِرَبِّهِمۡ يُشۡرِكُونَ\nСўнгра, У сиздан ёмонликни кушойиш қилган чоғда, кўрибсизки, сиздан бир гуруҳ ўз Роббиларига ширк келтирурлар'}, {'chapter': 16, 'verse': 55, 'text': 'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ\nУларга берган нарсаларимизга куфр келтирурлар. Бас, ҳузурланиб туринглар, тезда билурсиз'}, {'chapter': 16, 'verse': 56, 'text': 'وَيَجۡعَلُونَ لِمَا لَا يَعۡلَمُونَ نَصِيبٗا مِّمَّا رَزَقۡنَٰهُمۡۗ تَٱللَّهِ لَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَفۡتَرُونَ\nВа улар ўзлари (моҳиятини) билмаган нарсага Биз уларга берган ризқдан насиба ажратурлар. Аллоҳга қасамки, ўзингиз уйдирган ёлғон нарса ҳақида албатта сўралурсиз'}, {'chapter': 16, 'verse': 57, 'text': 'وَيَجۡعَلُونَ لِلَّهِ ٱلۡبَنَٰتِ سُبۡحَٰنَهُۥ وَلَهُم مَّا يَشۡتَهُونَ\nУлар қизларни Аллоҳники қилишур. У (бундан) покдир. Уларга эса, кўнгиллари тусагани (эмиш)'}, {'chapter': 16, 'verse': 58, 'text': 'وَإِذَا بُشِّرَ أَحَدُهُم بِٱلۡأُنثَىٰ ظَلَّ وَجۡهُهُۥ مُسۡوَدّٗا وَهُوَ كَظِيمٞ\nҚачонки улардан бирига қизнинг хушхабари берилса, ғам-аламга тўлиб, юзи қорайиб кетур'}, {'chapter': 16, 'verse': 59, 'text': 'يَتَوَٰرَىٰ مِنَ ٱلۡقَوۡمِ مِن سُوٓءِ مَا بُشِّرَ بِهِۦٓۚ أَيُمۡسِكُهُۥ عَلَىٰ هُونٍ أَمۡ يَدُسُّهُۥ فِي ٱلتُّرَابِۗ أَلَا سَآءَ مَا يَحۡكُمُونَ\nУнга берилган хушхабарнинг ёмонлигидан (уялиб) қавмидан беркинур. У(қиз)ни хорлик-ла олиб қолсамикин ёки тупроққа кўмсамикин? Огоҳ бўлинг! Улар қилган ҳукм нақадар ёмон'}, {'chapter': 16, 'verse': 60, 'text': 'لِلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ مَثَلُ ٱلسَّوۡءِۖ وَلِلَّهِ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОхиратга иймон келтирмайдиганларнинг мисоли ёмон мисолдир. Аъло мисол Аллоҳникидир. У Азийз ва Ҳакиймдир'}, {'chapter': 16, 'verse': 61, 'text': 'وَلَوۡ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِظُلۡمِهِم مَّا تَرَكَ عَلَيۡهَا مِن دَآبَّةٖ وَلَٰكِن يُؤَخِّرُهُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۖ فَإِذَا جَآءَ أَجَلُهُمۡ لَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ\nАгар Аллоҳ одамларни қилган зулмларига биноан тутганида, ер юзида битта ҳам жонзотни қўймас эди. Лекин уларни белгиланган ажалига қўйиб қўюр. Қачонки, ажаллари келса, уни бир соат орқага ҳам, олдинга ҳам сура олмаслар'}, {'chapter': 16, 'verse': 62, 'text': 'وَيَجۡعَلُونَ لِلَّهِ مَا يَكۡرَهُونَۚ وَتَصِفُ أَلۡسِنَتُهُمُ ٱلۡكَذِبَ أَنَّ لَهُمُ ٱلۡحُسۡنَىٰۚ لَا جَرَمَ أَنَّ لَهُمُ ٱلنَّارَ وَأَنَّهُم مُّفۡرَطُونَ\nУлар ўзлари ёқтирмаган нарсани Аллоҳники қилурлар. Уларнинг тиллари эса, ёлғондан ўзларига гўзал (оқибат) бўлишини васф қилур. Ҳеч шубҳа йўқки, уларга, албатта, дўзах бўлур ва, албатта, улар унутилганлардир'}, {'chapter': 16, 'verse': 63, 'text': 'تَٱللَّهِ لَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَهُوَ وَلِيُّهُمُ ٱلۡيَوۡمَ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nАллоҳга қасамки, Биз сендан олдинги умматларга ҳам (Пайғамбар) юборганмиз. Бас, шайтон уларга амалларини зийнатлаб кўрсатди. У бугун уларнинг дўстидир. Уларга аламли азоб бўлур'}, {'chapter': 16, 'verse': 64, 'text': 'وَمَآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ إِلَّا لِتُبَيِّنَ لَهُمُ ٱلَّذِي ٱخۡتَلَفُواْ فِيهِ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ\nБиз сенга китобни, фақат, уларга ўзлари ихтилоф қилган нарсаларни баён қилишинг учун, иймон келтирадиган қавмларга ҳидоят ва раҳмат этиб нозил этдик, холос'}, {'chapter': 16, 'verse': 65, 'text': 'وَٱللَّهُ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَسۡمَعُونَ\nАллоҳ осмондан сув тушириб, у ила ерни ўлимидан кейин тирилтирди. Албатта, бунда эшитадиган қавмлар учун оят — белгилар бордир'}, {'chapter': 16, 'verse': 66, 'text': 'وَإِنَّ لَكُمۡ فِي ٱلۡأَنۡعَٰمِ لَعِبۡرَةٗۖ نُّسۡقِيكُم مِّمَّا فِي بُطُونِهِۦ مِنۢ بَيۡنِ فَرۡثٖ وَدَمٖ لَّبَنًا خَالِصٗا سَآئِغٗا لِّلشَّـٰرِبِينَ\nЧорва ҳайвонларида ҳам сиз учун, албатта, ибрат бордир. Биз сизларни уларнинг қорнидан, гўнг ва қон орасидан ичувчилар(томоғи)дан осон ўтувчи соф сут ила суғорамиз'}, {'chapter': 16, 'verse': 67, 'text': 'وَمِن ثَمَرَٰتِ ٱلنَّخِيلِ وَٱلۡأَعۡنَٰبِ تَتَّخِذُونَ مِنۡهُ سَكَرٗا وَرِزۡقًا حَسَنًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡقِلُونَ\nВа хурмо ва узумларнинг меваларидан маст қилувчи нарса ва гўзал ризқ олурсиз. Албатта, бунда ақл юритгувчилар учун оят — белги бордир'}, {'chapter': 16, 'verse': 68, 'text': 'وَأَوۡحَىٰ رَبُّكَ إِلَى ٱلنَّحۡلِ أَنِ ٱتَّخِذِي مِنَ ٱلۡجِبَالِ بُيُوتٗا وَمِنَ ٱلشَّجَرِ وَمِمَّا يَعۡرِشُونَ\nРоббинг асаларига: «Тоғлардан, дарахтлардан ва кўтарилган сўритоклардан уй тутгин'}, {'chapter': 16, 'verse': 69, 'text': 'ثُمَّ كُلِي مِن كُلِّ ٱلثَّمَرَٰتِ فَٱسۡلُكِي سُبُلَ رَبِّكِ ذُلُلٗاۚ يَخۡرُجُ مِنۢ بُطُونِهَا شَرَابٞ مُّخۡتَلِفٌ أَلۡوَٰنُهُۥ فِيهِ شِفَآءٞ لِّلنَّاسِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَتَفَكَّرُونَ\nСўнгра ҳамма мевалардан егин. Бас, Роббинг осон қилиб қўйган йўллардан юргин», деб ваҳий қилди. Уларнинг қоринларидан турли рангдаги шароб чиқар. Унда одамлар учун шифо бордир. Албатта, бунда тафаккур қилувчилар учун оят — белги бордир'}, {'chapter': 16, 'verse': 70, 'text': 'وَٱللَّهُ خَلَقَكُمۡ ثُمَّ يَتَوَفَّىٰكُمۡۚ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرۡذَلِ ٱلۡعُمُرِ لِكَيۡ لَا يَعۡلَمَ بَعۡدَ عِلۡمٖ شَيۡـًٔاۚ إِنَّ ٱللَّهَ عَلِيمٞ قَدِيرٞ\nАллоҳ сизларни яратгандир. Сўнгра, У сизларни вафот эттирадир. Сизлардан баъзилар билимли бўлгандан сўнг, ҳеч нарса билмаслиги учун, умрнинг энг заифлигига қайтариладир. Албатта, Аллоҳ билгувчи ва қодирдир'}, {'chapter': 16, 'verse': 71, 'text': 'وَٱللَّهُ فَضَّلَ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖ فِي ٱلرِّزۡقِۚ فَمَا ٱلَّذِينَ فُضِّلُواْ بِرَآدِّي رِزۡقِهِمۡ عَلَىٰ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَهُمۡ فِيهِ سَوَآءٌۚ أَفَبِنِعۡمَةِ ٱللَّهِ يَجۡحَدُونَ\nАллоҳ ризқда баъзингизни баъзингиздан афзал қилди. Бас, афзал қилинганлар ўз ризқларини қўлларида мулк бўлганларга, ҳамма тенг бўлсин деб, берувчи эмаслар-ку. Аллоҳнинг неъматини инкор қиладиларми'}, {'chapter': 16, 'verse': 72, 'text': 'وَٱللَّهُ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَجَعَلَ لَكُم مِّنۡ أَزۡوَٰجِكُم بَنِينَ وَحَفَدَةٗ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِۚ أَفَبِٱلۡبَٰطِلِ يُؤۡمِنُونَ وَبِنِعۡمَتِ ٱللَّهِ هُمۡ يَكۡفُرُونَ\nАллоҳ сизларга ўзларингиздан жуфт(ҳалол)лар қилди ва сизларга жуфтларингиздан болалар ва набиралар қилди ҳамда сизларга пок ризқларни берди. Ботилга иймон келтирадилар-у, Аллоҳнинг неъматига куфр келтирадиларми'}, {'chapter': 16, 'verse': 73, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَمۡلِكُ لَهُمۡ رِزۡقٗا مِّنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ شَيۡـٔٗا وَلَا يَسۡتَطِيعُونَ\nВа Аллоҳдан ўзга — уларга осмонлару ердан ҳеч бир ризққа молик бўла олмайдиган ва ҳеч нарсага қодир бўла олмайдиган — нарсаларга ибодат қилурларми'}, {'chapter': 16, 'verse': 74, 'text': 'فَلَا تَضۡرِبُواْ لِلَّهِ ٱلۡأَمۡثَالَۚ إِنَّ ٱللَّهَ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nАллоҳга мисол келтирманглар! Албатта, Аллоҳ биладир. Сизлар эса, билмассизлар'}, {'chapter': 16, 'verse': 75, 'text': '۞ضَرَبَ ٱللَّهُ مَثَلًا عَبۡدٗا مَّمۡلُوكٗا لَّا يَقۡدِرُ عَلَىٰ شَيۡءٖ وَمَن رَّزَقۡنَٰهُ مِنَّا رِزۡقًا حَسَنٗا فَهُوَ يُنفِقُ مِنۡهُ سِرّٗا وَجَهۡرًاۖ هَلۡ يَسۡتَوُۥنَۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nАллоҳ ҳеч нарсага қодир бўлмайдиган мамлук — қул билан Биз уни яхши ризқ ила ризқлантирган ва ўшандан махфий ва ошкора инфоқ қиладиган кимсани мисол келтиради. Улар тенг бўлурларми? Ҳамду сано Аллоҳгадир. Лекин кўплари билмаслар'}, {'chapter': 16, 'verse': 76, 'text': 'وَضَرَبَ ٱللَّهُ مَثَلٗا رَّجُلَيۡنِ أَحَدُهُمَآ أَبۡكَمُ لَا يَقۡدِرُ عَلَىٰ شَيۡءٖ وَهُوَ كَلٌّ عَلَىٰ مَوۡلَىٰهُ أَيۡنَمَا يُوَجِّههُّ لَا يَأۡتِ بِخَيۡرٍ هَلۡ يَسۡتَوِي هُوَ وَمَن يَأۡمُرُ بِٱلۡعَدۡلِ وَهُوَ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳ икки кишини зарбулмасал келтирди. Бирлари соқов, ҳеч нарсага қодир бўлмас. У ўз хожасига юкдир. Қаерга юборса ҳам, яхшилик келтирмас. Ўша одам билан ўзи тўғри йўлда туриб, бошқаларни адолатга амр қиладиган киши баробар бўлурми'}, {'chapter': 16, 'verse': 77, 'text': 'وَلِلَّهِ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَآ أَمۡرُ ٱلسَّاعَةِ إِلَّا كَلَمۡحِ ٱلۡبَصَرِ أَوۡ هُوَ أَقۡرَبُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nОсмонлару ернинг ғайб(илм)и Аллоҳникидир. Соат (қиёмат) иши эса, кўз очиб-юмгандек ёки ундан ҳам яқинроқдир. Албатта, Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 16, 'verse': 78, 'text': 'وَٱللَّهُ أَخۡرَجَكُم مِّنۢ بُطُونِ أُمَّهَٰتِكُمۡ لَا تَعۡلَمُونَ شَيۡـٔٗا وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَ لَعَلَّكُمۡ تَشۡكُرُونَ\nАллоҳ сизни оналарингиз қорнидан ҳеч нарса билмайдиган ҳолингизда чиқарди. У сизга қулоқ, кўзлар ва дилларни берди. Шоядки, шукр қилсангиз'}, {'chapter': 16, 'verse': 79, 'text': 'أَلَمۡ يَرَوۡاْ إِلَى ٱلطَّيۡرِ مُسَخَّرَٰتٖ فِي جَوِّ ٱلسَّمَآءِ مَا يُمۡسِكُهُنَّ إِلَّا ٱللَّهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nОсмон ҳавосидаги беминнат бўйсундирилган қушларга қарамайдиларми? Уларни фақатгина Аллоҳ ушлаб турадир, холос. Албатта, бунда иймон келтирадиган қавмлар учун оят-белгилар бордир'}, {'chapter': 16, 'verse': 80, 'text': 'وَٱللَّهُ جَعَلَ لَكُم مِّنۢ بُيُوتِكُمۡ سَكَنٗا وَجَعَلَ لَكُم مِّن جُلُودِ ٱلۡأَنۡعَٰمِ بُيُوتٗا تَسۡتَخِفُّونَهَا يَوۡمَ ظَعۡنِكُمۡ وَيَوۡمَ إِقَامَتِكُمۡ وَمِنۡ أَصۡوَافِهَا وَأَوۡبَارِهَا وَأَشۡعَارِهَآ أَثَٰثٗا وَمَتَٰعًا إِلَىٰ حِينٖ\nАллоҳ сизга уйларингизни оромгоҳ қилди. У сизга чорва ҳайвонларининг терисидан кўчадиган ва қўнадиган кунингизда енгил кўтариладиган уйлар қилди. Уларнинг жунлари, юнглари ва қилларидан маълум вақтгача жиҳоз ва мато бўладиган қилди'}, {'chapter': 16, 'verse': 81, 'text': 'وَٱللَّهُ جَعَلَ لَكُم مِّمَّا خَلَقَ ظِلَٰلٗا وَجَعَلَ لَكُم مِّنَ ٱلۡجِبَالِ أَكۡنَٰنٗا وَجَعَلَ لَكُمۡ سَرَٰبِيلَ تَقِيكُمُ ٱلۡحَرَّ وَسَرَٰبِيلَ تَقِيكُم بَأۡسَكُمۡۚ كَذَٰلِكَ يُتِمُّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تُسۡلِمُونَ\nАллоҳ сизга Ўзи яратган нарсалардан соялар қилиб қўйди ва сизга тоғлардан бошпаналар, сизларни иссиқдан сақлайдиган кийимлар ва зарбадан сақлайдиган кийимлар қилиб қўйди. Шундай қилиб, У сизларга Ўз неъматини батамом қилиб берди. Шоядки, мусулмон бўлсангиз'}, {'chapter': 16, 'verse': 82, 'text': 'فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАгар юз ўгириб кетсалар, бас, сенинг зиммангда очиқ-ойдин етказиб қўйиш, холос'}, {'chapter': 16, 'verse': 83, 'text': 'يَعۡرِفُونَ نِعۡمَتَ ٱللَّهِ ثُمَّ يُنكِرُونَهَا وَأَكۡثَرُهُمُ ٱلۡكَٰفِرُونَ\nУлар Аллоҳнинг неъматини танирлар, кейин эса, инкор қилурлар. Уларнинг кўплари кофирлардир'}, {'chapter': 16, 'verse': 84, 'text': 'وَيَوۡمَ نَبۡعَثُ مِن كُلِّ أُمَّةٖ شَهِيدٗا ثُمَّ لَا يُؤۡذَنُ لِلَّذِينَ كَفَرُواْ وَلَا هُمۡ يُسۡتَعۡتَبُونَ\nУ кунда барча умматлардан гувоҳ келтирурмиз, сўнгра, куфр келтирганларга (узр учун) изн ҳам берилмас ва улар итобга ҳам қайтарилмаслар'}, {'chapter': 16, 'verse': 85, 'text': 'وَإِذَا رَءَا ٱلَّذِينَ ظَلَمُواْ ٱلۡعَذَابَ فَلَا يُخَفَّفُ عَنۡهُمۡ وَلَا هُمۡ يُنظَرُونَ\nЗулм қилганлар азобни кўрган вақтларида, улардан у енгиллатилмас ва уларга муҳлат ҳам берилмас'}, {'chapter': 16, 'verse': 86, 'text': 'وَإِذَا رَءَا ٱلَّذِينَ أَشۡرَكُواْ شُرَكَآءَهُمۡ قَالُواْ رَبَّنَا هَـٰٓؤُلَآءِ شُرَكَآؤُنَا ٱلَّذِينَ كُنَّا نَدۡعُواْ مِن دُونِكَۖ فَأَلۡقَوۡاْ إِلَيۡهِمُ ٱلۡقَوۡلَ إِنَّكُمۡ لَكَٰذِبُونَ\nВа ширк келтирганлар ўзларининг «шерик»ларини кўрган чоғларида: «Эй Роббимиз, анавилар бизнинг Сендан ўзга ибодат қилган «шерик»ларимиз», дерлар. Бас, у(шерик)лар буларга: «Албатта, сизлар ёлғончидирсизлар», деб сўз ташларлар'}, {'chapter': 16, 'verse': 87, 'text': 'وَأَلۡقَوۡاْ إِلَى ٱللَّهِ يَوۡمَئِذٍ ٱلسَّلَمَۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nЎшал кунда у(мушрик)лар Аллоҳга таслим бўлурлар ва уйдириб юрган нарсалари ботил бўлур'}, {'chapter': 16, 'verse': 88, 'text': 'ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ زِدۡنَٰهُمۡ عَذَابٗا فَوۡقَ ٱلۡعَذَابِ بِمَا كَانُواْ يُفۡسِدُونَ\nКуфр келтирган ва Аллоҳнинг йўлидан тўсганларга фасод қилганлари учун азоб устига азоб зиёда қилдик'}, {'chapter': 16, 'verse': 89, 'text': 'وَيَوۡمَ نَبۡعَثُ فِي كُلِّ أُمَّةٖ شَهِيدًا عَلَيۡهِم مِّنۡ أَنفُسِهِمۡۖ وَجِئۡنَا بِكَ شَهِيدًا عَلَىٰ هَـٰٓؤُلَآءِۚ وَنَزَّلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ تِبۡيَٰنٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ وَبُشۡرَىٰ لِلۡمُسۡلِمِينَ\nҲар бир умматга ўзидан гувоҳ олиб келган кунимизда сени анавиларга (умматингга) гувоҳ қилиб келтирамиз. Сенга Китобни ҳар бир нарсани баён қилувчи этиб, мусулмонларга ҳидоят, раҳмат ва хушхабар қилиб нозил этдик'}, {'chapter': 16, 'verse': 90, 'text': '۞إِنَّ ٱللَّهَ يَأۡمُرُ بِٱلۡعَدۡلِ وَٱلۡإِحۡسَٰنِ وَإِيتَآيِٕ ذِي ٱلۡقُرۡبَىٰ وَيَنۡهَىٰ عَنِ ٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِ وَٱلۡبَغۡيِۚ يَعِظُكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ\nАлбатта, Аллоҳ адолатга, эҳсонга, қариндошларга яхшилик қилишга амр этадир ва фаҳшу мункар (ишлардан) ҳамда зулмкорликдан қайтарур. У сизларга ваъз қилур. Шоядки, эсласангиз'}, {'chapter': 16, 'verse': 91, 'text': 'وَأَوۡفُواْ بِعَهۡدِ ٱللَّهِ إِذَا عَٰهَدتُّمۡ وَلَا تَنقُضُواْ ٱلۡأَيۡمَٰنَ بَعۡدَ تَوۡكِيدِهَا وَقَدۡ جَعَلۡتُمُ ٱللَّهَ عَلَيۡكُمۡ كَفِيلًاۚ إِنَّ ٱللَّهَ يَعۡلَمُ مَا تَفۡعَلُونَ\nАгар аҳдлашсангиз, Аллоҳнинг аҳдига вафо қилинг. Қасамларни таъкид қилинганидан сўнг бузманг. Батаҳқиқ, Аллоҳни ўзингизга кафил қилгансиз-а! Албатта, Аллоҳ нима қилаётганингизни билур'}, {'chapter': 16, 'verse': 92, 'text': 'وَلَا تَكُونُواْ كَٱلَّتِي نَقَضَتۡ غَزۡلَهَا مِنۢ بَعۡدِ قُوَّةٍ أَنكَٰثٗا تَتَّخِذُونَ أَيۡمَٰنَكُمۡ دَخَلَۢا بَيۡنَكُمۡ أَن تَكُونَ أُمَّةٌ هِيَ أَرۡبَىٰ مِنۡ أُمَّةٍۚ إِنَّمَا يَبۡلُوكُمُ ٱللَّهُ بِهِۦۚ وَلَيُبَيِّنَنَّ لَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ مَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nТўқигани кучли бўлгандан сўнг уни сўкиб юборган хотинга ўхшаб, бир уммат бошқа умматдан зиёдроқ бўлгани учун, қасамларингизни орангизга алдов воситаси қилиб олманг. Албатта, бу билан сизни Аллоҳ синамоқда, холос. Албатта, У қиёмат куни нима ҳақида ихтилоф қилганингизни баён қилиб берур'}, {'chapter': 16, 'verse': 93, 'text': 'وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَلَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَعۡمَلُونَ\nАгар Аллоҳ хоҳласа, сизларни бир уммат қилар эди. Лекин хоҳлаган кимсани залолатга кетказади ва хоҳлаган кимсани ҳидоят қиладир. Ва, албатта, қилиб юрган амалларингиздан сўралурсиз'}, {'chapter': 16, 'verse': 94, 'text': 'وَلَا تَتَّخِذُوٓاْ أَيۡمَٰنَكُمۡ دَخَلَۢا بَيۡنَكُمۡ فَتَزِلَّ قَدَمُۢ بَعۡدَ ثُبُوتِهَا وَتَذُوقُواْ ٱلسُّوٓءَ بِمَا صَدَدتُّمۡ عَن سَبِيلِ ٱللَّهِ وَلَكُمۡ عَذَابٌ عَظِيمٞ\nҚасамларингизни орангизда алдов воситаси қилиб олманг. Бас, қадам собит бўлганидан сўнг тоймасин ва Аллоҳнинг йўлидан тўсганингиз учун ёмонликни татиб юрманг. Сизга улкан азоб бўлмасин'}, {'chapter': 16, 'verse': 95, 'text': 'وَلَا تَشۡتَرُواْ بِعَهۡدِ ٱللَّهِ ثَمَنٗا قَلِيلًاۚ إِنَّمَا عِندَ ٱللَّهِ هُوَ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nАллоҳнинг аҳдини оз баҳога сотиб юборманг. Агар билсангиз, сиз учун, албатта, Аллоҳнинг ҳузуридаги нарса яхшироқдир'}, {'chapter': 16, 'verse': 96, 'text': 'مَا عِندَكُمۡ يَنفَدُ وَمَا عِندَ ٱللَّهِ بَاقٖۗ وَلَنَجۡزِيَنَّ ٱلَّذِينَ صَبَرُوٓاْ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُواْ يَعۡمَلُونَ\nСизнинг ҳузурингиздаги нарса тугайдир. Аллоҳнинг ҳузуридаги нарса эса, боқийдир. Албатта, Биз сабр этганларни қилиб юрган амалларининг энг гўзалига бериладиган ажр ила мукофотлармиз'}, {'chapter': 16, 'verse': 97, 'text': 'مَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَلَنُحۡيِيَنَّهُۥ حَيَوٰةٗ طَيِّبَةٗۖ وَلَنَجۡزِيَنَّهُمۡ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُواْ يَعۡمَلُونَ\nЭркагу аёл, ким мўмин ҳолида яхши амал қилса, Биз унга яхши ҳаёт кечиртирамиз ва, албатта, уларни қилиб юрган амалларининг энг гўзалига бериладиган ажр ила мукофотлармиз'}, {'chapter': 16, 'verse': 98, 'text': 'فَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ فَٱسۡتَعِذۡ بِٱللَّهِ مِنَ ٱلشَّيۡطَٰنِ ٱلرَّجِيمِ\nҚуръон ўқиган чоғингда, Аллоҳдан қувилган шайтон (шарри)дан паноҳ сўрагин'}, {'chapter': 16, 'verse': 99, 'text': 'إِنَّهُۥ لَيۡسَ لَهُۥ سُلۡطَٰنٌ عَلَى ٱلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nАлбатта, иймон келтирганларга ва ўз Роббиларига таваккал қилганларга у(шайтон)нинг султони йўқ'}, {'chapter': 16, 'verse': 100, 'text': 'إِنَّمَا سُلۡطَٰنُهُۥ عَلَى ٱلَّذِينَ يَتَوَلَّوۡنَهُۥ وَٱلَّذِينَ هُم بِهِۦ مُشۡرِكُونَ\nУнинг султони фақат уни дўст тутадиганларга ва у сабабли мушрик бўлганларга, холос'}, {'chapter': 16, 'verse': 101, 'text': 'وَإِذَا بَدَّلۡنَآ ءَايَةٗ مَّكَانَ ءَايَةٖ وَٱللَّهُ أَعۡلَمُ بِمَا يُنَزِّلُ قَالُوٓاْ إِنَّمَآ أَنتَ مُفۡتَرِۭۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nҚачонки, Биз бир оят ўрнини бошқа бир оят ила алмаштирсак, — ҳолбуки, Аллоҳ нимани нозил қилишни Ўзи яхши билгувчидир — улар: «Шубҳасиз, сен уйдирмачисан!» дерлар. Йўқ! Уларнинг кўплари билмаслар'}, {'chapter': 16, 'verse': 102, 'text': 'قُلۡ نَزَّلَهُۥ رُوحُ ٱلۡقُدُسِ مِن رَّبِّكَ بِٱلۡحَقِّ لِيُثَبِّتَ ٱلَّذِينَ ءَامَنُواْ وَهُدٗى وَبُشۡرَىٰ لِلۡمُسۡلِمِينَ\nСен уни руҳул қудус Роббингдан иймон келтирганларни собитқадам қилиш учун ва мусулмонларга ҳидоят ва хушхабар этиб ҳақ ила туширганини айт'}, {'chapter': 16, 'verse': 103, 'text': 'وَلَقَدۡ نَعۡلَمُ أَنَّهُمۡ يَقُولُونَ إِنَّمَا يُعَلِّمُهُۥ بَشَرٞۗ لِّسَانُ ٱلَّذِي يُلۡحِدُونَ إِلَيۡهِ أَعۡجَمِيّٞ وَهَٰذَا لِسَانٌ عَرَبِيّٞ مُّبِينٌ\nБатаҳқиқ, биламизки, албатта, улар: «Унга фақат башар таълим бермоқда, холос», дерлар. Улар гумон қилаётганнинг тили ажамийдир. Бу эса, очиқ-ойдин араб тилидадир'}, {'chapter': 16, 'verse': 104, 'text': 'إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِـَٔايَٰتِ ٱللَّهِ لَا يَهۡدِيهِمُ ٱللَّهُ وَلَهُمۡ عَذَابٌ أَلِيمٌ\nАлбатта, Аллоҳнинг оятларига иймон келтирмайдиганларни Аллоҳ ҳидоятга солмайди ва уларга аламли азоб бордир'}, {'chapter': 16, 'verse': 105, 'text': 'إِنَّمَا يَفۡتَرِي ٱلۡكَذِبَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِـَٔايَٰتِ ٱللَّهِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰذِبُونَ\nЁлғонни фақат Аллоҳнинг оятларига иймон келтирмайдиганларгина тўқирлар. Ана ўшалар ўзлари ёлғончилардир'}, {'chapter': 16, 'verse': 106, 'text': 'مَن كَفَرَ بِٱللَّهِ مِنۢ بَعۡدِ إِيمَٰنِهِۦٓ إِلَّا مَنۡ أُكۡرِهَ وَقَلۡبُهُۥ مُطۡمَئِنُّۢ بِٱلۡإِيمَٰنِ وَلَٰكِن مَّن شَرَحَ بِٱلۡكُفۡرِ صَدۡرٗا فَعَلَيۡهِمۡ غَضَبٞ مِّنَ ٱللَّهِ وَلَهُمۡ عَذَابٌ عَظِيمٞ\nКим иймондан сўнг Аллоҳга куфр келтирса, — қалби иймон ила ором топа туриб зўрланганлар бундан мустасно — ким кўксини куфрга очса, бас, уларга Аллоҳдан ғазаб бор. Уларга улкан азоб бор'}, {'chapter': 16, 'verse': 107, 'text': 'ذَٰلِكَ بِأَنَّهُمُ ٱسۡتَحَبُّواْ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا عَلَى ٱلۡأٓخِرَةِ وَأَنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nБу, уларнинг дунё ҳаётини охиратдан устун қўйганлари сабабидандир. Албатта, Аллоҳ кофир қавмларни ҳидоят этмас'}, {'chapter': 16, 'verse': 108, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَسَمۡعِهِمۡ وَأَبۡصَٰرِهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡغَٰفِلُونَ\nАна ўшаларнинг қалблари, қулоқлари ва кўзларига Аллоҳ муҳр босгандир. Ана ўшалар ғофиллардир'}, {'chapter': 16, 'verse': 109, 'text': 'لَا جَرَمَ أَنَّهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡخَٰسِرُونَ\nҲеч шубҳа йўқки, охиратда улар зиён кўргувчилардир'}, {'chapter': 16, 'verse': 110, 'text': 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُواْ مِنۢ بَعۡدِ مَا فُتِنُواْ ثُمَّ جَٰهَدُواْ وَصَبَرُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ\nСўнгра, албатта, Роббинг фитнага учраганларидан кейин ҳижрат этиб, жиҳод қилган ва сабр этганлари учун, албатта, Роббинг шулардан кейин мағфиратли ва раҳимлидир'}, {'chapter': 16, 'verse': 111, 'text': '۞يَوۡمَ تَأۡتِي كُلُّ نَفۡسٖ تُجَٰدِلُ عَن نَّفۡسِهَا وَتُوَفَّىٰ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nУ кунда ҳар жон ўзи ҳақида тортишиш учун келур ва ҳар бир жонга амалига яраша вафо қилинур ҳамда уларга зулм этилмас'}, {'chapter': 16, 'verse': 112, 'text': 'وَضَرَبَ ٱللَّهُ مَثَلٗا قَرۡيَةٗ كَانَتۡ ءَامِنَةٗ مُّطۡمَئِنَّةٗ يَأۡتِيهَا رِزۡقُهَا رَغَدٗا مِّن كُلِّ مَكَانٖ فَكَفَرَتۡ بِأَنۡعُمِ ٱللَّهِ فَأَذَٰقَهَا ٱللَّهُ لِبَاسَ ٱلۡجُوعِ وَٱلۡخَوۡفِ بِمَا كَانُواْ يَصۡنَعُونَ\nАллоҳ бир шаҳарни зарбулмасал қилади: у омонлик ва хотиржамликда эди. Ризқи ҳар макондан сероб ҳолда келиб турарди. Сўнг, у Аллоҳнинг неъматларига куфр келтирди. Бас, Аллоҳ унга, қилган ишлари учун, очлик ва хавф кийимини тоттирди'}, {'chapter': 16, 'verse': 113, 'text': 'وَلَقَدۡ جَآءَهُمۡ رَسُولٞ مِّنۡهُمۡ فَكَذَّبُوهُ فَأَخَذَهُمُ ٱلۡعَذَابُ وَهُمۡ ظَٰلِمُونَ\nБатаҳқиқ, уларга ўзларидан Пайғамбар келди. Бас, улар уни ёлғончига чиқардилар. Кейин, золим бўлганлари учун, уларни азоб тутди'}, {'chapter': 16, 'verse': 114, 'text': 'فَكُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَٰلٗا طَيِّبٗا وَٱشۡكُرُواْ نِعۡمَتَ ٱللَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ\nБас, Аллоҳ сизга ризқ қилиб берган нарсалардан ҳалол-пок ҳолида енг. Ва агар унга ибодат қиладиган бўлсангиз, Аллоҳнинг неъматига шукр қилинг'}, {'chapter': 16, 'verse': 115, 'text': 'إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۖ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nСизларга фақат ўлимтикни, қонни, чўчқа гўштини ва Аллоҳдан ўзгага атаб сўйилган нарсаларнигина ҳаром қилди. Ким золим ва ҳаддан ошувчи бўлмаган ҳолида мажбур бўлиб қолса, албатта, Аллоҳ мағфиратли ва раҳимлидир'}, {'chapter': 16, 'verse': 116, 'text': 'وَلَا تَقُولُواْ لِمَا تَصِفُ أَلۡسِنَتُكُمُ ٱلۡكَذِبَ هَٰذَا حَلَٰلٞ وَهَٰذَا حَرَامٞ لِّتَفۡتَرُواْ عَلَى ٱللَّهِ ٱلۡكَذِبَۚ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ\nАллоҳга нисбатан ёлғон тўқиш учун тилларингиз васф қилган ёлғонни гапириб: «Бу ҳалол, бу ҳаром», деяверманглар. Албатта, Аллоҳга нисбатан ёлғон тўқувчилар нажот топмаслар'}, {'chapter': 16, 'verse': 117, 'text': 'مَتَٰعٞ قَلِيلٞ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nОзгина матоҳ ва аламли азоб бор'}, {'chapter': 16, 'verse': 118, 'text': 'وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا مَا قَصَصۡنَا عَلَيۡكَ مِن قَبۡلُۖ وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nЯҳудий бўлганларга эса, сенга бундан олдин айтиб берган нарсаларимизни ҳаром қилдик. Биз уларга зулм қилмадик. Лекин улар ўзларига зулм қилган эдилар'}, {'chapter': 16, 'verse': 119, 'text': 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُواْ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٌ\nСўнгра, албатта, Роббинг билмасдан ёмон амал қилганларга, ундан кейин эса, тавба қилиб яхши амалларни қилганларга, албатта, Роббинг шулардан сўнг мағфиратли ва раҳимлидир'}, {'chapter': 16, 'verse': 120, 'text': 'إِنَّ إِبۡرَٰهِيمَ كَانَ أُمَّةٗ قَانِتٗا لِّلَّهِ حَنِيفٗا وَلَمۡ يَكُ مِنَ ٱلۡمُشۡرِكِينَ\nАлбатта, Иброҳим Аллоҳнинг итоат қилгувчи, ҳаққа мойил уммати эди. Мушриклардан бўлган эмас'}, {'chapter': 16, 'verse': 121, 'text': 'شَاكِرٗا لِّأَنۡعُمِهِۚ ٱجۡتَبَىٰهُ وَهَدَىٰهُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nУнинг неъматларига шукр қилгувчи эди. У зот уни танлаб олди ва сиротул мустақиймга ҳидоят қилди'}, {'chapter': 16, 'verse': 122, 'text': 'وَءَاتَيۡنَٰهُ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ\nУнга бу дунёда яхши (ҳаёт) бердик. Охиратда эса, у, албатта, солиҳлардандир'}, {'chapter': 16, 'verse': 123, 'text': 'ثُمَّ أَوۡحَيۡنَآ إِلَيۡكَ أَنِ ٱتَّبِعۡ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nСўнгра, сенга: «Иброҳимнинг ҳаққа мойил миллатига эргаш. У мушриклардан бўлмаган эди», деб ваҳий қилдик'}, {'chapter': 16, 'verse': 124, 'text': 'إِنَّمَا جُعِلَ ٱلسَّبۡتُ عَلَى ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِۚ وَإِنَّ رَبَّكَ لَيَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nШанба, фақат, у тўғрисида ихтилоф қилганлар зиммасига юкланди, холос. Албатта, Роббинг қиёмат кунида улар орасида ихтилоф қилган нарсалари ҳақида ҳукм чиқарур'}, {'chapter': 16, 'verse': 125, 'text': 'ٱدۡعُ إِلَىٰ سَبِيلِ رَبِّكَ بِٱلۡحِكۡمَةِ وَٱلۡمَوۡعِظَةِ ٱلۡحَسَنَةِۖ وَجَٰدِلۡهُم بِٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\nРоббингнинг йўлига ҳикмат ва яхши мавъиза ила даъват қил. Ва улар ила гўзал услубда мужодала эт. Албатта, Роббинг Ўз йўлидан адашганларни ўта билгувчи ва ҳидоят топгувчиларни ҳам ўта билгувчидир'}, {'chapter': 16, 'verse': 126, 'text': 'وَإِنۡ عَاقَبۡتُمۡ فَعَاقِبُواْ بِمِثۡلِ مَا عُوقِبۡتُم بِهِۦۖ وَلَئِن صَبَرۡتُمۡ لَهُوَ خَيۡرٞ لِّلصَّـٰبِرِينَ\nАгар иқоб қиладиган бўлсангиз, ўзингизга қилинган иқобга ўхшаш иқоб қилинг. Агар сабр қилсангиз, албатта, у сабр қилгувчилар учун хайрлидир'}, {'chapter': 16, 'verse': 127, 'text': 'وَٱصۡبِرۡ وَمَا صَبۡرُكَ إِلَّا بِٱللَّهِۚ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُ فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ\nСабр қил, сенинг сабринг фақат Аллоҳ ила бўлур. Улар (ҳоли)га хафа бўлма. Улар қилаётган макрдан сиқилма'}, {'chapter': 16, 'verse': 128, 'text': 'إِنَّ ٱللَّهَ مَعَ ٱلَّذِينَ ٱتَّقَواْ وَّٱلَّذِينَ هُم مُّحۡسِنُونَ\nАлбатта, Аллоҳ тақво қилганлар ва гўзал ишлар қилганлар биландир'}], '17': [{'chapter': 17, 'verse': 1, 'text': 'سُبۡحَٰنَ ٱلَّذِيٓ أَسۡرَىٰ بِعَبۡدِهِۦ لَيۡلٗا مِّنَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ إِلَى ٱلۡمَسۡجِدِ ٱلۡأَقۡصَا ٱلَّذِي بَٰرَكۡنَا حَوۡلَهُۥ لِنُرِيَهُۥ مِنۡ ءَايَٰتِنَآۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\nЎз бандасини кечаси Масжидул Ҳаромдан атрофини баракали қилганимиз Масжидул Ақсога мўъжизаларимизни кўрсатиш учун сайр қилдирган Зот пок бўлди. Албатта, У эшитгувчи ва кўргувчи Зотдир'}, {'chapter': 17, 'verse': 2, 'text': 'وَءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَـٰٓءِيلَ أَلَّا تَتَّخِذُواْ مِن دُونِي وَكِيلٗا\nВа Биз Мусога китобни бердик ва у(китоб)ни Бани Исроилга ҳидоят қилиб: «Мендан ўзгани вакил тутманглар!» дедик'}, {'chapter': 17, 'verse': 3, 'text': 'ذُرِّيَّةَ مَنۡ حَمَلۡنَا مَعَ نُوحٍۚ إِنَّهُۥ كَانَ عَبۡدٗا شَكُورٗا\n(Эй) Нуҳ билан бирга кўтарганларимизнинг зурриётлари! Албатта, у ўта шукрли банда эди'}, {'chapter': 17, 'verse': 4, 'text': 'وَقَضَيۡنَآ إِلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ فِي ٱلۡكِتَٰبِ لَتُفۡسِدُنَّ فِي ٱلۡأَرۡضِ مَرَّتَيۡنِ وَلَتَعۡلُنَّ عُلُوّٗا كَبِيرٗا\nБиз у китобда Бани Исроилга, ер юзида икки марта бузғунчилик қилурсизлар ва катта туғёнга кетурсизлар, деб билдирдик'}, {'chapter': 17, 'verse': 5, 'text': 'فَإِذَا جَآءَ وَعۡدُ أُولَىٰهُمَا بَعَثۡنَا عَلَيۡكُمۡ عِبَادٗا لَّنَآ أُوْلِي بَأۡسٖ شَدِيدٖ فَجَاسُواْ خِلَٰلَ ٱلدِّيَارِۚ وَكَانَ وَعۡدٗا مَّفۡعُولٗا\nБас, ўша икки(бузғунчилик)дан биринчисининг ваъда вақти келганда, устингизга шиддатли куч-қудратга эга бандаларимизни юборамиз. Бас, улар ҳовли-жойларни оралаб кезадилар. Бу, қилиниши лозим ваъда эди'}, {'chapter': 17, 'verse': 6, 'text': 'ثُمَّ رَدَدۡنَا لَكُمُ ٱلۡكَرَّةَ عَلَيۡهِمۡ وَأَمۡدَدۡنَٰكُم بِأَمۡوَٰلٖ وَبَنِينَ وَجَعَلۡنَٰكُمۡ أَكۡثَرَ نَفِيرًا\nСўнгра, сизга улар устидан ғалабани қайтардик ва сизга молу дунё ва фарзандлар ила мадад бердик. Ва сизларнинг ададингизни кўп қилиб қўйдик'}, {'chapter': 17, 'verse': 7, 'text': 'إِنۡ أَحۡسَنتُمۡ أَحۡسَنتُمۡ لِأَنفُسِكُمۡۖ وَإِنۡ أَسَأۡتُمۡ فَلَهَاۚ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ لِيَسُـُٔواْ وُجُوهَكُمۡ وَلِيَدۡخُلُواْ ٱلۡمَسۡجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٖ وَلِيُتَبِّرُواْ مَا عَلَوۡاْ تَتۡبِيرًا\nАгар яхшилик қилсангиз, ўзингиз учун яхшилик қиласиз. Агар ёмонлик қилсангиз ҳам, ўзингиз учундир. Бас, кейинги(бузғунчилик)нинг ваъда вақти келганда, юзингизни қора қилишлари учун, масжидга аввал кирганларидек киришлари учун ва эгаллаган нарсаларини тамоман парчалашлари учун (бандаларимизни юборурмиз)'}, {'chapter': 17, 'verse': 8, 'text': 'عَسَىٰ رَبُّكُمۡ أَن يَرۡحَمَكُمۡۚ وَإِنۡ عُدتُّمۡ عُدۡنَاۚ وَجَعَلۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ حَصِيرًا\nШояд, Роббингиз сизга раҳим қилса. Агар сиз қайтсангиз, Биз ҳам қайтамиз. Ва жаҳаннамни кофирларга зиндон қилганмиз'}, {'chapter': 17, 'verse': 9, 'text': 'إِنَّ هَٰذَا ٱلۡقُرۡءَانَ يَهۡدِي لِلَّتِي هِيَ أَقۡوَمُ وَيُبَشِّرُ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرٗا كَبِيرٗا\nАлбатта, бу Қуръон энг тўғри йўлга ҳидоят қилур ва яхши амалларни қилувчи мўминларга, албатта ўшаларга, улуғ ажр борлиги хушхабарини берур'}, {'chapter': 17, 'verse': 10, 'text': 'وَأَنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا\nАлбатта, охиратга иймон келтирмайдиганларга, ўшаларга, аламли азобни тайёрлаб қўйдик'}, {'chapter': 17, 'verse': 11, 'text': 'وَيَدۡعُ ٱلۡإِنسَٰنُ بِٱلشَّرِّ دُعَآءَهُۥ بِٱلۡخَيۡرِۖ وَكَانَ ٱلۡإِنسَٰنُ عَجُولٗا\nИнсон яхшиликка дуо қилганидек, ёмонликка ҳам дуо қилур. Инсон шошқалоқ бўлгандир'}, {'chapter': 17, 'verse': 12, 'text': 'وَجَعَلۡنَا ٱلَّيۡلَ وَٱلنَّهَارَ ءَايَتَيۡنِۖ فَمَحَوۡنَآ ءَايَةَ ٱلَّيۡلِ وَجَعَلۡنَآ ءَايَةَ ٱلنَّهَارِ مُبۡصِرَةٗ لِّتَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡ وَلِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ وَكُلَّ شَيۡءٖ فَصَّلۡنَٰهُ تَفۡصِيلٗا\nКеча ва кундузни икки белги — аломат қилдик. Кеча аломатини маҳв этдик. Роббингиздан фазл талаб қилишингиз ва йиллар ададини ҳамда ҳисобини билишингиз учун кундуз аломатини кўрсатувчи қилдик. Ва ҳар бир нарсани батафсил баён қилганмиз'}, {'chapter': 17, 'verse': 13, 'text': 'وَكُلَّ إِنسَٰنٍ أَلۡزَمۡنَٰهُ طَـٰٓئِرَهُۥ فِي عُنُقِهِۦۖ وَنُخۡرِجُ لَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ كِتَٰبٗا يَلۡقَىٰهُ مَنشُورًا\nБиз ҳар бир инсоннинг амалини бўйнига боғлаб қўйганмиз ва қиёмат куни унга китоб чиқарамизки, у унга очилган ҳолда рўбарў бўлур'}, {'chapter': 17, 'verse': 14, 'text': 'ٱقۡرَأۡ كِتَٰبَكَ كَفَىٰ بِنَفۡسِكَ ٱلۡيَوۡمَ عَلَيۡكَ حَسِيبٗا\nКитобингни ўқи, бугунги кунда сен ўзингга ўзинг ҳисобчиликка кифоя қилурсан (дейилур)'}, {'chapter': 17, 'verse': 15, 'text': 'مَّنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبۡعَثَ رَسُولٗا\nКим ҳидоятга юрса, фақат ўзи учунгина ҳидоят топадир. Ким залолатга кетса, фақат ўзи учунгина залолатга кетадир. Ҳеч бир жон ўзганинг оғирлигини кўтарувчи бўлмас. Токи Пайғамбар юбормагунимизча, азобловчи бўлган эмасмиз'}, {'chapter': 17, 'verse': 16, 'text': 'وَإِذَآ أَرَدۡنَآ أَن نُّهۡلِكَ قَرۡيَةً أَمَرۡنَا مُتۡرَفِيهَا فَفَسَقُواْ فِيهَا فَحَقَّ عَلَيۡهَا ٱلۡقَوۡلُ فَدَمَّرۡنَٰهَا تَدۡمِيرٗا\nҚачон бир шаҳарни ҳалок этишни истасак, унинг маишатпарастларини (итоатга) амр қиламиз, бас, улар у(шаҳар)да фисқ қилурлар. Шунда унга «сўз» ҳақ бўлур. Бас, Биз у(шаҳар)ни мутлақо вайрон қилурмиз'}, {'chapter': 17, 'verse': 17, 'text': 'وَكَمۡ أَهۡلَكۡنَا مِنَ ٱلۡقُرُونِ مِنۢ بَعۡدِ نُوحٖۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِۦ خَبِيرَۢا بَصِيرٗا\nНуҳдан сўнг қанчадан-қанча асрларни ҳалок қилдик. Бандаларининг гуноҳларидан хабардор бўлиш ва уларни кўришда Роббинг Ўзи кифоя қилур'}, {'chapter': 17, 'verse': 18, 'text': 'مَّن كَانَ يُرِيدُ ٱلۡعَاجِلَةَ عَجَّلۡنَا لَهُۥ فِيهَا مَا نَشَآءُ لِمَن نُّرِيدُ ثُمَّ جَعَلۡنَا لَهُۥ جَهَنَّمَ يَصۡلَىٰهَا مَذۡمُومٗا مَّدۡحُورٗا\nКим шошган(дунё)ни истаса, унга бу дунёда ирода қилган кишимизга нимани хоҳласак, шошилинч берурмиз. Сўнгра, унга жаҳаннамни берурмиз. Унга хор ва қувғинга учраган ҳолда кирур'}, {'chapter': 17, 'verse': 19, 'text': 'وَمَنۡ أَرَادَ ٱلۡأٓخِرَةَ وَسَعَىٰ لَهَا سَعۡيَهَا وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ كَانَ سَعۡيُهُم مَّشۡكُورٗا\nКим охиратни истаса ва унга эришиш учун мўмин бўлган ҳолида керакли ҳаракатини қилса, бас, ана ўшаларнинг ҳаракатлари мақбулдир'}, {'chapter': 17, 'verse': 20, 'text': 'كُلّٗا نُّمِدُّ هَـٰٓؤُلَآءِ وَهَـٰٓؤُلَآءِ مِنۡ عَطَآءِ رَبِّكَۚ وَمَا كَانَ عَطَآءُ رَبِّكَ مَحۡظُورًا\nБарчага — манавиларга ҳам, анавиларга ҳам Роббингнинг атою неъматларидан чўзамиз. Роббингнинг атою неъматлари чекланган эмас'}, {'chapter': 17, 'verse': 21, 'text': 'ٱنظُرۡ كَيۡفَ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۚ وَلَلۡأٓخِرَةُ أَكۡبَرُ دَرَجَٰتٖ وَأَكۡبَرُ تَفۡضِيلٗا\nБаъзиларини баъзиларидан қандай афзал қилиб қўйганимизга назар сол. Албатта, охират даражаси ва афзаллиги каттароқдир'}, {'chapter': 17, 'verse': 22, 'text': 'لَّا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَقۡعُدَ مَذۡمُومٗا مَّخۡذُولٗا\nАллоҳ билан бирга ўзга илоҳни (шерик) қилма! Яна, мазамматга учраб, чорасиз ўтириб қолмагин'}, {'chapter': 17, 'verse': 23, 'text': '۞وَقَضَىٰ رَبُّكَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنًاۚ إِمَّا يَبۡلُغَنَّ عِندَكَ ٱلۡكِبَرَ أَحَدُهُمَآ أَوۡ كِلَاهُمَا فَلَا تَقُل لَّهُمَآ أُفّٖ وَلَا تَنۡهَرۡهُمَا وَقُل لَّهُمَا قَوۡلٗا كَرِيمٗا\nРоббинг фақат Унинг Ўзигагина ибодат қилишингни ва ота-онага яхшилик қилишни амр этди. Агар ҳузурингда уларнинг бирлари ёки икковлари ҳам кексаликка етсалар, бас, уларга «уфф» дема, уларга озор берма ва уларга яхши сўз айт'}, {'chapter': 17, 'verse': 24, 'text': 'وَٱخۡفِضۡ لَهُمَا جَنَاحَ ٱلذُّلِّ مِنَ ٱلرَّحۡمَةِ وَقُل رَّبِّ ٱرۡحَمۡهُمَا كَمَا رَبَّيَانِي صَغِيرٗا\nИкковларига меҳрибонлик ила хокисорлик қанотингни пастлат ва: «Роббим, алар мени кичикликда тарбия қилганларидек, уларга раҳим қилгин», деб айт'}, {'chapter': 17, 'verse': 25, 'text': 'رَّبُّكُمۡ أَعۡلَمُ بِمَا فِي نُفُوسِكُمۡۚ إِن تَكُونُواْ صَٰلِحِينَ فَإِنَّهُۥ كَانَ لِلۡأَوَّـٰبِينَ غَفُورٗا\nРоббингиз сизнинг дилингиздаги нарсани ҳам ўта билгувчидир. Агар аҳли солиҳлардан бўлсангиз, албатта, У сертавбалар учун сермағфиратдир'}, {'chapter': 17, 'verse': 26, 'text': 'وَءَاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَلَا تُبَذِّرۡ تَبۡذِيرًا\nҚариндошга ҳаққини бер! Мискинга ва йўл ўғлига ҳам. Исрофгарчиликка мутлақо йўл қўйма'}, {'chapter': 17, 'verse': 27, 'text': 'إِنَّ ٱلۡمُبَذِّرِينَ كَانُوٓاْ إِخۡوَٰنَ ٱلشَّيَٰطِينِۖ وَكَانَ ٱلشَّيۡطَٰنُ لِرَبِّهِۦ كَفُورٗا\nАлбатта, исрофчилар шайтонларнинг биродарлари бўлганлар. Шайтон эса, Роббига ўта ношукр бўлгандир'}, {'chapter': 17, 'verse': 28, 'text': 'وَإِمَّا تُعۡرِضَنَّ عَنۡهُمُ ٱبۡتِغَآءَ رَحۡمَةٖ مِّن رَّبِّكَ تَرۡجُوهَا فَقُل لَّهُمۡ قَوۡلٗا مَّيۡسُورٗا\nАгар улардан Роббингдан раҳмат талаб қилган ҳолда юз ўгирадиган бўлсанг, бас, уларга мулойим сўз айтгин'}, {'chapter': 17, 'verse': 29, 'text': 'وَلَا تَجۡعَلۡ يَدَكَ مَغۡلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبۡسُطۡهَا كُلَّ ٱلۡبَسۡطِ فَتَقۡعُدَ مَلُومٗا مَّحۡسُورًا\nҚўлингни бўйнингга боғлаб олма. Уни бир йўла ёзиб ҳам юборма. Яна, маломат ва ҳасратда ўтириб қолмагин'}, {'chapter': 17, 'verse': 30, 'text': 'إِنَّ رَبَّكَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا\nАлбатта, Роббинг ризқни кимга хоҳласа, кенг қилур ёки тор қилур. Албатта, У бандаларидан хабардор ва кўргувчи Зотдир'}, {'chapter': 17, 'verse': 31, 'text': 'وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُمۡ خَشۡيَةَ إِمۡلَٰقٖۖ نَّحۡنُ نَرۡزُقُهُمۡ وَإِيَّاكُمۡۚ إِنَّ قَتۡلَهُمۡ كَانَ خِطۡـٔٗا كَبِيرٗا\nБолаларингизни фақирликдан қўрқиб ўлдирманг. Биз уларни ҳам, сизларни ҳам ризқлантирамиз. Албатта, уларни ўлдириш катта хатодир'}, {'chapter': 17, 'verse': 32, 'text': 'وَلَا تَقۡرَبُواْ ٱلزِّنَىٰٓۖ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَسَآءَ سَبِيلٗا\nВа зинога яқинлашманглар. Албатта, у фоҳиша иш ва ёмон йўлдир'}, {'chapter': 17, 'verse': 33, 'text': 'وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۗ وَمَن قُتِلَ مَظۡلُومٗا فَقَدۡ جَعَلۡنَا لِوَلِيِّهِۦ سُلۡطَٰنٗا فَلَا يُسۡرِف فِّي ٱلۡقَتۡلِۖ إِنَّهُۥ كَانَ مَنصُورٗا\nАллоҳ (ўлдиришни) ҳаром қилган жонни ўлдирманг. Магар ҳақ ила бўлса, майли. Кимки мазлум ҳолида ўлдирилса, унинг валийига ҳақ берганмиз. Бас, у ўлдиришда исроф қилмасин. Албатта, у нусрат берилган одамдир'}, {'chapter': 17, 'verse': 34, 'text': 'وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ بِٱلۡعَهۡدِۖ إِنَّ ٱلۡعَهۡدَ كَانَ مَسۡـُٔولٗا\nЕтимнинг молига, токи у балоғат ёшига етгунча, яқинлашманглар. Магар яхшилик ила бўлса, майли. Аҳдга вафо қилинглар. Албатта, аҳд масъул бўлган ишдир'}, {'chapter': 17, 'verse': 35, 'text': 'وَأَوۡفُواْ ٱلۡكَيۡلَ إِذَا كِلۡتُمۡ وَزِنُواْ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِيمِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلٗا\nЎлчаган чоғингизда тўлиқ ўлчанг ва тўғри тарози ила тортинг. Ана ўша яхшидир ва оқибати гўзалдир'}, {'chapter': 17, 'verse': 36, 'text': 'وَلَا تَقۡفُ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۚ إِنَّ ٱلسَّمۡعَ وَٱلۡبَصَرَ وَٱلۡفُؤَادَ كُلُّ أُوْلَـٰٓئِكَ كَانَ عَنۡهُ مَسۡـُٔولٗا\nЎзинг билмаган нарсага эргашма! Албатта, қулоқ, кўз ва дил — ана ўшалар, масъулдирлар'}, {'chapter': 17, 'verse': 37, 'text': 'وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّكَ لَن تَخۡرِقَ ٱلۡأَرۡضَ وَلَن تَبۡلُغَ ٱلۡجِبَالَ طُولٗا\nЕр юзида кибру ҳаво ила юрма! Албатта, сен зинҳор ерни тешиб юбора олмайсан ва зинҳор бўйда тоғларга ета олмайсан'}, {'chapter': 17, 'verse': 38, 'text': 'كُلُّ ذَٰلِكَ كَانَ سَيِّئُهُۥ عِندَ رَبِّكَ مَكۡرُوهٗا\nБуларнинг барча ёмони Роббининг ҳузурида хуш кўрилмагандир'}, {'chapter': 17, 'verse': 39, 'text': 'ذَٰلِكَ مِمَّآ أَوۡحَىٰٓ إِلَيۡكَ رَبُّكَ مِنَ ٱلۡحِكۡمَةِۗ وَلَا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتُلۡقَىٰ فِي جَهَنَّمَ مَلُومٗا مَّدۡحُورًا\nУшбулар Роббинг сенга ваҳий қилган баъзи ҳикматлардир. Аллоҳ билан бирга ўзга илоҳ қилма. Яна, жаҳаннамга маломат қилинган ва қувилган ҳолингда ташланмагин'}, {'chapter': 17, 'verse': 40, 'text': 'أَفَأَصۡفَىٰكُمۡ رَبُّكُم بِٱلۡبَنِينَ وَٱتَّخَذَ مِنَ ٱلۡمَلَـٰٓئِكَةِ إِنَٰثًاۚ إِنَّكُمۡ لَتَقُولُونَ قَوۡلًا عَظِيمٗا\nРоббингиз сизларга ўғилларни хос қилиб бериб, фаришталарни Ўзига қиз қилиб олдими? Албатта, сизлар зўр гапни айтмоқдасиз'}, {'chapter': 17, 'verse': 41, 'text': 'وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرۡءَانِ لِيَذَّكَّرُواْ وَمَا يَزِيدُهُمۡ إِلَّا نُفُورٗا\nБатаҳқиқ, Биз бу Қуръонда эслашлари учун баён қилдик. Лекин бу уларга нафратдан ўзгани зиёда қилмас'}, {'chapter': 17, 'verse': 42, 'text': 'قُل لَّوۡ كَانَ مَعَهُۥٓ ءَالِهَةٞ كَمَا يَقُولُونَ إِذٗا لَّٱبۡتَغَوۡاْ إِلَىٰ ذِي ٱلۡعَرۡشِ سَبِيلٗا\nСен: «Агар, улар айтганидек, У билан бирга бошқа илоҳлар бўлганида, улар Арш эгаси томон йўл излардилар», деб айт'}, {'chapter': 17, 'verse': 43, 'text': 'سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَقُولُونَ عُلُوّٗا كَبِيرٗا\nУ зот улар айтаётган нарсадан пок ва ниҳоятда олийдир'}, {'chapter': 17, 'verse': 44, 'text': 'تُسَبِّحُ لَهُ ٱلسَّمَٰوَٰتُ ٱلسَّبۡعُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ وَإِن مِّن شَيۡءٍ إِلَّا يُسَبِّحُ بِحَمۡدِهِۦ وَلَٰكِن لَّا تَفۡقَهُونَ تَسۡبِيحَهُمۡۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا\nУнга етти осмону ер ва улардаги кимсалар тасбиҳ айтур. Унинг ҳамди ила тасбиҳ айтмаган ҳеч бир нарса йўқ. Лекин уларнинг тасбиҳини англамайсизлар. Албатта, у ҳалийм ва сермағфират Зотдир'}, {'chapter': 17, 'verse': 45, 'text': 'وَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ جَعَلۡنَا بَيۡنَكَ وَبَيۡنَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ حِجَابٗا مَّسۡتُورٗا\nҚачон Қуръон қироат қилсанг, сен билан охиратга иймон келтирмайдиганлар орасида кўринмас парда қилурмиз'}, {'chapter': 17, 'verse': 46, 'text': 'وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِذَا ذَكَرۡتَ رَبَّكَ فِي ٱلۡقُرۡءَانِ وَحۡدَهُۥ وَلَّوۡاْ عَلَىٰٓ أَدۡبَٰرِهِمۡ نُفُورٗا\nВа қалбларига, уни англамасликлари учун, ғилоф қилдик ва қулоқларини кар қилдик. Қачон сен Қуръонда Роббингнинг ёлғиз Ўзини зикр қилсанг, улар ортларига ўгирилиб қочурлар'}, {'chapter': 17, 'verse': 47, 'text': 'نَّحۡنُ أَعۡلَمُ بِمَا يَسۡتَمِعُونَ بِهِۦٓ إِذۡ يَسۡتَمِعُونَ إِلَيۡكَ وَإِذۡ هُمۡ نَجۡوَىٰٓ إِذۡ يَقُولُ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا\nБиз улар нима сабабдан тинглаётганларини, улар сени тинглаётганларида ўзаро пичирлашаётганларини, золимларнинг: «Сизлар сеҳрланган кишига эргашяпсиз, холос», деганларини ҳам яхши билурмиз'}, {'chapter': 17, 'verse': 48, 'text': 'ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَالَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا\nУлар сенга қандай мисол келтирганларига назар сол! Бас, залолатга кетдилар ва (тўғри) йўлга қодир бўлмаслар'}, {'chapter': 17, 'verse': 49, 'text': 'وَقَالُوٓاْ أَءِذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدٗا\nУлар: «Суяк ва титилган тупроқ бўлганимиздан кейин ҳам, яна янгидан яратилиб тирилтириламизми?» дедилар'}, {'chapter': 17, 'verse': 50, 'text': '۞قُلۡ كُونُواْ حِجَارَةً أَوۡ حَدِيدًا\nСен:«Тош ёки темир бўлиб олинглар'}, {'chapter': 17, 'verse': 51, 'text': 'أَوۡ خَلۡقٗا مِّمَّا يَكۡبُرُ فِي صُدُورِكُمۡۚ فَسَيَقُولُونَ مَن يُعِيدُنَاۖ قُلِ ٱلَّذِي فَطَرَكُمۡ أَوَّلَ مَرَّةٖۚ فَسَيُنۡغِضُونَ إِلَيۡكَ رُءُوسَهُمۡ وَيَقُولُونَ مَتَىٰ هُوَۖ قُلۡ عَسَىٰٓ أَن يَكُونَ قَرِيبٗا\nЁки кўнгилларингизда катта кўринган бошқа бирон махлуқ (бўлиб олинглар)», деб айт. Бас, улар: «Бизни ким қайта тирилтирур», дерлар. Сен:«Сизларни биринчи марта йўқдан бор қилган Зот», дегин. Улар сенга бошларини қимирлатурлар ва: «У қачон бўлур?» дерлар, Сен:«Шояд яқинда бўлса», дегин'}, {'chapter': 17, 'verse': 52, 'text': 'يَوۡمَ يَدۡعُوكُمۡ فَتَسۡتَجِيبُونَ بِحَمۡدِهِۦ وَتَظُنُّونَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗا\nУ кунда Ул зот сизни чақирур. Бас, сиз Унга ҳамд ила жавоб берурсиз ва (дунёда) фақат озгина турганлигингизни гумон қилурсиз'}, {'chapter': 17, 'verse': 53, 'text': 'وَقُل لِّعِبَادِي يَقُولُواْ ٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ ٱلشَّيۡطَٰنَ يَنزَغُ بَيۡنَهُمۡۚ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلۡإِنسَٰنِ عَدُوّٗا مُّبِينٗا\nБандаларимга айтгин, улар энг гўзал бўлган нарсани айтсинлар. Албатта, шайтон ораларини бузиб турадир. Албатта, шайтон инсон учун очиқ-ойдин душмандир'}, {'chapter': 17, 'verse': 54, 'text': 'رَّبُّكُمۡ أَعۡلَمُ بِكُمۡۖ إِن يَشَأۡ يَرۡحَمۡكُمۡ أَوۡ إِن يَشَأۡ يُعَذِّبۡكُمۡۚ وَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ وَكِيلٗا\nРоббингиз сизларни яхши билгувчи Зотдир. Хоҳласа, сизга раҳим қилар, хоҳласа сизни азоблар. Ва Биз сени улар устидан қўриқчи қилиб юборганимиз йўқ'}, {'chapter': 17, 'verse': 55, 'text': 'وَرَبُّكَ أَعۡلَمُ بِمَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَلَقَدۡ فَضَّلۡنَا بَعۡضَ ٱلنَّبِيِّـۧنَ عَلَىٰ بَعۡضٖۖ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا\nРоббинг осмонлару ердаги кимсаларни яхши билгувчи Зотдир. Батаҳқиқ, Биз баъзи Пайғамбарларни баъзиларидан афзал қилдик. Ва Довудга Забурни бердик'}, {'chapter': 17, 'verse': 56, 'text': 'قُلِ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُم مِّن دُونِهِۦ فَلَا يَمۡلِكُونَ كَشۡفَ ٱلضُّرِّ عَنكُمۡ وَلَا تَحۡوِيلًا\nСен: «Ундан ўзга ўзингизча гумон қилганларингизни чақиринг. Бас, улар сиздан зарарни кушойиш қила олишга ёки буриб юборишга молик эмаслар», деб айт'}, {'chapter': 17, 'verse': 57, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ يَدۡعُونَ يَبۡتَغُونَ إِلَىٰ رَبِّهِمُ ٱلۡوَسِيلَةَ أَيُّهُمۡ أَقۡرَبُ وَيَرۡجُونَ رَحۡمَتَهُۥ وَيَخَافُونَ عَذَابَهُۥٓۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحۡذُورٗا\nАна ўша дуо қилаётганлари Роббиларига васийла изларлар, қайсилари яқинроқ эканини (билмоқчи бўлурлар). Унинг раҳматини орзу қилурлар ва азобидан қўрқурлар. Албатта, Роббингнинг азоби ҳазир бўлишга лойиқдир'}, {'chapter': 17, 'verse': 58, 'text': 'وَإِن مِّن قَرۡيَةٍ إِلَّا نَحۡنُ مُهۡلِكُوهَا قَبۡلَ يَوۡمِ ٱلۡقِيَٰمَةِ أَوۡ مُعَذِّبُوهَا عَذَابٗا شَدِيدٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا\nҲеч бир шаҳар йўқки, Биз уни қиёмат кунидан аввал ҳалок қилгувчи бўлмасак ёки шиддатли азоб-ла азобловчи бўлмасак. Бу, китобда битилгандир'}, {'chapter': 17, 'verse': 59, 'text': 'وَمَا مَنَعَنَآ أَن نُّرۡسِلَ بِٱلۡأٓيَٰتِ إِلَّآ أَن كَذَّبَ بِهَا ٱلۡأَوَّلُونَۚ وَءَاتَيۡنَا ثَمُودَ ٱلنَّاقَةَ مُبۡصِرَةٗ فَظَلَمُواْ بِهَاۚ وَمَا نُرۡسِلُ بِٱلۡأٓيَٰتِ إِلَّا تَخۡوِيفٗا\nБизни оят-мўъжизаларни юборишимиздан фақат аввалгиларнинг уларни ёлғонга чиқарганлари ман қилди. Самудга туяни кўз очувчи (мўъжиза) қилиб бердик. Бас, унинг сабабидан (ўзларига) зулм қилдилар. Биз оят-мўъжизаларни фақат қўрқитиш учунгина юборамиз'}, {'chapter': 17, 'verse': 60, 'text': 'وَإِذۡ قُلۡنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِٱلنَّاسِۚ وَمَا جَعَلۡنَا ٱلرُّءۡيَا ٱلَّتِيٓ أَرَيۡنَٰكَ إِلَّا فِتۡنَةٗ لِّلنَّاسِ وَٱلشَّجَرَةَ ٱلۡمَلۡعُونَةَ فِي ٱلۡقُرۡءَانِۚ وَنُخَوِّفُهُمۡ فَمَا يَزِيدُهُمۡ إِلَّا طُغۡيَٰنٗا كَبِيرٗا\nБиз сенга: «Албатта, Роббинг одамларни иҳота қилиб ўраб олгандир», деганимизни эсла. Биз сенга кўрсатган нарсаларни ва Қуръонда лаънатланган дарахтни фақат одамлар учун синов қилдик, холос. Ва уларни қўрқитамиз. Бас, бу улар учун катта туғёндан бошқани зиёда қилмас'}, {'chapter': 17, 'verse': 61, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ قَالَ ءَأَسۡجُدُ لِمَنۡ خَلَقۡتَ طِينٗا\nФаришталарга: «Одамга сажда қилинглар!» деганимизда сажда қилганларини, магар иблис қилмаганини, «Сен лойдан яратган кимсага сажда қиламанми?» деганини эсла'}, {'chapter': 17, 'verse': 62, 'text': 'قَالَ أَرَءَيۡتَكَ هَٰذَا ٱلَّذِي كَرَّمۡتَ عَلَيَّ لَئِنۡ أَخَّرۡتَنِ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَأَحۡتَنِكَنَّ ذُرِّيَّتَهُۥٓ إِلَّا قَلِيلٗا\nУ: «Менга хабар бер-чи, мендан устун қилганинг шуми? Агар мени қиёмат кунигача қўйиб қўйсанг, унинг зурриётининг озгинасидан бошқасини жиловлаб оламан», деди'}, {'chapter': 17, 'verse': 63, 'text': 'قَالَ ٱذۡهَبۡ فَمَن تَبِعَكَ مِنۡهُمۡ فَإِنَّ جَهَنَّمَ جَزَآؤُكُمۡ جَزَآءٗ مَّوۡفُورٗا\nУ зот: «Боравер, улардан ким сенга эргашса, бас, албатта, жаҳаннам тўлиқ жазоингиз бўлур'}, {'chapter': 17, 'verse': 64, 'text': 'وَٱسۡتَفۡزِزۡ مَنِ ٱسۡتَطَعۡتَ مِنۡهُم بِصَوۡتِكَ وَأَجۡلِبۡ عَلَيۡهِم بِخَيۡلِكَ وَرَجِلِكَ وَشَارِكۡهُمۡ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِ وَعِدۡهُمۡۚ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا\nУлардан кимига кучинг етса, овозинг ила қўзғат, уларнинг устига отлиқ ва пиёда аскарингни торт, уларга молларда ва болаларда шерик бўл ва ваъдалар қил», деди. Шайтон уларга ғурурдан бошқани ваъда қилмас'}, {'chapter': 17, 'verse': 65, 'text': 'إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٞۚ وَكَفَىٰ بِرَبِّكَ وَكِيلٗا\nАлбатта, Менинг бандаларим устидан сенинг султонинг йўқ. Роббинг Ўзи вакилликка кифоядир'}, {'chapter': 17, 'verse': 66, 'text': 'رَّبُّكُمُ ٱلَّذِي يُزۡجِي لَكُمُ ٱلۡفُلۡكَ فِي ٱلۡبَحۡرِ لِتَبۡتَغُواْ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ كَانَ بِكُمۡ رَحِيمٗا\nРоббингиз, Унинг фазлидан талаб қилишингиз учун денгизда кемани юргизиб қўйган Зотдир. Албатта, у сизга раҳимлидир'}, {'chapter': 17, 'verse': 67, 'text': 'وَإِذَا مَسَّكُمُ ٱلضُّرُّ فِي ٱلۡبَحۡرِ ضَلَّ مَن تَدۡعُونَ إِلَّآ إِيَّاهُۖ فَلَمَّا نَجَّىٰكُمۡ إِلَى ٱلۡبَرِّ أَعۡرَضۡتُمۡۚ وَكَانَ ٱلۡإِنسَٰنُ كَفُورًا\nҚачон сизни денгизда фалокат тутса, Ундан ўзга илтижо қиладиганларингиз ғойиб бўлур. Қачонки сизга нажот бериб қуруқликка чиқарса, юз ўгирасиз. Инсон ўзи куфрон(и неъмат) қилгувчидир'}, {'chapter': 17, 'verse': 68, 'text': 'أَفَأَمِنتُمۡ أَن يَخۡسِفَ بِكُمۡ جَانِبَ ٱلۡبَرِّ أَوۡ يُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗا ثُمَّ لَا تَجِدُواْ لَكُمۡ وَكِيلًا\nСизни қуруқликнинг бир томонига ютқазиб юборишидан ёки устингиздан тошли шамол юборишидан, сўнгра ўзингизга қутқарувчи топа олмай қолишингиздан хотиржаммисиз'}, {'chapter': 17, 'verse': 69, 'text': 'أَمۡ أَمِنتُمۡ أَن يُعِيدَكُمۡ فِيهِ تَارَةً أُخۡرَىٰ فَيُرۡسِلَ عَلَيۡكُمۡ قَاصِفٗا مِّنَ ٱلرِّيحِ فَيُغۡرِقَكُم بِمَا كَفَرۡتُمۡ ثُمَّ لَا تَجِدُواْ لَكُمۡ عَلَيۡنَا بِهِۦ تَبِيعٗا\nЁки сизни у(денгиз)га яна бир марта қайтариб, устингиздан шиддатли шамол юбориб, куфр қилган нарсангиз сабабли ғарқ қилиб юборишидан ва сиз ўзингизга Биздан ўч олувчи топа олмай қолишингиздан хотиржаммисиз'}, {'chapter': 17, 'verse': 70, 'text': '۞وَلَقَدۡ كَرَّمۡنَا بَنِيٓ ءَادَمَ وَحَمَلۡنَٰهُمۡ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَىٰ كَثِيرٖ مِّمَّنۡ خَلَقۡنَا تَفۡضِيلٗا\nБатаҳқиқ, Биз Бани Одамни азизу мукаррам қилиб қўйдик ва уларни қуруқлигу денгизда (улов-ла) кўтардик ҳамда уларни пок нарсалар ила ризқлантирдик ва уни Ўзимиз яратган кўп нарсалардан мутлақо афзал қилиб қўйдик'}, {'chapter': 17, 'verse': 71, 'text': 'يَوۡمَ نَدۡعُواْ كُلَّ أُنَاسِۭ بِإِمَٰمِهِمۡۖ فَمَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَأُوْلَـٰٓئِكَ يَقۡرَءُونَ كِتَٰبَهُمۡ وَلَا يُظۡلَمُونَ فَتِيلٗا\nБиз барча одамларни ўз имомлари ила чақирадиган кунни (эсла). Кимга китоби ўнг томонидан берилса, бас, ана ўшалар китобларини ўқирлар ва уларга қилчалик зулм қилинмас'}, {'chapter': 17, 'verse': 72, 'text': 'وَمَن كَانَ فِي هَٰذِهِۦٓ أَعۡمَىٰ فَهُوَ فِي ٱلۡأٓخِرَةِ أَعۡمَىٰ وَأَضَلُّ سَبِيلٗا\nКим бу дунёда кўр-гумроҳ бўлса, у охиратда янада кўр-гумроҳ ва янада йўлдан адашганроқ бўладир'}, {'chapter': 17, 'verse': 73, 'text': 'وَإِن كَادُواْ لَيَفۡتِنُونَكَ عَنِ ٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ لِتَفۡتَرِيَ عَلَيۡنَا غَيۡرَهُۥۖ وَإِذٗا لَّٱتَّخَذُوكَ خَلِيلٗا\nСени Биз ваҳий қилган нарсадан фитна орқали буриб, Бизнинг шаънимизга бошқасини уйдиришингга ундашларига оз қолди. Шундай бўлган тақдирда, албатта, сени дўст тутар эдилар'}, {'chapter': 17, 'verse': 74, 'text': 'وَلَوۡلَآ أَن ثَبَّتۡنَٰكَ لَقَدۡ كِدتَّ تَرۡكَنُ إِلَيۡهِمۡ شَيۡـٔٗا قَلِيلًا\nАгар Биз сени собит қилмаганимизда, батаҳқиқ, уларга бир оз бўлса-да, мойил бўлишингга оз қолар эди'}, {'chapter': 17, 'verse': 75, 'text': 'إِذٗا لَّأَذَقۡنَٰكَ ضِعۡفَ ٱلۡحَيَوٰةِ وَضِعۡفَ ٱلۡمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيۡنَا نَصِيرٗا\nШундай бўлган тақдирда Биз сенга ҳаёт (азобини) ҳам икки ҳисса, мамот (азобини) ҳам икки ҳисса тоттирган бўлардик. Сўнгра, ўзинг учун Бизга қарши ёрдамчи топа олмас эдинг'}, {'chapter': 17, 'verse': 76, 'text': 'وَإِن كَادُواْ لَيَسۡتَفِزُّونَكَ مِنَ ٱلۡأَرۡضِ لِيُخۡرِجُوكَ مِنۡهَاۖ وَإِذٗا لَّا يَلۡبَثُونَ خِلَٰفَكَ إِلَّا قَلِيلٗا\nУлар сени бу ерда безовта қилиб, ундан сени чиқариб юборишларига оз қолди. Шундай бўлган тақдирда, сендан кейин улар озгина муддатдан бошқа тура олмаслар'}, {'chapter': 17, 'verse': 77, 'text': 'سُنَّةَ مَن قَدۡ أَرۡسَلۡنَا قَبۡلَكَ مِن رُّسُلِنَاۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحۡوِيلًا\nБу сендан олдин юборган Пайғамбарларимизнинг суннатидир. Бизнинг суннатимизга ўзгартириш топа олмайсан'}, {'chapter': 17, 'verse': 78, 'text': 'أَقِمِ ٱلصَّلَوٰةَ لِدُلُوكِ ٱلشَّمۡسِ إِلَىٰ غَسَقِ ٱلَّيۡلِ وَقُرۡءَانَ ٱلۡفَجۡرِۖ إِنَّ قُرۡءَانَ ٱلۡفَجۡرِ كَانَ مَشۡهُودٗا\nҚуёш оғишидан то тун қоронғусигача намозни тўкис адо қил. «Фажр Қуръони»ни ҳам. Албатта, «фажр Қуръони» гувоҳлантирилгандир'}, {'chapter': 17, 'verse': 79, 'text': 'وَمِنَ ٱلَّيۡلِ فَتَهَجَّدۡ بِهِۦ نَافِلَةٗ لَّكَ عَسَىٰٓ أَن يَبۡعَثَكَ رَبُّكَ مَقَامٗا مَّحۡمُودٗا\nКечанинг баъзисида у ила бедор бўл. Сенга нофила бўлсин. Шоядки, Роббинг сени мақтовли мақомда тирилтирса'}, {'chapter': 17, 'verse': 80, 'text': 'وَقُل رَّبِّ أَدۡخِلۡنِي مُدۡخَلَ صِدۡقٖ وَأَخۡرِجۡنِي مُخۡرَجَ صِدۡقٖ وَٱجۡعَل لِّي مِن لَّدُنكَ سُلۡطَٰنٗا نَّصِيرٗا\nВа: «Эй Роббим, мени кирадиган жойимга содиқлик ила киритиб, чиқадиган жойимга содиқлик билан чиқаргин ва менга Ўз ҳузурингдан ёрдам берувчи қувват ато эт», дегин'}, {'chapter': 17, 'verse': 81, 'text': 'وَقُلۡ جَآءَ ٱلۡحَقُّ وَزَهَقَ ٱلۡبَٰطِلُۚ إِنَّ ٱلۡبَٰطِلَ كَانَ زَهُوقٗا\nВа: «Ҳақ келди, ботил йўқ бўлди. Зотан, ботил доимо йўқ бўлгувчидир»'}, {'chapter': 17, 'verse': 82, 'text': 'وَنُنَزِّلُ مِنَ ٱلۡقُرۡءَانِ مَا هُوَ شِفَآءٞ وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ وَلَا يَزِيدُ ٱلظَّـٰلِمِينَ إِلَّا خَسَارٗا\nБиз Қуръонни мўминлар учун шифо ва раҳмат ўлароқ нозил қилурмиз. У золимларга зиёндан бошқани зиёда қилмас'}, {'chapter': 17, 'verse': 83, 'text': 'وَإِذَآ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ كَانَ يَـُٔوسٗا\nҚачон Биз инсонга неъмат ато этсак, юз ўгирур ва четга бурилиб, узоқлашур. Қачон уни ёмонлик тутса, ноумид бўлур'}, {'chapter': 17, 'verse': 84, 'text': 'قُلۡ كُلّٞ يَعۡمَلُ عَلَىٰ شَاكِلَتِهِۦ فَرَبُّكُمۡ أَعۡلَمُ بِمَنۡ هُوَ أَهۡدَىٰ سَبِيلٗا\nСен: «Ҳар ким ўз ҳолича амал қилур. Бас, Роббингиз ким тўғри йўлдалигини яхши билгувчидир», деб айт'}, {'chapter': 17, 'verse': 85, 'text': 'وَيَسۡـَٔلُونَكَ عَنِ ٱلرُّوحِۖ قُلِ ٱلرُّوحُ مِنۡ أَمۡرِ رَبِّي وَمَآ أُوتِيتُم مِّنَ ٱلۡعِلۡمِ إِلَّا قَلِيلٗا\nВа сендан руҳ ҳақида сўрарлар. Сен: «Руҳ Роббимнинг ишидир. Сизга жуда оз илм берилгандир», деб айт'}, {'chapter': 17, 'verse': 86, 'text': 'وَلَئِن شِئۡنَا لَنَذۡهَبَنَّ بِٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ ثُمَّ لَا تَجِدُ لَكَ بِهِۦ عَلَيۡنَا وَكِيلًا\nАгар хоҳласак, сенга ваҳий қилган нарсаларимизни кетказамиз. Сўнгра, Бизга қарши ўзингга вакил топа олмассан'}, {'chapter': 17, 'verse': 87, 'text': 'إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّ فَضۡلَهُۥ كَانَ عَلَيۡكَ كَبِيرٗا\nМагар Аллоҳдан марҳамат бўлсагина. Албатта, Унинг фазлу карами сен учун каттадир'}, {'chapter': 17, 'verse': 88, 'text': 'قُل لَّئِنِ ٱجۡتَمَعَتِ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَىٰٓ أَن يَأۡتُواْ بِمِثۡلِ هَٰذَا ٱلۡقُرۡءَانِ لَا يَأۡتُونَ بِمِثۡلِهِۦ وَلَوۡ كَانَ بَعۡضُهُمۡ لِبَعۡضٖ ظَهِيرٗا\n«Агар инсу жинлар тўпланиб, ушбу Қуръонга ўхшаш нарса келтирмоқчи бўлсалар, бунда баъзилари баъзиларига ёрдамчи бўлсалар ҳам, унга ўхшашини келтира олмаслар», деб айт'}, {'chapter': 17, 'verse': 89, 'text': 'وَلَقَدۡ صَرَّفۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا\nБатаҳқиқ, ушбу Қуръонда одамларга турли мисолларни баён қилдик. Бас, одамларнинг кўплари куфрдан бошқа нарсадан бош тортдилар'}, {'chapter': 17, 'verse': 90, 'text': 'وَقَالُواْ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ تَفۡجُرَ لَنَا مِنَ ٱلۡأَرۡضِ يَنۢبُوعًا\nУлар: «Токи бизга ердан чашма отилтирмагунингча, зинҳор сенга иймон келтирмасмиз», дедилар'}, {'chapter': 17, 'verse': 91, 'text': 'أَوۡ تَكُونَ لَكَ جَنَّةٞ مِّن نَّخِيلٖ وَعِنَبٖ فَتُفَجِّرَ ٱلۡأَنۡهَٰرَ خِلَٰلَهَا تَفۡجِيرًا\n«Ёки сенинг хурмо ва узум боғинг бўлиб ва уларнинг орасидан анҳорларни отилтириб чиқармагунингча'}, {'chapter': 17, 'verse': 92, 'text': 'أَوۡ تُسۡقِطَ ٱلسَّمَآءَ كَمَا زَعَمۡتَ عَلَيۡنَا كِسَفًا أَوۡ تَأۡتِيَ بِٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ قَبِيلًا\nЁки ўзинг даъво қилганингдек, устимиздан осмонни парча-парча қилиб туширмагунингча ёхуд Аллоҳни ва фаришталарни олдимизга келтирмагунингча» (сенга иймон келтирмасмиз, дедилар)'}, {'chapter': 17, 'verse': 93, 'text': 'أَوۡ يَكُونَ لَكَ بَيۡتٞ مِّن زُخۡرُفٍ أَوۡ تَرۡقَىٰ فِي ٱلسَّمَآءِ وَلَن نُّؤۡمِنَ لِرُقِيِّكَ حَتَّىٰ تُنَزِّلَ عَلَيۡنَا كِتَٰبٗا نَّقۡرَؤُهُۥۗ قُلۡ سُبۡحَانَ رَبِّي هَلۡ كُنتُ إِلَّا بَشَرٗا رَّسُولٗا\n«Ёки сенинг олтиндан безалган уйинг бўлмагунича, ёхуд осмонга кўтарилмагунинг-ча, ўша кўтарилишингга ҳам токи биз ўқишимиз учун китоб келтирмагунингча, ишонмаймиз», (дедилар). Сен: «Роббим пок бўлди. Мен фақат башар Пайғамбардан бошқа нарса эмасман», дегин'}, {'chapter': 17, 'verse': 94, 'text': 'وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُوٓاْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰٓ إِلَّآ أَن قَالُوٓاْ أَبَعَثَ ٱللَّهُ بَشَرٗا رَّسُولٗا\nОдамларга ҳидоят келган пайтда иймон келтиришларидан ман қилган нарса, фақат, Аллоҳ башардан Пайғамбар юборадими, дейишлари бўлди'}, {'chapter': 17, 'verse': 95, 'text': 'قُل لَّوۡ كَانَ فِي ٱلۡأَرۡضِ مَلَـٰٓئِكَةٞ يَمۡشُونَ مُطۡمَئِنِّينَ لَنَزَّلۡنَا عَلَيۡهِم مِّنَ ٱلسَّمَآءِ مَلَكٗا رَّسُولٗا\nСен: «Агар ер юзида фаришталар хотиржам юрганларида, уларга осмондан фаришта Пайғамбар туширган бўлар эдик», деб айт'}, {'chapter': 17, 'verse': 96, 'text': 'قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا\nСен: «Мен билан сизнинг орамизда гувоҳликка Аллоҳнинг Ўзи кифоя қилур. Албатта, У Ўз бандаларидан хабардор ва уларни кўриб тургувчи Зотдир», деб айт'}, {'chapter': 17, 'verse': 97, 'text': 'وَمَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُمۡ أَوۡلِيَآءَ مِن دُونِهِۦۖ وَنَحۡشُرُهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ عَلَىٰ وُجُوهِهِمۡ عُمۡيٗا وَبُكۡمٗا وَصُمّٗاۖ مَّأۡوَىٰهُمۡ جَهَنَّمُۖ كُلَّمَا خَبَتۡ زِدۡنَٰهُمۡ سَعِيرٗا\nАллоҳ кимни ҳидоят қилса, ўша ҳидоят топгувчидир. У кимни адаштирса, уларга Ундан ўзга зинҳор валийлар топа олмассан. Уларни қиёмат куни юзтубан, кўр, кар, соқов ҳолларида тўплаймиз. Уларнинг турар жойлари жаҳаннамдир. У қачон сокин бўлса, уларга оловни зиёда қиламиз'}, {'chapter': 17, 'verse': 98, 'text': 'ذَٰلِكَ جَزَآؤُهُم بِأَنَّهُمۡ كَفَرُواْ بِـَٔايَٰتِنَا وَقَالُوٓاْ أَءِذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدًا\nАна ўша, Бизнинг оятларимизга куфр келтирганлари ва «Агар суяк ва титилган тупроқ бўлсак ҳам, албатта янгитдан яратилиб, қайта тирилтирилурмизми?!» деганлари учун жазоларидир'}, {'chapter': 17, 'verse': 99, 'text': '۞أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ قَادِرٌ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۡ وَجَعَلَ لَهُمۡ أَجَلٗا لَّا رَيۡبَ فِيهِ فَأَبَى ٱلظَّـٰلِمُونَ إِلَّا كُفُورٗا\nУлар осмонлару ерни яратган Аллоҳ уларга ўхшашини яратишга қодир эканини ва улар учун шак-шубҳасиз ажал белгилаганини билмадиларми?! Бас, золимлар куфрдан бошқа нарсадан бош тортдилар'}, {'chapter': 17, 'verse': 100, 'text': 'قُل لَّوۡ أَنتُمۡ تَمۡلِكُونَ خَزَآئِنَ رَحۡمَةِ رَبِّيٓ إِذٗا لَّأَمۡسَكۡتُمۡ خَشۡيَةَ ٱلۡإِنفَاقِۚ وَكَانَ ٱلۡإِنسَٰنُ قَتُورٗا\nСен: «Агар сизлар Роббим раҳмати хазиналарига эга бўлсангиз ҳам, уни сарфлашдан қўрқиб, мумсиклик қилган бўлар эдингиз. Инсон зоти, ўзи, бахил бўлур», деб айт'}, {'chapter': 17, 'verse': 101, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ تِسۡعَ ءَايَٰتِۭ بَيِّنَٰتٖۖ فَسۡـَٔلۡ بَنِيٓ إِسۡرَـٰٓءِيلَ إِذۡ جَآءَهُمۡ فَقَالَ لَهُۥ فِرۡعَوۡنُ إِنِّي لَأَظُنُّكَ يَٰمُوسَىٰ مَسۡحُورٗا\nБатаҳқиқ, Биз Мусога тўққизта очиқ-ойдин оят-мўъжиза бердик. Бани Исроилдан уларга у (Мусо) келган пайти(нима бўлгани)ни сўрагин. Бас, Фиръавн унга: «Албатта, мен сени, эй Мусо, сеҳрлангансан деб ўйлайман», деди'}, {'chapter': 17, 'verse': 102, 'text': 'قَالَ لَقَدۡ عَلِمۡتَ مَآ أَنزَلَ هَـٰٓؤُلَآءِ إِلَّا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ بَصَآئِرَ وَإِنِّي لَأَظُنُّكَ يَٰفِرۡعَوۡنُ مَثۡبُورٗا\nУ: «Анавиларни кўзни очгувчи қилиб осмонлару ернинг Роббидан ўзга ҳеч бир Зот туширмаганини яхши биласан. Албатта, мен сени, эй Фиръавн, ҳалок бўлгувчисан деб ўйлайман», деди'}, {'chapter': 17, 'verse': 103, 'text': 'فَأَرَادَ أَن يَسۡتَفِزَّهُم مِّنَ ٱلۡأَرۡضِ فَأَغۡرَقۡنَٰهُ وَمَن مَّعَهُۥ جَمِيعٗا\nБас, уларни ердан қўзғатиб чиқармоқчи бўлди. Шунда, Биз уни ва у билан бирга бўлганларни жамлаб ғарқ қилдик'}, {'chapter': 17, 'verse': 104, 'text': 'وَقُلۡنَا مِنۢ بَعۡدِهِۦ لِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱسۡكُنُواْ ٱلۡأَرۡضَ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ جِئۡنَا بِكُمۡ لَفِيفٗا\nУ(Фиръавн)дан сўнг Бани Исроилга: «Мана шу ерни маскан тутинглар. Бас, охират ваъдаси келган чоғида, ҳаммангизни тўплаб келтирамиз», дедик'}, {'chapter': 17, 'verse': 105, 'text': 'وَبِٱلۡحَقِّ أَنزَلۡنَٰهُ وَبِٱلۡحَقِّ نَزَلَۗ وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا\nУни ҳақ ила нозил қилдик ва ҳақ ила нозил бўлди. Биз сени фақат хушхабар бергувчи ва огоҳлантиргувчи этиб юбордик, холос'}, {'chapter': 17, 'verse': 106, 'text': 'وَقُرۡءَانٗا فَرَقۡنَٰهُ لِتَقۡرَأَهُۥ عَلَى ٱلنَّاسِ عَلَىٰ مُكۡثٖ وَنَزَّلۡنَٰهُ تَنزِيلٗا\nВа Қуръонни одамларга аста-секин ўқиб беришинг учун бўлиб қўйдик ва бўлак-бўлак қилиб туширдик'}, {'chapter': 17, 'verse': 107, 'text': 'قُلۡ ءَامِنُواْ بِهِۦٓ أَوۡ لَا تُؤۡمِنُوٓاْۚ إِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مِن قَبۡلِهِۦٓ إِذَا يُتۡلَىٰ عَلَيۡهِمۡ يَخِرُّونَۤ لِلۡأَذۡقَانِۤ سُجَّدٗاۤ\nСен: «Унга иймон келтиринглар ёки иймон келтирманглар — бари бир. Албатта, ундан олдин илм берилганлар у уларга тиловат қилинганда юзлари ила саждага йиқилурлар», деб айт'}, {'chapter': 17, 'verse': 108, 'text': 'وَيَقُولُونَ سُبۡحَٰنَ رَبِّنَآ إِن كَانَ وَعۡدُ رَبِّنَا لَمَفۡعُولٗا\nВа: «Роббимиз мутлақо пок бўлди. Шубҳасиз, Роббимизнинг ваъдаси амалга ошажак», дерлар'}, {'chapter': 17, 'verse': 109, 'text': 'وَيَخِرُّونَ لِلۡأَذۡقَانِ يَبۡكُونَ وَيَزِيدُهُمۡ خُشُوعٗا۩\nВа юзлари ила йиқилиб йиғларлар ва у хокисорликларини зиёда қилур'}, {'chapter': 17, 'verse': 110, 'text': 'قُلِ ٱدۡعُواْ ٱللَّهَ أَوِ ٱدۡعُواْ ٱلرَّحۡمَٰنَۖ أَيّٗا مَّا تَدۡعُواْ فَلَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ وَلَا تَجۡهَرۡ بِصَلَاتِكَ وَلَا تُخَافِتۡ بِهَا وَٱبۡتَغِ بَيۡنَ ذَٰلِكَ سَبِيلٗا\nСен: «Аллоҳга дуо қилинглар, Роҳманга дуо қилинглар, қайсисига дуо қилсангиз ҳам, бари бир. Гўзал исмлар Уникидир», деб айт. Намозингни жуда баланд овоз ила ўқима, уни жуда махфий ҳам ўқима, бунинг орасида бир йўл тут'}, {'chapter': 17, 'verse': 111, 'text': 'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَلَمۡ يَكُن لَّهُۥ وَلِيّٞ مِّنَ ٱلذُّلِّۖ وَكَبِّرۡهُ تَكۡبِيرَۢا\nСен: «Ҳамд бўлсин, фарзанд тутмаган, мулкда шериги бўлмаган ва хорликдан қутқаргувчи дўстга зор бўлмаган Аллоҳга», дегин. Ва Уни мутлақо улуғла'}], '18': [{'chapter': 18, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِيٓ أَنزَلَ عَلَىٰ عَبۡدِهِ ٱلۡكِتَٰبَ وَلَمۡ يَجۡعَل لَّهُۥ عِوَجَاۜ\nЎз бандасига китоб нозил этган ва унда ҳеч бир эгрилик қилмаган Аллоҳга ҳамд бўлсин'}, {'chapter': 18, 'verse': 2, 'text': 'قَيِّمٗا لِّيُنذِرَ بَأۡسٗا شَدِيدٗا مِّن لَّدُنۡهُ وَيُبَشِّرَ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرًا حَسَنٗا\nУни тўғри қилган; Ўз ҳузуридан бўладиган шиддатли азобдан огоҳлантирадиган; яхши амалларни қиладиган мўминларга, уларга гўзал ажр бўлиши башоратини берадиган қилган Зотга'}, {'chapter': 18, 'verse': 3, 'text': 'مَّـٰكِثِينَ فِيهِ أَبَدٗا\n(Улар) у(ажр)да абадий қолгувчидирлар'}, {'chapter': 18, 'verse': 4, 'text': 'وَيُنذِرَ ٱلَّذِينَ قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗا\nВа, Аллоҳ Ўзига бола тутди, деганларни огоҳлантириш учун'}, {'chapter': 18, 'verse': 5, 'text': 'مَّا لَهُم بِهِۦ مِنۡ عِلۡمٖ وَلَا لِأٓبَآئِهِمۡۚ كَبُرَتۡ كَلِمَةٗ تَخۡرُجُ مِنۡ أَفۡوَٰهِهِمۡۚ إِن يَقُولُونَ إِلَّا كَذِبٗا\nНа уларнинг ва на ота-боболарининг бу ҳақда илми йўқдир. Оғизларидан чиқадиган сўз қандай ҳам оғир. Улар ёлғондан бошқани айтмаслар'}, {'chapter': 18, 'verse': 6, 'text': 'فَلَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ عَلَىٰٓ ءَاثَٰرِهِمۡ إِن لَّمۡ يُؤۡمِنُواْ بِهَٰذَا ٱلۡحَدِيثِ أَسَفًا\nУлар бу сўзга иймон келтирмасалар, эҳтимол, уларнинг ортидан таассуф ила ўзингни ҳалок этмоқчи бўларсан'}, {'chapter': 18, 'verse': 7, 'text': 'إِنَّا جَعَلۡنَا مَا عَلَى ٱلۡأَرۡضِ زِينَةٗ لَّهَا لِنَبۡلُوَهُمۡ أَيُّهُمۡ أَحۡسَنُ عَمَلٗا\nБиз ер юзидаги нарсаларни, уларнинг қайсилари гўзалроқ амал қилишини синаш учун, зебу зийнат қилиб қўйганмиз'}, {'chapter': 18, 'verse': 8, 'text': 'وَإِنَّا لَجَٰعِلُونَ مَا عَلَيۡهَا صَعِيدٗا جُرُزًا\nАлбатта, Биз унинг юзидаги нарсаларни қуп-қуруқ тупроққа айлантиргувчимиз'}, {'chapter': 18, 'verse': 9, 'text': 'أَمۡ حَسِبۡتَ أَنَّ أَصۡحَٰبَ ٱلۡكَهۡفِ وَٱلرَّقِيمِ كَانُواْ مِنۡ ءَايَٰتِنَا عَجَبًا\nКаҳф ва битик эгаларини Бизнинг ажойиб оят — белгиларимиздан деб ўйладингми'}, {'chapter': 18, 'verse': 10, 'text': 'إِذۡ أَوَى ٱلۡفِتۡيَةُ إِلَى ٱلۡكَهۡفِ فَقَالُواْ رَبَّنَآ ءَاتِنَا مِن لَّدُنكَ رَحۡمَةٗ وَهَيِّئۡ لَنَا مِنۡ أَمۡرِنَا رَشَدٗا\nҚачонки йигитлар каҳфдан жой олганларида: «Эй Роббимиз, бизга Ўз ҳузурингдан раҳмат бергин ва ишимизни ўзинг тўғрилагин», дедилар'}, {'chapter': 18, 'verse': 11, 'text': 'فَضَرَبۡنَا عَلَىٰٓ ءَاذَانِهِمۡ فِي ٱلۡكَهۡفِ سِنِينَ عَدَدٗا\nБас, каҳфда уларнинг қулоқларига бир неча йиллар (парда) урдик'}, {'chapter': 18, 'verse': 12, 'text': 'ثُمَّ بَعَثۡنَٰهُمۡ لِنَعۡلَمَ أَيُّ ٱلۡحِزۡبَيۡنِ أَحۡصَىٰ لِمَا لَبِثُوٓاْ أَمَدٗا\nСўнгра, икки гуруҳдан қайси бири уларнинг турган муддатини ҳисобловчироқ эканини билиш учун уларни уйғотдик'}, {'chapter': 18, 'verse': 13, 'text': 'نَّحۡنُ نَقُصُّ عَلَيۡكَ نَبَأَهُم بِٱلۡحَقِّۚ إِنَّهُمۡ فِتۡيَةٌ ءَامَنُواْ بِرَبِّهِمۡ وَزِدۡنَٰهُمۡ هُدٗى\nБиз сенга уларнинг хабарларини ҳақ ила қисса этиб берамиз. Албатта, улар Роббиларига иймон келтирган йигитлардир. Ва Биз уларга янада зиёда ҳидоят бердик'}, {'chapter': 18, 'verse': 14, 'text': 'وَرَبَطۡنَا عَلَىٰ قُلُوبِهِمۡ إِذۡ قَامُواْ فَقَالُواْ رَبُّنَا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَن نَّدۡعُوَاْ مِن دُونِهِۦٓ إِلَٰهٗاۖ لَّقَدۡ قُلۡنَآ إِذٗا شَطَطًا\nУларнинг қалбларини мустаҳкамладик. Қачонки турдилар, бас: «Бизнинг Роббимиз осмонлару ернинг Роббидир. Ундан ўзгани ҳаргиз илоҳ дея дуо қилмаймиз. Агар шундай қилсак, ноҳақ сўзни айтган бўлурмиз'}, {'chapter': 18, 'verse': 15, 'text': 'هَـٰٓؤُلَآءِ قَوۡمُنَا ٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗۖ لَّوۡلَا يَأۡتُونَ عَلَيۡهِم بِسُلۡطَٰنِۭ بَيِّنٖۖ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا\nАна у қавмимиз ундан ўзга худолар тутдилар. Уларга равшан ҳужжатлар келтира олармиканлар?! Аллоҳга ёлғон уйдиргандан ҳам золимроқ ким бор», дедилар'}, {'chapter': 18, 'verse': 16, 'text': 'وَإِذِ ٱعۡتَزَلۡتُمُوهُمۡ وَمَا يَعۡبُدُونَ إِلَّا ٱللَّهَ فَأۡوُۥٓاْ إِلَى ٱلۡكَهۡفِ يَنشُرۡ لَكُمۡ رَبُّكُم مِّن رَّحۡمَتِهِۦ وَيُهَيِّئۡ لَكُم مِّنۡ أَمۡرِكُم مِّرۡفَقٗا\n«Модомики, улардан ва улар ибодат қилаётган, Аллоҳдан бошқа нарсадан узоқлашмоқчи бўлсангиз, каҳфдан паноҳ жой олингиз. Роббингиз сизга Ўз раҳматини таратур ва сизга ишингизда осонлик яратур», дедилар'}, {'chapter': 18, 'verse': 17, 'text': '۞وَتَرَى ٱلشَّمۡسَ إِذَا طَلَعَت تَّزَٰوَرُ عَن كَهۡفِهِمۡ ذَاتَ ٱلۡيَمِينِ وَإِذَا غَرَبَت تَّقۡرِضُهُمۡ ذَاتَ ٱلشِّمَالِ وَهُمۡ فِي فَجۡوَةٖ مِّنۡهُۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِۗ مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُۥ وَلِيّٗا مُّرۡشِدٗا\nҚуёш чиққанида уларнинг каҳфининг ўнг томонидан мойил бўлганини, ботганда эса, чап томонидан қийиб ўтганини кўрасан. Улар эса у(ғор)нинг кенг жойидадирлар. Ана шу ҳам Аллоҳнинг мўъжизаларидандир. Кимни Аллоҳ ҳидоятга бошласа, ўша ҳидоят топгувчидир. Кимни адаштирса, бас, ҳаргиз унга иршод қилувчи бирон дўст топа олмассан'}, {'chapter': 18, 'verse': 18, 'text': 'وَتَحۡسَبُهُمۡ أَيۡقَاظٗا وَهُمۡ رُقُودٞۚ وَنُقَلِّبُهُمۡ ذَاتَ ٱلۡيَمِينِ وَذَاتَ ٱلشِّمَالِۖ وَكَلۡبُهُم بَٰسِطٞ ذِرَاعَيۡهِ بِٱلۡوَصِيدِۚ لَوِ ٱطَّلَعۡتَ عَلَيۡهِمۡ لَوَلَّيۡتَ مِنۡهُمۡ فِرَارٗا وَلَمُلِئۡتَ مِنۡهُمۡ رُعۡبٗا\nУларни уйғоқ деб ўйлайсан, ҳолбуки, ухлаб ётурлар. Уларни гоҳ ўнгга, гоҳ чапга ағдариб турурмиз. Итлари эса, остонада олд оёқларини чўзиб ётур. Гар уларни кўрсайдинг, ортга ўгирилиб қочар ва улардан қўрқинчга тўлар эдинг'}, {'chapter': 18, 'verse': 19, 'text': 'وَكَذَٰلِكَ بَعَثۡنَٰهُمۡ لِيَتَسَآءَلُواْ بَيۡنَهُمۡۚ قَالَ قَآئِلٞ مِّنۡهُمۡ كَمۡ لَبِثۡتُمۡۖ قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۚ قَالُواْ رَبُّكُمۡ أَعۡلَمُ بِمَا لَبِثۡتُمۡ فَٱبۡعَثُوٓاْ أَحَدَكُم بِوَرِقِكُمۡ هَٰذِهِۦٓ إِلَى ٱلۡمَدِينَةِ فَلۡيَنظُرۡ أَيُّهَآ أَزۡكَىٰ طَعَامٗا فَلۡيَأۡتِكُم بِرِزۡقٖ مِّنۡهُ وَلۡيَتَلَطَّفۡ وَلَا يُشۡعِرَنَّ بِكُمۡ أَحَدًا\nВа шундай қилиб, уларни ўзаро сўрашлари учун уйғотдик. Улардан бири: «Қанча қолдинглар?» деди. Улар: «Бир кун ёки бир куннинг баъзисича қолдик», дейишди. «Қанча қолганингизни Роббингиз яхши билгувчидир, шаҳарга бирингизни ушбу пулингиз ила юборинг, назар солиб, яхши таомни танлаб, ундан ризқ келтирсин ва диққатли бўлсин, сизни бировга сездириб қўймасин», дейишди'}, {'chapter': 18, 'verse': 20, 'text': 'إِنَّهُمۡ إِن يَظۡهَرُواْ عَلَيۡكُمۡ يَرۡجُمُوكُمۡ أَوۡ يُعِيدُوكُمۡ فِي مِلَّتِهِمۡ وَلَن تُفۡلِحُوٓاْ إِذًا أَبَدٗا\n«Чунки, агар улар сизни қўлга олсалар, тошбўрон қиладилар ёки ўз миллатларига қайтарадилар. Ундай бўлса, зинҳор ва абадий нажот топмассиз», дейишди'}, {'chapter': 18, 'verse': 21, 'text': 'وَكَذَٰلِكَ أَعۡثَرۡنَا عَلَيۡهِمۡ لِيَعۡلَمُوٓاْ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَأَنَّ ٱلسَّاعَةَ لَا رَيۡبَ فِيهَآ إِذۡ يَتَنَٰزَعُونَ بَيۡنَهُمۡ أَمۡرَهُمۡۖ فَقَالُواْ ٱبۡنُواْ عَلَيۡهِم بُنۡيَٰنٗاۖ رَّبُّهُمۡ أَعۡلَمُ بِهِمۡۚ قَالَ ٱلَّذِينَ غَلَبُواْ عَلَىٰٓ أَمۡرِهِمۡ لَنَتَّخِذَنَّ عَلَيۡهِم مَّسۡجِدٗا\nШундай қилиб, Аллоҳнинг ваъдаси ҳақ эканини ва қиёмат соатида шубҳа йўқ эканини билишлари учун уларни (одамларга) билдирдик. Энди эса, улар иши ҳақида тортиша бошладилар: «Уларнинг устларига бино барпо қилинглар, Роббилари уларни яхши билгувчидир», дедилар. Ишларига ғолиб келганлар эса: «Уларнинг устларига, албатта, масжид қуриб олурмиз», дедилар'}, {'chapter': 18, 'verse': 22, 'text': 'سَيَقُولُونَ ثَلَٰثَةٞ رَّابِعُهُمۡ كَلۡبُهُمۡ وَيَقُولُونَ خَمۡسَةٞ سَادِسُهُمۡ كَلۡبُهُمۡ رَجۡمَۢا بِٱلۡغَيۡبِۖ وَيَقُولُونَ سَبۡعَةٞ وَثَامِنُهُمۡ كَلۡبُهُمۡۚ قُل رَّبِّيٓ أَعۡلَمُ بِعِدَّتِهِم مَّا يَعۡلَمُهُمۡ إِلَّا قَلِيلٞۗ فَلَا تُمَارِ فِيهِمۡ إِلَّا مِرَآءٗ ظَٰهِرٗا وَلَا تَسۡتَفۡتِ فِيهِم مِّنۡهُمۡ أَحَدٗا\nҲали улар: «Учтадирлар, итлари тўртинчиларидир», дерлар. Ва: «Бештадирлар, итлари олтинчиларидир», деб ғайбга тош отадилар. Ва: «Еттитадирлар, итлари саккизинчиларидир» ҳам дерлар. «Роббим уларнинг саноғини яхши билгувчидир. Уларни жуда оз киши билур», дегин. Бас, улар ҳақида тортишиб юрма. Тортишсанг ҳам, очиқ тортиш. Улар ҳақида ҳеч кимдан фатво ҳам сўрама'}, {'chapter': 18, 'verse': 23, 'text': 'وَلَا تَقُولَنَّ لِشَاْيۡءٍ إِنِّي فَاعِلٞ ذَٰلِكَ غَدًا\nЗинҳор бир нарсани: «Буни эртага албатта қилгувчиман», дема'}, {'chapter': 18, 'verse': 24, 'text': 'إِلَّآ أَن يَشَآءَ ٱللَّهُۚ وَٱذۡكُر رَّبَّكَ إِذَا نَسِيتَ وَقُلۡ عَسَىٰٓ أَن يَهۡدِيَنِ رَبِّي لِأَقۡرَبَ مِنۡ هَٰذَا رَشَدٗا\nМагар: «Аллоҳ хоҳласа», дегин. Қачонки унутсанг, Роббингни эсла ва: «Шоядки, Роббим мени бундан яхшироққа яқин бўлишимга ҳидоят қилса», дегин'}, {'chapter': 18, 'verse': 25, 'text': 'وَلَبِثُواْ فِي كَهۡفِهِمۡ ثَلَٰثَ مِاْئَةٖ سِنِينَ وَٱزۡدَادُواْ تِسۡعٗا\nВа каҳфларида уч юз йил қолдилар ва тўққиз йил зиёда ҳам қилдилар'}, {'chapter': 18, 'verse': 26, 'text': 'قُلِ ٱللَّهُ أَعۡلَمُ بِمَا لَبِثُواْۖ لَهُۥ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَبۡصِرۡ بِهِۦ وَأَسۡمِعۡۚ مَا لَهُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا يُشۡرِكُ فِي حُكۡمِهِۦٓ أَحَدٗا\n«Уларнинг қанча қолганларини Аллоҳ яхши билгувчидир. Осмонлару ернинг ғайби Унга хосдир. У нақадар яхши кўргувчи ва яхши эшитгувчи Зотдир. Уларга Ундан ўзга дўст йўқдир. Ва У Ўз ҳукмида ҳеч кимни шерик қилмас», дегин'}, {'chapter': 18, 'verse': 27, 'text': 'وَٱتۡلُ مَآ أُوحِيَ إِلَيۡكَ مِن كِتَابِ رَبِّكَۖ لَا مُبَدِّلَ لِكَلِمَٰتِهِۦ وَلَن تَجِدَ مِن دُونِهِۦ مُلۡتَحَدٗا\nСенга Роббинг китобидан ваҳий қилинганини тиловат қил. Унинг калималарини алмаштиргувчи йўқ. Ундан ўзга ҳеч қандай паноҳни топа олмассан'}, {'chapter': 18, 'verse': 28, 'text': 'وَٱصۡبِرۡ نَفۡسَكَ مَعَ ٱلَّذِينَ يَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ وَلَا تَعۡدُ عَيۡنَاكَ عَنۡهُمۡ تُرِيدُ زِينَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَا تُطِعۡ مَنۡ أَغۡفَلۡنَا قَلۡبَهُۥ عَن ذِكۡرِنَا وَٱتَّبَعَ هَوَىٰهُ وَكَانَ أَمۡرُهُۥ فُرُطٗا\nЎзингни эртаю кеч Роббиларига дуо қилиб, Унинг юзини ирода қилувчилар ила сабр этиб тут. Ҳаёти дунё зийнатини хоҳлаб, улардан икки кўзингни бурма. Қалбини Ўз зикримиздан ғофил қилиб қўйганларимизга ва ҳавойи нафсига эргашиб иши издан чиққанларга итоат қилма'}, {'chapter': 18, 'verse': 29, 'text': 'وَقُلِ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَن شَآءَ فَلۡيُؤۡمِن وَمَن شَآءَ فَلۡيَكۡفُرۡۚ إِنَّآ أَعۡتَدۡنَا لِلظَّـٰلِمِينَ نَارًا أَحَاطَ بِهِمۡ سُرَادِقُهَاۚ وَإِن يَسۡتَغِيثُواْ يُغَاثُواْ بِمَآءٖ كَٱلۡمُهۡلِ يَشۡوِي ٱلۡوُجُوهَۚ بِئۡسَ ٱلشَّرَابُ وَسَآءَتۡ مُرۡتَفَقًا\nСен: «Бу ҳақ Роббингиз томонидандир. Бас, ким хоҳласа, иймон келтирсин, ким хоҳласа, куфр келтирсин», дегин. Албатта, Биз золимларга деворлари уларни ўраб оладиган оловни тайёрлаб қўйганмиз. Агар ёрдам сўрасалар, эритилган маъдан каби юзларни қовурувчи сув билан «ёрдам» берилади. Нақадар ёмон шароб ва нақадар ёмон жой'}, {'chapter': 18, 'verse': 30, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ إِنَّا لَا نُضِيعُ أَجۡرَ مَنۡ أَحۡسَنَ عَمَلًا\nАлбатта, Биз иймон келтирган ва яхши амаллар қилганларнинг, гўзал иш қилганларнинг ажрини зое қилмасмиз'}, {'chapter': 18, 'verse': 31, 'text': 'أُوْلَـٰٓئِكَ لَهُمۡ جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَيَلۡبَسُونَ ثِيَابًا خُضۡرٗا مِّن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۚ نِعۡمَ ٱلثَّوَابُ وَحَسُنَتۡ مُرۡتَفَقٗا\nАна ўшаларга адн жаннатлар бордир. Остиларидан анҳорлар оқиб туради. У ерда олтиндан бўлган билакузуклар ила безанурлар, шойи иплардан яхши либослар киярлар. Улар у ерда сўриларда ёнбошлаган ҳолда бўлурлар. Нақадар яхши савоб ва нақадар яхши жой'}, {'chapter': 18, 'verse': 32, 'text': '۞وَٱضۡرِبۡ لَهُم مَّثَلٗا رَّجُلَيۡنِ جَعَلۡنَا لِأَحَدِهِمَا جَنَّتَيۡنِ مِنۡ أَعۡنَٰبٖ وَحَفَفۡنَٰهُمَا بِنَخۡلٖ وَجَعَلۡنَا بَيۡنَهُمَا زَرۡعٗا\nУларга икки кишини мисол қилиб келтир. Улардан бирига узумлардан икки боғ бердик ва уларни хурмо ила ўрадик ва ораларида экинзор қилдик'}, {'chapter': 18, 'verse': 33, 'text': 'كِلۡتَا ٱلۡجَنَّتَيۡنِ ءَاتَتۡ أُكُلَهَا وَلَمۡ تَظۡلِم مِّنۡهُ شَيۡـٔٗاۚ وَفَجَّرۡنَا خِلَٰلَهُمَا نَهَرٗا\nЎша икки боғ ҳосилларини беками-кўст берар ва ораларидан анҳор оқизиб қўйган эдик'}, {'chapter': 18, 'verse': 34, 'text': 'وَكَانَ لَهُۥ ثَمَرٞ فَقَالَ لِصَٰحِبِهِۦ وَهُوَ يُحَاوِرُهُۥٓ أَنَا۠ أَكۡثَرُ مِنكَ مَالٗا وَأَعَزُّ نَفَرٗا\nУнинг меваси бор эди. У ўз шериги ила гаплашар экан: «Менинг сендан молим кўп ва одамларим қувватлироқ», деди'}, {'chapter': 18, 'verse': 35, 'text': 'وَدَخَلَ جَنَّتَهُۥ وَهُوَ ظَالِمٞ لِّنَفۡسِهِۦ قَالَ مَآ أَظُنُّ أَن تَبِيدَ هَٰذِهِۦٓ أَبَدٗا\nУ ўз боғига кирар экан, ўзига зулм қилган ҳолида: «Мен буни ҳеч қачон йўқ бўлади, деб ўйламасман'}, {'chapter': 18, 'verse': 36, 'text': 'وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّدِدتُّ إِلَىٰ رَبِّي لَأَجِدَنَّ خَيۡرٗا مِّنۡهَا مُنقَلَبٗا\nҚиёмат қоим бўлмаса керак, деб ўйлайман. Агар Роббимга қайтарилсам ҳам, албатта, бундан яхши оқибатни топаман», деди'}, {'chapter': 18, 'verse': 37, 'text': 'قَالَ لَهُۥ صَاحِبُهُۥ وَهُوَ يُحَاوِرُهُۥٓ أَكَفَرۡتَ بِٱلَّذِي خَلَقَكَ مِن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ سَوَّىٰكَ رَجُلٗا\nУнга шериги гаплаша туриб: «Сени тупроқдан, сўнгра нутфадан яратган ва инсон этиб ростлаган Зотга куфр келтирдингми'}, {'chapter': 18, 'verse': 38, 'text': 'لَّـٰكِنَّا۠ هُوَ ٱللَّهُ رَبِّي وَلَآ أُشۡرِكُ بِرَبِّيٓ أَحَدٗا\nЛекин мен: «У Аллоҳ менинг Роббимдир ва Унга ҳеч кимни шерик қилмайман», дейман'}, {'chapter': 18, 'verse': 39, 'text': 'وَلَوۡلَآ إِذۡ دَخَلۡتَ جَنَّتَكَ قُلۡتَ مَا شَآءَ ٱللَّهُ لَا قُوَّةَ إِلَّا بِٱللَّهِۚ إِن تَرَنِ أَنَا۠ أَقَلَّ مِنكَ مَالٗا وَوَلَدٗا\nБоғингга кирганингда: «Аллоҳнинг хоҳлагани, Аллоҳдан бошқада қувват йўқ», десайдинг. Агар мени ўзингдан молу дунё ва бола-чақа жиҳатидан кам деб билсанг'}, {'chapter': 18, 'verse': 40, 'text': 'فَعَسَىٰ رَبِّيٓ أَن يُؤۡتِيَنِ خَيۡرٗا مِّن جَنَّتِكَ وَيُرۡسِلَ عَلَيۡهَا حُسۡبَانٗا مِّنَ ٱلسَّمَآءِ فَتُصۡبِحَ صَعِيدٗا زَلَقًا\nШояд, Роббим менга сенинг боғингдан яхшироғини берса, сенинг боғингга осмондан офат юбориб, у сип-силлиқ тупроққа айланиб қолса'}, {'chapter': 18, 'verse': 41, 'text': 'أَوۡ يُصۡبِحَ مَآؤُهَا غَوۡرٗا فَلَن تَسۡتَطِيعَ لَهُۥ طَلَبٗا\nЁки суви ерга сингиб кетиб, сен уни талаб қилишга қодир бўлмай қолсанг», деди'}, {'chapter': 18, 'verse': 42, 'text': 'وَأُحِيطَ بِثَمَرِهِۦ فَأَصۡبَحَ يُقَلِّبُ كَفَّيۡهِ عَلَىٰ مَآ أَنفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُشۡرِكۡ بِرَبِّيٓ أَحَدٗا\nВа унинг меваси (ҳалокат-ла) ўралди. Ўзи эса, унга сарф қилгани ҳасратидан чапак чалиб, боғида ишкомлар йиқилиб ётганини кўриб: «Қани энди Роббимга ҳеч кимни шерик қилмаганимда», деб қолаверди'}, {'chapter': 18, 'verse': 43, 'text': 'وَلَمۡ تَكُن لَّهُۥ فِئَةٞ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مُنتَصِرًا\nУнга Аллоҳдан бошқа ёрдам берадиган гуруҳ бўлмади, ўзи ҳам нусрат қозона олмади'}, {'chapter': 18, 'verse': 44, 'text': 'هُنَالِكَ ٱلۡوَلَٰيَةُ لِلَّهِ ٱلۡحَقِّۚ هُوَ خَيۡرٞ ثَوَابٗا وَخَيۡرٌ عُقۡبٗا\nБундай мақомда ҳақ нусрат Аллоҳга хосдир. У яхши савобли ва яхши оқибатли Зотдир'}, {'chapter': 18, 'verse': 45, 'text': 'وَٱضۡرِبۡ لَهُم مَّثَلَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ فَأَصۡبَحَ هَشِيمٗا تَذۡرُوهُ ٱلرِّيَٰحُۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقۡتَدِرًا\nУларга дунё ҳаётининг мисолини келтир. У, Биз осмондан туширган сувга ўхшайди. Унинг сабаби ила ер набототлари аралашиб чиқди. Бас, хашакка айланиб, уни шамол учириб кетди. Аллоҳ ҳар бир нарсага қодир бўлган Зотдир'}, {'chapter': 18, 'verse': 46, 'text': 'ٱلۡمَالُ وَٱلۡبَنُونَ زِينَةُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱلۡبَٰقِيَٰتُ ٱلصَّـٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٌ أَمَلٗا\nМол-мулк, бола-чақа ҳаёти дунё зийнатидир. Боқий қолгувчи солиҳ амаллар Роббинг ҳузурида савоб ва умид жиҳатидан яхшироқдир'}, {'chapter': 18, 'verse': 47, 'text': 'وَيَوۡمَ نُسَيِّرُ ٱلۡجِبَالَ وَتَرَى ٱلۡأَرۡضَ بَارِزَةٗ وَحَشَرۡنَٰهُمۡ فَلَمۡ نُغَادِرۡ مِنۡهُمۡ أَحَدٗا\nТоғларни юргизадиган кунимизни ва ерни яп-яланғоч кўрадиган кунингни эсла! Ўшанда уларни жамлаймиз ва бирон кишини қўймаймиз'}, {'chapter': 18, 'verse': 48, 'text': 'وَعُرِضُواْ عَلَىٰ رَبِّكَ صَفّٗا لَّقَدۡ جِئۡتُمُونَا كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةِۭۚ بَلۡ زَعَمۡتُمۡ أَلَّن نَّجۡعَلَ لَكُم مَّوۡعِدٗا\nВа Роббингга сафга тортилган ҳолларида рўбарў қилиндилар. «Батаҳқиқ, сизларни аввал қандай яратган бўлсак, шундай Бизга келдингиз. Аммо сиз Бизни ҳеч жой ва вақт ваъда қилганимиз йўқ, деб ўйлар эдингиз», (дейилди)'}, {'chapter': 18, 'verse': 49, 'text': 'وَوُضِعَ ٱلۡكِتَٰبُ فَتَرَى ٱلۡمُجۡرِمِينَ مُشۡفِقِينَ مِمَّا فِيهِ وَيَقُولُونَ يَٰوَيۡلَتَنَا مَالِ هَٰذَا ٱلۡكِتَٰبِ لَا يُغَادِرُ صَغِيرَةٗ وَلَا كَبِيرَةً إِلَّآ أَحۡصَىٰهَاۚ وَوَجَدُواْ مَا عَمِلُواْ حَاضِرٗاۗ وَلَا يَظۡلِمُ رَبُّكَ أَحَدٗا\nВа китоб қўйилди. Бас, жиноятчиларни ундаги нарсадан қўрққан ҳолларида кўрасан. Улар: «Вой, шўримиз қурисин. Бу қандай китоб, кичикни ҳам, каттани ҳам ҳеч қўймай ҳисоб қилибди-я», дерлар. Ва қилган амалларини ҳозир ҳолда топдилар. Роббинг ҳеч кимга зулм қилмас'}, {'chapter': 18, 'verse': 50, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ كَانَ مِنَ ٱلۡجِنِّ فَفَسَقَ عَنۡ أَمۡرِ رَبِّهِۦٓۗ أَفَتَتَّخِذُونَهُۥ وَذُرِّيَّتَهُۥٓ أَوۡلِيَآءَ مِن دُونِي وَهُمۡ لَكُمۡ عَدُوُّۢۚ بِئۡسَ لِلظَّـٰلِمِينَ بَدَلٗا\nФаришталарга: «Одамга сажда қилинглар», деганимизни эсла. Бас, ўшанда улар сажда қилдилар. Магар иблис қилмади. У жиндан бўлган эди. У ўз Роббининг амридан чиқди. Энди сизлар мени қўйиб уни ва унинг зурриётларини дўст қилиб оласизларми?! Ҳолбуки, у сизларга душман-ку! У, золимлар учун нақадар ёмон бадалдир'}, {'chapter': 18, 'verse': 51, 'text': '۞مَّآ أَشۡهَدتُّهُمۡ خَلۡقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَا خَلۡقَ أَنفُسِهِمۡ وَمَا كُنتُ مُتَّخِذَ ٱلۡمُضِلِّينَ عَضُدٗا\nМен уларни на осмонлару ерни яратишга ва на ўзларини яратишга гувоҳ қилганман ва адаштиргувчиларни ёрдамчи қилиб олган эмасман'}, {'chapter': 18, 'verse': 52, 'text': 'وَيَوۡمَ يَقُولُ نَادُواْ شُرَكَآءِيَ ٱلَّذِينَ زَعَمۡتُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَجَعَلۡنَا بَيۡنَهُم مَّوۡبِقٗا\nУ зот: «Сизлар Менинг «шерикларим» деб ўйлаганларингизни чақиринг», деган кунида уларни чақирдилар. Бас, улар жавоб бермадилар ва уларнинг орасига ҳалокатгоҳ қилмишмиз'}, {'chapter': 18, 'verse': 53, 'text': 'وَرَءَا ٱلۡمُجۡرِمُونَ ٱلنَّارَ فَظَنُّوٓاْ أَنَّهُم مُّوَاقِعُوهَا وَلَمۡ يَجِدُواْ عَنۡهَا مَصۡرِفٗا\nЖиноятчилар дўзахни кўрурлар ва, албатта, унга тушувчи эканларига ишонарлар ва ундан қочар жой топмаслар'}, {'chapter': 18, 'verse': 54, 'text': 'وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرۡءَانِ لِلنَّاسِ مِن كُلِّ مَثَلٖۚ وَكَانَ ٱلۡإِنسَٰنُ أَكۡثَرَ شَيۡءٖ جَدَلٗا\nБатаҳқиқ, бу Қуръонда одамлар учун турли масаллар баён қилдик. Инсон, ўзи, кўп тортишувчи бўлган эди'}, {'chapter': 18, 'verse': 55, 'text': 'وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُوٓاْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰ وَيَسۡتَغۡفِرُواْ رَبَّهُمۡ إِلَّآ أَن تَأۡتِيَهُمۡ سُنَّةُ ٱلۡأَوَّلِينَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ قُبُلٗا\nОдамларни уларга ҳидоят келганида иймон келтиришларидан ва Роббиларига истиғфор айтишларидан фақатгина аввалгиларнинг суннати келишини ёки очиқ азоб келишини кутишлари тўсди, холос'}, {'chapter': 18, 'verse': 56, 'text': 'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۚ وَيُجَٰدِلُ ٱلَّذِينَ كَفَرُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّۖ وَٱتَّخَذُوٓاْ ءَايَٰتِي وَمَآ أُنذِرُواْ هُزُوٗا\nБиз Пайғамбарларни башорат бергувчи ва огоҳлантиргувчи қилиб юборурмиз, холос. Куфр келтирганлар эса, ботил ила мунозара қилиб, у билан ҳақни енгмоқчи бўлурлар. Ҳамда Менинг оятларимдан ва ўзлари огоҳлантирилган нарсадан истеҳзо қилурлар'}, {'chapter': 18, 'verse': 57, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ فَأَعۡرَضَ عَنۡهَا وَنَسِيَ مَا قَدَّمَتۡ يَدَاهُۚ إِنَّا جَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۖ وَإِن تَدۡعُهُمۡ إِلَى ٱلۡهُدَىٰ فَلَن يَهۡتَدُوٓاْ إِذًا أَبَدٗا\nРоббининг оятлари ила эслатилганда, улардан юз ўгириб, ўзи қилган(гуноҳ)ларни унутган кимсадан ҳам золимроқ одам борми?! Албатта, Биз у(Қуръон)ни англамасликлари учун уларнинг дилларига пардалар ва қулоқларига оғирлик қилиб қўйдик. Агар уларни ҳидоятга чақирсанг ҳам, ҳаргиз ҳидоят топмаслар'}, {'chapter': 18, 'verse': 58, 'text': 'وَرَبُّكَ ٱلۡغَفُورُ ذُو ٱلرَّحۡمَةِۖ لَوۡ يُؤَاخِذُهُم بِمَا كَسَبُواْ لَعَجَّلَ لَهُمُ ٱلۡعَذَابَۚ بَل لَّهُم مَّوۡعِدٞ لَّن يَجِدُواْ مِن دُونِهِۦ مَوۡئِلٗا\nВа Роббинг сермағфират ва раҳим-шафқат соҳиби Зотдир. Агар уларни қилган касбларига биноан олганида, азобларини тезлаштирган бўлар эди. Лекин уларга ваъда қилинган вақт бор. У келганда Ундан ўзга паноҳгоҳ топмаслар'}, {'chapter': 18, 'verse': 59, 'text': 'وَتِلۡكَ ٱلۡقُرَىٰٓ أَهۡلَكۡنَٰهُمۡ لَمَّا ظَلَمُواْ وَجَعَلۡنَا لِمَهۡلِكِهِم مَّوۡعِدٗا\nАна у шаҳарларни ҳам зулм қилганларида ҳалок этганмиз ва уларнинг ҳалокатларига вақт белгилаганмиз'}, {'chapter': 18, 'verse': 60, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِفَتَىٰهُ لَآ أَبۡرَحُ حَتَّىٰٓ أَبۡلُغَ مَجۡمَعَ ٱلۡبَحۡرَيۡنِ أَوۡ أَمۡضِيَ حُقُبٗا\nМусо ўз йигитига: «Икки денгиз жамланган жойга етмагунимча, йилларни ўтказсам ҳам, юравераман», деганини эсла'}, {'chapter': 18, 'verse': 61, 'text': 'فَلَمَّا بَلَغَا مَجۡمَعَ بَيۡنِهِمَا نَسِيَا حُوتَهُمَا فَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ سَرَبٗا\nИкковлон икки (денгиз) жамланган жойга етганларида, балиқларни унутдилар. Бас, у денгизни тешиб йўл олди'}, {'chapter': 18, 'verse': 62, 'text': 'فَلَمَّا جَاوَزَا قَالَ لِفَتَىٰهُ ءَاتِنَا غَدَآءَنَا لَقَدۡ لَقِينَا مِن سَفَرِنَا هَٰذَا نَصَبٗا\nУ(ер)дан ўтганлари замон у йигитига: «Тушлигимизни келтир. Бу сафаримизда чарчоққа учрадик», деди'}, {'chapter': 18, 'verse': 63, 'text': 'قَالَ أَرَءَيۡتَ إِذۡ أَوَيۡنَآ إِلَى ٱلصَّخۡرَةِ فَإِنِّي نَسِيتُ ٱلۡحُوتَ وَمَآ أَنسَىٰنِيهُ إِلَّا ٱلشَّيۡطَٰنُ أَنۡ أَذۡكُرَهُۥۚ وَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ عَجَبٗا\nУ: «Буни қара, харсангга (суяниб) жойлашганимизда, мен балиқни унутибман. Фақат шайтонгина уни эслашимни унуттирибди. Ажабо, у денгизга йўл олибди», деди'}, {'chapter': 18, 'verse': 64, 'text': 'قَالَ ذَٰلِكَ مَا كُنَّا نَبۡغِۚ فَٱرۡتَدَّا عَلَىٰٓ ءَاثَارِهِمَا قَصَصٗا\n«Ана шу биз истаган нарса», деди. Бас, келган йўлларидан изларига қайтдилар'}, {'chapter': 18, 'verse': 65, 'text': 'فَوَجَدَا عَبۡدٗا مِّنۡ عِبَادِنَآ ءَاتَيۡنَٰهُ رَحۡمَةٗ مِّنۡ عِندِنَا وَعَلَّمۡنَٰهُ مِن لَّدُنَّا عِلۡمٗا\nБас, бандаларимиздан бир бандани топдилар. Биз унга Ўз даргоҳимиздан раҳмат ва Ўз тарафимиздан илм ўргатган эдик'}, {'chapter': 18, 'verse': 66, 'text': 'قَالَ لَهُۥ مُوسَىٰ هَلۡ أَتَّبِعُكَ عَلَىٰٓ أَن تُعَلِّمَنِ مِمَّا عُلِّمۡتَ رُشۡدٗا\nМусо унга: «Сенга ўргатилган рушддан менга ҳам ўргатмоғинг учун сенга эргашсам майлими?» – деди'}, {'chapter': 18, 'verse': 67, 'text': 'قَالَ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا\nУ: «Сен мен билан бирга бўлишга сабр қила олмассан'}, {'chapter': 18, 'verse': 68, 'text': 'وَكَيۡفَ تَصۡبِرُ عَلَىٰ مَا لَمۡ تُحِطۡ بِهِۦ خُبۡرٗا\nЎзинг хабарини иҳота қилмаган нарсага қандай сабр қиласан», деди'}, {'chapter': 18, 'verse': 69, 'text': 'قَالَ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ صَابِرٗا وَلَآ أَعۡصِي لَكَ أَمۡرٗا\nУ: «Иншоаллоҳ, менинг сабрли эканимни кўрурсан ва сенга ҳеч бир ишда осий бўлмасман», деди'}, {'chapter': 18, 'verse': 70, 'text': 'قَالَ فَإِنِ ٱتَّبَعۡتَنِي فَلَا تَسۡـَٔلۡنِي عَن شَيۡءٍ حَتَّىٰٓ أُحۡدِثَ لَكَ مِنۡهُ ذِكۡرٗا\nУ: «Агар менга эргашадиган бўлсанг, то ўзим зикр қилиб айтмагунимча, бир нарса ҳақида сўрамагин», деди'}, {'chapter': 18, 'verse': 71, 'text': 'فَٱنطَلَقَا حَتَّىٰٓ إِذَا رَكِبَا فِي ٱلسَّفِينَةِ خَرَقَهَاۖ قَالَ أَخَرَقۡتَهَا لِتُغۡرِقَ أَهۡلَهَا لَقَدۡ جِئۡتَ شَيۡـًٔا إِمۡرٗا\nБас, юриб кетдилар. Кемага минганларида, уни тешиб қўйди. У (Мусо): «Буни унинг аҳлини ғарқ қилиш учун тешдингми?! Жуда ғалати нарса қилдинг-ку?!» – деди'}, {'chapter': 18, 'verse': 72, 'text': 'قَالَ أَلَمۡ أَقُلۡ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا\nУ: «Сенга, мен билан бирга бўлишга албатта сабр қила олмассан демабмидим?!» – деди'}, {'chapter': 18, 'verse': 73, 'text': 'قَالَ لَا تُؤَاخِذۡنِي بِمَا نَسِيتُ وَلَا تُرۡهِقۡنِي مِنۡ أَمۡرِي عُسۡرٗا\nУ: «Эсимдан чиққан нарса учун мени жазолама, бу ишим учун мени қийнама», деди'}, {'chapter': 18, 'verse': 74, 'text': 'فَٱنطَلَقَا حَتَّىٰٓ إِذَا لَقِيَا غُلَٰمٗا فَقَتَلَهُۥ قَالَ أَقَتَلۡتَ نَفۡسٗا زَكِيَّةَۢ بِغَيۡرِ نَفۡسٖ لَّقَدۡ جِئۡتَ شَيۡـٔٗا نُّكۡرٗا\nБас, улар юриб кетдилар. То бир болани учратганида, уни ўлдирди. У (Мусо): «Бировни ўлдирмаган бегуноҳ жонни ўлдирасанми?! Жуда мункар нарса қилдинг-ку?!» – деди'}, {'chapter': 18, 'verse': 75, 'text': '۞قَالَ أَلَمۡ أَقُل لَّكَ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا\nУ: «Сенга, сен мен билан бирга албатта сабр қила олмайсан демабмидим?!» – деди'}, {'chapter': 18, 'verse': 76, 'text': 'قَالَ إِن سَأَلۡتُكَ عَن شَيۡءِۭ بَعۡدَهَا فَلَا تُصَٰحِبۡنِيۖ قَدۡ بَلَغۡتَ مِن لَّدُنِّي عُذۡرٗا\nУ: «Агар бундан кейин сендан яна бир нарсани сўрасам, мен билан биродарлик қилмай қўявер. Батаҳқиқ, мен тарафимдан узр(қабули)ни охирига етказдинг», деди'}, {'chapter': 18, 'verse': 77, 'text': 'فَٱنطَلَقَا حَتَّىٰٓ إِذَآ أَتَيَآ أَهۡلَ قَرۡيَةٍ ٱسۡتَطۡعَمَآ أَهۡلَهَا فَأَبَوۡاْ أَن يُضَيِّفُوهُمَا فَوَجَدَا فِيهَا جِدَارٗا يُرِيدُ أَن يَنقَضَّ فَأَقَامَهُۥۖ قَالَ لَوۡ شِئۡتَ لَتَّخَذۡتَ عَلَيۡهِ أَجۡرٗا\nБас, юриб кетдилар. Қачонки, бир шаҳар аҳолисига келганларида, унинг аҳлидан таом сўрадилар. Улар икковларини зиёфат қилишдан бош тортдилар. Икковлари у ерда йиқилиб кетай деб турган бир деворни кўришди. Бас, уни турғизиб қўйди. У (Мусо): «Агар хоҳласанг, бунинг учун ҳақ олар эдинг», деди'}, {'chapter': 18, 'verse': 78, 'text': 'قَالَ هَٰذَا فِرَاقُ بَيۡنِي وَبَيۡنِكَۚ سَأُنَبِّئُكَ بِتَأۡوِيلِ مَا لَمۡ تَسۡتَطِع عَّلَيۡهِ صَبۡرًا\nУ: «Бу мен билан сенинг орамиздаги ажрашишдир. Энди сенга сабр қила олмаган нарса(лар)нинг таъвийлини айтиб бераман», деди'}, {'chapter': 18, 'verse': 79, 'text': 'أَمَّا ٱلسَّفِينَةُ فَكَانَتۡ لِمَسَٰكِينَ يَعۡمَلُونَ فِي ٱلۡبَحۡرِ فَأَرَدتُّ أَنۡ أَعِيبَهَا وَكَانَ وَرَآءَهُم مَّلِكٞ يَأۡخُذُ كُلَّ سَفِينَةٍ غَصۡبٗا\n«Кемага келсак, у денгизда ишлайдиган мискинларники эди. Бас, мен уни айбли қилишни ирода қилдим. Чунки, ортларида бир подшоҳ бўлиб, барча кемани тортиб олаётган эди'}, {'chapter': 18, 'verse': 80, 'text': 'وَأَمَّا ٱلۡغُلَٰمُ فَكَانَ أَبَوَاهُ مُؤۡمِنَيۡنِ فَخَشِينَآ أَن يُرۡهِقَهُمَا طُغۡيَٰنٗا وَكُفۡرٗا\nЭнди болага келсак, ота-онаси мўмин кишилар эди. Бас, биз унинг икковларини туғён ва куфр ила эзишидан қўрқдик'}, {'chapter': 18, 'verse': 81, 'text': 'فَأَرَدۡنَآ أَن يُبۡدِلَهُمَا رَبُّهُمَا خَيۡرٗا مِّنۡهُ زَكَوٰةٗ وَأَقۡرَبَ رُحۡمٗا\nБас, Роббилари уларга унинг бадалига ундан кўра яхшироғини, аҳли солиҳроқ ва меҳрибонроғини беришни ирода қилди'}, {'chapter': 18, 'verse': 82, 'text': 'وَأَمَّا ٱلۡجِدَارُ فَكَانَ لِغُلَٰمَيۡنِ يَتِيمَيۡنِ فِي ٱلۡمَدِينَةِ وَكَانَ تَحۡتَهُۥ كَنزٞ لَّهُمَا وَكَانَ أَبُوهُمَا صَٰلِحٗا فَأَرَادَ رَبُّكَ أَن يَبۡلُغَآ أَشُدَّهُمَا وَيَسۡتَخۡرِجَا كَنزَهُمَا رَحۡمَةٗ مِّن رَّبِّكَۚ وَمَا فَعَلۡتُهُۥ عَنۡ أَمۡرِيۚ ذَٰلِكَ تَأۡوِيلُ مَا لَمۡ تَسۡطِع عَّلَيۡهِ صَبۡرٗا\nДеворга келсак, у шаҳардаги икки етим боланики бўлиб, унинг остида икковларининг хазинаси бор эди. Уларнинг оталари солиҳ одам эди. Бас, Роббинг икковларининг вояга етишини ва хазиналарини чиқариб олишини ирода қилди. Бу, Аллоҳнинг раҳмати ила бўлди. Мен ўзимча қилганим йўқ. Мана шу, сен сабр қила олмаган нарсанинг таъвийлидир»'}, {'chapter': 18, 'verse': 83, 'text': 'وَيَسۡـَٔلُونَكَ عَن ذِي ٱلۡقَرۡنَيۡنِۖ قُلۡ سَأَتۡلُواْ عَلَيۡكُم مِّنۡهُ ذِكۡرًا\nСендан Зулқарнайн ҳақида сўрарлар. «Сизга унинг ҳақидаги зикрни тиловат қилурман», дегин'}, {'chapter': 18, 'verse': 84, 'text': 'إِنَّا مَكَّنَّا لَهُۥ فِي ٱلۡأَرۡضِ وَءَاتَيۡنَٰهُ مِن كُلِّ شَيۡءٖ سَبَبٗا\nАлбатта, Биз унга ер юзида имконият бердик ва ҳар бир нарсага (эришиш) сабабини ато қилдик'}, {'chapter': 18, 'verse': 85, 'text': 'فَأَتۡبَعَ سَبَبًا\nБас, сабабга эргашди'}, {'chapter': 18, 'verse': 86, 'text': 'حَتَّىٰٓ إِذَا بَلَغَ مَغۡرِبَ ٱلشَّمۡسِ وَجَدَهَا تَغۡرُبُ فِي عَيۡنٍ حَمِئَةٖ وَوَجَدَ عِندَهَا قَوۡمٗاۖ قُلۡنَا يَٰذَا ٱلۡقَرۡنَيۡنِ إِمَّآ أَن تُعَذِّبَ وَإِمَّآ أَن تَتَّخِذَ فِيهِمۡ حُسۡنٗا\nТо қуёш ботиш жойига етганида, унинг қора балчиқ булоққа ботаётганини кўрди ва унинг олдида бир қавмни кўрди. Биз: «Эй Зул-қарнайн, ё уларни азоблайсан, ёки гўзаллик-ла тутарсан», дедик'}, {'chapter': 18, 'verse': 87, 'text': 'قَالَ أَمَّا مَن ظَلَمَ فَسَوۡفَ نُعَذِّبُهُۥ ثُمَّ يُرَدُّ إِلَىٰ رَبِّهِۦ فَيُعَذِّبُهُۥ عَذَابٗا نُّكۡرٗا\nУ: «Ким зулм қилган бўлса, тезда уни азоблармиз, сўнгра, Роббига қайтарилар, бас, У зот ҳам уни мункар азоб ила азоблар'}, {'chapter': 18, 'verse': 88, 'text': 'وَأَمَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَلَهُۥ جَزَآءً ٱلۡحُسۡنَىٰۖ وَسَنَقُولُ لَهُۥ مِنۡ أَمۡرِنَا يُسۡرٗا\nАммо, иймон келтириб яхши амаллар қилганларга эса, уларга гўзал мукофот бордир ва унга ўз ишимиздан осонини айтурмиз», деди'}, {'chapter': 18, 'verse': 89, 'text': 'ثُمَّ أَتۡبَعَ سَبَبًا\nСўнгра, у яна сабабга эргашди'}, {'chapter': 18, 'verse': 90, 'text': 'حَتَّىٰٓ إِذَا بَلَغَ مَطۡلِعَ ٱلشَّمۡسِ وَجَدَهَا تَطۡلُعُ عَلَىٰ قَوۡمٖ لَّمۡ نَجۡعَل لَّهُم مِّن دُونِهَا سِتۡرٗا\nТо қуёш чиқиш жойига етганида, унинг бир қавм устидан чиқаётганини кўрди. Уларга у(қуёш)дан тўсиқ бўладиган нарса қилмаган эдик'}, {'chapter': 18, 'verse': 91, 'text': 'كَذَٰلِكَۖ وَقَدۡ أَحَطۡنَا بِمَا لَدَيۡهِ خُبۡرٗا\nАна шундай, батаҳқиқ, Биз унинг олдидаги нарсанинг хабарини иҳота қилдик'}, {'chapter': 18, 'verse': 92, 'text': 'ثُمَّ أَتۡبَعَ سَبَبًا\nСўнгра у яна сабабга эргашди'}, {'chapter': 18, 'verse': 93, 'text': 'حَتَّىٰٓ إِذَا بَلَغَ بَيۡنَ ٱلسَّدَّيۡنِ وَجَدَ مِن دُونِهِمَا قَوۡمٗا لَّا يَكَادُونَ يَفۡقَهُونَ قَوۡلٗا\nТо икки тоғ орасига етганида, уларнинг ортида бирон гапни англамайдиган қавмни кўрди'}, {'chapter': 18, 'verse': 94, 'text': 'قَالُواْ يَٰذَا ٱلۡقَرۡنَيۡنِ إِنَّ يَأۡجُوجَ وَمَأۡجُوجَ مُفۡسِدُونَ فِي ٱلۡأَرۡضِ فَهَلۡ نَجۡعَلُ لَكَ خَرۡجًا عَلَىٰٓ أَن تَجۡعَلَ بَيۡنَنَا وَبَيۡنَهُمۡ سَدّٗا\nУлар: «Эй Зулқарнайн, албатта, Яъжуж ва Маъжуж ер юзида бузғунчилик қилгувчилардир. Биз сенга харож берсак, биз билан уларнинг орасида тўсиқ қилиб берурмисан?» – дедилар'}, {'chapter': 18, 'verse': 95, 'text': 'قَالَ مَا مَكَّنِّي فِيهِ رَبِّي خَيۡرٞ فَأَعِينُونِي بِقُوَّةٍ أَجۡعَلۡ بَيۡنَكُمۡ وَبَيۡنَهُمۡ رَدۡمًا\nУ: «Роббим менга берган имконият яхшидир. Бас, сиз менга куч ила ёрдам беринг. Сиз билан уларнинг орасида бир девор қилажакман'}, {'chapter': 18, 'verse': 96, 'text': 'ءَاتُونِي زُبَرَ ٱلۡحَدِيدِۖ حَتَّىٰٓ إِذَا سَاوَىٰ بَيۡنَ ٱلصَّدَفَيۡنِ قَالَ ٱنفُخُواْۖ حَتَّىٰٓ إِذَا جَعَلَهُۥ نَارٗا قَالَ ءَاتُونِيٓ أُفۡرِغۡ عَلَيۡهِ قِطۡرٗا\nМенга темир парчаларини келтиринг», деди. Ниҳоят, икки тоғ томонлари ила баробарлашганда: «Дам уринглар», деди. Ниҳоят, у(тўп темир)ни ўтга айлантиргач: «Олиб келинглар, устидан мис қуяман», деди'}, {'chapter': 18, 'verse': 97, 'text': 'فَمَا ٱسۡطَٰعُوٓاْ أَن يَظۡهَرُوهُ وَمَا ٱسۡتَطَٰعُواْ لَهُۥ نَقۡبٗا\nБас, улар унинг устига чиқа олмадилар, уни тешиб ҳам ўта олмадилар'}, {'chapter': 18, 'verse': 98, 'text': 'قَالَ هَٰذَا رَحۡمَةٞ مِّن رَّبِّيۖ فَإِذَا جَآءَ وَعۡدُ رَبِّي جَعَلَهُۥ دَكَّآءَۖ وَكَانَ وَعۡدُ رَبِّي حَقّٗا\nУ: «Бу, Роббим раҳматидандир. Агар Роббимнинг ваъдаси келса, буни ер баробар қилур. Роббимнинг ваъдаси ҳақдир», деди'}, {'chapter': 18, 'verse': 99, 'text': '۞وَتَرَكۡنَا بَعۡضَهُمۡ يَوۡمَئِذٖ يَمُوجُ فِي بَعۡضٖۖ وَنُفِخَ فِي ٱلصُّورِ فَجَمَعۡنَٰهُمۡ جَمۡعٗا\nЎша куни уларни бир-бирлари ила мавж урган ҳолларида тарк этдик ва сур чалинди. Бас, уларнинг ҳаммаларини жамладик'}, {'chapter': 18, 'verse': 100, 'text': 'وَعَرَضۡنَا جَهَنَّمَ يَوۡمَئِذٖ لِّلۡكَٰفِرِينَ عَرۡضًا\nЎша куни кофирларга жаҳаннамни кўндаланг қилиб кўрсатдик'}, {'chapter': 18, 'verse': 101, 'text': 'ٱلَّذِينَ كَانَتۡ أَعۡيُنُهُمۡ فِي غِطَآءٍ عَن ذِكۡرِي وَكَانُواْ لَا يَسۡتَطِيعُونَ سَمۡعًا\nУлар кўзлари Менинг зикримдан пардада бўлган ва эшитишга қодир бўла олмайдиганлар эдилар'}, {'chapter': 18, 'verse': 102, 'text': 'أَفَحَسِبَ ٱلَّذِينَ كَفَرُوٓاْ أَن يَتَّخِذُواْ عِبَادِي مِن دُونِيٓ أَوۡلِيَآءَۚ إِنَّآ أَعۡتَدۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ نُزُلٗا\nЁки куфр келтирганлар Мени қўйиб бандаларимни валий — илоҳ қилиб олишни гумон қилдиларми?! Албатта, Биз жаҳаннамни кофирларга манзил этиб тайёрлаганмиз'}, {'chapter': 18, 'verse': 103, 'text': 'قُلۡ هَلۡ نُنَبِّئُكُم بِٱلۡأَخۡسَرِينَ أَعۡمَٰلًا\nСен: «Сизга амаллари юзасидан энг зиёнкорларнинг хабарини берайми'}, {'chapter': 18, 'verse': 104, 'text': 'ٱلَّذِينَ ضَلَّ سَعۡيُهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَهُمۡ يَحۡسَبُونَ أَنَّهُمۡ يُحۡسِنُونَ صُنۡعًا\nУлар бу дунё ҳаётидаёқ саъйи-ҳаракатлари ботил бўлган, ўзлари эса, гўзал иш қиляпман, деб ҳисоблайдиганлардир», дегин'}, {'chapter': 18, 'verse': 105, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَلِقَآئِهِۦ فَحَبِطَتۡ أَعۡمَٰلُهُمۡ فَلَا نُقِيمُ لَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ وَزۡنٗا\nАна ўшалар Роббилари оятларига ва унга рўбарў келишга куфр келтирганлардир. Бас, уларнинг амаллари ҳабата бўлди. Қиёмат куни уларга ҳеч қандай вазн бермасмиз'}, {'chapter': 18, 'verse': 106, 'text': 'ذَٰلِكَ جَزَآؤُهُمۡ جَهَنَّمُ بِمَا كَفَرُواْ وَٱتَّخَذُوٓاْ ءَايَٰتِي وَرُسُلِي هُزُوًا\nШунингдек, куфр келтирганлари ва Менинг оятларим ва Пайғамбарларимни масхара қилганлари сабабли уларнинг жазоси жаҳаннамдир'}, {'chapter': 18, 'verse': 107, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ كَانَتۡ لَهُمۡ جَنَّـٰتُ ٱلۡفِرۡدَوۡسِ نُزُلًا\nАлбатта, иймон келтирган ва яхши амалларни қилганларга Фирдавс жаннати манзил бўлгандир'}, {'chapter': 18, 'verse': 108, 'text': 'خَٰلِدِينَ فِيهَا لَا يَبۡغُونَ عَنۡهَا حِوَلٗا\nУнда абадий қолурлар ва ундан кўчишни истамаслар'}, {'chapter': 18, 'verse': 109, 'text': 'قُل لَّوۡ كَانَ ٱلۡبَحۡرُ مِدَادٗا لِّكَلِمَٰتِ رَبِّي لَنَفِدَ ٱلۡبَحۡرُ قَبۡلَ أَن تَنفَدَ كَلِمَٰتُ رَبِّي وَلَوۡ جِئۡنَا بِمِثۡلِهِۦ مَدَدٗا\nСен: «Агар денгиз Роббим калималарига сиёҳ бўлса-ю, унга ўз мислича мадад келтирсак ҳам, Роббим калималари тугашидан олдин денгиз тугаб қолур», дегин'}, {'chapter': 18, 'verse': 110, 'text': 'قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ فَمَن كَانَ يَرۡجُواْ لِقَآءَ رَبِّهِۦ فَلۡيَعۡمَلۡ عَمَلٗا صَٰلِحٗا وَلَا يُشۡرِكۡ بِعِبَادَةِ رَبِّهِۦٓ أَحَدَۢا\nСен: «Мен ҳам сизларга ўхшаган башарман. Менга, шубҳасиз, илоҳингиз битта илоҳ экани ваҳий қилинди. Бас, ким Роббига рўбарў келишни умид қилса, яхши амал қилсин ва Робби ибодатига биронтани шерик қилмасин», дегин'}], '19': [{'chapter': 19, 'verse': 1, 'text': 'كٓهيعٓصٓ\nКааф, ҳа, йо, айн, сод'}, {'chapter': 19, 'verse': 2, 'text': 'ذِكۡرُ رَحۡمَتِ رَبِّكَ عَبۡدَهُۥ زَكَرِيَّآ\nБу, Роббингнинг бандаси Закариёга раҳмати зикридир'}, {'chapter': 19, 'verse': 3, 'text': 'إِذۡ نَادَىٰ رَبَّهُۥ نِدَآءً خَفِيّٗا\nУ, Роббига махфий нидо қилган чоғини эсла'}, {'chapter': 19, 'verse': 4, 'text': 'قَالَ رَبِّ إِنِّي وَهَنَ ٱلۡعَظۡمُ مِنِّي وَٱشۡتَعَلَ ٱلرَّأۡسُ شَيۡبٗا وَلَمۡ أَكُنۢ بِدُعَآئِكَ رَبِّ شَقِيّٗا\nУ: «Эй Роббим, менинг суякларим мўртлашди, бошга оқ туташди, эй Роббим, сенга дуо қилиш-ла бадбахт бўлмаганман'}, {'chapter': 19, 'verse': 5, 'text': 'وَإِنِّي خِفۡتُ ٱلۡمَوَٰلِيَ مِن وَرَآءِي وَكَانَتِ ٱمۡرَأَتِي عَاقِرٗا فَهَبۡ لِي مِن لَّدُنكَ وَلِيّٗا\nВа, албатта, мен ортимдаги қариндошлардан қўрқдим. Аёлим туғмас эди. Бас, менга Ўз даргоҳингдан бир валий — ортдан қолгувчи ато эт'}, {'chapter': 19, 'verse': 6, 'text': 'يَرِثُنِي وَيَرِثُ مِنۡ ءَالِ يَعۡقُوبَۖ وَٱجۡعَلۡهُ رَبِّ رَضِيّٗا\nУ менга ва Яъқуб оиласига меросхўр бўлсин. Эй Роббим, уни Ўзинг рози бўладиган қил», деди'}, {'chapter': 19, 'verse': 7, 'text': 'يَٰزَكَرِيَّآ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ ٱسۡمُهُۥ يَحۡيَىٰ لَمۡ نَجۡعَل لَّهُۥ مِن قَبۡلُ سَمِيّٗا\n«Эй Закариё, Биз сенга исми Яҳё бўлган бир ўғилнинг хушхабарини берамиз. Бундан олдин унга отдош қилган эмасмиз»'}, {'chapter': 19, 'verse': 8, 'text': 'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَكَانَتِ ٱمۡرَأَتِي عَاقِرٗا وَقَدۡ بَلَغۡتُ مِنَ ٱلۡكِبَرِ عِتِيّٗا\nУ: «Эй Роббим, аёлим туғмас бўлса, ўзим қариб-чириб қолган бўлсам, қандай қилиб менинг ўғлим бўлсин?!» – деди'}, {'chapter': 19, 'verse': 9, 'text': 'قَالَ كَذَٰلِكَ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٞ وَقَدۡ خَلَقۡتُكَ مِن قَبۡلُ وَلَمۡ تَكُ شَيۡـٔٗا\nУ: «Ана шундай! Роббинг айтдики, бу Мен учун осон. Батаҳқиқ, бундан олдин сени яратдим. Ҳолбуки, сен ҳеч нарса эмас эдинг», деди'}, {'chapter': 19, 'verse': 10, 'text': 'قَالَ رَبِّ ٱجۡعَل لِّيٓ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَ لَيَالٖ سَوِيّٗا\nУ: «Роббим, менга бир белги ато қил», деди. У зот: «Сенинг белгинг саломат ҳолингда одамларга уч кеча гапирмаслигингдир», деди'}, {'chapter': 19, 'verse': 11, 'text': 'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ مِنَ ٱلۡمِحۡرَابِ فَأَوۡحَىٰٓ إِلَيۡهِمۡ أَن سَبِّحُواْ بُكۡرَةٗ وَعَشِيّٗا\nБас, ўз қавми ҳузурига меҳробдан чиқди ва уларга, эртаю кеч тасбиҳ айтинглар, деб ишора қилди'}, {'chapter': 19, 'verse': 12, 'text': 'يَٰيَحۡيَىٰ خُذِ ٱلۡكِتَٰبَ بِقُوَّةٖۖ وَءَاتَيۡنَٰهُ ٱلۡحُكۡمَ صَبِيّٗا\n«Эй Яҳё, китобни қувват-ла ол!» (дедик). Ва унга гўдаклигидаёқ ҳукмни бердик'}, {'chapter': 19, 'verse': 13, 'text': 'وَحَنَانٗا مِّن لَّدُنَّا وَزَكَوٰةٗۖ وَكَانَ تَقِيّٗا\nВа Ўз томонимиздан меҳрибонлик ва поклик (бердик). Ҳамда у тақводор эди'}, {'chapter': 19, 'verse': 14, 'text': 'وَبَرَّۢا بِوَٰلِدَيۡهِ وَلَمۡ يَكُن جَبَّارًا عَصِيّٗا\nУ ота-онасига меҳрибон бўлиб, жабр-ситам қилгувчи ва исёнчи эмасди'}, {'chapter': 19, 'verse': 15, 'text': 'وَسَلَٰمٌ عَلَيۡهِ يَوۡمَ وُلِدَ وَيَوۡمَ يَمُوتُ وَيَوۡمَ يُبۡعَثُ حَيّٗا\nУнга туғилган кунида ҳам, ўладиган кунида ҳам, қайта тириладиган кунида ҳам саломлар бўлсин'}, {'chapter': 19, 'verse': 16, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ مَرۡيَمَ إِذِ ٱنتَبَذَتۡ مِنۡ أَهۡلِهَا مَكَانٗا شَرۡقِيّٗا\nКитобда Марямни эсла. У ўз аҳлидан шарқий маконга ажраб чиққанда'}, {'chapter': 19, 'verse': 17, 'text': 'فَٱتَّخَذَتۡ مِن دُونِهِمۡ حِجَابٗا فَأَرۡسَلۡنَآ إِلَيۡهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرٗا سَوِيّٗا\nВа улар билан ўзи орасида тўсиқ олганда, Биз унга Ўз руҳимизни юбордик. Бас, у унга бус-бутун одам бўлиб кўринди'}, {'chapter': 19, 'verse': 18, 'text': 'قَالَتۡ إِنِّيٓ أَعُوذُ بِٱلرَّحۡمَٰنِ مِنكَ إِن كُنتَ تَقِيّٗا\nУ: «Мен Роҳмандан сендан паноҳ беришини сўрайман. Агар тақводор бўлсанг...» деди'}, {'chapter': 19, 'verse': 19, 'text': 'قَالَ إِنَّمَآ أَنَا۠ رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلَٰمٗا زَكِيّٗا\nУ: «Мен Роббингнинг элчисиман, холос, сенга бир пок ўғилни ҳадя этиш учун келдим», деди'}, {'chapter': 19, 'verse': 20, 'text': 'قَالَتۡ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞ وَلَمۡ أَكُ بَغِيّٗا\nУ: « Менда қаёқдан ўғил бўлсин, менга башар тегмаган бўлса, мен бузуқ ҳам бўлмасам?!» – деди'}, {'chapter': 19, 'verse': 21, 'text': 'قَالَ كَذَٰلِكِ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٞۖ وَلِنَجۡعَلَهُۥٓ ءَايَةٗ لِّلنَّاسِ وَرَحۡمَةٗ مِّنَّاۚ وَكَانَ أَمۡرٗا مَّقۡضِيّٗا\nУ: «Ана шундай, Роббинг айтдики, бу Мен учун осондир ва уни одамларга белги қилиш учун ҳамда Биздан раҳмат бўлиши учун, бу ҳукми чиқиб бўлган ишдир», деди'}, {'chapter': 19, 'verse': 22, 'text': '۞فَحَمَلَتۡهُ فَٱنتَبَذَتۡ بِهِۦ مَكَانٗا قَصِيّٗا\nБас, унга ҳомиладор бўлди. Сўнгра, у билан узоқ маконга четланди'}, {'chapter': 19, 'verse': 23, 'text': 'فَأَجَآءَهَا ٱلۡمَخَاضُ إِلَىٰ جِذۡعِ ٱلنَّخۡلَةِ قَالَتۡ يَٰلَيۡتَنِي مِتُّ قَبۡلَ هَٰذَا وَكُنتُ نَسۡيٗا مَّنسِيّٗا\nБас, тўлғоқ уни хурмо танасига олиб борди. У: «Кошки, бундан олдин ўлсам эди ва бутунлай унутилиб кетсам эди», деди'}, {'chapter': 19, 'verse': 24, 'text': 'فَنَادَىٰهَا مِن تَحۡتِهَآ أَلَّا تَحۡزَنِي قَدۡ جَعَلَ رَبُّكِ تَحۡتَكِ سَرِيّٗا\nУнинг остидан: «Маҳзун бўлма, батаҳқиқ, Роббинг остингдан оқар сув қилди'}, {'chapter': 19, 'verse': 25, 'text': 'وَهُزِّيٓ إِلَيۡكِ بِجِذۡعِ ٱلنَّخۡلَةِ تُسَٰقِطۡ عَلَيۡكِ رُطَبٗا جَنِيّٗا\nХурмо танасини силкит, сенга янги мева туширади», деб нидо қилди'}, {'chapter': 19, 'verse': 26, 'text': 'فَكُلِي وَٱشۡرَبِي وَقَرِّي عَيۡنٗاۖ فَإِمَّا تَرَيِنَّ مِنَ ٱلۡبَشَرِ أَحَدٗا فَقُولِيٓ إِنِّي نَذَرۡتُ لِلرَّحۡمَٰنِ صَوۡمٗا فَلَنۡ أُكَلِّمَ ٱلۡيَوۡمَ إِنسِيّٗا\nЕ, ич ва кўзинг қувонсин. Бас, бирор одамни кўрар бўлсанг: «Мен Роҳманга рўза назр қилдим. Бугун инсон зотига зинҳор гапирмасман», дегин'}, {'chapter': 19, 'verse': 27, 'text': 'فَأَتَتۡ بِهِۦ قَوۡمَهَا تَحۡمِلُهُۥۖ قَالُواْ يَٰمَرۡيَمُ لَقَدۡ جِئۡتِ شَيۡـٔٗا فَرِيّٗا\nБас, уни кўтариб қавмига олиб келди. Улар: «Эй Марям, сен ажойиб нарса келтирдинг-ку'}, {'chapter': 19, 'verse': 28, 'text': 'يَـٰٓأُخۡتَ هَٰرُونَ مَا كَانَ أَبُوكِ ٱمۡرَأَ سَوۡءٖ وَمَا كَانَتۡ أُمُّكِ بَغِيّٗا\nЭй, Ҳорун синглиси, отанг ёмон одаммас ва онанг ҳам бузуқ бўлмаган эди-ку!» – дедилар'}, {'chapter': 19, 'verse': 29, 'text': 'فَأَشَارَتۡ إِلَيۡهِۖ قَالُواْ كَيۡفَ نُكَلِّمُ مَن كَانَ فِي ٱلۡمَهۡدِ صَبِيّٗا\nБас, унга ишорат қилди. Улар: «Бешикдаги ёш бола ила қандай гаплашамиз?!» – дедилар'}, {'chapter': 19, 'verse': 30, 'text': 'قَالَ إِنِّي عَبۡدُ ٱللَّهِ ءَاتَىٰنِيَ ٱلۡكِتَٰبَ وَجَعَلَنِي نَبِيّٗا\nУ: «Албатта, мен Аллоҳнинг бандасиман. У менга китоб берди ва мени Пайғамбар қилди'}, {'chapter': 19, 'verse': 31, 'text': 'وَجَعَلَنِي مُبَارَكًا أَيۡنَ مَا كُنتُ وَأَوۡصَٰنِي بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ مَا دُمۡتُ حَيّٗا\nМени қаерда бўлсам ҳам, муборак қилди. Модомики, ҳаёт эканман, намоз ва закотни адо этмоқни тавсия қилди'}, {'chapter': 19, 'verse': 32, 'text': 'وَبَرَّۢا بِوَٰلِدَتِي وَلَمۡ يَجۡعَلۡنِي جَبَّارٗا شَقِيّٗا\nМени волидамга меҳрибон қилди ва жабр-ситам қилгувчи, бадбахт қилмади'}, {'chapter': 19, 'verse': 33, 'text': 'وَٱلسَّلَٰمُ عَلَيَّ يَوۡمَ وُلِدتُّ وَيَوۡمَ أَمُوتُ وَيَوۡمَ أُبۡعَثُ حَيّٗا\nМенга туғилган кунимда ҳам, ўладиган кунимда ҳам ва қайта тириладиган кунимда ҳам салом бордир», деди'}, {'chapter': 19, 'verse': 34, 'text': 'ذَٰلِكَ عِيسَى ٱبۡنُ مَرۡيَمَۖ قَوۡلَ ٱلۡحَقِّ ٱلَّذِي فِيهِ يَمۡتَرُونَ\nМана шу, шубҳа қилишаётган Ийсо ибн Марям тўғрисидаги ҳақ сўздир'}, {'chapter': 19, 'verse': 35, 'text': 'مَا كَانَ لِلَّهِ أَن يَتَّخِذَ مِن وَلَدٖۖ سُبۡحَٰنَهُۥٓۚ إِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nБола тутмоқ Аллоҳга ҳеч тўғри келмас. У бундан покдир. У бир ишни истаса, унга «Бўл!» дейди. Бас, бўлади'}, {'chapter': 19, 'verse': 36, 'text': 'وَإِنَّ ٱللَّهَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\n«Ва, албатта, Аллоҳ менинг Роббимдир ва сизнинг Роббингиздир. Бас, Унга ибодат қилинг. Мана шу тўғри йўлдир.»'}, {'chapter': 19, 'verse': 37, 'text': 'فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَيۡنِهِمۡۖ فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِن مَّشۡهَدِ يَوۡمٍ عَظِيمٍ\nСўнгра, фирқалар ўзаро ихтилофга тушдилар. Куфр келтирганларга буюк кундаги кўринишдан вой бўлсин'}, {'chapter': 19, 'verse': 38, 'text': 'أَسۡمِعۡ بِهِمۡ وَأَبۡصِرۡ يَوۡمَ يَأۡتُونَنَا لَٰكِنِ ٱلظَّـٰلِمُونَ ٱلۡيَوۡمَ فِي ضَلَٰلٖ مُّبِينٖ\nБизга келадиган куни қандай ҳам яхши эшитарлар, қандай ҳам яхши кўрарлар. Лекин золимлар бугунги кунда очиқ залолатдадирлар'}, {'chapter': 19, 'verse': 39, 'text': 'وَأَنذِرۡهُمۡ يَوۡمَ ٱلۡحَسۡرَةِ إِذۡ قُضِيَ ٱلۡأَمۡرُ وَهُمۡ فِي غَفۡلَةٖ وَهُمۡ لَا يُؤۡمِنُونَ\nУларни ҳасрат кунидан, иш битмиш пайтидан огоҳ қил. Улар ғафлатдадирлар, улар иймон келтирмаслар'}, {'chapter': 19, 'verse': 40, 'text': 'إِنَّا نَحۡنُ نَرِثُ ٱلۡأَرۡضَ وَمَنۡ عَلَيۡهَا وَإِلَيۡنَا يُرۡجَعُونَ\nАлбатта, Биз ерга ва унинг устидаги кимсаларга меросхўр бўламиз ва Бизгагина қайтурлар'}, {'chapter': 19, 'verse': 41, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِبۡرَٰهِيمَۚ إِنَّهُۥ كَانَ صِدِّيقٗا نَّبِيًّا\nКитобда Иброҳимни эсла. Албатта, у сиддиқ ва Набий бўлган эди'}, {'chapter': 19, 'verse': 42, 'text': 'إِذۡ قَالَ لِأَبِيهِ يَـٰٓأَبَتِ لِمَ تَعۡبُدُ مَا لَا يَسۡمَعُ وَلَا يُبۡصِرُ وَلَا يُغۡنِي عَنكَ شَيۡـٔٗا\nҚачонки у отасига: «Эй отажон, нима учун эшитмайдиган, кўрмайдиган ва сенга бирон фойда бермайдиган нарсага ибодат қиласан'}, {'chapter': 19, 'verse': 43, 'text': 'يَـٰٓأَبَتِ إِنِّي قَدۡ جَآءَنِي مِنَ ٱلۡعِلۡمِ مَا لَمۡ يَأۡتِكَ فَٱتَّبِعۡنِيٓ أَهۡدِكَ صِرَٰطٗا سَوِيّٗا\nЭй отажон, албатта, сенга келмаган илм менга келди. Бас, менга эргаш, сени тўғри йўлга бошлайман'}, {'chapter': 19, 'verse': 44, 'text': 'يَـٰٓأَبَتِ لَا تَعۡبُدِ ٱلشَّيۡطَٰنَۖ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلرَّحۡمَٰنِ عَصِيّٗا\nЭй отажон, шайтонга ибодат қилма. Албатта, шайтон Роҳманга осий бўлгандир'}, {'chapter': 19, 'verse': 45, 'text': 'يَـٰٓأَبَتِ إِنِّيٓ أَخَافُ أَن يَمَسَّكَ عَذَابٞ مِّنَ ٱلرَّحۡمَٰنِ فَتَكُونَ لِلشَّيۡطَٰنِ وَلِيّٗا\nЭй отажон, албатта, мен сени Роҳман томонидан азоб тутишидан ва шайтонга дўст бўлиб қолишингдан қўрқаман», деган эди'}, {'chapter': 19, 'verse': 46, 'text': 'قَالَ أَرَاغِبٌ أَنتَ عَنۡ ءَالِهَتِي يَـٰٓإِبۡرَٰهِيمُۖ لَئِن لَّمۡ تَنتَهِ لَأَرۡجُمَنَّكَۖ وَٱهۡجُرۡنِي مَلِيّٗا\nУ: «Эй Иброҳим, сен менинг худоларимдан юз ўгирувчимисан?! Қасамки, агар тўхтамасанг, сени, албатта, тошбўрон қиламан! Мендан узоқ кет!» – деди'}, {'chapter': 19, 'verse': 47, 'text': 'قَالَ سَلَٰمٌ عَلَيۡكَۖ سَأَسۡتَغۡفِرُ لَكَ رَبِّيٓۖ إِنَّهُۥ كَانَ بِي حَفِيّٗا\nУ: «Омон бўл. Энди Роббимдан сени мағфират қилишини сўрайман. Албатта, У менга жуда ҳам лутфли, марҳаматлидир'}, {'chapter': 19, 'verse': 48, 'text': 'وَأَعۡتَزِلُكُمۡ وَمَا تَدۡعُونَ مِن دُونِ ٱللَّهِ وَأَدۡعُواْ رَبِّي عَسَىٰٓ أَلَّآ أَكُونَ بِدُعَآءِ رَبِّي شَقِيّٗا\nМен сизлардан ҳам, Аллоҳдан ўзга илтижо қилаётган нарсангиздан ҳам четланаман ва Роббимга илтижо қиламан. Шоядки, Роббимга илтижо қилиш ила бадбахт бўлмасам», деди'}, {'chapter': 19, 'verse': 49, 'text': 'فَلَمَّا ٱعۡتَزَلَهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۖ وَكُلّٗا جَعَلۡنَا نَبِيّٗا\nБас, улардан ва улар Аллоҳдан ўзга ибодат қилаётган нарсадан четланганида, Биз унга Исҳоқ ва Яъқубни ҳадя этдик ва барчаларини Пайғамбар қилдик'}, {'chapter': 19, 'verse': 50, 'text': 'وَوَهَبۡنَا لَهُم مِّن رَّحۡمَتِنَا وَجَعَلۡنَا لَهُمۡ لِسَانَ صِدۡقٍ عَلِيّٗا\nВа уларга Ўз раҳматимиздан ҳадя этдик ҳамда уларга ростгўй ва олий тил бердик'}, {'chapter': 19, 'verse': 51, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ مُوسَىٰٓۚ إِنَّهُۥ كَانَ مُخۡلَصٗا وَكَانَ رَسُولٗا نَّبِيّٗا\nКитобда Мусони эсла. Албатта, у танлаб олинган Набий ва Расул эди'}, {'chapter': 19, 'verse': 52, 'text': 'وَنَٰدَيۡنَٰهُ مِن جَانِبِ ٱلطُّورِ ٱلۡأَيۡمَنِ وَقَرَّبۡنَٰهُ نَجِيّٗا\nБиз унга Турнинг ўнг томонидан нидо қилдик ва муножот учун Ўзимизни яқинлаштирдик'}, {'chapter': 19, 'verse': 53, 'text': 'وَوَهَبۡنَا لَهُۥ مِن رَّحۡمَتِنَآ أَخَاهُ هَٰرُونَ نَبِيّٗا\nВа унга Ўз раҳматимиздан оғаси Ҳорунни Пайғамбар қилиб ҳадя этдик'}, {'chapter': 19, 'verse': 54, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِسۡمَٰعِيلَۚ إِنَّهُۥ كَانَ صَادِقَ ٱلۡوَعۡدِ وَكَانَ رَسُولٗا نَّبِيّٗا\nВа китобда Исмоилни эсла. Албатта, у ваъдасига содиқ ҳамда Расул ва Набий бўлган эди'}, {'chapter': 19, 'verse': 55, 'text': 'وَكَانَ يَأۡمُرُ أَهۡلَهُۥ بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ وَكَانَ عِندَ رَبِّهِۦ مَرۡضِيّٗا\nУ аҳлини намозга ва закотга буюрар эди ва Робби ҳузурида рози бўлинган эди'}, {'chapter': 19, 'verse': 56, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِدۡرِيسَۚ إِنَّهُۥ كَانَ صِدِّيقٗا نَّبِيّٗا\nВа китобда Идрисни эсла. Албатта, у сиддиқ ва Набий бўлган эди'}, {'chapter': 19, 'verse': 57, 'text': 'وَرَفَعۡنَٰهُ مَكَانًا عَلِيًّا\nВа уни олий мақомга кўтардик'}, {'chapter': 19, 'verse': 58, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِم مِّنَ ٱلنَّبِيِّـۧنَ مِن ذُرِّيَّةِ ءَادَمَ وَمِمَّنۡ حَمَلۡنَا مَعَ نُوحٖ وَمِن ذُرِّيَّةِ إِبۡرَٰهِيمَ وَإِسۡرَـٰٓءِيلَ وَمِمَّنۡ هَدَيۡنَا وَٱجۡتَبَيۡنَآۚ إِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُ ٱلرَّحۡمَٰنِ خَرُّواْۤ سُجَّدٗاۤ وَبُكِيّٗا۩\nАна ўшалар, Аллоҳ неъмат берган зотлар Пайғамбарлардан, Одам зурриётидан, Нуҳ билан бирга (кемада) кўтарганларимиздан, Иброҳим ва Исроилнинг зурриётидан, ўзимиз ҳидоят қилган ва танлаб олганлардандир. Уларга Роҳманнинг ояти тиловат қилинса, йиғлаб саждага йиқилардилар'}, {'chapter': 19, 'verse': 59, 'text': '۞فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٌ أَضَاعُواْ ٱلصَّلَوٰةَ وَٱتَّبَعُواْ ٱلشَّهَوَٰتِۖ فَسَوۡفَ يَلۡقَوۡنَ غَيًّا\nБас, уларнинг ортидан бир ўринбосарлар қолдики, улар намозни зое қилиб, шаҳватларга эргашдилар. Бас, тезда улар ёмонликка йўлиқдилар'}, {'chapter': 19, 'verse': 60, 'text': 'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا يُظۡلَمُونَ شَيۡـٔٗا\nМагар ким тавба қилса ва иймон келтириб, амали солиҳ қилса, бас, ана ўшалар жаннатга кирурлар ва ҳеч зулм қилинмаслар'}, {'chapter': 19, 'verse': 61, 'text': 'جَنَّـٰتِ عَدۡنٍ ٱلَّتِي وَعَدَ ٱلرَّحۡمَٰنُ عِبَادَهُۥ بِٱلۡغَيۡبِۚ إِنَّهُۥ كَانَ وَعۡدُهُۥ مَأۡتِيّٗا\nРоҳман Ўз бандаларига ғайбдан ваъда қилган адн жаннатларидир. Албатта, Унинг ваъдаси келгусидир'}, {'chapter': 19, 'verse': 62, 'text': 'لَّا يَسۡمَعُونَ فِيهَا لَغۡوًا إِلَّا سَلَٰمٗاۖ وَلَهُمۡ رِزۡقُهُمۡ فِيهَا بُكۡرَةٗ وَعَشِيّٗا\nУлар унда беҳуда сўз эшитмаслар, илло, салом эшитурлар. Уларга у ерда эртаю кеч ризқлари келиб турур'}, {'chapter': 19, 'verse': 63, 'text': 'تِلۡكَ ٱلۡجَنَّةُ ٱلَّتِي نُورِثُ مِنۡ عِبَادِنَا مَن كَانَ تَقِيّٗا\nУ, бандаларимиздан тақводор бўлганларига мерос қилиб берадиган жаннатимиздир'}, {'chapter': 19, 'verse': 64, 'text': 'وَمَا نَتَنَزَّلُ إِلَّا بِأَمۡرِ رَبِّكَۖ لَهُۥ مَا بَيۡنَ أَيۡدِينَا وَمَا خَلۡفَنَا وَمَا بَيۡنَ ذَٰلِكَۚ وَمَا كَانَ رَبُّكَ نَسِيّٗا\nБиз фақат Роббингнинг амри билангина тушамиз. Бизнинг олдимиздаги нарсалар ва ортимиздаги нарсалар ҳамда уларнинг орасидаги нарсалар ҳам Уникидир. Роббинг унутгувчи бўлмаган'}, {'chapter': 19, 'verse': 65, 'text': 'رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا فَٱعۡبُدۡهُ وَٱصۡطَبِرۡ لِعِبَٰدَتِهِۦۚ هَلۡ تَعۡلَمُ لَهُۥ سَمِيّٗا\nУ осмонлару ернинг ва улар орасидаги нарсаларнинг Роббидир. Бас, Унга ибодат қил ва Унинг ибодатида сабрли бўл. Унга тенгдошни билурмисан'}, {'chapter': 19, 'verse': 66, 'text': 'وَيَقُولُ ٱلۡإِنسَٰنُ أَءِذَا مَا مِتُّ لَسَوۡفَ أُخۡرَجُ حَيًّا\nИнсон: «Агар ўлсам, яна қайта тирик ҳолда чиқариламанми?!» – дейди'}, {'chapter': 19, 'verse': 67, 'text': 'أَوَلَا يَذۡكُرُ ٱلۡإِنسَٰنُ أَنَّا خَلَقۡنَٰهُ مِن قَبۡلُ وَلَمۡ يَكُ شَيۡـٔٗا\nЎша инсон Биз уни бундан олдин ҳеч нарса бўлмай турганида яратганимизни эсламайдими'}, {'chapter': 19, 'verse': 68, 'text': 'فَوَرَبِّكَ لَنَحۡشُرَنَّهُمۡ وَٱلشَّيَٰطِينَ ثُمَّ لَنُحۡضِرَنَّهُمۡ حَوۡلَ جَهَنَّمَ جِثِيّٗا\nРоббингга қасамки, Биз уларни ва шайтонларни, албатта, маҳшарга тўплаймиз, сўнгра, уларни жаҳаннам атрофига тиз чўккан ҳолларида ҳозир қиламиз'}, {'chapter': 19, 'verse': 69, 'text': 'ثُمَّ لَنَنزِعَنَّ مِن كُلِّ شِيعَةٍ أَيُّهُمۡ أَشَدُّ عَلَى ٱلرَّحۡمَٰنِ عِتِيّٗا\nСўнгра, ҳар бир гуруҳдан Роҳманга ашаддий осий бўлганини суғуриб оламиз'}, {'chapter': 19, 'verse': 70, 'text': 'ثُمَّ لَنَحۡنُ أَعۡلَمُ بِٱلَّذِينَ هُمۡ أَوۡلَىٰ بِهَا صِلِيّٗا\nКейин, албатта, Биз у(жаҳаннам)га киришга кимлар лойиқроқ эканини яхши билгувчимиз'}, {'chapter': 19, 'verse': 71, 'text': 'وَإِن مِّنكُمۡ إِلَّا وَارِدُهَاۚ كَانَ عَلَىٰ رَبِّكَ حَتۡمٗا مَّقۡضِيّٗا\nСиздан унга яқинлашувчи бўлмаган ҳеч ким йўқ. Бу, Роббинг ҳузуридаги кескин ҳукмдир'}, {'chapter': 19, 'verse': 72, 'text': 'ثُمَّ نُنَجِّي ٱلَّذِينَ ٱتَّقَواْ وَّنَذَرُ ٱلظَّـٰلِمِينَ فِيهَا جِثِيّٗا\nСўнгра, тақво қилганларга нажот берамиз ва золимларни унда тиз чўккан ҳолларида қолдирамиз'}, {'chapter': 19, 'verse': 73, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُوٓاْ أَيُّ ٱلۡفَرِيقَيۡنِ خَيۡرٞ مَّقَامٗا وَأَحۡسَنُ نَدِيّٗا\nҚачонки уларга Бизнинг равшан оятларимиз тиловат қилинса, куфр келтирганлар иймон келтирганларга: «Икки гуруҳнинг қайси бири мақом ва мажлис жиҳатидан яхшироқдир?» – дерлар'}, {'chapter': 19, 'verse': 74, 'text': 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَحۡسَنُ أَثَٰثٗا وَرِءۡيٗا\nҲолбуки, Биз улардан олдин мол ва кўриниш жиҳатидан яхшироқ бўлган қанчадан-қанча асрларни ҳалок қилганмиз'}, {'chapter': 19, 'verse': 75, 'text': 'قُلۡ مَن كَانَ فِي ٱلضَّلَٰلَةِ فَلۡيَمۡدُدۡ لَهُ ٱلرَّحۡمَٰنُ مَدًّاۚ حَتَّىٰٓ إِذَا رَأَوۡاْ مَا يُوعَدُونَ إِمَّا ٱلۡعَذَابَ وَإِمَّا ٱلسَّاعَةَ فَسَيَعۡلَمُونَ مَنۡ هُوَ شَرّٞ مَّكَانٗا وَأَضۡعَفُ جُندٗا\nСен: «Ким залолатда бўлса, Роҳман унга муҳлат бериб қўйсин. Токи ўзларига ваъда қилинган нарсани — азобни ёки қиёматни кўрганларида, кимнинг мартабаси ёмонроқ ва аскари кучсизроқ эканини албатта билурлар», дегин'}, {'chapter': 19, 'verse': 76, 'text': 'وَيَزِيدُ ٱللَّهُ ٱلَّذِينَ ٱهۡتَدَوۡاْ هُدٗىۗ وَٱلۡبَٰقِيَٰتُ ٱلصَّـٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٞ مَّرَدًّا\nВа Аллоҳ ҳидоятга юрганларнинг ҳидоятини зиёда қилади. Боқий қолувчи солиҳ амаллар Роббинг наздида савоби яхшироқ ва оқибати хайрлидир'}, {'chapter': 19, 'verse': 77, 'text': 'أَفَرَءَيۡتَ ٱلَّذِي كَفَرَ بِـَٔايَٰتِنَا وَقَالَ لَأُوتَيَنَّ مَالٗا وَوَلَدًا\nОятларимизга куфр келтирган ва: «Албатта, (охиратда) менга молу дунё ҳамда бола-чақа берилажак», деганни кўрдингми'}, {'chapter': 19, 'verse': 78, 'text': 'أَطَّلَعَ ٱلۡغَيۡبَ أَمِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَٰنِ عَهۡدٗا\nУ ғойибни билармикан ёки Роҳманнинг ҳузуридан аҳду паймон олдимикан'}, {'chapter': 19, 'verse': 79, 'text': 'كَلَّاۚ سَنَكۡتُبُ مَا يَقُولُ وَنَمُدُّ لَهُۥ مِنَ ٱلۡعَذَابِ مَدّٗا\nЙўқ! Нима деганини албатта ёзиб қўямиз ва унга азобни узундан-узоқ қиламиз'}, {'chapter': 19, 'verse': 80, 'text': 'وَنَرِثُهُۥ مَا يَقُولُ وَيَأۡتِينَا فَرۡدٗا\nУ айтаётган нарсага меросхўр бўлурмиз ва у Бизга ёлғиз ҳолида келур'}, {'chapter': 19, 'verse': 81, 'text': 'وَٱتَّخَذُواْ مِن دُونِ ٱللَّهِ ءَالِهَةٗ لِّيَكُونُواْ لَهُمۡ عِزّٗا\nУлар ўзларига куч-қудрат, иззат бўлиши учун Аллоҳдан ўзгаларни «худо» қилиб олдилар'}, {'chapter': 19, 'verse': 82, 'text': 'كَلَّاۚ سَيَكۡفُرُونَ بِعِبَادَتِهِمۡ وَيَكُونُونَ عَلَيۡهِمۡ ضِدًّا\nЙўқ! У(«худо»)лар уларнинг ибодатларини инкор этурлар ва уларга зид бўлурлар'}, {'chapter': 19, 'verse': 83, 'text': 'أَلَمۡ تَرَ أَنَّآ أَرۡسَلۡنَا ٱلشَّيَٰطِينَ عَلَى ٱلۡكَٰفِرِينَ تَؤُزُّهُمۡ أَزّٗا\nБиз шайтонларни кофирларга уларни доимий қўзғаб туришга юборганимизни кўрмадингми'}, {'chapter': 19, 'verse': 84, 'text': 'فَلَا تَعۡجَلۡ عَلَيۡهِمۡۖ إِنَّمَا نَعُدُّ لَهُمۡ عَدّٗا\nУларнинг зиддига шошилма! Биз уларнинг саноғини санамоқдамиз'}, {'chapter': 19, 'verse': 85, 'text': 'يَوۡمَ نَحۡشُرُ ٱلۡمُتَّقِينَ إِلَى ٱلرَّحۡمَٰنِ وَفۡدٗا\nТақводорларни Роҳман ҳузурига меҳмон ҳайъати этиб тўплаган кунимизда'}, {'chapter': 19, 'verse': 86, 'text': 'وَنَسُوقُ ٱلۡمُجۡرِمِينَ إِلَىٰ جَهَنَّمَ وِرۡدٗا\nЖиноятчиларни жаҳаннамга чанқоқ ҳолларида ҳайдаймиз'}, {'chapter': 19, 'verse': 87, 'text': 'لَّا يَمۡلِكُونَ ٱلشَّفَٰعَةَ إِلَّا مَنِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَٰنِ عَهۡدٗا\nУлар шафоатга молик бўлмаслар. Магар ким Роҳман ҳузурида аҳду паймон олган бўлса (молик бўлар)'}, {'chapter': 19, 'verse': 88, 'text': 'وَقَالُواْ ٱتَّخَذَ ٱلرَّحۡمَٰنُ وَلَدٗا\nУлар: «Роҳман бола тутди», дедилар'}, {'chapter': 19, 'verse': 89, 'text': 'لَّقَدۡ جِئۡتُمۡ شَيۡـًٔا إِدّٗا\nБатаҳқиқ, жуда оғир нарса келтирдингиз'}, {'chapter': 19, 'verse': 90, 'text': 'تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِنۡهُ وَتَنشَقُّ ٱلۡأَرۡضُ وَتَخِرُّ ٱلۡجِبَالُ هَدًّا\nУндан осмонлар парчаланиб, ер ёрилиб, тоғлар қулаб йиқилай дейди'}, {'chapter': 19, 'verse': 91, 'text': 'أَن دَعَوۡاْ لِلرَّحۡمَٰنِ وَلَدٗا\nРоҳманнинг боласи бор, деганларига'}, {'chapter': 19, 'verse': 92, 'text': 'وَمَا يَنۢبَغِي لِلرَّحۡمَٰنِ أَن يَتَّخِذَ وَلَدًا\nРоҳманга бола тутиш лойиқ эмасдир'}, {'chapter': 19, 'verse': 93, 'text': 'إِن كُلُّ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ إِلَّآ ءَاتِي ٱلرَّحۡمَٰنِ عَبۡدٗا\nОсмонлару ердаги барча кимсалар Роҳманга банда бўлган ҳолларида келурлар'}, {'chapter': 19, 'verse': 94, 'text': 'لَّقَدۡ أَحۡصَىٰهُمۡ وَعَدَّهُمۡ عَدّٗا\nБатаҳқиқ, У зот уларни иҳота қилган ва санаб қўйгандир'}, {'chapter': 19, 'verse': 95, 'text': 'وَكُلُّهُمۡ ءَاتِيهِ يَوۡمَ ٱلۡقِيَٰمَةِ فَرۡدًا\nУларнинг барчалари У зотнинг ҳузурига қиёмат куни ёлғиз ҳолларида келгувчидирлар'}, {'chapter': 19, 'verse': 96, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَيَجۡعَلُ لَهُمُ ٱلرَّحۡمَٰنُ وُدّٗا\nАлбатта, иймон келтирган ва яхши амалларни қилганларга Роҳман муҳаббат пайдо қилур'}, {'chapter': 19, 'verse': 97, 'text': 'فَإِنَّمَا يَسَّرۡنَٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ ٱلۡمُتَّقِينَ وَتُنذِرَ بِهِۦ قَوۡمٗا لُّدّٗا\nБас, албатта, тақводорларга хушхабар беришинг ва у билан саркаш қавмларни огоҳлантиришинг учун у(Қуръон)ни тилингга осон қилдик'}, {'chapter': 19, 'verse': 98, 'text': 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هَلۡ تُحِسُّ مِنۡهُم مِّنۡ أَحَدٍ أَوۡ تَسۡمَعُ لَهُمۡ رِكۡزَۢا\nУлардан олдин қанчадан-қанча аср(аҳли)ни ҳалок қилдик. Улардан бирортасини ҳис этасанми ёки шарпасини эшитасанми'}], '20': [{'chapter': 20, 'verse': 1, 'text': 'طه\nТоҳа'}, {'chapter': 20, 'verse': 2, 'text': 'مَآ أَنزَلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ لِتَشۡقَىٰٓ\nСенга Қуръонни бадбахт бўлишинг учун нозил қилганимиз йўқ'}, {'chapter': 20, 'verse': 3, 'text': 'إِلَّا تَذۡكِرَةٗ لِّمَن يَخۡشَىٰ\nМагар қўрққан кимсаларга эслатма бўлиши учун'}, {'chapter': 20, 'verse': 4, 'text': 'تَنزِيلٗا مِّمَّنۡ خَلَقَ ٱلۡأَرۡضَ وَٱلسَّمَٰوَٰتِ ٱلۡعُلَى\nУ, ерни ва юксак осмонларни яратган Зотдан нозил бўлгандир'}, {'chapter': 20, 'verse': 5, 'text': 'ٱلرَّحۡمَٰنُ عَلَى ٱلۡعَرۡشِ ٱسۡتَوَىٰ\nУ — Роҳман (Зоти ва улуғлигига муносиб равишда) Аршга кўтарилди'}, {'chapter': 20, 'verse': 6, 'text': 'لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَمَا تَحۡتَ ٱلثَّرَىٰ\nОсмонлардаги нарсалару ердаги нарсалар, уларнинг орасидаги нарсалару ер остидаги нарсалар Уникидир'}, {'chapter': 20, 'verse': 7, 'text': 'وَإِن تَجۡهَرۡ بِٱلۡقَوۡلِ فَإِنَّهُۥ يَعۡلَمُ ٱلسِّرَّ وَأَخۡفَى\nСен агар сўзда овозингни юксалтирсанг ҳам. Бас, албатта, У сирни ҳам, махфийни ҳам билгувчидир'}, {'chapter': 20, 'verse': 8, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰ\nАллоҳ — Ундан бошқа ибодатга сазовор илоҳ йўқ Зотдир. Гўзал исмлар Уникидир'}, {'chapter': 20, 'verse': 9, 'text': 'وَهَلۡ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ\nСенга Мусонинг хабари келдими'}, {'chapter': 20, 'verse': 10, 'text': 'إِذۡ رَءَا نَارٗا فَقَالَ لِأَهۡلِهِ ٱمۡكُثُوٓاْ إِنِّيٓ ءَانَسۡتُ نَارٗا لَّعَلِّيٓ ءَاتِيكُم مِّنۡهَا بِقَبَسٍ أَوۡ أَجِدُ عَلَى ٱلنَّارِ هُدٗى\nУ, оловни кўрган чоғида ўз аҳлига: «Туриб туринглар, мен оловни кўрдим, шояд, сизга ундан чўғ келтирсам ёки олов бошида йўл кўрсатувчини топсам», деди'}, {'chapter': 20, 'verse': 11, 'text': 'فَلَمَّآ أَتَىٰهَا نُودِيَ يَٰمُوسَىٰٓ\nБас, у(олов)га келганида, нидо қилинди: «Эй Мусо'}, {'chapter': 20, 'verse': 12, 'text': 'إِنِّيٓ أَنَا۠ رَبُّكَ فَٱخۡلَعۡ نَعۡلَيۡكَ إِنَّكَ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوٗى\nАлбатта, Мен Ўзим сенинг Роббингдирман. Кавушларингни еч. Чунки, сен муқаддас Туво водийсидасан»'}, {'chapter': 20, 'verse': 13, 'text': 'وَأَنَا ٱخۡتَرۡتُكَ فَٱسۡتَمِعۡ لِمَا يُوحَىٰٓ\nМен сени ихтиёр қилиб олдим. Ваҳий қилинадиган нарсага қулоқ ос'}, {'chapter': 20, 'verse': 14, 'text': 'إِنَّنِيٓ أَنَا ٱللَّهُ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعۡبُدۡنِي وَأَقِمِ ٱلصَّلَوٰةَ لِذِكۡرِيٓ\nАлбатта, Мен Ўзим Аллоҳдирман. Мендан ўзга илоҳ йўқ. Бас, Менга ибодат қил ва Мени зикр этиш учун намозни тўкис адо эт'}, {'chapter': 20, 'verse': 15, 'text': 'إِنَّ ٱلسَّاعَةَ ءَاتِيَةٌ أَكَادُ أُخۡفِيهَا لِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا تَسۡعَىٰ\nАлбатта, соат (қиёмат) келгувчидир. Ҳар бир жон саъйи-ҳаракатига яраша жазо олиши учун уни махфий тутурман'}, {'chapter': 20, 'verse': 16, 'text': 'فَلَا يَصُدَّنَّكَ عَنۡهَا مَن لَّا يُؤۡمِنُ بِهَا وَٱتَّبَعَ هَوَىٰهُ فَتَرۡدَىٰ\nУнга иймон келтирмайдиганлар ва ҳавойи нафсига эргашганлар сени ҳаргиз ундан тўсмасинлар. У ҳолда ҳалок бўласан'}, {'chapter': 20, 'verse': 17, 'text': 'وَمَا تِلۡكَ بِيَمِينِكَ يَٰمُوسَىٰ\nЭй Мусо, қўлингдаги нима?»'}, {'chapter': 20, 'verse': 18, 'text': 'قَالَ هِيَ عَصَايَ أَتَوَكَّؤُاْ عَلَيۡهَا وَأَهُشُّ بِهَا عَلَىٰ غَنَمِي وَلِيَ فِيهَا مَـَٔارِبُ أُخۡرَىٰ\nУ: «Бу асоимдир, унга суянаман, у билан қўйларимга (барг) қоқиб бераман ва унда менинг бошқа ишларим бор», деди'}, {'chapter': 20, 'verse': 19, 'text': 'قَالَ أَلۡقِهَا يَٰمُوسَىٰ\nУ: «Эй Мусо, уни ташла», деди'}, {'chapter': 20, 'verse': 20, 'text': 'فَأَلۡقَىٰهَا فَإِذَا هِيَ حَيَّةٞ تَسۡعَىٰ\nБас, уни ташлади. Бирдан у (асо) илон бўлиб юра бошлади'}, {'chapter': 20, 'verse': 21, 'text': 'قَالَ خُذۡهَا وَلَا تَخَفۡۖ سَنُعِيدُهَا سِيرَتَهَا ٱلۡأُولَىٰ\nУ зот: «Уни ол, қўрқма. Биз уни аввалги ҳолига келтирамиз'}, {'chapter': 20, 'verse': 22, 'text': 'وَٱضۡمُمۡ يَدَكَ إِلَىٰ جَنَاحِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٍ ءَايَةً أُخۡرَىٰ\nҚўлингни қўлтиғингга тиқ, ёмонликсиз оппоқ бўлиб чиқадир. Бу бошқа бир мўъжизадир'}, {'chapter': 20, 'verse': 23, 'text': 'لِنُرِيَكَ مِنۡ ءَايَٰتِنَا ٱلۡكُبۡرَى\nБиз сенга буюк мўъжизаларимизни кўрсатиш учун (шундай қилдик)'}, {'chapter': 20, 'verse': 24, 'text': 'ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\nСен Фиръавнга бор, чунки, у туғёнга кетди», деди'}, {'chapter': 20, 'verse': 25, 'text': 'قَالَ رَبِّ ٱشۡرَحۡ لِي صَدۡرِي\nУ: «Эй Роббим, менинг қалбимни кенг қил'}, {'chapter': 20, 'verse': 26, 'text': 'وَيَسِّرۡ لِيٓ أَمۡرِي\nМенинг ишимни осон қил'}, {'chapter': 20, 'verse': 27, 'text': 'وَٱحۡلُلۡ عُقۡدَةٗ مِّن لِّسَانِي\nТилимдаги тугунни ечгин'}, {'chapter': 20, 'verse': 28, 'text': 'يَفۡقَهُواْ قَوۡلِي\nСўзимни англасинлар'}, {'chapter': 20, 'verse': 29, 'text': 'وَٱجۡعَل لِّي وَزِيرٗا مِّنۡ أَهۡلِي\nМенга ўз аҳлимдан бир вазир қилиб бер'}, {'chapter': 20, 'verse': 30, 'text': 'هَٰرُونَ أَخِي\nОғам Ҳорунни'}, {'chapter': 20, 'verse': 31, 'text': 'ٱشۡدُدۡ بِهِۦٓ أَزۡرِي\nУ билан белимни қувватла'}, {'chapter': 20, 'verse': 32, 'text': 'وَأَشۡرِكۡهُ فِيٓ أَمۡرِي\nУни ишимга шерик айла'}, {'chapter': 20, 'verse': 33, 'text': 'كَيۡ نُسَبِّحَكَ كَثِيرٗا\nСенга кўп тасбиҳ айтишимиз учун'}, {'chapter': 20, 'verse': 34, 'text': 'وَنَذۡكُرَكَ كَثِيرًا\nВа Сени кўп зикр қилишимиз учун'}, {'chapter': 20, 'verse': 35, 'text': 'إِنَّكَ كُنتَ بِنَا بَصِيرٗا\nАлбатта, Сен Ўзинг бизни кўриб турувчи эдинг», деди'}, {'chapter': 20, 'verse': 36, 'text': 'قَالَ قَدۡ أُوتِيتَ سُؤۡلَكَ يَٰمُوسَىٰ\nУ зот: «Эй Мусо, сенга сўраганинг берилди'}, {'chapter': 20, 'verse': 37, 'text': 'وَلَقَدۡ مَنَنَّا عَلَيۡكَ مَرَّةً أُخۡرَىٰٓ\nБатаҳқиқ, Биз сенга бошқа марта ҳам неъмат берган эдик'}, {'chapter': 20, 'verse': 38, 'text': 'إِذۡ أَوۡحَيۡنَآ إِلَىٰٓ أُمِّكَ مَا يُوحَىٰٓ\nЎшанда онангга илҳом қилинадиган нарсани илҳом қилиб'}, {'chapter': 20, 'verse': 39, 'text': 'أَنِ ٱقۡذِفِيهِ فِي ٱلتَّابُوتِ فَٱقۡذِفِيهِ فِي ٱلۡيَمِّ فَلۡيُلۡقِهِ ٱلۡيَمُّ بِٱلسَّاحِلِ يَأۡخُذۡهُ عَدُوّٞ لِّي وَعَدُوّٞ لَّهُۥۚ وَأَلۡقَيۡتُ عَلَيۡكَ مَحَبَّةٗ مِّنِّي وَلِتُصۡنَعَ عَلَىٰ عَيۡنِيٓ\n«Уни тобутга сол, уни эса, дарёга ташла. Дарё уни соҳилга отсин. Уни Менинг душманим ва унинг душмани олади», дедик. Ва сенга Ўзимдан муҳаббат солдим. Буни Менинг кўз олдимда етиштирилишинг учун (қилдим)'}, {'chapter': 20, 'verse': 40, 'text': 'إِذۡ تَمۡشِيٓ أُخۡتُكَ فَتَقُولُ هَلۡ أَدُلُّكُمۡ عَلَىٰ مَن يَكۡفُلُهُۥۖ فَرَجَعۡنَٰكَ إِلَىٰٓ أُمِّكَ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَۚ وَقَتَلۡتَ نَفۡسٗا فَنَجَّيۡنَٰكَ مِنَ ٱلۡغَمِّ وَفَتَنَّـٰكَ فُتُونٗاۚ فَلَبِثۡتَ سِنِينَ فِيٓ أَهۡلِ مَدۡيَنَ ثُمَّ جِئۡتَ عَلَىٰ قَدَرٖ يَٰمُوسَىٰ\nЎшанда опанг юриб бориб: «Уни кафилликка оладиганларга далолат қилайми?» – деган эди. Бас, Биз сени кўзи қувониши ва хафа бўлмаслиги учун онангга қайтардик. Ва бир жонни ўлдирдинг. Бас, Биз сени ғамдан қутқардик ва турли синовлар ила синадик. Сўнгра, Мадян аҳли ила йилларча қолдинг ва тақдирга биноан келдинг, эй Мусо'}, {'chapter': 20, 'verse': 41, 'text': 'وَٱصۡطَنَعۡتُكَ لِنَفۡسِي\nМен сени Ўзим учун танладим'}, {'chapter': 20, 'verse': 42, 'text': 'ٱذۡهَبۡ أَنتَ وَأَخُوكَ بِـَٔايَٰتِي وَلَا تَنِيَا فِي ذِكۡرِي\nСен ва оғанг мўъжизаларимни олиб боринглар ва Мени зикр этишда сусткашлик қилманглар'}, {'chapter': 20, 'verse': 43, 'text': 'ٱذۡهَبَآ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\nФиръавнга боринглар! Чунки, у туғёнга кетди'}, {'chapter': 20, 'verse': 44, 'text': 'فَقُولَا لَهُۥ قَوۡلٗا لَّيِّنٗا لَّعَلَّهُۥ يَتَذَكَّرُ أَوۡ يَخۡشَىٰ\nБас, унга юмшоқ сўз айтинглар. Шояд, эсласа ёки қўрқса»'}, {'chapter': 20, 'verse': 45, 'text': 'قَالَا رَبَّنَآ إِنَّنَا نَخَافُ أَن يَفۡرُطَ عَلَيۡنَآ أَوۡ أَن يَطۡغَىٰ\nИкковлари: «Эй Роббимиз, биз унинг тезда бизга ёмонлик қилишидан ёки ҳаддан ошишидан қўрқамиз», дедилар'}, {'chapter': 20, 'verse': 46, 'text': 'قَالَ لَا تَخَافَآۖ إِنَّنِي مَعَكُمَآ أَسۡمَعُ وَأَرَىٰ\nУ зот: «Қўрқманглар! Албатта, Мен сизлар биланман, эшитаман ва кўраман'}, {'chapter': 20, 'verse': 47, 'text': 'فَأۡتِيَاهُ فَقُولَآ إِنَّا رَسُولَا رَبِّكَ فَأَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَـٰٓءِيلَ وَلَا تُعَذِّبۡهُمۡۖ قَدۡ جِئۡنَٰكَ بِـَٔايَةٖ مِّن رَّبِّكَۖ وَٱلسَّلَٰمُ عَلَىٰ مَنِ ٱتَّبَعَ ٱلۡهُدَىٰٓ\nБас, унинг олдига боринглар ва унга: «Албатта, биз Роббингнинг Пайғамбарларимиз, Бани Исроилни биз билан қўйиб юбор. Уларни азоблама. Батаҳқиқ, биз сенга Роббингдан мўъжиза келтирдик. Ҳидоятга эргашган кимсага салом бўлур'}, {'chapter': 20, 'verse': 48, 'text': 'إِنَّا قَدۡ أُوحِيَ إِلَيۡنَآ أَنَّ ٱلۡعَذَابَ عَلَىٰ مَن كَذَّبَ وَتَوَلَّىٰ\nАлбатта, бизга ваҳий қилиндики, «Ёлғонга чиқарганларга ва юз ўгириб кетганларга, шубҳасиз, азоб бўлур», денглар», деди'}, {'chapter': 20, 'verse': 49, 'text': 'قَالَ فَمَن رَّبُّكُمَا يَٰمُوسَىٰ\nУ: «Эй Мусо, икковингизнинг Роббингиз ким?» – деди'}, {'chapter': 20, 'verse': 50, 'text': 'قَالَ رَبُّنَا ٱلَّذِيٓ أَعۡطَىٰ كُلَّ شَيۡءٍ خَلۡقَهُۥ ثُمَّ هَدَىٰ\nУ: «Бизнинг Роббимиз ҳар нарсага ўз яратилишини берган ҳамда уни ҳидоятга солган Зотдир», деди'}, {'chapter': 20, 'verse': 51, 'text': 'قَالَ فَمَا بَالُ ٱلۡقُرُونِ ٱلۡأُولَىٰ\nУ: «Ўтган асрларнинг ҳоли недир?» – деди'}, {'chapter': 20, 'verse': 52, 'text': 'قَالَ عِلۡمُهَا عِندَ رَبِّي فِي كِتَٰبٖۖ لَّا يَضِلُّ رَبِّي وَلَا يَنسَى\nУ: «Уларнинг илми Роббим ҳузурида, китобдадир. Роббим адашмас ва унутмас», деди'}, {'chapter': 20, 'verse': 53, 'text': 'ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدٗا وَسَلَكَ لَكُمۡ فِيهَا سُبُلٗا وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّن نَّبَاتٖ شَتَّىٰ\n«У сизга ерни бешик қилган, сизга унда йўллар очган ва осмондан сув туширган Зотдир». Бас, ўша (сув) ила турли набототлар жуфтларини чиқардик'}, {'chapter': 20, 'verse': 54, 'text': 'كُلُواْ وَٱرۡعَوۡاْ أَنۡعَٰمَكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّأُوْلِي ٱلنُّهَىٰ\nЕнглар ва чорваларингизни боқинглар. Албатта, бунда ақл эгалари учун ибратлар бордир'}, {'chapter': 20, 'verse': 55, 'text': '۞مِنۡهَا خَلَقۡنَٰكُمۡ وَفِيهَا نُعِيدُكُمۡ وَمِنۡهَا نُخۡرِجُكُمۡ تَارَةً أُخۡرَىٰ\nСизларни ундан яратдик, унга қайтарамиз ва яна бир бор ундан чиқариб оламиз'}, {'chapter': 20, 'verse': 56, 'text': 'وَلَقَدۡ أَرَيۡنَٰهُ ءَايَٰتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ\nБатаҳқиқ, Биз унга оят-мўъжизаларимизнинг ҳаммасини кўрсатдик. Бас, у ёлғонга чиқарди ва бош тортди'}, {'chapter': 20, 'verse': 57, 'text': 'قَالَ أَجِئۡتَنَا لِتُخۡرِجَنَا مِنۡ أَرۡضِنَا بِسِحۡرِكَ يَٰمُوسَىٰ\nУ: «Эй Мусо, бизни еримиздан чиқариш учун сеҳринг ила келдингми'}, {'chapter': 20, 'verse': 58, 'text': 'فَلَنَأۡتِيَنَّكَ بِسِحۡرٖ مِّثۡلِهِۦ فَٱجۡعَلۡ بَيۡنَنَا وَبَيۡنَكَ مَوۡعِدٗا لَّا نُخۡلِفُهُۥ نَحۡنُ وَلَآ أَنتَ مَكَانٗا سُوٗى\nБас, албатта, биз ҳам унга ўхшаш сеҳр келтирамиз, биз билан ўзинг орангда ваъда вақти тайин қил, унга биз ҳам, сен ҳам хилоф қилмайдиган бўлайлик, ўрта жой бўлсин», деди'}, {'chapter': 20, 'verse': 59, 'text': 'قَالَ مَوۡعِدُكُمۡ يَوۡمُ ٱلزِّينَةِ وَأَن يُحۡشَرَ ٱلنَّاسُ ضُحٗى\nУ: «Ваъда вақтингиз зийнат куни. Одамларнинг чоштгоҳда тўпланиши», деди'}, {'chapter': 20, 'verse': 60, 'text': 'فَتَوَلَّىٰ فِرۡعَوۡنُ فَجَمَعَ كَيۡدَهُۥ ثُمَّ أَتَىٰ\nБас, Фиръавн қайтиб кетди, сўнгра, ҳийласини йиғиб келди'}, {'chapter': 20, 'verse': 61, 'text': 'قَالَ لَهُم مُّوسَىٰ وَيۡلَكُمۡ لَا تَفۡتَرُواْ عَلَى ٱللَّهِ كَذِبٗا فَيُسۡحِتَكُم بِعَذَابٖۖ وَقَدۡ خَابَ مَنِ ٱفۡتَرَىٰ\nУларга Мусо: «Ҳолингизга вой! Аллоҳга нисбатан ёлғон уйдирманг, бас, сизни азоб ила бутунлай ҳалок қилур. Батаҳқиқ, ким ёлғон уйдирса, ноумид бўлур», деди'}, {'chapter': 20, 'verse': 62, 'text': 'فَتَنَٰزَعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡ وَأَسَرُّواْ ٱلنَّجۡوَىٰ\nБас, улар ишлари ҳақида ўзаро аста шивирлашиб, мунозара қила бошладилар'}, {'chapter': 20, 'verse': 63, 'text': 'قَالُوٓاْ إِنۡ هَٰذَٰنِ لَسَٰحِرَٰنِ يُرِيدَانِ أَن يُخۡرِجَاكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِمَا وَيَذۡهَبَا بِطَرِيقَتِكُمُ ٱلۡمُثۡلَىٰ\nУлар: «Албатта, бу иккови сеҳргардирлар. Сизни сеҳрлари билан ерларингиздан чиқаришни ва сизнинг афзал йўлингизни йўқотишни хоҳларлар'}, {'chapter': 20, 'verse': 64, 'text': 'فَأَجۡمِعُواْ كَيۡدَكُمۡ ثُمَّ ٱئۡتُواْ صَفّٗاۚ وَقَدۡ أَفۡلَحَ ٱلۡيَوۡمَ مَنِ ٱسۡتَعۡلَىٰ\nБас, макр-ҳийлангизни жамлаб, бир саф бўлиб келинг. Батаҳқиқ, бугунги кунда ким устун келса, ўша ғолиб бўлур», дедилар'}, {'chapter': 20, 'verse': 65, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ أَوَّلَ مَنۡ أَلۡقَىٰ\nУлар: «Эй Мусо, сен ташлайсанми ёки биз аввал ташловчи бўлайликми?» – дедилар'}, {'chapter': 20, 'verse': 66, 'text': 'قَالَ بَلۡ أَلۡقُواْۖ فَإِذَا حِبَالُهُمۡ وَعِصِيُّهُمۡ يُخَيَّلُ إِلَيۡهِ مِن سِحۡرِهِمۡ أَنَّهَا تَسۡعَىٰ\nУ: «Йўқ. Сизлар ташланг», деди. Бирдан уларнинг арқонлари ва асолари унга сеҳрларидан худди ҳаракатланаётгандек бўлиб кўринди'}, {'chapter': 20, 'verse': 67, 'text': 'فَأَوۡجَسَ فِي نَفۡسِهِۦ خِيفَةٗ مُّوسَىٰ\nБас, Мусо ичида хавф ҳис этди'}, {'chapter': 20, 'verse': 68, 'text': 'قُلۡنَا لَا تَخَفۡ إِنَّكَ أَنتَ ٱلۡأَعۡلَىٰ\nБиз: «Қўрқма, албатта, сен устунсан'}, {'chapter': 20, 'verse': 69, 'text': 'وَأَلۡقِ مَا فِي يَمِينِكَ تَلۡقَفۡ مَا صَنَعُوٓاْۖ إِنَّمَا صَنَعُواْ كَيۡدُ سَٰحِرٖۖ وَلَا يُفۡلِحُ ٱلسَّاحِرُ حَيۡثُ أَتَىٰ\nҚўлингдаги нарсани ташлагин, уларнинг қилган ҳунарини ютиб юборур. Уларнинг қилган ҳунари сеҳргарнинг ҳийласи, холос. Сеҳргар қаерда бўлса ҳам, зафар топмас», дедик'}, {'chapter': 20, 'verse': 70, 'text': 'فَأُلۡقِيَ ٱلسَّحَرَةُ سُجَّدٗا قَالُوٓاْ ءَامَنَّا بِرَبِّ هَٰرُونَ وَمُوسَىٰ\nБас, сеҳргарлар: «Ҳорун ва Мусонинг Роббига иймон келтирдик», деб саждага ташландилар'}, {'chapter': 20, 'verse': 71, 'text': 'قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِي عَلَّمَكُمُ ٱلسِّحۡرَۖ فَلَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ وَلَأُصَلِّبَنَّكُمۡ فِي جُذُوعِ ٱلنَّخۡلِ وَلَتَعۡلَمُنَّ أَيُّنَآ أَشَدُّ عَذَابٗا وَأَبۡقَىٰ\nУ: «Сизга изн бермасимдан олдин унга иймон келтирдингизми?! Албатта, у сизга сеҳр ўргатган каттангиздир! Бас, албатта, қўл-оёқларингизни қарама-қаршисидан кесурман ва, албатта, сизларни хурмо танасига осурман, албатта, қайси биримизнинг азобимиз шиддатлироқ ва боқийроқ эканини билурсиз», деди'}, {'chapter': 20, 'verse': 72, 'text': 'قَالُواْ لَن نُّؤۡثِرَكَ عَلَىٰ مَا جَآءَنَا مِنَ ٱلۡبَيِّنَٰتِ وَٱلَّذِي فَطَرَنَاۖ فَٱقۡضِ مَآ أَنتَ قَاضٍۖ إِنَّمَا تَقۡضِي هَٰذِهِ ٱلۡحَيَوٰةَ ٱلدُّنۡيَآ\nУлар: «Биз сени зинҳор ўзимизга келган очиқ-ойдин (мўъжиза)лардан ва бизни яратган Зотдан устун қўймасмиз. Бас, нима ҳукм қилсанг, қилавер. Сен фақат ҳаёти дунёда ҳукм чиқарасан, холос'}, {'chapter': 20, 'verse': 73, 'text': 'إِنَّآ ءَامَنَّا بِرَبِّنَا لِيَغۡفِرَ لَنَا خَطَٰيَٰنَا وَمَآ أَكۡرَهۡتَنَا عَلَيۡهِ مِنَ ٱلسِّحۡرِۗ وَٱللَّهُ خَيۡرٞ وَأَبۡقَىٰٓ\nАлбатта, биз Роббимиз хатоларимизни ва сен бизни мажбур қилган сеҳрни мағфират қилиши учун иймон келтирдик. Аллоҳ яхши ва боқийдир», дедилар'}, {'chapter': 20, 'verse': 74, 'text': 'إِنَّهُۥ مَن يَأۡتِ رَبَّهُۥ مُجۡرِمٗا فَإِنَّ لَهُۥ جَهَنَّمَ لَا يَمُوتُ فِيهَا وَلَا يَحۡيَىٰ\nШубҳасиз, ким Роббига жиноятчи бўлган ҳолида келса, унга албатта жаҳаннам бордир. У ерда у ўлиб ўлмас, яшаб яшамас'}, {'chapter': 20, 'verse': 75, 'text': 'وَمَن يَأۡتِهِۦ مُؤۡمِنٗا قَدۡ عَمِلَ ٱلصَّـٰلِحَٰتِ فَأُوْلَـٰٓئِكَ لَهُمُ ٱلدَّرَجَٰتُ ٱلۡعُلَىٰ\nКим Унга мўмин ўлароқ, яхши амаллар қилган ҳолида келса, бас, ана ўшаларга олий даражалар'}, {'chapter': 20, 'verse': 76, 'text': 'جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ مَن تَزَكَّىٰ\nОстидан анҳорлар оқиб турган адн жаннатлари бор. Унда абадий қолурлар. Ана шу, пок бўлганнинг мукофотидир'}, {'chapter': 20, 'verse': 77, 'text': 'وَلَقَدۡ أَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَسۡرِ بِعِبَادِي فَٱضۡرِبۡ لَهُمۡ طَرِيقٗا فِي ٱلۡبَحۡرِ يَبَسٗا لَّا تَخَٰفُ دَرَكٗا وَلَا تَخۡشَىٰ\nБатаҳқиқ, Биз Мусога: «Сен бандаларим ила кечаси йўлга туш, уларга денгиздан қуруқ йўл оч, етиб олишларидан хавф қилма ва қўрқма», деб ваҳий қилдик'}, {'chapter': 20, 'verse': 78, 'text': 'فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ بِجُنُودِهِۦ فَغَشِيَهُم مِّنَ ٱلۡيَمِّ مَا غَشِيَهُمۡ\nБас, Фиръавн аскарлари ила уларнинг ортидан таъқиб этди. Денгиз уларни ўраганича ўраб олди'}, {'chapter': 20, 'verse': 79, 'text': 'وَأَضَلَّ فِرۡعَوۡنُ قَوۡمَهُۥ وَمَا هَدَىٰ\nФиръавн ўз қавмини адаштирди, тўғри йўлга бошламади'}, {'chapter': 20, 'verse': 80, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ قَدۡ أَنجَيۡنَٰكُم مِّنۡ عَدُوِّكُمۡ وَوَٰعَدۡنَٰكُمۡ جَانِبَ ٱلطُّورِ ٱلۡأَيۡمَنَ وَنَزَّلۡنَا عَلَيۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰ\nЭй, Бани Исроил, батаҳқиқ, Биз сизни душманингиздан қутқардик ва сиз билан Турнинг ўнг томонини ваъдалашдик ҳамда сизларга манн ва бедана туширдик'}, {'chapter': 20, 'verse': 81, 'text': 'كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَلَا تَطۡغَوۡاْ فِيهِ فَيَحِلَّ عَلَيۡكُمۡ غَضَبِيۖ وَمَن يَحۡلِلۡ عَلَيۡهِ غَضَبِي فَقَدۡ هَوَىٰ\nБиз сизга ризқ қилиб берган пок нарсалардан енглар, унда ҳаддингиздан ошманг, яна, бошингизга ғазабим тушиб қолмасин. Кимнинг бошига ғазабим тушса, бас, батаҳқиқ, ҳалок бўлибди'}, {'chapter': 20, 'verse': 82, 'text': 'وَإِنِّي لَغَفَّارٞ لِّمَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا ثُمَّ ٱهۡتَدَىٰ\nАлбатта, Мен тавба қилган, иймон келтириб, яхши амал қилган, сўнгра тўғри юрганларни кўплаб мағфират қилгувчиман'}, {'chapter': 20, 'verse': 83, 'text': '۞وَمَآ أَعۡجَلَكَ عَن قَوۡمِكَ يَٰمُوسَىٰ\nЭй Мусо, сени қавмингдан нима илгарилатди'}, {'chapter': 20, 'verse': 84, 'text': 'قَالَ هُمۡ أُوْلَآءِ عَلَىٰٓ أَثَرِي وَعَجِلۡتُ إِلَيۡكَ رَبِّ لِتَرۡضَىٰ\nУ: «Улар ана изимдалар. Рози бўлишинг учун мен Сенга шошдим, эй Роббим», деди'}, {'chapter': 20, 'verse': 85, 'text': 'قَالَ فَإِنَّا قَدۡ فَتَنَّا قَوۡمَكَ مِنۢ بَعۡدِكَ وَأَضَلَّهُمُ ٱلسَّامِرِيُّ\nУ зот: «Батаҳқиқ, Биз қавмингни сендан кейин фитнага дучор қилдик. Сомирий уларни адаштирди», деди'}, {'chapter': 20, 'verse': 86, 'text': 'فَرَجَعَ مُوسَىٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗاۚ قَالَ يَٰقَوۡمِ أَلَمۡ يَعِدۡكُمۡ رَبُّكُمۡ وَعۡدًا حَسَنًاۚ أَفَطَالَ عَلَيۡكُمُ ٱلۡعَهۡدُ أَمۡ أَرَدتُّمۡ أَن يَحِلَّ عَلَيۡكُمۡ غَضَبٞ مِّن رَّبِّكُمۡ فَأَخۡلَفۡتُم مَّوۡعِدِي\nБас, Мусо қавмига ғазабланган, афсусланган ҳолда қайтди. У: «Эй қавмим, Роббингиз сизга яхши ваъда қилмаганмиди?! Сизларга замон узоқ кўриндими ёки бошингизга Роббингиздан ғазаб тушишини хоҳладингизми?! Менга берган ваъдага хилоф қилибсиз?!» – деди'}, {'chapter': 20, 'verse': 87, 'text': 'قَالُواْ مَآ أَخۡلَفۡنَا مَوۡعِدَكَ بِمَلۡكِنَا وَلَٰكِنَّا حُمِّلۡنَآ أَوۡزَارٗا مِّن زِينَةِ ٱلۡقَوۡمِ فَقَذَفۡنَٰهَا فَكَذَٰلِكَ أَلۡقَى ٱلسَّامِرِيُّ\nУлар: «Биз сенга берган ваъдага ўз ихтиёримиз ила хилоф қилмадик. Лекин бизга у қавмнинг зийнатидан оғирликлар юкланган эди. Бас, уларни ташладик, Сомирий ана шундай илқо қилди», дедилар'}, {'chapter': 20, 'verse': 88, 'text': 'فَأَخۡرَجَ لَهُمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٞ فَقَالُواْ هَٰذَآ إِلَٰهُكُمۡ وَإِلَٰهُ مُوسَىٰ فَنَسِيَ\nБас, у (Сомирий) уларга бўкирадиган бузоқ шаклини чиқариб берди. Улар: «Мана шу сизнинг илоҳингиз ва Мусонинг ҳам илоҳи, фақат у унутди», дедилар'}, {'chapter': 20, 'verse': 89, 'text': 'أَفَلَا يَرَوۡنَ أَلَّا يَرۡجِعُ إِلَيۡهِمۡ قَوۡلٗا وَلَا يَمۡلِكُ لَهُمۡ ضَرّٗا وَلَا نَفۡعٗا\nУнинг ўзларига бирон сўз қайтара олмаётганини, на зарар ва на фойдага молик бўла олмаётганини кўрмаяптиларми'}, {'chapter': 20, 'verse': 90, 'text': 'وَلَقَدۡ قَالَ لَهُمۡ هَٰرُونُ مِن قَبۡلُ يَٰقَوۡمِ إِنَّمَا فُتِنتُم بِهِۦۖ وَإِنَّ رَبَّكُمُ ٱلرَّحۡمَٰنُ فَٱتَّبِعُونِي وَأَطِيعُوٓاْ أَمۡرِي\nБатаҳқиқ, илгари Ҳорун уларга: «Эй қавмим, бу билан фитнага дучор қилиндингиз, холос. Шубҳасиз, Роббингиз Роҳмандир. Бас, менга эргашинг. Менинг амримга итоат қилинг», деган эди'}, {'chapter': 20, 'verse': 91, 'text': 'قَالُواْ لَن نَّبۡرَحَ عَلَيۡهِ عَٰكِفِينَ حَتَّىٰ يَرۡجِعَ إِلَيۡنَا مُوسَىٰ\nУлар: «То Мусо бизга қайтгунича, унга ибодат қилишда бардавом бўламиз», дедилар'}, {'chapter': 20, 'verse': 92, 'text': 'قَالَ يَٰهَٰرُونُ مَا مَنَعَكَ إِذۡ رَأَيۡتَهُمۡ ضَلُّوٓاْ\nУ: «Эй Ҳорун, уларнинг залолатга кетганларини кўрганингда сени нима ман қилди'}, {'chapter': 20, 'verse': 93, 'text': 'أَلَّا تَتَّبِعَنِۖ أَفَعَصَيۡتَ أَمۡرِي\nМенга эргашишингдан?! Менинг амримга осий бўлдингми?!» – деди'}, {'chapter': 20, 'verse': 94, 'text': 'قَالَ يَبۡنَؤُمَّ لَا تَأۡخُذۡ بِلِحۡيَتِي وَلَا بِرَأۡسِيٓۖ إِنِّي خَشِيتُ أَن تَقُولَ فَرَّقۡتَ بَيۡنَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَلَمۡ تَرۡقُبۡ قَوۡلِي\nУ: «Эй, онамнинг ўғли, соч-соқолимдан тутма. Мен сенинг: «Бани Исроилни тафриқага солибсан, гапимга кирмабсан, дейишингдан қўрқдим», деди'}, {'chapter': 20, 'verse': 95, 'text': 'قَالَ فَمَا خَطۡبُكَ يَٰسَٰمِرِيُّ\nУ: «Бу нима қилганинг, эй, Сомирий?!» – деди'}, {'chapter': 20, 'verse': 96, 'text': 'قَالَ بَصُرۡتُ بِمَا لَمۡ يَبۡصُرُواْ بِهِۦ فَقَبَضۡتُ قَبۡضَةٗ مِّنۡ أَثَرِ ٱلرَّسُولِ فَنَبَذۡتُهَا وَكَذَٰلِكَ سَوَّلَتۡ لِي نَفۡسِي\nУ: «Улар кўрмаган нарсани кўрдим. Бас, Расулнинг изидан бир сиқим олдим, сўнгра уни ташладим. Ҳавойи нафсим менга шундай қилишни хуш кўрсатди», деди'}, {'chapter': 20, 'verse': 97, 'text': 'قَالَ فَٱذۡهَبۡ فَإِنَّ لَكَ فِي ٱلۡحَيَوٰةِ أَن تَقُولَ لَا مِسَاسَۖ وَإِنَّ لَكَ مَوۡعِدٗا لَّن تُخۡلَفَهُۥۖ وَٱنظُرۡ إِلَىٰٓ إِلَٰهِكَ ٱلَّذِي ظَلۡتَ عَلَيۡهِ عَاكِفٗاۖ لَّنُحَرِّقَنَّهُۥ ثُمَّ لَنَنسِفَنَّهُۥ فِي ٱلۡيَمِّ نَسۡفًا\nУ: «Бас, жўна! Кет! Энди сенга бу ҳаётда «тегиш йўқ» дейишинг бор, холос. Албатта, сенга хилоф қилмайдиган ваъдамиз бор. Ўзинг ибодатида бардавом бўлган илоҳингга назар солиб қўй. Биз уни, албатта, куйдирамиз, сўнгра денгизга сочиб юборамиз'}, {'chapter': 20, 'verse': 98, 'text': 'إِنَّمَآ إِلَٰهُكُمُ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَۚ وَسِعَ كُلَّ شَيۡءٍ عِلۡمٗا\nАлбатта, сизнинг илоҳингиз Аллоҳдир, холос. Ундан ўзга ибодатга сазовор зот йўқдир. У барча нарсани Ўз илми билан қамраб олгандир'}, {'chapter': 20, 'verse': 99, 'text': 'كَذَٰلِكَ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ مَا قَدۡ سَبَقَۚ وَقَدۡ ءَاتَيۡنَٰكَ مِن لَّدُنَّا ذِكۡرٗا\nШундай қилиб, сенга ўтган нарсалар хабарини қисса қилиб берурмиз. Батаҳқиқ, сенга Ўз тарафимиздан Зикрни бердик'}, {'chapter': 20, 'verse': 100, 'text': 'مَّنۡ أَعۡرَضَ عَنۡهُ فَإِنَّهُۥ يَحۡمِلُ يَوۡمَ ٱلۡقِيَٰمَةِ وِزۡرًا\nКим ундан юз ўгирса, албатта, у қиёмат куни (гуноҳ) юкини кўтарур'}, {'chapter': 20, 'verse': 101, 'text': 'خَٰلِدِينَ فِيهِۖ وَسَآءَ لَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ حِمۡلٗا\nУнда абадий қолурлар. Қиёмат кунида уларнинг юки қандай ҳам ёмон бўлди'}, {'chapter': 20, 'verse': 102, 'text': 'يَوۡمَ يُنفَخُ فِي ٱلصُّورِۚ وَنَحۡشُرُ ٱلۡمُجۡرِمِينَ يَوۡمَئِذٖ زُرۡقٗا\nУ кун сурга пуфланадиган кундир. Ўша кунда жиноятчиларни кўзлари кўк бўлган ҳолда тўплармиз'}, {'chapter': 20, 'verse': 103, 'text': 'يَتَخَٰفَتُونَ بَيۡنَهُمۡ إِن لَّبِثۡتُمۡ إِلَّا عَشۡرٗا\nУлар ўзаро: «Ўн кундан ортиқ турганингиз йўқ», дея шивирлашар'}, {'chapter': 20, 'verse': 104, 'text': 'نَّحۡنُ أَعۡلَمُ بِمَا يَقُولُونَ إِذۡ يَقُولُ أَمۡثَلُهُمۡ طَرِيقَةً إِن لَّبِثۡتُمۡ إِلَّا يَوۡمٗا\nБиз нима деяётганларини яхши билгувчимиз. Ўшанда уларнинг энг тўғри йўлдагиси: «Бир кундан ортиқ турганимиз йўқ», деган эди'}, {'chapter': 20, 'verse': 105, 'text': 'وَيَسۡـَٔلُونَكَ عَنِ ٱلۡجِبَالِ فَقُلۡ يَنسِفُهَا رَبِّي نَسۡفٗا\nСендан тоғлар ҳақида сўрарлар. Бас: «Роббим уларни мутлақо совуриб юборажак», дегин'}, {'chapter': 20, 'verse': 106, 'text': 'فَيَذَرُهَا قَاعٗا صَفۡصَفٗا\nСўнг уларни теп-текис ҳолга келтирур'}, {'chapter': 20, 'verse': 107, 'text': 'لَّا تَرَىٰ فِيهَا عِوَجٗا وَلَآ أَمۡتٗا\nУ ерда чуқур ҳам, дўнг ҳам кўрмассан'}, {'chapter': 20, 'verse': 108, 'text': 'يَوۡمَئِذٖ يَتَّبِعُونَ ٱلدَّاعِيَ لَا عِوَجَ لَهُۥۖ وَخَشَعَتِ ٱلۡأَصۡوَاتُ لِلرَّحۡمَٰنِ فَلَا تَسۡمَعُ إِلَّا هَمۡسٗا\nУ кунда чорловчига эргашурлар. Ундан бурилиш йўқ. Овозлар Роҳман(ҳайбати)дан жим қолмиш. Пичирлашдан бошқа нарса эшитмассан'}, {'chapter': 20, 'verse': 109, 'text': 'يَوۡمَئِذٖ لَّا تَنفَعُ ٱلشَّفَٰعَةُ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَٰنُ وَرَضِيَ لَهُۥ قَوۡلٗا\nУ кунда Роҳман унга изн берган ва сўзидан рози бўлганлардан бошқага шафоат фойда бермас'}, {'chapter': 20, 'verse': 110, 'text': 'يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَلَا يُحِيطُونَ بِهِۦ عِلۡمٗا\nУ уларнинг олдиларидаги нарсаларни ҳам, орқаларидаги нарсаларни ҳам билур. Улар эса, Уни илм ила иҳота қила олмаслар'}, {'chapter': 20, 'verse': 111, 'text': '۞وَعَنَتِ ٱلۡوُجُوهُ لِلۡحَيِّ ٱلۡقَيُّومِۖ وَقَدۡ خَابَ مَنۡ حَمَلَ ظُلۡمٗا\nБутун юзлар тирик ва доим турувчи Зотга бош эгмиш. Батаҳқиқ, ким зулм кўтарган бўлса, ноумид бўлмиш'}, {'chapter': 20, 'verse': 112, 'text': 'وَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ وَهُوَ مُؤۡمِنٞ فَلَا يَخَافُ ظُلۡمٗا وَلَا هَضۡمٗا\nКим мўмин ҳолида яхши амаллардан қилган бўлса, зулмдан ҳам, нуқсондан ҳам қўрқмас'}, {'chapter': 20, 'verse': 113, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَٰهُ قُرۡءَانًا عَرَبِيّٗا وَصَرَّفۡنَا فِيهِ مِنَ ٱلۡوَعِيدِ لَعَلَّهُمۡ يَتَّقُونَ أَوۡ يُحۡدِثُ لَهُمۡ ذِكۡرٗا\nШундай қилиб, Биз уни арабий Қуръон этиб нозил этдик. Шоядки, тақво қилсалар ёки уларда эслаш пайдо қилса деб, унда қўрқинчли ваъдаларни баён қилдик'}, {'chapter': 20, 'verse': 114, 'text': 'فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۗ وَلَا تَعۡجَلۡ بِٱلۡقُرۡءَانِ مِن قَبۡلِ أَن يُقۡضَىٰٓ إِلَيۡكَ وَحۡيُهُۥۖ وَقُل رَّبِّ زِدۡنِي عِلۡمٗا\nҲақ Подшоҳ — Аллоҳ олий бўлди. Сенга ваҳийи битгунча Қуръон(тиловати)га ошиқма ва: «Роббим, илмимни зиёда қилгин», дегин'}, {'chapter': 20, 'verse': 115, 'text': 'وَلَقَدۡ عَهِدۡنَآ إِلَىٰٓ ءَادَمَ مِن قَبۡلُ فَنَسِيَ وَلَمۡ نَجِدۡ لَهُۥ عَزۡمٗا\nБатаҳқиқ, илгари Биз Одам билан аҳдлашган эдик. Лекин у унутди. Унда азму қарор топмадик'}, {'chapter': 20, 'verse': 116, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ\nБиз фаришталарга: «Одамга сажда қилинг», деганимизда, улар сажда қилдилар. Магар иблис бош тортди'}, {'chapter': 20, 'verse': 117, 'text': 'فَقُلۡنَا يَـٰٓـَٔادَمُ إِنَّ هَٰذَا عَدُوّٞ لَّكَ وَلِزَوۡجِكَ فَلَا يُخۡرِجَنَّكُمَا مِنَ ٱلۡجَنَّةِ فَتَشۡقَىٰٓ\nБас, Биз: «Эй Одам, албатта, бу сенга ва жуфтингга душмандир. Яна, у икковингизни жаннатдан чиқариб, бадбахт бўлиб қолмагин'}, {'chapter': 20, 'verse': 118, 'text': 'إِنَّ لَكَ أَلَّا تَجُوعَ فِيهَا وَلَا تَعۡرَىٰ\nАлбатта, сен у ерда оч-яланғоч қолмассан'}, {'chapter': 20, 'verse': 119, 'text': 'وَأَنَّكَ لَا تَظۡمَؤُاْ فِيهَا وَلَا تَضۡحَىٰ\nАлбатта, сен у ерда ташна ҳам бўлмайсан, иссиқда ҳам қолмассан», дедик'}, {'chapter': 20, 'verse': 120, 'text': 'فَوَسۡوَسَ إِلَيۡهِ ٱلشَّيۡطَٰنُ قَالَ يَـٰٓـَٔادَمُ هَلۡ أَدُلُّكَ عَلَىٰ شَجَرَةِ ٱلۡخُلۡدِ وَمُلۡكٖ لَّا يَبۡلَىٰ\nБас, шайтон уни васваса қилди. У: «Эй Одам, мен сенга абадият дарахтини ва битмас-туганмас мулкни кўрсатайми?» – деди'}, {'chapter': 20, 'verse': 121, 'text': 'فَأَكَلَا مِنۡهَا فَبَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۚ وَعَصَىٰٓ ءَادَمُ رَبَّهُۥ فَغَوَىٰ\nБас, икковлари ундан едилар. Кейин, авратлари ўзларига очилиб қолиб, ўзларини жаннат барглари билан тўса бошладилар. Одам Роббига осий бўлди ва йўлдан озди'}, {'chapter': 20, 'verse': 122, 'text': 'ثُمَّ ٱجۡتَبَٰهُ رَبُّهُۥ فَتَابَ عَلَيۡهِ وَهَدَىٰ\nСўнгра, Робби уни танлаб олди, бас, тавбасини қабул қилди ва ҳидоятга бошлади'}, {'chapter': 20, 'verse': 123, 'text': 'قَالَ ٱهۡبِطَا مِنۡهَا جَمِيعَۢاۖ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَنِ ٱتَّبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشۡقَىٰ\nУ: «Ҳаммангиз бир-бирингизга душман ҳолда ундан тушинг. Агар мен томонимдан сизга ҳидоят келганида, ким ҳидоятимга эргашса, адашмас ва бадбахт бўлмас'}, {'chapter': 20, 'verse': 124, 'text': 'وَمَنۡ أَعۡرَضَ عَن ذِكۡرِي فَإِنَّ لَهُۥ مَعِيشَةٗ ضَنكٗا وَنَحۡشُرُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ أَعۡمَىٰ\nКим Менинг зикримдан юз ўгирса, албатта, унга торчилик ҳаёти бўлур ва қиёмат куни уни кўр ҳолида тирилтирурмиз», деди'}, {'chapter': 20, 'verse': 125, 'text': 'قَالَ رَبِّ لِمَ حَشَرۡتَنِيٓ أَعۡمَىٰ وَقَدۡ كُنتُ بَصِيرٗا\nУ: «Эй Роббим, нима учун мени кўр ҳолда тирилтирдинг. Ахир, кўрувчи эдим-ку?!» – деди'}, {'chapter': 20, 'verse': 126, 'text': 'قَالَ كَذَٰلِكَ أَتَتۡكَ ءَايَٰتُنَا فَنَسِيتَهَاۖ وَكَذَٰلِكَ ٱلۡيَوۡمَ تُنسَىٰ\nУ: «Шундай. Сенга оятларимиз келди. Сен эса, уларни унутдинг? Шунингдек, сен ҳам бугун унутилурсан», деди'}, {'chapter': 20, 'verse': 127, 'text': 'وَكَذَٰلِكَ نَجۡزِي مَنۡ أَسۡرَفَ وَلَمۡ يُؤۡمِنۢ بِـَٔايَٰتِ رَبِّهِۦۚ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَشَدُّ وَأَبۡقَىٰٓ\nИсроф қилган ва Робби оятларига иймон келтирмаганларни ана шундай жазолаймиз. Охират азоби, албатта, ашаддий ва боқийдир'}, {'chapter': 20, 'verse': 128, 'text': 'أَفَلَمۡ يَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ يَمۡشُونَ فِي مَسَٰكِنِهِمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّأُوْلِي ٱلنُّهَىٰ\nУлардан илгари уларнинг масканларида юрган асрлардан қанчадан-қанчасини ҳалок қилганимиз ҳам ҳидоят қилмадими? Албатта, бунда ақл эгалари учун ибратлар бордир'}, {'chapter': 20, 'verse': 129, 'text': 'وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَكَانَ لِزَامٗا وَأَجَلٞ مُّسَمّٗى\nАгар Роббингдан сўз ўтиб қолмаганида ва номланган ажал бўлмаганида, албатта, (азоб) лозим бўлар эди'}, {'chapter': 20, 'verse': 130, 'text': 'فَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ غُرُوبِهَاۖ وَمِنۡ ءَانَآيِٕ ٱلَّيۡلِ فَسَبِّحۡ وَأَطۡرَافَ ٱلنَّهَارِ لَعَلَّكَ تَرۡضَىٰ\nБас, улар айтаётган нарсаларга сабр қил. Роббингга қуёш чиқишидан олдин ва ботишидан олдин тасбиҳ ва ҳамд айт. Шунингдек, кечанинг бир қисмида ва кундузнинг атрофида ҳам тасбиҳ айт. Шоядки, рози бўлсанг'}, {'chapter': 20, 'verse': 131, 'text': 'وَلَا تَمُدَّنَّ عَيۡنَيۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّنۡهُمۡ زَهۡرَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا لِنَفۡتِنَهُمۡ فِيهِۚ وَرِزۡقُ رَبِّكَ خَيۡرٞ وَأَبۡقَىٰ\nКўзларингни уларни синаш учун айримларига баҳраманд қилиб берганимиз ҳаёти дунёнинг турли зийнатларига тикма. Роббингнинг ризқи яхши ва боқийдир'}, {'chapter': 20, 'verse': 132, 'text': 'وَأۡمُرۡ أَهۡلَكَ بِٱلصَّلَوٰةِ وَٱصۡطَبِرۡ عَلَيۡهَاۖ لَا نَسۡـَٔلُكَ رِزۡقٗاۖ نَّحۡنُ نَرۡزُقُكَۗ وَٱلۡعَٰقِبَةُ لِلتَّقۡوَىٰ\nАҳлингни намозга амр эт ва ўзинг унга сабр қил. Биз сендан ризқ сўрамасмиз. Биз сенга ризқ берамиз. Оқибат тақвоникидир'}, {'chapter': 20, 'verse': 133, 'text': 'وَقَالُواْ لَوۡلَا يَأۡتِينَا بِـَٔايَةٖ مِّن رَّبِّهِۦٓۚ أَوَلَمۡ تَأۡتِهِم بَيِّنَةُ مَا فِي ٱلصُّحُفِ ٱلۡأُولَىٰ\nУлар: «У бизга Роббидан мўъжиза келтирса эди», дерлар. Ахир, уларга аввалги саҳифалардаги нарсаларнинг баёноти келмадими'}, {'chapter': 20, 'verse': 134, 'text': 'وَلَوۡ أَنَّآ أَهۡلَكۡنَٰهُم بِعَذَابٖ مِّن قَبۡلِهِۦ لَقَالُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ مِن قَبۡلِ أَن نَّذِلَّ وَنَخۡزَىٰ\nАгар Биз уларни у(келиши)дан олдин азоб-ла ҳалок қилганимизда эди, албатта: «Эй Роббимиз, бизга Пайғамбар юборганингда эди, хору зор ва шарманда бўлишимиздан олдин оятларингга эргашган бўлар эдик», дердилар'}, {'chapter': 20, 'verse': 135, 'text': 'قُلۡ كُلّٞ مُّتَرَبِّصٞ فَتَرَبَّصُواْۖ فَسَتَعۡلَمُونَ مَنۡ أَصۡحَٰبُ ٱلصِّرَٰطِ ٱلسَّوِيِّ وَمَنِ ٱهۡتَدَىٰ\nСен: «Барча кутгувчидир. Бас, кутинглар. Албатта, кимлар тўғри йўл соҳиблари ва кимлар ҳидоят топганини билурсизлар», деб айт'}], '21': [{'chapter': 21, 'verse': 1, 'text': 'ٱقۡتَرَبَ لِلنَّاسِ حِسَابُهُمۡ وَهُمۡ فِي غَفۡلَةٖ مُّعۡرِضُونَ\nОдамларга ҳисоблари яқинлашиб қолди. Улар эса, ғафлатда, юз ўгирувчи бўлган ҳолларида'}, {'chapter': 21, 'verse': 2, 'text': 'مَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّن رَّبِّهِم مُّحۡدَثٍ إِلَّا ٱسۡتَمَعُوهُ وَهُمۡ يَلۡعَبُونَ\nҚачон Роббиларидан янги эслатма келса, уни фақат ўйнаётган ҳолларида тингларлар'}, {'chapter': 21, 'verse': 3, 'text': 'لَاهِيَةٗ قُلُوبُهُمۡۗ وَأَسَرُّواْ ٱلنَّجۡوَى ٱلَّذِينَ ظَلَمُواْ هَلۡ هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡۖ أَفَتَأۡتُونَ ٱلسِّحۡرَ وَأَنتُمۡ تُبۡصِرُونَ\nҚалблари ғофил бўлиб. Зулм қилганлар ўзаро: «Бу ўзимизга ўхшаган башардан ўзга эмас-ку. Ўзингиз кўриб туриб сеҳрга келасизми?» – деб пичирлашарлар'}, {'chapter': 21, 'verse': 4, 'text': 'قَالَ رَبِّي يَعۡلَمُ ٱلۡقَوۡلَ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nУ: «Роббим осмону ердаги сўзни билур. У эшитгувчи ва билгувчи Зотдир», деди'}, {'chapter': 21, 'verse': 5, 'text': 'بَلۡ قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمِۭ بَلِ ٱفۡتَرَىٰهُ بَلۡ هُوَ شَاعِرٞ فَلۡيَأۡتِنَا بِـَٔايَةٖ كَمَآ أُرۡسِلَ ٱلۡأَوَّلُونَ\nУлар: «Балки, алғов-далғов тушлардир, балки, ўзи уйдириб олгандир, балки, у шоирдир. Бас, бизга аввалгиларга юборилганидек мўъжиза келтирсин», дедилар'}, {'chapter': 21, 'verse': 6, 'text': 'مَآ ءَامَنَتۡ قَبۡلَهُم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَآۖ أَفَهُمۡ يُؤۡمِنُونَ\nУлардан олдин бирор шаҳар (аҳолиси) иймон келтирмади. Биз уларни ҳалок қилдик. Энди улар иймон келтирармидилар'}, {'chapter': 21, 'verse': 7, 'text': 'وَمَآ أَرۡسَلۡنَا قَبۡلَكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِمۡۖ فَسۡـَٔلُوٓاْ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ\nБиз сендан илгари ҳам фақат эр кишиларни Пайғамбар қилиб юбориб, уларга ваҳий қилганмиз, холос. Агар билмасангиз, зикр аҳлларидан сўранг'}, {'chapter': 21, 'verse': 8, 'text': 'وَمَا جَعَلۡنَٰهُمۡ جَسَدٗا لَّا يَأۡكُلُونَ ٱلطَّعَامَ وَمَا كَانُواْ خَٰلِدِينَ\nБиз уларни таом емайдиган жасад қилганимиз йўқ. Улар абадий қолгувчи ҳам бўлган эмаслар'}, {'chapter': 21, 'verse': 9, 'text': 'ثُمَّ صَدَقۡنَٰهُمُ ٱلۡوَعۡدَ فَأَنجَيۡنَٰهُمۡ وَمَن نَّشَآءُ وَأَهۡلَكۡنَا ٱلۡمُسۡرِفِينَ\nСўнгра, уларга (берган) ваъдамиз устидан чиқдик. Бас, уларга ҳамда Ўзимиз хоҳлаганларга нажот бердик ва исрофчиларни ҳалок қилдик'}, {'chapter': 21, 'verse': 10, 'text': 'لَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ كِتَٰبٗا فِيهِ ذِكۡرُكُمۡۚ أَفَلَا تَعۡقِلُونَ\nБатаҳқиқ, Биз сизга китоб нозил қилдик. Унда сизнинг зикрингиз бор. Ақл ишлатмайсизларми'}, {'chapter': 21, 'verse': 11, 'text': 'وَكَمۡ قَصَمۡنَا مِن قَرۡيَةٖ كَانَتۡ ظَالِمَةٗ وَأَنشَأۡنَا بَعۡدَهَا قَوۡمًا ءَاخَرِينَ\nБиз золим бўлган шаҳар-қишлоқлардан қанчасини (белини) синдирдик ва улардан кейин бошқа қавмларни пайдо этдик'}, {'chapter': 21, 'verse': 12, 'text': 'فَلَمَّآ أَحَسُّواْ بَأۡسَنَآ إِذَا هُم مِّنۡهَا يَرۡكُضُونَ\nБизнинг азобимизни ҳис қилганларида эса, бирдан у ердан қочишга бошларлар'}, {'chapter': 21, 'verse': 13, 'text': 'لَا تَرۡكُضُواْ وَٱرۡجِعُوٓاْ إِلَىٰ مَآ أُتۡرِفۡتُمۡ فِيهِ وَمَسَٰكِنِكُمۡ لَعَلَّكُمۡ تُسۡـَٔلُونَ\nҚочманглар! маишатпарастлик қилган нарсаларингизга, масканларингизга қайтинглар. Ҳали, эҳтимол, сўралурсизлар'}, {'chapter': 21, 'verse': 14, 'text': 'قَالُواْ يَٰوَيۡلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ\nУлар: «Ҳолимизга вой! Биз, албатта, золим бўлган эканмиз», дедилар'}, {'chapter': 21, 'verse': 15, 'text': 'فَمَا زَالَت تِّلۡكَ دَعۡوَىٰهُمۡ حَتَّىٰ جَعَلۡنَٰهُمۡ حَصِيدًا خَٰمِدِينَ\nУларнинг ўша дод-войлари биз уларни ўрилган ўтдай, совуган кулдай қилгунимизча бардавом бўлди'}, {'chapter': 21, 'verse': 16, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَآءَ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا لَٰعِبِينَ\nБиз осмонлару ерни ва уларнинг орасидаги нарсаларни ўйин учун яратганимиз йўқ'}, {'chapter': 21, 'verse': 17, 'text': 'لَوۡ أَرَدۡنَآ أَن نَّتَّخِذَ لَهۡوٗا لَّٱتَّخَذۡنَٰهُ مِن لَّدُنَّآ إِن كُنَّا فَٰعِلِينَ\nАгар Биз кўнгилхуши тутишни ирода қилсак, уни қилувчи бўлганимизда ҳам, Ўз томонимиздан тутар эдик'}, {'chapter': 21, 'verse': 18, 'text': 'بَلۡ نَقۡذِفُ بِٱلۡحَقِّ عَلَى ٱلۡبَٰطِلِ فَيَدۡمَغُهُۥ فَإِذَا هُوَ زَاهِقٞۚ وَلَكُمُ ٱلۡوَيۡلُ مِمَّا تَصِفُونَ\nЙўқ, Биз ҳақни ботил устига отурмиз. Бас, уни яксон қилур. Кўрибсанки, у (ботил) заволга учрабди. Сизларга эса, қилган васфларингиздан ҳалокат келур'}, {'chapter': 21, 'verse': 19, 'text': 'وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَنۡ عِندَهُۥ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَلَا يَسۡتَحۡسِرُونَ\nОсмонлару ердаги барча жонзотлар Уникидир. Унинг ҳузуридагилар ибодатидан такаббурлик қилмаслар ва малол олмаслар'}, {'chapter': 21, 'verse': 20, 'text': 'يُسَبِّحُونَ ٱلَّيۡلَ وَٱلنَّهَارَ لَا يَفۡتُرُونَ\nУлар кечаю кундуз тасбиҳ айтарлар. Чарчамаслар'}, {'chapter': 21, 'verse': 21, 'text': 'أَمِ ٱتَّخَذُوٓاْ ءَالِهَةٗ مِّنَ ٱلۡأَرۡضِ هُمۡ يُنشِرُونَ\nЁки улар ердан худолар тутдилар-у, ўшалар тирилтирадиларми'}, {'chapter': 21, 'verse': 22, 'text': 'لَوۡ كَانَ فِيهِمَآ ءَالِهَةٌ إِلَّا ٱللَّهُ لَفَسَدَتَاۚ فَسُبۡحَٰنَ ٱللَّهِ رَبِّ ٱلۡعَرۡشِ عَمَّا يَصِفُونَ\nАгар у(осмонлару ер)ларда Аллоҳдан ўзга худолар бўлганида, бузилиб кетар эдилар. Аршнинг Робби Аллоҳ улар васф қилган нарсадан покдир'}, {'chapter': 21, 'verse': 23, 'text': 'لَا يُسۡـَٔلُ عَمَّا يَفۡعَلُ وَهُمۡ يُسۡـَٔلُونَ\nУ қилган нарсасидан сўралмас. Ҳолбуки, улар сўралурлар'}, {'chapter': 21, 'verse': 24, 'text': 'أَمِ ٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗۖ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡۖ هَٰذَا ذِكۡرُ مَن مَّعِيَ وَذِكۡرُ مَن قَبۡلِيۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ ٱلۡحَقَّۖ فَهُم مُّعۡرِضُونَ\nЁки Ундан ўзга худолар тутдиларми? Сен: «Ҳужжатларингизни келтиринг. Мана бу мен билан биргаларнинг зикридир ва мендан олдингиларнинг зикридир», дегин. Йўқ, уларнинг кўплари ҳақни билмаслар. Бас, улар юз ўгиргувчилардир'}, {'chapter': 21, 'verse': 25, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٍ إِلَّا نُوحِيٓ إِلَيۡهِ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعۡبُدُونِ\nСендан илгари юборган ҳар бир Пайғамбарга: «Албатта, Мендан ўзга ибодатга сазовор илоҳ йўқ. Бас, Менга ибодат қилинг», деб ваҳий қилганмиз'}, {'chapter': 21, 'verse': 26, 'text': 'وَقَالُواْ ٱتَّخَذَ ٱلرَّحۡمَٰنُ وَلَدٗاۗ سُبۡحَٰنَهُۥۚ بَلۡ عِبَادٞ مُّكۡرَمُونَ\nУлар: «Роҳман фарзанд тутди», дедилар. У покдир. Йўқ! Икром этилган бандалардирлар'}, {'chapter': 21, 'verse': 27, 'text': 'لَا يَسۡبِقُونَهُۥ بِٱلۡقَوۡلِ وَهُم بِأَمۡرِهِۦ يَعۡمَلُونَ\nУлар Ундан олдин сўз айтмаслар. Улар Унинг амри ила амал қилурлар'}, {'chapter': 21, 'verse': 28, 'text': 'يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَلَا يَشۡفَعُونَ إِلَّا لِمَنِ ٱرۡتَضَىٰ وَهُم مِّنۡ خَشۡيَتِهِۦ مُشۡفِقُونَ\nУ зот уларнинг олдиларидаги нарсани ҳам, ортларидаги нарсани ҳам билур. Улар фақат У зот рози бўлган шахсларгагина шафоат қилурлар. Улар У зотнинг қўрқинчидан титраган ҳолда турурлар'}, {'chapter': 21, 'verse': 29, 'text': '۞وَمَن يَقُلۡ مِنۡهُمۡ إِنِّيٓ إِلَٰهٞ مِّن دُونِهِۦ فَذَٰلِكَ نَجۡزِيهِ جَهَنَّمَۚ كَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ\nУлардан ким: «Мен Ундан ўзга илоҳман», деса, бас, ўшани жаҳаннам ила жазолармиз. Золимларни шундай жазолармиз'}, {'chapter': 21, 'verse': 30, 'text': 'أَوَلَمۡ يَرَ ٱلَّذِينَ كَفَرُوٓاْ أَنَّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ كَانَتَا رَتۡقٗا فَفَتَقۡنَٰهُمَاۖ وَجَعَلۡنَا مِنَ ٱلۡمَآءِ كُلَّ شَيۡءٍ حَيٍّۚ أَفَلَا يُؤۡمِنُونَ\nКуфр келтирганлар осмонлару ер битишган бўлган эканини, бас, Биз уларни очганимизни ва сувдан ҳар бир тирик нарсани қилганимизни билмасларми? Иймон келтирмасларми'}, {'chapter': 21, 'verse': 31, 'text': 'وَجَعَلۡنَا فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِهِمۡ وَجَعَلۡنَا فِيهَا فِجَاجٗا سُبُلٗا لَّعَلَّهُمۡ يَهۡتَدُونَ\nБиз ерда уларнинг тебранмасликлари учун тоғлар қилдик ва шоядки, тўғри йўл топсалар, деб унда даралар, йўллар қилдик'}, {'chapter': 21, 'verse': 32, 'text': 'وَجَعَلۡنَا ٱلسَّمَآءَ سَقۡفٗا مَّحۡفُوظٗاۖ وَهُمۡ عَنۡ ءَايَٰتِهَا مُعۡرِضُونَ\nВа осмонни сақланган шифт қилиб қўйдик. Улар эса, Унинг аломатларидан юз ўгиргувчи бўлмоқдалар'}, {'chapter': 21, 'verse': 33, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ ٱلَّيۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ فِي فَلَكٖ يَسۡبَحُونَ\nУ кечаю кундузни, қуёшу ойни яратган Зотдир. Ҳаммаси фалакда сузмоқдалар'}, {'chapter': 21, 'verse': 34, 'text': 'وَمَا جَعَلۡنَا لِبَشَرٖ مِّن قَبۡلِكَ ٱلۡخُلۡدَۖ أَفَإِيْن مِّتَّ فَهُمُ ٱلۡخَٰلِدُونَ\nСендан олдин ҳам бирор башарга абадийликни қилганимиз йўқ. Агар сен ўлсанг, улар абадий қолар эканларми'}, {'chapter': 21, 'verse': 35, 'text': 'كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۗ وَنَبۡلُوكُم بِٱلشَّرِّ وَٱلۡخَيۡرِ فِتۡنَةٗۖ وَإِلَيۡنَا تُرۡجَعُونَ\nҲар бир жон ўлимни татиб кўргувчидир. Биз сизларни ёмонлик ва яхшилик ила имтиҳон учун синаймиз ҳамда Бизгагина қайтарилурсиз'}, {'chapter': 21, 'verse': 36, 'text': 'وَإِذَا رَءَاكَ ٱلَّذِينَ كَفَرُوٓاْ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي يَذۡكُرُ ءَالِهَتَكُمۡ وَهُم بِذِكۡرِ ٱلرَّحۡمَٰنِ هُمۡ كَٰفِرُونَ\nКуфр келтирганлар сени кўрганларида, масхара қилиб: «Худоларингизни зикр қилаётган шуми?!» дейишдан бошқани билмаслар. Ҳолбуки, улар Роҳманнинг зикрига ўзлари кофирдирлар'}, {'chapter': 21, 'verse': 37, 'text': 'خُلِقَ ٱلۡإِنسَٰنُ مِنۡ عَجَلٖۚ سَأُوْرِيكُمۡ ءَايَٰتِي فَلَا تَسۡتَعۡجِلُونِ\nИнсон шошқалоқ ўлароқ яратилгандир. Мен, албатта, сизларга Ўз оят-аломатларимни кўрсатурман. Бас, мени шошилтирманглар'}, {'chapter': 21, 'verse': 38, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар содиқ бўлсангиз, бу ваъда қачон бўлур», дерлар'}, {'chapter': 21, 'verse': 39, 'text': 'لَوۡ يَعۡلَمُ ٱلَّذِينَ كَفَرُواْ حِينَ لَا يَكُفُّونَ عَن وُجُوهِهِمُ ٱلنَّارَ وَلَا عَن ظُهُورِهِمۡ وَلَا هُمۡ يُنصَرُونَ\nКуфр келтирганлар юзларидан ва сиртларидан оловни тўса олмайдиган ва ўзларига ёрдам берилмайдиган вақтни билсалар эди'}, {'chapter': 21, 'verse': 40, 'text': 'بَلۡ تَأۡتِيهِم بَغۡتَةٗ فَتَبۡهَتُهُمۡ فَلَا يَسۡتَطِيعُونَ رَدَّهَا وَلَا هُمۡ يُنظَرُونَ\nЙўқ! У уларга тўсатдан келиб, ҳайрату даҳшатга солур. Бас, уни рад қилишга қодир бўлмаслар ва уларга муҳлат ҳам берилмас'}, {'chapter': 21, 'verse': 41, 'text': 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nБатаҳқиқ, сендан олдин ҳам Пайғамбарлар масхара қилинган. Бас, уларни масхара қилганларга ўзлари масхара этиб юрган нарса тушди'}, {'chapter': 21, 'verse': 42, 'text': 'قُلۡ مَن يَكۡلَؤُكُم بِٱلَّيۡلِ وَٱلنَّهَارِ مِنَ ٱلرَّحۡمَٰنِۚ بَلۡ هُمۡ عَن ذِكۡرِ رَبِّهِم مُّعۡرِضُونَ\nСен: «Кечаси ва кундузи сизни Роҳмандан ким қўрийди?» – дегин. Йўқ! Улар Роббилари зикридан юз ўгиргувчилардир'}, {'chapter': 21, 'verse': 43, 'text': 'أَمۡ لَهُمۡ ءَالِهَةٞ تَمۡنَعُهُم مِّن دُونِنَاۚ لَا يَسۡتَطِيعُونَ نَصۡرَ أَنفُسِهِمۡ وَلَا هُم مِّنَّا يُصۡحَبُونَ\nЁки уларнинг Биздан ўзга ҳимоя қиладиган «худо»лари борми? Улар ўзларига ҳам ёрдам беришга қодир бўла олмаслар ва Биздан дўстлик топмаслар'}, {'chapter': 21, 'verse': 44, 'text': 'بَلۡ مَتَّعۡنَا هَـٰٓؤُلَآءِ وَءَابَآءَهُمۡ حَتَّىٰ طَالَ عَلَيۡهِمُ ٱلۡعُمُرُۗ أَفَلَا يَرَوۡنَ أَنَّا نَأۡتِي ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَآۚ أَفَهُمُ ٱلۡغَٰلِبُونَ\nБалки, Биз уларни ва ота-боболарини баҳраманд қилдик, ҳатто, уларга умр узун кўринди. Улар ерга келиб, уни атрофидан қисқартираётганимизни кўрмайдиларми?! Fолиб келгувчи уларми'}, {'chapter': 21, 'verse': 45, 'text': 'قُلۡ إِنَّمَآ أُنذِرُكُم بِٱلۡوَحۡيِۚ وَلَا يَسۡمَعُ ٱلصُّمُّ ٱلدُّعَآءَ إِذَا مَا يُنذَرُونَ\nСен: «Мен сизларни фақат ваҳий орқалигина огоҳлантираман. Гунглар огоҳлантирилганларида даъватни эшитмаслар», дегин'}, {'chapter': 21, 'verse': 46, 'text': 'وَلَئِن مَّسَّتۡهُمۡ نَفۡحَةٞ مِّنۡ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَٰوَيۡلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ\nҚасамки, агар уларга Роббинг азобидан салгина етса, албатта: «Ҳолимизга вой! Ҳақиқатан, биз золимлар бўлган эканмиз», дерлар'}, {'chapter': 21, 'verse': 47, 'text': 'وَنَضَعُ ٱلۡمَوَٰزِينَ ٱلۡقِسۡطَ لِيَوۡمِ ٱلۡقِيَٰمَةِ فَلَا تُظۡلَمُ نَفۡسٞ شَيۡـٔٗاۖ وَإِن كَانَ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٍ أَتَيۡنَا بِهَاۗ وَكَفَىٰ بِنَا حَٰسِبِينَ\nБиз қиёмат куни учун адолат тарозуларини қўюрмиз. Бирор жонга ҳеч қандай зулм қилинмас. Агар (амал) ачитқи донаси оғирлигича бўлса ҳам, келтирамиз. Ҳисобчиликда Ўзимиз кифоя қилурмиз'}, {'chapter': 21, 'verse': 48, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ وَهَٰرُونَ ٱلۡفُرۡقَانَ وَضِيَآءٗ وَذِكۡرٗا لِّلۡمُتَّقِينَ\nБатаҳқиқ, Биз Мусо ва Ҳорунга Фурқонни тақводорлар учун зиё ва эслатма қилиб бердик'}, {'chapter': 21, 'verse': 49, 'text': 'ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ وَهُم مِّنَ ٱلسَّاعَةِ مُشۡفِقُونَ\nУлар Роббиларидан ғойибона қўрқурлар ва улар (қиёмат) соатидан титрарлар'}, {'chapter': 21, 'verse': 50, 'text': 'وَهَٰذَا ذِكۡرٞ مُّبَارَكٌ أَنزَلۡنَٰهُۚ أَفَأَنتُمۡ لَهُۥ مُنكِرُونَ\nБу Биз нозил қилган муборак эслатмадир. Сизлар уни инкор қилгувчи бўласизларми'}, {'chapter': 21, 'verse': 51, 'text': '۞وَلَقَدۡ ءَاتَيۡنَآ إِبۡرَٰهِيمَ رُشۡدَهُۥ مِن قَبۡلُ وَكُنَّا بِهِۦ عَٰلِمِينَ\nБатаҳқиқ, илгари Биз Иброҳимга рушди ҳидоятни берган эдик. Биз унинг (ҳолини) билгувчи эдик'}, {'chapter': 21, 'verse': 52, 'text': 'إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَا هَٰذِهِ ٱلتَّمَاثِيلُ ٱلَّتِيٓ أَنتُمۡ لَهَا عَٰكِفُونَ\nЎшанда у отасига ва қавмига: «Манави сиз ибодатига берилаётган ҳайкаллар нима?» – деган эди'}, {'chapter': 21, 'verse': 53, 'text': 'قَالُواْ وَجَدۡنَآ ءَابَآءَنَا لَهَا عَٰبِدِينَ\nУлар: «Ота-боболаримизни уларга ибодат қилган ҳолларида топганмиз», дедилар'}, {'chapter': 21, 'verse': 54, 'text': 'قَالَ لَقَدۡ كُنتُمۡ أَنتُمۡ وَءَابَآؤُكُمۡ فِي ضَلَٰلٖ مُّبِينٖ\nУ: «Батаҳқиқ, сиз ҳам, ота-боболарингиз ҳам очиқ-ойдин залолатдасизлар», деди'}, {'chapter': 21, 'verse': 55, 'text': 'قَالُوٓاْ أَجِئۡتَنَا بِٱلۡحَقِّ أَمۡ أَنتَ مِنَ ٱللَّـٰعِبِينَ\nУлар: «Сен бизга ҳақ ила келдингми ёки ўйин қилгувчиларданмисан?» – дедилар'}, {'chapter': 21, 'verse': 56, 'text': 'قَالَ بَل رَّبُّكُمۡ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلَّذِي فَطَرَهُنَّ وَأَنَا۠ عَلَىٰ ذَٰلِكُم مِّنَ ٱلشَّـٰهِدِينَ\nУ: «Йўқ! Роббингиз осмонлару ернинг Роббидир. У зот уларни яратгандир. Мен бунга гувоҳлик бергувчиларданман'}, {'chapter': 21, 'verse': 57, 'text': 'وَتَٱللَّهِ لَأَكِيدَنَّ أَصۡنَٰمَكُم بَعۡدَ أَن تُوَلُّواْ مُدۡبِرِينَ\nАллоҳга қасамки, сизлар ортга ўгирилиб кетганингиздан кейин санамларингизга бир бало қиламан», деди'}, {'chapter': 21, 'verse': 58, 'text': 'فَجَعَلَهُمۡ جُذَٰذًا إِلَّا كَبِيرٗا لَّهُمۡ لَعَلَّهُمۡ إِلَيۡهِ يَرۡجِعُونَ\nБас, уларни парча-парча қилиб ташлади. Магар энг каттасини, шоядки, унга қайтсалар, деб қолдирди'}, {'chapter': 21, 'verse': 59, 'text': 'قَالُواْ مَن فَعَلَ هَٰذَا بِـَٔالِهَتِنَآ إِنَّهُۥ لَمِنَ ٱلظَّـٰلِمِينَ\nУлар: «Бизнинг худоларимизга ким бундай қилди?! Албатта, у золимлардандир!» – дедилар'}, {'chapter': 21, 'verse': 60, 'text': 'قَالُواْ سَمِعۡنَا فَتٗى يَذۡكُرُهُمۡ يُقَالُ لَهُۥٓ إِبۡرَٰهِيمُ\nУлар: «Уларни эслаб юрган бир йигитни эшитган эдик, уни Иброҳим, деб айтиларди», дедилар'}, {'chapter': 21, 'verse': 61, 'text': 'قَالُواْ فَأۡتُواْ بِهِۦ عَلَىٰٓ أَعۡيُنِ ٱلنَّاسِ لَعَلَّهُمۡ يَشۡهَدُونَ\nУлар: «Уни одамлар кўзи олдига олиб келинглар. Шоядки, гувоҳ бўлсалар», дедилар'}, {'chapter': 21, 'verse': 62, 'text': 'قَالُوٓاْ ءَأَنتَ فَعَلۡتَ هَٰذَا بِـَٔالِهَتِنَا يَـٰٓإِبۡرَٰهِيمُ\nУлар: «Худоларимизга буни сен қилдингми, эй Иброҳим?!» – дедилар'}, {'chapter': 21, 'verse': 63, 'text': 'قَالَ بَلۡ فَعَلَهُۥ كَبِيرُهُمۡ هَٰذَا فَسۡـَٔلُوهُمۡ إِن كَانُواْ يَنطِقُونَ\nУ: «Йўқ, буни манави, уларнинг каттаси қилди. Агар гапирадиган бўлсалар, улардан сўраб кўринглар», деди'}, {'chapter': 21, 'verse': 64, 'text': 'فَرَجَعُوٓاْ إِلَىٰٓ أَنفُسِهِمۡ فَقَالُوٓاْ إِنَّكُمۡ أَنتُمُ ٱلظَّـٰلِمُونَ\nБас, улар ўзларига келдилар ва: «Албатта, сизлар ўзингиз золимдирсизлар», дедилар'}, {'chapter': 21, 'verse': 65, 'text': 'ثُمَّ نُكِسُواْ عَلَىٰ رُءُوسِهِمۡ لَقَدۡ عَلِمۡتَ مَا هَـٰٓؤُلَآءِ يَنطِقُونَ\nСўнгра, яна бошлари айланиб: «Сен, батаҳқиқ, буларнинг гапирмаслигини билар эдинг!» – дедилар'}, {'chapter': 21, 'verse': 66, 'text': 'قَالَ أَفَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكُمۡ شَيۡـٔٗا وَلَا يَضُرُّكُمۡ\nУ: «Сиз, Аллоҳни қўйиб, ҳеч манфаат ҳам, зарар ҳам бермайдиган нарсага ибодат қиласизми'}, {'chapter': 21, 'verse': 67, 'text': 'أُفّٖ لَّكُمۡ وَلِمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِۚ أَفَلَا تَعۡقِلُونَ\nСизга ҳам, Аллоҳни қўйиб, ибодат қилаётган нарсаларингизга ҳам суф-э! Ахир, ақл юритиб кўрмайсизларми?!» – деди'}, {'chapter': 21, 'verse': 68, 'text': 'قَالُواْ حَرِّقُوهُ وَٱنصُرُوٓاْ ءَالِهَتَكُمۡ إِن كُنتُمۡ فَٰعِلِينَ\nУлар: «Уни куйдиринглар! Агар (бир иш) қиладиган бўлсангиз, худоларингизга ёрдам беринг!» – дедилар'}, {'chapter': 21, 'verse': 69, 'text': 'قُلۡنَا يَٰنَارُ كُونِي بَرۡدٗا وَسَلَٰمًا عَلَىٰٓ إِبۡرَٰهِيمَ\nБиз: «Эй олов, сен Иброҳимга салқин ва саломатлик бўл!» – дедик'}, {'chapter': 21, 'verse': 70, 'text': 'وَأَرَادُواْ بِهِۦ كَيۡدٗا فَجَعَلۡنَٰهُمُ ٱلۡأَخۡسَرِينَ\nУлар у(Иброҳим)га ёмонлик ирода қилган эдилар. Биз уларни энг зиёнкорлар қилиб қўйдик'}, {'chapter': 21, 'verse': 71, 'text': 'وَنَجَّيۡنَٰهُ وَلُوطًا إِلَى ٱلۡأَرۡضِ ٱلَّتِي بَٰرَكۡنَا فِيهَا لِلۡعَٰلَمِينَ\nУнга ва Лутга нажот бериб, оламлар учун баракали қилган ерга юбордик'}, {'chapter': 21, 'verse': 72, 'text': 'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ نَافِلَةٗۖ وَكُلّٗا جَعَلۡنَا صَٰلِحِينَ\nУнга Исҳоқни ҳадя этдик ва Яъқубни зиёда қилиб бердик ва ҳаммаларини аҳли солиҳлар қилдик'}, {'chapter': 21, 'verse': 73, 'text': 'وَجَعَلۡنَٰهُمۡ أَئِمَّةٗ يَهۡدُونَ بِأَمۡرِنَا وَأَوۡحَيۡنَآ إِلَيۡهِمۡ فِعۡلَ ٱلۡخَيۡرَٰتِ وَإِقَامَ ٱلصَّلَوٰةِ وَإِيتَآءَ ٱلزَّكَوٰةِۖ وَكَانُواْ لَنَا عَٰبِدِينَ\nВа уларни амримиз ила ҳидоятга бошловчилар қилдик. Уларга яхшиликлар қилишни, намозни тугал адо этишни ва закот беришни ваҳий қилдик. Улар Бизга ибодат қилгувчилар бўлдилар'}, {'chapter': 21, 'verse': 74, 'text': 'وَلُوطًا ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗا وَنَجَّيۡنَٰهُ مِنَ ٱلۡقَرۡيَةِ ٱلَّتِي كَانَت تَّعۡمَلُ ٱلۡخَبَـٰٓئِثَۚ إِنَّهُمۡ كَانُواْ قَوۡمَ سَوۡءٖ فَٰسِقِينَ\nВа Лутга ҳукм ва илм бердик ва унга ифлосликлар қилаётган қишлоқдан нажот бердик. Албатта, улар ёмон, бузғунчи қавм эдилар'}, {'chapter': 21, 'verse': 75, 'text': 'وَأَدۡخَلۡنَٰهُ فِي رَحۡمَتِنَآۖ إِنَّهُۥ مِنَ ٱلصَّـٰلِحِينَ\nВа Биз уни Ўз раҳматимизга киритдик. Албатта, у солиҳлардандир'}, {'chapter': 21, 'verse': 76, 'text': 'وَنُوحًا إِذۡ نَادَىٰ مِن قَبۡلُ فَٱسۡتَجَبۡنَا لَهُۥ فَنَجَّيۡنَٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ\nВа Нуҳни (эсла). Бундан олдин у нидо этганида, Биз унинг нидосига жавоб қилдик ва унга ҳамда унинг аҳлига улуғ ғамдан нажот бердик'}, {'chapter': 21, 'verse': 77, 'text': 'وَنَصَرۡنَٰهُ مِنَ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمَ سَوۡءٖ فَأَغۡرَقۡنَٰهُمۡ أَجۡمَعِينَ\nУнга оятларимизни ёлғонга чиқарган қавмга қарши нусрат бердик. Албатта, улар ёмон қавм эдилар. Бас, Биз уларнинг барчасини ғарқ қилдик'}, {'chapter': 21, 'verse': 78, 'text': 'وَدَاوُۥدَ وَسُلَيۡمَٰنَ إِذۡ يَحۡكُمَانِ فِي ٱلۡحَرۡثِ إِذۡ نَفَشَتۡ فِيهِ غَنَمُ ٱلۡقَوۡمِ وَكُنَّا لِحُكۡمِهِمۡ شَٰهِدِينَ\nВа Довуд билан Сулаймоннинг экинзор ҳақида ҳукм қилаётганларини (эсла). Ўшанда у(экинзор)га қавмнинг қўйи кечаси тарқалиб кетган эди. Биз уларнинг ҳукмига шоҳид бўлган эдик'}, {'chapter': 21, 'verse': 79, 'text': 'فَفَهَّمۡنَٰهَا سُلَيۡمَٰنَۚ وَكُلًّا ءَاتَيۡنَا حُكۡمٗا وَعِلۡمٗاۚ وَسَخَّرۡنَا مَعَ دَاوُۥدَ ٱلۡجِبَالَ يُسَبِّحۡنَ وَٱلطَّيۡرَۚ وَكُنَّا فَٰعِلِينَ\nБас, Биз уни Сулаймонга фаҳмлатдик. Уларнинг ҳар бирига ҳукмни ва илмни берган эдик. Довуд билан бирга тасбиҳ айтсин деб, тоғларни ва қушларни бўйинсундириб қўйдик. Шуларни қилгувчи бўлган Бизмиз'}, {'chapter': 21, 'verse': 80, 'text': 'وَعَلَّمۡنَٰهُ صَنۡعَةَ لَبُوسٖ لَّكُمۡ لِتُحۡصِنَكُم مِّنۢ بَأۡسِكُمۡۖ فَهَلۡ أَنتُمۡ شَٰكِرُونَ\nБиз унга сизлар учун сизни ёмонликдан сақлайдиган кийим қилиш санъатини ўргатдик. Энди шукр қиларсизлар'}, {'chapter': 21, 'verse': 81, 'text': 'وَلِسُلَيۡمَٰنَ ٱلرِّيحَ عَاصِفَةٗ تَجۡرِي بِأَمۡرِهِۦٓ إِلَى ٱلۡأَرۡضِ ٱلَّتِي بَٰرَكۡنَا فِيهَاۚ وَكُنَّا بِكُلِّ شَيۡءٍ عَٰلِمِينَ\nСулаймонга шамолни қаттиқ эсувчи қилиб бердик. У (шамол) ни унинг амри ила, Биз баракали қилган ерга эсадиган қилиб қўйдик. Барча нарсани билгувчи бўлдик'}, {'chapter': 21, 'verse': 82, 'text': 'وَمِنَ ٱلشَّيَٰطِينِ مَن يَغُوصُونَ لَهُۥ وَيَعۡمَلُونَ عَمَلٗا دُونَ ذَٰلِكَۖ وَكُنَّا لَهُمۡ حَٰفِظِينَ\nВа шайтонлардан унга ғаввослик қиладиганларни ва бундан бошқа амалларни бажарадиганларни бўйсундириб қўйдик. Биз уларни муҳофаза қилгувчи бўлдик'}, {'chapter': 21, 'verse': 83, 'text': '۞وَأَيُّوبَ إِذۡ نَادَىٰ رَبَّهُۥٓ أَنِّي مَسَّنِيَ ٱلضُّرُّ وَأَنتَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\nВа Айюбнинг Ўз Роббига нидо қилиб: «Албатта, мени зарар тутди. Сенинг Ўзинг раҳимлиларнинг раҳимлигисан!» деганини эсла'}, {'chapter': 21, 'verse': 84, 'text': 'فَٱسۡتَجَبۡنَا لَهُۥ فَكَشَفۡنَا مَا بِهِۦ مِن ضُرّٖۖ وَءَاتَيۡنَٰهُ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةٗ مِّنۡ عِندِنَا وَذِكۡرَىٰ لِلۡعَٰبِدِينَ\nБас, Биз уни (дуосини) истижобат қилдик. Унга етган зарарни кетказдик. Унга аҳлини, улар билан бирга яна шунчани ҳам бердик. Буни Ўз раҳматимиз ила ва «Обидларга эслатма бўлсин», деб қилдик'}, {'chapter': 21, 'verse': 85, 'text': 'وَإِسۡمَٰعِيلَ وَإِدۡرِيسَ وَذَا ٱلۡكِفۡلِۖ كُلّٞ مِّنَ ٱلصَّـٰبِرِينَ\nВа Исмоил, Идрис ва Зул-кифлни эсла. Уларнинг ҳар бири сабр қилгувчилардандир'}, {'chapter': 21, 'verse': 86, 'text': 'وَأَدۡخَلۡنَٰهُمۡ فِي رَحۡمَتِنَآۖ إِنَّهُم مِّنَ ٱلصَّـٰلِحِينَ\nВа уларни Ўз раҳматимизга киритдик. Албатта, улар солиҳлардандир'}, {'chapter': 21, 'verse': 87, 'text': 'وَذَا ٱلنُّونِ إِذ ذَّهَبَ مُغَٰضِبٗا فَظَنَّ أَن لَّن نَّقۡدِرَ عَلَيۡهِ فَنَادَىٰ فِي ٱلظُّلُمَٰتِ أَن لَّآ إِلَٰهَ إِلَّآ أَنتَ سُبۡحَٰنَكَ إِنِّي كُنتُ مِنَ ٱلظَّـٰلِمِينَ\nЗуннунни эсла. Ўшанда у ғазабланган ҳолда чиқиб кетган эди. Бас, у Бизни ўзига (ер юзини) тор этмас, деб гумон қилди. Зулматларда туриб: «Сендан ўзга илоҳ йўқ, Сен поксан, албатта, мен золимлардан бўлдим», деб нидо қилди'}, {'chapter': 21, 'verse': 88, 'text': 'فَٱسۡتَجَبۡنَا لَهُۥ وَنَجَّيۡنَٰهُ مِنَ ٱلۡغَمِّۚ وَكَذَٰلِكَ نُـۨجِي ٱلۡمُؤۡمِنِينَ\nБас, Биз уни (дуосини) ижобат қилдик. Унга ғамдан нажот бердик. Мўминларга шундай нажот берурмиз'}, {'chapter': 21, 'verse': 89, 'text': 'وَزَكَرِيَّآ إِذۡ نَادَىٰ رَبَّهُۥ رَبِّ لَا تَذَرۡنِي فَرۡدٗا وَأَنتَ خَيۡرُ ٱلۡوَٰرِثِينَ\nВа Закариёни эсла. Ўшанда у Роббига нидо қилиб: «Эй Роббим, мени ёлғиз ташлаб қўйма, Сен Ўзинг ворисларнинг энг яхшисисан», деган эди'}, {'chapter': 21, 'verse': 90, 'text': 'فَٱسۡتَجَبۡنَا لَهُۥ وَوَهَبۡنَا لَهُۥ يَحۡيَىٰ وَأَصۡلَحۡنَا لَهُۥ زَوۡجَهُۥٓۚ إِنَّهُمۡ كَانُواْ يُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِ وَيَدۡعُونَنَا رَغَبٗا وَرَهَبٗاۖ وَكَانُواْ لَنَا خَٰشِعِينَ\nБас, Биз уни (дуосини) ижобат этдик ва унга Яҳёни ҳадя қилдик ҳамда жуфтини ўнглаб қўйдик. Албатта, улар яхшиликларга шошилишар эди ва Бизга рағбат ила ва қўрқиб дуо қилишар эди. Улар Бизга таъзим ила бўйинсунувчи эдилар'}, {'chapter': 21, 'verse': 91, 'text': 'وَٱلَّتِيٓ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيهَا مِن رُّوحِنَا وَجَعَلۡنَٰهَا وَٱبۡنَهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ\nФаржини пок сақлаган аёлни эсла. Биз унга Ўз руҳимиздан пуфладик. Уни ва унинг ўғлини оламларга оят-белги қилдик'}, {'chapter': 21, 'verse': 92, 'text': 'إِنَّ هَٰذِهِۦٓ أُمَّتُكُمۡ أُمَّةٗ وَٰحِدَةٗ وَأَنَا۠ رَبُّكُمۡ فَٱعۡبُدُونِ\nАлбатта, сизнинг ушбу умматингиз бир умматдир ва Мен сизнинг Роббингизман. Бас, Менгагина ибодат қилинг'}, {'chapter': 21, 'verse': 93, 'text': 'وَتَقَطَّعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡۖ كُلٌّ إِلَيۡنَا رَٰجِعُونَ\nУлар эса, ишларини парчаладилар. Барчалари Бизга қайтувчидирлар'}, {'chapter': 21, 'verse': 94, 'text': 'فَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ وَهُوَ مُؤۡمِنٞ فَلَا كُفۡرَانَ لِسَعۡيِهِۦ وَإِنَّا لَهُۥ كَٰتِبُونَ\nКим мўмин ҳолида солиҳ амаллардан қилса, саъйи-ҳаракати зое кетмас. Албатта, Биз уни ёзиб қўйгувчидирмиз'}, {'chapter': 21, 'verse': 95, 'text': 'وَحَرَٰمٌ عَلَىٰ قَرۡيَةٍ أَهۡلَكۡنَٰهَآ أَنَّهُمۡ لَا يَرۡجِعُونَ\nБиз ҳалок қилган қишлоқ-шаҳарларнинг қайтмаслиги ҳаромдир'}, {'chapter': 21, 'verse': 96, 'text': 'حَتَّىٰٓ إِذَا فُتِحَتۡ يَأۡجُوجُ وَمَأۡجُوجُ وَهُم مِّن كُلِّ حَدَبٖ يَنسِلُونَ\nНиҳоят, Яъжуж ва Маъжуж очилиб, улар ҳар бир тепаликлардан оқиб келганларида'}, {'chapter': 21, 'verse': 97, 'text': 'وَٱقۡتَرَبَ ٱلۡوَعۡدُ ٱلۡحَقُّ فَإِذَا هِيَ شَٰخِصَةٌ أَبۡصَٰرُ ٱلَّذِينَ كَفَرُواْ يَٰوَيۡلَنَا قَدۡ كُنَّا فِي غَفۡلَةٖ مِّنۡ هَٰذَا بَلۡ كُنَّا ظَٰلِمِينَ\nВа ҳақ ваъда яқинлашгандаги ҳолни кўрсанг, куфр келтирганларнинг кўзлари чақчайиб: «Ҳолимизга вой! Бизлар бундан ғафлатда эдик. Йўқ! Бизлар зулм қилгувчилар эдик», дерлар'}, {'chapter': 21, 'verse': 98, 'text': 'إِنَّكُمۡ وَمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ حَصَبُ جَهَنَّمَ أَنتُمۡ لَهَا وَٰرِدُونَ\nАлбатта, сизлар ва Аллоҳдан ўзга ибодат қилган нарсаларингиз жаҳаннам ёқилғисидирсиз. Сизлар унга киргувчилардирсиз'}, {'chapter': 21, 'verse': 99, 'text': 'لَوۡ كَانَ هَـٰٓؤُلَآءِ ءَالِهَةٗ مَّا وَرَدُوهَاۖ وَكُلّٞ فِيهَا خَٰلِدُونَ\nАгар анавилар худолар бўлганида унга кирмас эдилар. Ҳаммалари унда абадий қолурлар'}, {'chapter': 21, 'verse': 100, 'text': 'لَهُمۡ فِيهَا زَفِيرٞ وَهُمۡ فِيهَا لَا يَسۡمَعُونَ\nУ ерда уларга инграш бордир. Улар у ерда эшитмаслар'}, {'chapter': 21, 'verse': 101, 'text': 'إِنَّ ٱلَّذِينَ سَبَقَتۡ لَهُم مِّنَّا ٱلۡحُسۡنَىٰٓ أُوْلَـٰٓئِكَ عَنۡهَا مُبۡعَدُونَ\nАлбатта, Биздан ўзлари учун яхшилик қилинганлар, ана ўшалар ундан(жаҳаннамдан) узоқлаштирилгандирлар'}, {'chapter': 21, 'verse': 102, 'text': 'لَا يَسۡمَعُونَ حَسِيسَهَاۖ وَهُمۡ فِي مَا ٱشۡتَهَتۡ أَنفُسُهُمۡ خَٰلِدُونَ\nУлар унинг чисирлашини(овозини ҳам) эшитмаслар. Улар ўз нафслари иштаҳа қилган нарсалари ичида абадий қолгувчилардир'}, {'chapter': 21, 'verse': 103, 'text': 'لَا يَحۡزُنُهُمُ ٱلۡفَزَعُ ٱلۡأَكۡبَرُ وَتَتَلَقَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ هَٰذَا يَوۡمُكُمُ ٱلَّذِي كُنتُمۡ تُوعَدُونَ\nУларни буюк даҳшат ғами босмас. Уларни фаришталар: «Бу сизларга ваъда қилинган кунингиздир», деб кутиб олурлар'}, {'chapter': 21, 'verse': 104, 'text': 'يَوۡمَ نَطۡوِي ٱلسَّمَآءَ كَطَيِّ ٱلسِّجِلِّ لِلۡكُتُبِۚ كَمَا بَدَأۡنَآ أَوَّلَ خَلۡقٖ نُّعِيدُهُۥۚ وَعۡدًا عَلَيۡنَآۚ إِنَّا كُنَّا فَٰعِلِينَ\nЎша кунда осмонни худди мактуб ёзилган дафтарни ёпгандек ёпамиз. Аввал қандай яратган бўлсак, шундай ҳолга қайтарамиз. Бу зиммамиздаги ваъдадир. Албатта, Биз буни қилгувчимиз'}, {'chapter': 21, 'verse': 105, 'text': 'وَلَقَدۡ كَتَبۡنَا فِي ٱلزَّبُورِ مِنۢ بَعۡدِ ٱلذِّكۡرِ أَنَّ ٱلۡأَرۡضَ يَرِثُهَا عِبَادِيَ ٱلصَّـٰلِحُونَ\nБатаҳқиқ, Биз Зикрдан сўнг Забурда ҳам: «Албатта, ерга солиҳ бандаларим меросхўр бўлурлар», деб ёзган эдик'}, {'chapter': 21, 'verse': 106, 'text': 'إِنَّ فِي هَٰذَا لَبَلَٰغٗا لِّقَوۡمٍ عَٰبِدِينَ\nАлбатта, бу(Қуръон)да ибодат қилгувчи қавм учун етарли ўгит бордир'}, {'chapter': 21, 'verse': 107, 'text': 'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا رَحۡمَةٗ لِّلۡعَٰلَمِينَ\nБиз сени оламларга фақат раҳмат қилиб юбордик'}, {'chapter': 21, 'verse': 108, 'text': 'قُلۡ إِنَّمَا يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ\nСен: «Менга фақат, сизнинг илоҳингиз албатта битта илоҳдир, деб ваҳий этилур. Бас, сизлар мусулмон бўласизларми?!» – деб айт'}, {'chapter': 21, 'verse': 109, 'text': 'فَإِن تَوَلَّوۡاْ فَقُلۡ ءَاذَنتُكُمۡ عَلَىٰ سَوَآءٖۖ وَإِنۡ أَدۡرِيٓ أَقَرِيبٌ أَم بَعِيدٞ مَّا تُوعَدُونَ\nАгар улар юз ўгириб кетсалар: «Мен сизларга баробарлик бўлиши учун эълон қилдим. Сизга ваъда қилинаётган нарса яқинми ёки узоқми — билмасман'}, {'chapter': 21, 'verse': 110, 'text': 'إِنَّهُۥ يَعۡلَمُ ٱلۡجَهۡرَ مِنَ ٱلۡقَوۡلِ وَيَعۡلَمُ مَا تَكۡتُمُونَ\nАлбатта, У сўзнинг ошкорасини ҳам биладир ва нимани беркитаётган бўлсангиз ҳам биладир'}, {'chapter': 21, 'verse': 111, 'text': 'وَإِنۡ أَدۡرِي لَعَلَّهُۥ فِتۡنَةٞ لَّكُمۡ وَمَتَٰعٌ إِلَىٰ حِينٖ\nБилмайман, эҳтимол, бу сизлар учун синовдир ёки вақтинчалик ҳаловатдир», деб айт'}, {'chapter': 21, 'verse': 112, 'text': 'قَٰلَ رَبِّ ٱحۡكُم بِٱلۡحَقِّۗ وَرَبُّنَا ٱلرَّحۡمَٰنُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ\nУ: «Эй Роббим, Ўзинг ҳақ ила ҳукм қил. Роббимиз Роҳмандир ва сизлар қилган васфлардан мадад сўраладиган Зотдир», деди'}], '22': [{'chapter': 22, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمۡۚ إِنَّ زَلۡزَلَةَ ٱلسَّاعَةِ شَيۡءٌ عَظِيمٞ\nЭй одамлар! Роббингиздан қўрқинг! Албатта, соат (қиёмат) зилзиласи улкан нарсадир'}, {'chapter': 22, 'verse': 2, 'text': 'يَوۡمَ تَرَوۡنَهَا تَذۡهَلُ كُلُّ مُرۡضِعَةٍ عَمَّآ أَرۡضَعَتۡ وَتَضَعُ كُلُّ ذَاتِ حَمۡلٍ حَمۡلَهَا وَتَرَى ٱلنَّاسَ سُكَٰرَىٰ وَمَا هُم بِسُكَٰرَىٰ وَلَٰكِنَّ عَذَابَ ٱللَّهِ شَدِيدٞ\nУни кўрадиган кунингизда, ҳар бир эмизувчи ўзи эмизаётган нарсасини унутар. Ҳар бир ҳомиладор ҳомиласини ташлар. Одамларни маст ҳолда кўрарсан. Ҳолбуки, улар маст эмас. Лекин Аллоҳнинг азоби шиддатлидир'}, {'chapter': 22, 'verse': 3, 'text': 'وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّبِعُ كُلَّ شَيۡطَٰنٖ مَّرِيدٖ\nОдамлардан Аллоҳ ҳақида билмасдан тортишганлари ва бебош шайтонга эргашганлари ҳам бор'}, {'chapter': 22, 'verse': 4, 'text': 'كُتِبَ عَلَيۡهِ أَنَّهُۥ مَن تَوَلَّاهُ فَأَنَّهُۥ يُضِلُّهُۥ وَيَهۡدِيهِ إِلَىٰ عَذَابِ ٱلسَّعِيرِ\nУнга битилганки, албатта, унга ким дўст бўлса, шубҳасиз, уни адаштирур ва дўзах азобига «ҳидоят» қилур'}, {'chapter': 22, 'verse': 5, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِي رَيۡبٖ مِّنَ ٱلۡبَعۡثِ فَإِنَّا خَلَقۡنَٰكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ مِنۡ عَلَقَةٖ ثُمَّ مِن مُّضۡغَةٖ مُّخَلَّقَةٖ وَغَيۡرِ مُخَلَّقَةٖ لِّنُبَيِّنَ لَكُمۡۚ وَنُقِرُّ فِي ٱلۡأَرۡحَامِ مَا نَشَآءُ إِلَىٰٓ أَجَلٖ مُّسَمّٗى ثُمَّ نُخۡرِجُكُمۡ طِفۡلٗا ثُمَّ لِتَبۡلُغُوٓاْ أَشُدَّكُمۡۖ وَمِنكُم مَّن يُتَوَفَّىٰ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرۡذَلِ ٱلۡعُمُرِ لِكَيۡلَا يَعۡلَمَ مِنۢ بَعۡدِ عِلۡمٖ شَيۡـٔٗاۚ وَتَرَى ٱلۡأَرۡضَ هَامِدَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡ وَأَنۢبَتَتۡ مِن كُلِّ زَوۡجِۭ بَهِيجٖ\nЭй, одамлар! Агар қайта тирилиш ҳақида шубҳа қиладиган бўлсангиз, бас, Биз сизга баён қилишимиз учун сизларни тупроқдан, сўнгра нутфадан, сўнгра алақадан, сўнгра битган-битмаган бир чайнам гўштдан яратдик. Ва бачадонларда хоҳлаган нарсамизни маълум муддатгача қарор топтирармиз. Кейин сизларни бувак ҳолингизда чиқарармиз. Сўнгра, вояга етишингиз учун (тарбия қилармиз). Сиздан вафот қилиб кетадиганлар ҳам, билганидан кейин ҳеч нарсани билмай қоладиган даражада ночор умрга етадиганлар ҳам бор. Ва ерни қақраган ҳолда кўрарсан. Қачонки, Биз унга сув туширсак, у сесканадир ва кўпчийдир. Ҳамда ҳар хил гўзал жуфтларни ўстирадир'}, {'chapter': 22, 'verse': 6, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّهُۥ يُحۡيِ ٱلۡمَوۡتَىٰ وَأَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nБу(ҳолат)лар кўрсатадики, албатта, Аллоҳ ҳақ Зотдир ва, албатта, У ўликларни тирилтиради ҳамда У ҳар бир нарсага қодирдир'}, {'chapter': 22, 'verse': 7, 'text': 'وَأَنَّ ٱلسَّاعَةَ ءَاتِيَةٞ لَّا رَيۡبَ فِيهَا وَأَنَّ ٱللَّهَ يَبۡعَثُ مَن فِي ٱلۡقُبُورِ\nВа, албатта, соат (қиёмат) келгувчидир, бунга шубҳа йўқ. Ва, албатта, Аллоҳ қабрлардаги кимсаларни тирилтирур'}, {'chapter': 22, 'verse': 8, 'text': 'وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ\nОдамлардан Аллоҳ ҳақида илмсиз (ҳолда), ҳидоятга ёки нурли китобга эга бўлмасдан туриб тортишадиганлари бор'}, {'chapter': 22, 'verse': 9, 'text': 'ثَانِيَ عِطۡفِهِۦ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِۖ لَهُۥ فِي ٱلدُّنۡيَا خِزۡيٞۖ وَنُذِيقُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ عَذَابَ ٱلۡحَرِيقِ\nАллоҳнинг йўлидан адаштириш учун, бўйнини булқаб (тортишар). Унга дунёда расволик бор. Қиёмат куни унга куйдириш азобини тоттирамиз'}, {'chapter': 22, 'verse': 10, 'text': 'ذَٰلِكَ بِمَا قَدَّمَتۡ يَدَاكَ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nБундай бўлиши ўз қўлларинг тақдим қилган нарсалардандир. Албатта, Аллоҳ бандаларга зулм қилгувчи эмасдир'}, {'chapter': 22, 'verse': 11, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَعۡبُدُ ٱللَّهَ عَلَىٰ حَرۡفٖۖ فَإِنۡ أَصَابَهُۥ خَيۡرٌ ٱطۡمَأَنَّ بِهِۦۖ وَإِنۡ أَصَابَتۡهُ فِتۡنَةٌ ٱنقَلَبَ عَلَىٰ وَجۡهِهِۦ خَسِرَ ٱلدُّنۡيَا وَٱلۡأٓخِرَةَۚ ذَٰلِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِينُ\nОдамлардан Аллоҳга бир томонлама ибодат қиладиганлари ҳам бор. Агар унга яхшилик етса, кўнгли тўлур. Агар унга бало етса, юз ўгириб кетур. У, дунёда ҳам, охиратда ҳам зиён кўрур. Ана ўша очиқ-ойдин зиёндир'}, {'chapter': 22, 'verse': 12, 'text': 'يَدۡعُواْ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُۥ وَمَا لَا يَنفَعُهُۥۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلۡبَعِيدُ\nУ, Аллоҳни қўйиб, ўзига зарар ҳам бермайдиган, манфаат ҳам бермайдиган нарсага илтижо қиладир. Шунинг ўзи қаттиқ адашишдир'}, {'chapter': 22, 'verse': 13, 'text': 'يَدۡعُواْ لَمَن ضَرُّهُۥٓ أَقۡرَبُ مِن نَّفۡعِهِۦۚ لَبِئۡسَ ٱلۡمَوۡلَىٰ وَلَبِئۡسَ ٱلۡعَشِيرُ\nУ, манфаатидан кўра зарари яқинроқ бўлганга илтижо қиладир. У қандай ҳам ёмон хожа ва қандай ҳам ёмон дўстдир'}, {'chapter': 22, 'verse': 14, 'text': 'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ إِنَّ ٱللَّهَ يَفۡعَلُ مَا يُرِيدُ\nАлбатта, иймон келтириб, яхши амалларни қилганларни Аллоҳ остиларидан анҳорлар оқиб турган жаннатларга киритадир. Албатта, Аллоҳ Ўзи ирода этган нарсани қиладир'}, {'chapter': 22, 'verse': 15, 'text': 'مَن كَانَ يَظُنُّ أَن لَّن يَنصُرَهُ ٱللَّهُ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ فَلۡيَمۡدُدۡ بِسَبَبٍ إِلَى ٱلسَّمَآءِ ثُمَّ لۡيَقۡطَعۡ فَلۡيَنظُرۡ هَلۡ يُذۡهِبَنَّ كَيۡدُهُۥ مَا يَغِيظُ\nКимки унга бу дунёю охиратда Аллоҳ нусрат беришига шубҳа қилса, бир восита топиб, осмонга осилсин, сўнгра уни кессин. Бас, назар солсин-чи, унинг бу ҳийласи ғам-ғуссани кеткизармикан'}, {'chapter': 22, 'verse': 16, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَٰهُ ءَايَٰتِۭ بَيِّنَٰتٖ وَأَنَّ ٱللَّهَ يَهۡدِي مَن يُرِيدُ\nМана шундай қилиб, Биз у(Қуръон)ни очиқ-ойдин оятлар ҳолида нозил қилдик. Албатта, Аллоҳ хоҳлаган кишиларни ҳидоят қиладир'}, {'chapter': 22, 'verse': 17, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلصَّـٰبِـِٔينَ وَٱلنَّصَٰرَىٰ وَٱلۡمَجُوسَ وَٱلَّذِينَ أَشۡرَكُوٓاْ إِنَّ ٱللَّهَ يَفۡصِلُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nАлбатта, Аллоҳ қиёмат куни иймон келтирганлар, яҳудий бўлганлар, собиийлар, насоролар, мажусийлар ва ширк келтирганлар орасида Ўзи ажрим қилур. Албатта, Аллоҳ ҳар бир нарсага шоҳиддир'}, {'chapter': 22, 'verse': 18, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يَسۡجُدُۤ لَهُۥۤ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ وَٱلشَّمۡسُ وَٱلۡقَمَرُ وَٱلنُّجُومُ وَٱلۡجِبَالُ وَٱلشَّجَرُ وَٱلدَّوَآبُّ وَكَثِيرٞ مِّنَ ٱلنَّاسِۖ وَكَثِيرٌ حَقَّ عَلَيۡهِ ٱلۡعَذَابُۗ وَمَن يُهِنِ ٱللَّهُ فَمَا لَهُۥ مِن مُّكۡرِمٍۚ إِنَّ ٱللَّهَ يَفۡعَلُ مَا يَشَآءُ۩\nОсмонлардаги кимсалар, ердаги кимсалар, қуёш, ой, юлдузлар, тоғлар, дарахтлар, жониворлар ва одамлардан кўплари, албатта, Аллоҳга сажда қилишини кўрмайсанми? Кўпларига эса, азоб ҳақ бўлди. Кимни Аллоҳ хор қилса, уни икром қилгувчи бўлмас. Албатта, Аллоҳ хоҳлаган нарсасини қиладир'}, {'chapter': 22, 'verse': 19, 'text': '۞هَٰذَانِ خَصۡمَانِ ٱخۡتَصَمُواْ فِي رَبِّهِمۡۖ فَٱلَّذِينَ كَفَرُواْ قُطِّعَتۡ لَهُمۡ ثِيَابٞ مِّن نَّارٖ يُصَبُّ مِن فَوۡقِ رُءُوسِهِمُ ٱلۡحَمِيمُ\nМана бу икки ғаним Роббилари ҳақида талашган эдилар. Бас, куфр келтирганларга оловдан кийим бичилди. Бошлари устидан ўта қайноқ сув қуйилар'}, {'chapter': 22, 'verse': 20, 'text': 'يُصۡهَرُ بِهِۦ مَا فِي بُطُونِهِمۡ وَٱلۡجُلُودُ\nУ билан қоринларидаги нарсалар ва терилари эритилур'}, {'chapter': 22, 'verse': 21, 'text': 'وَلَهُم مَّقَٰمِعُ مِنۡ حَدِيدٖ\nУлар учун темир гурзилар бор'}, {'chapter': 22, 'verse': 22, 'text': 'كُلَّمَآ أَرَادُوٓاْ أَن يَخۡرُجُواْ مِنۡهَا مِنۡ غَمٍّ أُعِيدُواْ فِيهَا وَذُوقُواْ عَذَابَ ٱلۡحَرِيقِ\nУлар ҳар қачон ундаги ғамдан чиқмоқчи бўлсалар, унга қайтарилурлар ва «Ёниш азобини тотинг!» дейилур'}, {'chapter': 22, 'verse': 23, 'text': 'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَلُؤۡلُؤٗاۖ وَلِبَاسُهُمۡ فِيهَا حَرِيرٞ\nАлбатта, иймон келтириб, яхши амалларни қилганларни Аллоҳ остидан анҳорлар оқиб турган жаннатларга киритур. Улар у ерда олтин билакузуклар ва инжулар ила безанурлар. У ердаги либослари эса, ипакдандир'}, {'chapter': 22, 'verse': 24, 'text': 'وَهُدُوٓاْ إِلَى ٱلطَّيِّبِ مِنَ ٱلۡقَوۡلِ وَهُدُوٓاْ إِلَىٰ صِرَٰطِ ٱلۡحَمِيدِ\nУлар ширин сўзга ҳидоят қилинурлар ва мақталган Зотнинг йўлига ҳидоят қилинурлар'}, {'chapter': 22, 'verse': 25, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ ٱلَّذِي جَعَلۡنَٰهُ لِلنَّاسِ سَوَآءً ٱلۡعَٰكِفُ فِيهِ وَٱلۡبَادِۚ وَمَن يُرِدۡ فِيهِ بِإِلۡحَادِۭ بِظُلۡمٖ نُّذِقۡهُ مِنۡ عَذَابٍ أَلِيمٖ\nКуфр келтирганлар ва Аллоҳнинг йўлидан ҳамда ерли ва четдан келган одамлар учун баробар қилиб қўйганимиз Масжидул Ҳаромдан тўсганларга ва у ерда ким зулм ила янглиш йўлга бурилишни ирода қилса, унга аламли азобни тоттирурмиз'}, {'chapter': 22, 'verse': 26, 'text': 'وَإِذۡ بَوَّأۡنَا لِإِبۡرَٰهِيمَ مَكَانَ ٱلۡبَيۡتِ أَن لَّا تُشۡرِكۡ بِي شَيۡـٔٗا وَطَهِّرۡ بَيۡتِيَ لِلطَّآئِفِينَ وَٱلۡقَآئِمِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ\nБиз Иброҳимга Байтнинг жойини белгилаб бериб: «Менга ҳеч нарсани шерик қилма, Байтимни тавоф қилгувчилар, (ибодатда) қоим тургувчилар ҳамда рукуъ, сужуд қилгувчилар учун поклагин'}, {'chapter': 22, 'verse': 27, 'text': 'وَأَذِّن فِي ٱلنَّاسِ بِٱلۡحَجِّ يَأۡتُوكَ رِجَالٗا وَعَلَىٰ كُلِّ ضَامِرٖ يَأۡتِينَ مِن كُلِّ فَجٍّ عَمِيقٖ\nОдамларни ҳажга чақир, улар сен томон пиёда, узоқ-узоқ йўллардан юриб, озиган уловлар устида келсинлар'}, {'chapter': 22, 'verse': 28, 'text': 'لِّيَشۡهَدُواْ مَنَٰفِعَ لَهُمۡ وَيَذۡكُرُواْ ٱسۡمَ ٱللَّهِ فِيٓ أَيَّامٖ مَّعۡلُومَٰتٍ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلۡأَنۡعَٰمِۖ فَكُلُواْ مِنۡهَا وَأَطۡعِمُواْ ٱلۡبَآئِسَ ٱلۡفَقِيرَ\nВа ўзларига бўладиган манфаатларга шоҳид бўлсинлар. Маълум кунларда уларга ризқ қилиб берган чорва ҳайвонларини (сўйишда) Аллоҳнинг исмини зикр қилсинлар. Бас, улардан енглар ва бечора ва фақирларга ҳам егизинглар'}, {'chapter': 22, 'verse': 29, 'text': 'ثُمَّ لۡيَقۡضُواْ تَفَثَهُمۡ وَلۡيُوفُواْ نُذُورَهُمۡ وَلۡيَطَّوَّفُواْ بِٱلۡبَيۡتِ ٱلۡعَتِيقِ\nСўнгра ўзларидаги кирларни кетказсинлар, назрларига вафо қилсинлар ва «қадимги уй»ни тавоф қилсинлар», деганимизни эсла'}, {'chapter': 22, 'verse': 30, 'text': 'ذَٰلِكَۖ وَمَن يُعَظِّمۡ حُرُمَٰتِ ٱللَّهِ فَهُوَ خَيۡرٞ لَّهُۥ عِندَ رَبِّهِۦۗ وَأُحِلَّتۡ لَكُمُ ٱلۡأَنۡعَٰمُ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡۖ فَٱجۡتَنِبُواْ ٱلرِّجۡسَ مِنَ ٱلۡأَوۡثَٰنِ وَٱجۡتَنِبُواْ قَوۡلَ ٱلزُّورِ\nАна шундай. Ким Аллоҳ ҳаром қилган нарсаларга риоя қилса, бас, бу Робби ҳузурида унинг ўзи учун яхшидир. Ва сизларга (келажакда) тиловат қилинадиганлардан бошқа чорва ҳайвонлари ҳалол қилинди. Бас, бутлардан иборат нажосатдан йироқ бўлинг ва ёлғон сўздан йироқ бўлинг'}, {'chapter': 22, 'verse': 31, 'text': 'حُنَفَآءَ لِلَّهِ غَيۡرَ مُشۡرِكِينَ بِهِۦۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَكَأَنَّمَا خَرَّ مِنَ ٱلسَّمَآءِ فَتَخۡطَفُهُ ٱلطَّيۡرُ أَوۡ تَهۡوِي بِهِ ٱلرِّيحُ فِي مَكَانٖ سَحِيقٖ\nАллоҳга бўлган иймондан оғманг, унга ширк келтиргувчи бўлманг. Ким Аллоҳга ширк келтирса, бас, у гўёки осмондан қулаб тушган-у, уни йиртқич қуш олиб қочган ёки уни шамол узоқ ерга учириб кетган кабидир'}, {'chapter': 22, 'verse': 32, 'text': 'ذَٰلِكَۖ وَمَن يُعَظِّمۡ شَعَـٰٓئِرَ ٱللَّهِ فَإِنَّهَا مِن تَقۡوَى ٱلۡقُلُوبِ\nИш бундоқ. Ким Аллоҳнинг нишонларини улуғласа, бас, албатта, бу, қалбларнинг тақвосидандир'}, {'chapter': 22, 'verse': 33, 'text': 'لَكُمۡ فِيهَا مَنَٰفِعُ إِلَىٰٓ أَجَلٖ مُّسَمّٗى ثُمَّ مَحِلُّهَآ إِلَى ٱلۡبَيۡتِ ٱلۡعَتِيقِ\nСизга у(нишон)ларда маълум вақтгача манфаатлар бордир. Сўнгра уларни (сўйиш) ҳалол макони «қадимги уй» (атрофи)дадир'}, {'chapter': 22, 'verse': 34, 'text': 'وَلِكُلِّ أُمَّةٖ جَعَلۡنَا مَنسَكٗا لِّيَذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلۡأَنۡعَٰمِۗ فَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَلَهُۥٓ أَسۡلِمُواْۗ وَبَشِّرِ ٱلۡمُخۡبِتِينَ\nҲар бир умматга, уларга чорва ҳайвонларини ризқ қилиб берган Аллоҳнинг исмини эслашлари учун, қурбонлик қилишни жорий этганмиз. Бас, илоҳларингиз бир илоҳдир. Бас, Унгагина мусулмон бўлинг. Итоатли, мутавозеъларга башорат бер'}, {'chapter': 22, 'verse': 35, 'text': 'ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَٱلصَّـٰبِرِينَ عَلَىٰ مَآ أَصَابَهُمۡ وَٱلۡمُقِيمِي ٱلصَّلَوٰةِ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар Аллоҳ зикр қилинган вақтда қалблари титрайдиган ва ўзларига етган мусийбатга сабр қилгувчилар ҳамда намозни тўкис адо этгувчилардир, уларга ризқ қилиб берган нарсамиздан инфоқ қиладилар'}, {'chapter': 22, 'verse': 36, 'text': 'وَٱلۡبُدۡنَ جَعَلۡنَٰهَا لَكُم مِّن شَعَـٰٓئِرِ ٱللَّهِ لَكُمۡ فِيهَا خَيۡرٞۖ فَٱذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَيۡهَا صَوَآفَّۖ فَإِذَا وَجَبَتۡ جُنُوبُهَا فَكُلُواْ مِنۡهَا وَأَطۡعِمُواْ ٱلۡقَانِعَ وَٱلۡمُعۡتَرَّۚ كَذَٰلِكَ سَخَّرۡنَٰهَا لَكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\nВа туяларни сиз учун Аллоҳнинг нишонларидан қилдик. Уларда сизга яхшилик бор. Уларга олд оёқларидан бири боғлиқ турган ҳолида Аллоҳнинг номини зикр қилинг. Ёнлари ерга текканида эса, бас, улардан енг ва қаноатли ва тиланган камбағалларни ҳам таомлантиринг. Шундай қилиб, Биз уларни сизга бўйинсундириб қўйдик. Шоядки, шукр қилсангиз'}, {'chapter': 22, 'verse': 37, 'text': 'لَن يَنَالَ ٱللَّهَ لُحُومُهَا وَلَا دِمَآؤُهَا وَلَٰكِن يَنَالُهُ ٱلتَّقۡوَىٰ مِنكُمۡۚ كَذَٰلِكَ سَخَّرَهَا لَكُمۡ لِتُكَبِّرُواْ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡۗ وَبَشِّرِ ٱلۡمُحۡسِنِينَ\nУларнинг гўштлари ҳам, қонлари ҳам зинҳор Аллоҳга етмас. Лекин Унга сиздан тақво етадир. Шундай қилиб, сизни ҳидоят қилгани эвазига Аллоҳга такбир айтишингиз учун уларни сизга бўйинсундириб қўйди. Яхшилик қилгувчиларга башорат бер'}, {'chapter': 22, 'verse': 38, 'text': '۞إِنَّ ٱللَّهَ يُدَٰفِعُ عَنِ ٱلَّذِينَ ءَامَنُوٓاْۗ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ خَوَّانٖ كَفُورٍ\nАлбатта, Аллоҳ иймон келтирганларни мудофаа қилур. Албатта, Аллоҳ барча хоин ва кофирларни хуш кўрмас'}, {'chapter': 22, 'verse': 39, 'text': 'أُذِنَ لِلَّذِينَ يُقَٰتَلُونَ بِأَنَّهُمۡ ظُلِمُواْۚ وَإِنَّ ٱللَّهَ عَلَىٰ نَصۡرِهِمۡ لَقَدِيرٌ\nЎзларига қарши уруш очганларга, уларга зулм қилингани учун, (урушга) изн берилди. Албатта, Аллоҳ уларга нусрат беришга қодирдир'}, {'chapter': 22, 'verse': 40, 'text': 'ٱلَّذِينَ أُخۡرِجُواْ مِن دِيَٰرِهِم بِغَيۡرِ حَقٍّ إِلَّآ أَن يَقُولُواْ رَبُّنَا ٱللَّهُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّهُدِّمَتۡ صَوَٰمِعُ وَبِيَعٞ وَصَلَوَٰتٞ وَمَسَٰجِدُ يُذۡكَرُ فِيهَا ٱسۡمُ ٱللَّهِ كَثِيرٗاۗ وَلَيَنصُرَنَّ ٱللَّهُ مَن يَنصُرُهُۥٓۚ إِنَّ ٱللَّهَ لَقَوِيٌّ عَزِيزٌ\nУлар ноҳақдан, фақатгина «Роббимиз Аллоҳ» деганлари учун ўз диёрларидан чиқарилгандирлар. Агар Аллоҳ одамларнинг баъзиларини баъзилари билан дафъ қилиб турмаса, узлатгоҳлар, канисалар, ҳавралар ва Аллоҳнинг номи кўплаб зикр қилинадиган масжидлар вайрон қилинган бўлур эди. Албатта, Аллоҳ Ўзига ёрдам берганларга ёрдам берур. Албатта, Аллоҳ кучли ва ғолибдир'}, {'chapter': 22, 'verse': 41, 'text': 'ٱلَّذِينَ إِن مَّكَّنَّـٰهُمۡ فِي ٱلۡأَرۡضِ أَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ وَأَمَرُواْ بِٱلۡمَعۡرُوفِ وَنَهَوۡاْ عَنِ ٱلۡمُنكَرِۗ وَلِلَّهِ عَٰقِبَةُ ٱلۡأُمُورِ\nУларга ер юзида имкон берсак, намозни тўкис адо этурлар, закотни берурлар, яхшиликка буюриб, ёмонликдан қайтарурлар. Ишларнинг оқибати Аллоҳга оиддир'}, {'chapter': 22, 'verse': 42, 'text': 'وَإِن يُكَذِّبُوكَ فَقَدۡ كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَعَادٞ وَثَمُودُ\nАгар сени ёлғончи қилаётган бўлсалар, бас, булардан олдин Нуҳ қавми, Од ва Самуд ҳам ёлғончи қилган эдилар'}, {'chapter': 22, 'verse': 43, 'text': 'وَقَوۡمُ إِبۡرَٰهِيمَ وَقَوۡمُ لُوطٖ\nИброҳим қавми, Лут қавми'}, {'chapter': 22, 'verse': 44, 'text': 'وَأَصۡحَٰبُ مَدۡيَنَۖ وَكُذِّبَ مُوسَىٰۖ فَأَمۡلَيۡتُ لِلۡكَٰفِرِينَ ثُمَّ أَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ نَكِيرِ\nМадян эгалари ҳам. Ва Мусо ҳам ёлғончи қилинди. Мен кофирларга бир оз муҳлат бердим, сўнгра уларни тутдим. Бас, Менинг инкорим қандоқ бўлди'}, {'chapter': 22, 'verse': 45, 'text': 'فَكَأَيِّن مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا وَهِيَ ظَالِمَةٞ فَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَبِئۡرٖ مُّعَطَّلَةٖ وَقَصۡرٖ مَّشِيدٍ\nҚанчадан-қанча шаҳар-қишлоқларни зулм қилгувчи бўлган ҳолида ҳалок этдик. Бас, улар (ҳозирда) шифтлари қулаган, ташландиқ қудуқ, (ҳувиллаган) юксак қаср (ҳолидадир)'}, {'chapter': 22, 'verse': 46, 'text': 'أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَتَكُونَ لَهُمۡ قُلُوبٞ يَعۡقِلُونَ بِهَآ أَوۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۖ فَإِنَّهَا لَا تَعۡمَى ٱلۡأَبۡصَٰرُ وَلَٰكِن تَعۡمَى ٱلۡقُلُوبُ ٱلَّتِي فِي ٱلصُّدُورِ\nАхир, ер юзида айланиб юрмайдиларми, токи қалблар ила ақл юритсалар, қулоқлари ила эшитсалар. Зеро, кўзлар кўр бўлмас, сийналардаги қалблар кўр бўлур'}, {'chapter': 22, 'verse': 47, 'text': 'وَيَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَن يُخۡلِفَ ٱللَّهُ وَعۡدَهُۥۚ وَإِنَّ يَوۡمًا عِندَ رَبِّكَ كَأَلۡفِ سَنَةٖ مِّمَّا تَعُدُّونَ\nУлар сендан азобнинг тезроқ келишини сўрарлар. Ҳолбуки, Аллоҳ ваъдасига хилоф қилмас. Дарҳақиқат, Роббинг ҳузуридаги бир кун сиз санайдиган минг йилдекдир'}, {'chapter': 22, 'verse': 48, 'text': 'وَكَأَيِّن مِّن قَرۡيَةٍ أَمۡلَيۡتُ لَهَا وَهِيَ ظَالِمَةٞ ثُمَّ أَخَذۡتُهَا وَإِلَيَّ ٱلۡمَصِيرُ\nҚанчадан-қанча шаҳар-қишлоқларга зулм қилгувчи бўлган ҳолида ҳам муҳлат бердим. Сўнгра уларни (азобга) тутдим. Қайтиш фақат Менгагинадир'}, {'chapter': 22, 'verse': 49, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَآ أَنَا۠ لَكُمۡ نَذِيرٞ مُّبِينٞ\nСен: «Эй одамлар! Мен сизлар учун фақат бир очиқ огоҳлантиргувчиман, холос», деб айт'}, {'chapter': 22, 'verse': 50, 'text': 'فَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nИймон келтириб, яхши амалларни қилганларга мағфират ва икромли ризқ бордир'}, {'chapter': 22, 'verse': 51, 'text': 'وَٱلَّذِينَ سَعَوۡاْ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nБизнинг оятларимизни ожиз қолдирмоқчи бўлиб, саъйи-ҳаракат қилганлар, ана ўшалар жаҳийм эгаларидир'}, {'chapter': 22, 'verse': 52, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٖ وَلَا نَبِيٍّ إِلَّآ إِذَا تَمَنَّىٰٓ أَلۡقَى ٱلشَّيۡطَٰنُ فِيٓ أُمۡنِيَّتِهِۦ فَيَنسَخُ ٱللَّهُ مَا يُلۡقِي ٱلشَّيۡطَٰنُ ثُمَّ يُحۡكِمُ ٱللَّهُ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nБиз сендан илгари ҳеч бир Расул ва Набий юбормадикки, у бир нарсани таманний қилганида, шайтон унинг таманнийсига (шубҳа) ташламаган бўлса. Бас, Аллоҳ шайтон ташлайдиган нарсани кетказур. Сўнгра, Аллоҳ Ўз оятларини мустаҳкам қилур. Аллоҳ ўта билгувчи ва ҳикматли Зотдир'}, {'chapter': 22, 'verse': 53, 'text': 'لِّيَجۡعَلَ مَا يُلۡقِي ٱلشَّيۡطَٰنُ فِتۡنَةٗ لِّلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡقَاسِيَةِ قُلُوبُهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَفِي شِقَاقِۭ بَعِيدٖ\nШайтон ташлайдиган нарса қалбларида касали борларга ва қалблари тошларга фитна бўлиши учундир. Албатта, золимлар (ҳақдан) узоқ тортишувдадирлар'}, {'chapter': 22, 'verse': 54, 'text': 'وَلِيَعۡلَمَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ فَيُؤۡمِنُواْ بِهِۦ فَتُخۡبِتَ لَهُۥ قُلُوبُهُمۡۗ وَإِنَّ ٱللَّهَ لَهَادِ ٱلَّذِينَ ءَامَنُوٓاْ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nИлм берилганлар у(Қуръон) Роббингдан келган ҳақ эканини билишлари, унга иймон келтиришлари ва қалблари унга боғланиб, таскин топиши учундир. Албатта, Аллоҳ иймон келтирганларни тўғри йўлга ҳидоят қилгувчидир'}, {'chapter': 22, 'verse': 55, 'text': 'وَلَا يَزَالُ ٱلَّذِينَ كَفَرُواْ فِي مِرۡيَةٖ مِّنۡهُ حَتَّىٰ تَأۡتِيَهُمُ ٱلسَّاعَةُ بَغۡتَةً أَوۡ يَأۡتِيَهُمۡ عَذَابُ يَوۡمٍ عَقِيمٍ\nКуфр келтирганлар уларга соат (қиёмат) тўсатдан келгунича ёки қисир Кун азоби келгунича у(Қуръон)дан шубҳада бўлишда давом этадилар'}, {'chapter': 22, 'verse': 56, 'text': 'ٱلۡمُلۡكُ يَوۡمَئِذٖ لِّلَّهِ يَحۡكُمُ بَيۡنَهُمۡۚ فَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فِي جَنَّـٰتِ ٱلنَّعِيمِ\nЎша кунда ҳукмронлик Аллоҳгагина хосдир. У улар орасида ҳукм қилур. Бас, иймон келтирганлар ва яхши амал қилганлар нозу неъмат жаннатларидадирлар'}, {'chapter': 22, 'verse': 57, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا فَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ\nКуфр келтирган ва оятларимизни ёлғонга чиқарганлар эса, бас, ана ўшаларга хорловчи азоб бордир'}, {'chapter': 22, 'verse': 58, 'text': 'وَٱلَّذِينَ هَاجَرُواْ فِي سَبِيلِ ٱللَّهِ ثُمَّ قُتِلُوٓاْ أَوۡ مَاتُواْ لَيَرۡزُقَنَّهُمُ ٱللَّهُ رِزۡقًا حَسَنٗاۚ وَإِنَّ ٱللَّهَ لَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ\nАллоҳ йўлида ҳижрат қилганлар, сўнгра ўлдирилиб ёки вафот этганларни, албатта, Аллоҳ гўзал ризқ ила ризқлантирур. Албатта, Аллоҳ ризқ бергувчиларнинг энг яхшисидир'}, {'chapter': 22, 'verse': 59, 'text': 'لَيُدۡخِلَنَّهُم مُّدۡخَلٗا يَرۡضَوۡنَهُۥۚ وَإِنَّ ٱللَّهَ لَعَلِيمٌ حَلِيمٞ\nАлбатта, У зот уларни ўзлари рози бўладиган жойга киритур. Албатта, Аллоҳ ўта билгувчи ва ҳалийм Зотдир'}, {'chapter': 22, 'verse': 60, 'text': '۞ذَٰلِكَۖ وَمَنۡ عَاقَبَ بِمِثۡلِ مَا عُوقِبَ بِهِۦ ثُمَّ بُغِيَ عَلَيۡهِ لَيَنصُرَنَّهُ ٱللَّهُۚ إِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٞ\nИш бундоқ. Ким ўзига иқоб қилинганига ўхшаш иқоб қилса-ю, сўнгра унга яна зўравонлик қилинса, Аллоҳ, албатта, унга нусрат берур. Албатта, Аллоҳ афвли ва мағфиратли Зотдир'}, {'chapter': 22, 'verse': 61, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَأَنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٞ\nЧунки, Аллоҳ, албатта, кечани кундузга киритур ва кундузни кечага киритур. Албатта, Аллоҳ ўта эшитгувчи ва ўта кўргувчи Зотдир'}, {'chapter': 22, 'verse': 62, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِۦ هُوَ ٱلۡبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ\nЧунки, Аллоҳнинг Ўзигина ҳақдир, Ундан бошқа топинаётганлари эса, ботилдир. Албатта, Аллоҳ юксак ва буюкдир'}, {'chapter': 22, 'verse': 63, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَتُصۡبِحُ ٱلۡأَرۡضُ مُخۡضَرَّةًۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٞ\nАллоҳ осмондан сув туширганида, ернинг ям-яшил бўлишини кўрмадингми?! Албатта, Аллоҳ ўта лутфли ва хабардор Зотдир'}, {'chapter': 22, 'verse': 64, 'text': 'لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nОсмонлару ердаги нарсалар Уникидир. Албатта, Аллоҳ, Унинг Ўзигина беҳожат ва мақталган Зотдир'}, {'chapter': 22, 'verse': 65, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِي ٱلۡأَرۡضِ وَٱلۡفُلۡكَ تَجۡرِي فِي ٱلۡبَحۡرِ بِأَمۡرِهِۦ وَيُمۡسِكُ ٱلسَّمَآءَ أَن تَقَعَ عَلَى ٱلۡأَرۡضِ إِلَّا بِإِذۡنِهِۦٓۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٞ رَّحِيمٞ\nАллоҳ ердаги нарсаларни, денгизда Ўз изни ила юрадиган кемани сизларга бўйсундириб қўйганини ҳамда осмонни изнисиз ерга тушишдан тутиб туришини кўрмадингизми?! Албатта, Аллоҳ одамларга шафқатли ва меҳрибондир'}, {'chapter': 22, 'verse': 66, 'text': 'وَهُوَ ٱلَّذِيٓ أَحۡيَاكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۗ إِنَّ ٱلۡإِنسَٰنَ لَكَفُورٞ\nСизларни тирилтирган, кейин ўлдирадиган, сўнгра (қайта) тирилтирадиган ҳам Унинг Ўзидир. Албатта, инсон ўта ношукрдир'}, {'chapter': 22, 'verse': 67, 'text': 'لِّكُلِّ أُمَّةٖ جَعَلۡنَا مَنسَكًا هُمۡ نَاسِكُوهُۖ فَلَا يُنَٰزِعُنَّكَ فِي ٱلۡأَمۡرِۚ وَٱدۡعُ إِلَىٰ رَبِّكَۖ إِنَّكَ لَعَلَىٰ هُدٗى مُّسۡتَقِيمٖ\nБиз ҳар бир умматга шариат берганмиз. Улар ўшанга амал қилгувчилардир. Бас, бу ишда ҳаргиз сен ила низо қилмасинлар. Роббингга даъват қил. Албатта, сен мустақийм ҳидоятдасан'}, {'chapter': 22, 'verse': 68, 'text': 'وَإِن جَٰدَلُوكَ فَقُلِ ٱللَّهُ أَعۡلَمُ بِمَا تَعۡمَلُونَ\nАгар улар сен билан тортишсалар: «Аллоҳ нима қилаётганингизни билгувчидир'}, {'chapter': 22, 'verse': 69, 'text': 'ٱللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nАллоҳ қиёмат куни сиз ихтилоф қилган нарса бўйича орангизда ҳукм қиладир», деб айт'}, {'chapter': 22, 'verse': 70, 'text': 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِنَّ ذَٰلِكَ فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nАллоҳ, албатта, осмону ердаги нарсани билишини, ана ўша, албатта, китобда эканини ва, албатта, у нарса Аллоҳга осон эканини билмайсанми'}, {'chapter': 22, 'verse': 71, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗا وَمَا لَيۡسَ لَهُم بِهِۦ عِلۡمٞۗ وَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٖ\nУлар Аллоҳни қўйиб, У зот у ҳақида бирон ҳужжат туширмаган, ўзларида у ҳақда илм бўлмаган нарсага ибодат қилурлар. Золимларга ҳеч бир нусрат бергувчи йўқдир'}, {'chapter': 22, 'verse': 72, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ تَعۡرِفُ فِي وُجُوهِ ٱلَّذِينَ كَفَرُواْ ٱلۡمُنكَرَۖ يَكَادُونَ يَسۡطُونَ بِٱلَّذِينَ يَتۡلُونَ عَلَيۡهِمۡ ءَايَٰتِنَاۗ قُلۡ أَفَأُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكُمُۚ ٱلنَّارُ وَعَدَهَا ٱللَّهُ ٱلَّذِينَ كَفَرُواْۖ وَبِئۡسَ ٱلۡمَصِيرُ\nУларга очиқ-ойдин оятларимиз тиловат қилинаётганида, куфр келтирганларнинг юзларида инкорни кўрурсан. Улар ўзларига оятларимизни тиловат қилиб бераётганларга чанг солишларига оз қолурлар. Сен уларга: «Бунингиздан ҳам ёмонроқ нарсанинг хабарини берайми? У дўзахдир. Аллоҳ уни куфр келтирганларга ваъда қилгандир. Ва у нақадар ёмон қайтиш жойидир», дегин'}, {'chapter': 22, 'verse': 73, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ضُرِبَ مَثَلٞ فَٱسۡتَمِعُواْ لَهُۥٓۚ إِنَّ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَن يَخۡلُقُواْ ذُبَابٗا وَلَوِ ٱجۡتَمَعُواْ لَهُۥۖ وَإِن يَسۡلُبۡهُمُ ٱلذُّبَابُ شَيۡـٔٗا لَّا يَسۡتَنقِذُوهُ مِنۡهُۚ ضَعُفَ ٱلطَّالِبُ وَٱلۡمَطۡلُوبُ\nЭй одамлар! Бир масал айтилмиш, бас, унга қулоқ солинг! Албатта, сизлар Аллоҳни қўйиб, топинаётган нарсаларингиз, агар барчалари бирлашсалар ҳам, битта пашша ярата олмаслар. Ва агар пашша улардан бирон нарсани тортиб олса, ундан ўшани қутқариб ололмаслар. Талаб қилувчи ҳам, талаб қилинган ҳам ожиз бўлди'}, {'chapter': 22, 'verse': 74, 'text': 'مَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦٓۚ إِنَّ ٱللَّهَ لَقَوِيٌّ عَزِيزٌ\nУлар Аллоҳнинг қадрини жойига қўймадилар. Албатта, Аллоҳ кучли ва азийздир'}, {'chapter': 22, 'verse': 75, 'text': 'ٱللَّهُ يَصۡطَفِي مِنَ ٱلۡمَلَـٰٓئِكَةِ رُسُلٗا وَمِنَ ٱلنَّاسِۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٞ\nАллоҳ фаришталардан ва одамлардан Пайғамбарлар танлар. Албатта, Аллоҳ ўта эшитгувчи ва кўргувчидир'}, {'chapter': 22, 'verse': 76, 'text': 'يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nУ зот уларнинг олдиларидаги ва ортларидаги нарсани билур. Барча ишлар ёлғиз Аллоҳгагина қайтарилур'}, {'chapter': 22, 'verse': 77, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱرۡكَعُواْ وَٱسۡجُدُواْۤ وَٱعۡبُدُواْ رَبَّكُمۡ وَٱفۡعَلُواْ ٱلۡخَيۡرَ لَعَلَّكُمۡ تُفۡلِحُونَ۩\nЭй, иймон келтирганлар, рукуъ, сажда қилинглар ва Роббингизга ибодат қилинглар ҳамда яхшилик қилинглар, шоядки, нажот топсангиз'}, {'chapter': 22, 'verse': 78, 'text': 'وَجَٰهِدُواْ فِي ٱللَّهِ حَقَّ جِهَادِهِۦۚ هُوَ ٱجۡتَبَىٰكُمۡ وَمَا جَعَلَ عَلَيۡكُمۡ فِي ٱلدِّينِ مِنۡ حَرَجٖۚ مِّلَّةَ أَبِيكُمۡ إِبۡرَٰهِيمَۚ هُوَ سَمَّىٰكُمُ ٱلۡمُسۡلِمِينَ مِن قَبۡلُ وَفِي هَٰذَا لِيَكُونَ ٱلرَّسُولُ شَهِيدًا عَلَيۡكُمۡ وَتَكُونُواْ شُهَدَآءَ عَلَى ٱلنَّاسِۚ فَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَٱعۡتَصِمُواْ بِٱللَّهِ هُوَ مَوۡلَىٰكُمۡۖ فَنِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِيرُ\nВа Аллоҳ (йўлида) ҳақ жиҳод қилингиз. У сизларни танлаб олди. Ва сизга бу динда ҳеч танглик қилмади. Бу, отангиз Иброҳимнинг миллати. У сизларни бундан олдин ва бу (Қуръон)да Пайғамбар сизларга гувоҳ бўлиши учун, сизлар эса, одамларга гувоҳлар бўлишингиз учун «мусулмонлар» деб номлади. Бас, намозни тўкис адо этинглар, закот беринглар ва Аллоҳга боғланинглар. У сизнинг хожангиздир. Бас, У зот қандай ҳам яхши хожа ва қандай ҳам яхши нусрат бергувчидир'}], '23': [{'chapter': 23, 'verse': 1, 'text': 'قَدۡ أَفۡلَحَ ٱلۡمُؤۡمِنُونَ\nБатаҳқиқ, мўминлар нажот топдилар'}, {'chapter': 23, 'verse': 2, 'text': 'ٱلَّذِينَ هُمۡ فِي صَلَاتِهِمۡ خَٰشِعُونَ\nУлар намозларида хушуъ қилувчилардир'}, {'chapter': 23, 'verse': 3, 'text': 'وَٱلَّذِينَ هُمۡ عَنِ ٱللَّغۡوِ مُعۡرِضُونَ\nУлар беҳуда нарсалардан юз ўгиргувчилардир'}, {'chapter': 23, 'verse': 4, 'text': 'وَٱلَّذِينَ هُمۡ لِلزَّكَوٰةِ فَٰعِلُونَ\nУлар закотни адо қилгувчилардир'}, {'chapter': 23, 'verse': 5, 'text': 'وَٱلَّذِينَ هُمۡ لِفُرُوجِهِمۡ حَٰفِظُونَ\nУлар фаржларини сақловчилардир'}, {'chapter': 23, 'verse': 6, 'text': 'إِلَّا عَلَىٰٓ أَزۡوَٰجِهِمۡ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَإِنَّهُمۡ غَيۡرُ مَلُومِينَ\nМагар ўз жуфти ҳалоллари ва қўлларида мулк бўлганлардан (сақламасалар), албатта, улар маломат қилингувчи эмаслар'}, {'chapter': 23, 'verse': 7, 'text': 'فَمَنِ ٱبۡتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡعَادُونَ\nКим ана шундан бошқани талаб қилса, бас, ана ўшалар тажовузкорлардир'}, {'chapter': 23, 'verse': 8, 'text': 'وَٱلَّذِينَ هُمۡ لِأَمَٰنَٰتِهِمۡ وَعَهۡدِهِمۡ رَٰعُونَ\nУлар омонатларига ва аҳдларига риоя қилгувчилардир'}, {'chapter': 23, 'verse': 9, 'text': 'وَٱلَّذِينَ هُمۡ عَلَىٰ صَلَوَٰتِهِمۡ يُحَافِظُونَ\nУлар намозларини муҳофаза қилгувчилардир'}, {'chapter': 23, 'verse': 10, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡوَٰرِثُونَ\nАна ўшалар ворис бўлгувчилардир'}, {'chapter': 23, 'verse': 11, 'text': 'ٱلَّذِينَ يَرِثُونَ ٱلۡفِرۡدَوۡسَ هُمۡ فِيهَا خَٰلِدُونَ\nУлар Фирдавс (жаннати)ни мерос олурлар, улар унда абадий қолгувчилардир'}, {'chapter': 23, 'verse': 12, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ مِن سُلَٰلَةٖ مِّن طِينٖ\nҚасамки, батаҳқиқ, инсонни лой сулоласидан яратдик'}, {'chapter': 23, 'verse': 13, 'text': 'ثُمَّ جَعَلۡنَٰهُ نُطۡفَةٗ فِي قَرَارٖ مَّكِينٖ\nСўнгра уни мустаҳкам қароргоҳда нутфа қилдик'}, {'chapter': 23, 'verse': 14, 'text': 'ثُمَّ خَلَقۡنَا ٱلنُّطۡفَةَ عَلَقَةٗ فَخَلَقۡنَا ٱلۡعَلَقَةَ مُضۡغَةٗ فَخَلَقۡنَا ٱلۡمُضۡغَةَ عِظَٰمٗا فَكَسَوۡنَا ٱلۡعِظَٰمَ لَحۡمٗا ثُمَّ أَنشَأۡنَٰهُ خَلۡقًا ءَاخَرَۚ فَتَبَارَكَ ٱللَّهُ أَحۡسَنُ ٱلۡخَٰلِقِينَ\nСўнгра нутфадан алақа яратдик, алақадан чайналган гўшт яратдик, чайналган парча гўштдан суяк яратдик, бас, суякка гўшт қопладик, сўнгра уни бошқа бир жонзот этиб пайдо қилдик. Бас, яратгувчиларнинг энг яхшиси Аллоҳ баракотли ва буюкдир'}, {'chapter': 23, 'verse': 15, 'text': 'ثُمَّ إِنَّكُم بَعۡدَ ذَٰلِكَ لَمَيِّتُونَ\nСўнгра, албатта, сизлар бундан кейин ўлгувчилардирсиз'}, {'chapter': 23, 'verse': 16, 'text': 'ثُمَّ إِنَّكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ تُبۡعَثُونَ\nСўнгра, албатта, сизлар қиёмат куни қайта тирилтирилурсиз'}, {'chapter': 23, 'verse': 17, 'text': 'وَلَقَدۡ خَلَقۡنَا فَوۡقَكُمۡ سَبۡعَ طَرَآئِقَ وَمَا كُنَّا عَنِ ٱلۡخَلۡقِ غَٰفِلِينَ\nБатаҳқиқ, Биз устингизда етти йўлни яратдик. Биз махлуқотдан ғофил бўлган эмасмиз'}, {'chapter': 23, 'verse': 18, 'text': 'وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَسۡكَنَّـٰهُ فِي ٱلۡأَرۡضِۖ وَإِنَّا عَلَىٰ ذَهَابِۭ بِهِۦ لَقَٰدِرُونَ\nБиз осмондан ўлчов ила сув тушириб, уни ерга жойлаштирдик. Албатта, Биз уни кетказишга ҳам қодирдирмиз'}, {'chapter': 23, 'verse': 19, 'text': 'فَأَنشَأۡنَا لَكُم بِهِۦ جَنَّـٰتٖ مِّن نَّخِيلٖ وَأَعۡنَٰبٖ لَّكُمۡ فِيهَا فَوَٰكِهُ كَثِيرَةٞ وَمِنۡهَا تَأۡكُلُونَ\nВа у (сув) ила сиз учун хурмо ва узум боғлари пайдо қилдик, сизга у(боғ)ларда кўп мевалар бўлиб, улардан ерсизлар'}, {'chapter': 23, 'verse': 20, 'text': 'وَشَجَرَةٗ تَخۡرُجُ مِن طُورِ سَيۡنَآءَ تَنۢبُتُ بِٱلدُّهۡنِ وَصِبۡغٖ لِّلۡأٓكِلِينَ\nВа Тури Сийнодан чиқадиган бир дарахтни (пайдо қилдикки), у ёғ ва егувчиларга нонхуриш ила ўсур'}, {'chapter': 23, 'verse': 21, 'text': 'وَإِنَّ لَكُمۡ فِي ٱلۡأَنۡعَٰمِ لَعِبۡرَةٗۖ نُّسۡقِيكُم مِّمَّا فِي بُطُونِهَا وَلَكُمۡ فِيهَا مَنَٰفِعُ كَثِيرَةٞ وَمِنۡهَا تَأۡكُلُونَ\nАлбатта, сиз учун чорва ҳайвонларида ибрат бордир. Биз сизни уларнинг қоринларидаги нарса ила суғорурмиз ва сиз учун уларда кўпгина манфаатлар бор ва улардан ерсизлар'}, {'chapter': 23, 'verse': 22, 'text': 'وَعَلَيۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ\nВа уларга ва кемаларга юкланурсиз'}, {'chapter': 23, 'verse': 23, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ\nБатаҳқиқ, Биз Нуҳни қавмига юбордик. Бас, у: «Эй қавмим, Аллоҳга ибодат қилинг. Сиз учун ундан ўзга ҳеч бир илоҳ йўқдир. Наҳотки қўрқмасангиз?!» деди'}, {'chapter': 23, 'verse': 24, 'text': 'فَقَالَ ٱلۡمَلَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يُرِيدُ أَن يَتَفَضَّلَ عَلَيۡكُمۡ وَلَوۡ شَآءَ ٱللَّهُ لَأَنزَلَ مَلَـٰٓئِكَةٗ مَّا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ\nБас, унинг қавмидан куфр келтирган зодагонлари: «Бу, сизга ўхшаш башар, холос, сиздан афзал бўлиб олмоқчи. Агар Аллоҳ хоҳласа, фаришталарни туширар эди. Аввалги ота-боболаримизда буни эшитган эмасмиз'}, {'chapter': 23, 'verse': 25, 'text': 'إِنۡ هُوَ إِلَّا رَجُلُۢ بِهِۦ جِنَّةٞ فَتَرَبَّصُواْ بِهِۦ حَتَّىٰ حِينٖ\nУ фақат бир жинни одам, холос. Бас, уни бир оз муддат пойлаб туринг», дедилар'}, {'chapter': 23, 'verse': 26, 'text': 'قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ\nУ: «Эй Роббим, мени ёлғончи қилганларга қарши менга нусрат бер», деди'}, {'chapter': 23, 'verse': 27, 'text': 'فَأَوۡحَيۡنَآ إِلَيۡهِ أَنِ ٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا فَإِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ فَٱسۡلُكۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ مِنۡهُمۡۖ وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ\nБас, Биз унга: «Бизнинг кўз ўнгимизда ва ваҳийимиз ила кема яса. Бас, Бизнинг амримиз келиб, таннур фаввора бўлганда, у(кема)га ҳар бир турдан бир жуфтдан ва аҳлингни чиқар, магар кимнинг зиддига олдин сўзимиз ўтган бўлса (чиқарма). Зулм қилганлар ҳақида Менга хитоб қилма. Албатта, улар ғарқ қилингувчилардир'}, {'chapter': 23, 'verse': 28, 'text': 'فَإِذَا ٱسۡتَوَيۡتَ أَنتَ وَمَن مَّعَكَ عَلَى ٱلۡفُلۡكِ فَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي نَجَّىٰنَا مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, сен ўзинг билан бирга бўлганлар ила кемага жойлашиб олгач: «Бизни золим қавмлардан қутқарган Аллоҳга ҳамдлар бўлсин», дегин'}, {'chapter': 23, 'verse': 29, 'text': 'وَقُل رَّبِّ أَنزِلۡنِي مُنزَلٗا مُّبَارَكٗا وَأَنتَ خَيۡرُ ٱلۡمُنزِلِينَ\nВа: «Эй Роббим, мени муборак манзилга туширгин, Сен Ўзинг манзилга туширгувчиларнинг энг яхшисисан», дегин», дедик'}, {'chapter': 23, 'verse': 30, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ وَإِن كُنَّا لَمُبۡتَلِينَ\nАлбатта, бунда оят-ибратлар бордир. Албатта, Биз синовчи бўлган эдик'}, {'chapter': 23, 'verse': 31, 'text': 'ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ\nСўнгра улардан кейин бошқа авлодларни пайдо қилдик'}, {'chapter': 23, 'verse': 32, 'text': 'فَأَرۡسَلۡنَا فِيهِمۡ رَسُولٗا مِّنۡهُمۡ أَنِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ\nБас, уларнинг ичига ўзларидан бўлган Пайғамбарни: «Аллоҳга ибодат қилинг. Сиз учун ундан ўзга илоҳ йўқдир, наҳотки, қўрқмасангиз», деб юбордик'}, {'chapter': 23, 'verse': 33, 'text': 'وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِهِ ٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِلِقَآءِ ٱلۡأٓخِرَةِ وَأَتۡرَفۡنَٰهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يَأۡكُلُ مِمَّا تَأۡكُلُونَ مِنۡهُ وَيَشۡرَبُ مِمَّا تَشۡرَبُونَ\nУнинг қавмидан куфр келтирган, охиратга рўбарў бўлишини ёлғонга чиқарган ва Биз уларни ҳаёти дунёда маишатпараст қилиб қўйган зодагонлари: «Бу, сизга ўхшаган башар, холос. Сиз еган нарсадан ейдир, сиз ичган нарсадан ичадир'}, {'chapter': 23, 'verse': 34, 'text': 'وَلَئِنۡ أَطَعۡتُم بَشَرٗا مِّثۡلَكُمۡ إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ\nАгар ўзингизга ўхшаган башарга итоат қилсангиз, унда, албатта, сиз зиёнкорлардандирсиз'}, {'chapter': 23, 'verse': 35, 'text': 'أَيَعِدُكُمۡ أَنَّكُمۡ إِذَا مِتُّمۡ وَكُنتُمۡ تُرَابٗا وَعِظَٰمًا أَنَّكُم مُّخۡرَجُونَ\nУ сизга, ўлсангиз, тупроқ ва суяк бўлсангиз ҳам, албатта, чиқарилгувчи бўлишингизни ваъда қилмоқдами'}, {'chapter': 23, 'verse': 36, 'text': '۞هَيۡهَاتَ هَيۡهَاتَ لِمَا تُوعَدُونَ\nҲайҳот! Ҳайҳот! Сизга ваъда қилинган нарса нақадар узоқ'}, {'chapter': 23, 'verse': 37, 'text': 'إِنۡ هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا نَحۡنُ بِمَبۡعُوثِينَ\nШу дунёдаги ҳаётимиздан бошқа ҳаёт йўқдир. Ўламиз, тириламиз ва биз қайта тирилгувчи эмасмиз'}, {'chapter': 23, 'verse': 38, 'text': 'إِنۡ هُوَ إِلَّا رَجُلٌ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا وَمَا نَحۡنُ لَهُۥ بِمُؤۡمِنِينَ\nУ фақат Аллоҳга нисбатан ёлғон уйдирган одам, холос. Биз унга иймон келтиргувчи эмасмиз», дедилар'}, {'chapter': 23, 'verse': 39, 'text': 'قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ\nУ: «Эй Роббим, мени ёлғончи қилганларга қарши менга нусрат бер», деди'}, {'chapter': 23, 'verse': 40, 'text': 'قَالَ عَمَّا قَلِيلٖ لَّيُصۡبِحُنَّ نَٰدِمِينَ\nУ зот: «Озгинадан сўнг улар надомат чекувчи бўлурлар», деди'}, {'chapter': 23, 'verse': 41, 'text': 'فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ بِٱلۡحَقِّ فَجَعَلۡنَٰهُمۡ غُثَآءٗۚ فَبُعۡدٗا لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, уларни ҳақ ила қичқириқ олди. Бас, Биз уларни хасу чўпга айлантирдик. Дафъ бўлсин золим қавмлар'}, {'chapter': 23, 'verse': 42, 'text': 'ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قُرُونًا ءَاخَرِينَ\nСўнгра улардан кейин бошқа аср авлодларини пайдо қилдик'}, {'chapter': 23, 'verse': 43, 'text': 'مَا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ\nҲеч бир уммат ўз ажалидан олдин ҳам кетмас, ортда ҳам қолмас'}, {'chapter': 23, 'verse': 44, 'text': 'ثُمَّ أَرۡسَلۡنَا رُسُلَنَا تَتۡرَاۖ كُلَّ مَا جَآءَ أُمَّةٗ رَّسُولُهَا كَذَّبُوهُۖ فَأَتۡبَعۡنَا بَعۡضَهُم بَعۡضٗا وَجَعَلۡنَٰهُمۡ أَحَادِيثَۚ فَبُعۡدٗا لِّقَوۡمٖ لَّا يُؤۡمِنُونَ\nСўнгра Пайғамбарларимизни кетма-кет юбордик. Ҳар қачон бир умматга ўз Пайғамбари келса, уни ёлғончи қилдилар. Бас, Биз ҳам уларни баъзилари ортидан баъзиларини (ҳалокатга) эргаштиравердик ва уларни гапу сўзга айлантирдик. Бас, дафъ бўлсин иймон келтирмайдиган қавмлар'}, {'chapter': 23, 'verse': 45, 'text': 'ثُمَّ أَرۡسَلۡنَا مُوسَىٰ وَأَخَاهُ هَٰرُونَ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ\nСўнгра Биз Мусо ва биродари Ҳорунни оятларимиз ва очиқ ҳужжат ила'}, {'chapter': 23, 'verse': 46, 'text': 'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمًا عَالِينَ\nФиръавн ва унинг аъёнларига юбордик. Бас, улар мутакаббирлик қилдилар ва ўзини юқори қўйган қавм бўлдилар'}, {'chapter': 23, 'verse': 47, 'text': 'فَقَالُوٓاْ أَنُؤۡمِنُ لِبَشَرَيۡنِ مِثۡلِنَا وَقَوۡمُهُمَا لَنَا عَٰبِدُونَ\nБас, улар: «Ўзимизга ўхшаган икки башарга иймон келтирамизми?! Ҳолбуки, уларнинг қавми бизга қуллик қилгувчи бўлиб турибди-ку?!» дедилар'}, {'chapter': 23, 'verse': 48, 'text': 'فَكَذَّبُوهُمَا فَكَانُواْ مِنَ ٱلۡمُهۡلَكِينَ\nБас, икковларини ёлғончи қилдилар ва ҳалок қилинганлардан бўлдилар'}, {'chapter': 23, 'verse': 49, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ لَعَلَّهُمۡ يَهۡتَدُونَ\nБатаҳқиқ, Биз, шоядки, ҳидоят топсалар деб, Мусога Китобни берган эдик'}, {'chapter': 23, 'verse': 50, 'text': 'وَجَعَلۡنَا ٱبۡنَ مَرۡيَمَ وَأُمَّهُۥٓ ءَايَةٗ وَءَاوَيۡنَٰهُمَآ إِلَىٰ رَبۡوَةٖ ذَاتِ قَرَارٖ وَمَعِينٖ\nИбн Марямни ва унинг онасини оят-аломат қилдик ва икковларини оқар сувли қароргоҳ тепаликка жойладик'}, {'chapter': 23, 'verse': 51, 'text': 'يَـٰٓأَيُّهَا ٱلرُّسُلُ كُلُواْ مِنَ ٱلطَّيِّبَٰتِ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ عَلِيمٞ\nЭй Пайғамбарлар! Пок нарсалардан енглар ва солиҳ амаллар қилинглар. Албатта, Мен нима амал қилаётганингизни ўта билгувчиман'}, {'chapter': 23, 'verse': 52, 'text': 'وَإِنَّ هَٰذِهِۦٓ أُمَّتُكُمۡ أُمَّةٗ وَٰحِدَةٗ وَأَنَا۠ رَبُّكُمۡ فَٱتَّقُونِ\nАлбатта, сизнинг бу умматингиз ягона умматдир. Мен эса, Роббингизман. Бас, Менга тақво қилинг'}, {'chapter': 23, 'verse': 53, 'text': 'فَتَقَطَّعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡ زُبُرٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمۡ فَرِحُونَ\nБас, улар ишларини ўз ораларида пора-пора қилиб бўлиб юбордилар. Ҳар фирқа ўз ҳузурларидаги нарса ила хурсанддирлар'}, {'chapter': 23, 'verse': 54, 'text': 'فَذَرۡهُمۡ فِي غَمۡرَتِهِمۡ حَتَّىٰ حِينٍ\nБас, сен уларни ғафлатларида бир муддат қўйиб қўй'}, {'chapter': 23, 'verse': 55, 'text': 'أَيَحۡسَبُونَ أَنَّمَا نُمِدُّهُم بِهِۦ مِن مَّالٖ وَبَنِينَ\nУлар Бизнинг ўзларига молу дунё ва фарзандлар ила мадад бераётганимизни'}, {'chapter': 23, 'verse': 56, 'text': 'نُسَارِعُ لَهُمۡ فِي ٱلۡخَيۡرَٰتِۚ بَل لَّا يَشۡعُرُونَ\nУларга яхшиликларни тезлатишимиз, деб ҳисоблайдиларми? Йўқ! Улар сезмаслар'}, {'chapter': 23, 'verse': 57, 'text': 'إِنَّ ٱلَّذِينَ هُم مِّنۡ خَشۡيَةِ رَبِّهِم مُّشۡفِقُونَ\nАлбатта, Роббиларидан қўрқиб хавфда турувчилар'}, {'chapter': 23, 'verse': 58, 'text': 'وَٱلَّذِينَ هُم بِـَٔايَٰتِ رَبِّهِمۡ يُؤۡمِنُونَ\nВа Роббилари оятларига иймон келтирадиганлар'}, {'chapter': 23, 'verse': 59, 'text': 'وَٱلَّذِينَ هُم بِرَبِّهِمۡ لَا يُشۡرِكُونَ\nВа Роббиларига ширк келтирмайдиганлар'}, {'chapter': 23, 'verse': 60, 'text': 'وَٱلَّذِينَ يُؤۡتُونَ مَآ ءَاتَواْ وَّقُلُوبُهُمۡ وَجِلَةٌ أَنَّهُمۡ إِلَىٰ رَبِّهِمۡ رَٰجِعُونَ\nВа берадиган нарсаларини албатта Роббиларига қайтгувчи эканликларидан қалблари титраган ҳолда берадиганлар'}, {'chapter': 23, 'verse': 61, 'text': 'أُوْلَـٰٓئِكَ يُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِ وَهُمۡ لَهَا سَٰبِقُونَ\nАна ўшалар яхшиликларга шошилурлар ва улар ўша (яхшилик)лар учун мусобақа қилурлар'}, {'chapter': 23, 'verse': 62, 'text': 'وَلَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۚ وَلَدَيۡنَا كِتَٰبٞ يَنطِقُ بِٱلۡحَقِّ وَهُمۡ لَا يُظۡلَمُونَ\nБиз ҳеч бир жонга тоқатидан ташқари таклиф қилмасмиз. Ҳузуримизда китоб бор. У ҳақни сўзлайдир. Уларга зулм қилинмас'}, {'chapter': 23, 'verse': 63, 'text': 'بَلۡ قُلُوبُهُمۡ فِي غَمۡرَةٖ مِّنۡ هَٰذَا وَلَهُمۡ أَعۡمَٰلٞ مِّن دُونِ ذَٰلِكَ هُمۡ لَهَا عَٰمِلُونَ\nЙўқ! Уларнинг қалблари бундан ғафлатдадир. Уларнинг бундан бошқа амаллари бордир. Улар ўшаларни қилгувчилардир'}, {'chapter': 23, 'verse': 64, 'text': 'حَتَّىٰٓ إِذَآ أَخَذۡنَا مُتۡرَفِيهِم بِٱلۡعَذَابِ إِذَا هُمۡ يَجۡـَٔرُونَ\nТоки уларнинг маишатпарастларини азоб ила тутганимизда, бирдан фарёд солурлар'}, {'chapter': 23, 'verse': 65, 'text': 'لَا تَجۡـَٔرُواْ ٱلۡيَوۡمَۖ إِنَّكُم مِّنَّا لَا تُنصَرُونَ\n«Энди, бугун фарёд солманг! Албатта, Биз томонимиздан сизга нусрат қилинмас'}, {'chapter': 23, 'verse': 66, 'text': 'قَدۡ كَانَتۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَكُنتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ تَنكِصُونَ\nБатаҳқиқ, Менинг оятларим сизга тиловат қилинар эди. Сиз эса, ортингизга қочар эдингиз'}, {'chapter': 23, 'verse': 67, 'text': 'مُسۡتَكۡبِرِينَ بِهِۦ سَٰمِرٗا تَهۡجُرُونَ\nУ билан кибрланар, тунги суҳбатингизда беҳуда сўзлар айтар эдингиз», деймиз'}, {'chapter': 23, 'verse': 68, 'text': 'أَفَلَمۡ يَدَّبَّرُواْ ٱلۡقَوۡلَ أَمۡ جَآءَهُم مَّا لَمۡ يَأۡتِ ءَابَآءَهُمُ ٱلۡأَوَّلِينَ\nНаҳотки улар бу сўзни тадаббур қилиб кўрмадилар? Ёки уларга аввалги ота-боболарига келмаган нарса келибдими'}, {'chapter': 23, 'verse': 69, 'text': 'أَمۡ لَمۡ يَعۡرِفُواْ رَسُولَهُمۡ فَهُمۡ لَهُۥ مُنكِرُونَ\nЁки ўз Пайғамбарларини танимасдан улар унга инкор қилгувчи бўлмоқдаларми'}, {'chapter': 23, 'verse': 70, 'text': 'أَمۡ يَقُولُونَ بِهِۦ جِنَّةُۢۚ بَلۡ جَآءَهُم بِٱلۡحَقِّ وَأَكۡثَرُهُمۡ لِلۡحَقِّ كَٰرِهُونَ\nЁки, унда жиннилик бор, дерларми? Йўқ! У уларга ҳақ ила келди. Уларнинг кўплари эса, ҳақни ёмон кўргувчидирлар'}, {'chapter': 23, 'verse': 71, 'text': 'وَلَوِ ٱتَّبَعَ ٱلۡحَقُّ أَهۡوَآءَهُمۡ لَفَسَدَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ بَلۡ أَتَيۡنَٰهُم بِذِكۡرِهِمۡ فَهُمۡ عَن ذِكۡرِهِم مُّعۡرِضُونَ\nАгар ҳақ уларнинг ҳавойи нафсларига эргашса, албатта, осмонлару ер ва улардаги кимсалар фасодга учрар эди. Йўқ! Биз уларга ўз шарафларини келтирдик. Улар эса, шарафларидан юз ўгирмоқдалар'}, {'chapter': 23, 'verse': 72, 'text': 'أَمۡ تَسۡـَٔلُهُمۡ خَرۡجٗا فَخَرَاجُ رَبِّكَ خَيۡرٞۖ وَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ\nЁки сен улардан харож сўраяпсанми?! Бас, Роббингнинг харожи яхшироқдир. У зот энг яхши ризқ бергувчидир'}, {'chapter': 23, 'verse': 73, 'text': 'وَإِنَّكَ لَتَدۡعُوهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАлбатта, сен уларни тўғри йўлга чақирасан'}, {'chapter': 23, 'verse': 74, 'text': 'وَإِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ عَنِ ٱلصِّرَٰطِ لَنَٰكِبُونَ\nАлбатта, охиратга иймон келтирмайдиганлар бу йўлдан озгувчидирлар'}, {'chapter': 23, 'verse': 75, 'text': '۞وَلَوۡ رَحِمۡنَٰهُمۡ وَكَشَفۡنَا مَا بِهِم مِّن ضُرّٖ لَّلَجُّواْ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАгар уларга раҳим қилиб, уларга етган зарарни кушойиш қилсак ҳам, туғёнларида бардавом бўлиб, адашиб-улоқиб юраверадилар'}, {'chapter': 23, 'verse': 76, 'text': 'وَلَقَدۡ أَخَذۡنَٰهُم بِٱلۡعَذَابِ فَمَا ٱسۡتَكَانُواْ لِرَبِّهِمۡ وَمَا يَتَضَرَّعُونَ\nБатаҳқиқ, Биз уларни азоб ила тутдик, бас, улар Роббиларига бўйин ҳам эгмадилар, тазарруъ ҳам қилмадилар'}, {'chapter': 23, 'verse': 77, 'text': 'حَتَّىٰٓ إِذَا فَتَحۡنَا عَلَيۡهِم بَابٗا ذَا عَذَابٖ شَدِيدٍ إِذَا هُمۡ فِيهِ مُبۡلِسُونَ\nТоки қачон уларга шиддатли азоб эшигини очганимизда, тўсатдан ноумид бўлгувчилардир'}, {'chapter': 23, 'verse': 78, 'text': 'وَهُوَ ٱلَّذِيٓ أَنشَأَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ\nУ зот сизга қулоқни, кўзлару қалбларни пайдо қилган зот. Нақадар оз шукр қилурсиз'}, {'chapter': 23, 'verse': 79, 'text': 'وَهُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ\nУ зот сизларни ер юзида яратиб, таратгандир. Ва Унгагина тўпланурсизлар'}, {'chapter': 23, 'verse': 80, 'text': 'وَهُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ وَلَهُ ٱخۡتِلَٰفُ ٱلَّيۡلِ وَٱلنَّهَارِۚ أَفَلَا تَعۡقِلُونَ\nУ зот тирилтирур ва ўлдирур. Кечаю кундузнинг алмашиниши ҳам Унга оид. Наҳотки ақл юритмасангиз'}, {'chapter': 23, 'verse': 81, 'text': 'بَلۡ قَالُواْ مِثۡلَ مَا قَالَ ٱلۡأَوَّلُونَ\nЙўқ! Улар аввалгилар айтган нарсага ўхшаш нарсани айтдилар'}, {'chapter': 23, 'verse': 82, 'text': 'قَالُوٓاْ أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ\nУлар: «Биз ўлиб, тупроқ ва суяк бўлганимиздан кейин ҳам-а? Биз, албатта, қайта тирилтирилувчимизми-а'}, {'chapter': 23, 'verse': 83, 'text': 'لَقَدۡ وُعِدۡنَا نَحۡنُ وَءَابَآؤُنَا هَٰذَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nБатаҳқиқ, бизга ҳам, бундан олдин ота-боболаримизга ҳам бу ваъда қилинган эди. Бу аввалгиларнинг афсонасидан бошқа нарса эмас», дедилар'}, {'chapter': 23, 'verse': 84, 'text': 'قُل لِّمَنِ ٱلۡأَرۡضُ وَمَن فِيهَآ إِن كُنتُمۡ تَعۡلَمُونَ\nСен: «Агар билсангиз, ер ва ундаги кимсалар кимникидир?» деб айт'}, {'chapter': 23, 'verse': 85, 'text': 'سَيَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَذَكَّرُونَ\nУлар: «Аллоҳникидир», дерлар. Сен: «Наҳотки эслатма олмасангиз?!» дегин'}, {'chapter': 23, 'verse': 86, 'text': 'قُلۡ مَن رَّبُّ ٱلسَّمَٰوَٰتِ ٱلسَّبۡعِ وَرَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ\nСен: «Етти осмоннинг Робби ким? Улуғ аршнинг Робби ким?» деб айт'}, {'chapter': 23, 'verse': 87, 'text': 'سَيَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَتَّقُونَ\nУлар: «Аллоҳникидир», дерлар. Сен: «Наҳотки қўрқмасангиз?!» деб айт'}, {'chapter': 23, 'verse': 88, 'text': 'قُلۡ مَنۢ بِيَدِهِۦ مَلَكُوتُ كُلِّ شَيۡءٖ وَهُوَ يُجِيرُ وَلَا يُجَارُ عَلَيۡهِ إِن كُنتُمۡ تَعۡلَمُونَ\nСен: «Агар билсангиз (айтинг-чи), ҳар бир нарсанинг мулки қўлида бўлган, Ўзи ҳимоя қиладиган ва Ундан ҳимоя қилинмайдиган зот ким?!» деб айт'}, {'chapter': 23, 'verse': 89, 'text': 'سَيَقُولُونَ لِلَّهِۚ قُلۡ فَأَنَّىٰ تُسۡحَرُونَ\nУлар: «Аллоҳникидир», дерлар. Сен: «Қандоқ ҳам сеҳрланмоқдасиз-а?!» деб айт'}, {'chapter': 23, 'verse': 90, 'text': 'بَلۡ أَتَيۡنَٰهُم بِٱلۡحَقِّ وَإِنَّهُمۡ لَكَٰذِبُونَ\nЙўқ! Биз уларга ҳақни келтирдик. Улар эса ёлғончилардир'}, {'chapter': 23, 'verse': 91, 'text': 'مَا ٱتَّخَذَ ٱللَّهُ مِن وَلَدٖ وَمَا كَانَ مَعَهُۥ مِنۡ إِلَٰهٍۚ إِذٗا لَّذَهَبَ كُلُّ إِلَٰهِۭ بِمَا خَلَقَ وَلَعَلَا بَعۡضُهُمۡ عَلَىٰ بَعۡضٖۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ\nАллоҳ ҳеч бир фарзанд тутмаган, У билан бирга ҳеч бир илоҳ бўлмаган. Шундоқ бўлганида, ҳар бир илоҳ ўзи яратган нарсаси билан кетиб, баъзилари баъзиларидан устун келар эди. Аллоҳ улар қилаётган васфдан пок бўлди'}, {'chapter': 23, 'verse': 92, 'text': 'عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУ зот ғайбни ва ҳозирни билгувчидир. У зот улар ширк келтираётган нарсадан олийдир'}, {'chapter': 23, 'verse': 93, 'text': 'قُل رَّبِّ إِمَّا تُرِيَنِّي مَا يُوعَدُونَ\nСен: «Эй Роббим, агар менга уларга ваъда қилинган нарсани кўрсатадиган бўлсанг'}, {'chapter': 23, 'verse': 94, 'text': 'رَبِّ فَلَا تَجۡعَلۡنِي فِي ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nЭй Роббим, мени золим қавмлар ичида қилмагин», деб айт'}, {'chapter': 23, 'verse': 95, 'text': 'وَإِنَّا عَلَىٰٓ أَن نُّرِيَكَ مَا نَعِدُهُمۡ لَقَٰدِرُونَ\nАлбатта, Биз уларга ваъда қилаётган нарсамизни сенга кўрсатишга қодирмиз'}, {'chapter': 23, 'verse': 96, 'text': 'ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ ٱلسَّيِّئَةَۚ نَحۡنُ أَعۡلَمُ بِمَا يَصِفُونَ\nСен эса, ёмонликни гўзал нарса ила дафъ қил. Биз нима васф қилаётганларини яхши билгувчимиз'}, {'chapter': 23, 'verse': 97, 'text': 'وَقُل رَّبِّ أَعُوذُ بِكَ مِنۡ هَمَزَٰتِ ٱلشَّيَٰطِينِ\nСен: «Эй Роббим, Сендан шайтонларнинг васвасасидан паноҳ сўрайман'}, {'chapter': 23, 'verse': 98, 'text': 'وَأَعُوذُ بِكَ رَبِّ أَن يَحۡضُرُونِ\nЭй Роббим, Сендан уларнинг менга ҳозир бўлишларидан паноҳ сўрайман», деб айт'}, {'chapter': 23, 'verse': 99, 'text': 'حَتَّىٰٓ إِذَا جَآءَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ رَبِّ ٱرۡجِعُونِ\nТоки улардан бирига ўлим келган чоғда: «Эй Роббим, мени қайтаргин'}, {'chapter': 23, 'verse': 100, 'text': 'لَعَلِّيٓ أَعۡمَلُ صَٰلِحٗا فِيمَا تَرَكۡتُۚ كَلَّآۚ إِنَّهَا كَلِمَةٌ هُوَ قَآئِلُهَاۖ وَمِن وَرَآئِهِم بَرۡزَخٌ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nШоядки, тарк қилган нарсамда солиҳ амал қилсам», дер. Йўқ! Албатта, бу (қуруқ) сўз бўлиб, у айтгувчи, холос. Уларнинг ортида қайта тириладиган кунларигача тўсиқ бордир'}, {'chapter': 23, 'verse': 101, 'text': 'فَإِذَا نُفِخَ فِي ٱلصُّورِ فَلَآ أَنسَابَ بَيۡنَهُمۡ يَوۡمَئِذٖ وَلَا يَتَسَآءَلُونَ\nБас, қачонки сурга пуфланганда, у кунда ораларида на насаб қолур ва на бир-бирларини суриштирурлар'}, {'chapter': 23, 'verse': 102, 'text': 'فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nКимнинг мезонлари оғир келса, бас, ана ўшалар ўзлари нажот топгувчилардир'}, {'chapter': 23, 'verse': 103, 'text': 'وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فِي جَهَنَّمَ خَٰلِدُونَ\nКимнинг мезони енгил келса, бас, ана ўшалар ўзларига зиён қилибдилар. Жаҳаннамда абадий қолгувчи бўлибдилар'}, {'chapter': 23, 'verse': 104, 'text': 'تَلۡفَحُ وُجُوهَهُمُ ٱلنَّارُ وَهُمۡ فِيهَا كَٰلِحُونَ\nЮзларини олов куйдирур. Улар унда бадбашара ҳолда бўлурлар'}, {'chapter': 23, 'verse': 105, 'text': 'أَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَكُنتُم بِهَا تُكَذِّبُونَ\nСизларга оятларим тиловат қилинган эмасмиди?! Бас, сиз уларни ёлғонга чиқарган эмасмидингиз'}, {'chapter': 23, 'verse': 106, 'text': 'قَالُواْ رَبَّنَا غَلَبَتۡ عَلَيۡنَا شِقۡوَتُنَا وَكُنَّا قَوۡمٗا ضَآلِّينَ\nУлар: «Эй Роббимиз, бадбахтлигимиз ўзимиздан устун келиб, залолатга кетган қавм бўлган эканмиз'}, {'chapter': 23, 'verse': 107, 'text': 'رَبَّنَآ أَخۡرِجۡنَا مِنۡهَا فَإِنۡ عُدۡنَا فَإِنَّا ظَٰلِمُونَ\nЭй Роббимиз, бизни бундан чиқаргин, агар яна (эски ҳолимизга) қайтсак, бас, албатта, золимлардан бўлурмиз», дерлар'}, {'chapter': 23, 'verse': 108, 'text': 'قَالَ ٱخۡسَـُٔواْ فِيهَا وَلَا تُكَلِّمُونِ\nУ зот: «У(ер)да хор бўлиб қолаверинг ва Менга гапирманг'}, {'chapter': 23, 'verse': 109, 'text': 'إِنَّهُۥ كَانَ فَرِيقٞ مِّنۡ عِبَادِي يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَا وَأَنتَ خَيۡرُ ٱلرَّـٰحِمِينَ\nАниқки, Менинг бандаларимдан бир гуруҳи: «Эй Роббимиз, биз иймон келтирдик. Бизни мағфират қилгин. Бизга раҳим қилгин. Сенинг Ўзинг раҳим қилгувчиларнинг яхшисисан», деган эдилар'}, {'chapter': 23, 'verse': 110, 'text': 'فَٱتَّخَذۡتُمُوهُمۡ سِخۡرِيًّا حَتَّىٰٓ أَنسَوۡكُمۡ ذِكۡرِي وَكُنتُم مِّنۡهُمۡ تَضۡحَكُونَ\nБас, сиз уларни масхара қилдингиз. Ҳаттоки, у(иш) сизга Менинг зикримни унуттирди. Сиз улардан кулар эдингиз'}, {'chapter': 23, 'verse': 111, 'text': 'إِنِّي جَزَيۡتُهُمُ ٱلۡيَوۡمَ بِمَا صَبَرُوٓاْ أَنَّهُمۡ هُمُ ٱلۡفَآئِزُونَ\nАлбатта, Мен бугун уларни сабр қилганлари учун мукофотладим. Албатта, улар ўзлари ютуққа эришгувчилардир'}, {'chapter': 23, 'verse': 112, 'text': 'قَٰلَ كَمۡ لَبِثۡتُمۡ فِي ٱلۡأَرۡضِ عَدَدَ سِنِينَ\nУ зот: «Ер юзида неча адад йил қолдингиз?» деди'}, {'chapter': 23, 'verse': 113, 'text': 'قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖ فَسۡـَٔلِ ٱلۡعَآدِّينَ\nУлар: «Бир кун ёки куннинг баъзисича қолдик. Ҳисобловчилардан сўра», дедилар'}, {'chapter': 23, 'verse': 114, 'text': 'قَٰلَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗاۖ لَّوۡ أَنَّكُمۡ كُنتُمۡ تَعۡلَمُونَ\nУ зот: «Агар билсангиз, жуда оз қолдингиз, холос», деди'}, {'chapter': 23, 'verse': 115, 'text': 'أَفَحَسِبۡتُمۡ أَنَّمَا خَلَقۡنَٰكُمۡ عَبَثٗا وَأَنَّكُمۡ إِلَيۡنَا لَا تُرۡجَعُونَ\nНаҳотки, Бизнинг сизни яратишимиз беҳуда бўлган ва сиз Бизга қайтарилмассиз, деб ҳисобласангиз'}, {'chapter': 23, 'verse': 116, 'text': 'فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۖ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡكَرِيمِ\nБас, ҳақ подшоҳ Аллоҳ юксакдир. Ҳеч бир илоҳ йўқ. Магар У — карамли аршнинг Робби бор'}, {'chapter': 23, 'verse': 117, 'text': 'وَمَن يَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ لَا بُرۡهَٰنَ لَهُۥ بِهِۦ فَإِنَّمَا حِسَابُهُۥ عِندَ رَبِّهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ\nКим Аллоҳ билан бирга бошқа илоҳга илтижо қилса, бу хусусда ҳеч бир далили йўқдир. Бас, унинг ҳисоби фақат Роббининг ҳузуридадир. Албатта, кофирлар нажот топмаслар'}, {'chapter': 23, 'verse': 118, 'text': 'وَقُل رَّبِّ ٱغۡفِرۡ وَٱرۡحَمۡ وَأَنتَ خَيۡرُ ٱلرَّـٰحِمِينَ\nСен: «Эй Роббим, мағфират қил, раҳим қил. Ўзинг раҳим қилгувчиларнинг яхшисисан», деб айт'}], '24': [{'chapter': 24, 'verse': 1, 'text': 'سُورَةٌ أَنزَلۡنَٰهَا وَفَرَضۡنَٰهَا وَأَنزَلۡنَا فِيهَآ ءَايَٰتِۭ بَيِّنَٰتٖ لَّعَلَّكُمۡ تَذَكَّرُونَ\n(Бу бир) сурадир. Биз уни нозил қилдик. Биз уни фарз қилдик. Биз унда очиқ-ойдин оятларни нозил қилдик. Шоядки, эсласангиз'}, {'chapter': 24, 'verse': 2, 'text': 'ٱلزَّانِيَةُ وَٱلزَّانِي فَٱجۡلِدُواْ كُلَّ وَٰحِدٖ مِّنۡهُمَا مِاْئَةَ جَلۡدَةٖۖ وَلَا تَأۡخُذۡكُم بِهِمَا رَأۡفَةٞ فِي دِينِ ٱللَّهِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ وَلۡيَشۡهَدۡ عَذَابَهُمَا طَآئِفَةٞ مِّنَ ٱلۡمُؤۡمِنِينَ\nЗинокор аёл ва зинокор эркак, икковларидан ҳар бирини юз даррадан уринг. Агар сиз Аллоҳга ва охират кунига иймон келтирадиган бўлсангиз, сизларни уларга нисбатан Аллоҳнинг динида шафқат тутмасин. Ва уларнинг азобига бир тоифа мўминлар гувоҳ бўлсинлар'}, {'chapter': 24, 'verse': 3, 'text': 'ٱلزَّانِي لَا يَنكِحُ إِلَّا زَانِيَةً أَوۡ مُشۡرِكَةٗ وَٱلزَّانِيَةُ لَا يَنكِحُهَآ إِلَّا زَانٍ أَوۡ مُشۡرِكٞۚ وَحُرِّمَ ذَٰلِكَ عَلَى ٱلۡمُؤۡمِنِينَ\nЗинокор эркак фақат зинокор ёки мушрика аёлга никоҳланур. Зинокор аёлга фақат зинокор ёки мушрик эркак никоҳланур. Бу, мўминларга ҳаром қилинмишдир'}, {'chapter': 24, 'verse': 4, 'text': 'وَٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ثُمَّ لَمۡ يَأۡتُواْ بِأَرۡبَعَةِ شُهَدَآءَ فَٱجۡلِدُوهُمۡ ثَمَٰنِينَ جَلۡدَةٗ وَلَا تَقۡبَلُواْ لَهُمۡ شَهَٰدَةً أَبَدٗاۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nПокиза аёлларга (бўҳтон) тоши отиб, сўнгра тўртта гувоҳ келтира олмаганларни саксон дарра уринглар ва уларнинг гувоҳлигини абадий қабул қилманглар. Ана ўшалар фосиқлардир'}, {'chapter': 24, 'verse': 5, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nМагар бундан кейин тавба қилиб, ўзини ислоҳ қилганлар (мустаснодир). Зеро, Аллоҳ ўта мағфиратли ва ўта раҳимлидир'}, {'chapter': 24, 'verse': 6, 'text': 'وَٱلَّذِينَ يَرۡمُونَ أَزۡوَٰجَهُمۡ وَلَمۡ يَكُن لَّهُمۡ شُهَدَآءُ إِلَّآ أَنفُسُهُمۡ فَشَهَٰدَةُ أَحَدِهِمۡ أَرۡبَعُ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِينَ\nЎз жуфтларига (бўҳтон) тоши отиб, фақат ўзларидан бошқа гувоҳлари бўлмаганлар эса, бас, улардан ҳар бирининг гувоҳлиги Аллоҳнинг номи ила тўрт марта, албатта, у ростгўйлардан эканлиги ҳақида шоҳидлик беришдир'}, {'chapter': 24, 'verse': 7, 'text': 'وَٱلۡخَٰمِسَةُ أَنَّ لَعۡنَتَ ٱللَّهِ عَلَيۡهِ إِن كَانَ مِنَ ٱلۡكَٰذِبِينَ\nБешинчисида эса, агар ёлғончилардан бўлса, унга Аллоҳнинг лаънати бўлишини (айтишликдир)'}, {'chapter': 24, 'verse': 8, 'text': 'وَيَدۡرَؤُاْ عَنۡهَا ٱلۡعَذَابَ أَن تَشۡهَدَ أَرۡبَعَ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلۡكَٰذِبِينَ\nВа у(аёл)дан азобни қайтарадиган нарса Аллоҳнинг номи ила тўрт марта, албатта, у(эр) ёлғончилардандир, деб шоҳидлик беришидир'}, {'chapter': 24, 'verse': 9, 'text': 'وَٱلۡخَٰمِسَةَ أَنَّ غَضَبَ ٱللَّهِ عَلَيۡهَآ إِن كَانَ مِنَ ٱلصَّـٰدِقِينَ\nБешинчисида эса, агар у(эр) ростгўйлардан бўлса, ўзи(аёл)га Аллоҳнинг ғазаби бўлишини (айтишликдир)'}, {'chapter': 24, 'verse': 10, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ تَوَّابٌ حَكِيمٌ\nАгар сизларга Аллоҳнинг фазли ва марҳамати бўлмаганида... Албатта, Аллоҳ тавбаларни кўплаб қабул қилгувчи ва ўта ҳикматли зот бўлмаганида эди'}, {'chapter': 24, 'verse': 11, 'text': 'إِنَّ ٱلَّذِينَ جَآءُو بِٱلۡإِفۡكِ عُصۡبَةٞ مِّنكُمۡۚ لَا تَحۡسَبُوهُ شَرّٗا لَّكُمۖ بَلۡ هُوَ خَيۡرٞ لَّكُمۡۚ لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُم مَّا ٱكۡتَسَبَ مِنَ ٱلۡإِثۡمِۚ وَٱلَّذِي تَوَلَّىٰ كِبۡرَهُۥ مِنۡهُمۡ لَهُۥ عَذَابٌ عَظِيمٞ\nАлбатта, ифкни келтирганлар ўзингиздан бўлган бир усба (тўда)дир. Уни ўзингизга ёмонлик деб ҳисобламанг. Аксинча, у сиз учун яхшиликдир. Улардан ҳар бир киши учун ўзи қилган касбнинг гуноҳи бордир. Улардан (гуноҳнинг) каттасини кўтарган кимсага буюк азоб бордир'}, {'chapter': 24, 'verse': 12, 'text': 'لَّوۡلَآ إِذۡ سَمِعۡتُمُوهُ ظَنَّ ٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بِأَنفُسِهِمۡ خَيۡرٗا وَقَالُواْ هَٰذَآ إِفۡكٞ مُّبِينٞ\nУ(ифк)ни эшитган вақтингизда мўминлар ва мўминалар ўзлари ҳақида яхши гумонга бориб: «Бу очиқ-ойдин ифк-ку!» десалар бўлмасмиди'}, {'chapter': 24, 'verse': 13, 'text': 'لَّوۡلَا جَآءُو عَلَيۡهِ بِأَرۡبَعَةِ شُهَدَآءَۚ فَإِذۡ لَمۡ يَأۡتُواْ بِٱلشُّهَدَآءِ فَأُوْلَـٰٓئِكَ عِندَ ٱللَّهِ هُمُ ٱلۡكَٰذِبُونَ\nУ(ифкчи)лар ўша(ифк)га тўртта гувоҳ келтирсалар бўлмасмиди?! Бас, гувоҳларни келтира олмаган чоғда ана ўшалар ўзлари Аллоҳнинг ҳузурида ёлғончидирлар'}, {'chapter': 24, 'verse': 14, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ لَمَسَّكُمۡ فِي مَآ أَفَضۡتُمۡ فِيهِ عَذَابٌ عَظِيمٌ\nАгар дунёю охиратда сизларга Аллоҳнинг фазли, марҳамати бўлмаганида, ўзингиз ичига шўнғиган нарса учун сизни, албатта, буюк азоб тутар эди'}, {'chapter': 24, 'verse': 15, 'text': 'إِذۡ تَلَقَّوۡنَهُۥ بِأَلۡسِنَتِكُمۡ وَتَقُولُونَ بِأَفۡوَاهِكُم مَّا لَيۡسَ لَكُم بِهِۦ عِلۡمٞ وَتَحۡسَبُونَهُۥ هَيِّنٗا وَهُوَ عِندَ ٱللَّهِ عَظِيمٞ\nЎшанда сиз у(ифк)ни тилларингиз ила илиб олар, оғзингиз ила ўзингиз билмаган нарсани гапирар эдингиз ва у (ифк)ни ўзингизга енгил санардингиз. Ҳолбуки, у Аллоҳнинг наздида буюкдир'}, {'chapter': 24, 'verse': 16, 'text': 'وَلَوۡلَآ إِذۡ سَمِعۡتُمُوهُ قُلۡتُم مَّا يَكُونُ لَنَآ أَن نَّتَكَلَّمَ بِهَٰذَا سُبۡحَٰنَكَ هَٰذَا بُهۡتَٰنٌ عَظِيمٞ\nУни эшитган чоғингизда: «Бизга буни гапирмоғимиз тўғри келмас. Ўзинг поксан! Бу катта бўҳтондир!» десангиз бўлмасмиди'}, {'chapter': 24, 'verse': 17, 'text': 'يَعِظُكُمُ ٱللَّهُ أَن تَعُودُواْ لِمِثۡلِهِۦٓ أَبَدًا إِن كُنتُم مُّؤۡمِنِينَ\nАгар мўмин бўлсангиз, ҳаргиз у(ифк)га ўхшаш нарсага қайтмаслигингизни Аллоҳ сизга ваъз-насиҳат қилур'}, {'chapter': 24, 'verse': 18, 'text': 'وَيُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nАллоҳ сизга оятларни баён қилур. Ва Аллоҳ ўта билгувчи, ўта ҳикматли Зотдир'}, {'chapter': 24, 'verse': 19, 'text': 'إِنَّ ٱلَّذِينَ يُحِبُّونَ أَن تَشِيعَ ٱلۡفَٰحِشَةُ فِي ٱلَّذِينَ ءَامَنُواْ لَهُمۡ عَذَابٌ أَلِيمٞ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nАлбатта, иймон келтирганлар ичида фоҳиша тарқалишини яхши кўрадиганларга бу дунёю охиратда аламли азоб бордир. Аллоҳ биладир, сизлар билмассизлар'}, {'chapter': 24, 'verse': 20, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ رَءُوفٞ رَّحِيمٞ\nАгар сизларга Аллоҳнинг фазли, марҳамати бўлмаганида... Ва, албатта, Аллоҳ ўта шафқатли ва раҳимли бўлмаганида'}, {'chapter': 24, 'verse': 21, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ وَمَن يَتَّبِعۡ خُطُوَٰتِ ٱلشَّيۡطَٰنِ فَإِنَّهُۥ يَأۡمُرُ بِٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۚ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ مَا زَكَىٰ مِنكُم مِّنۡ أَحَدٍ أَبَدٗا وَلَٰكِنَّ ٱللَّهَ يُزَكِّي مَن يَشَآءُۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nЭй, иймон келтирганлар! Шайтоннинг изидан эргашманг. Ким шайтоннинг изидан эргашса, бас, албатта, у фоҳиша ва мункарга буюрадир. Агар сизларга Аллоҳнинг фазли ва марҳамати бўлмаганида, абадул-абад ичингиздан бирор киши поклана олмас эди. Лекин Аллоҳ Ўзи хоҳлаган кишини поклар. Аллоҳ ўта эшитгувчи, ўта билгувчи Зотдир'}, {'chapter': 24, 'verse': 22, 'text': 'وَلَا يَأۡتَلِ أُوْلُواْ ٱلۡفَضۡلِ مِنكُمۡ وَٱلسَّعَةِ أَن يُؤۡتُوٓاْ أُوْلِي ٱلۡقُرۡبَىٰ وَٱلۡمَسَٰكِينَ وَٱلۡمُهَٰجِرِينَ فِي سَبِيلِ ٱللَّهِۖ وَلۡيَعۡفُواْ وَلۡيَصۡفَحُوٓاْۗ أَلَا تُحِبُّونَ أَن يَغۡفِرَ ٱللَّهُ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٌ\nСизлардан фазл ва бойлик эгаси бўлганлар қариндошларга, мискинларга ва Аллоҳнинг йўлида муҳожир бўлганларга (нафақа) бермасликка қасам ичмасинлар, бас, афв этсинлар, ўтиб юборсинлар. Аллоҳ сизларни мағфират қилишини хуш кўрмайсизларми?! Аллоҳ ўта мағфиратли, ўта раҳимли Зотдир'}, {'chapter': 24, 'verse': 23, 'text': 'إِنَّ ٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ٱلۡغَٰفِلَٰتِ ٱلۡمُؤۡمِنَٰتِ لُعِنُواْ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَلَهُمۡ عَذَابٌ عَظِيمٞ\nАлбатта, покиза, ғофила ва мўмина аёлларга (бўҳтон) тоши отганлар бу дунёю охиратда лаънатланурлар. Уларга катта азоб бордур'}, {'chapter': 24, 'verse': 24, 'text': 'يَوۡمَ تَشۡهَدُ عَلَيۡهِمۡ أَلۡسِنَتُهُمۡ وَأَيۡدِيهِمۡ وَأَرۡجُلُهُم بِمَا كَانُواْ يَعۡمَلُونَ\nУ кунда тиллари, қўллари ва оёқлари қилиб юрган амаллари ҳақида уларга қарши гувоҳлик берур'}, {'chapter': 24, 'verse': 25, 'text': 'يَوۡمَئِذٖ يُوَفِّيهِمُ ٱللَّهُ دِينَهُمُ ٱلۡحَقَّ وَيَعۡلَمُونَ أَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ ٱلۡمُبِينُ\nЎша кунда Аллоҳ уларнинг ҳақ жазоларини тўлиқ берур ва улар Аллоҳнинг Ўзи очиқ ҳақ эканини билурлар'}, {'chapter': 24, 'verse': 26, 'text': 'ٱلۡخَبِيثَٰتُ لِلۡخَبِيثِينَ وَٱلۡخَبِيثُونَ لِلۡخَبِيثَٰتِۖ وَٱلطَّيِّبَٰتُ لِلطَّيِّبِينَ وَٱلطَّيِّبُونَ لِلطَّيِّبَٰتِۚ أُوْلَـٰٓئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَۖ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nНопок хотинлар нопок эркаклар учундир. Нопок эркаклар нопок хотинлар учундир. Покиза аёллар покиза эркаклар учундир. Покиза эркаклар покиза аёллар учундир. Ана ўшалар у(бўҳтончи)лар айтаётган нарсалардан поклангандирлар. Уларга мағфират ва гўзал ризқ бордир'}, {'chapter': 24, 'verse': 27, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتًا غَيۡرَ بُيُوتِكُمۡ حَتَّىٰ تَسۡتَأۡنِسُواْ وَتُسَلِّمُواْ عَلَىٰٓ أَهۡلِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ\nЭй, иймон келтирганлар! Ўз уйларингиздан бошқа уйларга то изн сўрамагунингизча ва уларнинг аҳлига салом бермагунингизча, кирманг. Ана шундай қилмоғингиз сиз учун яхшидир, шоядки, эсласангиз'}, {'chapter': 24, 'verse': 28, 'text': 'فَإِن لَّمۡ تَجِدُواْ فِيهَآ أَحَدٗا فَلَا تَدۡخُلُوهَا حَتَّىٰ يُؤۡذَنَ لَكُمۡۖ وَإِن قِيلَ لَكُمُ ٱرۡجِعُواْ فَٱرۡجِعُواْۖ هُوَ أَزۡكَىٰ لَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ\nБас, агар у(уй)ларда бирортани топмасангиз, то сизга изн берилмагунча, уларга кирманг. Агар сизга, қайтинг, дейилса, бас, қайтинг, ўша сиз учун покдир. Аллоҳ нима амал қилаётганингизни ўта билгувчидир'}, {'chapter': 24, 'verse': 29, 'text': 'لَّيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَدۡخُلُواْ بُيُوتًا غَيۡرَ مَسۡكُونَةٖ فِيهَا مَتَٰعٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ\nМаскан бўлмаган, сиз учун фойда бор уйларга (изнсиз) киришингизда гуноҳ йўқдир. Аллоҳ нимани ошкор қилаётганингизни ва нимани яширин қилаётганингизни биладир'}, {'chapter': 24, 'verse': 30, 'text': 'قُل لِّلۡمُؤۡمِنِينَ يَغُضُّواْ مِنۡ أَبۡصَٰرِهِمۡ وَيَحۡفَظُواْ فُرُوجَهُمۡۚ ذَٰلِكَ أَزۡكَىٰ لَهُمۡۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا يَصۡنَعُونَ\nСен мўминларга айт, кўзларини тийсинлар ва фаржларини сақласинлар. Ана шу улар учун покдир. Албатта, Аллоҳ нима ҳунар қилаётганларидан хабардордир'}, {'chapter': 24, 'verse': 31, 'text': 'وَقُل لِّلۡمُؤۡمِنَٰتِ يَغۡضُضۡنَ مِنۡ أَبۡصَٰرِهِنَّ وَيَحۡفَظۡنَ فُرُوجَهُنَّ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنۡهَاۖ وَلۡيَضۡرِبۡنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّۖ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوۡ ءَابَآئِهِنَّ أَوۡ ءَابَآءِ بُعُولَتِهِنَّ أَوۡ أَبۡنَآئِهِنَّ أَوۡ أَبۡنَآءِ بُعُولَتِهِنَّ أَوۡ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ أَخَوَٰتِهِنَّ أَوۡ نِسَآئِهِنَّ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُنَّ أَوِ ٱلتَّـٰبِعِينَ غَيۡرِ أُوْلِي ٱلۡإِرۡبَةِ مِنَ ٱلرِّجَالِ أَوِ ٱلطِّفۡلِ ٱلَّذِينَ لَمۡ يَظۡهَرُواْ عَلَىٰ عَوۡرَٰتِ ٱلنِّسَآءِۖ وَلَا يَضۡرِبۡنَ بِأَرۡجُلِهِنَّ لِيُعۡلَمَ مَا يُخۡفِينَ مِن زِينَتِهِنَّۚ وَتُوبُوٓاْ إِلَى ٱللَّهِ جَمِيعًا أَيُّهَ ٱلۡمُؤۡمِنُونَ لَعَلَّكُمۡ تُفۡلِحُونَ\nСен мўминаларга айт: «Кўзларини тийсинлар, фаржларини сақласинлар ва зийнатларини кўрсатмасинлар, магар зоҳир бўлган зийнатлар бўлса (майли). Рўмолларини кўксиларига тўсиб юрсинлар. Зийнатларини кўрсатмасинлар, магар эрларига ё оталарига ё эрларининг оталарига ё ўғилларига ё эрларининг ўғилларига ё ака-укаларига ё ака-укаларининг ўғилларига ё опа-сингилларининг ўғилларига ё аёлларига ё ўз қўлларида мулк бўлганларга ё (аёлларга) беҳожат эркак хизматчиларга ё аёллар авратининг фарқига бормаган ёш болаларга (бўлса, майли). Махфий зийнатларини билдириш учун оёқларини (ерга) урмасинлар. Аллоҳга барчангиз тавба қилинг, эй мўминлар! Шоядки, нажот топсангизлар»'}, {'chapter': 24, 'verse': 32, 'text': 'وَأَنكِحُواْ ٱلۡأَيَٰمَىٰ مِنكُمۡ وَٱلصَّـٰلِحِينَ مِنۡ عِبَادِكُمۡ وَإِمَآئِكُمۡۚ إِن يَكُونُواْ فُقَرَآءَ يُغۡنِهِمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nОраларингиздаги никоҳсизларни ва қулу чўриларингиздан солиҳларини никоҳлаб қўйинг. Агар фақир бўлсалар, Аллоҳ уларни Ўз фазлидан бой қилур. Аллоҳ (фазли) кенг, ўта билгувчи Зотдир'}, {'chapter': 24, 'verse': 33, 'text': 'وَلۡيَسۡتَعۡفِفِ ٱلَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغۡنِيَهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱلَّذِينَ يَبۡتَغُونَ ٱلۡكِتَٰبَ مِمَّا مَلَكَتۡ أَيۡمَٰنُكُمۡ فَكَاتِبُوهُمۡ إِنۡ عَلِمۡتُمۡ فِيهِمۡ خَيۡرٗاۖ وَءَاتُوهُم مِّن مَّالِ ٱللَّهِ ٱلَّذِيٓ ءَاتَىٰكُمۡۚ وَلَا تُكۡرِهُواْ فَتَيَٰتِكُمۡ عَلَى ٱلۡبِغَآءِ إِنۡ أَرَدۡنَ تَحَصُّنٗا لِّتَبۡتَغُواْ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَن يُكۡرِههُّنَّ فَإِنَّ ٱللَّهَ مِنۢ بَعۡدِ إِكۡرَٰهِهِنَّ غَفُورٞ رَّحِيمٞ\nНикоҳ(имкон)ни топа олмаганлар, то Аллоҳ уларни Ўз фазлидан бой қилгунича, иффатларини сақласинлар. Қўлларингизда мулк бўлганлардан (озодлик) васиқасини истайдиганлари бўлса, бас, уларда яхшилик борлигини билсангиз, улар ила васиқа ёзинг ва Аллоҳнинг сизга берган молидан уларга беринг. Ҳаёти дунёнинг ўткинчи матоҳини истаб, покликни ирода қилган (чўри) қизларингизни фоҳишаликка мажбур қилманг. Ким уларни мажбур қилган бўлса, бас, албатта, Аллоҳ улар мажбур қилганларидан кейин ўта мағфиратли ва раҳимли Зотдир'}, {'chapter': 24, 'verse': 34, 'text': 'وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ ءَايَٰتٖ مُّبَيِّنَٰتٖ وَمَثَلٗا مِّنَ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۡ وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ\nБатаҳқиқ, Биз сизларга очиқ-ойдин оятларни, сиздан олдин ўтганлардан мисолни ва тақводорлар учун ваъзни нозил қилдик'}, {'chapter': 24, 'verse': 35, 'text': '۞ٱللَّهُ نُورُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ مَثَلُ نُورِهِۦ كَمِشۡكَوٰةٖ فِيهَا مِصۡبَاحٌۖ ٱلۡمِصۡبَاحُ فِي زُجَاجَةٍۖ ٱلزُّجَاجَةُ كَأَنَّهَا كَوۡكَبٞ دُرِّيّٞ يُوقَدُ مِن شَجَرَةٖ مُّبَٰرَكَةٖ زَيۡتُونَةٖ لَّا شَرۡقِيَّةٖ وَلَا غَرۡبِيَّةٖ يَكَادُ زَيۡتُهَا يُضِيٓءُ وَلَوۡ لَمۡ تَمۡسَسۡهُ نَارٞۚ نُّورٌ عَلَىٰ نُورٖۚ يَهۡدِي ٱللَّهُ لِنُورِهِۦ مَن يَشَآءُۚ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَٰلَ لِلنَّاسِۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\nАллоҳ осмонлар ва ернинг нуридир. Унинг нурининг мисоли худди бир токча, унинг ичига чироқ қўйилган, чироқ эса, шиша ичида, шиша эса, гўё дурдан бўлган бир юлдуз бўлиб, шарқий ҳам, ғарбий ҳам бўлмаган муборак зайтун дарахтидан ёқиладир. Унинг мойи ўзига олов тегмаса ҳам, ёритиб юборай дейдир. (Бу) нур устига нурдир. Аллоҳ хоҳлаган одамни Ўз нурига ҳидоят қилур. Аллоҳ одамларга мисолларни келтирур. Аллоҳ ҳар бир нарсани ўта билгувчидир'}, {'chapter': 24, 'verse': 36, 'text': 'فِي بُيُوتٍ أَذِنَ ٱللَّهُ أَن تُرۡفَعَ وَيُذۡكَرَ فِيهَا ٱسۡمُهُۥ يُسَبِّحُ لَهُۥ فِيهَا بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ\n(У) бир уйлардаки, Аллоҳ уларнинг кўтарилишига ва уларда Ўз исми зикр қилинишига изн бергандир. Уларда Унга эртаю кеч тасбиҳ айтурлар'}, {'chapter': 24, 'verse': 37, 'text': 'رِجَالٞ لَّا تُلۡهِيهِمۡ تِجَٰرَةٞ وَلَا بَيۡعٌ عَن ذِكۡرِ ٱللَّهِ وَإِقَامِ ٱلصَّلَوٰةِ وَإِيتَآءِ ٱلزَّكَوٰةِ يَخَافُونَ يَوۡمٗا تَتَقَلَّبُ فِيهِ ٱلۡقُلُوبُ وَٱلۡأَبۡصَٰرُ\nБир кишиларки, уларни тижорат ҳам, олди-сотди ҳам Аллоҳнинг зикридан, намозни тўкис адо этишдан ва закот беришдан машғул қила олмас. Улар қалблар ва кўзлар изтиробга тушадиган кундан қўрқарлар'}, {'chapter': 24, 'verse': 38, 'text': 'لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا عَمِلُواْ وَيَزِيدَهُم مِّن فَضۡلِهِۦۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ\nАллоҳ уларни қилган амалларининг энг гўзали ила мукофотлаши ва фазли карамидан зиёда қилиб бериши учундир. Аллоҳ хоҳлаган кишига беҳисоб ризқ берур'}, {'chapter': 24, 'verse': 39, 'text': 'وَٱلَّذِينَ كَفَرُوٓاْ أَعۡمَٰلُهُمۡ كَسَرَابِۭ بِقِيعَةٖ يَحۡسَبُهُ ٱلظَّمۡـَٔانُ مَآءً حَتَّىٰٓ إِذَا جَآءَهُۥ لَمۡ يَجِدۡهُ شَيۡـٔٗا وَوَجَدَ ٱللَّهَ عِندَهُۥ فَوَفَّىٰهُ حِسَابَهُۥۗ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ\nКуфр келтирганларнинг амаллари саҳродаги саробга ўхшар. Чанқоқ одам уни сув деб ҳисоблар. Қачонки, унинг олдига келса, ҳеч нарсани топмас. Ва у олдида Аллоҳни топур. Бас, У зот унинг ҳисобини тўлиқ қилур. Аллоҳ ўта тез ҳисоб қилгувчидир'}, {'chapter': 24, 'verse': 40, 'text': 'أَوۡ كَظُلُمَٰتٖ فِي بَحۡرٖ لُّجِّيّٖ يَغۡشَىٰهُ مَوۡجٞ مِّن فَوۡقِهِۦ مَوۡجٞ مِّن فَوۡقِهِۦ سَحَابٞۚ ظُلُمَٰتُۢ بَعۡضُهَا فَوۡقَ بَعۡضٍ إِذَآ أَخۡرَجَ يَدَهُۥ لَمۡ يَكَدۡ يَرَىٰهَاۗ وَمَن لَّمۡ يَجۡعَلِ ٱللَّهُ لَهُۥ نُورٗا فَمَا لَهُۥ مِن نُّورٍ\nЁки худди қаърсиз денгиздаги зулматларга ўхшар. Унинг устидан мавж қоплагандир, у(мавж)нинг ҳам устидан мавж ва уни эса, булут (қоплагандир). Бир-бирининг устидаги зулматлардир. У қўлини чиқарса, кўра олмас. Кимгаки Аллоҳ нур бермаса, унинг учун нур бўлмас'}, {'chapter': 24, 'verse': 41, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُسَبِّحُ لَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلطَّيۡرُ صَـٰٓفَّـٰتٖۖ كُلّٞ قَدۡ عَلِمَ صَلَاتَهُۥ وَتَسۡبِيحَهُۥۗ وَٱللَّهُ عَلِيمُۢ بِمَا يَفۡعَلُونَ\nАллоҳга осмонлар ва ердаги бор жонзот ва саф тортган ҳолидаги қушлар тасбиҳ айтишини ҳам кўрмайсанми?! Ҳар бири ўз дуоси ва тасбиҳини, батаҳқиқ, билмишдир. Аллоҳ нима амал қилишларини ўта билгувчидир'}, {'chapter': 24, 'verse': 42, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ\nОсмонлар ва ернинг мулки Аллоҳникидир ва қайтиш ҳам фақат Аллоҳгадир'}, {'chapter': 24, 'verse': 43, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُزۡجِي سَحَابٗا ثُمَّ يُؤَلِّفُ بَيۡنَهُۥ ثُمَّ يَجۡعَلُهُۥ رُكَامٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦ وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مِن جِبَالٖ فِيهَا مِنۢ بَرَدٖ فَيُصِيبُ بِهِۦ مَن يَشَآءُ وَيَصۡرِفُهُۥ عَن مَّن يَشَآءُۖ يَكَادُ سَنَا بَرۡقِهِۦ يَذۡهَبُ بِٱلۡأَبۡصَٰرِ\nАллоҳ булутларни ҳайдашини, сўнгра бирга тўплашини, сўнгра уйиб қўйишини кўрмаяпсанми?! Бас, унинг орасидан ёмғир чиқаётганини кўрурсан. У зот осмондан, ундаги тоғлардан дўл тушириб, у билан Ўзи хоҳлаган кишиларга мусийбат етказур ва уни Ўзи хоҳлаган кишилардан буриб юборур. Унинг чақмоғининг ярқираши кўзларни кетказгудек бўлур'}, {'chapter': 24, 'verse': 44, 'text': 'يُقَلِّبُ ٱللَّهُ ٱلَّيۡلَ وَٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ\nАллоҳ кеча ва кундузни айлантириб турур. Албатта, бунда кўзи очиқлар учун ибрат бордир'}, {'chapter': 24, 'verse': 45, 'text': 'وَٱللَّهُ خَلَقَ كُلَّ دَآبَّةٖ مِّن مَّآءٖۖ فَمِنۡهُم مَّن يَمۡشِي عَلَىٰ بَطۡنِهِۦ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰ رِجۡلَيۡنِ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰٓ أَرۡبَعٖۚ يَخۡلُقُ ٱللَّهُ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАллоҳ ҳамма жониворни сувдан яратди. Бас, улардан баъзилари қорни билан юрадир, баъзилари икки оёқ билан юрадир ва баъзилари тўрт (оёқ) билан. Аллоҳ Ўзи хоҳлаган нарсани яратур. Албатта, Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 24, 'verse': 46, 'text': 'لَّقَدۡ أَنزَلۡنَآ ءَايَٰتٖ مُّبَيِّنَٰتٖۚ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nБатаҳқиқ, Биз очиқ-ойдин баён қилгувчи оятларни туширдик. Аллоҳ Ўзи хоҳлаган кимсани тўғри йўлга ҳидоят қиладир'}, {'chapter': 24, 'verse': 47, 'text': 'وَيَقُولُونَ ءَامَنَّا بِٱللَّهِ وَبِٱلرَّسُولِ وَأَطَعۡنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُم مِّنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَـٰٓئِكَ بِٱلۡمُؤۡمِنِينَ\nУлар: «Аллоҳга ва Пайғамбарга иймон келтирдик ва итоат қилдик», дерлар. Сўнгра улардан бир гуруҳи юз ўгириб кетурлар. Ана ўшалар мўмин эмаслар'}, {'chapter': 24, 'verse': 48, 'text': 'وَإِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ إِذَا فَرِيقٞ مِّنۡهُم مُّعۡرِضُونَ\nУларни қачонки Аллоҳга ва Унинг Пайғамбарига ораларида ҳукм чиқариш учун чорланса, баногоҳ, улардан бир гуруҳи юз ўгиргувчилардир'}, {'chapter': 24, 'verse': 49, 'text': 'وَإِن يَكُن لَّهُمُ ٱلۡحَقُّ يَأۡتُوٓاْ إِلَيۡهِ مُذۡعِنِينَ\nАгар ҳақ улар (тарафда) бўлса, у(Пайғамбар)га бўйин эгиб келурлар'}, {'chapter': 24, 'verse': 50, 'text': 'أَفِي قُلُوبِهِم مَّرَضٌ أَمِ ٱرۡتَابُوٓاْ أَمۡ يَخَافُونَ أَن يَحِيفَ ٱللَّهُ عَلَيۡهِمۡ وَرَسُولُهُۥۚ بَلۡ أُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nУларнинг қалбларида мараз борми?! Ёки шубҳа қилдиларми?! Ёхуд Аллоҳ ва Унинг Пайғамбари уларга зулм қилишидан қўрқадиларми?! Йўқ! Уларнинг ўзлари золимлардир'}, {'chapter': 24, 'verse': 51, 'text': 'إِنَّمَا كَانَ قَوۡلَ ٱلۡمُؤۡمِنِينَ إِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ أَن يَقُولُواْ سَمِعۡنَا وَأَطَعۡنَاۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nАлбатта, мўминларнинг Аллоҳга ва Унинг Пайғамбарига ораларида ҳукм чиқариш учун чорланганларидаги гаплари: «Эшитдик ва итоат қилдик», демоқларидир. Ана ўшаларнинг ўзларигина нажот топгувчилардир'}, {'chapter': 24, 'verse': 52, 'text': 'وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ وَيَخۡشَ ٱللَّهَ وَيَتَّقۡهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ\nКим Аллоҳга ва Унинг Пайғамбарига итоат этса ҳамда Аллоҳдан қўрқиб, Унга тақво қилса, бас, ана ўшаларгина ютуққа эришгувчилардир'}, {'chapter': 24, 'verse': 53, 'text': '۞وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِنۡ أَمَرۡتَهُمۡ لَيَخۡرُجُنَّۖ قُل لَّا تُقۡسِمُواْۖ طَاعَةٞ مَّعۡرُوفَةٌۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nУлар агар амр қилсанг, албатта (жиҳодга) чиқишлари ҳақида Аллоҳ номи билан жон-жаҳдлари ила қасам ичарлар. Сен: «Қасам ичманглар! (Бу жиҳод) маълум тоатдир! Албатта, Аллоҳ нима амал қилаётганларингиздан хабардордир», деб айт'}, {'chapter': 24, 'verse': 54, 'text': 'قُلۡ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡهِ مَا حُمِّلَ وَعَلَيۡكُم مَّا حُمِّلۡتُمۡۖ وَإِن تُطِيعُوهُ تَهۡتَدُواْۚ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nСен: «Аллоҳга итоат қилинглар, Пайғамбарга итоат қилинглар. Бас, агар юз ўгириб кетсангиз, у(Пайғамбар)га ўзига юклатилган нарса, сизга ўзингизга юклатилган нарса. Агар унга итоат қилсангиз, ҳидоят топурсиз. Пайғамбарнинг зиммасида очиқ-ойдин етказишдан бошқа нарса йўқ», деб айт'}, {'chapter': 24, 'verse': 55, 'text': 'وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَيَسۡتَخۡلِفَنَّهُمۡ فِي ٱلۡأَرۡضِ كَمَا ٱسۡتَخۡلَفَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَلَيُمَكِّنَنَّ لَهُمۡ دِينَهُمُ ٱلَّذِي ٱرۡتَضَىٰ لَهُمۡ وَلَيُبَدِّلَنَّهُم مِّنۢ بَعۡدِ خَوۡفِهِمۡ أَمۡنٗاۚ يَعۡبُدُونَنِي لَا يُشۡرِكُونَ بِي شَيۡـٔٗاۚ وَمَن كَفَرَ بَعۡدَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nАллоҳ сизлардан иймон келтириб, солиҳ амалларни қилганларга уларни ер юзида худди улардан олдин ўтганларни халифа қилганидек халифа қилишни, улар учун Ўзи рози бўлган динни мустаҳкамлашни ва уларнинг хавф-хатарларидан сўнг омонликни бадал қилиб беришни ваъда қилди. Менгагина ибодат қилурлар ва Менга ҳеч нарсани ширк келтирмаслар. Шундан кейин ҳам ким куфр келтирса, бас, ана ўшалар, ўзлари фосиқлардир'}, {'chapter': 24, 'verse': 56, 'text': 'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ\nНамозни тўкис адо этинглар, закот беринглар ва Пайғамбарга итоат қилинглар. Шоядки, раҳим қилинсангиз'}, {'chapter': 24, 'verse': 57, 'text': 'لَا تَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِۚ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَلَبِئۡسَ ٱلۡمَصِيرُ\nСен куфр келтирганларни ер юзида қочиб қутула олгувчилардир, деб ҳисоблама! Уларнинг жойи дўзахдир. У нақадар ёмон борар жойдир'}, {'chapter': 24, 'verse': 58, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِيَسۡتَـٔۡذِنكُمُ ٱلَّذِينَ مَلَكَتۡ أَيۡمَٰنُكُمۡ وَٱلَّذِينَ لَمۡ يَبۡلُغُواْ ٱلۡحُلُمَ مِنكُمۡ ثَلَٰثَ مَرَّـٰتٖۚ مِّن قَبۡلِ صَلَوٰةِ ٱلۡفَجۡرِ وَحِينَ تَضَعُونَ ثِيَابَكُم مِّنَ ٱلظَّهِيرَةِ وَمِنۢ بَعۡدِ صَلَوٰةِ ٱلۡعِشَآءِۚ ثَلَٰثُ عَوۡرَٰتٖ لَّكُمۡۚ لَيۡسَ عَلَيۡكُمۡ وَلَا عَلَيۡهِمۡ جُنَاحُۢ بَعۡدَهُنَّۚ طَوَّـٰفُونَ عَلَيۡكُم بَعۡضُكُمۡ عَلَىٰ بَعۡضٖۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nЭй, иймон келтирганлар! Қўлингизда мулк бўлганлар ва ўзингиздан балоғатга етмаганлар сиздан уч вақтда изн сўрасинлар: бомдод намозидан олдин, пешинда кийимларингизни ечадиган пайтингизда ва хуфтон намозидан сўнг. (Ушбу) уч вақт сиз учун авратдир. Улардан кейин сизга ҳам, уларга ҳам гуноҳ йўқ. Улар сизнинг атрофингизда, бир-бирингизга айланиб турувчидирсиз. Аллоҳ сизларга оятларни ана шундай баён қилур. Аллоҳ ўта билгувчи, ўта ҳикматли Зотдир'}, {'chapter': 24, 'verse': 59, 'text': 'وَإِذَا بَلَغَ ٱلۡأَطۡفَٰلُ مِنكُمُ ٱلۡحُلُمَ فَلۡيَسۡتَـٔۡذِنُواْ كَمَا ٱسۡتَـٔۡذَنَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nҚачон сизнинг ёш болаларингиз балоғатга етсалар, улардан олдингилар изн сўраганидек, изн сўрасинлар. Аллоҳ Ўз оятларини сизга ана шундай баён қилур. Аллоҳ ўта билгувчи, ўта ҳикматли Зотдир'}, {'chapter': 24, 'verse': 60, 'text': 'وَٱلۡقَوَٰعِدُ مِنَ ٱلنِّسَآءِ ٱلَّـٰتِي لَا يَرۡجُونَ نِكَاحٗا فَلَيۡسَ عَلَيۡهِنَّ جُنَاحٌ أَن يَضَعۡنَ ثِيَابَهُنَّ غَيۡرَ مُتَبَرِّجَٰتِۭ بِزِينَةٖۖ وَأَن يَسۡتَعۡفِفۡنَ خَيۡرٞ لَّهُنَّۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nАёллардан (кексайиб) ўтириб қолганлари, никоҳни умид қилмайдиганларига зийнатларини кўз-кўз қилмаган ҳолда кийимларини қўйишларида гуноҳ йўқдир. Иффат талабида бўлсалар, ўзлари учун яхшидир. Аллоҳ ўта эшитгувчи, ўта билгувчи Зотдир'}, {'chapter': 24, 'verse': 61, 'text': 'لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞ وَلَا عَلَىٰٓ أَنفُسِكُمۡ أَن تَأۡكُلُواْ مِنۢ بُيُوتِكُمۡ أَوۡ بُيُوتِ ءَابَآئِكُمۡ أَوۡ بُيُوتِ أُمَّهَٰتِكُمۡ أَوۡ بُيُوتِ إِخۡوَٰنِكُمۡ أَوۡ بُيُوتِ أَخَوَٰتِكُمۡ أَوۡ بُيُوتِ أَعۡمَٰمِكُمۡ أَوۡ بُيُوتِ عَمَّـٰتِكُمۡ أَوۡ بُيُوتِ أَخۡوَٰلِكُمۡ أَوۡ بُيُوتِ خَٰلَٰتِكُمۡ أَوۡ مَا مَلَكۡتُم مَّفَاتِحَهُۥٓ أَوۡ صَدِيقِكُمۡۚ لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَأۡكُلُواْ جَمِيعًا أَوۡ أَشۡتَاتٗاۚ فَإِذَا دَخَلۡتُم بُيُوتٗا فَسَلِّمُواْ عَلَىٰٓ أَنفُسِكُمۡ تَحِيَّةٗ مِّنۡ عِندِ ٱللَّهِ مُبَٰرَكَةٗ طَيِّبَةٗۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ\nКўзи ожизга гуноҳ — танглик йўқдир. Ва, чўлоққа гуноҳ — танглик йўқдир. Ва, беморга гуноҳ — танглик йўқдир. Ва, Сизнинг ўзингизга ҳам ўз уйингиздан, ё оталарингизнинг уйларидан, ё оналарингизнинг уйларидан, ё ака-укаларингизнинг уйларидан, ё опа-сингилларингизнинг уйларидан, ё амакиларингизнинг уйларидан, ё аммаларингизнинг уйларидан, ё тоғаларингизнинг уйларидан, ё холаларингизнинг уйларидан, ё сиз калитига молик бўлган ёки дўстингиз (уйидан) емоқлигингизда (танглик) йўқдир. Сизларга жамланган ёки тарқоқ ҳолда емоқликда ҳам гуноҳ — танглик йўқдир. Уйларга кирган чоғингизда ўзларингизга Аллоҳдан бўлган покиза ва муборак табрик ила салом беринглар. Аллоҳ сизларга оятларни ана шундай баён қилур. Шоядки, ақл юритсангиз'}, {'chapter': 24, 'verse': 62, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَإِذَا كَانُواْ مَعَهُۥ عَلَىٰٓ أَمۡرٖ جَامِعٖ لَّمۡ يَذۡهَبُواْ حَتَّىٰ يَسۡتَـٔۡذِنُوهُۚ إِنَّ ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ أُوْلَـٰٓئِكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦۚ فَإِذَا ٱسۡتَـٔۡذَنُوكَ لِبَعۡضِ شَأۡنِهِمۡ فَأۡذَن لِّمَن شِئۡتَ مِنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمُ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nАлбатта, мўминлар Аллоҳга ва Унинг Расулига иймон келтирган кишилардир. Улар у(Пайғамбар) билан бирга жамловчи (муҳим) ишда бўлсалар, ундан изн сўрамасдан туриб кетиб қолмаслар. Албатта, сендан изн сўрайдиганлар, ана ўшалар Аллоҳга ва Унинг Расулига иймон келтирадиганлардир. Улар баъзи ишлари учун сендан изн сўрасалар, улардан кимга хоҳласанг, изн бер ва улар учун Аллоҳга истиғфор айт. Албатта, Аллоҳ ўта мағфиратли, ўта раҳимли Зотдир'}, {'chapter': 24, 'verse': 63, 'text': 'لَّا تَجۡعَلُواْ دُعَآءَ ٱلرَّسُولِ بَيۡنَكُمۡ كَدُعَآءِ بَعۡضِكُم بَعۡضٗاۚ قَدۡ يَعۡلَمُ ٱللَّهُ ٱلَّذِينَ يَتَسَلَّلُونَ مِنكُمۡ لِوَاذٗاۚ فَلۡيَحۡذَرِ ٱلَّذِينَ يُخَالِفُونَ عَنۡ أَمۡرِهِۦٓ أَن تُصِيبَهُمۡ فِتۡنَةٌ أَوۡ يُصِيبَهُمۡ عَذَابٌ أَلِيمٌ\nПайғамбарнинг орангиздаги даъватини бир-бирингизнинг даъватингизга ўхшатманг. Батаҳқиқ, сизлардан паналаб, аста-аста суғурилиб чиқиб кетадиганларни Аллоҳ яхши билур. Унинг ишига хилоф қиладиганлар ўзларига фитна етиши ёки аламли азоб етишидан ҳазир бўлсинлар'}, {'chapter': 24, 'verse': 64, 'text': 'أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قَدۡ يَعۡلَمُ مَآ أَنتُمۡ عَلَيۡهِ وَيَوۡمَ يُرۡجَعُونَ إِلَيۡهِ فَيُنَبِّئُهُم بِمَا عَمِلُواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ\nОгоҳ бўлинглар! Албатта, осмонлару ердаги нарсалар Аллоҳникидир. У зот сизнинг қай ҳолатда эканингизни яхши билур. Унинг Ўзига қайтариладиган кунда эса, қилган амаллари ҳақида уларга хабар берур. Аллоҳ ҳар бир нарсани ўта билгувчи Зотдир'}], '25': [{'chapter': 25, 'verse': 1, 'text': 'تَبَارَكَ ٱلَّذِي نَزَّلَ ٱلۡفُرۡقَانَ عَلَىٰ عَبۡدِهِۦ لِيَكُونَ لِلۡعَٰلَمِينَ نَذِيرًا\nОламларга огоҳлантирувчи бўлсин учун бандасига Фурқонни нозил қилган Зот баракотли, улуғдир'}, {'chapter': 25, 'verse': 2, 'text': 'ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَخَلَقَ كُلَّ شَيۡءٖ فَقَدَّرَهُۥ تَقۡدِيرٗا\nУ, осмонлару ернинг мулки Унга хос бўлган, Ўзига фарзанд тутмаган, мулкида шериги бўлмаган, ҳар бир нарсани ўлчов ила ўлчаб яратган Зотдир'}, {'chapter': 25, 'verse': 3, 'text': 'وَٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗ لَّا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ وَلَا يَمۡلِكُونَ لِأَنفُسِهِمۡ ضَرّٗا وَلَا نَفۡعٗا وَلَا يَمۡلِكُونَ مَوۡتٗا وَلَا حَيَوٰةٗ وَلَا نُشُورٗا\nУ Зотдан бошқа, ҳеч нарса ярата олмайдиган, ўзлари яратиладиган, ўзлари учун зарарга ҳам, нафга ҳам эга бўлмайдиган, ўлимга ҳам, ҳаётга ҳам, қайта тирилишга ҳам молик бўлмайдиган «худо»лар тутдилар'}, {'chapter': 25, 'verse': 4, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ إِفۡكٌ ٱفۡتَرَىٰهُ وَأَعَانَهُۥ عَلَيۡهِ قَوۡمٌ ءَاخَرُونَۖ فَقَدۡ جَآءُو ظُلۡمٗا وَزُورٗا\nКуфр келтирганлар: «Бу (Қуръон) уйдирмадан бошқа нарса эмас. Уни ўзи уйдириб олди ва бунда унга бошқа қавмлар кўмак бердилар», дедилар. Бас, шубҳасиз, зулм ва ёлғон гувоҳлик келтирдилар'}, {'chapter': 25, 'verse': 5, 'text': 'وَقَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ ٱكۡتَتَبَهَا فَهِيَ تُمۡلَىٰ عَلَيۡهِ بُكۡرَةٗ وَأَصِيلٗا\nУлар: «Бу аввалгиларнинг афсонасидир. Уларни ёздириб олган. Ҳолбуки, улар унга эртаю кеч имло қилинур», дедилар'}, {'chapter': 25, 'verse': 6, 'text': 'قُلۡ أَنزَلَهُ ٱلَّذِي يَعۡلَمُ ٱلسِّرَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ غَفُورٗا رَّحِيمٗا\nСен: «Уни осмонлару ердаги сирни биладиган Зот нозил қилди. Албатта, У мағфиратли ва раҳимли Зотдир», дегин'}, {'chapter': 25, 'verse': 7, 'text': 'وَقَالُواْ مَالِ هَٰذَا ٱلرَّسُولِ يَأۡكُلُ ٱلطَّعَامَ وَيَمۡشِي فِي ٱلۡأَسۡوَاقِ لَوۡلَآ أُنزِلَ إِلَيۡهِ مَلَكٞ فَيَكُونَ مَعَهُۥ نَذِيرًا\nУлар: «Бу не Пайғамбарки, таом еб, бозорда юрадир?! Унга бир фаришта туширилса эди, у билан огоҳлантиргувчи бўлар эди'}, {'chapter': 25, 'verse': 8, 'text': 'أَوۡ يُلۡقَىٰٓ إِلَيۡهِ كَنزٌ أَوۡ تَكُونُ لَهُۥ جَنَّةٞ يَأۡكُلُ مِنۡهَاۚ وَقَالَ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا\nЁки унга хазина ташланса ёхуд унинг боғи бўлса-ю, ундан еса», дедилар. Золимлар: «Сизлар фақат сеҳрланган кишига эргашяпсизлар, холос», дедилар'}, {'chapter': 25, 'verse': 9, 'text': 'ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَٰلَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا\nСенга қандай мисоллар келтирганига назар сол! Бас, улар йўлдан оздилар. Энди йўл топишга қодир эмаслар'}, {'chapter': 25, 'verse': 10, 'text': 'تَبَارَكَ ٱلَّذِيٓ إِن شَآءَ جَعَلَ لَكَ خَيۡرٗا مِّن ذَٰلِكَ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَيَجۡعَل لَّكَ قُصُورَۢا\nАгар хоҳласа, сенга ўшандан яхшироғини — остидан анҳорлар оқиб турадиган боғларни ва қасрларни қилиб берадиган Зот баракотли ва улуғ бўлди'}, {'chapter': 25, 'verse': 11, 'text': 'بَلۡ كَذَّبُواْ بِٱلسَّاعَةِۖ وَأَعۡتَدۡنَا لِمَن كَذَّبَ بِٱلسَّاعَةِ سَعِيرًا\nЙўқ! Улар соатни (қиёматни) ҳам ёлғонга чиқардилар. Ва Биз соатни (қиёматни) ёлғонга чиқарганларга дўзахни тайёрлаб қўйдик'}, {'chapter': 25, 'verse': 12, 'text': 'إِذَا رَأَتۡهُم مِّن مَّكَانِۭ بَعِيدٖ سَمِعُواْ لَهَا تَغَيُّظٗا وَزَفِيرٗا\nВақтики у уларни узоқ жойдан кўрганида, унинг ғазабнок қайнашини ва бўкиришини эшитурлар'}, {'chapter': 25, 'verse': 13, 'text': 'وَإِذَآ أُلۡقُواْ مِنۡهَا مَكَانٗا ضَيِّقٗا مُّقَرَّنِينَ دَعَوۡاْ هُنَالِكَ ثُبُورٗا\nУнинг тор жойига кишанланган ҳолларида ташланганларида эса, ўша жойда (ўзларига) ҳалокат тиларлар'}, {'chapter': 25, 'verse': 14, 'text': 'لَّا تَدۡعُواْ ٱلۡيَوۡمَ ثُبُورٗا وَٰحِدٗا وَٱدۡعُواْ ثُبُورٗا كَثِيرٗا\n«Бугунги кунда бир ҳалокатни эмас, кўп ҳалокатни тиланглар!»'}, {'chapter': 25, 'verse': 15, 'text': 'قُلۡ أَذَٰلِكَ خَيۡرٌ أَمۡ جَنَّةُ ٱلۡخُلۡدِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۚ كَانَتۡ لَهُمۡ جَزَآءٗ وَمَصِيرٗا\n«Бу яхшими ёки тақводорларга тайёрлаб қўйилган мангулик жаннати яхшими?» У (жаннат) улар учун мукофот ва қайтиш жойидир'}, {'chapter': 25, 'verse': 16, 'text': 'لَّهُمۡ فِيهَا مَا يَشَآءُونَ خَٰلِدِينَۚ كَانَ عَلَىٰ رَبِّكَ وَعۡدٗا مَّسۡـُٔولٗا\nУларга у ерда хоҳлаган нарсалари бор, абадий ҳолда қолурлар. Бу, Роббинг зиммасидаги масъулиятли ваъдадир'}, {'chapter': 25, 'verse': 17, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ فَيَقُولُ ءَأَنتُمۡ أَضۡلَلۡتُمۡ عِبَادِي هَـٰٓؤُلَآءِ أَمۡ هُمۡ ضَلُّواْ ٱلسَّبِيلَ\nУ(мушрик)ларни ва Аллоҳдан ўзга ибодат қилган нарсаларини тўплаган кунда, У зот: «Анави бандаларимни сизлар адаштирдингизми ёки улар ўзлари йўлдан оздиларми?!» дейдир'}, {'chapter': 25, 'verse': 18, 'text': 'قَالُواْ سُبۡحَٰنَكَ مَا كَانَ يَنۢبَغِي لَنَآ أَن نَّتَّخِذَ مِن دُونِكَ مِنۡ أَوۡلِيَآءَ وَلَٰكِن مَّتَّعۡتَهُمۡ وَءَابَآءَهُمۡ حَتَّىٰ نَسُواْ ٱلذِّكۡرَ وَكَانُواْ قَوۡمَۢا بُورٗا\nУлар: «Ўзинг поксан! Сендан ўзгани дўст тутмоқ биз учун ярашмас. Лекин Сен уларни ва ота-боболарини (шунчалик) ҳузурлантирдингки, ҳатто улар зикрингни унутдилар ва ҳалокат қавми бўлиб қолдилар», дерлар'}, {'chapter': 25, 'verse': 19, 'text': 'فَقَدۡ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسۡتَطِيعُونَ صَرۡفٗا وَلَا نَصۡرٗاۚ وَمَن يَظۡلِم مِّنكُمۡ نُذِقۡهُ عَذَابٗا كَبِيرٗا\nБатаҳқиқ, улар сизларни айтаётган нарсангизда ёлғончига чиқардилар. Энди (азобни) буришга ҳам, ёрдам олишга ҳам қодир бўлмассиз. Сиздан ким зулм қилган бўлса, унга азобни торттирамиз'}, {'chapter': 25, 'verse': 20, 'text': 'وَمَآ أَرۡسَلۡنَا قَبۡلَكَ مِنَ ٱلۡمُرۡسَلِينَ إِلَّآ إِنَّهُمۡ لَيَأۡكُلُونَ ٱلطَّعَامَ وَيَمۡشُونَ فِي ٱلۡأَسۡوَاقِۗ وَجَعَلۡنَا بَعۡضَكُمۡ لِبَعۡضٖ فِتۡنَةً أَتَصۡبِرُونَۗ وَكَانَ رَبُّكَ بَصِيرٗا\nБиз сендан олдин юборган Пайғамбарларимиз ҳам, албатта, таом ер ва бозорларда юрар эдилар. Баъзиларингизни баъзиларингизга синов қилдик. Сабр қиласизлармикин?! Роббинг кўргувчи бўлган Зотдир'}, {'chapter': 25, 'verse': 21, 'text': '۞وَقَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا لَوۡلَآ أُنزِلَ عَلَيۡنَا ٱلۡمَلَـٰٓئِكَةُ أَوۡ نَرَىٰ رَبَّنَاۗ لَقَدِ ٱسۡتَكۡبَرُواْ فِيٓ أَنفُسِهِمۡ وَعَتَوۡ عُتُوّٗا كَبِيرٗا\nБизга учрашдан умидсиз бўлганлар, бизга фаришталар туширилса, ёки, Роббимизни кўрсак эди, дерлар. Батаҳқиқ, улар ўзларидан кетдилар ва катта туғён-ла туғён қилдилар'}, {'chapter': 25, 'verse': 22, 'text': 'يَوۡمَ يَرَوۡنَ ٱلۡمَلَـٰٓئِكَةَ لَا بُشۡرَىٰ يَوۡمَئِذٖ لِّلۡمُجۡرِمِينَ وَيَقُولُونَ حِجۡرٗا مَّحۡجُورٗا\nУлар фаришталарни кўрадиган кунда, ҳа, ўша кунда жиноятчиларга хушхабар бўлмас. Ва (фаришталар): «Ҳаром, ҳаром», дерлар'}, {'chapter': 25, 'verse': 23, 'text': 'وَقَدِمۡنَآ إِلَىٰ مَا عَمِلُواْ مِنۡ عَمَلٖ فَجَعَلۡنَٰهُ هَبَآءٗ مَّنثُورًا\nВа қилган амалига келиб, уларни тўзон каби сочиб юборамиз'}, {'chapter': 25, 'verse': 24, 'text': 'أَصۡحَٰبُ ٱلۡجَنَّةِ يَوۡمَئِذٍ خَيۡرٞ مُّسۡتَقَرّٗا وَأَحۡسَنُ مَقِيلٗا\nЎша кунда жаннат эгаларининг қароргоҳлари ва оромгоҳлари ғоят яхшидир'}, {'chapter': 25, 'verse': 25, 'text': 'وَيَوۡمَ تَشَقَّقُ ٱلسَّمَآءُ بِٱلۡغَمَٰمِ وَنُزِّلَ ٱلۡمَلَـٰٓئِكَةُ تَنزِيلًا\nУ кунда булутли осмон ёрилиб, фаришталар бўлак-бўлак туширилурлар'}, {'chapter': 25, 'verse': 26, 'text': 'ٱلۡمُلۡكُ يَوۡمَئِذٍ ٱلۡحَقُّ لِلرَّحۡمَٰنِۚ وَكَانَ يَوۡمًا عَلَى ٱلۡكَٰفِرِينَ عَسِيرٗا\nУ кунда ҳукмронлик Роҳманга ҳақ бўлур. У кун кофирлар учун кўп қийин кун бўлур'}, {'chapter': 25, 'verse': 27, 'text': 'وَيَوۡمَ يَعَضُّ ٱلظَّالِمُ عَلَىٰ يَدَيۡهِ يَقُولُ يَٰلَيۡتَنِي ٱتَّخَذۡتُ مَعَ ٱلرَّسُولِ سَبِيلٗا\nУ кунда золим икки қўлини тишлаб: «Эй воҳ!!! Пайғамбар ила бир йўлни тутганимда эди!» дер'}, {'chapter': 25, 'verse': 28, 'text': 'يَٰوَيۡلَتَىٰ لَيۡتَنِي لَمۡ أَتَّخِذۡ فُلَانًا خَلِيلٗا\n«Эй воҳ!!! Кошки фалончини дўст тутмасам эди», дер'}, {'chapter': 25, 'verse': 29, 'text': 'لَّقَدۡ أَضَلَّنِي عَنِ ٱلذِّكۡرِ بَعۡدَ إِذۡ جَآءَنِيۗ وَكَانَ ٱلشَّيۡطَٰنُ لِلۡإِنسَٰنِ خَذُولٗا\n«Батаҳқиқ, менга Зикр (Қуръон) келгандан сўнг у (дўст) мени адаштирди. Шайтон инсонни ёрдамсиз ташлаб кетгувчидир»'}, {'chapter': 25, 'verse': 30, 'text': 'وَقَالَ ٱلرَّسُولُ يَٰرَبِّ إِنَّ قَوۡمِي ٱتَّخَذُواْ هَٰذَا ٱلۡقُرۡءَانَ مَهۡجُورٗا\nВа Пайғамбар: «Эй Роббим, албатта, менинг қавмим ушбу Қуръонни тарк этдилар», деди'}, {'chapter': 25, 'verse': 31, 'text': 'وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا مِّنَ ٱلۡمُجۡرِمِينَۗ وَكَفَىٰ بِرَبِّكَ هَادِيٗا وَنَصِيرٗا\nАна шундай. Ҳар Пайғамбар учун жиноятчилардан душман қилганмиз. Ҳидоят ва ёрдамчиликда Роббинг Ўзи кифоя қилур'}, {'chapter': 25, 'verse': 32, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ٱلۡقُرۡءَانُ جُمۡلَةٗ وَٰحِدَةٗۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِۦ فُؤَادَكَۖ وَرَتَّلۡنَٰهُ تَرۡتِيلٗا\nКуфр келтирганлар: «Унга Қуръон бир йўла нозил қилинса эди», дедилар. Биз сенинг қалбингни у билан собит қилиш учун шундай қилдик ва уни дона-дона қилиб ўқидик'}, {'chapter': 25, 'verse': 33, 'text': 'وَلَا يَأۡتُونَكَ بِمَثَلٍ إِلَّا جِئۡنَٰكَ بِٱلۡحَقِّ وَأَحۡسَنَ تَفۡسِيرًا\nУлар сенга бирор масал келтирсалар, Биз, албатта, сенга ҳақни ва гўзал шарҳини келтирурмиз'}, {'chapter': 25, 'verse': 34, 'text': 'ٱلَّذِينَ يُحۡشَرُونَ عَلَىٰ وُجُوهِهِمۡ إِلَىٰ جَهَنَّمَ أُوْلَـٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ سَبِيلٗا\nЖаҳаннам томон юз тубан ҳолларида судраладиганлар, ана ўшалар, маконлари энг ёмон, йўллари энг адашган кимсалардир'}, {'chapter': 25, 'verse': 35, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَا مَعَهُۥٓ أَخَاهُ هَٰرُونَ وَزِيرٗا\nБатаҳқиқ, Биз Мусога китобни бердик ва унга акаси Ҳорунни вазир қилдик'}, {'chapter': 25, 'verse': 36, 'text': 'فَقُلۡنَا ٱذۡهَبَآ إِلَى ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا فَدَمَّرۡنَٰهُمۡ تَدۡمِيرٗا\nБас: «Иккингиз Бизнинг оятларимизни ёлғонга чиқарган қавмга боринг», дедик. Сўнг уларни ер билан яксон қилдик'}, {'chapter': 25, 'verse': 37, 'text': 'وَقَوۡمَ نُوحٖ لَّمَّا كَذَّبُواْ ٱلرُّسُلَ أَغۡرَقۡنَٰهُمۡ وَجَعَلۡنَٰهُمۡ لِلنَّاسِ ءَايَةٗۖ وَأَعۡتَدۡنَا لِلظَّـٰلِمِينَ عَذَابًا أَلِيمٗا\nВа Нуҳ қавми Пайғамбарларни ёлғонга чиқарганларида ғарқ этдик ва уларни одамларга ибрат қилдик. Ҳамда Биз золимларга аламли азоблар тайёрлаб қўйдик'}, {'chapter': 25, 'verse': 38, 'text': 'وَعَادٗا وَثَمُودَاْ وَأَصۡحَٰبَ ٱلرَّسِّ وَقُرُونَۢا بَيۡنَ ذَٰلِكَ كَثِيرٗا\nОд, Самуд ва қудуқ эгаларини ҳамда бу орадаги асрларни эсла'}, {'chapter': 25, 'verse': 39, 'text': 'وَكُلّٗا ضَرَبۡنَا لَهُ ٱلۡأَمۡثَٰلَۖ وَكُلّٗا تَبَّرۡنَا تَتۡبِيرٗا\nУлардан ҳар бирига мисоллар келтирдик. Ва уларни қириб битирдик'}, {'chapter': 25, 'verse': 40, 'text': 'وَلَقَدۡ أَتَوۡاْ عَلَى ٱلۡقَرۡيَةِ ٱلَّتِيٓ أُمۡطِرَتۡ مَطَرَ ٱلسَّوۡءِۚ أَفَلَمۡ يَكُونُواْ يَرَوۡنَهَاۚ بَلۡ كَانُواْ لَا يَرۡجُونَ نُشُورٗا\nБатаҳқиқ, улар ёмонлик ёмғири ёғдирилган шаҳарга келган эдилар. Уни кўрмаган эдиларми?! Йўқ! Улар қайта тирилишдан умидсиз эдилар'}, {'chapter': 25, 'verse': 41, 'text': 'وَإِذَا رَأَوۡكَ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي بَعَثَ ٱللَّهُ رَسُولًا\nУлар сени кўрган вақтларида масхарага олурлар: «Аллоҳ Пайғамбар қилиб юборган шуми'}, {'chapter': 25, 'verse': 42, 'text': 'إِن كَادَ لَيُضِلُّنَا عَنۡ ءَالِهَتِنَا لَوۡلَآ أَن صَبَرۡنَا عَلَيۡهَاۚ وَسَوۡفَ يَعۡلَمُونَ حِينَ يَرَوۡنَ ٱلۡعَذَابَ مَنۡ أَضَلُّ سَبِيلًا\nАгар сабр қилмаганимизда, албатта, у бизни, сал бўлмаса, худоларимиздан оздирарди-я!» деб. Яқинда — азобни кўрган чоғларида — ким йўлдан озганроқ эканини билурлар'}, {'chapter': 25, 'verse': 43, 'text': 'أَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ أَفَأَنتَ تَكُونُ عَلَيۡهِ وَكِيلًا\nҲавойи нафсини ўзига худо қилиб олганни кўрдингми?! Энди сен унга вакил бўласанми'}, {'chapter': 25, 'verse': 44, 'text': 'أَمۡ تَحۡسَبُ أَنَّ أَكۡثَرَهُمۡ يَسۡمَعُونَ أَوۡ يَعۡقِلُونَۚ إِنۡ هُمۡ إِلَّا كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّ سَبِيلًا\nЁки сен уларнинг кўплари тинглай олурлар ё англай олурлар, деб ҳисоблайсанми? Аслида, уларнинг ҳайвонлардан фарқи йўқ. Балки яна ҳам йўлдан озганроқдирлар'}, {'chapter': 25, 'verse': 45, 'text': 'أَلَمۡ تَرَ إِلَىٰ رَبِّكَ كَيۡفَ مَدَّ ٱلظِّلَّ وَلَوۡ شَآءَ لَجَعَلَهُۥ سَاكِنٗا ثُمَّ جَعَلۡنَا ٱلشَّمۡسَ عَلَيۡهِ دَلِيلٗا\nРоббингнинг сояни қандай ёйиб қўйганини кўрмадингми? Агар хоҳласа, уни жилмас қиларди. Сўнгра қуёшни Биз унга далил қилдик'}, {'chapter': 25, 'verse': 46, 'text': 'ثُمَّ قَبَضۡنَٰهُ إِلَيۡنَا قَبۡضٗا يَسِيرٗا\nСўнгра Биз уни Ўзимизга аста-секин тортиб олдик'}, {'chapter': 25, 'verse': 47, 'text': 'وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِبَاسٗا وَٱلنَّوۡمَ سُبَاتٗا وَجَعَلَ ٱلنَّهَارَ نُشُورٗا\nУ сиз учун кечани либос, уйқуни истироҳат қилган ва кундузни тирилиш (замони) қилган Зотдир'}, {'chapter': 25, 'verse': 48, 'text': 'وَهُوَ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ طَهُورٗا\nУ Ўз раҳмати олдидан шамолларни хушхабар қилиб юборган Зотдир. Ва осмондан пок сув туширдик'}, {'chapter': 25, 'verse': 49, 'text': 'لِّنُحۡـِۧيَ بِهِۦ بَلۡدَةٗ مَّيۡتٗا وَنُسۡقِيَهُۥ مِمَّا خَلَقۡنَآ أَنۡعَٰمٗا وَأَنَاسِيَّ كَثِيرٗا\nУ ила ўлик юртни тирилтиришимиз ва ўзимиз яратган кўплаб чорва ҳайвонлари ҳамда инсонларни суғоришимиз учун'}, {'chapter': 25, 'verse': 50, 'text': 'وَلَقَدۡ صَرَّفۡنَٰهُ بَيۡنَهُمۡ لِيَذَّكَّرُواْ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا\nБатаҳқиқ, Биз у(Қуръон)ни эслатма олишлари учун ораларида турлича тасарруф қилиб кўрдик. Одамларнинг кўплари куфрдан бошқа нарсадан бош тортдилар'}, {'chapter': 25, 'verse': 51, 'text': 'وَلَوۡ شِئۡنَا لَبَعَثۡنَا فِي كُلِّ قَرۡيَةٖ نَّذِيرٗا\nАгар хоҳласак, ҳар шаҳарга битта огоҳлантиргувчи юборар эдик'}, {'chapter': 25, 'verse': 52, 'text': 'فَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَجَٰهِدۡهُم بِهِۦ جِهَادٗا كَبِيرٗا\nКофирларга итоат этма! Уларга қарши у(Қуръон) билан катта жидду жаҳд қил'}, {'chapter': 25, 'verse': 53, 'text': '۞وَهُوَ ٱلَّذِي مَرَجَ ٱلۡبَحۡرَيۡنِ هَٰذَا عَذۡبٞ فُرَاتٞ وَهَٰذَا مِلۡحٌ أُجَاجٞ وَجَعَلَ بَيۡنَهُمَا بَرۡزَخٗا وَحِجۡرٗا مَّحۡجُورٗا\nУ, икки денгизни — буниси чучук-ширин, униси шўр-аччиқ қилиб оқизиб қўйган ва ораларига кўринмас тўсиқ ҳамда очилмас сарҳад қилган Зотдир'}, {'chapter': 25, 'verse': 54, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ مِنَ ٱلۡمَآءِ بَشَرٗا فَجَعَلَهُۥ نَسَبٗا وَصِهۡرٗاۗ وَكَانَ رَبُّكَ قَدِيرٗا\nУ, сувдан башар яратган ва уни насл-насаб, қуда-анда қилиб қўйган Зотдир. Роббинг қодир бўлган Зотдир'}, {'chapter': 25, 'verse': 55, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُهُمۡ وَلَا يَضُرُّهُمۡۗ وَكَانَ ٱلۡكَافِرُ عَلَىٰ رَبِّهِۦ ظَهِيرٗا\nУлар Аллоҳдан ўзга, уларга наф ҳам, зарар ҳам етказмайдиган, нарсаларга ибодат қиладилар. Ва кофир, Роббига қарши ёрдамчи бўлгандир'}, {'chapter': 25, 'verse': 56, 'text': 'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا\nБиз сени фақат хушхабарчи ва огоҳлантиргувчи этиб юборганмиз, холос'}, {'chapter': 25, 'verse': 57, 'text': 'قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍ إِلَّا مَن شَآءَ أَن يَتَّخِذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا\nСен: «Мен сизлардан бунинг учун ажр сўрамайман, фақат, ким хоҳласа, Робби томон йўл олишини (сўрайман)», деб айт'}, {'chapter': 25, 'verse': 58, 'text': 'وَتَوَكَّلۡ عَلَى ٱلۡحَيِّ ٱلَّذِي لَا يَمُوتُ وَسَبِّحۡ بِحَمۡدِهِۦۚ وَكَفَىٰ بِهِۦ بِذُنُوبِ عِبَادِهِۦ خَبِيرًا\nВа барҳаёт, ўлмайдиган Зотга таваккал қил ҳамда Уни ҳамду сано ила поклаб ёд эт. Бандаларининг гуноҳларидан ўта хабардор бўлишда Унинг Ўзи кифоядир'}, {'chapter': 25, 'verse': 59, 'text': 'ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ ٱلرَّحۡمَٰنُ فَسۡـَٔلۡ بِهِۦ خَبِيرٗا\nУ осмонлару ерни ва уларнинг орасидаги нарсаларни олти кунда яратган, сўнгра (Зоти ва улуғлигига муносиб суратда) Аршга юксалган Зотдир. У Роҳмандир. Бас, ўша ўта Хабардордан сўра'}, {'chapter': 25, 'verse': 60, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱسۡجُدُواْۤ لِلرَّحۡمَٰنِ قَالُواْ وَمَا ٱلرَّحۡمَٰنُ أَنَسۡجُدُ لِمَا تَأۡمُرُنَا وَزَادَهُمۡ نُفُورٗا۩\nҚачонки уларга: «Роҳманга сажда қилинглар!» дейилса, улар: «Роҳман нима? Сен буюрган нарсага сажда қилаверамизми?» дерлар. Бу уларнинг узоқлашишини зиёда қилур'}, {'chapter': 25, 'verse': 61, 'text': 'تَبَارَكَ ٱلَّذِي جَعَلَ فِي ٱلسَّمَآءِ بُرُوجٗا وَجَعَلَ فِيهَا سِرَٰجٗا وَقَمَرٗا مُّنِيرٗا\nОсмонда буржлар қилган ва унда чироқ ва нур сочгувчи ой қилган Зот баракотли(буюк) бўлди'}, {'chapter': 25, 'verse': 62, 'text': 'وَهُوَ ٱلَّذِي جَعَلَ ٱلَّيۡلَ وَٱلنَّهَارَ خِلۡفَةٗ لِّمَنۡ أَرَادَ أَن يَذَّكَّرَ أَوۡ أَرَادَ شُكُورٗا\nУ ибрат олмоқни ирода қилган ва шукр қилмоқни ирода қилган киши учун кеча-кундузни бирин-кетин қилган Зотдир'}, {'chapter': 25, 'verse': 63, 'text': 'وَعِبَادُ ٱلرَّحۡمَٰنِ ٱلَّذِينَ يَمۡشُونَ عَلَى ٱلۡأَرۡضِ هَوۡنٗا وَإِذَا خَاطَبَهُمُ ٱلۡجَٰهِلُونَ قَالُواْ سَلَٰمٗا\nРоҳманнинг бандалари ер юзида тавозуъ ила юрадиган ва жоҳиллар хитоб қилганида, «салом», дейдиганлардир'}, {'chapter': 25, 'verse': 64, 'text': 'وَٱلَّذِينَ يَبِيتُونَ لِرَبِّهِمۡ سُجَّدٗا وَقِيَٰمٗا\nУлар тунларни Роббиларига сажда қилиб, бедор ўтказадилар'}, {'chapter': 25, 'verse': 65, 'text': 'وَٱلَّذِينَ يَقُولُونَ رَبَّنَا ٱصۡرِفۡ عَنَّا عَذَابَ جَهَنَّمَۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا\nУлар: «Эй Роббимиз, Ўзинг биздан жаҳаннам азобини бургин, албатта, жаҳаннам азоби доимийдир'}, {'chapter': 25, 'verse': 66, 'text': 'إِنَّهَا سَآءَتۡ مُسۡتَقَرّٗا وَمُقَامٗا\nАлбатта, у энг ёмон қароргоҳ ва турар жойдир», дейдиганлардир'}, {'chapter': 25, 'verse': 67, 'text': 'وَٱلَّذِينَ إِذَآ أَنفَقُواْ لَمۡ يُسۡرِفُواْ وَلَمۡ يَقۡتُرُواْ وَكَانَ بَيۡنَ ذَٰلِكَ قَوَامٗا\nУлар инфоқ қилганларида исроф ҳам ва хасислик ҳам қилмаслар. У иккиси ўртасида мўътадил бўлурлар'}, {'chapter': 25, 'verse': 68, 'text': 'وَٱلَّذِينَ لَا يَدۡعُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ وَلَا يَقۡتُلُونَ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّ وَلَا يَزۡنُونَۚ وَمَن يَفۡعَلۡ ذَٰلِكَ يَلۡقَ أَثَامٗا\nУлар Аллоҳ билан бирга бирор илоҳга илтижо қилмаслар. Аллоҳ (ўлдиришни) ҳаром қилган жонни ноҳақдан ўлдирмаслар. Зино қилмаслар. Ким ана шуни қилса, уқубатга дучор бўлур'}, {'chapter': 25, 'verse': 69, 'text': 'يُضَٰعَفۡ لَهُ ٱلۡعَذَابُ يَوۡمَ ٱلۡقِيَٰمَةِ وَيَخۡلُدۡ فِيهِۦ مُهَانًا\nҚиёмат куни унинг азоби бир неча баробар кўпайтирилур ва у(азоб)да хор бўлиб абадий қолур'}, {'chapter': 25, 'verse': 70, 'text': 'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ عَمَلٗا صَٰلِحٗا فَأُوْلَـٰٓئِكَ يُبَدِّلُ ٱللَّهُ سَيِّـَٔاتِهِمۡ حَسَنَٰتٖۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nМагар ким тавба қилса, иймон келтириб, солиҳ амал қилса, бас, Аллоҳ ана ўшаларнинг ёмонликларини яхшиликларга алмаштирур. Аллоҳ мағфиратли ва раҳимли бўлган Зотдир'}, {'chapter': 25, 'verse': 71, 'text': 'وَمَن تَابَ وَعَمِلَ صَٰلِحٗا فَإِنَّهُۥ يَتُوبُ إِلَى ٱللَّهِ مَتَابٗا\nКим тавба қилиб, солиҳ амал қилса, у, албатта, Аллоҳга (ҳақиқий) қайтиш ила қайтар'}, {'chapter': 25, 'verse': 72, 'text': 'وَٱلَّذِينَ لَا يَشۡهَدُونَ ٱلزُّورَ وَإِذَا مَرُّواْ بِٱللَّغۡوِ مَرُّواْ كِرَامٗا\nУлар ёлғон гувоҳлик бермайдиганлардир ва беҳуда нарсалар олдидан ўтсалар, ҳурматларини сақлаган ҳолда ўтарлар'}, {'chapter': 25, 'verse': 73, 'text': 'وَٱلَّذِينَ إِذَا ذُكِّرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَمۡ يَخِرُّواْ عَلَيۡهَا صُمّٗا وَعُمۡيَانٗا\nУларга Роббиларининг оятлари эслатилганда, у(оят)ларга гунг ва кўрлардек ташланмаслар'}, {'chapter': 25, 'verse': 74, 'text': 'وَٱلَّذِينَ يَقُولُونَ رَبَّنَا هَبۡ لَنَا مِنۡ أَزۡوَٰجِنَا وَذُرِّيَّـٰتِنَا قُرَّةَ أَعۡيُنٖ وَٱجۡعَلۡنَا لِلۡمُتَّقِينَ إِمَامًا\nУлар: «Эй Роббимиз, Ўзинг бизга жуфти ҳалолларимиздан ва зурриётларимиздан кўзимиз қувонадиган нарса ҳадя эт ҳамда бизларни тақводорларга йўлбошчи қил», дейдиганлардир'}, {'chapter': 25, 'verse': 75, 'text': 'أُوْلَـٰٓئِكَ يُجۡزَوۡنَ ٱلۡغُرۡفَةَ بِمَا صَبَرُواْ وَيُلَقَّوۡنَ فِيهَا تَحِيَّةٗ وَسَلَٰمًا\nАна ўшалар сабр қилганлари учун «ғурфа» ила мукофотланурлар ва унда табрик ва салом билан қаршиланурлар'}, {'chapter': 25, 'verse': 76, 'text': 'خَٰلِدِينَ فِيهَاۚ حَسُنَتۡ مُسۡتَقَرّٗا وَمُقَامٗا\nУлар ўшанда абадий қолурлар. У қандай ҳам гўзал қароргоҳ ва манзилдир'}, {'chapter': 25, 'verse': 77, 'text': 'قُلۡ مَا يَعۡبَؤُاْ بِكُمۡ رَبِّي لَوۡلَا دُعَآؤُكُمۡۖ فَقَدۡ كَذَّبۡتُمۡ فَسَوۡفَ يَكُونُ لِزَامَۢا\nСен: «Агар дуо-илтижоларингиз бўлмаса, Роббим сизларга ҳеч қиймат бермас. Бас, батаҳқиқ, сиз ёлғонга чиқардингиз. Энди сизларга (азоб) лозим бўлур», деб айт'}], '26': [{'chapter': 26, 'verse': 1, 'text': 'طسٓمٓ\nТо. Сийн. Мийм'}, {'chapter': 26, 'verse': 2, 'text': 'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ\nБулар очиқ китобнинг оятларидир'}, {'chapter': 26, 'verse': 3, 'text': 'لَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ أَلَّا يَكُونُواْ مُؤۡمِنِينَ\nЭҳтимол, (улар) мўмин бўлмаганлари учун сен ўзингни ҳалок қилмоқчидирсан'}, {'chapter': 26, 'verse': 4, 'text': 'إِن نَّشَأۡ نُنَزِّلۡ عَلَيۡهِم مِّنَ ٱلسَّمَآءِ ءَايَةٗ فَظَلَّتۡ أَعۡنَٰقُهُمۡ لَهَا خَٰضِعِينَ\nАгар хоҳласак, уларга осмондан оят-мўъжиза нозил қиламиз. Бас, уларнинг бўйинлари унга эгилиб қоладир'}, {'chapter': 26, 'verse': 5, 'text': 'وَمَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّنَ ٱلرَّحۡمَٰنِ مُحۡدَثٍ إِلَّا كَانُواْ عَنۡهُ مُعۡرِضِينَ\nУларга Роҳмандан янги эслатма келибдики, албатта, ундан юз ўгиргувчи бўлганлар'}, {'chapter': 26, 'verse': 6, 'text': 'فَقَدۡ كَذَّبُواْ فَسَيَأۡتِيهِمۡ أَنۢبَـٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nБас, батаҳқиқ, ёлғонга чиқардилар. Яқинда уларга ўзлари истеҳзо қилган нарсанинг хабари келажакдир'}, {'chapter': 26, 'verse': 7, 'text': 'أَوَلَمۡ يَرَوۡاْ إِلَى ٱلۡأَرۡضِ كَمۡ أَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ\nУлар ерга қарамайдиларми?! Биз унда карамли жуфтлардан қанчаларини ундириб қўйибмиз'}, {'chapter': 26, 'verse': 8, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмаслар'}, {'chapter': 26, 'verse': 9, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 10, 'text': 'وَإِذۡ نَادَىٰ رَبُّكَ مُوسَىٰٓ أَنِ ٱئۡتِ ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nРоббинг Мусога: «У золим қавмга боргин'}, {'chapter': 26, 'verse': 11, 'text': 'قَوۡمَ فِرۡعَوۡنَۚ أَلَا يَتَّقُونَ\nФиръавн қавмига. (Биздан) қўрқмайдиларми?!» деб нидо қилганини эсла'}, {'chapter': 26, 'verse': 12, 'text': 'قَالَ رَبِّ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ\nУ: «Эй Роббим, улар мени ёлғончи қилишларидан қўрқарман'}, {'chapter': 26, 'verse': 13, 'text': 'وَيَضِيقُ صَدۡرِي وَلَا يَنطَلِقُ لِسَانِي فَأَرۡسِلۡ إِلَىٰ هَٰرُونَ\nВа дилим сиқилишидан ҳамда тилим бурро бўлмай қолишидан. Бас, Ҳорунни элчи қил'}, {'chapter': 26, 'verse': 14, 'text': 'وَلَهُمۡ عَلَيَّ ذَنۢبٞ فَأَخَافُ أَن يَقۡتُلُونِ\nМенинг зиммамда улар наздида гуноҳ бор. Бас, мени ўлдиришларидан хавфдаман», деди'}, {'chapter': 26, 'verse': 15, 'text': 'قَالَ كَلَّاۖ فَٱذۡهَبَا بِـَٔايَٰتِنَآۖ إِنَّا مَعَكُم مُّسۡتَمِعُونَ\nУ зот: «Йўқ! Бас, иккингиз Бизнинг оят-мўъжизаларимиз ила боринглар. Албатта, Биз иккингиз билан эшитгувчимиз'}, {'chapter': 26, 'verse': 16, 'text': 'فَأۡتِيَا فِرۡعَوۡنَ فَقُولَآ إِنَّا رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ\nБас, Фиръавнга боринглар ва унга: «Биз Роббул оламийннинг Пайғамбарларимиз'}, {'chapter': 26, 'verse': 17, 'text': 'أَنۡ أَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَـٰٓءِيلَ\nБани Исроилни биз билан қўйиб юбор», деб айтинглар, деди'}, {'chapter': 26, 'verse': 18, 'text': 'قَالَ أَلَمۡ نُرَبِّكَ فِينَا وَلِيدٗا وَلَبِثۡتَ فِينَا مِنۡ عُمُرِكَ سِنِينَ\nУ (Фиръавн): «Биз сени гўдаклигингдан ўз ичимизда тарбия қилмадикми?! Умрингнинг бир неча йилини бизнинг ичимизда ўтказмадингми'}, {'chapter': 26, 'verse': 19, 'text': 'وَفَعَلۡتَ فَعۡلَتَكَ ٱلَّتِي فَعَلۡتَ وَأَنتَ مِنَ ٱلۡكَٰفِرِينَ\nВа нонкўрлардан бўлиб, қиладиган ишингни қилиб қўймадингми?!» деди'}, {'chapter': 26, 'verse': 20, 'text': 'قَالَ فَعَلۡتُهَآ إِذٗا وَأَنَا۠ مِنَ ٱلضَّآلِّينَ\nУ (Мусо): «Ўша ишни қилган бўлсам, билмасдан қилганман'}, {'chapter': 26, 'verse': 21, 'text': 'فَفَرَرۡتُ مِنكُمۡ لَمَّا خِفۡتُكُمۡ فَوَهَبَ لِي رَبِّي حُكۡمٗا وَجَعَلَنِي مِنَ ٱلۡمُرۡسَلِينَ\nБас, сизлардан хавфсираб қочиб кетдим. Кейин Аллоҳ менга ҳикмат берди ва мени Пайғамбарлардан қилди'}, {'chapter': 26, 'verse': 22, 'text': 'وَتِلۡكَ نِعۡمَةٞ تَمُنُّهَا عَلَيَّ أَنۡ عَبَّدتَّ بَنِيٓ إِسۡرَـٰٓءِيلَ\nЎша менга қилаётган миннатинг Бани Исроилни қул қилиб олганингдандир», деди'}, {'chapter': 26, 'verse': 23, 'text': 'قَالَ فِرۡعَوۡنُ وَمَا رَبُّ ٱلۡعَٰلَمِينَ\nФиръавн: «Роббул оламийн нима?» деди'}, {'chapter': 26, 'verse': 24, 'text': 'قَالَ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُم مُّوقِنِينَ\nУ (Мусо): «Агар англамоқчи бўлсангиз, У, осмонлару ер ва уларнинг орасидаги нарсаларнинг Роббидир», деди'}, {'chapter': 26, 'verse': 25, 'text': 'قَالَ لِمَنۡ حَوۡلَهُۥٓ أَلَا تَسۡتَمِعُونَ\nУ (Фиръавн) атрофидаги кишиларга: «Эшитмаяпсизларми?!» деди'}, {'chapter': 26, 'verse': 26, 'text': 'قَالَ رَبُّكُمۡ وَرَبُّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ\nУ: «Сизнинг Роббингиз ва аввалги ота-боболарингизнинг Роббидир», деди'}, {'chapter': 26, 'verse': 27, 'text': 'قَالَ إِنَّ رَسُولَكُمُ ٱلَّذِيٓ أُرۡسِلَ إِلَيۡكُمۡ لَمَجۡنُونٞ\nУ: «Албатта, сизга юборилган Пайғамбарингиз жиннидир», деди'}, {'chapter': 26, 'verse': 28, 'text': 'قَالَ رَبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُمۡ تَعۡقِلُونَ\nУ: «У зот, агар ақл юритсангиз, машриқу мағрибнинг ва улар ўртасидаги нарсаларнинг Роббидир», деди'}, {'chapter': 26, 'verse': 29, 'text': 'قَالَ لَئِنِ ٱتَّخَذۡتَ إِلَٰهًا غَيۡرِي لَأَجۡعَلَنَّكَ مِنَ ٱلۡمَسۡجُونِينَ\nУ: «Агар мендан бошқани илоҳ қилиб олсанг, албатта, мен сени зиндонбандлардан қилурман», деди'}, {'chapter': 26, 'verse': 30, 'text': 'قَالَ أَوَلَوۡ جِئۡتُكَ بِشَيۡءٖ مُّبِينٖ\nУ: «Агар мен сенга очиқ-ойдин нарса келтирсам ҳам-а?» деди'}, {'chapter': 26, 'verse': 31, 'text': 'قَالَ فَأۡتِ بِهِۦٓ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУ: «Агар ростгўйлардан бўлсанг, келтир уни!» деди'}, {'chapter': 26, 'verse': 32, 'text': 'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ\nБас, у (Мусо) асосини ташлади. Баногоҳ у аниқ аждарга айланди'}, {'chapter': 26, 'verse': 33, 'text': 'وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّـٰظِرِينَ\nВа қўлини чиқарди. Баногоҳ у назар қилгувчиларга оппоқ кўринди'}, {'chapter': 26, 'verse': 34, 'text': 'قَالَ لِلۡمَلَإِ حَوۡلَهُۥٓ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ\nУ (Фиръавн) атрофидаги аъёнларига: «Албатта, бу ўта билимдон сеҳргардир'}, {'chapter': 26, 'verse': 35, 'text': 'يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِۦ فَمَاذَا تَأۡمُرُونَ\nУ ўз сеҳри билан сизларни ерингиздан чиқармоқчи, нима дейсизлар?» деди'}, {'chapter': 26, 'verse': 36, 'text': 'قَالُوٓاْ أَرۡجِهۡ وَأَخَاهُ وَٱبۡعَثۡ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ\nУлар: «Уни ва акасини қўйиб тургин-да, шаҳарларга тўпловчиларни юбор'}, {'chapter': 26, 'verse': 37, 'text': 'يَأۡتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٖ\nСенга барча ўта билимдон сеҳргарларни олиб келсинлар», дедилар'}, {'chapter': 26, 'verse': 38, 'text': 'فَجُمِعَ ٱلسَّحَرَةُ لِمِيقَٰتِ يَوۡمٖ مَّعۡلُومٖ\nБас, сеҳргарлар маълум куни келишилган вақтда жамландилар'}, {'chapter': 26, 'verse': 39, 'text': 'وَقِيلَ لِلنَّاسِ هَلۡ أَنتُم مُّجۡتَمِعُونَ\nВа одамларга: «Сизлар тўпланиб бўлдингизми'}, {'chapter': 26, 'verse': 40, 'text': 'لَعَلَّنَا نَتَّبِعُ ٱلسَّحَرَةَ إِن كَانُواْ هُمُ ٱلۡغَٰلِبِينَ\nАжаб эрмас, устун келсалар, сеҳргарларга эргашсак», дейилди'}, {'chapter': 26, 'verse': 41, 'text': 'فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالُواْ لِفِرۡعَوۡنَ أَئِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَٰلِبِينَ\nСеҳргарлар келган чоғларида Фиръавнга: «Агар биз ғолиб келгувчи бўлсак, бизга, албатта, ҳақ бериладими?» дедилар'}, {'chapter': 26, 'verse': 42, 'text': 'قَالَ نَعَمۡ وَإِنَّكُمۡ إِذٗا لَّمِنَ ٱلۡمُقَرَّبِينَ\nУ: «Ҳа! У ҳолда сиз, албатта, энг яқинларимдан бўласиз», деди'}, {'chapter': 26, 'verse': 43, 'text': 'قَالَ لَهُم مُّوسَىٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ\nМусо уларга: «Сиз ташлайдиган нарсангизни ташланг», деди'}, {'chapter': 26, 'verse': 44, 'text': 'فَأَلۡقَوۡاْ حِبَالَهُمۡ وَعِصِيَّهُمۡ وَقَالُواْ بِعِزَّةِ فِرۡعَوۡنَ إِنَّا لَنَحۡنُ ٱلۡغَٰلِبُونَ\nБас, улар арқонлари ва асоларини ташладилар ва «Фиръавннинг иззати билан қасамки, албатта, биз ғолиблармиз», дедилар'}, {'chapter': 26, 'verse': 45, 'text': 'فَأَلۡقَىٰ مُوسَىٰ عَصَاهُ فَإِذَا هِيَ تَلۡقَفُ مَا يَأۡفِكُونَ\nСўнгра Мусо асосини ташлаган эди, бирдан у уларнинг уйдирмаларини юта бошлади'}, {'chapter': 26, 'verse': 46, 'text': 'فَأُلۡقِيَ ٱلسَّحَرَةُ سَٰجِدِينَ\nБас, сеҳргарлар сажда қилган ҳолларида йиқилдилар'}, {'chapter': 26, 'verse': 47, 'text': 'قَالُوٓاْ ءَامَنَّا بِرَبِّ ٱلۡعَٰلَمِينَ\nУлар: «Роббул оламийнга иймон келтирдик'}, {'chapter': 26, 'verse': 48, 'text': 'رَبِّ مُوسَىٰ وَهَٰرُونَ\nМусо ва Ҳоруннинг Роббига», дедилар'}, {'chapter': 26, 'verse': 49, 'text': 'قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِي عَلَّمَكُمُ ٱلسِّحۡرَ فَلَسَوۡفَ تَعۡلَمُونَۚ لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ وَلَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ\nУ: «Мен сизга изн бермасимдан олдин унга иймон келтирдингиз! Албатта, у сизга сеҳр ўргатган каттангиздир! Энди тезда биласиз, мен, албатта, қўл-оёғингизни қарама-қарши томонидан кесажакман ва барчангизни осажакман», деди'}, {'chapter': 26, 'verse': 50, 'text': 'قَالُواْ لَا ضَيۡرَۖ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ\nУлар: «Зарари йўқ. Албатта, биз Роббимизга қайтгувчимиз'}, {'chapter': 26, 'verse': 51, 'text': 'إِنَّا نَطۡمَعُ أَن يَغۡفِرَ لَنَا رَبُّنَا خَطَٰيَٰنَآ أَن كُنَّآ أَوَّلَ ٱلۡمُؤۡمِنِينَ\nМўминларнинг аввалгиси бўлганимиз учун Роббимиз хатоларимизни кечиришидан умид қиламиз», дедилар'}, {'chapter': 26, 'verse': 52, 'text': '۞وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَسۡرِ بِعِبَادِيٓ إِنَّكُم مُّتَّبَعُونَ\nВа Мусога: «Бандаларим билан кечаси йўлга чиқ. Албатта, таъқиб этилурсиз», деб ваҳий юбордик'}, {'chapter': 26, 'verse': 53, 'text': 'فَأَرۡسَلَ فِرۡعَوۡنُ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ\nБас, Фиръавн шаҳарларга тўпловчилар юборди'}, {'chapter': 26, 'verse': 54, 'text': 'إِنَّ هَـٰٓؤُلَآءِ لَشِرۡذِمَةٞ قَلِيلُونَ\n«Шубҳасиз, анавилар озгина тоифадирлар'}, {'chapter': 26, 'verse': 55, 'text': 'وَإِنَّهُمۡ لَنَا لَغَآئِظُونَ\nАлбатта, улар бизни ғазаблантиргувчилардир'}, {'chapter': 26, 'verse': 56, 'text': 'وَإِنَّا لَجَمِيعٌ حَٰذِرُونَ\nАлбатта, биз ҳаммамиз ҳушёрмиз», деб'}, {'chapter': 26, 'verse': 57, 'text': 'فَأَخۡرَجۡنَٰهُم مِّن جَنَّـٰتٖ وَعُيُونٖ\nБас, Биз уларни чиқардик, боғ-роғлар ва булоқлардан'}, {'chapter': 26, 'verse': 58, 'text': 'وَكُنُوزٖ وَمَقَامٖ كَرِيمٖ\nХазиналар ва муҳтарам маскандан'}, {'chapter': 26, 'verse': 59, 'text': 'كَذَٰلِكَۖ وَأَوۡرَثۡنَٰهَا بَنِيٓ إِسۡرَـٰٓءِيلَ\nАна шундай. Ўша(нарса)ларни Бани Исроилга мерос қилиб бердик'}, {'chapter': 26, 'verse': 60, 'text': 'فَأَتۡبَعُوهُم مُّشۡرِقِينَ\nБас, уларни қуёш чиқиш пайтида қувиб етдилар'}, {'chapter': 26, 'verse': 61, 'text': 'فَلَمَّا تَرَـٰٓءَا ٱلۡجَمۡعَانِ قَالَ أَصۡحَٰبُ مُوسَىٰٓ إِنَّا لَمُدۡرَكُونَ\nИкки жамоат бир-бирларини кўрган чоғида, Мусонинг одамлари: «Энди қўлга тушдик», дедилар'}, {'chapter': 26, 'verse': 62, 'text': 'قَالَ كَلَّآۖ إِنَّ مَعِيَ رَبِّي سَيَهۡدِينِ\nУ (Мусо): «Йўқ! Албатта, Роббим мен билан, У менга тўғри йўлни кўрсатур», деди'}, {'chapter': 26, 'verse': 63, 'text': 'فَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡبَحۡرَۖ فَٱنفَلَقَ فَكَانَ كُلُّ فِرۡقٖ كَٱلطَّوۡدِ ٱلۡعَظِيمِ\nБас, Биз Мусога: «Асоинг билан денгизни ур», деб ваҳий қилдик. Бас, у (денгиз) бўлиниб, ҳар бўлаги улкан тоғдек бўлди'}, {'chapter': 26, 'verse': 64, 'text': 'وَأَزۡلَفۡنَا ثَمَّ ٱلۡأٓخَرِينَ\nВа кейин бошқаларни ҳам яқинлаштирдик'}, {'chapter': 26, 'verse': 65, 'text': 'وَأَنجَيۡنَا مُوسَىٰ وَمَن مَّعَهُۥٓ أَجۡمَعِينَ\nМусо ва унга эргашганларга, ҳаммаларига нажот бердик'}, {'chapter': 26, 'verse': 66, 'text': 'ثُمَّ أَغۡرَقۡنَا ٱلۡأٓخَرِينَ\nСўнгра бошқаларни ғарқ қилдик'}, {'chapter': 26, 'verse': 67, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 68, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 69, 'text': 'وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ إِبۡرَٰهِيمَ\nВа уларга Иброҳимнинг хабарини тиловат қилиб бер'}, {'chapter': 26, 'verse': 70, 'text': 'إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَا تَعۡبُدُونَ\nЎшанда у отаси ва қавмига: «Нимага ибодат қилмоқдасизлар?» деган эди'}, {'chapter': 26, 'verse': 71, 'text': 'قَالُواْ نَعۡبُدُ أَصۡنَامٗا فَنَظَلُّ لَهَا عَٰكِفِينَ\nУлар: «Санамларга ибодат қилмоқдамиз. Ва уларга ихлос ила ибодат қилишда бардавом бўлурмиз», дедилар'}, {'chapter': 26, 'verse': 72, 'text': 'قَالَ هَلۡ يَسۡمَعُونَكُمۡ إِذۡ تَدۡعُونَ\nУ: «Илтижо қилганингизда улар сизни эшитадиларми'}, {'chapter': 26, 'verse': 73, 'text': 'أَوۡ يَنفَعُونَكُمۡ أَوۡ يَضُرُّونَ\nЁхуд наф ёки зарар етказа оладиларми?» деди'}, {'chapter': 26, 'verse': 74, 'text': 'قَالُواْ بَلۡ وَجَدۡنَآ ءَابَآءَنَا كَذَٰلِكَ يَفۡعَلُونَ\nУлар: «Йўқ, биз ота-боболаримизнинг шундай қилаётганларини кўрдик», дедилар'}, {'chapter': 26, 'verse': 75, 'text': 'قَالَ أَفَرَءَيۡتُم مَّا كُنتُمۡ تَعۡبُدُونَ\nУ: «Нимага ибодат қилаётганингизни билдингизми'}, {'chapter': 26, 'verse': 76, 'text': 'أَنتُمۡ وَءَابَآؤُكُمُ ٱلۡأَقۡدَمُونَ\nСиз ва қадимги оталарингиз'}, {'chapter': 26, 'verse': 77, 'text': 'فَإِنَّهُمۡ عَدُوّٞ لِّيٓ إِلَّا رَبَّ ٱلۡعَٰلَمِينَ\nАлбатта, Роббул оламийндан бошқа уларнинг барчаси менга душмандир», деди'}, {'chapter': 26, 'verse': 78, 'text': 'ٱلَّذِي خَلَقَنِي فَهُوَ يَهۡدِينِ\nУ мени яратган Зотдир. Бас, мени ҳидоят қиладиган ҳам Унинг Ўзи'}, {'chapter': 26, 'verse': 79, 'text': 'وَٱلَّذِي هُوَ يُطۡعِمُنِي وَيَسۡقِينِ\nВа мени таомлантирадиган ва суғорадиган ҳам Унинг Ўзи'}, {'chapter': 26, 'verse': 80, 'text': 'وَإِذَا مَرِضۡتُ فَهُوَ يَشۡفِينِ\nБемор бўлганимда менга шифо берадиган ҳам Унинг Ўзи'}, {'chapter': 26, 'verse': 81, 'text': 'وَٱلَّذِي يُمِيتُنِي ثُمَّ يُحۡيِينِ\nМени ўлдирадиган, сўнгра тирилтирадиган ҳам Унинг Ўзи'}, {'chapter': 26, 'verse': 82, 'text': 'وَٱلَّذِيٓ أَطۡمَعُ أَن يَغۡفِرَ لِي خَطِيٓـَٔتِي يَوۡمَ ٱلدِّينِ\nВа менинг хатоимни қиёмат куни мағфират қилишидан умидвор бўлганим ҳам Унинг Ўзи'}, {'chapter': 26, 'verse': 83, 'text': 'رَبِّ هَبۡ لِي حُكۡمٗا وَأَلۡحِقۡنِي بِٱلصَّـٰلِحِينَ\nЭй Роббим, менга ҳикмат ҳадя эт ва мени солиҳларга қўшгин'}, {'chapter': 26, 'verse': 84, 'text': 'وَٱجۡعَل لِّي لِسَانَ صِدۡقٖ فِي ٱلۡأٓخِرِينَ\nВа менга кейин келгувчилар ичида содиқ мақтовлар бўладиган қилгин'}, {'chapter': 26, 'verse': 85, 'text': 'وَٱجۡعَلۡنِي مِن وَرَثَةِ جَنَّةِ ٱلنَّعِيمِ\nВа мени наъийм жаннати ворисларидан қилгин'}, {'chapter': 26, 'verse': 86, 'text': 'وَٱغۡفِرۡ لِأَبِيٓ إِنَّهُۥ كَانَ مِنَ ٱلضَّآلِّينَ\nВа отамни мағфират қил. Албатта, у адашганлардан бўлди'}, {'chapter': 26, 'verse': 87, 'text': 'وَلَا تُخۡزِنِي يَوۡمَ يُبۡعَثُونَ\nВа қайта тирилтириладиган кунда мени шарманда қилмагин'}, {'chapter': 26, 'verse': 88, 'text': 'يَوۡمَ لَا يَنفَعُ مَالٞ وَلَا بَنُونَ\nУ кунда на мол манфаат берар ва на болалар'}, {'chapter': 26, 'verse': 89, 'text': 'إِلَّا مَنۡ أَتَى ٱللَّهَ بِقَلۡبٖ سَلِيمٖ\nМагар, ким Аллоҳ ҳузурига тоза қалб ила келса, (манфаат топар)'}, {'chapter': 26, 'verse': 90, 'text': 'وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِينَ\nВа жаннат тақводорларга яқинлаштирилди'}, {'chapter': 26, 'verse': 91, 'text': 'وَبُرِّزَتِ ٱلۡجَحِيمُ لِلۡغَاوِينَ\nЖаҳийм иғвога учганларга зоҳир қилинди'}, {'chapter': 26, 'verse': 92, 'text': 'وَقِيلَ لَهُمۡ أَيۡنَ مَا كُنتُمۡ تَعۡبُدُونَ\nВа уларга: «Ибодат қилиб юрган нарсаларингиз қани'}, {'chapter': 26, 'verse': 93, 'text': 'مِن دُونِ ٱللَّهِ هَلۡ يَنصُرُونَكُمۡ أَوۡ يَنتَصِرُونَ\nАллоҳдан ўзга. Улар сизга ёрдам бера олурларми ёки ўзларини қутқара олурларми?!» дейилди'}, {'chapter': 26, 'verse': 94, 'text': 'فَكُبۡكِبُواْ فِيهَا هُمۡ وَٱلۡغَاوُۥنَ\nБас, улар, иғвога учганлар унга юзтубан ташландилар'}, {'chapter': 26, 'verse': 95, 'text': 'وَجُنُودُ إِبۡلِيسَ أَجۡمَعُونَ\nВа иблиснинг аскарлари ҳаммалари биргаликда'}, {'chapter': 26, 'verse': 96, 'text': 'قَالُواْ وَهُمۡ فِيهَا يَخۡتَصِمُونَ\nУлар унинг ичида бир-бирлари ила хусумат қилар эканлар, дедилар'}, {'chapter': 26, 'verse': 97, 'text': 'تَٱللَّهِ إِن كُنَّا لَفِي ضَلَٰلٖ مُّبِينٍ\n«Аллоҳга қасамки, албатта, биз очиқ-ойдин адашувда эканмиз'}, {'chapter': 26, 'verse': 98, 'text': 'إِذۡ نُسَوِّيكُم بِرَبِّ ٱلۡعَٰلَمِينَ\nЧунки, сизларни Роббул оламийнга тенглаштирардик'}, {'chapter': 26, 'verse': 99, 'text': 'وَمَآ أَضَلَّنَآ إِلَّا ٱلۡمُجۡرِمُونَ\nБизни фақат жиноятчилар йўлдан оздирдилар'}, {'chapter': 26, 'verse': 100, 'text': 'فَمَا لَنَا مِن شَٰفِعِينَ\nМана энди биз учун шафоатчилар ҳам йўқ'}, {'chapter': 26, 'verse': 101, 'text': 'وَلَا صَدِيقٍ حَمِيمٖ\nБирон қадрдон дўст ҳам йўқ'}, {'chapter': 26, 'verse': 102, 'text': 'فَلَوۡ أَنَّ لَنَا كَرَّةٗ فَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nҚани энди ортга қайтсагу мўминлардан бўлсак.»'}, {'chapter': 26, 'verse': 103, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 104, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азизу меҳрибондир'}, {'chapter': 26, 'verse': 105, 'text': 'كَذَّبَتۡ قَوۡمُ نُوحٍ ٱلۡمُرۡسَلِينَ\nНуҳ қавми Пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 106, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ نُوحٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Нуҳ деди: «Қўрқмайсизми'}, {'chapter': 26, 'verse': 107, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта, мен сизларга ишончли Пайғамбарман'}, {'chapter': 26, 'verse': 108, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар'}, {'chapter': 26, 'verse': 109, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен сизлардан бунинг учун ҳақ — ажр сўрамайман. Менинг ажрим фақатгина Роббул оламийннинг зиммасидадир'}, {'chapter': 26, 'verse': 110, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар»'}, {'chapter': 26, 'verse': 111, 'text': '۞قَالُوٓاْ أَنُؤۡمِنُ لَكَ وَٱتَّبَعَكَ ٱلۡأَرۡذَلُونَ\nУлар: «Сенга пасткашлар эргашиб турган ҳолда, биз сенга иймон келтирармидик?!» дедилар'}, {'chapter': 26, 'verse': 112, 'text': 'قَالَ وَمَا عِلۡمِي بِمَا كَانُواْ يَعۡمَلُونَ\nУ деди: «Менинг улар нима қилаётганлари ҳақида илмим йўқ'}, {'chapter': 26, 'verse': 113, 'text': 'إِنۡ حِسَابُهُمۡ إِلَّا عَلَىٰ رَبِّيۖ لَوۡ تَشۡعُرُونَ\nАгар сезсангиз, уларнинг ҳисоби фақат Аллоҳнинг зиммасидадир'}, {'chapter': 26, 'verse': 114, 'text': 'وَمَآ أَنَا۠ بِطَارِدِ ٱلۡمُؤۡمِنِينَ\nВа мен мўминларни ҳайдаб юборгувчи эмасман'}, {'chapter': 26, 'verse': 115, 'text': 'إِنۡ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ\nМен фақат очиқ-ойдин огоҳлантиргувчиман, холос»'}, {'chapter': 26, 'verse': 116, 'text': 'قَالُواْ لَئِن لَّمۡ تَنتَهِ يَٰنُوحُ لَتَكُونَنَّ مِنَ ٱلۡمَرۡجُومِينَ\nУлар: «Эй Нуҳ, агар тўхтамасанг, албатта, тошбўрон қилинганлардан бўласан», дедилар'}, {'chapter': 26, 'verse': 117, 'text': 'قَالَ رَبِّ إِنَّ قَوۡمِي كَذَّبُونِ\nУ: «Эй Роббим, шубҳасиз, қавмим мени ёлғончи қилдилар'}, {'chapter': 26, 'verse': 118, 'text': 'فَٱفۡتَحۡ بَيۡنِي وَبَيۡنَهُمۡ فَتۡحٗا وَنَجِّنِي وَمَن مَّعِيَ مِنَ ٱلۡمُؤۡمِنِينَ\nБас, Ўзинг мен билан уларнинг орамизни яхшилаб очгин ва менга ҳамда мен билан бирга бўлган мўминларга нажот бергин», деди'}, {'chapter': 26, 'verse': 119, 'text': 'فَأَنجَيۡنَٰهُ وَمَن مَّعَهُۥ فِي ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\nБас, Биз унга ва у билан бирга бўлганларга тўлган кемада нажот бердик'}, {'chapter': 26, 'verse': 120, 'text': 'ثُمَّ أَغۡرَقۡنَا بَعۡدُ ٱلۡبَاقِينَ\nСўнгра, қолганларни ғарқ қилдик'}, {'chapter': 26, 'verse': 121, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 122, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 123, 'text': 'كَذَّبَتۡ عَادٌ ٱلۡمُرۡسَلِينَ\nОд қавми Пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 124, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ هُودٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Ҳуд деди: «Қўрқмайсизларми'}, {'chapter': 26, 'verse': 125, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта, мен сизларга ишончли Пайғамбарман'}, {'chapter': 26, 'verse': 126, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар'}, {'chapter': 26, 'verse': 127, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен сизлардан бунинг учун ҳақ — ажр сўрамасман. Менинг ажрим фақатгина Роббул оламийн зиммасидадир'}, {'chapter': 26, 'verse': 128, 'text': 'أَتَبۡنُونَ بِكُلِّ رِيعٍ ءَايَةٗ تَعۡبَثُونَ\nҲар тепаликка бир белги бўладиган бино қуриб, беҳуда ўйин қилаверасизми'}, {'chapter': 26, 'verse': 129, 'text': 'وَتَتَّخِذُونَ مَصَانِعَ لَعَلَّكُمۡ تَخۡلُدُونَ\nВа худди мангу туражакдек «маснаълар» тутасиз'}, {'chapter': 26, 'verse': 130, 'text': 'وَإِذَا بَطَشۡتُم بَطَشۡتُمۡ جَبَّارِينَ\nТутганингизда ўта жабрчи бўлиб тутдингиз'}, {'chapter': 26, 'verse': 131, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинг ва менга итоат қилинг'}, {'chapter': 26, 'verse': 132, 'text': 'وَٱتَّقُواْ ٱلَّذِيٓ أَمَدَّكُم بِمَا تَعۡلَمُونَ\nСизга ўзингиз билган нарса ила мадад берган Зотдан қўрқинг'}, {'chapter': 26, 'verse': 133, 'text': 'أَمَدَّكُم بِأَنۡعَٰمٖ وَبَنِينَ\nУ сизларга чорва ҳайвонлари ва фарзандлар ила мадад берди'}, {'chapter': 26, 'verse': 134, 'text': 'وَجَنَّـٰتٖ وَعُيُونٍ\nБоғ-роғлар ва булоқлар ила'}, {'chapter': 26, 'verse': 135, 'text': 'إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ\nМен, албатта, сизга улуғ куннинг азоби келишидан қўрқаман»'}, {'chapter': 26, 'verse': 136, 'text': 'قَالُواْ سَوَآءٌ عَلَيۡنَآ أَوَعَظۡتَ أَمۡ لَمۡ تَكُن مِّنَ ٱلۡوَٰعِظِينَ\nУлар: «Биз учун ваъз қилсанг ҳам ёки ваъз қилгувчилардан бўлмасанг ҳам — бари бир'}, {'chapter': 26, 'verse': 137, 'text': 'إِنۡ هَٰذَآ إِلَّا خُلُقُ ٱلۡأَوَّلِينَ\nБу эскиларнинг одати, холос'}, {'chapter': 26, 'verse': 138, 'text': 'وَمَا نَحۡنُ بِمُعَذَّبِينَ\nБиз азоблангувчи эмасмиз», дедилар'}, {'chapter': 26, 'verse': 139, 'text': 'فَكَذَّبُوهُ فَأَهۡلَكۡنَٰهُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nУлар у(Ҳуд)ни ёлғончи қилдилар. Бас, Биз уларни ҳалок қилдик. Албатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 140, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 141, 'text': 'كَذَّبَتۡ ثَمُودُ ٱلۡمُرۡسَلِينَ\nСамуд (қавми) Пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 142, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ صَٰلِحٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Солиҳ деди: «Қўрқмайсизларми'}, {'chapter': 26, 'verse': 143, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта, мен сизларга ишончли Пайғамбарман'}, {'chapter': 26, 'verse': 144, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар'}, {'chapter': 26, 'verse': 145, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен сизлардан бунинг учун ҳақ — ажр сўрамайман. Менинг ажрим фақатгина Роббул оламийн зиммасидадир'}, {'chapter': 26, 'verse': 146, 'text': 'أَتُتۡرَكُونَ فِي مَا هَٰهُنَآ ءَامِنِينَ\nСиз бу жойдаги нарсаларда омон ҳолда тарк қилинасизми'}, {'chapter': 26, 'verse': 147, 'text': 'فِي جَنَّـٰتٖ وَعُيُونٖ\nБоғ-роғларда, булоқларда'}, {'chapter': 26, 'verse': 148, 'text': 'وَزُرُوعٖ وَنَخۡلٖ طَلۡعُهَا هَضِيمٞ\nЭкинзорлару майин хурмозор ичларида-я'}, {'chapter': 26, 'verse': 149, 'text': 'وَتَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُيُوتٗا فَٰرِهِينَ\nВа тоғлардан манманлик-ла уйлар йўнаверасизми'}, {'chapter': 26, 'verse': 150, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар'}, {'chapter': 26, 'verse': 151, 'text': 'وَلَا تُطِيعُوٓاْ أَمۡرَ ٱلۡمُسۡرِفِينَ\nВа ер юзида исрофчиларнинг ишига итоат қилманглар'}, {'chapter': 26, 'verse': 152, 'text': 'ٱلَّذِينَ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ\nЕр юзида бузғунчилик қиладиган, ислоҳ этмайдиганларнинг»'}, {'chapter': 26, 'verse': 153, 'text': 'قَالُوٓاْ إِنَّمَآ أَنتَ مِنَ ٱلۡمُسَحَّرِينَ\nУлар: «Сен, албатта, сеҳрланганлардандирсан'}, {'chapter': 26, 'verse': 154, 'text': 'مَآ أَنتَ إِلَّا بَشَرٞ مِّثۡلُنَا فَأۡتِ بِـَٔايَةٍ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nСен бизга ўхшаган башардан бошқа ҳеч нарса эмассан. Агар ростгўйлардан бўлсанг, мўъжиза келтир», дедилар'}, {'chapter': 26, 'verse': 155, 'text': 'قَالَ هَٰذِهِۦ نَاقَةٞ لَّهَا شِرۡبٞ وَلَكُمۡ شِرۡبُ يَوۡمٖ مَّعۡلُومٖ\nУ: «Мана бу туя. Унга (бир кун) сув ичиш ва сизга маълум бир кун сув ичиш'}, {'chapter': 26, 'verse': 156, 'text': 'وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابُ يَوۡمٍ عَظِيمٖ\nВа унга ёмонлик етказманглар, унда, сизни улуғ куннинг азоби тутадир», деди'}, {'chapter': 26, 'verse': 157, 'text': 'فَعَقَرُوهَا فَأَصۡبَحُواْ نَٰدِمِينَ\nБас, у(туя)ни сўйдилару надомат чекувчига айландилар'}, {'chapter': 26, 'verse': 158, 'text': 'فَأَخَذَهُمُ ٱلۡعَذَابُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nБас, уларни азоб тутди. Албатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 159, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 160, 'text': 'كَذَّبَتۡ قَوۡمُ لُوطٍ ٱلۡمُرۡسَلِينَ\nЛут қавми Пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 161, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ لُوطٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Лут деди: «Қўрқмайсизларми'}, {'chapter': 26, 'verse': 162, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта, мен сизларга ишончли Пайғамбарман'}, {'chapter': 26, 'verse': 163, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар'}, {'chapter': 26, 'verse': 164, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен сизлардан бунинг учун ҳақ — ажр сўрамасман. Менинг ажрим фақатгина Роббул оламийн зиммасидадир'}, {'chapter': 26, 'verse': 165, 'text': 'أَتَأۡتُونَ ٱلذُّكۡرَانَ مِنَ ٱلۡعَٰلَمِينَ\nОламлар ичидан эркакларга борасизми'}, {'chapter': 26, 'verse': 166, 'text': 'وَتَذَرُونَ مَا خَلَقَ لَكُمۡ رَبُّكُم مِّنۡ أَزۡوَٰجِكُمۚ بَلۡ أَنتُمۡ قَوۡمٌ عَادُونَ\nВа Роббингиз сиз учун яратган жуфти ҳалолларингизни тарк қиласизми?! Йўқ!!! Сиз ҳаддан ошган қавмсиз'}, {'chapter': 26, 'verse': 167, 'text': 'قَالُواْ لَئِن لَّمۡ تَنتَهِ يَٰلُوطُ لَتَكُونَنَّ مِنَ ٱلۡمُخۡرَجِينَ\nУлар: «Эй Лут, агар тўхтамасанг, албатта, ҳайдаб чиқарилганлардан бўласан!» дедилар'}, {'chapter': 26, 'verse': 168, 'text': 'قَالَ إِنِّي لِعَمَلِكُم مِّنَ ٱلۡقَالِينَ\nУ: «Албатта, мен бу ишингизни ўта ёмон кўргувчиларданман'}, {'chapter': 26, 'verse': 169, 'text': 'رَبِّ نَجِّنِي وَأَهۡلِي مِمَّا يَعۡمَلُونَ\nЭй Роббим, менга ва аҳлимга қилаётган нарсаларидан нажот бергин!» деди'}, {'chapter': 26, 'verse': 170, 'text': 'فَنَجَّيۡنَٰهُ وَأَهۡلَهُۥٓ أَجۡمَعِينَ\nБас, унга ва аҳлига, ҳаммаларига нажот бердик'}, {'chapter': 26, 'verse': 171, 'text': 'إِلَّا عَجُوزٗا فِي ٱلۡغَٰبِرِينَ\nМагар (азобда) қолгувчилардан бўлган бир кампирга (нажот бермадик)'}, {'chapter': 26, 'verse': 172, 'text': 'ثُمَّ دَمَّرۡنَا ٱلۡأٓخَرِينَ\nСўнгра қолганларини ер билан яксон қилдик'}, {'chapter': 26, 'verse': 173, 'text': 'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ\nВа уларнинг устига бир ёмғир ёғдирдикки, бас, огоҳлантирилганларнинг ёмғири қандоқ ҳам ёмон бўлди'}, {'chapter': 26, 'verse': 174, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 175, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 176, 'text': 'كَذَّبَ أَصۡحَٰبُ لۡـَٔيۡكَةِ ٱلۡمُرۡسَلِينَ\nҚалин дарахтзор эгалари Пайғамбарларни ёлғончи қилдилар'}, {'chapter': 26, 'verse': 177, 'text': 'إِذۡ قَالَ لَهُمۡ شُعَيۡبٌ أَلَا تَتَّقُونَ\nЎшанда уларга Шуъайб деди: «Қўрқмайсизларми'}, {'chapter': 26, 'verse': 178, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта, мен сизларга ишончли Пайғамбарман'}, {'chapter': 26, 'verse': 179, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат қилинглар'}, {'chapter': 26, 'verse': 180, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен сизлардан бунинг учун ҳақ — ажр сўрамасман. Менинг ажрим фақатгина Роббул оламийн зиммасидадир'}, {'chapter': 26, 'verse': 181, 'text': '۞أَوۡفُواْ ٱلۡكَيۡلَ وَلَا تَكُونُواْ مِنَ ٱلۡمُخۡسِرِينَ\nБас, ўлчовни тўлиқ ўлчанглар ва камайтиргувчилардан бўлманглар'}, {'chapter': 26, 'verse': 182, 'text': 'وَزِنُواْ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِيمِ\nВа тўғри тарози ила тортинг'}, {'chapter': 26, 'verse': 183, 'text': 'وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nОдамларнинг нарсаларини камайтириб берманг. Ер юзида турли бузғунчиликлар қилманг'}, {'chapter': 26, 'verse': 184, 'text': 'وَٱتَّقُواْ ٱلَّذِي خَلَقَكُمۡ وَٱلۡجِبِلَّةَ ٱلۡأَوَّلِينَ\nСизни ва аввалги наслларни яратган Зотдан қўрқинг»'}, {'chapter': 26, 'verse': 185, 'text': 'قَالُوٓاْ إِنَّمَآ أَنتَ مِنَ ٱلۡمُسَحَّرِينَ\nУлар: «Албатта, сен сеҳрланганлардансан'}, {'chapter': 26, 'verse': 186, 'text': 'وَمَآ أَنتَ إِلَّا بَشَرٞ مِّثۡلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلۡكَٰذِبِينَ\nВа сен бизга ўхшаган башардан бошқа нарса эмассан. Биз сени, албатта, ёлғончилардан, деб биламиз'}, {'chapter': 26, 'verse': 187, 'text': 'فَأَسۡقِطۡ عَلَيۡنَا كِسَفٗا مِّنَ ٱلسَّمَآءِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nАгар ростгўйлардан бўлсанг, устимиздан осмоннинг бир бўлагини ташлавор», дедилар'}, {'chapter': 26, 'verse': 188, 'text': 'قَالَ رَبِّيٓ أَعۡلَمُ بِمَا تَعۡمَلُونَ\nУ : «Роббим қилаётган ишингизни ўта яхши билгувчидир», деди'}, {'chapter': 26, 'verse': 189, 'text': 'فَكَذَّبُوهُ فَأَخَذَهُمۡ عَذَابُ يَوۡمِ ٱلظُّلَّةِۚ إِنَّهُۥ كَانَ عَذَابَ يَوۡمٍ عَظِيمٍ\nБас, улар у(Шуъайб)ни ёлғончи қилдилар. Бас, уларни «соябон» куни азоби тутди. Албатта, у улуғ куннинг азоби эди'}, {'chapter': 26, 'verse': 190, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда оят-белги бор. Лекин кўплари мўмин бўлмадилар'}, {'chapter': 26, 'verse': 191, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАлбатта, Роббинг Ўзи азийзу меҳрибондир'}, {'chapter': 26, 'verse': 192, 'text': 'وَإِنَّهُۥ لَتَنزِيلُ رَبِّ ٱلۡعَٰلَمِينَ\nАлбатта, у(Қуръон) Роббул оламийннинг туширганидир'}, {'chapter': 26, 'verse': 193, 'text': 'نَزَلَ بِهِ ٱلرُّوحُ ٱلۡأَمِينُ\nУни Руҳул Амийн олиб тушди'}, {'chapter': 26, 'verse': 194, 'text': 'عَلَىٰ قَلۡبِكَ لِتَكُونَ مِنَ ٱلۡمُنذِرِينَ\nСенинг қалбингга, огоҳлантиргувчилардан бўлишинг учун'}, {'chapter': 26, 'verse': 195, 'text': 'بِلِسَانٍ عَرَبِيّٖ مُّبِينٖ\nОчиқ-ойдин араб тилида'}, {'chapter': 26, 'verse': 196, 'text': 'وَإِنَّهُۥ لَفِي زُبُرِ ٱلۡأَوَّلِينَ\nАлбатта, у(Қуръон) аввалгиларнинг китобларида ҳам бордир'}, {'chapter': 26, 'verse': 197, 'text': 'أَوَلَمۡ يَكُن لَّهُمۡ ءَايَةً أَن يَعۡلَمَهُۥ عُلَمَـٰٓؤُاْ بَنِيٓ إِسۡرَـٰٓءِيلَ\nУни Бани Исроил уламоларининг билишлари улар учун далил эмасми'}, {'chapter': 26, 'verse': 198, 'text': 'وَلَوۡ نَزَّلۡنَٰهُ عَلَىٰ بَعۡضِ ٱلۡأَعۡجَمِينَ\nАгар Биз уни баъзи ажамийларга нозил қилсак'}, {'chapter': 26, 'verse': 199, 'text': 'فَقَرَأَهُۥ عَلَيۡهِم مَّا كَانُواْ بِهِۦ مُؤۡمِنِينَ\nБас, у уларга уни ўқиб берса ҳам, унга мўмин бўлмас эдилар'}, {'chapter': 26, 'verse': 200, 'text': 'كَذَٰلِكَ سَلَكۡنَٰهُ فِي قُلُوبِ ٱلۡمُجۡرِمِينَ\nБиз у(Қуръон)ни жиноятчилар қалбларига ана шундай йўлладик'}, {'chapter': 26, 'verse': 201, 'text': 'لَا يُؤۡمِنُونَ بِهِۦ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nУлар унга то аламли азобни кўрмагунларича иймон келтирмаслар'}, {'chapter': 26, 'verse': 202, 'text': 'فَيَأۡتِيَهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nБас, уларга у(азоб) тўсатдан келур. Улар сезмай қолурлар'}, {'chapter': 26, 'verse': 203, 'text': 'فَيَقُولُواْ هَلۡ نَحۡنُ مُنظَرُونَ\nШунда улар: «Бизга муҳлат берилармикан?!» дейдилар'}, {'chapter': 26, 'verse': 204, 'text': 'أَفَبِعَذَابِنَا يَسۡتَعۡجِلُونَ\nБизнинг азобимизнинг тезроқ келишини истайдиларми-я'}, {'chapter': 26, 'verse': 205, 'text': 'أَفَرَءَيۡتَ إِن مَّتَّعۡنَٰهُمۡ سِنِينَ\nАйтгинг-чи, агар Биз уларни йилларча ҳузурлантирсак-да'}, {'chapter': 26, 'verse': 206, 'text': 'ثُمَّ جَآءَهُم مَّا كَانُواْ يُوعَدُونَ\nСўнгра уларга ваъда қилинган нарса келса'}, {'chapter': 26, 'verse': 207, 'text': 'مَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يُمَتَّعُونَ\nЎша ҳузурланиб юрган нарсалари нимага асқотур'}, {'chapter': 26, 'verse': 208, 'text': 'وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا لَهَا مُنذِرُونَ\nБиз бирон шаҳар-қишлоқни ҳалок қилган бўлсак, албатта, унинг огоҳлантиргувчилари бўлган'}, {'chapter': 26, 'verse': 209, 'text': 'ذِكۡرَىٰ وَمَا كُنَّا ظَٰلِمِينَ\nЭслатма бўлиши учундир. Биз зулм қилгувчи бўлмагандик'}, {'chapter': 26, 'verse': 210, 'text': 'وَمَا تَنَزَّلَتۡ بِهِ ٱلشَّيَٰطِينُ\nУ(Қуръон)ни шайтонлар олиб тушганлари йўқ'}, {'chapter': 26, 'verse': 211, 'text': 'وَمَا يَنۢبَغِي لَهُمۡ وَمَا يَسۡتَطِيعُونَ\nБунга уларнинг имконлари йўқ ва унга қодир ҳам эмаслар'}, {'chapter': 26, 'verse': 212, 'text': 'إِنَّهُمۡ عَنِ ٱلسَّمۡعِ لَمَعۡزُولُونَ\nАлбатта, улар эшитишдан четлатилгандирлар'}, {'chapter': 26, 'verse': 213, 'text': 'فَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَكُونَ مِنَ ٱلۡمُعَذَّبِينَ\nАллоҳ билан бирга бошқа илоҳга илтижо қилма. Йўқса, азобланганлардан бўласан'}, {'chapter': 26, 'verse': 214, 'text': 'وَأَنذِرۡ عَشِيرَتَكَ ٱلۡأَقۡرَبِينَ\nВа яқин қариндошларингни огоҳлантир'}, {'chapter': 26, 'verse': 215, 'text': 'وَٱخۡفِضۡ جَنَاحَكَ لِمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِينَ\nВа сенга эргашган мўминларга қанотингни пастлат'}, {'chapter': 26, 'verse': 216, 'text': 'فَإِنۡ عَصَوۡكَ فَقُلۡ إِنِّي بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ\nБас, агар улар сенга исён қилсалар: «Мен сиз қилаётган амаллардан безорман», дегин'}, {'chapter': 26, 'verse': 217, 'text': 'وَتَوَكَّلۡ عَلَى ٱلۡعَزِيزِ ٱلرَّحِيمِ\nВа азийз ва меҳрибон Зотга таваккал қил'}, {'chapter': 26, 'verse': 218, 'text': 'ٱلَّذِي يَرَىٰكَ حِينَ تَقُومُ\nСени қоим бўлганингда кўрадиган'}, {'chapter': 26, 'verse': 219, 'text': 'وَتَقَلُّبَكَ فِي ٱلسَّـٰجِدِينَ\nВа сажда қилгувчилар ичида ҳаракатланишингни (кўрадиган) Зотга'}, {'chapter': 26, 'verse': 220, 'text': 'إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАлбатта, Унинг Ўзи ўта эшитгувчи ва ўта билгувчи Зотдир'}, {'chapter': 26, 'verse': 221, 'text': 'هَلۡ أُنَبِّئُكُمۡ عَلَىٰ مَن تَنَزَّلُ ٱلشَّيَٰطِينُ\nСизларга шайтонлар кимга тушишини айтиб берайми'}, {'chapter': 26, 'verse': 222, 'text': 'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٖ\nУлар ўта уйдирмачи ва ўта гуноҳкорларга тушурлар'}, {'chapter': 26, 'verse': 223, 'text': 'يُلۡقُونَ ٱلسَّمۡعَ وَأَكۡثَرُهُمۡ كَٰذِبُونَ\nУлар (шайтонларга) қулоқ осарлар. Ва уларнинг кўплари ёлғончилардир'}, {'chapter': 26, 'verse': 224, 'text': 'وَٱلشُّعَرَآءُ يَتَّبِعُهُمُ ٱلۡغَاوُۥنَ\nВа шоирларга гумроҳлар эргашур'}, {'chapter': 26, 'verse': 225, 'text': 'أَلَمۡ تَرَ أَنَّهُمۡ فِي كُلِّ وَادٖ يَهِيمُونَ\nУларнинг ҳар водийда дайдишини'}, {'chapter': 26, 'verse': 226, 'text': 'وَأَنَّهُمۡ يَقُولُونَ مَا لَا يَفۡعَلُونَ\nВа ўзлари қилмайдиган ишларни айтишини кўрмадингми'}, {'chapter': 26, 'verse': 227, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَذَكَرُواْ ٱللَّهَ كَثِيرٗا وَٱنتَصَرُواْ مِنۢ بَعۡدِ مَا ظُلِمُواْۗ وَسَيَعۡلَمُ ٱلَّذِينَ ظَلَمُوٓاْ أَيَّ مُنقَلَبٖ يَنقَلِبُونَ\nМагар, иймон келтириб, солиҳ амаллар қилганлар, Аллоҳни кўп зикр этганлар ва мазлум бўлганларидан кейин нусрат қозонганлар (ундай эмаслар). Зулм қилганлар эса, тезда қайси ағдарилиш жойига ағдарилишларини билурлар'}], '27': [{'chapter': 27, 'verse': 1, 'text': 'طسٓۚ تِلۡكَ ءَايَٰتُ ٱلۡقُرۡءَانِ وَكِتَابٖ مُّبِينٍ\nТо. Сийн. Ушбулар Қуръон — очиқ китоб оятларидир'}, {'chapter': 27, 'verse': 2, 'text': 'هُدٗى وَبُشۡرَىٰ لِلۡمُؤۡمِنِينَ\nМўминлар учун ҳидоят ва хушхабардир'}, {'chapter': 27, 'verse': 3, 'text': 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ\nУлар намозни тўкис адо этадиган, закотни берадиган ва охиратга қаттиқ ишонадиганлардир'}, {'chapter': 27, 'verse': 4, 'text': 'إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ زَيَّنَّا لَهُمۡ أَعۡمَٰلَهُمۡ فَهُمۡ يَعۡمَهُونَ\nАлбатта, охиратга иймон келтирмайдиганларга Биз амалларини зийнатлаб кўрсатдик. Бас, улар адашиб-улоқиб юраверадилар'}, {'chapter': 27, 'verse': 5, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَهُمۡ سُوٓءُ ٱلۡعَذَابِ وَهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ\nАна ўшаларга ёмон азоб бордир ва улар охиратда энг кўп зиён кўргувчилардир'}, {'chapter': 27, 'verse': 6, 'text': 'وَإِنَّكَ لَتُلَقَّى ٱلۡقُرۡءَانَ مِن لَّدُنۡ حَكِيمٍ عَلِيمٍ\nАлбатта, сенга бу Қуръон ўта ҳикматли ва ўта илмли Зот томонидан берилур'}, {'chapter': 27, 'verse': 7, 'text': 'إِذۡ قَالَ مُوسَىٰ لِأَهۡلِهِۦٓ إِنِّيٓ ءَانَسۡتُ نَارٗا سَـَٔاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ ءَاتِيكُم بِشِهَابٖ قَبَسٖ لَّعَلَّكُمۡ تَصۡطَلُونَ\nМусо ўз аҳлига: «Аниқки, мен бир олов кўрдим. Тезда мен сизларга ундан бирор хабар ёки исинишингиз учун бир тутам чўғ келтирурман», деганини эсла'}, {'chapter': 27, 'verse': 8, 'text': 'فَلَمَّا جَآءَهَا نُودِيَ أَنۢ بُورِكَ مَن فِي ٱلنَّارِ وَمَنۡ حَوۡلَهَا وَسُبۡحَٰنَ ٱللَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nУ(олов)га келганида, олов олдидаги ва атрофидаги шахсларга барака берилди ва «Оламларнинг Робби — Аллоҳ покдир», деб нидо қилинди'}, {'chapter': 27, 'verse': 9, 'text': 'يَٰمُوسَىٰٓ إِنَّهُۥٓ أَنَا ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\n«Эй Мусо! Албатта, бу — Менман! Азийзу ҳакийм Аллоҳман'}, {'chapter': 27, 'verse': 10, 'text': 'وَأَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰ لَا تَخَفۡ إِنِّي لَا يَخَافُ لَدَيَّ ٱلۡمُرۡسَلُونَ\nВа асоингни ташла!» Бас, қачонки, Мусо у(асо)нинг илондек қимирлаётганини кўрганида, ортига қарамай қочди. «Эй Мусо! Қўрқма! Зеро, Менинг ҳузуримда Пайғамбарлар қўрқмаслар!»'}, {'chapter': 27, 'verse': 11, 'text': 'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسۡنَۢا بَعۡدَ سُوٓءٖ فَإِنِّي غَفُورٞ رَّحِيمٞ\nЛекин ким зулм қилса-ю, сўнгра ёмонлик кетидан унга яхшиликни бадал қилса, бас, Мен, албатта, мағфиратли ва меҳрибондирман'}, {'chapter': 27, 'verse': 12, 'text': 'وَأَدۡخِلۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖۖ فِي تِسۡعِ ءَايَٰتٍ إِلَىٰ فِرۡعَوۡنَ وَقَوۡمِهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nҚўлингни ёқангнинг ичига суқ, ҳеч қандай ёмонликсиз оппоқ бўлиб чиқур. Фиръавн ва унинг қавмига қаратилган тўққиз мўъжиза орасида (бу бордир). Албатта, улар бузғунчи қавм бўлдилар'}, {'chapter': 27, 'verse': 13, 'text': 'فَلَمَّا جَآءَتۡهُمۡ ءَايَٰتُنَا مُبۡصِرَةٗ قَالُواْ هَٰذَا سِحۡرٞ مُّبِينٞ\nБас, қачонки уларга оят-мўъжизаларимиз кўз очгувчи бўлиб келганида, «Бу очиқ-ойдин сеҳрдир», дедилар'}, {'chapter': 27, 'verse': 14, 'text': 'وَجَحَدُواْ بِهَا وَٱسۡتَيۡقَنَتۡهَآ أَنفُسُهُمۡ ظُلۡمٗا وَعُلُوّٗاۚ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ\nВа ўзлари аниқ билиб туриб, зулм ва кибр туфайли у(мўъжиза)ларни инкор этдилар. Бузғунчиларнинг оқибати қандай бўлишига назар сол'}, {'chapter': 27, 'verse': 15, 'text': 'وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ وَسُلَيۡمَٰنَ عِلۡمٗاۖ وَقَالَا ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي فَضَّلَنَا عَلَىٰ كَثِيرٖ مِّنۡ عِبَادِهِ ٱلۡمُؤۡمِنِينَ\nБатаҳқиқ, Биз Довуд ва Сулаймонга илм бердик. Икковлари: «Бизни кўпгина мўмин бандаларидан афзал қилган Аллоҳга ҳамдлар бўлсин», дедилар'}, {'chapter': 27, 'verse': 16, 'text': 'وَوَرِثَ سُلَيۡمَٰنُ دَاوُۥدَۖ وَقَالَ يَـٰٓأَيُّهَا ٱلنَّاسُ عُلِّمۡنَا مَنطِقَ ٱلطَّيۡرِ وَأُوتِينَا مِن كُلِّ شَيۡءٍۖ إِنَّ هَٰذَا لَهُوَ ٱلۡفَضۡلُ ٱلۡمُبِينُ\nВа Сулаймон Довудга ворис бўлди. У: «Эй одамлар, бизга қуш тили ўргатилди ва ҳар бир нарсадан берилди. Бу, албатта, очиқ-ойдин фазлдир», деди'}, {'chapter': 27, 'verse': 17, 'text': 'وَحُشِرَ لِسُلَيۡمَٰنَ جُنُودُهُۥ مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ وَٱلطَّيۡرِ فَهُمۡ يُوزَعُونَ\nСулаймонга жин, инс ва қушлардан бўлган аскарлари тўпланиб, тизилган ҳолда турдилар'}, {'chapter': 27, 'verse': 18, 'text': 'حَتَّىٰٓ إِذَآ أَتَوۡاْ عَلَىٰ وَادِ ٱلنَّمۡلِ قَالَتۡ نَمۡلَةٞ يَـٰٓأَيُّهَا ٱلنَّمۡلُ ٱدۡخُلُواْ مَسَٰكِنَكُمۡ لَا يَحۡطِمَنَّكُمۡ سُلَيۡمَٰنُ وَجُنُودُهُۥ وَهُمۡ لَا يَشۡعُرُونَ\nТоки улар чумолилар водийсига келганларида, бир чумоли: «Эй, чумолилар, масканларингга киринглар, Сулаймон ва унинг аскарлари сизларни билмасдан эзиб юбормасинлар», деди'}, {'chapter': 27, 'verse': 19, 'text': 'فَتَبَسَّمَ ضَاحِكٗا مِّن قَوۡلِهَا وَقَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَدۡخِلۡنِي بِرَحۡمَتِكَ فِي عِبَادِكَ ٱلصَّـٰلِحِينَ\nБас, у унинг сўзидан табассум қилди ва: «Роббим, мени Ўзинг менга ва ота-онамга берган неъматларингга шукр этишимга ва Сен рози бўладиган солиҳ амаллар қилишимга муяссар этгин. Ўз раҳматинг ила мени солиҳ бандаларингга қўшгин», деди'}, {'chapter': 27, 'verse': 20, 'text': 'وَتَفَقَّدَ ٱلطَّيۡرَ فَقَالَ مَالِيَ لَآ أَرَى ٱلۡهُدۡهُدَ أَمۡ كَانَ مِنَ ٱلۡغَآئِبِينَ\nВа қушларни текширди-да: «Нега Ҳудҳудни кўрмаётирман ёки ғойиблардан бўлдими?» деди'}, {'chapter': 27, 'verse': 21, 'text': 'لَأُعَذِّبَنَّهُۥ عَذَابٗا شَدِيدًا أَوۡ لَأَاْذۡبَحَنَّهُۥٓ أَوۡ لَيَأۡتِيَنِّي بِسُلۡطَٰنٖ مُّبِينٖ\n«Албатта, уни шиддатли азоб-ла азобларман ёки сўйиб юборурман ёхуд менга, албатта, очиқ-ойдин ҳужжат келтиражак.»'}, {'chapter': 27, 'verse': 22, 'text': 'فَمَكَثَ غَيۡرَ بَعِيدٖ فَقَالَ أَحَطتُ بِمَا لَمۡ تُحِطۡ بِهِۦ وَجِئۡتُكَ مِن سَبَإِۭ بِنَبَإٖ يَقِينٍ\nКўп ўтмасдан у келиб: «Сен билмаган нарсани билдим ва сенга Сабаъдан ишончли бир хабар олиб келдим'}, {'chapter': 27, 'verse': 23, 'text': 'إِنِّي وَجَدتُّ ٱمۡرَأَةٗ تَمۡلِكُهُمۡ وَأُوتِيَتۡ مِن كُلِّ شَيۡءٖ وَلَهَا عَرۡشٌ عَظِيمٞ\nДарҳақиқат, мен бир аёлни кўрдим. У уларнинг маликаси экан. Унга ҳар нарса берилган экан. Ҳамда унинг улуғ тахти бор экан'}, {'chapter': 27, 'verse': 24, 'text': 'وَجَدتُّهَا وَقَوۡمَهَا يَسۡجُدُونَ لِلشَّمۡسِ مِن دُونِ ٱللَّهِ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِيلِ فَهُمۡ لَا يَهۡتَدُونَ\nМен у(аёл)нинг ва қавмининг Аллоҳни қўйиб, қуёшга сажда қилаётганларини ва шайтон уларга амалларини зийнатлаб, йўлдан тўсаётганини кўрдим. Бас, улар ҳидоят топмаслар'}, {'chapter': 27, 'verse': 25, 'text': 'أَلَّاۤ يَسۡجُدُواْۤ لِلَّهِ ٱلَّذِي يُخۡرِجُ ٱلۡخَبۡءَ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَيَعۡلَمُ مَا تُخۡفُونَ وَمَا تُعۡلِنُونَ\nОсмонлару ердаги сирларни ошкор қиладиган, махфий ва ошкор қилган нарсаларингизни биладиган Аллоҳга сажда қилмайдиларми'}, {'chapter': 27, 'verse': 26, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ۩\nАллоҳ — Ундан ўзга ҳеч қандай илоҳ йўқ Зотдир. У улуғ аршнинг Роббидир», деди'}, {'chapter': 27, 'verse': 27, 'text': '۞قَالَ سَنَنظُرُ أَصَدَقۡتَ أَمۡ كُنتَ مِنَ ٱلۡكَٰذِبِينَ\nУ: «Кўрамиз, рост сўзладингми ёки ёлғончилардан бўлдингми'}, {'chapter': 27, 'verse': 28, 'text': 'ٱذۡهَب بِّكِتَٰبِي هَٰذَا فَأَلۡقِهۡ إِلَيۡهِمۡ ثُمَّ تَوَلَّ عَنۡهُمۡ فَٱنظُرۡ مَاذَا يَرۡجِعُونَ\nБу мактубимни олиб бориб, уларга ташла, сўнгра четга чиқиб назар сол-чи, нима қайтарурлар», деди'}, {'chapter': 27, 'verse': 29, 'text': 'قَالَتۡ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ إِنِّيٓ أُلۡقِيَ إِلَيَّ كِتَٰبٞ كَرِيمٌ\nУ (Малика): «Эй, аъёнлар! Менга кароматли бир мактуб ташланди'}, {'chapter': 27, 'verse': 30, 'text': 'إِنَّهُۥ مِن سُلَيۡمَٰنَ وَإِنَّهُۥ بِسۡمِ ٱللَّهِ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\nАлбатта, у Сулаймондандир, албатта, унда: «Бисмиллаҳир роҳманир роҳийм'}, {'chapter': 27, 'verse': 31, 'text': 'أَلَّا تَعۡلُواْ عَلَيَّ وَأۡتُونِي مُسۡلِمِينَ\nМенга қарши бош кўтармай, таслим бўлган ҳолингизда келинг», дейилмишдир», деди'}, {'chapter': 27, 'verse': 32, 'text': 'قَالَتۡ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ أَفۡتُونِي فِيٓ أَمۡرِي مَا كُنتُ قَاطِعَةً أَمۡرًا حَتَّىٰ تَشۡهَدُونِ\nУ (Малика): «Эй, аъёнлар! Менга бу ишимда фатво (маслаҳат) беринглар. Сизлар шоҳид бўлмагунингизча, мен бир ишга қатъий қарор қилмасман», деди'}, {'chapter': 27, 'verse': 33, 'text': 'قَالُواْ نَحۡنُ أُوْلُواْ قُوَّةٖ وَأُوْلُواْ بَأۡسٖ شَدِيدٖ وَٱلۡأَمۡرُ إِلَيۡكِ فَٱنظُرِي مَاذَا تَأۡمُرِينَ\nУлар: «Биз куч-қувват эгаларимиз ва шиддатли азму шижоат эгаларимиз. Иш ўзингга ҳавола. Нима амр қилишингни ўйлаб кўр», дедилар'}, {'chapter': 27, 'verse': 34, 'text': 'قَالَتۡ إِنَّ ٱلۡمُلُوكَ إِذَا دَخَلُواْ قَرۡيَةً أَفۡسَدُوهَا وَجَعَلُوٓاْ أَعِزَّةَ أَهۡلِهَآ أَذِلَّةٗۚ وَكَذَٰلِكَ يَفۡعَلُونَ\nУ: «Албатта, подшоҳлар бирор шаҳар-қишлоққа кирсалар, уни вайрон қилурлар ва унинг азиз аҳлларини хор қилурлар. Ана шундай қилурлар'}, {'chapter': 27, 'verse': 35, 'text': 'وَإِنِّي مُرۡسِلَةٌ إِلَيۡهِم بِهَدِيَّةٖ فَنَاظِرَةُۢ بِمَ يَرۡجِعُ ٱلۡمُرۡسَلُونَ\nМен уларга бир ҳадя юборурман, кўрай-чи, элчилар нима билан қайтар эканлар», деди'}, {'chapter': 27, 'verse': 36, 'text': 'فَلَمَّا جَآءَ سُلَيۡمَٰنَ قَالَ أَتُمِدُّونَنِ بِمَالٖ فَمَآ ءَاتَىٰنِۦَ ٱللَّهُ خَيۡرٞ مِّمَّآ ءَاتَىٰكُمۚ بَلۡ أَنتُم بِهَدِيَّتِكُمۡ تَفۡرَحُونَ\nҚачонки, (элчиси) Сулаймонга келганида, у: «Сизлар менга мол-дунё ила мадад бермоқчимисиз?! Бас, Аллоҳ менга берган нарса сизга берган нарсадан яхшидир. Аммо сизлар ҳадяларингиз билан қувонурсиз'}, {'chapter': 27, 'verse': 37, 'text': 'ٱرۡجِعۡ إِلَيۡهِمۡ فَلَنَأۡتِيَنَّهُم بِجُنُودٖ لَّا قِبَلَ لَهُم بِهَا وَلَنُخۡرِجَنَّهُم مِّنۡهَآ أَذِلَّةٗ وَهُمۡ صَٰغِرُونَ\n(Эй элчи!) Сен уларга қайтиб бор. Бас, албатта, биз улар бас кела олмайдиган қўшин ила борурмиз ва у ердан уларни хору зор ҳолларида чиқарурмиз», деди'}, {'chapter': 27, 'verse': 38, 'text': 'قَالَ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ أَيُّكُمۡ يَأۡتِينِي بِعَرۡشِهَا قَبۡلَ أَن يَأۡتُونِي مُسۡلِمِينَ\nУ (Сулаймон): «Эй аъёнлар! Улар менга таслим бўлиб келишларидан олдин қайсингиз ҳузуримга у(малика)нинг тахтини келтира олади?» деди'}, {'chapter': 27, 'verse': 39, 'text': 'قَالَ عِفۡرِيتٞ مِّنَ ٱلۡجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن تَقُومَ مِن مَّقَامِكَۖ وَإِنِّي عَلَيۡهِ لَقَوِيٌّ أَمِينٞ\nЖинлардан бўлган Ифрит: «Мен уни сенга мақомингдан тургунингча келтирурман. Албатта, бунга қувватим ва ишончим бор», деди'}, {'chapter': 27, 'verse': 40, 'text': 'قَالَ ٱلَّذِي عِندَهُۥ عِلۡمٞ مِّنَ ٱلۡكِتَٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن يَرۡتَدَّ إِلَيۡكَ طَرۡفُكَۚ فَلَمَّا رَءَاهُ مُسۡتَقِرًّا عِندَهُۥ قَالَ هَٰذَا مِن فَضۡلِ رَبِّي لِيَبۡلُوَنِيٓ ءَأَشۡكُرُ أَمۡ أَكۡفُرُۖ وَمَن شَكَرَ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ رَبِّي غَنِيّٞ كَرِيمٞ\nКитобдан илми бор шахс: «Мен уни сенга кўзингни очиб юмгунингча келтирурман», деди. Қачонки (Сулаймон) у(тахт)нинг ўз ҳузурида қарор топганини кўргач: «Бу Роббимнинг фазлидандир. Мен шукр қиламанми ёки куфрони неъмат қиламанми, синаш учундир. Ким шукр келтирса, ўзи учунгина шукр қилур. Ким куфрони неъмат қилса, бас, Роббим беҳожат ва карамли Зотдир», деди'}, {'chapter': 27, 'verse': 41, 'text': 'قَالَ نَكِّرُواْ لَهَا عَرۡشَهَا نَنظُرۡ أَتَهۡتَدِيٓ أَمۡ تَكُونُ مِنَ ٱلَّذِينَ لَا يَهۡتَدُونَ\nУ (Сулаймон): «Унинг тахтини танимайдиган қилиб қўйинг. Кўрайлик-чи, у(малика) уни танирми ёки танимайдиганлардан бўлурми?» деди'}, {'chapter': 27, 'verse': 42, 'text': 'فَلَمَّا جَآءَتۡ قِيلَ أَهَٰكَذَا عَرۡشُكِۖ قَالَتۡ كَأَنَّهُۥ هُوَۚ وَأُوتِينَا ٱلۡعِلۡمَ مِن قَبۡلِهَا وَكُنَّا مُسۡلِمِينَ\nҚачонки у келганида: «Сенинг тахтинг шундайми?» дейилди. У: «Худди ўшанинг ўзи», деди. «Бизга бундан илгари илм берилган эди ва мусулмон бўлган эдик»'}, {'chapter': 27, 'verse': 43, 'text': 'وَصَدَّهَا مَا كَانَت تَّعۡبُدُ مِن دُونِ ٱللَّهِۖ إِنَّهَا كَانَتۡ مِن قَوۡمٖ كَٰفِرِينَ\nВа у(малика)ни Аллоҳдан ўзга ибодат қилган нарса тўсди. Албатта, у кофир қавмлардан бўлган эди'}, {'chapter': 27, 'verse': 44, 'text': 'قِيلَ لَهَا ٱدۡخُلِي ٱلصَّرۡحَۖ فَلَمَّا رَأَتۡهُ حَسِبَتۡهُ لُجَّةٗ وَكَشَفَتۡ عَن سَاقَيۡهَاۚ قَالَ إِنَّهُۥ صَرۡحٞ مُّمَرَّدٞ مِّن قَوَارِيرَۗ قَالَتۡ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي وَأَسۡلَمۡتُ مَعَ سُلَيۡمَٰنَ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nУ(малика)га: «Саройга кир», дейилди. Қачонки уни кўрганида, сув деб ўйлаб, пойчаларини очди. У(Сулаймон): «Албатта, бу биллурдан силлиқ қилиб ясалган саройдир», деди. У(малика): «Эй Роббим, албатта, мен ўзимга зулм қилиб юрган эканман, Сулаймон ила оламларнинг Робби — Аллоҳга мусулмон бўлдим!» деди'}, {'chapter': 27, 'verse': 45, 'text': 'وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحًا أَنِ ٱعۡبُدُواْ ٱللَّهَ فَإِذَا هُمۡ فَرِيقَانِ يَخۡتَصِمُونَ\nБатаҳқиқ, Самуд қавмига, Аллоҳга ибодат қилинг, деб биродарлари Солиҳни юбордик. Бас, баногоҳ, улар икки гуруҳ бўлиб тортиша бошладилар'}, {'chapter': 27, 'verse': 46, 'text': 'قَالَ يَٰقَوۡمِ لِمَ تَسۡتَعۡجِلُونَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِۖ لَوۡلَا تَسۡتَغۡفِرُونَ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ\nУ (Солиҳ): «Эй қавмим, нима учун яхшиликдан олдин ёмонликка ошиқасиз?! Аллоҳдан мағфират сўрасангиз-чи?! Шояд раҳим қилинсангиз», деди'}, {'chapter': 27, 'verse': 47, 'text': 'قَالُواْ ٱطَّيَّرۡنَا بِكَ وَبِمَن مَّعَكَۚ قَالَ طَـٰٓئِرُكُمۡ عِندَ ٱللَّهِۖ بَلۡ أَنتُمۡ قَوۡمٞ تُفۡتَنُونَ\nУлар: «Сендан ва сен билан бўлганлардан шумландик», дедилар. У: «Шумланишингиз Аллоҳнинг ҳузуридадир. Балки сизлар синалаётган қавмсиз», деди'}, {'chapter': 27, 'verse': 48, 'text': 'وَكَانَ فِي ٱلۡمَدِينَةِ تِسۡعَةُ رَهۡطٖ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ\nУ шаҳарда тўққиз нафарли гуруҳ бор бўлиб, ер юзида бузғунчилик қилишар, ислоҳ қилмас эдилар'}, {'chapter': 27, 'verse': 49, 'text': 'قَالُواْ تَقَاسَمُواْ بِٱللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهۡلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدۡنَا مَهۡلِكَ أَهۡلِهِۦ وَإِنَّا لَصَٰدِقُونَ\nУлар: «Аллоҳнинг номига қасам ичинглар! Албатта, у (Солиҳ)ни ва унинг аҳлини тунда ўлдирамиз, сўнгра валийига, биз унинг аҳлининг ҳалокатига гувоҳ эмасмиз, албатта, биз ростгўйлармиз, деймиз», дедилар'}, {'chapter': 27, 'verse': 50, 'text': 'وَمَكَرُواْ مَكۡرٗا وَمَكَرۡنَا مَكۡرٗا وَهُمۡ لَا يَشۡعُرُونَ\nУлар макр қилдилар. Биз ҳам улар сезмаган ҳолда бир «макр» қилдик'}, {'chapter': 27, 'verse': 51, 'text': 'فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ مَكۡرِهِمۡ أَنَّا دَمَّرۡنَٰهُمۡ وَقَوۡمَهُمۡ أَجۡمَعِينَ\nНазар сол! Уларнинг макри оқибати нима бўлди?! Биз, албатта, уларни ва қавмларини, ҳаммаларини ер билан яксон қилдик'}, {'chapter': 27, 'verse': 52, 'text': 'فَتِلۡكَ بُيُوتُهُمۡ خَاوِيَةَۢ بِمَا ظَلَمُوٓاْۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡلَمُونَ\nАна, зулмлари сабабли уйлари ҳувиллаб ётибди! Албатта, бунда биладиган қавмлар учун ибрат бордир'}, {'chapter': 27, 'verse': 53, 'text': 'وَأَنجَيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nВа иймон келтириб, тақво қилиб юрганларга нажот бердик'}, {'chapter': 27, 'verse': 54, 'text': 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ وَأَنتُمۡ تُبۡصِرُونَ\nВа Лутни ҳам (Пайғамбар қилиб юбордик). Ўшанда у ўз қавмига: «Сиз кўриб-билиб туриб, фоҳиша иш қиласизми'}, {'chapter': 27, 'verse': 55, 'text': 'أَئِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ تَجۡهَلُونَ\nСиз, албатта, аёлларни қўйиб, эркакларга шаҳват ила яқинлик қиласизми?! Йўқ! Сиз жоҳиллик қилаётган қавмдирсиз!» деди'}, {'chapter': 27, 'verse': 56, 'text': '۞فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوٓاْ ءَالَ لُوطٖ مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ\nУнинг қавмининг жавоби: «Лут аҳлини юртингиздан қувиб чиқаринг, чунки улар покиза бўлишни хоҳлаган кишилардир», дейишдан бошқа нарса бўлмади'}, {'chapter': 27, 'verse': 57, 'text': 'فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَٰهَا مِنَ ٱلۡغَٰبِرِينَ\nБас, Биз унга ва хотинидан бошқа аҳлига нажот бердик. У(хотин)нинг азобда қолгувчилар ичида бўлишини тақдир қилдик'}, {'chapter': 27, 'verse': 58, 'text': 'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ\nВа уларнинг устларига бир ёмғир ёғдирдикки, бас, огоҳлантирилганларнинг азоби қандай ҳам ёмон бўлди'}, {'chapter': 27, 'verse': 59, 'text': 'قُلِ ٱلۡحَمۡدُ لِلَّهِ وَسَلَٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِينَ ٱصۡطَفَىٰٓۗ ءَآللَّهُ خَيۡرٌ أَمَّا يُشۡرِكُونَ\nСен: «Аллоҳга ҳамд бўлсин ва У танлаб олган бандаларига салом бўлсин. Аллоҳ яхшими ёки улар ширк келтираётган нарсаларми?» дегин'}, {'chapter': 27, 'verse': 60, 'text': 'أَمَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ لَكُم مِّنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهۡجَةٖ مَّا كَانَ لَكُمۡ أَن تُنۢبِتُواْ شَجَرَهَآۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ هُمۡ قَوۡمٞ يَعۡدِلُونَ\nЁки осмонлару ерни яратган, сизларга осмондан сув туширган Зотми? Бас, Биз у(сув) билан гўзал боғлар ўстирдик. Сиз ўзингиз унинг дарахтини ўстира олмас эдингиз. Аллоҳ билан бирга бошқа илоҳ борми?! Йўқ, улар ўзлари тенглаштирадиган қавмдирлар'}, {'chapter': 27, 'verse': 61, 'text': 'أَمَّن جَعَلَ ٱلۡأَرۡضَ قَرَارٗا وَجَعَلَ خِلَٰلَهَآ أَنۡهَٰرٗا وَجَعَلَ لَهَا رَوَٰسِيَ وَجَعَلَ بَيۡنَ ٱلۡبَحۡرَيۡنِ حَاجِزًاۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nЁки ерни қароргоҳ этган, унинг узра анҳорлар қилган, унга тоғлар қилган ва икки денгиз ўртасида тўсиқ қилган Зотми?! Аллоҳ билан бирга бошқа илоҳ борми?! Йўқ, уларнинг кўплари билмаслар'}, {'chapter': 27, 'verse': 62, 'text': 'أَمَّن يُجِيبُ ٱلۡمُضۡطَرَّ إِذَا دَعَاهُ وَيَكۡشِفُ ٱلسُّوٓءَ وَيَجۡعَلُكُمۡ خُلَفَآءَ ٱلۡأَرۡضِۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ قَلِيلٗا مَّا تَذَكَّرُونَ\nЁки музтар(ночор) одам дуо қилганда ижобат этиб, унинг мушкулини осон қилган ҳамда сизни ер юзида халифа қилган Зот яхшими?! Аллоҳ билан бирга бошқа илоҳ борми? Қанчалик оз эслайсизлар-а'}, {'chapter': 27, 'verse': 63, 'text': 'أَمَّن يَهۡدِيكُمۡ فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ وَمَن يُرۡسِلُ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦٓۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ تَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ\nЁки сизларни қуруқлик ва денгиз зулматларида тўғри йўлга соладиган ҳамда Ўз раҳмати олдидан шамолларни хушхабар қилиб юборадиган Зотми?! Аллоҳ билан бирга бошқа илоҳ борми?! Аллоҳ улар ширк келтираётган нарсалардан олийдир'}, {'chapter': 27, 'verse': 64, 'text': 'أَمَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nЁки аввал бошдан яратадиган, сўнгра яна қайтадан яратадиган ва сизларни осмонлару ердан ризқлантирадиган Зот яхшими? Аллоҳ билан бирга бошқа илоҳ борми?! «Агар ростгўй бўлсангиз, ҳужжатингизни келтиринг», дегин'}, {'chapter': 27, 'verse': 65, 'text': 'قُل لَّا يَعۡلَمُ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلۡغَيۡبَ إِلَّا ٱللَّهُۚ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ\nСен: «Осмонлару ерда Аллоҳдан бошқа ҳеч ким ғайбни билмас», деб айт. Улар қачон қайта тирилтирилишларини ҳам сезмаслар'}, {'chapter': 27, 'verse': 66, 'text': 'بَلِ ٱدَّـٰرَكَ عِلۡمُهُمۡ فِي ٱلۡأٓخِرَةِۚ بَلۡ هُمۡ فِي شَكّٖ مِّنۡهَاۖ بَلۡ هُم مِّنۡهَا عَمُونَ\nБалки илмлари охиратни идрок қилгандир? Йўқ, улар ундан шак-шубҳададирлар. Йўқ, улар ундан кўрдирлар'}, {'chapter': 27, 'verse': 67, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ أَءِذَا كُنَّا تُرَٰبٗا وَءَابَآؤُنَآ أَئِنَّا لَمُخۡرَجُونَ\nКуфр келтирганлар: «Биз ва ота-боболаримиз тупроқ бўлганимиздан кейин, яна чиқариламизми'}, {'chapter': 27, 'verse': 68, 'text': 'لَقَدۡ وُعِدۡنَا هَٰذَا نَحۡنُ وَءَابَآؤُنَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nБатаҳқиқ, бу бизга-да, ота-боболаримизга-да олдин ҳам ваъда қилинган эди. Бу, аввалгиларнинг афсонасидан бошқа ҳеч нарса эмас», дедилар'}, {'chapter': 27, 'verse': 69, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ\nСен: «Ер юзида сайр қилинглар ва жиноятчиларнинг оқибати қандай бўлганига назар солинглар», деб айт'}, {'chapter': 27, 'verse': 70, 'text': 'وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُن فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ\nСен улар ҳақида ғам чекма ва қилаётган макрларидан ҳам сиқилма'}, {'chapter': 27, 'verse': 71, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангиз, бу ваъда қачон бўлади?» дерлар'}, {'chapter': 27, 'verse': 72, 'text': 'قُلۡ عَسَىٰٓ أَن يَكُونَ رَدِفَ لَكُم بَعۡضُ ٱلَّذِي تَسۡتَعۡجِلُونَ\nСен: «Сиз келишига ошиқаётган нарсанинг баъзиси ортингизга мингашиб олган бўлса, ажаб эмас», деб айт'}, {'chapter': 27, 'verse': 73, 'text': 'وَإِنَّ رَبَّكَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ\nАлбатта, Роббинг одамларга нисбатан фазлу марҳамат соҳибидир. Лекин уларнинг кўплари шукр қилмаслар'}, {'chapter': 27, 'verse': 74, 'text': 'وَإِنَّ رَبَّكَ لَيَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ\nВа, албатта, Роббинг уларнинг диллари беркитган нарсани ҳам, ошкор қилган нарсани ҳам билур'}, {'chapter': 27, 'verse': 75, 'text': 'وَمَا مِنۡ غَآئِبَةٖ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِلَّا فِي كِتَٰبٖ مُّبِينٍ\nОсмону ерда ҳеч бир ғайб нарса йўқ, илло у очиқ-ойдин китобда бордир'}, {'chapter': 27, 'verse': 76, 'text': 'إِنَّ هَٰذَا ٱلۡقُرۡءَانَ يَقُصُّ عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَكۡثَرَ ٱلَّذِي هُمۡ فِيهِ يَخۡتَلِفُونَ\nАлбатта, ушбу Қуръон Бани Исроилга улар ихтилоф қилаётган нарсаларнинг кўпини айтиб берадир'}, {'chapter': 27, 'verse': 77, 'text': 'وَإِنَّهُۥ لَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ\nАлбатта, у (Қуръон) мўминлар учун ҳидоят ва раҳматдир'}, {'chapter': 27, 'verse': 78, 'text': 'إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُم بِحُكۡمِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡعَلِيمُ\nАлбатта, Роббинг улар ўртасида Ўз ҳукми ила ажрим чиқаради. У азийз ва билгувчидир'}, {'chapter': 27, 'verse': 79, 'text': 'فَتَوَكَّلۡ عَلَى ٱللَّهِۖ إِنَّكَ عَلَى ٱلۡحَقِّ ٱلۡمُبِينِ\nБас, сен Аллоҳга таваккал қил. Албатта, сен очиқ-ойдин ҳақдадирсан'}, {'chapter': 27, 'verse': 80, 'text': 'إِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوۡاْ مُدۡبِرِينَ\nАлбатта, сен ўликларга эшиттира олмайсан, ортга қараб қочаётган карларга ҳам чақириқни эшиттира олмайсан'}, {'chapter': 27, 'verse': 81, 'text': 'وَمَآ أَنتَ بِهَٰدِي ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ\nСен кўрларни залолатдан ҳидоятга солгувчи эмассан. Сен фақат мусулмон бўлган ҳолларида оятларимизга иймон келтирадиганларгагина эшиттира олурсан'}, {'chapter': 27, 'verse': 82, 'text': '۞وَإِذَا وَقَعَ ٱلۡقَوۡلُ عَلَيۡهِمۡ أَخۡرَجۡنَا لَهُمۡ دَآبَّةٗ مِّنَ ٱلۡأَرۡضِ تُكَلِّمُهُمۡ أَنَّ ٱلنَّاسَ كَانُواْ بِـَٔايَٰتِنَا لَا يُوقِنُونَ\nҚачонки уларнинг бошига сўз(ланган азоб) тушганида, Биз улар учун ердан бир жонивор чиқарамиз. У уларга, албатта, одамлар Бизнинг оятларимизга ишонмайдиган бўлганларини айтиб беради'}, {'chapter': 27, 'verse': 83, 'text': 'وَيَوۡمَ نَحۡشُرُ مِن كُلِّ أُمَّةٖ فَوۡجٗا مِّمَّن يُكَذِّبُ بِـَٔايَٰتِنَا فَهُمۡ يُوزَعُونَ\nБиз ҳар умматнинг оятларимизни ёлғонга чиқарганларидан бир гуруҳини тўплаган кунда улар жамланиб-тизилиб туришар'}, {'chapter': 27, 'verse': 84, 'text': 'حَتَّىٰٓ إِذَا جَآءُو قَالَ أَكَذَّبۡتُم بِـَٔايَٰتِي وَلَمۡ تُحِيطُواْ بِهَا عِلۡمًا أَمَّاذَا كُنتُمۡ تَعۡمَلُونَ\nТоки улар келиб бўлгач, У зот: «Бизнинг оятларимизни ёлғонга чиқардингизми?! Уларни пухта англамай туриб-а? Йўқса, нима қилган эдингиз?» дер'}, {'chapter': 27, 'verse': 85, 'text': 'وَوَقَعَ ٱلۡقَوۡلُ عَلَيۡهِم بِمَا ظَلَمُواْ فَهُمۡ لَا يَنطِقُونَ\nВа уларга, зулм қилганлари сабабли, сўз(ланган азоб) тушар. Бас, улар сўзлай олмаслар'}, {'chapter': 27, 'verse': 86, 'text': 'أَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا ٱلَّيۡلَ لِيَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nБиз сокинлик топишлари учун кечани, кўришлари учун кундузни қилганимизни кўрмадиларми?! Албатта, бунда иймон келтирадиган қавмлар учун ибратлар бордир'}, {'chapter': 27, 'verse': 87, 'text': 'وَيَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَفَزِعَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۚ وَكُلٌّ أَتَوۡهُ دَٰخِرِينَ\nСур чалиниб, Аллоҳ хоҳлаганидан бошқа осмонлардаги кимсалар ва ердаги кимсалар даҳшатга тушган кунда ҳамма У(зот)га бўйин эгиб келурлар'}, {'chapter': 27, 'verse': 88, 'text': 'وَتَرَى ٱلۡجِبَالَ تَحۡسَبُهَا جَامِدَةٗ وَهِيَ تَمُرُّ مَرَّ ٱلسَّحَابِۚ صُنۡعَ ٱللَّهِ ٱلَّذِيٓ أَتۡقَنَ كُلَّ شَيۡءٍۚ إِنَّهُۥ خَبِيرُۢ بِمَا تَفۡعَلُونَ\nТоғларни кўриб, қимирламай турибди деб ҳисоблайсан. Ҳолбуки, улар булутнинг ўтишидек ўтурлар. Бу ҳар бир нарсани пухта қиладиган Аллоҳнинг санъатидир. Албатта, У нима қилаётганингиздан хабардордир'}, {'chapter': 27, 'verse': 89, 'text': 'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَا وَهُم مِّن فَزَعٖ يَوۡمَئِذٍ ءَامِنُونَ\nКим яхшилик қилса, унга ундан кўра яхшироқ (мукофот) бўлур. Ҳамда улар ўша куннинг даҳшатидан омондадирлар'}, {'chapter': 27, 'verse': 90, 'text': 'وَمَن جَآءَ بِٱلسَّيِّئَةِ فَكُبَّتۡ وُجُوهُهُمۡ فِي ٱلنَّارِ هَلۡ تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\nКим ёмонлик қилса, бас, юзтубан дўзахга ташланурлар ва (уларга): «Сизларга фақат қилган амалларингиз жазоси берилмоқда, холос», (дейилур)'}, {'chapter': 27, 'verse': 91, 'text': 'إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ رَبَّ هَٰذِهِ ٱلۡبَلۡدَةِ ٱلَّذِي حَرَّمَهَا وَلَهُۥ كُلُّ شَيۡءٖۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ\nМен ушбу шаҳарнинг Роббига, уни ҳарам қилган Зотга ибодат қилишга амр этилдим, холос. Ҳамма нарса Уникидир. Ва мусулмонлардан бўлишга амр этилдим'}, {'chapter': 27, 'verse': 92, 'text': 'وَأَنۡ أَتۡلُوَاْ ٱلۡقُرۡءَانَۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَقُلۡ إِنَّمَآ أَنَا۠ مِنَ ٱلۡمُنذِرِينَ\nВа Қуръонни тиловат қилишга (амр этилдим). Ким ҳидоят топса, фақат ўзи учун ҳидоят топадир. Ким залолатга кетса, унга: «Мен огоҳлантиргувчиларданман, холос», дегин'}, {'chapter': 27, 'verse': 93, 'text': 'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ سَيُرِيكُمۡ ءَايَٰتِهِۦ فَتَعۡرِفُونَهَاۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nСен: «Аллоҳга ҳамд бўлсин! У сизларга Ўз оят-мўъжизаларини кўрсатур. Сиз уларни кўриб, танийсиз», деб айт. Роббинг қилаётган амалларингиздан ғофил эмасдир'}], '28': [{'chapter': 28, 'verse': 1, 'text': 'طسٓمٓ\nТо. Сийн. Мийм'}, {'chapter': 28, 'verse': 2, 'text': 'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ\nУшбулар очиқ-ойдин китоб оятларидир'}, {'chapter': 28, 'verse': 3, 'text': 'نَتۡلُواْ عَلَيۡكَ مِن نَّبَإِ مُوسَىٰ وَفِرۡعَوۡنَ بِٱلۡحَقِّ لِقَوۡمٖ يُؤۡمِنُونَ\nИймон келтирадиган қавм учун сенга Мусо ва Фиръавннинг хабаридан ҳақ ила тиловат қилиб берамиз'}, {'chapter': 28, 'verse': 4, 'text': 'إِنَّ فِرۡعَوۡنَ عَلَا فِي ٱلۡأَرۡضِ وَجَعَلَ أَهۡلَهَا شِيَعٗا يَسۡتَضۡعِفُ طَآئِفَةٗ مِّنۡهُمۡ يُذَبِّحُ أَبۡنَآءَهُمۡ وَيَسۡتَحۡيِۦ نِسَآءَهُمۡۚ إِنَّهُۥ كَانَ مِنَ ٱلۡمُفۡسِدِينَ\nАлбатта, Фиръавн ер юзида баланд кетди. У ўша(ер)нинг аҳолисини бўлиб ташлади. Улардан бир тоифасини эзар, ўғилларини сўйиб, қизларини тирик қолдирарди. Албатта, у бузғунчилардан бўлган эди'}, {'chapter': 28, 'verse': 5, 'text': 'وَنُرِيدُ أَن نَّمُنَّ عَلَى ٱلَّذِينَ ٱسۡتُضۡعِفُواْ فِي ٱلۡأَرۡضِ وَنَجۡعَلَهُمۡ أَئِمَّةٗ وَنَجۡعَلَهُمُ ٱلۡوَٰرِثِينَ\nБиз эса, ер юзида эзилганларга беминнат неъмат беришни, уларни пешво қилишни, уларни ворислар қилишни ва ер юзида уларга имконлар беришни'}, {'chapter': 28, 'verse': 6, 'text': 'وَنُمَكِّنَ لَهُمۡ فِي ٱلۡأَرۡضِ وَنُرِيَ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا مِنۡهُم مَّا كَانُواْ يَحۡذَرُونَ\nФиръавн, Ҳомон ва икковининг аскарларига улар(Бани Исроил)дан қўрққан нарсаларини кўрсатишни истаймиз'}, {'chapter': 28, 'verse': 7, 'text': 'وَأَوۡحَيۡنَآ إِلَىٰٓ أُمِّ مُوسَىٰٓ أَنۡ أَرۡضِعِيهِۖ فَإِذَا خِفۡتِ عَلَيۡهِ فَأَلۡقِيهِ فِي ٱلۡيَمِّ وَلَا تَخَافِي وَلَا تَحۡزَنِيٓۖ إِنَّا رَآدُّوهُ إِلَيۡكِ وَجَاعِلُوهُ مِنَ ٱلۡمُرۡسَلِينَ\nВа Мусонинг онасига: «Уни эмизавер. Бас, (унга ёмонлик етишидан) қўрққан чоғингда уни дарёга ташла, қўрқма, хафа бўлма, Биз, албатта, уни сенга қайтаргувчимиз ва Пайғамбарлардан қилгувчимиз», деб ваҳий қилдик'}, {'chapter': 28, 'verse': 8, 'text': 'فَٱلۡتَقَطَهُۥٓ ءَالُ فِرۡعَوۡنَ لِيَكُونَ لَهُمۡ عَدُوّٗا وَحَزَنًاۗ إِنَّ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا كَانُواْ خَٰطِـِٔينَ\nБас, Фиръавн оиласи уни ўзларига душман ва ғам-ғусса бўлиши учун тутиб олдилар. Албатта, Фиръавн, Ҳомон ва икковларининг аскарлари хатокорлардан бўлган эдилар'}, {'chapter': 28, 'verse': 9, 'text': 'وَقَالَتِ ٱمۡرَأَتُ فِرۡعَوۡنَ قُرَّتُ عَيۡنٖ لِّي وَلَكَۖ لَا تَقۡتُلُوهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗا وَهُمۡ لَا يَشۡعُرُونَ\nФиръавннинг хотини: «(Бу бола) мен учун ҳам, сен учун ҳам кўз қувончидир. Уни ўлдирманглар. Шоядки, у бизга фойда берса ёки уни бола қилиб олсак», деди. Ҳолбуки, улар сезмасдилар'}, {'chapter': 28, 'verse': 10, 'text': 'وَأَصۡبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَٰرِغًاۖ إِن كَادَتۡ لَتُبۡدِي بِهِۦ لَوۡلَآ أَن رَّبَطۡنَا عَلَىٰ قَلۡبِهَا لِتَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nМусонинг онасининг қалби бўшаб қолди. Мўминлардан бўлиши учун қалбини мустаҳкам боғламаганимизда, уни билдириб қўйишига сал қолди'}, {'chapter': 28, 'verse': 11, 'text': 'وَقَالَتۡ لِأُخۡتِهِۦ قُصِّيهِۖ فَبَصُرَتۡ بِهِۦ عَن جُنُبٖ وَهُمۡ لَا يَشۡعُرُونَ\nУ(она) у(Мусо)нинг опасига: «Изидан бор», деди. Бас, уни бир четдан кўрди. Ҳолбуки, улар сезмасдилар'}, {'chapter': 28, 'verse': 12, 'text': '۞وَحَرَّمۡنَا عَلَيۡهِ ٱلۡمَرَاضِعَ مِن قَبۡلُ فَقَالَتۡ هَلۡ أَدُلُّكُمۡ عَلَىٰٓ أَهۡلِ بَيۡتٖ يَكۡفُلُونَهُۥ لَكُمۡ وَهُمۡ لَهُۥ نَٰصِحُونَ\nВа бундан олдин унга эмизгувчиларни ҳаром қилган эдик. Бас, (опаси): «Мен сизларга уни сиз учун кафилликка оладиган ва унга насиҳатгўй бўлгувчи аҳли байтни айтиб берайми?» деди'}, {'chapter': 28, 'verse': 13, 'text': 'فَرَدَدۡنَٰهُ إِلَىٰٓ أُمِّهِۦ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَ وَلِتَعۡلَمَ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nБас, Биз уни онасига кўзи қувончга тўлиши, хафа бўлмаслиги ва, албатта, Аллоҳнинг ваъдаси ҳақ эканини билиши учун қайтардик. Лекин кўплари буни билмаслар'}, {'chapter': 28, 'verse': 14, 'text': 'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسۡتَوَىٰٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nҚачонки у вояга етиб, мукаммал бўлганида, унга ҳикмат ва илмни бердик. Биз яхши амал қилгувчиларни шундай мукофотлармиз'}, {'chapter': 28, 'verse': 15, 'text': 'وَدَخَلَ ٱلۡمَدِينَةَ عَلَىٰ حِينِ غَفۡلَةٖ مِّنۡ أَهۡلِهَا فَوَجَدَ فِيهَا رَجُلَيۡنِ يَقۡتَتِلَانِ هَٰذَا مِن شِيعَتِهِۦ وَهَٰذَا مِنۡ عَدُوِّهِۦۖ فَٱسۡتَغَٰثَهُ ٱلَّذِي مِن شِيعَتِهِۦ عَلَى ٱلَّذِي مِنۡ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَيۡهِۖ قَالَ هَٰذَا مِنۡ عَمَلِ ٱلشَّيۡطَٰنِۖ إِنَّهُۥ عَدُوّٞ مُّضِلّٞ مُّبِينٞ\nВа у шаҳарга унинг аҳолиси ғафлатдалик пайтда кирди. Унда уришаётган икки кишини кўрди. Буниси ўз гуруҳидан, униси эса, душманларидан эди. Бас, ўз гуруҳидан бўлган душман бўлганга қарши ундан ёрдам сўради. Шунда Мусо уни бир мушт уриб ўлдириб қўйди. У(Мусо): «Бу шайтоннинг ишидир. Албатта, у очиқ-ойдин душман ва йўлдан оздиргувчидир», деди'}, {'chapter': 28, 'verse': 16, 'text': 'قَالَ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي فَٱغۡفِرۡ لِي فَغَفَرَ لَهُۥٓۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nУ: «Эй Роббим, албатта, мен ўзимга зулм қилдим. Бас, мени мағфират қил», деди. Шунда У зот уни мағфират қилди. Албатта, Унинг Ўзи мағфират қилгувчи, меҳрибондир'}, {'chapter': 28, 'verse': 17, 'text': 'قَالَ رَبِّ بِمَآ أَنۡعَمۡتَ عَلَيَّ فَلَنۡ أَكُونَ ظَهِيرٗا لِّلۡمُجۡرِمِينَ\nУ: «Эй Роббим, менга берган неъматинг ҳаққи-ҳурмати, ҳаргиз, жиноятчиларга ёрдамчи бўлмасман», деди'}, {'chapter': 28, 'verse': 18, 'text': 'فَأَصۡبَحَ فِي ٱلۡمَدِينَةِ خَآئِفٗا يَتَرَقَّبُ فَإِذَا ٱلَّذِي ٱسۡتَنصَرَهُۥ بِٱلۡأَمۡسِ يَسۡتَصۡرِخُهُۥۚ قَالَ لَهُۥ مُوسَىٰٓ إِنَّكَ لَغَوِيّٞ مُّبِينٞ\nБас, у эрталаб шаҳарда қўрққан ҳолида, аланглаб кетаётган эди, бирдан, кеча ундан ёрдам сўраган яна ёрдам сўраб қичқириб қолди. Мусо унга: «Албатта, сен очиқ-ойдин гумроҳсан», деди'}, {'chapter': 28, 'verse': 19, 'text': 'فَلَمَّآ أَنۡ أَرَادَ أَن يَبۡطِشَ بِٱلَّذِي هُوَ عَدُوّٞ لَّهُمَا قَالَ يَٰمُوسَىٰٓ أَتُرِيدُ أَن تَقۡتُلَنِي كَمَا قَتَلۡتَ نَفۡسَۢا بِٱلۡأَمۡسِۖ إِن تُرِيدُ إِلَّآ أَن تَكُونَ جَبَّارٗا فِي ٱلۡأَرۡضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ ٱلۡمُصۡلِحِينَ\nҚачонки икковларига душман бўлганни тутмоқчи бўлган эди, у: «Эй Мусо, кеча бир жонни ўлдирганинг каби, мени ўлдирмоқчимисан?! Сен ер юзида фақат жаббор бўлишни истарсан ва ислоҳ қилгувчилардан бўлишни истамассан», деди'}, {'chapter': 28, 'verse': 20, 'text': 'وَجَآءَ رَجُلٞ مِّنۡ أَقۡصَا ٱلۡمَدِينَةِ يَسۡعَىٰ قَالَ يَٰمُوسَىٰٓ إِنَّ ٱلۡمَلَأَ يَأۡتَمِرُونَ بِكَ لِيَقۡتُلُوكَ فَٱخۡرُجۡ إِنِّي لَكَ مِنَ ٱلنَّـٰصِحِينَ\nВа шаҳарнинг нариги четидан бир киши шошиб келиб: «Эй Мусо, аниқки, аъёнлар сени ўлдириш учун тил бириктирмоқдалар. Бас, чиқиб кет! Албатта, мен сенга насиҳат қилгувчиларданман», деди'}, {'chapter': 28, 'verse': 21, 'text': 'فَخَرَجَ مِنۡهَا خَآئِفٗا يَتَرَقَّبُۖ قَالَ رَبِّ نَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, хавфсираб, аланглаган ҳолда у ердан: «Эй Роббим, менга золим қавмлардан нажот бергин», деб чиқди'}, {'chapter': 28, 'verse': 22, 'text': 'وَلَمَّا تَوَجَّهَ تِلۡقَآءَ مَدۡيَنَ قَالَ عَسَىٰ رَبِّيٓ أَن يَهۡدِيَنِي سَوَآءَ ٱلسَّبِيلِ\nҚачонки Мадян томон юзланганида: «Шоядки, Роббим мени тўғри йўлга ҳидоят қилса», деди'}, {'chapter': 28, 'verse': 23, 'text': 'وَلَمَّا وَرَدَ مَآءَ مَدۡيَنَ وَجَدَ عَلَيۡهِ أُمَّةٗ مِّنَ ٱلنَّاسِ يَسۡقُونَ وَوَجَدَ مِن دُونِهِمُ ٱمۡرَأَتَيۡنِ تَذُودَانِۖ قَالَ مَا خَطۡبُكُمَاۖ قَالَتَا لَا نَسۡقِي حَتَّىٰ يُصۡدِرَ ٱلرِّعَآءُۖ وَأَبُونَا شَيۡخٞ كَبِيرٞ\nҚачонки Мадян сувига етиб келгач, бир тўп одамларнинг ҳайвонларини суғораётганини кўрди ва улардан бошқа (қўйларини сувдан) қайтараётган икки аёлни кўрди. У: «Сизларга не бўлди?» деди. Улар: «Чўпонлар қайтмагунларича суғора олмаймиз, отамиз қари чол», дедилар'}, {'chapter': 28, 'verse': 24, 'text': 'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰٓ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَآ أَنزَلۡتَ إِلَيَّ مِنۡ خَيۡرٖ فَقِيرٞ\nБас, икковларига (ҳайвонларини) суғориб берди. Сўнгра сояга қайтди ва «Эй Роббим, албатта, менга туширажагинг ҳар бир яхшиликка муҳтожман», деди'}, {'chapter': 28, 'verse': 25, 'text': 'فَجَآءَتۡهُ إِحۡدَىٰهُمَا تَمۡشِي عَلَى ٱسۡتِحۡيَآءٖ قَالَتۡ إِنَّ أَبِي يَدۡعُوكَ لِيَجۡزِيَكَ أَجۡرَ مَا سَقَيۡتَ لَنَاۚ فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيۡهِ ٱلۡقَصَصَ قَالَ لَا تَخَفۡۖ نَجَوۡتَ مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, икковларидан бири ҳаё билан юриб келиб: «Отам сенга бизларга суғориб берганинг ҳақини бериш учун чақирмоқда», деди. Қачонки унга келганида ва қиссани айтиб берганида, у: «Қўрқма, золим қавмлардан нажот топдинг», деди'}, {'chapter': 28, 'verse': 26, 'text': 'قَالَتۡ إِحۡدَىٰهُمَا يَـٰٓأَبَتِ ٱسۡتَـٔۡجِرۡهُۖ إِنَّ خَيۡرَ مَنِ ٱسۡتَـٔۡجَرۡتَ ٱلۡقَوِيُّ ٱلۡأَمِينُ\nИкки(қиз)дан бири: «Эй отажон, уни ишга ёллаб ол. Чунки, ишга ёлланадиган энг яхши кимса кучли ва ишончли кимсадир», деди'}, {'chapter': 28, 'verse': 27, 'text': 'قَالَ إِنِّيٓ أُرِيدُ أَنۡ أُنكِحَكَ إِحۡدَى ٱبۡنَتَيَّ هَٰتَيۡنِ عَلَىٰٓ أَن تَأۡجُرَنِي ثَمَٰنِيَ حِجَجٖۖ فَإِنۡ أَتۡمَمۡتَ عَشۡرٗا فَمِنۡ عِندِكَۖ وَمَآ أُرِيدُ أَنۡ أَشُقَّ عَلَيۡكَۚ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰلِحِينَ\nУ: «Менга саккиз йил ишлаб беришинг шарти билан, шу икки қизимдан бирини сенга никоҳлаб бермоқчиман, агар ўн йилни тамомласанг, бу сенинг ишинг. Мен эса сенга машаққат қилишни истамасман. Иншоаллоҳ, менинг солиҳ кишилардан эканимни кўрасан», деди'}, {'chapter': 28, 'verse': 28, 'text': 'قَالَ ذَٰلِكَ بَيۡنِي وَبَيۡنَكَۖ أَيَّمَا ٱلۡأَجَلَيۡنِ قَضَيۡتُ فَلَا عُدۡوَٰنَ عَلَيَّۖ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ\nУ(Мусо): «Бу мен билан сенинг орамиздадир. Икки муддатдан қай бирини адо қилсам ҳам, менга тажовуз қилинмас. Айтаётган нарсамизга Аллоҳ вакилдир», деди'}, {'chapter': 28, 'verse': 29, 'text': '۞فَلَمَّا قَضَىٰ مُوسَى ٱلۡأَجَلَ وَسَارَ بِأَهۡلِهِۦٓ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارٗاۖ قَالَ لِأَهۡلِهِ ٱمۡكُثُوٓاْ إِنِّيٓ ءَانَسۡتُ نَارٗا لَّعَلِّيٓ ءَاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ جَذۡوَةٖ مِّنَ ٱلنَّارِ لَعَلَّكُمۡ تَصۡطَلُونَ\nМусо муддатни тугатиб, аҳли билан йўлга тушиб кетаётиб, Тур томонда узоқдан оловни кўриб қолди. У аҳлига: «Туриб туринглар, аниқки, мен узоқдан оловни кўрдим. Балки, у ердан сизга бирор хабар ёки исинишингиз учун бир тутам чўғ олиб келарман», деди'}, {'chapter': 28, 'verse': 30, 'text': 'فَلَمَّآ أَتَىٰهَا نُودِيَ مِن شَٰطِيِٕ ٱلۡوَادِ ٱلۡأَيۡمَنِ فِي ٱلۡبُقۡعَةِ ٱلۡمُبَٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن يَٰمُوسَىٰٓ إِنِّيٓ أَنَا ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nБас, қачонки унинг олдига етиб келганида, водийнинг ўнг томонидан, муборак жойдан, дарахтдан унга: «Эй Мусо, албатта, Мен, Ўзим оламларнинг Робби Аллоҳдирман'}, {'chapter': 28, 'verse': 31, 'text': 'وَأَنۡ أَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰٓ أَقۡبِلۡ وَلَا تَخَفۡۖ إِنَّكَ مِنَ ٱلۡأٓمِنِينَ\nВа асоингни ташла», деб нидо қилинди. Қачонки унинг катта илон каби қимирлаётганини кўргач, қарамасдан ортга қочди. «Эй Мусо, бери кел, қўрқма, албатта, сен омонликдагилардансан'}, {'chapter': 28, 'verse': 32, 'text': 'ٱسۡلُكۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖ وَٱضۡمُمۡ إِلَيۡكَ جَنَاحَكَ مِنَ ٱلرَّهۡبِۖ فَذَٰنِكَ بُرۡهَٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nҚўлингни ёқангдан (қўлтиғингга) суқ, бирон нуқсонсиз оппоқ бўлиб чиқур ва қўрқувдан қанот(қўл)ингни ўзингга тортиб ол. Бас, шу иккиси Роббингдан Фиръавн ва унинг одамларига икки ҳужжатдир. Албатта, улар фосиқ қавм бўлдилар», (дейилди)'}, {'chapter': 28, 'verse': 33, 'text': 'قَالَ رَبِّ إِنِّي قَتَلۡتُ مِنۡهُمۡ نَفۡسٗا فَأَخَافُ أَن يَقۡتُلُونِ\nУ: «Эй Роббим, мен улардан бир жонни ўлдирганман, улар мени ўлдиришларидан қўрқаман'}, {'chapter': 28, 'verse': 34, 'text': 'وَأَخِي هَٰرُونُ هُوَ أَفۡصَحُ مِنِّي لِسَانٗا فَأَرۡسِلۡهُ مَعِيَ رِدۡءٗا يُصَدِّقُنِيٓۖ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ\nБиродарим Ҳорун мендан кўра тили бурророқ, уни ҳам мен билан юбор, мени тасдиқлайдир. Зеро, улар мени ёлғончи қилишларидан қўрқурман», деди'}, {'chapter': 28, 'verse': 35, 'text': 'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجۡعَلُ لَكُمَا سُلۡطَٰنٗا فَلَا يَصِلُونَ إِلَيۡكُمَا بِـَٔايَٰتِنَآۚ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلۡغَٰلِبُونَ\nУ зот: «Биз сени оғанг билан қувватлаймиз ва икковингизга бир салтанат берурмизки, улар сизга етиша олмаслар. Бизнинг оятларимиз ила иккингиз ва сизга эргашганлар ғолибдирсиз», деди'}, {'chapter': 28, 'verse': 36, 'text': 'فَلَمَّا جَآءَهُم مُّوسَىٰ بِـَٔايَٰتِنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا سِحۡرٞ مُّفۡتَرٗى وَمَا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ\nБас, қачонки Мусо уларга очиқ-ойдин оятларимиз ила келганида, улар: «Бу уйдирма сеҳрдан бошқа нарса эмас. Аввалги ота-боболаримиздан ҳам бу ҳақда эшитмаган эдик», дедилар'}, {'chapter': 28, 'verse': 37, 'text': 'وَقَالَ مُوسَىٰ رَبِّيٓ أَعۡلَمُ بِمَن جَآءَ بِٱلۡهُدَىٰ مِنۡ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nВа Мусо: «Роббим Ўз ҳузуридан ким ҳидоят келтирганини ҳам, охират диёри оқибати кимга бўлишини ҳам яхши билгувчи Зотдир. Албатта, золимлар нажот топмаслар», деди'}, {'chapter': 28, 'verse': 38, 'text': 'وَقَالَ فِرۡعَوۡنُ يَـٰٓأَيُّهَا ٱلۡمَلَأُ مَا عَلِمۡتُ لَكُم مِّنۡ إِلَٰهٍ غَيۡرِي فَأَوۡقِدۡ لِي يَٰهَٰمَٰنُ عَلَى ٱلطِّينِ فَٱجۡعَل لِّي صَرۡحٗا لَّعَلِّيٓ أَطَّلِعُ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ مِنَ ٱلۡكَٰذِبِينَ\nВа Фиръавн: «Эй, аъёнлар, мен сизларга ўзимдан бошқа илоҳ борлигини билмасман. Бас, эй, Ҳомон, лойга ўт ёқиб, мен учун бир баланд қаср қур, шоядки (унга чиқиб) Мусонинг илоҳини кўрсам. Албатта, мен уни ёлғончилардан деб ўйламоқдаман», деди'}, {'chapter': 28, 'verse': 39, 'text': 'وَٱسۡتَكۡبَرَ هُوَ وَجُنُودُهُۥ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَظَنُّوٓاْ أَنَّهُمۡ إِلَيۡنَا لَا يُرۡجَعُونَ\nУ(Фиръавн) ва унинг аскарлари ер юзида ноҳақдан такаббурлик қилдилар ҳамда ўзларини Бизга қайтарилмаслар деб гумон қилдилар'}, {'chapter': 28, 'verse': 40, 'text': 'فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي ٱلۡيَمِّۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّـٰلِمِينَ\nБас, Биз уни ва унинг аскарларини тутдик ва денгизга отдик. Энди золимларнинг оқибати қандоқ бўлганига назар сол'}, {'chapter': 28, 'verse': 41, 'text': 'وَجَعَلۡنَٰهُمۡ أَئِمَّةٗ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ لَا يُنصَرُونَ\nВа уларни дўзахга чақирадиган пешволар қилдик. Қиёмат куни эса, уларга ёрдам берилмас'}, {'chapter': 28, 'verse': 42, 'text': 'وَأَتۡبَعۡنَٰهُمۡ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ هُم مِّنَ ٱلۡمَقۡبُوحِينَ\nВа бу дунёда уларга лаънатни эргаштириб қўйдик. Қиёмат куни эса, улар (раҳматдан) йироқ қилинганлардандир'}, {'chapter': 28, 'verse': 43, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ مِنۢ بَعۡدِ مَآ أَهۡلَكۡنَا ٱلۡقُرُونَ ٱلۡأُولَىٰ بَصَآئِرَ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُمۡ يَتَذَكَّرُونَ\nБатаҳқиқ, Биз аввалги асрларни ҳалок қилганимиздан сўнг Мусога одамлар учун нур, ҳидоят ва раҳмат қилиб китобни бердик. Шоядки, улар эсласалар'}, {'chapter': 28, 'verse': 44, 'text': 'وَمَا كُنتَ بِجَانِبِ ٱلۡغَرۡبِيِّ إِذۡ قَضَيۡنَآ إِلَىٰ مُوسَى ٱلۡأَمۡرَ وَمَا كُنتَ مِنَ ٱلشَّـٰهِدِينَ\nБиз Мусога ишни ҳукм қилган пайтимизда сен ғарбий томонда эмас эдинг ва гувоҳлардан ҳам бўлмагансан'}, {'chapter': 28, 'verse': 45, 'text': 'وَلَٰكِنَّآ أَنشَأۡنَا قُرُونٗا فَتَطَاوَلَ عَلَيۡهِمُ ٱلۡعُمُرُۚ وَمَا كُنتَ ثَاوِيٗا فِيٓ أَهۡلِ مَدۡيَنَ تَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَا وَلَٰكِنَّا كُنَّا مُرۡسِلِينَ\nЛекин Биз бир неча асрларни пайдо қилдик, бас, уларнинг умри чўзилди. Сен Мадянда туриб, уларга оятларимизни тиловат қилиб берганинг йўқ. Аммо Биз Пайғамбар юборгувчи бўлдик'}, {'chapter': 28, 'verse': 46, 'text': 'وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذۡ نَادَيۡنَا وَلَٰكِن رَّحۡمَةٗ مِّن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nБиз нидо қилган чоғимизда Турнинг ёнида йўқ эдинг. Лекин Роббингдан раҳмат сифатида сендан олдин огоҳлантиргувчи келмаган қавмни огоҳлантириш учун (қиссаларни сенга билдирдик). Шоядки, эсласалар'}, {'chapter': 28, 'verse': 47, 'text': 'وَلَوۡلَآ أَن تُصِيبَهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَيَقُولُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nАгар уларга қилмишлари туфайли мусийбат етганида: «Эй Роббимиз, бизга Пайғамбар юборганингда эди, оятларингга эргашар эдик ва мўминлардан бўлар эдик», демасалар эди'}, {'chapter': 28, 'verse': 48, 'text': 'فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُواْ لَوۡلَآ أُوتِيَ مِثۡلَ مَآ أُوتِيَ مُوسَىٰٓۚ أَوَلَمۡ يَكۡفُرُواْ بِمَآ أُوتِيَ مُوسَىٰ مِن قَبۡلُۖ قَالُواْ سِحۡرَانِ تَظَٰهَرَا وَقَالُوٓاْ إِنَّا بِكُلّٖ كَٰفِرُونَ\nҚачонки уларга бизнинг ҳузуримиздан ҳақ келганида, улар: «Унга ҳам Мусога берилган нарса берилса эди», дедилар. Улар илгари Мусога берилган нарсага куфр келтирмаган эдиларми?! Улар: «Икки сеҳр ўртага чиқди, бир-бирига ёрдам бермоқда, биз ҳар бирига куфр келтиргувчимиз», дедилар'}, {'chapter': 28, 'verse': 49, 'text': 'قُلۡ فَأۡتُواْ بِكِتَٰبٖ مِّنۡ عِندِ ٱللَّهِ هُوَ أَهۡدَىٰ مِنۡهُمَآ أَتَّبِعۡهُ إِن كُنتُمۡ صَٰدِقِينَ\nСен: «Агар ростгўйлардан бўлсангиз, Аллоҳнинг ҳузуридаги у иккисидан кўра тўғрироқ бир китоб келтиринг, мен унга эргашаман», дегин'}, {'chapter': 28, 'verse': 50, 'text': 'فَإِن لَّمۡ يَسۡتَجِيبُواْ لَكَ فَٱعۡلَمۡ أَنَّمَا يَتَّبِعُونَ أَهۡوَآءَهُمۡۚ وَمَنۡ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُ بِغَيۡرِ هُدٗى مِّنَ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nАгар сенга жавоб бера олмасалар, бас, билгинки, улар фақат ҳавойи нафсларига эргашмоқдалар, холос. Аллоҳдан бўлган ҳидоятсиз, ўз ҳавойи нафсига эргашгандан кўра ҳам гумроҳроқ кимса борми?! Албатта, Аллоҳ золим қавмларни ҳидоят қилмас'}, {'chapter': 28, 'verse': 51, 'text': '۞وَلَقَدۡ وَصَّلۡنَا لَهُمُ ٱلۡقَوۡلَ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nБатаҳқиқ, Биз уларга гапни етказиб қўйдик. Шоядки, эсласалар'}, {'chapter': 28, 'verse': 52, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ مِن قَبۡلِهِۦ هُم بِهِۦ يُؤۡمِنُونَ\nУ(Қуръон)дан олдин китоб берилганлар унга иймон келтирурлар'}, {'chapter': 28, 'verse': 53, 'text': 'وَإِذَا يُتۡلَىٰ عَلَيۡهِمۡ قَالُوٓاْ ءَامَنَّا بِهِۦٓ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّنَآ إِنَّا كُنَّا مِن قَبۡلِهِۦ مُسۡلِمِينَ\nУларга тиловат қилинган чоғда: «Биз унга иймон келтирдик. Албатта, у Роббимиздан келган ҳақдир. Албатта, биз бундан олдин ҳам мусулмон эдик», дедилар'}, {'chapter': 28, 'verse': 54, 'text': 'أُوْلَـٰٓئِكَ يُؤۡتَوۡنَ أَجۡرَهُم مَّرَّتَيۡنِ بِمَا صَبَرُواْ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nАна ўшаларга сабр қилганлари учун ажрлари икки марта берилган. Улар яхшилик ила ёмонликни дафъ қилурлар ва уларга ризқ қилиб берган нарсаларимиздан нафақа қилурлар'}, {'chapter': 28, 'verse': 55, 'text': 'وَإِذَا سَمِعُواْ ٱللَّغۡوَ أَعۡرَضُواْ عَنۡهُ وَقَالُواْ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ سَلَٰمٌ عَلَيۡكُمۡ لَا نَبۡتَغِي ٱلۡجَٰهِلِينَ\nҚачонки беҳуда нарса эшитсалар, ундан юз ўгириб: «Бизга ўз амалларимиз, сизга ўз амалларингиз, омон бўлинглар, биз жоҳилларни истамасмиз», дедилар'}, {'chapter': 28, 'verse': 56, 'text': 'إِنَّكَ لَا تَهۡدِي مَنۡ أَحۡبَبۡتَ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۚ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\nАлбатта, сен ўзинг севган кишингни ҳидоят қила олмассан. Лекин Аллоҳ кимни хоҳласа, ўшани ҳидоят қиладир. У ҳидоятга юрувчиларни яхши билгувчи Зотдир'}, {'chapter': 28, 'verse': 57, 'text': 'وَقَالُوٓاْ إِن نَّتَّبِعِ ٱلۡهُدَىٰ مَعَكَ نُتَخَطَّفۡ مِنۡ أَرۡضِنَآۚ أَوَلَمۡ نُمَكِّن لَّهُمۡ حَرَمًا ءَامِنٗا يُجۡبَىٰٓ إِلَيۡهِ ثَمَرَٰتُ كُلِّ شَيۡءٖ رِّزۡقٗا مِّن لَّدُنَّا وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nУлар: «Агар сен билан ҳидоятга эргашсак, ўз еримиздан юлиб олинамиз», дедилар. Биз уларга омонлик ҳарамини макон қилиб бермадикми?! У ерга ҳар нарсанинг мевалари Бизнинг даргоҳимиздан ризқ ўлароқ йиғилади-ку! Аммо кўплари билмаслар'}, {'chapter': 28, 'verse': 58, 'text': 'وَكَمۡ أَهۡلَكۡنَا مِن قَرۡيَةِۭ بَطِرَتۡ مَعِيشَتَهَاۖ فَتِلۡكَ مَسَٰكِنُهُمۡ لَمۡ تُسۡكَن مِّنۢ بَعۡدِهِمۡ إِلَّا قَلِيلٗاۖ وَكُنَّا نَحۡنُ ٱلۡوَٰرِثِينَ\nБиз маишатлари ҳаддан ошган қанчадан-қанча юртларни ҳалок қилдик. Бас, анавилар уларнинг масканлари, улардан кейин маскан қилинмади, илло, озгинаси, холос. Ва Биз Ўзимиз ворис бўлиб қолдик'}, {'chapter': 28, 'verse': 59, 'text': 'وَمَا كَانَ رَبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ حَتَّىٰ يَبۡعَثَ فِيٓ أُمِّهَا رَسُولٗا يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَاۚ وَمَا كُنَّا مُهۡلِكِي ٱلۡقُرَىٰٓ إِلَّا وَأَهۡلُهَا ظَٰلِمُونَ\nРоббинг шаҳар-қишлоқларни, то улар орасига уларга оятларимизни тиловат қиладиган Пайғамбар юбормасдан туриб, ҳалок қилувчи бўлмаган. Биз шаҳар-қишлоқларни фақат аҳли золим бўлган ҳолдагина ҳалок қилгувчи бўлганмиз'}, {'chapter': 28, 'verse': 60, 'text': 'وَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَزِينَتُهَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰٓۚ أَفَلَا تَعۡقِلُونَ\nВа сизга берилган нарсалар бу дунёнинг матоҳи ва зийнати, холос. Аллоҳнинг ҳузуридаги нарса яхшироқ ва боқийдир. Ақл ишлатмайсизларми'}, {'chapter': 28, 'verse': 61, 'text': 'أَفَمَن وَعَدۡنَٰهُ وَعۡدًا حَسَنٗا فَهُوَ لَٰقِيهِ كَمَن مَّتَّعۡنَٰهُ مَتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا ثُمَّ هُوَ يَوۡمَ ٱلۡقِيَٰمَةِ مِنَ ٱلۡمُحۡضَرِينَ\nБиз унга гўзал ваъда қилган, кейин ўшанга эришувчи киши, бу ҳаёти дунё матоҳини берганимиздан сўнгра қиёмат куни (азобга) ҳозир қилинганлардан бўлган киши каби бўлармиди'}, {'chapter': 28, 'verse': 62, 'text': 'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ\nУ зот уларни чақирган кунида: «Сиз даъво қилган Менинг «шерик»ларим қани?!» дер'}, {'chapter': 28, 'verse': 63, 'text': 'قَالَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ رَبَّنَا هَـٰٓؤُلَآءِ ٱلَّذِينَ أَغۡوَيۡنَآ أَغۡوَيۡنَٰهُمۡ كَمَا غَوَيۡنَاۖ تَبَرَّأۡنَآ إِلَيۡكَۖ مَا كَانُوٓاْ إِيَّانَا يَعۡبُدُونَ\nУстиларига сўз(ланган азоб) тушиши ҳақ бўлганлар: «Эй Роббимиз, анави йўлдан оздирганларимизни ўзимиз йўлдан озганимиздек оздирганмиз. Сенга ўзимизни оқладик. Улар бизга ибодат қилган эмаслар», дерлар'}, {'chapter': 28, 'verse': 64, 'text': 'وَقِيلَ ٱدۡعُواْ شُرَكَآءَكُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَرَأَوُاْ ٱلۡعَذَابَۚ لَوۡ أَنَّهُمۡ كَانُواْ يَهۡتَدُونَ\nВа: «Менинг «шерик»ларимни чақиринглар», дейилур. Улар чақирурлар. Бас, (сохта худолари) уларга жавоб бермаслар. Ва, азобни кўрурлар. Кошки, ҳидоят топган бўлганларида эди'}, {'chapter': 28, 'verse': 65, 'text': 'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ مَاذَآ أَجَبۡتُمُ ٱلۡمُرۡسَلِينَ\nУ кунда У зот уларга нидо қилиб: «Пайғамбарларга нима деб жавоб бердингиз?» дер'}, {'chapter': 28, 'verse': 66, 'text': 'فَعَمِيَتۡ عَلَيۡهِمُ ٱلۡأَنۢبَآءُ يَوۡمَئِذٖ فَهُمۡ لَا يَتَسَآءَلُونَ\nАна ўша кунда хабарлар улардан махфий бўлур ва улар бир-бирларидан сўрай олмаслар ҳам'}, {'chapter': 28, 'verse': 67, 'text': 'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَعَسَىٰٓ أَن يَكُونَ مِنَ ٱلۡمُفۡلِحِينَ\nАммо тавба қилган, иймон келтириб, солиҳ амалларни қилган киши, шоядки, нажот топгувчилардан бўлса'}, {'chapter': 28, 'verse': 68, 'text': 'وَرَبُّكَ يَخۡلُقُ مَا يَشَآءُ وَيَخۡتَارُۗ مَا كَانَ لَهُمُ ٱلۡخِيَرَةُۚ سُبۡحَٰنَ ٱللَّهِ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nВа Роббинг хоҳлаганини яратиб, хоҳлаганини ихтиёр қилур. Уларда ихтиёр ҳаққи йўқдир. Улар ширк келтираётган нарсалардан Аллоҳ пок ва юксакдир'}, {'chapter': 28, 'verse': 69, 'text': 'وَرَبُّكَ يَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ\nВа, Роббинг уларнинг қалблари яшираётган нарсаларни ҳам, ошкор қилаётган нарсаларни ҳам билур'}, {'chapter': 28, 'verse': 70, 'text': 'وَهُوَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡحَمۡدُ فِي ٱلۡأُولَىٰ وَٱلۡأٓخِرَةِۖ وَلَهُ ٱلۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ\nУ Аллоҳ Ундан бошқа ибодатга сазовор илоҳ йўқ Зотдир. Бу дунёда ва охиратда барча ҳамд Унгадир. Ҳукм ҳам Унинг Ўзигадир ва Унгагина қайтарилурсизлар'}, {'chapter': 28, 'verse': 71, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلَّيۡلَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِضِيَآءٍۚ أَفَلَا تَسۡمَعُونَ\nСен уларга: «Айтинг-чи, агар Аллоҳ кечани қиёмат кунигача доимий қилиб қўйса, Аллоҳдан ўзга қайси илоҳ сизга ёруғлик келтира олур? Эшитмайсизларми?!» деб айт'}, {'chapter': 28, 'verse': 72, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلنَّهَارَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِلَيۡلٖ تَسۡكُنُونَ فِيهِۚ أَفَلَا تُبۡصِرُونَ\nСен уларга: «Айтинг-чи, агар Аллоҳ кундузни қиёмат кунигача доимий қилиб қўйса, Аллоҳдан ўзга қайси илоҳ сизларга ором оладиганингиз кечани келтира олади?! Кўрмайсизларми?!» деб айт'}, {'chapter': 28, 'verse': 73, 'text': 'وَمِن رَّحۡمَتِهِۦ جَعَلَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ لِتَسۡكُنُواْ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nУ зот Ўз раҳмати ила сизларга кеча ва кундузни ором олишингиз ва Ўз фазлидан талаб қилишингиз учун пайдо қилди. Шоядки, шукр келтирсангиз'}, {'chapter': 28, 'verse': 74, 'text': 'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ\nУ кунда У зот уларга нидо қилиб: «Сизлар даъво қилган «шерик»ларим қани?» дер'}, {'chapter': 28, 'verse': 75, 'text': 'وَنَزَعۡنَا مِن كُلِّ أُمَّةٖ شَهِيدٗا فَقُلۡنَا هَاتُواْ بُرۡهَٰنَكُمۡ فَعَلِمُوٓاْ أَنَّ ٱلۡحَقَّ لِلَّهِ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nВа ҳар бир умматдан бир шоҳид чиқариб: «Ҳужжатларингизни келтиринг!» дегаймиз. Бас, улар ҳақ Аллоҳга эканини билурлар ва ўзлари уйдириб юрган нарсалари ғойиб бўлур'}, {'chapter': 28, 'verse': 76, 'text': '۞إِنَّ قَٰرُونَ كَانَ مِن قَوۡمِ مُوسَىٰ فَبَغَىٰ عَلَيۡهِمۡۖ وَءَاتَيۡنَٰهُ مِنَ ٱلۡكُنُوزِ مَآ إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِٱلۡعُصۡبَةِ أُوْلِي ٱلۡقُوَّةِ إِذۡ قَالَ لَهُۥ قَوۡمُهُۥ لَا تَفۡرَحۡۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡفَرِحِينَ\nАлбатта, Қорун Мусо қавмидан бўлиб, уларга такаббурлик қилди. Биз унга калитлари бир гуруҳ қувватлиларга ҳам оғирлик қиладиган хазиналарни берган эдик. Ўшанда қавми унга: «Ҳовлиқма, албатта, Аллоҳ ҳовлиққанларни суймас'}, {'chapter': 28, 'verse': 77, 'text': 'وَٱبۡتَغِ فِيمَآ ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلۡأٓخِرَةَۖ وَلَا تَنسَ نَصِيبَكَ مِنَ ٱلدُّنۡيَاۖ وَأَحۡسِن كَمَآ أَحۡسَنَ ٱللَّهُ إِلَيۡكَۖ وَلَا تَبۡغِ ٱلۡفَسَادَ فِي ٱلۡأَرۡضِۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ\nВа Аллоҳ сенга берган нарса билан охиратни излагин, бу дунёдаги насибангни ҳам унутма. Аллоҳ сенга яхшилик қилганидек, сен ҳам яхшилик қил. Ер юзида бузғунчиликни излама. Албатта, Аллоҳ бузғунчиларни суймас», дедилар'}, {'chapter': 28, 'verse': 78, 'text': 'قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمٍ عِندِيٓۚ أَوَلَمۡ يَعۡلَمۡ أَنَّ ٱللَّهَ قَدۡ أَهۡلَكَ مِن قَبۡلِهِۦ مِنَ ٱلۡقُرُونِ مَنۡ هُوَ أَشَدُّ مِنۡهُ قُوَّةٗ وَأَكۡثَرُ جَمۡعٗاۚ وَلَا يُسۡـَٔلُ عَن ذُنُوبِهِمُ ٱلۡمُجۡرِمُونَ\nУ (Қорун): «Менга у фақат ўзимдаги илм туфайлигина берилгандир», деди. Аллоҳ ундан олдинги асрларда ундан кўра қувватлироқ, ундан кўра кўпроқ (мол) тўплаганларни(ҳам) албатта ҳалок этганини у билмасмиди?! Гуноҳкорлардан гуноҳлари ҳақида сўраб ўтирилмас'}, {'chapter': 28, 'verse': 79, 'text': 'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ فِي زِينَتِهِۦۖ قَالَ ٱلَّذِينَ يُرِيدُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا يَٰلَيۡتَ لَنَا مِثۡلَ مَآ أُوتِيَ قَٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٖ\nБас, у (Қорун) қавми ҳузурига зебу зийнати ила чиқди. Ҳаёти дунёни хоҳлайдиганлар: «Қани энди бизга ҳам Қорунга берилган нарсадан бўлса эди. Ҳақиқатан, у улуғ насиба эгасидир», дедилар'}, {'chapter': 28, 'verse': 80, 'text': 'وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَيۡلَكُمۡ ثَوَابُ ٱللَّهِ خَيۡرٞ لِّمَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗاۚ وَلَا يُلَقَّىٰهَآ إِلَّا ٱلصَّـٰبِرُونَ\nИлм берилганлар эса: «Вой бўлсин сизларга! Иймон келтириб, солиҳ амалларни қилганлар учун Аллоҳнинг савоби яхшидир. Унга фақат сабр қилганларгина эришурлар», дедилар'}, {'chapter': 28, 'verse': 81, 'text': 'فَخَسَفۡنَا بِهِۦ وَبِدَارِهِ ٱلۡأَرۡضَ فَمَا كَانَ لَهُۥ مِن فِئَةٖ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلۡمُنتَصِرِينَ\nБас, Биз уни ва унинг ҳовли-жойини ерга юттирдик. Унга Аллоҳ(азоби)дан ёрдам берадиган гуруҳ ҳам бўлмади, унинг ўзи нусратга эришгувчилардан ҳам бўлмади'}, {'chapter': 28, 'verse': 82, 'text': 'وَأَصۡبَحَ ٱلَّذِينَ تَمَنَّوۡاْ مَكَانَهُۥ بِٱلۡأَمۡسِ يَقُولُونَ وَيۡكَأَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُۖ لَوۡلَآ أَن مَّنَّ ٱللَّهُ عَلَيۡنَا لَخَسَفَ بِنَاۖ وَيۡكَأَنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ\nВа кечагина унинг маконини орзу қилаётганлар: «Воажаб, Аллоҳ бандаларидан Ўзи хоҳлаганларига ризқини кенг ёки тор қиладиганга ўхшайдир. Агар Аллоҳ бизга лутф қилмаганида, бизни ҳам ерга юттирар эди. Воажаб, кофирлар нажот топмайдиганга ўхшайди-ку!» дейишга ўтдилар'}, {'chapter': 28, 'verse': 83, 'text': 'تِلۡكَ ٱلدَّارُ ٱلۡأٓخِرَةُ نَجۡعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوّٗا فِي ٱلۡأَرۡضِ وَلَا فَسَادٗاۚ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ\nБиз ўша охират диёрини ер юзида такаббурлик ва бузғунчиликни истамайдиганлар учун қилурмиз. Оқибат тақводорларникидир'}, {'chapter': 28, 'verse': 84, 'text': 'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَى ٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ إِلَّا مَا كَانُواْ يَعۡمَلُونَ\nКим бир яхшилик келтирса, унга ундан кўра хайрлироқ (мукофот) бор. Ким бир ёмонлик келтирса, ёмонлик қилганлар фақат қилганларига яраша жазоланурлар'}, {'chapter': 28, 'verse': 85, 'text': 'إِنَّ ٱلَّذِي فَرَضَ عَلَيۡكَ ٱلۡقُرۡءَانَ لَرَآدُّكَ إِلَىٰ مَعَادٖۚ قُل رَّبِّيٓ أَعۡلَمُ مَن جَآءَ بِٱلۡهُدَىٰ وَمَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ\nСенга Қуръонни фарз қилган Зот, албатта, сени қайтар жойга қайтаргувчидир. Сен: «Роббим ҳидоят келтирган шахсни ҳам ва очиқ-ойдин гумроҳликдаги шахсни ҳам яхши билгувчидир», деб айт'}, {'chapter': 28, 'verse': 86, 'text': 'وَمَا كُنتَ تَرۡجُوٓاْ أَن يُلۡقَىٰٓ إِلَيۡكَ ٱلۡكِتَٰبُ إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۖ فَلَا تَكُونَنَّ ظَهِيرٗا لِّلۡكَٰفِرِينَ\nСен ўзингга китоб туширилишидан умидвор ҳам эмас эдинг. Илло, Роббинг раҳмати бўлиб (тушди). Бас, ҳаргиз кофирларга ёрдамчи бўлма'}, {'chapter': 28, 'verse': 87, 'text': 'وَلَا يَصُدُّنَّكَ عَنۡ ءَايَٰتِ ٱللَّهِ بَعۡدَ إِذۡ أُنزِلَتۡ إِلَيۡكَۖ وَٱدۡعُ إِلَىٰ رَبِّكَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ\nВа сени улар сенга туширилган Аллоҳнинг оятларидан ҳаргиз тўсмасинлар. Роббингга даъват қил. Асло мушриклардан бўлма'}, {'chapter': 28, 'verse': 88, 'text': 'وَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۘ لَآ إِلَٰهَ إِلَّا هُوَۚ كُلُّ شَيۡءٍ هَالِكٌ إِلَّا وَجۡهَهُۥۚ لَهُ ٱلۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ\nАллоҳдан ўзга ҳеч бир илоҳга илтижо қилма. Ундан ўзга ибодатга сазовор зот йўқ. Ундан ўзга ҳар бир нарса ҳалок бўлгувчидир. Ҳукм Унинг Ўзигагинадир ва фақат Унгагина қайтарилурсиз'}], '29': [{'chapter': 29, 'verse': 1, 'text': 'الٓمٓ\nАлиф. Лаам. Мийм'}, {'chapter': 29, 'verse': 2, 'text': 'أَحَسِبَ ٱلنَّاسُ أَن يُتۡرَكُوٓاْ أَن يَقُولُوٓاْ ءَامَنَّا وَهُمۡ لَا يُفۡتَنُونَ\nОдамлар «иймон келтирдик» дейишлари ила имтиҳон қилинмай тарк этилишларини ўйладиларми'}, {'chapter': 29, 'verse': 3, 'text': 'وَلَقَدۡ فَتَنَّا ٱلَّذِينَ مِن قَبۡلِهِمۡۖ فَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ صَدَقُواْ وَلَيَعۡلَمَنَّ ٱلۡكَٰذِبِينَ\nВа, батаҳқиқ, Биз улардан олдингиларни ҳам имтиҳондан ўтказганмиз. Бас, Аллоҳ, албатта, содиқ бўлганларни ҳам билажак, ёлғончиларни ҳам билажак'}, {'chapter': 29, 'verse': 4, 'text': 'أَمۡ حَسِبَ ٱلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ أَن يَسۡبِقُونَاۚ سَآءَ مَا يَحۡكُمُونَ\nЁки ёмонликларни қиладиганлар биздан қочиб қутулишни ўйлайдиларми? Қилган ҳукмлари қандай ҳам ёмон-а'}, {'chapter': 29, 'verse': 5, 'text': 'مَن كَانَ يَرۡجُواْ لِقَآءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَأٓتٖۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nКим Аллоҳга рўбарў бўлишдан умидвор бўлса, албатта, Аллоҳ белгилаган вақт келгувчидир. У ўта эшитгувчи ва ўта билгувчидир'}, {'chapter': 29, 'verse': 6, 'text': 'وَمَن جَٰهَدَ فَإِنَّمَا يُجَٰهِدُ لِنَفۡسِهِۦٓۚ إِنَّ ٱللَّهَ لَغَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ\nКим жиҳод қилса, бас, албатта, ўз фойдаси учун жидду жаҳд қилур. Албатта, Аллоҳ барча оламлардан беҳожатдир'}, {'chapter': 29, 'verse': 7, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَنَجۡزِيَنَّهُمۡ أَحۡسَنَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ\nИймон келтириб, яхши амаллар қилганларнинг ёмонликларини албатта ўчирурмиз ва уларни қилган амалларининг энг яхшиси ила мукофотлармиз'}, {'chapter': 29, 'verse': 8, 'text': 'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ حُسۡنٗاۖ وَإِن جَٰهَدَاكَ لِتُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَآۚ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nВа инсонга ота-онасига яхшилик қилишни тавсия қилдик. Агар улар сенинг ўзинг билмаган нарсани Менга ширк келтиришинг учун жиҳод қилсалар, бас, уларга итоат қилма. Қайтишларингиз Менгагинадир. Бас, сизларга қилган нарсаларингиз хабарини берурман'}, {'chapter': 29, 'verse': 9, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُدۡخِلَنَّهُمۡ فِي ٱلصَّـٰلِحِينَ\nИймон келтириб, яхши амаллар қилганларни солиҳлар қаторига қўшурмиз'}, {'chapter': 29, 'verse': 10, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَآ أُوذِيَ فِي ٱللَّهِ جَعَلَ فِتۡنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِۖ وَلَئِن جَآءَ نَصۡرٞ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمۡۚ أَوَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِمَا فِي صُدُورِ ٱلۡعَٰلَمِينَ\nВа одамлардан, Аллоҳга иймон келтирдик, дейдиган, сўнгра Аллоҳнинг йўлида озорланса, одамларнинг фитнасини Аллоҳнинг азобидек қабул қиладиганлари ҳам бор. Агар Роббинг томонидан нусрат келса, улар, албатта, биз сиз билан бирга эдик, дерлар. Аллоҳ оламларнинг кўксиларидаги нарсаларни ўта билгувчи Зот эмасми'}, {'chapter': 29, 'verse': 11, 'text': 'وَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَلَيَعۡلَمَنَّ ٱلۡمُنَٰفِقِينَ\nАлбатта, Аллоҳ иймон келтирганларни ҳам яхши билур, мунофиқларни ҳам яхши билур'}, {'chapter': 29, 'verse': 12, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ ٱتَّبِعُواْ سَبِيلَنَا وَلۡنَحۡمِلۡ خَطَٰيَٰكُمۡ وَمَا هُم بِحَٰمِلِينَ مِنۡ خَطَٰيَٰهُم مِّن شَيۡءٍۖ إِنَّهُمۡ لَكَٰذِبُونَ\nВа куфр келтирганлар иймон келтирганларга: «Бизнинг йўлимизга эргашинглар, хатоларингизни биз кўтарайлик», дерлар. Ҳолбуки, улар аларнинг хатоларидан ҳеч бир нарсани кўтаргувчи эмаслар. Албатта, улар ёлғончилардир'}, {'chapter': 29, 'verse': 13, 'text': 'وَلَيَحۡمِلُنَّ أَثۡقَالَهُمۡ وَأَثۡقَالٗا مَّعَ أَثۡقَالِهِمۡۖ وَلَيُسۡـَٔلُنَّ يَوۡمَ ٱلۡقِيَٰمَةِ عَمَّا كَانُواْ يَفۡتَرُونَ\nАлбатта, улар ўз юкларини ҳам, у юклар билан бирга бошқа юкларни ҳам кўтарурлар. Албатта, қиёмат кунида уйдириб юрган нарсалари ҳақида сўралурлар'}, {'chapter': 29, 'verse': 14, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَلَبِثَ فِيهِمۡ أَلۡفَ سَنَةٍ إِلَّا خَمۡسِينَ عَامٗا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمۡ ظَٰلِمُونَ\nВа, батаҳқиқ, Биз Нуҳни қавмига Пайғамбар этиб юбордик. Бас, у улар ичида эллик йили кам минг йил турди. Сўнгра уларни золим бўлган ҳолларида тўфон олди'}, {'chapter': 29, 'verse': 15, 'text': 'فَأَنجَيۡنَٰهُ وَأَصۡحَٰبَ ٱلسَّفِينَةِ وَجَعَلۡنَٰهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ\nБас, Биз у(Нуҳ)га ва кема соҳибларига нажот бердик ва у(кема)ни оламларга оят-белги қилдик'}, {'chapter': 29, 'verse': 16, 'text': 'وَإِبۡرَٰهِيمَ إِذۡ قَالَ لِقَوۡمِهِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُۖ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nВа Иброҳимни (юбордик). Ўшанда у ўз қавмига: «Аллоҳга ибодат қилинглар ва Унга тақво қилинглар. Бундай қилишингиз, агар билсангиз, сиз учун яхшидир'}, {'chapter': 29, 'verse': 17, 'text': 'إِنَّمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ أَوۡثَٰنٗا وَتَخۡلُقُونَ إِفۡكًاۚ إِنَّ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ لَكُمۡ رِزۡقٗا فَٱبۡتَغُواْ عِندَ ٱللَّهِ ٱلرِّزۡقَ وَٱعۡبُدُوهُ وَٱشۡكُرُواْ لَهُۥٓۖ إِلَيۡهِ تُرۡجَعُونَ\nАлбатта, сизлар Аллоҳни қўйиб, бутларга ибодат қилмоқдасиз ва уйдирмалар тўқимоқдасиз, холос. Албатта, Аллоҳдан ўзга ибодат қилаётган нарсаларингиз сизга ризқ беришга молик эмаслар. Ризқни Аллоҳнинг ҳузуридан истанглар. Унга ибодат ва шукр қилинглар. Унгагина қайтарилурсиз'}, {'chapter': 29, 'verse': 18, 'text': 'وَإِن تُكَذِّبُواْ فَقَدۡ كَذَّبَ أُمَمٞ مِّن قَبۡلِكُمۡۖ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАгар сизлар ёлғончи қилсангиз, бас, сиздан олдинги умматлар ҳам ёлғончи қилганлар. Пайғамбар зиммасида очиқ-ойдин етказишдан бошқа нарса йўқ», деди'}, {'chapter': 29, 'verse': 19, 'text': 'أَوَلَمۡ يَرَوۡاْ كَيۡفَ يُبۡدِئُ ٱللَّهُ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥٓۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nАллоҳ махлуқни аввал бошдан қандай яратишини, сўнгра уни яна қайтаришини кўрмайдиларми?! Албатта, бу Аллоҳ учун осондир'}, {'chapter': 29, 'verse': 20, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ بَدَأَ ٱلۡخَلۡقَۚ ثُمَّ ٱللَّهُ يُنشِئُ ٱلنَّشۡأَةَ ٱلۡأٓخِرَةَۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nСен: «Ер юзида сайр этиб юринглар ва махлуқотларни У зот аввал бошдан қандай яратганига назар солинглар. Сўнгра Аллоҳ охират ҳаётини пайдо қилур. Албатта, Аллоҳ ҳар бир нарсага қодирдир», деб айт'}, {'chapter': 29, 'verse': 21, 'text': 'يُعَذِّبُ مَن يَشَآءُ وَيَرۡحَمُ مَن يَشَآءُۖ وَإِلَيۡهِ تُقۡلَبُونَ\nКимни хоҳласа, азоблайдир, кимни хоҳласа, раҳим қиладир. Унгагина қайтарилурсиз'}, {'chapter': 29, 'verse': 22, 'text': 'وَمَآ أَنتُم بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nСиз на ер юзида ва на кўкда У зотни ожиз қолдиргувчи эмассиз. Сизга Аллоҳдан ўзга дўст ҳам, ёрдамчи ҳам бўлмас'}, {'chapter': 29, 'verse': 23, 'text': 'وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ وَلِقَآئِهِۦٓ أُوْلَـٰٓئِكَ يَئِسُواْ مِن رَّحۡمَتِي وَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ\nАллоҳнинг оятларига ва Унга рўбарў бўлишга куфр келтирганлар, ана ўшалар Менинг раҳматимдан ноумид бўлдилар. Ана ўшаларнинг ўзларига аламли азоб бор'}, {'chapter': 29, 'verse': 24, 'text': 'فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ ٱقۡتُلُوهُ أَوۡ حَرِّقُوهُ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nУнинг қавмининг жавоби: «Уни ўлдиринглар ёки ўтда ёқинглар!» дейишларидан бошқа нарса бўлмади. Бас, Аллоҳ уни ўтдан қутқарди. Албатта, бунда иймон келтирадиган қавмлар учун оят-белгилар бордир'}, {'chapter': 29, 'verse': 25, 'text': 'وَقَالَ إِنَّمَا ٱتَّخَذۡتُم مِّن دُونِ ٱللَّهِ أَوۡثَٰنٗا مَّوَدَّةَ بَيۡنِكُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُ بَعۡضُكُم بِبَعۡضٖ وَيَلۡعَنُ بَعۡضُكُم بَعۡضٗا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ\nУ: «Сизлар, албатта, ҳаёти дунёдаги ўзаро дўстлигингиз учун Аллоҳни қўйиб, бутларни тутдингиз. Сўнгра, қиёмат кунида баъзингиз баъзингизга куфр келтирурсиз ва баъзингиз баъзингизни лаънатларсиз. Ва жойингиз дўзахдир. Ҳамда сизга нусрат бергувчилар бўлмас'}, {'chapter': 29, 'verse': 26, 'text': '۞فَـَٔامَنَ لَهُۥ لُوطٞۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّيٓۖ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nБас, унга Лут иймон келтирди ва: «Албатта, мен Роббимга ҳижрат қилгувчиман. Албатта, У Азийз ва Ҳакиймдир», деди'}, {'chapter': 29, 'verse': 27, 'text': 'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِ ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَ وَءَاتَيۡنَٰهُ أَجۡرَهُۥ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ\nБиз унга Исҳоқни ва Яъқубни бахш этдик ва Пайғамбарлик ҳамда китобни унинг зурриётида бўладиган қилдик. Унга бу дунёда ажрини бердик. Албатта, у охиратда солиҳлардан бўлгай'}, {'chapter': 29, 'verse': 28, 'text': 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ إِنَّكُمۡ لَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ\nВа Лутни (ҳам юбордик). Ўшанда у ўз қавмига: «Албатта, сиз ўзингиздан олдин ўтган оламлардан ҳеч ким қилмаган фоҳиша ишни қилмоқдасиз'}, {'chapter': 29, 'verse': 29, 'text': 'أَئِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ وَتَقۡطَعُونَ ٱلسَّبِيلَ وَتَأۡتُونَ فِي نَادِيكُمُ ٱلۡمُنكَرَۖ فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ ٱئۡتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nСизлар эркакларга яқинлик қилиб, йўлни кесасизми ва йиғинларингизда мункар иш қилурмисиз?!» деди. Унинг қавмининг жавоби: «Агар ростгўйлардан бўлсанг, бизга Аллоҳнинг азобини келтир», дейишдан бошқа бўлмади'}, {'chapter': 29, 'verse': 30, 'text': 'قَالَ رَبِّ ٱنصُرۡنِي عَلَى ٱلۡقَوۡمِ ٱلۡمُفۡسِدِينَ\nУ: «Эй Роббим, менга бузғунчи қавмларга қарши Ўзинг нусрат бер», деди'}, {'chapter': 29, 'verse': 31, 'text': 'وَلَمَّا جَآءَتۡ رُسُلُنَآ إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُوٓاْ إِنَّا مُهۡلِكُوٓاْ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِۖ إِنَّ أَهۡلَهَا كَانُواْ ظَٰلِمِينَ\nБизнинг элчиларимиз Иброҳимга хушхабарни келтиришганида: «Албатта, биз ушбу шаҳар аҳлини ҳалок қилгувчидирмиз. Албатта, унинг аҳли зулм қилгувчилардан бўлдилар», дедилар'}, {'chapter': 29, 'verse': 32, 'text': 'قَالَ إِنَّ فِيهَا لُوطٗاۚ قَالُواْ نَحۡنُ أَعۡلَمُ بِمَن فِيهَاۖ لَنُنَجِّيَنَّهُۥ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ\nУ: «Унда Лут бор-ку?!» деди. Улар: «Биз унда ким борлигини яхши билурмиз. Биз, албатта, унга ва унинг аҳлига нажот берурмиз. Магар, унинг хотини ҳалок бўлгувчилардан бўлгандир», дедилар'}, {'chapter': 29, 'verse': 33, 'text': 'وَلَمَّآ أَن جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗاۖ وَقَالُواْ لَا تَخَفۡ وَلَا تَحۡزَنۡ إِنَّا مُنَجُّوكَ وَأَهۡلَكَ إِلَّا ٱمۡرَأَتَكَ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ\nЭлчиларимиз Лутга келишганида, у улар сабабли маҳзун бўлди ва келишларидан юраги сиқилди. Улар: «Сен хавфсирама ва маҳзун ҳам бўлма, албатта, биз сени ва аҳлингни қутқаргувчилармиз. Магар, хотининг ҳалок бўлгувчилардан бўлгандир'}, {'chapter': 29, 'verse': 34, 'text': 'إِنَّا مُنزِلُونَ عَلَىٰٓ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ\nАлбатта, биз ушбу шаҳар аҳолиси бошига, фосиқлик қилганлари туфайли, осмондан азоб туширгувчимиз», дедилар'}, {'chapter': 29, 'verse': 35, 'text': 'وَلَقَد تَّرَكۡنَا مِنۡهَآ ءَايَةَۢ بَيِّنَةٗ لِّقَوۡمٖ يَعۡقِلُونَ\nБатаҳқиқ, Биз ундан ақл юритгувчи қавмлар учун очиқ-ойдин оят-белгилар қолдирганмиз'}, {'chapter': 29, 'verse': 36, 'text': 'وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗا فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ وَٱرۡجُواْ ٱلۡيَوۡمَ ٱلۡأٓخِرَ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nВа Мадянга биродарлари Шуъайбни (юбордик). Бас, у: «Эй қавмим, Аллоҳга ибодат қилинг, охират кунидан умидвор бўлинг ва ер юзида бузғунчилик ила ҳаракат қилиб юрманг», деди'}, {'chapter': 29, 'verse': 37, 'text': 'فَكَذَّبُوهُ فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ\nБас, уни ёлғончига чиқардилар. Шунда уларни бир силкиниш тутди ва диёрларида чўккалаб қолдилар'}, {'chapter': 29, 'verse': 38, 'text': 'وَعَادٗا وَثَمُودَاْ وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَٰكِنِهِمۡۖ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِيلِ وَكَانُواْ مُسۡتَبۡصِرِينَ\nВа Од ва Самудни (ҳам ҳалок этдик). Батаҳқиқ, бу сизга уларнинг масканларидан аён бўлиб турибди. Шайтон уларга амалларини зийнатлаб кўрсатиб, уларни йўлдан тўсди. Ҳолбуки, улар кўриб-билиб тургувчи эдилар'}, {'chapter': 29, 'verse': 39, 'text': 'وَقَٰرُونَ وَفِرۡعَوۡنَ وَهَٰمَٰنَۖ وَلَقَدۡ جَآءَهُم مُّوسَىٰ بِٱلۡبَيِّنَٰتِ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ وَمَا كَانُواْ سَٰبِقِينَ\nҚорун, Фиръавн ва Ҳомонларни (ҳам ҳалок қилдик). Батаҳқиқ, Мусо уларга очиқ-ойдин ҳужжатлар келтирди. Бас, улар ер юзида мутакаббирлик қилдилар ва қочиб қутулгувчи бўлмадилар'}, {'chapter': 29, 'verse': 40, 'text': 'فَكُلًّا أَخَذۡنَا بِذَنۢبِهِۦۖ فَمِنۡهُم مَّنۡ أَرۡسَلۡنَا عَلَيۡهِ حَاصِبٗا وَمِنۡهُم مَّنۡ أَخَذَتۡهُ ٱلصَّيۡحَةُ وَمِنۡهُم مَّنۡ خَسَفۡنَا بِهِ ٱلۡأَرۡضَ وَمِنۡهُم مَّنۡ أَغۡرَقۡنَاۚ وَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nУлардан ҳар бирини гуноҳи туфайли тутдик. Бас, улардан баъзилари устига тош ёғдирдик, баъзиларини қичқириқ ушлади, баъзиларини ерга юттирдик ва баъзиларини ғарқ қилдик. Аллоҳ уларга зулм этгувчи бўлмади, лекин улар ўзларига зулм этдилар'}, {'chapter': 29, 'verse': 41, 'text': 'مَثَلُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَ كَمَثَلِ ٱلۡعَنكَبُوتِ ٱتَّخَذَتۡ بَيۡتٗاۖ وَإِنَّ أَوۡهَنَ ٱلۡبُيُوتِ لَبَيۡتُ ٱلۡعَنكَبُوتِۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nАллоҳни қўйиб, ўзга дўстлар тутганларнинг мисоли ўзига уй тутган ўргимчакка ўхшайдир. Ҳолбуки, энг заиф уй ўргимчакнинг уясидир. Кошки, билсалар эди'}, {'chapter': 29, 'verse': 42, 'text': 'إِنَّ ٱللَّهَ يَعۡلَمُ مَا يَدۡعُونَ مِن دُونِهِۦ مِن شَيۡءٖۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАлбатта, Аллоҳ улар Ўзидан бошқа нима нарсага дуо қилаётганларини яхши биладир. Ва У азийз ва ҳакийм Зотдир'}, {'chapter': 29, 'verse': 43, 'text': 'وَتِلۡكَ ٱلۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنَّاسِۖ وَمَا يَعۡقِلُهَآ إِلَّا ٱلۡعَٰلِمُونَ\nУшбу мисолларни Биз одамлар учун келтирурмиз. Лекин уларга олимлардан ўзгаларнинг ақллари етмас'}, {'chapter': 29, 'verse': 44, 'text': 'خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ\nАллоҳ осмонлар ва ерни ҳақ ила яратгандир. Албатта, бунда мўминлар учун оят-белги бордир'}, {'chapter': 29, 'verse': 45, 'text': 'ٱتۡلُ مَآ أُوحِيَ إِلَيۡكَ مِنَ ٱلۡكِتَٰبِ وَأَقِمِ ٱلصَّلَوٰةَۖ إِنَّ ٱلصَّلَوٰةَ تَنۡهَىٰ عَنِ ٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۗ وَلَذِكۡرُ ٱللَّهِ أَكۡبَرُۗ وَٱللَّهُ يَعۡلَمُ مَا تَصۡنَعُونَ\nСенга китобдан ваҳий қилинган нарсани тиловат қил ва намозни тўкис адо эт, албатта, намоз фоҳиша ва мункар ишлардан қайтарур. Албатта, Аллоҳнинг зикри буюк ишдир. Аллоҳ нима қилсангиз, билур'}, {'chapter': 29, 'verse': 46, 'text': '۞وَلَا تُجَٰدِلُوٓاْ أَهۡلَ ٱلۡكِتَٰبِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ إِلَّا ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡۖ وَقُولُوٓاْ ءَامَنَّا بِٱلَّذِيٓ أُنزِلَ إِلَيۡنَا وَأُنزِلَ إِلَيۡكُمۡ وَإِلَٰهُنَا وَإِلَٰهُكُمۡ وَٰحِدٞ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\nСизлар аҳли китобларнинг зулм қилганларидан бошқалари билан фақат яхши услубда мужодала этинглар ва уларга: «Биз ўзимизга нозил қилинган ва сизга нозил қилинган нарсага иймон келтирдик, бизнинг илоҳимиз ва сизнинг илоҳингиз бирдир ва биз Унга бўйсунгувчимиз», денглар'}, {'chapter': 29, 'verse': 47, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَۚ فَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يُؤۡمِنُونَ بِهِۦۖ وَمِنۡ هَـٰٓؤُلَآءِ مَن يُؤۡمِنُ بِهِۦۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلۡكَٰفِرُونَ\nАна шундай қилиб, сенга ҳам китоб нозил қилдик. Бас, Биз китоб берган кимсалар унга иймон келтирурлар. Анавилардан ҳам унга иймон келтирадиганлари бор. Бизнинг оятларимизни фақат кофирларгина инкор этурлар'}, {'chapter': 29, 'verse': 48, 'text': 'وَمَا كُنتَ تَتۡلُواْ مِن قَبۡلِهِۦ مِن كِتَٰبٖ وَلَا تَخُطُّهُۥ بِيَمِينِكَۖ إِذٗا لَّٱرۡتَابَ ٱلۡمُبۡطِلُونَ\nУндан олдин ҳеч бир китобни тиловат қилмас эдинг ва қўлинг билан хат ёзмас эдинг. Агар шундай бўлганида, ботил аҳллари шубҳага тушган бўлур эдилар'}, {'chapter': 29, 'verse': 49, 'text': 'بَلۡ هُوَ ءَايَٰتُۢ بَيِّنَٰتٞ فِي صُدُورِ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلظَّـٰلِمُونَ\nЙўқ! У(Қуръон) илм берилганлар кўксидаги очиқ-ойдин оятлардир. Бизнинг оятларимизни фақат золимларгина инкор қилур'}, {'chapter': 29, 'verse': 50, 'text': 'وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَٰتٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّمَا ٱلۡأٓيَٰتُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ\nВа улар: «Унга Роббидан мўъжизалар нозил қилинса эди», дедилар. Сен: «Мўъжизалар ёлғиз Аллоҳнинг ҳузуридадир. Мен эса, фақат очиқ-ойдин огоҳлантиргувчиман, холос», дегин'}, {'chapter': 29, 'verse': 51, 'text': 'أَوَلَمۡ يَكۡفِهِمۡ أَنَّآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ يُتۡلَىٰ عَلَيۡهِمۡۚ إِنَّ فِي ذَٰلِكَ لَرَحۡمَةٗ وَذِكۡرَىٰ لِقَوۡمٖ يُؤۡمِنُونَ\nУларга Бизнинг сенга ўзлари учун тиловат қилинаётган ушбу китобни нозил этганимиз кифоя қилмасми?! Албатта, бунда иймон келтирадиган қавмлар учун раҳмат ва эслатма бордир'}, {'chapter': 29, 'verse': 52, 'text': 'قُلۡ كَفَىٰ بِٱللَّهِ بَيۡنِي وَبَيۡنَكُمۡ شَهِيدٗاۖ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱلَّذِينَ ءَامَنُواْ بِٱلۡبَٰطِلِ وَكَفَرُواْ بِٱللَّهِ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nСен: «Мен билан сизнинг орамизда гувоҳликка Аллоҳнинг Ўзи кифоядир. У осмонлару ердаги нарсани билур. Ботилга иймон келтириб, Аллоҳга куфр келтирганлар, ана ўшалар ўзлари зиён кўргувчилардир», деб айт'}, {'chapter': 29, 'verse': 53, 'text': 'وَيَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَوۡلَآ أَجَلٞ مُّسَمّٗى لَّجَآءَهُمُ ٱلۡعَذَابُۚ وَلَيَأۡتِيَنَّهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nУлар сендан азобни тезлаштиришни талаб қилурлар. Агар белгиланган ажал бўлмаганида, уларга, албатта, азоб келган бўлур эди. Албатта, уларга у тўсатдан, улар сезмаган ҳолда келур'}, {'chapter': 29, 'verse': 54, 'text': 'يَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِٱلۡكَٰفِرِينَ\nУлар сендан азобни тезлаштиришни талаб қилурлар. Ҳолбуки, жаҳаннам, ҳеч шубҳасиз, кофирларни ўраб тургувчидир'}, {'chapter': 29, 'verse': 55, 'text': 'يَوۡمَ يَغۡشَىٰهُمُ ٱلۡعَذَابُ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۡ وَيَقُولُ ذُوقُواْ مَا كُنتُمۡ تَعۡمَلُونَ\nУ кунда азоб уларни устларидан ва оёқлари остидан ўраб олур ва У зот: «Қилиб юрган амалларингиз(жазоси)ни тотинг!» деб айтур'}, {'chapter': 29, 'verse': 56, 'text': 'يَٰعِبَادِيَ ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ أَرۡضِي وَٰسِعَةٞ فَإِيَّـٰيَ فَٱعۡبُدُونِ\nЭй, иймон келтирган бандаларим, албатта, Менинг ерим кенгдир. Бас, Менгагина ибодат қилинглар'}, {'chapter': 29, 'verse': 57, 'text': 'كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۖ ثُمَّ إِلَيۡنَا تُرۡجَعُونَ\nҲар бир жон ўлимни тотажакдир. Сўнгра Бизгагина қайтарилурсиз'}, {'chapter': 29, 'verse': 58, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُبَوِّئَنَّهُم مِّنَ ٱلۡجَنَّةِ غُرَفٗا تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ نِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ\nИймон келтириб, яхши амалларни қилганларни жаннатдан остидан анҳорлар оқиб турган олиймақом жойларга жойлаштирурмиз. У ерда абадий қолурлар. Амал қилгувчиларнинг ажри қандай ҳам яхши'}, {'chapter': 29, 'verse': 59, 'text': 'ٱلَّذِينَ صَبَرُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nУлар сабр қилган ва Роббиларигагина таваккул қиладиган зотлардир'}, {'chapter': 29, 'verse': 60, 'text': 'وَكَأَيِّن مِّن دَآبَّةٖ لَّا تَحۡمِلُ رِزۡقَهَا ٱللَّهُ يَرۡزُقُهَا وَإِيَّاكُمۡۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nЎз ризқини кўтара олмайдиган қанчадан-қанча жонзотлар бор. Уларга ҳам, сизларга ҳам Аллоҳ ризқ берадир. Ва У ўта эшитгувчи ва ўта билгувчи Зотдир'}, {'chapter': 29, 'verse': 61, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَ لَيَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ يُؤۡفَكُونَ\nАгар улардан: «Осмонлару ерни ким яратган ва қуёшнию ойни ўз измига қаратган ким?» деб сўрасанг: «Аллоҳ», дерлар. Бас, унда қаёққа бурилмоқдалар'}, {'chapter': 29, 'verse': 62, 'text': 'ٱللَّهُ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥٓۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nАллоҳ Ўз бандаларидан кимни хоҳласа, ризқини кенг қилиб қўядир ва хоҳлаганиникини тор қиладир. Албатта, Аллоҳ ҳар бир нарсани ўта билгувчи Зотдир'}, {'chapter': 29, 'verse': 63, 'text': 'وَلَئِن سَأَلۡتَهُم مَّن نَّزَّلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ مِنۢ بَعۡدِ مَوۡتِهَا لَيَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ\nАгар улардан: «Осмондан сув тушириб, у билан ўлган ерни тирилтирган ким?!» деб сўрасанг, албатта, улар: «Аллоҳ», дерлар. Сен: «Аллоҳга ҳамд бўлсин!» деб айт. Йўқ! Уларнинг кўплари ақл юритмаслар'}, {'chapter': 29, 'verse': 64, 'text': 'وَمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا لَهۡوٞ وَلَعِبٞۚ وَإِنَّ ٱلدَّارَ ٱلۡأٓخِرَةَ لَهِيَ ٱلۡحَيَوَانُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nБу дунё ҳаёти фақат ўйин-кулгидан иборатдир. Агар билсалар, охират диёри, ана ўша ҳақиқий ҳаётдир'}, {'chapter': 29, 'verse': 65, 'text': 'فَإِذَا رَكِبُواْ فِي ٱلۡفُلۡكِ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ إِذَا هُمۡ يُشۡرِكُونَ\nУлар қачон кемага минсалар, Аллоҳга, Унинг динига ихлос қилган ҳолларида дуо қилурлар. Нажот бериб, уларни қуруқликка чиқарса, баногоҳ ширк келтирурлар'}, {'chapter': 29, 'verse': 66, 'text': 'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡ وَلِيَتَمَتَّعُواْۚ فَسَوۡفَ يَعۡلَمُونَ\nУлар Биз берган нарсага куфр келтираверсинлар ва ҳузур қилаверсинлар! Бас, тезда билурлар'}, {'chapter': 29, 'verse': 67, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا حَرَمًا ءَامِنٗا وَيُتَخَطَّفُ ٱلنَّاسُ مِنۡ حَوۡلِهِمۡۚ أَفَبِٱلۡبَٰطِلِ يُؤۡمِنُونَ وَبِنِعۡمَةِ ٱللَّهِ يَكۡفُرُونَ\nБиз уларга Ҳарамни омонлик жойи қилиб қўйганимизни билмабмидилар?! Ҳолбуки, уларнинг атрофида одамлар талон-тарожга учрамоқдалар. Улар ботилга иймон келтириб, Аллоҳнинг неъматига куфр келтирурларми'}, {'chapter': 29, 'verse': 68, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِٱلۡحَقِّ لَمَّا جَآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكَٰفِرِينَ\nАллоҳга нисбатан ёлғон тўқиган ёки ўзига келган ҳақни ёлғонга чиқарган кимсадан ҳам золимроқ одам борми?! Кофирларга жаҳаннамда турар жой йўқмиди'}, {'chapter': 29, 'verse': 69, 'text': 'وَٱلَّذِينَ جَٰهَدُواْ فِينَا لَنَهۡدِيَنَّهُمۡ سُبُلَنَاۚ وَإِنَّ ٱللَّهَ لَمَعَ ٱلۡمُحۡسِنِينَ\nБизнинг (йўлимизда) жидду жаҳд қилганларни Ўз йўлларимизга ҳидоят этурмиз. Албатта, Аллоҳ чиройли амал қилгувчилар биландир'}], '30': [{'chapter': 30, 'verse': 1, 'text': 'الٓمٓ\nАлиф. Лаам. Мийм'}, {'chapter': 30, 'verse': 2, 'text': 'غُلِبَتِ ٱلرُّومُ\nРум мағлуб бўлди'}, {'chapter': 30, 'verse': 3, 'text': 'فِيٓ أَدۡنَى ٱلۡأَرۡضِ وَهُم مِّنۢ بَعۡدِ غَلَبِهِمۡ سَيَغۡلِبُونَ\nЯқин ерда. Ва улар мағлубиятларидан кейин тезда ғолиб бўлажаклар'}, {'chapter': 30, 'verse': 4, 'text': 'فِي بِضۡعِ سِنِينَۗ لِلَّهِ ٱلۡأَمۡرُ مِن قَبۡلُ وَمِنۢ بَعۡدُۚ وَيَوۡمَئِذٖ يَفۡرَحُ ٱلۡمُؤۡمِنُونَ\nСаноқли йилларда. Ундан олдин ҳам, кейин ҳам барча иш Аллоҳдандир. Ўша кунда мўминлар шодланурлар'}, {'chapter': 30, 'verse': 5, 'text': 'بِنَصۡرِ ٱللَّهِۚ يَنصُرُ مَن يَشَآءُۖ وَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАллоҳнинг нусратидан. У хоҳлаганига нусрат берур. Ва У азийзу роҳиймдир'}, {'chapter': 30, 'verse': 6, 'text': 'وَعۡدَ ٱللَّهِۖ لَا يُخۡلِفُ ٱللَّهُ وَعۡدَهُۥ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\n(Бу) Аллоҳнинг ваъдасидир. Аллоҳ Ўз ваъдасига хилоф қилмас. Лекин одамларнинг кўпи буни билмаслар'}, {'chapter': 30, 'verse': 7, 'text': 'يَعۡلَمُونَ ظَٰهِرٗا مِّنَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَهُمۡ عَنِ ٱلۡأٓخِرَةِ هُمۡ غَٰفِلُونَ\nУлар бу дунё ҳаётининг зоҳирини билурлар. Ҳолбуки, улар охиратдан ғофилдирлар'}, {'chapter': 30, 'verse': 8, 'text': 'أَوَلَمۡ يَتَفَكَّرُواْ فِيٓ أَنفُسِهِمۗ مَّا خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۗ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ بِلِقَآيِٕ رَبِّهِمۡ لَكَٰفِرُونَ\nАхир, улар, ўзларича, Аллоҳ осмонлару ерни ва улар орасидаги нарсаларни фақат ҳақ билан ва маълум муддатга яратганини тафаккур қилиб кўрмасларми?! Ҳақиқатан, одамлардан кўплари Роббиларига рўбарў бўлишни инкор қилгувчилардир'}, {'chapter': 30, 'verse': 9, 'text': 'أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَانُوٓاْ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَأَثَارُواْ ٱلۡأَرۡضَ وَعَمَرُوهَآ أَكۡثَرَ مِمَّا عَمَرُوهَا وَجَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nЕр юзида юриб, ўзларидан олдингиларнинг оқибати қандай бўлганига назар солмасларми?! Уларнинг булардан қувватлари кўпроқ эди ва ерга ишлов бериб, уни булар обод қилгандан кўра кўпроқ обод қилган эдилар. Ва уларга Пайғамбарлари очиқ-ойдин(оят)лар билан келдилар. Аллоҳ уларга зулм қилмас эди, лекин улар ўзларига зулм қилардилар'}, {'chapter': 30, 'verse': 10, 'text': 'ثُمَّ كَانَ عَٰقِبَةَ ٱلَّذِينَ أَسَـٰٓـُٔواْ ٱلسُّوٓأَىٰٓ أَن كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ وَكَانُواْ بِهَا يَسۡتَهۡزِءُونَ\nСўнгра ёмонлик қилганларнинг оқибати Аллоҳнинг оятларини ёлғонга чиқарганлари ва уларни истеҳзо қилганлари учун ёмон бўлди'}, {'chapter': 30, 'verse': 11, 'text': 'ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ\nАллоҳ махлуқотни йўқдан бор қилур, сўнгра уни яна қайтадан вужудга келтирур, сўнгра Унга қайтарилурсиз'}, {'chapter': 30, 'verse': 12, 'text': 'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُبۡلِسُ ٱلۡمُجۡرِمُونَ\nҚиёмат қоим бўлган кунда жиноятчилар ноумид бўлурлар'}, {'chapter': 30, 'verse': 13, 'text': 'وَلَمۡ يَكُن لَّهُم مِّن شُرَكَآئِهِمۡ شُفَعَـٰٓؤُاْ وَكَانُواْ بِشُرَكَآئِهِمۡ كَٰفِرِينَ\nУларга (Аллоҳга келтирган) шерикларидан шафоатчилар бўлмади ва улар шерикларига куфр келтиргувчи бўлдилар'}, {'chapter': 30, 'verse': 14, 'text': 'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَتَفَرَّقُونَ\nҚиёмат қоим бўлган куни, ўша куни (гуруҳларга) бўлинурлар'}, {'chapter': 30, 'verse': 15, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَهُمۡ فِي رَوۡضَةٖ يُحۡبَرُونَ\nБас, иймон келтириб, яхши амалларни қилганлар, улар жаннатда шодланурлар'}, {'chapter': 30, 'verse': 16, 'text': 'وَأَمَّا ٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآيِٕ ٱلۡأٓخِرَةِ فَأُوْلَـٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ\nКуфр келтириб, Бизнинг оятларимизни ва охират мулоқотини ёлғонга чиқарганлар, ана ўшалар азобга ҳозир қилинурлар'}, {'chapter': 30, 'verse': 17, 'text': 'فَسُبۡحَٰنَ ٱللَّهِ حِينَ تُمۡسُونَ وَحِينَ تُصۡبِحُونَ\nБас, тунга киришингизда ҳам, тонгга киришингизда ҳам Аллоҳни поклаб ёд этинг'}, {'chapter': 30, 'verse': 18, 'text': 'وَلَهُ ٱلۡحَمۡدُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَعَشِيّٗا وَحِينَ تُظۡهِرُونَ\nОсмонлару ердаги ҳамду сано Уникидир. Оқшомда ва пешинда ҳам (поклаб ёд этинг)'}, {'chapter': 30, 'verse': 19, 'text': 'يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَيُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّ وَيُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ وَكَذَٰلِكَ تُخۡرَجُونَ\nУ ўликдан тирикни чиқарур ва тирикдан ўликни чиқарур ва ерни ўлимидан кейин тирилтирур. Шунга ўхшаш, сиз ҳам чиқарилурсиз'}, {'chapter': 30, 'verse': 20, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَكُم مِّن تُرَابٖ ثُمَّ إِذَآ أَنتُم بَشَرٞ تَنتَشِرُونَ\nСизни тупроқдан яратгани ва сўнгра сизлар башарга айланиб тарқалишингиз Унинг оят-белгиларидандир'}, {'chapter': 30, 'verse': 21, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا لِّتَسۡكُنُوٓاْ إِلَيۡهَا وَجَعَلَ بَيۡنَكُم مَّوَدَّةٗ وَرَحۡمَةًۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nВа сизларга сокинлик топишингиз учун ўзингиздан жуфтлар яратганлиги ва ораларингизда севги ва марҳаматни солиб қўйгани (ҳам) Унинг оят-белгиларидандир. Албатта, бунда тафаккур қиладиган қавмлар учун оят-белгилар бордир'}, {'chapter': 30, 'verse': 22, 'text': 'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفُ أَلۡسِنَتِكُمۡ وَأَلۡوَٰنِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡعَٰلِمِينَ\nВа Унинг оят-белгиларидан осмонлару ернинг яратилиши ва тилларингиз ва рангларингизнинг турлича бўлишидир. Албатта, бунда олимлар учун оят-белгилар бордир'}, {'chapter': 30, 'verse': 23, 'text': 'وَمِنۡ ءَايَٰتِهِۦ مَنَامُكُم بِٱلَّيۡلِ وَٱلنَّهَارِ وَٱبۡتِغَآؤُكُم مِّن فَضۡلِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ\nВа Унинг оят-белгиларидан кечаси ва кундузи ухлашингиз ҳамда Унинг фазлидан талаб қилишингиздир. Албатта, бунда эшитадиган қавм учун оят-белгилар бордир'}, {'chapter': 30, 'verse': 24, 'text': 'وَمِنۡ ءَايَٰتِهِۦ يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مَآءٗ فَيُحۡيِۦ بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nВа Унинг оятларидан сизга қўрқув ва умид учун чақмоқни кўрсатур ва осмондан сувни нозил қилади-да, у билан ерни ўлгандан кейин тирилтирур. Албатта, бунда ақл юритадиган қавм учун оят-белгилар бордир'}, {'chapter': 30, 'verse': 25, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَن تَقُومَ ٱلسَّمَآءُ وَٱلۡأَرۡضُ بِأَمۡرِهِۦۚ ثُمَّ إِذَا دَعَاكُمۡ دَعۡوَةٗ مِّنَ ٱلۡأَرۡضِ إِذَآ أَنتُمۡ تَخۡرُجُونَ\nУнинг оят-белгиларидан, осмонлару ер Унинг амри ила туришидир. Сўнгра сизларни бир бор чақирса, ердан ҳаммангиз бирдан чиқарсизлар'}, {'chapter': 30, 'verse': 26, 'text': 'وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ\nОсмонлару ердаги кимсалар Унга хосдир. Барчалари Унга итоат қилгувчидирлар'}, {'chapter': 30, 'verse': 27, 'text': 'وَهُوَ ٱلَّذِي يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَهُوَ أَهۡوَنُ عَلَيۡهِۚ وَلَهُ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ махлуқотни йўқдан бор қилур, сўнгра уни яна қайтадан вужудга келтирур, бу Унга жуда осондир. Осмонлару ердаги энг олий васф Уникидир. У азийз ва ҳакийм Зотдир'}, {'chapter': 30, 'verse': 28, 'text': 'ضَرَبَ لَكُم مَّثَلٗا مِّنۡ أَنفُسِكُمۡۖ هَل لَّكُم مِّن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن شُرَكَآءَ فِي مَا رَزَقۡنَٰكُمۡ فَأَنتُمۡ فِيهِ سَوَآءٞ تَخَافُونَهُمۡ كَخِيفَتِكُمۡ أَنفُسَكُمۡۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡقِلُونَ\nУ сизга ўзингиздан мисол келтирди. Сизларнинг қўлингиздаги(қулларингиз)дан Биз сизга ризқ қилиб берган нарсада шерикларингиз бор-у, сизлар у(ризқ)да тенг бўлиб, улар(ҳақи)да ҳам ўз(ҳақ)ингизда қўрққанингиздек қўрқасизми?! Ақл юритадиган қавмлар учун оятларни ана шундай муфассал қилиб борурмиз'}, {'chapter': 30, 'verse': 29, 'text': 'بَلِ ٱتَّبَعَ ٱلَّذِينَ ظَلَمُوٓاْ أَهۡوَآءَهُم بِغَيۡرِ عِلۡمٖۖ فَمَن يَهۡدِي مَنۡ أَضَلَّ ٱللَّهُۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nЙўқ!!! Зулм қилганлар илмсиз равишда ҳавойи нафсларига эргашдилар, холос. Аллоҳ залолатга солган кимсани ким ҳидоятга сола оларди?! Ва уларга нусрат бергувчилар бўлмас'}, {'chapter': 30, 'verse': 30, 'text': 'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗاۚ فِطۡرَتَ ٱللَّهِ ٱلَّتِي فَطَرَ ٱلنَّاسَ عَلَيۡهَاۚ لَا تَبۡدِيلَ لِخَلۡقِ ٱللَّهِۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nБас, сен ҳаққа мойил бўлиб, динга юз тут. Бу фитрат Аллоҳ одамларни яратган асл табиатдир. Аллоҳнинг яратганини ўзгартириб бўлмас. Ушбу тўғри диндир. Лекин одамларнинг кўпи билмаслар'}, {'chapter': 30, 'verse': 31, 'text': '۞مُنِيبِينَ إِلَيۡهِ وَٱتَّقُوهُ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَلَا تَكُونُواْ مِنَ ٱلۡمُشۡرِكِينَ\n(Ҳар бир нарсада) Унга қайтгувчи бўлароқ, Унга тақво қилинг, намозни тўкис адо этинг ва мушриклардан бўлманг'}, {'chapter': 30, 'verse': 32, 'text': 'مِنَ ٱلَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمۡ فَرِحُونَ\nДинларида тафриқага тушган, гуруҳ-гуруҳ бўлиб олиб, ҳар бир фирқа ўз олдидаги нарсадан шод бўлганлардан (бўлманг)'}, {'chapter': 30, 'verse': 33, 'text': 'وَإِذَا مَسَّ ٱلنَّاسَ ضُرّٞ دَعَوۡاْ رَبَّهُم مُّنِيبِينَ إِلَيۡهِ ثُمَّ إِذَآ أَذَاقَهُم مِّنۡهُ رَحۡمَةً إِذَا فَرِيقٞ مِّنۡهُم بِرَبِّهِمۡ يُشۡرِكُونَ\nҚачон одамларга зарар етса, Роббиларининг Ўзигагина қайтиб, дуо қилурлар. Сўнгра У уларга Ўзидан раҳмат тоттирганда, кўрибсанки, улардан баъзи гуруҳлари Роббиларига ширк келтирурлар'}, {'chapter': 30, 'verse': 34, 'text': 'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ\nБиз уларга берган нарсага куфр келтираверсинлар! Бас, ҳузурланаверинг! Яқинда биласизлар'}, {'chapter': 30, 'verse': 35, 'text': 'أَمۡ أَنزَلۡنَا عَلَيۡهِمۡ سُلۡطَٰنٗا فَهُوَ يَتَكَلَّمُ بِمَا كَانُواْ بِهِۦ يُشۡرِكُونَ\nЁки Биз уларга ҳужжат туширганмиз-у, у(ҳужжат) Унга ширк келтирган нарсалари (жоизлиги) ҳақида сўзларми'}, {'chapter': 30, 'verse': 36, 'text': 'وَإِذَآ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ فَرِحُواْ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ إِذَا هُمۡ يَقۡنَطُونَ\nВа қачон одамларга раҳматни тоттирсак, ундан шод бўлурлар. Агар уларга ўз қўллари тақдим қилган нарса туфайли ёмонлик етса, дарҳол умидсизликка тушурлар'}, {'chapter': 30, 'verse': 37, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nУлар Аллоҳ Ўзи хоҳлаган кишисига ризқни кенг ёки тор қилишини билмасларми? Албатта, бунда иймон келтирадиган қавм учун оят-белгилар бордир'}, {'chapter': 30, 'verse': 38, 'text': 'فَـَٔاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِۚ ذَٰلِكَ خَيۡرٞ لِّلَّذِينَ يُرِيدُونَ وَجۡهَ ٱللَّهِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nБас, қариндошга, мискинга ва йўлда қолганга ҳаққини бер. Ана шу, Аллоҳнинг юзини ирода қилганлар учун хайрлидир. Ва ана ўшалар нажот топгувчилардир'}, {'chapter': 30, 'verse': 39, 'text': 'وَمَآ ءَاتَيۡتُم مِّن رِّبٗا لِّيَرۡبُوَاْ فِيٓ أَمۡوَٰلِ ٱلنَّاسِ فَلَا يَرۡبُواْ عِندَ ٱللَّهِۖ وَمَآ ءَاتَيۡتُم مِّن زَكَوٰةٖ تُرِيدُونَ وَجۡهَ ٱللَّهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُضۡعِفُونَ\nОдамлар моллари ичида зиёда бўлсин деб рибо учун берган молингиз Аллоҳ ҳузурида зиёда бўлмас. Аллоҳнинг юзини кўзлаб берган закотингиз эса, бас, ана ўшалар бир неча баробар қилиб олгувчилардир'}, {'chapter': 30, 'verse': 40, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَكُمۡ ثُمَّ رَزَقَكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۖ هَلۡ مِن شُرَكَآئِكُم مَّن يَفۡعَلُ مِن ذَٰلِكُم مِّن شَيۡءٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nСизларни халқ қилган, сўнгра ризқлантирган, кейин ўлдирадиган ва яна тирилтирадиган зот Аллоҳдир. Сизнинг (Аллоҳга келтирган) шерикларингиздан ким ана шуларингиздан бирортасини қила оладир?! У зот улар ширк келтираётган нарсалардан пок ва олий бўлди'}, {'chapter': 30, 'verse': 41, 'text': 'ظَهَرَ ٱلۡفَسَادُ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ بِمَا كَسَبَتۡ أَيۡدِي ٱلنَّاسِ لِيُذِيقَهُم بَعۡضَ ٱلَّذِي عَمِلُواْ لَعَلَّهُمۡ يَرۡجِعُونَ\nҚилган амалларининг баъзисини тоттириш учун, одамлар қўллари касб қилган нарсалар туфайли қуруқликдаю денгизда фасод зоҳир бўлди. Шоядки, улар қайтсалар'}, {'chapter': 30, 'verse': 42, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلُۚ كَانَ أَكۡثَرُهُم مُّشۡرِكِينَ\nСен: «Ер юзида сайр қилинглар. Олдин ўтганларнинг оқибати қандай бўлганига назар солинглар. Уларнинг кўплари мушрик бўлган эдилар», деб айт'}, {'chapter': 30, 'verse': 43, 'text': 'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ ٱلۡقَيِّمِ مِن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۖ يَوۡمَئِذٖ يَصَّدَّعُونَ\nБас, Аллоҳ томонидан рад қилинмайдиган кун келмасдан аввал тўғри динга юз тут. Ўша кунда (одамлар) бўлак-бўлак бўлурлар'}, {'chapter': 30, 'verse': 44, 'text': 'مَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَمَنۡ عَمِلَ صَٰلِحٗا فَلِأَنفُسِهِمۡ يَمۡهَدُونَ\nКим куфр келтирса, унинг куфри ўзига зарар. Кимлар яхши амаллар қилган бўлса, ўзлари учун (замин) тайёрлар'}, {'chapter': 30, 'verse': 45, 'text': 'لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡكَٰفِرِينَ\nУ зот иймон келтирган ва яхши амалларни қилганларни Ўз фазли ила мукофотлаши учундир. Албатта, У кофирларни севмас'}, {'chapter': 30, 'verse': 46, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَن يُرۡسِلَ ٱلرِّيَاحَ مُبَشِّرَٰتٖ وَلِيُذِيقَكُم مِّن رَّحۡمَتِهِۦ وَلِتَجۡرِيَ ٱلۡفُلۡكُ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nВа Унинг оят-белгиларидан сизга Ўз раҳматидан тоттириш учун, Ўз амри ила кемаларни юрдириш учун ва Унинг фазлидан (ризқ) талаб қилишингиз учун шамолларни хушхабарчи қилиб юборишидир. Шоядки, шукр қилсангиз'}, {'chapter': 30, 'verse': 47, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِٱلۡبَيِّنَٰتِ فَٱنتَقَمۡنَا مِنَ ٱلَّذِينَ أَجۡرَمُواْۖ وَكَانَ حَقًّا عَلَيۡنَا نَصۡرُ ٱلۡمُؤۡمِنِينَ\nБатаҳқиқ, Биз сендан олдин ҳам Пайғамбарларни қавмларига юбордик. Бас, улар (қавмларига) очиқ-ойдин ҳужжатлар келтирганлар. Сўнг Биз жиноят қилганлардан интиқом олганмиз. Мўминларга нусрат бериш Бизнинг зиммамиздаги ҳақ бўлган'}, {'chapter': 30, 'verse': 48, 'text': 'ٱللَّهُ ٱلَّذِي يُرۡسِلُ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَيَبۡسُطُهُۥ فِي ٱلسَّمَآءِ كَيۡفَ يَشَآءُ وَيَجۡعَلُهُۥ كِسَفٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦۖ فَإِذَآ أَصَابَ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ\nАллоҳ шундай зотки, шамолларни юбориб, улар ила булутни қўзғатар ва у(булут)ни осмонда Ўзи хоҳлаганича ёяр ва бўлак-бўлак қилар, бас, унинг орасидан ёмғир чиқаётганини кўрасан. Қачон у(ёмғир)ни Ўз бандаларидан хоҳлаган кимсаларга етказса, улар дарҳол шод бўлурлар'}, {'chapter': 30, 'verse': 49, 'text': 'وَإِن كَانُواْ مِن قَبۡلِ أَن يُنَزَّلَ عَلَيۡهِم مِّن قَبۡلِهِۦ لَمُبۡلِسِينَ\nҲолбуки, устларига (ёмғир) туширилишидан олдин, ҳа, ундан олдин, мутлақо умидсиз эдилар'}, {'chapter': 30, 'verse': 50, 'text': 'فَٱنظُرۡ إِلَىٰٓ ءَاثَٰرِ رَحۡمَتِ ٱللَّهِ كَيۡفَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ ذَٰلِكَ لَمُحۡيِ ٱلۡمَوۡتَىٰۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nБас, Роббинг раҳмати асарларига назар сол. У ерни ўлиб ётганидан кейин қандай тирилтирур?! Албатта, ана шу Зот ўликларни ҳам тирилтиргувчидир ва У ҳар бир нарсага қодирдир'}, {'chapter': 30, 'verse': 51, 'text': 'وَلَئِنۡ أَرۡسَلۡنَا رِيحٗا فَرَأَوۡهُ مُصۡفَرّٗا لَّظَلُّواْ مِنۢ بَعۡدِهِۦ يَكۡفُرُونَ\nАгар Биз бир шамол юбориб, улар (экинларини) сарғайган ҳолда кўрсалар, ундан кейин, албатта, куфрони неъмат қилишга ўтарлар'}, {'chapter': 30, 'verse': 52, 'text': 'فَإِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوۡاْ مُدۡبِرِينَ\nАлбатта, сен ўликка эшиттира олмассан ва даъватни ортларига бурилиб кетаётган карларга эшиттира олмассан'}, {'chapter': 30, 'verse': 53, 'text': 'وَمَآ أَنتَ بِهَٰدِ ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ\nСен кўрларни гумроҳликларидан ҳидоят қилгувчи эмассан. Сен фақат Бизнинг оятларимизга иймон келтирганларгагина эшиттира олурсан. Ана ўшалар мусулмонлардир'}, {'chapter': 30, 'verse': 54, 'text': '۞ٱللَّهُ ٱلَّذِي خَلَقَكُم مِّن ضَعۡفٖ ثُمَّ جَعَلَ مِنۢ بَعۡدِ ضَعۡفٖ قُوَّةٗ ثُمَّ جَعَلَ مِنۢ بَعۡدِ قُوَّةٖ ضَعۡفٗا وَشَيۡبَةٗۚ يَخۡلُقُ مَا يَشَآءُۚ وَهُوَ ٱلۡعَلِيمُ ٱلۡقَدِيرُ\nАллоҳ сизни заифликда яратган, сўнгра заифликдан кейин қувватли қилган, сўнгра қувватдан кейин яна заифлик ва қарилик ориз қилган Зотдир. У хоҳлаганини яратадир. У ўта илмли ва ўта қудратлидир'}, {'chapter': 30, 'verse': 55, 'text': 'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُقۡسِمُ ٱلۡمُجۡرِمُونَ مَا لَبِثُواْ غَيۡرَ سَاعَةٖۚ كَذَٰلِكَ كَانُواْ يُؤۡفَكُونَ\nҚиёмат қоим бўладиган куни жиноятчилар бир соатдан бошқа турмаганлари ҳақида қасам ичурлар. Улар ана шундай ўгирилар эдилар'}, {'chapter': 30, 'verse': 56, 'text': 'وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَٱلۡإِيمَٰنَ لَقَدۡ لَبِثۡتُمۡ فِي كِتَٰبِ ٱللَّهِ إِلَىٰ يَوۡمِ ٱلۡبَعۡثِۖ فَهَٰذَا يَوۡمُ ٱلۡبَعۡثِ وَلَٰكِنَّكُمۡ كُنتُمۡ لَا تَعۡلَمُونَ\nИлм ва иймон берилганлар: «Батаҳқиқ, Аллоҳнинг китоби бўйича, қайта тирилиш кунигача турдингиз. Бас, бугун қайта тирилиш кунидир. Лекин сизлар билмас эдингиз», дерлар'}, {'chapter': 30, 'verse': 57, 'text': 'فَيَوۡمَئِذٖ لَّا يَنفَعُ ٱلَّذِينَ ظَلَمُواْ مَعۡذِرَتُهُمۡ وَلَا هُمۡ يُسۡتَعۡتَبُونَ\nБас, бугунги кунда зулм қилганларга маъзиратлари манфаат бермас ҳамда улар итобга ҳам қайтарилмаслар'}, {'chapter': 30, 'verse': 58, 'text': 'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖۚ وَلَئِن جِئۡتَهُم بِـَٔايَةٖ لَّيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ أَنتُمۡ إِلَّا مُبۡطِلُونَ\nБатаҳқиқ, Биз ушбу Қуръонда одамлар учун турли мисоллар келтирдик. Аниқки, агар сен уларга мўъжиза келтирсанг ҳам, куфр келтирганлар: «Сиз ботилчилардан ўзга эмассиз», дерлар'}, {'chapter': 30, 'verse': 59, 'text': 'كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلَّذِينَ لَا يَعۡلَمُونَ\nАллоҳ билмайдиганларнинг қалбларини ана шундай қилиб муҳрлар'}, {'chapter': 30, 'verse': 60, 'text': 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ وَلَا يَسۡتَخِفَّنَّكَ ٱلَّذِينَ لَا يُوقِنُونَ\nБас, сабр қил. Албатта, Аллоҳнинг ваъдаси ҳақдир. Собит иймон келтирмаганлар сени енгилтакликка бошламасинлар'}], '31': [{'chapter': 31, 'verse': 1, 'text': 'الٓمٓ\nАлиф. Лаам. Мийм'}, {'chapter': 31, 'verse': 2, 'text': 'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡحَكِيمِ\nУшбулар ҳикматли Китоб оятларидир'}, {'chapter': 31, 'verse': 3, 'text': 'هُدٗى وَرَحۡمَةٗ لِّلۡمُحۡسِنِينَ\nУ муҳсинларга ҳидоят ва раҳматдир'}, {'chapter': 31, 'verse': 4, 'text': 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ\nУлар намозни тўкис адо этадиган, закотни берадиган ва охиратга ишонч-ла иймон келтирадиганлардир'}, {'chapter': 31, 'verse': 5, 'text': 'أُوْلَـٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nАна ўшалар Роббиларидан бўлган ҳидоятдадирлар. Ана ўшалар ўзлари нажот топгувчилардир'}, {'chapter': 31, 'verse': 6, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَشۡتَرِي لَهۡوَ ٱلۡحَدِيثِ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّخِذَهَا هُزُوًاۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ\nОдамлар орасида(ўзлари) илмсиз равишда Аллоҳнинг йўлидан адаштириш учун ва у(йўл)ни истеҳзо қилиш учун беҳуда сўзни сотиб оладиган кимсалар ҳам бор. Ана ўшаларга, ўзларига хорловчи азоб бор'}, {'chapter': 31, 'verse': 7, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا وَلَّىٰ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَا كَأَنَّ فِيٓ أُذُنَيۡهِ وَقۡرٗاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٍ\nҚачон унга Бизнинг оятларимиз тиловат қилинса, худди уларни эшитмагандек, худди қулоғида оғирлик бордек, мутакаббирлик билан ортга қараб кетар. Бас, унга аламли азобнинг башоратини бер'}, {'chapter': 31, 'verse': 8, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ جَنَّـٰتُ ٱلنَّعِيمِ\nАлбатта, иймон келтириб, яхши амаллар қилганларга, ўшаларга неъмат жаннатлари бордир'}, {'chapter': 31, 'verse': 9, 'text': 'خَٰلِدِينَ فِيهَاۖ وَعۡدَ ٱللَّهِ حَقّٗاۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУлар унда абадий қолурлар. Аллоҳнинг ваъдаси ҳақдир. Ва У Азийз ва Ҳакиймдир'}, {'chapter': 31, 'verse': 10, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ وَأَلۡقَىٰ فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ\nУ, сиз кўриб турганингиздек, осмонларни устунсиз яратган, сизларни тебратмаслиги учун ер юзига оғир тоғларни ташлаган ва у(ер)да ҳар турли жониворларни таратиб қўйган Зотдир. Ва осмондан сув тушириб, у билан у(ер юзи)да турли гўзал ва карамли жуфтларни ўстириб қўйдик'}, {'chapter': 31, 'verse': 11, 'text': 'هَٰذَا خَلۡقُ ٱللَّهِ فَأَرُونِي مَاذَا خَلَقَ ٱلَّذِينَ مِن دُونِهِۦۚ بَلِ ٱلظَّـٰلِمُونَ فِي ضَلَٰلٖ مُّبِينٖ\nБу, Аллоҳнинг яратганидир. Қани Менга кўрсатинг-чи, Ундан бошқалар нимани яратдилар. Йўқ! Золимлар очиқ-ойдин адашувдадирлар'}, {'chapter': 31, 'verse': 12, 'text': 'وَلَقَدۡ ءَاتَيۡنَا لُقۡمَٰنَ ٱلۡحِكۡمَةَ أَنِ ٱشۡكُرۡ لِلَّهِۚ وَمَن يَشۡكُرۡ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِيٌّ حَمِيدٞ\nБатаҳқиқ, Биз Луқмонга: «Аллоҳга шукр қил. Ким шукр қилса, ўзи учун қиладир. Ким ношукрлик қилса, бас, албатта, Аллоҳ беҳожат ва мақталган Зотдир», деб ҳикматни бердик'}, {'chapter': 31, 'verse': 13, 'text': 'وَإِذۡ قَالَ لُقۡمَٰنُ لِٱبۡنِهِۦ وَهُوَ يَعِظُهُۥ يَٰبُنَيَّ لَا تُشۡرِكۡ بِٱللَّهِۖ إِنَّ ٱلشِّرۡكَ لَظُلۡمٌ عَظِيمٞ\nЛуқмон ўғлига ваъз-насиҳат қилиб айтганларини эсла: «Эй ўғилчам, Аллоҳга ширк келтирма. Албатта, ширк катта зулмдир»'}, {'chapter': 31, 'verse': 14, 'text': 'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ حَمَلَتۡهُ أُمُّهُۥ وَهۡنًا عَلَىٰ وَهۡنٖ وَفِصَٰلُهُۥ فِي عَامَيۡنِ أَنِ ٱشۡكُرۡ لِي وَلِوَٰلِدَيۡكَ إِلَيَّ ٱلۡمَصِيرُ\nБиз инсонга ота-онаси ҳақида тавсия қилдик: онаси уни заифлик устига заифлик билан кўтарди'}, {'chapter': 31, 'verse': 15, 'text': 'وَإِن جَٰهَدَاكَ عَلَىٰٓ أَن تُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَاۖ وَصَاحِبۡهُمَا فِي ٱلدُّنۡيَا مَعۡرُوفٗاۖ وَٱتَّبِعۡ سَبِيلَ مَنۡ أَنَابَ إِلَيَّۚ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nАгар икковлари сени ўзинг билмаган нарсани Менга ширк келтиришга зўрласалар, бас, уларга итоат этма! Ва дунё-да икковларига яхшилик қил ҳамда Менга йўналганларнинг йўлига эргаш. Сўнгра қайтишингиз фақат Менгадир. Бас, Мен сизга нима амал қилганингиз хабарини берурман'}, {'chapter': 31, 'verse': 16, 'text': 'يَٰبُنَيَّ إِنَّهَآ إِن تَكُ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٖ فَتَكُن فِي صَخۡرَةٍ أَوۡ فِي ٱلسَّمَٰوَٰتِ أَوۡ فِي ٱلۡأَرۡضِ يَأۡتِ بِهَا ٱللَّهُۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٞ\n«Эй ўғилчам, албатта, у (амал) агар ачитқининг бир дона уруғича бўлса-ю, у бир қоя тош ичида ёки осмонларда ёхуд ерда бўлса ҳам, Аллоҳ уни келтирур. Албатта, Аллоҳ ўта лутфли ва ўта хабардор Зотдир.»'}, {'chapter': 31, 'verse': 17, 'text': 'يَٰبُنَيَّ أَقِمِ ٱلصَّلَوٰةَ وَأۡمُرۡ بِٱلۡمَعۡرُوفِ وَٱنۡهَ عَنِ ٱلۡمُنكَرِ وَٱصۡبِرۡ عَلَىٰ مَآ أَصَابَكَۖ إِنَّ ذَٰلِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ\n«Эй ўғилчам, намозни тўкис адо қил, яхшиликка буюриб, ёмонликдан қайтар ва ўзингга етган мусийбатга сабр қил. Албатта, булар азм этилажак ишлардандир.»'}, {'chapter': 31, 'verse': 18, 'text': 'وَلَا تُصَعِّرۡ خَدَّكَ لِلنَّاسِ وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٖ\n«Одамлардан такаббур-ла юз ўгирма ва ер юзида кибр-ҳаво ила юрма. Албатта, Аллоҳ ҳеч бир мутакаббир ва мақтанчоқни севмас.»'}, {'chapter': 31, 'verse': 19, 'text': 'وَٱقۡصِدۡ فِي مَشۡيِكَ وَٱغۡضُضۡ مِن صَوۡتِكَۚ إِنَّ أَنكَرَ ٱلۡأَصۡوَٰتِ لَصَوۡتُ ٱلۡحَمِيرِ\n«Юришингда мўътадил бўл ва овозингни пасайтир. Чунки, овозларнинг энг ёмони эшакнинг овозидир.»'}, {'chapter': 31, 'verse': 20, 'text': 'أَلَمۡ تَرَوۡاْ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَأَسۡبَغَ عَلَيۡكُمۡ نِعَمَهُۥ ظَٰهِرَةٗ وَبَاطِنَةٗۗ وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ\nАллоҳ сизларга осмонлардаги нарсаларни ва ердаги нарсаларни беминнат хизматкор қилиб қўйганини ва сизга зоҳирий ва ботиний неъматларни мукаммал қилиб берганини билмадингизми?! Одамлар орасида илмсиз, ҳидоятсиз ва равшан китобсиз (бўла туриб) Аллоҳ ҳақида тортишадиганлари ҳам бор'}, {'chapter': 31, 'verse': 21, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُواْ مَآ أَنزَلَ ٱللَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ٱلشَّيۡطَٰنُ يَدۡعُوهُمۡ إِلَىٰ عَذَابِ ٱلسَّعِيرِ\nҚачон уларга: «Аллоҳ нозил қилган нарсага эргашинглар», дейилса: «Йўқ! Биз оталаримизни ниманинг устида топсак, ўшанга эргашурмиз», дерлар. Агар шайтон уларни дўзах азобига чақираётган бўлса ҳам-а'}, {'chapter': 31, 'verse': 22, 'text': '۞وَمَن يُسۡلِمۡ وَجۡهَهُۥٓ إِلَى ٱللَّهِ وَهُوَ مُحۡسِنٞ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰۗ وَإِلَى ٱللَّهِ عَٰقِبَةُ ٱلۡأُمُورِ\nКим гўзал амалларни қилгувчи бўлган ҳолида Аллоҳга юз тутса, батаҳқиқ, у ишончли арқонни маҳкам тутибди. Ишларнинг оқибати фақат Аллоҳга қайтур'}, {'chapter': 31, 'verse': 23, 'text': 'وَمَن كَفَرَ فَلَا يَحۡزُنكَ كُفۡرُهُۥٓۚ إِلَيۡنَا مَرۡجِعُهُمۡ فَنُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nКим куфр келтирса, унинг куфри сени хафа қилмасин. Қайтишлари фақат Бизгадир. Бас, Биз уларга нима амал қилганлари хабарини берурмиз. Албатта, Аллоҳ қалблардаги нарсаларни ўта билгувчидир'}, {'chapter': 31, 'verse': 24, 'text': 'نُمَتِّعُهُمۡ قَلِيلٗا ثُمَّ نَضۡطَرُّهُمۡ إِلَىٰ عَذَابٍ غَلِيظٖ\nУларни бир оз ҳузурлантирурмиз, сўнгра қаттиқ азобга мажбур қилурмиз'}, {'chapter': 31, 'verse': 25, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nАгар улардан: «Осмонлару ерни ким яратган?» деб сўрасанг, албатта, «Аллоҳ», дерлар. Сен: «Аллоҳга ҳамд бўлсин», деб айт. Йўқ! Кўплари билмаслар'}, {'chapter': 31, 'verse': 26, 'text': 'لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nОсмонлару ердаги нарсалар Аллоҳникидир. Албатта, Аллоҳ Ўзи беҳожат ва мақталган Зотдир'}, {'chapter': 31, 'verse': 27, 'text': 'وَلَوۡ أَنَّمَا فِي ٱلۡأَرۡضِ مِن شَجَرَةٍ أَقۡلَٰمٞ وَٱلۡبَحۡرُ يَمُدُّهُۥ مِنۢ بَعۡدِهِۦ سَبۡعَةُ أَبۡحُرٖ مَّا نَفِدَتۡ كَلِمَٰتُ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nАгар ер юзидаги дарахтнинг барчаси қаламлар бўлиб, денгиз — унинг ортида етти денгиз мадад бериб туриб — (сиёҳ) бўлса ҳам, Аллоҳнинг калималари битмас. Албатта, Аллоҳ Азийз ва Ҳакиймдир'}, {'chapter': 31, 'verse': 28, 'text': 'مَّا خَلۡقُكُمۡ وَلَا بَعۡثُكُمۡ إِلَّا كَنَفۡسٖ وَٰحِدَةٍۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٌ\nСизларни яратиш ҳам, қайта тирилтириш ҳам фақат бир жонни (яратиш ва қайта тирилтириш) кабидир. Албатта, Аллоҳ ўта эшитгувчи ва ўта кўргувчидир'}, {'chapter': 31, 'verse': 29, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِيٓ إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَأَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ خَبِيرٞ\nАллоҳ кечани кундузга киритишини ва кундузни кечага киритишини, қуёш ва ойни беминнат хизматкор қилиб қўйганини, уларнинг ҳар бири белгиланган муддат томон юришини ва, албатта, Аллоҳ нима амал қилаётганингиздан хабардор эканини кўрмадингми'}, {'chapter': 31, 'verse': 30, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِ ٱلۡبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ\nБу, албатта, Аллоҳнинг Ўзи ҳақ, албатта, Ундан ўзга илтижо қилаётганлари ботил ва, албатта, Аллоҳнинг Ўзи энг юксак ва энг буюк зот эканидандир'}, {'chapter': 31, 'verse': 31, 'text': 'أَلَمۡ تَرَ أَنَّ ٱلۡفُلۡكَ تَجۡرِي فِي ٱلۡبَحۡرِ بِنِعۡمَتِ ٱللَّهِ لِيُرِيَكُم مِّنۡ ءَايَٰتِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ\nКемалар Аллоҳнинг неъмати ила сизга Унинг оят-белгиларини кўрсатиш учун денгизда юрганини кўрмадингми?! Албатта, бунда ҳар бир ўта сабрли ва ўта шукрлилар учун оят-белгилар бордир'}, {'chapter': 31, 'verse': 32, 'text': 'وَإِذَا غَشِيَهُم مَّوۡجٞ كَٱلظُّلَلِ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ فَمِنۡهُم مُّقۡتَصِدٞۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا كُلُّ خَتَّارٖ كَفُورٖ\nҚачон уларни тоғ каби тўлқин ўраб қолса, Аллоҳнинг динига ихлос эртароқ, дуо қилурлар. Нажот бериб, уларни қуруқликка чиқарса, баъзилари тежамкор бўлур. Бизнинг оятларимизни фақат ўта хоин ва ўта кофирларгина инкор этурлар'}, {'chapter': 31, 'verse': 33, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمۡ وَٱخۡشَوۡاْ يَوۡمٗا لَّا يَجۡزِي وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوۡلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَيۡـًٔاۚ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ\nЭй одамлар! Роббингизга тақво қилинг ва ота боласига фойда бера олмайдиган, бола отасига бирор нарсада фойда бергувчи бўлмайдиган Кундан қўрқинг. Албатта, Аллоҳнинг ваъдаси ҳақдир. Бас, ҳаёти дунё сизни ғурурга кетказмасин. Ўта ғурурга кетказгувчи сизни Аллоҳ ила ғурурга кетказмасин'}, {'chapter': 31, 'verse': 34, 'text': 'إِنَّ ٱللَّهَ عِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَيُنَزِّلُ ٱلۡغَيۡثَ وَيَعۡلَمُ مَا فِي ٱلۡأَرۡحَامِۖ وَمَا تَدۡرِي نَفۡسٞ مَّاذَا تَكۡسِبُ غَدٗاۖ وَمَا تَدۡرِي نَفۡسُۢ بِأَيِّ أَرۡضٖ تَمُوتُۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرُۢ\nАлбатта, қиёмат соати илми ёлғиз Аллоҳнинг ҳузуридадир. Ёмғирни У ёғдирур. Бачадонлардаги нарсани ҳам У билур. Ҳеч бир жон эртага нима касб қилишини билмас. Ҳеч бир жон қайси ерда ўлишини ҳам билмас. Албатта, Аллоҳ ўта билгувчи ва ўта хабардордир'}], '32': [{'chapter': 32, 'verse': 1, 'text': 'الٓمٓ\nАлиф. Лаам. Мийм'}, {'chapter': 32, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ ٱلۡعَٰلَمِينَ\nБу китобнинг нозил қилиниши, унда ҳеч шак-шубҳа йўқ, оламларнинг Робби томонидандир'}, {'chapter': 32, 'verse': 3, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۚ بَلۡ هُوَ ٱلۡحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَهۡتَدُونَ\nЁки, унинг ўзи тўқиб олган, дерларми?! Йўқ! У сендан олдин ўзларига ҳеч огоҳлантиргувчи келмаган қавмни огоҳлантиришинг учун Роббингдан (келган) ҳақдир. Шоядки, улар ҳидоят топсалар'}, {'chapter': 32, 'verse': 4, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ مَا لَكُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا شَفِيعٍۚ أَفَلَا تَتَذَكَّرُونَ\nАллоҳ осмонлару Ерни ва улар орасидаги нарсаларни олти кунда яратиб, сўнгра (Зоти ва улуғлигига муносиб суратда) Аршга юксалган Зотдир. Сиз учун ундан ўзга дўст ҳам, шафоатчи ҳам йўқдир. Ахир, эсламайсизларми'}, {'chapter': 32, 'verse': 5, 'text': 'يُدَبِّرُ ٱلۡأَمۡرَ مِنَ ٱلسَّمَآءِ إِلَى ٱلۡأَرۡضِ ثُمَّ يَعۡرُجُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥٓ أَلۡفَ سَنَةٖ مِّمَّا تَعُدُّونَ\nУ зот осмондан ергача барча ишнинг тадбирини қилур. Сўнгра ўша (иш) миқдори сиз санаётган ҳисобда минг йилга тенг бўлган кунда Унга чиқар'}, {'chapter': 32, 'verse': 6, 'text': 'ذَٰلِكَ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАна шу Зот ғойибнию ҳозирни билгувчи, Азийзу Роҳиймдир'}, {'chapter': 32, 'verse': 7, 'text': 'ٱلَّذِيٓ أَحۡسَنَ كُلَّ شَيۡءٍ خَلَقَهُۥۖ وَبَدَأَ خَلۡقَ ٱلۡإِنسَٰنِ مِن طِينٖ\nУ Ўзи яратган ҳар бир нарсани гўзал этган ва инсонни яратишни лойдан бошлаган Зотдир'}, {'chapter': 32, 'verse': 8, 'text': 'ثُمَّ جَعَلَ نَسۡلَهُۥ مِن سُلَٰلَةٖ مِّن مَّآءٖ مَّهِينٖ\nСўнгра, унинг наслини ҳақир бир сувдан ажраб чиқадиган қилди'}, {'chapter': 32, 'verse': 9, 'text': 'ثُمَّ سَوَّىٰهُ وَنَفَخَ فِيهِ مِن رُّوحِهِۦۖ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ\nСўнгра уни ростлади ва унинг ичига Ўз руҳидан пуфлади. У сизларга қулоқ, кўзлар ва диллар пайдо қилди. Камдан-кам шукр қилурсизлар'}, {'chapter': 32, 'verse': 10, 'text': 'وَقَالُوٓاْ أَءِذَا ضَلَلۡنَا فِي ٱلۡأَرۡضِ أَءِنَّا لَفِي خَلۡقٖ جَدِيدِۭۚ بَلۡ هُم بِلِقَآءِ رَبِّهِمۡ كَٰفِرُونَ\nУлар: «Биз ер остида йўқ бўлиб кетгач ҳам, яна янгитдан яратиламизми?!» дерлар. Йўқ! Улар Роббиларига рўбарў бўлишни инкор қилгувчилардир'}, {'chapter': 32, 'verse': 11, 'text': '۞قُلۡ يَتَوَفَّىٰكُم مَّلَكُ ٱلۡمَوۡتِ ٱلَّذِي وُكِّلَ بِكُمۡ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ\nСен: «Сизларга вакил қилинган ўлим фариштаси жонингизни оладир. Сўнгра Роббингизга қайтарилурсиз», деб айт'}, {'chapter': 32, 'verse': 12, 'text': 'وَلَوۡ تَرَىٰٓ إِذِ ٱلۡمُجۡرِمُونَ نَاكِسُواْ رُءُوسِهِمۡ عِندَ رَبِّهِمۡ رَبَّنَآ أَبۡصَرۡنَا وَسَمِعۡنَا فَٱرۡجِعۡنَا نَعۡمَلۡ صَٰلِحًا إِنَّا مُوقِنُونَ\nГуноҳкорларнинг Роббилари ҳузурида бошларини эгиб туриб: «Эй Роббимиз, кўрдик, эшитдик. Бас, бизни ортга қайтар, яхши амал қиламиз. Албатта, биз ишониб етдик», дейишларини кўрсанг эди'}, {'chapter': 32, 'verse': 13, 'text': 'وَلَوۡ شِئۡنَا لَأٓتَيۡنَا كُلَّ نَفۡسٍ هُدَىٰهَا وَلَٰكِنۡ حَقَّ ٱلۡقَوۡلُ مِنِّي لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِينَ\nАгар хоҳлаганимизда, ҳар бир жонга ўз ҳидоятини берар эдик. Лекин Мендан: «Албатта, жаҳаннамни жинлар ва одамларни жамлаб тўлдирурман», деган сўз ҳақ бўлди'}, {'chapter': 32, 'verse': 14, 'text': 'فَذُوقُواْ بِمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَآ إِنَّا نَسِينَٰكُمۡۖ وَذُوقُواْ عَذَابَ ٱلۡخُلۡدِ بِمَا كُنتُمۡ تَعۡمَلُونَ\n«Бас, тотиб кўринглар, ушбу кунингизда учрашувни унутганингиз туфайли Биз ҳам сизни унутдик. Қилиб ўтган амалларингиз туфайли мангу азобни тотинг!» дейилур'}, {'chapter': 32, 'verse': 15, 'text': 'إِنَّمَا يُؤۡمِنُ بِـَٔايَٰتِنَا ٱلَّذِينَ إِذَا ذُكِّرُواْ بِهَا خَرُّواْۤ سُجَّدٗاۤ وَسَبَّحُواْ بِحَمۡدِ رَبِّهِمۡ وَهُمۡ لَا يَسۡتَكۡبِرُونَ۩\nАлбатта, Бизнинг оятларимизга улар-ла эслатма берилганда саждага йиқиладиган, Роббиларига ҳамд ила тасбиҳ айтадиган ва мутакаббирлик қилмайдиганларгина иймон келтирурлар'}, {'chapter': 32, 'verse': 16, 'text': 'تَتَجَافَىٰ جُنُوبُهُمۡ عَنِ ٱلۡمَضَاجِعِ يَدۡعُونَ رَبَّهُمۡ خَوۡفٗا وَطَمَعٗا وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУларнинг ёнбошлари ётар жойдан йироқ бўлур. Улар Роббиларига қўрқув ва умидворлик ила дуо қилурлар ва ўзларига ризқ қилиб берган нарсаларимиздан инфоқ қилурлар'}, {'chapter': 32, 'verse': 17, 'text': 'فَلَا تَعۡلَمُ نَفۡسٞ مَّآ أُخۡفِيَ لَهُم مِّن قُرَّةِ أَعۡيُنٖ جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ\nБас, ҳеч бир жон ўзлари учун қилиб ўтган амаллари мукофотига беркитиб қўйилган кўз қувончларини билмас'}, {'chapter': 32, 'verse': 18, 'text': 'أَفَمَن كَانَ مُؤۡمِنٗا كَمَن كَانَ فَاسِقٗاۚ لَّا يَسۡتَوُۥنَ\nАхир, мўмин бўлган одам фосиқ бўлган одамдек бўлурми?! Тенг бўлмаслар'}, {'chapter': 32, 'verse': 19, 'text': 'أَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَلَهُمۡ جَنَّـٰتُ ٱلۡمَأۡوَىٰ نُزُلَۢا بِمَا كَانُواْ يَعۡمَلُونَ\nАммо, иймон келтириб, яхши амалларни қилганларга, албатта уларга қилиб ўтган амаллари учун жаннатул маъво манзилдир'}, {'chapter': 32, 'verse': 20, 'text': 'وَأَمَّا ٱلَّذِينَ فَسَقُواْ فَمَأۡوَىٰهُمُ ٱلنَّارُۖ كُلَّمَآ أَرَادُوٓاْ أَن يَخۡرُجُواْ مِنۡهَآ أُعِيدُواْ فِيهَا وَقِيلَ لَهُمۡ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ\nАммо фосиқлик қилганлар эса, бас, уларнинг жойлари дўзахдир. Ҳар қачон ундан чиқмоқни ирода қилсалар, унга қайтарилурлар ва уларга: «Ўзингиз ёлғонга чиқарган дўзахнинг азобини тотинг», дейилур'}, {'chapter': 32, 'verse': 21, 'text': 'وَلَنُذِيقَنَّهُم مِّنَ ٱلۡعَذَابِ ٱلۡأَدۡنَىٰ دُونَ ٱلۡعَذَابِ ٱلۡأَكۡبَرِ لَعَلَّهُمۡ يَرۡجِعُونَ\nАлбатта, Биз уларга буюк азобдан олдин яқин азобни тоттирамиз. Шоядки, қайтсалар'}, {'chapter': 32, 'verse': 22, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ ثُمَّ أَعۡرَضَ عَنۡهَآۚ إِنَّا مِنَ ٱلۡمُجۡرِمِينَ مُنتَقِمُونَ\nРоббининг оятлари ила эслатилган, сўнгра улардан юз ўгирган одамдан ҳам золимроқ одам борми?! Албатта, Биз жиноятчилардан интиқом олгувчилармиз'}, {'chapter': 32, 'verse': 23, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَلَا تَكُن فِي مِرۡيَةٖ مِّن لِّقَآئِهِۦۖ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَـٰٓءِيلَ\nБатаҳқиқ, Биз Мусога китобни бердик. Бас, сен унинг рўбарў бўлишидан шубҳада бўлма. Ва биз уни Бани Исроилга ҳидоят қилиб бердик'}, {'chapter': 32, 'verse': 24, 'text': 'وَجَعَلۡنَا مِنۡهُمۡ أَئِمَّةٗ يَهۡدُونَ بِأَمۡرِنَا لَمَّا صَبَرُواْۖ وَكَانُواْ بِـَٔايَٰتِنَا يُوقِنُونَ\nВа сабр қилган пайтларида улардан Бизнинг амримиз ила ҳидоятга бошлайдиган пешволар чиқардик. Улар Бизнинг оятларимизга аниқ ишонардилар'}, {'chapter': 32, 'verse': 25, 'text': 'إِنَّ رَبَّكَ هُوَ يَفۡصِلُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nАлбатта, Роббинг Ўзи қиёмат куни улар орасида ихтилоф қилган нарсалари тўғрисида ажрим қилур'}, {'chapter': 32, 'verse': 26, 'text': 'أَوَلَمۡ يَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّنَ ٱلۡقُرُونِ يَمۡشُونَ فِي مَسَٰكِنِهِمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٍۚ أَفَلَا يَسۡمَعُونَ\nЎзларидан олдин ўтган, масканларида юриб турган қанчадан-қанча асрларни ҳалок қилганимиз уларни ҳидоятга бошламадими?! Албатта, бунда оят-белгилар бордир. Эшитмайдиларми'}, {'chapter': 32, 'verse': 27, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا نَسُوقُ ٱلۡمَآءَ إِلَى ٱلۡأَرۡضِ ٱلۡجُرُزِ فَنُخۡرِجُ بِهِۦ زَرۡعٗا تَأۡكُلُ مِنۡهُ أَنۡعَٰمُهُمۡ وَأَنفُسُهُمۡۚ أَفَلَا يُبۡصِرُونَ\nБизнинг сувни қуруқ ерга ҳайдаб, у билан чорвалари ва ўзлари ейдиган зироатни чиқаришимизни билмайдиларми?! Ахир, кўрмайдиларми'}, {'chapter': 32, 'verse': 28, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡفَتۡحُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсаларингиз, бу фатҳ қачон бўлур?!» дерлар'}, {'chapter': 32, 'verse': 29, 'text': 'قُلۡ يَوۡمَ ٱلۡفَتۡحِ لَا يَنفَعُ ٱلَّذِينَ كَفَرُوٓاْ إِيمَٰنُهُمۡ وَلَا هُمۡ يُنظَرُونَ\nСен: «Фатҳ куни куфр келтирганларга иймонлари манфаат бермас ва уларга муҳлат ҳам берилмас», деб айт'}, {'chapter': 32, 'verse': 30, 'text': 'فَأَعۡرِضۡ عَنۡهُمۡ وَٱنتَظِرۡ إِنَّهُم مُّنتَظِرُونَ\nБас, улардан юз ўгир ва мунтазир бўл. Албатта, улар ҳам мунтазирдирлар'}], '33': [{'chapter': 33, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ ٱتَّقِ ٱللَّهَ وَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَٱلۡمُنَٰفِقِينَۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nЭй Набий! Аллоҳга тақво қил ва кофиру мунофиқларга итоат этма! Албатта, Аллоҳ ўта олим ва ўта ҳикматли Зотдир'}, {'chapter': 33, 'verse': 2, 'text': 'وَٱتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ مِن رَّبِّكَۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nВа ўзингга Роббингдан ваҳий қилинаётган нарсага эргаш. Албатта, Аллоҳ нима амал қилаётганингиздан хабардордир'}, {'chapter': 33, 'verse': 3, 'text': 'وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا\nВа фақат Аллоҳга таваккал қил. Вакилликка Аллоҳ кофийдир'}, {'chapter': 33, 'verse': 4, 'text': 'مَّا جَعَلَ ٱللَّهُ لِرَجُلٖ مِّن قَلۡبَيۡنِ فِي جَوۡفِهِۦۚ وَمَا جَعَلَ أَزۡوَٰجَكُمُ ٱلَّـٰٓـِٔي تُظَٰهِرُونَ مِنۡهُنَّ أُمَّهَٰتِكُمۡۚ وَمَا جَعَلَ أَدۡعِيَآءَكُمۡ أَبۡنَآءَكُمۡۚ ذَٰلِكُمۡ قَوۡلُكُم بِأَفۡوَٰهِكُمۡۖ وَٱللَّهُ يَقُولُ ٱلۡحَقَّ وَهُوَ يَهۡدِي ٱلسَّبِيلَ\nАллоҳ бирон кишининг ичида иккита қалб қилган эмасдир. Ўзларингиз зиҳор қилаётган хотинларингизни сиз учун она ҳам қилган эмас ва асранди болаларингизни сизга фарзанд қилган эмас. Буларингиз оғзингиз билан айтган гапларингиздир. Аллоҳ эса, ҳақни айтур ва У тўғри йўлга ҳидоят қилур'}, {'chapter': 33, 'verse': 5, 'text': 'ٱدۡعُوهُمۡ لِأٓبَآئِهِمۡ هُوَ أَقۡسَطُ عِندَ ٱللَّهِۚ فَإِن لَّمۡ تَعۡلَمُوٓاْ ءَابَآءَهُمۡ فَإِخۡوَٰنُكُمۡ فِي ٱلدِّينِ وَمَوَٰلِيكُمۡۚ وَلَيۡسَ عَلَيۡكُمۡ جُنَاحٞ فِيمَآ أَخۡطَأۡتُم بِهِۦ وَلَٰكِن مَّا تَعَمَّدَتۡ قُلُوبُكُمۡۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمًا\nУларни ўз оталари номи билан чақиринглар. Ана шу, Аллоҳнинг наздида тўғридир. Агар оталарини билмасангиз, улар сизнинг дин қардошларингиз ва дўстларингиздир. Билмасдан қилган хато нарсангиз учун сизга гуноҳ йўқ. Аммо дилдан қасд қилган бўлсангиз, Аллоҳ ўта мағфиратли ва ўта раҳимли Зотдир'}, {'chapter': 33, 'verse': 6, 'text': 'ٱلنَّبِيُّ أَوۡلَىٰ بِٱلۡمُؤۡمِنِينَ مِنۡ أَنفُسِهِمۡۖ وَأَزۡوَٰجُهُۥٓ أُمَّهَٰتُهُمۡۗ وَأُوْلُواْ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ ٱللَّهِ مِنَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُهَٰجِرِينَ إِلَّآ أَن تَفۡعَلُوٓاْ إِلَىٰٓ أَوۡلِيَآئِكُم مَّعۡرُوفٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا\nНабий мўминлар учун ўзларидан кўра ҳақлидир. Унинг аёллари эса, уларнинг оналаридир. Аллоҳнинг Китобида қариндошлар бир-бирларига бошқа мўминлардан ва муҳожирлардан ҳақлидирлар. Фақат, дўстларингизга яхшилик қилмоғингиз мустаснодир. Бу, Китобда сатрлар ила битилмиш'}, {'chapter': 33, 'verse': 7, 'text': 'وَإِذۡ أَخَذۡنَا مِنَ ٱلنَّبِيِّـۧنَ مِيثَٰقَهُمۡ وَمِنكَ وَمِن نُّوحٖ وَإِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَى ٱبۡنِ مَرۡيَمَۖ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا\nНабийлардан аҳду паймонларини олганимизни эсла! Сендан, Нуҳдан, Иброҳимдан, Мусодан ва Ийсо ибн Марямдан ҳам. Биз улардан салмоқли аҳду паймон олдик'}, {'chapter': 33, 'verse': 8, 'text': 'لِّيَسۡـَٔلَ ٱلصَّـٰدِقِينَ عَن صِدۡقِهِمۡۚ وَأَعَدَّ لِلۡكَٰفِرِينَ عَذَابًا أَلِيمٗا\n(Буни Аллоҳ) содиқлардан садоқатлари ҳақида сўраш учун қилди. Ва кофирларга аламли азобни тайёрлаб қўйди'}, {'chapter': 33, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ جَآءَتۡكُمۡ جُنُودٞ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا وَجُنُودٗا لَّمۡ تَرَوۡهَاۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا\nЭй, иймон келтирганлар! Аллоҳнинг сизга берган неъматини эсланг. Ўшанда устингизга лашкарлар келган эди. Бас, Биз уларнинг устига шамолни ва сиз кўрмаган лашкарларни юбордик. Аллоҳ нима қилаётганингизни яхши кўриб тургувчи эди'}, {'chapter': 33, 'verse': 10, 'text': 'إِذۡ جَآءُوكُم مِّن فَوۡقِكُمۡ وَمِنۡ أَسۡفَلَ مِنكُمۡ وَإِذۡ زَاغَتِ ٱلۡأَبۡصَٰرُ وَبَلَغَتِ ٱلۡقُلُوبُ ٱلۡحَنَاجِرَ وَتَظُنُّونَ بِٱللَّهِ ٱلظُّنُونَا۠\nЎшанда улар сизнинг уст ва қуйи томонингиздан келган эдилар. Ўшанда кўзлар тиниб, жонлар ҳалқумга келган эди ва Аллоҳ ҳақида турли гумонлар қилардингиз'}, {'chapter': 33, 'verse': 11, 'text': 'هُنَالِكَ ٱبۡتُلِيَ ٱلۡمُؤۡمِنُونَ وَزُلۡزِلُواْ زِلۡزَالٗا شَدِيدٗا\nАна шунда мўминлар синовга дучор бўлдилар ва шиддатли ларзага тушдилар'}, {'chapter': 33, 'verse': 12, 'text': 'وَإِذۡ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ مَّا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥٓ إِلَّا غُرُورٗا\nЎшанда мунофиқлар ва қалбларида касаллик борлар: «Аллоҳ ва Унинг Расули бизга ёлғондан бошқани ваъда қилмаган экан», дердилар'}, {'chapter': 33, 'verse': 13, 'text': 'وَإِذۡ قَالَت طَّآئِفَةٞ مِّنۡهُمۡ يَـٰٓأَهۡلَ يَثۡرِبَ لَا مُقَامَ لَكُمۡ فَٱرۡجِعُواْۚ وَيَسۡتَـٔۡذِنُ فَرِيقٞ مِّنۡهُمُ ٱلنَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوۡرَةٞ وَمَا هِيَ بِعَوۡرَةٍۖ إِن يُرِيدُونَ إِلَّا فِرَارٗا\nЎшанда улардан бир тоифаси: «Эй, Ясриб аҳли, бу ерда сизга туриш имкони йўқ. Бас, қайтинглар», деди. Улардан бир гуруҳлари Набийдан изн сўраб: «Уйларимиз очиқ-сочиқ қолган», дерлар. Ҳолбуки, улар очиқ-сочиқ эмас. Улар қочишдан бошқани ирода қилмаслар'}, {'chapter': 33, 'verse': 14, 'text': 'وَلَوۡ دُخِلَتۡ عَلَيۡهِم مِّنۡ أَقۡطَارِهَا ثُمَّ سُئِلُواْ ٱلۡفِتۡنَةَ لَأٓتَوۡهَا وَمَا تَلَبَّثُواْ بِهَآ إِلَّا يَسِيرٗا\nАгар уларнинг устига у(Мадина)нинг ҳар тарафидан бостириб кирилса, сўнгра улардан фитна талаб қилинса, албатта, уни қиладилар. Ҳеч кечикмаслар. Магар озгинагина'}, {'chapter': 33, 'verse': 15, 'text': 'وَلَقَدۡ كَانُواْ عَٰهَدُواْ ٱللَّهَ مِن قَبۡلُ لَا يُوَلُّونَ ٱلۡأَدۡبَٰرَۚ وَكَانَ عَهۡدُ ٱللَّهِ مَسۡـُٔولٗا\nҲолбуки, улар бундан олдин ортга қараб қочмасликка Аллоҳга аҳд берган эдилар. Аллоҳга аҳд эса, масъулият бўлгандир'}, {'chapter': 33, 'verse': 16, 'text': 'قُل لَّن يَنفَعَكُمُ ٱلۡفِرَارُ إِن فَرَرۡتُم مِّنَ ٱلۡمَوۡتِ أَوِ ٱلۡقَتۡلِ وَإِذٗا لَّا تُمَتَّعُونَ إِلَّا قَلِيلٗا\nСен: «Қочиш сизга ҳеч қандай фойда бермас. Демак, агар ўлимдан ёки қатл бўлишдан қочсангиз ҳам, жуда оз ҳузурланасиз, холос», деб айт'}, {'chapter': 33, 'verse': 17, 'text': 'قُلۡ مَن ذَا ٱلَّذِي يَعۡصِمُكُم مِّنَ ٱللَّهِ إِنۡ أَرَادَ بِكُمۡ سُوٓءًا أَوۡ أَرَادَ بِكُمۡ رَحۡمَةٗۚ وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا\nСен: «Агар сизга Аллоҳ ёмонликни ирода қилса ёки сизга раҳматни ирода қилса, сизларни Ундан тўсадиган ким бор?!» деб айт. Улар ўзларига Аллоҳдан ўзга дўст ва ёрдам бергувчи топа олмаслар'}, {'chapter': 33, 'verse': 18, 'text': '۞قَدۡ يَعۡلَمُ ٱللَّهُ ٱلۡمُعَوِّقِينَ مِنكُمۡ وَٱلۡقَآئِلِينَ لِإِخۡوَٰنِهِمۡ هَلُمَّ إِلَيۡنَاۖ وَلَا يَأۡتُونَ ٱلۡبَأۡسَ إِلَّا قَلِيلًا\nБатаҳқиқ, Аллоҳ ичингиздаги (жидду жаҳдга) тўсиқлик қилгувчиларни ҳам ва ўз биродарларига: «Биз томон келинг», деювчиларни ҳам билур. Улар жангга келмаслар, илло, озгина келурлар'}, {'chapter': 33, 'verse': 19, 'text': 'أَشِحَّةً عَلَيۡكُمۡۖ فَإِذَا جَآءَ ٱلۡخَوۡفُ رَأَيۡتَهُمۡ يَنظُرُونَ إِلَيۡكَ تَدُورُ أَعۡيُنُهُمۡ كَٱلَّذِي يُغۡشَىٰ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَإِذَا ذَهَبَ ٱلۡخَوۡفُ سَلَقُوكُم بِأَلۡسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى ٱلۡخَيۡرِۚ أُوْلَـٰٓئِكَ لَمۡ يُؤۡمِنُواْ فَأَحۡبَطَ ٱللَّهُ أَعۡمَٰلَهُمۡۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا\nСизларга нисбатан бахиллик қилган ҳолларида (келурлар). Бас, хавф келган чоғда уларнинг сенга ўлим талвасасидаги кишига ўхшаб, кўзлари айланиб назар солишларини кўрурсан. Бас, хавф кетганда эса, яхшиликка очкўзлик қилиб, сизларга ўткир тиллар ила озор берурлар. Ана ўшалар, иймон келтирмаганлардир. Бас, Аллоҳ амалларини беҳуда кетказди. Бу эса, Аллоҳ учун осон эди'}, {'chapter': 33, 'verse': 20, 'text': 'يَحۡسَبُونَ ٱلۡأَحۡزَابَ لَمۡ يَذۡهَبُواْۖ وَإِن يَأۡتِ ٱلۡأَحۡزَابُ يَوَدُّواْ لَوۡ أَنَّهُم بَادُونَ فِي ٱلۡأَعۡرَابِ يَسۡـَٔلُونَ عَنۡ أَنۢبَآئِكُمۡۖ وَلَوۡ كَانُواْ فِيكُم مَّا قَٰتَلُوٓاْ إِلَّا قَلِيلٗا\nУлар аҳзобларни кетмаган деб ҳисобларлар. Агар аҳзоблар яна келсалар, улар аъробийлар ичида, чўлда бўлиб, сизнинг хабарингизни сўраб туришни орзу қилурлар. Агар сизнинг ичингизда бўлганларида ҳам, камдан-кам жанг қилган бўлур эдилар'}, {'chapter': 33, 'verse': 21, 'text': 'لَّقَدۡ كَانَ لَكُمۡ فِي رَسُولِ ٱللَّهِ أُسۡوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرۡجُواْ ٱللَّهَ وَٱلۡيَوۡمَ ٱلۡأٓخِرَ وَذَكَرَ ٱللَّهَ كَثِيرٗا\nБатаҳқиқ, сизлар учун, Аллоҳдан ва охират кунидан умидвор бўлганлар учун ва Аллоҳни кўп зикр қилганлар учун Расулуллоҳда гўзал ўрнак бор эди'}, {'chapter': 33, 'verse': 22, 'text': 'وَلَمَّا رَءَا ٱلۡمُؤۡمِنُونَ ٱلۡأَحۡزَابَ قَالُواْ هَٰذَا مَا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥ وَصَدَقَ ٱللَّهُ وَرَسُولُهُۥۚ وَمَا زَادَهُمۡ إِلَّآ إِيمَٰنٗا وَتَسۡلِيمٗا\nМўминлар аҳзобларни кўрганларида: «Бу бизга Аллоҳ ва Унинг Расули ваъда қилган нарсадир. Аллоҳ ва Унинг Расули рост сўзларлар», дедилар. Бу уларга иймон ва таслимдан бошқа нарсани зиёда этмади'}, {'chapter': 33, 'verse': 23, 'text': 'مِّنَ ٱلۡمُؤۡمِنِينَ رِجَالٞ صَدَقُواْ مَا عَٰهَدُواْ ٱللَّهَ عَلَيۡهِۖ فَمِنۡهُم مَّن قَضَىٰ نَحۡبَهُۥ وَمِنۡهُم مَّن يَنتَظِرُۖ وَمَا بَدَّلُواْ تَبۡدِيلٗا\nМўминлардан Аллоҳга берган ваъдаларига содиқ қолган кишилар бордир. Бас, улардан баъзилари ажалини топди. Баъзилари эса, интизор бўлиб турибдир. Улар бирор нарсани алмаштирмадилар'}, {'chapter': 33, 'verse': 24, 'text': 'لِّيَجۡزِيَ ٱللَّهُ ٱلصَّـٰدِقِينَ بِصِدۡقِهِمۡ وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ إِن شَآءَ أَوۡ يَتُوبَ عَلَيۡهِمۡۚ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nАллоҳ содиқларни садоқатлари сабабидан мукофотлаши учун ва мунофиқларни агар хоҳласа, азоблаши ёки тавбасини қабул қилиши учундир. Албатта, Аллоҳ ўта мағфиратли ва ўта раҳимлидир'}, {'chapter': 33, 'verse': 25, 'text': 'وَرَدَّ ٱللَّهُ ٱلَّذِينَ كَفَرُواْ بِغَيۡظِهِمۡ لَمۡ يَنَالُواْ خَيۡرٗاۚ وَكَفَى ٱللَّهُ ٱلۡمُؤۡمِنِينَ ٱلۡقِتَالَۚ وَكَانَ ٱللَّهُ قَوِيًّا عَزِيزٗا\nВа Аллоҳ куфр келтирганларни ғазаблари ила ортларига қайтарди. Улар ҳеч бир яхшиликка эриша олмадилар. Мўминлар учун урушда Аллоҳнинг Ўзи кифоя қилди. Ва Аллоҳ кучли ва азийз Зотдир'}, {'chapter': 33, 'verse': 26, 'text': 'وَأَنزَلَ ٱلَّذِينَ ظَٰهَرُوهُم مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ مِن صَيَاصِيهِمۡ وَقَذَفَ فِي قُلُوبِهِمُ ٱلرُّعۡبَ فَرِيقٗا تَقۡتُلُونَ وَتَأۡسِرُونَ فَرِيقٗا\nВа У зот уларга ёрдам берган аҳли китоблардан бўлганларни қўрғонларидан туширди ва қалбларига қўрқинч солди. Бир гуруҳини ўлдирурсизлар ва бир гуруҳини асир олурсизлар'}, {'chapter': 33, 'verse': 27, 'text': 'وَأَوۡرَثَكُمۡ أَرۡضَهُمۡ وَدِيَٰرَهُمۡ وَأَمۡوَٰلَهُمۡ وَأَرۡضٗا لَّمۡ تَطَـُٔوهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا\nВа сизларга уларнинг ерини, ҳовли-жойларини, молу мулкларини ва қадамингиз етмаган ерни мерос қилиб берди. Аллоҳ ҳар бир нарсага қодир Зотдир'}, {'chapter': 33, 'verse': 28, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّأَزۡوَٰجِكَ إِن كُنتُنَّ تُرِدۡنَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا وَزِينَتَهَا فَتَعَالَيۡنَ أُمَتِّعۡكُنَّ وَأُسَرِّحۡكُنَّ سَرَاحٗا جَمِيلٗا\nЭй Набий, жуфти ҳалолларингга: «Агар бу дунё ҳаётини ва унинг зийнатини ирода қилсангиз, келинглар, сизларни баҳраманд қилай ва чиройли бўшатиш ила бўшатиб қўяй'}, {'chapter': 33, 'verse': 29, 'text': 'وَإِن كُنتُنَّ تُرِدۡنَ ٱللَّهَ وَرَسُولَهُۥ وَٱلدَّارَ ٱلۡأٓخِرَةَ فَإِنَّ ٱللَّهَ أَعَدَّ لِلۡمُحۡسِنَٰتِ مِنكُنَّ أَجۡرًا عَظِيمٗا\nАгар Аллоҳни ва Унинг Расулини ва охират диёрини ирода қиладиган бўлсангиз, бас, албатта, Аллоҳ сизлардан гўзал амал қилгувчиларга улуғ ажрни тайёрлаб қўйган», деб айт'}, {'chapter': 33, 'verse': 30, 'text': 'يَٰنِسَآءَ ٱلنَّبِيِّ مَن يَأۡتِ مِنكُنَّ بِفَٰحِشَةٖ مُّبَيِّنَةٖ يُضَٰعَفۡ لَهَا ٱلۡعَذَابُ ضِعۡفَيۡنِۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا\nЭй, Набийнинг аёллари! Сиздан ким очиқ гуноҳ қилса, унга бериладиган азоб икки марта кўпайтирилур. Бу эса, Аллоҳ учун осон бўлган ишдир'}, {'chapter': 33, 'verse': 31, 'text': '۞وَمَن يَقۡنُتۡ مِنكُنَّ لِلَّهِ وَرَسُولِهِۦ وَتَعۡمَلۡ صَٰلِحٗا نُّؤۡتِهَآ أَجۡرَهَا مَرَّتَيۡنِ وَأَعۡتَدۡنَا لَهَا رِزۡقٗا كَرِيمٗا\nСизлардан ким Аллоҳ ва Унинг Расулига мудом итоатда бўлса ва яхши амал қилса, унинг ажрини икки марта берамиз ва унинг учун гўзал ризқни тайёрлаганмиз'}, {'chapter': 33, 'verse': 32, 'text': 'يَٰنِسَآءَ ٱلنَّبِيِّ لَسۡتُنَّ كَأَحَدٖ مِّنَ ٱلنِّسَآءِ إِنِ ٱتَّقَيۡتُنَّۚ فَلَا تَخۡضَعۡنَ بِٱلۡقَوۡلِ فَيَطۡمَعَ ٱلَّذِي فِي قَلۡبِهِۦ مَرَضٞ وَقُلۡنَ قَوۡلٗا مَّعۡرُوفٗا\nЭй, Набийнинг аёллари! Сизлар аёллардан бирортаси каби эмассиз. Агар тақво қилсангиз, майин сўз қилманг, яна, қалбида касали бор бўлган тамаъ қилиб юрмасин. Гапирганда яхши гапни гапиринглар'}, {'chapter': 33, 'verse': 33, 'text': 'وَقَرۡنَ فِي بُيُوتِكُنَّ وَلَا تَبَرَّجۡنَ تَبَرُّجَ ٱلۡجَٰهِلِيَّةِ ٱلۡأُولَىٰۖ وَأَقِمۡنَ ٱلصَّلَوٰةَ وَءَاتِينَ ٱلزَّكَوٰةَ وَأَطِعۡنَ ٱللَّهَ وَرَسُولَهُۥٓۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُذۡهِبَ عَنكُمُ ٱلرِّجۡسَ أَهۡلَ ٱلۡبَيۡتِ وَيُطَهِّرَكُمۡ تَطۡهِيرٗا\nВиқор билан уйингизда ўтиринг. Илгариги жоҳилиятнинг очиқ-сочиқлиги каби очиқ-сочиқ юрманг. Намозни тўкис адо этинг, закот беринг, Аллоҳга ва Унинг Расулига итоат қилинг. Албатта, Аллоҳ сиздан кирни кетказиб, бутунлай поклашни хоҳлайди, эй, аҳли байт'}, {'chapter': 33, 'verse': 34, 'text': 'وَٱذۡكُرۡنَ مَا يُتۡلَىٰ فِي بُيُوتِكُنَّ مِنۡ ءَايَٰتِ ٱللَّهِ وَٱلۡحِكۡمَةِۚ إِنَّ ٱللَّهَ كَانَ لَطِيفًا خَبِيرًا\nВа уйларингизда тиловат қилинаётган Аллоҳнинг оятларини ва ҳикматни эсланг. Албатта, Аллоҳ ўта латийф ва ўта хабардор бўлган Зотдир'}, {'chapter': 33, 'verse': 35, 'text': 'إِنَّ ٱلۡمُسۡلِمِينَ وَٱلۡمُسۡلِمَٰتِ وَٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ وَٱلۡقَٰنِتِينَ وَٱلۡقَٰنِتَٰتِ وَٱلصَّـٰدِقِينَ وَٱلصَّـٰدِقَٰتِ وَٱلصَّـٰبِرِينَ وَٱلصَّـٰبِرَٰتِ وَٱلۡخَٰشِعِينَ وَٱلۡخَٰشِعَٰتِ وَٱلۡمُتَصَدِّقِينَ وَٱلۡمُتَصَدِّقَٰتِ وَٱلصَّـٰٓئِمِينَ وَٱلصَّـٰٓئِمَٰتِ وَٱلۡحَٰفِظِينَ فُرُوجَهُمۡ وَٱلۡحَٰفِظَٰتِ وَٱلذَّـٰكِرِينَ ٱللَّهَ كَثِيرٗا وَٱلذَّـٰكِرَٰتِ أَعَدَّ ٱللَّهُ لَهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمٗا\nАлбатта, муслимлар ва муслималар, мўминлар ва мўминалар, давомли итоаткор эркаклар ва давомли итоаткор аёллар, садоқатли эркаклар ва садоқатли аёллар, сабрли эркаклар ва сабрли аёллар, хушуъли эркаклар ва хушуъли аёллар, садақа қилгувчи эркаклар ва садақа қилгувчи аёллар, рўза тутгувчи эркаклар ва рўза тутгувчи аёллар, фаржларини сақловчи эркаклар ва (фаржларини) сақловчи аёллар, Аллоҳни кўп зикр қилгувчи эркаклар ва (Аллоҳни кўп) зикр қилгувчи аёллар — ўшаларга Аллоҳ мағфиратни ва улуғ ажрни тайёрлаб қўйгандир'}, {'chapter': 33, 'verse': 36, 'text': 'وَمَا كَانَ لِمُؤۡمِنٖ وَلَا مُؤۡمِنَةٍ إِذَا قَضَى ٱللَّهُ وَرَسُولُهُۥٓ أَمۡرًا أَن يَكُونَ لَهُمُ ٱلۡخِيَرَةُ مِنۡ أَمۡرِهِمۡۗ وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ ضَلَّ ضَلَٰلٗا مُّبِينٗا\nҲеч бир мўмин эркак ва ҳеч бир мўмина аёл учун Аллоҳ ва Унинг Расули бир ишга ҳукм қилганида, ўз ишларини ўзларича ихтиёр қилмоқ йўқ. Ким Аллоҳга ва Унинг Расулига осий бўлса, бас, батаҳқиқ, очиқ адашиш-ла адашибди'}, {'chapter': 33, 'verse': 37, 'text': 'وَإِذۡ تَقُولُ لِلَّذِيٓ أَنۡعَمَ ٱللَّهُ عَلَيۡهِ وَأَنۡعَمۡتَ عَلَيۡهِ أَمۡسِكۡ عَلَيۡكَ زَوۡجَكَ وَٱتَّقِ ٱللَّهَ وَتُخۡفِي فِي نَفۡسِكَ مَا ٱللَّهُ مُبۡدِيهِ وَتَخۡشَى ٱلنَّاسَ وَٱللَّهُ أَحَقُّ أَن تَخۡشَىٰهُۖ فَلَمَّا قَضَىٰ زَيۡدٞ مِّنۡهَا وَطَرٗا زَوَّجۡنَٰكَهَا لِكَيۡ لَا يَكُونَ عَلَى ٱلۡمُؤۡمِنِينَ حَرَجٞ فِيٓ أَزۡوَٰجِ أَدۡعِيَآئِهِمۡ إِذَا قَضَوۡاْ مِنۡهُنَّ وَطَرٗاۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولٗا\nЭсла, Аллоҳ неъмат берган ва сен ҳам неъмат берган шахсга: «Жуфтингни ўзинг ила тутиб тур ва Аллоҳга тақво қил», дер эдинг. Ва ичингда Аллоҳ ошкор қилгувчи бўлган нарсани махфий тутардинг ва одамлардан қўрқардинг, ҳолбуки, Аллоҳдан қўрқмоғинг ҳақлироқ эди. Қачонки Зайд ундан ўз ҳожатини адо этгач, Биз сени унга уйлантирдик. Токи, мўминларга ўз асранди болалари хотинларида, улардан ҳожатларини адо қилишгач, танглик бўлмасин деб. Ва Аллоҳнинг амри амал қилиниши лозим бўлгандир'}, {'chapter': 33, 'verse': 38, 'text': 'مَّا كَانَ عَلَى ٱلنَّبِيِّ مِنۡ حَرَجٖ فِيمَا فَرَضَ ٱللَّهُ لَهُۥۖ سُنَّةَ ٱللَّهِ فِي ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلُۚ وَكَانَ أَمۡرُ ٱللَّهِ قَدَرٗا مَّقۡدُورًا\nНабийга Аллоҳ унга фарз қилган нарсада бирон танглик бўлмас. Бу — олдин ўтганлар ҳақида ҳам Аллоҳ тутган йўлдир. Аллоҳнинг амри белгиланган қадар бўлди'}, {'chapter': 33, 'verse': 39, 'text': 'ٱلَّذِينَ يُبَلِّغُونَ رِسَٰلَٰتِ ٱللَّهِ وَيَخۡشَوۡنَهُۥ وَلَا يَخۡشَوۡنَ أَحَدًا إِلَّا ٱللَّهَۗ وَكَفَىٰ بِٱللَّهِ حَسِيبٗا\nУлар Аллоҳнинг элчилигини етказадиган, Ундангина қўрқадиган, Аллоҳдан бошқа ҳеч кимдан қўрқмайдиган зотлардир. Аллоҳнинг Ўзи ҳисоб қилгувчиликка кифоядир'}, {'chapter': 33, 'verse': 40, 'text': 'مَّا كَانَ مُحَمَّدٌ أَبَآ أَحَدٖ مِّن رِّجَالِكُمۡ وَلَٰكِن رَّسُولَ ٱللَّهِ وَخَاتَمَ ٱلنَّبِيِّـۧنَۗ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا\nМуҳаммад сизлардан бирон эркакнинг отаси бўлган эмас. Лекин Аллоҳнинг Расули ва Пайғамбарларнинг сўнггисидир. Аллоҳ ҳар бир нарсани ўта билгувчидир'}, {'chapter': 33, 'verse': 41, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ ٱللَّهَ ذِكۡرٗا كَثِيرٗا\nЭй, иймон келтирганлар! Аллоҳни кўп зикр қилинглар'}, {'chapter': 33, 'verse': 42, 'text': 'وَسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا\nВа эртаю кеч Уни поклаб ёд этинглар'}, {'chapter': 33, 'verse': 43, 'text': 'هُوَ ٱلَّذِي يُصَلِّي عَلَيۡكُمۡ وَمَلَـٰٓئِكَتُهُۥ لِيُخۡرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَكَانَ بِٱلۡمُؤۡمِنِينَ رَحِيمٗا\nУ сизларни зулматлардан нурга чиқариш учун Ўзи ҳам, фаришталари ҳам саловот айтадир. У мўминларга ўта меҳрибон бўлган Зотдир'}, {'chapter': 33, 'verse': 44, 'text': 'تَحِيَّتُهُمۡ يَوۡمَ يَلۡقَوۡنَهُۥ سَلَٰمٞۚ وَأَعَدَّ لَهُمۡ أَجۡرٗا كَرِيمٗا\nУ зотга йўлиқадиган кундаги сўрашишлари саломдир. Ва уларга гўзал ажрни тайёрлаб қўйгандир'}, {'chapter': 33, 'verse': 45, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا\nЭй Набий, албатта, Биз сени гувоҳлик бергувчи, хушхабарчи ва огоҳлантиргувчи'}, {'chapter': 33, 'verse': 46, 'text': 'وَدَاعِيًا إِلَى ٱللَّهِ بِإِذۡنِهِۦ وَسِرَاجٗا مُّنِيرٗا\nВа Ўз изни ила Аллоҳга даъват қилгувчи ҳамда нурли чироқ қилиб юбордик'}, {'chapter': 33, 'verse': 47, 'text': 'وَبَشِّرِ ٱلۡمُؤۡمِنِينَ بِأَنَّ لَهُم مِّنَ ٱللَّهِ فَضۡلٗا كَبِيرٗا\nМўминларга уларга албатта Аллоҳдан улуғ фазлу марҳамат бўлиши ҳақида хушхабар бер'}, {'chapter': 33, 'verse': 48, 'text': 'وَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَٱلۡمُنَٰفِقِينَ وَدَعۡ أَذَىٰهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا\nКофир ва мунофиқларга итоат этма. Уларнинг озорларига парво қилма. Аллоҳга таваккал қил. Вакил бўлишга Аллоҳнинг Ўзи кифоядир'}, {'chapter': 33, 'verse': 49, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نَكَحۡتُمُ ٱلۡمُؤۡمِنَٰتِ ثُمَّ طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمۡ عَلَيۡهِنَّ مِنۡ عِدَّةٖ تَعۡتَدُّونَهَاۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحٗا جَمِيلٗا\nЭй, иймон келтирганлар! Қачонки мўминаларни никоҳлаб олсангиз, сўнгра уларни қўл теккизмай туриб, талоқ қилсангиз, улар зиммасида сиз учун идда ўтириш бўлмас. Бас, сиз уларни баҳраманд қилинг ва гўзал бўшатиш ила бўшатиб юборинг'}, {'chapter': 33, 'verse': 50, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِنَّآ أَحۡلَلۡنَا لَكَ أَزۡوَٰجَكَ ٱلَّـٰتِيٓ ءَاتَيۡتَ أُجُورَهُنَّ وَمَا مَلَكَتۡ يَمِينُكَ مِمَّآ أَفَآءَ ٱللَّهُ عَلَيۡكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّـٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَٰلَٰتِكَ ٱلَّـٰتِي هَاجَرۡنَ مَعَكَ وَٱمۡرَأَةٗ مُّؤۡمِنَةً إِن وَهَبَتۡ نَفۡسَهَا لِلنَّبِيِّ إِنۡ أَرَادَ ٱلنَّبِيُّ أَن يَسۡتَنكِحَهَا خَالِصَةٗ لَّكَ مِن دُونِ ٱلۡمُؤۡمِنِينَۗ قَدۡ عَلِمۡنَا مَا فَرَضۡنَا عَلَيۡهِمۡ فِيٓ أَزۡوَٰجِهِمۡ وَمَا مَلَكَتۡ أَيۡمَٰنُهُمۡ لِكَيۡلَا يَكُونَ عَلَيۡكَ حَرَجٞۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nЭй Набий, Биз сенга маҳрларини берган жуфтларингни, Аллоҳ сенга ўлжа қилиб берган қўл остингдаги(чўри)ларни, амакингнинг қизлари, аммаларингнинг қизлари, тоғангнинг қизлари, холаларингнинг қизларидан сен билан ҳижрат қилганларини ҳалол қилдик. Ва агар бир мўмина аёл ўз нафсини Набийга ҳадя этса, Набий уни никоҳлаб олишни ирода қилса, сен учун махсус ҳалол қилдик. Мўминларга эмас. Уларга жуфтлари ва қўллари остидаги мулк(чўри)лари ҳақида нимани фарз қилганимизни билурмиз. Буни сенга танглик бўлмаслиги учун қилдик. Аллоҳ ўта мағфиратли ва ўта раҳимли Зотдир'}, {'chapter': 33, 'verse': 51, 'text': '۞تُرۡجِي مَن تَشَآءُ مِنۡهُنَّ وَتُـٔۡوِيٓ إِلَيۡكَ مَن تَشَآءُۖ وَمَنِ ٱبۡتَغَيۡتَ مِمَّنۡ عَزَلۡتَ فَلَا جُنَاحَ عَلَيۡكَۚ ذَٰلِكَ أَدۡنَىٰٓ أَن تَقَرَّ أَعۡيُنُهُنَّ وَلَا يَحۡزَنَّ وَيَرۡضَيۡنَ بِمَآ ءَاتَيۡتَهُنَّ كُلُّهُنَّۚ وَٱللَّهُ يَعۡلَمُ مَا فِي قُلُوبِكُمۡۚ وَكَانَ ٱللَّهُ عَلِيمًا حَلِيمٗا\nУлардан хоҳлаганингни нари қўйиб, хоҳлаганингни ўзингга олурсан. Ўзинг четлатганлардан бирортасини истасанг, сенга гуноҳ бўлмас. Шундай бўлиши уларнинг кўзлари қувончга тўлиши, хафа бўлмасликлари ва ҳаммаларининг сен уларга берган нарсадан рози бўлишларига яқинроқдир. Аллоҳ қалбларингиздаги нарсани биладир ва Аллоҳ ўта билгувчи ва ўта ҳалийм Зотдир'}, {'chapter': 33, 'verse': 52, 'text': 'لَّا يَحِلُّ لَكَ ٱلنِّسَآءُ مِنۢ بَعۡدُ وَلَآ أَن تَبَدَّلَ بِهِنَّ مِنۡ أَزۡوَٰجٖ وَلَوۡ أَعۡجَبَكَ حُسۡنُهُنَّ إِلَّا مَا مَلَكَتۡ يَمِينُكَۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ رَّقِيبٗا\nСенга бундан бошқа аёллар ҳалол бўлмас ва уларга бошқа завжаларни алмаштиришинг ҳам бўлмас. Агар ўшаларнинг ҳусни сени ажаблантирса ҳам. Магар қўлингда мулк бўлган (чўри) бўлса мумкин. Аллоҳ ҳар бир нарсани кузатиб тургувчи Зотдир'}, {'chapter': 33, 'verse': 53, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتَ ٱلنَّبِيِّ إِلَّآ أَن يُؤۡذَنَ لَكُمۡ إِلَىٰ طَعَامٍ غَيۡرَ نَٰظِرِينَ إِنَىٰهُ وَلَٰكِنۡ إِذَا دُعِيتُمۡ فَٱدۡخُلُواْ فَإِذَا طَعِمۡتُمۡ فَٱنتَشِرُواْ وَلَا مُسۡتَـٔۡنِسِينَ لِحَدِيثٍۚ إِنَّ ذَٰلِكُمۡ كَانَ يُؤۡذِي ٱلنَّبِيَّ فَيَسۡتَحۡيِۦ مِنكُمۡۖ وَٱللَّهُ لَا يَسۡتَحۡيِۦ مِنَ ٱلۡحَقِّۚ وَإِذَا سَأَلۡتُمُوهُنَّ مَتَٰعٗا فَسۡـَٔلُوهُنَّ مِن وَرَآءِ حِجَابٖۚ ذَٰلِكُمۡ أَطۡهَرُ لِقُلُوبِكُمۡ وَقُلُوبِهِنَّۚ وَمَا كَانَ لَكُمۡ أَن تُؤۡذُواْ رَسُولَ ٱللَّهِ وَلَآ أَن تَنكِحُوٓاْ أَزۡوَٰجَهُۥ مِنۢ بَعۡدِهِۦٓ أَبَدًاۚ إِنَّ ذَٰلِكُمۡ كَانَ عِندَ ٱللَّهِ عَظِيمًا\nЭй, иймон келтирганлар! Набийнинг уйларига кирманг. Магар, таомга изн берилганида, унинг пишишига мунтазир бўлмайдиган бўлиб (киринг). Лекин чақирилсангиз, киринг-да, таомни еб бўлишингиз билан тарқалиб кетинг, гапга берилиб, (қолиб) кетманг. Албатта, бундай қилишингиз Набийга озор берур. У эса, сизлардан ҳаё қиларди. Аллоҳ ҳақ(ни айтиш)дан ҳаё қилмайдир. Қачонки улардан бирор нарса сўрасангиз, парда ортидан сўранг. Шундай қилмоғингиз ўз қалбларингиз учун ҳам, уларнинг қалблари учун ҳам покроқдир. Сиз учун Расулуллоҳга озор бериш ва ундан кейин унинг жуфтларини никоҳингизга олишингиз ҳеч қачон мумкин эмас. Албатта, бундай қилмоғингиз Аллоҳнинг наздида катта(гуноҳ)дир'}, {'chapter': 33, 'verse': 54, 'text': 'إِن تُبۡدُواْ شَيۡـًٔا أَوۡ تُخۡفُوهُ فَإِنَّ ٱللَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا\nГар бир нарсани ошкор қилсангиз ҳам, махфий қилсангиз ҳам, албатта, Аллоҳ барча нарсани ўта билгувчи Зотдир'}, {'chapter': 33, 'verse': 55, 'text': 'لَّا جُنَاحَ عَلَيۡهِنَّ فِيٓ ءَابَآئِهِنَّ وَلَآ أَبۡنَآئِهِنَّ وَلَآ إِخۡوَٰنِهِنَّ وَلَآ أَبۡنَآءِ إِخۡوَٰنِهِنَّ وَلَآ أَبۡنَآءِ أَخَوَٰتِهِنَّ وَلَا نِسَآئِهِنَّ وَلَا مَا مَلَكَتۡ أَيۡمَٰنُهُنَّۗ وَٱتَّقِينَ ٱللَّهَۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا\nУларга ўз оталари, ўғиллари, ака-укалари, ака-укаларининг ўғиллари, опа-сингилларнинг ўғиллари, ўз аёллари ва қўллари остида мулк бўлган(қул)лари ҳақида гуноҳ йўқдир. (Эй, Пайғамбар аёллари,) Аллоҳга тақво қилинглар. Албатта, Аллоҳ ҳар бир нарсага шоҳид Зотдир'}, {'chapter': 33, 'verse': 56, 'text': 'إِنَّ ٱللَّهَ وَمَلَـٰٓئِكَتَهُۥ يُصَلُّونَ عَلَى ٱلنَّبِيِّۚ يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ صَلُّواْ عَلَيۡهِ وَسَلِّمُواْ تَسۡلِيمًا\nАлбатта, Аллоҳ ва Унинг фаришталари Набийга саловот айтурлар. Эй, иймон келтирганлар! Сиз ҳам унга саловот айтинг ва салом юборинг'}, {'chapter': 33, 'verse': 57, 'text': 'إِنَّ ٱلَّذِينَ يُؤۡذُونَ ٱللَّهَ وَرَسُولَهُۥ لَعَنَهُمُ ٱللَّهُ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَأَعَدَّ لَهُمۡ عَذَابٗا مُّهِينٗا\nАлбатта, Аллоҳга ва Унинг Расулига озор берадиганларни Аллоҳ бу дунё ва охиратда лаънатлади ҳамда уларга хорловчи азобни тайёрлаб қўйди'}, {'chapter': 33, 'verse': 58, 'text': 'وَٱلَّذِينَ يُؤۡذُونَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ بِغَيۡرِ مَا ٱكۡتَسَبُواْ فَقَدِ ٱحۡتَمَلُواْ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا\nМўмин ва мўминаларга, улар бирор нарса қилмасалар ҳам, озор берадиганлар, батаҳқиқ, ўз устларига очиқ бўҳтон ва гуноҳни олибдилар'}, {'chapter': 33, 'verse': 59, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّأَزۡوَٰجِكَ وَبَنَاتِكَ وَنِسَآءِ ٱلۡمُؤۡمِنِينَ يُدۡنِينَ عَلَيۡهِنَّ مِن جَلَٰبِيبِهِنَّۚ ذَٰلِكَ أَدۡنَىٰٓ أَن يُعۡرَفۡنَ فَلَا يُؤۡذَيۡنَۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nЭй Набий, жуфтларингга, қизларингга ва мўминларнинг аёлларига айт: жилбобларини ўзларига яқин қилсинлар. Ана ўша уларнинг танилмоқлари ва озорга учрамасликлари учун яқинроқдир. Аллоҳ ўта мағфиратли ва раҳимли Зотдир'}, {'chapter': 33, 'verse': 60, 'text': '۞لَّئِن لَّمۡ يَنتَهِ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡمُرۡجِفُونَ فِي ٱلۡمَدِينَةِ لَنُغۡرِيَنَّكَ بِهِمۡ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَآ إِلَّا قَلِيلٗا\nҚасамки, агар мунофиқлар, қалбларида марази борлар ва Мадинада миш-миш тарқатувчилар тўхтамасалар, сени уларга қарши қўзғатурмиз. Сўнгра улар у(Мадина)да сенга озгинадан бошқа қўшни бўла олмаслар'}, {'chapter': 33, 'verse': 61, 'text': 'مَّلۡعُونِينَۖ أَيۡنَمَا ثُقِفُوٓاْ أُخِذُواْ وَقُتِّلُواْ تَقۡتِيلٗا\nЛаънатланган ҳолларида, қаерда топилсалар, тутилурлар ва ўлдириб ташланурлар'}, {'chapter': 33, 'verse': 62, 'text': 'سُنَّةَ ٱللَّهِ فِي ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا\nИлгари ўтганлар ҳақида Аллоҳ тутган йўл ҳам шудир. Аллоҳнинг йўлига ҳеч бадал топа олмассан'}, {'chapter': 33, 'verse': 63, 'text': 'يَسۡـَٔلُكَ ٱلنَّاسُ عَنِ ٱلسَّاعَةِۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِۚ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ تَكُونُ قَرِيبًا\nОдамлар сендан соат (қиёмат) ҳақида сўрарлар. Сен: «Унинг илми фақат Аллоҳнинг ҳузуридадир», деб айт. Недан билурсан, эҳтимол, ўша соат яқиндир'}, {'chapter': 33, 'verse': 64, 'text': 'إِنَّ ٱللَّهَ لَعَنَ ٱلۡكَٰفِرِينَ وَأَعَدَّ لَهُمۡ سَعِيرًا\nАлбатта, Аллоҳ кофирларни лаънатлади ва уларга дўзахни тайёрлаб қўйди'}, {'chapter': 33, 'verse': 65, 'text': 'خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا\nУлар унда абадий бардавом қолурлар. На бир дўст ва на бир ёрдамчи топа олмаслар'}, {'chapter': 33, 'verse': 66, 'text': 'يَوۡمَ تُقَلَّبُ وُجُوهُهُمۡ فِي ٱلنَّارِ يَقُولُونَ يَٰلَيۡتَنَآ أَطَعۡنَا ٱللَّهَ وَأَطَعۡنَا ٱلرَّسُولَا۠\nЮзлари олов ичида айлантирилаётган кунда улар: «Эҳ, кошки, Аллоҳга итоат қилсак эди, Расулига итоат қилсак эди», дерлар'}, {'chapter': 33, 'verse': 67, 'text': 'وَقَالُواْ رَبَّنَآ إِنَّآ أَطَعۡنَا سَادَتَنَا وَكُبَرَآءَنَا فَأَضَلُّونَا ٱلسَّبِيلَا۠\nВа: «Эй Роббимиз, албатта, биз бошлиқларимизга ва катталаримизга итоат қилдик. Бас, улар бизни йўлдан оздирдилар'}, {'chapter': 33, 'verse': 68, 'text': 'رَبَّنَآ ءَاتِهِمۡ ضِعۡفَيۡنِ مِنَ ٱلۡعَذَابِ وَٱلۡعَنۡهُمۡ لَعۡنٗا كَبِيرٗا\nЭй Роббимиз, сен уларга азобдан икки ҳисса бергин ва уларни катта лаънатлаш ила лаънатлагин!» дедилар'}, {'chapter': 33, 'verse': 69, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَٱلَّذِينَ ءَاذَوۡاْ مُوسَىٰ فَبَرَّأَهُ ٱللَّهُ مِمَّا قَالُواْۚ وَكَانَ عِندَ ٱللَّهِ وَجِيهٗا\nЭй, иймон келтирганлар! Мусога озор берганлар каби бўлманглар. Бас, Аллоҳ уни улар айтган нарсадан поклади. Ва у Аллоҳнинг наздида обрўли эди'}, {'chapter': 33, 'verse': 70, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَقُولُواْ قَوۡلٗا سَدِيدٗا\nЭй, иймон келтирганлар! Аллоҳга тақво қилинглар ва тўғри сўз сўзланглар'}, {'chapter': 33, 'verse': 71, 'text': 'يُصۡلِحۡ لَكُمۡ أَعۡمَٰلَكُمۡ وَيَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ فَازَ فَوۡزًا عَظِيمًا\nУ зот сизнинг амалларингизни солиҳ қиладир ва гуноҳларингизни мағфират қиладир. Ким Аллоҳга ва Унинг Расулига итоат қилса, батаҳқиқ, буюк зафарга эришибдир'}, {'chapter': 33, 'verse': 72, 'text': 'إِنَّا عَرَضۡنَا ٱلۡأَمَانَةَ عَلَى ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلۡجِبَالِ فَأَبَيۡنَ أَن يَحۡمِلۡنَهَا وَأَشۡفَقۡنَ مِنۡهَا وَحَمَلَهَا ٱلۡإِنسَٰنُۖ إِنَّهُۥ كَانَ ظَلُومٗا جَهُولٗا\nАлбатта, Биз бу омонатни осмонларга, ерга ва тоғларга таклиф қилдик. Бас, улар уни кўтаришдан бош тортдилар ва ундан қўрқдилар. Уни инсон кўтарди. Дарҳақиқат, у ўта золим ва ўта жоҳилдир'}, {'chapter': 33, 'verse': 73, 'text': 'لِّيُعَذِّبَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ وَيَتُوبَ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمَۢا\nАллоҳ мунофиқлар ва мунофиқаларни, мушриклар ва мушрикаларни азоблаши учун ҳамда мўмин ва мўминаларнинг тавбаларини қабул этиши учундир. Аллоҳ ўта мағфиратли ва ўта раҳимли Зотдир'}], '34': [{'chapter': 34, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَلَهُ ٱلۡحَمۡدُ فِي ٱلۡأٓخِرَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ\nОсмонлардаги нарсалар ва ердаги нарсалар Уники бўлган Аллоҳга ҳамд бўлсин. Охиратдаги ҳамд ҳам Уникидир. Ва У ўта ҳикматли, ўта хабардор Зотдир'}, {'chapter': 34, 'verse': 2, 'text': 'يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۚ وَهُوَ ٱلرَّحِيمُ ٱلۡغَفُورُ\nУ, ерга нима кирса ва ундан нима чиқса, биладир, осмондан нима тушса ва унга нима чиқса, биладир. Ва У ўта раҳимли ва ўта мағфиратли Зотдир'}, {'chapter': 34, 'verse': 3, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَأۡتِينَا ٱلسَّاعَةُۖ قُلۡ بَلَىٰ وَرَبِّي لَتَأۡتِيَنَّكُمۡ عَٰلِمِ ٱلۡغَيۡبِۖ لَا يَعۡزُبُ عَنۡهُ مِثۡقَالُ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَلَآ أَصۡغَرُ مِن ذَٰلِكَ وَلَآ أَكۡبَرُ إِلَّا فِي كِتَٰبٖ مُّبِينٖ\nКуфр келтирганлар: «Бизга соат (қиёмат) келмас», дедилар. Сен: «Йўқ! Fойибни билгувчи Роббим ила қасамки, у сизларга албатта келур. У зотдан осмонлару ердаги зарра вазнича, ундан кичик нарса ҳам, катта нарса ҳам ғойиб бўлмас. Илло, ҳаммаси очиқ китобдадир», деб айт'}, {'chapter': 34, 'verse': 4, 'text': 'لِّيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِۚ أُوْلَـٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nИймон келтирган ва яхши амалларни қилганларни мукофотлаш учун. Ана ўшаларга, мағфират ва гўзал ризқ бордир'}, {'chapter': 34, 'verse': 5, 'text': 'وَٱلَّذِينَ سَعَوۡ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٞ\nБизнинг оятларимизни ожиз қолдиргувчи бўлиш учун ҳаракат қилганларга эса, ана ўшаларга, аламли жазодан иборат азоб бордир'}, {'chapter': 34, 'verse': 6, 'text': 'وَيَرَى ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ ٱلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ هُوَ ٱلۡحَقَّ وَيَهۡدِيٓ إِلَىٰ صِرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ\nИлм берилганлар сенга Роббингдан нозил қилинган нарсани айни ҳақ деб ва азийз ва мақталган Зотнинг йўлига ҳидоят қиладир, деб билурлар'}, {'chapter': 34, 'verse': 7, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ هَلۡ نَدُلُّكُمۡ عَلَىٰ رَجُلٖ يُنَبِّئُكُمۡ إِذَا مُزِّقۡتُمۡ كُلَّ مُمَزَّقٍ إِنَّكُمۡ لَفِي خَلۡقٖ جَدِيدٍ\nКуфр келтирганлар эса: «Сизларга бутунлай титилиб битган чоғингизда албатта, янгитдан яратилишингиз хабарини бераётган кишини кўрсатайликми'}, {'chapter': 34, 'verse': 8, 'text': 'أَفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةُۢۗ بَلِ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ فِي ٱلۡعَذَابِ وَٱلضَّلَٰلِ ٱلۡبَعِيدِ\nУ, Аллоҳга нисбатан ёлғон тўқидими ёки жинни бўлиб қолдими?» – дедилар. Йўқ! Охиратга иймон келтирмайдиганлар азобда ва йироқ залолатдадирлар'}, {'chapter': 34, 'verse': 9, 'text': 'أَفَلَمۡ يَرَوۡاْ إِلَىٰ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِن نَّشَأۡ نَخۡسِفۡ بِهِمُ ٱلۡأَرۡضَ أَوۡ نُسۡقِطۡ عَلَيۡهِمۡ كِسَفٗا مِّنَ ٱلسَّمَآءِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّكُلِّ عَبۡدٖ مُّنِيبٖ\nАхир, улар осмону ердаги, олдиларидаги нарсаларни, ортларидаги нарсаларни кўрмайдиларми?! Агар хоҳласак, уларни ерга юттириб юборурмиз ёки устларига осмондан парчалар туширурмиз. Албатта, бунда У зотга қайтгувчи ҳар бир банда учун оят(белги) бордир'}, {'chapter': 34, 'verse': 10, 'text': '۞وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ مِنَّا فَضۡلٗاۖ يَٰجِبَالُ أَوِّبِي مَعَهُۥ وَٱلطَّيۡرَۖ وَأَلَنَّا لَهُ ٱلۡحَدِيدَ\nБатаҳқиқ, Биз Довудга Ўзимиздан фазл бердик. «Эй тоғлар, у билан бирга (тасбиҳни) қайтар ва қушлар ҳам», (дедик). Ва унга темирни юмшоқ қилдик'}, {'chapter': 34, 'verse': 11, 'text': 'أَنِ ٱعۡمَلۡ سَٰبِغَٰتٖ وَقَدِّرۡ فِي ٱلسَّرۡدِۖ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ بَصِيرٞ\nВа (унга): «Совутлар қилгин ва бичимини ўлчовли қил. Солиҳ амаллар қилинглар. Албатта, Мен нима амал қилаётганингизни яхши кўриб тургувчиман, дедик'}, {'chapter': 34, 'verse': 12, 'text': 'وَلِسُلَيۡمَٰنَ ٱلرِّيحَ غُدُوُّهَا شَهۡرٞ وَرَوَاحُهَا شَهۡرٞۖ وَأَسَلۡنَا لَهُۥ عَيۡنَ ٱلۡقِطۡرِۖ وَمِنَ ٱلۡجِنِّ مَن يَعۡمَلُ بَيۡنَ يَدَيۡهِ بِإِذۡنِ رَبِّهِۦۖ وَمَن يَزِغۡ مِنۡهُمۡ عَنۡ أَمۡرِنَا نُذِقۡهُ مِنۡ عَذَابِ ٱلسَّعِيرِ\nВа Сулаймонга шамолни (бердик). У(шамол)нинг эрталаб кетиши бир ойлик, кечқурун қайтиши бир ойлик йўл эди. Унга мис булоғини оқизиб қўйдик ва жинлардан Роббининг изни ила унинг ҳузурида ишлайдиганлари бор эди. Улардан ким амримиздан чиқса, унга қаттиқ олов азобини тоттирамиз'}, {'chapter': 34, 'verse': 13, 'text': 'يَعۡمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَٰرِيبَ وَتَمَٰثِيلَ وَجِفَانٖ كَٱلۡجَوَابِ وَقُدُورٖ رَّاسِيَٰتٍۚ ٱعۡمَلُوٓاْ ءَالَ دَاوُۥدَ شُكۡرٗاۚ وَقَلِيلٞ مِّنۡ عِبَادِيَ ٱلشَّكُورُ\nУ(жин)лар унга меҳроблар, ҳайкаллар, ҳовузлар каби лаганлар ва собит қозонлардан хоҳлаганини қилиб беришар эди. Эй, Оли Довуд, шукр қилинглар! Бандаларимдан шукр қилгувчилари оздир'}, {'chapter': 34, 'verse': 14, 'text': 'فَلَمَّا قَضَيۡنَا عَلَيۡهِ ٱلۡمَوۡتَ مَا دَلَّهُمۡ عَلَىٰ مَوۡتِهِۦٓ إِلَّا دَآبَّةُ ٱلۡأَرۡضِ تَأۡكُلُ مِنسَأَتَهُۥۖ فَلَمَّا خَرَّ تَبَيَّنَتِ ٱلۡجِنُّ أَن لَّوۡ كَانُواْ يَعۡلَمُونَ ٱلۡغَيۡبَ مَا لَبِثُواْ فِي ٱلۡعَذَابِ ٱلۡمُهِينِ\nҚачонки, Биз у(Сулаймон)га ўлимни ҳукм этганимизда, уларга унинг ўлими ҳақида асосини еяётган ёғоч қуртидан бошқа ҳеч бир нарса далолат қилмади. Қачонки, у йиқилганда, жинларга, агар улар ғайбни билганларида, хорловчи азобда қолмаган бўлишлари аён бўлди'}, {'chapter': 34, 'verse': 15, 'text': 'لَقَدۡ كَانَ لِسَبَإٖ فِي مَسۡكَنِهِمۡ ءَايَةٞۖ جَنَّتَانِ عَن يَمِينٖ وَشِمَالٖۖ كُلُواْ مِن رِّزۡقِ رَبِّكُمۡ وَٱشۡكُرُواْ لَهُۥۚ بَلۡدَةٞ طَيِّبَةٞ وَرَبٌّ غَفُورٞ\nБатаҳқиқ, сабаъликлар учун ўз масканларида оят (белги) — ўнг ва чапдан икки боғ бор эди. «Роббингиз ризқидан енг ва Унга шукр қилинг! Не гўзал юрт ва ўта мағфиратли Роббингиз бор», (дедик)'}, {'chapter': 34, 'verse': 16, 'text': 'فَأَعۡرَضُواْ فَأَرۡسَلۡنَا عَلَيۡهِمۡ سَيۡلَ ٱلۡعَرِمِ وَبَدَّلۡنَٰهُم بِجَنَّتَيۡهِمۡ جَنَّتَيۡنِ ذَوَاتَيۡ أُكُلٍ خَمۡطٖ وَأَثۡلٖ وَشَيۡءٖ مِّن سِدۡرٖ قَلِيلٖ\nУлар юз ўгирдилар. Бас, Биз устларига арим селини юбордик ва уларнинг икки боғларини аччиқ мевали, юлғунзор ва бир озгина сидрли икки «боғ»га айлантирдик'}, {'chapter': 34, 'verse': 17, 'text': 'ذَٰلِكَ جَزَيۡنَٰهُم بِمَا كَفَرُواْۖ وَهَلۡ نُجَٰزِيٓ إِلَّا ٱلۡكَفُورَ\nУларни ношукрлик қилганлари учун ана шундоқ жазоладик. Биз фақат ўта ношукрларгагина жазо берурмиз'}, {'chapter': 34, 'verse': 18, 'text': 'وَجَعَلۡنَا بَيۡنَهُمۡ وَبَيۡنَ ٱلۡقُرَى ٱلَّتِي بَٰرَكۡنَا فِيهَا قُرٗى ظَٰهِرَةٗ وَقَدَّرۡنَا فِيهَا ٱلسَّيۡرَۖ سِيرُواْ فِيهَا لَيَالِيَ وَأَيَّامًا ءَامِنِينَ\nБиз улар билан Ўзимиз баракали қилган шаҳарлар орасида кетма-кет қишлоқлар қилиб, улар орасидаги юришни ўлчовли этиб қўйган ва «Уларда кечалар ҳам, кундузлар ҳам омонлик-ла юраверинг», (деган эдик)'}, {'chapter': 34, 'verse': 19, 'text': 'فَقَالُواْ رَبَّنَا بَٰعِدۡ بَيۡنَ أَسۡفَارِنَا وَظَلَمُوٓاْ أَنفُسَهُمۡ فَجَعَلۡنَٰهُمۡ أَحَادِيثَ وَمَزَّقۡنَٰهُمۡ كُلَّ مُمَزَّقٍۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ\nУлар: «Эй Роббимиз, сафарларимиз орасини узоқ қил!» дедилар ва ўзларига зулм қилдилар. Бас, Биз уларни ҳикояларга айлантирдик ва бутунлай парча-парча қилиб ташладик. Албатта, бунда ҳар бир ўта сабрли ва ўта шукрли учун оят (белги)лар бордир'}, {'chapter': 34, 'verse': 20, 'text': 'وَلَقَدۡ صَدَّقَ عَلَيۡهِمۡ إِبۡلِيسُ ظَنَّهُۥ فَٱتَّبَعُوهُ إِلَّا فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ\nБатаҳқиқ, иблис улар ҳақидаги гумонини рўёбга чиқарди. Бас, фақат бир гуруҳ мўминлардан бошқалари унга эргашдилар'}, {'chapter': 34, 'verse': 21, 'text': 'وَمَا كَانَ لَهُۥ عَلَيۡهِم مِّن سُلۡطَٰنٍ إِلَّا لِنَعۡلَمَ مَن يُؤۡمِنُ بِٱلۡأٓخِرَةِ مِمَّنۡ هُوَ مِنۡهَا فِي شَكّٖۗ وَرَبُّكَ عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ\nҲолбуки, унинг улар устидан ҳеч бир ҳукмронлиги бўлмас эди. Магар, Биз охиратга иймон келтирадиганни унда (охиратда) шаки бордан айиришимиз учун бўлди. Роббинг ҳар бир нарсани муҳофаза қилгувчидир'}, {'chapter': 34, 'verse': 22, 'text': 'قُلِ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُم مِّن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ مِثۡقَالَ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَمَا لَهُمۡ فِيهِمَا مِن شِرۡكٖ وَمَا لَهُۥ مِنۡهُم مِّن ظَهِيرٖ\nСен: «Аллоҳдан ўзга гумон қилганларингизга дуо қилаверинглар! Улар осмонлару ерда зарра вазнича нарсага молик эмаслар. Уларга бу(икки)ларида шериклик ҳам йўқ. Ва У зотга улардан бирорта ёрдамчи ҳам йўқ», деб айт'}, {'chapter': 34, 'verse': 23, 'text': 'وَلَا تَنفَعُ ٱلشَّفَٰعَةُ عِندَهُۥٓ إِلَّا لِمَنۡ أَذِنَ لَهُۥۚ حَتَّىٰٓ إِذَا فُزِّعَ عَن قُلُوبِهِمۡ قَالُواْ مَاذَا قَالَ رَبُّكُمۡۖ قَالُواْ ٱلۡحَقَّۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ\nУ зотнинг ҳузурида фақат Ўзи изн берганлардан бошқанинг шафоати манфаат бермас. Токи қалбларидан қўрқинч кетгач: «Роббингиз нима деди?» – дерлар. «Ҳақни», дерлар. У олий ва буюк Зотдир'}, {'chapter': 34, 'verse': 24, 'text': '۞قُلۡ مَن يَرۡزُقُكُم مِّنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُلِ ٱللَّهُۖ وَإِنَّآ أَوۡ إِيَّاكُمۡ لَعَلَىٰ هُدًى أَوۡ فِي ضَلَٰلٖ مُّبِينٖ\nСен: «Сизга осмонлару ердан ким ризқ берадир?» – деб айт. Сен: «Аллоҳ. Ва албатта, биз ёки сиз ҳидоятда ёхуд очиқ-ойдин залолатдадирмиз», деб айт'}, {'chapter': 34, 'verse': 25, 'text': 'قُل لَّا تُسۡـَٔلُونَ عَمَّآ أَجۡرَمۡنَا وَلَا نُسۡـَٔلُ عَمَّا تَعۡمَلُونَ\nСен: «Сизлар биз қилган жиноятдан сўралмассиз, биз эса сиз қилган амалдан сўралмасмиз», деб айт'}, {'chapter': 34, 'verse': 26, 'text': 'قُلۡ يَجۡمَعُ بَيۡنَنَا رَبُّنَا ثُمَّ يَفۡتَحُ بَيۡنَنَا بِٱلۡحَقِّ وَهُوَ ٱلۡفَتَّاحُ ٱلۡعَلِيمُ\nСен: «Роббимиз бизларни жамлар, сўнгра ҳақ ила орамизни очиқ қилур. У ўта очгувчи ва ўта билгувчидир», деб айт'}, {'chapter': 34, 'verse': 27, 'text': 'قُلۡ أَرُونِيَ ٱلَّذِينَ أَلۡحَقۡتُم بِهِۦ شُرَكَآءَۖ كَلَّاۚ بَلۡ هُوَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nСен: «У зотга шериклар қилиб тиқиштирганларингизни менга кўрсатинг. Йўқ! Фақат Азийз ва Ҳакийм бўлган Аллоҳгина бордир», деб айт'}, {'chapter': 34, 'verse': 28, 'text': 'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا كَآفَّةٗ لِّلنَّاسِ بَشِيرٗا وَنَذِيرٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nБиз сени барча одамларга фақат хушхабарчи ва огоҳлантиргувчи қилиб юбордик. Лекин одамларнинг кўплари билмаслар'}, {'chapter': 34, 'verse': 29, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангиз, бу ваъда қачон бўлур?» – дерлар'}, {'chapter': 34, 'verse': 30, 'text': 'قُل لَّكُم مِّيعَادُ يَوۡمٖ لَّا تَسۡتَـٔۡخِرُونَ عَنۡهُ سَاعَةٗ وَلَا تَسۡتَقۡدِمُونَ\nСен: «Сизга бир куннинг ваъдаси бор. Ундан бир он ортга ҳам қола олмассиз, олдин ҳам кета олмассиз», деб айт'}, {'chapter': 34, 'verse': 31, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَن نُّؤۡمِنَ بِهَٰذَا ٱلۡقُرۡءَانِ وَلَا بِٱلَّذِي بَيۡنَ يَدَيۡهِۗ وَلَوۡ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ مَوۡقُوفُونَ عِندَ رَبِّهِمۡ يَرۡجِعُ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ ٱلۡقَوۡلَ يَقُولُ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ لَوۡلَآ أَنتُمۡ لَكُنَّا مُؤۡمِنِينَ\nКуфр келтирганлар: «Ушбу Қуръонга ҳам, ундан олдинги нарсага ҳам ҳеч иймон келтирмасмиз», дедилар. Агар сен ўша золимларнинг Роббилари ҳузурида турғазиб қўйилганларида бир-бирларига гап қайтараётганларини кўрсанг эди. Ўшанда заиф саналганлар мутакаббирлик қилганларга: «Сиз бўлмаганингизда, албатта, биз мўмин бўлар эдик», дерлар'}, {'chapter': 34, 'verse': 32, 'text': 'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ لِلَّذِينَ ٱسۡتُضۡعِفُوٓاْ أَنَحۡنُ صَدَدۡنَٰكُمۡ عَنِ ٱلۡهُدَىٰ بَعۡدَ إِذۡ جَآءَكُمۖ بَلۡ كُنتُم مُّجۡرِمِينَ\nМутакаббирлик қилганлар заиф саналганларга: «Сизга ҳидоят келганидан кейин биз сизни ундан тўсдикми?! Йўқ! Ўзингиз жиноятчи бўлдингиз», дерлар'}, {'chapter': 34, 'verse': 33, 'text': 'وَقَالَ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ بَلۡ مَكۡرُ ٱلَّيۡلِ وَٱلنَّهَارِ إِذۡ تَأۡمُرُونَنَآ أَن نَّكۡفُرَ بِٱللَّهِ وَنَجۡعَلَ لَهُۥٓ أَندَادٗاۚ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۚ وَجَعَلۡنَا ٱلۡأَغۡلَٰلَ فِيٓ أَعۡنَاقِ ٱلَّذِينَ كَفَرُواْۖ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ\nЗаиф саналганлар мутакаббирлик қилганларга: «Йўқ! Кечаю кундузнинг макр-ҳийласи (тўсди). Ўшанда сизлар бизни Аллоҳга куфр келтиришимизга, унга тенгдошлар қўшишимизга амр қилар эдингиз», дедилар. Улар азобни кўрган чоғларида надоматни ичларида тутдилар. Биз куфр келтирганларнинг бўйинларига кишанлар солдик. Улар фақат қилиб юрган амалларига яраша жазоланурлар'}, {'chapter': 34, 'verse': 34, 'text': 'وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَآ إِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ\nҚайсики бир шаҳар-қишлоққа огоҳлантиргувчи юборсак, албатта, унинг маишатбозлари: «Биз, албатта, сиз ила юборилган нарсага куфр келтиргувчилармиз», дедилар'}, {'chapter': 34, 'verse': 35, 'text': 'وَقَالُواْ نَحۡنُ أَكۡثَرُ أَمۡوَٰلٗا وَأَوۡلَٰدٗا وَمَا نَحۡنُ بِمُعَذَّبِينَ\nУлар: «Бизнинг молу дунёмиз, бола-чақамиз кўпроқ. Биз азоблангувчи эмасмиз», дерлар'}, {'chapter': 34, 'verse': 36, 'text': 'قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nСен: «Албатта, Роббим хоҳлаган кишисига ризқни кенг қилур ва тор қилур. Лекин одамларнинг кўплари билмаслар», деб айт'}, {'chapter': 34, 'verse': 37, 'text': 'وَمَآ أَمۡوَٰلُكُمۡ وَلَآ أَوۡلَٰدُكُم بِٱلَّتِي تُقَرِّبُكُمۡ عِندَنَا زُلۡفَىٰٓ إِلَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَـٰٓئِكَ لَهُمۡ جَزَآءُ ٱلضِّعۡفِ بِمَا عَمِلُواْ وَهُمۡ فِي ٱلۡغُرُفَٰتِ ءَامِنُونَ\nНа молу дунёларингиз ва на бола-чақаларингиз сизни Бизнинг ҳузуримизга яқин қила олмас. Магар ким иймон келтирган ва солиҳ амал қилган бўлса, ана ўшаларгагина қилган амаллари туфайли кўпайтирилган мукофот бордир. Улар (жаннат) болохоналарида омонликдадирлар'}, {'chapter': 34, 'verse': 38, 'text': 'وَٱلَّذِينَ يَسۡعَوۡنَ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ\nБизнинг оятларимизни ожиз қолдириш учун ҳаракат қиладиганлар эса, ана ўшалар азоб ичига ҳозир қилинганлардир'}, {'chapter': 34, 'verse': 39, 'text': 'قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥۚ وَمَآ أَنفَقۡتُم مِّن شَيۡءٖ فَهُوَ يُخۡلِفُهُۥۖ وَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ\nАйт: «Албатта, Роббим ризқни Ўз бандаларидан кимни хоҳласа ўшанга кенг қилур ва тор қилур. Ҳар бир инфоқ қилган нарсангизнинг ўрнини У тўлдирур. У зот ризқ бергувчиларнинг яхшисидир»'}, {'chapter': 34, 'verse': 40, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ يَقُولُ لِلۡمَلَـٰٓئِكَةِ أَهَـٰٓؤُلَآءِ إِيَّاكُمۡ كَانُواْ يَعۡبُدُونَ\nУларнинг барчаларини жамлайдиган, сўнгра У зот фаришталарга: «Анавилар сизларга ибодат қилган эдиларми?» дейдиган кунда'}, {'chapter': 34, 'verse': 41, 'text': 'قَالُواْ سُبۡحَٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِمۖ بَلۡ كَانُواْ يَعۡبُدُونَ ٱلۡجِنَّۖ أَكۡثَرُهُم بِهِم مُّؤۡمِنُونَ\nУлар: «Сен поксан! Сен бизнинг валийимизсан! Улар эмас! Йўқ! Улар жинларга ибодат қилар эдилар. Кўплари ўшаларга иймон келтиргувчидирлар», дерлар'}, {'chapter': 34, 'verse': 42, 'text': 'فَٱلۡيَوۡمَ لَا يَمۡلِكُ بَعۡضُكُمۡ لِبَعۡضٖ نَّفۡعٗا وَلَا ضَرّٗا وَنَقُولُ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّتِي كُنتُم بِهَا تُكَذِّبُونَ\nБугунги кунда бир-бирингиз учун манфаатга ҳам, зарарга ҳам молик эмассизлар. Ва зулм қилганларга: «Ўзингиз ёлғонга чиқариб юрган оловнинг азобини татиб кўринг», дермиз'}, {'chapter': 34, 'verse': 43, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا رَجُلٞ يُرِيدُ أَن يَصُدَّكُمۡ عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُكُمۡ وَقَالُواْ مَا هَٰذَآ إِلَّآ إِفۡكٞ مُّفۡتَرٗىۚ وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nҚачонки уларга Бизнинг очиқ-ойдин оятларимиз тиловат қилинса: «Бу, сизни ота-боболарингиз ибодат қилиб юрган нарсадан тўсмоқни ирода қиладиган одам, холос», дедилар ва «Бу, тўқилган уйдирмадан бошқа нарса эмас», дедилар. Ва куфр келтирганлар ўзларига ҳақ келган чоғда: «Бу, очиқ-ойдин сеҳрдан бошқа нарса эмас», дедилар'}, {'chapter': 34, 'verse': 44, 'text': 'وَمَآ ءَاتَيۡنَٰهُم مِّن كُتُبٖ يَدۡرُسُونَهَاۖ وَمَآ أَرۡسَلۡنَآ إِلَيۡهِمۡ قَبۡلَكَ مِن نَّذِيرٖ\nҲолбуки, Биз уларга дарс оладиган китобларни бермаган эдик. Шунингдек, сендан олдин уларга бирор огоҳлантиргувчи ҳам юбормаган эдик'}, {'chapter': 34, 'verse': 45, 'text': 'وَكَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَمَا بَلَغُواْ مِعۡشَارَ مَآ ءَاتَيۡنَٰهُمۡ فَكَذَّبُواْ رُسُلِيۖ فَكَيۡفَ كَانَ نَكِيرِ\nУлардан илгари ўтганлар ҳам (Пайғамбарларни) ёлғончи қилган эдилар. (Булар) уларга берган нарсаларимизнинг ўндан бирига ҳам етганлари йўқ. Бас, Пайғамбарларимни ёлғончи қилдилар, аммо инкорим қандоқ бўлди'}, {'chapter': 34, 'verse': 46, 'text': '۞قُلۡ إِنَّمَآ أَعِظُكُم بِوَٰحِدَةٍۖ أَن تَقُومُواْ لِلَّهِ مَثۡنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُواْۚ مَا بِصَاحِبِكُم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ لَّكُم بَيۡنَ يَدَيۡ عَذَابٖ شَدِيدٖ\nСен: «Мен сизга биргина насиҳат қилурман: Аллоҳ учун холис, якка-якка ва тўп-тўп туриб бир фикр юритиб кўринглар. Соҳибингизда жинга оид ҳеч нарса йўқ. У сизларга шиддатли азобдан олдинги огоҳлантиргувчи, холос», деб айт'}, {'chapter': 34, 'verse': 47, 'text': 'قُلۡ مَا سَأَلۡتُكُم مِّنۡ أَجۡرٖ فَهُوَ لَكُمۡۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٞ\nСен: «Мен сизлардан ҳеч бир ажр-ҳақ сўрамадим. Бас, у сизга бўлсин. Менинг ажрим фақат Аллоҳнинг зиммасида. У зот ҳар бир нарсага Ўзи шоҳиддир», деб айт'}, {'chapter': 34, 'verse': 48, 'text': 'قُلۡ إِنَّ رَبِّي يَقۡذِفُ بِٱلۡحَقِّ عَلَّـٰمُ ٱلۡغُيُوبِ\nСен: «Албатта, Роббим ҳақни отур. У зот ғайбларни ўта билгувчидир», деб айт'}, {'chapter': 34, 'verse': 49, 'text': 'قُلۡ جَآءَ ٱلۡحَقُّ وَمَا يُبۡدِئُ ٱلۡبَٰطِلُ وَمَا يُعِيدُ\nСен: «Ҳақ келди! Ботил бошлай олмас ва қайтара ҳам олмас», деб айт'}, {'chapter': 34, 'verse': 50, 'text': 'قُلۡ إِن ضَلَلۡتُ فَإِنَّمَآ أَضِلُّ عَلَىٰ نَفۡسِيۖ وَإِنِ ٱهۡتَدَيۡتُ فَبِمَا يُوحِيٓ إِلَيَّ رَبِّيٓۚ إِنَّهُۥ سَمِيعٞ قَرِيبٞ\nСен: «Агар мен залолатга кетган бўлсам, ўз зараримга кетаман. Агар ҳидоят топсам, Роббим менга ваҳий қилган нарса туфайли топаман. Албатта, У зот ўта эшитгувчи, яқиндир», деб айт'}, {'chapter': 34, 'verse': 51, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ فَزِعُواْ فَلَا فَوۡتَ وَأُخِذُواْ مِن مَّكَانٖ قَرِيبٖ\nАгар уларнинг талвасага тушишларини, энди қочиб қутулиш йўқ ва яқин макондан тутилишларини бир кўрсанг эди'}, {'chapter': 34, 'verse': 52, 'text': 'وَقَالُوٓاْ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانِۭ بَعِيدٖ\nУлар: «Унга иймон келтирдик», дерлар. Энди, узоқ бир макондан қўллари қандоқ етсин'}, {'chapter': 34, 'verse': 53, 'text': 'وَقَدۡ كَفَرُواْ بِهِۦ مِن قَبۡلُۖ وَيَقۡذِفُونَ بِٱلۡغَيۡبِ مِن مَّكَانِۭ بَعِيدٖ\nҲолбуки, унга олдин куфр келтирган эдилар. Fойибга узоқ жойдан тош отар эдилар'}, {'chapter': 34, 'verse': 54, 'text': 'وَحِيلَ بَيۡنَهُمۡ وَبَيۡنَ مَا يَشۡتَهُونَ كَمَا فُعِلَ بِأَشۡيَاعِهِم مِّن قَبۡلُۚ إِنَّهُمۡ كَانُواْ فِي شَكّٖ مُّرِيبِۭ\nВа улар билан ўзлари иштаҳа қилган нарсанинг ораси тўсилди. Худди олдин ўтган маслакдошлари каби. Албатта, улар шак-шубҳа ичида эдилар'}], '35': [{'chapter': 35, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ جَاعِلِ ٱلۡمَلَـٰٓئِكَةِ رُسُلًا أُوْلِيٓ أَجۡنِحَةٖ مَّثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۚ يَزِيدُ فِي ٱلۡخَلۡقِ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nҲамду сано осмонлару ерни йўқдан бор қилган, фаришталарни икки, уч, тўрт қанотли элчилар қилган Аллоҳга бўлсин. У зот махлуқотларда нимани хоҳласа, зиёда қилур. Албатта, Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 35, 'verse': 2, 'text': 'مَّا يَفۡتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحۡمَةٖ فَلَا مُمۡسِكَ لَهَاۖ وَمَا يُمۡسِكۡ فَلَا مُرۡسِلَ لَهُۥ مِنۢ بَعۡدِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАллоҳ одамларга не раҳматни очса, бас, уни тутиб қолгувчи бўлмас. У зот тутиб қолган нарсани Ундан кейин қўйиб юборгувчи бўлмас. У зот Азийзу Ҳакиймдир'}, {'chapter': 35, 'verse': 3, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡۚ هَلۡ مِنۡ خَٰلِقٍ غَيۡرُ ٱللَّهِ يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ\nЭй одамлар! Аллоҳнинг сизга берган неъматларини эсланг! Сизларга осмон ва ердан ризқ берадиган Аллоҳдан бошқа холиқ борми? Ундан ўзга ибодатга сазовор зот йўқ. Бас, қаён кетмоқдасиз'}, {'chapter': 35, 'verse': 4, 'text': 'وَإِن يُكَذِّبُوكَ فَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nАгар сени ёлғончига чиқарсалар, бас, сендан олдин ҳам Пайғамбарлар ёлғончи қилинганлар. Барча ишлар Аллоҳга қайтарилур'}, {'chapter': 35, 'verse': 5, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ\nЭй одамлар! Албатта, Аллоҳнинг ваъдаси ҳақдир. Бас, дунё ҳаёти сизни ғурурга кетказмасин. Ўта ғурурга кетказгувчи сизни Аллоҳ ила ғурурга кетказмасин'}, {'chapter': 35, 'verse': 6, 'text': 'إِنَّ ٱلشَّيۡطَٰنَ لَكُمۡ عَدُوّٞ فَٱتَّخِذُوهُ عَدُوًّاۚ إِنَّمَا يَدۡعُواْ حِزۡبَهُۥ لِيَكُونُواْ مِنۡ أَصۡحَٰبِ ٱلسَّعِيرِ\nАлбатта, шайтон сизга душмандир. Бас, уни душман тутинг. У ўз гуруҳини фақат ўта қизиган дўзахга эга бўлишларига чақирур'}, {'chapter': 35, 'verse': 7, 'text': 'ٱلَّذِينَ كَفَرُواْ لَهُمۡ عَذَابٞ شَدِيدٞۖ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٌ\nКуфр келтирганларга шиддатли азоб бордир. Иймон келтириб, солиҳ амалларни қилганларга мағфират ва улкан ажр бордир'}, {'chapter': 35, 'verse': 8, 'text': 'أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُ حَسَنٗاۖ فَإِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۖ فَلَا تَذۡهَبۡ نَفۡسُكَ عَلَيۡهِمۡ حَسَرَٰتٍۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَصۡنَعُونَ\nАхир, қилган ёмон амали ўзига зийнатлаб кўрсатилиб, уни гўзал деб билган одам(дан яхшилик чиқармиди)?! Албатта, Аллоҳ хоҳлаган кимсани залолатга кетказур, хоҳлаган кимсани ҳидоятга солур. Бас, уларга жонинг ҳасрат-ла ачинмасин. Албатта, Аллоҳ нима қилаётганларини яхши билгувчидир'}, {'chapter': 35, 'verse': 9, 'text': 'وَٱللَّهُ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَسُقۡنَٰهُ إِلَىٰ بَلَدٖ مَّيِّتٖ فَأَحۡيَيۡنَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ كَذَٰلِكَ ٱلنُّشُورُ\nАллоҳ шамолларни юборган Зотдир. Бас, улар булутни қўзғатади. Биз уни ўлик юрт томон ҳайдаб, у билан ерни ўлиб ётганидан кейин тирилтирурмиз. Тирилиш ҳам шунга ўхшашдир'}, {'chapter': 35, 'verse': 10, 'text': 'مَن كَانَ يُرِيدُ ٱلۡعِزَّةَ فَلِلَّهِ ٱلۡعِزَّةُ جَمِيعًاۚ إِلَيۡهِ يَصۡعَدُ ٱلۡكَلِمُ ٱلطَّيِّبُ وَٱلۡعَمَلُ ٱلصَّـٰلِحُ يَرۡفَعُهُۥۚ وَٱلَّذِينَ يَمۡكُرُونَ ٱلسَّيِّـَٔاتِ لَهُمۡ عَذَابٞ شَدِيدٞۖ وَمَكۡرُ أُوْلَـٰٓئِكَ هُوَ يَبُورُ\nКимки иззат истаса, бас, барча иззат Аллоҳникидир. Унга хуш каломлар юксалур ва солиҳ амал кўтарур уни. Макр ила ёмонликлар қилганларга шиддатли азоб бордир. Ана ўшаларнинг макри ботил бўлур'}, {'chapter': 35, 'verse': 11, 'text': 'وَٱللَّهُ خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ جَعَلَكُمۡ أَزۡوَٰجٗاۚ وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَمَا يُعَمَّرُ مِن مُّعَمَّرٖ وَلَا يُنقَصُ مِنۡ عُمُرِهِۦٓ إِلَّا فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nАллоҳ сизларни тупроқдан, сўнгра нутфадан яратиб, кейин жуфт-жуфт қилиб қўйди. Қайси бир урғочи нимага ҳомиладор бўлса, нимани туғса, фақат Унинг илми билан бўлур. Ҳар бир умр кўргувчига умр берилиши ёки унинг умрининг қисқартирилиши албатта китобда бордир. Албатта, бу Аллоҳга осондир'}, {'chapter': 35, 'verse': 12, 'text': 'وَمَا يَسۡتَوِي ٱلۡبَحۡرَانِ هَٰذَا عَذۡبٞ فُرَاتٞ سَآئِغٞ شَرَابُهُۥ وَهَٰذَا مِلۡحٌ أُجَاجٞۖ وَمِن كُلّٖ تَأۡكُلُونَ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُونَ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ فِيهِ مَوَاخِرَ لِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nДарёю денгиз, иккиси тенг бўла олмас. Буниси тотли, чанқоқ босар, ичиш осондир. Буниси эса шўр, тахирдир. Сиз ҳар биридан тоза гўшт ейсиз ва тақинчоқ чиқариб тақурсиз. Ва Унинг фазлидан талаб қилишингиз учун у(сув)ни ёриб кетаётган кемаларни кўрурсиз. Шоядки, шукр қилсангиз'}, {'chapter': 35, 'verse': 13, 'text': 'يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۚ وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ مَا يَمۡلِكُونَ مِن قِطۡمِيرٍ\nУ зот кечани кундузга, кундузни кечага киритур, қуёш ва ойни беминнат хизматкор қилиб қўйгандир. Ҳар бири белгиланган муддатгача юрадир. Ана шу Аллоҳ Роббингиздир. Мулк Уникидир. Ундан ўзга илтижо қилаётганларингиз эса, данак пўстлоғига ҳам молик эмаслар'}, {'chapter': 35, 'verse': 14, 'text': 'إِن تَدۡعُوهُمۡ لَا يَسۡمَعُواْ دُعَآءَكُمۡ وَلَوۡ سَمِعُواْ مَا ٱسۡتَجَابُواْ لَكُمۡۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُونَ بِشِرۡكِكُمۡۚ وَلَا يُنَبِّئُكَ مِثۡلُ خَبِيرٖ\nАгар уларга дуо қилсангиз, дуоингизни эшитмаслар. Гар эшитсалар ҳам, сизга жавоб бера олмаслар. Қиёмат куни эса, сизнинг уларни ширк келтирганингизни инкор этурлар. Ўта хабардор Зотдек ҳеч ким сенга хабар бера олмас'}, {'chapter': 35, 'verse': 15, 'text': '۞يَـٰٓأَيُّهَا ٱلنَّاسُ أَنتُمُ ٱلۡفُقَرَآءُ إِلَى ٱللَّهِۖ وَٱللَّهُ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nЭй одамлар! Сизлар Аллоҳга муҳтождирсиз. Аллоҳ эса, Ўзи беҳожат ва мақталган Зотдир'}, {'chapter': 35, 'verse': 16, 'text': 'إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ\nАгар У зот хоҳласа, сизни кетказиб, янги бир халқни келтирур'}, {'chapter': 35, 'verse': 17, 'text': 'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ\nВа бу Аллоҳ учун қийин эмасдир'}, {'chapter': 35, 'verse': 18, 'text': 'وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ وَإِن تَدۡعُ مُثۡقَلَةٌ إِلَىٰ حِمۡلِهَا لَا يُحۡمَلۡ مِنۡهُ شَيۡءٞ وَلَوۡ كَانَ ذَا قُرۡبَىٰٓۗ إِنَّمَا تُنذِرُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ وَأَقَامُواْ ٱلصَّلَوٰةَۚ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفۡسِهِۦۚ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ\nЎзи юкли жон ўзга жоннинг юкини кўтара олмас. Юки оғир жон ўз юкига (бошқани) чақирса-да, ундан ҳеч бир нарса кўтарилмас. Гарчи қариндош бўлса ҳам. Сен фақат ғайбдаги Роббиларидан қўрқадиган ва намозни тўкис адо этганларни огоҳлантирурсан. Ким покланса, ўзи учун покланур. Қайтиш Аллоҳгадир'}, {'chapter': 35, 'verse': 19, 'text': 'وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ\nКўр билан кўзи очиқ тенг бўлмас'}, {'chapter': 35, 'verse': 20, 'text': 'وَلَا ٱلظُّلُمَٰتُ وَلَا ٱلنُّورُ\nЗулумотлар билан нур ҳам'}, {'chapter': 35, 'verse': 21, 'text': 'وَلَا ٱلظِّلُّ وَلَا ٱلۡحَرُورُ\nСоя билан жазирама ҳам'}, {'chapter': 35, 'verse': 22, 'text': 'وَمَا يَسۡتَوِي ٱلۡأَحۡيَآءُ وَلَا ٱلۡأَمۡوَٰتُۚ إِنَّ ٱللَّهَ يُسۡمِعُ مَن يَشَآءُۖ وَمَآ أَنتَ بِمُسۡمِعٖ مَّن فِي ٱلۡقُبُورِ\nТириклар билан ўликлар тенг бўлмас. Албатта, Аллоҳ хоҳлаган кимсага эшиттирур. Сен қабрлардаги кимсаларга эшиттиргувчи эмассан'}, {'chapter': 35, 'verse': 23, 'text': 'إِنۡ أَنتَ إِلَّا نَذِيرٌ\nСен фақат огоҳлантиргувчисан, холос'}, {'chapter': 35, 'verse': 24, 'text': 'إِنَّآ أَرۡسَلۡنَٰكَ بِٱلۡحَقِّ بَشِيرٗا وَنَذِيرٗاۚ وَإِن مِّنۡ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٞ\nАлбатта, Биз сени ҳақ ила хушхабарчи ва огоҳлантиргувчи этиб юбордик. Қайси бир умматки бўлса, унда огоҳлантиргувчи бўлган'}, {'chapter': 35, 'verse': 25, 'text': 'وَإِن يُكَذِّبُوكَ فَقَدۡ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ وَبِٱلزُّبُرِ وَبِٱلۡكِتَٰبِ ٱلۡمُنِيرِ\nАгар сени ёлғончи қилсалар, бас, батаҳқиқ, улардан олдин ўтганлар ҳам ёлғончи қилганлар. Ҳолбуки, Пайғамбарлари уларга равшан мўъжизалар, саҳифалар ва нурли китоб келтирган эдилар'}, {'chapter': 35, 'verse': 26, 'text': 'ثُمَّ أَخَذۡتُ ٱلَّذِينَ كَفَرُواْۖ فَكَيۡفَ كَانَ نَكِيرِ\nСўнгра, куфр келтирганларни тутдим. Бас, Менинг инкорим қандоқ бўлди'}, {'chapter': 35, 'verse': 27, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ ثَمَرَٰتٖ مُّخۡتَلِفًا أَلۡوَٰنُهَاۚ وَمِنَ ٱلۡجِبَالِ جُدَدُۢ بِيضٞ وَحُمۡرٞ مُّخۡتَلِفٌ أَلۡوَٰنُهَا وَغَرَابِيبُ سُودٞ\nАллоҳнинг осмондан сув туширганини кўрмадингми? Бас, у ила турли ранглардаги меваларни ва тоғлардан ҳам йўл-йўл, оқ ҳамда қизилнинг турли рангларини ва қоп-қораларини чиқардик'}, {'chapter': 35, 'verse': 28, 'text': 'وَمِنَ ٱلنَّاسِ وَٱلدَّوَآبِّ وَٱلۡأَنۡعَٰمِ مُخۡتَلِفٌ أَلۡوَٰنُهُۥ كَذَٰلِكَۗ إِنَّمَا يَخۡشَى ٱللَّهَ مِنۡ عِبَادِهِ ٱلۡعُلَمَـٰٓؤُاْۗ إِنَّ ٱللَّهَ عَزِيزٌ غَفُورٌ\nШунингдек, одамлардан, ҳайвонлардан ва чорвалардан ҳам турли ранглардагиларини чиқардик. Аллоҳдан бандалари ичидан фақат олимларигина қўрқарлар. Албатта, Аллоҳ азийз ва ўта мағфиратли Зотдир'}, {'chapter': 35, 'verse': 29, 'text': 'إِنَّ ٱلَّذِينَ يَتۡلُونَ كِتَٰبَ ٱللَّهِ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ يَرۡجُونَ تِجَٰرَةٗ لَّن تَبُورَ\nАлбатта, Аллоҳнинг китобини тиловат қиладиганлар, намозни тўкис адо этиб, Биз уларга ризқ қилиб берган нарсалардан махфий ва ошкора инфоқ қилганлар ҳаргиз касодга учрамайдиган тижоратдан умидвор бўлурлар'}, {'chapter': 35, 'verse': 30, 'text': 'لِيُوَفِّيَهُمۡ أُجُورَهُمۡ وَيَزِيدَهُم مِّن فَضۡلِهِۦٓۚ إِنَّهُۥ غَفُورٞ شَكُورٞ\nЧунки, У зот уларга ажрларини тўлиқ қилиб берур ва Ўз фазлидан зиёда ҳам қилур. Албатта, У ўта мағфиратли ва Шакур(оз амал учун кўп савоб берувчи)дир'}, {'chapter': 35, 'verse': 31, 'text': 'وَٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ مِنَ ٱلۡكِتَٰبِ هُوَ ٱلۡحَقُّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِۗ إِنَّ ٱللَّهَ بِعِبَادِهِۦ لَخَبِيرُۢ بَصِيرٞ\nБиз сенга ваҳий қилган китоб айни ҳақдир, ўзидан олдинги нарсани тасдиқловчи бўлгандир. Албатта, Аллоҳ Ўз бандаларидан ўта хабардор ва уларни ўта кўргувчидир'}, {'chapter': 35, 'verse': 32, 'text': 'ثُمَّ أَوۡرَثۡنَا ٱلۡكِتَٰبَ ٱلَّذِينَ ٱصۡطَفَيۡنَا مِنۡ عِبَادِنَاۖ فَمِنۡهُمۡ ظَالِمٞ لِّنَفۡسِهِۦ وَمِنۡهُم مُّقۡتَصِدٞ وَمِنۡهُمۡ سَابِقُۢ بِٱلۡخَيۡرَٰتِ بِإِذۡنِ ٱللَّهِۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ\nСўнгра бу китобни Ўз бандаларимиздан танлаб олганларимизга мерос қилдик. Бас, улардан ўзига ўзи зулм қилувчилари бор, улардан ўртачалари бор ва улардан Аллоҳнинг изни ила яхшиликларга ўзувчилар ҳам бор. Ана шу айни улуғ фазлдир'}, {'chapter': 35, 'verse': 33, 'text': 'جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَلُؤۡلُؤٗاۖ وَلِبَاسُهُمۡ فِيهَا حَرِيرٞ\nУлар адн жаннатларига кирурлар. Унда тилло билакузуклар ва инжулар ила безатилурлар. Унда либослари ипакдир'}, {'chapter': 35, 'verse': 34, 'text': 'وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِيٓ أَذۡهَبَ عَنَّا ٱلۡحَزَنَۖ إِنَّ رَبَّنَا لَغَفُورٞ شَكُورٌ\nУлар: «Биздан ғам-қайғуни кетказган Аллоҳга ҳамду сано бўлсин. Албатта, Роббимиз ўта мағфиратли ва Шакур(оз амал учун кўп савоб берувчи)дир», – дерлар'}, {'chapter': 35, 'verse': 35, 'text': 'ٱلَّذِيٓ أَحَلَّنَا دَارَ ٱلۡمُقَامَةِ مِن فَضۡلِهِۦ لَا يَمَسُّنَا فِيهَا نَصَبٞ وَلَا يَمَسُّنَا فِيهَا لُغُوبٞ\nУ зот бизларни Ўз фазли ила бир турар жойга жойлаштирдики, у ерда бизга чарчоқ ҳам етмас, ҳорғинлик ҳам етмас'}, {'chapter': 35, 'verse': 36, 'text': 'وَٱلَّذِينَ كَفَرُواْ لَهُمۡ نَارُ جَهَنَّمَ لَا يُقۡضَىٰ عَلَيۡهِمۡ فَيَمُوتُواْ وَلَا يُخَفَّفُ عَنۡهُم مِّنۡ عَذَابِهَاۚ كَذَٰلِكَ نَجۡزِي كُلَّ كَفُورٖ\nКуфр келтирганларга жаҳаннам оташи бордир. Уларга на ҳукм қилинмаски, ўлсалар ва на улардан у(жаҳаннам)нинг азобидан енгиллатилмас. Ҳар бир кофирни шундоқ жазолаймиз'}, {'chapter': 35, 'verse': 37, 'text': 'وَهُمۡ يَصۡطَرِخُونَ فِيهَا رَبَّنَآ أَخۡرِجۡنَا نَعۡمَلۡ صَٰلِحًا غَيۡرَ ٱلَّذِي كُنَّا نَعۡمَلُۚ أَوَلَمۡ نُعَمِّرۡكُم مَّا يَتَذَكَّرُ فِيهِ مَن تَذَكَّرَ وَجَآءَكُمُ ٱلنَّذِيرُۖ فَذُوقُواْ فَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٍ\nУлар у ерда: «Эй Роббимиз!!! Бизларни чиқар!!! Қилиб юрган амалларимиздан бошқа, солиҳ амаллар қиламиз!!!» – деб доду фарёд солурлар. «Сизларга эслайдиган одам эслагудек умр бермаган эдикми?! Сизларга огоҳлантиргувчи келмаган эдими?! Бас, энди, тотаверинглар! Золимларга ҳеч бир ёрдамчи йўқдир», (дейилур)'}, {'chapter': 35, 'verse': 38, 'text': 'إِنَّ ٱللَّهَ عَٰلِمُ غَيۡبِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nАлбатта, Аллоҳ осмонлару ернинг ғайбини билгувчидир. Албатта, У диллардаги сирларни ҳам ўта билгувчидир'}, {'chapter': 35, 'verse': 39, 'text': 'هُوَ ٱلَّذِي جَعَلَكُمۡ خَلَـٰٓئِفَ فِي ٱلۡأَرۡضِۚ فَمَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَلَا يَزِيدُ ٱلۡكَٰفِرِينَ كُفۡرُهُمۡ عِندَ رَبِّهِمۡ إِلَّا مَقۡتٗاۖ وَلَا يَزِيدُ ٱلۡكَٰفِرِينَ كُفۡرُهُمۡ إِلَّا خَسَارٗا\nУ зот сизларни ер юзида халифалар қилиб қўйгандир. Ким куфр келтирса, унинг кофирлиги ўзига зарар. Кофирлар учун куфрлари Роббилари ҳузурида ғазабдан бошқани зиёда қилмас. Кофирлар учун куфрлари зиёндан бошқани зиёда қилмас'}, {'chapter': 35, 'verse': 40, 'text': 'قُلۡ أَرَءَيۡتُمۡ شُرَكَآءَكُمُ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِ أَمۡ ءَاتَيۡنَٰهُمۡ كِتَٰبٗا فَهُمۡ عَلَىٰ بَيِّنَتٖ مِّنۡهُۚ بَلۡ إِن يَعِدُ ٱلظَّـٰلِمُونَ بَعۡضُهُم بَعۡضًا إِلَّا غُرُورًا\nСен: «Менга Аллоҳдан бошқа илтижо қилаётган шерикларингизнинг хабарини беринг-чи?! Менга кўрсатинглар-чи, улар ерда нимани яратдилар?! Ёки уларнинг осмонлар(иши)да шерикликлари борми?!» – деб айт. Ёки Биз уларга китоб берганмиз-у, улар ундан бирон ҳужжатга эга бўлганларми?! Йўқ!!! Золимлар бир-бирларига алдовдан бошқани ваъда қилмаслар'}, {'chapter': 35, 'verse': 41, 'text': '۞إِنَّ ٱللَّهَ يُمۡسِكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ أَن تَزُولَاۚ وَلَئِن زَالَتَآ إِنۡ أَمۡسَكَهُمَا مِنۡ أَحَدٖ مِّنۢ بَعۡدِهِۦٓۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا\nАлбатта, Аллоҳ осмонлару ерни қулаб тушишларидан ушлаб турур. Агар улар қулайдиган бўлсалар, Ундан ўзга ҳеч ким уларни ушлаб тура олмас. Албатта, У ўта ҳалийм ва ўта мағфиратли бўлган Зотдир'}, {'chapter': 35, 'verse': 42, 'text': 'وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَهُمۡ نَذِيرٞ لَّيَكُونُنَّ أَهۡدَىٰ مِنۡ إِحۡدَى ٱلۡأُمَمِۖ فَلَمَّا جَآءَهُمۡ نَذِيرٞ مَّا زَادَهُمۡ إِلَّا نُفُورًا\nУлар жон-жаҳдлари билан, агар ўзларига огоҳлантирувчи келса, ҳар қандай умматдан кўра ҳидоятли бўлишлари ҳақида Аллоҳ номи ила қасам ичар эдилар. Қачонки огоҳлантирувчи келганида, уларда нафратдан бошқани зиёда қилмади'}, {'chapter': 35, 'verse': 43, 'text': 'ٱسۡتِكۡبَارٗا فِي ٱلۡأَرۡضِ وَمَكۡرَ ٱلسَّيِّيِٕۚ وَلَا يَحِيقُ ٱلۡمَكۡرُ ٱلسَّيِّئُ إِلَّا بِأَهۡلِهِۦۚ فَهَلۡ يَنظُرُونَ إِلَّا سُنَّتَ ٱلۡأَوَّلِينَۚ فَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَبۡدِيلٗاۖ وَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَحۡوِيلًا\nЕр юзида мутакаббирлик ва ёмон макр учун (қилдилар). Ёмон макр эса, фақат ўз эгасига қайтадир. Улар фақат илгаригиларнинг суннатига интизор бўлмоқдалар, холос. Аллоҳнинг суннатига ҳаргиз бадал топа олмассан. Аллоҳнинг суннатига ҳаргиз ўзгартириш топа олмассан'}, {'chapter': 35, 'verse': 44, 'text': 'أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَكَانُوٓاْ أَشَدَّ مِنۡهُمۡ قُوَّةٗۚ وَمَا كَانَ ٱللَّهُ لِيُعۡجِزَهُۥ مِن شَيۡءٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ عَلِيمٗا قَدِيرٗا\nАхир, улар ер юзида юриб ўзларидан олдин ўтганларнинг оқибати қандоқ бўлганига назар солмайдиларми?! Ҳолбуки, ўшалар булардан кўра қувватлироқ эдилар. На осмонларда ва на ерда бирор нарса Аллоҳни ожиз қолдира олмас. Албатта, У зот ўта илмли ва ўта қодирдир'}, {'chapter': 35, 'verse': 45, 'text': 'وَلَوۡ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِمَا كَسَبُواْ مَا تَرَكَ عَلَىٰ ظَهۡرِهَا مِن دَآبَّةٖ وَلَٰكِن يُؤَخِّرُهُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۖ فَإِذَا جَآءَ أَجَلُهُمۡ فَإِنَّ ٱللَّهَ كَانَ بِعِبَادِهِۦ بَصِيرَۢا\nАгар Аллоҳ одамларни қилганларига яраша оладиган бўлса, ер устида қимир этган жониворни қўймас эди. Лекин уларни белгиланган муддатгача қўйиб қўюр. Ўша ажаллари келганда эса... Бас, албатта, Аллоҳ бандаларини кўриб тургувчи Зотдир'}], '36': [{'chapter': 36, 'verse': 1, 'text': 'يسٓ\nЙаасийн'}, {'chapter': 36, 'verse': 2, 'text': 'وَٱلۡقُرۡءَانِ ٱلۡحَكِيمِ\nҚуръони Ҳаким ила қасам'}, {'chapter': 36, 'verse': 3, 'text': 'إِنَّكَ لَمِنَ ٱلۡمُرۡسَلِينَ\nАлбатта, сен Пайғамбарлардандирсан'}, {'chapter': 36, 'verse': 4, 'text': 'عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nСиротул мустақиймдасан'}, {'chapter': 36, 'verse': 5, 'text': 'تَنزِيلَ ٱلۡعَزِيزِ ٱلرَّحِيمِ\nБу Азийзу Роҳиймнинг нозил қилганидир'}, {'chapter': 36, 'verse': 6, 'text': 'لِتُنذِرَ قَوۡمٗا مَّآ أُنذِرَ ءَابَآؤُهُمۡ فَهُمۡ غَٰفِلُونَ\nОта-боболари огоҳлантирилмаган, ўзлари ҳам ғофил бўлган қавмларни огоҳлантиришинг учун'}, {'chapter': 36, 'verse': 7, 'text': 'لَقَدۡ حَقَّ ٱلۡقَوۡلُ عَلَىٰٓ أَكۡثَرِهِمۡ فَهُمۡ لَا يُؤۡمِنُونَ\nБатаҳқиқ, уларнинг кўпларига (азоб) сўзи ҳақ бўлди. Бас, улар иймон келтирмаслар'}, {'chapter': 36, 'verse': 8, 'text': 'إِنَّا جَعَلۡنَا فِيٓ أَعۡنَٰقِهِمۡ أَغۡلَٰلٗا فَهِيَ إِلَى ٱلۡأَذۡقَانِ فَهُم مُّقۡمَحُونَ\nБиз уларнинг бўйинларига кишанлар солдик. Улар иякларигача етадир. Бас, улар кўзлари юмуқ, осмонга қараб ғўдайиб қолдилар'}, {'chapter': 36, 'verse': 9, 'text': 'وَجَعَلۡنَا مِنۢ بَيۡنِ أَيۡدِيهِمۡ سَدّٗا وَمِنۡ خَلۡفِهِمۡ سَدّٗا فَأَغۡشَيۡنَٰهُمۡ فَهُمۡ لَا يُبۡصِرُونَ\nВа Биз уларнинг олдидан ҳам тўсиқ қилдик, орқаларидан ҳам тўсиқ қилдик, кўзларига парда тортдик. Бас, улар кўрмаслар'}, {'chapter': 36, 'verse': 10, 'text': 'وَسَوَآءٌ عَلَيۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ\nВа уларга бари бирдир — қўрқитсанг ҳам ва ёки қўрқитмасанг ҳам — иймонга келмаслар'}, {'chapter': 36, 'verse': 11, 'text': 'إِنَّمَا تُنذِرُ مَنِ ٱتَّبَعَ ٱلذِّكۡرَ وَخَشِيَ ٱلرَّحۡمَٰنَ بِٱلۡغَيۡبِۖ فَبَشِّرۡهُ بِمَغۡفِرَةٖ وَأَجۡرٖ كَرِيمٍ\nСен эса, фақат Зикр(эслатма)га эргашган ва Роҳмандан ғойибона қўрққан кимсани огоҳлантирурсан. Бас, унга мағфират ва гўзал ажрнинг хушхабарини бер'}, {'chapter': 36, 'verse': 12, 'text': 'إِنَّا نَحۡنُ نُحۡيِ ٱلۡمَوۡتَىٰ وَنَكۡتُبُ مَا قَدَّمُواْ وَءَاثَٰرَهُمۡۚ وَكُلَّ شَيۡءٍ أَحۡصَيۡنَٰهُ فِيٓ إِمَامٖ مُّبِينٖ\nАлбатта, Биз Ўзимиз ўликларни тирилтирурмиз ва улар тақдим қилган нарсаларни ва қолдирган асарларни ёзурмиз. Очиқ-ойдин имомда(яъни номаи аъмолда) ҳар бир нарсанинг ҳисобини олиб қўйганмиз'}, {'chapter': 36, 'verse': 13, 'text': 'وَٱضۡرِبۡ لَهُم مَّثَلًا أَصۡحَٰبَ ٱلۡقَرۡيَةِ إِذۡ جَآءَهَا ٱلۡمُرۡسَلُونَ\nСен уларга Пайғамбарлар шаҳар аҳолисига келган пайтни мисол қилиб келтир'}, {'chapter': 36, 'verse': 14, 'text': 'إِذۡ أَرۡسَلۡنَآ إِلَيۡهِمُ ٱثۡنَيۡنِ فَكَذَّبُوهُمَا فَعَزَّزۡنَا بِثَالِثٖ فَقَالُوٓاْ إِنَّآ إِلَيۡكُم مُّرۡسَلُونَ\nЎшанда Биз уларга иккитани юборган эдик, улар икковларини ёлғончига чиқардилар. Бас, Биз учинчи билан қўлладик. Шунда: «Албатта, биз сизларга юборилган Пайғамбарлармиз», дедилар'}, {'chapter': 36, 'verse': 15, 'text': 'قَالُواْ مَآ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا وَمَآ أَنزَلَ ٱلرَّحۡمَٰنُ مِن شَيۡءٍ إِنۡ أَنتُمۡ إِلَّا تَكۡذِبُونَ\nУлар: «Сиз бизга ўхшаган башардан бошқа нарса эмассиз. Роҳман ҳеч нарса нозил қилгани йўқ. Сизлар фақат ёлғон сўзламоқдасиз, холос», дедилар'}, {'chapter': 36, 'verse': 16, 'text': 'قَالُواْ رَبُّنَا يَعۡلَمُ إِنَّآ إِلَيۡكُمۡ لَمُرۡسَلُونَ\nУлар: «Роббимиз билади. Албатта, биз сизларга юборилган Пайғамбарлармиз», дедилар'}, {'chapter': 36, 'verse': 17, 'text': 'وَمَا عَلَيۡنَآ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nБизнинг зиммамизда очиқ-ойдин етказишдан бошқа нарса йўқ'}, {'chapter': 36, 'verse': 18, 'text': 'قَالُوٓاْ إِنَّا تَطَيَّرۡنَا بِكُمۡۖ لَئِن لَّمۡ تَنتَهُواْ لَنَرۡجُمَنَّكُمۡ وَلَيَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِيمٞ\nУлар: «Биз сизлардан бадгумон бўлдик. Агар тўхтамасангиз, сизни албатта тошбўрон қилурмиз ва сизга биздан аламли азоб етадир», дедилар'}, {'chapter': 36, 'verse': 19, 'text': 'قَالُواْ طَـٰٓئِرُكُم مَّعَكُمۡ أَئِن ذُكِّرۡتُمۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ\nУлар: «Бадгумонлигингиз ўзингиз билан. Сизга эслатма берилганигами?! Йўқ! Сиз ўзингиз исрофчи қавмсиз», дедилар'}, {'chapter': 36, 'verse': 20, 'text': 'وَجَآءَ مِنۡ أَقۡصَا ٱلۡمَدِينَةِ رَجُلٞ يَسۡعَىٰ قَالَ يَٰقَوۡمِ ٱتَّبِعُواْ ٱلۡمُرۡسَلِينَ\nШаҳар четидан бир киши шошилиб келиб: «Эй қавмим, юборилган Пайғамбарларга эргашинг'}, {'chapter': 36, 'verse': 21, 'text': 'ٱتَّبِعُواْ مَن لَّا يَسۡـَٔلُكُمۡ أَجۡرٗا وَهُم مُّهۡتَدُونَ\nСиздан ажр-ҳақ сўрамайдиган, ўзлари ҳидоятда бўлган шахсларга эргашинг'}, {'chapter': 36, 'verse': 22, 'text': 'وَمَالِيَ لَآ أَعۡبُدُ ٱلَّذِي فَطَرَنِي وَإِلَيۡهِ تُرۡجَعُونَ\nМенга нима бўлибдики, ўзимни яратган Зотга ибодат қилмайин?! Унгагина қайтарилурсизлар'}, {'chapter': 36, 'verse': 23, 'text': 'ءَأَتَّخِذُ مِن دُونِهِۦٓ ءَالِهَةً إِن يُرِدۡنِ ٱلرَّحۡمَٰنُ بِضُرّٖ لَّا تُغۡنِ عَنِّي شَفَٰعَتُهُمۡ شَيۡـٔٗا وَلَا يُنقِذُونِ\nМен Уни қўйиб, бошқа илоҳлар тутайми?! Агар Роҳман менга бирор зарарни ирода қилса, уларнинг шафоати менга ҳеч фойда бермас ва улар мени қутқара олмаслар'}, {'chapter': 36, 'verse': 24, 'text': 'إِنِّيٓ إِذٗا لَّفِي ضَلَٰلٖ مُّبِينٍ\nАлбатта, у тақдирда мен очиқ-ойдин адашувда бўлурман'}, {'chapter': 36, 'verse': 25, 'text': 'إِنِّيٓ ءَامَنتُ بِرَبِّكُمۡ فَٱسۡمَعُونِ\nАлбатта, мен Роббингизга иймон келтирдим. Бас, мени тинглангиз», деди'}, {'chapter': 36, 'verse': 26, 'text': 'قِيلَ ٱدۡخُلِ ٱلۡجَنَّةَۖ قَالَ يَٰلَيۡتَ قَوۡمِي يَعۡلَمُونَ\n«Жаннатга кир», дейилди. У: «Кошки, қавмим билсалар эди'}, {'chapter': 36, 'verse': 27, 'text': 'بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ ٱلۡمُكۡرَمِينَ\nРоббим мени мағфират этганини ва икром қилинганлардан қилганини», деди'}, {'chapter': 36, 'verse': 28, 'text': '۞وَمَآ أَنزَلۡنَا عَلَىٰ قَوۡمِهِۦ مِنۢ بَعۡدِهِۦ مِن جُندٖ مِّنَ ٱلسَّمَآءِ وَمَا كُنَّا مُنزِلِينَ\nБиз унинг қавмига ундан кейин осмондан бирор қўшин туширмадик ва туширувчи эмас ҳам эдик'}, {'chapter': 36, 'verse': 29, 'text': 'إِن كَانَتۡ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ فَإِذَا هُمۡ خَٰمِدُونَ\nФақат биргина қичқириқдан бошқа нарса бўлмади. Бирдан улар сўниб қолдилар'}, {'chapter': 36, 'verse': 30, 'text': 'يَٰحَسۡرَةً عَلَى ٱلۡعِبَادِۚ مَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nБандаларга ҳасратлар бўлсин! Ҳар қачон уларга Пайғамбар келса, уни фақат истеҳзо қилар эдилар'}, {'chapter': 36, 'verse': 31, 'text': 'أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ أَنَّهُمۡ إِلَيۡهِمۡ لَا يَرۡجِعُونَ\nАхир, улар ўзларидан олдинги қанча асрларни ҳалок этганимизни ва, албатта, ўшалар уларга қайтмасликларини кўрмайдиларми'}, {'chapter': 36, 'verse': 32, 'text': 'وَإِن كُلّٞ لَّمَّا جَمِيعٞ لَّدَيۡنَا مُحۡضَرُونَ\nАлбатта, барчалари тўпланган ҳолларида ҳузуримизга ҳозир қилингайлар'}, {'chapter': 36, 'verse': 33, 'text': 'وَءَايَةٞ لَّهُمُ ٱلۡأَرۡضُ ٱلۡمَيۡتَةُ أَحۡيَيۡنَٰهَا وَأَخۡرَجۡنَا مِنۡهَا حَبّٗا فَمِنۡهُ يَأۡكُلُونَ\nУлар учун ўлик ер оят-(белги)дир. Биз уни тирилтирдик ва ундан дон чиқардик. Бас, ундан ерлар'}, {'chapter': 36, 'verse': 34, 'text': 'وَجَعَلۡنَا فِيهَا جَنَّـٰتٖ مِّن نَّخِيلٖ وَأَعۡنَٰبٖ وَفَجَّرۡنَا فِيهَا مِنَ ٱلۡعُيُونِ\nВа унда хурмо ва узум боғлари яратдик ҳамда ичидан булоқларни чиқардик'}, {'chapter': 36, 'verse': 35, 'text': 'لِيَأۡكُلُواْ مِن ثَمَرِهِۦ وَمَا عَمِلَتۡهُ أَيۡدِيهِمۡۚ أَفَلَا يَشۡكُرُونَ\nУнинг меваларидан ва ўз қўллари қилган нарсалардан ейишлари учун. Шукр қилмайдиларми'}, {'chapter': 36, 'verse': 36, 'text': 'سُبۡحَٰنَ ٱلَّذِي خَلَقَ ٱلۡأَزۡوَٰجَ كُلَّهَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ وَمِنۡ أَنفُسِهِمۡ وَمِمَّا لَا يَعۡلَمُونَ\nЕр ўстирадиган нарсалардан, уларнинг ўзларидан ва улар билмайдиган нарсалардан, барчасидан жуфтларни яратган Зот пок бўлди'}, {'chapter': 36, 'verse': 37, 'text': 'وَءَايَةٞ لَّهُمُ ٱلَّيۡلُ نَسۡلَخُ مِنۡهُ ٱلنَّهَارَ فَإِذَا هُم مُّظۡلِمُونَ\nУлар учун кечаси ҳам оят (белги)дир. Биз уни кундуздан шилиб олурмиз. Бас, улар бирдан зулматда қолгувчилардир'}, {'chapter': 36, 'verse': 38, 'text': 'وَٱلشَّمۡسُ تَجۡرِي لِمُسۡتَقَرّٖ لَّهَاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\nҚуёш ўз истиқрори учун жараён этар. Бу азийзу ўта билгувчи Зотнинг тақдир қилганидир'}, {'chapter': 36, 'verse': 39, 'text': 'وَٱلۡقَمَرَ قَدَّرۡنَٰهُ مَنَازِلَ حَتَّىٰ عَادَ كَٱلۡعُرۡجُونِ ٱلۡقَدِيمِ\nОйни эса, токи эски (хурмо) шингили (ҳолига) қайтгунга қадар манзилларини бичиб қўйганмиз'}, {'chapter': 36, 'verse': 40, 'text': 'لَا ٱلشَّمۡسُ يَنۢبَغِي لَهَآ أَن تُدۡرِكَ ٱلۡقَمَرَ وَلَا ٱلَّيۡلُ سَابِقُ ٱلنَّهَارِۚ وَكُلّٞ فِي فَلَكٖ يَسۡبَحُونَ\nНа қуёшнинг ойга етиб олмоғи дуруст бўлар ва на кеча кундуздан ўзиб кетар. Ҳар бири фалакда сузиб юрар'}, {'chapter': 36, 'verse': 41, 'text': 'وَءَايَةٞ لَّهُمۡ أَنَّا حَمَلۡنَا ذُرِّيَّتَهُمۡ فِي ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\nУлар учун Биз уларнинг наслларини тўла кемада кўтарганимиз ҳам бир оят(белги)дир'}, {'chapter': 36, 'verse': 42, 'text': 'وَخَلَقۡنَا لَهُم مِّن مِّثۡلِهِۦ مَا يَرۡكَبُونَ\nВа Биз улар учун унга ўхшаш минадиган нарсаларни халқ қилдик'}, {'chapter': 36, 'verse': 43, 'text': 'وَإِن نَّشَأۡ نُغۡرِقۡهُمۡ فَلَا صَرِيخَ لَهُمۡ وَلَا هُمۡ يُنقَذُونَ\nАгар хоҳласак, уларни ғарқ этурмиз. Бас, уларнинг ёрдамчиси бўлмас ва улар қутқазилмаслар ҳам'}, {'chapter': 36, 'verse': 44, 'text': 'إِلَّا رَحۡمَةٗ مِّنَّا وَمَتَٰعًا إِلَىٰ حِينٖ\nМагар Биздан бир раҳмат ва маълум муддатгача баҳраманд бўлиш бўлсагина (омон қолурлар)'}, {'chapter': 36, 'verse': 45, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱتَّقُواْ مَا بَيۡنَ أَيۡدِيكُمۡ وَمَا خَلۡفَكُمۡ لَعَلَّكُمۡ تُرۡحَمُونَ\nУларга: «Олдингиздаги нарсадан ва ортингиздаги нарсадан қўрқинг. Шояд, раҳим қилинсангиз», дейилганда'}, {'chapter': 36, 'verse': 46, 'text': 'وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ\nВа уларга Роббилари оятларидан бир оят келганда, фақат ундан юз ўгиргувчи бўлурлар'}, {'chapter': 36, 'verse': 47, 'text': 'وَإِذَا قِيلَ لَهُمۡ أَنفِقُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ قَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُوٓاْ أَنُطۡعِمُ مَن لَّوۡ يَشَآءُ ٱللَّهُ أَطۡعَمَهُۥٓ إِنۡ أَنتُمۡ إِلَّا فِي ضَلَٰلٖ مُّبِينٖ\nУларга: «Аллоҳ сизга ризқ қилиб берган нарсадан инфоқ қилинглар», дейилганида, куфр келтирганлар иймон келтирганларга: «Аллоҳ хоҳласа, Ўзи таом берадиган кимсани биз таомлантирамизми?! Сиз очиқ-ойдин залолатдан бошқа нарсада эмассиз», дерлар'}, {'chapter': 36, 'verse': 48, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангиз, бу ваъда қачон бўлур?» дерлар'}, {'chapter': 36, 'verse': 49, 'text': 'مَا يَنظُرُونَ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ تَأۡخُذُهُمۡ وَهُمۡ يَخِصِّمُونَ\nУлар талашиб-тортишиб турганларида ўзларини бирдан оладиган биргина қичқириқдан бошқа нарсани кутаётганлари йўқ'}, {'chapter': 36, 'verse': 50, 'text': 'فَلَا يَسۡتَطِيعُونَ تَوۡصِيَةٗ وَلَآ إِلَىٰٓ أَهۡلِهِمۡ يَرۡجِعُونَ\nУлар на васият қилишга қодир бўларлар ва на ўз аҳлларига қайтарлар'}, {'chapter': 36, 'verse': 51, 'text': 'وَنُفِخَ فِي ٱلصُّورِ فَإِذَا هُم مِّنَ ٱلۡأَجۡدَاثِ إِلَىٰ رَبِّهِمۡ يَنسِلُونَ\nВа сурга пуфланди. Бас, улар бирдан Роббилари томон қабрлардан шошилиб чиқурлар'}, {'chapter': 36, 'verse': 52, 'text': 'قَالُواْ يَٰوَيۡلَنَا مَنۢ بَعَثَنَا مِن مَّرۡقَدِنَاۜۗ هَٰذَا مَا وَعَدَ ٱلرَّحۡمَٰنُ وَصَدَقَ ٱلۡمُرۡسَلُونَ\nУлар: «Воҳ, шўримиз қурисин! Бизни ётган жойимиздан ким қўзғатди?! Бу Роҳман ваъда қилган ва юборилган Пайғамбарлар тасдиқлаган нарса-ку?!» – дерлар'}, {'chapter': 36, 'verse': 53, 'text': 'إِن كَانَتۡ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ فَإِذَا هُمۡ جَمِيعٞ لَّدَيۡنَا مُحۡضَرُونَ\nФақат биргина қичқириқдан бошқа нарса бўлгани йўқ. Бас, улар бирдан тўпланиб, ҳузуримизда ҳозир қилингандирлар'}, {'chapter': 36, 'verse': 54, 'text': 'فَٱلۡيَوۡمَ لَا تُظۡلَمُ نَفۡسٞ شَيۡـٔٗا وَلَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\nБугунги кунда бирор жонга ҳеч бир зулм қилинмас. Қилиб ўтган амалларингиздан бошқасига жазо ҳам олмассиз'}, {'chapter': 36, 'verse': 55, 'text': 'إِنَّ أَصۡحَٰبَ ٱلۡجَنَّةِ ٱلۡيَوۡمَ فِي شُغُلٖ فَٰكِهُونَ\nАлбатта, жаннат эгалари у кунда роҳатланиш ила машғуллар'}, {'chapter': 36, 'verse': 56, 'text': 'هُمۡ وَأَزۡوَٰجُهُمۡ فِي ظِلَٰلٍ عَلَى ٱلۡأَرَآئِكِ مُتَّكِـُٔونَ\nУлар ва уларнинг жуфтлари сояларда сўриларда ёнбошлаб ётурлар'}, {'chapter': 36, 'verse': 57, 'text': 'لَهُمۡ فِيهَا فَٰكِهَةٞ وَلَهُم مَّا يَدَّعُونَ\nУлар учун у ерда мевалар бордир. Улар учун у ерда истаган нарсалари бордир'}, {'chapter': 36, 'verse': 58, 'text': 'سَلَٰمٞ قَوۡلٗا مِّن رَّبّٖ رَّحِيمٖ\nРоҳийм бўлган зот Роббдан «Салом» сўзи бордир'}, {'chapter': 36, 'verse': 59, 'text': 'وَٱمۡتَٰزُواْ ٱلۡيَوۡمَ أَيُّهَا ٱلۡمُجۡرِمُونَ\nЭй, жиноятчилар! Бугунги кунда сиз ажралингиз'}, {'chapter': 36, 'verse': 60, 'text': '۞أَلَمۡ أَعۡهَدۡ إِلَيۡكُمۡ يَٰبَنِيٓ ءَادَمَ أَن لَّا تَعۡبُدُواْ ٱلشَّيۡطَٰنَۖ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nМен сизларга: «Эй, Одам болалари, шайтонга ибодат қилманг, албатта, у сизга очиқ-ойдин душмандир'}, {'chapter': 36, 'verse': 61, 'text': 'وَأَنِ ٱعۡبُدُونِيۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nВа Менга ибодат қилинг, мана шу тўғри йўлдир», деб амр қилмаган эдимми'}, {'chapter': 36, 'verse': 62, 'text': 'وَلَقَدۡ أَضَلَّ مِنكُمۡ جِبِلّٗا كَثِيرًاۖ أَفَلَمۡ تَكُونُواْ تَعۡقِلُونَ\nБатаҳқиқ, у сизлардан кўпчилик авлодни адаштирди. Ақл юритувчи бўлмаган эдингизми'}, {'chapter': 36, 'verse': 63, 'text': 'هَٰذِهِۦ جَهَنَّمُ ٱلَّتِي كُنتُمۡ تُوعَدُونَ\nМана бу сизга ваъда қилинган жаҳаннамдир'}, {'chapter': 36, 'verse': 64, 'text': 'ٱصۡلَوۡهَا ٱلۡيَوۡمَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nКуфр келтириб ўтганингиз туфайли бугунги кунда унга киринг'}, {'chapter': 36, 'verse': 65, 'text': 'ٱلۡيَوۡمَ نَخۡتِمُ عَلَىٰٓ أَفۡوَٰهِهِمۡ وَتُكَلِّمُنَآ أَيۡدِيهِمۡ وَتَشۡهَدُ أَرۡجُلُهُم بِمَا كَانُواْ يَكۡسِبُونَ\nБугунги кунда уларнинг оғизларига муҳр урамиз. Нима касб қилганларини Бизга қўллари сўзлар ва оёқлари гувоҳлик берур'}, {'chapter': 36, 'verse': 66, 'text': 'وَلَوۡ نَشَآءُ لَطَمَسۡنَا عَلَىٰٓ أَعۡيُنِهِمۡ فَٱسۡتَبَقُواْ ٱلصِّرَٰطَ فَأَنَّىٰ يُبۡصِرُونَ\nАгар хоҳласак, кўзларини теп-текис қилиб қўюрмиз. Бас, улар йўлга шошилурлар. Аммо, қандай ҳам кўрсинлар'}, {'chapter': 36, 'verse': 67, 'text': 'وَلَوۡ نَشَآءُ لَمَسَخۡنَٰهُمۡ عَلَىٰ مَكَانَتِهِمۡ فَمَا ٱسۡتَطَٰعُواْ مُضِيّٗا وَلَا يَرۡجِعُونَ\nАгар хоҳласак, уларни жойларида қотириб қўюрмиз. Бас, олдинга юришга ҳам қодир бўлмаслар, ортга ҳам қайта олмаслар'}, {'chapter': 36, 'verse': 68, 'text': 'وَمَن نُّعَمِّرۡهُ نُنَكِّسۡهُ فِي ٱلۡخَلۡقِۚ أَفَلَا يَعۡقِلُونَ\nКимнинг умрини узоқ қилсак, уни хилқатида ночор қилиб қўямиз. Ахир, ақл ишлатиб кўрмайдиларми'}, {'chapter': 36, 'verse': 69, 'text': 'وَمَا عَلَّمۡنَٰهُ ٱلشِّعۡرَ وَمَا يَنۢبَغِي لَهُۥٓۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ وَقُرۡءَانٞ مُّبِينٞ\nБиз унга шеърни таълим берганимиз йўқ. Бу унинг учун тўғри ҳам келмас. У — зикр ва очиқ-ойдин Қуръондан ўзга нарса эмас'}, {'chapter': 36, 'verse': 70, 'text': 'لِّيُنذِرَ مَن كَانَ حَيّٗا وَيَحِقَّ ٱلۡقَوۡلُ عَلَى ٱلۡكَٰفِرِينَ\nТирик бўлган шахсларни огоҳлантириш ва кофирларга (азоб) сўзини ҳақ қилиш учундир'}, {'chapter': 36, 'verse': 71, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا خَلَقۡنَا لَهُم مِّمَّا عَمِلَتۡ أَيۡدِينَآ أَنۡعَٰمٗا فَهُمۡ لَهَا مَٰلِكُونَ\nАхир, Биз улар учун Ўз қўлларимиз-ла қилган нарсалардан чорваларни яратиб қўйганимизни кўрмайдиларми?! Бас, улар ўшаларга молик(эга)дирлар-ку'}, {'chapter': 36, 'verse': 72, 'text': 'وَذَلَّلۡنَٰهَا لَهُمۡ فَمِنۡهَا رَكُوبُهُمۡ وَمِنۡهَا يَأۡكُلُونَ\nБиз уларга ўша(ҳайвон)ларни бўйинсундириб қўйдик. Бас, улардан маркаблари бор ва улардан ерлар'}, {'chapter': 36, 'verse': 73, 'text': 'وَلَهُمۡ فِيهَا مَنَٰفِعُ وَمَشَارِبُۚ أَفَلَا يَشۡكُرُونَ\nУлар учун ўшаларда манфаатлар ва ичимликлар бор. Ахир, шукр қилмайдиларми'}, {'chapter': 36, 'verse': 74, 'text': 'وَٱتَّخَذُواْ مِن دُونِ ٱللَّهِ ءَالِهَةٗ لَّعَلَّهُمۡ يُنصَرُونَ\nУлар Аллоҳдан ўзга илоҳлар тутдилар. Гўёки, уларга ёрдам берилар эмиш'}, {'chapter': 36, 'verse': 75, 'text': 'لَا يَسۡتَطِيعُونَ نَصۡرَهُمۡ وَهُمۡ لَهُمۡ جُندٞ مُّحۡضَرُونَ\nУларнинг ёрдамига («худолари») қодир бўлмаслар. Улар эса, ўша(«худо»)лари учун ҳозир қилинган аскардирлар'}, {'chapter': 36, 'verse': 76, 'text': 'فَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّا نَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ\nУларнинг гапи сени хафа қилмасин. Албатта, Биз нимани сир тутсалар ва нимани ошкор қилсалар, билурмиз'}, {'chapter': 36, 'verse': 77, 'text': 'أَوَلَمۡ يَرَ ٱلۡإِنسَٰنُ أَنَّا خَلَقۡنَٰهُ مِن نُّطۡفَةٖ فَإِذَا هُوَ خَصِيمٞ مُّبِينٞ\nИнсон Биз уни нутфадан яратганимизни билмайдими?! Энди эса, у очиқ-ойдин хусуматчи бўлиб турибди'}, {'chapter': 36, 'verse': 78, 'text': 'وَضَرَبَ لَنَا مَثَلٗا وَنَسِيَ خَلۡقَهُۥۖ قَالَ مَن يُحۡيِ ٱلۡعِظَٰمَ وَهِيَ رَمِيمٞ\nУ Бизга мисол келтирди ва ўз яратилишини унутди. «Суякларни ким тирилтирур? Ҳолбуки, улар титилиб кетган-ку?!» – деди'}, {'chapter': 36, 'verse': 79, 'text': 'قُلۡ يُحۡيِيهَا ٱلَّذِيٓ أَنشَأَهَآ أَوَّلَ مَرَّةٖۖ وَهُوَ بِكُلِّ خَلۡقٍ عَلِيمٌ\nСен: «Уларни илк марта йўқдан бор қилган Зот тирилтиради ва у ҳар бир яратилган нарсани яхши билгувчидир», деб айт'}, {'chapter': 36, 'verse': 80, 'text': 'ٱلَّذِي جَعَلَ لَكُم مِّنَ ٱلشَّجَرِ ٱلۡأَخۡضَرِ نَارٗا فَإِذَآ أَنتُم مِّنۡهُ تُوقِدُونَ\nУ зот сизларга яшил дарахтдан олов қилиб бергандир. Энди эса, сиз ундан олов ёқмоқдасиз'}, {'chapter': 36, 'verse': 81, 'text': 'أَوَلَيۡسَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِقَٰدِرٍ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۚ بَلَىٰ وَهُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِيمُ\nОсмонлару ерни яратган Зот уларга ўхшашни яратишга қодир эмасми?! Йўқ! У ўта яратгувчи, ўта билгувчи Зотдир'}, {'chapter': 36, 'verse': 82, 'text': 'إِنَّمَآ أَمۡرُهُۥٓ إِذَآ أَرَادَ شَيۡـًٔا أَن يَقُولَ لَهُۥ كُن فَيَكُونُ\nҚачон бирон нарсани ирода қилса, Унинг иши «Бўл» демоқликдир, холос. Бас, у нарса бўлур'}, {'chapter': 36, 'verse': 83, 'text': 'فَسُبۡحَٰنَ ٱلَّذِي بِيَدِهِۦ مَلَكُوتُ كُلِّ شَيۡءٖ وَإِلَيۡهِ تُرۡجَعُونَ\nБас, барча нарсанинг том эгалиги қўлида бўлган Зот покдир. Унгагина қайтарилурсизлар'}], '37': [{'chapter': 37, 'verse': 1, 'text': 'وَٱلصَّـٰٓفَّـٰتِ صَفّٗا\nСафда тизилиб турувчилар ила қасам'}, {'chapter': 37, 'verse': 2, 'text': 'فَٱلزَّـٰجِرَٰتِ زَجۡرٗا\nЗажр (қилинадиганларни) зажр қилгувчилар ила қасам'}, {'chapter': 37, 'verse': 3, 'text': 'فَٱلتَّـٰلِيَٰتِ ذِكۡرًا\nЗикрни тиловат қилгувчилар ила қасам'}, {'chapter': 37, 'verse': 4, 'text': 'إِنَّ إِلَٰهَكُمۡ لَوَٰحِدٞ\nАлбатта, илоҳингиз ягонадир'}, {'chapter': 37, 'verse': 5, 'text': 'رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَرَبُّ ٱلۡمَشَٰرِقِ\nУ зот осмонлару ернинг ва иккиси орасидаги нарсаларнинг Роббидир. Ва машриқларнинг Роббидир'}, {'chapter': 37, 'verse': 6, 'text': 'إِنَّا زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِزِينَةٍ ٱلۡكَوَاكِبِ\nАлбатта, Биз бу дунё осмонини юлдузлар зийнати-ла зийнатладик'}, {'chapter': 37, 'verse': 7, 'text': 'وَحِفۡظٗا مِّن كُلِّ شَيۡطَٰنٖ مَّارِدٖ\nВа ҳар бир итоатсиз шайтондан сақлаш учун'}, {'chapter': 37, 'verse': 8, 'text': 'لَّا يَسَّمَّعُونَ إِلَى ٱلۡمَلَإِ ٱلۡأَعۡلَىٰ وَيُقۡذَفُونَ مِن كُلِّ جَانِبٖ\nУлар юксак тўплиққа қулоқ сола олмаслар ва ҳар томондан (учқун-ла) отилурлар'}, {'chapter': 37, 'verse': 9, 'text': 'دُحُورٗاۖ وَلَهُمۡ عَذَابٞ وَاصِبٌ\nҲайдалурлар! Улар учун узлуксиз азоб бордир'}, {'chapter': 37, 'verse': 10, 'text': 'إِلَّا مَنۡ خَطِفَ ٱلۡخَطۡفَةَ فَأَتۡبَعَهُۥ شِهَابٞ ثَاقِبٞ\nМагар бирорта сўзни ўғриликча тинглаган шахс бўлса, уни ёқувчи олов таъқиб этар'}, {'chapter': 37, 'verse': 11, 'text': 'فَٱسۡتَفۡتِهِمۡ أَهُمۡ أَشَدُّ خَلۡقًا أَم مَّنۡ خَلَقۡنَآۚ إِنَّا خَلَقۡنَٰهُم مِّن طِينٖ لَّازِبِۭ\nБас, сен улардан сўра: уларни яратиш қийинроқмикан ёки Биз яратган зотларни?! Албатта, Биз уларни ёпишқоқ лойдан яратдик'}, {'chapter': 37, 'verse': 12, 'text': 'بَلۡ عَجِبۡتَ وَيَسۡخَرُونَ\nБалки сен ажабландинг, улар эса масхара қилурлар'}, {'chapter': 37, 'verse': 13, 'text': 'وَإِذَا ذُكِّرُواْ لَا يَذۡكُرُونَ\nҚачонки эслатилсалар, эсламаслар'}, {'chapter': 37, 'verse': 14, 'text': 'وَإِذَا رَأَوۡاْ ءَايَةٗ يَسۡتَسۡخِرُونَ\nҚачонки бирор оят(белги)ни кўрсалар, қаттиқроқ масхара қилурлар'}, {'chapter': 37, 'verse': 15, 'text': 'وَقَالُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٌ\nУлар: «Бу (Қуръон) очиқ-ойдин сеҳрдан бошқа нарса эмас'}, {'chapter': 37, 'verse': 16, 'text': 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ\nЎлсак-да, тупроқ ва суяк бўлсак-да, албатта, биз қайта тирилтирилурмизми'}, {'chapter': 37, 'verse': 17, 'text': 'أَوَءَابَآؤُنَا ٱلۡأَوَّلُونَ\nАввалги ота-боболаримиз ҳам-а?!» – дедилар'}, {'chapter': 37, 'verse': 18, 'text': 'قُلۡ نَعَمۡ وَأَنتُمۡ دَٰخِرُونَ\nСен: «Ҳа! Хор бўлган ҳолларингизда!» дегин'}, {'chapter': 37, 'verse': 19, 'text': 'فَإِنَّمَا هِيَ زَجۡرَةٞ وَٰحِدَةٞ فَإِذَا هُمۡ يَنظُرُونَ\nБас, у фақатгина бир қичқириқдан иборат, холос. Бас, бирдан улар назар солиб турибдилар-да'}, {'chapter': 37, 'verse': 20, 'text': 'وَقَالُواْ يَٰوَيۡلَنَا هَٰذَا يَوۡمُ ٱلدِّينِ\nВа улар: «Воҳ, шўримиз қурисин! Бу, жазо куни-ку!» – дерлар'}, {'chapter': 37, 'verse': 21, 'text': 'هَٰذَا يَوۡمُ ٱلۡفَصۡلِ ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ\n«Бу, сизлар ёлғонга чиқариб юрган ажрим кунидир'}, {'chapter': 37, 'verse': 22, 'text': '۞ٱحۡشُرُواْ ٱلَّذِينَ ظَلَمُواْ وَأَزۡوَٰجَهُمۡ وَمَا كَانُواْ يَعۡبُدُونَ\nЗулм қилганларни, уларнинг жуфтларини ва улар ибодат қилиб юрган'}, {'chapter': 37, 'verse': 23, 'text': 'مِن دُونِ ٱللَّهِ فَٱهۡدُوهُمۡ إِلَىٰ صِرَٰطِ ٱلۡجَحِيمِ\nАллоҳдан ўзга нарсаларини тўпланглар. Бас, уларни жаҳийм йўлига ҳидоят қилинг» (дейилур)'}, {'chapter': 37, 'verse': 24, 'text': 'وَقِفُوهُمۡۖ إِنَّهُم مَّسۡـُٔولُونَ\nВа уларни тўхтатинглар! Албатта, улар сўралгувчидирлар'}, {'chapter': 37, 'verse': 25, 'text': 'مَا لَكُمۡ لَا تَنَاصَرُونَ\nСизларга нима бўлди? Ўзаро ёрдамлашмаяпсиз'}, {'chapter': 37, 'verse': 26, 'text': 'بَلۡ هُمُ ٱلۡيَوۡمَ مُسۡتَسۡلِمُونَ\nЙўқ! Улар бугунги кунда таслим бўлгувчилардир'}, {'chapter': 37, 'verse': 27, 'text': 'وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ\nУлар бир-бирларига савол берурлар'}, {'chapter': 37, 'verse': 28, 'text': 'قَالُوٓاْ إِنَّكُمۡ كُنتُمۡ تَأۡتُونَنَا عَنِ ٱلۡيَمِينِ\nУлар: «Албатта, сизлар бизга ўнг томондан келар эдингиз», дерлар'}, {'chapter': 37, 'verse': 29, 'text': 'قَالُواْ بَل لَّمۡ تَكُونُواْ مُؤۡمِنِينَ\nУлар: «Йўқ! Сиз ўзингиз мўмин бўлмаган эдингиз'}, {'chapter': 37, 'verse': 30, 'text': 'وَمَا كَانَ لَنَا عَلَيۡكُم مِّن سُلۡطَٰنِۭۖ بَلۡ كُنتُمۡ قَوۡمٗا طَٰغِينَ\nБизнинг устингиздан ҳукмронлигимиз бўлмаган эди. Йўқ! Сиз ўзингиз туғёнга кетган қавм эдингиз'}, {'chapter': 37, 'verse': 31, 'text': 'فَحَقَّ عَلَيۡنَا قَوۡلُ رَبِّنَآۖ إِنَّا لَذَآئِقُونَ\nБас, ҳаммамизга Роббимизнинг (азоб) сўзи ҳақ бўлди. Албатта, биз тотгувчилармиз'}, {'chapter': 37, 'verse': 32, 'text': 'فَأَغۡوَيۡنَٰكُمۡ إِنَّا كُنَّا غَٰوِينَ\nҲа, биз сизларни иғво қилдик. Албатта, биз иғво қилгувчи эдик», дерлар'}, {'chapter': 37, 'verse': 33, 'text': 'فَإِنَّهُمۡ يَوۡمَئِذٖ فِي ٱلۡعَذَابِ مُشۡتَرِكُونَ\nБас, улар у кунда азобда шерик бўлгувчилардир'}, {'chapter': 37, 'verse': 34, 'text': 'إِنَّا كَذَٰلِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِينَ\nАлбатта, Биз жиноятчиларга шундоқ қилурмиз'}, {'chapter': 37, 'verse': 35, 'text': 'إِنَّهُمۡ كَانُوٓاْ إِذَا قِيلَ لَهُمۡ لَآ إِلَٰهَ إِلَّا ٱللَّهُ يَسۡتَكۡبِرُونَ\nЧунки, уларга қачонки «Лаа илааҳа Иллаллоҳу», дейилса, мутакаббирлик қилар эдилар'}, {'chapter': 37, 'verse': 36, 'text': 'وَيَقُولُونَ أَئِنَّا لَتَارِكُوٓاْ ءَالِهَتِنَا لِشَاعِرٖ مَّجۡنُونِۭ\n«Ахир, биз бир жинни шоирни деб ўз худоларимизни тарк қилгувчи бўлайликми?!» – дер эдилар'}, {'chapter': 37, 'verse': 37, 'text': 'بَلۡ جَآءَ بِٱلۡحَقِّ وَصَدَّقَ ٱلۡمُرۡسَلِينَ\nЙўқ! У ҳақни келтирди ва Пайғамбарларни тасдиқ қилди'}, {'chapter': 37, 'verse': 38, 'text': 'إِنَّكُمۡ لَذَآئِقُواْ ٱلۡعَذَابِ ٱلۡأَلِيمِ\nАлбатта, сиз аламли азобни тотгувчидирсиз'}, {'chapter': 37, 'verse': 39, 'text': 'وَمَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\nҚилиб ўтган нарсангиздан бошқа нарсанинг жазосини олмассиз'}, {'chapter': 37, 'verse': 40, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг ихлосли бандаларига'}, {'chapter': 37, 'verse': 41, 'text': 'أُوْلَـٰٓئِكَ لَهُمۡ رِزۡقٞ مَّعۡلُومٞ\nАна ўшаларга, ўзларига маълум ризқ бордир'}, {'chapter': 37, 'verse': 42, 'text': 'فَوَٰكِهُ وَهُم مُّكۡرَمُونَ\nМева-чевалар бордир. Улар икром қилинган ҳолларида'}, {'chapter': 37, 'verse': 43, 'text': 'فِي جَنَّـٰتِ ٱلنَّعِيمِ\nНаъийм жаннатларида'}, {'chapter': 37, 'verse': 44, 'text': 'عَلَىٰ سُرُرٖ مُّتَقَٰبِلِينَ\nСўрилар устида бир-бирларига қараган ҳолларида бўлурлар'}, {'chapter': 37, 'verse': 45, 'text': 'يُطَافُ عَلَيۡهِم بِكَأۡسٖ مِّن مَّعِينِۭ\nУларга оқар чашмадан қадаҳлар айлантириб турилур'}, {'chapter': 37, 'verse': 46, 'text': 'بَيۡضَآءَ لَذَّةٖ لِّلشَّـٰرِبِينَ\nУ оппоқ, ичгувчига лаззатбахшдир'}, {'chapter': 37, 'verse': 47, 'text': 'لَا فِيهَا غَوۡلٞ وَلَا هُمۡ عَنۡهَا يُنزَفُونَ\nУнда ҳеч бир зарар йўқдир ва ундан сархуш ҳам бўлмаслар'}, {'chapter': 37, 'verse': 48, 'text': 'وَعِندَهُمۡ قَٰصِرَٰتُ ٱلطَّرۡفِ عِينٞ\nОлдиларида кўзларини (бегоналардан) тийгувчи шаҳло кўзлилар бордир'}, {'chapter': 37, 'verse': 49, 'text': 'كَأَنَّهُنَّ بَيۡضٞ مَّكۡنُونٞ\nУлар худди асраб қўйилган тухумга ўхшарлар'}, {'chapter': 37, 'verse': 50, 'text': 'فَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ\nБас, улар бир-бирларидан сўрай бошларлар'}, {'chapter': 37, 'verse': 51, 'text': 'قَالَ قَآئِلٞ مِّنۡهُمۡ إِنِّي كَانَ لِي قَرِينٞ\nИчларидан бир сўзловчи: «Аниқки, менинг бир дўстим бор эди'}, {'chapter': 37, 'verse': 52, 'text': 'يَقُولُ أَءِنَّكَ لَمِنَ ٱلۡمُصَدِّقِينَ\nУ: «Сен ҳам тасдиқловчиларданмисан'}, {'chapter': 37, 'verse': 53, 'text': 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَدِينُونَ\nЎлиб, тупроқ ва суяклар бўлиб кетсак ҳам, албатта, жазоланадиганлардан бўлурмизми?!» – дер эди'}, {'chapter': 37, 'verse': 54, 'text': 'قَالَ هَلۡ أَنتُم مُّطَّلِعُونَ\nСизлар ҳам қараяпсизларми?» – деди'}, {'chapter': 37, 'verse': 55, 'text': 'فَٱطَّلَعَ فَرَءَاهُ فِي سَوَآءِ ٱلۡجَحِيمِ\nБас, ўзи қараб, уни дўзахнинг ўртасида кўрди'}, {'chapter': 37, 'verse': 56, 'text': 'قَالَ تَٱللَّهِ إِن كِدتَّ لَتُرۡدِينِ\nУ: «Аллоҳга қасамки, мени ҳам ҳалок қилишингга оз қолган экан'}, {'chapter': 37, 'verse': 57, 'text': 'وَلَوۡلَا نِعۡمَةُ رَبِّي لَكُنتُ مِنَ ٱلۡمُحۡضَرِينَ\nАгар Роббимнинг неъмати бўлмаганида, мен ҳам (дўзахга) ҳозир қилинганлардан бўлур эдим», деди'}, {'chapter': 37, 'verse': 58, 'text': 'أَفَمَا نَحۡنُ بِمَيِّتِينَ\n«Биз ўлгувчи эмас эканмизми'}, {'chapter': 37, 'verse': 59, 'text': 'إِلَّا مَوۡتَتَنَا ٱلۡأُولَىٰ وَمَا نَحۡنُ بِمُعَذَّبِينَ\nФақат биринчи ўлимимиздан бошқа?! Биз азоблангувчи эмас эканмизми'}, {'chapter': 37, 'verse': 60, 'text': 'إِنَّ هَٰذَا لَهُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАлбатта, бу айни улуғ ютуқдир'}, {'chapter': 37, 'verse': 61, 'text': 'لِمِثۡلِ هَٰذَا فَلۡيَعۡمَلِ ٱلۡعَٰمِلُونَ\nБас, амал қилгувчилар мана шунга ўхшаш нарса учун амал этсинлар»'}, {'chapter': 37, 'verse': 62, 'text': 'أَذَٰلِكَ خَيۡرٞ نُّزُلًا أَمۡ شَجَرَةُ ٱلزَّقُّومِ\nМанзил ўлароқ ана ўша яхшими ёки заққум дарахтими'}, {'chapter': 37, 'verse': 63, 'text': 'إِنَّا جَعَلۡنَٰهَا فِتۡنَةٗ لِّلظَّـٰلِمِينَ\nАлбатта, Биз уни золимлар учун фитна қилдик'}, {'chapter': 37, 'verse': 64, 'text': 'إِنَّهَا شَجَرَةٞ تَخۡرُجُ فِيٓ أَصۡلِ ٱلۡجَحِيمِ\nДарҳақиқат, у бир дарахт бўлиб, дўзах қаъридан чиқур'}, {'chapter': 37, 'verse': 65, 'text': 'طَلۡعُهَا كَأَنَّهُۥ رُءُوسُ ٱلشَّيَٰطِينِ\nУнинг (мева) тугар жойи худди шайтонларнинг бошларига ўхшар'}, {'chapter': 37, 'verse': 66, 'text': 'فَإِنَّهُمۡ لَأٓكِلُونَ مِنۡهَا فَمَالِـُٔونَ مِنۡهَا ٱلۡبُطُونَ\nБас, албатта, улар ундан егувчилардир. Ундан қоринларни тўлдиргувчилардир'}, {'chapter': 37, 'verse': 67, 'text': 'ثُمَّ إِنَّ لَهُمۡ عَلَيۡهَا لَشَوۡبٗا مِّنۡ حَمِيمٖ\nСўнгра, бунинг устига, улар учун ўта қайноқ сувдан аралаштирма ҳам бор'}, {'chapter': 37, 'verse': 68, 'text': 'ثُمَّ إِنَّ مَرۡجِعَهُمۡ لَإِلَى ٱلۡجَحِيمِ\nСўнгра, албатта, уларнинг қайтар жойи жаҳаннамга бўлур'}, {'chapter': 37, 'verse': 69, 'text': 'إِنَّهُمۡ أَلۡفَوۡاْ ءَابَآءَهُمۡ ضَآلِّينَ\nЧунки, улар ўз оталарини адашган ҳолда топдилар'}, {'chapter': 37, 'verse': 70, 'text': 'فَهُمۡ عَلَىٰٓ ءَاثَٰرِهِمۡ يُهۡرَعُونَ\nЭнди эса, улар ўшаларнинг изидан чопмоқдалар'}, {'chapter': 37, 'verse': 71, 'text': 'وَلَقَدۡ ضَلَّ قَبۡلَهُمۡ أَكۡثَرُ ٱلۡأَوَّلِينَ\nБатаҳқиқ, улардан олдин аввалгиларнинг кўплари адашганлар'}, {'chapter': 37, 'verse': 72, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا فِيهِم مُّنذِرِينَ\nБатаҳқиқ, Биз ичларига огоҳлантиргувчилар юбордик'}, {'chapter': 37, 'verse': 73, 'text': 'فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُنذَرِينَ\nБас, огоҳлантирилганларнинг оқибати қандоқ бўлганига назар сол'}, {'chapter': 37, 'verse': 74, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг халос қилган бандаларигина (ҳалокатга учрамадилар)'}, {'chapter': 37, 'verse': 75, 'text': 'وَلَقَدۡ نَادَىٰنَا نُوحٞ فَلَنِعۡمَ ٱلۡمُجِيبُونَ\nБатаҳқиқ, Бизга Нуҳ нидо қилди. Бас, нақадар яхши ижобат қилгувчимиз'}, {'chapter': 37, 'verse': 76, 'text': 'وَنَجَّيۡنَٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ\nБиз унга ва унинг аҳлига улуғ ғамдан нажот бердик'}, {'chapter': 37, 'verse': 77, 'text': 'وَجَعَلۡنَا ذُرِّيَّتَهُۥ هُمُ ٱلۡبَاقِينَ\nВа унинг зурриётларини, ўзларини боқий қолгувчилар қилдик'}, {'chapter': 37, 'verse': 78, 'text': 'وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ\nВа кейингилар ичида унинг учун (олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 79, 'text': 'سَلَٰمٌ عَلَىٰ نُوحٖ فِي ٱلۡعَٰلَمِينَ\nБарча оламларда Нуҳга салом бўлсин'}, {'chapter': 37, 'verse': 80, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nАлбатта, Биз гўзал амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 81, 'text': 'إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nАлбатта, у мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 82, 'text': 'ثُمَّ أَغۡرَقۡنَا ٱلۡأٓخَرِينَ\nСўнгра, қолганларни ғарқ қилдик'}, {'chapter': 37, 'verse': 83, 'text': '۞وَإِنَّ مِن شِيعَتِهِۦ لَإِبۡرَٰهِيمَ\nАлбатта, Иброҳим ҳам унинг гуруҳидандир'}, {'chapter': 37, 'verse': 84, 'text': 'إِذۡ جَآءَ رَبَّهُۥ بِقَلۡبٖ سَلِيمٍ\nЎшанда у ўз Роббига соғлом қалб ила келмишди'}, {'chapter': 37, 'verse': 85, 'text': 'إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَاذَا تَعۡبُدُونَ\nЎшанда отасига ва қавмига: «Нимага ибодат қилмоқдасиз'}, {'chapter': 37, 'verse': 86, 'text': 'أَئِفۡكًا ءَالِهَةٗ دُونَ ٱللَّهِ تُرِيدُونَ\nАллоҳдан ўзга уйдирма илоҳларни хоҳлайсизларми'}, {'chapter': 37, 'verse': 87, 'text': 'فَمَا ظَنُّكُم بِرَبِّ ٱلۡعَٰلَمِينَ\nОламларнинг Робби ҳақида нима гумонингиз бор?!» – деган эди'}, {'chapter': 37, 'verse': 88, 'text': 'فَنَظَرَ نَظۡرَةٗ فِي ٱلنُّجُومِ\nБас, У юлдузларга бир назар солди'}, {'chapter': 37, 'verse': 89, 'text': 'فَقَالَ إِنِّي سَقِيمٞ\nВа: «Албатта, мен беморман», деди'}, {'chapter': 37, 'verse': 90, 'text': 'فَتَوَلَّوۡاْ عَنۡهُ مُدۡبِرِينَ\nДарҳол, ундан юз ўгириб юриб кетдилар'}, {'chapter': 37, 'verse': 91, 'text': 'فَرَاغَ إِلَىٰٓ ءَالِهَتِهِمۡ فَقَالَ أَلَا تَأۡكُلُونَ\nБас, тезда уларнинг худоларига бурилди ва: «Қани, емайсизларми'}, {'chapter': 37, 'verse': 92, 'text': 'مَا لَكُمۡ لَا تَنطِقُونَ\nСизга нима бўлди?! Гапирмайсизлар?!» – деди'}, {'chapter': 37, 'verse': 93, 'text': 'فَرَاغَ عَلَيۡهِمۡ ضَرۡبَۢا بِٱلۡيَمِينِ\nСўнг уларга яқинлашиб, ўнг қўл билан ура бошлади'}, {'chapter': 37, 'verse': 94, 'text': 'فَأَقۡبَلُوٓاْ إِلَيۡهِ يَزِفُّونَ\nУ томон шошиб, бир-бирларини туртиб келишди'}, {'chapter': 37, 'verse': 95, 'text': 'قَالَ أَتَعۡبُدُونَ مَا تَنۡحِتُونَ\nУ: «Ўзингиз йўниб ясаган нарсага ибодат қиласизларми?!» – деди'}, {'chapter': 37, 'verse': 96, 'text': 'وَٱللَّهُ خَلَقَكُمۡ وَمَا تَعۡمَلُونَ\nҲолбуки, сизни ҳам, қилган нарсаларингизни ҳам Аллоҳ яратган-ку'}, {'chapter': 37, 'verse': 97, 'text': 'قَالُواْ ٱبۡنُواْ لَهُۥ بُنۡيَٰنٗا فَأَلۡقُوهُ فِي ٱلۡجَحِيمِ\nУлар: «Унинг учун (ўчоқ) бино қилинглар, бас, уни гулханга отинглар!» дедилар'}, {'chapter': 37, 'verse': 98, 'text': 'فَأَرَادُواْ بِهِۦ كَيۡدٗا فَجَعَلۡنَٰهُمُ ٱلۡأَسۡفَلِينَ\nБас, улар унга макр-ҳийлани ирода қилишди. Биз уларни тубан қилдик'}, {'chapter': 37, 'verse': 99, 'text': 'وَقَالَ إِنِّي ذَاهِبٌ إِلَىٰ رَبِّي سَيَهۡدِينِ\nУ: «Албатта, мен Роббимга боргувчиман. У мени ҳидоят қилур'}, {'chapter': 37, 'verse': 100, 'text': 'رَبِّ هَبۡ لِي مِنَ ٱلصَّـٰلِحِينَ\nРоббим, менга солиҳлардан ато эт», деди'}, {'chapter': 37, 'verse': 101, 'text': 'فَبَشَّرۡنَٰهُ بِغُلَٰمٍ حَلِيمٖ\nБас, Биз унга бир ҳалим ўғилнинг хушхабарини бердик'}, {'chapter': 37, 'verse': 102, 'text': 'فَلَمَّا بَلَغَ مَعَهُ ٱلسَّعۡيَ قَالَ يَٰبُنَيَّ إِنِّيٓ أَرَىٰ فِي ٱلۡمَنَامِ أَنِّيٓ أَذۡبَحُكَ فَٱنظُرۡ مَاذَا تَرَىٰۚ قَالَ يَـٰٓأَبَتِ ٱفۡعَلۡ مَا تُؤۡمَرُۖ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰبِرِينَ\nБас, қачонки (бола) унинг ўзи билан бирга юрадиган бўлганида: «Эй ўғилчам, мен тушимда сени сўяётганимни кўрмоқдаман, боқиб кўр, нима дерсан?» – деди. У: «Эй отажон, сенга амр этилганни бажар, иншоаллоҳ, мени сабр қилгувчилардан топурсан», деди'}, {'chapter': 37, 'verse': 103, 'text': 'فَلَمَّآ أَسۡلَمَا وَتَلَّهُۥ لِلۡجَبِينِ\nИкковлари таслим бўлиб, уни пешонасича ётқизганида'}, {'chapter': 37, 'verse': 104, 'text': 'وَنَٰدَيۡنَٰهُ أَن يَـٰٓإِبۡرَٰهِيمُ\nБиз унга нидо қилдик: «Эй Иброҳим'}, {'chapter': 37, 'verse': 105, 'text': 'قَدۡ صَدَّقۡتَ ٱلرُّءۡيَآۚ إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nБатаҳқиқ, сен тушни тасдиқ қилдинг! Албатта, Биз гўзал иш қилгувчиларни мана шундай мукофотлаймиз»'}, {'chapter': 37, 'verse': 106, 'text': 'إِنَّ هَٰذَا لَهُوَ ٱلۡبَلَـٰٓؤُاْ ٱلۡمُبِينُ\nАлбатта, бу очиқ-ойдин синовнинг айни ўзидир'}, {'chapter': 37, 'verse': 107, 'text': 'وَفَدَيۡنَٰهُ بِذِبۡحٍ عَظِيمٖ\nБиз унинг ўрнига катта қурбонлик фидо қилдик'}, {'chapter': 37, 'verse': 108, 'text': 'وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ\nВа кейингилар ичида унинг учун (олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 109, 'text': 'سَلَٰمٌ عَلَىٰٓ إِبۡرَٰهِيمَ\nИброҳимга салом бўлсин'}, {'chapter': 37, 'verse': 110, 'text': 'كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nБиз гўзал амал қилгувчиларни шундай мукофотлармиз'}, {'chapter': 37, 'verse': 111, 'text': 'إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nЧунки, у мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 112, 'text': 'وَبَشَّرۡنَٰهُ بِإِسۡحَٰقَ نَبِيّٗا مِّنَ ٱلصَّـٰلِحِينَ\nЯна, Биз унга солиҳлардан бир Пайғамбар бўлажак Исҳоқнинг хушхабарини бердик'}, {'chapter': 37, 'verse': 113, 'text': 'وَبَٰرَكۡنَا عَلَيۡهِ وَعَلَىٰٓ إِسۡحَٰقَۚ وَمِن ذُرِّيَّتِهِمَا مُحۡسِنٞ وَظَالِمٞ لِّنَفۡسِهِۦ مُبِينٞ\nВа унга ҳам, Исҳоққа ҳам барака бердик. Икковларининг зурриётидан гўзал амал қилгувчи ҳам, ўзига очиқ-ойдин зулм қилгувчи ҳам бўлур'}, {'chapter': 37, 'verse': 114, 'text': 'وَلَقَدۡ مَنَنَّا عَلَىٰ مُوسَىٰ وَهَٰرُونَ\nБатаҳқиқ, Биз Мусо ва Ҳорунга неъмат бердик'}, {'chapter': 37, 'verse': 115, 'text': 'وَنَجَّيۡنَٰهُمَا وَقَوۡمَهُمَا مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ\nИкковларига ва қавмларига катта ғамдан нажот бердик'}, {'chapter': 37, 'verse': 116, 'text': 'وَنَصَرۡنَٰهُمۡ فَكَانُواْ هُمُ ٱلۡغَٰلِبِينَ\nВа уларга нусрат бердик, бас, улар ғолиб бўлдилар'}, {'chapter': 37, 'verse': 117, 'text': 'وَءَاتَيۡنَٰهُمَا ٱلۡكِتَٰبَ ٱلۡمُسۡتَبِينَ\nВа Биз икковларига равшану комил баёнли китобни бердик'}, {'chapter': 37, 'verse': 118, 'text': 'وَهَدَيۡنَٰهُمَا ٱلصِّرَٰطَ ٱلۡمُسۡتَقِيمَ\nВа икковларини сиротул мустақиймга ҳидоят қилдик'}, {'chapter': 37, 'verse': 119, 'text': 'وَتَرَكۡنَا عَلَيۡهِمَا فِي ٱلۡأٓخِرِينَ\nВа кейингилар ичида икковлари учун (олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 120, 'text': 'سَلَٰمٌ عَلَىٰ مُوسَىٰ وَهَٰرُونَ\nМусо ва Ҳорунга салом бўлсин'}, {'chapter': 37, 'verse': 121, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nБиз гўзал амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 122, 'text': 'إِنَّهُمَا مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nЧунки, икковлари мўмин бандаларимиздандирлар'}, {'chapter': 37, 'verse': 123, 'text': 'وَإِنَّ إِلۡيَاسَ لَمِنَ ٱلۡمُرۡسَلِينَ\nАлбатта, Илёс ҳам юборилган Пайғамбарлардандир'}, {'chapter': 37, 'verse': 124, 'text': 'إِذۡ قَالَ لِقَوۡمِهِۦٓ أَلَا تَتَّقُونَ\nЎшанда у қавмига: «Тақво қилмайсизми'}, {'chapter': 37, 'verse': 125, 'text': 'أَتَدۡعُونَ بَعۡلٗا وَتَذَرُونَ أَحۡسَنَ ٱلۡخَٰلِقِينَ\nБаълга ибодат қилиб, яратувчиларнинг энг яхшисини'}, {'chapter': 37, 'verse': 126, 'text': 'ٱللَّهَ رَبَّكُمۡ وَرَبَّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ\nЎз Роббингизни ва аввалги ота-боболарингизнинг Робби Аллоҳни тарк этасизми?!» – деди'}, {'chapter': 37, 'verse': 127, 'text': 'فَكَذَّبُوهُ فَإِنَّهُمۡ لَمُحۡضَرُونَ\nБас, уни ёлғончи қилдилар. Энди, албатта, улар (азобга) ҳозир қилингувчилардир'}, {'chapter': 37, 'verse': 128, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг мухлис бандаларигина (ҳозир қилинмаслар)'}, {'chapter': 37, 'verse': 129, 'text': 'وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ\nВа кейингилар ичида унинг учун (олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 130, 'text': 'سَلَٰمٌ عَلَىٰٓ إِلۡ يَاسِينَ\nИлёсийнга салом бўлсин'}, {'chapter': 37, 'verse': 131, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nБиз гўзал амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 132, 'text': 'إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nЧунки, у мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 133, 'text': 'وَإِنَّ لُوطٗا لَّمِنَ ٱلۡمُرۡسَلِينَ\nАлбатта, Лут ҳам юборилган Пайғамбарлардандир'}, {'chapter': 37, 'verse': 134, 'text': 'إِذۡ نَجَّيۡنَٰهُ وَأَهۡلَهُۥٓ أَجۡمَعِينَ\nЎшанда Биз унга ва унинг аҳлига, ҳаммаларига нажот бердик'}, {'chapter': 37, 'verse': 135, 'text': 'إِلَّا عَجُوزٗا فِي ٱلۡغَٰبِرِينَ\nМагар бир кампиргина (азобда) қолгувчилардан бўлди'}, {'chapter': 37, 'verse': 136, 'text': 'ثُمَّ دَمَّرۡنَا ٱلۡأٓخَرِينَ\nСўнгра қолганларини ҳалок этдик'}, {'chapter': 37, 'verse': 137, 'text': 'وَإِنَّكُمۡ لَتَمُرُّونَ عَلَيۡهِم مُّصۡبِحِينَ\nАлбатта, сиз кечаю кундуз уларнинг устидан ўтиб турурсиз. Ақл юритмайсизларми'}, {'chapter': 37, 'verse': 138, 'text': 'وَبِٱلَّيۡلِۚ أَفَلَا تَعۡقِلُونَ\nАлбатта, сиз кечаю кундуз уларнинг устидан ўтиб турурсиз. Ақл юритмайсизларми'}, {'chapter': 37, 'verse': 139, 'text': 'وَإِنَّ يُونُسَ لَمِنَ ٱلۡمُرۡسَلِينَ\nАлбатта, Юнус ҳам юборилган Пайғамбарлардандир'}, {'chapter': 37, 'verse': 140, 'text': 'إِذۡ أَبَقَ إِلَى ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\nЎшанда у тўлган кемага қочди'}, {'chapter': 37, 'verse': 141, 'text': 'فَسَاهَمَ فَكَانَ مِنَ ٱلۡمُدۡحَضِينَ\nБас, қуръа ташлашди ва у мағлублардан бўлди'}, {'chapter': 37, 'verse': 142, 'text': 'فَٱلۡتَقَمَهُ ٱلۡحُوتُ وَهُوَ مُلِيمٞ\nБас, уни маломатга лойиқ бўлган ҳолида наҳанг балиқ ютди'}, {'chapter': 37, 'verse': 143, 'text': 'فَلَوۡلَآ أَنَّهُۥ كَانَ مِنَ ٱلۡمُسَبِّحِينَ\nАгар у кўп тасбиҳ айтгувчилардан бўлмаганида эди'}, {'chapter': 37, 'verse': 144, 'text': 'لَلَبِثَ فِي بَطۡنِهِۦٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nУнинг қорнида қайта тирилтирадиган кунгача қолиб кетар эди'}, {'chapter': 37, 'verse': 145, 'text': '۞فَنَبَذۡنَٰهُ بِٱلۡعَرَآءِ وَهُوَ سَقِيمٞ\nБас, Биз уни бемор ҳолида майдонга отдик'}, {'chapter': 37, 'verse': 146, 'text': 'وَأَنۢبَتۡنَا عَلَيۡهِ شَجَرَةٗ مِّن يَقۡطِينٖ\nВа унинг устига қовоқ дарахтини ўстириб қўйдик'}, {'chapter': 37, 'verse': 147, 'text': 'وَأَرۡسَلۡنَٰهُ إِلَىٰ مِاْئَةِ أَلۡفٍ أَوۡ يَزِيدُونَ\nВа уни юз минг ёки ундан кўпроққа Пайғамбар қилиб юбордик'}, {'chapter': 37, 'verse': 148, 'text': 'فَـَٔامَنُواْ فَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ\nБас, иймон келтирдилар. Кейин Биз уларни маълум муддат баҳраманд қилдик'}, {'chapter': 37, 'verse': 149, 'text': 'فَٱسۡتَفۡتِهِمۡ أَلِرَبِّكَ ٱلۡبَنَاتُ وَلَهُمُ ٱلۡبَنُونَ\nБас, улардан сўра-чи, Роббингга қизлар-у, уларга ўғиллар эканми'}, {'chapter': 37, 'verse': 150, 'text': 'أَمۡ خَلَقۡنَا ٱلۡمَلَـٰٓئِكَةَ إِنَٰثٗا وَهُمۡ شَٰهِدُونَ\nЁки Биз фаришталарни қиз қилиб яратганмиз-у, улар гувоҳ бўлиб турганларми'}, {'chapter': 37, 'verse': 151, 'text': 'أَلَآ إِنَّهُم مِّنۡ إِفۡكِهِمۡ لَيَقُولُونَ\nОгоҳ бўлингким! Улар ўз уйдирмаларида дерлар'}, {'chapter': 37, 'verse': 152, 'text': 'وَلَدَ ٱللَّهُ وَإِنَّهُمۡ لَكَٰذِبُونَ\n«Аллоҳ туғди». Албатта, улар ёлғончилардир'}, {'chapter': 37, 'verse': 153, 'text': 'أَصۡطَفَى ٱلۡبَنَاتِ عَلَى ٱلۡبَنِينَ\nУ зот ўғилларни қўйиб, қизларни танлаб олибдими'}, {'chapter': 37, 'verse': 154, 'text': 'مَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ\nСизга нима бўлди?! Қандай ҳукм чиқармоқдасиз'}, {'chapter': 37, 'verse': 155, 'text': 'أَفَلَا تَذَكَّرُونَ\nАхир, эсламайсизларми'}, {'chapter': 37, 'verse': 156, 'text': 'أَمۡ لَكُمۡ سُلۡطَٰنٞ مُّبِينٞ\nЁки сизда очиқ-ойдин ҳужжат борми'}, {'chapter': 37, 'verse': 157, 'text': 'فَأۡتُواْ بِكِتَٰبِكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nАгар ростгўйлардан бўлсангиз, китобингизни келтиринг'}, {'chapter': 37, 'verse': 158, 'text': 'وَجَعَلُواْ بَيۡنَهُۥ وَبَيۡنَ ٱلۡجِنَّةِ نَسَبٗاۚ وَلَقَدۡ عَلِمَتِ ٱلۡجِنَّةُ إِنَّهُمۡ لَمُحۡضَرُونَ\nВа улар У билан жинлар орасида насаб (даъвоси) қилдилар. Ҳолбуки, жинлар ўзларининг албатта (азобга) ҳозир қилингувчиликларини билганлар'}, {'chapter': 37, 'verse': 159, 'text': 'سُبۡحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ\nАллоҳ улар васф қилган нарсадан покдир'}, {'chapter': 37, 'verse': 160, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nФақат Аллоҳнинг ихлосли бандаларигина (мустаснодир)'}, {'chapter': 37, 'verse': 161, 'text': 'فَإِنَّكُمۡ وَمَا تَعۡبُدُونَ\nАлбатта, сиз ҳам, сиз ибодат қилаётган нарсалар ҳам'}, {'chapter': 37, 'verse': 162, 'text': 'مَآ أَنتُمۡ عَلَيۡهِ بِفَٰتِنِينَ\nУ зотга қарши фитнага солгувчи бўла олмассиз'}, {'chapter': 37, 'verse': 163, 'text': 'إِلَّا مَنۡ هُوَ صَالِ ٱلۡجَحِيمِ\nИлло, ким жаҳаннамга киргувчи бўлса'}, {'chapter': 37, 'verse': 164, 'text': 'وَمَامِنَّآ إِلَّا لَهُۥ مَقَامٞ مَّعۡلُومٞ\nБиздан ҳар биримизнинг маълум мақомимиз бордир'}, {'chapter': 37, 'verse': 165, 'text': 'وَإِنَّا لَنَحۡنُ ٱلصَّآفُّونَ\nАлбатта, бизлар саф тортиб тургувчилармиз'}, {'chapter': 37, 'verse': 166, 'text': 'وَإِنَّا لَنَحۡنُ ٱلۡمُسَبِّحُونَ\nВа, албатта, бизлар тасбиҳ айтгувчилармиз'}, {'chapter': 37, 'verse': 167, 'text': 'وَإِن كَانُواْ لَيَقُولُونَ\nҲолбуки, улар дер эдилар'}, {'chapter': 37, 'verse': 168, 'text': 'لَوۡ أَنَّ عِندَنَا ذِكۡرٗا مِّنَ ٱلۡأَوَّلِينَ\n«Агар бизнинг ҳузуримизда ҳам аввалгилардан бир эслатма бўлганида'}, {'chapter': 37, 'verse': 169, 'text': 'لَكُنَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nАлбатта, Аллоҳнинг ихлосли бандаларидан бўлар эдик»'}, {'chapter': 37, 'verse': 170, 'text': 'فَكَفَرُواْ بِهِۦۖ فَسَوۡفَ يَعۡلَمُونَ\nБас, унга куфр келтирдилар. Энди яқинда билурлар'}, {'chapter': 37, 'verse': 171, 'text': 'وَلَقَدۡ سَبَقَتۡ كَلِمَتُنَا لِعِبَادِنَا ٱلۡمُرۡسَلِينَ\nБатаҳқиқ, Пайғамбар этиб юборилган бандаларимизга сўзимиз ўтгандир'}, {'chapter': 37, 'verse': 172, 'text': 'إِنَّهُمۡ لَهُمُ ٱلۡمَنصُورُونَ\nАлбатта, улар, ҳа, уларгина нусрат топишлари'}, {'chapter': 37, 'verse': 173, 'text': 'وَإِنَّ جُندَنَا لَهُمُ ٱلۡغَٰلِبُونَ\nВа, албатта, Бизнинг лашкаримизгина ғолиб бўлиши ҳақида'}, {'chapter': 37, 'verse': 174, 'text': 'فَتَوَلَّ عَنۡهُمۡ حَتَّىٰ حِينٖ\nБас, сен улардан бир вақтгача юз ўгир'}, {'chapter': 37, 'verse': 175, 'text': 'وَأَبۡصِرۡهُمۡ فَسَوۡفَ يُبۡصِرُونَ\nВа уларнинг (томошасини) кўр! Бас, тезда ўзлари ҳам кўражаклар'}, {'chapter': 37, 'verse': 176, 'text': 'أَفَبِعَذَابِنَا يَسۡتَعۡجِلُونَ\nҲали улар Бизнинг азобимизни шошилтирмоқдамилар'}, {'chapter': 37, 'verse': 177, 'text': 'فَإِذَا نَزَلَ بِسَاحَتِهِمۡ فَسَآءَ صَبَاحُ ٱلۡمُنذَرِينَ\nҚачонки, у уларнинг ҳовлисига тушганида, огоҳлантирилганларнинг тонги жуда ёмон бўлур'}, {'chapter': 37, 'verse': 178, 'text': 'وَتَوَلَّ عَنۡهُمۡ حَتَّىٰ حِينٖ\nБас, сен улардан бир вақтгача юз ўгир'}, {'chapter': 37, 'verse': 179, 'text': 'وَأَبۡصِرۡ فَسَوۡفَ يُبۡصِرُونَ\nВа (уларнинг томошасини) кўр! Бас, тезда ўзлари ҳам кўражаклар'}, {'chapter': 37, 'verse': 180, 'text': 'سُبۡحَٰنَ رَبِّكَ رَبِّ ٱلۡعِزَّةِ عَمَّا يَصِفُونَ\nСенинг Роббинг, иззат Робби улар қилган васфдан покдир'}, {'chapter': 37, 'verse': 181, 'text': 'وَسَلَٰمٌ عَلَى ٱلۡمُرۡسَلِينَ\nВа юборилган Пайғамбарларга салом бўлсин'}, {'chapter': 37, 'verse': 182, 'text': 'وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nОламларнинг Робби Аллоҳга ҳамдлар бўлсин'}], '38': [{'chapter': 38, 'verse': 1, 'text': 'صٓۚ وَٱلۡقُرۡءَانِ ذِي ٱلذِّكۡرِ\nСод. Зикр соҳиби бўлмиш Қуръон ила қасам'}, {'chapter': 38, 'verse': 2, 'text': 'بَلِ ٱلَّذِينَ كَفَرُواْ فِي عِزَّةٖ وَشِقَاقٖ\nАммо куфр келтирганлар кибру ҳаво ва мухолифликдадирлар'}, {'chapter': 38, 'verse': 3, 'text': 'كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ فَنَادَواْ وَّلَاتَ حِينَ مَنَاصٖ\nБиз улардан илгари ҳам қанча асрларни ҳалок этганмиз. Улар (ёрдам сўраб) нидо қилдилар. Аммо қочиб қутулиш вақти эмас эди'}, {'chapter': 38, 'verse': 4, 'text': 'وَعَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡۖ وَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا سَٰحِرٞ كَذَّابٌ\nУлар ичларига ўзларидан огоҳлантиргувчи келганидан ажабландилар ва кофирлар: «Бу, сеҳргар ва каззобдир», дедилар'}, {'chapter': 38, 'verse': 5, 'text': 'أَجَعَلَ ٱلۡأٓلِهَةَ إِلَٰهٗا وَٰحِدًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عُجَابٞ\n«У худоларни битта худо қилдими?! Албатта, бу ажабланарли нарса!»'}, {'chapter': 38, 'verse': 6, 'text': 'وَٱنطَلَقَ ٱلۡمَلَأُ مِنۡهُمۡ أَنِ ٱمۡشُواْ وَٱصۡبِرُواْ عَلَىٰٓ ءَالِهَتِكُمۡۖ إِنَّ هَٰذَا لَشَيۡءٞ يُرَادُ\nУларнинг аъёнлари: «Юринглар, худоларингиз тўғрисида сабр қилинглар. Албатта, бу қасдан қилинаётган нарсадир'}, {'chapter': 38, 'verse': 7, 'text': 'مَا سَمِعۡنَا بِهَٰذَا فِي ٱلۡمِلَّةِ ٱلۡأٓخِرَةِ إِنۡ هَٰذَآ إِلَّا ٱخۡتِلَٰقٌ\nБиз бу ҳақда охирги миллатдан эшитганимиз йўқ. Бу, тўқилгандан бошқа нарса эмас'}, {'chapter': 38, 'verse': 8, 'text': 'أَءُنزِلَ عَلَيۡهِ ٱلذِّكۡرُ مِنۢ بَيۡنِنَاۚ بَلۡ هُمۡ فِي شَكّٖ مِّن ذِكۡرِيۚ بَل لَّمَّا يَذُوقُواْ عَذَابِ\nБизнинг ичимиздан ўшанга зикр нозил қилинибдими?!» – деб туриб кетдилар. Йўқ! Улар Менинг Зикрим ҳақида шак-шубҳададирлар. Йўқ! Улар азобимни татиб кўрмадилар'}, {'chapter': 38, 'verse': 9, 'text': 'أَمۡ عِندَهُمۡ خَزَآئِنُ رَحۡمَةِ رَبِّكَ ٱلۡعَزِيزِ ٱلۡوَهَّابِ\nЁки уларнинг ҳузурида Азийзу ўта саховатли Роббингнинг раҳмат хазинаси борми'}, {'chapter': 38, 'verse': 10, 'text': 'أَمۡ لَهُم مُّلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ فَلۡيَرۡتَقُواْ فِي ٱلۡأَسۡبَٰبِ\nЁки осмонлару ернинг ва улар орасидаги нарсаларнинг мулки уларникими? Бас, унда сабаблари ила (осмонга) кўтарилсинлар'}, {'chapter': 38, 'verse': 11, 'text': 'جُندٞ مَّا هُنَالِكَ مَهۡزُومٞ مِّنَ ٱلۡأَحۡزَابِ\nАнави ердаги енгилган фирқалардан бўлган қандайдир лашкар'}, {'chapter': 38, 'verse': 12, 'text': 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَعَادٞ وَفِرۡعَوۡنُ ذُو ٱلۡأَوۡتَادِ\nУлардан олдин ҳам Нуҳ қавми, Од, қозиқлар эгаси Фиръавн ҳам ёлғончи қилган'}, {'chapter': 38, 'verse': 13, 'text': 'وَثَمُودُ وَقَوۡمُ لُوطٖ وَأَصۡحَٰبُ لۡـَٔيۡكَةِۚ أُوْلَـٰٓئِكَ ٱلۡأَحۡزَابُ\nВа Самуд, Лут қавми ва дарахтзор эгалари ҳам. Фирқалар ўшалардир'}, {'chapter': 38, 'verse': 14, 'text': 'إِن كُلٌّ إِلَّا كَذَّبَ ٱلرُّسُلَ فَحَقَّ عِقَابِ\nБарчалари, албатта, Пайғамбарларни ёлғончи қилдилар. Бас, Менинг иқобим ҳақ бўлди'}, {'chapter': 38, 'verse': 15, 'text': 'وَمَا يَنظُرُ هَـٰٓؤُلَآءِ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ مَّا لَهَا مِن فَوَاقٖ\nАна ўшалар лаҳзага кечикмай келадиган биргина қичқириқдан бошқа нарсага интизор бўлаётганлари йўқ'}, {'chapter': 38, 'verse': 16, 'text': 'وَقَالُواْ رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبۡلَ يَوۡمِ ٱلۡحِسَابِ\nУлар: «Эй Роббимиз, бизга ҳисоб кунидан илгари насибамизни тезроқ бер!» – дедилар'}, {'chapter': 38, 'verse': 17, 'text': 'ٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَٱذۡكُرۡ عَبۡدَنَا دَاوُۥدَ ذَا ٱلۡأَيۡدِۖ إِنَّهُۥٓ أَوَّابٌ\nУлар айтаётган нарсаларга сабр қил. Қувват соҳиби бўлган бандамиз Довудни эсла. Албатта, у ўта қайтгувчидир'}, {'chapter': 38, 'verse': 18, 'text': 'إِنَّا سَخَّرۡنَا ٱلۡجِبَالَ مَعَهُۥ يُسَبِّحۡنَ بِٱلۡعَشِيِّ وَٱلۡإِشۡرَاقِ\nАлбатта, Биз унга тоғларни бўйсундирдик. Улар у билан кечки пайту эрталаб тасбиҳ айтардилар'}, {'chapter': 38, 'verse': 19, 'text': 'وَٱلطَّيۡرَ مَحۡشُورَةٗۖ كُلّٞ لَّهُۥٓ أَوَّابٞ\nТўпланган ҳолдаги қушларни ҳам. Барчалари У зотга ўта қайтгувчидир'}, {'chapter': 38, 'verse': 20, 'text': 'وَشَدَدۡنَا مُلۡكَهُۥ وَءَاتَيۡنَٰهُ ٱلۡحِكۡمَةَ وَفَصۡلَ ٱلۡخِطَابِ\nВа Биз унга мулкини қувватли қилиб бердик ҳамда ҳикматни ва ҳал қилгувчи китобни бердик'}, {'chapter': 38, 'verse': 21, 'text': '۞وَهَلۡ أَتَىٰكَ نَبَؤُاْ ٱلۡخَصۡمِ إِذۡ تَسَوَّرُواْ ٱلۡمِحۡرَابَ\nСенга даъволашганларнинг хабари келдими? Ўшанда улар меҳробга чиқишган эди'}, {'chapter': 38, 'verse': 22, 'text': 'إِذۡ دَخَلُواْ عَلَىٰ دَاوُۥدَ فَفَزِعَ مِنۡهُمۡۖ قَالُواْ لَا تَخَفۡۖ خَصۡمَانِ بَغَىٰ بَعۡضُنَا عَلَىٰ بَعۡضٖ فَٱحۡكُم بَيۡنَنَا بِٱلۡحَقِّ وَلَا تُشۡطِطۡ وَٱهۡدِنَآ إِلَىٰ سَوَآءِ ٱلصِّرَٰطِ\nЎшанда улар Довуднинг олдига киришган эди. Бас, у улардан қўрқиб кетди. Улар: «Қўрқма, булар — икки даъволашгувчи томон. Биз бир-биримизга зулм қилдик. Бас, орамизда ҳақ ила ҳукм чиқар. Жабр қилма. Бизларни тўғри йўлга ҳидоят қил», дедилар'}, {'chapter': 38, 'verse': 23, 'text': 'إِنَّ هَٰذَآ أَخِي لَهُۥ تِسۡعٞ وَتِسۡعُونَ نَعۡجَةٗ وَلِيَ نَعۡجَةٞ وَٰحِدَةٞ فَقَالَ أَكۡفِلۡنِيهَا وَعَزَّنِي فِي ٱلۡخِطَابِ\n«Мана бу — менинг биродарим. Унинг тўқсон тўққизта совлиғи бор. Менинг эса, биргина совлиғим бор. Бас, у: «Уни ҳам менинг кафолатимга бер», деди ва сўзлашувда мендан устун чиқди»'}, {'chapter': 38, 'verse': 24, 'text': 'قَالَ لَقَدۡ ظَلَمَكَ بِسُؤَالِ نَعۡجَتِكَ إِلَىٰ نِعَاجِهِۦۖ وَإِنَّ كَثِيرٗا مِّنَ ٱلۡخُلَطَآءِ لَيَبۡغِي بَعۡضُهُمۡ عَلَىٰ بَعۡضٍ إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَقَلِيلٞ مَّا هُمۡۗ وَظَنَّ دَاوُۥدُ أَنَّمَا فَتَنَّـٰهُ فَٱسۡتَغۡفَرَ رَبَّهُۥ وَخَرَّۤ رَاكِعٗاۤ وَأَنَابَ۩\nУ: «Дарҳақиқат, сенинг совлиғингни сўраб, ўз совлиқларига (қўшиш) билан сенга зулм қилибди. Албатта, кўп шериклар бир-бирларига зулм қилурлар. Магар иймон келтириб, солиҳ амал қилганларгина (қилмаслар). Улар жуда ҳам оз», деди. Ва Довуд Биз уни синаганимизни билди-да, тезда Роббига истиғфор айтиб, саждага йиқилди ва тавба қилди'}, {'chapter': 38, 'verse': 25, 'text': 'فَغَفَرۡنَا لَهُۥ ذَٰلِكَۖ وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابٖ\nБас, Биз унинг ўша (хатоси)ни мағфират қилдик. Албатта, унинг учун ҳузуримизда яқинлик ва гўзал оқибат бордир'}, {'chapter': 38, 'verse': 26, 'text': 'يَٰدَاوُۥدُ إِنَّا جَعَلۡنَٰكَ خَلِيفَةٗ فِي ٱلۡأَرۡضِ فَٱحۡكُم بَيۡنَ ٱلنَّاسِ بِٱلۡحَقِّ وَلَا تَتَّبِعِ ٱلۡهَوَىٰ فَيُضِلَّكَ عَن سَبِيلِ ٱللَّهِۚ إِنَّ ٱلَّذِينَ يَضِلُّونَ عَن سَبِيلِ ٱللَّهِ لَهُمۡ عَذَابٞ شَدِيدُۢ بِمَا نَسُواْ يَوۡمَ ٱلۡحِسَابِ\nЭй Довуд, албатта, Биз сени ер юзида халифа қилдик. Бас, одамлар орасида ҳақ ила ҳукм юрит. Ҳавойи нафсга эргашма! Яна, у сени Аллоҳнинг йўлидан адаштирмасин. Албатта, Аллоҳнинг йўлидан адашадиганларга, унутганлари учун, ҳисоб кунида шиддатли азоб бордир'}, {'chapter': 38, 'verse': 27, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَآءَ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا بَٰطِلٗاۚ ذَٰلِكَ ظَنُّ ٱلَّذِينَ كَفَرُواْۚ فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِنَ ٱلنَّارِ\nБиз осмону ерни ва уларнинг орасидаги нарсаларни беҳуда яратганимиз йўқ. Бу, куфр келтирганларнинг гумонидир. Бас, куфр келтирганлар учун дўзах(азоби)дан вой бўлсин'}, {'chapter': 38, 'verse': 28, 'text': 'أَمۡ نَجۡعَلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ كَٱلۡمُفۡسِدِينَ فِي ٱلۡأَرۡضِ أَمۡ نَجۡعَلُ ٱلۡمُتَّقِينَ كَٱلۡفُجَّارِ\nЙўқ! Биз иймон келтириб, солиҳ амаллар қилганларни ер юзида бузғунчилик қилувчилар каби қилармидик?! Йўқ! Биз тақводорларни фожирлар каби қилармидик'}, {'chapter': 38, 'verse': 29, 'text': 'كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ مُبَٰرَكٞ لِّيَدَّبَّرُوٓاْ ءَايَٰتِهِۦ وَلِيَتَذَكَّرَ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nБиз сенга нозил қилган Китоб муборакдир. Ақл эгалари унинг оятларини тадаббур қилишлари ва эслашлари учундир'}, {'chapter': 38, 'verse': 30, 'text': 'وَوَهَبۡنَا لِدَاوُۥدَ سُلَيۡمَٰنَۚ نِعۡمَ ٱلۡعَبۡدُ إِنَّهُۥٓ أَوَّابٌ\nВа Биз Довудга Сулаймонни ҳадя қилдик. У қандай ҳам яхши бандадир. Албатта, у ўта қайтгувчидир'}, {'chapter': 38, 'verse': 31, 'text': 'إِذۡ عُرِضَ عَلَيۡهِ بِٱلۡعَشِيِّ ٱلصَّـٰفِنَٰتُ ٱلۡجِيَادُ\nЭсла, бир оқшом унга гижинглаб турган учқур отлар кўрсатилди'}, {'chapter': 38, 'verse': 32, 'text': 'فَقَالَ إِنِّيٓ أَحۡبَبۡتُ حُبَّ ٱلۡخَيۡرِ عَن ذِكۡرِ رَبِّي حَتَّىٰ تَوَارَتۡ بِٱلۡحِجَابِ\nУ: «Албатта, мен Роббимнинг зикридан кўра дунё ишқига берилиб кетиб, ҳатто (қуёш) парда ила беркинибди-ку'}, {'chapter': 38, 'verse': 33, 'text': 'رُدُّوهَا عَلَيَّۖ فَطَفِقَ مَسۡحَۢا بِٱلسُّوقِ وَٱلۡأَعۡنَاقِ\nУларни менга қайтаринг!» – деди. Сўнгра уларнинг оёқ ва бўйинларини «силай» бошлади'}, {'chapter': 38, 'verse': 34, 'text': 'وَلَقَدۡ فَتَنَّا سُلَيۡمَٰنَ وَأَلۡقَيۡنَا عَلَىٰ كُرۡسِيِّهِۦ جَسَدٗا ثُمَّ أَنَابَ\nБатаҳқиқ, Биз Сулаймонни синадик. Унинг курсисига жасадни ташладик. Сўнгра, у тавба қилди'}, {'chapter': 38, 'verse': 35, 'text': 'قَالَ رَبِّ ٱغۡفِرۡ لِي وَهَبۡ لِي مُلۡكٗا لَّا يَنۢبَغِي لِأَحَدٖ مِّنۢ بَعۡدِيٓۖ إِنَّكَ أَنتَ ٱلۡوَهَّابُ\nУ: «Эй Роббим, мени мағфират қилгин ва менга ўзимдан кейин бирор кишига муяссар бўлмайдиган мулкни ҳадя этгин. Албатта, сен кўплаб атоларни бергувчисан», деди'}, {'chapter': 38, 'verse': 36, 'text': 'فَسَخَّرۡنَا لَهُ ٱلرِّيحَ تَجۡرِي بِأَمۡرِهِۦ رُخَآءً حَيۡثُ أَصَابَ\nБас, Биз унга шамолни бўйсундирдик, у ирода қилган томонга майин эсаверади'}, {'chapter': 38, 'verse': 37, 'text': 'وَٱلشَّيَٰطِينَ كُلَّ بَنَّآءٖ وَغَوَّاصٖ\nЯна барча бинокор ва ғаввос шайтонларни ҳам'}, {'chapter': 38, 'verse': 38, 'text': 'وَءَاخَرِينَ مُقَرَّنِينَ فِي ٱلۡأَصۡفَادِ\nВа бошқаларини ҳам кишанланган ҳолда тўплаб (бўйсундирдик)'}, {'chapter': 38, 'verse': 39, 'text': 'هَٰذَا عَطَآؤُنَا فَٱمۡنُنۡ أَوۡ أَمۡسِكۡ بِغَيۡرِ حِسَابٖ\nБу Бизнинг ато этган нарсамиздир. Инъом эт ёки тутиб қол, ҳисоб йўқ'}, {'chapter': 38, 'verse': 40, 'text': 'وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابٖ\nВа, албатта, унинг учун ҳузуримизда яқинлик ва гўзал оқибат бордир'}, {'chapter': 38, 'verse': 41, 'text': 'وَٱذۡكُرۡ عَبۡدَنَآ أَيُّوبَ إِذۡ نَادَىٰ رَبَّهُۥٓ أَنِّي مَسَّنِيَ ٱلشَّيۡطَٰنُ بِنُصۡبٖ وَعَذَابٍ\nБизнинг бандамиз Аййубни эсла. Ўшанда у Роббига нидо қилиб: «Албатта, мени шайтон машаққат ва азоб ила тутди», деди'}, {'chapter': 38, 'verse': 42, 'text': 'ٱرۡكُضۡ بِرِجۡلِكَۖ هَٰذَا مُغۡتَسَلُۢ بَارِدٞ وَشَرَابٞ\n«Оёғинг ила тепгин! Бу чўмиладиган ва ичиладиган совуқ (сув)»'}, {'chapter': 38, 'verse': 43, 'text': 'وَوَهَبۡنَا لَهُۥٓ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةٗ مِّنَّا وَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ\nВа Биз унга аҳлини ва яна улар билан бирга мислларичани ҳам ҳадя этдик. Бу, Биздан раҳмат ўлароқ ва ақл эгаларига эслатма учун бўлди'}, {'chapter': 38, 'verse': 44, 'text': 'وَخُذۡ بِيَدِكَ ضِغۡثٗا فَٱضۡرِب بِّهِۦ وَلَا تَحۡنَثۡۗ إِنَّا وَجَدۡنَٰهُ صَابِرٗاۚ نِّعۡمَ ٱلۡعَبۡدُ إِنَّهُۥٓ أَوَّابٞ\n«Қўлингга бир дастани ол-да, у билан ур. Қасамингни бузма», (деди). Биз уни сабрли топдик. У қандай ҳам яхши банда. Албатта, у ўта қайтгувчидир'}, {'chapter': 38, 'verse': 45, 'text': 'وَٱذۡكُرۡ عِبَٰدَنَآ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَ أُوْلِي ٱلۡأَيۡدِي وَٱلۡأَبۡصَٰرِ\nҚўллар ва кўзлар соҳиблари бўлган бандаларимиз Иброҳим, Исҳоқ ва Яъқубни эсла'}, {'chapter': 38, 'verse': 46, 'text': 'إِنَّآ أَخۡلَصۡنَٰهُم بِخَالِصَةٖ ذِكۡرَى ٱلدَّارِ\nАлбатта, Биз уларни холис бир хислат-ла ажратдик. У (охират) диёрини эслаш эди'}, {'chapter': 38, 'verse': 47, 'text': 'وَإِنَّهُمۡ عِندَنَا لَمِنَ ٱلۡمُصۡطَفَيۡنَ ٱلۡأَخۡيَارِ\nАлбатта, улар Бизнинг ҳузуримизда танланган ахёрлардандирлар'}, {'chapter': 38, 'verse': 48, 'text': 'وَٱذۡكُرۡ إِسۡمَٰعِيلَ وَٱلۡيَسَعَ وَذَا ٱلۡكِفۡلِۖ وَكُلّٞ مِّنَ ٱلۡأَخۡيَارِ\nИсмоил, ал-Ясаъ ва Зул-Кифлни ҳам эсла. Барчалари ахёрлардандир'}, {'chapter': 38, 'verse': 49, 'text': 'هَٰذَا ذِكۡرٞۚ وَإِنَّ لِلۡمُتَّقِينَ لَحُسۡنَ مَـَٔابٖ\nБу эслатмадир. Албатта, тақводорлар учун гўзал оқибат'}, {'chapter': 38, 'verse': 50, 'text': 'جَنَّـٰتِ عَدۡنٖ مُّفَتَّحَةٗ لَّهُمُ ٱلۡأَبۡوَٰبُ\nУлар учун эшиклари очиқ адн жаннатлари бордир'}, {'chapter': 38, 'verse': 51, 'text': 'مُتَّكِـِٔينَ فِيهَا يَدۡعُونَ فِيهَا بِفَٰكِهَةٖ كَثِيرَةٖ وَشَرَابٖ\nУ ерда ёнбошлаган ҳолларида ундаги кўплаб мевалар ва шароблардан чақирадилар'}, {'chapter': 38, 'verse': 52, 'text': '۞وَعِندَهُمۡ قَٰصِرَٰتُ ٱلطَّرۡفِ أَتۡرَابٌ\nУларнинг ҳузурида кўзларини (номаҳрамдан) тийган тенгдош(ҳур)лар бордир'}, {'chapter': 38, 'verse': 53, 'text': 'هَٰذَا مَا تُوعَدُونَ لِيَوۡمِ ٱلۡحِسَابِ\nБу сизга ҳисоб куни учун ваъда этилган нарсалардир'}, {'chapter': 38, 'verse': 54, 'text': 'إِنَّ هَٰذَا لَرِزۡقُنَا مَا لَهُۥ مِن نَّفَادٍ\nАлбатта, бу Бизнинг (берадиган) ризқимиздир. Унинг ҳеч тугаши йўқдир'}, {'chapter': 38, 'verse': 55, 'text': 'هَٰذَاۚ وَإِنَّ لِلطَّـٰغِينَ لَشَرَّ مَـَٔابٖ\nИш бундайдир. Албатта, туғёнга кетгувчилар учун ёмон оқибат'}, {'chapter': 38, 'verse': 56, 'text': 'جَهَنَّمَ يَصۡلَوۡنَهَا فَبِئۡسَ ٱلۡمِهَادُ\nЖаҳаннам бордир. Унга кирурлар. Бас, у қандай ҳам ёмон ётоқ'}, {'chapter': 38, 'verse': 57, 'text': 'هَٰذَا فَلۡيَذُوقُوهُ حَمِيمٞ وَغَسَّاقٞ\nМана буни татиб турсинлар: қайноқ сув ва йирингни'}, {'chapter': 38, 'verse': 58, 'text': 'وَءَاخَرُ مِن شَكۡلِهِۦٓ أَزۡوَٰجٌ\nВа бошқа унинг шаклидаги қўша-қўша (азоблар) ҳам'}, {'chapter': 38, 'verse': 59, 'text': 'هَٰذَا فَوۡجٞ مُّقۡتَحِمٞ مَّعَكُمۡ لَا مَرۡحَبَۢا بِهِمۡۚ إِنَّهُمۡ صَالُواْ ٱلنَّارِ\n«Мана бу гуруҳ ҳам сиз билан бирга бостириб киргувчидир. Уларга марҳабо йўқ. Улар, албатта, жаҳаннамга киргувчилардир», дерлар'}, {'chapter': 38, 'verse': 60, 'text': 'قَالُواْ بَلۡ أَنتُمۡ لَا مَرۡحَبَۢا بِكُمۡۖ أَنتُمۡ قَدَّمۡتُمُوهُ لَنَاۖ فَبِئۡسَ ٱلۡقَرَارُ\nУлар: «Йўқ! Ўзингизга марҳабо йўқ! Сизлар ўша(жаҳаннам)ни бизга тақдим қилдингиз! Бас, у нақадар ёмон қароргоҳ!» – дерлар'}, {'chapter': 38, 'verse': 61, 'text': 'قَالُواْ رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدۡهُ عَذَابٗا ضِعۡفٗا فِي ٱلنَّارِ\nУлар: «Эй Роббимиз, бизга бу(жаҳаннам)ни ким тақдим этган бўлса, унга дўзахда икки баробар азобни зиёда қил!» – дерлар'}, {'chapter': 38, 'verse': 62, 'text': 'وَقَالُواْ مَا لَنَا لَا نَرَىٰ رِجَالٗا كُنَّا نَعُدُّهُم مِّنَ ٱلۡأَشۡرَارِ\nУлар: «Бизга нима бўлди? Ёмонлардан ҳисоблаб юрган кишиларимизни кўрмаяпмиз'}, {'chapter': 38, 'verse': 63, 'text': 'أَتَّخَذۡنَٰهُمۡ سِخۡرِيًّا أَمۡ زَاغَتۡ عَنۡهُمُ ٱلۡأَبۡصَٰرُ\nБиз уларни масхара қилиб олган эдикми ё?! Ёки кўзлар улардан тойдими?!» – дерлар'}, {'chapter': 38, 'verse': 64, 'text': 'إِنَّ ذَٰلِكَ لَحَقّٞ تَخَاصُمُ أَهۡلِ ٱلنَّارِ\nАлбатта, мана шу ҳақдир — дўзах аҳлининг ўзаро тортишувидир'}, {'chapter': 38, 'verse': 65, 'text': 'قُلۡ إِنَّمَآ أَنَا۠ مُنذِرٞۖ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ\nСен: «Мен фақат огоҳлантиргувчиман, холос. Ҳеч бир ибодатга сазовор зот йўқдир, магар ягона ва Қаҳҳор (барча махлуқотларни қабзасида тутиб, уларни Ўз ҳукмига юритиб ва қудрати ила бўйсундириб турувчи) Аллоҳгина бор'}, {'chapter': 38, 'verse': 66, 'text': 'رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلۡعَزِيزُ ٱلۡغَفَّـٰرُ\nУ осмонлару ернинг ва уларнинг орасидаги нарсаларнинг Робби, Азийзу Fаффордир», деб айт'}, {'chapter': 38, 'verse': 67, 'text': 'قُلۡ هُوَ نَبَؤٌاْ عَظِيمٌ\nСен: «Бу улуғ бир хабардир'}, {'chapter': 38, 'verse': 68, 'text': 'أَنتُمۡ عَنۡهُ مُعۡرِضُونَ\nСиз бўлсангиз, ундан юз ўгирмоқдасиз'}, {'chapter': 38, 'verse': 69, 'text': 'مَا كَانَ لِيَ مِنۡ عِلۡمِۭ بِٱلۡمَلَإِ ٱلۡأَعۡلَىٰٓ إِذۡ يَخۡتَصِمُونَ\nОлий тўплам ҳақида улар тортишаётганларида менинг ҳеч билимим бўлмаган эди'}, {'chapter': 38, 'verse': 70, 'text': 'إِن يُوحَىٰٓ إِلَيَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ\nМенга фақат ўзимнинг очиқ-ойдин огоҳлантиргувчи эканлигимгина ваҳий этилур», деб айт'}, {'chapter': 38, 'verse': 71, 'text': 'إِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن طِينٖ\nЭсла! Ўшанда Роббинг фаришталарга: «Албатта, мен лойдан башар яратгувчидирман'}, {'chapter': 38, 'verse': 72, 'text': 'فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ\nБас, қачонки уни ростлаб, ичига руҳимдан пуфлаганимда, унга сажда қилиб йиқилинглар!» – деган эди'}, {'chapter': 38, 'verse': 73, 'text': 'فَسَجَدَ ٱلۡمَلَـٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ\nБас, фаришталар барчалари жамланиб, сажда қилдилар'}, {'chapter': 38, 'verse': 74, 'text': 'إِلَّآ إِبۡلِيسَ ٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ\nМагар иблисгина мутакаббирлик қилди ва кофирлардан бўлди'}, {'chapter': 38, 'verse': 75, 'text': 'قَالَ يَـٰٓإِبۡلِيسُ مَا مَنَعَكَ أَن تَسۡجُدَ لِمَا خَلَقۡتُ بِيَدَيَّۖ أَسۡتَكۡبَرۡتَ أَمۡ كُنتَ مِنَ ٱلۡعَالِينَ\nУ зот: «Эй, иблис, Ўз қўлим билан яратган нарсага сажда қилишингдан сени нима ман қилди?! Мутакаббирлик қилдингми?! Ёки олий даражалилардан бўлдингми?!» – деди'}, {'chapter': 38, 'verse': 76, 'text': 'قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ\nУ: «Мен ундан яхшиман. Мени оловдан яратгансан. Уни лойдан яратгансан», деди'}, {'chapter': 38, 'verse': 77, 'text': 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ\nУ зот: «Сен ундан чиқ! Бас, албатта, сен қувилгансан'}, {'chapter': 38, 'verse': 78, 'text': 'وَإِنَّ عَلَيۡكَ لَعۡنَتِيٓ إِلَىٰ يَوۡمِ ٱلدِّينِ\nВа, албатта, сенга жазо кунигача менинг лаънатим бўлгай», деди'}, {'chapter': 38, 'verse': 79, 'text': 'قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nУ: «Эй Роббим, менга улар қайта тирилтирилажак кунгача муҳлат бергин!» – деди'}, {'chapter': 38, 'verse': 80, 'text': 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ\nУ зот: «Сен муҳлат берилганлардансан'}, {'chapter': 38, 'verse': 81, 'text': 'إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ\nМаълум вақт кунигача», деди'}, {'chapter': 38, 'verse': 82, 'text': 'قَالَ فَبِعِزَّتِكَ لَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ\nУ : «Сенинг иззатинг ила қасамки, мен уларнинг барчасини иғво қиламан'}, {'chapter': 38, 'verse': 83, 'text': 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ\nМагар, улардан халос қилинган бандаларинггина (иғвога учмас)», деди'}, {'chapter': 38, 'verse': 84, 'text': 'قَالَ فَٱلۡحَقُّ وَٱلۡحَقَّ أَقُولُ\nУ зот: «Ҳақ шулки, зотан, Мен фақат ҳақни айтурман'}, {'chapter': 38, 'verse': 85, 'text': 'لَأَمۡلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنۡهُمۡ أَجۡمَعِينَ\nАлбатта, Мен жаҳаннамни сен ва улардан сенга эргашганлар билан жамлаб тўлдирурман», деди'}, {'chapter': 38, 'verse': 86, 'text': 'قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٖ وَمَآ أَنَا۠ مِنَ ٱلۡمُتَكَلِّفِينَ\nСен: «Мен унинг учун сиздан ҳеч ажр, ҳақ сўрамасман. Мен сохтакорлардан ҳам эмасман'}, {'chapter': 38, 'verse': 87, 'text': 'إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nУ(Қуръон) фақат оламлар учун эслатма, холос'}, {'chapter': 38, 'verse': 88, 'text': 'وَلَتَعۡلَمُنَّ نَبَأَهُۥ بَعۡدَ حِينِۭ\nАлбатта, унинг хабарини бир муддатдан сўнг билурсиз», деб айт'}], '39': [{'chapter': 39, 'verse': 1, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nБу китобнинг нозил қилиниши азийзу ҳакийм бўлган Аллоҳ томонидандир'}, {'chapter': 39, 'verse': 2, 'text': 'إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ فَٱعۡبُدِ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ\nАлбатта, Биз сенга китобни ҳақ ила нозил қилдик. Бас, сен Аллоҳга Унга динни холис қилган ҳолингда ибодат эт'}, {'chapter': 39, 'verse': 3, 'text': 'أَلَا لِلَّهِ ٱلدِّينُ ٱلۡخَالِصُۚ وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ مَا نَعۡبُدُهُمۡ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلۡفَىٰٓ إِنَّ ٱللَّهَ يَحۡكُمُ بَيۡنَهُمۡ فِي مَا هُمۡ فِيهِ يَخۡتَلِفُونَۗ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ كَٰذِبٞ كَفَّارٞ\nОгоҳ бўлингким, холис дин Аллоҳникидир. Ундан ўзга авлиё (дўстлар) тутганлар: «Биз уларга фақат бизларни Аллоҳга жуда ҳам яқин қилишлари учунгина ибодат қилурмиз», (дерлар). Албатта, У зот улар орасида ихтилоф этаётган нарсалари бўйича ҳукм қилур. Албатта, Аллоҳ ёлғончи ва кофир кимсаларни ҳидоят қилмас'}, {'chapter': 39, 'verse': 4, 'text': 'لَّوۡ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدٗا لَّٱصۡطَفَىٰ مِمَّا يَخۡلُقُ مَا يَشَآءُۚ سُبۡحَٰنَهُۥۖ هُوَ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ\nАгар Аллоҳ бола тутишни истаса, албатта, Ўзи яратган нарсалардан хоҳлаганини танлаб олар эди. У зот покдир. Аллоҳ ягона Қаҳҳордир (яъни, Қаҳҳорлигидан бутун оламни Ўзига бўйсундириб ва якка Ўзи идора қилиб туради. Бу ишида У ҳеч қандай шерикка, ёрдамчига муҳтож эмас)'}, {'chapter': 39, 'verse': 5, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ يُكَوِّرُ ٱلَّيۡلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيۡلِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمًّىۗ أَلَا هُوَ ٱلۡعَزِيزُ ٱلۡغَفَّـٰرُ\nУ осмонлару ерни ҳақ ила яратган, кечани кундуз устига ўрайдиган, кундузни кеча устига ўрайдиган, қуёш ва ойни бўйсундирадиган Зотдир. Ҳар бири белгиланган муддатча юрмоқда. Огоҳ бўлингким, У азийзу ўта мағфиратли Зотдир'}, {'chapter': 39, 'verse': 6, 'text': 'خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ ثُمَّ جَعَلَ مِنۡهَا زَوۡجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلۡأَنۡعَٰمِ ثَمَٰنِيَةَ أَزۡوَٰجٖۚ يَخۡلُقُكُمۡ فِي بُطُونِ أُمَّهَٰتِكُمۡ خَلۡقٗا مِّنۢ بَعۡدِ خَلۡقٖ فِي ظُلُمَٰتٖ ثَلَٰثٖۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۖ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُصۡرَفُونَ\nУ сизларни бир жондан яратди. Сўнгра ундан жуфтини қилди ва сизларга чорва ҳайвонларидан саккиз жуфтни туширди. У сизни оналарингиз қорнида уч зулмат ичида бир яратишдан кейин яна бошқа яратишга ўтказиб, аста яратадир. Ана ўша Аллоҳ Роббингиздир. Барча мулк Уникидир. Ундан ўзга ҳеч бир ибодатга сазовор зот йўқ. Бас, қаён бурилиб кетмоқдасиз'}, {'chapter': 39, 'verse': 7, 'text': 'إِن تَكۡفُرُواْ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنكُمۡۖ وَلَا يَرۡضَىٰ لِعِبَادِهِ ٱلۡكُفۡرَۖ وَإِن تَشۡكُرُواْ يَرۡضَهُ لَكُمۡۗ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nАгар куфр келтирсангиз, албатта, Аллоҳ сиздан беҳожатдир. У зот Ўз бандаларининг кофир бўлишига рози бўлмас. Агар Унга шукр келтирсангиз, сиздан рози бўлур. Ҳеч бир оғирлик кўтаргувчи жон ўзга жоннинг оғирлигини кўтармас. Сўнгра фақатгина Роббингизга қайтишингиз бор. Бас, У нима амал қилиб юрганингизнинг хабарини берур. Албатта, У диллардаги нарсаларни ҳам ўта билгувчи Зотдир'}, {'chapter': 39, 'verse': 8, 'text': '۞وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَا رَبَّهُۥ مُنِيبًا إِلَيۡهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعۡمَةٗ مِّنۡهُ نَسِيَ مَا كَانَ يَدۡعُوٓاْ إِلَيۡهِ مِن قَبۡلُ وَجَعَلَ لِلَّهِ أَندَادٗا لِّيُضِلَّ عَن سَبِيلِهِۦۚ قُلۡ تَمَتَّعۡ بِكُفۡرِكَ قَلِيلًا إِنَّكَ مِنۡ أَصۡحَٰبِ ٱلنَّارِ\nҚачонки инсонни зарар тутса, Роббига тавба қилган ҳолида дуо қилур. Сўнгра унга У зот Ўз фазлидан неъмат берса, олдин дуо қилиб турган нарсасини унутур ва Аллоҳга Унинг йўлидан адаштирмоқ учун тенгдошлар қилур. Сен: «Куфринг билан бир оз ҳузурланиб тур. Албатта, сен жаҳаннам эгаларидансан», деб айт'}, {'chapter': 39, 'verse': 9, 'text': 'أَمَّنۡ هُوَ قَٰنِتٌ ءَانَآءَ ٱلَّيۡلِ سَاجِدٗا وَقَآئِمٗا يَحۡذَرُ ٱلۡأٓخِرَةَ وَيَرۡجُواْ رَحۡمَةَ رَبِّهِۦۗ قُلۡ هَلۡ يَسۡتَوِي ٱلَّذِينَ يَعۡلَمُونَ وَٱلَّذِينَ لَا يَعۡلَمُونَۗ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nЙўқса, у кечалари ибодат қилиб, сажда этган, бедор ҳолида охиратдан қўрқиб, Робби раҳматидан умидвор бўлган одамга тенгми?! Сен: «Биладиганлар билан билмайдиганлар тенг бўлармиди?!» – деб айт. Албатта, ақл эгаларигина эсларлар'}, {'chapter': 39, 'verse': 10, 'text': 'قُلۡ يَٰعِبَادِ ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ رَبَّكُمۡۚ لِلَّذِينَ أَحۡسَنُواْ فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٞۗ وَأَرۡضُ ٱللَّهِ وَٰسِعَةٌۗ إِنَّمَا يُوَفَّى ٱلصَّـٰبِرُونَ أَجۡرَهُم بِغَيۡرِ حِسَابٖ\nСен (Менинг тарафимдан): «Эй, иймон келтирган бандаларим, Роббингизга тақво қилинг. Бу дунёда гўзал амал қилганларга гўзаллик бордир. Аллоҳнинг ери кенгдир. Албатта, сабр қилгувчиларга ажрлари ҳисобсиз, тўлиқ берилур», деб айт'}, {'chapter': 39, 'verse': 11, 'text': 'قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ\nСен: «Албатта, мен динни Ўзи учун холис қилган ҳолимда Аллоҳга ибодат этишимга буюрилганман'}, {'chapter': 39, 'verse': 12, 'text': 'وَأُمِرۡتُ لِأَنۡ أَكُونَ أَوَّلَ ٱلۡمُسۡلِمِينَ\nВа мен мусулмонларнинг аввалгиси бўлишимга амр этилганман», деб айт'}, {'chapter': 39, 'verse': 13, 'text': 'قُلۡ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ\nСен: «Албатта, мен агар Роббимга исён этсам, улуғ куннинг азобидан қўрқаман», деб айт'}, {'chapter': 39, 'verse': 14, 'text': 'قُلِ ٱللَّهَ أَعۡبُدُ مُخۡلِصٗا لَّهُۥ دِينِي\nСен: «Динимни Ўзига холис қилган ҳолимда Аллоҳгагина ибодат қилурман'}, {'chapter': 39, 'verse': 15, 'text': 'فَٱعۡبُدُواْ مَا شِئۡتُم مِّن دُونِهِۦۗ قُلۡ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَا ذَٰلِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِينُ\nБас, сизлар Ундан ўзга нимани хоҳласангиз, ўшанга ибодат қилаверинглар», деб айт. Сен: «Албатта, зиёнкорлар қиёмат куни ўзларига ва аҳлларига зиён қилгувчилардир. Огоҳ бўлингким, ана ўша очиқ-ойдин зиёнкорликдир», деб айт'}, {'chapter': 39, 'verse': 16, 'text': 'لَهُم مِّن فَوۡقِهِمۡ ظُلَلٞ مِّنَ ٱلنَّارِ وَمِن تَحۡتِهِمۡ ظُلَلٞۚ ذَٰلِكَ يُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥۚ يَٰعِبَادِ فَٱتَّقُونِ\nУлар учун тепаларидан ҳам оловдан «соябонлар» бор, остларидан ҳам оловдан «соябонлар» бор. Бу билан Аллоҳ Ўз бандаларини қўрқитур. «Эй бандаларим, Менга тақво қилинглар!»'}, {'chapter': 39, 'verse': 17, 'text': 'وَٱلَّذِينَ ٱجۡتَنَبُواْ ٱلطَّـٰغُوتَ أَن يَعۡبُدُوهَا وَأَنَابُوٓاْ إِلَى ٱللَّهِ لَهُمُ ٱلۡبُشۡرَىٰۚ فَبَشِّرۡ عِبَادِ\nТоғутдан, унга ибодат қилишдан четда бўлганларга ва Аллоҳга қайтганларга хушхабар бор. Бас, бир бандаларимга хушхабар берки'}, {'chapter': 39, 'verse': 18, 'text': 'ٱلَّذِينَ يَسۡتَمِعُونَ ٱلۡقَوۡلَ فَيَتَّبِعُونَ أَحۡسَنَهُۥٓۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ هَدَىٰهُمُ ٱللَّهُۖ وَأُوْلَـٰٓئِكَ هُمۡ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nУлар гапни эшитиб, энг гўзалига эргашарлар. Ана ўшалар Аллоҳ ҳидоят қилганлардир. Ана ўшалар, ҳа, ўшалар ақл эгаларидир'}, {'chapter': 39, 'verse': 19, 'text': 'أَفَمَنۡ حَقَّ عَلَيۡهِ كَلِمَةُ ٱلۡعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِي ٱلنَّارِ\nЁки у ҳақида азоб сўзи ҳақ бўлган кимсани — дўзахдаги кимсани сен қутқара олурмисан'}, {'chapter': 39, 'verse': 20, 'text': 'لَٰكِنِ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ لَهُمۡ غُرَفٞ مِّن فَوۡقِهَا غُرَفٞ مَّبۡنِيَّةٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَعۡدَ ٱللَّهِ لَا يُخۡلِفُ ٱللَّهُ ٱلۡمِيعَادَ\nЛекин Роббиларига тақво қилганлар учун болохоналар устидан болохоналар бино қилингандир. Остларидан анҳорлар оқиб турур. Аллоҳнинг ваъдаси шу! Аллоҳ ваъдага хилоф қилмас'}, {'chapter': 39, 'verse': 21, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَسَلَكَهُۥ يَنَٰبِيعَ فِي ٱلۡأَرۡضِ ثُمَّ يُخۡرِجُ بِهِۦ زَرۡعٗا مُّخۡتَلِفًا أَلۡوَٰنُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصۡفَرّٗا ثُمَّ يَجۡعَلُهُۥ حُطَٰمًاۚ إِنَّ فِي ذَٰلِكَ لَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ\nАллоҳ осмондан сув тушириб, уни ердаги манбалардан юритиб қўйганини кўрмадингми?! Сўнгра у ила турли рангдаги экинларни чиқаради. Кейин у қурийди. Бас, уни сарғайган ҳолида кўрарсан. Сўнгра уни қуруқ чўпга айлантирур. Албаттта, бунда ақл эгалари учун эслатма бордир'}, {'chapter': 39, 'verse': 22, 'text': 'أَفَمَن شَرَحَ ٱللَّهُ صَدۡرَهُۥ لِلۡإِسۡلَٰمِ فَهُوَ عَلَىٰ نُورٖ مِّن رَّبِّهِۦۚ فَوَيۡلٞ لِّلۡقَٰسِيَةِ قُلُوبُهُم مِّن ذِكۡرِ ٱللَّهِۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ\nАллоҳ кўксини Исломга кенг қилиб очиб қўйган шахс ўз Роббидан бўлган нурда эмасми?! Аллоҳни зикр қилишдан қалблари қотиб қолганларга вой бўлсин! Ана ўшалар очиқ-ойдин залолатдадирлар'}, {'chapter': 39, 'verse': 23, 'text': 'ٱللَّهُ نَزَّلَ أَحۡسَنَ ٱلۡحَدِيثِ كِتَٰبٗا مُّتَشَٰبِهٗا مَّثَانِيَ تَقۡشَعِرُّ مِنۡهُ جُلُودُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُمۡ ثُمَّ تَلِينُ جُلُودُهُمۡ وَقُلُوبُهُمۡ إِلَىٰ ذِكۡرِ ٱللَّهِۚ ذَٰلِكَ هُدَى ٱللَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُۚ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٍ\nАллоҳ энг гўзал сўзни ўхшаш ва такрорланган китоб этиб туширар. Ундан Роббиларидан қўрқадиганларнинг терилари титрар. Сўнгра уларнинг терилари ва қалблари Аллоҳнинг зикрига юмшар. Ана шу, Аллоҳнинг ҳидоятидир. У ила Аллоҳ хоҳлаган кимсани ҳидоят қилар. Кимни Аллоҳ залолатга кетказса, бас, унинг учун ҳидоят қилгувчи йўқ'}, {'chapter': 39, 'verse': 24, 'text': 'أَفَمَن يَتَّقِي بِوَجۡهِهِۦ سُوٓءَ ٱلۡعَذَابِ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَقِيلَ لِلظَّـٰلِمِينَ ذُوقُواْ مَا كُنتُمۡ تَكۡسِبُونَ\nАхир қиёмат куни золимларга, касб қилиб юрган нарсангизни тотиб кўринг, дейилганида ёмон азобдан юзи ила сақланадиган одам (мўмин ила тенг бўлармиди)'}, {'chapter': 39, 'verse': 25, 'text': 'كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ\nУлардан олдингилар ҳам ёлғончи қилганлар, бас, уларга ўзлари сезмаган томондан азоб келган'}, {'chapter': 39, 'verse': 26, 'text': 'فَأَذَاقَهُمُ ٱللَّهُ ٱلۡخِزۡيَ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nАллоҳ уларга бу дунёда хорликни тоттирди, албатта, охират азоби, агар билсалар, янада улкан бўлур'}, {'chapter': 39, 'verse': 27, 'text': 'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖ لَّعَلَّهُمۡ يَتَذَكَّرُونَ\nБатаҳқиқ, Биз ушбу Қуръонда одамларга турли мисоллар келтирдик. Шоядки, эсласалар'}, {'chapter': 39, 'verse': 28, 'text': 'قُرۡءَانًا عَرَبِيًّا غَيۡرَ ذِي عِوَجٖ لَّعَلَّهُمۡ يَتَّقُونَ\nАрабча Қуръонни эгриликсиз қилиб (туширдикки), шоядки, тақво қилсалар'}, {'chapter': 39, 'verse': 29, 'text': 'ضَرَبَ ٱللَّهُ مَثَلٗا رَّجُلٗا فِيهِ شُرَكَآءُ مُتَشَٰكِسُونَ وَرَجُلٗا سَلَمٗا لِّرَجُلٍ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nАллоҳ бир мисол келтирадир. Бир одамга талашувчи шериклар эгадир. Саломат ҳолидаги бошқа бирига бир киши эгадир. Икковларининг мисоли баробарми?! Аллоҳга ҳамд бўлсин. Йўқ, кўплари билмаслар'}, {'chapter': 39, 'verse': 30, 'text': 'إِنَّكَ مَيِّتٞ وَإِنَّهُم مَّيِّتُونَ\nАлбатта, сен ҳам ўлгувчисан, улар ҳам ўлгувчидирлар'}, {'chapter': 39, 'verse': 31, 'text': 'ثُمَّ إِنَّكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ عِندَ رَبِّكُمۡ تَخۡتَصِمُونَ\nСўнгра, албатта, сизлар қиёмат куни Роббингиз ҳузурида тортишурсизлар'}, {'chapter': 39, 'verse': 32, 'text': '۞فَمَنۡ أَظۡلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدۡقِ إِذۡ جَآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكَٰفِرِينَ\nАллоҳга нисбатан ёлғон гапирган ва ўзига содиқ (сўз) келганида уни ёлғонга чиқаргандан ҳам золимроқ кимса борми?! Кофирларнинг жойи жаҳаннамда эмасми'}, {'chapter': 39, 'verse': 33, 'text': 'وَٱلَّذِي جَآءَ بِٱلصِّدۡقِ وَصَدَّقَ بِهِۦٓ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُتَّقُونَ\nСодиқ (сўз)ни келтирган ва уни тасдиқ қилган эса, ана ўшалар тақводорлардир'}, {'chapter': 39, 'verse': 34, 'text': 'لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ جَزَآءُ ٱلۡمُحۡسِنِينَ\nУлар учун Роббилари ҳузурида хоҳлаган нарсалари бордир. Ана ўша, гўзал амал қилгувчиларнинг мукофотидир'}, {'chapter': 39, 'verse': 35, 'text': 'لِيُكَفِّرَ ٱللَّهُ عَنۡهُمۡ أَسۡوَأَ ٱلَّذِي عَمِلُواْ وَيَجۡزِيَهُمۡ أَجۡرَهُم بِأَحۡسَنِ ٱلَّذِي كَانُواْ يَعۡمَلُونَ\nЗеро, Аллоҳ уларнинг қилган энг ёмон амалини ўчириб, қилиб юрган энг гўзал амаллари ажри ила мукофотлар'}, {'chapter': 39, 'verse': 36, 'text': 'أَلَيۡسَ ٱللَّهُ بِكَافٍ عَبۡدَهُۥۖ وَيُخَوِّفُونَكَ بِٱلَّذِينَ مِن دُونِهِۦۚ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ\nАллоҳ Ўз бандасига етарли эмасми?! Улар сени Ундан бошқалар билан қўрқитурлар. Кимни Аллоҳ залолатга кетказса, бас, унинг учун ҳеч бир ҳидоят қилгувчи йўқ'}, {'chapter': 39, 'verse': 37, 'text': 'وَمَن يَهۡدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّۗ أَلَيۡسَ ٱللَّهُ بِعَزِيزٖ ذِي ٱنتِقَامٖ\nКимни Аллоҳ ҳидоятга солса, бас, унинг учун ҳеч бир залолатга кетказгувчи йўқ. Аллоҳ Азийзу интиқом эгаси эмасми'}, {'chapter': 39, 'verse': 38, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ ٱللَّهُۚ قُلۡ أَفَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ إِنۡ أَرَادَنِيَ ٱللَّهُ بِضُرٍّ هَلۡ هُنَّ كَٰشِفَٰتُ ضُرِّهِۦٓ أَوۡ أَرَادَنِي بِرَحۡمَةٍ هَلۡ هُنَّ مُمۡسِكَٰتُ رَحۡمَتِهِۦۚ قُلۡ حَسۡبِيَ ٱللَّهُۖ عَلَيۡهِ يَتَوَكَّلُ ٱلۡمُتَوَكِّلُونَ\nАгар улардан: «Осмонлару ерни ким яратди?» – деб сўрасанг, албатта: «Аллоҳ!» – дерлар. Сен: «Менга хабар беринглар-чи! Сиз илтижо қилаётган, Аллоҳдан бошқа нарсалар, агар Аллоҳ менга зарарни ирода қилса, ўшалар Унинг зарарини аритгувчи бўла олурларми?! Ёки менга марҳаматни ирода қилса, ўшалар Унинг марҳаматини тутиб қолгувчи бўла олурми?!» – деб айт. Сен: «Менга Аллоҳ етарли, таваккал қилгувчилар Унгагина таваккал қилурлар», деб айт'}, {'chapter': 39, 'verse': 39, 'text': 'قُلۡ يَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ فَسَوۡفَ تَعۡلَمُونَ\nСен: «Эй қавмим, ўз ҳолингизча амал қилаверинг. Албатта, мен ҳам амал қилгувчиман. Бас, тезда билурсиз'}, {'chapter': 39, 'verse': 40, 'text': 'مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ\nКимга уни расво қиладиган азоб келажагини ва кимга давомли азоб тушажагини», деб айт'}, {'chapter': 39, 'verse': 41, 'text': 'إِنَّآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ لِلنَّاسِ بِٱلۡحَقِّۖ فَمَنِ ٱهۡتَدَىٰ فَلِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٍ\nАлбатта, Биз одамлар учун сенга китобни ҳақ ила туширдик. Бас, ким ҳидоятга юрса, ўзига фойда. Ким залолатга кетса, ўзига зарар. Сен уларга вакил (қўриқчи) эмассан'}, {'chapter': 39, 'verse': 42, 'text': 'ٱللَّهُ يَتَوَفَّى ٱلۡأَنفُسَ حِينَ مَوۡتِهَا وَٱلَّتِي لَمۡ تَمُتۡ فِي مَنَامِهَاۖ فَيُمۡسِكُ ٱلَّتِي قَضَىٰ عَلَيۡهَا ٱلۡمَوۡتَ وَيُرۡسِلُ ٱلۡأُخۡرَىٰٓ إِلَىٰٓ أَجَلٖ مُّسَمًّىۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nАллоҳ жонларни ўлими вақтида, ўлмаганларини эса, уйқуси вақтида олур. Бас, ўлишига ҳукм қилганларини ушлаб қолур, қолганларини маълум муддатгача қўйиб юборур. Албатта, бунда тафаккур қиладиган қавмлар учун оят(ибрат)лар бордир'}, {'chapter': 39, 'verse': 43, 'text': 'أَمِ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ شُفَعَآءَۚ قُلۡ أَوَلَوۡ كَانُواْ لَا يَمۡلِكُونَ شَيۡـٔٗا وَلَا يَعۡقِلُونَ\nЁки Аллоҳдан ўзга шафоатчилар тутганларми?! Сен: «Ахир, улар бирор нарсага эга бўлмасалар ва бирор нарсани англамасалар ҳам-а?!» – деб айт'}, {'chapter': 39, 'verse': 44, 'text': 'قُل لِّلَّهِ ٱلشَّفَٰعَةُ جَمِيعٗاۖ لَّهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ\nСен: «Барча шафоат ёлғиз Аллоҳникидир. Осмонлару ернинг мулки(эгалиги) ҳам ёлғиз Уникидир. Сўнгра Унгагина қайтарилурсизлар», деб айт'}, {'chapter': 39, 'verse': 45, 'text': 'وَإِذَا ذُكِرَ ٱللَّهُ وَحۡدَهُ ٱشۡمَأَزَّتۡ قُلُوبُ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِۖ وَإِذَا ذُكِرَ ٱلَّذِينَ مِن دُونِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ\nҚачонки Аллоҳнинг ёлғиз Ўзи зикр қилинса, охиратга иймон келтирмайдиганларнинг қалблари сиқилиб кетар. Қачонки Ундан ўзгалар зикр қилинсалар, бирдан шодланиб кетарлар'}, {'chapter': 39, 'verse': 46, 'text': 'قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ عَٰلِمَ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ أَنتَ تَحۡكُمُ بَيۡنَ عِبَادِكَ فِي مَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nСен: «Эй Аллоҳим! Осмонлару ерни яратган Зот! Fойиб ва ошкорни билгувчи Зот! Сенинг Ўзинг бандаларнинг орасида улар ихтилоф қилиб юрган нарсаларда ҳукм қилурсан», деб айт'}, {'chapter': 39, 'verse': 47, 'text': 'وَلَوۡ أَنَّ لِلَّذِينَ ظَلَمُواْ مَا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡاْ بِهِۦ مِن سُوٓءِ ٱلۡعَذَابِ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمۡ يَكُونُواْ يَحۡتَسِبُونَ\nАгар зулм этганларга ер юзидаги барча нарса ва у билан яна шу қадар нарса берилса ҳам, қиёмат куни ёмон азобга тўлов қилиб юборишлари аниқ. Ва уларга Аллоҳ томонидан ўзлари хаёлларига ҳам келтирмаган нарсалар зоҳир бўлур'}, {'chapter': 39, 'verse': 48, 'text': 'وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nВа уларга ўзлари касб қилган нарсаларнинг ёмонлиги зоҳир бўлур ва уларни ўзлари масхара қилиб юрган нарса ўраб олур'}, {'chapter': 39, 'verse': 49, 'text': 'فَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَانَا ثُمَّ إِذَا خَوَّلۡنَٰهُ نِعۡمَةٗ مِّنَّا قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمِۭۚ بَلۡ هِيَ فِتۡنَةٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nБас, қачонки инсонни зарар тутса, у Бизга дуо қилур. Сўнгра унга Биз Ўзимиздан неъмат берсак: «Албатта, бу менга илмим туфайли берилди», дер. Йўқ! У синовдир. Лекин кўплари билмаслар'}, {'chapter': 39, 'verse': 50, 'text': 'قَدۡ قَالَهَا ٱلَّذِينَ مِن قَبۡلِهِمۡ فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ\nБатаҳқиқ, буни улардан аввалгилар ҳам айтган эдилар. Бас, уларнинг қилган касблари ўзларига асқотмади'}, {'chapter': 39, 'verse': 51, 'text': 'فَأَصَابَهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْۚ وَٱلَّذِينَ ظَلَمُواْ مِنۡ هَـٰٓؤُلَآءِ سَيُصِيبُهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْ وَمَا هُم بِمُعۡجِزِينَ\nБас, уларга қилган касбларининг ёмонликлари мусийбат бўлди. Анавилардан зулм қилганларига ҳам тезда қилган касбларининг ёмонликлари мусийбат бўлур. Улар қочиб қутулгувчи ҳам эмаслар'}, {'chapter': 39, 'verse': 52, 'text': 'أَوَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nУлар Аллоҳ хоҳлаган кишисига ризқни кенг ва тор қилишини билмасмидилар?! Албатта, бунда иймон келтиргувчи қавмлар учун оят(белги)лар бордир'}, {'chapter': 39, 'verse': 53, 'text': '۞قُلۡ يَٰعِبَادِيَ ٱلَّذِينَ أَسۡرَفُواْ عَلَىٰٓ أَنفُسِهِمۡ لَا تَقۡنَطُواْ مِن رَّحۡمَةِ ٱللَّهِۚ إِنَّ ٱللَّهَ يَغۡفِرُ ٱلذُّنُوبَ جَمِيعًاۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nСен (Менинг тарафимдан): «Эй, ўз жонларига исроф (жавр) қилган бандаларим, Аллоҳнинг раҳматидан ноумид бўлманг! Албатта, Аллоҳ барча гуноҳларни мағфират этар. Албатта, Унинг Ўзи ўта мағфиратли ва ўта раҳимли Зотдир», деб айт'}, {'chapter': 39, 'verse': 54, 'text': 'وَأَنِيبُوٓاْ إِلَىٰ رَبِّكُمۡ وَأَسۡلِمُواْ لَهُۥ مِن قَبۡلِ أَن يَأۡتِيَكُمُ ٱلۡعَذَابُ ثُمَّ لَا تُنصَرُونَ\nСизга азоб келишидан олдин Роббингизга қайтинг ва Унга таслим бўлинг. Сўнгра сизга ёрдам берилмас'}, {'chapter': 39, 'verse': 55, 'text': 'وَٱتَّبِعُوٓاْ أَحۡسَنَ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَكُمُ ٱلۡعَذَابُ بَغۡتَةٗ وَأَنتُمۡ لَا تَشۡعُرُونَ\nСизга ўзингиз сезмаган ҳолда тўсатдан азоб келиб қолишидан олдин Роббингиздан сизга туширилган энг гўзал нарсага эргашинг'}, {'chapter': 39, 'verse': 56, 'text': 'أَن تَقُولَ نَفۡسٞ يَٰحَسۡرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِي جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّـٰخِرِينَ\nЖон: «Аллоҳ ҳақида йўл қўйган камчиликларим учун надомат бўлсин. Тўғриси, мен масхара қилгувчилардан бўлган эдим», демасидан олдин'}, {'chapter': 39, 'verse': 57, 'text': 'أَوۡ تَقُولَ لَوۡ أَنَّ ٱللَّهَ هَدَىٰنِي لَكُنتُ مِنَ ٱلۡمُتَّقِينَ\nЁки: «Агар Аллоҳ мени ҳидоят қилганида, албатта, тақводорлардан бўлар эдим», демасидан олдин'}, {'chapter': 39, 'verse': 58, 'text': 'أَوۡ تَقُولَ حِينَ تَرَى ٱلۡعَذَابَ لَوۡ أَنَّ لِي كَرَّةٗ فَأَكُونَ مِنَ ٱلۡمُحۡسِنِينَ\nЁки азобни кўрган чоғида: «Агар менга яна бир марта ортга (дунёга) қайтиш бўлганида, гўзал амал қилгувчилардан бўлар эдим», демасидан олдин'}, {'chapter': 39, 'verse': 59, 'text': 'بَلَىٰ قَدۡ جَآءَتۡكَ ءَايَٰتِي فَكَذَّبۡتَ بِهَا وَٱسۡتَكۡبَرۡتَ وَكُنتَ مِنَ ٱلۡكَٰفِرِينَ\n«Йўқ! Батаҳқиқ, сенга Менинг оятларим келди. Бас, сен уларни ёлғонга чиқардинг, мутакаббирлик қилдинг ва кофирлардан бўлдинг!» дер'}, {'chapter': 39, 'verse': 60, 'text': 'وَيَوۡمَ ٱلۡقِيَٰمَةِ تَرَى ٱلَّذِينَ كَذَبُواْ عَلَى ٱللَّهِ وُجُوهُهُم مُّسۡوَدَّةٌۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡمُتَكَبِّرِينَ\nҚиёмат куни Аллоҳга нисбатан ёлғон сўзлаганларни кўрсанг, юзлари қоп-қорадир. Ёки мутакаббирларнинг жойи жаҳаннамда эмасми'}, {'chapter': 39, 'verse': 61, 'text': 'وَيُنَجِّي ٱللَّهُ ٱلَّذِينَ ٱتَّقَوۡاْ بِمَفَازَتِهِمۡ لَا يَمَسُّهُمُ ٱلسُّوٓءُ وَلَا هُمۡ يَحۡزَنُونَ\nАллоҳ тақво қилганларга ютуқлари туфайли нажот берур. Уларга ёмонлик етмас ва улар хафа ҳам бўлмаслар'}, {'chapter': 39, 'verse': 62, 'text': 'ٱللَّهُ خَٰلِقُ كُلِّ شَيۡءٖۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ\nАллоҳ ҳар бир нарсанинг яратгувчисидир. Ва У ҳар бир нарсага вакилдир'}, {'chapter': 39, 'verse': 63, 'text': 'لَّهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nОсмонлару ернинг калитлари Унгагина хосдир. Аллоҳнинг оятларига куфр келтирганлар, ана ўшалар зиёнкорлардир'}, {'chapter': 39, 'verse': 64, 'text': 'قُلۡ أَفَغَيۡرَ ٱللَّهِ تَأۡمُرُوٓنِّيٓ أَعۡبُدُ أَيُّهَا ٱلۡجَٰهِلُونَ\nСен: «Эй, жоҳиллар! Мени Аллоҳдан ўзгага ибодат қилишимга буюрурмисиз?!» – деб айт'}, {'chapter': 39, 'verse': 65, 'text': 'وَلَقَدۡ أُوحِيَ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ لَئِنۡ أَشۡرَكۡتَ لَيَحۡبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ\nБатаҳқиқ, сенга ва сендан олдин ўтганларга ҳам: «Агар ширк келтирсанг, албатта, амалинг беҳуда кетур ва, албатта, зиёнкорлардан бўлурсан'}, {'chapter': 39, 'verse': 66, 'text': 'بَلِ ٱللَّهَ فَٱعۡبُدۡ وَكُن مِّنَ ٱلشَّـٰكِرِينَ\nЙўқ! Аллоҳгагина ибодат қил ва шукр этгувчилардан бўл!» – деб ваҳий қилинган'}, {'chapter': 39, 'verse': 67, 'text': 'وَمَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦ وَٱلۡأَرۡضُ جَمِيعٗا قَبۡضَتُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ وَٱلسَّمَٰوَٰتُ مَطۡوِيَّـٰتُۢ بِيَمِينِهِۦۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУлар Аллоҳнинг қадрини тўғри тақдирламадилар. Ҳолбуки, Ер борлигича Унинг сиқимидадир. Осмонлар эса, Унинг қўлига йиғилгандир. У зот улар ширк келтираётган нарсалардан пок ва юксакдир'}, {'chapter': 39, 'verse': 68, 'text': 'وَنُفِخَ فِي ٱلصُّورِ فَصَعِقَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۖ ثُمَّ نُفِخَ فِيهِ أُخۡرَىٰ فَإِذَا هُمۡ قِيَامٞ يَنظُرُونَ\nВа сурга пуфланди. Осмонлару ерда ким бўлса, қулаб ўлди. Магар Аллоҳ хоҳлаганларгина қолди. Сўнгра унга яна бир бор пуфланди. Бас, тўсатдан улар туриб интизор бўлурлар'}, {'chapter': 39, 'verse': 69, 'text': 'وَأَشۡرَقَتِ ٱلۡأَرۡضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلۡكِتَٰبُ وَجِاْيٓءَ بِٱلنَّبِيِّـۧنَ وَٱلشُّهَدَآءِ وَقُضِيَ بَيۡنَهُم بِٱلۡحَقِّ وَهُمۡ لَا يُظۡلَمُونَ\nВа ер Робби нури ила ёришди. Китоб қўйилди. Пайғамбарлар ва гувоҳлар келтирилди. Улар орасида ҳақ ила ҳукм чиқарилди. Уларга зулм қилинмади'}, {'chapter': 39, 'verse': 70, 'text': 'وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُوَ أَعۡلَمُ بِمَا يَفۡعَلُونَ\nВа ҳар бир жонга қилган амали (жазо ёки мукофоти) тўлиқ берилди. У зот улар қилаётган ишларни ўта яхши билгувчидир'}, {'chapter': 39, 'verse': 71, 'text': 'وَسِيقَ ٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ زُمَرًاۖ حَتَّىٰٓ إِذَا جَآءُوهَا فُتِحَتۡ أَبۡوَٰبُهَا وَقَالَ لَهُمۡ خَزَنَتُهَآ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَتۡلُونَ عَلَيۡكُمۡ ءَايَٰتِ رَبِّكُمۡ وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ بَلَىٰ وَلَٰكِنۡ حَقَّتۡ كَلِمَةُ ٱلۡعَذَابِ عَلَى ٱلۡكَٰفِرِينَ\nВа куфр келтирганлар жаҳаннамга гуруҳ-гуруҳ қилиб ҳайдалди. Токи унга етиб келган чоғларида, унинг эшиклари очилди ва унинг қўриқчилари уларга: «Сизларга ўзингиздан бўлган Пайғамбарлар келиб, Роббингиз оятларини тиловат қилиб бермадиларми? Сизларни ушбу кунингизга дучор бўлишингиздан огоҳлантирмадиларми?!» – дедилар. Улар: «Ҳа! Лекин азоб сўзи кофирлар устига ҳақ бўлгандир», дедилар'}, {'chapter': 39, 'verse': 72, 'text': 'قِيلَ ٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ\n«Жаҳаннам эшикларидан унда абадий қолгувчи бўлган ҳолингизда киринг», дейилди. Бас, мутакаббирларнинг жойи қандай ҳам ёмон бўлди'}, {'chapter': 39, 'verse': 73, 'text': 'وَسِيقَ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ إِلَى ٱلۡجَنَّةِ زُمَرًاۖ حَتَّىٰٓ إِذَا جَآءُوهَا وَفُتِحَتۡ أَبۡوَٰبُهَا وَقَالَ لَهُمۡ خَزَنَتُهَا سَلَٰمٌ عَلَيۡكُمۡ طِبۡتُمۡ فَٱدۡخُلُوهَا خَٰلِدِينَ\nВа Роббиларига тақво қилганлар жаннатга гуруҳ-гуруҳ қилиб олиб борилдилар. Токи унга етиб келган чоғларида, унинг эшиклари очилди ва унинг қўриқчилари уларга: «Сизга саломлар бўлсин! Хуш келдингиз! Бас, унга абадий қолгувчи бўлган ҳолингизда киринг», дедилар'}, {'chapter': 39, 'verse': 74, 'text': 'وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي صَدَقَنَا وَعۡدَهُۥ وَأَوۡرَثَنَا ٱلۡأَرۡضَ نَتَبَوَّأُ مِنَ ٱلۡجَنَّةِ حَيۡثُ نَشَآءُۖ فَنِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ\nУлар: «Бизларга берган ваъдасини рост қилган ва бизга бу ерни мерос қилиб берган Аллоҳга ҳамдлар бўлсин. Энди ундан хоҳлаган жойимизни оламиз», дедилар. Бас, амал қилувчиларнинг ажри қандай ҳам яхши бўлди'}, {'chapter': 39, 'verse': 75, 'text': 'وَتَرَى ٱلۡمَلَـٰٓئِكَةَ حَآفِّينَ مِنۡ حَوۡلِ ٱلۡعَرۡشِ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡۚ وَقُضِيَ بَيۡنَهُم بِٱلۡحَقِّۚ وَقِيلَ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nВа фаришталарни Аршни ўраб олиб Роббиларига ҳамд ила тасбиҳ айтаётган ҳолда кўрурсан. Улар орасида ҳақ ила ҳукм қилинди ва: «Оламларнинг Робби Аллоҳга ҳамду сано бўлсин!» – дейилди'}], '40': [{'chapter': 40, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 40, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\nБу китобнинг нозил қилиниши азийзу ўта илмли Аллоҳ томонидандир'}, {'chapter': 40, 'verse': 3, 'text': 'غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوۡبِ شَدِيدِ ٱلۡعِقَابِ ذِي ٱلطَّوۡلِۖ لَآ إِلَٰهَ إِلَّا هُوَۖ إِلَيۡهِ ٱلۡمَصِيرُ\nУ, гуноҳни мағфират қилгувчи, тавбани қабул этгувчи, иқоби шиддатли ва фазлу карам соҳиби бўлган Зотдир. Ундан ўзга ибодатга сазовор зот йўқ. Қайтиш Унинг Ўзигагинадир'}, {'chapter': 40, 'verse': 4, 'text': 'مَا يُجَٰدِلُ فِيٓ ءَايَٰتِ ٱللَّهِ إِلَّا ٱلَّذِينَ كَفَرُواْ فَلَا يَغۡرُرۡكَ تَقَلُّبُهُمۡ فِي ٱلۡبِلَٰدِ\nАллоҳнинг оятлари ҳақида фақатгина куфр келтирганлар талашиб-тортишурлар. Бас, уларнинг юртларда айланиб юришлари сени алдаб қўймасин'}, {'chapter': 40, 'verse': 5, 'text': 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَٱلۡأَحۡزَابُ مِنۢ بَعۡدِهِمۡۖ وَهَمَّتۡ كُلُّ أُمَّةِۭ بِرَسُولِهِمۡ لِيَأۡخُذُوهُۖ وَجَٰدَلُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّ فَأَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ\nУлардан олдин Нуҳ қавми ва улар кетидан келган фирқалар ҳам ёлғончи қилган эдилар. Ҳар уммат ўз Пайғамбарини тутишга қасд қилди ва ҳақни йўқотиш учун ботил ила мужодала этди. Бас, Мен уларни тутдим. Бас, Менинг иқобим қандай бўлди'}, {'chapter': 40, 'verse': 6, 'text': 'وَكَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ أَصۡحَٰبُ ٱلنَّارِ\nКуфр келтирганларга ҳам Роббингнинг (азоб) сўзи худди ана шундай ҳақ бўлди. Албатта, улар дўзах эгаларидир'}, {'chapter': 40, 'verse': 7, 'text': 'ٱلَّذِينَ يَحۡمِلُونَ ٱلۡعَرۡشَ وَمَنۡ حَوۡلَهُۥ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيُؤۡمِنُونَ بِهِۦ وَيَسۡتَغۡفِرُونَ لِلَّذِينَ ءَامَنُواْۖ رَبَّنَا وَسِعۡتَ كُلَّ شَيۡءٖ رَّحۡمَةٗ وَعِلۡمٗا فَٱغۡفِرۡ لِلَّذِينَ تَابُواْ وَٱتَّبَعُواْ سَبِيلَكَ وَقِهِمۡ عَذَابَ ٱلۡجَحِيمِ\nАршни кўтариб турганлар ва унинг атрофидагилар Роббиларига ҳамд ила тасбиҳ айтурлар, Унга иймон келтирурлар ва иймон келтирганларга мағфират сўраб: «Эй Роббимиз, раҳматинг ва илминг ила ҳар бир нарсани қуршагансан, бас, тавба қилиб, йўлингга эргашганларни мағфират қилгин ва уларни дўзах азобидан сақлагин'}, {'chapter': 40, 'verse': 8, 'text': 'رَبَّنَا وَأَدۡخِلۡهُمۡ جَنَّـٰتِ عَدۡنٍ ٱلَّتِي وَعَدتَّهُمۡ وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّـٰتِهِمۡۚ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nЭй Роббимиз, уларни ҳам, оталаридан, жуфтларидан ва зурриётларидан аҳли солиҳ бўлганларини ҳам Ўзинг уларга ваъда қилган адн жаннатларига киритгин. Албатта, Сенинг Ўзинг азийзу ўта ҳикматли зотсан'}, {'chapter': 40, 'verse': 9, 'text': 'وَقِهِمُ ٱلسَّيِّـَٔاتِۚ وَمَن تَقِ ٱلسَّيِّـَٔاتِ يَوۡمَئِذٖ فَقَدۡ رَحِمۡتَهُۥۚ وَذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nВа уларни ёмонликлардан сақла, ўша куни кимни ёмонликлардан сақласанг, батаҳқиқ, унга раҳим қилибсан. Ана ўша буюк ютуқдир», (дерлар)'}, {'chapter': 40, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ يُنَادَوۡنَ لَمَقۡتُ ٱللَّهِ أَكۡبَرُ مِن مَّقۡتِكُمۡ أَنفُسَكُمۡ إِذۡ تُدۡعَوۡنَ إِلَى ٱلۡإِيمَٰنِ فَتَكۡفُرُونَ\nАлбатта, куфр келтирганларга: «Иймонга чақирилганингизда куфр келтирган чоғингиздаги Аллоҳнинг ғазаби сизнинг ўзингизга (ҳозирги) ғазабингиздан каттароқдир», деб нидо қилинур'}, {'chapter': 40, 'verse': 11, 'text': 'قَالُواْ رَبَّنَآ أَمَتَّنَا ٱثۡنَتَيۡنِ وَأَحۡيَيۡتَنَا ٱثۡنَتَيۡنِ فَٱعۡتَرَفۡنَا بِذُنُوبِنَا فَهَلۡ إِلَىٰ خُرُوجٖ مِّن سَبِيلٖ\nУлар: «Эй Роббимиз, Сен бизни икки марта ўлдирдинг, икки марта тирилтирдинг. Бас, биз гуноҳларимизни эътироф этдик. Энди чиқишнинг бирон йўли борми?» – дедилар'}, {'chapter': 40, 'verse': 12, 'text': 'ذَٰلِكُم بِأَنَّهُۥٓ إِذَا دُعِيَ ٱللَّهُ وَحۡدَهُۥ كَفَرۡتُمۡ وَإِن يُشۡرَكۡ بِهِۦ تُؤۡمِنُواْۚ فَٱلۡحُكۡمُ لِلَّهِ ٱلۡعَلِيِّ ٱلۡكَبِيرِ\nБундай бўлишингиз Аллоҳнинг ёлғиз Ўзига даъват қилинганида, куфр келтирганингиз, Унга ширк келтирилса, иймон келтирганингиз учундир. Бас, ҳукм олий ва улуғ Аллоҳникидир'}, {'chapter': 40, 'verse': 13, 'text': 'هُوَ ٱلَّذِي يُرِيكُمۡ ءَايَٰتِهِۦ وَيُنَزِّلُ لَكُم مِّنَ ٱلسَّمَآءِ رِزۡقٗاۚ وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ\nУнинг Ўзи сизга оят (белги)ларини кўрсатадиган, осмондан сизга ризқ туширадиган Зотдир. Фақат қайтадиган кишигина англар'}, {'chapter': 40, 'verse': 14, 'text': 'فَٱدۡعُواْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ\nБас, агар кофирлар ёқтирмасалар ҳам, Аллоҳга динни холис қилган ҳолингизда ёлворинг'}, {'chapter': 40, 'verse': 15, 'text': 'رَفِيعُ ٱلدَّرَجَٰتِ ذُو ٱلۡعَرۡشِ يُلۡقِي ٱلرُّوحَ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦ لِيُنذِرَ يَوۡمَ ٱلتَّلَاقِ\nУ, даражаси юксак, Арш эгаси бўлган Зотдир. У, Ўз амри ила бандаларидан кимни хоҳласа, ўшанга мулоқот кунидан огоҳлантириши учун руҳни туширур'}, {'chapter': 40, 'verse': 16, 'text': 'يَوۡمَ هُم بَٰرِزُونَۖ لَا يَخۡفَىٰ عَلَى ٱللَّهِ مِنۡهُمۡ شَيۡءٞۚ لِّمَنِ ٱلۡمُلۡكُ ٱلۡيَوۡمَۖ لِلَّهِ ٱلۡوَٰحِدِ ٱلۡقَهَّارِ\nЎша кунда улар зоҳир бўлурлар. Аллоҳга улардан бирон нарса махфий қолмас. Бугун мулк кимникидир?! Яккаю ёлғиз, Қаҳҳор Аллоҳникидир'}, {'chapter': 40, 'verse': 17, 'text': 'ٱلۡيَوۡمَ تُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡۚ لَا ظُلۡمَ ٱلۡيَوۡمَۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nБугунги кунда ҳар бир жон ўзи қилган касбга яраша жазо олур. Бугунга кунда зулм йўқдур. Албатта, Аллоҳ ҳисобни тез қилгувчи Зотдир'}, {'chapter': 40, 'verse': 18, 'text': 'وَأَنذِرۡهُمۡ يَوۡمَ ٱلۡأٓزِفَةِ إِذِ ٱلۡقُلُوبُ لَدَى ٱلۡحَنَاجِرِ كَٰظِمِينَۚ مَا لِلظَّـٰلِمِينَ مِنۡ حَمِيمٖ وَلَا شَفِيعٖ يُطَاعُ\nСен уларни яқинлашгувчи Кундан огоҳлантир. Ўшанда жонлар халқумга келиб, ғам-ғуссага тўлиб турарлар. Золимлар учун ҳеч бир дўст ҳам, итоат қилинадиган шафоатчи ҳам йўқ'}, {'chapter': 40, 'verse': 19, 'text': 'يَعۡلَمُ خَآئِنَةَ ٱلۡأَعۡيُنِ وَمَا تُخۡفِي ٱلصُّدُورُ\nУ зот кўзларнинг хиёнатини ҳам, диллар махфий тутган нарсаларни ҳам билур'}, {'chapter': 40, 'verse': 20, 'text': 'وَٱللَّهُ يَقۡضِي بِٱلۡحَقِّۖ وَٱلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَقۡضُونَ بِشَيۡءٍۗ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\nАллоҳ ҳақ ила ҳукм қилур. Ундан ўзга илтижо қилаётганлари эса, ҳеч нарса билан ҳукм қилмаслар. Албатта, Аллоҳнинг Ўзи ўта эшитгувчи ва ўта кўргувчи Зотдир'}, {'chapter': 40, 'verse': 21, 'text': '۞أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ كَانُواْ مِن قَبۡلِهِمۡۚ كَانُواْ هُمۡ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَءَاثَارٗا فِي ٱلۡأَرۡضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقٖ\nАхир, улар ер юзида юриб, ўзларидан олдин ўтганларнинг оқибати қандай бўлганига назар солмайдиларми?! Ҳолбуки, ўшалар улардан кўра қувватлироқ ва ер юзида асарлари кўпроқ бўлган эдилар. Бас, Аллоҳ ўшаларни гуноҳлари сабабли тутди. Уларга Аллоҳдан ўзга бир сақловчи бўлмади'}, {'chapter': 40, 'verse': 22, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ كَانَت تَّأۡتِيهِمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَكَفَرُواْ فَأَخَذَهُمُ ٱللَّهُۚ إِنَّهُۥ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ\nБундай бўлишининг сабаби шуки, уларга Пайғамбарлари аниқ ҳужжатлар билан келар эдилар, улар эса, куфр келтирдилар. Бас, Аллоҳ уларни тутди. Албатта, У кучли ва иқоби шиддатли Зотдир'}, {'chapter': 40, 'verse': 23, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ\nБатаҳқиқ, Биз Мусони Ўз оятларимиз ва очиқ ҳужжат ила юбордик'}, {'chapter': 40, 'verse': 24, 'text': 'إِلَىٰ فِرۡعَوۡنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُواْ سَٰحِرٞ كَذَّابٞ\nФиръавнга, Ҳомонга ва Қорунга. Бас, улар: «(Бу) сеҳргар ва каззоб», дедилар'}, {'chapter': 40, 'verse': 25, 'text': 'فَلَمَّا جَآءَهُم بِٱلۡحَقِّ مِنۡ عِندِنَا قَالُواْ ٱقۡتُلُوٓاْ أَبۡنَآءَ ٱلَّذِينَ ءَامَنُواْ مَعَهُۥ وَٱسۡتَحۡيُواْ نِسَآءَهُمۡۚ وَمَا كَيۡدُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ\nМусо уларга Бизнинг ҳузуримиздан ҳақ ила келган чоғда, улар: «У билан бирга иймон келтирганларнинг ўғилларини қатл қилинглар, қизларини тирик қолдиринглар», дедилар. Кофирларнинг макр-ҳийласи залолатдан бошқа нарса эмас'}, {'chapter': 40, 'verse': 26, 'text': 'وَقَالَ فِرۡعَوۡنُ ذَرُونِيٓ أَقۡتُلۡ مُوسَىٰ وَلۡيَدۡعُ رَبَّهُۥٓۖ إِنِّيٓ أَخَافُ أَن يُبَدِّلَ دِينَكُمۡ أَوۡ أَن يُظۡهِرَ فِي ٱلۡأَرۡضِ ٱلۡفَسَادَ\nФиръавн: «Мени қўйинглар, Мусони ўлдирай. Майли, Роббига дуо қилсин. Албатта, мен унинг динингизни алмаштиришидан ва ер юзида фасод чиқаришидан қўрқмоқдаман», деди'}, {'chapter': 40, 'verse': 27, 'text': 'وَقَالَ مُوسَىٰٓ إِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٖ لَّا يُؤۡمِنُ بِيَوۡمِ ٱلۡحِسَابِ\nМусо: «Албатта, мен Роббим ва Роббингиз бўлган Зотдан ҳисоб кунига иймон келтирмайдиган ҳар бир мутакаббирдан паноҳ сўрайман», деди'}, {'chapter': 40, 'verse': 28, 'text': 'وَقَالَ رَجُلٞ مُّؤۡمِنٞ مِّنۡ ءَالِ فِرۡعَوۡنَ يَكۡتُمُ إِيمَٰنَهُۥٓ أَتَقۡتُلُونَ رَجُلًا أَن يَقُولَ رَبِّيَ ٱللَّهُ وَقَدۡ جَآءَكُم بِٱلۡبَيِّنَٰتِ مِن رَّبِّكُمۡۖ وَإِن يَكُ كَٰذِبٗا فَعَلَيۡهِ كَذِبُهُۥۖ وَإِن يَكُ صَادِقٗا يُصِبۡكُم بَعۡضُ ٱلَّذِي يَعِدُكُمۡۖ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ مُسۡرِفٞ كَذَّابٞ\nФиръавн аҳлидан бўлган, иймонини беркитиб юрадиган бир мўмин киши: «Бир одамни, Аллоҳ Роббимдир, дегани учун ўлдирасизларми?! Ҳолбуки, у сизга Роббингиздан очиқ-ойдин ҳужжатлар ила келмишдир. Агар у ёлғончи бўлса, ёлғони ўзига зарар. Агар ростгўй бўлса, унда у ваъда қилаётган баъзи мусийбатлар сизга етар. Албатта, Аллоҳ исрофчи ва каззоб бўлган шахсни ҳидоят қилмас'}, {'chapter': 40, 'verse': 29, 'text': 'يَٰقَوۡمِ لَكُمُ ٱلۡمُلۡكُ ٱلۡيَوۡمَ ظَٰهِرِينَ فِي ٱلۡأَرۡضِ فَمَن يَنصُرُنَا مِنۢ بَأۡسِ ٱللَّهِ إِن جَآءَنَاۚ قَالَ فِرۡعَوۡنُ مَآ أُرِيكُمۡ إِلَّا مَآ أَرَىٰ وَمَآ أَهۡدِيكُمۡ إِلَّا سَبِيلَ ٱلرَّشَادِ\nЭй қавмим, бугунги кунда мулк сизникидир. Ер юзида юзага чиқиб турибсизлар. Агар бизга Аллоҳнинг азоби келса, унда бизга ким ёрдам берар?!» – деди. Фиръавн: «Мен сизга ўзим кўраётган нарсадан бошқани кўрсатмасман ва сизларни тўғри йўлдан бошқага йўлламасман», деди'}, {'chapter': 40, 'verse': 30, 'text': 'وَقَالَ ٱلَّذِيٓ ءَامَنَ يَٰقَوۡمِ إِنِّيٓ أَخَافُ عَلَيۡكُم مِّثۡلَ يَوۡمِ ٱلۡأَحۡزَابِ\nИймон келтирган киши: «Эй қавмим, албатта, мен сизга ҳам бошқа фирқалар кунига ўхшаш кун'}, {'chapter': 40, 'verse': 31, 'text': 'مِثۡلَ دَأۡبِ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمۡۚ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعِبَادِ\nНуҳ қавми, Од, Самуд ва улардан кейингилар ҳолига ўхшаш ҳол бўлишидан қўрқурман. Аллоҳ бандаларга зулмни ирода қилмас'}, {'chapter': 40, 'verse': 32, 'text': 'وَيَٰقَوۡمِ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ يَوۡمَ ٱلتَّنَادِ\nЭй қавмим, албатта, мен сизларга нидолашув кунидан қўрқурман'}, {'chapter': 40, 'verse': 33, 'text': 'يَوۡمَ تُوَلُّونَ مُدۡبِرِينَ مَا لَكُم مِّنَ ٱللَّهِ مِنۡ عَاصِمٖۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ\nУ кунда ортга қараб қочурсиз. Сиз учун Аллоҳдан ҳеч бир қутқаргувчи бўлмас. Кимни Аллоҳ залолатга кетказса, бас, унга ҳеч бир ҳидоятчи бўлмас'}, {'chapter': 40, 'verse': 34, 'text': 'وَلَقَدۡ جَآءَكُمۡ يُوسُفُ مِن قَبۡلُ بِٱلۡبَيِّنَٰتِ فَمَا زِلۡتُمۡ فِي شَكّٖ مِّمَّا جَآءَكُم بِهِۦۖ حَتَّىٰٓ إِذَا هَلَكَ قُلۡتُمۡ لَن يَبۡعَثَ ٱللَّهُ مِنۢ بَعۡدِهِۦ رَسُولٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنۡ هُوَ مُسۡرِفٞ مُّرۡتَابٌ\nБатаҳқиқ, бундан олдин Юсуф очиқ-ойдин ҳужжатлар ила келди. Бас, сиз у ўзингизга келтирган нарсага шак келтириб, туриб олдингиз. Ниҳоят, у вафот этган пайтда: «Аллоҳ ундан кейин Пайғамбар юбормас», дедингиз. Аллоҳ шундай қилиб, исрофчи ва шубҳачи бўлган кимсани залолатга кетказди'}, {'chapter': 40, 'verse': 35, 'text': 'ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡۖ كَبُرَ مَقۡتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلۡبِ مُتَكَبِّرٖ جَبَّارٖ\nАллоҳнинг оятлари ҳақида ўзларига келган ҳужжат-далилсиз талашиб-тортишадиганлар Аллоҳ томонидан ҳам, иймон келтирганлар томонидан ҳам қаттиқ ғазабга қолурлар. Аллоҳ шундай қилиб, ҳар бир мутакаббир ва зўравон қалбни муҳрлар», деди'}, {'chapter': 40, 'verse': 36, 'text': 'وَقَالَ فِرۡعَوۡنُ يَٰهَٰمَٰنُ ٱبۡنِ لِي صَرۡحٗا لَّعَلِّيٓ أَبۡلُغُ ٱلۡأَسۡبَٰبَ\nФиръавн деди: «Эй, Ҳомон, менга бир баланд қаср бино қил, шояд, сабабларга етсам'}, {'chapter': 40, 'verse': 37, 'text': 'أَسۡبَٰبَ ٱلسَّمَٰوَٰتِ فَأَطَّلِعَ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ كَٰذِبٗاۚ وَكَذَٰلِكَ زُيِّنَ لِفِرۡعَوۡنَ سُوٓءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِيلِۚ وَمَا كَيۡدُ فِرۡعَوۡنَ إِلَّا فِي تَبَابٖ\nОсмонларнинг сабабларига етсам, сўнгра Мусонинг Илоҳига боқсам. Албатта, мен уни ёлғончи деб, гумон қилмоқдаман». Шундай қилиб, Фиръавнга унинг ёмон амали зийнатланди ва у йўлдан тўсилди. Фиръавннинг макр-ҳийласи зиёнкорликдан бошқа нарса эмас'}, {'chapter': 40, 'verse': 38, 'text': 'وَقَالَ ٱلَّذِيٓ ءَامَنَ يَٰقَوۡمِ ٱتَّبِعُونِ أَهۡدِكُمۡ سَبِيلَ ٱلرَّشَادِ\nИймон келтирган киши: «Эй қавмим, менга эргашинглар, сизни тўғри йўлга ҳидоят қилурман'}, {'chapter': 40, 'verse': 39, 'text': 'يَٰقَوۡمِ إِنَّمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا مَتَٰعٞ وَإِنَّ ٱلۡأٓخِرَةَ هِيَ دَارُ ٱلۡقَرَارِ\nЭй қавмим, бу дунё ҳаёти вақтинчалик бир матоҳ, холос. Албатта, охират, ўшанинг ўзи барқарорлик диёридир'}, {'chapter': 40, 'verse': 40, 'text': 'مَنۡ عَمِلَ سَيِّئَةٗ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَاۖ وَمَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ يُرۡزَقُونَ فِيهَا بِغَيۡرِ حِسَابٖ\nКим бирор ёмонлик қилса, фақат ўшанга яраша жазо олади, холос. Эркакдир, аёлдир, ҳар ким мўмин бўлган ҳолида солиҳ амал қилса, бас, ана ўшалар жаннатга кирурлар, у ерда ҳисобсиз ризқланурлар'}, {'chapter': 40, 'verse': 41, 'text': '۞وَيَٰقَوۡمِ مَا لِيٓ أَدۡعُوكُمۡ إِلَى ٱلنَّجَوٰةِ وَتَدۡعُونَنِيٓ إِلَى ٱلنَّارِ\nЭй қавмим, нечун мен сизни нажотга чақирсам, сиз мени дўзахга чақирурсиз'}, {'chapter': 40, 'verse': 42, 'text': 'تَدۡعُونَنِي لِأَكۡفُرَ بِٱللَّهِ وَأُشۡرِكَ بِهِۦ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞ وَأَنَا۠ أَدۡعُوكُمۡ إِلَى ٱلۡعَزِيزِ ٱلۡغَفَّـٰرِ\nСиз мени Аллоҳга куфр келтиришимга ва ўзим билмаган нарсани Унга ширк келтиришимга чақирмоқдасиз. Ҳолбуки, мен сизни азийзу ўта мағфиратли Зотга чақирмоқдаман'}, {'chapter': 40, 'verse': 43, 'text': 'لَا جَرَمَ أَنَّمَا تَدۡعُونَنِيٓ إِلَيۡهِ لَيۡسَ لَهُۥ دَعۡوَةٞ فِي ٱلدُّنۡيَا وَلَا فِي ٱلۡأٓخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى ٱللَّهِ وَأَنَّ ٱلۡمُسۡرِفِينَ هُمۡ أَصۡحَٰبُ ٱلنَّارِ\nҲеч шубҳа йўқки, албатта, сиз мени чақираётган нарсанинг дунёда ҳам, охиратда ҳам даъватга ҳаққи йўқдир. Албатта, қайтиб бораримиз Аллоҳгадир. Албатта, исрофчилар, айни ўшалар дўзах эгаларидир'}, {'chapter': 40, 'verse': 44, 'text': 'فَسَتَذۡكُرُونَ مَآ أَقُولُ لَكُمۡۚ وَأُفَوِّضُ أَمۡرِيٓ إِلَى ٱللَّهِۚ إِنَّ ٱللَّهَ بَصِيرُۢ بِٱلۡعِبَادِ\nҲали, мен сизга айтаётган гапларни эслайсизлар. Мен ишимни Аллоҳнинг Ўзига топширдим. Албатта, Аллоҳ бандаларини аниқ кўриб тургувчидир», деди'}, {'chapter': 40, 'verse': 45, 'text': 'فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُواْۖ وَحَاقَ بِـَٔالِ فِرۡعَوۡنَ سُوٓءُ ٱلۡعَذَابِ\nБас, Аллоҳ уни макрлари ёмонликларидан сақлади ва Фиръавн аҳлини ёмон азоб ўраб олди'}, {'chapter': 40, 'verse': 46, 'text': 'ٱلنَّارُ يُعۡرَضُونَ عَلَيۡهَا غُدُوّٗا وَعَشِيّٗاۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ أَدۡخِلُوٓاْ ءَالَ فِرۡعَوۡنَ أَشَدَّ ٱلۡعَذَابِ\nУ оловдир. Унга эртаю кеч кўндаланг қилинурлар. Соат (қиёмат) қоим бўлганида эса: «Фиръавн аҳлини энг ашаддий азобга киритинглар», дейилур'}, {'chapter': 40, 'verse': 47, 'text': 'وَإِذۡ يَتَحَآجُّونَ فِي ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَـٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا نَصِيبٗا مِّنَ ٱلنَّارِ\nЎшанда улар дўзахда бир-бирлари ила хусумат қилурлар. Бас, заифлар мутакаббирлик қилганларга: «Албатта, биз сизга эргашувчи бўлган эдик. Энди сиз бизнинг дўзах насибамиздан бирор бўлагини дафъ қиласизларми?!» – дерлар'}, {'chapter': 40, 'verse': 48, 'text': 'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُلّٞ فِيهَآ إِنَّ ٱللَّهَ قَدۡ حَكَمَ بَيۡنَ ٱلۡعِبَادِ\nМутакаббирлик қилганлар: «Албатта ҳаммамиз унинг ичидамиз. Шубҳасиз, Аллоҳ бандалар орасида ҳукм чиқариб бўлди», дерлар'}, {'chapter': 40, 'verse': 49, 'text': 'وَقَالَ ٱلَّذِينَ فِي ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدۡعُواْ رَبَّكُمۡ يُخَفِّفۡ عَنَّا يَوۡمٗا مِّنَ ٱلۡعَذَابِ\nДўзахда турганлар жаҳаннам қўриқчиларига: «Роббингизга дуо қилинглар, биздан бирор кун азобни енгиллатсин», дедилар'}, {'chapter': 40, 'verse': 50, 'text': 'قَالُوٓاْ أَوَلَمۡ تَكُ تَأۡتِيكُمۡ رُسُلُكُم بِٱلۡبَيِّنَٰتِۖ قَالُواْ بَلَىٰۚ قَالُواْ فَٱدۡعُواْۗ وَمَا دُعَـٰٓؤُاْ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٍ\nУлар: «Сизга Пайғамбарларингиз очиқ-ойдин ҳужжатлар билан келмаган эдиларми?!» – дедилар. Булар: «Ҳа», дейишди. Улар: «Бас, дуо қилаверинглар. Кофирларнинг дуоси залолатдан бошқа нарса эмас», дедилар'}, {'chapter': 40, 'verse': 51, 'text': 'إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيَوۡمَ يَقُومُ ٱلۡأَشۡهَٰدُ\nАлбатта, Биз Пайғамбарларимизга ва иймон келтирганларга бу дунё ҳаётида ва гувоҳлар турган кунда ҳам нусрат берурмиз'}, {'chapter': 40, 'verse': 52, 'text': 'يَوۡمَ لَا يَنفَعُ ٱلظَّـٰلِمِينَ مَعۡذِرَتُهُمۡۖ وَلَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوٓءُ ٱلدَّارِ\nУ кунда золимларга маъзиратлари наф бермас. Уларга лаънат бўлғай ва уларга ёмон ҳовли жой бўлғай'}, {'chapter': 40, 'verse': 53, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡهُدَىٰ وَأَوۡرَثۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡكِتَٰبَ\nБатаҳқиқ, Биз Мусога ҳидоятни бердик ва Бани Исроилни Китобга ворис қилдик'}, {'chapter': 40, 'verse': 54, 'text': 'هُدٗى وَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ\nАқл эгаларига ҳидоятчи ва эслатма бўлсин учун'}, {'chapter': 40, 'verse': 55, 'text': 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ بِٱلۡعَشِيِّ وَٱلۡإِبۡكَٰرِ\nБас, сабр қил. Албатта, Аллоҳнинг ваъдаси ҳақдир. Гуноҳинг учун истиғфор айт. Эртаю кеч Роббингга ҳамд ила тасбиҳ айт'}, {'chapter': 40, 'verse': 56, 'text': 'إِنَّ ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡ إِن فِي صُدُورِهِمۡ إِلَّا كِبۡرٞ مَّا هُم بِبَٰلِغِيهِۚ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\nАлбатта, Аллоҳнинг оятлари ҳақида ўзларига келган ҳужжатсиз талашиб-тортишадиганларнинг дилларида кибрдан ўзга нарса йўқ. Улар унга етишгувчи эмаслар. Бас, Аллоҳдан паноҳ сўра. Албатта, Унинг Ўзи ўта эшитгувчи, ўта кўргувчи Зотдир'}, {'chapter': 40, 'verse': 57, 'text': 'لَخَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ أَكۡبَرُ مِنۡ خَلۡقِ ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nАлбатта, осмонлару ерни яратиш одамларни яратишдан каттароқдир. Лекин одамларнинг кўплари билмаслар'}, {'chapter': 40, 'verse': 58, 'text': 'وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَلَا ٱلۡمُسِيٓءُۚ قَلِيلٗا مَّا تَتَذَكَّرُونَ\nКўр билан кўрувчи баробар бўлмас. Иймон келтириб, солиҳ амаллар қилганлар билан ёмонлик қилгувчи ҳам. Жуда ҳам кам эслайсизлар'}, {'chapter': 40, 'verse': 59, 'text': 'إِنَّ ٱلسَّاعَةَ لَأٓتِيَةٞ لَّا رَيۡبَ فِيهَا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ\nАлбатта, соат (қиёмат)келгувчидир. Унда шубҳа йўқ. Лекин одамларнинг кўплари иймон келтирмаслар'}, {'chapter': 40, 'verse': 60, 'text': 'وَقَالَ رَبُّكُمُ ٱدۡعُونِيٓ أَسۡتَجِبۡ لَكُمۡۚ إِنَّ ٱلَّذِينَ يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِي سَيَدۡخُلُونَ جَهَنَّمَ دَاخِرِينَ\nРоббингиз: «Менга дуо қилинг, сизга ижобат қилурман. Албатта, Менинг ибодатимдан кибр қилганлар жаҳаннамга хору зор ҳолларида кирурлар», деди'}, {'chapter': 40, 'verse': 61, 'text': 'ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ\nАллоҳ сизга сукунат топишингиз учун кечани, кўрсатувчи этиб кундузни қилиб берган Зотдир. Албатта, Аллоҳ одамларга нисбатан фазлу карам соҳибидир. Лекин одамларнинг кўплари билмаслар'}, {'chapter': 40, 'verse': 62, 'text': 'ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ خَٰلِقُ كُلِّ شَيۡءٖ لَّآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ\nАна ўша Аллоҳ Роббингиздир. У ҳар бир нарсанинг яратгувчисидир. Ундан ўзга ибодатга сазовор зот йўқ. Бас, қаёққа бурилиб кетмоқдасиз'}, {'chapter': 40, 'verse': 63, 'text': 'كَذَٰلِكَ يُؤۡفَكُ ٱلَّذِينَ كَانُواْ بِـَٔايَٰتِ ٱللَّهِ يَجۡحَدُونَ\nАллоҳнинг оятларини инкор қиладиганлар ана шундай бурилиб кетурлар'}, {'chapter': 40, 'verse': 64, 'text': 'ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ قَرَارٗا وَٱلسَّمَآءَ بِنَآءٗ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡۖ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nАллоҳ сизга ерни қароргоҳ, осмонни том қилган ва сизларнинг сувратларингизни гўзал сувратда қилган, сизларга пок нарсалардан ризқ берган Зотдир. Ана ўша Аллоҳ Роббингиздир. Бас, оламларнинг Робби Аллоҳ баракотли (юксак) бўлди'}, {'chapter': 40, 'verse': 65, 'text': 'هُوَ ٱلۡحَيُّ لَآ إِلَٰهَ إِلَّا هُوَ فَٱدۡعُوهُ مُخۡلِصِينَ لَهُ ٱلدِّينَۗ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nУ зот тирикдир. Ундан ўзга ибодатга сазовор зот йўқ. Бас, Унга динни холис қилган ҳолда дуо қилинг. Оламларнинг Робби Аллоҳга ҳамдлар бўлсин'}, {'chapter': 40, 'verse': 66, 'text': '۞قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَآءَنِيَ ٱلۡبَيِّنَٰتُ مِن رَّبِّي وَأُمِرۡتُ أَنۡ أُسۡلِمَ لِرَبِّ ٱلۡعَٰلَمِينَ\nСен: «Албатта, мен ўзимга Роббимдан очиқ-ойдин ҳужжатлар келган пайтда сиз илтижо этаётган нарсаларга ибодат қилишдан қайтарилганман ва оламларнинг Роббига таслим бўлишга амр этилдим», деб айт'}, {'chapter': 40, 'verse': 67, 'text': 'هُوَ ٱلَّذِي خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ مِنۡ عَلَقَةٖ ثُمَّ يُخۡرِجُكُمۡ طِفۡلٗا ثُمَّ لِتَبۡلُغُوٓاْ أَشُدَّكُمۡ ثُمَّ لِتَكُونُواْ شُيُوخٗاۚ وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبۡلُۖ وَلِتَبۡلُغُوٓاْ أَجَلٗا مُّسَمّٗى وَلَعَلَّكُمۡ تَعۡقِلُونَ\nУ сизни тупроқдан, сўнгра нутфадан, сўнгра (ёпишқоқ) лахта қондан яратган Зотдир. Сўнгра У сизни бувак ҳолингизда чиқарур. Сўнгра камолга етишингиз учун, сўнгра қари бўлишингиз учун (улғайтирур). Сизнинг ичингизда ундан олдин вафот этадиганлар ҳам бўлур. (Булар) белгиланган ажалга етишингиз учундир. Шоядки, ақл юритсангиз'}, {'chapter': 40, 'verse': 68, 'text': 'هُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُۖ فَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nУ зот тирилтирур ва ўлдирур. Қачонки бирор ишни хоҳласа, «Бўл!» дер ва у бўлур'}, {'chapter': 40, 'verse': 69, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ أَنَّىٰ يُصۡرَفُونَ\nАллоҳнинг оятлари ҳақида талашиб-тортишадиганларни кўрмайсанми?! Қаёққа бурилиб кетмоқдалар'}, {'chapter': 40, 'verse': 70, 'text': 'ٱلَّذِينَ كَذَّبُواْ بِٱلۡكِتَٰبِ وَبِمَآ أَرۡسَلۡنَا بِهِۦ رُسُلَنَاۖ فَسَوۡفَ يَعۡلَمُونَ\nКитобни ва Биз Пайғамбарларимиз ила юборган нарсани ёлғонга чиқарганлар тезда билурлар'}, {'chapter': 40, 'verse': 71, 'text': 'إِذِ ٱلۡأَغۡلَٰلُ فِيٓ أَعۡنَٰقِهِمۡ وَٱلسَّلَٰسِلُ يُسۡحَبُونَ\nЎшанда улар бўйинларида кишанлар ва занжирлар ила тортилурлар'}, {'chapter': 40, 'verse': 72, 'text': 'فِي ٱلۡحَمِيمِ ثُمَّ فِي ٱلنَّارِ يُسۡجَرُونَ\nЎта қайноқ сувга, сўнгра оловда куйдирилурлар'}, {'chapter': 40, 'verse': 73, 'text': 'ثُمَّ قِيلَ لَهُمۡ أَيۡنَ مَا كُنتُمۡ تُشۡرِكُونَ\nСўнгра уларга: «Ширк келтириб юрган нарсаларингиз қани'}, {'chapter': 40, 'verse': 74, 'text': 'مِن دُونِ ٱللَّهِۖ قَالُواْ ضَلُّواْ عَنَّا بَل لَّمۡ نَكُن نَّدۡعُواْ مِن قَبۡلُ شَيۡـٔٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ ٱلۡكَٰفِرِينَ\nАллоҳдан ўзга?!» – дейилди. Улар: «Биздан йўқолиб қолдилар. Йўқ. Олдин ҳеч нарсага илтижо қилмаган эдик», дедилар. Аллоҳ кофирларни шундай қилиб залолатга кетказур'}, {'chapter': 40, 'verse': 75, 'text': 'ذَٰلِكُم بِمَا كُنتُمۡ تَفۡرَحُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَمۡرَحُونَ\nМана шулар ер юзида ноҳақдан шодланиб юрганингиз ва кибру ҳавога берилиб юрганингиз учундир'}, {'chapter': 40, 'verse': 76, 'text': 'ٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ\nЖаҳаннам эшикларидан унда абадий қолгувчи бўлган ҳолингизда киринглар. Мутакаббирларнинг жойи нақадар ёмон'}, {'chapter': 40, 'verse': 77, 'text': 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۚ فَإِمَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا يُرۡجَعُونَ\nБас, сабр қил. Албатта, Аллоҳнинг ваъдаси ҳақдир. Энди Биз ёки сенга уларга ваъда қилган нарсаларимизнинг баъзиларини кўрсатурмиз ёки сени вафот эттирурмиз. Бас, Бизгагина қайтарилурлар'}, {'chapter': 40, 'verse': 78, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ مِنۡهُم مَّن قَصَصۡنَا عَلَيۡكَ وَمِنۡهُم مَّن لَّمۡ نَقۡصُصۡ عَلَيۡكَۗ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ فَإِذَا جَآءَ أَمۡرُ ٱللَّهِ قُضِيَ بِٱلۡحَقِّ وَخَسِرَ هُنَالِكَ ٱلۡمُبۡطِلُونَ\nБатаҳқиқ, Биз сендан олдин ҳам Пайғамбарлар юборганмиз. Улардан кимнингдир қиссасини сенга айтдик ва улардан кимнингдир қиссасини сенга айтмадик. Ҳеч Пайғамбар Аллоҳнинг изнисиз оят (мўъжиза) келтирган эмас. Бас, қачонки Аллоҳнинг амри келганида, ҳақ ила ҳукм чиқарилур ва ўшанда ботил иш қилиб юргувчилар зиён тортурлар'}, {'chapter': 40, 'verse': 79, 'text': 'ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَنۡعَٰمَ لِتَرۡكَبُواْ مِنۡهَا وَمِنۡهَا تَأۡكُلُونَ\nАллоҳ сизларга чорва ҳайвонларини улардан баъзиларини минишингиз учун яратди. Улардан баъзиларини ерсизлар'}, {'chapter': 40, 'verse': 80, 'text': 'وَلَكُمۡ فِيهَا مَنَٰفِعُ وَلِتَبۡلُغُواْ عَلَيۡهَا حَاجَةٗ فِي صُدُورِكُمۡ وَعَلَيۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ\nСиз учун уларда манфаатлар бор. Ва улар устида дилларингиздаги ҳожатларга етишишингиз учундир. Уларда ва кемаларда ташилурсиз'}, {'chapter': 40, 'verse': 81, 'text': 'وَيُرِيكُمۡ ءَايَٰتِهِۦ فَأَيَّ ءَايَٰتِ ٱللَّهِ تُنكِرُونَ\nВа У зот сизларга Ўз оят(белги)ларини кўрсатур. Бас, Аллоҳнинг қайси оят(белги)ларини инкор қилурсизлар'}, {'chapter': 40, 'verse': 82, 'text': 'أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَانُوٓاْ أَكۡثَرَ مِنۡهُمۡ وَأَشَدَّ قُوَّةٗ وَءَاثَارٗا فِي ٱلۡأَرۡضِ فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ\nАхир, улар ер юзида юриб, ўзларидан олдингиларнинг оқибати қандай бўлганига назар солмайдиларми?! Ҳолбуки, ўшалар булардан кўра қувватлироқ ва ер юзида асарлари кўпроқ бўлган эдилар. Бас, уларга касб қилган нарсалари асқотмади'}, {'chapter': 40, 'verse': 83, 'text': 'فَلَمَّا جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَرِحُواْ بِمَا عِندَهُم مِّنَ ٱلۡعِلۡمِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nҚачонки уларга Пайғамбарлари очиқ-ойдин ҳужжатлар билан келганларида, улар ўзларидаги илм билан мақтандилар. Ва уларни ўзлари истеҳзо қилган нарса ўраб олди'}, {'chapter': 40, 'verse': 84, 'text': 'فَلَمَّا رَأَوۡاْ بَأۡسَنَا قَالُوٓاْ ءَامَنَّا بِٱللَّهِ وَحۡدَهُۥ وَكَفَرۡنَا بِمَا كُنَّا بِهِۦ مُشۡرِكِينَ\nҚачонки азобимизни кўрганларида: «Биз Аллоҳнинг ёлғиз Ўзига иймон келтирдик ва Унга шерик қилган нарсаларимизга куфр келтирдик», дедилар'}, {'chapter': 40, 'verse': 85, 'text': 'فَلَمۡ يَكُ يَنفَعُهُمۡ إِيمَٰنُهُمۡ لَمَّا رَأَوۡاْ بَأۡسَنَاۖ سُنَّتَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ فِي عِبَادِهِۦۖ وَخَسِرَ هُنَالِكَ ٱلۡكَٰفِرُونَ\nБизнинг азобимизни кўрганларидаги иймонлари уларга манфаат бера олмас эди. Аллоҳнинг бандаларига жорий бўлиб келган суннати шу. Ана ўшанда кофирлар зиён кўрдилар'}], '41': [{'chapter': 41, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 41, 'verse': 2, 'text': 'تَنزِيلٞ مِّنَ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\n(Бу) Роҳман ва Роҳийм томонидан нозил қилингандир'}, {'chapter': 41, 'verse': 3, 'text': 'كِتَٰبٞ فُصِّلَتۡ ءَايَٰتُهُۥ قُرۡءَانًا عَرَبِيّٗا لِّقَوۡمٖ يَعۡلَمُونَ\nБиладиган қавмлар учун оятлари муфассал баён қилинган китобдир'}, {'chapter': 41, 'verse': 4, 'text': 'بَشِيرٗا وَنَذِيرٗا فَأَعۡرَضَ أَكۡثَرُهُمۡ فَهُمۡ لَا يَسۡمَعُونَ\nХушхабар бергувчи ва огоҳлантиргувчи бўлган ҳолидадир. Бас, кўплари ундан юз ўгирдилар. Энди улар эшитмаслар'}, {'chapter': 41, 'verse': 5, 'text': 'وَقَالُواْ قُلُوبُنَا فِيٓ أَكِنَّةٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ وَفِيٓ ءَاذَانِنَا وَقۡرٞ وَمِنۢ بَيۡنِنَا وَبَيۡنِكَ حِجَابٞ فَٱعۡمَلۡ إِنَّنَا عَٰمِلُونَ\nУлар: «Қалбларимиз сен даъват қилаётган нарсадан ғилофлардадир, қулоқларимизда оғирлик бор, сен билан бизнинг орамизда эса, парда бор. Бас, сен амалингни қилавер, биз ҳам, албатта, ўз амалимизни қилгувчилармиз», дедилар'}, {'chapter': 41, 'verse': 6, 'text': 'قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَٱسۡتَقِيمُوٓاْ إِلَيۡهِ وَٱسۡتَغۡفِرُوهُۗ وَوَيۡلٞ لِّلۡمُشۡرِكِينَ\nСен: «Мен ҳам сизга ўхшар башарман, холос. Менга, албатта, илоҳингиз ягона илоҳдир, дея ваҳий қилинур. Бас, Унинг Ўзигагина тўғри юзланинг ва Унга истиғфор айтинг. Мушриклар ҳолига вой бўлсин'}, {'chapter': 41, 'verse': 7, 'text': 'ٱلَّذِينَ لَا يُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ\nУлар закотни адо этмаслар ва улар ўзлари охиратга куфр келтиргувчилардир'}, {'chapter': 41, 'verse': 8, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ\nАлбатта, иймон келтириб, солиҳ амалларни қилганларга битмас-туганмас ажрлар бор», деб айт'}, {'chapter': 41, 'verse': 9, 'text': '۞قُلۡ أَئِنَّكُمۡ لَتَكۡفُرُونَ بِٱلَّذِي خَلَقَ ٱلۡأَرۡضَ فِي يَوۡمَيۡنِ وَتَجۡعَلُونَ لَهُۥٓ أَندَادٗاۚ ذَٰلِكَ رَبُّ ٱلۡعَٰلَمِينَ\nСен: «Сизлар, ҳақиқатан ҳам, ерни икки кунда яратган Зотга куфр келтиряпсизми ва Унга тенгдошлар қўшяпсизми?! Ахир, У зот оламларнинг Робби-ку'}, {'chapter': 41, 'verse': 10, 'text': 'وَجَعَلَ فِيهَا رَوَٰسِيَ مِن فَوۡقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقۡوَٰتَهَا فِيٓ أَرۡبَعَةِ أَيَّامٖ سَوَآءٗ لِّلسَّآئِلِينَ\nУ зот ўша(ер)нинг устида тоғларни қилди, уни баракотли қилди ва унинг (аҳли) ризқини тўрт кунда ўлчовли этиб тақдир қилди. Бу, сўровчилар учундир», деб айт'}, {'chapter': 41, 'verse': 11, 'text': 'ثُمَّ ٱسۡتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِيَ دُخَانٞ فَقَالَ لَهَا وَلِلۡأَرۡضِ ٱئۡتِيَا طَوۡعًا أَوۡ كَرۡهٗا قَالَتَآ أَتَيۡنَا طَآئِعِينَ\nСўнгра тутун ҳолидаги осмонга юзланиб, унга ва ерга: «Икковингиз ихтиёр қилган ҳолингизда ёки мажбур бўлган ҳолингизда келинг!» – деди. Икковлари: «Ихтиёр қилган ҳолимизда келдик», дедилар'}, {'chapter': 41, 'verse': 12, 'text': 'فَقَضَىٰهُنَّ سَبۡعَ سَمَٰوَاتٖ فِي يَوۡمَيۡنِ وَأَوۡحَىٰ فِي كُلِّ سَمَآءٍ أَمۡرَهَاۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَحِفۡظٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\nБас, икки кунда етти осмонни барпо этди ва ҳар бир осмонга ишини ваҳий қилди. Биз дунё осмонини чироқлар-ла зийнатладик ва қўридик. Бу азийзу ўта илмли Зотнинг ўлчовидир'}, {'chapter': 41, 'verse': 13, 'text': 'فَإِنۡ أَعۡرَضُواْ فَقُلۡ أَنذَرۡتُكُمۡ صَٰعِقَةٗ مِّثۡلَ صَٰعِقَةِ عَادٖ وَثَمُودَ\nБас, агар юз ўгирсалар, сен: «Мен сизларни Од ва Самудга келган чақмоққа ўхшаш чақмоқ-ла огоҳлантирдим», деб айт'}, {'chapter': 41, 'verse': 14, 'text': 'إِذۡ جَآءَتۡهُمُ ٱلرُّسُلُ مِنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۖ قَالُواْ لَوۡ شَآءَ رَبُّنَا لَأَنزَلَ مَلَـٰٓئِكَةٗ فَإِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ\nЎшанда Пайғамбарлар уларнинг олдиларидан ҳам, орқаларидан ҳам: «Фақат, Аллоҳдан ўзгага ибодат қилманглар», деб келганларида, улар: «Агар Роббимиз хоҳласа, фаришталарни туширар эди. Албатта, биз сиз билан юборилган нарсага кофирдирмиз», дедилар'}, {'chapter': 41, 'verse': 15, 'text': 'فَأَمَّا عَادٞ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَقَالُواْ مَنۡ أَشَدُّ مِنَّا قُوَّةًۖ أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَهُمۡ هُوَ أَشَدُّ مِنۡهُمۡ قُوَّةٗۖ وَكَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ\nОд (қавми) бўлса, ер юзида ноҳақдан мутакаббирлик қилдилар. Улар: «Биздан қувватлироқ ким бор?!» – дедилар. Уларни яратган Аллоҳ ўзларидан қувватли эканини билмасмидилар?! Улар Бизнинг оятларимизни инкор этар эдилар'}, {'chapter': 41, 'verse': 16, 'text': 'فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِيٓ أَيَّامٖ نَّحِسَاتٖ لِّنُذِيقَهُمۡ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَخۡزَىٰۖ وَهُمۡ لَا يُنصَرُونَ\nБас, Биз устларига шумлик кунларида сорсор шамолини юбордик. Буни уларга ҳаёти дунёнинг хорлик азобини тоттириш учун қилдик. Албатта, охират азоби хор қилгувчироқдир ва уларга нусрат берилмас'}, {'chapter': 41, 'verse': 17, 'text': 'وَأَمَّا ثَمُودُ فَهَدَيۡنَٰهُمۡ فَٱسۡتَحَبُّواْ ٱلۡعَمَىٰ عَلَى ٱلۡهُدَىٰ فَأَخَذَتۡهُمۡ صَٰعِقَةُ ٱلۡعَذَابِ ٱلۡهُونِ بِمَا كَانُواْ يَكۡسِبُونَ\nАммо Самуд бўлса, бас, Биз уларни ҳидоят қилдик. Улар эса, ҳидоятдан кўра кўрликни афзал кўрдилар. Бас, касб қилган нарсалари туфайли уларни хорлик азоби чақмоғи урди'}, {'chapter': 41, 'verse': 18, 'text': 'وَنَجَّيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nИймон келтирганларга ва тақво қилиб юрганларга нажот бердик'}, {'chapter': 41, 'verse': 19, 'text': 'وَيَوۡمَ يُحۡشَرُ أَعۡدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمۡ يُوزَعُونَ\nАллоҳнинг душманлари дўзахга жамланадиган, улар тизиб қўйиладиган кунни (эсла)'}, {'chapter': 41, 'verse': 20, 'text': 'حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيۡهِمۡ سَمۡعُهُمۡ وَأَبۡصَٰرُهُمۡ وَجُلُودُهُم بِمَا كَانُواْ يَعۡمَلُونَ\nНиҳоят, унга етиб келишгач, қулоқлари, кўзлари ва терилари қилиб ўтган нарсалари ҳақида уларга қарши гувоҳлик берадилар'}, {'chapter': 41, 'verse': 21, 'text': 'وَقَالُواْ لِجُلُودِهِمۡ لِمَ شَهِدتُّمۡ عَلَيۡنَاۖ قَالُوٓاْ أَنطَقَنَا ٱللَّهُ ٱلَّذِيٓ أَنطَقَ كُلَّ شَيۡءٖۚ وَهُوَ خَلَقَكُمۡ أَوَّلَ مَرَّةٖ وَإِلَيۡهِ تُرۡجَعُونَ\nУлар териларига: «Нима учун бизга қарши гувоҳлик бердингиз?» – дедилар. У(тери)лари: «Ҳар бир нарсани нутқ қилдирган Аллоҳ бизни ҳам нутқ қилдирди. У зот сизларни аввал бошда яратган ва Унгагина қайтарилурсизлар», дедилар'}, {'chapter': 41, 'verse': 22, 'text': 'وَمَا كُنتُمۡ تَسۡتَتِرُونَ أَن يَشۡهَدَ عَلَيۡكُمۡ سَمۡعُكُمۡ وَلَآ أَبۡصَٰرُكُمۡ وَلَا جُلُودُكُمۡ وَلَٰكِن ظَنَنتُمۡ أَنَّ ٱللَّهَ لَا يَعۡلَمُ كَثِيرٗا مِّمَّا تَعۡمَلُونَ\nСиз ўзингизга қарши қулоғингиз, кўзларингиз ва териларингиз гувоҳлик беришларидан яширинмас эдингиз. Лекин Аллоҳ қилаётган амалларингизнинг кўпини билмас, деб гумон қилар эдингиз'}, {'chapter': 41, 'verse': 23, 'text': 'وَذَٰلِكُمۡ ظَنُّكُمُ ٱلَّذِي ظَنَنتُم بِرَبِّكُمۡ أَرۡدَىٰكُمۡ فَأَصۡبَحۡتُم مِّنَ ٱلۡخَٰسِرِينَ\nАна ўша, Роббингиз ҳақида қилган гумонингиз сизни ҳалокатга учратди. Бас, зиёнкорлардан бўлдингиз'}, {'chapter': 41, 'verse': 24, 'text': 'فَإِن يَصۡبِرُواْ فَٱلنَّارُ مَثۡوٗى لَّهُمۡۖ وَإِن يَسۡتَعۡتِبُواْ فَمَا هُم مِّنَ ٱلۡمُعۡتَبِينَ\nАгар сабр қилсалар, жаҳаннам уларга макондир. Агар итобга қайтаришни талаб қилсалар, улар итобга қайтарилувчи ҳам бўлмаслар'}, {'chapter': 41, 'verse': 25, 'text': '۞وَقَيَّضۡنَا لَهُمۡ قُرَنَآءَ فَزَيَّنُواْ لَهُم مَّا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَحَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ\nБиз уларга «дўстлар» тайёрладик. Бас, ўшалар уларга олдиларидаги ва ортларидаги нарсаларни зийнатлаб кўрсатдилар ва уларга ҳам ўзларидан олдин ўтган жин ва инсдан бўлган умматлар қатори (азоб) сўзи ҳақ бўлди. Албатта, улар зиёнкор бўлдилар'}, {'chapter': 41, 'verse': 26, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَسۡمَعُواْ لِهَٰذَا ٱلۡقُرۡءَانِ وَٱلۡغَوۡاْ فِيهِ لَعَلَّكُمۡ تَغۡلِبُونَ\nКуфр келтирганлар: «Бу Қуръонга қулоқ солманглар, унга халақит беринг, шоядки, ғолиб бўлсангиз», дедилар'}, {'chapter': 41, 'verse': 27, 'text': 'فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُواْ عَذَابٗا شَدِيدٗا وَلَنَجۡزِيَنَّهُمۡ أَسۡوَأَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ\nБас, албатта, Биз куфр келтирганларга шиддатли азобни тоттирамиз ва, албатта, уларга қилиб юрган амалларининг энг ёмон жазосини берамиз'}, {'chapter': 41, 'verse': 28, 'text': 'ذَٰلِكَ جَزَآءُ أَعۡدَآءِ ٱللَّهِ ٱلنَّارُۖ لَهُمۡ فِيهَا دَارُ ٱلۡخُلۡدِ جَزَآءَۢ بِمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ\nМана, Аллоҳ душманларининг жазоси дўзахдир. Уларга унда абадийлик диёри бор. Оятларимизни инкор қилганларининг жазоси шудир'}, {'chapter': 41, 'verse': 29, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ رَبَّنَآ أَرِنَا ٱلَّذَيۡنِ أَضَلَّانَا مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ نَجۡعَلۡهُمَا تَحۡتَ أَقۡدَامِنَا لِيَكُونَا مِنَ ٱلۡأَسۡفَلِينَ\nКуфр келтирганлар: «Эй Роббимиз, бизни адаштирган жин ва инсдан бўлган шахсларни бизга кўрсатгин, уларни оёқларимиз остига олайлик, токи, улар энг пастлардан бўлсинлар», дедилар'}, {'chapter': 41, 'verse': 30, 'text': 'إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ تَتَنَزَّلُ عَلَيۡهِمُ ٱلۡمَلَـٰٓئِكَةُ أَلَّا تَخَافُواْ وَلَا تَحۡزَنُواْ وَأَبۡشِرُواْ بِٱلۡجَنَّةِ ٱلَّتِي كُنتُمۡ تُوعَدُونَ\nАлбатта, «Роббимиз Аллоҳ» деган, сўнгра мустақийм бўлганларнинг устиларидан фаришталар: «Қўрқманглар, маҳзун ҳам бўлманглар, ўзингизга ваъда қилинган жаннат хушхабарини қабул қилинглар'}, {'chapter': 41, 'verse': 31, 'text': 'نَحۡنُ أَوۡلِيَآؤُكُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَلَكُمۡ فِيهَا مَا تَشۡتَهِيٓ أَنفُسُكُمۡ وَلَكُمۡ فِيهَا مَا تَدَّعُونَ\nБиз ҳаёти дунёда ҳам, охиратда ҳам сизларнинг дўстларингиздирмиз. Сизга у(жаннат)да кўнглингиз иштаҳа қилган нарса бордир ва сизга унда орзу қилинган нарсангиз бордир'}, {'chapter': 41, 'verse': 32, 'text': 'نُزُلٗا مِّنۡ غَفُورٖ رَّحِيمٖ\nБу ўта мағфиратли ва ўта раҳимли Зотдан зиёфатдир», деб тушарлар'}, {'chapter': 41, 'verse': 33, 'text': 'وَمَنۡ أَحۡسَنُ قَوۡلٗا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَٰلِحٗا وَقَالَ إِنَّنِي مِنَ ٱلۡمُسۡلِمِينَ\nАллоҳга даъват қилган, солиҳ амалларни қилган ва «Албатта, мен мусулмонларданман!» деган кишидан ҳам гўзал сўзли ким бор'}, {'chapter': 41, 'verse': 34, 'text': 'وَلَا تَسۡتَوِي ٱلۡحَسَنَةُ وَلَا ٱلسَّيِّئَةُۚ ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ فَإِذَا ٱلَّذِي بَيۡنَكَ وَبَيۡنَهُۥ عَدَٰوَةٞ كَأَنَّهُۥ وَلِيٌّ حَمِيمٞ\nЯхшилик билан ёмонлик баробар бўлмас. Сен яхшилик бўлган нарса ила дафъ қил. Кўрибсанки, сен билан орасида адовати бор кимса худди содиқ дўстдек бўлур'}, {'chapter': 41, 'verse': 35, 'text': 'وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُواْ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٖ\nУнга фақат сабр қилганларгина эришур. Унга фақат улуғ насиба эгаси бўлганларгина эришур'}, {'chapter': 41, 'verse': 36, 'text': 'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАгар сени шайтон васвасасидан бир васваса тутса, Аллоҳдан паноҳ сўра. Албатта, У зот ўта эшитгувчи ва ўта билгувчи Зотдир'}, {'chapter': 41, 'verse': 37, 'text': 'وَمِنۡ ءَايَٰتِهِ ٱلَّيۡلُ وَٱلنَّهَارُ وَٱلشَّمۡسُ وَٱلۡقَمَرُۚ لَا تَسۡجُدُواْ لِلشَّمۡسِ وَلَا لِلۡقَمَرِ وَٱسۡجُدُواْۤ لِلَّهِۤ ٱلَّذِي خَلَقَهُنَّ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ\nКечаю кундуз, қуёшу ой Унинг оят(белги)ларидан. Бас, қуёшга ҳам, ойга ҳам сажда қилманглар. Уларни яратган Аллоҳга сажда қилинг. Гар сиз Унгагина ибодат қилмоқчи бўлсангиз'}, {'chapter': 41, 'verse': 38, 'text': 'فَإِنِ ٱسۡتَكۡبَرُواْ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيۡلِ وَٱلنَّهَارِ وَهُمۡ لَا يَسۡـَٔمُونَ۩\nАгар улар мутакаббирлик қилсалар. Роббингнинг ҳузуридагилар Унга кечасию кундузи тасбиҳ айтурлар. Улар малол олмаслар'}, {'chapter': 41, 'verse': 39, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَنَّكَ تَرَى ٱلۡأَرۡضَ خَٰشِعَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡۚ إِنَّ ٱلَّذِيٓ أَحۡيَاهَا لَمُحۡيِ ٱلۡمَوۡتَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nЯна Унинг оятларидан, ерни қақраган ҳолда кўришингдир. Бас, Биз унинг устидан сув туширсак, у қимирлар ва ўсар. Уни тирилтирган Зот, албатта, ўликларни ҳам тирилтиргувчидир. Албатта, У зот ҳар бир нарсага қодирдир'}, {'chapter': 41, 'verse': 40, 'text': 'إِنَّ ٱلَّذِينَ يُلۡحِدُونَ فِيٓ ءَايَٰتِنَا لَا يَخۡفَوۡنَ عَلَيۡنَآۗ أَفَمَن يُلۡقَىٰ فِي ٱلنَّارِ خَيۡرٌ أَم مَّن يَأۡتِيٓ ءَامِنٗا يَوۡمَ ٱلۡقِيَٰمَةِۚ ٱعۡمَلُواْ مَا شِئۡتُمۡ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٌ\nАлбатта, оятларимиз ҳақида эгрилик қиладиганлар Бизга махфий бўлиб қолмаслар. Дўзахга ташланадиган одам яхшими ёки қиёмат куни омон ҳолида келадиган одамми?! Нимани хоҳласангиз, қилаверинг. Албатта, У зот нима қилаётганингизни кўриб тургувчидир'}, {'chapter': 41, 'verse': 41, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ بِٱلذِّكۡرِ لَمَّا جَآءَهُمۡۖ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٞ\nАлбатта, ўзларига Зикр келганда унга куфр келтирганлар... Албатта, у азиз китобдир'}, {'chapter': 41, 'verse': 42, 'text': 'لَّا يَأۡتِيهِ ٱلۡبَٰطِلُ مِنۢ بَيۡنِ يَدَيۡهِ وَلَا مِنۡ خَلۡفِهِۦۖ تَنزِيلٞ مِّنۡ حَكِيمٍ حَمِيدٖ\nУнинг олдидан ҳам, ортидан ҳам ботил келмас. У, ўта ҳикматли ва ўта мақталган Зотнинг туширганидир'}, {'chapter': 41, 'verse': 43, 'text': 'مَّا يُقَالُ لَكَ إِلَّا مَا قَدۡ قِيلَ لِلرُّسُلِ مِن قَبۡلِكَۚ إِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ وَذُو عِقَابٍ أَلِيمٖ\nСенга айтилаётган нарсаки бор, батаҳқиқ, сендан олдинги Пайғамбарларга ҳам айтилгандир. Албатта, Роббинг мағфират эгасидир ва аламли азоб эгасидир'}, {'chapter': 41, 'verse': 44, 'text': 'وَلَوۡ جَعَلۡنَٰهُ قُرۡءَانًا أَعۡجَمِيّٗا لَّقَالُواْ لَوۡلَا فُصِّلَتۡ ءَايَٰتُهُۥٓۖ ءَا۬عۡجَمِيّٞ وَعَرَبِيّٞۗ قُلۡ هُوَ لِلَّذِينَ ءَامَنُواْ هُدٗى وَشِفَآءٞۚ وَٱلَّذِينَ لَا يُؤۡمِنُونَ فِيٓ ءَاذَانِهِمۡ وَقۡرٞ وَهُوَ عَلَيۡهِمۡ عَمًىۚ أُوْلَـٰٓئِكَ يُنَادَوۡنَ مِن مَّكَانِۭ بَعِيدٖ\nАгар Биз уни ажамийча Қуръон қилганимизда, албатта: «Унинг оятлари муфассал баён қилинганида эди. Арабийга ажамчами?!» – дер эдилар. Сен: «У, иймон келтирганлар учун ҳидоят ва шифодир. Иймон келтирмайдиганларнинг қулоқларида оғирлик бордир. У, улар учун кўрликдир. Улар узоқ макондан чақирилаётганлардир», деб айт'}, {'chapter': 41, 'verse': 45, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ\nБатаҳқиқ, Биз Мусога китобни бердик. Бас, у ҳақида ихтилоф қилинди. Агар Роббинг томонидан олдин сўз ўтмаганида, ораларида, албатта, ҳукм чиқариларди. Албатта, улар у ҳақда гумонли шакдадирлар'}, {'chapter': 41, 'verse': 46, 'text': 'مَّنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۗ وَمَا رَبُّكَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nКим солиҳ амал қилса, ўзига фойда. Ким ёмонлик қилса, ўзига зарар. Роббинг бандаларга зулм қилгувчи эмас'}, {'chapter': 41, 'verse': 47, 'text': '۞إِلَيۡهِ يُرَدُّ عِلۡمُ ٱلسَّاعَةِۚ وَمَا تَخۡرُجُ مِن ثَمَرَٰتٖ مِّنۡ أَكۡمَامِهَا وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَيَوۡمَ يُنَادِيهِمۡ أَيۡنَ شُرَكَآءِي قَالُوٓاْ ءَاذَنَّـٰكَ مَامِنَّا مِن شَهِيدٖ\nСоат(қиёмат) илми У зотнинг Ўзигагина қайтарилур. Барча меваларнинг гулкосаларидан чиқиши ҳам, ҳар бир урғочининг нимага ҳомиладор бўлиши ҳам, нимани туғиши ҳам фақат Унинг илми ила бўлур. У зот уларга: «Менинг шерикларим қани?!» – деб нидо қиладиган кунда, улар: «Биз сенга билдирамизки, бунга биздан бирорта гувоҳ йўқ», дерлар'}, {'chapter': 41, 'verse': 48, 'text': 'وَضَلَّ عَنۡهُم مَّا كَانُواْ يَدۡعُونَ مِن قَبۡلُۖ وَظَنُّواْ مَا لَهُم مِّن مَّحِيصٖ\nВа улардан илгари илтижо қилиб юрган нарсалари ғойиб бўлди. Улар ўзларига қочар жой йўқлигига тўла ишондилар'}, {'chapter': 41, 'verse': 49, 'text': 'لَّا يَسۡـَٔمُ ٱلۡإِنسَٰنُ مِن دُعَآءِ ٱلۡخَيۡرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٞ قَنُوطٞ\nИнсон яхшилик тилашни ҳеч малол олмас. Агар уни ёмонлик тутса, у умидсиз тушкундир'}, {'chapter': 41, 'verse': 50, 'text': 'وَلَئِنۡ أَذَقۡنَٰهُ رَحۡمَةٗ مِّنَّا مِنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ هَٰذَا لِي وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّجِعۡتُ إِلَىٰ رَبِّيٓ إِنَّ لِي عِندَهُۥ لَلۡحُسۡنَىٰۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُواْ بِمَا عَمِلُواْ وَلَنُذِيقَنَّهُم مِّنۡ عَذَابٍ غَلِيظٖ\nАгар Биз унга уни тутган зарардан сўнг Ўзимиздан раҳмат тоттирсак, албатта, у: «Бу меникидир. Мен соат(қиёмат) қоим бўлур, деб ўйламайман. Агар Роббимга қайтарилсам, албатта, мен учун Унинг ҳузурида гўзал (мукофот) бордир», дер. Бас, албатта, куфр келтирганларга қилган амаллари хабарини берурмиз. Албатта, уларга оғир азобдан тоттирурмиз'}, {'chapter': 41, 'verse': 51, 'text': 'وَإِذَآ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٖ\nҚачон инсонга неъмат берсак, у юз ўгирар ва ёнбоши ила узоқлашар. Қачон уни ёмонлик тутса, бас, у узундан-узоқ дуо эгасидир'}, {'chapter': 41, 'verse': 52, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ ثُمَّ كَفَرۡتُم بِهِۦ مَنۡ أَضَلُّ مِمَّنۡ هُوَ فِي شِقَاقِۭ بَعِيدٖ\nСен: «Менга айтинглар-чи, агар у Аллоҳнинг ҳузуридан бўлса-ю, сиз унга куфр келтириб юрган бўлсангиз. Хўш, (ҳақдан) узоқ тортишувда бўлганлардан ҳам адашганроқ ким бор?!» – деб айт'}, {'chapter': 41, 'verse': 53, 'text': 'سَنُرِيهِمۡ ءَايَٰتِنَا فِي ٱلۡأٓفَاقِ وَفِيٓ أَنفُسِهِمۡ حَتَّىٰ يَتَبَيَّنَ لَهُمۡ أَنَّهُ ٱلۡحَقُّۗ أَوَلَمۡ يَكۡفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nБиз уларга ҳам уфқлардаги, ҳам ўз нафсларидаги оят (белги)ларимизни кўрсатамиз. Токи, уларга унинг ҳақлиги равшан бўлсин. Сенинг Роббинг ҳар бир нарсага шоҳид эканлиги кифоя қилмасмиди'}, {'chapter': 41, 'verse': 54, 'text': 'أَلَآ إِنَّهُمۡ فِي مِرۡيَةٖ مِّن لِّقَآءِ رَبِّهِمۡۗ أَلَآ إِنَّهُۥ بِكُلِّ شَيۡءٖ مُّحِيطُۢ\nОгоҳ бўлинглар! Албатта, улар Роббиларига рўбарў бўлишдан шакдадирлар. Огоҳ бўлинглар! Албатта, У зот ҳар бир нарсани иҳота қилгувчидир'}], '42': [{'chapter': 42, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 42, 'verse': 2, 'text': 'عٓسٓقٓ\nАйн. Сийн. Қооф'}, {'chapter': 42, 'verse': 3, 'text': 'كَذَٰلِكَ يُوحِيٓ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАзийзу ўта ҳикматли бўлмиш Аллоҳ сенга ва сендан аввалгиларга мана шундай ваҳий қилур'}, {'chapter': 42, 'verse': 4, 'text': 'لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ\nОсмондаги нарсалар ва ердаги нарсалар уникидир. У зот юксак ва улуғдир'}, {'chapter': 42, 'verse': 5, 'text': 'تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِن فَوۡقِهِنَّۚ وَٱلۡمَلَـٰٓئِكَةُ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيَسۡتَغۡفِرُونَ لِمَن فِي ٱلۡأَرۡضِۗ أَلَآ إِنَّ ٱللَّهَ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nОсмонлар уст томонларидан ёрилиб кетай дейди. Фаришталар эса, Роббилари ҳамди ила тасбиҳ айтурлар ва ер юзидаги кимсаларга истиғфор сўрарлар. Огоҳ бўлингким, албатта, Аллоҳ ўта мағфиратли ва ўта раҳимли Зотдир'}, {'chapter': 42, 'verse': 6, 'text': 'وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيۡهِمۡ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ\nУндан ўзга «дўстлар» тутганларга Аллоҳ кузатгувчидир. Сен уларга вакил эмассан'}, {'chapter': 42, 'verse': 7, 'text': 'وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ قُرۡءَانًا عَرَبِيّٗا لِّتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَا وَتُنذِرَ يَوۡمَ ٱلۡجَمۡعِ لَا رَيۡبَ فِيهِۚ فَرِيقٞ فِي ٱلۡجَنَّةِ وَفَرِيقٞ فِي ٱلسَّعِيرِ\nАна шундай қилиб, Биз сенга шаҳарларнинг онасини ва унинг атрофини огоҳлантиришинг учун, бўлишида ҳеч шубҳа йўқ жамлаш кунидан огоҳлантиришинг учун арабий Қуръонни юбордик. Бир гуруҳ жаннатда, бир гуруҳ дўзахда'}, {'chapter': 42, 'verse': 8, 'text': 'وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَهُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمُونَ مَا لَهُم مِّن وَلِيّٖ وَلَا نَصِيرٍ\nАгар Аллоҳ хоҳлаганида, албатта, уларни бир уммат қилар эди. Лекин хоҳлаган кишини Ўз раҳматига киритур. Золимларга эса, ҳеч бир дўст ҳам, ёрдам берувчи ҳам йўқдир'}, {'chapter': 42, 'verse': 9, 'text': 'أَمِ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۖ فَٱللَّهُ هُوَ ٱلۡوَلِيُّ وَهُوَ يُحۡيِ ٱلۡمَوۡتَىٰ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЁки улар ундан бошқа «дўстлар» тутдиларми?! Бас, Аллоҳнинг Ўзигина дўстдир. У зот ўликларни тирилтирур. У зот ҳар бир нарсага қодирдир'}, {'chapter': 42, 'verse': 10, 'text': 'وَمَا ٱخۡتَلَفۡتُمۡ فِيهِ مِن شَيۡءٖ فَحُكۡمُهُۥٓ إِلَى ٱللَّهِۚ ذَٰلِكُمُ ٱللَّهُ رَبِّي عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ\n«Бирор нарсада ихтилоф қилсангиз, унинг ҳукми Аллоҳдадир. Ана ўша Аллоҳ Роббимдир. Унгагина таваккал қилдим ва унгагина қайтурман.»'}, {'chapter': 42, 'verse': 11, 'text': 'فَاطِرُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَمِنَ ٱلۡأَنۡعَٰمِ أَزۡوَٰجٗا يَذۡرَؤُكُمۡ فِيهِۚ لَيۡسَ كَمِثۡلِهِۦ شَيۡءٞۖ وَهُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\nУ, осмонлару ерни йўқдан бор қилгувчи Зотдир. У, сизларга ўз жинсингиздан жуфтлар яратди. Чорва ҳайвонларини ҳам жуфт қилиб яратди. У зот сизни ўша (жуфтлик) асосида кўпайтирур. У зотга ўхшаш ҳеч нарса йўқдир. У зот ўта эшитгувчи, ўта кўргувчи Зотдир'}, {'chapter': 42, 'verse': 12, 'text': 'لَهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ بِكُلِّ شَيۡءٍ عَلِيمٞ\nОсмонлару ер (хазийналари) калитлари Уникидир. У, хоҳлаган одамига ризқни кенг ёки тор қилур. Зотан, У барча нарсани ўта билгувчи Зотдир'}, {'chapter': 42, 'verse': 13, 'text': '۞شَرَعَ لَكُم مِّنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحٗا وَٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَمَا وَصَّيۡنَا بِهِۦٓ إِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰٓۖ أَنۡ أَقِيمُواْ ٱلدِّينَ وَلَا تَتَفَرَّقُواْ فِيهِۚ كَبُرَ عَلَى ٱلۡمُشۡرِكِينَ مَا تَدۡعُوهُمۡ إِلَيۡهِۚ ٱللَّهُ يَجۡتَبِيٓ إِلَيۡهِ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَن يُنِيبُ\nУ зот сизларга дин этиб Ўзи Нуҳга васият қилган ва сенга (эй Муҳаммад) ваҳий қилган нарсамизни ҳамда Иброҳим, Мусо ва Ийсога васият қилган нарсамизни баён қилди. «Динни барпо қилингиз ва унда тафриқага тушмангиз», деди. Мушрикларга сен уларни даъват қилган нарса оғир келди. Аллоҳ Ўзи хоҳлаган кишисини (Пайғамбарликка) танлаб олур ва У Ўзигагина илтижо этгувчи кишини ҳидоят қилур'}, {'chapter': 42, 'verse': 14, 'text': 'وَمَا تَفَرَّقُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ إِلَىٰٓ أَجَلٖ مُّسَمّٗى لَّقُضِيَ بَيۡنَهُمۡۚ وَإِنَّ ٱلَّذِينَ أُورِثُواْ ٱلۡكِتَٰبَ مِنۢ بَعۡدِهِمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ\nУлар фақат ўзларига илм келгандан кейингина, ўзаро ҳасад-адоват қилишиб тафриқага тушдилар. Агар Роббингдан (азоблашни) маълум муддатгача (қолдириш) ҳақида сўз ўтган бўлмаганида, улар орасида албатта ҳукм чиқарилган бўлур эди. Албатта, улардан кейин китобга ворис қилинганлар у ҳақида шак-шубҳададирлар'}, {'chapter': 42, 'verse': 15, 'text': 'فَلِذَٰلِكَ فَٱدۡعُۖ وَٱسۡتَقِمۡ كَمَآ أُمِرۡتَۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡۖ وَقُلۡ ءَامَنتُ بِمَآ أَنزَلَ ٱللَّهُ مِن كِتَٰبٖۖ وَأُمِرۡتُ لِأَعۡدِلَ بَيۡنَكُمُۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمۡۖ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡۖ لَا حُجَّةَ بَيۡنَنَا وَبَيۡنَكُمُۖ ٱللَّهُ يَجۡمَعُ بَيۡنَنَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ\nБас, шунинг учун ҳам, ўзингга амр қилинганидек даъват қил ва мустақийм тур ва уларнинг ҳавойи нафсларига эргашма ҳамда: «Мен Аллоҳ нозил қилган ҳар бир китобга иймон келтирдим ва орангизда адолат ўрнатишга амр қилиндим. Аллоҳ бизнинг Роббимиз, сизнинг ҳам Роббингиз. Бизнинг амалларимиз ўзимиз учун, сизнинг амалларингиз ўзингиз учун. Биз билан сизнинг орамизда жанжал(га ўрин) йўқ. Аллоҳ бизларни жамлайди ва қайтиш ёлғиз Унинг Ўзигадир», деб айт'}, {'chapter': 42, 'verse': 16, 'text': 'وَٱلَّذِينَ يُحَآجُّونَ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ٱسۡتُجِيبَ لَهُۥ حُجَّتُهُمۡ دَاحِضَةٌ عِندَ رَبِّهِمۡ وَعَلَيۡهِمۡ غَضَبٞ وَلَهُمۡ عَذَابٞ شَدِيدٌ\nАллоҳ ҳақида унга ижобат қилинганидан кейин ҳам талашиб-тортишадиганларнинг ҳужжатлари Роббилари ҳузурида ботилдир, уларга ғазаб бордир ва уларга шиддатли азоб бордир'}, {'chapter': 42, 'verse': 17, 'text': 'ٱللَّهُ ٱلَّذِيٓ أَنزَلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ وَٱلۡمِيزَانَۗ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٞ\nАллоҳ ҳақ ила китобни ва (адолат) тарозуни нозил қилган Зотдир. Қаердан билурсан, эҳтимол соат(қиёмат) яқиндир'}, {'chapter': 42, 'verse': 18, 'text': 'يَسۡتَعۡجِلُ بِهَا ٱلَّذِينَ لَا يُؤۡمِنُونَ بِهَاۖ وَٱلَّذِينَ ءَامَنُواْ مُشۡفِقُونَ مِنۡهَا وَيَعۡلَمُونَ أَنَّهَا ٱلۡحَقُّۗ أَلَآ إِنَّ ٱلَّذِينَ يُمَارُونَ فِي ٱلسَّاعَةِ لَفِي ضَلَٰلِۭ بَعِيدٍ\nУ(қиёмат)га иймон келтирмайдиганлар унинг тезлашини истайдилар, иймон келтирганлар эса, ундан қўрқувчидирлар, зотан, унинг шубҳасиз ҳақ эканини билурлар. Огоҳ бўлинг! Албатта, соат(қиёмат) ҳақида шубҳа қилгувчилар йироқ залолатдадирлар'}, {'chapter': 42, 'verse': 19, 'text': 'ٱللَّهُ لَطِيفُۢ بِعِبَادِهِۦ يَرۡزُقُ مَن يَشَآءُۖ وَهُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ\nАллоҳ бандаларига лутфлидир. У хоҳлаган кишига ризқ берур. У зот кучлигу азийздир'}, {'chapter': 42, 'verse': 20, 'text': 'مَن كَانَ يُرِيدُ حَرۡثَ ٱلۡأٓخِرَةِ نَزِدۡ لَهُۥ فِي حَرۡثِهِۦۖ وَمَن كَانَ يُرِيدُ حَرۡثَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِن نَّصِيبٍ\nКим охират ҳосилини истаса, биз унга ҳосилини зиёда қилиб берурмиз. Ким дунё ҳосилини истаса, биз унга ўшандан берурмиз ва унга охиратда бирон насиба бўлмас'}, {'chapter': 42, 'verse': 21, 'text': 'أَمۡ لَهُمۡ شُرَكَـٰٓؤُاْ شَرَعُواْ لَهُم مِّنَ ٱلدِّينِ مَا لَمۡ يَأۡذَنۢ بِهِ ٱللَّهُۚ وَلَوۡلَا كَلِمَةُ ٱلۡفَصۡلِ لَقُضِيَ بَيۡنَهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ\nЁки уларнинг диндан Аллоҳ изн бермаган нарсаларни шариат қилиб берган шерик(худо)лари борми?! Агар ажрим калимаси бўлмаганида, албатта, улар ўртасида ҳукм қилинган бўлар эди. Албатта, золим кимсалар учун аламли азоб бордир'}, {'chapter': 42, 'verse': 22, 'text': 'تَرَى ٱلظَّـٰلِمِينَ مُشۡفِقِينَ مِمَّا كَسَبُواْ وَهُوَ وَاقِعُۢ بِهِمۡۗ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فِي رَوۡضَاتِ ٱلۡجَنَّاتِۖ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ\nЗолимларни ўзлари касб қилган нарсалари(азоби)дан қўрқувчи ўлароқ кўрасан. Ҳолбуки, у уларга тушгувчидир. Иймон келтириб, солиҳ амалларни қилувчилар эса, жаннат боғларидадир. Улар учун Роббилари ҳузурида хоҳлаган нарсалари бордир. Мана шунинг ўзи катта фазлдир'}, {'chapter': 42, 'verse': 23, 'text': 'ذَٰلِكَ ٱلَّذِي يُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًا إِلَّا ٱلۡمَوَدَّةَ فِي ٱلۡقُرۡبَىٰۗ وَمَن يَقۡتَرِفۡ حَسَنَةٗ نَّزِدۡ لَهُۥ فِيهَا حُسۡنًاۚ إِنَّ ٱللَّهَ غَفُورٞ شَكُورٌ\nАна ўша, иймон келтириб, солиҳ амалларни қилган бандаларига Аллоҳ бераётган хушхабардир. (Эй Муҳаммад): «Мен сиздан бу учун ажр эмас, фақат қариндошлик меҳринигина сўрайман», деб айт. Ким яхшилик қилса, Биз унга ўша ишида яхшиликни зиёда қилурмиз. Албатта, Аллоҳ ўта мағфиратли, Шакурдир'}, {'chapter': 42, 'verse': 24, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗاۖ فَإِن يَشَإِ ٱللَّهُ يَخۡتِمۡ عَلَىٰ قَلۡبِكَۗ وَيَمۡحُ ٱللَّهُ ٱلۡبَٰطِلَ وَيُحِقُّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦٓۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nЁки улар: «У (Муҳаммад) Аллоҳга нисбатан ёлғон тўқиди», дейдиларми?! Бас, агар Аллоҳ хоҳласа, қалбингга муҳр босур ва Аллоҳ Ўз сўзлари ила ботилни йўқ қилиб, ҳақни ҳақ қилур. Албатта, У диллардаги нарсани ўта билгувчидир'}, {'chapter': 42, 'verse': 25, 'text': 'وَهُوَ ٱلَّذِي يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَعۡفُواْ عَنِ ٱلسَّيِّـَٔاتِ وَيَعۡلَمُ مَا تَفۡعَلُونَ\nУнинг Ўзи бандаларидан тавбани қабул қилур, гуноҳларини афв этур ва нима қилаётганингизни билур'}, {'chapter': 42, 'verse': 26, 'text': 'وَيَسۡتَجِيبُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَيَزِيدُهُم مِّن فَضۡلِهِۦۚ وَٱلۡكَٰفِرُونَ لَهُمۡ عَذَابٞ شَدِيدٞ\nУ зот иймон келтириб, солиҳ амаллар қилганларнинг (дуосини) ижобат қилур ва уларга Ўз фазлидан зиёда қилур. Кофирларга эса, уларга шиддатли азоб бордир'}, {'chapter': 42, 'verse': 27, 'text': '۞وَلَوۡ بَسَطَ ٱللَّهُ ٱلرِّزۡقَ لِعِبَادِهِۦ لَبَغَوۡاْ فِي ٱلۡأَرۡضِ وَلَٰكِن يُنَزِّلُ بِقَدَرٖ مَّا يَشَآءُۚ إِنَّهُۥ بِعِبَادِهِۦ خَبِيرُۢ بَصِيرٞ\nАгар Аллоҳ бандаларига ризқни кенг қилиб қўйса, албатта, улар ер юзида ҳаддан ошиб кетурлар, лекин У Ўзи хоҳлаганига ўлчов билан туширади. Албатта, у Ўз бандаларидан ўта хабардор ва уларни ўта кўрувчидир'}, {'chapter': 42, 'verse': 28, 'text': 'وَهُوَ ٱلَّذِي يُنَزِّلُ ٱلۡغَيۡثَ مِنۢ بَعۡدِ مَا قَنَطُواْ وَيَنشُرُ رَحۡمَتَهُۥۚ وَهُوَ ٱلۡوَلِيُّ ٱلۡحَمِيدُ\nУ зот (одамлар) ноумид бўлганларидан кейин ёмғирни нозил қилур ва Ўз раҳматини таратур. У валий ва мақталган Зотдир'}, {'chapter': 42, 'verse': 29, 'text': 'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٖۚ وَهُوَ عَلَىٰ جَمۡعِهِمۡ إِذَا يَشَآءُ قَدِيرٞ\nУнинг (қудрати) оят(белги)ларидан осмонлару ернинг яратилиши ва уларда турли жониворларнинг таратиб қўйилишидир. Ва У зот, агар хоҳласа, уларни жамлашга қодирдир'}, {'chapter': 42, 'verse': 30, 'text': 'وَمَآ أَصَٰبَكُم مِّن مُّصِيبَةٖ فَبِمَا كَسَبَتۡ أَيۡدِيكُمۡ وَيَعۡفُواْ عَن كَثِيرٖ\nСизга қайси бир мусийбат етса, бас, ўз қўлингиз қилган касбдандир ва У зот кўпини афв қилур'}, {'chapter': 42, 'verse': 31, 'text': 'وَمَآ أَنتُم بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nВа сиз ер юзида қочиб қутула олмайсиз. Ҳамда сиз учун Аллоҳдан ўзга валий ҳам, нусрат бергувчи ҳам йўқ'}, {'chapter': 42, 'verse': 32, 'text': 'وَمِنۡ ءَايَٰتِهِ ٱلۡجَوَارِ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ\nДенгизда тоғдек бўлиб сузиб юргувчи кемалар унинг (қудрати) оят(белги)ларидандир'}, {'chapter': 42, 'verse': 33, 'text': 'إِن يَشَأۡ يُسۡكِنِ ٱلرِّيحَ فَيَظۡلَلۡنَ رَوَاكِدَ عَلَىٰ ظَهۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٍ\nАгар У зот хоҳласа, шамолни тўхтатиб қўюр. Бас, у (кема)лар у(денгиз)нинг устида туриб қолурлар. Албатта, ана шунда ҳар бир сабр қилувчи ва шукр қилгувчи учун оят(белги)лар бордир'}, {'chapter': 42, 'verse': 34, 'text': 'أَوۡ يُوبِقۡهُنَّ بِمَا كَسَبُواْ وَيَعۡفُ عَن كَثِيرٖ\nЁки у(кема)ларни аларнинг касби туфайли ҳалок қилур ва У зот кўпни афв қилур'}, {'chapter': 42, 'verse': 35, 'text': 'وَيَعۡلَمَ ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِنَا مَا لَهُم مِّن مَّحِيصٖ\nВа Бизнинг оятларимиз ҳақида тортишадиганлар билсинларки, улар учун ҳеч бир қочар жой йўқдир'}, {'chapter': 42, 'verse': 36, 'text': 'فَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰ لِلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nСизга берилган нарсалар дунё ҳаётининг матоҳи, холос. Аллоҳнинг ҳузуридаги яхши ва боқий нарсалар эса, иймон келтирган ва Роббиларига таваккал қилганлар учундир'}, {'chapter': 42, 'verse': 37, 'text': 'وَٱلَّذِينَ يَجۡتَنِبُونَ كَبَـٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ وَإِذَا مَا غَضِبُواْ هُمۡ يَغۡفِرُونَ\nКатта гуноҳлар ва фоҳиша ишлардан четда бўладиган ва ғазабланган вақтда кечириб юборадиганлар учундир'}, {'chapter': 42, 'verse': 38, 'text': 'وَٱلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمۡ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَمۡرُهُمۡ شُورَىٰ بَيۡنَهُمۡ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nРоббиларига ижобат қилган, намозни тўкис адо этган, ишлари ўзаро шууро ила бўлган ва уларга ризқ қилиб берган нарсаларимиздан инфоқ қиладиганлар учундир'}, {'chapter': 42, 'verse': 39, 'text': 'وَٱلَّذِينَ إِذَآ أَصَابَهُمُ ٱلۡبَغۡيُ هُمۡ يَنتَصِرُونَ\nЎзларига тажовузкорлик етганда нусрат қозонадиганлар учундир'}, {'chapter': 42, 'verse': 40, 'text': 'وَجَزَـٰٓؤُاْ سَيِّئَةٖ سَيِّئَةٞ مِّثۡلُهَاۖ فَمَنۡ عَفَا وَأَصۡلَحَ فَأَجۡرُهُۥ عَلَى ٱللَّهِۚ إِنَّهُۥ لَا يُحِبُّ ٱلظَّـٰلِمِينَ\nЎмонликнинг жазоси ўз мислидек ёмонликдир. Ким афв этиб ислоҳ қилса, унинг ажри Аллоҳнинг зиммасидадир. Албатта, У зот золимларни севмас'}, {'chapter': 42, 'verse': 41, 'text': 'وَلَمَنِ ٱنتَصَرَ بَعۡدَ ظُلۡمِهِۦ فَأُوْلَـٰٓئِكَ مَا عَلَيۡهِم مِّن سَبِيلٍ\nАлбатта, ким ўзига бўлган зулмдан кейин нусрат қозонса, бас, ундайларни (айблашга) ҳеч йўл йўқ'}, {'chapter': 42, 'verse': 42, 'text': 'إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظۡلِمُونَ ٱلنَّاسَ وَيَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ\nФақатгина, одамларга зулм қиладиган ва ер юзида ноҳақ тажовузкорлик қиладиганларни (айблашга) йўл бордир. Ана ўшаларга аламли азоб бордир'}, {'chapter': 42, 'verse': 43, 'text': 'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنۡ عَزۡمِ ٱلۡأُمُورِ\nШубҳасиз, ким сабр қилиб, кечирса, албатта, бу мардлик ишларидандир'}, {'chapter': 42, 'verse': 44, 'text': 'وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِيّٖ مِّنۢ بَعۡدِهِۦۗ وَتَرَى ٱلظَّـٰلِمِينَ لَمَّا رَأَوُاْ ٱلۡعَذَابَ يَقُولُونَ هَلۡ إِلَىٰ مَرَدّٖ مِّن سَبِيلٖ\nАллоҳ кимни залолатга кетказса, бас, унга У зотдан ўзга ҳеч бир валий бўлмас. Золимларни кўрурсанки, азобни кўрган чоғларида: «(Ортга) қайтишга ҳеч бир йўл бормикан?» – дерлар'}, {'chapter': 42, 'verse': 45, 'text': 'وَتَرَىٰهُمۡ يُعۡرَضُونَ عَلَيۡهَا خَٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرۡفٍ خَفِيّٖۗ وَقَالَ ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَآ إِنَّ ٱلظَّـٰلِمِينَ فِي عَذَابٖ مُّقِيمٖ\nСен уларни у(дўзах)га кўндаланг қилинаётганларида хору зор ва қўрққан ҳолларида кўз қири билан назар солаётганларини кўрурсан. Иймон келтирганлар: «Албатта, зиёнкорлар қиёмат куни ўзларига ва аҳли аёлларига зиён қилганлардир», дерлар. Огоҳ бўлингким, албатта, золимлар доимий азобдадирлар'}, {'chapter': 42, 'verse': 46, 'text': 'وَمَا كَانَ لَهُم مِّنۡ أَوۡلِيَآءَ يَنصُرُونَهُم مِّن دُونِ ٱللَّهِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ\nВа уларга Аллоҳдан ўзга ёрдам берадиган ҳеч қандай валийлар бўлмади. Аллоҳ кимни залолатга кетказса, бас, унга ҳеч бир (тўғри) йўл йўқдир'}, {'chapter': 42, 'verse': 47, 'text': 'ٱسۡتَجِيبُواْ لِرَبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۚ مَا لَكُم مِّن مَّلۡجَإٖ يَوۡمَئِذٖ وَمَا لَكُم مِّن نَّكِيرٖ\nҚайтариб бўлмайдиган кун Аллоҳ томонидан келмай туриб, Роббингизга ижобат қилинг! Ўша куни сизга паноҳгоҳ топилмас ва сизга (тушган азобни) инкор қилгувчи топилмас'}, {'chapter': 42, 'verse': 48, 'text': 'فَإِنۡ أَعۡرَضُواْ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظًاۖ إِنۡ عَلَيۡكَ إِلَّا ٱلۡبَلَٰغُۗ وَإِنَّآ إِذَآ أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ فَرِحَ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَإِنَّ ٱلۡإِنسَٰنَ كَفُورٞ\nАгар улар юз ўгирсалар, бас, биз сени уларга қўриқчи қилиб юборганимиз йўқ. Сенинг зиммангда фақат (ваҳийни) етказиш бор, холос. Албатта, биз инсонга Ўзимиздан раҳмат тоттирган чоғимизда у ундан қувонур ва агар уларга ўз қўллари тақдим этган нарса туфайли ёмонлик етса, бас, албатта, инсон куфрони неъмат қилур'}, {'chapter': 42, 'verse': 49, 'text': 'لِّلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَخۡلُقُ مَا يَشَآءُۚ يَهَبُ لِمَن يَشَآءُ إِنَٰثٗا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ\nОсмонлару ернинг мулки Аллоҳникидир. У зот хоҳлаган нарсасини яратур. У зот хоҳлаган кишисига қизлар ҳадя этур ва хоҳлаган кишисига ўғиллар ҳадя этур'}, {'chapter': 42, 'verse': 50, 'text': 'أَوۡ يُزَوِّجُهُمۡ ذُكۡرَانٗا وَإِنَٰثٗاۖ وَيَجۡعَلُ مَن يَشَآءُ عَقِيمًاۚ إِنَّهُۥ عَلِيمٞ قَدِيرٞ\nЁки уларни жуфтлаб — ўғил-қиз қилиб берур ва хоҳлаган кишисини туғмас қилур. Албатта, У зот ўта билгувчи ва ўта қодирдир'}, {'chapter': 42, 'verse': 51, 'text': '۞وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحۡيًا أَوۡ مِن وَرَآيِٕ حِجَابٍ أَوۡ يُرۡسِلَ رَسُولٗا فَيُوحِيَ بِإِذۡنِهِۦ مَا يَشَآءُۚ إِنَّهُۥ عَلِيٌّ حَكِيمٞ\nАллоҳнинг башар билан гаплашмоғи бўлмаган, магар ваҳий орқали, ёки парда ортидан ёхуд элчи юборади ва ул У зотнинг изни ила хоҳлаган нарсасини ваҳий қилур. Албатта, У зот ўта олий ва ўта ҳикматлидир'}, {'chapter': 42, 'verse': 52, 'text': 'وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ رُوحٗا مِّنۡ أَمۡرِنَاۚ مَا كُنتَ تَدۡرِي مَا ٱلۡكِتَٰبُ وَلَا ٱلۡإِيمَٰنُ وَلَٰكِن جَعَلۡنَٰهُ نُورٗا نَّهۡدِي بِهِۦ مَن نَّشَآءُ مِنۡ عِبَادِنَاۚ وَإِنَّكَ لَتَهۡدِيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАна шунга ўхшаш, Биз сенга Ўз амримиздан бир руҳни ваҳий қилдик. Сен китоб нималигини ҳам, иймон нималигини ҳам идрок қилмас эдинг. Лекин, Биз уни бир нур қилдикки, у билан бандаларимиздан кимни хоҳласак ҳидоят қилурмиз. Албатта, сен тўғри йўлга ҳидоят қилурсан'}, {'chapter': 42, 'verse': 53, 'text': 'صِرَٰطِ ٱللَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ أَلَآ إِلَى ٱللَّهِ تَصِيرُ ٱلۡأُمُورُ\nОсмонлардаги нарсалар ва ердаги нарсалар Уники бўлган Аллоҳнинг йўлига (ҳидоят қилурсан). Огоҳ бўлингким, барча ишлар Аллоҳгагина қайтур'}], '43': [{'chapter': 43, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 43, 'verse': 2, 'text': 'وَٱلۡكِتَٰبِ ٱلۡمُبِينِ\nОчиқ-ойдин китоб ила қасам'}, {'chapter': 43, 'verse': 3, 'text': 'إِنَّا جَعَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ\nАлбатта, Биз уни арабча Қуръон қилдик. Шоядки, ақл юритсангиз'}, {'chapter': 43, 'verse': 4, 'text': 'وَإِنَّهُۥ فِيٓ أُمِّ ٱلۡكِتَٰبِ لَدَيۡنَا لَعَلِيٌّ حَكِيمٌ\nАлбатта, у ҳузуримиздаги она китобдадир, у олийдир, ҳикматлидир'}, {'chapter': 43, 'verse': 5, 'text': 'أَفَنَضۡرِبُ عَنكُمُ ٱلذِّكۡرَ صَفۡحًا أَن كُنتُمۡ قَوۡمٗا مُّسۡرِفِينَ\nНаҳотки, сиз исрофчи қавм бўлганингиз учун сиздан юз ўгириб, зикр(эслатма)ни буриб юборсак'}, {'chapter': 43, 'verse': 6, 'text': 'وَكَمۡ أَرۡسَلۡنَا مِن نَّبِيّٖ فِي ٱلۡأَوَّلِينَ\nАввалгиларга қанчадан-қанча Пайғамбарлар юбордик'}, {'chapter': 43, 'verse': 7, 'text': 'وَمَا يَأۡتِيهِم مِّن نَّبِيٍّ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУлар ўзларига келган ҳар бир Пайғамбарни албатта истеҳзо қилганлар'}, {'chapter': 43, 'verse': 8, 'text': 'فَأَهۡلَكۡنَآ أَشَدَّ مِنۡهُم بَطۡشٗا وَمَضَىٰ مَثَلُ ٱلۡأَوَّلِينَ\nБас, Биз улардан кўра кучлироқларни ҳалок қилганмиз. Аввалгиларнинг мисоли (зикр бўлиб) ўтди'}, {'chapter': 43, 'verse': 9, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ خَلَقَهُنَّ ٱلۡعَزِيزُ ٱلۡعَلِيمُ\nУлардан: «Осмонлару ерни ким яратган!» – деб сўрасанг, албатта: «Уларни азийз ва ўта илмли зот яратган», дерлар'}, {'chapter': 43, 'verse': 10, 'text': 'ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدٗا وَجَعَلَ لَكُمۡ فِيهَا سُبُلٗا لَّعَلَّكُمۡ تَهۡتَدُونَ\nУ зот сизга ерни бешик қилиб берди ва сизга унда йўллар қилиб қўйди. Шоядки, ҳидоят топсангиз'}, {'chapter': 43, 'verse': 11, 'text': 'وَٱلَّذِي نَزَّلَ مِنَ ٱلسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَنشَرۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ تُخۡرَجُونَ\nУ зот осмондан ўлчов ила сув туширди. Бас, биз у(сув) ила ўлик юртни тирилтирдик. Сиз ҳам шунга ўхшаш чиқарилурсиз'}, {'chapter': 43, 'verse': 12, 'text': 'وَٱلَّذِي خَلَقَ ٱلۡأَزۡوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ ٱلۡفُلۡكِ وَٱلۡأَنۡعَٰمِ مَا تَرۡكَبُونَ\nУ зот барча жуфтларни яратган ва сизга кемалар ҳамда чорва ҳайвонларидан минадиган нарсаларингизни қилди'}, {'chapter': 43, 'verse': 13, 'text': 'لِتَسۡتَوُۥاْ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذۡكُرُواْ نِعۡمَةَ رَبِّكُمۡ إِذَا ٱسۡتَوَيۡتُمۡ عَلَيۡهِ وَتَقُولُواْ سُبۡحَٰنَ ٱلَّذِي سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُۥ مُقۡرِنِينَ\nТоки, сиз уларнинг устига ўрнашгайсизлар, сўнгра, уларнинг устига ўрнашиб олгач, Роббингиз неъматини эслаб: «Бизга буни бўйсундирган Зот покдир. Биз бунга қодир эмас эдик'}, {'chapter': 43, 'verse': 14, 'text': 'وَإِنَّآ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ\nВа, албатта, биз Роббимизга қайтгувчилардирмиз», дегайсиз'}, {'chapter': 43, 'verse': 15, 'text': 'وَجَعَلُواْ لَهُۥ مِنۡ عِبَادِهِۦ جُزۡءًاۚ إِنَّ ٱلۡإِنسَٰنَ لَكَفُورٞ مُّبِينٌ\nУлар бандаларидан Унга бўлак (ҳукм) қилдилар. Албатта, инсон очиқ-ойдин ношукрдир'}, {'chapter': 43, 'verse': 16, 'text': 'أَمِ ٱتَّخَذَ مِمَّا يَخۡلُقُ بَنَاتٖ وَأَصۡفَىٰكُم بِٱلۡبَنِينَ\nЁки У зот Ўзи яратадиган нарсадан қизлар тутиб, сизларни ўғиллар ила сийладими'}, {'chapter': 43, 'verse': 17, 'text': 'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحۡمَٰنِ مَثَلٗا ظَلَّ وَجۡهُهُۥ مُسۡوَدّٗا وَهُوَ كَظِيمٌ\nҚачонки улардан бирортасига, Роҳманга мисол келтирган нарсанинг хушхабари берилса, ғазабга тўлиб, юзи қорайиб кетур'}, {'chapter': 43, 'verse': 18, 'text': 'أَوَمَن يُنَشَّؤُاْ فِي ٱلۡحِلۡيَةِ وَهُوَ فِي ٱلۡخِصَامِ غَيۡرُ مُبِينٖ\nЗебу зийнат ичида ўстириладиган ва хусумат пайтида очиқ-ойдин бўла олмайдиган кимсаними'}, {'chapter': 43, 'verse': 19, 'text': 'وَجَعَلُواْ ٱلۡمَلَـٰٓئِكَةَ ٱلَّذِينَ هُمۡ عِبَٰدُ ٱلرَّحۡمَٰنِ إِنَٰثًاۚ أَشَهِدُواْ خَلۡقَهُمۡۚ سَتُكۡتَبُ شَهَٰدَتُهُمۡ وَيُسۡـَٔلُونَ\nУлар Роҳманнинг бандалари бўлмиш фаришталарни аёллар ҳисобладилар. Ёки уларнинг яратилишига гувоҳ бўлдиларми?! Албатта, бу гувоҳликлари ёзилур ва сўроққа тутилурлар'}, {'chapter': 43, 'verse': 20, 'text': 'وَقَالُواْ لَوۡ شَآءَ ٱلرَّحۡمَٰنُ مَا عَبَدۡنَٰهُمۗ مَّا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَخۡرُصُونَ\nУлар: «Агар Роҳман хоҳлаганида, биз уларга ибодат қилмас эдик», дедилар. Уларда бу ҳақда ҳеч бир илм йўқ. Улар фақат алжирамоқдалар, холос'}, {'chapter': 43, 'verse': 21, 'text': 'أَمۡ ءَاتَيۡنَٰهُمۡ كِتَٰبٗا مِّن قَبۡلِهِۦ فَهُم بِهِۦ مُسۡتَمۡسِكُونَ\nЁки Биз уларга у(Қуръон)дан олдин бир китоб берганмиз-у, улар уни маҳкам тутувчиларми'}, {'chapter': 43, 'verse': 22, 'text': 'بَلۡ قَالُوٓاْ إِنَّا وَجَدۡنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٖ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّهۡتَدُونَ\nЙўқ! Улар: «Биз ота-боболаримизни бир динда топдикки, албатта, биз уларнинг изларидан (эргашиб) ҳидоят топгувчидирмиз», дедилар'}, {'chapter': 43, 'verse': 23, 'text': 'وَكَذَٰلِكَ مَآ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَآ إِنَّا وَجَدۡنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٖ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّقۡتَدُونَ\nХудди шунингдек, Биз сендан илгари ҳам қайси бир қишлоқ-шаҳарга огоҳлантиргувчи юборсак, албатта, унинг зодагонлари: «Таъкидки, биз ота-боболаримизни бир динда топдик ва, албатта, биз уларнинг изларидан эргашгувчилардирмиз», дедилар'}, {'chapter': 43, 'verse': 24, 'text': '۞قَٰلَ أَوَلَوۡ جِئۡتُكُم بِأَهۡدَىٰ مِمَّا وَجَدتُّمۡ عَلَيۡهِ ءَابَآءَكُمۡۖ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ\nУ (огоҳлантирувчи): «Агар мен сизга сиз ота-бобонгизни устида топган нарсангиздан ҳам ҳидоятлироқни келтирган бўлсам ҳам-а?!» – деди. Улар: «Биз сен ила юборилган нарсага кофирдирмиз», дедилар'}, {'chapter': 43, 'verse': 25, 'text': 'فَٱنتَقَمۡنَا مِنۡهُمۡۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\nБас, Биз улардан интиқом олдик. Энди назар сол. (Пайғамбарларни) ёлғончи қилгувчиларнинг оқибати қандай бўлди'}, {'chapter': 43, 'verse': 26, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ وَقَوۡمِهِۦٓ إِنَّنِي بَرَآءٞ مِّمَّا تَعۡبُدُونَ\nЭсла, Иброҳим отасига ва қавмига: «Мен, албатта, сиз ибодат қилаётган нарсадан воз кечгувчиман'}, {'chapter': 43, 'verse': 27, 'text': 'إِلَّا ٱلَّذِي فَطَرَنِي فَإِنَّهُۥ سَيَهۡدِينِ\nМагар мени яратган Зотдангина (воз кечмасман). Албатта, У зот мени ҳидоят қилур», деган эди'}, {'chapter': 43, 'verse': 28, 'text': 'وَجَعَلَهَا كَلِمَةَۢ بَاقِيَةٗ فِي عَقِبِهِۦ لَعَلَّهُمۡ يَرۡجِعُونَ\nВа бу (сўз)ни у ўз ортидан зурриётига боқий қолгувчи қилди. Шоядки (иймонга) қайтсалар'}, {'chapter': 43, 'verse': 29, 'text': 'بَلۡ مَتَّعۡتُ هَـٰٓؤُلَآءِ وَءَابَآءَهُمۡ حَتَّىٰ جَآءَهُمُ ٱلۡحَقُّ وَرَسُولٞ مُّبِينٞ\nМен эса, анавиларни ва уларнинг ота-боболарини, то уларга ҳақ ҳамда очиқ-ойдин Пайғамбар келгунча фойдалантирдим'}, {'chapter': 43, 'verse': 30, 'text': 'وَلَمَّا جَآءَهُمُ ٱلۡحَقُّ قَالُواْ هَٰذَا سِحۡرٞ وَإِنَّا بِهِۦ كَٰفِرُونَ\nҚачонки уларга ҳақ келганида, улар: «Бу сеҳрдир ва, албатта, биз бунга кофирдирмиз», дедилар'}, {'chapter': 43, 'verse': 31, 'text': 'وَقَالُواْ لَوۡلَا نُزِّلَ هَٰذَا ٱلۡقُرۡءَانُ عَلَىٰ رَجُلٖ مِّنَ ٱلۡقَرۡيَتَيۡنِ عَظِيمٍ\nУлар: «Ушбу Қуръон икки шаҳардан (бирида) бўлган улуғ кишига нозил қилинганида эди», дедилар'}, {'chapter': 43, 'verse': 32, 'text': 'أَهُمۡ يَقۡسِمُونَ رَحۡمَتَ رَبِّكَۚ نَحۡنُ قَسَمۡنَا بَيۡنَهُم مَّعِيشَتَهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَرَفَعۡنَا بَعۡضَهُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَتَّخِذَ بَعۡضُهُم بَعۡضٗا سُخۡرِيّٗاۗ وَرَحۡمَتُ رَبِّكَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ\nЁки улар Роббингнинг раҳматини тақсимлайдиларми? Биз ҳаёти дунёда уларнинг ораларида маишатларини тақсимладик ва баъзилари баъзиларини хизматкор қилиб олишлари учун айримларининг даражаларини айримларидан устун қилиб қўйганмиз. Роббингнинг раҳмати улар жамлайдиган нарсадан яхшидир'}, {'chapter': 43, 'verse': 33, 'text': 'وَلَوۡلَآ أَن يَكُونَ ٱلنَّاسُ أُمَّةٗ وَٰحِدَةٗ لَّجَعَلۡنَا لِمَن يَكۡفُرُ بِٱلرَّحۡمَٰنِ لِبُيُوتِهِمۡ سُقُفٗا مِّن فِضَّةٖ وَمَعَارِجَ عَلَيۡهَا يَظۡهَرُونَ\nАгар одамлар бир миллат бўлиб қолишлари (хавфи) бўлмаганда, албатта, Биз Роҳманга куфр келтирадиганларнинг уйлари шифтини ва унга чиқадиган нарвонларини кумушдан қилиб қўяр эдик'}, {'chapter': 43, 'verse': 34, 'text': 'وَلِبُيُوتِهِمۡ أَبۡوَٰبٗا وَسُرُرًا عَلَيۡهَا يَتَّكِـُٔونَ\nВа уйларининг эшикларини ҳам, устида ёнбошлаб ётадиган сўриларини ҳам'}, {'chapter': 43, 'verse': 35, 'text': 'وَزُخۡرُفٗاۚ وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَٱلۡأٓخِرَةُ عِندَ رَبِّكَ لِلۡمُتَّقِينَ\nВа зебу зийнату безакларни ҳам. Буларнинг ҳаммаси ҳаёти дунёнинг матоҳидан бошқа нарса эмас. Охират эса, Роббинг наздида тақводорлар учундир'}, {'chapter': 43, 'verse': 36, 'text': 'وَمَن يَعۡشُ عَن ذِكۡرِ ٱلرَّحۡمَٰنِ نُقَيِّضۡ لَهُۥ شَيۡطَٰنٗا فَهُوَ لَهُۥ قَرِينٞ\nКим Роҳманнинг эслатмасидан шабкўр бўлиб олса, Биз унга шайтонни яқин қилиб қўюрмиз, бас, у унга доимий ҳамроҳ бўлур'}, {'chapter': 43, 'verse': 37, 'text': 'وَإِنَّهُمۡ لَيَصُدُّونَهُمۡ عَنِ ٱلسَّبِيلِ وَيَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ\nАлбатта, у(шайтон)лар йўлдан тўсурлар ва улар ўзларини шак-шубҳасиз ҳидоят топгувчилардек ҳисоблайдилар'}, {'chapter': 43, 'verse': 38, 'text': 'حَتَّىٰٓ إِذَا جَآءَنَا قَالَ يَٰلَيۡتَ بَيۡنِي وَبَيۡنَكَ بُعۡدَ ٱلۡمَشۡرِقَيۡنِ فَبِئۡسَ ٱلۡقَرِينُ\nТоки, ҳузуримизга келганда: «Қани энди мен билан сенинг оранг мағриб ила машриқчалик узоқ бўлса эди. Бас, сен нақадар ёмон ҳамроҳсан», дер'}, {'chapter': 43, 'verse': 39, 'text': 'وَلَن يَنفَعَكُمُ ٱلۡيَوۡمَ إِذ ظَّلَمۡتُمۡ أَنَّكُمۡ فِي ٱلۡعَذَابِ مُشۡتَرِكُونَ\nЗулм қилганингиз учун бугунги кунда азобга шерик бўлишингиз ҳам ҳеч фойда бермас'}, {'chapter': 43, 'verse': 40, 'text': 'أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ أَوۡ تَهۡدِي ٱلۡعُمۡيَ وَمَن كَانَ فِي ضَلَٰلٖ مُّبِينٖ\nСен карларга эшиттира олармидинг ёки кўрларни ва очиқ-ойдин залолатда бўлган кимсаларни ҳидоятга сола олармидинг'}, {'chapter': 43, 'verse': 41, 'text': 'فَإِمَّا نَذۡهَبَنَّ بِكَ فَإِنَّا مِنۡهُم مُّنتَقِمُونَ\nАгар сени кетказсак ҳам, албатта, Биз улардан интиқом олурмиз'}, {'chapter': 43, 'verse': 42, 'text': 'أَوۡ نُرِيَنَّكَ ٱلَّذِي وَعَدۡنَٰهُمۡ فَإِنَّا عَلَيۡهِم مُّقۡتَدِرُونَ\nЁки Биз уларга ваъда қилинган нарсани сенга кўрсатурмиз. Бас, албатта, Биз уларга (азоб беришга) қодирмиз'}, {'chapter': 43, 'verse': 43, 'text': 'فَٱسۡتَمۡسِكۡ بِٱلَّذِيٓ أُوحِيَ إِلَيۡكَۖ إِنَّكَ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nБас, ўзингга ваҳий қилинган нарсани маҳкам тут! Албатта, сен тўғри йўлдасан'}, {'chapter': 43, 'verse': 44, 'text': 'وَإِنَّهُۥ لَذِكۡرٞ لَّكَ وَلِقَوۡمِكَۖ وَسَوۡفَ تُسۡـَٔلُونَ\nАлбатта, у(Қуръон) сенга ҳам, қавмингга ҳам шарафдир. Ва, албатта, сўралурсизлар'}, {'chapter': 43, 'verse': 45, 'text': 'وَسۡـَٔلۡ مَنۡ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رُّسُلِنَآ أَجَعَلۡنَا مِن دُونِ ٱلرَّحۡمَٰنِ ءَالِهَةٗ يُعۡبَدُونَ\nСендан олдин Биз юборган Пайғамбарларимиздан сўра: Роҳмандан бошқа ибодат қилинадиган илоҳлар қилган эканмизми'}, {'chapter': 43, 'verse': 46, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَقَالَ إِنِّي رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ\nБатаҳқиқ, Биз Мусони Ўз оят(мўъжиза)ларимиз ила Фиръавнга ва унинг аъёнларига юбордик. Бас, у: «Албатта, мен оламлар Роббининг Пайғамбаридирман», деди'}, {'chapter': 43, 'verse': 47, 'text': 'فَلَمَّا جَآءَهُم بِـَٔايَٰتِنَآ إِذَا هُم مِّنۡهَا يَضۡحَكُونَ\nБас, қачонки у уларга оят (мўъжиза)ларимизни келтирганда, улар баногоҳ у(мўъжиза)лардан кула бошладилар'}, {'chapter': 43, 'verse': 48, 'text': 'وَمَا نُرِيهِم مِّنۡ ءَايَةٍ إِلَّا هِيَ أَكۡبَرُ مِنۡ أُخۡتِهَاۖ وَأَخَذۡنَٰهُم بِٱلۡعَذَابِ لَعَلَّهُمۡ يَرۡجِعُونَ\nБиз уларга қайси бир оят (мўъжиза)ни кўрсатсак ҳам, албатта, у ўз шеригидан каттароқ бўлар эди. Шояд, (хатоларидан) қайтсалар, деб уларни азоб-ла тутдик'}, {'chapter': 43, 'verse': 49, 'text': 'وَقَالُواْ يَـٰٓأَيُّهَ ٱلسَّاحِرُ ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهۡتَدُونَ\nУлар: «Эй сеҳргар! Сенга берган аҳдига биноан, биз учун Роббингга дуо қил! Албатта, биз ҳидоят топгувчилардан бўламиз», дедилар'}, {'chapter': 43, 'verse': 50, 'text': 'فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِذَا هُمۡ يَنكُثُونَ\nБас, қачонки улардан азобни аритсанг, тўсатдан улар (аҳдни) бузарлар'}, {'chapter': 43, 'verse': 51, 'text': 'وَنَادَىٰ فِرۡعَوۡنُ فِي قَوۡمِهِۦ قَالَ يَٰقَوۡمِ أَلَيۡسَ لِي مُلۡكُ مِصۡرَ وَهَٰذِهِ ٱلۡأَنۡهَٰرُ تَجۡرِي مِن تَحۡتِيٓۚ أَفَلَا تُبۡصِرُونَ\nФиръавн ўз қавмига: «Эй қавмим! Миср мулки ва мана бу остимдан оқиб турган анҳорлар меники эмасми?! Кўрмаяпсизларми'}, {'chapter': 43, 'verse': 52, 'text': 'أَمۡ أَنَا۠ خَيۡرٞ مِّنۡ هَٰذَا ٱلَّذِي هُوَ مَهِينٞ وَلَا يَكَادُ يُبِينُ\nМен мана бу хор, гапини очиқ баён қила олмайдиган кимсадан яхши эмасманми'}, {'chapter': 43, 'verse': 53, 'text': 'فَلَوۡلَآ أُلۡقِيَ عَلَيۡهِ أَسۡوِرَةٞ مِّن ذَهَبٍ أَوۡ جَآءَ مَعَهُ ٱلۡمَلَـٰٓئِكَةُ مُقۡتَرِنِينَ\nУнга тиллодан бўлган билакузуклар ташланса ёки у билан бирга фаришталар ҳамроҳ бўлиб келса бўлмас эдими?!» – деб нидо қилди'}, {'chapter': 43, 'verse': 54, 'text': 'فَٱسۡتَخَفَّ قَوۡمَهُۥ فَأَطَاعُوهُۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nБас, у қавмини лақиллатди ва улар унга итоат қилдилар. Чунки, улар фосиқ қавм эдилар'}, {'chapter': 43, 'verse': 55, 'text': 'فَلَمَّآ ءَاسَفُونَا ٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ أَجۡمَعِينَ\nБас, қачонки улар Бизни дарғазаб қилишгач, улардан интиқом олдик — уларнинг барчаларини жамлаб, ғарқ қилдик'}, {'chapter': 43, 'verse': 56, 'text': 'فَجَعَلۡنَٰهُمۡ سَلَفٗا وَمَثَلٗا لِّلۡأٓخِرِينَ\nБас, уларни ўтмишга айлантирдик ва кейингиларга мисол қилиб қўйдик'}, {'chapter': 43, 'verse': 57, 'text': '۞وَلَمَّا ضُرِبَ ٱبۡنُ مَرۡيَمَ مَثَلًا إِذَا قَوۡمُكَ مِنۡهُ يَصِدُّونَ\nИбн Марям мисол келтирилган чоғда сенинг қавминг бирдан шодланиб қичқирурлар'}, {'chapter': 43, 'verse': 58, 'text': 'وَقَالُوٓاْ ءَأَٰلِهَتُنَا خَيۡرٌ أَمۡ هُوَۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلَۢاۚ بَلۡ هُمۡ قَوۡمٌ خَصِمُونَ\nУлар: «Бизнинг олиҳаларимиз яхшими ёки уми?» – дедилар. Улар уни сенга фақат талашиб-тортишиш учунгина мисол келтирдилар. Зеро, улар хусуматчи қавмдирлар'}, {'chapter': 43, 'verse': 59, 'text': 'إِنۡ هُوَ إِلَّا عَبۡدٌ أَنۡعَمۡنَا عَلَيۡهِ وَجَعَلۡنَٰهُ مَثَلٗا لِّبَنِيٓ إِسۡرَـٰٓءِيلَ\nУ фақат бир бандадир. Биз унга неъмат бердик ва уни Бани Исроилга мисол қилдик'}, {'chapter': 43, 'verse': 60, 'text': 'وَلَوۡ نَشَآءُ لَجَعَلۡنَا مِنكُم مَّلَـٰٓئِكَةٗ فِي ٱلۡأَرۡضِ يَخۡلُفُونَ\nАгар хоҳласак, ўрнингизга ер юзида халифа бўладиган фаришталар қилиб қўяр эдик'}, {'chapter': 43, 'verse': 61, 'text': 'وَإِنَّهُۥ لَعِلۡمٞ لِّلسَّاعَةِ فَلَا تَمۡتَرُنَّ بِهَا وَٱتَّبِعُونِۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nАлбатта, у (Ийсо) соат(қиёмат) илми(белгиси)дир. Бас, сиз у (қиёмат) ҳақида ҳеч шубҳа қилманг ва менга эргашинг. Мана шу тўғри йўлдир'}, {'chapter': 43, 'verse': 62, 'text': 'وَلَا يَصُدَّنَّكُمُ ٱلشَّيۡطَٰنُۖ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nШайтон сизни ҳаргиз (тўғри йўлдан) тўсмасин. Албатта, у сизга очиқ-ойдин душмандир'}, {'chapter': 43, 'verse': 63, 'text': 'وَلَمَّا جَآءَ عِيسَىٰ بِٱلۡبَيِّنَٰتِ قَالَ قَدۡ جِئۡتُكُم بِٱلۡحِكۡمَةِ وَلِأُبَيِّنَ لَكُم بَعۡضَ ٱلَّذِي تَخۡتَلِفُونَ فِيهِۖ فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nИйсо очиқ-ойдин ҳужжат (мўъжиза)лар билан келган чоғида: «Батаҳқиқ, мен сизга ҳикматни келтирдим ва сиз ихтилоф қилаётган баъзи нарсаларни баён қилиш учун келдим. Бас, Аллоҳга тақво ва менга итоат қилинглар'}, {'chapter': 43, 'verse': 64, 'text': 'إِنَّ ٱللَّهَ هُوَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nАлбатта, Аллоҳ менинг ҳам Роббим, сизнинг ҳам Роббингиздир. Бас, Унга ибодат қилинг. Мана шу тўғри йўлдир», деди'}, {'chapter': 43, 'verse': 65, 'text': 'فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَيۡنِهِمۡۖ فَوَيۡلٞ لِّلَّذِينَ ظَلَمُواْ مِنۡ عَذَابِ يَوۡمٍ أَلِيمٍ\nБас, уларнинг орасидан фирқалар ихтилоф қилдилар. Зулм қилганлар ҳолига аламли кун азобидан вой бўлсин'}, {'chapter': 43, 'verse': 66, 'text': 'هَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nУлар фақат ўзлари ҳам сезмай қолган ҳолда соат(қиёмат) келиб қолишини кутмоқдалар, холос'}, {'chapter': 43, 'verse': 67, 'text': 'ٱلۡأَخِلَّآءُ يَوۡمَئِذِۭ بَعۡضُهُمۡ لِبَعۡضٍ عَدُوٌّ إِلَّا ٱلۡمُتَّقِينَ\nЎша кунда тақводорлардан бошқа дўстлар бир-бирларига душмандир'}, {'chapter': 43, 'verse': 68, 'text': 'يَٰعِبَادِ لَا خَوۡفٌ عَلَيۡكُمُ ٱلۡيَوۡمَ وَلَآ أَنتُمۡ تَحۡزَنُونَ\nЭй бандаларим! Бугунги кунда сизга хавф йўқ ва маҳзун ҳам бўлмассиз'}, {'chapter': 43, 'verse': 69, 'text': 'ٱلَّذِينَ ءَامَنُواْ بِـَٔايَٰتِنَا وَكَانُواْ مُسۡلِمِينَ\nОятларимизга иймон келтирганлар ва мусулмон бўлганлар'}, {'chapter': 43, 'verse': 70, 'text': 'ٱدۡخُلُواْ ٱلۡجَنَّةَ أَنتُمۡ وَأَزۡوَٰجُكُمۡ تُحۡبَرُونَ\nЖаннатга киринглар, сизлар ва жуфтларингиз шод бўлурсизлар'}, {'chapter': 43, 'verse': 71, 'text': 'يُطَافُ عَلَيۡهِم بِصِحَافٖ مِّن ذَهَبٖ وَأَكۡوَابٖۖ وَفِيهَا مَا تَشۡتَهِيهِ ٱلۡأَنفُسُ وَتَلَذُّ ٱلۡأَعۡيُنُۖ وَأَنتُمۡ فِيهَا خَٰلِدُونَ\nУларга тиллодан бўлган товоқлар ва қадаҳлар айлантирилур. У ерда кўнгил иштаҳа қиладиган ва кўзлар лаззатланадиган нарсалар бордир. Сизлар у ерда абадий қолурсизлар'}, {'chapter': 43, 'verse': 72, 'text': 'وَتِلۡكَ ٱلۡجَنَّةُ ٱلَّتِيٓ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ\nМана шу, қилиб юрган амалларингиз сабабли сизга мерос қилиб берилган жаннатдир'}, {'chapter': 43, 'verse': 73, 'text': 'لَكُمۡ فِيهَا فَٰكِهَةٞ كَثِيرَةٞ مِّنۡهَا تَأۡكُلُونَ\nСизга у ерда кўплаб мевалар бор, улардан ерсизлар'}, {'chapter': 43, 'verse': 74, 'text': 'إِنَّ ٱلۡمُجۡرِمِينَ فِي عَذَابِ جَهَنَّمَ خَٰلِدُونَ\nАлбатта, жиноятчилар жаҳаннам азобида абадий қолурлар'}, {'chapter': 43, 'verse': 75, 'text': 'لَا يُفَتَّرُ عَنۡهُمۡ وَهُمۡ فِيهِ مُبۡلِسُونَ\nУ улардан енгиллатилмас ва улар у(азоб)да ноумид ҳолда қолурлар'}, {'chapter': 43, 'verse': 76, 'text': 'وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُواْ هُمُ ٱلظَّـٰلِمِينَ\nБиз уларга зулм қилганимиз йўқ. Лекин улар ўзлари золим бўлдилар'}, {'chapter': 43, 'verse': 77, 'text': 'وَنَادَوۡاْ يَٰمَٰلِكُ لِيَقۡضِ عَلَيۡنَا رَبُّكَۖ قَالَ إِنَّكُم مَّـٰكِثُونَ\nУлар: «Эй Молик! Роббинг бизни битирсин!» – деб нидо қилдилар. У: «Албатта, сиз туриб қолгувчисиз», деди'}, {'chapter': 43, 'verse': 78, 'text': 'لَقَدۡ جِئۡنَٰكُم بِٱلۡحَقِّ وَلَٰكِنَّ أَكۡثَرَكُمۡ لِلۡحَقِّ كَٰرِهُونَ\nБатаҳқиқ, Биз сизга ҳақни келтирдик. Лекин кўпларингиз ҳақни ёмон кўргувчисиз'}, {'chapter': 43, 'verse': 79, 'text': 'أَمۡ أَبۡرَمُوٓاْ أَمۡرٗا فَإِنَّا مُبۡرِمُونَ\nЁки улар бирон ишга қарор қилдиларми? Бас, Биз, албатта, қарор қилгувчимиз'}, {'chapter': 43, 'verse': 80, 'text': 'أَمۡ يَحۡسَبُونَ أَنَّا لَا نَسۡمَعُ سِرَّهُمۡ وَنَجۡوَىٰهُمۚ بَلَىٰ وَرُسُلُنَا لَدَيۡهِمۡ يَكۡتُبُونَ\nЁки улар Бизни уларнинг сирларини (билмас) ва пичирлашиб қилган маслаҳатларини эшитмас, деб ҳисоблайдиларми? Йўқ! Ҳолбуки, элчиларимиз уларнинг олдиларида ёзмоқдалар'}, {'chapter': 43, 'verse': 81, 'text': 'قُلۡ إِن كَانَ لِلرَّحۡمَٰنِ وَلَدٞ فَأَنَا۠ أَوَّلُ ٱلۡعَٰبِدِينَ\nСен: «Агар Роҳманнинг боласи бўлганида, мен унга ибодат қилгувчиларнинг биринчиси бўлар эдим», деб айт'}, {'chapter': 43, 'verse': 82, 'text': 'سُبۡحَٰنَ رَبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ رَبِّ ٱلۡعَرۡشِ عَمَّا يَصِفُونَ\nОсмонлару ернинг Робби, Аршнинг Робби улар қилаётган васфдан покдир'}, {'chapter': 43, 'verse': 83, 'text': 'فَذَرۡهُمۡ يَخُوضُواْ وَيَلۡعَبُواْ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي يُوعَدُونَ\nБас, уларни қўйиб бер, ўзларига ваъда қилинаётган кунга рўбарў бўлгунларича (ёмонликка) шўнғийверсинлар, ўйнайверсинлар'}, {'chapter': 43, 'verse': 84, 'text': 'وَهُوَ ٱلَّذِي فِي ٱلسَّمَآءِ إِلَٰهٞ وَفِي ٱلۡأَرۡضِ إِلَٰهٞۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡعَلِيمُ\nУ зот осмонда ҳам илоҳдир, ерда ҳам илоҳдир. У зот ўта ҳикматли, ўта илмлидир'}, {'chapter': 43, 'verse': 85, 'text': 'وَتَبَارَكَ ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَعِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَإِلَيۡهِ تُرۡجَعُونَ\nОсмонлару ернинг ва уларнинг орасидаги нарсаларнинг мулкига эга бўлган Зот баракотли(буюк)дир. Соат (қиёмат) илми Унинг ҳузуридадир. Унгагина қайтарилурсизлар'}, {'chapter': 43, 'verse': 86, 'text': 'وَلَا يَمۡلِكُ ٱلَّذِينَ يَدۡعُونَ مِن دُونِهِ ٱلشَّفَٰعَةَ إِلَّا مَن شَهِدَ بِٱلۡحَقِّ وَهُمۡ يَعۡلَمُونَ\nАллоҳдан ўзга илтижо қилаётган нарсалари шафоатга эга бўлмаслар. Магар, билган ҳолларида ҳақ шаҳодат берганларгина (эга бўлурлар)'}, {'chapter': 43, 'verse': 87, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَهُمۡ لَيَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ يُؤۡفَكُونَ\nАгар сен улардан ўзларини ким яратганини сўрасанг, албатта, «Аллоҳ», дерлар Бас, қаёққа бурилиб кетмоқдалар'}, {'chapter': 43, 'verse': 88, 'text': 'وَقِيلِهِۦ يَٰرَبِّ إِنَّ هَـٰٓؤُلَآءِ قَوۡمٞ لَّا يُؤۡمِنُونَ\nУ(Муҳаммад)нинг: «Эй Роббим, албатта, анавилар иймон келтирмайдиган қавмлардир», деган гапини ҳам (билур)'}, {'chapter': 43, 'verse': 89, 'text': 'فَٱصۡفَحۡ عَنۡهُمۡ وَقُلۡ سَلَٰمٞۚ فَسَوۡفَ يَعۡلَمُونَ\nБас, сен улардан юз ўгир ва: «Сизларга салом бўлсин», дегин. Бас, улар тезда билурлар'}], '44': [{'chapter': 44, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 44, 'verse': 2, 'text': 'وَٱلۡكِتَٰبِ ٱلۡمُبِينِ\nОчиқ-ойдин Китобга қасам'}, {'chapter': 44, 'verse': 3, 'text': 'إِنَّآ أَنزَلۡنَٰهُ فِي لَيۡلَةٖ مُّبَٰرَكَةٍۚ إِنَّا كُنَّا مُنذِرِينَ\nАлбатта, Биз уни муборак кечада нозил қилдик. Албатта, Биз огоҳлантиргувчи бўлдик'}, {'chapter': 44, 'verse': 4, 'text': 'فِيهَا يُفۡرَقُ كُلُّ أَمۡرٍ حَكِيمٍ\nЎша(кеча)да ҳар бир ҳикматли иш ажратилиб, ҳал қилинур'}, {'chapter': 44, 'verse': 5, 'text': 'أَمۡرٗا مِّنۡ عِندِنَآۚ إِنَّا كُنَّا مُرۡسِلِينَ\nБиз томонимиздан бўлган амрга биноан. Албатта, Биз Пайғамбар юборгувчи бўлдик'}, {'chapter': 44, 'verse': 6, 'text': 'رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nБу, Роббинг томонидан (кўрсатилган) раҳматдир. Албатта, Унинг Ўзи ўта эшитгувчи, ўта билгувчи Зотдир'}, {'chapter': 44, 'verse': 7, 'text': 'رَبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُم مُّوقِنِينَ\nУ — осмонлару ернинг ва уларнинг орасидаги нарсаларнинг Роббидир. Гар ишонгувчи бўлсангиз'}, {'chapter': 44, 'verse': 8, 'text': 'لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ رَبُّكُمۡ وَرَبُّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ\nУндан ўзга ибодатга сазовор зот йўқ. У тирилтирур ва ўлдирур. У сизнинг Роббингиз ва ота-боболарингизнинг Роббидир'}, {'chapter': 44, 'verse': 9, 'text': 'بَلۡ هُمۡ فِي شَكّٖ يَلۡعَبُونَ\nЙўқ! Улар шак-шубҳада ўйнамоқдалар'}, {'chapter': 44, 'verse': 10, 'text': 'فَٱرۡتَقِبۡ يَوۡمَ تَأۡتِي ٱلسَّمَآءُ بِدُخَانٖ مُّبِينٖ\nБас, осмон аниқ тутунни келтирадиган кунни кут'}, {'chapter': 44, 'verse': 11, 'text': 'يَغۡشَى ٱلنَّاسَۖ هَٰذَا عَذَابٌ أَلِيمٞ\nУ, одамларни ўраб олур. Бу, аламли азобдир'}, {'chapter': 44, 'verse': 12, 'text': 'رَّبَّنَا ٱكۡشِفۡ عَنَّا ٱلۡعَذَابَ إِنَّا مُؤۡمِنُونَ\nЭй Роббимиз! Биздан азобни кушойиш қил! Албатта, биз иймон келтиргувчидирмиз'}, {'chapter': 44, 'verse': 13, 'text': 'أَنَّىٰ لَهُمُ ٱلذِّكۡرَىٰ وَقَدۡ جَآءَهُمۡ رَسُولٞ مُّبِينٞ\nУларга қаёқдан ҳам эслатма бўлсин. Ҳолбуки, уларга очиқ-ойдин Пайғамбар келган эди'}, {'chapter': 44, 'verse': 14, 'text': 'ثُمَّ تَوَلَّوۡاْ عَنۡهُ وَقَالُواْ مُعَلَّمٞ مَّجۡنُونٌ\nСўнгра ундан юз ўгирдилар ва: «Бу — ўргатилган мажнун», дедилар'}, {'chapter': 44, 'verse': 15, 'text': 'إِنَّا كَاشِفُواْ ٱلۡعَذَابِ قَلِيلًاۚ إِنَّكُمۡ عَآئِدُونَ\nАлбатта, Биз азобни озгина кушойиш қилгувчимиз. Албатта, сизлар қайтгувчисизлар'}, {'chapter': 44, 'verse': 16, 'text': 'يَوۡمَ نَبۡطِشُ ٱلۡبَطۡشَةَ ٱلۡكُبۡرَىٰٓ إِنَّا مُنتَقِمُونَ\nКатта ушлаш ила ушлаганимиз куни, албатта, Биз интиқом олгувчимиз'}, {'chapter': 44, 'verse': 17, 'text': '۞وَلَقَدۡ فَتَنَّا قَبۡلَهُمۡ قَوۡمَ فِرۡعَوۡنَ وَجَآءَهُمۡ رَسُولٞ كَرِيمٌ\nБатаҳқиқ, улардан олдин Фиръавн қавмини синадик. Уларга карамли Пайғамбар келди'}, {'chapter': 44, 'verse': 18, 'text': 'أَنۡ أَدُّوٓاْ إِلَيَّ عِبَادَ ٱللَّهِۖ إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\n«Сизлар менга Аллоҳнинг бандаларини беринглар. Албатта, мен сизларга ишончли Пайғамбарман'}, {'chapter': 44, 'verse': 19, 'text': 'وَأَن لَّا تَعۡلُواْ عَلَى ٱللَّهِۖ إِنِّيٓ ءَاتِيكُم بِسُلۡطَٰنٖ مُّبِينٖ\nВа Аллоҳга мутакаббирлик қилманг. Албатта, мен сизга очиқ-ойдин ҳужжат келтирурман'}, {'chapter': 44, 'verse': 20, 'text': 'وَإِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُمۡ أَن تَرۡجُمُونِ\nВа, албатта, мен ўзимнинг Роббим ва сизнинг Роббингиздан мени тошбўрон қилишингиздан паноҳ сўрадим'}, {'chapter': 44, 'verse': 21, 'text': 'وَإِن لَّمۡ تُؤۡمِنُواْ لِي فَٱعۡتَزِلُونِ\nВа агар менга иймон келтирмасангиз, мендан четланинг»'}, {'chapter': 44, 'verse': 22, 'text': 'فَدَعَا رَبَّهُۥٓ أَنَّ هَـٰٓؤُلَآءِ قَوۡمٞ مُّجۡرِمُونَ\nУ Роббига: «Албатта, анавилар жиноятчи қавмдир», деб дуо қилди'}, {'chapter': 44, 'verse': 23, 'text': 'فَأَسۡرِ بِعِبَادِي لَيۡلًا إِنَّكُم مُّتَّبَعُونَ\nБас, бандаларим ила кечаси юриб кет. Албатта, сиз изингиздан қувилурсиз'}, {'chapter': 44, 'verse': 24, 'text': 'وَٱتۡرُكِ ٱلۡبَحۡرَ رَهۡوًاۖ إِنَّهُمۡ جُندٞ مُّغۡرَقُونَ\nВа денгизни сокин ҳолида қўй, албатта, улар ғарқ қилинадиган қўшиндир'}, {'chapter': 44, 'verse': 25, 'text': 'كَمۡ تَرَكُواْ مِن جَنَّـٰتٖ وَعُيُونٖ\nУлар ортларидан қанчадан-қанча боғу роғларни ва булоқларни'}, {'chapter': 44, 'verse': 26, 'text': 'وَزُرُوعٖ وَمَقَامٖ كَرِيمٖ\nЭкинзорларни ва яхши жойларни'}, {'chapter': 44, 'verse': 27, 'text': 'وَنَعۡمَةٖ كَانُواْ فِيهَا فَٰكِهِينَ\nВа ўзлари ичида маза қилаётган нозу неъматларни қолдирдилар'}, {'chapter': 44, 'verse': 28, 'text': 'كَذَٰلِكَۖ وَأَوۡرَثۡنَٰهَا قَوۡمًا ءَاخَرِينَ\nМана шундай! Биз у(нарса)ларни бошқа қавмларга мерос қилиб бердик'}, {'chapter': 44, 'verse': 29, 'text': 'فَمَا بَكَتۡ عَلَيۡهِمُ ٱلسَّمَآءُ وَٱلۡأَرۡضُ وَمَا كَانُواْ مُنظَرِينَ\nБас, уларга осмон ҳам, ер ҳам йиғламади ва улар муҳлат берилганлардан ҳам бўлмадилар'}, {'chapter': 44, 'verse': 30, 'text': 'وَلَقَدۡ نَجَّيۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ مِنَ ٱلۡعَذَابِ ٱلۡمُهِينِ\nБатаҳқиқ, Бани Исроилга хорловчи азобдан'}, {'chapter': 44, 'verse': 31, 'text': 'مِن فِرۡعَوۡنَۚ إِنَّهُۥ كَانَ عَالِيٗا مِّنَ ٱلۡمُسۡرِفِينَ\nФиръавндан нажот бердик. Албатта, у мутакаббир ва ҳаддидан ошганлардан эди'}, {'chapter': 44, 'verse': 32, 'text': 'وَلَقَدِ ٱخۡتَرۡنَٰهُمۡ عَلَىٰ عِلۡمٍ عَلَى ٱلۡعَٰلَمِينَ\nБатаҳқиқ, Биз уларни билиб туриб, оламлар ичидан танлаб олдик'}, {'chapter': 44, 'verse': 33, 'text': 'وَءَاتَيۡنَٰهُم مِّنَ ٱلۡأٓيَٰتِ مَا فِيهِ بَلَـٰٓؤٞاْ مُّبِينٌ\nВа уларга оят(мўъжиза)лардан ичида очиқ-ойдин синов бор нарсаларни бердик'}, {'chapter': 44, 'verse': 34, 'text': 'إِنَّ هَـٰٓؤُلَآءِ لَيَقُولُونَ\nАлбатта, анавилар дерлар'}, {'chapter': 44, 'verse': 35, 'text': 'إِنۡ هِيَ إِلَّا مَوۡتَتُنَا ٱلۡأُولَىٰ وَمَا نَحۡنُ بِمُنشَرِينَ\n«Биринчи ўлимимиздан бошқа ҳеч қандай ўлим йўқ ва биз қайта тирилтирилувчи эмасмиз'}, {'chapter': 44, 'verse': 36, 'text': 'فَأۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ\nАгар ростгўй бўлсангиз, ота-боболаримизни келтиринглар»'}, {'chapter': 44, 'verse': 37, 'text': 'أَهُمۡ خَيۡرٌ أَمۡ قَوۡمُ تُبَّعٖ وَٱلَّذِينَ مِن قَبۡلِهِمۡ أَهۡلَكۡنَٰهُمۡۚ إِنَّهُمۡ كَانُواْ مُجۡرِمِينَ\nУлар яхшироқми ёки Туббаъ қавмию улардан олдин ўтганларми?! Биз уларни ҳалок қилдик. Албатта, улар жиноятчи бўлган эдилар'}, {'chapter': 44, 'verse': 38, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا لَٰعِبِينَ\nОсмонлару ерни ва уларнинг орасидаги нарсаларни ўйнаб яратганимиз йўқ'}, {'chapter': 44, 'verse': 39, 'text': 'مَا خَلَقۡنَٰهُمَآ إِلَّا بِٱلۡحَقِّ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nБиз уларни фақат ҳақ ила яратдик, лекин кўплари билмаслар'}, {'chapter': 44, 'verse': 40, 'text': 'إِنَّ يَوۡمَ ٱلۡفَصۡلِ مِيقَٰتُهُمۡ أَجۡمَعِينَ\nАлбатта, ажратиш куни барчалари учун белгиланган вақтдир'}, {'chapter': 44, 'verse': 41, 'text': 'يَوۡمَ لَا يُغۡنِي مَوۡلًى عَن مَّوۡلٗى شَيۡـٔٗا وَلَا هُمۡ يُنصَرُونَ\nУ кунда дўст дўстга бирон нарсада асқотмас ва уларга ёрдам ҳам берилмас'}, {'chapter': 44, 'verse': 42, 'text': 'إِلَّا مَن رَّحِمَ ٱللَّهُۚ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nМагар, кимга Аллоҳ раҳим қилсагина (ёрдам берилур). Албатта, У азийз ва ўта раҳимли Зотдир'}, {'chapter': 44, 'verse': 43, 'text': 'إِنَّ شَجَرَتَ ٱلزَّقُّومِ\nАлбатта, Заққум дарахти'}, {'chapter': 44, 'verse': 44, 'text': 'طَعَامُ ٱلۡأَثِيمِ\nГуноҳкорнинг таомидир'}, {'chapter': 44, 'verse': 45, 'text': 'كَٱلۡمُهۡلِ يَغۡلِي فِي ٱلۡبُطُونِ\nУ, худди эритилган маъдандек, қоринларда қайнар'}, {'chapter': 44, 'verse': 46, 'text': 'كَغَلۡيِ ٱلۡحَمِيمِ\nҚайноқ сувнинг қайнаши каби'}, {'chapter': 44, 'verse': 47, 'text': 'خُذُوهُ فَٱعۡتِلُوهُ إِلَىٰ سَوَآءِ ٱلۡجَحِيمِ\nУни тутиб, жаҳиймнинг ўртасига судраб олиб боринглар'}, {'chapter': 44, 'verse': 48, 'text': 'ثُمَّ صُبُّواْ فَوۡقَ رَأۡسِهِۦ مِنۡ عَذَابِ ٱلۡحَمِيمِ\nСўнгра боши устидан қайноқ сув азобини қуйинглар'}, {'chapter': 44, 'verse': 49, 'text': 'ذُقۡ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡكَرِيمُ\n«Тотиб кўр! Албатта, сен ўзинг «азизу мукаррамсан!»'}, {'chapter': 44, 'verse': 50, 'text': 'إِنَّ هَٰذَا مَا كُنتُم بِهِۦ تَمۡتَرُونَ\nАлбатта, бу сиз (бўлишида) шубҳа қилиб юрган нарсадир!»'}, {'chapter': 44, 'verse': 51, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي مَقَامٍ أَمِينٖ\nАлбатта, тақводорлар омонлик жойида'}, {'chapter': 44, 'verse': 52, 'text': 'فِي جَنَّـٰتٖ وَعُيُونٖ\nБоғу роғлар ва булоқларда бўлурлар'}, {'chapter': 44, 'verse': 53, 'text': 'يَلۡبَسُونَ مِن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَقَٰبِلِينَ\nУлар шойи ипак киюрлар, бир-бирларига юзма-юз ҳолда (ўлтирурлар)'}, {'chapter': 44, 'verse': 54, 'text': 'كَذَٰلِكَ وَزَوَّجۡنَٰهُم بِحُورٍ عِينٖ\nАна шундай! Ва Биз уларни ҳури ийнларга уйлантирдик'}, {'chapter': 44, 'verse': 55, 'text': 'يَدۡعُونَ فِيهَا بِكُلِّ فَٰكِهَةٍ ءَامِنِينَ\nУлар у ерда омонлик ҳолларида турли меваларни чақирурлар'}, {'chapter': 44, 'verse': 56, 'text': 'لَا يَذُوقُونَ فِيهَا ٱلۡمَوۡتَ إِلَّا ٱلۡمَوۡتَةَ ٱلۡأُولَىٰۖ وَوَقَىٰهُمۡ عَذَابَ ٱلۡجَحِيمِ\nУлар у ерда биринчи ўлимдан бошқа ўлимни тотмаслар. У зот уларни жаҳийм азобидан сақлар'}, {'chapter': 44, 'verse': 57, 'text': 'فَضۡلٗا مِّن رَّبِّكَۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nБу, Роббингдан бўлган фазлдир. Ана шу улкан ютуқдир'}, {'chapter': 44, 'verse': 58, 'text': 'فَإِنَّمَا يَسَّرۡنَٰهُ بِلِسَانِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nБас, албатта, Биз у(Қуръон)ни сенинг тилинг ила осонлаштирдик. Шоядки, эсласалар'}, {'chapter': 44, 'verse': 59, 'text': 'فَٱرۡتَقِبۡ إِنَّهُم مُّرۡتَقِبُونَ\nБас, кутиб тур. Улар ҳам кутгувчидирлар'}], '45': [{'chapter': 45, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 45, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nБу китобнинг нозил қилиниши Азийз ва ўта ҳикматли Аллоҳдандир'}, {'chapter': 45, 'verse': 3, 'text': 'إِنَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّلۡمُؤۡمِنِينَ\nАлбатта, осмонлару ерда мўминлар учун оят(белги)лар бордир'}, {'chapter': 45, 'verse': 4, 'text': 'وَفِي خَلۡقِكُمۡ وَمَا يَبُثُّ مِن دَآبَّةٍ ءَايَٰتٞ لِّقَوۡمٖ يُوقِنُونَ\nВа сизларнинг яратилишингизда ва таратиб қўйилган жониворларда аниқ ишонадиган қавм учун оят(белги)лар бор'}, {'chapter': 45, 'verse': 5, 'text': 'وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن رِّزۡقٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَتَصۡرِيفِ ٱلرِّيَٰحِ ءَايَٰتٞ لِّقَوۡمٖ يَعۡقِلُونَ\nКечаю кундузнинг алмашиб туришида, Аллоҳ осмондан ризқ нозил қилиб, унинг ила ерни ўлимидан кейин тирилтиришида ва шамолларнинг йўналишида ақл юритадиган қавм учун оят(белги)лар бор'}, {'chapter': 45, 'verse': 6, 'text': 'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَ ٱللَّهِ وَءَايَٰتِهِۦ يُؤۡمِنُونَ\nБулар Аллоҳнинг оятларидир. Биз уларни сенга ҳақ ила тиловат қилурмиз. Улар Аллоҳни ва Унинг оятларини қўйиб, қайси гапга иймон келтирурлар'}, {'chapter': 45, 'verse': 7, 'text': 'وَيۡلٞ لِّكُلِّ أَفَّاكٍ أَثِيمٖ\nҲар бир ёлғончи гуноҳкорга ҳалокат бўлсин'}, {'chapter': 45, 'verse': 8, 'text': 'يَسۡمَعُ ءَايَٰتِ ٱللَّهِ تُتۡلَىٰ عَلَيۡهِ ثُمَّ يُصِرُّ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٖ\nУ ўзига тиловат қилинган Аллоҳнинг оятларини эшитади-ю, сўнгра, худди эшитмагандек, мутакаббирлик қилиб туриб олур. Бас, унга аламли азоб хушхабарини бер'}, {'chapter': 45, 'verse': 9, 'text': 'وَإِذَا عَلِمَ مِنۡ ءَايَٰتِنَا شَيۡـًٔا ٱتَّخَذَهَا هُزُوًاۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ\nҚачонки Бизнинг оятларимиздан бирор нарса билса, уни истеҳзо қилур. Ана ўшаларга хорловчи азоб бордир'}, {'chapter': 45, 'verse': 10, 'text': 'مِّن وَرَآئِهِمۡ جَهَنَّمُۖ وَلَا يُغۡنِي عَنۡهُم مَّا كَسَبُواْ شَيۡـٔٗا وَلَا مَا ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ\nВа ортларида жаҳаннам бор. Уларга касб қилган нарсалари ҳам, Аллоҳни қўйиб тутган «дўстлари» ҳам ҳеч бир нарсада асқотмас. Уларга улкан азоб бордир'}, {'chapter': 45, 'verse': 11, 'text': 'هَٰذَا هُدٗىۖ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٌ\nБу (Қуръон) ҳидоятдир. Роббилари оятларига куфр келтирганларга аламли азобнинг ашаддийси бордир'}, {'chapter': 45, 'verse': 12, 'text': '۞ٱللَّهُ ٱلَّذِي سَخَّرَ لَكُمُ ٱلۡبَحۡرَ لِتَجۡرِيَ ٱلۡفُلۡكُ فِيهِ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nАллоҳ сизга денгизни унда Ўз амри билан кемалар юриши учун ва сиз Унинг фазлидан исташингиз учун бўйсундириб қўйди. Шоядки, шукр келтирсангиз'}, {'chapter': 45, 'verse': 13, 'text': 'وَسَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ جَمِيعٗا مِّنۡهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nВа сизга осмонлардаги нарсаларни ва ердаги нарсаларни — ҳаммасини Ўз томонидан бўйсундириб қўйди. Албатта, ана шунда тафаккур қиладиган қавм учун оят(белги)лар бордир'}, {'chapter': 45, 'verse': 14, 'text': 'قُل لِّلَّذِينَ ءَامَنُواْ يَغۡفِرُواْ لِلَّذِينَ لَا يَرۡجُونَ أَيَّامَ ٱللَّهِ لِيَجۡزِيَ قَوۡمَۢا بِمَا كَانُواْ يَكۡسِبُونَ\nСен иймон келтирганларга айт. Аллоҳнинг кунларидан умидвор бўлмайдиганларни кечирсинлар. Токи, У зот бу қавмни касб қилиб юрган нарсалари учун мукофотласин'}, {'chapter': 45, 'verse': 15, 'text': 'مَنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۖ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ\nКим солиҳ амал қилса, ўзига фойда. Ким ёмонлик қилса, ўзига зарар. Сўнгра Роббингизга қайтарилурсиз'}, {'chapter': 45, 'verse': 16, 'text': 'وَلَقَدۡ ءَاتَيۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَى ٱلۡعَٰلَمِينَ\nБатаҳқиқ, Биз Бани Исроилга китобни, ҳукмни ва Пайғамбарликни бердик, уларга пок нарсаларни ризқ қилиб бердик ва уларни оламлар ичида афзал қилдик'}, {'chapter': 45, 'verse': 17, 'text': 'وَءَاتَيۡنَٰهُم بَيِّنَٰتٖ مِّنَ ٱلۡأَمۡرِۖ فَمَا ٱخۡتَلَفُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nВа Биз уларга (дин) иши ҳақида очиқ-ойдин баёнотлар бердик. Бас, улар ўзларига илм келганидан сўнг ўзаро ҳасад-адоват қилибгина ихтилофга тушдилар. Албатта, Роббинг қиёмат куни улар орасида ихтилоф қилган нарсалари ҳақида ҳукм чиқарур'}, {'chapter': 45, 'verse': 18, 'text': 'ثُمَّ جَعَلۡنَٰكَ عَلَىٰ شَرِيعَةٖ مِّنَ ٱلۡأَمۡرِ فَٱتَّبِعۡهَا وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ لَا يَعۡلَمُونَ\nСўнгра Биз сени (дин) ишида бир шариатга йўлладик. Бас, унга эргаш, билмайдиганларнинг ҳавойи нафсларига эргашма'}, {'chapter': 45, 'verse': 19, 'text': 'إِنَّهُمۡ لَن يُغۡنُواْ عَنكَ مِنَ ٱللَّهِ شَيۡـٔٗاۚ وَإِنَّ ٱلظَّـٰلِمِينَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۖ وَٱللَّهُ وَلِيُّ ٱلۡمُتَّقِينَ\nАлбатта, улар сендан Аллоҳ томонидан бўладиган ҳеч бир нарсани арита олмаслар. Ва, албатта, золимлар бир-бирларига дўстдирлар. Аллоҳ тақводорларнинг дўстидир'}, {'chapter': 45, 'verse': 20, 'text': 'هَٰذَا بَصَـٰٓئِرُ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُوقِنُونَ\nБу (Қуръон) одамлар учун қалб кўзлари, аниқ ишонадиган қавм учун ҳидоят ва раҳматдир'}, {'chapter': 45, 'verse': 21, 'text': 'أَمۡ حَسِبَ ٱلَّذِينَ ٱجۡتَرَحُواْ ٱلسَّيِّـَٔاتِ أَن نَّجۡعَلَهُمۡ كَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَوَآءٗ مَّحۡيَاهُمۡ وَمَمَاتُهُمۡۚ سَآءَ مَا يَحۡكُمُونَ\nНаҳотки, ёмонликларни қилганлар уларни иймон келтириб, солиҳ амаллар қилганларга ўхшаш қилмоғимизни, ҳаётлари ҳам, мамотлари ҳам баробар бўлишини гумон қилсалар?! Уларнинг қилган ҳукми нақадар ҳам ёмон'}, {'chapter': 45, 'verse': 22, 'text': 'وَخَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَلِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nАллоҳ осмонлару ерни ҳақ ила, ҳар бир жон қилган касбига яраша, зулм қилинмаган ҳолида жазоси (ажри)ни олиши учун яратган'}, {'chapter': 45, 'verse': 23, 'text': 'أَفَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلۡمٖ وَخَتَمَ عَلَىٰ سَمۡعِهِۦ وَقَلۡبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَٰوَةٗ فَمَن يَهۡدِيهِ مِنۢ بَعۡدِ ٱللَّهِۚ أَفَلَا تَذَكَّرُونَ\nАйтинг-чи, ҳавойи нафсини илоҳ қилиб олган, Аллоҳ уни билиб туриб залолатга солган ва қулоғига ҳамда қалбига муҳр босиб, кўзига парда тортиб қўйган кимсани Аллоҳдан бошқа ким ҳидоят қила оладир?! Эсламайсизларми'}, {'chapter': 45, 'verse': 24, 'text': 'وَقَالُواْ مَا هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا يُهۡلِكُنَآ إِلَّا ٱلدَّهۡرُۚ وَمَا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَظُنُّونَ\nУлар: «У (ҳаёт) фақат бу дунёдаги ҳаётимиздир. Ўламиз, тириламиз, бизни фақат замон ҳалок қилади», дерлар. Уларнинг бу ҳақда илмлари йўқ. Улар фақат гумон қиладилар, холос'}, {'chapter': 45, 'verse': 25, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ مَّا كَانَ حُجَّتَهُمۡ إِلَّآ أَن قَالُواْ ٱئۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ\nУларга очиқ-ойдин оятларимиз тиловат қилинган чоғда, уларнинг: «Агар ростгўй бўлсангиз, ота-боболаримизни келтиринг-чи?!» – дейишдан бошқа ҳужжатлари бўлмас'}, {'chapter': 45, 'verse': 26, 'text': 'قُلِ ٱللَّهُ يُحۡيِيكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يَجۡمَعُكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nСен: «Аллоҳ сизларни тирилтирур, сўнгра ўлдирур, сўнгра сизларни (бўлишида) шубҳа йўқ қиёмат кунида жамлар. Лекин одамларнинг кўплари билмаслар», деб айт'}, {'chapter': 45, 'verse': 27, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَخۡسَرُ ٱلۡمُبۡطِلُونَ\nВа осмонлару ернинг мулки Аллоҳникидир. Соат(қиёмат) қоим бўладиган кунда, ўша кунда ботилчилар зиён тортурлар'}, {'chapter': 45, 'verse': 28, 'text': 'وَتَرَىٰ كُلَّ أُمَّةٖ جَاثِيَةٗۚ كُلُّ أُمَّةٖ تُدۡعَىٰٓ إِلَىٰ كِتَٰبِهَا ٱلۡيَوۡمَ تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\nҲар бир умматни тиз чўккан ҳолида кўрурсан. Ҳар бир уммат ўз номаи аъмолига чақирилур: «Бугунги кунда қилиб юрган амалларингизнинг жазоси (ажри)ни олурсиз»'}, {'chapter': 45, 'verse': 29, 'text': 'هَٰذَا كِتَٰبُنَا يَنطِقُ عَلَيۡكُم بِٱلۡحَقِّۚ إِنَّا كُنَّا نَسۡتَنسِخُ مَا كُنتُمۡ تَعۡمَلُونَ\n«Мана бу китобимиз сизга ҳақни нутқ қилур. Албатта, Биз қилган амалингизни ёзиб турган эдик» (дейилур)'}, {'chapter': 45, 'verse': 30, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُدۡخِلُهُمۡ رَبُّهُمۡ فِي رَحۡمَتِهِۦۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡمُبِينُ\nБас, иймон келтириб солиҳ амалларни қилганларни Роббилари Ўз раҳматига киритур. Ана ўшанинг ўзи очиқ-ойдин ютуқдир'}, {'chapter': 45, 'verse': 31, 'text': 'وَأَمَّا ٱلَّذِينَ كَفَرُوٓاْ أَفَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَٱسۡتَكۡبَرۡتُمۡ وَكُنتُمۡ قَوۡمٗا مُّجۡرِمِينَ\nАммо куфр келтирганларга эса: «Ахир, сизга Менинг оятларим тиловат қилинмаганмиди?! Бас, сиз мутакаббирлик қилдингиз ва жиноятчи қавм бўлдингиз'}, {'chapter': 45, 'verse': 32, 'text': 'وَإِذَا قِيلَ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱلسَّاعَةُ لَا رَيۡبَ فِيهَا قُلۡتُم مَّا نَدۡرِي مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنّٗا وَمَا نَحۡنُ بِمُسۡتَيۡقِنِينَ\nВақтики: «Албатта, Аллоҳнинг ваъдаси ҳақдир ва соат (қиёмат)да шубҳа йўқдир», дейилса, «Соат(қиёмат) нима эканини билмасмиз. Биз фақат гумон қиламиз, холос. Биз аниқ ишонгувчи эмасмиз», дедингиз» (дейилур)'}, {'chapter': 45, 'verse': 33, 'text': 'وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nВа уларга қилган амалларининг ёмонликлари аён бўлди ва уларни ўзлари истеҳзо қилиб юрган нарса ўраб олди'}, {'chapter': 45, 'verse': 34, 'text': 'وَقِيلَ ٱلۡيَوۡمَ نَنسَىٰكُمۡ كَمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ\n«Бугунги кунда худди сиз ушбу кунингизга рўбарў бўлишни унутганингиздек, Биз ҳам сизни унутурмиз. Жойингиз жаҳаннамдир ва сизларга ҳеч ёрдамчилар йўқ'}, {'chapter': 45, 'verse': 35, 'text': 'ذَٰلِكُم بِأَنَّكُمُ ٱتَّخَذۡتُمۡ ءَايَٰتِ ٱللَّهِ هُزُوٗا وَغَرَّتۡكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ لَا يُخۡرَجُونَ مِنۡهَا وَلَا هُمۡ يُسۡتَعۡتَبُونَ\nБундай бўлишингиз, албатта, сизнинг Аллоҳнинг оятларини масхара қилиб олганингиз ва сизни дунё ҳаёти ғурурга кетказгани учундир», дейилди. Бас, бугунги кунда улар у(жаҳаннам)дан чиқарилмаслар ва улар итобга ҳам қайтарилмаслар'}, {'chapter': 45, 'verse': 36, 'text': 'فَلِلَّهِ ٱلۡحَمۡدُ رَبِّ ٱلسَّمَٰوَٰتِ وَرَبِّ ٱلۡأَرۡضِ رَبِّ ٱلۡعَٰلَمِينَ\nБас, барча ҳамд осмонларнинг Роббига, ернинг Роббига ва оламларнинг Роббигадир'}, {'chapter': 45, 'verse': 37, 'text': 'وَلَهُ ٱلۡكِبۡرِيَآءُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлару ердаги улуғлик Уникидир. У зот Азийзу Ҳакиймдир'}], '46': [{'chapter': 46, 'verse': 1, 'text': 'حمٓ\nҲаа. Мийм'}, {'chapter': 46, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nКитоб туширилиши Fолиб ва ҳикматли Аллоҳ тарафидандир'}, {'chapter': 46, 'verse': 3, 'text': 'مَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۚ وَٱلَّذِينَ كَفَرُواْ عَمَّآ أُنذِرُواْ مُعۡرِضُونَ\nОсмонлару ерни ҳамда улар ўртасидаги нарсаларни фақат ҳақ ва белгиланган муддат ила яратдик. Кофирлар бўлса, ўзлари огоҳлантирилган нарсадан юз ўгирувчилардир'}, {'chapter': 46, 'verse': 4, 'text': 'قُلۡ أَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِۖ ٱئۡتُونِي بِكِتَٰبٖ مِّن قَبۡلِ هَٰذَآ أَوۡ أَثَٰرَةٖ مِّنۡ عِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ\nСен: «Аллоҳдан ўзга сиғинаётган нарсаларингизнинг хабарини беринг-чи, менга кўрсатинг-чи, улар ердан нимани яратдилар? Ёки осмонларни (яратишда) иштироклари борми? Агар ростгўй бўлсаларингиз, менга бу(Қуръон)дан олдинги китобни ёки илмий бир белгини (далилни) келтиринг», деб айт'}, {'chapter': 46, 'verse': 5, 'text': 'وَمَنۡ أَضَلُّ مِمَّن يَدۡعُواْ مِن دُونِ ٱللَّهِ مَن لَّا يَسۡتَجِيبُ لَهُۥٓ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ وَهُمۡ عَن دُعَآئِهِمۡ غَٰفِلُونَ\nАллоҳни қўйиб, қиёмат кунигача дуосини мустажоб қилолмайдиганларга илтижо этадиганлардан ҳам адашганроқлар борми?! Ҳолбуки, у(бут)лар уларнинг дуосидан ғофилдирлар'}, {'chapter': 46, 'verse': 6, 'text': 'وَإِذَا حُشِرَ ٱلنَّاسُ كَانُواْ لَهُمۡ أَعۡدَآءٗ وَكَانُواْ بِعِبَادَتِهِمۡ كَٰفِرِينَ\nОдамлар маҳшарга тўпланганда, ўша(бут)лар уларга душман бўлурлар ва ибодатларига ҳам мункир бўлурлар'}, {'chapter': 46, 'verse': 7, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ هَٰذَا سِحۡرٞ مُّبِينٌ\nҚачонки уларга равшан оятларимиз тиловат қилинса, куфр келтирганлар Ҳақ (Қуръон) уларга келгандаёқ, бу очиқ-ойдин сеҳрдир, дерлар'}, {'chapter': 46, 'verse': 8, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَلَا تَمۡلِكُونَ لِي مِنَ ٱللَّهِ شَيۡـًٔاۖ هُوَ أَعۡلَمُ بِمَا تُفِيضُونَ فِيهِۚ كَفَىٰ بِهِۦ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۖ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nБалки, у(Қуръон)ни ўзи тўқиб олган, ҳам дерлар. Сен: «Агар уни тўқиб олган бўлсам, Аллоҳдан (келадиган) бирор нарсани мендан қайтаришга қодир эмассиз. У зотнинг Ўзи у(китоб) ҳақида сиз шўнғиётган нарсаларни яхши билувчидир. Мен билан сизнинг ўртангизда гувоҳликка Унинг Ўзи кифоядир. Ва У кечирувчи ҳам раҳимли Зотдир», деб айт'}, {'chapter': 46, 'verse': 9, 'text': 'قُلۡ مَا كُنتُ بِدۡعٗا مِّنَ ٱلرُّسُلِ وَمَآ أَدۡرِي مَا يُفۡعَلُ بِي وَلَا بِكُمۡۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّ وَمَآ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ\nСен: «Мен биринчи Пайғамбар эмасман, менга ва сизларга нима қилинишини билмасман. Фақат, ўзимга ваҳий қилинаётган нарсагагина эргашурман. Мен очиқ-ойдин огоҳлантирувчиман, холос», деб айт'}, {'chapter': 46, 'verse': 10, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ وَكَفَرۡتُم بِهِۦ وَشَهِدَ شَاهِدٞ مِّنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ عَلَىٰ مِثۡلِهِۦ فَـَٔامَنَ وَٱسۡتَكۡبَرۡتُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nСен: «Айтинг-чи, агар у (Қуръон) Аллоҳдан бўлса-ю, сиз унга куфр келтирсангиз ва ҳолбуки, Бани Исроилдан бир гувоҳ у(Қуръон)га ўхшаш нарсага гувоҳлик бериб, иймон келтириб турибди, сиз эса, кибру ҳаво қиляпсиз. Албатта, Аллоҳ золим қавмларни ҳидоят қилмас», деб айт'}, {'chapter': 46, 'verse': 11, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ لَوۡ كَانَ خَيۡرٗا مَّا سَبَقُونَآ إِلَيۡهِۚ وَإِذۡ لَمۡ يَهۡتَدُواْ بِهِۦ فَسَيَقُولُونَ هَٰذَآ إِفۡكٞ قَدِيمٞ\nКуфр келтирганлар иймон келтирганларга: «Агар у (Қуръон) яхшилик бўлганида, унга биздан аввал бормасдилар», дедилар. У(Қуръон)дан ҳидоят топишмагач, бу — қадимги уйдирмадир, дерлар'}, {'chapter': 46, 'verse': 12, 'text': 'وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةٗۚ وَهَٰذَا كِتَٰبٞ مُّصَدِّقٞ لِّسَانًا عَرَبِيّٗا لِّيُنذِرَ ٱلَّذِينَ ظَلَمُواْ وَبُشۡرَىٰ لِلۡمُحۡسِنِينَ\nУ(Қуръон)дан олдин Мусонинг китоби йўл бошловчи ва раҳмат эди. Бу (Қуръон) эса, араб тилидаги, тасдиқловчи, зулм қилувчиларни огоҳлантириш учун ва яхшиларга башорат китобидир'}, {'chapter': 46, 'verse': 13, 'text': 'إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта «Роббимиз Аллоҳдир» деган, сўнгра бунда мустақийм турганларга хавф йўқдир ва улар хафа ҳам бўлмаслар'}, {'chapter': 46, 'verse': 14, 'text': 'أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ\nАна ўшалар жаннат эгаларидир, қилган амаллари мукофотига унда абадий қолурлар'}, {'chapter': 46, 'verse': 15, 'text': 'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ إِحۡسَٰنًاۖ حَمَلَتۡهُ أُمُّهُۥ كُرۡهٗا وَوَضَعَتۡهُ كُرۡهٗاۖ وَحَمۡلُهُۥ وَفِصَٰلُهُۥ ثَلَٰثُونَ شَهۡرًاۚ حَتَّىٰٓ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرۡبَعِينَ سَنَةٗ قَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَصۡلِحۡ لِي فِي ذُرِّيَّتِيٓۖ إِنِّي تُبۡتُ إِلَيۡكَ وَإِنِّي مِنَ ٱلۡمُسۡلِمِينَ\nБиз инсонни ота-онасига яхшилик қилишга буюрдик. Онаси унга қийналиб ҳомиладор бўлди ва уни қийналиб туғди. Ва унинг ҳомиласи ва кўкракдан ажратиши ўттиз ойдир. Токи, у камолга етиб, қирқ ёшга кирганида: «Роббим, Ўзинг менга ва ота-онамга берган неъматларингга шукр қилишимга ва Сен рози бўлган солиҳ(иш)ни қилишимга илҳом бергин. Ва менинг зурриётларимни ҳам солиҳлардан қилгин. Албатта, мен тавба қилдим ва албатта, мен мусулмонларданман», деди'}, {'chapter': 46, 'verse': 16, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ نَتَقَبَّلُ عَنۡهُمۡ أَحۡسَنَ مَا عَمِلُواْ وَنَتَجَاوَزُ عَن سَيِّـَٔاتِهِمۡ فِيٓ أَصۡحَٰبِ ٱلۡجَنَّةِۖ وَعۡدَ ٱلصِّدۡقِ ٱلَّذِي كَانُواْ يُوعَدُونَ\nАна ўшаларнинг энг яхши амалларини қабул қилиб, ёмонликларини ўтиб юборурмиз. Улар жаннат эгаларидандир. Уларга берилган ҳақ ваъда шулдир'}, {'chapter': 46, 'verse': 17, 'text': 'وَٱلَّذِي قَالَ لِوَٰلِدَيۡهِ أُفّٖ لَّكُمَآ أَتَعِدَانِنِيٓ أَنۡ أُخۡرَجَ وَقَدۡ خَلَتِ ٱلۡقُرُونُ مِن قَبۡلِي وَهُمَا يَسۡتَغِيثَانِ ٱللَّهَ وَيۡلَكَ ءَامِنۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ فَيَقُولُ مَا هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nОта-онасига: «Уфф сизларга, (қабрдан) чиқарилишимнинг ваъдасини бераяпсизларми?! Мендан аввал ҳам асрлар ўтган-ку!» – деган кишига, Аллоҳдан мадад сўраган ҳолда, улар: «Ҳолингга вой бўлгур, иймон келтир, албатта, Аллоҳнинг ваъдаси ҳақдир», дедилар. У бўлса: «Бу ўтганларнинг афсоналари, холос», дер'}, {'chapter': 46, 'verse': 18, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ\nАна ўшаларга ўзларидан аввал ўтган жин ва инс умматлари қаторида (азоб) сўзи ҳақ бўлгандир. Албатта, улар зиён кўрувчилардан бўлдилар'}, {'chapter': 46, 'verse': 19, 'text': 'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۖ وَلِيُوَفِّيَهُمۡ أَعۡمَٰلَهُمۡ وَهُمۡ لَا يُظۡلَمُونَ\nҲар бирларига қилган амалларига яраша даражалар бордир. Ва уларга амалларининг (мукофот ёки жазоси) тўлиқ берилур ва уларга зулм қилинмас'}, {'chapter': 46, 'verse': 20, 'text': 'وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَذۡهَبۡتُمۡ طَيِّبَٰتِكُمۡ فِي حَيَاتِكُمُ ٱلدُّنۡيَا وَٱسۡتَمۡتَعۡتُم بِهَا فَٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَسۡتَكۡبِرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَفۡسُقُونَ\nКуфр келтирганлар дўзахга кўндаланг қилинган кунда (уларга): «Ҳаёти дунёдаёқ ҳузур-ҳаловатингизни кетказиб, мазасини татиб бўлгансиз. Бугунги кунда ер юзида ноҳақ кибру ҳаво ва фисқу фасод қилганингиз учун хорлик азоби ила жазоланурсиз», дейилур'}, {'chapter': 46, 'verse': 21, 'text': '۞وَٱذۡكُرۡ أَخَا عَادٍ إِذۡ أَنذَرَ قَوۡمَهُۥ بِٱلۡأَحۡقَافِ وَقَدۡ خَلَتِ ٱلنُّذُرُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦٓ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ\nЎз қавмини қум тепаликларда огоҳлантирган Однинг биродарини эсла! Ва ҳолбуки, ундан аввал ҳам, кейин ҳам огоҳлантирувчилар ўтган. «У зотдан ўзгага ибодат қилманглар, албатта, мен улуғ кунда бошингизга тушадиган азобдан қўрқурман, (деди)»'}, {'chapter': 46, 'verse': 22, 'text': 'قَالُوٓاْ أَجِئۡتَنَا لِتَأۡفِكَنَا عَنۡ ءَالِهَتِنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУлар: «Бизни худоларимиздан оздиришга келдингми? Агар ростгўйлардан бўлсанг, бизга ваъда қилаётган нарсангни келтир», дедилар'}, {'chapter': 46, 'verse': 23, 'text': 'قَالَ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّآ أُرۡسِلۡتُ بِهِۦ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ\nУ: «Албатта, илм Аллоҳнинг ҳузуридадир. Мен нима билан юборилган бўлсам, шуни етказмоқдаман. Лекин сизларнинг жоҳил қавмлигингизни кўриб турибман», деди'}, {'chapter': 46, 'verse': 24, 'text': 'فَلَمَّا رَأَوۡهُ عَارِضٗا مُّسۡتَقۡبِلَ أَوۡدِيَتِهِمۡ قَالُواْ هَٰذَا عَارِضٞ مُّمۡطِرُنَاۚ بَلۡ هُوَ مَا ٱسۡتَعۡجَلۡتُم بِهِۦۖ رِيحٞ فِيهَا عَذَابٌ أَلِيمٞ\nУ(азоб)нинг водийлари томон кўндаланг бўлиб юрганини кўришганда, бу кўндаланг бўлувчи бизга ёмғир ёғдирур, дейишди. Йўқ! Бу, ўзингиз орзиқиб сўраган нарса, аламли азоби бор бўрондир'}, {'chapter': 46, 'verse': 25, 'text': 'تُدَمِّرُ كُلَّ شَيۡءِۭ بِأَمۡرِ رَبِّهَا فَأَصۡبَحُواْ لَا يُرَىٰٓ إِلَّا مَسَٰكِنُهُمۡۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ\nУ, Роббисининг амри ила ҳар бир нарсани вайрон қилур. Бас, масканларидан бошқа нарса кўринмай қолди. Жиноятчи қавмларни шундоқ жазолармиз'}, {'chapter': 46, 'verse': 26, 'text': 'وَلَقَدۡ مَكَّنَّـٰهُمۡ فِيمَآ إِن مَّكَّنَّـٰكُمۡ فِيهِ وَجَعَلۡنَا لَهُمۡ سَمۡعٗا وَأَبۡصَٰرٗا وَأَفۡـِٔدَةٗ فَمَآ أَغۡنَىٰ عَنۡهُمۡ سَمۡعُهُمۡ وَلَآ أَبۡصَٰرُهُمۡ وَلَآ أَفۡـِٔدَتُهُم مِّن شَيۡءٍ إِذۡ كَانُواْ يَجۡحَدُونَ بِـَٔايَٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nВа, дарҳақиқат, Биз уларга сизга бермаган имконларни берган эдик. Ҳамда қулоқ, кўз ва қалблар ато этдик. Бас, на қулоқлари, на кўзлари ва на қалблари уларга ҳеч бир фойда бермади. Чунки, улар Аллоҳнинг оятларини инкор қилган эдилар. Бас, уларни ўзлари масхара қилган нарса (азоб) ўраб олди'}, {'chapter': 46, 'verse': 27, 'text': 'وَلَقَدۡ أَهۡلَكۡنَا مَا حَوۡلَكُم مِّنَ ٱلۡقُرَىٰ وَصَرَّفۡنَا ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَرۡجِعُونَ\nҲақиқатан, атрофингиздаги шаҳар-қишлоқларни ҳалокатга учратдик ва шоядки, ўзларига қайтсалар, деб ҳужжат-далилларни баён қилдик'}, {'chapter': 46, 'verse': 28, 'text': 'فَلَوۡلَا نَصَرَهُمُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ قُرۡبَانًا ءَالِهَةَۢۖ بَلۡ ضَلُّواْ عَنۡهُمۡۚ وَذَٰلِكَ إِفۡكُهُمۡ وَمَا كَانُواْ يَفۡتَرُونَ\nҚани энди, Аллоҳни қўйиб, У зотга яқинлаштирувчи деб тутинган «худолари» уларга ёрдам берсалар эди. Йўқ! Улар ғойиб бўлдилар. Ва бу уларнинг уйдирма ҳамда тўқиган бўҳтонларидир'}, {'chapter': 46, 'verse': 29, 'text': 'وَإِذۡ صَرَفۡنَآ إِلَيۡكَ نَفَرٗا مِّنَ ٱلۡجِنِّ يَسۡتَمِعُونَ ٱلۡقُرۡءَانَ فَلَمَّا حَضَرُوهُ قَالُوٓاْ أَنصِتُواْۖ فَلَمَّا قُضِيَ وَلَّوۡاْ إِلَىٰ قَوۡمِهِم مُّنذِرِينَ\nВа сен томон бир гуруҳ жинларни Қуръон эшитишга бурганимизни эсла. У(Қуръон эшитиш)га ҳозир бўлганларида: «Жим, қулоқ осинглар!» – дедилар. Қачонки, қироат охирлаганда, ўз қавмларига огоҳлантирувчи бўлиб қайтдилар'}, {'chapter': 46, 'verse': 30, 'text': 'قَالُواْ يَٰقَوۡمَنَآ إِنَّا سَمِعۡنَا كِتَٰبًا أُنزِلَ مِنۢ بَعۡدِ مُوسَىٰ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ يَهۡدِيٓ إِلَى ٱلۡحَقِّ وَإِلَىٰ طَرِيقٖ مُّسۡتَقِيمٖ\nУлар: «Эй қавмимиз! Биз Мусодан кейин туширилган, ўзидан аввалги нарса(китоблар)ни тасдиқловчи, ҳақ(дин)га ва тўғри йўлга ҳидоят қилувчи китобни эшитдик'}, {'chapter': 46, 'verse': 31, 'text': 'يَٰقَوۡمَنَآ أَجِيبُواْ دَاعِيَ ٱللَّهِ وَءَامِنُواْ بِهِۦ يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُجِرۡكُم مِّنۡ عَذَابٍ أَلِيمٖ\nЭй қавмимиз, Аллоҳнинг даъватчисига жавоб беринг ва Унга иймон келтиринг. У сизларнинг гуноҳларингизни мағфират қилар ва аламли азобдан сақлар'}, {'chapter': 46, 'verse': 32, 'text': 'وَمَن لَّا يُجِبۡ دَاعِيَ ٱللَّهِ فَلَيۡسَ بِمُعۡجِزٖ فِي ٱلۡأَرۡضِ وَلَيۡسَ لَهُۥ مِن دُونِهِۦٓ أَوۡلِيَآءُۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ\nКимки, Аллоҳнинг даъватчисини қабул қилмаса, бас, у ер юзида қочиб қутула олувчи эмасдир ва унга У зотдан ўзга валийлар ҳам йўқдир. Ана ўшалар очиқ-ойдин залолатдадир», дедилар'}, {'chapter': 46, 'verse': 33, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَلَمۡ يَعۡيَ بِخَلۡقِهِنَّ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰۚ بَلَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nУлар осмонлару ерни яратган ва уларни яратишда беҳол бўлмаган Аллоҳ ўликларни ҳам тирилтиришга қодир эканини англаб етмадиларми? Зеро, У зот ҳар бир нарсага қодирдир'}, {'chapter': 46, 'verse': 34, 'text': 'وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۖ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nКуфр келтирганлар дўзахга кўндаланг қилинган кунда: «Бу, ҳақ эмасми?!» (дейилур). Улар: «Роббимизга қасамки, худди шундоқ», дедилар. «Бас, куфр келтирганингиз учун азобни тортинглар», деди'}, {'chapter': 46, 'verse': 35, 'text': 'فَٱصۡبِرۡ كَمَا صَبَرَ أُوْلُواْ ٱلۡعَزۡمِ مِنَ ٱلرُّسُلِ وَلَا تَسۡتَعۡجِل لَّهُمۡۚ كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَ مَا يُوعَدُونَ لَمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّن نَّهَارِۭۚ بَلَٰغٞۚ فَهَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلۡفَٰسِقُونَ\nБас, азму матонат соҳиби бўлган Пайғамбарлар сабр қилгандек, сабр қил, уларга (азоб етишига) шошилма! Улар ваъда қилинган нарсани кўрганларида худди (бу дунёда) кундуздан бир лаҳзагина турганга ўхшарлар. Бу етказишдир! Бас, фақат фосиқ қавмларгина ҳалок қилинур, холос'}], '47': [{'chapter': 47, 'verse': 1, 'text': 'ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ أَضَلَّ أَعۡمَٰلَهُمۡ\nКуфр келтирган ва Аллоҳнинг йўлидан тўсганларнинг амалларини У зот ботил қилди'}, {'chapter': 47, 'verse': 2, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَءَامَنُواْ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٖ وَهُوَ ٱلۡحَقُّ مِن رَّبِّهِمۡ كَفَّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَأَصۡلَحَ بَالَهُمۡ\nИймон келтирган, солиҳ амалларни қилган ва Муҳаммадга нозил қилинган нарса(Қуръон)га — ҳолбуки у (Қуръон) Роббиларидан келган ҳақдир — иймон келтирганларнинг айбларини беркитар ва аҳволларини яхшилар'}, {'chapter': 47, 'verse': 3, 'text': 'ذَٰلِكَ بِأَنَّ ٱلَّذِينَ كَفَرُواْ ٱتَّبَعُواْ ٱلۡبَٰطِلَ وَأَنَّ ٱلَّذِينَ ءَامَنُواْ ٱتَّبَعُواْ ٱلۡحَقَّ مِن رَّبِّهِمۡۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ لِلنَّاسِ أَمۡثَٰلَهُمۡ\nУндоқ бўлишига, албатта, куфр келтирганларнинг ботилга эргашганлари ва иймон келтирганларнинг Роббиларидан бўлган ҳаққа эргашганликлари сабабдир. Аллоҳ кишиларга ўзларининг мисолларини шундай келтирар'}, {'chapter': 47, 'verse': 4, 'text': 'فَإِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ فَضَرۡبَ ٱلرِّقَابِ حَتَّىٰٓ إِذَآ أَثۡخَنتُمُوهُمۡ فَشُدُّواْ ٱلۡوَثَاقَ فَإِمَّا مَنَّۢا بَعۡدُ وَإِمَّا فِدَآءً حَتَّىٰ تَضَعَ ٱلۡحَرۡبُ أَوۡزَارَهَاۚ ذَٰلِكَۖ وَلَوۡ يَشَآءُ ٱللَّهُ لَٱنتَصَرَ مِنۡهُمۡ وَلَٰكِن لِّيَبۡلُوَاْ بَعۡضَكُم بِبَعۡضٖۗ وَٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ فَلَن يُضِلَّ أَعۡمَٰلَهُمۡ\nҚачонки куфр келтирганлар билан тўқнашсангиз, бас, бўйинларга урмоқ керак. Вақтики, уларни тор-мор қилсангиз, бас, маҳкам боғланг. Сўнгра ёки миннат, ёки товон олишдир. Ҳаттоки, уруш ўз анжомларини қўйгунча. Ана шундай. Аллоҳ хоҳласа, улардан Ўзи ғолиб келарди, лекин баъзиларингизни баъзиларингиз ила синаш учун (қилди). Аллоҳ йўлида қатл қилинганларнинг амалларини У зот ҳеч зое қилмас'}, {'chapter': 47, 'verse': 5, 'text': 'سَيَهۡدِيهِمۡ وَيُصۡلِحُ بَالَهُمۡ\nАлбатта, уларни ҳидоят қилар ва аҳволларини яхшилар'}, {'chapter': 47, 'verse': 6, 'text': 'وَيُدۡخِلُهُمُ ٱلۡجَنَّةَ عَرَّفَهَا لَهُمۡ\nҲамда Ўзи уларга таърифлаб берган жаннатга киритар'}, {'chapter': 47, 'verse': 7, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَنصُرُواْ ٱللَّهَ يَنصُرۡكُمۡ وَيُثَبِّتۡ أَقۡدَامَكُمۡ\nЭй, иймон келтирганлар, агар Аллоҳга ёрдам берсангиз, сизга ҳам ёрдам берур ва қадамларингизни собит қилур'}, {'chapter': 47, 'verse': 8, 'text': 'وَٱلَّذِينَ كَفَرُواْ فَتَعۡسٗا لَّهُمۡ وَأَضَلَّ أَعۡمَٰلَهُمۡ\nВа куфр келтирганларга бахтсизлик ва амалларини зое қилиш бўлур'}, {'chapter': 47, 'verse': 9, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ كَرِهُواْ مَآ أَنزَلَ ٱللَّهُ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ\nБу, уларнинг Аллоҳ нозил қилган нарсани хуш кўрмаганлари сабабидандир. Бас, У зот амалларини ботил қилди'}, {'chapter': 47, 'verse': 10, 'text': '۞أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ دَمَّرَ ٱللَّهُ عَلَيۡهِمۡۖ وَلِلۡكَٰفِرِينَ أَمۡثَٰلُهَا\nЕр юзида юриб, ўзларидан аввалгиларнинг оқибати қандоқ бўлганига назар солмайдиларми? Аллоҳ уларни вайрон қилди-ку! Кофирларга шунга ўхшаш бўлур'}, {'chapter': 47, 'verse': 11, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ مَوۡلَى ٱلَّذِينَ ءَامَنُواْ وَأَنَّ ٱلۡكَٰفِرِينَ لَا مَوۡلَىٰ لَهُمۡ\nШундоқ бўлиши, албатта, Аллоҳ иймон келтирганларга дўст эканлигидан ва албатта, кофирларга дўст йўқлигидандир'}, {'chapter': 47, 'verse': 12, 'text': 'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَٱلَّذِينَ كَفَرُواْ يَتَمَتَّعُونَ وَيَأۡكُلُونَ كَمَا تَأۡكُلُ ٱلۡأَنۡعَٰمُ وَٱلنَّارُ مَثۡوٗى لَّهُمۡ\nАлбатта, Аллоҳ иймон келтирганлар ва яхши амал қилганларни тагидан анҳорлар оқиб турган жаннатларга киритур. Куфр келтирганлар ҳузур қилурлар ва худди чорва ҳайвонлари егандек ерлар ва дўзах уларнинг жойидир'}, {'chapter': 47, 'verse': 13, 'text': 'وَكَأَيِّن مِّن قَرۡيَةٍ هِيَ أَشَدُّ قُوَّةٗ مِّن قَرۡيَتِكَ ٱلَّتِيٓ أَخۡرَجَتۡكَ أَهۡلَكۡنَٰهُمۡ فَلَا نَاصِرَ لَهُمۡ\nКўпгина шаҳарлар сени чиқарган шаҳарингдан кўра қувватлироқ эди, уларни ҳалок қилдик. Бас, уларга нусрат берувчи йўқдир'}, {'chapter': 47, 'verse': 14, 'text': 'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ كَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُم\nЎз Роббисидан аниқ ҳужжатга эга бўлган шахс ёмон амали ўзига зийнатли кўринган ва ҳавойи нафсига эргашганларга ўхшаши мумкинми'}, {'chapter': 47, 'verse': 15, 'text': 'مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ فِيهَآ أَنۡهَٰرٞ مِّن مَّآءٍ غَيۡرِ ءَاسِنٖ وَأَنۡهَٰرٞ مِّن لَّبَنٖ لَّمۡ يَتَغَيَّرۡ طَعۡمُهُۥ وَأَنۡهَٰرٞ مِّنۡ خَمۡرٖ لَّذَّةٖ لِّلشَّـٰرِبِينَ وَأَنۡهَٰرٞ مِّنۡ عَسَلٖ مُّصَفّٗىۖ وَلَهُمۡ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَمَغۡفِرَةٞ مِّن رَّبِّهِمۡۖ كَمَنۡ هُوَ خَٰلِدٞ فِي ٱلنَّارِ وَسُقُواْ مَآءً حَمِيمٗا فَقَطَّعَ أَمۡعَآءَهُمۡ\nТақводорларга ваъда қилинган жаннатнинг мисоли (шуки): ичида айнимаган сувли анҳорлари, таъми ўзгармаган сутли анҳорлари, ичувчиларга лаззатбахш хамр(шароб)ли анҳорлари, мусаффо асалли анҳорлари бор. Улар учун унда барча мевалар ва ўз Роббиларидан мағфират бор. (Ўшалар) оташда мангу қолувчи ва ўта қайноқ сув ила суғорилиб, ичаклари титилиб кетган кимсага ўхшарми'}, {'chapter': 47, 'verse': 16, 'text': 'وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَ حَتَّىٰٓ إِذَا خَرَجُواْ مِنۡ عِندِكَ قَالُواْ لِلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مَاذَا قَالَ ءَانِفًاۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡ\nВа улардан сенга қулоқ осадиганлари бор. Ҳузурингдан чиққанларида эса, илм берилганларга: «Ҳали нима деди?» – дерлар. Ана ўшалар қалбларига Аллоҳ тамға босганлар ва ҳавойи нафсларига эргашганлардир'}, {'chapter': 47, 'verse': 17, 'text': 'وَٱلَّذِينَ ٱهۡتَدَوۡاْ زَادَهُمۡ هُدٗى وَءَاتَىٰهُمۡ تَقۡوَىٰهُمۡ\nҲидоят топганларнинг ҳидоятини У зот зиёда қилди ва уларга тақволарини берди'}, {'chapter': 47, 'verse': 18, 'text': 'فَهَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗۖ فَقَدۡ جَآءَ أَشۡرَاطُهَاۚ فَأَنَّىٰ لَهُمۡ إِذَا جَآءَتۡهُمۡ ذِكۡرَىٰهُمۡ\nУлар соат(қиёмат)нинг бирдан келишидан бошқа нарсага интизор бўлишаётгани йўқ. Дарвоқе, аломатлари келиб ҳам қолди. У келиб қолса, улар қаердан эслатма олурлар'}, {'chapter': 47, 'verse': 19, 'text': 'فَٱعۡلَمۡ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱللَّهُ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَٱللَّهُ يَعۡلَمُ مُتَقَلَّبَكُمۡ وَمَثۡوَىٰكُمۡ\nБас, билгин: Аллоҳдан ўзга ибодатга сазовор зот йўқ, ўзингнинг, мўминларнинг ва мўминаларнинг гуноҳлари мағфират қилинишини сўра. Аллоҳ сизларнинг кезар жойларингизни ҳам, борар жойларингизни ҳам билур'}, {'chapter': 47, 'verse': 20, 'text': 'وَيَقُولُ ٱلَّذِينَ ءَامَنُواْ لَوۡلَا نُزِّلَتۡ سُورَةٞۖ فَإِذَآ أُنزِلَتۡ سُورَةٞ مُّحۡكَمَةٞ وَذُكِرَ فِيهَا ٱلۡقِتَالُ رَأَيۡتَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يَنظُرُونَ إِلَيۡكَ نَظَرَ ٱلۡمَغۡشِيِّ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَأَوۡلَىٰ لَهُمۡ\nВа иймон келтирганлар: «Бирон сура нозил қилинса эди», дерлар. Вақтики, муҳкам сура нозил қилиниб, унда уруш зикр қилинса, қалбларида касали борларнинг сенга худди жон бераётганнинг қарашидек қараётганини кўрасан'}, {'chapter': 47, 'verse': 21, 'text': 'طَاعَةٞ وَقَوۡلٞ مَّعۡرُوفٞۚ فَإِذَا عَزَمَ ٱلۡأَمۡرُ فَلَوۡ صَدَقُواْ ٱللَّهَ لَكَانَ خَيۡرٗا لَّهُمۡ\nУларга тоат ва яхши сўз яхшироқдир, бас, иш жиддийлашганда, Аллоҳга содиқ қолсалар, ўзларига яхши бўлур эди'}, {'chapter': 47, 'verse': 22, 'text': 'فَهَلۡ عَسَيۡتُمۡ إِن تَوَلَّيۡتُمۡ أَن تُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَتُقَطِّعُوٓاْ أَرۡحَامَكُمۡ\nЭҳтимол, (иймондан) юз ўгирсангиз, ер юзида фасод қилиб, қардошлик ришталарини узарсиз'}, {'chapter': 47, 'verse': 23, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمۡ وَأَعۡمَىٰٓ أَبۡصَٰرَهُمۡ\nАна ўшаларни Аллоҳ лаънатлади, уларни кар қилди ва кўзларини кўр қилди'}, {'chapter': 47, 'verse': 24, 'text': 'أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَ أَمۡ عَلَىٰ قُلُوبٍ أَقۡفَالُهَآ\nҚуръонни тадаббур ила ўйлаб кўрмайдиларми? Ёки қалбларда қулфлар борми'}, {'chapter': 47, 'verse': 25, 'text': 'إِنَّ ٱلَّذِينَ ٱرۡتَدُّواْ عَلَىٰٓ أَدۡبَٰرِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَى ٱلشَّيۡطَٰنُ سَوَّلَ لَهُمۡ وَأَمۡلَىٰ لَهُمۡ\nАлбатта, ҳидоят равшан бўлгандан сўнг ортларига қайтганларга шайтон зийнатлаб кўрсатади ва уларни ўша йўллади'}, {'chapter': 47, 'verse': 26, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لِلَّذِينَ كَرِهُواْ مَا نَزَّلَ ٱللَّهُ سَنُطِيعُكُمۡ فِي بَعۡضِ ٱلۡأَمۡرِۖ وَٱللَّهُ يَعۡلَمُ إِسۡرَارَهُمۡ\nУндоқ бўлишлиги, албатта, уларнинг Аллоҳ нозил қилган нарсани хуш кўрмаганларга: «Сизларга баъзи ишларда итоат қилурмиз», дейишлари сабабидандир. Ва ҳолбуки, Аллоҳ сир-асрорларини билиб турибди'}, {'chapter': 47, 'verse': 27, 'text': 'فَكَيۡفَ إِذَا تَوَفَّتۡهُمُ ٱلۡمَلَـٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ\nФаришталар юзларига ва ортларига уриб жонларини олаётганларида қандоқ бўларкин'}, {'chapter': 47, 'verse': 28, 'text': 'ذَٰلِكَ بِأَنَّهُمُ ٱتَّبَعُواْ مَآ أَسۡخَطَ ٱللَّهَ وَكَرِهُواْ رِضۡوَٰنَهُۥ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ\nБундай бўлиши, албатта, уларнинг Аллоҳни ғазаблантирган нарсага эргашганлари ва Унинг розилигини хуш кўрмаганликлари сабабидандир. Бас, У зот амалларини ҳабата қилди'}, {'chapter': 47, 'verse': 29, 'text': 'أَمۡ حَسِبَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ أَن لَّن يُخۡرِجَ ٱللَّهُ أَضۡغَٰنَهُمۡ\nҚалбларида касал борлар, Аллоҳ уларнинг қаттиқ ҳасадларини ошкор қилмас, деб ҳисоблайдиларми'}, {'chapter': 47, 'verse': 30, 'text': 'وَلَوۡ نَشَآءُ لَأَرَيۡنَٰكَهُمۡ فَلَعَرَفۡتَهُم بِسِيمَٰهُمۡۚ وَلَتَعۡرِفَنَّهُمۡ فِي لَحۡنِ ٱلۡقَوۡلِۚ وَٱللَّهُ يَعۡلَمُ أَعۡمَٰلَكُمۡ\nАгар хоҳласак, Биз уларни сенга кўрсатамиз, таъкидки, сен уларни сиймоларидан танийсан, албатта, сен уларни гап оҳангларидан танийсан. Аллоҳ амалларингизни билиб турур'}, {'chapter': 47, 'verse': 31, 'text': 'وَلَنَبۡلُوَنَّكُمۡ حَتَّىٰ نَعۡلَمَ ٱلۡمُجَٰهِدِينَ مِنكُمۡ وَٱلصَّـٰبِرِينَ وَنَبۡلُوَاْ أَخۡبَارَكُمۡ\nВа таъкидки, Биз сизларни синармиз, токи, сизлардан мужоҳидларни, сабрлиларни билсак ва хабарларингизни имтиҳон этсак'}, {'chapter': 47, 'verse': 32, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ وَشَآقُّواْ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَىٰ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗا وَسَيُحۡبِطُ أَعۡمَٰلَهُمۡ\nАлбатта, куфр келтирганлар ва Аллоҳнинг йўлидан тўсганлар ҳамда ҳидоят аниқ бўлгандан сўнг Пайғамбарга қарши чиққанлар Аллоҳга ҳеч зарар келтира олмаслар. Ва уларнинг амалларини ҳабата қилур'}, {'chapter': 47, 'verse': 33, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَلَا تُبۡطِلُوٓاْ أَعۡمَٰلَكُمۡ\nЭй, иймон келтирганлар, Аллоҳга итоат этинг, Расулга бўйинсунинг ва амалларингизни ботил қилмангиз'}, {'chapter': 47, 'verse': 34, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ ثُمَّ مَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡ\nАлбатта, куфр келтирганлар ва Аллоҳнинг йўлидан тўсган, сўнгра кофир ҳолларида ўлганларни Аллоҳ ҳеч-ҳеч мағфират қилмас'}, {'chapter': 47, 'verse': 35, 'text': 'فَلَا تَهِنُواْ وَتَدۡعُوٓاْ إِلَى ٱلسَّلۡمِ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ وَٱللَّهُ مَعَكُمۡ وَلَن يَتِرَكُمۡ أَعۡمَٰلَكُمۡ\nБас, бўш келманглар ва ўзингиз устун бўлиб туриб, ярашга чақирманглар. Аллоҳ сиз билан ва амалларингизни ҳеч-ҳеч камайтирмас'}, {'chapter': 47, 'verse': 36, 'text': 'إِنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ يُؤۡتِكُمۡ أُجُورَكُمۡ وَلَا يَسۡـَٔلۡكُمۡ أَمۡوَٰلَكُمۡ\nАлбатта, бу дунё ҳаёти ўйин ва кўнгил хушидир ва гар иймон келтирсангиз ва тақво қилсангиз, У зот ажрларингизни берур ва молу дунёларингизни сўрамас'}, {'chapter': 47, 'verse': 37, 'text': 'إِن يَسۡـَٔلۡكُمُوهَا فَيُحۡفِكُمۡ تَبۡخَلُواْ وَيُخۡرِجۡ أَضۡغَٰنَكُمۡ\nАгар сиздан уларни сўраса, қийнаса, бахиллик қиласизлар ва Аллоҳ кек-ҳиқдларингизни чиқарур'}, {'chapter': 47, 'verse': 38, 'text': 'هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ تُدۡعَوۡنَ لِتُنفِقُواْ فِي سَبِيلِ ٱللَّهِ فَمِنكُم مَّن يَبۡخَلُۖ وَمَن يَبۡخَلۡ فَإِنَّمَا يَبۡخَلُ عَن نَّفۡسِهِۦۚ وَٱللَّهُ ٱلۡغَنِيُّ وَأَنتُمُ ٱلۡفُقَرَآءُۚ وَإِن تَتَوَلَّوۡاْ يَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ ثُمَّ لَا يَكُونُوٓاْ أَمۡثَٰلَكُم\nҲой, сизлар. Анавилар. Аллоҳнинг йўлида нафақа қилишга чақирилсангиз, бас, баъзиларингиз бахиллик қилурсизлар. Ва ким бахиллик қилса, албатта, ўзига бахиллик қилур. Ва, ҳолбуки, Аллоҳ бой, сизлар камбағалсизлар. Ва агар сиз юз ўгириб кетсангиз, сиздан бошқа қавмни ўрнингизга келтирур, улар эса сизларга ўхшаган бўлмаслар'}], '48': [{'chapter': 48, 'verse': 1, 'text': 'إِنَّا فَتَحۡنَا لَكَ فَتۡحٗا مُّبِينٗا\nАлбатта Биз сенга равшан фатҳни бердик'}, {'chapter': 48, 'verse': 2, 'text': 'لِّيَغۡفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكَ وَيَهۡدِيَكَ صِرَٰطٗا مُّسۡتَقِيمٗا\nАллоҳ сенинг аввал ўтган гуноҳларингни ва кейингиларини мағфират этиш, неъматини батамом қилиш ва сени тўғри йўлга бошлаш учун'}, {'chapter': 48, 'verse': 3, 'text': 'وَيَنصُرَكَ ٱللَّهُ نَصۡرًا عَزِيزًا\nВа Аллоҳ сенга иззатли нусрат бериши учун'}, {'chapter': 48, 'verse': 4, 'text': 'هُوَ ٱلَّذِيٓ أَنزَلَ ٱلسَّكِينَةَ فِي قُلُوبِ ٱلۡمُؤۡمِنِينَ لِيَزۡدَادُوٓاْ إِيمَٰنٗا مَّعَ إِيمَٰنِهِمۡۗ وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nУ, иймонларига иймон зиёда бўлиши учун мўминлар қалбига сокинликни нозил этган Зотдир. Ва осмонлару ернинг лашкари Аллоҳникидир. Ва Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 48, 'verse': 5, 'text': 'لِّيُدۡخِلَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَيُكَفِّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡۚ وَكَانَ ذَٰلِكَ عِندَ ٱللَّهِ فَوۡزًا عَظِيمٗا\nМўминлар ва мўминаларни тагларидан анҳорлар оқиб турган жаннатларга мангу киритиш учун ва уларнинг гуноҳларини ювиш учун. Ва бу, Аллоҳнинг ҳузурида катта ютуқдир'}, {'chapter': 48, 'verse': 6, 'text': 'وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ ٱلظَّآنِّينَ بِٱللَّهِ ظَنَّ ٱلسَّوۡءِۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۖ وَغَضِبَ ٱللَّهُ عَلَيۡهِمۡ وَلَعَنَهُمۡ وَأَعَدَّ لَهُمۡ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرٗا\nАллоҳ тўғрисида ёмон гумон қилувчи мунофиқ ва мунофиқалар, мушрик ва мушрикаларни азоблаш учун... Уларни ёмонлик ўраб олсин. Уларга Аллоҳ ғазаб қилди, уларни лаънатлади ва уларга жаҳаннамни тайёрлаб қўйди. Ва нақадар ёмон жой у'}, {'chapter': 48, 'verse': 7, 'text': 'وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا\nОсмонлару ернинг лашкарлари Аллоҳникидир. Ва, Аллоҳ иззатли ва ҳикматли Зотдир'}, {'chapter': 48, 'verse': 8, 'text': 'إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا\nАлбатта, Биз сени гувоҳлик берувчи, хушхабар элтувчи ва огоҳлантирувчи қилиб юбордик'}, {'chapter': 48, 'verse': 9, 'text': 'لِّتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُۚ وَتُسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا\nАллоҳга ва Унинг Расулига иймон келтиришингиз, Уни (динни) қўллаб-қувватлашингиз, улуғлашингиз ҳамда Унга эртаю кеч тасбиҳ айтишингиз учундир'}, {'chapter': 48, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ ٱللَّهَ يَدُ ٱللَّهِ فَوۡقَ أَيۡدِيهِمۡۚ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفۡسِهِۦۖ وَمَنۡ أَوۡفَىٰ بِمَا عَٰهَدَ عَلَيۡهُ ٱللَّهَ فَسَيُؤۡتِيهِ أَجۡرًا عَظِيمٗا\nАлбатта, сенга байъат қилаётганлар фақат Аллоҳнинг Ўзига байъат қилмоқдалар. Аллоҳнинг қўли уларнинг қўллари устидадир. Ким (байъатни) бузса, ўзига қарши бузади, холос ва ким Аллоҳга қилган аҳдига вафо қилса, унга тезда улкан ажр берилур'}, {'chapter': 48, 'verse': 11, 'text': 'سَيَقُولُ لَكَ ٱلۡمُخَلَّفُونَ مِنَ ٱلۡأَعۡرَابِ شَغَلَتۡنَآ أَمۡوَٰلُنَا وَأَهۡلُونَا فَٱسۡتَغۡفِرۡ لَنَاۚ يَقُولُونَ بِأَلۡسِنَتِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ قُلۡ فَمَن يَمۡلِكُ لَكُم مِّنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ بِكُمۡ ضَرًّا أَوۡ أَرَادَ بِكُمۡ نَفۡعَۢاۚ بَلۡ كَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرَۢا\nОртда қолган аъробийлар сенга: «Молимиз ва аҳлимиз бизни машғул қилди, бизга истиғфор айт», дейишар. Улар қалбларида йўқ нарсани тиллари ила айтурлар. Сен: «Агар Аллоҳ сизга зарарни ирода қилса ёки сизга манфаатни ирода қилса, ким сизларни Аллоҳдан (келадиган) бирор нарсадан (тўсишга) молик бўладир? Таъкидки, Аллоҳ қилган амалларингиздан хабардордир», деб айт'}, {'chapter': 48, 'verse': 12, 'text': 'بَلۡ ظَنَنتُمۡ أَن لَّن يَنقَلِبَ ٱلرَّسُولُ وَٱلۡمُؤۡمِنُونَ إِلَىٰٓ أَهۡلِيهِمۡ أَبَدٗا وَزُيِّنَ ذَٰلِكَ فِي قُلُوبِكُمۡ وَظَنَنتُمۡ ظَنَّ ٱلسَّوۡءِ وَكُنتُمۡ قَوۡمَۢا بُورٗا\nБалки сизлар, Расул ва мўминлар абадий ўз аҳлларига қайтиб келмаслар, деб гумон қилдингиз ва бу нарса қалбингизда зийнатланди, ёмон гумон қилдингиз ва ёмон қавм бўлдингиз'}, {'chapter': 48, 'verse': 13, 'text': 'وَمَن لَّمۡ يُؤۡمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَعِيرٗا\nВа ким Аллоҳга ва Унинг Расулига иймон келтирмаса, албатта, Биз кофирларга дўзахни тайёрлаганмиз'}, {'chapter': 48, 'verse': 14, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nОсмонлару ернинг мулки Аллоҳникидир. Кимни хоҳласа, мағфират қилар ва кимни хоҳласа, азоблар. Ва Аллоҳ кечирувчи, раҳимли Зотдир'}, {'chapter': 48, 'verse': 15, 'text': 'سَيَقُولُ ٱلۡمُخَلَّفُونَ إِذَا ٱنطَلَقۡتُمۡ إِلَىٰ مَغَانِمَ لِتَأۡخُذُوهَا ذَرُونَا نَتَّبِعۡكُمۡۖ يُرِيدُونَ أَن يُبَدِّلُواْ كَلَٰمَ ٱللَّهِۚ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمۡ قَالَ ٱللَّهُ مِن قَبۡلُۖ فَسَيَقُولُونَ بَلۡ تَحۡسُدُونَنَاۚ بَلۡ كَانُواْ لَا يَفۡقَهُونَ إِلَّا قَلِيلٗا\nЎлжалар томон, уларни олиш учун юрганингизда ортда қолганлар: «Қўйиб беринг, биз ҳам сизга эргашурмиз», дейишар. Аллоҳнинг каломини ўзгартирмоқчи бўлишар. Айт: «Ҳеч-ҳеч бизга эргашмайсиз, ушбуни Аллоҳ аввал айтган». Улар: «Балки, ҳасад қилаётгандирсизлар», дейишар. Йўқ, улар озгина нарсадан бошқа нарсани англамаслар'}, {'chapter': 48, 'verse': 16, 'text': 'قُل لِّلۡمُخَلَّفِينَ مِنَ ٱلۡأَعۡرَابِ سَتُدۡعَوۡنَ إِلَىٰ قَوۡمٍ أُوْلِي بَأۡسٖ شَدِيدٖ تُقَٰتِلُونَهُمۡ أَوۡ يُسۡلِمُونَۖ فَإِن تُطِيعُواْ يُؤۡتِكُمُ ٱللَّهُ أَجۡرًا حَسَنٗاۖ وَإِن تَتَوَلَّوۡاْ كَمَا تَوَلَّيۡتُم مِّن قَبۡلُ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا\nОртда қолган аъробийларга: «Катта кучга эга қавмга (қарши) чақирилурсиз, улар мусулмон бўлгунларича урушасиз. Агар итоат қилсангиз, Аллоҳ сизга яхши ажр берур. Ва агар яна аввал қочганингиздек, ортга қочсангиз, аламли азоб ила азоблар», деб айт'}, {'chapter': 48, 'verse': 17, 'text': 'لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَمَن يَتَوَلَّ يُعَذِّبۡهُ عَذَابًا أَلِيمٗا\nКўрга қийинчилик йўқ, чўлоққа ҳам қийинчилик йўқ. Хаста кишига ҳам қийинчилик йўқ. Ким Аллоҳга ва Унинг Расулига итоат қилса, уни остидан сувлар оқиб турган жаннатларга киритилади. Ким юз ўгирса, аламли азоб ила азоблар'}, {'chapter': 48, 'verse': 18, 'text': '۞لَّقَدۡ رَضِيَ ٱللَّهُ عَنِ ٱلۡمُؤۡمِنِينَ إِذۡ يُبَايِعُونَكَ تَحۡتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِمۡ فَأَنزَلَ ٱلسَّكِينَةَ عَلَيۡهِمۡ وَأَثَٰبَهُمۡ فَتۡحٗا قَرِيبٗا\nБатаҳқиқ, Аллоҳ мўминлардан дарахт остида сенга байъат қилаётганларида рози бўлди. Бас, уларнинг қалбларидагини билди, уларга сокинликни туширди ва яқин фатҳ ила мукофотлади'}, {'chapter': 48, 'verse': 19, 'text': 'وَمَغَانِمَ كَثِيرَةٗ يَأۡخُذُونَهَاۗ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا\nВа кўпгина ўлжаларни ҳам олурлар. Ва Аллоҳ Азийз ва Ҳакийм Зотдир'}, {'chapter': 48, 'verse': 20, 'text': 'وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِيرَةٗ تَأۡخُذُونَهَا فَعَجَّلَ لَكُمۡ هَٰذِهِۦ وَكَفَّ أَيۡدِيَ ٱلنَّاسِ عَنكُمۡ وَلِتَكُونَ ءَايَةٗ لِّلۡمُؤۡمِنِينَ وَيَهۡدِيَكُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا\nАллоҳ сизларга кўпгина ўлжаларни ваъда қилди. Уларни олурсизлар, бунисини сизга тезлатиб берди, (ўтган воқеа) мўминларга ибрат бўлиши учун ва сизларни сиротул мустақиймга бошлаш учун одамларнинг қўлини сиздан тўсди'}, {'chapter': 48, 'verse': 21, 'text': 'وَأُخۡرَىٰ لَمۡ تَقۡدِرُواْ عَلَيۡهَا قَدۡ أَحَاطَ ٱللَّهُ بِهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا\nВа сизлар қодир бўлмаган бошқасини ҳам (ваъда қилди). Уни Аллоҳ иҳота қилган ва Аллоҳ ҳар бир нарсага қодир Зотдир'}, {'chapter': 48, 'verse': 22, 'text': 'وَلَوۡ قَٰتَلَكُمُ ٱلَّذِينَ كَفَرُواْ لَوَلَّوُاْ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا\nАгар кофирлар сизлар билан урушганларида, орқага қараб қочар эдилар, сўнгра ўзларига валий ҳам, нусрат бергувчи ҳам топмасдилар'}, {'chapter': 48, 'verse': 23, 'text': 'سُنَّةَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا\nАллоҳнинг аввалдан келаётган суннатидир. Ва Аллоҳнинг суннатини ҳеч ўзгартира олмассан'}, {'chapter': 48, 'verse': 24, 'text': 'وَهُوَ ٱلَّذِي كَفَّ أَيۡدِيَهُمۡ عَنكُمۡ وَأَيۡدِيَكُمۡ عَنۡهُم بِبَطۡنِ مَكَّةَ مِنۢ بَعۡدِ أَنۡ أَظۡفَرَكُمۡ عَلَيۡهِمۡۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا\nУ зот Макка водийсида сизларни уларнинг устидан зафар қозонтирганидан сўнг уларнинг қўлларини сиздан, сизнинг қўлингизни улардан тўсган Зотдир. Ва Аллоҳ қилаётган амалларингизни кўриб турувчидир'}, {'chapter': 48, 'verse': 25, 'text': 'هُمُ ٱلَّذِينَ كَفَرُواْ وَصَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَٱلۡهَدۡيَ مَعۡكُوفًا أَن يَبۡلُغَ مَحِلَّهُۥۚ وَلَوۡلَا رِجَالٞ مُّؤۡمِنُونَ وَنِسَآءٞ مُّؤۡمِنَٰتٞ لَّمۡ تَعۡلَمُوهُمۡ أَن تَطَـُٔوهُمۡ فَتُصِيبَكُم مِّنۡهُم مَّعَرَّةُۢ بِغَيۡرِ عِلۡمٖۖ لِّيُدۡخِلَ ٱللَّهُ فِي رَحۡمَتِهِۦ مَن يَشَآءُۚ لَوۡ تَزَيَّلُواْ لَعَذَّبۡنَا ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابًا أَلِيمًا\nУлар куфр келтирган, сизларни Масжидул Ҳаромдан, қурбонликларни эса, ушланиб ўз жойига етишидан тўсган кишилардир, агар сиз билмайдиган мўмин эркаклар ва мўмина аёллар бўлмаганида, сизга билмасдан уларни ҳалок қилганингиз туфайли гуноҳ етиши бўлмаганида эди, Аллоҳ кимни хоҳласа, Ўз раҳматига киритиши учун, агар ажраб турганларида эди, улардан кофирларини аламли азоб ила азоблар эдик'}, {'chapter': 48, 'verse': 26, 'text': 'إِذۡ جَعَلَ ٱلَّذِينَ كَفَرُواْ فِي قُلُوبِهِمُ ٱلۡحَمِيَّةَ حَمِيَّةَ ٱلۡجَٰهِلِيَّةِ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَلۡزَمَهُمۡ كَلِمَةَ ٱلتَّقۡوَىٰ وَكَانُوٓاْ أَحَقَّ بِهَا وَأَهۡلَهَاۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا\nЭсла! Куфр келтирганлар қалбларида «ҳамийят»ни, «жоҳилият ҳамийяти»ни жойлаганларида, Аллоҳ Ўз сокинлигини Пайғамбарига ва мўминларга туширди ва уларга тақво калимасини лозим кўрди, улар ушбуга ҳақли ва аҳл эдилар. Ва Аллоҳ ҳар бир нарсани билувчидир'}, {'chapter': 48, 'verse': 27, 'text': 'لَّقَدۡ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءۡيَا بِٱلۡحَقِّۖ لَتَدۡخُلُنَّ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ مُحَلِّقِينَ رُءُوسَكُمۡ وَمُقَصِّرِينَ لَا تَخَافُونَۖ فَعَلِمَ مَا لَمۡ تَعۡلَمُواْ فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتۡحٗا قَرِيبًا\nБатаҳқиқ, Аллоҳ Расулининг тушини ҳақ ила тасдиқлади, албатта, Масжидул Ҳаромга, иншоаллоҳ, омонликда, сочларингиз олинган, қисқартирилган ҳолда, қўрқмасдан кирасизлар. Бас, У сиз билмаган нарсани билади ва сизларга бундан бошқа яқин фатҳни қилди'}, {'chapter': 48, 'verse': 28, 'text': 'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا\nУ Ўз Расулини ҳидоят ва ҳақ дин билан, у(дин)ни барча динлардан устун қилиш учун юборган Зотдир. Гувоҳликка Аллоҳнинг Ўзи кифоядир'}, {'chapter': 48, 'verse': 29, 'text': 'مُّحَمَّدٞ رَّسُولُ ٱللَّهِۚ وَٱلَّذِينَ مَعَهُۥٓ أَشِدَّآءُ عَلَى ٱلۡكُفَّارِ رُحَمَآءُ بَيۡنَهُمۡۖ تَرَىٰهُمۡ رُكَّعٗا سُجَّدٗا يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗاۖ سِيمَاهُمۡ فِي وُجُوهِهِم مِّنۡ أَثَرِ ٱلسُّجُودِۚ ذَٰلِكَ مَثَلُهُمۡ فِي ٱلتَّوۡرَىٰةِۚ وَمَثَلُهُمۡ فِي ٱلۡإِنجِيلِ كَزَرۡعٍ أَخۡرَجَ شَطۡـَٔهُۥ فَـَٔازَرَهُۥ فَٱسۡتَغۡلَظَ فَٱسۡتَوَىٰ عَلَىٰ سُوقِهِۦ يُعۡجِبُ ٱلزُّرَّاعَ لِيَغِيظَ بِهِمُ ٱلۡكُفَّارَۗ وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِنۡهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمَۢا\nМуҳаммад Аллоҳнинг Расулидир, у билан бирга бўлганлар кофирларга шиддатли, ўзаро раҳимдилдирлар. Аллоҳдан фазл ва розилик тилаб рукуъ ва сажда қилган ҳолларини кўрасан. Уларнинг сиймоси сажда асаридан юзларида (балқийди). Ана ўша, уларнинг Таврот ва Инжилдаги мисолларидир. Бу, худди, уруғини ёриб чиқиб, қувватланиб, йўғонлашиб, поясида тик туриб, деҳқонларни ажаблантирган бир экинга ўхшар. Кофирларнинг ғазабини қўзитиш учун. Аллоҳ улардан иймон келтириб ва солиҳ амалларни қилганларига мағфират ва улуғ ажрни ваъда қилди'}], '49': [{'chapter': 49, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُقَدِّمُواْ بَيۡنَ يَدَيِ ٱللَّهِ وَرَسُولِهِۦۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nЭй, иймон келтирганлар! Аллоҳдан ва Унинг Расулидан олдин шошилманглар. Ва Аллоҳга тақво қилинглар. Албатта Аллоҳ эшитувчи ва билувчи Зотдир'}, {'chapter': 49, 'verse': 2, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَرۡفَعُوٓاْ أَصۡوَٰتَكُمۡ فَوۡقَ صَوۡتِ ٱلنَّبِيِّ وَلَا تَجۡهَرُواْ لَهُۥ بِٱلۡقَوۡلِ كَجَهۡرِ بَعۡضِكُمۡ لِبَعۡضٍ أَن تَحۡبَطَ أَعۡمَٰلُكُمۡ وَأَنتُمۡ لَا تَشۡعُرُونَ\nЭй, иймон келтирганлар! Овозингизни Пайғамбар овозидан юқори кўтарманг ва унга бир-бирингиз ила сўзлашгандек дағал сўз айтманг, ўзингиз сезмаган ҳолда амалларингиз ҳабата бўлиб қолмасин'}, {'chapter': 49, 'verse': 3, 'text': 'إِنَّ ٱلَّذِينَ يَغُضُّونَ أَصۡوَٰتَهُمۡ عِندَ رَسُولِ ٱللَّهِ أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱمۡتَحَنَ ٱللَّهُ قُلُوبَهُمۡ لِلتَّقۡوَىٰۚ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٌ\nАлбатта, Расулуллоҳнинг ҳузурида овозларини пастлатганлар, ана ўшалар Аллоҳ қалбларини тақвога имтиҳон қилган зотлардир. Уларга мағфират ва улкан ажр бор'}, {'chapter': 49, 'verse': 4, 'text': 'إِنَّ ٱلَّذِينَ يُنَادُونَكَ مِن وَرَآءِ ٱلۡحُجُرَٰتِ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ\nАлбатта, сени ҳужралар ортидан чақираётганларнинг кўплари ақл ишлатмаслар'}, {'chapter': 49, 'verse': 5, 'text': 'وَلَوۡ أَنَّهُمۡ صَبَرُواْ حَتَّىٰ تَخۡرُجَ إِلَيۡهِمۡ لَكَانَ خَيۡرٗا لَّهُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nАгар уларнинг ҳузурига чиққунингча сабр қилсалар, ўзлари учун яхши бўларди. Аллоҳ мағфират қилувчи ва раҳимлидир'}, {'chapter': 49, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن جَآءَكُمۡ فَاسِقُۢ بِنَبَإٖ فَتَبَيَّنُوٓاْ أَن تُصِيبُواْ قَوۡمَۢا بِجَهَٰلَةٖ فَتُصۡبِحُواْ عَلَىٰ مَا فَعَلۡتُمۡ نَٰدِمِينَ\nЭй, иймон келтирганлар! Агар фосиқ хабар келтирса, аниқлаб кўринглар, бир қавмга билмасдан мусийбат етказиб қўйиб, қилганингизга надомат чекувчи бўлманглар'}, {'chapter': 49, 'verse': 7, 'text': 'وَٱعۡلَمُوٓاْ أَنَّ فِيكُمۡ رَسُولَ ٱللَّهِۚ لَوۡ يُطِيعُكُمۡ فِي كَثِيرٖ مِّنَ ٱلۡأَمۡرِ لَعَنِتُّمۡ وَلَٰكِنَّ ٱللَّهَ حَبَّبَ إِلَيۡكُمُ ٱلۡإِيمَٰنَ وَزَيَّنَهُۥ فِي قُلُوبِكُمۡ وَكَرَّهَ إِلَيۡكُمُ ٱلۡكُفۡرَ وَٱلۡفُسُوقَ وَٱلۡعِصۡيَانَۚ أُوْلَـٰٓئِكَ هُمُ ٱلرَّـٰشِدُونَ\nВа билингларки, албатта, ичларингизда Расулуллоҳ бордир. Агар у ишлардан кўпида сизга итоат қилса, албатта, қийинчиликка дучор бўлардингиз. Лекин, Аллоҳ сизларга иймонни маҳбуб қилди ва уни қалбларингизда зийнатлади ва сизларга куфрни, фисқни ва исённи ёмон кўрсатди. Ана ўшалар тўғриликда юрувчилардир'}, {'chapter': 49, 'verse': 8, 'text': 'فَضۡلٗا مِّنَ ٱللَّهِ وَنِعۡمَةٗۚ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nБу, Аллоҳ томонидан фазл ва неъматдир. Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 49, 'verse': 9, 'text': 'وَإِن طَآئِفَتَانِ مِنَ ٱلۡمُؤۡمِنِينَ ٱقۡتَتَلُواْ فَأَصۡلِحُواْ بَيۡنَهُمَاۖ فَإِنۢ بَغَتۡ إِحۡدَىٰهُمَا عَلَى ٱلۡأُخۡرَىٰ فَقَٰتِلُواْ ٱلَّتِي تَبۡغِي حَتَّىٰ تَفِيٓءَ إِلَىٰٓ أَمۡرِ ٱللَّهِۚ فَإِن فَآءَتۡ فَأَصۡلِحُواْ بَيۡنَهُمَا بِٱلۡعَدۡلِ وَأَقۡسِطُوٓاْۖ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ\nАгар мўминлардан икки тоифа уришиб кетсалар, бас, ўрталарини ислоҳ қилинг. Агар улардан бири иккинчисига тажовуз қилса, сиз тажовуз қилганига қарши, то у Аллоҳнинг амрига қайтгунича, урушинг, агар қайтса, бас, ўрталарини адолат билан ислоҳ қилинг. Одил бўлинг, албатта, Аллоҳ адолат қилгувчиларни суядир'}, {'chapter': 49, 'verse': 10, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ إِخۡوَةٞ فَأَصۡلِحُواْ بَيۡنَ أَخَوَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ\nАлбатта, мўминлар биродардирлар, бас, икки биродарингиз ўртасини ислоҳ қилинг, Аллоҳга тақво қилинг, шоядки, раҳим қилинсангиз'}, {'chapter': 49, 'verse': 11, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَسۡخَرۡ قَوۡمٞ مِّن قَوۡمٍ عَسَىٰٓ أَن يَكُونُواْ خَيۡرٗا مِّنۡهُمۡ وَلَا نِسَآءٞ مِّن نِّسَآءٍ عَسَىٰٓ أَن يَكُنَّ خَيۡرٗا مِّنۡهُنَّۖ وَلَا تَلۡمِزُوٓاْ أَنفُسَكُمۡ وَلَا تَنَابَزُواْ بِٱلۡأَلۡقَٰبِۖ بِئۡسَ ٱلِٱسۡمُ ٱلۡفُسُوقُ بَعۡدَ ٱلۡإِيمَٰنِۚ وَمَن لَّمۡ يَتُبۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nЭй, иймон келтирганлар! Бир қавм бошқасини масхара қилмасин, эҳтимол, улар булардан яхшироқдир. Ва аёллар ҳам бошқа аёлларни (масхара қилмасин), эҳтимол, улар булардан яхшироқдир. Ва ўзингизни ўзингиз мазах қилманг, бир-бирингизга лақаб қўйманг. Иймондан кейин фосиқлик исми нақадар ёмон! Ва ким тавба қилмаса, бас, ана ўшалар, ўзлари золимлардир'}, {'chapter': 49, 'verse': 12, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱجۡتَنِبُواْ كَثِيرٗا مِّنَ ٱلظَّنِّ إِنَّ بَعۡضَ ٱلظَّنِّ إِثۡمٞۖ وَلَا تَجَسَّسُواْ وَلَا يَغۡتَب بَّعۡضُكُم بَعۡضًاۚ أَيُحِبُّ أَحَدُكُمۡ أَن يَأۡكُلَ لَحۡمَ أَخِيهِ مَيۡتٗا فَكَرِهۡتُمُوهُۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ تَوَّابٞ رَّحِيمٞ\nЭй, иймон келтирганлар! Кўп гумонлардан четда бўлинглар, чунки баъзи гумонлар гуноҳдир. Жосуслик қилманглар. Баъзиларингиз баъзиларингизни ғийбат қилманглар. Сизлардан бирорталарингиз ўзининг ўлган биродарининг гўштини ейишни яхши кўрурми? Ҳа, ёмон кўрасизлар. Аллоҳдан қўрқинглар! Албатта, Аллоҳ тавбани кўп қабул қилувчи ва раҳимлидир'}, {'chapter': 49, 'verse': 13, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّا خَلَقۡنَٰكُم مِّن ذَكَرٖ وَأُنثَىٰ وَجَعَلۡنَٰكُمۡ شُعُوبٗا وَقَبَآئِلَ لِتَعَارَفُوٓاْۚ إِنَّ أَكۡرَمَكُمۡ عِندَ ٱللَّهِ أَتۡقَىٰكُمۡۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٞ\nЭй одамлар! Биз сизларни бир эркак ва аёлдан яратдик ва сизларни ўзаро танишишингиз учун халқлар ва қабилалар қилиб қўйдик. Албатта, Аллоҳнинг ҳузурида энг ҳурматлигингиз энг тақводорингиздир. Албатта, Аллоҳ билувчи ва хабардор Зотдир'}, {'chapter': 49, 'verse': 14, 'text': '۞قَالَتِ ٱلۡأَعۡرَابُ ءَامَنَّاۖ قُل لَّمۡ تُؤۡمِنُواْ وَلَٰكِن قُولُوٓاْ أَسۡلَمۡنَا وَلَمَّا يَدۡخُلِ ٱلۡإِيمَٰنُ فِي قُلُوبِكُمۡۖ وَإِن تُطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ لَا يَلِتۡكُم مِّنۡ أَعۡمَٰلِكُمۡ شَيۡـًٔاۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nАъробийлар: «Иймон келтирдик», дейишди. Сен: «Иймон келтирганингиз йўқ, лекин, бўйинсундик, денглар, ҳали иймон қалбларингизга киргани йўқ ва агар Аллоҳга ва Унинг Расулига итоат қилсангиз, амалларингиздан ҳеч нарса камаймас. Албатта, Аллоҳ кечирувчи ва раҳимли Зотдир», деб айт'}, {'chapter': 49, 'verse': 15, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمۡ يَرۡتَابُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِۚ أُوْلَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ\nАлбатта, ҳақиқий мўминлар Аллоҳга ва Унинг Расулига иймон келтирган, сўнгра шубҳа қилмаган ва Аллоҳнинг йўлида моллари ва жонлари ила жиҳод қилганлардир. Ана ўшалар, ўшаларгина (иймонида) содиқлардир'}, {'chapter': 49, 'verse': 16, 'text': 'قُلۡ أَتُعَلِّمُونَ ٱللَّهَ بِدِينِكُمۡ وَٱللَّهُ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\nСен: «Аллоҳга динингизни билдирмоқчимисиз? Ва ҳолбуки, Аллоҳ осмонлару ердаги нарсани билувчи Зотдир. Аллоҳ ҳар бир нарсани билувчидир», деб айт'}, {'chapter': 49, 'verse': 17, 'text': 'يَمُنُّونَ عَلَيۡكَ أَنۡ أَسۡلَمُواْۖ قُل لَّا تَمُنُّواْ عَلَيَّ إِسۡلَٰمَكُمۖ بَلِ ٱللَّهُ يَمُنُّ عَلَيۡكُمۡ أَنۡ هَدَىٰكُمۡ لِلۡإِيمَٰنِ إِن كُنتُمۡ صَٰدِقِينَ\nСенга мусулмон бўлганларини миннат қилурлар. Сен: «Менга Исломингизни миннат қилманг. Балки агар содиқ бўлсангиз, Аллоҳ сизни иймонга ҳидоят қилганини миннат қилур», деб айт'}, {'chapter': 49, 'verse': 18, 'text': 'إِنَّ ٱللَّهَ يَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ بَصِيرُۢ بِمَا تَعۡمَلُونَ\nАлбатта, Аллоҳ осмонлару ернинг ғайбини билувчи Зотдир. Ва Аллоҳ қилаётган ишларингизни кўрувчи Зотдир'}], '50': [{'chapter': 50, 'verse': 1, 'text': 'قٓۚ وَٱلۡقُرۡءَانِ ٱلۡمَجِيدِ\nҚооф. Улуғ Қуръон ила қасам'}, {'chapter': 50, 'verse': 2, 'text': 'بَلۡ عَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡ فَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا شَيۡءٌ عَجِيبٌ\nБалки, уларга ўзларидан огоҳлантирувчи келганидан ажабландилар ва кофирлар: «Бу ажиб нарса-ку?» – дедилар'}, {'chapter': 50, 'verse': 3, 'text': 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗاۖ ذَٰلِكَ رَجۡعُۢ بَعِيدٞ\nЎлиб, тупроқ бўлганимиздан кейин ҳам-а? Бу қайтиш (ақлдан) узоқ'}, {'chapter': 50, 'verse': 4, 'text': 'قَدۡ عَلِمۡنَا مَا تَنقُصُ ٱلۡأَرۡضُ مِنۡهُمۡۖ وَعِندَنَا كِتَٰبٌ حَفِيظُۢ\nҲақиқатан, Биз уларни ер камайтираётганини билдик ва Бизнинг ҳузуримизда сақловчи китоб бор'}, {'chapter': 50, 'verse': 5, 'text': 'بَلۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَهُمۡ فِيٓ أَمۡرٖ مَّرِيجٍ\nАниқки, уларга ҳақ келганда, уни ёлғонга чиқардилар. Бас, улар изтиробли ишдадирлар'}, {'chapter': 50, 'verse': 6, 'text': 'أَفَلَمۡ يَنظُرُوٓاْ إِلَى ٱلسَّمَآءِ فَوۡقَهُمۡ كَيۡفَ بَنَيۡنَٰهَا وَزَيَّنَّـٰهَا وَمَا لَهَا مِن فُرُوجٖ\nУстиларидаги осмонга назар солмайдиларми? Уни қандай бино қилибмиз ва зийнатлабмиз? Ва (ҳолбуки), унинг тешиклари ҳам йўқ'}, {'chapter': 50, 'verse': 7, 'text': 'وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجِۭ بَهِيجٖ\nВа ерни чўзиб қўйдик, унга тоғларни ташладик ва унда турли гўзал жуфтларни ўстирдик'}, {'chapter': 50, 'verse': 8, 'text': 'تَبۡصِرَةٗ وَذِكۡرَىٰ لِكُلِّ عَبۡدٖ مُّنِيبٖ\n(Роббисига) қайтувчи ҳар бир бандага кўргазма ва эслатма қилиб'}, {'chapter': 50, 'verse': 9, 'text': 'وَنَزَّلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ مُّبَٰرَكٗا فَأَنۢبَتۡنَا بِهِۦ جَنَّـٰتٖ وَحَبَّ ٱلۡحَصِيدِ\nВа осмондан барака сувини туширдик ҳамда у билан боғ-роғларни ва дон ҳосилини ўстирдик'}, {'chapter': 50, 'verse': 10, 'text': 'وَٱلنَّخۡلَ بَاسِقَٰتٖ لَّهَا طَلۡعٞ نَّضِيدٞ\nВа зич мевали шингиллари бор хурмоларни ҳам'}, {'chapter': 50, 'verse': 11, 'text': 'رِّزۡقٗا لِّلۡعِبَادِۖ وَأَحۡيَيۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ ٱلۡخُرُوجُ\nБандаларга ризқ қилиб. Ва у (ёмғир) билан ўлган шаҳарни тирилтирдик. Шундай бўладир (қабрдан) чиқиш ҳам'}, {'chapter': 50, 'verse': 12, 'text': 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَأَصۡحَٰبُ ٱلرَّسِّ وَثَمُودُ\nУлардан аввал Нуҳ қавми, қудуқ соҳиблари ва Самуд ёлғонга чиқарганлар'}, {'chapter': 50, 'verse': 13, 'text': 'وَعَادٞ وَفِرۡعَوۡنُ وَإِخۡوَٰنُ لُوطٖ\nВа Од, Фиръавн, Лут биродарлари'}, {'chapter': 50, 'verse': 14, 'text': 'وَأَصۡحَٰبُ ٱلۡأَيۡكَةِ وَقَوۡمُ تُبَّعٖۚ كُلّٞ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِيدِ\nВа дарахтзор эгалари ва Туббаъ қавми — барчалари Пайғамбарларни ёлғонга чиқарганлар. Бас, (азоб) ваъдам ҳақ бўлди'}, {'chapter': 50, 'verse': 15, 'text': 'أَفَعَيِينَا بِٱلۡخَلۡقِ ٱلۡأَوَّلِۚ بَلۡ هُمۡ فِي لَبۡسٖ مِّنۡ خَلۡقٖ جَدِيدٖ\nАввалги яратишга ожиз бўл-дикми? Йўқ! Улар янгидан яратилишга шубҳададирлар'}, {'chapter': 50, 'verse': 16, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ وَنَعۡلَمُ مَا تُوَسۡوِسُ بِهِۦ نَفۡسُهُۥۖ وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنۡ حَبۡلِ ٱلۡوَرِيدِ\nАлбатта, инсонни Биз яратганмиз ва унинг нафси нимани васваса қилишини ҳам билурмиз. Ва Биз унга жон томиридан ҳам яқинмиз'}, {'chapter': 50, 'verse': 17, 'text': 'إِذۡ يَتَلَقَّى ٱلۡمُتَلَقِّيَانِ عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ قَعِيدٞ\nВақтики икки кутиб олувчи ўнгда ва чапда ўтирган ҳолларида кутиб олурлар'}, {'chapter': 50, 'verse': 18, 'text': 'مَّا يَلۡفِظُ مِن قَوۡلٍ إِلَّا لَدَيۡهِ رَقِيبٌ عَتِيدٞ\nБирор сўз айтмас, магар ҳузурида ҳозиру нозир борлар'}, {'chapter': 50, 'verse': 19, 'text': 'وَجَآءَتۡ سَكۡرَةُ ٱلۡمَوۡتِ بِٱلۡحَقِّۖ ذَٰلِكَ مَا كُنتَ مِنۡهُ تَحِيدُ\nЎлимнинг мастлиги (талвасаси) ҳақиқат бўлиб келди. Сен қочиб юрган нарса шулдир'}, {'chapter': 50, 'verse': 20, 'text': 'وَنُفِخَ فِي ٱلصُّورِۚ ذَٰلِكَ يَوۡمُ ٱلۡوَعِيدِ\nВа сур ҳам чалинди. Бу, қўрқитилган кундир'}, {'chapter': 50, 'verse': 21, 'text': 'وَجَآءَتۡ كُلُّ نَفۡسٖ مَّعَهَا سَآئِقٞ وَشَهِيدٞ\nВа ҳар бир жон ҳайдагувчи ва гувоҳлик берувчи билан келди'}, {'chapter': 50, 'verse': 22, 'text': 'لَّقَدۡ كُنتَ فِي غَفۡلَةٖ مِّنۡ هَٰذَا فَكَشَفۡنَا عَنكَ غِطَآءَكَ فَبَصَرُكَ ٱلۡيَوۡمَ حَدِيدٞ\nАлбатта, сен бундан ғафлатда эдинг. Бас, Биз сенинг пардангни очдик ва бугун кўзинг ўткирдир'}, {'chapter': 50, 'verse': 23, 'text': 'وَقَالَ قَرِينُهُۥ هَٰذَا مَا لَدَيَّ عَتِيدٌ\nВа унинг «яқини»: «Мана бу мендаги тайёр нарса», деди'}, {'chapter': 50, 'verse': 24, 'text': 'أَلۡقِيَا فِي جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٖ\nҲар бир қайсарни, кофирни жаҳаннамга ташланг'}, {'chapter': 50, 'verse': 25, 'text': 'مَّنَّاعٖ لِّلۡخَيۡرِ مُعۡتَدٖ مُّرِيبٍ\nЯхшиликни ман қилувчини, тажовузкорни, шак келтирувчини'}, {'chapter': 50, 'verse': 26, 'text': 'ٱلَّذِي جَعَلَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَأَلۡقِيَاهُ فِي ٱلۡعَذَابِ ٱلشَّدِيدِ\nАллоҳ билан бирга бошқани илоҳ қилиб олганни шиддатли азобга ташланг'}, {'chapter': 50, 'verse': 27, 'text': '۞قَالَ قَرِينُهُۥ رَبَّنَا مَآ أَطۡغَيۡتُهُۥ وَلَٰكِن كَانَ فِي ضَلَٰلِۭ بَعِيدٖ\nУнинг «яқини»: «Роббимиз, уни мен туғёнга кетказганим йўқ, лекин унинг ўзи узоқ залолатда бўлди», дер'}, {'chapter': 50, 'verse': 28, 'text': 'قَالَ لَا تَخۡتَصِمُواْ لَدَيَّ وَقَدۡ قَدَّمۡتُ إِلَيۡكُم بِٱلۡوَعِيدِ\nУ зот деди: «Менинг ҳузуримда тортишманглар, сизларга азоб ваъдасини тақдим қилувдим'}, {'chapter': 50, 'verse': 29, 'text': 'مَا يُبَدَّلُ ٱلۡقَوۡلُ لَدَيَّ وَمَآ أَنَا۠ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nМенинг ҳузуримда гап ўзгармас ва Мен бандаларга зулм қилгувчи эмасман»'}, {'chapter': 50, 'verse': 30, 'text': 'يَوۡمَ نَقُولُ لِجَهَنَّمَ هَلِ ٱمۡتَلَأۡتِ وَتَقُولُ هَلۡ مِن مَّزِيدٖ\nБугун жаҳаннамга: «Тўлдингми?» десак, у: «Яна қўшимча борми?» дер'}, {'chapter': 50, 'verse': 31, 'text': 'وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِينَ غَيۡرَ بَعِيدٍ\nВа жаннат тақводорларга узоқ бўлмаган жойга яқинлаштирилди'}, {'chapter': 50, 'verse': 32, 'text': 'هَٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٖ\nБу — сизга ваъда қилинган нарсадир. Ҳар бир сертавбага ва сақловчига'}, {'chapter': 50, 'verse': 33, 'text': 'مَّنۡ خَشِيَ ٱلرَّحۡمَٰنَ بِٱلۡغَيۡبِ وَجَآءَ بِقَلۡبٖ مُّنِيبٍ\nРоҳмандан ғойибона қўрққан ва саломат қалб ила келган кимсага'}, {'chapter': 50, 'verse': 34, 'text': 'ٱدۡخُلُوهَا بِسَلَٰمٖۖ ذَٰلِكَ يَوۡمُ ٱلۡخُلُودِ\n«Унга саломат бўлган ҳолда кирингиз. Бу — мангулик кунидир» (дейилур)'}, {'chapter': 50, 'verse': 35, 'text': 'لَهُم مَّا يَشَآءُونَ فِيهَا وَلَدَيۡنَا مَزِيدٞ\nУ ерда нима хоҳласалар, уларники. Ва ҳузуримизда зиёдаси бор'}, {'chapter': 50, 'verse': 36, 'text': 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَشَدُّ مِنۡهُم بَطۡشٗا فَنَقَّبُواْ فِي ٱلۡبِلَٰدِ هَلۡ مِن مَّحِيصٍ\nУлардан аввал, улардан кўра кучлироқ аср(авлод)лардан қанчасини ҳалок қилдик. «Қочадиган жой борми?» деб юртма-юрт кезиб чиқдилар'}, {'chapter': 50, 'verse': 37, 'text': 'إِنَّ فِي ذَٰلِكَ لَذِكۡرَىٰ لِمَن كَانَ لَهُۥ قَلۡبٌ أَوۡ أَلۡقَى ٱلسَّمۡعَ وَهُوَ شَهِيدٞ\nАлбатта, бунда қалби борларга ва зеҳн билан қулоқ осганларга эслатма бордир'}, {'chapter': 50, 'verse': 38, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ وَمَا مَسَّنَا مِن لُّغُوبٖ\nАлбатта, осмонлару ерни ва уларнинг орасидаги нарсаларни олти кунда яратдик. Ва Бизни чарчоқ тутмади'}, {'chapter': 50, 'verse': 39, 'text': 'فَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ ٱلۡغُرُوبِ\nУларнинг айтаётганларига сабр қил ва Роббингга қуёш чиқиши ва ботишидан аввал ҳамду тасбиҳ айт'}, {'chapter': 50, 'verse': 40, 'text': 'وَمِنَ ٱلَّيۡلِ فَسَبِّحۡهُ وَأَدۡبَٰرَ ٱلسُّجُودِ\nКечасида ҳам Унга тасбиҳ айт. Ва саждадан кейин ҳам'}, {'chapter': 50, 'verse': 41, 'text': 'وَٱسۡتَمِعۡ يَوۡمَ يُنَادِ ٱلۡمُنَادِ مِن مَّكَانٖ قَرِيبٖ\nНидо қилувчи яқин жойдан нидо қилган кунда қулоқ ос'}, {'chapter': 50, 'verse': 42, 'text': 'يَوۡمَ يَسۡمَعُونَ ٱلصَّيۡحَةَ بِٱلۡحَقِّۚ ذَٰلِكَ يَوۡمُ ٱلۡخُرُوجِ\nУ кунда қичқириқни ҳақ ила эшитурлар. Ана ўша, чиқиш кунидир'}, {'chapter': 50, 'verse': 43, 'text': 'إِنَّا نَحۡنُ نُحۡيِۦ وَنُمِيتُ وَإِلَيۡنَا ٱلۡمَصِيرُ\nАлбатта, Биз тирилтирурмиз, ўлдирурмиз. Ва қайтиш ҳам Бизгадир'}, {'chapter': 50, 'verse': 44, 'text': 'يَوۡمَ تَشَقَّقُ ٱلۡأَرۡضُ عَنۡهُمۡ سِرَاعٗاۚ ذَٰلِكَ حَشۡرٌ عَلَيۡنَا يَسِيرٞ\nУ кунда устларидаги ер ёрилиб, шошиб чиқурлар. Ана ўша, Биз учун осон тўплашдир'}, {'chapter': 50, 'verse': 45, 'text': 'نَّحۡنُ أَعۡلَمُ بِمَا يَقُولُونَۖ وَمَآ أَنتَ عَلَيۡهِم بِجَبَّارٖۖ فَذَكِّرۡ بِٱلۡقُرۡءَانِ مَن يَخَافُ وَعِيدِ\nУларнинг айтганларини Биз билувчимиз. Сен уларнинг устидаги жаббор эмассан. Бас, Қуръон ила азоб ваъдамдан қўрққанларни огоҳлантир'}], '51': [{'chapter': 51, 'verse': 1, 'text': 'وَٱلذَّـٰرِيَٰتِ ذَرۡوٗا\nЧанг тўзитувчи(шамол)лар билан қасам'}, {'chapter': 51, 'verse': 2, 'text': 'فَٱلۡحَٰمِلَٰتِ وِقۡرٗا\nЮк кўтарувчи(булут)лар билан қасам'}, {'chapter': 51, 'verse': 3, 'text': 'فَٱلۡجَٰرِيَٰتِ يُسۡرٗا\nОсон юрувчи(кема)лар билан қасам'}, {'chapter': 51, 'verse': 4, 'text': 'فَٱلۡمُقَسِّمَٰتِ أَمۡرًا\nИш тақсимловчи(фаришта)лар билан қасам'}, {'chapter': 51, 'verse': 5, 'text': 'إِنَّمَا تُوعَدُونَ لَصَادِقٞ\nАлбатта, сизга ваъда қилинган нарса ростдир'}, {'chapter': 51, 'verse': 6, 'text': 'وَإِنَّ ٱلدِّينَ لَوَٰقِعٞ\nВа, албатта, қиёмат воқеъ бўлгувчидир'}, {'chapter': 51, 'verse': 7, 'text': 'وَٱلسَّمَآءِ ذَاتِ ٱلۡحُبُكِ\nГўзал, мустаҳкам осмон билан қасам'}, {'chapter': 51, 'verse': 8, 'text': 'إِنَّكُمۡ لَفِي قَوۡلٖ مُّخۡتَلِفٖ\nАлбатта, сизларнинг гапларингиз ихтилофлидир'}, {'chapter': 51, 'verse': 9, 'text': 'يُؤۡفَكُ عَنۡهُ مَنۡ أُفِكَ\nУ(Қуръон)дан ким бурилса, (бутунлай) буриб қўйиладир'}, {'chapter': 51, 'verse': 10, 'text': 'قُتِلَ ٱلۡخَرَّـٰصُونَ\nЁлғончиларга лаънат бўлсин'}, {'chapter': 51, 'verse': 11, 'text': 'ٱلَّذِينَ هُمۡ فِي غَمۡرَةٖ سَاهُونَ\nУлар жаҳолатдаги ғофиллардир'}, {'chapter': 51, 'verse': 12, 'text': 'يَسۡـَٔلُونَ أَيَّانَ يَوۡمُ ٱلدِّينِ\nУлар: «Ҳисоб-китоб куни қачон», деб сўрарлар'}, {'chapter': 51, 'verse': 13, 'text': 'يَوۡمَ هُمۡ عَلَى ٱلنَّارِ يُفۡتَنُونَ\nБу кунда улар оловда азобланурлар'}, {'chapter': 51, 'verse': 14, 'text': 'ذُوقُواْ فِتۡنَتَكُمۡ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ\n«Азобингизни татиб кўринг», сиз «Қачон бўладир», деб шошилган нарса — шу!» (дейилур)'}, {'chapter': 51, 'verse': 15, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَعُيُونٍ\nАлбатта, тақводорлар жаннатлар ва булоқлардадир'}, {'chapter': 51, 'verse': 16, 'text': 'ءَاخِذِينَ مَآ ءَاتَىٰهُمۡ رَبُّهُمۡۚ إِنَّهُمۡ كَانُواْ قَبۡلَ ذَٰلِكَ مُحۡسِنِينَ\nРоббилари берган нарсаларни олувчилардир. Чунки, улар бундан олдин эҳсон қилувчилардан бўлганлардир'}, {'chapter': 51, 'verse': 17, 'text': 'كَانُواْ قَلِيلٗا مِّنَ ٱلَّيۡلِ مَا يَهۡجَعُونَ\nУлар кечалари оз ухлар эдилар'}, {'chapter': 51, 'verse': 18, 'text': 'وَبِٱلۡأَسۡحَارِ هُمۡ يَسۡتَغۡفِرُونَ\nВа улар саҳарларда истиғфор айтар эдилар'}, {'chapter': 51, 'verse': 19, 'text': 'وَفِيٓ أَمۡوَٰلِهِمۡ حَقّٞ لِّلسَّآئِلِ وَٱلۡمَحۡرُومِ\nВа молу мулкларида сўровчи ва бечораларнинг ҳаққи бордир'}, {'chapter': 51, 'verse': 20, 'text': 'وَفِي ٱلۡأَرۡضِ ءَايَٰتٞ لِّلۡمُوقِنِينَ\nВа ерда чуқур ишонувчилар учун белгилар бор'}, {'chapter': 51, 'verse': 21, 'text': 'وَفِيٓ أَنفُسِكُمۡۚ أَفَلَا تُبۡصِرُونَ\nВа ўзларингизда ҳам (белгилар) бор. Ёки кўрмаяпсизларми'}, {'chapter': 51, 'verse': 22, 'text': 'وَفِي ٱلسَّمَآءِ رِزۡقُكُمۡ وَمَا تُوعَدُونَ\nВа осмонда ризқингиз ҳамда сизга ваъда қилинаётган нарса (бор)'}, {'chapter': 51, 'verse': 23, 'text': 'فَوَرَبِّ ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِنَّهُۥ لَحَقّٞ مِّثۡلَ مَآ أَنَّكُمۡ تَنطِقُونَ\nОсмону ернинг Роббиси ила қасамки, албатта, у (ваъда) мисоли сиз нутқ қилаётганингиздек ҳақиқатдир'}, {'chapter': 51, 'verse': 24, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ ضَيۡفِ إِبۡرَٰهِيمَ ٱلۡمُكۡرَمِينَ\nСенга Иброҳимнинг карамли меҳмонларининг хабари келмадими'}, {'chapter': 51, 'verse': 25, 'text': 'إِذۡ دَخَلُواْ عَلَيۡهِ فَقَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞ قَوۡمٞ مُّنكَرُونَ\nУлар унинг ҳузурига кириб, «Салом» деганларида, у «Салом, нотаниш қавмлар», деди'}, {'chapter': 51, 'verse': 26, 'text': 'فَرَاغَ إِلَىٰٓ أَهۡلِهِۦ فَجَآءَ بِعِجۡلٖ سَمِينٖ\nАҳлига (махфий) бориб, семиз бузоқни (пишириб) келтирди'}, {'chapter': 51, 'verse': 27, 'text': 'فَقَرَّبَهُۥٓ إِلَيۡهِمۡ قَالَ أَلَا تَأۡكُلُونَ\nУни уларга яқин сурди ва: «Емайсизларми?» – деди'}, {'chapter': 51, 'verse': 28, 'text': 'فَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۖ قَالُواْ لَا تَخَفۡۖ وَبَشَّرُوهُ بِغُلَٰمٍ عَلِيمٖ\nУлардан хавфсиради. Улар: «Қўрқма», дедилар ва унга илмли ўғил башоратини бердилар'}, {'chapter': 51, 'verse': 29, 'text': 'فَأَقۡبَلَتِ ٱمۡرَأَتُهُۥ فِي صَرَّةٖ فَصَكَّتۡ وَجۡهَهَا وَقَالَتۡ عَجُوزٌ عَقِيمٞ\nБас, хотини қичқириб келди ва ўз юзига шапалоқ тушириб: «Қари, туғмас хотин-а?!» – деди'}, {'chapter': 51, 'verse': 30, 'text': 'قَالُواْ كَذَٰلِكِ قَالَ رَبُّكِۖ إِنَّهُۥ هُوَ ٱلۡحَكِيمُ ٱلۡعَلِيمُ\nУлар: «Сенинг Роббинг шундай деди. Албатта, Унинг Ўзи Ҳакийм ва Алиймдир», дедилар'}, {'chapter': 51, 'verse': 31, 'text': '۞قَالَ فَمَا خَطۡبُكُمۡ أَيُّهَا ٱلۡمُرۡسَلُونَ\nУ: «Эй элчилар! Сизнинг ишингиз недир?» – деди'}, {'chapter': 51, 'verse': 32, 'text': 'قَالُوٓاْ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمٖ مُّجۡرِمِينَ\nУлар: «Албатта, биз жиноятчи қавмга юборилганмиз'}, {'chapter': 51, 'verse': 33, 'text': 'لِنُرۡسِلَ عَلَيۡهِمۡ حِجَارَةٗ مِّن طِينٖ\nУларнинг устидан лойдан (пиширилган) тошларни ташлаш учун'}, {'chapter': 51, 'verse': 34, 'text': 'مُّسَوَّمَةً عِندَ رَبِّكَ لِلۡمُسۡرِفِينَ\nРоббинг ҳузурида исрофчилар учун белгилаб қўйилганларни», дедилар'}, {'chapter': 51, 'verse': 35, 'text': 'فَأَخۡرَجۡنَا مَن كَانَ فِيهَا مِنَ ٱلۡمُؤۡمِنِينَ\nВа Биз у жойдан мўминларни чиқардик'}, {'chapter': 51, 'verse': 36, 'text': 'فَمَا وَجَدۡنَا فِيهَا غَيۡرَ بَيۡتٖ مِّنَ ٱلۡمُسۡلِمِينَ\nУ ерда бир хонадондан бошқа мусулмонларни топмадик'}, {'chapter': 51, 'verse': 37, 'text': 'وَتَرَكۡنَا فِيهَآ ءَايَةٗ لِّلَّذِينَ يَخَافُونَ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nВа у ерда аламли азобдан қўрқадиганлар учун оят(белги) қолдирдик'}, {'chapter': 51, 'verse': 38, 'text': 'وَفِي مُوسَىٰٓ إِذۡ أَرۡسَلۡنَٰهُ إِلَىٰ فِرۡعَوۡنَ بِسُلۡطَٰنٖ مُّبِينٖ\nВа Мусода ҳам (белги) бор. Биз уни Фиръавнга равшан ҳужжат ила юборганимизда'}, {'chapter': 51, 'verse': 39, 'text': 'فَتَوَلَّىٰ بِرُكۡنِهِۦ وَقَالَ سَٰحِرٌ أَوۡ مَجۡنُونٞ\nУ юз ўгириб, ишонгани томон юрди ва: «(Мусо) сеҳргар ёки мажнундир», деди'}, {'chapter': 51, 'verse': 40, 'text': 'فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي ٱلۡيَمِّ وَهُوَ مُلِيمٞ\nБас, Биз уни ва аскарларини тутиб денгизга ташладик ва у маломатга лойиқ иш қилувчи эди'}, {'chapter': 51, 'verse': 41, 'text': 'وَفِي عَادٍ إِذۡ أَرۡسَلۡنَا عَلَيۡهِمُ ٱلرِّيحَ ٱلۡعَقِيمَ\nВа Одда ҳам (белги) бор. Вақтики, Биз уларга туғмас шамолни юбордик'}, {'chapter': 51, 'verse': 42, 'text': 'مَا تَذَرُ مِن شَيۡءٍ أَتَتۡ عَلَيۡهِ إِلَّا جَعَلَتۡهُ كَٱلرَّمِيمِ\nУ қайси нарсага етиб борса, титиб ташламасдан қўймас'}, {'chapter': 51, 'verse': 43, 'text': 'وَفِي ثَمُودَ إِذۡ قِيلَ لَهُمۡ تَمَتَّعُواْ حَتَّىٰ حِينٖ\nВа Самудда ҳам (белги) бор. Уларга: «Вақтинчалик маза қилиб туринглар», дейилган вақтда'}, {'chapter': 51, 'verse': 44, 'text': 'فَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ وَهُمۡ يَنظُرُونَ\nАллоҳнинг амридан бош тортдилар, бас, қараб турган ҳолларида уларни қаттиқ овоз тутди'}, {'chapter': 51, 'verse': 45, 'text': 'فَمَا ٱسۡتَطَٰعُواْ مِن قِيَامٖ وَمَا كَانُواْ مُنتَصِرِينَ\nБас, улар ўринларидан туролмадилар ва ёрдам ҳам ололмадилар'}, {'chapter': 51, 'verse': 46, 'text': 'وَقَوۡمَ نُوحٖ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nВа бундан аввал Нуҳ қавмини ҳам (ҳалок этганмиз). Албатта, улар фосиқ қавм эдилар'}, {'chapter': 51, 'verse': 47, 'text': 'وَٱلسَّمَآءَ بَنَيۡنَٰهَا بِأَيۡيْدٖ وَإِنَّا لَمُوسِعُونَ\nОсмонни Ўз қўлимизда(қудратимиз) ила бино қилдик. Албатта, Биз уни кенгайтирувчимиз'}, {'chapter': 51, 'verse': 48, 'text': 'وَٱلۡأَرۡضَ فَرَشۡنَٰهَا فَنِعۡمَ ٱلۡمَٰهِدُونَ\nВа ерни тўшаб қўйдик. Биз қандоқ ҳам яхши тайёрловчидирмиз'}, {'chapter': 51, 'verse': 49, 'text': 'وَمِن كُلِّ شَيۡءٍ خَلَقۡنَا زَوۡجَيۡنِ لَعَلَّكُمۡ تَذَكَّرُونَ\nВа Биз ҳар бир нарсани жуфт яратдик. Шоядки, эсласангизлар'}, {'chapter': 51, 'verse': 50, 'text': 'فَفِرُّوٓاْ إِلَى ٱللَّهِۖ إِنِّي لَكُم مِّنۡهُ نَذِيرٞ مُّبِينٞ\nБас, фақат Аллоҳгагина қочинг. Албатта, мен сизларга Ундан (келган) очиқ-ойдин огоҳлантирувчиман'}, {'chapter': 51, 'verse': 51, 'text': 'وَلَا تَجۡعَلُواْ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۖ إِنِّي لَكُم مِّنۡهُ نَذِيرٞ مُّبِينٞ\nВа Аллоҳга бошқа маъбудни шерик қилманглар. Албатта, мен сизларга Ундан (келган) очиқ-ойдин огоҳлантирувчиман'}, {'chapter': 51, 'verse': 52, 'text': 'كَذَٰلِكَ مَآ أَتَى ٱلَّذِينَ مِن قَبۡلِهِم مِّن رَّسُولٍ إِلَّا قَالُواْ سَاحِرٌ أَوۡ مَجۡنُونٌ\nХудди шундай, булардан аввалгиларга ҳам ҳар бир Пайғамбар келган вақт (уни) сеҳргар ёки мажнун деганлар'}, {'chapter': 51, 'verse': 53, 'text': 'أَتَوَاصَوۡاْ بِهِۦۚ بَلۡ هُمۡ قَوۡمٞ طَاغُونَ\nКелишиб олганмилар?! Йўқ, улар туғёнга кетган қавмлардир'}, {'chapter': 51, 'verse': 54, 'text': 'فَتَوَلَّ عَنۡهُمۡ فَمَآ أَنتَ بِمَلُومٖ\nУлардан юз ўгир. Бас, сен маломатга қолувчи эмассан'}, {'chapter': 51, 'verse': 55, 'text': 'وَذَكِّرۡ فَإِنَّ ٱلذِّكۡرَىٰ تَنفَعُ ٱلۡمُؤۡمِنِينَ\nВа эслатгин. Албатта, эслатиш мўминларга манфаат берур'}, {'chapter': 51, 'verse': 56, 'text': 'وَمَا خَلَقۡتُ ٱلۡجِنَّ وَٱلۡإِنسَ إِلَّا لِيَعۡبُدُونِ\nЖин ва инсонни фақат Менга ибодат қилишлари учунгина яратдим'}, {'chapter': 51, 'verse': 57, 'text': 'مَآ أُرِيدُ مِنۡهُم مِّن رِّزۡقٖ وَمَآ أُرِيدُ أَن يُطۡعِمُونِ\nМен улардан ризқ хоҳламайман ва Мени овқатлантиришларини ҳам хоҳламасман'}, {'chapter': 51, 'verse': 58, 'text': 'إِنَّ ٱللَّهَ هُوَ ٱلرَّزَّاقُ ذُو ٱلۡقُوَّةِ ٱلۡمَتِينُ\nАлбатта, Аллоҳ Ўзи ризқ берувчи, қувват эгаси, шиддатлидир'}, {'chapter': 51, 'verse': 59, 'text': 'فَإِنَّ لِلَّذِينَ ظَلَمُواْ ذَنُوبٗا مِّثۡلَ ذَنُوبِ أَصۡحَٰبِهِمۡ فَلَا يَسۡتَعۡجِلُونِ\nАлбатта, зулм қилганларга ўз биродарлари азобичалик азоб бор, шошилмасинлар'}, {'chapter': 51, 'verse': 60, 'text': 'فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِن يَوۡمِهِمُ ٱلَّذِي يُوعَدُونَ\nКофирларга ваъда қилинаётган кунларида ҳалокат бўлсин'}], '52': [{'chapter': 52, 'verse': 1, 'text': 'وَٱلطُّورِ\nТур билан қасам'}, {'chapter': 52, 'verse': 2, 'text': 'وَكِتَٰبٖ مَّسۡطُورٖ\nСатрларга битилган китоб билан қасам'}, {'chapter': 52, 'verse': 3, 'text': 'فِي رَقّٖ مَّنشُورٖ\nЁзишга тайёрланган терига'}, {'chapter': 52, 'verse': 4, 'text': 'وَٱلۡبَيۡتِ ٱلۡمَعۡمُورِ\nВа обод уй билан қасам'}, {'chapter': 52, 'verse': 5, 'text': 'وَٱلسَّقۡفِ ٱلۡمَرۡفُوعِ\nВа кўтарилган шифт билан қасам'}, {'chapter': 52, 'verse': 6, 'text': 'وَٱلۡبَحۡرِ ٱلۡمَسۡجُورِ\nВа тўлган денгиз билан қасам'}, {'chapter': 52, 'verse': 7, 'text': 'إِنَّ عَذَابَ رَبِّكَ لَوَٰقِعٞ\nАлбатта, Роббингнинг азоби рўй беражак'}, {'chapter': 52, 'verse': 8, 'text': 'مَّا لَهُۥ مِن دَافِعٖ\nУни қайтарувчи йўқдир'}, {'chapter': 52, 'verse': 9, 'text': 'يَوۡمَ تَمُورُ ٱلسَّمَآءُ مَوۡرٗا\nУ кунда осмон ларзага келиб чайқалур'}, {'chapter': 52, 'verse': 10, 'text': 'وَتَسِيرُ ٱلۡجِبَالُ سَيۡرٗا\nВа тоғлар сайр этиб юрур'}, {'chapter': 52, 'verse': 11, 'text': 'فَوَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nБас, ўша кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 52, 'verse': 12, 'text': 'ٱلَّذِينَ هُمۡ فِي خَوۡضٖ يَلۡعَبُونَ\n(Ботилга) шўнғиб ўйнашадиганларга'}, {'chapter': 52, 'verse': 13, 'text': 'يَوۡمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا\nУ кунда жаҳаннам ўтига кучлаб итарилурлар'}, {'chapter': 52, 'verse': 14, 'text': 'هَٰذِهِ ٱلنَّارُ ٱلَّتِي كُنتُم بِهَا تُكَذِّبُونَ\nМана бу, сизлар ёлғонга чиқарган ўт бўлади'}, {'chapter': 52, 'verse': 15, 'text': 'أَفَسِحۡرٌ هَٰذَآ أَمۡ أَنتُمۡ لَا تُبۡصِرُونَ\nБу сеҳрми?! Ёки сизлар кўрмаяпсизларми'}, {'chapter': 52, 'verse': 16, 'text': 'ٱصۡلَوۡهَا فَٱصۡبِرُوٓاْ أَوۡ لَا تَصۡبِرُواْ سَوَآءٌ عَلَيۡكُمۡۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\nКиринглар унга! Чидайсизларми, чидамайсизларми — бари бир. Қилиб юрган амалларингизнинг жазосини оласизлар, холос'}, {'chapter': 52, 'verse': 17, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَنَعِيمٖ\nАлбатта, тақводорлар жаннатлар ва нозу неъматлар ичидадирлар'}, {'chapter': 52, 'verse': 18, 'text': 'فَٰكِهِينَ بِمَآ ءَاتَىٰهُمۡ رَبُّهُمۡ وَوَقَىٰهُمۡ رَبُّهُمۡ عَذَابَ ٱلۡجَحِيمِ\nРоббилари уларга берган нарсалардан лаззатдадирлар. Роббилари уларни жаҳаннам азобидан сақлар'}, {'chapter': 52, 'verse': 19, 'text': 'كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ\nЕнглар, ичинглар, ош бўлсин. (Булар) қилган амалларингиз учундир'}, {'chapter': 52, 'verse': 20, 'text': 'مُتَّكِـِٔينَ عَلَىٰ سُرُرٖ مَّصۡفُوفَةٖۖ وَزَوَّجۡنَٰهُم بِحُورٍ عِينٖ\nУлар қатор тизилган сўриларга ёнбошлаган ҳолдалар. Ва уларни гўзал кўзли ҳурларга уйлаб қўйганмиз'}, {'chapter': 52, 'verse': 21, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَٱتَّبَعَتۡهُمۡ ذُرِّيَّتُهُم بِإِيمَٰنٍ أَلۡحَقۡنَا بِهِمۡ ذُرِّيَّتَهُمۡ وَمَآ أَلَتۡنَٰهُم مِّنۡ عَمَلِهِم مِّن شَيۡءٖۚ كُلُّ ٱمۡرِيِٕۭ بِمَا كَسَبَ رَهِينٞ\nЎзлари иймон келтириб, зурриётлари уларга иймон билан эргашганларнинг зурриётини уларга қўшдик ва уларнинг амалларидан ҳеч нарсани камайтирмадик. Ҳар бир киши ўз касби гаровидир'}, {'chapter': 52, 'verse': 22, 'text': 'وَأَمۡدَدۡنَٰهُم بِفَٰكِهَةٖ وَلَحۡمٖ مِّمَّا يَشۡتَهُونَ\nВа Биз уларга хоҳлаган мевалари ва гўштларини муҳайё қилиб қўйдик'}, {'chapter': 52, 'verse': 23, 'text': 'يَتَنَٰزَعُونَ فِيهَا كَأۡسٗا لَّا لَغۡوٞ فِيهَا وَلَا تَأۡثِيمٞ\nУлар унда қадаҳларни бир-бирларига узатурлар. У ерда беҳуда сергаплик ва гуноҳ йўқ'}, {'chapter': 52, 'verse': 24, 'text': '۞وَيَطُوفُ عَلَيۡهِمۡ غِلۡمَانٞ لَّهُمۡ كَأَنَّهُمۡ لُؤۡلُؤٞ مَّكۡنُونٞ\nВа атрофларида худди садафдаги дурдек ғуломлари айланиб, хизматда юришар'}, {'chapter': 52, 'verse': 25, 'text': 'وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ\nБаъзилари бошқаларидан ўгирилиб сўрай бошладилар'}, {'chapter': 52, 'verse': 26, 'text': 'قَالُوٓاْ إِنَّا كُنَّا قَبۡلُ فِيٓ أَهۡلِنَا مُشۡفِقِينَ\n«Албатта, биз илгари ўз аҳлимиз даврасида У зотдан қўрқувчилардан эдик'}, {'chapter': 52, 'verse': 27, 'text': 'فَمَنَّ ٱللَّهُ عَلَيۡنَا وَوَقَىٰنَا عَذَابَ ٱلسَّمُومِ\nБас, Аллоҳ бизга марҳамат қилди ва бизни дўзах азобидан сақлади'}, {'chapter': 52, 'verse': 28, 'text': 'إِنَّا كُنَّا مِن قَبۡلُ نَدۡعُوهُۖ إِنَّهُۥ هُوَ ٱلۡبَرُّ ٱلرَّحِيمُ\nАлбатта, биз илгари Унга дуо қилардик. Албатта, Унинг Ўзи яхшилик қилувчи ва раҳимлидур»'}, {'chapter': 52, 'verse': 29, 'text': 'فَذَكِّرۡ فَمَآ أَنتَ بِنِعۡمَتِ رَبِّكَ بِكَاهِنٖ وَلَا مَجۡنُونٍ\nБас, эслатгин: сен Роббинг неъмати ила фолбин ҳам эмассан, мажнун ҳам эмассан'}, {'chapter': 52, 'verse': 30, 'text': 'أَمۡ يَقُولُونَ شَاعِرٞ نَّتَرَبَّصُ بِهِۦ رَيۡبَ ٱلۡمَنُونِ\nБалки: «У — шоир, биз унга ўлим етишини кутамиз», дерлар'}, {'chapter': 52, 'verse': 31, 'text': 'قُلۡ تَرَبَّصُواْ فَإِنِّي مَعَكُم مِّنَ ٱلۡمُتَرَبِّصِينَ\nАйтгин: «Кутинглар, албатта, мен ҳам сизлар билан бирга кутувчидирман»'}, {'chapter': 52, 'verse': 32, 'text': 'أَمۡ تَأۡمُرُهُمۡ أَحۡلَٰمُهُم بِهَٰذَآۚ أَمۡ هُمۡ قَوۡمٞ طَاغُونَ\nЁ ақллари шунга буюрадими, ёки улар туғёнга кетган қавмларми'}, {'chapter': 52, 'verse': 33, 'text': 'أَمۡ يَقُولُونَ تَقَوَّلَهُۥۚ بَل لَّا يُؤۡمِنُونَ\nБалки, у(Қуръон)ни ўзи тўқиди, дерлар? Йўқ, улар иймон келтирмаслар'}, {'chapter': 52, 'verse': 34, 'text': 'فَلۡيَأۡتُواْ بِحَدِيثٖ مِّثۡلِهِۦٓ إِن كَانُواْ صَٰدِقِينَ\nАгар уларнинг гапи рост бўлса, у(Қуръон)га ўхшаш сўзни келтирсинлар'}, {'chapter': 52, 'verse': 35, 'text': 'أَمۡ خُلِقُواْ مِنۡ غَيۡرِ شَيۡءٍ أَمۡ هُمُ ٱلۡخَٰلِقُونَ\nБалки, улар, ҳеч нарсадан ҳеч нарса йўқ, ўзлари яралгандирлар?! Ёки улар яратувчиларми'}, {'chapter': 52, 'verse': 36, 'text': 'أَمۡ خَلَقُواْ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَۚ بَل لَّا يُوقِنُونَ\nБалки, улар осмонлару ерни яратгандирлар?! Йўқ, улар ишонмаслар'}, {'chapter': 52, 'verse': 37, 'text': 'أَمۡ عِندَهُمۡ خَزَآئِنُ رَبِّكَ أَمۡ هُمُ ٱلۡمُصَۜيۡطِرُونَ\nБалки, уларнинг ҳузурида Роббингнинг хазийналари бордир?! Ёки улар бошқарувчиларми'}, {'chapter': 52, 'verse': 38, 'text': 'أَمۡ لَهُمۡ سُلَّمٞ يَسۡتَمِعُونَ فِيهِۖ فَلۡيَأۡتِ مُسۡتَمِعُهُم بِسُلۡطَٰنٖ مُّبِينٍ\nБалки, уларнинг (хабарни) эшитадиган нарвонлари бордир?! Ундоқ бўлса, эшитувчилари равшан ҳужжат келтирсин'}, {'chapter': 52, 'verse': 39, 'text': 'أَمۡ لَهُ ٱلۡبَنَٰتُ وَلَكُمُ ٱلۡبَنُونَ\nЁки У зотга қизлар-у, сизларга ўғилларми'}, {'chapter': 52, 'verse': 40, 'text': 'أَمۡ تَسۡـَٔلُهُمۡ أَجۡرٗا فَهُم مِّن مَّغۡرَمٖ مُّثۡقَلُونَ\nЁки сен улардан ҳақ сўраяпсан-у, улар тўловдан қийналаяптиларми'}, {'chapter': 52, 'verse': 41, 'text': 'أَمۡ عِندَهُمُ ٱلۡغَيۡبُ فَهُمۡ يَكۡتُبُونَ\nБалки, уларнинг ихтиёрида ғайб (илми) бўлиб, улар ёзиб олаётгандирлар'}, {'chapter': 52, 'verse': 42, 'text': 'أَمۡ يُرِيدُونَ كَيۡدٗاۖ فَٱلَّذِينَ كَفَرُواْ هُمُ ٱلۡمَكِيدُونَ\nБалки, ҳийла кўрсатишни хоҳларлар?! Бас, кофирлар ҳийлаларининг жазосини тортарлар'}, {'chapter': 52, 'verse': 43, 'text': 'أَمۡ لَهُمۡ إِلَٰهٌ غَيۡرُ ٱللَّهِۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يُشۡرِكُونَ\nБалки, уларнинг Аллоҳдан бошқа худолари бордир?! Аллоҳ улар келтираётган ширкдан пок бўлди'}, {'chapter': 52, 'verse': 44, 'text': 'وَإِن يَرَوۡاْ كِسۡفٗا مِّنَ ٱلسَّمَآءِ سَاقِطٗا يَقُولُواْ سَحَابٞ مَّرۡكُومٞ\nАгар улар бошларига тушаётган парчани кўрсалар ҳам, тўпланган булут, дерлар'}, {'chapter': 52, 'verse': 45, 'text': 'فَذَرۡهُمۡ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي فِيهِ يُصۡعَقُونَ\nУларни ҳалокатга учрайдиган кунларига йўлиққунларигача тек қўй'}, {'chapter': 52, 'verse': 46, 'text': 'يَوۡمَ لَا يُغۡنِي عَنۡهُمۡ كَيۡدُهُمۡ شَيۡـٔٗا وَلَا هُمۡ يُنصَرُونَ\nУ кунда ҳийлалари уларга ҳеч қандай фойда бермас ва уларга ёрдам ҳам берилмас'}, {'chapter': 52, 'verse': 47, 'text': 'وَإِنَّ لِلَّذِينَ ظَلَمُواْ عَذَابٗا دُونَ ذَٰلِكَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nВа, албатта, зулм қилганларга ундан ўзга азоб ҳам бор, лекин кўплари билмаслар'}, {'chapter': 52, 'verse': 48, 'text': 'وَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ فَإِنَّكَ بِأَعۡيُنِنَاۖ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ حِينَ تَقُومُ\nБас, Роббинг ҳукмига сабр қил. Албатта, сен кўз ўнгимиздасан, ўрнингдан турганингда Роббингга тасбиҳ айт'}, {'chapter': 52, 'verse': 49, 'text': 'وَمِنَ ٱلَّيۡلِ فَسَبِّحۡهُ وَإِدۡبَٰرَ ٱلنُّجُومِ\nВа кечаси ҳам. Бас, юлдузлар ботгандан кейин ҳам тасбиҳ айт'}], '53': [{'chapter': 53, 'verse': 1, 'text': 'وَٱلنَّجۡمِ إِذَا هَوَىٰ\nБотаётган юлдуз билан қасам'}, {'chapter': 53, 'verse': 2, 'text': 'مَا ضَلَّ صَاحِبُكُمۡ وَمَا غَوَىٰ\nСизнинг Биродарингиз адашгани ҳам йўқ, йўлдан озгани ҳам йўқ'}, {'chapter': 53, 'verse': 3, 'text': 'وَمَا يَنطِقُ عَنِ ٱلۡهَوَىٰٓ\nУ ҳаводан нутқ қилмас'}, {'chapter': 53, 'verse': 4, 'text': 'إِنۡ هُوَ إِلَّا وَحۡيٞ يُوحَىٰ\nУ (Қуръон) ваҳийдан ўзга нарса эмас'}, {'chapter': 53, 'verse': 5, 'text': 'عَلَّمَهُۥ شَدِيدُ ٱلۡقُوَىٰ\nУ(Муҳаммад)га шиддатли қувват эгаси таълим берди'}, {'chapter': 53, 'verse': 6, 'text': 'ذُو مِرَّةٖ فَٱسۡتَوَىٰ\nМиррат эгаси кўтарилди'}, {'chapter': 53, 'verse': 7, 'text': 'وَهُوَ بِٱلۡأُفُقِ ٱلۡأَعۡلَىٰ\nВа у олий уфқда эди'}, {'chapter': 53, 'verse': 8, 'text': 'ثُمَّ دَنَا فَتَدَلَّىٰ\nСўнгра яқинлашди ва жуда ҳам яқин бўлди'}, {'chapter': 53, 'verse': 9, 'text': 'فَكَانَ قَابَ قَوۡسَيۡنِ أَوۡ أَدۡنَىٰ\nБас, икки камон миқдоридай жойда бўлди. Балки ундан ҳам яқинроқ келди'}, {'chapter': 53, 'verse': 10, 'text': 'فَأَوۡحَىٰٓ إِلَىٰ عَبۡدِهِۦ مَآ أَوۡحَىٰ\nВа У зот бандасига нимани ваҳий қилган бўлса, етказди'}, {'chapter': 53, 'verse': 11, 'text': 'مَا كَذَبَ ٱلۡفُؤَادُ مَا رَأَىٰٓ\nҚалб (кўз) кўрганини ёлғонга чиқармади'}, {'chapter': 53, 'verse': 12, 'text': 'أَفَتُمَٰرُونَهُۥ عَلَىٰ مَا يَرَىٰ\nУ билан ўзи кўрган нарса устида тортишасизларми'}, {'chapter': 53, 'verse': 13, 'text': 'وَلَقَدۡ رَءَاهُ نَزۡلَةً أُخۡرَىٰ\nВа, дарҳақиқат, уни бошқа сафар кўрди'}, {'chapter': 53, 'verse': 14, 'text': 'عِندَ سِدۡرَةِ ٱلۡمُنتَهَىٰ\nСидратул мунтаҳа(тугаш дарахти) олдида'}, {'chapter': 53, 'verse': 15, 'text': 'عِندَهَا جَنَّةُ ٱلۡمَأۡوَىٰٓ\nУнинг ҳузурида маъво жаннати бор'}, {'chapter': 53, 'verse': 16, 'text': 'إِذۡ يَغۡشَى ٱلسِّدۡرَةَ مَا يَغۡشَىٰ\nЎшанда, у(дарахт)ни ўрайдиган нарса ўраб турибди'}, {'chapter': 53, 'verse': 17, 'text': 'مَا زَاغَ ٱلۡبَصَرُ وَمَا طَغَىٰ\nНигоҳ бурилмади ҳам, ҳаддидан ошмади ҳам'}, {'chapter': 53, 'verse': 18, 'text': 'لَقَدۡ رَأَىٰ مِنۡ ءَايَٰتِ رَبِّهِ ٱلۡكُبۡرَىٰٓ\nДарҳақиқат, ўз Роббисининг улкан белгиларидан кўрди'}, {'chapter': 53, 'verse': 19, 'text': 'أَفَرَءَيۡتُمُ ٱللَّـٰتَ وَٱلۡعُزَّىٰ\nЛот ва Уззони ўйлаб кўрдингизми'}, {'chapter': 53, 'verse': 20, 'text': 'وَمَنَوٰةَ ٱلثَّالِثَةَ ٱلۡأُخۡرَىٰٓ\nВа бошқаси, учинчиси — Манотни ҳам (ўйлаб кўрдингизми)'}, {'chapter': 53, 'verse': 21, 'text': 'أَلَكُمُ ٱلذَّكَرُ وَلَهُ ٱلۡأُنثَىٰ\nСизларга эркак-у, У зотга аёлми'}, {'chapter': 53, 'verse': 22, 'text': 'تِلۡكَ إِذٗا قِسۡمَةٞ ضِيزَىٰٓ\nУндоқ бўлса, бу нотўғри тақсим-ку'}, {'chapter': 53, 'verse': 23, 'text': 'إِنۡ هِيَ إِلَّآ أَسۡمَآءٞ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَمَا تَهۡوَى ٱلۡأَنفُسُۖ وَلَقَدۡ جَآءَهُم مِّن رَّبِّهِمُ ٱلۡهُدَىٰٓ\nУ(бут)лар сиз ва ота-боболарингиз қўйган номлардан ўзга ҳеч нарса эмас. Аллоҳ бунга ҳеч ҳужжат туширган эмас. У(мушрик)лар, Роббиларидан ҳидоят келганига қарамай, гумон ва ҳавойи нафс хоҳишига эргашарлар, холос'}, {'chapter': 53, 'verse': 24, 'text': 'أَمۡ لِلۡإِنسَٰنِ مَا تَمَنَّىٰ\nЁки ҳар инсон нимани хоҳласа, шу бўлаверадими'}, {'chapter': 53, 'verse': 25, 'text': 'فَلِلَّهِ ٱلۡأٓخِرَةُ وَٱلۡأُولَىٰ\nЗеро, охират ҳам, дунё ҳам Аллоҳникидир'}, {'chapter': 53, 'verse': 26, 'text': '۞وَكَم مِّن مَّلَكٖ فِي ٱلسَّمَٰوَٰتِ لَا تُغۡنِي شَفَٰعَتُهُمۡ شَيۡـًٔا إِلَّا مِنۢ بَعۡدِ أَن يَأۡذَنَ ٱللَّهُ لِمَن يَشَآءُ وَيَرۡضَىٰٓ\nОсмонларда қанчадан-қанча фаришталар мавжуд, уларнинг шафоати ҳеч фойда бермас. Магар, Аллоҳ Ўзи хоҳлаган ва рози бўлганларга изн берса'}, {'chapter': 53, 'verse': 27, 'text': 'إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ لَيُسَمُّونَ ٱلۡمَلَـٰٓئِكَةَ تَسۡمِيَةَ ٱلۡأُنثَىٰ\nАлбатта, охиратга иймон келтирмаганлар фаришталарни аёл номи билан номларлар'}, {'chapter': 53, 'verse': 28, 'text': 'وَمَا لَهُم بِهِۦ مِنۡ عِلۡمٍۖ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّۖ وَإِنَّ ٱلظَّنَّ لَا يُغۡنِي مِنَ ٱلۡحَقِّ شَيۡـٔٗا\nУларда бу ҳақда ҳеч қандай илм йўқ. Гумондан бошқа нарсага эргашмаслар. Албатта, гумон ҳақиқат ўрнига ўтмас'}, {'chapter': 53, 'verse': 29, 'text': 'فَأَعۡرِضۡ عَن مَّن تَوَلَّىٰ عَن ذِكۡرِنَا وَلَمۡ يُرِدۡ إِلَّا ٱلۡحَيَوٰةَ ٱلدُّنۡيَا\nБизнинг зикримизга орқа ўгирган, ҳаёти дунёдан бошқани хоҳламайдиганлардан юз ўгир'}, {'chapter': 53, 'verse': 30, 'text': 'ذَٰلِكَ مَبۡلَغُهُم مِّنَ ٱلۡعِلۡمِۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِمَنِ ٱهۡتَدَىٰ\nУларнинг эришган илмлари мана шу. Албатта, Роббинг Унинг йўлидан адашганларни билгувчидир ва У ҳидоятдагиларни ҳам билгувчидир'}, {'chapter': 53, 'verse': 31, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ لِيَجۡزِيَ ٱلَّذِينَ أَسَـٰٓـُٔواْ بِمَا عَمِلُواْ وَيَجۡزِيَ ٱلَّذِينَ أَحۡسَنُواْ بِٱلۡحُسۡنَى\nОсмонлардагию ердаги барча нарсалар Аллоҳникидир. У ёмонлик қилганларни амалига яраша жазолар ва яхшилик қилганларни гўзал (мукофот) ила мукофотлар'}, {'chapter': 53, 'verse': 32, 'text': 'ٱلَّذِينَ يَجۡتَنِبُونَ كَبَـٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ إِلَّا ٱللَّمَمَۚ إِنَّ رَبَّكَ وَٰسِعُ ٱلۡمَغۡفِرَةِۚ هُوَ أَعۡلَمُ بِكُمۡ إِذۡ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَإِذۡ أَنتُمۡ أَجِنَّةٞ فِي بُطُونِ أُمَّهَٰتِكُمۡۖ فَلَا تُزَكُّوٓاْ أَنفُسَكُمۡۖ هُوَ أَعۡلَمُ بِمَنِ ٱتَّقَىٰٓ\nУлар кичик гуноҳлардан бошқа катта гуноҳлар ва фаҳш ишлардан четда бўладиганлардир. Албатта, Роббинг мағфирати кенг Зотдир. У сизларни тупроқдан пайдо қилганда ҳам, оналарингиз қоринларида ҳомилалик чоғингизни ҳам яхши билувчидир. Шундоқ экан, ўзингизни оқламанг, У ким тақводорлигини яхши биладир'}, {'chapter': 53, 'verse': 33, 'text': 'أَفَرَءَيۡتَ ٱلَّذِي تَوَلَّىٰ\nЮз ўгириб кетганни кўрдингми'}, {'chapter': 53, 'verse': 34, 'text': 'وَأَعۡطَىٰ قَلِيلٗا وَأَكۡدَىٰٓ\nУ озгина берди ва тўхтади'}, {'chapter': 53, 'verse': 35, 'text': 'أَعِندَهُۥ عِلۡمُ ٱلۡغَيۡبِ فَهُوَ يَرَىٰٓ\nУнинг ҳузурида ғайб илми бор-у, у кўриб турибдими'}, {'chapter': 53, 'verse': 36, 'text': 'أَمۡ لَمۡ يُنَبَّأۡ بِمَا فِي صُحُفِ مُوسَىٰ\nЁки Мусонинг саҳифаларидаги нарсалар хабари унга берилмадими'}, {'chapter': 53, 'verse': 37, 'text': 'وَإِبۡرَٰهِيمَ ٱلَّذِي وَفَّىٰٓ\nВа вафо қилган Иброҳимнинг'}, {'chapter': 53, 'verse': 38, 'text': 'أَلَّا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰ\nАлбатта, ҳеч бир гуноҳкор бошқанинг гуноҳини кўтармас'}, {'chapter': 53, 'verse': 39, 'text': 'وَأَن لَّيۡسَ لِلۡإِنسَٰنِ إِلَّا مَا سَعَىٰ\nҲар бир инсонга ўз қилганидан бошқа нарса йўқ'}, {'chapter': 53, 'verse': 40, 'text': 'وَأَنَّ سَعۡيَهُۥ سَوۡفَ يُرَىٰ\nВа, албатта, унинг қилмиши тезда кўринур'}, {'chapter': 53, 'verse': 41, 'text': 'ثُمَّ يُجۡزَىٰهُ ٱلۡجَزَآءَ ٱلۡأَوۡفَىٰ\nСўнгра жазо ёки мукофоти тўлиқ берилур'}, {'chapter': 53, 'verse': 42, 'text': 'وَأَنَّ إِلَىٰ رَبِّكَ ٱلۡمُنتَهَىٰ\nВа, албатта, интиҳо Роббинг ҳузуридадир'}, {'chapter': 53, 'verse': 43, 'text': 'وَأَنَّهُۥ هُوَ أَضۡحَكَ وَأَبۡكَىٰ\nВа, албатта, кулдирган ҳам Ўзи, йиғлатган ҳам Ўзидир'}, {'chapter': 53, 'verse': 44, 'text': 'وَأَنَّهُۥ هُوَ أَمَاتَ وَأَحۡيَا\nАлбатта, ўлдирган ҳам Ўзи, тирилтирган ҳам Ўзи'}, {'chapter': 53, 'verse': 45, 'text': 'وَأَنَّهُۥ خَلَقَ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰ\nАлбатта, икки жуфтни — эркак ва аёлни яратган ҳам Ўзи'}, {'chapter': 53, 'verse': 46, 'text': 'مِن نُّطۡفَةٍ إِذَا تُمۡنَىٰ\nОтилиб чиққан нутфадан'}, {'chapter': 53, 'verse': 47, 'text': 'وَأَنَّ عَلَيۡهِ ٱلنَّشۡأَةَ ٱلۡأُخۡرَىٰ\nВа, албатта, кейинги пайдо қилиш зиммасидаги зот ҳам Ўзи'}, {'chapter': 53, 'verse': 48, 'text': 'وَأَنَّهُۥ هُوَ أَغۡنَىٰ وَأَقۡنَىٰ\nВа, албатта, беҳожат қилган ҳам Ўзи, бадавлат қилган ҳам Ўзи'}, {'chapter': 53, 'verse': 49, 'text': 'وَأَنَّهُۥ هُوَ رَبُّ ٱلشِّعۡرَىٰ\nВа, албатта Шеъронинг роббиси ҳам Унинг Ўзи'}, {'chapter': 53, 'verse': 50, 'text': 'وَأَنَّهُۥٓ أَهۡلَكَ عَادًا ٱلۡأُولَىٰ\nВа, албатта, биринчи Одни ҳалок қилган ҳам Ўзи'}, {'chapter': 53, 'verse': 51, 'text': 'وَثَمُودَاْ فَمَآ أَبۡقَىٰ\nВа Самудни боқий қолдирмаган ҳам Ўзи'}, {'chapter': 53, 'verse': 52, 'text': 'وَقَوۡمَ نُوحٖ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُواْ هُمۡ أَظۡلَمَ وَأَطۡغَىٰ\nВа бундан олдин Нуҳ қавмини (ҳалок қилган ҳам Ўзи). Чунки, улар ўта золим ва ўта туғёнга кетган эдилар'}, {'chapter': 53, 'verse': 53, 'text': 'وَٱلۡمُؤۡتَفِكَةَ أَهۡوَىٰ\nВа тўнтарилганни қулатган (ҳам Ўзи)'}, {'chapter': 53, 'verse': 54, 'text': 'فَغَشَّىٰهَا مَا غَشَّىٰ\nБас, уни ўраган нарса ўраб олди'}, {'chapter': 53, 'verse': 55, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكَ تَتَمَارَىٰ\nШундай экан, Роббингнинг қайси неъматларига шак келтирасан'}, {'chapter': 53, 'verse': 56, 'text': 'هَٰذَا نَذِيرٞ مِّنَ ٱلنُّذُرِ ٱلۡأُولَىٰٓ\nБу, аввалги огоҳлантирувчилар каби бир огоҳлантирувчидир'}, {'chapter': 53, 'verse': 57, 'text': 'أَزِفَتِ ٱلۡأٓزِفَةُ\nЯқинлашувчи яқинлашди'}, {'chapter': 53, 'verse': 58, 'text': 'لَيۡسَ لَهَا مِن دُونِ ٱللَّهِ كَاشِفَةٌ\nУни Аллоҳдан бошқа кашф қилувчи йўқ'}, {'chapter': 53, 'verse': 59, 'text': 'أَفَمِنۡ هَٰذَا ٱلۡحَدِيثِ تَعۡجَبُونَ\nБу сўздан сизлар ажабланасизларми'}, {'chapter': 53, 'verse': 60, 'text': 'وَتَضۡحَكُونَ وَلَا تَبۡكُونَ\nВа куласизлар-у, йиғламайсизларми'}, {'chapter': 53, 'verse': 61, 'text': 'وَأَنتُمۡ سَٰمِدُونَ\nВа ҳолбуки, сизлар бепарвосизлар'}, {'chapter': 53, 'verse': 62, 'text': 'فَٱسۡجُدُواْۤ لِلَّهِۤ وَٱعۡبُدُواْ۩\nБас, Аллоҳга сажда қилинглар ва ибодат қилинглар'}], '54': [{'chapter': 54, 'verse': 1, 'text': 'ٱقۡتَرَبَتِ ٱلسَّاعَةُ وَٱنشَقَّ ٱلۡقَمَرُ\nСоат(қиёмат) яқинлашди ва ой бўлинди'}, {'chapter': 54, 'verse': 2, 'text': 'وَإِن يَرَوۡاْ ءَايَةٗ يُعۡرِضُواْ وَيَقُولُواْ سِحۡرٞ مُّسۡتَمِرّٞ\nАгар бир оят(мўъжиза)ни кўрсалар, юз ўгирарлар ва: «Бу — ўткинчи сеҳр», дерлар'}, {'chapter': 54, 'verse': 3, 'text': 'وَكَذَّبُواْ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡۚ وَكُلُّ أَمۡرٖ مُّسۡتَقِرّٞ\nВа ёлғонга чиқардилар ҳамда ҳавойи нафсларига эргашдилар. Ҳолбуки, ҳар бир иш ўз ўрнини топувчидир'}, {'chapter': 54, 'verse': 4, 'text': 'وَلَقَدۡ جَآءَهُم مِّنَ ٱلۡأَنۢبَآءِ مَا فِيهِ مُزۡدَجَرٌ\nВа, дарҳақиқат, уларга ҳайиқтирадиган хабарлардан келган эди'}, {'chapter': 54, 'verse': 5, 'text': 'حِكۡمَةُۢ بَٰلِغَةٞۖ فَمَا تُغۡنِ ٱلنُّذُرُ\nУларда етук ҳикмат (бор эди). Бироқ, огоҳлантирувчилар фойда бермади'}, {'chapter': 54, 'verse': 6, 'text': 'فَتَوَلَّ عَنۡهُمۡۘ يَوۡمَ يَدۡعُ ٱلدَّاعِ إِلَىٰ شَيۡءٖ نُّكُرٍ\nБас, Улардан юз ўгир! Чақирувчи мункар нарсага чақирган кунда'}, {'chapter': 54, 'verse': 7, 'text': 'خُشَّعًا أَبۡصَٰرُهُمۡ يَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ كَأَنَّهُمۡ جَرَادٞ مُّنتَشِرٞ\nҚабрлардан, кўзлари қўрқинчга тўлган ҳолда, худди ёйилган чигирткага ўхшаб чиқиб келурлар'}, {'chapter': 54, 'verse': 8, 'text': 'مُّهۡطِعِينَ إِلَى ٱلدَّاعِۖ يَقُولُ ٱلۡكَٰفِرُونَ هَٰذَا يَوۡمٌ عَسِرٞ\nЧақирувчига қараб бўйинларини чўзиб, шошилиб борурлар. Кофирлар: «Бу қийин кун-ку», дерлар'}, {'chapter': 54, 'verse': 9, 'text': '۞كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ فَكَذَّبُواْ عَبۡدَنَا وَقَالُواْ مَجۡنُونٞ وَٱزۡدُجِرَ\nБулардан олдин Нуҳ қавми ҳам ёлғончи қилган эди. Бас, бандамизни ёлғончига чиқаришди ва, мажнун, дейишди ҳамда унга тўсиқлар қўйишди'}, {'chapter': 54, 'verse': 10, 'text': 'فَدَعَا رَبَّهُۥٓ أَنِّي مَغۡلُوبٞ فَٱنتَصِرۡ\nВа Роббисига дуо қилиб: «Албатта, мен мағлуб бўлдим, ёрдам бер», деди'}, {'chapter': 54, 'verse': 11, 'text': 'فَفَتَحۡنَآ أَبۡوَٰبَ ٱلسَّمَآءِ بِمَآءٖ مُّنۡهَمِرٖ\nБас, осмон эшикларини шаррос сув билан очиб юбордик'}, {'chapter': 54, 'verse': 12, 'text': 'وَفَجَّرۡنَا ٱلۡأَرۡضَ عُيُونٗا فَٱلۡتَقَى ٱلۡمَآءُ عَلَىٰٓ أَمۡرٖ قَدۡ قُدِرَ\nВа ердан отилтириб булоқлар чиқардик. Бас, сувлар тақдир қилинган иш учун бирлашдилар'}, {'chapter': 54, 'verse': 13, 'text': 'وَحَمَلۡنَٰهُ عَلَىٰ ذَاتِ أَلۡوَٰحٖ وَدُسُرٖ\nВа у(Нуҳ)ни тахталари ва михлари бор нарса устига кўтардик'}, {'chapter': 54, 'verse': 14, 'text': 'تَجۡرِي بِأَعۡيُنِنَا جَزَآءٗ لِّمَن كَانَ كُفِرَ\nУ(Кема) инкор қилинган шахс(Нуҳ)га мукофот бўлиб, Бизнинг иноятимиз ила юрадир'}, {'chapter': 54, 'verse': 15, 'text': 'وَلَقَد تَّرَكۡنَٰهَآ ءَايَةٗ فَهَلۡ مِن مُّدَّكِرٖ\nВа у(ҳодиса)ни белги қилиб қолдирдик. Қани, ибрат олувчи борми'}, {'chapter': 54, 'verse': 16, 'text': 'فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nБас, Менинг азобим ва огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 17, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nДарҳақиқат, Биз Қуръонни зикр учун осон қилдик. Бас, зикр қилувчи борми'}, {'chapter': 54, 'verse': 18, 'text': 'كَذَّبَتۡ عَادٞ فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nОд (Ҳудни) ёлғончига чиқарди. Бас, Менинг азобим ва огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 19, 'text': 'إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِي يَوۡمِ نَحۡسٖ مُّسۡتَمِرّٖ\nБиз давомли машъум кунда улар устига сорсор шамолини юбордик'}, {'chapter': 54, 'verse': 20, 'text': 'تَنزِعُ ٱلنَّاسَ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٖ مُّنقَعِرٖ\nУ, одамларни, худди хурмо дарахтини томири билан қўпориб юборгандек, (ердан) узиб-учириб ташларди'}, {'chapter': 54, 'verse': 21, 'text': 'فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nБас, Менинг азобим ва огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 22, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nДарҳақиқат, Биз Қуръонни зикр учун осон қилдик. Бас, зикр қилувчи борми'}, {'chapter': 54, 'verse': 23, 'text': 'كَذَّبَتۡ ثَمُودُ بِٱلنُّذُرِ\nСамуд огоҳлантирувчиларни ёлғонга чиқарди'}, {'chapter': 54, 'verse': 24, 'text': 'فَقَالُوٓاْ أَبَشَرٗا مِّنَّا وَٰحِدٗا نَّتَّبِعُهُۥٓ إِنَّآ إِذٗا لَّفِي ضَلَٰلٖ وَسُعُرٍ\nУлар: «Биз ўзимиздан бўлган бир одамга эргашамизми? Агар шундай бўлса, албатта, адашувда ва жинниликда бўламиз'}, {'chapter': 54, 'verse': 25, 'text': 'أَءُلۡقِيَ ٱلذِّكۡرُ عَلَيۡهِ مِنۢ بَيۡنِنَا بَلۡ هُوَ كَذَّابٌ أَشِرٞ\nБизларнинг орамиздан унга зикр (ваҳий) туширилибдими?! Йўқ, у ёлғончи ва кеккайгандир», дедилар'}, {'chapter': 54, 'verse': 26, 'text': 'سَيَعۡلَمُونَ غَدٗا مَّنِ ٱلۡكَذَّابُ ٱلۡأَشِرُ\nЭртага ким ёлғончи ва кеккайган эканини билурлар'}, {'chapter': 54, 'verse': 27, 'text': 'إِنَّا مُرۡسِلُواْ ٱلنَّاقَةِ فِتۡنَةٗ لَّهُمۡ فَٱرۡتَقِبۡهُمۡ وَٱصۡطَبِرۡ\nБиз уларни синаш учун туяни юборувчимиз. Уларни кузатиб тур ва сабр қил'}, {'chapter': 54, 'verse': 28, 'text': 'وَنَبِّئۡهُمۡ أَنَّ ٱلۡمَآءَ قِسۡمَةُۢ بَيۡنَهُمۡۖ كُلُّ شِرۡبٖ مُّحۡتَضَرٞ\nВа уларга, албатта, сув ораларида тақсимланганининг хабарини бер. Ҳар бир ичишга (ўз вақтида) ҳозир бўлинар'}, {'chapter': 54, 'verse': 29, 'text': 'فَنَادَوۡاْ صَاحِبَهُمۡ فَتَعَاطَىٰ فَعَقَرَ\nБас, улар ўз шерикларини чақирдилар. У (туяни) тутди ва сўйиб юборди'}, {'chapter': 54, 'verse': 30, 'text': 'فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nБас, Менинг азобим ва огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 31, 'text': 'إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ صَيۡحَةٗ وَٰحِدَةٗ فَكَانُواْ كَهَشِيمِ ٱلۡمُحۡتَظِرِ\nАлбатта, Биз уларга бир овоз юбордик. Ва улар қўра қурувчи (йиққан) қуруқ шох-шаббаларга ўхшаб қолдилар'}, {'chapter': 54, 'verse': 32, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nДарҳақиқат, Биз Қуръонни зикр учун осон қилдик. Бас, зикр қилувчи борми'}, {'chapter': 54, 'verse': 33, 'text': 'كَذَّبَتۡ قَوۡمُ لُوطِۭ بِٱلنُّذُرِ\nЛут қавми огоҳлантирувчиларни ёлғончига чиқарди'}, {'chapter': 54, 'verse': 34, 'text': 'إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ حَاصِبًا إِلَّآ ءَالَ لُوطٖۖ نَّجَّيۡنَٰهُم بِسَحَرٖ\nАлбатта, Биз уларнинг устига (тош) бўрон юбордик. Фақат Лут хонадонига саҳар чоғи нажот бердик'}, {'chapter': 54, 'verse': 35, 'text': 'نِّعۡمَةٗ مِّنۡ عِندِنَاۚ كَذَٰلِكَ نَجۡزِي مَن شَكَرَ\nБу, Биз тарафимиздан неъмат бўлди. Кимки шукр қилса, худди шундай мукофотлармиз'}, {'chapter': 54, 'verse': 36, 'text': 'وَلَقَدۡ أَنذَرَهُم بَطۡشَتَنَا فَتَمَارَوۡاْ بِٱلنُّذُرِ\nДарҳақиқат, у(Лут) уларни Бизнинг тутишимиздан огоҳлантирган эди. Бас, улар огоҳлантиришларга ишонмадилар'}, {'chapter': 54, 'verse': 37, 'text': 'وَلَقَدۡ رَٰوَدُوهُ عَن ضَيۡفِهِۦ فَطَمَسۡنَآ أَعۡيُنَهُمۡ فَذُوقُواْ عَذَابِي وَنُذُرِ\nУлар ундан меҳмонларини топширишни сўрадилар. Бас, кўзларини кўр қилдик. Менинг азобим ва огоҳлантиришларимни тотиб кўрсинлар'}, {'chapter': 54, 'verse': 38, 'text': 'وَلَقَدۡ صَبَّحَهُم بُكۡرَةً عَذَابٞ مُّسۡتَقِرّٞ\nБатаҳқиқ, эрта тонгда уларни собит, давомли азоб тутди'}, {'chapter': 54, 'verse': 39, 'text': 'فَذُوقُواْ عَذَابِي وَنُذُرِ\nБас, Менинг азобларимни ва огоҳлантиришларимни тотиб кўринглар'}, {'chapter': 54, 'verse': 40, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nДарҳақиқат, Биз Қуръонни зикр учун осон қилдик. Бас, зикр қилувчи борми'}, {'chapter': 54, 'verse': 41, 'text': 'وَلَقَدۡ جَآءَ ءَالَ فِرۡعَوۡنَ ٱلنُّذُرُ\nДарҳақиқат, Фиръавн аҳлига огоҳлантиришлар келди'}, {'chapter': 54, 'verse': 42, 'text': 'كَذَّبُواْ بِـَٔايَٰتِنَا كُلِّهَا فَأَخَذۡنَٰهُمۡ أَخۡذَ عَزِيزٖ مُّقۡتَدِرٍ\nБизнинг оятларимизнинг барчасини ёлғонга чиқаришди. Бас, Биз уларни ғолиб, қудратли олувчининг олиши билан олдик'}, {'chapter': 54, 'verse': 43, 'text': 'أَكُفَّارُكُمۡ خَيۡرٞ مِّنۡ أُوْلَـٰٓئِكُمۡ أَمۡ لَكُم بَرَآءَةٞ فِي ٱلزُّبُرِ\nСизнинг кофирларингиз аввалгиларингиздан яхшироқми?! Ёки китобларда сизларга оқлов борми'}, {'chapter': 54, 'verse': 44, 'text': 'أَمۡ يَقُولُونَ نَحۡنُ جَمِيعٞ مُّنتَصِرٞ\nЁки, Биз нусратга эришувчи жамоатмиз, дейишар'}, {'chapter': 54, 'verse': 45, 'text': 'سَيُهۡزَمُ ٱلۡجَمۡعُ وَيُوَلُّونَ ٱلدُّبُرَ\nУ жамоа тезда енгилар ва орқаларига қараб қочарлар'}, {'chapter': 54, 'verse': 46, 'text': 'بَلِ ٱلسَّاعَةُ مَوۡعِدُهُمۡ وَٱلسَّاعَةُ أَدۡهَىٰ وَأَمَرُّ\nҲа, уларга ваъда қилинган вақт, соат(қиёмат)дир. Ва у соат (азоби) улканроқ ва аччиқроқдир'}, {'chapter': 54, 'verse': 47, 'text': 'إِنَّ ٱلۡمُجۡرِمِينَ فِي ضَلَٰلٖ وَسُعُرٖ\nАлбатта, жинояткорлар адашувда ва олов-оташдадирлар'}, {'chapter': 54, 'verse': 48, 'text': 'يَوۡمَ يُسۡحَبُونَ فِي ٱلنَّارِ عَلَىٰ وُجُوهِهِمۡ ذُوقُواْ مَسَّ سَقَرَ\nЮзтубан дўзахга судраладиган кунда, жаҳаннам азобини татиб кўринг, (дейилур)'}, {'chapter': 54, 'verse': 49, 'text': 'إِنَّا كُلَّ شَيۡءٍ خَلَقۡنَٰهُ بِقَدَرٖ\nАлбатта, Биз ҳар нарсани ўлчов билан яратдик'}, {'chapter': 54, 'verse': 50, 'text': 'وَمَآ أَمۡرُنَآ إِلَّا وَٰحِدَةٞ كَلَمۡحِۭ بِٱلۡبَصَرِ\nБизнинг фармонимиз бир сўздан ўзга эмас. Кўз юмиб очгунча бўлур'}, {'chapter': 54, 'verse': 51, 'text': 'وَلَقَدۡ أَهۡلَكۡنَآ أَشۡيَاعَكُمۡ فَهَلۡ مِن مُّدَّكِرٖ\nДарҳақиқат, Биз сизга ўхшаганларни ҳалок қилдик. Бас, буни эсловчилардан борми, ўзи'}, {'chapter': 54, 'verse': 52, 'text': 'وَكُلُّ شَيۡءٖ فَعَلُوهُ فِي ٱلزُّبُرِ\nВа уларнинг ҳар бир ишлари китоблардадир'}, {'chapter': 54, 'verse': 53, 'text': 'وَكُلُّ صَغِيرٖ وَكَبِيرٖ مُّسۡتَطَرٌ\nҲар бир кичигу катта (ишлар) сатрлангандир'}, {'chapter': 54, 'verse': 54, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَنَهَرٖ\nАлбатта, тақводорлар жаннату анҳорлардадирлар'}, {'chapter': 54, 'verse': 55, 'text': 'فِي مَقۡعَدِ صِدۡقٍ عِندَ مَلِيكٖ مُّقۡتَدِرِۭ\nСидқ ўриндиқда, қудратли Подшоҳ ҳузуридадир'}], '55': [{'chapter': 55, 'verse': 1, 'text': 'ٱلرَّحۡمَٰنُ\nРоҳман'}, {'chapter': 55, 'verse': 2, 'text': 'عَلَّمَ ٱلۡقُرۡءَانَ\nҚуръонни ўргатди'}, {'chapter': 55, 'verse': 3, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ\nИнсонни яратди'}, {'chapter': 55, 'verse': 4, 'text': 'عَلَّمَهُ ٱلۡبَيَانَ\nУнга баённи ўргатди'}, {'chapter': 55, 'verse': 5, 'text': 'ٱلشَّمۡسُ وَٱلۡقَمَرُ بِحُسۡبَانٖ\nҚуёш ва ой ҳисобдадир'}, {'chapter': 55, 'verse': 6, 'text': 'وَٱلنَّجۡمُ وَٱلشَّجَرُ يَسۡجُدَانِ\nЎт-ўлан ҳам, дарахтлар ҳам сажда қилурлар'}, {'chapter': 55, 'verse': 7, 'text': 'وَٱلسَّمَآءَ رَفَعَهَا وَوَضَعَ ٱلۡمِيزَانَ\nОсмонни баланд кўтарди ва (адолат) тарозуни ўрнатди'}, {'chapter': 55, 'verse': 8, 'text': 'أَلَّا تَطۡغَوۡاْ فِي ٱلۡمِيزَانِ\nМезонда ҳаддингиздан ошмаслигингиз учун'}, {'chapter': 55, 'verse': 9, 'text': 'وَأَقِيمُواْ ٱلۡوَزۡنَ بِٱلۡقِسۡطِ وَلَا تُخۡسِرُواْ ٱلۡمِيزَانَ\nАдолат ила ўлчанг ва тарозудан уриб қолманг'}, {'chapter': 55, 'verse': 10, 'text': 'وَٱلۡأَرۡضَ وَضَعَهَا لِلۡأَنَامِ\nВа ерни халойиқ учун қўйди'}, {'chapter': 55, 'verse': 11, 'text': 'فِيهَا فَٰكِهَةٞ وَٱلنَّخۡلُ ذَاتُ ٱلۡأَكۡمَامِ\nУ(ер)да мевалар ва гулкосали хурмолар бор'}, {'chapter': 55, 'verse': 12, 'text': 'وَٱلۡحَبُّ ذُو ٱلۡعَصۡفِ وَٱلرَّيۡحَانُ\nВа сомонли донлар ҳамда райҳонлар бор'}, {'chapter': 55, 'verse': 13, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 14, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ مِن صَلۡصَٰلٖ كَٱلۡفَخَّارِ\nИнсонни сополга ўхшаш қуриган лойдан яратди'}, {'chapter': 55, 'verse': 15, 'text': 'وَخَلَقَ ٱلۡجَآنَّ مِن مَّارِجٖ مِّن نَّارٖ\nВа жинларни ўт-алангадан яратди'}, {'chapter': 55, 'verse': 16, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 17, 'text': 'رَبُّ ٱلۡمَشۡرِقَيۡنِ وَرَبُّ ٱلۡمَغۡرِبَيۡنِ\nУ икки машриқ ва икки мағрибнинг Роббидир'}, {'chapter': 55, 'verse': 18, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 19, 'text': 'مَرَجَ ٱلۡبَحۡرَيۡنِ يَلۡتَقِيَانِ\nДарёю денгизларни бир-бири билан учрашадиган қилиб қўйди'}, {'chapter': 55, 'verse': 20, 'text': 'بَيۡنَهُمَا بَرۡزَخٞ لَّا يَبۡغِيَانِ\nЎрталаридаги тўсиқдан ошиб ўтолмаслар'}, {'chapter': 55, 'verse': 21, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 22, 'text': 'يَخۡرُجُ مِنۡهُمَا ٱللُّؤۡلُؤُ وَٱلۡمَرۡجَانُ\nУ иккисидан луълуъ ва маржон чиқар'}, {'chapter': 55, 'verse': 23, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларрини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 24, 'text': 'وَلَهُ ٱلۡجَوَارِ ٱلۡمُنشَـَٔاتُ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ\nДенгизларда тоғдек бўлиб сузиб юрувчилар ҳам Уникидир'}, {'chapter': 55, 'verse': 25, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 26, 'text': 'كُلُّ مَنۡ عَلَيۡهَا فَانٖ\n(Ер) юзидаги барча жонзот фонийдир'}, {'chapter': 55, 'verse': 27, 'text': 'وَيَبۡقَىٰ وَجۡهُ رَبِّكَ ذُو ٱلۡجَلَٰلِ وَٱلۡإِكۡرَامِ\nУлуғлик ва икром эгаси Роббингнинг Ўзигина боқийдир'}, {'chapter': 55, 'verse': 28, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 29, 'text': 'يَسۡـَٔلُهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ كُلَّ يَوۡمٍ هُوَ فِي شَأۡنٖ\nУндан еру осмондаги жонзотлар сўрарлар. У ҳар куни ишдадир'}, {'chapter': 55, 'verse': 30, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 31, 'text': 'سَنَفۡرُغُ لَكُمۡ أَيُّهَ ٱلثَّقَلَانِ\nАлбатта, сизлар учун вақт топамиз, эй, инсу жинлар'}, {'chapter': 55, 'verse': 32, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 33, 'text': 'يَٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ إِنِ ٱسۡتَطَعۡتُمۡ أَن تَنفُذُواْ مِنۡ أَقۡطَارِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ فَٱنفُذُواْۚ لَا تَنفُذُونَ إِلَّا بِسُلۡطَٰنٖ\nЭй, жин ва инс жамоалари, агар сиз осмонлару ер чегараларидан чиқиб кетишга қодир бўлсаларингиз, чиқаверинглар. Фақат, султон ила чиқа олурсиз, холос'}, {'chapter': 55, 'verse': 34, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 35, 'text': 'يُرۡسَلُ عَلَيۡكُمَا شُوَاظٞ مِّن نَّارٖ وَنُحَاسٞ فَلَا تَنتَصِرَانِ\nУстингиздан олов ва тутун юборилур. Бас, қутула олмассиз'}, {'chapter': 55, 'verse': 36, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 37, 'text': 'فَإِذَا ٱنشَقَّتِ ٱلسَّمَآءُ فَكَانَتۡ وَرۡدَةٗ كَٱلدِّهَانِ\nОсмон ёрилиб, қирмизи мойга айланган вақтда'}, {'chapter': 55, 'verse': 38, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 39, 'text': 'فَيَوۡمَئِذٖ لَّا يُسۡـَٔلُ عَن ذَنۢبِهِۦٓ إِنسٞ وَلَا جَآنّٞ\nБас, у кунда инс ҳам, жин ҳам гуноҳидан сўралмас'}, {'chapter': 55, 'verse': 40, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 41, 'text': 'يُعۡرَفُ ٱلۡمُجۡرِمُونَ بِسِيمَٰهُمۡ فَيُؤۡخَذُ بِٱلنَّوَٰصِي وَٱلۡأَقۡدَامِ\nЖинояткорлар сиймоларидан билиниб турар. Ва уларнинг пешона сочлари ва оёқларидан тутилур'}, {'chapter': 55, 'verse': 42, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 43, 'text': 'هَٰذِهِۦ جَهَنَّمُ ٱلَّتِي يُكَذِّبُ بِهَا ٱلۡمُجۡرِمُونَ\nЖинояткорлар ёлғонга чиқараётган жаҳаннам шу бўладир'}, {'chapter': 55, 'verse': 44, 'text': 'يَطُوفُونَ بَيۡنَهَا وَبَيۡنَ حَمِيمٍ ءَانٖ\nУлар жаҳаннам билан ўта қайноқ сув ўртасида айланурлар'}, {'chapter': 55, 'verse': 45, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 46, 'text': 'وَلِمَنۡ خَافَ مَقَامَ رَبِّهِۦ جَنَّتَانِ\nРоббиси ҳузурида туришдан қўрққанлар учун икки жаннат бор'}, {'chapter': 55, 'verse': 47, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 48, 'text': 'ذَوَاتَآ أَفۡنَانٖ\nУ(жаннат)лар шох-новдалидирлар'}, {'chapter': 55, 'verse': 49, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 50, 'text': 'فِيهِمَا عَيۡنَانِ تَجۡرِيَانِ\nИкковларида икки оқар булоқ бор'}, {'chapter': 55, 'verse': 51, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 52, 'text': 'فِيهِمَا مِن كُلِّ فَٰكِهَةٖ زَوۡجَانِ\nИкковларида ҳам мевадан жуфт навлар бор'}, {'chapter': 55, 'verse': 53, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 54, 'text': 'مُتَّكِـِٔينَ عَلَىٰ فُرُشِۭ بَطَآئِنُهَا مِنۡ إِسۡتَبۡرَقٖۚ وَجَنَى ٱلۡجَنَّتَيۡنِ دَانٖ\nИпак астарли тўшакларга ёнбошлаган ва икки жаннат мевалари яқин бўлган ҳолларида'}, {'chapter': 55, 'verse': 55, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 56, 'text': 'فِيهِنَّ قَٰصِرَٰتُ ٱلطَّرۡفِ لَمۡ يَطۡمِثۡهُنَّ إِنسٞ قَبۡلَهُمۡ وَلَا جَآنّٞ\nУларда кўзлари тийилган, аввал инс ҳам, жин ҳам тегмаган(ҳур)лар бор'}, {'chapter': 55, 'verse': 57, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 58, 'text': 'كَأَنَّهُنَّ ٱلۡيَاقُوتُ وَٱلۡمَرۡجَانُ\nУ(ҳур)лар худди ёқут ва маржонга ўхшарлар'}, {'chapter': 55, 'verse': 59, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 60, 'text': 'هَلۡ جَزَآءُ ٱلۡإِحۡسَٰنِ إِلَّا ٱلۡإِحۡسَٰنُ\nЭҳсоннинг мукофоти фақат эҳсондир'}, {'chapter': 55, 'verse': 61, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 62, 'text': 'وَمِن دُونِهِمَا جَنَّتَانِ\nУ икковидан пастроқда яна икки жаннат бор'}, {'chapter': 55, 'verse': 63, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 64, 'text': 'مُدۡهَآمَّتَانِ\nИкковлари ям-яшилдир'}, {'chapter': 55, 'verse': 65, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 66, 'text': 'فِيهِمَا عَيۡنَانِ نَضَّاخَتَانِ\nИкковларида отилиб турувчи икки булоқ бордир'}, {'chapter': 55, 'verse': 67, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 68, 'text': 'فِيهِمَا فَٰكِهَةٞ وَنَخۡلٞ وَرُمَّانٞ\nИкковларида мевалар, хурмо ва анорлар бордир'}, {'chapter': 55, 'verse': 69, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 70, 'text': 'فِيهِنَّ خَيۡرَٰتٌ حِسَانٞ\nУларда яхши гўзаллар бор'}, {'chapter': 55, 'verse': 71, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 72, 'text': 'حُورٞ مَّقۡصُورَٰتٞ فِي ٱلۡخِيَامِ\nЧодирларни лозим тутган ҳурлар бор'}, {'chapter': 55, 'verse': 73, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 74, 'text': 'لَمۡ يَطۡمِثۡهُنَّ إِنسٞ قَبۡلَهُمۡ وَلَا جَآنّٞ\nАввал уларга инс ҳам, жин ҳам тегмаган'}, {'chapter': 55, 'verse': 75, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 76, 'text': 'مُتَّكِـِٔينَ عَلَىٰ رَفۡرَفٍ خُضۡرٖ وَعَبۡقَرِيٍّ حِسَانٖ\nЯшил болишларга ва гўзал гиламларда ёнбошлаган ҳолларидадир'}, {'chapter': 55, 'verse': 77, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, Роббингизнинг қайси неъматларини ёлғон дея олурсиз'}, {'chapter': 55, 'verse': 78, 'text': 'تَبَٰرَكَ ٱسۡمُ رَبِّكَ ذِي ٱلۡجَلَٰلِ وَٱلۡإِكۡرَامِ\nУлуғлик ва икром эгаси бўлмиш Роббингизнинг исми муборак бўлди'}], '56': [{'chapter': 56, 'verse': 1, 'text': 'إِذَا وَقَعَتِ ٱلۡوَاقِعَةُ\nВоқиъа воқеъ бўлганда'}, {'chapter': 56, 'verse': 2, 'text': 'لَيۡسَ لِوَقۡعَتِهَا كَاذِبَةٌ\nУнинг воқеъ бўлишини ёлғонга чиқарувчи (жон) йўқдир'}, {'chapter': 56, 'verse': 3, 'text': 'خَافِضَةٞ رَّافِعَةٌ\nУ, ҳам пастлатувчи, ҳам кўтарувчидир'}, {'chapter': 56, 'verse': 4, 'text': 'إِذَا رُجَّتِ ٱلۡأَرۡضُ رَجّٗا\nҚачонки, ер қаттиқ ларзага келтирилганда'}, {'chapter': 56, 'verse': 5, 'text': 'وَبُسَّتِ ٱلۡجِبَالُ بَسّٗا\nВа тоғлар майдаланиб титилганда'}, {'chapter': 56, 'verse': 6, 'text': 'فَكَانَتۡ هَبَآءٗ مُّنۢبَثّٗا\nБас, тарқалган чанг-тўзонга айланганда'}, {'chapter': 56, 'verse': 7, 'text': 'وَكُنتُمۡ أَزۡوَٰجٗا ثَلَٰثَةٗ\nВа сиз уч тоифа бўлганингизда'}, {'chapter': 56, 'verse': 8, 'text': 'فَأَصۡحَٰبُ ٱلۡمَيۡمَنَةِ مَآ أَصۡحَٰبُ ٱلۡمَيۡمَنَةِ\nЎнг тараф эгалари. Ўнг тараф эгалари не(саодатлилар)дир'}, {'chapter': 56, 'verse': 9, 'text': 'وَأَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ مَآ أَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ\nВа чап тараф эгалари. Чап тараф эгалари не(бадбахтлар)дир'}, {'chapter': 56, 'verse': 10, 'text': 'وَٱلسَّـٰبِقُونَ ٱلسَّـٰبِقُونَ\nВа пешқадамлар, пешқадамлар'}, {'chapter': 56, 'verse': 11, 'text': 'أُوْلَـٰٓئِكَ ٱلۡمُقَرَّبُونَ\nУлар (Аллоҳга) яқин бўлганлар'}, {'chapter': 56, 'verse': 12, 'text': 'فِي جَنَّـٰتِ ٱلنَّعِيمِ\nСернеъмат жаннатлардадирлар'}, {'chapter': 56, 'verse': 13, 'text': 'ثُلَّةٞ مِّنَ ٱلۡأَوَّلِينَ\nАввалгилардан кўпчилик'}, {'chapter': 56, 'verse': 14, 'text': 'وَقَلِيلٞ مِّنَ ٱلۡأٓخِرِينَ\nВа охиргилардан озчилик'}, {'chapter': 56, 'verse': 15, 'text': 'عَلَىٰ سُرُرٖ مَّوۡضُونَةٖ\nТўқима сўрилардадирлар'}, {'chapter': 56, 'verse': 16, 'text': 'مُّتَّكِـِٔينَ عَلَيۡهَا مُتَقَٰبِلِينَ\nБир-бирларига рўбарў бўлиб ёнбошлаган ҳолдадирлар'}, {'chapter': 56, 'verse': 17, 'text': 'يَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ\nАтрофларида мангу ёш йигитлар айланиб юрурлар'}, {'chapter': 56, 'verse': 18, 'text': 'بِأَكۡوَابٖ وَأَبَارِيقَ وَكَأۡسٖ مِّن مَّعِينٖ\nОқар чашма майи тўлатилган қадаҳлар, кўзалар ва косалар билан'}, {'chapter': 56, 'verse': 19, 'text': 'لَّا يُصَدَّعُونَ عَنۡهَا وَلَا يُنزِفُونَ\nУндан бош оғриғи ҳам, маст ҳам бўлмаслар'}, {'chapter': 56, 'verse': 20, 'text': 'وَفَٰكِهَةٖ مِّمَّا يَتَخَيَّرُونَ\nВа хоҳлаган меваларини олиб'}, {'chapter': 56, 'verse': 21, 'text': 'وَلَحۡمِ طَيۡرٖ مِّمَّا يَشۡتَهُونَ\nИштаҳалари тусаган қуш гўштини олиб (айланиб юрарлар)'}, {'chapter': 56, 'verse': 22, 'text': 'وَحُورٌ عِينٞ\nЯна, кўзи гўзал ҳурлар бор'}, {'chapter': 56, 'verse': 23, 'text': 'كَأَمۡثَٰلِ ٱللُّؤۡلُوِٕ ٱلۡمَكۡنُونِ\nМисоли яширилган дур каби'}, {'chapter': 56, 'verse': 24, 'text': 'جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ\nБулар — қилиб юрган амалларнинг мукофотидур'}, {'chapter': 56, 'verse': 25, 'text': 'لَا يَسۡمَعُونَ فِيهَا لَغۡوٗا وَلَا تَأۡثِيمًا\nУлар у(Жаннат)да беҳуда, гуноҳ(сўз)ларни эшитмаслар'}, {'chapter': 56, 'verse': 26, 'text': 'إِلَّا قِيلٗا سَلَٰمٗا سَلَٰمٗا\nМагар «Салом, салом» дейилганини (эшитарлар)'}, {'chapter': 56, 'verse': 27, 'text': 'وَأَصۡحَٰبُ ٱلۡيَمِينِ مَآ أَصۡحَٰبُ ٱلۡيَمِينِ\nЎнг тараф эгалари. Ўнг тараф эгалари недир'}, {'chapter': 56, 'verse': 28, 'text': 'فِي سِدۡرٖ مَّخۡضُودٖ\nУлар тиконсиз сидрзорлардадирлар'}, {'chapter': 56, 'verse': 29, 'text': 'وَطَلۡحٖ مَّنضُودٖ\nСермева бананзорлардадирлар'}, {'chapter': 56, 'verse': 30, 'text': 'وَظِلّٖ مَّمۡدُودٖ\nВа ёйилган соялардадирлар'}, {'chapter': 56, 'verse': 31, 'text': 'وَمَآءٖ مَّسۡكُوبٖ\nОқар сувлардадирлар'}, {'chapter': 56, 'verse': 32, 'text': 'وَفَٰكِهَةٖ كَثِيرَةٖ\nСероб мевалардадирлар'}, {'chapter': 56, 'verse': 33, 'text': 'لَّا مَقۡطُوعَةٖ وَلَا مَمۡنُوعَةٖ\nТугамайдиган ва ман қилинмайдиган'}, {'chapter': 56, 'verse': 34, 'text': 'وَفُرُشٖ مَّرۡفُوعَةٍ\nКўтарилган кўрпачалардадирлар'}, {'chapter': 56, 'verse': 35, 'text': 'إِنَّآ أَنشَأۡنَٰهُنَّ إِنشَآءٗ\nАлбатта, Биз у(ҳур)ларни дафъатан, хос қилиб, яратдик'}, {'chapter': 56, 'verse': 36, 'text': 'فَجَعَلۡنَٰهُنَّ أَبۡكَارًا\nБас, уларни бокира қизлар қилиб қўйдик'}, {'chapter': 56, 'verse': 37, 'text': 'عُرُبًا أَتۡرَابٗا\nБир хилдаги тенгдош маҳбубалар'}, {'chapter': 56, 'verse': 38, 'text': 'لِّأَصۡحَٰبِ ٱلۡيَمِينِ\nЎнг тараф эгалари учун'}, {'chapter': 56, 'verse': 39, 'text': 'ثُلَّةٞ مِّنَ ٱلۡأَوَّلِينَ\nУлар аввалгилардан кўпгина жамоатлардир'}, {'chapter': 56, 'verse': 40, 'text': 'وَثُلَّةٞ مِّنَ ٱلۡأٓخِرِينَ\nКейингилардан ҳам кўпгина жамоатлардир'}, {'chapter': 56, 'verse': 41, 'text': 'وَأَصۡحَٰبُ ٱلشِّمَالِ مَآ أَصۡحَٰبُ ٱلشِّمَالِ\nВа чап тараф эгалари. Чап тараф эгалари не(бадбахтлар)дир'}, {'chapter': 56, 'verse': 42, 'text': 'فِي سَمُومٖ وَحَمِيمٖ\nУлар ўт шамол ва қайноқ сувдадирлар'}, {'chapter': 56, 'verse': 43, 'text': 'وَظِلّٖ مِّن يَحۡمُومٖ\nҚоп-қора тутун соясидадирлар'}, {'chapter': 56, 'verse': 44, 'text': 'لَّا بَارِدٖ وَلَا كَرِيمٍ\nНа салқин эмас ва на фойдали'}, {'chapter': 56, 'verse': 45, 'text': 'إِنَّهُمۡ كَانُواْ قَبۡلَ ذَٰلِكَ مُتۡرَفِينَ\nАлбатта, улар бундан олдин ҳойи-ҳавасга берилганлардан эдилар'}, {'chapter': 56, 'verse': 46, 'text': 'وَكَانُواْ يُصِرُّونَ عَلَى ٱلۡحِنثِ ٱلۡعَظِيمِ\nВа катта гуноҳ ишларни мудом қилиб келган эдилар'}, {'chapter': 56, 'verse': 47, 'text': 'وَكَانُواْ يَقُولُونَ أَئِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ\nВа: «Биз ўлиб, тупроқ ва суяк бўлсак ҳам, қайта тирилтириламизми'}, {'chapter': 56, 'verse': 48, 'text': 'أَوَءَابَآؤُنَا ٱلۡأَوَّلُونَ\nАввал ўтган оталаримиз ҳам-а?!» – дер эдилар'}, {'chapter': 56, 'verse': 49, 'text': 'قُلۡ إِنَّ ٱلۡأَوَّلِينَ وَٱلۡأٓخِرِينَ\nАйтгил: «Албатта, аввалгилар ва охиргилар'}, {'chapter': 56, 'verse': 50, 'text': 'لَمَجۡمُوعُونَ إِلَىٰ مِيقَٰتِ يَوۡمٖ مَّعۡلُومٖ\nМаълум куни, белгиланган вақтда, албатта, тўпланувчидирлар»'}, {'chapter': 56, 'verse': 51, 'text': 'ثُمَّ إِنَّكُمۡ أَيُّهَا ٱلضَّآلُّونَ ٱلۡمُكَذِّبُونَ\nСўнгра, сиз, эй адашганлар, ёлғонга чиқарувчилар'}, {'chapter': 56, 'verse': 52, 'text': 'لَأٓكِلُونَ مِن شَجَرٖ مِّن زَقُّومٖ\nАлбатта, заққум дарахтидан еювчисизлар'}, {'chapter': 56, 'verse': 53, 'text': 'فَمَالِـُٔونَ مِنۡهَا ٱلۡبُطُونَ\nБас, ундан қоринларни тўлдирувчисизлар'}, {'chapter': 56, 'verse': 54, 'text': 'فَشَٰرِبُونَ عَلَيۡهِ مِنَ ٱلۡحَمِيمِ\nБас, устидан ўта қайноқ сувдан ичувчисизлар'}, {'chapter': 56, 'verse': 55, 'text': 'فَشَٰرِبُونَ شُرۡبَ ٱلۡهِيمِ\nБас, чанқоқ туядек ичувчисизлар'}, {'chapter': 56, 'verse': 56, 'text': 'هَٰذَا نُزُلُهُمۡ يَوۡمَ ٱلدِّينِ\nУларнинг қиёмат кунидаги зиёфатлари мана шу'}, {'chapter': 56, 'verse': 57, 'text': 'نَحۡنُ خَلَقۡنَٰكُمۡ فَلَوۡلَا تُصَدِّقُونَ\nБиз сизларни яратганмиз, ишонсаларинг-чи'}, {'chapter': 56, 'verse': 58, 'text': 'أَفَرَءَيۡتُم مَّا تُمۡنُونَ\nЎзингиз чиқарадиган манийни ўйлаб кўринг-а'}, {'chapter': 56, 'verse': 59, 'text': 'ءَأَنتُمۡ تَخۡلُقُونَهُۥٓ أَمۡ نَحۡنُ ٱلۡخَٰلِقُونَ\nУни сизлар яратасизми ёки Биз яратувчимизми'}, {'chapter': 56, 'verse': 60, 'text': 'نَحۡنُ قَدَّرۡنَا بَيۡنَكُمُ ٱلۡمَوۡتَ وَمَا نَحۡنُ بِمَسۡبُوقِينَ\nОрангиздаги ўлимни Биз белгилаганмиз ва Биз ожиз эмасмиз'}, {'chapter': 56, 'verse': 61, 'text': 'عَلَىٰٓ أَن نُّبَدِّلَ أَمۡثَٰلَكُمۡ وَنُنشِئَكُمۡ فِي مَا لَا تَعۡلَمُونَ\nСизнинг ўрнингизга сизга ўхшашларни алмаштиришдан ва сизларни ўзингиз билмайдиган ҳолда яратишдан'}, {'chapter': 56, 'verse': 62, 'text': 'وَلَقَدۡ عَلِمۡتُمُ ٱلنَّشۡأَةَ ٱلۡأُولَىٰ فَلَوۡلَا تَذَكَّرُونَ\nВа дарҳақиқат, аввалги яратилишни билдингиз-ку, эсласаларингиз-чи'}, {'chapter': 56, 'verse': 63, 'text': 'أَفَرَءَيۡتُم مَّا تَحۡرُثُونَ\nЎзингиз экаётган нарсани ўйлаб кўринг-а'}, {'chapter': 56, 'verse': 64, 'text': 'ءَأَنتُمۡ تَزۡرَعُونَهُۥٓ أَمۡ نَحۡنُ ٱلزَّـٰرِعُونَ\nУни сиз ундирасизми ёки Биз ундирувчимизми'}, {'chapter': 56, 'verse': 65, 'text': 'لَوۡ نَشَآءُ لَجَعَلۡنَٰهُ حُطَٰمٗا فَظَلۡتُمۡ تَفَكَّهُونَ\nАгар хоҳласак, Биз уни қуруқ чўпга айлантириб қўямиз. Сизлар эса, надоматда ажабланиб'}, {'chapter': 56, 'verse': 66, 'text': 'إِنَّا لَمُغۡرَمُونَ\n«Албатта, биз зиён кўрганлармиз'}, {'chapter': 56, 'verse': 67, 'text': 'بَلۡ نَحۡنُ مَحۡرُومُونَ\nБалки, маҳрум бўлганлармиз, (дейсизлар)'}, {'chapter': 56, 'verse': 68, 'text': 'أَفَرَءَيۡتُمُ ٱلۡمَآءَ ٱلَّذِي تَشۡرَبُونَ\nЎзингиз ичадиган сувни ўйлаб кўринг-а'}, {'chapter': 56, 'verse': 69, 'text': 'ءَأَنتُمۡ أَنزَلۡتُمُوهُ مِنَ ٱلۡمُزۡنِ أَمۡ نَحۡنُ ٱلۡمُنزِلُونَ\nУни булутлардан сиз туширасизми ёки Биз туширувчимизми'}, {'chapter': 56, 'verse': 70, 'text': 'لَوۡ نَشَآءُ جَعَلۡنَٰهُ أُجَاجٗا فَلَوۡلَا تَشۡكُرُونَ\nАгар хоҳласак, Биз уни шўр қилиб қўямиз. Шукр қилсангиз-чи'}, {'chapter': 56, 'verse': 71, 'text': 'أَفَرَءَيۡتُمُ ٱلنَّارَ ٱلَّتِي تُورُونَ\nЎзингиз ёқадиган оловни ўйлаб кўринг-а'}, {'chapter': 56, 'verse': 72, 'text': 'ءَأَنتُمۡ أَنشَأۡتُمۡ شَجَرَتَهَآ أَمۡ نَحۡنُ ٱلۡمُنشِـُٔونَ\nУнинг дарахтини сиз яратгансизми ёки Биз яратувчимизми'}, {'chapter': 56, 'verse': 73, 'text': 'نَحۡنُ جَعَلۡنَٰهَا تَذۡكِرَةٗ وَمَتَٰعٗا لِّلۡمُقۡوِينَ\nБиз у(олов)ни эслатма ва йўловчилар учун манфаат қилиб қўйдик'}, {'chapter': 56, 'verse': 74, 'text': 'فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ\nБас, улуғ Роббинг исмини поклаб ёд эт'}, {'chapter': 56, 'verse': 75, 'text': '۞فَلَآ أُقۡسِمُ بِمَوَٰقِعِ ٱلنُّجُومِ\nЮлдузларнинг манзиллари билан қасам ичаман'}, {'chapter': 56, 'verse': 76, 'text': 'وَإِنَّهُۥ لَقَسَمٞ لَّوۡ تَعۡلَمُونَ عَظِيمٌ\nАлбатта, агар билсангиз, бу улуғ қасамдир'}, {'chapter': 56, 'verse': 77, 'text': 'إِنَّهُۥ لَقُرۡءَانٞ كَرِيمٞ\nАлбатта, у Қуръони Каримдир'}, {'chapter': 56, 'verse': 78, 'text': 'فِي كِتَٰبٖ مَّكۡنُونٖ\nУ Сақланган китобдадир'}, {'chapter': 56, 'verse': 79, 'text': 'لَّا يَمَسُّهُۥٓ إِلَّا ٱلۡمُطَهَّرُونَ\nУни фақат покланганларгина ушлайдир'}, {'chapter': 56, 'verse': 80, 'text': 'تَنزِيلٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nУ, оламларнинг Роббисидан туширилгандир'}, {'chapter': 56, 'verse': 81, 'text': 'أَفَبِهَٰذَا ٱلۡحَدِيثِ أَنتُم مُّدۡهِنُونَ\nБас, сизлар мана шу сўзга бепарволик қиласизларми'}, {'chapter': 56, 'verse': 82, 'text': 'وَتَجۡعَلُونَ رِزۡقَكُمۡ أَنَّكُمۡ تُكَذِّبُونَ\nВа ёлғонга чиқаришни ризқ қилиб оласизларми'}, {'chapter': 56, 'verse': 83, 'text': 'فَلَوۡلَآ إِذَا بَلَغَتِ ٱلۡحُلۡقُومَ\nЖонингиз ҳалқумга келиб қолса-чи'}, {'chapter': 56, 'verse': 84, 'text': 'وَأَنتُمۡ حِينَئِذٖ تَنظُرُونَ\nҲолбуки, ўша вақтда ўзларингиз қараб турган бўласизлар'}, {'chapter': 56, 'verse': 85, 'text': 'وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنكُمۡ وَلَٰكِن لَّا تُبۡصِرُونَ\nБиз эса, сиздан кўра унга яқинмиз, аммо сизлар кўрмайсизлар'}, {'chapter': 56, 'verse': 86, 'text': 'فَلَوۡلَآ إِن كُنتُمۡ غَيۡرَ مَدِينِينَ\nАгар сиз ҳисоб бермайдиган бўлсангиз'}, {'chapter': 56, 'verse': 87, 'text': 'تَرۡجِعُونَهَآ إِن كُنتُمۡ صَٰدِقِينَ\nУ(жон)ни ўрнига қайтаринг-чи, агар гапларингиз рост бўлса'}, {'chapter': 56, 'verse': 88, 'text': 'فَأَمَّآ إِن كَانَ مِنَ ٱلۡمُقَرَّبِينَ\nАгар яқинлаштирилганлардан бўлса'}, {'chapter': 56, 'verse': 89, 'text': 'فَرَوۡحٞ وَرَيۡحَانٞ وَجَنَّتُ نَعِيمٖ\nРоҳатда, фароғатда ва сернеъмат жаннатдадир'}, {'chapter': 56, 'verse': 90, 'text': 'وَأَمَّآ إِن كَانَ مِنۡ أَصۡحَٰبِ ٱلۡيَمِينِ\nАгар ўнг тараф эгаларидан бўлса'}, {'chapter': 56, 'verse': 91, 'text': 'فَسَلَٰمٞ لَّكَ مِنۡ أَصۡحَٰبِ ٱلۡيَمِينِ\nБас, сенга ўнг тараф эгаларидан Салом бўлсин, (дейилур)'}, {'chapter': 56, 'verse': 92, 'text': 'وَأَمَّآ إِن كَانَ مِنَ ٱلۡمُكَذِّبِينَ ٱلضَّآلِّينَ\nВа агар ёлғонга чиқарувчи гумроҳлардан бўлса'}, {'chapter': 56, 'verse': 93, 'text': 'فَنُزُلٞ مِّنۡ حَمِيمٖ\nБас, ўта қайноқ сувдан зиёфат'}, {'chapter': 56, 'verse': 94, 'text': 'وَتَصۡلِيَةُ جَحِيمٍ\nВа дўзахга киришдир'}, {'chapter': 56, 'verse': 95, 'text': 'إِنَّ هَٰذَا لَهُوَ حَقُّ ٱلۡيَقِينِ\nАлбатта, ушбу, таъкидки, очиқ-ойдин ҳақиқатдир'}, {'chapter': 56, 'verse': 96, 'text': 'فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ\nБас, улуғ Роббинг исмини поклаб ёд эт'}], '57': [{'chapter': 57, 'verse': 1, 'text': 'سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлардаги ва ердаги нарсалар Аллоҳга тасбиҳ айтди. У зот Азийз ва Ҳакиймдир'}, {'chapter': 57, 'verse': 2, 'text': 'لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлару ернинг мулки Уникидир. У тирилтирур ва ўлдирур ва У ҳар бир нарсага қодирдир'}, {'chapter': 57, 'verse': 3, 'text': 'هُوَ ٱلۡأَوَّلُ وَٱلۡأٓخِرُ وَٱلظَّـٰهِرُ وَٱلۡبَاطِنُۖ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nУ аввал ва охир, ошкор ва пинҳондир. Ва У ҳар бир нарсани билувчидир'}, {'chapter': 57, 'verse': 4, 'text': 'هُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۖ وَهُوَ مَعَكُمۡ أَيۡنَ مَا كُنتُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nУ осмонлару ерни олти кунда яратиб, сўнгра (Зоти ва улуғлигига муносиб суратда) Аршга юксалган Зотдир. Ерга нима кирганини ҳам, ундан нима чиққанини ҳам, осмондан нима тушганини ҳам, унга нима чиққанини ҳам биладир. У, қаерда бўлсангиз ҳам, сиз билан бирга. Аллоҳ қилаётган амалларингизни кўриб турувчидир'}, {'chapter': 57, 'verse': 5, 'text': 'لَّهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nОсмонлару ернинг мулки Уникидир ва барча ишлар Аллоҳнинг Ўзигагина қайтадир'}, {'chapter': 57, 'verse': 6, 'text': 'يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِۚ وَهُوَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nКечасини кундузга киритади, кундузни кечасига киритади ва У кўксингиздаги нарсани ҳам яхши билувчидир'}, {'chapter': 57, 'verse': 7, 'text': 'ءَامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَأَنفِقُواْ مِمَّا جَعَلَكُم مُّسۡتَخۡلَفِينَ فِيهِۖ فَٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَأَنفَقُواْ لَهُمۡ أَجۡرٞ كَبِيرٞ\nАллоҳга ва Унинг Расулига иймон келтиринглар ҳамда сизларни халифа қилиб қўйган нарсалардан эҳсон қилинглар. Бас, сизлардан иймон келтирган ва эҳсон қилганларга улкан ажр бордир'}, {'chapter': 57, 'verse': 8, 'text': 'وَمَا لَكُمۡ لَا تُؤۡمِنُونَ بِٱللَّهِ وَٱلرَّسُولُ يَدۡعُوكُمۡ لِتُؤۡمِنُواْ بِرَبِّكُمۡ وَقَدۡ أَخَذَ مِيثَٰقَكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nСизларга нима бўлдики, Аллоҳга иймон келтирмайсизлар? Ҳолбуки, Пайғамбар сизларни Роббиларингизга иймон келтиришга чақириб турибди. У зот сизлардан аҳд олган эди. Агар иймон келтирувчи бўлсаларингиз'}, {'chapter': 57, 'verse': 9, 'text': 'هُوَ ٱلَّذِي يُنَزِّلُ عَلَىٰ عَبۡدِهِۦٓ ءَايَٰتِۭ بَيِّنَٰتٖ لِّيُخۡرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَإِنَّ ٱللَّهَ بِكُمۡ لَرَءُوفٞ رَّحِيمٞ\nУ зот сизларни зулматлардан нурга чиқариш учун Ўз бандасига равшан оятларни туширади. Ва, албатта, Аллоҳ сизларга шафқатли ва раҳимлидир'}, {'chapter': 57, 'verse': 10, 'text': 'وَمَا لَكُمۡ أَلَّا تُنفِقُواْ فِي سَبِيلِ ٱللَّهِ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ لَا يَسۡتَوِي مِنكُم مَّنۡ أَنفَقَ مِن قَبۡلِ ٱلۡفَتۡحِ وَقَٰتَلَۚ أُوْلَـٰٓئِكَ أَعۡظَمُ دَرَجَةٗ مِّنَ ٱلَّذِينَ أَنفَقُواْ مِنۢ بَعۡدُ وَقَٰتَلُواْۚ وَكُلّٗا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nСизларга нима бўлдики, Аллоҳнинг йўлида эҳсон қилмайсизлар? Ҳолбуки, осмонлару ернинг мероси Аллоҳникидир. Сизлардан ҳеч ким фатҳдан илгари эҳсон қилганларга ва урушда қатнашганларга баробар бўла олмас. Уларнинг даражалари фатҳдан сўнг эҳсон қилганлардан ва урушда қатнашганлардан кўра улуғдир. Аллоҳ барчага яхшиликни ваъда қилган. Ва Аллоҳ нима қилаётганларингиздан хабардордир'}, {'chapter': 57, 'verse': 11, 'text': 'مَّن ذَا ٱلَّذِي يُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفَهُۥ لَهُۥ وَلَهُۥٓ أَجۡرٞ كَرِيمٞ\nАллоҳга яхши қарз берадиган киши борми?! Бас, У зот унга бир неча марта кўпайтириб қайтаради ва унга карамли ажр бор'}, {'chapter': 57, 'verse': 12, 'text': 'يَوۡمَ تَرَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ يَسۡعَىٰ نُورُهُم بَيۡنَ أَيۡدِيهِمۡ وَبِأَيۡمَٰنِهِمۖ بُشۡرَىٰكُمُ ٱلۡيَوۡمَ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nМўмин ва мўминаларни уларнинг олдиларида ва ўнг тарафларида нурлари ҳаракат қилаётган ҳолда кўрган кунингда, Сизларга бугунги куннинг хушхабари, дарахтлари остидан сувлари оқиб турган жаннатлардир. У жойда абадий қолажаксиз. Бу эса, айнан улкан ютуқдир (дейилур)'}, {'chapter': 57, 'verse': 13, 'text': 'يَوۡمَ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلۡمُنَٰفِقَٰتُ لِلَّذِينَ ءَامَنُواْ ٱنظُرُونَا نَقۡتَبِسۡ مِن نُّورِكُمۡ قِيلَ ٱرۡجِعُواْ وَرَآءَكُمۡ فَٱلۡتَمِسُواْ نُورٗاۖ فَضُرِبَ بَيۡنَهُم بِسُورٖ لَّهُۥ بَابُۢ بَاطِنُهُۥ فِيهِ ٱلرَّحۡمَةُ وَظَٰهِرُهُۥ مِن قِبَلِهِ ٱلۡعَذَابُ\nМунофиқ ва мунофиқалар иймонлиларга: «Тўхтаб туринглар, биз ҳам нурингиздан бир оз олайлик», деган ва уларга: «Ортингизга қайтинг ҳамда нур қидиринг», дейилган кунда. Бас, уларнинг орасига эшиги бор қўрғон урилур. Унинг ичида раҳмат, ташқарисида — олдида азобдир'}, {'chapter': 57, 'verse': 14, 'text': 'يُنَادُونَهُمۡ أَلَمۡ نَكُن مَّعَكُمۡۖ قَالُواْ بَلَىٰ وَلَٰكِنَّكُمۡ فَتَنتُمۡ أَنفُسَكُمۡ وَتَرَبَّصۡتُمۡ وَٱرۡتَبۡتُمۡ وَغَرَّتۡكُمُ ٱلۡأَمَانِيُّ حَتَّىٰ جَآءَ أَمۡرُ ٱللَّهِ وَغَرَّكُم بِٱللَّهِ ٱلۡغَرُورُ\nУларга (мунофиқлар): «Биз сиз билан бирга эмасмидик?!» – деб нидо қилурлар. Улар: «Худди шундай. Лекин сизлар Аллоҳнинг амри келгунча ўзингизни ўзингиз фитнага дучор қилдингиз, кўз тикиб кутдингиз, шубҳа қилдингиз ва сизларни хомхаёллар ғурурга кетказди. Сизларни Аллоҳ ҳақида алдамчи алдаб қўйди'}, {'chapter': 57, 'verse': 15, 'text': 'فَٱلۡيَوۡمَ لَا يُؤۡخَذُ مِنكُمۡ فِدۡيَةٞ وَلَا مِنَ ٱلَّذِينَ كَفَرُواْۚ مَأۡوَىٰكُمُ ٱلنَّارُۖ هِيَ مَوۡلَىٰكُمۡۖ وَبِئۡسَ ٱلۡمَصِيرُ\nБугун сизлардан ҳам, куфр келтирганлардан ҳам тўлов олинмас. Жойларингиз жаҳаннамда, у сизнинг эгангиздир. Қандай ҳам ёмон борар жой!» – дерлар'}, {'chapter': 57, 'verse': 16, 'text': '۞أَلَمۡ يَأۡنِ لِلَّذِينَ ءَامَنُوٓاْ أَن تَخۡشَعَ قُلُوبُهُمۡ لِذِكۡرِ ٱللَّهِ وَمَا نَزَلَ مِنَ ٱلۡحَقِّ وَلَا يَكُونُواْ كَٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلُ فَطَالَ عَلَيۡهِمُ ٱلۡأَمَدُ فَقَسَتۡ قُلُوبُهُمۡۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ\nИймон келтирганлар учун Аллоҳнинг зикрига ва нозил бўлган ҳаққа қалблари юмшаш, айни чоғда, олдин китоб берилган, сўнг муддат ўтиши билан қалблари қотиб қолган, кўплари фосиқ бўлганларга ўхшамаслик вақти келмадими'}, {'chapter': 57, 'verse': 17, 'text': 'ٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ قَدۡ بَيَّنَّا لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ\nШуни билингларки, албатта Аллоҳ ўлик ерни тирилтирур. Дарҳақиқат, сизларга оятларни баён қилдик, шоядки, ақл юритсангизлар'}, {'chapter': 57, 'verse': 18, 'text': 'إِنَّ ٱلۡمُصَّدِّقِينَ وَٱلۡمُصَّدِّقَٰتِ وَأَقۡرَضُواْ ٱللَّهَ قَرۡضًا حَسَنٗا يُضَٰعَفُ لَهُمۡ وَلَهُمۡ أَجۡرٞ كَرِيمٞ\nАлбатта, садақа қилувчи эркагу аёл ҳамда Аллоҳга қарзи ҳасана берувчиларга (савоб) кўпайтириб берилур. Уларга карамли ажр ҳам бор'}, {'chapter': 57, 'verse': 19, 'text': 'وَٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦٓ أُوْلَـٰٓئِكَ هُمُ ٱلصِّدِّيقُونَۖ وَٱلشُّهَدَآءُ عِندَ رَبِّهِمۡ لَهُمۡ أَجۡرُهُمۡ وَنُورُهُمۡۖ وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nАллоҳга ва Унинг Пайғамбарларига иймон келтирган кишилар, ана ўшалар, сиддиқлардир ва Роббилари ҳузуридаги шоҳидлардир. Уларнинг ажрлари ва нурлари бор. Куфр келтирганлар ва оятларимизни ёлғонга чиқарган кишилар, ана ўшалар дўзах эгаларидир'}, {'chapter': 57, 'verse': 20, 'text': 'ٱعۡلَمُوٓاْ أَنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞ وَزِينَةٞ وَتَفَاخُرُۢ بَيۡنَكُمۡ وَتَكَاثُرٞ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِۖ كَمَثَلِ غَيۡثٍ أَعۡجَبَ ٱلۡكُفَّارَ نَبَاتُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصۡفَرّٗا ثُمَّ يَكُونُ حُطَٰمٗاۖ وَفِي ٱلۡأٓخِرَةِ عَذَابٞ شَدِيدٞ وَمَغۡفِرَةٞ مِّنَ ٱللَّهِ وَرِضۡوَٰنٞۚ وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا مَتَٰعُ ٱلۡغُرُورِ\nБилингки, албатта, бу дунё ҳаёти ўйин, кўнгил эрмаги ва зеб-зийнатдан, ўзаро фахрланишдан, мол-мулкни ва бола-чақани кўпайтиришдан иборатдир. У, худди, ўсимлиги деҳқонларни ажаблантирган ёмғирга ўхшайдир. Кейинроқ бу ўсимликлар қурийди ва сарғайганини кўрасан. Сўнгра у хас-чўпга айланиб кетадир. Охиратда эса, қаттиқ азоб бор, Аллоҳдан мағфират ва розилик бор. Бу дунё ҳаёти ғурурга кетказувчи матодан ўзга ҳеч нарса эмас'}, {'chapter': 57, 'verse': 21, 'text': 'سَابِقُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا كَعَرۡضِ ٱلسَّمَآءِ وَٱلۡأَرۡضِ أُعِدَّتۡ لِلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦۚ ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nРоббингизнинг мағфиратига ва Аллоҳга ҳамда Унинг Пайғамбарларига иймон келтирганлар учун тайёрланган, кенглиги осмону ернинг кенглигича бўлган жаннатга мусобақалашинг. Ана ўша, Аллоҳнинг фазли. Кимга хоҳласа, беради. Аллоҳ улуғ фазл эгасидир'}, {'chapter': 57, 'verse': 22, 'text': 'مَآ أَصَابَ مِن مُّصِيبَةٖ فِي ٱلۡأَرۡضِ وَلَا فِيٓ أَنفُسِكُمۡ إِلَّا فِي كِتَٰبٖ مِّن قَبۡلِ أَن نَّبۡرَأَهَآۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nБирор мусийбатни пайдо қилмасимиздан олдин у Китобда битилган бўлур, акс ҳолда, ер юзида ва ўз шахсларингизда сизга бирор мусийбат етмас. Албатта, бу иш Аллоҳ учун осондир'}, {'chapter': 57, 'verse': 23, 'text': 'لِّكَيۡلَا تَأۡسَوۡاْ عَلَىٰ مَا فَاتَكُمۡ وَلَا تَفۡرَحُواْ بِمَآ ءَاتَىٰكُمۡۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٍ\nТоки, кетган нарсага қайғуриб, келган нарсадан хурсанд бўлмаслигингиз учун. Аллоҳ барча димоғдор ва мақтанчоқларни суймас'}, {'chapter': 57, 'verse': 24, 'text': 'ٱلَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِۗ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nУлар бахилдирлар ва кишиларни ҳам бахилликка буюрурлар. Ким юз ўгирса, бас, Аллоҳ беҳожат ва мақталган Зотдир'}, {'chapter': 57, 'verse': 25, 'text': 'لَقَدۡ أَرۡسَلۡنَا رُسُلَنَا بِٱلۡبَيِّنَٰتِ وَأَنزَلۡنَا مَعَهُمُ ٱلۡكِتَٰبَ وَٱلۡمِيزَانَ لِيَقُومَ ٱلنَّاسُ بِٱلۡقِسۡطِۖ وَأَنزَلۡنَا ٱلۡحَدِيدَ فِيهِ بَأۡسٞ شَدِيدٞ وَمَنَٰفِعُ لِلنَّاسِ وَلِيَعۡلَمَ ٱللَّهُ مَن يَنصُرُهُۥ وَرُسُلَهُۥ بِٱلۡغَيۡبِۚ إِنَّ ٱللَّهَ قَوِيٌّ عَزِيزٞ\nДарҳақиқат, Биз Пайғамбарларимизни равшан(нарса)лар ила юбордик ва улар ила китоб ҳамда одамлар адолатда туришлари учун мезон туширдик. Ва темирни туширдик — унда катта куч-қувват ва одамлар учун манфаатлар бор. Аллоҳ ким Ўзига ва Пайғамбарларига ғойибона ёрдам бераётганини билиши учун. Албатта, Аллоҳ кучли ва азийздир'}, {'chapter': 57, 'verse': 26, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحٗا وَإِبۡرَٰهِيمَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِمَا ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَۖ فَمِنۡهُم مُّهۡتَدٖۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ\nДарҳақиқат, Нуҳ ва Иброҳимни Пайғамбар қилиб юбордик ҳамда Пайғамбарликни ва китобни икковларининг зурриётига хос қилдик. Улар орасида ҳидоят топганлар бор, лекин уларнинг кўплари фосиқлардир'}, {'chapter': 57, 'verse': 27, 'text': 'ثُمَّ قَفَّيۡنَا عَلَىٰٓ ءَاثَٰرِهِم بِرُسُلِنَا وَقَفَّيۡنَا بِعِيسَى ٱبۡنِ مَرۡيَمَ وَءَاتَيۡنَٰهُ ٱلۡإِنجِيلَۖ وَجَعَلۡنَا فِي قُلُوبِ ٱلَّذِينَ ٱتَّبَعُوهُ رَأۡفَةٗ وَرَحۡمَةٗۚ وَرَهۡبَانِيَّةً ٱبۡتَدَعُوهَا مَا كَتَبۡنَٰهَا عَلَيۡهِمۡ إِلَّا ٱبۡتِغَآءَ رِضۡوَٰنِ ٱللَّهِ فَمَا رَعَوۡهَا حَقَّ رِعَايَتِهَاۖ فَـَٔاتَيۡنَا ٱلَّذِينَ ءَامَنُواْ مِنۡهُمۡ أَجۡرَهُمۡۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ\nСўнгра уларнинг ортидан Пайғамбарларимизни кетма-кет юбордик ва Ийсо ибн Марямни ҳам ортларидан юбордик ҳамда унга Инжилни бердик. Унга эргашганларнинг қалбларига меҳрибонлик ва раҳимдилликни солдик. Роҳибликни ўзлари чиқариб олдилар, Биз буни уларга фарз қилмаган эдик, фақат ўзлари Аллоҳнинг розилигини тилаб қилдилар, лекин ҳақиқий риоясини қила олмадилар. Бас, улардан иймон келтирганларига ажрларини бердик, лекин уларнинг кўпи фосиқлардир'}, {'chapter': 57, 'verse': 28, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَءَامِنُواْ بِرَسُولِهِۦ يُؤۡتِكُمۡ كِفۡلَيۡنِ مِن رَّحۡمَتِهِۦ وَيَجۡعَل لَّكُمۡ نُورٗا تَمۡشُونَ بِهِۦ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭй, иймон келтирганлар! Аллоҳга тақво қилинглар ва Унинг Пайғамбарига иймон келтиринглар. У зот Ўз раҳматидан сизларга икки ҳисса берадир ва сизларнинг юришингизга нур берадир ҳамда сизларни мағфират қиладир. Аллоҳ кечирувчи, раҳимли Зотдир'}, {'chapter': 57, 'verse': 29, 'text': 'لِّئَلَّا يَعۡلَمَ أَهۡلُ ٱلۡكِتَٰبِ أَلَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّن فَضۡلِ ٱللَّهِ وَأَنَّ ٱلۡفَضۡلَ بِيَدِ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nТоки китоб аҳллари Аллоҳнинг фазлидан ҳеч нарсага қодир эмасликларини, фазл Аллоҳнинг қўлида бўлиб, хоҳлаган кишисига беришини билсинлар. Аллоҳ улуғ фазл эгасидир'}], '58': [{'chapter': 58, 'verse': 1, 'text': 'قَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّتِي تُجَٰدِلُكَ فِي زَوۡجِهَا وَتَشۡتَكِيٓ إِلَى ٱللَّهِ وَٱللَّهُ يَسۡمَعُ تَحَاوُرَكُمَآۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٌ\nАлбатта, Аллоҳ сен билан ўз эри ҳақида тортишаётган ва Аллоҳга шикоят қилаётган(аёл)нинг сўзини эшитди. Ҳа, Аллоҳ икковингизнинг гаплашувингизни эшитмоқда. Албатта, Аллоҳ эшитувчи ва кўрувчидир'}, {'chapter': 58, 'verse': 2, 'text': 'ٱلَّذِينَ يُظَٰهِرُونَ مِنكُم مِّن نِّسَآئِهِم مَّا هُنَّ أُمَّهَٰتِهِمۡۖ إِنۡ أُمَّهَٰتُهُمۡ إِلَّا ٱلَّـٰٓـِٔي وَلَدۡنَهُمۡۚ وَإِنَّهُمۡ لَيَقُولُونَ مُنكَرٗا مِّنَ ٱلۡقَوۡلِ وَزُورٗاۚ وَإِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٞ\nИчларингизда ўз хотинларидан зиҳор қиладиганлар (билиб қўйсинларки), улар аларнинг оналари эмас. Туққан оналаридан ўзга ҳеч ким уларга она эмаслар. Ва, албатта, улар инкор қилинган ва ғирт ёлғон сўзларни айтурлар. Ва, албатта, Аллоҳ кечирувчи ва мағфират қилувчидир'}, {'chapter': 58, 'verse': 3, 'text': 'وَٱلَّذِينَ يُظَٰهِرُونَ مِن نِّسَآئِهِمۡ ثُمَّ يَعُودُونَ لِمَا قَالُواْ فَتَحۡرِيرُ رَقَبَةٖ مِّن قَبۡلِ أَن يَتَمَآسَّاۚ ذَٰلِكُمۡ تُوعَظُونَ بِهِۦۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nХотинлардан зиҳор қилганлар ўз айтганларидан қайтсалар, (қайта) қўшилмасларидан аввал бир қул озод қилсинлар. Бу (ҳукм) ваъзланишингиз учундир. Аллоҳ қилаётган ишларингиздан хабардордир'}, {'chapter': 58, 'verse': 4, 'text': 'فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ مِن قَبۡلِ أَن يَتَمَآسَّاۖ فَمَن لَّمۡ يَسۡتَطِعۡ فَإِطۡعَامُ سِتِّينَ مِسۡكِينٗاۚ ذَٰلِكَ لِتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦۚ وَتِلۡكَ حُدُودُ ٱللَّهِۗ وَلِلۡكَٰفِرِينَ عَذَابٌ أَلِيمٌ\nКим (қул) топмаса, бас (қайта) қўшилмасдан аввал икки ой кетма-кет рўза тутсин. Ким (рўза тутишга) қодир бўлмаса, олтмиш мискинни тўйғазсин. Бу (ҳукм) Аллоҳга ва Унинг Расулига иймон келтиришингиз учундир. Ва бу(ҳукм)лар Аллоҳнинг чегараларидир. Ва кофирларга аламли азоб бор'}, {'chapter': 58, 'verse': 5, 'text': 'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥ كُبِتُواْ كَمَا كُبِتَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ وَقَدۡ أَنزَلۡنَآ ءَايَٰتِۭ بَيِّنَٰتٖۚ وَلِلۡكَٰفِرِينَ عَذَابٞ مُّهِينٞ\nАлбатта, Аллоҳга ва Унинг Расулига душманлик қиладиганлар, улардан аввалгилар хор бўлганидек, хор бўлурлар. Ҳолбуки, батаҳқиқ, Биз очиқ-ойдин оятларни тушириб қўйганмиз. Ва кофирларга хорловчи азоб бор'}, {'chapter': 58, 'verse': 6, 'text': 'يَوۡمَ يَبۡعَثُهُمُ ٱللَّهُ جَمِيعٗا فَيُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ أَحۡصَىٰهُ ٱللَّهُ وَنَسُوهُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nАллоҳ барчаларини қайта тирилтирадиган кунда, қилган ишларининг хабарини берур. Аллоҳ ҳисоблаб турган, улар эса унутганлар. Ҳолбуки, Аллоҳ ҳар бир нарсага шоҳиддир'}, {'chapter': 58, 'verse': 7, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ مَا يَكُونُ مِن نَّجۡوَىٰ ثَلَٰثَةٍ إِلَّا هُوَ رَابِعُهُمۡ وَلَا خَمۡسَةٍ إِلَّا هُوَ سَادِسُهُمۡ وَلَآ أَدۡنَىٰ مِن ذَٰلِكَ وَلَآ أَكۡثَرَ إِلَّا هُوَ مَعَهُمۡ أَيۡنَ مَا كَانُواْۖ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُواْ يَوۡمَ ٱلۡقِيَٰمَةِۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАлбатта, Аллоҳ осмонлару ердаги барча нарсаларни билишини англамадингми? Агар учтанинг сирли сўзлашуви бўлса, албатта, У зот тўртинчидир. Бештаники бўлса, У зот олтинчидир. Бундан оз бўлса ҳам, кўп бўлса ҳам ва қаерда бўлсалар ҳам, албатта, Аллоҳ улар биландир. Сўнг қиёмат кунида уларга қилган ишларидан хабар берадир. Албатта, Аллоҳ ҳар бир нарсани билувчидир'}, {'chapter': 58, 'verse': 8, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ نُهُواْ عَنِ ٱلنَّجۡوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُواْ عَنۡهُ وَيَتَنَٰجَوۡنَ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَمَعۡصِيَتِ ٱلرَّسُولِۖ وَإِذَا جَآءُوكَ حَيَّوۡكَ بِمَا لَمۡ يُحَيِّكَ بِهِ ٱللَّهُ وَيَقُولُونَ فِيٓ أَنفُسِهِمۡ لَوۡلَا يُعَذِّبُنَا ٱللَّهُ بِمَا نَقُولُۚ حَسۡبُهُمۡ جَهَنَّمُ يَصۡلَوۡنَهَاۖ فَبِئۡسَ ٱلۡمَصِيرُ\nСирли сўзлашувдан қайтарилганларни кўрмадингми?! Ман этилган ишга яна қайтурлар, гуноҳ ва душманлик ила ва Пайғамбарга осий бўлиб, сирли сўзларлар. Ва ҳузурингга келсалар, сен билан Аллоҳ ўргатмаган тарзда сўрашурлар. Ва ичларида, шу гапимиз учун Аллоҳ бизни азобламаса эди, дерлар. Уларга жаҳаннам кифоядир, унга кирурлар. У қандоқ ҳам ёмон жой'}, {'chapter': 58, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا تَنَٰجَيۡتُمۡ فَلَا تَتَنَٰجَوۡاْ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَمَعۡصِيَتِ ٱلرَّسُولِ وَتَنَٰجَوۡاْ بِٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ\nЭй, иймон келтирганлар! Қачон сирли суҳбат қилсангиз, гуноҳ, душманлик ва Пайғамбарга маъсиятга сирли суҳбат қилмангиз. Яхшилик ва тақвога сирли суҳбат қилингиз. Ҳузурига тўпланиб борадиганингиз Аллоҳга тақво қилингиз'}, {'chapter': 58, 'verse': 10, 'text': 'إِنَّمَا ٱلنَّجۡوَىٰ مِنَ ٱلشَّيۡطَٰنِ لِيَحۡزُنَ ٱلَّذِينَ ءَامَنُواْ وَلَيۡسَ بِضَآرِّهِمۡ شَيۡـًٔا إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nАлбатта, иймонлиларни ранжитиш учун қилинган сирли суҳбат шайтондандур, Аллоҳнинг изни билан қилинган сирли суҳбатгина уларга ҳеч зарар етказмас. Мўминлар Аллоҳнинг Ўзига таваккал қилсинлар'}, {'chapter': 58, 'verse': 11, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا قِيلَ لَكُمۡ تَفَسَّحُواْ فِي ٱلۡمَجَٰلِسِ فَٱفۡسَحُواْ يَفۡسَحِ ٱللَّهُ لَكُمۡۖ وَإِذَا قِيلَ ٱنشُزُواْ فَٱنشُزُواْ يَرۡفَعِ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ دَرَجَٰتٖۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nЭй, иймон келтирганлар! Агар сизларга мажлисларда «Силжиброқ ўтиринг» дейилса, бас, силжинг, агар «Туринг» дейилса, туринг. Аллоҳ сизлардан иймон келтирганларнинг ва илм берилганларнинг даражаларини кўтарадир. Аллоҳ нима қилаётганларингиздан хабардордир'}, {'chapter': 58, 'verse': 12, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نَٰجَيۡتُمُ ٱلرَّسُولَ فَقَدِّمُواْ بَيۡنَ يَدَيۡ نَجۡوَىٰكُمۡ صَدَقَةٗۚ ذَٰلِكَ خَيۡرٞ لَّكُمۡ وَأَطۡهَرُۚ فَإِن لَّمۡ تَجِدُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nЭй, иймон келтирганлар! Агар Пайғамбар билан сирли суҳбат қилмоқчи бўлсангиз, сирли суҳбатингиздан аввал садақа беринг. Шундоқ қилмоғингиз ўзингиз учун яхшироқдир ва (қалбингиз учун) покроқдир. Агар (садақага нарса) топмасангиз, албатта Аллоҳ кечирувчи ва раҳимлидир'}, {'chapter': 58, 'verse': 13, 'text': 'ءَأَشۡفَقۡتُمۡ أَن تُقَدِّمُواْ بَيۡنَ يَدَيۡ نَجۡوَىٰكُمۡ صَدَقَٰتٖۚ فَإِذۡ لَمۡ تَفۡعَلُواْ وَتَابَ ٱللَّهُ عَلَيۡكُمۡ فَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nСизларга сирли суҳбатингиздан аввал садақа бериш оғир келдими? Агар (буйруқни) бажармасангиз, (майли) Аллоҳ афв қилди. Бас, намоз ўқингиз ва закот берингиз. Ва Аллоҳга ва Унинг Пайғамбарига итоат қилингиз. Аллоҳ қилаётган ишларингиздан хабардордир'}, {'chapter': 58, 'verse': 14, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ تَوَلَّوۡاْ قَوۡمًا غَضِبَ ٱللَّهُ عَلَيۡهِم مَّا هُم مِّنكُمۡ وَلَا مِنۡهُمۡ وَيَحۡلِفُونَ عَلَى ٱلۡكَذِبِ وَهُمۡ يَعۡلَمُونَ\nАллоҳнинг ғазабига дучор бўлган қавмни дўст тутганларни кўрмайсанми? Улар сизлардан ҳам эмас, улардан ҳам эмас. Билиб туриб, ёлғонга қасам ичарлар'}, {'chapter': 58, 'verse': 15, 'text': 'أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابٗا شَدِيدًاۖ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ\nАллоҳ улар учун оғир азобни тайёрлаган. Уларнинг қилаётган ишлари нақадар ёмон'}, {'chapter': 58, 'verse': 16, 'text': 'ٱتَّخَذُوٓاْ أَيۡمَٰنَهُمۡ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ ٱللَّهِ فَلَهُمۡ عَذَابٞ مُّهِينٞ\nУлар қасамларини ўзлари учун қалқон қилиб олдилар. Бас, Аллоҳнинг йўлидан тўсдилар. Уларга хорловчи азоб бордир'}, {'chapter': 58, 'verse': 17, 'text': 'لَّن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـًٔاۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nМоллари ҳам, болалари ҳам улардан Аллоҳ(азоби)дан ҳеч нарсани қайтара олмас. Ана ўшалар дўзах аҳлидурлар. Улар унда абадий қолурлар'}, {'chapter': 58, 'verse': 18, 'text': 'يَوۡمَ يَبۡعَثُهُمُ ٱللَّهُ جَمِيعٗا فَيَحۡلِفُونَ لَهُۥ كَمَا يَحۡلِفُونَ لَكُمۡ وَيَحۡسَبُونَ أَنَّهُمۡ عَلَىٰ شَيۡءٍۚ أَلَآ إِنَّهُمۡ هُمُ ٱلۡكَٰذِبُونَ\nАллоҳ уларнинг барчасини қайта тирилтирадиган кунда, худди сизларга қасам ичганларидек, Унга ҳам қасам ичарлар, ва бирор нарсага эришамиз, деб ўйларлар. Аё, огоҳ бўлинг, улар, ҳа, улар ёлғончидирлар'}, {'chapter': 58, 'verse': 19, 'text': 'ٱسۡتَحۡوَذَ عَلَيۡهِمُ ٱلشَّيۡطَٰنُ فَأَنسَىٰهُمۡ ذِكۡرَ ٱللَّهِۚ أُوْلَـٰٓئِكَ حِزۡبُ ٱلشَّيۡطَٰنِۚ أَلَآ إِنَّ حِزۡبَ ٱلشَّيۡطَٰنِ هُمُ ٱلۡخَٰسِرُونَ\nШайтон уларни эгаллаб олган ва Аллоҳнинг зикрини унуттирган. Ундоқ кишилар шайтон фирқасидандирлар. Аё, огоҳ бўлинглар, шайтон фирқасидагилар, ҳа, ўшалар ютқазувчидирлар'}, {'chapter': 58, 'verse': 20, 'text': 'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥٓ أُوْلَـٰٓئِكَ فِي ٱلۡأَذَلِّينَ\nАлбатта, Аллоҳга ва Унинг Расулига душманлик қилувчилар хор бўлганлардандир'}, {'chapter': 58, 'verse': 21, 'text': 'كَتَبَ ٱللَّهُ لَأَغۡلِبَنَّ أَنَا۠ وَرُسُلِيٓۚ إِنَّ ٱللَّهَ قَوِيٌّ عَزِيزٞ\nАллоҳ: «Мен ва Менинг Расулларим ғалаба қиламиз», деб ҳукм қилган. Албатта, Аллоҳ қувватли ва ғолибдир'}, {'chapter': 58, 'verse': 22, 'text': 'لَّا تَجِدُ قَوۡمٗا يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ يُوَآدُّونَ مَنۡ حَآدَّ ٱللَّهَ وَرَسُولَهُۥ وَلَوۡ كَانُوٓاْ ءَابَآءَهُمۡ أَوۡ أَبۡنَآءَهُمۡ أَوۡ إِخۡوَٰنَهُمۡ أَوۡ عَشِيرَتَهُمۡۚ أُوْلَـٰٓئِكَ كَتَبَ فِي قُلُوبِهِمُ ٱلۡإِيمَٰنَ وَأَيَّدَهُم بِرُوحٖ مِّنۡهُۖ وَيُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ رَضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ أُوْلَـٰٓئِكَ حِزۡبُ ٱللَّهِۚ أَلَآ إِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡمُفۡلِحُونَ\nАллоҳга ва охират кунига иймон келтирган қавмнинг Аллоҳ ва Унинг Расулига душманлик қилувчиларга дўстлик қилганларини топмассан. Ҳатто, у(душман)лар ўз оталари, болалари, ака-укалари ёки қариндошлари бўлса ҳам. Аллоҳ ундоқ кишиларнинг қалбларида иймонни собит қилган, уларни Ўз ҳузуридан бир руҳ билан қўллагандир. Ва уларни остидан сувлар оқиб турган жаннатларга киритур. Улар унда абадий қолурлар. Аллоҳ улардан рози бўлур. Улар Аллоҳдан рози бўлурлар. Ундай кишилар Аллоҳнинг фирқасидандирлар. Аё, огоҳ бўлинглар, албатта, Аллоҳнинг фирқасидагилар, ҳа, ўшалар ютувчилардир'}], '59': [{'chapter': 59, 'verse': 1, 'text': 'سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлардагию ердаги барча нарсалар Аллоҳни поклаб ёд этди. У зот азийз ва ҳикматли Зотдир'}, {'chapter': 59, 'verse': 2, 'text': 'هُوَ ٱلَّذِيٓ أَخۡرَجَ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ مِن دِيَٰرِهِمۡ لِأَوَّلِ ٱلۡحَشۡرِۚ مَا ظَنَنتُمۡ أَن يَخۡرُجُواْۖ وَظَنُّوٓاْ أَنَّهُم مَّانِعَتُهُمۡ حُصُونُهُم مِّنَ ٱللَّهِ فَأَتَىٰهُمُ ٱللَّهُ مِنۡ حَيۡثُ لَمۡ يَحۡتَسِبُواْۖ وَقَذَفَ فِي قُلُوبِهِمُ ٱلرُّعۡبَۚ يُخۡرِبُونَ بُيُوتَهُم بِأَيۡدِيهِمۡ وَأَيۡدِي ٱلۡمُؤۡمِنِينَ فَٱعۡتَبِرُواْ يَـٰٓأُوْلِي ٱلۡأَبۡصَٰرِ\nУ зот аҳли китобдан бўлган кофирларни ўз диёрларидан биринчи ҳашр учун чиқарган Зотдир. Уларни чиқар деб ўйламаган эдингиз. Ўзлари ҳам, қўрғонимиз бизни Аллоҳ(азоби)дан сақлаб қолур, деб ўйлашарди. Аллоҳ улар ўйламаган тарафдан олди ва уларнинг қалбига қўрқинч солди. Уйларини ўз қўллари билан ва мўминларнинг қўллари билан буза бошладилар. Ибрат олинг, эй, ақл эгалари'}, {'chapter': 59, 'verse': 3, 'text': 'وَلَوۡلَآ أَن كَتَبَ ٱللَّهُ عَلَيۡهِمُ ٱلۡجَلَآءَ لَعَذَّبَهُمۡ فِي ٱلدُّنۡيَاۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابُ ٱلنَّارِ\nМабодо, Аллоҳ уларга кўчишни ёзмаганда ҳам, бу дунёда уларни азоблар эди. Ва уларга охиратда дўзах азоби бордур'}, {'chapter': 59, 'verse': 4, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ ٱللَّهَ وَرَسُولَهُۥۖ وَمَن يُشَآقِّ ٱللَّهَ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nУндай бўлиши улар Аллоҳга ва Унинг Расулига хилоф қилганлари учундир. Кимки Аллоҳга хилоф қилса (унутмасинки), бас, албатта, Аллоҳ азоби шиддатли Зотдир'}, {'chapter': 59, 'verse': 5, 'text': 'مَا قَطَعۡتُم مِّن لِّينَةٍ أَوۡ تَرَكۡتُمُوهَا قَآئِمَةً عَلَىٰٓ أُصُولِهَا فَبِإِذۡنِ ٱللَّهِ وَلِيُخۡزِيَ ٱلۡفَٰسِقِينَ\nСизнинг хурмони кесганингиз ёки аслида тик қолдирганингиз, фақат, Аллоҳнинг изни билан ва бузғунчиларни хорлаши учун бўлди'}, {'chapter': 59, 'verse': 6, 'text': 'وَمَآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡهُمۡ فَمَآ أَوۡجَفۡتُمۡ عَلَيۡهِ مِنۡ خَيۡلٖ وَلَا رِكَابٖ وَلَٰكِنَّ ٱللَّهَ يُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَن يَشَآءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАллоҳ Ўз Расулига улардан қайтариб берган нарсага сизлар от ёки туя чоптириб бормадингиз. Лекин Аллоҳ Ўз Пайғамбарини, кимнинг устидан хоҳласа, ғолиб қилади. Аллоҳ ҳар бир нарсага қодирдир'}, {'chapter': 59, 'verse': 7, 'text': 'مَّآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡ أَهۡلِ ٱلۡقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِ كَيۡ لَا يَكُونَ دُولَةَۢ بَيۡنَ ٱلۡأَغۡنِيَآءِ مِنكُمۡۚ وَمَآ ءَاتَىٰكُمُ ٱلرَّسُولُ فَخُذُوهُ وَمَا نَهَىٰكُمۡ عَنۡهُ فَٱنتَهُواْۚ وَٱتَّقُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nАллоҳ Ўз Расулига шаҳар-қишлоқ аҳлидан қайтариб берган нарса... Аллоҳгадир ва Унинг Расулига ва яқин қариндошларига, етимларга ва мискинларга ва кўча ўғилларигадир. Сизлардан бойларингиз орасида айланадиган нарса бўлиб қолмаслиги учун. Пайғамбар сизга нимани берса, ўшани олинглар ва нимадан қайтарса, ўшандан қайтинглар. Аллоҳдан қўрқинглар. Албатта, Аллоҳ азоби зўр Зотдир'}, {'chapter': 59, 'verse': 8, 'text': 'لِلۡفُقَرَآءِ ٱلۡمُهَٰجِرِينَ ٱلَّذِينَ أُخۡرِجُواْ مِن دِيَٰرِهِمۡ وَأَمۡوَٰلِهِمۡ يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗا وَيَنصُرُونَ ٱللَّهَ وَرَسُولَهُۥٓۚ أُوْلَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ\nЎз диёрларидан ва мол-мулкларидан жудо қилинган фақир муҳожирларгадир. Улар Аллоҳдан фазл ва Унинг розилигини умид қиладилар. Аллоҳга ва Унинг Расулига ёрдам берадилар. Ўшандоқ кишилар содиқдирлар'}, {'chapter': 59, 'verse': 9, 'text': 'وَٱلَّذِينَ تَبَوَّءُو ٱلدَّارَ وَٱلۡإِيمَٰنَ مِن قَبۡلِهِمۡ يُحِبُّونَ مَنۡ هَاجَرَ إِلَيۡهِمۡ وَلَا يَجِدُونَ فِي صُدُورِهِمۡ حَاجَةٗ مِّمَّآ أُوتُواْ وَيُؤۡثِرُونَ عَلَىٰٓ أَنفُسِهِمۡ وَلَوۡ كَانَ بِهِمۡ خَصَاصَةٞۚ وَمَن يُوقَ شُحَّ نَفۡسِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nУ(муҳожир)лардан аввал бу жой(Мадина)да яшаган ва иймонга (ихлос қилганлар). Улар юртларига ҳижрат қилиб келганларни севарлар ва уларга берилган нарсаларга қалбларида ҳасад қилмаслар. Ва гар ўзларининг ҳожатлари бўлса ҳам, уларни ўзларидан устун қўярлар. Ким ўз нафсининг бахиллигидан сақланса, ундоқ кишилар, ҳа, ўшалар ютувчилардир'}, {'chapter': 59, 'verse': 10, 'text': 'وَٱلَّذِينَ جَآءُو مِنۢ بَعۡدِهِمۡ يَقُولُونَ رَبَّنَا ٱغۡفِرۡ لَنَا وَلِإِخۡوَٰنِنَا ٱلَّذِينَ سَبَقُونَا بِٱلۡإِيمَٰنِ وَلَا تَجۡعَلۡ فِي قُلُوبِنَا غِلّٗا لِّلَّذِينَ ءَامَنُواْ رَبَّنَآ إِنَّكَ رَءُوفٞ رَّحِيمٌ\nУлардан кейин келганлар: «Эй Роббимиз, бизни ва биздан аввал иймон билан ўтган биродарларимизни мағфират қилгин, иймон келтирганларга қалбимизда нафрат (пайдо) қилмагин. Эй Роббимиз, албатта, Сен шафқатли ва меҳрибонсан», дерлар'}, {'chapter': 59, 'verse': 11, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ نَافَقُواْ يَقُولُونَ لِإِخۡوَٰنِهِمُ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ لَئِنۡ أُخۡرِجۡتُمۡ لَنَخۡرُجَنَّ مَعَكُمۡ وَلَا نُطِيعُ فِيكُمۡ أَحَدًا أَبَدٗا وَإِن قُوتِلۡتُمۡ لَنَنصُرَنَّكُمۡ وَٱللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ\nНифоқ қилганларнинг аҳли китобдан бўлган ўз биродарларига: «Агар сизлар (диёрларингиздан) қувилсангиз, биз ҳам сизлар билан кетамиз, сизни қўйиб ҳеч қачон ҳеч кимга итоат қилмасмиз ва мабодо сизларга уруш қилинса, албатта ёрдам берурмиз», дейишларини кўрмайсанми'}, {'chapter': 59, 'verse': 12, 'text': 'لَئِنۡ أُخۡرِجُواْ لَا يَخۡرُجُونَ مَعَهُمۡ وَلَئِن قُوتِلُواْ لَا يَنصُرُونَهُمۡ وَلَئِن نَّصَرُوهُمۡ لَيُوَلُّنَّ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يُنصَرُونَ\nАлбатта, агар у(яҳудий)лар чиқарилсалар, (мунофиқлар) улар билан бирга чиқмаслар. Ва, албатта, агар у(яҳудий)ларга (қарши) уруш қилинса, (мунофиқлар) ёрдам бермаслар. Борди-ю, ёрдам берганларида ҳам, ортларига қараб қочарлар ва улар ёрдамсиз қоларлар'}, {'chapter': 59, 'verse': 13, 'text': 'لَأَنتُمۡ أَشَدُّ رَهۡبَةٗ فِي صُدُورِهِم مِّنَ ٱللَّهِۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ\nАлбатта, уларнинг қалбларига Аллоҳдан кўра сизлар кўпроқ қўрқинчли туюласизлар. Чунки, улар яхши англамайдиган қавмдир'}, {'chapter': 59, 'verse': 14, 'text': 'لَا يُقَٰتِلُونَكُمۡ جَمِيعًا إِلَّا فِي قُرٗى مُّحَصَّنَةٍ أَوۡ مِن وَرَآءِ جُدُرِۭۚ بَأۡسُهُم بَيۡنَهُمۡ شَدِيدٞۚ تَحۡسَبُهُمۡ جَمِيعٗا وَقُلُوبُهُمۡ شَتَّىٰۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ\nСизларга қарши уюшиб, очиқ уруш қила олмаслар, балки қўрғон билан ўралган қишлоқларда ёки деворлар ортига бекиниб олиб уруш қилурлар. Ўз ораларида адоватлари шиддатли. Уларни бир деб ўйлайсан. Ҳолбуки, қалблари тарқоқдир. Бу ҳол уларнинг ақл юритмайдиган қавм эканликларидандир'}, {'chapter': 59, 'verse': 15, 'text': 'كَمَثَلِ ٱلَّذِينَ مِن قَبۡلِهِمۡ قَرِيبٗاۖ ذَاقُواْ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nБу, худди, яқинда ўз қилмишларининг оқибатини татиб кўрганларга ўхшашдир. Ва уларга (охиратда) аламли азоблар бор'}, {'chapter': 59, 'verse': 16, 'text': 'كَمَثَلِ ٱلشَّيۡطَٰنِ إِذۡ قَالَ لِلۡإِنسَٰنِ ٱكۡفُرۡ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيٓءٞ مِّنكَ إِنِّيٓ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَٰلَمِينَ\nБу, худди, шайтон инсонга, «кофир бўл», деган пайтга ўхшайдир. У куфр келтирганда эса: «Сен билан менинг ишим йўқ, мен оламлар Робби — Аллоҳдан қўрқаман», деди'}, {'chapter': 59, 'verse': 17, 'text': 'فَكَانَ عَٰقِبَتَهُمَآ أَنَّهُمَا فِي ٱلنَّارِ خَٰلِدَيۡنِ فِيهَاۚ وَذَٰلِكَ جَزَـٰٓؤُاْ ٱلظَّـٰلِمِينَ\nБас, уларнинг оқибатлари бир — албатта, икковлари ҳам дўзахда бўлурлар, у ерда абадий қолурлар. Ва бу золимларнинг жазосидир'}, {'chapter': 59, 'verse': 18, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَلۡتَنظُرۡ نَفۡسٞ مَّا قَدَّمَتۡ لِغَدٖۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nЭй, иймон келтирганлар! Аллоҳга тақво қилинглар! Ҳар ким эрталик кун учун нима қилганига назар солсин. Аллоҳга тақво қилинглар! Албатта, Аллоҳ нима қилаётганингиздан хабардордир'}, {'chapter': 59, 'verse': 19, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ نَسُواْ ٱللَّهَ فَأَنسَىٰهُمۡ أَنفُسَهُمۡۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nАллоҳни унутганларга ўхшаган бўлманглар. У зот уларга ўз шахсларини унуттирди. Ундоқ кишилар фосиқдирлар'}, {'chapter': 59, 'verse': 20, 'text': 'لَا يَسۡتَوِيٓ أَصۡحَٰبُ ٱلنَّارِ وَأَصۡحَٰبُ ٱلۡجَنَّةِۚ أَصۡحَٰبُ ٱلۡجَنَّةِ هُمُ ٱلۡفَآئِزُونَ\nОлов эгалари ва жаннат эгалари тенг эмаслар. Жаннат эгалари ютуққа эришганлардир'}, {'chapter': 59, 'verse': 21, 'text': 'لَوۡ أَنزَلۡنَا هَٰذَا ٱلۡقُرۡءَانَ عَلَىٰ جَبَلٖ لَّرَأَيۡتَهُۥ خَٰشِعٗا مُّتَصَدِّعٗا مِّنۡ خَشۡيَةِ ٱللَّهِۚ وَتِلۡكَ ٱلۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنَّاسِ لَعَلَّهُمۡ يَتَفَكَّرُونَ\nАгар ушбу Қуръонни тоққа туширганимизда эди, унинг Аллоҳдан қўрққанидан титраб-қақшаб, парчаланиб кетганини кўрар эдинг. Ушбу мисолларни одамларга келтирурмиз, шоядки, улар фикр юритсалар'}, {'chapter': 59, 'verse': 22, 'text': 'هُوَ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَۖ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِۖ هُوَ ٱلرَّحۡمَٰنُ ٱلرَّحِيمُ\nУ — Аллоҳ Ундан ўзга илоҳ йўқ Зотдир. У ғойибни ҳам, ошкорни ҳам билувчидир. У меҳрибон ва раҳимлидир'}, {'chapter': 59, 'verse': 23, 'text': 'هُوَ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡمَلِكُ ٱلۡقُدُّوسُ ٱلسَّلَٰمُ ٱلۡمُؤۡمِنُ ٱلۡمُهَيۡمِنُ ٱلۡعَزِيزُ ٱلۡجَبَّارُ ٱلۡمُتَكَبِّرُۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يُشۡرِكُونَ\nУ — Аллоҳ Ундан ўзга илоҳ йўқ Зотдир. У — Малик, У — Қуддус, У — Салом, У — Муъмин, У — Муҳаймин, У — Азийз, У — Жаббор, У — Мутакаббирдир. Аллоҳ улар ширк келтираётган нарсалардан покдир'}, {'chapter': 59, 'verse': 24, 'text': 'هُوَ ٱللَّهُ ٱلۡخَٰلِقُ ٱلۡبَارِئُ ٱلۡمُصَوِّرُۖ لَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ يُسَبِّحُ لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ — Аллоҳ — Холиқ, Бориъ, Мусоввирдур. Барча гўзал исмлар Уникидир. Осмонлару ердаги барча нарсалар Уни поклаб ёд этадилар. Яна У — Азийз ва Ҳакиймдир'}], '60': [{'chapter': 60, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ عَدُوِّي وَعَدُوَّكُمۡ أَوۡلِيَآءَ تُلۡقُونَ إِلَيۡهِم بِٱلۡمَوَدَّةِ وَقَدۡ كَفَرُواْ بِمَا جَآءَكُم مِّنَ ٱلۡحَقِّ يُخۡرِجُونَ ٱلرَّسُولَ وَإِيَّاكُمۡ أَن تُؤۡمِنُواْ بِٱللَّهِ رَبِّكُمۡ إِن كُنتُمۡ خَرَجۡتُمۡ جِهَٰدٗا فِي سَبِيلِي وَٱبۡتِغَآءَ مَرۡضَاتِيۚ تُسِرُّونَ إِلَيۡهِم بِٱلۡمَوَدَّةِ وَأَنَا۠ أَعۡلَمُ بِمَآ أَخۡفَيۡتُمۡ وَمَآ أَعۡلَنتُمۡۚ وَمَن يَفۡعَلۡهُ مِنكُمۡ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ\nЭй, иймон келтирганлар! Агар Менинг йўлимда жиҳод қилиб ва розилигимни сўраб чиққан бўлсангиз, Менинг душманларимни ва ўзингизнинг душманларингизни дўст тутманглар. Сизлар уларга дўстлик қиласизлар, улар бўлса, сизларга келган ҳаққа куфр келтирурлар. Роббингиз бўлмиш Аллоҳга иймон келтирганингиз учун, Пайғамбарни ва сизни ҳайдаб чиқарурлар. Сиз уларга дўстлик қилиб сир айтурсиз. Ҳолбуки, Мен нимани махфий тутганингизнию нимани ошкор қилганингизни жуда яхши билурман. Ораларингизда ким ўша ишни қилса, тўғри йўлдан адашган бўлур'}, {'chapter': 60, 'verse': 2, 'text': 'إِن يَثۡقَفُوكُمۡ يَكُونُواْ لَكُمۡ أَعۡدَآءٗ وَيَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ وَأَلۡسِنَتَهُم بِٱلسُّوٓءِ وَوَدُّواْ لَوۡ تَكۡفُرُونَ\nАгар улар сиздан устун келсалар, сизга душман бўлурлар ва сизларга қўлларию тиллари билан ёмонлик етказишга интилурлар. Ва сизларнинг кофир бўлишларингизни хоҳларлар'}, {'chapter': 60, 'verse': 3, 'text': 'لَن تَنفَعَكُمۡ أَرۡحَامُكُمۡ وَلَآ أَوۡلَٰدُكُمۡۚ يَوۡمَ ٱلۡقِيَٰمَةِ يَفۡصِلُ بَيۡنَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nҚариндошларингиз ва фарзандларингиз сизга ҳеч манфаат бера олмаслар. Қиёмат куни У зот ораларингизни бўладир. Аллоҳ нима қилаётганингизни кўриб тургувчидир'}, {'chapter': 60, 'verse': 4, 'text': 'قَدۡ كَانَتۡ لَكُمۡ أُسۡوَةٌ حَسَنَةٞ فِيٓ إِبۡرَٰهِيمَ وَٱلَّذِينَ مَعَهُۥٓ إِذۡ قَالُواْ لِقَوۡمِهِمۡ إِنَّا بُرَءَـٰٓؤُاْ مِنكُمۡ وَمِمَّا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ كَفَرۡنَا بِكُمۡ وَبَدَا بَيۡنَنَا وَبَيۡنَكُمُ ٱلۡعَدَٰوَةُ وَٱلۡبَغۡضَآءُ أَبَدًا حَتَّىٰ تُؤۡمِنُواْ بِٱللَّهِ وَحۡدَهُۥٓ إِلَّا قَوۡلَ إِبۡرَٰهِيمَ لِأَبِيهِ لَأَسۡتَغۡفِرَنَّ لَكَ وَمَآ أَمۡلِكُ لَكَ مِنَ ٱللَّهِ مِن شَيۡءٖۖ رَّبَّنَا عَلَيۡكَ تَوَكَّلۡنَا وَإِلَيۡكَ أَنَبۡنَا وَإِلَيۡكَ ٱلۡمَصِيرُ\nҲақиқатан, сизларга Иброҳим ва у билан бирга бўлганларда яхши ўрнак бор. Улар: «Биз сиздан ва сиз ибодат қилаётган нарсадан безормиз, сизга куфр келтирдик, энди, токи ягона Аллоҳга иймон келтирмагунларингизча, орамизда доимий адоват ва кучли нафрат пайдо бўлди», дедилар. Аммо, Иброҳимнинг отасига қарата: «Албатта, сен учун истиғфор айтаман. Сендан Аллоҳ(азоби)дан бирор нарса қайтаришга молик эмасман», деган сўзида (ўрнак йўқ). «Эй Роббимиз, фақат Сенга таваккал қилдик, фақат Сенгагина қайтдик ва қайтиб борадиган жой ҳам фақат Сенгагинадир»'}, {'chapter': 60, 'verse': 5, 'text': 'رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلَّذِينَ كَفَرُواْ وَٱغۡفِرۡ لَنَا رَبَّنَآۖ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\n«Эй Роббимиз, бизни кофирларга фитна қилиб қўймагин ва бизни кечиргин. Эй Роббимиз, албатта Сен, Ўзинг ўолибсан ва Ҳакиймсан»'}, {'chapter': 60, 'verse': 6, 'text': 'لَقَدۡ كَانَ لَكُمۡ فِيهِمۡ أُسۡوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرۡجُواْ ٱللَّهَ وَٱلۡيَوۡمَ ٱلۡأٓخِرَۚ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nАлбатта, кимнинг Аллоҳдан ва қиёмат кунидан умиди бўлса, шулар учун уларда яхши ўрнак бор эди. Лекин ким юз ўгирса (билиб қўйсинки), Аллоҳ беҳожат, мақталган Зотдир'}, {'chapter': 60, 'verse': 7, 'text': '۞عَسَى ٱللَّهُ أَن يَجۡعَلَ بَيۡنَكُمۡ وَبَيۡنَ ٱلَّذِينَ عَادَيۡتُم مِّنۡهُم مَّوَدَّةٗۚ وَٱللَّهُ قَدِيرٞۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nШоядки, Аллоҳ сизлар билан улар ичидаги душманларингиз ўртасида дўстлик пайдо қилса, Аллоҳ бунга қодирдир. Аллоҳ кечирувчи ва раҳиймлидир'}, {'chapter': 60, 'verse': 8, 'text': 'لَّا يَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ لَمۡ يُقَٰتِلُوكُمۡ فِي ٱلدِّينِ وَلَمۡ يُخۡرِجُوكُم مِّن دِيَٰرِكُمۡ أَن تَبَرُّوهُمۡ وَتُقۡسِطُوٓاْ إِلَيۡهِمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ\nАллоҳ сизларни диний уруш қилмаган ва диёрларингиздан қувиб чиқармаганларга яхшилик ва адолат қилишдан қайтармас. Албатта, Аллоҳ адолат қилувчиларни яхши кўрадир'}, {'chapter': 60, 'verse': 9, 'text': 'إِنَّمَا يَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ قَٰتَلُوكُمۡ فِي ٱلدِّينِ وَأَخۡرَجُوكُم مِّن دِيَٰرِكُمۡ وَظَٰهَرُواْ عَلَىٰٓ إِخۡرَاجِكُمۡ أَن تَوَلَّوۡهُمۡۚ وَمَن يَتَوَلَّهُمۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nАлбатта, Аллоҳ сизларни диний уруш қилувчилар, диёрингиздан чиқарувчилар ва чиқарилишингизга ёрдам берганларга дўстлик қилишдан қайтарадир. Уларни дўст тутганлар золим кишилардир'}, {'chapter': 60, 'verse': 10, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا جَآءَكُمُ ٱلۡمُؤۡمِنَٰتُ مُهَٰجِرَٰتٖ فَٱمۡتَحِنُوهُنَّۖ ٱللَّهُ أَعۡلَمُ بِإِيمَٰنِهِنَّۖ فَإِنۡ عَلِمۡتُمُوهُنَّ مُؤۡمِنَٰتٖ فَلَا تَرۡجِعُوهُنَّ إِلَى ٱلۡكُفَّارِۖ لَا هُنَّ حِلّٞ لَّهُمۡ وَلَا هُمۡ يَحِلُّونَ لَهُنَّۖ وَءَاتُوهُم مَّآ أَنفَقُواْۚ وَلَا جُنَاحَ عَلَيۡكُمۡ أَن تَنكِحُوهُنَّ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّۚ وَلَا تُمۡسِكُواْ بِعِصَمِ ٱلۡكَوَافِرِ وَسۡـَٔلُواْ مَآ أَنفَقۡتُمۡ وَلۡيَسۡـَٔلُواْ مَآ أَنفَقُواْۚ ذَٰلِكُمۡ حُكۡمُ ٱللَّهِ يَحۡكُمُ بَيۡنَكُمۡۖ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nЭй, иймон келтирганлар! Агар сизларга мўминалар ҳижрат қилиб келсалар, уларни имтиҳон қилиб кўринглар. Аллоҳ уларнинг иймонини билгувчидир. Бас, мўминаликларини билсангиз, уларни кофирларга қайтариб юборманг. Бу(аёл)лар уларга ҳалол эмас ва улар ҳам буларга ҳалол бўлмаслар. Ва у(кофир)ларга нафақа қилган нарсаларини беринглар. Ва агар у(аёл)ларни маҳрларини бериб, никоҳлаб олсангизлар, сизларга гуноҳ бўлмас. Кофир хотинларингизни никоҳларингизда ушлаб турманглар. Нафақа қилган нарсаларингизни сўранглар, улар ҳам нафақа қилган нарсаларини сўрасинлар. Бу, сизларга Аллоҳнинг ҳукмидир. У, орангизда ҳукм қиладир. Аллоҳ билгувчи ва ҳикматлидир'}, {'chapter': 60, 'verse': 11, 'text': 'وَإِن فَاتَكُمۡ شَيۡءٞ مِّنۡ أَزۡوَٰجِكُمۡ إِلَى ٱلۡكُفَّارِ فَعَاقَبۡتُمۡ فَـَٔاتُواْ ٱلَّذِينَ ذَهَبَتۡ أَزۡوَٰجُهُم مِّثۡلَ مَآ أَنفَقُواْۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ\nАгар аёлларингиздан бирортаси кофирларга ўтиб кетса, урушда туширилган ўлжадан хотини кетиб қолганга у нафақа қилган миқдорни берингиз. Ўзингиз иймон келтирган Аллоҳдан қўрқинг'}, {'chapter': 60, 'verse': 12, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِذَا جَآءَكَ ٱلۡمُؤۡمِنَٰتُ يُبَايِعۡنَكَ عَلَىٰٓ أَن لَّا يُشۡرِكۡنَ بِٱللَّهِ شَيۡـٔٗا وَلَا يَسۡرِقۡنَ وَلَا يَزۡنِينَ وَلَا يَقۡتُلۡنَ أَوۡلَٰدَهُنَّ وَلَا يَأۡتِينَ بِبُهۡتَٰنٖ يَفۡتَرِينَهُۥ بَيۡنَ أَيۡدِيهِنَّ وَأَرۡجُلِهِنَّ وَلَا يَعۡصِينَكَ فِي مَعۡرُوفٖ فَبَايِعۡهُنَّ وَٱسۡتَغۡفِرۡ لَهُنَّ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nЭй Набий! Агар мўминалар сенга Аллоҳга ҳеч нарсани ширк келтирмаслик, ўғрилик қилмаслик, зино қилмаслик, болаларини ўлдирмаслик, қўллари ва оёқлари орасидан бўҳтон тўқиб келтирмаслик ва сенга яхшилик ишда исён қилмасликка байъат қилиб келсалар, уларнинг байъатини қабул қил. Ва Аллоҳдан уларнинг гуноҳини кечишни сўра. Албатта, Аллоҳ гуноҳларни кечирувчи ва меҳрибондир'}, {'chapter': 60, 'verse': 13, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَوَلَّوۡاْ قَوۡمًا غَضِبَ ٱللَّهُ عَلَيۡهِمۡ قَدۡ يَئِسُواْ مِنَ ٱلۡأٓخِرَةِ كَمَا يَئِسَ ٱلۡكُفَّارُ مِنۡ أَصۡحَٰبِ ٱلۡقُبُورِ\nЭй, иймон келтирганлар! Аллоҳнинг ғазабига учраган қавмни дўст тутманглар. Кофирлар қабр эгаларидан умид узганларидек, улар ҳам охиратдан умид узганлар'}], '61': [{'chapter': 61, 'verse': 1, 'text': 'سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлару ердаги барча нарсалар Аллоҳга тасбиҳ айтади. У зот ғолиб ва ҳикмат эгасидир'}, {'chapter': 61, 'verse': 2, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِمَ تَقُولُونَ مَا لَا تَفۡعَلُونَ\nЭй, иймон келтирганлар! Нима учун қилмаган нарсаларингизни гапирасиз'}, {'chapter': 61, 'verse': 3, 'text': 'كَبُرَ مَقۡتًا عِندَ ٱللَّهِ أَن تَقُولُواْ مَا لَا تَفۡعَلُونَ\nҚилмайдиган нарсангизни гапиришингиз Аллоҳнинг наздида қаттиқ ёмон кўрилгандир'}, {'chapter': 61, 'verse': 4, 'text': 'إِنَّ ٱللَّهَ يُحِبُّ ٱلَّذِينَ يُقَٰتِلُونَ فِي سَبِيلِهِۦ صَفّٗا كَأَنَّهُم بُنۡيَٰنٞ مَّرۡصُوصٞ\nАлбатта, Аллоҳ Ўз йўлида худди жипс бинодек бўлиб, сафда туриб уруш қиладиганларни яхши кўрадир'}, {'chapter': 61, 'verse': 5, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ لِمَ تُؤۡذُونَنِي وَقَد تَّعۡلَمُونَ أَنِّي رَسُولُ ٱللَّهِ إِلَيۡكُمۡۖ فَلَمَّا زَاغُوٓاْ أَزَاغَ ٱللَّهُ قُلُوبَهُمۡۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\nМусо ўз қавмига: «Эй қавмим, Аллоҳнинг Расули эканимни билиб туриб яна нима учун менга озор берасиз», деганини эсла. Улар (ҳақдан) бурилган эдилар, Аллоҳ уларнинг қалбини буриб қўйди. Аллоҳ фосиқ қавмларни ҳидоят қилмас'}, {'chapter': 61, 'verse': 6, 'text': 'وَإِذۡ قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ إِنِّي رَسُولُ ٱللَّهِ إِلَيۡكُم مُّصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ ٱلتَّوۡرَىٰةِ وَمُبَشِّرَۢا بِرَسُولٖ يَأۡتِي مِنۢ بَعۡدِي ٱسۡمُهُۥٓ أَحۡمَدُۖ فَلَمَّا جَآءَهُم بِٱلۡبَيِّنَٰتِ قَالُواْ هَٰذَا سِحۡرٞ مُّبِينٞ\nИйсо ибн Марям: «Эй, Бани Исроил, албатта, мен ўзимдан олдин келган Тавротни тасдиқлаш ва мендан кейин келувчи Аҳмад исмли Расулнинг башоратини бериш учун Аллоҳ сизларга юборган Пайғамбарман», деганини эсла. Қачонки у(Аҳмад) уларга ҳақ билан келган вақтда эса, бу очиқ-ойдин сеҳрдир, дедилар'}, {'chapter': 61, 'verse': 7, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُوَ يُدۡعَىٰٓ إِلَى ٱلۡإِسۡلَٰمِۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nИсломга даъват қилиниб турганида Аллоҳга нисбатан ёлғон тўқиган кимсадан ҳам золимроқ киши борми? Ҳолбуки, Аллоҳ золимларни ҳидоятга солмас'}, {'chapter': 61, 'verse': 8, 'text': 'يُرِيدُونَ لِيُطۡفِـُٔواْ نُورَ ٱللَّهِ بِأَفۡوَٰهِهِمۡ وَٱللَّهُ مُتِمُّ نُورِهِۦ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ\nОғизлари билан Аллоҳнинг нурини ўчирмоқчи бўладилар. Ҳолбуки, Аллоҳ, кофирлар ёқтирмаса ҳам, ўз нурини тугал этгувчидир'}, {'chapter': 61, 'verse': 9, 'text': 'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ\nУ зот Ўз Расулини ҳидоят ва ҳақ дин билан барча динлардан устун қилиш учун юборган Зотдир. Гарчи мушриклар ёқтирмасалар ҳам'}, {'chapter': 61, 'verse': 10, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ هَلۡ أَدُلُّكُمۡ عَلَىٰ تِجَٰرَةٖ تُنجِيكُم مِّنۡ عَذَابٍ أَلِيمٖ\nЭй, иймон келтирганлар! Сизларни аламли азобдан нажот топтирувчи тижоратга далолат қилайми'}, {'chapter': 61, 'verse': 11, 'text': 'تُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ وَتُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nАллоҳга ва Унинг Расулига иймон келтирасизлар ҳамда Унинг йўлида молингиз ва жонингиз билан жиҳод қиласизлар. Шундоқ қилмоқларингиз, агар билсангизлар, ўзларингиз учун яхшидир'}, {'chapter': 61, 'verse': 12, 'text': 'يَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡ وَيُدۡخِلۡكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّـٰتِ عَدۡنٖۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nГуноҳларингизни кечирур ва сизларни адн жаннатидаги (дарахтлари) остидан анҳорлар оқиб турган боғларга ва яхши масканларга киритур. Бу нарса сиз учун улуғ ютуқдир'}, {'chapter': 61, 'verse': 13, 'text': 'وَأُخۡرَىٰ تُحِبُّونَهَاۖ نَصۡرٞ مِّنَ ٱللَّهِ وَفَتۡحٞ قَرِيبٞۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nВа сизлар яхши кўрадиган бошқаси ҳам бор. Аллоҳдан ёрдам ва яқин зафар. Ва мўминларга башорат бер'}, {'chapter': 61, 'verse': 14, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُوٓاْ أَنصَارَ ٱللَّهِ كَمَا قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ لِلۡحَوَارِيِّـۧنَ مَنۡ أَنصَارِيٓ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ ٱللَّهِۖ فَـَٔامَنَت طَّآئِفَةٞ مِّنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ وَكَفَرَت طَّآئِفَةٞۖ فَأَيَّدۡنَا ٱلَّذِينَ ءَامَنُواْ عَلَىٰ عَدُوِّهِمۡ فَأَصۡبَحُواْ ظَٰهِرِينَ\nЭй, иймон келтирганлар! Аллоҳнинг ёрдамчилари бўлинг! Ийсо ибн Марям ҳаворийларга: «Ким Аллоҳ учун менинг ёрдамчим(бўлади)?» деганида, ҳаворийлар: «Биз Аллоҳнинг ёрдамчиларимиз», дедилар. Бас, Бани Исроилдан бир тоифа иймон келтирди ва бир тоифа кофир бўлди. Бас, Биз иймон келтирганларни душманлари устидан қўллаб-қувватладик. Ва улар ғолиб бўлдилар'}], '62': [{'chapter': 62, 'verse': 1, 'text': 'يُسَبِّحُ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ ٱلۡمَلِكِ ٱلۡقُدُّوسِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nОсмонлару ердаги барча нарсалар Малик, Қуддус, Азийз ва Ҳакийм бўлган Аллоҳга тасбиҳ айтадир'}, {'chapter': 62, 'verse': 2, 'text': 'هُوَ ٱلَّذِي بَعَثَ فِي ٱلۡأُمِّيِّـۧنَ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٖ\nУ зот саводсизлар ичидан, гарчи улар бундан аввал очиқ залолатда бўлсалар ҳам, уларга оятларини ўқиб берадиган ва уларни поклайдиган ҳамда китобни ва ҳикматни ўргатадиган Пайғамбарни юборди'}, {'chapter': 62, 'verse': 3, 'text': 'وَءَاخَرِينَ مِنۡهُمۡ لَمَّا يَلۡحَقُواْ بِهِمۡۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nВа ҳозирча уларга қўшилмаган бошқаларга ҳам. Ва У зот ғолиб ва ҳикмат эгасидир'}, {'chapter': 62, 'verse': 4, 'text': 'ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nБу нарса Аллоҳнинг фазлидир (ва уни) хоҳлаган кишисига берадир. Аллоҳ улуғ фазл эгасидир'}, {'chapter': 62, 'verse': 5, 'text': 'مَثَلُ ٱلَّذِينَ حُمِّلُواْ ٱلتَّوۡرَىٰةَ ثُمَّ لَمۡ يَحۡمِلُوهَا كَمَثَلِ ٱلۡحِمَارِ يَحۡمِلُ أَسۡفَارَۢاۚ بِئۡسَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nУстларига Таврот юклатилган, сўнгра уни кўтармаганлар мисоли устига китоб юкланган эшакка ўхшарлар. Аллоҳнинг оятларини ёлғонга чиқарган қавмнинг мисоли қандоқ ҳам ёмон. Аллоҳ золим қавмларни ҳидоят қилмас'}, {'chapter': 62, 'verse': 6, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلَّذِينَ هَادُوٓاْ إِن زَعَمۡتُمۡ أَنَّكُمۡ أَوۡلِيَآءُ لِلَّهِ مِن دُونِ ٱلنَّاسِ فَتَمَنَّوُاْ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ\nСен: «Эй, яҳудий бўлганлар, албатта, сиз, бошқалардан фарқли ўлароқ, Аллоҳнинг авлиёларимиз деб даъво қиладиган бўлсангиз, бас, ўлимни орзу қилинг, агар ростгўй бўлсангиз», деб айт'}, {'chapter': 62, 'verse': 7, 'text': 'وَلَا يَتَمَنَّوۡنَهُۥٓ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nУлар қўллари қилган(гуноҳ)лари туфайли ҳеч қачон у(ўлим)ни орзу қилмаслар. Аллоҳ золимларни билувчидир'}, {'chapter': 62, 'verse': 8, 'text': 'قُلۡ إِنَّ ٱلۡمَوۡتَ ٱلَّذِي تَفِرُّونَ مِنۡهُ فَإِنَّهُۥ مُلَٰقِيكُمۡۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nСен: «Албатта, сиз ундан қочаётган ўлим, таъкид шулки, сизга барибир йўлиқувчидир. Сўнгра махфий ва ошкор нарсаларни Билувчининг ҳузурига қайтарилурсизлар. Бас, У сизга нима қилиб юрганингизнинг хабарини берадир», деб айт'}, {'chapter': 62, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نُودِيَ لِلصَّلَوٰةِ مِن يَوۡمِ ٱلۡجُمُعَةِ فَٱسۡعَوۡاْ إِلَىٰ ذِكۡرِ ٱللَّهِ وَذَرُواْ ٱلۡبَيۡعَۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nЭй, иймон келтирганлар! Жума кунида, намозга нидо қилинган пайтда, Аллоҳнинг зикрига шошилинг ва савдони қўйинг. Агар билсангиз, бу ўзингиз учун яхшидир'}, {'chapter': 62, 'verse': 10, 'text': 'فَإِذَا قُضِيَتِ ٱلصَّلَوٰةُ فَٱنتَشِرُواْ فِي ٱلۡأَرۡضِ وَٱبۡتَغُواْ مِن فَضۡلِ ٱللَّهِ وَٱذۡكُرُواْ ٱللَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ\nБас, намоз тугагандан сўнг ер юзи бўйлаб тарқалинг ва Аллоҳнинг фазлидан талаб қилинг ва Аллоҳни кўп эсланг, шоядки, ютуққа эришсангиз'}, {'chapter': 62, 'verse': 11, 'text': 'وَإِذَا رَأَوۡاْ تِجَٰرَةً أَوۡ لَهۡوًا ٱنفَضُّوٓاْ إِلَيۡهَا وَتَرَكُوكَ قَآئِمٗاۚ قُلۡ مَا عِندَ ٱللَّهِ خَيۡرٞ مِّنَ ٱللَّهۡوِ وَمِنَ ٱلتِّجَٰرَةِۚ وَٱللَّهُ خَيۡرُ ٱلرَّـٰزِقِينَ\nВа улар тижорат ёки ўйин-кулгу кўрганларида, унга томон ёпирилдилар ва сени тик ҳолда тарк этдилар. Сен: «Аллоҳнинг ҳузуридаги нарса ўйин-кулгудан ва тижоратдан яхшидир. Ва Аллоҳ энг яхши ризқ берувчидир», деб айт'}], '63': [{'chapter': 63, 'verse': 1, 'text': 'إِذَا جَآءَكَ ٱلۡمُنَٰفِقُونَ قَالُواْ نَشۡهَدُ إِنَّكَ لَرَسُولُ ٱللَّهِۗ وَٱللَّهُ يَعۡلَمُ إِنَّكَ لَرَسُولُهُۥ وَٱللَّهُ يَشۡهَدُ إِنَّ ٱلۡمُنَٰفِقِينَ لَكَٰذِبُونَ\nМунофиқлар ҳузурингга келганларида: «Гувоҳлик берамизки, сен, албатта, Аллоҳнинг Расулисан», дерлар. Ҳолбуки, Аллоҳ, албатта, сен Унинг Расули эканингни биладир. Ва Аллоҳ гувоҳлик берурки, албатта, мунофиқлар ёлғончидирлар'}, {'chapter': 63, 'verse': 2, 'text': 'ٱتَّخَذُوٓاْ أَيۡمَٰنَهُمۡ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ ٱللَّهِۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ\nУлар қасамларини қалқон қилиб олдилар ва Аллоҳнинг йўлидан тўсдилар. Бас, уларнинг қилган ишлари ёмон бўлди'}, {'chapter': 63, 'verse': 3, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ ءَامَنُواْ ثُمَّ كَفَرُواْ فَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ\nБунга сабаб шуки, улар иймон келтирдилар, сўнг кофир бўлдилар, бас, қалбларига муҳр босилди. Энди улар фаҳмламаслар'}, {'chapter': 63, 'verse': 4, 'text': '۞وَإِذَا رَأَيۡتَهُمۡ تُعۡجِبُكَ أَجۡسَامُهُمۡۖ وَإِن يَقُولُواْ تَسۡمَعۡ لِقَوۡلِهِمۡۖ كَأَنَّهُمۡ خُشُبٞ مُّسَنَّدَةٞۖ يَحۡسَبُونَ كُلَّ صَيۡحَةٍ عَلَيۡهِمۡۚ هُمُ ٱلۡعَدُوُّ فَٱحۡذَرۡهُمۡۚ قَٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ يُؤۡفَكُونَ\nВа агар уларни кўрсанг, жисмлари сени ажаблантирур ва агар гапирсалар, гапларига қулоқ осарсан. Улар худди суяб қўйилган ходага ўхшарлар. Ҳар қичқириқни зиддиларига ҳисобларлар. Улар душмандирлар. Улардан ҳазир бўл. Аллоҳ уларга уруш очсин, ҳақдан қандоқ ўгирилурлар'}, {'chapter': 63, 'verse': 5, 'text': 'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ يَسۡتَغۡفِرۡ لَكُمۡ رَسُولُ ٱللَّهِ لَوَّوۡاْ رُءُوسَهُمۡ وَرَأَيۡتَهُمۡ يَصُدُّونَ وَهُم مُّسۡتَكۡبِرُونَ\nВа агар уларга: «Келинглар, Аллоҳнинг Расули сизларга истиғфор айтур», дейилса, улар бошларини чайқарлар. Ва уларнинг мутакаббирлик ҳолида юз ўгираётганларини кўрурсан'}, {'chapter': 63, 'verse': 6, 'text': 'سَوَآءٌ عَلَيۡهِمۡ أَسۡتَغۡفَرۡتَ لَهُمۡ أَمۡ لَمۡ تَسۡتَغۡفِرۡ لَهُمۡ لَن يَغۡفِرَ ٱللَّهُ لَهُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\nУлар учун истиғфор айтасанми ёки айтмайсанми — бари бир. Аллоҳ уларни ҳеч мағфират қилмас. Зеро, Аллоҳ фосиқ қавмларни ҳидоят қилмас'}, {'chapter': 63, 'verse': 7, 'text': 'هُمُ ٱلَّذِينَ يَقُولُونَ لَا تُنفِقُواْ عَلَىٰ مَنۡ عِندَ رَسُولِ ٱللَّهِ حَتَّىٰ يَنفَضُّواْۗ وَلِلَّهِ خَزَآئِنُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَٰكِنَّ ٱلۡمُنَٰفِقِينَ لَا يَفۡقَهُونَ\nУлар: «Расулуллоҳнинг ҳузуридагиларга нафақа берманглар, ҳаттоки, тарқаб кетсинлар», дерлар. Ҳолбуки, осмонлару ернинг хазийналари Аллоҳникидур. Ва лекин мунофиқлар тушунмаслар'}, {'chapter': 63, 'verse': 8, 'text': 'يَقُولُونَ لَئِن رَّجَعۡنَآ إِلَى ٱلۡمَدِينَةِ لَيُخۡرِجَنَّ ٱلۡأَعَزُّ مِنۡهَا ٱلۡأَذَلَّۚ وَلِلَّهِ ٱلۡعِزَّةُ وَلِرَسُولِهِۦ وَلِلۡمُؤۡمِنِينَ وَلَٰكِنَّ ٱلۡمُنَٰفِقِينَ لَا يَعۡلَمُونَ\nУлар: «Агар Мадинага қайтиб борсак, албатта, азиз хорни ундан чиқарур», дерлар. Ҳолбуки, азизлик Аллоҳга, Унинг Расулига ва мўминларига хосдир. Аммо мунофиқлар билмаслар'}, {'chapter': 63, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُلۡهِكُمۡ أَمۡوَٰلُكُمۡ وَلَآ أَوۡلَٰدُكُمۡ عَن ذِكۡرِ ٱللَّهِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nЭй, иймон келтирганлар! Молларингиз ва болаларингиз сизни Аллоҳнинг зикридан чалғитмасин. Ким шундай қилса, бас, ана ўшалар ютқазганлардир'}, {'chapter': 63, 'verse': 10, 'text': 'وَأَنفِقُواْ مِن مَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ أَحَدَكُمُ ٱلۡمَوۡتُ فَيَقُولَ رَبِّ لَوۡلَآ أَخَّرۡتَنِيٓ إِلَىٰٓ أَجَلٖ قَرِيبٖ فَأَصَّدَّقَ وَأَكُن مِّنَ ٱلصَّـٰلِحِينَ\nВа бирингизга ўлим келиб: «Эй Роббим, агар менинг ўлимимни яқин муддатга орқага сурсанг, бас, садақа қилиб солиҳлардан бўлсам», демасдан аввал, Биз сизларга ризқ қилиб берган нарсалардан нафақа қилинг'}, {'chapter': 63, 'verse': 11, 'text': 'وَلَن يُؤَخِّرَ ٱللَّهُ نَفۡسًا إِذَا جَآءَ أَجَلُهَاۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nАллоҳ ажали келган жонни ҳеч-ҳеч орқага сурмас. Ва, Аллоҳ нима қилаётганингиздан хабардордир'}], '64': [{'chapter': 64, 'verse': 1, 'text': 'يُسَبِّحُ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ لَهُ ٱلۡمُلۡكُ وَلَهُ ٱلۡحَمۡدُۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлардаги нарсалар ва ердаги нарсалар Аллоҳга тасбиҳ айтурлар. Мулк Унга хосдир, мақтов ҳам Унга хосдир ва У барча нарсага қодирдир'}, {'chapter': 64, 'verse': 2, 'text': 'هُوَ ٱلَّذِي خَلَقَكُمۡ فَمِنكُمۡ كَافِرٞ وَمِنكُم مُّؤۡمِنٞۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ\nУ, сизларни яратган Зотдир. Бас, баъзиларингиз кофир ва баъзиларингиз мўминдир. Аллоҳ нима қилаётганингизни кўриб турувчидир'}, {'chapter': 64, 'verse': 3, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡۖ وَإِلَيۡهِ ٱلۡمَصِيرُ\nУ, осмонлару ерни ҳақ билан яратди. Ва қиёфангизни чиройли сувратда қилди. Ва қайтиб бориш Унинг Ўзигадир'}, {'chapter': 64, 'verse': 4, 'text': 'يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَيَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَۚ وَٱللَّهُ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nУ, осмонлару ердаги нарсаларни биладир. Сизлар сир тутаётган нарсани ҳам ва эълон қилаётган нарсани ҳам биладир. Аллоҳ кўкракдаги нарсаларни ҳам билувчидир'}, {'chapter': 64, 'verse': 5, 'text': 'أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَبۡلُ فَذَاقُواْ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nСизларга олдин кофир бўлганларнинг хабари келмадими? Бас, улар ўз ишларининг азобини татидилар ва уларга аламли азоблар бор'}, {'chapter': 64, 'verse': 6, 'text': 'ذَٰلِكَ بِأَنَّهُۥ كَانَت تَّأۡتِيهِمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَقَالُوٓاْ أَبَشَرٞ يَهۡدُونَنَا فَكَفَرُواْ وَتَوَلَّواْۖ وَّٱسۡتَغۡنَى ٱللَّهُۚ وَٱللَّهُ غَنِيٌّ حَمِيدٞ\nБунинг боиси шуки, уларга Пайғамбарлари ойдин далиллар билан келардилар, улар бўлса, бизни башар ҳидоятга бошларми, деб куфрга кетардилар ва (ҳақдан) юз ўгирардилар. Ва Аллоҳ беҳожат бўлди. Аллоҳ беҳожат ва мақталгандир'}, {'chapter': 64, 'verse': 7, 'text': 'زَعَمَ ٱلَّذِينَ كَفَرُوٓاْ أَن لَّن يُبۡعَثُواْۚ قُلۡ بَلَىٰ وَرَبِّي لَتُبۡعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلۡتُمۡۚ وَذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nКофирлар ҳеч қайта тирилмасликларини даъво қилурлар. Сен: «Ундоқ эмас, Роббим билан қасамки, албатта қайта тирилтириласизлар, сўнгра қилган ишларингиздан хабардор қилинасизлар. Бу иш Аллоҳ учун осондир», деб айт'}, {'chapter': 64, 'verse': 8, 'text': 'فَـَٔامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَٱلنُّورِ ٱلَّذِيٓ أَنزَلۡنَاۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nБас, Аллоҳга, Унинг Расулига ва Биз туширган нурга иймон келтиринглар. Аллоҳ нима қилаётганингиздан хабардордир'}, {'chapter': 64, 'verse': 9, 'text': 'يَوۡمَ يَجۡمَعُكُمۡ لِيَوۡمِ ٱلۡجَمۡعِۖ ذَٰلِكَ يَوۡمُ ٱلتَّغَابُنِۗ وَمَن يُؤۡمِنۢ بِٱللَّهِ وَيَعۡمَلۡ صَٰلِحٗا يُكَفِّرۡ عَنۡهُ سَيِّـَٔاتِهِۦ وَيُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nУ сизларни «Жамлаш куни»га тўплайдиган кунни эсланг. У кун «тағобун» кунидир. Ким Аллоҳга иймон келтирса ва солиҳ амаллар қилса, унинг гуноҳларини кечирур ва (дарахтлари) остидан анҳорлар оқиб турган жаннатларга киритур. Унда абадул-абад қолурлар. Бу улкан ютуқдир'}, {'chapter': 64, 'verse': 10, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِ خَٰلِدِينَ فِيهَاۖ وَبِئۡسَ ٱلۡمَصِيرُ\nКуфр келтириб, Бизнинг оятларимизни ёлғонга чиқарганлар, ана ўшалар оташ эгаларидир. Унда абадий қолурлар. Қандоқ ҳам ёмон жой у'}, {'chapter': 64, 'verse': 11, 'text': 'مَآ أَصَابَ مِن مُّصِيبَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ وَمَن يُؤۡمِنۢ بِٱللَّهِ يَهۡدِ قَلۡبَهُۥۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\nСизга Аллоҳнинг изнисиз ҳеч бир мусийбат етмас. Ким Аллоҳга иймон келтирса, У зот унинг қалбини ҳидоятга солур. Аллоҳ ҳар бир нарсани билувчидир'}, {'chapter': 64, 'verse': 12, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۚ فَإِن تَوَلَّيۡتُمۡ فَإِنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАллоҳга итоат қилинг ва Расулга итоат қилинг. Агар юз ўгириб кетсангиз, бас, Бизнинг Расулимизга (вазифа) очиқ-ойдин етказиш, холос'}, {'chapter': 64, 'verse': 13, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nАллоҳ, Ундан ўзга ибодатга сазовор зот йўқ. Бас, мўминлар Аллоҳнинг Ўзигагина таваккал қилсинлар'}, {'chapter': 64, 'verse': 14, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ مِنۡ أَزۡوَٰجِكُمۡ وَأَوۡلَٰدِكُمۡ عَدُوّٗا لَّكُمۡ فَٱحۡذَرُوهُمۡۚ وَإِن تَعۡفُواْ وَتَصۡفَحُواْ وَتَغۡفِرُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nЭй, иймон келтирганлар! Албатта, жуфти ҳалолларингиздан ва болаларингиздан сизга душман бор, бас, улардан ҳазир бўлинг. Ва агар афв этсангиз, кўнгилга олмасангиз ва айбларини кечирсангиз... Бас, албатта, Аллоҳ кўп мағфират қилувчи ва раҳимлидир'}, {'chapter': 64, 'verse': 15, 'text': 'إِنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞۚ وَٱللَّهُ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ\nАлбатта, мол-мулкларингиз ва фарзандларингиз синовдан бошқа нарса эмас. Ва Аллоҳнинг ҳузурида улуғ ажр бор'}, {'chapter': 64, 'verse': 16, 'text': 'فَٱتَّقُواْ ٱللَّهَ مَا ٱسۡتَطَعۡتُمۡ وَٱسۡمَعُواْ وَأَطِيعُواْ وَأَنفِقُواْ خَيۡرٗا لِّأَنفُسِكُمۡۗ وَمَن يُوقَ شُحَّ نَفۡسِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nАллоҳга қўлингиздан келганича тақво қилинг ва (амрини) тингланг ва итоат қилинг ва инфоқ қилинг — ўзингизга яхши бўладир. Ва кимки нафсининг қизғанчиқлигидан сақланса, ундайлар нажот топувчилардир'}, {'chapter': 64, 'verse': 17, 'text': 'إِن تُقۡرِضُواْ ٱللَّهَ قَرۡضًا حَسَنٗا يُضَٰعِفۡهُ لَكُمۡ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ شَكُورٌ حَلِيمٌ\nАгар Аллоҳга яхши қарз берсангиз, У сизга кўпайтириб берур ва сизни кечирур. Аллоҳ шакур ва ҳалиймдир'}, {'chapter': 64, 'verse': 18, 'text': 'عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ ғойиб ва очиқни билувчи, азийз ва ҳакийм Зотдир'}], '65': [{'chapter': 65, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحۡصُواْ ٱلۡعِدَّةَۖ وَٱتَّقُواْ ٱللَّهَ رَبَّكُمۡۖ لَا تُخۡرِجُوهُنَّ مِنۢ بُيُوتِهِنَّ وَلَا يَخۡرُجۡنَ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَتِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ لَا تَدۡرِي لَعَلَّ ٱللَّهَ يُحۡدِثُ بَعۡدَ ذَٰلِكَ أَمۡرٗا\nЭй Набий! Агар аёлларни талоқ қиладиган бўлсангиз, иддалари учун қилинг ва иддани ҳисобланг ва Аллоҳга тақво қилинг. Уларни уйларидан чиқарманглар ва ўзлари ҳам чиқмасинлар, илло, очиқ-ойдин фоҳиша иш қилган бўлсалар (чиқарилурлар) ва ана шулар Аллоҳнинг чегараларидир. Ким Аллоҳнинг чегарасидан чиқса, ҳақиқатан, ўзига ўзи зулм қилибди. Сен билмассан, шоядки, Аллоҳ ундан кейин бирор ишни пайдо қилса'}, {'chapter': 65, 'verse': 2, 'text': 'فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ فَارِقُوهُنَّ بِمَعۡرُوفٖ وَأَشۡهِدُواْ ذَوَيۡ عَدۡلٖ مِّنكُمۡ وَأَقِيمُواْ ٱلشَّهَٰدَةَ لِلَّهِۚ ذَٰلِكُمۡ يُوعَظُ بِهِۦ مَن كَانَ يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ وَمَن يَتَّقِ ٱللَّهَ يَجۡعَل لَّهُۥ مَخۡرَجٗا\nВа улар муддатларига етсалар, яхшилик билан ушланглар, ёки яхшилик билан ажралинглар ва сиздан икки адолатли кишини гувоҳ қилинглар. Ва гувоҳликни Аллоҳ учун қилинглар. Бу ишингиздан, кимнинг Аллоҳга ва қиёмат кунига иймони бўлса, ваъзланур. Ким Аллоҳга тақво қилса, унинг йўлини очиб қўядир'}, {'chapter': 65, 'verse': 3, 'text': 'وَيَرۡزُقۡهُ مِنۡ حَيۡثُ لَا يَحۡتَسِبُۚ وَمَن يَتَوَكَّلۡ عَلَى ٱللَّهِ فَهُوَ حَسۡبُهُۥٓۚ إِنَّ ٱللَّهَ بَٰلِغُ أَمۡرِهِۦۚ قَدۡ جَعَلَ ٱللَّهُ لِكُلِّ شَيۡءٖ قَدۡرٗا\nВа унга ўзи ўйламаган тарафдан ризқ берур. Ким Аллоҳга таваккал қилса, бас, унга У зотнинг Ўзи кифоядир. Албатта, Аллоҳ Ўз ишини етказувчидир. Ҳақиқатан, Аллоҳ ҳар бир нарсага ўлчов қилиб қўйгандир'}, {'chapter': 65, 'verse': 4, 'text': 'وَٱلَّـٰٓـِٔي يَئِسۡنَ مِنَ ٱلۡمَحِيضِ مِن نِّسَآئِكُمۡ إِنِ ٱرۡتَبۡتُمۡ فَعِدَّتُهُنَّ ثَلَٰثَةُ أَشۡهُرٖ وَٱلَّـٰٓـِٔي لَمۡ يَحِضۡنَۚ وَأُوْلَٰتُ ٱلۡأَحۡمَالِ أَجَلُهُنَّ أَن يَضَعۡنَ حَمۡلَهُنَّۚ وَمَن يَتَّقِ ٱللَّهَ يَجۡعَل لَّهُۥ مِنۡ أَمۡرِهِۦ يُسۡرٗا\nАёлларингиздан ҳайздан ноумид бўлганларининг иддаси, агар шубҳангиз бўлса, уч ойдир. Ҳайз кўрмаганлариники ҳам. Ҳомиладорларнинг иддаси ҳомилаларини қўймоқликдир. Ким Аллоҳга тақво қилса, У зот унинг ишида осонлик қилиб берадир'}, {'chapter': 65, 'verse': 5, 'text': 'ذَٰلِكَ أَمۡرُ ٱللَّهِ أَنزَلَهُۥٓ إِلَيۡكُمۡۚ وَمَن يَتَّقِ ٱللَّهَ يُكَفِّرۡ عَنۡهُ سَيِّـَٔاتِهِۦ وَيُعۡظِمۡ لَهُۥٓ أَجۡرًا\nБулар Аллоҳнинг сизга туширган амридир. Ким Аллоҳга тақво қилса, унинг гуноҳларини кечирур ва ажрини кўпайтириб берур'}, {'chapter': 65, 'verse': 6, 'text': 'أَسۡكِنُوهُنَّ مِنۡ حَيۡثُ سَكَنتُم مِّن وُجۡدِكُمۡ وَلَا تُضَآرُّوهُنَّ لِتُضَيِّقُواْ عَلَيۡهِنَّۚ وَإِن كُنَّ أُوْلَٰتِ حَمۡلٖ فَأَنفِقُواْ عَلَيۡهِنَّ حَتَّىٰ يَضَعۡنَ حَمۡلَهُنَّۚ فَإِنۡ أَرۡضَعۡنَ لَكُمۡ فَـَٔاتُوهُنَّ أُجُورَهُنَّ وَأۡتَمِرُواْ بَيۡنَكُم بِمَعۡرُوفٖۖ وَإِن تَعَاسَرۡتُمۡ فَسَتُرۡضِعُ لَهُۥٓ أُخۡرَىٰ\nУ(аёл)ларга, имконингиз борича, ўзингиз яшаб турган жойдан жой беринг. Уларга сиқиштириш мақсадида зарар етказманг. Агар ҳомиладор бўлсалар, то ҳомилаларини қўйгунларича уларга нафақа беринглар. Агар сизга (фарзандни) эмизиб берсалар, ҳақларини беринглар, ўзаро яхшилик билан маслаҳат қилинглар. Агар қийинчиликка учрасангиз, у(эр)га бошқа аёл эмизиб берур'}, {'chapter': 65, 'verse': 7, 'text': 'لِيُنفِقۡ ذُو سَعَةٖ مِّن سَعَتِهِۦۖ وَمَن قُدِرَ عَلَيۡهِ رِزۡقُهُۥ فَلۡيُنفِقۡ مِمَّآ ءَاتَىٰهُ ٱللَّهُۚ لَا يُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا مَآ ءَاتَىٰهَاۚ سَيَجۡعَلُ ٱللَّهُ بَعۡدَ عُسۡرٖ يُسۡرٗا\nРизқи кенг кенглигидан нафақа қилсин. Кимнинг ризқи тор бўлса, унга Аллоҳ берганидан нафақа қилсин. Аллоҳ ҳар бир нафсни Ўзи берганидан ортиқчага таклиф қилмас. Аллоҳ қийинчиликдан сўнг тезда осонликни берур'}, {'chapter': 65, 'verse': 8, 'text': 'وَكَأَيِّن مِّن قَرۡيَةٍ عَتَتۡ عَنۡ أَمۡرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبۡنَٰهَا حِسَابٗا شَدِيدٗا وَعَذَّبۡنَٰهَا عَذَابٗا نُّكۡرٗا\nКўпгина қишлоқлар ўз Роббисининг ва Унинг Расулларининг амрига саркашлик қилди. Бас, Биз уларни шиддатли ҳисобга олдик ва қаттиқ азоб ила азобладик'}, {'chapter': 65, 'verse': 9, 'text': 'فَذَاقَتۡ وَبَالَ أَمۡرِهَا وَكَانَ عَٰقِبَةُ أَمۡرِهَا خُسۡرًا\nБас, улар ўз ишларининг ёмонлигини тотиб кўрдилар ва ишларининг оқибати ютқизиқ бўлди'}, {'chapter': 65, 'verse': 10, 'text': 'أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابٗا شَدِيدٗاۖ فَٱتَّقُواْ ٱللَّهَ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ ٱلَّذِينَ ءَامَنُواْۚ قَدۡ أَنزَلَ ٱللَّهُ إِلَيۡكُمۡ ذِكۡرٗا\nАллоҳ уларга қаттиқ азобни тайёрлаб қўйди. Бас, Аллоҳга тақво қилинг, эй, иймон келтирган ақл эгалари. Ҳақиқатан, Аллоҳ сизларга зикрни туширди'}, {'chapter': 65, 'verse': 11, 'text': 'رَّسُولٗا يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِ ٱللَّهِ مُبَيِّنَٰتٖ لِّيُخۡرِجَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَمَن يُؤۡمِنۢ بِٱللَّهِ وَيَعۡمَلۡ صَٰلِحٗا يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ قَدۡ أَحۡسَنَ ٱللَّهُ لَهُۥ رِزۡقًا\nПайғамбарки, иймон келтириб ва яхши амалларни қилганларни зулматлардан нурга чиқариш учун, сизларга Аллоҳнинг равшан оятларини тиловат қилиб берур. Ва ким Аллоҳга иймон келтирса ва яхши амалларни қилса, У зот уни (дарахтлари) остидан анҳорлар оқиб турган жаннатларга киритур. У ерда абадул-абад қолурлар. Ҳақиқатан, Аллоҳ унинг ризқини яхши қилгандир'}, {'chapter': 65, 'verse': 12, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَ سَبۡعَ سَمَٰوَٰتٖ وَمِنَ ٱلۡأَرۡضِ مِثۡلَهُنَّۖ يَتَنَزَّلُ ٱلۡأَمۡرُ بَيۡنَهُنَّ لِتَعۡلَمُوٓاْ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ وَأَنَّ ٱللَّهَ قَدۡ أَحَاطَ بِكُلِّ شَيۡءٍ عِلۡمَۢا\nАллоҳ етти осмонни ва улар мислидек ерни яратган Зотдир. Уларнинг орасидан У зотнинг амри тушадир. Аллоҳ ҳар бир нарсага қодир эканини ва, албатта, Аллоҳ ҳар бир нарсани Ўз илми ила ўраб олганини билишингиз учундир'}], '66': [{'chapter': 66, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ لِمَ تُحَرِّمُ مَآ أَحَلَّ ٱللَّهُ لَكَۖ تَبۡتَغِي مَرۡضَاتَ أَزۡوَٰجِكَۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭй Набий! Нима учун хотинларинг розилигини тилаб, Аллоҳ сенга ҳалол қилган нарсани ҳаром қилурсан! Аллоҳ кўп мағфират қилувчи ва раҳимлидир'}, {'chapter': 66, 'verse': 2, 'text': 'قَدۡ فَرَضَ ٱللَّهُ لَكُمۡ تَحِلَّةَ أَيۡمَٰنِكُمۡۚ وَٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\nБатаҳқиқ, Аллоҳ сизларга қасамларингизни бўшатишни шариатга киритди. Ва Аллоҳ сизнинг хожангиздир ва У ўта билувчи ва ўта ҳикматлидир'}, {'chapter': 66, 'verse': 3, 'text': 'وَإِذۡ أَسَرَّ ٱلنَّبِيُّ إِلَىٰ بَعۡضِ أَزۡوَٰجِهِۦ حَدِيثٗا فَلَمَّا نَبَّأَتۡ بِهِۦ وَأَظۡهَرَهُ ٱللَّهُ عَلَيۡهِ عَرَّفَ بَعۡضَهُۥ وَأَعۡرَضَ عَنۢ بَعۡضٖۖ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتۡ مَنۡ أَنۢبَأَكَ هَٰذَاۖ قَالَ نَبَّأَنِيَ ٱلۡعَلِيمُ ٱلۡخَبِيرُ\nНабий хотинларидан баъзисига сир гап айтган эди. Бас, у (сир)дан хабар берди ва Аллоҳ у(иш)ни у(Набий)га билдирди. У баъзисини билдирди ва баъзисини эсламади. Қачон у(Набий) ул(аёл)га (иш) ҳақида хабар берганида, у: «Буни сенга ким хабар қилди», деди. У(Набий): «Менга ҳамма нарсани билувчи ва ҳамма нарсадан хабардор Аллоҳ хабар берди», деди'}, {'chapter': 66, 'verse': 4, 'text': 'إِن تَتُوبَآ إِلَى ٱللَّهِ فَقَدۡ صَغَتۡ قُلُوبُكُمَاۖ وَإِن تَظَٰهَرَا عَلَيۡهِ فَإِنَّ ٱللَّهَ هُوَ مَوۡلَىٰهُ وَجِبۡرِيلُ وَصَٰلِحُ ٱلۡمُؤۡمِنِينَۖ وَٱلۡمَلَـٰٓئِكَةُ بَعۡدَ ذَٰلِكَ ظَهِيرٌ\nАгар икковингиз Аллоҳга тавба қилсангиз, батаҳқиқ, қалбларингиз (ҳаққа) мойил бўлур. Агар унга қарши чиқсангиз, бас, албатта, Аллоҳ унинг ёрдамчисидир ва Жаброил ҳам, солиҳ мўминлар ҳам. Ундан сўнг фаришталар унга суянчиқдир'}, {'chapter': 66, 'verse': 5, 'text': 'عَسَىٰ رَبُّهُۥٓ إِن طَلَّقَكُنَّ أَن يُبۡدِلَهُۥٓ أَزۡوَٰجًا خَيۡرٗا مِّنكُنَّ مُسۡلِمَٰتٖ مُّؤۡمِنَٰتٖ قَٰنِتَٰتٖ تَـٰٓئِبَٰتٍ عَٰبِدَٰتٖ سَـٰٓئِحَٰتٖ ثَيِّبَٰتٖ وَأَبۡكَارٗا\nУ сизларни талоқ қилса, шоядки, Робби сиздан яхшироқ, муслима, мўмина, итоатли, тавбачи, ибодатли, рўзадор жувон ва қизларни ўрнингизга хотин қилиб берса'}, {'chapter': 66, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ قُوٓاْ أَنفُسَكُمۡ وَأَهۡلِيكُمۡ نَارٗا وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُ عَلَيۡهَا مَلَـٰٓئِكَةٌ غِلَاظٞ شِدَادٞ لَّا يَعۡصُونَ ٱللَّهَ مَآ أَمَرَهُمۡ وَيَفۡعَلُونَ مَا يُؤۡمَرُونَ\nЭй, иймон келтирганлар! Ўзингизни ва аҳли аёлингизни ёқилғиси одамлару тошдан бўлган ўтдан сақланг. Унинг тепасида қўпол, дарғазаб фаришталар бўлиб, улар Аллоҳнинг амрига исён қилмаслар ва нимага буюрилсалар, шуни қилурлар'}, {'chapter': 66, 'verse': 7, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ كَفَرُواْ لَا تَعۡتَذِرُواْ ٱلۡيَوۡمَۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\nЭй, куфр келтирганлар! Бугунги кунда узр айтманглар, сиз ўзингиз қилган ишларнинг жазосини оляпсиз, холос'}, {'chapter': 66, 'verse': 8, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ تُوبُوٓاْ إِلَى ٱللَّهِ تَوۡبَةٗ نَّصُوحًا عَسَىٰ رَبُّكُمۡ أَن يُكَفِّرَ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيُدۡخِلَكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ يَوۡمَ لَا يُخۡزِي ٱللَّهُ ٱلنَّبِيَّ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥۖ نُورُهُمۡ يَسۡعَىٰ بَيۡنَ أَيۡدِيهِمۡ وَبِأَيۡمَٰنِهِمۡ يَقُولُونَ رَبَّنَآ أَتۡمِمۡ لَنَا نُورَنَا وَٱغۡفِرۡ لَنَآۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЭй, иймон келтирганлар! Аллоҳга холис тавба қилинглар! Шоядки, Роббингиз гуноҳларингизни ювса ва сизни (дарахтлари) остидан анҳорлар оқиб турган жаннатларга киритса. Бир кундаки, унда Аллоҳ Набийни ва у билан бирга бўлган иймон келтирганларни шарманда қилмас. Уларнинг нурлари олдиларида ва ўнг тарафларида юриб борадир. Улар эса: «Роббимиз, бизга нуримизни батамом қилиб бер, гуноҳларимизни кечир, албатта, Сен барча нарсага қодирсан», дерлар'}, {'chapter': 66, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ جَٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَٰفِقِينَ وَٱغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nЭй Набий! Кофир ва мунофиқларга қарши жиҳод қил ва уларга маҳкам тур. Уларнинг жойи жаҳаннамдир ва у жой қандоқ ҳам ёмон манзил'}, {'chapter': 66, 'verse': 10, 'text': 'ضَرَبَ ٱللَّهُ مَثَلٗا لِّلَّذِينَ كَفَرُواْ ٱمۡرَأَتَ نُوحٖ وَٱمۡرَأَتَ لُوطٖۖ كَانَتَا تَحۡتَ عَبۡدَيۡنِ مِنۡ عِبَادِنَا صَٰلِحَيۡنِ فَخَانَتَاهُمَا فَلَمۡ يُغۡنِيَا عَنۡهُمَا مِنَ ٱللَّهِ شَيۡـٔٗا وَقِيلَ ٱدۡخُلَا ٱلنَّارَ مَعَ ٱلدَّـٰخِلِينَ\nАллоҳ куфр келтирганларга Нуҳнинг хотини ва Лутнинг хотинини мисол қилиб келтирди. Улар бандаларимиздан икки солиҳ банданинг тагида эдилар. Бас, уларга хиёнат қилдилар ва (эрлари) улардан Аллоҳнинг азобини қайтара олмадилар ва уларга: «Дўзахга кирувчилар билан киринглар», дейилди'}, {'chapter': 66, 'verse': 11, 'text': 'وَضَرَبَ ٱللَّهُ مَثَلٗا لِّلَّذِينَ ءَامَنُواْ ٱمۡرَأَتَ فِرۡعَوۡنَ إِذۡ قَالَتۡ رَبِّ ٱبۡنِ لِي عِندَكَ بَيۡتٗا فِي ٱلۡجَنَّةِ وَنَجِّنِي مِن فِرۡعَوۡنَ وَعَمَلِهِۦ وَنَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nАллоҳ иймон келтирганларга Фиръавннинг хотинини мисол қилиб кўрсатди. У (хотин): «Роббим, менга Ўз ҳузурингда, жаннатда бир уй бино қилгин. Менга Фиръавндан ва унинг ишидан нажот бергин ва золим қавмлардан нажот бергин», деб айтди'}, {'chapter': 66, 'verse': 12, 'text': 'وَمَرۡيَمَ ٱبۡنَتَ عِمۡرَٰنَ ٱلَّتِيٓ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتۡ بِكَلِمَٰتِ رَبِّهَا وَكُتُبِهِۦ وَكَانَتۡ مِنَ ٱلۡقَٰنِتِينَ\nВа Марям Имрон қизини (мисол келтирди), у фаржини пок сақлаган эди, Биз унга Ўз руҳимиздан пуфладик ва У Роббисининг сўзларини ҳамда китобларини тасдиқ қилди ва итоаткорлардан бўлди'}], '67': [{'chapter': 67, 'verse': 1, 'text': 'تَبَٰرَكَ ٱلَّذِي بِيَدِهِ ٱلۡمُلۡكُ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nҚўлида мулк бўлган Зот улуғ, муқаддас бўлди. У барча нарсага қодирдир'}, {'chapter': 67, 'verse': 2, 'text': 'ٱلَّذِي خَلَقَ ٱلۡمَوۡتَ وَٱلۡحَيَوٰةَ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡغَفُورُ\nУ, ўлим ва ҳаётни сизларнинг қайсиларингиз амалда яхшироқ эканлигингизни синаш учун яратди. У азийз ва ўта мағфиратлидир'}, {'chapter': 67, 'verse': 3, 'text': 'ٱلَّذِي خَلَقَ سَبۡعَ سَمَٰوَٰتٖ طِبَاقٗاۖ مَّا تَرَىٰ فِي خَلۡقِ ٱلرَّحۡمَٰنِ مِن تَفَٰوُتٖۖ فَٱرۡجِعِ ٱلۡبَصَرَ هَلۡ تَرَىٰ مِن فُطُورٖ\nУ етти осмонни табақама-табақа яратган. Роҳманнинг яратганида ҳеч тафовут кўрмассан. Қайта назар сол! Бирор камчилик кўряпсанми'}, {'chapter': 67, 'verse': 4, 'text': 'ثُمَّ ٱرۡجِعِ ٱلۡبَصَرَ كَرَّتَيۡنِ يَنقَلِبۡ إِلَيۡكَ ٱلۡبَصَرُ خَاسِئٗا وَهُوَ حَسِيرٞ\nСўнг такрор-такрор назар сол, назар сенга истаганини топа олмай, ҳориб-чарчаган ҳолда қайтадир'}, {'chapter': 67, 'verse': 5, 'text': 'وَلَقَدۡ زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَجَعَلۡنَٰهَا رُجُومٗا لِّلشَّيَٰطِينِۖ وَأَعۡتَدۡنَا لَهُمۡ عَذَابَ ٱلسَّعِيرِ\nВа дарҳақиқат, Биз яқин осмонни чироқлар билан зийнатлаб қўйдик ва у(чироқ)ларни шайтонларга отиладиган нарса ҳам қилиб қўйдик. Ва у(шайтон)ларга олови қизиб турган жаҳаннамни тайёрлаб қўйдик'}, {'chapter': 67, 'verse': 6, 'text': 'وَلِلَّذِينَ كَفَرُواْ بِرَبِّهِمۡ عَذَابُ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمَصِيرُ\nЎз Роббиларига куфр келтирганлар учун жаҳаннам азоби бордур ва у қандай ҳам ёмон қайтадиган жой'}, {'chapter': 67, 'verse': 7, 'text': 'إِذَآ أُلۡقُواْ فِيهَا سَمِعُواْ لَهَا شَهِيقٗا وَهِيَ تَفُورُ\nУлар жаҳаннамга ташланган чоғларида унинг ҳанграган овозини эшитурлар, у эса, қайнаб турган бўладир'}, {'chapter': 67, 'verse': 8, 'text': 'تَكَادُ تَمَيَّزُ مِنَ ٱلۡغَيۡظِۖ كُلَّمَآ أُلۡقِيَ فِيهَا فَوۡجٞ سَأَلَهُمۡ خَزَنَتُهَآ أَلَمۡ يَأۡتِكُمۡ نَذِيرٞ\nУ ғазабдан ёрилиб кетай дер. Ҳар қачон унга янги гуруҳ ташланганда, унинг хизматкорлари: «Сизларга огоҳлантирувчи келмаганмиди?» – деб сўрайдир'}, {'chapter': 67, 'verse': 9, 'text': 'قَالُواْ بَلَىٰ قَدۡ جَآءَنَا نَذِيرٞ فَكَذَّبۡنَا وَقُلۡنَا مَا نَزَّلَ ٱللَّهُ مِن شَيۡءٍ إِنۡ أَنتُمۡ إِلَّا فِي ضَلَٰلٖ كَبِيرٖ\nУлар: «Ҳа, дарҳақиқат, бизга огоҳлантирувчи келган эди. Лекин биз (уни) ёлғончига чиқардик ва: «Аллоҳ ҳеч нарса туширгани йўқ, сизлар катта адашувдан бошқа ҳеч нарса эмассизлар», деган эдик», дейишар'}, {'chapter': 67, 'verse': 10, 'text': 'وَقَالُواْ لَوۡ كُنَّا نَسۡمَعُ أَوۡ نَعۡقِلُ مَا كُنَّا فِيٓ أَصۡحَٰبِ ٱلسَّعِيرِ\nВа улар: «Агар эътибор қулоғи билан эшитганимизда ёки ақлни ишлатганимизда эди, дўзахийлар орасида бўлмаган бўлар эдик», дерлар'}, {'chapter': 67, 'verse': 11, 'text': 'فَٱعۡتَرَفُواْ بِذَنۢبِهِمۡ فَسُحۡقٗا لِّأَصۡحَٰبِ ٱلسَّعِيرِ\nБас, улар ўз гуноҳларини эътироф этдилар. Йўқолсин дўзахийлар'}, {'chapter': 67, 'verse': 12, 'text': 'إِنَّ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ\nАлбатта, ўз Роббиларидан ғойибдан қўрқадиганлар учун гуноҳларини кечиш ва улкан ажр-савоблар бордир'}, {'chapter': 67, 'verse': 13, 'text': 'وَأَسِرُّواْ قَوۡلَكُمۡ أَوِ ٱجۡهَرُواْ بِهِۦٓۖ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nВа сиз сўзингизни сир тутасизми ёки ошкора айтасизми, албатта, У кўнгиллардагини ҳам билгувчи Зотдир'}, {'chapter': 67, 'verse': 14, 'text': 'أَلَا يَعۡلَمُ مَنۡ خَلَقَ وَهُوَ ٱللَّطِيفُ ٱلۡخَبِيرُ\nЯратган зот Ўзи билмасми? Ҳолбуки, У дақиқларгача билувчи ва хабардордир'}, {'chapter': 67, 'verse': 15, 'text': 'هُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ ذَلُولٗا فَٱمۡشُواْ فِي مَنَاكِبِهَا وَكُلُواْ مِن رِّزۡقِهِۦۖ وَإِلَيۡهِ ٱلنُّشُورُ\nУ сизларга ерни бўйсундириб қўйган Зотдир. Бас, анинг турли жойларида юринг ва Унинг ризқидан енг ва қабрдан чиқиб бориш ҳам Унинг ҳузурига бўладир'}, {'chapter': 67, 'verse': 16, 'text': 'ءَأَمِنتُم مَّن فِي ٱلسَّمَآءِ أَن يَخۡسِفَ بِكُمُ ٱلۡأَرۡضَ فَإِذَا هِيَ تَمُورُ\nОсмондаги Зот сизларни изтиробда ҳаракатланиб турган ерга юттириб юборишидан омонда бўлдингизми'}, {'chapter': 67, 'verse': 17, 'text': 'أَمۡ أَمِنتُم مَّن فِي ٱلسَّمَآءِ أَن يُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗاۖ فَسَتَعۡلَمُونَ كَيۡفَ نَذِيرِ\nЁки осмондаги Зот сизларга азоб шамолни юборишидан омонда бўлдингизми? Бас, албатта, огоҳлантириш қандоқ бўлишини биласизлар'}, {'chapter': 67, 'verse': 18, 'text': 'وَلَقَدۡ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَكَيۡفَ كَانَ نَكِيرِ\nВа, дарҳақиқат, улардан аввалгилари ҳам ёлғонга чиқарган эдилар. Бас, инкорим қандай бўлди'}, {'chapter': 67, 'verse': 19, 'text': 'أَوَلَمۡ يَرَوۡاْ إِلَى ٱلطَّيۡرِ فَوۡقَهُمۡ صَـٰٓفَّـٰتٖ وَيَقۡبِضۡنَۚ مَا يُمۡسِكُهُنَّ إِلَّا ٱلرَّحۡمَٰنُۚ إِنَّهُۥ بِكُلِّ شَيۡءِۭ بَصِيرٌ\nТепаларида (қанотларини) ёйиб ва йиғиб (учаётган) қушни кўрмайдиларми? Уларни Роҳмандан бошқа ушлаб турувчи йўқ. Албатта, У барча нарсани кўрувчидир'}, {'chapter': 67, 'verse': 20, 'text': 'أَمَّنۡ هَٰذَا ٱلَّذِي هُوَ جُندٞ لَّكُمۡ يَنصُرُكُم مِّن دُونِ ٱلرَّحۡمَٰنِۚ إِنِ ٱلۡكَٰفِرُونَ إِلَّا فِي غُرُورٍ\nРоҳмандан бошқа сизларга ёрдам берадиган аскар ким ўзи? Кофирлар ғурурдан бошқа ҳеч нарсада эмаслар'}, {'chapter': 67, 'verse': 21, 'text': 'أَمَّنۡ هَٰذَا ٱلَّذِي يَرۡزُقُكُمۡ إِنۡ أَمۡسَكَ رِزۡقَهُۥۚ بَل لَّجُّواْ فِي عُتُوّٖ وَنُفُورٍ\nУ ризқни тортиб қўйса, сизга ризқ берадиган ким ўзи?! Балки улар абадий туғёнда ва ҳақдан нафратда бўлурлар'}, {'chapter': 67, 'verse': 22, 'text': 'أَفَمَن يَمۡشِي مُكِبًّا عَلَىٰ وَجۡهِهِۦٓ أَهۡدَىٰٓ أَمَّن يَمۡشِي سَوِيًّا عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nЮз тубан қоқилиб юраётган киши ҳидоятдами ёки тўғри йўлдан қоматини тик тутиб бораётган кишими'}, {'chapter': 67, 'verse': 23, 'text': 'قُلۡ هُوَ ٱلَّذِيٓ أَنشَأَكُمۡ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ\nАйт: «У сизларни йўқдан бор қилган ва сизларга эшитиш, кўриш ва ақлни берган Зотдир. Жуда ҳам оз шукр қиласизлар-а?!»'}, {'chapter': 67, 'verse': 24, 'text': 'قُلۡ هُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ\nАйт: «У сизларни ер юзида яратган, кўпайтирган Зотдир. Ва Унинг ҳузурига тўпланурсизлар»'}, {'chapter': 67, 'verse': 25, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nВа улар: «Агар ростгўй бўлсангиз, бу ваъда қачон бўлур?» – дерлар'}, {'chapter': 67, 'verse': 26, 'text': 'قُلۡ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٞ\nАйт: «Албатта, илм фақат Аллоҳнинг ҳузуридадир. Ва албатта, мен яққол огоҳлантирувчиман, холос»'}, {'chapter': 67, 'verse': 27, 'text': 'فَلَمَّا رَأَوۡهُ زُلۡفَةٗ سِيٓـَٔتۡ وُجُوهُ ٱلَّذِينَ كَفَرُواْ وَقِيلَ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تَدَّعُونَ\nБас у(азоб)ни яқиндан кўрганда куфр келтирганларнинг юзлари қораядир. Ва уларга: «Сўраган нарсангиз шу бўлур», дейилур'}, {'chapter': 67, 'verse': 28, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَهۡلَكَنِيَ ٱللَّهُ وَمَن مَّعِيَ أَوۡ رَحِمَنَا فَمَن يُجِيرُ ٱلۡكَٰفِرِينَ مِنۡ عَذَابٍ أَلِيمٖ\nАйт: «Айтинглар-чи, агар Аллоҳ мени ва мен билан бўлганларни ҳалок қилса ёки раҳим қилса, кофирларни аламли азобдан ким қутқариб қоладир'}, {'chapter': 67, 'verse': 29, 'text': 'قُلۡ هُوَ ٱلرَّحۡمَٰنُ ءَامَنَّا بِهِۦ وَعَلَيۡهِ تَوَكَّلۡنَاۖ فَسَتَعۡلَمُونَ مَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ\nАйт: «У Роҳмандур, биз Унга иймон келтирдик ва Унга таваккал қилдик. Бас, сизлар тезда ким очиқ-ойдин хатода эканини билурсизлар»'}, {'chapter': 67, 'verse': 30, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَصۡبَحَ مَآؤُكُمۡ غَوۡرٗا فَمَن يَأۡتِيكُم بِمَآءٖ مَّعِينِۭ\nАйт: «Айтинглар-чи, агар сувларингиз ерга сингиб кетса, сизларга ким осонгина топиладиган сувни келтирур?»'}], '68': [{'chapter': 68, 'verse': 1, 'text': 'نٓۚ وَٱلۡقَلَمِ وَمَا يَسۡطُرُونَ\nНуун. Қалам билан ва сатрларга ёзадиган нарсалар билан қасам'}, {'chapter': 68, 'verse': 2, 'text': 'مَآ أَنتَ بِنِعۡمَةِ رَبِّكَ بِمَجۡنُونٖ\nСен Роббинг неъмати ила мажнун эмассан'}, {'chapter': 68, 'verse': 3, 'text': 'وَإِنَّ لَكَ لَأَجۡرًا غَيۡرَ مَمۡنُونٖ\nВа албатта, сенга миннат қилинмайдиган ажр бордур'}, {'chapter': 68, 'verse': 4, 'text': 'وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٖ\nВа албатта, сен улкан хулқдасан'}, {'chapter': 68, 'verse': 5, 'text': 'فَسَتُبۡصِرُ وَيُبۡصِرُونَ\nБас, тезда кўрасан ва улар ҳам кўрурлар'}, {'chapter': 68, 'verse': 6, 'text': 'بِأَييِّكُمُ ٱلۡمَفۡتُونُ\nСизлардан қайсингизда мажнунлик борлигини'}, {'chapter': 68, 'verse': 7, 'text': 'إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\nАлбатта, Роббинг Ўзи ким Унинг йўлидан адашганини билувчидур ва У ҳидоятда юрувчиларни ҳам билувчидур'}, {'chapter': 68, 'verse': 8, 'text': 'فَلَا تُطِعِ ٱلۡمُكَذِّبِينَ\nБас, ёлғонга чиқарувчиларга итоат қилма'}, {'chapter': 68, 'verse': 9, 'text': 'وَدُّواْ لَوۡ تُدۡهِنُ فَيُدۡهِنُونَ\nАгар сен муроса қилсанг, улар ҳам муроса қилишни орзу қиладилар'}, {'chapter': 68, 'verse': 10, 'text': 'وَلَا تُطِعۡ كُلَّ حَلَّافٖ مَّهِينٍ\nИтоат қилма! Ҳар бир қасамхўр, пасткашга'}, {'chapter': 68, 'verse': 11, 'text': 'هَمَّازٖ مَّشَّآءِۭ بِنَمِيمٖ\nАйбловчи, чақимчилик қилиб юрувчига'}, {'chapter': 68, 'verse': 12, 'text': 'مَّنَّاعٖ لِّلۡخَيۡرِ مُعۡتَدٍ أَثِيمٍ\nЯхшиликни ман қилувчи, тажовузкор, сергуноҳга'}, {'chapter': 68, 'verse': 13, 'text': 'عُتُلِّۭ بَعۡدَ ذَٰلِكَ زَنِيمٍ\nҚўпол, ундан сўнг, отасининг тайини йўққа'}, {'chapter': 68, 'verse': 14, 'text': 'أَن كَانَ ذَا مَالٖ وَبَنِينَ\nМоли ва ўғиллари кўп бўлганидан'}, {'chapter': 68, 'verse': 15, 'text': 'إِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nБизнинг оятларимиз унга ўқилган пайтда, аввалгиларнинг афсоналари, дер'}, {'chapter': 68, 'verse': 16, 'text': 'سَنَسِمُهُۥ عَلَى ٱلۡخُرۡطُومِ\nТезда унинг тумшуғига белги қўямиз'}, {'chapter': 68, 'verse': 17, 'text': 'إِنَّا بَلَوۡنَٰهُمۡ كَمَا بَلَوۡنَآ أَصۡحَٰبَ ٱلۡجَنَّةِ إِذۡ أَقۡسَمُواْ لَيَصۡرِمُنَّهَا مُصۡبِحِينَ\nАлбатта, Биз уларни боғ эгаларини синаганимиздек синаб кўрдик. Вақтики, эрта тонгда териб оламиз, деб қасам ичдилар'}, {'chapter': 68, 'verse': 18, 'text': 'وَلَا يَسۡتَثۡنُونَ\nВа истисно қилмадилар'}, {'chapter': 68, 'verse': 19, 'text': 'فَطَافَ عَلَيۡهَا طَآئِفٞ مِّن رَّبِّكَ وَهُمۡ نَآئِمُونَ\nУлар ухлаб ётганда, у(боғ)нинг устидан Роббингдан бўлган айланувчи айланиб чиқди'}, {'chapter': 68, 'verse': 20, 'text': 'فَأَصۡبَحَتۡ كَٱلصَّرِيمِ\nБас, у худди меваси териб олинганга ўхшаб қолди'}, {'chapter': 68, 'verse': 21, 'text': 'فَتَنَادَوۡاْ مُصۡبِحِينَ\nУлар тонг саҳарда бир-бирларини чақирдилар'}, {'chapter': 68, 'verse': 22, 'text': 'أَنِ ٱغۡدُواْ عَلَىٰ حَرۡثِكُمۡ إِن كُنتُمۡ صَٰرِمِينَ\nАгар терадиган бўлсангиз, экинзорингизга эртароқ боринг, деб'}, {'chapter': 68, 'verse': 23, 'text': 'فَٱنطَلَقُواْ وَهُمۡ يَتَخَٰفَتُونَ\nБас, паст овоз билан гаплашиб юриб кетдилар'}, {'chapter': 68, 'verse': 24, 'text': 'أَن لَّا يَدۡخُلَنَّهَا ٱلۡيَوۡمَ عَلَيۡكُم مِّسۡكِينٞ\nБугун сиз борингизда у(боғ)га мискин кира кўрмасин, деб'}, {'chapter': 68, 'verse': 25, 'text': 'وَغَدَوۡاْ عَلَىٰ حَرۡدٖ قَٰدِرِينَ\nВа эрталабда қасд-ла, ўзларини қодир сезиб, бордилар'}, {'chapter': 68, 'verse': 26, 'text': 'فَلَمَّا رَأَوۡهَا قَالُوٓاْ إِنَّا لَضَآلُّونَ\nУни кўрганларида, биз адашибмиз, дедилар'}, {'chapter': 68, 'verse': 27, 'text': 'بَلۡ نَحۡنُ مَحۡرُومُونَ\nБалки биз маҳрум бўлгандирмиз, дедилар'}, {'chapter': 68, 'verse': 28, 'text': 'قَالَ أَوۡسَطُهُمۡ أَلَمۡ أَقُل لَّكُمۡ لَوۡلَا تُسَبِّحُونَ\nУларнинг энг яхши ва адолатлиси: «Сизларга тасбиҳ айтсаларингиз-чи, демабмидим», деди'}, {'chapter': 68, 'verse': 29, 'text': 'قَالُواْ سُبۡحَٰنَ رَبِّنَآ إِنَّا كُنَّا ظَٰلِمِينَ\nРоббимиз айблардан пок бўлди, албатта, биз золимлардан эдик, дедилар'}, {'chapter': 68, 'verse': 30, 'text': 'فَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَلَٰوَمُونَ\nБас, баъзилари баъзиларини маломат қила бошладилар'}, {'chapter': 68, 'verse': 31, 'text': 'قَالُواْ يَٰوَيۡلَنَآ إِنَّا كُنَّا طَٰغِينَ\n«Вой, шўримиз қурсин, туғёнга кетган эканмиз'}, {'chapter': 68, 'verse': 32, 'text': 'عَسَىٰ رَبُّنَآ أَن يُبۡدِلَنَا خَيۡرٗا مِّنۡهَآ إِنَّآ إِلَىٰ رَبِّنَا رَٰغِبُونَ\nШояд, Роббимиз унинг ўрнига ундан кўра яхшироғини берса. Биз ўз Роббимизга рағбатлимиз, дедилар'}, {'chapter': 68, 'verse': 33, 'text': 'كَذَٰلِكَ ٱلۡعَذَابُۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nАзоб шундоқ бўлур ва агар билсалар, албатта, охират азоби бундан катта бўладир'}, {'chapter': 68, 'verse': 34, 'text': 'إِنَّ لِلۡمُتَّقِينَ عِندَ رَبِّهِمۡ جَنَّـٰتِ ٱلنَّعِيمِ\nАлбатта, тақводорлар учун Роббилари ҳузурида наъийм жаннатлари бордур'}, {'chapter': 68, 'verse': 35, 'text': 'أَفَنَجۡعَلُ ٱلۡمُسۡلِمِينَ كَٱلۡمُجۡرِمِينَ\nМусулмонларни жинояткорларга ўхшатиб қўярмидик'}, {'chapter': 68, 'verse': 36, 'text': 'مَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ\nСизларга нима бўлди, қандай ҳукм чиқаряпсизлар'}, {'chapter': 68, 'verse': 37, 'text': 'أَمۡ لَكُمۡ كِتَٰبٞ فِيهِ تَدۡرُسُونَ\nЁки сизларнинг китобингиз бор-у, ундан ўқидингизми'}, {'chapter': 68, 'verse': 38, 'text': 'إِنَّ لَكُمۡ فِيهِ لَمَا تَخَيَّرُونَ\nУнда сизларга нимани ихтиёр қилсангиз, албатта, (бўлаверади) дейилганми'}, {'chapter': 68, 'verse': 39, 'text': 'أَمۡ لَكُمۡ أَيۡمَٰنٌ عَلَيۡنَا بَٰلِغَةٌ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ إِنَّ لَكُمۡ لَمَا تَحۡكُمُونَ\nЁки сизларга бизнинг қиёмат кунигача етадиган берган ваъдаларимиз бўлиб, унда, албатта, сиз нима ҳукм қилсангиз, шу бўлади, дейилганми'}, {'chapter': 68, 'verse': 40, 'text': 'سَلۡهُمۡ أَيُّهُم بِذَٰلِكَ زَعِيمٌ\nСўра улардан, қай бирлари бу нарсага кафил экан'}, {'chapter': 68, 'verse': 41, 'text': 'أَمۡ لَهُمۡ شُرَكَآءُ فَلۡيَأۡتُواْ بِشُرَكَآئِهِمۡ إِن كَانُواْ صَٰدِقِينَ\nЁки уларнинг шериклари бормикин, агар ростгўй бўлсалар, ўша шерикларини ҳам олиб келсинлар'}, {'chapter': 68, 'verse': 42, 'text': 'يَوۡمَ يُكۡشَفُ عَن سَاقٖ وَيُدۡعَوۡنَ إِلَى ٱلسُّجُودِ فَلَا يَسۡتَطِيعُونَ\nБолдирлар шимарилган кунида ва улар саждага чақирилганда, қодир бўлмаслар'}, {'chapter': 68, 'verse': 43, 'text': 'خَٰشِعَةً أَبۡصَٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٞۖ وَقَدۡ كَانُواْ يُدۡعَوۡنَ إِلَى ٱلسُّجُودِ وَهُمۡ سَٰلِمُونَ\nУларнинг кўзлари қўрқинчга тўла, хорлик ўраб олган. Ва дарҳақиқат, улар саломатлик ҳолларида сажда қилишга чақирилган эдилар'}, {'chapter': 68, 'verse': 44, 'text': 'فَذَرۡنِي وَمَن يُكَذِّبُ بِهَٰذَا ٱلۡحَدِيثِۖ سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ\nБу гапни ёлғонга чиқарувчиларни Менинг Ўзимга қўйиб қўй. Биз уларни ўзлари билмаган тарафдан аста-секин оламиз'}, {'chapter': 68, 'verse': 45, 'text': 'وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ\nВа уларга муҳлат бераман. Албатта, Менинг ҳийлам метиндир'}, {'chapter': 68, 'verse': 46, 'text': 'أَمۡ تَسۡـَٔلُهُمۡ أَجۡرٗا فَهُم مِّن مَّغۡرَمٖ مُّثۡقَلُونَ\nЁки сен улардан ҳақ сўраяпсан-у, улар қарзга ботиб қолдиларми'}, {'chapter': 68, 'verse': 47, 'text': 'أَمۡ عِندَهُمُ ٱلۡغَيۡبُ فَهُمۡ يَكۡتُبُونَ\nЁки ҳузурларида ғойиб илми бор-у, улар ёзиб оладиларми'}, {'chapter': 68, 'verse': 48, 'text': 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ ٱلۡحُوتِ إِذۡ نَادَىٰ وَهُوَ مَكۡظُومٞ\nРоббинг ҳукмига сабр қил ва бир вақтлар ғамга тўлган ҳолда нидо қилган балиқ соҳибига ўхшама'}, {'chapter': 68, 'verse': 49, 'text': 'لَّوۡلَآ أَن تَدَٰرَكَهُۥ نِعۡمَةٞ مِّن رَّبِّهِۦ لَنُبِذَ بِٱلۡعَرَآءِ وَهُوَ مَذۡمُومٞ\nАгар уни Роббисининг неъмати топмаганда, очиқ саҳро жойга, мазамматга қолган ҳолда ташланган бўларди'}, {'chapter': 68, 'verse': 50, 'text': 'فَٱجۡتَبَٰهُ رَبُّهُۥ فَجَعَلَهُۥ مِنَ ٱلصَّـٰلِحِينَ\nБас, уни Роббиси танлаб олди ва солиҳ бандаларидан қилди'}, {'chapter': 68, 'verse': 51, 'text': 'وَإِن يَكَادُ ٱلَّذِينَ كَفَرُواْ لَيُزۡلِقُونَكَ بِأَبۡصَٰرِهِمۡ لَمَّا سَمِعُواْ ٱلذِّكۡرَ وَيَقُولُونَ إِنَّهُۥ لَمَجۡنُونٞ\nВа кофирлар зикрни эшитаётганларида сени кўзлари билан тойдирмоқчи бўлдилар ва, албатта, у жиннидур, дерлар'}, {'chapter': 68, 'verse': 52, 'text': 'وَمَا هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nҲолбуки, у бутун оламларга эслатмадан ўзга нарса эмас'}], '69': [{'chapter': 69, 'verse': 1, 'text': 'ٱلۡحَآقَّةُ\nАл-Ҳааққоҳ'}, {'chapter': 69, 'verse': 2, 'text': 'مَا ٱلۡحَآقَّةُ\nАл-Ҳааққоҳ нима ўзи'}, {'chapter': 69, 'verse': 3, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡحَآقَّةُ\nАл-Ҳааққоҳ нималигини сенга нима билдирди'}, {'chapter': 69, 'verse': 4, 'text': 'كَذَّبَتۡ ثَمُودُ وَعَادُۢ بِٱلۡقَارِعَةِ\nСамуд ва Од қориъани ёлғонга чиқаришди'}, {'chapter': 69, 'verse': 5, 'text': 'فَأَمَّا ثَمُودُ فَأُهۡلِكُواْ بِٱلطَّاغِيَةِ\nБас, аммо Самуд ҳаддан ошувчи билан ҳалок қилинди'}, {'chapter': 69, 'verse': 6, 'text': 'وَأَمَّا عَادٞ فَأُهۡلِكُواْ بِرِيحٖ صَرۡصَرٍ عَاتِيَةٖ\nВа аммо Од қаттиқ (совуқ) шамол билан ҳалок қилиндилар'}, {'chapter': 69, 'verse': 7, 'text': 'سَخَّرَهَا عَلَيۡهِمۡ سَبۡعَ لَيَالٖ وَثَمَٰنِيَةَ أَيَّامٍ حُسُومٗاۖ فَتَرَى ٱلۡقَوۡمَ فِيهَا صَرۡعَىٰ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٍ خَاوِيَةٖ\nУ зот уларнинг устидан бу шамолни етти кечаю саккиз кундуз «беминнат хизмат» қилдириб қўйди. Бас, қавмнинг у ерда ҳалок бўлиб, ағдарилиб ётганини кўрасан. Улар худди хурмонинг чириган томирларига ўхшарлар'}, {'chapter': 69, 'verse': 8, 'text': 'فَهَلۡ تَرَىٰ لَهُم مِّنۢ بَاقِيَةٖ\nБас, сен улардан бирортасининг боқий қолганини кўрасанми'}, {'chapter': 69, 'verse': 9, 'text': 'وَجَآءَ فِرۡعَوۡنُ وَمَن قَبۡلَهُۥ وَٱلۡمُؤۡتَفِكَٰتُ بِٱلۡخَاطِئَةِ\nФиръавн, ундан аввалгилар ва тўнтарилганликлар хато қилдилар'}, {'chapter': 69, 'verse': 10, 'text': 'فَعَصَوۡاْ رَسُولَ رَبِّهِمۡ فَأَخَذَهُمۡ أَخۡذَةٗ رَّابِيَةً\nБас, улар Роббиларининг Расулига осий бўлдилар. Дарҳақиқат, У зот уларни қаттиқ тутишлик билан тутди'}, {'chapter': 69, 'verse': 11, 'text': 'إِنَّا لَمَّا طَغَا ٱلۡمَآءُ حَمَلۡنَٰكُمۡ فِي ٱلۡجَارِيَةِ\nАлбатта, Биз сув туғёнга келган пайтда сизларни кемада кўтарганмиз'}, {'chapter': 69, 'verse': 12, 'text': 'لِنَجۡعَلَهَا لَكُمۡ تَذۡكِرَةٗ وَتَعِيَهَآ أُذُنٞ وَٰعِيَةٞ\nУни сизларга эслатма қилиш учун ва англовчи қулоқлар англаб олиши учун'}, {'chapter': 69, 'verse': 13, 'text': 'فَإِذَا نُفِخَ فِي ٱلصُّورِ نَفۡخَةٞ وَٰحِدَةٞ\nАгар дудга биргина пуфлаш-ла пуфланса'}, {'chapter': 69, 'verse': 14, 'text': 'وَحُمِلَتِ ٱلۡأَرۡضُ وَٱلۡجِبَالُ فَدُكَّتَا دَكَّةٗ وَٰحِدَةٗ\nВа еру тоғлар кўтарилиб, бир мартагина силкитилса'}, {'chapter': 69, 'verse': 15, 'text': 'فَيَوۡمَئِذٖ وَقَعَتِ ٱلۡوَاقِعَةُ\nБас, ўша кунда, Воқиъа воқеъ бўлибдир'}, {'chapter': 69, 'verse': 16, 'text': 'وَٱنشَقَّتِ ٱلسَّمَآءُ فَهِيَ يَوۡمَئِذٖ وَاهِيَةٞ\nВа осмон парчаланса, бас, ўша кунда, у (осмон) беҳолдир'}, {'chapter': 69, 'verse': 17, 'text': 'وَٱلۡمَلَكُ عَلَىٰٓ أَرۡجَآئِهَاۚ وَيَحۡمِلُ عَرۡشَ رَبِّكَ فَوۡقَهُمۡ يَوۡمَئِذٖ ثَمَٰنِيَةٞ\nВа фаришталар унинг атрофида ва ўша кунда Роббингнинг аршини саккизтаси кўтариб турадир'}, {'chapter': 69, 'verse': 18, 'text': 'يَوۡمَئِذٖ تُعۡرَضُونَ لَا تَخۡفَىٰ مِنكُمۡ خَافِيَةٞ\nБас, ўша кунда кўрикдан ўтасизлар, бирорта нарсангиз махфий қолмайдир'}, {'chapter': 69, 'verse': 19, 'text': 'فَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَيَقُولُ هَآؤُمُ ٱقۡرَءُواْ كِتَٰبِيَهۡ\nБас, кимнинг китоби ўнг тарафдан берилса, у: «Мана, менинг китобимни ўқиб кўринглар'}, {'chapter': 69, 'verse': 20, 'text': 'إِنِّي ظَنَنتُ أَنِّي مُلَٰقٍ حِسَابِيَهۡ\nАлбатта, мен ҳисоб-китобимга йўлиқишимга ишонардим», дейдир'}, {'chapter': 69, 'verse': 21, 'text': 'فَهُوَ فِي عِيشَةٖ رَّاضِيَةٖ\nБас, у розилик ҳаётидадир'}, {'chapter': 69, 'verse': 22, 'text': 'فِي جَنَّةٍ عَالِيَةٖ\nОлий жаннатдадир'}, {'chapter': 69, 'verse': 23, 'text': 'قُطُوفُهَا دَانِيَةٞ\nУнинг мевалари яқиндир'}, {'chapter': 69, 'verse': 24, 'text': 'كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَآ أَسۡلَفۡتُمۡ فِي ٱلۡأَيَّامِ ٱلۡخَالِيَةِ\nЎтган кунларда қилган нарсаларингиз туфайли енглар ва ичинглар, ош бўлсин'}, {'chapter': 69, 'verse': 25, 'text': 'وَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِشِمَالِهِۦ فَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُوتَ كِتَٰبِيَهۡ\nВа аммо кимнинг китоби чап тарафдан берилса, у: «Вой шўрим, кошки менга китобим берилмаса эди'}, {'chapter': 69, 'verse': 26, 'text': 'وَلَمۡ أَدۡرِ مَا حِسَابِيَهۡ\nВа ҳисоб-китоб қандоқлигини билмасам эдим'}, {'chapter': 69, 'verse': 27, 'text': 'يَٰلَيۡتَهَا كَانَتِ ٱلۡقَاضِيَةَ\nҚани энди, (бу дунёдаги ўлим) охиргиси бўлса эди'}, {'chapter': 69, 'verse': 28, 'text': 'مَآ أَغۡنَىٰ عَنِّي مَالِيَهۡۜ\nМолу мулким мени сақлаб қола олмади'}, {'chapter': 69, 'verse': 29, 'text': 'هَلَكَ عَنِّي سُلۡطَٰنِيَهۡ\nМенинг султоним ҳам ҳалок бўлди», дейдир'}, {'chapter': 69, 'verse': 30, 'text': 'خُذُوهُ فَغُلُّوهُ\nТутинглар уни ва кишанланглар уни'}, {'chapter': 69, 'verse': 31, 'text': 'ثُمَّ ٱلۡجَحِيمَ صَلُّوهُ\nСўнгра жаҳаннамга солинглар уни'}, {'chapter': 69, 'verse': 32, 'text': 'ثُمَّ فِي سِلۡسِلَةٖ ذَرۡعُهَا سَبۡعُونَ ذِرَاعٗا فَٱسۡلُكُوهُ\nСўнгра етмиш қулочли занжирга киритинг уни'}, {'chapter': 69, 'verse': 33, 'text': 'إِنَّهُۥ كَانَ لَا يُؤۡمِنُ بِٱللَّهِ ٱلۡعَظِيمِ\nЧунки, у улуғ Аллоҳга иймон келтирмаган эди'}, {'chapter': 69, 'verse': 34, 'text': 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ\nВа мискинларга таом беришга қизиқтирмаган эди'}, {'chapter': 69, 'verse': 35, 'text': 'فَلَيۡسَ لَهُ ٱلۡيَوۡمَ هَٰهُنَا حَمِيمٞ\nБас, бугунги кунда унга бу ерда бирон чин дўст йўқ'}, {'chapter': 69, 'verse': 36, 'text': 'وَلَا طَعَامٌ إِلَّا مِنۡ غِسۡلِينٖ\nВа ғислийндан ўзга таом ҳам йўқ'}, {'chapter': 69, 'verse': 37, 'text': 'لَّا يَأۡكُلُهُۥٓ إِلَّا ٱلۡخَٰطِـُٔونَ\nУни хатокорлардан бошқа ҳеч ким емас'}, {'chapter': 69, 'verse': 38, 'text': 'فَلَآ أُقۡسِمُ بِمَا تُبۡصِرُونَ\nСизлар кўриб турган нарсалар билан қасам ичурман'}, {'chapter': 69, 'verse': 39, 'text': 'وَمَا لَا تُبۡصِرُونَ\nВа сизлар кўрмайдиган нарсалар билан ҳам'}, {'chapter': 69, 'verse': 40, 'text': 'إِنَّهُۥ لَقَوۡلُ رَسُولٖ كَرِيمٖ\nАлбатта у (Қуръон) карамли Расулнинг сўзидир'}, {'chapter': 69, 'verse': 41, 'text': 'وَمَا هُوَ بِقَوۡلِ شَاعِرٖۚ قَلِيلٗا مَّا تُؤۡمِنُونَ\nВа у шоирнинг сўзи эмас. Сизлар жуда оз ишонасизлар'}, {'chapter': 69, 'verse': 42, 'text': 'وَلَا بِقَوۡلِ كَاهِنٖۚ قَلِيلٗا مَّا تَذَكَّرُونَ\nВа у фолбиннинг сўзи ҳам эмас. Сизлар жуда оз эслайсизлар'}, {'chapter': 69, 'verse': 43, 'text': 'تَنزِيلٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nУ оламлар Роббисидан туширилгандир'}, {'chapter': 69, 'verse': 44, 'text': 'وَلَوۡ تَقَوَّلَ عَلَيۡنَا بَعۡضَ ٱلۡأَقَاوِيلِ\nВа агар у Бизнинг (номимиздан) баъзан ёлғон сўзларни тўқиса'}, {'chapter': 69, 'verse': 45, 'text': 'لَأَخَذۡنَا مِنۡهُ بِٱلۡيَمِينِ\nАлбатта, Биз уни қудрат билан тутамиз'}, {'chapter': 69, 'verse': 46, 'text': 'ثُمَّ لَقَطَعۡنَا مِنۡهُ ٱلۡوَتِينَ\nСўнгра, албатта, унинг шоҳ томирини кесамиз'}, {'chapter': 69, 'verse': 47, 'text': 'فَمَا مِنكُم مِّنۡ أَحَدٍ عَنۡهُ حَٰجِزِينَ\nБас, сизлардан ҳеч ким уни тўсиб қола олмас'}, {'chapter': 69, 'verse': 48, 'text': 'وَإِنَّهُۥ لَتَذۡكِرَةٞ لِّلۡمُتَّقِينَ\nВа албатта, у (Қуръон) тақводорлар учун эслатмадир'}, {'chapter': 69, 'verse': 49, 'text': 'وَإِنَّا لَنَعۡلَمُ أَنَّ مِنكُم مُّكَذِّبِينَ\nВа албатта, Биз сизларнинг ичингизда ёлғонга чиқарувчилар борлигини билиб турамиз'}, {'chapter': 69, 'verse': 50, 'text': 'وَإِنَّهُۥ لَحَسۡرَةٌ عَلَى ٱلۡكَٰفِرِينَ\nВа албатта, у (Қуръон) кофирлар учун ҳасратдир'}, {'chapter': 69, 'verse': 51, 'text': 'وَإِنَّهُۥ لَحَقُّ ٱلۡيَقِينِ\nВа албатта, у (Қуръон) шубҳасиз ҳақиқатдир'}, {'chapter': 69, 'verse': 52, 'text': 'فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ\nБас, улуғ Роббингнинг исмини поклаб ёд эт'}], '70': [{'chapter': 70, 'verse': 1, 'text': 'سَأَلَ سَآئِلُۢ بِعَذَابٖ وَاقِعٖ\nСўровчи воқеъ бўлувчи азоб ҳақида сўради'}, {'chapter': 70, 'verse': 2, 'text': 'لِّلۡكَٰفِرِينَ لَيۡسَ لَهُۥ دَافِعٞ\nКофирларга. Уни дафъ қилувчи йўқдир'}, {'chapter': 70, 'verse': 3, 'text': 'مِّنَ ٱللَّهِ ذِي ٱلۡمَعَارِجِ\nУ Аллоҳдан — маъорижлар эгасидандир'}, {'chapter': 70, 'verse': 4, 'text': 'تَعۡرُجُ ٱلۡمَلَـٰٓئِكَةُ وَٱلرُّوحُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥ خَمۡسِينَ أَلۡفَ سَنَةٖ\nФаришталар ва Руҳ Унинг ҳузурига миқдори эллик минг йил бўлган кунда кўтарилур'}, {'chapter': 70, 'verse': 5, 'text': 'فَٱصۡبِرۡ صَبۡرٗا جَمِيلًا\nБас, чиройли сабр қил'}, {'chapter': 70, 'verse': 6, 'text': 'إِنَّهُمۡ يَرَوۡنَهُۥ بَعِيدٗا\nАлбатта, улар у(қиёмат)ни узоқ деб биладир'}, {'chapter': 70, 'verse': 7, 'text': 'وَنَرَىٰهُ قَرِيبٗا\nВа Биз уни жуда яқин деб билурмиз'}, {'chapter': 70, 'verse': 8, 'text': 'يَوۡمَ تَكُونُ ٱلسَّمَآءُ كَٱلۡمُهۡلِ\nУ кунда осмон худди қора мой қолдиқларига ўхшаб қоладир'}, {'chapter': 70, 'verse': 9, 'text': 'وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ\nВа тоғлар худди титилган жунга ўхшаб қоладир'}, {'chapter': 70, 'verse': 10, 'text': 'وَلَا يَسۡـَٔلُ حَمِيمٌ حَمِيمٗا\nВа яқин дўст ўз дўстидан сўрамайдир'}, {'chapter': 70, 'verse': 11, 'text': 'يُبَصَّرُونَهُمۡۚ يَوَدُّ ٱلۡمُجۡرِمُ لَوۡ يَفۡتَدِي مِنۡ عَذَابِ يَوۡمِئِذِۭ بِبَنِيهِ\nУларни кўриб турадирлар. Гуноҳкор ўша кун азобидан қутулиш учун ўз болаларини ҳам'}, {'chapter': 70, 'verse': 12, 'text': 'وَصَٰحِبَتِهِۦ وَأَخِيهِ\nВа хотинини ва ака-укасини ҳам'}, {'chapter': 70, 'verse': 13, 'text': 'وَفَصِيلَتِهِ ٱلَّتِي تُـٔۡوِيهِ\nВа уни ўзига олган қабиласини ҳам'}, {'chapter': 70, 'verse': 14, 'text': 'وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗا ثُمَّ يُنجِيهِ\nВа ер юзидаги жамийки кимсаларни ҳам фидо қилишни хоҳлайдир. Сўнгра ўзи нажот топса'}, {'chapter': 70, 'verse': 15, 'text': 'كَلَّآۖ إِنَّهَا لَظَىٰ\nЙўқ! Албатта, у(жаҳаннам) кучли алангадур'}, {'chapter': 70, 'verse': 16, 'text': 'نَزَّاعَةٗ لِّلشَّوَىٰ\nБошнинг терисини сидириб олувчидур'}, {'chapter': 70, 'verse': 17, 'text': 'تَدۡعُواْ مَنۡ أَدۡبَرَ وَتَوَلَّىٰ\nУ чақирадир. Орқага кетган ва юз ўгирганни'}, {'chapter': 70, 'verse': 18, 'text': 'وَجَمَعَ فَأَوۡعَىٰٓ\nВа (молни) йиғиб, беркитганни'}, {'chapter': 70, 'verse': 19, 'text': '۞إِنَّ ٱلۡإِنسَٰنَ خُلِقَ هَلُوعًا\nАлбатта, инсон «ҳалуъ» қилиб яратилгандир'}, {'chapter': 70, 'verse': 20, 'text': 'إِذَا مَسَّهُ ٱلشَّرُّ جَزُوعٗا\nАгар унга ёмонлик етса, чидамсиздир'}, {'chapter': 70, 'verse': 21, 'text': 'وَإِذَا مَسَّهُ ٱلۡخَيۡرُ مَنُوعًا\nВа агар яхшилик етса, кўп ман қилувчидир'}, {'chapter': 70, 'verse': 22, 'text': 'إِلَّا ٱلۡمُصَلِّينَ\nИлло, намоз ўқувчиларгина мустаснодир'}, {'chapter': 70, 'verse': 23, 'text': 'ٱلَّذِينَ هُمۡ عَلَىٰ صَلَاتِهِمۡ دَآئِمُونَ\nУлар намозларида доим бўладиганлардир'}, {'chapter': 70, 'verse': 24, 'text': 'وَٱلَّذِينَ فِيٓ أَمۡوَٰلِهِمۡ حَقّٞ مَّعۡلُومٞ\nУларнинг молу мулкларида маълум ҳаққ бордур'}, {'chapter': 70, 'verse': 25, 'text': 'لِّلسَّآئِلِ وَٱلۡمَحۡرُومِ\nСўровчи ва бечоралар учундир'}, {'chapter': 70, 'verse': 26, 'text': 'وَٱلَّذِينَ يُصَدِّقُونَ بِيَوۡمِ ٱلدِّينِ\nВа улар қиёмат кунини тасдиқлайдиганлардир'}, {'chapter': 70, 'verse': 27, 'text': 'وَٱلَّذِينَ هُم مِّنۡ عَذَابِ رَبِّهِم مُّشۡفِقُونَ\nВа улар ўз Роббилари азобидан қўрқадиганлардир'}, {'chapter': 70, 'verse': 28, 'text': 'إِنَّ عَذَابَ رَبِّهِمۡ غَيۡرُ مَأۡمُونٖ\nАлбатта, Роббиларининг азобидан омонлик йўқдир'}, {'chapter': 70, 'verse': 29, 'text': 'وَٱلَّذِينَ هُمۡ لِفُرُوجِهِمۡ حَٰفِظُونَ\nВа улар ўз фаржларини сақлайдиганлардир'}, {'chapter': 70, 'verse': 30, 'text': 'إِلَّا عَلَىٰٓ أَزۡوَٰجِهِمۡ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَإِنَّهُمۡ غَيۡرُ مَلُومِينَ\nИлло, ўз хотинлари ёки мулки бўлганлардан (сақламасалар), албатта улар маломат қилинмасдир'}, {'chapter': 70, 'verse': 31, 'text': 'فَمَنِ ٱبۡتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡعَادُونَ\nКимки ўшандан ўзгани истаса, бас, улар чегарадан чиққанлардир'}, {'chapter': 70, 'verse': 32, 'text': 'وَٱلَّذِينَ هُمۡ لِأَمَٰنَٰتِهِمۡ وَعَهۡدِهِمۡ رَٰعُونَ\nУлар омонатларига ва аҳдларига риоя қиладиганлардир'}, {'chapter': 70, 'verse': 33, 'text': 'وَٱلَّذِينَ هُم بِشَهَٰدَٰتِهِمۡ قَآئِمُونَ\nВа улар гувоҳликларини тўғри берадиганлардир'}, {'chapter': 70, 'verse': 34, 'text': 'وَٱلَّذِينَ هُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ\nУлар намозларини муҳофаза қиладиганлардир'}, {'chapter': 70, 'verse': 35, 'text': 'أُوْلَـٰٓئِكَ فِي جَنَّـٰتٖ مُّكۡرَمُونَ\nЎшалар жаннатларда ҳурматланадиганлардир'}, {'chapter': 70, 'verse': 36, 'text': 'فَمَالِ ٱلَّذِينَ كَفَرُواْ قِبَلَكَ مُهۡطِعِينَ\nКуфр келтирганларга не бўлдики, сен томон бўйин чўзиб шошилмоқдалар'}, {'chapter': 70, 'verse': 37, 'text': 'عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ عِزِينَ\nЎнг тарафдан ва чап тарафдан тўп-тўп бўлмоқдалар'}, {'chapter': 70, 'verse': 38, 'text': 'أَيَطۡمَعُ كُلُّ ٱمۡرِيٕٖ مِّنۡهُمۡ أَن يُدۡخَلَ جَنَّةَ نَعِيمٖ\nУлардан ҳар бир киши наъийм жаннатга киритилишни тамаъ қилурми'}, {'chapter': 70, 'verse': 39, 'text': 'كَلَّآۖ إِنَّا خَلَقۡنَٰهُم مِّمَّا يَعۡلَمُونَ\nЙўқ! Албатта, Биз уларни ўзлари билган нарсадан яратганмиз'}, {'chapter': 70, 'verse': 40, 'text': 'فَلَآ أُقۡسِمُ بِرَبِّ ٱلۡمَشَٰرِقِ وَٱلۡمَغَٰرِبِ إِنَّا لَقَٰدِرُونَ\nМашриқлар ва мағриблар Роббиси билан қасам ичаманки, албатта, Биз қодирмиз'}, {'chapter': 70, 'verse': 41, 'text': 'عَلَىٰٓ أَن نُّبَدِّلَ خَيۡرٗا مِّنۡهُمۡ وَمَا نَحۡنُ بِمَسۡبُوقِينَ\nЎринларига улардан яхшироқларини алмаштиришга ва Биз бу ишни қилишдан ожизмасмиз'}, {'chapter': 70, 'verse': 42, 'text': 'فَذَرۡهُمۡ يَخُوضُواْ وَيَلۡعَبُواْ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي يُوعَدُونَ\nУларни тек қўй. То ваъда қилинган кунга йўлиққунларича (хоҳлаганларига) шўнғийверсинлар ва ўйнайверсинлар'}, {'chapter': 70, 'verse': 43, 'text': 'يَوۡمَ يَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ سِرَاعٗا كَأَنَّهُمۡ إِلَىٰ نُصُبٖ يُوفِضُونَ\nУ кунда худди санамлар(ибодати)га шошганларидек, қабрларидан шошиб чиқурлар'}, {'chapter': 70, 'verse': 44, 'text': 'خَٰشِعَةً أَبۡصَٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٞۚ ذَٰلِكَ ٱلۡيَوۡمُ ٱلَّذِي كَانُواْ يُوعَدُونَ\nКўзлари қўрқинчга тўлган, ўзларини хорлик қоплаган ҳолда турурлар. Бу ўша, уларга ваъда қилинган кундур'}], '71': [{'chapter': 71, 'verse': 1, 'text': 'إِنَّآ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ أَنۡ أَنذِرۡ قَوۡمَكَ مِن قَبۡلِ أَن يَأۡتِيَهُمۡ عَذَابٌ أَلِيمٞ\nБиз Нуҳни ўз қавмига, қавмингни уларга аламли азоб келишидан аввал огоҳлантиргин, деб Пайғамбар қилиб юбордик'}, {'chapter': 71, 'verse': 2, 'text': 'قَالَ يَٰقَوۡمِ إِنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ\nУ: «Эй қавмим, албатта, мен сиз учун очиқ-ойдин огоҳлантирувчиман'}, {'chapter': 71, 'verse': 3, 'text': 'أَنِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ\nАллоҳга ибодат қилинглар, Унга тақво қилинглар ва менга итоат қилинглар'}, {'chapter': 71, 'verse': 4, 'text': 'يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرۡكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمًّىۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُۚ لَوۡ كُنتُمۡ تَعۡلَمُونَ\nУ сизларнинг гуноҳларингиздан мағфират қиладир ва сизни белгиланган ажалгача қўйиб қўядир. Агар билсангиз, албатта, Аллоҳнинг ажали орқага сурилмайдир», – деди'}, {'chapter': 71, 'verse': 5, 'text': 'قَالَ رَبِّ إِنِّي دَعَوۡتُ قَوۡمِي لَيۡلٗا وَنَهَارٗا\nУ: «Роббим, мен ўз қавмимни кечаю кундуз даъват қилдим'}, {'chapter': 71, 'verse': 6, 'text': 'فَلَمۡ يَزِدۡهُمۡ دُعَآءِيٓ إِلَّا فِرَارٗا\nМенинг чақириғим уларда қочишдан бошқа нарсани зиёда қилмади'}, {'chapter': 71, 'verse': 7, 'text': 'وَإِنِّي كُلَّمَا دَعَوۡتُهُمۡ لِتَغۡفِرَ لَهُمۡ جَعَلُوٓاْ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِمۡ وَٱسۡتَغۡشَوۡاْ ثِيَابَهُمۡ وَأَصَرُّواْ وَٱسۡتَكۡبَرُواْ ٱسۡتِكۡبَارٗا\nВа албатта, қачонки мен уларни Сенинг мағфират қилишинг учун чақирсам, улар бармоқларини қулоқларига тиқдилар ва кийимларига бурканиб олдилар, ҳамда (кофирликда) собит бўлдилар ва ниҳоятда улкан такаббурлик қилдилар'}, {'chapter': 71, 'verse': 8, 'text': 'ثُمَّ إِنِّي دَعَوۡتُهُمۡ جِهَارٗا\nСўнгра, албатта, мен уларни жаҳрий даъват қилдим'}, {'chapter': 71, 'verse': 9, 'text': 'ثُمَّ إِنِّيٓ أَعۡلَنتُ لَهُمۡ وَأَسۡرَرۡتُ لَهُمۡ إِسۡرَارٗا\nСўнгра, албатта, мен уларга ошкора ва яширин (даъват) қилдим'}, {'chapter': 71, 'verse': 10, 'text': 'فَقُلۡتُ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ إِنَّهُۥ كَانَ غَفَّارٗا\nБас, ўз Роббингизга истиғфор айтинг, албатта, У гуноҳларни кўплаб мағфират қилувчидир», дедим'}, {'chapter': 71, 'verse': 11, 'text': 'يُرۡسِلِ ٱلسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا\n«У зот осмондан устингизга кетма-кет барака ёмғирини юборадир'}, {'chapter': 71, 'verse': 12, 'text': 'وَيُمۡدِدۡكُم بِأَمۡوَٰلٖ وَبَنِينَ وَيَجۡعَل لَّكُمۡ جَنَّـٰتٖ وَيَجۡعَل لَّكُمۡ أَنۡهَٰرٗا\nВа сизга молу мулк ва бола-чақа ила мадад берадир ва сизларга боғу роғлар ҳамда анҳорларни берадир'}, {'chapter': 71, 'verse': 13, 'text': 'مَّا لَكُمۡ لَا تَرۡجُونَ لِلَّهِ وَقَارٗا\nСизларга нима бўлдики, Аллоҳнинг азаматини ўйлаб кўрмайсизлар'}, {'chapter': 71, 'verse': 14, 'text': 'وَقَدۡ خَلَقَكُمۡ أَطۡوَارًا\nҲолбуки, У сизларни ҳолатма-ҳолат яратди'}, {'chapter': 71, 'verse': 15, 'text': 'أَلَمۡ تَرَوۡاْ كَيۡفَ خَلَقَ ٱللَّهُ سَبۡعَ سَمَٰوَٰتٖ طِبَاقٗا\nАллоҳ етти осмонни қандоқ қилиб табақама-табақа яратиб қўйганини кўрмадингизми'}, {'chapter': 71, 'verse': 16, 'text': 'وَجَعَلَ ٱلۡقَمَرَ فِيهِنَّ نُورٗا وَجَعَلَ ٱلشَّمۡسَ سِرَاجٗا\nВа улар ичида ойни нур ва қуёшни чироқ қилиб қўйганини кўрмадингизми'}, {'chapter': 71, 'verse': 17, 'text': 'وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلۡأَرۡضِ نَبَاتٗا\nВа Аллоҳ сизларни ердан ўстириб чиқарадир'}, {'chapter': 71, 'verse': 18, 'text': 'ثُمَّ يُعِيدُكُمۡ فِيهَا وَيُخۡرِجُكُمۡ إِخۡرَاجٗا\nСўнгра сизларни унга қайтарадир ва яна чиқарадир'}, {'chapter': 71, 'verse': 19, 'text': 'وَٱللَّهُ جَعَلَ لَكُمُ ٱلۡأَرۡضَ بِسَاطٗا\nВа Аллоҳ сизларга ерни гиламдек тўшаб қўйди'}, {'chapter': 71, 'verse': 20, 'text': 'لِّتَسۡلُكُواْ مِنۡهَا سُبُلٗا فِجَاجٗا\nУнда сиз кенг йўллардан юришингиз учун», деди'}, {'chapter': 71, 'verse': 21, 'text': 'قَالَ نُوحٞ رَّبِّ إِنَّهُمۡ عَصَوۡنِي وَٱتَّبَعُواْ مَن لَّمۡ يَزِدۡهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارٗا\nНуҳ: «Эй Роббим, албатта, улар менга исён қилдилар ва моли, бола-чақаси хусрондан бошқани зиёда қилмаганга эргашдилар'}, {'chapter': 71, 'verse': 22, 'text': 'وَمَكَرُواْ مَكۡرٗا كُبَّارٗا\nВа ниҳоятда улкан макр қилдилар'}, {'chapter': 71, 'verse': 23, 'text': 'وَقَالُواْ لَا تَذَرُنَّ ءَالِهَتَكُمۡ وَلَا تَذَرُنَّ وَدّٗا وَلَا سُوَاعٗا وَلَا يَغُوثَ وَيَعُوقَ وَنَسۡرٗا\nВа улар: «Олиҳаларингизни ҳеч тарк қилманглар, ва, албатта, Ваддни ҳам, Суваъни ҳам, Яғусни ҳам, Яъуқни ҳам ва Насрни ҳам тарк қилманг», дедилар'}, {'chapter': 71, 'verse': 24, 'text': 'وَقَدۡ أَضَلُّواْ كَثِيرٗاۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا ضَلَٰلٗا\nДарҳақиқат, улар кўпларини адаштирди. Ва Сен золимларга залолатдан бошқани зиёда қилма», деди'}, {'chapter': 71, 'verse': 25, 'text': 'مِّمَّا خَطِيٓـَٰٔتِهِمۡ أُغۡرِقُواْ فَأُدۡخِلُواْ نَارٗا فَلَمۡ يَجِدُواْ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارٗا\nУлар хатолари туфайли ғарқ қилиндилар, бас, оловга киритилдилар. Бас, ўзларига Аллоҳдан ўзга ёрдамчи топа олмадилар'}, {'chapter': 71, 'verse': 26, 'text': 'وَقَالَ نُوحٞ رَّبِّ لَا تَذَرۡ عَلَى ٱلۡأَرۡضِ مِنَ ٱلۡكَٰفِرِينَ دَيَّارًا\nВа Нуҳ: «Эй Роббим, ер юзида кофирлардан бирорта ҳам ҳаракатланувчини қўймагин'}, {'chapter': 71, 'verse': 27, 'text': 'إِنَّكَ إِن تَذَرۡهُمۡ يُضِلُّواْ عِبَادَكَ وَلَا يَلِدُوٓاْ إِلَّا فَاجِرٗا كَفَّارٗا\nАлбатта, сен уларни тек қўйсанг, бандаларингни адаштирадилар ва фожиру кофирдан бошқа туғмаслар'}, {'chapter': 71, 'verse': 28, 'text': 'رَّبِّ ٱغۡفِرۡ لِي وَلِوَٰلِدَيَّ وَلِمَن دَخَلَ بَيۡتِيَ مُؤۡمِنٗا وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا تَبَارَۢا\nРоббим, мени мағфират қилгин, менинг ота-онамни ҳам ва уйимга мўмин бўлиб кирганларни ва мўминлару мўминаларни ҳам. Ва золимларга ҳалокатдан ўзга нарсани зиёда қилма», деди'}], '72': [{'chapter': 72, 'verse': 1, 'text': 'قُلۡ أُوحِيَ إِلَيَّ أَنَّهُ ٱسۡتَمَعَ نَفَرٞ مِّنَ ٱلۡجِنِّ فَقَالُوٓاْ إِنَّا سَمِعۡنَا قُرۡءَانًا عَجَبٗا\nСен айт: «Менга ваҳий қилиндики, албатта жинлардан (бир неча) нафари қулоқ осдилар ва дедилар: «Биз ажойиб Қуръонни эшитдик'}, {'chapter': 72, 'verse': 2, 'text': 'يَهۡدِيٓ إِلَى ٱلرُّشۡدِ فَـَٔامَنَّا بِهِۦۖ وَلَن نُّشۡرِكَ بِرَبِّنَآ أَحَدٗا\nУ тўғри йўлга ҳидоят қиладир. Бас, биз унга иймон келтирдик ва ўз Роббимизга бировни ширк келтирмасмиз'}, {'chapter': 72, 'verse': 3, 'text': 'وَأَنَّهُۥ تَعَٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَٰحِبَةٗ وَلَا وَلَدٗا\nВа албатта, Роббимизнинг азамати юксак бўлди. У ўзига хотин ва фарзанд тутгани йўқ'}, {'chapter': 72, 'verse': 4, 'text': 'وَأَنَّهُۥ كَانَ يَقُولُ سَفِيهُنَا عَلَى ٱللَّهِ شَطَطٗا\nВа албатта, эси пастимиз Аллоҳга ноҳақ гапларни айтарди'}, {'chapter': 72, 'verse': 5, 'text': 'وَأَنَّا ظَنَنَّآ أَن لَّن تَقُولَ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَى ٱللَّهِ كَذِبٗا\nВа албатта, биз инс ҳам, жин ҳам Аллоҳга (нисбатан) ёлғон гапирмас, деб ўйлардик'}, {'chapter': 72, 'verse': 6, 'text': 'وَأَنَّهُۥ كَانَ رِجَالٞ مِّنَ ٱلۡإِنسِ يَعُوذُونَ بِرِجَالٖ مِّنَ ٱلۡجِنِّ فَزَادُوهُمۡ رَهَقٗا\nВа албатта, инсдан баъзи кишилар жинларнинг баъзи кишиларидан паноҳ сўрардилар. Бас, бу(жин)лар у(инсон)ларнинг гуноҳ ва хорлигини зиёда қилдилар'}, {'chapter': 72, 'verse': 7, 'text': 'وَأَنَّهُمۡ ظَنُّواْ كَمَا ظَنَنتُمۡ أَن لَّن يَبۡعَثَ ٱللَّهُ أَحَدٗا\nВа албатта, у(инсон)лар худди сизлар гумон қилганингиздек, Аллоҳ бирор кишини ўлгандан сўнг тирилтирмас, деб гумон қилардилар'}, {'chapter': 72, 'verse': 8, 'text': 'وَأَنَّا لَمَسۡنَا ٱلسَّمَآءَ فَوَجَدۡنَٰهَا مُلِئَتۡ حَرَسٗا شَدِيدٗا وَشُهُبٗا\nВа албатта, биз осмон(га етиш)ни талаб қилдик. Бас, уни кучли қўриқчиларга ва учқунларга тўлган ҳолда кўрдик'}, {'chapter': 72, 'verse': 9, 'text': 'وَأَنَّا كُنَّا نَقۡعُدُ مِنۡهَا مَقَٰعِدَ لِلسَّمۡعِۖ فَمَن يَسۡتَمِعِ ٱلۡأٓنَ يَجِدۡ لَهُۥ شِهَابٗا رَّصَدٗا\nВа албатта, биз у(осмон)да тинглашга қулай жойларга жойлашиб олар эдик. Энди эса, ким қулоқ солса, ўзини пойлаб турган учқунга дучор бўладир'}, {'chapter': 72, 'verse': 10, 'text': 'وَأَنَّا لَا نَدۡرِيٓ أَشَرٌّ أُرِيدَ بِمَن فِي ٱلۡأَرۡضِ أَمۡ أَرَادَ بِهِمۡ رَبُّهُمۡ رَشَدٗا\nВа албатта, биз, ер юзидагиларга ёмонлик ирода қилиндими ёки Роббилари уларга рушдни ирода қилдими, билмасмиз'}, {'chapter': 72, 'verse': 11, 'text': 'وَأَنَّا مِنَّا ٱلصَّـٰلِحُونَ وَمِنَّا دُونَ ذَٰلِكَۖ كُنَّا طَرَآئِقَ قِدَدٗا\nВа албатта, биздан солиҳлар ҳам ва бошқалар ҳам бор. Турли гуруҳлар бўлган эдик'}, {'chapter': 72, 'verse': 12, 'text': 'وَأَنَّا ظَنَنَّآ أَن لَّن نُّعۡجِزَ ٱللَّهَ فِي ٱلۡأَرۡضِ وَلَن نُّعۡجِزَهُۥ هَرَبٗا\nВа албатта, биз Аллоҳни ер юзида ожиз қолдира олмаслигимизга ва Ундан қочиб қутула олмаслигимизга ишондик'}, {'chapter': 72, 'verse': 13, 'text': 'وَأَنَّا لَمَّا سَمِعۡنَا ٱلۡهُدَىٰٓ ءَامَنَّا بِهِۦۖ فَمَن يُؤۡمِنۢ بِرَبِّهِۦ فَلَا يَخَافُ بَخۡسٗا وَلَا رَهَقٗا\nВа албатта, биз ҳидоятни эшитган пайтимизда унга иймон келтирдик. Кимки ўз Роббисига иймон келтирса, савоби озайиб қолишдан ҳам, унга оғир юк юкланишидан ҳам қўрқмайди'}, {'chapter': 72, 'verse': 14, 'text': 'وَأَنَّا مِنَّا ٱلۡمُسۡلِمُونَ وَمِنَّا ٱلۡقَٰسِطُونَۖ فَمَنۡ أَسۡلَمَ فَأُوْلَـٰٓئِكَ تَحَرَّوۡاْ رَشَدٗا\nВа албатта, биздан мусулмонлар (бор) ва биздан адашганлар (бор). Кимлар Исломга кирса, бас, улар ҳақ йўлни танлабдилар'}, {'chapter': 72, 'verse': 15, 'text': 'وَأَمَّا ٱلۡقَٰسِطُونَ فَكَانُواْ لِجَهَنَّمَ حَطَبٗا\nВа аммо, адашганлар, бас, жаҳаннамга ўтин бўлибдилар'}, {'chapter': 72, 'verse': 16, 'text': 'وَأَلَّوِ ٱسۡتَقَٰمُواْ عَلَى ٱلطَّرِيقَةِ لَأَسۡقَيۡنَٰهُم مَّآءً غَدَقٗا\nВа агар улар тўғри йўлдан юрсалар, албатта Биз уларни кўп сув ила суғорамиз'}, {'chapter': 72, 'verse': 17, 'text': 'لِّنَفۡتِنَهُمۡ فِيهِۚ وَمَن يُعۡرِضۡ عَن ذِكۡرِ رَبِّهِۦ يَسۡلُكۡهُ عَذَابٗا صَعَدٗا\nУ ила уларни синаш учун. Ва кимки ўз Роббиси зикридан юз ўгирса, уни У зот қийин азобга соладир'}, {'chapter': 72, 'verse': 18, 'text': 'وَأَنَّ ٱلۡمَسَٰجِدَ لِلَّهِ فَلَا تَدۡعُواْ مَعَ ٱللَّهِ أَحَدٗا\nВа албатта, масжидлар Аллоҳникидир. Бас, Аллоҳдан бошқага дуо қилманг'}, {'chapter': 72, 'verse': 19, 'text': 'وَأَنَّهُۥ لَمَّا قَامَ عَبۡدُ ٱللَّهِ يَدۡعُوهُ كَادُواْ يَكُونُونَ عَلَيۡهِ لِبَدٗا\nВа албатта, Аллоҳнинг бандаси Унга ибодат қилиб турганда улар унинг атрофида от ёлидек тўпланиб, босиб юборай дейишди'}, {'chapter': 72, 'verse': 20, 'text': 'قُلۡ إِنَّمَآ أَدۡعُواْ رَبِّي وَلَآ أُشۡرِكُ بِهِۦٓ أَحَدٗا\nСен: «Албатта, мен фақат ўз Роббимга ибодат қилурман ва Унга ҳеч кимни ширк келтирмасман», деб айт'}, {'chapter': 72, 'verse': 21, 'text': 'قُلۡ إِنِّي لَآ أَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا رَشَدٗا\nСен: «Албатта, мен сизлар учун зарарга ҳам, манфаатга ҳам молик эмасман», деб айт'}, {'chapter': 72, 'verse': 22, 'text': 'قُلۡ إِنِّي لَن يُجِيرَنِي مِنَ ٱللَّهِ أَحَدٞ وَلَنۡ أَجِدَ مِن دُونِهِۦ مُلۡتَحَدًا\nСен: «Албатта, мени ҳеч ким Аллоҳ(азоби)дан сақлаб қола олмас ва ҳаргиз Аллоҳдан ўзга пушти паноҳ топа олмасман'}, {'chapter': 72, 'verse': 23, 'text': 'إِلَّا بَلَٰغٗا مِّنَ ٱللَّهِ وَرِسَٰلَٰتِهِۦۚ وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدًا\nФақат, Аллоҳдан У зотнинг топшириқларини етказишдир. Кимки Аллоҳга ва Унинг Пайғамбарига бўйсунмаса, унга жаҳаннам оташи бор, унда абадий қолажаклар», деб айт'}, {'chapter': 72, 'verse': 24, 'text': 'حَتَّىٰٓ إِذَا رَأَوۡاْ مَا يُوعَدُونَ فَسَيَعۡلَمُونَ مَنۡ أَضۡعَفُ نَاصِرٗا وَأَقَلُّ عَدَدٗا\nТоки уларга ваъда қилинган нарсани кўрганларида, кимнинг ёрдамчиси заиф-у, кимнинг сони озлигини ҳам билурлар'}, {'chapter': 72, 'verse': 25, 'text': 'قُلۡ إِنۡ أَدۡرِيٓ أَقَرِيبٞ مَّا تُوعَدُونَ أَمۡ يَجۡعَلُ لَهُۥ رَبِّيٓ أَمَدًا\nСен: «Сизларга ваъда қилинган нарса яқинми ёки Роббим унга узоқ муддат қилганми, билмасман», деб айт'}, {'chapter': 72, 'verse': 26, 'text': 'عَٰلِمُ ٱلۡغَيۡبِ فَلَا يُظۡهِرُ عَلَىٰ غَيۡبِهِۦٓ أَحَدًا\nУ ғайбни билувчидир ва ҳеч кимни ўз ғайбидан хабардор қилмас'}, {'chapter': 72, 'verse': 27, 'text': 'إِلَّا مَنِ ٱرۡتَضَىٰ مِن رَّسُولٖ فَإِنَّهُۥ يَسۡلُكُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ رَصَدٗا\nИлло, Ўзи рози бўлган Пайғамбарлар. Албатта, Аллоҳ унинг олдидан ҳам, ортидан ҳам кузатиб юрувчи қўйиб қўядир'}, {'chapter': 72, 'verse': 28, 'text': 'لِّيَعۡلَمَ أَن قَدۡ أَبۡلَغُواْ رِسَٰلَٰتِ رَبِّهِمۡ وَأَحَاطَ بِمَا لَدَيۡهِمۡ وَأَحۡصَىٰ كُلَّ شَيۡءٍ عَدَدَۢا\n(Пайғамбарлар) ўз Роббилари рисолатларини ҳақиқатан етказганликларини ва Аллоҳ у(банда)лар ҳузуридаги барча нарсани иҳота қилиб олганлигини ва ҳамма нарсанинг ададини ҳисоб қилганлигини билиш учун'}], '73': [{'chapter': 73, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلۡمُزَّمِّلُ\nЭй, ўраниб ётувчи'}, {'chapter': 73, 'verse': 2, 'text': 'قُمِ ٱلَّيۡلَ إِلَّا قَلِيلٗا\nКечанинг озгинасидан бошқасини (ибодатда) тик туриб ўтказ'}, {'chapter': 73, 'verse': 3, 'text': 'نِّصۡفَهُۥٓ أَوِ ٱنقُصۡ مِنۡهُ قَلِيلًا\nЯрмида ёки ундан бир оз қисқароғида (бедор бўл)'}, {'chapter': 73, 'verse': 4, 'text': 'أَوۡ زِدۡ عَلَيۡهِ وَرَتِّلِ ٱلۡقُرۡءَانَ تَرۡتِيلًا\nЁки у(ярим)га яна қўш ва Қуръонни тартил билан тиловат қил'}, {'chapter': 73, 'verse': 5, 'text': 'إِنَّا سَنُلۡقِي عَلَيۡكَ قَوۡلٗا ثَقِيلًا\nАлбатта, Биз устингга оғир сўзни ташлармиз'}, {'chapter': 73, 'verse': 6, 'text': 'إِنَّ نَاشِئَةَ ٱلَّيۡلِ هِيَ أَشَدُّ وَطۡـٔٗا وَأَقۡوَمُ قِيلًا\nАлбатта, кечки ибодат кўпроқ мувофиқ келур ва қироати тўғрироқ бўлур'}, {'chapter': 73, 'verse': 7, 'text': 'إِنَّ لَكَ فِي ٱلنَّهَارِ سَبۡحٗا طَوِيلٗا\nАлбатта, кундузи сенинг узоқ ишлашинг бор'}, {'chapter': 73, 'verse': 8, 'text': 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ وَتَبَتَّلۡ إِلَيۡهِ تَبۡتِيلٗا\nВа Роббингнинг исмини зикр эт ва Унинг ибодатига бутунлай ажралиб чиқ'}, {'chapter': 73, 'verse': 9, 'text': 'رَّبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ لَآ إِلَٰهَ إِلَّا هُوَ فَٱتَّخِذۡهُ وَكِيلٗا\nУ машриқу мағрибнинг Роббидир. Ундан ўзга ибодатга сазовор илоҳ йўқ. Бас, Уни ўзингга вакил қил'}, {'chapter': 73, 'verse': 10, 'text': 'وَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَٱهۡجُرۡهُمۡ هَجۡرٗا جَمِيلٗا\nВа уларнинг гапларига сабр қил ва улардан чиройли четлаш-ла четлаб тур'}, {'chapter': 73, 'verse': 11, 'text': 'وَذَرۡنِي وَٱلۡمُكَذِّبِينَ أُوْلِي ٱلنَّعۡمَةِ وَمَهِّلۡهُمۡ قَلِيلًا\nАҳли неъмат бўлган ёлғонга чиқарувчиларни Ўзимга қўйиб қўй ва уларга озгина муҳлат бер'}, {'chapter': 73, 'verse': 12, 'text': 'إِنَّ لَدَيۡنَآ أَنكَالٗا وَجَحِيمٗا\nАлбатта, Бизда оғир кишанлар ва жаҳаннам бор'}, {'chapter': 73, 'verse': 13, 'text': 'وَطَعَامٗا ذَا غُصَّةٖ وَعَذَابًا أَلِيمٗا\nВа (томоққа) тиқиладиган таом ва аламли азоб бор'}, {'chapter': 73, 'verse': 14, 'text': 'يَوۡمَ تَرۡجُفُ ٱلۡأَرۡضُ وَٱلۡجِبَالُ وَكَانَتِ ٱلۡجِبَالُ كَثِيبٗا مَّهِيلًا\nБир кундаки, унда еру тоғлар ларзага келади ва тоғлар оқувчи қумтепаларга айланади'}, {'chapter': 73, 'verse': 15, 'text': 'إِنَّآ أَرۡسَلۡنَآ إِلَيۡكُمۡ رَسُولٗا شَٰهِدًا عَلَيۡكُمۡ كَمَآ أَرۡسَلۡنَآ إِلَىٰ فِرۡعَوۡنَ رَسُولٗا\nАлбатта, Биз сизларга, худди Фиръавнга Расул юборганимиздек, сиз ҳақингизда гувоҳлик берувчи Расулни юбордик'}, {'chapter': 73, 'verse': 16, 'text': 'فَعَصَىٰ فِرۡعَوۡنُ ٱلرَّسُولَ فَأَخَذۡنَٰهُ أَخۡذٗا وَبِيلٗا\nБас, Фиръавн Расулга осий бўлди ва Биз уни шиддатли азоб билан тутдик'}, {'chapter': 73, 'verse': 17, 'text': 'فَكَيۡفَ تَتَّقُونَ إِن كَفَرۡتُمۡ يَوۡمٗا يَجۡعَلُ ٱلۡوِلۡدَٰنَ شِيبًا\nАгар кофир бўлсаларингиз, болаларнинг сочини оқартириб юборадиган кундан қандай сақланурсиз'}, {'chapter': 73, 'verse': 18, 'text': 'ٱلسَّمَآءُ مُنفَطِرُۢ بِهِۦۚ كَانَ وَعۡدُهُۥ مَفۡعُولًا\nУнда осмон парчаланувчидир, У зотнинг ваъдаси амалга ошадир'}, {'chapter': 73, 'verse': 19, 'text': 'إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا\nАлбатта, булар эслатмадир, Ким хоҳласа, ўз Робби томон йўл оладир'}, {'chapter': 73, 'verse': 20, 'text': '۞إِنَّ رَبَّكَ يَعۡلَمُ أَنَّكَ تَقُومُ أَدۡنَىٰ مِن ثُلُثَيِ ٱلَّيۡلِ وَنِصۡفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٞ مِّنَ ٱلَّذِينَ مَعَكَۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيۡلَ وَٱلنَّهَارَۚ عَلِمَ أَن لَّن تُحۡصُوهُ فَتَابَ عَلَيۡكُمۡۖ فَٱقۡرَءُواْ مَا تَيَسَّرَ مِنَ ٱلۡقُرۡءَانِۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرۡضَىٰ وَءَاخَرُونَ يَضۡرِبُونَ فِي ٱلۡأَرۡضِ يَبۡتَغُونَ مِن فَضۡلِ ٱللَّهِ وَءَاخَرُونَ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِۖ فَٱقۡرَءُواْ مَا تَيَسَّرَ مِنۡهُۚ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَقۡرِضُواْ ٱللَّهَ قَرۡضًا حَسَنٗاۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيۡرٗا وَأَعۡظَمَ أَجۡرٗاۚ وَٱسۡتَغۡفِرُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمُۢ\nАлбатта, Роббинг сени кечанинг учдан иккисидан озроғини, ё ярмини, ё учдан бирини қоим бўлиб ўтказаётганингни ва сен билан бирга бўлганлардан ҳам бир тоифа (шундоқ қилаётганларини) биладир. Ва Аллоҳ кечаю кундузни Ўзи ўлчайдир. У сизлар ҳеч чидай олмаслигингизни билиб, сизларга (енгилликни) қайтарди. Бас (энди), Қуръондан муяссар бўлганича ўқинглар. Сизлардан келажакда касаллар бўлишини ҳам билди. Бошқалар Ер юзида юриб, Аллоҳнинг фазлини ахтаришини, яна бошқалар эса, Аллоҳнинг йўлида жиҳод қилишларини (ҳам билди). Бас, ундан муяссар бўлганича қироат қилинглар. Ва намозни қоим қилинглар, закот беринглар ва Аллоҳга яхши қарз беринглар. Ўзингиз учун нима яхшилик қилган бўлсангиз, Аллоҳнинг ҳузурида ундан яхшироғини ва улканроқ ажрни топарсизлар. Аллоҳга истиғфор айтинглар. Албатта, Аллоҳ мағфират қилувчи ва раҳмдилдир'}], '74': [{'chapter': 74, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلۡمُدَّثِّرُ\nЭй, бурканиб ётган'}, {'chapter': 74, 'verse': 2, 'text': 'قُمۡ فَأَنذِرۡ\nТур ва огоҳлантир'}, {'chapter': 74, 'verse': 3, 'text': 'وَرَبَّكَ فَكَبِّرۡ\nВа Роббингни улуғла'}, {'chapter': 74, 'verse': 4, 'text': 'وَثِيَابَكَ فَطَهِّرۡ\nВа кийимингни покла'}, {'chapter': 74, 'verse': 5, 'text': 'وَٱلرُّجۡزَ فَٱهۡجُرۡ\nВа азоб(сабабчиси)дан четлан'}, {'chapter': 74, 'verse': 6, 'text': 'وَلَا تَمۡنُن تَسۡتَكۡثِرُ\nВа кўп (иш) қилган бўлсанг ҳам, миннат қилма'}, {'chapter': 74, 'verse': 7, 'text': 'وَلِرَبِّكَ فَٱصۡبِرۡ\nВа Роббинг учун сабр қил'}, {'chapter': 74, 'verse': 8, 'text': 'فَإِذَا نُقِرَ فِي ٱلنَّاقُورِ\nБас, вақти келиб, дудга пуфланса'}, {'chapter': 74, 'verse': 9, 'text': 'فَذَٰلِكَ يَوۡمَئِذٖ يَوۡمٌ عَسِيرٌ\nБас, ўша, ўшал кун қийин кундир'}, {'chapter': 74, 'verse': 10, 'text': 'عَلَى ٱلۡكَٰفِرِينَ غَيۡرُ يَسِيرٖ\nУ кофирларга осон эмасдир'}, {'chapter': 74, 'verse': 11, 'text': 'ذَرۡنِي وَمَنۡ خَلَقۡتُ وَحِيدٗا\nМени Ўзим якка яратган киши билан қўйиб қўй'}, {'chapter': 74, 'verse': 12, 'text': 'وَجَعَلۡتُ لَهُۥ مَالٗا مَّمۡدُودٗا\nВа унга кўплаб молу дунё бериб қўйдим'}, {'chapter': 74, 'verse': 13, 'text': 'وَبَنِينَ شُهُودٗا\nВа ҳозиру нозир фарзандлар ҳам'}, {'chapter': 74, 'verse': 14, 'text': 'وَمَهَّدتُّ لَهُۥ تَمۡهِيدٗا\nВа унга (ҳамма нарсани) осонлаштириб қўйдим'}, {'chapter': 74, 'verse': 15, 'text': 'ثُمَّ يَطۡمَعُ أَنۡ أَزِيدَ\nСўнгра, яна зиёда қилишимни тамаъ қилур'}, {'chapter': 74, 'verse': 16, 'text': 'كَلَّآۖ إِنَّهُۥ كَانَ لِأٓيَٰتِنَا عَنِيدٗا\nЙўқ! Чунки, у Бизнинг оятларимизга саркаш эди'}, {'chapter': 74, 'verse': 17, 'text': 'سَأُرۡهِقُهُۥ صَعُودًا\nТезда уни машаққатли чиқиш-ла ҳоритаман'}, {'chapter': 74, 'verse': 18, 'text': 'إِنَّهُۥ فَكَّرَ وَقَدَّرَ\nАлбатта, у тафаккур қилди ва чамалаб кўрди'}, {'chapter': 74, 'verse': 19, 'text': 'فَقُتِلَ كَيۡفَ قَدَّرَ\nЛаънат бўлсин унга, қандоқ ҳам чамалади'}, {'chapter': 74, 'verse': 20, 'text': 'ثُمَّ قُتِلَ كَيۡفَ قَدَّرَ\nСўнгра яна лаънат бўлсин унга, қандоқ ҳам чамалади'}, {'chapter': 74, 'verse': 21, 'text': 'ثُمَّ نَظَرَ\nСўнгра назар солди'}, {'chapter': 74, 'verse': 22, 'text': 'ثُمَّ عَبَسَ وَبَسَرَ\nСўнгра юзини буриштирди ва қавоғини солди'}, {'chapter': 74, 'verse': 23, 'text': 'ثُمَّ أَدۡبَرَ وَٱسۡتَكۡبَرَ\nСўнгра юз ўгирди ва такаббурлик қилди'}, {'chapter': 74, 'verse': 24, 'text': 'فَقَالَ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ يُؤۡثَرُ\nБас, «Бу асар қолган сеҳрдан ўзга ҳеч нарса эмас'}, {'chapter': 74, 'verse': 25, 'text': 'إِنۡ هَٰذَآ إِلَّا قَوۡلُ ٱلۡبَشَرِ\nБу башар сўзидан ўзга ҳеч нарса эмас», деди'}, {'chapter': 74, 'verse': 26, 'text': 'سَأُصۡلِيهِ سَقَرَ\nТезда уни сақар(дўзахи)га киритурман'}, {'chapter': 74, 'verse': 27, 'text': 'وَمَآ أَدۡرَىٰكَ مَا سَقَرُ\nВа сақар нималигини сенга нима билдирди'}, {'chapter': 74, 'verse': 28, 'text': 'لَا تُبۡقِي وَلَا تَذَرُ\nУ боқий қолдирмас ва тарк ҳам қилмас'}, {'chapter': 74, 'verse': 29, 'text': 'لَوَّاحَةٞ لِّلۡبَشَرِ\nУ терини кўп куйдирувчидир'}, {'chapter': 74, 'verse': 30, 'text': 'عَلَيۡهَا تِسۡعَةَ عَشَرَ\nУ(сақар)нинг устида ўн тўққизта (фаришта) бор'}, {'chapter': 74, 'verse': 31, 'text': 'وَمَا جَعَلۡنَآ أَصۡحَٰبَ ٱلنَّارِ إِلَّا مَلَـٰٓئِكَةٗۖ وَمَا جَعَلۡنَا عِدَّتَهُمۡ إِلَّا فِتۡنَةٗ لِّلَّذِينَ كَفَرُواْ لِيَسۡتَيۡقِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَيَزۡدَادَ ٱلَّذِينَ ءَامَنُوٓاْ إِيمَٰنٗا وَلَا يَرۡتَابَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَٱلۡمُؤۡمِنُونَ وَلِيَقُولَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡكَٰفِرُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَمَا يَعۡلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَۚ وَمَا هِيَ إِلَّا ذِكۡرَىٰ لِلۡبَشَرِ\nДўзах соҳибларини фаришталардан қилганимиз ва уларнинг адади ҳам фақат кофирларни синаш учун. Китоб берилганлар ишонч ҳосил қилишлари учун. Ва иймонлиларнинг иймони зиёда бўлиши учун. Китоб берилганлар ва мўминлар шак-шубҳа қилмасликлари учун ва қалбларида касали борлар (мунофиқлар) ва кофирлар, Аллоҳ бу билан нима мисолни ирода қилмоқчи, дейишлари учун. Шундоқ қилиб, Аллоҳ хоҳлаган кимсани адаштирур ва хоҳлаган кимсани ҳидоятга солур. Роббингнинг аскарларини Ўзидан бошқа ҳеч ким билмас. Ва у (дўзах) башар учун эслатмадан ўзга ҳеч нарса эмас'}, {'chapter': 74, 'verse': 32, 'text': 'كَلَّا وَٱلۡقَمَرِ\nЙўқ! Ой билан қасам'}, {'chapter': 74, 'verse': 33, 'text': 'وَٱلَّيۡلِ إِذۡ أَدۡبَرَ\nВа чекинаётган тун билан қасам'}, {'chapter': 74, 'verse': 34, 'text': 'وَٱلصُّبۡحِ إِذَآ أَسۡفَرَ\nВа ёришаётган тонг билан қасам'}, {'chapter': 74, 'verse': 35, 'text': 'إِنَّهَا لَإِحۡدَى ٱلۡكُبَرِ\nАлбатта, у(дўзах) улкан(азоб)ларнинг биридир'}, {'chapter': 74, 'verse': 36, 'text': 'نَذِيرٗا لِّلۡبَشَرِ\nУ башарият учун огоҳлантирувчидир'}, {'chapter': 74, 'verse': 37, 'text': 'لِمَن شَآءَ مِنكُمۡ أَن يَتَقَدَّمَ أَوۡ يَتَأَخَّرَ\nСизлардан пешқадам бўлишни ёки ортда қолишни хоҳлаётган кимсалар учундир'}, {'chapter': 74, 'verse': 38, 'text': 'كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ رَهِينَةٌ\nҲар бир шахс ўз касбининг гаровидир'}, {'chapter': 74, 'verse': 39, 'text': 'إِلَّآ أَصۡحَٰبَ ٱلۡيَمِينِ\n(Бундан) ўнг тараф соҳиблари мустаснодир'}, {'chapter': 74, 'verse': 40, 'text': 'فِي جَنَّـٰتٖ يَتَسَآءَلُونَ\nУлар жаннатларда, сўрайдирлар'}, {'chapter': 74, 'verse': 41, 'text': 'عَنِ ٱلۡمُجۡرِمِينَ\nГуноҳкорлардан'}, {'chapter': 74, 'verse': 42, 'text': 'مَا سَلَكَكُمۡ فِي سَقَرَ\n«Сизларни дўзахга нима киритди?» (деб)'}, {'chapter': 74, 'verse': 43, 'text': 'قَالُواْ لَمۡ نَكُ مِنَ ٱلۡمُصَلِّينَ\nАлар: «Намоз ўқийдиганлардан бўлмаган эдик'}, {'chapter': 74, 'verse': 44, 'text': 'وَلَمۡ نَكُ نُطۡعِمُ ٱلۡمِسۡكِينَ\nВа мискинларга таом бермаган эдик'}, {'chapter': 74, 'verse': 45, 'text': 'وَكُنَّا نَخُوضُ مَعَ ٱلۡخَآئِضِينَ\nВа ботилга шўнғувчилар билан бирга шўнғир эдик'}, {'chapter': 74, 'verse': 46, 'text': 'وَكُنَّا نُكَذِّبُ بِيَوۡمِ ٱلدِّينِ\nВа қиёмат кунини ёлғонга чиқарар эдик'}, {'chapter': 74, 'verse': 47, 'text': 'حَتَّىٰٓ أَتَىٰنَا ٱلۡيَقِينُ\nТоки бизга ўлим келгунича», дерлар'}, {'chapter': 74, 'verse': 48, 'text': 'فَمَا تَنفَعُهُمۡ شَفَٰعَةُ ٱلشَّـٰفِعِينَ\nБас, уларга шафоатчиларнинг шафоати манфаат бермайдир'}, {'chapter': 74, 'verse': 49, 'text': 'فَمَا لَهُمۡ عَنِ ٱلتَّذۡكِرَةِ مُعۡرِضِينَ\nУларга нима бўлдики, эслатмадан юз ўгирарлар'}, {'chapter': 74, 'verse': 50, 'text': 'كَأَنَّهُمۡ حُمُرٞ مُّسۡتَنفِرَةٞ\nХудди қаттиқ қўрққан эшаклар'}, {'chapter': 74, 'verse': 51, 'text': 'فَرَّتۡ مِن قَسۡوَرَةِۭ\nАрслондан қочгани каби'}, {'chapter': 74, 'verse': 52, 'text': 'بَلۡ يُرِيدُ كُلُّ ٱمۡرِيٕٖ مِّنۡهُمۡ أَن يُؤۡتَىٰ صُحُفٗا مُّنَشَّرَةٗ\nБалки улардан ҳар бири ўзига нашр қилинган саҳифалар берилишини ирода қилар'}, {'chapter': 74, 'verse': 53, 'text': 'كَلَّاۖ بَل لَّا يَخَافُونَ ٱلۡأٓخِرَةَ\nЙўқ! Балки улар охиратдан қўрқмаслар'}, {'chapter': 74, 'verse': 54, 'text': 'كَلَّآ إِنَّهُۥ تَذۡكِرَةٞ\nЙўқ! Албатта, у(Қуръон) эслатмадур'}, {'chapter': 74, 'verse': 55, 'text': 'فَمَن شَآءَ ذَكَرَهُۥ\nБас, ким хоҳласа, уни эслайдир'}, {'chapter': 74, 'verse': 56, 'text': 'وَمَا يَذۡكُرُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ هُوَ أَهۡلُ ٱلتَّقۡوَىٰ وَأَهۡلُ ٱلۡمَغۡفِرَةِ\nВа фақат Аллоҳ хоҳласагина, эсларлар. У зот тақво аҳлидир ва мағфират аҳлидир'}], '75': [{'chapter': 75, 'verse': 1, 'text': 'لَآ أُقۡسِمُ بِيَوۡمِ ٱلۡقِيَٰمَةِ\nҚиёмат куни билан қасам'}, {'chapter': 75, 'verse': 2, 'text': 'وَلَآ أُقۡسِمُ بِٱلنَّفۡسِ ٱللَّوَّامَةِ\nВа маломатчи нафс билан қасам'}, {'chapter': 75, 'verse': 3, 'text': 'أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَلَّن نَّجۡمَعَ عِظَامَهُۥ\n(Кофир) Инсон Бизни унинг суякларини жамлаб олмас, деб ўйларми'}, {'chapter': 75, 'verse': 4, 'text': 'بَلَىٰ قَٰدِرِينَ عَلَىٰٓ أَن نُّسَوِّيَ بَنَانَهُۥ\nАлбатта, Биз унинг бармоқ учларини ҳам асл ҳолига келтиришга қодирмиз'}, {'chapter': 75, 'verse': 5, 'text': 'بَلۡ يُرِيدُ ٱلۡإِنسَٰنُ لِيَفۡجُرَ أَمَامَهُۥ\nБалки, инсон келажакда фисқу фужур қилишни хоҳлайдир'}, {'chapter': 75, 'verse': 6, 'text': 'يَسۡـَٔلُ أَيَّانَ يَوۡمُ ٱلۡقِيَٰمَةِ\nҚиёмат куни қачон? — деб сўрайдир'}, {'chapter': 75, 'verse': 7, 'text': 'فَإِذَا بَرِقَ ٱلۡبَصَرُ\nҚачонки кўз лол қолиб қўрқинчга тўлса'}, {'chapter': 75, 'verse': 8, 'text': 'وَخَسَفَ ٱلۡقَمَرُ\nВа ой тутилса'}, {'chapter': 75, 'verse': 9, 'text': 'وَجُمِعَ ٱلشَّمۡسُ وَٱلۡقَمَرُ\nВа қуёш билан ой жамланса'}, {'chapter': 75, 'verse': 10, 'text': 'يَقُولُ ٱلۡإِنسَٰنُ يَوۡمَئِذٍ أَيۡنَ ٱلۡمَفَرُّ\nЎшал кунда инсон: «Қочар жой қаерда?» деб қоладир'}, {'chapter': 75, 'verse': 11, 'text': 'كَلَّا لَا وَزَرَ\nЙўқ! Қочар жой йўқдир'}, {'chapter': 75, 'verse': 12, 'text': 'إِلَىٰ رَبِّكَ يَوۡمَئِذٍ ٱلۡمُسۡتَقَرُّ\nУ кунда жойлашув фақат Роббинг ҳузурида бўладир'}, {'chapter': 75, 'verse': 13, 'text': 'يُنَبَّؤُاْ ٱلۡإِنسَٰنُ يَوۡمَئِذِۭ بِمَا قَدَّمَ وَأَخَّرَ\nУ кунда инсонга аввалу охир қилган ишларининг хабари бериладир'}, {'chapter': 75, 'verse': 14, 'text': 'بَلِ ٱلۡإِنسَٰنُ عَلَىٰ نَفۡسِهِۦ بَصِيرَةٞ\nҲа, инсон ўз нафсига қарши ўзи шоҳиддир'}, {'chapter': 75, 'verse': 15, 'text': 'وَلَوۡ أَلۡقَىٰ مَعَاذِيرَهُۥ\nВа агар узрларини тўкиб солса ҳам'}, {'chapter': 75, 'verse': 16, 'text': 'لَا تُحَرِّكۡ بِهِۦ لِسَانَكَ لِتَعۡجَلَ بِهِۦٓ\nСен шошилиб у билан тилингни қимирлатма'}, {'chapter': 75, 'verse': 17, 'text': 'إِنَّ عَلَيۡنَا جَمۡعَهُۥ وَقُرۡءَانَهُۥ\nАлбатта, у(Қуръон)ни жамлаш ва ўқиб бериш Бизнинг зиммамизда'}, {'chapter': 75, 'verse': 18, 'text': 'فَإِذَا قَرَأۡنَٰهُ فَٱتَّبِعۡ قُرۡءَانَهُۥ\nБас, Биз уни ўқисак, қироатига зеҳн солиб тур'}, {'chapter': 75, 'verse': 19, 'text': 'ثُمَّ إِنَّ عَلَيۡنَا بَيَانَهُۥ\nСўнгра у(Қуръон)ни баён қилиб бериш ҳам Бизнинг зиммамизда'}, {'chapter': 75, 'verse': 20, 'text': 'كَلَّا بَلۡ تُحِبُّونَ ٱلۡعَاجِلَةَ\nЙўқ! Балки сизлар шошар дунёни яхши кўрасизлар'}, {'chapter': 75, 'verse': 21, 'text': 'وَتَذَرُونَ ٱلۡأٓخِرَةَ\nВа охиратни тарк этасизлар'}, {'chapter': 75, 'verse': 22, 'text': 'وُجُوهٞ يَوۡمَئِذٖ نَّاضِرَةٌ\nУ кунда чиройли юзлар бор'}, {'chapter': 75, 'verse': 23, 'text': 'إِلَىٰ رَبِّهَا نَاظِرَةٞ\nЎз Роббисига назар солувчилар'}, {'chapter': 75, 'verse': 24, 'text': 'وَوُجُوهٞ يَوۡمَئِذِۭ بَاسِرَةٞ\nВа у кунда тиришиб-буришган юзлар ҳам бор'}, {'chapter': 75, 'verse': 25, 'text': 'تَظُنُّ أَن يُفۡعَلَ بِهَا فَاقِرَةٞ\nЎзларига умуртқасини синдирадиган иш бўлишига ишонарлар'}, {'chapter': 75, 'verse': 26, 'text': 'كَلَّآ إِذَا بَلَغَتِ ٱلتَّرَاقِيَ\nЙўқ! Вақтики, (жон) ҳалқумга келса'}, {'chapter': 75, 'verse': 27, 'text': 'وَقِيلَ مَنۡۜ رَاقٖ\nВа «Ким буни қутқарар?» дейилса'}, {'chapter': 75, 'verse': 28, 'text': 'وَظَنَّ أَنَّهُ ٱلۡفِرَاقُ\nВа, албатта, бу фироқ эканлигига ишонса'}, {'chapter': 75, 'verse': 29, 'text': 'وَٱلۡتَفَّتِ ٱلسَّاقُ بِٱلسَّاقِ\nВа болдирлар бир-бирига алмашса'}, {'chapter': 75, 'verse': 30, 'text': 'إِلَىٰ رَبِّكَ يَوۡمَئِذٍ ٱلۡمَسَاقُ\nУ кунда ҳайдаб бориш жойи Роббинг ҳузуридадир'}, {'chapter': 75, 'verse': 31, 'text': 'فَلَا صَدَّقَ وَلَا صَلَّىٰ\nБас, у тасдиқламади ва намоз ўқимади'}, {'chapter': 75, 'verse': 32, 'text': 'وَلَٰكِن كَذَّبَ وَتَوَلَّىٰ\nВа лекин ёлғонга чиқарди ва юз ўгирди'}, {'chapter': 75, 'verse': 33, 'text': 'ثُمَّ ذَهَبَ إِلَىٰٓ أَهۡلِهِۦ يَتَمَطَّىٰٓ\nСўнг ўз аҳлига керилиб борди'}, {'chapter': 75, 'verse': 34, 'text': 'أَوۡلَىٰ لَكَ فَأَوۡلَىٰ\nҲолингга вой бўлсин сенинг, вой бўлсин'}, {'chapter': 75, 'verse': 35, 'text': 'ثُمَّ أَوۡلَىٰ لَكَ فَأَوۡلَىٰٓ\nЯна ҳолингга вой бўлсин сенинг, вой бўлсин'}, {'chapter': 75, 'verse': 36, 'text': 'أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَن يُتۡرَكَ سُدًى\nИнсон беҳуда тарк қилинишликнинг ҳисобини қилурми'}, {'chapter': 75, 'verse': 37, 'text': 'أَلَمۡ يَكُ نُطۡفَةٗ مِّن مَّنِيّٖ يُمۡنَىٰ\nУ манийдан оққан нутфа бўлмаганмиди'}, {'chapter': 75, 'verse': 38, 'text': 'ثُمَّ كَانَ عَلَقَةٗ فَخَلَقَ فَسَوَّىٰ\nСўнг алақа бўлди. Бас, беками-кўст этиб яратди'}, {'chapter': 75, 'verse': 39, 'text': 'فَجَعَلَ مِنۡهُ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ\nБас, ундан жуфтни — эркак ва аёлни яратди'}, {'chapter': 75, 'verse': 40, 'text': 'أَلَيۡسَ ذَٰلِكَ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰ\nШундай Зот ўликларни тирилтиришга қодир эмасми'}], '76': [{'chapter': 76, 'verse': 1, 'text': 'هَلۡ أَتَىٰ عَلَى ٱلۡإِنسَٰنِ حِينٞ مِّنَ ٱلدَّهۡرِ لَمۡ يَكُن شَيۡـٔٗا مَّذۡكُورًا\nДарҳақиқат, инсонга у эсга олинадиган нарса бўлмаган бир замон келмадими'}, {'chapter': 76, 'verse': 2, 'text': 'إِنَّا خَلَقۡنَا ٱلۡإِنسَٰنَ مِن نُّطۡفَةٍ أَمۡشَاجٖ نَّبۡتَلِيهِ فَجَعَلۡنَٰهُ سَمِيعَۢا بَصِيرًا\nАлбатта, Биз инсонни аралаш нутфадан яратдик. Синаш учун, бас, уни эшитадиган, кўрадиган қилдик'}, {'chapter': 76, 'verse': 3, 'text': 'إِنَّا هَدَيۡنَٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرٗا وَإِمَّا كَفُورًا\nАлбатта, Биз унга йўл кўрсатдик. (У эса) ёки шукр қилувчи, ёки куфр келтирувчи бўлди'}, {'chapter': 76, 'verse': 4, 'text': 'إِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَلَٰسِلَاْ وَأَغۡلَٰلٗا وَسَعِيرًا\nАлбатта, Биз кофирлар учун занжирлар, кишанлар ва ёниб турган оловни тайёрлаб қўйганмиз'}, {'chapter': 76, 'verse': 5, 'text': 'إِنَّ ٱلۡأَبۡرَارَ يَشۡرَبُونَ مِن كَأۡسٖ كَانَ مِزَاجُهَا كَافُورًا\nАлбатта, яхшилар каафур аралаштирилган қадаҳдан ичарлар'}, {'chapter': 76, 'verse': 6, 'text': 'عَيۡنٗا يَشۡرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفۡجِيرٗا\nБир булоқданки, ундан Аллоҳнинг бандалари отилтириб ичарлар'}, {'chapter': 76, 'verse': 7, 'text': 'يُوفُونَ بِٱلنَّذۡرِ وَيَخَافُونَ يَوۡمٗا كَانَ شَرُّهُۥ مُسۡتَطِيرٗا\nУлар назрга вафо қиларлар ва ёмонлиги кенг тарқаладиган кундан қўрқарлар'}, {'chapter': 76, 'verse': 8, 'text': 'وَيُطۡعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسۡكِينٗا وَيَتِيمٗا وَأَسِيرًا\nЎзлари таомни яхши кўриб турсалар ҳам, уни мискин, етим ва асирларга берарлар'}, {'chapter': 76, 'verse': 9, 'text': 'إِنَّمَا نُطۡعِمُكُمۡ لِوَجۡهِ ٱللَّهِ لَا نُرِيدُ مِنكُمۡ جَزَآءٗ وَلَا شُكُورًا\n«Албатта, биз сизларни фақат Аллоҳнинг розилиги учун овқатлантирамиз. Бунинг эвазига сизлардан мукофот ёки ташаккур хоҳламаймиз'}, {'chapter': 76, 'verse': 10, 'text': 'إِنَّا نَخَافُ مِن رَّبِّنَا يَوۡمًا عَبُوسٗا قَمۡطَرِيرٗا\nАлбатта, биз қўрқинчли, шиддатли, узун кунда ўз Роббимиздан қўрқурмиз», дерлар'}, {'chapter': 76, 'verse': 11, 'text': 'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلۡيَوۡمِ وَلَقَّىٰهُمۡ نَضۡرَةٗ وَسُرُورٗا\nБас, уларни Роббилари ўшал кун шарридан сақлади ва уларга хушрўйлик ҳамда хурсандчилик ато қилди'}, {'chapter': 76, 'verse': 12, 'text': 'وَجَزَىٰهُم بِمَا صَبَرُواْ جَنَّةٗ وَحَرِيرٗا\nВа сабр қилганликлари учун, уларни боғу роғ ва ипаклар билан мукофотлади'}, {'chapter': 76, 'verse': 13, 'text': 'مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۖ لَا يَرَوۡنَ فِيهَا شَمۡسٗا وَلَا زَمۡهَرِيرٗا\nУ ерда сўриларда суянган ҳолдалар. У ерда қуёшни ҳам, совуқни ҳам кўрмаслар'}, {'chapter': 76, 'verse': 14, 'text': 'وَدَانِيَةً عَلَيۡهِمۡ ظِلَٰلُهَا وَذُلِّلَتۡ قُطُوفُهَا تَذۡلِيلٗا\nВа устиларига у(жаннат)нинг соялари яқиндир ҳамда унинг мевалари жуда ҳам осон қилингандир'}, {'chapter': 76, 'verse': 15, 'text': 'وَيُطَافُ عَلَيۡهِم بِـَٔانِيَةٖ مِّن فِضَّةٖ وَأَكۡوَابٖ كَانَتۡ قَوَارِيرَا۠\nВа уларга кумуш идишлар ва биллурлардан бўлган қадаҳлар айлантириб турилур'}, {'chapter': 76, 'verse': 16, 'text': 'قَوَارِيرَاْ مِن فِضَّةٖ قَدَّرُوهَا تَقۡدِيرٗا\nБиллурлар кумушдандир, (соқийлар) ўлчовини келтиргандирлар'}, {'chapter': 76, 'verse': 17, 'text': 'وَيُسۡقَوۡنَ فِيهَا كَأۡسٗا كَانَ مِزَاجُهَا زَنجَبِيلًا\nВа улар у(ер)да аралашмаси занжабийл бўлган қадаҳдан суғорилурлар'}, {'chapter': 76, 'verse': 18, 'text': 'عَيۡنٗا فِيهَا تُسَمَّىٰ سَلۡسَبِيلٗا\nУ(ер)даги «салсабийл» номли булоқдан'}, {'chapter': 76, 'verse': 19, 'text': '۞وَيَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ إِذَا رَأَيۡتَهُمۡ حَسِبۡتَهُمۡ لُؤۡلُؤٗا مَّنثُورٗا\nВа уларнинг атрофида абадий ёш болалар айланарлар, агар уларни кўрсанг, сочилган маржон, деб ўйларсан'}, {'chapter': 76, 'verse': 20, 'text': 'وَإِذَا رَأَيۡتَ ثَمَّ رَأَيۡتَ نَعِيمٗا وَمُلۡكٗا كَبِيرًا\nВа агар кўрсанг, у ерда неъматни ва улкан мулкни кўрурсан'}, {'chapter': 76, 'verse': 21, 'text': 'عَٰلِيَهُمۡ ثِيَابُ سُندُسٍ خُضۡرٞ وَإِسۡتَبۡرَقٞۖ وَحُلُّوٓاْ أَسَاوِرَ مِن فِضَّةٖ وَسَقَىٰهُمۡ رَبُّهُمۡ شَرَابٗا طَهُورًا\nУларнинг устиларида яшил сундусдан (юпқа ипакдан) ва истабрақдан (қалин ипакдан) кийимлар бор ва кумуш билакузуклар-ла ясанганлар ва Роббилари уларни пок шароб билан суғорур'}, {'chapter': 76, 'verse': 22, 'text': 'إِنَّ هَٰذَا كَانَ لَكُمۡ جَزَآءٗ وَكَانَ سَعۡيُكُم مَّشۡكُورًا\nАлбатта, бу сизга мукофот бўлди ва сизнинг амалингиз қабул бўлди'}, {'chapter': 76, 'verse': 23, 'text': 'إِنَّا نَحۡنُ نَزَّلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ تَنزِيلٗا\nАлбатта, Биз сенга Қуръонни бўлак-бўлак қилиб туширдик'}, {'chapter': 76, 'verse': 24, 'text': 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تُطِعۡ مِنۡهُمۡ ءَاثِمًا أَوۡ كَفُورٗا\nБас, Роббинг ҳукмига сабр қил ва улардан гуноҳкор ёки кофирларига итоат қилма'}, {'chapter': 76, 'verse': 25, 'text': 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ بُكۡرَةٗ وَأَصِيلٗا\nЭртаю кеч Роббинг исмини зикр қил'}, {'chapter': 76, 'verse': 26, 'text': 'وَمِنَ ٱلَّيۡلِ فَٱسۡجُدۡ لَهُۥ وَسَبِّحۡهُ لَيۡلٗا طَوِيلًا\nВа кечасида ҳам Унга сажда қил ва узоқ кечаларда Унга тасбиҳ айт'}, {'chapter': 76, 'verse': 27, 'text': 'إِنَّ هَـٰٓؤُلَآءِ يُحِبُّونَ ٱلۡعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمۡ يَوۡمٗا ثَقِيلٗا\nАлбатта, анавилар шошган(дунё)ни яхши кўрурлар ва оғир кунни ортларида қолдирурлар'}, {'chapter': 76, 'verse': 28, 'text': 'نَّحۡنُ خَلَقۡنَٰهُمۡ وَشَدَدۡنَآ أَسۡرَهُمۡۖ وَإِذَا شِئۡنَا بَدَّلۡنَآ أَمۡثَٰلَهُمۡ تَبۡدِيلًا\nБиз уларни яратдик ва яратилишларини маҳкам қилдик. Ва агар хоҳласак, уларнинг ўринларига ўхшашларини келтирамиз'}, {'chapter': 76, 'verse': 29, 'text': 'إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا\nАлбатта, бу (сура) эслатмадур. Бас, ким хоҳласа, ўз Роббисига йўл оладир'}, {'chapter': 76, 'verse': 30, 'text': 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nВа Аллоҳ хоҳласагина, сизлар ҳам хоҳлайсизлар. Албатта, Аллоҳ билувчи ва ҳикматли Зотдир'}, {'chapter': 76, 'verse': 31, 'text': 'يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمِينَ أَعَدَّ لَهُمۡ عَذَابًا أَلِيمَۢا\nУ кимни хоҳласа, Ўз раҳматига киритур ва золимлар учун аламли азоблар тайёрлаб қўйгандир'}], '77': [{'chapter': 77, 'verse': 1, 'text': 'وَٱلۡمُرۡسَلَٰتِ عُرۡفٗا\nКет-кет юборилганлар билан қасам'}, {'chapter': 77, 'verse': 2, 'text': 'فَٱلۡعَٰصِفَٰتِ عَصۡفٗا\nҚаттиқ эсган шамоллар билан қасам'}, {'chapter': 77, 'verse': 3, 'text': 'وَٱلنَّـٰشِرَٰتِ نَشۡرٗا\nВа (шариатни) нашр қилувчилар билан қасам'}, {'chapter': 77, 'verse': 4, 'text': 'فَٱلۡفَٰرِقَٰتِ فَرۡقٗا\nФарқловчилар билан қасам'}, {'chapter': 77, 'verse': 5, 'text': 'فَٱلۡمُلۡقِيَٰتِ ذِكۡرًا\nВаҳийни ташувчилар билан қасам'}, {'chapter': 77, 'verse': 6, 'text': 'عُذۡرًا أَوۡ نُذۡرًا\nУзрларни ёки огоҳлантиришларни ҳам'}, {'chapter': 77, 'verse': 7, 'text': 'إِنَّمَا تُوعَدُونَ لَوَٰقِعٞ\nАлбатта, сизга ваъда қилинаётган нарса воқеъ бўлгувчидир'}, {'chapter': 77, 'verse': 8, 'text': 'فَإِذَا ٱلنُّجُومُ طُمِسَتۡ\nБас, вақтики юлдузлар ўчирилса'}, {'chapter': 77, 'verse': 9, 'text': 'وَإِذَا ٱلسَّمَآءُ فُرِجَتۡ\nВа вақтики, осмон ёрилса'}, {'chapter': 77, 'verse': 10, 'text': 'وَإِذَا ٱلۡجِبَالُ نُسِفَتۡ\nВа вақтики, тоғлар кўчирилса'}, {'chapter': 77, 'verse': 11, 'text': 'وَإِذَا ٱلرُّسُلُ أُقِّتَتۡ\nВа вақтики, Пайғамбарларга вақт белгиланса'}, {'chapter': 77, 'verse': 12, 'text': 'لِأَيِّ يَوۡمٍ أُجِّلَتۡ\nҚандоқ кунга сурилган-а'}, {'chapter': 77, 'verse': 13, 'text': 'لِيَوۡمِ ٱلۡفَصۡلِ\nАжратиш кунига'}, {'chapter': 77, 'verse': 14, 'text': 'وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلۡفَصۡلِ\nАжратиш кунини сенга нима билдирди'}, {'chapter': 77, 'verse': 15, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 16, 'text': 'أَلَمۡ نُهۡلِكِ ٱلۡأَوَّلِينَ\nАввалгиларни ҳалок қилмадикми'}, {'chapter': 77, 'verse': 17, 'text': 'ثُمَّ نُتۡبِعُهُمُ ٱلۡأٓخِرِينَ\nСўнгра кейингиларини уларга эргаштирмадикми'}, {'chapter': 77, 'verse': 18, 'text': 'كَذَٰلِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِينَ\nГуноҳкорларни шундоқ қиламиз'}, {'chapter': 77, 'verse': 19, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 20, 'text': 'أَلَمۡ نَخۡلُقكُّم مِّن مَّآءٖ مَّهِينٖ\nСизларни ҳақир сувдан яратмадикми'}, {'chapter': 77, 'verse': 21, 'text': 'فَجَعَلۡنَٰهُ فِي قَرَارٖ مَّكِينٍ\nБас, уни мустаҳкам қароргоҳга жойламадикми'}, {'chapter': 77, 'verse': 22, 'text': 'إِلَىٰ قَدَرٖ مَّعۡلُومٖ\nМаълум вақтгача'}, {'chapter': 77, 'verse': 23, 'text': 'فَقَدَرۡنَا فَنِعۡمَ ٱلۡقَٰدِرُونَ\nБас, Биз қодир бўлдик ва қандоқ ҳам яхши қодир бўлувчимиз'}, {'chapter': 77, 'verse': 24, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 25, 'text': 'أَلَمۡ نَجۡعَلِ ٱلۡأَرۡضَ كِفَاتًا\nЕрни ўзига тортувчи қилиб қўймадикми'}, {'chapter': 77, 'verse': 26, 'text': 'أَحۡيَآءٗ وَأَمۡوَٰتٗا\nТирикларни ва ўликларни'}, {'chapter': 77, 'verse': 27, 'text': 'وَجَعَلۡنَا فِيهَا رَوَٰسِيَ شَٰمِخَٰتٖ وَأَسۡقَيۡنَٰكُم مَّآءٗ فُرَاتٗا\nВа Биз унда юксак тоғлар қилдик ва сизларни зилол сув билан суғордик'}, {'chapter': 77, 'verse': 28, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 29, 'text': 'ٱنطَلِقُوٓاْ إِلَىٰ مَا كُنتُم بِهِۦ تُكَذِّبُونَ\nЮрингиз! Ўзингиз ёлғонга чиқарган нарсага қараб'}, {'chapter': 77, 'verse': 30, 'text': 'ٱنطَلِقُوٓاْ إِلَىٰ ظِلّٖ ذِي ثَلَٰثِ شُعَبٖ\nЮрингиз! Уч шўъбаси бор «соя»га қараб'}, {'chapter': 77, 'verse': 31, 'text': 'لَّا ظَلِيلٖ وَلَا يُغۡنِي مِنَ ٱللَّهَبِ\nУ, салқин қилувчимас ва оловни ҳам қайтармас'}, {'chapter': 77, 'verse': 32, 'text': 'إِنَّهَا تَرۡمِي بِشَرَرٖ كَٱلۡقَصۡرِ\nАлбатта, у (жаҳаннам) қасрдек учқунларни отадир'}, {'chapter': 77, 'verse': 33, 'text': 'كَأَنَّهُۥ جِمَٰلَتٞ صُفۡرٞ\nУ (учқун) худди қорамтир-сарғиш туяларга ўхшайдир'}, {'chapter': 77, 'verse': 34, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 35, 'text': 'هَٰذَا يَوۡمُ لَا يَنطِقُونَ\nБу, улар гапира олмайдиган кундир'}, {'chapter': 77, 'verse': 36, 'text': 'وَلَا يُؤۡذَنُ لَهُمۡ فَيَعۡتَذِرُونَ\nВа уларга узр айтишга изн берилмайдир'}, {'chapter': 77, 'verse': 37, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 38, 'text': 'هَٰذَا يَوۡمُ ٱلۡفَصۡلِۖ جَمَعۡنَٰكُمۡ وَٱلۡأَوَّلِينَ\nБу ажратиш куни сизларни ва аввалгиларни жамладик'}, {'chapter': 77, 'verse': 39, 'text': 'فَإِن كَانَ لَكُمۡ كَيۡدٞ فَكِيدُونِ\nБас, сизларнинг ҳийлангиз бўлса, Менга ишлатиб кўринг-чи'}, {'chapter': 77, 'verse': 40, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 41, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي ظِلَٰلٖ وَعُيُونٖ\nАлбатта, тақводорлар соялар ва булоқлардадир'}, {'chapter': 77, 'verse': 42, 'text': 'وَفَوَٰكِهَ مِمَّا يَشۡتَهُونَ\nВа ўзлари хоҳлаган мевалардадир'}, {'chapter': 77, 'verse': 43, 'text': 'كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ\nЕнглар ва ичинглар, ош бўлсин. Қилган амалларингиз сабабидандир'}, {'chapter': 77, 'verse': 44, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nАлбатта, Биз муҳсинларни шундай мукофотлармиз'}, {'chapter': 77, 'verse': 45, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 46, 'text': 'كُلُواْ وَتَمَتَّعُواْ قَلِيلًا إِنَّكُم مُّجۡرِمُونَ\nЕнглар ва мазза қилинглар, озгинагина. Албатта, сизлар гуноҳкорсизлар'}, {'chapter': 77, 'verse': 47, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 48, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱرۡكَعُواْ لَا يَرۡكَعُونَ\nВа агар уларга рукуъ қилинглар дейилса, рукуъ қилмаслар'}, {'chapter': 77, 'verse': 49, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎшал кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 77, 'verse': 50, 'text': 'فَبِأَيِّ حَدِيثِۭ بَعۡدَهُۥ يُؤۡمِنُونَ\nБас, ундан кейин қайси сўзга ишонадилар'}], '78': [{'chapter': 78, 'verse': 1, 'text': 'عَمَّ يَتَسَآءَلُونَ\nНима ҳақида сўрашади'}, {'chapter': 78, 'verse': 2, 'text': 'عَنِ ٱلنَّبَإِ ٱلۡعَظِيمِ\nУлкан, муҳим хабар ҳақида'}, {'chapter': 78, 'verse': 3, 'text': 'ٱلَّذِي هُمۡ فِيهِ مُخۡتَلِفُونَ\nШундай хабарки, улар унинг ҳақида ихтилофдадирлар'}, {'chapter': 78, 'verse': 4, 'text': 'كَلَّا سَيَعۡلَمُونَ\nЙўқ! Улар тезда билурлар'}, {'chapter': 78, 'verse': 5, 'text': 'ثُمَّ كَلَّا سَيَعۡلَمُونَ\nЯна йўқ! Улар тезда билурлар'}, {'chapter': 78, 'verse': 6, 'text': 'أَلَمۡ نَجۡعَلِ ٱلۡأَرۡضَ مِهَٰدٗا\nЕрни бешикдек мослаб қўймадикми'}, {'chapter': 78, 'verse': 7, 'text': 'وَٱلۡجِبَالَ أَوۡتَادٗا\nВа тоғларни қозиқ (қилиб қўймадикми)'}, {'chapter': 78, 'verse': 8, 'text': 'وَخَلَقۡنَٰكُمۡ أَزۡوَٰجٗا\nВа сизларни жуфт қилиб яратдик'}, {'chapter': 78, 'verse': 9, 'text': 'وَجَعَلۡنَا نَوۡمَكُمۡ سُبَاتٗا\nВа уйқуларингизни роҳат қилиб қўйдик'}, {'chapter': 78, 'verse': 10, 'text': 'وَجَعَلۡنَا ٱلَّيۡلَ لِبَاسٗا\nВа кечасини либос қилдик'}, {'chapter': 78, 'verse': 11, 'text': 'وَجَعَلۡنَا ٱلنَّهَارَ مَعَاشٗا\nВа кундузни маош (тирикчилик) вақти қилдик'}, {'chapter': 78, 'verse': 12, 'text': 'وَبَنَيۡنَا فَوۡقَكُمۡ سَبۡعٗا شِدَادٗا\nТепангизга етти мустаҳкам(осмон)ни бино қилдик'}, {'chapter': 78, 'verse': 13, 'text': 'وَجَعَلۡنَا سِرَاجٗا وَهَّاجٗا\nВа шуъла таратувчи чироқ ҳам қилдик'}, {'chapter': 78, 'verse': 14, 'text': 'وَأَنزَلۡنَا مِنَ ٱلۡمُعۡصِرَٰتِ مَآءٗ ثَجَّاجٗا\nВа сиқилган(булут)лардан оқувчи сувлар туширдик'}, {'chapter': 78, 'verse': 15, 'text': 'لِّنُخۡرِجَ بِهِۦ حَبّٗا وَنَبَاتٗا\nУ(сув) билан дон ва наботот чиқаришимиз учун'}, {'chapter': 78, 'verse': 16, 'text': 'وَجَنَّـٰتٍ أَلۡفَافًا\nВа дарахтлари бир-бирига киришган бўстонлар'}, {'chapter': 78, 'verse': 17, 'text': 'إِنَّ يَوۡمَ ٱلۡفَصۡلِ كَانَ مِيقَٰتٗا\nАлбатта, ажрим куни вақти белгиланган эди'}, {'chapter': 78, 'verse': 18, 'text': 'يَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَتَأۡتُونَ أَفۡوَاجٗا\nУ кунда сурга пуфланур ва сизлар тўп-тўп бўлиб келурсиз'}, {'chapter': 78, 'verse': 19, 'text': 'وَفُتِحَتِ ٱلسَّمَآءُ فَكَانَتۡ أَبۡوَٰبٗا\nОсмон ёрилиб эшиклар очилур'}, {'chapter': 78, 'verse': 20, 'text': 'وَسُيِّرَتِ ٱلۡجِبَالُ فَكَانَتۡ سَرَابًا\nТоғлар ўрнидан кўчирилиб, саробга айланур'}, {'chapter': 78, 'verse': 21, 'text': 'إِنَّ جَهَنَّمَ كَانَتۡ مِرۡصَادٗا\nАлбатта, жаҳаннам пойлаб турувчидир'}, {'chapter': 78, 'verse': 22, 'text': 'لِّلطَّـٰغِينَ مَـَٔابٗا\nТуғёнга кетганларнинг қайтар жойидир'}, {'chapter': 78, 'verse': 23, 'text': 'لَّـٰبِثِينَ فِيهَآ أَحۡقَابٗا\nУлар (унда) замонлар бўйи қоларлар'}, {'chapter': 78, 'verse': 24, 'text': 'لَّا يَذُوقُونَ فِيهَا بَرۡدٗا وَلَا شَرَابًا\nУлар (унда) совуқни ҳам, шаробни ҳам татий олмаслар'}, {'chapter': 78, 'verse': 25, 'text': 'إِلَّا حَمِيمٗا وَغَسَّاقٗا\nМагар қайноқ сув ва йирингни (татирлар)'}, {'chapter': 78, 'verse': 26, 'text': 'جَزَآءٗ وِفَاقًا\nМувофиқ жазо ўлароқ'}, {'chapter': 78, 'verse': 27, 'text': 'إِنَّهُمۡ كَانُواْ لَا يَرۡجُونَ حِسَابٗا\nЧунки, улар ҳисоб-китоб бўлишига ишонмасдилар'}, {'chapter': 78, 'verse': 28, 'text': 'وَكَذَّبُواْ بِـَٔايَٰتِنَا كِذَّابٗا\nВа Бизнинг оятларимизни ёлғон дейишган эди, ёлғон'}, {'chapter': 78, 'verse': 29, 'text': 'وَكُلَّ شَيۡءٍ أَحۡصَيۡنَٰهُ كِتَٰبٗا\nБиз ҳар бир нарсани китобга жамлаб қўйганмиз'}, {'chapter': 78, 'verse': 30, 'text': 'فَذُوقُواْ فَلَن نَّزِيدَكُمۡ إِلَّا عَذَابًا\nБас, татиб кўринглар, сизларга азобдан бошқа ҳеч нарсани зиёда қилмасмиз'}, {'chapter': 78, 'verse': 31, 'text': 'إِنَّ لِلۡمُتَّقِينَ مَفَازًا\nАлбатта, тақводорларга ютуқ бордир'}, {'chapter': 78, 'verse': 32, 'text': 'حَدَآئِقَ وَأَعۡنَٰبٗا\nБоғ-роғлар, узумзорлар бордир'}, {'chapter': 78, 'verse': 33, 'text': 'وَكَوَاعِبَ أَتۡرَابٗا\nВа янги бўйи етган бир хил ёшдаги қизлар бордир'}, {'chapter': 78, 'verse': 34, 'text': 'وَكَأۡسٗا دِهَاقٗا\nВа тўлдирилган қадаҳлар бордир'}, {'chapter': 78, 'verse': 35, 'text': 'لَّا يَسۡمَعُونَ فِيهَا لَغۡوٗا وَلَا كِذَّـٰبٗا\nУ ерда бекорчи ва ёлғон гапларни эшитмаслар'}, {'chapter': 78, 'verse': 36, 'text': 'جَزَآءٗ مِّن رَّبِّكَ عَطَآءً حِسَابٗا\nРоббингдан мукофот, совға ҳисобидандир'}, {'chapter': 78, 'verse': 37, 'text': 'رَّبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلرَّحۡمَٰنِۖ لَا يَمۡلِكُونَ مِنۡهُ خِطَابٗا\nУ, осмонлар, ер ва уларнинг орасидаги барча нарсаларнинг Робби, Роҳмандир. Унинг ҳузурида ҳеч ким хитоб қилишга қодир бўлмас'}, {'chapter': 78, 'verse': 38, 'text': 'يَوۡمَ يَقُومُ ٱلرُّوحُ وَٱلۡمَلَـٰٓئِكَةُ صَفّٗاۖ لَّا يَتَكَلَّمُونَ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَٰنُ وَقَالَ صَوَابٗا\nЎша кунда Руҳ ва фаришталар саф бўлиб турар. Роҳман изн берганлар ва тўғри сўзлаганлардан ўзгалар ҳеч гапира олмаслар'}, {'chapter': 78, 'verse': 39, 'text': 'ذَٰلِكَ ٱلۡيَوۡمُ ٱلۡحَقُّۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ مَـَٔابًا\nУ ҳақ кундир. Ким хоҳласа, ўз Роббига қайтар йўл оладир'}, {'chapter': 78, 'verse': 40, 'text': 'إِنَّآ أَنذَرۡنَٰكُمۡ عَذَابٗا قَرِيبٗا يَوۡمَ يَنظُرُ ٱلۡمَرۡءُ مَا قَدَّمَتۡ يَدَاهُ وَيَقُولُ ٱلۡكَافِرُ يَٰلَيۡتَنِي كُنتُ تُرَٰبَۢا\nАлбатта, Биз сизларни яқин келажакдаги азобдан қўрқитдик. У кунда, ҳар киши қўли тақдим қилган нарсага назар соладир. Ва кофир. «Кошки тупроқ бўлиб кетсам», дейдир'}], '79': [{'chapter': 79, 'verse': 1, 'text': 'وَٱلنَّـٰزِعَٰتِ غَرۡقٗا\nШиддат билан (жонни) суғуриб олувчи(фаришта)лар билан қасам'}, {'chapter': 79, 'verse': 2, 'text': 'وَٱلنَّـٰشِطَٰتِ نَشۡطٗا\nЮмшоқлик билан жон олувчи(фаришта)лар билан қасам'}, {'chapter': 79, 'verse': 3, 'text': 'وَٱلسَّـٰبِحَٰتِ سَبۡحٗا\nСузиб юрувчи(фаришта)лар билан қасам'}, {'chapter': 79, 'verse': 4, 'text': 'فَٱلسَّـٰبِقَٰتِ سَبۡقٗا\nЎзишда мусобақа қилувчи(фаришта)лар билан қасам'}, {'chapter': 79, 'verse': 5, 'text': 'فَٱلۡمُدَبِّرَٰتِ أَمۡرٗا\nАмрнинг тадбирини қилувчи(фаришта)лар билан қасам'}, {'chapter': 79, 'verse': 6, 'text': 'يَوۡمَ تَرۡجُفُ ٱلرَّاجِفَةُ\nИзтиробга тушувчи изтиробга тушган кунда'}, {'chapter': 79, 'verse': 7, 'text': 'تَتۡبَعُهَا ٱلرَّادِفَةُ\nУнга эргашувчи эргашганда'}, {'chapter': 79, 'verse': 8, 'text': 'قُلُوبٞ يَوۡمَئِذٖ وَاجِفَةٌ\nЎша кунда қалблар хавфга тўладир'}, {'chapter': 79, 'verse': 9, 'text': 'أَبۡصَٰرُهَا خَٰشِعَةٞ\nУларнинг кўзлари қўрқинчга тўладир'}, {'chapter': 79, 'verse': 10, 'text': 'يَقُولُونَ أَءِنَّا لَمَرۡدُودُونَ فِي ٱلۡحَافِرَةِ\nУлар: «Биз, албатта, ортга қайтариламизми?» – дерлар'}, {'chapter': 79, 'verse': 11, 'text': 'أَءِذَا كُنَّا عِظَٰمٗا نَّخِرَةٗ\n«Чириган суяк бўлганимиздан кейин ҳам-а?»'}, {'chapter': 79, 'verse': 12, 'text': 'قَالُواْ تِلۡكَ إِذٗا كَرَّةٌ خَاسِرَةٞ\nАгар шундоқ бўлса, бу ҳасратий қайтиш-ку!» – дерлар'}, {'chapter': 79, 'verse': 13, 'text': 'فَإِنَّمَا هِيَ زَجۡرَةٞ وَٰحِدَةٞ\nАлбатта, у(қиёмат) бир қичқириқдан бошқа нарса эмас'}, {'chapter': 79, 'verse': 14, 'text': 'فَإِذَا هُم بِٱلسَّاهِرَةِ\nКўрибсизки, улар (қиёмат) майдонида турибдир'}, {'chapter': 79, 'verse': 15, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ\nСенга Мусонинг хабари келдими'}, {'chapter': 79, 'verse': 16, 'text': 'إِذۡ نَادَىٰهُ رَبُّهُۥ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوًى\nҚачонки Роббиси унга Муқаддас Тува водийсида нидо қилди'}, {'chapter': 79, 'verse': 17, 'text': 'ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\n«Фиръавн ҳузурига бор, албатта, у туғёнга кетди'}, {'chapter': 79, 'verse': 18, 'text': 'فَقُلۡ هَل لَّكَ إِلَىٰٓ أَن تَزَكَّىٰ\nБас унга айт: «Сенда покланишга (рағбат) борми'}, {'chapter': 79, 'verse': 19, 'text': 'وَأَهۡدِيَكَ إِلَىٰ رَبِّكَ فَتَخۡشَىٰ\nВа сени Роббингга ҳидоят қилсам, шоядки (ундан) қўрқсанг», дегин»'}, {'chapter': 79, 'verse': 20, 'text': 'فَأَرَىٰهُ ٱلۡأٓيَةَ ٱلۡكُبۡرَىٰ\nВа унга улкан аломатни кўрсатди'}, {'chapter': 79, 'verse': 21, 'text': 'فَكَذَّبَ وَعَصَىٰ\nУл уни ёлғонга чиқарди ва осий бўлди'}, {'chapter': 79, 'verse': 22, 'text': 'ثُمَّ أَدۡبَرَ يَسۡعَىٰ\nСўнг ортига ўгирилиб, тезлаб кетди'}, {'chapter': 79, 'verse': 23, 'text': 'فَحَشَرَ فَنَادَىٰ\nКейин (одамларни) тўплади ва нидо қилди'}, {'chapter': 79, 'verse': 24, 'text': 'فَقَالَ أَنَا۠ رَبُّكُمُ ٱلۡأَعۡلَىٰ\nВа, мен сизларнинг энг олий Роббингизман, деди'}, {'chapter': 79, 'verse': 25, 'text': 'فَأَخَذَهُ ٱللَّهُ نَكَالَ ٱلۡأٓخِرَةِ وَٱلۡأُولَىٰٓ\nБас, Аллоҳ уни аввалги ва охирги гуноҳлари учун азобга олди'}, {'chapter': 79, 'verse': 26, 'text': 'إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّمَن يَخۡشَىٰٓ\nАлбатта, бунда У зотдан қўрққанлар учун ибрат бордир'}, {'chapter': 79, 'verse': 27, 'text': 'ءَأَنتُمۡ أَشَدُّ خَلۡقًا أَمِ ٱلسَّمَآءُۚ بَنَىٰهَا\nСизларни яратиш қийинми ёки У зот бино қилган осмонними'}, {'chapter': 79, 'verse': 28, 'text': 'رَفَعَ سَمۡكَهَا فَسَوَّىٰهَا\nУнинг шифтини юқори кўтарди ва беками-кўст, мустаҳкам қилиб қўйди'}, {'chapter': 79, 'verse': 29, 'text': 'وَأَغۡطَشَ لَيۡلَهَا وَأَخۡرَجَ ضُحَىٰهَا\nКечасини қоронғу ва кундузини ёруғ қилди'}, {'chapter': 79, 'verse': 30, 'text': 'وَٱلۡأَرۡضَ بَعۡدَ ذَٰلِكَ دَحَىٰهَآ\nВа ундан сўнг ерни тухум шаклида қилди'}, {'chapter': 79, 'verse': 31, 'text': 'أَخۡرَجَ مِنۡهَا مَآءَهَا وَمَرۡعَىٰهَا\nУндан сувни, ўт-ўланларни чиқарди'}, {'chapter': 79, 'verse': 32, 'text': 'وَٱلۡجِبَالَ أَرۡسَىٰهَا\nВа тоғларни собит қилди'}, {'chapter': 79, 'verse': 33, 'text': 'مَتَٰعٗا لَّكُمۡ وَلِأَنۡعَٰمِكُمۡ\nСизларга ва чорваларингизга манфаат бўлсин учун'}, {'chapter': 79, 'verse': 34, 'text': 'فَإِذَا جَآءَتِ ٱلطَّآمَّةُ ٱلۡكُبۡرَىٰ\nВақтики катта ғолиб (қиёмат) келса'}, {'chapter': 79, 'verse': 35, 'text': 'يَوۡمَ يَتَذَكَّرُ ٱلۡإِنسَٰنُ مَا سَعَىٰ\nУ куни инсон қилган ишларини эслайдир'}, {'chapter': 79, 'verse': 36, 'text': 'وَبُرِّزَتِ ٱلۡجَحِيمُ لِمَن يَرَىٰ\nВа жаҳаннам кўрадиганларга зоҳир қилинадир'}, {'chapter': 79, 'verse': 37, 'text': 'فَأَمَّا مَن طَغَىٰ\nАммо кимики туғёнга кетган бўлса'}, {'chapter': 79, 'verse': 38, 'text': 'وَءَاثَرَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا\nВа бу дунё ҳаётини устун қўйган бўлса'}, {'chapter': 79, 'verse': 39, 'text': 'فَإِنَّ ٱلۡجَحِيمَ هِيَ ٱلۡمَأۡوَىٰ\nБас, албатта, Жаҳийм (унга) ўрин бўладир'}, {'chapter': 79, 'verse': 40, 'text': 'وَأَمَّا مَنۡ خَافَ مَقَامَ رَبِّهِۦ وَنَهَى ٱلنَّفۡسَ عَنِ ٱلۡهَوَىٰ\nВа аммо, ким Роббисининг мақомидан қўрққан ва ўз нафсини ҳаволаниб кетишидан қайтарган бўлса'}, {'chapter': 79, 'verse': 41, 'text': 'فَإِنَّ ٱلۡجَنَّةَ هِيَ ٱلۡمَأۡوَىٰ\nБас, албатта, жаннат (унга) ўрин бўладир'}, {'chapter': 79, 'verse': 42, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرۡسَىٰهَا\nСендан, қиёмат қачон бўлур, деб сўрашадир'}, {'chapter': 79, 'verse': 43, 'text': 'فِيمَ أَنتَ مِن ذِكۡرَىٰهَآ\nУ(қиёмат)нинг зикрини сен қандоқ билардинг'}, {'chapter': 79, 'verse': 44, 'text': 'إِلَىٰ رَبِّكَ مُنتَهَىٰهَآ\nУнинг интиҳоси Роббинггагина хос'}, {'chapter': 79, 'verse': 45, 'text': 'إِنَّمَآ أَنتَ مُنذِرُ مَن يَخۡشَىٰهَا\nАлбатта, сен у(қиёмат)дан қўрқувчиларга огоҳлантирувчисан, холос'}, {'chapter': 79, 'verse': 46, 'text': 'كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَهَا لَمۡ يَلۡبَثُوٓاْ إِلَّا عَشِيَّةً أَوۡ ضُحَىٰهَا\nУ(қиёмат)ни кўрган кунлари, худди, бу дунёда бир кеча ёки кундуздан бошқа турмаганга ўхшарлар'}], '80': [{'chapter': 80, 'verse': 1, 'text': 'عَبَسَ وَتَوَلَّىٰٓ\nАфтини буриштирди ва юз ўгирди'}, {'chapter': 80, 'verse': 2, 'text': 'أَن جَآءَهُ ٱلۡأَعۡمَىٰ\nКўзи кўр киши ҳузурига келганда'}, {'chapter': 80, 'verse': 3, 'text': 'وَمَا يُدۡرِيكَ لَعَلَّهُۥ يَزَّكَّىٰٓ\nҚаердан биласан, эҳтимол, у покланар'}, {'chapter': 80, 'verse': 4, 'text': 'أَوۡ يَذَّكَّرُ فَتَنفَعَهُ ٱلذِّكۡرَىٰٓ\nЁки насиҳат олар ва мавъиза унга манфаат берар'}, {'chapter': 80, 'verse': 5, 'text': 'أَمَّا مَنِ ٱسۡتَغۡنَىٰ\nАммо истиғно қилганга бўлса'}, {'chapter': 80, 'verse': 6, 'text': 'فَأَنتَ لَهُۥ تَصَدَّىٰ\nСен у томонга интиласан'}, {'chapter': 80, 'verse': 7, 'text': 'وَمَا عَلَيۡكَ أَلَّا يَزَّكَّىٰ\nУ покланмаса, сени ҳеч ким маломат қилмас'}, {'chapter': 80, 'verse': 8, 'text': 'وَأَمَّا مَن جَآءَكَ يَسۡعَىٰ\nАммо ҳузурингга шошилиб келган'}, {'chapter': 80, 'verse': 9, 'text': 'وَهُوَ يَخۡشَىٰ\nВа У зотдан қўрққан ҳолда бўлгандан эса'}, {'chapter': 80, 'verse': 10, 'text': 'فَأَنتَ عَنۡهُ تَلَهَّىٰ\nБас, Сен ундан машғул бўласан'}, {'chapter': 80, 'verse': 11, 'text': 'كَلَّآ إِنَّهَا تَذۡكِرَةٞ\nЙўқ! Албатта улар (Қуръон оятлари) эслатмадир'}, {'chapter': 80, 'verse': 12, 'text': 'فَمَن شَآءَ ذَكَرَهُۥ\nКим хоҳласа, уни эслайдир'}, {'chapter': 80, 'verse': 13, 'text': 'فِي صُحُفٖ مُّكَرَّمَةٖ\nҲурматланган саҳифалардадир'}, {'chapter': 80, 'verse': 14, 'text': 'مَّرۡفُوعَةٖ مُّطَهَّرَةِۭ\nОлий қадардирлар, поклангандирлар'}, {'chapter': 80, 'verse': 15, 'text': 'بِأَيۡدِي سَفَرَةٖ\nЭлчи(фаришта)лар қўлларидадир'}, {'chapter': 80, 'verse': 16, 'text': 'كِرَامِۭ بَرَرَةٖ\nУлар ҳурматланган ва ўта яхшидирлар'}, {'chapter': 80, 'verse': 17, 'text': 'قُتِلَ ٱلۡإِنسَٰنُ مَآ أَكۡفَرَهُۥ\nИнсонга лаънат бўлсин, унинг кофирлиги қандоқ шиддатли бўлди'}, {'chapter': 80, 'verse': 18, 'text': 'مِنۡ أَيِّ شَيۡءٍ خَلَقَهُۥ\nУни қайси нарсадан яратди'}, {'chapter': 80, 'verse': 19, 'text': 'مِن نُّطۡفَةٍ خَلَقَهُۥ فَقَدَّرَهُۥ\nНутфадан уни яратди ва уни ўлчовли қилди'}, {'chapter': 80, 'verse': 20, 'text': 'ثُمَّ ٱلسَّبِيلَ يَسَّرَهُۥ\nСўнгра, чиқиш йўлини осон қилди'}, {'chapter': 80, 'verse': 21, 'text': 'ثُمَّ أَمَاتَهُۥ فَأَقۡبَرَهُۥ\nСўнгра ўлдирди ва қабрга киргазди'}, {'chapter': 80, 'verse': 22, 'text': 'ثُمَّ إِذَا شَآءَ أَنشَرَهُۥ\nСўнгра (қачон) хоҳласа, уни қайта тирилтиради'}, {'chapter': 80, 'verse': 23, 'text': 'كَلَّا لَمَّا يَقۡضِ مَآ أَمَرَهُۥ\nЙўқ, ундоқ эмас! У ўзига У зот қилган амрни бажармади'}, {'chapter': 80, 'verse': 24, 'text': 'فَلۡيَنظُرِ ٱلۡإِنسَٰنُ إِلَىٰ طَعَامِهِۦٓ\nИнсон ўз таомига назар солсин'}, {'chapter': 80, 'verse': 25, 'text': 'أَنَّا صَبَبۡنَا ٱلۡمَآءَ صَبّٗا\nАлбатта, Биз сувни роса қуйиб қўйибмиз'}, {'chapter': 80, 'verse': 26, 'text': 'ثُمَّ شَقَقۡنَا ٱلۡأَرۡضَ شَقّٗا\nСўнгра ерни ўзига хос ёрдик'}, {'chapter': 80, 'verse': 27, 'text': 'فَأَنۢبَتۡنَا فِيهَا حَبّٗا\nБиз унда донни'}, {'chapter': 80, 'verse': 28, 'text': 'وَعِنَبٗا وَقَضۡبٗا\nВа узум ва кўкларни'}, {'chapter': 80, 'verse': 29, 'text': 'وَزَيۡتُونٗا وَنَخۡلٗا\nВа зайтун ва хурмоларни'}, {'chapter': 80, 'verse': 30, 'text': 'وَحَدَآئِقَ غُلۡبٗا\nВа тиғиз боғларни'}, {'chapter': 80, 'verse': 31, 'text': 'وَفَٰكِهَةٗ وَأَبّٗا\nВа мевалару ўт-ўланларни ўстириб қўйдик'}, {'chapter': 80, 'verse': 32, 'text': 'مَّتَٰعٗا لَّكُمۡ وَلِأَنۡعَٰمِكُمۡ\nСизларга ва чорваларингизга манфаат ва лаззат бўлсин, деб'}, {'chapter': 80, 'verse': 33, 'text': 'فَإِذَا جَآءَتِ ٱلصَّآخَّةُ\nВақтики кар қилувчи овоз келса'}, {'chapter': 80, 'verse': 34, 'text': 'يَوۡمَ يَفِرُّ ٱلۡمَرۡءُ مِنۡ أَخِيهِ\nУ кунда киши ўз ака-укасидан'}, {'chapter': 80, 'verse': 35, 'text': 'وَأُمِّهِۦ وَأَبِيهِ\nВа онаси ва отасидан'}, {'chapter': 80, 'verse': 36, 'text': 'وَصَٰحِبَتِهِۦ وَبَنِيهِ\nВа хотини ва бола-чақасидан қочади'}, {'chapter': 80, 'verse': 37, 'text': 'لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُمۡ يَوۡمَئِذٖ شَأۡنٞ يُغۡنِيهِ\nУ кунда улардан ҳар бир шахсни овора қилувчи ўз иши бор'}, {'chapter': 80, 'verse': 38, 'text': 'وُجُوهٞ يَوۡمَئِذٖ مُّسۡفِرَةٞ\nУ кунда порлоқ чеҳралар бор'}, {'chapter': 80, 'verse': 39, 'text': 'ضَاحِكَةٞ مُّسۡتَبۡشِرَةٞ\nУлар кулгу билан тўла, етган хушхабардан хурсанд'}, {'chapter': 80, 'verse': 40, 'text': 'وَوُجُوهٞ يَوۡمَئِذٍ عَلَيۡهَا غَبَرَةٞ\nУ кунда бир чеҳралар борки, уларни ғубор босган'}, {'chapter': 80, 'verse': 41, 'text': 'تَرۡهَقُهَا قَتَرَةٌ\nУстини зулмат қоплаган'}, {'chapter': 80, 'verse': 42, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡكَفَرَةُ ٱلۡفَجَرَةُ\nУлар, ана ўшалар, кофирлар ва фожирлардир'}], '81': [{'chapter': 81, 'verse': 1, 'text': 'إِذَا ٱلشَّمۡسُ كُوِّرَتۡ\nВақтики, қуёшни буклаб, ўралса'}, {'chapter': 81, 'verse': 2, 'text': 'وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتۡ\nВа вақтики юлдузлар узилиб тушиб, сочилиб кетса'}, {'chapter': 81, 'verse': 3, 'text': 'وَإِذَا ٱلۡجِبَالُ سُيِّرَتۡ\nВа вақтики, тоғлар жойидан кўчирилса'}, {'chapter': 81, 'verse': 4, 'text': 'وَإِذَا ٱلۡعِشَارُ عُطِّلَتۡ\nВа вақтики, ҳомиласига ўн ой тўлган туялар қаровсиз қолса'}, {'chapter': 81, 'verse': 5, 'text': 'وَإِذَا ٱلۡوُحُوشُ حُشِرَتۡ\nВа вақтики, ваҳший ҳайвонлар бир жойга тўпланса'}, {'chapter': 81, 'verse': 6, 'text': 'وَإِذَا ٱلۡبِحَارُ سُجِّرَتۡ\nВа вақтики, денгизлар аланга олиб ёнса'}, {'chapter': 81, 'verse': 7, 'text': 'وَإِذَا ٱلنُّفُوسُ زُوِّجَتۡ\nВа вақтики, нафслар жуфтлаштирилса'}, {'chapter': 81, 'verse': 8, 'text': 'وَإِذَا ٱلۡمَوۡءُۥدَةُ سُئِلَتۡ\nВа вақтики, тириклай кўмилган қиздан сўралса'}, {'chapter': 81, 'verse': 9, 'text': 'بِأَيِّ ذَنۢبٖ قُتِلَتۡ\nУ нима гуноҳ учун ўлдирилган'}, {'chapter': 81, 'verse': 10, 'text': 'وَإِذَا ٱلصُّحُفُ نُشِرَتۡ\nВа вақтики, саҳифалар нашр қилинса'}, {'chapter': 81, 'verse': 11, 'text': 'وَإِذَا ٱلسَّمَآءُ كُشِطَتۡ\nВа вақтики, осмон қўпорилса'}, {'chapter': 81, 'verse': 12, 'text': 'وَإِذَا ٱلۡجَحِيمُ سُعِّرَتۡ\nВа вақтики, жаҳаннам қиздирилса'}, {'chapter': 81, 'verse': 13, 'text': 'وَإِذَا ٱلۡجَنَّةُ أُزۡلِفَتۡ\nВа вақтики, жаннат яқинлаштирилса'}, {'chapter': 81, 'verse': 14, 'text': 'عَلِمَتۡ نَفۡسٞ مَّآ أَحۡضَرَتۡ\nҲар бир жон нима ҳозирлаганини биладир'}, {'chapter': 81, 'verse': 15, 'text': 'فَلَآ أُقۡسِمُ بِٱلۡخُنَّسِ\n(Кундузи) махфий бўлувчи(юлдуз)лар билан қасам'}, {'chapter': 81, 'verse': 16, 'text': 'ٱلۡجَوَارِ ٱلۡكُنَّسِ\n(Кечалари) юрувчи, беркинувчи(юлдуз)лар билан қасам'}, {'chapter': 81, 'verse': 17, 'text': 'وَٱلَّيۡلِ إِذَا عَسۡعَسَ\nҚоронғулиги ила кириб келган кеча билан қасам'}, {'chapter': 81, 'verse': 18, 'text': 'وَٱلصُّبۡحِ إِذَا تَنَفَّسَ\nНафас олиб кириб келган тонг билан қасам'}, {'chapter': 81, 'verse': 19, 'text': 'إِنَّهُۥ لَقَوۡلُ رَسُولٖ كَرِيمٖ\nАлбатта у(Қуръон) карамли элчининг сўзидир'}, {'chapter': 81, 'verse': 20, 'text': 'ذِي قُوَّةٍ عِندَ ذِي ٱلۡعَرۡشِ مَكِينٖ\nУ қувватли, арш Эгаси ҳузурида мартабаси олийдир'}, {'chapter': 81, 'verse': 21, 'text': 'مُّطَاعٖ ثَمَّ أَمِينٖ\nИтоат қилинган, омонатлидир'}, {'chapter': 81, 'verse': 22, 'text': 'وَمَا صَاحِبُكُم بِمَجۡنُونٖ\nСизнинг соҳибингиз мажнун эмасдир'}, {'chapter': 81, 'verse': 23, 'text': 'وَلَقَدۡ رَءَاهُ بِٱلۡأُفُقِ ٱلۡمُبِينِ\nБатаҳқиқ, ул ани очиқ-ойдин уфқда кўрди'}, {'chapter': 81, 'verse': 24, 'text': 'وَمَا هُوَ عَلَى ٱلۡغَيۡبِ بِضَنِينٖ\nВа у ғайбга бахил эмасдир'}, {'chapter': 81, 'verse': 25, 'text': 'وَمَا هُوَ بِقَوۡلِ شَيۡطَٰنٖ رَّجِيمٖ\nВа у(Қуръон) тошбўрон қилинган шайтоннинг сўзи эмасдир'}, {'chapter': 81, 'verse': 26, 'text': 'فَأَيۡنَ تَذۡهَبُونَ\nБас, қаёққа кетмоқдасиз'}, {'chapter': 81, 'verse': 27, 'text': 'إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nУ (Қуръон) оламларга эслатмадан бошқа нарса эмасдир'}, {'chapter': 81, 'verse': 28, 'text': 'لِمَن شَآءَ مِنكُمۡ أَن يَسۡتَقِيمَ\nСизлардан истиқомат(тўғри йўл)да бўлишни хоҳловчилар учундир'}, {'chapter': 81, 'verse': 29, 'text': 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nОламларнинг Робби Аллоҳ хоҳламаса, сизлар хоҳлай олмассизлар'}], '82': [{'chapter': 82, 'verse': 1, 'text': 'إِذَا ٱلسَّمَآءُ ٱنفَطَرَتۡ\nВақтики, осмон ёрилса'}, {'chapter': 82, 'verse': 2, 'text': 'وَإِذَا ٱلۡكَوَاكِبُ ٱنتَثَرَتۡ\nВа вақтики, юлдузлар сочилиб кетса'}, {'chapter': 82, 'verse': 3, 'text': 'وَإِذَا ٱلۡبِحَارُ فُجِّرَتۡ\nВа вақтики, денгизлар портлатилса'}, {'chapter': 82, 'verse': 4, 'text': 'وَإِذَا ٱلۡقُبُورُ بُعۡثِرَتۡ\nВа вақтики, қабрлар остин-устин бўлса'}, {'chapter': 82, 'verse': 5, 'text': 'عَلِمَتۡ نَفۡسٞ مَّا قَدَّمَتۡ وَأَخَّرَتۡ\nҲар бир жон — нимани тақдим қилдию нимани таъхир қилди — биладир'}, {'chapter': 82, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلۡإِنسَٰنُ مَا غَرَّكَ بِرَبِّكَ ٱلۡكَرِيمِ\nЭй Инсон! Сени нима Карамли Роббинг ҳақида ғурурга кетказди'}, {'chapter': 82, 'verse': 7, 'text': 'ٱلَّذِي خَلَقَكَ فَسَوَّىٰكَ فَعَدَلَكَ\nУ сени тўла-тўкис ва мўътадил қилиб яратганди'}, {'chapter': 82, 'verse': 8, 'text': 'فِيٓ أَيِّ صُورَةٖ مَّا شَآءَ رَكَّبَكَ\nЎзи хоҳлаган сувратда сени таркиб қилди'}, {'chapter': 82, 'verse': 9, 'text': 'كَلَّا بَلۡ تُكَذِّبُونَ بِٱلدِّينِ\nЙўқ!!! Балки, сиз жазони ёлғонга чиқармоқдасиз'}, {'chapter': 82, 'verse': 10, 'text': 'وَإِنَّ عَلَيۡكُمۡ لَحَٰفِظِينَ\nҲолбуки, сизнинг устингиздан кузатиб турувчилар бордир'}, {'chapter': 82, 'verse': 11, 'text': 'كِرَامٗا كَٰتِبِينَ\nУлар ҳурматли ёзиб тургувчилардир'}, {'chapter': 82, 'verse': 12, 'text': 'يَعۡلَمُونَ مَا تَفۡعَلُونَ\nУлар нима қилаётганингизни биладир'}, {'chapter': 82, 'verse': 13, 'text': 'إِنَّ ٱلۡأَبۡرَارَ لَفِي نَعِيمٖ\nАлбатта, яхшилар жаннатдадир'}, {'chapter': 82, 'verse': 14, 'text': 'وَإِنَّ ٱلۡفُجَّارَ لَفِي جَحِيمٖ\nВа, албатта, фожирлар жаҳаннамдадир'}, {'chapter': 82, 'verse': 15, 'text': 'يَصۡلَوۡنَهَا يَوۡمَ ٱلدِّينِ\nУлар у ерга қиёмат куни кирадир'}, {'chapter': 82, 'verse': 16, 'text': 'وَمَا هُمۡ عَنۡهَا بِغَآئِبِينَ\nВа улар ундан ғойиб бўла олмаслар'}, {'chapter': 82, 'verse': 17, 'text': 'وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلدِّينِ\nВа, жазо куни нима эканини сенга нима билдирди'}, {'chapter': 82, 'verse': 18, 'text': 'ثُمَّ مَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلدِّينِ\nСўнг, жазо куни нима эканини сенга нима билдирди'}, {'chapter': 82, 'verse': 19, 'text': 'يَوۡمَ لَا تَمۡلِكُ نَفۡسٞ لِّنَفۡسٖ شَيۡـٔٗاۖ وَٱلۡأَمۡرُ يَوۡمَئِذٖ لِّلَّهِ\nЎша кунда ҳеч бир жон бошқа бир жон учун бирор нарса қилиш имконига молик эмас. У кунда барча иш Аллоҳнинг Ўзига хос'}], '83': [{'chapter': 83, 'verse': 1, 'text': 'وَيۡلٞ لِّلۡمُطَفِّفِينَ\nВайл бўлсин ўлчовдан уриб қолувчиларга'}, {'chapter': 83, 'verse': 2, 'text': 'ٱلَّذِينَ إِذَا ٱكۡتَالُواْ عَلَى ٱلنَّاسِ يَسۡتَوۡفُونَ\nУлар одамлардан нарса ўлчаб олсалар, тўлиқ оларлар'}, {'chapter': 83, 'verse': 3, 'text': 'وَإِذَا كَالُوهُمۡ أَو وَّزَنُوهُمۡ يُخۡسِرُونَ\nВа агар одамларга ўлчаб ёки тортиб берсалар, камайтирарлар'}, {'chapter': 83, 'verse': 4, 'text': 'أَلَا يَظُنُّ أُوْلَـٰٓئِكَ أَنَّهُم مَّبۡعُوثُونَ\nАна шулар, албатта, қайта тирилтирилишларини ўйламайдиларми'}, {'chapter': 83, 'verse': 5, 'text': 'لِيَوۡمٍ عَظِيمٖ\nУлуғ бир кунда'}, {'chapter': 83, 'verse': 6, 'text': 'يَوۡمَ يَقُومُ ٱلنَّاسُ لِرَبِّ ٱلۡعَٰلَمِينَ\nУ кунда одамлар оламларнинг Робби ҳузурида тик турарлар'}, {'chapter': 83, 'verse': 7, 'text': 'كَلَّآ إِنَّ كِتَٰبَ ٱلۡفُجَّارِ لَفِي سِجِّينٖ\nЙўқ! Албатта фожирларларнинг китоби «сижжийн»дадир'}, {'chapter': 83, 'verse': 8, 'text': 'وَمَآ أَدۡرَىٰكَ مَا سِجِّينٞ\n«Сижжийн» қандай нарса эканини сенга нима билдирди'}, {'chapter': 83, 'verse': 9, 'text': 'كِتَٰبٞ مَّرۡقُومٞ\nУ(Сижжийн) ёзилган китобдур'}, {'chapter': 83, 'verse': 10, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nЎша кунда ёлғонга чиқарувчиларга вайл бўлсин'}, {'chapter': 83, 'verse': 11, 'text': 'ٱلَّذِينَ يُكَذِّبُونَ بِيَوۡمِ ٱلدِّينِ\nУлар жазо (қиёмат) кунини ёлғонга чиқарурлар'}, {'chapter': 83, 'verse': 12, 'text': 'وَمَا يُكَذِّبُ بِهِۦٓ إِلَّا كُلُّ مُعۡتَدٍ أَثِيمٍ\nУни ҳаддан ошувчи ва ўта гуноҳкорлардан бошқалар ёлғонга чиқармаслар'}, {'chapter': 83, 'verse': 13, 'text': 'إِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nҚачон унга оятларимиз тиловат қилинса, «аввалгиларнинг афсоналари», дер'}, {'chapter': 83, 'verse': 14, 'text': 'كَلَّاۖ بَلۡۜ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُواْ يَكۡسِبُونَ\nЙўқ! Уларнинг қилган касблари қалбларига моғор бўлиб ўрнашиб қолгандир'}, {'chapter': 83, 'verse': 15, 'text': 'كَلَّآ إِنَّهُمۡ عَن رَّبِّهِمۡ يَوۡمَئِذٖ لَّمَحۡجُوبُونَ\nЙўқ!!! Албатта, улар ўша кунда ўз Роббиларини кўришдан тўсилурлар'}, {'chapter': 83, 'verse': 16, 'text': 'ثُمَّ إِنَّهُمۡ لَصَالُواْ ٱلۡجَحِيمِ\nСўнгра, албатта, улар жаҳиймга кирувчилардир'}, {'chapter': 83, 'verse': 17, 'text': 'ثُمَّ يُقَالُ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ\nСўнгра, мана бу, сизлар ёлғонга чиқариб юрган нарса, дейилур'}, {'chapter': 83, 'verse': 18, 'text': 'كَلَّآ إِنَّ كِتَٰبَ ٱلۡأَبۡرَارِ لَفِي عِلِّيِّينَ\nЙўқ!!! Албатта, аброрларнинг китоби «Иллиййийн»дадур'}, {'chapter': 83, 'verse': 19, 'text': 'وَمَآ أَدۡرَىٰكَ مَا عِلِّيُّونَ\n«Иллиййийн» қандоқ нарса эканини сенга нима билдирди'}, {'chapter': 83, 'verse': 20, 'text': 'كِتَٰبٞ مَّرۡقُومٞ\nУ ёзилган китобдур'}, {'chapter': 83, 'verse': 21, 'text': 'يَشۡهَدُهُ ٱلۡمُقَرَّبُونَ\nУнга муқарраб(фаришта)лар шоҳид бўлурлар'}, {'chapter': 83, 'verse': 22, 'text': 'إِنَّ ٱلۡأَبۡرَارَ لَفِي نَعِيمٍ\nАлбатта, аброрлар жаннати наъиймдадурлар'}, {'chapter': 83, 'verse': 23, 'text': 'عَلَى ٱلۡأَرَآئِكِ يَنظُرُونَ\nПардали баланд сўрилар устида назар солиб турурлар'}, {'chapter': 83, 'verse': 24, 'text': 'تَعۡرِفُ فِي وُجُوهِهِمۡ نَضۡرَةَ ٱلنَّعِيمِ\nУларнинг чеҳраларидан жаннат равнақини билиб оласан'}, {'chapter': 83, 'verse': 25, 'text': 'يُسۡقَوۡنَ مِن رَّحِيقٖ مَّخۡتُومٍ\nУлар муҳрланган, ниҳоятда пок шаробдан ичурлар'}, {'chapter': 83, 'verse': 26, 'text': 'خِتَٰمُهُۥ مِسۡكٞۚ وَفِي ذَٰلِكَ فَلۡيَتَنَافَسِ ٱلۡمُتَنَٰفِسُونَ\nУ(шароб)нинг хотимаси мискдандир. Ва шу нарса учун мусобақа қилувчилар мусобақа қилсинлар'}, {'chapter': 83, 'verse': 27, 'text': 'وَمِزَاجُهُۥ مِن تَسۡنِيمٍ\nУ шаробнинг аралашмаси «Таснийм»дандир'}, {'chapter': 83, 'verse': 28, 'text': 'عَيۡنٗا يَشۡرَبُ بِهَا ٱلۡمُقَرَّبُونَ\nУ(Таснийм) бир булоқдирки, ундан муқарраблар ичарлар'}, {'chapter': 83, 'verse': 29, 'text': 'إِنَّ ٱلَّذِينَ أَجۡرَمُواْ كَانُواْ مِنَ ٱلَّذِينَ ءَامَنُواْ يَضۡحَكُونَ\nАлбатта, жиноят қилганлар иймон келтирганлар устидан кулар эдилар'}, {'chapter': 83, 'verse': 30, 'text': 'وَإِذَا مَرُّواْ بِهِمۡ يَتَغَامَزُونَ\nАгар мўминлар уларнинг ёнидан ўтсалар, масхаралаб ишора қилар эдилар'}, {'chapter': 83, 'verse': 31, 'text': 'وَإِذَا ٱنقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمُ ٱنقَلَبُواْ فَكِهِينَ\nАгар (кофирлар) ўз аҳллари ҳузурига қайтиб борсалар, ҳузурланиб қайтиб борар эдилар'}, {'chapter': 83, 'verse': 32, 'text': 'وَإِذَا رَأَوۡهُمۡ قَالُوٓاْ إِنَّ هَـٰٓؤُلَآءِ لَضَآلُّونَ\nВа агар у(мўмин)ларни кўрсалар, «Албатта, анавилар адашганлар», дер эдилар'}, {'chapter': 83, 'verse': 33, 'text': 'وَمَآ أُرۡسِلُواْ عَلَيۡهِمۡ حَٰفِظِينَ\nҲолбуки, улар мўминлар устидан кузатувчи қилиб юборилганлари йўқ-ку'}, {'chapter': 83, 'verse': 34, 'text': 'فَٱلۡيَوۡمَ ٱلَّذِينَ ءَامَنُواْ مِنَ ٱلۡكُفَّارِ يَضۡحَكُونَ\nЭнди, бу Кунда иймон келтирганлар кофирлар устидан куларлар'}, {'chapter': 83, 'verse': 35, 'text': 'عَلَى ٱلۡأَرَآئِكِ يَنظُرُونَ\nПардали, баланд сўрилар устида назар солиб турарлар'}, {'chapter': 83, 'verse': 36, 'text': 'هَلۡ ثُوِّبَ ٱلۡكُفَّارُ مَا كَانُواْ يَفۡعَلُونَ\nКофирлар бу дунёда қилган ишларининг «савобини» олармиканлар'}], '84': [{'chapter': 84, 'verse': 1, 'text': 'إِذَا ٱلسَّمَآءُ ٱنشَقَّتۡ\nВақтики, осмон ёрилса'}, {'chapter': 84, 'verse': 2, 'text': 'وَأَذِنَتۡ لِرَبِّهَا وَحُقَّتۡ\nУ(осмон) ўз Роббисига қулоқ осди, бўйсунди ва шундоқ бўлиши керак эди'}, {'chapter': 84, 'verse': 3, 'text': 'وَإِذَا ٱلۡأَرۡضُ مُدَّتۡ\nВа вақтики, ер чўзилса'}, {'chapter': 84, 'verse': 4, 'text': 'وَأَلۡقَتۡ مَا فِيهَا وَتَخَلَّتۡ\nВа ўз ичидаги нарсаларни отиб чиқариб, холи қолса'}, {'chapter': 84, 'verse': 5, 'text': 'وَأَذِنَتۡ لِرَبِّهَا وَحُقَّتۡ\nУ(ер) ўз Роббисига қулоқ осди, бўйсунди ва шундоқ бўлиши керак эди'}, {'chapter': 84, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلۡإِنسَٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدۡحٗا فَمُلَٰقِيهِ\nЭй инсон! Албатта, сен Роббингга йўлиққунингча, ҳаракату уринишда бўласан ва охири унга йўлиқасан ҳам'}, {'chapter': 84, 'verse': 7, 'text': 'فَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ\nАммо кимнинг китоби ўнг тарафдан берилса'}, {'chapter': 84, 'verse': 8, 'text': 'فَسَوۡفَ يُحَاسَبُ حِسَابٗا يَسِيرٗا\nТезда, осонгина ҳисоб қилинур'}, {'chapter': 84, 'verse': 9, 'text': 'وَيَنقَلِبُ إِلَىٰٓ أَهۡلِهِۦ مَسۡرُورٗا\nВа аҳли ҳузурига хурсанд ҳолда қайтиб борур'}, {'chapter': 84, 'verse': 10, 'text': 'وَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ وَرَآءَ ظَهۡرِهِۦ\nВа аммо кимнинг китоби орқа тарафдан берилса'}, {'chapter': 84, 'verse': 11, 'text': 'فَسَوۡفَ يَدۡعُواْ ثُبُورٗا\nУ тезда ўзига ҳалокатни чақирур'}, {'chapter': 84, 'verse': 12, 'text': 'وَيَصۡلَىٰ سَعِيرًا\nВа қизиб турган дўзахга кирур'}, {'chapter': 84, 'verse': 13, 'text': 'إِنَّهُۥ كَانَ فِيٓ أَهۡلِهِۦ مَسۡرُورًا\nЧунки, у (бу дунёда) ўз аҳли билан хурсанд бўлиб ўтган эди'}, {'chapter': 84, 'verse': 14, 'text': 'إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ\nЧунки, у ҳеч қачон орқага қайтмасликни ўйлаган эди'}, {'chapter': 84, 'verse': 15, 'text': 'بَلَىٰٓۚ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرٗا\nҲа! Албатта, Робби уни доим кўриб турувчи эди'}, {'chapter': 84, 'verse': 16, 'text': 'فَلَآ أُقۡسِمُ بِٱلشَّفَقِ\nШафақ билан қасамки'}, {'chapter': 84, 'verse': 17, 'text': 'وَٱلَّيۡلِ وَمَا وَسَقَ\nКечаси ва у ўраб олган нарсалар билан қасамки'}, {'chapter': 84, 'verse': 18, 'text': 'وَٱلۡقَمَرِ إِذَا ٱتَّسَقَ\nВа тўлин ой билан қасамки'}, {'chapter': 84, 'verse': 19, 'text': 'لَتَرۡكَبُنَّ طَبَقًا عَن طَبَقٖ\nАлбатта, сизлар табақама-табақа минасизлар'}, {'chapter': 84, 'verse': 20, 'text': 'فَمَا لَهُمۡ لَا يُؤۡمِنُونَ\nНима бўлган уларга, иймон келтирмаслар'}, {'chapter': 84, 'verse': 21, 'text': 'وَإِذَا قُرِئَ عَلَيۡهِمُ ٱلۡقُرۡءَانُ لَا يَسۡجُدُونَۤ۩\nВа агар уларга Қуръон ўқилса, сажда қилмаслар'}, {'chapter': 84, 'verse': 22, 'text': 'بَلِ ٱلَّذِينَ كَفَرُواْ يُكَذِّبُونَ\nБалки кофирлар ёлғонга чиқарурлар'}, {'chapter': 84, 'verse': 23, 'text': 'وَٱللَّهُ أَعۡلَمُ بِمَا يُوعُونَ\nУлар (қалбларида) нимани сақлаётганларини Аллоҳнинг Ўзи биладир'}, {'chapter': 84, 'verse': 24, 'text': 'فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ\nУларга аламли азобнинг «башоратини» бер'}, {'chapter': 84, 'verse': 25, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونِۭ\nИлло, иймон келтирганларга ва яхши амаллар қилганларга миннатсиз, узлуксиз ажрлар бор'}], '85': [{'chapter': 85, 'verse': 1, 'text': 'وَٱلسَّمَآءِ ذَاتِ ٱلۡبُرُوجِ\nБуржлар эгаси бўлган осмон билан қасам'}, {'chapter': 85, 'verse': 2, 'text': 'وَٱلۡيَوۡمِ ٱلۡمَوۡعُودِ\nВа ваъда қилинган кун билан қасам'}, {'chapter': 85, 'verse': 3, 'text': 'وَشَاهِدٖ وَمَشۡهُودٖ\nГувоҳлик берувчи ва гувоҳлик берилган билан қасам'}, {'chapter': 85, 'verse': 4, 'text': 'قُتِلَ أَصۡحَٰبُ ٱلۡأُخۡدُودِ\n«Ухдуд» эгаларига лаънат бўлсин'}, {'chapter': 85, 'verse': 5, 'text': 'ٱلنَّارِ ذَاتِ ٱلۡوَقُودِ\nУ ёқилғиси кўп, қаттиқ исиган оловдир'}, {'chapter': 85, 'verse': 6, 'text': 'إِذۡ هُمۡ عَلَيۡهَا قُعُودٞ\nУ(кофир)лар ўша(чуқур)лар атрофида ўтирибдирлар'}, {'chapter': 85, 'verse': 7, 'text': 'وَهُمۡ عَلَىٰ مَا يَفۡعَلُونَ بِٱلۡمُؤۡمِنِينَ شُهُودٞ\nВа улар мўминларга қилаётган нарсаларига ўзлари гувоҳдирлар'}, {'chapter': 85, 'verse': 8, 'text': 'وَمَا نَقَمُواْ مِنۡهُمۡ إِلَّآ أَن يُؤۡمِنُواْ بِٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ\nВа (улар) мўминлардан Азийз, Ҳамийд Аллоҳга бўлган иймондан бошқа «айб» топа олмадилар'}, {'chapter': 85, 'verse': 9, 'text': 'ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nУ шундай зотки, осмонлару ернинг мулки Унга хосдир ва Аллоҳ ҳар бир нарсага Ўзи гувоҳдир'}, {'chapter': 85, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ فَتَنُواْ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ ثُمَّ لَمۡ يَتُوبُواْ فَلَهُمۡ عَذَابُ جَهَنَّمَ وَلَهُمۡ عَذَابُ ٱلۡحَرِيقِ\nАлбатта, мўмин ва мўминаларни фитна қилиб, сўнгра тавба қилмаганларга, ана ўшаларга жаҳаннам азоби ва куйдирувчи азоб бор'}, {'chapter': 85, 'verse': 11, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡكَبِيرُ\nАлбатта, иймон келтирган ва солиҳ амал қилганларга, уларга, остидан анҳорлар оқиб турган жаннатлар бор. Бу эса ниҳоятда улкан ютуқдир'}, {'chapter': 85, 'verse': 12, 'text': 'إِنَّ بَطۡشَ رَبِّكَ لَشَدِيدٌ\nАлбатта, Роббингнинг чангали жуда ҳам шиддатли, қаттиқдир'}, {'chapter': 85, 'verse': 13, 'text': 'إِنَّهُۥ هُوَ يُبۡدِئُ وَيُعِيدُ\nАлбатта, У Ўзи бошлайдир ва қайтарадир'}, {'chapter': 85, 'verse': 14, 'text': 'وَهُوَ ٱلۡغَفُورُ ٱلۡوَدُودُ\nВа У мағфират қилувчи, барчага яхшиликни раво кўрувчи Зотдир'}, {'chapter': 85, 'verse': 15, 'text': 'ذُو ٱلۡعَرۡشِ ٱلۡمَجِيدُ\nУ, Арш эгаси ҳамда қадри улуғ Зотдир'}, {'chapter': 85, 'verse': 16, 'text': 'فَعَّالٞ لِّمَا يُرِيدُ\nУ нимани хоҳласа, қила олувчи Зотдир'}, {'chapter': 85, 'verse': 17, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ ٱلۡجُنُودِ\nСенга аскарларнинг хабари келдими'}, {'chapter': 85, 'verse': 18, 'text': 'فِرۡعَوۡنَ وَثَمُودَ\nФиръавн ва Самуднинг'}, {'chapter': 85, 'verse': 19, 'text': 'بَلِ ٱلَّذِينَ كَفَرُواْ فِي تَكۡذِيبٖ\nБалки, кофирлар ёлғонга чиқаришадир'}, {'chapter': 85, 'verse': 20, 'text': 'وَٱللَّهُ مِن وَرَآئِهِم مُّحِيطُۢ\nҲолбуки, Аллоҳ уларнинг ортидан иҳота қилиб турувчидир'}, {'chapter': 85, 'verse': 21, 'text': 'بَلۡ هُوَ قُرۡءَانٞ مَّجِيدٞ\nЙўқ!!! У Қуръони Мажийддур'}, {'chapter': 85, 'verse': 22, 'text': 'فِي لَوۡحٖ مَّحۡفُوظِۭ\nУ Лавҳул Маҳфуздадир'}], '86': [{'chapter': 86, 'verse': 1, 'text': 'وَٱلسَّمَآءِ وَٱلطَّارِقِ\nОсмон билан қасам ва ториқ билан қасам'}, {'chapter': 86, 'verse': 2, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلطَّارِقُ\nТориқ нималигини сенга не билдирди'}, {'chapter': 86, 'verse': 3, 'text': 'ٱلنَّجۡمُ ٱلثَّاقِبُ\nУ, нур сочиб турувчи юлдуздир'}, {'chapter': 86, 'verse': 4, 'text': 'إِن كُلُّ نَفۡسٖ لَّمَّا عَلَيۡهَا حَافِظٞ\nҲеч бир жон йўқки, унинг ҳифз қилиб турувчиси бўлмаса'}, {'chapter': 86, 'verse': 5, 'text': 'فَلۡيَنظُرِ ٱلۡإِنسَٰنُ مِمَّ خُلِقَ\nИнсон нимадан яралганига назар солсин'}, {'chapter': 86, 'verse': 6, 'text': 'خُلِقَ مِن مَّآءٖ دَافِقٖ\nУ отилиб чиқувчи сувдан яралгандир'}, {'chapter': 86, 'verse': 7, 'text': 'يَخۡرُجُ مِنۢ بَيۡنِ ٱلصُّلۡبِ وَٱلتَّرَآئِبِ\nУ (сув) сулб (орқа умуртқа) ва тароиб (кўкрак суяги) орасидан чиқадир'}, {'chapter': 86, 'verse': 8, 'text': 'إِنَّهُۥ عَلَىٰ رَجۡعِهِۦ لَقَادِرٞ\nАлбатта, У зот уни қайта тирилтиришга қодирдир'}, {'chapter': 86, 'verse': 9, 'text': 'يَوۡمَ تُبۡلَى ٱلسَّرَآئِرُ\nСирлар фош бўладиган кундадир'}, {'chapter': 86, 'verse': 10, 'text': 'فَمَا لَهُۥ مِن قُوَّةٖ وَلَا نَاصِرٖ\nБас (ўшанда) у(инсон)да қувват ҳам, нусрат берувчи ҳам бўлмайдир'}, {'chapter': 86, 'verse': 11, 'text': 'وَٱلسَّمَآءِ ذَاتِ ٱلرَّجۡعِ\nҚайтувчи соҳиби бўлган осмон билан қасам'}, {'chapter': 86, 'verse': 12, 'text': 'وَٱلۡأَرۡضِ ذَاتِ ٱلصَّدۡعِ\nЁриб чиқувчи эгаси бўлган ер билан қасам'}, {'chapter': 86, 'verse': 13, 'text': 'إِنَّهُۥ لَقَوۡلٞ فَصۡلٞ\nАлбатта, у (Қуръон) ажратувчи сўздир'}, {'chapter': 86, 'verse': 14, 'text': 'وَمَا هُوَ بِٱلۡهَزۡلِ\nВа у ҳазил эмасдир'}, {'chapter': 86, 'verse': 15, 'text': 'إِنَّهُمۡ يَكِيدُونَ كَيۡدٗا\nАлбатта, улар ҳийла қиладир'}, {'chapter': 86, 'verse': 16, 'text': 'وَأَكِيدُ كَيۡدٗا\nВа Мен ҳам «ҳийла» қилурман'}, {'chapter': 86, 'verse': 17, 'text': 'فَمَهِّلِ ٱلۡكَٰفِرِينَ أَمۡهِلۡهُمۡ رُوَيۡدَۢا\nБас, кофирларга муҳлат бер. Ҳа, уларга озгина муҳлат бер'}], '87': [{'chapter': 87, 'verse': 1, 'text': 'سَبِّحِ ٱسۡمَ رَبِّكَ ٱلۡأَعۡلَى\nОлий қадар бўлган Роббинг исмини поклаб ёд эт'}, {'chapter': 87, 'verse': 2, 'text': 'ٱلَّذِي خَلَقَ فَسَوَّىٰ\nУ яратган ва мутаносиб қилган Зотдир'}, {'chapter': 87, 'verse': 3, 'text': 'وَٱلَّذِي قَدَّرَ فَهَدَىٰ\nУ, ўлчов билан қилган ва ҳидоятга бошлаган Зотдир'}, {'chapter': 87, 'verse': 4, 'text': 'وَٱلَّذِيٓ أَخۡرَجَ ٱلۡمَرۡعَىٰ\nУ, ўт-ўланни чиқарган Зотдир'}, {'chapter': 87, 'verse': 5, 'text': 'فَجَعَلَهُۥ غُثَآءً أَحۡوَىٰ\nБас, уни қорайган хашак қилган Зотдир'}, {'chapter': 87, 'verse': 6, 'text': 'سَنُقۡرِئُكَ فَلَا تَنسَىٰٓ\nБиз сенга (Қуръонни) қироат қилдирамиз, бас, сен уни эсдан чиқармассан'}, {'chapter': 87, 'verse': 7, 'text': 'إِلَّا مَا شَآءَ ٱللَّهُۚ إِنَّهُۥ يَعۡلَمُ ٱلۡجَهۡرَ وَمَا يَخۡفَىٰ\nИлло, Аллоҳ хоҳлаганини. Албатта, У ошкорни ҳам, махфий нарсаларни ҳам биладир'}, {'chapter': 87, 'verse': 8, 'text': 'وَنُيَسِّرُكَ لِلۡيُسۡرَىٰ\nВа сени осон йўлга муяссар қиламиз'}, {'chapter': 87, 'verse': 9, 'text': 'فَذَكِّرۡ إِن نَّفَعَتِ ٱلذِّكۡرَىٰ\nБас, агар эслатиш манфаат берса, эслатгин'}, {'chapter': 87, 'verse': 10, 'text': 'سَيَذَّكَّرُ مَن يَخۡشَىٰ\nАлбатта, У зотдан қўрққан эсга оладир'}, {'chapter': 87, 'verse': 11, 'text': 'وَيَتَجَنَّبُهَا ٱلۡأَشۡقَى\nВа бадбахтгина ундан четда бўладир'}, {'chapter': 87, 'verse': 12, 'text': 'ٱلَّذِي يَصۡلَى ٱلنَّارَ ٱلۡكُبۡرَىٰ\nУ катта оловга кирадир'}, {'chapter': 87, 'verse': 13, 'text': 'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحۡيَىٰ\nСўнгра, унинг ичида ўлмай ҳам, тирилмай ҳам қоладир'}, {'chapter': 87, 'verse': 14, 'text': 'قَدۡ أَفۡلَحَ مَن تَزَكَّىٰ\nҲақиқатан, ким пок бўлса, ютуқ топадир'}, {'chapter': 87, 'verse': 15, 'text': 'وَذَكَرَ ٱسۡمَ رَبِّهِۦ فَصَلَّىٰ\nВа Робби исмини зикр қилса ва намоз ўқиса ҳамдир'}, {'chapter': 87, 'verse': 16, 'text': 'بَلۡ تُؤۡثِرُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا\nБалки, сизлар бу дунё ҳаётини устун қўясизлар'}, {'chapter': 87, 'verse': 17, 'text': 'وَٱلۡأٓخِرَةُ خَيۡرٞ وَأَبۡقَىٰٓ\nҲолбуки, охират яхшироқ ва боқийроқдир'}, {'chapter': 87, 'verse': 18, 'text': 'إِنَّ هَٰذَا لَفِي ٱلصُّحُفِ ٱلۡأُولَىٰ\nАлбатта, бу аввалги саҳифаларда бордир'}, {'chapter': 87, 'verse': 19, 'text': 'صُحُفِ إِبۡرَٰهِيمَ وَمُوسَىٰ\nИброҳим ва Мусо саҳифаларидадир'}], '88': [{'chapter': 88, 'verse': 1, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ ٱلۡغَٰشِيَةِ\nБатаҳқиқ, сенга ғошия хабари келди'}, {'chapter': 88, 'verse': 2, 'text': 'وُجُوهٞ يَوۡمَئِذٍ خَٰشِعَةٌ\nЎшал кунда бир чеҳралар қўрқинч билан тўладир'}, {'chapter': 88, 'verse': 3, 'text': 'عَامِلَةٞ نَّاصِبَةٞ\nУлар иш бажарувчидир ва ниҳоятда чарчагандир'}, {'chapter': 88, 'verse': 4, 'text': 'تَصۡلَىٰ نَارًا حَامِيَةٗ\nЎта қизиб турган оловга кирадир'}, {'chapter': 88, 'verse': 5, 'text': 'تُسۡقَىٰ مِنۡ عَيۡنٍ ءَانِيَةٖ\nУлар қайнаб турган булоқдан суғориладир'}, {'chapter': 88, 'verse': 6, 'text': 'لَّيۡسَ لَهُمۡ طَعَامٌ إِلَّا مِن ضَرِيعٖ\nУларга тикандан бошқа таом йўқдир'}, {'chapter': 88, 'verse': 7, 'text': 'لَّا يُسۡمِنُ وَلَا يُغۡنِي مِن جُوعٖ\nУ на семиртирмас ва на очликни кетказмасдир'}, {'chapter': 88, 'verse': 8, 'text': 'وُجُوهٞ يَوۡمَئِذٖ نَّاعِمَةٞ\nЎшал кунда бир чеҳралар неъмат хурсандлиги билан тўладир'}, {'chapter': 88, 'verse': 9, 'text': 'لِّسَعۡيِهَا رَاضِيَةٞ\nУлар ўз ишларидан розидир'}, {'chapter': 88, 'verse': 10, 'text': 'فِي جَنَّةٍ عَالِيَةٖ\nУлар олий даражали жаннатдадир'}, {'chapter': 88, 'verse': 11, 'text': 'لَّا تَسۡمَعُ فِيهَا لَٰغِيَةٗ\nУнда бекорчи гапларни эшитмаслар'}, {'chapter': 88, 'verse': 12, 'text': 'فِيهَا عَيۡنٞ جَارِيَةٞ\nУнда оқиб турган булоқлар бордир'}, {'chapter': 88, 'verse': 13, 'text': 'فِيهَا سُرُرٞ مَّرۡفُوعَةٞ\nУнда баланд-баланд сўрилар бордир'}, {'chapter': 88, 'verse': 14, 'text': 'وَأَكۡوَابٞ مَّوۡضُوعَةٞ\nВа тайёрлаб қўйилган қадаҳлар бордир'}, {'chapter': 88, 'verse': 15, 'text': 'وَنَمَارِقُ مَصۡفُوفَةٞ\nВа саф-саф қилиб тизилган ёстиқлар бордир'}, {'chapter': 88, 'verse': 16, 'text': 'وَزَرَابِيُّ مَبۡثُوثَةٌ\nҲамма ёққа тўшалган гиламлар бордир'}, {'chapter': 88, 'verse': 17, 'text': 'أَفَلَا يَنظُرُونَ إِلَى ٱلۡإِبِلِ كَيۡفَ خُلِقَتۡ\nУлар назар солмасми: Туянинг қандай яратилганига'}, {'chapter': 88, 'verse': 18, 'text': 'وَإِلَى ٱلسَّمَآءِ كَيۡفَ رُفِعَتۡ\nВа осмоннинг қандоқ кўтарилганига'}, {'chapter': 88, 'verse': 19, 'text': 'وَإِلَى ٱلۡجِبَالِ كَيۡفَ نُصِبَتۡ\nВа тоғларнинг қандоқ ўрнаштирилганига'}, {'chapter': 88, 'verse': 20, 'text': 'وَإِلَى ٱلۡأَرۡضِ كَيۡفَ سُطِحَتۡ\nВа ернинг қандоқ текислаб қўйилганига'}, {'chapter': 88, 'verse': 21, 'text': 'فَذَكِّرۡ إِنَّمَآ أَنتَ مُذَكِّرٞ\nБас, эслатгин, сен, албатта, эслатувчидирсан'}, {'chapter': 88, 'verse': 22, 'text': 'لَّسۡتَ عَلَيۡهِم بِمُصَيۡطِرٍ\nСен уларнинг устидан ҳукмингни ўтказувчи эмассан'}, {'chapter': 88, 'verse': 23, 'text': 'إِلَّا مَن تَوَلَّىٰ وَكَفَرَ\nИлло, ким юз ўгирса ва кофир бўлса'}, {'chapter': 88, 'verse': 24, 'text': 'فَيُعَذِّبُهُ ٱللَّهُ ٱلۡعَذَابَ ٱلۡأَكۡبَرَ\nБас, уни Аллоҳ катта азоб билан азоблайдир'}, {'chapter': 88, 'verse': 25, 'text': 'إِنَّ إِلَيۡنَآ إِيَابَهُمۡ\nАлбатта, уларнинг қайтиши Бизгадир'}, {'chapter': 88, 'verse': 26, 'text': 'ثُمَّ إِنَّ عَلَيۡنَا حِسَابَهُم\nСўнгра, албатта, уларнинг ҳисоб-китоби ҳам Бизда бўладир'}], '89': [{'chapter': 89, 'verse': 1, 'text': 'وَٱلۡفَجۡرِ\nФажр билан қасам'}, {'chapter': 89, 'verse': 2, 'text': 'وَلَيَالٍ عَشۡرٖ\nВа ўн кеча билан қасам'}, {'chapter': 89, 'verse': 3, 'text': 'وَٱلشَّفۡعِ وَٱلۡوَتۡرِ\nЖуфту тоқ билан қасам'}, {'chapter': 89, 'verse': 4, 'text': 'وَٱلَّيۡلِ إِذَا يَسۡرِ\nВа юрган тун билан қасам'}, {'chapter': 89, 'verse': 5, 'text': 'هَلۡ فِي ذَٰلِكَ قَسَمٞ لِّذِي حِجۡرٍ\nМана шуларда ақл эгаси учун (қаноатлантирувчи) қасам бордир'}, {'chapter': 89, 'verse': 6, 'text': 'أَلَمۡ تَرَ كَيۡفَ فَعَلَ رَبُّكَ بِعَادٍ\nСенинг Роббинг Одга қандай (муомала) қилганини билмадингми'}, {'chapter': 89, 'verse': 7, 'text': 'إِرَمَ ذَاتِ ٱلۡعِمَادِ\nБаланд устунли «Ирам»га'}, {'chapter': 89, 'verse': 8, 'text': 'ٱلَّتِي لَمۡ يُخۡلَقۡ مِثۡلُهَا فِي ٱلۡبِلَٰدِ\nУнинг мисли бошқа диёрларда халқ қилинмаганга'}, {'chapter': 89, 'verse': 9, 'text': 'وَثَمُودَ ٱلَّذِينَ جَابُواْ ٱلصَّخۡرَ بِٱلۡوَادِ\nВа водийда харсанг тошларни кесиб (иморат ясаган) Самудни'}, {'chapter': 89, 'verse': 10, 'text': 'وَفِرۡعَوۡنَ ذِي ٱلۡأَوۡتَادِ\nВа кўп аскарлари бор Фиръавнни'}, {'chapter': 89, 'verse': 11, 'text': 'ٱلَّذِينَ طَغَوۡاْ فِي ٱلۡبِلَٰدِ\nУлар турли ўлкаларда туғёнга кетган эдилар'}, {'chapter': 89, 'verse': 12, 'text': 'فَأَكۡثَرُواْ فِيهَا ٱلۡفَسَادَ\nУлар у(ўлка)ларда бузғунчиликни кўпайтирган эдилар'}, {'chapter': 89, 'verse': 13, 'text': 'فَصَبَّ عَلَيۡهِمۡ رَبُّكَ سَوۡطَ عَذَابٍ\nБас, Роббинг уларнинг бошига турли азобларни қуйди'}, {'chapter': 89, 'verse': 14, 'text': 'إِنَّ رَبَّكَ لَبِٱلۡمِرۡصَادِ\nАлбатта, Роббинг кузатиб турувчидир'}, {'chapter': 89, 'verse': 15, 'text': 'فَأَمَّا ٱلۡإِنسَٰنُ إِذَا مَا ٱبۡتَلَىٰهُ رَبُّهُۥ فَأَكۡرَمَهُۥ وَنَعَّمَهُۥ فَيَقُولُ رَبِّيٓ أَكۡرَمَنِ\nБас, қачон инсонни Робби синаш учун икром қилса ва неъмат берса, «Мени Роббим икром қилди», дейдир'}, {'chapter': 89, 'verse': 16, 'text': 'وَأَمَّآ إِذَا مَا ٱبۡتَلَىٰهُ فَقَدَرَ عَلَيۡهِ رِزۡقَهُۥ فَيَقُولُ رَبِّيٓ أَهَٰنَنِ\nБас, қачон Робби синаш учун ризқини тор қилиб қўйса, у: «Роббим мени хорлади», дейдир'}, {'chapter': 89, 'verse': 17, 'text': 'كَلَّاۖ بَل لَّا تُكۡرِمُونَ ٱلۡيَتِيمَ\nЙўқ! Балки сизлар етимларни икром қилмайсизлар'}, {'chapter': 89, 'verse': 18, 'text': 'وَلَا تَحَـٰٓضُّونَ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ\nБир-бирингизни мискинларга таом беришга ундамайсизлар'}, {'chapter': 89, 'verse': 19, 'text': 'وَتَأۡكُلُونَ ٱلتُّرَاثَ أَكۡلٗا لَّمّٗا\nВа меросни шиддат-ла ейсизлар'}, {'chapter': 89, 'verse': 20, 'text': 'وَتُحِبُّونَ ٱلۡمَالَ حُبّٗا جَمّٗا\nВа молни кўп яхши кўрасизлар'}, {'chapter': 89, 'verse': 21, 'text': 'كَلَّآۖ إِذَا دُكَّتِ ٱلۡأَرۡضُ دَكّٗا دَكّٗا\nЙўқ! Вақтики, ер зилзилага тушиб, парча-парча бўлиб кетса'}, {'chapter': 89, 'verse': 22, 'text': 'وَجَآءَ رَبُّكَ وَٱلۡمَلَكُ صَفّٗا صَفّٗا\nВа Роббинг ва фаришталар саф-саф бўлиб келсалар'}, {'chapter': 89, 'verse': 23, 'text': 'وَجِاْيٓءَ يَوۡمَئِذِۭ بِجَهَنَّمَۚ يَوۡمَئِذٖ يَتَذَكَّرُ ٱلۡإِنسَٰنُ وَأَنَّىٰ لَهُ ٱلذِّكۡرَىٰ\nВа ўша кунда жаҳаннамни келтирса, ўша кунда инсон эслайдир. Лекин бу эслашдан энди не фойда'}, {'chapter': 89, 'verse': 24, 'text': 'يَقُولُ يَٰلَيۡتَنِي قَدَّمۡتُ لِحَيَاتِي\nУ: «Афсуски, охират ҳаётимга ҳам бирор нарса тақдим қилсам бўлар экан», дейдир'}, {'chapter': 89, 'verse': 25, 'text': 'فَيَوۡمَئِذٖ لَّا يُعَذِّبُ عَذَابَهُۥٓ أَحَدٞ\nБас, ўша кунда Аллоҳнинг азобига ўхшаш азоб-ла ҳеч ким азобламайдир'}, {'chapter': 89, 'verse': 26, 'text': 'وَلَا يُوثِقُ وَثَاقَهُۥٓ أَحَدٞ\nВа Унинг боғлашига ўхшаш ҳеч ким боғламайдир'}, {'chapter': 89, 'verse': 27, 'text': 'يَـٰٓأَيَّتُهَا ٱلنَّفۡسُ ٱلۡمُطۡمَئِنَّةُ\nЭй, хотиржам нафс'}, {'chapter': 89, 'verse': 28, 'text': 'ٱرۡجِعِيٓ إِلَىٰ رَبِّكِ رَاضِيَةٗ مَّرۡضِيَّةٗ\nРоббингга сен Ундан, У сендан рози бўлган ҳолда қайт'}, {'chapter': 89, 'verse': 29, 'text': 'فَٱدۡخُلِي فِي عِبَٰدِي\nБас, бандаларим ичига киргин'}, {'chapter': 89, 'verse': 30, 'text': 'وَٱدۡخُلِي جَنَّتِي\nВа жаннатимга киргин'}], '90': [{'chapter': 90, 'verse': 1, 'text': 'لَآ أُقۡسِمُ بِهَٰذَا ٱلۡبَلَدِ\nМана шу шаҳар билан қасам'}, {'chapter': 90, 'verse': 2, 'text': 'وَأَنتَ حِلُّۢ بِهَٰذَا ٱلۡبَلَدِ\nСен муқим турган бу шаҳар-ла'}, {'chapter': 90, 'verse': 3, 'text': 'وَوَالِدٖ وَمَا وَلَدَ\nВа Волид ва ундан тарқаган валадлар билан қасам'}, {'chapter': 90, 'verse': 4, 'text': 'لَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ فِي كَبَدٍ\nБатаҳқиқ, Биз инсонни машаққатда яратдик'}, {'chapter': 90, 'verse': 5, 'text': 'أَيَحۡسَبُ أَن لَّن يَقۡدِرَ عَلَيۡهِ أَحَدٞ\nУ инсон унга ҳеч кимнинг кучи етмас, деб ҳисобларми'}, {'chapter': 90, 'verse': 6, 'text': 'يَقُولُ أَهۡلَكۡتُ مَالٗا لُّبَدًا\nУ: «Кўплаб мол-дунё нафақа қилдим», дейдир'}, {'chapter': 90, 'verse': 7, 'text': 'أَيَحۡسَبُ أَن لَّمۡ يَرَهُۥٓ أَحَدٌ\nУ ўзини биров кўрмади, деб ҳисоблайдирми'}, {'chapter': 90, 'verse': 8, 'text': 'أَلَمۡ نَجۡعَل لَّهُۥ عَيۡنَيۡنِ\nБиз унга икки кўзни'}, {'chapter': 90, 'verse': 9, 'text': 'وَلِسَانٗا وَشَفَتَيۡنِ\nВа тилни ва икки лабни бермадикми'}, {'chapter': 90, 'verse': 10, 'text': 'وَهَدَيۡنَٰهُ ٱلنَّجۡدَيۡنِ\nВа Биз уни икки йўлга йўллаб қўймадикми'}, {'chapter': 90, 'verse': 11, 'text': 'فَلَا ٱقۡتَحَمَ ٱلۡعَقَبَةَ\nБас, у довон ошиб ўтмади'}, {'chapter': 90, 'verse': 12, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡعَقَبَةُ\nДовон қандоқ нарса эканини сенга нима билдирди'}, {'chapter': 90, 'verse': 13, 'text': 'فَكُّ رَقَبَةٍ\nУ қул озод қилишдир'}, {'chapter': 90, 'verse': 14, 'text': 'أَوۡ إِطۡعَٰمٞ فِي يَوۡمٖ ذِي مَسۡغَبَةٖ\nЁки очарчилик кунида таом беришдир'}, {'chapter': 90, 'verse': 15, 'text': 'يَتِيمٗا ذَا مَقۡرَبَةٍ\nҚариндош етимга'}, {'chapter': 90, 'verse': 16, 'text': 'أَوۡ مِسۡكِينٗا ذَا مَتۡرَبَةٖ\nЁки тупроққа қорилган мискинга'}, {'chapter': 90, 'verse': 17, 'text': 'ثُمَّ كَانَ مِنَ ٱلَّذِينَ ءَامَنُواْ وَتَوَاصَوۡاْ بِٱلصَّبۡرِ وَتَوَاصَوۡاْ بِٱلۡمَرۡحَمَةِ\nСўнгра, иймон келтирганлар ва бир-бирини сабрга, меҳр-шафқатга чақирганлар бўлса'}, {'chapter': 90, 'verse': 18, 'text': 'أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡمَيۡمَنَةِ\nАна ўшалар ўнг томон эгаларидир'}, {'chapter': 90, 'verse': 19, 'text': 'وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا هُمۡ أَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ\nОятларимизга куфр келтирганлар чап томон эгаларидир'}, {'chapter': 90, 'verse': 20, 'text': 'عَلَيۡهِمۡ نَارٞ مُّؤۡصَدَةُۢ\nУларни чиқиб бўлмайдиган ўт-олов ўраб олгандир'}], '91': [{'chapter': 91, 'verse': 1, 'text': 'وَٱلشَّمۡسِ وَضُحَىٰهَا\nҚуёш билан ва унинг зиёси билан қасам'}, {'chapter': 91, 'verse': 2, 'text': 'وَٱلۡقَمَرِ إِذَا تَلَىٰهَا\nУнинг ортидан келган ой билан қасам'}, {'chapter': 91, 'verse': 3, 'text': 'وَٱلنَّهَارِ إِذَا جَلَّىٰهَا\nВа у(қуёш)ни равшан кўрсатган наҳор билан қасам'}, {'chapter': 91, 'verse': 4, 'text': 'وَٱلَّيۡلِ إِذَا يَغۡشَىٰهَا\nВа у(қуёш)ни қоплаган тун билан қасам'}, {'chapter': 91, 'verse': 5, 'text': 'وَٱلسَّمَآءِ وَمَا بَنَىٰهَا\nВа осмон билан ва унинг бино қилиниши билан қасам'}, {'chapter': 91, 'verse': 6, 'text': 'وَٱلۡأَرۡضِ وَمَا طَحَىٰهَا\nВа ер билан ва унинг тўшалиши билан қасам'}, {'chapter': 91, 'verse': 7, 'text': 'وَنَفۡسٖ وَمَا سَوَّىٰهَا\nВа нафс билан ва унинг мукаммал қилиниши билан қасам'}, {'chapter': 91, 'verse': 8, 'text': 'فَأَلۡهَمَهَا فُجُورَهَا وَتَقۡوَىٰهَا\nБас, унга фужурини ва тақвосини билдирди'}, {'chapter': 91, 'verse': 9, 'text': 'قَدۡ أَفۡلَحَ مَن زَكَّىٰهَا\nБатаҳқиқ, ким у(нафс)ни покласа, ютуққа эришди'}, {'chapter': 91, 'verse': 10, 'text': 'وَقَدۡ خَابَ مَن دَسَّىٰهَا\nВа батаҳқиқ, ким у(нафс)ни кирласа, ноумид бўлди'}, {'chapter': 91, 'verse': 11, 'text': 'كَذَّبَتۡ ثَمُودُ بِطَغۡوَىٰهَآ\nСамуд қавми ўз туғёни ила ёлғонга чиқарди'}, {'chapter': 91, 'verse': 12, 'text': 'إِذِ ٱنۢبَعَثَ أَشۡقَىٰهَا\nВақтики, унинг энг бадбахти шошилиб турди'}, {'chapter': 91, 'verse': 13, 'text': 'فَقَالَ لَهُمۡ رَسُولُ ٱللَّهِ نَاقَةَ ٱللَّهِ وَسُقۡيَٰهَا\nБас, уларга Аллоҳнинг Расули: «Аллоҳнинг туясига ва унинг сувига тегманглар», деди'}, {'chapter': 91, 'verse': 14, 'text': 'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمۡدَمَ عَلَيۡهِمۡ رَبُّهُم بِذَنۢبِهِمۡ فَسَوَّىٰهَا\nБас, (уни) ёлғончига чиқаришди ва (туяни) сўйишди. Бас, Робблари уларни гуноҳлари сабабли йўқ қилиб юборди ва бунда барчани баробар қилди'}, {'chapter': 91, 'verse': 15, 'text': 'وَلَا يَخَافُ عُقۡبَٰهَا\nВа У зот бу(иш)нинг оқибатидан қўрқмас'}], '92': [{'chapter': 92, 'verse': 1, 'text': 'وَٱلَّيۡلِ إِذَا يَغۡشَىٰ\nТуннинг қоплаб келаётган пайти билан қасам'}, {'chapter': 92, 'verse': 2, 'text': 'وَٱلنَّهَارِ إِذَا تَجَلَّىٰ\nНаҳорнинг тажалли пайти билан қасам'}, {'chapter': 92, 'verse': 3, 'text': 'وَمَا خَلَقَ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ\nЭркак ва урғочининг яратилиши билан қасам'}, {'chapter': 92, 'verse': 4, 'text': 'إِنَّ سَعۡيَكُمۡ لَشَتَّىٰ\nАлбатта, сизларнинг ишларингиз турличадур'}, {'chapter': 92, 'verse': 5, 'text': 'فَأَمَّا مَنۡ أَعۡطَىٰ وَٱتَّقَىٰ\nАммо кимки (ато) берса ва тақво қилса'}, {'chapter': 92, 'verse': 6, 'text': 'وَصَدَّقَ بِٱلۡحُسۡنَىٰ\nВа гўзални тасдиқ қилса'}, {'chapter': 92, 'verse': 7, 'text': 'فَسَنُيَسِّرُهُۥ لِلۡيُسۡرَىٰ\nБас, Биз уни осонга муяссар қиламиз'}, {'chapter': 92, 'verse': 8, 'text': 'وَأَمَّا مَنۢ بَخِلَ وَٱسۡتَغۡنَىٰ\nВа аммо, кимки бахиллик ва истиғно қилса'}, {'chapter': 92, 'verse': 9, 'text': 'وَكَذَّبَ بِٱلۡحُسۡنَىٰ\nВа гўзални ёлғонга чиқарса'}, {'chapter': 92, 'verse': 10, 'text': 'فَسَنُيَسِّرُهُۥ لِلۡعُسۡرَىٰ\nБас, Биз уни қийинга муяссар қиламиз'}, {'chapter': 92, 'verse': 11, 'text': 'وَمَا يُغۡنِي عَنۡهُ مَالُهُۥٓ إِذَا تَرَدَّىٰٓ\nУ қулаган вақтда унга мол-мулки фойда бермас'}, {'chapter': 92, 'verse': 12, 'text': 'إِنَّ عَلَيۡنَا لَلۡهُدَىٰ\nАлбатта, ҳидоятга бошлаш Бизнинг зиммамиздадир'}, {'chapter': 92, 'verse': 13, 'text': 'وَإِنَّ لَنَا لَلۡأٓخِرَةَ وَٱلۡأُولَىٰ\nАлбатта, ҳам охират ва ҳам бу дунё Бизникидир'}, {'chapter': 92, 'verse': 14, 'text': 'فَأَنذَرۡتُكُمۡ نَارٗا تَلَظَّىٰ\nБас, Мен сизларни ловуллаган ўтдан огоҳлантирдим'}, {'chapter': 92, 'verse': 15, 'text': 'لَا يَصۡلَىٰهَآ إِلَّا ٱلۡأَشۡقَى\nУнга бадбахтдан бошқа кирмас'}, {'chapter': 92, 'verse': 16, 'text': 'ٱلَّذِي كَذَّبَ وَتَوَلَّىٰ\nЎша ёлғонга чиқарган ва юз ўгирган эди'}, {'chapter': 92, 'verse': 17, 'text': 'وَسَيُجَنَّبُهَا ٱلۡأَتۡقَى\nВа албатта, ундан тақводор банда четда қоладир'}, {'chapter': 92, 'verse': 18, 'text': 'ٱلَّذِي يُؤۡتِي مَالَهُۥ يَتَزَكَّىٰ\nУ, бойлигини сарфлайдир ва ўзини поклайдир'}, {'chapter': 92, 'verse': 19, 'text': 'وَمَا لِأَحَدٍ عِندَهُۥ مِن نِّعۡمَةٖ تُجۡزَىٰٓ\nБирор кишининг унда қайтарилиши лозим яхшилиги йўқ эди'}, {'chapter': 92, 'verse': 20, 'text': 'إِلَّا ٱبۡتِغَآءَ وَجۡهِ رَبِّهِ ٱلۡأَعۡلَىٰ\nФақат, ўзининг олий мақом Роббининг розилигини сўраб қиладир'}, {'chapter': 92, 'verse': 21, 'text': 'وَلَسَوۡفَ يَرۡضَىٰ\nВа албатта, тезда рози бўладир'}], '93': [{'chapter': 93, 'verse': 1, 'text': 'وَٱلضُّحَىٰ\nЧоштгоҳ билан қасам'}, {'chapter': 93, 'verse': 2, 'text': 'وَٱلَّيۡلِ إِذَا سَجَىٰ\nСукунатга чўмган тун билан қасам'}, {'chapter': 93, 'verse': 3, 'text': 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ\nРоббинг сени тарк қилгани йўқ, сенга ғазаб ҳам қилгани йўқ'}, {'chapter': 93, 'verse': 4, 'text': 'وَلَلۡأٓخِرَةُ خَيۡرٞ لَّكَ مِنَ ٱلۡأُولَىٰ\nВа албатта, охират сен учун бу дунёдан яхшидир'}, {'chapter': 93, 'verse': 5, 'text': 'وَلَسَوۡفَ يُعۡطِيكَ رَبُّكَ فَتَرۡضَىٰٓ\nВа тезда Роббинг сенга ато қилур ва сен рози бўлурсан'}, {'chapter': 93, 'verse': 6, 'text': 'أَلَمۡ يَجِدۡكَ يَتِيمٗا فَـَٔاوَىٰ\nУ сени етим топиб, жойлаб қўймадими'}, {'chapter': 93, 'verse': 7, 'text': 'وَوَجَدَكَ ضَآلّٗا فَهَدَىٰ\nВа У сени ҳайрон ҳолда топиб, ҳидоятга бошламадими'}, {'chapter': 93, 'verse': 8, 'text': 'وَوَجَدَكَ عَآئِلٗا فَأَغۡنَىٰ\nВа У сени камбағал ҳолда топиб, бой қилмадими'}, {'chapter': 93, 'verse': 9, 'text': 'فَأَمَّا ٱلۡيَتِيمَ فَلَا تَقۡهَرۡ\nАммо етимга қаҳр қилмагин'}, {'chapter': 93, 'verse': 10, 'text': 'وَأَمَّا ٱلسَّآئِلَ فَلَا تَنۡهَرۡ\nВа аммо, «соил»га зажр қилма'}, {'chapter': 93, 'verse': 11, 'text': 'وَأَمَّا بِنِعۡمَةِ رَبِّكَ فَحَدِّثۡ\nВа аммо Роббингнинг берган неъмати ҳақида сўзла'}], '94': [{'chapter': 94, 'verse': 1, 'text': 'أَلَمۡ نَشۡرَحۡ لَكَ صَدۡرَكَ\nСенинг кўксингни кенг қилиб қўймадикми'}, {'chapter': 94, 'verse': 2, 'text': 'وَوَضَعۡنَا عَنكَ وِزۡرَكَ\nВа сенинг юкингни енгиллатмадикми'}, {'chapter': 94, 'verse': 3, 'text': 'ٱلَّذِيٓ أَنقَضَ ظَهۡرَكَ\nЕлкангни босиб турганни'}, {'chapter': 94, 'verse': 4, 'text': 'وَرَفَعۡنَا لَكَ ذِكۡرَكَ\nВа сенинг зикрингни юқори кўтармадикми'}, {'chapter': 94, 'verse': 5, 'text': 'فَإِنَّ مَعَ ٱلۡعُسۡرِ يُسۡرًا\nБас, албатта, қийинчилик билан осончилик бордир'}, {'chapter': 94, 'verse': 6, 'text': 'إِنَّ مَعَ ٱلۡعُسۡرِ يُسۡرٗا\nАлбатта, қийинчилик билан осончилик бордир'}, {'chapter': 94, 'verse': 7, 'text': 'فَإِذَا فَرَغۡتَ فَٱنصَبۡ\nФориғ бўлсанг, (ибодатга) урингин'}, {'chapter': 94, 'verse': 8, 'text': 'وَإِلَىٰ رَبِّكَ فَٱرۡغَب\nВа фақат Роббингга рағбат қилгин'}], '95': [{'chapter': 95, 'verse': 1, 'text': 'وَٱلتِّينِ وَٱلزَّيۡتُونِ\nАнжир билан қасам ва зайтун билан қасам'}, {'chapter': 95, 'verse': 2, 'text': 'وَطُورِ سِينِينَ\nВа Тури Сийно билан қасам'}, {'chapter': 95, 'verse': 3, 'text': 'وَهَٰذَا ٱلۡبَلَدِ ٱلۡأَمِينِ\nВа мана бу эминлик юрти (Макка) билан қасам'}, {'chapter': 95, 'verse': 4, 'text': 'لَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ فِيٓ أَحۡسَنِ تَقۡوِيمٖ\nБатаҳқиқ, Биз инсонни энг яхши сувратда яратдик'}, {'chapter': 95, 'verse': 5, 'text': 'ثُمَّ رَدَدۡنَٰهُ أَسۡفَلَ سَٰفِلِينَ\nСўнгра уни асфала саафилийнга қайтардик'}, {'chapter': 95, 'verse': 6, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَلَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ\nИлло, иймон келтирганлар ва яхши амаллар қилганларга миннатсиз ва узлуксиз ажрлар бордир'}, {'chapter': 95, 'verse': 7, 'text': 'فَمَا يُكَذِّبُكَ بَعۡدُ بِٱلدِّينِ\nБас, сени охиратни ёлғонга чиқаришга нима олиб келур'}, {'chapter': 95, 'verse': 8, 'text': 'أَلَيۡسَ ٱللَّهُ بِأَحۡكَمِ ٱلۡحَٰكِمِينَ\nАллоҳ ҳукм қилгувчиларнинг энг ҳикматлиси эмасми'}], '96': [{'chapter': 96, 'verse': 1, 'text': 'ٱقۡرَأۡ بِٱسۡمِ رَبِّكَ ٱلَّذِي خَلَقَ\nЯратган Роббинг номи билан ўқи'}, {'chapter': 96, 'verse': 2, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ مِنۡ عَلَقٍ\nУ инсонни алақдан яратди'}, {'chapter': 96, 'verse': 3, 'text': 'ٱقۡرَأۡ وَرَبُّكَ ٱلۡأَكۡرَمُ\nЎқи! Роббинг Карамлиларнинг карамлисидир'}, {'chapter': 96, 'verse': 4, 'text': 'ٱلَّذِي عَلَّمَ بِٱلۡقَلَمِ\nУ, қалам билан илм ўргатгандир'}, {'chapter': 96, 'verse': 5, 'text': 'عَلَّمَ ٱلۡإِنسَٰنَ مَا لَمۡ يَعۡلَمۡ\nУ, инсонга билмаган нарсасини ўргатди'}, {'chapter': 96, 'verse': 6, 'text': 'كَلَّآ إِنَّ ٱلۡإِنسَٰنَ لَيَطۡغَىٰٓ\nЙўқ! Инсон, албатта, туғёнга кетур'}, {'chapter': 96, 'verse': 7, 'text': 'أَن رَّءَاهُ ٱسۡتَغۡنَىٰٓ\nГар ўзини бой кўрса'}, {'chapter': 96, 'verse': 8, 'text': 'إِنَّ إِلَىٰ رَبِّكَ ٱلرُّجۡعَىٰٓ\nАлбатта, қайтиб бориш Роббинггадир'}, {'chapter': 96, 'verse': 9, 'text': 'أَرَءَيۡتَ ٱلَّذِي يَنۡهَىٰ\nҚайтарганни кўрдингми'}, {'chapter': 96, 'verse': 10, 'text': 'عَبۡدًا إِذَا صَلَّىٰٓ\nБандани намоз ўқиётганида'}, {'chapter': 96, 'verse': 11, 'text': 'أَرَءَيۡتَ إِن كَانَ عَلَى ٱلۡهُدَىٰٓ\nАйтинг-чи, агар ўша ҳидоятда бўлса'}, {'chapter': 96, 'verse': 12, 'text': 'أَوۡ أَمَرَ بِٱلتَّقۡوَىٰٓ\nЁки тақвога чақирса'}, {'chapter': 96, 'verse': 13, 'text': 'أَرَءَيۡتَ إِن كَذَّبَ وَتَوَلَّىٰٓ\nАйтинг-чи, агар у ёлғонга чиқарса ва юз ўгирса'}, {'chapter': 96, 'verse': 14, 'text': 'أَلَمۡ يَعۡلَم بِأَنَّ ٱللَّهَ يَرَىٰ\nАллоҳ уни кўриб туришини билмасми'}, {'chapter': 96, 'verse': 15, 'text': 'كَلَّا لَئِن لَّمۡ يَنتَهِ لَنَسۡفَعَۢا بِٱلنَّاصِيَةِ\nЙўқ! Агар қайтмаса, пешонасидан шиддат-ла тутамиз'}, {'chapter': 96, 'verse': 16, 'text': 'نَاصِيَةٖ كَٰذِبَةٍ خَاطِئَةٖ\nЁлғончи, хатокор пешонасидан'}, {'chapter': 96, 'verse': 17, 'text': 'فَلۡيَدۡعُ نَادِيَهُۥ\nБас, у ўз тўпини чақирсин'}, {'chapter': 96, 'verse': 18, 'text': 'سَنَدۡعُ ٱلزَّبَانِيَةَ\nБиз ҳам забонияларни чақирурмиз'}, {'chapter': 96, 'verse': 19, 'text': 'كَلَّا لَا تُطِعۡهُ وَٱسۡجُدۡۤ وَٱقۡتَرِب۩\nЙўқ! Сен унга итоат қилма! Сажда қил ва қурбат ҳосил қил'}], '97': [{'chapter': 97, 'verse': 1, 'text': 'إِنَّآ أَنزَلۡنَٰهُ فِي لَيۡلَةِ ٱلۡقَدۡرِ\nАлбатта, Биз У(Қуръон)ни Қадр кечасида туширдик'}, {'chapter': 97, 'verse': 2, 'text': 'وَمَآ أَدۡرَىٰكَ مَا لَيۡلَةُ ٱلۡقَدۡرِ\nЛайлатул Қадр қандоқ нарса эканини сенга нима билдирди'}, {'chapter': 97, 'verse': 3, 'text': 'لَيۡلَةُ ٱلۡقَدۡرِ خَيۡرٞ مِّنۡ أَلۡفِ شَهۡرٖ\nЛайлатул Қадр минг ойдин яхшироқдир'}, {'chapter': 97, 'verse': 4, 'text': 'تَنَزَّلُ ٱلۡمَلَـٰٓئِكَةُ وَٱلرُّوحُ فِيهَا بِإِذۡنِ رَبِّهِم مِّن كُلِّ أَمۡرٖ\nУнда фаришталар ва Руҳ Роббилари изни билан барча ишлар учун тушадир'}, {'chapter': 97, 'verse': 5, 'text': 'سَلَٰمٌ هِيَ حَتَّىٰ مَطۡلَعِ ٱلۡفَجۡرِ\nУ то тонг отгунча салом бўлиб турадир'}], '98': [{'chapter': 98, 'verse': 1, 'text': 'لَمۡ يَكُنِ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَٱلۡمُشۡرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأۡتِيَهُمُ ٱلۡبَيِّنَةُ\nАҳли китоб ва мушриклардан бўлган куфр келтирганлар «баййина» келмагунча ажралмасдилар'}, {'chapter': 98, 'verse': 2, 'text': 'رَسُولٞ مِّنَ ٱللَّهِ يَتۡلُواْ صُحُفٗا مُّطَهَّرَةٗ\nУ (баййина), Аллоҳ тарафидан юборилган Расул бўлиб, покланган саҳифаларни тиловат қиладур'}, {'chapter': 98, 'verse': 3, 'text': 'فِيهَا كُتُبٞ قَيِّمَةٞ\nУларда ажойиб битиклар бор'}, {'chapter': 98, 'verse': 4, 'text': 'وَمَا تَفَرَّقَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَةُ\nКитоб берилганлар уларга «баййина» келганидан сўнггина бўлиниб кетишди'}, {'chapter': 98, 'verse': 5, 'text': 'وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُواْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ حُنَفَآءَ وَيُقِيمُواْ ٱلصَّلَوٰةَ وَيُؤۡتُواْ ٱلزَّكَوٰةَۚ وَذَٰلِكَ دِينُ ٱلۡقَيِّمَةِ\nҲолбуки, улар фақат Аллоҳгагина ибодат қилишга, Унинг динигагина ихлос қилишга, бошқа динларга мойил бўлмасликка, намозни тўлиқ ўқишга, закот беришга буюрилган эдилар. Ана шу, тўғри(миллат)нинг динидир'}, {'chapter': 98, 'verse': 6, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَٱلۡمُشۡرِكِينَ فِي نَارِ جَهَنَّمَ خَٰلِدِينَ فِيهَآۚ أُوْلَـٰٓئِكَ هُمۡ شَرُّ ٱلۡبَرِيَّةِ\nАлбатта, аҳли китоб ва мушриклардан бўлган куфр келтирганлар жаҳаннам ўтидадурлар, у ерда абадий қолурлар. Ундайлар халойиқнинг энг ёмонидирлар'}, {'chapter': 98, 'verse': 7, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ هُمۡ خَيۡرُ ٱلۡبَرِيَّةِ\nАлбатта, иймон келтирганлар ва солиҳ амалларни қилганлар, ана ўшалар, халойиқнинг энг яхшисидир'}, {'chapter': 98, 'verse': 8, 'text': 'جَزَآؤُهُمۡ عِندَ رَبِّهِمۡ جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ لِمَنۡ خَشِيَ رَبَّهُۥ\nУларнинг мукофотлари Роббилари ҳузуридаги (дарахтлари) остидан анҳорлар оқиб турган «адн» жаннатларидир. У ерда абадул-абад қолурлар. Аллоҳ улардан рози бўлди, улар ҳам Ундан рози бўлдилар. Бу, Роббидан қўрққанлар учундир'}], '99': [{'chapter': 99, 'verse': 1, 'text': 'إِذَا زُلۡزِلَتِ ٱلۡأَرۡضُ زِلۡزَالَهَا\nВақтики, ер ўзига хос зилзилага тушса'}, {'chapter': 99, 'verse': 2, 'text': 'وَأَخۡرَجَتِ ٱلۡأَرۡضُ أَثۡقَالَهَا\nВа ер ўз юкларини отиб чиқарса'}, {'chapter': 99, 'verse': 3, 'text': 'وَقَالَ ٱلۡإِنسَٰنُ مَا لَهَا\nВа инсон: «Унга нима бўлди?!» – деса'}, {'chapter': 99, 'verse': 4, 'text': 'يَوۡمَئِذٖ تُحَدِّثُ أَخۡبَارَهَا\nЎша кунда у (ер) ўз хабарини айтадир'}, {'chapter': 99, 'verse': 5, 'text': 'بِأَنَّ رَبَّكَ أَوۡحَىٰ لَهَا\nСенинг Роббинг унга ваҳий юборгани учундир'}, {'chapter': 99, 'verse': 6, 'text': 'يَوۡمَئِذٖ يَصۡدُرُ ٱلنَّاسُ أَشۡتَاتٗا لِّيُرَوۡاْ أَعۡمَٰلَهُمۡ\nЎша кунда одамлар амалларини кўриш учун жойларидан гуруҳ-гуруҳ бўлиб қўзғалурлар'}, {'chapter': 99, 'verse': 7, 'text': 'فَمَن يَعۡمَلۡ مِثۡقَالَ ذَرَّةٍ خَيۡرٗا يَرَهُۥ\nБас, ким зарра оғирлигида яхшилик қилса ҳам кўрадир'}, {'chapter': 99, 'verse': 8, 'text': 'وَمَن يَعۡمَلۡ مِثۡقَالَ ذَرَّةٖ شَرّٗا يَرَهُۥ\nВа ким зарра оғирлигида ёмонлик қилса ҳам кўрадир'}], '100': [{'chapter': 100, 'verse': 1, 'text': 'وَٱلۡعَٰدِيَٰتِ ضَبۡحٗا\nҲарсиллаб чопувчи отлар билан қасам'}, {'chapter': 100, 'verse': 2, 'text': 'فَٱلۡمُورِيَٰتِ قَدۡحٗا\nЎт чақнатиб чопувчи отлар билан қасам'}, {'chapter': 100, 'verse': 3, 'text': 'فَٱلۡمُغِيرَٰتِ صُبۡحٗا\nТонг чоғида ғорат қилувчи отлар билан қасам'}, {'chapter': 100, 'verse': 4, 'text': 'فَأَثَرۡنَ بِهِۦ نَقۡعٗا\nЎша ила чанг қўзғатдилар'}, {'chapter': 100, 'verse': 5, 'text': 'فَوَسَطۡنَ بِهِۦ جَمۡعًا\nВа у ила жамоа ўртасига (ёриб) кирдилар'}, {'chapter': 100, 'verse': 6, 'text': 'إِنَّ ٱلۡإِنسَٰنَ لِرَبِّهِۦ لَكَنُودٞ\nАлбатта, инсон ўз Роббига ношукрдир'}, {'chapter': 100, 'verse': 7, 'text': 'وَإِنَّهُۥ عَلَىٰ ذَٰلِكَ لَشَهِيدٞ\nВа албатта, у ана шунга ўзи гувоҳдир'}, {'chapter': 100, 'verse': 8, 'text': 'وَإِنَّهُۥ لِحُبِّ ٱلۡخَيۡرِ لَشَدِيدٌ\nАлбатта, у мол-дунёга ҳаддан ташқари ўчдир'}, {'chapter': 100, 'verse': 9, 'text': '۞أَفَلَا يَعۡلَمُ إِذَا بُعۡثِرَ مَا فِي ٱلۡقُبُورِ\nУ билмасми?! Қабрдагилар қўзғатилганда'}, {'chapter': 100, 'verse': 10, 'text': 'وَحُصِّلَ مَا فِي ٱلصُّدُورِ\nВа кўкракларидаги ошкор бўлганда'}, {'chapter': 100, 'verse': 11, 'text': 'إِنَّ رَبَّهُم بِهِمۡ يَوۡمَئِذٖ لَّخَبِيرُۢ\nЎша кунда Роббилари улардан хабардор эканини'}], '101': [{'chapter': 101, 'verse': 1, 'text': 'ٱلۡقَارِعَةُ\nАл-Қориъа'}, {'chapter': 101, 'verse': 2, 'text': 'مَا ٱلۡقَارِعَةُ\nАл-Қориъа нима'}, {'chapter': 101, 'verse': 3, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡقَارِعَةُ\nАл-Қориъа қандоқ нарса эканлигини сенга нима билдирди'}, {'chapter': 101, 'verse': 4, 'text': 'يَوۡمَ يَكُونُ ٱلنَّاسُ كَٱلۡفَرَاشِ ٱلۡمَبۡثُوثِ\nУ кунда одамлар тўзиб кетган капалакка ўхшаб қоларлар'}, {'chapter': 101, 'verse': 5, 'text': 'وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ ٱلۡمَنفُوشِ\nВа тоғлар титилган жунга ўхшаб қоларлар'}, {'chapter': 101, 'verse': 6, 'text': 'فَأَمَّا مَن ثَقُلَتۡ مَوَٰزِينُهُۥ\nКимнинг тарозуси оғир келса'}, {'chapter': 101, 'verse': 7, 'text': 'فَهُوَ فِي عِيشَةٖ رَّاضِيَةٖ\nБас, у розилик ҳаётидадир'}, {'chapter': 101, 'verse': 8, 'text': 'وَأَمَّا مَنۡ خَفَّتۡ مَوَٰزِينُهُۥ\nВа аммо кимнинг тарозуси енгил келса'}, {'chapter': 101, 'verse': 9, 'text': 'فَأُمُّهُۥ هَاوِيَةٞ\nБас, унинг онаси ҳаавия (дўзах)дир'}, {'chapter': 101, 'verse': 10, 'text': 'وَمَآ أَدۡرَىٰكَ مَا هِيَهۡ\nУ (Ҳаавия) қандоқ нарса эканлигини сенга нима билдирди'}, {'chapter': 101, 'verse': 11, 'text': 'نَارٌ حَامِيَةُۢ\nУ қаттиқ қизиган ўтдир'}], '102': [{'chapter': 102, 'verse': 1, 'text': 'أَلۡهَىٰكُمُ ٱلتَّكَاثُرُ\n«Кимники кўп» сизни машғул қилди'}, {'chapter': 102, 'verse': 2, 'text': 'حَتَّىٰ زُرۡتُمُ ٱلۡمَقَابِرَ\nТо қабрларни зиёрат қилгунингизча'}, {'chapter': 102, 'verse': 3, 'text': 'كَلَّا سَوۡفَ تَعۡلَمُونَ\nЙўқ! Сизлар тезда биласиз'}, {'chapter': 102, 'verse': 4, 'text': 'ثُمَّ كَلَّا سَوۡفَ تَعۡلَمُونَ\nЯна йўқ! Сизлар тезда биласиз'}, {'chapter': 102, 'verse': 5, 'text': 'كَلَّا لَوۡ تَعۡلَمُونَ عِلۡمَ ٱلۡيَقِينِ\nЙўқ! Агар Сиз аниқ илм ила билганингизда эди'}, {'chapter': 102, 'verse': 6, 'text': 'لَتَرَوُنَّ ٱلۡجَحِيمَ\nАлбатта, жаҳаннамни кўрасиз'}, {'chapter': 102, 'verse': 7, 'text': 'ثُمَّ لَتَرَوُنَّهَا عَيۡنَ ٱلۡيَقِينِ\nВа яна, албатта, уни ишонч кўзи билан кўрасиз'}, {'chapter': 102, 'verse': 8, 'text': 'ثُمَّ لَتُسۡـَٔلُنَّ يَوۡمَئِذٍ عَنِ ٱلنَّعِيمِ\nСўнгра, ўша кунда, албатта, берилган неъматлардан сўраласиз'}], '103': [{'chapter': 103, 'verse': 1, 'text': 'وَٱلۡعَصۡرِ\nАср билан қасам'}, {'chapter': 103, 'verse': 2, 'text': 'إِنَّ ٱلۡإِنسَٰنَ لَفِي خُسۡرٍ\nАлбатта, инсон хусрондадир'}, {'chapter': 103, 'verse': 3, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَتَوَاصَوۡاْ بِٱلۡحَقِّ وَتَوَاصَوۡاْ بِٱلصَّبۡرِ\nИлло, иймон келтирганлар ва солиҳ амаллар қилганлар, бир-бирларини ҳақ йўлга чақирганлар ва бир-бирларини сабрга чақирганлар (ундоқ эмасдир)'}], '104': [{'chapter': 104, 'verse': 1, 'text': 'وَيۡلٞ لِّكُلِّ هُمَزَةٖ لُّمَزَةٍ\nҲар бир обрў тўкувчи ва айбловчига «вайл» бўлсин'}, {'chapter': 104, 'verse': 2, 'text': 'ٱلَّذِي جَمَعَ مَالٗا وَعَدَّدَهُۥ\nУ мол жамлади ва уни санаб турур'}, {'chapter': 104, 'verse': 3, 'text': 'يَحۡسَبُ أَنَّ مَالَهُۥٓ أَخۡلَدَهُۥ\nУ, албатта, моли уни абадий қолдирур деб ҳисоб қилур'}, {'chapter': 104, 'verse': 4, 'text': 'كَلَّاۖ لَيُنۢبَذَنَّ فِي ٱلۡحُطَمَةِ\nЙўқ! Албатта, у «ҳутома»га хор-зор этиб ташланур'}, {'chapter': 104, 'verse': 5, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡحُطَمَةُ\n«Ҳутома» қандоқ нарса эканини сенга нима билдирур'}, {'chapter': 104, 'verse': 6, 'text': 'نَارُ ٱللَّهِ ٱلۡمُوقَدَةُ\nУ, Аллоҳнинг шиддатли, ўчмас оловидир'}, {'chapter': 104, 'verse': 7, 'text': 'ٱلَّتِي تَطَّلِعُ عَلَى ٱلۡأَفۡـِٔدَةِ\nҚалбларгача етиб борадигандур'}, {'chapter': 104, 'verse': 8, 'text': 'إِنَّهَا عَلَيۡهِم مُّؤۡصَدَةٞ\nАлбатта, у уларнинг устидан қоплангандир'}, {'chapter': 104, 'verse': 9, 'text': 'فِي عَمَدٖ مُّمَدَّدَةِۭ\nУзун-узун устунларга боғлангандир'}], '105': [{'chapter': 105, 'verse': 1, 'text': 'أَلَمۡ تَرَ كَيۡفَ فَعَلَ رَبُّكَ بِأَصۡحَٰبِ ٱلۡفِيلِ\nРоббинг фийл соҳибларини нима қилганини билмадингми'}, {'chapter': 105, 'verse': 2, 'text': 'أَلَمۡ يَجۡعَلۡ كَيۡدَهُمۡ فِي تَضۡلِيلٖ\nУларнинг макру ҳийласини зое кетказмадими'}, {'chapter': 105, 'verse': 3, 'text': 'وَأَرۡسَلَ عَلَيۡهِمۡ طَيۡرًا أَبَابِيلَ\nВа уларнинг устига тўп-тўп қушларни юбормадими'}, {'chapter': 105, 'verse': 4, 'text': 'تَرۡمِيهِم بِحِجَارَةٖ مِّن سِجِّيلٖ\nЛойдан пиширилган тошларни отадиган'}, {'chapter': 105, 'verse': 5, 'text': 'فَجَعَلَهُمۡ كَعَصۡفٖ مَّأۡكُولِۭ\nБас, уларни қурт еб ташлаган ўсимликка ўхшатиб юбормадими'}], '106': [{'chapter': 106, 'verse': 1, 'text': 'لِإِيلَٰفِ قُرَيۡشٍ\nҚурайшнинг лозим тутиши учун'}, {'chapter': 106, 'verse': 2, 'text': 'إِۦلَٰفِهِمۡ رِحۡلَةَ ٱلشِّتَآءِ وَٱلصَّيۡفِ\nУларнинг қиш ва ёз сафарларини лозим тутиши учун'}, {'chapter': 106, 'verse': 3, 'text': 'فَلۡيَعۡبُدُواْ رَبَّ هَٰذَا ٱلۡبَيۡتِ\nБас, улар мана шу Байтнинг Роббига ибодат қилсинлар'}, {'chapter': 106, 'verse': 4, 'text': 'ٱلَّذِيٓ أَطۡعَمَهُم مِّن جُوعٖ وَءَامَنَهُم مِّنۡ خَوۡفِۭ\nУ уларни очликдан тўқ қилган, хавфдан омон қилгандир'}], '107': [{'chapter': 107, 'verse': 1, 'text': 'أَرَءَيۡتَ ٱلَّذِي يُكَذِّبُ بِٱلدِّينِ\nОхират(жазоси)ни ёлғонга чиқарувчини кўрдингми'}, {'chapter': 107, 'verse': 2, 'text': 'فَذَٰلِكَ ٱلَّذِي يَدُعُّ ٱلۡيَتِيمَ\nУ, етимни қўполлик билан ҳайдайдир'}, {'chapter': 107, 'verse': 3, 'text': 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ\nВа мискинларга таом беришга қизиқтирмайдир'}, {'chapter': 107, 'verse': 4, 'text': 'فَوَيۡلٞ لِّلۡمُصَلِّينَ\nБир «намозхонлар»га вайл бўлсинки'}, {'chapter': 107, 'verse': 5, 'text': 'ٱلَّذِينَ هُمۡ عَن صَلَاتِهِمۡ سَاهُونَ\nУлар намозларини унутувчилардир'}, {'chapter': 107, 'verse': 6, 'text': 'ٱلَّذِينَ هُمۡ يُرَآءُونَ\nУндоқ кишилар риё қиладиганлардир'}, {'chapter': 107, 'verse': 7, 'text': 'وَيَمۡنَعُونَ ٱلۡمَاعُونَ\nВа мааъуунни ман қиладиганлардир'}], '108': [{'chapter': 108, 'verse': 1, 'text': 'إِنَّآ أَعۡطَيۡنَٰكَ ٱلۡكَوۡثَرَ\nАлбатта, Биз сенга Кавсарни бердик'}, {'chapter': 108, 'verse': 2, 'text': 'فَصَلِّ لِرَبِّكَ وَٱنۡحَرۡ\nБас, Роббингга намоз ўқи ва жонлиқ сўй'}, {'chapter': 108, 'verse': 3, 'text': 'إِنَّ شَانِئَكَ هُوَ ٱلۡأَبۡتَرُ\nАлбатта, сени ёмон кўриб айбловчининг ўзининг орқаси кесик'}], '109': [{'chapter': 109, 'verse': 1, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلۡكَٰفِرُونَ\nАйт: «Эй, кофирлар'}, {'chapter': 109, 'verse': 2, 'text': 'لَآ أَعۡبُدُ مَا تَعۡبُدُونَ\nМен сиз ибодат қилган нарсаларга ибодат қилмасман'}, {'chapter': 109, 'verse': 3, 'text': 'وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ\nВа сизлар ҳам мен ибодат қиладиган нарсага ибодат қилувчимассиз'}, {'chapter': 109, 'verse': 4, 'text': 'وَلَآ أَنَا۠ عَابِدٞ مَّا عَبَدتُّمۡ\nВа мен сиз ибодат қилган нарсага ибодат қилувчимасман'}, {'chapter': 109, 'verse': 5, 'text': 'وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ\nВа сиз ҳам мен ибодат қиладиган нарсага ибодат қилувчимассиз'}, {'chapter': 109, 'verse': 6, 'text': 'لَكُمۡ دِينُكُمۡ وَلِيَ دِينِ\nСизга ўз динингиз, Менга ўз диним'}], '110': [{'chapter': 110, 'verse': 1, 'text': 'إِذَا جَآءَ نَصۡرُ ٱللَّهِ وَٱلۡفَتۡحُ\nВақтики, Аллоҳнинг нусрати ва фатҳ келса'}, {'chapter': 110, 'verse': 2, 'text': 'وَرَأَيۡتَ ٱلنَّاسَ يَدۡخُلُونَ فِي دِينِ ٱللَّهِ أَفۡوَاجٗا\nВа одамларнинг Аллоҳнинг динига тўп-тўп бўлиб кираётганини кўрсанг'}, {'chapter': 110, 'verse': 3, 'text': 'فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَٱسۡتَغۡفِرۡهُۚ إِنَّهُۥ كَانَ تَوَّابَۢا\nБас, Роббингни поклаб ёд эт ва Унга истиғфор айт. Албатта, У тавбаларни кўплаб қабул этувчидир'}], '111': [{'chapter': 111, 'verse': 1, 'text': 'تَبَّتۡ يَدَآ أَبِي لَهَبٖ وَتَبَّ\nАбу Лаҳабнинг икки қўли ҳалок бўлсин, ҳалок'}, {'chapter': 111, 'verse': 2, 'text': 'مَآ أَغۡنَىٰ عَنۡهُ مَالُهُۥ وَمَا كَسَبَ\nУнга моли ва касб қилган нарсалари фойда бермади'}, {'chapter': 111, 'verse': 3, 'text': 'سَيَصۡلَىٰ نَارٗا ذَاتَ لَهَبٖ\nУ тезда чўғи қизиб турган ўтга кирадир'}, {'chapter': 111, 'verse': 4, 'text': 'وَٱمۡرَأَتُهُۥ حَمَّالَةَ ٱلۡحَطَبِ\nВа унинг ўтин кўтарган хотини ҳам'}, {'chapter': 111, 'verse': 5, 'text': 'فِي جِيدِهَا حَبۡلٞ مِّن مَّسَدِۭ\nУнинг бўйнида эшилган арқон'}], '112': [{'chapter': 112, 'verse': 1, 'text': 'قُلۡ هُوَ ٱللَّهُ أَحَدٌ\nАйт: «У Аллоҳ ягонадир'}, {'chapter': 112, 'verse': 2, 'text': 'ٱللَّهُ ٱلصَّمَدُ\nАллоҳ — Cомаддир'}, {'chapter': 112, 'verse': 3, 'text': 'لَمۡ يَلِدۡ وَلَمۡ يُولَدۡ\nУ туғмаган ва туғилмаган'}, {'chapter': 112, 'verse': 4, 'text': 'وَلَمۡ يَكُن لَّهُۥ كُفُوًا أَحَدُۢ\nВа Унга ҳеч ким тенг бўлмаган»'}], '113': [{'chapter': 113, 'verse': 1, 'text': 'قُلۡ أَعُوذُ بِرَبِّ ٱلۡفَلَقِ\n«Тонг Роббисидан паноҳ сўрайман'}, {'chapter': 113, 'verse': 2, 'text': 'مِن شَرِّ مَا خَلَقَ\nУ яратган нарсалар ёмонлигидан'}, {'chapter': 113, 'verse': 3, 'text': 'وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ\nВа кириб келган қоронғу кечанинг ёмонлигидан'}, {'chapter': 113, 'verse': 4, 'text': 'وَمِن شَرِّ ٱلنَّفَّـٰثَٰتِ فِي ٱلۡعُقَدِ\nВа тугунларга дам солувчилар ёмонлигидан'}, {'chapter': 113, 'verse': 5, 'text': 'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ\nВа ҳасад қилган ҳасадчининг ёмонлигидан», деб айт'}], '114': [{'chapter': 114, 'verse': 1, 'text': 'قُلۡ أَعُوذُ بِرَبِّ ٱلنَّاسِ\n«Одамлар Роббисидан'}, {'chapter': 114, 'verse': 2, 'text': 'مَلِكِ ٱلنَّاسِ\nОдамлар подшоҳидан'}, {'chapter': 114, 'verse': 3, 'text': 'إِلَٰهِ ٱلنَّاسِ\nОдамлар Илоҳидан паноҳ сўрайман'}, {'chapter': 114, 'verse': 4, 'text': 'مِن شَرِّ ٱلۡوَسۡوَاسِ ٱلۡخَنَّاسِ\nБеркиниб, кўриниб турувчи васвасачининг'}, {'chapter': 114, 'verse': 5, 'text': 'ٱلَّذِي يُوَسۡوِسُ فِي صُدُورِ ٱلنَّاسِ\nОдамлар қалбларига васваса соладиганнинг'}, {'chapter': 114, 'verse': 6, 'text': 'مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ\nЖинлар ва одамлардан бўлганнинг ёмонлигидан», деб айт'}]}