quranlist2 = [{'chapter': 1, 'verse': 1, 'text': 'بِسۡمِ ٱللَّهِ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\nМеҳрибон ва раҳмли Аллоҳ номи билан (бошлайман)'}, {'chapter': 1, 'verse': 2, 'text': 'ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nҲамду сано бутун оламлар Парвардигори Аллоҳ учундир'}, {'chapter': 1, 'verse': 3, 'text': 'ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\nМеҳрибон ва Раҳмли'}, {'chapter': 1, 'verse': 4, 'text': 'مَٰلِكِ يَوۡمِ ٱلدِّينِ\nЖазо (Қиёмат) кунининг Эгаси — Подшоҳи'}, {'chapter': 1, 'verse': 5, 'text': 'إِيَّاكَ نَعۡبُدُ وَإِيَّاكَ نَسۡتَعِينُ\nСенгагина ибодат қиламиз ва Сендангина мадад сўраймиз'}, {'chapter': 1, 'verse': 6, 'text': 'ٱهۡدِنَا ٱلصِّرَٰطَ ٱلۡمُسۡتَقِيمَ\nБизларни Тўғри Йўлга йўллагайсан'}, {'chapter': 1, 'verse': 7, 'text': 'صِرَٰطَ ٱلَّذِينَ أَنۡعَمۡتَ عَلَيۡهِمۡ غَيۡرِ ٱلۡمَغۡضُوبِ عَلَيۡهِمۡ وَلَا ٱلضَّآلِّينَ\nУ шундай зотларнинг йўлики, Сен Ўзинг уларга инъом қилгансан — улар ғазабга дучор бўлмаган ва залолатга кетмаган — адашмаганларнинг йўлидир'}, {'chapter': 2, 'verse': 1, 'text': 'الٓمٓ\nАлиф, лом, мим'}, {'chapter': 2, 'verse': 2, 'text': 'ذَٰلِكَ ٱلۡكِتَٰبُ لَا رَيۡبَۛ فِيهِۛ هُدٗى لِّلۡمُتَّقِينَ\n(Ҳақ эканлигига) ҳеч қандай шак-шубҳа бўлмаган ушбу Китоб тақволи кишилар учун раҳбар – йўлбошчидир'}, {'chapter': 2, 'verse': 3, 'text': 'ٱلَّذِينَ يُؤۡمِنُونَ بِٱلۡغَيۡبِ وَيُقِيمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар ғойибга ишонадиган, намозни тўкис адо этадиган ва биз ризқ қилиб берган нарсалардан инфоқ-эҳсон қиладиган зотлардир'}, {'chapter': 2, 'verse': 4, 'text': 'وَٱلَّذِينَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ وَبِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ\nУлар сизга нозил қилинган ва сиздан илгари туширилган нарсаларга (динларга) иймон келтирадилар ва Охират кунига аниқ ишонадилар'}, {'chapter': 2, 'verse': 5, 'text': 'أُوْلَـٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nПарвардигорлари тарафидан ҳидоят топганлар ана ўшалардир ва нажот топгувчилар ҳам уларнинг ўзидир'}, {'chapter': 2, 'verse': 6, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ سَوَآءٌ عَلَيۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ\nКуфр йўлини тутган кимсалар эса хоҳ (Аллоҳ азобидан) қўрқитинг, хоҳ қўрқитманг, уларга баробардир — иймон келтирмайдилар'}, {'chapter': 2, 'verse': 7, 'text': 'خَتَمَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَعَلَىٰ سَمۡعِهِمۡۖ وَعَلَىٰٓ أَبۡصَٰرِهِمۡ غِشَٰوَةٞۖ وَلَهُمۡ عَذَابٌ عَظِيمٞ\nАллоҳ уларнинг дилларини ва қулоқларини муҳрлаб қўйган. Кўзларини эса парда қоплаб олган. Улар учун буюк азоб бордир'}, {'chapter': 2, 'verse': 8, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ وَبِٱلۡيَوۡمِ ٱلۡأٓخِرِ وَمَا هُم بِمُؤۡمِنِينَ\nОдамлар орасида шундай кимсалар ҳам борки, ўзлари мўмин бўлмаганлари ҳолда «Аллоҳга ва Охират кунига иймон келтирдик», дейдилар'}, {'chapter': 2, 'verse': 9, 'text': 'يُخَٰدِعُونَ ٱللَّهَ وَٱلَّذِينَ ءَامَنُواْ وَمَا يَخۡدَعُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ\nУлар Аллоҳни ва иймонли кишиларни алдамоқчи бўладилар ва ўзлари сезмаганлари ҳолда фақат ўзларинигина алдайдилар'}, {'chapter': 2, 'verse': 10, 'text': 'فِي قُلُوبِهِم مَّرَضٞ فَزَادَهُمُ ٱللَّهُ مَرَضٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡذِبُونَ\nУларнинг дилларида мараз бор эди, бас Аллоҳ маразларини янада зиёда қилди. Улар учун қилган ёлғонлари сабаб аламли азоб бордир'}, {'chapter': 2, 'verse': 11, 'text': 'وَإِذَا قِيلَ لَهُمۡ لَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ قَالُوٓاْ إِنَّمَا نَحۡنُ مُصۡلِحُونَ\nУларга: «Ер юзида бузғунчилик қилманглар», дейилса, «Биз ислоҳ қилувчилармиз», дейдилар'}, {'chapter': 2, 'verse': 12, 'text': 'أَلَآ إِنَّهُمۡ هُمُ ٱلۡمُفۡسِدُونَ وَلَٰكِن لَّا يَشۡعُرُونَ\nОгоҳ бўлингизким, улар албатта бузғунчилардир, лекин ўзлари буни сезмайдилар'}, {'chapter': 2, 'verse': 13, 'text': 'وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ كَمَآ ءَامَنَ ٱلنَّاسُ قَالُوٓاْ أَنُؤۡمِنُ كَمَآ ءَامَنَ ٱلسُّفَهَآءُۗ أَلَآ إِنَّهُمۡ هُمُ ٱلسُّفَهَآءُ وَلَٰكِن لَّا يَعۡلَمُونَ\nУларга: «Мана бу кишилардек иймон келтиринглар», дейилса, «Шу пасткашларга ўхшаб мўмин бўламизми?», дейдилар. Огоҳ бўлингизким, уларнинг ўзлари тубан кимсалардир, лекин буни билмайдилар'}, {'chapter': 2, 'verse': 14, 'text': 'وَإِذَا لَقُواْ ٱلَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ إِلَىٰ شَيَٰطِينِهِمۡ قَالُوٓاْ إِنَّا مَعَكُمۡ إِنَّمَا نَحۡنُ مُسۡتَهۡزِءُونَ\nИймон келтирган зотларга йўлиққанларида: «Биз ҳам иймон келтирдик», дейдилар. Ўзларининг шайтонлари (бошлиқлари) билан холи қолганда эса: «Биз албатта сизлар билан биргамиз, фақат (уларнинг устидан) кулмоқдамиз, холос», дейишади'}, {'chapter': 2, 'verse': 15, 'text': 'ٱللَّهُ يَسۡتَهۡزِئُ بِهِمۡ وَيَمُدُّهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАллоҳ уларнинг устидан кулади ва ўз туғёнларида адашиб-улоқиб юришларини давомли қилади'}, {'chapter': 2, 'verse': 16, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلضَّلَٰلَةَ بِٱلۡهُدَىٰ فَمَا رَبِحَت تِّجَٰرَتُهُمۡ وَمَا كَانُواْ مُهۡتَدِينَ\nУлар ҳақ йўлнинг ўрнига залолатни сотиб олган кимсалар бўлиб, бу савдоларида фойда қилмадилар ва Тўғри Йўлга юрувчилардан бўлмадилар'}, {'chapter': 2, 'verse': 17, 'text': 'مَثَلُهُمۡ كَمَثَلِ ٱلَّذِي ٱسۡتَوۡقَدَ نَارٗا فَلَمَّآ أَضَآءَتۡ مَا حَوۡلَهُۥ ذَهَبَ ٱللَّهُ بِنُورِهِمۡ وَتَرَكَهُمۡ فِي ظُلُمَٰتٖ لَّا يُبۡصِرُونَ\nУлар мисоли бир ўт (машъала) ёқувчига ўхшайдилар. Энди олов атрофини ёритганида Аллоҳ ёруғликни кетказиб, уларни ҳеч нарсани кўролмайдиган ҳолда зулматларда қолдиради'}, {'chapter': 2, 'verse': 18, 'text': 'صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَرۡجِعُونَ\n(Улар) кар, соқов, кўрдурлар, демак (йўлларидан) қайтмайдилар'}, {'chapter': 2, 'verse': 19, 'text': 'أَوۡ كَصَيِّبٖ مِّنَ ٱلسَّمَآءِ فِيهِ ظُلُمَٰتٞ وَرَعۡدٞ وَبَرۡقٞ يَجۡعَلُونَ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِم مِّنَ ٱلصَّوَٰعِقِ حَذَرَ ٱلۡمَوۡتِۚ وَٱللَّهُ مُحِيطُۢ بِٱلۡكَٰفِرِينَ\nЁки улар чақмоқ, момогулдурак, қоронғуликлар билан осмондан қуяётган ёмғир остида яшиндан қўрққанларидан панжаларини қулоқларига тиқиб олган ҳолларида ўлим — ҳалокатдан қочмоқчи бўлиб турган кимсалар кабидирлар. Аллоҳ эса кофирларни ўраб олгувчидир'}, {'chapter': 2, 'verse': 20, 'text': 'يَكَادُ ٱلۡبَرۡقُ يَخۡطَفُ أَبۡصَٰرَهُمۡۖ كُلَّمَآ أَضَآءَ لَهُم مَّشَوۡاْ فِيهِ وَإِذَآ أَظۡلَمَ عَلَيۡهِمۡ قَامُواْۚ وَلَوۡ شَآءَ ٱللَّهُ لَذَهَبَ بِسَمۡعِهِمۡ وَأَبۡصَٰرِهِمۡۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЯшин кўзларини кўр қилгудек бўлади. У (атрофини) ёритганида юриб оладилар, ўчганида туриб қоладилар. Агар Аллоҳ хоҳласа қулоқ ва кўзларини йўқ қилган бўлур эди. Шубҳасиз, Аллоҳ ҳар нарсага қодирдир'}, {'chapter': 2, 'verse': 21, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱعۡبُدُواْ رَبَّكُمُ ٱلَّذِي خَلَقَكُمۡ وَٱلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ\nЭй инсонлар, сизларни ва сизлардан илгари ўтганларни тақво эгалари бўлишингиз учун яратган Парвардигорингизга ибодат қилингиз'}, {'chapter': 2, 'verse': 22, 'text': 'ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ فِرَٰشٗا وَٱلسَّمَآءَ بِنَآءٗ وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ فَلَا تَجۡعَلُواْ لِلَّهِ أَندَادٗا وَأَنتُمۡ تَعۡلَمُونَ\nУ зот сизлар учун Ерни қароргоҳ, осмонни том қилиб қўйди ва осмондан сув тушириб, унинг ёрдамида сизларга ризқ бўлсин, деб мевалар чиқарди. Бас, билиб туриб ўзгаларни Аллоҳга тенглаштирманг'}, {'chapter': 2, 'verse': 23, 'text': 'وَإِن كُنتُمۡ فِي رَيۡبٖ مِّمَّا نَزَّلۡنَا عَلَىٰ عَبۡدِنَا فَأۡتُواْ بِسُورَةٖ مِّن مِّثۡلِهِۦ وَٱدۡعُواْ شُهَدَآءَكُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ\nАгар биз бандамизга туширган нарсадан (Қуръондан) шак-шубҳада бўлсангиз, у ҳолда шунга ўхшаган биргина сура келтиринг ва Аллоҳдан ўзга гувоҳларингизни чақиринг – агар ростгўй бўлсангиз'}, {'chapter': 2, 'verse': 24, 'text': 'فَإِن لَّمۡ تَفۡعَلُواْ وَلَن تَفۡعَلُواْ فَٱتَّقُواْ ٱلنَّارَ ٱلَّتِي وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُۖ أُعِدَّتۡ لِلۡكَٰفِرِينَ\nЭнди агар бундай қилолмасангиз — ҳаргиз қилолмайсиз ҳам — у ҳолда одамлар ва тошлар ўтин бўладиган, кофирлар учун тайёрлаб қўйилган дўзахдан қўрқинг'}, {'chapter': 2, 'verse': 25, 'text': 'وَبَشِّرِ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ كُلَّمَا رُزِقُواْ مِنۡهَا مِن ثَمَرَةٖ رِّزۡقٗا قَالُواْ هَٰذَا ٱلَّذِي رُزِقۡنَا مِن قَبۡلُۖ وَأُتُواْ بِهِۦ مُتَشَٰبِهٗاۖ وَلَهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَهُمۡ فِيهَا خَٰلِدُونَ\nИймон келтириб, яхши амаллар қилган зотларга хушхабар берингки, улар учун остларидан дарёлар оқиб турувчи боғлар бор. Қачон ўша боғларнинг бирор мевасидан баҳраманд бўлсалар, «Илгари татиб кўрган нарсамиз-ку», дейишади. Зеро уларга сурати бир-бирига ўхшаш мевалар берилади. Ва улар учун жаннатда покиза жуфтлар бордир. У зотлар жаннатда абадий қолажаклар'}, {'chapter': 2, 'verse': 26, 'text': '۞إِنَّ ٱللَّهَ لَا يَسۡتَحۡيِۦٓ أَن يَضۡرِبَ مَثَلٗا مَّا بَعُوضَةٗ فَمَا فَوۡقَهَاۚ فَأَمَّا ٱلَّذِينَ ءَامَنُواْ فَيَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۖ وَأَمَّا ٱلَّذِينَ كَفَرُواْ فَيَقُولُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلٗاۘ يُضِلُّ بِهِۦ كَثِيرٗا وَيَهۡدِي بِهِۦ كَثِيرٗاۚ وَمَا يُضِلُّ بِهِۦٓ إِلَّا ٱلۡفَٰسِقِينَ\nАллоҳ чивин ёки ундан-да ҳақир нарсалар ҳақида масал айтишдан ҳеч тортинмайди. Иймонли кишилар унинг (масалнинг) ҳақиқатан Парвардигорлари тарафидан эканини биладилар. Кофирлар эса: «Буни мисол қилиш билан Аллоҳ нима демоқчи?», дейдилар. Бу масал сабаб (Аллоҳ) кўпларни адаштиради ва кўпларни ҳақ йўлга ҳидоят қилади. Бу масал сабаб фақат фосиқ кимсаларнигина адаштиради'}, {'chapter': 2, 'verse': 27, 'text': 'ٱلَّذِينَ يَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي ٱلۡأَرۡضِۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nУлар Аллоҳга берган аҳдни мустаҳкам боғлангандан сўнг бузадиган, Аллоҳ муҳофаза қилишни буюрган боғларни узадиган кимсалардир. Ана ўшалар, ҳақиқий зиён кўргувчилардир'}, {'chapter': 2, 'verse': 28, 'text': 'كَيۡفَ تَكۡفُرُونَ بِٱللَّهِ وَكُنتُمۡ أَمۡوَٰتٗا فَأَحۡيَٰكُمۡۖ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ\nАллоҳни қандай инкор қиласиз? Ҳолбуки, У (дунёга келмасингиздан олдин) жонсиз эдингиз сизга жон ато этди, сўнгра сизни ўлдириб, яна қайта тирилтиради ва ниҳоят яна Унга қайтажаксизлар'}, {'chapter': 2, 'verse': 29, 'text': 'هُوَ ٱلَّذِي خَلَقَ لَكُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا ثُمَّ ٱسۡتَوَىٰٓ إِلَى ٱلسَّمَآءِ فَسَوَّىٰهُنَّ سَبۡعَ سَمَٰوَٰتٖۚ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nУ шундай зотки, сиз учун Ердаги барча нарсани яратди. Сўнгра самога юзланди-да, уларни етти осмон қилиб тиклади. У ҳамма нарсани билгувчидир'}, {'chapter': 2, 'verse': 30, 'text': 'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي جَاعِلٞ فِي ٱلۡأَرۡضِ خَلِيفَةٗۖ قَالُوٓاْ أَتَجۡعَلُ فِيهَا مَن يُفۡسِدُ فِيهَا وَيَسۡفِكُ ٱلدِّمَآءَ وَنَحۡنُ نُسَبِّحُ بِحَمۡدِكَ وَنُقَدِّسُ لَكَۖ قَالَ إِنِّيٓ أَعۡلَمُ مَا لَا تَعۡلَمُونَ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиз фаришталарга: «Мен Ерда (Одамни) халифа қилмоқчиман», деганида улар айтдилар: «У Ерда бузғунчилик қиладиган, қонлар тўкадиган кимсани (халифа) қиласанми? Ҳолбуки, биз ҳамду сано айтиш билан Сени улуғлаймиз ва Сенинг номингни мудом пок тутамиз». (Аллоҳ) айтди: «Албатта Мен сизлар билмаган нарсаларни биламан»'}, {'chapter': 2, 'verse': 31, 'text': 'وَعَلَّمَ ءَادَمَ ٱلۡأَسۡمَآءَ كُلَّهَا ثُمَّ عَرَضَهُمۡ عَلَى ٱلۡمَلَـٰٓئِكَةِ فَقَالَ أَنۢبِـُٔونِي بِأَسۡمَآءِ هَـٰٓؤُلَآءِ إِن كُنتُمۡ صَٰدِقِينَ\nВа У зот Одамга барча нарсаларнинг исмларини ўргатди. Сўнгра уларни фаришталарга рўбарў қилиб деди: «Агар халифаликка биз ҳақдормиз, деган сўзларингиз рост бўлса, мана бу нарсаларнинг исмларини Менга билдиринг!»'}, {'chapter': 2, 'verse': 32, 'text': 'قَالُواْ سُبۡحَٰنَكَ لَا عِلۡمَ لَنَآ إِلَّا مَا عَلَّمۡتَنَآۖ إِنَّكَ أَنتَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\nУлар айтдилар: «Эй пок Парвардигор, биз фақат Сен билдирган нарсаларнигина биламиз. Албатта, Сен ўзинг илму ҳикмат соҳибисан»'}, {'chapter': 2, 'verse': 33, 'text': 'قَالَ يَـٰٓـَٔادَمُ أَنۢبِئۡهُم بِأَسۡمَآئِهِمۡۖ فَلَمَّآ أَنۢبَأَهُم بِأَسۡمَآئِهِمۡ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيٓ أَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَأَعۡلَمُ مَا تُبۡدُونَ وَمَا كُنتُمۡ تَكۡتُمُونَ\n(Аллоҳ); «Эй Одам, буларга у нарсаларнинг исмларини билдир», деди. (Одам) уларга барча нарсаларнинг исмларини билдирганидан кейин (Аллоҳ) айтди: «Сизларга, Мен Еру-осмонларнинг сирларини ва сизлар ошкор қилган ва яширган нарсаларни биламан, демаганмидим?»'}, {'chapter': 2, 'verse': 34, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ وَٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Биз фаришталарга Одамга таъзим қилинг дейишимиз билан саждага эгилдилар. Фақат Иблис кибр ва ор қилиб — кофирлардан бўлди'}, {'chapter': 2, 'verse': 35, 'text': 'وَقُلۡنَا يَـٰٓـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ وَكُلَا مِنۡهَا رَغَدًا حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِينَ\nВа айтдик: «Эй Одам, сиз жуфтингиз билан жаннатни маскан тутинг ва ундан хоҳлаган жойларингизда бемалол таомланинг. Фақат мана бу дарахтга яқинлашмангки, у ҳолда золимлардан бўлиб қоласиз»'}, {'chapter': 2, 'verse': 36, 'text': 'فَأَزَلَّهُمَا ٱلشَّيۡطَٰنُ عَنۡهَا فَأَخۡرَجَهُمَا مِمَّا كَانَا فِيهِۖ وَقُلۡنَا ٱهۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي ٱلۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ\nБас, уларни шайтон йўлдан оздириб, масканларидан чиқарди ва айтдик: «Тушингиз (жаннатдан Ерга)! (Сизлар) бир-бирингизга душмансиз. Энди маълум вақтгача (ажалларингиз етгунча) Ерда маскан тутиб яшайсиз»'}, {'chapter': 2, 'verse': 37, 'text': 'فَتَلَقَّىٰٓ ءَادَمُ مِن رَّبِّهِۦ كَلِمَٰتٖ فَتَابَ عَلَيۡهِۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nКейин, Одам Парвардигори тарафидан бир неча сўзлар (фармонлар) қабул қилиб олганидан сўнг, Аллоҳ унинг тавбасини қабул қилди. Албатта У тавбаларни қабул қилгувчи меҳрибон зотдир'}, {'chapter': 2, 'verse': 38, 'text': 'قُلۡنَا ٱهۡبِطُواْ مِنۡهَا جَمِيعٗاۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَن تَبِعَ هُدَايَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\n«У жойдан (жаннатдан) ҳаммангиз тушинг», дедик. «Бас, сизларга Мен тарафдан ҳидоят келганида, шу ҳақ йўлга эргашган кишиларга хавфу хатар йўқ ва улар ғамгин бўлмайдилар»'}, {'chapter': 2, 'verse': 39, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nОятларимизни инкор қилиб, ёлғон деган кимсалар эса дўзах эгалари бўлиб, унда абадий қолажаклар'}, {'chapter': 2, 'verse': 40, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَوۡفُواْ بِعَهۡدِيٓ أُوفِ بِعَهۡدِكُمۡ وَإِيَّـٰيَ فَٱرۡهَبُونِ\nЭй Бани Исроил, сизларга инъом қилган неъматимни эсланг ва Менга берган ваъда — аҳдга вафо қилинг. Шунда Мен ҳам аҳдга вафо қиламан. Ва Мендангина қўрқинглар'}, {'chapter': 2, 'verse': 41, 'text': 'وَءَامِنُواْ بِمَآ أَنزَلۡتُ مُصَدِّقٗا لِّمَا مَعَكُمۡ وَلَا تَكُونُوٓاْ أَوَّلَ كَافِرِۭ بِهِۦۖ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗا وَإِيَّـٰيَ فَٱتَّقُونِ\nСизлардаги нарсани (Тавротни) тасдиқ этган ҳолда нозил қилган нарсам (Қуръонга) иймон келтирингиз. Уни биринчи инкор қилувчилардан бўлмангиз. Ва оятларимни қиймати оз нарсаларга алмаштирманглар ва Мендангина қўрқинглар'}, {'chapter': 2, 'verse': 42, 'text': 'وَلَا تَلۡبِسُواْ ٱلۡحَقَّ بِٱلۡبَٰطِلِ وَتَكۡتُمُواْ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ\nҲақни ботилга аралаштирманглар ва билган ҳолингизда ҳақни беркитманглар'}, {'chapter': 2, 'verse': 43, 'text': 'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَٱرۡكَعُواْ مَعَ ٱلرَّـٰكِعِينَ\nНамозни тўкис адо қилинг, закотни беринг ва руку қилгувчилар билан бирга руку қилинг'}, {'chapter': 2, 'verse': 44, 'text': '۞أَتَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبِرِّ وَتَنسَوۡنَ أَنفُسَكُمۡ وَأَنتُمۡ تَتۡلُونَ ٱلۡكِتَٰبَۚ أَفَلَا تَعۡقِلُونَ\nОдамларни яхшиликка чорлаб, ўзларингизни унутасизларми? Ҳолбуки, ўзларингиз китоб (Таврот) тиловат қиласизлар. Ақлингизни юргизмайсизларми'}, {'chapter': 2, 'verse': 45, 'text': 'وَٱسۡتَعِينُواْ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى ٱلۡخَٰشِعِينَ\nСабр ва намоз ўқиш билан мадад сўранглар. Албатта у (намоз ўқиш) оғир ишдир. Магар шикастанафс зотларга (оғир эмасдир)'}, {'chapter': 2, 'verse': 46, 'text': 'ٱلَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَأَنَّهُمۡ إِلَيۡهِ رَٰجِعُونَ\nУлар ўзларининг Парвардигорларига рўбарў бўлишларини ва шубҳасиз, Унга қайтажакларини биладиган зотлардир'}, {'chapter': 2, 'verse': 47, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى ٱلۡعَٰلَمِينَ\nЭй Бани Исроил, сизларга инъом қилган неъматимни ва сизларни бутун оламдан афзал қилган пайтимни эсланглар'}, {'chapter': 2, 'verse': 48, 'text': 'وَٱتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا شَفَٰعَةٞ وَلَا يُؤۡخَذُ مِنۡهَا عَدۡلٞ وَلَا هُمۡ يُنصَرُونَ\nБиров биров томонидан ҳеч нарса ўтай олмайдиган, ҳеч кимдан оқлов қабул қилинмайдиган ва (гуноҳлари учун) эваз ҳам олинмайдиган – ёрдам берилмайдиган Кундан қўрқинглар'}, {'chapter': 2, 'verse': 49, 'text': 'وَإِذۡ نَجَّيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ يُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ\nСизларни оғир азоблар билан қийнаган, ўғилларингизни сўйиб, хотинларингизни тирик қолдираётган Фиръавн одамларидан қутқарганимизни эсланглар. Ана ўша ишларда Парвардигорингиз томонидан буюк синов бўлганди'}, {'chapter': 2, 'verse': 50, 'text': 'وَإِذۡ فَرَقۡنَا بِكُمُ ٱلۡبَحۡرَ فَأَنجَيۡنَٰكُمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَ وَأَنتُمۡ تَنظُرُونَ\nДенгизни ўртасидан бўлиб сизларни қутқариб, кўз ўнгингизда Фиръавн одамларини ғарқ қилганимизни эсланглар'}, {'chapter': 2, 'verse': 51, 'text': 'وَإِذۡ وَٰعَدۡنَا مُوسَىٰٓ أَرۡبَعِينَ لَيۡلَةٗ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ\nМусо билан қирқ кечага ваъдалашганимиз, у (бизнинг ҳузуримизга) кетганидан кейин сизлар ўзингизга зулм қилган ҳолингизда бузоққа ибодат қилганингизни эсланглар'}, {'chapter': 2, 'verse': 52, 'text': 'ثُمَّ عَفَوۡنَا عَنكُم مِّنۢ بَعۡدِ ذَٰلِكَ لَعَلَّكُمۡ تَشۡكُرُونَ\nСўнгра, ўша ишлардан кейин ҳам шукр қилишингиз учун сизларни афв этгандик'}, {'chapter': 2, 'verse': 53, 'text': 'وَإِذۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَٱلۡفُرۡقَانَ لَعَلَّكُمۡ تَهۡتَدُونَ\nСизлар ҳақ йўлни топишингиз учун Мусога китоб ва Фурқонни (яъни Тавротни) берганимизни эсланглар'}, {'chapter': 2, 'verse': 54, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ إِنَّكُمۡ ظَلَمۡتُمۡ أَنفُسَكُم بِٱتِّخَاذِكُمُ ٱلۡعِجۡلَ فَتُوبُوٓاْ إِلَىٰ بَارِئِكُمۡ فَٱقۡتُلُوٓاْ أَنفُسَكُمۡ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ عِندَ بَارِئِكُمۡ فَتَابَ عَلَيۡكُمۡۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nМусонинг ўз қавмига айтган гапларини эсланг: «Қавмим, сизлар бузоққа ибодат қилиш билан ўзингизга зулм қилдингиз. Энди ўзингизни (бир-бирингизни) ўлдириш билан сизни бор қилган Зотга тавба қиласиз. Бор қилгувчи Зот наздида мана шу (қатл) сизлар учун хайрлироқдир». Бас, Аллоҳ таоло тавбангизни қабул қилди, албатта У кечиримли, меҳрибондир'}, {'chapter': 2, 'verse': 55, 'text': 'وَإِذۡ قُلۡتُمۡ يَٰمُوسَىٰ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ نَرَى ٱللَّهَ جَهۡرَةٗ فَأَخَذَتۡكُمُ ٱلصَّـٰعِقَةُ وَأَنتُمۡ تَنظُرُونَ\nЭй Бани Исроил, эсланг: «Эй Мусо, Аллоҳни очиқ-равшан кўрмагунимизча ҳаргиз сенга ишонмаймиз», дейишингиз билан қараб турган ҳолингизда сизларни чақмоқ урди'}, {'chapter': 2, 'verse': 56, 'text': 'ثُمَّ بَعَثۡنَٰكُم مِّنۢ بَعۡدِ مَوۡتِكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\nСўнгра шукр қилишингиз учун ўлганингиздан кейин тирилтирдик'}, {'chapter': 2, 'verse': 57, 'text': 'وَظَلَّلۡنَا عَلَيۡكُمُ ٱلۡغَمَامَ وَأَنزَلۡنَا عَلَيۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nВа булутни устингизга соябон қилдик ва осмондан ширинлик ва беданалар ёғдириб, «Сизларга ризқ қилиб берган пок нарсалардан енглар», (дедик). (Бани Исроил) Бизга зулм қилмадилар, балки ўзларига зулм қилгувчи бўлдилар'}, {'chapter': 2, 'verse': 58, 'text': 'وَإِذۡ قُلۡنَا ٱدۡخُلُواْ هَٰذِهِ ٱلۡقَرۡيَةَ فَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ رَغَدٗا وَٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا وَقُولُواْ حِطَّةٞ نَّغۡفِرۡ لَكُمۡ خَطَٰيَٰكُمۡۚ وَسَنَزِيدُ ٱلۡمُحۡسِنِينَ\n«Ушбу қишлоққа (Қуддусга) киринглар-да, ундан хоҳлаган жойларингизда еб-ичинглар. Дарвозадан сажда қилган ҳолингизда киринглар ва «афв эт» денглар, биз хатоларингизни мағфират қиламиз ва яхшилик эгаларига зиёда неъматлар ато қиламиз», деганимизни эсланглар'}, {'chapter': 2, 'verse': 59, 'text': 'فَبَدَّلَ ٱلَّذِينَ ظَلَمُواْ قَوۡلًا غَيۡرَ ٱلَّذِي قِيلَ لَهُمۡ فَأَنزَلۡنَا عَلَى ٱلَّذِينَ ظَلَمُواْ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ\nШунда золим кимсалар уларга айтилган сўздан бошқа сўзни алмаштириб айтдилар. Бас, қилган исёнлари оқибатида бу золимлар устига Биз осмондан бало ёғдирдик'}, {'chapter': 2, 'verse': 60, 'text': '۞وَإِذِ ٱسۡتَسۡقَىٰ مُوسَىٰ لِقَوۡمِهِۦ فَقُلۡنَا ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنفَجَرَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۖ كُلُواْ وَٱشۡرَبُواْ مِن رِّزۡقِ ٱللَّهِ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nЭсланг (эй Бани Исроил), Мусо ўз қавми учун сув тилаганида: «Асоингни тошга ургин», дедик. Бас, ундан ўн икки чашма отилиб чиқди — ҳар бир қабилага (улар ўн икки уруғ эдилар) ўз суви маълум бўлди. «Аллоҳ берган ризқдан еб-ичинглар, Ер юзида бузғунчилик жиноятларини қилманглар»'}, {'chapter': 2, 'verse': 61, 'text': 'وَإِذۡ قُلۡتُمۡ يَٰمُوسَىٰ لَن نَّصۡبِرَ عَلَىٰ طَعَامٖ وَٰحِدٖ فَٱدۡعُ لَنَا رَبَّكَ يُخۡرِجۡ لَنَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ مِنۢ بَقۡلِهَا وَقِثَّآئِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۖ قَالَ أَتَسۡتَبۡدِلُونَ ٱلَّذِي هُوَ أَدۡنَىٰ بِٱلَّذِي هُوَ خَيۡرٌۚ ٱهۡبِطُواْ مِصۡرٗا فَإِنَّ لَكُم مَّا سَأَلۡتُمۡۗ وَضُرِبَتۡ عَلَيۡهِمُ ٱلذِّلَّةُ وَٱلۡمَسۡكَنَةُ وَبَآءُو بِغَضَبٖ مِّنَ ٱللَّهِۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلنَّبِيِّـۧنَ بِغَيۡرِ ٱلۡحَقِّۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ\nЭсланг: «Эй Мусо, бир хил таомга ҳеч қаноат қила олмаяпмиз. Парвардигорингга дуо қил, бизга ерда униб-ўсадиган сабзавотлардан; бодринг, саримсоқ, мош, пиёзга ўхшаган ўсимликлардан чиқариб берсин», деганингизда, у: «Яхши нарсани паст нарсага алмаштиришни истайсизларми? Қайси шаҳарга тушсангизлар сўраган нарсаларингиз турибди-ку?», деди. (Ношукрликлари сабаб) уларга хорлик ва мискинлик ёзиб қўйилди ва Аллоҳнинг ғазабига дучор бўлдилар. Бунга сабаб уларнинг Аллоҳ оятларини инкор қилганлари ва Пайғамбарларни ноҳақ қатл қилганларидир. Бунга сабаб итоат қилмай тажовузкор бўлганларидир'}, {'chapter': 2, 'verse': 62, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلنَّصَٰرَىٰ وَٱلصَّـٰبِـِٔينَ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта, иймон келтирган зотлар, яҳудий бўлганлар, насронийлар ва собиийлар (фаришталарга сиғинувчилар) — (улардан) қайсилари (аввал қайси динда бўлганларидан қатъи назар, иймонга келиб, ёлғиз) Аллоҳга, Охират кунига ишонса ва яхши амаллар қилса, ўшаларга Парвардигорлари ҳузурида ажр бордир ва улар учун хавфу хатар, ғам-андуҳ йўқдир'}, {'chapter': 2, 'verse': 63, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ\nЭсланг (эй Бани Исроил), биз аҳду паймонингизни олгандик ва устингизда Тур тоғини кўтариб туриб, «Сизга берган нарсамизни (Тавротни) маҳкам ушланг ва ундаги бор нарсани ёдингизда тутинг — шояд тақво эгаларидан бўлсангиз», дегандик'}, {'chapter': 2, 'verse': 64, 'text': 'ثُمَّ تَوَلَّيۡتُم مِّنۢ بَعۡدِ ذَٰلِكَۖ فَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَكُنتُم مِّنَ ٱلۡخَٰسِرِينَ\nКейин ўша аҳду паймондан кейин юз ўгирдингиз. Агар сизларга Аллоҳнинг фазлу карами бўлмаганда эди, зиён кўргувчилардан бўлиб қолар эдингиз'}, {'chapter': 2, 'verse': 65, 'text': 'وَلَقَدۡ عَلِمۡتُمُ ٱلَّذِينَ ٱعۡتَدَوۡاْ مِنكُمۡ فِي ٱلسَّبۡتِ فَقُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ\nОрангиздан шанба кунида ҳаддан ошган кимсаларни билдингиз. Бас, Биз уларга: «Бадбахт маймунларга айланингиз», дедик'}, {'chapter': 2, 'verse': 66, 'text': 'فَجَعَلۡنَٰهَا نَكَٰلٗا لِّمَا بَيۡنَ يَدَيۡهَا وَمَا خَلۡفَهَا وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ\nБиз бу уқубатни ўша вақт ва кейинги даврлар учун ибрат, тақволи кишилар учун мавъиза (насиҳат) қилдик'}, {'chapter': 2, 'verse': 67, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦٓ إِنَّ ٱللَّهَ يَأۡمُرُكُمۡ أَن تَذۡبَحُواْ بَقَرَةٗۖ قَالُوٓاْ أَتَتَّخِذُنَا هُزُوٗاۖ قَالَ أَعُوذُ بِٱللَّهِ أَنۡ أَكُونَ مِنَ ٱلۡجَٰهِلِينَ\nЭсланг! Мусо ўз қавмига: «Аллоҳ бир сигир сўйишларингни буюрди», деганида, улар: «Устимиздан куляпсанми?», дедилар. (Мусо) деди: «Ундай жоҳиллардан бўлиб қолишдан Аллоҳ паноҳ беришини сўрайман»'}, {'chapter': 2, 'verse': 68, 'text': 'قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا فَارِضٞ وَلَا بِكۡرٌ عَوَانُۢ بَيۡنَ ذَٰلِكَۖ فَٱفۡعَلُواْ مَا تُؤۡمَرُونَ\nУлар: «Парвардигорингга дуо қил, унинг қандайлигини баён қилсин», дедилар. (Мусо) деди: «У зотнинг айтишича, у қари ҳам, ёш ғунажин ҳам бўлмаган, икковининг ўртасидаги бир сигирдир. Энди фармонни адо этинглар»'}, {'chapter': 2, 'verse': 69, 'text': 'قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا لَوۡنُهَاۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ صَفۡرَآءُ فَاقِعٞ لَّوۡنُهَا تَسُرُّ ٱلنَّـٰظِرِينَ\nУлар дедилар: «Парвардигорингга дуо қил. Унинг рангини баён қилсин». (Мусо) айтди: «У зотнинг айтишича, у очиқ-малларанг сигир бўлиб, кўрганларнинг кўнгли чоғ бўлади»'}, {'chapter': 2, 'verse': 70, 'text': 'قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ إِنَّ ٱلۡبَقَرَ تَشَٰبَهَ عَلَيۡنَا وَإِنَّآ إِن شَآءَ ٱللَّهُ لَمُهۡتَدُونَ\nУлар айтдилар: «Парвардигорингга дуо қил, унинг қандайлигини баён қилсин, бу мол бизга иштибоҳли бўлиб қолди. ИншоАллоҳ, энди йўлимизни топиб оламиз»'}, {'chapter': 2, 'verse': 71, 'text': 'قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا ذَلُولٞ تُثِيرُ ٱلۡأَرۡضَ وَلَا تَسۡقِي ٱلۡحَرۡثَ مُسَلَّمَةٞ لَّا شِيَةَ فِيهَاۚ قَالُواْ ٱلۡـَٰٔنَ جِئۡتَ بِٱلۡحَقِّۚ فَذَبَحُوهَا وَمَا كَادُواْ يَفۡعَلُونَ\n(Мусо) айтди: «У зотнинг айтишича, у шундай сигирки, хор бўлиб Ер ҳайдамайди, у билан (сув ташиб) экин суғормайдилар, айбу нуқсонсиз, унда ўз рангидан бошқа ранг-бўёқ ҳам йўқ». Улар: «Энди ҳақиқатни айтдинг», дедилар-да, узоқ машаққатлар чекиб (ўша айтилган сифатларга эга бўлган сигирни топиб) сўйдилар'}, {'chapter': 2, 'verse': 72, 'text': 'وَإِذۡ قَتَلۡتُمۡ نَفۡسٗا فَٱدَّـٰرَ\u2009ٰٔتُمۡ فِيهَاۖ وَٱللَّهُ مُخۡرِجٞ مَّا كُنتُمۡ تَكۡتُمُونَ\nЭсланг (эй Бани Исроил), бир жонни ўлдириб қўйиб, унинг қотили ким экани ҳақида жанжаллашиб қолгандингиз, ҳолбуки, Аллоҳ яширган нарсаларингизни юзага чиқаргувчидир'}, {'chapter': 2, 'verse': 73, 'text': 'فَقُلۡنَا ٱضۡرِبُوهُ بِبَعۡضِهَاۚ كَذَٰلِكَ يُحۡيِ ٱللَّهُ ٱلۡمَوۡتَىٰ وَيُرِيكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\nШунда биз: «Уни (мақтулни) сўйилган сигирнинг бир бўлаги билан уринглар», дегандик. Аллоҳ ўликларни шу тариқа тирилтиради ва сизларга фикр қилишингиз учун ўз оятларини кўрсатади'}, {'chapter': 2, 'verse': 74, 'text': 'ثُمَّ قَسَتۡ قُلُوبُكُم مِّنۢ بَعۡدِ ذَٰلِكَ فَهِيَ كَٱلۡحِجَارَةِ أَوۡ أَشَدُّ قَسۡوَةٗۚ وَإِنَّ مِنَ ٱلۡحِجَارَةِ لَمَا يَتَفَجَّرُ مِنۡهُ ٱلۡأَنۡهَٰرُۚ وَإِنَّ مِنۡهَا لَمَا يَشَّقَّقُ فَيَخۡرُجُ مِنۡهُ ٱلۡمَآءُۚ وَإِنَّ مِنۡهَا لَمَا يَهۡبِطُ مِنۡ خَشۡيَةِ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nСўнгра — шундай мўъжизаларни кўргандан кейин ҳам дилларингиз қотди. Бас, у диллар тош кабидир ёки ундан-да қаттиқроқдир. Зеро, шундай тошлар борки, улардан дарёлар отилиб чиқади, яна шундайлари борки, ёрилиб, ичидан сув чиқади, яна шундайлари ҳам борки, Аллоҳдан қўрққанидан пастга қулайди. Аллоҳ қилаётган амалларингдан ғофил эмасдир'}, {'chapter': 2, 'verse': 75, 'text': '۞أَفَتَطۡمَعُونَ أَن يُؤۡمِنُواْ لَكُمۡ وَقَدۡ كَانَ فَرِيقٞ مِّنۡهُمۡ يَسۡمَعُونَ كَلَٰمَ ٱللَّهِ ثُمَّ يُحَرِّفُونَهُۥ مِنۢ بَعۡدِ مَا عَقَلُوهُ وَهُمۡ يَعۡلَمُونَ\nУларнинг иймонга келишларига орзуманд бўласизми (эй мўминлар)? Ҳолбуки улардан бир гуруҳи Аллоҳнинг каломини эшитадилар, сўнгра, уни англаб етганларидан кейин билиб туриб ўзгартирадилар'}, {'chapter': 2, 'verse': 76, 'text': 'وَإِذَا لَقُواْ ٱلَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَا بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ قَالُوٓاْ أَتُحَدِّثُونَهُم بِمَا فَتَحَ ٱللَّهُ عَلَيۡكُمۡ لِيُحَآجُّوكُم بِهِۦ عِندَ رَبِّكُمۡۚ أَفَلَا تَعۡقِلُونَ\nУлар иймонли кишиларга йўлиққанларида: «Иймон келтирдик», дейдилар. Бир-бировлари билан ёлғиз қолганларида эса: «Уларга (мусулмонларга) Аллоҳ сизларга очган нарса ҳақида гапиряпсизларми? Ўша ҳужжат билан Парвардигорингизнинг ҳузурида ҳужжатлашишлари учун-а? Ақлингизни ишлатмайсизми?», дейишади'}, {'chapter': 2, 'verse': 77, 'text': 'أَوَلَا يَعۡلَمُونَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ\nАллоҳ, шубҳасиз, уларнинг яширган нарсаларини ҳам, ошкор қилган нарсаларини ҳам билгувчи эканини билмайдиларми'}, {'chapter': 2, 'verse': 78, 'text': 'وَمِنۡهُمۡ أُمِّيُّونَ لَا يَعۡلَمُونَ ٱلۡكِتَٰبَ إِلَّآ أَمَانِيَّ وَإِنۡ هُمۡ إِلَّا يَظُنُّونَ\nУларнинг ичида китобдан бехабар, фақат хом хаёлларнигина биладиган омийлари ҳам борки, улар фақат гумон қиладилар, холос'}, {'chapter': 2, 'verse': 79, 'text': 'فَوَيۡلٞ لِّلَّذِينَ يَكۡتُبُونَ ٱلۡكِتَٰبَ بِأَيۡدِيهِمۡ ثُمَّ يَقُولُونَ هَٰذَا مِنۡ عِندِ ٱللَّهِ لِيَشۡتَرُواْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَوَيۡلٞ لَّهُم مِّمَّا كَتَبَتۡ أَيۡدِيهِمۡ وَوَيۡلٞ لَّهُم مِّمَّا يَكۡسِبُونَ\nЎз қўллари билан китоб ёзиб, сўнгра озгина қийматга сотиш учун: «Бу китоб Аллоҳ ҳузуридан келди», дейдиган кимсаларга ҳалокат бўлғай, бас, ёзган нарсалари уларга ҳалокат бўлғай, топган фойдалари уларга ҳалокат бўлғай'}, {'chapter': 2, 'verse': 80, 'text': 'وَقَالُواْ لَن تَمَسَّنَا ٱلنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَةٗۚ قُلۡ أَتَّخَذۡتُمۡ عِندَ ٱللَّهِ عَهۡدٗا فَلَن يُخۡلِفَ ٱللَّهُ عَهۡدَهُۥٓۖ أَمۡ تَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nЯҳудийлар: «Бизларга дўзах ўти саноқли кунлардагина тегади», дедилар. Уларга айтинг: «(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳдан аҳду паймон олдингизми — зотан, Аллоҳ ҳаргиз аҳдига хилоф қилмайди — ёки Аллоҳ ҳақида билмаган нарсаларингизни айтяпсизми?»'}, {'chapter': 2, 'verse': 81, 'text': 'بَلَىٰۚ مَن كَسَبَ سَيِّئَةٗ وَأَحَٰطَتۡ بِهِۦ خَطِيٓـَٔتُهُۥ فَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЙўқ! Кимда-ким ёмонлик касб қилса ва гуноҳга ўралиб қолса, ана ўшалар дўзах эгаларидир. Улар ўша жойда абадий қолажаклар'}, {'chapter': 2, 'verse': 82, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nИймон келтириб, яхши амаллар қилган зотлар эса жаннат эгалари бўлиб, ўша жойда абадий қолгувчилардир'}, {'chapter': 2, 'verse': 83, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ لَا تَعۡبُدُونَ إِلَّا ٱللَّهَ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَانٗا وَذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَقُولُواْ لِلنَّاسِ حُسۡنٗا وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ ثُمَّ تَوَلَّيۡتُمۡ إِلَّا قَلِيلٗا مِّنكُمۡ وَأَنتُم مُّعۡرِضُونَ\n«Фақат Аллоҳгагина ибодат қиласизлар, ота-она, қариндош-уруғ, етим ва мискинларга яхшилик қиласизлар. Одамларга чиройли сўзлар сўзлангиз ва намозни тўкис адо қилинг, закотни беринг», деб Бани Исроилдан аҳду паймон олганимизни эсланглар. Кейин эса (Эй Бани Исроил,) озгина кишидан ташқари ҳаммангиз юз ўгирган ҳолда кетдингиз'}, {'chapter': 2, 'verse': 84, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ لَا تَسۡفِكُونَ دِمَآءَكُمۡ وَلَا تُخۡرِجُونَ أَنفُسَكُم مِّن دِيَٰرِكُمۡ ثُمَّ أَقۡرَرۡتُمۡ وَأَنتُمۡ تَشۡهَدُونَ\nЭсланг, «Бир-бирингизнинг қонингизни тўкмайсиз, бир-бировларингизни диёрларингиздан ҳайдамайсиз», деб аҳду паймонингизни олгандик, сўнгра ўзингиз гувоҳ бўлганингиз ҳолда бунга иқрор бўлгандингиз'}, {'chapter': 2, 'verse': 85, 'text': 'ثُمَّ أَنتُمۡ هَـٰٓؤُلَآءِ تَقۡتُلُونَ أَنفُسَكُمۡ وَتُخۡرِجُونَ فَرِيقٗا مِّنكُم مِّن دِيَٰرِهِمۡ تَظَٰهَرُونَ عَلَيۡهِم بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَإِن يَأۡتُوكُمۡ أُسَٰرَىٰ تُفَٰدُوهُمۡ وَهُوَ مُحَرَّمٌ عَلَيۡكُمۡ إِخۡرَاجُهُمۡۚ أَفَتُؤۡمِنُونَ بِبَعۡضِ ٱلۡكِتَٰبِ وَتَكۡفُرُونَ بِبَعۡضٖۚ فَمَا جَزَآءُ مَن يَفۡعَلُ ذَٰلِكَ مِنكُمۡ إِلَّا خِزۡيٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يُرَدُّونَ إِلَىٰٓ أَشَدِّ ٱلۡعَذَابِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nКейин эса ўзингиз бир-бирингизни ўлдирмоқдасиз, ўз тоифангиздан бўлган бир гуруҳни диёрларидан ҳайдаб, уларнинг устида гуноҳ ва зулм билан бир-бирингизга ҳамкорлик қилмоқдасиз. Агар улар сизларга асир ҳолда келишса, товон тўлаб қутқариб ҳам оласиз. Ҳолбуки, уларни ҳайдаш сизларга ҳаром қилинган эди. Ё китобнинг (Тавротнинг) бир қисмига ишониб, бир қисмини инкор қиласизми? Ораларингдан ким бу ишни қилса, унинг жазоси бу дунёда расво бўлиш, Қиёмат Кунидан эса қаттиқ азобга дучор қилинадилар. Аллоҳ қилаётган ишларингдан ғофил эмасдир'}, {'chapter': 2, 'verse': 86, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا بِٱلۡأٓخِرَةِۖ فَلَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنصَرُونَ\nАна ўшалар Охират ўрнига дунё ҳаётини сотиб олган кимсалардир. Бас, азоблари енгиллатилмайди ва уларга ёрдам ҳам берилмайди'}, {'chapter': 2, 'verse': 87, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَقَفَّيۡنَا مِنۢ بَعۡدِهِۦ بِٱلرُّسُلِۖ وَءَاتَيۡنَا عِيسَى ٱبۡنَ مَرۡيَمَ ٱلۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ ٱلۡقُدُسِۗ أَفَكُلَّمَا جَآءَكُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُكُمُ ٱسۡتَكۡبَرۡتُمۡ فَفَرِيقٗا كَذَّبۡتُمۡ وَفَرِيقٗا تَقۡتُلُونَ\nАлбатта Биз Мусога китоб ато этдик. Ундан кейин ҳам пайғамбарлар юбордик. Ийсо бинни Марямга ҳужжатлар бердик ва уни Руҳул-қудс (Жаброил) ила қувватлантирдик. Ҳар қачон бир Пайғамбар сизларга ёқмайдиган нарса (оятлар) келтирса, бировини ёлғончи деб, бировини ўлдираверасизларми'}, {'chapter': 2, 'verse': 88, 'text': 'وَقَالُواْ قُلُوبُنَا غُلۡفُۢۚ بَل لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَقَلِيلٗا مَّا يُؤۡمِنُونَ\n«Дилимиз берк», дедилар. Йўқ, аксинча, куфрлари сабабли уларни Аллоҳ лаънатлаган. Бас, камдан-камлари иймон келтирадилар'}, {'chapter': 2, 'verse': 89, 'text': 'وَلَمَّا جَآءَهُمۡ كِتَٰبٞ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ وَكَانُواْ مِن قَبۡلُ يَسۡتَفۡتِحُونَ عَلَى ٱلَّذِينَ كَفَرُواْ فَلَمَّا جَآءَهُم مَّا عَرَفُواْ كَفَرُواْ بِهِۦۚ فَلَعۡنَةُ ٱللَّهِ عَلَى ٱلۡكَٰفِرِينَ\nҚачонки уларга Аллоҳнинг ҳузуридан ўзларида бор нарсани (Тавротни) тасдиқ этувчи китоб келганда — ҳолбуки, илгари кофирларга қарши ўша китобдан ёрдам кутардилар — бас, қачонки уларга ўзлари билган нарса келганда унга кофир бўлдилар. Кофирларга Аллоҳнинг лаънати бўлгай'}, {'chapter': 2, 'verse': 90, 'text': 'بِئۡسَمَا ٱشۡتَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡ أَن يَكۡفُرُواْ بِمَآ أَنزَلَ ٱللَّهُ بَغۡيًا أَن يُنَزِّلَ ٱللَّهُ مِن فَضۡلِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ فَبَآءُو بِغَضَبٍ عَلَىٰ غَضَبٖۚ وَلِلۡكَٰفِرِينَ عَذَابٞ مُّهِينٞ\nАллоҳ бандаларидан хоҳлаган зотга ўз фазлу карами билан (ваҳий) юборишига қарши чиқиб, Аллоҳ нозил қилган нарсага кофир бўлишлари сабабли улар ўзларини энг ярамас нарсага сотдилар. Бас, ғазаб устига ғазаб билан қайтдилар. Кофирлар учун хор қилувчи азоб бордир'}, {'chapter': 2, 'verse': 91, 'text': 'وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ بِمَآ أَنزَلَ ٱللَّهُ قَالُواْ نُؤۡمِنُ بِمَآ أُنزِلَ عَلَيۡنَا وَيَكۡفُرُونَ بِمَا وَرَآءَهُۥ وَهُوَ ٱلۡحَقُّ مُصَدِّقٗا لِّمَا مَعَهُمۡۗ قُلۡ فَلِمَ تَقۡتُلُونَ أَنۢبِيَآءَ ٱللَّهِ مِن قَبۡلُ إِن كُنتُم مُّؤۡمِنِينَ\nҚачон уларга: «Аллоҳ нозил қилган нарсага иймон келтиринг», дейилса, «Биз ўзимизга нозил қилинган нарсага иймон келтирамиз», дейдилар. Ва ундан кейин келган ўзларидаги нарсани (Тавротни) тасдиқ этувчи ҳақ (китоб)га кофир бўладилар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг, «Агар мўмин бўлган бўлсангизлар, нима сабабдан илгари Аллоҳнинг пайғамбарларини ўлдирар эдингизлар?»'}, {'chapter': 2, 'verse': 92, 'text': '۞وَلَقَدۡ جَآءَكُم مُّوسَىٰ بِٱلۡبَيِّنَٰتِ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ\nМусо сизларга очиқ ҳужжатлар келтирганидан кейин ўзларингизга зулм қилган ҳолингизда унинг кетидан бузоққа сиғиндингиз'}, {'chapter': 2, 'verse': 93, 'text': 'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱسۡمَعُواْۖ قَالُواْ سَمِعۡنَا وَعَصَيۡنَا وَأُشۡرِبُواْ فِي قُلُوبِهِمُ ٱلۡعِجۡلَ بِكُفۡرِهِمۡۚ قُلۡ بِئۡسَمَا يَأۡمُرُكُم بِهِۦٓ إِيمَٰنُكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nЭсланг (эй Бани Исроил), биз аҳду паймонингизни олгач, устингизда Тур тоғини кўтариб туриб: «Келтирган нарсамизни (динимизни) маҳкам ушланг ва қулоқ солинг», деганимизда, «Эшитдик ва бўйинсунмадик», дедилар. Кофирликлари сабаб кўнгиллари фақат бузоққа ибодат қилиш билан тўлади. Айтинг: «Агар сиз мўмин бўлсангиз, иймонингиз сизларни мунча ярамас нарсаларга буюради?»'}, {'chapter': 2, 'verse': 94, 'text': 'قُلۡ إِن كَانَتۡ لَكُمُ ٱلدَّارُ ٱلۡأٓخِرَةُ عِندَ ٱللَّهِ خَالِصَةٗ مِّن دُونِ ٱلنَّاسِ فَتَمَنَّوُاْ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар Аллоҳ ҳузурида Охират диёри (жаннат) бошқа одамларсиз фақат сизларники бўлса, у ҳолда ростгўй бўлсангизлар, ўлишни орзу қилинглар!»'}, {'chapter': 2, 'verse': 95, 'text': 'وَلَن يَتَمَنَّوۡهُ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nҚилган кирдикорлари сабабли ҳаргиз уни (ўлимни) орзу қилолмайдилар. Аллоҳ золимларни билгувчидир'}, {'chapter': 2, 'verse': 96, 'text': 'وَلَتَجِدَنَّهُمۡ أَحۡرَصَ ٱلنَّاسِ عَلَىٰ حَيَوٰةٖ وَمِنَ ٱلَّذِينَ أَشۡرَكُواْۚ يَوَدُّ أَحَدُهُمۡ لَوۡ يُعَمَّرُ أَلۡفَ سَنَةٖ وَمَا هُوَ بِمُزَحۡزِحِهِۦ مِنَ ٱلۡعَذَابِ أَن يُعَمَّرَۗ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ\nУларнинг ҳатто мушриклардан ҳам ҳаётга (яшашга) очкўз кишилар эканини кўрасиз, уларнинг айримлари минг йил умр кўришни истайдилар. Ҳолбуки, узун умр уни азобдан узоқлаштирмайди. Ахир Аллоҳ қилаётган амалларини кўриб тургувчидир'}, {'chapter': 2, 'verse': 97, 'text': 'قُلۡ مَن كَانَ عَدُوّٗا لِّـجِبۡرِيلَ فَإِنَّهُۥ نَزَّلَهُۥ عَلَىٰ قَلۡبِكَ بِإِذۡنِ ٱللَّهِ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَهُدٗى وَبُشۡرَىٰ لِلۡمُؤۡمِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Ким Жаброилга душман бўлса, (у Аллоҳга душмандир). Зеро у (Жаброил) Аллоҳнинг изни билан қалбингизга ўзидан аввалги китобларни тасдиқ этадиган ва мўминларга ҳидоят ва хушхабар бўлган Қуръонни нозил қилди'}, {'chapter': 2, 'verse': 98, 'text': 'مَن كَانَ عَدُوّٗا لِّلَّهِ وَمَلَـٰٓئِكَتِهِۦ وَرُسُلِهِۦ وَجِبۡرِيلَ وَمِيكَىٰلَ فَإِنَّ ٱللَّهَ عَدُوّٞ لِّلۡكَٰفِرِينَ\nКим Аллоҳга ва Унинг фаришталарига, пайғамбарларига, Жаброил ва Микоилга душман бўлса, бас, албатта Аллоҳ (бундай) кофирларга душмандир'}, {'chapter': 2, 'verse': 99, 'text': 'وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكَ ءَايَٰتِۭ بَيِّنَٰتٖۖ وَمَا يَكۡفُرُ بِهَآ إِلَّا ٱلۡفَٰسِقُونَ\nҲақиқатан Биз сизга очиқ-ойдин оятлар нозил қилдик. У (оятларга) фақат фосиқларгина кофир бўладилар'}, {'chapter': 2, 'verse': 100, 'text': 'أَوَكُلَّمَا عَٰهَدُواْ عَهۡدٗا نَّبَذَهُۥ فَرِيقٞ مِّنۡهُمۚ بَلۡ أَكۡثَرُهُمۡ لَا يُؤۡمِنُونَ\nҲар қачон бирон аҳдга аҳдлашсалар улардан бир гуруҳи бузаверадими? Ҳақиқатан уларнинг кўпи иймонсизлардир'}, {'chapter': 2, 'verse': 101, 'text': 'وَلَمَّا جَآءَهُمۡ رَسُولٞ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ نَبَذَ فَرِيقٞ مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ كِتَٰبَ ٱللَّهِ وَرَآءَ ظُهُورِهِمۡ كَأَنَّهُمۡ لَا يَعۡلَمُونَ\nҚачон уларга Аллоҳнинг ҳузуридан ўзларидаги нарсани тасдиқ этувчи Пайғамбар келса, китоб (Таврот) берилган кимсалардан бир гуруҳи Аллоҳнинг китобини (Қуръони Каримни) ҳеч нарса билмагандек орқаларига отадилар'}, {'chapter': 2, 'verse': 102, 'text': 'وَٱتَّبَعُواْ مَا تَتۡلُواْ ٱلشَّيَٰطِينُ عَلَىٰ مُلۡكِ سُلَيۡمَٰنَۖ وَمَا كَفَرَ سُلَيۡمَٰنُ وَلَٰكِنَّ ٱلشَّيَٰطِينَ كَفَرُواْ يُعَلِّمُونَ ٱلنَّاسَ ٱلسِّحۡرَ وَمَآ أُنزِلَ عَلَى ٱلۡمَلَكَيۡنِ بِبَابِلَ هَٰرُوتَ وَمَٰرُوتَۚ وَمَا يُعَلِّمَانِ مِنۡ أَحَدٍ حَتَّىٰ يَقُولَآ إِنَّمَا نَحۡنُ فِتۡنَةٞ فَلَا تَكۡفُرۡۖ فَيَتَعَلَّمُونَ مِنۡهُمَا مَا يُفَرِّقُونَ بِهِۦ بَيۡنَ ٱلۡمَرۡءِ وَزَوۡجِهِۦۚ وَمَا هُم بِضَآرِّينَ بِهِۦ مِنۡ أَحَدٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡۚ وَلَقَدۡ عَلِمُواْ لَمَنِ ٱشۡتَرَىٰهُ مَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِنۡ خَلَٰقٖۚ وَلَبِئۡسَ مَا شَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nВа Сулаймон давридаги шайтонлар (жинлар) айтган нарсаларга эргашадилар. Сулаймон кофир эмас эди, балки одамларга сеҳр ўргатадиган шайтонлар кофир эдилар. Ва Бобилдаги Ҳорут ва Морут номли фаришталарга туширилган нарсаларга эргашадилар. — Ҳолбуки, у фаришталар: «Биз фақатгина фитнамиз (яъни одамларни алдаб имтиҳон қилиш учун юборилганмиз), бас, (биз айтган нарсаларни қилиб) кофир бўлиб қолма», демасдан туриб ҳеч кимга ҳеч нарса ўргатмас эдилар. — Ва ўшалардан (Ҳорут ва Морутдан) эр-хотиннинг ўртасини бузадиган нарсалар ўрганадилар. (Лекин) улар (яҳудий сеҳргарлар) сеҳр билан Аллоҳнинг изнисиз ҳеч кимга зарар етказа олмайдилар. Ва ҳеч фойдасиз, билъакс зарарли нарсаларни ўрганадилар. Ахир (Аллоҳнинг китоби ўрнига сеҳрни) алмашган кимсаларга Охиратда ҳеч қандай насиба йўқ эканини билган эдилар-ку. Жонларини нақадар ёмон нарсага (яъни Охиратдаги маҳрумликка) сотганларини билсалар эди'}, {'chapter': 2, 'verse': 103, 'text': 'وَلَوۡ أَنَّهُمۡ ءَامَنُواْ وَٱتَّقَوۡاْ لَمَثُوبَةٞ مِّنۡ عِندِ ٱللَّهِ خَيۡرٞۚ لَّوۡ كَانُواْ يَعۡلَمُونَ\nАгар улар иймонга келиб Аллоҳдан қўрқсалар, Аллоҳ тарафидан бўладиган савоб, албатта яхшироқ бўлишини билсалар эди'}, {'chapter': 2, 'verse': 104, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقُولُواْ رَٰعِنَا وَقُولُواْ ٱنظُرۡنَا وَٱسۡمَعُواْۗ وَلِلۡكَٰفِرِينَ عَذَابٌ أَلِيمٞ\nЭй мўминлар, «Роъино» деманг. «Унзурно» денглар. Ва (Аллоҳнинг амр-наҳий-фармон-таъқиқларига) қулоқ солинг! Кофирлар учун аламли азоб бор'}, {'chapter': 2, 'verse': 105, 'text': 'مَّا يَوَدُّ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَلَا ٱلۡمُشۡرِكِينَ أَن يُنَزَّلَ عَلَيۡكُم مِّنۡ خَيۡرٖ مِّن رَّبِّكُمۡۚ وَٱللَّهُ يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nНа аҳли китоблар (яҳудий ва насронийлар)дан бўлган кофирлар ва на мушриклар сизга Парвардигорингиз тарафидан бир яхшилик (яъни ваҳий) тушишини истамайдилар. Аллоҳ эса раҳмат-марҳаматини ўзи хоҳлаган кишиларга хос қилиб беради. Аллоҳ буюк фазлу карам Соҳибидир'}, {'chapter': 2, 'verse': 106, 'text': '۞مَا نَنسَخۡ مِنۡ ءَايَةٍ أَوۡ نُنسِهَا نَأۡتِ بِخَيۡرٖ مِّنۡهَآ أَوۡ مِثۡلِهَآۗ أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), бирон оятни бекор қилсак ёки ёдингиздан чиқарсак, ундан яхшироғини ёки ўшанинг мислини келтирамиз. Аллоҳ ҳар нарсага қодир зот эканини билмадингизми'}, {'chapter': 2, 'verse': 107, 'text': 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ\nЕру осмонлар ёлғиз Аллоҳнинг мулки эканини ва сизлар учун Аллоҳдан ўзга бирон дўст ёки ёрдамчи йўқ эканини билмадингизми'}, {'chapter': 2, 'verse': 108, 'text': 'أَمۡ تُرِيدُونَ أَن تَسۡـَٔلُواْ رَسُولَكُمۡ كَمَا سُئِلَ مُوسَىٰ مِن قَبۡلُۗ وَمَن يَتَبَدَّلِ ٱلۡكُفۡرَ بِٱلۡإِيمَٰنِ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ\n(Эй мўминлар), ёки сизлар ҳам ўз Пайғамбарингиздан, илгари Мусодан сўралган нарсаларни сўрашни истайсизми ? Ким иймонга куфрни алмаштирса, албатта Тўғри Йўлдан адашади'}, {'chapter': 2, 'verse': 109, 'text': 'وَدَّ كَثِيرٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ لَوۡ يَرُدُّونَكُم مِّنۢ بَعۡدِ إِيمَٰنِكُمۡ كُفَّارًا حَسَدٗا مِّنۡ عِندِ أَنفُسِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡحَقُّۖ فَٱعۡفُواْ وَٱصۡفَحُواْ حَتَّىٰ يَأۡتِيَ ٱللَّهُ بِأَمۡرِهِۦٓۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАҳли китобларнинг кўпчилиги сизларни, иймонли бўлганингиздан кейин (яъни ҳозир), уларнинг ўзларига ҳам ҳақиқат очиқ равшан бўлганидан кейин ҳасад қилганлари сабабли куфрга қайтаришни истайди. Бас, Аллоҳнинг фармони келгунича уларни афв қилинг ва кечиринг. Албатта, Аллоҳ ҳар нарсага қодирдир'}, {'chapter': 2, 'verse': 110, 'text': 'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ ٱللَّهِۗ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ\nНамозни тўкис адо қилинг ва закотни беринг! Ўзингиз учун нима яхшилик қилган бўлсангиз, уни Аллоҳ ҳузурида топажаксиз. Шубҳасиз Аллоҳ қилган амалларингизни кўриб тургувчидир'}, {'chapter': 2, 'verse': 111, 'text': 'وَقَالُواْ لَن يَدۡخُلَ ٱلۡجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوۡ نَصَٰرَىٰۗ تِلۡكَ أَمَانِيُّهُمۡۗ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\n«Жаннатга фақат яҳудий ёки насроний бўлганлар киради», дейишди. (Яъни яҳудийлар: «Биз кирамиз», дейишса, насронийлар: «Биз кирамиз», дейишди.) Бу уларнинг хом хаёлларидир. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар ростгўй бўлсангиз, ҳужжатларингизни келтиринглар!»'}, {'chapter': 2, 'verse': 112, 'text': 'بَلَىٰۚ مَنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنٞ فَلَهُۥٓ أَجۡرُهُۥ عِندَ رَبِّهِۦ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nЙўқ, ким яхшилик қилган ҳолида ўзини Аллоҳга топширса, унинг учун Аллоҳ ҳузурида ажр бор ва улар учун хавфу хатар йўқ ва улар ғамгин бўлмайдилар'}, {'chapter': 2, 'verse': 113, 'text': 'وَقَالَتِ ٱلۡيَهُودُ لَيۡسَتِ ٱلنَّصَٰرَىٰ عَلَىٰ شَيۡءٖ وَقَالَتِ ٱلنَّصَٰرَىٰ لَيۡسَتِ ٱلۡيَهُودُ عَلَىٰ شَيۡءٖ وَهُمۡ يَتۡلُونَ ٱلۡكِتَٰبَۗ كَذَٰلِكَ قَالَ ٱلَّذِينَ لَا يَعۡلَمُونَ مِثۡلَ قَوۡلِهِمۡۚ فَٱللَّهُ يَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nЯҳудийлар: «Насронийлар бирон (арзигулик) нарса — дин устида эмаслар», дейишди. Насронийлар: «Яҳудийлар бирон (арзигулик) нарса — дин устида эмаслар», дейишди. Ҳолбуки, улар (яъни, яҳудийлар ҳам, насронийлар ҳам) китоб (яъни, Таврот, Инжил) тиловат қиладилар, шунингдек, билмайдиган кимсалар уларнинг гапига ўхшаш гап айтдилар. Бас, уларнинг ўзаро ихтилоф қилиб ўтган нарсалари ҳақида Аллоҳ Қиёмат Кунида ҳукм қилади'}, {'chapter': 2, 'verse': 114, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّن مَّنَعَ مَسَٰجِدَ ٱللَّهِ أَن يُذۡكَرَ فِيهَا ٱسۡمُهُۥ وَسَعَىٰ فِي خَرَابِهَآۚ أُوْلَـٰٓئِكَ مَا كَانَ لَهُمۡ أَن يَدۡخُلُوهَآ إِلَّا خَآئِفِينَۚ لَهُمۡ فِي ٱلدُّنۡيَا خِزۡيٞ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٞ\nАллоҳнинг масжидларини уларда Аллоҳ исми зикр қилинишидан тўсадиган ва уларни хароб қилиш ҳаракатида юрадиган кимсалардан ҳам золимроқ ким бор? Ахир бу кимсалар учун ундай жойларга фақат қўрққан ҳолларда кириш жоиз эди-ку. Улар учун бу дунёда расволик, Охиратда эса буюк азоб бордир'}, {'chapter': 2, 'verse': 115, 'text': 'وَلِلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ فَأَيۡنَمَا تُوَلُّواْ فَثَمَّ وَجۡهُ ٱللَّهِۚ إِنَّ ٱللَّهَ وَٰسِعٌ عَلِيمٞ\nМашриқ ҳам, мағриб ҳам Аллоҳникидир. Бас, қай тарафга юз бурсангиз, ўша жой Аллоҳнинг тарафидир. Шубҳасиз Аллоҳ (ҳамма томонни) қамраб олгувчи Билимдондир'}, {'chapter': 2, 'verse': 116, 'text': 'وَقَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ بَل لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ\n«Аллоҳнинг боласи бор», дедилар. У зот буларнинг бадгумонидан мутлақо покдир. Балки осмонлардаги ва Ердаги бор нарса У зотга тегишлидирки, ҳаммаси Унга бўйинсунади'}, {'chapter': 2, 'verse': 117, 'text': 'بَدِيعُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\n(У) Еру осмонларни пайдо қилгувчидир. Бирор ишни ҳукм қилса унга фақатгина «Бўл» дейди, холос. Бас, у иш бўлади'}, {'chapter': 2, 'verse': 118, 'text': 'وَقَالَ ٱلَّذِينَ لَا يَعۡلَمُونَ لَوۡلَا يُكَلِّمُنَا ٱللَّهُ أَوۡ تَأۡتِينَآ ءَايَةٞۗ كَذَٰلِكَ قَالَ ٱلَّذِينَ مِن قَبۡلِهِم مِّثۡلَ قَوۡلِهِمۡۘ تَشَٰبَهَتۡ قُلُوبُهُمۡۗ قَدۡ بَيَّنَّا ٱلۡأٓيَٰتِ لِقَوۡمٖ يُوقِنُونَ\nБилмайдиган кимсалар: «Аллоҳ бизга гапирса эди ёки бирор аломат келса эди», дейдилар. Улардан илгаригилар ҳам уларнинг гапларига ўхшаш гапларни айтишган эди. Диллари бир-бирига ўхшайди. Биз аниқ ишонадиган қавм учун оятларни баён қилдик'}, {'chapter': 2, 'verse': 119, 'text': 'إِنَّآ أَرۡسَلۡنَٰكَ بِٱلۡحَقِّ بَشِيرٗا وَنَذِيرٗاۖ وَلَا تُسۡـَٔلُ عَنۡ أَصۡحَٰبِ ٱلۡجَحِيمِ\nАлбатта Биз сизни ҳақ (дин) билан хушхабар элтгувчи ва огоҳлантирувчи қилиб юбордик. Дўзахилар хусусида сиз масъул эмассиз'}, {'chapter': 2, 'verse': 120, 'text': 'وَلَن تَرۡضَىٰ عَنكَ ٱلۡيَهُودُ وَلَا ٱلنَّصَٰرَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمۡۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۗ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ ٱلَّذِي جَآءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ\nЯҳудий ва насронийлар уларнинг динига кирмагунингизча ҳаргиз сиздан рози бўлмайдилар. Айтинг: «Аллоҳнинг йўлигина ҳақиқий йўлдир». Қасамки, агар сизга келган ҳақиқий билимдан кейин уларнинг нафс-ҳаволарига эргашсангиз, Аллоҳ тарафидан сизга на бир дўст ва на бир ёрдамчи бўлмайди'}, {'chapter': 2, 'verse': 121, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَتۡلُونَهُۥ حَقَّ تِلَاوَتِهِۦٓ أُوْلَـٰٓئِكَ يُؤۡمِنُونَ بِهِۦۗ وَمَن يَكۡفُرۡ بِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nБиз китоб (яъни Таврот, Инжил) ато этган зотлар орасида уни тўғри тиловат қиладиган кишилар ҳам бордирки, ана ўшалар (Қуръонга) иймон келтирурлар. Ва кимда-ким унга (Қуръонга) кофир бўлса, ўшалар зиён кўргувчилардир'}, {'chapter': 2, 'verse': 122, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى ٱلۡعَٰلَمِينَ\nЭй Бани Исроил, сизларга инъом қилган неъматимни ва сизларни бутун оламга афзал қилган пайтимни эсланг'}, {'chapter': 2, 'verse': 123, 'text': 'وَٱتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا عَدۡلٞ وَلَا تَنفَعُهَا شَفَٰعَةٞ وَلَا هُمۡ يُنصَرُونَ\nВа биров биров томонидан ҳеч нарса ўтай олмайдиган ва ҳеч кимдан эваз қабул қилинмайдиган, оқлов фойда бермайдиган ва ҳеч кимга ёрдам ҳам берилмайдиган кундан қўрқинг'}, {'chapter': 2, 'verse': 124, 'text': '۞وَإِذِ ٱبۡتَلَىٰٓ إِبۡرَٰهِـۧمَ رَبُّهُۥ بِكَلِمَٰتٖ فَأَتَمَّهُنَّۖ قَالَ إِنِّي جَاعِلُكَ لِلنَّاسِ إِمَامٗاۖ قَالَ وَمِن ذُرِّيَّتِيۖ قَالَ لَا يَنَالُ عَهۡدِي ٱلظَّـٰلِمِينَ\nЭсланг: Иброҳимни Парвардигори бир неча сўзлар билан имтиҳон қилганида, уларни бенуқсон ҳолда адо этди. Шунда (Аллоҳ таоло): «Албатта Мен сени одамларга имом қилгувчидирман», деди. «Зурриётимни ҳам-а?», деб сўради у. (Аллоҳ таоло) айтди: «Менинг бу аҳдим (сенинг зурриётинг орасидаги) золим кимсаларга етмайди»'}, {'chapter': 2, 'verse': 125, 'text': 'وَإِذۡ جَعَلۡنَا ٱلۡبَيۡتَ مَثَابَةٗ لِّلنَّاسِ وَأَمۡنٗا وَٱتَّخِذُواْ مِن مَّقَامِ إِبۡرَٰهِـۧمَ مُصَلّٗىۖ وَعَهِدۡنَآ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ أَن طَهِّرَا بَيۡتِيَ لِلطَّآئِفِينَ وَٱلۡعَٰكِفِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ\nЭсланг: «Байтуллоҳни одамлар учун зиёратгоҳ ва тинч жой қилдик ва Иброҳимнинг (Каъбатуллоҳни бино қилаётган вақтида) турган ўрнини намозгоҳ қилиб олинг, (дедик) ва Иброҳим билан Исмоилга: «Менинг байтимни тавоф қилгувчилар, ўша Ерда ибодат қилиб турувчилар, рукуъ-сажда қилувчилар учун пок тутинг», деб буюрдик'}, {'chapter': 2, 'verse': 126, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ ٱجۡعَلۡ هَٰذَا بَلَدًا ءَامِنٗا وَٱرۡزُقۡ أَهۡلَهُۥ مِنَ ٱلثَّمَرَٰتِ مَنۡ ءَامَنَ مِنۡهُم بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُۥ قَلِيلٗا ثُمَّ أَضۡطَرُّهُۥٓ إِلَىٰ عَذَابِ ٱلنَّارِۖ وَبِئۡسَ ٱلۡمَصِيرُ\nЭсланг: Иброҳим: «Эй Парвардигор, бу шаҳарни тинч шаҳар қилгин ва унинг аҳлларидан Аллоҳга ва Охират кунига ишонувчиларини турли мевалар билан ризқлантиргин», деганида, Аллоҳ таоло: «Кофирларини ҳам бир оз фойдалантириб, сўнгра дўзах азобига дучор қиламанки, бу энг ёмон оқибатдир», деди'}, {'chapter': 2, 'verse': 127, 'text': 'وَإِذۡ يَرۡفَعُ إِبۡرَٰهِـۧمُ ٱلۡقَوَاعِدَ مِنَ ٱلۡبَيۡتِ وَإِسۡمَٰعِيلُ رَبَّنَا تَقَبَّلۡ مِنَّآۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nЭсланг: Иброҳим Исмоил билан биргаликда Байтуллоҳнинг пойдеворини кўтариб (дедилар): «Парвардигоро, биздан (ушбу қилган амалимизни) Ўзинг қабул айла. Шубҳасиз, Сен эшитгувчи, билгувчисан'}, {'chapter': 2, 'verse': 128, 'text': 'رَبَّنَا وَٱجۡعَلۡنَا مُسۡلِمَيۡنِ لَكَ وَمِن ذُرِّيَّتِنَآ أُمَّةٗ مُّسۡلِمَةٗ لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبۡ عَلَيۡنَآۖ إِنَّكَ أَنتَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nПарвардигоро, бизни Ўзингга бўйинсунгувчилардан қилгин ва зурриётимиздан ҳам Ўзингга итоат қиладиган кишилар чиқаргин. Ва бизга (қиладиган) иборатларимизни ўргатгин ва тавба-тазарруъларимизни қабул эт! Шубҳасиз, Сен тавбаларни қабул қилгувчи, Раҳмлисан'}, {'chapter': 2, 'verse': 129, 'text': 'رَبَّنَا وَٱبۡعَثۡ فِيهِمۡ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِكَ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَيُزَكِّيهِمۡۖ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nПарвардигоро, уларнинг орасига ўзларидан бўлган, уларга оятларингни тиловат қилиб берадиган, Китоб ва Ҳикматни (Қуръон ва Ҳадисни) ўргатадиган ва уларни поклайдиган бир пайғамбарни юбор! Шубҳасиз, Сен қудрат ва ҳикмат соҳибисан»'}, {'chapter': 2, 'verse': 130, 'text': 'وَمَن يَرۡغَبُ عَن مِّلَّةِ إِبۡرَٰهِـۧمَ إِلَّا مَن سَفِهَ نَفۡسَهُۥۚ وَلَقَدِ ٱصۡطَفَيۡنَٰهُ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ\nИброҳимнинг динидан фақат енгилтакларгина юз ўгирадилар. Ҳақиқатан, Биз дунёда уни танлаб олдик ва у Охиратда шубҳасиз солиҳ (яқинларимиздан)дир'}, {'chapter': 2, 'verse': 131, 'text': 'إِذۡ قَالَ لَهُۥ رَبُّهُۥٓ أَسۡلِمۡۖ قَالَ أَسۡلَمۡتُ لِرَبِّ ٱلۡعَٰلَمِينَ\nЭсланг: Парвардигори унга: «Бўйинсун!», деганида: «Бутун оламлар эгасига бўйинсундим» деди'}, {'chapter': 2, 'verse': 132, 'text': 'وَوَصَّىٰ بِهَآ إِبۡرَٰهِـۧمُ بَنِيهِ وَيَعۡقُوبُ يَٰبَنِيَّ إِنَّ ٱللَّهَ ٱصۡطَفَىٰ لَكُمُ ٱلدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ\nИброҳим, у билан бирга Яъқуб ҳам болаларига шу динни васият қилиб дедилар: «Ўғилларим, албатта Аллоҳ сиз учун шу динни танлади. Бас, мусулмон бўлган ҳолингиздагина дунёдан ўтинг!»'}, {'chapter': 2, 'verse': 133, 'text': 'أَمۡ كُنتُمۡ شُهَدَآءَ إِذۡ حَضَرَ يَعۡقُوبَ ٱلۡمَوۡتُ إِذۡ قَالَ لِبَنِيهِ مَا تَعۡبُدُونَ مِنۢ بَعۡدِيۖ قَالُواْ نَعۡبُدُ إِلَٰهَكَ وَإِلَٰهَ ءَابَآئِكَ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ إِلَٰهٗا وَٰحِدٗا وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n(Эй яҳудийлар), ёки Яъқубга ўлим келгач, у ўғилларига: «Мендан кейин нимага ибодат қиласизлар?», деганида: «Сенинг Аллоҳнг ва оталаринг Иброҳим, Исмоил ва Исҳоқларнинг Аллоҳ бўлмиш ягона Аллоҳга ибодат қиламиз ва бизлар фақат Унинг Ўзигагина бўйинсунувчилармиз», деб жавоб қилганларига гувоҳ бўлганмисизлар'}, {'chapter': 2, 'verse': 134, 'text': 'تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ\nУлар ўтиб кетган кишилардир. Уларнинг қилган амаллари ўзлари учун, сизларнинг қилган амалларингиз ўзларингиз учундир. Ва сизлар улар қилган амаллар учун масъул бўлмайсиз'}, {'chapter': 2, 'verse': 135, 'text': 'وَقَالُواْ كُونُواْ هُودًا أَوۡ نَصَٰرَىٰ تَهۡتَدُواْۗ قُلۡ بَلۡ مِلَّةَ إِبۡرَٰهِـۧمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nЯҳудий ёки насроний бўлинг, ҳидоят топасиз, дедилар. (Яъни, яҳудийлар яҳудий бўлинг, деди, насронийлар насроний бўлинг, деди). (Айтинг: Эй Муҳаммад алайҳис-салоту вас-салом), балки биз ҳақ йўлдан тойилмаган, мушриклардан бўлмаган Иброҳим миллатига — динига эргашамиз'}, {'chapter': 2, 'verse': 136, 'text': 'قُولُوٓاْ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَآ أُوتِيَ ٱلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\nАйтингиз (эй мўминлар), Аллоҳга ва бизга нозил қилинган китобга ва Иброҳим, Исмоил, Исҳоқ, Яъқубга ва ўша уруғ-авлодга нозил қилинган нарсаларга, Мусо ва Ийсога берилган нарсаларга ва барча пайғамбарларга Парвардигорлари тарафидан берилган нарсаларга ишондик. Биз улардан бирортасини ажратиб қўймаймиз ва биз У зотга (Аллоҳ таолога) бўйинсунувчилармиз'}, {'chapter': 2, 'verse': 137, 'text': 'فَإِنۡ ءَامَنُواْ بِمِثۡلِ مَآ ءَامَنتُم بِهِۦ فَقَدِ ٱهۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا هُمۡ فِي شِقَاقٖۖ فَسَيَكۡفِيكَهُمُ ٱللَّهُۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nЭнди агар улар (яъни, аҳли китоблар) сизлар иймон келтирган нарсаларга иймон келтирсалар, ҳақ йўлни топибдилар. Агар юз ўгирсалар, у ҳолда, шубҳасиз, сизларга нисбатан гина ва адоватлари бордир. Уларнинг ёмонлигидан сизга (Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳнинг Ўзи етарлидир. У эшитгувчи, билгувчидир'}, {'chapter': 2, 'verse': 138, 'text': 'صِبۡغَةَ ٱللَّهِ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ صِبۡغَةٗۖ وَنَحۡنُ لَهُۥ عَٰبِدُونَ\nАллоҳнинг ранги-ла (рангинмиз). Аллоҳдан ҳам гўзалроқ ранг бергувчи борми? Бизлар Унгагина ибодат қилгувчилармиз'}, {'chapter': 2, 'verse': 139, 'text': 'قُلۡ أَتُحَآجُّونَنَا فِي ٱللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمۡ وَلَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ وَنَحۡنُ لَهُۥ مُخۡلِصُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг, бизнинг ҳам, сизнинг ҳам Парвардигорингиз бўлган Аллоҳ хусусида биз билан жанжаллашасизми? Ҳолбуки, бизнинг қилган амалимиз ўзимиз учун, сизнинг қилган амалингиз ўзингиз учун-ку? Биз фақат Унгагина ихлос қўювчилармиз'}, {'chapter': 2, 'verse': 140, 'text': 'أَمۡ تَقُولُونَ إِنَّ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطَ كَانُواْ هُودًا أَوۡ نَصَٰرَىٰۗ قُلۡ ءَأَنتُمۡ أَعۡلَمُ أَمِ ٱللَّهُۗ وَمَنۡ أَظۡلَمُ مِمَّن كَتَمَ شَهَٰدَةً عِندَهُۥ مِنَ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nЁки «Иброҳим, Исмоил, Исҳоқ, Яъқуб ва ўша уруғ-авлод яҳудий ё насроний бўлган», дейсизми? (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Уларнинг динларини сизлар яхшироқ биласизми ёки Аллоҳми? Аллоҳ томонидан бўлган гувоҳликни яширган кимсадан ҳам золимроқ одам борми? Аллоҳ қилаётган ишларингдан ғофил эмас»'}, {'chapter': 2, 'verse': 141, 'text': 'تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ\nУлар ўтиб кетган кишилардир. Уларнинг қилган амаллари ўзлари учун, сизларнинг қилган амалингиз ўзингиз учундир ва сизлар улар қилган амаллар учун масъул бўлмайсиз'}, {'chapter': 2, 'verse': 142, 'text': '۞سَيَقُولُ ٱلسُّفَهَآءُ مِنَ ٱلنَّاسِ مَا وَلَّىٰهُمۡ عَن قِبۡلَتِهِمُ ٱلَّتِي كَانُواْ عَلَيۡهَاۚ قُل لِّلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nОдамлардан ақлсизлари: «Улар (мусулмонлар)ни илгари қараган қиблаларидан қандай нарса юз ўгиртирди?», дейдилар. Айтинг: «Машриқ ҳам, Мағриб ҳам Аллоҳникидир. У Ўзи хоҳлаган кишиларни ҳақ йўлга ҳидоят қилади»'}, {'chapter': 2, 'verse': 143, 'text': 'وَكَذَٰلِكَ جَعَلۡنَٰكُمۡ أُمَّةٗ وَسَطٗا لِّتَكُونُواْ شُهَدَآءَ عَلَى ٱلنَّاسِ وَيَكُونَ ٱلرَّسُولُ عَلَيۡكُمۡ شَهِيدٗاۗ وَمَا جَعَلۡنَا ٱلۡقِبۡلَةَ ٱلَّتِي كُنتَ عَلَيۡهَآ إِلَّا لِنَعۡلَمَ مَن يَتَّبِعُ ٱلرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيۡهِۚ وَإِن كَانَتۡ لَكَبِيرَةً إِلَّا عَلَى ٱلَّذِينَ هَدَى ٱللَّهُۗ وَمَا كَانَ ٱللَّهُ لِيُضِيعَ إِيمَٰنَكُمۡۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٞ رَّحِيمٞ\nШунингдек, (яъни, Ҳақ йўлга ҳидоят қилганимиз каби), сизларни бошқа одамлар устида гувоҳ бўлишингиз ва Пайғамбар сизларнинг устингизда гувоҳ бўлиши учун ўрта (адолатли) бир миллат қилдик. Сиз илгари қараган қиблани Биз фақатгина ким пайғамбарга эргашиб, ким орқасига қайтиб кетишини билиш учун қилганмиз, холос. Шубҳасиз, бу оғир иш. Магар Аллоҳ ҳидоят қилган зотларгагина (оғир эмасдир). Аллоҳ иймонларингизни (яъни, иймон билан ўқиган намозларингизни) зое қилгувчи эмас. Албатта, Аллоҳ одамларга меҳрибон ва раҳмлидир'}, {'chapter': 2, 'verse': 144, 'text': 'قَدۡ نَرَىٰ تَقَلُّبَ وَجۡهِكَ فِي ٱلسَّمَآءِۖ فَلَنُوَلِّيَنَّكَ قِبۡلَةٗ تَرۡضَىٰهَاۚ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥۗ وَإِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ لَيَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ\nГоҳо юзингиз самода кезганини кўрамиз. Бас, албатта Биз сизни ўзингиз рози бўлган қиблага бурамиз. Юзингизни Масжидул-Ҳаром (Макка) томонга буринг! (Эй мўминлар, сизлар ҳам) қаерда бўлсангизлар, юзларингизни унинг тарафига бурингиз! Китоб берилган зотлар албатта бунинг Парвардигорлари томонидан (келган ҳақ ҳукм) эканини биладилар. Аллоҳ улар қилаётган амаллардан ғофил эмасдир'}, {'chapter': 2, 'verse': 145, 'text': 'وَلَئِنۡ أَتَيۡتَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ بِكُلِّ ءَايَةٖ مَّا تَبِعُواْ قِبۡلَتَكَۚ وَمَآ أَنتَ بِتَابِعٖ قِبۡلَتَهُمۡۚ وَمَا بَعۡضُهُم بِتَابِعٖ قِبۡلَةَ بَعۡضٖۚ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم مِّنۢ بَعۡدِ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ إِنَّكَ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ\nҚасамки, агар Сиз китоб берилган кимсаларга бор мўъжизаларни келтирсангиз ҳам улар қиблангизга боқмайдилар. Сиз ҳам уларнинг қибласига боқувчи эмассиз. Улар (яҳудий ва насронийлар)нинг бировлари бировларининг қибласига боқувчи эмас. Агар сиз ўзингизга келган билим (ваҳий)дан кейин ҳам уларнинг ҳавойи нафсларига эргашсангиз, у ҳолда шубҳасиз, золимлардан бўлиб қоласиз'}, {'chapter': 2, 'verse': 146, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمۡۖ وَإِنَّ فَرِيقٗا مِّنۡهُمۡ لَيَكۡتُمُونَ ٱلۡحَقَّ وَهُمۡ يَعۡلَمُونَ\nБиз Китоб ато этган кимсалар (яҳудий ва насронийлар) уни (Муҳаммад пайғамбарни) ўғил фарзандларини таниган каби танийдилар. (Яъни, ўзларининг илоҳий китобларида у ҳақда ўқиганлар.) Ва шунга қарамай, улардан бир гуруҳи билган ҳолларида ҳақиқатни беркитадилар'}, {'chapter': 2, 'verse': 147, 'text': 'ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ\nБу ҳақиқат (яъни, қибланинг ўзгариши) Парвардигорингиз томонидандир. Бас, ҳаргиз шубҳа қилгувчилардан бўлманг'}, {'chapter': 2, 'verse': 148, 'text': 'وَلِكُلّٖ وِجۡهَةٌ هُوَ مُوَلِّيهَاۖ فَٱسۡتَبِقُواْ ٱلۡخَيۡرَٰتِۚ أَيۡنَ مَا تَكُونُواْ يَأۡتِ بِكُمُ ٱللَّهُ جَمِيعًاۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nҲар кимнинг ўз юзланадиган тарафи бор. Бас, яхши ишларга шошилингиз! Қаерда бўлманг, Аллоҳ сизларни барчаларингизни (ҳисоб-китоб учун) келтиради. Албатта, Аллоҳ ҳар нарсага қодирдир'}, {'chapter': 2, 'verse': 149, 'text': 'وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ وَإِنَّهُۥ لَلۡحَقُّ مِن رَّبِّكَۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қайси тарафдан чиқсангиз, юзингизни Масжидул-Ҳаром томонга буринг! У шубҳасиз, Парвардигорингиз (буюрган) ҳақ (қибла)дир. Аллоҳ қилаётган амалларингиздан ғофил эмасдир'}, {'chapter': 2, 'verse': 150, 'text': 'وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيۡكُمۡ حُجَّةٌ إِلَّا ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِي وَلِأُتِمَّ نِعۡمَتِي عَلَيۡكُمۡ وَلَعَلَّكُمۡ تَهۡتَدُونَ\nҚайси тарафдан чиқсангиз, юзингизни Масжидул-Ҳаром томонга буринг! (Эй мўминлар), қаерда бўлсангизлар, юзларингизни ўша томонга бурингиз! Токи (айрим) одамлар учун сизларнинг устингизда ҳужжат бўлиб қолмасин. Лекин уларнинг ичида зулм қилгувчилар ҳам борки, сизлар улардан қўрқмангиз. Мендан қўрқингиз! Ва токи сизларга неъматимни комил қилиб беришим ва ҳидоят топишингиз учун шундай қилингиз'}, {'chapter': 2, 'verse': 151, 'text': 'كَمَآ أَرۡسَلۡنَا فِيكُمۡ رَسُولٗا مِّنكُمۡ يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِنَا وَيُزَكِّيكُمۡ وَيُعَلِّمُكُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَيُعَلِّمُكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ\nШунингдек, сизларга ўзларингиздан бўлган, сизларга Бизнинг оятларимизни тиловат қиладиган, сизларни поклайдиган, сизларга Китоб ва Ҳикматни (Қуръон ва Ҳадисни) ўргатадиган ва билмаган нарсаларингизни билдирган бир Пайғамбар юбордик'}, {'chapter': 2, 'verse': 152, 'text': 'فَٱذۡكُرُونِيٓ أَذۡكُرۡكُمۡ وَٱشۡكُرُواْ لِي وَلَا تَكۡفُرُونِ\nБас, Мени эслангиз, Мен ҳам сизларни эслайман ва Менга шукр қилингиз ва Менга куфр келтирмангиз'}, {'chapter': 2, 'verse': 153, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱسۡتَعِينُواْ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِينَ\nЭй мўминлар, сабр қилиш ва намоз ўқиш билан (Аллоҳдан) мадад сўранглар! Албатта, Аллоҳ сабр қилгувчилар билан биргадир'}, {'chapter': 2, 'verse': 154, 'text': 'وَلَا تَقُولُواْ لِمَن يُقۡتَلُ فِي سَبِيلِ ٱللَّهِ أَمۡوَٰتُۢۚ بَلۡ أَحۡيَآءٞ وَلَٰكِن لَّا تَشۡعُرُونَ\nВа Аллоҳ йўлида ҳалок бўлган шаҳидлар ҳақида: «Булар ўликлар», демангиз! Йўқ, улар тириклардир, лекин сизлар сезмайсизлар'}, {'chapter': 2, 'verse': 155, 'text': 'وَلَنَبۡلُوَنَّكُم بِشَيۡءٖ مِّنَ ٱلۡخَوۡفِ وَٱلۡجُوعِ وَنَقۡصٖ مِّنَ ٱلۡأَمۡوَٰلِ وَٱلۡأَنفُسِ وَٱلثَّمَرَٰتِۗ وَبَشِّرِ ٱلصَّـٰبِرِينَ\nВа албатта, Биз сизларни хавфу хатар, очлик, молу жон ва мева-чеваларни камайтириш каби нарсалар билан имтиҳон қиламиз. Сабрли кишиларга хушхабар беринг'}, {'chapter': 2, 'verse': 156, 'text': 'ٱلَّذِينَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةٞ قَالُوٓاْ إِنَّا لِلَّهِ وَإِنَّآ إِلَيۡهِ رَٰجِعُونَ\nУлар шундай кишиларки, бирор мусибат келганда: «Албатта, биз Аллоҳнинг (бандаларимиз) ва албатта биз У зотга қайтгувчилармиз», дейдилар'}, {'chapter': 2, 'verse': 157, 'text': 'أُوْلَـٰٓئِكَ عَلَيۡهِمۡ صَلَوَٰتٞ مِّن رَّبِّهِمۡ وَرَحۡمَةٞۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُهۡتَدُونَ\nАна ўшаларга Парвардигорлари томонидан саловот (мағфират) ва раҳмат бордур. Ана ўшалар ҳақ йўлни топгувчилардир'}, {'chapter': 2, 'verse': 158, 'text': '۞إِنَّ ٱلصَّفَا وَٱلۡمَرۡوَةَ مِن شَعَآئِرِ ٱللَّهِۖ فَمَنۡ حَجَّ ٱلۡبَيۡتَ أَوِ ٱعۡتَمَرَ فَلَا جُنَاحَ عَلَيۡهِ أَن يَطَّوَّفَ بِهِمَاۚ وَمَن تَطَوَّعَ خَيۡرٗا فَإِنَّ ٱللَّهَ شَاكِرٌ عَلِيمٌ\nАлбатта Сафо ва Марва Аллоҳнинг (динининг) нишоналаридандир. Бас, ким ҳаж ёки умра қилса, у иккисининг орасида саъй қилишининг гуноҳи йўқдир. Ва ким ўз ихтиёри-ла яхшиликни ният қилса, бас, албатта Аллоҳ Шокир — тоатга яраша тақдирловчи ва Алим — яхши билгувчидир'}, {'chapter': 2, 'verse': 159, 'text': 'إِنَّ ٱلَّذِينَ يَكۡتُمُونَ مَآ أَنزَلۡنَا مِنَ ٱلۡبَيِّنَٰتِ وَٱلۡهُدَىٰ مِنۢ بَعۡدِ مَا بَيَّنَّـٰهُ لِلنَّاسِ فِي ٱلۡكِتَٰبِ أُوْلَـٰٓئِكَ يَلۡعَنُهُمُ ٱللَّهُ وَيَلۡعَنُهُمُ ٱللَّـٰعِنُونَ\nБиз нозил қилган ҳужжатлар ва ҳидоятдан иборат (Муҳаммаднинг ҳақ пайғамбар эканлиги ҳақидаги) нарсаларни одамларга Китобда (Тавротда) равшан қилиб берганимиздан кейин беркитган кимсаларни шубҳасиз, Аллоҳ лаънатлагай ва лаънатловчи зотлар (фаришталар ва мўминлар) лаънатлагайлар'}, {'chapter': 2, 'verse': 160, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَبَيَّنُواْ فَأُوْلَـٰٓئِكَ أَتُوبُ عَلَيۡهِمۡ وَأَنَا ٱلتَّوَّابُ ٱلرَّحِيمُ\nМагар қайсилари тавба қилиб, ўзларини ўнглаб, (одамларга ҳақиқатни) очиқ айтсалар, бас, уларнинг тавбаларини қабул қиламан. Ва фақат Менгина тавбаларни қабул қилгувчи, раҳмлидирман'}, {'chapter': 2, 'verse': 161, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٌ أُوْلَـٰٓئِكَ عَلَيۡهِمۡ لَعۡنَةُ ٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلنَّاسِ أَجۡمَعِينَ\nКуфр йўлига кириб, кофир ҳолида ўлиб кетган кимсалар шубҳасиз, Аллоҳнинг ва жамики фаришта ва одамларнинг лаънатига дучор бўлғайлар'}, {'chapter': 2, 'verse': 162, 'text': 'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ\nВа у ерда азоблари енгиллатилмаган, (озгина бўлса ҳам нафас ростлашга) муҳлат берилмаган ҳолларида абадий (жаҳаннамда) қолгайлар'}, {'chapter': 2, 'verse': 163, 'text': 'وَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ لَّآ إِلَٰهَ إِلَّا هُوَ ٱلرَّحۡمَٰنُ ٱلرَّحِيمُ\nИлоҳингиз ягона илоҳдир. Ундан ўзга ҳақ илоҳ йўқдир. У Раҳмон ва Раҳимдир'}, {'chapter': 2, 'verse': 164, 'text': 'إِنَّ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَٱلۡفُلۡكِ ٱلَّتِي تَجۡرِي فِي ٱلۡبَحۡرِ بِمَا يَنفَعُ ٱلنَّاسَ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن مَّآءٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖ وَتَصۡرِيفِ ٱلرِّيَٰحِ وَٱلسَّحَابِ ٱلۡمُسَخَّرِ بَيۡنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nАлбатта осмонлар ва Ернинг яратилишида, кеча ва кундузнинг алмашиб туришида, денгизда одамларга керакли нарсаларни олиб юрган кемаларнинг (сузишида) ва Аллоҳ осмондан туширган ва у сабабли ўлик Ерни тирилтириб, бор жонзотни тарқатиб-ёйиб юборган — сув деган неъматда ва шамолларнинг йўналтирилишида, осмон ва Ер орасидаги итоатгўй булутда — (буларнинг ҳаммасида) ақлли кишилар учун оят-аломатлар бордир'}, {'chapter': 2, 'verse': 165, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَتَّخِذُ مِن دُونِ ٱللَّهِ أَندَادٗا يُحِبُّونَهُمۡ كَحُبِّ ٱللَّهِۖ وَٱلَّذِينَ ءَامَنُوٓاْ أَشَدُّ حُبّٗا لِّلَّهِۗ وَلَوۡ يَرَى ٱلَّذِينَ ظَلَمُوٓاْ إِذۡ يَرَوۡنَ ٱلۡعَذَابَ أَنَّ ٱلۡقُوَّةَ لِلَّهِ جَمِيعٗا وَأَنَّ ٱللَّهَ شَدِيدُ ٱلۡعَذَابِ\nОдамлар орасида шундай кимсалар борки, улар ўзгаларни Аллоҳга тенг билиб, уларни Аллоҳни севгандек севадилар. Иймонли кишиларнинг Аллоҳга бўлган муҳаббатлари қаттиқроқдир. Бундай золимлар азобга рўбарў бўладиган замонларида (Қиёмат Кунида) бутун куч-қудрат Аллоҳники бўлишини ва Аллоҳнинг азоби қаттиқ азоб эканини билсалар эди'}, {'chapter': 2, 'verse': 166, 'text': 'إِذۡ تَبَرَّأَ ٱلَّذِينَ ٱتُّبِعُواْ مِنَ ٱلَّذِينَ ٱتَّبَعُواْ وَرَأَوُاْ ٱلۡعَذَابَ وَتَقَطَّعَتۡ بِهِمُ ٱلۡأَسۡبَابُ\nУ кунда пешволар эргашувчилардан тонадилар ва ҳаммалари азобни (кўзлари билан) кўрадилар. (У кунда уларни боғлаб турадиган) баҳонаю сабаблар узилади'}, {'chapter': 2, 'verse': 167, 'text': 'وَقَالَ ٱلَّذِينَ ٱتَّبَعُواْ لَوۡ أَنَّ لَنَا كَرَّةٗ فَنَتَبَرَّأَ مِنۡهُمۡ كَمَا تَبَرَّءُواْ مِنَّاۗ كَذَٰلِكَ يُرِيهِمُ ٱللَّهُ أَعۡمَٰلَهُمۡ حَسَرَٰتٍ عَلَيۡهِمۡۖ وَمَا هُم بِخَٰرِجِينَ مِنَ ٱلنَّارِ\nЭргашувчилар: «Қанийди, яна бир карра (яшашнинг) иложи бўлса-ю, улар биздан тонганларидек, биз ҳам улардан тонсак», дейдилар. Шундай қилиб, Аллоҳ таоло уларнинг қилган амалларини ўзларига ҳасрат-надомат қилиб кўрсатади ва улар жаҳаннамдан чиқувчи бўлмайдилар'}, {'chapter': 2, 'verse': 168, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ كُلُواْ مِمَّا فِي ٱلۡأَرۡضِ حَلَٰلٗا طَيِّبٗا وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٌ\nЭй одамлар, Ердаги ҳалол-пок нарсалардан енглар ва шайтоннинг изидан эргашманглар! Шубҳасиз, у сизларнинг очиқ душманингиздир'}, {'chapter': 2, 'verse': 169, 'text': 'إِنَّمَا يَأۡمُرُكُم بِٱلسُّوٓءِ وَٱلۡفَحۡشَآءِ وَأَن تَقُولُواْ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nУ сизларни фақат ёмонлик ва бузуқликка ва Аллоҳ шаънида билмаган нарсаларингизни гапиришга буюради, холос'}, {'chapter': 2, 'verse': 170, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُواْ مَآ أَنزَلَ ٱللَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَآ أَلۡفَيۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡقِلُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ\nҚачон (мушрикларга): «Аллоҳ нозил қилган ҳукмларга бўйсунингиз», дейилса, улар: «Йўқ, биз оталаримизни қандай йўлда топган бўлсак, ўшанга эргашамиз», дейишади. Агар оталари ҳеч нарсага ақллари етмайдиган, Тўғри Йўлни тополмайдиган бўлсалар, ҳам-а'}, {'chapter': 2, 'verse': 171, 'text': 'وَمَثَلُ ٱلَّذِينَ كَفَرُواْ كَمَثَلِ ٱلَّذِي يَنۡعِقُ بِمَا لَا يَسۡمَعُ إِلَّا دُعَآءٗ وَنِدَآءٗۚ صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَعۡقِلُونَ\nКуфр йўлидаги кимсаларни (ҳақ йўлга даъват қилувчи кишининг) мисоли худди фақат овоз ва чақириқни эшитадиган ҳайвонларга қичқираётган кишининг мисолидир. Улар кар, соқов, кўрдирлар. Демак, тушунмайдилар'}, {'chapter': 2, 'verse': 172, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَٱشۡكُرُواْ لِلَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ\nЭй мўминлар, сизларга ризқ қилиб берганимиз — покиза нарсалардан енглар ва агар Аллоҳнинг Ўзигагана ибодат қилгувчи бўлсангизлар, У зотга шукр қилингиз'}, {'chapter': 2, 'verse': 173, 'text': 'إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ بِهِۦ لِغَيۡرِ ٱللَّهِۖ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nУ сизларга фақатгина ўлакса, қонни, тўнғиз гўштини ва Аллоҳдан ўзгага аталиб сўйилган нарсаларнигина ҳаром қилди. Энди кимки золим бўлмаган ва ҳаддан ошмаган ҳолида ночор вазиятда қолса, гуноҳкор бўлмайди. Албатта, Аллоҳ мағфират қилгувчи, раҳмлидир'}, {'chapter': 2, 'verse': 174, 'text': 'إِنَّ ٱلَّذِينَ يَكۡتُمُونَ مَآ أَنزَلَ ٱللَّهُ مِنَ ٱلۡكِتَٰبِ وَيَشۡتَرُونَ بِهِۦ ثَمَنٗا قَلِيلًا أُوْلَـٰٓئِكَ مَا يَأۡكُلُونَ فِي بُطُونِهِمۡ إِلَّا ٱلنَّارَ وَلَا يُكَلِّمُهُمُ ٱللَّهُ يَوۡمَ ٱلۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ\nАллоҳ нозил қилган Китоб (Таврот)дан (Муҳаммад алайҳис-салоту вас-саломнинг пайғамбарлигига доир) бўлган нарсани яширадиган ва уни озгина қийматга сотадиган кимсалар, ана ўшалар қоринларини фақат олов билан тўлдирадилар, Қиёмат Кунида Аллоҳ уларга гапирмайди, боқмайди ва уларни (гуноҳларидан) покламайди. Улар учун аламли азоб бордир'}, {'chapter': 2, 'verse': 175, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلضَّلَٰلَةَ بِٱلۡهُدَىٰ وَٱلۡعَذَابَ بِٱلۡمَغۡفِرَةِۚ فَمَآ أَصۡبَرَهُمۡ عَلَى ٱلنَّارِ\nАна ўшалар ҳидоят ўрнига залолатни, мағфират ўрнига азобни сотиб олган кимсалардир. Жаҳаннам ўтига мунча сабр-тоқатли эканлар-а'}, {'chapter': 2, 'verse': 176, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ نَزَّلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّۗ وَإِنَّ ٱلَّذِينَ ٱخۡتَلَفُواْ فِي ٱلۡكِتَٰبِ لَفِي شِقَاقِۭ بَعِيدٖ\nЎша (азоб) Аллоҳ бу Китоб (Таврот)ни ҳақ билан нозил қилгани сабаблидир. Албатта, бу Китоб хусусида талашиб-тортишадиган кимсалар мангу талашдадирлар (яъни, улар ҳеч қачон ҳақ билан келиша олмайдилар)'}, {'chapter': 2, 'verse': 177, 'text': '۞لَّيۡسَ ٱلۡبِرَّ أَن تُوَلُّواْ وُجُوهَكُمۡ قِبَلَ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَلَٰكِنَّ ٱلۡبِرَّ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلۡكِتَٰبِ وَٱلنَّبِيِّـۧنَ وَءَاتَى ٱلۡمَالَ عَلَىٰ حُبِّهِۦ ذَوِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَٱلسَّآئِلِينَ وَفِي ٱلرِّقَابِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَٱلۡمُوفُونَ بِعَهۡدِهِمۡ إِذَا عَٰهَدُواْۖ وَٱلصَّـٰبِرِينَ فِي ٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ وَحِينَ ٱلۡبَأۡسِۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ صَدَقُواْۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُتَّقُونَ\nЮзларингизни Машриқ ва Мағриб томонларига бураверишингиз яхшилик эмас, балки Аллоҳга, Охират кунига, фаришталарга, китобларга, пайғамбарларга иймон келтирган, ўзи яхши кўриб туриб молини қариндош-уруғларига, етим-есирларга, мискин-бечораларга, йўловчи-мусофирларга, тиланчи-гадоларга ва қулларни озод қилиш йўлида берадиган, намозни тўкис адо қилиб, закотни берадиган киши яхши кишидир ва аҳдлашганларида аҳдларига вафо қилгувчилар ва хусусан оғир-енгил кунларда ва жангу жадал пайтида сабр-тоқат қилгувчилар яхши кишилардир. Ана ўшалар чин иймонли кишилардир ва ана ўшалар асл тақводорлардир'}, {'chapter': 2, 'verse': 178, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ ٱلۡقِصَاصُ فِي ٱلۡقَتۡلَىۖ ٱلۡحُرُّ بِٱلۡحُرِّ وَٱلۡعَبۡدُ بِٱلۡعَبۡدِ وَٱلۡأُنثَىٰ بِٱلۡأُنثَىٰۚ فَمَنۡ عُفِيَ لَهُۥ مِنۡ أَخِيهِ شَيۡءٞ فَٱتِّبَاعُۢ بِٱلۡمَعۡرُوفِ وَأَدَآءٌ إِلَيۡهِ بِإِحۡسَٰنٖۗ ذَٰلِكَ تَخۡفِيفٞ مِّن رَّبِّكُمۡ وَرَحۡمَةٞۗ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ\nЭй мўминлар, сизларга ўлдирилган кишилар учун озод киши муқобилига озод кишидан, қул учун қулдан, аёл киши учун аёлдан қасос олиш фарз қилинди. Энди кимга биродари томонидан бир оз афв қилинса (яъни, қотилдан қасос олиш ўрнига товон олишга рози бўлинса), у ҳолда яхшилик билан бўйинсуниш ва чиройли суратда товон тўлаш лозимдир. Бу (хукм) Парвардигорингиз томонидан енгиллик ва раҳм-шафқатдир. Бас, кимки шундан кейин ҳаддан ошса (масалан, товон тўлангандан кейин ҳам қотилни ўлдирса), унинг учун аламли азоб бордир'}, {'chapter': 2, 'verse': 179, 'text': 'وَلَكُمۡ فِي ٱلۡقِصَاصِ حَيَوٰةٞ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ لَعَلَّكُمۡ تَتَّقُونَ\nСизлар учун қасосда ҳаёт бор, эй аҳли донишлар! Шояд (жиноятлардан) сақлансангизлар'}, {'chapter': 2, 'verse': 180, 'text': 'كُتِبَ عَلَيۡكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ إِن تَرَكَ خَيۡرًا ٱلۡوَصِيَّةُ لِلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِينَ\nБировингизга ўлим келган пайтида (ўзидан кейин) мол-дунё қолдираётган бўлса, адолат — тўғрилик билан ота-онага, қариндош-уруғга васият қилиниши фарз этилди. Бундай васият қилиш Аллоҳдан қўрқувчилар устидаги бурчдир'}, {'chapter': 2, 'verse': 181, 'text': 'فَمَنۢ بَدَّلَهُۥ بَعۡدَ مَا سَمِعَهُۥ فَإِنَّمَآ إِثۡمُهُۥ عَلَى ٱلَّذِينَ يُبَدِّلُونَهُۥٓۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nКим уни (васиятни) эшитганидан кейин ўзгартирса, гуноҳи фақат ўзгартирганларнинг бўйнида. Албатта, Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 2, 'verse': 182, 'text': 'فَمَنۡ خَافَ مِن مُّوصٖ جَنَفًا أَوۡ إِثۡمٗا فَأَصۡلَحَ بَيۡنَهُمۡ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nЭнди ким васият қилгувчи томонидан бирон тойилиш ёки гуноҳ содир бўлишидан хавф қилиб (васият қилгувчи билан меросхўрнинг) ораларини ислоҳ қилиб қўйса, гуноҳкор бўлмайди. Албатта, Аллоҳ мағфират қилгувчи, раҳмлидир'}, {'chapter': 2, 'verse': 183, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ ٱلصِّيَامُ كَمَا كُتِبَ عَلَى ٱلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ\nЭй мўминлар, тақволи кишилар бўлишингиз учун сизлардан илгари ўтганларга фарз қилингани каби сизларга ҳам рўза тутиш фарз қилинди'}, {'chapter': 2, 'verse': 184, 'text': 'أَيَّامٗا مَّعۡدُودَٰتٖۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۚ وَعَلَى ٱلَّذِينَ يُطِيقُونَهُۥ فِدۡيَةٞ طَعَامُ مِسۡكِينٖۖ فَمَن تَطَوَّعَ خَيۡرٗا فَهُوَ خَيۡرٞ لَّهُۥۚ وَأَن تَصُومُواْ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n(Рўза) саноқли кунлардадир. Энди сизлардан бирор киши хаста ёки мусофир бўлса, у ҳолда (рўза тутолмаган кунларининг) саноғини бошқа кунларда тутади. (Кексалик ёки заифлиги сабабли) рўза тутишга қийналадиган кишилар бир мискин-бечоранинг бир кунлик таоми миқдорида эваз тўлашлари лозим. Бас, ким ўз ихтиёри билан зиёда яхшилик қилса (лозим бўлганидан ортиқроқ эваз тўласа), ўзига яхши. Агар билсангиз, рўза тутишингиз ўзингиз учун (эваз бериш ёки ҳатто узрли ҳолатда рўза тутмасликка нисбатан) яхшироқдир'}, {'chapter': 2, 'verse': 185, 'text': 'شَهۡرُ رَمَضَانَ ٱلَّذِيٓ أُنزِلَ فِيهِ ٱلۡقُرۡءَانُ هُدٗى لِّلنَّاسِ وَبَيِّنَٰتٖ مِّنَ ٱلۡهُدَىٰ وَٱلۡفُرۡقَانِۚ فَمَن شَهِدَ مِنكُمُ ٱلشَّهۡرَ فَلۡيَصُمۡهُۖ وَمَن كَانَ مَرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۗ يُرِيدُ ٱللَّهُ بِكُمُ ٱلۡيُسۡرَ وَلَا يُرِيدُ بِكُمُ ٱلۡعُسۡرَ وَلِتُكۡمِلُواْ ٱلۡعِدَّةَ وَلِتُكَبِّرُواْ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n(У саноқли кунлар) Рамазон ойидирки, бу ойда одамлар учун ҳидоят бўлиб ва ҳидоят ва фурқон (ҳақ билан ботилни ажратгувчи)нинг очиқ оятлари бўлиб Қуръон нозил қилинган. Бас, сизлардан ким бу ойда ҳозир бўлса, рўза тутсин. Ва ким хаста ёки мусофир бўлса, у ҳолда (рўза тутолмаган кунларининг) саноғини бошқа кунларда (тузалгач ёки сафардан қайтгач) тутади. Аллоҳ сизларга енгилликни истайди, сизларга оғир бўлишини истамайди. Бу саноқни тўлдиришингиз ва ҳидоят қилгани сабабли Аллоҳни улуғлашингиз учундир. Шояд шукр қилсангиз'}, {'chapter': 2, 'verse': 186, 'text': 'وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌۖ أُجِيبُ دَعۡوَةَ ٱلدَّاعِ إِذَا دَعَانِۖ فَلۡيَسۡتَجِيبُواْ لِي وَلۡيُؤۡمِنُواْ بِي لَعَلَّهُمۡ يَرۡشُدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), бандаларим сиздан, Мен ҳақимда сўрасалар, Мен уларга яқинман. Менга дуо қилган пайтларида дуогўйларининг дуосини ижобат қиламан. Бас, улар ҳам Менинг (даъватимга) жавоб қилсинлар ва Менга иймон келтирсинлар. (Шунда) шояд Тўғри Йўлни топсалар'}, {'chapter': 2, 'verse': 187, 'text': 'أُحِلَّ لَكُمۡ لَيۡلَةَ ٱلصِّيَامِ ٱلرَّفَثُ إِلَىٰ نِسَآئِكُمۡۚ هُنَّ لِبَاسٞ لَّكُمۡ وَأَنتُمۡ لِبَاسٞ لَّهُنَّۗ عَلِمَ ٱللَّهُ أَنَّكُمۡ كُنتُمۡ تَخۡتَانُونَ أَنفُسَكُمۡ فَتَابَ عَلَيۡكُمۡ وَعَفَا عَنكُمۡۖ فَٱلۡـَٰٔنَ بَٰشِرُوهُنَّ وَٱبۡتَغُواْ مَا كَتَبَ ٱللَّهُ لَكُمۡۚ وَكُلُواْ وَٱشۡرَبُواْ حَتَّىٰ يَتَبَيَّنَ لَكُمُ ٱلۡخَيۡطُ ٱلۡأَبۡيَضُ مِنَ ٱلۡخَيۡطِ ٱلۡأَسۡوَدِ مِنَ ٱلۡفَجۡرِۖ ثُمَّ أَتِمُّواْ ٱلصِّيَامَ إِلَى ٱلَّيۡلِۚ وَلَا تُبَٰشِرُوهُنَّ وَأَنتُمۡ عَٰكِفُونَ فِي ٱلۡمَسَٰجِدِۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَقۡرَبُوهَاۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ يَتَّقُونَ\nСизларга рўза кечасида хотинларингизга қўшилиш ҳалол қилинди. Улар сизларнинг либосингиз, сизлар улар учун либоссиз (яъни, эр-хотин бир-бирига киши либосга муҳтож бўлгани каби муҳтождирлар). Аллоҳ сизлар ўзларингга хиёнат қилаётганларингни билиб, тавбаларингизни қабул қилди ва сизларни афв этди. Энди улар билан (рўза кечаларида ҳам бемалол) қовушингиз ва Аллоҳ сизлар учун ёзган нарсани (фарзандни) талаб қилингиз! Ва то тонгдан оқ ип қора ипдан ажраладиган пайтгача еб-ичаверинглар. Сўнгра кечгача рўзани бенуқсон қилиб тутинглар! Масжидларда эътикофда бўлган чоғингизда (кечалари ҳам) улар (хотинларингиз) билан қўшилманг! Булар (юқорида мазкур бўлган ҳукмлар) Аллоҳнинг чегараларидир. Бас, уларга яқинлашмангиз! Одамлар ҳаром ишлардан сақланишлари учун Аллоҳ ўз оятларини мана шундай очиқ-равшан баён қилади'}, {'chapter': 2, 'verse': 188, 'text': 'وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِٱلۡبَٰطِلِ وَتُدۡلُواْ بِهَآ إِلَى ٱلۡحُكَّامِ لِتَأۡكُلُواْ فَرِيقٗا مِّنۡ أَمۡوَٰلِ ٱلنَّاسِ بِٱلۡإِثۡمِ وَأَنتُمۡ تَعۡلَمُونَ\nМол-дунёларингизни ораларингизда ноҳақ — ҳаром йўллар билан емангиз! (Яъни, бир-бирингизнинг ҳаққингизни емангиз!) Ва (гуноҳ қилаётганингизни) билиб туриб, одамларнинг молларидан бир қисмини ҳаром йўл билан ейиш учун (молларингизни пора қилиб) ҳокимларга узатманг'}, {'chapter': 2, 'verse': 189, 'text': '۞يَسۡـَٔلُونَكَ عَنِ ٱلۡأَهِلَّةِۖ قُلۡ هِيَ مَوَٰقِيتُ لِلنَّاسِ وَٱلۡحَجِّۗ وَلَيۡسَ ٱلۡبِرُّ بِأَن تَأۡتُواْ ٱلۡبُيُوتَ مِن ظُهُورِهَا وَلَٰكِنَّ ٱلۡبِرَّ مَنِ ٱتَّقَىٰۗ وَأۡتُواْ ٱلۡبُيُوتَ مِنۡ أَبۡوَٰبِهَاۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\nСиздан ҳилоллар — янги чиққан ойлар ҳақида сўрашади. Айтинг: у (ойлар) одамлар ва ҳаж учун вақт ўлчовларидир. Уйларингизга орқа томонидан киришингиз яхшилик эмас, балки яхшилик, Аллоҳдан қўрққан кишининг (қилган) ишидир. Уйларга эшикларидан кирингиз! Ва Аллоҳдан қўрқинг! Шояд нажот топсангиз'}, {'chapter': 2, 'verse': 190, 'text': 'وَقَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ ٱلَّذِينَ يُقَٰتِلُونَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ\nСизларга қарши урушувчилар билан Аллоҳ йўлида жанг қилингиз ва тажовузкор бўлмангиз! Шубҳасиз, Аллоҳ тажовузкорларни севмайди'}, {'chapter': 2, 'verse': 191, 'text': 'وَٱقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡ وَأَخۡرِجُوهُم مِّنۡ حَيۡثُ أَخۡرَجُوكُمۡۚ وَٱلۡفِتۡنَةُ أَشَدُّ مِنَ ٱلۡقَتۡلِۚ وَلَا تُقَٰتِلُوهُمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ حَتَّىٰ يُقَٰتِلُوكُمۡ فِيهِۖ فَإِن قَٰتَلُوكُمۡ فَٱقۡتُلُوهُمۡۗ كَذَٰلِكَ جَزَآءُ ٱلۡكَٰفِرِينَ\nУларни (сизлар билан уруш олиб бораётганларни) топган жойингизда ўлдирингиз ва сизларни (қувиб) чиқарган жой (яъни, Макка)дан уларни ҳам қувиб чиқарингиз! (Одамларни) алдаб, фитнага солиш ўлдиришдан ёмонроқдир. Ўзлари уруш бошлагунча улар билан Масжидул-Ҳаром олдида урушмангиз! Бас, агар ўзлари уруш бошласалар, у ҳолда уларни ўлдиринглар! Кофирларнинг жазоси шундай бўлади'}, {'chapter': 2, 'verse': 192, 'text': 'فَإِنِ ٱنتَهَوۡاْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nЭнди агар тўхтасалар (куфрдан кечиб, Исломга кирсалар), бас, албатта Аллоҳ кечиргувчи раҳмлидир'}, {'chapter': 2, 'verse': 193, 'text': 'وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ ٱلدِّينُ لِلَّهِۖ فَإِنِ ٱنتَهَوۡاْ فَلَا عُدۡوَٰنَ إِلَّا عَلَى ٱلظَّـٰلِمِينَ\nТо фитна тугаб, бутун дин Аллоҳ учун бўлгунга қадар улар билан урушингиз! Энди агар тўхтасалар, у ҳолда сизлар ҳам тўхтангиз! Зеро, фақат золимларга қарши душманлик қилиш мумкин'}, {'chapter': 2, 'verse': 194, 'text': 'ٱلشَّهۡرُ ٱلۡحَرَامُ بِٱلشَّهۡرِ ٱلۡحَرَامِ وَٱلۡحُرُمَٰتُ قِصَاصٞۚ فَمَنِ ٱعۡتَدَىٰ عَلَيۡكُمۡ فَٱعۡتَدُواْ عَلَيۡهِ بِمِثۡلِ مَا ٱعۡتَدَىٰ عَلَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ\nУруш ҳаром қилинган ой уруш ҳаром қилинган ой муқобилида бўлади. Ҳурматлар (бузилса, риоя қилинмаса) қасос (олинади). Бас, ким сизларга тажовуз қилса, сизлар ҳам уларга тажовузлари муқобилида тажовуз қилинг! Ва Аллоҳдан қўрқингиз! Билингларки, Аллоҳ Ўзидан қўрқувчилар билан биргадир'}, {'chapter': 2, 'verse': 195, 'text': 'وَأَنفِقُواْ فِي سَبِيلِ ٱللَّهِ وَلَا تُلۡقُواْ بِأَيۡدِيكُمۡ إِلَى ٱلتَّهۡلُكَةِ وَأَحۡسِنُوٓاْۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُحۡسِنِينَ\nАллоҳ йўлидаги (кураш учун молларингизни) сарфланглар! Ва (бахиллик қилиш билан) ўзингизни ҳалокатга ташламанг! Яхшилик қилинг! Албатта, Аллоҳ яхшилик қилгувчиларни севади'}, {'chapter': 2, 'verse': 196, 'text': 'وَأَتِمُّواْ ٱلۡحَجَّ وَٱلۡعُمۡرَةَ لِلَّهِۚ فَإِنۡ أُحۡصِرۡتُمۡ فَمَا ٱسۡتَيۡسَرَ مِنَ ٱلۡهَدۡيِۖ وَلَا تَحۡلِقُواْ رُءُوسَكُمۡ حَتَّىٰ يَبۡلُغَ ٱلۡهَدۡيُ مَحِلَّهُۥۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ بِهِۦٓ أَذٗى مِّن رَّأۡسِهِۦ فَفِدۡيَةٞ مِّن صِيَامٍ أَوۡ صَدَقَةٍ أَوۡ نُسُكٖۚ فَإِذَآ أَمِنتُمۡ فَمَن تَمَتَّعَ بِٱلۡعُمۡرَةِ إِلَى ٱلۡحَجِّ فَمَا ٱسۡتَيۡسَرَ مِنَ ٱلۡهَدۡيِۚ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖ فِي ٱلۡحَجِّ وَسَبۡعَةٍ إِذَا رَجَعۡتُمۡۗ تِلۡكَ عَشَرَةٞ كَامِلَةٞۗ ذَٰلِكَ لِمَن لَّمۡ يَكُنۡ أَهۡلُهُۥ حَاضِرِي ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nҲаж ва умрани Аллоҳ учун комил суратда адо қилинг! Агар (касаллик ёки йўлтусарлар сабабли) йўлингиз тўсилиб қолса, муяссар бўлган бирор қурбонлик юборинг ва қурбонлик ўз жойига етиб бормагунча сочларингизни қирдирманг! Энди агар сизлардан (ҳожилардан) кимдир касал ёки бошида бирон дарди бўлса (шу узр билан эҳромда турган ҳолида сочини қирдирса), у ҳолда (уч кун) рўза тутиш ёки (олти мискинга) садақа бериш ёхуд (бир қўй) сўйиш билан эваз тўласин! Қачонки (йўлтўсарлар ёки бошқа ҳар қандай монеъдан) хотиржам бўлсангиз, (ораларингиздан) ким ҳаж (йўлида) умрадан фойдаланган бўлса (яъни, ҳаж мавсумида аввал умра амалларини бажариб, эҳромдан чиқиб, сўнгра ҳажга эҳром боғлаган бўлса), муяссар бўлган бирон қурбонликни қилсин! Ким (қурбонлик қиладиган нарса) топа олмаса, уч кун ҳажда, етти кун қайтганидан кейин рўза тутсин! Бу тўла ўн кундир. Ушбу ҳукм аҳли — оиласи Масжидул-Ҳаромда бўлмаган кишилар учундир. (У ернинг аҳолиси учун бундай ҳуқуқ ва бурчлар йўқ). Аллоҳдан қўрқингиз! Ва билингки, Аллоҳ — азоби қаттиқ Зотдир'}, {'chapter': 2, 'verse': 197, 'text': 'ٱلۡحَجُّ أَشۡهُرٞ مَّعۡلُومَٰتٞۚ فَمَن فَرَضَ فِيهِنَّ ٱلۡحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي ٱلۡحَجِّۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ يَعۡلَمۡهُ ٱللَّهُۗ وَتَزَوَّدُواْ فَإِنَّ خَيۡرَ ٱلزَّادِ ٱلتَّقۡوَىٰۖ وَٱتَّقُونِ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ\nҲаж (мавсуми) маълум ойлардир. (Яъни, Шаввол, Зул-қаъда ва Зул-ҳижжа ойининг ўн куни). Бас, ким шу ойларда ўзига ҳажни фарз қилса (ҳаж қилишни ният қилса), ҳаж давомида (жуфтига) яқинлашмайди, гуноҳ ишлар, жанжал-сурон қилмайди. Қандай яхши амал қилсангиз, албатта Аллоҳ билади. Ва (ҳаж қилиш учун йўлга тушишдан аввал зарур озуқа билан) таъминланиб олинг! Энг яхши озуқа (яъни, ўзингиз билан бирга бўлиши лозим бўлган энг яхши нарса) Аллоҳдан қўрқишдир. Мендан қўрқингиз, эй аҳли донишлар'}, {'chapter': 2, 'verse': 198, 'text': 'لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡۚ فَإِذَآ أَفَضۡتُم مِّنۡ عَرَفَٰتٖ فَٱذۡكُرُواْ ٱللَّهَ عِندَ ٱلۡمَشۡعَرِ ٱلۡحَرَامِۖ وَٱذۡكُرُوهُ كَمَا هَدَىٰكُمۡ وَإِن كُنتُم مِّن قَبۡلِهِۦ لَمِنَ ٱلضَّآلِّينَ\n(Ҳаж сафарида тижорат қилиш билан) Парвардигорингиздан фазлу карам исташингиздан сизлар учун ҳеч қандай гуноҳ йўқдир. Энди Арафотдан (Макка яқинидаги тоғ) тушганингиздан кейин Машъар-ул-Ҳаромда Аллоҳни зикр қилинг! У Зот сизларни — гарчи илгари адашганлардан бўлган эсангиз-да — Ҳақ йўлга ҳидоят қилгани янглиғ сизлар ҳам У Зотни эсланг — зикр қилинг!»'}, {'chapter': 2, 'verse': 199, 'text': 'ثُمَّ أَفِيضُواْ مِنۡ حَيۡثُ أَفَاضَ ٱلنَّاسُ وَٱسۡتَغۡفِرُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nСўнгра одамлар тушган томондан тушинглар ва Аллоҳдан (гуноҳларингизни) мағфират қилишини сўранглар! Албатта, Аллоҳ мағфират қилгувчи, раҳмлидир'}, {'chapter': 2, 'verse': 200, 'text': 'فَإِذَا قَضَيۡتُم مَّنَٰسِكَكُمۡ فَٱذۡكُرُواْ ٱللَّهَ كَذِكۡرِكُمۡ ءَابَآءَكُمۡ أَوۡ أَشَدَّ ذِكۡرٗاۗ فَمِنَ ٱلنَّاسِ مَن يَقُولُ رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِنۡ خَلَٰقٖ\nЭнди, маросимларингизни адо қилиб бўлгач, Аллоҳни аждодларингизни эслагандек, балки ундан-да қаттиқроқ эсланг! Зотан, одамлар орасида шундай кимсалар борки, «Эй Парвардигор, бизга мана шу дунёда бергин», дейди. Ҳолбуки, унинг учун Охиратда ҳеч қандай насиба йўқ'}, {'chapter': 2, 'verse': 201, 'text': 'وَمِنۡهُم مَّن يَقُولُ رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ حَسَنَةٗ وَقِنَا عَذَابَ ٱلنَّارِ\nВа уларнинг орасида шундай зотлар ҳам борки, «Парвардигоро, бизга бу дунёда ҳам яхшилик ато қилгин, Охиратда ҳам яхшилик ато этгин ва бизни дўзах азобидан асрагин», дейдилар'}, {'chapter': 2, 'verse': 202, 'text': 'أُوْلَـٰٓئِكَ لَهُمۡ نَصِيبٞ مِّمَّا كَسَبُواْۚ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ\nАна ўша зотлар учун қилган ибодатлари (ҳаж, дуолари) шарофатидан насиба бор. Аллоҳ тез ҳисоб-китоб қилгувчи Зотдир'}, {'chapter': 2, 'verse': 203, 'text': '۞وَٱذۡكُرُواْ ٱللَّهَ فِيٓ أَيَّامٖ مَّعۡدُودَٰتٖۚ فَمَن تَعَجَّلَ فِي يَوۡمَيۡنِ فَلَآ إِثۡمَ عَلَيۡهِ وَمَن تَأَخَّرَ فَلَآ إِثۡمَ عَلَيۡهِۖ لِمَنِ ٱتَّقَىٰۗ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّكُمۡ إِلَيۡهِ تُحۡشَرُونَ\nСаноқли кунларда Аллоҳни зикр қилингиз! Ким икки кунда шошилиб (зикрни тўхтатса) унга гуноҳ йўқ, ким кейинда (учинчи кунга ҳам) қолса, унга-да гуноҳ йўқдир. (Мазкур ҳукмлар) тақводор кишилар учундир. Аллоҳдан қўрқингиз ва билингизким, сизлар шубҳасиз, Унинг ҳузурида тўпланажаксиз'}, {'chapter': 2, 'verse': 204, 'text': 'وَمِنَ ٱلنَّاسِ مَن يُعۡجِبُكَ قَوۡلُهُۥ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيُشۡهِدُ ٱللَّهَ عَلَىٰ مَا فِي قَلۡبِهِۦ وَهُوَ أَلَدُّ ٱلۡخِصَامِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), одамлар орасида шундай кимсалар борки, унинг гапирган гапи, ҳаёти дунёда Сизга қизиқ туюлади. Ва ўзи (Исломга) ашаддий хусуматчи бўлган ҳолида дилидаги «иймонига» Аллоҳни гувоҳ келтиради'}, {'chapter': 2, 'verse': 205, 'text': 'وَإِذَا تَوَلَّىٰ سَعَىٰ فِي ٱلۡأَرۡضِ لِيُفۡسِدَ فِيهَا وَيُهۡلِكَ ٱلۡحَرۡثَ وَٱلنَّسۡلَۚ وَٱللَّهُ لَا يُحِبُّ ٱلۡفَسَادَ\n(Олдингиздан) кетганида эса Ерда бузғунчилик, экин ва наслларни ҳалок қилиш учун югуриб-елиб юради. Аллоҳ эса бузғунчилик-фасодни севмайди'}, {'chapter': 2, 'verse': 206, 'text': 'وَإِذَا قِيلَ لَهُ ٱتَّقِ ٱللَّهَ أَخَذَتۡهُ ٱلۡعِزَّةُ بِٱلۡإِثۡمِۚ فَحَسۡبُهُۥ جَهَنَّمُۖ وَلَبِئۡسَ ٱلۡمِهَادُ\nҚачон унга: «Аллоҳдан қўрққин», дейилса, уни кибру ҳавоси гуноҳга тортади-етаклайди. Унга жаҳаннам бас келар. Нақадар ёмон жой-а'}, {'chapter': 2, 'verse': 207, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَشۡرِي نَفۡسَهُ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِۚ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ\nОдамлар орасида Аллоҳ ризолиги йўлида ўз жонини берадиган зотлар ҳам бор. Аллоҳ бандаларига ғамхўр — меҳрибондир'}, {'chapter': 2, 'verse': 208, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱدۡخُلُواْ فِي ٱلسِّلۡمِ كَآفَّةٗ وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nЭй мўминлар, Исломга тўлиғича кирингиз! (Яъни, Исломнинг баъзи ҳукмларига итоат қилиб, баъзиларига итоат қилмайдиган кимсалардан бўлмангиз)! Ва шайтоннинг изидан эргашманглар! Шубҳасиз, у сизларга очиқ душмандир'}, {'chapter': 2, 'verse': 209, 'text': 'فَإِن زَلَلۡتُم مِّنۢ بَعۡدِ مَا جَآءَتۡكُمُ ٱلۡبَيِّنَٰتُ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ\nАгар сизларга очиқ ҳужжатлар келганидан кейин ҳам тойилсангиз, билингизки, албатта Аллоҳ қудратли, ҳикматли Зотдир'}, {'chapter': 2, 'verse': 210, 'text': 'هَلۡ يَنظُرُونَ إِلَّآ أَن يَأۡتِيَهُمُ ٱللَّهُ فِي ظُلَلٖ مِّنَ ٱلۡغَمَامِ وَٱلۡمَلَـٰٓئِكَةُ وَقُضِيَ ٱلۡأَمۡرُۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nУлар (Ҳақ йўлни инкор этувчилар Қиёмат Кунида) булутдан бўлган соябон остида Аллоҳ ва фаришталар (ҳисоб-китоб учун) келишини ва иш тамом бўлишини (яъни, улар куфрлари сабабли дўзахга ҳукм қилинишларини) кутяптиларми? Ваҳоланки, барча ишлар ёлғиз Аллоҳга қайтарилажак'}, {'chapter': 2, 'verse': 211, 'text': 'سَلۡ بَنِيٓ إِسۡرَـٰٓءِيلَ كَمۡ ءَاتَيۡنَٰهُم مِّنۡ ءَايَةِۭ بَيِّنَةٖۗ وَمَن يُبَدِّلۡ نِعۡمَةَ ٱللَّهِ مِنۢ بَعۡدِ مَا جَآءَتۡهُ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nБани Исроилдан сўранг (Эй Муҳаммад алайҳис-салоту вас-салом), уларга қанчалаб очиқ мўъжизалар бердик экан! Ким Аллоҳнинг неъмати келганидан сўнг, уни ўзгартирар экан, бас, албатта Аллоҳ жазоси қаттиқ Зотдир'}, {'chapter': 2, 'verse': 212, 'text': 'زُيِّنَ لِلَّذِينَ كَفَرُواْ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَيَسۡخَرُونَ مِنَ ٱلَّذِينَ ءَامَنُواْۘ وَٱلَّذِينَ ٱتَّقَوۡاْ فَوۡقَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ\nКуфр йўлини тутган кимсалар учун бу ҳаёти дунё зийнатлаб қўйилди. Улар иймон эгаларининг устларидан куладилар. Ҳолбуки, Қиёмат Кунида тақводор зотлар улардан баланддир. Аллоҳ Ўзи истаган кишиларга ҳисобсиз ризқ беради'}, {'chapter': 2, 'verse': 213, 'text': 'كَانَ ٱلنَّاسُ أُمَّةٗ وَٰحِدَةٗ فَبَعَثَ ٱللَّهُ ٱلنَّبِيِّـۧنَ مُبَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ ٱلۡكِتَٰبَ بِٱلۡحَقِّ لِيَحۡكُمَ بَيۡنَ ٱلنَّاسِ فِيمَا ٱخۡتَلَفُواْ فِيهِۚ وَمَا ٱخۡتَلَفَ فِيهِ إِلَّا ٱلَّذِينَ أُوتُوهُ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ بَغۡيَۢا بَيۡنَهُمۡۖ فَهَدَى ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ لِمَا ٱخۡتَلَفُواْ فِيهِ مِنَ ٱلۡحَقِّ بِإِذۡنِهِۦۗ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٍ\nОдамлар бир миллат эдилар. Сўнг (ораларида келишмовчиликлар пайдо бўлгач), Аллоҳ (мўминларга) хушхабар элтгувчи ва (кофирларни жаҳаннам азобидан) қўрқитувчи пайғамбарларини юборди. Ва уларга қўшиб одамлар орасида чиққан тортишувларга ҳакам бўлсин, деб ҳақ китобни (Таврот, Инжилни) нозил қилди. Энди очиқ оятлар келганидан кейин, фақат шу китоб берилган кимсалар ҳадларидан ошиб, талашиб-тортишдилар. Сўнгра Аллоҳ Ўз изни билан мўминларни улар (китоб берилган кимсалар) ихтилоф қилган ҳақ йўлга йўллади. Аллоҳ Ўзи истаган кишиларни Тўғри Йўлга ҳидоят қилади'}, {'chapter': 2, 'verse': 214, 'text': 'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ ٱلۡجَنَّةَ وَلَمَّا يَأۡتِكُم مَّثَلُ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۖ مَّسَّتۡهُمُ ٱلۡبَأۡسَآءُ وَٱلضَّرَّآءُ وَزُلۡزِلُواْ حَتَّىٰ يَقُولَ ٱلرَّسُولُ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ مَتَىٰ نَصۡرُ ٱللَّهِۗ أَلَآ إِنَّ نَصۡرَ ٱللَّهِ قَرِيبٞ\nЁки (эй мўминлар), сизлардан илгари ўтган зотлар мисоли-ибрати сизларга келмай туриб жаннатга киришни ўйладингизми? Уларга бало ва мусибатлар (устма-уст) келиб, шундай ларзага тушган эдиларки, ҳатто пайғамбар ва иймонли кишилар: «Ахир қачон Аллоҳнинг ёрдами келади?» дейишган эди. (Шунда) уларга бундай жавоб бўлган эди: «Огоҳ бўлингизким, Аллоҳнинг ёрдами яқиндир»'}, {'chapter': 2, 'verse': 215, 'text': 'يَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلۡ مَآ أَنفَقۡتُم مِّنۡ خَيۡرٖ فَلِلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٞ\nСиздан (Эй Муҳаммад алайҳис-салоту вас-салом), нималарни эҳсон қилишни сўрайдилар. Айтинг: «Қандай яхшилик қилсангиз, ота-онага, қариндош-уруғга, етим-мискинларга ва йўловчи-мусофирларга қилингиз. Қандай яхшилик қилсангиз, шубҳасиз, Аллоҳ уни билгувчидир»'}, {'chapter': 2, 'verse': 216, 'text': 'كُتِبَ عَلَيۡكُمُ ٱلۡقِتَالُ وَهُوَ كُرۡهٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَهُوَ خَيۡرٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تُحِبُّواْ شَيۡـٔٗا وَهُوَ شَرّٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nСизларга ёқмаса-да, жанг қилишингиз фарз қилинди. (Зотан,) сизлар ўзингиз учун яхши бўлган нарсани ёқтирмаслигингиз ва сиз учун ёмон бўлган нарсани яхши кўришингиз мумкин. Аллоҳ билур, сизлар билмассиз'}, {'chapter': 2, 'verse': 217, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلشَّهۡرِ ٱلۡحَرَامِ قِتَالٖ فِيهِۖ قُلۡ قِتَالٞ فِيهِ كَبِيرٞۚ وَصَدٌّ عَن سَبِيلِ ٱللَّهِ وَكُفۡرُۢ بِهِۦ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَإِخۡرَاجُ أَهۡلِهِۦ مِنۡهُ أَكۡبَرُ عِندَ ٱللَّهِۚ وَٱلۡفِتۡنَةُ أَكۡبَرُ مِنَ ٱلۡقَتۡلِۗ وَلَا يَزَالُونَ يُقَٰتِلُونَكُمۡ حَتَّىٰ يَرُدُّوكُمۡ عَن دِينِكُمۡ إِنِ ٱسۡتَطَٰعُواْۚ وَمَن يَرۡتَدِدۡ مِنكُمۡ عَن دِينِهِۦ فَيَمُتۡ وَهُوَ كَافِرٞ فَأُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nСиздан «шаҳри ҳаром» — уруш ҳаром бўлган ойда жанг қилиш ҳақида сўрайдилар. Айтинг: «У ойда жанг қилиш катта гуноҳдир. Аллоҳнинг йўлидан тўсиш, Унга ишонмаслик ва Масжидул-Ҳаромдан (яъни, Маккадан) тўсиш ва ундан аҳлларини қувиб чиқариш. Аллоҳ наздида улуғроқ гуноҳдир. Фитна (алдаш) ўлдирищдан-да каттароқ гуноҳдир. Улар (кофирлар) қўлларидан келса то динингиздан қайтаргунларича сизлар билан уришаверадилар. Сизлардан ким ўз динидан қайтиб, динсиз ҳолда ўлса, ундай кимсаларнинг қилган амаллари дунёю Охиратда беҳуда кетур. Улар дўзах эгаларидир ва унда абадий қолажаклар'}, {'chapter': 2, 'verse': 218, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ أُوْلَـٰٓئِكَ يَرۡجُونَ رَحۡمَتَ ٱللَّهِۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nИймон келтирган, (ватанларидан) ҳижрат қилган, Аллоҳ йўлида курашган зотлар — ана ўшалар Аллоҳнинг раҳматидан умидвордирлар. Аллоҳ мағфират ва раҳм-шафқат қилгувчидир'}, {'chapter': 2, 'verse': 219, 'text': '۞يَسۡـَٔلُونَكَ عَنِ ٱلۡخَمۡرِ وَٱلۡمَيۡسِرِۖ قُلۡ فِيهِمَآ إِثۡمٞ كَبِيرٞ وَمَنَٰفِعُ لِلنَّاسِ وَإِثۡمُهُمَآ أَكۡبَرُ مِن نَّفۡعِهِمَاۗ وَيَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلِ ٱلۡعَفۡوَۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ\nСиздан ароқ (ичкилик) ва қимор ҳақида сўрайдилар. Айтинг: «Буларда катта гуноҳ ва одамлар учун фойдалар бор. Буларнинг гуноҳи фойдасидан каттароқдир». Ва Сиздан (эй Муҳаммад алайҳис-салоту вас-салом), нималарни эҳсон қилишни сўрайдилар. (Ўзингиздан) ортганини, деб жавоб қилинг! Шундай қилиб, тафаккур қилишингиз учун Аллоҳ сизларга ўз оятларини баён этади'}, {'chapter': 2, 'verse': 220, 'text': 'فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۗ وَيَسۡـَٔلُونَكَ عَنِ ٱلۡيَتَٰمَىٰۖ قُلۡ إِصۡلَاحٞ لَّهُمۡ خَيۡرٞۖ وَإِن تُخَالِطُوهُمۡ فَإِخۡوَٰنُكُمۡۚ وَٱللَّهُ يَعۡلَمُ ٱلۡمُفۡسِدَ مِنَ ٱلۡمُصۡلِحِۚ وَلَوۡ شَآءَ ٱللَّهُ لَأَعۡنَتَكُمۡۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nДунё ва Охират ҳақида (тафаккур қилишингиз учун Аллоҳ сизларга ўз оятларини баён этади). Сиздан етимларнинг (моллари) ҳақида сўрайдилар. Айтинг: «Уларни ислоҳ қилиш яхшидир. Агар (молларингизни) уларнинг (моллари) билан қўшиб юборсангиз (зарари йўқ). Зеро, улар биродарларингиздир. Аллоҳ ким бузғунчи, ким ўнгловчи — ислоҳ қилувчи эканини билади. Агар истаса, сизларни машаққатга солган бўлур эди. Албатта, Аллоҳ қудрат ва ҳикмат эгасидир'}, {'chapter': 2, 'verse': 221, 'text': 'وَلَا تَنكِحُواْ ٱلۡمُشۡرِكَٰتِ حَتَّىٰ يُؤۡمِنَّۚ وَلَأَمَةٞ مُّؤۡمِنَةٌ خَيۡرٞ مِّن مُّشۡرِكَةٖ وَلَوۡ أَعۡجَبَتۡكُمۡۗ وَلَا تُنكِحُواْ ٱلۡمُشۡرِكِينَ حَتَّىٰ يُؤۡمِنُواْۚ وَلَعَبۡدٞ مُّؤۡمِنٌ خَيۡرٞ مِّن مُّشۡرِكٖ وَلَوۡ أَعۡجَبَكُمۡۗ أُوْلَـٰٓئِكَ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَٱللَّهُ يَدۡعُوٓاْ إِلَى ٱلۡجَنَّةِ وَٱلۡمَغۡفِرَةِ بِإِذۡنِهِۦۖ وَيُبَيِّنُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nТо иймонга келмагунларича мушрика аёлларга уйланмангиз! Шубҳасиз, озод мушрика аёлдан — гарчи у сизга ёқса-да, — иймонли чўри яхшироқдир. То иймонга келмагунларича мушрик эркакларни (қизларингизга) уйламангиз! Шубҳасиз, озод мушрикдан — гарчи у сизга ёқса-да, — иймонли қул яхшироқдир. Улар (мушриклар) дўзахга чақирадилар. Аллоҳ Ўз изни-иродаси билан жаннат ва мағфиратга даъват қилади. Ва одамлар эслатма олишлари учун Ўз оятларини баён қилади'}, {'chapter': 2, 'verse': 222, 'text': 'وَيَسۡـَٔلُونَكَ عَنِ ٱلۡمَحِيضِۖ قُلۡ هُوَ أَذٗى فَٱعۡتَزِلُواْ ٱلنِّسَآءَ فِي ٱلۡمَحِيضِ وَلَا تَقۡرَبُوهُنَّ حَتَّىٰ يَطۡهُرۡنَۖ فَإِذَا تَطَهَّرۡنَ فَأۡتُوهُنَّ مِنۡ حَيۡثُ أَمَرَكُمُ ٱللَّهُۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلتَّوَّـٰبِينَ وَيُحِبُّ ٱلۡمُتَطَهِّرِينَ\nСиздан ҳайз ҳақида сўрайдилар. Айтинг: «У кўнгилсиз-нопок нарсадир. Бас, ҳайз пайтида аёлларингиздан четланингиз ва то покланмагунларича уларга яқинлашмангиз! Пок бўлганларидан кейин уларга Аллоҳ буюрган тарафдан келингиз! Албатта, Аллоҳ тавба қилгувчиларни ва ўзларини мудом пок тутгувчиларни севади»'}, {'chapter': 2, 'verse': 223, 'text': 'نِسَآؤُكُمۡ حَرۡثٞ لَّكُمۡ فَأۡتُواْ حَرۡثَكُمۡ أَنَّىٰ شِئۡتُمۡۖ وَقَدِّمُواْ لِأَنفُسِكُمۡۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّكُم مُّلَٰقُوهُۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nХотинларингиз сизлар учун экинзордир. Бас, экинзорингизга хоҳлаган ҳолатингизда яқинлашаверинг ва ўзингиз учун яхши амаллар тақдим қилинг! Аллоҳдан қўрқингиз ва билингизким, сизлар Унга рўбарў бўлгувчисиз. (Эй Муҳаммад алайҳис-салоту вас-салом), иймонли кишиларга (жаннат) хушхабарини етказинг'}, {'chapter': 2, 'verse': 224, 'text': 'وَلَا تَجۡعَلُواْ ٱللَّهَ عُرۡضَةٗ لِّأَيۡمَٰنِكُمۡ أَن تَبَرُّواْ وَتَتَّقُواْ وَتُصۡلِحُواْ بَيۡنَ ٱلنَّاسِۚ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nЯхшилик қилиш, тақводор бўлиш ва одамлар орасини ислоҳ қилиш (каби амаллардан тўсувчи бўлган) қасамларингиз учун Аллоҳни баҳона қилмангиз! Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 2, 'verse': 225, 'text': 'لَّا يُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا كَسَبَتۡ قُلُوبُكُمۡۗ وَٱللَّهُ غَفُورٌ حَلِيمٞ\nАллоҳ сизларни тилларингиздаги беҳуда қасамларингиз сабабли жазоламайди. Балки дилларингиз билан қилган нарсангиз (қасамингиз) билан жазолайди. Аллоҳ мағфиратли, ҳалим Зотдир'}, {'chapter': 2, 'verse': 226, 'text': 'لِّلَّذِينَ يُؤۡلُونَ مِن نِّسَآئِهِمۡ تَرَبُّصُ أَرۡبَعَةِ أَشۡهُرٖۖ فَإِن فَآءُو فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nХотинларидан қасам билан юз ўгирган кишилар учун тўрт ой муҳлат бор. Агар (шу муддат ичида хотинларига) қайтсалар (жоиздир). Зеро, Аллоҳ шубҳасиз, мағфиратли, раҳмлидир'}, {'chapter': 2, 'verse': 227, 'text': 'وَإِنۡ عَزَمُواْ ٱلطَّلَٰقَ فَإِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nВа агар талоқ қилишни қасд қилган бўлсалар, бас, албатта Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 2, 'verse': 228, 'text': 'وَٱلۡمُطَلَّقَٰتُ يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ ثَلَٰثَةَ قُرُوٓءٖۚ وَلَا يَحِلُّ لَهُنَّ أَن يَكۡتُمۡنَ مَا خَلَقَ ٱللَّهُ فِيٓ أَرۡحَامِهِنَّ إِن كُنَّ يُؤۡمِنَّ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنۡ أَرَادُوٓاْ إِصۡلَٰحٗاۚ وَلَهُنَّ مِثۡلُ ٱلَّذِي عَلَيۡهِنَّ بِٱلۡمَعۡرُوفِۚ وَلِلرِّجَالِ عَلَيۡهِنَّ دَرَجَةٞۗ وَٱللَّهُ عَزِيزٌ حَكِيمٌ\nТалоқ қилинган аёллар уч ҳайз муддатида ўзларини кузатадилар. Агар Аллоҳга ва Охират Кунига ишонсалар, улар учун Аллоҳ бачадонларида яратган нарсани (ҳомилани ёки ҳайзни) яширишлари ҳалол бўлмайди. Агар эрлари ислоҳни (оилани тиклашни) истасалар, шу муддат ичида уларни қайтариб (никоҳларига) олишга ҳақлидирлар. Ва яхши амалларда улар (аёллар) учун зиммаларидаги эрлари олдидаги бурчлари баробарида ҳуқуқлари ҳам бор. Ва эркаклар учун (аёллар) устида бир даража (имтиёз) бор. Аллоҳ қудрат ва ҳикмат эгасидир'}, {'chapter': 2, 'verse': 229, 'text': 'ٱلطَّلَٰقُ مَرَّتَانِۖ فَإِمۡسَاكُۢ بِمَعۡرُوفٍ أَوۡ تَسۡرِيحُۢ بِإِحۡسَٰنٖۗ وَلَا يَحِلُّ لَكُمۡ أَن تَأۡخُذُواْ مِمَّآ ءَاتَيۡتُمُوهُنَّ شَيۡـًٔا إِلَّآ أَن يَخَافَآ أَلَّا يُقِيمَا حُدُودَ ٱللَّهِۖ فَإِنۡ خِفۡتُمۡ أَلَّا يُقِيمَا حُدُودَ ٱللَّهِ فَلَا جُنَاحَ عَلَيۡهِمَا فِيمَا ٱفۡتَدَتۡ بِهِۦۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَعۡتَدُوهَاۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\n(Эр хотинини қайта никоҳига олиши мумкин бўлган) талоқ икки мартадир. Сўнгра (оилани) яхшилик билан сақлаш ёки чиройли суратда ажрашиш (лозим). Ва сизлар учун уларга (хотинларингизга) берган нарсаларингизда (уларнинг маҳрларидан) бирон нарсани қайтариб олишингиз дуруст бўлмайди. Фақат эр-хотин Аллоҳ буюрган (оилавий турмуш борасидаги) қонун-қоидаларни адо қила олмасликларидан қўрқсаларгина (дурустдир). Бас, агар уларнинг (эр-хотиннинг) Аллоҳ буюрган қонун-қоидаларни адо қила олмасликларидан қўрқсангизлар, у ҳолда хотин (эридан ажрашиш учун) эваз қилиб берган нарсада (яъни, хотиннинг эваз беришида, эрнинг шу эвазни олишида) улар учун гуноҳ йўқдир. Бу Аллоҳнинг қонун-чегараларидир. Бас, улардан тажовуз қилиб ўтмангиз! Ким Аллоҳ чегараларидан тажовуз қилиб ўтса, ана ўшалар золимлардир'}, {'chapter': 2, 'verse': 230, 'text': 'فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُۥ مِنۢ بَعۡدُ حَتَّىٰ تَنكِحَ زَوۡجًا غَيۡرَهُۥۗ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يَتَرَاجَعَآ إِن ظَنَّآ أَن يُقِيمَا حُدُودَ ٱللَّهِۗ وَتِلۡكَ حُدُودُ ٱللَّهِ يُبَيِّنُهَا لِقَوۡمٖ يَعۡلَمُونَ\nЭнди агар уни (учинчи марта) талоқ қилса, у аёл то бошқа эр билан турмуш қурмагунича аввалги эрига ҳалол бўлмайди. Сўнг агар (кейинги эр ҳам) талоқ қилса, бу ҳолда Аллоҳ буюрган қонун-қоидаларни адо қилишларига ишонсалар, (у хотин аввалги эри билан) бир-бирларига қайтишларининг, яъни қайта турмуш қуришларининг гуноҳи йўқдир. Булар Аллоҳнинг чегараларидирки, буларни биладиган қавм учун баён қилади'}, {'chapter': 2, 'verse': 231, 'text': 'وَإِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ سَرِّحُوهُنَّ بِمَعۡرُوفٖۚ وَلَا تُمۡسِكُوهُنَّ ضِرَارٗا لِّتَعۡتَدُواْۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ وَلَا تَتَّخِذُوٓاْ ءَايَٰتِ ٱللَّهِ هُزُوٗاۚ وَٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ وَمَآ أَنزَلَ عَلَيۡكُم مِّنَ ٱلۡكِتَٰبِ وَٱلۡحِكۡمَةِ يَعِظُكُم بِهِۦۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nҚачон хотинларингизни талоқ қилсангизлар ва уларнинг идда муддатлари битиб қолса, бас, уларни яхшилик билан олиб қолинг ёки яхшилик билан кузатинг. Уларга зулм қилиш билан зарар етказиш учун ушлаб турмангиз. Ким шундай қилса, ўзига зулм қилибди. Аллоҳнинг оятларини ҳазил деб билманглар! Аллоҳнинг сизларга берган неъматини ва сизларга ваъз-насиҳат этиб нозил қилган Китоб ва (у Китобдаги) ҳикматдан иборат нарсани ёдингизда тутингиз! Аллоҳдан қўрқингиз ва билингизким, албатта Аллоҳ ҳамма нарсани билгувчидир'}, {'chapter': 2, 'verse': 232, 'text': 'وَإِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَلَا تَعۡضُلُوهُنَّ أَن يَنكِحۡنَ أَزۡوَٰجَهُنَّ إِذَا تَرَٰضَوۡاْ بَيۡنَهُم بِٱلۡمَعۡرُوفِۗ ذَٰلِكَ يُوعَظُ بِهِۦ مَن كَانَ مِنكُمۡ يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۗ ذَٰلِكُمۡ أَزۡكَىٰ لَكُمۡ وَأَطۡهَرُۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nҚачон хотинларингизни талоқ қилсангизлар ва уларнинг идда муддатлари битиб қолса, бас, (эй ота-оналар,) уларни ўзаро яхшилик билан келишишган бўлсалар, эрларига қайта никоҳланишдан тўсманг! Бу ҳукмлардан сизларнинг ораларингиздаги Аллоҳга ва Охират Кунига ишонадиган зотлар ваъз-насиҳат оладилар. Бу ҳукм (яъни, эр-хотинни ярашиб олишларидан тўсмаслик) сизлар учун энг тоза ва покловчи ҳукмдир. Аллоҳ билур, сизлар билмассиз'}, {'chapter': 2, 'verse': 233, 'text': '۞وَٱلۡوَٰلِدَٰتُ يُرۡضِعۡنَ أَوۡلَٰدَهُنَّ حَوۡلَيۡنِ كَامِلَيۡنِۖ لِمَنۡ أَرَادَ أَن يُتِمَّ ٱلرَّضَاعَةَۚ وَعَلَى ٱلۡمَوۡلُودِ لَهُۥ رِزۡقُهُنَّ وَكِسۡوَتُهُنَّ بِٱلۡمَعۡرُوفِۚ لَا تُكَلَّفُ نَفۡسٌ إِلَّا وُسۡعَهَاۚ لَا تُضَآرَّ وَٰلِدَةُۢ بِوَلَدِهَا وَلَا مَوۡلُودٞ لَّهُۥ بِوَلَدِهِۦۚ وَعَلَى ٱلۡوَارِثِ مِثۡلُ ذَٰلِكَۗ فَإِنۡ أَرَادَا فِصَالًا عَن تَرَاضٖ مِّنۡهُمَا وَتَشَاوُرٖ فَلَا جُنَاحَ عَلَيۡهِمَاۗ وَإِنۡ أَرَدتُّمۡ أَن تَسۡتَرۡضِعُوٓاْ أَوۡلَٰدَكُمۡ فَلَا جُنَاحَ عَلَيۡكُمۡ إِذَا سَلَّمۡتُم مَّآ ءَاتَيۡتُم بِٱلۡمَعۡرُوفِۗ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ\nОналар болаларини тўла икки йил эмизадилар. (Бу ҳукм) эмизишни бенуқсон қилмоқчи бўлган кишилар учундир. Уларни (яъни, оналарни) яхшилик билан едириб-кийдириш отанинг зиммасидадир. Ҳеч кимга тоқатидан ташқари нарса таклиф қилинмайди. Болалари сабабли ота ҳам, она ҳам зарар тортмасин. Меросхўр — ворис зиммасида ҳам худди шундай (бурч) бор. Агар (ота-она) ўзаро келишиб, маслаҳат билан болани сутдан ажратмоқчи бўлсалар, гуноҳкор бўлмайдилар. Агар болаларингизни (эмизувчи энагага) эмиздирмоқчи бўлсангиз, берадиган ҳақни яхшилик билан тўла-тўкис тўлаб турсангиз, гуноҳкор бўлмайсиз. Аллоҳдан қўрқингиз ва билингизки, албатта Аллоҳ қилаётган амалларингизни кўргувчидир'}, {'chapter': 2, 'verse': 234, 'text': 'وَٱلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ أَرۡبَعَةَ أَشۡهُرٖ وَعَشۡرٗاۖ فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ بِٱلۡمَعۡرُوفِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nСизлардан (мусулмонлардан) вафот қилган кишилар хотинларини қолдирган бўлсалар, улар тўрт ою ўн кун ўзларига қараб (идда сақлаб) турадилар. Энди қачон (идда) муддатдари битса, улар ўз хусусларида яхшилик билан қилган (бошқа турмуш қуриш хусусидаги) ишларида сизлар (яъни, ўлган эрининг уруғлари) учун гуноҳ йўқдир. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 2, 'verse': 235, 'text': 'وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا عَرَّضۡتُم بِهِۦ مِنۡ خِطۡبَةِ ٱلنِّسَآءِ أَوۡ أَكۡنَنتُمۡ فِيٓ أَنفُسِكُمۡۚ عَلِمَ ٱللَّهُ أَنَّكُمۡ سَتَذۡكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّآ أَن تَقُولُواْ قَوۡلٗا مَّعۡرُوفٗاۚ وَلَا تَعۡزِمُواْ عُقۡدَةَ ٱلنِّكَاحِ حَتَّىٰ يَبۡلُغَ ٱلۡكِتَٰبُ أَجَلَهُۥۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِيٓ أَنفُسِكُمۡ فَٱحۡذَرُوهُۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَفُورٌ حَلِيمٞ\nУ аёлларга (яъни, эрлари ўлиб, идда сақлаб турган аёлларга) совчиликни ишора қилишингизда ёки ичларингизда (совчилик ҳақида) ўйлашингизда сизлар учун гуноҳ йўқ. Аллоҳ сизлар у аёлларга (совчилик ҳақида) зикр қилишингизни билди. Лекин улар билан хуфёна ваъдалашмангиз! Фақат яхши гаплар гапиришингиз (жоиздир). То идда муддати битмагунча никоҳ боғлашга қасд қилманг! Ва билингизки, албатта Аллоҳ ичларингиздаги нарсани билади. Бас, Унинг (азобидан) эҳтиёт бўлинг! Ва билингизки, албатта Аллоҳ мағфиратли, ҳалимдир'}, {'chapter': 2, 'verse': 236, 'text': 'لَّا جُنَاحَ عَلَيۡكُمۡ إِن طَلَّقۡتُمُ ٱلنِّسَآءَ مَا لَمۡ تَمَسُّوهُنَّ أَوۡ تَفۡرِضُواْ لَهُنَّ فَرِيضَةٗۚ وَمَتِّعُوهُنَّ عَلَى ٱلۡمُوسِعِ قَدَرُهُۥ وَعَلَى ٱلۡمُقۡتِرِ قَدَرُهُۥ مَتَٰعَۢا بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُحۡسِنِينَ\nАгар хотинларингизни уларга қўл текизмай туриб ёки улар учун маҳр белгиламай туриб талоқ қилсангиз (ҳам) сизлар учун гуноҳ йўқдир. Ва (бу ҳолда) уларни бой борича, йўқ ҳолича яхшилик қилиш билан фойдалантиринг! (Бу ҳукм) яхшилик қилгувчилар зиммасига бурч бўлди'}, {'chapter': 2, 'verse': 237, 'text': 'وَإِن طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ وَقَدۡ فَرَضۡتُمۡ لَهُنَّ فَرِيضَةٗ فَنِصۡفُ مَا فَرَضۡتُمۡ إِلَّآ أَن يَعۡفُونَ أَوۡ يَعۡفُوَاْ ٱلَّذِي بِيَدِهِۦ عُقۡدَةُ ٱلنِّكَاحِۚ وَأَن تَعۡفُوٓاْ أَقۡرَبُ لِلتَّقۡوَىٰۚ وَلَا تَنسَوُاْ ٱلۡفَضۡلَ بَيۡنَكُمۡۚ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٌ\nАгар уларни маҳрни белгилаб қўйган ҳолингизда қўл тегизишдан илгари талоқ қилсангизлар, белгиланган маҳрнинг ярми (зиммангиздадир). Магар улар (ярим маҳрни олишдан) кечиб юборса ёки никоҳ қўлида бўлган зот (яъни, эр ўзига қайтган ярим маҳрдан) кечса, (яъни, бутун маҳрни тўласа жоиздир). Кечиб юборишингиз тақвога яқинроқ ишдир. Ўзаро бир-бирларингизга фазлу карам қилишни унутмангиз! Албатта, Аллоҳ қилаётган амалларингизни кўргувчидир'}, {'chapter': 2, 'verse': 238, 'text': 'حَٰفِظُواْ عَلَى ٱلصَّلَوَٰتِ وَٱلصَّلَوٰةِ ٱلۡوُسۡطَىٰ وَقُومُواْ لِلَّهِ قَٰنِتِينَ\nБарча намозларни ва хусусан ўрта намозни (аср намозини) сақланглар — ўз вақтларида адо қилинглар! Ва Аллоҳ учун бўйсунган ҳолда туринглар'}, {'chapter': 2, 'verse': 239, 'text': 'فَإِنۡ خِفۡتُمۡ فَرِجَالًا أَوۡ رُكۡبَانٗاۖ فَإِذَآ أَمِنتُمۡ فَٱذۡكُرُواْ ٱللَّهَ كَمَا عَلَّمَكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ\nЭнди агар (душман ҳужумидан) хавфда қолсангиз, пиёда ёки отлиқ ҳолингизда (ибодат қилаверингиз!) Қачон хотиржам бўлганингизда, билмаган вақтингизда Аллоҳ қандай ўргатган бўлса, шундай ҳолда (яъни, намознинг барча арконларини ўрнига қўйиб) Аллоҳни зикр қилингиз'}, {'chapter': 2, 'verse': 240, 'text': 'وَٱلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا وَصِيَّةٗ لِّأَزۡوَٰجِهِم مَّتَٰعًا إِلَى ٱلۡحَوۡلِ غَيۡرَ إِخۡرَاجٖۚ فَإِنۡ خَرَجۡنَ فَلَا جُنَاحَ عَلَيۡكُمۡ فِي مَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ مِن مَّعۡرُوفٖۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ\nСизлардан вафот қилган кишилар хотинларини қолдирган бўлсалар (ўлимларидан олдин), хотинларига бир йилгача (эрнинг уйидан) чиқарилмай фойдаланадиган миқдордаги нарсани васият қилсинлар. Агар улар (хотинлар) ўзлари чиқиб кетсалар, сизлар (эрнинг уруғлари) учун у хотинлар ўз хусусларида қилган амаллари сабабли гуноҳ йўқдир. Аллоҳ қудратли, ҳикматли Зотдир'}, {'chapter': 2, 'verse': 241, 'text': 'وَلِلۡمُطَلَّقَٰتِ مَتَٰعُۢ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِينَ\nТалоқ қилинган аёлларни яхшилик билан фойдалантириш тақводор эрларнинг зиммасидаги бурчдир'}, {'chapter': 2, 'verse': 242, 'text': 'كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\nШундай қилиб, ақл юргизишингиз учун Аллоҳ сизларга Ўз оятларини баён қилади'}, {'chapter': 2, 'verse': 243, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ خَرَجُواْ مِن دِيَٰرِهِمۡ وَهُمۡ أُلُوفٌ حَذَرَ ٱلۡمَوۡتِ فَقَالَ لَهُمُ ٱللَّهُ مُوتُواْ ثُمَّ أَحۡيَٰهُمۡۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ\nЎлимдан (яъни, вабога йўлиқишдан) қочиб, ўз диёрларидан чиққан мингларча кишининг ҳоли-хабарини билмадингизми? Бас, Аллоҳ уларга «Ўлинглар!», деди. (Ва улар ўлдилар. Яъни ўлимнинг сабаби касаллик эмас, балки Аллоҳнинг амридир). Сўнгра уларга ҳаёт ато қилди. Албатта, Аллоҳ одамларга фазлу карам қилгувчидир. Лекин жуда кўп одамлар шукр қилмайдилар'}, {'chapter': 2, 'verse': 244, 'text': 'وَقَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nАллоҳ йўлида жанг қилингиз ва билингизки, албатта Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 2, 'verse': 245, 'text': 'مَّن ذَا ٱلَّذِي يُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفَهُۥ لَهُۥٓ أَضۡعَافٗا كَثِيرَةٗۚ وَٱللَّهُ يَقۡبِضُ وَيَبۡصُۜطُ وَإِلَيۡهِ تُرۡجَعُونَ\nАллоҳга қарзи ҳасана (ихтиёрий қарз) берадиган ким борки, (Аллоҳ) унга бир неча баробар қилиб қайтарса. Ҳолбуки, (ризқингизни) тор ва кенг қиладиган Аллоҳдир. Ва Унга қайтарилажаксиз'}, {'chapter': 2, 'verse': 246, 'text': 'أَلَمۡ تَرَ إِلَى ٱلۡمَلَإِ مِنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ مِنۢ بَعۡدِ مُوسَىٰٓ إِذۡ قَالُواْ لِنَبِيّٖ لَّهُمُ ٱبۡعَثۡ لَنَا مَلِكٗا نُّقَٰتِلۡ فِي سَبِيلِ ٱللَّهِۖ قَالَ هَلۡ عَسَيۡتُمۡ إِن كُتِبَ عَلَيۡكُمُ ٱلۡقِتَالُ أَلَّا تُقَٰتِلُواْۖ قَالُواْ وَمَا لَنَآ أَلَّا نُقَٰتِلَ فِي سَبِيلِ ٱللَّهِ وَقَدۡ أُخۡرِجۡنَا مِن دِيَٰرِنَا وَأَبۡنَآئِنَاۖ فَلَمَّا كُتِبَ عَلَيۡهِمُ ٱلۡقِتَالُ تَوَلَّوۡاْ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nБани Исроилдан бўлган, Мусодан кейинги бир жамоатнинг ҳоли-хабарини билмадингизми? Қайсики, улар ўзларининг (Шамъун исмли) пайғамбарларига: «Биз учун бир подшоҳ тайинлагин, биз (у билан бирга) Аллоҳ йўлида жанг қилайлик», дейишди. «Агар сизларга уруш фарз қилинса, эҳтимол урушмассиз?», деди (пайғамбар). Улар айтдилар: «Диёримиздан ҳайдалиб, болаларимиздан (ажралиб) турибмиз-ку, нега Аллоҳ йўлида урушмас эканмиз?» Қачонки уларга уруш фарз қилинганда озгина кишидан ташқари ҳаммалари бош тортдилар. Аллоҳ зулм қилгувчиларни билгувчидир'}, {'chapter': 2, 'verse': 247, 'text': 'وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ٱللَّهَ قَدۡ بَعَثَ لَكُمۡ طَالُوتَ مَلِكٗاۚ قَالُوٓاْ أَنَّىٰ يَكُونُ لَهُ ٱلۡمُلۡكُ عَلَيۡنَا وَنَحۡنُ أَحَقُّ بِٱلۡمُلۡكِ مِنۡهُ وَلَمۡ يُؤۡتَ سَعَةٗ مِّنَ ٱلۡمَالِۚ قَالَ إِنَّ ٱللَّهَ ٱصۡطَفَىٰهُ عَلَيۡكُمۡ وَزَادَهُۥ بَسۡطَةٗ فِي ٱلۡعِلۡمِ وَٱلۡجِسۡمِۖ وَٱللَّهُ يُؤۡتِي مُلۡكَهُۥ مَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nПайғамбарлари уларга айтди: «Албатта, Аллоҳ сизларга Толутни подшоҳ қилиб тайинлади». «У қаёқдан бизга подшоҳлик қилсин, ахир биз подшоҳликка ундан ҳақлироқмиз-ку, қолаверса, унга кенг-катта мол-давлат ато қилинмаган бўлса», дейишди. Пайғамбар айтди: «Албатта, Аллоҳ уни сизларнинг устингизга сайлади ва унга илмда ва жисмда қувватни зиёда қилди. Аллоҳ мулкини Ўзи истаган кишига беради». Аллоҳ (фазлу карами) кенг, билгувчидир'}, {'chapter': 2, 'verse': 248, 'text': 'وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ءَايَةَ مُلۡكِهِۦٓ أَن يَأۡتِيَكُمُ ٱلتَّابُوتُ فِيهِ سَكِينَةٞ مِّن رَّبِّكُمۡ وَبَقِيَّةٞ مِّمَّا تَرَكَ ءَالُ مُوسَىٰ وَءَالُ هَٰرُونَ تَحۡمِلُهُ ٱلۡمَلَـٰٓئِكَةُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nПайғамбарлари уларга айтди: «Унинг подшоҳ бўлиб сайланганлигининг аломати — сизларга бир сандиқ келишидирки, унда Парвардигорингиз томонидан хотиржамлик ва Мусо ва Ҳорун оилаларидан қолган мерос жо қилинган бўлиб, фаришталар кўтариб турадилар. Агар чиндан иймон эгалари бўлсангиз, шубҳасиз, бу воқеада сизлар учун оят-аломат бордир'}, {'chapter': 2, 'verse': 249, 'text': 'فَلَمَّا فَصَلَ طَالُوتُ بِٱلۡجُنُودِ قَالَ إِنَّ ٱللَّهَ مُبۡتَلِيكُم بِنَهَرٖ فَمَن شَرِبَ مِنۡهُ فَلَيۡسَ مِنِّي وَمَن لَّمۡ يَطۡعَمۡهُ فَإِنَّهُۥ مِنِّيٓ إِلَّا مَنِ ٱغۡتَرَفَ غُرۡفَةَۢ بِيَدِهِۦۚ فَشَرِبُواْ مِنۡهُ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ فَلَمَّا جَاوَزَهُۥ هُوَ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ قَالُواْ لَا طَاقَةَ لَنَا ٱلۡيَوۡمَ بِجَالُوتَ وَجُنُودِهِۦۚ قَالَ ٱلَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ ٱللَّهِ كَم مِّن فِئَةٖ قَلِيلَةٍ غَلَبَتۡ فِئَةٗ كَثِيرَةَۢ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِينَ\nҚачонки Толут аскарлари билан (шаҳардан) чиққач, айтди: «Албатта, Аллоҳ сизларни бир дарё билан имтиҳон қилади. Бас, ким ундан сув ичса менинг (аскарим)дан эмас ва ким ундан ичмаса, у мендандир. Магар ким қўли билан фақат бир ҳовуч олса (жоиздир)». Бас, у дарёдан озгиналаридан бошқа ҳаммалари ичдилар. У иймон келтирган кишилар билан бирга дарёдан ўтгач, (аскарлар) айтдилар: «Энди бу кун Жолут ва унинг лашкарларига кучимиз етмайди». (Шунда) Аллоҳга рўбарў бўлишларига ишонадиган зотлар: «Қанчадан-қанча кичкина гуруҳлар Аллоҳнинг изни билан катта гуруҳлар устидан ғалаба қилган. Аллоҳ сабр қилгувчилар билан биргадир», дедилар'}, {'chapter': 2, 'verse': 250, 'text': 'وَلَمَّا بَرَزُواْ لِجَالُوتَ وَجُنُودِهِۦ قَالُواْ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nҚачонки Жолут ва унинг лашкарига рўбарў келганларида: «Парвардигоро, устимиздан сабру тоқат ёғдиргил, қадамларимизни собит қил ва Ўзинг бизни бу кофир қавм устига ғолиб қил!», дедилар'}, {'chapter': 2, 'verse': 251, 'text': 'فَهَزَمُوهُم بِإِذۡنِ ٱللَّهِ وَقَتَلَ دَاوُۥدُ جَالُوتَ وَءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ وَٱلۡحِكۡمَةَ وَعَلَّمَهُۥ مِمَّا يَشَآءُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّفَسَدَتِ ٱلۡأَرۡضُ وَلَٰكِنَّ ٱللَّهَ ذُو فَضۡلٍ عَلَى ٱلۡعَٰلَمِينَ\nБас, Аллоҳнинг изни билан уларни енгдилар. Ва (Толутнинг аскарларидан бўлган) Довуд Жолутни ўлдирди. Ва Аллоҳ унга (Довудга) подшоҳлик, пайғамбарлик ва Ўзи хоҳлаган нарсаларидан таълим берди. Агар Аллоҳ одамларнинг айримларини айримлари билан дафъ қилиб турмас экан, шубҳасиз, Ер фасодга дучор бўлади. Лекин Аллоҳ барча оламлар устида фазлу карам Соҳибидир'}, {'chapter': 2, 'verse': 252, 'text': 'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۚ وَإِنَّكَ لَمِنَ ٱلۡمُرۡسَلِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), булар Аллоҳнинг оятларидир. Уларни Сизга ҳаққирост тиловат қилмоқдамиз. Ва Сиз, шак-шубҳасиз, пайғамбарлардандирсиз'}, {'chapter': 2, 'verse': 253, 'text': '۞تِلۡكَ ٱلرُّسُلُ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۘ مِّنۡهُم مَّن كَلَّمَ ٱللَّهُۖ وَرَفَعَ بَعۡضَهُمۡ دَرَجَٰتٖۚ وَءَاتَيۡنَا عِيسَى ٱبۡنَ مَرۡيَمَ ٱلۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ ٱلۡقُدُسِۗ وَلَوۡ شَآءَ ٱللَّهُ مَا ٱقۡتَتَلَ ٱلَّذِينَ مِنۢ بَعۡدِهِم مِّنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ وَلَٰكِنِ ٱخۡتَلَفُواْ فَمِنۡهُم مَّنۡ ءَامَنَ وَمِنۡهُم مَّن كَفَرَۚ وَلَوۡ شَآءَ ٱللَّهُ مَا ٱقۡتَتَلُواْ وَلَٰكِنَّ ٱللَّهَ يَفۡعَلُ مَا يُرِيدُ\nЎша пайғамбарларнинг айримларини айримларидан афзал қилдик. Уларнинг ораларида Аллоҳ (бевосита) сўзлаган зотлар бор. Ва (уларнинг) айримларини (Аллоҳ) юксак даражаларга кўтарди. Ийсо бинни Марямга ҳужжатлар бердик ва уни Руҳул-Қудс (Жаброил) билан қувватлантирдик. Агар Аллоҳ хоҳлаганда у пайғамбарлардан кейин ўтган одамлар ҳужжатлар келганидан кейин уришишмаган бўлардилар. Аммо улар ихтилоф қилдилар. Бас, уларнинг орасида мўминлар ҳам бор, кофирлар ҳам. Агар Аллоҳ хоҳлаганда уришишмаган бўлардилар, лекин Аллоҳ Ўзи хоҳлаган ишини қилади'}, {'chapter': 2, 'verse': 254, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِمَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعٞ فِيهِ وَلَا خُلَّةٞ وَلَا شَفَٰعَةٞۗ وَٱلۡكَٰفِرُونَ هُمُ ٱلظَّـٰلِمُونَ\nЭй мўминлар, на олди-сотди, на ошна-оғайнигарчилик ва на шафоат — қўллов бўлмайдиган Кун келишидан илгари ҳаёти дунёда сизларга ризқ қилиб берган нарсаларимиздан инфоқ-эҳсон қилинглар! Кофир бўлган кимсаларгина зулм қилгувчилардир'}, {'chapter': 2, 'verse': 255, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡحَيُّ ٱلۡقَيُّومُۚ لَا تَأۡخُذُهُۥ سِنَةٞ وَلَا نَوۡمٞۚ لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ مَن ذَا ٱلَّذِي يَشۡفَعُ عِندَهُۥٓ إِلَّا بِإِذۡنِهِۦۚ يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۖ وَلَا يُحِيطُونَ بِشَيۡءٖ مِّنۡ عِلۡمِهِۦٓ إِلَّا بِمَا شَآءَۚ وَسِعَ كُرۡسِيُّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَۖ وَلَا يَـُٔودُهُۥ حِفۡظُهُمَاۚ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ\nАллоҳдан ўзга ҳеч қандай илоҳ йўқ. Фақат Унинг Ўзи бордир. У тирик ва абадий тургувчидир. Уни на мудроқ, на уйқу олмайди. Самовот ва Ердаги бор нарсалар Унингдир. Унинг ҳузурида ҳеч ким (бировни) Унинг изнисиз оқлай олмайди. У уларнинг (барча одамларнинг) олдиларидаги ва орқаларидаги бор нарсани билади. Ва улар У зотнинг илмидан фақат Ўзи истаган нарсаларнигина биладилар. Унинг курсиси осмонлар ва Ердан кенгдир. Ва уни осмонлар ва Ерни ҳифзу ҳимоятда сақлаб туриш қийнамайди. У энг юксак ва буюкдир'}, {'chapter': 2, 'verse': 256, 'text': 'لَآ إِكۡرَاهَ فِي ٱلدِّينِۖ قَد تَّبَيَّنَ ٱلرُّشۡدُ مِنَ ٱلۡغَيِّۚ فَمَن يَكۡفُرۡ بِٱلطَّـٰغُوتِ وَيُؤۡمِنۢ بِٱللَّهِ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰ لَا ٱنفِصَامَ لَهَاۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\nДинга зўрлаб (киритиш) йўқдир. (Зеро) ҳақ йўл залолатдан ажраб бўлди. Бас, ким шайтондан юз ўгириб, Аллоҳга иймон келтирса, у ҳеч ажраб кетмайдиган мустаҳкам ҳалқани ушлабди. Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 2, 'verse': 257, 'text': 'ٱللَّهُ وَلِيُّ ٱلَّذِينَ ءَامَنُواْ يُخۡرِجُهُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۖ وَٱلَّذِينَ كَفَرُوٓاْ أَوۡلِيَآؤُهُمُ ٱلطَّـٰغُوتُ يُخۡرِجُونَهُم مِّنَ ٱلنُّورِ إِلَى ٱلظُّلُمَٰتِۗ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nАллоҳ мўминларнинг дўстидир. Уларни қоронғу зулматлардан ёруғлик — нурга чиқаради. Кофирларнинг дўстлари эса шайтонлардир. Улар кофирларни ёруғлик-нурдан қоронғу-зулматларга чиқарадилар. Ана ўшалар жаҳаннам эгаларидир ва унда абадий қолажаклар'}, {'chapter': 2, 'verse': 258, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِي حَآجَّ إِبۡرَٰهِـۧمَ فِي رَبِّهِۦٓ أَنۡ ءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ إِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّيَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ قَالَ أَنَا۠ أُحۡيِۦ وَأُمِيتُۖ قَالَ إِبۡرَٰهِـۧمُ فَإِنَّ ٱللَّهَ يَأۡتِي بِٱلشَّمۡسِ مِنَ ٱلۡمَشۡرِقِ فَأۡتِ بِهَا مِنَ ٱلۡمَغۡرِبِ فَبُهِتَ ٱلَّذِي كَفَرَۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nАллоҳ подшоҳлик берганидан ҳовлиқиб Иброҳим билан Парвардигори ҳақида талашган кимсанинг (Намруднинг) ҳоли-хабарини билмадингизми? Қайсики Иброҳим: «Парвардигорим тирилтириб, ўлдирадиган зотдир», деганида, у: «Мен (ҳам) тирилтираман ва ўлдираман», деди. Иброҳим айтди: «Албатта, Аллоҳ қуёшни Машриқдан чиқаради. Сен уни Мағрибдан чиқаргин-чи?» Шунда бу кофир довдираб қолди. Аллоҳ золим кимсаларни ҳидоят қилмайди'}, {'chapter': 2, 'verse': 259, 'text': 'أَوۡ كَٱلَّذِي مَرَّ عَلَىٰ قَرۡيَةٖ وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ يُحۡيِۦ هَٰذِهِ ٱللَّهُ بَعۡدَ مَوۡتِهَاۖ فَأَمَاتَهُ ٱللَّهُ مِاْئَةَ عَامٖ ثُمَّ بَعَثَهُۥۖ قَالَ كَمۡ لَبِثۡتَۖ قَالَ لَبِثۡتُ يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۖ قَالَ بَل لَّبِثۡتَ مِاْئَةَ عَامٖ فَٱنظُرۡ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمۡ يَتَسَنَّهۡۖ وَٱنظُرۡ إِلَىٰ حِمَارِكَ وَلِنَجۡعَلَكَ ءَايَةٗ لِّلنَّاسِۖ وَٱنظُرۡ إِلَى ٱلۡعِظَامِ كَيۡفَ نُنشِزُهَا ثُمَّ نَكۡسُوهَا لَحۡمٗاۚ فَلَمَّا تَبَيَّنَ لَهُۥ قَالَ أَعۡلَمُ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЁки бир зот (Узайр пайғамбар) ҳақидаги масални (билмадингизмики), у зот томлари йиқилиб ҳувиллаб қолган бир қишлоқдан ўтаркан: «Аллоҳ бу хароб бўлган қишлоқни қандай тирилтирар экан-а?» деди. Шунда Аллоҳ уни юз йил муддатга ўлдирди. Сўнгра тирилтириб сўради: «Қанча муддат (ўлик ҳолда) турдинг?» «Бир кун ё ярим кун», деди у. Аллоҳ деди: «Йўқ, юз йил турдинг. Таом ва ичимлигингга қара — бузилган эмас. Энди эшагингни (чириб, суяклари ажраб кетганини) кўргин. (Бу ҳодисани Аллоҳнинг қудратини кўрсатиш) ва сени одамлар учун оят — ибрат қилиш учун (келтирдик). Бу суякларни қандай тиклаб, сўнг уларни гўшт билан қоплашимизни кўргин». Қачонки унга бу нарсалар аниқ кўрингач: «Албатта, Аллоҳ ҳамма нарсага қодир эканини биламан», деди'}, {'chapter': 2, 'verse': 260, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ أَرِنِي كَيۡفَ تُحۡيِ ٱلۡمَوۡتَىٰۖ قَالَ أَوَلَمۡ تُؤۡمِنۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطۡمَئِنَّ قَلۡبِيۖ قَالَ فَخُذۡ أَرۡبَعَةٗ مِّنَ ٱلطَّيۡرِ فَصُرۡهُنَّ إِلَيۡكَ ثُمَّ ٱجۡعَلۡ عَلَىٰ كُلِّ جَبَلٖ مِّنۡهُنَّ جُزۡءٗا ثُمَّ ٱدۡعُهُنَّ يَأۡتِينَكَ سَعۡيٗاۚ وَٱعۡلَمۡ أَنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Иброҳим: «Парвардигор, менга ўликларни қандай қилиб тирилтиришингни кўрсат», деганида, Аллоҳ: «Ишонмаганмидинг?», деди. Иброҳим айтди: «Йўқ, ишонаман-ку, лекин дилим яна ҳам таскин топиши учун». Аллоҳ айтди: «Тўртта қушни олиб, ўз олдингга тўплагин (ва уларни бош, оёқ, қанот ва патларини тортиб-узиб, сўнгра бир-бирларига аралаштириб), кейин (атрофдаги тоғлардан) ҳар бир тоғнинг устига уларни бўлак-бўлак қилиб қўйгин ва шундан сўнг уларни чақиргин, сенинг олдингга дарҳол келадилар. Билгилки, Аллоҳ шубҳасиз, қудратли ва ҳикматлидир»'}, {'chapter': 2, 'verse': 261, 'text': 'مَّثَلُ ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ ٱللَّهِ كَمَثَلِ حَبَّةٍ أَنۢبَتَتۡ سَبۡعَ سَنَابِلَ فِي كُلِّ سُنۢبُلَةٖ مِّاْئَةُ حَبَّةٖۗ وَٱللَّهُ يُضَٰعِفُ لِمَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٌ\nАллоҳ йўлида молларини инфоқ-эҳсон қиладиган кишиларнинг мисоли худди ҳар бир бошоғида юзтадан дони бўлган еттита бошоқни ундириб чиқарган бир дона донга ўхшайди. (Яъни, қилинган бир яхшилик етти юз баробар бўлиб қайтишига ишора қилинмоқда.) Аллоҳ истаган кишиларига бир неча баробар қилиб беради. Аллоҳ (фазлу карами) кенг, билгувчидир'}, {'chapter': 2, 'verse': 262, 'text': 'ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ ٱللَّهِ ثُمَّ لَا يُتۡبِعُونَ مَآ أَنفَقُواْ مَنّٗا وَلَآ أَذٗى لَّهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nМолларини Аллоҳ йўлида сарфлаб, сўнгра берган нарсаларига миннат ва озорни эргаштирмайдиган зотлар учун Парвардигорлари ҳузурида улуғ ажр бордир. Улар учун ҳеч қандай хавфу хатар йўқ ва улар ғамгин бўлмайдилар'}, {'chapter': 2, 'verse': 263, 'text': '۞قَوۡلٞ مَّعۡرُوفٞ وَمَغۡفِرَةٌ خَيۡرٞ مِّن صَدَقَةٖ يَتۡبَعُهَآ أَذٗىۗ وَٱللَّهُ غَنِيٌّ حَلِيمٞ\nЯхши гап ва кечириш, ортидан озор келадиган садақадан яхшироқ. Аллоҳ беҳожат ва ҳалимдир'}, {'chapter': 2, 'verse': 264, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُبۡطِلُواْ صَدَقَٰتِكُم بِٱلۡمَنِّ وَٱلۡأَذَىٰ كَٱلَّذِي يُنفِقُ مَالَهُۥ رِئَآءَ ٱلنَّاسِ وَلَا يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ فَمَثَلُهُۥ كَمَثَلِ صَفۡوَانٍ عَلَيۡهِ تُرَابٞ فَأَصَابَهُۥ وَابِلٞ فَتَرَكَهُۥ صَلۡدٗاۖ لَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّمَّا كَسَبُواْۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nЭй мўминлар, молини одамларга кўрсатиш учун берадиган, Аллоҳга ва Охират Кунига ишонмайдиган кимсага ўхшаб, берган садақаларингизни миннат ва озор билан йўққа чиқарманг! Зеро, унинг (риёкорнинг) мисоли худди бир устини чанг-тупроқ қоплаб олган силлиқ тошга ўхшайдики, унга шаррос қуйган ёмғир теккан-да қаттиқ-силлиқ тошлигича қолдирган. (Ундай риёкор) мунофиқлар яхши амаллар қилдик, деб ҳисоблайдилар, аммо Қиёмат Кунида топган ва сарфлаган дунёларидан ҳеч нарсага эга бўлмайдилар. Аллоҳ кофирларни ҳидоят қилмайди'}, {'chapter': 2, 'verse': 265, 'text': 'وَمَثَلُ ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمُ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِ وَتَثۡبِيتٗا مِّنۡ أَنفُسِهِمۡ كَمَثَلِ جَنَّةِۭ بِرَبۡوَةٍ أَصَابَهَا وَابِلٞ فَـَٔاتَتۡ أُكُلَهَا ضِعۡفَيۡنِ فَإِن لَّمۡ يُصِبۡهَا وَابِلٞ فَطَلّٞۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ\nМолларини Аллоҳнинг ризолигини истаб ва дилларида мустаҳкам ишонч билан сарф қиладиган зотларнинг мисоли худди адирликдаги шаррос қуйган ёмғир теккач, ҳосилини икки баробар қилган боққа ўхшайди. Энди агар унга шаррос қуйган ёмғир тегмаган бўлса, майдалаб ёққан ёмғир ҳам кифоя қилади. Аллоҳ қилаётган амалингизни кўргувчидир'}, {'chapter': 2, 'verse': 266, 'text': 'أَيَوَدُّ أَحَدُكُمۡ أَن تَكُونَ لَهُۥ جَنَّةٞ مِّن نَّخِيلٖ وَأَعۡنَابٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ لَهُۥ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَأَصَابَهُ ٱلۡكِبَرُ وَلَهُۥ ذُرِّيَّةٞ ضُعَفَآءُ فَأَصَابَهَآ إِعۡصَارٞ فِيهِ نَارٞ فَٱحۡتَرَقَتۡۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ\nСизлардан биронтангиз тагларидан дарёлар оқиб тургувчи, хурмо-ю узумлари бор, ҳар турли меваларга тўла боғи бўлиб, кексайиб нимжон болалари билан қолган пайтида ўша боғини ўтли бўрон уриб, ёниб битишини истайдими? Шундай қилиб, тафаккур қилишингиз учун Аллоҳ сизларга ўз оятларини баён қилади'}, {'chapter': 2, 'verse': 267, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِن طَيِّبَٰتِ مَا كَسَبۡتُمۡ وَمِمَّآ أَخۡرَجۡنَا لَكُم مِّنَ ٱلۡأَرۡضِۖ وَلَا تَيَمَّمُواْ ٱلۡخَبِيثَ مِنۡهُ تُنفِقُونَ وَلَسۡتُم بِـَٔاخِذِيهِ إِلَّآ أَن تُغۡمِضُواْ فِيهِۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَنِيٌّ حَمِيدٌ\nЭй мўминлар, касб қилиб топган нарсаларингизнинг ҳалол-покизаларидан ва Биз сизлар учун Ердан чиқарган нарсалардан инфоқ-эҳсон қилингиз! Эҳсон қилиш учун улардан ўзингиз фақат кўз юмиб туриб оладиган — паст-нопокларини танламангиз! Ва билингизки, албатта Аллоҳ беҳожат ва ҳамду сано эгаси бўлган Зотдир'}, {'chapter': 2, 'verse': 268, 'text': 'ٱلشَّيۡطَٰنُ يَعِدُكُمُ ٱلۡفَقۡرَ وَيَأۡمُرُكُم بِٱلۡفَحۡشَآءِۖ وَٱللَّهُ يَعِدُكُم مَّغۡفِرَةٗ مِّنۡهُ وَفَضۡلٗاۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nШайтон сизларни (агар инфоқ-эҳсон қилсангиз) камбағал бўлиб қолишингиздан қўрқитади ва фаҳш ишларга буюради. Аллоҳ сизларга ўз тарафидан мағфират ва фазлу карам (бойлик) ваъда қилади. Аллоҳ (фазлу карами) кенг ва билгувчидир'}, {'chapter': 2, 'verse': 269, 'text': 'يُؤۡتِي ٱلۡحِكۡمَةَ مَن يَشَآءُۚ وَمَن يُؤۡتَ ٱلۡحِكۡمَةَ فَقَدۡ أُوتِيَ خَيۡرٗا كَثِيرٗاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nУ Ўзи истаган кишиларга ҳикмат (фойдали билим) беради. Кимга ҳикмат берилган бўлса, бас, муҳаққақки, унга кўп яхшилик берилибди. Ваъз-эслатмаларни фақат аҳли донишларгина оладилар'}, {'chapter': 2, 'verse': 270, 'text': 'وَمَآ أَنفَقۡتُم مِّن نَّفَقَةٍ أَوۡ نَذَرۡتُم مِّن نَّذۡرٖ فَإِنَّ ٱللَّهَ يَعۡلَمُهُۥۗ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٍ\nИнфоқ қилган ҳар бир нафақангиз ва назр қилган ҳар бир назрингизни шубҳасиз, Аллоҳ билади. Ва (инфоқ-эҳсон қилмайдиган) золимлар учун ҳеч қандай ёрдамчи йўқдир'}, {'chapter': 2, 'verse': 271, 'text': 'إِن تُبۡدُواْ ٱلصَّدَقَٰتِ فَنِعِمَّا هِيَۖ وَإِن تُخۡفُوهَا وَتُؤۡتُوهَا ٱلۡفُقَرَآءَ فَهُوَ خَيۡرٞ لَّكُمۡۚ وَيُكَفِّرُ عَنكُم مِّن سَيِّـَٔاتِكُمۡۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nАгар садақаларни ошкора ҳолда берсангиз, жуда яхши. Ва агар махфий қилиб, фақир-камбағалларга берсангиз — бу ўзингиз учун янада яхшироқдир. Ва қилган гуноҳларингизга каффорат бўлади. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 2, 'verse': 272, 'text': '۞لَّيۡسَ عَلَيۡكَ هُدَىٰهُمۡ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَلِأَنفُسِكُمۡۚ وَمَا تُنفِقُونَ إِلَّا ٱبۡتِغَآءَ وَجۡهِ ٱللَّهِۚ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ\nУларни ҳидоят қилиш Сизнинг зиммангизда эмас (Эй Муҳаммад алайҳис-салоту вас-салом). Балки Аллоҳ Ўзи истаган кишиларни ҳидоят қилади. Қандай яхшилик (мол) инфоқ-эҳсон қилсангизлар, бас, ўзингиз учундир. Фақат Аллоҳ юзини истаб яхшилик инфоқ-эҳсон қилингиз! Қилган ҳар бир яхшилигингиз сизларга зулм қилинмаган ҳолида ўзингизга тўла-тўкис қайтарилади'}, {'chapter': 2, 'verse': 273, 'text': 'لِلۡفُقَرَآءِ ٱلَّذِينَ أُحۡصِرُواْ فِي سَبِيلِ ٱللَّهِ لَا يَسۡتَطِيعُونَ ضَرۡبٗا فِي ٱلۡأَرۡضِ يَحۡسَبُهُمُ ٱلۡجَاهِلُ أَغۡنِيَآءَ مِنَ ٱلتَّعَفُّفِ تَعۡرِفُهُم بِسِيمَٰهُمۡ لَا يَسۡـَٔلُونَ ٱلنَّاسَ إِلۡحَافٗاۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٌ\n(Садақот-эҳсонлар) Аллоҳнинг йўлида тўсилган (яъни, ўзларини ушлаган), бирор Ерга сафар қилишга қодир бўлмайдиган, қаноатлари сабабли, билмаган одамга бой-бадавлат бўлиб кўринадиган фақир-камбағаллар учундир. Уларнинг фақирликларини сиймоларидан билиб оласиз. Улар тиланиб туриб олмайдилар, қандай яхшилик қилсангиз, бас, албатта Аллоҳ уни билгувчидир'}, {'chapter': 2, 'verse': 274, 'text': 'ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُم بِٱلَّيۡلِ وَٱلنَّهَارِ سِرّٗا وَعَلَانِيَةٗ فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nМол-дунёларини кечаю кундуз, яширин ва ошкора эҳсон қиладиган зотлар учун Парвардигорларининг ҳузурида улуғ ажр бор. Улар учун ҳеч қандай хавфу хатар йўқ ва улар ғамгин бўлмайдилар'}, {'chapter': 2, 'verse': 275, 'text': 'ٱلَّذِينَ يَأۡكُلُونَ ٱلرِّبَوٰاْ لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ ٱلَّذِي يَتَخَبَّطُهُ ٱلشَّيۡطَٰنُ مِنَ ٱلۡمَسِّۚ ذَٰلِكَ بِأَنَّهُمۡ قَالُوٓاْ إِنَّمَا ٱلۡبَيۡعُ مِثۡلُ ٱلرِّبَوٰاْۗ وَأَحَلَّ ٱللَّهُ ٱلۡبَيۡعَ وَحَرَّمَ ٱلرِّبَوٰاْۚ فَمَن جَآءَهُۥ مَوۡعِظَةٞ مِّن رَّبِّهِۦ فَٱنتَهَىٰ فَلَهُۥ مَا سَلَفَ وَأَمۡرُهُۥٓ إِلَى ٱللَّهِۖ وَمَنۡ عَادَ فَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nСудхўр бўлган кимсалар (Қиёмат Кунида қабрларидан) турмайдилар, магар жин чалган мажнун каби турадилар. Бунга сабаб уларнинг: «Байъ (олди-сотди) ҳам судхўрликнинг ўзи-ку?», деганларидир. Ҳолбуки, Аллоҳ байъни ҳалол, судхўрликни ҳаром қилган. Бас, кимга Парвардигоридан мавъиза — насиҳат етгач (судхўрликдан) тўхтаса, у ҳолда аввал ўтгани ўзига ва унинг иши Аллоҳга (топширилади). (Яъни, Аллоҳ хоҳласа афв қилар). Ва ким (судхўрликка) қайтса, ўшалар жаҳаннам эгаларидир ва унда абадий қолажаклар'}, {'chapter': 2, 'verse': 276, 'text': 'يَمۡحَقُ ٱللَّهُ ٱلرِّبَوٰاْ وَيُرۡبِي ٱلصَّدَقَٰتِۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ\nАллоҳ судхўрликнинг (фойдасини) йўқ қилади ва садақаларнинг (фойдасини) зиёда қилади. Аллоҳ ҳар қандай (судхўрликни ҳалол деб биладиган) кўрнамакни ва (судхўрлик қиладиган) жиноятчини севмайди'}, {'chapter': 2, 'verse': 277, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта иймон келтирган, яхши амаллар қилган ва намозни тўкис адо қилиб, закотни берган зотлар учун Парвардигорлари ҳузурида ажр бор. Улар учун ҳеч қандай хавф-хатар йўқ ва улар ғамгин бўлмайдилар'}, {'chapter': 2, 'verse': 278, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَذَرُواْ مَا بَقِيَ مِنَ ٱلرِّبَوٰٓاْ إِن كُنتُم مُّؤۡمِنِينَ\nЭй мўминлар, Аллоҳдан қўрқингиз ва агар чиндан мўмин бўлсангизлар, судхўрлик сарқитларини тарк қилингиз. (Яъни, одамларга берган қарзларингиздан чиқадиган фойдани олмангиз)'}, {'chapter': 2, 'verse': 279, 'text': 'فَإِن لَّمۡ تَفۡعَلُواْ فَأۡذَنُواْ بِحَرۡبٖ مِّنَ ٱللَّهِ وَرَسُولِهِۦۖ وَإِن تُبۡتُمۡ فَلَكُمۡ رُءُوسُ أَمۡوَٰلِكُمۡ لَا تَظۡلِمُونَ وَلَا تُظۡلَمُونَ\nЭнди агар (фармонимизни) қилмасангиз, у ҳолда Аллоҳ ва пайғамбари томонидан бўлган урушни билиб қўйинг! Агар тавба қилсангиз, дастмоянгиз ўзингизга — золим ҳам, мазлум ҳам бўлмайсиз'}, {'chapter': 2, 'verse': 280, 'text': 'وَإِن كَانَ ذُو عُسۡرَةٖ فَنَظِرَةٌ إِلَىٰ مَيۡسَرَةٖۚ وَأَن تَصَدَّقُواْ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nАгар (қарздор) ночор бўлса, бойигунча кутинг! Агар билсангизлар (берган қарзингизни) садақа қилиб юборишингиз ўзингиз учун яхшироқдир'}, {'chapter': 2, 'verse': 281, 'text': 'وَٱتَّقُواْ يَوۡمٗا تُرۡجَعُونَ فِيهِ إِلَى ٱللَّهِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nВа (барчангиз) Аллоҳга қайтариладиган кундан қўрқингиз! Сўнгра ҳар бир жонга қилган амали учун тўла жазо берилади ва ҳеч кимга зулм қилинмайди'}, {'chapter': 2, 'verse': 282, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا تَدَايَنتُم بِدَيۡنٍ إِلَىٰٓ أَجَلٖ مُّسَمّٗى فَٱكۡتُبُوهُۚ وَلۡيَكۡتُب بَّيۡنَكُمۡ كَاتِبُۢ بِٱلۡعَدۡلِۚ وَلَا يَأۡبَ كَاتِبٌ أَن يَكۡتُبَ كَمَا عَلَّمَهُ ٱللَّهُۚ فَلۡيَكۡتُبۡ وَلۡيُمۡلِلِ ٱلَّذِي عَلَيۡهِ ٱلۡحَقُّ وَلۡيَتَّقِ ٱللَّهَ رَبَّهُۥ وَلَا يَبۡخَسۡ مِنۡهُ شَيۡـٔٗاۚ فَإِن كَانَ ٱلَّذِي عَلَيۡهِ ٱلۡحَقُّ سَفِيهًا أَوۡ ضَعِيفًا أَوۡ لَا يَسۡتَطِيعُ أَن يُمِلَّ هُوَ فَلۡيُمۡلِلۡ وَلِيُّهُۥ بِٱلۡعَدۡلِۚ وَٱسۡتَشۡهِدُواْ شَهِيدَيۡنِ مِن رِّجَالِكُمۡۖ فَإِن لَّمۡ يَكُونَا رَجُلَيۡنِ فَرَجُلٞ وَٱمۡرَأَتَانِ مِمَّن تَرۡضَوۡنَ مِنَ ٱلشُّهَدَآءِ أَن تَضِلَّ إِحۡدَىٰهُمَا فَتُذَكِّرَ إِحۡدَىٰهُمَا ٱلۡأُخۡرَىٰۚ وَلَا يَأۡبَ ٱلشُّهَدَآءُ إِذَا مَا دُعُواْۚ وَلَا تَسۡـَٔمُوٓاْ أَن تَكۡتُبُوهُ صَغِيرًا أَوۡ كَبِيرًا إِلَىٰٓ أَجَلِهِۦۚ ذَٰلِكُمۡ أَقۡسَطُ عِندَ ٱللَّهِ وَأَقۡوَمُ لِلشَّهَٰدَةِ وَأَدۡنَىٰٓ أَلَّا تَرۡتَابُوٓاْ إِلَّآ أَن تَكُونَ تِجَٰرَةً حَاضِرَةٗ تُدِيرُونَهَا بَيۡنَكُمۡ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَلَّا تَكۡتُبُوهَاۗ وَأَشۡهِدُوٓاْ إِذَا تَبَايَعۡتُمۡۚ وَلَا يُضَآرَّ كَاتِبٞ وَلَا شَهِيدٞۚ وَإِن تَفۡعَلُواْ فَإِنَّهُۥ فُسُوقُۢ بِكُمۡۗ وَٱتَّقُواْ ٱللَّهَۖ وَيُعَلِّمُكُمُ ٱللَّهُۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\nЭй мўминлар, бир-бирларингиз билан қарз муомаласини қилсангиз, ёзиб қўйинглар! Ораларингизда бир котиб адолат билан ёзсин! Ҳеч бир котиб Аллоҳ билдирган ёзишдан бош тортмасин, албатта ёзсин! Зиммасида қарзи бўлган киши ёздирсин — Парвардигори бўлмиш Аллоҳдан қўрқсин ва у қарздан бирон нарса камайтириб ёзмасин! Агар зиммасида қарзи бўлган киши ақлсиз ё ожиз (ёш бола ё жуда кекса) бўлса, ёки (кар, соқовлиги ё тил билмаслиги сабабли айтиб туриб) ёздиришга қодир бўлмаса, унинг ҳомий вакили адолат билан ёздирсин! Ва сизлар рози бўладиган (адолатли) гувоҳлардан икки эркак кишини, агар икки эркак топилмаса, бир эркак ва икки аёлни — бири унутиб адашса, яна бири унга эслатади — гувоҳ қилинглар! Гувоҳлар (гувоҳлик учун) чақирилганда бош тортмасинлар! Хоҳ катта, хоҳ кичик қарз бўлсин, муддати келгунича ёзиб қўйишдан эринманглар. Мана шу Аллоҳ наздида адолатлироқ, гувоҳлик учун тўғрироқ ва шак-шубҳа қилмасликка яқинроқ бўлган (ҳукмдир). Лекин агар (ўртадаги муомалангиз) ораларингизда юргизиб турган нақд савдо бўлса, уни ёзмасангизда, сизлар учун гуноҳ йўқдир. Ва бир-бирингиз билан савдо қилганда гувоҳ келтиринг! Ва на котиб, на гувоҳга зарар етмасин. Агар шундай қилсангиз (яъни, улардан биронтасига зарар етказсангиз), бу Аллоҳга итоатсизлигингиздир. Аллоҳдан қўрқингиз! Аллоҳ сизларга (ҳақ-ҳидоят йўлини) билдиради. Аллоҳ ҳамма нарсани билгувчидир'}, {'chapter': 2, 'verse': 283, 'text': '۞وَإِن كُنتُمۡ عَلَىٰ سَفَرٖ وَلَمۡ تَجِدُواْ كَاتِبٗا فَرِهَٰنٞ مَّقۡبُوضَةٞۖ فَإِنۡ أَمِنَ بَعۡضُكُم بَعۡضٗا فَلۡيُؤَدِّ ٱلَّذِي ٱؤۡتُمِنَ أَمَٰنَتَهُۥ وَلۡيَتَّقِ ٱللَّهَ رَبَّهُۥۗ وَلَا تَكۡتُمُواْ ٱلشَّهَٰدَةَۚ وَمَن يَكۡتُمۡهَا فَإِنَّهُۥٓ ءَاثِمٞ قَلۡبُهُۥۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ\nАгар сафарда бўлсангиз ва котиб топа олмасангиз (берилган нарса эвазига) қўл билан тутгудек гаров олинг! Агар бир-бирларингизга омонат қўйсангиз, омонат қўйилган киши омонатини адо қилсин ва Парвардигори — Аллоҳдан қўрқсин! Гувоҳликни яширмангиз! Ким (гувоҳликдан бош тортиш билан) уни яширса, бас, албатта унинг қалби осий — гуноҳкордир. Аллоҳ қилаётган амалингизни билгувчидир'}, {'chapter': 2, 'verse': 284, 'text': 'لِّلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَإِن تُبۡدُواْ مَا فِيٓ أَنفُسِكُمۡ أَوۡ تُخۡفُوهُ يُحَاسِبۡكُم بِهِ ٱللَّهُۖ فَيَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nСамовот ва Ердаги бор нарса Аллоҳникидир. Ичингиздаги нарсани хоҳ ошкор қилинг, хоҳ яширинг, Аллоҳ сизларни ўша нарса билан ҳисоб-китоб қилади ва Ўзи истаган кишини мағфират қилиб, Ўзи истаган кишини азоблайди. Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 2, 'verse': 285, 'text': 'ءَامَنَ ٱلرَّسُولُ بِمَآ أُنزِلَ إِلَيۡهِ مِن رَّبِّهِۦ وَٱلۡمُؤۡمِنُونَۚ كُلٌّ ءَامَنَ بِٱللَّهِ وَمَلَـٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّن رُّسُلِهِۦۚ وَقَالُواْ سَمِعۡنَا وَأَطَعۡنَاۖ غُفۡرَانَكَ رَبَّنَا وَإِلَيۡكَ ٱلۡمَصِيرُ\nПайғамбар ўзига Парвардигоридан нозил қилинган нарсага иймон келтирди ва мўминлар (ҳам иймон келтирдилар). Аллоҳга, фаришталарига, китобларига ва пайғамбарларига иймон келтирган ҳар бир киши (айтди): «Унинг пайғамбарларидан бирон кишини ажратиб қўймаймиз». Ва «Эшитдик ва итоат этдик. Парвардигоро, гуноҳларимизни мағфират қилишингни сўраймиз. Ва фақат Ўзингга қайтажакмиз», дедилар'}, {'chapter': 2, 'verse': 286, 'text': 'لَا يُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا وُسۡعَهَاۚ لَهَا مَا كَسَبَتۡ وَعَلَيۡهَا مَا ٱكۡتَسَبَتۡۗ رَبَّنَا لَا تُؤَاخِذۡنَآ إِن نَّسِينَآ أَوۡ أَخۡطَأۡنَاۚ رَبَّنَا وَلَا تَحۡمِلۡ عَلَيۡنَآ إِصۡرٗا كَمَا حَمَلۡتَهُۥ عَلَى ٱلَّذِينَ مِن قَبۡلِنَاۚ رَبَّنَا وَلَا تُحَمِّلۡنَا مَا لَا طَاقَةَ لَنَا بِهِۦۖ وَٱعۡفُ عَنَّا وَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَآۚ أَنتَ مَوۡلَىٰنَا فَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nАллоҳ ҳеч бир жонни тоқатидан ташқари нарсага таклиф қилмайди. (Ҳар кимнинг) қилган (яхши) амали ўзи учундир ва (ёмон) амали ҳам ўзининг бўйнигадир. Парвардигоро, агар унутган ёки хато қилган бўлсак, бизни азобингга гирифтор айлама! Парвардигоро, бизларнинг зиммамизга биздан илгари ўтганларнинг буйинларига қўйган юкингни юклама! Парвардигоро, бизларни тоқатимиз етмайдиган нарсага зўрлама! Бизларни афв эт, (гуноҳларимизни) мағфират қил, (ҳолимизга) раҳм айла! Ўзинг Хожамизсан! Бас, бу кофир қавм устига Ўзинг бизни ғолиб қил'}, {'chapter': 3, 'verse': 1, 'text': 'الٓمٓ\nАлиф, Лом, Мим'}, {'chapter': 3, 'verse': 2, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡحَيُّ ٱلۡقَيُّومُ\nАллоҳ (ягона) ҳеч бир илоҳ йўқ. Фақат Унинг Ўзи бор. У тирик ва абадий тургувчидир'}, {'chapter': 3, 'verse': 3, 'text': 'نَزَّلَ عَلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَأَنزَلَ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), У Зот Сизга ўзидан аввал тушган китобларни тасдиқловчи бўлган бу Китобни (Қуръонни) ҳақ билан нозил қилди. Таврот ва Инжилни ҳам туширди'}, {'chapter': 3, 'verse': 4, 'text': 'مِن قَبۡلُ هُدٗى لِّلنَّاسِ وَأَنزَلَ ٱلۡفُرۡقَانَۗ إِنَّ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ لَهُمۡ عَذَابٞ شَدِيدٞۗ وَٱللَّهُ عَزِيزٞ ذُو ٱنتِقَامٍ\nУндан олдин одамларни ҳақ йўлга ҳидоят қилиш учун. (Энди эса) ҳақ билан ноҳақни ажратгувчини (— Қуръонни) нозил қилди. Аллоҳнинг оятларини инкор қилувчи кимсалар учун шубҳасиз, қаттиқ азоб бордир. Аллоҳ қудратли ва интиқом Соҳибидир'}, {'chapter': 3, 'verse': 5, 'text': 'إِنَّ ٱللَّهَ لَا يَخۡفَىٰ عَلَيۡهِ شَيۡءٞ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ\nАлбатта, Аллоҳга на Ердаги ва на кўкдаги бирон нарса махфий эмасдир'}, {'chapter': 3, 'verse': 6, 'text': 'هُوَ ٱلَّذِي يُصَوِّرُكُمۡ فِي ٱلۡأَرۡحَامِ كَيۡفَ يَشَآءُۚ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ сизларга (оналарингизнинг) қоринларида бўлган пайтингизда Ўзи хоҳлаган суратни берган Зотдир. Ҳеч бир илоҳ йўқ, фақат Унинг Ўзи бор. У қудратли, ҳикматлидир'}, {'chapter': 3, 'verse': 7, 'text': 'هُوَ ٱلَّذِيٓ أَنزَلَ عَلَيۡكَ ٱلۡكِتَٰبَ مِنۡهُ ءَايَٰتٞ مُّحۡكَمَٰتٌ هُنَّ أُمُّ ٱلۡكِتَٰبِ وَأُخَرُ مُتَشَٰبِهَٰتٞۖ فَأَمَّا ٱلَّذِينَ فِي قُلُوبِهِمۡ زَيۡغٞ فَيَتَّبِعُونَ مَا تَشَٰبَهَ مِنۡهُ ٱبۡتِغَآءَ ٱلۡفِتۡنَةِ وَٱبۡتِغَآءَ تَأۡوِيلِهِۦۖ وَمَا يَعۡلَمُ تَأۡوِيلَهُۥٓ إِلَّا ٱللَّهُۗ وَٱلرَّـٰسِخُونَ فِي ٱلۡعِلۡمِ يَقُولُونَ ءَامَنَّا بِهِۦ كُلّٞ مِّنۡ عِندِ رَبِّنَاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nУ Сизга Китоб нозил қилган Зотдирки, у (Китобдан) шу Китобнинг асли — моҳияти бўлган муҳкам — аниқ-равшан оятлар ҳам ва бошқа (Қиёмат, жаннат, дўзах ва ҳоказолар ҳақидаги) муташобиҳ — тушуниш қийин бўлган оятлар ҳам (ўрин олгандир). Энди дилларида ҳақ йўлдан оғиш бўлган кимсалар одамларни алдаб фитнага солиш ва ўз ҳавойи нафсларига мувофиқ таъвил-тафсир қилиш учун Унинг муташобиҳ оятларига эргашадидар. Ҳолбуки, ундай оятларнинг таъвилини ёлғиз Аллоҳгина билур. Илмда собитқадам бўлган бундай кишилар эса: «У Китобга иймон келтирганмиз. Ҳамма оятлари Парвардигоримиз ҳузуридандир», дейдилар. Ва фақат аҳли донишларгина панд-насиҳат олурлар'}, {'chapter': 3, 'verse': 8, 'text': 'رَبَّنَا لَا تُزِغۡ قُلُوبَنَا بَعۡدَ إِذۡ هَدَيۡتَنَا وَهَبۡ لَنَا مِن لَّدُنكَ رَحۡمَةًۚ إِنَّكَ أَنتَ ٱلۡوَهَّابُ\n«Парвардигоро, бизни ҳидоят қилганингдан кейин — энди дилларимизни ҳақ йўлдан оғдирма ва бизга Ўз ҳузурингдан раҳмат ато этгин! Албатта, Сенгина барча яхшиликларни ато этгувчисан!»'}, {'chapter': 3, 'verse': 9, 'text': 'رَبَّنَآ إِنَّكَ جَامِعُ ٱلنَّاسِ لِيَوۡمٖ لَّا رَيۡبَ فِيهِۚ إِنَّ ٱللَّهَ لَا يُخۡلِفُ ٱلۡمِيعَادَ\n«Парвардигоро, албатта Сен одамларни ҳеч шак-шубҳасиз, келадиган бир Кунга тўплагувчисан. Албатта, Аллоҳ ваъдасига хилоф қилмагай!»'}, {'chapter': 3, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـٔٗاۖ وَأُوْلَـٰٓئِكَ هُمۡ وَقُودُ ٱلنَّارِ\nКуфр йўлини тутган кимсаларнинг на моллари ва на болалари уларни Аллоҳ азобидан қутқара олмайди. Улар дўзахнинг ўтинларидир'}, {'chapter': 3, 'verse': 11, 'text': 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِنَا فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۗ وَٱللَّهُ شَدِيدُ ٱلۡعِقَابِ\nУларнинг ҳоли худди Фиръавн одамларининг ва улардан аввал ўтган кофирларнинг ҳоли кабидирки, бизнинг оятларимизни ёлғон дейишгач, Аллоҳ уларни гуноҳлари (куфрлари) билан ушлаб азоблади. Аллоҳ азоби қаттиқ Зотдир'}, {'chapter': 3, 'verse': 12, 'text': 'قُل لِّلَّذِينَ كَفَرُواْ سَتُغۡلَبُونَ وَتُحۡشَرُونَ إِلَىٰ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمِهَادُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), куфр йўлини тутган кимсаларга айтинг: «шак-шубҳасиз, (бу дунёда) мағлуб бўласизлар ва (Охиратда) жаҳаннамда тўпланажаксиз. Нақадар ёмон жой у!»'}, {'chapter': 3, 'verse': 13, 'text': 'قَدۡ كَانَ لَكُمۡ ءَايَةٞ فِي فِئَتَيۡنِ ٱلۡتَقَتَاۖ فِئَةٞ تُقَٰتِلُ فِي سَبِيلِ ٱللَّهِ وَأُخۡرَىٰ كَافِرَةٞ يَرَوۡنَهُم مِّثۡلَيۡهِمۡ رَأۡيَ ٱلۡعَيۡنِۚ وَٱللَّهُ يُؤَيِّدُ بِنَصۡرِهِۦ مَن يَشَآءُۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ\n(Эй яҳудийлар), сизлар учун бири Аллоҳ йўлида жанг қилган, иккинчиси кофир бўлган икки гуруҳ орасидаги тўқнашувда ибратли ҳодиса бўлган эди. Улар (мусулмонлар) ўз кўзлари билан (кофирларнинг) икки баробар кўп эканини кўриб турар эдилар. Аллоҳ эса Ўзи истаган кишиларни ғолиб қилиш билан қўллаб-қувватлайди. Албатта, бу воқеада фикр эгалари учун ибрат бордир'}, {'chapter': 3, 'verse': 14, 'text': 'زُيِّنَ لِلنَّاسِ حُبُّ ٱلشَّهَوَٰتِ مِنَ ٱلنِّسَآءِ وَٱلۡبَنِينَ وَٱلۡقَنَٰطِيرِ ٱلۡمُقَنطَرَةِ مِنَ ٱلذَّهَبِ وَٱلۡفِضَّةِ وَٱلۡخَيۡلِ ٱلۡمُسَوَّمَةِ وَٱلۡأَنۡعَٰمِ وَٱلۡحَرۡثِۗ ذَٰلِكَ مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلۡمَـَٔابِ\nОдамларга аёллар, болалар, туганмас олтин-кумуш бойликлар, (қиммат) баҳоли отлар, чорва ва экин-тикинлар каби истак-хоҳишларга кўнгил қўйиш чиройли қилинди. Ҳолбуки, бу нарсалар ҳаёти дунёнинг (ўткинчи) нарсаларидир. Аллоҳнинг ҳузурида эса энг гўзал қайтадиган жой — жаннат бордир. (Яъни, ўткинчи нарсаларга кўнгил бермай, ҳақиқий гўзалликни севмоқ лозим)'}, {'chapter': 3, 'verse': 15, 'text': '۞قُلۡ أَؤُنَبِّئُكُم بِخَيۡرٖ مِّن ذَٰلِكُمۡۖ لِلَّذِينَ ٱتَّقَوۡاْ عِندَ رَبِّهِمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَأَزۡوَٰجٞ مُّطَهَّرَةٞ وَرِضۡوَٰنٞ مِّنَ ٱللَّهِۗ وَٱللَّهُ بَصِيرُۢ بِٱلۡعِبَادِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Сизларга булардан яхшироқ нарса ҳақида хабар берайми? Тақводор кишилар учун Парвардигорлари ҳузурида улар абадий қоладиган, тагларидан дарёлар оқиб тургувчи боғлар, покиза жуфтлар ва Аллоҳнинг Ризолиги бор. Аллоҳ бандаларини кўриб тургувчидир'}, {'chapter': 3, 'verse': 16, 'text': 'ٱلَّذِينَ يَقُولُونَ رَبَّنَآ إِنَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ ٱلنَّارِ\nУлар: «Парвардигоро, бизлар Сенга иймон келтирганмиз, энди Ўзинг гуноҳларимизни мағфират айла ва бизни жаҳаннам азобидан асра», дейдилар'}, {'chapter': 3, 'verse': 17, 'text': 'ٱلصَّـٰبِرِينَ وَٱلصَّـٰدِقِينَ وَٱلۡقَٰنِتِينَ وَٱلۡمُنفِقِينَ وَٱلۡمُسۡتَغۡفِرِينَ بِٱلۡأَسۡحَارِ\nУлар сабр-қаноатли, иймонларида содиқ, ибодат-итоатли, инфоқ-эҳсонли бўлган ва саҳарларда Аллоҳдан мағфират тилайдиган кишилардир'}, {'chapter': 3, 'verse': 18, 'text': 'شَهِدَ ٱللَّهُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا هُوَ وَٱلۡمَلَـٰٓئِكَةُ وَأُوْلُواْ ٱلۡعِلۡمِ قَآئِمَۢا بِٱلۡقِسۡطِۚ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАллоҳ, фаришталар ва илм аҳллари — ёлғиз Аллоҳдан ўзга ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи борлигига гувоҳлик бердилар. У адолат билан тургувчи — ҳукм қилгувчидир. Ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи бор. У қудратли, ҳикмат эгасидир'}, {'chapter': 3, 'verse': 19, 'text': 'إِنَّ ٱلدِّينَ عِندَ ٱللَّهِ ٱلۡإِسۡلَٰمُۗ وَمَا ٱخۡتَلَفَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۗ وَمَن يَكۡفُرۡ بِـَٔايَٰتِ ٱللَّهِ فَإِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nАлбатта, Аллоҳ наздида мақбул бўладиган дин фақат Ислом динидир. Аҳли китоблар (яҳудий ва насронийлар) уларга (Ислом ва Муҳаммаднинг ҳақ пайғамбарлиги ҳақида) ҳужжат келганидан кейин фақат ўзаро ҳасад-адоват қилганлари сабаблигина талашиб-тортишдилар. Ким Аллоҳнинг оятларига кофир бўлса, бас, албатта Аллоҳ тез ҳисоб-китоб қилгувчи Зотдир'}, {'chapter': 3, 'verse': 20, 'text': 'فَإِنۡ حَآجُّوكَ فَقُلۡ أَسۡلَمۡتُ وَجۡهِيَ لِلَّهِ وَمَنِ ٱتَّبَعَنِۗ وَقُل لِّلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَٱلۡأُمِّيِّـۧنَ ءَأَسۡلَمۡتُمۡۚ فَإِنۡ أَسۡلَمُواْ فَقَدِ ٱهۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُۗ وَٱللَّهُ بَصِيرُۢ بِٱلۡعِبَادِ\nАгар Сиз билан (дин ҳақида) тортишсалар: «Мен ва менга тобеъ бўлган кишилар ўзимизни Аллоҳга топширдик — Унга бўйсундик», деб айтинг! Сўнг аҳли китоблар ва (китоб берилмаган) омий-бутпарастлардан: «Исломга кирдингизми?», деб сўранг! Агар Исломга кирсалар, муҳаққақ Тўғри Йўлни топибдилар. Энди агар юз ўгирсалар, у ҳолда Сизнинг зиммангиздаги нарса фақатгина етказишдир. Аллоҳ бандаларини кўриб тургувчидир'}, {'chapter': 3, 'verse': 21, 'text': 'إِنَّ ٱلَّذِينَ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلنَّبِيِّـۧنَ بِغَيۡرِ حَقّٖ وَيَقۡتُلُونَ ٱلَّذِينَ يَأۡمُرُونَ بِٱلۡقِسۡطِ مِنَ ٱلنَّاسِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ\nАллоҳнинг оятларини инкор қиладиган, пайғамбарларни ноҳақ ўлдирадиган ва одамлар орасидаги адолатга чақирувчи зотларни ўлдирадиган кимсаларга, албатта, аламли азоб «хушхабар»ини етказинг'}, {'chapter': 3, 'verse': 22, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nУларнинг қилган амаллари дунёю Охиратда беҳуда кетмиш ва улар учун бирор ёрдамчи йўқдир'}, {'chapter': 3, 'verse': 23, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يُدۡعَوۡنَ إِلَىٰ كِتَٰبِ ٱللَّهِ لِيَحۡكُمَ بَيۡنَهُمۡ ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُمۡ وَهُم مُّعۡرِضُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Китобдан (Тавротдан) насибадор бўлган кимсалар ораларида ҳакамлик қилсин учун Аллоҳнинг Китобига чақирилганларни, сўнгра улардан бир гуруҳи юз ўгирган ҳолларида бурилиб кетганларини кўрмадингизми'}, {'chapter': 3, 'verse': 24, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَن تَمَسَّنَا ٱلنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَٰتٖۖ وَغَرَّهُمۡ فِي دِينِهِم مَّا كَانُواْ يَفۡتَرُونَ\nБунга (яъни, адолатли ҳукмдан юз ўгиришларига) сабаб уларнинг: «Бизга фақат саноқли кунлардагина дўзах ўти тегади», дейишларидир. Ўзлари тўқиган бу ёлғонлари уларни динларидан адаштириб қўйди'}, {'chapter': 3, 'verse': 25, 'text': 'فَكَيۡفَ إِذَا جَمَعۡنَٰهُمۡ لِيَوۡمٖ لَّا رَيۡبَ فِيهِ وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nЭнди уларни ҳеч шак-шубҳасиз, келадиган Кунга тўплаганимизда ва ҳар бир жон мазлум бўлмаган ҳолда қилган амаллари учун тўла жазо-мукофот берилганда қандай бўларкин'}, {'chapter': 3, 'verse': 26, 'text': 'قُلِ ٱللَّهُمَّ مَٰلِكَ ٱلۡمُلۡكِ تُؤۡتِي ٱلۡمُلۡكَ مَن تَشَآءُ وَتَنزِعُ ٱلۡمُلۡكَ مِمَّن تَشَآءُ وَتُعِزُّ مَن تَشَآءُ وَتُذِلُّ مَن تَشَآءُۖ بِيَدِكَ ٱلۡخَيۡرُۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАйтинг: «Эй мулку давлат эгаси бўлган Аллоҳ, Сен истаган кишингга мулк ато қилурсан ва истаган кишингдан бу мулкни тортиб олурсан, истаган кишингни азиз қилурсан ва истаган кишингни хор қилурсан. Бор яхшилик ёлғиз Сенинг қўлингдадир. Албатта, Сен барча нарсага қодирсан'}, {'chapter': 3, 'verse': 27, 'text': 'تُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَتُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِۖ وَتُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَتُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّۖ وَتَرۡزُقُ مَن تَشَآءُ بِغَيۡرِ حِسَابٖ\nКечани кундузга киритурсан ва кундузни кечага киритурсан, ўликдан тирикни чиқарурсан ва тирикдан ўликни чиқарурсан ҳамда истаган кишингга беҳисоб ризқ берурсан»'}, {'chapter': 3, 'verse': 28, 'text': 'لَّا يَتَّخِذِ ٱلۡمُؤۡمِنُونَ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۖ وَمَن يَفۡعَلۡ ذَٰلِكَ فَلَيۡسَ مِنَ ٱللَّهِ فِي شَيۡءٍ إِلَّآ أَن تَتَّقُواْ مِنۡهُمۡ تُقَىٰةٗۗ وَيُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ\n(Модомики, барча иш — бутун мулк Аллоҳнинг қўлида экан, демак), мўминлар мўминларни қўйиб, кофирларни дўст тутмасинлар! Ким шундай қилса, бас, Аллоҳ томонидан ҳеч нарсада эмас (яъни, Аллоҳга бегонадир). Магар улардан эҳтиёт бўлиб турсаларингиз (юзаки муомала қилсангиз жоиздир), Аллоҳ сизларни Ўзининг (азобидан) огоҳ қилур. Ва фақат Аллоҳга қайтажаксиз'}, {'chapter': 3, 'verse': 29, 'text': 'قُلۡ إِن تُخۡفُواْ مَا فِي صُدُورِكُمۡ أَوۡ تُبۡدُوهُ يَعۡلَمۡهُ ٱللَّهُۗ وَيَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Дилларингиздаги нарсани хоҳ яширинг, хоҳ ошкор қилинг, Аллоҳ билур. Ва (шунингдек, У зот) осмонлар ва Ердаги бор нарсани билур. Аллоҳ ҳамма нарсага қодирдир»'}, {'chapter': 3, 'verse': 30, 'text': 'يَوۡمَ تَجِدُ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ مِنۡ خَيۡرٖ مُّحۡضَرٗا وَمَا عَمِلَتۡ مِن سُوٓءٖ تَوَدُّ لَوۡ أَنَّ بَيۡنَهَا وَبَيۡنَهُۥٓ أَمَدَۢا بَعِيدٗاۗ وَيُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ\nҲар бир жон ўз қилган яхши амалларини ҳозиру нозир ҳолда кўрадиган, ёмон амалларининг эса олис-олисларда қолиб кетишини истайдиган Кунни (эсланглар)! Аллоҳ сизларни Ўзининг (азобидан) огоҳ қилур. Аллоҳ бандаларига меҳрибондир'}, {'chapter': 3, 'verse': 31, 'text': 'قُلۡ إِن كُنتُمۡ تُحِبُّونَ ٱللَّهَ فَٱتَّبِعُونِي يُحۡبِبۡكُمُ ٱللَّهُ وَيَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар Аллоҳни севсангиз, менга эргашинглар. Шунда Аллоҳ сизларни севади ва гуноҳларингизни мағфират қилади. Аллоҳ (гуноҳларни) мағфират қилгувчи, меҳрибондир»'}, {'chapter': 3, 'verse': 32, 'text': 'قُلۡ أَطِيعُواْ ٱللَّهَ وَٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡكَٰفِرِينَ\nАйтинг: «Аллоҳ ва пайғамбарга итоат қилингиз!» Агар юз ўгирсалар, бас, албатта Аллоҳ (бундай) кофирларни севмас'}, {'chapter': 3, 'verse': 33, 'text': '۞إِنَّ ٱللَّهَ ٱصۡطَفَىٰٓ ءَادَمَ وَنُوحٗا وَءَالَ إِبۡرَٰهِيمَ وَءَالَ عِمۡرَٰنَ عَلَى ٱلۡعَٰلَمِينَ\nАлбатта, Аллоҳ Одам ва Нуҳни ҳамда Иброҳим хонадонини ва Имрон хонадонини бутун оламлар устида (пайғамбарлик учун) танлаб олди'}, {'chapter': 3, 'verse': 34, 'text': 'ذُرِّيَّةَۢ بَعۡضُهَا مِنۢ بَعۡضٖۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\nУларнинг бировлари бировларига зурриёт (эдилар). Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 3, 'verse': 35, 'text': 'إِذۡ قَالَتِ ٱمۡرَأَتُ عِمۡرَٰنَ رَبِّ إِنِّي نَذَرۡتُ لَكَ مَا فِي بَطۡنِي مُحَرَّرٗا فَتَقَبَّلۡ مِنِّيٓۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Имроннинг хотини: «Парвардигорим, мен қорнимдаги нарсани (ҳомилани дунё ишларидан) озод этиб, Сенга назр қилдим. Бас, (бу назримни Ўз даргоҳингда) қабул айла! Албатта, Сен эшитгувчи, билгувчисан», деганини эсланг'}, {'chapter': 3, 'verse': 36, 'text': 'فَلَمَّا وَضَعَتۡهَا قَالَتۡ رَبِّ إِنِّي وَضَعۡتُهَآ أُنثَىٰ وَٱللَّهُ أَعۡلَمُ بِمَا وَضَعَتۡ وَلَيۡسَ ٱلذَّكَرُ كَٱلۡأُنثَىٰۖ وَإِنِّي سَمَّيۡتُهَا مَرۡيَمَ وَإِنِّيٓ أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ ٱلشَّيۡطَٰنِ ٱلرَّجِيمِ\nКўзи ёригач эса бундай деди: «Парвардигор, мен қиз туғдим». — Ҳолбуки, Аллоҳ унинг нима туққанини билгувчироқдир ва ҳар қандай ўғил бу қиз каби (яъни, бу қизнинг ўрнини босгувчи) эмасдир. — «Ва мен унга Марям деб исм қўйдим. Мен бу қизга ва унинг зурриётига даргоҳингдан қувилган шайтоннинг ёмонлигидан паноҳ беришингни Сендан илтижо қиламан»'}, {'chapter': 3, 'verse': 37, 'text': 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٖ وَأَنۢبَتَهَا نَبَاتًا حَسَنٗا وَكَفَّلَهَا زَكَرِيَّاۖ كُلَّمَا دَخَلَ عَلَيۡهَا زَكَرِيَّا ٱلۡمِحۡرَابَ وَجَدَ عِندَهَا رِزۡقٗاۖ قَالَ يَٰمَرۡيَمُ أَنَّىٰ لَكِ هَٰذَاۖ قَالَتۡ هُوَ مِنۡ عِندِ ٱللَّهِۖ إِنَّ ٱللَّهَ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٍ\nБас, Парвардигори уни (Марямни) хуш қабул айлаб, чиройли парвариш этди ва унга Закариёни кафил қилди. Ҳар қачон Закариё (Марямнинг) олдига — ҳужрага кирганида унинг ҳузурида бир ризқ-насиба кўрди. У: «Эй Марям, бу нарсалар сенга қаёқдан келди?», деб сўраганида (Марям) жавоб қилди: «Булар Аллоҳ ҳузуридандир. Албатта, Аллоҳ Ўзи истаган кишиларга беҳисоб ризқ берур»'}, {'chapter': 3, 'verse': 38, 'text': 'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُۥۖ قَالَ رَبِّ هَبۡ لِي مِن لَّدُنكَ ذُرِّيَّةٗ طَيِّبَةًۖ إِنَّكَ سَمِيعُ ٱلدُّعَآءِ\nЎшанда Закариё Парвардигорига дуо қилиб деди: «Парвардигорим, менга ҳам Ўз ҳузурингдан бир покиза фарзанд ато эт! Албатта, Сен дуо-илтижоларимни эшитгувчисан»'}, {'chapter': 3, 'verse': 39, 'text': 'فَنَادَتۡهُ ٱلۡمَلَـٰٓئِكَةُ وَهُوَ قَآئِمٞ يُصَلِّي فِي ٱلۡمِحۡرَابِ أَنَّ ٱللَّهَ يُبَشِّرُكَ بِيَحۡيَىٰ مُصَدِّقَۢا بِكَلِمَةٖ مِّنَ ٱللَّهِ وَسَيِّدٗا وَحَصُورٗا وَنَبِيّٗا مِّنَ ٱلصَّـٰلِحِينَ\nСўнг меҳробда намоз ўқиб турган вақтида унга фаришталар нидо қилдилар: «(Эй Закариё), Аллоҳ сенга Аллоҳнинг сўзини тасдиқ этадиган, (ўз қавмига) хожа бўладиган, (шаҳватлардан ўзини) тиядиган ва солиҳ пайғамбарлардан бўладиган Яҳё исмли фарзанд хушхабарини берур»'}, {'chapter': 3, 'verse': 40, 'text': 'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَقَدۡ بَلَغَنِيَ ٱلۡكِبَرُ وَٱمۡرَأَتِي عَاقِرٞۖ قَالَ كَذَٰلِكَ ٱللَّهُ يَفۡعَلُ مَا يَشَآءُ\n«Парвардигорим, менга кексалик етган, хотиним туғмас бўлса, мендан қандай фарзанд бўлсин?», деди (Закариё). (Аллоҳ) айтди: «Шундай, Аллоҳ истаган ишини қилур»'}, {'chapter': 3, 'verse': 41, 'text': 'قَالَ رَبِّ ٱجۡعَل لِّيٓ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَةَ أَيَّامٍ إِلَّا رَمۡزٗاۗ وَٱذۡكُر رَّبَّكَ كَثِيرٗا وَسَبِّحۡ بِٱلۡعَشِيِّ وَٱلۡإِبۡكَٰرِ\nПарвардигор, мен учун (кампирим ҳомилали бўлганига) бирон белги — аломат қилсанг», деди у. Аллоҳ айтди: «Сенга белги шуки, уч кун одамларга фақат имо-ишора билангина гапира оласан. Парвардигорингни мудом ёд қил ва туну кун У зотни поклаб ибодат қил!»'}, {'chapter': 3, 'verse': 42, 'text': 'وَإِذۡ قَالَتِ ٱلۡمَلَـٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ ٱللَّهَ ٱصۡطَفَىٰكِ وَطَهَّرَكِ وَٱصۡطَفَىٰكِ عَلَىٰ نِسَآءِ ٱلۡعَٰلَمِينَ\nФаришталар Марямга: «Ё Марям, албатта Аллоҳ сени (аёллар орасидан) танлаб олди ва (барча гуноҳлардан) поклади ҳамда сени бутун оламларнинг аёлларидан афзал қилди'}, {'chapter': 3, 'verse': 43, 'text': 'يَٰمَرۡيَمُ ٱقۡنُتِي لِرَبِّكِ وَٱسۡجُدِي وَٱرۡكَعِي مَعَ ٱلرَّـٰكِعِينَ\nЁ Марям, Парвардигорингга итоат қил ва рукуъ (Аллоҳга ибодат) қилгувчилар билан бирга сажда ва рукуъ (яъни, ибодат қил!») — деганларини эсланг'}, {'chapter': 3, 'verse': 44, 'text': 'ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهِ إِلَيۡكَۚ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يُلۡقُونَ أَقۡلَٰمَهُمۡ أَيُّهُمۡ يَكۡفُلُ مَرۡيَمَ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يَخۡتَصِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ғайб хабарларидан бўлган бу хабарни сизга Биз ваҳий қилмоқдамиз. Ҳолбуки, Сиз улардан қайсилари Марямга кафил бўлишини билишлик учун қаламларини (қуръа қилиб) ташлаганларида уларнинг олдида эмасдингиз ва талашиб-тортишганларида ҳам уларнинг олдида эмасдингиз'}, {'chapter': 3, 'verse': 45, 'text': 'إِذۡ قَالَتِ ٱلۡمَلَـٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ ٱللَّهَ يُبَشِّرُكِ بِكَلِمَةٖ مِّنۡهُ ٱسۡمُهُ ٱلۡمَسِيحُ عِيسَى ٱبۡنُ مَرۡيَمَ وَجِيهٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمِنَ ٱلۡمُقَرَّبِينَ\nЭсланг (эй Муҳаммад алайҳис-салоту вас-салом), фаришталар дедилар: «Ё Марям, албатта Аллоҳ сенга Ўзининг Сўзини хушхабар қилиб берадики, унинг исми ал-Масиҳ Ийсо бинни Марям бўлиб, дунё-ю Охиратда обрўли ва (Аллоҳнинг) яқинларидандир'}, {'chapter': 3, 'verse': 46, 'text': 'وَيُكَلِّمُ ٱلنَّاسَ فِي ٱلۡمَهۡدِ وَكَهۡلٗا وَمِنَ ٱلصَّـٰلِحِينَ\nУ одамларга гўдаклик пайтида ҳам вояга етган чоғидагидек гапирур. Ва (у) солиҳ бандалардандир»'}, {'chapter': 3, 'verse': 47, 'text': 'قَالَتۡ رَبِّ أَنَّىٰ يَكُونُ لِي وَلَدٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞۖ قَالَ كَذَٰلِكِ ٱللَّهُ يَخۡلُقُ مَا يَشَآءُۚ إِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nМарям: «Парвардигорим, менга одамзод тегмаган бўлса, қаёқдан менда фарзанд бўлсин?», деди. Аллоҳ айтди: «Шундай, Аллоҳ Ўзи хоҳлаган нарсасини (Ўзи хоҳлаган суратда) яратади. Қачон бирон ишни ирода қилса, унга: «Бўл!», дейди. Бас, у иш бўлади»'}, {'chapter': 3, 'verse': 48, 'text': 'وَيُعَلِّمُهُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ\nВа унга (Ийсога) ёзиш ва ҳикмат илмини, Таврот ва Инжилни таълим беради'}, {'chapter': 3, 'verse': 49, 'text': 'وَرَسُولًا إِلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَنِّي قَدۡ جِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ أَنِّيٓ أَخۡلُقُ لَكُم مِّنَ ٱلطِّينِ كَهَيۡـَٔةِ ٱلطَّيۡرِ فَأَنفُخُ فِيهِ فَيَكُونُ طَيۡرَۢا بِإِذۡنِ ٱللَّهِۖ وَأُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ وَأُحۡيِ ٱلۡمَوۡتَىٰ بِإِذۡنِ ٱللَّهِۖ وَأُنَبِّئُكُم بِمَا تَأۡكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nУни Бани Исроил қавмига пайғамбар қилади. (Ийсо Бани Исроилга дейди): «Мен (ўзимнинг ҳақ пайғамбар эканлигим ҳақида) сизларга Парвардигорингиздан оят-далил келтирдим: Мен сизларга лойдан қуш тимсолини ясаб унга пуфласам, у Аллоҳнинг изни-иродаси билан ҳақиқий қуш бўлади. Ва яна кўр, пес касалларини тузата оламан ва Аллоҳнинг изни билан ўликларни тирилтираман ҳамда сизларга ейдиган ва уйларингизда сақлайдиган нарсаларингизни айтиб беришга қодирман. Агар мўмин бўлсангизлар, албатта бу ишларда сизлар учун аниқ оят-далиллар бордир»'}, {'chapter': 3, 'verse': 50, 'text': 'وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ ٱلتَّوۡرَىٰةِ وَلِأُحِلَّ لَكُم بَعۡضَ ٱلَّذِي حُرِّمَ عَلَيۡكُمۡۚ وَجِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\n«(Мен) сизларга ўзимдан олдинги Тавротнинг (ҳақ эканлигини) тасдиқловчи бўлган ҳолда, сизлар учун ҳаром қилинган айрим нарсаларни ҳалол қилиш учун (келдим). Ва сизларга Парвардигорингиздан оят-далил келтирдим. Бас, Аллоҳдан қўрқингиз ва менга итоат қилингиз'}, {'chapter': 3, 'verse': 51, 'text': 'إِنَّ ٱللَّهَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nАлбатта, Аллоҳ Парвардигорим ва Парвардигорингиздир. Бас, Унга бандалик қилингиз! Мана шу Тўғри Йўлдир!»'}, {'chapter': 3, 'verse': 52, 'text': '۞فَلَمَّآ أَحَسَّ عِيسَىٰ مِنۡهُمُ ٱلۡكُفۡرَ قَالَ مَنۡ أَنصَارِيٓ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ ٱللَّهِ ءَامَنَّا بِٱللَّهِ وَٱشۡهَدۡ بِأَنَّا مُسۡلِمُونَ\nЭнди қачонки Ийсо улар томондан куфрни кўргач: «Аллоҳнинг динига даъват қилишимда ким менга ёрдамчи бўлур?», деди. Ҳаворийлар айтдилар: «Биз Аллоҳнинг (динига) ёрдам бергувчилармиз. Аллоҳга иймон келтирдик. (Эй Ийсо), гувоҳ бўлгинки, биз Аллоҳга бўйсунгувчилармиз'}, {'chapter': 3, 'verse': 53, 'text': 'رَبَّنَآ ءَامَنَّا بِمَآ أَنزَلۡتَ وَٱتَّبَعۡنَا ٱلرَّسُولَ فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِينَ\nПарвардигоро, нозил қилган нарсангга ишондик, пайғамбарингга эргашдик. Бас, бизни (Ўзингнинг бирлигингга, пайғамбарингнинг ҳақлигига) гувоҳлик берувчилар қаторига ёзгин!»'}, {'chapter': 3, 'verse': 54, 'text': 'وَمَكَرُواْ وَمَكَرَ ٱللَّهُۖ وَٱللَّهُ خَيۡرُ ٱلۡمَٰكِرِينَ\n(Яҳудийлар) макр қилдилар. Аллоҳ ҳам «макр» қилди. Аллоҳ макр қилгувчиларнинг «зўри»дир'}, {'chapter': 3, 'verse': 55, 'text': 'إِذۡ قَالَ ٱللَّهُ يَٰعِيسَىٰٓ إِنِّي مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ ٱلَّذِينَ كَفَرُواْ وَجَاعِلُ ٱلَّذِينَ ٱتَّبَعُوكَ فَوۡقَ ٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۖ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأَحۡكُمُ بَيۡنَكُمۡ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ айтган бу сўзларни эсланг: «Ё Ийсо, албатта Мен сени Ердан бус-бутун (яъни, ҳеч қандай зарар етказмасдан) олувчи ва Ўз ҳузуримга кўтаргувчиман. Ва кофирлар ёмонлигидан халос қилгувчиман ҳамда то Қиёмат Кунигача сенга эргашган зотларни кофирлардан юқори қўйгувчиман. Сўнгра Менга қайтажаксиз. Бас, ўзим сизлар талашиб-тортишган нарсалар ҳақида ораларингизда ҳакамлик қилурман'}, {'chapter': 3, 'verse': 56, 'text': 'فَأَمَّا ٱلَّذِينَ كَفَرُواْ فَأُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nБас, кофир бўлган кимсаларга дунё ва Охиратда қаттиқ азоб-ла азоб берурман. Ва улар учун ҳеч қандай ёрдамчи бўлмас'}, {'chapter': 3, 'verse': 57, 'text': 'وَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡۗ وَٱللَّهُ لَا يُحِبُّ ٱلظَّـٰلِمِينَ\n(Аллоҳ) иймон келтириб, яхши амаллар қилган зотларга эса ажрларини комил суратда берур. Аллоҳ зулм қилгувчиларни севмайди. (Бинобарин, Ўзи ҳам ҳеч кимга зулм қилмай, ҳар кимга қилган ишига лойиқ жазо беради)»'}, {'chapter': 3, 'verse': 58, 'text': 'ذَٰلِكَ نَتۡلُوهُ عَلَيۡكَ مِنَ ٱلۡأٓيَٰتِ وَٱلذِّكۡرِ ٱلۡحَكِيمِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сизга тиловат қилаётганимиз бу қисса Аллоҳнинг оятлари ва ҳикматли эслатмаларидандир'}, {'chapter': 3, 'verse': 59, 'text': 'إِنَّ مَثَلَ عِيسَىٰ عِندَ ٱللَّهِ كَمَثَلِ ءَادَمَۖ خَلَقَهُۥ مِن تُرَابٖ ثُمَّ قَالَ لَهُۥ كُن فَيَكُونُ\nАлбатта Ийсонинг (отасиз туғилишининг) мисоли Аллоҳ наздида худди Одамнинг мисоли кабидирки, уни тупроқдаи яратиб, сўнгра «Бўл», деди. Бас, у (жонли одам) бўлди'}, {'chapter': 3, 'verse': 60, 'text': 'ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُن مِّنَ ٱلۡمُمۡتَرِينَ\n(Бу) Парвардигорингиз томонидан бўлган ҳақ сўздир. Бас, шубҳа қилгувчилардан бўлманг'}, {'chapter': 3, 'verse': 61, 'text': 'فَمَنۡ حَآجَّكَ فِيهِ مِنۢ بَعۡدِ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ فَقُلۡ تَعَالَوۡاْ نَدۡعُ أَبۡنَآءَنَا وَأَبۡنَآءَكُمۡ وَنِسَآءَنَا وَنِسَآءَكُمۡ وَأَنفُسَنَا وَأَنفُسَكُمۡ ثُمَّ نَبۡتَهِلۡ فَنَجۡعَل لَّعۡنَتَ ٱللَّهِ عَلَى ٱلۡكَٰذِبِينَ\nЭнди Сизга келган мана шу ҳақ маълумотдан кейин (насронийлардан) кимда-ким Сиз билан талашиб-тортишмоқчи бўлса, (ундай кимсаларга) айтинг: «Келинглар, ўғилларимизни ва ўғилларингизни, аёлларимиз ва аёлларингизни, ўзларимизни ва ўзларингизни чорлаб-йиғайлик, сўнгра Аллоҳга тазарруъ билан илтижо қилайлик-да, ёлғончиларни Аллоҳ лаънат қилишини сўрайлик»'}, {'chapter': 3, 'verse': 62, 'text': 'إِنَّ هَٰذَا لَهُوَ ٱلۡقَصَصُ ٱلۡحَقُّۚ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАлбатта, бу ҳақ-рост қиссадир. (Яъни, Ийсо ҳеч қандай худо эмас, балки Аллоҳнинг бошқа ҳамма пайғамбари каби оддий одам зотидандир). Ва ҳеч қандай илоҳ йўқ, фақат Аллоҳнинг Ўзи бор. Ва албатта Аллоҳнинг Ўзигина қудратли ва ҳикматлидир'}, {'chapter': 3, 'verse': 63, 'text': 'فَإِن تَوَلَّوۡاْ فَإِنَّ ٱللَّهَ عَلِيمُۢ بِٱلۡمُفۡسِدِينَ\nЭнди ҳам (ҳақиқатдан) юз ўгирсалар бас, албатта Аллоҳ бузғунчиларни билгувчидир'}, {'chapter': 3, 'verse': 64, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ تَعَالَوۡاْ إِلَىٰ كَلِمَةٖ سَوَآءِۭ بَيۡنَنَا وَبَيۡنَكُمۡ أَلَّا نَعۡبُدَ إِلَّا ٱللَّهَ وَلَا نُشۡرِكَ بِهِۦ شَيۡـٔٗا وَلَا يَتَّخِذَ بَعۡضُنَا بَعۡضًا أَرۡبَابٗا مِّن دُونِ ٱللَّهِۚ فَإِن تَوَلَّوۡاْ فَقُولُواْ ٱشۡهَدُواْ بِأَنَّا مُسۡلِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом) айтинг: «Эй аҳли китоб» (яъни, яҳудий ва насронийлар), бизга ҳам, сизга ҳам баб-баробар бўлган бир сўзга келингиз — ёлғиз Аллоҳгагина ибодат қилайлик, Унга ҳеч нарсани шерик қилмайлик ва бир-бировларимизни Аллоҳдан ўзга худо қилиб олмайлик». Агар улар (яъни, аҳли китоблар) ушбу даъватдан юз ўгирсалар, у ҳолда сизлар (эй мўминлар): «Гувоҳ бўлинглар, биз мусулмонлар — ягона Аллоҳга итоат қилгувчилармиз», деб айтингиз'}, {'chapter': 3, 'verse': 65, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تُحَآجُّونَ فِيٓ إِبۡرَٰهِيمَ وَمَآ أُنزِلَتِ ٱلتَّوۡرَىٰةُ وَٱلۡإِنجِيلُ إِلَّا مِنۢ بَعۡدِهِۦٓۚ أَفَلَا تَعۡقِلُونَ\nЭй аҳли китоб, нима учун Иброҳим хусусида (у бизнинг динимизда бўлган, деб) талашасизлар! Ахир Таврот ҳам, Инжил ҳам ундан кейин нозил бўлгани аниқ-ку?! Ақл юргизмайсизларми'}, {'chapter': 3, 'verse': 66, 'text': 'هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ حَٰجَجۡتُمۡ فِيمَا لَكُم بِهِۦ عِلۡمٞ فَلِمَ تُحَآجُّونَ فِيمَا لَيۡسَ لَكُم بِهِۦ عِلۡمٞۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nҲой (яҳудий ва насронийлар), сизлар биладиган нарсаларингиз (Мусо ва Ийсо пайғамбарлар) ҳақида-ку талашдингиз. Энди нима учун билмайдиган нарсангиз (Иброҳимнинг қайси динда бўлганлиги) хусусида талашмоқдасиз? Аллоҳ билур, сизлар билмассиз'}, {'chapter': 3, 'verse': 67, 'text': 'مَا كَانَ إِبۡرَٰهِيمُ يَهُودِيّٗا وَلَا نَصۡرَانِيّٗا وَلَٰكِن كَانَ حَنِيفٗا مُّسۡلِمٗا وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nИброҳим яҳудий ҳам, насроний ҳам эмас, балки ҳақ йўлдан тоймаган ҳолида (Аллоҳга) итоат қилгувчи киши бўлган. У мушриклардан ҳам бўлмаган'}, {'chapter': 3, 'verse': 68, 'text': 'إِنَّ أَوۡلَى ٱلنَّاسِ بِإِبۡرَٰهِيمَ لَلَّذِينَ ٱتَّبَعُوهُ وَهَٰذَا ٱلنَّبِيُّ وَٱلَّذِينَ ءَامَنُواْۗ وَٱللَّهُ وَلِيُّ ٱلۡمُؤۡمِنِينَ\nОдамларнинг Иброҳимга яқинроғи унинг йўлига эргашган зотлар, мана бу пайғамбар (Муҳаммад алайҳис-салоту вас-салом) ва иймон келтирган кишилардир. Аллоҳ мўминларнинг дўстидир'}, {'chapter': 3, 'verse': 69, 'text': 'وَدَّت طَّآئِفَةٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ لَوۡ يُضِلُّونَكُمۡ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ\nАҳли китобдан бир тоифаси сизларни (ҳақ динингиздан) адаштирмоқни хоҳлайди. Лекин улар ўзлари сезмаган ҳолларида фақат ўзларинигина адаштирадилар, холос'}, {'chapter': 3, 'verse': 70, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَأَنتُمۡ تَشۡهَدُونَ\nЭй аҳли китоб, нима учун ўзларингиз гувоҳ бўлиб туриб, Аллоҳнинг (Тавротдаги Муҳаммаднинг ҳақ пайғамбар эканлиги ҳақидаги) оятларини инкор қиласиз'}, {'chapter': 3, 'verse': 71, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَلۡبِسُونَ ٱلۡحَقَّ بِٱلۡبَٰطِلِ وَتَكۡتُمُونَ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ\nЭй аҳли китоб, нима учун ўзингиз билган ҳолингизда ҳақни ботилга аралаштирасиз ва ҳақни беркитасиз'}, {'chapter': 3, 'verse': 72, 'text': 'وَقَالَت طَّآئِفَةٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ ءَامِنُواْ بِٱلَّذِيٓ أُنزِلَ عَلَى ٱلَّذِينَ ءَامَنُواْ وَجۡهَ ٱلنَّهَارِ وَٱكۡفُرُوٓاْ ءَاخِرَهُۥ لَعَلَّهُمۡ يَرۡجِعُونَ\nАҳли китобдан бир тоифаси бир-бирларига шундай дейишди: «Мўминларга нозил қилинган нарсага (Қуръонга) куннинг бошида иймон келтириб, куннинг охирида уни инкор қилинглар. Шояд (улар ҳам иймонларидан) қайтсалар'}, {'chapter': 3, 'verse': 73, 'text': 'وَلَا تُؤۡمِنُوٓاْ إِلَّا لِمَن تَبِعَ دِينَكُمۡ قُلۡ إِنَّ ٱلۡهُدَىٰ هُدَى ٱللَّهِ أَن يُؤۡتَىٰٓ أَحَدٞ مِّثۡلَ مَآ أُوتِيتُمۡ أَوۡ يُحَآجُّوكُمۡ عِندَ رَبِّكُمۡۗ قُلۡ إِنَّ ٱلۡفَضۡلَ بِيَدِ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nТоки бирорта одамга сизларга берилган билим берилмаслиги ёки Парвардигорингиз ҳузурида сизлар билан ҳужжатлашмаслиги учун фақат ўз динингизга бўйсунган кишиларнинг гапларинигина тасдиқ этиб ишонинглар!» (Эй Муҳаммад алайҳис-салоту вас-салом) айтинг: «Албатта, Ҳақ йўл Аллоҳнинг йўлидир». Айтинг: «Албатта, фазлу карам Аллоҳнинг қўлида, уни Ўзи истаган кишиларга берур. Аллоҳ фазли кенг, билгувчи Зотдир'}, {'chapter': 3, 'verse': 74, 'text': 'يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nЎз раҳматини (яъни, пайғамбарликни) Ўзи истаган кишига хос қилиб беради. Аллоҳ улуғ фазлу марҳамат Соҳибидир»'}, {'chapter': 3, 'verse': 75, 'text': '۞وَمِنۡ أَهۡلِ ٱلۡكِتَٰبِ مَنۡ إِن تَأۡمَنۡهُ بِقِنطَارٖ يُؤَدِّهِۦٓ إِلَيۡكَ وَمِنۡهُم مَّنۡ إِن تَأۡمَنۡهُ بِدِينَارٖ لَّا يُؤَدِّهِۦٓ إِلَيۡكَ إِلَّا مَا دُمۡتَ عَلَيۡهِ قَآئِمٗاۗ ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَيۡسَ عَلَيۡنَا فِي ٱلۡأُمِّيِّـۧنَ سَبِيلٞ وَيَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ يَعۡلَمُونَ\nАҳли китоб орасида шундай кишилар борки, унга беҳисоб молни омонат қўйсангиз, хиёнат қилмай сизга қайтаради. Уларнинг орасида яна шундайлари ҳам борки, унга бир динорни ишониб берсангиз, то устида туриб олмагунингизча сизга қайтиб бермайди. Бунинг боиси — уларнинг «омий-китобсиз кимсалар учун бизнинг устимизга ҳеч қандай йўл йўқ», дейишларидир. (Яъни, айрим яҳудийлар ўзларидан бошқаларни паст табақа ҳисоблаб: «Биз Аллоҳнинг суйган бандаларимиз, демак, бошқа миллатларнинг моллари биз учун ҳалол», дер эдилар.) Улар билиб туриб Аллоҳ ҳақида ёлғон сўзларни сўзлайдилар'}, {'chapter': 3, 'verse': 76, 'text': 'بَلَىٰۚ مَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ وَٱتَّقَىٰ فَإِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ\nЙўқ! (Уларнинг даъволари ботилдир.) Балки ким аҳдига вафо қилса ва тақводор бўлса (ана ўша киши, Аллоҳнинг суйган бандасидир). Зеро, Аллоҳ тақво қилгувчи кишиларни севади'}, {'chapter': 3, 'verse': 77, 'text': 'إِنَّ ٱلَّذِينَ يَشۡتَرُونَ بِعَهۡدِ ٱللَّهِ وَأَيۡمَٰنِهِمۡ ثَمَنٗا قَلِيلًا أُوْلَـٰٓئِكَ لَا خَلَٰقَ لَهُمۡ فِي ٱلۡأٓخِرَةِ وَلَا يُكَلِّمُهُمُ ٱللَّهُ وَلَا يَنظُرُ إِلَيۡهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nАллоҳга берган аҳд-паймон ва қасамларини озгина қийматга сотиб юборадиган кимсалар учун Охиратда ҳеч қандай насиба йўқдир. Қиёмат Кунида Аллоҳ уларга сўз қотмайди, раҳмат назари билан боқмайди ва гуноҳларидан покламайди. Улар учун аламли азоб бордир'}, {'chapter': 3, 'verse': 78, 'text': 'وَإِنَّ مِنۡهُمۡ لَفَرِيقٗا يَلۡوُۥنَ أَلۡسِنَتَهُم بِٱلۡكِتَٰبِ لِتَحۡسَبُوهُ مِنَ ٱلۡكِتَٰبِ وَمَا هُوَ مِنَ ٱلۡكِتَٰبِ وَيَقُولُونَ هُوَ مِنۡ عِندِ ٱللَّهِ وَمَا هُوَ مِنۡ عِندِ ٱللَّهِۖ وَيَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ يَعۡلَمُونَ\nУлардан (яҳудийлардан) яна бир гуруҳи борки, сизлар Китобдан (Тавротдан) бўлмаган нарсани Китобдан деб ўйлашингиз учун Таврот китобини тилларини бураб (бузиб-ўзгартириб) ўқийдилар ва Аллоҳнинг ҳузуридан келмаган (ўзларининг сўзларини) «Аллоҳнинг ҳузуридан келган», дейдилар. Улар билиб туриб Аллоҳ ҳақида ёлғон сўзлайдилар'}, {'chapter': 3, 'verse': 79, 'text': 'مَا كَانَ لِبَشَرٍ أَن يُؤۡتِيَهُ ٱللَّهُ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُواْ عِبَادٗا لِّي مِن دُونِ ٱللَّهِ وَلَٰكِن كُونُواْ رَبَّـٰنِيِّـۧنَ بِمَا كُنتُمۡ تُعَلِّمُونَ ٱلۡكِتَٰبَ وَبِمَا كُنتُمۡ تَدۡرُسُونَ\nБирор одам учун Аллоҳ унга Китоб, ҳикмат ва пайғамбарлик берганидан сўнг, одамларга: «Аллоҳга эмас, менга ибодат қилинглар», дейиши жоиз эмас, балки уларга: «Аллоҳнинг китобини одамларга таълим бериб ва ўзингиз ўқиб-ўрганиб, ёлғиз Парвардигорга ибодат қиладиган кишилар бўлинглар» (демоғи лозимдир)'}, {'chapter': 3, 'verse': 80, 'text': 'وَلَا يَأۡمُرَكُمۡ أَن تَتَّخِذُواْ ٱلۡمَلَـٰٓئِكَةَ وَٱلنَّبِيِّـۧنَ أَرۡبَابًاۚ أَيَأۡمُرُكُم بِٱلۡكُفۡرِ بَعۡدَ إِذۡ أَنتُم مُّسۡلِمُونَ\nУ (пайғамбар) сизларни фаришталар ва бошқа пайғамбарларни худо қилиб олишингизга буюриши ҳам жоиз эмас. Ахир у сизларни мусулмон — Аллоҳга бўйсунгувчи бўлганингиздан кейин куфр — динсизликка қайтишга буюрадими'}, {'chapter': 3, 'verse': 81, 'text': 'وَإِذۡ أَخَذَ ٱللَّهُ مِيثَٰقَ ٱلنَّبِيِّـۧنَ لَمَآ ءَاتَيۡتُكُم مِّن كِتَٰبٖ وَحِكۡمَةٖ ثُمَّ جَآءَكُمۡ رَسُولٞ مُّصَدِّقٞ لِّمَا مَعَكُمۡ لَتُؤۡمِنُنَّ بِهِۦ وَلَتَنصُرُنَّهُۥۚ قَالَ ءَأَقۡرَرۡتُمۡ وَأَخَذۡتُمۡ عَلَىٰ ذَٰلِكُمۡ إِصۡرِيۖ قَالُوٓاْ أَقۡرَرۡنَاۚ قَالَ فَٱشۡهَدُواْ وَأَنَا۠ مَعَكُم مِّنَ ٱلشَّـٰهِدِينَ\nЭсланг (эй аҳли китоб), Аллоҳ барча пайғамбарлардан: «Мен сизларга қандай Китоб ва ҳикмат бермайин, кейин сизларнинг пайғамбарлигингизни тасдиқ этувчи бир пайғамбар (яъни Муҳаммад алайҳис-салоту вас-салом) келгач, албатта унга иймон келтирурсиз ва ёрдам берурсиз», деб аҳд-паймон олгач, уларга: «Мана шу аҳд-паймонимни иқрор бўлиб қабул қилдингизларми?», деди. У пайғамбарлар: «Иқрормиз», деб жавоб қилишгач, Аллоҳ айтди: «Гувоҳ бўлингиз! Мен ҳам сизлар билан гувоҳ бўлгувчиларданман»'}, {'chapter': 3, 'verse': 82, 'text': 'فَمَن تَوَلَّىٰ بَعۡدَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nБас, ким шундан кейин ҳам (аҳд-паймондан) юз ўгирса, ана ўшандай кимсалар фосиқ — бузуқилардир'}, {'chapter': 3, 'verse': 83, 'text': 'أَفَغَيۡرَ دِينِ ٱللَّهِ يَبۡغُونَ وَلَهُۥٓ أَسۡلَمَ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَإِلَيۡهِ يُرۡجَعُونَ\nАллоҳнинг динидан ўзга дин истайдиларми?! Ахир осмонлару Ердаги барча жонзот истаса-истамаса Унга бўйсуниб турибди-ку! Ўшалар (яъни, Аллоҳнинг динига итоат қилишни хоҳламаётганлар) ҳам фақат Унинг Ўзига қайтариладилар-ку'}, {'chapter': 3, 'verse': 84, 'text': 'قُلۡ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ عَلَيۡنَا وَمَآ أُنزِلَ عَلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَٱلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), «Аллоҳга, бизга нозил қилинган нарсага (Қуръонга) ва Иброҳим, Исмоил, Исҳоқ, Яъқуб ва унинг уруғ-авлодларига нозил қилинган нарсаларга ва Мусо, Ийсо ҳамда барча пайғамбарларга Парвардигорлари томонидан берилган нарсаларга иймон келтирдик. У пайғамбарлардан бирортасини ажратиб қўймаймиз. Ва биз Унинг Ўзигагина бўйсунгувчилармиз», деб айтинг'}, {'chapter': 3, 'verse': 85, 'text': 'وَمَن يَبۡتَغِ غَيۡرَ ٱلۡإِسۡلَٰمِ دِينٗا فَلَن يُقۡبَلَ مِنۡهُ وَهُوَ فِي ٱلۡأٓخِرَةِ مِنَ ٱلۡخَٰسِرِينَ\nКимда-ким Исломдан ўзга дин истаса, бас (унинг «дини» Аллоҳ ҳузурида) ҳаргиз қабул қилинмайди ва у Охиратда зиён кўргувчилардандир'}, {'chapter': 3, 'verse': 86, 'text': 'كَيۡفَ يَهۡدِي ٱللَّهُ قَوۡمٗا كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ وَشَهِدُوٓاْ أَنَّ ٱلرَّسُولَ حَقّٞ وَجَآءَهُمُ ٱلۡبَيِّنَٰتُۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nИймон келтириб, пайғамбарнинг ҳақ пайғамбар эканлигига гувоҳ бўлишганидан кейин ва уларга далил-оятлар келганидан кейин кофир бўлган кимсаларни Аллоҳ қандай ҳидоят қилсин?! Аллоҳ зулм қилгувчи қавмни ҳидоят қилмайди'}, {'chapter': 3, 'verse': 87, 'text': 'أُوْلَـٰٓئِكَ جَزَآؤُهُمۡ أَنَّ عَلَيۡهِمۡ لَعۡنَةَ ٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلنَّاسِ أَجۡمَعِينَ\nУларнинг жазоси — устларига тушажак Аллоҳнинг, фаришталарнинг ва барча одамларнинг лаънатидир'}, {'chapter': 3, 'verse': 88, 'text': 'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ\nУлар азоблари енгиллатилмаган ҳолда дўзахда абадий қолгувчилардир ва уларга (бу азоб бирон лаҳза) кечиктирилмайди'}, {'chapter': 3, 'verse': 89, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nМагар шундан (яъни, куфрларидан) кейин тавба қилиб, ўзларини ўнгласалар (Аллоҳ гуноҳларини мағфират қилур). Чунки Аллоҳ мағфират қилгувчи, меҳрибондир'}, {'chapter': 3, 'verse': 90, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ ثُمَّ ٱزۡدَادُواْ كُفۡرٗا لَّن تُقۡبَلَ تَوۡبَتُهُمۡ وَأُوْلَـٰٓئِكَ هُمُ ٱلضَّآلُّونَ\nАлбатта иймон келтирганларидан кейин кофир бўлган, сўнгра куфрлари янада зиёда бўлган кимсаларнинг қилган тавбалари ҳаргиз қабул қилинмайди. Ана ўшалар адашганлардир'}, {'chapter': 3, 'verse': 91, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يُقۡبَلَ مِنۡ أَحَدِهِم مِّلۡءُ ٱلۡأَرۡضِ ذَهَبٗا وَلَوِ ٱفۡتَدَىٰ بِهِۦٓۗ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nАлбатта кофир бўлган ва кофир ҳолда ўлган кимсалар, агар улардан биронтаси Ер юзи тўла олтинни тўлов қилиб берса ҳам ҳаргиз қабул қилинмас. Ундай кимсалар учун аламли азоб бордир. Ва улар учун ҳеч қандай ёрдамчи бўлмас'}, {'chapter': 3, 'verse': 92, 'text': 'لَن تَنَالُواْ ٱلۡبِرَّ حَتَّىٰ تُنفِقُواْ مِمَّا تُحِبُّونَۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٞ\nСуйган нарсаларингиздан инфоқ-эҳсон қилиб бермагунингизча ҳаргиз яхшиликка (жаннатга) етмагайсиз. Ҳар қандай нарсани инфоқ қилсангиз, албатта Аллоҳ уни билур'}, {'chapter': 3, 'verse': 93, 'text': '۞كُلُّ ٱلطَّعَامِ كَانَ حِلّٗا لِّبَنِيٓ إِسۡرَـٰٓءِيلَ إِلَّا مَا حَرَّمَ إِسۡرَـٰٓءِيلُ عَلَىٰ نَفۡسِهِۦ مِن قَبۡلِ أَن تُنَزَّلَ ٱلتَّوۡرَىٰةُۚ قُلۡ فَأۡتُواْ بِٱلتَّوۡرَىٰةِ فَٱتۡلُوهَآ إِن كُنتُمۡ صَٰدِقِينَ\nТаврот туширилишидан илгари Исроил (Яъқуб) ўзига ҳаром қилган нарса (туянинг гўшти билан сути) дан бошқа барча таомлар Бани Исроил қавмига ҳалол эди. (Эй Муҳаммад алайҳис-салоту вас-салом), «Агар ростгўй бўлсангизлар, Тавротни келтириб ўқиб кўринглар», деб айтинг'}, {'chapter': 3, 'verse': 94, 'text': 'فَمَنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ مِنۢ بَعۡدِ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nБас, ким мана шундан кейин ҳам Аллоҳга бўҳтон қилса, ана ўшалар зулм қилгувчилардир'}, {'chapter': 3, 'verse': 95, 'text': 'قُلۡ صَدَقَ ٱللَّهُۗ فَٱتَّبِعُواْ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), «Аллоҳнинг сўзи ростдир. Бас, ҳақ йўлдан тоймаган ва мушриклардан бўлмаган Иброҳимнинг динига эргашинглар!», деб айтинг'}, {'chapter': 3, 'verse': 96, 'text': 'إِنَّ أَوَّلَ بَيۡتٖ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبَارَكٗا وَهُدٗى لِّلۡعَٰلَمِينَ\nАлбатта, одамлар ибодат қилишлари учун қурилган биринчи уй Маккадаги муборак ва бутун оламлар учун ҳидоят (маёғи) бўлган Каъбадир'}, {'chapter': 3, 'verse': 97, 'text': 'فِيهِ ءَايَٰتُۢ بَيِّنَٰتٞ مَّقَامُ إِبۡرَٰهِيمَۖ وَمَن دَخَلَهُۥ كَانَ ءَامِنٗاۗ وَلِلَّهِ عَلَى ٱلنَّاسِ حِجُّ ٱلۡبَيۡتِ مَنِ ٱسۡتَطَاعَ إِلَيۡهِ سَبِيلٗاۚ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ\nУнда мақоми Иброҳим (яъни, Каъбани қуришда Иброҳим алайҳиссалом оёқлари остида бўлган харсанг тош, у тошдан ҳанузгача Иброҳим пайғамбарнинг оёқ излари кетмаган) ва унга кирган одам ҳар қандай хавф-хатардан омон бўлиши (каби) очиқ оят-аломатлар бордир. Ва йўлга қодир бўлган кишилар зиммасида Аллоҳ учун мана шу уйни ҳаж-зиёрат қилиш бурчи бордир. Кимда-ким кофир бўлса (яъни, Каъбани зиёрат қилиш фарз эканини инкор қилса), бас, албатта Аллоҳ бутун оламлардан беҳожат бўлган Зотдир'}, {'chapter': 3, 'verse': 98, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَٱللَّهُ شَهِيدٌ عَلَىٰ مَا تَعۡمَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Эй аҳли китоб, нима учун Аллоҳнинг оятларини инкор қилмоқдасиз? Ахир Аллоҳ қилаётган амалларингизга гувоҳ-ку!»'}, {'chapter': 3, 'verse': 99, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَصُدُّونَ عَن سَبِيلِ ٱللَّهِ مَنۡ ءَامَنَ تَبۡغُونَهَا عِوَجٗا وَأَنتُمۡ شُهَدَآءُۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nАйтинг: «Эй аҳли китоб, нима учун иймон келтирган кишиларни Аллоҳнинг йўлидан тўсасизлар ва ўзингиз гувоҳ бўлган ҳолингизда ундан (яъни, Аллоҳнинг йўлидан) қийиқ ахтарасизлар? Ахир Аллоҳ қилаётган амалларингиздан ғофил эмас-ку!»'}, {'chapter': 3, 'verse': 100, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ فَرِيقٗا مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ يَرُدُّوكُم بَعۡدَ إِيمَٰنِكُمۡ كَٰفِرِينَ\nЭй мўминлар, агар китоб берилган кимсаларнинг баъзи бир гуруҳларига бўйсунсангиз, улар сизларни иймонга келганингиздан кейин яна кофирликка қайтарадилар'}, {'chapter': 3, 'verse': 101, 'text': 'وَكَيۡفَ تَكۡفُرُونَ وَأَنتُمۡ تُتۡلَىٰ عَلَيۡكُمۡ ءَايَٰتُ ٱللَّهِ وَفِيكُمۡ رَسُولُهُۥۗ وَمَن يَعۡتَصِم بِٱللَّهِ فَقَدۡ هُدِيَ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳнинг оятлари сизларга тиловат қилинаётган, Унинг пайғамбари орангизда бўлган бир пайтда қандай қилиб диндан қайтасиз?! Ким Аллоҳга боғланса (яъни, Унинг динини маҳкам ушласа) бас, муҳаққақки, Тўғри Йўлга ҳидоят қилинибди'}, {'chapter': 3, 'verse': 102, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ حَقَّ تُقَاتِهِۦ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ\nЭй мўминлар, Аллоҳдан ҳақ-рост қўрқиш билан қўрқинглар ва фақат мусулмон бўлган ҳолларингда дунёдан ўтинглар'}, {'chapter': 3, 'verse': 103, 'text': 'وَٱعۡتَصِمُواْ بِحَبۡلِ ٱللَّهِ جَمِيعٗا وَلَا تَفَرَّقُواْۚ وَٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ كُنتُمۡ أَعۡدَآءٗ فَأَلَّفَ بَيۡنَ قُلُوبِكُمۡ فَأَصۡبَحۡتُم بِنِعۡمَتِهِۦٓ إِخۡوَٰنٗا وَكُنتُمۡ عَلَىٰ شَفَا حُفۡرَةٖ مِّنَ ٱلنَّارِ فَأَنقَذَكُم مِّنۡهَاۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَهۡتَدُونَ\nВа барчангиз Аллоҳнинг арқонига (Қуръонга) боғланингиз ва бўлинмангиз! Ҳамда Аллоҳнинг сизларга берган неъматини эсланг: бир-бирингизга душман бўлган пайтларингизда дилларингизни ошно қилиб қўйди-ю, сизлар Унинг неъмати сабаб биродарларга айландингиз. Ва дўзах чоҳининг ёқасида турган эдингиз, сизларни ундан халос қилди. Ҳақ йўлни топишингиз учун Аллоҳ сизларга ўз оятларини мана шундай баён қилади'}, {'chapter': 3, 'verse': 104, 'text': 'وَلۡتَكُن مِّنكُمۡ أُمَّةٞ يَدۡعُونَ إِلَى ٱلۡخَيۡرِ وَيَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nОраларингиздан яхшиликка (Исломга) даъват қиладиган, ибодат-итоатга буюрадиган ва исён-гуноҳдан қайтарадиган бир жамоат бўлсин. Ана ўшалар нажот топгувчилардир'}, {'chapter': 3, 'verse': 105, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ تَفَرَّقُواْ وَٱخۡتَلَفُواْ مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡبَيِّنَٰتُۚ وَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ عَظِيمٞ\nАниқ ҳужжатлар келганидан кейин бўлиниб кетган ва бир-бирлари билан ихтилоф қилиб, талашиб-тортишган кимсалар каби бўлмангиз! Ана ундайлар учун улуғ азоб бордир'}, {'chapter': 3, 'verse': 106, 'text': 'يَوۡمَ تَبۡيَضُّ وُجُوهٞ وَتَسۡوَدُّ وُجُوهٞۚ فَأَمَّا ٱلَّذِينَ ٱسۡوَدَّتۡ وُجُوهُهُمۡ أَكَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n(Қиёмат) Кунида (мўминларнинг) юзлари оқ бўлур, (кофирларнинг) юзлари қора бўлур. Энди юзлари қора бўлган кимсаларга: «Иймон келтиргандан кейин яна диндан чиқдингизми? Бас, кофир бўлганингиз касофатига мана бу азобни тотиб кўрингиз», дейилур'}, {'chapter': 3, 'verse': 107, 'text': 'وَأَمَّا ٱلَّذِينَ ٱبۡيَضَّتۡ وُجُوهُهُمۡ فَفِي رَحۡمَةِ ٱللَّهِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЮзлари оқ бўлган зотлар эса Аллоҳнинг раҳмати (жаннати)да бўлиб, ўша жойда абадий қолажаклар'}, {'chapter': 3, 'verse': 108, 'text': 'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۗ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعَٰلَمِينَ\nБулар Аллоҳнинг оятларидир. Сизга уларни ҳақ-рост тиловат қилмоқдамиз. Аллоҳ барча оламларга (яъни, ҳеч кимга) бирон зулм қилишни истамас'}, {'chapter': 3, 'verse': 109, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nОсмонлардаги бор нарсалар ва Ердаги бор нарсалар Аллоҳникидир. Ва барча ишлар ёлғиз Аллоҳга қайтарилажак'}, {'chapter': 3, 'verse': 110, 'text': 'كُنتُمۡ خَيۡرَ أُمَّةٍ أُخۡرِجَتۡ لِلنَّاسِ تَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَتَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَتُؤۡمِنُونَ بِٱللَّهِۗ وَلَوۡ ءَامَنَ أَهۡلُ ٱلۡكِتَٰبِ لَكَانَ خَيۡرٗا لَّهُمۚ مِّنۡهُمُ ٱلۡمُؤۡمِنُونَ وَأَكۡثَرُهُمُ ٱلۡفَٰسِقُونَ\n(Эй уммати Муҳаммад), сизлар одамлар учун чиқарилган миллатларнинг энг яхшиси бўлдингиз. Зеро сизлар яхши амалларга буюрасиз, ёмон амаллардан қайтарасиз ва Аллоҳга иймон келтирасиз. Агар аҳли китоб ҳам иймон келтирганда эди, ўзлари учун яхши бўларди. Улардан мўминлари ҳам бор ва (лекин) уларнинг кўплари итоатсиз кимсалардир'}, {'chapter': 3, 'verse': 111, 'text': 'لَن يَضُرُّوكُمۡ إِلَّآ أَذٗىۖ وَإِن يُقَٰتِلُوكُمۡ يُوَلُّوكُمُ ٱلۡأَدۡبَارَ ثُمَّ لَا يُنصَرُونَ\nУлар сизларга озор беришдан бошқа ҳеч қандай зарар етказа олмайдилар. Агар сизлар билан урушсалар, орқаларига қараб қочадилар. Сўнгра уларга (ҳеч ким томонидан) ёрдам берилмайди'}, {'chapter': 3, 'verse': 112, 'text': 'ضُرِبَتۡ عَلَيۡهِمُ ٱلذِّلَّةُ أَيۡنَ مَا ثُقِفُوٓاْ إِلَّا بِحَبۡلٖ مِّنَ ٱللَّهِ وَحَبۡلٖ مِّنَ ٱلنَّاسِ وَبَآءُو بِغَضَبٖ مِّنَ ٱللَّهِ وَضُرِبَتۡ عَلَيۡهِمُ ٱلۡمَسۡكَنَةُۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ\nАгар Аллоҳнинг ва одамларнинг (яъни, мусулмонларнинг) аҳди паноҳида бўлмасалар, албатта уларга қаерда бўлмасинлар, хор-зорлик битиб қўйилди. Улар Аллоҳнинг ғазаби билан кетдилар ва уларга бечораҳоллик ёзиб қўйилди. Бунга сабаб уларнинг Аллоҳ оятларига кофир бўлганликлари ва пайғамбарларни ноҳақ ўлдирганликларидир. Бунга сабаб уларнинг қилган исёнлари ва тажовузкор бўлганларидир'}, {'chapter': 3, 'verse': 113, 'text': '۞لَيۡسُواْ سَوَآءٗۗ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ أُمَّةٞ قَآئِمَةٞ يَتۡلُونَ ءَايَٰتِ ٱللَّهِ ءَانَآءَ ٱلَّيۡلِ وَهُمۡ يَسۡجُدُونَ\n(Аҳли китобнинг ҳаммаси ҳам) баробар эмас. Аҳли китоб орасида сажда қилган ҳолларида тунлари Аллоҳнинг оятларини тиловат қиладиган Тўғри Йўлдаги жамоат ҳам бор'}, {'chapter': 3, 'verse': 114, 'text': 'يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَيَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَيُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِۖ وَأُوْلَـٰٓئِكَ مِنَ ٱلصَّـٰلِحِينَ\n(Ўша зотлар) Аллоҳга ва Охират кунига иймон келтирадилар, яхши амалларга буюриб, ёмон амаллардан қайтарадилар ва яхшилик қилишга шошиладилар. Ана ўшалар солиҳ бандалардандирлар'}, {'chapter': 3, 'verse': 115, 'text': 'وَمَا يَفۡعَلُواْ مِنۡ خَيۡرٖ فَلَن يُكۡفَرُوهُۗ وَٱللَّهُ عَلِيمُۢ بِٱلۡمُتَّقِينَ\nУлар қилган бирон яхшилик беркитилмайди (яъни, зое бўлмайди). Аллоҳ тақводорларни билгувчидир'}, {'chapter': 3, 'verse': 116, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـٔٗاۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nКуфр йўлини тутган кимсаларни на моллари ва на болалари Аллоҳ томонидан бўладиган нарса- (азобдан) қутқара олмайди. Ана ўшалар жаҳаннам эгалари бўлиб, ўша жойда абадий қолажаклар'}, {'chapter': 3, 'verse': 117, 'text': 'مَثَلُ مَا يُنفِقُونَ فِي هَٰذِهِ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَثَلِ رِيحٖ فِيهَا صِرٌّ أَصَابَتۡ حَرۡثَ قَوۡمٖ ظَلَمُوٓاْ أَنفُسَهُمۡ فَأَهۡلَكَتۡهُۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَٰكِنۡ أَنفُسَهُمۡ يَظۡلِمُونَ\nУлар бу дунёда (хўжакўрсинга) қилган инфоқ-эҳсонларининг мисоли худди динсизлик билан ўзларига зулм қилган қавмнинг экинига тегиб, уни ҳалок қилган совуқ шамолга ўхшайди. Уларга Аллоҳ зулм қилмади. Балки улар ўзларига зулм қилдилар'}, {'chapter': 3, 'verse': 118, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ بِطَانَةٗ مِّن دُونِكُمۡ لَا يَأۡلُونَكُمۡ خَبَالٗا وَدُّواْ مَا عَنِتُّمۡ قَدۡ بَدَتِ ٱلۡبَغۡضَآءُ مِنۡ أَفۡوَٰهِهِمۡ وَمَا تُخۡفِي صُدُورُهُمۡ أَكۡبَرُۚ قَدۡ بَيَّنَّا لَكُمُ ٱلۡأٓيَٰتِۖ إِن كُنتُمۡ تَعۡقِلُونَ\nЭй мўминлар, ўзларингни қўйиб, (у мунофиқларни) сирдош дўст тутманглар! Улар сизларга зарар етказишда кучларини аямайдилар ва ёмон ҳолга тушишингизни орзу қиладилар. Уларнинг сизларни ёмон кўришлари оғизларидан ошкор бўлди. Дилларидаги адоватлари эса янада каттароқдир. Агар ақл юргизсангизлар сизлар учун оят-аломатларни аниқ-равшан қилиб бердик'}, {'chapter': 3, 'verse': 119, 'text': 'هَـٰٓأَنتُمۡ أُوْلَآءِ تُحِبُّونَهُمۡ وَلَا يُحِبُّونَكُمۡ وَتُؤۡمِنُونَ بِٱلۡكِتَٰبِ كُلِّهِۦ وَإِذَا لَقُوكُمۡ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ عَضُّواْ عَلَيۡكُمُ ٱلۡأَنَامِلَ مِنَ ٱلۡغَيۡظِۚ قُلۡ مُوتُواْ بِغَيۡظِكُمۡۗ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nҲой (мўминлар), сизлар уларни яхши кўрасизлар-у, улар сизларни суймайдилар. Сизлар ҳамма китобларга иймон келтирасизлар-у, (улар сизларнинг китобингизга иймон келтирмайдилар). Сизларга йўлиққанларида: «иймон келтирдик», дейишади. Ўзлари холи қолишганда эса сизларни қаттиқ ёмон кўрганлари сабабли бармоқларини тишлайдилар. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга): «Шу адоватларинг билан ўлиб кетинглар!», деб айтинг! Албатта, Аллоҳ дилларни эгаллаган сирларни билгувчидир'}, {'chapter': 3, 'verse': 120, 'text': 'إِن تَمۡسَسۡكُمۡ حَسَنَةٞ تَسُؤۡهُمۡ وَإِن تُصِبۡكُمۡ سَيِّئَةٞ يَفۡرَحُواْ بِهَاۖ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ لَا يَضُرُّكُمۡ كَيۡدُهُمۡ شَيۡـًٔاۗ إِنَّ ٱللَّهَ بِمَا يَعۡمَلُونَ مُحِيطٞ\nАгар сизларга бирон яхшилик етса, бу уларни хафа қилади. Агар сабр-тоқат қилсангиз ва Аллоҳдан қўрқсангиз, уларнинг найранглари сизларга ҳеч қандай зарар қила олмайди. Албатта, Аллоҳ уларнинг қилаётган амалларини иҳота қилгувчидир'}, {'chapter': 3, 'verse': 121, 'text': 'وَإِذۡ غَدَوۡتَ مِنۡ أَهۡلِكَ تُبَوِّئُ ٱلۡمُؤۡمِنِينَ مَقَٰعِدَ لِلۡقِتَالِۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), аҳли оилангиз ҳузуридан чиқиб, мўминларни урушадиган жойларига тахт қилиб қўйган пайтингизни эсланг! Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 3, 'verse': 122, 'text': 'إِذۡ هَمَّت طَّآئِفَتَانِ مِنكُمۡ أَن تَفۡشَلَا وَٱللَّهُ وَلِيُّهُمَاۗ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nЎша пайт сизларнинг орангиздан икки гуруҳ умидсизликка туша бошлади. Ҳолбуки, Аллоҳ уларга ёр эди. Бас, мўминлар ёлғиз Аллоҳгагина суянсинлар'}, {'chapter': 3, 'verse': 123, 'text': 'وَلَقَدۡ نَصَرَكُمُ ٱللَّهُ بِبَدۡرٖ وَأَنتُمۡ أَذِلَّةٞۖ فَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تَشۡكُرُونَ\nАхир Аллоҳ Бадр жангида кучсиз бўлсангиз-да, сизларни ғолиб қилди-ку. Бас, Аллоҳдан қўрқингиз! Шунда шояд (унинг неъматларига) шукр қилгайсиз'}, {'chapter': 3, 'verse': 124, 'text': 'إِذۡ تَقُولُ لِلۡمُؤۡمِنِينَ أَلَن يَكۡفِيَكُمۡ أَن يُمِدَّكُمۡ رَبُّكُم بِثَلَٰثَةِ ءَالَٰفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُنزَلِينَ\nМўминларга: «Парвардигорингиз сизларга уч минг кўкдан туширилган фаришта билан мадад бериши кифоя қилмайдими?», деган пайтингизни эсланг'}, {'chapter': 3, 'verse': 125, 'text': 'بَلَىٰٓۚ إِن تَصۡبِرُواْ وَتَتَّقُواْ وَيَأۡتُوكُم مِّن فَوۡرِهِمۡ هَٰذَا يُمۡدِدۡكُمۡ رَبُّكُم بِخَمۡسَةِ ءَالَٰفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُسَوِّمِينَ\nЙўқ, (кифоя қилур). Агар сабр қилсангизлар ва Аллоҳдан қўрқсангизлар ва улар (кофирлар) шу онда сизларга ҳужум қилсалар, Парвардигорингиз сизларга беш минг белгили фаришта билан мадад берур'}, {'chapter': 3, 'verse': 126, 'text': 'وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ لَكُمۡ وَلِتَطۡمَئِنَّ قُلُوبُكُم بِهِۦۗ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nБу (мададни) Аллоҳ фақат сизларга хушхабар бўлсин, деб ва дилларингиз таскин топишлиги учун қилди. Аслида ғалаба фақат қудрат ва ҳикмат эгаси бўлган Аллоҳ ҳузуридан келур'}, {'chapter': 3, 'verse': 127, 'text': 'لِيَقۡطَعَ طَرَفٗا مِّنَ ٱلَّذِينَ كَفَرُوٓاْ أَوۡ يَكۡبِتَهُمۡ فَيَنقَلِبُواْ خَآئِبِينَ\nКофирларнинг бир қисмини ҳалок қилиш ёки мағлуб қилиб, ноумид ҳолда қайтариш учун (Аллоҳ сизларни ғолиб қилди)'}, {'chapter': 3, 'verse': 128, 'text': 'لَيۡسَ لَكَ مِنَ ٱلۡأَمۡرِ شَيۡءٌ أَوۡ يَتُوبَ عَلَيۡهِمۡ أَوۡ يُعَذِّبَهُمۡ فَإِنَّهُمۡ ظَٰلِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сиз учун бу ишда (яъни, бандаларга нажот бериш ёки уларни ҳалок қилиш ишида) ҳеч бир нарса (яъни, инон-ихтиёр) йўқдир. (Аллоҳнинг Ўзи) ё уларнинг тавбаларини қабул қилар ёки золимликлари сабабли азоблар'}, {'chapter': 3, 'verse': 129, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nОсмонлар ва Ердаги бор нарса Аллоҳникидир. У Зот Ўзи истаган кишининг гуноҳларини мағфират қилур ва Ўзи истаган кимсани азоблар. Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 3, 'verse': 130, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَأۡكُلُواْ ٱلرِّبَوٰٓاْ أَضۡعَٰفٗا مُّضَٰعَفَةٗۖ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй мўминлар, (берган қарзларингизни) бир неча баробар қилиб олиш билан судхўрлик қилмангиз! Аллоҳдан қўрқингиз! (Шунда) шояд нажот топгайсиз'}, {'chapter': 3, 'verse': 131, 'text': 'وَٱتَّقُواْ ٱلنَّارَ ٱلَّتِيٓ أُعِدَّتۡ لِلۡكَٰفِرِينَ\nВа кофирлар учун тайёрлаб қўйилган дўзахдан қўрқингиз'}, {'chapter': 3, 'verse': 132, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ\nАллоҳ ва пайғамбарга итоат қилингиз. Шояд Аллоҳнинг раҳматига мушарраф бўлсангиз'}, {'chapter': 3, 'verse': 133, 'text': '۞وَسَارِعُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ أُعِدَّتۡ لِلۡمُتَّقِينَ\nВа Парвардигорингиз томонидан бўлғуси мағфиратга ҳамда тақводорлар учун тайёрлаб қўйилган эни осмонлар ва Ер баробарида бўлган жаннатга шошилингиз'}, {'chapter': 3, 'verse': 134, 'text': 'ٱلَّذِينَ يُنفِقُونَ فِي ٱلسَّرَّآءِ وَٱلضَّرَّآءِ وَٱلۡكَٰظِمِينَ ٱلۡغَيۡظَ وَٱلۡعَافِينَ عَنِ ٱلنَّاسِۗ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ\n(У тақводор зотлар) яхши-ёмон кунларда инфоқ-эҳсон қиладиган, ғазабларини ичларига ютадиган, одамларнинг (хато-камчиликларини) афв этадиган кишилардир. Аллоҳ бундай яхшилик қилувчиларни севади'}, {'chapter': 3, 'verse': 135, 'text': 'وَٱلَّذِينَ إِذَا فَعَلُواْ فَٰحِشَةً أَوۡ ظَلَمُوٓاْ أَنفُسَهُمۡ ذَكَرُواْ ٱللَّهَ فَٱسۡتَغۡفَرُواْ لِذُنُوبِهِمۡ وَمَن يَغۡفِرُ ٱلذُّنُوبَ إِلَّا ٱللَّهُ وَلَمۡ يُصِرُّواْ عَلَىٰ مَا فَعَلُواْ وَهُمۡ يَعۡلَمُونَ\n(У тақводор зотлар) қачон бирон-бир нолойиқ гуноҳ иш қилиб қўйсалар ёки (қандайдир гуноҳ иш қилиш билан) ўзларига зулм қилсалар, дарҳол Аллоҳни эслаб, гуноҳларини мағфират-кечирим сўрайдиган, — ҳар қандай гуноҳни ёлғиз Аллоҳгина мағфират қилур, — ва билган ҳолларида қилган гуноҳларида давом этмайдиган кишилардир'}, {'chapter': 3, 'verse': 136, 'text': 'أُوْلَـٰٓئِكَ جَزَآؤُهُم مَّغۡفِرَةٞ مِّن رَّبِّهِمۡ وَجَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَنِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ\nУларнинг мукофотлари Парвардигорлари томонидан мағфират ва тагларидан дарёлар оқиб тургувчи жаннатлар бўлиб, улар ўша жойда абадий қолажаклар. Яхши амал қилгувчиларнинг ажрлари нақадар яхши ажр'}, {'chapter': 3, 'verse': 137, 'text': 'قَدۡ خَلَتۡ مِن قَبۡلِكُمۡ سُنَنٞ فَسِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\n(Эй мўминлар), сизлардан илгари ҳам суннатлар — ибратлар ўтгандир. Бас, Ерда айланиб юриб, Аллоҳнинг динини ёлғон деганларнинг оқибатлари қандай бўлганини бир кўринглар'}, {'chapter': 3, 'verse': 138, 'text': 'هَٰذَا بَيَانٞ لِّلنَّاسِ وَهُدٗى وَمَوۡعِظَةٞ لِّلۡمُتَّقِينَ\nБу (Қуръон) одамлар учун Тўғри Йўлни баён қилгувчи ва тақво эгалари учун ҳидоят ва панд-насиҳатдир'}, {'chapter': 3, 'verse': 139, 'text': 'وَلَا تَهِنُواْ وَلَا تَحۡزَنُواْ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ إِن كُنتُم مُّؤۡمِنِينَ\nКуфрга қарши курашда (сустлашмангиз) ва (Уҳуд жангидаги мағлубият сабабли) ғамгин бўлмангиз! Агар (ҳақиқий) иймон эгалари бўлсангизлар, сизлар устун бўлгувчидирсизлар'}, {'chapter': 3, 'verse': 140, 'text': 'إِن يَمۡسَسۡكُمۡ قَرۡحٞ فَقَدۡ مَسَّ ٱلۡقَوۡمَ قَرۡحٞ مِّثۡلُهُۥۚ وَتِلۡكَ ٱلۡأَيَّامُ نُدَاوِلُهَا بَيۡنَ ٱلنَّاسِ وَلِيَعۡلَمَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَيَتَّخِذَ مِنكُمۡ شُهَدَآءَۗ وَٱللَّهُ لَا يُحِبُّ ٱلظَّـٰلِمِينَ\nАгар (бугун — Уҳудда) сизларга жароҳат етган бўлса, (Бадр жангида) у қавмга ҳам худди шундай жароҳат етган. (Токи ибрат бўлсин деб) ва Аллоҳ ҳақиқий иймон келтирган кишиларни билиши ҳамда ораларингдан шаҳидларни саралаб олиши учун бу кунларни (яъни, ғалаба ва мағлубият кунларини) одамлар орасида айлантириб турамиз. Аллоҳ золим кимсаларни севмайди'}, {'chapter': 3, 'verse': 141, 'text': 'وَلِيُمَحِّصَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَيَمۡحَقَ ٱلۡكَٰفِرِينَ\nВа токи Аллоҳ иймон келтирган зотларни (гуноҳларидан) поклаш ва кофирларни ҳалок қилиш учун (бу кунларни одамлар орасида айлантириб туради)'}, {'chapter': 3, 'verse': 142, 'text': 'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ ٱلۡجَنَّةَ وَلَمَّا يَعۡلَمِ ٱللَّهُ ٱلَّذِينَ جَٰهَدُواْ مِنكُمۡ وَيَعۡلَمَ ٱلصَّـٰبِرِينَ\n(Эй мўминлар), ёки Аллоҳ сизларнинг ичингиздан ким ҳақ йўлда курашган-у, ким сабр-тоқат қилганини мутлақо билмай туриб жаннатга кирамиз, деб ўйладингизми'}, {'chapter': 3, 'verse': 143, 'text': 'وَلَقَدۡ كُنتُمۡ تَمَنَّوۡنَ ٱلۡمَوۡتَ مِن قَبۡلِ أَن تَلۡقَوۡهُ فَقَدۡ رَأَيۡتُمُوهُ وَأَنتُمۡ تَنظُرُونَ\nЎлимга рўбарў бўлишингиздан илгари уни орзу қилар эдингиз. Мана энди (яъни, Уҳудда) уни кўзингиз билан кўриб турибсиз'}, {'chapter': 3, 'verse': 144, 'text': 'وَمَا مُحَمَّدٌ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُۚ أَفَإِيْن مَّاتَ أَوۡ قُتِلَ ٱنقَلَبۡتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡۚ وَمَن يَنقَلِبۡ عَلَىٰ عَقِبَيۡهِ فَلَن يَضُرَّ ٱللَّهَ شَيۡـٔٗاۚ وَسَيَجۡزِي ٱللَّهُ ٱلشَّـٰكِرِينَ\nМуҳаммад фақат бир пайғамбар, холос. Ундан илгари ҳам пайғамбарлар ўтгандир. Бас, агар у (яъни, Муҳаммад алайҳис-салоту вас-салом) вафот қилса ёки ўлдирилса, орқангизга (куфрга) қайтиб кетасизми?! Кимда-ким орқасига қайтиб кетса, Аллоҳга бирон зиён етказа олмас, (балки фақат ўзига зарар қилади, холос). Аллоҳ эса (йўлларидан қайтмай) шукр қилгувчи бандаларини муносиб мукофотлайди'}, {'chapter': 3, 'verse': 145, 'text': 'وَمَا كَانَ لِنَفۡسٍ أَن تَمُوتَ إِلَّا بِإِذۡنِ ٱللَّهِ كِتَٰبٗا مُّؤَجَّلٗاۗ وَمَن يُرِدۡ ثَوَابَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَن يُرِدۡ ثَوَابَ ٱلۡأٓخِرَةِ نُؤۡتِهِۦ مِنۡهَاۚ وَسَنَجۡزِي ٱلشَّـٰكِرِينَ\nҲар бир жон фақат Аллоҳнинг изни билан ва аниқ белгилаб қўйилган муддатда ўлади. Ким дунё савобини истаса (яъни, фақат ўлжа истаб жангга кирса) унга ўша истаган нарсасини берурмиз. Ким Охират савобини истаса, унга-да истаган нарсасини берурмиз. Ва шукр қилгувчиларни муносиб мукофотлаймиз'}, {'chapter': 3, 'verse': 146, 'text': 'وَكَأَيِّن مِّن نَّبِيّٖ قَٰتَلَ مَعَهُۥ رِبِّيُّونَ كَثِيرٞ فَمَا وَهَنُواْ لِمَآ أَصَابَهُمۡ فِي سَبِيلِ ٱللَّهِ وَمَا ضَعُفُواْ وَمَا ٱسۡتَكَانُواْۗ وَٱللَّهُ يُحِبُّ ٱلصَّـٰبِرِينَ\nҚанчадан-қанча пайғамбарлар ўтганки, улар билан биргаликда кўпдан-кўп художўйлар (кофирларга қарши) жанг қилганлар. Ҳамда Аллоҳ йўлида ўзларига етган машаққатлар сабабли сусткашлик-заифлик қилмаганлар ва (кофирларга) бўйин эгмаганлар. Аллоҳ мана шундай сабр қилгувчиларни севади'}, {'chapter': 3, 'verse': 147, 'text': 'وَمَا كَانَ قَوۡلَهُمۡ إِلَّآ أَن قَالُواْ رَبَّنَا ٱغۡفِرۡ لَنَا ذُنُوبَنَا وَإِسۡرَافَنَا فِيٓ أَمۡرِنَا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nУларнинг айтган гаплари фақат шундан иборат эди: «Парвардигоро, гуноҳларимизни ва ишларимиздаги хатоларимизни мағфират айла, (жанг майдонида) қадамларимизни собит қил ва бу кофир қавм устига Ўзинг бизни ғолиб қил!»'}, {'chapter': 3, 'verse': 148, 'text': 'فَـَٔاتَىٰهُمُ ٱللَّهُ ثَوَابَ ٱلدُّنۡيَا وَحُسۡنَ ثَوَابِ ٱلۡأٓخِرَةِۗ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ\nБас, Аллоҳ уларга ҳам бу дунё савобини (ғалаба ва мол-давлатни) ва ҳам Охиратдаги гўзал ажрни (жаннатни) ато қилди. Аллоҳ чиройли амал қилгувчиларни севади'}, {'chapter': 3, 'verse': 149, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ ٱلَّذِينَ كَفَرُواْ يَرُدُّوكُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ\nЭй мўминлар, агар кофирларга итоат қилсангиз, сизларни кетингизга (куфрга) қайтарадилар. Бас, зиён кўргувчиларга айланиб қоласизлар'}, {'chapter': 3, 'verse': 150, 'text': 'بَلِ ٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ خَيۡرُ ٱلنَّـٰصِرِينَ\nЙўқ, (улар сизларнинг дўстингиз эмас, балки) Аллоҳ Хожангиздир. Ва У зот энг яхши ёрдамчидир'}, {'chapter': 3, 'verse': 151, 'text': 'سَنُلۡقِي فِي قُلُوبِ ٱلَّذِينَ كَفَرُواْ ٱلرُّعۡبَ بِمَآ أَشۡرَكُواْ بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗاۖ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَبِئۡسَ مَثۡوَى ٱلظَّـٰلِمِينَ\n(Илоҳ эканлигига) ҳеч қандай ҳужжат-далил келмаган нарсани (яъни, жонсиз бутларни) Аллоҳга шерик қилганлари сабабли кофирларнинг дилларига қўрқув солажакмиз. Уларнинг борар жойлари жаҳаннамдир, зулм қилгувчиларнинг жойлари нақадар ёмон жой'}, {'chapter': 3, 'verse': 152, 'text': 'وَلَقَدۡ صَدَقَكُمُ ٱللَّهُ وَعۡدَهُۥٓ إِذۡ تَحُسُّونَهُم بِإِذۡنِهِۦۖ حَتَّىٰٓ إِذَا فَشِلۡتُمۡ وَتَنَٰزَعۡتُمۡ فِي ٱلۡأَمۡرِ وَعَصَيۡتُم مِّنۢ بَعۡدِ مَآ أَرَىٰكُم مَّا تُحِبُّونَۚ مِنكُم مَّن يُرِيدُ ٱلدُّنۡيَا وَمِنكُم مَّن يُرِيدُ ٱلۡأٓخِرَةَۚ ثُمَّ صَرَفَكُمۡ عَنۡهُمۡ لِيَبۡتَلِيَكُمۡۖ وَلَقَدۡ عَفَا عَنكُمۡۗ وَٱللَّهُ ذُو فَضۡلٍ عَلَى ٱلۡمُؤۡمِنِينَ\nАллоҳ ўз ваъдасининг устидан чиқди — сизлар Унинг изни билан кофирларни қира бошладингиз. То сусткашлик қилиб, (Пайғамбар алайҳис-салоту вас-саломнинг) амру фармонлари борасида талашиб-тортишган ва Аллоҳ сизлар яхши кўрган нарсани (яъни, ғалабани) кўзингизга кўрсатиб қўйганидан кейин пайғамбарнинг амридан бош тортган пайтингизгача (бу устунликларингиз давом этди). Орангизда дунё истаган кишилар ҳам, Охират истаган кишилар ҳам бор эди. Сўнгра сизларни имтиҳон қилиш учун уларнинг (устидан ғалаба қилиш ўрнига мағлубият томонга) буриб юборди. Энди гуноҳларингизни афв қилди. Аллоҳ иймон эгаларига фазлу марҳамат соҳиби бўлган Зотдир'}, {'chapter': 3, 'verse': 153, 'text': '۞إِذۡ تُصۡعِدُونَ وَلَا تَلۡوُۥنَ عَلَىٰٓ أَحَدٖ وَٱلرَّسُولُ يَدۡعُوكُمۡ فِيٓ أُخۡرَىٰكُمۡ فَأَثَٰبَكُمۡ غَمَّۢا بِغَمّٖ لِّكَيۡلَا تَحۡزَنُواْ عَلَىٰ مَا فَاتَكُمۡ وَلَا مَآ أَصَٰبَكُمۡۗ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\n(Эй мўминлар), ҳеч кимга боқмай (жанг майдонидан) чиқиб кетган пайтингизни эсланг! Ҳолбуки пайғамбар ортингизда сизларни чорламоқда эди. Бас (ғалаба, ўлжалар янглиғ) сизлардан кетган нарсаларга ва (мағлубият, қурбонлар каби) сизларга етган нарсаларга хафа-ғамгин бўлмасликларингиз учун Аллоҳ сизларга ғам устига ғам берди. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 3, 'verse': 154, 'text': 'ثُمَّ أَنزَلَ عَلَيۡكُم مِّنۢ بَعۡدِ ٱلۡغَمِّ أَمَنَةٗ نُّعَاسٗا يَغۡشَىٰ طَآئِفَةٗ مِّنكُمۡۖ وَطَآئِفَةٞ قَدۡ أَهَمَّتۡهُمۡ أَنفُسُهُمۡ يَظُنُّونَ بِٱللَّهِ غَيۡرَ ٱلۡحَقِّ ظَنَّ ٱلۡجَٰهِلِيَّةِۖ يَقُولُونَ هَل لَّنَا مِنَ ٱلۡأَمۡرِ مِن شَيۡءٖۗ قُلۡ إِنَّ ٱلۡأَمۡرَ كُلَّهُۥ لِلَّهِۗ يُخۡفُونَ فِيٓ أَنفُسِهِم مَّا لَا يُبۡدُونَ لَكَۖ يَقُولُونَ لَوۡ كَانَ لَنَا مِنَ ٱلۡأَمۡرِ شَيۡءٞ مَّا قُتِلۡنَا هَٰهُنَاۗ قُل لَّوۡ كُنتُمۡ فِي بُيُوتِكُمۡ لَبَرَزَ ٱلَّذِينَ كُتِبَ عَلَيۡهِمُ ٱلۡقَتۡلُ إِلَىٰ مَضَاجِعِهِمۡۖ وَلِيَبۡتَلِيَ ٱللَّهُ مَا فِي صُدُورِكُمۡ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمۡۚ وَٱللَّهُ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nСўнгра бу ғамнинг кетидан сизларга бир ором уйқусини туширдики, у сизлардан бўлган бир тоифа кишиларни (яъни, ҳақиқий мўминларни) қамраб олди. Бошқа бир тоифа (яъни, мунофиқлар) эса Аллоҳ ҳақида нотўғри, динсизларча бадгумон қилиб, ўз жонларининг ғамига тушиб қолдилар. Улар: «Энди биз учун ҳеч қандай ғалаба бўлмайди», дейишади. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Бутун иш Аллоҳнинг (қўлида)!» Улар сизга ошкор қилмайдиган нарсани (мунофиқликларини) яширадилар. Айтадиларки: «Агар ихтиёримиз ўзимизда бўлганида эди, бу Ерларда ўлиб кетмаган бўлар эдик». (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар уйларингизнинг ичига беркиниб олганингизда ҳам ўлдирилиши Аллоҳ томонидан ҳукм қилинган кишилар шубҳасиз ҳалокат жойига келган булар эдилар». (Аллоҳ бу мағлубиятни) кўнгилларингиздаги нарсани (иймон ёки нифоқни) имтиҳон қилиш ва дилларингиздаги нарса (иймон)ни поклаш учун қилди. Аллоҳ дилларни эгаллаган сирларни билгувчидир'}, {'chapter': 3, 'verse': 155, 'text': 'إِنَّ ٱلَّذِينَ تَوَلَّوۡاْ مِنكُمۡ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ إِنَّمَا ٱسۡتَزَلَّهُمُ ٱلشَّيۡطَٰنُ بِبَعۡضِ مَا كَسَبُواْۖ وَلَقَدۡ عَفَا ٱللَّهُ عَنۡهُمۡۗ إِنَّ ٱللَّهَ غَفُورٌ حَلِيمٞ\nИкки жамоат тўқнашган куни сизларнинг ичингиздан бўлган (уруш майдонидан) қочган кишиларни фақат шайтоннинг ўзи қилган айрим гуноҳлари сабабли йўлдан оздирди. Энди Аллоҳ уларнинг гуноҳларини афв этди. Албатта, Аллоҳ мағфиратли, ҳалим Зотдир'}, {'chapter': 3, 'verse': 156, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَٱلَّذِينَ كَفَرُواْ وَقَالُواْ لِإِخۡوَٰنِهِمۡ إِذَا ضَرَبُواْ فِي ٱلۡأَرۡضِ أَوۡ كَانُواْ غُزّٗى لَّوۡ كَانُواْ عِندَنَا مَا مَاتُواْ وَمَا قُتِلُواْ لِيَجۡعَلَ ٱللَّهُ ذَٰلِكَ حَسۡرَةٗ فِي قُلُوبِهِمۡۗ وَٱللَّهُ يُحۡيِۦ وَيُمِيتُۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nЭй мўминлар, куфр йўлини тутган ва бошқа Ерларга сафар қилиб ёки ғазотга чиқиб кетган дўстлари ҳақида: «Агар биз билан бирга қолганларида ўлмаган ва ўлдирилмаган бўлар эдилар», дейдиган кимсаларга ўхшамангиз! (Бу гапларини) Аллоҳ уларнинг дилларидаги ҳасрат қилиб қўяди. Ахир тирилтирадиган ҳам, ўлдирадиган ҳам Аллоҳ-ку?! Аллоҳ қилаётган амалларингизни кўргувчидир'}, {'chapter': 3, 'verse': 157, 'text': 'وَلَئِن قُتِلۡتُمۡ فِي سَبِيلِ ٱللَّهِ أَوۡ مُتُّمۡ لَمَغۡفِرَةٞ مِّنَ ٱللَّهِ وَرَحۡمَةٌ خَيۡرٞ مِّمَّا يَجۡمَعُونَ\nҚасамки, агар Аллоҳ йўлида ўлдирилсангизлар ёки ўлсангизлар, албатта Аллоҳ томонидан бўлғуси мағфират ва раҳмат улар (яъни, мунофиқ, кофирлар) тўплаган дунёдан яхшироқдир'}, {'chapter': 3, 'verse': 158, 'text': 'وَلَئِن مُّتُّمۡ أَوۡ قُتِلۡتُمۡ لَإِلَى ٱللَّهِ تُحۡشَرُونَ\nҚасамки, агар ўлсангиз ҳам, ўлдирилсангиз ҳам, албатта Аллоҳ ҳузурига тўпланажаксиз'}, {'chapter': 3, 'verse': 159, 'text': 'فَبِمَا رَحۡمَةٖ مِّنَ ٱللَّهِ لِنتَ لَهُمۡۖ وَلَوۡ كُنتَ فَظًّا غَلِيظَ ٱلۡقَلۡبِ لَٱنفَضُّواْ مِنۡ حَوۡلِكَۖ فَٱعۡفُ عَنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمۡ وَشَاوِرۡهُمۡ فِي ٱلۡأَمۡرِۖ فَإِذَا عَزَمۡتَ فَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَوَكِّلِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ томонидан бўлган бир марҳамат сабабли уларга (саҳобаларингизга) юмшоқ сўзли бўлдингиз. Агар қўпол, қаттиқ дил бўлганингизда эди, албатта атрофингиздан тарқалиб кетган бўлар эдилар. Бас, уларни афв этинг, улар учун Аллоҳдан мағфират сўранг ва ишларингизда уларга маслаҳат солинг! Энди (маслаҳат қилгач, бирон ишни) қасд қилсангиз, Аллоҳга суянинг-таваккал қилинг! Албатта, Аллоҳ Ўзига таваккал қилувчиларни севади'}, {'chapter': 3, 'verse': 160, 'text': 'إِن يَنصُرۡكُمُ ٱللَّهُ فَلَا غَالِبَ لَكُمۡۖ وَإِن يَخۡذُلۡكُمۡ فَمَن ذَا ٱلَّذِي يَنصُرُكُم مِّنۢ بَعۡدِهِۦۗ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nАгар сизларга Аллоҳ ёр бўлса, ҳеч ким сизлардан ғолиб бўлмас. Ва агар сизларни ёрдамсиз қўйса, Ундан ўзга ҳеч бир зот ёрдамчи бўлмас. Иймон келтирган зотлар Аллоҳнинг Ўзигагина таваккал қилсинлар'}, {'chapter': 3, 'verse': 161, 'text': 'وَمَا كَانَ لِنَبِيٍّ أَن يَغُلَّۚ وَمَن يَغۡلُلۡ يَأۡتِ بِمَا غَلَّ يَوۡمَ ٱلۡقِيَٰمَةِۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nҲеч бир пайғамбар учун (урушдан тушган ўлжаларга) хиёнат қилиш жоиз эмас. Ким хиёнат қилса, Қиёмат Кунида (Аллоҳнинг ҳузурига) қилган хиёнати билан бирга келур. Сўнгра ҳар бир жон мазлум бўлмаган ҳолида қилган амалига комил жазо олғай'}, {'chapter': 3, 'verse': 162, 'text': 'أَفَمَنِ ٱتَّبَعَ رِضۡوَٰنَ ٱللَّهِ كَمَنۢ بَآءَ بِسَخَطٖ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nАллоҳнинг ризолигини истаган киши, Аллоҳнинг ғазабига дучор бўлган, борар жойи жаҳаннам бўлган кимса каби бўладими?! Нақадар ёмон оқибат бу'}, {'chapter': 3, 'verse': 163, 'text': 'هُمۡ دَرَجَٰتٌ عِندَ ٱللَّهِۗ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ\nУлар Аллоҳнинг ҳузурида ҳар ҳил даража-манзилатларда бўлурлар. Аллоҳ улар қилаётган амалларни кўргувчидир'}, {'chapter': 3, 'verse': 164, 'text': 'لَقَدۡ مَنَّ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ إِذۡ بَعَثَ فِيهِمۡ رَسُولٗا مِّنۡ أَنفُسِهِمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٍ\nАлбатта, Аллоҳ мўминларга ўзларидан бўлган (яъни, одам жинсидан бўлган), уларга Аллоҳнинг оятларини тиловат қиладиган, (гуноҳларидан) поклайдиган ҳамда уларга Китоб ва ҳикматни ўргатадиган пайғамбарни юбориш билан яшилик қилди. Зеро, улар илгари очиқ хато-залолатда эдилар'}, {'chapter': 3, 'verse': 165, 'text': 'أَوَلَمَّآ أَصَٰبَتۡكُم مُّصِيبَةٞ قَدۡ أَصَبۡتُم مِّثۡلَيۡهَا قُلۡتُمۡ أَنَّىٰ هَٰذَاۖ قُلۡ هُوَ مِنۡ عِندِ أَنفُسِكُمۡۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\n(Эй мўминлар), агар сизларга (Уҳудда душман томонидан) бир мусибат етса — ҳолбуки, сизлар (Бадр жангида) унга икки баробар мусибат етказган эдингиз — «Бу бало қайдан келди?», дейсизларми?! (Эй Муҳаммад алайҳис-салоту вас-салом), уларга: «Бу мусибат — мағлубият ўз қилмишларингиздан», деб айтинг! Албатта, Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 3, 'verse': 166, 'text': 'وَمَآ أَصَٰبَكُمۡ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ فَبِإِذۡنِ ٱللَّهِ وَلِيَعۡلَمَ ٱلۡمُؤۡمِنِينَ\nИкки жамоат тўқнашган куни сизларга етган мусибат Аллоҳнинг изни-иродаси билан ва ҳақиқий мўминларни билиш учун эди'}, {'chapter': 3, 'verse': 167, 'text': 'وَلِيَعۡلَمَ ٱلَّذِينَ نَافَقُواْۚ وَقِيلَ لَهُمۡ تَعَالَوۡاْ قَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ أَوِ ٱدۡفَعُواْۖ قَالُواْ لَوۡ نَعۡلَمُ قِتَالٗا لَّٱتَّبَعۡنَٰكُمۡۗ هُمۡ لِلۡكُفۡرِ يَوۡمَئِذٍ أَقۡرَبُ مِنۡهُمۡ لِلۡإِيمَٰنِۚ يَقُولُونَ بِأَفۡوَٰهِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ وَٱللَّهُ أَعۡلَمُ بِمَا يَكۡتُمُونَ\nҲамда нифоқ йўлига кирган кимсаларни аниқлаш учун бўлди. Уларга: «Келинглар, Аллоҳ йўлида жанг қилинглар ёки (ҳеч бўлмаса бизнинг саноғимизни кўпайтириш билан душманни) даф қилинглар», дейилса, «Агар жанг бўлишини аниқ билганимизда-ку сизларнинг ортингиздан борган бўлар эдик», дейишади. Ўша куни улар иймондан кўра куфрга яқинроқ эдилар. Улар оғизда кўнгилларида бўлмаган нарсаларни гапирмоқдалар. Аллоҳ эса уларнинг яширган сирларини жуда яхши билгувчидир'}, {'chapter': 3, 'verse': 168, 'text': 'ٱلَّذِينَ قَالُواْ لِإِخۡوَٰنِهِمۡ وَقَعَدُواْ لَوۡ أَطَاعُونَا مَا قُتِلُواْۗ قُلۡ فَٱدۡرَءُواْ عَنۡ أَنفُسِكُمُ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ўша ўзлари ўтириб олиб дўстлари ҳақида: «Агар бизнинг гапимизга кириб (жанг қилишдан бош тортганларида) ўлдирилмаган бўлар эдилар», дейдиган кимсаларга: «Агар айтган гапларингиз рост бўлса, ўзларингдан ўлимни даф қилинглар-чи», деб айтинг'}, {'chapter': 3, 'verse': 169, 'text': 'وَلَا تَحۡسَبَنَّ ٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ أَمۡوَٰتَۢاۚ بَلۡ أَحۡيَآءٌ عِندَ رَبِّهِمۡ يُرۡزَقُونَ\nАллоҳ йўлидаги жангда ўлдирилган зотларни ҳаргиз ўликлар деб ўйламанг! Йўқ, улар тириклардир! У зотлар Аллоҳ Ўз фазлу карами билан берган неъматлардан хушнуд ҳолларида баҳраманд бўлмоқдалар'}, {'chapter': 3, 'verse': 170, 'text': 'فَرِحِينَ بِمَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ وَيَسۡتَبۡشِرُونَ بِٱلَّذِينَ لَمۡ يَلۡحَقُواْ بِهِم مِّنۡ خَلۡفِهِمۡ أَلَّا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nВа ҳали ортларидан етиб келмаган биродарларига ҳеч қандай хавфу хатар йўқлиги ва ғамгин бўлмасликлари ҳақида хушхабар бермоқдалар'}, {'chapter': 3, 'verse': 171, 'text': '۞يَسۡتَبۡشِرُونَ بِنِعۡمَةٖ مِّنَ ٱللَّهِ وَفَضۡلٖ وَأَنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُؤۡمِنِينَ\nУлар Аллоҳ томонидан бўлган неъмат ва фазлу карам ҳақида ҳамда мўминларнинг ажри-мукофотини Аллоҳ зое қилмаслиги ҳақида хушхабар берадилар'}, {'chapter': 3, 'verse': 172, 'text': 'ٱلَّذِينَ ٱسۡتَجَابُواْ لِلَّهِ وَٱلرَّسُولِ مِنۢ بَعۡدِ مَآ أَصَابَهُمُ ٱلۡقَرۡحُۚ لِلَّذِينَ أَحۡسَنُواْ مِنۡهُمۡ وَٱتَّقَوۡاْ أَجۡرٌ عَظِيمٌ\nУлар ўзларига жароҳат етганидан кейин ҳам Аллоҳ ва расулига бўйсунган зотлардир. Уларнинг орасидаги чиройли итоат қилган ва Аллоҳдан қўрққан зотлар учун улуғ ажр — мукофот бордир'}, {'chapter': 3, 'verse': 173, 'text': 'ٱلَّذِينَ قَالَ لَهُمُ ٱلنَّاسُ إِنَّ ٱلنَّاسَ قَدۡ جَمَعُواْ لَكُمۡ فَٱخۡشَوۡهُمۡ فَزَادَهُمۡ إِيمَٰنٗا وَقَالُواْ حَسۡبُنَا ٱللَّهُ وَنِعۡمَ ٱلۡوَكِيلُ\nУндай зотларга айрим кимсалар: «Қурайш одамлари сизларга қарши (саноқсиз лашкар) тўплаган; бас, улардан қўрқингиз!», деганларида бу гап уларнинг иймонларини зиёда қилди ва: «Бизга ёлғиз Аллоҳнинг Ўзи кифоя. У зот энг яхши ишончли вакилдир!», дедилар'}, {'chapter': 3, 'verse': 174, 'text': 'فَٱنقَلَبُواْ بِنِعۡمَةٖ مِّنَ ٱللَّهِ وَفَضۡلٖ لَّمۡ يَمۡسَسۡهُمۡ سُوٓءٞ وَٱتَّبَعُواْ رِضۡوَٰنَ ٱللَّهِۗ وَٱللَّهُ ذُو فَضۡلٍ عَظِيمٍ\nБас, у юришдан бирон нохушлик етмай, Аллоҳнинг неъмат ва фазли билан қайтдилар. Улар Аллоҳнинг ризолигини истадилар. Аллоҳ эса улуғ фазлу марҳамат Соҳибидир'}, {'chapter': 3, 'verse': 175, 'text': 'إِنَّمَا ذَٰلِكُمُ ٱلشَّيۡطَٰنُ يُخَوِّفُ أَوۡلِيَآءَهُۥ فَلَا تَخَافُوهُمۡ وَخَافُونِ إِن كُنتُم مُّؤۡمِنِينَ\nАлбатта, ўша (сизларни васвасага солмоқчи бўлган) шайтоннинг ўзидир. У сизларни ўзининг дўстларидан (кофирлардан) қўрқитмоқчи бўлади. Бас, агар мўмин бўлсангизлар, улардан қўрқмангиз, Мендан қўрқингиз'}, {'chapter': 3, 'verse': 176, 'text': 'وَلَا يَحۡزُنكَ ٱلَّذِينَ يُسَٰرِعُونَ فِي ٱلۡكُفۡرِۚ إِنَّهُمۡ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗاۚ يُرِيدُ ٱللَّهُ أَلَّا يَجۡعَلَ لَهُمۡ حَظّٗا فِي ٱلۡأٓخِرَةِۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), куфр-динсизликка шошаётган кимсалар Сизни ғамгин қилмасин! Албатта, улар Аллоҳга ҳеч қандай зарар етказа олмайдилар. (Фақат) Аллоҳ улар учун Охиратда бирон насиба бўлмаслигини истайди. Улар учун улуғ азоб бор (холос)'}, {'chapter': 3, 'verse': 177, 'text': 'إِنَّ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلۡكُفۡرَ بِٱلۡإِيمَٰنِ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nАлбатта, куфрни иймонга алмаштирадиган кимсалар Аллоҳга ҳеч қандай зарар етказа олмайдилар. Улар учун аламли азоб бордир'}, {'chapter': 3, 'verse': 178, 'text': 'وَلَا يَحۡسَبَنَّ ٱلَّذِينَ كَفَرُوٓاْ أَنَّمَا نُمۡلِي لَهُمۡ خَيۡرٞ لِّأَنفُسِهِمۡۚ إِنَّمَا نُمۡلِي لَهُمۡ لِيَزۡدَادُوٓاْ إِثۡمٗاۖ وَلَهُمۡ عَذَابٞ مُّهِينٞ\nКуфр йўлини тутган кимсалар уларга берган муҳлатимизни зинҳор ўзлари учун яхшилик деб ҳисобламасинлар! Балки Биз уларга фақат гуноҳларини зиёда қилишлари учунгина муҳлат берамиз. Улар учун хор қилгувчи азоб бордир'}, {'chapter': 3, 'verse': 179, 'text': 'مَّا كَانَ ٱللَّهُ لِيَذَرَ ٱلۡمُؤۡمِنِينَ عَلَىٰ مَآ أَنتُمۡ عَلَيۡهِ حَتَّىٰ يَمِيزَ ٱلۡخَبِيثَ مِنَ ٱلطَّيِّبِۗ وَمَا كَانَ ٱللَّهُ لِيُطۡلِعَكُمۡ عَلَى ٱلۡغَيۡبِ وَلَٰكِنَّ ٱللَّهَ يَجۡتَبِي مِن رُّسُلِهِۦ مَن يَشَآءُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرُسُلِهِۦۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ فَلَكُمۡ أَجۡرٌ عَظِيمٞ\nАллоҳ мўминларни сизлар бўлган мана шу ҳолатда (яъни, ким мўмин-у, ким мунофиқлиги маълум бўлмаган ҳолатда) ташлаб қўювчи эмас. Ҳали у (Уҳудда мунофиқларни мўминлардан ажратиб қўйгани каби) нопокни покдан ажратади. Аллоҳ сизларни ғайб илмидан хабардор қилмади. Лекин Аллоҳ пайғамбарларидан Ўзи истаган зотларни сайлаб олур (ва уни ғойибдан хабардор, огоҳ қилур). Бас, Аллоҳ ва Унинг пайғамбарларига иймон келтирингиз! Агар иймон келтириб, Аллоҳдан қўрқсангиз, сизлар учун улуғ ажр-мукофот бордир'}, {'chapter': 3, 'verse': 180, 'text': 'وَلَا يَحۡسَبَنَّ ٱلَّذِينَ يَبۡخَلُونَ بِمَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ هُوَ خَيۡرٗا لَّهُمۖ بَلۡ هُوَ شَرّٞ لَّهُمۡۖ سَيُطَوَّقُونَ مَا بَخِلُواْ بِهِۦ يَوۡمَ ٱلۡقِيَٰمَةِۗ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nАллоҳ фазлу карами билан ато қилган нарсаларнинг (закотини) беришга бахиллик қилган кимсалар ҳаргиз бу қилмишларини ўзлари учун яхшилик деб ҳисобламасинлар! Йўқ, бу қилмишлари ўзлари учун ёмонликдир. Бахиллик қилиб бермаган нарсалари Қиёмат Кунида бўйинларига ўралажак! Осмонлару Ернинг мероси Аллоҳникидир. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 3, 'verse': 181, 'text': 'لَّقَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ فَقِيرٞ وَنَحۡنُ أَغۡنِيَآءُۘ سَنَكۡتُبُ مَا قَالُواْ وَقَتۡلَهُمُ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَنَقُولُ ذُوقُواْ عَذَابَ ٱلۡحَرِيقِ\nАлбатта, Аллоҳ: «Аллоҳ камбағал, бизлар боймиз», деган кимсаларнинг сўзини эшитди. Уларнинг айтган гапларини ва пайғамбарларни ноҳақ ўлдирганларини ёзиб қўюрмиз ва (Қиёмат Куни) уларга: «Куйдургувчи — дўзах азобини тотиб кўрингиз!», деб айтурмиз'}, {'chapter': 3, 'verse': 182, 'text': 'ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّامٖ لِّلۡعَبِيدِ\nБу (азоб) ўзларингиз қилган гуноҳларингиз сабаблидир. Албатта, Аллоҳ бандаларига зулм қилгувчи эмасдир'}, {'chapter': 3, 'verse': 183, 'text': 'ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ عَهِدَ إِلَيۡنَآ أَلَّا نُؤۡمِنَ لِرَسُولٍ حَتَّىٰ يَأۡتِيَنَا بِقُرۡبَانٖ تَأۡكُلُهُ ٱلنَّارُۗ قُلۡ قَدۡ جَآءَكُمۡ رُسُلٞ مِّن قَبۡلِي بِٱلۡبَيِّنَٰتِ وَبِٱلَّذِي قُلۡتُمۡ فَلِمَ قَتَلۡتُمُوهُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nУ кимсалар: «Албатта, Аллоҳ бизга бирон пайғамбар то бир қурбонлик атаб, уни (осмондан тушган) олов еб (яъни, ёндириб) кетмагунча унга иймон келтирмаслигимизни буюрган», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Муҳаққақки, мендан илгари сизларга кўп пайғамбарлар очиқ ҳужжатлар ва сизлар айтган нарсани (яъни, қурбонликларни) келтирганлар. Бас, агар шу гапларингиз рост бўлса, нима учун уларни ўлдирган эдингизлар?!»'}, {'chapter': 3, 'verse': 184, 'text': 'فَإِن كَذَّبُوكَ فَقَدۡ كُذِّبَ رُسُلٞ مِّن قَبۡلِكَ جَآءُو بِٱلۡبَيِّنَٰتِ وَٱلزُّبُرِ وَٱلۡكِتَٰبِ ٱلۡمُنِيرِ\n(Эй Муҳамамад алайҳис-салоту вас-салом), агар Сизни ёлғончи қилишган бўлса, (асло ғамгин бўлманг, чунки) Сиздан илгари ҳам очиқ ҳужжатлар, битиклар ва нурли Китоб келтирган кўп пайғамбарлар ёлғончи қилинганлар'}, {'chapter': 3, 'verse': 185, 'text': 'كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۗ وَإِنَّمَا تُوَفَّوۡنَ أُجُورَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۖ فَمَن زُحۡزِحَ عَنِ ٱلنَّارِ وَأُدۡخِلَ ٱلۡجَنَّةَ فَقَدۡ فَازَۗ وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا مَتَٰعُ ٱلۡغُرُورِ\nҲар бир жон ўлимни тотгувчидир. Ва Қиёмат Кунида ҳеч шак-шубҳасиз, ажр-савобларингизни комил суратда олурсиз. Бас, ким дўзахдан четлатилиб, жаннатга киритилса, муҳаққақ (бахт-саодатга) эришгай. Бу ҳаёти дунё эса фақат алдагувчи матодир'}, {'chapter': 3, 'verse': 186, 'text': '۞لَتُبۡلَوُنَّ فِيٓ أَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ وَلَتَسۡمَعُنَّ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَمِنَ ٱلَّذِينَ أَشۡرَكُوٓاْ أَذٗى كَثِيرٗاۚ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ فَإِنَّ ذَٰلِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ\n(Эй мўминлар), албатта молу жонларингизда имтиҳон қилинурсизлар (яъни, Аллоҳ таоло мол ва жонларингизга хилма-хил офату балолар юбориб, сизларнинг иймонингизни текшириб кўрар) ва албатта сизлардан илгари Китоб берилган кимсалар томонидан ва мушриклар томонидан кўп азият-маломатлар эшитажаксизлар. Агар (озорларига) сабр-тоқат қилсангизлар ва Аллоҳдан қўрқсангизлар (нажот топурсиз). Чунки бу иш (яъни, сабр-тоқатли бўлиб, Аллоҳдан қўрқиш) ишларнинг мақсадга мувофиғидир'}, {'chapter': 3, 'verse': 187, 'text': 'وَإِذۡ أَخَذَ ٱللَّهُ مِيثَٰقَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ لَتُبَيِّنُنَّهُۥ لِلنَّاسِ وَلَا تَكۡتُمُونَهُۥ فَنَبَذُوهُ وَرَآءَ ظُهُورِهِمۡ وَٱشۡتَرَوۡاْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَبِئۡسَ مَا يَشۡتَرُونَ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ Китоб берилган кимсалардан «Албатта, у (Китоб)ни одамларга очиқ баён қилурсизлар ва яширмайсизлар!», деб аҳд-паймон олган эди. Сўнг улар бу аҳд-паймонни ортларига ташладилар ва уни озгина қийматга сотдилар. Уларнинг бу олди-сотдилари нақадар ёмон иш бўлди'}, {'chapter': 3, 'verse': 188, 'text': 'لَا تَحۡسَبَنَّ ٱلَّذِينَ يَفۡرَحُونَ بِمَآ أَتَواْ وَّيُحِبُّونَ أَن يُحۡمَدُواْ بِمَا لَمۡ يَفۡعَلُواْ فَلَا تَحۡسَبَنَّهُم بِمَفَازَةٖ مِّنَ ٱلۡعَذَابِۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз ҳаргиз қилган ишлари (яъни, одамларни ҳақ йўлдан адаштиришлари) билан хурсанд бўладиган ва қилмаган ишлари билан мақтовлар эшитишни яхши кўрадиган кимсалар азобдан йироқ макондалар, деб ўйламанг! Улар учун аламли азоб бордир'}, {'chapter': 3, 'verse': 189, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлар ва Ердаги бор мулк Аллоҳникидир. Ва Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 3, 'verse': 190, 'text': 'إِنَّ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ لَأٓيَٰتٖ لِّأُوْلِي ٱلۡأَلۡبَٰبِ\nАлбатта, осмонлар ва Ернинг яралишида ҳамда кеча ва кундузнинг алмашиниб туришида ақл эгалари учун (бир Яратувчи ва бошқариб тургувчи Зот мавжуд эканлигига) оят-аломатлар бордир'}, {'chapter': 3, 'verse': 191, 'text': 'ٱلَّذِينَ يَذۡكُرُونَ ٱللَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِهِمۡ وَيَتَفَكَّرُونَ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ رَبَّنَا مَا خَلَقۡتَ هَٰذَا بَٰطِلٗا سُبۡحَٰنَكَ فَقِنَا عَذَابَ ٱلنَّارِ\nУлар турганда ҳам, ўтирганда ҳам, ётганда ҳам Аллоҳни эслайдилар ҳамда осмонлар ва Ернинг яралиши ҳақида тафаккур қилиб (дейдилар): «Парвардигоро, бу (борлиқ)ни беҳуда яратганинг йўқ! Сен (беҳуда бирон иш қилиш айбидан) поксан! Ўзинг бизни жаҳаннам азобидан асрагил'}, {'chapter': 3, 'verse': 192, 'text': 'رَبَّنَآ إِنَّكَ مَن تُدۡخِلِ ٱلنَّارَ فَقَدۡ أَخۡزَيۡتَهُۥۖ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٖ\nПарвардигоро, албатта Сен кимни жаҳаннамга киритсанг, муҳаққақ, уни расво қилибсан. Ва бундай золимлар учун ҳеч қандай ёрдамчи йўқдир'}, {'chapter': 3, 'verse': 193, 'text': 'رَّبَّنَآ إِنَّنَا سَمِعۡنَا مُنَادِيٗا يُنَادِي لِلۡإِيمَٰنِ أَنۡ ءَامِنُواْ بِرَبِّكُمۡ فَـَٔامَنَّاۚ رَبَّنَا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَكَفِّرۡ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّنَا مَعَ ٱلۡأَبۡرَارِ\nПарвардигоро, албатта биз: «Парвардигорингизга иймон келтирингиз!», деб иймонга чорлаган жарчи (Муҳаммад алайҳис-салоту вас-саломнинг нидоларини) эшитдигу иймон келтирдик. Парвардигоро, бизнинг гуноҳларимизни мағфират қил, қилган ёмонликларимизни (саҳифайи аъмолимиздан) ўчиргил ва бизларни яхшилар билан бирга ўлдир'}, {'chapter': 3, 'verse': 194, 'text': 'رَبَّنَا وَءَاتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخۡزِنَا يَوۡمَ ٱلۡقِيَٰمَةِۖ إِنَّكَ لَا تُخۡلِفُ ٱلۡمِيعَادَ\nПарвардигоро, бизларга пайғамбарларинг орқали ваъда қилган нарсаларингни (жаннат, мағфират, висолингдан баҳраманд этиш каби неъматларингни) ато қил ва бизларни Қиёмат Кунида расво қилмагил! Албатта, Сен ваъдангга хилоф қилмагайсан'}, {'chapter': 3, 'verse': 195, 'text': 'فَٱسۡتَجَابَ لَهُمۡ رَبُّهُمۡ أَنِّي لَآ أُضِيعُ عَمَلَ عَٰمِلٖ مِّنكُم مِّن ذَكَرٍ أَوۡ أُنثَىٰۖ بَعۡضُكُم مِّنۢ بَعۡضٖۖ فَٱلَّذِينَ هَاجَرُواْ وَأُخۡرِجُواْ مِن دِيَٰرِهِمۡ وَأُوذُواْ فِي سَبِيلِي وَقَٰتَلُواْ وَقُتِلُواْ لَأُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأُدۡخِلَنَّهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ ثَوَابٗا مِّنۡ عِندِ ٱللَّهِۚ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلثَّوَابِ\nБас, Парвардигорлари уларнинг дуоларини ижобат қилиб (деди): «Албатта, Мен сизлардан бирон амал қилгувчи эркак ё аёлнинг амалини зое қилмагайман. Зеро, бировингиз бировингиздандирсиз. (Яъни, эркак аёлдан, аёл эркакдан дунёга келган). Бас, ватанларидан ҳижрат қилган, диёрларидан қувилган, менинг йўлимда азиятлар чеккан ва (менинг йўлимда) жанг қилиб, қатл этилган зотларнинг қилган гуноҳларини ўчиргайман ва уларни тагларидан дарёлар оқиб турадиган боғларга киритурман. Бу Аллоҳ ҳузуридан бўлган савоб-мукофот бўлади. Зеро, энг гўзал савоб-мукофот Аллоҳнинг ҳузуридадир»'}, {'chapter': 3, 'verse': 196, 'text': 'لَا يَغُرَّنَّكَ تَقَلُّبُ ٱلَّذِينَ كَفَرُواْ فِي ٱلۡبِلَٰدِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сизни кофирларнинг шаҳарларда кезишлари (яъни, уларнинг ҳар жойда керилиб юришлари) алдаб қўймасин'}, {'chapter': 3, 'verse': 197, 'text': 'مَتَٰعٞ قَلِيلٞ ثُمَّ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ\n(Бу елиб-югуриш, ғолибона кезишлар) озгина матодир. Сўнгра жойлари жаҳаннам бўлгай! Нақадар ёмон жой у'}, {'chapter': 3, 'verse': 198, 'text': 'لَٰكِنِ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا نُزُلٗا مِّنۡ عِندِ ٱللَّهِۗ وَمَا عِندَ ٱللَّهِ خَيۡرٞ لِّلۡأَبۡرَارِ\nАммо Парвардигоридан қўрққан зотлар учун Аллоҳ ҳузуридан зиёфат бўлмиш — тагларидан дарёлар оқиб тургувчи боғлар бордирки, улар бу боғларда абадий қолажаклар. Яхшилар учун Аллоҳ ҳузуридаги нарса (яъни, савоб-ажр кофирларнинг муваққат кайф-сафоларидан) афзалроқдир'}, {'chapter': 3, 'verse': 199, 'text': 'وَإِنَّ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ لَمَن يُؤۡمِنُ بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡكُمۡ وَمَآ أُنزِلَ إِلَيۡهِمۡ خَٰشِعِينَ لِلَّهِ لَا يَشۡتَرُونَ بِـَٔايَٰتِ ٱللَّهِ ثَمَنٗا قَلِيلًاۚ أُوْلَـٰٓئِكَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡۗ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nАлбатта, аҳли китоб ичида Аллоҳга ҳамда сизларга нозил қилинган нарсага — (Қуръонга) ҳам, ўзларига нозил қилинган нарсага — (Тавротга, Инжилга) ҳам иймон келтирадиган, Аллоҳга итоат қилиб бош эгувчи, Аллоҳнинг оятларини озгина қийматга сотиб юбормайдиган зотлар ҳам бор эканлиги аниқ-равшандир. Ана ўшалар учун Парвардигорлари ҳузурида ажру савоб бордир. Албатта, Аллоҳ тезда ҳисоб-китоб қилгувчи Зотдир'}, {'chapter': 3, 'verse': 200, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱصۡبِرُواْ وَصَابِرُواْ وَرَابِطُواْ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй мўминлар, сабр қилингиз ва сабру тоқат қилишда (кофирлардан) устун бўлингиз ҳамда доимо (кураш-жиҳод учун) белингиз боғлиқ (ҳолда ҳозир) бўлиб турингиз! Ва Аллоҳдан қўрқингизким, (шунда) шояд нажот топгайсизлар'}, {'chapter': 4, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمُ ٱلَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَخَلَقَ مِنۡهَا زَوۡجَهَا وَبَثَّ مِنۡهُمَا رِجَالٗا كَثِيرٗا وَنِسَآءٗۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِي تَسَآءَلُونَ بِهِۦ وَٱلۡأَرۡحَامَۚ إِنَّ ٱللَّهَ كَانَ عَلَيۡكُمۡ رَقِيبٗا\nЭй инсонлар! Сизларни бир жондан (Одамдан) яратган ва ундан жуфтини (Ҳаввони) вужудга келтирган ҳамда у икковидан кўп эркак ва аёлларни тарқатган Парвардигорингиздан қўрқингиз! Яна ораларингиздаги савол-жавобларда ўртага номи қўйиладиган Аллоҳдан ҳамда қариндош-уруғларингиз(дан алоқани узишдан) қўрқингиз! Албатта, Аллоҳ устингизда кузатувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 2, 'text': 'وَءَاتُواْ ٱلۡيَتَٰمَىٰٓ أَمۡوَٰلَهُمۡۖ وَلَا تَتَبَدَّلُواْ ٱلۡخَبِيثَ بِٱلطَّيِّبِۖ وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَهُمۡ إِلَىٰٓ أَمۡوَٰلِكُمۡۚ إِنَّهُۥ كَانَ حُوبٗا كَبِيرٗا\n(Вояга етганларидан кейин қўл остингиздаги) етимларга молларини берингиз ва (етимларнинг сизлар учун) нопок бўлган молларини (ўзингизнинг ҳаққингиз бўлган) пок нарсага алмаштириб олмангиз! Ва уларнинг молларини ўзингизнинг молингизга қўшиб емангиз! Зеро, бу катта гуноҳ бўлган ишдир'}, {'chapter': 4, 'verse': 3, 'text': 'وَإِنۡ خِفۡتُمۡ أَلَّا تُقۡسِطُواْ فِي ٱلۡيَتَٰمَىٰ فَٱنكِحُواْ مَا طَابَ لَكُم مِّنَ ٱلنِّسَآءِ مَثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۖ فَإِنۡ خِفۡتُمۡ أَلَّا تَعۡدِلُواْ فَوَٰحِدَةً أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۚ ذَٰلِكَ أَدۡنَىٰٓ أَلَّا تَعُولُواْ\nАгар етим қизларга адолат қила олмасликдан қўрқсангиз, сизлар учун никоҳи ҳалол бўлган аёлларга иккита, учта, тўрттадан уйланаверинглар. Энди агар (хотинлар орасида) адолат қила олмасликдан қўрқсангиз, битта аёлга (уйланинг) ёки қўл остингиздаги чўри билан (кифояланинг). Мана шу жабру зулмга ўтиб кетмаслигингизга яқинроқ ишдир'}, {'chapter': 4, 'verse': 4, 'text': 'وَءَاتُواْ ٱلنِّسَآءَ صَدُقَٰتِهِنَّ نِحۡلَةٗۚ فَإِن طِبۡنَ لَكُمۡ عَن شَيۡءٖ مِّنۡهُ نَفۡسٗا فَكُلُوهُ هَنِيٓـٔٗا مَّرِيٓـٔٗا\nХотинларингизга маҳрларини ҳадя каби (яъни, чин кўнгилдан, мамнунлик билан) берингиз! Агар ўзлари сизлар учун у маҳрдан бирон нарсани ихтиёрий равишда кечсалар, сизлар уни пок ва муборак билиб еяверинглар'}, {'chapter': 4, 'verse': 5, 'text': 'وَلَا تُؤۡتُواْ ٱلسُّفَهَآءَ أَمۡوَٰلَكُمُ ٱلَّتِي جَعَلَ ٱللَّهُ لَكُمۡ قِيَٰمٗا وَٱرۡزُقُوهُمۡ فِيهَا وَٱكۡسُوهُمۡ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا\n(Қўл остингиздаги) ақлсиз кимсаларга (яъни, ёш, нодон етимларга) Аллоҳ сизлар учун турмуш воситаси қилиб қўйган молларингизни (яъни, қўлларингиздаги уларнинг молларини) бериб қўймангиз, балки уларни ўша моллардан едириб-кийдиринг ва уларга яхши сўзлар билан муомала қилинг'}, {'chapter': 4, 'verse': 6, 'text': 'وَٱبۡتَلُواْ ٱلۡيَتَٰمَىٰ حَتَّىٰٓ إِذَا بَلَغُواْ ٱلنِّكَاحَ فَإِنۡ ءَانَسۡتُم مِّنۡهُمۡ رُشۡدٗا فَٱدۡفَعُوٓاْ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡۖ وَلَا تَأۡكُلُوهَآ إِسۡرَافٗا وَبِدَارًا أَن يَكۡبَرُواْۚ وَمَن كَانَ غَنِيّٗا فَلۡيَسۡتَعۡفِفۡۖ وَمَن كَانَ فَقِيرٗا فَلۡيَأۡكُلۡ بِٱلۡمَعۡرُوفِۚ فَإِذَا دَفَعۡتُمۡ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡ فَأَشۡهِدُواْ عَلَيۡهِمۡۚ وَكَفَىٰ بِٱللَّهِ حَسِيبٗا\nЕтимларни то балоғат ёшига етгунларича имтиҳон қилиб (текшириб) туринглар. Агар уларнинг эс-ҳушлари жойида эканини кўрсангиз, молларини ўзларига топширинглар. У молларни исроф қилиб ва (эгалари) катта бўлиб қолмасин, деб шошилиб еб қўйманглар. (Етимни оталиққа олган киши) агар бой бўлса, (етимнинг молидан) парҳез қилсин. Бордию камбағал бўлса, яхшилик билан (яъни, қилган хизматига яраша) олиб есин. Мол-мулкларини ўзларига қайтарганингизда эса гувоҳ келтиринглар. Аллоҳнинг Ўзи етарли ҳисоб-китоб қилгувчидир'}, {'chapter': 4, 'verse': 7, 'text': 'لِّلرِّجَالِ نَصِيبٞ مِّمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَ مِمَّا قَلَّ مِنۡهُ أَوۡ كَثُرَۚ نَصِيبٗا مَّفۡرُوضٗا\nЭркаклар учун ота-оналар ва қариндош-уруғлари қолдириб кетган меросдан улуш бордир. Аёллар учун ҳам ота-оналари ва қариндош-уруғлари қолдириб кетган меросдан улуш бордир. Бу улушлар озми-кўплигидан (қатъий назар) фарз қилинган ҳақлардандир'}, {'chapter': 4, 'verse': 8, 'text': 'وَإِذَا حَضَرَ ٱلۡقِسۡمَةَ أُوْلُواْ ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينُ فَٱرۡزُقُوهُم مِّنۡهُ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا\nМерос тақсимида узоқ қариндошлар, етим ва мискинлар ҳам ҳозир бўлсалар, уларни ҳам шу меросдан баҳраманд қилиб, яхши сўзлар айтинглар'}, {'chapter': 4, 'verse': 9, 'text': 'وَلۡيَخۡشَ ٱلَّذِينَ لَوۡ تَرَكُواْ مِنۡ خَلۡفِهِمۡ ذُرِّيَّةٗ ضِعَٰفًا خَافُواْ عَلَيۡهِمۡ فَلۡيَتَّقُواْ ٱللَّهَ وَلۡيَقُولُواْ قَوۡلٗا سَدِيدًا\n(Бировнинг ўлимига ҳозир бўлган) кишиларнинг ўзлари ожиз-нотавон фарзандларини қолдириб вафот қилиб кетсалар, улардан хавотир олганлари каби (ўзгаларнинг етимлари ҳаққидан ҳам) қўрқсинлар. Бас, Аллоҳдан қўрқиб, (ўлим олдидаги кишига) ҳақ сўзни айтсинлар'}, {'chapter': 4, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ يَأۡكُلُونَ أَمۡوَٰلَ ٱلۡيَتَٰمَىٰ ظُلۡمًا إِنَّمَا يَأۡكُلُونَ فِي بُطُونِهِمۡ نَارٗاۖ وَسَيَصۡلَوۡنَ سَعِيرٗا\nЕтимларнинг молларини зулм йўли билан ейдиган кимсалар ҳеч шак-шубҳасиз, қоринларига олов еган бўлурлар. Ва албатта дўзахга киражаклар'}, {'chapter': 4, 'verse': 11, 'text': 'يُوصِيكُمُ ٱللَّهُ فِيٓ أَوۡلَٰدِكُمۡۖ لِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَيَيۡنِۚ فَإِن كُنَّ نِسَآءٗ فَوۡقَ ٱثۡنَتَيۡنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۖ وَإِن كَانَتۡ وَٰحِدَةٗ فَلَهَا ٱلنِّصۡفُۚ وَلِأَبَوَيۡهِ لِكُلِّ وَٰحِدٖ مِّنۡهُمَا ٱلسُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُۥ وَلَدٞۚ فَإِن لَّمۡ يَكُن لَّهُۥ وَلَدٞ وَوَرِثَهُۥٓ أَبَوَاهُ فَلِأُمِّهِ ٱلثُّلُثُۚ فَإِن كَانَ لَهُۥٓ إِخۡوَةٞ فَلِأُمِّهِ ٱلسُّدُسُۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِي بِهَآ أَوۡ دَيۡنٍۗ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ لَا تَدۡرُونَ أَيُّهُمۡ أَقۡرَبُ لَكُمۡ نَفۡعٗاۚ فَرِيضَةٗ مِّنَ ٱللَّهِۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nАллоҳ фарзандларингизга (тегишли мерос) ҳақида бир ўғил учун икки қиз улуши баробарида мерос беришни амр қилур. Агар меросхўрлар иккидан ортиқ қиз бўлса, уларга улар (ота-она) қолдирган нарсанинг учдан иккиси, агар якка қиз бўлса, унга (мероснинг) ярми тегур. Агар марҳумнинг фарзанди бўлса, қолдирган меросидан ота-онасининг ҳар бирига олтидан бири тегур. Энди агар фарзанд бўлмай, фақат ота-онаси (меросхўр) бўлса, у ҳолда онасига учдан бир тегур (қолгани отасига қолади). Агар унинг ака-укалари бўлса, онасига олтидан бир тегур, (қолган қисми отасига тегади, зеро, марҳумнинг ака-укаларини едириб-кийдириш ва уйлаб-жойлаш отанинг зиммасидадир. Бу тақсимотлар) марҳум қилган васият ва унинг қарзлари адо этилганидан кейин бўлур. Ота-оналарингиз ва фарзандларингизнинг қайси бирлари сизлар учун фойдаси тегувчироқ эканини билмайсизлар. (Бинобарин, ўзингизга қолса, мерос тақсимотини ҳам адолат билан ҳал қила олмайсиз. Шу боисдан кимга қанча мерос тегиши) Аллоҳ томонидан қатъий фарз қилиб қўйилди. Албатта, Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 12, 'text': '۞وَلَكُمۡ نِصۡفُ مَا تَرَكَ أَزۡوَٰجُكُمۡ إِن لَّمۡ يَكُن لَّهُنَّ وَلَدٞۚ فَإِن كَانَ لَهُنَّ وَلَدٞ فَلَكُمُ ٱلرُّبُعُ مِمَّا تَرَكۡنَۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِينَ بِهَآ أَوۡ دَيۡنٖۚ وَلَهُنَّ ٱلرُّبُعُ مِمَّا تَرَكۡتُمۡ إِن لَّمۡ يَكُن لَّكُمۡ وَلَدٞۚ فَإِن كَانَ لَكُمۡ وَلَدٞ فَلَهُنَّ ٱلثُّمُنُ مِمَّا تَرَكۡتُمۚ مِّنۢ بَعۡدِ وَصِيَّةٖ تُوصُونَ بِهَآ أَوۡ دَيۡنٖۗ وَإِن كَانَ رَجُلٞ يُورَثُ كَلَٰلَةً أَوِ ٱمۡرَأَةٞ وَلَهُۥٓ أَخٌ أَوۡ أُخۡتٞ فَلِكُلِّ وَٰحِدٖ مِّنۡهُمَا ٱلسُّدُسُۚ فَإِن كَانُوٓاْ أَكۡثَرَ مِن ذَٰلِكَ فَهُمۡ شُرَكَآءُ فِي ٱلثُّلُثِۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصَىٰ بِهَآ أَوۡ دَيۡنٍ غَيۡرَ مُضَآرّٖۚ وَصِيَّةٗ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِيمٌ حَلِيمٞ\nСизларга хотинларингиз қолдирган меросдан — агар улардан фарзанд қолмаган бўлса — ярми тегур. Энди агар уларнинг фарзандлари қолган бўлса, сизларга уларнинг меросидан тўртдан бири тегур. (Бу тақсимотлар) марҳума қилган васият ва унинг қарзлари адо этилгандан кейин бўлур. Уларга (хотинларингизга) сизлар қолдирган меросдан — агар сизлардан фарзанд қолмаган бўлса — тўртдан бири тегур. Энди агар сизларнинг фарзандингиз қолган бўлса, уларга сизнинг меросингиздан саккиздан бири тегур. (Бу тақсимотлар) сизлар қилган васият ва қарзларингиз адо қилингандан кейин бўлур. Агар мероси қолаётган эркак ёки аёлнинг на ота ва на онаси бўлмай, (она томонидан) бир ака ёки укаси ёхуд бир опа ёки синглиси қолган бўлса, уларнинг икковидан ҳар бирига олтидан бир тегур. Энди агар улар бирдан ортиқ бўлсалар, улар мероснинг учдан бир ҳиссасига тенг шерик бўлурлар. (Бу тақсимотлар) меросхўрларга зарар етказмайдиган ҳолда қилинган васият ва қарзлар адо қилингандан кейин бўлур. (Бу ҳукмлар) Аллоҳ томонидан бўлган амр-фармондир. Аллоҳ билгувчи ва ҳалимдир'}, {'chapter': 4, 'verse': 13, 'text': 'تِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nМана шу Аллоҳнинг (белгилаб қўйган) ҳадларидир. Ким Аллоҳ ва Унинг пайғамбарига итоат этса, уни остидан дарёлар оқиб турадиган жаннатларга киритиб, ўша жойда абадий ҳаёт бахш этар. Ва бу буюк саодатдир'}, {'chapter': 4, 'verse': 14, 'text': 'وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ وَيَتَعَدَّ حُدُودَهُۥ يُدۡخِلۡهُ نَارًا خَٰلِدٗا فِيهَا وَلَهُۥ عَذَابٞ مُّهِينٞ\nКим Аллоҳ ва Унинг пайғамбарига итоатсизлик қилиб Аллоҳнинг белгилаб қўйган ҳадларидан тажовуз қилса, уни абадий қоладиган жойи бўлмиш дўзахга киритур. Ва унинг учун хор қилгувчи азоб бордир'}, {'chapter': 4, 'verse': 15, 'text': 'وَٱلَّـٰتِي يَأۡتِينَ ٱلۡفَٰحِشَةَ مِن نِّسَآئِكُمۡ فَٱسۡتَشۡهِدُواْ عَلَيۡهِنَّ أَرۡبَعَةٗ مِّنكُمۡۖ فَإِن شَهِدُواْ فَأَمۡسِكُوهُنَّ فِي ٱلۡبُيُوتِ حَتَّىٰ يَتَوَفَّىٰهُنَّ ٱلۡمَوۡتُ أَوۡ يَجۡعَلَ ٱللَّهُ لَهُنَّ سَبِيلٗا\nХотинларингиздан қайси бир аёл фоҳишалик қилса, ундай аёлларнинг устида ўзларингиздан бўлган тўрт кишини гувоҳ қилинг. Агар улар гувоҳлик берсалар, то бу аёлларга ўлим келгунча ёки Аллоҳ бирор йўлга солгунча, уларни уйларида сақлангиз'}, {'chapter': 4, 'verse': 16, 'text': 'وَٱلَّذَانِ يَأۡتِيَٰنِهَا مِنكُمۡ فَـَٔاذُوهُمَاۖ فَإِن تَابَا وَأَصۡلَحَا فَأَعۡرِضُواْ عَنۡهُمَآۗ إِنَّ ٱللَّهَ كَانَ تَوَّابٗا رَّحِيمًا\nСизлардан икки эркак шундай қилса (яъни, баччавозлик ёки фоҳишабозлик қилса), уларни (уриб-сўкиш билан) таъзирларини берингиз. Агар тавба қилиб, ўзларини тузатсалар, уларни тинч қўйинглар. Албатта, Аллоҳ тавбаларни қабул қилгувчи ва меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 17, 'text': 'إِنَّمَا ٱلتَّوۡبَةُ عَلَى ٱللَّهِ لِلَّذِينَ يَعۡمَلُونَ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ يَتُوبُونَ مِن قَرِيبٖ فَأُوْلَـٰٓئِكَ يَتُوبُ ٱللَّهُ عَلَيۡهِمۡۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nАлбатта, Аллоҳнинг тавбаларни қабул қилиши фақат биронта гуноҳни нодонлик билан қилиб қўйиб, сўнгра дарҳол тавба қилган кишилар учундир. Аллоҳ ана шундай кишиларнинг тавбасини қабул қилур. Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 18, 'text': 'وَلَيۡسَتِ ٱلتَّوۡبَةُ لِلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ حَتَّىٰٓ إِذَا حَضَرَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ إِنِّي تُبۡتُ ٱلۡـَٰٔنَ وَلَا ٱلَّذِينَ يَمُوتُونَ وَهُمۡ كُفَّارٌۚ أُوْلَـٰٓئِكَ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا\nНа гуноҳ ишларни мудом қилиб юриб, қачонки, бировларига ўлим келганида «энди тавба қилдим», дейдиган ва на кофир ҳолда ўлиб кетадиган кимсаларнинг тавбалари қабул қилинмас. Улар учун аламли азобни тайёрлаб қўйганмиз'}, {'chapter': 4, 'verse': 19, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَحِلُّ لَكُمۡ أَن تَرِثُواْ ٱلنِّسَآءَ كَرۡهٗاۖ وَلَا تَعۡضُلُوهُنَّ لِتَذۡهَبُواْ بِبَعۡضِ مَآ ءَاتَيۡتُمُوهُنَّ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَعَاشِرُوهُنَّ بِٱلۡمَعۡرُوفِۚ فَإِن كَرِهۡتُمُوهُنَّ فَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَيَجۡعَلَ ٱللَّهُ فِيهِ خَيۡرٗا كَثِيرٗا\nЭй мўминлар, сизлар учун хотинларни мажбурий ҳолда мерос қилиб олиш дуруст эмасдир. Ва (агар бирга яшаш ниятингиз бўлмаса), берган маҳрларингизнинг бир қисмини қайтариб олиш учун хотинларингизни (бошқа эрга тегишдан) тўсиб туриб олманглар! Магар улар очиқ бузуқлик қилсалар (у ҳолда берган маҳрингизни қайтариб олишингиз мумкин). Улар билан тинч-тотув яшанглар. Агар уларни ёмон кўрсангизлар ҳам (сабр қилиб иноқ ҳолда яшайверинглар). Зеро, сизлар ёмон кўрган нарсада Аллоҳ кўп яхшиликларни қилиб қўйган бўлиши мумкин'}, {'chapter': 4, 'verse': 20, 'text': 'وَإِنۡ أَرَدتُّمُ ٱسۡتِبۡدَالَ زَوۡجٖ مَّكَانَ زَوۡجٖ وَءَاتَيۡتُمۡ إِحۡدَىٰهُنَّ قِنطَارٗا فَلَا تَأۡخُذُواْ مِنۡهُ شَيۡـًٔاۚ أَتَأۡخُذُونَهُۥ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا\nАгар бир хотинни қўйиб, бошқа хотинга уйланмоқчи бўлсангизлар, аввалгисига саноқсиз молу дунёни (маҳр қилиб) берган бўлсангиз-да, ундан бирон нарсани қайтариб олмангизлар! Уни бўҳтон ва очиқ гуноҳ билан оласизми'}, {'chapter': 4, 'verse': 21, 'text': 'وَكَيۡفَ تَأۡخُذُونَهُۥ وَقَدۡ أَفۡضَىٰ بَعۡضُكُمۡ إِلَىٰ بَعۡضٖ وَأَخَذۡنَ مِنكُم مِّيثَٰقًا غَلِيظٗا\nАхир бир-бирингиз билан қўшилиб, улар (яъни, хотинларингиз) сизлардан қатъий аҳд-паймон олганларидан сўнг қандай қилиб уни (яъни, маҳрни) қайтариб оласиз'}, {'chapter': 4, 'verse': 22, 'text': 'وَلَا تَنكِحُواْ مَا نَكَحَ ءَابَآؤُكُم مِّنَ ٱلنِّسَآءِ إِلَّا مَا قَدۡ سَلَفَۚ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَمَقۡتٗا وَسَآءَ سَبِيلًا\nОталарингиз уйланган хотинларни никоҳингизга олманг! Магар илгари ўтган бўлса, (уйланган бўлсангиз, Аллоҳ афв этар). Албатта, бу хунук ва жирканч бўлган ёмон қилиқдир'}, {'chapter': 4, 'verse': 23, 'text': 'حُرِّمَتۡ عَلَيۡكُمۡ أُمَّهَٰتُكُمۡ وَبَنَاتُكُمۡ وَأَخَوَٰتُكُمۡ وَعَمَّـٰتُكُمۡ وَخَٰلَٰتُكُمۡ وَبَنَاتُ ٱلۡأَخِ وَبَنَاتُ ٱلۡأُخۡتِ وَأُمَّهَٰتُكُمُ ٱلَّـٰتِيٓ أَرۡضَعۡنَكُمۡ وَأَخَوَٰتُكُم مِّنَ ٱلرَّضَٰعَةِ وَأُمَّهَٰتُ نِسَآئِكُمۡ وَرَبَـٰٓئِبُكُمُ ٱلَّـٰتِي فِي حُجُورِكُم مِّن نِّسَآئِكُمُ ٱلَّـٰتِي دَخَلۡتُم بِهِنَّ فَإِن لَّمۡ تَكُونُواْ دَخَلۡتُم بِهِنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ وَحَلَـٰٓئِلُ أَبۡنَآئِكُمُ ٱلَّذِينَ مِنۡ أَصۡلَٰبِكُمۡ وَأَن تَجۡمَعُواْ بَيۡنَ ٱلۡأُخۡتَيۡنِ إِلَّا مَا قَدۡ سَلَفَۗ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nСизлар учун оналарингиз, қизларингиз, опа-сингилларингиз, аммаларингиз, холаларингиз, ака-укаларингизнинг қизлари, опа-сингилларингизнинг қизлари, эмизган оналарингиз, эмишган опа-сингилларингиз, қайноналарингиз, жинсий алоқада бўлган хотинларингизнинг тарбиянгизда бўлган қизлари (мана шу санаб ўтилган аёлларга уйланиш ҳаром қилинди) — агар хотинларингиз билан жинсий алоқада бўлмаган бўлсангиз (уларни талоқ қилгандан кейин аввалги эрларидан туғилган қизларига уйлансангиз), сизлар учун гуноҳ йўқдир. Яна ўз пушти камарингиздан бўлган ўғилларингизнинг хотинларига (уйланишингиз) ҳамда опа-сингилни жамлашларингиз (яъни, бирини талоқ қилмай туриб бошқасига уйланишларингиз ҳаром қилинди). Магар илгари ўтган бўлса (Аллоҳ афв этар). Албатта, Аллоҳ мағфиратли ва меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 24, 'text': '۞وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلنِّسَآءِ إِلَّا مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۖ كِتَٰبَ ٱللَّهِ عَلَيۡكُمۡۚ وَأُحِلَّ لَكُم مَّا وَرَآءَ ذَٰلِكُمۡ أَن تَبۡتَغُواْ بِأَمۡوَٰلِكُم مُّحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَۚ فَمَا ٱسۡتَمۡتَعۡتُم بِهِۦ مِنۡهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِيضَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا تَرَٰضَيۡتُم بِهِۦ مِنۢ بَعۡدِ ٱلۡفَرِيضَةِۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nЯна эрлик аёллар (ҳам ҳаром қилинди). Магар (уруш-жангларда) эга бўлган чўриларингиз ҳалолдир. (Бу ҳукмларни) Аллоҳ зиммангизга фарз қилиб қўйди. Мана шулардан бошқа аёлларга зино қилувчи бўлмаган ҳолингизда молларингизни маҳр қилиб бериш билан уйланишга ҳаракат қилиш сизлар учун ҳалол қилинди. Улар билан никоҳ орқали яқинлик қилишингиз билан белгиланган маҳрларини берингиз! Маҳр белгиланганидан кейин ўзларингиз келишиб олишингизда (яъни, улар ўз ихтиёрлари билан ҳаққи-маҳрларидан кечиб юборишларида) сизлар учун гуноҳ йўқдир. Албатта, Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 25, 'text': 'وَمَن لَّمۡ يَسۡتَطِعۡ مِنكُمۡ طَوۡلًا أَن يَنكِحَ ٱلۡمُحۡصَنَٰتِ ٱلۡمُؤۡمِنَٰتِ فَمِن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن فَتَيَٰتِكُمُ ٱلۡمُؤۡمِنَٰتِۚ وَٱللَّهُ أَعۡلَمُ بِإِيمَٰنِكُمۚ بَعۡضُكُم مِّنۢ بَعۡضٖۚ فَٱنكِحُوهُنَّ بِإِذۡنِ أَهۡلِهِنَّ وَءَاتُوهُنَّ أُجُورَهُنَّ بِٱلۡمَعۡرُوفِ مُحۡصَنَٰتٍ غَيۡرَ مُسَٰفِحَٰتٖ وَلَا مُتَّخِذَٰتِ أَخۡدَانٖۚ فَإِذَآ أُحۡصِنَّ فَإِنۡ أَتَيۡنَ بِفَٰحِشَةٖ فَعَلَيۡهِنَّ نِصۡفُ مَا عَلَى ٱلۡمُحۡصَنَٰتِ مِنَ ٱلۡعَذَابِۚ ذَٰلِكَ لِمَنۡ خَشِيَ ٱلۡعَنَتَ مِنكُمۡۚ وَأَن تَصۡبِرُواْ خَيۡرٞ لَّكُمۡۗ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nСизлардан кимда-ким озод мўмина аёлларга уйланиш имкониятига эга бўлмаса, қўлларингиздаги мўмина чўрилардан бирига уйланаверсин. Аллоҳ иймонларингизни жуда яхши билгувчидир. Бировингиз бировингиздандирсиз (яъни, ҳаммангизнинг аслингиз биттадир). Бас, улардан ўзларини сақлаган, фоҳишалик қилмаган ва махфий ўйнашлар тутмаганларига хожаларининг изни билан уйланаверинглар ва маҳрларини тўкис қилиб беринглар! Энди эрга теккач, агар бузуқлик қилсалар, уларга озод аёлларга бериладиган азобнинг ярми берилур. Бу (яъни, чўриларга уйланиш рухсати) орангиздаги бузилиб кетишдан қўрққан кишилар учундир. Сабр қилиб (уйланмай туришларингиз) ўзингиз учун яхшироқдир. Аллоҳ мағфиратли ва меҳрибондир'}, {'chapter': 4, 'verse': 26, 'text': 'يُرِيدُ ٱللَّهُ لِيُبَيِّنَ لَكُمۡ وَيَهۡدِيَكُمۡ سُنَنَ ٱلَّذِينَ مِن قَبۡلِكُمۡ وَيَتُوبَ عَلَيۡكُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nАллоҳ сизларга шариат ҳукмларини баён қилишни, сизларни илгари ўтган (ҳақ йўлдаги) зотларнинг йўлларига ҳидоят қилишни ва тавбаларингизни қабул қилишни истайди. Аллоҳ билим ва ҳикмат Эгасидир'}, {'chapter': 4, 'verse': 27, 'text': 'وَٱللَّهُ يُرِيدُ أَن يَتُوبَ عَلَيۡكُمۡ وَيُرِيدُ ٱلَّذِينَ يَتَّبِعُونَ ٱلشَّهَوَٰتِ أَن تَمِيلُواْ مَيۡلًا عَظِيمٗا\nАллоҳ тавбаларингизни қабул қилишни истайди, ҳою ҳавасга берилган кимсалар эса (ҳақ йўлдан) бутунлай оғиб кетишингизни истайдилар'}, {'chapter': 4, 'verse': 28, 'text': 'يُرِيدُ ٱللَّهُ أَن يُخَفِّفَ عَنكُمۡۚ وَخُلِقَ ٱلۡإِنسَٰنُ ضَعِيفٗا\nАллоҳ (буюрган шариат қонунлари билан) сизлардан (юкингизни) енгиллатишни истайди. (Зотан), инсон ожиз қилиб яратилгандир'}, {'chapter': 4, 'verse': 29, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِٱلۡبَٰطِلِ إِلَّآ أَن تَكُونَ تِجَٰرَةً عَن تَرَاضٖ مِّنكُمۡۚ وَلَا تَقۡتُلُوٓاْ أَنفُسَكُمۡۚ إِنَّ ٱللَّهَ كَانَ بِكُمۡ رَحِيمٗا\nЭй мўминлар, молларингизни ўрталарингизда ноҳақ (яъни, ўғрилик, қароқчилик, судхўрлик, порахўрлик, қимор каби) йўллар билан емангиз! Балки ўзаро ризолик билан бўлган савдо-сотиқ орқали мол-дунё касб қилингиз. Ҳамда бир-бирларингизни ўлдирмангиз! Албатта, Аллоҳ сизларга меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 30, 'text': 'وَمَن يَفۡعَلۡ ذَٰلِكَ عُدۡوَٰنٗا وَظُلۡمٗا فَسَوۡفَ نُصۡلِيهِ نَارٗاۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًا\nКим ҳаддан ошиб, зўравонлик билан шундай ишларни қилса, биз уни дўзахга киритажакмиз. Бу Аллоҳ учун осон бўлган ишдир'}, {'chapter': 4, 'verse': 31, 'text': 'إِن تَجۡتَنِبُواْ كَبَآئِرَ مَا تُنۡهَوۡنَ عَنۡهُ نُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَنُدۡخِلۡكُم مُّدۡخَلٗا كَرِيمٗا\nАгар сизлар ман этилган гуноҳларнинг катталаридан сақлансангизлар, қилган кичик гуноҳларингизни ўчирурмиз ва сизларни улуғ манзил — жаннатга киритурмиз'}, {'chapter': 4, 'verse': 32, 'text': 'وَلَا تَتَمَنَّوۡاْ مَا فَضَّلَ ٱللَّهُ بِهِۦ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖۚ لِّلرِّجَالِ نَصِيبٞ مِّمَّا ٱكۡتَسَبُواْۖ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا ٱكۡتَسَبۡنَۚ وَسۡـَٔلُواْ ٱللَّهَ مِن فَضۡلِهِۦٓۚ إِنَّ ٱللَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا\nАллоҳ бирон неъмат билан бирингизни бирингиздан ортиқ қилиб қўйган бўлса, сизлар уни (ҳасад-адоват билан) орзу қилманг! Эркаклар ҳам ўз меҳнатларидан насиба олурлар, аёллар ҳам ўз меҳнатларидан насиба олурлар. Сизлар (бировларга берилган неъматларга кўз олайтириш ўрнига) Аллоҳнинг фазлу марҳаматини сўранглар. Албатта, Аллоҳ ҳамма нарсани билгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 33, 'text': 'وَلِكُلّٖ جَعَلۡنَا مَوَٰلِيَ مِمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَۚ وَٱلَّذِينَ عَقَدَتۡ أَيۡمَٰنُكُمۡ فَـَٔاتُوهُمۡ نَصِيبَهُمۡۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا\nҲар бир инсон учун ота-она ва қариндошлар қолдириб кетган меросдан (баҳраманд бўлгувчи) меросхўрлар қилиб қўйдик. Кимлар билан аҳд-паймон қилган бўлсангиз, бас, уларга ўз ҳиссаларини берингиз! Албатта, Аллоҳ ҳамма нарсага гувоҳ бўлган Зотдир'}, {'chapter': 4, 'verse': 34, 'text': 'ٱلرِّجَالُ قَوَّـٰمُونَ عَلَى ٱلنِّسَآءِ بِمَا فَضَّلَ ٱللَّهُ بَعۡضَهُمۡ عَلَىٰ بَعۡضٖ وَبِمَآ أَنفَقُواْ مِنۡ أَمۡوَٰلِهِمۡۚ فَٱلصَّـٰلِحَٰتُ قَٰنِتَٰتٌ حَٰفِظَٰتٞ لِّلۡغَيۡبِ بِمَا حَفِظَ ٱللَّهُۚ وَٱلَّـٰتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَٱهۡجُرُوهُنَّ فِي ٱلۡمَضَاجِعِ وَٱضۡرِبُوهُنَّۖ فَإِنۡ أَطَعۡنَكُمۡ فَلَا تَبۡغُواْ عَلَيۡهِنَّ سَبِيلًاۗ إِنَّ ٱللَّهَ كَانَ عَلِيّٗا كَبِيرٗا\nЭркаклар хотинлари устида раҳбардирлар. Бунга сабаб Аллоҳ уларнинг бировларини бировларидан (яъни, эркакларни аёллардан) ортиқ қилгани ва эркаклар (хотинлари ва оилалари учун) ўз мол-мулкларидан сарф-харажат қилганларидир. Бас, ибодат-итоатли ва эрлари йўқлигида Аллоҳнинг ҳифзу ҳимояти билан (эрларининг мол-мулкларини ва ўз иффатларини) сақловчи хотинлар — яхши хотинлардир. Хотинларингизнинг итоатсизлигидан қўрқсангиз, аввало уларга панд-насиҳат қилинглар, сўнг (яъни, насиҳатларинг кор қилмаса) уларни ётоқларда тарк қилингиз (улар билан бир жойда ётманг, яқинлашманг), сўнгра (яъни, шунда ҳам сизларга бўйсунмасалар), уринглар! Аммо сизларга итоат қилсалар, уларга қарши бошқа йўл ахтарманглар. Албатта, Аллоҳ энг юксак ва буюк бўлган Зотдир'}, {'chapter': 4, 'verse': 35, 'text': 'وَإِنۡ خِفۡتُمۡ شِقَاقَ بَيۡنِهِمَا فَٱبۡعَثُواْ حَكَمٗا مِّنۡ أَهۡلِهِۦ وَحَكَمٗا مِّنۡ أَهۡلِهَآ إِن يُرِيدَآ إِصۡلَٰحٗا يُوَفِّقِ ٱللَّهُ بَيۡنَهُمَآۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا خَبِيرٗا\nАгар эр-хотиннинг оралари бузилиб кетишидан қўрқсангизлар, эр томонидан бир ҳакам, хотин томонидан бир ҳакам чақирингиз. Агар улар ислоҳ қилишни истасалар, Аллоҳ эр-хотин орасига иттифоқликни солур. Албатта, Аллоҳ билгувчи ва хабардор бўлган Зотдир'}, {'chapter': 4, 'verse': 36, 'text': '۞وَٱعۡبُدُواْ ٱللَّهَ وَلَا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنٗا وَبِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱلۡجَارِ ذِي ٱلۡقُرۡبَىٰ وَٱلۡجَارِ ٱلۡجُنُبِ وَٱلصَّاحِبِ بِٱلۡجَنۢبِ وَٱبۡنِ ٱلسَّبِيلِ وَمَا مَلَكَتۡ أَيۡمَٰنُكُمۡۗ إِنَّ ٱللَّهَ لَا يُحِبُّ مَن كَانَ مُخۡتَالٗا فَخُورًا\nАллоҳга бандалик қилинглар ва Унга ҳеч нарсани шерик қилманглар! Ота-онангизга ҳамда қариндош-уруғ, етим ва мискинларга, қариндош қўшни ва бегона қўшнига, ёнингиздаги ҳамроҳингизга, йўловчи мусофирга ва қўлларингиздаги қулларингизга яхшилик қилингиз! Албатта, Аллоҳ мутакаббир ва мақтанчоқ бўлган кимсаларни севмайди'}, {'chapter': 4, 'verse': 37, 'text': 'ٱلَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِ وَيَكۡتُمُونَ مَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا\nУлар ўзлари бахиллик қиладиган ва ўзга одамларни ҳам бахилликка чақирадиган ҳамда Аллоҳ фазлу карамидан берган неъматларни яширадиган кимсалардир. Биз бундай кофирлар учун хор қилгувчи азобни тайёрлаб қўйганмиз'}, {'chapter': 4, 'verse': 38, 'text': 'وَٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ رِئَآءَ ٱلنَّاسِ وَلَا يُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡيَوۡمِ ٱلۡأٓخِرِۗ وَمَن يَكُنِ ٱلشَّيۡطَٰنُ لَهُۥ قَرِينٗا فَسَآءَ قَرِينٗا\nЯна молларини одамлар кўрсин, деб эҳсон қиладиган, ўзлари эса на Аллоҳга ва на Охират Кунига иймон келтирмайдиган кимсаларни ҳам (Аллоҳ севмайди). Кимга шайтон дўст бўлса, у энг ёмон дўст булур'}, {'chapter': 4, 'verse': 39, 'text': 'وَمَاذَا عَلَيۡهِمۡ لَوۡ ءَامَنُواْ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَأَنفَقُواْ مِمَّا رَزَقَهُمُ ٱللَّهُۚ وَكَانَ ٱللَّهُ بِهِمۡ عَلِيمًا\nАгар Аллоҳ ва Охират Кунига иймон келтирсалар ва Аллоҳ ризқ қилиб берган нарсаларидан эҳсон қилсалар, уларга қандай зарар бўлар эди? Аллоҳ уларнинг (қилган амалларини) билгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 40, 'text': 'إِنَّ ٱللَّهَ لَا يَظۡلِمُ مِثۡقَالَ ذَرَّةٖۖ وَإِن تَكُ حَسَنَةٗ يُضَٰعِفۡهَا وَيُؤۡتِ مِن لَّدُنۡهُ أَجۡرًا عَظِيمٗا\nШубҳасиз, Аллоҳ бировга бир зарра вазнича зулм қилмас. Агар заррача яхшилик бўлса, уни бир неча баробар қилур ва ўз ҳузуридан улуғ ажр ато қилур'}, {'chapter': 4, 'verse': 41, 'text': 'فَكَيۡفَ إِذَا جِئۡنَا مِن كُلِّ أُمَّةِۭ بِشَهِيدٖ وَجِئۡنَا بِكَ عَلَىٰ هَـٰٓؤُلَآءِ شَهِيدٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), биз ҳар бир умматдан (ўша умматнинг пайғамбарини) гувоҳ келтирганимизда ва Сизни ана уларга қарши гувоҳ қилганимизда уларнинг ҳоли не кечур'}, {'chapter': 4, 'verse': 42, 'text': 'يَوۡمَئِذٖ يَوَدُّ ٱلَّذِينَ كَفَرُواْ وَعَصَوُاْ ٱلرَّسُولَ لَوۡ تُسَوَّىٰ بِهِمُ ٱلۡأَرۡضُ وَلَا يَكۡتُمُونَ ٱللَّهَ حَدِيثٗا\nУ Кунда кофир бўлган ва пайғамбарга итоатсизлик қилган кимсалар Ер билан яксон бўлиб кетишни орзу қиладилар ва (лекин) Аллоҳдан бирон гапни яшира олмайдилар'}, {'chapter': 4, 'verse': 43, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقۡرَبُواْ ٱلصَّلَوٰةَ وَأَنتُمۡ سُكَٰرَىٰ حَتَّىٰ تَعۡلَمُواْ مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِي سَبِيلٍ حَتَّىٰ تَغۡتَسِلُواْۚ وَإِن كُنتُم مَّرۡضَىٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآءَ أَحَدٞ مِّنكُم مِّنَ ٱلۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ ٱلنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَٱمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُمۡۗ إِنَّ ٱللَّهَ كَانَ عَفُوًّا غَفُورًا\nЭй мўминлар, токи гапираётган гапларингизни билмагунингизча маст ҳолингизда намозга яқин келманглар. Ва яна сафарда бўлмасангиз то чўмилмагунингизча жунуб ҳолингизда (намозга яқин келманг). Агар бетоб ё сафарда бўлсангизлар, ёки сизлардан бирон киши ҳожатхонадан чиққан бўлса, ёхуд хотинларингиз билан яқинлашган бўлсангиз ва (мазкур ҳолатларда) сув топа олмасангиз покиза тупроқ билан таяммум қилиб, юз ва қўлларингизга суртинглар. Албатта, Аллоҳ афв этувчи ва мағфиратли бўлган Зотдир'}, {'chapter': 4, 'verse': 44, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يَشۡتَرُونَ ٱلضَّلَٰلَةَ وَيُرِيدُونَ أَن تَضِلُّواْ ٱلسَّبِيلَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), китобдан (яъни, Тавротдан) насибадор бўлган кимсаларнинг гумроҳликни сотиб олаётганларини ва сизлар ҳам ҳақ йўлдан тойишингизни истаётганларини кўрмадингизми'}, {'chapter': 4, 'verse': 45, 'text': 'وَٱللَّهُ أَعۡلَمُ بِأَعۡدَآئِكُمۡۚ وَكَفَىٰ بِٱللَّهِ وَلِيّٗا وَكَفَىٰ بِٱللَّهِ نَصِيرٗا\nҲолбуки, Аллоҳ душманларингизни жуда яхши билгувчидир. Аллоҳнинг ўзи етарли дўст, Аллоҳнинг ўзи етарли ёрдамчидир'}, {'chapter': 4, 'verse': 46, 'text': 'مِّنَ ٱلَّذِينَ هَادُواْ يُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَيَقُولُونَ سَمِعۡنَا وَعَصَيۡنَا وَٱسۡمَعۡ غَيۡرَ مُسۡمَعٖ وَرَٰعِنَا لَيَّۢا بِأَلۡسِنَتِهِمۡ وَطَعۡنٗا فِي ٱلدِّينِۚ وَلَوۡ أَنَّهُمۡ قَالُواْ سَمِعۡنَا وَأَطَعۡنَا وَٱسۡمَعۡ وَٱنظُرۡنَا لَكَانَ خَيۡرٗا لَّهُمۡ وَأَقۡوَمَ وَلَٰكِن لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا\nЯҳудий бўлганлар орасида шундайлари борки, улар (Таврот китобида бўлган Муҳаммад алайҳис-салоту вас-салом ҳақидаги) Аллоҳнинг сўзини ўз ўрнидан ўзгартирадилар ва тилларини буриб, динга жароҳат етказиш ғаразида: «Тингладик ва бош тортдик. Қулоқ сол, эшитмай қолгур! (Роино) Бизга боқ», дейдилар. Агар улар «Тингладик ва буйинсундик. Бизни ҳам тинглаб, (унзурно) бизга ҳам назар қилиб тур», деганларида ўзлари учун яхшироқ ва туғрироқ бўлур эди. Лекин улар куфрлари сабабли Аллоҳнинг лаънатига дучор бўлганлар. Бас, уларнинг камдан-камлари иймон келтирадилар'}, {'chapter': 4, 'verse': 47, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ ءَامِنُواْ بِمَا نَزَّلۡنَا مُصَدِّقٗا لِّمَا مَعَكُم مِّن قَبۡلِ أَن نَّطۡمِسَ وُجُوهٗا فَنَرُدَّهَا عَلَىٰٓ أَدۡبَارِهَآ أَوۡ نَلۡعَنَهُمۡ كَمَا لَعَنَّآ أَصۡحَٰبَ ٱلسَّبۡتِۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولًا\nЭй Китоб берилган кимсалар, юзларни бузиб тескарисига айлантириб юборишимиздан илгари ёки уларни «Шанба эгаларини» лаънатлаганимиздек лаънатлашимиздан (яъни, маймун ва тўнғизларга айлантириб қўйишимиздан) илгари сизлардаги нарса (Таврот)ни тасдиқлаган ҳолда нозил қилган Китобимиз (Қурьон)га иймон келтирингиз. Аллоҳнинг амр-фармони шубҳасиз, адо қилингусидир'}, {'chapter': 4, 'verse': 48, 'text': 'إِنَّ ٱللَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَقَدِ ٱفۡتَرَىٰٓ إِثۡمًا عَظِيمًا\nАлбатта, Аллоҳ Ўзига (бирон нарсанинг) шерик қилинишини кечирмас. Шундан бошқа гуноҳларни Ўзи хоҳлаган бандалари учун кечирур. Ким Аллоҳга (бирор кимса ёки нарсани) шерик келтирса, бас, у буюк гуноҳни тўқиб чиқарибди'}, {'chapter': 4, 'verse': 49, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يُزَكُّونَ أَنفُسَهُمۚ بَلِ ٱللَّهُ يُزَكِّي مَن يَشَآءُ وَلَا يُظۡلَمُونَ فَتِيلًا\n(Эй Мҳаммад), ўзларини пок қилиб кўрсатаётганларни кўрмадингизми? Йўқ! Фақат Аллоҳ Ўзи хоҳлаган зотларни поклагувсидир. Ва уларга қилчалик зулм қилинмас'}, {'chapter': 4, 'verse': 50, 'text': 'ٱنظُرۡ كَيۡفَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَكَفَىٰ بِهِۦٓ إِثۡمٗا مُّبِينًا\nУларнинг («Биз Аллоҳнинг суюкли болаларимиз», деб) Аллоҳ шаънига ёлғон тўқиётганларини кўринг. Мана шунинг ўзи етарли очиқ гуноҳдир'}, {'chapter': 4, 'verse': 51, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يُؤۡمِنُونَ بِٱلۡجِبۡتِ وَٱلطَّـٰغُوتِ وَيَقُولُونَ لِلَّذِينَ كَفَرُواْ هَـٰٓؤُلَآءِ أَهۡدَىٰ مِنَ ٱلَّذِينَ ءَامَنُواْ سَبِيلًا\nКитобдан насибадор бўлган кимсаларнинг бут ва санамларга сиғинаётганларини ҳамда кофир кимсалар ҳақида: «Ўшалар иймон эгалари бўлган мусулмонлардан кўра туғрироқ йўлдалар», деяётганларини кўрмадингизми'}, {'chapter': 4, 'verse': 52, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُۖ وَمَن يَلۡعَنِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ نَصِيرًا\nУлар Аллоҳ лаънатлаган кимсалардир. Кимники Аллоҳ лаънатлар экан, бас, ҳеч қачон унинг учун бирон ёрдамчини топа олмайсиз'}, {'chapter': 4, 'verse': 53, 'text': 'أَمۡ لَهُمۡ نَصِيبٞ مِّنَ ٱلۡمُلۡكِ فَإِذٗا لَّا يُؤۡتُونَ ٱلنَّاسَ نَقِيرًا\nЁки уларнинг мол-мулклари борми? Шундай бўлган тақдирда ҳам одамларга зиғирчалик мурувват қилмаган бўлур эдилар'}, {'chapter': 4, 'verse': 54, 'text': 'أَمۡ يَحۡسُدُونَ ٱلنَّاسَ عَلَىٰ مَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۖ فَقَدۡ ءَاتَيۡنَآ ءَالَ إِبۡرَٰهِيمَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَءَاتَيۡنَٰهُم مُّلۡكًا عَظِيمٗا\nЁки одамларга (яъни, Муҳаммад алайҳис-салоту вас-саломга) Аллоҳ ўз фазлу карамидан берган нарсага (яъни, пайғамбарликка) ҳасад қиляптиларми? Ахир Биз илгари ҳам Иброҳим сулоласига (яъни, Мусо, Довуд, Сулаймон каби зотларга) Китоб ва ҳикмат (пайғамбарлик) бериб, уларни улуғ мулку давлатга эга қилган эдик-ку'}, {'chapter': 4, 'verse': 55, 'text': 'فَمِنۡهُم مَّنۡ ءَامَنَ بِهِۦ وَمِنۡهُم مَّن صَدَّ عَنۡهُۚ وَكَفَىٰ بِجَهَنَّمَ سَعِيرًا\nБас, уларнинг ичида унга (яъни, Муҳаммадга) иймон келтирганлар ҳам бор, ундан юз ўгирганлар ҳам бор. (Буларга) жаҳаннам ўти бас келур'}, {'chapter': 4, 'verse': 56, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا سَوۡفَ نُصۡلِيهِمۡ نَارٗا كُلَّمَا نَضِجَتۡ جُلُودُهُم بَدَّلۡنَٰهُمۡ جُلُودًا غَيۡرَهَا لِيَذُوقُواْ ٱلۡعَذَابَۗ إِنَّ ٱللَّهَ كَانَ عَزِيزًا حَكِيمٗا\nБизнинг оятларимизни инкор қилган кимсаларни албатта дўзахга киритажакмиз. Қачонки терилари куйиб битиши билан ҳақиқий азобни тотиб кўришлари учун ўрнига бошқа териларни алмаштирамиз. Албатта, Аллоҳ қудрат ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 57, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَنُدۡخِلُهُمۡ ظِلّٗا ظَلِيلًا\nИймон келтирган ва яхши амаллар қилган зотларни эса улар абадий яшайдиган, остидан дарёлар оқиб турадиган жаннатларга дохил қилажакмиз. Улар учун у жойларда пок жуфтлар бордир. Уларни қуюқ сояли жойларга киритурмиз'}, {'chapter': 4, 'verse': 58, 'text': '۞إِنَّ ٱللَّهَ يَأۡمُرُكُمۡ أَن تُؤَدُّواْ ٱلۡأَمَٰنَٰتِ إِلَىٰٓ أَهۡلِهَا وَإِذَا حَكَمۡتُم بَيۡنَ ٱلنَّاسِ أَن تَحۡكُمُواْ بِٱلۡعَدۡلِۚ إِنَّ ٱللَّهَ نِعِمَّا يَعِظُكُم بِهِۦٓۗ إِنَّ ٱللَّهَ كَانَ سَمِيعَۢا بَصِيرٗا\nАлбатта, Аллоҳ сизларни омонатларни ўз эгаларига топширишга ва одамлар орасида ҳукм қилганингизда адолат билан ҳукм қилишга буюради. Албатта, Аллоҳ сизларга энг яхши панд-насиҳатлар қилур. Албатта, Аллоҳ эшитгувчи, кўргувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 59, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَأُوْلِي ٱلۡأَمۡرِ مِنكُمۡۖ فَإِن تَنَٰزَعۡتُمۡ فِي شَيۡءٖ فَرُدُّوهُ إِلَى ٱللَّهِ وَٱلرَّسُولِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلًا\nЭй мўминлар, Аллоҳга итоат қилингиз ва пайғамбарга ҳамда ўзларингиздан бўлган (яъни, мусулмон) ҳокимларга бўйсунингиз! Бордию бирон нарса ҳақида талашиб қолсангиз, — агар ҳақиқатан Аллоҳга ва Охират Кунига ишонсангиз — у нарсани Аллоҳга ва пайғамбарига қайтарингиз! Мана шу яхшироқ ва чиройлироқ ечимдир'}, {'chapter': 4, 'verse': 60, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يَزۡعُمُونَ أَنَّهُمۡ ءَامَنُواْ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ يُرِيدُونَ أَن يَتَحَاكَمُوٓاْ إِلَى ٱلطَّـٰغُوتِ وَقَدۡ أُمِرُوٓاْ أَن يَكۡفُرُواْ بِهِۦۖ وَيُرِيدُ ٱلشَّيۡطَٰنُ أَن يُضِلَّهُمۡ ضَلَٰلَۢا بَعِيدٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), ўзларини сизга нозил қилинган нарсага (Қуръонга) ва сиздан илгари нозил қилинган нарсаларга иймон келтирган деб ҳисоблайдиган (айрим) кимсаларнинг шайтонга ҳукм сўраб боришни истаётганларини кўрмадингизми? Ҳолбуки, уларга унга ишонмаслик буюрилган эди. (Чунки) шайтон уларни бутунлай йўлдан оздиришни истайди'}, {'chapter': 4, 'verse': 61, 'text': 'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ رَأَيۡتَ ٱلۡمُنَٰفِقِينَ يَصُدُّونَ عَنكَ صُدُودٗا\nҚачон уларга «Аллоҳ нозил қилган Китобга ва пайғамбар ўгитларига келинглар», дейилса, у мунофиқларнинг сиздан қаттиқ юз ўгирганларини кўрасиз'}, {'chapter': 4, 'verse': 62, 'text': 'فَكَيۡفَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ ثُمَّ جَآءُوكَ يَحۡلِفُونَ بِٱللَّهِ إِنۡ أَرَدۡنَآ إِلَّآ إِحۡسَٰنٗا وَتَوۡفِيقًا\nЭнди ўзларининг қилмишлари сабабли уларга бирон мусибат етса, шундан кейин сизнинг ҳузурингизга келишиб: «Биз фақат яхшиликни ва орани ўнглашни истаган эдик, холос», деб қасам ичишлари қандоқ бўлди'}, {'chapter': 4, 'verse': 63, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ يَعۡلَمُ ٱللَّهُ مَا فِي قُلُوبِهِمۡ فَأَعۡرِضۡ عَنۡهُمۡ وَعِظۡهُمۡ وَقُل لَّهُمۡ فِيٓ أَنفُسِهِمۡ قَوۡلَۢا بَلِيغٗا\nУндай кимсаларнинг дилларидаги нарсани Аллоҳ яхши билур. Бас, сиз уларнинг (кирдикорларига) боқманг ва уларга панд-насиҳат қилиб, ўзлари ҳақида етук сўзларни айтинг'}, {'chapter': 4, 'verse': 64, 'text': 'وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا لِيُطَاعَ بِإِذۡنِ ٱللَّهِۚ وَلَوۡ أَنَّهُمۡ إِذ ظَّلَمُوٓاْ أَنفُسَهُمۡ جَآءُوكَ فَٱسۡتَغۡفَرُواْ ٱللَّهَ وَٱسۡتَغۡفَرَ لَهُمُ ٱلرَّسُولُ لَوَجَدُواْ ٱللَّهَ تَوَّابٗا رَّحِيمٗا\nБиз қай бир пайғамбарни юборган бўлсак, фақат Аллоҳнинг изни-иродаси билан унга итоат қилинсин, деб юборганмиз. Агар улар (шайтондан ҳукм сўраб боришлари билан) жонларига жабр қилган пайтларида дарҳол сизнинг олдингизга келиб, Аллоҳдан мағфират сўраганларида ва Пайғамбар ҳам улар учун мағфират сўраганида эди, Аллоҳнинг тавбаларни қабул қилгувчи, меҳрибон эканлигини топган бўлур эдилар'}, {'chapter': 4, 'verse': 65, 'text': 'فَلَا وَرَبِّكَ لَا يُؤۡمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيۡنَهُمۡ ثُمَّ لَا يَجِدُواْ فِيٓ أَنفُسِهِمۡ حَرَجٗا مِّمَّا قَضَيۡتَ وَيُسَلِّمُواْ تَسۡلِيمٗا\nЙўқ, Парвардигорингизга қасамки, то улар ўз ўрталарида чиққан келишмовчиликларда сизни ҳакам қилмагунларича ва кейин сиз чиқарган ҳукмдан дилларида ҳеч қандай танглик топмай, тўла таслим бўлмагунларича-бўйсунмагунларича зинҳор мўмин бўла олмайдилар'}, {'chapter': 4, 'verse': 66, 'text': 'وَلَوۡ أَنَّا كَتَبۡنَا عَلَيۡهِمۡ أَنِ ٱقۡتُلُوٓاْ أَنفُسَكُمۡ أَوِ ٱخۡرُجُواْ مِن دِيَٰرِكُم مَّا فَعَلُوهُ إِلَّا قَلِيلٞ مِّنۡهُمۡۖ وَلَوۡ أَنَّهُمۡ فَعَلُواْ مَا يُوعَظُونَ بِهِۦ لَكَانَ خَيۡرٗا لَّهُمۡ وَأَشَدَّ تَثۡبِيتٗا\nАгар Биз уларга (Бани Исроилга буюрганимиз каби) «бир-бирингизни ўлдиринг ва ўз диёрларингиздан чиқинг», деб буюрганимизда эди, улардан жуда оз қисми бу буйруқни бажарган бўлур эди. Агар улар қилинган панд-насиҳатларга амал қилганларида, ўзлари учун яхшироқ ва (иймонларини) мустаҳкам қилгувчироқ иш бўлур эди'}, {'chapter': 4, 'verse': 67, 'text': 'وَإِذٗا لَّأٓتَيۡنَٰهُم مِّن لَّدُنَّآ أَجۡرًا عَظِيمٗا\nВа у ҳолда Биз ҳам уларга ўз ҳузуримиздан улуғ мукофот берган бўлардик'}, {'chapter': 4, 'verse': 68, 'text': 'وَلَهَدَيۡنَٰهُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا\nҲамда уларни ҳақ йўлга ҳидоят қилган бўлардик'}, {'chapter': 4, 'verse': 69, 'text': 'وَمَن يُطِعِ ٱللَّهَ وَٱلرَّسُولَ فَأُوْلَـٰٓئِكَ مَعَ ٱلَّذِينَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِم مِّنَ ٱلنَّبِيِّـۧنَ وَٱلصِّدِّيقِينَ وَٱلشُّهَدَآءِ وَٱلصَّـٰلِحِينَۚ وَحَسُنَ أُوْلَـٰٓئِكَ رَفِيقٗا\nКимда-ким Аллоҳ ва пайғамбарга итоат этса, ана ўшалар Аллоҳ инъомларига сазовор бўлган зотлар — пайғамбарлар, сиддиқлар (ҳақ-рост иймон эгалари), шаҳидлар ва фақат яхши амаллар билан ўтган кишилар билан бирга бўлурлар. Улар эса энг яхши ҳамроҳлардир'}, {'chapter': 4, 'verse': 70, 'text': 'ذَٰلِكَ ٱلۡفَضۡلُ مِنَ ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ عَلِيمٗا\nБу Аллоҳ томонидан бўлган фазлу-марҳаматдир. Аллоҳнинг ўзи етарли билгувчидир'}, {'chapter': 4, 'verse': 71, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ خُذُواْ حِذۡرَكُمۡ فَٱنفِرُواْ ثُبَاتٍ أَوِ ٱنفِرُواْ جَمِيعٗا\nЭй мўминлар, душмандан эҳтиёт чораларингизни кўриб, жангга гуруҳларга бўлиниб ёки ҳаммангиз тўпланиб чиқинглар'}, {'chapter': 4, 'verse': 72, 'text': 'وَإِنَّ مِنكُمۡ لَمَن لَّيُبَطِّئَنَّ فَإِنۡ أَصَٰبَتۡكُم مُّصِيبَةٞ قَالَ قَدۡ أَنۡعَمَ ٱللَّهُ عَلَيَّ إِذۡ لَمۡ أَكُن مَّعَهُمۡ شَهِيدٗا\nШубҳасиз ораларингизда (жангга чиқишдан) кетда қоладиган, агар сизларга бирон мусибат етса, «Аллоҳ ёрлақаб улар билан бирга бўлмадим, (акс ҳолда ҳалок бўлур эдим)», дейдиган кимсалар ҳам бор'}, {'chapter': 4, 'verse': 73, 'text': 'وَلَئِنۡ أَصَٰبَكُمۡ فَضۡلٞ مِّنَ ٱللَّهِ لَيَقُولَنَّ كَأَن لَّمۡ تَكُنۢ بَيۡنَكُمۡ وَبَيۡنَهُۥ مَوَدَّةٞ يَٰلَيۡتَنِي كُنتُ مَعَهُمۡ فَأَفُوزَ فَوۡزًا عَظِيمٗا\nБордию сизларга Аллоҳдан фазлу марҳамат етса (яъни, ғалабага эришсангиз), у гўё (илгари) сизлар билан ўзининг орасида ҳеч қандай танишлик бўлмагандек «кошкийди, мен ҳам ўшалар билан бирга бўлиб, улуғ зафар (ва ўлжалар)га эришсам эди», дейиши аниқдир'}, {'chapter': 4, 'verse': 74, 'text': '۞فَلۡيُقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ ٱلَّذِينَ يَشۡرُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا بِٱلۡأٓخِرَةِۚ وَمَن يُقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ فَيُقۡتَلۡ أَوۡ يَغۡلِبۡ فَسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا\nАллоҳ йўлида бу ҳаёти дунё эвазига Охиратни сотиб оладиган зотлар жанг қилсинлар. Ким Аллоҳ йўлида жанг қилиб ўлдирилса ёки ғалаба қилса, унга улуғ мукофот ато этажакмиз'}, {'chapter': 4, 'verse': 75, 'text': 'وَمَا لَكُمۡ لَا تُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِ وَٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلرِّجَالِ وَٱلنِّسَآءِ وَٱلۡوِلۡدَٰنِ ٱلَّذِينَ يَقُولُونَ رَبَّنَآ أَخۡرِجۡنَا مِنۡ هَٰذِهِ ٱلۡقَرۡيَةِ ٱلظَّالِمِ أَهۡلُهَا وَٱجۡعَل لَّنَا مِن لَّدُنكَ وَلِيّٗا وَٱجۡعَل لَّنَا مِن لَّدُنكَ نَصِيرًا\n(Эй мўминлар), сизларга нима бўлдики, Аллоҳ йўлида ва «Парвардигоро, бизни эгалари золим бўлган бу шаҳардан озод қил ва бизга ўз ҳузурингдан бир дўст бергин, бизга ўз ҳузурингдан бир ёрдамчи қилгин», деяётган эркаклар, аёллар ҳамда болалардан иборат бўлган бечоралар(ни озод қилиш) йўлида жанг қилмаяпсизлар'}, {'chapter': 4, 'verse': 76, 'text': 'ٱلَّذِينَ ءَامَنُواْ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِۖ وَٱلَّذِينَ كَفَرُواْ يُقَٰتِلُونَ فِي سَبِيلِ ٱلطَّـٰغُوتِ فَقَٰتِلُوٓاْ أَوۡلِيَآءَ ٱلشَّيۡطَٰنِۖ إِنَّ كَيۡدَ ٱلشَّيۡطَٰنِ كَانَ ضَعِيفًا\nМўминлар Аллоҳ йўлида жанг қиладилар, кофир кимсалар эса шайтон йўлида жанг қиладилар. Бас, (эй мўминлар), шайтоннинг дўстларига қарши жанг қилингиз! Шубҳасиз, шайтоннинг макри заиф бўлгувчидир'}, {'chapter': 4, 'verse': 77, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ قِيلَ لَهُمۡ كُفُّوٓاْ أَيۡدِيَكُمۡ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ فَلَمَّا كُتِبَ عَلَيۡهِمُ ٱلۡقِتَالُ إِذَا فَرِيقٞ مِّنۡهُمۡ يَخۡشَوۡنَ ٱلنَّاسَ كَخَشۡيَةِ ٱللَّهِ أَوۡ أَشَدَّ خَشۡيَةٗۚ وَقَالُواْ رَبَّنَا لِمَ كَتَبۡتَ عَلَيۡنَا ٱلۡقِتَالَ لَوۡلَآ أَخَّرۡتَنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖۗ قُلۡ مَتَٰعُ ٱلدُّنۡيَا قَلِيلٞ وَٱلۡأٓخِرَةُ خَيۡرٞ لِّمَنِ ٱتَّقَىٰ وَلَا تُظۡلَمُونَ فَتِيلًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), мана бу кимсаларни кўрмадингизмики, уларга (илгари Маккада турган пайтларида кофирларга қарши жанг қилишга талабгор бўлганларида) «Ўзингизни босинг ва намозингизни ўқиб, закотингизни адо этиб туринглар», дейилган эди. Энди жанг фарз қилинганида эса баногоҳ улардан бир тўпи одамлардан Аллоҳдан қўрққандек, балки ундан-да қаттиқроқ қўрқмоқдалар. Ва (ҳатто) «Парвардигор, нега бизга жангни фарз қилдинг? Бизларга бу ажални бир оз кейинроқ юборганингда эди», дедилар. Уларга айтинг: «Дунё матоси озгинадир. Аллоҳдан қўрққан кишига Охират яхшироқдир. Сизларга қилчалик зулм қилинмас»'}, {'chapter': 4, 'verse': 78, 'text': 'أَيۡنَمَا تَكُونُواْ يُدۡرِككُّمُ ٱلۡمَوۡتُ وَلَوۡ كُنتُمۡ فِي بُرُوجٖ مُّشَيَّدَةٖۗ وَإِن تُصِبۡهُمۡ حَسَنَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِ ٱللَّهِۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِكَۚ قُلۡ كُلّٞ مِّنۡ عِندِ ٱللَّهِۖ فَمَالِ هَـٰٓؤُلَآءِ ٱلۡقَوۡمِ لَا يَكَادُونَ يَفۡقَهُونَ حَدِيثٗا\nҚаерда бўлсангиз, ҳатто мустаҳкам қалъалар ичида бўлсангиз ҳам ўлим сизларни топиб олур. Агар уларга (мунофиқларга) бирон яхшилик етса, «Бу Аллоҳнинг ҳузуридан», дейдилар. Борди-ю бирон ёмонлик етиб қолса, «Бунга сен сабабсан», дейдилар. Айтинг: «Ҳамма нарса Аллоҳдандир». Нега бу қавм кишилари ҳеч гап англамайдилар-а'}, {'chapter': 4, 'verse': 79, 'text': 'مَّآ أَصَابَكَ مِنۡ حَسَنَةٖ فَمِنَ ٱللَّهِۖ وَمَآ أَصَابَكَ مِن سَيِّئَةٖ فَمِن نَّفۡسِكَۚ وَأَرۡسَلۡنَٰكَ لِلنَّاسِ رَسُولٗاۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا\n(Эй инсон), сенга етган ҳар қандай яхшилик фақат Аллоҳдандир. Сенга етган ҳар қандай ёмонлик эса ўзингнинг қилмишингдандир. (Эй Муҳаммад алайҳис-салоту вас-салом), сизни Биз одамларга пайғамбар қилиб юбордик. Бунга Аллоҳнинг ўзи етарли гувоҳдир'}, {'chapter': 4, 'verse': 80, 'text': 'مَّن يُطِعِ ٱلرَّسُولَ فَقَدۡ أَطَاعَ ٱللَّهَۖ وَمَن تَوَلَّىٰ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗا\nКимки пайғамбарга итоат этса, демак, Аллоҳга итоат этибди. Кимки юз ўгирса (бунинг сизга зарари йўқдир. Зеро), Биз сизни уларнинг устига қўриқчи қилиб юборганимиз йўқ'}, {'chapter': 4, 'verse': 81, 'text': 'وَيَقُولُونَ طَاعَةٞ فَإِذَا بَرَزُواْ مِنۡ عِندِكَ بَيَّتَ طَآئِفَةٞ مِّنۡهُمۡ غَيۡرَ ٱلَّذِي تَقُولُۖ وَٱللَّهُ يَكۡتُبُ مَا يُبَيِّتُونَۖ فَأَعۡرِضۡ عَنۡهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًا\nУлар «Итоат этамиз», дейдилару ҳузурингиздан чиққанларидан кейин эса улардан бир тоифаси сиз айтган гапларнинг аксига хуфёна тил бириктирадилар. Аллоҳ эса уларнинг хуфёна келишувларини ёзиб қўюр. Бас, сиз улардан юз ўгириб, Аллоҳга таваккул қилинг. Аллоҳнинг ўзи етарли вакилдир'}, {'chapter': 4, 'verse': 82, 'text': 'أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَۚ وَلَوۡ كَانَ مِنۡ عِندِ غَيۡرِ ٱللَّهِ لَوَجَدُواْ فِيهِ ٱخۡتِلَٰفٗا كَثِيرٗا\nАхир улар Қуръон ҳақида фикр юритмайдиларми?! Агар у Аллоҳдан бошқа биров томонидан бўлса эди, унда кўп қарама-қаршиликларни топган бўлар эдилар-ку'}, {'chapter': 4, 'verse': 83, 'text': 'وَإِذَا جَآءَهُمۡ أَمۡرٞ مِّنَ ٱلۡأَمۡنِ أَوِ ٱلۡخَوۡفِ أَذَاعُواْ بِهِۦۖ وَلَوۡ رَدُّوهُ إِلَى ٱلرَّسُولِ وَإِلَىٰٓ أُوْلِي ٱلۡأَمۡرِ مِنۡهُمۡ لَعَلِمَهُ ٱلَّذِينَ يَسۡتَنۢبِطُونَهُۥ مِنۡهُمۡۗ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَٱتَّبَعۡتُمُ ٱلشَّيۡطَٰنَ إِلَّا قَلِيلٗا\nҚачон уларга (жангга кетган мусулмон аскарлар ҳақида) тинчлик ёки хавф-хатар (яъни, ғалаба ёки мағлубият) хабари келса, уни ёйиб юборадилар. Агар (улар ўзларига келган хабарни ҳар кимга ёйиб юрмасдан) Пайғамбарга ва ўзларидан бўлган бошлиқларгагина етказганларида эди, уни (яъни, мана шу хабарнинг ҳақиқатини) билмоқчи бўлган кишилар ўшалардан билган бўлар эдилар. Агар сизларга Аллоҳнинг фазлу марҳамати бўлмаса эди, (яъни, Аллоҳ ўз марҳамати билан сизларни иймонда устивор қилмаса эди) айрим кишилардан ташқари ҳаммангиз шайтонга эргашиб кетар эдингиз'}, {'chapter': 4, 'verse': 84, 'text': 'فَقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ لَا تُكَلَّفُ إِلَّا نَفۡسَكَۚ وَحَرِّضِ ٱلۡمُؤۡمِنِينَۖ عَسَى ٱللَّهُ أَن يَكُفَّ بَأۡسَ ٱلَّذِينَ كَفَرُواْۚ وَٱللَّهُ أَشَدُّ بَأۡسٗا وَأَشَدُّ تَنكِيلٗا\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ йўлида жанг қилинг. Зотан, сиз фақат ўзингиз учун жавобгарсиз. (Демак, ёлғиз қолсангиз ҳам жанг қилинг) ва мўминларни ҳам тарғиб қилинг! Шояд Аллоҳ кофир бўлган кимсаларнинг шиддатини даф қилса, Аллоҳ қуввати ортиқ, азоби қаттиқ Зотдир'}, {'chapter': 4, 'verse': 85, 'text': 'مَّن يَشۡفَعۡ شَفَٰعَةً حَسَنَةٗ يَكُن لَّهُۥ نَصِيبٞ مِّنۡهَاۖ وَمَن يَشۡفَعۡ شَفَٰعَةٗ سَيِّئَةٗ يَكُن لَّهُۥ كِفۡلٞ مِّنۡهَاۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقِيتٗا\nКим (одамлар орасида) чиройли — ҳақ тараф олиш билан тараф олса, ўзига ҳам унинг савобидан бир улуш тегур. Ким ноҳақ тараф олиш билан тараф олса, ўзи учун унинг гуноҳидан бир улуш тегур. Аллоҳ ҳамма нарсага қодир бўлган Зотдир'}, {'chapter': 4, 'verse': 86, 'text': 'وَإِذَا حُيِّيتُم بِتَحِيَّةٖ فَحَيُّواْ بِأَحۡسَنَ مِنۡهَآ أَوۡ رُدُّوهَآۗ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٍ حَسِيبًا\nҚачон сизларга бирон ибора билан салом берилса, сизлар ундан чиройлироқ қилиб алик олинглар ёки (ҳеч бўлмаса) ўша иборани қайтаринглар. Албатта, Аллоҳ ҳамма нарсани ҳисобга олгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 87, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۗ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ حَدِيثٗا\nЁлғиз Аллоҳдан ўзга ҳеч қандай илоҳ йўқдир. У сизларни ҳеч шак-шубҳасиз, бўлган Қиёмат Кунида тўплайди. Аллоҳдан кўра ростгўйроқ ким бор'}, {'chapter': 4, 'verse': 88, 'text': '۞فَمَا لَكُمۡ فِي ٱلۡمُنَٰفِقِينَ فِئَتَيۡنِ وَٱللَّهُ أَرۡكَسَهُم بِمَا كَسَبُوٓاْۚ أَتُرِيدُونَ أَن تَهۡدُواْ مَنۡ أَضَلَّ ٱللَّهُۖ وَمَن يُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا\n(Эй мўминлар), нега сизлар мунофиқлар тўғрисида икки гуруҳга бўлиниб олдингиз? Ахир, Аллоҳ уларни ўз қилмишлари сабабли (куфр ва нифоққа) қайтарди-ку?! Ёки сизлар Аллоҳ адаштирган кимсаларни Тўғри Йўлга солмоқчимисизлар? Кимники Аллоҳ адаштириб қўйган бўлса, унинг учун ҳаргиз йўл топа олмайсиз'}, {'chapter': 4, 'verse': 89, 'text': 'وَدُّواْ لَوۡ تَكۡفُرُونَ كَمَا كَفَرُواْ فَتَكُونُونَ سَوَآءٗۖ فَلَا تَتَّخِذُواْ مِنۡهُمۡ أَوۡلِيَآءَ حَتَّىٰ يُهَاجِرُواْ فِي سَبِيلِ ٱللَّهِۚ فَإِن تَوَلَّوۡاْ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَيۡثُ وَجَدتُّمُوهُمۡۖ وَلَا تَتَّخِذُواْ مِنۡهُمۡ وَلِيّٗا وَلَا نَصِيرًا\nУлар ўзлари кофир бўлганлари каби сизлар ҳам кофир бўлиб, улар билан баробар бўлиб қолишларингизни истайдилар. Бас, сизлар то Аллоҳ йўлида ҳижрат қилмагунларича уларни дўст тутманглар! Агар юз ўгирсалар, уларни топган жойингизда тутиб ўлдирингиз ва улардан на бир дўст ва на бир ёрдамчи олмангиз'}, {'chapter': 4, 'verse': 90, 'text': 'إِلَّا ٱلَّذِينَ يَصِلُونَ إِلَىٰ قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٌ أَوۡ جَآءُوكُمۡ حَصِرَتۡ صُدُورُهُمۡ أَن يُقَٰتِلُوكُمۡ أَوۡ يُقَٰتِلُواْ قَوۡمَهُمۡۚ وَلَوۡ شَآءَ ٱللَّهُ لَسَلَّطَهُمۡ عَلَيۡكُمۡ فَلَقَٰتَلُوكُمۡۚ فَإِنِ ٱعۡتَزَلُوكُمۡ فَلَمۡ يُقَٰتِلُوكُمۡ وَأَلۡقَوۡاْ إِلَيۡكُمُ ٱلسَّلَمَ فَمَا جَعَلَ ٱللَّهُ لَكُمۡ عَلَيۡهِمۡ سَبِيلٗا\nАммо (у мунофиқлардан) ораларингизда аҳду паймон бўлган қавмга келиб қўшилган ёки хоҳ сизлар билан, хоҳ ўзларининг қавми билан урушиш жонига тегиб олдингизга келган кимсаларга тегмангиз! Агар Аллоҳ истаса эди, уларни сизлардан устун қилиб қўйган бўлар эди ва улар сизларга қарши жанг қилар эдилар. Бас, агар сизлардан четланиб, сизларга қарши жанг қилмай сулҳ тузишни таклиф қилиб келсалар, Аллоҳ сизлар учун уларнинг устига ҳеч қандай йўл бермайди'}, {'chapter': 4, 'verse': 91, 'text': 'سَتَجِدُونَ ءَاخَرِينَ يُرِيدُونَ أَن يَأۡمَنُوكُمۡ وَيَأۡمَنُواْ قَوۡمَهُمۡ كُلَّ مَا رُدُّوٓاْ إِلَى ٱلۡفِتۡنَةِ أُرۡكِسُواْ فِيهَاۚ فَإِن لَّمۡ يَعۡتَزِلُوكُمۡ وَيُلۡقُوٓاْ إِلَيۡكُمُ ٱلسَّلَمَ وَيَكُفُّوٓاْ أَيۡدِيَهُمۡ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡۚ وَأُوْلَـٰٓئِكُمۡ جَعَلۡنَا لَكُمۡ عَلَيۡهِمۡ سُلۡطَٰنٗا مُّبِينٗا\n(У мунофиқлардан) бошқа бир тоифасининг эса сизлардан ҳам, ўз қавмларидан ҳам тинч-хотиржам бўлишни кўзлаётганларини кўрасизлар. Ҳар қачон фитнага (яъни, куфрга) қайтишга тўғри келса, ўзларини унга ташлайдилар. Бас, агар улар сизлардан четланмас, сизларга сулҳ таклиф қилмас ва қўлларини тиймас эканлар, уларни топган жойингизда тутиб ўлдирингиз. Мана, биз бундай кимсаларга қарши (ҳужум қилишингиз учун) сизларга очиқ ҳужжат бериб қўйдик'}, {'chapter': 4, 'verse': 92, 'text': 'وَمَا كَانَ لِمُؤۡمِنٍ أَن يَقۡتُلَ مُؤۡمِنًا إِلَّا خَطَـٔٗاۚ وَمَن قَتَلَ مُؤۡمِنًا خَطَـٔٗا فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖ وَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦٓ إِلَّآ أَن يَصَّدَّقُواْۚ فَإِن كَانَ مِن قَوۡمٍ عَدُوّٖ لَّكُمۡ وَهُوَ مُؤۡمِنٞ فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ وَإِن كَانَ مِن قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞ فَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦ وَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ تَوۡبَةٗ مِّنَ ٱللَّهِۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nМўмин мўминни фақат билмаган ҳолдагина ўлдириб қўйиши мумкин. Ким бир мўминни билмай ўлдириб қўйса, у бир мўмин қулни озод қилиши ва марҳумнинг эгаларига — агар кечиб юбормасалар — хун тўлаши вожибдир. Агар марҳум ўзи мўмин бўлиб, сизларга душман қавмдан бўлса, фақат бир мўмин қулни озод қилиши вожиб. Агар у сизлар билан сулҳ тузган қавмдан бўлса, унинг эгаларига хун тўлаш ва бир мўмин қулни озод қилиш лозим. Бас, ким (озод қилиш учун бир қул) топишга қодир бўлмаса, Аллоҳга тавба қилиб муттасил икки ой рўза тутмоғи лозим. Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 93, 'text': 'وَمَن يَقۡتُلۡ مُؤۡمِنٗا مُّتَعَمِّدٗا فَجَزَآؤُهُۥ جَهَنَّمُ خَٰلِدٗا فِيهَا وَغَضِبَ ٱللَّهُ عَلَيۡهِ وَلَعَنَهُۥ وَأَعَدَّ لَهُۥ عَذَابًا عَظِيمٗا\nКим қасддан бир мўминни ўлдирса, унинг жазоси жаҳаннам бўлиб, ўша жойда абадий қолажак. Ва у Аллоҳнинг ғазаби ва лаънатига дучор бўлган, Аллоҳ унинг учун улуғ азобни тайёрлаб қўйгандир'}, {'chapter': 4, 'verse': 94, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا ضَرَبۡتُمۡ فِي سَبِيلِ ٱللَّهِ فَتَبَيَّنُواْ وَلَا تَقُولُواْ لِمَنۡ أَلۡقَىٰٓ إِلَيۡكُمُ ٱلسَّلَٰمَ لَسۡتَ مُؤۡمِنٗا تَبۡتَغُونَ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فَعِندَ ٱللَّهِ مَغَانِمُ كَثِيرَةٞۚ كَذَٰلِكَ كُنتُم مِّن قَبۡلُ فَمَنَّ ٱللَّهُ عَلَيۡكُمۡ فَتَبَيَّنُوٓاْۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nЭй мўминлар, қачон Аллоҳ йўлида жиҳод учун сафарга чиқсангиз (душманларингизни) аниқ таниб олинглар ва бу ҳаёти дунёнинг нарсасини истаб сизларга салом берган кишига: «Сен мўмин эмассан!», деманглар! Зеро, Аллоҳнинг ҳузурида беҳисоб ўлжалар бордир. Илгари сизлар ҳам шундай эдингиз (яъни, кофирлар қўл остида мусулмонлигингизни яшириб юришга мажбур эдингиз). Энди Аллоҳ сизларни (Ислом давлатида яшаш неъмати билан) мамнун қилди. Бас, (душманларингизни) аниқ таниб олинглар. Албатта, Аллоҳ қилаётган амалларингиздан хабардор бўлган Зотдир'}, {'chapter': 4, 'verse': 95, 'text': 'لَّا يَسۡتَوِي ٱلۡقَٰعِدُونَ مِنَ ٱلۡمُؤۡمِنِينَ غَيۡرُ أُوْلِي ٱلضَّرَرِ وَٱلۡمُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ فَضَّلَ ٱللَّهُ ٱلۡمُجَٰهِدِينَ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ عَلَى ٱلۡقَٰعِدِينَ دَرَجَةٗۚ وَكُلّٗا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَفَضَّلَ ٱللَّهُ ٱلۡمُجَٰهِدِينَ عَلَى ٱلۡقَٰعِدِينَ أَجۡرًا عَظِيمٗا\nМўминлардан ўзлари бешикаст бўлиб туриб (жиҳодга чиқмай) ўтириб олган кишилар билан Аллоҳ йўлида мол ва жонлари ила курашган зотлар баробар бўлмайди. Аллоҳ молу жонлари билан курашган зотларни (бирон узр сабабли жиҳодга чиқмай) ўтириб олган кишилардан ҳам бир даража афзал қилди ва барчаларига гўзал оқибатни (яъни, жаннатни) ваъда қилди. Ҳамда Аллоҳ жиҳод қилгувчиларни ўтириб олувчилардан улуғ мукофотни —'}, {'chapter': 4, 'verse': 96, 'text': 'دَرَجَٰتٖ مِّنۡهُ وَمَغۡفِرَةٗ وَرَحۡمَةٗۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمًا\nЎз томонидан бўлажак юксак даражалар, мағфират ва марҳамат билан афзал қилди. Аллоҳ мағфиратли, меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 97, 'text': 'إِنَّ ٱلَّذِينَ تَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡ قَالُواْ فِيمَ كُنتُمۡۖ قَالُواْ كُنَّا مُسۡتَضۡعَفِينَ فِي ٱلۡأَرۡضِۚ قَالُوٓاْ أَلَمۡ تَكُنۡ أَرۡضُ ٱللَّهِ وَٰسِعَةٗ فَتُهَاجِرُواْ فِيهَاۚ فَأُوْلَـٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَسَآءَتۡ مَصِيرًا\nАлбатта, (мусулмонлар билан бирга ҳижрат қилмасдан кофирлар қўл остида яшашга рози бўлиш билан) ўз жонларига жабр қилган кимсаларнинг жонларини олиш чоғида фаришталар уларга: «Қандай ҳолда яшадингиз?», деганларида: «Биз бу Ерда чорасиз бечоралар эдик», дедилар. (Шунда фаришталар): «Ҳижрат қилсанглар Аллоҳнинг Ери кенг эди-ку?! (Нега дину иймонларингиз йўлида бу юртдан ҳижрат қилмадинглар?)», дейишди. Бундайларнинг жонлари жаҳаннамдир. Қандай ёмон жойдир у'}, {'chapter': 4, 'verse': 98, 'text': 'إِلَّا ٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلرِّجَالِ وَٱلنِّسَآءِ وَٱلۡوِلۡدَٰنِ لَا يَسۡتَطِيعُونَ حِيلَةٗ وَلَا يَهۡتَدُونَ سَبِيلٗا\nФақат бирон чора топишга қодир бўлмай, ҳижрат йўлини истаб топа олмай чорасиз қолган кишилар, аёллар ва болалар бор'}, {'chapter': 4, 'verse': 99, 'text': 'فَأُوْلَـٰٓئِكَ عَسَى ٱللَّهُ أَن يَعۡفُوَ عَنۡهُمۡۚ وَكَانَ ٱللَّهُ عَفُوًّا غَفُورٗا\nБундайларни шояд Аллоҳ авф этса. Зотан, Аллоҳ афв этгувчи, мағфират қилгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 100, 'text': '۞وَمَن يُهَاجِرۡ فِي سَبِيلِ ٱللَّهِ يَجِدۡ فِي ٱلۡأَرۡضِ مُرَٰغَمٗا كَثِيرٗا وَسَعَةٗۚ وَمَن يَخۡرُجۡ مِنۢ بَيۡتِهِۦ مُهَاجِرًا إِلَى ٱللَّهِ وَرَسُولِهِۦ ثُمَّ يُدۡرِكۡهُ ٱلۡمَوۡتُ فَقَدۡ وَقَعَ أَجۡرُهُۥ عَلَى ٱللَّهِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nКим Аллоҳ йўлида ҳижрат қилса, Ер юзида кўп паноҳ бўлгудек жойларни ва кенгчиликни топгай. Ким уйидан Аллоҳ ва Унинг пайғамбари сари муҳожир бўлиб чиқиб, сўнг (шу йўлда) унга ўлим етса, муҳаққақки унинг ажри-мукофоти Аллоҳнинг зиммасига тушар. Аллоҳ мағфиратли, меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 101, 'text': 'وَإِذَا ضَرَبۡتُمۡ فِي ٱلۡأَرۡضِ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَقۡصُرُواْ مِنَ ٱلصَّلَوٰةِ إِنۡ خِفۡتُمۡ أَن يَفۡتِنَكُمُ ٱلَّذِينَ كَفَرُوٓاْۚ إِنَّ ٱلۡكَٰفِرِينَ كَانُواْ لَكُمۡ عَدُوّٗا مُّبِينٗا\n(Эй мўминлар), сафар қилиб бошқа Ерларга борганингизда агар кофирлар фитнасидан қўрқсангиз, намозингизни қаср (қисқа) қилиб ўқишингизнинг гуноҳи йўқдир. Чунки кофирлар сизларга очиқ душман бўлган кимсалардир'}, {'chapter': 4, 'verse': 102, 'text': 'وَإِذَا كُنتَ فِيهِمۡ فَأَقَمۡتَ لَهُمُ ٱلصَّلَوٰةَ فَلۡتَقُمۡ طَآئِفَةٞ مِّنۡهُم مَّعَكَ وَلۡيَأۡخُذُوٓاْ أَسۡلِحَتَهُمۡۖ فَإِذَا سَجَدُواْ فَلۡيَكُونُواْ مِن وَرَآئِكُمۡ وَلۡتَأۡتِ طَآئِفَةٌ أُخۡرَىٰ لَمۡ يُصَلُّواْ فَلۡيُصَلُّواْ مَعَكَ وَلۡيَأۡخُذُواْ حِذۡرَهُمۡ وَأَسۡلِحَتَهُمۡۗ وَدَّ ٱلَّذِينَ كَفَرُواْ لَوۡ تَغۡفُلُونَ عَنۡ أَسۡلِحَتِكُمۡ وَأَمۡتِعَتِكُمۡ فَيَمِيلُونَ عَلَيۡكُم مَّيۡلَةٗ وَٰحِدَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ إِن كَانَ بِكُمۡ أَذٗى مِّن مَّطَرٍ أَوۡ كُنتُم مَّرۡضَىٰٓ أَن تَضَعُوٓاْ أَسۡلِحَتَكُمۡۖ وَخُذُواْ حِذۡرَكُمۡۗ إِنَّ ٱللَّهَ أَعَدَّ لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), қачон сиз (жанг майдонида) мўминлар орасида бўлиб, уларга имом бўлган ҳолда намоз ўқимоқчи бўлсангиз, улардан бир тоифаси қуролланган ҳолларида сиз билан намозга турсинлар. Бас, қачон сажда қилишгач (яъни, намознинг бир ракаатини ўқиб бўлишгач), орқаларингга бориб, (сизларни қўриқлаб) турсинлар ва ҳали намоз ўқимаган бошқа тоифа келиб сиз билан бирга намоз ўқисинлар. Улар ҳам эҳтиёт чораларини кўриб, қуролланиб олсинлар. Кофирлар сизлар қурол-яроғ ва асбоб-анжомларингиздан ғафлатда бўлган пайтингизда устингизга бирданига ҳамла қилишни истайдилар. Агар ёғингарчиликдан азият чексангизлар ёки бемор бўлсангизлар, қуролларингизни ечиб қўйишингиз гуноҳ эмас. Аммо эҳтиёт чорангизни кўриб қўйинглар. Албатта, Аллоҳ кофирлар учун хор қилгувчи азобни тайёрлаб қўйгандир'}, {'chapter': 4, 'verse': 103, 'text': 'فَإِذَا قَضَيۡتُمُ ٱلصَّلَوٰةَ فَٱذۡكُرُواْ ٱللَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِكُمۡۚ فَإِذَا ٱطۡمَأۡنَنتُمۡ فَأَقِيمُواْ ٱلصَّلَوٰةَۚ إِنَّ ٱلصَّلَوٰةَ كَانَتۡ عَلَى ٱلۡمُؤۡمِنِينَ كِتَٰبٗا مَّوۡقُوتٗا\nНамозни адо қилиб бўлганингиздан кейин ҳам турган, ўтирган ва ёнбошлаган пайтларингизда доим Аллоҳни ёд этингиз! Бас, қачон хотиржам бўлгач, намозни тўкис адо этингиз! Албатта, намоз мўминларга (вақти) тайинланган фарз бўлди'}, {'chapter': 4, 'verse': 104, 'text': 'وَلَا تَهِنُواْ فِي ٱبۡتِغَآءِ ٱلۡقَوۡمِۖ إِن تَكُونُواْ تَأۡلَمُونَ فَإِنَّهُمۡ يَأۡلَمُونَ كَمَا تَأۡلَمُونَۖ وَتَرۡجُونَ مِنَ ٱللَّهِ مَا لَا يَرۡجُونَۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمًا\nБу кофир қавмни қувишда сусткашлик қилмангиз! Агар қийналаётган бўлсангизлар, улар ҳам сизлар қийналганингиздек қийналмоқдалар. (Шу билан бирга) сизлар Аллоҳ томонидан улар умид қилмаган нарсани (яъни, савобни, шаҳидлик неъматига муяссар бўлишни, қолаверса, ғалаба қозонишни) умид қилмоқдасизлар-ку?! Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 105, 'text': 'إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ لِتَحۡكُمَ بَيۡنَ ٱلنَّاسِ بِمَآ أَرَىٰكَ ٱللَّهُۚ وَلَا تَكُن لِّلۡخَآئِنِينَ خَصِيمٗا\nАлбатта, Биз сизга ушбу Ҳақ Китобни (Қуръонни) одамлар орасида Аллоҳ кўрсатган йўл билан ҳукм этишингиз учун нозил қилдик. Сиз хоинларни ҳимоя қилгувчи бўлманг'}, {'chapter': 4, 'verse': 106, 'text': 'وَٱسۡتَغۡفِرِ ٱللَّهَۖ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nАллоҳдан мағфират қилишни сўранг! Албатта, Аллоҳ мағфират қилгувчи ва меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 107, 'text': 'وَلَا تُجَٰدِلۡ عَنِ ٱلَّذِينَ يَخۡتَانُونَ أَنفُسَهُمۡۚ إِنَّ ٱللَّهَ لَا يُحِبُّ مَن كَانَ خَوَّانًا أَثِيمٗا\nСиз ўзларига хиёнат қиладиган (жинояткор) кимсаларнинг ёнини олманг! Албатта, Аллоҳ хоин ва жиноятчи бўлган кимсаларни севмайди'}, {'chapter': 4, 'verse': 108, 'text': 'يَسۡتَخۡفُونَ مِنَ ٱلنَّاسِ وَلَا يَسۡتَخۡفُونَ مِنَ ٱللَّهِ وَهُوَ مَعَهُمۡ إِذۡ يُبَيِّتُونَ مَا لَا يَرۡضَىٰ مِنَ ٱلۡقَوۡلِۚ وَكَانَ ٱللَّهُ بِمَا يَعۡمَلُونَ مُحِيطًا\nУлар (ўзларининг жиноятларини) одамлардан яшира оладилар, аммо Аллоҳдан яшира олмайдилар. У Ўзи рози бўлмайдиган гапларнинг режасини хуфёна тузаётган пайтларида ҳам улар билан бирга бўлур. Аллоҳ уларнинг қилаётган амалларини иҳота қилгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 109, 'text': 'هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ جَٰدَلۡتُمۡ عَنۡهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فَمَن يُجَٰدِلُ ٱللَّهَ عَنۡهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ أَم مَّن يَكُونُ عَلَيۡهِمۡ وَكِيلٗا\nҲой сизлар, бу дунёда-ку, уларнинг ёнини олдингиз. Энди Қиёмат Кунида ким уларни Аллоҳдан ҳимоя қилиб ёнларини олур ёки ким уларнинг устида вакил бўлиб турар'}, {'chapter': 4, 'verse': 110, 'text': 'وَمَن يَعۡمَلۡ سُوٓءًا أَوۡ يَظۡلِمۡ نَفۡسَهُۥ ثُمَّ يَسۡتَغۡفِرِ ٱللَّهَ يَجِدِ ٱللَّهَ غَفُورٗا رَّحِيمٗا\nКим бирон-бир ёмон иш қилса ёки ўз жонига жабр қилса, сўнгра Аллоҳдан мағфират сўраса, Аллоҳни мағфират қилгувчи ва меҳрибон эканини топар-кўрар'}, {'chapter': 4, 'verse': 111, 'text': 'وَمَن يَكۡسِبۡ إِثۡمٗا فَإِنَّمَا يَكۡسِبُهُۥ عَلَىٰ نَفۡسِهِۦۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nКимки бирон гуноҳ қилса, фақат ўзига зарар қилган бўлади. Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 112, 'text': 'وَمَن يَكۡسِبۡ خَطِيٓـَٔةً أَوۡ إِثۡمٗا ثُمَّ يَرۡمِ بِهِۦ بَرِيٓـٔٗا فَقَدِ ٱحۡتَمَلَ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا\nКимки бирон хато ёки гуноҳни қилиб қўйиб, сўнг уни бир пок одамга туҳмат қилиб отса, муҳаққақки, у бўҳтон ва очиқ гуноҳни ўз зиммасига олибди'}, {'chapter': 4, 'verse': 113, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكَ وَرَحۡمَتُهُۥ لَهَمَّت طَّآئِفَةٞ مِّنۡهُمۡ أَن يُضِلُّوكَ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡۖ وَمَا يَضُرُّونَكَ مِن شَيۡءٖۚ وَأَنزَلَ ٱللَّهُ عَلَيۡكَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَعَلَّمَكَ مَا لَمۡ تَكُن تَعۡلَمُۚ وَكَانَ فَضۡلُ ٱللَّهِ عَلَيۡكَ عَظِيمٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар сизга Аллоҳнинг фазлу марҳамати бўлмаганда эди, улардан бир тоифаси сизни йўлдан оздиришга уриниб қолган эди. Лекин улар фақат ўзларини адаштирадилар, холос. Сизга эса бирон зиён етказа олмайдилар. (Зеро) Аллоҳ сизга Китоб ва ҳикматни нозил қилди ва билмаган нарсаларингизни билдирди. Аллоҳнинг сизга қилган фазлу марҳамати улуғ бўлди'}, {'chapter': 4, 'verse': 114, 'text': '۞لَّا خَيۡرَ فِي كَثِيرٖ مِّن نَّجۡوَىٰهُمۡ إِلَّا مَنۡ أَمَرَ بِصَدَقَةٍ أَوۡ مَعۡرُوفٍ أَوۡ إِصۡلَٰحِۭ بَيۡنَ ٱلنَّاسِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِ فَسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا\nУларнинг кўп махфий суҳбатларидан — агар садақа беришга ё бирон яхшилик қилишга ёки одамлар ўртасини ислоҳ қилишга буюрган бўлмасалар — ҳеч қандай фойда йўқдир. Ким Аллоҳ ризосини истаб бу ишларни қилса, унга улуғ мукофот беражакмиз'}, {'chapter': 4, 'verse': 115, 'text': 'وَمَن يُشَاقِقِ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُ ٱلۡهُدَىٰ وَيَتَّبِعۡ غَيۡرَ سَبِيلِ ٱلۡمُؤۡمِنِينَ نُوَلِّهِۦ مَا تَوَلَّىٰ وَنُصۡلِهِۦ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرًا\nКим ҳақ йўлни аниқ билганидан кейин пайғамбарга хилоф иш қилса ва мўминларнинг йўлларидан бошқа йўлга эргашиб кетса, биз уни кетганича қўйиб берамиз. Сўнгра жаҳаннамга дохил қиламиз. Нақадар ёмон жойдир у'}, {'chapter': 4, 'verse': 116, 'text': 'إِنَّ ٱللَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَقَدۡ ضَلَّ ضَلَٰلَۢا بَعِيدًا\nАлбатта, Аллоҳ ўзига (бирон нарса ё кимсанинг) шерик қилинишини кечирмайди. Шундан бошқа гуноҳларни Ўзи хоҳлаган бандалари учун кечиради. Кимки Аллоҳга ширк келтирса, демак, жуда қаттиқ йўлдан озибди'}, {'chapter': 4, 'verse': 117, 'text': 'إِن يَدۡعُونَ مِن دُونِهِۦٓ إِلَّآ إِنَٰثٗا وَإِن يَدۡعُونَ إِلَّا شَيۡطَٰنٗا مَّرِيدٗا\nУлар (яъни, Аллоҳга ширк келтирган кимсалар) Аллоҳни қўйиб, фақат (Лот, Уззо, Манот каби) санамларга сиғинадилар ва фақат итоатсиз шайтонга сиғинадилар'}, {'chapter': 4, 'verse': 118, 'text': 'لَّعَنَهُ ٱللَّهُۘ وَقَالَ لَأَتَّخِذَنَّ مِنۡ عِبَادِكَ نَصِيبٗا مَّفۡرُوضٗا\nАллоҳ уни (шайтонни) лаънатлади. (Шунда) у деди: «Қасамки, бандаларнинг орасидан ўзимга тегишли насибани албатта оламан'}, {'chapter': 4, 'verse': 119, 'text': 'وَلَأُضِلَّنَّهُمۡ وَلَأُمَنِّيَنَّهُمۡ وَلَأٓمُرَنَّهُمۡ فَلَيُبَتِّكُنَّ ءَاذَانَ ٱلۡأَنۡعَٰمِ وَلَأٓمُرَنَّهُمۡ فَلَيُغَيِّرُنَّ خَلۡقَ ٱللَّهِۚ وَمَن يَتَّخِذِ ٱلشَّيۡطَٰنَ وَلِيّٗا مِّن دُونِ ٱللَّهِ فَقَدۡ خَسِرَ خُسۡرَانٗا مُّبِينٗا\nВа уларни йўлдан оздираман, хомхаёлларга мубтало қиламан, буюрсам улар чорваларининг қулоқларини кесадилар, буюрсам Аллоҳ яратган нарсаларни ўзгартириб-бузадилар». Кимки Аллоҳни қўйиб, шайтонни дўст тутса, бас, у очиқдан-очиқ зиён қилибди'}, {'chapter': 4, 'verse': 120, 'text': 'يَعِدُهُمۡ وَيُمَنِّيهِمۡۖ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا\n(Шайтон) уларга ваъдалар берур, хомхаёлларга мубтало қилур. Охир-оқибатда шайтон берган ваъдалар фақат ёлғон-сароб бўлиб чиқар'}, {'chapter': 4, 'verse': 121, 'text': 'أُوْلَـٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُ وَلَا يَجِدُونَ عَنۡهَا مَحِيصٗا\nУндай кимсаларнинг жойлари жаҳаннам бўлиб, ундан қочиб қутула олмагайлар'}, {'chapter': 4, 'verse': 122, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ وَعۡدَ ٱللَّهِ حَقّٗاۚ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ قِيلٗا\nИймон келтириб, яхши амалларни қилган зотларни эса остидан дарёлар оқиб турадиган жаннатларга киритажакмиз — улар у Ерда абадий яшарлар. Бу Аллоҳнинг ҳақ ваъдасидир. Аллоҳдан кўра ростгўйроқ ким бор'}, {'chapter': 4, 'verse': 123, 'text': 'لَّيۡسَ بِأَمَانِيِّكُمۡ وَلَآ أَمَانِيِّ أَهۡلِ ٱلۡكِتَٰبِۗ مَن يَعۡمَلۡ سُوٓءٗا يُجۡزَ بِهِۦ وَلَا يَجِدۡ لَهُۥ مِن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا\nСизларнинг хомхаёлларингиз ҳам, аҳли китобнинг хомхаёллари ҳам ҳақ эмас — кимки ёмон амал қилса, жазосини олур ва ўзи учун Аллоҳдан ўзга на бир дўст ва на бир мададкор топмас'}, {'chapter': 4, 'verse': 124, 'text': 'وَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ مِن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا يُظۡلَمُونَ نَقِيرٗا\nХоҳ эркак, хоҳ аёл бўлсин — кимда-ким мўмин бўлган ҳолида яхши амаллардан қилса, ана ўша кишилар жаннатга кирурлар ва уларга заррача зулм қилинмас'}, {'chapter': 4, 'verse': 125, 'text': 'وَمَنۡ أَحۡسَنُ دِينٗا مِّمَّنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنٞ وَٱتَّبَعَ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۗ وَٱتَّخَذَ ٱللَّهُ إِبۡرَٰهِيمَ خَلِيلٗا\nҲақиқий мусулмон бўлган ҳолида ўзини Аллоҳга топширган ва ҳақ йўлдаги Иброҳимнинг динига эргашган кишининг динидан ҳам гўзалроқ дин борми?! (Ахир) Иброҳимни Аллоҳ Ўзига дўст тутган'}, {'chapter': 4, 'verse': 126, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٖ مُّحِيطٗا\nОсмонлардаги ва Ердаги бор нарса Аллоҳникидир. Аллоҳ ҳамма нарсани иҳота қилгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 127, 'text': 'وَيَسۡتَفۡتُونَكَ فِي ٱلنِّسَآءِۖ قُلِ ٱللَّهُ يُفۡتِيكُمۡ فِيهِنَّ وَمَا يُتۡلَىٰ عَلَيۡكُمۡ فِي ٱلۡكِتَٰبِ فِي يَتَٰمَى ٱلنِّسَآءِ ٱلَّـٰتِي لَا تُؤۡتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرۡغَبُونَ أَن تَنكِحُوهُنَّ وَٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلۡوِلۡدَٰنِ وَأَن تَقُومُواْ لِلۡيَتَٰمَىٰ بِٱلۡقِسۡطِۚ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ كَانَ بِهِۦ عَلِيمٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиздан аёллар ҳақида фатво сўрайдилар. Айтинг: «Улар тўғрисида сизларга Аллоҳ ва Китобда (Қуръонда) тиловат қилинадиган оятлар фатво берур. Яна (қарамоғингиздаги вояга етган) етим қизларни (агар гўзал бўлсалар) улар учун фарз қилинган маҳрларини бермасдан (никоҳингизга олишингизни) ва (агар хунук бўлсалар) никоҳингизга олишдан юз ўгириб (улардан қоладиган меросга тама қилиб то ўлгунларича бошқа бировга турмушга чиқармай сақлашларингизни сизларга ҳаром қилади). Яна ночор болалар ҳақида фатво бериб, бундай етимлар хусусида адолат билан туришингизни амр қилур. Сизлар нима яхшилик қилсангиз, албатта Аллоҳ уни билиб тургувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 128, 'text': 'وَإِنِ ٱمۡرَأَةٌ خَافَتۡ مِنۢ بَعۡلِهَا نُشُوزًا أَوۡ إِعۡرَاضٗا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يُصۡلِحَا بَيۡنَهُمَا صُلۡحٗاۚ وَٱلصُّلۡحُ خَيۡرٞۗ وَأُحۡضِرَتِ ٱلۡأَنفُسُ ٱلشُّحَّۚ وَإِن تُحۡسِنُواْ وَتَتَّقُواْ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nАгар бирон аёл эри томонидан кўнгилсизлик ёки юз ўгириб кетиш содир бўлишидан қўрқса, у иккови ўзаро бир сулҳга келишиб олишлари зарарсиздир. Сулҳ (ажралиб кетишдан) яхшироқдир. Нафслар қизғанишга ҳозиру нозир бўлиб турадилар. Агар аёлларингизга чиройли муомала қилсангиз ва Аллоҳдан қўрқсангиз (ўзингизга яхшидир). Зеро, Аллоҳ қилаётган амалларингиздан хабардор бўлган Зотдир'}, {'chapter': 4, 'verse': 129, 'text': 'وَلَن تَسۡتَطِيعُوٓاْ أَن تَعۡدِلُواْ بَيۡنَ ٱلنِّسَآءِ وَلَوۡ حَرَصۡتُمۡۖ فَلَا تَمِيلُواْ كُلَّ ٱلۡمَيۡلِ فَتَذَرُوهَا كَٱلۡمُعَلَّقَةِۚ وَإِن تُصۡلِحُواْ وَتَتَّقُواْ فَإِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nҲар қанча уринсангизлар ҳам хотинларингиз ўртасида адолат қилишга қодир бўлмайсизлар. Бас, бутунлай (суйган хотинларингиз томонга) оғиб кетиб (кўнгилсиз бўлиб қолган) хотинингизни муаллақа каби ташлаб қўймангиз! Агар ўзларингизни ўнглаб Аллоҳдан қўрқсангизлар, албатта Аллоҳ мағфиратли, меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 130, 'text': 'وَإِن يَتَفَرَّقَا يُغۡنِ ٱللَّهُ كُلّٗا مِّن سَعَتِهِۦۚ وَكَانَ ٱللَّهُ وَٰسِعًا حَكِيمٗا\nБордию эр-хотин ажралиб кетсалар, Аллоҳ иккаласини ҳам Ўзининг кенг карами билан беҳожат қилиб қўйгай. (Яъни, ҳар иккисига ҳам аввалги турмушидан яхшироқ эр ёки хотин насиб этиши мумкин). Аллоҳ фазлу карами кенг ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 131, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَلَقَدۡ وَصَّيۡنَا ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَإِيَّاكُمۡ أَنِ ٱتَّقُواْ ٱللَّهَۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ غَنِيًّا حَمِيدٗا\nОсмонлардаги ва Ердаги бор нарса Аллоҳникидир. Биз сизлардан илгари Китоб берилган зотларга ҳам, сизларга ҳам Аллоҳдан қўрқинглар, деб амр қилдик. Агар кофир бўлиб кетсангизлар (Аллоҳга ҳеч бир зарар етказа олмайсизлар). Зеро, осмонлардаги бор нарса ва Ердаги бор нарса Аллоҳникидир. Ва Аллоҳ беҳожат ва мақтовга сазовор бўлган Зотдир'}, {'chapter': 4, 'verse': 132, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًا\nОсмонлардаги ва Ердаги бор нарса Аллоҳникидир. Аллоҳнинг Ўзи етарли вакилдир. (Яъни, Ер ва осмонлардаги барча ишларни кузатиб, сақлаб турувчи Зотдир)'}, {'chapter': 4, 'verse': 133, 'text': 'إِن يَشَأۡ يُذۡهِبۡكُمۡ أَيُّهَا ٱلنَّاسُ وَيَأۡتِ بِـَٔاخَرِينَۚ وَكَانَ ٱللَّهُ عَلَىٰ ذَٰلِكَ قَدِيرٗا\nАгар хоҳласа, эй одамлар, сизларни кетказиб, бошқаларни келтирур. Аллоҳ бунга қодир бўлган Зотдир'}, {'chapter': 4, 'verse': 134, 'text': 'مَّن كَانَ يُرِيدُ ثَوَابَ ٱلدُّنۡيَا فَعِندَ ٱللَّهِ ثَوَابُ ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَكَانَ ٱللَّهُ سَمِيعَۢا بَصِيرٗا\nКим бу дунёнинг савобини истаса, билсинки, Аллоҳнинг ҳузурида ҳам бу дунёнинг, ҳам Охиратнинг савоби бордир. Аллоҳ эшитгувчи, кўргувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 135, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُواْ قَوَّـٰمِينَ بِٱلۡقِسۡطِ شُهَدَآءَ لِلَّهِ وَلَوۡ عَلَىٰٓ أَنفُسِكُمۡ أَوِ ٱلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَۚ إِن يَكُنۡ غَنِيًّا أَوۡ فَقِيرٗا فَٱللَّهُ أَوۡلَىٰ بِهِمَاۖ فَلَا تَتَّبِعُواْ ٱلۡهَوَىٰٓ أَن تَعۡدِلُواْۚ وَإِن تَلۡوُۥٓاْ أَوۡ تُعۡرِضُواْ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nЭй мўминлар, адолат билан тургувчи ҳамда ўзларингнинг ёки ота-она ва қариндош-уруғларнинг зарарига бўлса-да, Аллоҳ учун тўғри гувоҳлик бергувчи бўлинглар! У (яъни, гувоҳлик берилувчи) бой бўладими, камбағал бўладими, ҳар иккисига ҳам Олоҳнинг Ўзи (сизлардан кўра) яқинроқдир. Бас, адолат қилмасдан нафси ҳавога эргашиб кетманглар. Агар тилларингни буриб (нотўғри гувоҳлик берсанглар) ёки (гувоҳлик беришдан) бош тортсанглар, албатта Аллоҳ қилаётган амалларингиздан хабардор бўлган Зотдир'}, {'chapter': 4, 'verse': 136, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ ءَامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَٱلۡكِتَٰبِ ٱلَّذِي نَزَّلَ عَلَىٰ رَسُولِهِۦ وَٱلۡكِتَٰبِ ٱلَّذِيٓ أَنزَلَ مِن قَبۡلُۚ وَمَن يَكۡفُرۡ بِٱللَّهِ وَمَلَـٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ فَقَدۡ ضَلَّ ضَلَٰلَۢا بَعِيدًا\nЭй мўминлар, Аллоҳга, Унинг пайғамбарига ва шу пайғамбарига нозил қилган Китобига ҳамда илгари нозил бўлган Китобларига иймонингиз комил бўлсин. Кимки Аллоҳга, фаришталарга, китобларига, пайғамбарларига ва Охират Кунига ишонмаса, (кофир бўлса), демак, у жуда қаттиқ адашибди'}, {'chapter': 4, 'verse': 137, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ٱزۡدَادُواْ كُفۡرٗا لَّمۡ يَكُنِ ٱللَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ سَبِيلَۢا\nАлбатта иймон келтириб, сўнг куфрга қайтган, кейин яна иймонга келиб, сўнгра яна кофир бўлган, кейин эса куфрлари янада зиёда бўлган кимсаларни Аллоҳ ҳаргиз мағфират қилмайди ва уларни зинҳор ҳақ йўлга ҳидоят қилмайди'}, {'chapter': 4, 'verse': 138, 'text': 'بَشِّرِ ٱلۡمُنَٰفِقِينَ بِأَنَّ لَهُمۡ عَذَابًا أَلِيمًا\nБундай мунофиқларга улар учун аламли азоб борлигидан «хушхабар» бериб қўйинг'}, {'chapter': 4, 'verse': 139, 'text': 'ٱلَّذِينَ يَتَّخِذُونَ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۚ أَيَبۡتَغُونَ عِندَهُمُ ٱلۡعِزَّةَ فَإِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِيعٗا\nУлар мўминларни қўйиб кофирларни дўст тутадилар. Улар ўша кофирлар олдидан куч-қудрат излайдиларми?! (Овора бўладилар!) Зеро, бор куч-қудрат Аллоҳникидир'}, {'chapter': 4, 'verse': 140, 'text': 'وَقَدۡ نَزَّلَ عَلَيۡكُمۡ فِي ٱلۡكِتَٰبِ أَنۡ إِذَا سَمِعۡتُمۡ ءَايَٰتِ ٱللَّهِ يُكۡفَرُ بِهَا وَيُسۡتَهۡزَأُ بِهَا فَلَا تَقۡعُدُواْ مَعَهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦٓ إِنَّكُمۡ إِذٗا مِّثۡلُهُمۡۗ إِنَّ ٱللَّهَ جَامِعُ ٱلۡمُنَٰفِقِينَ وَٱلۡكَٰفِرِينَ فِي جَهَنَّمَ جَمِيعًا\nАхир Аллоҳ сизларга Китобда: «Қачонки Аллоҳнинг оятлари инкор қилинаётганини ва масхара қилинаётганини эшитсангизлар, то бошқа гапга ўтмагунларича ундай кимсалар билан бирга ўтирманглар», деган сўзларни нозил қилган эди-ку?! (Модомики, улар билан ўтирган экансиз), демак, сизлар ҳам шак-шубҳасиз, уларнинг ўзисиз. Албатта, Аллоҳ барча мунофиқ ва кофирларни жаҳаннамга жамлагувчидир'}, {'chapter': 4, 'verse': 141, 'text': 'ٱلَّذِينَ يَتَرَبَّصُونَ بِكُمۡ فَإِن كَانَ لَكُمۡ فَتۡحٞ مِّنَ ٱللَّهِ قَالُوٓاْ أَلَمۡ نَكُن مَّعَكُمۡ وَإِن كَانَ لِلۡكَٰفِرِينَ نَصِيبٞ قَالُوٓاْ أَلَمۡ نَسۡتَحۡوِذۡ عَلَيۡكُمۡ وَنَمۡنَعۡكُم مِّنَ ٱلۡمُؤۡمِنِينَۚ فَٱللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَلَن يَجۡعَلَ ٱللَّهُ لِلۡكَٰفِرِينَ عَلَى ٱلۡمُؤۡمِنِينَ سَبِيلًا\nУлар (яъни, мунофиқлар) сизларга кўз тикиб турадилар. Агар сизларга Аллоҳ томонидан фатҳу зафар бўлса: «Биз ҳам сизлар билан бир диндамиз-ку», дейдилар. Агар ғалаба кофирларга насиб бўлса: «Биз сизлардан устун бўлган ва сизларни мўминлардан ҳимоя қилган эмасмидик?», дейишади. Энди Қиёмат Кунида Аллоҳ ўрталарингда ҳакамлик қилур. Ва Аллоҳ ҳаргиз кофирлар учун мўминлар устига йўл бермагай'}, {'chapter': 4, 'verse': 142, 'text': 'إِنَّ ٱلۡمُنَٰفِقِينَ يُخَٰدِعُونَ ٱللَّهَ وَهُوَ خَٰدِعُهُمۡ وَإِذَا قَامُوٓاْ إِلَى ٱلصَّلَوٰةِ قَامُواْ كُسَالَىٰ يُرَآءُونَ ٱلنَّاسَ وَلَا يَذۡكُرُونَ ٱللَّهَ إِلَّا قَلِيلٗا\nАлбатта, мунофиқлар Аллоҳни алдамоқчи бўладилар. — Ҳолбуки, Аллоҳ уларни «алдаб» қўйгувчидир. — Ва қачон намозга турсалар дангасалик билан, хўжакўрсинга турадилар ва Аллоҳни камдан-кам ёдга оладилар'}, {'chapter': 4, 'verse': 143, 'text': 'مُّذَبۡذَبِينَ بَيۡنَ ذَٰلِكَ لَآ إِلَىٰ هَـٰٓؤُلَآءِ وَلَآ إِلَىٰ هَـٰٓؤُلَآءِۚ وَمَن يُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا\nУлар на уёқлик, на буёқлик бўлмай икки орада сарсон ҳолда қолганлар. Кимники Аллоҳ йўлдан оздирса сиз унга бирон йўл топиб бера олмайсиз'}, {'chapter': 4, 'verse': 144, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۚ أَتُرِيدُونَ أَن تَجۡعَلُواْ لِلَّهِ عَلَيۡكُمۡ سُلۡطَٰنٗا مُّبِينًا\nЭй мўминлар, мўминларни қўйиб, кофирларни дўст тутманглар! Аллоҳ учун ўз зарарларингизга очиқ ҳужжат қилиб беришни истайсизми'}, {'chapter': 4, 'verse': 145, 'text': 'إِنَّ ٱلۡمُنَٰفِقِينَ فِي ٱلدَّرۡكِ ٱلۡأَسۡفَلِ مِنَ ٱلنَّارِ وَلَن تَجِدَ لَهُمۡ نَصِيرًا\nАлбатта, мунофиқлар дўзахнинг энг тубан жойида бўлурлар. Ва улар учун бирон мададкор топа олмайсиз'}, {'chapter': 4, 'verse': 146, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَٱعۡتَصَمُواْ بِٱللَّهِ وَأَخۡلَصُواْ دِينَهُمۡ لِلَّهِ فَأُوْلَـٰٓئِكَ مَعَ ٱلۡمُؤۡمِنِينَۖ وَسَوۡفَ يُؤۡتِ ٱللَّهُ ٱلۡمُؤۡمِنِينَ أَجۡرًا عَظِيمٗا\nМагар тавба қилиб ўзларини ўнглаган ва Аллоҳга боғланиб, динларини Аллоҳ учун холис қилган зотлар — ана ўшаларгина мўминлар билан бирга бўлурлар. Яқинда Аллоҳ мўминларга улуғ ажр ато этажак'}, {'chapter': 4, 'verse': 147, 'text': 'مَّا يَفۡعَلُ ٱللَّهُ بِعَذَابِكُمۡ إِن شَكَرۡتُمۡ وَءَامَنتُمۡۚ وَكَانَ ٱللَّهُ شَاكِرًا عَلِيمٗا\nАгар шукр қилиб, иймонли бўлсангизлар, Аллоҳ сизларни нега азобласин?! Ахир Аллоҳ (бандаларининг тоат-ибодатларига савоб ато этиш билан) шукр қилгувчи ва билгувчи бўлган Зот-ку'}, {'chapter': 4, 'verse': 148, 'text': '۞لَّا يُحِبُّ ٱللَّهُ ٱلۡجَهۡرَ بِٱلسُّوٓءِ مِنَ ٱلۡقَوۡلِ إِلَّا مَن ظُلِمَۚ وَكَانَ ٱللَّهُ سَمِيعًا عَلِيمًا\nАллоҳ ёмон сўзларнинг (яъни, дуоибаднинг) ошкора айтилишини суймайди. Магар мазлум томонидан бўлса (жоиздир). Аллоҳ эшитгувчи, билгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 149, 'text': 'إِن تُبۡدُواْ خَيۡرًا أَوۡ تُخۡفُوهُ أَوۡ تَعۡفُواْ عَن سُوٓءٖ فَإِنَّ ٱللَّهَ كَانَ عَفُوّٗا قَدِيرًا\n(Эй мўминлар), хоҳ ошкора, хоҳ яширинча бирон яхшилик қилсангиз ёки бирон ёмонликни афв қилиб юборсангиз (Аллоҳ суйган бандалардан бўлурсиз). Зеро, Аллоҳ қудрат эгаси бўлгани ҳолда афв қилгувчи бўлган Зотдир'}, {'chapter': 4, 'verse': 150, 'text': 'إِنَّ ٱلَّذِينَ يَكۡفُرُونَ بِٱللَّهِ وَرُسُلِهِۦ وَيُرِيدُونَ أَن يُفَرِّقُواْ بَيۡنَ ٱللَّهِ وَرُسُلِهِۦ وَيَقُولُونَ نُؤۡمِنُ بِبَعۡضٖ وَنَكۡفُرُ بِبَعۡضٖ وَيُرِيدُونَ أَن يَتَّخِذُواْ بَيۡنَ ذَٰلِكَ سَبِيلًا\nАлбатта, Аллоҳ ва пайғамбарига ишонмайдиган, Аллоҳ билан пайғамбарларининг ораларини ажратишни истайдиган (яъни, Аллоҳга ишониб, пайғамбарларини инкор қиладиган) ва «айрим пайғамбарларга ишонамиз, айримларига ишонмаймиз», дейдиган ҳамда ора йўлни тутишни истайдиган кимсалар —'}, {'chapter': 4, 'verse': 151, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰفِرُونَ حَقّٗاۚ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا\nАна ўшалар ҳақиқий кофирдирлар. Бундай, кофирлар учун хор қилгувчи азобни тайёрлаб қўйганмиз'}, {'chapter': 4, 'verse': 152, 'text': 'وَٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦ وَلَمۡ يُفَرِّقُواْ بَيۡنَ أَحَدٖ مِّنۡهُمۡ أُوْلَـٰٓئِكَ سَوۡفَ يُؤۡتِيهِمۡ أُجُورَهُمۡۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nАллоҳ ва Унинг пайғамбарларига иймон келтирган ҳамда улардан биронтасини ажратиб қўймаган зотларга эса яқинда (Аллоҳ муносиб) мукофотларини ато этажак. Аллоҳ мағфиратли, меҳрибон бўлган Зотдир'}, {'chapter': 4, 'verse': 153, 'text': 'يَسۡـَٔلُكَ أَهۡلُ ٱلۡكِتَٰبِ أَن تُنَزِّلَ عَلَيۡهِمۡ كِتَٰبٗا مِّنَ ٱلسَّمَآءِۚ فَقَدۡ سَأَلُواْ مُوسَىٰٓ أَكۡبَرَ مِن ذَٰلِكَ فَقَالُوٓاْ أَرِنَا ٱللَّهَ جَهۡرَةٗ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ بِظُلۡمِهِمۡۚ ثُمَّ ٱتَّخَذُواْ ٱلۡعِجۡلَ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ فَعَفَوۡنَا عَن ذَٰلِكَۚ وَءَاتَيۡنَا مُوسَىٰ سُلۡطَٰنٗا مُّبِينٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), аҳли китоб сиздан кўкдан бир Китоб туширишингизни сўрайди. Мусодан бундан ҳам каттароқ нарсани сўраган эдилар: «Бизга Аллоҳни очиқ кўрсат», дейишган эди. Шунда золимликлари сабабли уларни чақмоқ урган эди. Сўнгра уларга аниқ-равшан ҳужжатлар келганидан кейин бузоқни худо қилиб олишди. Буни ҳам афв этдик ва Мусога очиқ ҳужжат ато этдик'}, {'chapter': 4, 'verse': 154, 'text': 'وَرَفَعۡنَا فَوۡقَهُمُ ٱلطُّورَ بِمِيثَٰقِهِمۡ وَقُلۡنَا لَهُمُ ٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا وَقُلۡنَا لَهُمۡ لَا تَعۡدُواْ فِي ٱلسَّبۡتِ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا\nАҳдларини (олишдан бош тортганларида, қўрққан ҳолларида куч билан) олишлари учун устларига Тур тоғини кўтардик ва уларга: «Бу дарвозадан сажда қилган ҳолингизда кирингиз», дедик. Яна уларга: «Шанба кунлари (балиқ овлаш билан) ҳаддингиздан ошманг», дедик ҳамда улардан қаттиқ аҳд-паймон олдик'}, {'chapter': 4, 'verse': 155, 'text': 'فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ وَكُفۡرِهِم بِـَٔايَٰتِ ٱللَّهِ وَقَتۡلِهِمُ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَقَوۡلِهِمۡ قُلُوبُنَا غُلۡفُۢۚ بَلۡ طَبَعَ ٱللَّهُ عَلَيۡهَا بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا\nСўнгра аҳду паймонларини бузганликлари, Аллоҳнинг оятларини инкор қилганликлари, пайғамбарларни ноҳақ ўлдирганликлари ва: «Дилларимиз қулф», деганликлари сабабли (Биз уларни лаънатладик). Йўқ, (уларнинг диллари шунчаки қулфланган эмас), балки куфрлари сабабли Аллоҳ уларнинг дилларини муҳрлаб қўйгандир. Бас, камдан-камлари иймон келтирадилар'}, {'chapter': 4, 'verse': 156, 'text': 'وَبِكُفۡرِهِمۡ وَقَوۡلِهِمۡ عَلَىٰ مَرۡيَمَ بُهۡتَٰنًا عَظِيمٗا\nЯна кофирликлари ва Марям хусусида улуғ бўҳтон қилганликлари сабабли (Биз уларни лаънатладик)'}, {'chapter': 4, 'verse': 157, 'text': 'وَقَوۡلِهِمۡ إِنَّا قَتَلۡنَا ٱلۡمَسِيحَ عِيسَى ٱبۡنَ مَرۡيَمَ رَسُولَ ٱللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمۡۚ وَإِنَّ ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِ لَفِي شَكّٖ مِّنۡهُۚ مَا لَهُم بِهِۦ مِنۡ عِلۡمٍ إِلَّا ٱتِّبَاعَ ٱلظَّنِّۚ وَمَا قَتَلُوهُ يَقِينَۢا\nҲамда Аллоҳнинг пайғамбари бўлган ал-Масиҳ Ийсо бинни Марямни «бизлар ўлдирганмиз», деган сўзлари сабабли (Биз уларни лаънатладик). Ҳолбуки, улар уни ўлдирганлари ҳам, осганлари ҳам йўқ. Фақат улар учун (бошқа биров Ийсога) ўхшатиб қўйилди, холос. Албатта, Ийсо ҳақида талашиб-тортишган кимсалар унинг (ўлдирилган-ўлдирилмагани) ҳақида шубҳада қолганлар. У ҳақда фақат гумонларга бериладилар, холос. Уни ўлдирмаганлари аниқдир'}, {'chapter': 4, 'verse': 158, 'text': 'بَل رَّفَعَهُ ٱللَّهُ إِلَيۡهِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا\nБалки уни Аллоҳ ўз ҳузурига кўтаргандир. Аллоҳ қудрат ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 159, 'text': 'وَإِن مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ إِلَّا لَيُؤۡمِنَنَّ بِهِۦ قَبۡلَ مَوۡتِهِۦۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكُونُ عَلَيۡهِمۡ شَهِيدٗا\nҲар бир аҳли китоб ўлими олдидан унга (яъни, Ийсога) албатта иймон келтирур. Қиёмат Кунида эса у буларнинг зарарига гувоҳ бўлур'}, {'chapter': 4, 'verse': 160, 'text': 'فَبِظُلۡمٖ مِّنَ ٱلَّذِينَ هَادُواْ حَرَّمۡنَا عَلَيۡهِمۡ طَيِّبَٰتٍ أُحِلَّتۡ لَهُمۡ وَبِصَدِّهِمۡ عَن سَبِيلِ ٱللَّهِ كَثِيرٗا\nЯҳудий бўлган кимсаларнинг золимликлари ва кўп кишиларни Аллоҳ йўлидан тўсганлари сабабли уларга олдин ҳалол қилинган нарсаларни ҳаром қилиб қўйдик'}, {'chapter': 4, 'verse': 161, 'text': 'وَأَخۡذِهِمُ ٱلرِّبَوٰاْ وَقَدۡ نُهُواْ عَنۡهُ وَأَكۡلِهِمۡ أَمۡوَٰلَ ٱلنَّاسِ بِٱلۡبَٰطِلِۚ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ مِنۡهُمۡ عَذَابًا أَلِيمٗا\nЯна манъ этилган бўлсалар-да, судхўрлик қилганлари ва одамларнинг молларини ноҳақ еганлари сабабли (уларга олдин ҳалол қилинган нарсаларни ҳаром қилиб қўйдик). Ва улардан кофир бўлган кимсалар учун аламли азобни тайёрлаб қўйдик'}, {'chapter': 4, 'verse': 162, 'text': 'لَّـٰكِنِ ٱلرَّـٰسِخُونَ فِي ٱلۡعِلۡمِ مِنۡهُمۡ وَٱلۡمُؤۡمِنُونَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَۚ وَٱلۡمُقِيمِينَ ٱلصَّلَوٰةَۚ وَٱلۡمُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلۡمُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ أُوْلَـٰٓئِكَ سَنُؤۡتِيهِمۡ أَجۡرًا عَظِيمًا\nЛекин уларнинг орасидан илмда мустаҳкам бўлган зотлар ҳамда сизга нозил қилинган нарсага ва сиздан илгари нозил қилинган нарсаларга иймон келтирадиган мўминлар, хусусан намозни барпо қилувчилар, закотни адо қилгувчилар ва Аллоҳ ва Охират Кунига иймон келтирувчилар — ана ўшаларга улуғ мукофот ато этажакмиз'}, {'chapter': 4, 'verse': 163, 'text': '۞إِنَّآ أَوۡحَيۡنَآ إِلَيۡكَ كَمَآ أَوۡحَيۡنَآ إِلَىٰ نُوحٖ وَٱلنَّبِيِّـۧنَ مِنۢ بَعۡدِهِۦۚ وَأَوۡحَيۡنَآ إِلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَعِيسَىٰ وَأَيُّوبَ وَيُونُسَ وَهَٰرُونَ وَسُلَيۡمَٰنَۚ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Биз Нуҳга ва ундан кейинги пайғамбарларга ваҳий юборганимиз каби сизга ҳам ваҳий юбордик, яна Иброҳим, Исмоил, Исҳоқ, Яъқуб ва унинг уруғ-авлоди, Ийсо, Айюб, Юнус, Ҳорун ва Сулаймон пайғамбарларга ҳам ваҳий юборганмиз. Довудга Забурни ато этдик'}, {'chapter': 4, 'verse': 164, 'text': 'وَرُسُلٗا قَدۡ قَصَصۡنَٰهُمۡ عَلَيۡكَ مِن قَبۡلُ وَرُسُلٗا لَّمۡ نَقۡصُصۡهُمۡ عَلَيۡكَۚ وَكَلَّمَ ٱللَّهُ مُوسَىٰ تَكۡلِيمٗا\nАйрим пайғамбарлар ҳақида сизга илгари ҳикоя қилдик, айрим пайғамбарларни эса сизга ҳикоя қилганимиз йўқ. Мусо билан Аллоҳнинг Ўзи (бевосита) гаплашди'}, {'chapter': 4, 'verse': 165, 'text': 'رُّسُلٗا مُّبَشِّرِينَ وَمُنذِرِينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى ٱللَّهِ حُجَّةُۢ بَعۡدَ ٱلرُّسُلِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا\nТоки бу пайғамбарлар ўтганларидан кейин одамлар учун Аллоҳга қарши ҳужжат бўлиб қолмаслиги учун пайғамбарларни (мўминларга жаннат ҳақида) хушхабар элтувчи ва (кофирларни дўзах азобидан) қўрқитувчи қилиб юбордик. Аллоҳ қудрат ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 166, 'text': 'لَّـٰكِنِ ٱللَّهُ يَشۡهَدُ بِمَآ أَنزَلَ إِلَيۡكَۖ أَنزَلَهُۥ بِعِلۡمِهِۦۖ وَٱلۡمَلَـٰٓئِكَةُ يَشۡهَدُونَۚ وَكَفَىٰ بِٱللَّهِ شَهِيدًا\nЛекин Аллоҳ сизга Ўз билими билан нозил қилган нарсага гувоҳлик берур. Фаришталар ҳам гувоҳлик берурлар. Аллоҳнинг Ўзи етарли гувоҳдир'}, {'chapter': 4, 'verse': 167, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ قَدۡ ضَلُّواْ ضَلَٰلَۢا بَعِيدًا\nАлбатта, кофир бўлган ва Аллоҳнинг йўлидан тўсган кимсалар қаттиқ адашгандирлар'}, {'chapter': 4, 'verse': 168, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَظَلَمُواْ لَمۡ يَكُنِ ٱللَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ طَرِيقًا\nАлбатта, куфр йўлига кириб, жабру зулм қилган кимсаларни Аллоҳ ҳаргиз кечирмайди ва зинҳор ҳақ йўлга ҳидоят қилмайди'}, {'chapter': 4, 'verse': 169, 'text': 'إِلَّا طَرِيقَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا\nМагар уларни жаҳаннам йўлига равона қилур ва улар у жойда абадий қолажаклар. Бу Аллоҳга осондир'}, {'chapter': 4, 'verse': 170, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُمُ ٱلرَّسُولُ بِٱلۡحَقِّ مِن رَّبِّكُمۡ فَـَٔامِنُواْ خَيۡرٗا لَّكُمۡۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nЭй одамлар, бу пайғамбар сизларга Парвардигорингиздан ҳақ динни келтирди. Бас, иймон келтирингиз, ўзингиз учун яхши бўлур. Агар инкор қилсанглар (ўзингизга зарар қиласиз, холос). Зеро, Осмонлардаги бор нарса ва Ердаги бор нарса Аллоҳникидир. Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 4, 'verse': 171, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ وَلَا تَقُولُواْ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ إِنَّمَا ٱلۡمَسِيحُ عِيسَى ٱبۡنُ مَرۡيَمَ رَسُولُ ٱللَّهِ وَكَلِمَتُهُۥٓ أَلۡقَىٰهَآ إِلَىٰ مَرۡيَمَ وَرُوحٞ مِّنۡهُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرُسُلِهِۦۖ وَلَا تَقُولُواْ ثَلَٰثَةٌۚ ٱنتَهُواْ خَيۡرٗا لَّكُمۡۚ إِنَّمَا ٱللَّهُ إِلَٰهٞ وَٰحِدٞۖ سُبۡحَٰنَهُۥٓ أَن يَكُونَ لَهُۥ وَلَدٞۘ لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا\nЭй аҳли китоб, (Ийсони худо дейиш билан) динингизда ҳаддингиздан ошмангиз! Аллоҳ шаънига эса фақат ҳақ гапни айтингиз! Албатта, ал-Масиҳ Ийсо бинни Марям фақат Аллоҳнинг пайғамбари ва Унинг Марямга етказган сўзи ҳамда унинг томонидан бўлган руҳ (Соҳибидир), холос. Бас, Аллоҳ ва Унинг пайғамбарларига иймон келтирингиз! «(Аллоҳ) учта (яъни, Аллоҳ Марям ва Ийсо учалалари ҳам Аллоҳлардир)», демангиз! (Бундай ботил эътиқоддан) тўхтангиз, шунда ўзингиз учун яхши бўлур. Ҳеч шак-шубҳасиз, Аллоҳ якка-ягонадир. У фарзандлик бўлишдан пок бўлган Зотдир. Осмонлардаги ва Ердаги бор нарса Уникидир. Аллоҳнинг Ўзи етарли вакилдир'}, {'chapter': 4, 'verse': 172, 'text': 'لَّن يَسۡتَنكِفَ ٱلۡمَسِيحُ أَن يَكُونَ عَبۡدٗا لِّلَّهِ وَلَا ٱلۡمَلَـٰٓئِكَةُ ٱلۡمُقَرَّبُونَۚ وَمَن يَسۡتَنكِفۡ عَنۡ عِبَادَتِهِۦ وَيَسۡتَكۡبِرۡ فَسَيَحۡشُرُهُمۡ إِلَيۡهِ جَمِيعٗا\nМасиҳ ҳам, Аллоҳга яқин қилинган фаришталар ҳам Аллоҳга банда бўлишдан зинҳор ор қилмайдилар. Кимки Унга бандалик қилишдан ор қилса ва кибру ҳаво қилса, бас, уларнинг барчасини Ўз ҳузурига жам қилажак'}, {'chapter': 4, 'verse': 173, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡ وَيَزِيدُهُم مِّن فَضۡلِهِۦۖ وَأَمَّا ٱلَّذِينَ ٱسۡتَنكَفُواْ وَٱسۡتَكۡبَرُواْ فَيُعَذِّبُهُمۡ عَذَابًا أَلِيمٗا وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا\nАна ўшанда иймон келтириб, яхши амаллар қилган кишиларнинг мукофотларини комил қилиб бериб, яна Ўз фазлу карамидан қўшимча ҳам қилур. Аммо ор қилган, кибру ҳаво қилган кимсаларни эса аламли азоб билан азоблайди ва улар ўзлари учун Аллоҳдан ўзга на бир дўст ва на бир мададкор тополмаслар'}, {'chapter': 4, 'verse': 174, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُم بُرۡهَٰنٞ مِّن رَّبِّكُمۡ وَأَنزَلۡنَآ إِلَيۡكُمۡ نُورٗا مُّبِينٗا\nЭй одамлар, сизларга Парвардигорингиздан ҳужжат келди. Ва Биз сизларга равшан нурни нозил қилдик'}, {'chapter': 4, 'verse': 175, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَٱعۡتَصَمُواْ بِهِۦ فَسَيُدۡخِلُهُمۡ فِي رَحۡمَةٖ مِّنۡهُ وَفَضۡلٖ وَيَهۡدِيهِمۡ إِلَيۡهِ صِرَٰطٗا مُّسۡتَقِيمٗا\nАллоҳга иймон келтириб, Унга боғланган зотларни ўз томонидан бўлган раҳмат ва фазлу марҳаматга (яъни, жаннатга) дохил қилур. Ва уларни Ўзининг туғри йўлига йўллагай'}, {'chapter': 4, 'verse': 176, 'text': 'يَسۡتَفۡتُونَكَ قُلِ ٱللَّهُ يُفۡتِيكُمۡ فِي ٱلۡكَلَٰلَةِۚ إِنِ ٱمۡرُؤٌاْ هَلَكَ لَيۡسَ لَهُۥ وَلَدٞ وَلَهُۥٓ أُخۡتٞ فَلَهَا نِصۡفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ إِن لَّمۡ يَكُن لَّهَا وَلَدٞۚ فَإِن كَانَتَا ٱثۡنَتَيۡنِ فَلَهُمَا ٱلثُّلُثَانِ مِمَّا تَرَكَۚ وَإِن كَانُوٓاْ إِخۡوَةٗ رِّجَالٗا وَنِسَآءٗ فَلِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَيَيۡنِۗ يُبَيِّنُ ٱللَّهُ لَكُمۡ أَن تَضِلُّواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сиздан фатво сўрайдилар. Айтинг: «Аллоҳ сизларга на ота ва на боласи бўлмаган одам тўғрисида фатво берур: агар фарзандсиз бўлган ёлғиз бир эркак ўлса, (агар) унинг синглиси бўлса, унга (акаси) қўйиб кетган мероснинг ярмини олур. Агар синглисининг фарзанди бўлмаса, ака унга меросхўр бўлур. Агар сингиллар иккита бўлса, улар учун ака қолдирган нарсадан учдан иккиси тегур. Агар меросхўрлар ака-сингиллар бўлса, бир эркак учун икки аёл ҳиссаси берилур. Адашиб кетмасликларинг учун Аллоҳ сизларга (Ўз ҳукмларини) баён қилур Аллоҳ ҳамма нарсани билгувчидир»'}, {'chapter': 5, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَوۡفُواْ بِٱلۡعُقُودِۚ أُحِلَّتۡ لَكُم بَهِيمَةُ ٱلۡأَنۡعَٰمِ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡ غَيۡرَ مُحِلِّي ٱلصَّيۡدِ وَأَنتُمۡ حُرُمٌۗ إِنَّ ٱللَّهَ يَحۡكُمُ مَا يُرِيدُ\nЭй мўминлар, ақдларга (ўзаро келишган битимларга) вафо қилингиз! Сизлар учун — (қуйидаги оятларда ҳаромлиги) тиловат қилинадиган нарсалардан бошқа чорва ҳайвонлари ҳалол қилинди. Лекин иҳромда бўлган ҳолларингизда ов овлашни ҳалол қилиб олмангиз. Албатта, Аллоҳ Ўзи истагандек ҳукм қилур'}, {'chapter': 5, 'verse': 2, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُحِلُّواْ شَعَـٰٓئِرَ ٱللَّهِ وَلَا ٱلشَّهۡرَ ٱلۡحَرَامَ وَلَا ٱلۡهَدۡيَ وَلَا ٱلۡقَلَـٰٓئِدَ وَلَآ ءَآمِّينَ ٱلۡبَيۡتَ ٱلۡحَرَامَ يَبۡتَغُونَ فَضۡلٗا مِّن رَّبِّهِمۡ وَرِضۡوَٰنٗاۚ وَإِذَا حَلَلۡتُمۡ فَٱصۡطَادُواْۚ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ أَن صَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ أَن تَعۡتَدُواْۘ وَتَعَاوَنُواْ عَلَى ٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَلَا تَعَاوَنُواْ عَلَى ٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِۚ وَٱتَّقُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nЭй мўминлар, Аллоҳ буюрган удумларни бузишни, уруш ҳаром қилинган ойда жанг қилишни, Аллоҳ йўлида ҳадя этилган ҳайвонларни (ўлдиришни) ва уларга осиб қўйилган ҳадялик белгиларини (узиб ташлашни) ҳамда Парвардигорларидан фазлу марҳамат ва ризолик тилаб Байтул-Ҳаромни (Каъбани) мақсад қилиб кетаётган кишиларга (қарши жанг қилишни) ҳалол қилиб олмангиз! (Иҳромдан чиқиб) ҳалол бўлгач, ов қилаверингиз. (Сизларни Каъбадан тўсганлари сабабли) бирон қавмни ёмон кўришингиз ҳаддингиздан ошишингизга — адолатсизликка тортмасин — сабаб бўлмасин! Яхшилик ва тақво йўлида ҳамкорлик қилингиз, гуноҳ ва ҳаддан ошиш йўлида ҳамкорлик қилмангиз! Аллоҳдан қўрқингиз! Шубҳасиз, Аллоҳнинг азоби қаттиқдир'}, {'chapter': 5, 'verse': 3, 'text': 'حُرِّمَتۡ عَلَيۡكُمُ ٱلۡمَيۡتَةُ وَٱلدَّمُ وَلَحۡمُ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦ وَٱلۡمُنۡخَنِقَةُ وَٱلۡمَوۡقُوذَةُ وَٱلۡمُتَرَدِّيَةُ وَٱلنَّطِيحَةُ وَمَآ أَكَلَ ٱلسَّبُعُ إِلَّا مَا ذَكَّيۡتُمۡ وَمَا ذُبِحَ عَلَى ٱلنُّصُبِ وَأَن تَسۡتَقۡسِمُواْ بِٱلۡأَزۡلَٰمِۚ ذَٰلِكُمۡ فِسۡقٌۗ ٱلۡيَوۡمَ يَئِسَ ٱلَّذِينَ كَفَرُواْ مِن دِينِكُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِۚ ٱلۡيَوۡمَ أَكۡمَلۡتُ لَكُمۡ دِينَكُمۡ وَأَتۡمَمۡتُ عَلَيۡكُمۡ نِعۡمَتِي وَرَضِيتُ لَكُمُ ٱلۡإِسۡلَٰمَ دِينٗاۚ فَمَنِ ٱضۡطُرَّ فِي مَخۡمَصَةٍ غَيۡرَ مُتَجَانِفٖ لِّإِثۡمٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nСизларга ўлакса, қон, тўнғиз гўшти, Аллоҳдан бошқа бировнинг йўлида сўйилган нарса, бўғилиб ўлган, уриб ўлдирилган, баланд жойдан қулаб ўлган, (бошқа бирон ҳайвон билан) сузишиб ўлган ва йиртқич ҳайвон тишлаб ўлдирган жониворлар ҳаром қилинди. Магар (бу жониворларни жони чиқмасдан туриб) сўйиб юборишга улгуриб қолган бўлсангиз, ҳалолдир. Яна бутларга атаб сўйилган ҳайвонлар (гўштини истеъмол қилиш) ва (фол) чўплардан қисматингизни сўрашингиз (ҳам сизларга ҳаром қилинди). Зотан бу ишларингиз итоатсизликдир. Бугун энди кофир бўлган кимсаларнинг сизларни динингиздан (чиқаришдан) умидлари узилди. Бас, улардан қўрқмангиз, Мендан қўрқингиз! Бугун сизларга динингизни комил қилдим, неъматимни бенуқсон, тўкис қилиб бердим ва сизлар учун (фақат) Исломни дин қилиб танладим. Бас, кимки очарчиликда, гуноҳ томонга оғмаган ҳолида (мазкур ҳаром қилинган нарсаларни ейишга) мажбур бўлса (Аллоҳ кечирар). Зеро, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 5, 'verse': 4, 'text': 'يَسۡـَٔلُونَكَ مَاذَآ أُحِلَّ لَهُمۡۖ قُلۡ أُحِلَّ لَكُمُ ٱلطَّيِّبَٰتُ وَمَا عَلَّمۡتُم مِّنَ ٱلۡجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ ٱللَّهُۖ فَكُلُواْ مِمَّآ أَمۡسَكۡنَ عَلَيۡكُمۡ وَٱذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَيۡهِۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мўминлар) Сиздан ўзлари учун нималар ҳалол қилинганини сўрайдилар. Айтинг: «Сизлар учун барча покиза нарсалар ва яна сизларнинг таълимингизни олган жониворларнинг — Аллоҳ сизларга билдирган нарсалардан билдириб қўлга ўргатган жониворларнинг (тутиб келтирган овлари) ҳалол қилинди. Бас, улар сизлар учун ушлаб келтирган нарсаларни еяверинглар ва (уларни овга қўйиб юбораётганларингда) Аллоҳнинг номини зикр қилинглар! Аллоҳдан қўрқинглар! Албатта, Аллоҳ жуда тез ҳисоб-китоб қилгувчи Зотдир'}, {'chapter': 5, 'verse': 5, 'text': 'ٱلۡيَوۡمَ أُحِلَّ لَكُمُ ٱلطَّيِّبَٰتُۖ وَطَعَامُ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ حِلّٞ لَّكُمۡ وَطَعَامُكُمۡ حِلّٞ لَّهُمۡۖ وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلۡمُؤۡمِنَٰتِ وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّ مُحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَ وَلَا مُتَّخِذِيٓ أَخۡدَانٖۗ وَمَن يَكۡفُرۡ بِٱلۡإِيمَٰنِ فَقَدۡ حَبِطَ عَمَلُهُۥ وَهُوَ فِي ٱلۡأٓخِرَةِ مِنَ ٱلۡخَٰسِرِينَ\nБугун сизлар учун барча покиза нарсалар ҳалол қилинди. Китоб берилган кимсаларнинг таомлари сизлар учун ҳалолдир ва сизларнинг таомларингиз улар учун ҳалолдир. Қачонки зино қилмай ва яширин ўйнаш тутмай маҳрларини бериб уйлансангиз, сизлар учун мўминалар орасидан ўзларини ҳаромдан сақлаган аёллар ва сизлардан илгари Китоб берилган кимсалардан бўлган ўзларини ҳаромдан сақлаган аёллар ҳам ҳалолдир. Кимки иймонидан қайтса, унинг қилган амали беҳуда кетиши аниқдир ва у Охиратда зиён кўргувчилар тоифасидандир'}, {'chapter': 5, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا قُمۡتُمۡ إِلَى ٱلصَّلَوٰةِ فَٱغۡسِلُواْ وُجُوهَكُمۡ وَأَيۡدِيَكُمۡ إِلَى ٱلۡمَرَافِقِ وَٱمۡسَحُواْ بِرُءُوسِكُمۡ وَأَرۡجُلَكُمۡ إِلَى ٱلۡكَعۡبَيۡنِۚ وَإِن كُنتُمۡ جُنُبٗا فَٱطَّهَّرُواْۚ وَإِن كُنتُم مَّرۡضَىٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآءَ أَحَدٞ مِّنكُم مِّنَ ٱلۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ ٱلنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَٱمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُم مِّنۡهُۚ مَا يُرِيدُ ٱللَّهُ لِيَجۡعَلَ عَلَيۡكُم مِّنۡ حَرَجٖ وَلَٰكِن يُرِيدُ لِيُطَهِّرَكُمۡ وَلِيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\nЭй мўминлар, намозга турганингизда юзларингизни ҳамда қўлларингизни чиғаноқларигача ювингиз, бошларингизга масҳ тортингиз (яъни, нам қўлларингиз билан силангиз) ва оёқларингизни ошиқларигача ювингиз! Агар жунуб бўлсаларингиз, чўмилингиз! Агар бемор ё мусофир бўлсангиз ёки сизлардан биров ҳожатхонадан чиққан бўлса ёхуд хотинларингизга қўшилган бўлсаларингиз ва (мазкур ҳолатларда) сув топа олмасангиз, покиза тупроқ билан таяммум қилингиз (яъни, юзингиз ва қўлларингизни покиза тупроқ билан силангиз). Аллоҳ сизларга машаққат қилмоқни истамайди, балки сизларни поклашни ва шукр қилишларингиз учун сизларга Ўз неъматини комил қилиб беришни истайди'}, {'chapter': 5, 'verse': 7, 'text': 'وَٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ وَمِيثَٰقَهُ ٱلَّذِي وَاثَقَكُم بِهِۦٓ إِذۡ قُلۡتُمۡ سَمِعۡنَا وَأَطَعۡنَاۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nАллоҳнинг сизларга берган неъматини ва: «Эшитдик ва итоат қилдик», деган пайтингизда сизлар билан боғлаган аҳд-паймонини эслангиз! Аллоҳдан қўрқингиз! Албатта, Аллоҳ дилларни эгаллаган (сирларни) билгувчидир'}, {'chapter': 5, 'verse': 8, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُواْ قَوَّـٰمِينَ لِلَّهِ شُهَدَآءَ بِٱلۡقِسۡطِۖ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ عَلَىٰٓ أَلَّا تَعۡدِلُواْۚ ٱعۡدِلُواْ هُوَ أَقۡرَبُ لِلتَّقۡوَىٰۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nЭй мўминлар, Аллоҳнинг Ўзи учун ҳақ йўлни тутгувчи, адолат билан шаҳодат-гувоҳлик бергувчи бўлингиз! Бирон қавмни ёмон кўришингиз сизларни адолат қилмасликка тортмасин! Адолат қилингиз! Шу тақвога яқинроқдир. Аллоҳдан қўрқингиз! Албатта Аллоҳ, қилаётган амалларингиздан хабардордир'}, {'chapter': 5, 'verse': 9, 'text': 'وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٞ\nАллоҳ иймон келтирган ва яхши амаллар қилган зотлар учун мағфират ва улуғ ажр бўлишини ваъда қилди'}, {'chapter': 5, 'verse': 10, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nКофир бўлган ва Бизнинг оятларимизни ёлғон деган кимсалар эса жаҳаннам эгаларидир'}, {'chapter': 5, 'verse': 11, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ هَمَّ قَوۡمٌ أَن يَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ فَكَفَّ أَيۡدِيَهُمۡ عَنكُمۡۖ وَٱتَّقُواْ ٱللَّهَۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nЭй мўминлар, Аллоҳнинг сизларга берган неъматини — бир қавм сизларга қўл кўтаришни (яъни, ҳалок қилишни) қасд қилганида уларнинг қўлларини сизлардан қайтарганини эслангиз! Аллоҳдан қўрқингиз! Иймонли кишилар ёлғиз Аллоҳгагина суянсинлар'}, {'chapter': 5, 'verse': 12, 'text': '۞وَلَقَدۡ أَخَذَ ٱللَّهُ مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَبَعَثۡنَا مِنۡهُمُ ٱثۡنَيۡ عَشَرَ نَقِيبٗاۖ وَقَالَ ٱللَّهُ إِنِّي مَعَكُمۡۖ لَئِنۡ أَقَمۡتُمُ ٱلصَّلَوٰةَ وَءَاتَيۡتُمُ ٱلزَّكَوٰةَ وَءَامَنتُم بِرُسُلِي وَعَزَّرۡتُمُوهُمۡ وَأَقۡرَضۡتُمُ ٱللَّهَ قَرۡضًا حَسَنٗا لَّأُكَفِّرَنَّ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَلَأُدۡخِلَنَّكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ فَمَن كَفَرَ بَعۡدَ ذَٰلِكَ مِنكُمۡ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ\nАллоҳ Бани Исроилнинг аҳд-паймонини олди ва ўзларидан ўн икки бошлиқни (Мусо билан бирга, Шом заминини маскан тутган кофирларга қарши жанг қилиш учун) жўнатиб, айтди: «Албатта Мен сизлар билан биргаман. Қасамки, агар намозни тўкис адо қилсангиз, закотни (ҳақдорларга) ато қилсангиз, пайғамбарларимга иймон келтириб, ёрдам берсангиз ва (бева-бечораларга яхшилик — эҳсон қилиш билан) Аллоҳга қарзи ҳасана берсангиз (яхшилик ва Унинг йўлида инфоқ-эҳсон қилсангиз), албатта ёмонликларингизни (яъни, қилган гуноҳларингизни) ўчирурман ва албатта сизларни остидан дарёлар оқиб турадиган жаннатларга дохил қилурман. Мана шу аҳд-паймондан кейин сизлардан кимки кофир бўлса, унинг Тўғри Йўлдан озгани аниқдир»'}, {'chapter': 5, 'verse': 13, 'text': 'فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ لَعَنَّـٰهُمۡ وَجَعَلۡنَا قُلُوبَهُمۡ قَٰسِيَةٗۖ يُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَآئِنَةٖ مِّنۡهُمۡ إِلَّا قَلِيلٗا مِّنۡهُمۡۖ فَٱعۡفُ عَنۡهُمۡ وَٱصۡفَحۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُحۡسِنِينَ\nСўнг аҳд-паймонларини бузганлари сабабли уларни лаънатладик ва тошбағир қилиб қўйдик. Улар (Тавротдаги Муҳаммад алайҳис-салоту вас-саломнинг пайғамбар бўлишлари ҳақидаги) сўзларини ўз ўринларидан ўзгартирадилар, ўзларига эслатма қилиб берилган нарсалардан эса (кўп) ҳиссасини унутиб юборганлар. (Эй Муҳаммад алайҳис-салоту вас-салом), сиз мудом улар тарафидан қилинган бирон хиёнат устидан чиқасиз. Магар улардан (Исломни қабул қилган) озчилик кишиларгина хиёнат қилмайдилар, холос. Бас, уларни афв этиб, кечириб юбораверинг. Албатта, Аллоҳ яхшилик қилгувчиларни севади'}, {'chapter': 5, 'verse': 14, 'text': 'وَمِنَ ٱلَّذِينَ قَالُوٓاْ إِنَّا نَصَٰرَىٰٓ أَخَذۡنَا مِيثَٰقَهُمۡ فَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦ فَأَغۡرَيۡنَا بَيۡنَهُمُ ٱلۡعَدَاوَةَ وَٱلۡبَغۡضَآءَ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۚ وَسَوۡفَ يُنَبِّئُهُمُ ٱللَّهُ بِمَا كَانُواْ يَصۡنَعُونَ\n«Бизлар насронийлармиз», деган кимсалардан ҳам аҳд-паймонларини олдик. Сўнг ўзларига эслатма қилиб берилган нарсалардан (кўп) ҳиссасини унутдилар. Бас, уларнинг ўрталарида то Қиёмат Кунигача буғзу адоватни авж олдирдик. Яқинда Аллоҳ уларга қилиб ўтган «ҳунар»ларининг хабарини беражак'}, {'chapter': 5, 'verse': 15, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ قَدۡ جَآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ كَثِيرٗا مِّمَّا كُنتُمۡ تُخۡفُونَ مِنَ ٱلۡكِتَٰبِ وَيَعۡفُواْ عَن كَثِيرٖۚ قَدۡ جَآءَكُم مِّنَ ٱللَّهِ نُورٞ وَكِتَٰبٞ مُّبِينٞ\nЭй аҳли китоб, мана, элчимиз (Муҳаммад алайҳис-салоту вас-салом) сизлар китобингиз (Таврот, Инжил) орасидан яшириб келган (у зотнинг пайғамбар бўлишлари ва яна бошқа зарур масалалар хусусидаги) кўп нарсаларни сизларга баён этган ҳолда, (фақат сизларнинг сирларингизни очиб, шарманда қилиш учунгина ишлайдиган) кўп нарсаларни тарк этган ҳолда келди. Сизларга Аллоҳ тарафидан Нур-Очиқ Китоб келди'}, {'chapter': 5, 'verse': 16, 'text': 'يَهۡدِي بِهِ ٱللَّهُ مَنِ ٱتَّبَعَ رِضۡوَٰنَهُۥ سُبُلَ ٱلسَّلَٰمِ وَيُخۡرِجُهُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ بِإِذۡنِهِۦ وَيَهۡدِيهِمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳ у сабабли ўзининг ризолигига эргашган зотларни нажот йўлларига ҳидоят қилур ва Ўзи изну иродаси билан уларни зулматлардан нурга чиқарур ва уларни Тўғри Йўлга ҳидоят қилур'}, {'chapter': 5, 'verse': 17, 'text': 'لَّقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَۚ قُلۡ فَمَن يَمۡلِكُ مِنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ أَن يُهۡلِكَ ٱلۡمَسِيحَ ٱبۡنَ مَرۡيَمَ وَأُمَّهُۥ وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗاۗ وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۚ يَخۡلُقُ مَا يَشَآءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\n«Аллоҳ – Масиҳ бинни Марямдир», деган кимсаларнинг кофир бўлганликлари аниқдир. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар Аллоҳ Масиҳ бинни Марямни, унинг онасини ва Ердаги барча кишиларни ҳалок этишни истаса, ким Аллоҳ тарафидан бўлган нарсани (балони) қайтаришга эга бўла олади?!» Осмонлар, Ер ва уларнинг орасидаги бор нарсалар Аллоҳнинг мулкидир. У ўзи истаган нарсасини Ўзи истаган суратда яратур. Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 5, 'verse': 18, 'text': 'وَقَالَتِ ٱلۡيَهُودُ وَٱلنَّصَٰرَىٰ نَحۡنُ أَبۡنَـٰٓؤُاْ ٱللَّهِ وَأَحِبَّـٰٓؤُهُۥۚ قُلۡ فَلِمَ يُعَذِّبُكُم بِذُنُوبِكُمۖ بَلۡ أَنتُم بَشَرٞ مِّمَّنۡ خَلَقَۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ\nЯҳудий ва насронийлар: «Биз Аллоҳнинг суюкли болаларимиз», дедилар. Айтинг: «У ҳолда нега сизларни гуноҳларингиз сабабли азоблайди?! Йўқ! Сизлар ҳам У яратган барча одамлар каби одамсизлар. У зот Ўзи истаган кишисини мағфират қилур, истаган кишисига азоб берур». Осмонлар, Ер ва уларнинг орасидаги бор нарсалар Аллоҳнинг мулкидир. Фақат унинг Ўзига қайтилур'}, {'chapter': 5, 'verse': 19, 'text': 'يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ قَدۡ جَآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ عَلَىٰ فَتۡرَةٖ مِّنَ ٱلرُّسُلِ أَن تَقُولُواْ مَا جَآءَنَا مِنۢ بَشِيرٖ وَلَا نَذِيرٖۖ فَقَدۡ جَآءَكُم بَشِيرٞ وَنَذِيرٞۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЭй аҳли китоб, мана, элчимиз (Муҳаммад алайҳис-салоту вас-салом) аввалги пайғамбарлардан анча замон ўтиб: «Бизга на бирон (жаннат ҳақида) хушхабар бергувчи ва на бирон (дўзах азобидан) қўрқитгувчи келмаган», демасликларингиз учун сизларга дин ҳукмларини баён қилган ҳолда келди. Мана, сизларга хушхабар бергувчи ва (дўзахдан) қўрқитгувчи бўлган зот келди. Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 5, 'verse': 20, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ جَعَلَ فِيكُمۡ أَنۢبِيَآءَ وَجَعَلَكُم مُّلُوكٗا وَءَاتَىٰكُم مَّا لَمۡ يُؤۡتِ أَحَدٗا مِّنَ ٱلۡعَٰلَمِينَ\nЭсланг (эй Муҳаммад алайҳис-салоту вас-салом), Мусо қавмига: «Эй қавмим, Аллоҳнинг сизларга берган неъматини — орангиздан пайғамбарлар чиқарганини, яна сизларни мулку давлат эгалари қилиб, барча оламлардан бирон кишига бермаган инъомларини сизларга ато этганини ёдга олингиз'}, {'chapter': 5, 'verse': 21, 'text': 'يَٰقَوۡمِ ٱدۡخُلُواْ ٱلۡأَرۡضَ ٱلۡمُقَدَّسَةَ ٱلَّتِي كَتَبَ ٱللَّهُ لَكُمۡ وَلَا تَرۡتَدُّواْ عَلَىٰٓ أَدۡبَارِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ\nЭй қавмим, Аллоҳ сизлар учун ёзиб қўймиш бу муқаддас Ерга (шаҳарга) кирингиз! Ортларингизга қайтиб кетмангиз, акс ҳолда зиён кўргувчиларга айланиб қолурсиз», деди'}, {'chapter': 5, 'verse': 22, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِنَّ فِيهَا قَوۡمٗا جَبَّارِينَ وَإِنَّا لَن نَّدۡخُلَهَا حَتَّىٰ يَخۡرُجُواْ مِنۡهَا فَإِن يَخۡرُجُواْ مِنۡهَا فَإِنَّا دَٰخِلُونَ\nЎшанда улар: «Ё Мусо, у Ерда зўравон одамлар бор. То улар чиқмагунларича биз у Ерга зинҳор кира олмагаймиз. Агар улар чиқиб кетсаларгина биз кирурмиз», дедилар'}, {'chapter': 5, 'verse': 23, 'text': 'قَالَ رَجُلَانِ مِنَ ٱلَّذِينَ يَخَافُونَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِمَا ٱدۡخُلُواْ عَلَيۡهِمُ ٱلۡبَابَ فَإِذَا دَخَلۡتُمُوهُ فَإِنَّكُمۡ غَٰلِبُونَۚ وَعَلَى ٱللَّهِ فَتَوَكَّلُوٓاْ إِن كُنتُم مُّؤۡمِنِينَ\n(Шунда ўн икки бошлиқ орасидан) Аллоҳдан қўрқадиган ва унинг инъом-марҳаматига муяссар бўлган икки киши: «Уларнинг устига дарвозадан тўсатдан кирингиз, агар унга кириб олсангизлар, бас, шубҳасиз, ғолиб бўлурсиз. Агар мўмин бўлсангизлар, ёлғиз Аллоҳгагина суянинглар!», дедилар'}, {'chapter': 5, 'verse': 24, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِنَّا لَن نَّدۡخُلَهَآ أَبَدٗا مَّا دَامُواْ فِيهَا فَٱذۡهَبۡ أَنتَ وَرَبُّكَ فَقَٰتِلَآ إِنَّا هَٰهُنَا قَٰعِدُونَ\n(Қавм) айтди: «Ё Мусо, модомики улар шу жойда экан, биз ҳеч қачон кира олмагаймиз. Бас, боргин, сен ўзинг ва Парвардигоринг улар билан уришаверинглар. Биз эса мана шу Ерда ўтириб кутурмиз»'}, {'chapter': 5, 'verse': 25, 'text': 'قَالَ رَبِّ إِنِّي لَآ أَمۡلِكُ إِلَّا نَفۡسِي وَأَخِيۖ فَٱفۡرُقۡ بَيۡنَنَا وَبَيۡنَ ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ\nМусо айтди: «Парвардигорим, мен фақат ўзимга ва биродарим (Ҳорунга) эгаман, холос. Бас, Ўзинг биз билан бу итоатсиз қавмнинг орасини ажратгин»'}, {'chapter': 5, 'verse': 26, 'text': 'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيۡهِمۡۛ أَرۡبَعِينَ سَنَةٗۛ يَتِيهُونَ فِي ٱلۡأَرۡضِۚ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ\nАллоҳ деди: «Энди қирқ йил мобайнида бу Ер улар учун ҳаромдир. Улар Ерда (саҳроларда) адашиб-улоқиб юрурлар. Сен бу итоатсиз қавм (қилмиши)дан маҳзун бўлмагин»'}, {'chapter': 5, 'verse': 27, 'text': '۞وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ ٱبۡنَيۡ ءَادَمَ بِٱلۡحَقِّ إِذۡ قَرَّبَا قُرۡبَانٗا فَتُقُبِّلَ مِنۡ أَحَدِهِمَا وَلَمۡ يُتَقَبَّلۡ مِنَ ٱلۡأٓخَرِ قَالَ لَأَقۡتُلَنَّكَۖ قَالَ إِنَّمَا يَتَقَبَّلُ ٱللَّهُ مِنَ ٱلۡمُتَّقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларга Одамнинг икки ўғли ҳақидаги хабарни ҳаққирост тиловат қилинг. Ўшанда у иккови қурбонлик қилганларида (қурбонликлари) бировларидан қабул қилинган, иккинчиларидан қабул қилинмаган эди. Шунда у «Қасамки, сени ўлдирурман», деганида, биродари айтди: «Аллоҳ фақат тақводорлардангина (қурбонликларини) қабул қилур»'}, {'chapter': 5, 'verse': 28, 'text': 'لَئِنۢ بَسَطتَ إِلَيَّ يَدَكَ لِتَقۡتُلَنِي مَآ أَنَا۠ بِبَاسِطٖ يَدِيَ إِلَيۡكَ لِأَقۡتُلَكَۖ إِنِّيٓ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَٰلَمِينَ\nҚасамки, агар сен мени ўлдириш учун қўл чўзсанг, мен сени ўлдириш учун қўл чўзгувчи эмасман. Чунки мен бутун оламлар Парвардигори бўлмиш Аллоҳдан қўрқаман'}, {'chapter': 5, 'verse': 29, 'text': 'إِنِّيٓ أُرِيدُ أَن تَبُوٓأَ بِإِثۡمِي وَإِثۡمِكَ فَتَكُونَ مِنۡ أَصۡحَٰبِ ٱلنَّارِۚ وَذَٰلِكَ جَزَـٰٓؤُاْ ٱلظَّـٰلِمِينَ\nМен сени менинг гуноҳим ҳамда ўзингнинг гуноҳинг билан қайтиб, дўзах эгаларидан бўлиб қолишингни истайман. Золимларнинг жазоси шудир'}, {'chapter': 5, 'verse': 30, 'text': 'فَطَوَّعَتۡ لَهُۥ نَفۡسُهُۥ قَتۡلَ أَخِيهِ فَقَتَلَهُۥ فَأَصۡبَحَ مِنَ ٱلۡخَٰسِرِينَ\nБас, нафси унга ўз биродарини ўлдиришни чиройли қилиб кўрсатиб (Қобил) уни ўлдирди ва зиён кўргувчилардан бўлиб қолди'}, {'chapter': 5, 'verse': 31, 'text': 'فَبَعَثَ ٱللَّهُ غُرَابٗا يَبۡحَثُ فِي ٱلۡأَرۡضِ لِيُرِيَهُۥ كَيۡفَ يُوَٰرِي سَوۡءَةَ أَخِيهِۚ قَالَ يَٰوَيۡلَتَىٰٓ أَعَجَزۡتُ أَنۡ أَكُونَ مِثۡلَ هَٰذَا ٱلۡغُرَابِ فَأُوَٰرِيَ سَوۡءَةَ أَخِيۖ فَأَصۡبَحَ مِنَ ٱلنَّـٰدِمِينَ\nСўнгра Аллоҳ унга биродарининг мурдасини қандай кўмишни кўрсатиш учун Ер титадиган бир қарға юборди. У: «Менга ўлим бўлсин, мана шу қарғачалик бўла олмадимми — биродаримнинг мурдасини ўзим кўма олмадимми?!», деб надомат қилгувчилардан бўлиб қолди'}, {'chapter': 5, 'verse': 32, 'text': 'مِنۡ أَجۡلِ ذَٰلِكَ كَتَبۡنَا عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَنَّهُۥ مَن قَتَلَ نَفۡسَۢا بِغَيۡرِ نَفۡسٍ أَوۡ فَسَادٖ فِي ٱلۡأَرۡضِ فَكَأَنَّمَا قَتَلَ ٱلنَّاسَ جَمِيعٗا وَمَنۡ أَحۡيَاهَا فَكَأَنَّمَآ أَحۡيَا ٱلنَّاسَ جَمِيعٗاۚ وَلَقَدۡ جَآءَتۡهُمۡ رُسُلُنَا بِٱلۡبَيِّنَٰتِ ثُمَّ إِنَّ كَثِيرٗا مِّنۡهُم بَعۡدَ ذَٰلِكَ فِي ٱلۡأَرۡضِ لَمُسۡرِفُونَ\nАна ўша (фожеа) сабабли Бани Исроил зиммасига (шундай фармонни) ёзиб қўйдик: кимки бирон жонни ўлдирмаган ва Ерда бузғунчилик қилиб юрмаган одамни ўлдирса, демак, у гуё барча одамларни ўлдирибди ва кимки унга ҳаёт ато этса (яъни, ўлдиришдан бош тортса), демак, у гўё барча одамларга ҳаёт берибди. Уларга (Бани Исроилга) пайғамбарларимиз мана шундай ҳужжатлар келтирдилар. Шундан кейин ҳам улардан кўплари Ер юзида (қон тўкиш билан) ҳаддан ошиб юргувчидирлар'}, {'chapter': 5, 'verse': 33, 'text': 'إِنَّمَا جَزَـٰٓؤُاْ ٱلَّذِينَ يُحَارِبُونَ ٱللَّهَ وَرَسُولَهُۥ وَيَسۡعَوۡنَ فِي ٱلۡأَرۡضِ فَسَادًا أَن يُقَتَّلُوٓاْ أَوۡ يُصَلَّبُوٓاْ أَوۡ تُقَطَّعَ أَيۡدِيهِمۡ وَأَرۡجُلُهُم مِّنۡ خِلَٰفٍ أَوۡ يُنفَوۡاْ مِنَ ٱلۡأَرۡضِۚ ذَٰلِكَ لَهُمۡ خِزۡيٞ فِي ٱلدُّنۡيَاۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٌ\nАлбатта, Аллоҳ ва пайғамбарига қарши урушадиган ва Ерда бузғунчилик қилиш ҳаракатида юрадиган кимсаларнинг жазоси — ўлдирилиш ё дорга осилиш, ёки оёқ-қўллари тескарисига (яъни, ўнг қўл ва чап оёқ ёки чап қўл билан ўнг оёқ) кесилиши, ёхуд ўз Ерларидан сургун қилинишларидир. Бу жазо улар учун бу дунёда расволик — шармандалик бўлур. Охиратда эса улар учун улуғ азоб бордир'}, {'chapter': 5, 'verse': 34, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ مِن قَبۡلِ أَن تَقۡدِرُواْ عَلَيۡهِمۡۖ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nМагар сизлар қодир бўлишингиздан (қўлга тушишларидан) илгари тавба қилган кишилар бўлса, билингизки, Аллоҳ мағфиратли, меҳрибон Зотдир'}, {'chapter': 5, 'verse': 35, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَٱبۡتَغُوٓاْ إِلَيۡهِ ٱلۡوَسِيلَةَ وَجَٰهِدُواْ فِي سَبِيلِهِۦ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй мўминлар, Аллоҳдан қўрқингиз ва Унга яқин бўлиш йўлини излангиз ҳамда Унинг йўлида жиҳод қилингиз, шунда шояд нажот топурсиз'}, {'chapter': 5, 'verse': 36, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ لَوۡ أَنَّ لَهُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لِيَفۡتَدُواْ بِهِۦ مِنۡ عَذَابِ يَوۡمِ ٱلۡقِيَٰمَةِ مَا تُقُبِّلَ مِنۡهُمۡۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ\nКофир бўлган кимсалар эса Ердаги бор нарса икки баробар бўлиб уларнинг мулкига айланиб, ўшани Қиёмат Кунидаги азобдан (нажот топиш учун) сарф қилмоқчи бўлсалар, улардан қабул қилинмас. Улар учун аламли азоб бордир'}, {'chapter': 5, 'verse': 37, 'text': 'يُرِيدُونَ أَن يَخۡرُجُواْ مِنَ ٱلنَّارِ وَمَا هُم بِخَٰرِجِينَ مِنۡهَاۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ\nУлар дўзахдан чиқмоқчи бўладилар-у, ҳеч ундан чиқувчи бўла олмайдилар — улар учун доимий азоб бордир'}, {'chapter': 5, 'verse': 38, 'text': 'وَٱلسَّارِقُ وَٱلسَّارِقَةُ فَٱقۡطَعُوٓاْ أَيۡدِيَهُمَا جَزَآءَۢ بِمَا كَسَبَا نَكَٰلٗا مِّنَ ٱللَّهِۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ\nЎғри эркакни ҳам, ўғри аёлни ҳам қўлларини кесинглар! (Токи бу) уларнинг қилмишларига жазо бўлсин, Аллоҳ томонидан берилган азоб бўлсин. Аллоҳ қудрат ва ҳикмат Эгасидир'}, {'chapter': 5, 'verse': 39, 'text': 'فَمَن تَابَ مِنۢ بَعۡدِ ظُلۡمِهِۦ وَأَصۡلَحَ فَإِنَّ ٱللَّهَ يَتُوبُ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nАммо ким бундай (ўғрилик билан қилган) жабру зулмидан кейин тавба қилиб, ўзини тузатса, албатта Аллоҳ унинг тавбасини қабул қилар. Албатта, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 5, 'verse': 40, 'text': 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ يُعَذِّبُ مَن يَشَآءُ وَيَغۡفِرُ لِمَن يَشَآءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ахир сиз Осмонлар ва Ер Аллоҳнинг мулки эканини, У зот Ўзи истаган кишисини азоблаб, истаган кишисини мағфират қилишини билмадингизми? Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 5, 'verse': 41, 'text': '۞يَـٰٓأَيُّهَا ٱلرَّسُولُ لَا يَحۡزُنكَ ٱلَّذِينَ يُسَٰرِعُونَ فِي ٱلۡكُفۡرِ مِنَ ٱلَّذِينَ قَالُوٓاْ ءَامَنَّا بِأَفۡوَٰهِهِمۡ وَلَمۡ تُؤۡمِن قُلُوبُهُمۡۛ وَمِنَ ٱلَّذِينَ هَادُواْۛ سَمَّـٰعُونَ لِلۡكَذِبِ سَمَّـٰعُونَ لِقَوۡمٍ ءَاخَرِينَ لَمۡ يَأۡتُوكَۖ يُحَرِّفُونَ ٱلۡكَلِمَ مِنۢ بَعۡدِ مَوَاضِعِهِۦۖ يَقُولُونَ إِنۡ أُوتِيتُمۡ هَٰذَا فَخُذُوهُ وَإِن لَّمۡ تُؤۡتَوۡهُ فَٱحۡذَرُواْۚ وَمَن يُرِدِ ٱللَّهُ فِتۡنَتَهُۥ فَلَن تَمۡلِكَ لَهُۥ مِنَ ٱللَّهِ شَيۡـًٔاۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ لَمۡ يُرِدِ ٱللَّهُ أَن يُطَهِّرَ قُلُوبَهُمۡۚ لَهُمۡ فِي ٱلدُّنۡيَا خِزۡيٞۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٞ\nЭй пайғамбар, оғизларида: «Иймон келтирдик», деган, аммо диллари иймон келтирмаган (мунофиқлардан) ва яҳудийлардан бўлган, куфр томонга қараб чопаётган кимсалар сизни маҳзун қилмасин. Улар ёлғонга қулоқ бергувчи ва сизнинг олдингизга келмаган, сўзларни ўз ўринларига қўйилгандан кейин ўзгартирадиган бошқа қавмга (яъни, яҳудийларга) қулоқ бергувчи кимсалардир. (Яҳудийлар): «Агар (Муҳаммад тарафидан) сизларга мана шу ҳукм берилса, олинглар, бўлмаса (яъни, бошқа бир ҳукм айтилса), эҳтиёт бўлинглар — олманглар, дейишади. Кимники Аллоҳ ўзи адаштиришни истаса, бас, сиз унинг учун Аллоҳ томонидан ҳеч нарса қила олмассиз. Улар Аллоҳ кўнгилларини поклашни истамаган кимсалардир. Улар учун дунёда расволик, Охиратда эса улуғ азоб бордир'}, {'chapter': 5, 'verse': 42, 'text': 'سَمَّـٰعُونَ لِلۡكَذِبِ أَكَّـٰلُونَ لِلسُّحۡتِۚ فَإِن جَآءُوكَ فَٱحۡكُم بَيۡنَهُمۡ أَوۡ أَعۡرِضۡ عَنۡهُمۡۖ وَإِن تُعۡرِضۡ عَنۡهُمۡ فَلَن يَضُرُّوكَ شَيۡـٔٗاۖ وَإِنۡ حَكَمۡتَ فَٱحۡكُم بَيۡنَهُم بِٱلۡقِسۡطِۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ\nУлар ёлғонга қулоқ берувчи ҳаромхўр кимсалардир. Бас, агар Сизга келсалар, (хоҳласангиз) ўрталарида ҳакамлик қилинг, (хоҳласангиз) улардан юз ўгиринг. Агар улардан юз ўгирсангиз, зинҳор Сизга бирон зарар етказа олмаслар. Агар ҳукм қилсангиз, ўрталарида адолат билан ҳукм қилинг. Албатта, Аллоҳ адолат қилгувчиларни севади'}, {'chapter': 5, 'verse': 43, 'text': 'وَكَيۡفَ يُحَكِّمُونَكَ وَعِندَهُمُ ٱلتَّوۡرَىٰةُ فِيهَا حُكۡمُ ٱللَّهِ ثُمَّ يَتَوَلَّوۡنَ مِنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَـٰٓئِكَ بِٱلۡمُؤۡمِنِينَ\nОлдиларида Таврот, унда Аллоҳнинг ҳукми бўлгани ҳолда Сиздан ҳукм сўраб, сўнгра (яъни, ҳукм қилганингиздан кейин қандай) юз ўгириб кетадилар-а? Улар ҳеч мўмин эмаслар'}, {'chapter': 5, 'verse': 44, 'text': 'إِنَّآ أَنزَلۡنَا ٱلتَّوۡرَىٰةَ فِيهَا هُدٗى وَنُورٞۚ يَحۡكُمُ بِهَا ٱلنَّبِيُّونَ ٱلَّذِينَ أَسۡلَمُواْ لِلَّذِينَ هَادُواْ وَٱلرَّبَّـٰنِيُّونَ وَٱلۡأَحۡبَارُ بِمَا ٱسۡتُحۡفِظُواْ مِن كِتَٰبِ ٱللَّهِ وَكَانُواْ عَلَيۡهِ شُهَدَآءَۚ فَلَا تَخۡشَوُاْ ٱلنَّاسَ وَٱخۡشَوۡنِ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗاۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰفِرُونَ\nАлбатта, Биз Тавротни ҳидоят ва нурни ўз ичига олган ҳолда нозил қилганмиз. Аллоҳга бўйинсунувчи бўлган пайғамбарлар, илоҳий билим эгалари ва донишмандлар ўзларига Аллоҳнинг китоби омонат қўйилгани сабабли яҳудийларга у (яъни, Таврот ҳукмлари) билан ҳукм қиладилар. Улар бу китоб устида гувоҳдирлар. Бас, (эй яҳудий уламолар), одамлардан қўрқмангиз, Мендан қўрқингиз ва Менинг оятларимни қиймати оз нарсаларга алмаштирмангиз! Кимда-ким Аллоҳ нозил қилган дин билан ҳукм қилмас экан, бас, улар кофирлардир'}, {'chapter': 5, 'verse': 45, 'text': 'وَكَتَبۡنَا عَلَيۡهِمۡ فِيهَآ أَنَّ ٱلنَّفۡسَ بِٱلنَّفۡسِ وَٱلۡعَيۡنَ بِٱلۡعَيۡنِ وَٱلۡأَنفَ بِٱلۡأَنفِ وَٱلۡأُذُنَ بِٱلۡأُذُنِ وَٱلسِّنَّ بِٱلسِّنِّ وَٱلۡجُرُوحَ قِصَاصٞۚ فَمَن تَصَدَّقَ بِهِۦ فَهُوَ كَفَّارَةٞ لَّهُۥۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nУнда (яъни, Тавротда) яҳудийларга жонга жон, кўзга кўз, бурунга бурун, қулоққа қулоқ, тишга тиш ва (яна бошқа барча) жароҳатларга ҳам қасос олинади, деб ёзиб қўйдик. Энди ким уни (яъни, қасосни) кечиб юборса, ўзи учун каффорат (яъни, гуноҳларни ўчириб юборувчи) бўлур. Кимда-ким Аллоҳ нозил қилган дин билан ҳукм қилмас экан, бас, улар золимлардир'}, {'chapter': 5, 'verse': 46, 'text': 'وَقَفَّيۡنَا عَلَىٰٓ ءَاثَٰرِهِم بِعِيسَى ٱبۡنِ مَرۡيَمَ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلتَّوۡرَىٰةِۖ وَءَاتَيۡنَٰهُ ٱلۡإِنجِيلَ فِيهِ هُدٗى وَنُورٞ وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلتَّوۡرَىٰةِ وَهُدٗى وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ\nУларнинг изларидан ўзидан олдинги Тавротни тасдиқловчи бўлган Ийсо бинни Марямни эргаштирдик ва унга ҳидоят ва нурни ўз ичига олган, ўзидан олдинги Тавротни тасдиқлагувчи ва тақводорлар учун ҳидоят, панд-насиҳат бўлган Инжилни бердик'}, {'chapter': 5, 'verse': 47, 'text': 'وَلۡيَحۡكُمۡ أَهۡلُ ٱلۡإِنجِيلِ بِمَآ أَنزَلَ ٱللَّهُ فِيهِۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nҲамда (буюрдик) «Инжил аҳли унда Аллоҳ нозил қилган нарсалар билан ҳукм қилсинлар!» Кимда-ким Аллоҳ нозил этган дин билан ҳукм этмас экан, улар фосиқлар — итоатсиз кимсалардир'}, {'chapter': 5, 'verse': 48, 'text': 'وَأَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلۡكِتَٰبِ وَمُهَيۡمِنًا عَلَيۡهِۖ فَٱحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ ٱللَّهُۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ عَمَّا جَآءَكَ مِنَ ٱلۡحَقِّۚ لِكُلّٖ جَعَلۡنَا مِنكُمۡ شِرۡعَةٗ وَمِنۡهَاجٗاۚ وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۖ فَٱسۡتَبِقُواْ ٱلۡخَيۡرَٰتِۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nСизга эса (эй Муҳаммад алайҳис-салоту вас-салом), ўзидан олдинги китоб(ларни) тасдиқлагувчи ва у (китоблар) устида гувоҳ бўлган бу Китобни ҳаққирост нозил қилдик. Бас, одамлар ўртасида Аллоҳ нозил қилган нарса билан ҳукм қилинг ва Сизга келган Ҳақдан юз ўгириб, уларнинг ҳавойи нафсларига эргашманг! Сизлардан ҳар бир миллат (яъни, дин) учун (алоҳида) шариат ва йўл қилиб қўйдик. Агар Аллоҳ хоҳласа эди, ҳаммангизни бир миллат қилиб қўйган бўлур эди. Лекин ўзи ато этган нарсаларда сизларни имтиҳон қилиш учун (ҳар бир миллатга алоҳида шариат йўл қилиб қўйди). Бас, яхши амалларга шошилингиз! Барчангиз Аллоҳга қайтишингиз бор. Сўнг У зот сизларга ихтилоф қилгувчи бўлган нарсаларингизнинг хабарини берур'}, {'chapter': 5, 'verse': 49, 'text': 'وَأَنِ ٱحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ ٱللَّهُ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ وَٱحۡذَرۡهُمۡ أَن يَفۡتِنُوكَ عَنۢ بَعۡضِ مَآ أَنزَلَ ٱللَّهُ إِلَيۡكَۖ فَإِن تَوَلَّوۡاْ فَٱعۡلَمۡ أَنَّمَا يُرِيدُ ٱللَّهُ أَن يُصِيبَهُم بِبَعۡضِ ذُنُوبِهِمۡۗ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ لَفَٰسِقُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), улар ўртасида Аллоҳ нозил қилган нарса билан ҳукм қилинг, уларнинг ҳавойи нафсларига эргашманг ва Аллоҳ Сизга нозил қилган ҳукмларнинг айримларидан Сизни буриб фитнага солиб қўйишларидан эҳтиёт бўлинг! Агар (Сизнинг ҳукмингиздан) юз ўгирсалар, билингки, Аллоҳ уларга айрим гуноҳлари сабабли мусибат етказишни истамоқда. Шубҳасиз, одамлардан кўплари итоатсиздирлар'}, {'chapter': 5, 'verse': 50, 'text': 'أَفَحُكۡمَ ٱلۡجَٰهِلِيَّةِ يَبۡغُونَۚ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ حُكۡمٗا لِّقَوۡمٖ يُوقِنُونَ\nДинсизлик ҳукмрон бўлишини истайдиларми?! Иймонлари комил бўлган қавм учун Аллоҳдан ҳам гўзалроқ ҳукм қилгувчи ким бор'}, {'chapter': 5, 'verse': 51, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلۡيَهُودَ وَٱلنَّصَٰرَىٰٓ أَوۡلِيَآءَۘ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَإِنَّهُۥ مِنۡهُمۡۗ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nЭй мўминлар, яҳудийлар ва насронийларни дўст тутмангиз! Уларнинг баъзилари баъзиларига дўстдирлар. Сизлардан ким уларга дўст бўлса, бас, у ўшалардандир. Албатта, Аллоҳ золим қавмни ҳидоят қилмас'}, {'chapter': 5, 'verse': 52, 'text': 'فَتَرَى ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يُسَٰرِعُونَ فِيهِمۡ يَقُولُونَ نَخۡشَىٰٓ أَن تُصِيبَنَا دَآئِرَةٞۚ فَعَسَى ٱللَّهُ أَن يَأۡتِيَ بِٱلۡفَتۡحِ أَوۡ أَمۡرٖ مِّنۡ عِندِهِۦ فَيُصۡبِحُواْ عَلَىٰ مَآ أَسَرُّواْ فِيٓ أَنفُسِهِمۡ نَٰدِمِينَ\nБас, Сиз дилларида мараз бўлган кимсалар: «Бизга бирон бало етишидан қўрқамиз», деган ҳолларида улар (кофирлар) томонга шошаётганларини кўрасиз. Шояд Аллоҳ мусулмонларга ғалаба берса ёки ўз ҳузуридан (бу мунофиқ кимсаларни шарманда қиладиган) бирон ишни келтириб, улар ичларида яширган нарсаларига надомат қилувчиларга айланиб қолсалар'}, {'chapter': 5, 'verse': 53, 'text': 'وَيَقُولُ ٱلَّذِينَ ءَامَنُوٓاْ أَهَـٰٓؤُلَآءِ ٱلَّذِينَ أَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ إِنَّهُمۡ لَمَعَكُمۡۚ حَبِطَتۡ أَعۡمَٰلُهُمۡ فَأَصۡبَحُواْ خَٰسِرِينَ\n(Шунда) мўминлар (бир-бирларига): «Жон-жаҳдлари билан «Сизлар билан биргамиз», деб қасам ичганлар шуларми?», дейдилар. У мунофиқларнинг қилган амаллари беҳуда кетди ва ўзлари зиён кўргувчиларга айланиб қолишди'}, {'chapter': 5, 'verse': 54, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ مَن يَرۡتَدَّ مِنكُمۡ عَن دِينِهِۦ فَسَوۡفَ يَأۡتِي ٱللَّهُ بِقَوۡمٖ يُحِبُّهُمۡ وَيُحِبُّونَهُۥٓ أَذِلَّةٍ عَلَى ٱلۡمُؤۡمِنِينَ أَعِزَّةٍ عَلَى ٱلۡكَٰفِرِينَ يُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ وَلَا يَخَافُونَ لَوۡمَةَ لَآئِمٖۚ ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٌ\nЭй мўминлар, сизларнинг ичингиздан кимда-ким динидан қайтса, Аллоҳ бошқа бир қавмни келтирурки, Аллоҳ уларни яхши кўрур, улар Аллоҳни яхши кўрурлар. Улар мўминларга ҳокисор, кофирларга эса қаттиққўл, бирон маломатгўйнинг маломатидан қўрқмай Аллоҳ йўлида курашадиган кишилардир. Бу Аллоҳнинг фазлу марҳамати бўлиб, Ўзи хоҳлаган кишиларга берур. Аллоҳ фазлу карами кенг, билгувчидир'}, {'chapter': 5, 'verse': 55, 'text': 'إِنَّمَا وَلِيُّكُمُ ٱللَّهُ وَرَسُولُهُۥ وَٱلَّذِينَ ءَامَنُواْ ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُمۡ رَٰكِعُونَ\nСизларнинг дўстингиз фақат Аллоҳ, Унинг пайғамбари ва таъзим-тавозеъ қилган ҳолларида намозни тўкис адо этадиган, закотни (ҳақдорларга) ато этадиган мўминлардир'}, {'chapter': 5, 'verse': 56, 'text': 'وَمَن يَتَوَلَّ ٱللَّهَ وَرَسُولَهُۥ وَٱلَّذِينَ ءَامَنُواْ فَإِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡغَٰلِبُونَ\nКимки Аллоҳни, Унинг пайғамбарини ва мўминларни дўст тутса, (нажот топгай), зеро фақат Аллоҳнинг гуруҳигина ғолиб бўлгувчидир'}, {'chapter': 5, 'verse': 57, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلَّذِينَ ٱتَّخَذُواْ دِينَكُمۡ هُزُوٗا وَلَعِبٗا مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَٱلۡكُفَّارَ أَوۡلِيَآءَۚ وَٱتَّقُواْ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِينَ\nЭй мўминлар, сизлардан илгари китоб берилганлар ичидан динингизни ҳазил-масхара қилиб олган кимсаларни ва кофирларни дўст тутмангиз! Агар мўмин бўлсангизлар, Аллоҳдан қўрқингиз'}, {'chapter': 5, 'verse': 58, 'text': 'وَإِذَا نَادَيۡتُمۡ إِلَى ٱلصَّلَوٰةِ ٱتَّخَذُوهَا هُزُوٗا وَلَعِبٗاۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ\nСизлар қачон намозга чақирсангиз (азон айтсангиз), улар ўша (намозни) масхара ўйин қилиб оладилар. Бунга сабаб уларнинг ақлсиз — жоҳил қавм эканликларидир'}, {'chapter': 5, 'verse': 59, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ هَلۡ تَنقِمُونَ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ مِن قَبۡلُ وَأَنَّ أَكۡثَرَكُمۡ فَٰسِقُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Эй аҳли китоб, сизлар бизни Аллоҳга, бизга нозил қилинган Китоб (Қуръон)га ва илгари нозил қилинган китобларга иймон келтирганимиз, сизларнинг кўпчилигингиз эса фосиқ — итоатсиз бўлганингиз сабаблигина ёмон кўрасизлар»'}, {'chapter': 5, 'verse': 60, 'text': 'قُلۡ هَلۡ أُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكَ مَثُوبَةً عِندَ ٱللَّهِۚ مَن لَّعَنَهُ ٱللَّهُ وَغَضِبَ عَلَيۡهِ وَجَعَلَ مِنۡهُمُ ٱلۡقِرَدَةَ وَٱلۡخَنَازِيرَ وَعَبَدَ ٱلطَّـٰغُوتَۚ أُوْلَـٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ عَن سَوَآءِ ٱلسَّبِيلِ\nАйтинг: «Сизларга Аллоҳнинг ҳузурида бундан кўра ёмонроқ «савоб» (жазо оладиган кимсалар) ҳақида хабар берайми? Улар Аллоҳнинг лаънатига дучор бўлган, ғазабига гирифтор бўлган ва маймун, тўнғизларга айлантириб қўйилганлар ҳамда шайтонга қул бўлган кимсалардир. Ана ўшалар энг ёмон мартабада бўлган ва бутунлай Тўғри Йўлдан озган кимсалардир'}, {'chapter': 5, 'verse': 61, 'text': 'وَإِذَا جَآءُوكُمۡ قَالُوٓاْ ءَامَنَّا وَقَد دَّخَلُواْ بِٱلۡكُفۡرِ وَهُمۡ قَدۡ خَرَجُواْ بِهِۦۚ وَٱللَّهُ أَعۡلَمُ بِمَا كَانُواْ يَكۡتُمُونَ\n(Эй мўминлар), улар сизларнинг олдингизга келганларида: «Иймон келтирдик», дейдилар. Ҳолбуки, улар (Эй Муҳаммад алайҳис-салоту вас-салом, сизнинг ҳузурингизга) кофир ҳолда кириб, кофир ҳолда чиқурлар. Аллоҳ улар яширувчи бўлган нарсани (яъни, дилларидаги куфрни) жуда яхши билгувчидир'}, {'chapter': 5, 'verse': 62, 'text': 'وَتَرَىٰ كَثِيرٗا مِّنۡهُمۡ يُسَٰرِعُونَ فِي ٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُواْ يَعۡمَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларнинг кўпларини гуноҳ, зулм томонга ва ҳаромхўрликка чопаётганларини кўрасиз. Қилаётган ҳунарлари нақадар ёмон ҳунар'}, {'chapter': 5, 'verse': 63, 'text': 'لَوۡلَا يَنۡهَىٰهُمُ ٱلرَّبَّـٰنِيُّونَ وَٱلۡأَحۡبَارُ عَن قَوۡلِهِمُ ٱلۡإِثۡمَ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُواْ يَصۡنَعُونَ\nОлим ва донишмандлари уларни гуноҳ сўзлардан, ҳаромхўрликдан қайтармайдиларми?! Қилаётган ҳунарлари нақадар ёмон ҳунар'}, {'chapter': 5, 'verse': 64, 'text': 'وَقَالَتِ ٱلۡيَهُودُ يَدُ ٱللَّهِ مَغۡلُولَةٌۚ غُلَّتۡ أَيۡدِيهِمۡ وَلُعِنُواْ بِمَا قَالُواْۘ بَلۡ يَدَاهُ مَبۡسُوطَتَانِ يُنفِقُ كَيۡفَ يَشَآءُۚ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۚ وَأَلۡقَيۡنَا بَيۡنَهُمُ ٱلۡعَدَٰوَةَ وَٱلۡبَغۡضَآءَ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۚ كُلَّمَآ أَوۡقَدُواْ نَارٗا لِّلۡحَرۡبِ أَطۡفَأَهَا ٱللَّهُۚ وَيَسۡعَوۡنَ فِي ٱلۡأَرۡضِ فَسَادٗاۚ وَٱللَّهُ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ\nЯҳудийлар: «Аллоҳнинг қўли боғлиқ (яъни, бахил)», дедилар. Ўзларининг қўллари боғланиб қолгай ва айтган гаплари билан лаънатга учрагайлар. Йўқ! У зотнинг қўллари очиқдир ва Ўзи истагандек инфоқ-эҳсон қилур. Сизга Парвардигорингиз томонидан нозил қилинган нарса (Қуръон) улардан кўпларига туғён ва куфрни зиёда қилиши шубҳасиздир. Уларнинг ораларига то Қиёмат Кунигача (давом этадиган) буғзу адоватни ташлаб қўйганмиз. Улар қачон (Сизга қарши) уруш оловини ёқсалар, Аллоҳ уни ўчирур. Улар Ерда бузғунчилик қилиб юрурлар. Аллоҳ эса бузғунчи кимсаларни севмас'}, {'chapter': 5, 'verse': 65, 'text': 'وَلَوۡ أَنَّ أَهۡلَ ٱلۡكِتَٰبِ ءَامَنُواْ وَٱتَّقَوۡاْ لَكَفَّرۡنَا عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأَدۡخَلۡنَٰهُمۡ جَنَّـٰتِ ٱلنَّعِيمِ\nАгар аҳли китоб иймон келтириб, Аллоҳдан қўрқсалар эди, Биз албатта ёмонликларини ўчирган ва жаннат неъматларига дохил қилган бўлар эдик'}, {'chapter': 5, 'verse': 66, 'text': 'وَلَوۡ أَنَّهُمۡ أَقَامُواْ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡهِم مِّن رَّبِّهِمۡ لَأَكَلُواْ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۚ مِّنۡهُمۡ أُمَّةٞ مُّقۡتَصِدَةٞۖ وَكَثِيرٞ مِّنۡهُمۡ سَآءَ مَا يَعۡمَلُونَ\nАгар улар Тавротга, Инжилга ва Парвардигорлари томонидан нозил қилинган барча нарсаларга амал қилганларида эди, устларидан (осмондан) ҳам, остларидан (ердан) ҳам ризқланган бўлур эдилар. Уларнинг ораларида адолат эгалари бўлган кишилар ҳам бор. Аммо уларнинг кўплари нақадар ёмон амалларни қиладилар'}, {'chapter': 5, 'verse': 67, 'text': '۞يَـٰٓأَيُّهَا ٱلرَّسُولُ بَلِّغۡ مَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَۖ وَإِن لَّمۡ تَفۡعَلۡ فَمَا بَلَّغۡتَ رِسَالَتَهُۥۚ وَٱللَّهُ يَعۡصِمُكَ مِنَ ٱلنَّاسِۗ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nЭй пайғамбар, сизга Парвардигорингиз томонидан нозил қилинган нарсани етказинг! Агар (бу фармонга амал) қилмасангиз, Унинг элчилигини (бандаларига) етказмаган бўлурсиз, Аллоҳ Сизни одамлардан (уларнинг зараридан) сақлагай. Албатта, Аллоҳ кофир қавмни ҳидоят қилмас'}, {'chapter': 5, 'verse': 68, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَسۡتُمۡ عَلَىٰ شَيۡءٍ حَتَّىٰ تُقِيمُواْ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡۗ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۖ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nАйтинг: «Эй аҳли китоб, то Тавротга, Инжилга ва сизларга Парвардигорингиз томонидан нозил қилинган барча нарсаларга амал қилмагунингизча, ҳеч қандай динда эмассизлар. (Эй Муҳаммад алайҳис-салоту вас-салом), сизга Парвардигорингиз томонидан нозил қилинган нарса улардан кўпларига туғён ва куфрни зиёда қилиши шубҳасиздир. Бас, сиз бу кофир қавм (қилмиши)дан маҳзун бўлманг'}, {'chapter': 5, 'verse': 69, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلصَّـٰبِـُٔونَ وَٱلنَّصَٰرَىٰ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта, иймон келтирган зотлар, яҳудий бўлганлар, собиийлар (фаришталарга сиғинувчилар) ва насронийлар — (улардан) қайсилари (иймонга келиб) Аллоҳга, Охират кунига ишонса ва яхши амаллар қилса, ўшалар учун хавфу хатар, ғам-андуҳ йўқдур'}, {'chapter': 5, 'verse': 70, 'text': 'لَقَدۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَأَرۡسَلۡنَآ إِلَيۡهِمۡ رُسُلٗاۖ كُلَّمَا جَآءَهُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُهُمۡ فَرِيقٗا كَذَّبُواْ وَفَرِيقٗا يَقۡتُلُونَ\nБиз Бани Исроилнинг аҳд-паймонини олдик ва уларга пайғамбарлар юбордик. Қачон уларга бир пайғамбар нафслари хоҳламаган бирон ҳукмни келтирса, бировларини ёлғончи қиладилар, бировларини эса ўлдирадилар'}, {'chapter': 5, 'verse': 71, 'text': 'وَحَسِبُوٓاْ أَلَّا تَكُونَ فِتۡنَةٞ فَعَمُواْ وَصَمُّواْ ثُمَّ تَابَ ٱللَّهُ عَلَيۡهِمۡ ثُمَّ عَمُواْ وَصَمُّواْ كَثِيرٞ مِّنۡهُمۡۚ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ\nВа (бу қилмишлари натижасида) ҳеч қандай фитна бўлмайди, деб ўйлаб кўру кар бўлиб олдилар. Сўнг (тавба қилганларида) Аллоҳ тавбаларини қабул қилди. Кейин улардан кўплари яна кўр ва кар бўлиб олдилар. Аллоҳ қилаётган амалларини кўргувчидир'}, {'chapter': 5, 'verse': 72, 'text': 'لَقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَۖ وَقَالَ ٱلۡمَسِيحُ يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱعۡبُدُواْ ٱللَّهَ رَبِّي وَرَبَّكُمۡۖ إِنَّهُۥ مَن يُشۡرِكۡ بِٱللَّهِ فَقَدۡ حَرَّمَ ٱللَّهُ عَلَيۡهِ ٱلۡجَنَّةَ وَمَأۡوَىٰهُ ٱلنَّارُۖ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٖ\n«Аллоҳ — Масиҳ бинни Марямдир», деган кимсалар аниқ кофир бўлдилар. Ҳолбуки, Масиҳ: «Эй Бани Исроил, Парвардигорим ва Парвардигорингиз бўлмиш Аллоҳга бандалик қилингиз!», демишдир. Албатта кимда-ким Аллоҳга ширк келтирса, Аллоҳ унга жаннатни ҳаром қилур ва борар жойи дўзах бўлур. Зулм қилгувчилар учун бирон ёрдамчи бўлмас'}, {'chapter': 5, 'verse': 73, 'text': 'لَّقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ ثَالِثُ ثَلَٰثَةٖۘ وَمَا مِنۡ إِلَٰهٍ إِلَّآ إِلَٰهٞ وَٰحِدٞۚ وَإِن لَّمۡ يَنتَهُواْ عَمَّا يَقُولُونَ لَيَمَسَّنَّ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٌ\n«Аллоҳ — Учтанинг (яъни, Аллоҳ, Марям, Ийсонинг) биридир», деган кимсалар ҳам аниқ кофир бўладилар. Чунки ягона Илоҳдан бошқа бирон илоҳ йўқдир. Агар улар айтаётган гапларидан тўхтамасалар, албатта улардан кофир бўлган кимсаларни аламли азоб ушлайди'}, {'chapter': 5, 'verse': 74, 'text': 'أَفَلَا يَتُوبُونَ إِلَى ٱللَّهِ وَيَسۡتَغۡفِرُونَهُۥۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nАхир, Аллоҳга тавба қилиб, мағфират қилишини сўрамайдиларми?! Аллоҳ мағфиратли, меҳрибон-ку'}, {'chapter': 5, 'verse': 75, 'text': 'مَّا ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُ وَأُمُّهُۥ صِدِّيقَةٞۖ كَانَا يَأۡكُلَانِ ٱلطَّعَامَۗ ٱنظُرۡ كَيۡفَ نُبَيِّنُ لَهُمُ ٱلۡأٓيَٰتِ ثُمَّ ٱنظُرۡ أَنَّىٰ يُؤۡفَكُونَ\nМасиҳ бинни Марям фақат бир пайғамбар бўлиб, ундан илгари ҳам кўп пайғамбарлар ўтгандир. Унинг онаси эса сиддиқа — Аллоҳга ҳаққи-рост иймон келтирган аёлдир. Иккалалари ҳам таом Ер эдилар. (Эй Муҳаммад алайҳис-салоту вас-салом), оят-ҳужжатларни уларга қандай баён қилишимизни кўринг, сўнг уларни қандай бўҳтон томонга оғаётганларни кўринг'}, {'chapter': 5, 'verse': 76, 'text': 'قُلۡ أَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا نَفۡعٗاۚ وَٱللَّهُ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАйтинг: «Аллоҳни қўйиб, сизлар зарар ҳам, фойда ҳам беришга эга бўлмаган нарсаларга ибодат қиласизми? Аллоҳ эшитгувчи, билгувчидир»'}, {'chapter': 5, 'verse': 77, 'text': 'قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ غَيۡرَ ٱلۡحَقِّ وَلَا تَتَّبِعُوٓاْ أَهۡوَآءَ قَوۡمٖ قَدۡ ضَلُّواْ مِن قَبۡلُ وَأَضَلُّواْ كَثِيرٗا وَضَلُّواْ عَن سَوَآءِ ٱلسَّبِيلِ\nАйтинг: «Эй аҳли китоб, динингизда ноҳақ тажовуз қилманг ва (Ислом келишидан) илгари йўлдан озган ва кўпларни йўлдан оздирган ҳамда Тўғри Йўлдан чиқиб кетган қавмнинг нафси ҳаволарига эргашманг'}, {'chapter': 5, 'verse': 78, 'text': 'لُعِنَ ٱلَّذِينَ كَفَرُواْ مِنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ عَلَىٰ لِسَانِ دَاوُۥدَ وَعِيسَى ٱبۡنِ مَرۡيَمَۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ\nБани Исроил орасидан кофир бўлган кимсалар Довуд ва Ийсо бинни Марям тилида лаънатлангандирлар. Бунга сабаб уларнинг қилган исёнлари ва тажовузкор бўлганларидир'}, {'chapter': 5, 'verse': 79, 'text': 'كَانُواْ لَا يَتَنَاهَوۡنَ عَن مُّنكَرٖ فَعَلُوهُۚ لَبِئۡسَ مَا كَانُواْ يَفۡعَلُونَ\nУлар бир-бирларини қилган нолойиқ ишларидан қайтармас эдилар. Бу қилмишлари нақадар ёмон иш'}, {'chapter': 5, 'verse': 80, 'text': 'تَرَىٰ كَثِيرٗا مِّنۡهُمۡ يَتَوَلَّوۡنَ ٱلَّذِينَ كَفَرُواْۚ لَبِئۡسَ مَا قَدَّمَتۡ لَهُمۡ أَنفُسُهُمۡ أَن سَخِطَ ٱللَّهُ عَلَيۡهِمۡ وَفِي ٱلۡعَذَابِ هُمۡ خَٰلِدُونَ\nУлардан кўплари кофир бўлган кимсаларни дўст тутганларини кўрасиз. Уларга нафси ҳаволари нақадар ёмон нарсани — Аллоҳнинг ғазабини келтирди. Энди улар абадий азобда қолгувчидирлар'}, {'chapter': 5, 'verse': 81, 'text': 'وَلَوۡ كَانُواْ يُؤۡمِنُونَ بِٱللَّهِ وَٱلنَّبِيِّ وَمَآ أُنزِلَ إِلَيۡهِ مَا ٱتَّخَذُوهُمۡ أَوۡلِيَآءَ وَلَٰكِنَّ كَثِيرٗا مِّنۡهُمۡ فَٰسِقُونَ\nАгар Аллоҳга, пайғамбарга ва унга нозил қилинган китобга иймон келтирганларида эди, уларни — кофирларни дўст тутмаган бўлур эдилар. Лекин улардан кўплари итоатсиз кимсалардир'}, {'chapter': 5, 'verse': 82, 'text': '۞لَتَجِدَنَّ أَشَدَّ ٱلنَّاسِ عَدَٰوَةٗ لِّلَّذِينَ ءَامَنُواْ ٱلۡيَهُودَ وَٱلَّذِينَ أَشۡرَكُواْۖ وَلَتَجِدَنَّ أَقۡرَبَهُم مَّوَدَّةٗ لِّلَّذِينَ ءَامَنُواْ ٱلَّذِينَ قَالُوٓاْ إِنَّا نَصَٰرَىٰۚ ذَٰلِكَ بِأَنَّ مِنۡهُمۡ قِسِّيسِينَ وَرُهۡبَانٗا وَأَنَّهُمۡ لَا يَسۡتَكۡبِرُونَ\nИймон келтирган зотларга энг қаттиқ адоват қилгувчи одамлар яҳудийлар ва мушрик бўлган кимсалар эканини кўрасиз. Иймон келтирган зотларга энг яқин дўст бўлгувчилар эса: «Биз насронийлармиз», деган кишилар эканини кўрасиз. Бунга сабаб уларнинг орасида олимлар ва роҳиблар бор эканлиги ва уларнинг кибр-ҳаво қилмасликларидир'}, {'chapter': 5, 'verse': 83, 'text': 'وَإِذَا سَمِعُواْ مَآ أُنزِلَ إِلَى ٱلرَّسُولِ تَرَىٰٓ أَعۡيُنَهُمۡ تَفِيضُ مِنَ ٱلدَّمۡعِ مِمَّا عَرَفُواْ مِنَ ٱلۡحَقِّۖ يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِينَ\nУлар пайғамбарга нозил қилинган нарсани (Қуръонни) эшитган пайтларида, унинг ҳақ эканини билганлари сабабли кўзларидан ёш қуйилаётганини кўрасиз. Улар: «Парвардигоро, иймон келтирдик. Бас, бизни гувоҳлик бергувчилар қаторига ёзгин», дейдилар'}, {'chapter': 5, 'verse': 84, 'text': 'وَمَا لَنَا لَا نُؤۡمِنُ بِٱللَّهِ وَمَا جَآءَنَا مِنَ ٱلۡحَقِّ وَنَطۡمَعُ أَن يُدۡخِلَنَا رَبُّنَا مَعَ ٱلۡقَوۡمِ ٱلصَّـٰلِحِينَ\n«Бизга нима бўлдики, ўзимиз Аллоҳга ва бизга келган ҳақ динга ишонмаймиз-у, яна Парвардигоримиз бизни ҳам бу солиҳ қавм билан бирга (жаннатга) дохил қилишини тамаъ қиламиз?!»'}, {'chapter': 5, 'verse': 85, 'text': 'فَأَثَٰبَهُمُ ٱللَّهُ بِمَا قَالُواْ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ ٱلۡمُحۡسِنِينَ\nБас, шу гаплари сабабли Аллоҳ уларни остидан дарёлар оқиб турадиган, улар абадий яшайдиган жаннатлар билан мукофотлади. Бу чиройли амал қилгувчиларнинг мукофотидир'}, {'chapter': 5, 'verse': 86, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nКофир бўлган ва Бизнинг оятларимизни ёлғон деган кимсалар эса жаҳаннам эгаларидир'}, {'chapter': 5, 'verse': 87, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُحَرِّمُواْ طَيِّبَٰتِ مَآ أَحَلَّ ٱللَّهُ لَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ\nЭй мўминлар, Аллоҳ сизлар учун ҳалол қилиб қўйган покиза нарсаларни ҳаром деманглар ва (Аллоҳ белгилаган чегарадан) тажовуз қилманглар! Албатта, Аллоҳ тажовузкор кимсаларни севмас'}, {'chapter': 5, 'verse': 88, 'text': 'وَكُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَٰلٗا طَيِّبٗاۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ\nАллоҳ сизларга ризқ қилиб берган ҳалол, пок нарсалардан енглар ва ўзларингиз иймон келтирган Аллоҳдан қўрқинглар'}, {'chapter': 5, 'verse': 89, 'text': 'لَا يُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا عَقَّدتُّمُ ٱلۡأَيۡمَٰنَۖ فَكَفَّـٰرَتُهُۥٓ إِطۡعَامُ عَشَرَةِ مَسَٰكِينَ مِنۡ أَوۡسَطِ مَا تُطۡعِمُونَ أَهۡلِيكُمۡ أَوۡ كِسۡوَتُهُمۡ أَوۡ تَحۡرِيرُ رَقَبَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖۚ ذَٰلِكَ كَفَّـٰرَةُ أَيۡمَٰنِكُمۡ إِذَا حَلَفۡتُمۡۚ وَٱحۡفَظُوٓاْ أَيۡمَٰنَكُمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَشۡكُرُونَ\nАллоҳ сизларни тилларингиздаги беҳуда қасамларингиз сабабли жазоламайди. Балки (дилларингиздаги) маҳкам тугган қасам сабабли (агар шундай қасамни бузсангиз) жазолайди. Бас, унинг каффорати ўз оилангизни боқадиган ўртача таомлардан ўнта мискинга таом бериш ёки уларнинг сарпосини қилиш, ёхуд бир қулни озод қилишдир. Ким (бу нарсаларни) топа олмаса, уч кун рўза тутсин! Мана шу — қасам ичган (ва уни бузган) пайтингиздаги қасамларингиз каффоратидир. Қасамларингизни (бузишдан) сақланингиз. Шукр қилишингиз учун Аллоҳ сизларга Ўз оятларини мана шундай очиқ-ойдин баён қилур'}, {'chapter': 5, 'verse': 90, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّمَا ٱلۡخَمۡرُ وَٱلۡمَيۡسِرُ وَٱلۡأَنصَابُ وَٱلۡأَزۡلَٰمُ رِجۡسٞ مِّنۡ عَمَلِ ٱلشَّيۡطَٰنِ فَٱجۡتَنِبُوهُ لَعَلَّكُمۡ تُفۡلِحُونَ\nЭй мўминлар, ароқ (маст қиладиган ичкилик ичиш), қимор (ўйнаш), бутлар (яъни, уларга сиғиниш) ва чўплар (яъни, чўплар билан фолбинлик қилиш) шайтон амалидан бўлган ҳаром ишдир. Бас, нажот топишингиз учун уларнинг ҳар биридан узоқ бўлингиз'}, {'chapter': 5, 'verse': 91, 'text': 'إِنَّمَا يُرِيدُ ٱلشَّيۡطَٰنُ أَن يُوقِعَ بَيۡنَكُمُ ٱلۡعَدَٰوَةَ وَٱلۡبَغۡضَآءَ فِي ٱلۡخَمۡرِ وَٱلۡمَيۡسِرِ وَيَصُدَّكُمۡ عَن ذِكۡرِ ٱللَّهِ وَعَنِ ٱلصَّلَوٰةِۖ فَهَلۡ أَنتُم مُّنتَهُونَ\nИчкилик, қимор сабабли шайтон ўрталарингизга буғзу адоват солишни ҳамда сизларни Аллоҳни зикр қилишдан ва намоз ўқишдан тўсишни истайди, холос! Энди тўхтарсизлар'}, {'chapter': 5, 'verse': 92, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَٱحۡذَرُواْۚ فَإِن تَوَلَّيۡتُمۡ فَٱعۡلَمُوٓاْ أَنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАллоҳга итоат қилингиз, пайғамбарга буйинсунингиз ва (ҳаромдан) эҳтиёт бўлингиз! Агар (итоатдан) бош тортсангиз, билингизки, пайғамбаримиз зиммасида фақат (бизнинг ҳукмларимизни) очиқ-равшан қилиб етказиш бор, холос'}, {'chapter': 5, 'verse': 93, 'text': 'لَيۡسَ عَلَى ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جُنَاحٞ فِيمَا طَعِمُوٓاْ إِذَا مَا ٱتَّقَواْ وَّءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ ثُمَّ ٱتَّقَواْ وَّءَامَنُواْ ثُمَّ ٱتَّقَواْ وَّأَحۡسَنُواْۚ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ\nИймон келтирган ва яхши амаллар қилган зотлар учун — агар (ҳаром нарсалардан) сақланиб, иймон ва яхши амалларида собит бўлсалар, сўнгра ўзларини сақлаб (у нарсаларнинг ҳаромлигига) иймон келтирсалар, сўнгра Аллоҳдан қўрқиб, чиройли амаллар қилсалар — (илгари) еб-ичган нарсаларида гуноҳ йўқдир. Аллоҳ чиройли амал қилгувчиларни севади'}, {'chapter': 5, 'verse': 94, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَيَبۡلُوَنَّكُمُ ٱللَّهُ بِشَيۡءٖ مِّنَ ٱلصَّيۡدِ تَنَالُهُۥٓ أَيۡدِيكُمۡ وَرِمَاحُكُمۡ لِيَعۡلَمَ ٱللَّهُ مَن يَخَافُهُۥ بِٱلۡغَيۡبِۚ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ\nЭй мўминлар, албатта Аллоҳ ғойибда (кўрмай) туриб ўзидан қўрқадиган кишиларни билиш учун сизларни (ҳаж сафарида бўлган чоғингизда) қўл ва найзаларингиз етгудек овлар билан имтиҳон қилур. Мана шу (огоҳлантиришдан) кейин ким ҳаддидан ошса (иҳромда бўлган ҳолда ов овласа), унинг учун аламли азоб бордир'}, {'chapter': 5, 'verse': 95, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقۡتُلُواْ ٱلصَّيۡدَ وَأَنتُمۡ حُرُمٞۚ وَمَن قَتَلَهُۥ مِنكُم مُّتَعَمِّدٗا فَجَزَآءٞ مِّثۡلُ مَا قَتَلَ مِنَ ٱلنَّعَمِ يَحۡكُمُ بِهِۦ ذَوَا عَدۡلٖ مِّنكُمۡ هَدۡيَۢا بَٰلِغَ ٱلۡكَعۡبَةِ أَوۡ كَفَّـٰرَةٞ طَعَامُ مَسَٰكِينَ أَوۡ عَدۡلُ ذَٰلِكَ صِيَامٗا لِّيَذُوقَ وَبَالَ أَمۡرِهِۦۗ عَفَا ٱللَّهُ عَمَّا سَلَفَۚ وَمَنۡ عَادَ فَيَنتَقِمُ ٱللَّهُ مِنۡهُۚ وَٱللَّهُ عَزِيزٞ ذُو ٱنتِقَامٍ\nЭй мўминлар, иҳромда бўлган чоғингизда бирон овни ўлдирманг! Сизларнинг орангиздан ким қасддан бирон овни ўлдирса, (унинг зиммасида) чорва молларидан ўлдирган ови баробарида жазо бордирки, у Каъбага етиб боргувчи ҳадя бўлиб, унга ўзларингдан икки адолат эгаси ҳакамлик қилур. Ёки (унинг зиммасида) каффорат — (ўша овнинг қиймати баробарида) мискинларга таом бериш ё ўз ишининг зиёнини тотиб кўриши учун шунинг (яъни, ўша қийматнинг) баробарида рўза тутиш бордир. Илгари ўтган нарсаларни (яъни, иҳромда бўлган ҳолда қилинган овларни) Аллоҳ афв этди. Ким яна қайтиб (ов) қилса, ундан Аллоҳ интиқом олур. Аллоҳ қудрат ва интиқом Соҳибидир'}, {'chapter': 5, 'verse': 96, 'text': 'أُحِلَّ لَكُمۡ صَيۡدُ ٱلۡبَحۡرِ وَطَعَامُهُۥ مَتَٰعٗا لَّكُمۡ وَلِلسَّيَّارَةِۖ وَحُرِّمَ عَلَيۡكُمۡ صَيۡدُ ٱلۡبَرِّ مَا دُمۡتُمۡ حُرُمٗاۗ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ\nСизларга ўзингиз ва бошқа мусофирлар фойдаланиши учун денгиз (сув) ови ва унинг емишлари (яъни, сизлар овламай ўзи соҳилга чиқиб қолган нарсалар) ҳалол қилинди. Ва модомики иҳромда экансизлар, сизларга қуруқлик ови ҳаром қилинди. (Қиёмат Куни) даргоҳида тўпланадиган Аллоҳдан қўрқингиз'}, {'chapter': 5, 'verse': 97, 'text': '۞جَعَلَ ٱللَّهُ ٱلۡكَعۡبَةَ ٱلۡبَيۡتَ ٱلۡحَرَامَ قِيَٰمٗا لِّلنَّاسِ وَٱلشَّهۡرَ ٱلۡحَرَامَ وَٱلۡهَدۡيَ وَٱلۡقَلَـٰٓئِدَۚ ذَٰلِكَ لِتَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَأَنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАллоҳ Каъба — Байтул-Ҳаромни, уруш ҳаром этилган ойни, ҳадя (қурбонлик) ва ҳадя белгиларини (ҳадя учун аталган жониворларга осиб қўйиладиган ҳадялик аломатларини) одамлар учун тўғрилик асоси қилиб қўйди. Бу (нарса) Аллоҳ осмонлар ва Ердаги бор нарсани билишини — Аллоҳ ҳамма нарсани билгувчи эканини билишинглар учундир'}, {'chapter': 5, 'verse': 98, 'text': 'ٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ وَأَنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nАлбатта, Аллоҳ азоби қаттиқ Зот эканини ва албатта Аллоҳ мағфиратли, меҳрибон эканини билингиз'}, {'chapter': 5, 'verse': 99, 'text': 'مَّا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُۗ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ\nПайғамбар зиммасида фақат етказиш бор, холос. Аллоҳ ошкор этган нарсангизни ҳам, яширган нарсангизни ҳам билур'}, {'chapter': 5, 'verse': 100, 'text': 'قُل لَّا يَسۡتَوِي ٱلۡخَبِيثُ وَٱلطَّيِّبُ وَلَوۡ أَعۡجَبَكَ كَثۡرَةُ ٱلۡخَبِيثِۚ فَٱتَّقُواْ ٱللَّهَ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ لَعَلَّكُمۡ تُفۡلِحُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Гарчи нопок нарсанинг кўплиги сизни қизиқтирса ҳам, нопок нарса пок нарсага баробар бўлмас. Бас, аҳли дониш, нажот топишингиз учун Аллоҳдан қўрқингиз'}, {'chapter': 5, 'verse': 101, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَسۡـَٔلُواْ عَنۡ أَشۡيَآءَ إِن تُبۡدَ لَكُمۡ تَسُؤۡكُمۡ وَإِن تَسۡـَٔلُواْ عَنۡهَا حِينَ يُنَزَّلُ ٱلۡقُرۡءَانُ تُبۡدَ لَكُمۡ عَفَا ٱللَّهُ عَنۡهَاۗ وَٱللَّهُ غَفُورٌ حَلِيمٞ\nЭй мўминлар, (Аллоҳ сизлардан кечиб юборган), очилганда сизларни хафа қиладиган нарсалар ҳақида сўраманглар! Қуръон нозил бўлаётганда у нарсалар ҳақида сўрасангиз, сизларга очиб берилади (яъни, зиммангизга юкланади). Ҳолбуки, Аллоҳ у нарсаларни кечиб юборган эди. Аллоҳ мағфиратли, ҳалимдир'}, {'chapter': 5, 'verse': 102, 'text': 'قَدۡ سَأَلَهَا قَوۡمٞ مِّن قَبۡلِكُمۡ ثُمَّ أَصۡبَحُواْ بِهَا كَٰفِرِينَ\nУ нарсалар ҳақида сизлардан олдинги қавм сўраган эди. Сўнгра эса у нарсаларни инкор қилувчиларга айландилар'}, {'chapter': 5, 'verse': 103, 'text': 'مَا جَعَلَ ٱللَّهُ مِنۢ بَحِيرَةٖ وَلَا سَآئِبَةٖ وَلَا وَصِيلَةٖ وَلَا حَامٖ وَلَٰكِنَّ ٱلَّذِينَ كَفَرُواْ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَأَكۡثَرُهُمۡ لَا يَعۡقِلُونَ\nАллоҳ на «баҳийра», на «соиба», на «васийла» ва на «ҳомий» қилишга (буюргани) йўқ. Лекин кофир кимсалар Аллоҳ шаънига ёлғон тўқимоқдалар. Уларнинг кўплари ақлсиздирлар'}, {'chapter': 5, 'verse': 104, 'text': 'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ قَالُواْ حَسۡبُنَا مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡلَمُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ\nҚачон уларга: «Аллоҳ нозил қилган нарсага ва пайғамбарга келинглар», дейилса, улар: «Биз учун ота-боболаримизни ниманинг (яъни, қайси диннинг) устида топган бўлсак, ўша етарли», дейишади. Агар ота-боболари ҳеч нарсани билмайдиган ва ҳақ йўлни топа олмайдиган бўлсалар ҳам-а'}, {'chapter': 5, 'verse': 105, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ عَلَيۡكُمۡ أَنفُسَكُمۡۖ لَا يَضُرُّكُم مَّن ضَلَّ إِذَا ٱهۡتَدَيۡتُمۡۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nЭй мўминлар, ўзингизни билингиз! (Яъни, гуноҳлардан сақланингиз!) Модомики, ҳақ йўлни тутган экансиз, адашган кимсалар сизларга зарар етказа олмас. Барчангиз ҳам Аллоҳга қайтишингиз бордир. Ана ўшанда Аллоҳ сизларга қилиб ўтган амалларингизнинг хабарини берур'}, {'chapter': 5, 'verse': 106, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ شَهَٰدَةُ بَيۡنِكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ حِينَ ٱلۡوَصِيَّةِ ٱثۡنَانِ ذَوَا عَدۡلٖ مِّنكُمۡ أَوۡ ءَاخَرَانِ مِنۡ غَيۡرِكُمۡ إِنۡ أَنتُمۡ ضَرَبۡتُمۡ فِي ٱلۡأَرۡضِ فَأَصَٰبَتۡكُم مُّصِيبَةُ ٱلۡمَوۡتِۚ تَحۡبِسُونَهُمَا مِنۢ بَعۡدِ ٱلصَّلَوٰةِ فَيُقۡسِمَانِ بِٱللَّهِ إِنِ ٱرۡتَبۡتُمۡ لَا نَشۡتَرِي بِهِۦ ثَمَنٗا وَلَوۡ كَانَ ذَا قُرۡبَىٰ وَلَا نَكۡتُمُ شَهَٰدَةَ ٱللَّهِ إِنَّآ إِذٗا لَّمِنَ ٱلۡأٓثِمِينَ\nЭй мўминлар, бировингизга ўлим келганда — васият қилар вақтида ўзингиздан (яъни, мусулмонлардан) бўлган икки адодат эгаси ёки агар бирор Ерга сафар қилиб, ўша Ерда сизга ўлим етган бўлса, ўзгалардан бўлган икки киши ўрталарингизда гувоҳ бўлсин! У гувоҳларни намоз ўқиб чиққанларидан кейин тутасизлар. Агар (уларнинг адолат қилишига) шубҳа қилсангиз, улар Аллоҳ номига бундай қасам ичадилар: «Гарчи гувоҳлик берилувчи, яъни меросхўр қариндошимиз бўлса-да, бу қасамимизни озгина қийматга алмаштирмаймиз ва Аллоҳ учун берган гувоҳлигимизни яширмаймиз. Зотан у ҳолда биз ҳеч шак-шубҳасиз, гуноҳкорлардан бўлиб қоламиз»'}, {'chapter': 5, 'verse': 107, 'text': 'فَإِنۡ عُثِرَ عَلَىٰٓ أَنَّهُمَا ٱسۡتَحَقَّآ إِثۡمٗا فَـَٔاخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ ٱلَّذِينَ ٱسۡتَحَقَّ عَلَيۡهِمُ ٱلۡأَوۡلَيَٰنِ فَيُقۡسِمَانِ بِٱللَّهِ لَشَهَٰدَتُنَآ أَحَقُّ مِن شَهَٰدَتِهِمَا وَمَا ٱعۡتَدَيۡنَآ إِنَّآ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ\nАгар уларнинг гуноҳкор бўлганлари (яъни, ёлғон гувоҳлик берганлари) маълум бўлиб қолса, у ҳолда уларнинг ўринларига турадиган, меросга ҳақдор кишилардан бўлган бошқа икки киши (гувоҳлик бериш учун) энг лойиқ кишилардир. Бас, бу иккови ҳам: «Бизнинг берган гувоҳлигимиз уларнинг гувоҳлигидан тўғрироқ, биз (бу гувоҳлик беришда) ҳақдан тажовуз қилганимиз йўқ. Чунки у ҳолда биз ҳеч шак-шубҳасиз, золимлардан бўлиб қолурмиз», деб Аллоҳ номига қасам ичадилар'}, {'chapter': 5, 'verse': 108, 'text': 'ذَٰلِكَ أَدۡنَىٰٓ أَن يَأۡتُواْ بِٱلشَّهَٰدَةِ عَلَىٰ وَجۡهِهَآ أَوۡ يَخَافُوٓاْ أَن تُرَدَّ أَيۡمَٰنُۢ بَعۡدَ أَيۡمَٰنِهِمۡۗ وَٱتَّقُواْ ٱللَّهَ وَٱسۡمَعُواْۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\nМана шу рост гувоҳлик беришларига ёки қасамларидан кейин яна қайта қасамлар ичилишидан (яъни, шарманда бўлишларидан) қўрқишларига яқинроқ ҳукмдир. (Эй мўминлар), Аллоҳдан қўрқингиз ва Унга қулоқ тутингиз! Зотан, Аллоҳ итоатсиз қавмни ҳақ йўлга ҳидоят қилмас'}, {'chapter': 5, 'verse': 109, 'text': '۞يَوۡمَ يَجۡمَعُ ٱللَّهُ ٱلرُّسُلَ فَيَقُولُ مَاذَآ أُجِبۡتُمۡۖ قَالُواْ لَا عِلۡمَ لَنَآۖ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُيُوبِ\nАллоҳ пайғамбарларини тўплаб: «Сизларга (яъни, қилган даъватингизга умматларингиз томонидан) қандай жавоб бўлди?», деб сўрайдиган кунида, улар: «Биз билмаймиз. Фақат Сенинг Ўзинггина ғайб илмларининг билимдонисан», дейдилар'}, {'chapter': 5, 'verse': 110, 'text': 'إِذۡ قَالَ ٱللَّهُ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ ٱذۡكُرۡ نِعۡمَتِي عَلَيۡكَ وَعَلَىٰ وَٰلِدَتِكَ إِذۡ أَيَّدتُّكَ بِرُوحِ ٱلۡقُدُسِ تُكَلِّمُ ٱلنَّاسَ فِي ٱلۡمَهۡدِ وَكَهۡلٗاۖ وَإِذۡ عَلَّمۡتُكَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَۖ وَإِذۡ تَخۡلُقُ مِنَ ٱلطِّينِ كَهَيۡـَٔةِ ٱلطَّيۡرِ بِإِذۡنِي فَتَنفُخُ فِيهَا فَتَكُونُ طَيۡرَۢا بِإِذۡنِيۖ وَتُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ بِإِذۡنِيۖ وَإِذۡ تُخۡرِجُ ٱلۡمَوۡتَىٰ بِإِذۡنِيۖ وَإِذۡ كَفَفۡتُ بَنِيٓ إِسۡرَـٰٓءِيلَ عَنكَ إِذۡ جِئۡتَهُم بِٱلۡبَيِّنَٰتِ فَقَالَ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ айтган эди: «Эй Ийсо бинни Марям, сенга ва волидангга берган неъматимни — Сени Руҳул-Қудс (Жаброил) билан қувватлантирганимда, одамларга бешикда (гўдаклигингда) ҳам балоғатга етган ҳолингдаги каби сўзлаганингни, сенга китоб — хат ва ҳикмат — илмни, Таврот ва Инжилни билдирганимни ва Менинг изним-ихтиёрим билаи лойдан қуш тимсолини ясаганингни, сўнгра унга пуфлаганингда, Менинг изним-ихтиёрим билан ростакам қушга айланганини, Менинг изним-ихтиёрим билан кўр ва песларни тузатганингни ва Менинг изним-ихтиёрим билан ўликларни (тирилтириб, қабрларидан) чиқарганингни эслагин! Яна Бани Исроилга бу мўъжизаларни келтирган пайтингда уларни сени (ҳалок қилишдан) тўсганимни эслагин! Ўшанда уларнинг ораларидаги кофир бўлган кимса: «Бу очиқдан-очиқ сеҳрдан ўзга нарса эмас», дейишган эди'}, {'chapter': 5, 'verse': 111, 'text': 'وَإِذۡ أَوۡحَيۡتُ إِلَى ٱلۡحَوَارِيِّـۧنَ أَنۡ ءَامِنُواْ بِي وَبِرَسُولِي قَالُوٓاْ ءَامَنَّا وَٱشۡهَدۡ بِأَنَّنَا مُسۡلِمُونَ\nЯна ҳаворийларга (сен орқали): «Менга ва пайғамбаримга иймон келтиринглар», деб ваҳий юборганимда, улар: «Иймон келтирдик. (Эй Парвардигор), бизнинг итоат қилгувчилар эканимизга Ўзинг шоҳид бўл», деганларини эслагин'}, {'chapter': 5, 'verse': 112, 'text': 'إِذۡ قَالَ ٱلۡحَوَارِيُّونَ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ هَلۡ يَسۡتَطِيعُ رَبُّكَ أَن يُنَزِّلَ عَلَيۡنَا مَآئِدَةٗ مِّنَ ٱلسَّمَآءِۖ قَالَ ٱتَّقُواْ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِينَ\nҲаворийлар: «Эй Ийсо бинни Марям, Парвардигоринг бизга осмондан бир дастурхон нозил қилишга қодирми?», деган пайтларида (Ийсо уларга): «Агар мўмин бўлсангизлар, Аллоҳдан қўрқинглар», деб жавоб қилди'}, {'chapter': 5, 'verse': 113, 'text': 'قَالُواْ نُرِيدُ أَن نَّأۡكُلَ مِنۡهَا وَتَطۡمَئِنَّ قُلُوبُنَا وَنَعۡلَمَ أَن قَدۡ صَدَقۡتَنَا وَنَكُونَ عَلَيۡهَا مِنَ ٱلشَّـٰهِدِينَ\nУлар дедилар: «Биз (фақатгина) у дастурхондан ейишни ва кўнгилларимиз таскин топишини ҳамда сенинг рост сўзлаганингни аниқ билиб, у дастурхонга гувоҳлик берувчилардан бўлишни истаймиз»'}, {'chapter': 5, 'verse': 114, 'text': 'قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ ٱللَّهُمَّ رَبَّنَآ أَنزِلۡ عَلَيۡنَا مَآئِدَةٗ مِّنَ ٱلسَّمَآءِ تَكُونُ لَنَا عِيدٗا لِّأَوَّلِنَا وَءَاخِرِنَا وَءَايَةٗ مِّنكَۖ وَٱرۡزُقۡنَا وَأَنتَ خَيۡرُ ٱلرَّـٰزِقِينَ\n(Шунда) Ийсо бинни Марям: «Аллоҳим, Парвардигоро, бизга осмондан бир дастурхон нозил қилгинки, у аввалу охиримиз учун байрам ва Сенинг оят-мўъжизанг бўлиб қолгай. Бизни (шу неъматдан) баҳраманд қилгил. Ўзинг энг яхши ризқ бергувчисан», деди'}, {'chapter': 5, 'verse': 115, 'text': 'قَالَ ٱللَّهُ إِنِّي مُنَزِّلُهَا عَلَيۡكُمۡۖ فَمَن يَكۡفُرۡ بَعۡدُ مِنكُمۡ فَإِنِّيٓ أُعَذِّبُهُۥ عَذَابٗا لَّآ أُعَذِّبُهُۥٓ أَحَدٗا مِّنَ ٱلۡعَٰلَمِينَ\nШунда Аллоҳ айтди: «Мен уни сизларга туширгувчиман. Аммо шундан кейин сизлардан кимда-ким кофир бўлса, уни шундай азобга гирифтор қиламанки, бутун оламлардан бирон одамни ундай азобламагайман»'}, {'chapter': 5, 'verse': 116, 'text': 'وَإِذۡ قَالَ ٱللَّهُ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ ءَأَنتَ قُلۡتَ لِلنَّاسِ ٱتَّخِذُونِي وَأُمِّيَ إِلَٰهَيۡنِ مِن دُونِ ٱللَّهِۖ قَالَ سُبۡحَٰنَكَ مَا يَكُونُ لِيٓ أَنۡ أَقُولَ مَا لَيۡسَ لِي بِحَقٍّۚ إِن كُنتُ قُلۡتُهُۥ فَقَدۡ عَلِمۡتَهُۥۚ تَعۡلَمُ مَا فِي نَفۡسِي وَلَآ أَعۡلَمُ مَا فِي نَفۡسِكَۚ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُيُوبِ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ: «Эй Ийсо бинни Марям, одамларга: «Аллоҳни қўйиб, мени ва онамни худо қилиб олинглар», деб сен айтдингми?», деганида, (Ийсо) айтди: «Эй пок Парвардигор, ҳаққим бўлмаган нарсани айтиш мен учун дуруст эмас-ку. Агар айтган бўлганимда, Сен албатта билар эдинг. Зотан Сен дилимдаги бор нарсани билурсан. Аммо мен Сенинг ҳузурингдаги ҳеч нарсани билмасман. Фақат Сен Ўзинг ғайб илмларининг билимдонисан'}, {'chapter': 5, 'verse': 117, 'text': 'مَا قُلۡتُ لَهُمۡ إِلَّا مَآ أَمَرۡتَنِي بِهِۦٓ أَنِ ٱعۡبُدُواْ ٱللَّهَ رَبِّي وَرَبَّكُمۡۚ وَكُنتُ عَلَيۡهِمۡ شَهِيدٗا مَّا دُمۡتُ فِيهِمۡۖ فَلَمَّا تَوَفَّيۡتَنِي كُنتَ أَنتَ ٱلرَّقِيبَ عَلَيۡهِمۡۚ وَأَنتَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nМен уларга фақат Ўзинг амр қилган гапнигина айтдим: «Парвардигорим ва Парвардигорингиз бўлмиш Аллоҳга ибодат қилингиз!» Ва ораларида бўлган муддатимда уларнинг устида гувоҳ бўлиб турдим. Мени Ўз ҳузурингга чорлаганингдан кейин эса, Сен Ўзинг уларнинг устида кузатувчи бўлдинг. Сен Ўзинг ҳамма нарсага гувоҳдирсан'}, {'chapter': 5, 'verse': 118, 'text': 'إِن تُعَذِّبۡهُمۡ فَإِنَّهُمۡ عِبَادُكَۖ وَإِن تَغۡفِرۡ لَهُمۡ فَإِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАгар уларни азобласанг, улар Сенинг ожиз бандаларинг. Агар уларни мағфират қилсанг, албатта Сен Ўзинг қудрат, ҳикмат Эгасидирсан»'}, {'chapter': 5, 'verse': 119, 'text': 'قَالَ ٱللَّهُ هَٰذَا يَوۡمُ يَنفَعُ ٱلصَّـٰدِقِينَ صِدۡقُهُمۡۚ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАллоҳ айтди: «Бу кун (яъни, Қиёмат Куни) ҳаққи-рост иймон келтирганларга рост сўзлари фойда берадиган кундир». Улар учун остидан дарёлар оқиб турадиган жаннатлар бордир. Улар унда абадий қолажаклар. Улардан Аллоҳ рози бўлди, улар ҳам Аллоҳдан рози бўлдилар. Бу эса улуғ бахтдир'}, {'chapter': 5, 'verse': 120, 'text': 'لِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا فِيهِنَّۚ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرُۢ\nОсмонлар ва Ер ҳамда улардаги бор нарса Аллоҳнинг мулкидир. У ҳамма нарсага қодирдир'}, {'chapter': 6, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَجَعَلَ ٱلظُّلُمَٰتِ وَٱلنُّورَۖ ثُمَّ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡ يَعۡدِلُونَ\nҲамду сано осмонлар ва Ерни яратган, зулматлар ва нурни таратган Аллоҳ учундир. Сўнгра (яъни, Унинг шундай ягона эканлиги ҳақидаги ҳужжатларни кўра туриб) кофир бўлган кимсалар Парвардигорларига (ўзгаларни) тенглаштирадилар'}, {'chapter': 6, 'verse': 2, 'text': 'هُوَ ٱلَّذِي خَلَقَكُم مِّن طِينٖ ثُمَّ قَضَىٰٓ أَجَلٗاۖ وَأَجَلٞ مُّسَمًّى عِندَهُۥۖ ثُمَّ أَنتُمۡ تَمۡتَرُونَ\nУ сизларни (Отангиз Одам Атони) лойдан яратиб, сўнгра ажални (яъни, тириклик муддатини) белгилаб қўйган Зотдир. Унинг ҳузурида яна бир белгиланган ажал (яъни, тирилиш муддати) бордир. Шундан кейин ҳам сизлар (эй кофирлар) шак-шубҳа қилмоқдасиз'}, {'chapter': 6, 'verse': 3, 'text': 'وَهُوَ ٱللَّهُ فِي ٱلسَّمَٰوَٰتِ وَفِي ٱلۡأَرۡضِ يَعۡلَمُ سِرَّكُمۡ وَجَهۡرَكُمۡ وَيَعۡلَمُ مَا تَكۡسِبُونَ\nУ осмонлару Ердаги якка-ю ягона Аллоҳдирки, сизларнинг яширган ва ошкор қилган нарсангизни ҳам, қилган касбу корингизни ҳам билур'}, {'chapter': 6, 'verse': 4, 'text': 'وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ\nУларга (яъни, кофирларга) Парвардигорларининг оятларидан бирон оят келса, албатта, ундан юз ўгирувчи бўлдилар'}, {'chapter': 6, 'verse': 5, 'text': 'فَقَدۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَسَوۡفَ يَأۡتِيهِمۡ أَنۢبَـٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУларга ҳақ (Қуръон) келганда уни ҳам ёлғон дедилар. Энди яқинда уларга ўзлари масхара қилувчи бўлган нарсанинг хабарлари келажак (яъни, Қуръонда айтилган хабарлар ҳақ эканлигини билиб оладилар)'}, {'chapter': 6, 'verse': 6, 'text': 'أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ مَّكَّنَّـٰهُمۡ فِي ٱلۡأَرۡضِ مَا لَمۡ نُمَكِّن لَّكُمۡ وَأَرۡسَلۡنَا ٱلسَّمَآءَ عَلَيۡهِم مِّدۡرَارٗا وَجَعَلۡنَا ٱلۡأَنۡهَٰرَ تَجۡرِي مِن تَحۡتِهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ\nУлардан илгари неча асрларни (замонларни) ҳалок қилганимизни кўрмадиларми?! Уларга (яъни, ўша асрларда яшаган одамларга) Ерда сизларга бермаган жойларни берган, устларидан самони (яъни, ёмғирни) сероб қилиб юборган, остларидан дарёларни оқизиб қўйган эдик. Бас, гуноҳлари сабабли ўшаларни ҳам ҳалок қилдик ва улардан сўнг яна бошқа асрларни пайдо қилдик'}, {'chapter': 6, 'verse': 7, 'text': 'وَلَوۡ نَزَّلۡنَا عَلَيۡكَ كِتَٰبٗا فِي قِرۡطَاسٖ فَلَمَسُوهُ بِأَيۡدِيهِمۡ لَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар сизга қоғозга (ёзилган) бир мактуб туширсак ва уни қўллари билан ушлаб кўрсалар ҳам кофир бўлган кимсалар: Бу очиқ сеҳрдан ўзга нарса эмас!, деган бўлур эдилар'}, {'chapter': 6, 'verse': 8, 'text': 'وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ مَلَكٞۖ وَلَوۡ أَنزَلۡنَا مَلَكٗا لَّقُضِيَ ٱلۡأَمۡرُ ثُمَّ لَا يُنظَرُونَ\nУнга (яъни, Муҳаммадга пайғамбарлигини тасдиқ этадиган) бирон фаришта тушса эди, дедилар. Агар фаришта туширсак, иш тугатилган бўлур эди. Сўнгра уларга қараб турилмас — муҳлат берилмас'}, {'chapter': 6, 'verse': 9, 'text': 'وَلَوۡ جَعَلۡنَٰهُ مَلَكٗا لَّجَعَلۡنَٰهُ رَجُلٗا وَلَلَبَسۡنَا عَلَيۡهِم مَّا يَلۡبِسُونَ\nАгар унинг (пайғамбарнинг) ўзини фаришта қилсак ҳам, уни эр киши (яъни, инсон суратида) қилган ва уларга ноаниқ ҳолда турган нарсани яна хиралаштирган бўлур эдик'}, {'chapter': 6, 'verse': 10, 'text': 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиздан аввал ўтган пайғамбарларнинг устидан ҳам кулинган. Сўнг уларни масхара қилган кимсаларни ўша кулгилари ўраб, (домига тортиб) кетган (яъни, ҳалок қилган)'}, {'chapter': 6, 'verse': 11, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ ثُمَّ ٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\nАйтинг: Ерни айланиб сайру саёҳат қилинглар, сўнгра (пайғамбарларни) ёлғончи қилгувчиларнинг оқибати қандоқ бўлганини кўринглар'}, {'chapter': 6, 'verse': 12, 'text': 'قُل لِّمَن مَّا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُل لِّلَّهِۚ كَتَبَ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۚ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ\nАйтинг (сўранг): «Осмонлар ва Ердаги нарсалар кимники?» Айтинг: «Аллоҳники». У Ўз устига осмону Ердаги бор мавжудотга раҳмат-марҳамат қилишни ёзиб қўйгандир. У албатта сизларни ҳеч шак-шубҳасиз, бўлган Қиёмат Кунига тўплайди. Фақат ўзларига зиён қилган кимсаларгина иймон келтирмайдилар'}, {'chapter': 6, 'verse': 13, 'text': '۞وَلَهُۥ مَا سَكَنَ فِي ٱلَّيۡلِ وَٱلنَّهَارِۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nКеча ва кундузга жо бўлган бор нарса Унингдир. У эшитгувчи, билгувчидир'}, {'chapter': 6, 'verse': 14, 'text': 'قُلۡ أَغَيۡرَ ٱللَّهِ أَتَّخِذُ وَلِيّٗا فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَهُوَ يُطۡعِمُ وَلَا يُطۡعَمُۗ قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَكُونَ أَوَّلَ مَنۡ أَسۡلَمَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ\nАйтинг: «Ўзи ризқланмай (таомланмай барчани таомлантирган) ҳолида осмонлар ва Ернинг яратгувчиси бўлган Аллоҳдан ўзгани дўст тутайми?!» Айтинг: «Албатта, мен Аллоҳга бўйинсунгувчи кишиларнинг аввалгиси бўлишга маъмурман (амр қилинганман)». Ҳаргиз мушриклардан бўлманг'}, {'chapter': 6, 'verse': 15, 'text': 'قُلۡ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ\nАйтинг: «Албатта, мен агар Парвардигоримга итоатсизлик қилсам, Улуғ Кунда (Қиёматда Аллоҳ томонидан) бўладиган азобдан қўрқаман»'}, {'chapter': 6, 'verse': 16, 'text': 'مَّن يُصۡرَفۡ عَنۡهُ يَوۡمَئِذٖ فَقَدۡ رَحِمَهُۥۚ وَذَٰلِكَ ٱلۡفَوۡزُ ٱلۡمُبِينُ\nЎша кунда кимдан (азоб) четлатилса, бас, унга Аллоҳ раҳм қилибди. Мана шу очиқ нажотдир'}, {'chapter': 6, 'verse': 17, 'text': 'وَإِن يَمۡسَسۡكَ ٱللَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يَمۡسَسۡكَ بِخَيۡرٖ فَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАгар Аллоҳ сизни (камбағаллик, хасталик каби) бирон зиён билан ушласа, бас, уни фақат Ўзигина аритгувчидир. Агар сизни бирон яхшилик билан ушласа, бас, билингки, У ҳамма нарсага қодирдир'}, {'chapter': 6, 'verse': 18, 'text': 'وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ\nУнинг ўзи бандалари устида ғолибдир. Унинг Ўзи ҳикмат соҳиби (ҳар нарсадан) огоҳдир'}, {'chapter': 6, 'verse': 19, 'text': 'قُلۡ أَيُّ شَيۡءٍ أَكۡبَرُ شَهَٰدَةٗۖ قُلِ ٱللَّهُۖ شَهِيدُۢ بَيۡنِي وَبَيۡنَكُمۡۚ وَأُوحِيَ إِلَيَّ هَٰذَا ٱلۡقُرۡءَانُ لِأُنذِرَكُم بِهِۦ وَمَنۢ بَلَغَۚ أَئِنَّكُمۡ لَتَشۡهَدُونَ أَنَّ مَعَ ٱللَّهِ ءَالِهَةً أُخۡرَىٰۚ قُل لَّآ أَشۡهَدُۚ قُلۡ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَإِنَّنِي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ\nАйтинг (сўранг): «Қай нарса энг улуғ гувоҳ бўлур?» Айтинг: «Аллоҳ». У мен билан сизларнинг ўртамизда гувоҳдир. Сизларни ва Қуръон етиб борган кишиларни (Охират азобидан) огоҳлантиришим учун менга — мана шу Қуръон ваҳий қилинди. Сизлар ҳақиқатан ҳам: «Аллоҳ билан бирга бошқа худолар ҳам бор, деб гувоҳлик бера оласизми? Айтинг: «Мен бундай гувоҳлик бермайман! Айтинг: «Ҳеч шак-шубҳасиз, Аллоҳ якка-ю ягонадир. Албатта мен сизларнинг ширкингиздан покман»'}, {'chapter': 6, 'verse': 20, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمُۘ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ\nБиз китоб ато қилган кимсалар (яъни, яҳудий ва насронийлар) уни (Муҳаммад пайғамбарни) ўз болаларини таниган каби танийдилар. Ўзларига зиён қилган кимсаларгина иймон келтирмайдилар'}, {'chapter': 6, 'verse': 21, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nАллоҳ шаънига ёлғон тўқиган ёки Унинг оятларини ёлғон деган кимсадан ҳам золимроқ ким бор?! Албатта, бундай золимлар нажот топмаслар'}, {'chapter': 6, 'verse': 22, 'text': 'وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُوٓاْ أَيۡنَ شُرَكَآؤُكُمُ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ\nУларнинг барчаларини тўплаб, сўнгра мушрик бўлган кимсаларга сизлар: «(Аллоҳнинг шериклари деб) гумон қилган бутларингиз қани?!», деб сўрайдиган Кунимизни (эсланг)'}, {'chapter': 6, 'verse': 23, 'text': 'ثُمَّ لَمۡ تَكُن فِتۡنَتُهُمۡ إِلَّآ أَن قَالُواْ وَٱللَّهِ رَبِّنَا مَا كُنَّا مُشۡرِكِينَ\nКейин уларнинг алдов-ёлғонлари фақат шу бўлур: «Парвардигоримиз Аллоҳга қасамки, бизлар мушрик эмас эдик»'}, {'chapter': 6, 'verse': 24, 'text': 'ٱنظُرۡ كَيۡفَ كَذَبُواْ عَلَىٰٓ أَنفُسِهِمۡۚ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), улар (барча сирларни билгувчи бўлган Аллоҳ таоло ҳузурида ёлғон сўзлаш билан) ўзларини қандай алдаганликларини ва тўқиб олган «худолари» йўқ бўлиб қолганлигини кўринг'}, {'chapter': 6, 'verse': 25, 'text': 'وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَۖ وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَاۖ حَتَّىٰٓ إِذَا جَآءُوكَ يُجَٰدِلُونَكَ يَقُولُ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nУлардан сизга қулоқ тутадиганлари ҳам бор, (лекин) Биз уни (Қуръонни) англамасликлари учун дилларини пардалаб, қулоқларини оғир қилиб қўйдик. Улар бор оятларни (мўъжизаларни) кўрсалар ҳам, у (оят)ларга иймон келтирмайдилар. Ҳаттоки сиз билан жанжаллашгани олдингизга келган пайтларида ҳам, кофир бўлган кимсалар: «Бу (Қуръон) фақат аввалгилардан қолган афсоналар, холос», дейдилар'}, {'chapter': 6, 'verse': 26, 'text': 'وَهُمۡ يَنۡهَوۡنَ عَنۡهُ وَيَنۡـَٔوۡنَ عَنۡهُۖ وَإِن يُهۡلِكُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ\nУлар (ўзгаларни) ундан (Қуръондан) қайтарадилар ва ўзлари ҳам ундан четланадилар. Улар ўзлари сезмаган ҳолларида фақат ўзларини ҳалок қиладилар, холос'}, {'chapter': 6, 'verse': 27, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ وُقِفُواْ عَلَى ٱلنَّارِ فَقَالُواْ يَٰلَيۡتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِـَٔايَٰتِ رَبِّنَا وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларни дўзах устида турғазиб қўйилган пайтларида: «Қани энди бизлар (дунёга) қайтарилсак ва Парвардигоримизнинг оятларини ёлғон демай, мўминлардан бўлсак», деганларини кўрсангиз эди'}, {'chapter': 6, 'verse': 28, 'text': 'بَلۡ بَدَا لَهُم مَّا كَانُواْ يُخۡفُونَ مِن قَبۡلُۖ وَلَوۡ رُدُّواْ لَعَادُواْ لِمَا نُهُواْ عَنۡهُ وَإِنَّهُمۡ لَكَٰذِبُونَ\nЙўқ, (иймонга келмайдилар. Фақат ҳозир — Қиёмат Куни) уларга илгари яшириб юрган нарсалари ошкор бўлиб қолди, холос. Агар (дунёга) қайтарилсалар, шубҳасиз, ўзлари, манъ қилинган ишларга қайтадилар — албатта улар ёлғончидирлар'}, {'chapter': 6, 'verse': 29, 'text': 'وَقَالُوٓاْ إِنۡ هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا وَمَا نَحۡنُ بِمَبۡعُوثِينَ\nУлар: «Ҳаёт фақат дунёдаги ҳаётимиздир. Биз ҳеч қайта тирилгувчи эмасмиз», деб айтадилар'}, {'chapter': 6, 'verse': 30, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ وُقِفُواْ عَلَىٰ رَبِّهِمۡۚ قَالَ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۚ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сиз уларни Парвардигорлари ҳузурида турғазиб қўйилган пайтларида, Аллоҳ: «Мана шу тирилиш ҳақ эмасми?!», деганида, улар: «Парвардигоримизга қасамки, ҳақ экан», деганларини, Аллоҳ эса: «Мана энди кофир бўлиб ўтганингиз сабабли азобни тотиб кўрингиз!», деб жавоб қилганини кўрсангиз эди'}, {'chapter': 6, 'verse': 31, 'text': 'قَدۡ خَسِرَ ٱلَّذِينَ كَذَّبُواْ بِلِقَآءِ ٱللَّهِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمُ ٱلسَّاعَةُ بَغۡتَةٗ قَالُواْ يَٰحَسۡرَتَنَا عَلَىٰ مَا فَرَّطۡنَا فِيهَا وَهُمۡ يَحۡمِلُونَ أَوۡزَارَهُمۡ عَلَىٰ ظُهُورِهِمۡۚ أَلَا سَآءَ مَا يَزِرُونَ\nТо тўсатдан Қиёмат келиб қолганда, устларига гуноҳларини орқалаб олган ҳолларида: «У (дунё)да қилган сусткашлигимизга ҳасрат-надоматлар бўлғай», деб қоладиган пайтларигача Аллоҳга рўбарў бўлишни ёлғон деб юрган кимсалар ўзларига зиён қиладилар. Улар нақадар ёмон нарсани орқалаб юрадилар-а'}, {'chapter': 6, 'verse': 32, 'text': 'وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا لَعِبٞ وَلَهۡوٞۖ وَلَلدَّارُ ٱلۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ\nБу ҳаёти дунё фақат (бир нафаслик) ўйин-кулгидир, холос. Албатта, Аллоҳдан қўрқадиган кишилар учун Охират диёри яхшироқдир. Ақл юргазмайсизларми'}, {'chapter': 6, 'verse': 33, 'text': 'قَدۡ نَعۡلَمُ إِنَّهُۥ لَيَحۡزُنُكَ ٱلَّذِي يَقُولُونَۖ فَإِنَّهُمۡ لَا يُكَذِّبُونَكَ وَلَٰكِنَّ ٱلظَّـٰلِمِينَ بِـَٔايَٰتِ ٱللَّهِ يَجۡحَدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларнинг гаплари сизни маҳзун қилишини билурмиз. Зотан, улар сизни ёлғончи қилмайдилар, балки бу золимлар Аллоҳнинг оятларини инкор қиладилар'}, {'chapter': 6, 'verse': 34, 'text': 'وَلَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَ فَصَبَرُواْ عَلَىٰ مَا كُذِّبُواْ وَأُوذُواْ حَتَّىٰٓ أَتَىٰهُمۡ نَصۡرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَٰتِ ٱللَّهِۚ وَلَقَدۡ جَآءَكَ مِن نَّبَإِيْ ٱلۡمُرۡسَلِينَ\n(Маълумки, сиздан аввалги пайғамбарлар ҳам ёлғончи қилинганлар. Бас, то уларга Бизнинг ёрдамимиз келгунича ёлғончи қилинганлари ва озорланганларига сабр қилганлар. Аллоҳнинг ?»Пайғамбарларимни Ўзим қўллагайман», деган) сўзларини ўзгартира олгувчи бирон кимса йўқдир. Ахир сизга (Эй Муҳаммад алайҳис-салоту вас-салом), ўтган пайғамбарларнинг айрим хабарлари келган-ку'}, {'chapter': 6, 'verse': 35, 'text': 'وَإِن كَانَ كَبُرَ عَلَيۡكَ إِعۡرَاضُهُمۡ فَإِنِ ٱسۡتَطَعۡتَ أَن تَبۡتَغِيَ نَفَقٗا فِي ٱلۡأَرۡضِ أَوۡ سُلَّمٗا فِي ٱلسَّمَآءِ فَتَأۡتِيَهُم بِـَٔايَةٖۚ وَلَوۡ شَآءَ ٱللَّهُ لَجَمَعَهُمۡ عَلَى ٱلۡهُدَىٰۚ فَلَا تَكُونَنَّ مِنَ ٱلۡجَٰهِلِينَ\nАгар сизга уларнинг юз ўгиришлари оғир ботиб (сабр қила олмасангиз), у ҳолда Ерга (кириб кетадиган) бирон тешик ёки осмонга (чиқиб кетадиган) бирон нарвон истаб, уларга оят-мўъжиза (топиб) келтиришга қодир бўлсангиз (шундай қилаверинг). Агар Аллоҳ хоҳлаганда албатта уларни ҳидоят устида бирлаштирган бўлур эди-ку! Бас, ҳаргиз жоҳиллардан бўлманг'}, {'chapter': 6, 'verse': 36, 'text': '۞إِنَّمَا يَسۡتَجِيبُ ٱلَّذِينَ يَسۡمَعُونَۘ وَٱلۡمَوۡتَىٰ يَبۡعَثُهُمُ ٱللَّهُ ثُمَّ إِلَيۡهِ يُرۡجَعُونَ\nАлбатта, эшита оладиган зотларгина (сизнинг даъватингизни) қабул қилурлар. Ўликларни (кофирларни) эса Аллоҳ тирилтирур. Сўнгра Ўзига қайтарилурлар'}, {'chapter': 6, 'verse': 37, 'text': 'وَقَالُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ قَادِرٌ عَلَىٰٓ أَن يُنَزِّلَ ءَايَةٗ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\n(Мушриклар): «Унга (яъни, Муҳаммадга) Парвардигоридан бирон оят-мўъжиза туширилса эди», дедилар. Айтинг: «Албатта, Аллоҳ (улар истаган) оятни туширишга қодирдир. Лекин уларнинг кўплари (ўша мўъжиза тушганидан кейин ҳам инкор қилсалар, қандай бало келишини) билмайдилар»'}, {'chapter': 6, 'verse': 38, 'text': 'وَمَا مِن دَآبَّةٖ فِي ٱلۡأَرۡضِ وَلَا طَـٰٓئِرٖ يَطِيرُ بِجَنَاحَيۡهِ إِلَّآ أُمَمٌ أَمۡثَالُكُمۚ مَّا فَرَّطۡنَا فِي ٱلۡكِتَٰبِ مِن شَيۡءٖۚ ثُمَّ إِلَىٰ رَبِّهِمۡ يُحۡشَرُونَ\nЕрда судралиб юрган ҳар бир жонивор, осмонда қанот қоқаётган ҳар бир қуш худди сизлар каби (Бизнинг қўл остимиздаги) жамоалардир. Китобда (яъни, тақдири азал китобида) бирон нарсани қўймай (ёзганмиз). Кейин ҳаммалари Парвардигорлари даргоҳига тўпланурлар'}, {'chapter': 6, 'verse': 39, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا صُمّٞ وَبُكۡمٞ فِي ٱلظُّلُمَٰتِۗ مَن يَشَإِ ٱللَّهُ يُضۡلِلۡهُ وَمَن يَشَأۡ يَجۡعَلۡهُ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nБизнинг оятларимизни ёлғон деган кимсалар зулматларда қолган кар-соқовдирлар. Аллоҳ хоҳлаган кишисини адаштирур, хоҳлаган кишисини Тўғри Йўлда (барқарор) қилур'}, {'chapter': 6, 'verse': 40, 'text': 'قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ أَوۡ أَتَتۡكُمُ ٱلسَّاعَةُ أَغَيۡرَ ٱللَّهِ تَدۡعُونَ إِن كُنتُمۡ صَٰدِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар сизлар ?»Аллоҳдан ўзга илоҳлар бор», деган сўзингизда) ростгўй бўлсангизлар, хабар берингиз-чи, агар сизларга Аллоҳнинг азоби келса ёки Қиёмат қойим бўлса, Аллоҳдан ўзгага дуо-илтижо қиласизларми'}, {'chapter': 6, 'verse': 41, 'text': 'بَلۡ إِيَّاهُ تَدۡعُونَ فَيَكۡشِفُ مَا تَدۡعُونَ إِلَيۡهِ إِن شَآءَ وَتَنسَوۡنَ مَا تُشۡرِكُونَ\nЙўқ, Унинг Ўзигагина илтижо қиласизлар, бас, агар хоҳласа, У зот сизлар дуо-илтижо қилган нарсани (бало-мусибатни) аритур. Аллоҳга шерик деб биладиган бутларингизни эса унутиб юборурсизлар»'}, {'chapter': 6, 'verse': 42, 'text': 'وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَأَخَذۡنَٰهُم بِٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ لَعَلَّهُمۡ يَتَضَرَّعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), маълумки, сиздан аввалги миллатларга ҳам элчилар юборганмиз. (Элчиларимизни улар ёлғончи қилишгач), шояд тавба-тазарру қилсалар, деб уларни бало ва зиёнлар билан ушлаганмиз'}, {'chapter': 6, 'verse': 43, 'text': 'فَلَوۡلَآ إِذۡ جَآءَهُم بَأۡسُنَا تَضَرَّعُواْ وَلَٰكِن قَسَتۡ قُلُوبُهُمۡ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ مَا كَانُواْ يَعۡمَلُونَ\nБизнинг балойимиз етганда ҳам тазарру қилмадиларми?! (Албатта, тавба-тазарру қилишлари лозим эди), лекин уларнинг диллари қотиб қолган ва шайтон қилиб юрган ишларини ўзларига чиройли кўрсатиб қўйгандир'}, {'chapter': 6, 'verse': 44, 'text': 'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦ فَتَحۡنَا عَلَيۡهِمۡ أَبۡوَٰبَ كُلِّ شَيۡءٍ حَتَّىٰٓ إِذَا فَرِحُواْ بِمَآ أُوتُوٓاْ أَخَذۡنَٰهُم بَغۡتَةٗ فَإِذَا هُم مُّبۡلِسُونَ\nЭнди — қачонки улар ўзлари учун эслатма қилиб берилган нарсани унутишгач, Биз уларга ҳамма нарсанинг эшикларини очиб қўйдик. Қачонки ўзларига берилган нарсалар билан шод турганларида, уларни тўсатдан (азоб билан) ушладик. Бас, бутунлай номурод бўлдилар'}, {'chapter': 6, 'verse': 45, 'text': 'فَقُطِعَ دَابِرُ ٱلۡقَوۡمِ ٱلَّذِينَ ظَلَمُواْۚ وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nБас, бутун оламларнинг Парвардигори — Аллоҳга ҳамд бўлсинки, золим қавмнинг думлари қирқилди (ҳалок қилиндилар)'}, {'chapter': 6, 'verse': 46, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَخَذَ ٱللَّهُ سَمۡعَكُمۡ وَأَبۡصَٰرَكُمۡ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِهِۗ ٱنظُرۡ كَيۡفَ نُصَرِّفُ ٱلۡأٓيَٰتِ ثُمَّ هُمۡ يَصۡدِفُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Хабар берингиз-чи, агар Аллоҳ қулоқ ва кўзларингизни тортиб олса ва дилларингизни муҳрлаб қўйса, Аллоҳдан ўзга қайси бир илоҳ у аъзоларни сизларга келтириб берур?» Қаранг, Биз оятларни қандай баён қилмоқдамиз, сўнгра улар юз ўгирмоқдалар'}, {'chapter': 6, 'verse': 47, 'text': 'قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ بَغۡتَةً أَوۡ جَهۡرَةً هَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلظَّـٰلِمُونَ\nАйтинг: «Хабар берингиз-чи, агар сизларга Аллоҳнинг азоби тўсатдан ёки очиқдан-очиқ (яъни, кечаси ёки кундузи) келса, золим қавмдан ўзга биров ҳалок қилинурми?! (Йўқ, фақат золим қавм, яъни сизлар ҳалок қилинурсизлар)'}, {'chapter': 6, 'verse': 48, 'text': 'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۖ فَمَنۡ ءَامَنَ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nБиз пайғамбарларни фақат (жаннат ҳақида) хушхабар элтгувчи ва (дўзах азобидан) огоҳлантиргувчи бўлган ҳоллардагина юборамиз. Бас, кимлар иймон келтириб, (ўзларини) тузатсалар, уларга хавфу хатар йўқдир ва улар ғамгин бўлмаслар'}, {'chapter': 6, 'verse': 49, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا يَمَسُّهُمُ ٱلۡعَذَابُ بِمَا كَانُواْ يَفۡسُقُونَ\nБизнинг оятларимизни ёлғон деган кимсалар эса итоатсиз бўлганлари сабабли азобга гирифтор бўлурлар'}, {'chapter': 6, 'verse': 50, 'text': 'قُل لَّآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ ٱللَّهِ وَلَآ أَعۡلَمُ ٱلۡغَيۡبَ وَلَآ أَقُولُ لَكُمۡ إِنِّي مَلَكٌۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۚ قُلۡ هَلۡ يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُۚ أَفَلَا تَتَفَكَّرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен сизларга: «Ҳузуримда Аллоҳнинг хазиналари мавжуд», демайман ва ғайбни ҳам билмайман. Яна сизларга: «Мен фариштаман», ҳам демайман. Мен фақат ўзимга ваҳий қилинган нарсаларгагина эргашурман. Айтинг: «Кўр билан кўргувчи (яъни, йўлсиз билан Аллоҳнинг йўлида юргувчи) киши баробар бўлурми?!» Тафаккур қилмайсизларми'}, {'chapter': 6, 'verse': 51, 'text': 'وَأَنذِرۡ بِهِ ٱلَّذِينَ يَخَافُونَ أَن يُحۡشَرُوٓاْ إِلَىٰ رَبِّهِمۡ لَيۡسَ لَهُم مِّن دُونِهِۦ وَلِيّٞ وَلَا شَفِيعٞ لَّعَلَّهُمۡ يَتَّقُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), бу (Қуръон) билан ўзлари учун Аллоҳдан ўзга на бир дўст ва на бир оқловчи бўлмаган ҳолда Парвардигорлари даргоҳида тўпланишдан қўрқадиган зотларни қўрқитинг! Шояд улар (Аллоҳдан) қўрқсалар'}, {'chapter': 6, 'verse': 52, 'text': 'وَلَا تَطۡرُدِ ٱلَّذِينَ يَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ مَا عَلَيۡكَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَمَا مِنۡ حِسَابِكَ عَلَيۡهِم مِّن شَيۡءٖ فَتَطۡرُدَهُمۡ فَتَكُونَ مِنَ ٱلظَّـٰلِمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сиз эрта-ю кеч Парвардигорларининг Юзини истаб, Унга илтижо қиладиган зотларни (ҳузурингиздан) ҳайдаманг! Сизнинг зиммангизда уларнинг ҳисоб-китобларидан ҳеч нарса йўқдир ва сизнинг ҳисоб-китобингиздан уларнинг зиммаларида ҳеч нарса йўқдир. Бас, уларни ҳайдаб золимлардан бўлиб қолманг'}, {'chapter': 6, 'verse': 53, 'text': 'وَكَذَٰلِكَ فَتَنَّا بَعۡضَهُم بِبَعۡضٖ لِّيَقُولُوٓاْ أَهَـٰٓؤُلَآءِ مَنَّ ٱللَّهُ عَلَيۡهِم مِّنۢ بَيۡنِنَآۗ أَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِٱلشَّـٰكِرِينَ\n«Бизларнинг орамиздан ана ўшаларга Аллоҳ инъом қилган эмишми?», дейишлари учун уларнинг баъзиларини баьзилари билан ана шундай имтиҳон қилдик. Ахир шукр қилгувчиларни Аллоҳ яхшироқ билгувчи эмас-ми'}, {'chapter': 6, 'verse': 54, 'text': 'وَإِذَا جَآءَكَ ٱلَّذِينَ يُؤۡمِنُونَ بِـَٔايَٰتِنَا فَقُلۡ سَلَٰمٌ عَلَيۡكُمۡۖ كَتَبَ رَبُّكُمۡ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَ أَنَّهُۥ مَنۡ عَمِلَ مِنكُمۡ سُوٓءَۢا بِجَهَٰلَةٖ ثُمَّ تَابَ مِنۢ بَعۡدِهِۦ وَأَصۡلَحَ فَأَنَّهُۥ غَفُورٞ رَّحِيمٞ\nҚачон сизнинг оддингизга Бизнинг оятларимизга иймон келтирадиган зотлар келсалар: «Саламун алайкум — сизларга тинчлик бўлсин, Парвардигорингиз Ўз зиммасига раҳмат — марҳамат қилишни ёзгандир. Сизлардан ким билмасдан бирон ёмонлик (гуноҳ) қилса, сўнгра ўша ёмонлигининг кетидан тавба қилиб, (ўзини) тузатса, бас, (гуноҳи кечирилади), албатта (Аллоҳ) мағфиратли, меҳрибондир», деб айтинг'}, {'chapter': 6, 'verse': 55, 'text': 'وَكَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ وَلِتَسۡتَبِينَ سَبِيلُ ٱلۡمُجۡرِمِينَ\n(Ҳақиқий мўминлар кимлар экани маълум бўлиши) ва жиноятчи кимсаларнинг йўли ошкор бўлиши учун оятларимизни мана шундай муфассал қилурмиз'}, {'chapter': 6, 'verse': 56, 'text': 'قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِۚ قُل لَّآ أَتَّبِعُ أَهۡوَآءَكُمۡ قَدۡ ضَلَلۡتُ إِذٗا وَمَآ أَنَا۠ مِنَ ٱلۡمُهۡتَدِينَ\nАйтинг: «Мен Аллоҳни қўйиб — сизлар илтижо қилаётган бутларга бандалик қилишдан қайтарилганман». Айтинг: «Сизларнинг ҳавойи-нафсларингизга эргашмайман. Зеро, у ҳолда йўлдан озурман ва ҳидоят топгувчилардан бўлмай қолурман'}, {'chapter': 6, 'verse': 57, 'text': 'قُلۡ إِنِّي عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَكَذَّبۡتُم بِهِۦۚ مَا عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦٓۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ يَقُصُّ ٱلۡحَقَّۖ وَهُوَ خَيۡرُ ٱلۡفَٰصِلِينَ\nАйтинг: «Мен Парвардигорим тарафидан (Унинг ёлғиз маъбуд эканлиги хусусида) аниқ ҳужжатга эгаман. Сизлар эса уни ёлғон дедингиз. Сизлар шоштираётган нарса (яъни, Аллоҳнинг азоби) менинг илгимда эмас, ҳукм қилиш фақат Аллоҳнинг Ўзига хосдир. У фақат ҳақни сўйлар. У (ҳақ билан ноҳақни) ажратгувчиларнинг энг яхшисидир»'}, {'chapter': 6, 'verse': 58, 'text': 'قُل لَّوۡ أَنَّ عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦ لَقُضِيَ ٱلۡأَمۡرُ بَيۡنِي وَبَيۡنَكُمۡۗ وَٱللَّهُ أَعۡلَمُ بِٱلظَّـٰلِمِينَ\nАйтинг: «Агар сизлар шоштираётган нарса менинг илгимда бўлганида эди, албатта сизлар билан ўртамиздаги иш тугаган бўлур эди (яъни, мен сизларни дарҳол азоблаган бўлур эдим). Аллоҳ золимларни жуда яхши билгувчидир»'}, {'chapter': 6, 'verse': 59, 'text': '۞وَعِندَهُۥ مَفَاتِحُ ٱلۡغَيۡبِ لَا يَعۡلَمُهَآ إِلَّا هُوَۚ وَيَعۡلَمُ مَا فِي ٱلۡبَرِّ وَٱلۡبَحۡرِۚ وَمَا تَسۡقُطُ مِن وَرَقَةٍ إِلَّا يَعۡلَمُهَا وَلَا حَبَّةٖ فِي ظُلُمَٰتِ ٱلۡأَرۡضِ وَلَا رَطۡبٖ وَلَا يَابِسٍ إِلَّا فِي كِتَٰبٖ مُّبِينٖ\nҒайб очқичлари Унинг ҳузуридадирким, уларни ёлғиз Ўзигина билур. У қуруқлик ва денгиздаги бор нарсаларни билур. Бирон барг (шохидан узилиб) тушмас, магар У билур. Ер тубларидаги ҳар бир дон, бор ҳўлу қуруқ нарса, албатта, Очиқ Китобда (яъни, Лавҳул-Маҳфузда) мавжуддир'}, {'chapter': 6, 'verse': 60, 'text': 'وَهُوَ ٱلَّذِي يَتَوَفَّىٰكُم بِٱلَّيۡلِ وَيَعۡلَمُ مَا جَرَحۡتُم بِٱلنَّهَارِ ثُمَّ يَبۡعَثُكُمۡ فِيهِ لِيُقۡضَىٰٓ أَجَلٞ مُّسَمّٗىۖ ثُمَّ إِلَيۡهِ مَرۡجِعُكُمۡ ثُمَّ يُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nУ кечаси «жонингизни оладиган (яъни, уйқу берадиган), кундузи қилган ишингизни биладиган Зотдир. Сўнгра айтилган ажал (яшаш учун берилган муҳлат) адо қилиниши учун унда (яъни, кундузи) сизларни «тирилтирур». Кейин Ўзига қайтишингиз бордир. Сўнгра сизларга қилиб ўтган амалларингизнинг хабарини берур'}, {'chapter': 6, 'verse': 61, 'text': 'وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۖ وَيُرۡسِلُ عَلَيۡكُمۡ حَفَظَةً حَتَّىٰٓ إِذَا جَآءَ أَحَدَكُمُ ٱلۡمَوۡتُ تَوَفَّتۡهُ رُسُلُنَا وَهُمۡ لَا يُفَرِّطُونَ\nУ бандалари устида ғолиб бўлган Зотдир. Устингизга (қилган амалларингизни) ёдлаб олгувчи фаришталарни элчи қилиб юборур. Қачонки бировингизга ўлим келса, элчиларимиз сусткашлик қилмаган ҳолларида, унинг жонини олурлар'}, {'chapter': 6, 'verse': 62, 'text': 'ثُمَّ رُدُّوٓاْ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۚ أَلَا لَهُ ٱلۡحُكۡمُ وَهُوَ أَسۡرَعُ ٱلۡحَٰسِبِينَ\nСўнгра (бандалар) ҳақиқий Хожалари — Аллоҳга қайтарилурлар. Огоҳ бўлингизким, ҳукм қилиш Унинг Ўзигагина хосдир ва У жуда тез ҳисоб-китоб қилгувчидир'}, {'chapter': 6, 'verse': 63, 'text': 'قُلۡ مَن يُنَجِّيكُم مِّن ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ تَدۡعُونَهُۥ تَضَرُّعٗا وَخُفۡيَةٗ لَّئِنۡ أَنجَىٰنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Қасамки, агар бизга мана шу (балолардан) нажот берса, албатта шукр этгувчилардан бўлурмиз, деб ошкора ва хуфёна тавба-тазарру қилган ҳолда, илтижолар қиладиган пайтингизда, сизларга қуруқлик ва денгиз балоларидан ким нажот берур?»'}, {'chapter': 6, 'verse': 64, 'text': 'قُلِ ٱللَّهُ يُنَجِّيكُم مِّنۡهَا وَمِن كُلِّ كَرۡبٖ ثُمَّ أَنتُمۡ تُشۡرِكُونَ\nАйтинг: «Аллоҳнинг Ўзи сизларга у (бало)лардан ва барча ғамлардан нажот берур. Сўнгра сизлар яна ширк келтирурсиз!»'}, {'chapter': 6, 'verse': 65, 'text': 'قُلۡ هُوَ ٱلۡقَادِرُ عَلَىٰٓ أَن يَبۡعَثَ عَلَيۡكُمۡ عَذَابٗا مِّن فَوۡقِكُمۡ أَوۡ مِن تَحۡتِ أَرۡجُلِكُمۡ أَوۡ يَلۡبِسَكُمۡ شِيَعٗا وَيُذِيقَ بَعۡضَكُم بَأۡسَ بَعۡضٍۗ ٱنظُرۡ كَيۡفَ نُصَرِّفُ ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَفۡقَهُونَ\nАйтинг: «У сизларга устингиздан ё оёқларингиз остидан азоб юборишга, ёки сизларни гуруҳ-гуруҳ қилиб аралаштириб — қориштириб (яъни, бири бирига душман тўдаларга айлантириб) юбориб айримларингизга айримларингизнинг зарарини тотдириб қўйишга қодир бўлган Зотдир». Қаранг, англаб етармиканлар, деб оятларимизни қандай баён қилмоқдамиз'}, {'chapter': 6, 'verse': 66, 'text': 'وَكَذَّبَ بِهِۦ قَوۡمُكَ وَهُوَ ٱلۡحَقُّۚ قُل لَّسۡتُ عَلَيۡكُم بِوَكِيلٖ\nҚавмингиз уни (яъни, Қуръонни) ёлғон деди. Ҳолбуки, у ҳақдир. Уларга айтинг: «Мен устингизда вакил — қўриқчи эмасман'}, {'chapter': 6, 'verse': 67, 'text': 'لِّكُلِّ نَبَإٖ مُّسۡتَقَرّٞۚ وَسَوۡفَ تَعۡلَمُونَ\nҲар бир хабарнинг ўз ўрни бор. Яқинда билажаксиз»'}, {'chapter': 6, 'verse': 68, 'text': 'وَإِذَا رَأَيۡتَ ٱلَّذِينَ يَخُوضُونَ فِيٓ ءَايَٰتِنَا فَأَعۡرِضۡ عَنۡهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦۚ وَإِمَّا يُنسِيَنَّكَ ٱلشَّيۡطَٰنُ فَلَا تَقۡعُدۡ بَعۡدَ ٱلذِّكۡرَىٰ مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nҚачон Бизнинг оятларимизни (масхара қилишга) киришаётган кимсаларни кўрсангиз, то бошқа гапга киришгунларича улардан юз ўгиринг! Энди агар шайтон ёдингиздан чиқарса, эслаганингиздан сўнг бу золим қавм билан бирга ўтирманг'}, {'chapter': 6, 'verse': 69, 'text': 'وَمَا عَلَى ٱلَّذِينَ يَتَّقُونَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَلَٰكِن ذِكۡرَىٰ لَعَلَّهُمۡ يَتَّقُونَ\nТақво қилгувчи зотлар зиммасида у (қавм)нинг ҳисоб-китобидан ҳеч нарса йўқ, лекин улар (оятларимизни масхара қилишдан) сақланишлари учун (тақводор зотлар уларга) эслатиб қўймоқлари (лозимдир)'}, {'chapter': 6, 'verse': 70, 'text': 'وَذَرِ ٱلَّذِينَ ٱتَّخَذُواْ دِينَهُمۡ لَعِبٗا وَلَهۡوٗا وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ وَذَكِّرۡ بِهِۦٓ أَن تُبۡسَلَ نَفۡسُۢ بِمَا كَسَبَتۡ لَيۡسَ لَهَا مِن دُونِ ٱللَّهِ وَلِيّٞ وَلَا شَفِيعٞ وَإِن تَعۡدِلۡ كُلَّ عَدۡلٖ لَّا يُؤۡخَذۡ مِنۡهَآۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ أُبۡسِلُواْ بِمَا كَسَبُواْۖ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ\nДинларини ўйин-ҳазил қилиб олган, ҳаёти дунёга алданиб қолган кимсаларни тарк қилинг! Ва уни (яъни, Қуръонни, одамларга) эслатиб қўйинг! Токи бирон жон (Қиёмат Кунида) касб қилган (гуноҳлари) сабабли ўзи учун Аллоҳдан ўзга на бир дўст ва на бир оқловчи бўлмаган, агар бор нарсани берса ҳам — ундан қабул қилинмайдиган ҳолда, ҳалокатга ташланмасин. Қилган амаллари сабабли (Аллоҳнинг азобига) ташланган ана ўша кимсалар учун қайноқ сувдан иборат ичимлик ва кофир бўлганлари сабабли аламли азоб бордир'}, {'chapter': 6, 'verse': 71, 'text': 'قُلۡ أَنَدۡعُواْ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰٓ أَعۡقَابِنَا بَعۡدَ إِذۡ هَدَىٰنَا ٱللَّهُ كَٱلَّذِي ٱسۡتَهۡوَتۡهُ ٱلشَّيَٰطِينُ فِي ٱلۡأَرۡضِ حَيۡرَانَ لَهُۥٓ أَصۡحَٰبٞ يَدۡعُونَهُۥٓ إِلَى ٱلۡهُدَى ٱئۡتِنَاۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۖ وَأُمِرۡنَا لِنُسۡلِمَ لِرَبِّ ٱلۡعَٰلَمِينَ\nАйтинг: «Аллоҳдан ўзга, бизга фойда ҳам, зиён ҳам келтирмайдиган нарсага илтижо қиламизми ва Аллоҳ бизни ҳидоят қилганидан сўнг яна ортимизга қайтариламизми? Худди жинлар чалиб, лол-ҳайрон ҳолда (номаълум) Ерга олиб кетган ва дўстлари: «Кел», деб ҳидоятга чорлаётган кимсага ўхшаб-а?» Айтинг: «Аллоҳнинг Йўлигина Тўғри Йўлдир. Биз барча оламлар Парвардигорига бўйинсунишга маъмурмиз (амр этилганмиз)»'}, {'chapter': 6, 'verse': 72, 'text': 'وَأَنۡ أَقِيمُواْ ٱلصَّلَوٰةَ وَٱتَّقُوهُۚ وَهُوَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ\n(Бизларга) «Намозни тўкис адо қилингиз ва (Аллоҳдан) қўрқингиз (деб буюрилгандир). У (ҳаммангиз Қиёмат Кунида) ҳузурига тўпланадиган Зотдир'}, {'chapter': 6, 'verse': 73, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ وَيَوۡمَ يَقُولُ كُن فَيَكُونُۚ قَوۡلُهُ ٱلۡحَقُّۚ وَلَهُ ٱلۡمُلۡكُ يَوۡمَ يُنفَخُ فِي ٱلصُّورِۚ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ\nУ осмонлар ва Ерни ҳаққи рост яратган Зотдир. У: «Бўл», деган куни (барча нарса пайдо) бўлур. Унинг сўзи ҳақдир. Сур чалинадиган (яъни, Қиёмат қойим бўладиган) кунда подшоҳлик ёлғиз Уникидир. У ғайб ва шаҳодатни (яъни, яширин ва ошкора нарсаларни) билгувчидир. У ҳикмат соҳиби ва (барча нарсадан) хабардор бўлган Зотдир'}, {'chapter': 6, 'verse': 74, 'text': '۞وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ ءَازَرَ أَتَتَّخِذُ أَصۡنَامًا ءَالِهَةً إِنِّيٓ أَرَىٰكَ وَقَوۡمَكَ فِي ضَلَٰلٖ مُّبِينٖ\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Иброҳим отаси Озарга: «Бутларни худо қилиб оляпсанми? Мен сени ва қавмингни очиқ залолатда деб биламан», деган эди'}, {'chapter': 6, 'verse': 75, 'text': 'وَكَذَٰلِكَ نُرِيٓ إِبۡرَٰهِيمَ مَلَكُوتَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلِيَكُونَ مِنَ ٱلۡمُوقِنِينَ\nШундай қилиб Иброҳимга (ўзи учун ҳужжат қилиб олиши) ва аниқ ишонувчилардан бўлиб қолиши учун осмонлар ва Ер мамлакатларини кўрсатурмиз'}, {'chapter': 6, 'verse': 76, 'text': 'فَلَمَّا جَنَّ عَلَيۡهِ ٱلَّيۡلُ رَءَا كَوۡكَبٗاۖ قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَآ أُحِبُّ ٱلۡأٓفِلِينَ\nБас, қачонки уни тун ўраб олганида юлдузни кўриб: «Мана шу Парвардигорим», деди. У ботиб кетгач: «Ботиб кетгувчиларни суймайман», деди'}, {'chapter': 6, 'verse': 77, 'text': 'فَلَمَّا رَءَا ٱلۡقَمَرَ بَازِغٗا قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَئِن لَّمۡ يَهۡدِنِي رَبِّي لَأَكُونَنَّ مِنَ ٱلۡقَوۡمِ ٱلضَّآلِّينَ\nБас, қачонки чиқаётган ойни кўргач: «Мана шу Парвардигорим», деди. У ҳам ботиб кетгач: «Қасамки, агар Парвардигоримнинг Ўзи мени ҳидоят қилмаса, йўлдан озган қавмдан бўлиб қолурман», деди'}, {'chapter': 6, 'verse': 78, 'text': 'فَلَمَّا رَءَا ٱلشَّمۡسَ بَازِغَةٗ قَالَ هَٰذَا رَبِّي هَٰذَآ أَكۡبَرُۖ فَلَمَّآ أَفَلَتۡ قَالَ يَٰقَوۡمِ إِنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ\nБас, қачонки чиқаётган қуёшни кўргач: «Мана шу Парвардигорим. Мана шу каттароқ-ку!» деди. У ҳам ботиб кетгач: «Эй қавмим, мен сизларнинг ширкингиздан покман'}, {'chapter': 6, 'verse': 79, 'text': 'إِنِّي وَجَّهۡتُ وَجۡهِيَ لِلَّذِي فَطَرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ حَنِيفٗاۖ وَمَآ أَنَا۠ مِنَ ٱلۡمُشۡرِكِينَ\nМен ҳақ йўлга мойил бўлган ҳолимда, юзимни осмонлар ва Ерни яратган Зотга қаратдим ва мен мушриклардан эмасман», деди'}, {'chapter': 6, 'verse': 80, 'text': 'وَحَآجَّهُۥ قَوۡمُهُۥۚ قَالَ أَتُحَـٰٓجُّوٓنِّي فِي ٱللَّهِ وَقَدۡ هَدَىٰنِۚ وَلَآ أَخَافُ مَا تُشۡرِكُونَ بِهِۦٓ إِلَّآ أَن يَشَآءَ رَبِّي شَيۡـٔٗاۚ وَسِعَ رَبِّي كُلَّ شَيۡءٍ عِلۡمًاۚ أَفَلَا تَتَذَكَّرُونَ\nҚавми у билан тортишган эди: «Мен билан Аллоҳ хусусида тортишасизми?! Ахир (Унинг Ўзи) мени ҳидоят қилди-ку?! Мен сизлар Аллоҳга шерик қилган бутларингиздан қўрқмайман. (Чунки у бутлар ҳеч қандай зиён етказа олмас), магар Парвардигорим хоҳлаган нарсагина (бўлур). Парвардигорим барча нарсани Ўз илми билан қамраб олгандир. Эслатма-ибрат олмайсизларми?!», деди'}, {'chapter': 6, 'verse': 81, 'text': 'وَكَيۡفَ أَخَافُ مَآ أَشۡرَكۡتُمۡ وَلَا تَخَافُونَ أَنَّكُمۡ أَشۡرَكۡتُم بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ عَلَيۡكُمۡ سُلۡطَٰنٗاۚ فَأَيُّ ٱلۡفَرِيقَيۡنِ أَحَقُّ بِٱلۡأَمۡنِۖ إِن كُنتُمۡ تَعۡلَمُونَ\n«Сизлар (Аллоҳ) бирон ҳужжат туширмаган бутларни Аллоҳга ширк қилишдан қўрқмайсиз-у, мен сизлар Аллоҳга шерик қилиб олган бутларингиздан қандай қўрқай?! Агар билсангиз (айтинг-чи), қайси гуруҳ (қўрқмай) хотиржам бўлишга ҳақлироқ?»'}, {'chapter': 6, 'verse': 82, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَلَمۡ يَلۡبِسُوٓاْ إِيمَٰنَهُم بِظُلۡمٍ أُوْلَـٰٓئِكَ لَهُمُ ٱلۡأَمۡنُ وَهُم مُّهۡتَدُونَ\nИймон келтирган, ўз иймонларини зулм (ширк) билан аралаштирмаган зотлар — ана ўшалар хотиржам бўлгувчидирлар ва ўшалар ҳидоят топгувчидирлар'}, {'chapter': 6, 'verse': 83, 'text': 'وَتِلۡكَ حُجَّتُنَآ ءَاتَيۡنَٰهَآ إِبۡرَٰهِيمَ عَلَىٰ قَوۡمِهِۦۚ نَرۡفَعُ دَرَجَٰتٖ مَّن نَّشَآءُۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ\nМана шулар Иброҳимга қавми устида берган ҳужжатларимиздир. Биз Ўзимиз хоҳлаган кишиларни (мана шундай баланд) даражаларга кўтарурмиз. Албатта, Парвардигорингиз ҳикмат эгаси ва билгувчидир'}, {'chapter': 6, 'verse': 84, 'text': 'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۚ كُلًّا هَدَيۡنَاۚ وَنُوحًا هَدَيۡنَا مِن قَبۡلُۖ وَمِن ذُرِّيَّتِهِۦ دَاوُۥدَ وَسُلَيۡمَٰنَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَىٰ وَهَٰرُونَۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nУнга (Иброҳимга фарзандлари) Исҳоқ ва Яъқубни ҳадя этдик. Буларнинг барчаларини ҳидоят қилдик. Илгари Нуҳни ҳам ҳидоят қилган эдик. Унинг зурриётидан Довуд, Сулаймон, Айюб, Юсуф, Мусо ва Ҳорунни (ҳам ҳидоят қилдик). Чиройли амал қилгувчиларни мана шундай мукофотлаймиз'}, {'chapter': 6, 'verse': 85, 'text': 'وَزَكَرِيَّا وَيَحۡيَىٰ وَعِيسَىٰ وَإِلۡيَاسَۖ كُلّٞ مِّنَ ٱلصَّـٰلِحِينَ\nЗакариё, Яҳё, Ийсо ва Илёсни (ҳам ҳидоят қилдик). Барчалари солиҳ бандалардандир'}, {'chapter': 6, 'verse': 86, 'text': 'وَإِسۡمَٰعِيلَ وَٱلۡيَسَعَ وَيُونُسَ وَلُوطٗاۚ وَكُلّٗا فَضَّلۡنَا عَلَى ٱلۡعَٰلَمِينَ\nИсмоил, ал-Ясаъ (Юшаъ пайғамбар), Юнус ва Лутни (ҳам ҳидоят қилдик) ва барчаларини бутун оламлардан афзал қилдик'}, {'chapter': 6, 'verse': 87, 'text': 'وَمِنۡ ءَابَآئِهِمۡ وَذُرِّيَّـٰتِهِمۡ وَإِخۡوَٰنِهِمۡۖ وَٱجۡتَبَيۡنَٰهُمۡ وَهَدَيۡنَٰهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nУларнинг ота-боболаридан, зурриётларидан ва биродарларидан (кўпларини ҳам афзал қилдик). Ҳамда уларни сайлаб, Тўғри Йўлга ҳидоят қилдик'}, {'chapter': 6, 'verse': 88, 'text': 'ذَٰلِكَ هُدَى ٱللَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَلَوۡ أَشۡرَكُواْ لَحَبِطَ عَنۡهُم مَّا كَانُواْ يَعۡمَلُونَ\nБу Аллоҳнинг ҳидояти бўлиб, у билан бандаларидан Ўзи хоҳлаган кишиларни ҳидоят қилур. Агар улар мушрик бўлганларида қилган амаллари беҳуда кетган бўлур эди'}, {'chapter': 6, 'verse': 89, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَۚ فَإِن يَكۡفُرۡ بِهَا هَـٰٓؤُلَآءِ فَقَدۡ وَكَّلۡنَا بِهَا قَوۡمٗا لَّيۡسُواْ بِهَا بِكَٰفِرِينَ\nАна ўша зотларга Китоб, Ҳикмат ва пайғамбарлик берганмиз. Бас, агар бу оятларни ана улар (яъни, Макка мушриклари) инкор этсалар, Биз бу оятларни инкор қилмайдиган қавм (яъни, мўмин-мусулмонлар) учун тайёрлаб қўйганмиз'}, {'chapter': 6, 'verse': 90, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ هَدَى ٱللَّهُۖ فَبِهُدَىٰهُمُ ٱقۡتَدِهۡۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ هُوَ إِلَّا ذِكۡرَىٰ لِلۡعَٰلَمِينَ\nАна ўша (юқорида мазкур бўлган зотлар) Аллоҳ ҳидоят қилган кишилардир. Бас, ўшаларнинг йўлларигагина эргашинг! (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен сизлардан бунинг учун (яъни, Қуръон учун) ҳақ сўрамайман. Бу (Китоб) бутун оламлар учун эслатма (ҳужжат)дир, холос»'}, {'chapter': 6, 'verse': 91, 'text': 'وَمَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦٓ إِذۡ قَالُواْ مَآ أَنزَلَ ٱللَّهُ عَلَىٰ بَشَرٖ مِّن شَيۡءٖۗ قُلۡ مَنۡ أَنزَلَ ٱلۡكِتَٰبَ ٱلَّذِي جَآءَ بِهِۦ مُوسَىٰ نُورٗا وَهُدٗى لِّلنَّاسِۖ تَجۡعَلُونَهُۥ قَرَاطِيسَ تُبۡدُونَهَا وَتُخۡفُونَ كَثِيرٗاۖ وَعُلِّمۡتُم مَّا لَمۡ تَعۡلَمُوٓاْ أَنتُمۡ وَلَآ ءَابَآؤُكُمۡۖ قُلِ ٱللَّهُۖ ثُمَّ ذَرۡهُمۡ فِي خَوۡضِهِمۡ يَلۡعَبُونَ\n(Яҳудийлар): «Аллоҳ башарга ҳеч нарса нозил қилган эмас», деган пайтларида, Аллоҳни тўғри таний олмадилар. Айтинг: «Одамлар учун нур ва ҳидоят бўлган ҳолида Мусо келтирган, сизлар дафтар қилиб олиб (айрим оятларни) кўрсатиб, (Муҳаммад пайғамбар ҳақидаги) кўп (оятларини эса яшириб юрадиган ва (ундаги оятларда) на ўзларингиз ва на ота-боболарингиз билган нарсалар баён этилган Китобни (Тавротни) ким нозил қилган эди?!»?»Аллоҳ!», деб жавоб қилинг. Сўнгра уларни ўзлари шўнғиган ноТўғри Йўлларида адашган ҳолларида тарк этинг'}, {'chapter': 6, 'verse': 92, 'text': 'وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ مُّصَدِّقُ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَلِتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَاۚ وَٱلَّذِينَ يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ يُؤۡمِنُونَ بِهِۦۖ وَهُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ\nБу (Қуръон) бир муборак, ўзидан олдинги китобларни тасдиқлайдиган, барча қишлоқлар (ва шаҳарларнинг онаси маркази бўлмиш (Макка аҳлини) ҳамда унинг атрофидаги кишиларни (Охират азобидан) огоҳлантиришингиз учун Ўзимиз нозил қилган Китобдир. Охиратга иймон келтирадиган кишилар унга ҳам иймон келтирадилар ва улар намозларини ўз вақтида адо қиладилар'}, {'chapter': 6, 'verse': 93, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ قَالَ أُوحِيَ إِلَيَّ وَلَمۡ يُوحَ إِلَيۡهِ شَيۡءٞ وَمَن قَالَ سَأُنزِلُ مِثۡلَ مَآ أَنزَلَ ٱللَّهُۗ وَلَوۡ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ فِي غَمَرَٰتِ ٱلۡمَوۡتِ وَٱلۡمَلَـٰٓئِكَةُ بَاسِطُوٓاْ أَيۡدِيهِمۡ أَخۡرِجُوٓاْ أَنفُسَكُمُۖ ٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَقُولُونَ عَلَى ٱللَّهِ غَيۡرَ ٱلۡحَقِّ وَكُنتُمۡ عَنۡ ءَايَٰتِهِۦ تَسۡتَكۡبِرُونَ\nАллоҳ шаънига ёлғон тўқиган ёки ўзига ҳеч нарса ваҳий қилинмаган ҳолда: «Менга ваҳий келди», деган ҳамда: «Аллоҳ нозил қилган нарсанинг мислини (ўхшашини) мен ҳам нозил қила оламан», деб даъво этган кимсалардан ҳам золимроқ ким бор? (Эй Муҳаммад алайҳис-салоту вас-салом), бу золимларни ўлим гирдобида қолган, ўлим фаришталари қўлларини чўзиб: «Жонларингизни берингиз! Бу кун Аллоҳ шаънига ноҳақ гапларни айтганингиз ва Унинг оятларидан юз ўгириб, кибру ҳаво қилганингиз сабабли хорлик азоби билан жазоланадиган кунингиздир», деб турган пайтда бир кўрсангиз эди'}, {'chapter': 6, 'verse': 94, 'text': 'وَلَقَدۡ جِئۡتُمُونَا فُرَٰدَىٰ كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةٖ وَتَرَكۡتُم مَّا خَوَّلۡنَٰكُمۡ وَرَآءَ ظُهُورِكُمۡۖ وَمَا نَرَىٰ مَعَكُمۡ شُفَعَآءَكُمُ ٱلَّذِينَ زَعَمۡتُمۡ أَنَّهُمۡ فِيكُمۡ شُرَكَـٰٓؤُاْۚ لَقَد تَّقَطَّعَ بَيۡنَكُمۡ وَضَلَّ عَنكُم مَّا كُنتُمۡ تَزۡعُمُونَ\nМана, Бизнинг ҳузуримизга сизларни аввал-бошда қандай яратган бўлсак, шундай ёлғиз ҳолда келдингиз. Сизларга берган нарсаларимизни ортингизда қолдирибсиз. Сизлар билан бирга ўзингизча Аллоҳга шерик деб гумон қилган оқловчиларингизни ҳам кўрмаяпмиз. Ўрталарингиз (орангиз) узилиб қолибди. Ишониб юрган нарсаларингиз бутларингиз сизлардан йўқ бўлибди'}, {'chapter': 6, 'verse': 95, 'text': '۞إِنَّ ٱللَّهَ فَالِقُ ٱلۡحَبِّ وَٱلنَّوَىٰۖ يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَمُخۡرِجُ ٱلۡمَيِّتِ مِنَ ٱلۡحَيِّۚ ذَٰلِكُمُ ٱللَّهُۖ فَأَنَّىٰ تُؤۡفَكُونَ\nАлбатта, Аллоҳ дон ва уруғларни ёриб-ундиргувчидир. У зот ўликдан тирикни чиқарур, ўликни тирикдан чиқаргувчидир. Ана ўша зот Аллоҳдир. Бас, қаёққа бурилиб кетмоқдасизлар'}, {'chapter': 6, 'verse': 96, 'text': 'فَالِقُ ٱلۡإِصۡبَاحِ وَجَعَلَ ٱلَّيۡلَ سَكَنٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ حُسۡبَانٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\n(У зулматни ёриб) тонгни чиқаргувчидир. У кечани сукунат ором қилиб қўйди, қуёш ва ойни эса ҳисоб ўлчови қилди. Бу қудратли ва билгувчи Зотнинг ўлчовидир'}, {'chapter': 6, 'verse': 97, 'text': 'وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلنُّجُومَ لِتَهۡتَدُواْ بِهَا فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nУ қуруқлик ва денгиз зулматларида йўл топишингиз учун сизларга юлдузларни яратиб қўйган Зотдир. Биладиган қавм учун оятларни батафсил баён қилдик'}, {'chapter': 6, 'verse': 98, 'text': 'وَهُوَ ٱلَّذِيٓ أَنشَأَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ فَمُسۡتَقَرّٞ وَمُسۡتَوۡدَعٞۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَفۡقَهُونَ\nУ сизларни бир жондан (Одам алайҳиссаломдан) пайдо қилган Зотдир. Бас, (сизлар учун она қорни) қароргоҳ ва (ота пушти) омонат турадиган жойдир. Англайдиган қавм учун оятларни батафсил баён қилдик'}, {'chapter': 6, 'verse': 99, 'text': 'وَهُوَ ٱلَّذِيٓ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ نَبَاتَ كُلِّ شَيۡءٖ فَأَخۡرَجۡنَا مِنۡهُ خَضِرٗا نُّخۡرِجُ مِنۡهُ حَبّٗا مُّتَرَاكِبٗا وَمِنَ ٱلنَّخۡلِ مِن طَلۡعِهَا قِنۡوَانٞ دَانِيَةٞ وَجَنَّـٰتٖ مِّنۡ أَعۡنَابٖ وَٱلزَّيۡتُونَ وَٱلرُّمَّانَ مُشۡتَبِهٗا وَغَيۡرَ مُتَشَٰبِهٍۗ ٱنظُرُوٓاْ إِلَىٰ ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَيَنۡعِهِۦٓۚ إِنَّ فِي ذَٰلِكُمۡ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nУ осмондан сув туширган Зотдир. Бас, у сув ёрдамида ҳар турли набототни чиқардик. Ундан эса гиёҳларни чиқардик. Ундан (гиёҳдан) устма-уст бўлган донларни (яъни, бошоқларни) чиқарурмиз. Хурмо новдаларидан (йиғиб-териб олгувчилар учун) яқин бошлар-ғужумлар (бўлур). Яна узумзор боғларни ҳамда (барглари) бир-бирига ўхшаган, (мевалари эса) ўхшамайдиган зайтун ва анорларни (чиқарурмиз). (Эй инсонлар), уларнинг ҳар бирини мева туккан пайтидаги мевасига ва кейин пишган ҳолига боқинг! Албатта, бунда иймон келтирадиган қавм учун оят-мўъжизалар мавжуддир'}, {'chapter': 6, 'verse': 100, 'text': 'وَجَعَلُواْ لِلَّهِ شُرَكَآءَ ٱلۡجِنَّ وَخَلَقَهُمۡۖ وَخَرَقُواْ لَهُۥ بَنِينَ وَبَنَٰتِۭ بِغَيۡرِ عِلۡمٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَصِفُونَ\nАллоҳга жинларни шерик қилдилар (яъни, жинларни ҳам худо деб билдилар). Ҳолбуки, уларни (Аллоҳнинг) Ўзи яратган эди. Яна Унинг учун билмаган ҳолларида ўғил-қизлар «яса?» олдилар. У зот булар сифатлаётган нарсалардан (шериклар ва болалардан) пок ва юксакдир'}, {'chapter': 6, 'verse': 101, 'text': 'بَدِيعُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَنَّىٰ يَكُونُ لَهُۥ وَلَدٞ وَلَمۡ تَكُن لَّهُۥ صَٰحِبَةٞۖ وَخَلَقَ كُلَّ شَيۡءٖۖ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nУ осмонлар ва Ерни пайдо қилгувчидир. Унинг жуфти йўқ-ку, қаёқдан боласи бўлсин! У ҳамма нарсани яратди ва У ҳамма нарсани билгувчидир'}, {'chapter': 6, 'verse': 102, 'text': 'ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡۖ لَآ إِلَٰهَ إِلَّا هُوَۖ خَٰلِقُ كُلِّ شَيۡءٖ فَٱعۡبُدُوهُۚ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ\nМана шу Аллоҳ Парвардигорингиздир. Ҳеч қандай илоҳ йўқ, магар Унинг Ўзи бордир. У ҳамма нарсани яратгувчидир. Бас, Унга бандалик қилингиз! У ҳамма нарсанинг устидаги вакил — муҳофаза қилгувчидир'}, {'chapter': 6, 'verse': 103, 'text': 'لَّا تُدۡرِكُهُ ٱلۡأَبۡصَٰرُ وَهُوَ يُدۡرِكُ ٱلۡأَبۡصَٰرَۖ وَهُوَ ٱللَّطِيفُ ٱلۡخَبِيرُ\nКўзлар Унга ета олмас. У кўзларга етар! У меҳрибон ва (ҳар нарсадан) огоҳ Зотдир'}, {'chapter': 6, 'verse': 104, 'text': 'قَدۡ جَآءَكُم بَصَآئِرُ مِن رَّبِّكُمۡۖ فَمَنۡ أَبۡصَرَ فَلِنَفۡسِهِۦۖ وَمَنۡ عَمِيَ فَعَلَيۡهَاۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ\nСизларга Парвардигорингиздан ҳужжатлар келмишдир. Ким (уларни) кўрса, ўзи учун кўрур. Ким кўр бўлиб олса, ўзига зиёндир. Мен сизларнинг устингизда қўриқчи эмасман'}, {'chapter': 6, 'verse': 105, 'text': 'وَكَذَٰلِكَ نُصَرِّفُ ٱلۡأٓيَٰتِ وَلِيَقُولُواْ دَرَسۡتَ وَلِنُبَيِّنَهُۥ لِقَوۡمٖ يَعۡلَمُونَ\n(Кофирлар: «Аввалги китоблардан ўрганиб олганса?»), дейишлари учун ва биладиган қавмга равшан қилиб бериш учун оятларимизни мана шундай баён қилурмиз'}, {'chapter': 6, 'verse': 106, 'text': 'ٱتَّبِعۡ مَآ أُوحِيَ إِلَيۡكَ مِن رَّبِّكَۖ لَآ إِلَٰهَ إِلَّا هُوَۖ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиздан сизга ваҳий қилинган нарсаларга эргашинг! Ҳеч қандай илоҳ йўқ, магар Унинг Ўзи бордир. Мушриклардан юз ўгиринг'}, {'chapter': 6, 'verse': 107, 'text': 'وَلَوۡ شَآءَ ٱللَّهُ مَآ أَشۡرَكُواْۗ وَمَا جَعَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ\nАгар Аллоҳ хоҳлаганида, улар мушрик бўлмаган бўлур эдилар. Биз сизни уларнинг устида қўриқчи қилиб қўйганимиз йўқ. Сиз уларнинг устида вакил ҳам эмассиз'}, {'chapter': 6, 'verse': 108, 'text': 'وَلَا تَسُبُّواْ ٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ فَيَسُبُّواْ ٱللَّهَ عَدۡوَۢا بِغَيۡرِ عِلۡمٖۗ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمۡ ثُمَّ إِلَىٰ رَبِّهِم مَّرۡجِعُهُمۡ فَيُنَبِّئُهُم بِمَا كَانُواْ يَعۡمَلُونَ\nАллоҳдан ўзгага сиғинадиган кимсаларни (бутларини) сўкмангиз! У ҳолда улар ҳадларидан ошиб, жоҳиллик билан Аллоҳни сўкурлар. Ҳар бир миллатга ўзлари қилган амални мана шундай чиройли кўрсатиб қўйганмиз. Сўнгра Парвардигорларига қайтмоқлари бор. Бас, У зот уларга қилиб ўтган амалларининг хабарини берур'}, {'chapter': 6, 'verse': 109, 'text': 'وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَتۡهُمۡ ءَايَةٞ لَّيُؤۡمِنُنَّ بِهَاۚ قُلۡ إِنَّمَا ٱلۡأٓيَٰتُ عِندَ ٱللَّهِۖ وَمَا يُشۡعِرُكُمۡ أَنَّهَآ إِذَا جَآءَتۡ لَا يُؤۡمِنُونَ\nУлар агар бирон оят — мўъжиза келса, албатта иймон келтиришлари ҳақида жон-жаҳдлари билан Аллоҳ номига қасам ичдилар. Айтинг: «Оятлар фақат Аллоҳнинг ҳузуридадир». Қаердан билурсизлар (эҳтимол) оят келган вақтда ҳам иймон келтирмаслар'}, {'chapter': 6, 'verse': 110, 'text': 'وَنُقَلِّبُ أَفۡـِٔدَتَهُمۡ وَأَبۡصَٰرَهُمۡ كَمَا لَمۡ يُؤۡمِنُواْ بِهِۦٓ أَوَّلَ مَرَّةٖ وَنَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nВа худди аввал-бошда унга (яъни, Аллоҳнинг оятларига) иймон келтирмаганлари каби уларнинг дил ва кўзларини айлантириб қўюрмиз ва ўз туғёнларида адашиб-улоқиб юрган ҳолларида тарк қилурмиз'}, {'chapter': 6, 'verse': 111, 'text': '۞وَلَوۡ أَنَّنَا نَزَّلۡنَآ إِلَيۡهِمُ ٱلۡمَلَـٰٓئِكَةَ وَكَلَّمَهُمُ ٱلۡمَوۡتَىٰ وَحَشَرۡنَا عَلَيۡهِمۡ كُلَّ شَيۡءٖ قُبُلٗا مَّا كَانُواْ لِيُؤۡمِنُوٓاْ إِلَّآ أَن يَشَآءَ ٱللَّهُ وَلَٰكِنَّ أَكۡثَرَهُمۡ يَجۡهَلُونَ\nАгар биз уларга фаришталарни туширсак ва ўликлар (қабрларидан тирилиб келиб) уларга гапирсалар ҳамда барча нарсани (халойиқни) уларнинг олдида очиқ-аён тўпласак ҳам, иймон келтирувчи бўлмайдилар, магар Аллоҳ хоҳласагина (иймон келтирурлар). Лекин уларнинг кўплари (буни) билмайдилар'}, {'chapter': 6, 'verse': 112, 'text': 'وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا شَيَٰطِينَ ٱلۡإِنسِ وَٱلۡجِنِّ يُوحِي بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ زُخۡرُفَ ٱلۡقَوۡلِ غُرُورٗاۚ وَلَوۡ شَآءَ رَبُّكَ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ\nШунингдек, ҳар бир пайғамбар учун инсу жин(дан бўлган) шайтонларни душман қилиб қўйдик. Улар бир-бирларини алдаш учун гўзал (ялтироқ) сўзлар билан васваса қиладилар. Агар Парвардигорингиз хоҳласа, ундай қилмаган бўлур эдилар. Бас, уларни туҳмат, бўҳтонлари билан бирга тарк қилинг'}, {'chapter': 6, 'verse': 113, 'text': 'وَلِتَصۡغَىٰٓ إِلَيۡهِ أَفۡـِٔدَةُ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ وَلِيَرۡضَوۡهُ وَلِيَقۡتَرِفُواْ مَا هُم مُّقۡتَرِفُونَ\nУндай сўзларга Охиратга ишонмайдиган кимсаларнинг диллари мойил бўлиши учун ва ўша сўзларга рози бўлишиб, ўзлари қилмоқчи бўлган гуноҳларини қилишлари учун (у шайтонлар васваса қиладилар)'}, {'chapter': 6, 'verse': 114, 'text': 'أَفَغَيۡرَ ٱللَّهِ أَبۡتَغِي حَكَمٗا وَهُوَ ٱلَّذِيٓ أَنزَلَ إِلَيۡكُمُ ٱلۡكِتَٰبَ مُفَصَّلٗاۚ وَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡلَمُونَ أَنَّهُۥ مُنَزَّلٞ مِّن رَّبِّكَ بِٱلۡحَقِّۖ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга айтинг): «Аллоҳдан ўзгани ҳакам қилиб олайми?! Ахир Унинг Ўзи сизларга бу Китоб (Қуръонни) муфассал нозил қилган зот-ку?» Биз Китоб берган кимсалар (яҳудий ва насронийлар) унинг (Қуръоннинг) Парвардигорингиздан ҳаққи рост нозил қилинганини билурлар. Бас, ҳаргиз шак-шубҳа қилгувчилардан бўлманг'}, {'chapter': 6, 'verse': 115, 'text': 'وَتَمَّتۡ كَلِمَتُ رَبِّكَ صِدۡقٗا وَعَدۡلٗاۚ لَّا مُبَدِّلَ لِكَلِمَٰتِهِۦۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nПарвардигорингизнинг сўзлари сидқу адолатда комил бўлди. Унинг сўзларини ўзгартиргувчи йўқдир. У эшитгувчи, билгувчидир'}, {'chapter': 6, 'verse': 116, 'text': 'وَإِن تُطِعۡ أَكۡثَرَ مَن فِي ٱلۡأَرۡضِ يُضِلُّوكَ عَن سَبِيلِ ٱللَّهِۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ\nЕр юзидаги кимсаларнинг жуда кўпларига итоат қиладиган бўлсангиз, сизни Аллоҳнинг йўлидан оздирурлар. (Зотан) улар фақат гумонларигагина эргашурлар ва фақат алдайдилар, холос'}, {'chapter': 6, 'verse': 117, 'text': 'إِنَّ رَبَّكَ هُوَ أَعۡلَمُ مَن يَضِلُّ عَن سَبِيلِهِۦۖ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\nАлбатта, Парвардигорингиз Унинг йўлидан адашган кимсаларни жуда яхши билгувчидир ва У ҳақ йўлда юргувчиларни ҳам жуда яхши билгувчидир'}, {'chapter': 6, 'verse': 118, 'text': 'فَكُلُواْ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَيۡهِ إِن كُنتُم بِـَٔايَٰتِهِۦ مُؤۡمِنِينَ\nБас, агар Аллоҳнинг оятларига иймон келтиргувчи бўлсангизлар, Аллоҳнинг номи зикр қилинган нарсалардан (яъни, Аллоҳнинг номи айтилиб сўйилган ҳайвонларнинг гўштидан) еяверинглар'}, {'chapter': 6, 'verse': 119, 'text': 'وَمَا لَكُمۡ أَلَّا تَأۡكُلُواْ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَيۡهِ وَقَدۡ فَصَّلَ لَكُم مَّا حَرَّمَ عَلَيۡكُمۡ إِلَّا مَا ٱضۡطُرِرۡتُمۡ إِلَيۡهِۗ وَإِنَّ كَثِيرٗا لَّيُضِلُّونَ بِأَهۡوَآئِهِم بِغَيۡرِ عِلۡمٍۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِٱلۡمُعۡتَدِينَ\nАллоҳнинг номи зикр қилинган нарсалардан нима учун емас экансизлар?! Ахир У сизларга ҳаром қилинган нарсаларни муфассал баён қилган-ку. Магар музтар бўлиб қолган ҳолингиздагина (у нарсалардан ейишингиз мумкин). Шубҳасиз, кўп (кишилар) ўз ҳою-ҳаваслари билан билмаган ҳолларида (ўзларини) йўлдан оздирурлар. Албатта, Парвардигорингиз бундай тажовузкор кимсаларни жуда яхши билгувчидир'}, {'chapter': 6, 'verse': 120, 'text': 'وَذَرُواْ ظَٰهِرَ ٱلۡإِثۡمِ وَبَاطِنَهُۥٓۚ إِنَّ ٱلَّذِينَ يَكۡسِبُونَ ٱلۡإِثۡمَ سَيُجۡزَوۡنَ بِمَا كَانُواْ يَقۡتَرِفُونَ\nГуноҳнинг очиғини ҳам, махфийсини ҳам тарк қилингиз! Албатта, гуноҳ қилгувчи кимсалар ўзлари қилиб ўтган гуноҳларига жазо олажаклар'}, {'chapter': 6, 'verse': 121, 'text': 'وَلَا تَأۡكُلُواْ مِمَّا لَمۡ يُذۡكَرِ ٱسۡمُ ٱللَّهِ عَلَيۡهِ وَإِنَّهُۥ لَفِسۡقٞۗ وَإِنَّ ٱلشَّيَٰطِينَ لَيُوحُونَ إِلَىٰٓ أَوۡلِيَآئِهِمۡ لِيُجَٰدِلُوكُمۡۖ وَإِنۡ أَطَعۡتُمُوهُمۡ إِنَّكُمۡ لَمُشۡرِكُونَ\nАллоҳнинг номи зикр қилинмаган нарсалардан емангиз! Зотан, бу иш итоатсизликдир. Албатта, шайтонлар ўз дўстларини (яъни, мушрикларни) сизлар билан жанжаллашишлари учун васвасага солурлар. Агар уларга бўйинсунсангизлар, ҳеч шак-шубҳасиз, мушриклардан бўлиб қолурсизлар'}, {'chapter': 6, 'verse': 122, 'text': 'أَوَمَن كَانَ مَيۡتٗا فَأَحۡيَيۡنَٰهُ وَجَعَلۡنَا لَهُۥ نُورٗا يَمۡشِي بِهِۦ فِي ٱلنَّاسِ كَمَن مَّثَلُهُۥ فِي ٱلظُّلُمَٰتِ لَيۡسَ بِخَارِجٖ مِّنۡهَاۚ كَذَٰلِكَ زُيِّنَ لِلۡكَٰفِرِينَ مَا كَانُواْ يَعۡمَلُونَ\nАввал ўлик (кофир) бўлган, сўнгра биз уни (динга ҳидоят қилиш билан) тирилтириб, унга одамлар орасида ўзи билан бирга олиб юрадиган нурни (иймонни) бериб қўйганимиз бир киши зулматларда қолиб кетган ва ундан ҳеч чиқувчи бўлмаган кимсага ўхшайдими?! (Мўминларга иймонлари қандай чиройли кўринса), кофирларга ҳам қилаётган амаллари ана шундай чиройли қилиб кўрсатиб қўйилди'}, {'chapter': 6, 'verse': 123, 'text': 'وَكَذَٰلِكَ جَعَلۡنَا فِي كُلِّ قَرۡيَةٍ أَكَٰبِرَ مُجۡرِمِيهَا لِيَمۡكُرُواْ فِيهَاۖ وَمَا يَمۡكُرُونَ إِلَّا بِأَنفُسِهِمۡ وَمَا يَشۡعُرُونَ\n(Худди Макка зодагонларини фосиқ-итоатсиз кимсалар қилганимиз каби) ҳар бир қишлоқда ўша жойнинг катталарини ҳийла-найранглар қиладиган жиноятчилар қилиб қўйдик. Улар фақат ўзларинигина алдайдилар, холос, лекин ўзлари сезмайдилар'}, {'chapter': 6, 'verse': 124, 'text': 'وَإِذَا جَآءَتۡهُمۡ ءَايَةٞ قَالُواْ لَن نُّؤۡمِنَ حَتَّىٰ نُؤۡتَىٰ مِثۡلَ مَآ أُوتِيَ رُسُلُ ٱللَّهِۘ ٱللَّهُ أَعۡلَمُ حَيۡثُ يَجۡعَلُ رِسَالَتَهُۥۗ سَيُصِيبُ ٱلَّذِينَ أَجۡرَمُواْ صَغَارٌ عِندَ ٱللَّهِ وَعَذَابٞ شَدِيدُۢ بِمَا كَانُواْ يَمۡكُرُونَ\nҚачон уларга (Муҳаммад алайҳис-салоту вас-саломнинг пайғамбар эканлиги ҳақида) бирон оят келса: «То бизга ҳам Аллоҳнинг пайғамбарларига берилган оятларга ўхшаш оятлар берилмагунча ҳаргиз иймон келтирмаймиз», дейдилар. Аллоҳ пайғамбарлик вазифасини қаерга қўйишни яхшироқ билгувчидир. Яқинда бундай жинояткорларга қилиб ўтган ҳийла-найранглари сабабли Аллоҳ ҳузурида хорлик ва қаттиқ азоб етажакдир'}, {'chapter': 6, 'verse': 125, 'text': 'فَمَن يُرِدِ ٱللَّهُ أَن يَهۡدِيَهُۥ يَشۡرَحۡ صَدۡرَهُۥ لِلۡإِسۡلَٰمِۖ وَمَن يُرِدۡ أَن يُضِلَّهُۥ يَجۡعَلۡ صَدۡرَهُۥ ضَيِّقًا حَرَجٗا كَأَنَّمَا يَصَّعَّدُ فِي ٱلسَّمَآءِۚ كَذَٰلِكَ يَجۡعَلُ ٱللَّهُ ٱلرِّجۡسَ عَلَى ٱلَّذِينَ لَا يُؤۡمِنُونَ\nАллоҳ кимни ҳидоят қилишни истаса, унинг кўнглини Ислом учун кенг қилиб қўяр. Кимни адаштиришни истаса, унинг кўнглини худди осмонга кўтарилиб кетаётгандек, тор ва танг қилиб қўяр. Шунингдек, Аллоҳ иймонсиз кимсаларга разолатни (ғолиб) қилур'}, {'chapter': 6, 'verse': 126, 'text': 'وَهَٰذَا صِرَٰطُ رَبِّكَ مُسۡتَقِيمٗاۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَذَّكَّرُونَ\nМана шу Парвардигорингизнинг Тўғри Йўлидир. Эслатма-ибрат олгувчи қавм учун оятларни батафсил қилдик'}, {'chapter': 6, 'verse': 127, 'text': '۞لَهُمۡ دَارُ ٱلسَّلَٰمِ عِندَ رَبِّهِمۡۖ وَهُوَ وَلِيُّهُم بِمَا كَانُواْ يَعۡمَلُونَ\nУлар учун Парвардигорлари ҳузурида дорус-салом (тинчлик диёри яъни, жаннат) бордир. Қилиб ўтган яхши амаллари сабабли У зот (Аллоҳ) уларнинг дўстидир'}, {'chapter': 6, 'verse': 128, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ جَمِيعٗا يَٰمَعۡشَرَ ٱلۡجِنِّ قَدِ ٱسۡتَكۡثَرۡتُم مِّنَ ٱلۡإِنسِۖ وَقَالَ أَوۡلِيَآؤُهُم مِّنَ ٱلۡإِنسِ رَبَّنَا ٱسۡتَمۡتَعَ بَعۡضُنَا بِبَعۡضٖ وَبَلَغۡنَآ أَجَلَنَا ٱلَّذِيٓ أَجَّلۡتَ لَنَاۚ قَالَ ٱلنَّارُ مَثۡوَىٰكُمۡ خَٰلِدِينَ فِيهَآ إِلَّا مَا شَآءَ ٱللَّهُۚ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом, эсланг, Аллоҳ) уларнинг барчаларини тўплайдиган кунда: «Эй жинлар жамоаси, инсонлардан жуда кўпини (адаштирдингиз)», дер. Шунда у жинларнинг инсонлардан бўлган дўстлари: «Парвардигоро, бизлар бир-биримиздан фойдаландик ва Ўзинг биз учун белгилаб қўйган ажалимизга етиб келдик», деганларида, Аллоҳ айтур: «Жойингиз дўзахдир! Унда абадий қолурсиз, магар Аллоҳнииг хоҳиши билангина (чиқишингиз мумкин)». Албатта, Парвардигорингиз ҳикмат ва билим Соҳибидир'}, {'chapter': 6, 'verse': 129, 'text': 'وَكَذَٰلِكَ نُوَلِّي بَعۡضَ ٱلظَّـٰلِمِينَ بَعۡضَۢا بِمَا كَانُواْ يَكۡسِبُونَ\nШунингдек, қилиб юрган гуноҳлари сабабли золим кимсаларни бир-бирларига дўст қилиб қўюрмиз'}, {'chapter': 6, 'verse': 130, 'text': 'يَٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ شَهِدۡنَا عَلَىٰٓ أَنفُسِنَاۖ وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كَٰفِرِينَ\nЭй жин ва инс жамоаси, сизларга ўзларингиздан бўлган пайғамбарлар Менинг оятларимни сўзлаган ва сизларни мана шу (яъни, Қиёмат) кунингиздаги учрашувдан қўрқитган ҳолларида келмадиларми? Улар: «Ўзимизнинг зиёнимизга гувоҳмиз (яъни, ўша пайғамбарлар келган эди)», дедилар. Уларни ҳаёти дунё алдаб қўйди ва кофир эканликлари ҳақида ўзларига қарши гувоҳлик бердилар'}, {'chapter': 6, 'verse': 131, 'text': 'ذَٰلِكَ أَن لَّمۡ يَكُن رَّبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمٖ وَأَهۡلُهَا غَٰفِلُونَ\nБунга (яъни, аввал пайғамбарлар юбориб огоҳлантиришга) сабаб, Парвардигорингизнинг аҳолиси ғофил бўлган қишлоқ-шаҳарларни зулм билан ҳалок қилгувчи эмаслигидир'}, {'chapter': 6, 'verse': 132, 'text': 'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ\nҲар ким учун қилган амалларидан даражот-савоблар бор. Парвардигорингиз уларнинг қилаётган амалларидан ғофил эмасдир'}, {'chapter': 6, 'verse': 133, 'text': 'وَرَبُّكَ ٱلۡغَنِيُّ ذُو ٱلرَّحۡمَةِۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَسۡتَخۡلِفۡ مِنۢ بَعۡدِكُم مَّا يَشَآءُ كَمَآ أَنشَأَكُم مِّن ذُرِّيَّةِ قَوۡمٍ ءَاخَرِينَ\nПарвардигорингиз беҳожат ва меҳрибондир. (Эй золимлар), агар У зот хоҳласа худди сизларни бошқа қавмнинг зурриётидан пайдо қилгани каби сизларни кетказиб, ортингиздан истаган халқни халифа — ўринбосар қилур'}, {'chapter': 6, 'verse': 134, 'text': 'إِنَّ مَا تُوعَدُونَ لَأٓتٖۖ وَمَآ أَنتُم بِمُعۡجِزِينَ\nАлбатта, сизларга ваъда қилинаётган нарса (қайта тирилиш, Охират, Қиёмат Куни) ҳеч шак-шубҳасиз, келувчидир. Сизлар (Аллоҳни) ожиз қила олгувчи эмассиз'}, {'chapter': 6, 'verse': 135, 'text': 'قُلۡ يَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَامِلٞۖ فَسَوۡفَ تَعۡلَمُونَ مَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Эй қавмим, жойингиздан жилмай (яъни, куфрингиздан қайтмай) амалингизни қилаверинг. Мен ҳам (Парвардигорим буюрган) амални қилгувчиман. Бас, яқинда Охират диёридаги оқибат кимнинг фойдасига бўлишини билажаксиз. Шубҳасиз, золим кимсалар нажот топмаслар»'}, {'chapter': 6, 'verse': 136, 'text': 'وَجَعَلُواْ لِلَّهِ مِمَّا ذَرَأَ مِنَ ٱلۡحَرۡثِ وَٱلۡأَنۡعَٰمِ نَصِيبٗا فَقَالُواْ هَٰذَا لِلَّهِ بِزَعۡمِهِمۡ وَهَٰذَا لِشُرَكَآئِنَاۖ فَمَا كَانَ لِشُرَكَآئِهِمۡ فَلَا يَصِلُ إِلَى ٱللَّهِۖ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَىٰ شُرَكَآئِهِمۡۗ سَآءَ مَا يَحۡكُمُونَ\n(Мушриклар) Аллоҳ учун Унинг Ўзи яратган экин ва чорвалардан бир улуш ажратиб, ўзларича: «Бу Аллоҳ учун, бу (қолгани) эса бутларимиз учун», дедилар. Энди бутлари учун бўлган улуш Аллоҳ (улушига) қўшилмайди. Аллоҳ учун бўлган улуш эса бутларига қўшилаверади. Нақадар ёмон ҳукм чиқарадилар-а'}, {'chapter': 6, 'verse': 137, 'text': 'وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٖ مِّنَ ٱلۡمُشۡرِكِينَ قَتۡلَ أَوۡلَٰدِهِمۡ شُرَكَآؤُهُمۡ لِيُرۡدُوهُمۡ وَلِيَلۡبِسُواْ عَلَيۡهِمۡ دِينَهُمۡۖ وَلَوۡ شَآءَ ٱللَّهُ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ\nШунингдек, шайтонлари мушрикларни ҳалок этиш ва динларини чалкаштириб юбориш учун улардан кўпларига болаларини ўлдиришни (яъни, ўз бутлари учун қурбон қилишни) чиройли кўрсатиб қўйди. Агар Аллоҳ хоҳлаганида бу ишни қилмаган бўлур эдилар. Бас, уларни туҳмат-бўҳтонлари билан тарк қилинг'}, {'chapter': 6, 'verse': 138, 'text': 'وَقَالُواْ هَٰذِهِۦٓ أَنۡعَٰمٞ وَحَرۡثٌ حِجۡرٞ لَّا يَطۡعَمُهَآ إِلَّا مَن نَّشَآءُ بِزَعۡمِهِمۡ وَأَنۡعَٰمٌ حُرِّمَتۡ ظُهُورُهَا وَأَنۡعَٰمٞ لَّا يَذۡكُرُونَ ٱسۡمَ ٱللَّهِ عَلَيۡهَا ٱفۡتِرَآءً عَلَيۡهِۚ سَيَجۡزِيهِم بِمَا كَانُواْ يَفۡتَرُونَ\nУлар ўзларича: «Мана бу ҳайвон ва экинлар ҳаром, уларни фақат биз хоҳлаган кишиларгина (яъни, бутхона хизматчилари ва эр кишиларгина) ейдилар. Бу ҳайвонларни эса миниш ҳаром қилинган», дедилар. Улар ҳайвонларни (сўйишда) Аллоҳнинг номини зикр қилмайдилар, (балки бутларнинг номини айтадилар ва мана шу қилмишларини Аллоҳ амри деб) У зотга туҳмат қиладилар. Яқинда бу туҳматлари сабабли уларга Ўзи жазо беражак'}, {'chapter': 6, 'verse': 139, 'text': 'وَقَالُواْ مَا فِي بُطُونِ هَٰذِهِ ٱلۡأَنۡعَٰمِ خَالِصَةٞ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰٓ أَزۡوَٰجِنَاۖ وَإِن يَكُن مَّيۡتَةٗ فَهُمۡ فِيهِ شُرَكَآءُۚ سَيَجۡزِيهِمۡ وَصۡفَهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ\nУлар: «Мана бу ҳайвонларнинг қоринларидаги нарсалар фақат эркакларимиз учундир, хотинларимизга эса ҳаромдир», дедилар. Агар у нарса (яъни, ҳомила) ўлик бўлса, у ҳолда ҳаммалари шерикдирлар (яъни, эркак-хотин ҳаммалари ейишаверади). Яқинда бундай (ўзларича ҳалол-ҳаром деб) сифатлаганлари учун уларга Ўзи жазо беражак. Албатта, У ҳикмат ва билим Соҳибидир'}, {'chapter': 6, 'verse': 140, 'text': 'قَدۡ خَسِرَ ٱلَّذِينَ قَتَلُوٓاْ أَوۡلَٰدَهُمۡ سَفَهَۢا بِغَيۡرِ عِلۡمٖ وَحَرَّمُواْ مَا رَزَقَهُمُ ٱللَّهُ ٱفۡتِرَآءً عَلَى ٱللَّهِۚ قَدۡ ضَلُّواْ وَمَا كَانُواْ مُهۡتَدِينَ\nЎз болаларини ақлсизлик қилиб жоҳиллик билан ўлдирган ва Аллоҳга туҳмат қилиб Аллоҳ берган (ҳалол) ризқни ҳаром қилиб олган кимсалар муҳаққақки, (ўзларига) зиён қилдилар. Улар аниқ адашдилар ва Тўғри Йўлга юргувчи бўлмадилар'}, {'chapter': 6, 'verse': 141, 'text': '۞وَهُوَ ٱلَّذِيٓ أَنشَأَ جَنَّـٰتٖ مَّعۡرُوشَٰتٖ وَغَيۡرَ مَعۡرُوشَٰتٖ وَٱلنَّخۡلَ وَٱلزَّرۡعَ مُخۡتَلِفًا أُكُلُهُۥ وَٱلزَّيۡتُونَ وَٱلرُّمَّانَ مُتَشَٰبِهٗا وَغَيۡرَ مُتَشَٰبِهٖۚ كُلُواْ مِن ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَءَاتُواْ حَقَّهُۥ يَوۡمَ حَصَادِهِۦۖ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡرِفِينَ\nУ (Аллоҳ сўритокларга) кўтарилувчи ва кўтарилмайдиган боғларни, хурмозорлар ва меваларни, хилма-хил бўлган экинларни, (барглари) бир-бирига ўхшаган ва (мевалари эса) ўхшамаган зайтун ва анорларни яратган Зотдир. Мевалаганда мевасидан енглар, ўрим-терим кунида (бечора-мискинларга) ҳаққини беринглар ва исроф қилманглар! Албатта, У исроф қилувчиларни севмас'}, {'chapter': 6, 'verse': 142, 'text': 'وَمِنَ ٱلۡأَنۡعَٰمِ حَمُولَةٗ وَفَرۡشٗاۚ كُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nҲайвонлардан юк ташувчиларни ва (сўйиш учун) ётқизиладиган ҳайвонларни (яратди). Аллоҳ сизларга ризқ қилиб берган нарсалардан енглар ва (ўзингизча ҳалол, ҳаром деб) шайтоннинг изидан эргашманглар! Албатта, у (шайтон) сизларга очиқ душмандир'}, {'chapter': 6, 'verse': 143, 'text': 'ثَمَٰنِيَةَ أَزۡوَٰجٖۖ مِّنَ ٱلضَّأۡنِ ٱثۡنَيۡنِ وَمِنَ ٱلۡمَعۡزِ ٱثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ ٱلۡأُنثَيَيۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ ٱلۡأُنثَيَيۡنِۖ نَبِّـُٔونِي بِعِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ\nСаккизта жуфтни (яратди). Қўйдан иккини (яъни, эркак ва урғочини), эчкидан иккини (яратди). Ўзларича ҳаром қилиб олувчи мушрикларга айтинг: «(Аллоҳ бу ҳайвонлардан) иккала эркагини ҳаром қилганми ё иккала урғочисиними, ёки иккала урғочининг бачадонларида бўлган ҳомиланими?! Агар ростгўй бўлсангизлар, бирон ҳужжат билан менга хабар беринглар-чи?!»'}, {'chapter': 6, 'verse': 144, 'text': 'وَمِنَ ٱلۡإِبِلِ ٱثۡنَيۡنِ وَمِنَ ٱلۡبَقَرِ ٱثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ ٱلۡأُنثَيَيۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ ٱلۡأُنثَيَيۡنِۖ أَمۡ كُنتُمۡ شُهَدَآءَ إِذۡ وَصَّىٰكُمُ ٱللَّهُ بِهَٰذَاۚ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا لِّيُضِلَّ ٱلنَّاسَ بِغَيۡرِ عِلۡمٍۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nТуядан иккини, қора молдан иккини (яратди). Айтинг: «Иккала эркагини ҳаром қилганми ё иккала урғочисиними, ёки иккала урғочининг қоринларида бўлган ҳомиланими?! Ёки Аллоҳ сизларни шунга (яъни, ҳаром қилишга) буюрган пайтида гувоҳ бўлганмидингизлар?! Билмаган ҳолда одамларни йўлдан оздириш учун Аллоҳ шаънига ёлғон тўқиган кимсадан ҳам золимроқ ким бор?!» Албатта, Аллоҳ золим қавмни ҳидоят қилмас'}, {'chapter': 6, 'verse': 145, 'text': 'قُل لَّآ أَجِدُ فِي مَآ أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَىٰ طَاعِمٖ يَطۡعَمُهُۥٓ إِلَّآ أَن يَكُونَ مَيۡتَةً أَوۡ دَمٗا مَّسۡفُوحًا أَوۡ لَحۡمَ خِنزِيرٖ فَإِنَّهُۥ رِجۡسٌ أَوۡ فِسۡقًا أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۚ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ رَبَّكَ غَفُورٞ رَّحِيمٞ\nАйтинг: «Менга ваҳий қилинган Қуръонда ейдиган киши учун ҳаром қилинган нарсани кўрмаяпман. Магар ўлакса ё тўқилган қон, ёки тўнғиз гўшти бўлса — чунки у ҳаромдир, ёхуд Аллоҳдан ўзга учун аталган фосиқлик бўлса (ҳаромдир). Бас, ким золим ва тажовузкор бўлмаган ҳолда музтар бўлиб қолса (масалан, очликдан ўлмаслик учун, у нарсалардан ейиш билан гуноҳкор бўлмас, зеро) Парвардигорингиз мағфиратли, меҳрибондир»'}, {'chapter': 6, 'verse': 146, 'text': 'وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا كُلَّ ذِي ظُفُرٖۖ وَمِنَ ٱلۡبَقَرِ وَٱلۡغَنَمِ حَرَّمۡنَا عَلَيۡهِمۡ شُحُومَهُمَآ إِلَّا مَا حَمَلَتۡ ظُهُورُهُمَآ أَوِ ٱلۡحَوَايَآ أَوۡ مَا ٱخۡتَلَطَ بِعَظۡمٖۚ ذَٰلِكَ جَزَيۡنَٰهُم بِبَغۡيِهِمۡۖ وَإِنَّا لَصَٰدِقُونَ\nЯҳудий бўлган кимсаларга барча туёқлиларни ҳаром қилдик. Мол-қўйдан улар учун ёғларни ҳаром қилдик. Магар биқинларидаги ё ичларидаги, ёки суякка аралашган ёғларгина (ҳаром эмасдир). Золимликлари сабабли уларни шундай жазоладик. Албатта, Биз рост сўзлагувчимиз'}, {'chapter': 6, 'verse': 147, 'text': 'فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمۡ ذُو رَحۡمَةٖ وَٰسِعَةٖ وَلَا يُرَدُّ بَأۡسُهُۥ عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِينَ\nЭнди агар сизни ёлғончи қилсалар, айтинг: «Парвардигорингиз чексиз меҳрибондир, аммо Унинг азоби бирон жиноятчи қавмдан қайтарилмас»'}, {'chapter': 6, 'verse': 148, 'text': 'سَيَقُولُ ٱلَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ ٱللَّهُ مَآ أَشۡرَكۡنَا وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن شَيۡءٖۚ كَذَٰلِكَ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ حَتَّىٰ ذَاقُواْ بَأۡسَنَاۗ قُلۡ هَلۡ عِندَكُم مِّنۡ عِلۡمٖ فَتُخۡرِجُوهُ لَنَآۖ إِن تَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ أَنتُمۡ إِلَّا تَخۡرُصُونَ\nҲали мушрик бўлган кимсалар: «Агар Аллоҳ хоҳлаганида биз ҳам, ота-боболаримиз ҳам на мушрик бўлган ва на бирон нарсани ҳаром қилиб олган бўлур эдик», дедилар. Улардан аввалгилари ҳам то азобимизни тотгунларича (ўз пайғамбарларини) мана шундай ёлғончи қилганлар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Ҳузурингизда бизга кўрсатадиган (айтаётган сўзларингизни тасдиқловчи) бирон ҳужжатингиз борми? Сизлар фақат гумонга эргашмоқдасиз, сизлар фақат ёлғон сўзламоқдасиз!»'}, {'chapter': 6, 'verse': 149, 'text': 'قُلۡ فَلِلَّهِ ٱلۡحُجَّةُ ٱلۡبَٰلِغَةُۖ فَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ\nАйтинг: «Етук ҳужжат фақат Аллоҳнинг ҳужжатидир. Бас, агар хоҳлаганида барчангизни ҳидоят қилган бўлур эди»'}, {'chapter': 6, 'verse': 150, 'text': 'قُلۡ هَلُمَّ شُهَدَآءَكُمُ ٱلَّذِينَ يَشۡهَدُونَ أَنَّ ٱللَّهَ حَرَّمَ هَٰذَاۖ فَإِن شَهِدُواْ فَلَا تَشۡهَدۡ مَعَهُمۡۚ وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ وَهُم بِرَبِّهِمۡ يَعۡدِلُونَ\nАйтинг: «Аллоҳ мана шу (мазкур нарсаларни) ҳаром қилганига гувоҳлик берадиган гувоҳларингизни келтирингиз!» Энди агар улар гувоҳлик берсалар ҳам, сиз улар билан гувоҳлик берманг (яъни, уларнинг берган гувоҳликларига ишонманг)! Ва Бизнииг оятларимизни ёлғон дейдиган, Охиратга иймон келтирмайдиган кимсаларнинг ҳавойи нафсларига эргашманг! Улар Парвардигорларига (ўзгаларни) тенглаштирурлар'}, {'chapter': 6, 'verse': 151, 'text': '۞قُلۡ تَعَالَوۡاْ أَتۡلُ مَا حَرَّمَ رَبُّكُمۡ عَلَيۡكُمۡۖ أَلَّا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنٗاۖ وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُم مِّنۡ إِمۡلَٰقٖ نَّحۡنُ نَرۡزُقُكُمۡ وَإِيَّاهُمۡۖ وَلَا تَقۡرَبُواْ ٱلۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَۖ وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ\nАйтинг: «Келинглар, Парвардигорингиз сизларга ҳаром қилган нарсаларни тиловат қилиб берай. У зотга бирон нарсани шерик қилмангиз; ота-онага яхшилик қилингиз; болаларингизни камбағалликдан (қўрқиб) ўлдирмангиз, — сизларни ҳам, уларни ҳам Биз ризқлантирурмиз — бузуқ ишларнинг ошкорасига ҳам, махфийсига ҳам яқинлашмангиз; Аллоҳ (ўлдиришни) ҳаром қилган жонларни ўлдирмангиз, магар ҳақ билан (яъни, қасос олиш учун ё муртад бўлгани, ёки зино қилгани учун шариат ҳукми билан ўлдиришингиз мумкин). Шояд ақл юргизарсиз, деб сизларни мана шу нарсаларга буюрди»'}, {'chapter': 6, 'verse': 152, 'text': 'وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ ٱلۡكَيۡلَ وَٱلۡمِيزَانَ بِٱلۡقِسۡطِۖ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۖ وَإِذَا قُلۡتُمۡ فَٱعۡدِلُواْ وَلَوۡ كَانَ ذَا قُرۡبَىٰۖ وَبِعَهۡدِ ٱللَّهِ أَوۡفُواْۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَذَكَّرُونَ\nЕтимнинг молига то вояга етгунича фақат энг чиройли йўл билан яқинлашингиз! Ўлчов ва тарозини адолат билан тўла тортингиз! Биз ҳеч бир жонни тоқатидан ташқари нарсага таклиф қилмаймиз. Сўзлаганингизда (гувоҳлик берганингизда) гарчи қариндошингиз бўлса ҳам адолат қилингиз! Аллоҳга берган аҳдингизга вафо қилингиз! Шояд эслатма (насиҳат) олурсиз, деб сизларни мана шу нарсага буюрди'}, {'chapter': 6, 'verse': 153, 'text': 'وَأَنَّ هَٰذَا صِرَٰطِي مُسۡتَقِيمٗا فَٱتَّبِعُوهُۖ وَلَا تَتَّبِعُواْ ٱلسُّبُلَ فَتَفَرَّقَ بِكُمۡ عَن سَبِيلِهِۦۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَتَّقُونَ\nАлбатта, мана шу Менинг Тўғри Йўлимдир. Бас, шу йўлга эргашинглар! (Бошқа) йўлларга эргашмангизки, улар сизларни Унинг йўлидан узиб қўяр. Шояд тақво қилсангиз, деб сизларни мана шу нарсаларга буюрди'}, {'chapter': 6, 'verse': 154, 'text': 'ثُمَّ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ تَمَامًا عَلَى ٱلَّذِيٓ أَحۡسَنَ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُم بِلِقَآءِ رَبِّهِمۡ يُؤۡمِنُونَ\nЯна (билингизки), чиройли амал қилган зотларга (неъматимизни) комил қилиб бериш ва барча нарсани аниқ баён қилиш учун ҳамда ҳидоят ва раҳмат бўлсин, деб Мусога Китоб (Таврот) ато этдик. Шояд (Бани Исроил) Парвардигорларига рўбару бўлишларига иймон келтирсалар'}, {'chapter': 6, 'verse': 155, 'text': 'وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ فَٱتَّبِعُوهُ وَٱتَّقُواْ لَعَلَّكُمۡ تُرۡحَمُونَ\nМана бу эса Биз нозил қилган муборак Қуръондир! Бас, унга эргашингиз ва (Аллоҳдан) қўрқингиз! Шояд раҳматга эришсангизлар'}, {'chapter': 6, 'verse': 156, 'text': 'أَن تَقُولُوٓاْ إِنَّمَآ أُنزِلَ ٱلۡكِتَٰبُ عَلَىٰ طَآئِفَتَيۡنِ مِن قَبۡلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمۡ لَغَٰفِلِينَ\n«Биздан аввалги икки тоифага китоб нозил бўлган, биз эса уларнинг дарсларини ўрганишдан ғофилмиз», демасларингиз учун'}, {'chapter': 6, 'verse': 157, 'text': 'أَوۡ تَقُولُواْ لَوۡ أَنَّآ أُنزِلَ عَلَيۡنَا ٱلۡكِتَٰبُ لَكُنَّآ أَهۡدَىٰ مِنۡهُمۡۚ فَقَدۡ جَآءَكُم بَيِّنَةٞ مِّن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞۚ فَمَنۡ أَظۡلَمُ مِمَّن كَذَّبَ بِـَٔايَٰتِ ٱللَّهِ وَصَدَفَ عَنۡهَاۗ سَنَجۡزِي ٱلَّذِينَ يَصۡدِفُونَ عَنۡ ءَايَٰتِنَا سُوٓءَ ٱلۡعَذَابِ بِمَا كَانُواْ يَصۡدِفُونَ\nЁки: «Агар бизга китоб нозил қилинганида, улардан тўғрироқ йўлда бўлур эдик», демасларингиз учун (сизларга бу Қуръонни нозил қилдик). Энди сизларга Парвардигорингиздан ҳужжат, ҳидоят ва раҳмат келди. Бас, Аллоҳнинг оятларини ёлғон деган ва улардан юз ўгирган кимсадан ҳам золимроқ ким бор?! Яқинда оятларимиздан юз ўгирган кимсаларни юз ўгирувчи бўлганлари сабабли ёмон азоб билан жазолагаймиз'}, {'chapter': 6, 'verse': 158, 'text': 'هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ ٱلۡمَلَـٰٓئِكَةُ أَوۡ يَأۡتِيَ رَبُّكَ أَوۡ يَأۡتِيَ بَعۡضُ ءَايَٰتِ رَبِّكَۗ يَوۡمَ يَأۡتِي بَعۡضُ ءَايَٰتِ رَبِّكَ لَا يَنفَعُ نَفۡسًا إِيمَٰنُهَا لَمۡ تَكُنۡ ءَامَنَتۡ مِن قَبۡلُ أَوۡ كَسَبَتۡ فِيٓ إِيمَٰنِهَا خَيۡرٗاۗ قُلِ ٱنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ\nУлар фақат (жонларини олиш учун ўлим) фаришталари келишини ё (Қиёмат Кунида бандалари ўртасида ҳукм қилиш учун) Парвардигорингизнинг келишини, ёки Парвардигорингизнинг оятларидан — Қиёмат аломатларидан айримлари келишини кутмоқдалар, холос. Парвардигорингизнинг айрим оятлари келадиган кунда эса илгари иймон келтирмаган ёки иймонида яхшилик касб қилмаган бирон жонга (энди келтирган) иймони фойда бермас. Айтинг: «Кутаверинглар! Биз ҳам кутгувчилармиз!»'}, {'chapter': 6, 'verse': 159, 'text': 'إِنَّ ٱلَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗا لَّسۡتَ مِنۡهُمۡ فِي شَيۡءٍۚ إِنَّمَآ أَمۡرُهُمۡ إِلَى ٱللَّهِ ثُمَّ يُنَبِّئُهُم بِمَا كَانُواْ يَفۡعَلُونَ\nДинларини бўлиб, ўзлари ҳам гуруҳларга бўлиниб олган кимсалар тўғрисидан бирон нарсада (масъул) эмассиз. Уларнинг ишлари фақат Аллоҳнинг ўзига ҳавола. Кейин (Қиёмат Кунида) У зот уларга қилиб ўтган ишларининг хабарини берур'}, {'chapter': 6, 'verse': 160, 'text': 'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ عَشۡرُ أَمۡثَالِهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَا وَهُمۡ لَا يُظۡلَمُونَ\nКим бирон чиройли амал қилса, унга ўн баробар қилиб (қайтарилур). Ким бирон ёмон иш қилса, фақат ўшанинг баробарида жазоланур ва уларга зулм қилинмас'}, {'chapter': 6, 'verse': 161, 'text': 'قُلۡ إِنَّنِي هَدَىٰنِي رَبِّيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ دِينٗا قِيَمٗا مِّلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۚ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nАйтинг: «Мени Парвардигорим Тўғри Йўлга, Рост динга — Ҳақ Йўлга мойил бўлган Иброҳимнинг миллатига ҳидоят қилди. У мушриклардан бўлмаган эди»'}, {'chapter': 6, 'verse': 162, 'text': 'قُلۡ إِنَّ صَلَاتِي وَنُسُكِي وَمَحۡيَايَ وَمَمَاتِي لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nАйтинг: «Албатта, намозим, ибодатларим, ҳаёту мамотим бутун оламларнинг Парвардигори бўлмиш Аллоҳ учундир'}, {'chapter': 6, 'verse': 163, 'text': 'لَا شَرِيكَ لَهُۥۖ وَبِذَٰلِكَ أُمِرۡتُ وَأَنَا۠ أَوَّلُ ٱلۡمُسۡلِمِينَ\nУ зотнинг биронта шериги йўқдир. Мана шунга (яъни, ягона Аллоҳга ихлос-ибодат қилишга) буюрилганман. Ва мен бўйинсунгувчиларнинг аввали — пешқадамиман»'}, {'chapter': 6, 'verse': 164, 'text': 'قُلۡ أَغَيۡرَ ٱللَّهِ أَبۡغِي رَبّٗا وَهُوَ رَبُّ كُلِّ شَيۡءٖۚ وَلَا تَكۡسِبُ كُلُّ نَفۡسٍ إِلَّا عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nАйтинг: «Аллоҳдан ўзгани Парвардигорим дейинми?! Ахир У барча нарсанинг Парвардигори-ку?! Ҳар бир жоннинг қилган гуноҳи фақат ўз зиёнига бўлур. Ҳеч бир кўтаргувчи (яъни, гуноҳкор жон) ўзга жоннинг юкини (яъни, гуноҳини) кўтармас. Кейин Парвардигорингизга қайтишингиз бор. Бас, У сизларга ихтилоф қилиб ўтган нарсаларингизнинг (яъни, Қиёматнинг) хабарини берур'}, {'chapter': 6, 'verse': 165, 'text': 'وَهُوَ ٱلَّذِي جَعَلَكُمۡ خَلَـٰٓئِفَ ٱلۡأَرۡضِ وَرَفَعَ بَعۡضَكُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۗ إِنَّ رَبَّكَ سَرِيعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمُۢ\nУ сизларни Ерда халифалар қилиб қўйган ва Ўзи ато этган неъматлар билан сизларни имтиҳон қилиш учун айримларингизни айримларингиздан юқорироқ даражаларга кўтариб қўйган Зотдир. Албатта, Парвардигорингиз тезда жазолагувчидир ва албатта У мағфиратли, меҳрибондир'}, {'chapter': 7, 'verse': 1, 'text': 'الٓمٓصٓ\nАлиф, Лом, Мим, Сод'}, {'chapter': 7, 'verse': 2, 'text': 'كِتَٰبٌ أُنزِلَ إِلَيۡكَ فَلَا يَكُن فِي صَدۡرِكَ حَرَجٞ مِّنۡهُ لِتُنذِرَ بِهِۦ وَذِكۡرَىٰ لِلۡمُؤۡمِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, бу Қуръон) сизга унинг ёрдамида кофирларни (Аллоҳнинг азобидан) огоҳлантиришингиз учун нозил қилинган Китобдир — бас, кўнглингизда у сабабли танглик (шубҳаланиш) бўлмасин! — ва у мўминлар учун бир эслатмадир'}, {'chapter': 7, 'verse': 3, 'text': 'ٱتَّبِعُواْ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡ وَلَا تَتَّبِعُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۗ قَلِيلٗا مَّا تَذَكَّرُونَ\n(Эй инсонлар), сизларга Парвардигорингиздан нозил қилинган нарсага (Китобга) эргашингиз, ундан ўзга «дўстларга» эргашмангиз! Камдан-кам панд-насиҳат олурсизлар'}, {'chapter': 7, 'verse': 4, 'text': 'وَكَم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا فَجَآءَهَا بَأۡسُنَا بَيَٰتًا أَوۡ هُمۡ قَآئِلُونَ\n(Бизнинг амримизга итоат қилмаган) қанчадан-қанча қишлоқни ҳалок қилдик — бас, уларга азобимиз тун уйқусида ёки туш уйқусида (яъни, кундузи) келди'}, {'chapter': 7, 'verse': 5, 'text': 'فَمَا كَانَ دَعۡوَىٰهُمۡ إِذۡ جَآءَهُم بَأۡسُنَآ إِلَّآ أَن قَالُوٓاْ إِنَّا كُنَّا ظَٰلِمِينَ\nБизнинг азобимиз келган вақтида улар бирон даъво қила олмадилар, магар «Бизлар ўзимизга зулм қилдик», дея олдилар, холос'}, {'chapter': 7, 'verse': 6, 'text': 'فَلَنَسۡـَٔلَنَّ ٱلَّذِينَ أُرۡسِلَ إِلَيۡهِمۡ وَلَنَسۡـَٔلَنَّ ٱلۡمُرۡسَلِينَ\nЭнди албатта Биз ўзларига пайғамбар юборилган кишилар билан ҳам, (уларга) юборилган пайғамбарлар билан ҳам савол-жавоб қилурмиз'}, {'chapter': 7, 'verse': 7, 'text': 'فَلَنَقُصَّنَّ عَلَيۡهِم بِعِلۡمٖۖ وَمَا كُنَّا غَآئِبِينَ\nЭнди албатта уларга, билган ҳолимизда, (қилиб ўтган ишлари ҳақида) сўйлаб берурмиз. (Зотан) Биз ғойиб — йўқ эмас эдик, (балки барча нарсага гувоҳ бўлиб турган эдик)'}, {'chapter': 7, 'verse': 8, 'text': 'وَٱلۡوَزۡنُ يَوۡمَئِذٍ ٱلۡحَقُّۚ فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\n(Амалларни) тўғри тортишлик ўша Куни бўлур. Бас, кимнинг тортилган (яхши амаллари) оғир келса, ана ўшалар нажот топгувчилардир'}, {'chapter': 7, 'verse': 9, 'text': 'وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُم بِمَا كَانُواْ بِـَٔايَٰتِنَا يَظۡلِمُونَ\nКимнинг тортилган (яхши амаллари) енгил бўлса, бас, улар (Бизнинг оятларимизни инкор қилиш билан) зулм қилиб ўтганлари сабабли ўзларига зиён қилган кимсалардир'}, {'chapter': 7, 'verse': 10, 'text': 'وَلَقَدۡ مَكَّنَّـٰكُمۡ فِي ٱلۡأَرۡضِ وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَۗ قَلِيلٗا مَّا تَشۡكُرُونَ\nАниқки, Биз сизларни Ерда барқарор этиб, сизлар учун унда тирикчилик воситаларини пайдо қилдик. Камдан-кам шукр қилурсизлар'}, {'chapter': 7, 'verse': 11, 'text': 'وَلَقَدۡ خَلَقۡنَٰكُمۡ ثُمَّ صَوَّرۡنَٰكُمۡ ثُمَّ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ لَمۡ يَكُن مِّنَ ٱلسَّـٰجِدِينَ\nАниқки, Биз сизларни яратдик, сўнг сизларга сурат бердик, сўнгра фаришталарга: «Одамга сажда қилинглар», дейишимиз билан улар сажда қилдилар. Магар иблис сажда қилгувчилардан бўлмади'}, {'chapter': 7, 'verse': 12, 'text': 'قَالَ مَا مَنَعَكَ أَلَّا تَسۡجُدَ إِذۡ أَمَرۡتُكَۖ قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ\n(Аллоҳ) деди: «Сенга буюрган пайтимда нима сени сажда қилишдан тўсди?» «Мен ундан (Одамдан) яхшироқман. Мени оловдан яратгансан. Уни эса лойдан яратдинг», деди у'}, {'chapter': 7, 'verse': 13, 'text': 'قَالَ فَٱهۡبِطۡ مِنۡهَا فَمَا يَكُونُ لَكَ أَن تَتَكَبَّرَ فِيهَا فَٱخۡرُجۡ إِنَّكَ مِنَ ٱلصَّـٰغِرِينَ\n(Аллоҳ) деди: «У ҳолда ундан (жаннатдан) тушгин! Сен учун унда кибру ҳаво қилиб юриш жоиз эмас. Бас, чиқ! Албатта сен хор бўлгувчилардандирсан!»'}, {'chapter': 7, 'verse': 14, 'text': 'قَالَ أَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\n«Менга улар тириладиган Кунгача (Қиёматгача яшаш учун) муҳлат бер», деди у'}, {'chapter': 7, 'verse': 15, 'text': 'قَالَ إِنَّكَ مِنَ ٱلۡمُنظَرِينَ\n(Аллоҳ) деди: «Сен муҳлат берилганлардансан»'}, {'chapter': 7, 'verse': 16, 'text': 'قَالَ فَبِمَآ أَغۡوَيۡتَنِي لَأَقۡعُدَنَّ لَهُمۡ صِرَٰطَكَ ٱلۡمُسۡتَقِيمَ\nУ айтди: «Қасамки, энди мени йўлдан оздирганинг сабабли мудом Сенинг Тўғри Йўлинг устида уларни (Одам болаларини) кутиб ўтирурман'}, {'chapter': 7, 'verse': 17, 'text': 'ثُمَّ لَأٓتِيَنَّهُم مِّنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ وَعَنۡ أَيۡمَٰنِهِمۡ وَعَن شَمَآئِلِهِمۡۖ وَلَا تَجِدُ أَكۡثَرَهُمۡ شَٰكِرِينَ\nСўнгра уларга олдиларидан ва ортларидан, ўнгу сўлларидан келиб (Тўғри Йўлдан оздирурман) ва (оқибатда) уларнинг кўпларини (берган неъматларингга) шукр қилган ҳолларида топмайсан»'}, {'chapter': 7, 'verse': 18, 'text': 'قَالَ ٱخۡرُجۡ مِنۡهَا مَذۡءُومٗا مَّدۡحُورٗاۖ لَّمَن تَبِعَكَ مِنۡهُمۡ لَأَمۡلَأَنَّ جَهَنَّمَ مِنكُمۡ أَجۡمَعِينَ\n(Аллоҳ) айтди: «Ундан (жаннатдан) жирканч ва мағлуб ҳолда чиқ! Қасамки, улардан кимда-ким сенга эргашса, албатта жаҳаннамни сизларнинг барчаларингиз (сен ва сенга эргашганлар) билан тўлдирурман'}, {'chapter': 7, 'verse': 19, 'text': 'وَيَـٰٓـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ فَكُلَا مِنۡ حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِينَ\nЭй Одам, сен эса жуфтинг (Ҳавво) билан жаннатни маскан тутиб, хоҳлаган жойингиздан таомланинг. Фақат мана бу дарахтга яқинлашмангки, у ҳолда золимлардан бўлиб қолурсиз'}, {'chapter': 7, 'verse': 20, 'text': 'فَوَسۡوَسَ لَهُمَا ٱلشَّيۡطَٰنُ لِيُبۡدِيَ لَهُمَا مَا وُۥرِيَ عَنۡهُمَا مِن سَوۡءَٰتِهِمَا وَقَالَ مَا نَهَىٰكُمَا رَبُّكُمَا عَنۡ هَٰذِهِ ٱلشَّجَرَةِ إِلَّآ أَن تَكُونَا مَلَكَيۡنِ أَوۡ تَكُونَا مِنَ ٱلۡخَٰلِدِينَ\nСўнг шайтон уларни беркитилган авратларини очиб юбориш учун васвасага солди ва: «Парвардигорингиз фақат фаришталарга айланмаслигингиз ёки (жаннатда) абадий яшаб қолмаслигингиз учунгина сизларни бу дарахтдан қайтарди», деди'}, {'chapter': 7, 'verse': 21, 'text': 'وَقَاسَمَهُمَآ إِنِّي لَكُمَا لَمِنَ ٱلنَّـٰصِحِينَ\nВа уларга: «Албатта мен сизларга холисларданман», деб қасам ичди'}, {'chapter': 7, 'verse': 22, 'text': 'فَدَلَّىٰهُمَا بِغُرُورٖۚ فَلَمَّا ذَاقَا ٱلشَّجَرَةَ بَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۖ وَنَادَىٰهُمَا رَبُّهُمَآ أَلَمۡ أَنۡهَكُمَا عَن تِلۡكُمَا ٱلشَّجَرَةِ وَأَقُل لَّكُمَآ إِنَّ ٱلشَّيۡطَٰنَ لَكُمَا عَدُوّٞ مُّبِينٞ\nБас, алдов билан уларни (тубан ҳолатга) тушириб қўйди. Ўша дарахтдан тотиб кўришлари биланоқ авратлари очилиб қолди ва ўзларини жаннат япроқлари билан тўса бошладилар. (Шунда) уларга Парвардигорлари нидо қилиб (деди): «Сизларни бу дарахтдан қайтармаганмидим ва албатта шайтон сизларнинг очиқ душманингиз демаганмидим?!»'}, {'chapter': 7, 'verse': 23, 'text': 'قَالَا رَبَّنَا ظَلَمۡنَآ أَنفُسَنَا وَإِن لَّمۡ تَغۡفِرۡ لَنَا وَتَرۡحَمۡنَا لَنَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ\nУлар: «Парвардигоро, бизлар ўз жонимизга жабр-зулм қилдик. Агар бизларни мағфират ва раҳм қилмасанг, шубҳасиз, зиён кўргувчилардан бўлиб қолурмиз», дедилар'}, {'chapter': 7, 'verse': 24, 'text': 'قَالَ ٱهۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي ٱلۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ\nАллоҳ айтди: «Бир-бирингизга (яъни, шайтон инсонга, инсон шайтонга) душман бўлган ҳолингизда (жаннатдан) тушингиз! Энди сизлар учун маълум бир вақтгача (яъни, ажалингиз етгунча) Ерда қарор топиб фойдаланиш бор'}, {'chapter': 7, 'verse': 25, 'text': 'قَالَ فِيهَا تَحۡيَوۡنَ وَفِيهَا تَمُوتُونَ وَمِنۡهَا تُخۡرَجُونَ\nАллоҳ айтди: Унда яшайсизлар ва унда ўлурсизлар ва ундан (ҳисоб-китоб учун) чиқарилурсизлар'}, {'chapter': 7, 'verse': 26, 'text': 'يَٰبَنِيٓ ءَادَمَ قَدۡ أَنزَلۡنَا عَلَيۡكُمۡ لِبَاسٗا يُوَٰرِي سَوۡءَٰتِكُمۡ وَرِيشٗاۖ وَلِبَاسُ ٱلتَّقۡوَىٰ ذَٰلِكَ خَيۡرٞۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِ لَعَلَّهُمۡ يَذَّكَّرُونَ\nЭй Одам болалари, Биз сизларга авратларингизни беркитадиган либосни ҳам, ясан-тусан (либосини) ҳам туширдик. (Ҳаммасидан) яхшироқ либос тақво либосидир. Бу — Аллоҳнинг (фазлу марҳаматига далолат қилгувчи) оятларидандир. Шояд эслатма (панд-насиҳат) олсалар'}, {'chapter': 7, 'verse': 27, 'text': 'يَٰبَنِيٓ ءَادَمَ لَا يَفۡتِنَنَّكُمُ ٱلشَّيۡطَٰنُ كَمَآ أَخۡرَجَ أَبَوَيۡكُم مِّنَ ٱلۡجَنَّةِ يَنزِعُ عَنۡهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوۡءَٰتِهِمَآۚ إِنَّهُۥ يَرَىٰكُمۡ هُوَ وَقَبِيلُهُۥ مِنۡ حَيۡثُ لَا تَرَوۡنَهُمۡۗ إِنَّا جَعَلۡنَا ٱلشَّيَٰطِينَ أَوۡلِيَآءَ لِلَّذِينَ لَا يُؤۡمِنُونَ\nЭй Одам болалари, шайтон Оталарингизнинг авратларини ўзларига кўрсатиш (яъни, уятли аҳволга солиб қўйиш) учун либосларини ечиб, жаннатдан чиқаргани каби сизларни ҳам алдаб қўймасин! Чунки у ва унинг малайлари сизларни ўзингиз билмайдиган тарафдан кўриб турадилар (яъни, қандай алдашганини сезмай қолишингиз мумкин). Албатта, Биз шайтонни иймонсиз кимсаларга дўст қилиб қўйганмиз'}, {'chapter': 7, 'verse': 28, 'text': 'وَإِذَا فَعَلُواْ فَٰحِشَةٗ قَالُواْ وَجَدۡنَا عَلَيۡهَآ ءَابَآءَنَا وَٱللَّهُ أَمَرَنَا بِهَاۗ قُلۡ إِنَّ ٱللَّهَ لَا يَأۡمُرُ بِٱلۡفَحۡشَآءِۖ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nУлар қачон бирон бузуқ иш қилсалар (масалан, улар Каъбани яланғоч ҳолларида тавоф қилар эдилар): «Ота-боболаримизни шундай ҳолда топганмиз. Буни бизга Аллоҳ буюрган», дейдилар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Аллоҳ ҳеч қачон бундай бузуқ ишларга буюрмайди. Аллоҳ шаънига (ўзингиз) билмайдиган нарсаларни айтаверасизларми?!»'}, {'chapter': 7, 'verse': 29, 'text': 'قُلۡ أَمَرَ رَبِّي بِٱلۡقِسۡطِۖ وَأَقِيمُواْ وُجُوهَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَٱدۡعُوهُ مُخۡلِصِينَ لَهُ ٱلدِّينَۚ كَمَا بَدَأَكُمۡ تَعُودُونَ\nАйтинг: «Парвардигорим адолатга буюргандир. Ҳар бир сажда чоғида ўзларингизни тикиб қўйингиз (яъни, бор вужудингиз билан сидқидилдан сажда қилингиз) ва У зотга динингизни холис қилган ҳолда илтижо этингиз. Сизларни бошлаб яратган ҳолига қайтурсиз (яъни, аввал сизлардан йўқдан бор қилгани каби Қиёмат Кунида яна ҳисоб-китоб учун қабрларингиздан чиқарур)'}, {'chapter': 7, 'verse': 30, 'text': 'فَرِيقًا هَدَىٰ وَفَرِيقًا حَقَّ عَلَيۡهِمُ ٱلضَّلَٰلَةُۚ إِنَّهُمُ ٱتَّخَذُواْ ٱلشَّيَٰطِينَ أَوۡلِيَآءَ مِن دُونِ ٱللَّهِ وَيَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ\nУ бир гуруҳни ҳидоят қилди, бошқа бир гуруҳга эса йўлдан озиш ҳақ бўлди. Чунки улар Аллоҳни қўйиб, шайтонни дўст тутиниб олганлар-да, яна ўзларини ҳидоят топгувчилар, деб ҳисоблайдилар'}, {'chapter': 7, 'verse': 31, 'text': '۞يَٰبَنِيٓ ءَادَمَ خُذُواْ زِينَتَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَكُلُواْ وَٱشۡرَبُواْ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡرِفِينَ\nЭй Одам болалари, ҳар бир сажда чоғида зийнатланингиз (яъни, тоза либосда бўлингиз) ҳамда (хоҳлаганингизча) еб-ичаверинглар, фақат исроф қилманглар. Зотан, У исроф қилгувчи кимсаларни севмас'}, {'chapter': 7, 'verse': 32, 'text': 'قُلۡ مَنۡ حَرَّمَ زِينَةَ ٱللَّهِ ٱلَّتِيٓ أَخۡرَجَ لِعِبَادِهِۦ وَٱلطَّيِّبَٰتِ مِنَ ٱلرِّزۡقِۚ قُلۡ هِيَ لِلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا خَالِصَةٗ يَوۡمَ ٱلۡقِيَٰمَةِۗ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Аллоҳ бандалари учун чиқарган безак (либосларни) ва ҳалол-пок ризқларни ким ҳаром қилди?!» Айтинг: «Улар (яъни, безак ва ҳалол ризқлар) ҳаёти дўнёда иймон эгалари учун (бўлиб, кофирлар ҳам уларга шерик бўлур), Қиёмат Кунида эса фақат (мўминларнинг) ўзларига хосдир». Биладиган қавм учун оятларни мана шундай муфассал қилурмиз'}, {'chapter': 7, 'verse': 33, 'text': 'قُلۡ إِنَّمَا حَرَّمَ رَبِّيَ ٱلۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَ وَٱلۡإِثۡمَ وَٱلۡبَغۡيَ بِغَيۡرِ ٱلۡحَقِّ وَأَن تُشۡرِكُواْ بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗا وَأَن تَقُولُواْ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\nАйтинг: «Парвардигорим фақатгина ошкор ва яширин бузуқликларни, (барча) гуноҳ ишларни, ноҳақ зулм қилишни ва Аллоҳга (шерик эканлигига) ҳеч қандай ҳужжат туширмаган нарсаларни Унга шерик қилиб олишингизни ҳамда Аллоҳ шаънига билмайдиган нарсаларни гапиришингизни ҳаром қилган, холос'}, {'chapter': 7, 'verse': 34, 'text': 'وَلِكُلِّ أُمَّةٍ أَجَلٞۖ فَإِذَا جَآءَ أَجَلُهُمۡ لَا يَسۡتَأۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ\nҲар бир жамоат — қавм учун ажал бордир. Бас, қачон уларга ажаллари келса, уни бирон соатга кетга ҳам, илгарига ҳам сура олмайдилар'}, {'chapter': 7, 'verse': 35, 'text': 'يَٰبَنِيٓ ءَادَمَ إِمَّا يَأۡتِيَنَّكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي فَمَنِ ٱتَّقَىٰ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nЭй Одам болалари, ҳар қачон сизларга ўзларингиздан бўлган пайғамбарлар Менинг оятларимни сўзлаган ҳолларида келганларида кимда-ким (ширкдан) сақланиб, ўзини ўнглаб олса, уларга хавф-хатар йўқдир ва улар ғамгин бўлмайдилар'}, {'chapter': 7, 'verse': 36, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱسۡتَكۡبَرُواْ عَنۡهَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nБизнинг оятларимизни ёлғон деган ва улардан кибру ҳаво билан (юз ўгирган) кимсалар эса дўзах эгаларидир. Улар у жойда (дўзахда) абадий қолгувчидирлар'}, {'chapter': 7, 'verse': 37, 'text': 'فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ أُوْلَـٰٓئِكَ يَنَالُهُمۡ نَصِيبُهُم مِّنَ ٱلۡكِتَٰبِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمۡ رُسُلُنَا يَتَوَفَّوۡنَهُمۡ قَالُوٓاْ أَيۡنَ مَا كُنتُمۡ تَدۡعُونَ مِن دُونِ ٱللَّهِۖ قَالُواْ ضَلُّواْ عَنَّا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كَٰفِرِينَ\nАллоҳ шаънига ёлғон тўқиган ёки Унинг оятларини ёлғон деган кимсадан ҳам золимроқ ким бор?! Уларга ёзилмиш — китобдан бўлган ризқ-рўзлари етиб-битгач, жонларини олгувчи элчиларимиз — ўлим фаришталари келиб: «Аллоҳни қўйиб, илтижо қилиб ўтган бутларингиз қани (келиб сизларни ўлимдан қутқармайдиларми?)», деганларида, улар: «Биздан ғойиб бўлиб қолдилар», дейишади ва ўзларининг зиёнларига — кофир бўлганликларига гувоҳлик беришади'}, {'chapter': 7, 'verse': 38, 'text': 'قَالَ ٱدۡخُلُواْ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِكُم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِ فِي ٱلنَّارِۖ كُلَّمَا دَخَلَتۡ أُمَّةٞ لَّعَنَتۡ أُخۡتَهَاۖ حَتَّىٰٓ إِذَا ٱدَّارَكُواْ فِيهَا جَمِيعٗا قَالَتۡ أُخۡرَىٰهُمۡ لِأُولَىٰهُمۡ رَبَّنَا هَـٰٓؤُلَآءِ أَضَلُّونَا فَـَٔاتِهِمۡ عَذَابٗا ضِعۡفٗا مِّنَ ٱلنَّارِۖ قَالَ لِكُلّٖ ضِعۡفٞ وَلَٰكِن لَّا تَعۡلَمُونَ\n(Аллоҳ) айтур: «Сизлардан илгари ўтган жин ва инсдан иборат (кофир) миллатлар билан бирга дўзахга кирингиз!» Қачон бир жамоат (ўша дўзахга) кирганида, шеригини (яъни, ўзини йўлдан урганларни) лаънатлайди. Қачонки унда (дўзахда) ҳаммалари топишишгач, кейингилари (эргашганлар) аввалгилари (бошлиқлари) ҳақида: «Парвардигоро, ана ўшалар бизларни йўлдан оздирганлар, бас, уларга дўзах азобини икки баробар қилгин», дейдилар. (Аллоҳ) айтур: «Ҳар бирингиз учун икки баробар азоб бўлур, лекин сизлар билмайсизлар»'}, {'chapter': 7, 'verse': 39, 'text': 'وَقَالَتۡ أُولَىٰهُمۡ لِأُخۡرَىٰهُمۡ فَمَا كَانَ لَكُمۡ عَلَيۡنَا مِن فَضۡلٖ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡسِبُونَ\nАввалгилари эса кейингиларига: «Сизларнинг биздан бирон афзаллик томонларингиз йўқдир, бас, қилиб ўтган гуноҳларингизга яраша азобингизни тотаверинглар», дейдилар'}, {'chapter': 7, 'verse': 40, 'text': 'إِنَّ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱسۡتَكۡبَرُواْ عَنۡهَا لَا تُفَتَّحُ لَهُمۡ أَبۡوَٰبُ ٱلسَّمَآءِ وَلَا يَدۡخُلُونَ ٱلۡجَنَّةَ حَتَّىٰ يَلِجَ ٱلۡجَمَلُ فِي سَمِّ ٱلۡخِيَاطِۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُجۡرِمِينَ\nАлбатта, оятларимизни ёлғон деган ва улардан кибр-ҳаво билан (юз ўгирган) кимсалар учун ҳаргиз само эшиклари очилмас ва токи туя игна тешигидан ўтмас экан, улар ҳам жаннатга кира олмаслар (яъни, ҳеч қачон кирмаслар). Жинояткор — осийларни мана шундай жазолагаймиз'}, {'chapter': 7, 'verse': 41, 'text': 'لَهُم مِّن جَهَنَّمَ مِهَادٞ وَمِن فَوۡقِهِمۡ غَوَاشٖۚ وَكَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ\nУлар учун жаҳаннамдан тўшак, устларидан эса (оловдан бўлган) чойшаблар бўлур. Золимларни мана шундай жазолагаймиз'}, {'chapter': 7, 'verse': 42, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nИймон келтириб, яхши амаллар қилган зотлар — ҳеч бир жонни тоқатидан ташқари нарсага таклиф қилмаймиз – улар жаннат эгалари бўлиб, унда абадий қолажаклар'}, {'chapter': 7, 'verse': 43, 'text': 'وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلّٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُۖ وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي هَدَىٰنَا لِهَٰذَا وَمَا كُنَّا لِنَهۡتَدِيَ لَوۡلَآ أَنۡ هَدَىٰنَا ٱللَّهُۖ لَقَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّۖ وَنُودُوٓاْ أَن تِلۡكُمُ ٱلۡجَنَّةُ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ\nОстларидан дарёлар оқиб турар экан, уларнинг кўнгилларидан (бир-бирларига нисбатан бўлган) ғиллу ғашликларни тортиб олурмиз ва улар: «Бизларни бу (неъматларга) йўллаган Зот — Аллоҳга ҳамду-сано бўлгай. Агар бизни Аллоҳ ҳидоят қилмаганида, ҳаргиз йўл топа олмас эдик. Ҳақиқатан, Парвардигоримизнинг пайғамбарлари ҳақ динни келтирган эканлар», дейдилар ва уларга: «Қилиб ўтган (яхши) амалларингиз сабабли сизларга мерос қилиб берилган жаннат мана шудир», деб нидо қилинур'}, {'chapter': 7, 'verse': 44, 'text': 'وَنَادَىٰٓ أَصۡحَٰبُ ٱلۡجَنَّةِ أَصۡحَٰبَ ٱلنَّارِ أَن قَدۡ وَجَدۡنَا مَا وَعَدَنَا رَبُّنَا حَقّٗا فَهَلۡ وَجَدتُّم مَّا وَعَدَ رَبُّكُمۡ حَقّٗاۖ قَالُواْ نَعَمۡۚ فَأَذَّنَ مُؤَذِّنُۢ بَيۡنَهُمۡ أَن لَّعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِينَ\n(Шунда) жаннат эгалари дўзах эгаларига қараб: «Бизлар Парвардигоримиз берган ваъданинг (яъни, жаннат ва ундаги неъматларнинг) ҳақ эканини кўрдик, сизлар ҳам Парвардигорингиз ваъда қилган нарсанинг (яъни, дўзах ва ундаги азоб-уқубатларниш) ҳақ эканини кўрдингизми?!» деганларида, улар: «Ҳа», дейдилар. Бас, уларнинг ўрталарида бир жарчи: «Золимларга Аллоҳнинг лаънати бўлгай», деб жар солур'}, {'chapter': 7, 'verse': 45, 'text': 'ٱلَّذِينَ يَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِٱلۡأٓخِرَةِ كَٰفِرُونَ\nУлар Аллоҳнинг йўлидан тўсадиган ва уни бузишни истайдиган, ўзлари Охиратга ишонмайдиган кимсалардир'}, {'chapter': 7, 'verse': 46, 'text': 'وَبَيۡنَهُمَا حِجَابٞۚ وَعَلَى ٱلۡأَعۡرَافِ رِجَالٞ يَعۡرِفُونَ كُلَّۢا بِسِيمَىٰهُمۡۚ وَنَادَوۡاْ أَصۡحَٰبَ ٱلۡجَنَّةِ أَن سَلَٰمٌ عَلَيۡكُمۡۚ لَمۡ يَدۡخُلُوهَا وَهُمۡ يَطۡمَعُونَ\nУларнинг (жаннат эгалари билан дўзах эгаларининг) ўрталарида тўсиқ бўлиб, у деворлар устида ҳаммани (яъни, жаннатиларни ҳам, дузахиларни ҳам) сиймоларидан таниб оладиган кишилар бўлур. Улар жаннат эгаларига: «Сизларга тинчлик бўлгай», дейдилар. Ўзлари эса тамаъ қилган — истаган ҳолларида, унга (жаннатга) кира олмайдилар'}, {'chapter': 7, 'verse': 47, 'text': '۞وَإِذَا صُرِفَتۡ أَبۡصَٰرُهُمۡ تِلۡقَآءَ أَصۡحَٰبِ ٱلنَّارِ قَالُواْ رَبَّنَا لَا تَجۡعَلۡنَا مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nҚачон кўзлари дўзах эгалари тарафга бурилиб қолса: «Парвардигоро, бизларни бу золим қавм билан бирга қилиб қўймагайсан», дейдилар'}, {'chapter': 7, 'verse': 48, 'text': 'وَنَادَىٰٓ أَصۡحَٰبُ ٱلۡأَعۡرَافِ رِجَالٗا يَعۡرِفُونَهُم بِسِيمَىٰهُمۡ قَالُواْ مَآ أَغۡنَىٰ عَنكُمۡ جَمۡعُكُمۡ وَمَا كُنتُمۡ تَسۡتَكۡبِرُونَ\nАъроф эгалари (яъни, қилган яхши амаллари билан ёмон амаллари баробар келиб, жаннати ҳам, дўзахи ҳам бўлмай ўртада аъроф — деворлар устида турган кишилар) сиймоларидан таниб олган кишиларига (яъни, дўзахиларга) нидо қилиб дедилар: «Тўплаган молу дунёингиз ва қилган кибру ҳавойингиз сизларга асқотмабди-ку!»'}, {'chapter': 7, 'verse': 49, 'text': 'أَهَـٰٓؤُلَآءِ ٱلَّذِينَ أَقۡسَمۡتُمۡ لَا يَنَالُهُمُ ٱللَّهُ بِرَحۡمَةٍۚ ٱدۡخُلُواْ ٱلۡجَنَّةَ لَا خَوۡفٌ عَلَيۡكُمۡ وَلَآ أَنتُمۡ تَحۡزَنُونَ\nСизлар: «Аллоҳ уларга бирон раҳмат-марҳамат етказмайди, деб қасам ичган кишилар ана у (ҳаёти дунёдан камбағал-бечоралик билан ўтган мўмин-мусулмон)ларми? (Ахир уларга) «Сизлар учун ҳеч қандай хавф-хатар йўқ ва сизлар ҳеч ғамгин бўлмайсизлар» (дейилди-ку)'}, {'chapter': 7, 'verse': 50, 'text': 'وَنَادَىٰٓ أَصۡحَٰبُ ٱلنَّارِ أَصۡحَٰبَ ٱلۡجَنَّةِ أَنۡ أَفِيضُواْ عَلَيۡنَا مِنَ ٱلۡمَآءِ أَوۡ مِمَّا رَزَقَكُمُ ٱللَّهُۚ قَالُوٓاْ إِنَّ ٱللَّهَ حَرَّمَهُمَا عَلَى ٱلۡكَٰفِرِينَ\nДўзах эгалари жаннат эгаларига: «Бизларга ҳам сувдан ё Аллоҳ сизларни баҳраманд қилган нарсалардан тўкинглар», деб нидо қилганларида, улар: «Аллоҳ бу неъматларини кофирларга ҳаром қилгандир», дедилар'}, {'chapter': 7, 'verse': 51, 'text': 'ٱلَّذِينَ ٱتَّخَذُواْ دِينَهُمۡ لَهۡوٗا وَلَعِبٗا وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ نَنسَىٰهُمۡ كَمَا نَسُواْ لِقَآءَ يَوۡمِهِمۡ هَٰذَا وَمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ\nУлар динларини ҳазил-мазах қилиб олган ва ҳаёти дунёга алданиб қолган кимсалардир. Бас бу Кун улар бугунги учрашувни унутиб қўйганлари ва Бизнинг оятларимизни инкор қилувчи бўлганлари каби Биз ҳам уларни «унутиб қўярмиз»'}, {'chapter': 7, 'verse': 52, 'text': 'وَلَقَدۡ جِئۡنَٰهُم بِكِتَٰبٖ فَصَّلۡنَٰهُ عَلَىٰ عِلۡمٍ هُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ\nҲақиқатан, Биз уларга (Макка аҳолисига) иймон келтирувчи қавм учун ҳидоят ва раҳмат бўлган ва Ўз билимимиз билан муфассал қилган бир Китоб (Қуръон) келтирдик'}, {'chapter': 7, 'verse': 53, 'text': 'هَلۡ يَنظُرُونَ إِلَّا تَأۡوِيلَهُۥۚ يَوۡمَ يَأۡتِي تَأۡوِيلُهُۥ يَقُولُ ٱلَّذِينَ نَسُوهُ مِن قَبۡلُ قَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّ فَهَل لَّنَا مِن شُفَعَآءَ فَيَشۡفَعُواْ لَنَآ أَوۡ نُرَدُّ فَنَعۡمَلَ غَيۡرَ ٱلَّذِي كُنَّا نَعۡمَلُۚ قَدۡ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nУлар эса (бу Китобнинг) таъвийл-оқибатини (яъни, ундаги оятлар ҳақ эканлиги исботланишини) кутадилар, холос. Унинг таъвийли келадиган Кунда илгари уни унутган (яъни, ундан юз ўгирган) кимсалар: «Парвардигоримизнинг пайғамбарлари ҳақ динни келтирган эканлар, энди бизни оқлайдиган оқловчилар бормикан ёки қилиб ўтган (ёмон) ишларимиздан бошқа (эзгу) амалларни қилиш учун (яна дунёга) қайтарилармиканмиз», деб қоладилар. Улар ҳақиқатан, ўзларига зиён қилдилар ва ўзлари тўқиб олган бутлари улардан ғойиб бўлди'}, {'chapter': 7, 'verse': 54, 'text': 'إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يُغۡشِي ٱلَّيۡلَ ٱلنَّهَارَ يَطۡلُبُهُۥ حَثِيثٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ وَٱلنُّجُومَ مُسَخَّرَٰتِۭ بِأَمۡرِهِۦٓۗ أَلَا لَهُ ٱلۡخَلۡقُ وَٱلۡأَمۡرُۗ تَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nАлбатта, Парвардигорингиз — Аллоҳ шундай Зотдирки, осмонлар ва Ерни олти кунда яратиб, сўнгра (Зоти ва Улуғлигига муносиб суратда) Аршга юксалди. У кечани (қоронғуликни) кундузга ўрар (ва кеча кундузни) шошилган ҳолда қувиб юрар. У қуёш, ой ва юлдузларни Ўз амрига бўйсундирилган ҳолда (яратди). Огоҳ бўлингизким, яратиш ва буюриш фақат Уникидир. Барча оламлар Парвардигори бўлмиш Аллоҳ баракотли Буюкдир'}, {'chapter': 7, 'verse': 55, 'text': 'ٱدۡعُواْ رَبَّكُمۡ تَضَرُّعٗا وَخُفۡيَةًۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ\n(Эй мўминлар), сизлар Парвардигорингизга тазарру билан ичингизда (махфий) илтижо қилингиз! Зотан, У ҳаддан ошувчи кимсаларни (яъни, эл кўзига кўрсатиш учун риёкорлик қилувчиларни) севмас'}, {'chapter': 7, 'verse': 56, 'text': 'وَلَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَا وَٱدۡعُوهُ خَوۡفٗا وَطَمَعًاۚ إِنَّ رَحۡمَتَ ٱللَّهِ قَرِيبٞ مِّنَ ٱلۡمُحۡسِنِينَ\nВа (Аллоҳнинг дини билан) ўнглаб қўйилганидан кейин, Ерда (куфр йўлини тутиб) бузғунчилик қилманг! У Зотга (азобидан) қўрқиб, (раҳматидан) умидвор бўлган ҳолингизда ибодат қилингиз! Зеро, Аллоҳнинг раҳмати чиройли амал қилувчиларга яқиндир'}, {'chapter': 7, 'verse': 57, 'text': 'وَهُوَ ٱلَّذِي يُرۡسِلُ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۖ حَتَّىٰٓ إِذَآ أَقَلَّتۡ سَحَابٗا ثِقَالٗا سُقۡنَٰهُ لِبَلَدٖ مَّيِّتٖ فَأَنزَلۡنَا بِهِ ٱلۡمَآءَ فَأَخۡرَجۡنَا بِهِۦ مِن كُلِّ ٱلثَّمَرَٰتِۚ كَذَٰلِكَ نُخۡرِجُ ٱلۡمَوۡتَىٰ لَعَلَّكُمۡ تَذَكَّرُونَ\nУ шундай Зотки, Ўз раҳматининг (яъни, ёмғирнинг) олдидан хушхабар қилиб шамолларни юборур. Қачонки улар вазмин булутларни кўтариб келгач, Биз уни (булутни, чанқоқ) ўлик шаҳарга ёғдирурмиз. Бас, унга сув-ёмғир ёғдириб, унинг ёрдамида ҳар турли мевалар чиқарурмиз. Ўликларни ҳам (ўз жойларидан) мана шундай чиқарурмиз. Шояд эслатма-ибрат олсангизлар'}, {'chapter': 7, 'verse': 58, 'text': 'وَٱلۡبَلَدُ ٱلطَّيِّبُ يَخۡرُجُ نَبَاتُهُۥ بِإِذۡنِ رَبِّهِۦۖ وَٱلَّذِي خَبُثَ لَا يَخۡرُجُ إِلَّا نَكِدٗاۚ كَذَٰلِكَ نُصَرِّفُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَشۡكُرُونَ\nПок шаҳар — ернинг гиёҳи Парвардигорининг изни-иродаси билан (осон) униб чиқур. Нопок (яъни, шўр, тошлоқ) ерники эса фақат қийинчилик — машаққат билангина чиқур. Шукр қиладиган қавм учун оят-аломатларимизни мана шундай баён қилиб берурмиз'}, {'chapter': 7, 'verse': 59, 'text': 'لَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ\nҚасамки, Нуҳни ўз қавмига пайғамбар қилдик. Бас, у: «Эй қавмим, Аллоҳга ибодат қилинглар! Сизлар учун Ундан ўзга бирон Илоҳ йўқдир. Албатта, мен сизларнинг буюк Кун (Қиёмат Кунининг) азобига гирифтор бўлишингиздан қўрқаман», деди'}, {'chapter': 7, 'verse': 60, 'text': 'قَالَ ٱلۡمَلَأُ مِن قَوۡمِهِۦٓ إِنَّا لَنَرَىٰكَ فِي ضَلَٰلٖ مُّبِينٖ\n(Шунда) унинг қавмидан (зодагон) одамлар: «Биз сенинг очиқ залолатда эканлигингни кўрмоқдамиз», дейишди'}, {'chapter': 7, 'verse': 61, 'text': 'قَالَ يَٰقَوۡمِ لَيۡسَ بِي ضَلَٰلَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nУ айтди: «Эй қавмим, мен мутлақо залолатда эмасман, балки мен барча оламларнинг Парвардигори тарафидан юборилган пайғамбарман'}, {'chapter': 7, 'verse': 62, 'text': 'أُبَلِّغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنصَحُ لَكُمۡ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\nМен сизларга Парвардигоримнинг вазифаларини (буюрганларини) етказурман ва сизларга насиҳат қилурман ҳамда Аллоҳ тарафидан сизлар билмайдиган нарсаларни билурман'}, {'chapter': 7, 'verse': 63, 'text': 'أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡ وَلِتَتَّقُواْ وَلَعَلَّكُمۡ تُرۡحَمُونَ\nСизларни (Охират азобидан) огоҳлантириш учун ва тақводор бўлишингиз ҳамда (Аллоҳнинг) раҳматига эришишингиз учун сизларга Парвардигорингиздан бўлган эслатма (огоҳлантириш) ўзингиздан бўлган бир киши зиммасида (яъни, менинг зиммамда) келганидан ажабландингизми?»'}, {'chapter': 7, 'verse': 64, 'text': 'فَكَذَّبُوهُ فَأَنجَيۡنَٰهُ وَٱلَّذِينَ مَعَهُۥ فِي ٱلۡفُلۡكِ وَأَغۡرَقۡنَا ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمًا عَمِينَ\nБас, уни ёлғончи қилдилар. Шунда Биз уни ва у билан бирга бўлган (унга иймон келтирган) зотларни кемада қутқардик ва Бизнинг оятларимизни ёлғон деган кимсаларни ғарқ қилиб юбордик. Чунки улар (кўнгиллари) кўр бўлган қавм эдилар'}, {'chapter': 7, 'verse': 65, 'text': '۞وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ\nОд қавмига ўз биродарлари Ҳудни (пайғамбар қилдик). У айтди: «Эй қавмим, Аллоҳга ибодат қилингиз! Сизлар учун Ундан ўзга бирон илоҳ йўқдир. Унинг азобидан қўрқмайсизларми?!»'}, {'chapter': 7, 'verse': 66, 'text': 'قَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦٓ إِنَّا لَنَرَىٰكَ فِي سَفَاهَةٖ وَإِنَّا لَنَظُنُّكَ مِنَ ٱلۡكَٰذِبِينَ\n(Шунда) унинг қавмидан кофир бўлган одамлар: «Биз сенинг нодон эканингни кўриб турибмиз ва албатта биз сени ёлғончилардан, деб гумон қилмоқдамиз», дейишди'}, {'chapter': 7, 'verse': 67, 'text': 'قَالَ يَٰقَوۡمِ لَيۡسَ بِي سَفَاهَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\nУ айтди: «Эй қавмим, мен нодон эмасман, балки мен барча оламлар Парвардигори тарафидан юборилган пайғамбарман'}, {'chapter': 7, 'verse': 68, 'text': 'أُبَلِّغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنَا۠ لَكُمۡ نَاصِحٌ أَمِينٌ\nМен сизларга Парвардигоримнинг вазифаларини (буюрганларини) етказурман ва мен сизлар учун ишончли насиҳат қилгувчиман'}, {'chapter': 7, 'verse': 69, 'text': 'أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡۚ وَٱذۡكُرُوٓاْ إِذۡ جَعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ قَوۡمِ نُوحٖ وَزَادَكُمۡ فِي ٱلۡخَلۡقِ بَصۜۡطَةٗۖ فَٱذۡكُرُوٓاْ ءَالَآءَ ٱللَّهِ لَعَلَّكُمۡ تُفۡلِحُونَ\nСизларга Парвардигорингиздан бўлган эслатма (сизларни Охират азобидан) огоҳлантириш учун ўзингиздан бўлган бир киши зиммасида келганидан ажабландингизми? (Аллоҳ) сизларни Нуҳ қавмидан кейин халифа (ўринбосар) қилиб қўйганини ва куч-қувватингизни зиёда қилиб яратганини эслангиз! Бас, Аллоҳнинг неъматларини эслангиз, шояд нажот топурсиз'}, {'chapter': 7, 'verse': 70, 'text': 'قَالُوٓاْ أَجِئۡتَنَا لِنَعۡبُدَ ٱللَّهَ وَحۡدَهُۥ وَنَذَرَ مَا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУлар: «Сен бизга, ёлғиз Аллоҳга ибодат қилишимиз ва ота-боболаримиз сиғиниб ўтган бутларни тарк қилишимиз учун келдингми? У ҳолда, агар ростгўй кишилардан бўлсанг, бизга ваъда қилаётган нарсангни (яъни, Аллоҳнинг азобини) келтиргин-чи?» дедилар'}, {'chapter': 7, 'verse': 71, 'text': 'قَالَ قَدۡ وَقَعَ عَلَيۡكُم مِّن رَّبِّكُمۡ رِجۡسٞ وَغَضَبٌۖ أَتُجَٰدِلُونَنِي فِيٓ أَسۡمَآءٖ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّا نَزَّلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٖۚ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ\nУ айтди: «Энди устингизга Парвардигорингиз томонидан азоб ва ғазаб тушиши аниқ бўлди. Мен билан ўзингиз ва ота-боболарингиз қўйиб олган (бутларингизнинг номлари ҳақида баҳслашмоқчимисизлар? Ахир Аллоҳ (уларга ибодат қилиш ҳақида) бирон ҳужжат туширмаган-ку?! У ҳолда (Аллоҳнинг азобига) кўз тутаверинглар, мен ҳам сизлар билан бирга кутувчиларданман»'}, {'chapter': 7, 'verse': 72, 'text': 'فَأَنجَيۡنَٰهُ وَٱلَّذِينَ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَقَطَعۡنَا دَابِرَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ وَمَا كَانُواْ مُؤۡمِنِينَ\nБас, унга (Ҳудга) ва у билан бирга бўлган (мўмин зотларга) Ўз раҳмат-марҳаматимиз билан нажот бердик ва Бизнинг оятларимизни ёлғон деган кимсаларнинг думларини қирқдик (ҳалок қилдик). Улар мўмин эмас эдилар'}, {'chapter': 7, 'verse': 73, 'text': 'وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَدۡ جَآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ هَٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٌ أَلِيمٞ\nСамуд қавмига ўз биродарлари Солиҳни (пайғамбар қилдик). У айтди: «Эй қавмим, Аллоҳга ибодат қилингиз! Сизлар учун Ундан ўзга бирон илоҳ йўқдир. Сизларга Парвардигорингиз томонидан ҳужжат — мана бу Аллоҳ (юборган) туя сизлар учун оят — мўъжиза бўлиб келди. Бас, уни Аллоҳнинг ерида еб-ичиб юрган ҳолида қўйиб юборинглар ва унга бирон ёмонлик етказмангларки, у ҳолда сизларни аламли азоб ушлайди (азобга йўлиқасизлар)»'}, {'chapter': 7, 'verse': 74, 'text': 'وَٱذۡكُرُوٓاْ إِذۡ جَعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ عَادٖ وَبَوَّأَكُمۡ فِي ٱلۡأَرۡضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُورٗا وَتَنۡحِتُونَ ٱلۡجِبَالَ بُيُوتٗاۖ فَٱذۡكُرُوٓاْ ءَالَآءَ ٱللَّهِ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\n«Сизларни Од (қавми)дан кейин халифа қилиб қўйганини ва сизларга Ернинг текисликларида (ёзлик) қасрлар қуриб олишингиз, тоғлик жойларида (қишлик) бошпаналар йўниб (тиклаб) олишингиз учун маскан берганини эслангиз! Бас, Аллоҳнинг неъматларини эслангиз ва Ерда бузғунчилик қилиб санқиб юрмангиз!»'}, {'chapter': 7, 'verse': 75, 'text': 'قَالَ ٱلۡمَلَأُ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لِلَّذِينَ ٱسۡتُضۡعِفُواْ لِمَنۡ ءَامَنَ مِنۡهُمۡ أَتَعۡلَمُونَ أَنَّ صَٰلِحٗا مُّرۡسَلٞ مِّن رَّبِّهِۦۚ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلَ بِهِۦ مُؤۡمِنُونَ\n(Шунда) унинг қавмидан бўлган мутакаббир кимсалар бечораларига — уларнинг ораларидаги мўмин бўлган зотларга: «Солиҳни Парвардигори тарафидан юборилган пайғамбар деб билурмисиз?», дейишди. Улар айтдилар: «Албатта, биз унинг воситасида юборилган нарсага (динга) иймон келтиргувчимиз»'}, {'chapter': 7, 'verse': 76, 'text': 'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا بِٱلَّذِيٓ ءَامَنتُم بِهِۦ كَٰفِرُونَ\nМутакаббир кимсалар эса: «Биз сизлар иймон келтирган динга кофирмиз», дейишди'}, {'chapter': 7, 'verse': 77, 'text': 'فَعَقَرُواْ ٱلنَّاقَةَ وَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ وَقَالُواْ يَٰصَٰلِحُ ٱئۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلۡمُرۡسَلِينَ\nКейин туяни сўйиб юборишди ва Парвардигорларининг амридан юз ўгиришди, сўнг: «Эй Солиҳ, агар пайғамбарлардан бўлсанг, бизга ваъда қилган нарсангни (яъни, Аллоҳнинг азобини) келтир-чи?», дейишди'}, {'chapter': 7, 'verse': 78, 'text': 'فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ\nБас, уларни даҳшатли зилзила тутиб, турган жойларида тўкилдилар (ҳалок бўлдилар)'}, {'chapter': 7, 'verse': 79, 'text': 'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَالَةَ رَبِّي وَنَصَحۡتُ لَكُمۡ وَلَٰكِن لَّا تُحِبُّونَ ٱلنَّـٰصِحِينَ\nКейин (Солиҳ) улардан юз ўгириб (ўзича) деди: «Эй қавмим, мана, мен сизларга Парвардигоримнинг динини етказдим ва сизларга насиҳатлар қилдим. Лекин сизлар холис насиҳат қилгувчиларни севмайсиз»'}, {'chapter': 7, 'verse': 80, 'text': 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ\nЛут (пайғамбар) ўз қавмига: «Шундай бузуқлик қиласизларми? Ахир сизлардан илгари бутун оламлардан бирон кимса бундай қилмаган эди-ку'}, {'chapter': 7, 'verse': 81, 'text': 'إِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ\n(Наҳотки) сизлар хотинларингизни қўйиб, нафсингизни қондириш учун эркакларга борсангиз?! Йўқ, сизлар ҳаддан ошувчи қавмдирсиз», деганини эсланг'}, {'chapter': 7, 'verse': 82, 'text': 'وَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوهُم مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ\nҚавмнинг жавоби эса фақат мана бундан иборат бўлди: «Уларни (яъни, Лут ва тобеъларини) қишлоғингиздан чиқариб юборингиз! Чунки улар ҳаддан ортиқ покиза одамлар экан»'}, {'chapter': 7, 'verse': 83, 'text': 'فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ\nБас, унга ва аҳлига (яъни, Лут ва унга тобеъ бўлган зотларга) нажот бердик. Магар унинг хотини (нажот топмади, чунки у Аллоҳнинг азобида) қолиб ҳалок бўлгувчилардан эди'}, {'chapter': 7, 'verse': 84, 'text': 'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ\nУларнинг устига даҳшатли ёмғир — тош ёғдирдик. Ана энди жиноятчи — осий қавмнинг оқибати (қисмати) қандай бўлганини кўринг'}, {'chapter': 7, 'verse': 85, 'text': 'وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَدۡ جَآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ فَأَوۡفُواْ ٱلۡكَيۡلَ وَٱلۡمِيزَانَ وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nМадян қавмига ўз биродарлари Шуайбни (пайғамбар қилдик). У айтди: «Эй қавмим, Аллоҳга ибодат қилингиз! Сизлар учун Ундан ўзга бирон илоҳ йўқдир. Сизларга Парвардигорингиз тарафидан ҳужжат (яъни, дин) келди. Бас, ўлчов ва тарозини комил қилиб (тортингиз) ва одамларнинг ҳақларидан уриб қолмангиз ҳамда Ер (юзи пайғамбарлар юбориш билан) ўнглаб қўйилганидан кейин, унда (кофирлик билан) бузғунчилик қилиб юрманглар. Агар мўмин бўлсангизлар, мана шу ўзларингиз учун яхшироқдир'}, {'chapter': 7, 'verse': 86, 'text': 'وَلَا تَقۡعُدُواْ بِكُلِّ صِرَٰطٖ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِيلِ ٱللَّهِ مَنۡ ءَامَنَ بِهِۦ وَتَبۡغُونَهَا عِوَجٗاۚ وَٱذۡكُرُوٓاْ إِذۡ كُنتُمۡ قَلِيلٗا فَكَثَّرَكُمۡۖ وَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ\nҲар кўчада (одамларни) қўрқитиб ва Аллоҳга иймон келтирган зотларни Унинг йўлида тўсиб ҳамда у йўлни бузишга ҳаракат қилиб ўтирмангиз! Озчилик (яъни, куч-қувватсиз, ночор) бўлган пайтингизда сизларни кўпайтирганини (яъни, куч-қудратли, азиз қилганини) эсланг ва бузғунчи кимсаларнинг оқибати (қисмати) қандай бўлганини кўринг'}, {'chapter': 7, 'verse': 87, 'text': 'وَإِن كَانَ طَآئِفَةٞ مِّنكُمۡ ءَامَنُواْ بِٱلَّذِيٓ أُرۡسِلۡتُ بِهِۦ وَطَآئِفَةٞ لَّمۡ يُؤۡمِنُواْ فَٱصۡبِرُواْ حَتَّىٰ يَحۡكُمَ ٱللَّهُ بَيۡنَنَاۚ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ\nАгар сизлардан бир тоифа мен билан юборилган динга иймон келтирувчи бўлиб, бошқа бир тоифа эса иймон келтирмаса, у ҳолда то Аллоҳ ўртамизда Ўз ҳукмини чиқаргунича (яъни, ким ҳақ, ким ноҳақ эканлиги маълум бўлгунича) сабр қилингиз. У ҳукм қилгувчиларнинг яхшироғидир»'}, {'chapter': 7, 'verse': 88, 'text': '۞قَالَ ٱلۡمَلَأُ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لَنُخۡرِجَنَّكَ يَٰشُعَيۡبُ وَٱلَّذِينَ ءَامَنُواْ مَعَكَ مِن قَرۡيَتِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۚ قَالَ أَوَلَوۡ كُنَّا كَٰرِهِينَ\n(Шунда) унинг қавмидан бўлган мутакаббир кимсалар: «Эй Шуайб, ё сени ва сен билан бирга иймон келтирган кишиларни қишлоғимиздан ҳайдаб чиқарамиз, ёки сизлар бизнинг динимизга қайтасизлар», дейишди. У айтди: «Агар (динингизни) ёмон кўрсак ҳам-а'}, {'chapter': 7, 'verse': 89, 'text': 'قَدِ ٱفۡتَرَيۡنَا عَلَى ٱللَّهِ كَذِبًا إِنۡ عُدۡنَا فِي مِلَّتِكُم بَعۡدَ إِذۡ نَجَّىٰنَا ٱللَّهُ مِنۡهَاۚ وَمَا يَكُونُ لَنَآ أَن نَّعُودَ فِيهَآ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّنَاۚ وَسِعَ رَبُّنَا كُلَّ شَيۡءٍ عِلۡمًاۚ عَلَى ٱللَّهِ تَوَكَّلۡنَاۚ رَبَّنَا ٱفۡتَحۡ بَيۡنَنَا وَبَيۡنَ قَوۡمِنَا بِٱلۡحَقِّ وَأَنتَ خَيۡرُ ٱلۡفَٰتِحِينَ\nАгар Аллоҳ бизга ўша динингиздан нажот берганидан кейин яна унга қайтсак, Аллоҳ шаънига ёлғон тўқиган бўламиз-ку? (яъни, аслида бўлмаган нарсаларни Аллоҳга шерик қилган бўламиз-ку?) Биз учун у динга қайтиш жоиз эмас, магар Парвардигоримиз — Аллоҳ (бизни йўлдан оздиришни) хоҳласагина (қайтишимиз мумкин). Парвардигоримизнинг илми ҳамма нарсани қамраб олгандир. Аллоҳнинг Ўзига таваккул қилдик (суяндик). Парвардигоро, биз билан қавмимиз ўртасида ҳақ ҳукм қилгайсан. Ўзинг ҳукм қилгувчиларнинг яхшироғидирсан»'}, {'chapter': 7, 'verse': 90, 'text': 'وَقَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ لَئِنِ ٱتَّبَعۡتُمۡ شُعَيۡبًا إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ\n(Шунда унинг) қавмидан кофир бўлган кимсалар: «Қасамки, агар Шуайбга эргашсанглар, албатта зиён кўргувчи бўлурсизлар», дейишди'}, {'chapter': 7, 'verse': 91, 'text': 'فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ\nБас, уларни даҳшатли зилзила тутиб, турган жойларида тўкилдилар (ҳалок бўлдилар)'}, {'chapter': 7, 'verse': 92, 'text': 'ٱلَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَاۚ ٱلَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَانُواْ هُمُ ٱلۡخَٰسِرِينَ\nШуайбни ёлғончи қилган кимсалар гўё у ерда яшамагандек бўлиб қолдилар. Шуайбни ёлғончи қилган кимсаларнинг ўзлари зиён кўргувчи бўлдилар'}, {'chapter': 7, 'verse': 93, 'text': 'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَٰلَٰتِ رَبِّي وَنَصَحۡتُ لَكُمۡۖ فَكَيۡفَ ءَاسَىٰ عَلَىٰ قَوۡمٖ كَٰفِرِينَ\nКейин (Шуайб) улардан юз ўгириб, (ўзича) деди: «Эй қавмим, мана, мен Парвардигоримнинг динини сизларга етказдим ва насиҳатлар қилдим. Энди шунча ваъз-насиҳатлардан кейин ҳам кофир (бўлган) қавмга қандай қайғурурман?»'}, {'chapter': 7, 'verse': 94, 'text': 'وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّبِيٍّ إِلَّآ أَخَذۡنَآ أَهۡلَهَا بِٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ لَعَلَّهُمۡ يَضَّرَّعُونَ\nБиз бирон қишлоққа (жойга) пайғамбар юборсак (ва у ердаги одамлар биз юборган пайғамбарга иймон келтиришмаса) албатта унинг аҳлини тавба-тазарру қилишлари учун қашшоқлик ва мусибатлар билан ушлаганмиз'}, {'chapter': 7, 'verse': 95, 'text': 'ثُمَّ بَدَّلۡنَا مَكَانَ ٱلسَّيِّئَةِ ٱلۡحَسَنَةَ حَتَّىٰ عَفَواْ وَّقَالُواْ قَدۡ مَسَّ ءَابَآءَنَا ٱلضَّرَّآءُ وَٱلسَّرَّآءُ فَأَخَذۡنَٰهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nСўнгра ўша ёмонлик ўрнига яхшиликни (бой-бадавлатлик ва хотиржамликни) алмаштириб қўйдик. Ҳатто кўпайишиб кетганларида ва: «Бундай қийинчилик ва кенгчиликлар ота-боболаримизга ҳам етгай (яъни, бизнинг динсизлигимизнинг бу ишларга ҳеч қандай алоқаси йўқ, балки дунёнинг тартиби ўзи шунақа — бир танглик келса, бир мўл-кўлчилик бўлади)», дейишгач, уларни ўзлари сезмаган ҳолларида тўсатдан (бирон бало-ҳалокат билан) ушладик'}, {'chapter': 7, 'verse': 96, 'text': 'وَلَوۡ أَنَّ أَهۡلَ ٱلۡقُرَىٰٓ ءَامَنُواْ وَٱتَّقَوۡاْ لَفَتَحۡنَا عَلَيۡهِم بَرَكَٰتٖ مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ وَلَٰكِن كَذَّبُواْ فَأَخَذۡنَٰهُم بِمَا كَانُواْ يَكۡسِبُونَ\nАгар у қишлоқларнинг (жойнинг) аҳли иймон келтириб, тақводор бўлганларида эди, албатта Биз уларга осмону Ердан баракот (дарвозаларини) очиб қўйган бўлур эдик. Лекин улар (пайғамбарларимизни) ёлғончи қилдилар, бас, уларни ўзлари қилган гуноҳлари сабабли ушладик'}, {'chapter': 7, 'verse': 97, 'text': 'أَفَأَمِنَ أَهۡلُ ٱلۡقُرَىٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا بَيَٰتٗا وَهُمۡ نَآئِمُونَ\nУ қишлоқларнинг аҳли бало-қазойимиз тунда, ухлаётган ҳолларида келиб қолишидан хотиржам бўлиб қолдиларми? (Кутмаганмидилар)'}, {'chapter': 7, 'verse': 98, 'text': 'أَوَأَمِنَ أَهۡلُ ٱلۡقُرَىٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا ضُحٗى وَهُمۡ يَلۡعَبُونَ\nЁки у қишлоқларнинг аҳли бало-қазойимиз чошгоҳ пайтида, ўйин-кулги қилаётган ҳолларида келиб қолишидан хотиржаммилар'}, {'chapter': 7, 'verse': 99, 'text': 'أَفَأَمِنُواْ مَكۡرَ ٱللَّهِۚ فَلَا يَأۡمَنُ مَكۡرَ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡخَٰسِرُونَ\nУлар Аллоҳнинг «макри»дан хотиржам бўлиб қолдиларми? Бас, Аллоҳнинг «макри»дан фақат зиён кўргувчи қавмгина хотиржам бўлур'}, {'chapter': 7, 'verse': 100, 'text': 'أَوَلَمۡ يَهۡدِ لِلَّذِينَ يَرِثُونَ ٱلۡأَرۡضَ مِنۢ بَعۡدِ أَهۡلِهَآ أَن لَّوۡ نَشَآءُ أَصَبۡنَٰهُم بِذُنُوبِهِمۡۚ وَنَطۡبَعُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَسۡمَعُونَ\nБу ерга (илгариги) эгаларидан кейин меросхўр бўлаётган кимсаларни, агар хоҳласак, гуноҳлари сабабли мусибат етказиб қўйишимиз маълум эмасми? Биз уларнинг дилларини (ана шундай) муҳрлаб қўюрмиз. Сўнг улар (ҳеч қандай панд-насиҳатга) қулоқ солмай қўядилар'}, {'chapter': 7, 'verse': 101, 'text': 'تِلۡكَ ٱلۡقُرَىٰ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآئِهَاۚ وَلَقَدۡ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ مِن قَبۡلُۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلۡكَٰفِرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга ўша қишлоқлар — уларнинг хабарларидан сўйлаётирмиз. Албатта, уларга пайғамбарлари ҳужжат-мўъжизалар келтирганлар, аммо улар илгари (яъни, мўъжизалар келмай туриб, пайғамбарларини) ёлғончи қилганлари сабабли (мўъжизалар келгандан кейин ҳам) иймон келтирувчи бўлмадилар. Аллоҳ кофирларнинг кўнгилларини ана шундай муҳрлаб қўюр'}, {'chapter': 7, 'verse': 102, 'text': 'وَمَا وَجَدۡنَا لِأَكۡثَرِهِم مِّنۡ عَهۡدٖۖ وَإِن وَجَدۡنَآ أَكۡثَرَهُمۡ لَفَٰسِقِينَ\nУларнинг аксарида бирон аҳд (яъни, аҳдга вафодорликни) топмадик ва мудом уларнинг аксарини итоатсиз ҳолда топдик'}, {'chapter': 7, 'verse': 103, 'text': 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَظَلَمُواْ بِهَاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ\nСўнгра — улардан кейин Мусони Ўз оятларимиз билан Фиръавн ва унинг одамларига юборган эдик, улар (у оятларга) золим-кофир бўлдилар. Ана энди бузғунчиларнинг оқибатлари қандай бўлганини кўринг'}, {'chapter': 7, 'verse': 104, 'text': 'وَقَالَ مُوسَىٰ يَٰفِرۡعَوۡنُ إِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\n(Мусо) деди: «Эй Фиръавн, албатта мен барча оламларнинг Парвардигоридан элчиман'}, {'chapter': 7, 'verse': 105, 'text': 'حَقِيقٌ عَلَىٰٓ أَن لَّآ أَقُولَ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ قَدۡ جِئۡتُكُم بِبَيِّنَةٖ مِّن رَّبِّكُمۡ فَأَرۡسِلۡ مَعِيَ بَنِيٓ إِسۡرَـٰٓءِيلَ\nМенинг зиммамда Аллоҳ шаънига фақат ҳақни айтиш бордир. Мана, сизларга Парвардигорингиздан ҳужжат келтирдим. Бас, энди Бани Исроилни мен билан бирга (ўз юртларига) жўнатгин»'}, {'chapter': 7, 'verse': 106, 'text': 'قَالَ إِن كُنتَ جِئۡتَ بِـَٔايَةٖ فَأۡتِ بِهَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\n(Фиръавн): «Агар ростгўй кишилардан бўлиб, оят-мўъжиза билан келган бўлсанг, қани, уни келтир», деди'}, {'chapter': 7, 'verse': 107, 'text': 'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ\nШунда (Мусо тутиб турган) асосини ташлаган эди, баногоҳ у асо ростакам аждарга айланди'}, {'chapter': 7, 'verse': 108, 'text': 'وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّـٰظِرِينَ\nКейин қўлини (чўнтагидан) чиқарган эди, баногоҳ у қараб турганларга (кундан ҳам) оқ бўлиб кўринди (ҳолбуки, Мусо қорамағиз одам эди)'}, {'chapter': 7, 'verse': 109, 'text': 'قَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ\nФиръавн қавмидан (яъни, қибтийлардан) бўлган кимсалар: «Шубҳасиз, бу (Мусо ўткир сеҳргардир)», дедилар'}, {'chapter': 7, 'verse': 110, 'text': 'يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُمۡۖ فَمَاذَا تَأۡمُرُونَ\nФиръавн айтди: «У сизларни ўз ерларингиздан чиқармоқчи, нима дейсизлар?»'}, {'chapter': 7, 'verse': 111, 'text': 'قَالُوٓاْ أَرۡجِهۡ وَأَخَاهُ وَأَرۡسِلۡ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ\nУлар дедилар: «Уни ва акаси (Ҳорун)ни (кетказмай) ушлаб тургин-да, ҳамма шаҳарларга (сеҳргарларни) йиғиб келадиган кишиларни юборгин'}, {'chapter': 7, 'verse': 112, 'text': 'يَأۡتُوكَ بِكُلِّ سَٰحِرٍ عَلِيمٖ\nУулар сенга жами ўткир сеҳргарларни келтирсинлар»'}, {'chapter': 7, 'verse': 113, 'text': 'وَجَآءَ ٱلسَّحَرَةُ فِرۡعَوۡنَ قَالُوٓاْ إِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَٰلِبِينَ\n(Шундан кейин) сеҳргарлар Фиръавн олдига келишиб: «Агар биз ғолиб бўлсак, албатта (катта) мукофот (берурсан)», дейишди'}, {'chapter': 7, 'verse': 114, 'text': 'قَالَ نَعَمۡ وَإِنَّكُمۡ لَمِنَ ٱلۡمُقَرَّبِينَ\nУ: «Ҳа, (агар ғолиб бўлсангизлар), албатта сизлар менинг яқинларимдан бўлурсизлар», деди'}, {'chapter': 7, 'verse': 115, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ نَحۡنُ ٱلۡمُلۡقِينَ\nУлар айтдилар: «Эй Мусо, ё сен (қўлингдаги асони) ташла, ёки биз (ўзимизнинг қўлимиздаги нарсаларимизни) ташлаймиз»'}, {'chapter': 7, 'verse': 116, 'text': 'قَالَ أَلۡقُواْۖ فَلَمَّآ أَلۡقَوۡاْ سَحَرُوٓاْ أَعۡيُنَ ٱلنَّاسِ وَٱسۡتَرۡهَبُوهُمۡ وَجَآءُو بِسِحۡرٍ عَظِيمٖ\nУ (Мусо): «Сизлар ташланглар», деди. Бас, улар ташлаганларида, одамларни кўзларини бўяб даҳшатга солиб қўйдилар ва зўр сеҳр кўрсатдилар'}, {'chapter': 7, 'verse': 117, 'text': '۞وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَلۡقِ عَصَاكَۖ فَإِذَا هِيَ تَلۡقَفُ مَا يَأۡفِكُونَ\nБиз Мусога: «Асойингни ташлагин», деб ваҳий юбордик. Баногоҳ у (аждарга айланган ҳасса) уларнинг «уйдирма»ларини юта бошлади'}, {'chapter': 7, 'verse': 118, 'text': 'فَوَقَعَ ٱلۡحَقُّ وَبَطَلَ مَا كَانُواْ يَعۡمَلُونَ\nБас, ҳақиқат қарор топди, уларнинг қилган ҳаракатлари эса ботил бўлди'}, {'chapter': 7, 'verse': 119, 'text': 'فَغُلِبُواْ هُنَالِكَ وَٱنقَلَبُواْ صَٰغِرِينَ\nБас, у ерда (Фиръавн ва унинг тарафдорлари) мағлуб бўлишиб, беобрў бўлган ҳолларида (шаҳарга) қайтдилар'}, {'chapter': 7, 'verse': 120, 'text': 'وَأُلۡقِيَ ٱلسَّحَرَةُ سَٰجِدِينَ\n(Шу пайт) у сеҳргарлар сажда қилган ҳолларида ерга ташланиб'}, {'chapter': 7, 'verse': 121, 'text': 'قَالُوٓاْ ءَامَنَّا بِرَبِّ ٱلۡعَٰلَمِينَ\n«Барча оламларнинг Парвардигорига —'}, {'chapter': 7, 'verse': 122, 'text': 'رَبِّ مُوسَىٰ وَهَٰرُونَ\nМусо ва Ҳоруннинг Парвардигорига иймон келтирдик», дедилар'}, {'chapter': 7, 'verse': 123, 'text': 'قَالَ فِرۡعَوۡنُ ءَامَنتُم بِهِۦ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّ هَٰذَا لَمَكۡرٞ مَّكَرۡتُمُوهُ فِي ٱلۡمَدِينَةِ لِتُخۡرِجُواْ مِنۡهَآ أَهۡلَهَاۖ فَسَوۡفَ تَعۡلَمُونَ\nФиръавн айтди: «Мен изн бермай туриб унга иймон келтирдингизми?! Шубҳасиз, бу (қилмишингиз) ушбу шаҳардан унинг аҳолисини чиқариш учун қилган макрингиздир. Энди яқинда билурсиз'}, {'chapter': 7, 'verse': 124, 'text': 'لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ ثُمَّ لَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ\nАлбатта оёқ-қўлларингизни қарама-қаршисига (ўнг қўл, чап оёғингизни) кесурман, сўнгра сизларнинг барчангизни дорга осурман»'}, {'chapter': 7, 'verse': 125, 'text': 'قَالُوٓاْ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ\nУлар дедилар: «Албатта, бизлар Парвардигоримизга қайтгувчимиз (бас, сен бизни ўлим билан қўрқита олмайсан)'}, {'chapter': 7, 'verse': 126, 'text': 'وَمَا تَنقِمُ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِـَٔايَٰتِ رَبِّنَا لَمَّا جَآءَتۡنَاۚ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَتَوَفَّنَا مُسۡلِمِينَ\nСен биздан фақатгина Парвардигоримизнинг оятлари келганда уларга иймон келтирганимиз учунгина ўч олмоқдасан. Парвардигоро, устимиздан сабру тоқатни ёғдиргайсан ва бизларни фақат мусулмон бўлган ҳолимизда ўлдиргайсан!»'}, {'chapter': 7, 'verse': 127, 'text': 'وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ أَتَذَرُ مُوسَىٰ وَقَوۡمَهُۥ لِيُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَيَذَرَكَ وَءَالِهَتَكَۚ قَالَ سَنُقَتِّلُ أَبۡنَآءَهُمۡ وَنَسۡتَحۡيِۦ نِسَآءَهُمۡ وَإِنَّا فَوۡقَهُمۡ قَٰهِرُونَ\n(Шунда) Фиръавн қавмидан бўлган одамлар: «(Эй Фиръавн), Мусо ва қавмининг Ерда бузғунчилик қилиб юришига ҳамда сени ва худоларингни тарк этишига қўйиб берасанми?» дейишганида, у деди: «Уларнинг ўғилларини ўлдириб, аёлларини тирик қолдиражакмиз. Албатта, биз уларнинг устида ғолибдурмиз»'}, {'chapter': 7, 'verse': 128, 'text': 'قَالَ مُوسَىٰ لِقَوۡمِهِ ٱسۡتَعِينُواْ بِٱللَّهِ وَٱصۡبِرُوٓاْۖ إِنَّ ٱلۡأَرۡضَ لِلَّهِ يُورِثُهَا مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ\n(Фиръавннинг сўзларидан даҳшатга тушиб кетганларида) Мусо қавмига деди: «Аллоҳдан мадад тилаб, сабр-тоқат қилингиз! Бу Ер шак-шубҳасиз, Аллоҳникидир. Уни Ўзи хоҳлаган бандаларига мерос қилиб берур. Оқибат-натижа эса тақводорларники (Аллоҳдан қўрққанларники) бўлур»'}, {'chapter': 7, 'verse': 129, 'text': 'قَالُوٓاْ أُوذِينَا مِن قَبۡلِ أَن تَأۡتِيَنَا وَمِنۢ بَعۡدِ مَا جِئۡتَنَاۚ قَالَ عَسَىٰ رَبُّكُمۡ أَن يُهۡلِكَ عَدُوَّكُمۡ وَيَسۡتَخۡلِفَكُمۡ فِي ٱلۡأَرۡضِ فَيَنظُرَ كَيۡفَ تَعۡمَلُونَ\nУлар айтдилар: «Сен бизга (пайғамбар бўлиб) келишингдан илгари ҳам (яъни, ўшанда ҳам Фиръавн ўғилларимизни ўлдириб, аёлларимизни тирик қолдириб, зулм қилган эди), сен келганингдан кейин ҳам озор кўрдик». У (Мусо) деди: «Шояд Парвардигорингиз душманларингизни ҳалок этиб, сизларни бу Ерга халифа қилса ва қандай амаллар қилишингизни кўрса»'}, {'chapter': 7, 'verse': 130, 'text': 'وَلَقَدۡ أَخَذۡنَآ ءَالَ فِرۡعَوۡنَ بِٱلسِّنِينَ وَنَقۡصٖ مِّنَ ٱلثَّمَرَٰتِ لَعَلَّهُمۡ يَذَّكَّرُونَ\nҚасамки, Биз Фиръавн одамларини панд-насиҳат олишлари учун (қаҳатчилик) йиллари билан ва мева-чеваларнинг ҳосилини камайтириш билан ушладик'}, {'chapter': 7, 'verse': 131, 'text': 'فَإِذَا جَآءَتۡهُمُ ٱلۡحَسَنَةُ قَالُواْ لَنَا هَٰذِهِۦۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَطَّيَّرُواْ بِمُوسَىٰ وَمَن مَّعَهُۥٓۗ أَلَآ إِنَّمَا طَـٰٓئِرُهُمۡ عِندَ ٱللَّهِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nШундан кейин ҳам қачон уларга яхшилигимиз (осойишта ҳаёт, унумдорлик) келса: «Бунга ўзимиз ҳақдормиз», дейишди. Агар уларга бирон ёмонлик етиб қолса, Мусо ва у билан бирга (унга иймон келтирган) кишилардан бадгумон бўлдилар. Огоҳ бўлсинларки, албатта уларнинг кўргуликлари (яъни, улар кўрадиган яхшиликлар ҳам, ёмонликлар ҳам) фақат Аллоҳнинг ҳузуридадир. Лекин уларнинг кўплари (буни) билмайдилар'}, {'chapter': 7, 'verse': 132, 'text': 'وَقَالُواْ مَهۡمَا تَأۡتِنَا بِهِۦ مِنۡ ءَايَةٖ لِّتَسۡحَرَنَا بِهَا فَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ\nУлар (Мусога): «Бизларни сеҳрлаш учун қандай оят-мўъжиза келтирсанг ҳам биз сенга ҳаргиз иймон келтиргувчи эмасмиз», дедилар'}, {'chapter': 7, 'verse': 133, 'text': 'فَأَرۡسَلۡنَا عَلَيۡهِمُ ٱلطُّوفَانَ وَٱلۡجَرَادَ وَٱلۡقُمَّلَ وَٱلضَّفَادِعَ وَٱلدَّمَ ءَايَٰتٖ مُّفَصَّلَٰتٖ فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ\nБас, Биз уларнинг устларига тўфон (сел), чигиртка, бит, бақа ва қон (балоларини) очиқ оят-мўъжизалар қилиб юбордик. (Лекин) улар кибр-ҳаво қилдилар ва жиноятчи — осий қавм бўлдилар'}, {'chapter': 7, 'verse': 134, 'text': 'وَلَمَّا وَقَعَ عَلَيۡهِمُ ٱلرِّجۡزُ قَالُواْ يَٰمُوسَى ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَۖ لَئِن كَشَفۡتَ عَنَّا ٱلرِّجۡزَ لَنُؤۡمِنَنَّ لَكَ وَلَنُرۡسِلَنَّ مَعَكَ بَنِيٓ إِسۡرَـٰٓءِيلَ\nҚачонки уларнинг устига бу азоб тушгач: «Эй Мусо, Парвардигорингга сенга берган ваъдаси ҳаққи-ҳурмати, дуо қил! Қасамки, агар бизлардан шу азобни кўтарсанг, албатта сенга (пайғамбарлигингга) иймон келтирурмиз ва Бани Исроилни сен билан бирга (ўз ватанларига) жўнатурмиз», дедилар'}, {'chapter': 7, 'verse': 135, 'text': 'فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلرِّجۡزَ إِلَىٰٓ أَجَلٍ هُم بَٰلِغُوهُ إِذَا هُمۡ يَنكُثُونَ\nЭнди улардан бу азобимизни (улар) ўзлари етиб боргувчи бўлган муддатгача (яъни, Қиёмат Кунигача) кўтарганимизда эса, баногоҳ (ичган қасамларини) бузиб турибдилар'}, {'chapter': 7, 'verse': 136, 'text': 'فَٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ فِي ٱلۡيَمِّ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ\nБас, Биз улардан интиқом олдик — оятларимизни ёлғон деганлари ва улардан ғофил бўлганликлари сабабли денгизга ғарқ қилдик'}, {'chapter': 7, 'verse': 137, 'text': 'وَأَوۡرَثۡنَا ٱلۡقَوۡمَ ٱلَّذِينَ كَانُواْ يُسۡتَضۡعَفُونَ مَشَٰرِقَ ٱلۡأَرۡضِ وَمَغَٰرِبَهَا ٱلَّتِي بَٰرَكۡنَا فِيهَاۖ وَتَمَّتۡ كَلِمَتُ رَبِّكَ ٱلۡحُسۡنَىٰ عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ بِمَا صَبَرُواْۖ وَدَمَّرۡنَا مَا كَانَ يَصۡنَعُ فِرۡعَوۡنُ وَقَوۡمُهُۥ وَمَا كَانُواْ يَعۡرِشُونَ\n(Миср ва Шомдаги) Ўзимиз баракотли қилган ернинг машриқ ва мағрибларига бечора қавмни (яъни, Бани Исроилни) ворис қилиб қўйдик. Сабр-тоқат қилганлари сабабли Бани Исроилга Парвардигорингизнинг («Бу ердан бечора бўлган зотларга инъом қилишни ва уларни одамларга етакчи қилиб қўйишни истаймиз», деган) гўзал сўзлари тўла-тўкис бўлди. Фиръавн ва қавми қуриб олган иморатларни ва баланд қилиб кўтарган қасрларни эса вайрон қилдик'}, {'chapter': 7, 'verse': 138, 'text': 'وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡبَحۡرَ فَأَتَوۡاْ عَلَىٰ قَوۡمٖ يَعۡكُفُونَ عَلَىٰٓ أَصۡنَامٖ لَّهُمۡۚ قَالُواْ يَٰمُوسَى ٱجۡعَل لَّنَآ إِلَٰهٗا كَمَا لَهُمۡ ءَالِهَةٞۚ قَالَ إِنَّكُمۡ قَوۡمٞ تَجۡهَلُونَ\n(Кейин) Бани Исроилни денгиздан ўтказганимиздан сўнг, улар бутларига сиғиниб турган бир қавм олдидан ўтдилар ва: «Эй Мусо, бизларга ҳам уларнинг илоҳлари каби худо қилиб (ясаб) бер», дедилар. У айтди: «Албатта, сизлар билмайдиган қавмдирсизлар'}, {'chapter': 7, 'verse': 139, 'text': 'إِنَّ هَـٰٓؤُلَآءِ مُتَبَّرٞ مَّا هُمۡ فِيهِ وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ\nАхир уларнинг (ибодат қилиб) турган нарсалари йўқ бўлгувчи, қилиб турган амаллари эса ботил-ку?»'}, {'chapter': 7, 'verse': 140, 'text': 'قَالَ أَغَيۡرَ ٱللَّهِ أَبۡغِيكُمۡ إِلَٰهٗا وَهُوَ فَضَّلَكُمۡ عَلَى ٱلۡعَٰلَمِينَ\n(Яна) айтди: «Мен, сизларни бутун оламлардан афзал қилиб қўйган Аллоҳдан ўзгани (яъни, жонсиз бутни) сизларга худо қилиб бераманми?!»'}, {'chapter': 7, 'verse': 141, 'text': 'وَإِذۡ أَنجَيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ يُقَتِّلُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ\n(Эй Бани Исроил), сизларни ёмон азоб қийнаётган, ўғилларингизни ўлдириб, аёлларингизни тирик қолдираётган Фиръавн одамларидан қутқарганимизни эслангиз! Бу ишларда (яъни, бу азоб ва нажотда) Парвардигорингиз тарафидан улуғ имтиҳон бордир'}, {'chapter': 7, 'verse': 142, 'text': '۞وَوَٰعَدۡنَا مُوسَىٰ ثَلَٰثِينَ لَيۡلَةٗ وَأَتۡمَمۡنَٰهَا بِعَشۡرٖ فَتَمَّ مِيقَٰتُ رَبِّهِۦٓ أَرۡبَعِينَ لَيۡلَةٗۚ وَقَالَ مُوسَىٰ لِأَخِيهِ هَٰرُونَ ٱخۡلُفۡنِي فِي قَوۡمِي وَأَصۡلِحۡ وَلَا تَتَّبِعۡ سَبِيلَ ٱلۡمُفۡسِدِينَ\nМусо билан ўттиз кечага ваъдалашган эдик. Сўнгра уни яна ўн (кеча) билан тўлдирдик. Бас, Парвардигорининг (унинг учун белгилаган) вақти комил қирқ кеча бўлди. (Яъни, Аллоҳ Тур тоғида Мусога шунча муддат танҳо ибодат қилишни буюрди. Аллоҳ таолога муножот қилиш учун кетар экан), Мусо акаси Ҳорунга деди: «Қавмим устида менинг ўринбосарим бўлгин ва (агар улар ёмон амал қилсалар), тузатгин. Бузғунчи кимсаларнинг йўлига эргашмагин»'}, {'chapter': 7, 'verse': 143, 'text': 'وَلَمَّا جَآءَ مُوسَىٰ لِمِيقَٰتِنَا وَكَلَّمَهُۥ رَبُّهُۥ قَالَ رَبِّ أَرِنِيٓ أَنظُرۡ إِلَيۡكَۚ قَالَ لَن تَرَىٰنِي وَلَٰكِنِ ٱنظُرۡ إِلَى ٱلۡجَبَلِ فَإِنِ ٱسۡتَقَرَّ مَكَانَهُۥ فَسَوۡفَ تَرَىٰنِيۚ فَلَمَّا تَجَلَّىٰ رَبُّهُۥ لِلۡجَبَلِ جَعَلَهُۥ دَكّٗا وَخَرَّ مُوسَىٰ صَعِقٗاۚ فَلَمَّآ أَفَاقَ قَالَ سُبۡحَٰنَكَ تُبۡتُ إِلَيۡكَ وَأَنَا۠ أَوَّلُ ٱلۡمُؤۡمِنِينَ\nҚачонки Мусо (ваъдалашган) вақтимизда (Тур тоғига) келиб, Парвардигори унга (бевосита) сўзлагач, у: «Парвардигорим, менга (жамолингни) кўрсатгин. Сенга бир қарай», деди. (Аллоҳ) айтди: «Сен Мени (бу дунёда) ҳаргиз кўролмайсан. Аммо мана бу тоғга боқ. (Мен унга кўринурман.) Бас, агар у (Мен кўринганимда) ўрнашган жойида тура олса, сен ҳам Мени кўражаксан». Қачонки, Парвардигори у тоғга кўринган эди, уни майда-майда қилиб ташлади ва (бу ҳолни кўрган) Мусо ҳушсиз ҳолда йиқилди. Ўзига келганидан кейин эса деди: «Пок Парвардигор (ноўрин савол сўрашдан), Ўзингга тавба қилдим. Энди мен (Сенинг нақадар буюк зот эканингга) иймон келтирувчиларнинг аввали — пешқадамиман»'}, {'chapter': 7, 'verse': 144, 'text': 'قَالَ يَٰمُوسَىٰٓ إِنِّي ٱصۡطَفَيۡتُكَ عَلَى ٱلنَّاسِ بِرِسَٰلَٰتِي وَبِكَلَٰمِي فَخُذۡ مَآ ءَاتَيۡتُكَ وَكُن مِّنَ ٱلشَّـٰكِرِينَ\nАллоҳ айтди: «Эй Мусо, ҳақиқатан Мен сени одамлар устида пайғамбарим бўлишга ва (бевосита) каломимни эшитишга танлаб олдим. Бас, сенга ато этган нарсамни (яъни, пайғамбарликни) олгин ва шукр қилгувчилардан бўлгин!»'}, {'chapter': 7, 'verse': 145, 'text': 'وَكَتَبۡنَا لَهُۥ فِي ٱلۡأَلۡوَاحِ مِن كُلِّ شَيۡءٖ مَّوۡعِظَةٗ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ فَخُذۡهَا بِقُوَّةٖ وَأۡمُرۡ قَوۡمَكَ يَأۡخُذُواْ بِأَحۡسَنِهَاۚ سَأُوْرِيكُمۡ دَارَ ٱلۡفَٰسِقِينَ\nУнинг учун лавҳларга (яъни, Таврот варақларига) ҳамма нарсани — панд-насиҳат ва барча нарсаларнинг тафсилотини ёзиб қўйдик ва: «Уларни маҳкам ушлаган ҳамда қавмингни у панд-насиҳатларнинг энг гўзалларини олишларига буюргин» (дедик). Энди сизларга итоатсиз кимсаларнинг диёрини кўрсатурман'}, {'chapter': 7, 'verse': 146, 'text': 'سَأَصۡرِفُ عَنۡ ءَايَٰتِيَ ٱلَّذِينَ يَتَكَبَّرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَا وَإِن يَرَوۡاْ سَبِيلَ ٱلرُّشۡدِ لَا يَتَّخِذُوهُ سَبِيلٗا وَإِن يَرَوۡاْ سَبِيلَ ٱلۡغَيِّ يَتَّخِذُوهُ سَبِيلٗاۚ ذَٰلِكَ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ\nЭнди Ерда ноҳақ кибру ҳаво қилиб юрадиган, агар барча оят-мўъжизаларни кўрсалар ҳам, уларга иймон келтирмайдиган, Тўғри Йўлни кўрсалар уни (ўзлар учун) йўл қилиб олмайдиган, агар залолат — ноТўғри Йўлни кўрсалар, уни йўл қилиб оладиган кимсаларни Ўз оят-мўъжизаларимдан буриб юборурман (яъни, англаб етмайдиган қилиб қўюрман). Бунга сабаб улар Бизнинг оятларимизни ёлғон деганлари ва у оятлардан ғофил бўлиб олганларидир'}, {'chapter': 7, 'verse': 147, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآءِ ٱلۡأٓخِرَةِ حَبِطَتۡ أَعۡمَٰلُهُمۡۚ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ\nБизнинг оятларимизни ва Охиратдаги мулоқот (Қиёмат)ни ёлғон деган кимсаларнинг қилган амаллари беҳуда кетар. Улар фақат ўзларининг амаллари (яъни, куфру исёнлари) билан жазоланурлар'}, {'chapter': 7, 'verse': 148, 'text': 'وَٱتَّخَذَ قَوۡمُ مُوسَىٰ مِنۢ بَعۡدِهِۦ مِنۡ حُلِيِّهِمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٌۚ أَلَمۡ يَرَوۡاْ أَنَّهُۥ لَا يُكَلِّمُهُمۡ وَلَا يَهۡدِيهِمۡ سَبِيلًاۘ ٱتَّخَذُوهُ وَكَانُواْ ظَٰلِمِينَ\nМусонинг қавми ундан кейин (яъни, у Тур тоғига Аллоҳга муножот қилиш учун кетганидан кейин) ўзларининг безак буюмларидан бир бузоқни — маърайдиган бир бузоқ жасади(нинг шаклини ясаб) олдилар (ва унга ибодат қила бошладилар) — у ўзларига сўзламаслигини ва ҳидоят ҳам қила олмаслигини билмадиларми?! — улар ўшани (ибодат қилиш учун) ушлаб, (ўзларига) зулм қилгувчи бўлдилар'}, {'chapter': 7, 'verse': 149, 'text': 'وَلَمَّا سُقِطَ فِيٓ أَيۡدِيهِمۡ وَرَأَوۡاْ أَنَّهُمۡ قَدۡ ضَلُّواْ قَالُواْ لَئِن لَّمۡ يَرۡحَمۡنَا رَبُّنَا وَيَغۡفِرۡ لَنَا لَنَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ\nҚачонки қўлга тушишгач (яъни, бузоқ худо эмаслигини билишиб, ҳафсалалари совугач) ва ўзларининг йўлдан озганликларини билгач, айтдилар: «Қасамки, агар Парвардигоримиз бизга раҳм этмаса ва (бу гуноҳимизни) мағфират қилмаса, албатта зиён кўргувчилардан бўлиб қолурмиз»'}, {'chapter': 7, 'verse': 150, 'text': 'وَلَمَّا رَجَعَ مُوسَىٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗا قَالَ بِئۡسَمَا خَلَفۡتُمُونِي مِنۢ بَعۡدِيٓۖ أَعَجِلۡتُمۡ أَمۡرَ رَبِّكُمۡۖ وَأَلۡقَى ٱلۡأَلۡوَاحَ وَأَخَذَ بِرَأۡسِ أَخِيهِ يَجُرُّهُۥٓ إِلَيۡهِۚ قَالَ ٱبۡنَ أُمَّ إِنَّ ٱلۡقَوۡمَ ٱسۡتَضۡعَفُونِي وَكَادُواْ يَقۡتُلُونَنِي فَلَا تُشۡمِتۡ بِيَ ٱلۡأَعۡدَآءَ وَلَا تَجۡعَلۡنِي مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nҚачонки Мусо қавмига (уларнинг бузоққа сиғинганликларидан) ғазабланган ва ғамгин бўлган ҳолда қайтгач: «Менинг ортимдан нақадар ёмон (ишлар қилиб) қолувчи (халифа) бўлдингиз. Парвардигорингизнинг амр-фармонидан (у етиб келгунича сабр қилмай) шошиб кетдингизми?» деб (ғазабланганидан қўлидаги) лавҳларни ташлаб юборди ва акасининг бошидан (сочидан) ушлаб ўзига торта бошлади. (Ҳорун) деди: «Эй онамнинг боласи — биродарим», бу қавм мени хўрлаб, ўлдиришларига оз қолди, энди сен ҳам бу душманларни менинг устимдан кулдирмагин ва мени бу золим қавм билан бирга деб билмагин'}, {'chapter': 7, 'verse': 151, 'text': 'قَالَ رَبِّ ٱغۡفِرۡ لِي وَلِأَخِي وَأَدۡخِلۡنَا فِي رَحۡمَتِكَۖ وَأَنتَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\n(Мусо) деди: «Парвардигорим, мени ва биродаримни Ўзинг мағфират қилгайсан ва бизни Ўз раҳматингга дохил қилгайсан. Ўзинг раҳм қилгувчиларнинг раҳмлироғисан»'}, {'chapter': 7, 'verse': 152, 'text': 'إِنَّ ٱلَّذِينَ ٱتَّخَذُواْ ٱلۡعِجۡلَ سَيَنَالُهُمۡ غَضَبٞ مِّن رَّبِّهِمۡ وَذِلَّةٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُفۡتَرِينَ\nШубҳасиз, бузоқни («худо» қилиб) ушлаб олган кимсаларга Парвардигорлари тарафидан ғазаб ва ҳаёти дунёда хор-зорлик етар. (Аллоҳ шаънига) ёлғон тўқувчи кимсаларни мана шундай жазолаймиз'}, {'chapter': 7, 'verse': 153, 'text': 'وَٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ ثُمَّ تَابُواْ مِنۢ بَعۡدِهَا وَءَامَنُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ\nГуноҳ ишларни қилиб, кейин уларнинг ортидан тавба қилган ва иймон келтирган кишилар учун эса албатта Парвардигорингиз ўшандан кейин ҳам мағфират қилгувчи, меҳрибондир'}, {'chapter': 7, 'verse': 154, 'text': 'وَلَمَّا سَكَتَ عَن مُّوسَى ٱلۡغَضَبُ أَخَذَ ٱلۡأَلۡوَاحَۖ وَفِي نُسۡخَتِهَا هُدٗى وَرَحۡمَةٞ لِّلَّذِينَ هُمۡ لِرَبِّهِمۡ يَرۡهَبُونَ\nҚачонки Мусонинг ғазаби босилгач, ҳалиги лавҳларни (ердан) олди — унда Парвардигоридан қўрқадиган кишилар учун ҳидоят ва раҳмат бўлур, деб битилган эди'}, {'chapter': 7, 'verse': 155, 'text': 'وَٱخۡتَارَ مُوسَىٰ قَوۡمَهُۥ سَبۡعِينَ رَجُلٗا لِّمِيقَٰتِنَاۖ فَلَمَّآ أَخَذَتۡهُمُ ٱلرَّجۡفَةُ قَالَ رَبِّ لَوۡ شِئۡتَ أَهۡلَكۡتَهُم مِّن قَبۡلُ وَإِيَّـٰيَۖ أَتُهۡلِكُنَا بِمَا فَعَلَ ٱلسُّفَهَآءُ مِنَّآۖ إِنۡ هِيَ إِلَّا فِتۡنَتُكَ تُضِلُّ بِهَا مَن تَشَآءُ وَتَهۡدِي مَن تَشَآءُۖ أَنتَ وَلِيُّنَا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَاۖ وَأَنتَ خَيۡرُ ٱلۡغَٰفِرِينَ\nМусо Бизнинг белгилаган вақтимизга (ҳузуримизга) олиб келиш учун ўз қавмидан (бузоққа сиғинмаган) етмиш кишини танлаб олди. Шу пайт уларни даҳшатли зилзила тутган эди, айтди: «Парвардигорим, агар хоҳласанг уларни ҳам, мени ҳам илгари (яъни, улар бузоққа сиғинаётган пайтларида) ҳалок қилсанг бўлур эди. Орамиздаги ақлсиз кимсаларнинг қилмишлари сабабли (энди) бизларни ҳалок қиласанми? Ахир бу (яъни, уларнинг бузоққа сиғинишлари) фақатгина Ўзинг хоҳлаган кишингни у сабабли адаштирадиган, хоҳлаган кишини ҳидоят қиладиган бир имтиҳонинг эди-ку? Ўзинг Хожамизсан, бас, бизни мағфират қил, бизларга раҳм айла. Ўзинг мағфират қилгувчиларнинг энг яхшисидирсан'}, {'chapter': 7, 'verse': 156, 'text': '۞وَٱكۡتُبۡ لَنَا فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ إِنَّا هُدۡنَآ إِلَيۡكَۚ قَالَ عَذَابِيٓ أُصِيبُ بِهِۦ مَنۡ أَشَآءُۖ وَرَحۡمَتِي وَسِعَتۡ كُلَّ شَيۡءٖۚ فَسَأَكۡتُبُهَا لِلَّذِينَ يَتَّقُونَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلَّذِينَ هُم بِـَٔايَٰتِنَا يُؤۡمِنُونَ\nБизлар учун бу дунёда ҳам, Охиратда ҳам яхшилик ёзгин. Ўзингга тавба қилдик». (Аллоҳ) айтди: «Азобимни ўзим хоҳлаган кимсага етказурман. Раҳматим — меҳрибонлигим эса ҳамма нарсадан кенгдир. Мен уни (раҳматимни) тақво қиладиган, закотни берадиган зотларга ва Бизнинг оятларимизга иймон келтиргувчи бўлган кишиларга ёзурман'}, {'chapter': 7, 'verse': 157, 'text': 'ٱلَّذِينَ يَتَّبِعُونَ ٱلرَّسُولَ ٱلنَّبِيَّ ٱلۡأُمِّيَّ ٱلَّذِي يَجِدُونَهُۥ مَكۡتُوبًا عِندَهُمۡ فِي ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِيلِ يَأۡمُرُهُم بِٱلۡمَعۡرُوفِ وَيَنۡهَىٰهُمۡ عَنِ ٱلۡمُنكَرِ وَيُحِلُّ لَهُمُ ٱلطَّيِّبَٰتِ وَيُحَرِّمُ عَلَيۡهِمُ ٱلۡخَبَـٰٓئِثَ وَيَضَعُ عَنۡهُمۡ إِصۡرَهُمۡ وَٱلۡأَغۡلَٰلَ ٱلَّتِي كَانَتۡ عَلَيۡهِمۡۚ فَٱلَّذِينَ ءَامَنُواْ بِهِۦ وَعَزَّرُوهُ وَنَصَرُوهُ وَٱتَّبَعُواْ ٱلنُّورَ ٱلَّذِيٓ أُنزِلَ مَعَهُۥٓ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nУлар шундай кишилардирки, уммий (саводсиз) пайғамбарга — номини ўз олдиларидаги Таврот ва Инжилда ёзилган ҳолда топишадиган — элчимизга (Муҳаммадга) эргашадилар. У пайғамбар уларни яхшиликка буюради, ёмонликдан қайтаради ва пок нарсаларни улар учун ҳалол қилиб, нопок нарсаларни уларга ҳаром қилади ҳамда улардан юкларини ва устларидаги кишанларини олиб ташлайди (яъни, Ислом динидан аввалги динларда бўлган оғир, машаққатли ибодатларни олиб ташлаб, уларнинг ўрнига осон ва енгилларини келтиради). Бас, унга иймон келтирган, уни улуғлаб, унга ёрдам қилган ҳамда унинг (келиши) билан нозил қилинган нурга (яъни, Қуръонга) эргашган зотлар — ана ўшаларгина нажот топгувчилардир'}, {'chapter': 7, 'verse': 158, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنِّي رَسُولُ ٱللَّهِ إِلَيۡكُمۡ جَمِيعًا ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرَسُولِهِ ٱلنَّبِيِّ ٱلۡأُمِّيِّ ٱلَّذِي يُؤۡمِنُ بِٱللَّهِ وَكَلِمَٰتِهِۦ وَٱتَّبِعُوهُ لَعَلَّكُمۡ تَهۡتَدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Эй одамлар, албатта мен сизларнинг барчангизга Аллоҳ (юборган) элчиман. У шундай Зотки, самовот ва Ер Унинг мулкидир. Ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи бордир. Ҳаёт ва ўлим берадиган ҳам Унинг Ўзидир. Бас, Аллоҳга ва Унинг элчисига — Аллоҳ ва Унинг сўзларига ишонадиган уммий пайғамбарга иймон келтирингиз ва унга эргашингиз — шояд ҳидоят топурсиз»'}, {'chapter': 7, 'verse': 159, 'text': 'وَمِن قَوۡمِ مُوسَىٰٓ أُمَّةٞ يَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ يَعۡدِلُونَ\nМусо қавмидан шундай жамоат ҳам борки, ҳақ (сўз) билан (одамларни) Тўғри Йўлга бошларлар ва ҳақ (ҳукмлар) билан адолат килурлар'}, {'chapter': 7, 'verse': 160, 'text': 'وَقَطَّعۡنَٰهُمُ ٱثۡنَتَيۡ عَشۡرَةَ أَسۡبَاطًا أُمَمٗاۚ وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ إِذِ ٱسۡتَسۡقَىٰهُ قَوۡمُهُۥٓ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنۢبَجَسَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۚ وَظَلَّلۡنَا عَلَيۡهِمُ ٱلۡغَمَٰمَ وَأَنزَلۡنَا عَلَيۡهِمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nУларни (яъни, Мусо қавмини) жамоа-жамоа қилиб ўн икки уруғ — авлодга бўлиб юбордик ва Мусога қавми ундан сув сўраган пайтида: «Асоинг билан тошни ургин!», деб ваҳий юбордик. Бас, ундан (тошдан) ўн икки чашма отилиб чиқди — ҳамма одамлар ўз сувларини билдилар. Уларга (саҳрода) булутни соябон қилдик ва устларидан ширинлик ва беданалар ёғдириб: «Сизларни баҳраманд қилган пок нарсаларимиздан енглар», дедик. Улар (берган неъматларимизга ношукр бўлишлари билан) Бизга жабр қилмадилар, балки ўзларига жабр қилгувчи бўлдилар'}, {'chapter': 7, 'verse': 161, 'text': 'وَإِذۡ قِيلَ لَهُمُ ٱسۡكُنُواْ هَٰذِهِ ٱلۡقَرۡيَةَ وَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ وَقُولُواْ حِطَّةٞ وَٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا نَّغۡفِرۡ لَكُمۡ خَطِيٓـَٰٔتِكُمۡۚ سَنَزِيدُ ٱلۡمُحۡسِنِينَ\nУларга: «Мана шу шаҳарни (Байтул-Муқаддасни) маскан тутингиз ва ундан хоҳлаган жойингизда таомланингиз ҳамда «кечиргин» деб шаҳарга дарвозадан сажда қилган ҳолингизда кирингиз! (Шунда) гуноҳларингизни мағфират қилурмиз, чиройли амал қилгувчиларга эса (марҳаматимизни) зиёда қилурмиз», деган пайтимизни эсланг'}, {'chapter': 7, 'verse': 162, 'text': 'فَبَدَّلَ ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡ قَوۡلًا غَيۡرَ ٱلَّذِي قِيلَ لَهُمۡ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَظۡلِمُونَ\nБас, улардан золим бўлган кимсалар ўзларига айтилган («кечиргин») сўзини бутунлай бошқа бир сўзга алмаштирдилар. (Шундан кейин) уларга золим бўлганлари сабабли осмондан азоб юбордик'}, {'chapter': 7, 'verse': 163, 'text': 'وَسۡـَٔلۡهُمۡ عَنِ ٱلۡقَرۡيَةِ ٱلَّتِي كَانَتۡ حَاضِرَةَ ٱلۡبَحۡرِ إِذۡ يَعۡدُونَ فِي ٱلسَّبۡتِ إِذۡ تَأۡتِيهِمۡ حِيتَانُهُمۡ يَوۡمَ سَبۡتِهِمۡ شُرَّعٗا وَيَوۡمَ لَا يَسۡبِتُونَ لَا تَأۡتِيهِمۡۚ كَذَٰلِكَ نَبۡلُوهُم بِمَا كَانُواْ يَفۡسُقُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), улардан (яҳудийлардан) денгиз олдидаги қишлоқ ҳақида — шанба куни ҳаддан ошганлари ҳақида (яъни, манъ қилинганига қарамасдан шанба куни балиқ овлаганлари ҳақида) сўранг. Ўшанда, шанбалик қилган (яъни, балиқ овламаган) кунларида балиқлар (сув юзида) очиқ келар эди, шанбалик қилмаган кунларида (яъни, шанбадан бошқа кунларда) эса келмас эди. Уларни итоатсиз бўлганлари сабабли мана шундай имтиҳон қилурмиз'}, {'chapter': 7, 'verse': 164, 'text': 'وَإِذۡ قَالَتۡ أُمَّةٞ مِّنۡهُمۡ لِمَ تَعِظُونَ قَوۡمًا ٱللَّهُ مُهۡلِكُهُمۡ أَوۡ مُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗاۖ قَالُواْ مَعۡذِرَةً إِلَىٰ رَبِّكُمۡ وَلَعَلَّهُمۡ يَتَّقُونَ\nЎшанда улардан бир жамоат: «Не сабабдан Аллоҳнинг Ўзи ҳалок этадиган ёки қаттиқ азоблайдиган қавмга панд-насиҳат қилурсизлар?» деганларида, улар (яъни, иккинчи тоифадаги одамлар) айтдилар: «Парвардигорингизга (Қиёмат Кунида) узримизни айтиш учун ва шояд (улар) Аллоҳдан қўрқсалар, деб (насиҳат қилмоқдамиз)»'}, {'chapter': 7, 'verse': 165, 'text': 'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦٓ أَنجَيۡنَا ٱلَّذِينَ يَنۡهَوۡنَ عَنِ ٱلسُّوٓءِ وَأَخَذۡنَا ٱلَّذِينَ ظَلَمُواْ بِعَذَابِۭ بَـِٔيسِۭ بِمَا كَانُواْ يَفۡسُقُونَ\nБас, қачон улар (яъни, итоатсиз кишилар) ўзларига эслатма қилиб берилган нарсани унутишгач, бу гуноҳ ишдан қайтарган зотларга нажот бердик ва золим кимсаларни итоатсиз бўлганлари сабабли қаттиқ азоб билан ушладик'}, {'chapter': 7, 'verse': 166, 'text': 'فَلَمَّا عَتَوۡاْ عَن مَّا نُهُواْ عَنۡهُ قُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ\nЭнди қачонки ўзлари қайтарилган ишдан (қайтмасдан) кибру ҳаво қилганларидан кейин уларга: «Бадбахт маймунларга айланингиз», дедик'}, {'chapter': 7, 'verse': 167, 'text': 'وَإِذۡ تَأَذَّنَ رَبُّكَ لَيَبۡعَثَنَّ عَلَيۡهِمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَن يَسُومُهُمۡ سُوٓءَ ٱلۡعَذَابِۗ إِنَّ رَبَّكَ لَسَرِيعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمٞ\nЎшанда Парвардигорингиз албатта уларнинг устига Қиёмат Кунигача ёмон азоб билан азоблайдиган зотларни юборишини билдирган эди. Шубҳасиз, Парвардигорингиз (кофирлар учун) азоби қаттиқ, (мўминларни эса) мағфират қилгувчи, меҳрибондир'}, {'chapter': 7, 'verse': 168, 'text': 'وَقَطَّعۡنَٰهُمۡ فِي ٱلۡأَرۡضِ أُمَمٗاۖ مِّنۡهُمُ ٱلصَّـٰلِحُونَ وَمِنۡهُمۡ دُونَ ذَٰلِكَۖ وَبَلَوۡنَٰهُم بِٱلۡحَسَنَٰتِ وَٱلسَّيِّـَٔاتِ لَعَلَّهُمۡ يَرۡجِعُونَ\nУларни (яҳудийларни) бутун Ер юзига жамоа-жамоа қилиб бўлиб юбордик. Уларнинг ораларида яхшилари (иймонга келганлари) ҳам, ундай эмаслари (иймонсизлари) ҳам бордир. Уларни (куфр ва исёндан) қайтишлари учун кўп яхшилик (неъматлар) ва ёмонликлар (балолар) билан синаб кўрдик'}, {'chapter': 7, 'verse': 169, 'text': 'فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٞ وَرِثُواْ ٱلۡكِتَٰبَ يَأۡخُذُونَ عَرَضَ هَٰذَا ٱلۡأَدۡنَىٰ وَيَقُولُونَ سَيُغۡفَرُ لَنَا وَإِن يَأۡتِهِمۡ عَرَضٞ مِّثۡلُهُۥ يَأۡخُذُوهُۚ أَلَمۡ يُؤۡخَذۡ عَلَيۡهِم مِّيثَٰقُ ٱلۡكِتَٰبِ أَن لَّا يَقُولُواْ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّ وَدَرَسُواْ مَا فِيهِۗ وَٱلدَّارُ ٱلۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ\nУлардан сўнг Китобга (Тавротга) меросхўр бўлган бир авлод келдики, улар бу тубан (дунё) нарсаларини (ҳалол, ҳаромлигига қарамай) оладилар ва «Албатта бизлар мағфират қилинурмиз», дейдилар. Ҳолбуки, агар яна ўшанга ўхшаган нарса келиб қолса, уни ҳам олаверадилар. Ахир улардан Китобда (Тавротда) Аллоҳ шаънига фақат ҳақни айтишлари борасида аҳд-паймон олинмаганмиди? Ундаги оятларни ўқиб ўрганган эдилар-ку! Аллоҳдан қўрқадиган зотлар учун (бу дунё матоларидан) Охират диёри яхшироқ-ку! Ақл юргизмайсизларми'}, {'chapter': 7, 'verse': 170, 'text': 'وَٱلَّذِينَ يُمَسِّكُونَ بِٱلۡكِتَٰبِ وَأَقَامُواْ ٱلصَّلَوٰةَ إِنَّا لَا نُضِيعُ أَجۡرَ ٱلۡمُصۡلِحِينَ\n(Уларнинг ораларида) Китобни маҳкам ушлаган ва намозни тўкис адо қилган зотлар ҳам борки, албатта Биз ўзларини тузатгувчи кишиларнинг ажр-мукофотларини зое қилмагаймиз'}, {'chapter': 7, 'verse': 171, 'text': '۞وَإِذۡ نَتَقۡنَا ٱلۡجَبَلَ فَوۡقَهُمۡ كَأَنَّهُۥ ظُلَّةٞ وَظَنُّوٓاْ أَنَّهُۥ وَاقِعُۢ بِهِمۡ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Тур тоғини кўчириб олиб, уларнинг устларида гўё бир соябондек кўтариб туриб, улар уни устларига қулаб тушади, деб гумон қилган ҳолларида: «Биз сизларга ато этган нарсани (Тавротни) маҳкам ушлангиз ва тақводор бўлишингиз учун ундаги оятларни ёдда тутингиз!», деган пайтимизни эсланг'}, {'chapter': 7, 'verse': 172, 'text': 'وَإِذۡ أَخَذَ رَبُّكَ مِنۢ بَنِيٓ ءَادَمَ مِن ظُهُورِهِمۡ ذُرِّيَّتَهُمۡ وَأَشۡهَدَهُمۡ عَلَىٰٓ أَنفُسِهِمۡ أَلَسۡتُ بِرَبِّكُمۡۖ قَالُواْ بَلَىٰ شَهِدۡنَآۚ أَن تَقُولُواْ يَوۡمَ ٱلۡقِيَٰمَةِ إِنَّا كُنَّا عَنۡ هَٰذَا غَٰفِلِينَ\nПарвардигорингиз Одам болаларининг белларидан (яъни, пушти камарларидан то Қиёмат Кунигача дунёга келадиган барча) зурриётларини олиб: «Мен Парвардигорингиз эмасманми?», деб, ўзларига қарши гувоҳ қилганида, улар: «Ҳақиқатан Сен Парвардигоримизсан, бизлар бунга шоҳидмиз», деганларини эсланг! (Сизлардан бундай гувоҳлик — аҳд-паймон олишимиз) Қиёмат Кунида: «Бизлар бундан бехабар эдик», демасликларингиз учундир'}, {'chapter': 7, 'verse': 173, 'text': 'أَوۡ تَقُولُوٓاْ إِنَّمَآ أَشۡرَكَ ءَابَآؤُنَا مِن قَبۡلُ وَكُنَّا ذُرِّيَّةٗ مِّنۢ بَعۡدِهِمۡۖ أَفَتُهۡلِكُنَا بِمَا فَعَلَ ٱلۡمُبۡطِلُونَ\nЁки: «Ахир илгари ота-боболаримиз мушрик бўлган эсалар, бизлар улардан кейин келган (ва уларнинг ўргатган йўлларига эргашган) авлод бўлсак, ўша ноҳақ йўлда ўтган кимсаларнинг қилмишлари сабабли бизларни ҳалок қилурмисан», демасликларингиз учундир'}, {'chapter': 7, 'verse': 174, 'text': 'وَكَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ وَلَعَلَّهُمۡ يَرۡجِعُونَ\nШояд (куфрларидан) қайтсалар, деб оятларни мана шундай муфассал қилурмиз'}, {'chapter': 7, 'verse': 175, 'text': 'وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ ٱلَّذِيٓ ءَاتَيۡنَٰهُ ءَايَٰتِنَا فَٱنسَلَخَ مِنۡهَا فَأَتۡبَعَهُ ٱلشَّيۡطَٰنُ فَكَانَ مِنَ ٱلۡغَاوِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларга (яҳудийларга) бир кимсанинг хабарини тиловат қилинг — у қимсага оятларимизни билдирган эдик. Бас, у ўша оятларимиздан четлангач (яъни, уларга амал қилмагач), уни шайтон эргаштириб кетиб, йўлдан озгувчилардан бўлиб қолди'}, {'chapter': 7, 'verse': 176, 'text': 'وَلَوۡ شِئۡنَا لَرَفَعۡنَٰهُ بِهَا وَلَٰكِنَّهُۥٓ أَخۡلَدَ إِلَى ٱلۡأَرۡضِ وَٱتَّبَعَ هَوَىٰهُۚ فَمَثَلُهُۥ كَمَثَلِ ٱلۡكَلۡبِ إِن تَحۡمِلۡ عَلَيۡهِ يَلۡهَثۡ أَوۡ تَتۡرُكۡهُ يَلۡهَثۚ ذَّـٰلِكَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۚ فَٱقۡصُصِ ٱلۡقَصَصَ لَعَلَّهُمۡ يَتَفَكَّرُونَ\nАгар хоҳласак уни ўша оятлар сабабли (юқори даражаларга) кўтарган бўлур эдик. Лекин у ерга (яъни, молу дунёга) берилди ва ҳавойи-нафсига эргашди. Бас, унинг мисоли худди бир итга ўхшайдики, уни ҳайдасанг ҳам тилини осилтириб тураверади ёки (ўз ҳолига) қўйсанг ҳам тилини осилтириб тураверади. Бу Бизнинг оятларимизни ёлғон деган кимсаларнинг мисолидир. Улар тафаккур қилсинлар учун бу қиссаларни сўйланг'}, {'chapter': 7, 'verse': 177, 'text': 'سَآءَ مَثَلًا ٱلۡقَوۡمُ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَأَنفُسَهُمۡ كَانُواْ يَظۡلِمُونَ\nБизнинг оятларимизни ёлғон деган кимсаларнинг мисоли нақадар ёмон бўлди ва улар ўзларига зулм қилгувчи бўлдилар'}, {'chapter': 7, 'verse': 178, 'text': 'مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِيۖ وَمَن يُضۡلِلۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nАллоҳ кимни ҳидоят қилса, бас, ўша ҳидоят топгувчидир. У зот кимни йўлдан оздирса, бас, ана ўшалар зиён кўргувчидирлар'}, {'chapter': 7, 'verse': 179, 'text': 'وَلَقَدۡ ذَرَأۡنَا لِجَهَنَّمَ كَثِيرٗا مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ لَهُمۡ قُلُوبٞ لَّا يَفۡقَهُونَ بِهَا وَلَهُمۡ أَعۡيُنٞ لَّا يُبۡصِرُونَ بِهَا وَلَهُمۡ ءَاذَانٞ لَّا يَسۡمَعُونَ بِهَآۚ أُوْلَـٰٓئِكَ كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡغَٰفِلُونَ\nБиз жин ва инсдан кўпларини жаҳаннам учун яратганимиз муҳаққақдир. Уларнинг диллари бору англай олмайдилар, кўзлари бору кўра олмайдилар, қулоқлари бору эшитмайдилар. Улар чорвалар кабидирлар, йўқ, улар (беақл, бефаҳмликда чорвалардан ҳам) адашганроқдирлар. Ана ўшалар ғафлатда қолган кимсалардир'}, {'chapter': 7, 'verse': 180, 'text': 'وَلِلَّهِ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰ فَٱدۡعُوهُ بِهَاۖ وَذَرُواْ ٱلَّذِينَ يُلۡحِدُونَ فِيٓ أَسۡمَـٰٓئِهِۦۚ سَيُجۡزَوۡنَ مَا كَانُواْ يَعۡمَلُونَ\nАллоҳнинг гўзал исмлари бордир. Бас, Уни ўша исмлар билан чорланглар (ёд этинглар). Унинг исмларида ҳақдан оғиб (ноўрин жойларда уларни қўллайдиган мушрик) кимсаларни тарк қилинглар. Улар яқинда қилиб ўтган амалларига яраша жазоланурлар'}, {'chapter': 7, 'verse': 181, 'text': 'وَمِمَّنۡ خَلَقۡنَآ أُمَّةٞ يَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ يَعۡدِلُونَ\nБиз яратган зотлар орасида (одамларни) ҳақ (дин) билан ҳидоят қиладиган ва ҳақ (ҳукм) билан адолат қиладиган бир жамоат ҳам бордир'}, {'chapter': 7, 'verse': 182, 'text': 'وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ\nБизнинг оятларимизни ёлғон деган кимсаларни эса ўзлари билмай қоладиган тарафдан секин-аста ҳалокатга олиб борурмиз'}, {'chapter': 7, 'verse': 183, 'text': 'وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ\n(Ҳозирча) уларга муҳлат бериб қўюрман. Албатта, Менинг «макрим» қаттиқдир'}, {'chapter': 7, 'verse': 184, 'text': 'أَوَلَمۡ يَتَفَكَّرُواْۗ مَا بِصَاحِبِهِم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ مُّبِينٌ\nУлар ҳамроҳларида (яъни, Муҳаммад алайҳис-салоту вас-саломда) ҳеч қандай жунун йўқлигини ўйламайдиларми?! У фақат (Аллоҳнинг азобидан) ошкора огоҳлантиргувчи Зотдир'}, {'chapter': 7, 'verse': 185, 'text': 'أَوَلَمۡ يَنظُرُواْ فِي مَلَكُوتِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا خَلَقَ ٱللَّهُ مِن شَيۡءٖ وَأَنۡ عَسَىٰٓ أَن يَكُونَ قَدِ ٱقۡتَرَبَ أَجَلُهُمۡۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَهُۥ يُؤۡمِنُونَ\nСамовот ва Ер мамлакатлари ҳақида, Аллоҳ яратган нарсалар тўғрисида ва ажаллари яқинлашиб қолган бўлиши мумкинлиги хусусида ўйламайдиларми?! Ундан (Қуръондан) сўнг (яъни, унга ишонмаганларидан кейин) яна қандай гапга ишонадилар-а'}, {'chapter': 7, 'verse': 186, 'text': 'مَن يُضۡلِلِ ٱللَّهُ فَلَا هَادِيَ لَهُۥۚ وَيَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nКимни Аллоҳ адаштириб қўйса, унинг учун бирон ҳидоят қилгувчи бўлмас. (Аллоҳ) ундайларни ўз туғёнларида адашиб-улоқиб юрган ҳолларида тарк қилур'}, {'chapter': 7, 'verse': 187, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرۡسَىٰهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ رَبِّيۖ لَا يُجَلِّيهَا لِوَقۡتِهَآ إِلَّا هُوَۚ ثَقُلَتۡ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ لَا تَأۡتِيكُمۡ إِلَّا بَغۡتَةٗۗ يَسۡـَٔلُونَكَ كَأَنَّكَ حَفِيٌّ عَنۡهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиздан Соат (яъни, Қиёмат) қачон воқе бўлади, деб сўрайдилар. Айтинг: «Унинг билими фақатгина Парвардигорим даргоҳидадир. Вақти-соати келганида ҳам фақат Ўзи ошкор қилур. У (яъни, Қиёмат) самовот ва Ер учун жуда оғир (даҳшатли) бир ишдир. У сизларга фақат тўсатдан — кутилмаганда келур». Гўё сиз у ҳақда яхши биладигандек сўрайдилар. Айтинг: «Унинг билими фақатгина Аллоҳ даргоҳидадир. Лекин жуда кўп одамлар уни билмайдилар»'}, {'chapter': 7, 'verse': 188, 'text': 'قُل لَّآ أَمۡلِكُ لِنَفۡسِي نَفۡعٗا وَلَا ضَرًّا إِلَّا مَا شَآءَ ٱللَّهُۚ وَلَوۡ كُنتُ أَعۡلَمُ ٱلۡغَيۡبَ لَٱسۡتَكۡثَرۡتُ مِنَ ٱلۡخَيۡرِ وَمَا مَسَّنِيَ ٱلسُّوٓءُۚ إِنۡ أَنَا۠ إِلَّا نَذِيرٞ وَبَشِيرٞ لِّقَوۡمٖ يُؤۡمِنُونَ\nАйтинг: «Мен ўзим учун ҳам на фойда ва на зиён етказишга эга бўла оламан. Магар Аллоҳнинг хоҳлагани бўлур. Агар ғайбни билганимда эди, яхши амалларни кўпайтирган бўлур эдим ва менга бирон зиён етмаган бўлур эди. Мен фақат иймон келтирадиган қавм учун (дўзах азобидан) огоҳлантиргувчи ва (жаннат неъматлари ҳақида) хушхабар бергувчиман, холос»'}, {'chapter': 7, 'verse': 189, 'text': '۞هُوَ ٱلَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَجَعَلَ مِنۡهَا زَوۡجَهَا لِيَسۡكُنَ إِلَيۡهَاۖ فَلَمَّا تَغَشَّىٰهَا حَمَلَتۡ حَمۡلًا خَفِيفٗا فَمَرَّتۡ بِهِۦۖ فَلَمَّآ أَثۡقَلَت دَّعَوَا ٱللَّهَ رَبَّهُمَا لَئِنۡ ءَاتَيۡتَنَا صَٰلِحٗا لَّنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ\nУ (Аллоҳ) шундай зотки, сизларни бир жондан (Одамдан) яратди ва у ором-осойиш топсин, деб унинг ўзидан жуфтини вужудга келтирди. Унга қўшилганидан кейин у (Ҳавво) енгил юк (ҳомила) билан юкли бўлиб, ўша (юк) билан юрди. Бас, қачонки, оғирлашганида, (улар) Парвардигорлари — Аллоҳга дуо қилдилар: «Қасамки, агар бизга солиҳ фарзанд ато қилсанг, албатта шукр қилгувчилардан бўлурмиз»'}, {'chapter': 7, 'verse': 190, 'text': 'فَلَمَّآ ءَاتَىٰهُمَا صَٰلِحٗا جَعَلَا لَهُۥ شُرَكَآءَ فِيمَآ ءَاتَىٰهُمَاۚ فَتَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ\nЭнди (Аллоҳ) уларга солиҳ (фарзанд) ато этганидан кейин Унга (Унинг) Ўзи ато этган нарсада (яъни, болага ном қўйишда) ширк келтира бошладилар. Бас, Аллоҳ уларнинг ширк келтирган нарсаларидан юксакдир'}, {'chapter': 7, 'verse': 191, 'text': 'أَيُشۡرِكُونَ مَا لَا يَخۡلُقُ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ\nҲеч нарсани ярата олмайдиган, балки ўзи яратилувчи бўлган нарсаларни (Аллоҳга) шерик қиладиларми'}, {'chapter': 7, 'verse': 192, 'text': 'وَلَا يَسۡتَطِيعُونَ لَهُمۡ نَصۡرٗا وَلَآ أَنفُسَهُمۡ يَنصُرُونَ\nУлар учун бирон ёрдам қилишга қодир бўлмайдиган, ҳатто ўзига ўзи ёрдам қила олмайдиган (нарсаларни Аллоҳга шерик қиладиларми)'}, {'chapter': 7, 'verse': 193, 'text': 'وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا يَتَّبِعُوكُمۡۚ سَوَآءٌ عَلَيۡكُمۡ أَدَعَوۡتُمُوهُمۡ أَمۡ أَنتُمۡ صَٰمِتُونَ\nАгар уларни (яъни, бутларни) ҳидоятга чақирсангиз, сизларга эргашмайдилар (яъни, улар жонсиздирлар). Сизлар учун уларни чақиришингиз ёки сукут қилиб туришингиз баробардир (яъни, барибир улар жавоб бера олмайдилар)'}, {'chapter': 7, 'verse': 194, 'text': 'إِنَّ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ عِبَادٌ أَمۡثَالُكُمۡۖ فَٱدۡعُوهُمۡ فَلۡيَسۡتَجِيبُواْ لَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nШубҳасиз, Аллоҳдан ўзга сизлар илтижо қилаётган нарсалар худди (сизнинг) ўзларингиз каби бандадирлар. Бас, агар ростгўй бўлсангизлар, сизлар уларни чақиринглар, улар сизларга жавоб қилсинлар-чи'}, {'chapter': 7, 'verse': 195, 'text': 'أَلَهُمۡ أَرۡجُلٞ يَمۡشُونَ بِهَآۖ أَمۡ لَهُمۡ أَيۡدٖ يَبۡطِشُونَ بِهَآۖ أَمۡ لَهُمۡ أَعۡيُنٞ يُبۡصِرُونَ بِهَآۖ أَمۡ لَهُمۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۗ قُلِ ٱدۡعُواْ شُرَكَآءَكُمۡ ثُمَّ كِيدُونِ فَلَا تُنظِرُونِ\nУларнинг оёқлари бормики, юрсалар, ё қўллари бормики тутсалар, ёки кўзлари бормики кўрсалар, ёхуд қулоқлари бормики тингласалар?! (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Уша бутларингизни чақиринглар-да, сўнгра менга (бир лаҳза) муҳлат бермай бор ҳийла-найрангингизни қилаверинглар». (Қандай ҳунар кўрсатманг, натижаси ҳечдир)'}, {'chapter': 7, 'verse': 196, 'text': 'إِنَّ وَلِـِّۧيَ ٱللَّهُ ٱلَّذِي نَزَّلَ ٱلۡكِتَٰبَۖ وَهُوَ يَتَوَلَّى ٱلصَّـٰلِحِينَ\nЗотан, менинг (ёрдам берувчи ва сақловчи) Эгам шу Китобни нозил қилган Аллоҳдир. У солиҳ бандаларга ёр бўлур'}, {'chapter': 7, 'verse': 197, 'text': 'وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَطِيعُونَ نَصۡرَكُمۡ وَلَآ أَنفُسَهُمۡ يَنصُرُونَ\nУндан ўзга сизлар илтижо қилаётган нарсалар сизларга ёрдам қилишга қодир эмаслар ва улар ўзларига ҳам ёрдам қила олмаслар'}, {'chapter': 7, 'verse': 198, 'text': 'وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا يَسۡمَعُواْۖ وَتَرَىٰهُمۡ يَنظُرُونَ إِلَيۡكَ وَهُمۡ لَا يُبۡصِرُونَ\nАгар уларни ҳидоятга чорласангиз, эшитмайдилар. Улар ўзлари кўрмаган ҳолларида сизга (боқиб) турганларини кўрурсиз'}, {'chapter': 7, 'verse': 199, 'text': 'خُذِ ٱلۡعَفۡوَ وَأۡمُرۡ بِٱلۡعُرۡفِ وَأَعۡرِضۡ عَنِ ٱلۡجَٰهِلِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), марҳаматли бўлинг, яхшиликка буюринг ва жоҳиллардан юз ўгиринг'}, {'chapter': 7, 'verse': 200, 'text': 'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۚ إِنَّهُۥ سَمِيعٌ عَلِيمٌ\nАгар сизни шайтоннинг васвасаси йўлдан урмоқчи бўлса, Аллоҳдан паноҳ сўранг! Албатта, У эшитувчи, билгувчидир'}, {'chapter': 7, 'verse': 201, 'text': 'إِنَّ ٱلَّذِينَ ٱتَّقَوۡاْ إِذَا مَسَّهُمۡ طَـٰٓئِفٞ مِّنَ ٱلشَّيۡطَٰنِ تَذَكَّرُواْ فَإِذَا هُم مُّبۡصِرُونَ\nТақво қилгувчи зотларни қачон шайтон томонидан бирон васваса ушласа, (Аллоҳни) эслайдилар, бас (Тўғри Йўлни) кўра бошлайдилар'}, {'chapter': 7, 'verse': 202, 'text': 'وَإِخۡوَٰنُهُمۡ يَمُدُّونَهُمۡ فِي ٱلۡغَيِّ ثُمَّ لَا يُقۡصِرُونَ\nУларнинг (шайтонларнинг) дўстлари эса йўлдан оздиришда уларга (шайтонларга) ёрдам берадилар, сўнг (бу иғволарини) тўхтатмайдилар'}, {'chapter': 7, 'verse': 203, 'text': 'وَإِذَا لَمۡ تَأۡتِهِم بِـَٔايَةٖ قَالُواْ لَوۡلَا ٱجۡتَبَيۡتَهَاۚ قُلۡ إِنَّمَآ أَتَّبِعُ مَا يُوحَىٰٓ إِلَيَّ مِن رَّبِّيۚ هَٰذَا بَصَآئِرُ مِن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُؤۡمِنُونَ\nҚачон уларга бирон оят келтирмасангиз, «Ўзинг тўқий қолмайсанми?», дейишади. Айтинг: «Мен фақат Парвардигоримдан келган ваҳийгагана эргашурман. Бу (Қуръон) иймон келтирадиган қавм учун Парвардигорингиз тарафидан кўрсатма, ҳидоят ва раҳматдир»'}, {'chapter': 7, 'verse': 204, 'text': 'وَإِذَا قُرِئَ ٱلۡقُرۡءَانُ فَٱسۡتَمِعُواْ لَهُۥ وَأَنصِتُواْ لَعَلَّكُمۡ تُرۡحَمُونَ\nҚачонки Қуръон қироат қилинса, унга қулоқ тутингиз ва жим турингиз — шояд (Аллоҳ тарафидан) раҳматга сазовор бўлсангизлар'}, {'chapter': 7, 'verse': 205, 'text': 'وَٱذۡكُر رَّبَّكَ فِي نَفۡسِكَ تَضَرُّعٗا وَخِيفَةٗ وَدُونَ ٱلۡجَهۡرِ مِنَ ٱلۡقَوۡلِ بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ وَلَا تَكُن مِّنَ ٱلۡغَٰفِلِينَ\nПарвардигорингизни ичингизда ёлвориб, қўрқиб, дилдан эрта-ю кеч ёд қилинг ва ғофил кимсалардан бўлманг'}, {'chapter': 7, 'verse': 206, 'text': 'إِنَّ ٱلَّذِينَ عِندَ رَبِّكَ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَيُسَبِّحُونَهُۥ وَلَهُۥ يَسۡجُدُونَۤ۩\nАлбатта, Парвардигорингизнинг ҳузуридаги зотлар (яъни, фаришталар) Унга ибодат қилишдан ор қилмайдилар. Уни поклайдилар ва Унгагина сажда қиладилар'}, {'chapter': 8, 'verse': 1, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلۡأَنفَالِۖ قُلِ ٱلۡأَنفَالُ لِلَّهِ وَٱلرَّسُولِۖ فَٱتَّقُواْ ٱللَّهَ وَأَصۡلِحُواْ ذَاتَ بَيۡنِكُمۡۖ وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥٓ إِن كُنتُم مُّؤۡمِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сиздан ўлжалар ҳақида сўрайдилар. Айтинг: «Ўлжалар Аллоҳ ва пайғамбарникидир. Бас, Аллоҳдан қўрқингиз ва ўз ораларингизни ўнглангиз! Агар мўмин бўлсангизлар, Аллоҳ ва Унинг пайғамбарига бўйсунингиз'}, {'chapter': 8, 'verse': 2, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَإِذَا تُلِيَتۡ عَلَيۡهِمۡ ءَايَٰتُهُۥ زَادَتۡهُمۡ إِيمَٰنٗا وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nФақат Аллоҳ (номи) зикр қилинганида қалбларига қўрқинч тушадиган, Унинг оятлари тиловат қилинганда иймонлари зиёда бўладиган ва Парвардигорларигагина суянадиган кишилар (ҳақиқий) мўминдирлар'}, {'chapter': 8, 'verse': 3, 'text': 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар намозни тўкис адо этадилар ва Биз уларни баҳраманд қилган нарсалардан инфоқ-эҳсон қиладилар'}, {'chapter': 8, 'verse': 4, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُمۡ دَرَجَٰتٌ عِندَ رَبِّهِمۡ وَمَغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nАна ўшалар ҳақиқий мўминлар бўлиб, улар учун Парвардигорлари ҳузурида (яъни, жаннатда, юксак) даражалар, мағфират ва улуғ ризқ бордир'}, {'chapter': 8, 'verse': 5, 'text': 'كَمَآ أَخۡرَجَكَ رَبُّكَ مِنۢ بَيۡتِكَ بِٱلۡحَقِّ وَإِنَّ فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ لَكَٰرِهُونَ\n(Бу ўлжа тақсимотида чиққан можаро) худди мўминлардан бир гуруҳи (урушни) аниқ ёмон кўриб турган ҳолларида, Парвардигорингиз ҳақ ҳукм билан сизни уйингиздан (жанггоҳга) чиқарганга ўхшайди'}, {'chapter': 8, 'verse': 6, 'text': 'يُجَٰدِلُونَكَ فِي ٱلۡحَقِّ بَعۡدَ مَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى ٱلۡمَوۡتِ وَهُمۡ يَنظُرُونَ\n(Ўшанда) улар гўё кўриб турган ҳолларида ўлимга ҳайдаб кетилаётгандек, Сиз билан аниқ-равшан бўлган ҳақиқат (яъни, урушга чиқиш) тўғрисида баҳслашган эдилар'}, {'chapter': 8, 'verse': 7, 'text': 'وَإِذۡ يَعِدُكُمُ ٱللَّهُ إِحۡدَى ٱلطَّآئِفَتَيۡنِ أَنَّهَا لَكُمۡ وَتَوَدُّونَ أَنَّ غَيۡرَ ذَاتِ ٱلشَّوۡكَةِ تَكُونُ لَكُمۡ وَيُرِيدُ ٱللَّهُ أَن يُحِقَّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦ وَيَقۡطَعَ دَابِرَ ٱلۡكَٰفِرِينَ\nЎшанда (эй мўминлар), Аллоҳ икки тоифадан (яъни, карвон ва қўшиндан) бирини сизлар учун (ўлжа) қилишга ваъда берган эди. Сизлар қурол-яроғсиз тоифа (яъни, карвон) ўзингиз учун бўлишини истаган эдингиз. Аллоҳ эса Ўз сўзлари билан ҳақиқатни муқаррар этишни ва кофирларнинг думини қирқишни ирода қилган эди'}, {'chapter': 8, 'verse': 8, 'text': 'لِيُحِقَّ ٱلۡحَقَّ وَيُبۡطِلَ ٱلۡبَٰطِلَ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ\n(Аллоҳ), гарчи жиноятчи — осийлар ёмон кўрсалар-да, ҳақни ҳақ, ботилни ботил қилиш учун (шундай ирода қилган эди)'}, {'chapter': 8, 'verse': 9, 'text': 'إِذۡ تَسۡتَغِيثُونَ رَبَّكُمۡ فَٱسۡتَجَابَ لَكُمۡ أَنِّي مُمِدُّكُم بِأَلۡفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُرۡدِفِينَ\nЎшанда Парвардигорингиздан мадад тилаганингизда, У сизларга ижобат қилиб: «Мен сизларга кетма-кет келадиган минглаб фаришталар билан мадад берурман», деди'}, {'chapter': 8, 'verse': 10, 'text': 'وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ وَلِتَطۡمَئِنَّ بِهِۦ قُلُوبُكُمۡۚ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ\nАллоҳ фақат хушхабар бўлсин, деб ва кўнгилларингиз хотиржам бўлиши учун шундай қилди. Ғалаба фақат Аллоҳнинг даргоҳидан бўлур. Ҳақиқатан, Аллоҳ ғолиб ва ҳикматлидир'}, {'chapter': 8, 'verse': 11, 'text': 'إِذۡ يُغَشِّيكُمُ ٱلنُّعَاسَ أَمَنَةٗ مِّنۡهُ وَيُنَزِّلُ عَلَيۡكُم مِّنَ ٱلسَّمَآءِ مَآءٗ لِّيُطَهِّرَكُم بِهِۦ وَيُذۡهِبَ عَنكُمۡ رِجۡزَ ٱلشَّيۡطَٰنِ وَلِيَرۡبِطَ عَلَىٰ قُلُوبِكُمۡ وَيُثَبِّتَ بِهِ ٱلۡأَقۡدَامَ\nЎшанда хотиржам бўлишингиз учун сизларни Ўз тарафидан уйқуга чўмдирган ва поклаб, сизлардан шайтон васвасасини кетказиш учун дилларингизни бир ва қадамларингизни маҳкам қилиш учун устингизга самодан сув — ёмғир ёғдирган эди'}, {'chapter': 8, 'verse': 12, 'text': 'إِذۡ يُوحِي رَبُّكَ إِلَى ٱلۡمَلَـٰٓئِكَةِ أَنِّي مَعَكُمۡ فَثَبِّتُواْ ٱلَّذِينَ ءَامَنُواْۚ سَأُلۡقِي فِي قُلُوبِ ٱلَّذِينَ كَفَرُواْ ٱلرُّعۡبَ فَٱضۡرِبُواْ فَوۡقَ ٱلۡأَعۡنَاقِ وَٱضۡرِبُواْ مِنۡهُمۡ كُلَّ بَنَانٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ўшанда Парвардигорингиз фаришталарга: «Албатта, Мен сизлар билан биргаман, бас, сизлар иймон келтирган зотларнинг (ғалаба қозонишларига бўлган ишончларини) маҳкамланглар! Мен кофир бўлган кимсаларнинг дилларига қўрқув солажакман. Бас, уларнинг бўйинларини узинглар, бутун-ҳамма бармоқларини чопинглар», деб ваҳий қилган эди'}, {'chapter': 8, 'verse': 13, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ ٱللَّهَ وَرَسُولَهُۥۚ وَمَن يُشَاقِقِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nБунга сабаб, уларнинг Аллоҳ ва Унинг пайғамбарига қарши турганларидир. Кимки, Аллоҳ ва Унинг пайғамбарига қарши турса, бас, албатта Аллоҳнинг жазоси қаттиқдир'}, {'chapter': 8, 'verse': 14, 'text': 'ذَٰلِكُمۡ فَذُوقُوهُ وَأَنَّ لِلۡكَٰفِرِينَ عَذَابَ ٱلنَّارِ\n(Эй кофирлар), мана шу сизларнинг (жазоингиздир) бас, тортаверинглар. Кофирлар учун яна дўзах азоби ҳам бордир'}, {'chapter': 8, 'verse': 15, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ زَحۡفٗا فَلَا تُوَلُّوهُمُ ٱلۡأَدۡبَارَ\nЭй мўминлар, кофирларнинг ҳужумига дуч келганингизда, уларга орқа ўгирманглар (яъни, қочманглар)'}, {'chapter': 8, 'verse': 16, 'text': 'وَمَن يُوَلِّهِمۡ يَوۡمَئِذٖ دُبُرَهُۥٓ إِلَّا مُتَحَرِّفٗا لِّقِتَالٍ أَوۡ مُتَحَيِّزًا إِلَىٰ فِئَةٖ فَقَدۡ بَآءَ بِغَضَبٖ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nКимки ўша (уруш) кунида жанг йўсинини ўзгартириш ёки бошқа бир гуруҳ (мусулмонларга) қўшилишдан ташқари ҳолатда, уларга (кофирларга) орқа ўгириб қочса, бас, у Аллоҳ тарафидан ғазаб билан кетибди ва унинг жойи жаҳаннамдир. Нақадар ёмон оқибат-а'}, {'chapter': 8, 'verse': 17, 'text': 'فَلَمۡ تَقۡتُلُوهُمۡ وَلَٰكِنَّ ٱللَّهَ قَتَلَهُمۡۚ وَمَا رَمَيۡتَ إِذۡ رَمَيۡتَ وَلَٰكِنَّ ٱللَّهَ رَمَىٰ وَلِيُبۡلِيَ ٱلۡمُؤۡمِنِينَ مِنۡهُ بَلَآءً حَسَنًاۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nБас, (эй мусулмонлар), уларни сизлар ўлдирмадингиз, балки Аллоҳ ўлдирди. (Эй Муҳаммад алайҳис-салоту вас-салом, уларнинг юзига қўлингиздаги бир сиқим тупроқни) отган пайтингизда, сиз отмадингиз, балки Аллоҳ отди ва (бу ғалабани Аллоҳ) Ўз томонидан мўминларга чиройли инъом бўлсин, деб қилди. Албатта Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 8, 'verse': 18, 'text': 'ذَٰلِكُمۡ وَأَنَّ ٱللَّهَ مُوهِنُ كَيۡدِ ٱلۡكَٰفِرِينَ\n(Эй мўминлар), мана шу (сизларнинг мукофотингиздир). Албатта, Аллоҳ кофирларнинг ҳийласини заиф қилгувчи Зотдир'}, {'chapter': 8, 'verse': 19, 'text': 'إِن تَسۡتَفۡتِحُواْ فَقَدۡ جَآءَكُمُ ٱلۡفَتۡحُۖ وَإِن تَنتَهُواْ فَهُوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَعُودُواْ نَعُدۡ وَلَن تُغۡنِيَ عَنكُمۡ فِئَتُكُمۡ شَيۡـٔٗا وَلَوۡ كَثُرَتۡ وَأَنَّ ٱللَّهَ مَعَ ٱلۡمُؤۡمِنِينَ\n(Эй аҳли Макка), агар ғалабани истаган бўлсангизлар, мана, сизларнинг (зиёнингизга) ғалаба келди, Агар (пайғамбарга қарши урушишдан) тўхтасангизлар, бас, бу ўзингиз учун яхшидир. Агар (яна урушга) қайтсангизлар, Биз ҳам қайтамиз ва гуруҳингиз қанча кўп бўлмасин, сизларни ҳеч нарсадан беҳожат қила олмайди. Албатта, Аллоҳ мўминлар билан биргадир'}, {'chapter': 8, 'verse': 20, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَوَلَّوۡاْ عَنۡهُ وَأَنتُمۡ تَسۡمَعُونَ\nЭй мўминлар, Аллоҳга ва Унинг пайғамбарига итоат қилингиз ва (Қуръонни) эшитиб туриб, ундан юз ўгириб кетмангиз'}, {'chapter': 8, 'verse': 21, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ قَالُواْ سَمِعۡنَا وَهُمۡ لَا يَسۡمَعُونَ\nВа ўзлари эшитмаган (яъни, англаб, иймон келтирмаган) ҳолларида, «эшитдик», деган кимсалар каби бўлмангиз'}, {'chapter': 8, 'verse': 22, 'text': '۞إِنَّ شَرَّ ٱلدَّوَآبِّ عِندَ ٱللَّهِ ٱلصُّمُّ ٱلۡبُكۡمُ ٱلَّذِينَ لَا يَعۡقِلُونَ\nАлбатта, Аллоҳнинг наздида энг ёмон ҳайвонлар кар ва соқов бўлиб олган ақлсиз кимсалардир'}, {'chapter': 8, 'verse': 23, 'text': 'وَلَوۡ عَلِمَ ٱللَّهُ فِيهِمۡ خَيۡرٗا لَّأَسۡمَعَهُمۡۖ وَلَوۡ أَسۡمَعَهُمۡ لَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ\nАгар Аллоҳ уларда бирон яхшиликни билганида эди, албатта уларнинг қулоқларини очиб қўйган бўлур эди ва агар (Аллоҳ уларнинг) қулоқларини очиб қўйганида ҳам улар юз ўгирган ҳолларида кетган бўлур эдилар'}, {'chapter': 8, 'verse': 24, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱسۡتَجِيبُواْ لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمۡ لِمَا يُحۡيِيكُمۡۖ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَحُولُ بَيۡنَ ٱلۡمَرۡءِ وَقَلۡبِهِۦ وَأَنَّهُۥٓ إِلَيۡهِ تُحۡشَرُونَ\nЭй мўминлар, Аллоҳ ва Унинг пайғамбари сизларни абадий ҳаёт берадиган нарсага (яъни, динга) даъват қилар экан, уни қабул қилинглар ва билингларки, шубҳасиз, Аллоҳ ҳар бир киши билан унинг қалби ўртасини эгаллаб турур ва шубҳасиз, Унинг ҳузурига тўпланурсизлар'}, {'chapter': 8, 'verse': 25, 'text': 'وَٱتَّقُواْ فِتۡنَةٗ لَّا تُصِيبَنَّ ٱلَّذِينَ ظَلَمُواْ مِنكُمۡ خَآصَّةٗۖ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nҲамда сизлардан фақат золим кимсаларнинг ўзигагина етмай (балки барчага етадиган) балодан сақланингиз! Ва билингизки, албатта Аллоҳнинг азоби қаттиқдир'}, {'chapter': 8, 'verse': 26, 'text': 'وَٱذۡكُرُوٓاْ إِذۡ أَنتُمۡ قَلِيلٞ مُّسۡتَضۡعَفُونَ فِي ٱلۡأَرۡضِ تَخَافُونَ أَن يَتَخَطَّفَكُمُ ٱلنَّاسُ فَـَٔاوَىٰكُمۡ وَأَيَّدَكُم بِنَصۡرِهِۦ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِ لَعَلَّكُمۡ تَشۡكُرُونَ\nВа сизлар Ерда (яъни, Маккада) озчилик ва ночор бўлган ҳолингизда, одамлар (яъни, Макка мушриклари) талаб кетишларидан қўрқиб турган пайтингизда сизларга жой бериб (яъни, Мадинага кўчириб) Ўз ёрдами билан қўллаб-қувватлаганини ва шукр қилишингиз учун покиза ризқлар билан баҳраманд қилганини эслангиз'}, {'chapter': 8, 'verse': 27, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَخُونُواْ ٱللَّهَ وَٱلرَّسُولَ وَتَخُونُوٓاْ أَمَٰنَٰتِكُمۡ وَأَنتُمۡ تَعۡلَمُونَ\nЭй мўминлар, Аллоҳ ва Унинг пайғамбарига хиёнат қилмангиз ва билган ҳолингизда сизларга ишониб берилан нарсаларга (яъни, динга ва бошқа ҳар қандай омонатларга) хиёнат қилмангиз'}, {'chapter': 8, 'verse': 28, 'text': 'وَٱعۡلَمُوٓاْ أَنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞ وَأَنَّ ٱللَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ\nБилингизки, албатта мол-дунёларингиз ва бола-чақангиз фақат бир фитна-алдовдир, холос. Ёлғиз Аллоҳнинг ҳузуридагина (яъни, унинг амру фармонига итоат этишдагина) улуғ ажр-савоб бордир'}, {'chapter': 8, 'verse': 29, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَتَّقُواْ ٱللَّهَ يَجۡعَل لَّكُمۡ فُرۡقَانٗا وَيُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيَغۡفِرۡ لَكُمۡۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nЭй мўминлар, агар Аллоҳдан қўрқсангизлар, сизлар учун ҳақ билан ноҳақни ажратадиган ҳидоят ато қилур ва ёмонлик-гуноҳларингизни ўчириб, сизларни мағфират қилур. Аллоҳ улуғ фазлу марҳамат Соҳибидир'}, {'chapter': 8, 'verse': 30, 'text': 'وَإِذۡ يَمۡكُرُ بِكَ ٱلَّذِينَ كَفَرُواْ لِيُثۡبِتُوكَ أَوۡ يَقۡتُلُوكَ أَوۡ يُخۡرِجُوكَۚ وَيَمۡكُرُونَ وَيَمۡكُرُ ٱللَّهُۖ وَٱللَّهُ خَيۡرُ ٱلۡمَٰكِرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), кофирлар сизни ҳибс қилиш ё ўлдириш, ёки (Маккадан) чиқариб юбориш учун сизга макр қилган пайтларини эсланг! Улар макр қилурлар, Аллоҳ ҳам «макр» қилур. Аллоҳ «макр» қилувчиларнинг зўридир'}, {'chapter': 8, 'verse': 31, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا قَالُواْ قَدۡ سَمِعۡنَا لَوۡ نَشَآءُ لَقُلۡنَا مِثۡلَ هَٰذَآ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nҚачон уларга (кофирларга) Бизнинг оятларимиз тиловат қилинса: «Эшитганмиз, агар хоҳласак, бунга ўхшаган гапни ўзимиз айтган бўлур эдик. Бу фақат аввалгилардан қолган афсоналар, холос», дейдилар'}, {'chapter': 8, 'verse': 32, 'text': 'وَإِذۡ قَالُواْ ٱللَّهُمَّ إِن كَانَ هَٰذَا هُوَ ٱلۡحَقَّ مِنۡ عِندِكَ فَأَمۡطِرۡ عَلَيۡنَا حِجَارَةٗ مِّنَ ٱلسَّمَآءِ أَوِ ٱئۡتِنَا بِعَذَابٍ أَلِيمٖ\nУларнинг: «Эй Худо, агар мана шу (Қуръон) Сенинг даргоҳингдан келган ҳақиқат бўлса, устимизга самодан тош ёғдиргин ёки бизларга аламли азоб келтиргин», деганларини эсланг'}, {'chapter': 8, 'verse': 33, 'text': 'وَمَا كَانَ ٱللَّهُ لِيُعَذِّبَهُمۡ وَأَنتَ فِيهِمۡۚ وَمَا كَانَ ٱللَّهُ مُعَذِّبَهُمۡ وَهُمۡ يَسۡتَغۡفِرُونَ\nМодомики, сиз уларнинг ораларида экансиз, Аллоҳ уларни ҳаргиз азобламас ва улар (мўминлар) мағфират сўраб турган ҳолларида ҳам Аллоҳ уларни азоблагувчи эмасдир'}, {'chapter': 8, 'verse': 34, 'text': 'وَمَا لَهُمۡ أَلَّا يُعَذِّبَهُمُ ٱللَّهُ وَهُمۡ يَصُدُّونَ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَمَا كَانُوٓاْ أَوۡلِيَآءَهُۥٓۚ إِنۡ أَوۡلِيَآؤُهُۥٓ إِلَّا ٱلۡمُتَّقُونَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\n(Лекин агар) улар (иймонга келмасдан кишиларни) Масжидул-Ҳаромдан тўсар эканлар, Аллоҳ уларни нега азобламасин?! (Яъни, Сиз ва мўминлар Маккадан чиқиб кетганингиздан сўнг уларни албатта азобга гирифтор қилур). Улар унинг (Масжидул-Ҳаромнинг) эгалари эмаслар. Унинг эгалари фақат Аллоҳдан қўрқувчи зотлардир. Лекин уларнинг кўплари билмайдилар'}, {'chapter': 8, 'verse': 35, 'text': 'وَمَا كَانَ صَلَاتُهُمۡ عِندَ ٱلۡبَيۡتِ إِلَّا مُكَآءٗ وَتَصۡدِيَةٗۚ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nУларнинг Байтуллоҳ олдида қилган «ибодат»лари фақат ҳуштак ва чапак чалиш бўлди. Бас, (эй мушриклар, бугун — Бадрда) кофир бўлганинглар сабабли азобимизни тотиб кўринглар'}, {'chapter': 8, 'verse': 36, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ يُنفِقُونَ أَمۡوَٰلَهُمۡ لِيَصُدُّواْ عَن سَبِيلِ ٱللَّهِۚ فَسَيُنفِقُونَهَا ثُمَّ تَكُونُ عَلَيۡهِمۡ حَسۡرَةٗ ثُمَّ يُغۡلَبُونَۗ وَٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ يُحۡشَرُونَ\nАлбатта, кофир бўлган кимсалар мол-дунёларини Аллоҳнинг йўлидан тўсиш учун ишлатурлар. Бас, уни сарфлайдилар-у, сўнгра ўша (моллари) ўзларига ҳасрат бўлур, сўнгра мағлуб бўлурлар. Кофир бўлган кимсалар жаҳаннамга тўпланурлар'}, {'chapter': 8, 'verse': 37, 'text': 'لِيَمِيزَ ٱللَّهُ ٱلۡخَبِيثَ مِنَ ٱلطَّيِّبِ وَيَجۡعَلَ ٱلۡخَبِيثَ بَعۡضَهُۥ عَلَىٰ بَعۡضٖ فَيَرۡكُمَهُۥ جَمِيعٗا فَيَجۡعَلَهُۥ فِي جَهَنَّمَۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nАллоҳ нопокни покдан (кофирни мўминдан) ажратиши ва нопок кимсаларнинг ҳаммаларини устма-уст тахлаб, жаҳаннамга солиши учун (тўпланурлар). Ана ўшалар зиён кўргувчилардир'}, {'chapter': 8, 'verse': 38, 'text': 'قُل لِّلَّذِينَ كَفَرُوٓاْ إِن يَنتَهُواْ يُغۡفَرۡ لَهُم مَّا قَدۡ سَلَفَ وَإِن يَعُودُواْ فَقَدۡ مَضَتۡ سُنَّتُ ٱلۡأَوَّلِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), кофир бўлган кимсаларга айтингки, агар (кофирликларидан) тўхтасалар, ўтган гуноҳлари мағфират қилинур. Агар яна (кофирликка) қайтсалар, у ҳолда аввалгиларининг суннатлари (яъни, кўргуликлари) ўтган-ку, (яъни, уларнинг ҳам бошларига аввалги кофирларнинг куни тушар)'}, {'chapter': 8, 'verse': 39, 'text': 'وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ ٱلدِّينُ كُلُّهُۥ لِلَّهِۚ فَإِنِ ٱنتَهَوۡاْ فَإِنَّ ٱللَّهَ بِمَا يَعۡمَلُونَ بَصِيرٞ\nТо (дунёда) бирон фитна-алдов қолмай, бутун дин фақат Аллоҳ учун бўлгунга қадар улар билан жанг қилинглар! Энди агар (кофирликдан) тўхтасалар, бас, албатта Аллоҳ қилаётган амалларини кўргувчидир'}, {'chapter': 8, 'verse': 40, 'text': 'وَإِن تَوَلَّوۡاْ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَوۡلَىٰكُمۡۚ نِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِيرُ\nАгар (кофирлар иймондан) юз ўгирсалар, бас, (эй мўминлар), билингизки, шубҳасиз, Аллоҳ Хожангиздир. У зот нақадар яхши хожа ва нақадар яхши ёрдамчидир'}, {'chapter': 8, 'verse': 41, 'text': '۞وَٱعۡلَمُوٓاْ أَنَّمَا غَنِمۡتُم مِّن شَيۡءٖ فَأَنَّ لِلَّهِ خُمُسَهُۥ وَلِلرَّسُولِ وَلِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ وَمَآ أَنزَلۡنَا عَلَىٰ عَبۡدِنَا يَوۡمَ ٱلۡفُرۡقَانِ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nАгар Аллоҳга ва (ҳақ билан ноҳақнинг) ажралиш кунида — икки жамоа тўқнашган кунда, бандамиз (Муҳаммадга) нозил қилганимиз (оятлар ва фаришталардан иборат) нарсага иймон келтиргувчи бўлсангизлар, билингизки, ўлжа қилиб олган нарсангизнинг бешдан бири Аллоҳ учун, пайғамбар учун ва у зотнинг қариндош-уруғи, етимлар, мискинлар ва мусофирлар учундир. Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 8, 'verse': 42, 'text': 'إِذۡ أَنتُم بِٱلۡعُدۡوَةِ ٱلدُّنۡيَا وَهُم بِٱلۡعُدۡوَةِ ٱلۡقُصۡوَىٰ وَٱلرَّكۡبُ أَسۡفَلَ مِنكُمۡۚ وَلَوۡ تَوَاعَدتُّمۡ لَٱخۡتَلَفۡتُمۡ فِي ٱلۡمِيعَٰدِ وَلَٰكِن لِّيَقۡضِيَ ٱللَّهُ أَمۡرٗا كَانَ مَفۡعُولٗا لِّيَهۡلِكَ مَنۡ هَلَكَ عَنۢ بَيِّنَةٖ وَيَحۡيَىٰ مَنۡ حَيَّ عَنۢ بَيِّنَةٖۗ وَإِنَّ ٱللَّهَ لَسَمِيعٌ عَلِيمٌ\nЎшанда сизлар (Мадинага) яқинроқ тарафда, улар (яъни, мушрикларнинг қўшинлари) йироқроқ тарафда, отлиқлар (яъни, сизлар ўлжа қилиб олмоқчи бўлган карвон) эса сизлардан қуйироқда (денгиз соҳили бўйлаб кетмоқда) эдилар. Агар мушриклар билан жанг қилиш учун (аввалдан) ваъдалашганларингизда, бирон муайян вақтга келиша олмаган бўлур эдингиз. Лекин Аллоҳ ҳалок бўлгувчилар (яъни, кофирлик йўлини тутгувчилар) очиқ ҳужжат билан (яъни, Аллоҳнинг мўъжизасига гувоҳ бўлган ҳолларида) ҳалок бўлсинлар, тирик қолгувчилар (яъни, иймонига келувчилар) ҳам очиқ ҳужжат билан тирик қолсинлар, деб қилинувчи бўлган ишни (яъни, мусулмонларнинг ғалаба қозонишлари, кофирларнинг мағлуб бўлишларини) амалга ошириш учун (сизларни тўқнаштириб қўйди). Шак-шубҳасиз, Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 8, 'verse': 43, 'text': 'إِذۡ يُرِيكَهُمُ ٱللَّهُ فِي مَنَامِكَ قَلِيلٗاۖ وَلَوۡ أَرَىٰكَهُمۡ كَثِيرٗا لَّفَشِلۡتُمۡ وَلَتَنَٰزَعۡتُمۡ فِي ٱلۡأَمۡرِ وَلَٰكِنَّ ٱللَّهَ سَلَّمَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга тушингизда Аллоҳ уларни оз кўрсатганини эсланг! Агар сизга уларни кўп кўрсатганда эди, албатта сизлар сустлашиб қолган ва бу иш (яъни, кофирларга қарши жангга чиқиш) хусусида тортишиб қолган бўлур эдингизлар. Лекин Аллоҳ сизларни бундан сақлади. Албатта, У дилларни эгаллаб олган сирларни билгувчидир'}, {'chapter': 8, 'verse': 44, 'text': 'وَإِذۡ يُرِيكُمُوهُمۡ إِذِ ٱلۡتَقَيۡتُمۡ فِيٓ أَعۡيُنِكُمۡ قَلِيلٗا وَيُقَلِّلُكُمۡ فِيٓ أَعۡيُنِهِمۡ لِيَقۡضِيَ ٱللَّهُ أَمۡرٗا كَانَ مَفۡعُولٗاۗ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n(Эй мўминлар), Аллоҳ қилинадиган ишни амалга ошириш учун тўқнашган пайтингизда сизларнинг кўзингизга уларни кам қилиб кўрсатганини, уларнинг кўзларига эса сизларни оз қилиб қўйганини эслангиз! Барча ишлар Аллоҳга қайтарилур'}, {'chapter': 8, 'verse': 45, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمۡ فِئَةٗ فَٱثۡبُتُواْ وَٱذۡكُرُواْ ٱللَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ\nЭй мўминлар, (кофир) жамоатга рўбарў бўлганингизда, саботли бўлингиз ва доимо Аллоҳни ёд этингиз, шояд нажот топурсизлар'}, {'chapter': 8, 'verse': 46, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَنَٰزَعُواْ فَتَفۡشَلُواْ وَتَذۡهَبَ رِيحُكُمۡۖ وَٱصۡبِرُوٓاْۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِينَ\nВа Аллоҳ ҳамда Унинг пайғамбарига итоат қилингиз ва (ўзаро) талашиб-тортишмангизки, у ҳолда сустлашиб, куч-қувватингиз кетур. Сабр-тоқат қилингиз! Албатта, Аллоҳ сабр қилгувчилар билан биргадир'}, {'chapter': 8, 'verse': 47, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ خَرَجُواْ مِن دِيَٰرِهِم بَطَرٗا وَرِئَآءَ ٱلنَّاسِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِۚ وَٱللَّهُ بِمَا يَعۡمَلُونَ مُحِيطٞ\nЎз диёрларидан (яъни, Маккадан) кибру ҳаво билан ўзларини одамларга кўз-кўзлаб чиққан ва Аллоҳнинг йўлидан тўсадиган кимсалар каби бўлмангизлар! Аллоҳ уларнинг қилаётган ишларини иҳота қилгувчидир (ўраб олгувчидир)'}, {'chapter': 8, 'verse': 48, 'text': 'وَإِذۡ زَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ وَقَالَ لَا غَالِبَ لَكُمُ ٱلۡيَوۡمَ مِنَ ٱلنَّاسِ وَإِنِّي جَارٞ لَّكُمۡۖ فَلَمَّا تَرَآءَتِ ٱلۡفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَيۡهِ وَقَالَ إِنِّي بَرِيٓءٞ مِّنكُمۡ إِنِّيٓ أَرَىٰ مَا لَا تَرَوۡنَ إِنِّيٓ أَخَافُ ٱللَّهَۚ وَٱللَّهُ شَدِيدُ ٱلۡعِقَابِ\nЎшанда, шайтон уларга қилаётган ишларини чиройли кўрсатиб: «Бу кун сизлардан ғолиб бўладиган бирон одам йўқ, ўзим сизларнинг ҳомийингизман», деди. Энди қачонки икки жамоат учрашганида эса, кетига қайтиб кетди ва: «Албатта, мен сизлардан безорман. Мен (мусулмонлар орасида) сизлар кўрмаётган нарсани (яъни, фаришталар борлигини) кўрмоқдаман. Мен Аллоҳдан қўрқаман. Аллоҳнинг азоби қаттиқдир», деди'}, {'chapter': 8, 'verse': 49, 'text': 'إِذۡ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ غَرَّ هَـٰٓؤُلَآءِ دِينُهُمۡۗ وَمَن يَتَوَكَّلۡ عَلَى ٱللَّهِ فَإِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nЎшанда мунофиқлар ва дилларида мараз бўлган кимсалар: «Уларни (яъни, мусулмонларни) динлари мағрур қилиб юборди (яъни, озгина одам билан шундай катта қўшинга қарши жанг қилмоқчи бўляптилар»), дейишар эди. (Мунофиқларнинг бу сўзлари нотўғридир) ким Аллоҳга суянса, бас, албатта Аллоҳ ғолиб ва ҳикматлидир'}, {'chapter': 8, 'verse': 50, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ يَتَوَفَّى ٱلَّذِينَ كَفَرُواْ ٱلۡمَلَـٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ وَذُوقُواْ عَذَابَ ٱلۡحَرِيقِ\nАгар фаришталар кофир кимсаларнинг юз ва кетларига уриб, жонларини олаётган ва: «Ёндирувчи — дўзах азобини тотингиз'}, {'chapter': 8, 'verse': 51, 'text': 'ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nБунга сабаб ўзларингиз қилган амалларингиздир, зеро Аллоҳ ҳаргиз бандаларига зулм қилгувчи эмасдир», деяётган пайтини кўрсангиз эди'}, {'chapter': 8, 'verse': 52, 'text': 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۚ إِنَّ ٱللَّهَ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ\n(Уларнинг қилмишлари) худди Фиръавн одамларининг ва улардан олдинги кимсаларнинг қилмишларига ўхшайди: улар Аллоҳ оятларига кофир бўлишгач, Аллоҳ уларни гуноҳлари сабабли (азоб билан) ушлаган эди. Албатта, Аллоҳ қувват эгаси ва азоби қаттиқдир'}, {'chapter': 8, 'verse': 53, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ لَمۡ يَكُ مُغَيِّرٗا نِّعۡمَةً أَنۡعَمَهَا عَلَىٰ قَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡ وَأَنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nБунга (яъни, уларнинг азобга гирифтор бўлишларига) сабаб — Аллоҳ бир қавмга инъом қилган неъматини то улар ўзларини ўзгартирмагунларича ўзгартирувчи эмаслиги ва Аллоҳ эшитгувчи, билгувчи эканлигидир'}, {'chapter': 8, 'verse': 54, 'text': 'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِ رَبِّهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَۚ وَكُلّٞ كَانُواْ ظَٰلِمِينَ\n(Уларнинг қилмишлари) худди Фиръавн одамлари ва улардан олдинги кимсаларнинг қилмишларига ўхшайди. Улар (Фиръавн одамлари) Парвардигорларининг оятларини ёлғон дейишгач, Биз уларнинг гуноҳлари сабабли ҳалок қилдик ва (денгизга) ғарқ этдик. (Чунки) ҳаммалари золим кимсалар эдилар'}, {'chapter': 8, 'verse': 55, 'text': 'إِنَّ شَرَّ ٱلدَّوَآبِّ عِندَ ٱللَّهِ ٱلَّذِينَ كَفَرُواْ فَهُمۡ لَا يُؤۡمِنُونَ\nАллоҳнинг наздида энг ёмон жонзотлар кофир бўлган кимсалардир. Чунки, улар иймонсиздирлар'}, {'chapter': 8, 'verse': 56, 'text': 'ٱلَّذِينَ عَٰهَدتَّ مِنۡهُمۡ ثُمَّ يَنقُضُونَ عَهۡدَهُمۡ فِي كُلِّ مَرَّةٖ وَهُمۡ لَا يَتَّقُونَ\nУларнинг орасида сиз билан аҳдлашиб, сўнгра (Аллоҳдан) қўрқмасдан ҳар сафар ўз аҳд-паймонларини бузаверадиган кимсалар бордир'}, {'chapter': 8, 'verse': 57, 'text': 'فَإِمَّا تَثۡقَفَنَّهُمۡ فِي ٱلۡحَرۡبِ فَشَرِّدۡ بِهِم مَّنۡ خَلۡفَهُمۡ لَعَلَّهُمۡ يَذَّكَّرُونَ\nБас, агар уларни жангда топсангиз, уларни ҳалок қилиш билан ортларидаги кимсаларни қўрқитиб қўйинг! Шояд эслатма олсалар'}, {'chapter': 8, 'verse': 58, 'text': 'وَإِمَّا تَخَافَنَّ مِن قَوۡمٍ خِيَانَةٗ فَٱنۢبِذۡ إِلَيۡهِمۡ عَلَىٰ سَوَآءٍۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡخَآئِنِينَ\nАгар (ўрталарингизда аҳд-паймон бўлган) бирон қавм тарафидан хиёнатни сезсангиз, уларга (қилган аҳд-паймонларини) баб-баробар қилиб ташланг (яъни, ўрталарингиздаги аҳд-паймон тугаганини ҳар икки тараф ҳам билсин). Албатта, Аллоҳ хиёнаткор кимсаларни севмас'}, {'chapter': 8, 'verse': 59, 'text': 'وَلَا يَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ سَبَقُوٓاْۚ إِنَّهُمۡ لَا يُعۡجِزُونَ\nКофир бўлган кимсалар устун келдик, деб ҳисобламасинлар, улар (Бизни) ожиз қила олмайдилар. (Албатта, улар Аллоҳдан қилмишларига яраша жазо олурлар)'}, {'chapter': 8, 'verse': 60, 'text': 'وَأَعِدُّواْ لَهُم مَّا ٱسۡتَطَعۡتُم مِّن قُوَّةٖ وَمِن رِّبَاطِ ٱلۡخَيۡلِ تُرۡهِبُونَ بِهِۦ عَدُوَّ ٱللَّهِ وَعَدُوَّكُمۡ وَءَاخَرِينَ مِن دُونِهِمۡ لَا تَعۡلَمُونَهُمُ ٱللَّهُ يَعۡلَمُهُمۡۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فِي سَبِيلِ ٱللَّهِ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ\n(Эй мўминлар), улар учун имконингиз борича куч ва эгарланган отларни тайёрлаб қўйингизки, бу билан Аллоҳнинг ва ўзларингизнинг душманларингизни ҳамда улардан ташқари сизлар билмайдиган — Аллоҳ биладиган бошқа бировларни ҳам қўрқувга солурсизлар. Аллоҳ йўлида нимани сарф қилсангиз, сизларга зулм қилинмаган ҳолда комил қилиб қайтарилур'}, {'chapter': 8, 'verse': 61, 'text': '۞وَإِن جَنَحُواْ لِلسَّلۡمِ فَٱجۡنَحۡ لَهَا وَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАгар улар сулҳга майл қилсалар, сиз ҳам унга мойил бўлинг ва Аллоҳга таваккул қилинг! Албатта, Унинг Ўзи эшитгувчи, билгувчидир'}, {'chapter': 8, 'verse': 62, 'text': 'وَإِن يُرِيدُوٓاْ أَن يَخۡدَعُوكَ فَإِنَّ حَسۡبَكَ ٱللَّهُۚ هُوَ ٱلَّذِيٓ أَيَّدَكَ بِنَصۡرِهِۦ وَبِٱلۡمُؤۡمِنِينَ\nАгар улар сизни алдамоқчи бўлсалар, бас, албатта сизга Аллоҳнинг ўзи кифоядир. У сизни Ўз ёрдами ва мўминлар билан қувватлантирган Зотдир'}, {'chapter': 8, 'verse': 63, 'text': 'وَأَلَّفَ بَيۡنَ قُلُوبِهِمۡۚ لَوۡ أَنفَقۡتَ مَا فِي ٱلۡأَرۡضِ جَمِيعٗا مَّآ أَلَّفۡتَ بَيۡنَ قُلُوبِهِمۡ وَلَٰكِنَّ ٱللَّهَ أَلَّفَ بَيۡنَهُمۡۚ إِنَّهُۥ عَزِيزٌ حَكِيمٞ\nУ уларнинг (мўминларнинг) дилларини бирлаштирган Зотдир. Агар (сиз) Ердаги бор нарсани сарфласангиз ҳам, уларнинг дилларини бирлаштира олмаган бўлур эдингиз. Лекин Аллоҳ уларни бирлаштирди. Албатта, У қудратли, ҳикматлидир'}, {'chapter': 8, 'verse': 64, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ حَسۡبُكَ ٱللَّهُ وَمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِينَ\nЭй пайғамбар, сизга ва сизга эргашган мўминларга Аллоҳнинг Ўзи кифоядир'}, {'chapter': 8, 'verse': 65, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ حَرِّضِ ٱلۡمُؤۡمِنِينَ عَلَى ٱلۡقِتَالِۚ إِن يَكُن مِّنكُمۡ عِشۡرُونَ صَٰبِرُونَ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُم مِّاْئَةٞ يَغۡلِبُوٓاْ أَلۡفٗا مِّنَ ٱلَّذِينَ كَفَرُواْ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ\nЭй пайғамбар, мўминларни жангга чорланг! Агар сизлардан йигирмата сабр-тоқатли киши бўлса, икки юзта (душманни) енгар! Агар сизлардан юз киши бўлса, кофирлардан мингини енгар! Бунга сабаб, уларнинг онгсиз қавм эканлигидир'}, {'chapter': 8, 'verse': 66, 'text': 'ٱلۡـَٰٔنَ خَفَّفَ ٱللَّهُ عَنكُمۡ وَعَلِمَ أَنَّ فِيكُمۡ ضَعۡفٗاۚ فَإِن يَكُن مِّنكُم مِّاْئَةٞ صَابِرَةٞ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُمۡ أَلۡفٞ يَغۡلِبُوٓاْ أَلۡفَيۡنِ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِينَ\nЭнди Аллоҳ сизларнинг (юкингизни) енгил қилди, сизларда ожизлик борлигини билди. Бас, сизлардан юзта сабр-тоқатли киши бўлса, икки юзта (кофирни) енгар. Агар сизлардан минг киши бўлса, Аллоҳнинг изни билан икки минг (кофир)ни енгар. Аллоҳ сабр қилгувчилар билан биргадир'}, {'chapter': 8, 'verse': 67, 'text': 'مَا كَانَ لِنَبِيٍّ أَن يَكُونَ لَهُۥٓ أَسۡرَىٰ حَتَّىٰ يُثۡخِنَ فِي ٱلۡأَرۡضِۚ تُرِيدُونَ عَرَضَ ٱلدُّنۡيَا وَٱللَّهُ يُرِيدُ ٱلۡأٓخِرَةَۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ\nБирон пайғамбар учун то ерда ғолиб бўлмагунича, асир олиш жоиз эмас эди. (Эй мўминлар), сизлар дунё нарсаларини истамоқдасиз. Аллоҳ эса Охират (неъматлари сизларники бўлиши)ни истайди. Аллоҳ қудратли, ҳикматлидир'}, {'chapter': 8, 'verse': 68, 'text': 'لَّوۡلَا كِتَٰبٞ مِّنَ ٱللَّهِ سَبَقَ لَمَسَّكُمۡ فِيمَآ أَخَذۡتُمۡ عَذَابٌ عَظِيمٞ\nАгар Аллоҳнинг ҳукми азалийсида (билмасдан қилган хатони кечиришлиги) ёзиб қўйилмаганида эди, албатта сизларга товон олганинглар сабабли улуғ азоб етган бўлур эди'}, {'chapter': 8, 'verse': 69, 'text': 'فَكُلُواْ مِمَّا غَنِمۡتُمۡ حَلَٰلٗا طَيِّبٗاۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nЭнди ўлжа қилиб олган нарсаларингизни ҳалол-пок ҳолда еяверинглар ва Аллоҳдан қўрқинглар! Албатта, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 8, 'verse': 70, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّمَن فِيٓ أَيۡدِيكُم مِّنَ ٱلۡأَسۡرَىٰٓ إِن يَعۡلَمِ ٱللَّهُ فِي قُلُوبِكُمۡ خَيۡرٗا يُؤۡتِكُمۡ خَيۡرٗا مِّمَّآ أُخِذَ مِنكُمۡ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭй пайғамбар, сизларнинг қўлингизда асир бўлган кишиларга айтинг: «Агар Аллоҳ дилларингизда яхшилик (яъни, иймон, ихлос) борлигини билса, сизларга ўзингиздан олинган нарсадан (яъни, тўлаган товонингиздан) яхшироқ нарса берур ва сизларни мағфират қилур». Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 8, 'verse': 71, 'text': 'وَإِن يُرِيدُواْ خِيَانَتَكَ فَقَدۡ خَانُواْ ٱللَّهَ مِن قَبۡلُ فَأَمۡكَنَ مِنۡهُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nАгар улар сизга хиёнат қилмоқчи бўлсалар — бас, (билингизки) улар илгари Аллоҳга ҳам хиёнат қилган эдилар. Шунда Аллоҳ (сизни) уларга ғолиб қилди. Аллоҳ билим, ҳикмат Соҳибидир'}, {'chapter': 8, 'verse': 72, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِ وَٱلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَـٰٓئِكَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَٱلَّذِينَ ءَامَنُواْ وَلَمۡ يُهَاجِرُواْ مَا لَكُم مِّن وَلَٰيَتِهِم مِّن شَيۡءٍ حَتَّىٰ يُهَاجِرُواْۚ وَإِنِ ٱسۡتَنصَرُوكُمۡ فِي ٱلدِّينِ فَعَلَيۡكُمُ ٱلنَّصۡرُ إِلَّا عَلَىٰ قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nАлбатта, иймон келтирган, ҳижрат қилган ва молу жонлари билан Аллоҳ йўлида курашган зотлар ва (Маккадан ҳижрат қилиб келган муҳожирларга) уй-жой бериб, ёрдам қилган зотлар — ана ўшалар, бир-бирларига дўстдирлар. (Яъни, тириклари бир-бирига ҳамкор, ўрталарида ўлим бўлса, бир-бирларига меросхўрдирлар). Иймон келтирган, аммо ҳижрат қилмаган (яъни, ҳали-ҳануз Маккада яшаб турган) кишилар эса то ҳижрат қилмагунларича сизлар уларга дўстлик қила олмайсизлар (яъни, бир-бирларингизга ҳамкор, меросхўр бўла олмайсизлар). Агар улар дин йўлида сизлардан ёрдам сўрасалар, ёрдам қилиш зиммангиздадир. Магар уларга, ўрталарингизда (урушмаслик ҳақида) аҳд-паймон бўлган қавмга зарар етказиш билан ёрдам қилмайсизлар. Аллоҳ қилаётган амалларингизни кўргувчидир'}, {'chapter': 8, 'verse': 73, 'text': 'وَٱلَّذِينَ كَفَرُواْ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٍۚ إِلَّا تَفۡعَلُوهُ تَكُن فِتۡنَةٞ فِي ٱلۡأَرۡضِ وَفَسَادٞ كَبِيرٞ\nКофир бўлган кимсалар бир-бирларига ҳамкордирлар. (Бас, эй мўминлар, сизлар уларни ўзларингизга душман тутингиз). Агар шундай қилмасанглар (яъни, мўминга дўст, кофирга душман бўлмасанглар), ерда фитна ва катта фасод бўлур'}, {'chapter': 8, 'verse': 74, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ وَٱلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nИймон келтирган, ҳижрат қилган ва Аллоҳ йўлида курашган зотлар ва (муҳожирларга) уй-жой бериб, ёрдам қилган зотлар — ана ўшалар ҳақиқий мўминлар бўлиб, улар учун мағфират ва улуғ ризқ бордир'}, {'chapter': 8, 'verse': 75, 'text': 'وَٱلَّذِينَ ءَامَنُواْ مِنۢ بَعۡدُ وَهَاجَرُواْ وَجَٰهَدُواْ مَعَكُمۡ فَأُوْلَـٰٓئِكَ مِنكُمۡۚ وَأُوْلُواْ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ ٱللَّهِۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمُۢ\n(Сизлардан) кейин иймон келтириб ҳижрат қилган ва сизлар билан бирга курашган зотлар — ана ўшалар сизлардандир. Аллоҳнинг Китобида қон-қариндошлар бир-бирларига (меросхўр бўлишга) ҳақдорроқдирлар. Албатта, Аллоҳ ҳамма нарсани билгувчидир'}, {'chapter': 9, 'verse': 1, 'text': 'بَرَآءَةٞ مِّنَ ٱللَّهِ وَرَسُولِهِۦٓ إِلَى ٱلَّذِينَ عَٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِينَ\n(Эй мўминлар, ушбу эълон) Аллоҳ ва Унинг пайғамбари томонидан сизлар (ўзаро урушмаслик ҳақида) аҳд-паймон қилган мушрик кимсаларга ора очиқлик (эълонидир)'}, {'chapter': 9, 'verse': 2, 'text': 'فَسِيحُواْ فِي ٱلۡأَرۡضِ أَرۡبَعَةَ أَشۡهُرٖ وَٱعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي ٱللَّهِ وَأَنَّ ٱللَّهَ مُخۡزِي ٱلۡكَٰفِرِينَ\n(Эй мушриклар, уруш ҳаром қилинган) тўрт ой ичида Ерда сайру саёҳат қилиб юраверинглар ва билингларки, сизлар Аллоҳни ожиз қилувчи эмассизлар — албатта Аллоҳ кофирларни расво қилгувчидир'}, {'chapter': 9, 'verse': 3, 'text': 'وَأَذَٰنٞ مِّنَ ٱللَّهِ وَرَسُولِهِۦٓ إِلَى ٱلنَّاسِ يَوۡمَ ٱلۡحَجِّ ٱلۡأَكۡبَرِ أَنَّ ٱللَّهَ بَرِيٓءٞ مِّنَ ٱلۡمُشۡرِكِينَ وَرَسُولُهُۥۚ فَإِن تُبۡتُمۡ فَهُوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَوَلَّيۡتُمۡ فَٱعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي ٱللَّهِۗ وَبَشِّرِ ٱلَّذِينَ كَفَرُواْ بِعَذَابٍ أَلِيمٍ\nВа (ушбу эълон) Аллоҳ ва Унинг пайғамбари томонидан одамларга катта ҳаж кунида (яъни, Қурбон ҳайити кунида) Аллоҳ ва Унинг пайғамбари мушриклардан безор эканлигини билдиришдир. Бас, (эй мушриклар), агар тавба қилсангизлар, бу ўзингиз учун яхшироқдир. Агар юз ўгирсангизлар, у ҳолда билингизки, сизлар Аллоҳни ожиз қилувчи эмассиз. (Эй Муҳаммад алайҳис-салоту вас-салом), кофир бўлган кимсаларга аламли азоб «хушхабар»ини етказинг'}, {'chapter': 9, 'verse': 4, 'text': 'إِلَّا ٱلَّذِينَ عَٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِينَ ثُمَّ لَمۡ يَنقُصُوكُمۡ شَيۡـٔٗا وَلَمۡ يُظَٰهِرُواْ عَلَيۡكُمۡ أَحَدٗا فَأَتِمُّوٓاْ إِلَيۡهِمۡ عَهۡدَهُمۡ إِلَىٰ مُدَّتِهِمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ\nМагар (эй мўминлар), сизлар аҳд-паймон қилган мушрик кимсалар кейин (аҳдларидан) бирон нарсани бузмасалар ва сизларнинг зиёнингизга биров билан ҳамкорлик қилмасалар, у ҳолда уларнинг аҳдларини белгиланган муддатгача тўла-тўкис етказинглар! Албатта, Аллоҳ тақво қилгувчи зотларни севар'}, {'chapter': 9, 'verse': 5, 'text': 'فَإِذَا ٱنسَلَخَ ٱلۡأَشۡهُرُ ٱلۡحُرُمُ فَٱقۡتُلُواْ ٱلۡمُشۡرِكِينَ حَيۡثُ وَجَدتُّمُوهُمۡ وَخُذُوهُمۡ وَٱحۡصُرُوهُمۡ وَٱقۡعُدُواْ لَهُمۡ كُلَّ مَرۡصَدٖۚ فَإِن تَابُواْ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ فَخَلُّواْ سَبِيلَهُمۡۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nБас, қачон уруш ҳаром қилинган ойлар чиқса, мушрикларни топган жойингизда ўлдирингиз, (асир) олингиз, қамал қилингиз ва барча йўлларда уларни кузатиб турингиз! Энди агар тавба қилсалар ва намозни тўкис адо қилишиб, закотни берсалар, уларнинг йўлларини тўсмангиз! (яъни, улар билан урушишни бас қилингиз). Албатта, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 9, 'verse': 6, 'text': 'وَإِنۡ أَحَدٞ مِّنَ ٱلۡمُشۡرِكِينَ ٱسۡتَجَارَكَ فَأَجِرۡهُ حَتَّىٰ يَسۡمَعَ كَلَٰمَ ٱللَّهِ ثُمَّ أَبۡلِغۡهُ مَأۡمَنَهُۥۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар мушриклардан биронтаси сиздан ҳимоя сўраса, бас, уни ҳимоя қилинг, токи у Аллоҳнинг каломини эшитсин. Сўнг уни ўзи учун тинч бўлган жойга етказиб қўйинг. Бу (ҳукм) уларнинг билмайдиган қавм бўлганлари учундир'}, {'chapter': 9, 'verse': 7, 'text': 'كَيۡفَ يَكُونُ لِلۡمُشۡرِكِينَ عَهۡدٌ عِندَ ٱللَّهِ وَعِندَ رَسُولِهِۦٓ إِلَّا ٱلَّذِينَ عَٰهَدتُّمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ فَمَا ٱسۡتَقَٰمُواْ لَكُمۡ فَٱسۡتَقِيمُواْ لَهُمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ\nАллоҳ наздида ва Унинг пайғамбари наздида мушриклар учун қандай аҳд-паймон бўлсин?! (Яъни, ҳеч қандай аҳд бўлиши мумкин эмас, чунки улар Аллоҳга ҳам, Унинг пайғамбарига ҳам иймон келтирмайдилар, демак, берган ваъдаларини бузаверадилар), магар сизлар Масжидул-Ҳаром олдида (яъни, Маккада) аҳдлашган кимсалар борки, улар модомики аҳдларида барқарор турар эканлар, сизлар ҳам аҳдларингизда турингиз! Албатта, Аллоҳ тақво қилгувчи зотларни севар'}, {'chapter': 9, 'verse': 8, 'text': 'كَيۡفَ وَإِن يَظۡهَرُواْ عَلَيۡكُمۡ لَا يَرۡقُبُواْ فِيكُمۡ إِلّٗا وَلَا ذِمَّةٗۚ يُرۡضُونَكُم بِأَفۡوَٰهِهِمۡ وَتَأۡبَىٰ قُلُوبُهُمۡ وَأَكۡثَرُهُمۡ فَٰسِقُونَ\n(Улар учун) қандай (аҳд-паймон) бўлсин? Ҳолбуки, улар агар устингизда ғолиб бўлсалар, сизларнинг хусусингизда на аҳдга ва на бурчга боқадилар. Оғизларида сизларни рози қилишади-ю, кўнгиллари (аҳдга вафо қилишга) ибо-ор қилади. Уларнинг кўплари итоатсиздир'}, {'chapter': 9, 'verse': 9, 'text': 'ٱشۡتَرَوۡاْ بِـَٔايَٰتِ ٱللَّهِ ثَمَنٗا قَلِيلٗا فَصَدُّواْ عَن سَبِيلِهِۦٓۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ\nУлар Аллоҳнинг оятларини озгина қийматга алмаштириб, (инсонларни) Унинг йўлидан (яъни, динидан) тўсдилар. Дарҳақиқат, улар энг ёмон ишни қилгувчи бўлдилар'}, {'chapter': 9, 'verse': 10, 'text': 'لَا يَرۡقُبُونَ فِي مُؤۡمِنٍ إِلّٗا وَلَا ذِمَّةٗۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُعۡتَدُونَ\nУлар бирон мўмин хусусида на аҳдга ва на бурчга боқадилар. Улар тажовузкор кимсалардир'}, {'chapter': 9, 'verse': 11, 'text': 'فَإِن تَابُواْ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ فَإِخۡوَٰنُكُمۡ فِي ٱلدِّينِۗ وَنُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nЭнди агар тавба қилсалар ва намозни тўкис адо этишиб, закотни (ҳақдорларга) ато қилсалар, у ҳолда диний биродарингиздирлар. Биз оятларимизни биладиган қавм учун муфассал баён қилурмиз'}, {'chapter': 9, 'verse': 12, 'text': 'وَإِن نَّكَثُوٓاْ أَيۡمَٰنَهُم مِّنۢ بَعۡدِ عَهۡدِهِمۡ وَطَعَنُواْ فِي دِينِكُمۡ فَقَٰتِلُوٓاْ أَئِمَّةَ ٱلۡكُفۡرِ إِنَّهُمۡ لَآ أَيۡمَٰنَ لَهُمۡ لَعَلَّهُمۡ يَنتَهُونَ\nАгар аҳдлашганларидан кейин қасамларини бузсалар ва сизларнинг динингизга тош отсалар, у ҳолда (бу кирдикорларидан) тўхташлари учун куфр етакчиларига қарши жанг қилингиз! Зеро, улар учун ҳеч қандай қасам йўқдир. (Яъни, улар мудом ўзлари ичган қасамларни бузаверадилар)'}, {'chapter': 9, 'verse': 13, 'text': 'أَلَا تُقَٰتِلُونَ قَوۡمٗا نَّكَثُوٓاْ أَيۡمَٰنَهُمۡ وَهَمُّواْ بِإِخۡرَاجِ ٱلرَّسُولِ وَهُم بَدَءُوكُمۡ أَوَّلَ مَرَّةٍۚ أَتَخۡشَوۡنَهُمۡۚ فَٱللَّهُ أَحَقُّ أَن تَخۡشَوۡهُ إِن كُنتُم مُّؤۡمِنِينَ\n(Эй мўминлар), қасамларини бузган ва пайғамбарни (Маккадан) чиқариб юборишга қасд қилган ҳамда ўзлари аввал-бошда сизларга қарши (жанг қилишни) бошлаган кимсалар билан жанг қилмайсизларми?! Улардан қўрқасизларми?! Агар (ҳақиқий) мўмин бўлсангизлар, қўрқишингизга лойиқроқ зот Аллоҳ-ку, ахир'}, {'chapter': 9, 'verse': 14, 'text': 'قَٰتِلُوهُمۡ يُعَذِّبۡهُمُ ٱللَّهُ بِأَيۡدِيكُمۡ وَيُخۡزِهِمۡ وَيَنصُرۡكُمۡ عَلَيۡهِمۡ وَيَشۡفِ صُدُورَ قَوۡمٖ مُّؤۡمِنِينَ\nУлар билан жанг қилинглар, (шунда) Аллоҳ уларни сизларнинг қўлингиз билан азоблайди, расво қилади ва сизларни уларнинг устига ғолиб қилади ҳамда мўмин қавм дилларини қондиради'}, {'chapter': 9, 'verse': 15, 'text': 'وَيُذۡهِبۡ غَيۡظَ قُلُوبِهِمۡۗ وَيَتُوبُ ٱللَّهُ عَلَىٰ مَن يَشَآءُۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nВа кўнгилларидаги ғам-андуҳни кетказади. Аллоҳ Ўзи хоҳлаган кишиларнинг тавбаларини қабул қилур. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 9, 'verse': 16, 'text': 'أَمۡ حَسِبۡتُمۡ أَن تُتۡرَكُواْ وَلَمَّا يَعۡلَمِ ٱللَّهُ ٱلَّذِينَ جَٰهَدُواْ مِنكُمۡ وَلَمۡ يَتَّخِذُواْ مِن دُونِ ٱللَّهِ وَلَا رَسُولِهِۦ وَلَا ٱلۡمُؤۡمِنِينَ وَلِيجَةٗۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\n(Эй мўминлар), балки сизларнинг орангизда (ҳақ йўлда) курашган ва Аллоҳдан, Унинг пайғамбаридан ва мўминлардан ўзга бирон кимсани дўст тутмаган зотларни, Аллоҳ билмасдан (яъни, имтиҳон қилмасдан ўз ҳолига) қўйиб қўйишини ўйлагандирсизлар?! Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 9, 'verse': 17, 'text': 'مَا كَانَ لِلۡمُشۡرِكِينَ أَن يَعۡمُرُواْ مَسَٰجِدَ ٱللَّهِ شَٰهِدِينَ عَلَىٰٓ أَنفُسِهِم بِٱلۡكُفۡرِۚ أُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ وَفِي ٱلنَّارِ هُمۡ خَٰلِدُونَ\nМушриклар ўзларининг кофир эканликларига гувоҳ бўлган ҳолларида Аллоҳнинг масжидларини обод қилишлари жоиз эмас. Уларнинг қилган амаллари беҳуда кетар, ўзлари эса дўзахда абадий қолгувчидирлар'}, {'chapter': 9, 'verse': 18, 'text': 'إِنَّمَا يَعۡمُرُ مَسَٰجِدَ ٱللَّهِ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَلَمۡ يَخۡشَ إِلَّا ٱللَّهَۖ فَعَسَىٰٓ أُوْلَـٰٓئِكَ أَن يَكُونُواْ مِنَ ٱلۡمُهۡتَدِينَ\nАллоҳнинг масжидларини фақат Аллоҳга ва Охират кунига иймон келтирган, намозни тўкис адо этган, закотни (ҳақдорларга) ато қилган ва ёлғиз Аллоҳдангина қўрқадиган зотларгина обод қилурлар. Ана ўшалар ҳидоят топгувчи зотлар бўлсалар, ажабмас'}, {'chapter': 9, 'verse': 19, 'text': '۞أَجَعَلۡتُمۡ سِقَايَةَ ٱلۡحَآجِّ وَعِمَارَةَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ كَمَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَجَٰهَدَ فِي سَبِيلِ ٱللَّهِۚ لَا يَسۡتَوُۥنَ عِندَ ٱللَّهِۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\n(Эй мушриклар), сизлар ҳожиларга сув беришни ва Масжидул-Ҳаромни обод қилишни — Аллоҳга ва Охират кунига иймон келтирган ҳамда Аллоҳ йўлида курашган зотлар(нинг амалларига) тенг, деб билдингизми?! Улар (яъни, мўминлар билан мушриклар) Аллоҳ наздида тенг эмаслар, Аллоҳ золим қавмни ҳидоят қилмас'}, {'chapter': 9, 'verse': 20, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ أَعۡظَمُ دَرَجَةً عِندَ ٱللَّهِۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ\nИймон келтирган, ҳижрат қилган ва Аллоҳ йўлида молу жонлари билан курашган зотларнинг Аллоҳ наздидаги даражалари жуда улуғдир ва ўшалар (бахт-саодатга) эришувчидирлар'}, {'chapter': 9, 'verse': 21, 'text': 'يُبَشِّرُهُمۡ رَبُّهُم بِرَحۡمَةٖ مِّنۡهُ وَرِضۡوَٰنٖ وَجَنَّـٰتٖ لَّهُمۡ فِيهَا نَعِيمٞ مُّقِيمٌ\nПарвардигорлари уларга Ўз тарафидан раҳмат ва ризолик ҳамда улар учун бўладиган жаннатлар хушхабарини берурки, у жаннатларда доимий неъматлар бордир'}, {'chapter': 9, 'verse': 22, 'text': 'خَٰلِدِينَ فِيهَآ أَبَدًاۚ إِنَّ ٱللَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ\nУлар ўша жойларда абадий қолурлар. Дарҳақиқат, фақат Аллоҳнинг ҳузуридагина улуғ ажр бордир'}, {'chapter': 9, 'verse': 23, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُوٓاْ ءَابَآءَكُمۡ وَإِخۡوَٰنَكُمۡ أَوۡلِيَآءَ إِنِ ٱسۡتَحَبُّواْ ٱلۡكُفۡرَ عَلَى ٱلۡإِيمَٰنِۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nЭй мўминлар, агар иймондан куфрни афзал билсалар, ота-оналарингиз ва ака-укаларингизни (ҳам) дўст тутмангиз! Сизларнинг ичингизда кимда-ким уларни дўст тутса, (яъни, уларни деб иймондан куфрга қайтса) бас, улар золимлардир'}, {'chapter': 9, 'verse': 24, 'text': 'قُلۡ إِن كَانَ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ وَإِخۡوَٰنُكُمۡ وَأَزۡوَٰجُكُمۡ وَعَشِيرَتُكُمۡ وَأَمۡوَٰلٌ ٱقۡتَرَفۡتُمُوهَا وَتِجَٰرَةٞ تَخۡشَوۡنَ كَسَادَهَا وَمَسَٰكِنُ تَرۡضَوۡنَهَآ أَحَبَّ إِلَيۡكُم مِّنَ ٱللَّهِ وَرَسُولِهِۦ وَجِهَادٖ فِي سَبِيلِهِۦ فَتَرَبَّصُواْ حَتَّىٰ يَأۡتِيَ ٱللَّهُ بِأَمۡرِهِۦۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар ота-оналарингиз, болаларингиз, ака-укаларингиз, жуфтларингиз, қариндош-уруғларингиз ва касб қилиб топган мол-дунёларингиз, касод бўлиб қолишидан қўрқадиган тижоратларингиз ҳамда яхши кўрадиган уй-жойларингиз сизларга Аллоҳдан, Унинг пайғамбаридан ва Унинг йўлида жиҳод қилишдан суюклироқ бўлса, у ҳолда то Аллоҳ ўз амрини (яъни, азобини) келтирганича кутиб тураверинглар. Аллоҳ итоатсиз қавмни ҳидоят қилмас'}, {'chapter': 9, 'verse': 25, 'text': 'لَقَدۡ نَصَرَكُمُ ٱللَّهُ فِي مَوَاطِنَ كَثِيرَةٖ وَيَوۡمَ حُنَيۡنٍ إِذۡ أَعۡجَبَتۡكُمۡ كَثۡرَتُكُمۡ فَلَمۡ تُغۡنِ عَنكُمۡ شَيۡـٔٗا وَضَاقَتۡ عَلَيۡكُمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ ثُمَّ وَلَّيۡتُم مُّدۡبِرِينَ\nАллоҳ сизларни кўп ўринларда ғолиб қилди. Ҳунайн (Макка билан Тоиф ўртасидаги бир водий) кунини (эсланглар)! Ўшанда сизларни кўп эканлигингиз мағрур қилиб қўйган эди, аммо у (яъни, саноғингизнинг кўплиги) сизларни ҳеч нарсадан беҳожат қила олмади (қутқариб олмади) ва сизларга кенг ер торлик қилиб қолди, сўнг юз ўгирган ҳолингизда чекиндингиз'}, {'chapter': 9, 'verse': 26, 'text': 'ثُمَّ أَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَنزَلَ جُنُودٗا لَّمۡ تَرَوۡهَا وَعَذَّبَ ٱلَّذِينَ كَفَرُواْۚ وَذَٰلِكَ جَزَآءُ ٱلۡكَٰفِرِينَ\nКейин Аллоҳ пайғамбарига ва мўминларга Ўз томонидан хотиржамлик нозил қилди ҳамда сизлар кўрмаган бир лашкарни (яъни, фаришталарни) туширди ва кофир бўлган кимсаларни азобга дучор қилди. Кофирларнинг жазоси шудир'}, {'chapter': 9, 'verse': 27, 'text': 'ثُمَّ يَتُوبُ ٱللَّهُ مِنۢ بَعۡدِ ذَٰلِكَ عَلَىٰ مَن يَشَآءُۗ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nКейин — ўша (жангдан) сўнг Аллоҳ Ўзи хоҳлаган кишиларнинг тавбаларини қабул қилур. Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 9, 'verse': 28, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّمَا ٱلۡمُشۡرِكُونَ نَجَسٞ فَلَا يَقۡرَبُواْ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ بَعۡدَ عَامِهِمۡ هَٰذَاۚ وَإِنۡ خِفۡتُمۡ عَيۡلَةٗ فَسَوۡفَ يُغۡنِيكُمُ ٱللَّهُ مِن فَضۡلِهِۦٓ إِن شَآءَۚ إِنَّ ٱللَّهَ عَلِيمٌ حَكِيمٞ\nЭй мўминлар, ҳеч шак-шубҳасиз, мушриклар нопок кимсалардир, бас, (улар) бу йилдан сўнг Масжидул-Ҳаромга яқин келмасинлар! Агар сизлар (мушриклар ҳаж мавсумида Маккага келтирадиган озиқ-овқатлар ва бошқа нарсалар тўхтаб қолиши сабабли) камбағалликдан қўрқсангизлар, (билингки) яқинда (Аллоҳ) хоҳласа, Ўз фазлу карами билан сизларни бой-бадавлат қилажак. Албатта, Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 9, 'verse': 29, 'text': 'قَٰتِلُواْ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡيَوۡمِ ٱلۡأٓخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ ٱللَّهُ وَرَسُولُهُۥ وَلَا يَدِينُونَ دِينَ ٱلۡحَقِّ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ حَتَّىٰ يُعۡطُواْ ٱلۡجِزۡيَةَ عَن يَدٖ وَهُمۡ صَٰغِرُونَ\nАллоҳга ва Охират кунига ишонмайдиган, Аллоҳ ва Унинг пайғамбари ҳаром деган нарсани ҳаром деб билмайдиган, Ҳақ (яъни, Ислом) динига эътиқод қилмайдиган аҳли китоблардан иборат бўлган кимсаларга қарши то улар хорланган (мағлуб) ҳолларида ўз қўллари билан (зиммаларидаги) солиқни тўламагунларича, жанг қилингиз'}, {'chapter': 9, 'verse': 30, 'text': 'وَقَالَتِ ٱلۡيَهُودُ عُزَيۡرٌ ٱبۡنُ ٱللَّهِ وَقَالَتِ ٱلنَّصَٰرَى ٱلۡمَسِيحُ ٱبۡنُ ٱللَّهِۖ ذَٰلِكَ قَوۡلُهُم بِأَفۡوَٰهِهِمۡۖ يُضَٰهِـُٔونَ قَوۡلَ ٱلَّذِينَ كَفَرُواْ مِن قَبۡلُۚ قَٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ يُؤۡفَكُونَ\nЯҳудийлар: «Узайр Аллоҳнинг ўғли», дедилар. Насронийлар: «(Ийсо) Масиҳ Аллоҳнинг ўғли», дедилар. Бу уларнинг оғизларидаги (ҳужжат-далилсиз) гапларидир. Уларнинг бу гаплари худди аввалги кофир бўлган кимсаларнинг гапига ўхшайди. Уларни Аллоҳ лаънатлагай! Қандай адашмоқдалар-а'}, {'chapter': 9, 'verse': 31, 'text': 'ٱتَّخَذُوٓاْ أَحۡبَارَهُمۡ وَرُهۡبَٰنَهُمۡ أَرۡبَابٗا مِّن دُونِ ٱللَّهِ وَٱلۡمَسِيحَ ٱبۡنَ مَرۡيَمَ وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُوٓاْ إِلَٰهٗا وَٰحِدٗاۖ لَّآ إِلَٰهَ إِلَّا هُوَۚ سُبۡحَٰنَهُۥ عَمَّا يُشۡرِكُونَ\nУлар Аллоҳни қўйиб ўзларининг донишмандларини ва роҳибларини ҳамда Масиҳ бинни Марямни Парвардигор деб билдилар. Ҳолбуки, фақат ягона Аллоҳга бандалик қилишга маъмур (буюрилган) эдилар. Ҳеч илоҳ йўқ, фақат Унинг Ўзи бордир. У зот уларнинг ширкларидан покдир'}, {'chapter': 9, 'verse': 32, 'text': 'يُرِيدُونَ أَن يُطۡفِـُٔواْ نُورَ ٱللَّهِ بِأَفۡوَٰهِهِمۡ وَيَأۡبَى ٱللَّهُ إِلَّآ أَن يُتِمَّ نُورَهُۥ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ\nУлар Аллоҳнинг Нурини (яъни, Исломни) оғизлари (яъни, беҳуда гаплари) билан ўчирмоқчи бўладилар. Аллоҳ эса, гарчи (кофирлар) ёмон кўрсалар-да, фақат Ўз нурини комил қилишни (яъни, ҳар тарафга ёйишни) истайди'}, {'chapter': 9, 'verse': 33, 'text': 'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ\nУ (Аллоҳ) Ўз пайғамбарини ҳидоят ва ҳақ дин билан — гарчи мушриклар хоҳламасалар-да — барча динларга ғолиб қилиш учун юборган Зотдир'}, {'chapter': 9, 'verse': 34, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ كَثِيرٗا مِّنَ ٱلۡأَحۡبَارِ وَٱلرُّهۡبَانِ لَيَأۡكُلُونَ أَمۡوَٰلَ ٱلنَّاسِ بِٱلۡبَٰطِلِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِۗ وَٱلَّذِينَ يَكۡنِزُونَ ٱلذَّهَبَ وَٱلۡفِضَّةَ وَلَا يُنفِقُونَهَا فِي سَبِيلِ ٱللَّهِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٖ\nЭй мўминлар, ўша донишмандлар, роҳиблардан кўпчилиги одамларнинг молларини ноҳақ (йўл) билан ейдилар ва Аллоҳнинг йўлидан тўсадилар. Олтин-кумушни босиб, уни Аллоҳ йўлида инфоқ-эҳсон қилмайдиган кимсаларга аламли азоб «хушхабарини» етказинг'}, {'chapter': 9, 'verse': 35, 'text': 'يَوۡمَ يُحۡمَىٰ عَلَيۡهَا فِي نَارِ جَهَنَّمَ فَتُكۡوَىٰ بِهَا جِبَاهُهُمۡ وَجُنُوبُهُمۡ وَظُهُورُهُمۡۖ هَٰذَا مَا كَنَزۡتُمۡ لِأَنفُسِكُمۡ فَذُوقُواْ مَا كُنتُمۡ تَكۡنِزُونَ\nУ кунда (Қиёматда) ўша (олтин-кумушни) жаҳаннам ўтида қизитилиб, ўша билан уларнинг пешоналари, ёнлари ва кетларига тамға босилиб: «Мана бу ўзларингиз учун тўплаган нарсаларингиздир. Энди тўплаб-босган нарсаларингизнинг мазасини тотиб кўринглар» (дейилур)'}, {'chapter': 9, 'verse': 36, 'text': 'إِنَّ عِدَّةَ ٱلشُّهُورِ عِندَ ٱللَّهِ ٱثۡنَا عَشَرَ شَهۡرٗا فِي كِتَٰبِ ٱللَّهِ يَوۡمَ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ مِنۡهَآ أَرۡبَعَةٌ حُرُمٞۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُۚ فَلَا تَظۡلِمُواْ فِيهِنَّ أَنفُسَكُمۡۚ وَقَٰتِلُواْ ٱلۡمُشۡرِكِينَ كَآفَّةٗ كَمَا يُقَٰتِلُونَكُمۡ كَآفَّةٗۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ\nАлбатта, ойларнинг саноғи Аллоҳнинг наздида, Аллоҳ осмонлар ва Ерни яратган кунида белгилаб қўйганидек, ўн икки ойдир. Улардан тўрти (уруш) ҳаром қилинган (ойлар)дир. Мана шу ҳақ диндир (яъни, ҳақ ҳукмдир). Бас, у ойларда (урушни ҳалол деб жангга кириб) ўзларингизга зулм қилмангиз! Барча мушриклар (бу ҳукмга қарамай) сизларга қарши жанг қилгани каби (биринчи бўлиб жанг бошласалар), сизлар ҳам барчангиз уларга қарши жанг қилингиз! Ва билингизки, Аллоҳ тақводорлар билан биргадир'}, {'chapter': 9, 'verse': 37, 'text': 'إِنَّمَا ٱلنَّسِيٓءُ زِيَادَةٞ فِي ٱلۡكُفۡرِۖ يُضَلُّ بِهِ ٱلَّذِينَ كَفَرُواْ يُحِلُّونَهُۥ عَامٗا وَيُحَرِّمُونَهُۥ عَامٗا لِّيُوَاطِـُٔواْ عِدَّةَ مَا حَرَّمَ ٱللَّهُ فَيُحِلُّواْ مَا حَرَّمَ ٱللَّهُۚ زُيِّنَ لَهُمۡ سُوٓءُ أَعۡمَٰلِهِمۡۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nШак-шубҳасиз, (ҳаром ойларни) кетга суриш ортиқ даражадаги кофирликдирки, у сабабли кофирлар бутунлай йўлдан тойилурлар. Улар Аллоҳ ҳаром қилган ойларнинг саноғини (тўрт ойга) тўғрилаб, Аллоҳ ҳаром қилган нарсани ҳалол қилиб олиш учун уни (яъни, уруш ҳаром қилинган ойларни) бир йил ҳалол десалар, яна бир йил ҳаром дейдилар. Уларга бу хунук ишлари чиройли кўрсатиб қўйилди. (Чунки) Аллоҳ кофир қавмни ҳидоят қилмас'}, {'chapter': 9, 'verse': 38, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ مَا لَكُمۡ إِذَا قِيلَ لَكُمُ ٱنفِرُواْ فِي سَبِيلِ ٱللَّهِ ٱثَّاقَلۡتُمۡ إِلَى ٱلۡأَرۡضِۚ أَرَضِيتُم بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا مِنَ ٱلۡأٓخِرَةِۚ فَمَا مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فِي ٱلۡأٓخِرَةِ إِلَّا قَلِيلٌ\nЭй мўминлар, сизларга нима бўлдики, Аллоҳ йўлида (жиҳодга) чиқинглар, дейилса, ўз ерингизга (яъни, юртингизга) ёпишиб олдингиз?! Охират (неъматлари)дан юз ўгириб, ҳаёти дунё (лаззатлари)га рози бўлурмисиз?! Охират олдида бу дунё матоси жуда оз нарса-ку'}, {'chapter': 9, 'verse': 39, 'text': 'إِلَّا تَنفِرُواْ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا وَيَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّوهُ شَيۡـٔٗاۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nАгар (жиҳодга) чиқмасангизлар, (Аллоҳ) сизларни аламли азоб билан азоблар ва ўрнингизга бошқа бир қавмни келтирур. Сизлар эса У зотга бирон зиён етказа олмайсиз. Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 9, 'verse': 40, 'text': 'إِلَّا تَنصُرُوهُ فَقَدۡ نَصَرَهُ ٱللَّهُ إِذۡ أَخۡرَجَهُ ٱلَّذِينَ كَفَرُواْ ثَانِيَ ٱثۡنَيۡنِ إِذۡ هُمَا فِي ٱلۡغَارِ إِذۡ يَقُولُ لِصَٰحِبِهِۦ لَا تَحۡزَنۡ إِنَّ ٱللَّهَ مَعَنَاۖ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَيۡهِ وَأَيَّدَهُۥ بِجُنُودٖ لَّمۡ تَرَوۡهَا وَجَعَلَ كَلِمَةَ ٱلَّذِينَ كَفَرُواْ ٱلسُّفۡلَىٰۗ وَكَلِمَةُ ٱللَّهِ هِيَ ٱلۡعُلۡيَاۗ وَٱللَّهُ عَزِيزٌ حَكِيمٌ\nАгар сизлар унга (яъни, пайғамбарга) ёрдам қилмасангиз (Аллоҳнинг Ўзи унга ёрдам қилур). Уни кофирлар икки кишининг бири бўлган ҳолида (яъни, бир ҳамроҳи билан Маккадан) ҳайдаб чиқарганларида, унга Аллоҳ ёрдам берди-ку. Ўшанда икковлон ғорда бўлган пайтларида ҳамроҳига: «Ғамгин бўлма, шубҳасиз, Аллоҳ биз билан биргадир», дер экан, Аллоҳ унинг устига хотиржамлик туширди ва уни сизлар кўрмаган лашкарлар (яъни, фаришталар) билан қўллаб-қувватлади ҳамда кофир бўлган кимсаларнинг сўзларини тубан қилиб қўйди, Аллоҳнинг сўзигина юксак сўздир. Аллоҳ қудратли, ҳикматлидир'}, {'chapter': 9, 'verse': 41, 'text': 'ٱنفِرُواْ خِفَافٗا وَثِقَالٗا وَجَٰهِدُواْ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ فِي سَبِيلِ ٱللَّهِۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n(Эй мўминлар), хоҳ енгил, хоҳ оғир ҳолингизда (яъни, истасангиз, истамасангиз жиҳодга) чиқингиз ва молу жонларингиз билан Аллоҳ йўлида курашингиз! Агар билсангизлар, мана шу ўзларингиз учун яхшироқдир'}, {'chapter': 9, 'verse': 42, 'text': 'لَوۡ كَانَ عَرَضٗا قَرِيبٗا وَسَفَرٗا قَاصِدٗا لَّٱتَّبَعُوكَ وَلَٰكِنۢ بَعُدَتۡ عَلَيۡهِمُ ٱلشُّقَّةُۚ وَسَيَحۡلِفُونَ بِٱللَّهِ لَوِ ٱسۡتَطَعۡنَا لَخَرَجۡنَا مَعَكُمۡ يُهۡلِكُونَ أَنفُسَهُمۡ وَٱللَّهُ يَعۡلَمُ إِنَّهُمۡ لَكَٰذِبُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар (сиз даъват қилаётган жиҳод) осон фойда ва яқин сафар бўлганида, улар (яъни, мунофиқлар) албатта сизга эргашган бўлур эдилар. Лекин уларга масофа узоқ бўлиб кўринди (шунинг учун чиқмадилар). Ҳали улар (ёлғон қасам билан) ўзларини ҳалок қилиб: «Агар қодир бўлганимизда сизлар билан бирга чиққан бўлур эдик», деб қасам ҳам ичадилар. Аллоҳ эса уларнинг ҳеч шак-шубҳасиз, ёлғончи эканликларини билур'}, {'chapter': 9, 'verse': 43, 'text': 'عَفَا ٱللَّهُ عَنكَ لِمَ أَذِنتَ لَهُمۡ حَتَّىٰ يَتَبَيَّنَ لَكَ ٱلَّذِينَ صَدَقُواْ وَتَعۡلَمَ ٱلۡكَٰذِبِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ сизни афв қилди. (Лекин) нима учун то сизга ростгўй кишилар аниқ бўлиб, ёлғончиларни ҳам аниқ билгунингизча, (сабр қилиб турмай, уларнинг ёлғон қасамларига ишониб) уларга (жиҳодга чиқмаслик учун) изн бердингиз'}, {'chapter': 9, 'verse': 44, 'text': 'لَا يَسۡتَـٔۡذِنُكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۗ وَٱللَّهُ عَلِيمُۢ بِٱلۡمُتَّقِينَ\nАллоҳга ва Охират кунига ишонган зотлар сиздан молу жонлари билан жиҳод қилишдан (қолиш учун) изн сўрамайдилар. Аллоҳ тақводорларни билгувчидир'}, {'chapter': 9, 'verse': 45, 'text': 'إِنَّمَا يَسۡتَـٔۡذِنُكَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَٱرۡتَابَتۡ قُلُوبُهُمۡ فَهُمۡ فِي رَيۡبِهِمۡ يَتَرَدَّدُونَ\nБалки фақат Аллоҳга ва Охират кунига ишонмайдиган ва кўнгилларида шак-шубҳа бўлган кимсаларгина сиздан (қолиш учун) изн сўрайдилар. Чунки улар ўз шубҳаларида иккиланиб юрадилар'}, {'chapter': 9, 'verse': 46, 'text': '۞وَلَوۡ أَرَادُواْ ٱلۡخُرُوجَ لَأَعَدُّواْ لَهُۥ عُدَّةٗ وَلَٰكِن كَرِهَ ٱللَّهُ ٱنۢبِعَاثَهُمۡ فَثَبَّطَهُمۡ وَقِيلَ ٱقۡعُدُواْ مَعَ ٱلۡقَٰعِدِينَ\nАгар улар (жиҳодга) чиқишни истаганларида унинг учун тайёргарлик кўриб қўйган бўлур эдилар. Лекин Аллоҳ чиқишларини истамай, уларни дангаса қилиб қўйди ва уларга (ўзларидан бўлган кимсалар тарафидан): «Қолганлар (яъни, аёллар, ёш болалар ва ожиз-нотавон кишилар) билан бирга ўтираверинглар», дейилди'}, {'chapter': 9, 'verse': 47, 'text': 'لَوۡ خَرَجُواْ فِيكُم مَّا زَادُوكُمۡ إِلَّا خَبَالٗا وَلَأَوۡضَعُواْ خِلَٰلَكُمۡ يَبۡغُونَكُمُ ٱلۡفِتۡنَةَ وَفِيكُمۡ سَمَّـٰعُونَ لَهُمۡۗ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nАгар улар сизларнинг орангиздан чиққанларида ҳам, сизларга фақат ортиқча зиён бўлган ва ўрталарингизда сизларни фитнага солиш учун югуриб-елиб юрган бўлур эдилар ҳамда ораларингизда уларга қулоқ солувчилар ҳам (топилган) бўлур эди. Аллоҳ золим кимсаларни билгувчидир'}, {'chapter': 9, 'verse': 48, 'text': 'لَقَدِ ٱبۡتَغَوُاْ ٱلۡفِتۡنَةَ مِن قَبۡلُ وَقَلَّبُواْ لَكَ ٱلۡأُمُورَ حَتَّىٰ جَآءَ ٱلۡحَقُّ وَظَهَرَ أَمۡرُ ٱللَّهِ وَهُمۡ كَٰرِهُونَ\nУлар илгари — ҳануз ҳақиқат рўёбга чиқиб, Аллоҳнинг дини ғолиб бўлмай туриб ҳам (буни) ёмон кўрган ҳолларида фитна қилмоқчи бўлган ва сизнинг ишларингизни ортга кеткизмоқчи бўлган эдилар-ку'}, {'chapter': 9, 'verse': 49, 'text': 'وَمِنۡهُم مَّن يَقُولُ ٱئۡذَن لِّي وَلَا تَفۡتِنِّيٓۚ أَلَا فِي ٱلۡفِتۡنَةِ سَقَطُواْۗ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِٱلۡكَٰفِرِينَ\nУларнинг орасида: «Менга (жангдан қолиш учун) изн бергин, мени фитнага солмагин», дейдиган кимсалар ҳам бордир. Огоҳ бўлингизким, улар (жангдан қолишлари сабабли) фитнанинг ўзига тушдилар. Шак-шубҳа йўқки, жаҳаннам кофирларии ўраб олгувчидир'}, {'chapter': 9, 'verse': 50, 'text': 'إِن تُصِبۡكَ حَسَنَةٞ تَسُؤۡهُمۡۖ وَإِن تُصِبۡكَ مُصِيبَةٞ يَقُولُواْ قَدۡ أَخَذۡنَآ أَمۡرَنَا مِن قَبۡلُ وَيَتَوَلَّواْ وَّهُمۡ فَرِحُونَ\nАгар сизга бирон яхшилик (ғалаба ёки ўлжа) етса, бу уларни хафа қилур. Агар сизга (Уҳуд жангида бўлганидек) бирон мусибат етса: «Ишнинг олдини олибмиз», дейишиб, хурсанд бўлган ҳолларида юз ўгириб кетурлар'}, {'chapter': 9, 'verse': 51, 'text': 'قُل لَّن يُصِيبَنَآ إِلَّا مَا كَتَبَ ٱللَّهُ لَنَا هُوَ مَوۡلَىٰنَاۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nАйтинг: «Бизга фақат Аллоҳ биз учун ёзиб қўйган нарсагина етур. У бизнинг Хожамиздир. Бас, иймонли кишилар фақат Аллоҳгагина суянсинлар!»'}, {'chapter': 9, 'verse': 52, 'text': 'قُلۡ هَلۡ تَرَبَّصُونَ بِنَآ إِلَّآ إِحۡدَى ٱلۡحُسۡنَيَيۡنِۖ وَنَحۡنُ نَتَرَبَّصُ بِكُمۡ أَن يُصِيبَكُمُ ٱللَّهُ بِعَذَابٖ مِّنۡ عِندِهِۦٓ أَوۡ بِأَيۡدِينَاۖ فَتَرَبَّصُوٓاْ إِنَّا مَعَكُم مُّتَرَبِّصُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Сизлар биз учун фақат икки яхшилик (яъни, ё шаҳид бўлишимиз ёки ғалаба қилишимиз)дан бирини кутмоқдасиз. Биз эса сизлар учун Аллоҳ сизларга Ўз даргоҳидан ёки бизнинг қўлимиз билан бир азоб етказишини кутмоқдамиз. Бас, сизлар ҳам кутаверинглар, биз ҳам сизлар билан бирга кутгувчимиз»'}, {'chapter': 9, 'verse': 53, 'text': 'قُلۡ أَنفِقُواْ طَوۡعًا أَوۡ كَرۡهٗا لَّن يُتَقَبَّلَ مِنكُمۡ إِنَّكُمۡ كُنتُمۡ قَوۡمٗا فَٰسِقِينَ\nАйтинг: «(Эй мунофиқлар), хоҳ ўз хоҳишингиз билан, хоҳ мажбуран инфоқ-эҳсон қилинглар, сизлардан ҳаргиз қабул қилинмас. Чунки сизлар итоатсиз қавм бўлдингиз»'}, {'chapter': 9, 'verse': 54, 'text': 'وَمَا مَنَعَهُمۡ أَن تُقۡبَلَ مِنۡهُمۡ نَفَقَٰتُهُمۡ إِلَّآ أَنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَبِرَسُولِهِۦ وَلَا يَأۡتُونَ ٱلصَّلَوٰةَ إِلَّا وَهُمۡ كُسَالَىٰ وَلَا يُنفِقُونَ إِلَّا وَهُمۡ كَٰرِهُونَ\nҚилган инфоқ-эҳсонлари (Аллоҳ даргоҳида) қабул қилинишидан тўсган нарса фақат уларнинг Аллоҳ ва Унинг пайғамбарига иймон келтирмаганлари, намозга фақат эринган ҳолларидагина келишлари ва ўзлари истамаган ҳолларидагана инфоқ-эҳсон қилганликларидир'}, {'chapter': 9, 'verse': 55, 'text': 'فَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُعَذِّبَهُم بِهَا فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ\nБас, сизни уларнинг молу дунёлари ва бола-чақалари қизиқтирмасин! Чунки Аллоҳ ўша нарсалар сабабли ҳаёти дунёда уларни азоб-уқубатга солишни ва кофир бўлган ҳолларида, жонлари чиқишини истайди, холос'}, {'chapter': 9, 'verse': 56, 'text': 'وَيَحۡلِفُونَ بِٱللَّهِ إِنَّهُمۡ لَمِنكُمۡ وَمَا هُم مِّنكُمۡ وَلَٰكِنَّهُمۡ قَوۡمٞ يَفۡرَقُونَ\nУлар ўзларининг сизлар билан бирга эканликларига Аллоҳ номига қасам ичадилар. Ҳолбуки, улар сизлардан эмасдирлар. Фақат улар қўрқоқ қавмдирлар (яъни, мунофиқликлари маълум бўлиб қолса, бошларига бошқа кофирларнинг куни тушиб қолишидан қўрқадилар)'}, {'chapter': 9, 'verse': 57, 'text': 'لَوۡ يَجِدُونَ مَلۡجَـًٔا أَوۡ مَغَٰرَٰتٍ أَوۡ مُدَّخَلٗا لَّوَلَّوۡاْ إِلَيۡهِ وَهُمۡ يَجۡمَحُونَ\nАгар улар бирон бошпанами, ғорми ёки кирадиган бирон тешикми топсалар эди, чопганларича ўшанга қараб жўнаб қолган бўлур эдилар'}, {'chapter': 9, 'verse': 58, 'text': 'وَمِنۡهُم مَّن يَلۡمِزُكَ فِي ٱلصَّدَقَٰتِ فَإِنۡ أُعۡطُواْ مِنۡهَا رَضُواْ وَإِن لَّمۡ يُعۡطَوۡاْ مِنۡهَآ إِذَا هُمۡ يَسۡخَطُونَ\nУларнинг орасида садақотлар тўғрисида сизга таъна қилгувчи кимсалар ҳам бордир. Бас, агар ўша садақалардан уларга ҳам берилса, рози бўладилар, агар берилмаса, бирдан норози бўлиб қоладилар'}, {'chapter': 9, 'verse': 59, 'text': 'وَلَوۡ أَنَّهُمۡ رَضُواْ مَآ ءَاتَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ وَقَالُواْ حَسۡبُنَا ٱللَّهُ سَيُؤۡتِينَا ٱللَّهُ مِن فَضۡلِهِۦ وَرَسُولُهُۥٓ إِنَّآ إِلَى ٱللَّهِ رَٰغِبُونَ\nАгар улар Аллоҳ ва Унинг пайғамбари уларга берган нарсага рози бўлишиб: «Аллоҳ биз учун кифоя қилур. Яқинда Аллоҳ ва Унинг пайғамбари бизга Ўз фазлу карамидан (мол-дунё ҳам, бахт саодат ҳам) берур, биз Аллоҳнинг Ўзига интилгувчи кишилармиз», десалар эди (ўзлари учун яхши бўлур эди)'}, {'chapter': 9, 'verse': 60, 'text': '۞إِنَّمَا ٱلصَّدَقَٰتُ لِلۡفُقَرَآءِ وَٱلۡمَسَٰكِينِ وَٱلۡعَٰمِلِينَ عَلَيۡهَا وَٱلۡمُؤَلَّفَةِ قُلُوبُهُمۡ وَفِي ٱلرِّقَابِ وَٱلۡغَٰرِمِينَ وَفِي سَبِيلِ ٱللَّهِ وَٱبۡنِ ٱلسَّبِيلِۖ فَرِيضَةٗ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nАлбатта, садақалар (яъни, закотлар) Аллоҳ томонидан фарз бўлган ҳолда, фақат фақирларга, мискинларга, садақа йиғувчиларга, кўнгиллари (Исломга) ошна қилинувчи кишиларга, бўйинларни (қулларни) озод қилишга, қарздор кишиларга ва Аллоҳ йўлида (яъни, жиҳодга ёки ҳажга кетаётганларга) ҳамда йўловчи мусофирларга берилур. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 9, 'verse': 61, 'text': 'وَمِنۡهُمُ ٱلَّذِينَ يُؤۡذُونَ ٱلنَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٞۚ قُلۡ أُذُنُ خَيۡرٖ لَّكُمۡ يُؤۡمِنُ بِٱللَّهِ وَيُؤۡمِنُ لِلۡمُؤۡمِنِينَ وَرَحۡمَةٞ لِّلَّذِينَ ءَامَنُواْ مِنكُمۡۚ وَٱلَّذِينَ يُؤۡذُونَ رَسُولَ ٱللَّهِ لَهُمۡ عَذَابٌ أَلِيمٞ\nУларнинг орасида пайғамбарга озор берадиган ва «у (яъни, пайғамбар) қулоқ тутгувчи (яъни, эшитган нарсасига ишонаверадиган киши)», дейдиган кимсалар бордир. Айтинг: («Ҳа, у) сизлар учун яхшилик бўлган нарсага қулоқ тутгувчи, Аллоҳга иймон келтирувчи, мўминларга ишонадиган ва сизларнинг орангиздаги иймон келтирган зотлар учун раҳмат-марҳамати бўлган зотдир. Аллоҳнинг пайғамбарига озор берувчи кимсалар учун эса аламли азоб бордир»'}, {'chapter': 9, 'verse': 62, 'text': 'يَحۡلِفُونَ بِٱللَّهِ لَكُمۡ لِيُرۡضُوكُمۡ وَٱللَّهُ وَرَسُولُهُۥٓ أَحَقُّ أَن يُرۡضُوهُ إِن كَانُواْ مُؤۡمِنِينَ\n(Эй мўминлар), улар сизларни рози қилиш учун (пайғамбарга озор бермадик, деб) Аллоҳ номига қасам ичадилар. Ҳолбуки, агар мўмин бўлганларида, Аллоҳ ва Унинг пайғамбарини рози қилмоқлари лойиқроқ иш эди'}, {'chapter': 9, 'verse': 63, 'text': 'أَلَمۡ يَعۡلَمُوٓاْ أَنَّهُۥ مَن يُحَادِدِ ٱللَّهَ وَرَسُولَهُۥ فَأَنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدٗا فِيهَاۚ ذَٰلِكَ ٱلۡخِزۡيُ ٱلۡعَظِيمُ\nУлар кимда-ким Аллоҳ ва Унинг пайғамбари чизган чизиқдан чиқса, унинг учун ўзи абадий қоладиган жаҳаннам оташи бўлишини ва бу улуғ-катта расволик эканини билмадиларми'}, {'chapter': 9, 'verse': 64, 'text': 'يَحۡذَرُ ٱلۡمُنَٰفِقُونَ أَن تُنَزَّلَ عَلَيۡهِمۡ سُورَةٞ تُنَبِّئُهُم بِمَا فِي قُلُوبِهِمۡۚ قُلِ ٱسۡتَهۡزِءُوٓاْ إِنَّ ٱللَّهَ مُخۡرِجٞ مَّا تَحۡذَرُونَ\nБу мунофиқлар ўзлари ҳақида дилларидаги нарсадан (яъни, мунофиқликларидан) огоҳлантирадиган бирон сура нозил бўлиб қолишидан қўрқадилар-у (яна пайғамбар ва мўминларни) масхара қилиб куладилар. Айтинг: «Кулаверинглар, Аллоҳ сизлар (фош бўлиб қолишидан) қўрқаётган нарсани албатта (юзага) чиқаргувчидир»'}, {'chapter': 9, 'verse': 65, 'text': 'وَلَئِن سَأَلۡتَهُمۡ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلۡعَبُۚ قُلۡ أَبِٱللَّهِ وَءَايَٰتِهِۦ وَرَسُولِهِۦ كُنتُمۡ تَسۡتَهۡزِءُونَ\nҚасамки, агар улардан (нега сизнинг устингиздан кулганлари ҳақида) сўрасангиз, албатта улар: «Биз фақат баҳслашиб, ҳазиллашиб келяпмиз, холос», дейдилар. Айтинг: «Аллоҳдан, Унинг оятларидан, Унинг пайғамбаридан кулувчи бўлдингизми?»'}, {'chapter': 9, 'verse': 66, 'text': 'لَا تَعۡتَذِرُواْ قَدۡ كَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡۚ إِن نَّعۡفُ عَن طَآئِفَةٖ مِّنكُمۡ نُعَذِّبۡ طَآئِفَةَۢ بِأَنَّهُمۡ كَانُواْ مُجۡرِمِينَ\nУзр айтманглар! Сизлар иймон келтирганингиздан сўнг яна куфрга қайтдингиз. Агар сизлардан бир тоифани (чин ихлос билан тавба қилганлари учун) афв қилсак, бошқа бир тоифани жиноятчи бўлганликлари сабабли азоблаймиз'}, {'chapter': 9, 'verse': 67, 'text': 'ٱلۡمُنَٰفِقُونَ وَٱلۡمُنَٰفِقَٰتُ بَعۡضُهُم مِّنۢ بَعۡضٖۚ يَأۡمُرُونَ بِٱلۡمُنكَرِ وَيَنۡهَوۡنَ عَنِ ٱلۡمَعۡرُوفِ وَيَقۡبِضُونَ أَيۡدِيَهُمۡۚ نَسُواْ ٱللَّهَ فَنَسِيَهُمۡۚ إِنَّ ٱلۡمُنَٰفِقِينَ هُمُ ٱلۡفَٰسِقُونَ\nМунофиқ эркаклар ва мунофиқ аёллар бир-бирларидандирлар (яъни, кофирликда бир-бирларига ўхшайдилар). Улар ёмонликка буюрадилар, яхшиликдан тўхтатадилар ва (Аллоҳ йўлида инфоқ-эҳсон қилишдан) қўлларини (ўзларини) тиядилар. Улар Аллоҳни унутишгач, Аллоҳ ҳам уларни унутди. Албатта, мунофиқлар ҳақиқий фосиқ-итоатсиздирлар'}, {'chapter': 9, 'verse': 68, 'text': 'وَعَدَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡكُفَّارَ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۚ هِيَ حَسۡبُهُمۡۚ وَلَعَنَهُمُ ٱللَّهُۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ\nАллоҳ мунофиқ ва мунофиқаларга ҳамда кофирларга улар абадий қоладиган жаҳаннам оташини ваъда қилдики, ўша улар учун етарлидир. Аллоҳ уларни лаънатлади. Улар учун доимий азоб бордир'}, {'chapter': 9, 'verse': 69, 'text': 'كَٱلَّذِينَ مِن قَبۡلِكُمۡ كَانُوٓاْ أَشَدَّ مِنكُمۡ قُوَّةٗ وَأَكۡثَرَ أَمۡوَٰلٗا وَأَوۡلَٰدٗا فَٱسۡتَمۡتَعُواْ بِخَلَٰقِهِمۡ فَٱسۡتَمۡتَعۡتُم بِخَلَٰقِكُمۡ كَمَا ٱسۡتَمۡتَعَ ٱلَّذِينَ مِن قَبۡلِكُم بِخَلَٰقِهِمۡ وَخُضۡتُمۡ كَٱلَّذِي خَاضُوٓاْۚ أُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\n(Эй мунофиқлар), сизлар ҳам худди сизлардан илгари ўтган (мунофиқлар)га ўхшайсизлар. Улар сизлардан кўра қувватлироқ ва мол-мулк, бола-чақалари ҳам кўпроқ эди. Бас, (бу дунёдаги) ўз насибаларини кўрдилар. Сизлар ҳам, илгари ўтганлар насибаларини кўрганларидек, ўз насибангизни кўрдингиз ва улар шўнғиган нарсага (яъни, ноҳақ йўлга) шўнғидингиз. Уларнинг (яъни, барча мунофиқларнинг) қилган амаллари дунёю Охиратда беҳуда кетди. Ана ўшалар ҳақиқий зиён кўргувчилардир'}, {'chapter': 9, 'verse': 70, 'text': 'أَلَمۡ يَأۡتِهِمۡ نَبَأُ ٱلَّذِينَ مِن قَبۡلِهِمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَقَوۡمِ إِبۡرَٰهِيمَ وَأَصۡحَٰبِ مَدۡيَنَ وَٱلۡمُؤۡتَفِكَٰتِۚ أَتَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nУларга ўзларидан илгари ўтган кишилар бўлмиш Нуҳ, Од, Самуд қавмларининг ва Иброҳим қавмининг, Мадян аҳолисининг (яъни, Шуайб қавмининг) ҳамда зеру забар бўлган қишлоқларнинг (яъни, Лут қавми қишлоқларининг) ҳоли-хабари келмадими?! Уларга пайғамбарлари очиқ далил-ҳужжатлар келтирган эдилар (шунда улар пайғамбарларини ёлғончи қилишгач, бу қавмлар устига Аллоҳнинг балолари ёғилган эди). Бас, Аллоҳ уларга зулм қилгувчи бўлмади, балки ўзлари ўзларига зулм қилган эдилар'}, {'chapter': 9, 'verse': 71, 'text': 'وَٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ يَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَيُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَيُطِيعُونَ ٱللَّهَ وَرَسُولَهُۥٓۚ أُوْلَـٰٓئِكَ سَيَرۡحَمُهُمُ ٱللَّهُۗ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nМўмин ва мўминалар бир-бирларига дўстдирлар. Улар яхшиликка буюрадилар, ёмонликдан тўхтатадилар, намозни тўкис адо этадилар, закотни (ҳақдорларга) ато этадилар, Аллоҳ ва Унинг пайғамбарига итоат қиладилар. Ана ўшаларга Аллоҳ раҳм қилур. Шак-шубҳасиз, Аллоҳ қудратли, ҳикматлидир'}, {'chapter': 9, 'verse': 72, 'text': 'وَعَدَ ٱللَّهُ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّـٰتِ عَدۡنٖۚ وَرِضۡوَٰنٞ مِّنَ ٱللَّهِ أَكۡبَرُۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАллоҳ мўмин, мўминаларга остидан дарёлар оқиб турадиган, улар абадий қоладиган жаннатлар ва жаннат боғларидаги покиза масканларни ваъда қилди. Аллоҳнинг розилиги эса ҳамма нарсадан улуғроқдир. Мана шу Ҳақиқий буюк бахтдир'}, {'chapter': 9, 'verse': 73, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ جَٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَٰفِقِينَ وَٱغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nЭй пайғамбар, кофир ва мунофиқларга қарши курашинг ва уларга қаттиққўл бўлинг. Уларнинг жойлари жаханнамдир. Нақадар ёмон оқибат бу'}, {'chapter': 9, 'verse': 74, 'text': 'يَحۡلِفُونَ بِٱللَّهِ مَا قَالُواْ وَلَقَدۡ قَالُواْ كَلِمَةَ ٱلۡكُفۡرِ وَكَفَرُواْ بَعۡدَ إِسۡلَٰمِهِمۡ وَهَمُّواْ بِمَا لَمۡ يَنَالُواْۚ وَمَا نَقَمُوٓاْ إِلَّآ أَنۡ أَغۡنَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ مِن فَضۡلِهِۦۚ فَإِن يَتُوبُواْ يَكُ خَيۡرٗا لَّهُمۡۖ وَإِن يَتَوَلَّوۡاْ يُعَذِّبۡهُمُ ٱللَّهُ عَذَابًا أَلِيمٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَمَا لَهُمۡ فِي ٱلۡأَرۡضِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nУлар (яъни, мунофиқлар сизга етиб келган ҳақорат сўзларини) айтмаганликларига қасам ичадилар. Ҳолбуки, куфр сўзини аниқ айтган эдилар ва Исломга кирганларидан сўнг яна куфрга қайтган эдилар ҳамда ўзлари етолмаган нарса (яъни, пайғамбар жонига суиқасд қилишга) қасд қилган эдилар. Улар (мунофиқлар) фақат Аллоҳ ва Унинг пайғамбари уларни Ўз фазлу карамидан бой-бадавлат қилиб қўйгани учунгина (Аллоҳ ва Унинг пайғамбарини) ёмон кўрдилар, холос. Бас, энди агар тавба қилсалар, ўзлари учун яхши бўлур. Агар юз ўгирсалар, Аллоҳ уларни дунёю Охиратда аламли азоб билан азоблар ва улар учун Ер юзида бирон дўст ва ёрдамчи бўлмас'}, {'chapter': 9, 'verse': 75, 'text': '۞وَمِنۡهُم مَّنۡ عَٰهَدَ ٱللَّهَ لَئِنۡ ءَاتَىٰنَا مِن فَضۡلِهِۦ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ ٱلصَّـٰلِحِينَ\nУларнинг орасида: «Қасамки, агар (Аллоҳ) бизга Ўз фазлу карамидан (мол-давлат) ато қилса, албатта биз камбағалларга садақотлар қилурмиз ва солиҳ бандалардан бўлурмиз», деб Аллоҳга аҳд-паймон берадиган кимсалар ҳам бордир'}, {'chapter': 9, 'verse': 76, 'text': 'فَلَمَّآ ءَاتَىٰهُم مِّن فَضۡلِهِۦ بَخِلُواْ بِهِۦ وَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ\nЭнди қачонки (Аллоҳ) уларга Ўз фазлу карамидан (мол-давлат) ато қилса, унга бахиллик қилурлар ва юз ўгирган ҳолларида кетурлар'}, {'chapter': 9, 'verse': 77, 'text': 'فَأَعۡقَبَهُمۡ نِفَاقٗا فِي قُلُوبِهِمۡ إِلَىٰ يَوۡمِ يَلۡقَوۡنَهُۥ بِمَآ أَخۡلَفُواْ ٱللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُواْ يَكۡذِبُونَ\nБас, (садақотлар берурмиз), деб Аллоҳга берган ваъдаларига хилоф қилганлари ва (иймон келтирганмиз), деб ёлғон гапирувчи бўлганлари сабабли (Аллоҳ) уларга то Унинг Ўзига рўбарў бўладиган Кунгача дилларида нифоқ бўлишини қисмат қилиб қўйди'}, {'chapter': 9, 'verse': 78, 'text': 'أَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ سِرَّهُمۡ وَنَجۡوَىٰهُمۡ وَأَنَّ ٱللَّهَ عَلَّـٰمُ ٱلۡغُيُوبِ\nУлар Аллоҳ уларнинг яширган сирларини ҳам, ўзаро шивир-шивирларини ҳам билишини ва Аллоҳ ғайбни — кўринмас нарсаларни ҳам аниқ билувчи эканини билмадиларми'}, {'chapter': 9, 'verse': 79, 'text': 'ٱلَّذِينَ يَلۡمِزُونَ ٱلۡمُطَّوِّعِينَ مِنَ ٱلۡمُؤۡمِنِينَ فِي ٱلصَّدَقَٰتِ وَٱلَّذِينَ لَا يَجِدُونَ إِلَّا جُهۡدَهُمۡ فَيَسۡخَرُونَ مِنۡهُمۡ سَخِرَ ٱللَّهُ مِنۡهُمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ\nМўминлар орасидан ўз хоҳиши билан садақотлар қилувчи кишиларни ва (инфоқ-эҳон қилиш учун) кучлари етган нарсани топиб келган кишиларни айблаб, масхара қиладиган кимсаларни Аллоҳ масхара қилур ва улар учун аламли азоб бордир'}, {'chapter': 9, 'verse': 80, 'text': 'ٱسۡتَغۡفِرۡ لَهُمۡ أَوۡ لَا تَسۡتَغۡفِرۡ لَهُمۡ إِن تَسۡتَغۡفِرۡ لَهُمۡ سَبۡعِينَ مَرَّةٗ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡۚ ذَٰلِكَ بِأَنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَرَسُولِهِۦۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), хоҳ улар учун мағфират сўранг, хоҳ мағфират сўраманг — агар етмиш марталаб улар учун мағфират сўрасангиз ҳам, Аллоҳ уларни асло мағфират қилмас. Бунга сабаб уларнинг Аллоҳ ва Унинг пайғамбарига кофир бўлганларидир. Аллоҳ эса итоатсиз қавмни ҳидоят қилмас'}, {'chapter': 9, 'verse': 81, 'text': 'فَرِحَ ٱلۡمُخَلَّفُونَ بِمَقۡعَدِهِمۡ خِلَٰفَ رَسُولِ ٱللَّهِ وَكَرِهُوٓاْ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِ وَقَالُواْ لَا تَنفِرُواْ فِي ٱلۡحَرِّۗ قُلۡ نَارُ جَهَنَّمَ أَشَدُّ حَرّٗاۚ لَّوۡ كَانُواْ يَفۡقَهُونَ\n(Жиҳодга чиқишдан) қолган мунофиқлар Аллоҳнинг пайғамбарига хилоф қилиб қолганларидан хурсанд бўлдилар ва молу жонлари билан Аллоҳ йўлида жиҳод қилишни истамадилар ҳамда (бир-бирларига): «Бу иссиқда урушга чиқманглар», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар улар англай олсалар, жаҳаннам оташи янада иссиқроқир»'}, {'chapter': 9, 'verse': 82, 'text': 'فَلۡيَضۡحَكُواْ قَلِيلٗا وَلۡيَبۡكُواْ كَثِيرٗا جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ\nБас, ўзларининг касб қилган нарсалари (яъни, кофирликни танлаганлари) жазосига (бу дунёда) озгина кулсинлар, (сўнгра Охиратда) кўп — абадий йиғласинлар'}, {'chapter': 9, 'verse': 83, 'text': 'فَإِن رَّجَعَكَ ٱللَّهُ إِلَىٰ طَآئِفَةٖ مِّنۡهُمۡ فَٱسۡتَـٔۡذَنُوكَ لِلۡخُرُوجِ فَقُل لَّن تَخۡرُجُواْ مَعِيَ أَبَدٗا وَلَن تُقَٰتِلُواْ مَعِيَ عَدُوًّاۖ إِنَّكُمۡ رَضِيتُم بِٱلۡقُعُودِ أَوَّلَ مَرَّةٖ فَٱقۡعُدُواْ مَعَ ٱلۡخَٰلِفِينَ\nЭнди агар Аллоҳ сизни (Табук жангидан) қайтариб, улардан бирон тоифага (рўбарў қилса) бас, улар сиздан (бундан буён бўладиган ғазотларга) чиқиш учун изн сўрасалар, айтингки: «Мен билан ҳаргиз чиқмайсизлар ва мен билан бирга бирор душманга қарши жанг ҳам қилмайсизлар. Чунки сизлар аввал-бошда чиқмай ўтиришга рози бўлдингиз, бас, яна қолгувчи кимсалар билан бирга ўтираверинглар»'}, {'chapter': 9, 'verse': 84, 'text': 'وَلَا تُصَلِّ عَلَىٰٓ أَحَدٖ مِّنۡهُم مَّاتَ أَبَدٗا وَلَا تَقُمۡ عَلَىٰ قَبۡرِهِۦٓۖ إِنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَرَسُولِهِۦ وَمَاتُواْ وَهُمۡ فَٰسِقُونَ\nУлардан биронтаси ўлса, зинҳор унинг (жаноза) намозини ўқиманг ва қабри устига ҳам бориб турманг! Чунки улар Аллоҳ ва Унинг пайғамбарига кофир бўлдилар ва итоатсиз ҳолларида ўлдилар'}, {'chapter': 9, 'verse': 85, 'text': 'وَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَأَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ ٱللَّهُ أَن يُعَذِّبَهُم بِهَا فِي ٱلدُّنۡيَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ\nСизни уларнинг молу дунёлари ва бола-чақалари қизиқтирмасин! Чунки Аллоҳ ўша нарсалар сабабли бу дунёда уларни азоб-уқубатга солишни ва кофир бўлган ҳолларида, жонлари чиқишини истайди, холос'}, {'chapter': 9, 'verse': 86, 'text': 'وَإِذَآ أُنزِلَتۡ سُورَةٌ أَنۡ ءَامِنُواْ بِٱللَّهِ وَجَٰهِدُواْ مَعَ رَسُولِهِ ٱسۡتَـٔۡذَنَكَ أُوْلُواْ ٱلطَّوۡلِ مِنۡهُمۡ وَقَالُواْ ذَرۡنَا نَكُن مَّعَ ٱلۡقَٰعِدِينَ\nҚачон: «Аллоҳга иймон келтирингиз ва Унинг пайғамбари билан бирга жиҳод қилингиз», деган бирон сура нозил қилинса, улардан бой-бадавлат бўлган кимсалар сиздан (қолиш учун) изн сўрайдилар ва «Бизни қўйгин, анави қолгувчи (маъзур, узрли кишилар) билан бирга бўлайлик», дейдилар'}, {'chapter': 9, 'verse': 87, 'text': 'رَضُواْ بِأَن يَكُونُواْ مَعَ ٱلۡخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ\nУлар хотин-халаж билан қолишга рози бўлдилар. Диллари муҳрлаб қўйилди. Бас, энди улар (жиҳодга чиқишда бўладиган яхшиликни) англамайдилар'}, {'chapter': 9, 'verse': 88, 'text': 'لَٰكِنِ ٱلرَّسُولُ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ جَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ وَأُوْلَـٰٓئِكَ لَهُمُ ٱلۡخَيۡرَٰتُۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nЛекин Пайғамбар ва у билан бирга бўлган, иймон келтирган зотлар молу жонлари билан курашдилар. Барча яхшиликлар ана ўшалар учундир. Нажот топгувчилар ҳам уларнинг ўзидир'}, {'chapter': 9, 'verse': 89, 'text': 'أَعَدَّ ٱللَّهُ لَهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАллоҳ улар учун остидан дарёлар оқиб турадиган, улар абадий қоладиган жаннатларни тайёрлаб қўйгандир. Мана шу буюк бахтдир'}, {'chapter': 9, 'verse': 90, 'text': 'وَجَآءَ ٱلۡمُعَذِّرُونَ مِنَ ٱلۡأَعۡرَابِ لِيُؤۡذَنَ لَهُمۡ وَقَعَدَ ٱلَّذِينَ كَذَبُواْ ٱللَّهَ وَرَسُولَهُۥۚ سَيُصِيبُ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٞ\nАъробийлардан (яъни, тоғлик динсиз араблардан) ҳам ўзлари учун изн берилишини сўраб узр айтгувчилар келди ва (иймон келтирдик, деб) Аллоҳ ва Унинг пайғамбарини алдаган кимсалар урушдан қолдилар. Албатта, уларнинг орасидаги кофир кимсаларга аламли азоб етажак'}, {'chapter': 9, 'verse': 91, 'text': 'لَّيۡسَ عَلَى ٱلضُّعَفَآءِ وَلَا عَلَى ٱلۡمَرۡضَىٰ وَلَا عَلَى ٱلَّذِينَ لَا يَجِدُونَ مَا يُنفِقُونَ حَرَجٌ إِذَا نَصَحُواْ لِلَّهِ وَرَسُولِهِۦۚ مَا عَلَى ٱلۡمُحۡسِنِينَ مِن سَبِيلٖۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nОжиз-нотавонларга, ҳасталарга ва (жиҳод учун) бергани ҳеч нарса топа олмаётган кишиларга — агар улар Аллоҳ ва Унинг пайғамбари учун холис бўлсалар, бирон хараж — гуноҳ йўқдир. Чиройли амал қилувчи — муҳсинларни айблашга ҳеч қандай йўл йўқ. Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 9, 'verse': 92, 'text': 'وَلَا عَلَى ٱلَّذِينَ إِذَا مَآ أَتَوۡكَ لِتَحۡمِلَهُمۡ قُلۡتَ لَآ أَجِدُ مَآ أَحۡمِلُكُمۡ عَلَيۡهِ تَوَلَّواْ وَّأَعۡيُنُهُمۡ تَفِيضُ مِنَ ٱلدَّمۡعِ حَزَنًا أَلَّا يَجِدُواْ مَا يُنفِقُونَ\nЯна сизга (жангга миниб бориш учун) от-улов сўраб келган вақтларида, уларга: «Сизларни миндириб юборадиган улов йўқ-ку», деганингизда (жиҳод учун) ишлатиладиган бирон нарса топа олмаганлари учун ғамгин бўлишиб, кўзларидан ёш тўкиб, қайтиб кетган кишиларни ҳам айблашга ҳеч қандай йўл йўқдир'}, {'chapter': 9, 'verse': 93, 'text': '۞إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ وَهُمۡ أَغۡنِيَآءُۚ رَضُواْ بِأَن يَكُونُواْ مَعَ ٱلۡخَوَالِفِ وَطَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَعۡلَمُونَ\nФақат ўзлари бой-бадавлат бўлган ҳолларида сиздан изн сўрайдиган кимсаларнигина айблашга йўл бордир. Улар хотин-халаж билан қолишга рози бўлдилар. Аллоҳ уларнинг дилларини муҳрлаб қўйди. Бас, энди (улар жиҳодга чиқишда бўладиган яхшиликни) билмайдилар'}, {'chapter': 9, 'verse': 94, 'text': 'يَعۡتَذِرُونَ إِلَيۡكُمۡ إِذَا رَجَعۡتُمۡ إِلَيۡهِمۡۚ قُل لَّا تَعۡتَذِرُواْ لَن نُّؤۡمِنَ لَكُمۡ قَدۡ نَبَّأَنَا ٱللَّهُ مِنۡ أَخۡبَارِكُمۡۚ وَسَيَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nУларнинг олдига қайтган вақтингизда (ғазотга бора олмаганликлари учун) сизлардан узр сўрайдилар. Айтинг: «Узр сўраманглар, сизларга ҳаргиз ишонмаймиз. Аллоҳ бизни сизларнинг ҳоли-хабарингиздан огоҳ қилди. Шубҳасиз, амалларингизни Аллоҳ ва Унинг пайғамбари кўриб туради. Сўнгра ғайбу шаҳодатни (яъни, яширин ва ошкор ишларнинг ҳаммасини) билгувчи Зотга қайтарилурсизлар. Бас, У сизларга қилиб ўтган амалларингизнинг хабарини берур»'}, {'chapter': 9, 'verse': 95, 'text': 'سَيَحۡلِفُونَ بِٱللَّهِ لَكُمۡ إِذَا ٱنقَلَبۡتُمۡ إِلَيۡهِمۡ لِتُعۡرِضُواْ عَنۡهُمۡۖ فَأَعۡرِضُواْ عَنۡهُمۡۖ إِنَّهُمۡ رِجۡسٞۖ وَمَأۡوَىٰهُمۡ جَهَنَّمُ جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ\nУларнинг олдига қайтиб борган вақтларингизда улардан юз ўгиришларингиз (яъни, айбламасликларингиз учун сизларга уларнинг ростдан ҳам узрли эканликларига) Аллоҳ номи билан қасам ичадилар. Бас, улардан юз ўгиринглар! Чунки улар нопокдирлар ва қилиб ўтган нарсаларини (яъни, мунофиқликлари) жазосига жойлари жаҳаннамдир'}, {'chapter': 9, 'verse': 96, 'text': 'يَحۡلِفُونَ لَكُمۡ لِتَرۡضَوۡاْ عَنۡهُمۡۖ فَإِن تَرۡضَوۡاْ عَنۡهُمۡ فَإِنَّ ٱللَّهَ لَا يَرۡضَىٰ عَنِ ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ\nУлардан рози бўлишларингиз учун сизларга қасам ичадилар. Агар сизлар улардан рози бўлсангизлар ҳам, Аллоҳ бу итоатсиз қавмдан ҳеч рози бўлмайди'}, {'chapter': 9, 'verse': 97, 'text': 'ٱلۡأَعۡرَابُ أَشَدُّ كُفۡرٗا وَنِفَاقٗا وَأَجۡدَرُ أَلَّا يَعۡلَمُواْ حُدُودَ مَآ أَنزَلَ ٱللَّهُ عَلَىٰ رَسُولِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nАъробийлар куфру нифоқда қаттиқроқ ва Аллоҳ Ўз пайғамбарига нозил қилган нарсаларнинг чегараларини билмасликка лойиқроқдирлар. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 9, 'verse': 98, 'text': 'وَمِنَ ٱلۡأَعۡرَابِ مَن يَتَّخِذُ مَا يُنفِقُ مَغۡرَمٗا وَيَتَرَبَّصُ بِكُمُ ٱلدَّوَآئِرَۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nАъробийлар орасида шундай кимсалар ҳам борки, қилган садақасини (ўзи учун) зиён деб билади ва сизларга (мўминларга) балолар келишига кўз тутиб туради. (Барча) ёмон бало(лар) ўзларига бўлсин! Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 9, 'verse': 99, 'text': 'وَمِنَ ٱلۡأَعۡرَابِ مَن يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَيَتَّخِذُ مَا يُنفِقُ قُرُبَٰتٍ عِندَ ٱللَّهِ وَصَلَوَٰتِ ٱلرَّسُولِۚ أَلَآ إِنَّهَا قُرۡبَةٞ لَّهُمۡۚ سَيُدۡخِلُهُمُ ٱللَّهُ فِي رَحۡمَتِهِۦٓۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nАъробийлар орасида Аллоҳга ва Охират кунига иймон келтирадиган ва қиладиган инфоқ-эҳсонини Аллоҳ даргоҳида қурбат (яқинлик) ҳосил қилиш ва пайғамбар дуосига (мушарраф бўлиш) деб биладиган зотлар ҳам бор. Огоҳ бўлсинларким, ўша инфоқ-эҳсонлари ўзлари учун қурбатдир. Аллоҳ уларни Ўз раҳматига дохил қилажак. Албатта, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 9, 'verse': 100, 'text': 'وَٱلسَّـٰبِقُونَ ٱلۡأَوَّلُونَ مِنَ ٱلۡمُهَٰجِرِينَ وَٱلۡأَنصَارِ وَٱلَّذِينَ ٱتَّبَعُوهُم بِإِحۡسَٰنٖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُ وَأَعَدَّ لَهُمۡ جَنَّـٰتٖ تَجۡرِي تَحۡتَهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nМуҳожир ва ансорларнинг биринчи пешқадамлари ва уларга чиройли амаллар билан эргашган зотлар — Аллоҳ улардан рози бўлди ва улар ҳам Ундан рози бўлдилар. Яна (Аллоҳ) улар учун остидан дарёлар оқиб турадиган, улар абадий қоладиган жаннатларни тайёрлаб қўйди. Мана шу буюк бахтдир'}, {'chapter': 9, 'verse': 101, 'text': 'وَمِمَّنۡ حَوۡلَكُم مِّنَ ٱلۡأَعۡرَابِ مُنَٰفِقُونَۖ وَمِنۡ أَهۡلِ ٱلۡمَدِينَةِ مَرَدُواْ عَلَى ٱلنِّفَاقِ لَا تَعۡلَمُهُمۡۖ نَحۡنُ نَعۡلَمُهُمۡۚ سَنُعَذِّبُهُم مَّرَّتَيۡنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٖ\nСизларнинг атрофингиздаги аъробийлар орасида мунофиқлар бор. Мадина аҳолиси орасида ҳам (мунофиқлар бордир). Улар доимий нифоқдадирлар, (лекин) сиз уларни билмайсиз. Биз билурмиз. Уларни қайта-қайта (яъни, ҳаётлик пайтларида қатл қилиш, асир олиш, ўлганларидан кейин эса қабр азоби билан) азоблаймиз. Сўнгра (Қиёмат Кунида) улуғ азобга қайтарилурлар'}, {'chapter': 9, 'verse': 102, 'text': 'وَءَاخَرُونَ ٱعۡتَرَفُواْ بِذُنُوبِهِمۡ خَلَطُواْ عَمَلٗا صَٰلِحٗا وَءَاخَرَ سَيِّئًا عَسَى ٱللَّهُ أَن يَتُوبَ عَلَيۡهِمۡۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nЯна бошқа бир кишилар борки, гуноҳларини эътироф қилурлар. Улар яхши амални бошқа — ёмонига аралаштириб юборганлар. Аллоҳ уларнинг тавбаларини қабул қилса ажабмас. Албатта, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 9, 'verse': 103, 'text': 'خُذۡ مِنۡ أَمۡوَٰلِهِمۡ صَدَقَةٗ تُطَهِّرُهُمۡ وَتُزَكِّيهِم بِهَا وَصَلِّ عَلَيۡهِمۡۖ إِنَّ صَلَوٰتَكَ سَكَنٞ لَّهُمۡۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларнинг молларидан бир қисмини ўзларини поклаб тозалайдиган садақа сифатида олинг ва уларнинг ҳақларига дуо қилинг. Албатта, сизнинг дуоингиз улар учун ором-осойишталик бўлур. Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 9, 'verse': 104, 'text': 'أَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ هُوَ يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَأۡخُذُ ٱلصَّدَقَٰتِ وَأَنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nУлар (гуноҳкорлар) Аллоҳнинг Ўзи бандалари томонидан бўлган тавбани қабул этишини, садақаларни ҳам (Ўз даргоҳида) қабул қилишини ва ҳақиқий тавбаларни қабул этгувчи ва меҳрибон зот Аллоҳнинг Ўзи эканини билмадиларми'}, {'chapter': 9, 'verse': 105, 'text': 'وَقُلِ ٱعۡمَلُواْ فَسَيَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ وَٱلۡمُؤۡمِنُونَۖ وَسَتُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nАйтинг: «(Хоҳлаган) амални қилинглар. Бас, Аллоҳ, Унинг пайғамбари ва мўминлар қилган амалларингизни кўриб турар ва яқинда ғайбу шаҳодатни (яъни, яширин ва ошкор нарсаларнинг барчасини) билгувчи Зотга қайтарилурсизлар. Бас, У зот сизларга қилиб ўтган амалларингизнинг хабарини берур»'}, {'chapter': 9, 'verse': 106, 'text': 'وَءَاخَرُونَ مُرۡجَوۡنَ لِأَمۡرِ ٱللَّهِ إِمَّا يُعَذِّبُهُمۡ وَإِمَّا يَتُوبُ عَلَيۡهِمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nЯна бошқа бир (гуруҳ, ғазотга чиқмаган)лар борки, улар Аллоҳнинг амрига қолдирилурлар — У зот Ўзи уларни ё азобга гирифтор қилар, ёки тавбаларини қабул қилур. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 9, 'verse': 107, 'text': 'وَٱلَّذِينَ ٱتَّخَذُواْ مَسۡجِدٗا ضِرَارٗا وَكُفۡرٗا وَتَفۡرِيقَۢا بَيۡنَ ٱلۡمُؤۡمِنِينَ وَإِرۡصَادٗا لِّمَنۡ حَارَبَ ٱللَّهَ وَرَسُولَهُۥ مِن قَبۡلُۚ وَلَيَحۡلِفُنَّ إِنۡ أَرَدۡنَآ إِلَّا ٱلۡحُسۡنَىٰۖ وَٱللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ\nЯна шундай кимсалар ҳам борки, улар (мўминларга) зиён етказиш, куфрни кучайтириб ва мўминлар ўртасига тафриқа солиш ҳамда илгари Аллоҳ ва Унинг пайғамбарига қарши урушган (бир) кимсага кўз тутиш учун (фитна юзасидан) масжид қуриб олдилар. Яна: «Биз фақат яхшиликнигина истаган эдик», деб қасам ҳам ичадилар. Аллоҳ гувоҳлик берурки, улар шак-шубҳасиз, ёлғончидирлар'}, {'chapter': 9, 'verse': 108, 'text': 'لَا تَقُمۡ فِيهِ أَبَدٗاۚ لَّمَسۡجِدٌ أُسِّسَ عَلَى ٱلتَّقۡوَىٰ مِنۡ أَوَّلِ يَوۡمٍ أَحَقُّ أَن تَقُومَ فِيهِۚ فِيهِ رِجَالٞ يُحِبُّونَ أَن يَتَطَهَّرُواْۚ وَٱللَّهُ يُحِبُّ ٱلۡمُطَّهِّرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ҳеч қачон у масжидда намозга турманг! Биринчи кундан тақво асосига қурилган масжид борки, сиз ўшанда туришингиз лойиқроқ. Унда покликни севадиган кишилар бордир. Аллоҳ эса ўзларини мудом пок тутгувчи зотларни севар'}, {'chapter': 9, 'verse': 109, 'text': 'أَفَمَنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ تَقۡوَىٰ مِنَ ٱللَّهِ وَرِضۡوَٰنٍ خَيۡرٌ أَم مَّنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ شَفَا جُرُفٍ هَارٖ فَٱنۡهَارَ بِهِۦ فِي نَارِ جَهَنَّمَۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nБас, бу биноларини Аллоҳдан қўрқиш ва Унинг ризолиги асосига қурган киши яхшироқми ёки биноларига емирилаётган жар ёқасини асос қилиб олиб, шу сабабли ўзи ҳам жаҳаннам ўтига қулаган кимсами?! Аллоҳ бундай золим қавмни ҳидоят қилмас'}, {'chapter': 9, 'verse': 110, 'text': 'لَا يَزَالُ بُنۡيَٰنُهُمُ ٱلَّذِي بَنَوۡاْ رِيبَةٗ فِي قُلُوبِهِمۡ إِلَّآ أَن تَقَطَّعَ قُلُوبُهُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nҚурган биноларининг (бузиб ташланиши) уларнинг кўнгилларида шак-шубҳа бўлиб қолур. Магар юраклари ёрилиб — жонлари чиқиб кетсагина (шак-шубҳа ҳам йўқ бўлур). Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 9, 'verse': 111, 'text': '۞إِنَّ ٱللَّهَ ٱشۡتَرَىٰ مِنَ ٱلۡمُؤۡمِنِينَ أَنفُسَهُمۡ وَأَمۡوَٰلَهُم بِأَنَّ لَهُمُ ٱلۡجَنَّةَۚ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِ فَيَقۡتُلُونَ وَيُقۡتَلُونَۖ وَعۡدًا عَلَيۡهِ حَقّٗا فِي ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِيلِ وَٱلۡقُرۡءَانِۚ وَمَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ مِنَ ٱللَّهِۚ فَٱسۡتَبۡشِرُواْ بِبَيۡعِكُمُ ٱلَّذِي بَايَعۡتُم بِهِۦۚ وَذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАлбатта, Аллоҳ мўминларнинг жонларини ва молларини улардан жаннат баробарига сотиб олди — улар Аллоҳ йўлида жанг қилишиб (кофирларни) ўлдирадилар ва (ўзлари ҳам Аллоҳ учун шаҳид бўлиб) ўлдириладилар, (бундай мўминларга жаннат берилишига) Аллоҳ Таврот, Инжил ва Қуръонда Ўзининг ҳақ ваъдасини бергандир. Аллоҳдан ҳам аҳдига вафодорроқ ким бор? Бас, (эй мўминлар), қилган бу савдоларингиздан шод бўлингиз. Мана шу ҳақикий буюк бахтдир'}, {'chapter': 9, 'verse': 112, 'text': 'ٱلتَّـٰٓئِبُونَ ٱلۡعَٰبِدُونَ ٱلۡحَٰمِدُونَ ٱلسَّـٰٓئِحُونَ ٱلرَّـٰكِعُونَ ٱلسَّـٰجِدُونَ ٱلۡأٓمِرُونَ بِٱلۡمَعۡرُوفِ وَٱلنَّاهُونَ عَنِ ٱلۡمُنكَرِ وَٱلۡحَٰفِظُونَ لِحُدُودِ ٱللَّهِۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\n(Улар — тавба қилгувчилар Аллоҳнинг Ўзигагина) ибодат қилгувчилар, шукр қилгувчилар, рўза тутгувчилар, рукуъ-сажда қилгувчилар, яхшиликка буюргувчилар, ёмонликдан тўхтатгувчилар ва Аллоҳ белгилаб қўйган қонун-қоидаларга доимий риоя қилгувчи (мўминлар)дир. (Эй Муҳаммад алайҳис-салоту вас-салом), бу мўминларга жаннат хушхабарини етказинг'}, {'chapter': 9, 'verse': 113, 'text': 'مَا كَانَ لِلنَّبِيِّ وَٱلَّذِينَ ءَامَنُوٓاْ أَن يَسۡتَغۡفِرُواْ لِلۡمُشۡرِكِينَ وَلَوۡ كَانُوٓاْ أُوْلِي قُرۡبَىٰ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمۡ أَنَّهُمۡ أَصۡحَٰبُ ٱلۡجَحِيمِ\nНа пайғамбар ва на бу мўминлар — агар мушриклар қариндошлари бўлса ҳам — уларнинг дўзах эгалари эканликлари аниқ маълум бўлганидан кейин, у мушриклар учун мағфират сўрашлари жоиз эмасдир'}, {'chapter': 9, 'verse': 114, 'text': 'وَمَا كَانَ ٱسۡتِغۡفَارُ إِبۡرَٰهِيمَ لِأَبِيهِ إِلَّا عَن مَّوۡعِدَةٖ وَعَدَهَآ إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُۥٓ أَنَّهُۥ عَدُوّٞ لِّلَّهِ تَبَرَّأَ مِنۡهُۚ إِنَّ إِبۡرَٰهِيمَ لَأَوَّـٰهٌ حَلِيمٞ\nИброҳимнинг ўз отаси учун мағфират сўраши фақат унга берган ваъдаси сабабли эди. Энди қачонки унга отаси Аллоҳнинг душмани эканлиги аниқ маълум бўлгач, ундан бутунлай тонди. Албатта, Иброҳим кўнгилчан ва ҳалимдир'}, {'chapter': 9, 'verse': 115, 'text': 'وَمَا كَانَ ٱللَّهُ لِيُضِلَّ قَوۡمَۢا بَعۡدَ إِذۡ هَدَىٰهُمۡ حَتَّىٰ يُبَيِّنَ لَهُم مَّا يَتَّقُونَۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАллоҳ бирон қавмни ҳидоят қилганидан кейин то уларга сақланишлари лозим бўлган нарсаларни аниқ баён қилиб бермагунича, уларни йўлдан оздиргувчи бўлмади (яъни, ҳамма нарсани билиб туриб ўзларини сақламаган кимсаларнигина йўлдан оздирди). Албатта, Аллоҳ ҳамма нарсани билгувчидир'}, {'chapter': 9, 'verse': 116, 'text': 'إِنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۚ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nАлбатта, осмонлар ва Ернинг подшоҳлиги Аллоҳнинг Ўзи учундир. У ҳаёт ва ўлим берур. Сизлар учун Аллоҳдан ўзга на бир дўст ва на бир ёрдамчи бордир'}, {'chapter': 9, 'verse': 117, 'text': 'لَّقَد تَّابَ ٱللَّهُ عَلَى ٱلنَّبِيِّ وَٱلۡمُهَٰجِرِينَ وَٱلۡأَنصَارِ ٱلَّذِينَ ٱتَّبَعُوهُ فِي سَاعَةِ ٱلۡعُسۡرَةِ مِنۢ بَعۡدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٖ مِّنۡهُمۡ ثُمَّ تَابَ عَلَيۡهِمۡۚ إِنَّهُۥ بِهِمۡ رَءُوفٞ رَّحِيمٞ\nҲақиқатан, Аллоҳ пайғамбарнинг, муҳожирлар ва ансорларнинг тавбаларини қабул қилди. Улардан бир гуруҳнинг диллари (ғазотдаги машаққат ва ташналик сабабли) тойилаёзганидан кейин оғир соатда унга (яъни, Пайғамбарга) эргашган эдилар. Сўнг уларнинг тавбаларини (Аллоҳ) қабул қилди. Албатта, У зот мўминларга марҳаматли, меҳрибондир'}, {'chapter': 9, 'verse': 118, 'text': 'وَعَلَى ٱلثَّلَٰثَةِ ٱلَّذِينَ خُلِّفُواْ حَتَّىٰٓ إِذَا ضَاقَتۡ عَلَيۡهِمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ وَضَاقَتۡ عَلَيۡهِمۡ أَنفُسُهُمۡ وَظَنُّوٓاْ أَن لَّا مَلۡجَأَ مِنَ ٱللَّهِ إِلَّآ إِلَيۡهِ ثُمَّ تَابَ عَلَيۡهِمۡ لِيَتُوبُوٓاْۚ إِنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ\nЯна ўша уч кишининг (тавбаларини ҳам қабул қилдики), то уларга кенг Ер торлик килиб қолгунча ва диллари сиқилиб, Аллоҳнинг (ғазабидан) фақат Ўзига тавба қилиш билангина қутулиш мумкин эканини билгунларича (тавбалари) қолдирилган эди (яъни, тавба қилишга муваффақ бўлмаган эдилар). Сўнгра (Аллоҳ) тавба қилишлари учун уларга тавба йўлини очди. Албатта, Аллоҳ тавбаларни қабул қилгувчи, меҳрибондир'}, {'chapter': 9, 'verse': 119, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَكُونُواْ مَعَ ٱلصَّـٰدِقِينَ\nЭй мўминлар, Аллоҳдан қўрқингиз ва иймонларида ростгўй бўлган зотлар билан бирга бўлингиз'}, {'chapter': 9, 'verse': 120, 'text': 'مَا كَانَ لِأَهۡلِ ٱلۡمَدِينَةِ وَمَنۡ حَوۡلَهُم مِّنَ ٱلۡأَعۡرَابِ أَن يَتَخَلَّفُواْ عَن رَّسُولِ ٱللَّهِ وَلَا يَرۡغَبُواْ بِأَنفُسِهِمۡ عَن نَّفۡسِهِۦۚ ذَٰلِكَ بِأَنَّهُمۡ لَا يُصِيبُهُمۡ ظَمَأٞ وَلَا نَصَبٞ وَلَا مَخۡمَصَةٞ فِي سَبِيلِ ٱللَّهِ وَلَا يَطَـُٔونَ مَوۡطِئٗا يَغِيظُ ٱلۡكُفَّارَ وَلَا يَنَالُونَ مِنۡ عَدُوّٖ نَّيۡلًا إِلَّا كُتِبَ لَهُم بِهِۦ عَمَلٞ صَٰلِحٌۚ إِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nМадина аҳолиси ва уларнинг атрофларидаги аъробийлар учун (жиҳодга чиқмай) Аллоҳнинг пайғамбаридан қолишлари ва Ундан юз ўгириб ўзлари билан овора бўлишлари жоиз эмас эди. Зеро, Аллоҳ йўлида уларга бирон ташналик, машаққат, очлик етса ва кофирларни ғазаблантирадиган бирон қадам боссалар, душманга бирон зиён етказсалар, албатта улар учун бу ишлари сабабли, яхши амал ёзилур. Зотан, Аллоҳ яхшилик қилгувчиларнинг амалларини зое қилмас'}, {'chapter': 9, 'verse': 121, 'text': 'وَلَا يُنفِقُونَ نَفَقَةٗ صَغِيرَةٗ وَلَا كَبِيرَةٗ وَلَا يَقۡطَعُونَ وَادِيًا إِلَّا كُتِبَ لَهُمۡ لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا كَانُواْ يَعۡمَلُونَ\nУлар кичикми, каттами бирон инфоқ-эҳсон қилсалар ва (ғазот йўлида) бирон водийдан ўтсалар, албатта қилган энг чиройли амалларига яраша Аллоҳ мукофотлаши учун (буларнинг ҳаммаси) улар фойдасига ёзилур'}, {'chapter': 9, 'verse': 122, 'text': '۞وَمَا كَانَ ٱلۡمُؤۡمِنُونَ لِيَنفِرُواْ كَآفَّةٗۚ فَلَوۡلَا نَفَرَ مِن كُلِّ فِرۡقَةٖ مِّنۡهُمۡ طَآئِفَةٞ لِّيَتَفَقَّهُواْ فِي ٱلدِّينِ وَلِيُنذِرُواْ قَوۡمَهُمۡ إِذَا رَجَعُوٓاْ إِلَيۡهِمۡ لَعَلَّهُمۡ يَحۡذَرُونَ\nБарча мўминлар (жангга) чиқишлари лойиқ эмас. Ахир улардан ҳар бир гуруҳдан бир тоифа одамлар (жанг учун) чиқмайдиларми?! (Қолганлари эса Мадинада) динни ўрганиб, (жангга кетган) қавмлари уларнинг олдиларига қайтган вақтларида, у қавмлар Аллоҳнинг азобидан сақланишлари учун уларни огоҳлантиргани (қолмайдиларми)'}, {'chapter': 9, 'verse': 123, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ قَٰتِلُواْ ٱلَّذِينَ يَلُونَكُم مِّنَ ٱلۡكُفَّارِ وَلۡيَجِدُواْ فِيكُمۡ غِلۡظَةٗۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ\nЭй мўминлар, ёнларингиздаги кофирларга қарши жанг қилинглар ва улар сизлардаги куч-қувватни кўрсинлар! Билингларки, албатта Аллоҳ тақводорлар билан биргадир'}, {'chapter': 9, 'verse': 124, 'text': 'وَإِذَا مَآ أُنزِلَتۡ سُورَةٞ فَمِنۡهُم مَّن يَقُولُ أَيُّكُمۡ زَادَتۡهُ هَٰذِهِۦٓ إِيمَٰنٗاۚ فَأَمَّا ٱلَّذِينَ ءَامَنُواْ فَزَادَتۡهُمۡ إِيمَٰنٗا وَهُمۡ يَسۡتَبۡشِرُونَ\nҚачон бирон сура нозил қилинса, улардан (яъни, мунофиқлардан) бўлган кимсалар (масхара қилишиб): «Қани, бу сура қайси бирларингизнинг иймонларингизни зиёда қилди?» дейишади. Бас, у (сура) иймон келтирган зотларнинг иймонларини албатта зиёда қилур ва улар шод-хуррам бўлурлар'}, {'chapter': 9, 'verse': 125, 'text': 'وَأَمَّا ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ فَزَادَتۡهُمۡ رِجۡسًا إِلَىٰ رِجۡسِهِمۡ وَمَاتُواْ وَهُمۡ كَٰفِرُونَ\nАммо дилларида мараз бўлган кимсаларни эса динсизликларига яна динсизлик қўшур ва улар кофир ҳолларида ўлурлар'}, {'chapter': 9, 'verse': 126, 'text': 'أَوَلَا يَرَوۡنَ أَنَّهُمۡ يُفۡتَنُونَ فِي كُلِّ عَامٖ مَّرَّةً أَوۡ مَرَّتَيۡنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمۡ يَذَّكَّرُونَ\nКўрмайдилармики, улар ҳар йили бир-икки марта балога – фитналарга йўлиқмоқдалар. Шундан кейин ҳам на тавба қиладилар ва на панд-насиҳат оладилар'}, {'chapter': 9, 'verse': 127, 'text': 'وَإِذَا مَآ أُنزِلَتۡ سُورَةٞ نَّظَرَ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ هَلۡ يَرَىٰكُم مِّنۡ أَحَدٖ ثُمَّ ٱنصَرَفُواْۚ صَرَفَ ٱللَّهُ قُلُوبَهُم بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ\nҚачонки бирон сура нозил қилинса, (у мунофиқлар) «Сизларни биронта (мусулмон) кўриб қолмадими?», деб бир-бирларига қараб оладилар-да, сўнгра (уйларига) жўнаб қоладилар. Улар (ҳақни) англамайдиган қавм бўлганлари сабабли, Аллоҳ дилларини (Қуръонни фаҳмлашдан) буриб қўйгандир'}, {'chapter': 9, 'verse': 128, 'text': 'لَقَدۡ جَآءَكُمۡ رَسُولٞ مِّنۡ أَنفُسِكُمۡ عَزِيزٌ عَلَيۡهِ مَا عَنِتُّمۡ حَرِيصٌ عَلَيۡكُم بِٱلۡمُؤۡمِنِينَ رَءُوفٞ رَّحِيمٞ\n(Эй инсонлар), ахир сизларга ўзларингиздан бўлган, сизларнинг кулфат-машаққат чекишингиздан қийналувчи, сизларнинг (Тўғри Йўл — Ҳақ динга келишингизга) ҳарис – ташна бўлган ва барча мўминларга марҳаматли, меҳрибон бўлган бир Пайғамбар келди-ку'}, {'chapter': 9, 'verse': 129, 'text': 'فَإِن تَوَلَّوۡاْ فَقُلۡ حَسۡبِيَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَهُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ\nАна энди ҳам юз ўгирсалар, у ҳолда айтинг: «Менга Аллоҳнинг Ўзи етарлидир! Ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи бордир! (Мен Унинг) Ўзига суяндим. У зот улуғ арш Соҳибидир»'}, {'chapter': 10, 'verse': 1, 'text': 'الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡحَكِيمِ\nАлиф, Лом, Ро. Бу ҳикматли Китоб оятларидир'}, {'chapter': 10, 'verse': 2, 'text': 'أَكَانَ لِلنَّاسِ عَجَبًا أَنۡ أَوۡحَيۡنَآ إِلَىٰ رَجُلٖ مِّنۡهُمۡ أَنۡ أَنذِرِ ٱلنَّاسَ وَبَشِّرِ ٱلَّذِينَ ءَامَنُوٓاْ أَنَّ لَهُمۡ قَدَمَ صِدۡقٍ عِندَ رَبِّهِمۡۗ قَالَ ٱلۡكَٰفِرُونَ إِنَّ هَٰذَا لَسَٰحِرٞ مُّبِينٌ\nБу одамлар учун ўзларидан бўлган бир кишига (яъни, Муҳаммад алайҳис-салоту вас-саломга, кофир) кимсаларни (Аллоҳнинг азобидан) огоҳлантиринг, иймон келтирган зотларга эса улар учун Парвардигорлари ҳузурида азалий рост мартаба борлиги ҳақида хушхабар етказинг, деб ваҳий юборганимиз қизиқ туюлдими?! Кофирлар: «Дарҳақиқат, бу (яъни, Муҳаммад) зўр сеҳргардир», дедилар'}, {'chapter': 10, 'verse': 3, 'text': 'إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يُدَبِّرُ ٱلۡأَمۡرَۖ مَا مِن شَفِيعٍ إِلَّا مِنۢ بَعۡدِ إِذۡنِهِۦۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ فَٱعۡبُدُوهُۚ أَفَلَا تَذَكَّرُونَ\n(Эй инсонлар), албатта, Парвардигорингиз осмонлар ва Ерни олти кунда яратиб, сўнгра (Зоти ва Улуғлигига муносиб суратда) Арш узра юксалган Аллоҳдир. У (ҳар бир) ишнинг тадбирини қилур. (Қиёмат Кунида) бирон оқловчи бўлмас, магар Унинг изну иродасидан сўнггина (оқлай олур). Ана ўша Аллоҳ Парвардигорингиздир, бас, Унга ибодат қилингиз! Ахир эслатма-ибрат олмайсизларми'}, {'chapter': 10, 'verse': 4, 'text': 'إِلَيۡهِ مَرۡجِعُكُمۡ جَمِيعٗاۖ وَعۡدَ ٱللَّهِ حَقًّاۚ إِنَّهُۥ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ بِٱلۡقِسۡطِۚ وَٱلَّذِينَ كَفَرُواْ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ\n(Қиёмат Кунида) барчангиз Унга қайтишингиз бордир. (Бу) Аллоҳнинг ҳаққирост ваъдасидир. Албатта, аввал-бошда барча нарсани Унинг Ўзи яратур. Сўнгра иймон келтириб, яхши амаллар қилиб ўтган зотларни адолат билан мукофотлаш учун (Қиёмат Куни) яна қайта яратур. Куфр йўлини тутган кимсалар учун эса қайноқ сувдан иборат ичимлик ва кофир бўлганлари сабабли аламли азоб бўлур'}, {'chapter': 10, 'verse': 5, 'text': 'هُوَ ٱلَّذِي جَعَلَ ٱلشَّمۡسَ ضِيَآءٗ وَٱلۡقَمَرَ نُورٗا وَقَدَّرَهُۥ مَنَازِلَ لِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ مَا خَلَقَ ٱللَّهُ ذَٰلِكَ إِلَّا بِٱلۡحَقِّۚ يُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ\nУ (Аллоҳ) қуёшни (кундузлари) зиё сочгувчи, ойни эса (кечалари) нур — ёруғлик қилган ва сизлар йилларнинг саноғини ҳамда (вақтларнинг) ҳисобини билишларингиз учун уни (яъни, ойни бир қанча) манзил-буржларга бўлиб қўйган Зотдир. Ҳеч шак-шубҳасиз, Аллоҳ бу (борлиқни) ҳақ (қонун ва мақсад) билан яратди. У зот биладиган қавм учун Ўз оятларини муфассал баён қилур'}, {'chapter': 10, 'verse': 6, 'text': 'إِنَّ فِي ٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَا خَلَقَ ٱللَّهُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَّقُونَ\nАлбатта, кеча ва кундузнинг алмашиб туришида ҳамда Аллоҳ осмонлар ва Ерда яратиб қўйган нарсаларда Аллоҳдан қўрқадиган қавм учун оят-аломатлар бордир'}, {'chapter': 10, 'verse': 7, 'text': 'إِنَّ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا وَرَضُواْ بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَٱطۡمَأَنُّواْ بِهَا وَٱلَّذِينَ هُمۡ عَنۡ ءَايَٰتِنَا غَٰفِلُونَ\nАлбатта, Бизга рўбарў бўлишни умид қилмайдиган, ҳаёти дунёнинг ўзигагина рози бўлиб, фақат ўша билан хотирлари жам бўлган кимсалар ҳамда Бизнинг оятларимиздан ғофил қолган кимсалар —'}, {'chapter': 10, 'verse': 8, 'text': 'أُوْلَـٰٓئِكَ مَأۡوَىٰهُمُ ٱلنَّارُ بِمَا كَانُواْ يَكۡسِبُونَ\nАна ўшаларнинг жойлари, касб қилиб ўтган гуноҳлари сабабли дўзахдир'}, {'chapter': 10, 'verse': 9, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ يَهۡدِيهِمۡ رَبُّهُم بِإِيمَٰنِهِمۡۖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ فِي جَنَّـٰتِ ٱلنَّعِيمِ\nИймон келтириб, яхши амаллар қилган зотларни эса иймон-эътиқодлари сабабли Парвардигорлари ҳидоят қилур — ноз-неъмат боғларида остларидан дарёлар оқиб турур'}, {'chapter': 10, 'verse': 10, 'text': 'دَعۡوَىٰهُمۡ فِيهَا سُبۡحَٰنَكَ ٱللَّهُمَّ وَتَحِيَّتُهُمۡ فِيهَا سَلَٰمٞۚ وَءَاخِرُ دَعۡوَىٰهُمۡ أَنِ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nУ жойдаги дуолари: «Эй Аллоҳ, Ўзингни поклаймиз», дейиш, у жойдаги ўзаро саломлари (бир-бирларига) тинчлик тилашдир. Охирги тилаклари эса бутун оламлар Парвардигори — Аллоҳга ҳамду сано айтишдир'}, {'chapter': 10, 'verse': 11, 'text': '۞وَلَوۡ يُعَجِّلُ ٱللَّهُ لِلنَّاسِ ٱلشَّرَّ ٱسۡتِعۡجَالَهُم بِٱلۡخَيۡرِ لَقُضِيَ إِلَيۡهِمۡ أَجَلُهُمۡۖ فَنَذَرُ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАгар Аллоҳ бу одамларга (яъни, мушрикларга) яхшиликни тез келтиргани каби ёмонликни ҳам нақд қилса эди, албатта уларнинг ажаллари етган бўлур эди. Бас, (Биз уларга ёмонликни нақд қилмаймиз, балки) Бизга рўбарў бўлишни умид қилмайдиган кимсаларни, ўз туғёнларида адашиб-улоқиб юрган ҳолларида ташлаб қўюрмиз'}, {'chapter': 10, 'verse': 12, 'text': 'وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ٱلضُّرُّ دَعَانَا لِجَنۢبِهِۦٓ أَوۡ قَاعِدًا أَوۡ قَآئِمٗا فَلَمَّا كَشَفۡنَا عَنۡهُ ضُرَّهُۥ مَرَّ كَأَن لَّمۡ يَدۡعُنَآ إِلَىٰ ضُرّٖ مَّسَّهُۥۚ كَذَٰلِكَ زُيِّنَ لِلۡمُسۡرِفِينَ مَا كَانُواْ يَعۡمَلُونَ\nҚачон инсон зотига бирон зиён-мусибат етса, ётган ҳолида ҳам, ўтириб ҳам, туриб ҳам Бизга дуо-илтижо қилур. Энди қачонки ундан мусибатини кеткизсак, гўё (ҳеч қачон) ўзига етган бало-мусибатдан Бизга илтижо қилмагандек (юз ўгириб) кетур. Ҳаддан ошувчи кимсаларга ўз қилмишлари мана шундай чиройли қилиб қўйилди'}, {'chapter': 10, 'verse': 13, 'text': 'وَلَقَدۡ أَهۡلَكۡنَا ٱلۡقُرُونَ مِن قَبۡلِكُمۡ لَمَّا ظَلَمُواْ وَجَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ وَمَا كَانُواْ لِيُؤۡمِنُواْۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ\nМаълумки, сизлардан аввалги асрлардаги миллатларни ўзларига зулм қилганлари ва пайғамбарлари очиқ-равшан ҳужжат-мўъжизалар келтирган пайтларида, иймон келтирмаганлари учун ҳалок қилганмиз. Жиноятчи қавмни мана шундай жазолагаймиз'}, {'chapter': 10, 'verse': 14, 'text': 'ثُمَّ جَعَلۡنَٰكُمۡ خَلَـٰٓئِفَ فِي ٱلۡأَرۡضِ مِنۢ بَعۡدِهِمۡ لِنَنظُرَ كَيۡفَ تَعۡمَلُونَ\nСўнгра қандай амаллар қилишингизни кўриш учун сизларни уларнинг ортидан Ерга халифа (ўринбосар) қилиб қўйдик'}, {'chapter': 10, 'verse': 15, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَاتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا ٱئۡتِ بِقُرۡءَانٍ غَيۡرِ هَٰذَآ أَوۡ بَدِّلۡهُۚ قُلۡ مَا يَكُونُ لِيٓ أَنۡ أُبَدِّلَهُۥ مِن تِلۡقَآيِٕ نَفۡسِيٓۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۖ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ\nҚачон уларга (уларнинг олдида) Бизнинг очиқ равшан оятларимиз тиловат қилинса, Бизга рўбарў бўлишни умид қилмайдиган кимсалар: «Бу Қуръондан бошқа бир Қуръон келтиргин ёки буни ўзгартиргин», дедилар. Айтинг: «Мен учун уни ўз томонимдан ўзгартириш дуруст эмасдир. Мен фақат ўзимга ваҳий қилинган оятларгагина эргашурман. Чунки мен агар Парвардигоримга исён қилсам, улуғ Куннинг (Қиёматнинг) азобидан қўрқурман»'}, {'chapter': 10, 'verse': 16, 'text': 'قُل لَّوۡ شَآءَ ٱللَّهُ مَا تَلَوۡتُهُۥ عَلَيۡكُمۡ وَلَآ أَدۡرَىٰكُم بِهِۦۖ فَقَدۡ لَبِثۡتُ فِيكُمۡ عُمُرٗا مِّن قَبۡلِهِۦٓۚ أَفَلَا تَعۡقِلُونَ\nАйтинг: «Агар Аллоҳ хоҳлаганида, мен уни (Қуръонни) тиловат қилмаган бўлур эдим ва (мен орқали Аллоҳ) уни сизларга билдирмаган бўлур эди. Ахир ундан (Қуръон нозил бўлишидан) илгари ҳам бир умр сизларнинг орангизда яшаб турдим-ку! Ахир, ақл юргизмайсизларми'}, {'chapter': 10, 'verse': 17, 'text': 'فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡمُجۡرِمُونَ\nАхир Аллоҳ шаънига бўҳтон қилган ёки Унинг оятларини ёлғон деган кимсадан ҳам золимроқ ким бор?!Албатта (бундай) жиноятчи кимсалар нажот топмаслар'}, {'chapter': 10, 'verse': 18, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡ وَيَقُولُونَ هَـٰٓؤُلَآءِ شُفَعَـٰٓؤُنَا عِندَ ٱللَّهِۚ قُلۡ أَتُنَبِّـُٔونَ ٱللَّهَ بِمَا لَا يَعۡلَمُ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУлар Аллоҳни қўйиб, ўзларига зиён ҳам, фойда ҳам етказа олмайдиган нарсаларга ибодат қиладилар ва: «Ана шу нарсалар Аллоҳ ҳузурида бизларнинг оқловчиларимиз», дейдилар. Айтинг: «Аллоҳга осмонлар ва Ердаги У зот билмайдиган нарсаларни (шерикларни) билдириб қўймоқчимисизлар?!» Аллоҳ уларнинг (мушрикларнинг) ширкларидан пок ва юксак бўлган Зотдир'}, {'chapter': 10, 'verse': 19, 'text': 'وَمَا كَانَ ٱلنَّاسُ إِلَّآ أُمَّةٗ وَٰحِدَةٗ فَٱخۡتَلَفُواْۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡ فِيمَا فِيهِ يَخۡتَلِفُونَ\n(Аввалда) одамлар фақат бир миллат (яъни, бир динда) бўлган эдилар. Сўнгра бўлиниб кетдилар. Агар Парвардигорингиз томонидан (яхши-ёмон амалларнинг мукофот-жазолари фақат Қиёматда бўлиши хусусида) Сўз бўлмаганида эди, талашиб-тортишаётган нарсалари ҳақида уларнинг ўрталарида ҳукм қилинган бўлур эди (яъни, диндан чиққан кимсалар шу дунёдаёқ жазоланган бўлур эдилар)'}, {'chapter': 10, 'verse': 20, 'text': 'وَيَقُولُونَ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۖ فَقُلۡ إِنَّمَا ٱلۡغَيۡبُ لِلَّهِ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ\nУлар: «Унга (яъни, Муҳаммад алайҳис-салоту вас-саломга) Парвардигори томонидан оят-мўъжиза тушса эди», дейдилар. Бас, (эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Ғайб (яъни, келажак ва инсон идрокидан махфий бўлган барча нарсалар билими) фақат Аллоҳникидир. Бас, кутаверинглар. Мен ҳам сизлар билан бирга кўз тутгувчиларданман»'}, {'chapter': 10, 'verse': 21, 'text': 'وَإِذَآ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ مِّنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُمۡ إِذَا لَهُم مَّكۡرٞ فِيٓ ءَايَاتِنَاۚ قُلِ ٱللَّهُ أَسۡرَعُ مَكۡرًاۚ إِنَّ رُسُلَنَا يَكۡتُبُونَ مَا تَمۡكُرُونَ\nҚачон бу одамларга етган бало (қаҳатчилик)дан кейин раҳмат (тўкинлик)ни тотдирсак, баногоҳ улар Бизнинг оятларимиз ҳақида макр (ишлатиб, масхара) қилурлар. Айтинг: «Аллоҳнинг макри» (азоби) жуда тездир. Албатта, Бизнинг элчиларимиз (малоикаларимиз) қилаётган макрларингизни ёзиб қўюрлар'}, {'chapter': 10, 'verse': 22, 'text': 'هُوَ ٱلَّذِي يُسَيِّرُكُمۡ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِۖ حَتَّىٰٓ إِذَا كُنتُمۡ فِي ٱلۡفُلۡكِ وَجَرَيۡنَ بِهِم بِرِيحٖ طَيِّبَةٖ وَفَرِحُواْ بِهَا جَآءَتۡهَا رِيحٌ عَاصِفٞ وَجَآءَهُمُ ٱلۡمَوۡجُ مِن كُلِّ مَكَانٖ وَظَنُّوٓاْ أَنَّهُمۡ أُحِيطَ بِهِمۡ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ لَئِنۡ أَنجَيۡتَنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ\nУ (Аллоҳ) шундай Зотки, сизларни қуруқлик ва денгизда юргизур. То сизлар кемаларда бўлган вақтларингизда ва у (кемалар) хуш ел билан уларни (яъни, сизларни) олиб кетаётганида ва улар бу билан шод-хуррам бўлганларида, бир қаттиқ шамол келур ҳамда уларнинг устига ҳар тарафдан тўлқин келиб, ўзларининг гирдобда қолганларини билганларида: «Қасамки, агар мана шу (бало)дан бизга нажот берсанг, албатта шукр қилгувчилардан бўлурмиз», деб чин ихлос билан Аллоҳга илтижо қилурлар'}, {'chapter': 10, 'verse': 23, 'text': 'فَلَمَّآ أَنجَىٰهُمۡ إِذَا هُمۡ يَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۗ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَا بَغۡيُكُمۡ عَلَىٰٓ أَنفُسِكُمۖ مَّتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ إِلَيۡنَا مَرۡجِعُكُمۡ فَنُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nЭнди қачонки Аллоҳ уларга нажот бергач, баногоҳ улар Ер юзида ноҳақ зулму тажовузкорлик қилурлар. Эй одамлар, бу тажовузингиз фақат ўзингизнинг зиёнингизгадир. (Сизлар бу арзимас ва қисқа) ҳаёти дунё матоси(дан фойдаланмоқдасиз). Сўнгра Бизга қайтишингиз бордир. Бас, Биз сизларга қилиб ўтган амалларингизни билдирурмиз'}, {'chapter': 10, 'verse': 24, 'text': 'إِنَّمَا مَثَلُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ مِمَّا يَأۡكُلُ ٱلنَّاسُ وَٱلۡأَنۡعَٰمُ حَتَّىٰٓ إِذَآ أَخَذَتِ ٱلۡأَرۡضُ زُخۡرُفَهَا وَٱزَّيَّنَتۡ وَظَنَّ أَهۡلُهَآ أَنَّهُمۡ قَٰدِرُونَ عَلَيۡهَآ أَتَىٰهَآ أَمۡرُنَا لَيۡلًا أَوۡ نَهَارٗا فَجَعَلۡنَٰهَا حَصِيدٗا كَأَن لَّمۡ تَغۡنَ بِٱلۡأَمۡسِۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَتَفَكَّرُونَ\nДарҳақиқат, бу ҳаёти дунёнинг мисоли худди Биз осмондан ёғдирган бир сувга ўхшайди, бас, одамзод ва ҳайвонот ейдиган нарсалардан иборат бўлган ер набототи у билан аралашиб, ҳатто ер чирой олиб, ясан-тусан қилганида ва ернинг аҳли унинг устида кучли-қудратлимиз, деб ўйлай бошлаганида, ерга кечаси ё кундузи Бизнинг фармонимиз келиб, Биз уни гўё куни-кеча обод бўлмагандек, вайронага айлантириб қўюрмиз. Тафаккур қила оладиган қавм учун оятларни мана шундай муфассал баён қилурмиз'}, {'chapter': 10, 'verse': 25, 'text': 'وَٱللَّهُ يَدۡعُوٓاْ إِلَىٰ دَارِ ٱلسَّلَٰمِ وَيَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳ тинчлик диёри — жаннатга даъват қилур ва Ўзи хоҳлаган зотларни Тўғри Йўлга ҳидоят қилур'}, {'chapter': 10, 'verse': 26, 'text': '۞لِّلَّذِينَ أَحۡسَنُواْ ٱلۡحُسۡنَىٰ وَزِيَادَةٞۖ وَلَا يَرۡهَقُ وُجُوهَهُمۡ قَتَرٞ وَلَا ذِلَّةٌۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЧиройли амал қилган зотлар учун гўзал оқибат ва зиёда неъматлар бордир. Уларнинг юзларини на қаролик ва на хорлик қоплар. Ана ўшалар жаннат эгалари бўлиб, у жойда абадий қолурлар'}, {'chapter': 10, 'verse': 27, 'text': 'وَٱلَّذِينَ كَسَبُواْ ٱلسَّيِّـَٔاتِ جَزَآءُ سَيِّئَةِۭ بِمِثۡلِهَا وَتَرۡهَقُهُمۡ ذِلَّةٞۖ مَّا لَهُم مِّنَ ٱللَّهِ مِنۡ عَاصِمٖۖ كَأَنَّمَآ أُغۡشِيَتۡ وُجُوهُهُمۡ قِطَعٗا مِّنَ ٱلَّيۡلِ مُظۡلِمًاۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nЁмон амаллар қилган кимсалар учун эса ўша ёмонликлари баробарида ёмон жазо бўлур ва уларни хорлик ўраб олур — улар учун Аллоҳ тарафидан бирон-бир (жазодан) сақловчи бўлмас. Уларнинг юзлари гўё қоронғу кечадан бир бўлаги қоплагандек. Ана ўшалар дўзах эгалари бўлиб, у жойда абадий қолурлар'}, {'chapter': 10, 'verse': 28, 'text': 'وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُواْ مَكَانَكُمۡ أَنتُمۡ وَشُرَكَآؤُكُمۡۚ فَزَيَّلۡنَا بَيۡنَهُمۡۖ وَقَالَ شُرَكَآؤُهُم مَّا كُنتُمۡ إِيَّانَا تَعۡبُدُونَ\nУ кунда (Қиёматда) Биз уларнинг (яъни, инсонларнинг) ҳаммаларини тўплаймиз, сўнгра мушрик бўлган кимсаларга: «Сизлар ҳам, Аллоҳга шерик қилиб олган бутларингиз ҳам ўз жойингиздан жилмангиз», деб уларнинг ўрталарини ажратурмиз (яъни, мушриклар дунёда доим бирга бўлиб, сиғиниб юрган бутларидан ажралиб қолурлар) ва бутлари (уларга): «Сизлар бизга ибодат қилганингиз йўқ (яъни, биз, бутлар жонсиз нарсалар бўлганимиз учун бизга сиғинганларингизни билганимиз йўқ)'}, {'chapter': 10, 'verse': 29, 'text': 'فَكَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنَنَا وَبَيۡنَكُمۡ إِن كُنَّا عَنۡ عِبَادَتِكُمۡ لَغَٰفِلِينَ\nБас, Аллоҳнинг Ўзи биз билан сизнинг ўртамизда гувоҳ бўлиш учун кифоядир. Албатта, биз сизларнинг ибодатингиздан бехабармиз», дейдилар'}, {'chapter': 10, 'verse': 30, 'text': 'هُنَالِكَ تَبۡلُواْ كُلُّ نَفۡسٖ مَّآ أَسۡلَفَتۡۚ وَرُدُّوٓاْ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nЎша ерда ҳар бир жон қилиб ўтган ишидан хабардор бўлур ва (ҳаммалари) ҳақиқий Хожалари — Аллоҳга қайтарилурлар ҳамда ўзлари ўйлаб тўқиб чиқарган бутлари улардан ғойиб бўлур'}, {'chapter': 10, 'verse': 31, 'text': 'قُلۡ مَن يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ أَمَّن يَمۡلِكُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَمَن يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَيُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّ وَمَن يُدَبِّرُ ٱلۡأَمۡرَۚ فَسَيَقُولُونَ ٱللَّهُۚ فَقُلۡ أَفَلَا تَتَّقُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом) айтинг: «Ким сизларга осмон ва заминдан ризқ берур ёки ким қулоқ-кўзларингизга эгалик қилур?! Ким ўликдан тирикни чиқарур ва тирикдан ўликни чиқарур ҳамда ким барча ишларни тадбир қилиб турур?!» Улар албатта: «Аллоҳ», дейдилар. Бас, сиз айтинг: «Ахир Ўша зотдан қўрқмайсизларми?!»'}, {'chapter': 10, 'verse': 32, 'text': 'فَذَٰلِكُمُ ٱللَّهُ رَبُّكُمُ ٱلۡحَقُّۖ فَمَاذَا بَعۡدَ ٱلۡحَقِّ إِلَّا ٱلضَّلَٰلُۖ فَأَنَّىٰ تُصۡرَفُونَ\nБас, мана шу Аллоҳ — ҳақиқий Павардигорингиздир. Ҳақиқатдан кейин эса фақатгина йўлдан озиш бор, холос (яъни, ҳақиқий Парвардигорингиз бўлмиш Аллоҳдан ўзгага сиғинар экансиз, албатта йўлдан озган бўлурсиз). Бас, қай тарафга бурилиб кетмоқдасизлар'}, {'chapter': 10, 'verse': 33, 'text': 'كَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ فَسَقُوٓاْ أَنَّهُمۡ لَا يُؤۡمِنُونَ\nМана шундай қилиб, иймон келтирмаганлари сабабли итоатсиз кимсалар устига Парвардигорингизнинг («Албатта жаҳаннамни кофирлар билан тўлдирурман», деган) Сўзи ҳақ бўлди'}, {'chapter': 10, 'verse': 34, 'text': 'قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۚ قُلِ ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۖ فَأَنَّىٰ تُؤۡفَكُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг (сўранг): «Бутларингиз орасида аввал-бошда ўзи яратиб, сўнгра яна қайта ярата оладиган биров борми?!» Айтинг: «Аллоҳ аввал-бошда Ўзи яратиб, сўнгра Ўзи яна қайта яратур. Бас, қай тарафга бурилиб кетмоқдасизлар?!»'}, {'chapter': 10, 'verse': 35, 'text': 'قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَهۡدِيٓ إِلَى ٱلۡحَقِّۚ قُلِ ٱللَّهُ يَهۡدِي لِلۡحَقِّۗ أَفَمَن يَهۡدِيٓ إِلَى ٱلۡحَقِّ أَحَقُّ أَن يُتَّبَعَ أَمَّن لَّا يَهِدِّيٓ إِلَّآ أَن يُهۡدَىٰۖ فَمَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ\nАйтинг (сўранг): «Бутларингиз орасида ҳақ йўлга ҳидоят қила оладиган биров борми?!» Айтинг: «Аллоҳ ҳақ йўлга ҳидоят қилур. Энди ҳақ йўлга ҳидоят қиладиган зот итоат этилишга ҳақлироқми ёки (ҳеч кимни) ҳидоят қила олмайдиган, балки ўзи ҳидоятга муҳтож бўлган кимсами?! (Эй мушриклар), сизларга не бўлди — қандай ҳукм чиқармоқдасиз?!»'}, {'chapter': 10, 'verse': 36, 'text': 'وَمَا يَتَّبِعُ أَكۡثَرُهُمۡ إِلَّا ظَنًّاۚ إِنَّ ٱلظَّنَّ لَا يُغۡنِي مِنَ ٱلۡحَقِّ شَيۡـًٔاۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَفۡعَلُونَ\nУларнинг кўплари жонли-жонсиз бутларга сиғинишларида фақат гумонга эргашадилар, холос. Гумон эса бирон нарсада ҳақиқатнинг ўрнини босолмайди. Албатта, Аллоҳ уларнинг қилаётган ишларини билгувчидир'}, {'chapter': 10, 'verse': 37, 'text': 'وَمَا كَانَ هَٰذَا ٱلۡقُرۡءَانُ أَن يُفۡتَرَىٰ مِن دُونِ ٱللَّهِ وَلَٰكِن تَصۡدِيقَ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ ٱلۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ ٱلۡعَٰلَمِينَ\nУшбу Қуръон Аллоҳдан ўзга биров томонидан тўқилган бўлиши мумкин эмас. Балки, у бутун оламлар Парвардигори томонидан келган ва (ҳақ эканлигига) шак-шубҳа бўлмаган ўзидан аввалги (Таврот, Инжил каби) нарсаларни тасдиқловчи ва (шариат ҳукмларидан (иборат) муфассил Китобдир'}, {'chapter': 10, 'verse': 38, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُواْ بِسُورَةٖ مِّثۡلِهِۦ وَٱدۡعُواْ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ\nЁки: «Уни (Муҳаммад) тўқиб чиқарган», дейдиларми?! (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «У ҳолда агар ростгўй бўлсангизлар, Аллоҳдан ўзга кучингиз етган барча бутларни (ёрдамга) чорлаб, шу (Қуръон)га ўхшаш биргина сура келтирингизлар!»'}, {'chapter': 10, 'verse': 39, 'text': 'بَلۡ كَذَّبُواْ بِمَا لَمۡ يُحِيطُواْ بِعِلۡمِهِۦ وَلَمَّا يَأۡتِهِمۡ تَأۡوِيلُهُۥۚ كَذَٰلِكَ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّـٰلِمِينَ\nЙўқ! Улар Қуръон илмини эгалламасдан ва уларга унинг таъвил — моҳияти аён бўлмасдан туриб «ёлғон», дедилар. Улардан аввалгилар ҳам (ўз пайғамбарларининг сўзларини) мана шундай «ёлғон», деган эдилар. Мана энди ундай золим кимсаларнинг оқибати қандай бўлганини кўринг'}, {'chapter': 10, 'verse': 40, 'text': 'وَمِنۡهُم مَّن يُؤۡمِنُ بِهِۦ وَمِنۡهُم مَّن لَّا يُؤۡمِنُ بِهِۦۚ وَرَبُّكَ أَعۡلَمُ بِٱلۡمُفۡسِدِينَ\nУларнинг (яъни, мушрикларнинг) орасида унга (Қуръонга) ишонадиганлари ҳам бор. Уларнинг орасида унга ишонмайдиганлари ҳам бордир. Парвардигорингиз бузғунчи кимсаларни жуда яхши билгувчидир'}, {'chapter': 10, 'verse': 41, 'text': 'وَإِن كَذَّبُوكَ فَقُل لِّي عَمَلِي وَلَكُمۡ عَمَلُكُمۡۖ أَنتُم بَرِيٓـُٔونَ مِمَّآ أَعۡمَلُ وَأَنَا۠ بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар улар сизни ёлғончи қилсалар, у ҳолда айтинг: «Менинг қилган амалим ўзим учун, сизларнинг амалингиз ўзингиз учундир. Сизлар менинг амалимдан поксизлар, мен эса сизлар қилаётган амалларингиздан покдирман»'}, {'chapter': 10, 'verse': 42, 'text': 'وَمِنۡهُم مَّن يَسۡتَمِعُونَ إِلَيۡكَۚ أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ وَلَوۡ كَانُواْ لَا يَعۡقِلُونَ\nУларнинг орасида сизга қулоқ тутадиганлари ҳам бор (аммо гаранг одамдек, ҳеч нарса англамайдилар). Ахир сиз, агар ўзлари ақл юргизмасалар, кар кимсаларга эшиттира олурмисиз'}, {'chapter': 10, 'verse': 43, 'text': 'وَمِنۡهُم مَّن يَنظُرُ إِلَيۡكَۚ أَفَأَنتَ تَهۡدِي ٱلۡعُمۡيَ وَلَوۡ كَانُواْ لَا يُبۡصِرُونَ\nУларнинг орасида сизга қараб турадиганлари ҳам бор (аммо кўр одамдек, сиздаги пайғамбарлик белгиларини кўрмайдилар). Ахир сиз, агар ўзлари кўрмасалар, кўр кимсаларни ҳидоят қила олурмисиз'}, {'chapter': 10, 'verse': 44, 'text': 'إِنَّ ٱللَّهَ لَا يَظۡلِمُ ٱلنَّاسَ شَيۡـٔٗا وَلَٰكِنَّ ٱلنَّاسَ أَنفُسَهُمۡ يَظۡلِمُونَ\nШубҳасиз, Аллоҳ инсонларга бирон зулм-адолатсизлик қилмас. Балки, инсонлар (Яратганга иймон келтирмасликлари билан) ўзларига ўзлари зулм қилурлар'}, {'chapter': 10, 'verse': 45, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ كَأَن لَّمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّنَ ٱلنَّهَارِ يَتَعَارَفُونَ بَيۡنَهُمۡۚ قَدۡ خَسِرَ ٱلَّذِينَ كَذَّبُواْ بِلِقَآءِ ٱللَّهِ وَمَا كَانُواْ مُهۡتَدِينَ\n(Аллоҳ) уларни (яъни, мушрикларни ҳисоб-китоб учун) тўплайдиган кунда улар (бу дунёда) гўёки кундуз куни бир соатгина тургандек (яшагандек) бир-бирларини танирлар. Аллоҳга рўбарў бўлишни ёлғон деган кимсалар муҳаққақ зиён қилдилар ва ҳақ йўлга юргувчи бўлмадилар'}, {'chapter': 10, 'verse': 46, 'text': 'وَإِمَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ ٱللَّهُ شَهِيدٌ عَلَىٰ مَا يَفۡعَلُونَ\nАгар уларга ваъда қилаётганимиз азобни сизга кўрсатсак ҳам (яъни, ҳаёт пайтингизда уларнинг кофир бўлганлари оқибатида тортадиган азобларини кўрсангиз ҳам), ёки (уларни азобга дучор қилишимиздан илгарироқ) сизни вафот қилдирсак ҳам, ҳар ҳолда Ўзимизга қайтишлари бордир. Қолаверса, Аллоҳ улар қилаётган барча ишларга гувоҳдир'}, {'chapter': 10, 'verse': 47, 'text': 'وَلِكُلِّ أُمَّةٖ رَّسُولٞۖ فَإِذَا جَآءَ رَسُولُهُمۡ قُضِيَ بَيۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ\nҲар бир миллат учун пайғамбар бўлур. Бас, қачон пайғамбарлари келганида, ўша (миллат)ларга зулм қилинмаган ҳолда, уларнинг ўрталарида адолат билан ҳукм қилинур'}, {'chapter': 10, 'verse': 48, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангизлар (айтинглар-чи), мана шу ваъда (қилинган азоб) қачон бўлади?!», дейдилар'}, {'chapter': 10, 'verse': 49, 'text': 'قُل لَّآ أَمۡلِكُ لِنَفۡسِي ضَرّٗا وَلَا نَفۡعًا إِلَّا مَا شَآءَ ٱللَّهُۗ لِكُلِّ أُمَّةٍ أَجَلٌۚ إِذَا جَآءَ أَجَلُهُمۡ فَلَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен агар Аллоҳ хоҳламаса (ўзгалар у ёқда турсин), ўзимга ҳам бирон зиён ё фойда етказишга қодир эмасман. (Бинобарин, келажак, ғайб илмларидан ҳам хабардор эмасман, лекин) ҳар бир миллат-авлод учун бир ажал бордир. Қачон ажаллари етса, уни бирон соат кетга ҳам сура олмайдилар, илгари ҳам қила олмайдилар'}, {'chapter': 10, 'verse': 50, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُهُۥ بَيَٰتًا أَوۡ نَهَارٗا مَّاذَا يَسۡتَعۡجِلُ مِنۡهُ ٱلۡمُجۡرِمُونَ\nАйтинг: «Хабар берингиз-чи, агар У зотнинг азоби сизларга кечаси ё кундузи келса (бундан сизларга не фойда?) Бу жиноятчи кимсалар (яъни, мушриклар) у азобнинг нимасини шоштирмоқдалар-а'}, {'chapter': 10, 'verse': 51, 'text': 'أَثُمَّ إِذَا مَا وَقَعَ ءَامَنتُم بِهِۦٓۚ ءَآلۡـَٰٔنَ وَقَدۡ كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ\nЎшанда (Аллоҳнинг азоби бошингизга) тушганида (сизларга): «Унга иймон келтирдингизлар? Энди-я?! Ахир (азоб тушишига ишонмай) уни шоштираётган эдингизлар-ку?!» (дейилур)'}, {'chapter': 10, 'verse': 52, 'text': 'ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلۡخُلۡدِ هَلۡ تُجۡزَوۡنَ إِلَّا بِمَا كُنتُمۡ تَكۡسِبُونَ\nСўнгра ўзларига зулм қилган бу кимсаларга айтилур: «Мангу азобини тотингиз! Сизлар фақат ўзларингиз касб қилган гуноҳларингиз сабабли жазоланмоқдасиз»'}, {'chapter': 10, 'verse': 53, 'text': '۞وَيَسۡتَنۢبِـُٔونَكَ أَحَقٌّ هُوَۖ قُلۡ إِي وَرَبِّيٓ إِنَّهُۥ لَحَقّٞۖ وَمَآ أَنتُم بِمُعۡجِزِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиздан: «У (ваъда қилинган азоб) ҳақми?», деб сўрайдилар. Айтинг: «Ҳа, Парвардигоримга қасамки, албатта у ҳақдир. Сизлар (ундан) қочиб қутулгувчи эмассизлар»'}, {'chapter': 10, 'verse': 54, 'text': 'وَلَوۡ أَنَّ لِكُلِّ نَفۡسٖ ظَلَمَتۡ مَا فِي ٱلۡأَرۡضِ لَٱفۡتَدَتۡ بِهِۦۗ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۖ وَقُضِيَ بَيۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ\nУлар азобни кўрган вақтларида агар ўзига жабр қилган (яъни, кофир бўлиб ўтган) ҳар бир жон учун Ердаги бор нарса бўлса, у (ўша азобдан қутулиш учун) албатта уни (бор нарсасини) фидо қилган ва (иймон келтирмагани учун) афсус-надоматлар чеккан бўлур эди. (У кунда) уларга зулм қилинмаган ҳолда ўрталарида адолат билан ҳукм қилинур. (Яъни, бу дунёдаги қилмишларига яраша жазоланурлар)'}, {'chapter': 10, 'verse': 55, 'text': 'أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ أَلَآ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nОгоҳ бўлингизким, осмонлар ва Ердаги нарсалар, шубҳасиз, Аллоҳникидир. Огоҳ бўлингизким, албатта Аллоҳнинг ваъдаси ҳаққи-рост ваъдадир. Лекин уларнинг кўплари билмайдилар'}, {'chapter': 10, 'verse': 56, 'text': 'هُوَ يُحۡيِۦ وَيُمِيتُ وَإِلَيۡهِ تُرۡجَعُونَ\nУ тирилтирур ва ўлдирур. Ва Унгагина қайтарилурсизлар'}, {'chapter': 10, 'verse': 57, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَتۡكُم مَّوۡعِظَةٞ مِّن رَّبِّكُمۡ وَشِفَآءٞ لِّمَا فِي ٱلصُّدُورِ وَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ\nЭй инсонлар, сизларга Парвардигорингиз томонидан панд-насиҳат, дилларингиздаги (бузуқ эътиқодлардан иборат) нарсаларга шифо ва иймон келтирган зотларга ҳидоят ва раҳмат (яъни, Қуръон) келди'}, {'chapter': 10, 'verse': 58, 'text': 'قُلۡ بِفَضۡلِ ٱللَّهِ وَبِرَحۡمَتِهِۦ فَبِذَٰلِكَ فَلۡيَفۡرَحُواْ هُوَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Аллоҳнинг фазлу марҳамати (яъни, Ислом) ва раҳмат-меҳрибонлиги (яъни, Қуръон) билан — мана шу (неъмат) билан шод-хуррам бўлсинлар. (Зеро), бу улар тўплайдиган мол-дунёларидан яхшироқдир»'}, {'chapter': 10, 'verse': 59, 'text': 'قُلۡ أَرَءَيۡتُم مَّآ أَنزَلَ ٱللَّهُ لَكُم مِّن رِّزۡقٖ فَجَعَلۡتُم مِّنۡهُ حَرَامٗا وَحَلَٰلٗا قُلۡ ءَآللَّهُ أَذِنَ لَكُمۡۖ أَمۡ عَلَى ٱللَّهِ تَفۡتَرُونَ\nАйтинг: «Хабар берингиз-чи (эй мушриклар), Аллоҳ сизлар учун нозил қилган ризқу рўзнинг (айримларини) ҳаром, айримларини) ҳалол қилиб олдингиз». Айтинг: «Хабар берингиз-чи, (бундай қилиш учун) сизларга Аллоҳ изн бердими ёки Аллоҳ шаънига бўҳтон қилмоқдамисизлар?!»'}, {'chapter': 10, 'verse': 60, 'text': 'وَمَا ظَنُّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ يَوۡمَ ٱلۡقِيَٰمَةِۗ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ\nАллоҳ шаънига ёлғон тўқийдиган кимсалар Қиёмат Куни ҳақида қандай гумон қилар эканлар-а? Ҳеч шак-шубҳасиз, Аллоҳ одамларга фазлу марҳамат Соҳибидир, лекин уларнинг кўплари шукр қилмайдилар'}, {'chapter': 10, 'verse': 61, 'text': 'وَمَا تَكُونُ فِي شَأۡنٖ وَمَا تَتۡلُواْ مِنۡهُ مِن قُرۡءَانٖ وَلَا تَعۡمَلُونَ مِنۡ عَمَلٍ إِلَّا كُنَّا عَلَيۡكُمۡ شُهُودًا إِذۡ تُفِيضُونَ فِيهِۚ وَمَا يَعۡزُبُ عَن رَّبِّكَ مِن مِّثۡقَالِ ذَرَّةٖ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ وَلَآ أَصۡغَرَ مِن ذَٰلِكَ وَلَآ أَكۡبَرَ إِلَّا فِي كِتَٰبٖ مُّبِينٍ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз қандай иш (билан) машғул бўлманг ва у (иш) ҳақида Қуръондан бирон оят ўқиманг, (эй инсонлар), сизлар қандай амални қилманглар, ўша ишларга киришган пайтингизда, албатта Биз Сизларнинг устингизда гувоҳ бўлурмиз. Еру осмондаги бир зарра мисоличалик, ундан ҳам кичикроқ (ёки) каттароқ бирон нарса Парвардигорингиздан махфий бўлмас — албатта, очиқ Китобда (яъни, Лавҳул-Маҳфузда) мавжуд бўлур'}, {'chapter': 10, 'verse': 62, 'text': 'أَلَآ إِنَّ أَوۡلِيَآءَ ٱللَّهِ لَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nОгоҳ бўлингизким, албатта Аллоҳнинг дўстларига (Охиратда) бирон хавф-хатар йўқдир ва улар ғамгин бўлмайдилар'}, {'chapter': 10, 'verse': 63, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nУлар иймон келтирган ва (Аллоҳдан) қўрқувчи бўлган зотлардир'}, {'chapter': 10, 'verse': 64, 'text': 'لَهُمُ ٱلۡبُشۡرَىٰ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۚ لَا تَبۡدِيلَ لِكَلِمَٰتِ ٱللَّهِۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nУлар учун ҳаёти дунёда ҳам, Охиратда ҳам хушхабар бордир. Аллоҳнинг сўзлари (оятлари) ўзгартирилмас. Мана шу (мазкур неъматларга эришиш) буюк саодатдир'}, {'chapter': 10, 'verse': 65, 'text': 'وَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِيعًاۚ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nУларнинг (кофирларнинг «Сен пайғамбар эмассан», деган) сўзлари сизни ғамгин қилмасин. Бутун куч-қудрат Аллоҳникидир. У эшитгувчи, билгувчидир'}, {'chapter': 10, 'verse': 66, 'text': 'أَلَآ إِنَّ لِلَّهِ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِۗ وَمَا يَتَّبِعُ ٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ شُرَكَآءَۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ\nОгоҳ бўлингизким, албатта осмонлардаги ва Ердаги барча жонзот ва махлуқот Аллоҳникидир. Аллоҳни қўйиб, бутларга илтижо қиладиган кимсалар фақат гумонга эргашадилар, холос. Улар фақат тахмин қиладилар, холос'}, {'chapter': 10, 'verse': 67, 'text': 'هُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ\nУ сизлар ором олишингиз учун кечани ва (касбу кор, сайру саёҳат қилишингиз учун) очиқ-ёруғ бўлган кундузни яратиб қўйган Зотдир. Албатта, бунда қулоқ тутиб англовчи қавм учуч оят-аломатлар бордир'}, {'chapter': 10, 'verse': 68, 'text': 'قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ هُوَ ٱلۡغَنِيُّۖ لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ إِنۡ عِندَكُم مِّن سُلۡطَٰنِۭ بِهَٰذَآۚ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ\n«Аллоҳнинг боласи бор», дедилар. У зот (буларнинг бадгумонидан) мутлақо покдир. У беҳожатдир. Осмонлар ва Ердаги бор нарсалар Уникидир. Сизларнинг ҳузурингизда бу тўғрида (яъни, Аллоҳнинг боласи бор, деган) ҳеч қандай ҳужжат йўқ-ку! Аллоҳ шаънига билмайдиган нарсангизни айтаверасизларми'}, {'chapter': 10, 'verse': 69, 'text': 'قُلۡ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Шубҳасиз, Аллоҳ шаънига ёлғон тўқийдиган кимсалар нажот топмаслар»'}, {'chapter': 10, 'verse': 70, 'text': 'مَتَٰعٞ فِي ٱلدُّنۡيَا ثُمَّ إِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ نُذِيقُهُمُ ٱلۡعَذَابَ ٱلشَّدِيدَ بِمَا كَانُواْ يَكۡفُرُونَ\n(Ундай кимсалар учун) бу дунёда озгина фойдаланиш бор. Сўнгра Бизга қайтишади. Ана ундан кейин кофир бўлиб ўтганлари учун уларга қаттиқ азоб торттирурмиз'}, {'chapter': 10, 'verse': 71, 'text': '۞وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ نُوحٍ إِذۡ قَالَ لِقَوۡمِهِۦ يَٰقَوۡمِ إِن كَانَ كَبُرَ عَلَيۡكُم مَّقَامِي وَتَذۡكِيرِي بِـَٔايَٰتِ ٱللَّهِ فَعَلَى ٱللَّهِ تَوَكَّلۡتُ فَأَجۡمِعُوٓاْ أَمۡرَكُمۡ وَشُرَكَآءَكُمۡ ثُمَّ لَا يَكُنۡ أَمۡرُكُمۡ عَلَيۡكُمۡ غُمَّةٗ ثُمَّ ٱقۡضُوٓاْ إِلَيَّ وَلَا تُنظِرُونِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларга Нуҳ ҳақидаги хабарни тиловат килинг, у қавмига (бундай) деган эди: «Эй қавмим, агар сизларга (орангизда узоқ йиллар яшаб) турганлигим ва Аллоҳ оятларини эслатишим оғирлик қилган бўлса, бас, мен Аллоҳнинг Ўзига суяндим — таваккул қилдим. Энди бутларингиз билан бирга билган ишларингизни қилаверинглар. Кейин қилаёттан ишларингиз ўзларингизга махфий бўлиб қолмасин (яъни, бемалол, ошкор иш тутаверинглар, мен парво қилмайман), сўнгра менга нисбатан (хоҳлаган) ҳукмингизни ижро этаверинглар ва менга муҳлат ҳам берманглар'}, {'chapter': 10, 'verse': 72, 'text': 'فَإِن تَوَلَّيۡتُمۡ فَمَا سَأَلۡتُكُم مِّنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ\nБас, агар (менинг қилган панд-насиҳатимдан) юз ўгирар экансизлар, мен сизлардан (унинг учун) бирор ажр-ҳақ сўраганим йўқ (яъни, менга ҳақ беришни истамаганликдан эмас, балки бадбахтликларинг туфайли юз ўгирмоқдасизлар). Мен ажр — савобни фақат Аллоҳдан (сўрайман). Ва мен Унга бўйинсунувчи — мусулмонлардан бўлишга маъмурман (амр этилганман)»'}, {'chapter': 10, 'verse': 73, 'text': 'فَكَذَّبُوهُ فَنَجَّيۡنَٰهُ وَمَن مَّعَهُۥ فِي ٱلۡفُلۡكِ وَجَعَلۡنَٰهُمۡ خَلَـٰٓئِفَ وَأَغۡرَقۡنَا ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُنذَرِينَ\nСўнгра уни (Нуҳни) ёлғончи қилишгач, Биз унга ва у билан кемада бирга бўлган кишиларга нажот бердик ва уларни (ғарқ бўлиб кетганларнинг ўрнига Ерда) халифа килдик. Бизнинг оятларимизни ёлғон деган кимсаларни эса ғарқ қилдик. (Эй Муҳаммад алайҳис-салоту вас-салом), мана (кофирликлари учун дўзах азоби билан) қўрқитилган кимсаларнинг оқибати қандай бўлганини кўринг'}, {'chapter': 10, 'verse': 74, 'text': 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِۦ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِٱلۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ بِهِۦ مِن قَبۡلُۚ كَذَٰلِكَ نَطۡبَعُ عَلَىٰ قُلُوبِ ٱلۡمُعۡتَدِينَ\nСўнгра (Нуҳдан) кейин ҳам (кўп) пайғамбарларни ўз қавмларига юбордик. Бас, у пайғамбарлар уларга очиқ ҳужжат-мўъжизалар келтирганларида, улар илгари ёлғон деган нарсаларига (пайғамбарларнинг мўъжизаларини кўрганларидан кейин ҳам) иймон келтирувчи бўлмадилар. Тажовузкор кимсаларнинг дилларини мана шундай муҳрлаб қўюрмиз'}, {'chapter': 10, 'verse': 75, 'text': 'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ وَهَٰرُونَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ بِـَٔايَٰتِنَا فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ\nСўнгра уларнинг ортидан Мусо билан Ҳорунни Ўз оят-мўъжизаларимиз билан Фиръавн ва унинг одамларига юборганимизда, улар кибру ҳаво қилдилар ва жиноятчи-гуноҳкор қавм бўлдилар'}, {'chapter': 10, 'verse': 76, 'text': 'فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُوٓاْ إِنَّ هَٰذَا لَسِحۡرٞ مُّبِينٞ\nБас, қачонки уларга Бизнинг ҳузуримиздан Ҳақиқат келганида, улар: «Бу очиқ сеҳр», дедилар'}, {'chapter': 10, 'verse': 77, 'text': 'قَالَ مُوسَىٰٓ أَتَقُولُونَ لِلۡحَقِّ لَمَّا جَآءَكُمۡۖ أَسِحۡرٌ هَٰذَا وَلَا يُفۡلِحُ ٱلسَّـٰحِرُونَ\nМусо деди: «Сизларга ҳақиқат келган вақтида («Бу сеҳр»), дейсизларми? Сеҳрми шу? Ахир сеҳргарлар муваффақият қозонолмайдилар-ку?!»'}, {'chapter': 10, 'verse': 78, 'text': 'قَالُوٓاْ أَجِئۡتَنَا لِتَلۡفِتَنَا عَمَّا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَا وَتَكُونَ لَكُمَا ٱلۡكِبۡرِيَآءُ فِي ٱلۡأَرۡضِ وَمَا نَحۡنُ لَكُمَا بِمُؤۡمِنِينَ\nУлар дедилар: «Сен бизларни ота-боболаримизни амал қилган ҳолда топган динимиздан буриш учун ва икковингиз еримизга эга бўлиб олиш учун келдингми?! Биз сизларга иймон келтиргувчи эмасмиз!»'}, {'chapter': 10, 'verse': 79, 'text': 'وَقَالَ فِرۡعَوۡنُ ٱئۡتُونِي بِكُلِّ سَٰحِرٍ عَلِيمٖ\nФиръавн ўз одамларига: «Барча билимдон сеҳргарларни келтирингиз!», деди'}, {'chapter': 10, 'verse': 80, 'text': 'فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالَ لَهُم مُّوسَىٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ\nБас, сеҳргарлар келганларида, Мусо уларга: «Ташламоқчи бўлган нарсаларингизни (яъни, асо ва арқонларингизни) ташланглар», деди'}, {'chapter': 10, 'verse': 81, 'text': 'فَلَمَّآ أَلۡقَوۡاْ قَالَ مُوسَىٰ مَا جِئۡتُم بِهِ ٱلسِّحۡرُۖ إِنَّ ٱللَّهَ سَيُبۡطِلُهُۥٓ إِنَّ ٱللَّهَ لَا يُصۡلِحُ عَمَلَ ٱلۡمُفۡسِدِينَ\nҚачонки улар (қўлларидаги нарсаларини) ташлаганларида (ва у арқон, асолар илонларга айланиб қолганида) Мусо деди: «(Мана шу) сизлар қилган иш сеҳрдир. Албатта, Аллоҳ уни ботил қилур. Зеро, Аллоҳ бузғунчи кимсаларнинг ишини ўнгламайди'}, {'chapter': 10, 'verse': 82, 'text': 'وَيُحِقُّ ٱللَّهُ ٱلۡحَقَّ بِكَلِمَٰتِهِۦ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ\nВа гарчи жиноятчи кимсалар истамасалар-да, Аллоҳ ўз сўзлари (яъни, амру фармонлари) билан ҳақиқатни рўёбга чиқарур»'}, {'chapter': 10, 'verse': 83, 'text': 'فَمَآ ءَامَنَ لِمُوسَىٰٓ إِلَّا ذُرِّيَّةٞ مِّن قَوۡمِهِۦ عَلَىٰ خَوۡفٖ مِّن فِرۡعَوۡنَ وَمَلَإِيْهِمۡ أَن يَفۡتِنَهُمۡۚ وَإِنَّ فِرۡعَوۡنَ لَعَالٖ فِي ٱلۡأَرۡضِ وَإِنَّهُۥ لَمِنَ ٱلۡمُسۡرِفِينَ\nШунда Мусо қавмидан фақат бир тоифа одам Фиръавн ва унинг одамларининг фитна-фасодларидан қўрққан ҳолда иймон келтирдилар. Чунки Фиръавн у ерда ғолиб (эди) ва у, шубҳасиз, ҳаддан ошувчи кимсалардандир'}, {'chapter': 10, 'verse': 84, 'text': 'وَقَالَ مُوسَىٰ يَٰقَوۡمِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ فَعَلَيۡهِ تَوَكَّلُوٓاْ إِن كُنتُم مُّسۡلِمِينَ\nМусо у (иймон келтирган кишиларга) деди: «Эй қавмим, агар Аллоҳга иймон келтирар экансизлар, демак, мусулмон бўлсангизлар, Унинг Ўзигагина суянинглар — таваккул қилинглар!»'}, {'chapter': 10, 'verse': 85, 'text': 'فَقَالُواْ عَلَى ٱللَّهِ تَوَكَّلۡنَا رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, улар айтдилар: «Аллоҳнинг Ўзига таваккул қилдик. Парвардигоро, бизларни бу золим қавмга мафтун — алданувчи қилиб қўйма'}, {'chapter': 10, 'verse': 86, 'text': 'وَنَجِّنَا بِرَحۡمَتِكَ مِنَ ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ\nВа Ўзинг раҳм айлаб бизларни бу кофир қавмдан қутқар»'}, {'chapter': 10, 'verse': 87, 'text': 'وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰ وَأَخِيهِ أَن تَبَوَّءَا لِقَوۡمِكُمَا بِمِصۡرَ بُيُوتٗا وَٱجۡعَلُواْ بُيُوتَكُمۡ قِبۡلَةٗ وَأَقِيمُواْ ٱلصَّلَوٰةَۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nБиз Мусо ва унинг биродарига: «Қавмларингиз учун Мисрда уйлар тайёрланглар ва бу уйларингизни қибла-намозгоҳ қилиб (у уйларда) намозни тўкис адо қилинглар. (Эй Мусо), иймон келтирган зотларга (тез орада ғалаба қилишлари ҳақида) хушхабар беринг», деб ваҳий юбордик'}, {'chapter': 10, 'verse': 88, 'text': 'وَقَالَ مُوسَىٰ رَبَّنَآ إِنَّكَ ءَاتَيۡتَ فِرۡعَوۡنَ وَمَلَأَهُۥ زِينَةٗ وَأَمۡوَٰلٗا فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا رَبَّنَا لِيُضِلُّواْ عَن سَبِيلِكَۖ رَبَّنَا ٱطۡمِسۡ عَلَىٰٓ أَمۡوَٰلِهِمۡ وَٱشۡدُدۡ عَلَىٰ قُلُوبِهِمۡ فَلَا يُؤۡمِنُواْ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nМусо деди: «Парвардигоро, дарҳақиқат, Сен Фиръавн ва унинг одамларига ҳаёти дунёда зеб-зийнат ва мол-дунё ато этдинг. Парвардигоро, улар (мана шу мол-давлатлари билан одамларни) Сенинг йўлингдан оздиришлари учун шундай қилдинг. Парвардигоро, уларнинг мол-дунёларини йўқ қилгин, кўнгилларини қаттиқ қилгин, токи аламли азобни кўрмагунларича, иймон келтирмасинлар'}, {'chapter': 10, 'verse': 89, 'text': 'قَالَ قَدۡ أُجِيبَت دَّعۡوَتُكُمَا فَٱسۡتَقِيمَا وَلَا تَتَّبِعَآنِّ سَبِيلَ ٱلَّذِينَ لَا يَعۡلَمُونَ\n(Аллоҳ) деди: «Дуоларингиз ижобат бўлди. Энди Тўғри Йўлда собит бўлинглар ва ҳатто ҳаргиз билмайдиган кимсаларнинг йўлига эргашманглар!»'}, {'chapter': 10, 'verse': 90, 'text': '۞وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡبَحۡرَ فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ وَجُنُودُهُۥ بَغۡيٗا وَعَدۡوًاۖ حَتَّىٰٓ إِذَآ أَدۡرَكَهُ ٱلۡغَرَقُ قَالَ ءَامَنتُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱلَّذِيٓ ءَامَنَتۡ بِهِۦ بَنُوٓاْ إِسۡرَـٰٓءِيلَ وَأَنَا۠ مِنَ ٱلۡمُسۡلِمِينَ\nБиз Бани Исроилни денгиздан ўтказганимиздан кейин уларга зулму зўравонлик қилиш учун Фиръавн ва унинг лашкари қувиб етди. Энди унга (Фиръавнга денгизда) ғарқ бўлиш (пайти) етганида эса, у деди: «Ҳеч қандай илоҳ йўқ, магар Бани Исроил иймон келтирган Зот — Аллоҳгина борлигига иймон келтирдим. Мен мусулмонлардандирман — Аллоҳга бўйинсунувчилардандирман»'}, {'chapter': 10, 'verse': 91, 'text': 'ءَآلۡـَٰٔنَ وَقَدۡ عَصَيۡتَ قَبۡلُ وَكُنتَ مِنَ ٱلۡمُفۡسِدِينَ\nЭнди-ми?! Ахир сен илгари (яъни, шу пайтгача) итоатсизлик қилган ва бузғунчи кимсалардан бўлган эдинг-ку'}, {'chapter': 10, 'verse': 92, 'text': 'فَٱلۡيَوۡمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنۡ خَلۡفَكَ ءَايَةٗۚ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ عَنۡ ءَايَٰتِنَا لَغَٰفِلُونَ\nМана бугун ўзингдан кейинги кишиларга оят — ибрат бўлишинг учун сенинг жасадингни қутқарурмиз. Дарҳақиқат, кўп одамлар Бизнинг оятларимиздан ғофилдирлар (яъни, улардан ибрат олмайдилар)'}, {'chapter': 10, 'verse': 93, 'text': 'وَلَقَدۡ بَوَّأۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ مُبَوَّأَ صِدۡقٖ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ فَمَا ٱخۡتَلَفُواْ حَتَّىٰ جَآءَهُمُ ٱلۡعِلۡمُۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nШубҳасиз, Биз Бани Исроилни гўзал манзилга жойлаштирдик ва уларга ҳалол-пок нарсалардан ризқу рўз бердик. Сўнг улар то билим (яъни, Таврот) келгунича ихтилоф қилмадилар. Албатта, Парвардигорингиз Қиёмат Кунида талашиб-тортишган нарсалари ҳақида уларнинг ўртасида Ўзи ҳакамлик қилур'}, {'chapter': 10, 'verse': 94, 'text': 'فَإِن كُنتَ فِي شَكّٖ مِّمَّآ أَنزَلۡنَآ إِلَيۡكَ فَسۡـَٔلِ ٱلَّذِينَ يَقۡرَءُونَ ٱلۡكِتَٰبَ مِن قَبۡلِكَۚ لَقَدۡ جَآءَكَ ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар Биз сизга нозил этган нарсадан (яъни, илгари ўтган қавмлар ҳақида нозил қилган оятларимиздан) шубҳада бўлсангиз, у ҳолда ўзингиздан аввалги Китоб (яъни, Таврот ёки Инжилни) ўқийдиган кишилардан сўранг! Аниқки, сизга Парвардигорингиз томонидан Ҳақ — Қуръон келди. Бас, ҳаргиз шубҳа қилгувчилардан бўлманг'}, {'chapter': 10, 'verse': 95, 'text': 'وَلَا تَكُونَنَّ مِنَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ فَتَكُونَ مِنَ ٱلۡخَٰسِرِينَ\nВа ҳаргиз Аллоҳнинг оятларимни ёлғон дейдиганлардан бўлмангки, у ҳолда зиёнкорлардан бўлиб қолурсиз'}, {'chapter': 10, 'verse': 96, 'text': 'إِنَّ ٱلَّذِينَ حَقَّتۡ عَلَيۡهِمۡ كَلِمَتُ رَبِّكَ لَا يُؤۡمِنُونَ\nАлбатта, зиммаларига Парвардигорингизнинг Сўзи (яъни, азоби) вожиб бўлган кимсалар иймон келтирмайдилар'}, {'chapter': 10, 'verse': 97, 'text': 'وَلَوۡ جَآءَتۡهُمۡ كُلُّ ءَايَةٍ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nГарчи уларга барча оят-мўъжизалар келса-да, то аламли азобни кўрмагунларича (иймон келтирмайдилар)'}, {'chapter': 10, 'verse': 98, 'text': 'فَلَوۡلَا كَانَتۡ قَرۡيَةٌ ءَامَنَتۡ فَنَفَعَهَآ إِيمَٰنُهَآ إِلَّا قَوۡمَ يُونُسَ لَمَّآ ءَامَنُواْ كَشَفۡنَا عَنۡهُمۡ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ\nҚани энди (биз ҳалок қилган қишлоқлар ичида) бирон қишлоқ (аҳли ўз пайғамбарлари айтган азобни сезган вақтларида) иймон келтирсалар эди, албатта иймонлари фойда қилган (яъни, ҳалок бўлмаган) бўлар эди. Фақат Юнус қавмигина (шундай қилди). Иймон келтиришгач, улардан ҳаёти дунёдаги расволик азобини кетказдик ва уларни маълум бир вақтгача (яъни, ўз ажаллари билан вафот этгунларича) фойдалантирдик'}, {'chapter': 10, 'verse': 99, 'text': 'وَلَوۡ شَآءَ رَبُّكَ لَأٓمَنَ مَن فِي ٱلۡأَرۡضِ كُلُّهُمۡ جَمِيعًاۚ أَفَأَنتَ تُكۡرِهُ ٱلنَّاسَ حَتَّىٰ يَكُونُواْ مُؤۡمِنِينَ\nАгар Парвардигорингиз хоҳласа эди, бутун Ер юзидаги барча кишилар иймон келтирган бўлур эдилар. Ахир сиз одамларни мўмин бўлишга мажбур қилурмисиз? (Яъни, бундай қилиш дуруст эмасдир. Чунки киши ўз ихтиёри билан келтирган иймонгина ҳақиқий мақбул иймондир)'}, {'chapter': 10, 'verse': 100, 'text': 'وَمَا كَانَ لِنَفۡسٍ أَن تُؤۡمِنَ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَيَجۡعَلُ ٱلرِّجۡسَ عَلَى ٱلَّذِينَ لَا يَعۡقِلُونَ\nАллоҳнинг изну иродасисиз ҳеч бир жон мўмин бўла олмас. У (Аллоҳ) ақл юргизмайдиган кимсаларни азобга дучор қилур'}, {'chapter': 10, 'verse': 101, 'text': 'قُلِ ٱنظُرُواْ مَاذَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَا تُغۡنِي ٱلۡأٓيَٰتُ وَٱلنُّذُرُ عَن قَوۡمٖ لَّا يُؤۡمِنُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, Макка мушрикларига) айтинг: «Осмонлар ва Ердаги нарсаларни (яъни, ҳамма нарсанинг ягона яратувчиси бор эканлигига далолат қилувчи белгиларни) кузатинглар. (Чунки) иймон келтирмайдиган қавм учун (ҳеч қандай) оят-мўъжизалар ва қўрқитувлар фойда бермас'}, {'chapter': 10, 'verse': 102, 'text': 'فَهَلۡ يَنتَظِرُونَ إِلَّا مِثۡلَ أَيَّامِ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِهِمۡۚ قُلۡ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ\nУлар фақат ўзларидан илгари ўтган (кофир) кимсаларнинг кунига ўхшаш (бир кунни, яъни Қиёматни, Аллоҳнинг азоби)ни кутмоқдалар, холос. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Кутаверинглар. Мен ҳам сизлар билан бирга кутгувчиларданман»'}, {'chapter': 10, 'verse': 103, 'text': 'ثُمَّ نُنَجِّي رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ حَقًّا عَلَيۡنَا نُنجِ ٱلۡمُؤۡمِنِينَ\nСўнгра (яъни, Қиёмат келиб, азоб нозил бўлгач), пайғамбарларимиз ва иймон келтирган зотларга нажот берурмиз. Шундай қилиб, (охир-оқибатда) иймон келтирган зотларга нажот бериш Бизнинг зиммамиздаги бурч бўлди'}, {'chapter': 10, 'verse': 104, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِي شَكّٖ مِّن دِينِي فَلَآ أَعۡبُدُ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَلَٰكِنۡ أَعۡبُدُ ٱللَّهَ ٱلَّذِي يَتَوَفَّىٰكُمۡۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nАйтинг: «Эй инсонлар, агар сизлар менинг динимдан шак-шубҳада бўлсангиз, бас (билингизки), мен сизлар Аллоҳни қўйиб сиғинадиган бутларингизга ибодат қилмайман, балки сизларнинг жонингизни оладиган Зот — Аллоҳга ибодат қилурман. Ва мен мўминлардан бўлишга маъмурман (амр этилганман)'}, {'chapter': 10, 'verse': 105, 'text': 'وَأَنۡ أَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗا وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ\nВа (менга буюрилганки), ўзингизни доимо тўғри бўлган динда (Исломда) тутинг, ҳаргиз мушриклардан бўлманг'}, {'chapter': 10, 'verse': 106, 'text': 'وَلَا تَدۡعُ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكَ وَلَا يَضُرُّكَۖ فَإِن فَعَلۡتَ فَإِنَّكَ إِذٗا مِّنَ ٱلظَّـٰلِمِينَ\nВа Аллоҳни қуйиб, сизга фойда ҳам, зиён ҳам етказа олмайдиган нарсага илтижо қилманг! Бас, агар шундай қилсангиз, у ҳолда албатта золимлардандирсиз'}, {'chapter': 10, 'verse': 107, 'text': 'وَإِن يَمۡسَسۡكَ ٱللَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يُرِدۡكَ بِخَيۡرٖ فَلَا رَآدَّ لِفَضۡلِهِۦۚ يُصِيبُ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nАгар Аллоҳ сизга бирон зиён етказса, уни фақат Ўзигина кетказа олур. Агар сизга бирон яхшилик (етказишни) ирода қилса, Унинг фазлу марҳаматини қайтара олгувчи йўқдир. У Ўзи хоҳлаган бандаларига яхшилик етказур. У мағфиратли, меҳрибондир'}, {'chapter': 10, 'verse': 108, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُمُ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنَا۠ عَلَيۡكُم بِوَكِيلٖ\nАйтинг: «Эй инсонлар, сизларга Парвардигорингиздан Ҳақ Қуръон келди. Бас, ким ҳидоят йўлига юрса, фақат ўз фойдасига юрган бўлур. Ким (у ҳидоят йўлидан) озса, фақат ўзининг зиёнига адашган бўлур. Мен сизларнинг устингизда қўриқчи эмасман»'}, {'chapter': 10, 'verse': 109, 'text': 'وَٱتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ وَٱصۡبِرۡ حَتَّىٰ يَحۡكُمَ ٱللَّهُۚ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ўзингизга ваҳий қилиб юборилаётган нарсага (яъни, амру фармонларга) эргашинг ва то Аллоҳ Ўз ҳукмини туширгунича, (даъват йўлидаги машаққатларга ва кофирлар томонидан етадиган озорларга) сабр қилинг. У зот ҳукм қилгувчиларнинг энг яхшисидир'}, {'chapter': 11, 'verse': 1, 'text': 'الٓرۚ كِتَٰبٌ أُحۡكِمَتۡ ءَايَٰتُهُۥ ثُمَّ فُصِّلَتۡ مِن لَّدُنۡ حَكِيمٍ خَبِيرٍ\nАлиф, Лом, Ро. (Ушбу Китоб) ҳикмат Соҳиби ва (ҳамма нарсадан) хабардор Зот — Аллоҳ томонидан оятлари бузилмайдиган мустаҳкам ва муфассал қилиб (нозил қилинган) бир Китобдир'}, {'chapter': 11, 'verse': 2, 'text': 'أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۚ إِنَّنِي لَكُم مِّنۡهُ نَذِيرٞ وَبَشِيرٞ\nСизлар фақат Аллоҳгагина ибодат қилишингиз учун (нозил қилинган бир Китобдир). (Эй Муҳаммад алайҳис-салоту вас-салом, айтинг): Албатта, мен эса сизларга У зот тарафидан (юборилган, кофирларни дўзах азобидан) огоҳлантиргувчи ва (мўминларга жаннат ҳақида) хушхабар элтгувчидирман'}, {'chapter': 11, 'verse': 3, 'text': 'وَأَنِ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُمَتِّعۡكُم مَّتَٰعًا حَسَنًا إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَيُؤۡتِ كُلَّ ذِي فَضۡلٖ فَضۡلَهُۥۖ وَإِن تَوَلَّوۡاْ فَإِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ كَبِيرٍ\n(Аллоҳ сизларга амр қилурки), Парвардигорингиздан мағфират сўранглар, сўнгра Унинг Ўзига тавба қилинглар, шунда (У) сизларни маълум муддатгача (ажалларингиз етгунича) чиройли мато — фойдаланиш билан фойдалантирур ва ҳар бир яхшилик соҳибига яхшилик (яъни, ажр — мукофот) берур. Агар юз ўгирсангизлар, у ҳолда Мен сизларга Улуғ Кун (яъни, Қиёмат Куни) азоби етишидан қўрқаман'}, {'chapter': 11, 'verse': 4, 'text': 'إِلَى ٱللَّهِ مَرۡجِعُكُمۡۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nАллоҳнинг Ўзига қайтишларингиз бор. У эса ҳамма нарсага қодирдир'}, {'chapter': 11, 'verse': 5, 'text': 'أَلَآ إِنَّهُمۡ يَثۡنُونَ صُدُورَهُمۡ لِيَسۡتَخۡفُواْ مِنۡهُۚ أَلَا حِينَ يَسۡتَغۡشُونَ ثِيَابَهُمۡ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nОгоҳ бўлингизким, албатта улар (кофирлар) Ундан сир тутиш учун дилларидаги (адоватлари)ни яширадилар. Огоҳ бўлсинларким, улар кийимларига ўраниб олган чоғларида ҳам (Аллоҳ) уларнинг яширган ва ошкор қилган нарсаларини билур. Дарҳақиқат, У зот дилларни эгаллаган сирларни билгувчидир'}, {'chapter': 11, 'verse': 6, 'text': '۞وَمَا مِن دَآبَّةٖ فِي ٱلۡأَرۡضِ إِلَّا عَلَى ٱللَّهِ رِزۡقُهَا وَيَعۡلَمُ مُسۡتَقَرَّهَا وَمُسۡتَوۡدَعَهَاۚ كُلّٞ فِي كِتَٰبٖ مُّبِينٖ\nЎрмалаган нарса борки, барчасининг ризқи Аллоҳнинг зиммасидадир. У зот уларнинг турар жойларини ҳам, борар жойларини ҳам билур. Ҳамма нарса очиқ-равшан Китобда бордир'}, {'chapter': 11, 'verse': 7, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ وَكَانَ عَرۡشُهُۥ عَلَى ٱلۡمَآءِ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۗ وَلَئِن قُلۡتَ إِنَّكُم مَّبۡعُوثُونَ مِنۢ بَعۡدِ ٱلۡمَوۡتِ لَيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nУ (Аллоҳ) сизларнинг қайсиларингиз яхшироқ иш — амал қилиб яшашингизни синаш учун олти кунда осмонлар ва Ерни яратган Зотдир. Илгари У зотнинг арши сув устида (яъни, у арш остида сувдан бўлак ҳеч нарса яратилмаган эди). (Эй Муҳаммад алайҳис-салоту вас-салом), қасамки, агар сиз: «Шак-шубҳасиз, ўлганингиздан кейин қайта тирилурсизлар», десангиз, албатта кофир бўлган кимсалар: «Бу очиқ сеҳр-жодудан ўзга нарса эмас, дейдилар'}, {'chapter': 11, 'verse': 8, 'text': 'وَلَئِنۡ أَخَّرۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِلَىٰٓ أُمَّةٖ مَّعۡدُودَةٖ لَّيَقُولُنَّ مَا يَحۡبِسُهُۥٓۗ أَلَا يَوۡمَ يَأۡتِيهِمۡ لَيۡسَ مَصۡرُوفًا عَنۡهُمۡ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nҚасамки, агар уларни азоблашни маълум муддатгача кечиктирсак, албатта улар: «Уни (азобни) нима тўсмоқда?», деб истеҳзо қиладилар. Огоҳ бўлсинларким, у (азоб) келар кунида улардан қайтиб кетувчи бўлмас ва кулиб-истеҳзо қилиб юрган нарсалари уларни ўраб олур'}, {'chapter': 11, 'verse': 9, 'text': 'وَلَئِنۡ أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ ثُمَّ نَزَعۡنَٰهَا مِنۡهُ إِنَّهُۥ لَيَـُٔوسٞ كَفُورٞ\nҚасамки, агар Биз инсон зотига раҳмат-марҳаматимизни тотдириб, сўнгра Ўзимиз уни тортиб олсак, у албатта ноумид ва ношукрлик қилиши шубҳасиздир'}, {'chapter': 11, 'verse': 10, 'text': 'وَلَئِنۡ أَذَقۡنَٰهُ نَعۡمَآءَ بَعۡدَ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ ذَهَبَ ٱلسَّيِّـَٔاتُ عَنِّيٓۚ إِنَّهُۥ لَفَرِحٞ فَخُورٌ\nҚасамки, агар унга бирон зиён-заҳмат етганидан кейин Биз ноз-неъматларни тотдириб қўйсак, албатта у: «Барча ёмонликлар мендан нари кетди», (энди ҳеч қачон қайтиб келмайди) дейди. Дарҳақиқат, у мағрур ва мақтанчоқдир'}, {'chapter': 11, 'verse': 11, 'text': 'إِلَّا ٱلَّذِينَ صَبَرُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ\nМагар (балоли кунларда) сабр қилиб, (сафоли кунларда) яхши амаллар қиладиган зотлар борки, ана ўшалар учун мағфират ва катта ажр-мукофот бўлур'}, {'chapter': 11, 'verse': 12, 'text': 'فَلَعَلَّكَ تَارِكُۢ بَعۡضَ مَا يُوحَىٰٓ إِلَيۡكَ وَضَآئِقُۢ بِهِۦ صَدۡرُكَ أَن يَقُولُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ كَنزٌ أَوۡ جَآءَ مَعَهُۥ مَلَكٌۚ إِنَّمَآ أَنتَ نَذِيرٞۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эҳтимол, сиз (мушриклар): «Унга осмондан хазина — бойлик туширилса ёки у билан бирга (унинг пайғамбар эканлигини тасдиқлайдиган) бирон фаришта келса бўлмасмиди?», деганлари сабабли ўзингизга ваҳий қилинаётган оятларнинг айримларини (уларга етказишни) тарк қиларсиз ва шу сабабли, дилингиз сиқилар? (Йўқ, сиз ўзингизга нозил қилинган оятларни тўла-тўкис ҳолида кишиларга етказаверинг, чунки) фақатгина сиз (кофирларни дўзах азобидан) огоҳлантиргувчисиз, холос. Аллоҳ ҳамма нарсанинг устида (кузатиб, билиб тургувчи) Вакилдир'}, {'chapter': 11, 'verse': 13, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُواْ بِعَشۡرِ سُوَرٖ مِّثۡلِهِۦ مُفۡتَرَيَٰتٖ وَٱدۡعُواْ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ\nЁки: «Уни (яъни, Қуръонни Муҳаммад) тўқиб чиқарган», дейдиларми? Айтинг: «У ҳолда, агар ростгўй бўлсангизлар, Аллоҳдан ўзга кучингиз етган барча бутларни (ёрдамга) чорлаб шунга ўхшаш ўнтагина «тўқилган» сура келтирингиз!»'}, {'chapter': 11, 'verse': 14, 'text': 'فَإِلَّمۡ يَسۡتَجِيبُواْ لَكُمۡ فَٱعۡلَمُوٓاْ أَنَّمَآ أُنزِلَ بِعِلۡمِ ٱللَّهِ وَأَن لَّآ إِلَٰهَ إِلَّا هُوَۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ\nБас, агар улар (яъни, Аллоҳни қўйиб, сиғинадиган бутларингиз) сизларга жавоб қилмасалар, у ҳолда, билингизки, албатта (бу Қурьон) Аллоҳнинг илми азалийси билан нозил қилингандир ва Ундан ўзга бирон илоҳ йўқдир. Энди мусулмон бўларсизлар?! (Яъни, Аллоҳ ва Унинг пайғамбарига бўйинсунарсизлар)'}, {'chapter': 11, 'verse': 15, 'text': 'مَن كَانَ يُرِيدُ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا وَزِينَتَهَا نُوَفِّ إِلَيۡهِمۡ أَعۡمَٰلَهُمۡ فِيهَا وَهُمۡ فِيهَا لَا يُبۡخَسُونَ\nКим (фақат) шу ҳаёти дунёни ва унинг зебу зийнатларини истайдиган бўлса, уларга қилган амалларини(нг ажр-мукофотини) шу дунёда комил қилиб берурмиз ва улар бу дунёда зиён кўрмайдилар'}, {'chapter': 11, 'verse': 16, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَيۡسَ لَهُمۡ فِي ٱلۡأٓخِرَةِ إِلَّا ٱلنَّارُۖ وَحَبِطَ مَا صَنَعُواْ فِيهَا وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ\nУндай кимсалар учун Охиратда дўзах ўтидан ўзга ҳеч қандай насиба йўқдир. Уларнинг бу дунёда қилган барча яхшиликлари беҳуда кетур ва қилиб ўтган амаллари бефойдадир'}, {'chapter': 11, 'verse': 17, 'text': 'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ وَيَتۡلُوهُ شَاهِدٞ مِّنۡهُ وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةًۚ أُوْلَـٰٓئِكَ يُؤۡمِنُونَ بِهِۦۚ وَمَن يَكۡفُرۡ بِهِۦ مِنَ ٱلۡأَحۡزَابِ فَٱلنَّارُ مَوۡعِدُهُۥۚ فَلَا تَكُ فِي مِرۡيَةٖ مِّنۡهُۚ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ\nАна энди Парвардигори томонидан аниқ ҳужжатга (яъни, Қуръонга) эга бўлган киши (фақат ҳаёти дунё манфаатларини кўзловчи кимсалар билан баробарми?!) Ҳолбуки, Аллоҳ томонидан бўлган шоҳид (яъни, Жаброил фаришта) унинг (Муҳаммад алайҳис-салоту вас-саломнинг) ҳамроҳидир ва ундан (яъни, аниқ ҳужжат бўлган Қуръондан) илгари (Бани Исроил учун келган) пешво ва (Аллоҳнинг) марҳамати бўлган Мусонинг Китоби (Таврот) бордир. Ана ўша зотлар (яъни, аниқ ҳужжат эгалари) унга (Қуръонга) иймон келтирурлар. Барча гуруҳлар орасидан ким у Қуръонга кофир бўлса, унга ваъда қилинган жой — дўзахдир. Бас (эй Муҳаммад алайҳис-салоту вас-салом, сиз у ҳақда шак-шубҳага тушманг! Албатта, у Парвардигорингиз томонидан келган Ҳақиқатдир. Лекин одамларнинг кўплари иймон келтирмайдилар)'}, {'chapter': 11, 'verse': 18, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًاۚ أُوْلَـٰٓئِكَ يُعۡرَضُونَ عَلَىٰ رَبِّهِمۡ وَيَقُولُ ٱلۡأَشۡهَٰدُ هَـٰٓؤُلَآءِ ٱلَّذِينَ كَذَبُواْ عَلَىٰ رَبِّهِمۡۚ أَلَا لَعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِينَ\nАллоҳ шаънига ёлғон тўқиган кимсадан ҳам золимроқ ким бор?! Ундай кимсалар (Қиёмат Кунида) Парвардигорларига рўбарў қилинурлар ва барча гувоҳ: «Мана шулар Парвардигор шаънига ёлғон сўзларини сўзлаганлар», дейдилар. Огоҳ бўлингизким, бундай золимларга Аллоҳнинг лаънати бўлур'}, {'chapter': 11, 'verse': 19, 'text': 'ٱلَّذِينَ يَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ\nУ золим кимсалар (одамларни) Аллоҳнинг йўлидан тўсиб, уни (Аллоҳнинг йўлини) бузмоқчи бўладилар. Улар Охиратни бутунлай инкор этадилар'}, {'chapter': 11, 'verse': 20, 'text': 'أُوْلَـٰٓئِكَ لَمۡ يَكُونُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَمَا كَانَ لَهُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِيَآءَۘ يُضَٰعَفُ لَهُمُ ٱلۡعَذَابُۚ مَا كَانُواْ يَسۡتَطِيعُونَ ٱلسَّمۡعَ وَمَا كَانُواْ يُبۡصِرُونَ\nУндай кимсалар Ер юзида (ҳеч қаерга) қочиб қутулолмас ва улар учун Аллоҳдан ўзга дўстлар-ёрдамчилар ҳам бўлмас. Уларга азоб бир неча баробар қилинур. (Гарчи уларга Ҳақни эшитиб, кўришлари учун қулоқ, кўз берилган бўлса-да), эшитгувчи ҳам, кўргувчи ҳам бўлмадилар'}, {'chapter': 11, 'verse': 21, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nУлар ўзларига зиён қилган кимсалардир. Ўзлари тўқиб олган бутлари эса (бирон фойда бермай) улардан ғойиб бўлди'}, {'chapter': 11, 'verse': 22, 'text': 'لَا جَرَمَ أَنَّهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ\nШак-шубҳа йўқки, улар Охиратда янада каттароқ зиён кўргувчилардир'}, {'chapter': 11, 'verse': 23, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَأَخۡبَتُوٓاْ إِلَىٰ رَبِّهِمۡ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ\nАлбатта, иймон келтириб, яхши амаллар қилган ва Парвардигорларига итоат қилиб бўйинсунган зотлар — ана ўшалар, жаннат эгалари бўлиб, ўша жойда абадий қолурлар'}, {'chapter': 11, 'verse': 24, 'text': '۞مَثَلُ ٱلۡفَرِيقَيۡنِ كَٱلۡأَعۡمَىٰ وَٱلۡأَصَمِّ وَٱلۡبَصِيرِ وَٱلسَّمِيعِۚ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ أَفَلَا تَذَكَّرُونَ\nБу икки гуруҳнинг (яъни, кофирлар билан мўминлар гуруҳининг) мисоли, худди кўру кар билан кўргувчи ва эшитгувчининг мисолидир. Шу иккаласи бир-бирига баробар бўлурми?! Ибрат олмайсизларми'}, {'chapter': 11, 'verse': 25, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ إِنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ\nДарҳақиқат, Биз Нуҳни ўз қавмига (пайғамбар қилиб) юбордик. (У деди): «Албатта, мен сизларга (келган) очиқ огоҳлантиргувчиман'}, {'chapter': 11, 'verse': 26, 'text': 'أَن لَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۖ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ أَلِيمٖ\nСизлар ёлғиз Аллоҳга ибодат қилинглар. (Агар ёлғиз Аллоҳдан ўзга бировга сиғинсангизлар), сизларнинг устингизга аламли азоб куни (тушиши)дан қўрқаман»'}, {'chapter': 11, 'verse': 27, 'text': 'فَقَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا نَرَىٰكَ إِلَّا بَشَرٗا مِّثۡلَنَا وَمَا نَرَىٰكَ ٱتَّبَعَكَ إِلَّا ٱلَّذِينَ هُمۡ أَرَاذِلُنَا بَادِيَ ٱلرَّأۡيِ وَمَا نَرَىٰ لَكُمۡ عَلَيۡنَا مِن فَضۡلِۭ بَلۡ نَظُنُّكُمۡ كَٰذِبِينَ\nШунда унинг қавми орасидаги кофир бўлган кимсалар: «Бизлар сени худди ўзимизга ўхшаган одам, деб биламиз ва сенга фақат ичимиздаги пасткаш (ялангоёқ) кимсалар ўйламасдан эргашганини кўрмоқдамиз. Бизнингча, сизларнинг биздан бирон ортиқчалик жойингиз йўқ, балки биз сизларни ёлғончи деб ўйламоқдамиз», дедилар'}, {'chapter': 11, 'verse': 28, 'text': 'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي رَحۡمَةٗ مِّنۡ عِندِهِۦ فَعُمِّيَتۡ عَلَيۡكُمۡ أَنُلۡزِمُكُمُوهَا وَأَنتُمۡ لَهَا كَٰرِهُونَ\nУ деди: «Эй қавмим, хабар берингиз-чи, агар мен Парвардигорим томонидан аниқ ҳужжатга эга бўлсам ва У зот менга Ўз даргоҳидан раҳмат — пайғамбарликни ато этган бўлса-ю, аммо бу сизларга махфий бўлса, биз сизларни ўзингиз истамаган ҳолда (иймон келтиришга) мажбур қиламизми?!» (Йўқ, ундай қилмаймиз, чунки фақат ўз ихтиёрларингиз билан келтирган иймонларингизгина мақбулдир)'}, {'chapter': 11, 'verse': 29, 'text': 'وَيَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مَالًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۚ وَمَآ أَنَا۠ بِطَارِدِ ٱلَّذِينَ ءَامَنُوٓاْۚ إِنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ\n«Эй қавмим, мен сизлардан бу (даъватим) учун мол-дунё сўрамайман. Менинг ажр-мукофотим фақат Аллоҳнинг зиммасидадир. Ва мен (сизнинг сўзингизга кириб) иймон келтирган кишиларни (ҳузуримдан) қувмайман ҳам. Чунки улар Парвардигорга рўбарў бўлгувчидирлар (бас, агар камбағал бўлганлари учун уларни ҳузуримдан ҳайдасам, албатта, Қиёмат Кунида Парвардигорга устимдан шикоят қилурлар). Лекин мен сизларнинг нодон қавм эканлигингизни кўрмоқдаман»'}, {'chapter': 11, 'verse': 30, 'text': 'وَيَٰقَوۡمِ مَن يَنصُرُنِي مِنَ ٱللَّهِ إِن طَرَدتُّهُمۡۚ أَفَلَا تَذَكَّرُونَ\n«Эй қавмим, агар мен уларни ҳайдасам, ким менга Аллоҳдан (яъни, Аллоҳнинг азобидан) ёрдам-нажот берур? Ўйлаб кўрмайсизларми?!»'}, {'chapter': 11, 'verse': 31, 'text': 'وَلَآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ ٱللَّهِ وَلَآ أَعۡلَمُ ٱلۡغَيۡبَ وَلَآ أَقُولُ إِنِّي مَلَكٞ وَلَآ أَقُولُ لِلَّذِينَ تَزۡدَرِيٓ أَعۡيُنُكُمۡ لَن يُؤۡتِيَهُمُ ٱللَّهُ خَيۡرًاۖ ٱللَّهُ أَعۡلَمُ بِمَا فِيٓ أَنفُسِهِمۡ إِنِّيٓ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ\n«Мен сизларга: «Ҳузуримда Аллоҳнинг хазиналари бор», демайман. Ғайбни (кўздан пинҳон нарса ва ҳодисаларни) ҳам билмайман. «Мен фариштаман», ҳам демайман. Шунингдек, сизлар ўзингизча ҳақорат қилаётган кишилар ҳақида: «Аллоҳ уларга ҳеч қандай яхшилик бермайди», деб айтмайман. Уларнинг дилларидаги нарсани (ихлос-иймонни) Аллоҳ жуда яхши билур. Акс ҳолда (яъни, агар улар тўғрисида юқоридаги сўзларни айтадиган бўлсам), мен албатта золимлардан бўлиб қолурман»'}, {'chapter': 11, 'verse': 32, 'text': 'قَالُواْ يَٰنُوحُ قَدۡ جَٰدَلۡتَنَا فَأَكۡثَرۡتَ جِدَٰلَنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУлар (кофирлар) дедилар: «Эй Нуҳ, мана биз билан кўп-баҳсу мужодала қилдинг. Энди агар ростгўйлардан бўлсанг, бизга ваъда қилаётган нарсангни (яъни, азобни) келтир-чи»'}, {'chapter': 11, 'verse': 33, 'text': 'قَالَ إِنَّمَا يَأۡتِيكُم بِهِ ٱللَّهُ إِن شَآءَ وَمَآ أَنتُم بِمُعۡجِزِينَ\n(Нуҳ) деди: «Уни сизларга ёлғиз Аллоҳнинг Ўзи хоҳлаган пайтида келтирур ва сизлар (ўшанда) ҳеч қаёққа қочиб қутула олмассиз»'}, {'chapter': 11, 'verse': 34, 'text': 'وَلَا يَنفَعُكُمۡ نُصۡحِيٓ إِنۡ أَرَدتُّ أَنۡ أَنصَحَ لَكُمۡ إِن كَانَ ٱللَّهُ يُرِيدُ أَن يُغۡوِيَكُمۡۚ هُوَ رَبُّكُمۡ وَإِلَيۡهِ تُرۡجَعُونَ\n«Агар Аллоҳ сизларни йўлдан оздиришни истаса, мен насиҳат қилишга уринганим билан насиҳатим сизларга фойда бермайди. Парвардигорингиз Унинг Ўзидир ва фақат Унинг Ўзига қайтарилурсизлар»'}, {'chapter': 11, 'verse': 35, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَعَلَيَّ إِجۡرَامِي وَأَنَا۠ بَرِيٓءٞ مِّمَّا تُجۡرِمُونَ\nЁки улар (яъни, Қурайш кофирлари): «Уни (яъни, Қуръонни Муҳаммаднинг) ўзи тўқиб чиқарган», дейдиларми? (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар уни мен тўқиб чиқарган бўлсам, гуноҳим ўз бўйнимда. Аммо мен сизларнинг қилаётган жиноятингиз — кофирлигингиздан покман»'}, {'chapter': 11, 'verse': 36, 'text': 'وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُۥ لَن يُؤۡمِنَ مِن قَوۡمِكَ إِلَّا مَن قَدۡ ءَامَنَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَفۡعَلُونَ\nНуҳга ваҳий қилиндики: «Қавмингдан фақат аввалда иймон келтирган кишилардан бошқа ҳеч ким иймон келтирмайди. Бас, уларнинг (яъни, ҳануз иймон келтирмаган кимсаларнинг) қилмишларидан қайғурмагин'}, {'chapter': 11, 'verse': 37, 'text': 'وَٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ\nБизнинг ҳифзи-ҳимоямизда ва Бизнинг ваҳий таълимимиз билан бир кема ясагин ҳамда золим кимсалар ҳақида (яъни, уларга нажот беришимни сўраб) Менга хитоб-илтижо қилмагин. Улар шак-шубҳасиз, ғарқ қилингувчидирлар»'}, {'chapter': 11, 'verse': 38, 'text': 'وَيَصۡنَعُ ٱلۡفُلۡكَ وَكُلَّمَا مَرَّ عَلَيۡهِ مَلَأٞ مِّن قَوۡمِهِۦ سَخِرُواْ مِنۡهُۚ قَالَ إِن تَسۡخَرُواْ مِنَّا فَإِنَّا نَسۡخَرُ مِنكُمۡ كَمَا تَسۡخَرُونَ\nУ кемани ясар экан, қачон олдидан ўз қавмидан бўлган (кофир) кимсалар ўтсалар, уни масхара қилиб кулдилар. У деди: «Агар (бугун) сизлар биздан кулсангизлар, бас, худди сизлар кулганингиз каби биз ҳам сизлар(нинг устингиз)дан кулурмиз»'}, {'chapter': 11, 'verse': 39, 'text': 'فَسَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ\n«Бас, яқинда кимга шарманда қилгувчи азоб келишини ва кимнинг устига мангу азоб тушишини билиб олажаксиз»'}, {'chapter': 11, 'verse': 40, 'text': 'حَتَّىٰٓ إِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ قُلۡنَا ٱحۡمِلۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ وَمَنۡ ءَامَنَۚ وَمَآ ءَامَنَ مَعَهُۥٓ إِلَّا قَلِيلٞ\nТо Бизнинг фармонимиз келиб, ердан фавворалар отилган вақтда, (Нуҳга) дедик: «У кемага ҳар (жонивордан) бир жуфтдан ва аҳли оилангни чиқаргин. Лекин қайси кимсалар хусусида Бизнинг сўзимиз (яъни, ғарқ бўлиш ҳақидаги ҳукмимиз) ўтган бўлса, (уларни терс қилгин. Булар барча кофирлар ва Нуҳ пайғамбарнинг хотини Воила билан ўғли Канъон эди). Яна иймон келтирган кишиларнинг барчасини (кемага чиқаргин)!» Унга иймон келтирган кишилар эса жуда оз эдилар'}, {'chapter': 11, 'verse': 41, 'text': '۞وَقَالَ ٱرۡكَبُواْ فِيهَا بِسۡمِ ٱللَّهِ مَجۡرٜىٰهَا وَمُرۡسَىٰهَآۚ إِنَّ رَبِّي لَغَفُورٞ رَّحِيمٞ\nУ деди: «Кемага мининглар! Унинг юриши ҳам, туриши ҳам Аллоҳ номи билан бўлур. Албатта, Парвардигорим мағфиратли, меҳрибондир»'}, {'chapter': 11, 'verse': 42, 'text': 'وَهِيَ تَجۡرِي بِهِمۡ فِي مَوۡجٖ كَٱلۡجِبَالِ وَنَادَىٰ نُوحٌ ٱبۡنَهُۥ وَكَانَ فِي مَعۡزِلٖ يَٰبُنَيَّ ٱرۡكَب مَّعَنَا وَلَا تَكُن مَّعَ ٱلۡكَٰفِرِينَ\nКема уларни тоғлардек тўлқинлар орасида олиб кетар экан, Нуҳ бир четда қолган ўғлига нидо қилди: «Эй ўғилчам, биз билан бирга (кемага) мингин, у кофирлар билан бирга қолмагин!»'}, {'chapter': 11, 'verse': 43, 'text': 'قَالَ سَـَٔاوِيٓ إِلَىٰ جَبَلٖ يَعۡصِمُنِي مِنَ ٱلۡمَآءِۚ قَالَ لَا عَاصِمَ ٱلۡيَوۡمَ مِنۡ أَمۡرِ ٱللَّهِ إِلَّا مَن رَّحِمَۚ وَحَالَ بَيۡنَهُمَا ٱلۡمَوۡجُ فَكَانَ مِنَ ٱلۡمُغۡرَقِينَ\nУ (ўғли) деди: «Мен ўзимни сувдан сақлайдиган бирон тоғнинг устига чиқиб кетажакман». (Нуҳ) айтди: «Бугун Аллоҳнинг (кофирларни ҳалок қилиш ҳақидаги) амридан сақлагувчи йўқдир, магар Ўзи раҳм қилган кишиларнигина (сақлар)». (Шу пайт) ўрталарини тўлқин тўсиб қўйди-да, (ўғил) ғарқ қилинувчилардан бўлиб қолди'}, {'chapter': 11, 'verse': 44, 'text': 'وَقِيلَ يَـٰٓأَرۡضُ ٱبۡلَعِي مَآءَكِ وَيَٰسَمَآءُ أَقۡلِعِي وَغِيضَ ٱلۡمَآءُ وَقُضِيَ ٱلۡأَمۡرُ وَٱسۡتَوَتۡ عَلَى ٱلۡجُودِيِّۖ وَقِيلَ بُعۡدٗا لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ\n(Сўнгра): «Эй Ер, сувингни ютгил, эй само, ўзингни тутгин (яъни, «ёғишни бас қил»), дейилди. Сув қуриди, фармон бажарилди ва (кема) Жудий тоғи узра тўхтади ҳамда: «Золим қавмга ҳалокат бўлғай», дейилди'}, {'chapter': 11, 'verse': 45, 'text': 'وَنَادَىٰ نُوحٞ رَّبَّهُۥ فَقَالَ رَبِّ إِنَّ ٱبۡنِي مِنۡ أَهۡلِي وَإِنَّ وَعۡدَكَ ٱلۡحَقُّ وَأَنتَ أَحۡكَمُ ٱلۡحَٰكِمِينَ\nНуҳ Парвардигорига нидо қилиб, деди: «Парвардигорим, албатта, ўғлим менинг аҳли оиламдандир ва шубҳасиз, Сенинг (аҳли оиламга нажот бериш хусусидаги) ваъданг ҳақ ваъдадир. Сен энг адолатли ҳукм қилгувчисан»'}, {'chapter': 11, 'verse': 46, 'text': 'قَالَ يَٰنُوحُ إِنَّهُۥ لَيۡسَ مِنۡ أَهۡلِكَۖ إِنَّهُۥ عَمَلٌ غَيۡرُ صَٰلِحٖۖ فَلَا تَسۡـَٔلۡنِ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۖ إِنِّيٓ أَعِظُكَ أَن تَكُونَ مِنَ ٱلۡجَٰهِلِينَ\n(Аллоҳ) айтди: «Эй Нуҳ, у сенинг аҳлингдан эмас! Албатта у(нинг қилган амали куфр йўлини тутгани) ёмон амалдир. Бас, ўзинг яхши билмаган нарса ҳақида асло Мендан сўрамагин! Албатта, Мен сенга жоҳил-нодонлардан бўлмасликни буюраман»'}, {'chapter': 11, 'verse': 47, 'text': 'قَالَ رَبِّ إِنِّيٓ أَعُوذُ بِكَ أَنۡ أَسۡـَٔلَكَ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞۖ وَإِلَّا تَغۡفِرۡ لِي وَتَرۡحَمۡنِيٓ أَكُن مِّنَ ٱلۡخَٰسِرِينَ\nУ (Нуҳ) деди: «Парвардигорим, мен Сендан ўзим билмаган нарсани сўрашдан паноҳ тилайман! Энди агар мени мағфират қилиб, Ўз раҳматингга олмасанг, зиён кўргувчилардан бўлиб қолурман»'}, {'chapter': 11, 'verse': 48, 'text': 'قِيلَ يَٰنُوحُ ٱهۡبِطۡ بِسَلَٰمٖ مِّنَّا وَبَرَكَٰتٍ عَلَيۡكَ وَعَلَىٰٓ أُمَمٖ مِّمَّن مَّعَكَۚ وَأُمَمٞ سَنُمَتِّعُهُمۡ ثُمَّ يَمَسُّهُم مِّنَّا عَذَابٌ أَلِيمٞ\n(Шунда) айтилди: «Эй Нуҳ, Бизнинг тинч, саломат сақлашимиз билан ва ўзингга ҳамда сен билан бирга бўлган кишиларнинг зурриётларига бўладиган баракотлар билан (кемадан ерга) тушгин. (Яъни, сен ва сенга иймон келтирган кишиларга Биз томондан тинчлик-омонлик ва файзу баракотлар етар.) Яна бошқа (яъни, кофир) жамоалар ҳам келурки, Биз уларни ҳам озгина фойдалантирурмиз, сўнгра эса уларни Биз томондан бўлган аламли азоб тутар»'}, {'chapter': 11, 'verse': 49, 'text': 'تِلۡكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهَآ إِلَيۡكَۖ مَا كُنتَ تَعۡلَمُهَآ أَنتَ وَلَا قَوۡمُكَ مِن قَبۡلِ هَٰذَاۖ فَٱصۡبِرۡۖ إِنَّ ٱلۡعَٰقِبَةَ لِلۡمُتَّقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), булар Биз сизга ваҳий қилаётган ғайб хабарларидандир. Илгари буларни на сиз ва на қавмингиз билар эдингиз. Бас (ҳақ динга даъват қилишда кофирларнинг етказаётган озорларига) сабр қилинг. Албатта, оқибат — натижа Аллоҳдан қўрқувчи кишиларникидир'}, {'chapter': 11, 'verse': 50, 'text': 'وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۖ إِنۡ أَنتُمۡ إِلَّا مُفۡتَرُونَ\nОд қабиласига ўз биродарлари Ҳудни (пайғамбар қилдик). У айтди: «Эй қавмим, Аллоҳга ибодат қилингиз! Сизлар учун Ундан ўзга бирон илоҳ йўқдир. Сизлар (хилма-хил бутларни) фақат ўзларингиз тўқиб олурсизлар, холос (улар хеч қандай илоҳ эмасдирлар)»'}, {'chapter': 11, 'verse': 51, 'text': 'يَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱلَّذِي فَطَرَنِيٓۚ أَفَلَا تَعۡقِلُونَ\n«Эй қавмим, мен сизлардан бу (даъватим учун ажр-мукофот сўрамайман. Менинг ажр-мукофотим фақат мени яратган Зот зиммасидадир. Ахир ақл юргизмайсизларми?!»)'}, {'chapter': 11, 'verse': 52, 'text': 'وَيَٰقَوۡمِ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُرۡسِلِ ٱلسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا وَيَزِدۡكُمۡ قُوَّةً إِلَىٰ قُوَّتِكُمۡ وَلَا تَتَوَلَّوۡاْ مُجۡرِمِينَ\n«Эй қавмим, Парвардигорингиздан мағфират сўрангиз, сўнг Унга тавба-тазарру қилингиз, шунда У зот устингизга осмондан ёмғир қуйдирар ва куч-қувватларингизга яна куч-қувват қўшар. Жинояткор-гуноҳкор бўлган ҳолингизда (менинг даъватимдан) юз ўгириб кетмангиз!»'}, {'chapter': 11, 'verse': 53, 'text': 'قَالُواْ يَٰهُودُ مَا جِئۡتَنَا بِبَيِّنَةٖ وَمَا نَحۡنُ بِتَارِكِيٓ ءَالِهَتِنَا عَن قَوۡلِكَ وَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ\nУлар дедилар: «Эй Ҳуд, сен бизга (ўзингнинг пайғамбар эканлигингаи тасдиқлайдиган) бирон ҳужжат келтирмадинг. Бизлар сенинг қуруқ гапинг билан ўз худоларимизни тарк қилгувчи эмасмиз. Ва бизлар сенга иймон келтиргувчилар эмасмиз»'}, {'chapter': 11, 'verse': 54, 'text': 'إِن نَّقُولُ إِلَّا ٱعۡتَرَىٰكَ بَعۡضُ ءَالِهَتِنَا بِسُوٓءٖۗ قَالَ إِنِّيٓ أُشۡهِدُ ٱللَّهَ وَٱشۡهَدُوٓاْ أَنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ\n«Бизлар фақат: «Сени худоларимиздан бири, бир бало (яъни, мажнун) қилиб қўйган», деймиз, холос». У (Ҳуд) айтди: «Албатта, мен Аллоҳни гувоҳ келтираман, яна ўзларингиз ҳам гувоҳ бўлингларки, мен сизларнинг (сохта-ёлғон худоларни) шерик қилишингиздан безорман'}, {'chapter': 11, 'verse': 55, 'text': 'مِن دُونِهِۦۖ فَكِيدُونِي جَمِيعٗا ثُمَّ لَا تُنظِرُونِ\nАллоҳни қўйиб, (Унга сохта-ёлғон худоларни шерик қилишингиздан безорман). Кейин менга муҳлат ҳам бермай қўя қолинглар». (Яъни, қўлингиздан келган ҳийла-найрангларингизни дарҳол, кечиктирмай қилаверинглар)'}, {'chapter': 11, 'verse': 56, 'text': 'إِنِّي تَوَكَّلۡتُ عَلَى ٱللَّهِ رَبِّي وَرَبِّكُمۚ مَّا مِن دَآبَّةٍ إِلَّا هُوَ ءَاخِذُۢ بِنَاصِيَتِهَآۚ إِنَّ رَبِّي عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\n«Мен фақат менинг ҳам, сизларнинг ҳам Парвардигоримиз бўлган Аллоҳга суяндим. Ўрмалаган нарса борки, барчаси У зотнинг қўл остидадир. Парвардигорим, шак-шубҳасиз, Тўғри Йўлдадир»'}, {'chapter': 11, 'verse': 57, 'text': 'فَإِن تَوَلَّوۡاْ فَقَدۡ أَبۡلَغۡتُكُم مَّآ أُرۡسِلۡتُ بِهِۦٓ إِلَيۡكُمۡۚ وَيَسۡتَخۡلِفُ رَبِّي قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّونَهُۥ شَيۡـًٔاۚ إِنَّ رَبِّي عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ\n«Энди агар (менинг даъватимдан) юз ўгирсангизлар, бас, мен сизларга элчи қилиб юборилган динни етказдим. Парвардигорим (сизларни ҳалок қилиб), ўрнингизга бошқа қавмни келтирур ва сизлар (ўз куфрингиз билан) У зотга бирон зиён етказа олмассиз. Албатта, Парвардигорим ҳамма нарсани кузатиб тургувчидир»'}, {'chapter': 11, 'verse': 58, 'text': 'وَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا هُودٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَنَجَّيۡنَٰهُم مِّنۡ عَذَابٍ غَلِيظٖ\nҚачонки Бизнинг фармонимиз (яъни, азобимиз) келганида, Ҳуд ва у билан бирга иймон келтирган кишиларни Ўз раҳмат-марҳаматимиз билан қутқардик ва уларга қаттиқ азобдан нажот бердик'}, {'chapter': 11, 'verse': 59, 'text': 'وَتِلۡكَ عَادٞۖ جَحَدُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَعَصَوۡاْ رُسُلَهُۥ وَٱتَّبَعُوٓاْ أَمۡرَ كُلِّ جَبَّارٍ عَنِيدٖ\nПарвардигорларининг оятларини инкор қилган, Унинг пайғамбарларига исён қилган ва барча қайсар, золимлар амру фармонларига бўйинсунган ана ўшалардир'}, {'chapter': 11, 'verse': 60, 'text': 'وَأُتۡبِعُواْ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗ وَيَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَآ إِنَّ عَادٗا كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّعَادٖ قَوۡمِ هُودٖ\nУларга бу дунёда ҳам, Қиёмат Кунида ҳам лаънат айтилур. Огоҳ бўлингизким, албатта Од қабиласи Парвардигорларига кофир бўлган эдилар. Огоҳ бўлингизким, Ҳуднинг қавми бўлган Од ҳалокатга учради'}, {'chapter': 11, 'verse': 61, 'text': '۞وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ هُوَ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَٱسۡتَعۡمَرَكُمۡ فِيهَا فَٱسۡتَغۡفِرُوهُ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي قَرِيبٞ مُّجِيبٞ\nСамуд қабиласига ўз биродарлари Солиҳни (пайғамбар қилдик). У айтди: «Эй қавмим, Аллоҳга ибодат қилингиз! Сизлар учун Ундан ўзга бирон илоҳ йўқдир. У сизларни ердан пайдо қилиб, унга жойлаштирди. Бас, Ундан мағфират сўранглар ва Унга тавба-тазарру қилинглар. Албатта, Парвардигорим яқин ва (дуоларни) ижобат қилгувчидир»'}, {'chapter': 11, 'verse': 62, 'text': 'قَالُواْ يَٰصَٰلِحُ قَدۡ كُنتَ فِينَا مَرۡجُوّٗا قَبۡلَ هَٰذَآۖ أَتَنۡهَىٰنَآ أَن نَّعۡبُدَ مَا يَعۡبُدُ ءَابَآؤُنَا وَإِنَّنَا لَفِي شَكّٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ مُرِيبٖ\nУлар дедилар: «Эй Солиҳ, сен илгари бизнинг орамиздаги умидли киши эдинг-ку. Энди бизларни ота-боболаримиз ибодат қилиб келаётган бутларга ибодат қилишимиздан қайтарурмисан?! Албатта, бизлар сенинг даъват қилаётган динингдан шак-шубҳадамиз»'}, {'chapter': 11, 'verse': 63, 'text': 'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي مِنۡهُ رَحۡمَةٗ فَمَن يَنصُرُنِي مِنَ ٱللَّهِ إِنۡ عَصَيۡتُهُۥۖ فَمَا تَزِيدُونَنِي غَيۡرَ تَخۡسِيرٖ\nУ айтди: «Эй қавмим, хабар берингиз-чи, агар мен Парвардигорим томонидан аниқ ҳужжатга эга бўлсам ва менга Ўзининг раҳмати — пайғамбарликни ато этган бўлса-ю, (шундан кейин) мен У зотга исён қилсам, у ҳолда Аллоҳдан (яъни, унинг азобидан) мени ким қутқарур?! Бас, сизлар (мени исёнга чорлашингиз билан) зиёндан ўзга нарса келтирмайсизлар!»'}, {'chapter': 11, 'verse': 64, 'text': 'وَيَٰقَوۡمِ هَٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٞ قَرِيبٞ\n«Эй қавмим, мана бу Аллоҳ (юборган) туя сизлар учун оят-мўъжиза бўлиб келди. Бас, уни Аллоҳнинг Ерида еб-ичиб юрган ҳолида қўйиб юборинглар ва унга бирон ёмонлик етказманглар, акс ҳолда сизларни яқин азоб ушлар»'}, {'chapter': 11, 'verse': 65, 'text': 'فَعَقَرُوهَا فَقَالَ تَمَتَّعُواْ فِي دَارِكُمۡ ثَلَٰثَةَ أَيَّامٖۖ ذَٰلِكَ وَعۡدٌ غَيۡرُ مَكۡذُوبٖ\nУлар эса туяни сўйиб юбордилар. Шунда (Солиҳ) айтди: «Уй-жойларингизда уч кун (тириклик неъматидан) баҳраманд бўлингиз. Мана шу чин ваъдадир»'}, {'chapter': 11, 'verse': 66, 'text': 'فَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا صَٰلِحٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَمِنۡ خِزۡيِ يَوۡمِئِذٍۚ إِنَّ رَبَّكَ هُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ\nЭнди қачонки Бизнинг фармонимиз (яъни, биз юборган бало) келганида, Солиҳ ва у билан бирга иймон келтирган кишиларга Ўз раҳматимиз билан (у азобдан) ва ўша кундаги шармандаликдан нажот бердик. Албатта, Парвардигорингиз ҳақиқий куч-қудрат Эгасидир'}, {'chapter': 11, 'verse': 67, 'text': 'وَأَخَذَ ٱلَّذِينَ ظَلَمُواْ ٱلصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيَٰرِهِمۡ جَٰثِمِينَ\nЗолим кимсаларни эса даҳшатли қичқириқ тутиб, турган жойларида тўкилдилар'}, {'chapter': 11, 'verse': 68, 'text': 'كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَآ إِنَّ ثَمُودَاْ كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّثَمُودَ\nГўё у ерда ҳеч қачон яшамагандек. Огоҳ бўлингизким, албатта Самуд (қабиласи) Парвардигорларига кофир бўлган эдилар. Огоҳ бўлингизким, Самуд (қабиласи) ҳалокатга учради'}, {'chapter': 11, 'verse': 69, 'text': 'وَلَقَدۡ جَآءَتۡ رُسُلُنَآ إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞۖ فَمَا لَبِثَ أَن جَآءَ بِعِجۡلٍ حَنِيذٖ\nДарҳақиқат, Бизнинг элчиларимиз — фаришталар Иброҳимга (Исҳоқ исмли фарзанд кўриши ҳақида) хушхабар билан келиб, салом бердилар. У ҳам: «Салом», деди-да, қараб турмай бир бузоқни қовуриб келтирди'}, {'chapter': 11, 'verse': 70, 'text': 'فَلَمَّا رَءَآ أَيۡدِيَهُمۡ لَا تَصِلُ إِلَيۡهِ نَكِرَهُمۡ وَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۚ قَالُواْ لَا تَخَفۡ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمِ لُوطٖ\nЭнди қачонки у овқатга қўл чўзмаганларини кўргач, улардан шубҳаланди ва улардан хавфсирай бошлади. (Шунда) улар: «Қўрқмагин. Бизлар (фаришталармиз таом емаймиз), Лут қавмига (уларни ҳалок қилиш учун) юборилганмиз», дедилар'}, {'chapter': 11, 'verse': 71, 'text': 'وَٱمۡرَأَتُهُۥ قَآئِمَةٞ فَضَحِكَتۡ فَبَشَّرۡنَٰهَا بِإِسۡحَٰقَ وَمِن وَرَآءِ إِسۡحَٰقَ يَعۡقُوبَ\n(Парда ортида Иброҳимнинг) хотини турган эди. У (фаришталарнинг Лут қавмини ҳалок қилиш учун келганликларини эшитиб) кулди — хурсанд бўлди. Шунда Биз (фаришталаримиз воситасида) у аёлга Исҳоқ (исмли ўғил кўриши) ҳақида ва Исҳоқдан кейин Яъқуб (исмли набира кўриши) ҳақида хабар бердик'}, {'chapter': 11, 'verse': 72, 'text': 'قَالَتۡ يَٰوَيۡلَتَىٰٓ ءَأَلِدُ وَأَنَا۠ عَجُوزٞ وَهَٰذَا بَعۡلِي شَيۡخًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عَجِيبٞ\nУ деди: «Вой ўлмасам, ўзим кампир, эрим қари чол бўлса ҳам туғавераманми? Бу жуда қизиқ иш-ку!»'}, {'chapter': 11, 'verse': 73, 'text': 'قَالُوٓاْ أَتَعۡجَبِينَ مِنۡ أَمۡرِ ٱللَّهِۖ رَحۡمَتُ ٱللَّهِ وَبَرَكَٰتُهُۥ عَلَيۡكُمۡ أَهۡلَ ٱلۡبَيۡتِۚ إِنَّهُۥ حَمِيدٞ مَّجِيدٞ\nУлар айтдилар: «Аллоҳнинг амридан ажабланяпсанми? Эй хонадон эгалари, сизларга Аллоҳнинг раҳмати ва барокоти бўлгай. Албатта, У ҳамду сано Эгаси ва Буюкдир»'}, {'chapter': 11, 'verse': 74, 'text': 'فَلَمَّا ذَهَبَ عَنۡ إِبۡرَٰهِيمَ ٱلرَّوۡعُ وَجَآءَتۡهُ ٱلۡبُشۡرَىٰ يُجَٰدِلُنَا فِي قَوۡمِ لُوطٍ\nЭнди қачонки Иброҳимдан қўрқув кетиб, унга хушхабар етгач, Биз (яъни, фаришталаримиз) билан Лут қавми (уларни ҳалок қилишга шошмаслик) ҳақида баҳслаша бошлади'}, {'chapter': 11, 'verse': 75, 'text': 'إِنَّ إِبۡرَٰهِيمَ لَحَلِيمٌ أَوَّـٰهٞ مُّنِيبٞ\nЗотан, Иброҳим ҳалим, кўнгилчан ва ибодатли кишидир'}, {'chapter': 11, 'verse': 76, 'text': 'يَـٰٓإِبۡرَٰهِيمُ أَعۡرِضۡ عَنۡ هَٰذَآۖ إِنَّهُۥ قَدۡ جَآءَ أَمۡرُ رَبِّكَۖ وَإِنَّهُمۡ ءَاتِيهِمۡ عَذَابٌ غَيۡرُ مَرۡدُودٖ\n(Фаришталар айтдилар): «Эй Иброҳим, бу баҳсни қўй. Чунки Парвардигорингнинг фармони келиб бўлган. Албатта, уларга қайтариб бўлмас азоб келгувчидир»'}, {'chapter': 11, 'verse': 77, 'text': 'وَلَمَّا جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗا وَقَالَ هَٰذَا يَوۡمٌ عَصِيبٞ\nҚачонки элчиларимиз Лутнинг оддига келганларида, у бундан ёмон ҳолга тушди ва уларнинг келишларидан юраги сиқилиб, деди: «Бу кун оғир кундир»'}, {'chapter': 11, 'verse': 78, 'text': 'وَجَآءَهُۥ قَوۡمُهُۥ يُهۡرَعُونَ إِلَيۡهِ وَمِن قَبۡلُ كَانُواْ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِۚ قَالَ يَٰقَوۡمِ هَـٰٓؤُلَآءِ بَنَاتِي هُنَّ أَطۡهَرُ لَكُمۡۖ فَٱتَّقُواْ ٱللَّهَ وَلَا تُخۡزُونِ فِي ضَيۡفِيٓۖ أَلَيۡسَ مِنكُمۡ رَجُلٞ رَّشِيدٞ\n(Лут пайғамбарнинг ҳузурига йигитлар келганини эшитишгач) қавми унинг олдига чопиб келишди, илгаридан ёмон ишлар — баччабозлик қилар эдилар. У айтди: «Эй, қавмим, анави қизларим (яъни, шаҳримиздаги қизлар) сизлар учун покроқ-ку (яъни, ўшаларга уйланаверсангизлар бўлмайдими)?! Бас, Аллоҳдан қўрқинглар ва меҳмонларим олдида мени шарманда қилманглар. Орангизда бирон Тўғри Йўлга юргувчи киши йўқми?!»'}, {'chapter': 11, 'verse': 79, 'text': 'قَالُواْ لَقَدۡ عَلِمۡتَ مَا لَنَا فِي بَنَاتِكَ مِنۡ حَقّٖ وَإِنَّكَ لَتَعۡلَمُ مَا نُرِيدُ\nУлар дедилар: «Сен қизларингга муҳтож эмаслигимизни аниқ биласан. Шунингдек, биз нимани исташимизни ҳам жуда яхши биласан»'}, {'chapter': 11, 'verse': 80, 'text': 'قَالَ لَوۡ أَنَّ لِي بِكُمۡ قُوَّةً أَوۡ ءَاوِيٓ إِلَىٰ رُكۡنٖ شَدِيدٖ\n(Лут) деди: «Қани эди, сизларга кучим етса ёки кучли бир суянчиғим бўлса»'}, {'chapter': 11, 'verse': 81, 'text': 'قَالُواْ يَٰلُوطُ إِنَّا رُسُلُ رَبِّكَ لَن يَصِلُوٓاْ إِلَيۡكَۖ فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ ٱلَّيۡلِ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٌ إِلَّا ٱمۡرَأَتَكَۖ إِنَّهُۥ مُصِيبُهَا مَآ أَصَابَهُمۡۚ إِنَّ مَوۡعِدَهُمُ ٱلصُّبۡحُۚ أَلَيۡسَ ٱلصُّبۡحُ بِقَرِيبٖ\n(Шунда фаришталар) айтдилар: «Эй Лут, биз Парвардигорингнинг элчиларимиз. Улар сенга ҳаргиз тега олмаслар. Бас, сен кечанинг (қолган) қисмида аҳли оилангни олиб чиқиб кетгин ва сизларнинг ичингиздан хотинингдан бошқа ҳеч ким атрофга алангламасин! Зотан, унга (хотинингга) ҳам уларга етган бало етгувчидир. Уларга ваъда қилинган вақт субҳдир. Субҳ яқин эмасми?!»'}, {'chapter': 11, 'verse': 82, 'text': 'فَلَمَّا جَآءَ أَمۡرُنَا جَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهَا حِجَارَةٗ مِّن سِجِّيلٖ مَّنضُودٖ\nЭнди қачонки Бизнинг фармонимиз (яъни, азобимиз) келганида, у жойларни остин-устун қилиб юбордик ва уларнинг (кофирларнинг) устига сопол тошларни пайдар-пай ёғдирдик'}, {'chapter': 11, 'verse': 83, 'text': 'مُّسَوَّمَةً عِندَ رَبِّكَۖ وَمَا هِيَ مِنَ ٱلظَّـٰلِمِينَ بِبَعِيدٖ\nПарвардигорингиз даргоҳида белгилаб қўйилган (сопол тошларни пайдар-пай ёғдирдик). (Эй Муҳаммад алайҳис-салоту вас-салом), у жойлар бу золимлардан (яъни, Макка мушрикларидан) йироқ эмасдир (яъни, ўша кофирларнинг оқибати нима бўлганини кўриб ибрат олсалар бўлмайдими)'}, {'chapter': 11, 'verse': 84, 'text': '۞وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ وَلَا تَنقُصُواْ ٱلۡمِكۡيَالَ وَٱلۡمِيزَانَۖ إِنِّيٓ أَرَىٰكُم بِخَيۡرٖ وَإِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ مُّحِيطٖ\nМадян қавмига ўз биродарлари Шуъайбни (пайғамбар қилдик). У айтди: «Эй қавмим, Аллоҳга ибодат қилингиз! Сизлар учун Ундан ўзга бирон илоҳ йўқдир. Ва ўлчов ва тарозидан уриб колманглар. Мен сизларнинг яхшиликда (яъни, тўкин-сочинликда) эканлигингизни кўриб турибман. Ва мен (агар иймон келтирмасангизлар) сизларнинг устингизга ўраб олувчи Кун — Қиёматнинг азоби тушишидан қўрқаман»'}, {'chapter': 11, 'verse': 85, 'text': 'وَيَٰقَوۡمِ أَوۡفُواْ ٱلۡمِكۡيَالَ وَٱلۡمِيزَانَ بِٱلۡقِسۡطِۖ وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\n«Эй қавмим, ўлчов ва тарозини адолат билан тўла тортингиз! Одамларнинг нарсаларини уриб қолмангиз ва Ерда бузғунчилик билан санқиб юрмангиз!»'}, {'chapter': 11, 'verse': 86, 'text': 'بَقِيَّتُ ٱللَّهِ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ\n«Агар мўмин бўлсангизлар (ҳаром-ҳариш йўллар билан бой-бадавлат бўлгандан кўра) Аллоҳ қолдирган (ҳалол) нарса сизлар учун яхшироқдир. Мен сизларнинг устингизда қўриқчи эмасман»'}, {'chapter': 11, 'verse': 87, 'text': 'قَالُواْ يَٰشُعَيۡبُ أَصَلَوٰتُكَ تَأۡمُرُكَ أَن نَّتۡرُكَ مَا يَعۡبُدُ ءَابَآؤُنَآ أَوۡ أَن نَّفۡعَلَ فِيٓ أَمۡوَٰلِنَا مَا نَشَـٰٓؤُاْۖ إِنَّكَ لَأَنتَ ٱلۡحَلِيمُ ٱلرَّشِيدُ\nУлар (масхара қилиб) дедилар: «Эй Шуъайб, бизлар ота-боболаримиз ибодат қилиб келаётган бутларни тарк қилишимиз кераклигини ёки ўз мол-мулкимизни ўзимиз хоҳлагандек тасарруф қилмаслигимиз кераклигини сенга ўқиётган намозинг буюрмоқдами? Ҳақиқатан, сен жуда кўнгилчан, ҳалим ва рашид (Тўғри Йўлни топиб олган) кишисан-да!»'}, {'chapter': 11, 'verse': 88, 'text': 'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَرَزَقَنِي مِنۡهُ رِزۡقًا حَسَنٗاۚ وَمَآ أُرِيدُ أَنۡ أُخَالِفَكُمۡ إِلَىٰ مَآ أَنۡهَىٰكُمۡ عَنۡهُۚ إِنۡ أُرِيدُ إِلَّا ٱلۡإِصۡلَٰحَ مَا ٱسۡتَطَعۡتُۚ وَمَا تَوۡفِيقِيٓ إِلَّا بِٱللَّهِۚ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ\nУ айтди: «Эй қавмим, хабар берингиз-чи, агар мен Парвардигорим томонидан аниқ ҳужжатга эга бўлсам ва У зот мени Ўз томонидан гўзал ризқ билан ризқлантирган бўлса (шу ҳалол ризқни ҳаромга аралаштиришим керак-ми?!) Мен сизларга хилоф қилиб, сизларни қайтараётган нарсани (яъни, ҳалол-ҳаромга аралаштиришни) ўзим қилишни истамайман. Мен фақат имконим борича ислоҳ қилишни истайман, холос. Ва (бунга) ёлғиз Аллоҳнинг ёрдами билангина муваффақ бўлурман. Ўзига суяндим ва Ўзига илтижо қилурман»'}, {'chapter': 11, 'verse': 89, 'text': 'وَيَٰقَوۡمِ لَا يَجۡرِمَنَّكُمۡ شِقَاقِيٓ أَن يُصِيبَكُم مِّثۡلُ مَآ أَصَابَ قَوۡمَ نُوحٍ أَوۡ قَوۡمَ هُودٍ أَوۡ قَوۡمَ صَٰلِحٖۚ وَمَا قَوۡمُ لُوطٖ مِّنكُم بِبَعِيدٖ\n«Эй қавмим, тағин менга бўйинсунмаслигингиз сизларга ҳам Нуҳ қавмига ё Ҳуд қавмига ёки Солиҳ қавмига етган балолар етишига сабаб бўлмасин. Лут қавми ҳам сизлардан йироқ эмасдир»'}, {'chapter': 11, 'verse': 90, 'text': 'وَٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي رَحِيمٞ وَدُودٞ\n«Парвардигорингиздан мағфират сўрангиз, сўнг Унга тавба-тазарру қилингиз! Албатта, Парвардигорим (тавба қилгувчиларга) марҳамат қилгувчи ва (уларни) дўст тутгувчидир»'}, {'chapter': 11, 'verse': 91, 'text': 'قَالُواْ يَٰشُعَيۡبُ مَا نَفۡقَهُ كَثِيرٗا مِّمَّا تَقُولُ وَإِنَّا لَنَرَىٰكَ فِينَا ضَعِيفٗاۖ وَلَوۡلَا رَهۡطُكَ لَرَجَمۡنَٰكَۖ وَمَآ أَنتَ عَلَيۡنَا بِعَزِيزٖ\nУлар дедилар: «Эй Шуъайб, сен айтаётган нарсаларнинг кўпини англаб етмаётирмиз ва орамизда ожиз бир кимса эканингни кўриб турибмиз. Агар қавму қариндошларинг бўлмаганида, албатта сени тошбўрон қилган бўлур эдик. Сен бизлар учун азиз — мўътабар киши эмассан»'}, {'chapter': 11, 'verse': 92, 'text': 'قَالَ يَٰقَوۡمِ أَرَهۡطِيٓ أَعَزُّ عَلَيۡكُم مِّنَ ٱللَّهِ وَٱتَّخَذۡتُمُوهُ وَرَآءَكُمۡ ظِهۡرِيًّاۖ إِنَّ رَبِّي بِمَا تَعۡمَلُونَ مُحِيطٞ\nУ айтди: «Эй қавмим, менинг қавму қариндошларим сизлар учун Аллоҳдан ҳам азизроқмики, У зотга орқа ўгириб олдингиз? (Яъни, сизлар менинг қавму қариндошимни эмас, балки Аллоҳни ҳурмат қилишингиз лозим эмасми?) Шубҳасиз, Парвардигорим қилаётган амалларингизни билиб — иҳота қилиб тургувчидир»'}, {'chapter': 11, 'verse': 93, 'text': 'وَيَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ سَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَمَنۡ هُوَ كَٰذِبٞۖ وَٱرۡتَقِبُوٓاْ إِنِّي مَعَكُمۡ رَقِيبٞ\n«Эй қавмим, ўзингиз билган ишни қилаверинг, мен ҳам (ўз ишимни) қилгувчиман. Яқинда кимга шарманда қиладиган азоб келишини ва ким ёлғончи эканини билиб олурсиз. Кўз тутаверинглар, мен ҳам сизлар билан бирга кўз тутгувчиман»'}, {'chapter': 11, 'verse': 94, 'text': 'وَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا شُعَيۡبٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَأَخَذَتِ ٱلَّذِينَ ظَلَمُواْ ٱلصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيَٰرِهِمۡ جَٰثِمِينَ\nҚачонки Бизнинг фармонимиз (яъни, азобимиз) келганида, Шуъайбга ва у билан бирга иймон келтирган кишиларга Ўз раҳматимиз билан нажот бердик. Золим кимсаларни эса даҳшатли қичқириқ тушиб, ўз диёрларида тўкилиб қолдилир'}, {'chapter': 11, 'verse': 95, 'text': 'كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَا بُعۡدٗا لِّمَدۡيَنَ كَمَا بَعِدَتۡ ثَمُودُ\nГўё ҳеч қачон (дунёда) яшамагандек. Огоҳ бўлингизким, худди Самуд қабиласи ҳалокатга учрагани каби Мадян қавми ҳам ҳалокатга учради'}, {'chapter': 11, 'verse': 96, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ\nДарҳақиқат, Биз Мусони ўз оят-мўъжизаларимиз ва очиқ ҳужжат билан (Фиръавн ва одамларига) юбордик'}, {'chapter': 11, 'verse': 97, 'text': 'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَٱتَّبَعُوٓاْ أَمۡرَ فِرۡعَوۡنَۖ وَمَآ أَمۡرُ فِرۡعَوۡنَ بِرَشِيدٖ\n(Мусони) Фиръавн ва унинг одамларига (пайғамбар қилиб) юборганимизда, улар Фиръавннинг амрига бўйинсундилар. Ҳолбуки, Фиръавннинг амри тўғри эмасдир'}, {'chapter': 11, 'verse': 98, 'text': 'يَقۡدُمُ قَوۡمَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ فَأَوۡرَدَهُمُ ٱلنَّارَۖ وَبِئۡسَ ٱلۡوِرۡدُ ٱلۡمَوۡرُودُ\nУ (Фиръавн) Қиёмат Кунида ўз қавмини бошлаб бориб дўзахга туширур. У тушиладиган энг ёмон жойдир'}, {'chapter': 11, 'verse': 99, 'text': 'وَأُتۡبِعُواْ فِي هَٰذِهِۦ لَعۡنَةٗ وَيَوۡمَ ٱلۡقِيَٰمَةِۚ بِئۡسَ ٱلرِّفۡدُ ٱلۡمَرۡفُودُ\nУларга бу дунёда ҳам, Қиёмат Кунида ҳам лаънат айтилур, у (жазо) бериладиган энг ёмон «ёрдам»дир'}, {'chapter': 11, 'verse': 100, 'text': 'ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡقُرَىٰ نَقُصُّهُۥ عَلَيۡكَۖ مِنۡهَا قَآئِمٞ وَحَصِيدٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), мана шу Биз сизга сўйлаб бераётган шаҳарларнинг хабарларидандир. Улардан (ҳали-ҳануз обод-соғ) турганлари ҳам бор, ўрилиб битганлари (ҳалок бўлиб йўқолиб кетганлари) ҳам бордир'}, {'chapter': 11, 'verse': 101, 'text': 'وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن ظَلَمُوٓاْ أَنفُسَهُمۡۖ فَمَآ أَغۡنَتۡ عَنۡهُمۡ ءَالِهَتُهُمُ ٱلَّتِي يَدۡعُونَ مِن دُونِ ٱللَّهِ مِن شَيۡءٖ لَّمَّا جَآءَ أَمۡرُ رَبِّكَۖ وَمَا زَادُوهُمۡ غَيۡرَ تَتۡبِيبٖ\nУларга Биз зулм қилмадик, балки ўзларига ўзлари зулм қилдилар, бас, қачонки, Парвардигорингизнинг фармони (яъни, азоби) келганида, Аллоҳни қўйиб, илтижо қиладиган бутлари уларни ҳеч нарсадан беҳожат қилмади ва уларга зиёндан ўзга бирон нарса келтирмади'}, {'chapter': 11, 'verse': 102, 'text': 'وَكَذَٰلِكَ أَخۡذُ رَبِّكَ إِذَآ أَخَذَ ٱلۡقُرَىٰ وَهِيَ ظَٰلِمَةٌۚ إِنَّ أَخۡذَهُۥٓ أَلِيمٞ شَدِيدٌ\nПарвардигорингиз (аҳли-эгалари) золим бўлган шаҳарларни ушлаганида, мана шундай ушлар. Унинг ушлаши-азоби аламли ва қаттиқдир'}, {'chapter': 11, 'verse': 103, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّمَنۡ خَافَ عَذَابَ ٱلۡأٓخِرَةِۚ ذَٰلِكَ يَوۡمٞ مَّجۡمُوعٞ لَّهُ ٱلنَّاسُ وَذَٰلِكَ يَوۡمٞ مَّشۡهُودٞ\nАлбатта бу қиссаларда Охират азобидан қўрққан кишилар учун оят-ибратлар бордир. У Кун барча одамлар тўпланадиган ва ҳозир бўлинадиган Кундир'}, {'chapter': 11, 'verse': 104, 'text': 'وَمَا نُؤَخِّرُهُۥٓ إِلَّا لِأَجَلٖ مَّعۡدُودٖ\nБиз уни фақат саноқли муддатгача таъхир қилиб, кечиктириб турамиз, холос'}, {'chapter': 11, 'verse': 105, 'text': 'يَوۡمَ يَأۡتِ لَا تَكَلَّمُ نَفۡسٌ إِلَّا بِإِذۡنِهِۦۚ فَمِنۡهُمۡ شَقِيّٞ وَسَعِيدٞ\nУ Кун келганида, бирон жон гапирмас, магар Унинг (Аллоҳнинг) изни билангина гапирур. Бас, у (Кунга ҳозир бўлганлар) орасида бахтсизи ҳам бўлур, бахтлиси ҳам'}, {'chapter': 11, 'verse': 106, 'text': 'فَأَمَّا ٱلَّذِينَ شَقُواْ فَفِي ٱلنَّارِ لَهُمۡ فِيهَا زَفِيرٞ وَشَهِيقٌ\nБас, энди бадбахт кимсалар дўзахда бўлиб, у жойда оҳу фарёд қилурлар'}, {'chapter': 11, 'verse': 107, 'text': 'خَٰلِدِينَ فِيهَا مَا دَامَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۚ إِنَّ رَبَّكَ فَعَّالٞ لِّمَا يُرِيدُ\nУлар унда осмонлар ва Ер тургунча қолурлар, магар Парвардигорингиз хоҳласа (яна Ўзи хоҳлаган ҳукмини қилур). Зотан, Парвардигорингиз фақат Ўзи истаган ишни қилгувчи Зотдир'}, {'chapter': 11, 'verse': 108, 'text': '۞وَأَمَّا ٱلَّذِينَ سُعِدُواْ فَفِي ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا مَا دَامَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۖ عَطَآءً غَيۡرَ مَجۡذُوذٖ\nБахтиёр зотлар эса жаннатда бўлиб, у жойда осмонлар ва Ер тургунча турурлар, магар Парвардигорингиз хоҳласа (яна Ўзи хоҳлаган ҳукмини қилур). Бу (яъни, жаннатга тушиш бахтига эришиш Парвардигорингизнинг) туганмас инъомидир'}, {'chapter': 11, 'verse': 109, 'text': 'فَلَا تَكُ فِي مِرۡيَةٖ مِّمَّا يَعۡبُدُ هَـٰٓؤُلَآءِۚ مَا يَعۡبُدُونَ إِلَّا كَمَا يَعۡبُدُ ءَابَآؤُهُم مِّن قَبۡلُۚ وَإِنَّا لَمُوَفُّوهُمۡ نَصِيبَهُمۡ غَيۡرَ مَنقُوصٖ\nБас, сиз, (эй Муҳаммад алайҳис-салоту вас-салом), ана улар (яъни, мушриклар) ибодат қилаётган нарсадан шак-шубҳада бўлманг (яъни, уларнинг ноҳақ йўлда эканликлари аниқдир). Улар ҳам худди ота-боболари каби ибодат қилмоқдалар (бутга сиғинмоқдалар), холос (уларни ҳам ота-боболарини ҳалок қилганимиз каби ҳалок қилурмиз). Албатта, Биз уларнинг насибаларини (яъни, жазоларин) тўла қилиб берурмиз'}, {'chapter': 11, 'verse': 110, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ\nДарҳақиқат, Биз Мусога Китоб (яъни, Таврот) берганимизда, у ҳақда ихтилоф қилинган эди (яъни, айрим кишилар унга иймон келтирган бўлсалар, айримлар уни инкор қилдилар). Агар Парвардигорингиз томонидан (барча ҳисоб-китоб Қиёмат Кунида бўлади, деган) Сўз ўтмаганида, уларнинг ўртасида (шу дунёдаёқ ким ҳақ, ким ноҳақ эканлиги ҳақида) ҳукм чиқарилган бўлур эди. Албатта, улар (яъни, Макка мушриклари) у ҳақда (яъни, Қуръон хусусида) шак-шубҳа остидадирлар'}, {'chapter': 11, 'verse': 111, 'text': 'وَإِنَّ كُلّٗا لَّمَّا لَيُوَفِّيَنَّهُمۡ رَبُّكَ أَعۡمَٰلَهُمۡۚ إِنَّهُۥ بِمَا يَعۡمَلُونَ خَبِيرٞ\nҚасамки, албатта барча халойиққа қилган амалларининг жазосини Парвардигорингиз комил қилиб берур. Албатта, У зот уларнинг қилаётган амалларидан хабардор — огоҳдир'}, {'chapter': 11, 'verse': 112, 'text': 'فَٱسۡتَقِمۡ كَمَآ أُمِرۡتَ وَمَن تَابَ مَعَكَ وَلَا تَطۡغَوۡاْۚ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٞ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз ва сиз билан бирга тавба қилган зотлар ўзингизга буюрилгани янглиғ Тўғри Йўлда бўлингиз! Туғёнга тушмангиз! Албатта, У зот қилаётган амалларингизни кўриб тургувчидир'}, {'chapter': 11, 'verse': 113, 'text': 'وَلَا تَرۡكَنُوٓاْ إِلَى ٱلَّذِينَ ظَلَمُواْ فَتَمَسَّكُمُ ٱلنَّارُ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِيَآءَ ثُمَّ لَا تُنصَرُونَ\nЗулм йўлини тутган кимсаларга берилиб кетманглар (эргашманглар). Акс ҳолда сизларга дўзах ўти етар. Сизлар учун Аллоҳдан ўзга бирон дўст йўқдир. Кейин (яъни, золимларга эргашсангиз) сизларга ёрдам берилмас'}, {'chapter': 11, 'verse': 114, 'text': 'وَأَقِمِ ٱلصَّلَوٰةَ طَرَفَيِ ٱلنَّهَارِ وَزُلَفٗا مِّنَ ٱلَّيۡلِۚ إِنَّ ٱلۡحَسَنَٰتِ يُذۡهِبۡنَ ٱلسَّيِّـَٔاتِۚ ذَٰلِكَ ذِكۡرَىٰ لِلذَّـٰكِرِينَ\nКундузнинг ҳар икки тарафида ва кечадан бир бўлагида намозни тўкис адо қилинг! Албатта, яхши амаллар ёмонлик-гуноҳларни кетказур. Бу қулоқ тутгувчилар учун эслатмадир'}, {'chapter': 11, 'verse': 115, 'text': 'وَٱصۡبِرۡ فَإِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, машаққат ва озорларга) сабр қилинг! Зотан, Аллоҳ чиройли амал қилгувчиларнинг ажр-мукофотларини зое этмас'}, {'chapter': 11, 'verse': 116, 'text': 'فَلَوۡلَا كَانَ مِنَ ٱلۡقُرُونِ مِن قَبۡلِكُمۡ أُوْلُواْ بَقِيَّةٖ يَنۡهَوۡنَ عَنِ ٱلۡفَسَادِ فِي ٱلۡأَرۡضِ إِلَّا قَلِيلٗا مِّمَّنۡ أَنجَيۡنَا مِنۡهُمۡۗ وَٱتَّبَعَ ٱلَّذِينَ ظَلَمُواْ مَآ أُتۡرِفُواْ فِيهِ وَكَانُواْ مُجۡرِمِينَ\nБас, сизлардан аввалги асрларда ўтганлар орасида ҳам Ерда бузғунчиликдан қайтарадиган боқий (дин) эгалари бўлганларида эди. Уларнинг ичларида фақат Биз нажот берган камдан-кам кишиларгина (шундай зотлар эдилар). Золим кимсалар эса ўз маишатларига берилиб кетдилар ва жиноятчи бўлиб қолдилар'}, {'chapter': 11, 'verse': 117, 'text': 'وَمَا كَانَ رَبُّكَ لِيُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمٖ وَأَهۡلُهَا مُصۡلِحُونَ\nПарвардигорингиз у шаҳарларнинг ўзларини тузатиб (ҳақ йўлга юрган) аҳолисини зулм билан ҳалок қилган эмасдир'}, {'chapter': 11, 'verse': 118, 'text': 'وَلَوۡ شَآءَ رَبُّكَ لَجَعَلَ ٱلنَّاسَ أُمَّةٗ وَٰحِدَةٗۖ وَلَا يَزَالُونَ مُخۡتَلِفِينَ\nАгар Парвардигорингиз хоҳлаганида, барча одамларни бир миллат (яъни, бир динга эргашувчи) қилган бўлур эди. (Лекин У зот бундай қилишни истамади. Шунинг учун) улар (одамлар) мудом ихтилоф қилурлар'}, {'chapter': 11, 'verse': 119, 'text': 'إِلَّا مَن رَّحِمَ رَبُّكَۚ وَلِذَٰلِكَ خَلَقَهُمۡۗ وَتَمَّتۡ كَلِمَةُ رَبِّكَ لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِينَ\nМагар Парвардигорингиз раҳм қилган кишиларгина (ҳақ йўлда иттифоқ бўлиб яшарлар). Уларни шунинг учун (яъни, бировлари ҳақ йўлда ҳидоят топишлари, бошқалари ноҳақ йўлларда талашиб-тортишиб юришлари учун) яратгандир. Парвардигорингизнинг «Мен жаҳаннамни (кофир) жин ва кофир одамларнинг барчаси билан тўлдирурмен», деган Сўзи тўла ҳақ бўлди'}, {'chapter': 11, 'verse': 120, 'text': 'وَكُلّٗا نَّقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ ٱلرُّسُلِ مَا نُثَبِّتُ بِهِۦ فُؤَادَكَۚ وَجَآءَكَ فِي هَٰذِهِ ٱلۡحَقُّ وَمَوۡعِظَةٞ وَذِكۡرَىٰ لِلۡمُؤۡمِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга пайғамбарларнинг хабарларидан дилингизни мустаҳкам қиладиган қиссаларни сўйлаб берурмиз ва бу қиссаларда сизга ҳақиқат ва панд-насиҳат ҳамда барча мўминлар учун эслатмалар келди'}, {'chapter': 11, 'verse': 121, 'text': 'وَقُل لِّلَّذِينَ لَا يُؤۡمِنُونَ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنَّا عَٰمِلُونَ\nИймон келтирмайдиган кимсаларга айтинг: «Ўзларингиз билган амалларингизни қилаверингиз, биз ҳам ўз амалимизни қилгувчимиз'}, {'chapter': 11, 'verse': 122, 'text': 'وَٱنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ\nҲамда (қилаётган ишларингизнинг оқибатини) кутаверингиз, биз ҳам кутувчилармиз'}, {'chapter': 11, 'verse': 123, 'text': 'وَلِلَّهِ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَإِلَيۡهِ يُرۡجَعُ ٱلۡأَمۡرُ كُلُّهُۥ فَٱعۡبُدۡهُ وَتَوَكَّلۡ عَلَيۡهِۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nОсмонлар ва Ер фақат Аллоҳникидир ва барча ишлар Унинг Ўзига қайтарилур. Бас, Унга ибодат қилинг ва Ўзига суянинг! Парвардигорингиз қилаётган амалларингиздан ғофил — бехабар эмасдир'}, {'chapter': 12, 'verse': 1, 'text': 'الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ\nАлиф, Лом, Ро. Ушбу (оятлар) очиқ-равшан Китоб оятларидир'}, {'chapter': 12, 'verse': 2, 'text': 'إِنَّآ أَنزَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ\nДарҳақиқат, Биз уни (бу оятларни) ақлларингизни юргизишингиз учун арабий Қуръон ҳолида нозил қилдик'}, {'chapter': 12, 'verse': 3, 'text': 'نَحۡنُ نَقُصُّ عَلَيۡكَ أَحۡسَنَ ٱلۡقَصَصِ بِمَآ أَوۡحَيۡنَآ إِلَيۡكَ هَٰذَا ٱلۡقُرۡءَانَ وَإِن كُنتَ مِن قَبۡلِهِۦ لَمِنَ ٱلۡغَٰفِلِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизга ушбу Қуръон (сураси)ни ваҳий қилиш билан қиссаларнинг энг гўзалини сўйлаб берурмиз. Ҳолбуки (Қуръон нозил бўлиши)дан илгари, сиз (бу қиссадан) бехабар кишилардан (бири) эдингиз'}, {'chapter': 12, 'verse': 4, 'text': 'إِذۡ قَالَ يُوسُفُ لِأَبِيهِ يَـٰٓأَبَتِ إِنِّي رَأَيۡتُ أَحَدَ عَشَرَ كَوۡكَبٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ رَأَيۡتُهُمۡ لِي سَٰجِدِينَ\nЭсланг, Юсуф отасига деган эди: «Эй отажон, мен тушимда ўн бир юлдузни, яна қуёш ва ойни кўрибман. Ҳаммалари менга сажда қилишаётган эмиш»'}, {'chapter': 12, 'verse': 5, 'text': 'قَالَ يَٰبُنَيَّ لَا تَقۡصُصۡ رُءۡيَاكَ عَلَىٰٓ إِخۡوَتِكَ فَيَكِيدُواْ لَكَ كَيۡدًاۖ إِنَّ ٱلشَّيۡطَٰنَ لِلۡإِنسَٰنِ عَدُوّٞ مُّبِينٞ\nУ (отаси) деди: «Эй ўғилчам, бу тушингни оға-иниларингга ҳаргиз айта кўрма, токи улар сенга қарши бирон ҳийла қилмасинлар.Чунки шайтон инсонга очиқ душмандир»'}, {'chapter': 12, 'verse': 6, 'text': 'وَكَذَٰلِكَ يَجۡتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِن تَأۡوِيلِ ٱلۡأَحَادِيثِ وَيُتِمُّ نِعۡمَتَهُۥ عَلَيۡكَ وَعَلَىٰٓ ءَالِ يَعۡقُوبَ كَمَآ أَتَمَّهَا عَلَىٰٓ أَبَوَيۡكَ مِن قَبۡلُ إِبۡرَٰهِيمَ وَإِسۡحَٰقَۚ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٞ\nШунингдек, (яъни, сенга шундай улуғ туш кўришни насиб этган каби) Парвардигоринг сени (пайғамбарлик учун) танлар ва сенга барча тушларнинг таъбирини билдирур ҳамда худди илгари аждодларинг Иброҳим ва Исҳоққа комил қилиб бергани каби, сенга ва Яъқубнинг (бошқа) фарзандларига ҳам, Ўзининг (пайғамбарлик) неъматини комил қилиб берур. Албатта, Парвардигоринг илм ва ҳикмат Соҳибидир'}, {'chapter': 12, 'verse': 7, 'text': '۞لَّقَدۡ كَانَ فِي يُوسُفَ وَإِخۡوَتِهِۦٓ ءَايَٰتٞ لِّلسَّآئِلِينَ\nДарҳақиқат, Юсуф ва унинг оға-инилари (ҳақидаги қисса)да сўрагувчилар учун оят-ибратлар бордир'}, {'chapter': 12, 'verse': 8, 'text': 'إِذۡ قَالُواْ لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰٓ أَبِينَا مِنَّا وَنَحۡنُ عُصۡبَةٌ إِنَّ أَبَانَا لَفِي ضَلَٰلٖ مُّبِينٍ\nЎшанда улар (Юсуфнинг ўгай оғалари) айтган эдилар: «Гарчи биз бир жамоат (яъни, кўпчилик) бўлсак-да, шак-шубҳасиз, Юсуф ва унинг (туғишган) биродари (Бинямин) отамизга биздан кўра суюклироқдир. Дарҳақиқат, отамиз очиқ залолатдадир'}, {'chapter': 12, 'verse': 9, 'text': 'ٱقۡتُلُواْ يُوسُفَ أَوِ ٱطۡرَحُوهُ أَرۡضٗا يَخۡلُ لَكُمۡ وَجۡهُ أَبِيكُمۡ وَتَكُونُواْ مِنۢ بَعۡدِهِۦ قَوۡمٗا صَٰلِحِينَ\nЮсуфни ўлдиринглар, ёки бирон ерга олиб бориб ташланглар, (шундагина) оталарингиз фақат сизларга боқар. Кейин эса (тавба-тазарру қилиб) яхши қавм бўлиб олурсизлар»'}, {'chapter': 12, 'verse': 10, 'text': 'قَالَ قَآئِلٞ مِّنۡهُمۡ لَا تَقۡتُلُواْ يُوسُفَ وَأَلۡقُوهُ فِي غَيَٰبَتِ ٱلۡجُبِّ يَلۡتَقِطۡهُ بَعۡضُ ٱلسَّيَّارَةِ إِن كُنتُمۡ فَٰعِلِينَ\n(Шунда) улардан бир сўзлагувчи деди: «Юсуфни ўлдирманглар, балки агар (ундан қутулиш учун) бирон иш қилмоқчи бўлсангизлар, уни қудуқ қаърига ташлаб юборинглар, йўловчи карвонлар олиб кетсин»'}, {'chapter': 12, 'verse': 11, 'text': 'قَالُواْ يَـٰٓأَبَانَا مَا لَكَ لَا تَأۡمَ۬نَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُۥ لَنَٰصِحُونَ\n(Улар шундай қарорга келганларидан сўнг, Яъқуб алайҳиссаломнинг олдига келиб) дедилар: «Эй ота, не сабабдан Юсуфни бизга ишонмайсан? Ахир биз уни, шак-шубҳасиз, холис (яхши кўрувчи)лармиз-ку'}, {'chapter': 12, 'verse': 12, 'text': 'أَرۡسِلۡهُ مَعَنَا غَدٗا يَرۡتَعۡ وَيَلۡعَبۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ\nУни эртага биз билан айланишга юборгин, ўйнаб-ёзилиб келсин. Албатта, биз уни қўриқлаб-муҳофаза қилгувчилардирмиз»'}, {'chapter': 12, 'verse': 13, 'text': 'قَالَ إِنِّي لَيَحۡزُنُنِيٓ أَن تَذۡهَبُواْ بِهِۦ وَأَخَافُ أَن يَأۡكُلَهُ ٱلذِّئۡبُ وَأَنتُمۡ عَنۡهُ غَٰفِلُونَ\n(Яъқуб) деди: «Уни олиб кетишларингиз мени маҳзун қилур. Мен, сизлар ғафлатда қолиб, уни бўри еб кетишидан қўрқурман»'}, {'chapter': 12, 'verse': 14, 'text': 'قَالُواْ لَئِنۡ أَكَلَهُ ٱلذِّئۡبُ وَنَحۡنُ عُصۡبَةٌ إِنَّآ إِذٗا لَّخَٰسِرُونَ\nУлар дедилар: «Қасамки, агар биз бутун бир жамоат бўлган ҳолимизда (ғафлатда қолиб) уни бўри еб кетса, демак, биз, шак-шубҳасиз, зиён кўргувчи — ҳалок бўлгувчилардирмиз»'}, {'chapter': 12, 'verse': 15, 'text': 'فَلَمَّا ذَهَبُواْ بِهِۦ وَأَجۡمَعُوٓاْ أَن يَجۡعَلُوهُ فِي غَيَٰبَتِ ٱلۡجُبِّۚ وَأَوۡحَيۡنَآ إِلَيۡهِ لَتُنَبِّئَنَّهُم بِأَمۡرِهِمۡ هَٰذَا وَهُمۡ لَا يَشۡعُرُونَ\nБас, қачонки уни олиб кетишиб, сўнгра қудуқ қаърига ташлашга қасд қилган вақтларида, Биз унга (Юсуфга): «Сен албатта (вақти келиб) оға-иниларингга бу қилмишлари ҳақида хабар берурсан. Улар (ўшанда, сен Юсуф эканингни) сезмайдилар ҳам», деб ваҳий қилдик'}, {'chapter': 12, 'verse': 16, 'text': 'وَجَآءُوٓ أَبَاهُمۡ عِشَآءٗ يَبۡكُونَ\nУлар кечқурун оталари олдига йиғлаган ҳолларида келишди'}, {'chapter': 12, 'verse': 17, 'text': 'قَالُواْ يَـٰٓأَبَانَآ إِنَّا ذَهَبۡنَا نَسۡتَبِقُ وَتَرَكۡنَا يُوسُفَ عِندَ مَتَٰعِنَا فَأَكَلَهُ ٱلذِّئۡبُۖ وَمَآ أَنتَ بِمُؤۡمِنٖ لَّنَا وَلَوۡ كُنَّا صَٰدِقِينَ\nВа: «Эй ота, бизлар Юсуфни нарсаларимиз олдида қолдириб қувлашиб кетган эдик, уни бўри еб кетибди. Энди рост гапирсак ҳам сен бизларга асло ишонмайсан», дедилар'}, {'chapter': 12, 'verse': 18, 'text': 'وَجَآءُو عَلَىٰ قَمِيصِهِۦ بِدَمٖ كَذِبٖۚ قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٞۖ وَٱللَّهُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ\nВа унинг (Юсуфнинг) кўйлагини ёлғон қонга (яъни, бир қўйни сўйишиб ўшанинг қонига) бўяб келтирдилар. (Шунда Яъқуб) айтди: «Йўқ! Сизларга ҳавойи-нафсларинг бирон (ёмон) ишни чиройли қилиб кўрсатган. Энди (менинг ишим) чиройли сабр қилмоқдир. Сизлар сўзлаётган бу нарса (айрилиқ-мусибат) устида мадад сўраладиган зот ёлғиз Аллоҳдир»'}, {'chapter': 12, 'verse': 19, 'text': 'وَجَآءَتۡ سَيَّارَةٞ فَأَرۡسَلُواْ وَارِدَهُمۡ فَأَدۡلَىٰ دَلۡوَهُۥۖ قَالَ يَٰبُشۡرَىٰ هَٰذَا غُلَٰمٞۚ وَأَسَرُّوهُ بِضَٰعَةٗۚ وَٱللَّهُ عَلِيمُۢ بِمَا يَعۡمَلُونَ\n(Юсуф қудуққа ташланганининг учинчи куни, у очликдан азобланиб ётганида) бир карвон келиб, ўзларининг сув изловчиларини (сув келтиргани) юборганларида, у челакни (қудуққа) ташлаган эди, (сувнинг ўрнига челакка осилиб Юсуф чиқди. Шунда у): «Суюнчи беринглар, бу бола-ку!», деб юборди. Сўнг уни сотиладиган нарсалари қатори яшириб қўйдилар. Аллоҳ уларнинг қилаётган амалларини билиб тургувчидир'}, {'chapter': 12, 'verse': 20, 'text': 'وَشَرَوۡهُ بِثَمَنِۭ بَخۡسٖ دَرَٰهِمَ مَعۡدُودَةٖ وَكَانُواْ فِيهِ مِنَ ٱلزَّـٰهِدِينَ\n(Кейин, карвон Мисрга етиб келгач), уни арзон баҳода — бир неча тангага сотиб юбордилар. (Чунки) улар (Юсуфга) қизиқмаган эдилар'}, {'chapter': 12, 'verse': 21, 'text': 'وَقَالَ ٱلَّذِي ٱشۡتَرَىٰهُ مِن مِّصۡرَ لِٱمۡرَأَتِهِۦٓ أَكۡرِمِي مَثۡوَىٰهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗاۚ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي ٱلۡأَرۡضِ وَلِنُعَلِّمَهُۥ مِن تَأۡوِيلِ ٱلۡأَحَادِيثِۚ وَٱللَّهُ غَالِبٌ عَلَىٰٓ أَمۡرِهِۦ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nУни Мисрдан сотиб олган (Миср шаҳрининг ҳокими — Қитфийр деган) киши хотини (Зулайҳо)га: «Уни яхшилаб жойлаштиргин. Шояд бизга бирон фойдаси тегиб қолса ёки ўзимизга бола қилиб олармиз», деди. Мана шундай қилиб (яъни, ўлимдан, чоҳдан қутқариб ва Миср ҳокимининг кўнглини мойил қилиш билан) Юсуфни — унга барча тушларнинг таъбирини билдириш учун, — ўша ерга (Миср ҳокимининг уйига) жойлаштириб қўйдик. Аллоҳ Ўз ишида ғолибдир (яъни, Уни Ўзи хоҳлаган ишни қилишдан ҳеч ким манъ қила олмайди), лекин одамларнинг кўплари (буни) билмайдилар'}, {'chapter': 12, 'verse': 22, 'text': 'وَلَمَّا بَلَغَ أَشُدَّهُۥٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nҚачонки (Юсуф) вояга етгач, унга ҳикмат ва билим бердик. Биз чиройли амал қилгувчи зотларни мана шундай мукофотлаймиз'}, {'chapter': 12, 'verse': 23, 'text': 'وَرَٰوَدَتۡهُ ٱلَّتِي هُوَ فِي بَيۡتِهَا عَن نَّفۡسِهِۦ وَغَلَّقَتِ ٱلۡأَبۡوَٰبَ وَقَالَتۡ هَيۡتَ لَكَۚ قَالَ مَعَاذَ ٱللَّهِۖ إِنَّهُۥ رَبِّيٓ أَحۡسَنَ مَثۡوَايَۖ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nУ (Юсуф) уйида бўлган аёл (яъни, Зулайҳо, унинг ҳусну малоҳатига ошиқи беқарор бўлиб) уни йўлдан урмоқчи бўлди. Эшикларни маҳкам беркитиб «келақолгин», деганида, (Юсуф) айтди: «Аллоҳ сақласин. Ахир у (яъни, эринг) менга яхши жойлар берган хожам-ку! Албатта, золим кимсалар (яъни, зинокорлар) асло нажот топмаслар»'}, {'chapter': 12, 'verse': 24, 'text': 'وَلَقَدۡ هَمَّتۡ بِهِۦۖ وَهَمَّ بِهَا لَوۡلَآ أَن رَّءَا بُرۡهَٰنَ رَبِّهِۦۚ كَذَٰلِكَ لِنَصۡرِفَ عَنۡهُ ٱلسُّوٓءَ وَٱلۡفَحۡشَآءَۚ إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُخۡلَصِينَ\nҲақиқатан, (Зулайҳо) унга (яъни, Юсуфнинг висолига) интилди. Агар Парвардигорининг очиқ ҳужжат-аломатини кўрмаганида, (Юсуф) ҳам унга (Зулайҳога) мойил бўлар эди. Ундан (Юсуфдан) ёмонлик ва бузуқликни четлатиш учун мана шундай қилдик (яъни, уни Ўз ҳифзу ҳимоятимизда асрадик). Зеро, у покиза (пайғамбарликка танланган, мухлис) бандаларимиздандир'}, {'chapter': 12, 'verse': 25, 'text': 'وَٱسۡتَبَقَا ٱلۡبَابَ وَقَدَّتۡ قَمِيصَهُۥ مِن دُبُرٖ وَأَلۡفَيَا سَيِّدَهَا لَدَا ٱلۡبَابِۚ قَالَتۡ مَا جَزَآءُ مَنۡ أَرَادَ بِأَهۡلِكَ سُوٓءًا إِلَّآ أَن يُسۡجَنَ أَوۡ عَذَابٌ أَلِيمٞ\n(Иккаласи ҳам) эшик томонга чопишди ва (Зулайҳо Юсуфга етиб олиб) унинг кўйлагини орқа томондан йиртиб юборди. Эшик олдида эса (Зулайҳонинг) хожаси — эрига йўлиқиб қолишди. (Шунда Зулайҳо эрига қараб): «Сенинг оилангга ёмонлик қилмоқчи бўлган кимсанинг жазоси ё зиндон, ёки аламли азобдир», деди'}, {'chapter': 12, 'verse': 26, 'text': 'قَالَ هِيَ رَٰوَدَتۡنِي عَن نَّفۡسِيۚ وَشَهِدَ شَاهِدٞ مِّنۡ أَهۡلِهَآ إِن كَانَ قَمِيصُهُۥ قُدَّ مِن قُبُلٖ فَصَدَقَتۡ وَهُوَ مِنَ ٱلۡكَٰذِبِينَ\n(Юсуф) айтди: «Унинг ўзи мени йўлдан урмоқчи бўлди». (Шу пайт Зулайҳонинг) уруғларидан бири гувоҳлик бериб (деди): «Агар унинг кўйлаги олд томондан йиртилган бўлса, у ҳолда (Зулайҳонинг) гапи ростдир ва (Юсуф) ёлғончилардандир'}, {'chapter': 12, 'verse': 27, 'text': 'وَإِن كَانَ قَمِيصُهُۥ قُدَّ مِن دُبُرٖ فَكَذَبَتۡ وَهُوَ مِنَ ٱلصَّـٰدِقِينَ\nАгар кўйлаги орқа томондан йиртилган бўлса, у ҳолда (Зулайҳо)нинг гапи ёлғондир ва (Юсуф) ростгўйлардан бўлур»'}, {'chapter': 12, 'verse': 28, 'text': 'فَلَمَّا رَءَا قَمِيصَهُۥ قُدَّ مِن دُبُرٖ قَالَ إِنَّهُۥ مِن كَيۡدِكُنَّۖ إِنَّ كَيۡدَكُنَّ عَظِيمٞ\nЭнди қачонки (Зулайҳонинг эри Юсуфнинг) кўйлаги орқа томонидан йиртилганини кўргач: «(Эй аёллар-а), албатта бу (иш) сизларнинг макр-ҳийлаларингиздандир. Дарҳақиқат, сизларнинг макрларингиз жуда зўрдир»'}, {'chapter': 12, 'verse': 29, 'text': 'يُوسُفُ أَعۡرِضۡ عَنۡ هَٰذَاۚ وَٱسۡتَغۡفِرِي لِذَنۢبِكِۖ إِنَّكِ كُنتِ مِنَ ٱلۡخَاطِـِٔينَ\n«Юсуф, сен бу (ишни) унутгин, сен эса (эй Зулайҳо), қилган гуноҳингга истиғфор айт – тавба қил. Чунки сен адашган кимсалардан бўлдинг», деди'}, {'chapter': 12, 'verse': 30, 'text': '۞وَقَالَ نِسۡوَةٞ فِي ٱلۡمَدِينَةِ ٱمۡرَأَتُ ٱلۡعَزِيزِ تُرَٰوِدُ فَتَىٰهَا عَن نَّفۡسِهِۦۖ قَدۡ شَغَفَهَا حُبًّاۖ إِنَّا لَنَرَىٰهَا فِي ضَلَٰلٖ مُّبِينٖ\nШаҳардаги аёллар: «Ҳокимнинг хотини ўз хизматкорини йўлдан урмоқчи бўлибди. Роса юрагидан урибди-да! Албатта, бизлар уни очиқ залолатда деб биламиз», дедилар'}, {'chapter': 12, 'verse': 31, 'text': 'فَلَمَّا سَمِعَتۡ بِمَكۡرِهِنَّ أَرۡسَلَتۡ إِلَيۡهِنَّ وَأَعۡتَدَتۡ لَهُنَّ مُتَّكَـٔٗا وَءَاتَتۡ كُلَّ وَٰحِدَةٖ مِّنۡهُنَّ سِكِّينٗا وَقَالَتِ ٱخۡرُجۡ عَلَيۡهِنَّۖ فَلَمَّا رَأَيۡنَهُۥٓ أَكۡبَرۡنَهُۥ وَقَطَّعۡنَ أَيۡدِيَهُنَّ وَقُلۡنَ حَٰشَ لِلَّهِ مَا هَٰذَا بَشَرًا إِنۡ هَٰذَآ إِلَّا مَلَكٞ كَرِيمٞ\nЭнди қачонки (Зулайҳо) уларнинг иғволарини эшитгач, уларга одам юборди ва (келганларидан кейин) суяниб ўтирадиган жой тайёрлади ҳамда (анвойи мева-чевалар билан дастурхонни тўлдириб) улардан ҳар бирига биттадан пичоқ бериб қўйди. Кейин (Юсуфга) «Уларнинг олдига чиққин», деди. Бас, қачонки уни кўришгач (улар беихтиёр уни) олқишладилар ва (ўзлари сезмаган ҳолларида, қўлларидаги пичоқлари билан) ўз қўлларини кеса бошладилар. Ҳамда: «Эй пок Аллоҳ, бу башар (фарзанди) эмас, бу асил фариштанинг ўзи-ку», деб юбордилар'}, {'chapter': 12, 'verse': 32, 'text': 'قَالَتۡ فَذَٰلِكُنَّ ٱلَّذِي لُمۡتُنَّنِي فِيهِۖ وَلَقَدۡ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ فَٱسۡتَعۡصَمَۖ وَلَئِن لَّمۡ يَفۡعَلۡ مَآ ءَامُرُهُۥ لَيُسۡجَنَنَّ وَلَيَكُونٗا مِّنَ ٱلصَّـٰغِرِينَ\n(Шунда Зулайҳо): деди: «Мана шу йигит ҳақида сизлар мени маломат қилган эдингиз. Дарҳақиқат, уни йўлдан урмоқчи бўлганимда, у покдомонлик қилди – бўйинсунмади. Қасамки, агар менинг амримни бажармас экан, албатта зиндонга ташланур ва хор-зор қилинур»'}, {'chapter': 12, 'verse': 33, 'text': 'قَالَ رَبِّ ٱلسِّجۡنُ أَحَبُّ إِلَيَّ مِمَّا يَدۡعُونَنِيٓ إِلَيۡهِۖ وَإِلَّا تَصۡرِفۡ عَنِّي كَيۡدَهُنَّ أَصۡبُ إِلَيۡهِنَّ وَأَكُن مِّنَ ٱلۡجَٰهِلِينَ\n(Юсуф) деди: «Парвардигорим, мен учун булар мени чорлаётган нарсадан кўра зиндон яхшироқдир. Агар Ўзинг мендан уларнинг макрларини нари қилмасанг, уларга кўнгил бериб нодон кимсалардан бўлиб қолурман»'}, {'chapter': 12, 'verse': 34, 'text': 'فَٱسۡتَجَابَ لَهُۥ رَبُّهُۥ فَصَرَفَ عَنۡهُ كَيۡدَهُنَّۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nБас, Парвардигори (Юсуфнинг дуосини) ижобат қилиб, уларнинг макрларини ундан нари қилди. У зот, шак-шубҳасиз, эшитгувчи, билгувчидир'}, {'chapter': 12, 'verse': 35, 'text': 'ثُمَّ بَدَا لَهُم مِّنۢ بَعۡدِ مَا رَأَوُاْ ٱلۡأٓيَٰتِ لَيَسۡجُنُنَّهُۥ حَتَّىٰ حِينٖ\nСўнгра, уларга (яъни, Миср ҳокими ва унинг одамларига Юсуф пок эканлиги ҳақидаги) оят-аломатларни кўрганларидан кейин, уни бир вақтгача (яъни, гап-сўзлар тингунча) зиндонбанд қилиб туриш маъқул кўринди'}, {'chapter': 12, 'verse': 36, 'text': 'وَدَخَلَ مَعَهُ ٱلسِّجۡنَ فَتَيَانِۖ قَالَ أَحَدُهُمَآ إِنِّيٓ أَرَىٰنِيٓ أَعۡصِرُ خَمۡرٗاۖ وَقَالَ ٱلۡأٓخَرُ إِنِّيٓ أَرَىٰنِيٓ أَحۡمِلُ فَوۡقَ رَأۡسِي خُبۡزٗا تَأۡكُلُ ٱلطَّيۡرُ مِنۡهُۖ نَبِّئۡنَا بِتَأۡوِيلِهِۦٓۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِينَ\nЗиндонга у билан бирга яна икки йигит тушган эди. Улардан бири (яъни, илгари ҳокимнинг соқийси бўлган йигит): «Мен тушимда шароб тайёрлаётган эмишман», деди. Бошқаси эса (яъни, илгари ҳокимнинг новвойи бўлган йигит): «Мен бошим устида нон кўтариб турганмишман, қушлар ундан еяётган эмишлар. Сен бизга шунинг таъбирини айтиб берсанг. Биз сенинг чиройли амаллар қилгувчи киши эканлигингни кўриб турибмиз», деди'}, {'chapter': 12, 'verse': 37, 'text': 'قَالَ لَا يَأۡتِيكُمَا طَعَامٞ تُرۡزَقَانِهِۦٓ إِلَّا نَبَّأۡتُكُمَا بِتَأۡوِيلِهِۦ قَبۡلَ أَن يَأۡتِيَكُمَاۚ ذَٰلِكُمَا مِمَّا عَلَّمَنِي رَبِّيٓۚ إِنِّي تَرَكۡتُ مِلَّةَ قَوۡمٖ لَّا يُؤۡمِنُونَ بِٱللَّهِ وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ\n(Юсуф) айтди: «Мен сизларга ризқ бўладиган (ҳар қандай) таом олдингизга келишидан илгари унинг қандай таом эканини таъвилини айтиб бера оламан. Бу Парвардигорим менга билдирган нарсалардандир. Чунки мен Аллоҳга иймон келтирмайдиган ва Охиратни мутлақо инкор қилувчи қавмнинг миллати — динини тарк этган кишиман»'}, {'chapter': 12, 'verse': 38, 'text': 'وَٱتَّبَعۡتُ مِلَّةَ ءَابَآءِيٓ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَۚ مَا كَانَ لَنَآ أَن نُّشۡرِكَ بِٱللَّهِ مِن شَيۡءٖۚ ذَٰلِكَ مِن فَضۡلِ ٱللَّهِ عَلَيۡنَا وَعَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ\n«Ва мен аждодларим Иброҳим, Исҳоқ ва Яъқубларнинг миллати — динига эргашганман. Биз (яъни, пайғамбарлар) учун Аллоҳга бирон нарсани шерик қилиш жоиз эмасдир. Бу (яъни, ёлғиз Аллоҳгагина бандалик қилиш неъмати) Аллоҳнинг биз — пайғамбарларга ва барча одамларга қилган фазл-марҳаматларидандир. Лекин одамларнинг кўплари шукр қилмайдилар'}, {'chapter': 12, 'verse': 39, 'text': 'يَٰصَٰحِبَيِ ٱلسِّجۡنِ ءَأَرۡبَابٞ مُّتَفَرِّقُونَ خَيۡرٌ أَمِ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ\nЭй ҳамзиндон дўстларим, тарқоқ турли-туман «худолар» яхшироқми ёки ягона ва ғолиб Аллоҳми'}, {'chapter': 12, 'verse': 40, 'text': 'مَا تَعۡبُدُونَ مِن دُونِهِۦٓ إِلَّآ أَسۡمَآءٗ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِ أَمَرَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nСизлар эса, У зотни қўйиб, ўзларингиз ва ота-боболарингиз атаб олган номлар-бутларгагина ибодат қиласизлар. Ахир Аллоҳ уларга (ибодат қилиш ҳақида) бирон ҳужжат туширмаган-ку? Ҳукм-ҳокимлик фақат Аллоҳникидир. У зот сизларни фақат Ўзигагина ибодат қилишга буюргандир. Энг тўғри дин мана шудир. Лекин одамларнинг кўплари буни билмайдилар'}, {'chapter': 12, 'verse': 41, 'text': 'يَٰصَٰحِبَيِ ٱلسِّجۡنِ أَمَّآ أَحَدُكُمَا فَيَسۡقِي رَبَّهُۥ خَمۡرٗاۖ وَأَمَّا ٱلۡأٓخَرُ فَيُصۡلَبُ فَتَأۡكُلُ ٱلطَّيۡرُ مِن رَّأۡسِهِۦۚ قُضِيَ ٱلۡأَمۡرُ ٱلَّذِي فِيهِ تَسۡتَفۡتِيَانِ\nЭй ҳамзиндон дўстларим, энди сизларнинг биринчингизни айтсак, бас, у (бу зиндондан халос бўлиб) яна хожасига соқийлик қилур. Аммо иккинчингиз эса дорга осилиб, (ўлаксахўр) қушлар унинг бошидан чўқирлар. Сизлар таъбир сўраётган иш битди (яъни, энди бу таъбирим ҳеч қачон ўзгармайди)»'}, {'chapter': 12, 'verse': 42, 'text': 'وَقَالَ لِلَّذِي ظَنَّ أَنَّهُۥ نَاجٖ مِّنۡهُمَا ٱذۡكُرۡنِي عِندَ رَبِّكَ فَأَنسَىٰهُ ٱلشَّيۡطَٰنُ ذِكۡرَ رَبِّهِۦ فَلَبِثَ فِي ٱلسِّجۡنِ بِضۡعَ سِنِينَ\n(Юсуф) у икковининг ичидан қутулиб кетади, деб ўйлаган кишига (яъни, соқийга): «Хожангнинг олдида мен ҳақимда зикр қилгин (зора, у мени бу зулмдан озод қилса)», деди. (Лекин) шайтон хожасига зикр қилишни унинг ёдидан чиқариб, (Юсуф) бир неча йил зиндонда қолиб кетди'}, {'chapter': 12, 'verse': 43, 'text': 'وَقَالَ ٱلۡمَلِكُ إِنِّيٓ أَرَىٰ سَبۡعَ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعَ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖۖ يَـٰٓأَيُّهَا ٱلۡمَلَأُ أَفۡتُونِي فِي رُءۡيَٰيَ إِن كُنتُمۡ لِلرُّءۡيَا تَعۡبُرُونَ\n(Кунларнинг бирида Миср мамлакатининг) шоҳи (Райён биннил-Валид): «Мен тушимда еттита ориқ сигир еттита семиз сигирни еяётганини ва еттита яшил бошоқ билан бирга бошқа қуриган (етти бошоқ)ни кўрдим. Аъёнларим, агар тушларнинг таьбирини айта олувчи бўлсангизлар, бу тушимнинг таъбирини (менга) айтинглар-чи», деди'}, {'chapter': 12, 'verse': 44, 'text': 'قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمٖۖ وَمَا نَحۡنُ بِتَأۡوِيلِ ٱلۡأَحۡلَٰمِ بِعَٰلِمِينَ\nУлар дедилар: «Бу алоқ-чалоқ тушлар экан. Биз бундай тушларнинг таъбирини билгувчи эмасмиз»'}, {'chapter': 12, 'verse': 45, 'text': 'وَقَالَ ٱلَّذِي نَجَا مِنۡهُمَا وَٱدَّكَرَ بَعۡدَ أُمَّةٍ أَنَا۠ أُنَبِّئُكُم بِتَأۡوِيلِهِۦ فَأَرۡسِلُونِ\n(Шунда) ҳалиги икки (маҳбус)нинг зиндондан қутулиб чиққани, шунча муддат ўтгач, энди (Юсуфнинг сўзлари) ёдига тушиб, деди: «Унинг таъбирини сизларга мен айтиб берурман, фақат мени (зиндонда ётган Юсуфнинг олдига) юборинглар»'}, {'chapter': 12, 'verse': 46, 'text': 'يُوسُفُ أَيُّهَا ٱلصِّدِّيقُ أَفۡتِنَا فِي سَبۡعِ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعِ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖ لَّعَلِّيٓ أَرۡجِعُ إِلَى ٱلنَّاسِ لَعَلَّهُمۡ يَعۡلَمُونَ\n(Унга рухсат берилгач, зиндонга Юсуфнинг олдига кириб айтди): «Юсуф, эй ростгўй зот, бизга еттита ориқ сигир еттита семиз сигирни еяётгани ҳамда еттита яшил бошоқ билан бирга турган бошқа қуриган (етти бошоқ) ҳақидаги (туш) таъбирини айтиб бергин. Шояд мен одамлар олдига қайтиб боргач (буни уларга айтиб берсам), улар ҳам билиб олишса»'}, {'chapter': 12, 'verse': 47, 'text': 'قَالَ تَزۡرَعُونَ سَبۡعَ سِنِينَ دَأَبٗا فَمَا حَصَدتُّمۡ فَذَرُوهُ فِي سُنۢبُلِهِۦٓ إِلَّا قَلِيلٗا مِّمَّا تَأۡكُلُونَ\n(Юсуф) деди: «Сизлар пайдар-пай етти йил (дон) экинглар. Кейин ўриб олган ҳосилингизни (бузилмасин учун) ўз бошоғида қолдиринглар, магар ейдиган озгина озуқаларингизнигина (янчиб олинглар)'}, {'chapter': 12, 'verse': 48, 'text': 'ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ سَبۡعٞ شِدَادٞ يَأۡكُلۡنَ مَا قَدَّمۡتُمۡ لَهُنَّ إِلَّا قَلِيلٗا مِّمَّا تُحۡصِنُونَ\nСўнгра ўша (серҳосил етти йил)дан кейин етти йил қаҳатчилик бўлиб, улар учун тайёрлаб-асраб қўйган бор ҳосилингизни еб кетар. Магар озгина (уруғлик учун) асраб қўйган донларингизгина қолур'}, {'chapter': 12, 'verse': 49, 'text': 'ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ عَامٞ فِيهِ يُغَاثُ ٱلنَّاسُ وَفِيهِ يَعۡصِرُونَ\nСўнгра, ўша (қаҳатчилик йиллари)дан кейин бир йил келурки, унда одамлар ёғингарчилик остида қолурлар ва (турли мевалардан) шароблар тайёрлаб олурлар»'}, {'chapter': 12, 'verse': 50, 'text': 'وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِي بِهِۦۖ فَلَمَّا جَآءَهُ ٱلرَّسُولُ قَالَ ٱرۡجِعۡ إِلَىٰ رَبِّكَ فَسۡـَٔلۡهُ مَا بَالُ ٱلنِّسۡوَةِ ٱلَّـٰتِي قَطَّعۡنَ أَيۡدِيَهُنَّۚ إِنَّ رَبِّي بِكَيۡدِهِنَّ عَلِيمٞ\n(Соқий подшоҳнинг ҳузурига қайтиб, унинг кўрган тушини Юсуф қандай таъбир қилганини айтгач), подшоҳ деди: «Уни менинг олдимга келтирингиз!» Қачонки (Юсуфнинг олдига) шоҳ элчиси келгач, у: «Хожангнинг ёнига қайтиб бориб, (аввал) ундан ўз қўлларини кесган аёллар нима бўлганини сўра. Албатта, Парвардигорим уларнинг макрларини билгувчидир», деди'}, {'chapter': 12, 'verse': 51, 'text': 'قَالَ مَا خَطۡبُكُنَّ إِذۡ رَٰوَدتُّنَّ يُوسُفَ عَن نَّفۡسِهِۦۚ قُلۡنَ حَٰشَ لِلَّهِ مَا عَلِمۡنَا عَلَيۡهِ مِن سُوٓءٖۚ قَالَتِ ٱمۡرَأَتُ ٱلۡعَزِيزِ ٱلۡـَٰٔنَ حَصۡحَصَ ٱلۡحَقُّ أَنَا۠ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ وَإِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِينَ\n(Шунда подшоҳ у аёлларни тўплаб) деди: «Юсуфни йўлдан урмоқчи бўлган вақтингизда нима бўлган эди (яъни, у ҳам сизларга майл кўрсатганмиди)?» (Аёллар) айтдилар: «Аллоҳ сақласин! Бизлар ундан бирон ёмонликни сезмаганмиз». (Миср шаҳри) ҳокимининг хотини (Зулайҳо) деди: «Мана энди ҳақиқат тантана қилди. Уни мен йўлдан урмоқчи бўлган эдим. У, шак-шубҳасиз, ростгўй зотлардандир»'}, {'chapter': 12, 'verse': 52, 'text': 'ذَٰلِكَ لِيَعۡلَمَ أَنِّي لَمۡ أَخُنۡهُ بِٱلۡغَيۡبِ وَأَنَّ ٱللَّهَ لَا يَهۡدِي كَيۡدَ ٱلۡخَآئِنِينَ\n(Элчи қайтиб келиб, Юсуфга бўлиб ўтган гапларни айтиб бергач, у деди): «Буни (яъни, ўзимнинг бегуноҳлигимни исботлаш талабини) мен (шаҳар ҳокими) йўқлигида Унга хиёнат қилмаганимни ва Аллоҳ ҳеч қачон хоинларнинг ишини ўнгламаслигини (мамлакат шоҳи) билиши учун (қилдим)»'}, {'chapter': 12, 'verse': 53, 'text': '۞وَمَآ أُبَرِّئُ نَفۡسِيٓۚ إِنَّ ٱلنَّفۡسَ لَأَمَّارَةُۢ بِٱلسُّوٓءِ إِلَّا مَا رَحِمَ رَبِّيٓۚ إِنَّ رَبِّي غَفُورٞ رَّحِيمٞ\n«Мен нафсимни оқламайман. Чунки нафс агар Парвардигоримнинг Ўзи раҳм қилмаса — албатта барча ёмонликларга буюргувчидир. Дарҳақиқат, Парвардигорим мағфиратли, меҳрибондир»'}, {'chapter': 12, 'verse': 54, 'text': 'وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِي بِهِۦٓ أَسۡتَخۡلِصۡهُ لِنَفۡسِيۖ فَلَمَّا كَلَّمَهُۥ قَالَ إِنَّكَ ٱلۡيَوۡمَ لَدَيۡنَا مَكِينٌ أَمِينٞ\nПодшоҳ айтди: «Уни ҳузуримга келтирингиз, ўзимнинг хос кишиларимдан қилиб олай». Бас, қачонки (Юсуф у билан сўзлашгач, «Сен бу кун бизнинг даргоҳимизда мартабали, ишончли кишидирсан», деди)'}, {'chapter': 12, 'verse': 55, 'text': 'قَالَ ٱجۡعَلۡنِي عَلَىٰ خَزَآئِنِ ٱلۡأَرۡضِۖ إِنِّي حَفِيظٌ عَلِيمٞ\n(Юсуф) деди: «Мени шу ернинг хазиналари устига қўйгин. Чунки мен (уларни) тўла-тўкис сақлайдиган ва (тўғри тасарруф қилишни) биладиган кишиман»'}, {'chapter': 12, 'verse': 56, 'text': 'وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي ٱلۡأَرۡضِ يَتَبَوَّأُ مِنۡهَا حَيۡثُ يَشَآءُۚ نُصِيبُ بِرَحۡمَتِنَا مَن نَّشَآءُۖ وَلَا نُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nШундай қилиб, Юсуфни (Миср) еридан ўзи хоҳлаган жойда манзил-маскан тутиб яшайдиган мақомга эриштирдик. Биз фазлу марҳаматимизни Ўзимиз хоҳлаган кишиларга етказурмиз ва чиройли амал қилгувчи зотларнинг ажр-мукофотини зое қилмасмиз'}, {'chapter': 12, 'verse': 57, 'text': 'وَلَأَجۡرُ ٱلۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nАлбатта, иймон келтирган ва тақво эгалари бўлган зотлар учун Охиратда ажр-мукофот янада яхшироқ бўлур'}, {'chapter': 12, 'verse': 58, 'text': 'وَجَآءَ إِخۡوَةُ يُوسُفَ فَدَخَلُواْ عَلَيۡهِ فَعَرَفَهُمۡ وَهُمۡ لَهُۥ مُنكِرُونَ\nЮсуфнинг оға-инилари (Мисрга) келишиб, унинг ҳузури кирганларида, уларни таниди. Улар эса (Юсуф)ни танимаган эдилар'}, {'chapter': 12, 'verse': 59, 'text': 'وَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ قَالَ ٱئۡتُونِي بِأَخٖ لَّكُم مِّنۡ أَبِيكُمۡۚ أَلَا تَرَوۡنَ أَنِّيٓ أُوفِي ٱلۡكَيۡلَ وَأَنَا۠ خَيۡرُ ٱلۡمُنزِلِينَ\nҚачонки уларнинг ҳожатларини равон қилгач, (Юсуф) деди: «Ўша ота бир бўлган иниларингизни менинг ҳузуримга келтирингиз. Ахир мен сизларга энг яхши мезбон бўлиб, (дон-дунларни) тўла қилиб ўлчаб бераётганимни кўрмаяпсизларми'}, {'chapter': 12, 'verse': 60, 'text': 'فَإِن لَّمۡ تَأۡتُونِي بِهِۦ فَلَا كَيۡلَ لَكُمۡ عِندِي وَلَا تَقۡرَبُونِ\nЭнди агар уни менинг ҳузуримга олиб келмасангизлар (яъни, мени алдасангизлар), у ҳолда менинг даргоҳимда — сизлар учун бир қадоқ ҳам нарса йўқдир, менинг олдимга яқин келмай қўя қолинглар»'}, {'chapter': 12, 'verse': 61, 'text': 'قَالُواْ سَنُرَٰوِدُ عَنۡهُ أَبَاهُ وَإِنَّا لَفَٰعِلُونَ\nУлар: «Отасидан у(ни биз билан қўшиб юбориши) ҳақида ўтиниб сўраймиз. Албатта, бизлар (шундай) қилгувчимиз», дедилар'}, {'chapter': 12, 'verse': 62, 'text': 'وَقَالَ لِفِتۡيَٰنِهِ ٱجۡعَلُواْ بِضَٰعَتَهُمۡ فِي رِحَالِهِمۡ لَعَلَّهُمۡ يَعۡرِفُونَهَآ إِذَا ٱنقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمۡ لَعَلَّهُمۡ يَرۡجِعُونَ\n(Юсуф ўзининг тарозибон) йигитларига буюрди: «Уларнинг (донга алмаштириш учун) келтирган нарсаларини юкларининг ичига (қайтариб) солиб қўйинглар. Шояд уйларига қайтган вақтларида, у нарсаларини таниб қолиб яна қайтиб келсалар, ажаб эмас»'}, {'chapter': 12, 'verse': 63, 'text': 'فَلَمَّا رَجَعُوٓاْ إِلَىٰٓ أَبِيهِمۡ قَالُواْ يَـٰٓأَبَانَا مُنِعَ مِنَّا ٱلۡكَيۡلُ فَأَرۡسِلۡ مَعَنَآ أَخَانَا نَكۡتَلۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ\nЭнди қачонки улар оталарининг олдига қайтиб келишгач, дедилар: «Эй ота, (бундан кейин) бизларга озиқ-овқат берилмайдиган бўлди. Фақат биз билан бирга инимизни ҳам юборсанггина озиқ-овқат ола биламиз. Бизлар албатта уни муҳофаза қилгувчимиз»'}, {'chapter': 12, 'verse': 64, 'text': 'قَالَ هَلۡ ءَامَنُكُمۡ عَلَيۡهِ إِلَّا كَمَآ أَمِنتُكُمۡ عَلَىٰٓ أَخِيهِ مِن قَبۡلُ فَٱللَّهُ خَيۡرٌ حَٰفِظٗاۖ وَهُوَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\n(Яъқуб) деди: «Мен уни сизларга ишонмайман. Магар илгари унинг биродарини ишонганимдек ишонаман (яъни, ўшанда ҳам менга берган ваъдаларингизга хиёнат қилган эдингиз. Демак, мен сизларга унинг инисини ҳам ишониб топширолмайман. Лекин мен ёлғиз Аллоҳнинг сақлашига ишонаман, холос). Зотан, Аллоҳ энг яхши сақлагувчидир ва У зот меҳрибонларнинг меҳрибонроғидир»'}, {'chapter': 12, 'verse': 65, 'text': 'وَلَمَّا فَتَحُواْ مَتَٰعَهُمۡ وَجَدُواْ بِضَٰعَتَهُمۡ رُدَّتۡ إِلَيۡهِمۡۖ قَالُواْ يَـٰٓأَبَانَا مَا نَبۡغِيۖ هَٰذِهِۦ بِضَٰعَتُنَا رُدَّتۡ إِلَيۡنَاۖ وَنَمِيرُ أَهۡلَنَا وَنَحۡفَظُ أَخَانَا وَنَزۡدَادُ كَيۡلَ بَعِيرٖۖ ذَٰلِكَ كَيۡلٞ يَسِيرٞ\nҚачонки улар (олиб келган) юкларини очиб кўришгач, (озиқ-овқатга алмаштириб келиш учун олиб борган) нарсаларини ўзларига қайтарилганини кўриб дедилар: «Эй ота, мана нарсаларимиз ўзимизга қайтарилибди. Яна нимани истаймиз?» (Энди сен бизларга инимизни ҳам қўшиб юборсанг), яна оиламизга озиқ-овқат келтирурмиз, инимизни муҳофаза қилиб (бориб келурмиз) ва (инимиз учун ҳам) бир туя юкни ортиқроқ олурмиз. Бу (мисрлик сахий ҳоким наздида) озгина нарсадир»'}, {'chapter': 12, 'verse': 66, 'text': 'قَالَ لَنۡ أُرۡسِلَهُۥ مَعَكُمۡ حَتَّىٰ تُؤۡتُونِ مَوۡثِقٗا مِّنَ ٱللَّهِ لَتَأۡتُنَّنِي بِهِۦٓ إِلَّآ أَن يُحَاطَ بِكُمۡۖ فَلَمَّآ ءَاتَوۡهُ مَوۡثِقَهُمۡ قَالَ ٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ\nЯъқуб айтди: «Агар (қароқчилар ё бошқа тўсиқларга дуч келиб) мағлуб бўлиб, ҳалокатга учрамасангизлар, албатта уни (Биняминни) менинг олдимга келтиришингиз ҳақида Аллоҳдан васиқа келтирмасангизлар, (яъни, Аллоҳ номига қасам ичмасангизлар) уни ҳаргиз сизлар билан бирга юбормасман. Энди қачонки унга ўз аҳд-паймонларини беришгач (Яъқуб): «Бу айтаётган гапларимизга Аллоҳ вакил-гувоҳдир», деди'}, {'chapter': 12, 'verse': 67, 'text': 'وَقَالَ يَٰبَنِيَّ لَا تَدۡخُلُواْ مِنۢ بَابٖ وَٰحِدٖ وَٱدۡخُلُواْ مِنۡ أَبۡوَٰبٖ مُّتَفَرِّقَةٖۖ وَمَآ أُغۡنِي عَنكُم مِّنَ ٱللَّهِ مِن شَيۡءٍۖ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَعَلَيۡهِ فَلۡيَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ\n(Сўнг уларни сафарга кузатар экан), айтди: «Эй ўғилларим (Мисрга) бир дарвозадан кирманглар, балки бошқа-бошқа дарвозалардан киринглар! Мен сизлардан Аллоҳнинг бирон ҳукмини қайтара олмайман. Ҳукм ёлғиз Аллоҳнинг измидадир. Мен фақат У зотнинг ўзига таваккул қилганман-суянганман. Барча таваккул қилгувчилар Унинг Ўзигагина таваккул қилсинлар-суянсинлар»'}, {'chapter': 12, 'verse': 68, 'text': 'وَلَمَّا دَخَلُواْ مِنۡ حَيۡثُ أَمَرَهُمۡ أَبُوهُم مَّا كَانَ يُغۡنِي عَنۡهُم مِّنَ ٱللَّهِ مِن شَيۡءٍ إِلَّا حَاجَةٗ فِي نَفۡسِ يَعۡقُوبَ قَضَىٰهَاۚ وَإِنَّهُۥ لَذُو عِلۡمٖ لِّمَا عَلَّمۡنَٰهُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nУлар оталари буюрган тарафлардан (Мисрга) киришлари улардан Аллоҳнинг бирон ҳукмини қайтарувчи эмасдир. (Бу) фақат Яъқубнинг дилидаги (фарзандларига нисбатан меҳр-шафқатдан пайдо бўлган) бир эҳтиёж эдики, у ўзининг шу эҳтиёжини қондирган эди, холос. Зотан, у Биз (ваҳий орқали) берган таълимимиз сабабли чуқур билим эгасидир. Лекин одамларнинг кўпи (буни) билмайдилар'}, {'chapter': 12, 'verse': 69, 'text': 'وَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَخَاهُۖ قَالَ إِنِّيٓ أَنَا۠ أَخُوكَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَعۡمَلُونَ\nҚачонки улар Юсуфнинг ҳузурига кирганларида, у инисини ўзига тортиб: «Мен сенинг оғангман. Энди улар (яъни, оға-иниларимиз) қилгувчи бўлган нарса (яъни, уларнинг ҳасад-адоватлари)дан ғамгин бўлмагин», деди'}, {'chapter': 12, 'verse': 70, 'text': 'فَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ جَعَلَ ٱلسِّقَايَةَ فِي رَحۡلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا ٱلۡعِيرُ إِنَّكُمۡ لَسَٰرِقُونَ\nБас, қачонки уларнинг ҳожатларини равон қилгач, инисининг юки орасига (билдирмай) бир қадаҳни солиб қўйди. Сўнгра эса жарчи: «Эй карвон эгалари, сизлар ўғридирсизлар», деб жар солди'}, {'chapter': 12, 'verse': 71, 'text': 'قَالُواْ وَأَقۡبَلُواْ عَلَيۡهِم مَّاذَا تَفۡقِدُونَ\nУлар (жарчилар) ўзларининг олдига келишгач: «Нима йўқотдингизлар?», дедилар'}, {'chapter': 12, 'verse': 72, 'text': 'قَالُواْ نَفۡقِدُ صُوَاعَ ٱلۡمَلِكِ وَلِمَن جَآءَ بِهِۦ حِمۡلُ بَعِيرٖ وَأَنَا۠ بِهِۦ زَعِيمٞ\n(Жарчилардан бири) айтди: «Подшоҳнинг қадаҳини йўқотдик. Уни топиб келтирган кишига бир туя юк (мукофотдир). Мен шунга вакилман»'}, {'chapter': 12, 'verse': 73, 'text': 'قَالُواْ تَٱللَّهِ لَقَدۡ عَلِمۡتُم مَّا جِئۡنَا لِنُفۡسِدَ فِي ٱلۡأَرۡضِ وَمَا كُنَّا سَٰرِقِينَ\nУлар дедилар: «Аллоҳга қасамки, бизлар ўғри эмасмиз. Ахир бизлар бу ерга бузғунчилик қилиш учун келмаганимизни биласизлар-ку! Бизлар ўғри эмасмиз»'}, {'chapter': 12, 'verse': 74, 'text': 'قَالُواْ فَمَا جَزَـٰٓؤُهُۥٓ إِن كُنتُمۡ كَٰذِبِينَ\n«Агар ёлғон айтган бўлсангизлар, (ўғрининг) жазоси нима бўлур?», сўрашди (жарчилар)'}, {'chapter': 12, 'verse': 75, 'text': 'قَالُواْ جَزَـٰٓؤُهُۥ مَن وُجِدَ فِي رَحۡلِهِۦ فَهُوَ جَزَـٰٓؤُهُۥۚ كَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ\nУлар дедилар: «Унинг жазоси, кимнинг юки орасидан топилса, ўша кимса жазоланур — қул қилинур. Бизлар (ўғрилик қилиш билан ўзгаларга) зулм қилган кимсаларни мана шундай жазолаймиз»'}, {'chapter': 12, 'verse': 76, 'text': 'فَبَدَأَ بِأَوۡعِيَتِهِمۡ قَبۡلَ وِعَآءِ أَخِيهِ ثُمَّ ٱسۡتَخۡرَجَهَا مِن وِعَآءِ أَخِيهِۚ كَذَٰلِكَ كِدۡنَا لِيُوسُفَۖ مَا كَانَ لِيَأۡخُذَ أَخَاهُ فِي دِينِ ٱلۡمَلِكِ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ نَرۡفَعُ دَرَجَٰتٖ مَّن نَّشَآءُۗ وَفَوۡقَ كُلِّ ذِي عِلۡمٍ عَلِيمٞ\nБас, (Юсуфнинг хизматкори ахтаришни) унинг биродари (Бинямин)нинг идишидан илгари уларнинг (яъни, бошқа оға-иниларининг) идишларидан бошлади. Сўнгра уни (Юсуфнинг) биродари идишидан «топиб» олди. Биз Юсуфга мана шундай ҳийла-тадбирни билдирдик. (Чунки Миср) подшоҳининг динида у биродарини (ўғрилик қилгани учун қул қилиб) олиб қола олмас эди. Магар Аллоҳнинг хоҳиши билан (мазкур ҳийла-тадбир воситасидагина олиб қолди). Биз ўзимиз хоҳлаган кишиларни юксак даражаларга кўтарурмиз. Ҳар бир илм соҳибининг устида (ундан устунроқ) билимдон бордир'}, {'chapter': 12, 'verse': 77, 'text': '۞قَالُوٓاْ إِن يَسۡرِقۡ فَقَدۡ سَرَقَ أَخٞ لَّهُۥ مِن قَبۡلُۚ فَأَسَرَّهَا يُوسُفُ فِي نَفۡسِهِۦ وَلَمۡ يُبۡدِهَا لَهُمۡۚ قَالَ أَنتُمۡ شَرّٞ مَّكَانٗاۖ وَٱللَّهُ أَعۡلَمُ بِمَا تَصِفُونَ\nУлар: «Агар бу (Бинямин) ўғрилик қилган бўлса (эҳтимол, чунки) илгари унинг биродари (Юсуф) ҳам ўғрилик қилган эди», дедилар. Бас, Юсуф бу гапни ичига солиб, уларга билдирмади. Ва: (ўзича) «Сизлар энг ёмон-тубан мартабададирсизлар (яъни, бир вақтлар ўғилни отадан ўғирлаб кетгансизлар). Аллоҳ сизларнинг тўқиб чиқараётган гапларингизни жуда яхши билгувчидир», деди'}, {'chapter': 12, 'verse': 78, 'text': 'قَالُواْ يَـٰٓأَيُّهَا ٱلۡعَزِيزُ إِنَّ لَهُۥٓ أَبٗا شَيۡخٗا كَبِيرٗا فَخُذۡ أَحَدَنَا مَكَانَهُۥٓۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِينَ\nСўнг улар дедилар: «Эй улуғ зот, унинг бир кекса отаси бор. Сен унинг (Биняминнинг) ўрнига бизлардан биримизни олиб қолгин. Зотан бизлар сенинг чиройли амалларни қилгувчи зотлардан эканлигингни кўрмоқдамиз»'}, {'chapter': 12, 'verse': 79, 'text': 'قَالَ مَعَاذَ ٱللَّهِ أَن نَّأۡخُذَ إِلَّا مَن وَجَدۡنَا مَتَٰعَنَا عِندَهُۥٓ إِنَّآ إِذٗا لَّظَٰلِمُونَ\n(Юсуф) айтди: «Нарсамизни идишидан топиб олган кимсадан ўзгани олиб қолишдан Аллоҳ сақласин. Акс ҳолда шак-шубҳасиз, золим кимсалардан бўлиб қолурмиз»'}, {'chapter': 12, 'verse': 80, 'text': 'فَلَمَّا ٱسۡتَيۡـَٔسُواْ مِنۡهُ خَلَصُواْ نَجِيّٗاۖ قَالَ كَبِيرُهُمۡ أَلَمۡ تَعۡلَمُوٓاْ أَنَّ أَبَاكُمۡ قَدۡ أَخَذَ عَلَيۡكُم مَّوۡثِقٗا مِّنَ ٱللَّهِ وَمِن قَبۡلُ مَا فَرَّطتُمۡ فِي يُوسُفَۖ فَلَنۡ أَبۡرَحَ ٱلۡأَرۡضَ حَتَّىٰ يَأۡذَنَ لِيٓ أَبِيٓ أَوۡ يَحۡكُمَ ٱللَّهُ لِيۖ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ\nЭнди қачонки ундан умидлари узилгач, (оға-инилар) бир четга чиқиб хуфёна маслаҳатлашдилар. Уларнинг катталари айтди: «Оталарингиз сизларнинг устингизда Аллоҳдан васиқа (яъни, аҳд-паймонингизни) олиб қолганини унутдингизми?! Илгари Юсуф тўғрисида ҳам шундай сусткашлик қилган эдинглар. Бас, то отам менга изн бермагунича ёки Аллоҳ менинг фойдамга ҳукм қилмагунича (яъни, инимиз озод бўлмагунича) мана шу ердан ҳаргиз жилмайман. У зот (Аллоҳ) ҳукм қилгувчиларнинг яхшироғидир»'}, {'chapter': 12, 'verse': 81, 'text': 'ٱرۡجِعُوٓاْ إِلَىٰٓ أَبِيكُمۡ فَقُولُواْ يَـٰٓأَبَانَآ إِنَّ ٱبۡنَكَ سَرَقَ وَمَا شَهِدۡنَآ إِلَّا بِمَا عَلِمۡنَا وَمَا كُنَّا لِلۡغَيۡبِ حَٰفِظِينَ\n«Сизлар оталарингиз олдига қайтиб, айтинглар: «Эй ота, дарҳақиқат, ўғлинг ўғрилик қилди. Бизлар фақат кўрган-билган нарсамизга гувоҳлик бермоқдамиз. (Олдин сенга уни асрашни ваъда қилганимизда) ғайбни (яъни, келажакда нима ишлар бўлишини) билмаган эдик'}, {'chapter': 12, 'verse': 82, 'text': 'وَسۡـَٔلِ ٱلۡقَرۡيَةَ ٱلَّتِي كُنَّا فِيهَا وَٱلۡعِيرَ ٱلَّتِيٓ أَقۡبَلۡنَا فِيهَاۖ وَإِنَّا لَصَٰدِقُونَ\nБиз бўлиб қайтган шаҳардан ва биз бирга кетган карвондан сўраб-суриштиргин. Албатта, бизлар рост сўзлагувчилармиз»'}, {'chapter': 12, 'verse': 83, 'text': 'قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٌۖ عَسَى ٱللَّهُ أَن يَأۡتِيَنِي بِهِمۡ جَمِيعًاۚ إِنَّهُۥ هُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\n(Улар Яъқуб алайҳиссаломнинг олдиларига келиб шу сўзларни айтишгач), у деди: «Йўқ! Сизларга ҳавойи-нафсингиз бирон (ёмон) ишни чиройли қилиб кўрсатган. Энди (менинг ишим) чиройли сабр қилмоқдир. Шоядки, Аллоҳ уларнинг (яъни, Юсуф, Бинямин ва катта ўғлимнинг) барчаларини бағримга қайтарса. Албатта, У зот билим ва ҳикмат Соҳибидир»'}, {'chapter': 12, 'verse': 84, 'text': 'وَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَـٰٓأَسَفَىٰ عَلَىٰ يُوسُفَ وَٱبۡيَضَّتۡ عَيۡنَاهُ مِنَ ٱلۡحُزۡنِ فَهُوَ كَظِيمٞ\nКейин улардан юз ўгириб, деди: «Эй бечора Юсуф-а!» Ғам-алам ютавериб у зотнинг кўзлари оқарди (ожиз бўлиб қолди)'}, {'chapter': 12, 'verse': 85, 'text': 'قَالُواْ تَٱللَّهِ تَفۡتَؤُاْ تَذۡكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوۡ تَكُونَ مِنَ ٱلۡهَٰلِكِينَ\n(Шунда ўғиллари): «Аллоҳ номига қасамки, сен то рамақижон бўлиб қолгунингча ё бир йўла ҳалок бўлгунингча Юсуфни эслайверадиган бўлдинг», дедилар'}, {'chapter': 12, 'verse': 86, 'text': 'قَالَ إِنَّمَآ أَشۡكُواْ بَثِّي وَحُزۡنِيٓ إِلَى ٱللَّهِ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\nУ айтди: «Мен ғаму ташвишларимдан ёлғиз Аллоҳгагина шикоят қилиб йиғламоқдаман ва мен Аллоҳнинг (меҳрибон, раҳмли эканлиги ҳақида) сизлар билмайдиган нарсаларни билурман»'}, {'chapter': 12, 'verse': 87, 'text': 'يَٰبَنِيَّ ٱذۡهَبُواْ فَتَحَسَّسُواْ مِن يُوسُفَ وَأَخِيهِ وَلَا تَاْيۡـَٔسُواْ مِن رَّوۡحِ ٱللَّهِۖ إِنَّهُۥ لَا يَاْيۡـَٔسُ مِن رَّوۡحِ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡكَٰفِرُونَ\n«Эй ўғилларим, боринглар, Юсуф ва унинг биродарини изланглар ва Аллоҳнинг раҳматидан ноумид бўлманглар. Зеро, Аллоҳнинг раҳматидан фақат кофир қавмгина ноумид бўлур»'}, {'chapter': 12, 'verse': 88, 'text': 'فَلَمَّا دَخَلُواْ عَلَيۡهِ قَالُواْ يَـٰٓأَيُّهَا ٱلۡعَزِيزُ مَسَّنَا وَأَهۡلَنَا ٱلضُّرُّ وَجِئۡنَا بِبِضَٰعَةٖ مُّزۡجَىٰةٖ فَأَوۡفِ لَنَا ٱلۡكَيۡلَ وَتَصَدَّقۡ عَلَيۡنَآۖ إِنَّ ٱللَّهَ يَجۡزِي ٱلۡمُتَصَدِّقِينَ\n(Улар оталаридан бу сўзларни эшитгач, озиқ-овқатга алмаштириш учун бор бисотларини олишиб, яна Миср томонга равона бўлишди). Энди қачонки (Мисрга етиб келиб Юсуфнинг) ҳузурига киришгач, дедилар: «Эй улуғ зот, бизни ва аҳли оилаларимизни очарчилик ушлади. Бизлар бу сафар ҳузурингга ўтмас молларни олиб келдик. Бизларга етарли ўлчовда (озиқ-овқат) бергин ва бизларга хайр-садақа қилгин. Аллоҳ, шак-шубҳасиз, (сендек) садақа бергувчи зотларга мукофотлар ато этур»'}, {'chapter': 12, 'verse': 89, 'text': 'قَالَ هَلۡ عَلِمۡتُم مَّا فَعَلۡتُم بِيُوسُفَ وَأَخِيهِ إِذۡ أَنتُمۡ جَٰهِلُونَ\n(Юсуф оға-иниларини бундай забун ҳолатда кўргач тоқат қила олмасдан) деди: «Ёш-нодон бўлган пайтингизда Юсуф ва унинг биродарига нима ишлар қилганларингизни эслайсизлар-ми?»'}, {'chapter': 12, 'verse': 90, 'text': 'قَالُوٓاْ أَءِنَّكَ لَأَنتَ يُوسُفُۖ قَالَ أَنَا۠ يُوسُفُ وَهَٰذَآ أَخِيۖ قَدۡ مَنَّ ٱللَّهُ عَلَيۡنَآۖ إِنَّهُۥ مَن يَتَّقِ وَيَصۡبِرۡ فَإِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ\nУлар: «Сен Юсуфмисан?», деб сўрадилар. «Ҳа, мен Юсуфман, бу эса биродаримдир. Аллоҳ бизга марҳамат кўргузди (яъни, шунча йиллик айрилиқ азобидан кейин бизни яна бирлаштирди). Дарҳақиқат, кимда-ким Аллоҳдан қўрқиб, сабру қаноат қилса, албатта Аллоҳ бундай чиройли амалларни қилгувчи кишиларнинг ажру мукофотини зое қилмас», деди у'}, {'chapter': 12, 'verse': 91, 'text': 'قَالُواْ تَٱللَّهِ لَقَدۡ ءَاثَرَكَ ٱللَّهُ عَلَيۡنَا وَإِن كُنَّا لَخَٰطِـِٔينَ\nУлар дедилар: «Аллоҳга қасамки, Аллоҳ сени бизлардан афзал қилмишдир. Бизлар эса, шак-шубҳасиз, адашгувчилардан бўлдик»'}, {'chapter': 12, 'verse': 92, 'text': 'قَالَ لَا تَثۡرِيبَ عَلَيۡكُمُ ٱلۡيَوۡمَۖ يَغۡفِرُ ٱللَّهُ لَكُمۡۖ وَهُوَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\nУ деди: «Бу кун сизлар айбланмайсиз. Аллоҳ сизларни мағфират қилгай. У зот раҳм қилгувчиларнинг раҳмлироғидир»'}, {'chapter': 12, 'verse': 93, 'text': 'ٱذۡهَبُواْ بِقَمِيصِي هَٰذَا فَأَلۡقُوهُ عَلَىٰ وَجۡهِ أَبِي يَأۡتِ بَصِيرٗا وَأۡتُونِي بِأَهۡلِكُمۡ أَجۡمَعِينَ\n«Сизлар мана бу кўйлагимни олиб бориб, отамнинг юзига ташласангиз, унинг кўзлари очилур. Сўнг барча аҳли оилаларингиз билан бирга менинг олдимга келинглар»'}, {'chapter': 12, 'verse': 94, 'text': 'وَلَمَّا فَصَلَتِ ٱلۡعِيرُ قَالَ أَبُوهُمۡ إِنِّي لَأَجِدُ رِيحَ يُوسُفَۖ لَوۡلَآ أَن تُفَنِّدُونِ\nКарвон (Мисрдан) чиқиши биланоқ оталари (Яъқуб алайҳиссалом ўз уйларида туриб, ҳузуридаги кишиларга): «Мен Юсуфнинг бўйини туймоқдаман. Агар мени ақлдан озган демасангизлар (Юсуф тирик, деган бўлур эдим)», деди'}, {'chapter': 12, 'verse': 95, 'text': 'قَالُواْ تَٱللَّهِ إِنَّكَ لَفِي ضَلَٰلِكَ ٱلۡقَدِيمِ\n(Улар) айтдилар: «Аллоҳга қасамки, сен (ҳали ҳам) ўша эски хатоингда турибсан»'}, {'chapter': 12, 'verse': 96, 'text': 'فَلَمَّآ أَن جَآءَ ٱلۡبَشِيرُ أَلۡقَىٰهُ عَلَىٰ وَجۡهِهِۦ فَٱرۡتَدَّ بَصِيرٗاۖ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيٓ أَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ\nЭнди қачонки хушхабарчи келиб, уни (яъни, Юсуфнинг кўйлагини Яъқубнинг) юзига ташлагач, унинг кўзлари очилди. «Сизларга мен Аллоҳнинг (меҳрибон, раҳмли эканлиги ҳақида) сизлар билмайдиган нарсаларни билурман, демаганмидим», деди у'}, {'chapter': 12, 'verse': 97, 'text': 'قَالُواْ يَـٰٓأَبَانَا ٱسۡتَغۡفِرۡ لَنَا ذُنُوبَنَآ إِنَّا كُنَّا خَٰطِـِٔينَ\nУлар дедилар: «Эй ота, (Аллоҳдан) бизларнинг гуноҳларимизни мағфират қилишини сўрагин. Албатта, бизлар хато қилгувчилардан бўлдик»'}, {'chapter': 12, 'verse': 98, 'text': 'قَالَ سَوۡفَ أَسۡتَغۡفِرُ لَكُمۡ رَبِّيٓۖ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nУ айтди: «Албатта, Парвардигоримдан сизларни мағфират қилмоғини сўрайман. Зотан ёлғиз У зотгина мағфират қилгувчи, меҳрибондир»'}, {'chapter': 12, 'verse': 99, 'text': 'فَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَبَوَيۡهِ وَقَالَ ٱدۡخُلُواْ مِصۡرَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ\nЭнди қачонки (манзилга етиб келишиб) Юсуфнинг ҳузурига (чодирига) кирганларида, у ўзини ота-онасининг қучоғига отди ва «Иншооллоҳ, Мисрга тинч-омон киринглар», деди'}, {'chapter': 12, 'verse': 100, 'text': 'وَرَفَعَ أَبَوَيۡهِ عَلَى ٱلۡعَرۡشِ وَخَرُّواْ لَهُۥ سُجَّدٗاۖ وَقَالَ يَـٰٓأَبَتِ هَٰذَا تَأۡوِيلُ رُءۡيَٰيَ مِن قَبۡلُ قَدۡ جَعَلَهَا رَبِّي حَقّٗاۖ وَقَدۡ أَحۡسَنَ بِيٓ إِذۡ أَخۡرَجَنِي مِنَ ٱلسِّجۡنِ وَجَآءَ بِكُم مِّنَ ٱلۡبَدۡوِ مِنۢ بَعۡدِ أَن نَّزَغَ ٱلشَّيۡطَٰنُ بَيۡنِي وَبَيۡنَ إِخۡوَتِيٓۚ إِنَّ رَبِّي لَطِيفٞ لِّمَا يَشَآءُۚ إِنَّهُۥ هُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\n(Кейин, Мисрга кириб Юсуфнинг саройига келганларидан сўнг) у ота-онасини ўзининг тахтига чиқарди ва улар (яъни, ота-она ва оға-инилари Юсуфга) сажда қилган ҳолларида йиқилдилар. У деди: «Эй отажон, мана шу (қирқ йил) илгари кўрган тушимнинг таъбиридир. Парвардигорим уни рост қилди. Дарҳақиқат, У зот менга (буюк) инъом қилди — мени зиндондан чиқарди, шайтон мен билан оға-иниларим орасини бузиб иғво қилганидан кейин, мана сизларни чўлу саҳродан (Мисрга эсон-омон ҳолингизда) келтирди. Албатта, Парвардигорим Ўзи хоҳлаган нарсани сездирмай амалга оширгувчи Зотдир. Албатта, Унинг Ўзигина билим ва ҳикмат Соҳибидир»'}, {'chapter': 12, 'verse': 101, 'text': '۞رَبِّ قَدۡ ءَاتَيۡتَنِي مِنَ ٱلۡمُلۡكِ وَعَلَّمۡتَنِي مِن تَأۡوِيلِ ٱلۡأَحَادِيثِۚ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ أَنتَ وَلِيِّۦ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ تَوَفَّنِي مُسۡلِمٗا وَأَلۡحِقۡنِي بِٱلصَّـٰلِحِينَ\n«Парвардигорим, менга подшоҳликни ато этдинг, яна (барча) тушларнинг таъвил-таъбирларидан таълим бердинг. Эй осмонлар ва Ерни яратган Зот, дунёю Охиратда Ўзинг Хожамдирсан. (Умрим битиб, сен Ўзинг белгилаб қўйган ажалим етганида) мусулмон ҳолимда жонимни олгин ва мени ҳам солиҳ бандаларинг қаторига қўшгин»'}, {'chapter': 12, 'verse': 102, 'text': 'ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهِ إِلَيۡكَۖ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ أَجۡمَعُوٓاْ أَمۡرَهُمۡ وَهُمۡ يَمۡكُرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), бу Биз сизга ваҳий қилаётган ғайб хабарларидандир. (Чунки сиз Юсуфнинг оға-инилари) макр-ҳийла билан ўзларининг режаларини тузиб иттифоқ қилишаётган пайтларида уларнинг ёнларида ҳозир эмасдингиз'}, {'chapter': 12, 'verse': 103, 'text': 'وَمَآ أَكۡثَرُ ٱلنَّاسِ وَلَوۡ حَرَصۡتَ بِمُؤۡمِنِينَ\nГарчи сиз (барча одамларнинг иймон келтиришларини) жуда истасангиз-да, одамларнинг кўплари мўмин бўлмайдилар'}, {'chapter': 12, 'verse': 104, 'text': 'وَمَا تَسۡـَٔلُهُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nСиз улардан бу (даъватингиз) учун бирон ажр-ҳақ сўрамайсиз. Бу (Қуръон) фақатгина барча оламлар учун бир эслатмадир, холос'}, {'chapter': 12, 'verse': 105, 'text': 'وَكَأَيِّن مِّنۡ ءَايَةٖ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ يَمُرُّونَ عَلَيۡهَا وَهُمۡ عَنۡهَا مُعۡرِضُونَ\nОсмонлар ва Ерда (Аллоҳнинг борлиги ва бирлигига далолат қилгувчи) қанчадан-қанча оят-аломатлар бордир. (Лекин) улар бу оятлардан юз ўгирган ҳолларида ўтиб кетаверадилар'}, {'chapter': 12, 'verse': 106, 'text': 'وَمَا يُؤۡمِنُ أَكۡثَرُهُم بِٱللَّهِ إِلَّا وَهُم مُّشۡرِكُونَ\nУларнинг кўплари Аллоҳга фақат мушрик бўлган ҳолларидагина иймон келтирадилар'}, {'chapter': 12, 'verse': 107, 'text': 'أَفَأَمِنُوٓاْ أَن تَأۡتِيَهُمۡ غَٰشِيَةٞ مِّنۡ عَذَابِ ٱللَّهِ أَوۡ تَأۡتِيَهُمُ ٱلسَّاعَةُ بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nЁки улар Аллоҳнинг азобидан бирон бало-офат келиб қолишидан ё тўсатдан улар сезмаган ҳолларида Қиёмат қойим бўлишидан хотиржаммилар'}, {'chapter': 12, 'verse': 108, 'text': 'قُلۡ هَٰذِهِۦ سَبِيلِيٓ أَدۡعُوٓاْ إِلَى ٱللَّهِۚ عَلَىٰ بَصِيرَةٍ أَنَا۠ وَمَنِ ٱتَّبَعَنِيۖ وَسُبۡحَٰنَ ٱللَّهِ وَمَآ أَنَا۠ مِنَ ٱلۡمُشۡرِكِينَ\nАйтинг: «Менинг йўлим шудир. Мен Аллоҳга даъват қиламан. Мен ва менга эргашган кишилар аниқ ҳужжатга — ишончга эгамиз. (Ҳар қандай шерикдан) Аллоҳни поклайман. (Зеро) мен мушриклардан эмасман»'}, {'chapter': 12, 'verse': 109, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِم مِّنۡ أَهۡلِ ٱلۡقُرَىٰٓۗ أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۗ وَلَدَارُ ٱلۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ٱتَّقَوۡاْۚ أَفَلَا تَعۡقِلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан илгари ҳам фақат шаҳар аҳлларидан бўлган (фаришталарни эмас) кишиларни пайғамбар қилиб, уларга ваҳий юборганмиз. Ахир (одамлар) Ер юзида сайру-саёҳат этишиб, ўзларидан аввалги (иймонсиз) кимсаларнинг оқибатлари қандай бўлганини кўрсалар бўлмайдими? (Аллоҳдан) қўрққан зотлар учун, Охират диёри (бу фоний дунёдан) яхшироқдир. Ақл юргизмайсизларми'}, {'chapter': 12, 'verse': 110, 'text': 'حَتَّىٰٓ إِذَا ٱسۡتَيۡـَٔسَ ٱلرُّسُلُ وَظَنُّوٓاْ أَنَّهُمۡ قَدۡ كُذِبُواْ جَآءَهُمۡ نَصۡرُنَا فَنُجِّيَ مَن نَّشَآءُۖ وَلَا يُرَدُّ بَأۡسُنَا عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِينَ\nҲар қачон пайғамбарларимиз ноумид бўлиб: «Бизлар ёлғончи қилиндик — пайғамбар эканлигимизга ишонмадилар», деб ўйлай бошлаганларида, уларга Бизнинг мададимиз — ғалабамиз келиб, Биз ҳоҳлаган кишиларга нажот берилар эди. (Аммо) жиноятчи бўлган қавмдан бизнинг азобимиз қайтарилмас'}, {'chapter': 12, 'verse': 111, 'text': 'لَقَدۡ كَانَ فِي قَصَصِهِمۡ عِبۡرَةٞ لِّأُوْلِي ٱلۡأَلۡبَٰبِۗ مَا كَانَ حَدِيثٗا يُفۡتَرَىٰ وَلَٰكِن تَصۡدِيقَ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ كُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ\nДарҳақиқат, уларнинг қиссаларида ақл эгалари учун ибрат бордир. (Ушбу Қуръон) тўқиб чиқариладиган сўз эмас, балки ўзидан аввалги нарсаларни (яъни, самовий китобларни) тасдиқ этувчи, унга иймон келтирадиган қавм учун барча нарсаларни муфассал баён қилиб берувчи ҳидоят ва раҳмат (бўлган бир Китобдир)'}, {'chapter': 13, 'verse': 1, 'text': 'الٓمٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِۗ وَٱلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ ٱلۡحَقُّ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ\nАлиф, Лом, Мим, Ро. Ушбу (оятлар) Китоб (Қуръон) оятларидир. (Эй Муҳаммад алайҳис-салоту вас-салом), сизга нозил қилинган бу нарса (яъни, Қуръон айни) ҳақиқатдир, лекин одамларнинг кўплари иймон келтирмайдилар'}, {'chapter': 13, 'verse': 2, 'text': 'ٱللَّهُ ٱلَّذِي رَفَعَ ٱلسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ يُدَبِّرُ ٱلۡأَمۡرَ يُفَصِّلُ ٱلۡأٓيَٰتِ لَعَلَّكُم بِلِقَآءِ رَبِّكُمۡ تُوقِنُونَ\nАллоҳ осмонларни сизлар кўргудек устунларсиз кўтариб қўйган, сўнгра (Зоти ва Улуғлигига лойиқ тарзда) Аршга юксалиб, қуёш ва ойни (Ўз амрига) бўйсундирган Зотдир. Уларнинг ҳар бири муайян муддатгача (яъни, Қиёмат қойим бўлгунича) сайр қилаверар. (Аллоҳ) барча ишнинг тадбирини қилур ва сизлар (Қиёмат Кунида унга) Парвардигорингизга рўбарў бўлишга иймон келтиришингиз учун Ўз оятларини муфассал баён қилур'}, {'chapter': 13, 'verse': 3, 'text': 'وَهُوَ ٱلَّذِي مَدَّ ٱلۡأَرۡضَ وَجَعَلَ فِيهَا رَوَٰسِيَ وَأَنۡهَٰرٗاۖ وَمِن كُلِّ ٱلثَّمَرَٰتِ جَعَلَ فِيهَا زَوۡجَيۡنِ ٱثۡنَيۡنِۖ يُغۡشِي ٱلَّيۡلَ ٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nУ Ерни ёйиқ қилиб яратиб, унда тоғлар ва дарёлар пайдо қилган Зотдир. У Ердаги ҳар мевани иккитадан — жуфт-жуфт (яъни, эркак-урғочи) қилиб яратди. У кечани кундуз устига ёяр (яъни, кеча-кундуз ҳам бир-бирларининг жуфтларидир). Албатта, бу (мисоллар)да тафаккур қиладиган қавм учун оят-ибратлар бордир'}, {'chapter': 13, 'verse': 4, 'text': 'وَفِي ٱلۡأَرۡضِ قِطَعٞ مُّتَجَٰوِرَٰتٞ وَجَنَّـٰتٞ مِّنۡ أَعۡنَٰبٖ وَزَرۡعٞ وَنَخِيلٞ صِنۡوَانٞ وَغَيۡرُ صِنۡوَانٖ يُسۡقَىٰ بِمَآءٖ وَٰحِدٖ وَنُفَضِّلُ بَعۡضَهَا عَلَىٰ بَعۡضٖ فِي ٱلۡأُكُلِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nБу Ерда бир-бирига қўшни бўлган бўлак-бўлак (яъни, бири унумдор бўлса, бири шўр) ерлар, узумзор боғлар, экинзорлар ва шохлаб кетган-шохламаган хурмолар бўлиб, (уларнинг ҳаммаси ҳам) бир сув билан суғорилур. (Лекин) Биз уларнинг айримларидан айримларини таъмлироқ-ейишлироқ қилиб қўюрмиз. Албатта, бунда ҳам ақл юргизадиган қавм учун оят-ибратлар бордир'}, {'chapter': 13, 'verse': 5, 'text': '۞وَإِن تَعۡجَبۡ فَعَجَبٞ قَوۡلُهُمۡ أَءِذَا كُنَّا تُرَٰبًا أَءِنَّا لَفِي خَلۡقٖ جَدِيدٍۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ وَأُوْلَـٰٓئِكَ ٱلۡأَغۡلَٰلُ فِيٓ أَعۡنَاقِهِمۡۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар (бирон нарсадан) ажаблансангиз, у ҳолда уларнинг (кофирларнинг): «Тупроқ бўлиб кетгач, бизлар яна янгитдан яратиламизми?», деган гаплари ажабланарлидир. Улар Парвардигорларига кофир бўлган кимсалардир. (Қиёмат Кунида) уларнинг бўйинларида кишанлар бўлур. Улар дўзах эгалари бўлиб, ўша жойда абадий қолурлар'}, {'chapter': 13, 'verse': 6, 'text': 'وَيَسۡتَعۡجِلُونَكَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِ وَقَدۡ خَلَتۡ مِن قَبۡلِهِمُ ٱلۡمَثُلَٰتُۗ وَإِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ لِّلنَّاسِ عَلَىٰ ظُلۡمِهِمۡۖ وَإِنَّ رَبَّكَ لَشَدِيدُ ٱلۡعِقَابِ\nУлар сиздан яхшилик — Аллоҳнинг раҳматидан аввал ёмонлик — Аллоҳнинг азоби келишини талаб қиладилар. Улардан илгари ҳам бундай мисоллар (Аллоҳнинг азобига дучор бўлганлар) ўтгандир. Албатта, Парвардигорингиз одамлар ўзларига зулм қилгувчи бўлган ҳолларида ҳам мағфират Эгасидир. Албатта, Парвардигорингиз азоби қаттиқ Зотдир'}, {'chapter': 13, 'verse': 7, 'text': 'وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦٓۗ إِنَّمَآ أَنتَ مُنذِرٞۖ وَلِكُلِّ قَوۡمٍ هَادٍ\nКофир бўлган кимсалар: «Унга (Муҳаммадга) Парвардигори томонидан бирон оят-мўъжиза нозил қилинса эди», дейдилар. Сиз фақат (кофирларни Аллоҳнинг азобидан) огоҳлантиргувчидирсиз. Ҳар бир қавм учун (Аллоҳ йўлига) етакловчи — ҳидоят қилгувчи бордир'}, {'chapter': 13, 'verse': 8, 'text': 'ٱللَّهُ يَعۡلَمُ مَا تَحۡمِلُ كُلُّ أُنثَىٰ وَمَا تَغِيضُ ٱلۡأَرۡحَامُ وَمَا تَزۡدَادُۚ وَكُلُّ شَيۡءٍ عِندَهُۥ بِمِقۡدَارٍ\nФақат Аллоҳгина ҳар бир аёлнинг кўтариб юрган ҳомиласини (ўғилми-қизми, расоми-норасоми, чиройлими-хунукми эканини) ҳам, бачадонлар (муддатидан илгари) ташлайдиган болани ҳам, (тўққиз ойдан) ортиқроқ туриб қоладиган болани ҳам билур. У Зот даргоҳида ҳар бир нарса ўлчовлидир'}, {'chapter': 13, 'verse': 9, 'text': 'عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡكَبِيرُ ٱلۡمُتَعَالِ\nУ ғайбу шаҳодатни (яъни, махфий ва ошкора барча нарсани) билгувчи буюк ва юксак Зотдир'}, {'chapter': 13, 'verse': 10, 'text': 'سَوَآءٞ مِّنكُم مَّنۡ أَسَرَّ ٱلۡقَوۡلَ وَمَن جَهَرَ بِهِۦ وَمَنۡ هُوَ مُسۡتَخۡفِۭ بِٱلَّيۡلِ وَسَارِبُۢ بِٱلنَّهَارِ\nСизларнинг орангиздаги ўз гапини яширган киши ҳам, уни ошкора қилган киши ҳам, кеча қоронғусида яширинувчи ҳам, кундузи очиқ-ошкора юрувчи ҳам (У зот учун) баробардир'}, {'chapter': 13, 'verse': 11, 'text': 'لَهُۥ مُعَقِّبَٰتٞ مِّنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ يَحۡفَظُونَهُۥ مِنۡ أَمۡرِ ٱللَّهِۗ إِنَّ ٱللَّهَ لَا يُغَيِّرُ مَا بِقَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡۗ وَإِذَآ أَرَادَ ٱللَّهُ بِقَوۡمٖ سُوٓءٗا فَلَا مَرَدَّ لَهُۥۚ وَمَا لَهُم مِّن دُونِهِۦ مِن وَالٍ\nУнинг (яъни, ҳар инсоннинг) олдида ҳам, ортида ҳам таъқиб қилгувчи (фаришталар) бўлиб, улар Аллоҳнинг амри билан уни сақлаб-муҳофаза қилиб турурлар. Аниқки, то бирон қавм ўзларини ўзгартирмагунларича Аллоҳ уларнинг аҳволини ўзгартирмас. Қачон Аллоҳ бирон қавмга ёмонлик — бало юборишни ирода қилса, бас, уни қайтариб бўлмас. Ва улар учун Ундан ўзга ҳоким йўқдир'}, {'chapter': 13, 'verse': 12, 'text': 'هُوَ ٱلَّذِي يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنشِئُ ٱلسَّحَابَ ٱلثِّقَالَ\n(Эй одамлар, Аллоҳ) сизларга (момақалдироқдан) қўрққан ва (ёмғир ёғишидан) умидвор бўлган ҳолингизда чақмоқни кўргизадиган ва вазмин булутларни пайдо қиладиган Зотдир'}, {'chapter': 13, 'verse': 13, 'text': 'وَيُسَبِّحُ ٱلرَّعۡدُ بِحَمۡدِهِۦ وَٱلۡمَلَـٰٓئِكَةُ مِنۡ خِيفَتِهِۦ وَيُرۡسِلُ ٱلصَّوَٰعِقَ فَيُصِيبُ بِهَا مَن يَشَآءُ وَهُمۡ يُجَٰدِلُونَ فِي ٱللَّهِ وَهُوَ شَدِيدُ ٱلۡمِحَالِ\nМомақалдироқ ҳам У зотга (Аллоҳга) ҳамду сано билан тасбеҳ айтур. Фаришталар ҳам У зотдан қўрққанлари сабабли (тасбеҳ айтурлар). У зот чақмоқлар юбориб, улар билан Ўзи хоҳлаган кимсаларни — у кимсалар Аллоҳ хусусида талашиб-тортишиб турган ҳолларида, урар. У (ушлаган вақтида) қаттиқ ушловчи Зотдир'}, {'chapter': 13, 'verse': 14, 'text': 'لَهُۥ دَعۡوَةُ ٱلۡحَقِّۚ وَٱلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَجِيبُونَ لَهُم بِشَيۡءٍ إِلَّا كَبَٰسِطِ كَفَّيۡهِ إِلَى ٱلۡمَآءِ لِيَبۡلُغَ فَاهُ وَمَا هُوَ بِبَٰلِغِهِۦۚ وَمَا دُعَآءُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ\nҲақиқий дуо-илтижо фақат Унга қилинур. Ундан ўзга (мушриклар) дуо-илтижо қилаётган бутлар у мушрикларнинг биронта дуосини мустажоб қилмас. Илло улар бир кимсага ўхшайдиларки, у кимса кафтларини сувга ёзиб, у сув оғзига етишини кутиб турар. Ҳолбуки, у (сув) унинг оғзига етгувчи эмасдир. (Яъни, худди сув жонсиз нарса бўлгани туфайли ташна кишининг ҳолидан бехабар бўлиши, бинобарин, унинг оғзига бориб етмагани каби у мушриклар дуо-илтижо қиладиган бутлари ҳам жонсиз нарса бўлганлари сабабли уларнинг дуоларини эшитмас — мустажоб қилмас.) Бундай кофирларнинг дуолари мутлақо хато — бефойдадир'}, {'chapter': 13, 'verse': 15, 'text': 'وَلِلَّهِۤ يَسۡجُدُۤ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَظِلَٰلُهُم بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ۩\nОсмонлар ва Ердаги барча жонзот ва уларнинг соялари хоҳ истасинлар, хоҳ истамасинлар, эрта-ю кеч фақат Аллоҳгагина сажда қиладилар — бўйсунадилар'}, {'chapter': 13, 'verse': 16, 'text': 'قُلۡ مَن رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ قُلِ ٱللَّهُۚ قُلۡ أَفَٱتَّخَذۡتُم مِّن دُونِهِۦٓ أَوۡلِيَآءَ لَا يَمۡلِكُونَ لِأَنفُسِهِمۡ نَفۡعٗا وَلَا ضَرّٗاۚ قُلۡ هَلۡ يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ أَمۡ هَلۡ تَسۡتَوِي ٱلظُّلُمَٰتُ وَٱلنُّورُۗ أَمۡ جَعَلُواْ لِلَّهِ شُرَكَآءَ خَلَقُواْ كَخَلۡقِهِۦ فَتَشَٰبَهَ ٱلۡخَلۡقُ عَلَيۡهِمۡۚ قُلِ ٱللَّهُ خَٰلِقُ كُلِّ شَيۡءٖ وَهُوَ ٱلۡوَٰحِدُ ٱلۡقَهَّـٰرُ\n(Эй Муҳаммад алайҳис-салоту вас-салом) айтинг (қавмингиздан сўранг: «Осмонлар ва Ернинг Парвардигори ким?» «Аллоҳ», деб жавоб қилинг (чунки, улар бундан бошқа жавоб топа олмайдилар. Улардан): «Бас, Ўша (Аллоҳни қўйиб, (ўзгалар эмас, ҳатто) ўзларига ҳам фойда-зиён етказишга қодир бўлмайдиган бутларни ушлаб олдингизми?», деб сўранг. Яна айтингки, «Кўр (гумроҳ) билан кўрувчи (ҳақ йўлга ҳидоят топган киши) баробар бўлурми? Ёки зулматлар билан нур баробар бўлурми?» Ё улар Аллоҳ яратгани каби ярата оладиган бутларни У зотга шерик қилишиб олиб, сўнгра уларга яратилган нарсаларни (Аллоҳ яратганми ёки сиғинаётган бутлари яратганми) номаълум бўлиб қолдими? Айтинг: «Аллоҳ барча нарсани яратгувчидир ва У танҳо ғолибдир»'}, {'chapter': 13, 'verse': 17, 'text': 'أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَسَالَتۡ أَوۡدِيَةُۢ بِقَدَرِهَا فَٱحۡتَمَلَ ٱلسَّيۡلُ زَبَدٗا رَّابِيٗاۖ وَمِمَّا يُوقِدُونَ عَلَيۡهِ فِي ٱلنَّارِ ٱبۡتِغَآءَ حِلۡيَةٍ أَوۡ مَتَٰعٖ زَبَدٞ مِّثۡلُهُۥۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ ٱلۡحَقَّ وَٱلۡبَٰطِلَۚ فَأَمَّا ٱلزَّبَدُ فَيَذۡهَبُ جُفَآءٗۖ وَأَمَّا مَا يَنفَعُ ٱلنَّاسَ فَيَمۡكُثُ فِي ٱلۡأَرۡضِۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ\n(Аллоҳ) осмондан, сув (ёмғир) ёғдирганида жилғалар тўлиб-тошиб оқиб, бу сел ўз устида кўпикларни ҳам кўтариб келур. (Одамлар) зеб-зийнат ёки асбоб-ускуна ясаш учун ўтда эритадиган нарса-маъданлардан ҳам шунга ўхшаш кўпик (чиқинди пайдо) бўлур. Аллоҳ ҳақ билан ботилни мана шундай (мисол билан) баён қилур. Энди у кўпик-чиқинди ўз-ўзидан йўқ бўлиб кетур. Одамларга фойдали бўлган нарса эса Ер юзида қолур. Аллоҳ мисолларни мана шундай баён қилур'}, {'chapter': 13, 'verse': 18, 'text': 'لِلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمُ ٱلۡحُسۡنَىٰۚ وَٱلَّذِينَ لَمۡ يَسۡتَجِيبُواْ لَهُۥ لَوۡ أَنَّ لَهُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡاْ بِهِۦٓۚ أُوْلَـٰٓئِكَ لَهُمۡ سُوٓءُ ٱلۡحِسَابِ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ\nПарвардигорларига итоат этган зотлар учун гўзал мукофот — жаннат бордир. У зотга итоат қилмаган кимсалар эса агар Ердаги бор молу-давлат (яна) икки баробар (кўпайиб) уларнинг ўзлариники бўлса, албатта уни (Аллоҳнинг азобидан қутулиш учун) фидо қилган бўлур эдилар. Улар учун энг ёмон ҳисоб-китоб бўлур. Уларнинг жойлари жаҳаннамдир. Нақадар ёмон жой у'}, {'chapter': 13, 'verse': 19, 'text': '۞أَفَمَن يَعۡلَمُ أَنَّمَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ ٱلۡحَقُّ كَمَنۡ هُوَ أَعۡمَىٰٓۚ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ ٱلۡأَلۡبَٰبِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ахир сизга Парвардигорингиз томонидан нозил қилинган нарса аниқ ҳақиқат эканини биладиган — иймон келтирадиган киши (кўнгил кўзи) кўр кимсага ўхшайдими? Ақл эгаларигина эслатма олурлар'}, {'chapter': 13, 'verse': 20, 'text': 'ٱلَّذِينَ يُوفُونَ بِعَهۡدِ ٱللَّهِ وَلَا يَنقُضُونَ ٱلۡمِيثَٰقَ\nУлар фақат Аллоҳга берган аҳдларига вафо қиладиган ва мийсоқ — ваъдаларини бузмайдиган зотлардир'}, {'chapter': 13, 'verse': 21, 'text': 'وَٱلَّذِينَ يَصِلُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيَخۡشَوۡنَ رَبَّهُمۡ وَيَخَافُونَ سُوٓءَ ٱلۡحِسَابِ\nУлар Аллоҳ боғланишга буюрган нарсаларни (яъни, қариндош-уруғлар билан алоқани) боғлайдилар. Парвардигорларидан қўрқадилар, ҳисоб-китобга дучор бўлишдан қўрқиб, (доим чиройли амаллар қилишга интиладилар)'}, {'chapter': 13, 'verse': 22, 'text': 'وَٱلَّذِينَ صَبَرُواْ ٱبۡتِغَآءَ وَجۡهِ رَبِّهِمۡ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ أُوْلَـٰٓئِكَ لَهُمۡ عُقۡبَى ٱلدَّارِ\nПарвардигорларининг Юзини кўзлаб (меҳнат-машаққатларга) сабр-тоқат қилиб намозларини тўкис адо этган ва Биз ризқ қилиб берган нарсалардан махфий ва ошкора инфоқ-эҳсон қилган ҳамда ёмонликни яхшилик билан қайтарадиган зотлар — ана ўшалар учун Охират диёри бордир'}, {'chapter': 13, 'verse': 23, 'text': 'جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّـٰتِهِمۡۖ وَٱلۡمَلَـٰٓئِكَةُ يَدۡخُلُونَ عَلَيۡهِم مِّن كُلِّ بَابٖ\n(У диёр) абадий туриладиган жаннатлар бўлиб, улар у жойларга, ота-боболари, жуфти ҳалоллари ва зурриётларидан иборат бўлган солиҳ бандалар билан бирга кирурлар. Сўнг уларнинг ҳузурларига ҳар эшикдан фаришталар кириб (дерлар)'}, {'chapter': 13, 'verse': 24, 'text': 'سَلَٰمٌ عَلَيۡكُم بِمَا صَبَرۡتُمۡۚ فَنِعۡمَ عُقۡبَى ٱلدَّارِ\n«(Аллоҳ йулида меҳнат-машаққатларга) сабр-тоқат қилиб ўтганларингиз сабабли (энди бу жаннатларда) сизларга тинчлик-осойишталик бўлгай. Бу Охират диёри нақадар яхши!»'}, {'chapter': 13, 'verse': 25, 'text': 'وَٱلَّذِينَ يَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي ٱلۡأَرۡضِ أُوْلَـٰٓئِكَ لَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوٓءُ ٱلدَّارِ\nАллоҳга берган аҳд-паймонларини мустаҳкам бўлганидан кейин бузадиган, Аллоҳ боғланишига буюрган нарсаларни узадиган ва Ер юзида бузғунчилик қилиб юрадиган кимсалар ҳам борки, улар учун (Аллоҳнинг) лаънати бўлур ва улар учун энг ёмон диёр — жаҳаннам бордир'}, {'chapter': 13, 'verse': 26, 'text': 'ٱللَّهُ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ وَفَرِحُواْ بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا فِي ٱلۡأٓخِرَةِ إِلَّا مَتَٰعٞ\nАллоҳ, Ўзи хоҳлаган кишиларнинг ризқини кенг қилур (ва Ўзи хоҳлаган кишиларнинг ризқини) танг қилур. (Мушриклар) мана шу ҳаёти дунё билан шод-хуррам бўлдилар, ҳолбуки бу ҳаёти дунё Охират олдида фақат бир арзимас матодир'}, {'chapter': 13, 'verse': 27, 'text': 'وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَنۡ أَنَابَ\nКуфр йўлини тутган кимсалар: «Унга (Муҳаммадга) Парвардигори томонидан бирон оят-мўъжиза нозил қилинса эди», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Албатта, Аллоҳ Ўзи хоҳлаган кимсаларни гумроҳ қилур ва (Аллоҳга) ижобат-тавба қилган кишиларни Ўз (динига) ҳидоят қилур»'}, {'chapter': 13, 'verse': 28, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَتَطۡمَئِنُّ قُلُوبُهُم بِذِكۡرِ ٱللَّهِۗ أَلَا بِذِكۡرِ ٱللَّهِ تَطۡمَئِنُّ ٱلۡقُلُوبُ\nУлар иймон келтирган ва қалблари Аллоҳни зикр қилиш — эслаш билан ором оладиган зотлардир. Огоҳ бўлингизким, Аллоҳни зикр қилиш билан қалблар ором олур'}, {'chapter': 13, 'verse': 29, 'text': 'ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ طُوبَىٰ لَهُمۡ وَحُسۡنُ مَـَٔابٖ\nИймон келтириб, яхши амаллар қилган зотлар учун хушнудлик ва гўзал оқибат — жаннат бордир'}, {'chapter': 13, 'verse': 30, 'text': 'كَذَٰلِكَ أَرۡسَلۡنَٰكَ فِيٓ أُمَّةٖ قَدۡ خَلَتۡ مِن قَبۡلِهَآ أُمَمٞ لِّتَتۡلُوَاْ عَلَيۡهِمُ ٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَهُمۡ يَكۡفُرُونَ بِٱلرَّحۡمَٰنِۚ قُلۡ هُوَ رَبِّي لَآ إِلَٰهَ إِلَّا هُوَ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ مَتَابِ\n(Эй Муҳаммад алайҳис-салоту вас-салом) шундай қилиб, Биз сизни (кўпдан-кўп) авлодлардан кейин келган бир авлодга — улар Меҳрибон (Аллоҳ)ни инкор қилиб турган ҳолларида — Биз сизга ваҳий қилган нарсаларни тиловат қилиб беришингиз учун пайғамбар қилиб юбордик. Айтинг: «У (Меҳрибон Аллоҳ) менинг Парвардигоримдир. Ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи бордир. Унинг Ўзига таваккул қилдим — суяндим ва Унинг Ўзига тавба-тазарру қилурман»'}, {'chapter': 13, 'verse': 31, 'text': 'وَلَوۡ أَنَّ قُرۡءَانٗا سُيِّرَتۡ بِهِ ٱلۡجِبَالُ أَوۡ قُطِّعَتۡ بِهِ ٱلۡأَرۡضُ أَوۡ كُلِّمَ بِهِ ٱلۡمَوۡتَىٰۗ بَل لِّلَّهِ ٱلۡأَمۡرُ جَمِيعًاۗ أَفَلَمۡ يَاْيۡـَٔسِ ٱلَّذِينَ ءَامَنُوٓاْ أَن لَّوۡ يَشَآءُ ٱللَّهُ لَهَدَى ٱلنَّاسَ جَمِيعٗاۗ وَلَا يَزَالُ ٱلَّذِينَ كَفَرُواْ تُصِيبُهُم بِمَا صَنَعُواْ قَارِعَةٌ أَوۡ تَحُلُّ قَرِيبٗا مِّن دَارِهِمۡ حَتَّىٰ يَأۡتِيَ وَعۡدُ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يُخۡلِفُ ٱلۡمِيعَادَ\nАгар бир Қуръон бўлиб, унинг ёрдами билан тоғлар жойидан жилдирилса, ё унинг ёрдами билан Ер ёрилиб (дарёлар пайдо қилинса, ёки унинг ёрдами билан ўликлар тилга киритилса ҳам (кофир бўлган кимсалар иймон келтирмайдилар). Йўқ, барча иш ёлғиз Аллоҳнинг (илкидадир. Яъни, Аллоҳ хоҳласа иймон келтирадилар, агар У зот хоҳламас экан, барча талаблари бажо қилинганида ҳам улар иймон келтирмайдилар). Иймон келтирган кишилар агар Аллоҳ хоҳласа, шак-шубҳасиз, (бирон мўъжиза нозил қилмасданоқ) барча одамларни ҳидоят қилишини (бу Аллоҳ учун ҳеч нарса эмаслигини) билмадиларми? (Лекин Аллоҳнинг Ўзигина биладиган ҳикмат сабабли У зот ҳидоят йўлига юриш ё юрмасликни бандаларининг ихтиёрига ташлаб қўйгандир). То Аллоҳнинг ваъдаси келгунича (яъни, Макка мусулмонлар тарафидан фатҳ қилингунча ҳам) куфр йўлини тутган кимсаларга ўзларининг қилган қилиқлари сабабли мудом турли офат-балолар етар ёки уларнинг диёрларига яқин жойларга (кулфат) тушиб (уларни безовта, беҳаловат қилур). Албатта, Аллоҳ ваъдасига хилоф қилмас'}, {'chapter': 13, 'verse': 32, 'text': 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَأَمۡلَيۡتُ لِلَّذِينَ كَفَرُواْ ثُمَّ أَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиздан аввалги пайғамбарларнинг устидан ҳам кулиб-масхара қилинганида, Мен ўша (пайғамбарларини ёлғончи қилиб) куфр йўлини тутган кимсаларга (бир оз) муҳлат бериб, сўнгра уларни (Ўзимнинг азобим билан) ушлаганман. Сўнг уларни қандай жазолаганимни (билурсиз)'}, {'chapter': 13, 'verse': 33, 'text': 'أَفَمَنۡ هُوَ قَآئِمٌ عَلَىٰ كُلِّ نَفۡسِۭ بِمَا كَسَبَتۡۗ وَجَعَلُواْ لِلَّهِ شُرَكَآءَ قُلۡ سَمُّوهُمۡۚ أَمۡ تُنَبِّـُٔونَهُۥ بِمَا لَا يَعۡلَمُ فِي ٱلۡأَرۡضِ أَم بِظَٰهِرٖ مِّنَ ٱلۡقَوۡلِۗ بَلۡ زُيِّنَ لِلَّذِينَ كَفَرُواْ مَكۡرُهُمۡ وَصُدُّواْ عَنِ ٱلسَّبِيلِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ\nҲар бир жоннинг қилган амали устидан (кузатиб) тургувчи Зот ҳеч нарсани кўрмайдиган, эшитмайдиган жонсиз бутларга ўхшайдими?» Улар (турли маҳлуқларни) Аллоҳга шерик қилиб олдилар. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «У бутларингизнинг номларини (ва уларнинг қўлларидан келадиган ишларини) айтинглар-чи? Балки (Аллоҳга) Ер юзидаги билмайдиган бирон нарсасининг хабарини берарсизлар?! Ёки фақат юзаки гап билан (яъни, ҳеч қандай иш-амал қўлидан келмайдиган жонсиз нарсаларни қуруқ сўзларга ишониб худо деб сиғиниб юрибсизларми?») Йўқ. Куфр йўлини тутган кимсаларга ўзларининг макр-алдовлари чиройли кўрасатилди ва улар ҳақиқат йўлидан тўсилдилар. Кимни Аллоҳ йўлдан оздирса, унинг учун бирон ҳидоят қилгувчи бўлмас'}, {'chapter': 13, 'verse': 34, 'text': 'لَّهُمۡ عَذَابٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَشَقُّۖ وَمَا لَهُم مِّنَ ٱللَّهِ مِن وَاقٖ\nУлар учун ҳаёти дунёда ҳам азоб бўлур. Аммо Охират азоби оғирроқ-қаттиқроқдир. Уларни Аллоҳ (азоби)дан сақловчи бўлмас'}, {'chapter': 13, 'verse': 35, 'text': '۞مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ أُكُلُهَا دَآئِمٞ وَظِلُّهَاۚ تِلۡكَ عُقۡبَى ٱلَّذِينَ ٱتَّقَواْۚ وَّعُقۡبَى ٱلۡكَٰفِرِينَ ٱلنَّارُ\nТақво эгалари учун ваъда қилинган жаннатнинг мисоли (будир): унинг остидан дарёлар оқур, мевалари ва соялари боқийдир. Бу тақво-парҳез билан ўтган зотларнинг оқибат-келажагидир. Кофирларнинг оқибати эса дўзахдир'}, {'chapter': 13, 'verse': 36, 'text': 'وَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَفۡرَحُونَ بِمَآ أُنزِلَ إِلَيۡكَۖ وَمِنَ ٱلۡأَحۡزَابِ مَن يُنكِرُ بَعۡضَهُۥۚ قُلۡ إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ وَلَآ أُشۡرِكَ بِهِۦٓۚ إِلَيۡهِ أَدۡعُواْ وَإِلَيۡهِ مَـَٔابِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Китоб (Таврот, Инжил) ато этган кишилар Сизга нозил қилинган нарса (Қуръон) сабабли шодланадилар. (Яҳудий ва насронийлардан сизга қарши бўлган) тўдалар орасида унинг (Қуръоннинг) бир қисмини инкор қиладиган кимсалар ҳам бордир. (Уларга) айтинг: «Албатта, мен ёлғиз Аллоҳга ибодат қилишга ва Унга (бировни) шерик қилмасликка маъмурман (амр этилганман). Мен факат Ўша зотга (ибодат қилишга) даъват қилурман ва ёлғиз Унинг Ўзига қайтурман»'}, {'chapter': 13, 'verse': 37, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَٰهُ حُكۡمًا عَرَبِيّٗاۚ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِيّٖ وَلَا وَاقٖ\nШунингдек, (яъни, илгари ўтган пайғамбарларимизга ўз тилларида ваҳий юборганимиз каби) Биз уни (Қуръонни) арабий ҳукм ҳолида нозил қилдик. Қасамки, агар сиз ўзингизга келган бу билимдан сўнг уларнинг ҳою ҳавасларига эргашсангиз, сиз учун Аллоҳ томонидан бирон дўст ҳам, сақловчи ҳам бўлмас'}, {'chapter': 13, 'verse': 38, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ وَجَعَلۡنَا لَهُمۡ أَزۡوَٰجٗا وَذُرِّيَّةٗۚ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ لِكُلِّ أَجَلٖ كِتَابٞ\nДарҳақиқат, сиздан илгари ҳам (кўп) пайғамбарлар юборганмиз ва уларга жуфтлар ва зурриётлар берганмиз. Бирон пайғамбар учун бирон оят-мўъжиза келтириш имкони бўлмаган, магар Аллоҳнинг изни-иродаси билангина (келтирганлар). Ҳар бир вақт учун (муносиб) китоб-ҳукм бордир'}, {'chapter': 13, 'verse': 39, 'text': 'يَمۡحُواْ ٱللَّهُ مَا يَشَآءُ وَيُثۡبِتُۖ وَعِندَهُۥٓ أُمُّ ٱلۡكِتَٰبِ\nАллоҳ Ўзи хоҳлаган нарсани (яъни, ҳукмни) ўчирур ва (Ўзи хоҳлаган ҳукмни) устивор қилур. Асл Китоб (яъни, барча нарсаларнинг билими) Унинг даргоҳидадир'}, {'chapter': 13, 'verse': 40, 'text': 'وَإِن مَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُ وَعَلَيۡنَا ٱلۡحِسَابُ\nАгар Биз сизга (кофирларга) ваъда қилган айрим нарсаларни (яъни, азобларимизни) кўрсатсак ҳам ёки сизни (уларни азоблашдан илгари) вафот қилдирсак ҳам, албатта, сизнинг зиммангизда (барибир ҳақ динни одамларга тўла) етказиш, Бизнинг зиммамизда эса ҳисоб-китоб қилиш бордир'}, {'chapter': 13, 'verse': 41, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا نَأۡتِي ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَاۚ وَٱللَّهُ يَحۡكُمُ لَا مُعَقِّبَ لِحُكۡمِهِۦۚ وَهُوَ سَرِيعُ ٱلۡحِسَابِ\n(Макка мушриклари) Биз уларнинг ерларига келиб уни атрофидан камайтираётганимизни (яъни, мусулмонлар қадам-бақадам фатҳ қилиб келаётганларини) кўрмадиларми? Аллоҳ ҳукм қилур — Унинг ҳукмини текширувчи бўлмас. У тез ҳисоб-китоб қилгувчидир'}, {'chapter': 13, 'verse': 42, 'text': 'وَقَدۡ مَكَرَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَلِلَّهِ ٱلۡمَكۡرُ جَمِيعٗاۖ يَعۡلَمُ مَا تَكۡسِبُ كُلُّ نَفۡسٖۗ وَسَيَعۡلَمُ ٱلۡكُفَّـٰرُ لِمَنۡ عُقۡبَى ٱلدَّارِ\nУлардан илгари ўтган кимсалар ҳам (ўзларининг пайғамбарларига қарши турли) макр-ҳийлалар қилганлар. Аммо барча макр-ҳийлалар Аллоҳнинг (илкидадир. Демак, Ўша зотдан амр бўлмаса, бирон макр фойда - зарар етказа олмайди). У зот ҳар бир жоннинг қилаётган ишини билур. Яқинда кофирлар Охират диёри кимники эканини билиб олажаклар'}, {'chapter': 13, 'verse': 43, 'text': 'وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَسۡتَ مُرۡسَلٗاۚ قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡ وَمَنۡ عِندَهُۥ عِلۡمُ ٱلۡكِتَٰبِ\n(Эй Муҳаммад алайҳис-салоту вас-салом, сизга) кофир бўлган кимсалар: «Пайғамбар эмассан», дейдилар. Айтинг: «Мен билан сизларнинг орамизда Аллоҳ ва Китоб — Қуръон илмидан хабардор бўлган (яъни, Аллоҳнинг каломини бандаларнинг сўзларидан ажрата оладиган) ҳар бир киши етарли гувоҳдир'}, {'chapter': 14, 'verse': 1, 'text': 'الٓرۚ كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ لِتُخۡرِجَ ٱلنَّاسَ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ بِإِذۡنِ رَبِّهِمۡ إِلَىٰ صِرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ\nАлиф, Лом, Ро. (Эй Муҳаммад алайҳис-салоту вас-салом, бу Қуръон) Биз сизга, одамларни Парвардигорларининг изни-иродаси билан зулматлардан нурга — қудрат ва ҳамду сано эгаси бўлган Зотнинг йўлига — олиб чиқишингиз учун нозил қилган Китобдир'}, {'chapter': 14, 'verse': 2, 'text': 'ٱللَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَوَيۡلٞ لِّلۡكَٰفِرِينَ مِنۡ عَذَابٖ شَدِيدٍ\nАллоҳ — осмонлар ва Ердаги бор нарса Ўзиники бўлган Зотдир. Кофирлар учун қаттиқ азоб — ҳалокат бўлгай'}, {'chapter': 14, 'verse': 3, 'text': 'ٱلَّذِينَ يَسۡتَحِبُّونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا عَلَى ٱلۡأٓخِرَةِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجًاۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلِۭ بَعِيدٖ\nУлар ҳаёти дунёни Охиратдан афзал биладиган, (одамларни) Аллоҳнинг йўлидан тўсадиган ва уни (эгишни-бузишни истайдиган) кимсалардир. Улар (ҳақ йўлдан) йироқ бўлган гумроҳликдадирлар'}, {'chapter': 14, 'verse': 4, 'text': 'وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوۡمِهِۦ لِيُبَيِّنَ لَهُمۡۖ فَيُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nБиз ҳар бир пайғамбарни (ҳукмларимизни) баён қилиб бериши учун ўз қавмининг тили билан (сўзлайдиган қилиб) юборганмиз. Сўнгра Аллоҳ хоҳлаган кимсани йўлдан оздидур ва Ўзи хоҳлаган кишини ҳидоят қилур. У қудратли, ҳикматли Зотдир'}, {'chapter': 14, 'verse': 5, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَآ أَنۡ أَخۡرِجۡ قَوۡمَكَ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ وَذَكِّرۡهُم بِأَيَّىٰمِ ٱللَّهِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ\nДарҳақиқат, Биз Мусони (Бани Исроил қавмига) Ўз оятларимиз билан юбориб (унга дедик): «Қавмингни (куфр) зулматларидан (иймон) нурига олиб чиққин ва уларга Аллоҳнинг неъматларини эслатгин! Албатта, бу (неъматлар)да ҳар бир сабр-қаноатли, шукр қилгувчи киши учун оят-ибратлар бордир»'}, {'chapter': 14, 'verse': 6, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ أَنجَىٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ وَيُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ\nЎшанда Мусо қавмига айтди: «Аллоҳнинг сизларга берган неъматини — сизларни оғир азоблар билан қийнаётган, ўғилларингизни сўйиб, хотинларингизни тирик қолдираётган Фиръавн одамларидан қутқарганимизни эслангиз. Ана ўша ишларда Парвардигорингиз томонидан буюк синов бордир'}, {'chapter': 14, 'verse': 7, 'text': 'وَإِذۡ تَأَذَّنَ رَبُّكُمۡ لَئِن شَكَرۡتُمۡ لَأَزِيدَنَّكُمۡۖ وَلَئِن كَفَرۡتُمۡ إِنَّ عَذَابِي لَشَدِيدٞ\nЯна Парвардигорингиз билдирган (бу сўзларни) эслангиз: «Қасамки, агар сизлар (Менинг берган неъматларимга) шукр қилсангизлар, албатта (уларни янада) зиёда қилурман. Энди агар куфрони (неъмат) қилсангизлар, албатта, азобим ҳам жуда қаттиқдир»'}, {'chapter': 14, 'verse': 8, 'text': 'وَقَالَ مُوسَىٰٓ إِن تَكۡفُرُوٓاْ أَنتُمۡ وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗا فَإِنَّ ٱللَّهَ لَغَنِيٌّ حَمِيدٌ\nМусо яна деди: «Агар сизлар ўзингиз ва Ер юзидаги барча кишилар кофир бўлсалар ҳам (Аллоҳга бирон зиён етказа олмайсизлар). Зеро, Аллоҳ (сизларнинг шукр қилишингиздан) беҳожат, ҳамду сано Эгасидир»'}, {'chapter': 14, 'verse': 9, 'text': 'أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ ٱلَّذِينَ مِن قَبۡلِكُمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمۡ لَا يَعۡلَمُهُمۡ إِلَّا ٱللَّهُۚ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَرَدُّوٓاْ أَيۡدِيَهُمۡ فِيٓ أَفۡوَٰهِهِمۡ وَقَالُوٓاْ إِنَّا كَفَرۡنَا بِمَآ أُرۡسِلۡتُم بِهِۦ وَإِنَّا لَفِي شَكّٖ مِّمَّا تَدۡعُونَنَآ إِلَيۡهِ مُرِيبٖ\nСизларга илгари ўтган кишиларнинг — Нуҳ қавмининг, Од, Самуд (қабилаларининг) ва улардан кейин ўтган, (сону саноқларини) фақат Аллоҳгина биладиган қавмларнинг хабари келмадими? Уларга пайғамбарлари (ўзларининг ҳақ пайғамбар эканликларини билдирувчи) ҳужжатлар келтирганларида, улар қўллари билан (пайғамбарларнинг) оғизларини тўсиб, дедилар: «Биз сизлар элчи қилиб юборилган динга кофир бўлдик. Чунки биз сизлар бизни даъват қилаётган динингиздан шак-шубҳададирмиз»'}, {'chapter': 14, 'verse': 10, 'text': '۞قَالَتۡ رُسُلُهُمۡ أَفِي ٱللَّهِ شَكّٞ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَدۡعُوكُمۡ لِيَغۡفِرَ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرَكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۚ قَالُوٓاْ إِنۡ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا تُرِيدُونَ أَن تَصُدُّونَا عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتُونَا بِسُلۡطَٰنٖ مُّبِينٖ\nПайғамбарлари айтдилар: «Осмонлар ва Ернинг яратувчиси бўлмиш Аллоҳ(нинг борлиги ва бирлиги) хусусида шак-шубҳа бўлиши мумкинми?! У зот сизларнинг гуноҳларингизни мағфират қилиш ва ўзларингизни маълум муддатгача (ҳалок қилмай) қолдириш учун (иймонга) даъват қилмоқда-ку!» Улар дедилар: «Сизлар ҳам худди ўзимизга ўхшаган одамларсиз, фақат бизни ота-боболаримиз ибодат қилиб ўтган нарсадан (санамларга сиғинишдан) тўсмоқчисизлар, холос. Бас, (агар чин пайғамбар бўлсангизлар) бизга аниқ бир ҳужжат далил келтиринглар-чи?!»'}, {'chapter': 14, 'verse': 11, 'text': 'قَالَتۡ لَهُمۡ رُسُلُهُمۡ إِن نَّحۡنُ إِلَّا بَشَرٞ مِّثۡلُكُمۡ وَلَٰكِنَّ ٱللَّهَ يَمُنُّ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَمَا كَانَ لَنَآ أَن نَّأۡتِيَكُم بِسُلۡطَٰنٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nПайғамбарлари уларга айтдилар: «(Ҳа), биз ҳам худди сизларга ўхшаган одамлармиз, лекин Аллоҳ Ўзи хоҳлаган бандаларига (пайғамбарлик) инъом қилур. Биз сизларга бирон ҳужжат келтира олмаймиз, магар Аллоҳнинг изни-иродаси билангина (келтиришимиз мумкин). Иймон келтирган кишилар ёлғиз Аллоҳгагина таваккул қилсинлар'}, {'chapter': 14, 'verse': 12, 'text': 'وَمَا لَنَآ أَلَّا نَتَوَكَّلَ عَلَى ٱللَّهِ وَقَدۡ هَدَىٰنَا سُبُلَنَاۚ وَلَنَصۡبِرَنَّ عَلَىٰ مَآ ءَاذَيۡتُمُونَاۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ\n(Ахир) бизни (тўғри) йўлимизга ҳидоят қилган Аллоҳга нечун таваккул қилмаймиз. Албатта, биз сизлар етказган озорларга сабр қилурмиз. Таваккул қилгувчилар ёлғиз Аллоҳга таваккул қилсинлар»'}, {'chapter': 14, 'verse': 13, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِرُسُلِهِمۡ لَنُخۡرِجَنَّكُم مِّنۡ أَرۡضِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۖ فَأَوۡحَىٰٓ إِلَيۡهِمۡ رَبُّهُمۡ لَنُهۡلِكَنَّ ٱلظَّـٰلِمِينَ\nКуфр йўлини тутган кимсалар ўзларининг пайғамбарларига: «Албатта, биз сизларни еримиздан ҳайдаб чиқарурмиз ёки сизлар бизнинг динимизга қайтурсизлар», дедилар. Шунда Парвардигорлари уларга (яъни, пайғамбарларга): «Албатта, Биз бу золимларни ҳалок қилурмиз'}, {'chapter': 14, 'verse': 14, 'text': 'وَلَنُسۡكِنَنَّكُمُ ٱلۡأَرۡضَ مِنۢ بَعۡدِهِمۡۚ ذَٰلِكَ لِمَنۡ خَافَ مَقَامِي وَخَافَ وَعِيدِ\nВа улардан сўнг шу ерни сизлар учун маскан қилурмиз. Бу (ваъда) Менинг ҳузуримда (сўроқ-савол бериб) туришдан қўрқувчи ва Менинг (кофирларни дўзахга гирифтор қилиш хусусидаги) ваъдамдан хавф қилгувчи кишилар учундир», деб ваҳий юборди'}, {'chapter': 14, 'verse': 15, 'text': 'وَٱسۡتَفۡتَحُواْ وَخَابَ كُلُّ جَبَّارٍ عَنِيدٖ\n(Сўнгра пайғамбарлар Аллоҳдан) мадад тиладилар ва барча жабркор-саркаш кимсалар бахтсизликка дучор бўлдилар'}, {'chapter': 14, 'verse': 16, 'text': 'مِّن وَرَآئِهِۦ جَهَنَّمُ وَيُسۡقَىٰ مِن مَّآءٖ صَدِيدٖ\nУнинг (яъни, ҳар бир кофир бўлган кимсанинг) олдида жаҳаннам бордир. (Жаҳаннамда) унга йирингли сувдан берилади'}, {'chapter': 14, 'verse': 17, 'text': 'يَتَجَرَّعُهُۥ وَلَا يَكَادُ يُسِيغُهُۥ وَيَأۡتِيهِ ٱلۡمَوۡتُ مِن كُلِّ مَكَانٖ وَمَا هُوَ بِمَيِّتٖۖ وَمِن وَرَآئِهِۦ عَذَابٌ غَلِيظٞ\n(Ўшанда) уни ютмоқчи бўлади-ю, (томоғидан) ўтказолмайди, унга ҳар томондан ўлим келади-ю, у ўлолмайди. Унинг ортида (бундан-да) оғир азоб бордир'}, {'chapter': 14, 'verse': 18, 'text': 'مَّثَلُ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ أَعۡمَٰلُهُمۡ كَرَمَادٍ ٱشۡتَدَّتۡ بِهِ ٱلرِّيحُ فِي يَوۡمٍ عَاصِفٖۖ لَّا يَقۡدِرُونَ مِمَّا كَسَبُواْ عَلَىٰ شَيۡءٖۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلۡبَعِيدُ\nПарвардигорларига кофир бўлган кимсалар қилган амалларининг мисоли худди бўронли кунда қаттиқ шамол учириб кетган кулга ўхшайди — улар қилган амалларидан бирон фойдага эга бўла олмайдилар. Бу узоқ — қаттиқ йўлдан озишдир'}, {'chapter': 14, 'verse': 19, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ\n(Эй инсон), Аллоҳ осмонлар ва Ерни ҳақ (қонун) билан яратиб қўйганини кўрмадингми?! Агар У зот хоҳласа сизларни кетказиб, (ўрнингизга ўзгаларни) янгитдан яратур'}, {'chapter': 14, 'verse': 20, 'text': 'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ\nБу (иш) Аллоҳ учун қийин эмасдир'}, {'chapter': 14, 'verse': 21, 'text': 'وَبَرَزُواْ لِلَّهِ جَمِيعٗا فَقَالَ ٱلضُّعَفَـٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا مِنۡ عَذَابِ ٱللَّهِ مِن شَيۡءٖۚ قَالُواْ لَوۡ هَدَىٰنَا ٱللَّهُ لَهَدَيۡنَٰكُمۡۖ سَوَآءٌ عَلَيۡنَآ أَجَزِعۡنَآ أَمۡ صَبَرۡنَا مَا لَنَا مِن مَّحِيصٖ\n(Қиёмат Кунида) барча (халойиқ) Аллоҳга рўбарў бўлиб, бечора (эргашувчи)лар мутакаббир кимсаларга (яъни, ўзларининг собиқ йўлбошчиларига): «Бизлар сизларга эргашган эдик, энди сизлар бизларни Аллоҳнинг бирон азобидан қутқара олурмисизлар?!» деганларида, улар айтдилар: «Агар Аллоҳ бизни (ҳақ йўлга) ҳидоят қилганида биз ҳам сизларни ҳидоят қилган бўлур эдик. (Энди эса) дод-вой қиламизми, сабр-қаноат қиламизми, биз учун баробардир — ҳеч қандай нажот йўқдир»'}, {'chapter': 14, 'verse': 22, 'text': 'وَقَالَ ٱلشَّيۡطَٰنُ لَمَّا قُضِيَ ٱلۡأَمۡرُ إِنَّ ٱللَّهَ وَعَدَكُمۡ وَعۡدَ ٱلۡحَقِّ وَوَعَدتُّكُمۡ فَأَخۡلَفۡتُكُمۡۖ وَمَا كَانَ لِيَ عَلَيۡكُم مِّن سُلۡطَٰنٍ إِلَّآ أَن دَعَوۡتُكُمۡ فَٱسۡتَجَبۡتُمۡ لِيۖ فَلَا تَلُومُونِي وَلُومُوٓاْ أَنفُسَكُمۖ مَّآ أَنَا۠ بِمُصۡرِخِكُمۡ وَمَآ أَنتُم بِمُصۡرِخِيَّ إِنِّي كَفَرۡتُ بِمَآ أَشۡرَكۡتُمُونِ مِن قَبۡلُۗ إِنَّ ٱلظَّـٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ\nҚачонки, иш тугагач (яъни, жаннат аҳли жаннатга сазовор бўлиб, дўзахилар дўзахга ҳукм қилингач), шайтон деди: «Албатта, Аллоҳ сизларга ҳақ ваъда қилган эди. Мен эса (ёлғон) ваъдалар бериб, сизларни алдаган эдим. (Лекин) мен учун сизларнинг устингизда ҳеч қандай ҳукмронлик йўқ эди, илло мен сизларни (куфр йўлига) чақиришим биланоқ ўзингиз менга итоат этдингиз. Энди мени эмас, ўзларингизни маломат қилингиз. Мен сизларга ёрдам бера олмайман, сизлар ҳам менга ёрдам бергувчи эмассиз. Албатта, мен сизлар илгари (Аллоҳга) мени шерик қилганингизни инкор қилурман. Албатта, золимлар (яъни, кофирлар) учун аламли азоб бордир'}, {'chapter': 14, 'verse': 23, 'text': 'وَأُدۡخِلَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا بِإِذۡنِ رَبِّهِمۡۖ تَحِيَّتُهُمۡ فِيهَا سَلَٰمٌ\nИймон келтирган ва яхши амаллар қилган зотлар остидан дарёлар оқиб турадиган жаннатларга киритилиб, Парвардигорларининг изни-иродаси билан у жойда мангу қолдилар. Уларнинг (жаннатдаги) салом-аликлари бир-бирларига тинчлик-омонлик тилашдир'}, {'chapter': 14, 'verse': 24, 'text': 'أَلَمۡ تَرَ كَيۡفَ ضَرَبَ ٱللَّهُ مَثَلٗا كَلِمَةٗ طَيِّبَةٗ كَشَجَرَةٖ طَيِّبَةٍ أَصۡلُهَا ثَابِتٞ وَفَرۡعُهَا فِي ٱلسَّمَآءِ\n(Эй инсон), Аллоҳ яхши сўзга (яъни, иймон калимасига) қандай мисол келтирганини кўргин: у сўз худди бир асил дарахтга ўхшайдики, унинг илдизи (ер остига) маҳкам ўрнашган, шохлари эса осмондадир'}, {'chapter': 14, 'verse': 25, 'text': 'تُؤۡتِيٓ أُكُلَهَا كُلَّ حِينِۭ بِإِذۡنِ رَبِّهَاۗ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ لِلنَّاسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ\n(У) Парвардигорининг изни-иродаси билан мудом мева берур. Аллоҳ одамлар эслатма-ибрат олишлари учун мана шундай мисоллар келтирур'}, {'chapter': 14, 'verse': 26, 'text': 'وَمَثَلُ كَلِمَةٍ خَبِيثَةٖ كَشَجَرَةٍ خَبِيثَةٍ ٱجۡتُثَّتۡ مِن فَوۡقِ ٱلۡأَرۡضِ مَا لَهَا مِن قَرَارٖ\nЁмон сўзнинг (яъни, куфр сўзнинг) мисоли эса ердан (илдизи) узилиб қолган, бир жойда (ўрнашиб) қўним топмайдиган нопок дарахтга ўхшар'}, {'chapter': 14, 'verse': 27, 'text': 'يُثَبِّتُ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ بِٱلۡقَوۡلِ ٱلثَّابِتِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَيُضِلُّ ٱللَّهُ ٱلظَّـٰلِمِينَۚ وَيَفۡعَلُ ٱللَّهُ مَا يَشَآءُ\nАллоҳ иймон келтирган кишиларни ҳаёти дунёда ҳам, Охиратда ҳам устивор Сўз (иймон калимаси) билан собитқадам қилур. Золимларни эса Аллоҳ (ҳақ) йўлдан оздирур. Аллоҳ Ўзи хоҳлаган ишни қилур'}, {'chapter': 14, 'verse': 28, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ بَدَّلُواْ نِعۡمَتَ ٱللَّهِ كُفۡرٗا وَأَحَلُّواْ قَوۡمَهُمۡ دَارَ ٱلۡبَوَارِ\n(Эй инсон), Аллоҳнинг неъматини (яъни, иймонни) куфрга алмаштириб юборган ва қавмларини (яъни, ўзининг ортидан эргашганларни) ҳалокат чоҳига қулатган кимсаларни кўрмадингми'}, {'chapter': 14, 'verse': 29, 'text': 'جَهَنَّمَ يَصۡلَوۡنَهَاۖ وَبِئۡسَ ٱلۡقَرَارُ\nЖаҳаннамга (қулатган кимсаларни кўрмадингми?!) Улар унга киражаклар. Нақадар ёмон жой у'}, {'chapter': 14, 'verse': 30, 'text': 'وَجَعَلُواْ لِلَّهِ أَندَادٗا لِّيُضِلُّواْ عَن سَبِيلِهِۦۗ قُلۡ تَمَتَّعُواْ فَإِنَّ مَصِيرَكُمۡ إِلَى ٱلنَّارِ\nУлар (одамларни) Аллоҳ йўлидан оздириш учун У зотга «тенг»ларни (ўйлаб) топдилар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Фойдаланиб қолинглар! Ҳеч шубҳа йўқки, оқибат борадиган жойингиз дўзахдир»'}, {'chapter': 14, 'verse': 31, 'text': 'قُل لِّعِبَادِيَ ٱلَّذِينَ ءَامَنُواْ يُقِيمُواْ ٱلصَّلَوٰةَ وَيُنفِقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعٞ فِيهِ وَلَا خِلَٰلٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), иймон келтирган бандаларимга айтинг, намозни тўкис адо қилсинлар ҳамда олди-сотди ва ошна-оғайнигарчилик бўлмайдиган Кун (Қиёмат) келиб қолишидан илгари Биз уларга ризқ қилиб берган нарсалардан махфий ва ошкора инфоқ-эҳсон қилсинлар'}, {'chapter': 14, 'verse': 32, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ وَسَخَّرَ لَكُمُ ٱلۡفُلۡكَ لِتَجۡرِيَ فِي ٱلۡبَحۡرِ بِأَمۡرِهِۦۖ وَسَخَّرَ لَكُمُ ٱلۡأَنۡهَٰرَ\nАллоҳ осмонлар ва Ерни яратган ва осмондан сув (ёмғир-қор) ёғдириб, унинг ёрдамида сизларга ризқ бўладиган меваларни чиқарган Зотдир. У Ўз амри билан денгизда сузиб юриши учун кемаларни сизларга бўйинсундирди. Яна дарёларни ҳам сизларга бўйинсундирди'}, {'chapter': 14, 'verse': 33, 'text': 'وَسَخَّرَ لَكُمُ ٱلشَّمۡسَ وَٱلۡقَمَرَ دَآئِبَيۡنِۖ وَسَخَّرَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ\nУ доимо айланиб турувчи қуёш ва ойни ҳам сизлар учун бўйинсундирди (яъни, сизлар яхши яшашингиз учун тартиб-интизомга солиб қўйди). Яна кеча ва кундузни сизлар учун бўйинсундирди'}, {'chapter': 14, 'verse': 34, 'text': 'وَءَاتَىٰكُم مِّن كُلِّ مَا سَأَلۡتُمُوهُۚ وَإِن تَعُدُّواْ نِعۡمَتَ ٱللَّهِ لَا تُحۡصُوهَآۗ إِنَّ ٱلۡإِنسَٰنَ لَظَلُومٞ كَفَّارٞ\nШунингдек, сизларга барча сўраган нарсаларингиздан ато этди. Агар Аллоҳнинг неъматларини санасангизлар, саноғига етолмайсизлар. Ҳақиқатан, инсон зоти ўта золим ва жуда ношукрдир'}, {'chapter': 14, 'verse': 35, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِيمُ رَبِّ ٱجۡعَلۡ هَٰذَا ٱلۡبَلَدَ ءَامِنٗا وَٱجۡنُبۡنِي وَبَنِيَّ أَن نَّعۡبُدَ ٱلۡأَصۡنَامَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эсланг, Иброҳим айтган эди: «Парвардигорим, бу шаҳарни (яъни, Маккани) тинч шаҳар қилгин, мени ва болаларимни бутларга бандалик қилишдан йироқ қилгин'}, {'chapter': 14, 'verse': 36, 'text': 'رَبِّ إِنَّهُنَّ أَضۡلَلۡنَ كَثِيرٗا مِّنَ ٱلنَّاسِۖ فَمَن تَبِعَنِي فَإِنَّهُۥ مِنِّيۖ وَمَنۡ عَصَانِي فَإِنَّكَ غَفُورٞ رَّحِيمٞ\nПарвардигорим, у бутлар кўпдан-кўп одамларни йўлдан оздирдилар. Бас, ким менга эргашса, ана ўша мендандир (яъни, менинг динимдадир). Ким менга исён қилса, яна Ўзинг мағфиратли, меҳрибонсан (яъни, ундай кимсаларни ҳам ҳақ йўлга ҳидоят қилишга қодирсан)'}, {'chapter': 14, 'verse': 37, 'text': 'رَّبَّنَآ إِنِّيٓ أَسۡكَنتُ مِن ذُرِّيَّتِي بِوَادٍ غَيۡرِ ذِي زَرۡعٍ عِندَ بَيۡتِكَ ٱلۡمُحَرَّمِ رَبَّنَا لِيُقِيمُواْ ٱلصَّلَوٰةَ فَٱجۡعَلۡ أَفۡـِٔدَةٗ مِّنَ ٱلنَّاسِ تَهۡوِيٓ إِلَيۡهِمۡ وَٱرۡزُقۡهُم مِّنَ ٱلثَّمَرَٰتِ لَعَلَّهُمۡ يَشۡكُرُونَ\nПарвардигоро, албатта мен зурриётимдан (бир бўлагини — ўғлим Исмоил ва унинг онаси Ҳожарни) Сенинг ҳурматли Байтинг ҳузуридаги экин ўсмайдиган бир водийга жойлаштирдим. Парвардигоро, (улар) намозни тўкис адо қилсинлар деб (шундай қилдим). Бас, Сен Ўзинг одамларнинг дилларини уларга мойил қилиб қўйгил ва уларни (барча) мевалардан баҳраманд этгил. Шояд шукр қилсалар'}, {'chapter': 14, 'verse': 38, 'text': 'رَبَّنَآ إِنَّكَ تَعۡلَمُ مَا نُخۡفِي وَمَا نُعۡلِنُۗ وَمَا يَخۡفَىٰ عَلَى ٱللَّهِ مِن شَيۡءٖ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ\nПарвардигоро, албатта Сен бизлар яширган нарсани ҳам, ошкор қилган нарсани ҳам билурсан, Аллоҳ учун Еру осмондаги бирон нарса махфий эмасдир'}, {'chapter': 14, 'verse': 39, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي وَهَبَ لِي عَلَى ٱلۡكِبَرِ إِسۡمَٰعِيلَ وَإِسۡحَٰقَۚ إِنَّ رَبِّي لَسَمِيعُ ٱلدُّعَآءِ\nМенга кексалик пайтимда Исмоил ва Исҳоқни ҳадя қилган зот — Аллоҳга ҳамду санолар бўлсин. Албатта, Парвардигорим (барча) дуоларни эшитгувчидир'}, {'chapter': 14, 'verse': 40, 'text': 'رَبِّ ٱجۡعَلۡنِي مُقِيمَ ٱلصَّلَوٰةِ وَمِن ذُرِّيَّتِيۚ رَبَّنَا وَتَقَبَّلۡ دُعَآءِ\nПарвардигорим, мени ва зурриётимдан (бўлган болаларимни) намозни тўкис адо этгувчи қилгил. Парвардигоро, дуоимни қабул айла'}, {'chapter': 14, 'verse': 41, 'text': 'رَبَّنَا ٱغۡفِرۡ لِي وَلِوَٰلِدَيَّ وَلِلۡمُؤۡمِنِينَ يَوۡمَ يَقُومُ ٱلۡحِسَابُ\nПарвардигоро, ҳисоб-китоб қилинадиган (Қиёмат) куни мени, ота-онамни ва барча мўминларни мағфират қилгил'}, {'chapter': 14, 'verse': 42, 'text': 'وَلَا تَحۡسَبَنَّ ٱللَّهَ غَٰفِلًا عَمَّا يَعۡمَلُ ٱلظَّـٰلِمُونَۚ إِنَّمَا يُؤَخِّرُهُمۡ لِيَوۡمٖ تَشۡخَصُ فِيهِ ٱلۡأَبۡصَٰرُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз ҳаргиз: «Аллоҳ золим кимсаларнинг қилаётган амалларидан ғофил», деб ўйламанг! Фақат Аллоҳ уларни(нг жазоларини) кўзлар (даҳшатдан) қотиб қоладиган (қўрқинчли Қиёмат) кунига қолдирмоқда, холос'}, {'chapter': 14, 'verse': 43, 'text': 'مُهۡطِعِينَ مُقۡنِعِي رُءُوسِهِمۡ لَا يَرۡتَدُّ إِلَيۡهِمۡ طَرۡفُهُمۡۖ وَأَفۡـِٔدَتُهُمۡ هَوَآءٞ\n(У куни) улар бошларини (осмонга) кўтарган ҳолларида (чорланган томонгга хисоб-китоб учун) чопурлар. Кўзлари ўзларига қайтмайди (яъни, қўрқувдан қотиб қолиб, ўзларининг қандай ҳолда эканликларини ҳам кўрмайдилар). Диллари (даҳшатдан) бўм-бўш бўлиб қолур'}, {'chapter': 14, 'verse': 44, 'text': 'وَأَنذِرِ ٱلنَّاسَ يَوۡمَ يَأۡتِيهِمُ ٱلۡعَذَابُ فَيَقُولُ ٱلَّذِينَ ظَلَمُواْ رَبَّنَآ أَخِّرۡنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖ نُّجِبۡ دَعۡوَتَكَ وَنَتَّبِعِ ٱلرُّسُلَۗ أَوَلَمۡ تَكُونُوٓاْ أَقۡسَمۡتُم مِّن قَبۡلُ مَا لَكُم مِّن زَوَالٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), одамларни огоҳлантириб қўйингки, уларга азоб келар кунда (Қиёматда) золим кимсалар: «Парвардигоро, бизларга озгина муҳлат бергин, (яъни, бизларни дунёга қайтариб, озгина ҳаёт бергин, албатта) Сенинг даъватингни қабул қилурмиз ва пайғамбарларингга эргашурмиз», дейдилар. (Шунда уларга жавоб қилинур): «Илгари (ҳаёт пайтларингизда) ҳеч қачон заволга юз тутмаслигингиз (яъни, ҳеч қачон ўлмаслигингиз ва Охират жазосига дучор бўлмаслигингиз) ҳақида қасам ичмаган эдингизларми'}, {'chapter': 14, 'verse': 45, 'text': 'وَسَكَنتُمۡ فِي مَسَٰكِنِ ٱلَّذِينَ ظَلَمُوٓاْ أَنفُسَهُمۡ وَتَبَيَّنَ لَكُمۡ كَيۡفَ فَعَلۡنَا بِهِمۡ وَضَرَبۡنَا لَكُمُ ٱلۡأَمۡثَالَ\n(Ахир) сизлар ўзларига зулм қилган кимсаларнинг масканларига (улар заволга юз тутганларидан кейин) жойлашган эдингизлар. Уларни қандай (ҳалок) қилганимиз ҳам сизларга аниқ маълум эди. Биз сизлар учун (қанчадан-қанча) мисоллар келтирган эдик (лекин сизлар бу кўрган билганларингиздан ибрат олмадингиз, ҳақ йўлга юрмадингиз)»'}, {'chapter': 14, 'verse': 46, 'text': 'وَقَدۡ مَكَرُواْ مَكۡرَهُمۡ وَعِندَ ٱللَّهِ مَكۡرُهُمۡ وَإِن كَانَ مَكۡرُهُمۡ لِتَزُولَ مِنۡهُ ٱلۡجِبَالُ\nДарҳақиқат, улар (яъни, Макка мушриклари) ўзларининг макр-ҳийлаларини қилдилар. (Лекин) агар уларнинг макрлари сабабли тоғлар емирилиб кетадиган бўлса-да, уларнинг (бу) макр-ҳийлалари Аллоҳ ҳузурида (маълумдир)'}, {'chapter': 14, 'verse': 47, 'text': 'فَلَا تَحۡسَبَنَّ ٱللَّهَ مُخۡلِفَ وَعۡدِهِۦ رُسُلَهُۥٓۚ إِنَّ ٱللَّهَ عَزِيزٞ ذُو ٱنتِقَامٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз ҳаргиз Аллоҳни ўз пайғамбарларига берган («Биз албатта пайғамбарларимизни ғолиб қилурмиз», деган) ваъдасига хилоф қилувчи, деб ўйламанг. Албатта, Аллоҳ интиқом олувчи ғолибдир'}, {'chapter': 14, 'verse': 48, 'text': 'يَوۡمَ تُبَدَّلُ ٱلۡأَرۡضُ غَيۡرَ ٱلۡأَرۡضِ وَٱلسَّمَٰوَٰتُۖ وَبَرَزُواْ لِلَّهِ ٱلۡوَٰحِدِ ٱلۡقَهَّارِ\nАлбатта, Аллоҳ Ер бошқа Ерга, осмонлар (ўзга осмонларга) айланиб қоладиган ҳамда (барча одамлар) ёлғиз ва қудратли Аллоҳга рубарў бўладиган Кунда (Қиёматда интиқом олгувчидир)'}, {'chapter': 14, 'verse': 49, 'text': 'وَتَرَى ٱلۡمُجۡرِمِينَ يَوۡمَئِذٖ مُّقَرَّنِينَ فِي ٱلۡأَصۡفَادِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), у кунда жиноятчи кимсаларни кишанлар билан боғланган ҳолларида кўрурсиз'}, {'chapter': 14, 'verse': 50, 'text': 'سَرَابِيلُهُم مِّن قَطِرَانٖ وَتَغۡشَىٰ وُجُوهَهُمُ ٱلنَّارُ\nУларнинг кийимлари қора мойдан бўлиб, юзларини олов ўраб олур'}, {'chapter': 14, 'verse': 51, 'text': 'لِيَجۡزِيَ ٱللَّهُ كُلَّ نَفۡسٖ مَّا كَسَبَتۡۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nАллоҳ ҳар бир жонга қилган ишига яраша жазо бериш учун (мана шундай қайта тирилтирур). Албатта, Аллоҳ жуда тез ҳисоб-китоб қилгувчидир'}, {'chapter': 14, 'verse': 52, 'text': 'هَٰذَا بَلَٰغٞ لِّلنَّاسِ وَلِيُنذَرُواْ بِهِۦ وَلِيَعۡلَمُوٓاْ أَنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَلِيَذَّكَّرَ أُوْلُواْ ٱلۡأَلۡبَٰبِ\n(Қуръон) одамлар огоҳлантирилишлари ва (Аллоҳ) ягона илоҳ эканини билишлари ҳамда ақл эгалари эслатма-ибрат олишлари учун (Аллоҳ тарафидан нозил қилинган) бир баёнот — хабардир'}, {'chapter': 15, 'verse': 1, 'text': 'الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ وَقُرۡءَانٖ مُّبِينٖ\nАлиф, Лом, Ро. Ушбу (оятлар) Китобнинг — очиқ Қуръоннинг оятларидир'}, {'chapter': 15, 'verse': 2, 'text': 'رُّبَمَا يَوَدُّ ٱلَّذِينَ كَفَرُواْ لَوۡ كَانُواْ مُسۡلِمِينَ\nҲали (Қиёмат Куни) кофир бўлган кимсалар мусулмон бўлишни истаб қолурлар'}, {'chapter': 15, 'verse': 3, 'text': 'ذَرۡهُمۡ يَأۡكُلُواْ وَيَتَمَتَّعُواْ وَيُلۡهِهِمُ ٱلۡأَمَلُۖ فَسَوۡفَ يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларни қўяверинг, еб-ичиб, фойдаланиб қолсинлар, орзу-ҳавасларига машғул бўлаверсинлар. Бас, яқинда (бу қилмишларининг оқибати нима бўлишини) билиб олурлар'}, {'chapter': 15, 'verse': 4, 'text': 'وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا وَلَهَا كِتَابٞ مَّعۡلُومٞ\nБиз бирон шаҳарни (яъни, унинг аҳолисини) ҳалок қилган эмасмиз, магар унинг учун аниқ муддат бўлгандир (яъни, ўша муддати — ажали битгандан сўнггина ҳалок қилганмиз)'}, {'chapter': 15, 'verse': 5, 'text': 'مَّا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ\nБирон авлод ўз ажалидан илгари кета олмайди. Шунингдек, (ундан) ортда ҳам қола олмайди'}, {'chapter': 15, 'verse': 6, 'text': 'وَقَالُواْ يَـٰٓأَيُّهَا ٱلَّذِي نُزِّلَ عَلَيۡهِ ٱلذِّكۡرُ إِنَّكَ لَمَجۡنُونٞ\nУлар (яъни, Макка кофирлари Муҳаммад алайҳис-салоту вас-саломни масхара қилишиб): «Эй (ўзига) эслатма — Куръон нозил килинган (деб даъво қилаётган) киши, дарҳақиқат, сен мажнунсан'}, {'chapter': 15, 'verse': 7, 'text': 'لَّوۡمَا تَأۡتِينَا بِٱلۡمَلَـٰٓئِكَةِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nАгар («Мен Аллоҳнинг пайғамбариман», деган даъвоингда) ростгўйлардан бўлсанг, бизга (сўзларингни тасдиқлайдиган) фаришталарни келтиргин-чи», дедилар'}, {'chapter': 15, 'verse': 8, 'text': 'مَا نُنَزِّلُ ٱلۡمَلَـٰٓئِكَةَ إِلَّا بِٱلۡحَقِّ وَمَا كَانُوٓاْ إِذٗا مُّنظَرِينَ\nБиз эса фаришталарни фақатгина (кофирлар зиммасига) ҳақ (бўлган азоб) билан туширурмиз ва бу ҳолда уларга (кофирларга) қараб турилмас (яъни, улар дарҳол ҳалоқ қилинурлар)'}, {'chapter': 15, 'verse': 9, 'text': 'إِنَّا نَحۡنُ نَزَّلۡنَا ٱلذِّكۡرَ وَإِنَّا لَهُۥ لَحَٰفِظُونَ\nАлбатта, бу эслатмани (яъни, Қуръонни) Биз Ўзимиз нозил қилдик ва шубҳасиз, Ўзимиз уни сақлагувчимиз'}, {'chapter': 15, 'verse': 10, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي شِيَعِ ٱلۡأَوَّلِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), аниқки, Биз сиздан илгари ўтган, аввалги (турли-туман) тоифаларга ҳам (пайғамбарлар) юборганмиз'}, {'chapter': 15, 'verse': 11, 'text': 'وَمَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУлар ҳам ўзларига келган ҳар бир пайғамбарни масхара қилиб кулган эдилар'}, {'chapter': 15, 'verse': 12, 'text': 'كَذَٰلِكَ نَسۡلُكُهُۥ فِي قُلُوبِ ٱلۡمُجۡرِمِينَ\nШунингдек, уни (яъни, пайғамбарлар устидан кулиш иллатини) барча жиноятчи — осийларнинг дилларига солиб қўюрмиз'}, {'chapter': 15, 'verse': 13, 'text': 'لَا يُؤۡمِنُونَ بِهِۦ وَقَدۡ خَلَتۡ سُنَّةُ ٱلۡأَوَّلِينَ\nУлар унга (Қуръонга) иймон келтирмайдилар. Ҳолбуки, аввалгиларнинг суннатлари — ибратлари ўтгандир (яъни, аввалги кофирлик билан ўтган кимсаларнинг оқибатлари нима бўлгани маълумдир)'}, {'chapter': 15, 'verse': 14, 'text': 'وَلَوۡ فَتَحۡنَا عَلَيۡهِم بَابٗا مِّنَ ٱلسَّمَآءِ فَظَلُّواْ فِيهِ يَعۡرُجُونَ\nАгар Биз уларга осмондан бир дарвоза очиб қўйсаг-у, ундан (осмонга) кўтарила бошласалар ҳам'}, {'chapter': 15, 'verse': 15, 'text': 'لَقَالُوٓاْ إِنَّمَا سُكِّرَتۡ أَبۡصَٰرُنَا بَلۡ نَحۡنُ قَوۡمٞ مَّسۡحُورُونَ\nалбатта улар: «Шак-шубҳасиз, бизларнинг кўзларимиз боғланиб қолди, балки бизлар сеҳрланиб қолган кишилардирмиз», деган бўлур эдилар'}, {'chapter': 15, 'verse': 16, 'text': 'وَلَقَدۡ جَعَلۡنَا فِي ٱلسَّمَآءِ بُرُوجٗا وَزَيَّنَّـٰهَا لِلنَّـٰظِرِينَ\nМаълумки, Биз осмонда буржлар яратдик ва кузатувчилар учун уни (юлдузлар билан) безаб қўйдик'}, {'chapter': 15, 'verse': 17, 'text': 'وَحَفِظۡنَٰهَا مِن كُلِّ شَيۡطَٰنٖ رَّجِيمٍ\nВа уни (осмонни) барча малъун-шайтон (жинлар)дан муҳофаза қилдик'}, {'chapter': 15, 'verse': 18, 'text': 'إِلَّا مَنِ ٱسۡتَرَقَ ٱلسَّمۡعَ فَأَتۡبَعَهُۥ شِهَابٞ مُّبِينٞ\nМагар (у жинлардан) биронтаси (осмондаги малоикаларнинг бирон сўзини) ўғринча эшитиб олса, бас, уни очиқ (учар) юлдуз қувиб етар (яъни, ҳалок этар)'}, {'chapter': 15, 'verse': 19, 'text': 'وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ شَيۡءٖ مَّوۡزُونٖ\nЕрни эса ёйиқ — кенг қилдик ва унда тоғларни ўрнатиб қўйдик ҳамда унда (ҳар ернинг ўзига) мосланган турли-туман нарсаларни (ўсимлик, меваларни) ундириб-ўстирдик'}, {'chapter': 15, 'verse': 20, 'text': 'وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَ وَمَن لَّسۡتُمۡ لَهُۥ بِرَٰزِقِينَ\nВа унда (яъни, Ерда) сизлар учун ҳам сизлар ризқлантира олмайдиган (балки фақат Биз ризқ берадиган сизларнинг қўл остингиздаги) кишилар учун ҳам тирикчилик — ризқу насибаларни яратиб қўйдик'}, {'chapter': 15, 'verse': 21, 'text': 'وَإِن مِّن شَيۡءٍ إِلَّا عِندَنَا خَزَآئِنُهُۥ وَمَا نُنَزِّلُهُۥٓ إِلَّا بِقَدَرٖ مَّعۡلُومٖ\n(Сизларнинг ризқу рўзингиз бўлмиш) ҳар бир нарсанинг хазиналари — манбалари Бизнинг даргоҳимиздадир. Биз у нарсаларни аниқ ўлчов билан (сизларга) туширурмиз'}, {'chapter': 15, 'verse': 22, 'text': 'وَأَرۡسَلۡنَا ٱلرِّيَٰحَ لَوَٰقِحَ فَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ فَأَسۡقَيۡنَٰكُمُوهُ وَمَآ أَنتُمۡ لَهُۥ بِخَٰزِنِينَ\nБиз шамолларни (булутларга) ҳомиладор бўлган ҳолларида юбордик, осмондан сув (ёмғир) ёғдириб, сизларни у билан суғордик. Сизлар уни тўплаб олгувчи эмассиз (балки Биз дарё ва кўллар яратиб, у сувни сизлар учун тўплаб берурмиз)'}, {'chapter': 15, 'verse': 23, 'text': 'وَإِنَّا لَنَحۡنُ نُحۡيِۦ وَنُمِيتُ وَنَحۡنُ ٱلۡوَٰرِثُونَ\nАлбатта, Биз ўзимиз (барча жонзотга) ҳаёт берурмиз ва ўлим берурмиз. Биз ворисдирмиз (яъни, барча ўтгандан кейин ёлғиз Ўзимиз қолурмиз)'}, {'chapter': 15, 'verse': 24, 'text': 'وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَقۡدِمِينَ مِنكُمۡ وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَـٔۡخِرِينَ\nАниқки, Биз сизлардан илгари ўтган (барча) зотларни билдик. Аниқки, Биз кейин келгувчи зотларни ҳам билдик'}, {'chapter': 15, 'verse': 25, 'text': 'وَإِنَّ رَبَّكَ هُوَ يَحۡشُرُهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Парвардигорингизнинг Ўзи (Қиёмат Куни) уларни (яъни, аввалу охир ўтган инсонларни) тўплар. Албатта, У ҳикмат ва билим Соҳибидир'}, {'chapter': 15, 'verse': 26, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ مِن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ\nМаълумки, Биз инсонни (Одамнинг асли) қора ботқоқдан (бўлиб одам сурати берилгач), қуритилган лойдан яратганмиз'}, {'chapter': 15, 'verse': 27, 'text': 'وَٱلۡجَآنَّ خَلَقۡنَٰهُ مِن قَبۡلُ مِن نَّارِ ٱلسَّمُومِ\nЖинни (яъни, Иблисни) эса (Одамдан) илгари оташ-оловдан яратган эдик'}, {'chapter': 15, 'verse': 28, 'text': 'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эсланг, Парвардигорингиз фаришталарга: «Албатта, Мен (асли) қора ботқоқдан бўлиб (одам сурати берилгач) қуритилган лойдан инсон Яратгувчиман'}, {'chapter': 15, 'verse': 29, 'text': 'فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ\nБас, қачон уни тиклаб, ичига Ўз (даргоҳимдаги) жондан киритганимда, сизлар унга сажда қилган ҳолингизда йиқилингиз!», деганида'}, {'chapter': 15, 'verse': 30, 'text': 'فَسَجَدَ ٱلۡمَلَـٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ\nБарча-барча фаришталар унга (яъни, Одамга) сажда килдилар'}, {'chapter': 15, 'verse': 31, 'text': 'إِلَّآ إِبۡلِيسَ أَبَىٰٓ أَن يَكُونَ مَعَ ٱلسَّـٰجِدِينَ\nМагар иблис сажда қилувчилар билан бирга бўлишдан бош тортди'}, {'chapter': 15, 'verse': 32, 'text': 'قَالَ يَـٰٓإِبۡلِيسُ مَا لَكَ أَلَّا تَكُونَ مَعَ ٱلسَّـٰجِدِينَ\n(Шунда Аллоҳ) деди: «Эй Иблис, нечун сен сажда қилгувчилар билан бирга эмассан?»'}, {'chapter': 15, 'verse': 33, 'text': 'قَالَ لَمۡ أَكُن لِّأَسۡجُدَ لِبَشَرٍ خَلَقۡتَهُۥ مِن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ\nУ айтди: «Мен (асли) қора ботқоқдан бўлиб, (одам сурати берилгач) қуритилган лойдан Сен яратган Одамга сажда қилгувчи эмасман»'}, {'chapter': 15, 'verse': 34, 'text': 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ\n(Аллоҳ) деди: «Бас, ундан (яъни, жаннатдан) чиқ! Энди сен, шак-шубҳасиз, (Менинг даргоҳимдан) қувилган — малъунсан'}, {'chapter': 15, 'verse': 35, 'text': 'وَإِنَّ عَلَيۡكَ ٱللَّعۡنَةَ إِلَىٰ يَوۡمِ ٱلدِّينِ\nВа албатта, то жазо (Қиёмат) кунигача сенга (барчанинг) лаънати бўлур»'}, {'chapter': 15, 'verse': 36, 'text': 'قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\n(Иблис илтижо қилиб) деди: «Парвардигорим, у ҳолда менга улар қайта тириладиган кунгача (ўлмасдан яшаш учун) муҳлат бергин»'}, {'chapter': 15, 'verse': 37, 'text': 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ\n(Аллоҳ) деди: «Бас, сен муҳлат берилганлардансан'}, {'chapter': 15, 'verse': 38, 'text': 'إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ\nМаълум вақтда (етиб келадиган) Кунгача (Қиёматгача) (муҳлат берилганлардансан)»'}, {'chapter': 15, 'verse': 39, 'text': 'قَالَ رَبِّ بِمَآ أَغۡوَيۡتَنِي لَأُزَيِّنَنَّ لَهُمۡ فِي ٱلۡأَرۡضِ وَلَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ\n(Иблис) айтди: «Парвардигорим, қасамки, энди мени йўлдан оздирганинг сабабли албатта, уларга (Одам болаларига) Ердаги (барча гуноҳ ишларни) чиройли кўрсатиб қўюрман ва албатта, уларнинг ҳаммаларини йўлдан оздирурман'}, {'chapter': 15, 'verse': 40, 'text': 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ\nМагар уларнинг орасидаги покиза бандаларинггина (Ҳақ Йўлдан озмай қолурлар)»'}, {'chapter': 15, 'verse': 41, 'text': 'قَالَ هَٰذَا صِرَٰطٌ عَلَيَّ مُسۡتَقِيمٌ\n(Аллоҳ) деди: «Менинг зиммамдаги Тўғри Йўл будир'}, {'chapter': 15, 'verse': 42, 'text': 'إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٌ إِلَّا مَنِ ٱتَّبَعَكَ مِنَ ٱلۡغَاوِينَ\nАниқки, Менинг бандаларим устида сен учун ҳеч қандай салтанат — ҳукмронлик йўқдир, магар сенга эргашган гумроҳ кимсаларнигина (бу Тўғри Йўлдан оздира олурсан)»'}, {'chapter': 15, 'verse': 43, 'text': 'وَإِنَّ جَهَنَّمَ لَمَوۡعِدُهُمۡ أَجۡمَعِينَ\nУ кимсаларнинг барчалари учун ваъда қилинган жой, шак-шубҳасиз, жаҳаннамдир'}, {'chapter': 15, 'verse': 44, 'text': 'لَهَا سَبۡعَةُ أَبۡوَٰبٖ لِّكُلِّ بَابٖ مِّنۡهُمۡ جُزۡءٞ مَّقۡسُومٌ\nУнинг етти дарвозаси бўлиб, ҳар бир дарвозадан уларнинг бир тўдаси кирур'}, {'chapter': 15, 'verse': 45, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَعُيُونٍ\nТақводор — парҳезкор зотлар эса, албатта, боғлар ва чашмалар устидадирлар'}, {'chapter': 15, 'verse': 46, 'text': 'ٱدۡخُلُوهَا بِسَلَٰمٍ ءَامِنِينَ\n(У зотларга: «Жаннатлар)га тинчлик билан соғ-омон киринглар», (дейилур)'}, {'chapter': 15, 'verse': 47, 'text': 'وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلٍّ إِخۡوَٰنًا عَلَىٰ سُرُرٖ مُّتَقَٰبِلِينَ\nБиз (жаннатлардаги) сўриларда дўст-биродар бўлиб, бир-бирларига рўбарў ўтирган зотларнинг дилларидаги ҳар қандай гина-кудуратларни чиқариб ташладик'}, {'chapter': 15, 'verse': 48, 'text': 'لَا يَمَسُّهُمۡ فِيهَا نَصَبٞ وَمَا هُم مِّنۡهَا بِمُخۡرَجِينَ\nУ жойда уларга бирон чарчоқ етмас ва улар у жойдан чиқарилгувчи ҳам эмаслар'}, {'chapter': 15, 'verse': 49, 'text': '۞نَبِّئۡ عِبَادِيٓ أَنِّيٓ أَنَا ٱلۡغَفُورُ ٱلرَّحِيمُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), бандаларимга ёлғиз Менинг Ўзимгина мағфиратли, меҳрибон эканлигимни'}, {'chapter': 15, 'verse': 50, 'text': 'وَأَنَّ عَذَابِي هُوَ ٱلۡعَذَابُ ٱلۡأَلِيمُ\nВа Менинг азобим энг аламли азоб эканлигини хабар қилинг'}, {'chapter': 15, 'verse': 51, 'text': 'وَنَبِّئۡهُمۡ عَن ضَيۡفِ إِبۡرَٰهِيمَ\nЯна уларга Иброҳимнинг меҳмонлари ҳақида хабар беринг'}, {'chapter': 15, 'verse': 52, 'text': 'إِذۡ دَخَلُواْ عَلَيۡهِ فَقَالُواْ سَلَٰمٗا قَالَ إِنَّا مِنكُمۡ وَجِلُونَ\nЎшанда улар (Иброҳимнинг) ҳузурига кириб: «Салом», деганларида, (Иброҳим алик олиб, уларга бир семиз бузоқни пишириб келтирди. Лекин меҳмонлар таомга қўл чўзишмагач), у: «Биз сизлардан қўрқмоқдамиз (Кимсизлар ўзи)?», деди'}, {'chapter': 15, 'verse': 53, 'text': 'قَالُواْ لَا تَوۡجَلۡ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ عَلِيمٖ\nУлар дедилар: «Қўрқмагин (биз Аллоҳ юборган элчи — фаришталармиз). Биз сенга бир билимдон ўғил (яъни, Исҳоқ) хушхабарини етказурмиз»'}, {'chapter': 15, 'verse': 54, 'text': 'قَالَ أَبَشَّرۡتُمُونِي عَلَىٰٓ أَن مَّسَّنِيَ ٱلۡكِبَرُ فَبِمَ تُبَشِّرُونَ\nУ деди: «Менга кексалик етган пайтида (фарзанд кўришим ҳақида) хушхабар келтирдингизми? Энди менга (бу ёшимда) ниманинг ҳам хушхабарини етказар эдингизлар?»'}, {'chapter': 15, 'verse': 55, 'text': 'قَالُواْ بَشَّرۡنَٰكَ بِٱلۡحَقِّ فَلَا تَكُن مِّنَ ٱلۡقَٰنِطِينَ\nУлар айтдилар: «Биз сенга ростдан ҳам (фарзанд кўришинг) хушхабарини келтирдик. Бас, сен ноумид кимсалардан бўлмагин!»'}, {'chapter': 15, 'verse': 56, 'text': 'قَالَ وَمَن يَقۡنَطُ مِن رَّحۡمَةِ رَبِّهِۦٓ إِلَّا ٱلضَّآلُّونَ\nУ деди: «Парвардигорининг фазлу раҳматидан фақат гумроҳ кимсаларгина ноумид бўлурлар»'}, {'chapter': 15, 'verse': 57, 'text': 'قَالَ فَمَا خَطۡبُكُمۡ أَيُّهَا ٱلۡمُرۡسَلُونَ\n(Сўнгра, уларга қараб яна) деди: «Эй элчилар, нима юмуш билан келдингиз?»'}, {'chapter': 15, 'verse': 58, 'text': 'قَالُوٓاْ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمٖ مُّجۡرِمِينَ\nУлар дедилар: «Бизлар бир жиноятчи қавмга (яъни, Лут қавмига уларни ҳалок қилиш учун юборилганмиз)'}, {'chapter': 15, 'verse': 59, 'text': 'إِلَّآ ءَالَ لُوطٍ إِنَّا لَمُنَجُّوهُمۡ أَجۡمَعِينَ\nЛекин биз Лутнинг оиласини тўла-тўкис қутқаргувчидирмиз'}, {'chapter': 15, 'verse': 60, 'text': 'إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَآ إِنَّهَا لَمِنَ ٱلۡغَٰبِرِينَ\nФақат унинг хотинига (нажот йўқдир)». (Чунки) Биз (яъни, Аллоҳ) унинг (азоб-уқубатда) қолгувчилардан бўлишини тақдир қилиб қўйганмиз'}, {'chapter': 15, 'verse': 61, 'text': 'فَلَمَّا جَآءَ ءَالَ لُوطٍ ٱلۡمُرۡسَلُونَ\nЭнди қачонки Лут оиласига элчиларимиз келгач'}, {'chapter': 15, 'verse': 62, 'text': 'قَالَ إِنَّكُمۡ قَوۡمٞ مُّنكَرُونَ\nУ (Лут) деди: «Аниқки, сизлар нотаниш қавмсиз»'}, {'chapter': 15, 'verse': 63, 'text': 'قَالُواْ بَلۡ جِئۡنَٰكَ بِمَا كَانُواْ فِيهِ يَمۡتَرُونَ\nУлар дедилар: «Йўқ, (биз Аллоҳнинг элчи — фаришталаримиз). Биз сенга улар (яъни, қавминг) шак-шубҳа қилаётган нарсани (яъни, Аллоҳнинг азобини) келтирдик'}, {'chapter': 15, 'verse': 64, 'text': 'وَأَتَيۡنَٰكَ بِٱلۡحَقِّ وَإِنَّا لَصَٰدِقُونَ\nБиз сенга ҳақ (азоб хабари)ни келтирдик. Биз, шак-шубҳасиз, ростгўйдирмиз'}, {'chapter': 15, 'verse': 65, 'text': 'فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ ٱلَّيۡلِ وَٱتَّبِعۡ أَدۡبَٰرَهُمۡ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٞ وَٱمۡضُواْ حَيۡثُ تُؤۡمَرُونَ\nЭнди сен кечанинг бир қисми (ўтгач), аҳли оиланг билан йўлга туш ва ўзинг уларнинг ортида кетгин. Сизлардан бирон киши атрофга алангламасин ва сизларга буюрилган тарафга қараб кетаверинглар»'}, {'chapter': 15, 'verse': 66, 'text': 'وَقَضَيۡنَآ إِلَيۡهِ ذَٰلِكَ ٱلۡأَمۡرَ أَنَّ دَابِرَ هَـٰٓؤُلَآءِ مَقۡطُوعٞ مُّصۡبِحِينَ\nБиз унга (яъни, Лутга) тонг пайтида ана у (кофир қавм)нинг думи қирқилиши — ҳалок бўлиши ҳақидаги мана шу фармонни ваҳий қилдик'}, {'chapter': 15, 'verse': 67, 'text': 'وَجَآءَ أَهۡلُ ٱلۡمَدِينَةِ يَسۡتَبۡشِرُونَ\n(Лутнинг уйига чиройли йигитлар келгани ҳақидаги хабарни эшитиб) шаҳар аҳолиси шод-хуррам бўлган ҳолларида келдилар. (Чунки улар баччабозлик иллатига мубтало бўлган эдилар)'}, {'chapter': 15, 'verse': 68, 'text': 'قَالَ إِنَّ هَـٰٓؤُلَآءِ ضَيۡفِي فَلَا تَفۡضَحُونِ\n(Шунда Лут) деди: «Ахир бу (йигитлар) менинг меҳмонларим-ку. Мени шарманда қилманглар'}, {'chapter': 15, 'verse': 69, 'text': 'وَٱتَّقُواْ ٱللَّهَ وَلَا تُخۡزُونِ\nАллоҳдан қўрқинглар! Мени расво қилманглар!»'}, {'chapter': 15, 'verse': 70, 'text': 'قَالُوٓاْ أَوَلَمۡ نَنۡهَكَ عَنِ ٱلۡعَٰلَمِينَ\nУлар айтдилар: «Биз сени барча олам (одамларини ҳимоя қилиш)дан қайтармаганмидик?!»'}, {'chapter': 15, 'verse': 71, 'text': 'قَالَ هَـٰٓؤُلَآءِ بَنَاتِيٓ إِن كُنتُمۡ فَٰعِلِينَ\nУ (Лут) деди: «Агар (шу ишни) қилгувчи бўлсангизлар, ана (қишлоғимиздаги) қизларим (туришибди, ўша қизларни никоҳларингизга олаверинглар)»'}, {'chapter': 15, 'verse': 72, 'text': 'لَعَمۡرُكَ إِنَّهُمۡ لَفِي سَكۡرَتِهِمۡ يَعۡمَهُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ҳаётингизга қасамки, улар ўз мастликларида тентираб юрган ҳолларида'}, {'chapter': 15, 'verse': 73, 'text': 'فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ مُشۡرِقِينَ\n(тўсатдан) тонг пайтида уларни даҳшатли қичқириқ тутди'}, {'chapter': 15, 'verse': 74, 'text': 'فَجَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهِمۡ حِجَارَةٗ مِّن سِجِّيلٍ\nБиз у шаҳарни остин-устун қилиб юбордик ва уларнинг устига сополдан бўлган тош ёғдирдик'}, {'chapter': 15, 'verse': 75, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡمُتَوَسِّمِينَ\nАлбатта, бу (ҳодиса)да фаросатли кишилар учун оят-ибратлар бордир'}, {'chapter': 15, 'verse': 76, 'text': 'وَإِنَّهَا لَبِسَبِيلٖ مُّقِيمٍ\nДарвоқеъ, у (шаҳар) доимий йўл устидадир (яъни, унинг нима сабабдан Аллоҳнинг ғазабига дучор бўлиб вайрон қилингани ҳақида тафаккур қилмайдиларми)'}, {'chapter': 15, 'verse': 77, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ\nАлбатта, бунда мўминлар учун оят-ибрат бордир'}, {'chapter': 15, 'verse': 78, 'text': 'وَإِن كَانَ أَصۡحَٰبُ ٱلۡأَيۡكَةِ لَظَٰلِمِينَ\nШубҳасиз, дарахтзор эгалари (яъни, Шуъайб пайғамбарнинг қавми золим (кофир) бўлганлари аниқдир'}, {'chapter': 15, 'verse': 79, 'text': 'فَٱنتَقَمۡنَا مِنۡهُمۡ وَإِنَّهُمَا لَبِإِمَامٖ مُّبِينٖ\nБас, Биз улардан интиқом олдик. Дарвоқеъ, уларнинг (яъни, Лут қавмининг шаҳри ва Шуъайб қавмининг шаҳри) иккиси ҳам очиқ йўл устидадир. (Яъни, уларни кўриб ибрат олмайсизларми)'}, {'chapter': 15, 'verse': 80, 'text': 'وَلَقَدۡ كَذَّبَ أَصۡحَٰبُ ٱلۡحِجۡرِ ٱلۡمُرۡسَلِينَ\nАниқки, Ҳижр (водий) эгалари (Самуд оиласи) ҳам пайғамбарларни (яъни, Солиҳ алайҳиссаломни) ёлғончи қилгандирлар'}, {'chapter': 15, 'verse': 81, 'text': 'وَءَاتَيۡنَٰهُمۡ ءَايَٰتِنَا فَكَانُواْ عَنۡهَا مُعۡرِضِينَ\nБиз уларга Ўз оят-мўъжизаларимизни ато этган эдик, улар бу оятлардан юз ўгирувчи бўлдилар'}, {'chapter': 15, 'verse': 82, 'text': 'وَكَانُواْ يَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُيُوتًا ءَامِنِينَ\n(Шундан сўнг) улар тинч-омонликда (гўё ҳеч қачон ўлмайдигандек) тоғларидан уйлар йўниб юрганларида'}, {'chapter': 15, 'verse': 83, 'text': 'فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ مُصۡبِحِينَ\nуларни тонг чоғида даҳшатли қичқириқ тутди'}, {'chapter': 15, 'verse': 84, 'text': 'فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ\nСўнгра уларга қилган нарсалари (яъни, тош қалъалари) бирон фойда бермади'}, {'chapter': 15, 'verse': 85, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّۗ وَإِنَّ ٱلسَّاعَةَ لَأٓتِيَةٞۖ فَٱصۡفَحِ ٱلصَّفۡحَ ٱلۡجَمِيلَ\nБиз осмонлар ва Ерни ҳамда уларнинг ўртасидаги нарсаларни фақат Ҳақ (қонун-қоида) билан яратдик. Соат — Қиёмат ҳам, шак-шубҳасиз, келгувчидир. Бас (шундай экан, эй Муҳаммад алайҳис-салоту вас-салом, сиз жоҳил — кимсаларнинг етказаётган озор-азиятларига сабр-тоқат қилинг ва улардан) чиройли юз ўгириш билан юз ўгиринг'}, {'chapter': 15, 'verse': 86, 'text': 'إِنَّ رَبَّكَ هُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِيمُ\nАлбатта, Парвардигорингизнинг Ўзигина яратгувчи, билгувчидир'}, {'chapter': 15, 'verse': 87, 'text': 'وَلَقَدۡ ءَاتَيۡنَٰكَ سَبۡعٗا مِّنَ ٱلۡمَثَانِي وَٱلۡقُرۡءَانَ ٱلۡعَظِيمَ\nДарҳақиқат, Биз сизга етти такрорланувчини (яъни, етти оятдан иборат бўлган, ҳар бир ракаат намозда такрорланадиган «Фотиҳа» сурасини) ва Улуғ Қуръонни ато этдик'}, {'chapter': 15, 'verse': 88, 'text': 'لَا تَمُدَّنَّ عَيۡنَيۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّنۡهُمۡ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَٱخۡفِضۡ جَنَاحَكَ لِلۡمُؤۡمِنِينَ\nСиз кўзларингизни Биз у (кофирлардан айрим) тоифаларни баҳраманд қилган нарсаларга (яъни, уларнинг молу давлат, шаъну шавкатларига) тикманг! Уларнинг (иймонсизликлари) устида ғамгин ҳам бўлманг! Мўминлар учун қанотингизни паст тутинг (яъни, мудом камтар-тавозуъли бўлинг)'}, {'chapter': 15, 'verse': 89, 'text': 'وَقُلۡ إِنِّيٓ أَنَا ٱلنَّذِيرُ ٱلۡمُبِينُ\nВа (иймонсиз кимсаларга айтинг: «Албатта мен (сизларга Аллоҳнинг азоби тушишидан) очиқ-ошкор огоҳлантиргувчиман»'}, {'chapter': 15, 'verse': 90, 'text': 'كَمَآ أَنزَلۡنَا عَلَى ٱلۡمُقۡتَسِمِينَ\nХудди тақсимловчиларга (яъни, Қуръоннинг айрим оятлари ўзларининг истак-хоҳишларига тўғри келиб қолса, унга иймон келтириб, айрим оятларини эса ҳавои хоҳишларига тўғри келмагани учун инкор этадиган яҳудий ва насронийларга илгари Таврот ва Инжил китобларини) нозил қилганимиз каби (сизга ҳам ушбу Қуръонни нозил қилдик)'}, {'chapter': 15, 'verse': 91, 'text': 'ٱلَّذِينَ جَعَلُواْ ٱلۡقُرۡءَانَ عِضِينَ\nҚуръонни бўлак-бўлак қилиб олганларга (китобларни нозил қилганимиз каби сизга ҳам ушбу Қуръонни нозил қилдик)'}, {'chapter': 15, 'verse': 92, 'text': 'فَوَرَبِّكَ لَنَسۡـَٔلَنَّهُمۡ أَجۡمَعِينَ\nПарвардигорингиз номига қасамки, албатта уларнинг барчасидан сўраймиз'}, {'chapter': 15, 'verse': 93, 'text': 'عَمَّا كَانُواْ يَعۡمَلُونَ\nҚилиб ўтган иш — амаллари ҳақида (сўраймиз)'}, {'chapter': 15, 'verse': 94, 'text': 'فَٱصۡدَعۡ بِمَا تُؤۡمَرُ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ\nБас, сиз ўзингизга буюрилган ишни (яъни, Ҳак динга даъват қилишни) юзага чиқаринг ва мушриклардан юз ўгиринг'}, {'chapter': 15, 'verse': 95, 'text': 'إِنَّا كَفَيۡنَٰكَ ٱلۡمُسۡتَهۡزِءِينَ\nАлбатта, Биз Ўзимиз сизни масхара қилгувчиларни (яъни, уларни ҳалок этиш учун) кифоя қилурмиз'}, {'chapter': 15, 'verse': 96, 'text': 'ٱلَّذِينَ يَجۡعَلُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۚ فَسَوۡفَ يَعۡلَمُونَ\nУлар Аллоҳга яна бошқа «илоҳ»ларни (шерик) қилурлар. Бас, яқинда (Қиёмат Кунида бу қилмишларининг оқибатини) билиб олурлар'}, {'chapter': 15, 'verse': 97, 'text': 'وَلَقَدۡ نَعۡلَمُ أَنَّكَ يَضِيقُ صَدۡرُكَ بِمَا يَقُولُونَ\nШак-шубҳасиз, Биз улар (сизни масхара қилиб) айтаётган сўзлардан дилингиз сиқилишини билурмиз'}, {'chapter': 15, 'verse': 98, 'text': 'فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَكُن مِّنَ ٱلسَّـٰجِدِينَ\nБас, сиз Парвардигорингизга ҳамд айтиш билан (У зотни «шерик»лардан) покланг ва сажда қилгувчилардан бўлинг (шунда Аллоҳ дилингиздаги ғам-аламни кетказур)'}, {'chapter': 15, 'verse': 99, 'text': 'وَٱعۡبُدۡ رَبَّكَ حَتَّىٰ يَأۡتِيَكَ ٱلۡيَقِينُ\n(Шунингдек), то сизга аниқ нарса (яъни, ўлим соати) келгунича Парвардигорингизга ибодат қилинг'}, {'chapter': 16, 'verse': 1, 'text': 'أَتَىٰٓ أَمۡرُ ٱللَّهِ فَلَا تَسۡتَعۡجِلُوهُۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nАллоҳнинг фармони (яъни, Қиёмат) келиб қолди. Бас, сизлар (эй мушриклар), уни шоштирмай қўя қолинглар. (Аллоҳ) уларнинг ширкларидан пок ва юксакдир'}, {'chapter': 16, 'verse': 2, 'text': 'يُنَزِّلُ ٱلۡمَلَـٰٓئِكَةَ بِٱلرُّوحِ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ أَنۡ أَنذِرُوٓاْ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱتَّقُونِ\n(Аллоҳ) Ўз амри-иродаси билан Ўзи хоҳлаган бандаларига фаришталарни (шундай) ваҳий билан туширур: («Эй пайғамбарларим, инсонларни) огоҳлантирингларки, ҳеч қандай илоҳ йўқ, фақат Мен Ўзимгина бордирман. Бас, (барчаларингиз) Мендангина қўрқингиз!»'}, {'chapter': 16, 'verse': 3, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ تَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУ осмонлар ва Ерни ҳақ (қонун) билан яратди. У (мушрикларнинг) ширкларидан юксакдир'}, {'chapter': 16, 'verse': 4, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ مِن نُّطۡفَةٖ فَإِذَا هُوَ خَصِيمٞ مُّبِينٞ\nУ инсонни нутфадан — бир томчи сувдан яратган эди. Баногоҳ (инсон) очиқдан-очиқ рақибга айланиб олди'}, {'chapter': 16, 'verse': 5, 'text': 'وَٱلۡأَنۡعَٰمَ خَلَقَهَاۖ لَكُمۡ فِيهَا دِفۡءٞ وَمَنَٰفِعُ وَمِنۡهَا تَأۡكُلُونَ\nУ зот яна сизлар учун иссиқ кийим ва (турли) манфаатлар бўлсин, деб чорва молларини ҳам яратдики, сизларнинг таом-озуқаларингиз улардан бўлур'}, {'chapter': 16, 'verse': 6, 'text': 'وَلَكُمۡ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسۡرَحُونَ\nЯна сизлар учун улар кечқурун ўтлоқдан қайтаётган ва тонгда ўтлоққа кетаётган пайтида бир чирой — файз бордир'}, {'chapter': 16, 'verse': 7, 'text': 'وَتَحۡمِلُ أَثۡقَالَكُمۡ إِلَىٰ بَلَدٖ لَّمۡ تَكُونُواْ بَٰلِغِيهِ إِلَّا بِشِقِّ ٱلۡأَنفُسِۚ إِنَّ رَبَّكُمۡ لَرَءُوفٞ رَّحِيمٞ\nШунингдек, улар сизлар жонларингизни қийнаб, зўр-базўр (машаққат билан) етадиган юртларга юкларингизни элтиб берур. Албатта, Парвардигорингиз меҳрибон ва раҳмлидир'}, {'chapter': 16, 'verse': 8, 'text': 'وَٱلۡخَيۡلَ وَٱلۡبِغَالَ وَٱلۡحَمِيرَ لِتَرۡكَبُوهَا وَزِينَةٗۚ وَيَخۡلُقُ مَا لَا تَعۡلَمُونَ\nУ зот яна сизлар минишингиз учун зийнат қилиб отлар, хачирлар ва эшакларни (яратди). Яна сизлар (ҳали) билмайдиган нарсаларни ҳам яратур'}, {'chapter': 16, 'verse': 9, 'text': 'وَعَلَى ٱللَّهِ قَصۡدُ ٱلسَّبِيلِ وَمِنۡهَا جَآئِرٞۚ وَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ\nТўғри Йўлга (ҳидоят қилиш) ёлғиз Аллоҳ измидадир. (Зотан, йўллар) орасида эгриси ҳам бордир. Агар (Аллоҳ) хоҳлаганида сизларнинг барчангизни (Тўғри Йўлга) ҳидоят қилган бўлур эди. (Лекин У зот сизларга қайси йўл тўғри ва қайси йўллар эгри эканини кўрсатиб, қай бир йўлни танлаш ихтиёрини ўзларингизга қўйиб берди ва ақл-идрок билан Тўғри Йўлни танлаб олган бахтли бандаларини Ўз хоҳиш-иродаси билан ўша Ҳақ Йўлга ҳидоят этишни ваъда қилди, эгри йўлларни танлаган кимсалар эса ҳар икки дунёда бахтсиз бўлиб, ўзларига зиён қилишлари тўғрисида хабар берди)'}, {'chapter': 16, 'verse': 10, 'text': 'هُوَ ٱلَّذِيٓ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗۖ لَّكُم مِّنۡهُ شَرَابٞ وَمِنۡهُ شَجَرٞ فِيهِ تُسِيمُونَ\nУ осмондан сизлар учун ичимлик бўлган сувни ёғдирган Зотдир. Сизлар (ҳайвонларингизни) боқадиган дов-дарахтлар (ўт-ўланлар) ҳам ўша сувдан (ичар)'}, {'chapter': 16, 'verse': 11, 'text': 'يُنۢبِتُ لَكُم بِهِ ٱلزَّرۡعَ وَٱلزَّيۡتُونَ وَٱلنَّخِيلَ وَٱلۡأَعۡنَٰبَ وَمِن كُلِّ ٱلثَّمَرَٰتِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَتَفَكَّرُونَ\nУ зот сизлар учун ўша (сув) ёрдамида (турли) экинларни, зайтун, хурмо, узум ва барча меваларни ундириб-ўстирур. Албатта, бу нарсада (яъни, бир хил сув ёрдамида рангу рўйи, таъму лаззати бошқа-бошқа бўлган мева-чеваларнинг униб-ўсишида) тафаккур қиладиган қавм учун оят-ибрат бордир'}, {'chapter': 16, 'verse': 12, 'text': 'وَسَخَّرَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ وَٱلنُّجُومُ مُسَخَّرَٰتُۢ بِأَمۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nУ зот сизлар (бекаму кўст ҳаёт кечиришингиз учун) кеча ва кундузни, қуёш ва ойни (бир тартибга солиб) бўйсундириб қўйди. (Барча) юлдуз — сайёралар ҳам Унинг амрига бўйинсундирилгандир. Албатта, бу нарсада ақл юргизадиган қавм учун оят-ибратлар бордир'}, {'chapter': 16, 'verse': 13, 'text': 'وَمَا ذَرَأَ لَكُمۡ فِي ٱلۡأَرۡضِ مُخۡتَلِفًا أَلۡوَٰنُهُۥٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَذَّكَّرُونَ\nЯна У зот сизлар учун Ерда яратиб қўйган ранго-ранг нарсаларни (яъни, ҳайвонот ва наботот оламини, ер ости ва ер усти бойликларини ҳам сизларга бўйинсундирди). Албатта, бу нарсада ибрат-эслатма оладиган қавм учун оят-ибрат бордир'}, {'chapter': 16, 'verse': 14, 'text': 'وَهُوَ ٱلَّذِي سَخَّرَ ٱلۡبَحۡرَ لِتَأۡكُلُواْ مِنۡهُ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُواْ مِنۡهُ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ مَوَاخِرَ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nУ сизлар янги гўшт (яъни, балиқ гўшти) ейишларингиз ва тақадиган тақинчоқлар чиқариб олишларингиз учун (сизларга) денгизни ҳам бўйинсундириб қўйган Зотдир. Сиз унда (сувни) ёриб кетаётган кемаларни кўрурсиз. У зотнинг фазлу марҳаматидан (ризқу рўз) исташларингиз ва (берган неъматларига) шукр қилишингиз учун (сизларга денгизларни бўйинсундириб қўйди)'}, {'chapter': 16, 'verse': 15, 'text': 'وَأَلۡقَىٰ فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَأَنۡهَٰرٗا وَسُبُلٗا لَّعَلَّكُمۡ تَهۡتَدُونَ\nУ зот Ер сизларни тебратмаслиги учун унда тоғларни барпо қилди, (ўзларингиз учун ичимлик бўлиши ҳамда экин-тикинларингизни суғоришингиз учун) дарёларни (оқизиб қўйди) ва (кўзлаган манзилларингизга боришда) адашмасликларингиз учун йўлларни пайдо қилди'}, {'chapter': 16, 'verse': 16, 'text': 'وَعَلَٰمَٰتٖۚ وَبِٱلنَّجۡمِ هُمۡ يَهۡتَدُونَ\nВа (у йўлларга) белгилар (қилиб қўйди. Кечаларда эса кишилар) юлдузлар билан йўл топурлар'}, {'chapter': 16, 'verse': 17, 'text': 'أَفَمَن يَخۡلُقُ كَمَن لَّا يَخۡلُقُۚ أَفَلَا تَذَكَّرُونَ\n(Эй мушриклар), ахир (барча мавжудотни) яратгувчи зот — Аллоҳ билан (ҳеч нарса) ярата олмайдиган (бутларингиз) баробарми?! Ўйламайсизларми'}, {'chapter': 16, 'verse': 18, 'text': 'وَإِن تَعُدُّواْ نِعۡمَةَ ٱللَّهِ لَا تُحۡصُوهَآۗ إِنَّ ٱللَّهَ لَغَفُورٞ رَّحِيمٞ\nАгар Аллоҳнинг неъматларини санасангизлар, саноғига ета олмайсизлар. Албатта, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 16, 'verse': 19, 'text': 'وَٱللَّهُ يَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَ\nАллоҳ яширган нарсаларингизни ҳам, ошкор қилган нарсаларингизни ҳам билур'}, {'chapter': 16, 'verse': 20, 'text': 'وَٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ لَا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ\n(Мушрикларнинг) Аллоҳни қўйиб, илтижо қилаётган бутлари бирон нарсани яратгувчи эмасдир, балки ўзлари яралгувчидирлар'}, {'chapter': 16, 'verse': 21, 'text': 'أَمۡوَٰتٌ غَيۡرُ أَحۡيَآءٖۖ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ\n(Улар) жонсиз ўликлардир. Ва улар (ўзларига сиғинаётган мушрикларнинг) қачон қайта тирилишларини ҳам сеза олмайдилар'}, {'chapter': 16, 'verse': 22, 'text': 'إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۚ فَٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ قُلُوبُهُم مُّنكِرَةٞ وَهُم مُّسۡتَكۡبِرُونَ\nИлоҳингиз ягона Аллоҳдир. Бас, Охиратга иймон келтирмайдиган кимсаларнинг кўнгиллари (Аллоҳнинг якка-ягона эканлигини) инкор қилур, (уларнинг) ўзлари мутакаббир кимсалардир'}, {'chapter': 16, 'verse': 23, 'text': 'لَا جَرَمَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡتَكۡبِرِينَ\nШак-шубҳа йўқки, Аллоҳ уларнинг яширган нарсаларини ҳам, ошкор қилган нарсаларини ҳам билур. Албатта, У зот мутакаббир кимсаларни севмас'}, {'chapter': 16, 'verse': 24, 'text': 'وَإِذَا قِيلَ لَهُم مَّاذَآ أَنزَلَ رَبُّكُمۡ قَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\n(Қачон уларга: «Парвардигорингиз Муҳаммад алайҳис-салоту вас-саломга) қандай нарса нозил қилди?», дейилса, улар (масхара қилишиб): «Аввал ўтганларнинг афсоналарини», дейдилар'}, {'chapter': 16, 'verse': 25, 'text': 'لِيَحۡمِلُوٓاْ أَوۡزَارَهُمۡ كَامِلَةٗ يَوۡمَ ٱلۡقِيَٰمَةِ وَمِنۡ أَوۡزَارِ ٱلَّذِينَ يُضِلُّونَهُم بِغَيۡرِ عِلۡمٍۗ أَلَا سَآءَ مَا يَزِرُونَ\nУлар (бу ёлғон сўзларни, бошқа одамларни йўлдан оздириш учун ва оқибатда) Қиёмат Кунида, ўзларининг барча гуноҳларига ҳамда ўзлари жаҳолат билан йўлдан оздирган кимсаларнинг гуноҳларидан ҳам (бир қисмига) жавобгар бўлиш учун (айтгандирлар). Огоҳ бўлингиз, улар жуда ёмон юкни (яъни, гуноҳларни) орқалаб олибдилар'}, {'chapter': 16, 'verse': 26, 'text': 'قَدۡ مَكَرَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَأَتَى ٱللَّهُ بُنۡيَٰنَهُم مِّنَ ٱلۡقَوَاعِدِ فَخَرَّ عَلَيۡهِمُ ٱلسَّقۡفُ مِن فَوۡقِهِمۡ وَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ\nУлардан аввалги (кофир) кимсалар ҳам (ўз пайғамбарларига мана шунга ўхшаш) макр-ҳийлалар қилган эдилар. Шунда Аллоҳ улар (қурган макр-ҳийла) биноларини таг-туги билан емириб ташлади, бас, улар (тўқиб олган ёлғон бино — тузумлари)нинг томи ўзларининг устига қулади ва уларга ўзлари сезмаган — кутмаган тарафдан азоб-ҳалокат келди'}, {'chapter': 16, 'verse': 27, 'text': 'ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يُخۡزِيهِمۡ وَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تُشَـٰٓقُّونَ فِيهِمۡۚ قَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ إِنَّ ٱلۡخِزۡيَ ٱلۡيَوۡمَ وَٱلسُّوٓءَ عَلَى ٱلۡكَٰفِرِينَ\nКейин — Қиёмат Кунида (Аллоҳ) уларни яна расво қилур ва айтур: «Менинг «шерик»ларим қаерда қолишди?! (Ахир) сизлар (Менинг пайғамбарларим билан ўшалар тўғрисида) талашиб-тортишар эдинглар-ку?!» (Шунда) илм берилган зотлар (яъни, пайғамбарлар ва уламолар) дерлар: «Дарҳақиқат, бугун бутун расволик ва азоб-уқубат қофирлар устига тушар»'}, {'chapter': 16, 'verse': 28, 'text': 'ٱلَّذِينَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡۖ فَأَلۡقَوُاْ ٱلسَّلَمَ مَا كُنَّا نَعۡمَلُ مِن سُوٓءِۭۚ بَلَىٰٓۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا كُنتُمۡ تَعۡمَلُونَ\nУлар (куфр йўлини танлашлари сабабли) ўз жонларига жабр қилган ҳолларида (ўлим) фаришталари уларнинг жонларини олурлар. Шунда улар (тақдирга) тан беришиб — (бўйинларини қисишиб): «Бизлар бирон ёмонлик — гуноҳ қилган эмасмиз», (дерлар). Йўқ! Шак-шубҳасиз, Аллоҳ қилган амалларингизни билгувчидир'}, {'chapter': 16, 'verse': 29, 'text': 'فَٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَلَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ\nЭнди ўзларингиз мангу қоладиган жаҳаннам дарвозаларидан кирингиз! Мутакаббир кимсаларнинг борар жойлари нақадар ёмон'}, {'chapter': 16, 'verse': 30, 'text': '۞وَقِيلَ لِلَّذِينَ ٱتَّقَوۡاْ مَاذَآ أَنزَلَ رَبُّكُمۡۚ قَالُواْ خَيۡرٗاۗ لِّلَّذِينَ أَحۡسَنُواْ فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٞۚ وَلَدَارُ ٱلۡأٓخِرَةِ خَيۡرٞۚ وَلَنِعۡمَ دَارُ ٱلۡمُتَّقِينَ\nТақводор бўлган зотларга: «Парвардигорингиз (Ўз пайғамбарига) қандай нарсани нозил қилди?», дейилганида, улар: «Яхшиликни», дерлар. Чиройли амал қилган зотлар учун бу дунёда ҳам чиройли мукофотлар бўлур. Лекин шак-шубҳа йўқки, Охират диёри янада яхшироқдир. Тақводор зотларнинг турар жойлари нақадар яхши'}, {'chapter': 16, 'verse': 31, 'text': 'جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ لَهُمۡ فِيهَا مَا يَشَآءُونَۚ كَذَٰلِكَ يَجۡزِي ٱللَّهُ ٱلۡمُتَّقِينَ\nУлар остидан дарёлар оқиб турадиган мангу жаннатларга кирурлар. Улар учун у жойда хоҳлаган нарсалари бўлур. Аллоҳ тақводор зотларни мана шундай мукофотлар'}, {'chapter': 16, 'verse': 32, 'text': 'ٱلَّذِينَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ طَيِّبِينَ يَقُولُونَ سَلَٰمٌ عَلَيۡكُمُ ٱدۡخُلُواْ ٱلۡجَنَّةَ بِمَا كُنتُمۡ تَعۡمَلُونَ\nУлар (ширк ва исёндан) пок бўлган ҳолларида, фаришталар уларнинг жонларини олар эканлар: «Сизларга тинчлик-омонлик бўлгай. (Энди) қилиб ўтган амалларингиз сабабли жаннатга кирингиз», дерлар'}, {'chapter': 16, 'verse': 33, 'text': 'هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ ٱلۡمَلَـٰٓئِكَةُ أَوۡ يَأۡتِيَ أَمۡرُ رَبِّكَۚ كَذَٰلِكَ فَعَلَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\n(Кофирлар эса) фақат ўзларига (ўлим) фаришталари келишини ёки Парвардигорингизнинг амри — азоби келишини кутарлар. Улардан аввал ўтган (кофир) кимсалар ҳам мана шундай қилган эдилар (яъни, ўзларига келган пайғамбарларни ёлғончи қилишиб, ҳалокатга дучор бўлган эдилар). Уларга Аллоҳ жабр қилгани йўқ, балки улар (ўзлари) ўз жонларига жабр қилгувчи бўлдилар'}, {'chapter': 16, 'verse': 34, 'text': 'فَأَصَابَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nБас, қилган ёмонликлари (уларнинг) ўзларига етди ва кулиб-масхара қилган нарсалари ўзларини тутди'}, {'chapter': 16, 'verse': 35, 'text': 'وَقَالَ ٱلَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ ٱللَّهُ مَا عَبَدۡنَا مِن دُونِهِۦ مِن شَيۡءٖ نَّحۡنُ وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن دُونِهِۦ مِن شَيۡءٖۚ كَذَٰلِكَ فَعَلَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ فَهَلۡ عَلَى ٱلرُّسُلِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nМушрик бўлган кимсалар: «Агар Аллоҳ хоҳлаганида, биз (ҳам), ота-боболаримиз (ҳам) Ундан ўзга бирон нарсага ибодат қилмаган ва Унинг (ҳукмисиз) бирон нарсани ҳаром қилмаган бўлур эдик», дедилар. Улардан аввал ўтган (кофир) кимсалар ҳам мана шундай қилган эдилар (яъни, ўз ихтиёрлари билан куфр йўлини танлаб, сўнгра: «Бизнинг кофирлигимизга Аллоҳнинг Ўзи сабаб», деб Аллоҳ таоло шаънига туҳмат қилган эдилар). Пайғамбарлар зиммасида (одамларни мажбуран динга киргизиш эмас, балки) фақат (Аллоҳ нозил қилган ваҳийни) очиқ-ойдин етказиш бордир'}, {'chapter': 16, 'verse': 36, 'text': 'وَلَقَدۡ بَعَثۡنَا فِي كُلِّ أُمَّةٖ رَّسُولًا أَنِ ٱعۡبُدُواْ ٱللَّهَ وَٱجۡتَنِبُواْ ٱلطَّـٰغُوتَۖ فَمِنۡهُم مَّنۡ هَدَى ٱللَّهُ وَمِنۡهُم مَّنۡ حَقَّتۡ عَلَيۡهِ ٱلضَّلَٰلَةُۚ فَسِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\nАниқки, Биз ҳар бир умматга: «Аллоҳга ибодат қилинглар ва шайтондан йироқ бўлинглар», (деган ваҳий билан) бир пайғамбар юборганмиз. Шунда улар (ўша умматлар) орасида Аллоҳ ҳидоят қилган зотлар ҳам шунингдек, чекларига йўлдан озишлик тушган кимсалар ҳам бўлган. Бас, Ер юзида сайру саёҳат қилиб, (ўз пайғамбарларини) ёлғончи қилган кимсаларнинг оқибатлари қандай бўлганини кўринглар'}, {'chapter': 16, 'verse': 37, 'text': 'إِن تَحۡرِصۡ عَلَىٰ هُدَىٰهُمۡ فَإِنَّ ٱللَّهَ لَا يَهۡدِي مَن يُضِلُّۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар сиз у (мушрик)ларнинг ҳидоят топишларига ташна бўлсангиз-да, билингки, Аллоҳ Ўзи йўлдан оздирадиган кимсаларни ҳаргиз ҳидоят қилмас ва улар учун бирон мададкор ҳам бўлмас'}, {'chapter': 16, 'verse': 38, 'text': 'وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَا يَبۡعَثُ ٱللَّهُ مَن يَمُوتُۚ بَلَىٰ وَعۡدًا عَلَيۡهِ حَقّٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nУлар жон-жаҳдлари билан: «Аллоҳ ўлган кишиларни қайта тирилтирмайди», деб Аллоҳ номига қасам ичдилар. Йўқ (албатта, тирилтирур)! Бу — У зотнинг ҳақ ваъдасидир. Лекин одамларнинг кўплари (бу олам имтиҳон олами эканини, асл-абадий ҳаёт қайта тирилгандан кейин бўлишини) билмайдилар'}, {'chapter': 16, 'verse': 39, 'text': 'لِيُبَيِّنَ لَهُمُ ٱلَّذِي يَخۡتَلِفُونَ فِيهِ وَلِيَعۡلَمَ ٱلَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ كَانُواْ كَٰذِبِينَ\nУларга ўзлари ихтилоф қилаётган нарсаларини баён қилиб (кўрсатиб) бериш учун ҳамда кофир бўлган кимсалар ўзларининг ёлғончи эканликларини билишлари учун ҳам (албатта, Аллоҳ барча халойиқни қайта тирилтирур)'}, {'chapter': 16, 'verse': 40, 'text': 'إِنَّمَا قَوۡلُنَا لِشَيۡءٍ إِذَآ أَرَدۡنَٰهُ أَن نَّقُولَ لَهُۥ كُن فَيَكُونُ\nБиз бирон нарсани ирода қилсак, унга айтадиган сўзимиз «Бўл», демоқликдир. Бас, ўша нарса бўлур'}, {'chapter': 16, 'verse': 41, 'text': 'وَٱلَّذِينَ هَاجَرُواْ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ظُلِمُواْ لَنُبَوِّئَنَّهُمۡ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَلَأَجۡرُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nЗулму қийноқларга дучор бўлганларидан сўнг, Аллоҳ йўлида ҳижрат қилган зотларни, албатта, бу дунёда ҳам гўзал (гўшаларга) жойлаштирурмиз. Энди Охират ажри — мукофоти янада каттароқ эканини (одамлар) билсалар эди'}, {'chapter': 16, 'verse': 42, 'text': 'ٱلَّذِينَ صَبَرُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nУлар (мушриклар етказган озор-азиятларга) сабр-тоқат қилган ва ёлғиз Парвардигоргагина суянган зотлардир'}, {'chapter': 16, 'verse': 43, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِمۡۖ فَسۡـَٔلُوٓاْ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан (яъни, сизни пайғамбар қилиб юборишимиздан) илгари ҳам (фаришталарни эмас, балки) фақат (сиз каби) кишиларни — инсонларнигина Ўзимиз туширган ваҳий билан (пайғамбар қилиб) юборганмиз. (Эй мушриклар), агар (бу ҳақда) билмайдиган бўлсангизлар, аҳли илмлардан (яъни, Таврот ва Инжилни биладиган кишилардан) сўранглар'}, {'chapter': 16, 'verse': 44, 'text': 'بِٱلۡبَيِّنَٰتِ وَٱلزُّبُرِۗ وَأَنزَلۡنَآ إِلَيۡكَ ٱلذِّكۡرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيۡهِمۡ وَلَعَلَّهُمۡ يَتَفَكَّرُونَ\n(Биз аввалги пайғамбарларни) очиқ ҳужжатлар ва китоблар билан (юборганмиз). Сизга эса одамларга нозил қилган нарсаларни (яъни, шариат аҳкомларини) баён қилиб беришингиз учун ва шояд тафаккур қилсалар, деб бу эслатмани — Қуръонни нозил қилдик'}, {'chapter': 16, 'verse': 45, 'text': 'أَفَأَمِنَ ٱلَّذِينَ مَكَرُواْ ٱلسَّيِّـَٔاتِ أَن يَخۡسِفَ ٱللَّهُ بِهِمُ ٱلۡأَرۡضَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ\n(Муҳаммад алайҳис-салоту вас-саломни қатл этиш учун) энг ёмон макру ҳийлаларни қилган кимсалар ўзларини ер ютишидан ёки (уларга) сезмаган-кутмаган тарафларидан (Аллоҳнинг) азоби келиб қолишидан хотиржам бўлдиларми'}, {'chapter': 16, 'verse': 46, 'text': 'أَوۡ يَأۡخُذَهُمۡ فِي تَقَلُّبِهِمۡ فَمَا هُم بِمُعۡجِزِينَ\nЁки сафар-саёҳатларда юрган чоғларида уларни (бало-офатлар) ушлаб, ҳеч қаёққа қочиб қутулолмай қолишларидан (қўрқмадиларми)'}, {'chapter': 16, 'verse': 47, 'text': 'أَوۡ يَأۡخُذَهُمۡ عَلَىٰ تَخَوُّفٖ فَإِنَّ رَبَّكُمۡ لَرَءُوفٞ رَّحِيمٌ\nЁки (қилган макр-ҳийлаларининг оқибатидан ўзлари) қўрқиб-пусиб юрган пайтларида, уларни (Аллоҳнинг балоси) ушлаб қолишидан (қўрқмадиларми)?! Дарҳақиқат, Парвардигорингиз меҳрибон ва раҳмлидир'}, {'chapter': 16, 'verse': 48, 'text': 'أَوَلَمۡ يَرَوۡاْ إِلَىٰ مَا خَلَقَ ٱللَّهُ مِن شَيۡءٖ يَتَفَيَّؤُاْ ظِلَٰلُهُۥ عَنِ ٱلۡيَمِينِ وَٱلشَّمَآئِلِ سُجَّدٗا لِّلَّهِ وَهُمۡ دَٰخِرُونَ\nАхир улар Аллоҳ яратган барча нарсаларнинг соялари ўнгу сўлларига, Аллоҳга сажда қилган ҳолда эгилаётганини — улар (барчаси Аллоҳга) бўйинсунувчи эканликларини кўрмадиларми'}, {'chapter': 16, 'verse': 49, 'text': 'وَلِلَّهِۤ يَسۡجُدُۤ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ مِن دَآبَّةٖ وَٱلۡمَلَـٰٓئِكَةُ وَهُمۡ لَا يَسۡتَكۡبِرُونَ\nОсмонлар ва Ердаги барча жониворлар ва фаришталар ҳам кибр-ҳаво қилмаган ҳолларида ёлғиз Аллоҳга сажда қилурлар'}, {'chapter': 16, 'verse': 50, 'text': 'يَخَافُونَ رَبَّهُم مِّن فَوۡقِهِمۡ وَيَفۡعَلُونَ مَا يُؤۡمَرُونَ۩\nУлар устиларидаги (уларни кузатиб турган) Парвардигорларидан қўрқурлар ва фақат ўзларига амр этилган ишларнигина қилурлар'}, {'chapter': 16, 'verse': 51, 'text': '۞وَقَالَ ٱللَّهُ لَا تَتَّخِذُوٓاْ إِلَٰهَيۡنِ ٱثۡنَيۡنِۖ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ فَإِيَّـٰيَ فَٱرۡهَبُونِ\nАллоҳ: «Икки худога бандалик қилманглар! Фақат биргина илоҳ (яъни, ёлғиз Аллоҳ таологина) бордир, холос. Бас, Мендангина қўрқингиз!», деди'}, {'chapter': 16, 'verse': 52, 'text': 'وَلَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَهُ ٱلدِّينُ وَاصِبًاۚ أَفَغَيۡرَ ٱللَّهِ تَتَّقُونَ\nОсмонлардаги ва Ердаги барча нарсалар Унинг мулкидир. Тоат-ибодат мудом ёлғиз Унга (бўлмоғи лозимдир). Аллоҳдан ўзгалардан (яъни, жонсиз бутлардан ёки ўзларингизга ўхшаган одамлардан) қўрқурмисизлар'}, {'chapter': 16, 'verse': 53, 'text': 'وَمَا بِكُم مِّن نِّعۡمَةٖ فَمِنَ ٱللَّهِۖ ثُمَّ إِذَا مَسَّكُمُ ٱلضُّرُّ فَإِلَيۡهِ تَجۡـَٔرُونَ\nСизларга берилган барча ноз-неъматлар ёлғиз Аллоҳдандир. Шунингдек, қачон сизларга (бирон) бало-мусибат етса ҳам фақат Унгагина илтижо қилурсизлар'}, {'chapter': 16, 'verse': 54, 'text': 'ثُمَّ إِذَا كَشَفَ ٱلضُّرَّ عَنكُمۡ إِذَا فَرِيقٞ مِّنكُم بِرَبِّهِمۡ يُشۡرِكُونَ\nСўнг, У зот сизлардан балони аритганида эса, баногоҳ сизларнинг орангиздан бир тўда чиқиб, Парвардигорларига ширк келтирурлар'}, {'chapter': 16, 'verse': 55, 'text': 'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ\nБиз уларга ато этган нарсаларга куфрони неъмат қилишиб, (Парвардигорларига ширк келтирурлар). Бас, (эй мушриклар, тўрт кунлик дунёда Биз берган неъматлардан) фойдаланиб қолинглар. Яқинда, (яъни, Қиёмат Кунида бу ширкларингизнинг оқибатини) билурсизлар'}, {'chapter': 16, 'verse': 56, 'text': 'وَيَجۡعَلُونَ لِمَا لَا يَعۡلَمُونَ نَصِيبٗا مِّمَّا رَزَقۡنَٰهُمۡۗ تَٱللَّهِ لَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَفۡتَرُونَ\nУлар Биз ризқ қилиб берган нарсалардан ўзлари билмайдиган (яъни, жоҳилликлари, билимсизликлари сабабли худо деб ўйлаган) бутлари учун насиба ажратурлар. (Эй мушриклар), Аллоҳ номига қасамки, албатта сизлар ўзларингиз тўқиб-бичиб олган бутларингиз тўғрисида (Қиёмат Кунида) масъул бўлурсизлар'}, {'chapter': 16, 'verse': 57, 'text': 'وَيَجۡعَلُونَ لِلَّهِ ٱلۡبَنَٰتِ سُبۡحَٰنَهُۥ وَلَهُم مَّا يَشۡتَهُونَ\nУлар: («Фаришталар Аллоҳнинг қизлари», дейишиб), у «қизлар»ни Аллоҳники қилишади — У зот (бу бўҳтондан) покдир — ўзларининг кўнгиллари тусаган нарсани (яъни, ўғилларни) эса ўзлариники (қилиб олишди)'}, {'chapter': 16, 'verse': 58, 'text': 'وَإِذَا بُشِّرَ أَحَدُهُم بِٱلۡأُنثَىٰ ظَلَّ وَجۡهُهُۥ مُسۡوَدّٗا وَهُوَ كَظِيمٞ\nҚачон бировларига қиз (кўргани ҳақида) хушхабар берилса, ғазабга тўлиб, юзлари қорайиб кетар'}, {'chapter': 16, 'verse': 59, 'text': 'يَتَوَٰرَىٰ مِنَ ٱلۡقَوۡمِ مِن سُوٓءِ مَا بُشِّرَ بِهِۦٓۚ أَيُمۡسِكُهُۥ عَلَىٰ هُونٍ أَمۡ يَدُسُّهُۥ فِي ٱلتُّرَابِۗ أَلَا سَآءَ مَا يَحۡكُمُونَ\nВа у (қизни) хўрлаган ҳолида олиб қолиш ёки (тириклай) тупроққа қориш (тўғрисида ўй суриб), ўзига хушхабар берилган нарсанинг (яъни, қиз кўришнинг) «ёмон»лигидан (номус қилиб) одамлардан яшириниб олур. Огоҳ булингизким, улар (бу қилмишлари билан) энг ёмон (яъни, ноҳақ) ҳукм чиқарурлар'}, {'chapter': 16, 'verse': 60, 'text': 'لِلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ مَثَلُ ٱلسَّوۡءِۖ وَلِلَّهِ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОхиратга иймон келтирмайдиган кимсалар энг ёмон сифат эгаларидир. Аллоҳ эса энг юксак хислатлар Соҳибидир. У қудрат ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 16, 'verse': 61, 'text': 'وَلَوۡ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِظُلۡمِهِم مَّا تَرَكَ عَلَيۡهَا مِن دَآبَّةٖ وَلَٰكِن يُؤَخِّرُهُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۖ فَإِذَا جَآءَ أَجَلُهُمۡ لَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ\nАгар Аллоҳ одамларни (улар қилган ҳар қандай) зулм-зўравонликлари билан ушлаганида (азоблаганида) Ер юзида бирон жониворни қўймаган бўлур эди. Лекин У зот уларни маълум муддатгача қўйиб қўяр. Бас, қачон уларнинг ажаллари етиб келганида эса уни бирон соат кетга ҳам сура олмайдилар, муқаддам ҳам қила олмайдилар'}, {'chapter': 16, 'verse': 62, 'text': 'وَيَجۡعَلُونَ لِلَّهِ مَا يَكۡرَهُونَۚ وَتَصِفُ أَلۡسِنَتُهُمُ ٱلۡكَذِبَ أَنَّ لَهُمُ ٱلۡحُسۡنَىٰۚ لَا جَرَمَ أَنَّ لَهُمُ ٱلنَّارَ وَأَنَّهُم مُّفۡرَطُونَ\nУлар ўзлари ёмон кўрадиган нарсаларни (яъни, қиз болаларни) Аллоҳники дейишиб, яна (уларнинг) тиллари ўзлари учун чиройли мукофот — жаннат бўлиши ҳақида ёлғон сўзларни сўзлайди. Шак-шубҳа йўқки, улар дўзах эгаларидир ва албатта, улар (дўзахга) биринчилар (қаторида ташланурлар)'}, {'chapter': 16, 'verse': 63, 'text': 'تَٱللَّهِ لَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَهُوَ وَلِيُّهُمُ ٱلۡيَوۡمَ وَلَهُمۡ عَذَابٌ أَلِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳ номига қасамки, дарҳақиқат, Биз сиздан илгари ўтган умматларга ҳам пайғамбарлар юборганимизда, шайтон уларга ҳам ўзларининг (қабиҳ) амалларини чиройли қилиб кўрсатган эди. Чунки у бугун (бу дунёда) уларнинг «дўсти»дир. (Охиратда эса) улар учун аламли азоб бордир'}, {'chapter': 16, 'verse': 64, 'text': 'وَمَآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ إِلَّا لِتُبَيِّنَ لَهُمُ ٱلَّذِي ٱخۡتَلَفُواْ فِيهِ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ\nБиз сизга иймон келтирган қавм учун ҳидоят ва раҳмат бўлган бу Китоб — Қуръонни фақат уларга ўзлари ихтилоф этаётган нарсаларни баён қилиб беришингиз учунгина нозил қилдик'}, {'chapter': 16, 'verse': 65, 'text': 'وَٱللَّهُ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَسۡمَعُونَ\nАллоҳ осмондан сув (ёмғир) ёғдириб, унинг ёрдамида ўлик ерни тирилтирди. Албатта, бу ишда (ваъз насиҳатга) қулоқ тутадиган қавм учун оят-ибрат бордир'}, {'chapter': 16, 'verse': 66, 'text': 'وَإِنَّ لَكُمۡ فِي ٱلۡأَنۡعَٰمِ لَعِبۡرَةٗۖ نُّسۡقِيكُم مِّمَّا فِي بُطُونِهِۦ مِنۢ بَيۡنِ فَرۡثٖ وَدَمٖ لَّبَنًا خَالِصٗا سَآئِغٗا لِّلشَّـٰرِبِينَ\nДарвоқеъ, сизлар учун чорва ҳайвонларида ҳам бир ибрат бордир. Биз сизларни уларнинг қоринларидаги нажосат ва қон ораларидан ичувчилар учун ўтимли таъмли бўлиб чиққан нарса — тоза сут билан суғорурмиз'}, {'chapter': 16, 'verse': 67, 'text': 'وَمِن ثَمَرَٰتِ ٱلنَّخِيلِ وَٱلۡأَعۡنَٰبِ تَتَّخِذُونَ مِنۡهُ سَكَرٗا وَرِزۡقًا حَسَنًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡقِلُونَ\nСизлар маст қилгувчи (ароқни) ҳам, гўзал — ҳалол ризқ(нинг ўзини) ҳам хурмо ва узумларнинг меваларидан олурсизлар. Албатта, бунда ҳам ақл юритадиган қавм учун оят-ибрат бордир'}, {'chapter': 16, 'verse': 68, 'text': 'وَأَوۡحَىٰ رَبُّكَ إِلَى ٱلنَّحۡلِ أَنِ ٱتَّخِذِي مِنَ ٱلۡجِبَالِ بُيُوتٗا وَمِنَ ٱلشَّجَرِ وَمِمَّا يَعۡرِشُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиз асаларига ваҳий — амр қилди: «Тоғларга, дарахтларга ва (одамлар) қурадиган инларга уя солгин'}, {'chapter': 16, 'verse': 69, 'text': 'ثُمَّ كُلِي مِن كُلِّ ٱلثَّمَرَٰتِ فَٱسۡلُكِي سُبُلَ رَبِّكِ ذُلُلٗاۚ يَخۡرُجُ مِنۢ بُطُونِهَا شَرَابٞ مُّخۡتَلِفٌ أَلۡوَٰنُهُۥ فِيهِ شِفَآءٞ لِّلنَّاسِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَتَفَكَّرُونَ\nСўнгра турли мевалардан еб, Парвардигоринг (сен учун) осон-қулай қилиб қўйган йўллардан юргин!» Унинг қорнидан одамлар учун шифо бўлган алвон рангли ичимлик-асал чиқур. Албатта, бу ишда тафаккур қиладиган қавм учун оят-ибрат бордир'}, {'chapter': 16, 'verse': 70, 'text': 'وَٱللَّهُ خَلَقَكُمۡ ثُمَّ يَتَوَفَّىٰكُمۡۚ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرۡذَلِ ٱلۡعُمُرِ لِكَيۡ لَا يَعۡلَمَ بَعۡدَ عِلۡمٖ شَيۡـًٔاۚ إِنَّ ٱللَّهَ عَلِيمٞ قَدِيرٞ\nСизларни Аллоҳ яратгандир. Сўнгра жонингизни ҳам Унинг Ўзи олур. Сизларнинг орангизда (ёшликдаги гўзал-тотли ҳаётдан кексалик пайтидаги) энг тубан кун кўришга (яъни, ўз-ўзини бошқара олмайдиган ҳолатга) қайтарилиб, илгари билган нарсаларининг барчасини унутиб юборадиган кишилар ҳам бордир. Албатта, Аллоҳ билгувчи ва қудратлидир'}, {'chapter': 16, 'verse': 71, 'text': 'وَٱللَّهُ فَضَّلَ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖ فِي ٱلرِّزۡقِۚ فَمَا ٱلَّذِينَ فُضِّلُواْ بِرَآدِّي رِزۡقِهِمۡ عَلَىٰ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَهُمۡ فِيهِ سَوَآءٌۚ أَفَبِنِعۡمَةِ ٱللَّهِ يَجۡحَدُونَ\nАллоҳ айримларингиздан айримларингизни ризқда устун қилиб қўйди. Бас, бу (ризқда) устун қилинган кишилар ўзларининг ризқларини қўл остидаги қулларига қайтариб (бой-камбағалликда) барчалари баробар бўлиб олмайдилар-ку! (Шундай бўлгач, қандоқ қилиб барчага ризқу рўз ато этгувчи Аллоҳ таолога қўлидан ҳеч иш келмайдиган бутларни баробар қиладилар?!) Аллоҳнинг неъматини инкор қилурларми?! (Яъни, Яратганнинг Ўзи ато этган ризқу рўзни инкор қилишиб, бу неъматларни ўша жонсиз бутлар берган, деб ўйлайдиларми)'}, {'chapter': 16, 'verse': 72, 'text': 'وَٱللَّهُ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَجَعَلَ لَكُم مِّنۡ أَزۡوَٰجِكُم بَنِينَ وَحَفَدَةٗ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِۚ أَفَبِٱلۡبَٰطِلِ يُؤۡمِنُونَ وَبِنِعۡمَتِ ٱللَّهِ هُمۡ يَكۡفُرُونَ\nАллоҳ сизлар учун ўзларингиздан жуфтлар яратиб, жуфтларингиздан сизлар учун болалар, набиралар пайдо қилди ва сизларни ҳалол-пок ризқлардан баҳраманд этди. Бас, улар ботил бутларга иймон келтириб, Аллоҳнинг неъматига кофир бўлурларми'}, {'chapter': 16, 'verse': 73, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَمۡلِكُ لَهُمۡ رِزۡقٗا مِّنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ شَيۡـٔٗا وَلَا يَسۡتَطِيعُونَ\nВа Аллоҳни қўйиб, улар учун осмонлар ва Ердан бирон нарсани ризқ қилиб бера олмайдиган, ҳеч нарсага қодир бўлмайдиган бутларга ибодат қилурларми'}, {'chapter': 16, 'verse': 74, 'text': 'فَلَا تَضۡرِبُواْ لِلَّهِ ٱلۡأَمۡثَالَۚ إِنَّ ٱللَّهَ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nБас, Аллоҳнинг ўхшашини қидирманглар! (У зотнинг мисли — ўхшаши йўқдир.) Албатта Аллоҳ (барча нарсани) билур, сизлар (ҳеч нарсани) билмассиз'}, {'chapter': 16, 'verse': 75, 'text': '۞ضَرَبَ ٱللَّهُ مَثَلًا عَبۡدٗا مَّمۡلُوكٗا لَّا يَقۡدِرُ عَلَىٰ شَيۡءٖ وَمَن رَّزَقۡنَٰهُ مِنَّا رِزۡقًا حَسَنٗا فَهُوَ يُنفِقُ مِنۡهُ سِرّٗا وَجَهۡرًاۖ هَلۡ يَسۡتَوُۥنَۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nАллоҳ бундоқ бир мисол келтирур: ҳеч нарсага кучи етмайдиган эгалик (бировга қарам) қул билан Биз Ўз тарафимиздан чиройли мўл ризқ бериб, ўша ризқдан яширин ё ошкора инфоқ-эҳсон қилаётган (эркин) бир киши баробар бўлурми? (Бас, нега мушриклар қулга ўхшаган жонсиз бутларни бутун коинотнинг эгаси бўлган ва уни Ўзи хоҳлаганидек тасарруф қиладиган Аллоҳ таолога тенг шерик деб биладилар?!) Ҳамду сано Аллоҳникидир (яъни, Ўша зотгина ҳамду санога ва маъбудликка лойиқдир). Лекин уларнинг кўплари (буни) билмайдилар'}, {'chapter': 16, 'verse': 76, 'text': 'وَضَرَبَ ٱللَّهُ مَثَلٗا رَّجُلَيۡنِ أَحَدُهُمَآ أَبۡكَمُ لَا يَقۡدِرُ عَلَىٰ شَيۡءٖ وَهُوَ كَلٌّ عَلَىٰ مَوۡلَىٰهُ أَيۡنَمَا يُوَجِّههُّ لَا يَأۡتِ بِخَيۡرٍ هَلۡ يَسۡتَوِي هُوَ وَمَن يَأۡمُرُ بِٱلۡعَدۡلِ وَهُوَ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАллоҳ яна икки киши ҳақида мисол келтирур: улардан бири ҳеч нарсага кучи етмайдиган гунг-соқовдир. У эгасига ортиқча юк бўлиб (эгаси) қаерга жўнатса, бирон яхшилик-фойда келтирмайди. Ўша кимса билан ўзи Тўғри Йўлда бўлган ҳолда, (ўзгаларни ҳам) адолат-ҳақиқатга буюрадиган киши баробар бўлурми?! (Ҳеч қачон баробар бўлмас. У ҳолда нега мушриклар гунг-соқов бутларни бутун оламни Тўғри Йўлга юришга буюрадиган Аллоҳ таолога тенг — шерик деб биладилар)'}, {'chapter': 16, 'verse': 77, 'text': 'وَلِلَّهِ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَآ أَمۡرُ ٱلسَّاعَةِ إِلَّا كَلَمۡحِ ٱلۡبَصَرِ أَوۡ هُوَ أَقۡرَبُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nОсмонлар ва Ердаги ғайб — сирларни (билиш) Аллоҳга хосдир. Соат — Қиёмат амри (яъни, Қиёматнинг қойим бўлиши) кўз очиб юмгунча ё ундан-да тездир. Дарҳақиқат, Аллоҳ ҳамма нарсага қодирдир'}, {'chapter': 16, 'verse': 78, 'text': 'وَٱللَّهُ أَخۡرَجَكُم مِّنۢ بُطُونِ أُمَّهَٰتِكُمۡ لَا تَعۡلَمُونَ شَيۡـٔٗا وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَ لَعَلَّكُمۡ تَشۡكُرُونَ\nАллоҳ сизларни оналарингиз қорнидан бирон нарса билмаган ҳолингизда чиқарди ва шукр қилишингиз учун сизларга кулоқ, кўзлар ва дилларни берди'}, {'chapter': 16, 'verse': 79, 'text': 'أَلَمۡ يَرَوۡاْ إِلَى ٱلطَّيۡرِ مُسَخَّرَٰتٖ فِي جَوِّ ٱلسَّمَآءِ مَا يُمۡسِكُهُنَّ إِلَّا ٱللَّهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nОсмони фалакда (учиш учун) бўйинсундирилган қушларга боқмайдиларми?! Уларни ёлғиз Аллоҳ ушлаб турибди-ку! Албатта, бу ишда иймон келтирадиган қавм учун оят-ибратлар бордир'}, {'chapter': 16, 'verse': 80, 'text': 'وَٱللَّهُ جَعَلَ لَكُم مِّنۢ بُيُوتِكُمۡ سَكَنٗا وَجَعَلَ لَكُم مِّن جُلُودِ ٱلۡأَنۡعَٰمِ بُيُوتٗا تَسۡتَخِفُّونَهَا يَوۡمَ ظَعۡنِكُمۡ وَيَوۡمَ إِقَامَتِكُمۡ وَمِنۡ أَصۡوَافِهَا وَأَوۡبَارِهَا وَأَشۡعَارِهَآ أَثَٰثٗا وَمَتَٰعًا إِلَىٰ حِينٖ\nАллоҳ сизлар учун уйларингизни оромгоҳ қилди ва сизлар учун чорва ҳайвонларининг териларидан кўчадиган кунларингизда ва қўнадиган кунларингизда енгил кўтариб кета оладиган уй-ўтовлар (бунёд) этди ҳамда (қўйларнинг) жунларидан, (туяларнинг) юнгларидан ва (эчкиларнинг) тивитларидан маълум бир вақтга қадар фойдаланиладиган жиҳоз ва матолар (яратди)'}, {'chapter': 16, 'verse': 81, 'text': 'وَٱللَّهُ جَعَلَ لَكُم مِّمَّا خَلَقَ ظِلَٰلٗا وَجَعَلَ لَكُم مِّنَ ٱلۡجِبَالِ أَكۡنَٰنٗا وَجَعَلَ لَكُمۡ سَرَٰبِيلَ تَقِيكُمُ ٱلۡحَرَّ وَسَرَٰبِيلَ تَقِيكُم بَأۡسَكُمۡۚ كَذَٰلِكَ يُتِمُّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تُسۡلِمُونَ\nАллоҳ сизлар учун Ўзи яратган нарсалардан соя-салқин жойлар (бунёд) этди ва сизлар учун тоғлардан ғор-бошпаналар қилди ҳамда сизлар учун иссиқ (совуқ)дан асрайдиган кийимлар ва сизларни (душманнинг) зиён етказишидан сақлайдиган совутлар (яратди). У зот шояд мусулмон бўларсизлар, деб сизларга Ўз неъматини мана шундай комил қилиб берур'}, {'chapter': 16, 'verse': 82, 'text': 'فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُ ٱلۡمُبِينُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар энди ҳам улар (сиздан) юз ўгирсалар, у ҳолда сизнинг зиммангизда фақат (ҳақиқатни мана шундай) очиқ-ойдин қилиб етказиш бор, холос'}, {'chapter': 16, 'verse': 83, 'text': 'يَعۡرِفُونَ نِعۡمَتَ ٱللَّهِ ثُمَّ يُنكِرُونَهَا وَأَكۡثَرُهُمُ ٱلۡكَٰفِرُونَ\nУлар Аллоҳнинг неъматини танирлар ва кейин уни инкор қилурлар. Уларнинг кўплари кофирлардир (яъни, ҳеч қачон Аллоҳга иймон келтирмайдилар)'}, {'chapter': 16, 'verse': 84, 'text': 'وَيَوۡمَ نَبۡعَثُ مِن كُلِّ أُمَّةٖ شَهِيدٗا ثُمَّ لَا يُؤۡذَنُ لِلَّذِينَ كَفَرُواْ وَلَا هُمۡ يُسۡتَعۡتَبُونَ\n(Қиёмат) куни ҳар бир умматдан бир гувоҳни (яъни, ўша умматнинг пайғамбарини) келтириб қўюрмиз. Сўнгра (энди) кофир бўлган кимсаларга (тавба-тазарруъ қилиш учун) изн берилмас ҳамда улардан (Аллоҳ рози бўладиган амалларга) қайтиш ҳам талаб қилинмас'}, {'chapter': 16, 'verse': 85, 'text': 'وَإِذَا رَءَا ٱلَّذِينَ ظَلَمُواْ ٱلۡعَذَابَ فَلَا يُخَفَّفُ عَنۡهُمۡ وَلَا هُمۡ يُنظَرُونَ\nЗолим (кофир) бўлган кимсалар азобни кўрган вақтларида (яъни, Қиёмат Кунида) улардан (у азоб) енгиллатилмас ва уларга муҳлат ҳам берилмас (яъни, барча яхши амалларни ҳаёт вақтларида ўз ихтиёрлари билан қилган кишиларгина савоб олурлар)'}, {'chapter': 16, 'verse': 86, 'text': 'وَإِذَا رَءَا ٱلَّذِينَ أَشۡرَكُواْ شُرَكَآءَهُمۡ قَالُواْ رَبَّنَا هَـٰٓؤُلَآءِ شُرَكَآؤُنَا ٱلَّذِينَ كُنَّا نَدۡعُواْ مِن دُونِكَۖ فَأَلۡقَوۡاْ إِلَيۡهِمُ ٱلۡقَوۡلَ إِنَّكُمۡ لَكَٰذِبُونَ\n(У кунда) мушрик бўлган кимсалар ўзларининг бутларини кўрган вақтларида: «Парвардигоро, бизлар Сени қўйиб, анави бутларимизга илтижо қилар эдик», деганларида, у (бутлар бунга жавобан): «Сизлар, шак-шубҳасиз, ёлғончидирсизлар», деб гап қотарлар'}, {'chapter': 16, 'verse': 87, 'text': 'وَأَلۡقَوۡاْ إِلَى ٱللَّهِ يَوۡمَئِذٍ ٱلسَّلَمَۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nУлар у Кунда ёлғиз Аллоҳга бўйинсунурлар, ўзлари тўқиб олган «худо»лари эса улардан ғойиб бўлур'}, {'chapter': 16, 'verse': 88, 'text': 'ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ زِدۡنَٰهُمۡ عَذَابٗا فَوۡقَ ٱلۡعَذَابِ بِمَا كَانُواْ يُفۡسِدُونَ\nКофир бўлган ва (одамларни) Аллоҳ йўлидан тўсган кимсаларга бузғунчилик қилганликлари сабабли азоб устига азобни зиёда қилурмиз'}, {'chapter': 16, 'verse': 89, 'text': 'وَيَوۡمَ نَبۡعَثُ فِي كُلِّ أُمَّةٖ شَهِيدًا عَلَيۡهِم مِّنۡ أَنفُسِهِمۡۖ وَجِئۡنَا بِكَ شَهِيدًا عَلَىٰ هَـٰٓؤُلَآءِۚ وَنَزَّلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ تِبۡيَٰنٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ وَبُشۡرَىٰ لِلۡمُسۡلِمِينَ\n(Қиёмат Куни) ҳар бир уммат орасидан ўзларидан бўлган бир гувоҳни (яъни, пайғамбарни) келтирурмиз. Сизни эса (эй Муҳаммад алайҳис-салоту вас-салом), ана у (ўз умматингизга) гувоҳ қилиб келтирдик ва сизга ҳамма нарсани баён қилиб берувчи, ҳидоят, раҳмат ва мусулмонлар учун хушхабар бўлган Китобни — Қуръонни нозил қилдик'}, {'chapter': 16, 'verse': 90, 'text': '۞إِنَّ ٱللَّهَ يَأۡمُرُ بِٱلۡعَدۡلِ وَٱلۡإِحۡسَٰنِ وَإِيتَآيِٕ ذِي ٱلۡقُرۡبَىٰ وَيَنۡهَىٰ عَنِ ٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِ وَٱلۡبَغۡيِۚ يَعِظُكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ\nАлбатта, Аллоҳ адолатга, чиройли амаллар қилишга ва қавм-қариндошга яхшилик қилишга буюрур ҳамда бузуқлик, ёмон ишлар ва зўравонликлардан қайтарур. У зот шояд ибрат-эслатма оларсизлар, деб сизларга панд-насиҳат қилур'}, {'chapter': 16, 'verse': 91, 'text': 'وَأَوۡفُواْ بِعَهۡدِ ٱللَّهِ إِذَا عَٰهَدتُّمۡ وَلَا تَنقُضُواْ ٱلۡأَيۡمَٰنَ بَعۡدَ تَوۡكِيدِهَا وَقَدۡ جَعَلۡتُمُ ٱللَّهَ عَلَيۡكُمۡ كَفِيلًاۚ إِنَّ ٱللَّهَ يَعۡلَمُ مَا تَفۡعَلُونَ\nАҳд-паймон берган вақтларингизда, Аллоҳга берган аҳдингизга вафо қилингиз! Аллоҳни кафил қилиб ичган қасамларингизни (Аллоҳ номини зикр этиш билан) мустаҳкам қилганингиздан кейин бузмангиз! Албатта, Аллоҳ қилаётган ишларингизни билур'}, {'chapter': 16, 'verse': 92, 'text': 'وَلَا تَكُونُواْ كَٱلَّتِي نَقَضَتۡ غَزۡلَهَا مِنۢ بَعۡدِ قُوَّةٍ أَنكَٰثٗا تَتَّخِذُونَ أَيۡمَٰنَكُمۡ دَخَلَۢا بَيۡنَكُمۡ أَن تَكُونَ أُمَّةٌ هِيَ أَرۡبَىٰ مِنۡ أُمَّةٍۚ إِنَّمَا يَبۡلُوكُمُ ٱللَّهُ بِهِۦۚ وَلَيُبَيِّنَنَّ لَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ مَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nВа сизлар бир миллат бошқа бир миллатдан (сон ё бойлик жиҳатидан) ортиқроқ бўлгани учун қасамларингизни (бузиб) алдов воситаси қилишингиз билан (яъни, бошқа бойроқ ва кучлироқ қавмни топсангиз, аввалги қасамингиздан кечиб кетаверишингиз билан) худди ўзи тўқиган нарсасини пишиқ-пухта бўлганидан сўнг парча-парча қилиб бузиб-чуватиб ташлаган хотинга ўхшаб қолмангиз! Албатта, Аллоҳ бу (аҳдга вафо қилишга буюриш) билан сизларни имтиҳон қилур ва албатта, Қиёмат Кунида сизларга ихтилоф қилиб ўтган нарсаларингизни баён қилиб берур'}, {'chapter': 16, 'verse': 93, 'text': 'وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَلَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَعۡمَلُونَ\nАгар Аллоҳ хоҳлаганида, албатта, сизларни бир уммат қилган бўлур эди. Лекин У зот Ўзи хоҳлаган кимсаларни йўлдан оздирур, Ўзи хоҳлаган кишиларни (Тўғри Йўлга) ҳидоят қилур. Ва албатта, қилиб ўтган амалларингизга масъул бўлурсизлар'}, {'chapter': 16, 'verse': 94, 'text': 'وَلَا تَتَّخِذُوٓاْ أَيۡمَٰنَكُمۡ دَخَلَۢا بَيۡنَكُمۡ فَتَزِلَّ قَدَمُۢ بَعۡدَ ثُبُوتِهَا وَتَذُوقُواْ ٱلسُّوٓءَ بِمَا صَدَدتُّمۡ عَن سَبِيلِ ٱللَّهِ وَلَكُمۡ عَذَابٌ عَظِيمٞ\nҚасамларингизни алдов воситаси қилиб олмангиз! У ҳолда (Ислом йўлида) маҳкам ўрнашган оёқ тойиб кетур ва Аллоҳ йўлидан тўсганларингиз сабабли (чунки, сизларнинг қасамхўрлик қилганингизни кўриб, айрим кишилар Исломга интилиш ўрнига ундан йироқлашиб кетурлар ва бунга сизларнинг қилмишингаз сабаб бўлур) ёмонлик (бало ва мусибатлар)ни тотиб қолурсизлар ҳамда (Охиратда) сизлар учун улуғ азоб бўлур'}, {'chapter': 16, 'verse': 95, 'text': 'وَلَا تَشۡتَرُواْ بِعَهۡدِ ٱللَّهِ ثَمَنٗا قَلِيلًاۚ إِنَّمَا عِندَ ٱللَّهِ هُوَ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nАллоҳга берилган аҳд-паймонни озгина қийматга алмаштириб юбормангиз! Агар билсангизлар, албатта, Аллоҳ даргоҳидаги нарса (яъни, ажр-савоб) сизлар учун (дунё матоларидан) яхшироқдир'}, {'chapter': 16, 'verse': 96, 'text': 'مَا عِندَكُمۡ يَنفَدُ وَمَا عِندَ ٱللَّهِ بَاقٖۗ وَلَنَجۡزِيَنَّ ٱلَّذِينَ صَبَرُوٓاْ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُواْ يَعۡمَلُونَ\nСизларнинг ҳузурларингиздаги нарсалар йўқ бўлур. Аллоҳ даргоҳидаги нарсалар эса боқийдир. Биз, албатта, сабр-қаноат этган зотларни ўзлари қилиб ўтган амалларидан чиройлироқ ажр-савоблар билан мукофотлаймиз'}, {'chapter': 16, 'verse': 97, 'text': 'مَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَلَنُحۡيِيَنَّهُۥ حَيَوٰةٗ طَيِّبَةٗۖ وَلَنَجۡزِيَنَّهُمۡ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُواْ يَعۡمَلُونَ\nЭркакми ё аёлми — кимда-ким мўмин бўлган ҳолида бирон яхши амал қилса, бас, Биз унга покиза ҳаёт ато этурмиз ва уларни ўзлари қилиб ўтган амалларидан чиройлироқ ажр-савоблар билан мукофотлаймиз'}, {'chapter': 16, 'verse': 98, 'text': 'فَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ فَٱسۡتَعِذۡ بِٱللَّهِ مِنَ ٱلشَّيۡطَٰنِ ٱلرَّجِيمِ\n(Эй мўмин бандам), ҳар қачон Қуръон қироат қилсанг, албатта, қувилган — малъун шайтон (васвасаси)дан Аллоҳ паноҳ беришини сўрагин'}, {'chapter': 16, 'verse': 99, 'text': 'إِنَّهُۥ لَيۡسَ لَهُۥ سُلۡطَٰنٌ عَلَى ٱلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nАлбатта, иймон келтирган ва ёлғиз Парвардигорларига таваккул қиладиган зотлар устида (шайтон) учун ҳеч қандай салтанат — ҳукмронлик йўқдир'}, {'chapter': 16, 'verse': 100, 'text': 'إِنَّمَا سُلۡطَٰنُهُۥ عَلَى ٱلَّذِينَ يَتَوَلَّوۡنَهُۥ وَٱلَّذِينَ هُم بِهِۦ مُشۡرِكُونَ\nУнинг (шайтоннинг) ҳукмронлиги фақат (уни) дўст тутиб, (Аллоҳ)га шерик қилиб оладиган кимсалар устидадир'}, {'chapter': 16, 'verse': 101, 'text': 'وَإِذَا بَدَّلۡنَآ ءَايَةٗ مَّكَانَ ءَايَةٖ وَٱللَّهُ أَعۡلَمُ بِمَا يُنَزِّلُ قَالُوٓاْ إِنَّمَآ أَنتَ مُفۡتَرِۭۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nҚачон Биз бир оят ўрнига бошқа бир оятни алмаштирсак — ҳолбуки, Аллоҳ Ўзи нозил қиладиган оятларни жуда яхши билгувчидир — улар (пайғамбарга): «Шак-шубҳасиз, сен ўзинг тўқиб олурсан», дейдилар! Йўқ, уларнинг кўплари билмайдилар'}, {'chapter': 16, 'verse': 102, 'text': 'قُلۡ نَزَّلَهُۥ رُوحُ ٱلۡقُدُسِ مِن رَّبِّكَ بِٱلۡحَقِّ لِيُثَبِّتَ ٱلَّذِينَ ءَامَنُواْ وَهُدٗى وَبُشۡرَىٰ لِلۡمُسۡلِمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) уни (яъни, Қуръонни) Руҳул-Қудус (яъни, Жаброил) Парвардигорингиз тарафидан иймон келтирган зотларни собитқадам қилиш учун мусулмонларга ҳидоят ва хушхабар булган ҳолида ҳаққи-рост нозил қилганини айтинг'}, {'chapter': 16, 'verse': 103, 'text': 'وَلَقَدۡ نَعۡلَمُ أَنَّهُمۡ يَقُولُونَ إِنَّمَا يُعَلِّمُهُۥ بَشَرٞۗ لِّسَانُ ٱلَّذِي يُلۡحِدُونَ إِلَيۡهِ أَعۡجَمِيّٞ وَهَٰذَا لِسَانٌ عَرَبِيّٞ مُّبِينٌ\nАниқки, Биз уларнинг «(Қуръонни Муҳаммадга) бирон одам ўргатмоқда», деяётганини билурмиз. (Лекин уларнинг бу даъволари пучдир, чунки) улар ишора қилаётган кимсанинг тили ажамий (яъни, арабий эмас), бу (Қуръон) эса очиқ-равшан арабийдир'}, {'chapter': 16, 'verse': 104, 'text': 'إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِـَٔايَٰتِ ٱللَّهِ لَا يَهۡدِيهِمُ ٱللَّهُ وَلَهُمۡ عَذَابٌ أَلِيمٌ\nАлбатта, Аллоҳ оятларига иймон келтирмайдиган кимсаларни Аллоҳ ҳидоят қилмас. Улар учун аламли азоб бордир'}, {'chapter': 16, 'verse': 105, 'text': 'إِنَّمَا يَفۡتَرِي ٱلۡكَذِبَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِـَٔايَٰتِ ٱللَّهِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰذِبُونَ\nЁлғон сўзларни ҳам фақат Аллоҳ оятларига иймон келтирмайдиган кимсаларгина тўқурлар. Ана ўшаларнинг ўзлари ёлғончидирлар'}, {'chapter': 16, 'verse': 106, 'text': 'مَن كَفَرَ بِٱللَّهِ مِنۢ بَعۡدِ إِيمَٰنِهِۦٓ إِلَّا مَنۡ أُكۡرِهَ وَقَلۡبُهُۥ مُطۡمَئِنُّۢ بِٱلۡإِيمَٰنِ وَلَٰكِن مَّن شَرَحَ بِٱلۡكُفۡرِ صَدۡرٗا فَعَلَيۡهِمۡ غَضَبٞ مِّنَ ٱللَّهِ وَلَهُمۡ عَذَابٌ عَظِيمٞ\nКим Аллоҳга иймон келтирганидан кейин (яна қайтиб) кофир бўлса, (Аллоҳнинг ғазабига дучор бўлур). Лекин ким қалби иймон билан ором олгани ҳолда (куфр калимасини айтишга) мажбур қилинса, (унинг иймонига зиён етмас). Аммо кимнинг кўнгли куфр билан (яъни, диндан чиқиб, кофир бўлиш билан) ёзиладиган бўлса, бас, ундай кимсаларга Аллоҳ томонидан ғазаб ва улуғ азоб бордир'}, {'chapter': 16, 'verse': 107, 'text': 'ذَٰلِكَ بِأَنَّهُمُ ٱسۡتَحَبُّواْ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا عَلَى ٱلۡأٓخِرَةِ وَأَنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ\nБунга (яъни, бундай азоб-уқубатларга дучор бўлишларига) сабаб улар ҳаёти дунёни Охиратдан афзал билганларидир. Аллоҳ эса кофир қавмни ҳидоят қилмас. (Бас, улар кофир бўлганлари сабабли ҳақ йўлни топа олмай ўлиб кетурлар)'}, {'chapter': 16, 'verse': 108, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَسَمۡعِهِمۡ وَأَبۡصَٰرِهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡغَٰفِلُونَ\nАллоҳ ундай кимсаларнинг дилларини, қулоқларини ва кўзларини муҳрлаб қўйгандир. Улар ғофил кимсалардир'}, {'chapter': 16, 'verse': 109, 'text': 'لَا جَرَمَ أَنَّهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡخَٰسِرُونَ\nШак-шубҳа йўқки, улар Охиратда зиён кўргувчилардир'}, {'chapter': 16, 'verse': 110, 'text': 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُواْ مِنۢ بَعۡدِ مَا فُتِنُواْ ثُمَّ جَٰهَدُواْ وَصَبَرُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ\nСўнгра (эй Муҳаммад алайҳис-салоту вас-салом, билингки), албатта, Парвардигорингиз, фитналарга дучор этилганларидан кейин (Маккаи Мукаррамадан Мадинаи Мунавварага) ҳижрат қилган, сўнг (Аллоҳ йўлида) жиҳод этиб, (бу жиҳод машаққатларига сабр-тоқат қилган зотлар учун) мададкор, ёрдамчидир. Албатта, Парвардигорингиз у (меҳнат-машаққатларни кўтарганларидан) кейин (улар учун) мағфират қилгувчи, меҳрибондир'}, {'chapter': 16, 'verse': 111, 'text': '۞يَوۡمَ تَأۡتِي كُلُّ نَفۡسٖ تُجَٰدِلُ عَن نَّفۡسِهَا وَتُوَفَّىٰ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nҲар бир жон фақат ўзини ўйлаб — ҳимоя қилиб қоладиган ва ҳар бир жон қилган амалининг (мукофотини) тўла оладиган ҳамда уларга зулм қилинмайдиган Кунни (эсланг)'}, {'chapter': 16, 'verse': 112, 'text': 'وَضَرَبَ ٱللَّهُ مَثَلٗا قَرۡيَةٗ كَانَتۡ ءَامِنَةٗ مُّطۡمَئِنَّةٗ يَأۡتِيهَا رِزۡقُهَا رَغَدٗا مِّن كُلِّ مَكَانٖ فَكَفَرَتۡ بِأَنۡعُمِ ٱللَّهِ فَأَذَٰقَهَا ٱللَّهُ لِبَاسَ ٱلۡجُوعِ وَٱلۡخَوۡفِ بِمَا كَانُواْ يَصۡنَعُونَ\nАллоҳ бир шаҳарни (яъни, Маккани) мисол келтирур: у тинч, сокин (шаҳар) эди, ҳар томондан ризқу рўзи бекаму-кўст келиб турар эди. Бас, (қачонки) у (яъни, унинг аҳолиси) Аллоҳнинг неъматига ношукрлик қилгач, Аллоҳ у (шаҳар аҳолисига) бу «хунарлари» (куфру исёнлари) сабабли очарчилик ва нотинчлик балосини тотдириб қўйди'}, {'chapter': 16, 'verse': 113, 'text': 'وَلَقَدۡ جَآءَهُمۡ رَسُولٞ مِّنۡهُمۡ فَكَذَّبُوهُ فَأَخَذَهُمُ ٱلۡعَذَابُ وَهُمۡ ظَٰلِمُونَ\nДарҳақиқат, уларга ўзларидан бўлган бир пайғамбар келганида уни ёлғончи қилишгач, уларни золим бўлган ҳолларида (Аллоҳнинг) азоби ушлади'}, {'chapter': 16, 'verse': 114, 'text': 'فَكُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَٰلٗا طَيِّبٗا وَٱشۡكُرُواْ نِعۡمَتَ ٱللَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ\nБас, (эй мўминлар), Аллоҳ сизларга ризқ қилиб берган ҳалол-пок нарсалардан енглар ва агар Аллоҳга ибодат этгувчи бўлсангизлар, У зотнинг неъматларига шукр қилинглар'}, {'chapter': 16, 'verse': 115, 'text': 'إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۖ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nУ (Аллоҳ) сизларга фақатгина ўлаксани, қонни, тўнғиз гўштини ва Аллоҳдан ўзгага аталиб сўйилган нарсаларнигина ҳаром қилди. Энди кимки золим ва ҳаддан ошувчи бўлмаган ҳолида ночор вазиятда қолса (ва шу ҳолда мазкур нарсалардан ўлмагудек миқдорда еса, гуноҳкор бўлмайди). Бас, албатта, Аллоҳ мағфират қилгувчи, раҳмлидир'}, {'chapter': 16, 'verse': 116, 'text': 'وَلَا تَقُولُواْ لِمَا تَصِفُ أَلۡسِنَتُكُمُ ٱلۡكَذِبَ هَٰذَا حَلَٰلٞ وَهَٰذَا حَرَامٞ لِّتَفۡتَرُواْ عَلَى ٱللَّهِ ٱلۡكَذِبَۚ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ\nАллоҳ шаънига ёлғон тўқиш учун (яъни, Аллоҳ буюрмаган ҳукмларни Аллоҳники дейиш учун) тилларингизга келган ёлгонни гапириб: «Бу ҳалол, бу ҳаром», деяверманглар! Чунки Аллоҳ шаънига ёлғон тўқийдиган кимсалар ҳеч нажот топмаслар'}, {'chapter': 16, 'verse': 117, 'text': 'مَتَٰعٞ قَلِيلٞ وَلَهُمۡ عَذَابٌ أَلِيمٞ\n(Ундай кимсалар учун бу ҳаёти дунёда) озгина фойда бўлур. (Охиратда эса) улар учун аламли азоб бордир'}, {'chapter': 16, 'verse': 118, 'text': 'وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا مَا قَصَصۡنَا عَلَيۡكَ مِن قَبۡلُۖ وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), яҳудий бўлган кимсаларга илгари (Анъом сурасида) сизга сўзлаб берган нарсаларни ҳаром қилдик. Биз уларга зулм қилмадик, балки улар ўзларига (ўзлари) зулм қилгувчи бўлдилар'}, {'chapter': 16, 'verse': 119, 'text': 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُواْ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٌ\nСўнгра (билингки), албатта, Парвардигорингиз, билмаган ҳолда ёмонлик — гуноҳ қилиб қўйиб, кейин тавба қилган ва ўзларини тузатган кишилар учун, шак-шубҳасиз, ўша (тавба-тазарруъларидан) сўнг Парвардигорингиз (уларни) мағфират қилгувчи, меҳрибондир'}, {'chapter': 16, 'verse': 120, 'text': 'إِنَّ إِبۡرَٰهِيمَ كَانَ أُمَّةٗ قَانِتٗا لِّلَّهِ حَنِيفٗا وَلَمۡ يَكُ مِنَ ٱلۡمُشۡرِكِينَ\nАлбатта, Иброҳим Аллоҳга итоат қилгувчи, ҳақ йўлдан тойилмаган бир пешво-имом эди. У мушриклардан эмас эди'}, {'chapter': 16, 'verse': 121, 'text': 'شَاكِرٗا لِّأَنۡعُمِهِۚ ٱجۡتَبَىٰهُ وَهَدَىٰهُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nУ (Аллоҳнинг) неъматларига шукр қилгувчи эди. (Шу сабабдан Аллоҳ) уни (пайғамбарликка) танлаб, Тўғри Йўлга ҳидоят қилди'}, {'chapter': 16, 'verse': 122, 'text': 'وَءَاتَيۡنَٰهُ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ\nБиз унга бу дунёда чиройли (обрў-эътибор) ато этдик (зеро, барча динларнинг аҳллари Иброҳим алайҳиссаломнинг номларини ҳурмат билан тилга оладилар). Албатта, у Охиратда ҳам солиҳ — яқинларимиздандир'}, {'chapter': 16, 'verse': 123, 'text': 'ثُمَّ أَوۡحَيۡنَآ إِلَيۡكَ أَنِ ٱتَّبِعۡ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ\nСўнгра (Биз, эй Муҳаммад алайҳис-салоту вас-салом), сизга ҳақ йўлдан тойилмаган Иброҳимнинг динига эргашинг, у мушриклардан эмас эди, деб ваҳий юбордик'}, {'chapter': 16, 'verse': 124, 'text': 'إِنَّمَا جُعِلَ ٱلسَّبۡتُ عَلَى ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِۚ وَإِنَّ رَبَّكَ لَيَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\n«Шанба» (у кунни улуғлаш) фақатгина у кун ҳақида ихтилоф қилган кимсаларнинг (яъни, яҳудийларнинг) устига (фарз) қилингандир. Албатта, Парвардигорингиз Қиёмат Кунида уларнинг ўртасида ўзлари ихтилоф қилиб ўтган нарсалар тўғрисида ҳукм қилур'}, {'chapter': 16, 'verse': 125, 'text': 'ٱدۡعُ إِلَىٰ سَبِيلِ رَبِّكَ بِٱلۡحِكۡمَةِ وَٱلۡمَوۡعِظَةِ ٱلۡحَسَنَةِۖ وَجَٰدِلۡهُم بِٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингизнинг йўли — динига донолик, ҳикмат ва чиройли панд-насиҳат билан даъват қилинг! Улар (сиз билан талашиб-тортишадиган кимсалар) билан энг гўзал йўлда мужодала — мунозара қилинг! Албатта, Парвардигорингизнинг Ўзи Унинг йўлидан озган кимсаларни жуда яхши билур ва У зот ҳидоят топгувчи кишиларни ҳам жуда яхши билур'}, {'chapter': 16, 'verse': 126, 'text': 'وَإِنۡ عَاقَبۡتُمۡ فَعَاقِبُواْ بِمِثۡلِ مَا عُوقِبۡتُم بِهِۦۖ وَلَئِن صَبَرۡتُمۡ لَهُوَ خَيۡرٞ لِّلصَّـٰبِرِينَ\n(Эй мўминлар), агар сизлар (ўзингизга етган бирон зиён-заҳмат учун) интиқом олмоқчи бўлсангизлар, у ҳолда фақат сизларга етказилган зиён баробарида интиқом олингиз. (Аммо) агар (интиқом олмай) сабр-тоқат қилсангизлар, албатта сабр қилгувчи кишилар учун (бу интиқом олмоқдан) яхшироқдир'}, {'chapter': 16, 'verse': 127, 'text': 'وَٱصۡبِرۡ وَمَا صَبۡرُكَ إِلَّا بِٱللَّهِۚ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُ فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, бу даъват йўлида сизга етган озор-азиятларга) сабр-тоқат қилинг. Ёлғиз Аллоҳ ёрдами билангина сабр қилишга қодир бўлурсиз. Уларнинг (яъни, мушрикларнинг динга кирмаганликлари) устида маҳзун бўлманг. Уларнинг макру ҳийлаларидан сиқилманг ҳам'}, {'chapter': 16, 'verse': 128, 'text': 'إِنَّ ٱللَّهَ مَعَ ٱلَّذِينَ ٱتَّقَواْ وَّٱلَّذِينَ هُم مُّحۡسِنُونَ\nЗотан, Аллоҳ тақводор бўлган ва чиройли амаллар қилгувчи зотлар билан биргадир'}, {'chapter': 17, 'verse': 1, 'text': 'سُبۡحَٰنَ ٱلَّذِيٓ أَسۡرَىٰ بِعَبۡدِهِۦ لَيۡلٗا مِّنَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ إِلَى ٱلۡمَسۡجِدِ ٱلۡأَقۡصَا ٱلَّذِي بَٰرَكۡنَا حَوۡلَهُۥ لِنُرِيَهُۥ مِنۡ ءَايَٰتِنَآۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\n(Аллоҳ) бир кеча, Ўз бандаси (Муҳаммад)ни — унга оят-мўъжизаларимиздан кўрсатиш учун (Маккадаги) Масжидул-Ҳаромдан (Қуддусдаги) Биз атрофини баракотли қилиб қўйган Масжидул-Ақсога сайр қилдирган (барча айбу нуқсондан) пок Зотдир. Дарҳақиқат, У эшитгувчи, кўргувчи Зотдир'}, {'chapter': 17, 'verse': 2, 'text': 'وَءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَـٰٓءِيلَ أَلَّا تَتَّخِذُواْ مِن دُونِي وَكِيلٗا\nБиз Мусога Китоб (Таврот) ато этдик ва у (Китобни) Бани Исроил (қавми) учун ҳидоят қилиб (дедик): «Сизлар Мендан ўзгани вакил (яъни, барча ишлар ишониб топшириладиган зот) қилиб олманглар!»'}, {'chapter': 17, 'verse': 3, 'text': 'ذُرِّيَّةَ مَنۡ حَمَلۡنَا مَعَ نُوحٍۚ إِنَّهُۥ كَانَ عَبۡدٗا شَكُورٗا\n«(Эй) Биз (ғарқ бўлишдан қутқариб) Нуҳ билан бирга (кемада) кўтариб нажот берган кишиларнинг зурриётлари, (сизлар ҳам Нуҳнинг йўлини тутинглар), зеро, у доимо шукр қилгувчи банда эди»'}, {'chapter': 17, 'verse': 4, 'text': 'وَقَضَيۡنَآ إِلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ فِي ٱلۡكِتَٰبِ لَتُفۡسِدُنَّ فِي ٱلۡأَرۡضِ مَرَّتَيۡنِ وَلَتَعۡلُنَّ عُلُوّٗا كَبِيرٗا\nШунингдек, Биз Бани Исроилга у Китобда: «Сизлар албатта, (Шом) заминида икки марта бузғунчилик қилурсизлар ва катта туғёнга тушурсизлар», деб хабар бердик'}, {'chapter': 17, 'verse': 5, 'text': 'فَإِذَا جَآءَ وَعۡدُ أُولَىٰهُمَا بَعَثۡنَا عَلَيۡكُمۡ عِبَادٗا لَّنَآ أُوْلِي بَأۡسٖ شَدِيدٖ فَجَاسُواْ خِلَٰلَ ٱلدِّيَارِۚ وَكَانَ وَعۡدٗا مَّفۡعُولٗا\nБас, қачон у (бузғунчилик)лардан аввалгисининг вақти келганида, Биз сизларнинг устингизга (қилмишларингиз баробарида) жазолаш учун (куч-қувват эгалари бўлган) бандаларимизни юбордик. Улар (сизларни ҳалок қилиш, асир олиш учун) ҳовли-жойлар оралаб кездилар. (Бу ваъда) адо қилингувчи ваъда бўлди'}, {'chapter': 17, 'verse': 6, 'text': 'ثُمَّ رَدَدۡنَا لَكُمُ ٱلۡكَرَّةَ عَلَيۡهِمۡ وَأَمۡدَدۡنَٰكُم بِأَمۡوَٰلٖ وَبَنِينَ وَجَعَلۡنَٰكُمۡ أَكۡثَرَ نَفِيرًا\nСўнгра (тавба-тазарру қилиб ўзларингизни ўнглаганингиздан кейин) Биз сизларни қайтадан уларнинг (Бобил ҳукмдорининг) устига ғолиб қилдик ва сизларга мол-дунё, бола-чақа билан мадад бериб сон-саноқларингизни кўпайтирдик'}, {'chapter': 17, 'verse': 7, 'text': 'إِنۡ أَحۡسَنتُمۡ أَحۡسَنتُمۡ لِأَنفُسِكُمۡۖ وَإِنۡ أَسَأۡتُمۡ فَلَهَاۚ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ لِيَسُـُٔواْ وُجُوهَكُمۡ وَلِيَدۡخُلُواْ ٱلۡمَسۡجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٖ وَلِيُتَبِّرُواْ مَا عَلَوۡاْ تَتۡبِيرًا\nАгар (шундан кейин) чиройли амаллар қилсангизлар, ўзларингизга яхшилик қилган бўлурсизлар. Агар ёмон, гуноҳ амаллар қилсангизлар ҳам ўзларингиз учундир. Бас, қачон кейинги (бузғунчиликнинг) вақти-соати келганида, (улар яна) юзларингизни қаро қилишлари, аввалги сафар кирганлари каби Масжид (ал-Ақсо)га кириб, (уни топташлари) учун ҳамда ўзлари эгаллаб олган (барча) ерларни вайрон этишлари учун (яна душманларингизни сизларнинг устингизга юборурмиз)'}, {'chapter': 17, 'verse': 8, 'text': 'عَسَىٰ رَبُّكُمۡ أَن يَرۡحَمَكُمۡۚ وَإِنۡ عُدتُّمۡ عُدۡنَاۚ وَجَعَلۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ حَصِيرًا\n(Агар тавба-тазарру қилсангизлар), шояд Парвардигорларингиз сизларга раҳм қилса. Агар (яна бузғунчиликка) қайтсангизлар, биз ҳам (сизларни ҳалок этишга) қайтурмиз. Биз кофирлар учун жаҳаннамни зиндон қилиб қўйганмиз'}, {'chapter': 17, 'verse': 9, 'text': 'إِنَّ هَٰذَا ٱلۡقُرۡءَانَ يَهۡدِي لِلَّتِي هِيَ أَقۡوَمُ وَيُبَشِّرُ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرٗا كَبِيرٗا\nАлбатта, бу Қуръон энг Тўғри Йўлга ҳидоят қилур ва яхши амалларни қиладиган мўминларга катта ажр-мукофот борлигини(нг) хушхабар(ини) берур'}, {'chapter': 17, 'verse': 10, 'text': 'وَأَنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا\nШунингдек, Охиратга ишонмайдиган кимсалар учун аламли азоб тайёрлаб қўйганимизни(нг ҳам «хушхабари»ни берур)'}, {'chapter': 17, 'verse': 11, 'text': 'وَيَدۡعُ ٱلۡإِنسَٰنُ بِٱلشَّرِّ دُعَآءَهُۥ بِٱلۡخَيۡرِۖ وَكَانَ ٱلۡإِنسَٰنُ عَجُولٗا\nИнсон (Аллоҳ таолодан) яхшилик сўраб дуои хайр қилгани каби (гоҳо сиқилган пайтларида) ёмонлик тилаб дуоибад ҳам қилур. Инсон (мана шундай) шошқалоқдир'}, {'chapter': 17, 'verse': 12, 'text': 'وَجَعَلۡنَا ٱلَّيۡلَ وَٱلنَّهَارَ ءَايَتَيۡنِۖ فَمَحَوۡنَآ ءَايَةَ ٱلَّيۡلِ وَجَعَلۡنَآ ءَايَةَ ٱلنَّهَارِ مُبۡصِرَةٗ لِّتَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡ وَلِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ وَكُلَّ شَيۡءٖ فَصَّلۡنَٰهُ تَفۡصِيلٗا\nБиз кеча ва кундузни (ўзимизнинг қудрати илоҳиямизни кўрсатиб турадиган) икки белги — аломат қилиб қўйдик. (Ҳеч нарса) кўринмас қоронғиликни кечанинг аломати қилдик. Кундуз аломатини эса, Парвардигорингиздан фазлу марҳамат (яъни, ризқу рўз) исташларингиз учун ҳамда йиллар саноғини ва ҳисоб-китобни билишларингиз учун ёруғлик қилиб қўйдик. Ва биз барча нарсани (рўзи азалдаёқ) батафсил баён қилиб қўйганмиз (яъни, дунёдаги содир бўладиган бирон иш ёки воқеа-ҳодиса тасодифий бўлмай, балки ҳар бирининг воқеъ бўлиш жойи, вақти ва сабаблари батафсил баён қилиб қўйилгандир)'}, {'chapter': 17, 'verse': 13, 'text': 'وَكُلَّ إِنسَٰنٍ أَلۡزَمۡنَٰهُ طَـٰٓئِرَهُۥ فِي عُنُقِهِۦۖ وَنُخۡرِجُ لَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ كِتَٰبٗا يَلۡقَىٰهُ مَنشُورًا\nБиз ҳар бир инсоннинг амалини бўйнига илиб қўйгандирмиз (яъни, унинг қилган ҳар бир амали икки дунёда унга ажралмас ҳамроҳ бўлур). Биз Қиёмат Куни унга очиқ ҳолда рўбарў бўладиган бир китобни (яъни, номаи аъмолини) чиқариб кўрсатурмиз'}, {'chapter': 17, 'verse': 14, 'text': 'ٱقۡرَأۡ كِتَٰبَكَ كَفَىٰ بِنَفۡسِكَ ٱلۡيَوۡمَ عَلَيۡكَ حَسِيبٗا\n(Ва унга дейилур): «Китобингни (яъни, номаи аъмолингни) ўқи! Бугун ўз нафсинг ўзингга қарши етарли ҳисоб-китоб қилгувчидир»'}, {'chapter': 17, 'verse': 15, 'text': 'مَّنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبۡعَثَ رَسُولٗا\nКим ҳидоят йўлига юрса, бас, ўзи учун юрар. Ким (ҳидоят) йўлидан озса, у ҳам ўз зиёнига озур. Ҳеч бир кўтаргувчи (яъни, гуноҳкор жон) ўзга жоннинг юкини (яъни, гуноҳини) кўтармас. Биз то бирон пайғамбар юбормагунча (у орқали Ўзимизнинг амру фармонларимизни юбориб, унга итоат қилишдан бош тортмагунларича, бирон кимсани) азоблагувчи эмасмиз'}, {'chapter': 17, 'verse': 16, 'text': 'وَإِذَآ أَرَدۡنَآ أَن نُّهۡلِكَ قَرۡيَةً أَمَرۡنَا مُتۡرَفِيهَا فَفَسَقُواْ فِيهَا فَحَقَّ عَلَيۡهَا ٱلۡقَوۡلُ فَدَمَّرۡنَٰهَا تَدۡمِيرٗا\nБиз қачон бирон шаҳарни ҳалок қилишни истасак, унинг боёнларини (Ўзимиз юборган пайғамбарларга итоат этишга) буюриб, улар итоатсизлик қилишгач, у шаҳар (аҳолиси) устига Сўз (азоб тушиши) вожиб — муқаррар бўлур. Бас, биз уни вайронага айлантирурмиз'}, {'chapter': 17, 'verse': 17, 'text': 'وَكَمۡ أَهۡلَكۡنَا مِنَ ٱلۡقُرُونِ مِنۢ بَعۡدِ نُوحٖۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِۦ خَبِيرَۢا بَصِيرٗا\nНуҳ (қавмидан) кейин ҳам қанча асрларни (авлодларни) ҳалок қилдик. (Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингизнинг Ўзи бандаларининг гуноҳларидан етарли огоҳ, кўриб тургувчидир'}, {'chapter': 17, 'verse': 18, 'text': 'مَّن كَانَ يُرِيدُ ٱلۡعَاجِلَةَ عَجَّلۡنَا لَهُۥ فِيهَا مَا نَشَآءُ لِمَن نُّرِيدُ ثُمَّ جَعَلۡنَا لَهُۥ جَهَنَّمَ يَصۡلَىٰهَا مَذۡمُومٗا مَّدۡحُورٗا\nКим нақд (дунё)ни кўзловчи бўлса, Биз (шу дунёда улардан) Ўзимиз истаган кимсалар учун Ўзимиз хоҳлаган нарсани нақд қилиб берурмиз. Сўнгра (яъни, Охиратда) унинг учун ўзи мазаммат ва қувғинга дучор бўлган ҳолда кирадиган жаҳаннамни жой қилиб берурмиз'}, {'chapter': 17, 'verse': 19, 'text': 'وَمَنۡ أَرَادَ ٱلۡأٓخِرَةَ وَسَعَىٰ لَهَا سَعۡيَهَا وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ كَانَ سَعۡيُهُم مَّشۡكُورٗا\nКим Охиратни истаса ва мўмин бўлган ҳолида (Охиратга лойиқ) саъй-ҳаракат қилса, бас, ундай зотларнинг саъйлари (Аллоҳ наздида) мақбул бўлур'}, {'chapter': 17, 'verse': 20, 'text': 'كُلّٗا نُّمِدُّ هَـٰٓؤُلَآءِ وَهَـٰٓؤُلَآءِ مِنۡ عَطَآءِ رَبِّكَۚ وَمَا كَانَ عَطَآءُ رَبِّكَ مَحۡظُورًا\n(Эй Муҳаммад алайҳис-салоту вас-салом, бу дунёда одамларнинг) барчаларига — мана бу (мўмин)ларга ҳам, анави (кофир)ларга ҳам Парвардигорингизнинг неъматидан ато этурмиз. Парвардигорингизнинг неъмати (ҳеч кимдан) манъ қилинмас'}, {'chapter': 17, 'verse': 21, 'text': 'ٱنظُرۡ كَيۡفَ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۚ وَلَلۡأٓخِرَةُ أَكۡبَرُ دَرَجَٰتٖ وَأَكۡبَرُ تَفۡضِيلٗا\n(Бу дунёда) уларнинг бировларидан бировларини (ризқу мартабада) қандай устун қилганимизни кўринг! Шак-шубҳа йўқки, Охират даражалари неча баробар улуғ ва афзалроқдир'}, {'chapter': 17, 'verse': 22, 'text': 'لَّا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَقۡعُدَ مَذۡمُومٗا مَّخۡذُولٗا\nСиз яна Аллоҳ билан бирга ўзга бирон илоҳ ҳам бор, деб мазамматлангувчи ва маҳрум бўлиб ўтирманг'}, {'chapter': 17, 'verse': 23, 'text': '۞وَقَضَىٰ رَبُّكَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنًاۚ إِمَّا يَبۡلُغَنَّ عِندَكَ ٱلۡكِبَرَ أَحَدُهُمَآ أَوۡ كِلَاهُمَا فَلَا تَقُل لَّهُمَآ أُفّٖ وَلَا تَنۡهَرۡهُمَا وَقُل لَّهُمَا قَوۡلٗا كَرِيمٗا\nПарвардигорингиз, ёлғиз Унинг Ўзига ибодат қилишларингизни ҳамда ота-онага яхшилик қилишларингизни амр этди. Агар уларнинг (ота-онангизнинг) бирови ёки ҳар иккиси сенинг қўл остингда кексалик ёшига етсалар, уларга қараб «уф» тортма ва уларнинг (сўзларини) қайтарма! Уларга (доимо) яхши сўз айт'}, {'chapter': 17, 'verse': 24, 'text': 'وَٱخۡفِضۡ لَهُمَا جَنَاحَ ٱلذُّلِّ مِنَ ٱلرَّحۡمَةِ وَقُل رَّبِّ ٱرۡحَمۡهُمَا كَمَا رَبَّيَانِي صَغِيرٗا\nУлар учун, меҳрибонлик билан, хорлик қанотини паст тут — хокисор бўл ва: «Парвардигорим, мени (улар) гўдаклик чоғимдан тарбиялаб-ўстирганларидек, сен ҳам уларга раҳм-шафқат қилгин», деб (ҳақларига дуо қил)'}, {'chapter': 17, 'verse': 25, 'text': 'رَّبُّكُمۡ أَعۡلَمُ بِمَا فِي نُفُوسِكُمۡۚ إِن تَكُونُواْ صَٰلِحِينَ فَإِنَّهُۥ كَانَ لِلۡأَوَّـٰبِينَ غَفُورٗا\n(Эй инсонлар), Парвардигорингиз дилларингиздаги сирларингизни жуда яхши билгувчидир. Агар сизлар яхши бўлсангизлар (яъни, ота-оналарингизнинг ҳуқуқларини таниб, уларга меҳрибон бўлсангизлар. Албатта, Аллоҳ билмасдан қилган гуноҳларингизни мағфират қилур). Зеро, у тавба қилгувчиларни мағфират этувчи бўлган Зотдир'}, {'chapter': 17, 'verse': 26, 'text': 'وَءَاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَلَا تُبَذِّرۡ تَبۡذِيرًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), қавми-қариндошга, мискин ва йўловчига (хайру эҳсон қилиш билан) ҳақларини ато этинг ва исрофгарчиликка йўл қўйманг'}, {'chapter': 17, 'verse': 27, 'text': 'إِنَّ ٱلۡمُبَذِّرِينَ كَانُوٓاْ إِخۡوَٰنَ ٱلشَّيَٰطِينِۖ وَكَانَ ٱلشَّيۡطَٰنُ لِرَبِّهِۦ كَفُورٗا\nЧунки исроф қилгувчилар шайтонларнинг дўстлари бўлган кимсалардир. Шайтон эса Парвардигорига бутунлай кофир бўлгандир'}, {'chapter': 17, 'verse': 28, 'text': 'وَإِمَّا تُعۡرِضَنَّ عَنۡهُمُ ٱبۡتِغَآءَ رَحۡمَةٖ مِّن رَّبِّكَ تَرۡجُوهَا فَقُل لَّهُمۡ قَوۡلٗا مَّيۡسُورٗا\nАгар сиз Парвардигорингиз томонидан бўладиган марҳаматдан умидвор ҳолингизда (ҳозир қўлингизда бўлмагани сабабли хайр-эҳсон қилиш имконини топмай), улардан (яъни, қавм-қариндош, мискин ва йўловчилардан) юз ўгирадиган бўлсангиз, у ҳолда уларга юмшоқ сўз айтинг'}, {'chapter': 17, 'verse': 29, 'text': 'وَلَا تَجۡعَلۡ يَدَكَ مَغۡلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبۡسُطۡهَا كُلَّ ٱلۡبَسۡطِ فَتَقۡعُدَ مَلُومٗا مَّحۡسُورًا\n(Бахиллик билан) қўлингизни бўйнингизга боғлаб ҳам олманг. Исрофгарчилик қилиш билан уни бутунлай ёзиб ҳам юборманг. Акс ҳолда, маломат ва надоматда қолурсиз'}, {'chapter': 17, 'verse': 30, 'text': 'إِنَّ رَبَّكَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا\nШубҳасиз, Парвардигорингиз Ўзи хоҳлаган кишиларнинг ризқини кенг қилур ва (Ўзи хоҳлаган кишиларнинг ризқини) танг қилур. Албатта, у бандаларидан огоҳ ва (уларни) кўриб тургувчи бўлган Зотдир'}, {'chapter': 17, 'verse': 31, 'text': 'وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُمۡ خَشۡيَةَ إِمۡلَٰقٖۖ نَّحۡنُ نَرۡزُقُهُمۡ وَإِيَّاكُمۡۚ إِنَّ قَتۡلَهُمۡ كَانَ خِطۡـٔٗا كَبِيرٗا\n(Эй инсонлар), болаларингизни йўқчилиқдан қўрқиб ўлдирмангизлар — уларга ҳам, сизларга ҳам Биз ўзимиз ризқ берурмиз. Уларни ўлдирмоқ, шак-шубҳасиз, катта хатодир'}, {'chapter': 17, 'verse': 32, 'text': 'وَلَا تَقۡرَبُواْ ٱلزِّنَىٰٓۖ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَسَآءَ سَبِيلٗا\nЗинога яқинлашманглар! Чунки (бу) бузуқликдир — энг ёмон йўлдир'}, {'chapter': 17, 'verse': 33, 'text': 'وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۗ وَمَن قُتِلَ مَظۡلُومٗا فَقَدۡ جَعَلۡنَا لِوَلِيِّهِۦ سُلۡطَٰنٗا فَلَا يُسۡرِف فِّي ٱلۡقَتۡلِۖ إِنَّهُۥ كَانَ مَنصُورٗا\nАллоҳ (ўлдиришни) ҳаром қилган жонни (яъни, қатл этилишга мустаҳиқ бўлмаган жонни ноҳақ ўлдирмангиз, магар ҳақ шаръий қонун) билангина ўлдиришларингиз мумкиндир. Кимки мазлум бўлган ҳолида (яъни, ноҳақ) ўлдирилса, биз унинг эгаси — вориси учун (қасос олишга) салтанат-ҳуқуқ бергандирмиз. Бас, у (қотилни) ўлдиришда ҳаддан ошмасин (яъни, қотилнинг ўрнига бошқа бировни ўлдирмасин, ё бир кишининг муқобилига икки кишини ўлдирмасин, ёки қотилни ўлдиришда унинг қулоқ-бурнини кесмасин)! Зеро, у (Аллоҳ томонидан қотилнинг устига) ғолиб қилинган зотдир'}, {'chapter': 17, 'verse': 34, 'text': 'وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ بِٱلۡعَهۡدِۖ إِنَّ ٱلۡعَهۡدَ كَانَ مَسۡـُٔولٗا\nЕтимнинг молига то у балоғат ёшига етгунича яқинлашманглар, магар энг чиройли йўсинда (унга бирон зиён етказмасдан яқинлашинглар — тасарруф қилинглар). Аҳдга вафо қилинглар. Зеро, аҳд-паймон (Қиёмат Куни) масъул бўлинадиган ишдир'}, {'chapter': 17, 'verse': 35, 'text': 'وَأَوۡفُواْ ٱلۡكَيۡلَ إِذَا كِلۡتُمۡ وَزِنُواْ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِيمِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلٗا\n(Ўрталарингиздаги олди-берди, савдо-сотиқдан бирон нарсани) ўлчаган вақтларингизда ўлчовни тўла-тўкис қилинглар ва тўғри тарози билан тортинглар! Мана шу энг яхши ва чиройли ечим — ҳукмдир'}, {'chapter': 17, 'verse': 36, 'text': 'وَلَا تَقۡفُ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۚ إِنَّ ٱلسَّمۡعَ وَٱلۡبَصَرَ وَٱلۡفُؤَادَ كُلُّ أُوْلَـٰٓئِكَ كَانَ عَنۡهُ مَسۡـُٔولٗا\n(Эй инсон), ўзинг аниқ билмаган нарсага эргашма! Чунки қулоқ, кўз, дил — буларнинг барчаси тўғрисида (ҳар бир инсон) масъул бўлур (яъни, эшитган, кўрган ишонган ҳар бир нарсаси учун киши Қиёмат Кунида жавоб беради)'}, {'chapter': 17, 'verse': 37, 'text': 'وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّكَ لَن تَخۡرِقَ ٱلۡأَرۡضَ وَلَن تَبۡلُغَ ٱلۡجِبَالَ طُولٗا\nЕр юзида кибр-ҳаво билан юрмагин! Чунки сен (оёқларинг билан) ҳаргиз ерни тешиб (унинг тубига) кетолмайсан ва бўйи бастда тоғларга етолмайсан. (Бас, бу қадар ожиз экансан, нечун кибру ҳаво қилурсан)'}, {'chapter': 17, 'verse': 38, 'text': 'كُلُّ ذَٰلِكَ كَانَ سَيِّئُهُۥ عِندَ رَبِّكَ مَكۡرُوهٗا\nБуларнинг (яъни, юқоридаги оятларда манъ этилган нарсаларнинг) барчаси Парвардигорингиз наздида макруҳ бўлган гуноҳлардир'}, {'chapter': 17, 'verse': 39, 'text': 'ذَٰلِكَ مِمَّآ أَوۡحَىٰٓ إِلَيۡكَ رَبُّكَ مِنَ ٱلۡحِكۡمَةِۗ وَلَا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتُلۡقَىٰ فِي جَهَنَّمَ مَلُومٗا مَّدۡحُورًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), бу оятлар Парвардигорингиз сизга ваҳий қилган ҳикматлардан айримларидир. Сиз Аллоҳ билан бирга яна бошқа бирон илоҳ бор деб, маломатланган ва (Аллоҳ раҳматидан) қувилган ҳолда жаҳаннамга ташланманг'}, {'chapter': 17, 'verse': 40, 'text': 'أَفَأَصۡفَىٰكُمۡ رَبُّكُم بِٱلۡبَنِينَ وَٱتَّخَذَ مِنَ ٱلۡمَلَـٰٓئِكَةِ إِنَٰثًاۚ إِنَّكُمۡ لَتَقُولُونَ قَوۡلًا عَظِيمٗا\n(Эй мушриклар), Парвардигорингиз сизларга ўғилларни танлаб бериб, Ўзига фаришталарни қиз қилиб олдими?! Дарҳақиқат, сизлар оғир сўзни — катта гуноҳни сўзламоқдасиз'}, {'chapter': 17, 'verse': 41, 'text': 'وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرۡءَانِ لِيَذَّكَّرُواْ وَمَا يَزِيدُهُمۡ إِلَّا نُفُورٗا\nАлбатта, Биз бу Қуръонда улар эслатма (ибрат) олишлари учун (турли масал ва ҳикматларни) баён қилдик. (Лекин бу эслатмалар) уларни янада (ҳақдан) йироқлаштирур'}, {'chapter': 17, 'verse': 42, 'text': 'قُل لَّوۡ كَانَ مَعَهُۥٓ ءَالِهَةٞ كَمَا يَقُولُونَ إِذٗا لَّٱبۡتَغَوۡاْ إِلَىٰ ذِي ٱلۡعَرۡشِ سَبِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар улар айтганларидек Аллоҳ билан бирга (бошқа) илоҳлар ҳам бўлса эди, у ҳолда ўша (илоҳлар) Арш Соҳибига (яъни, Аллоҳга) қарши йўл излаган бўлур эдилар»'}, {'chapter': 17, 'verse': 43, 'text': 'سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَقُولُونَ عُلُوّٗا كَبِيرٗا\n(Аллоҳ) улар айтган нарсадан пок ва ниҳоят даражада юксакдир'}, {'chapter': 17, 'verse': 44, 'text': 'تُسَبِّحُ لَهُ ٱلسَّمَٰوَٰتُ ٱلسَّبۡعُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ وَإِن مِّن شَيۡءٍ إِلَّا يُسَبِّحُ بِحَمۡدِهِۦ وَلَٰكِن لَّا تَفۡقَهُونَ تَسۡبِيحَهُمۡۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا\nЕтти осмон, Ер ва улардаги бор жонзот (Аллоҳни) поклар. Мавжуд бўлган барча нарса ҳамду сано айтиш билан У зотни поклар. Лекин сизлар (эй инсонлар), уларнинг тасбеҳ айтишларини — поклашларини англамассизлар. Дарҳақиқат, У ҳалим ва мағфиратли бўлган Зотдир'}, {'chapter': 17, 'verse': 45, 'text': 'وَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ جَعَلۡنَا بَيۡنَكَ وَبَيۡنَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ حِجَابٗا مَّسۡتُورٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), қачон сиз Қуръон қироат қилганингизда, Биз сиз билан Охиратга ишонмайдиган кимсалар ўртасида кўринмас парда қилиб (тортиб) қўюрмиз (бас, улар Қуръон маъноларини англай олмаслар)'}, {'chapter': 17, 'verse': 46, 'text': 'وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِذَا ذَكَرۡتَ رَبَّكَ فِي ٱلۡقُرۡءَانِ وَحۡدَهُۥ وَلَّوۡاْ عَلَىٰٓ أَدۡبَٰرِهِمۡ نُفُورٗا\nШунингдек, (Қуръонни) англай олмасинлар учун уларнинг дилларида тўсиқлар ва қулоқларида оғирлик — карлик пайдо қилурмиз. Қачон сиз Қуръонда ёлғиз Парвардигорингизни зикр қилсангиз, (мушриклар) орқаларига бурилиб қочурлар'}, {'chapter': 17, 'verse': 47, 'text': 'نَّحۡنُ أَعۡلَمُ بِمَا يَسۡتَمِعُونَ بِهِۦٓ إِذۡ يَسۡتَمِعُونَ إِلَيۡكَ وَإِذۡ هُمۡ نَجۡوَىٰٓ إِذۡ يَقُولُ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا\nБиз уларнинг сизга (яъни, сизнинг қироатингизга) қулоқ тутаётган пайтларида нима сабабдан қулоқ тутаётганларини ва ўзаро шивирлашиб, золим кимсалар (мўминларга): «Аниқки сизлар сеҳрланган, ақлдан озган кишига эргашмоқдасизлар», деяётганларини жуда яхши билурмиз'}, {'chapter': 17, 'verse': 48, 'text': 'ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَالَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), улар сиз ҳақингизда (сеҳрланган, мажнун, шоир деб) қандай мисоллар келтирганларини, бас, йўлдан озиб (ҳақ) йўлни топа олмаётганларини кўринг'}, {'chapter': 17, 'verse': 49, 'text': 'وَقَالُوٓاْ أَءِذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدٗا\nУлар: «Суяк ва (чирик) мурдаларга айланиб кетгач, яна янгитдан яралиб тирилурмизми?!», дедилар'}, {'chapter': 17, 'verse': 50, 'text': '۞قُلۡ كُونُواْ حِجَارَةً أَوۡ حَدِيدًا\nАйтинг: «Сизлар хоҳ тош ё темир бўлинглар'}, {'chapter': 17, 'verse': 51, 'text': 'أَوۡ خَلۡقٗا مِّمَّا يَكۡبُرُ فِي صُدُورِكُمۡۚ فَسَيَقُولُونَ مَن يُعِيدُنَاۖ قُلِ ٱلَّذِي فَطَرَكُمۡ أَوَّلَ مَرَّةٖۚ فَسَيُنۡغِضُونَ إِلَيۡكَ رُءُوسَهُمۡ وَيَقُولُونَ مَتَىٰ هُوَۖ قُلۡ عَسَىٰٓ أَن يَكُونَ قَرِيبٗا\nЁки кўнгилларингизда (ҳаётлигини тасаввур қилиши) жуда қийин бўлган бошқа бирон махлуқ бўлинглар, (ҳар ҳолда Аллоҳ сизларни тирилтирур)». Ҳали улар: «Бизларни ким қайта тирилтира олур?», дейдилар. «Сизларни илк бор яратган зот», деб айтинг! Ҳали улар: «У (кун) қачон бўлур?», деб (сўрайдилар). «Шоядки яқин бўлса», деб айтинг'}, {'chapter': 17, 'verse': 52, 'text': 'يَوۡمَ يَدۡعُوكُمۡ فَتَسۡتَجِيبُونَ بِحَمۡدِهِۦ وَتَظُنُّونَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗا\nУ зот сизларни чорлайдиган кунда Унга ҳамду сано билан жавоб берурсизлар ва ўзларингизнинг (дунёда) жуда оз (муддат) турганларингизни ўйлаб қолурсизлар'}, {'chapter': 17, 'verse': 53, 'text': 'وَقُل لِّعِبَادِي يَقُولُواْ ٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ ٱلشَّيۡطَٰنَ يَنزَغُ بَيۡنَهُمۡۚ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلۡإِنسَٰنِ عَدُوّٗا مُّبِينٗا\nБандаларимга айтинг-ки, улар (ўзаро сўзлашганларида) энг гўзал сўзлардан сўзласинлар. Зеро, шайтон уларнинг ўрталарида бузғунчилик қилур. Дарҳақиқат, шайтон инсонга очиқ душмандир'}, {'chapter': 17, 'verse': 54, 'text': 'رَّبُّكُمۡ أَعۡلَمُ بِكُمۡۖ إِن يَشَأۡ يَرۡحَمۡكُمۡ أَوۡ إِن يَشَأۡ يُعَذِّبۡكُمۡۚ وَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ وَكِيلٗا\n(Эй инсонлар), Парвардигорингиз сизларни жуда яхши билур. Хоҳласа сизларга раҳм-шафқат қилур, хоҳласа сизларга азоб берур. (Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизни (одамлар) устига қўриқчи қилиб юборган эмасмиз (яъни, сизнинг вазифангиз фақат Бизнинг амру фармонларимизни уларга етказмоқдир)'}, {'chapter': 17, 'verse': 55, 'text': 'وَرَبُّكَ أَعۡلَمُ بِمَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَلَقَدۡ فَضَّلۡنَا بَعۡضَ ٱلنَّبِيِّـۧنَ عَلَىٰ بَعۡضٖۖ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا\nПарвардигорингиз осмонлар ва Ердаги бор жонзотни жуда яхши билур. Ҳақиқатан, Биз айрим пайғамбарларни айримларидан устун қилдик. Биз Довудга Забурни ато этдик'}, {'chapter': 17, 'verse': 56, 'text': 'قُلِ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُم مِّن دُونِهِۦ فَلَا يَمۡلِكُونَ كَشۡفَ ٱلضُّرِّ عَنكُمۡ وَلَا تَحۡوِيلًا\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «(Аллоҳдан) ўзга (илоҳ) деб гумон қилган кимсаларингизни чорланглар! Улар сизлардан бирон зиённи арита олмаслар ва (уни сизлардан бошқаларга) буриб ҳам юбора олмаслар'}, {'chapter': 17, 'verse': 57, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ يَدۡعُونَ يَبۡتَغُونَ إِلَىٰ رَبِّهِمُ ٱلۡوَسِيلَةَ أَيُّهُمۡ أَقۡرَبُ وَيَرۡجُونَ رَحۡمَتَهُۥ وَيَخَافُونَ عَذَابَهُۥٓۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحۡذُورٗا\nУлар (илоҳ деб) илтижо қиладиган ўша кимсаларнинг ўзлари ҳам Парвардигорга қай бирлари яқинроқ бўлиш учун йўл изларлар. У зотнинг раҳмат-марҳаматидан умидвор бўлурлар, азобидан қўрқурлар. Парвардигорингизнинг азоби қўрқинчлидир'}, {'chapter': 17, 'verse': 58, 'text': 'وَإِن مِّن قَرۡيَةٍ إِلَّا نَحۡنُ مُهۡلِكُوهَا قَبۡلَ يَوۡمِ ٱلۡقِيَٰمَةِ أَوۡ مُعَذِّبُوهَا عَذَابٗا شَدِيدٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا\n(Ер юзидаги) бор қишлоқ-шаҳарнинг (барчасини) Биз Қиёмат Куни (келиши)дан илгари ҳалок қилгувчидирмиз ёки қаттиқ азоб билан азоблагувчидирмиз. Ушбу ҳукм Китобда тақдири азалда битилгандир'}, {'chapter': 17, 'verse': 59, 'text': 'وَمَا مَنَعَنَآ أَن نُّرۡسِلَ بِٱلۡأٓيَٰتِ إِلَّآ أَن كَذَّبَ بِهَا ٱلۡأَوَّلُونَۚ وَءَاتَيۡنَا ثَمُودَ ٱلنَّاقَةَ مُبۡصِرَةٗ فَظَلَمُواْ بِهَاۚ وَمَا نُرۡسِلُ بِٱلۡأٓيَٰتِ إِلَّا تَخۡوِيفٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушриклар сиздан талаб қилган) оят-мўъжизаларни юборишдан Бизни фақат аввалги умматлар у (мўъжизаларни) ёлғон деганларигина қайтарди. Самуд қабиласига очиқ (мўъжиза бўлган) туяни ато этганимизда уни инкор этдилар (ва оқибатда қирилиб кетдилар). Биз мўъжизаларни фақат (бандаларимизни ҳалокатдан) қўрқитиш учунгина юборурмиз'}, {'chapter': 17, 'verse': 60, 'text': 'وَإِذۡ قُلۡنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِٱلنَّاسِۚ وَمَا جَعَلۡنَا ٱلرُّءۡيَا ٱلَّتِيٓ أَرَيۡنَٰكَ إِلَّا فِتۡنَةٗ لِّلنَّاسِ وَٱلشَّجَرَةَ ٱلۡمَلۡعُونَةَ فِي ٱلۡقُرۡءَانِۚ وَنُخَوِّفُهُمۡ فَمَا يَزِيدُهُمۡ إِلَّا طُغۡيَٰنٗا كَبِيرٗا\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), Биз сизга Парвардигорингиз (барча) одамларни ўраб-иҳота қилиб олгандир, деган эдик. Биз сизга (Меърож кечасида кўрсатган) «Туш»ни ва Қуръонда лаънатланган (Заққум) дарахтини у одамлар учун бир фитна-имтиҳон қилдик, холос. Биз (мушрикларни турли мўъжизалар билан) қўрқитурмиз, аммо бу уларни баттар туғёнга солур'}, {'chapter': 17, 'verse': 61, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ قَالَ ءَأَسۡجُدُ لِمَنۡ خَلَقۡتَ طِينٗا\nЭсланг, Биз фаришталарга Одамга сажда қилинг, дейишимиз билан саждага эгилдилар. Фақат иблис (кибру ҳаво билан): «Сен лойдан яратган кимсага сажда қилурманми?», деди'}, {'chapter': 17, 'verse': 62, 'text': 'قَالَ أَرَءَيۡتَكَ هَٰذَا ٱلَّذِي كَرَّمۡتَ عَلَيَّ لَئِنۡ أَخَّرۡتَنِ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَأَحۡتَنِكَنَّ ذُرِّيَّتَهُۥٓ إِلَّا قَلِيلٗا\nУ (яна шундай) деди: «Менга хабар бергин-чи, мана шу кимсани мендан улуғ-устун қилдингми? Қасамки, агар Сен мени Қиёмат Кунигача (тирик) қолдирсанг, албатта, мен унинг зурриётини қириб юборурман, (яъни, ҳақ йўлдан оздириб, ҳалокат йўлларига буриб юборурман), магар озгиналаригина (ҳақ йўлда) қолурлар»'}, {'chapter': 17, 'verse': 63, 'text': 'قَالَ ٱذۡهَبۡ فَمَن تَبِعَكَ مِنۡهُمۡ فَإِنَّ جَهَنَّمَ جَزَآؤُكُمۡ جَزَآءٗ مَّوۡفُورٗا\n(Аллоҳ) айтди: «Бор, (сенга Қиёматгача умр бердим). Бас улардан ким сенга эргашса, у ҳолда, шак-шубҳасиз, жаҳаннам сизларга етарли жазо бўлур'}, {'chapter': 17, 'verse': 64, 'text': 'وَٱسۡتَفۡزِزۡ مَنِ ٱسۡتَطَعۡتَ مِنۡهُم بِصَوۡتِكَ وَأَجۡلِبۡ عَلَيۡهِم بِخَيۡلِكَ وَرَجِلِكَ وَشَارِكۡهُمۡ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِ وَعِدۡهُمۡۚ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا\nУлардан кучинг етган кимсани овозинг билан қўзғат, уларнинг устига отлиқ ва пиёда (лашкарингни) торт, топган мол-давлат ва бола-чақаларида уларга шерик бўл (яъни, уларни ҳаромдан мол-дунё ва бола-чақа орттиришга унда), уларга (ёлғон) ваъдалар қил». Дарҳақиқат, шайтон уларга фақат алдов-ёлғон нарсаларнигина ваъда қилур'}, {'chapter': 17, 'verse': 65, 'text': 'إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٞۚ وَكَفَىٰ بِرَبِّكَ وَكِيلٗا\n«Менинг (иймон-эътиқодли) бандаларим устига эса сен учун ҳеч қандай салтанат-ҳукмронлик бўлмас. Парвардигорингнинг Ўзи етарли қўриқловчидир»'}, {'chapter': 17, 'verse': 66, 'text': 'رَّبُّكُمُ ٱلَّذِي يُزۡجِي لَكُمُ ٱلۡفُلۡكَ فِي ٱلۡبَحۡرِ لِتَبۡتَغُواْ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ كَانَ بِكُمۡ رَحِيمٗا\n(Эй инсонлар), Парвардигорингиз Унинг фазлу марҳаматидан (ризқу рўз) изланглар, деб сизлар учун денгизда кемаларни юргизиб қўйган Зотдир. Дарҳақиқат, У сизларга меҳрибондир'}, {'chapter': 17, 'verse': 67, 'text': 'وَإِذَا مَسَّكُمُ ٱلضُّرُّ فِي ٱلۡبَحۡرِ ضَلَّ مَن تَدۡعُونَ إِلَّآ إِيَّاهُۖ فَلَمَّا نَجَّىٰكُمۡ إِلَى ٱلۡبَرِّ أَعۡرَضۡتُمۡۚ وَكَانَ ٱلۡإِنسَٰنُ كَفُورًا\nҚачон денгизда сизларга бирон мусибат етса, сизлар илтижо қиладиган бутлар ғойиб бўлур, фақат Унинг Ўзигина қолур. Энди қачонқи, У зот сизларга нажот бериб қуруқликка чиқариб қўйгач, сизлар Ундан юз ўгирурсиз. Дарҳақиқат, инсон куфрони неъмат қилгувчидир'}, {'chapter': 17, 'verse': 68, 'text': 'أَفَأَمِنتُمۡ أَن يَخۡسِفَ بِكُمۡ جَانِبَ ٱلۡبَرِّ أَوۡ يُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗا ثُمَّ لَا تَجِدُواْ لَكُمۡ وَكِيلًا\nЁ (Аллоҳ), сизларни қуруқлик томонида ҳам (ерга) юттириб юборишидан, ёхуд устингиздан тош ёғдиришидан, сўнгра ўзларингиз учун бирон вакил — сақловчи тополмай қолиш (ғами)дан хотиржаммисиз'}, {'chapter': 17, 'verse': 69, 'text': 'أَمۡ أَمِنتُمۡ أَن يُعِيدَكُمۡ فِيهِ تَارَةً أُخۡرَىٰ فَيُرۡسِلَ عَلَيۡكُمۡ قَاصِفٗا مِّنَ ٱلرِّيحِ فَيُغۡرِقَكُم بِمَا كَفَرۡتُمۡ ثُمَّ لَا تَجِدُواْ لَكُمۡ عَلَيۡنَا بِهِۦ تَبِيعٗا\nЁки У зот сизларни яна қайтадан (денгизга) қайтариб, устингизга қаттиқ шамол юборишидан, бас, кофир бўлганларингиз сабабли ғарқ қилишидан, сўнгра ўзларингиз учун Биздан ўч олиб бергувчи бировни топа олмай қолиш ғамидан хотиржаммисиз'}, {'chapter': 17, 'verse': 70, 'text': '۞وَلَقَدۡ كَرَّمۡنَا بَنِيٓ ءَادَمَ وَحَمَلۡنَٰهُمۡ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَىٰ كَثِيرٖ مِّمَّنۡ خَلَقۡنَا تَفۡضِيلٗا\nДарҳақиқат, Биз Одам болаларини азиз-мукаррам қилдик ва уларни барру-баҳрда — қуруқлик ва денгизда (от-улов ва кемаларга) чиқариб қўйдик ҳамда уларга ҳалол-пок нарсалардан ризқу рўз бердик ва уларни Ўзимиз яратган жуда кўп жонзотлардан афзал-устун қилиб қўйдик'}, {'chapter': 17, 'verse': 71, 'text': 'يَوۡمَ نَدۡعُواْ كُلَّ أُنَاسِۭ بِإِمَٰمِهِمۡۖ فَمَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَأُوْلَـٰٓئِكَ يَقۡرَءُونَ كِتَٰبَهُمۡ وَلَا يُظۡلَمُونَ فَتِيلٗا\nБиз ҳар бир одамни ўз номаи аъмоли билан чорлайдиган (Қиёмат) кунини эслангиз! Бас, кимгаки ўз китоби — номаи аъмоли ўнг қўлидан берилса, ана ўша кишилар ўзларига қилчалик зулм қилинмаган ҳолида, номаи аъмолларини ўқурлар (яъни, унда бу дунёда қилиб ўтган барча яхши амалларининг савоби битилган бўлур)'}, {'chapter': 17, 'verse': 72, 'text': 'وَمَن كَانَ فِي هَٰذِهِۦٓ أَعۡمَىٰ فَهُوَ فِي ٱلۡأٓخِرَةِ أَعۡمَىٰ وَأَضَلُّ سَبِيلٗا\nКимки, бу дунёда кўр-гумроҳ экан, бас, у Охиратда ҳам кўр ва бутунлай йўлдан озгувчидир'}, {'chapter': 17, 'verse': 73, 'text': 'وَإِن كَادُواْ لَيَفۡتِنُونَكَ عَنِ ٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ لِتَفۡتَرِيَ عَلَيۡنَا غَيۡرَهُۥۖ وَإِذٗا لَّٱتَّخَذُوكَ خَلِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушриклар) сизни Бизнинг шаънимизга, (ваҳийдан) ўзга нарсаларни тўқиб чиқаришингиз учун — Биз сизга ваҳий қилган оятлардан алдаб-буриб юборишларига оз қолди. У ҳолда, (яъни, уларнинг йўриқларига юрсангиз), сизни дўст қилиб олган бўлур эдилар'}, {'chapter': 17, 'verse': 74, 'text': 'وَلَوۡلَآ أَن ثَبَّتۡنَٰكَ لَقَدۡ كِدتَّ تَرۡكَنُ إِلَيۡهِمۡ شَيۡـٔٗا قَلِيلًا\nАгар Биз сизни (ҳақ йўлда) қилмаганимизда, сиз уларга бир қадар мойил бўлишингизга оз қолди'}, {'chapter': 17, 'verse': 75, 'text': 'إِذٗا لَّأَذَقۡنَٰكَ ضِعۡفَ ٱلۡحَيَوٰةِ وَضِعۡفَ ٱلۡمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيۡنَا نَصِيرٗا\nУ ҳолда, албатта, Биз сизга дунё (азобини) ҳам, Охират (азобини) ҳам икки ҳисса қилиб тотдирган бўлур эдик, сўнгра ўзингиз учун Бизга қарши бирон мададкор топа олмаган бўлур эдингиз'}, {'chapter': 17, 'verse': 76, 'text': 'وَإِن كَادُواْ لَيَسۡتَفِزُّونَكَ مِنَ ٱلۡأَرۡضِ لِيُخۡرِجُوكَ مِنۡهَاۖ وَإِذٗا لَّا يَلۡبَثُونَ خِلَٰفَكَ إِلَّا قَلِيلٗا\nУлар сизни бу tрдан (Маккадан) чиқариб юбориш учун (турли макр-ҳийлалар билан) қўзғатиб юборишларига оз қолди. У ҳолда, уларнинг ўзлари ҳам сиздан кейин озгина (муддат) тура оладилар, холос (сўнгра, албатта ҳалок қилинурлар)'}, {'chapter': 17, 'verse': 77, 'text': 'سُنَّةَ مَن قَدۡ أَرۡسَلۡنَا قَبۡلَكَ مِن رُّسُلِنَاۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحۡوِيلًا\n(Ўз пайғамбарларини қувиб чиқарган қавмнинг ҳалок қилиниши) сиздан илгари юборган пайғамбарларимиздан қолган йўл-қонундир. Сиз ҳаргиз Бизнинг қонунимиз ўзгарганини кўрмайсиз'}, {'chapter': 17, 'verse': 78, 'text': 'أَقِمِ ٱلصَّلَوٰةَ لِدُلُوكِ ٱلشَّمۡسِ إِلَىٰ غَسَقِ ٱلَّيۡلِ وَقُرۡءَانَ ٱلۡفَجۡرِۖ إِنَّ قُرۡءَانَ ٱلۡفَجۡرِ كَانَ مَشۡهُودٗا\nҚуёш оғишидан то тун қоронғусигача намозни тўкис адо қилинг ва тонгги ўқишни (бомдод намозини) ҳам (тўкис адо қилинг). Зеро тонгги ўқиш (кеча ва кундуз фаришталари) ҳозир бўладиган намоздир'}, {'chapter': 17, 'verse': 79, 'text': 'وَمِنَ ٱلَّيۡلِ فَتَهَجَّدۡ بِهِۦ نَافِلَةٗ لَّكَ عَسَىٰٓ أَن يَبۡعَثَكَ رَبُّكَ مَقَامٗا مَّحۡمُودٗا\nКечанинг (бир қисмида) уйғониб ўзингиз учун нафл (ибодат) бўлган намозни ўқинг! Шоядки, Парвардигорингиз сизни (Қиёмат Кунида Буюк Шафоатни — ҳисоб-китобни бошлашини Аллоҳдан сўраб берувчи) мақтовли мақомда тирилтирур'}, {'chapter': 17, 'verse': 80, 'text': 'وَقُل رَّبِّ أَدۡخِلۡنِي مُدۡخَلَ صِدۡقٖ وَأَخۡرِجۡنِي مُخۡرَجَ صِدۡقٖ وَٱجۡعَل لِّي مِن لَّدُنكَ سُلۡطَٰنٗا نَّصِيرٗا\nВа айтинг: «Парвардигорим, мени (қабримга) ҳаққи-рост (яъни, гуноҳлардан пок бўлган ҳолимда) дохил қилгин ва (Қиёмат Куни тирилтирганингда ҳам) ҳаққи-рост (ўнг томоним билан) чиқаргин, ҳамда мен учун ўз даргоҳингдан бир мададкор қувват ато қилгин»'}, {'chapter': 17, 'verse': 81, 'text': 'وَقُلۡ جَآءَ ٱلۡحَقُّ وَزَهَقَ ٱلۡبَٰطِلُۚ إِنَّ ٱلۡبَٰطِلَ كَانَ زَهُوقٗا\nЯна айтинг: «Ҳақиқат (яъни, Ислом) келди ва ботил (яъни, куфр) ўчиб-йўқолди. Чунки ботил йўқолгувчи нарсадир»'}, {'chapter': 17, 'verse': 82, 'text': 'وَنُنَزِّلُ مِنَ ٱلۡقُرۡءَانِ مَا هُوَ شِفَآءٞ وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ وَلَا يَزِيدُ ٱلظَّـٰلِمِينَ إِلَّا خَسَارٗا\nБиз мўминлар учун шифо ва раҳмат бўлган Қуръон оятларини нозил қилурмиз. (Лекин бу оятлар) золим-кофир кимсаларга фақат зиённи зиёда қилур'}, {'chapter': 17, 'verse': 83, 'text': 'وَإِذَآ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ كَانَ يَـُٔوسٗا\nҚачон Биз инсонга (тинчлик, саломатлик, тўкинликни) инъом этсак, у (шукр қилишдан) юз ўгириб, четга бурилиб кетур. Қачон унга (хасталик, камбағаллик каби) бирор ёмонлик етса, ноумид бўлур'}, {'chapter': 17, 'verse': 84, 'text': 'قُلۡ كُلّٞ يَعۡمَلُ عَلَىٰ شَاكِلَتِهِۦ فَرَبُّكُمۡ أَعۡلَمُ بِمَنۡ هُوَ أَهۡدَىٰ سَبِيلٗا\nАйтинг: «Ҳар ким ўз ҳолича амал қилур. Парвардигорингиз Тўғри Йўлдаги зотларни жуда яхши билур (ва ҳар кимга қилган иш-амалига мувофиқ жазо-мукофот берур»)'}, {'chapter': 17, 'verse': 85, 'text': 'وَيَسۡـَٔلُونَكَ عَنِ ٱلرُّوحِۖ قُلِ ٱلرُّوحُ مِنۡ أَمۡرِ رَبِّي وَمَآ أُوتِيتُم مِّنَ ٱلۡعِلۡمِ إِلَّا قَلِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиздан руҳ-жон ҳақида сўрайдилар. Айтинг: «Руҳ ёлғиз Парвардигорим биладиган ишлардандир». Сизларга жуда оз илм берилгандир'}, {'chapter': 17, 'verse': 86, 'text': 'وَلَئِن شِئۡنَا لَنَذۡهَبَنَّ بِٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ ثُمَّ لَا تَجِدُ لَكَ بِهِۦ عَلَيۡنَا وَكِيلًا\nҚасамки, агар хоҳласак, сўзсиз, сизга ваҳий қилган нарса — Қуръонни (сиздан) кетказурмиз, сўнгра ўзингиз учун (яъни, Биздан Қуръонни қайтариб олиш учун) Бизга қарши бирон вакил-ҳомий топа олмассиз'}, {'chapter': 17, 'verse': 87, 'text': 'إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّ فَضۡلَهُۥ كَانَ عَلَيۡكَ كَبِيرٗا\nИлло, Парвардигорингизнинг марҳамати билангина (Қуръон неъмати қўлингизда, дилингизда боқийдир). Дарҳақиқат, У зотнинг сизга бўлган фазлу марҳамати каттадир'}, {'chapter': 17, 'verse': 88, 'text': 'قُل لَّئِنِ ٱجۡتَمَعَتِ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَىٰٓ أَن يَأۡتُواْ بِمِثۡلِ هَٰذَا ٱلۡقُرۡءَانِ لَا يَأۡتُونَ بِمِثۡلِهِۦ وَلَوۡ كَانَ بَعۡضُهُمۡ لِبَعۡضٖ ظَهِيرٗا\nАйтинг: «Қасамки, агар бор инсу жин мана шу Қуръонни ўхшашини келтириш йўлида бирлашиб, бир-бирларига ёрдамчи бўлсалар-да, унинг ўхшашини келтира олмаслар»'}, {'chapter': 17, 'verse': 89, 'text': 'وَلَقَدۡ صَرَّفۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا\nАниқки, Биз бу Қуръонда одамлар учун барча мисол-ибратлардан баён қилдик. Лекин одамларнинг кўплари фақат куфрнигина ихтиёр этдилар'}, {'chapter': 17, 'verse': 90, 'text': 'وَقَالُواْ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ تَفۡجُرَ لَنَا مِنَ ٱلۡأَرۡضِ يَنۢبُوعًا\nВа дедилар: «Токи бизлар учун мана шу ерни ёриб, бир чашма чиқариб бермасанг, биз сенга ҳаргиз иймон келтирмаймиз'}, {'chapter': 17, 'verse': 91, 'text': 'أَوۡ تَكُونَ لَكَ جَنَّةٞ مِّن نَّخِيلٖ وَعِنَبٖ فَتُفَجِّرَ ٱلۡأَنۡهَٰرَ خِلَٰلَهَا تَفۡجِيرًا\nЁ сенинг хурмозор, узумзор боғинг бўлиб, унинг ўртасидан ёриб дарёлар пайдо қилмасанг'}, {'chapter': 17, 'verse': 92, 'text': 'أَوۡ تُسۡقِطَ ٱلسَّمَآءَ كَمَا زَعَمۡتَ عَلَيۡنَا كِسَفًا أَوۡ تَأۡتِيَ بِٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ قَبِيلًا\nЁки, ўзинг даъво қилганингдек, осмонни устимизга парча-парча қилиб туширмагунингча ёки Аллоҳ ва фаришталарини олдимизга келтирмагунингча'}, {'chapter': 17, 'verse': 93, 'text': 'أَوۡ يَكُونَ لَكَ بَيۡتٞ مِّن زُخۡرُفٍ أَوۡ تَرۡقَىٰ فِي ٱلسَّمَآءِ وَلَن نُّؤۡمِنَ لِرُقِيِّكَ حَتَّىٰ تُنَزِّلَ عَلَيۡنَا كِتَٰبٗا نَّقۡرَؤُهُۥۗ قُلۡ سُبۡحَانَ رَبِّي هَلۡ كُنتُ إِلَّا بَشَرٗا رَّسُولٗا\nЁки сенинг олтиндан бир уйинг бўлмагунича, ёхуд ўзинг (кўз олдимизда) осмонга кўтарилмагунингча (ҳаргиз сенга иймон келтирмаймиз). Токи, бизларга ўқийдиган бирон китоб туширмас экансан, бу (осмонга) кўтарилишингга ҳам ҳаргиз ишонмаймиз». Айтинг: «Эй Пок Парвардигорим, мен фақат элчи-пайғамбар бўлган бир одам эдим-ку!»'}, {'chapter': 17, 'verse': 94, 'text': 'وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُوٓاْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰٓ إِلَّآ أَن قَالُوٓاْ أَبَعَثَ ٱللَّهُ بَشَرٗا رَّسُولٗا\nОдамларга ҳидоят келган пайтида, фақат: «Аллоҳ (фаришталарни эмас, балки) одамзодни пайғамбар қилиб юборибдими?!», деган сўзларигина уларни иймон келтиришларидан тўсди'}, {'chapter': 17, 'verse': 95, 'text': 'قُل لَّوۡ كَانَ فِي ٱلۡأَرۡضِ مَلَـٰٓئِكَةٞ يَمۡشُونَ مُطۡمَئِنِّينَ لَنَزَّلۡنَا عَلَيۡهِم مِّنَ ٱلسَّمَآءِ مَلَكٗا رَّسُولٗا\nАйтинг: «Агар бу — Ерда (одамлар эмас, балки) фаришталар маскан тутиб юрганларида эди, албатта, Биз (яъни, Аллоҳ) уларга осмондан фаришта пайғамбарни туширган бўлур эдик»'}, {'chapter': 17, 'verse': 96, 'text': 'قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا\nАйтинг: «Мен билан сизларнинг ўртамизда Аллоҳнинг Ўзи етарли гувоҳдир. Албатта, У бандаларидан огоҳ ва (уларни) кўриб тургувчи бўлган Зотдир»'}, {'chapter': 17, 'verse': 97, 'text': 'وَمَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُمۡ أَوۡلِيَآءَ مِن دُونِهِۦۖ وَنَحۡشُرُهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ عَلَىٰ وُجُوهِهِمۡ عُمۡيٗا وَبُكۡمٗا وَصُمّٗاۖ مَّأۡوَىٰهُمۡ جَهَنَّمُۖ كُلَّمَا خَبَتۡ زِدۡنَٰهُمۡ سَعِيرٗا\nАллоҳ кимни ҳидоят қилса, бас, ўша ҳидоят топгучидир. У зот кимни йўлдан оздирса, бас, улар учун Ундан ўзга дўст мададкорларни топа олмассиз, уларни Қиёмат Кунида юзтубан ҳолларида кўр, соқов, кар қилиб тирилтирурмиз. Уларнинг борар жойлари жаҳаннамдир. Ҳар қачон (жаҳаннам ўти) сусайса, Биз уларга оловни ловуллатиб юборурмиз'}, {'chapter': 17, 'verse': 98, 'text': 'ذَٰلِكَ جَزَآؤُهُم بِأَنَّهُمۡ كَفَرُواْ بِـَٔايَٰتِنَا وَقَالُوٓاْ أَءِذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدًا\nБу Бизнинг оятларимизга кофир бўлганлари ҳамда: «Суяк ва (чирик) мурдаларга айланган вақтимизда янгитдан яралиб қайта тирилурмизми?!», деганлари сабабли, уларга берилган жазодир'}, {'chapter': 17, 'verse': 99, 'text': '۞أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ قَادِرٌ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۡ وَجَعَلَ لَهُمۡ أَجَلٗا لَّا رَيۡبَ فِيهِ فَأَبَى ٱلظَّـٰلِمُونَ إِلَّا كُفُورٗا\nАхир улар (кофирлар) осмонлар ва Ерни яратган зот – Аллоҳ – уларнинг (яъни, инсонларнинг) мислини ҳам яратишга қодир эканини билмадиларми?! (Аллоҳ) улар учун (яъни, ўлишлари ва қайта тирилишлари учун) аниқ бир муддатни белгилаб қўйгандир. Лекин золим кимсалар фақат куфргагина рози бўлурлар'}, {'chapter': 17, 'verse': 100, 'text': 'قُل لَّوۡ أَنتُمۡ تَمۡلِكُونَ خَزَآئِنَ رَحۡمَةِ رَبِّيٓ إِذٗا لَّأَمۡسَكۡتُمۡ خَشۡيَةَ ٱلۡإِنفَاقِۚ وَكَانَ ٱلۡإِنسَٰنُ قَتُورٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, у мушрикларга) айтинг: «Агар сизлар Парвардигоримнинг раҳмат-ризқ хазиналарига эга бўлсангизлар, ўшанда ҳам (уни) ишлатишдан (тугатиб қўйишдан) қўрқиб, мумсиклик-бахиллик қилган бўлур эдингизлар». (Чунки) инсон зоти хасис бўлур'}, {'chapter': 17, 'verse': 101, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ تِسۡعَ ءَايَٰتِۭ بَيِّنَٰتٖۖ فَسۡـَٔلۡ بَنِيٓ إِسۡرَـٰٓءِيلَ إِذۡ جَآءَهُمۡ فَقَالَ لَهُۥ فِرۡعَوۡنُ إِنِّي لَأَظُنُّكَ يَٰمُوسَىٰ مَسۡحُورٗا\nАниқки, Биз Мусога тўққиз очиқ оят-мўъжиза ато этдик. Сўнг у (Фиръавн ва унинг одамларининг олдига) келган пайтида, (унга): «Бани Исроилни (ўз юртлари Шомга қўйиб юборилишини, Фиръавндан) сўрагин», (деб амр этдик). Шунда Фиръавн унга: «Эй Мусо, ҳеч шубҳа йўқки, мен сени сеҳр-жодуга чалинган, деб ўйламоқдаман», деди'}, {'chapter': 17, 'verse': 102, 'text': 'قَالَ لَقَدۡ عَلِمۡتَ مَآ أَنزَلَ هَـٰٓؤُلَآءِ إِلَّا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ بَصَآئِرَ وَإِنِّي لَأَظُنُّكَ يَٰفِرۡعَوۡنُ مَثۡبُورٗا\n(Мусо) деди: «(Эй Фиръавн), сен анави очиқ-равшан (мўъжизаларни) фақат осмонлар ва Ернинг Парвардигоригина нозил қилганини аниқ билурсан. Ҳеч шубҳа йўқки, эй Фиръавн, мен сени ҳалок қилингувчи, деб ўйламоқдаман»'}, {'chapter': 17, 'verse': 103, 'text': 'فَأَرَادَ أَن يَسۡتَفِزَّهُم مِّنَ ٱلۡأَرۡضِ فَأَغۡرَقۡنَٰهُ وَمَن مَّعَهُۥ جَمِيعٗا\nШунда, (Фиръавн Мусо ва унинг қавмини) қувиб чиқармоқчи бўлганида, Биз уни (Фиръавнни) ва у билан бирга бўлган кимсаларнинг барчаларини (денгизга) ғарқ қилдик'}, {'chapter': 17, 'verse': 104, 'text': 'وَقُلۡنَا مِنۢ بَعۡدِهِۦ لِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱسۡكُنُواْ ٱلۡأَرۡضَ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ جِئۡنَا بِكُمۡ لَفِيفٗا\n(Фиръавндан) сўнг, Бани Исроилга: «Мана шу ерни (Мисрни) маскан тутинглар. Энди қачон ваъда қилинган Охират келганида сизларнинг барчангизни (қайта тирилтириб) келтирурмиз», дедик'}, {'chapter': 17, 'verse': 105, 'text': 'وَبِٱلۡحَقِّ أَنزَلۡنَٰهُ وَبِٱلۡحَقِّ نَزَلَۗ وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا\nБиз (Қуръонни) ҳаққи-рост нозил қилдик ва у ҳақ (ҳукм-фармонларимиз) билан нозил бўлди. (Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизни фақат (мўминларга) хушхабар элтгувчи ва (кофирларни дўзах азобидан) огоҳлантиргувчи қилиб юбордик'}, {'chapter': 17, 'verse': 106, 'text': 'وَقُرۡءَانٗا فَرَقۡنَٰهُ لِتَقۡرَأَهُۥ عَلَى ٱلنَّاسِ عَلَىٰ مُكۡثٖ وَنَزَّلۡنَٰهُ تَنزِيلٗا\nСиз Қуръонни одамларга аста-секин ўқиб беришингиз учун Биз уни бўлакларга бўлдик ва бўлиб-бўлиб нозил қилдик'}, {'chapter': 17, 'verse': 107, 'text': 'قُلۡ ءَامِنُواْ بِهِۦٓ أَوۡ لَا تُؤۡمِنُوٓاْۚ إِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مِن قَبۡلِهِۦٓ إِذَا يُتۡلَىٰ عَلَيۡهِمۡ يَخِرُّونَۤ لِلۡأَذۡقَانِۤ سُجَّدٗاۤ\nАйтинг: «(Эй мушриклар), сизлар (хоҳласангиз), Унга ишонинглар, (хоҳласангиз) ишонманглар. Зотан, (Қуръон нозил бўлишидан) илгари илм (яъни, Таврот) берилган кишилар (Қуръон) тиловат қилинган вақтида сажда қилган ҳолларида юзлари билан йиқилурлар'}, {'chapter': 17, 'verse': 108, 'text': 'وَيَقُولُونَ سُبۡحَٰنَ رَبِّنَآ إِن كَانَ وَعۡدُ رَبِّنَا لَمَفۡعُولٗا\nВа улар: «Эй Пок Парвардигор! Албатта Парвардигоримизнинг («Мен сўнгги пайғамбаримни юборурман ва унга Қуръон нозил қилурман», деган) ваъдаси адо қилингувчи ваъдадир», дейдилар'}, {'chapter': 17, 'verse': 109, 'text': 'وَيَخِرُّونَ لِلۡأَذۡقَانِ يَبۡكُونَ وَيَزِيدُهُمۡ خُشُوعٗا۩\nУлар, йиғлаган ҳолларида, юзлари билан йиқилурлар ва (Қуръон) уларнинг хокисорликларини зиёда қилур'}, {'chapter': 17, 'verse': 110, 'text': 'قُلِ ٱدۡعُواْ ٱللَّهَ أَوِ ٱدۡعُواْ ٱلرَّحۡمَٰنَۖ أَيّٗا مَّا تَدۡعُواْ فَلَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ وَلَا تَجۡهَرۡ بِصَلَاتِكَ وَلَا تُخَافِتۡ بِهَا وَٱبۡتَغِ بَيۡنَ ذَٰلِكَ سَبِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Аллоҳ, деб чорлангиз, ёки Раҳмон — Меҳрибон, деб чорлангиз. Қандай чорласангизлар-да (жоиздир). Зеро, У зотнинг гўзал исмлари бордир». (Эй Муҳаммад алайҳис-салоту вас-салом), сиз намозингизни жуда жаҳрий (баланд овозда, ошкора) ҳам қилиб юборманг (токи, мушриклар эшитиб, Қуръонни ҳақорат қилмасинлар) ва ўта махфий ҳам қилиб юборманг (токи, саҳобаларингиз тинглаб-фойдалансинлар). Шуларнинг (яъни, жаҳрийлик билан махфийлик) ўртасидаги йўлни тутинг'}, {'chapter': 17, 'verse': 111, 'text': 'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَلَمۡ يَكُن لَّهُۥ وَلِيّٞ مِّنَ ٱلذُّلِّۖ وَكَبِّرۡهُ تَكۡبِيرَۢا\n«Ҳамду сано бола-чақа қилмаган, подшоҳлик-илоҳликда шериги йўқ, хор-зорликдан (қутқаргувчи) дўсти-ёрдамчиси бўлмаган (яъни, ҳеч кимга хор-муҳтож бўлмайдиган) зот — Аллоҳ учундир», деб айтинг ва У зотни мудом улуғланг'}, {'chapter': 18, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِيٓ أَنزَلَ عَلَىٰ عَبۡدِهِ ٱلۡكِتَٰبَ وَلَمۡ يَجۡعَل لَّهُۥ عِوَجَاۜ\n(Барча) ҳамду сано Аллоҳ учундир. У зот бандаси (Муҳаммад)га Китобни (Қуръонни) нозил қилди ва уни бирон эгрилигисиз — расо (Китоб) қилди'}, {'chapter': 18, 'verse': 2, 'text': 'قَيِّمٗا لِّيُنذِرَ بَأۡسٗا شَدِيدٗا مِّن لَّدُنۡهُ وَيُبَشِّرَ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرًا حَسَنٗا\nТоки (кофирларни) Ўз ҳузуридан келадиган қаттиқ азобдан қўрқитиш ва яхши амаллар қиладиган мўминларга гўзал мукофот — жаннат хушхабарини бериш учун (У зот Қуръонни нозил қилди)'}, {'chapter': 18, 'verse': 3, 'text': 'مَّـٰكِثِينَ فِيهِ أَبَدٗا\n(Мўминлар ўзлари учун мукофот бўлмиш жаннатда) мангу қолгувчидирлар'}, {'chapter': 18, 'verse': 4, 'text': 'وَيُنذِرَ ٱلَّذِينَ قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗا\nШунингдек, У зот «Аллоҳнинг боласи бор», деган кимсаларни (қаттиқ азобдан) огоҳлантириш учун (ушбу Китобни нозил қилгандир)'}, {'chapter': 18, 'verse': 5, 'text': 'مَّا لَهُم بِهِۦ مِنۡ عِلۡمٖ وَلَا لِأٓبَآئِهِمۡۚ كَبُرَتۡ كَلِمَةٗ تَخۡرُجُ مِنۡ أَفۡوَٰهِهِمۡۚ إِن يَقُولُونَ إِلَّا كَذِبٗا\nЎзларининг ҳам, ота-боболарининг ҳам бирон билим-маърифати бўлмаган ҳолда («Аллоҳнинг боласи бор», деган кимсаларни). Уларнинг оғизларидан чиққан сўз оғир сўздир. Улар фақат ёлғон сўзлайдилар'}, {'chapter': 18, 'verse': 6, 'text': 'فَلَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ عَلَىٰٓ ءَاثَٰرِهِمۡ إِن لَّمۡ يُؤۡمِنُواْ بِهَٰذَا ٱلۡحَدِيثِ أَسَفًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар улар ушбу сўзга — Қуръонга иймон келтирмасалар, эҳтимол сиз уларнинг ортидан, афсус-надомат билан ўзингизни ҳалок қилурсиз (бундоқ қилмоқ сизга нораводир)'}, {'chapter': 18, 'verse': 7, 'text': 'إِنَّا جَعَلۡنَا مَا عَلَى ٱلۡأَرۡضِ زِينَةٗ لَّهَا لِنَبۡلُوَهُمۡ أَيُّهُمۡ أَحۡسَنُ عَمَلٗا\nАлбатта, Биз (бандаларимиздан) қайсилари чиройлироқ амалларни қилишларини синамоқ учун Ер устидаги бор нарсани унга зийнат — безак қилиб қўйдик (яъни, ўша безакларга алданиб, мағрурланиб қолмаган кишиларгина Бизнинг синовимиздан ўтурлар)'}, {'chapter': 18, 'verse': 8, 'text': 'وَإِنَّا لَجَٰعِلُونَ مَا عَلَيۡهَا صَعِيدٗا جُرُزًا\nҲеч шак-шубҳасиз, унинг (Ернинг) устидаги (безаклар)ни сип-силлиқ, қуп-қуруқ қилиб (йўқ қилиб) қўйгувчи ҳам Биздирмиз'}, {'chapter': 18, 'verse': 9, 'text': 'أَمۡ حَسِبۡتَ أَنَّ أَصۡحَٰبَ ٱلۡكَهۡفِ وَٱلرَّقِيمِ كَانُواْ مِنۡ ءَايَٰتِنَا عَجَبًا\nБалки сиз Каҳф (ғор) ва Битик (уларнинг қиссалари битилган тош) эгалари (ҳақидаги ривоятдан воқиф бўлгач, бу) Бизнинг (Охират, Қиёмат ҳаққи-рост эканлиги тўғрисидаги) оятларимизнинг энг ажойиби, деб ўйлагандирсиз'}, {'chapter': 18, 'verse': 10, 'text': 'إِذۡ أَوَى ٱلۡفِتۡيَةُ إِلَى ٱلۡكَهۡفِ فَقَالُواْ رَبَّنَآ ءَاتِنَا مِن لَّدُنكَ رَحۡمَةٗ وَهَيِّئۡ لَنَا مِنۡ أَمۡرِنَا رَشَدٗا\nЭсланг, ўшанда улар ғорга паноҳ истаб боришиб: «Парвардигоро, бизларга ўз ҳузурингдан раҳмат — марҳамат ато этгин ва бизларнинг ишимизни Ўзинг ўнглагин», дедилар'}, {'chapter': 18, 'verse': 11, 'text': 'فَضَرَبۡنَا عَلَىٰٓ ءَاذَانِهِمۡ فِي ٱلۡكَهۡفِ سِنِينَ عَدَدٗا\nБас, Биз ўша ғорда бир неча йил уларнинг қулоқларига уриб (яъни, уларни ухлатиб) қўйдик'}, {'chapter': 18, 'verse': 12, 'text': 'ثُمَّ بَعَثۡنَٰهُمۡ لِنَعۡلَمَ أَيُّ ٱلۡحِزۡبَيۡنِ أَحۡصَىٰ لِمَا لَبِثُوٓاْ أَمَدٗا\nСўнгра (уларнинг ичидаги қанча ухлаганлари ҳақида тортишадиган) икки гуруҳдан қайси бири турган муддатларини (тўғри) ҳисоблаганини билиш учун уларни уйғотдик'}, {'chapter': 18, 'verse': 13, 'text': 'نَّحۡنُ نَقُصُّ عَلَيۡكَ نَبَأَهُم بِٱلۡحَقِّۚ إِنَّهُمۡ فِتۡيَةٌ ءَامَنُواْ بِرَبِّهِمۡ وَزِدۡنَٰهُمۡ هُدٗى\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизга уларнинг ҳол-хабарларини рўй-рост сўйламоқдамиз. Дарҳақиқат, улар Парвардигорларига иймон келтирган ва Биз иймонларини зиёда қилган йигитлардир'}, {'chapter': 18, 'verse': 14, 'text': 'وَرَبَطۡنَا عَلَىٰ قُلُوبِهِمۡ إِذۡ قَامُواْ فَقَالُواْ رَبُّنَا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَن نَّدۡعُوَاْ مِن دُونِهِۦٓ إِلَٰهٗاۖ لَّقَدۡ قُلۡنَآ إِذٗا شَطَطًا\nБиз уларнинг дилларини (иймонларини) қувватлантирдик — ўшанда улар (золим шоҳ қаршисида) туриб дедилар: «Бизнинг Парвардигоримиз осмонлар ва Ернинг Парвардигоридир. Бизлар Ундан ўзга бирон «илоҳ»га ҳаргиз илтижо қилмаймиз. Акс ҳолда, ноҳақ сўзни айтган бўлурмиз'}, {'chapter': 18, 'verse': 15, 'text': 'هَـٰٓؤُلَآءِ قَوۡمُنَا ٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗۖ لَّوۡلَا يَأۡتُونَ عَلَيۡهِم بِسُلۡطَٰنِۭ بَيِّنٖۖ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا\nАна у қавмимиз эса Ундан ўзгаларни «илоҳ» қилиб олдилар. Улар ўзлари учун (ўша бутларнинг илоҳ экани ҳақида) бирон ҳужжат келтирсалар эди. Бас, Аллоҳ шаънига ёлғон тўқиган кимсадан ҳам золимроқ ким бор?!»'}, {'chapter': 18, 'verse': 16, 'text': 'وَإِذِ ٱعۡتَزَلۡتُمُوهُمۡ وَمَا يَعۡبُدُونَ إِلَّا ٱللَّهَ فَأۡوُۥٓاْ إِلَى ٱلۡكَهۡفِ يَنشُرۡ لَكُمۡ رَبُّكُم مِّن رَّحۡمَتِهِۦ وَيُهَيِّئۡ لَكُم مِّنۡ أَمۡرِكُم مِّرۡفَقٗا\n(Йигитлар!) Модомики улардан (мушриклардан) ва Аллоҳдан ўзга улар сиғинаётган нарсалардан юз ўгирган экансизлар, энди ғорга паноҳ топиб борингиз. Парвардигорингиз сизларга Ўз раҳмат марҳаматини кенг — сероб қилур ва сизларнинг ишларингизни ўнглар»'}, {'chapter': 18, 'verse': 17, 'text': '۞وَتَرَى ٱلشَّمۡسَ إِذَا طَلَعَت تَّزَٰوَرُ عَن كَهۡفِهِمۡ ذَاتَ ٱلۡيَمِينِ وَإِذَا غَرَبَت تَّقۡرِضُهُمۡ ذَاتَ ٱلشِّمَالِ وَهُمۡ فِي فَجۡوَةٖ مِّنۡهُۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِۗ مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُۥ وَلِيّٗا مُّرۡشِدٗا\nҚуёш чиқишда уларнинг ғорларидан ўнг тарафдан ўтиб кетганини, ботишда эса улардан сўл томонга йироқлашиб кетганини (чиқишда ҳам, ботишда ҳам уларни қиздириб, уйқу-оромларини бузмаганини), улар эса (ғор)нинг тўрида эканликларини кўрурсан. Бу ҳам Аллоҳнинг оят-мўъжизаларидан (биридир). Кимни Аллоҳ ҳидоят қилса, бас, ўшагина ҳидоят топгувчидир. Кимни йўлдан оздирса, бас, унинг учун Тўғри Йўлга йўллагувчи бирон дўст топа олмассан'}, {'chapter': 18, 'verse': 18, 'text': 'وَتَحۡسَبُهُمۡ أَيۡقَاظٗا وَهُمۡ رُقُودٞۚ وَنُقَلِّبُهُمۡ ذَاتَ ٱلۡيَمِينِ وَذَاتَ ٱلشِّمَالِۖ وَكَلۡبُهُم بَٰسِطٞ ذِرَاعَيۡهِ بِٱلۡوَصِيدِۚ لَوِ ٱطَّلَعۡتَ عَلَيۡهِمۡ لَوَلَّيۡتَ مِنۡهُمۡ فِرَارٗا وَلَمُلِئۡتَ مِنۡهُمۡ رُعۡبٗا\nУйқуда эканлар, (кўзлари очиқ бўлгани учун) сен уларни уйғоқ, деб ўйларсан. Биз уларни (баданларидан ерга тегиб турган томони чириб кетмаслиги учун) ўнг томон, сўл томонга айлантириб турурмиз. Уларнинг итлари эса олди оёқларини остонага ёзиб ётур. Агар уларнинг устидан чиқиб қолсанг (ва кўзлари очиқ ҳолда у ёндан-бу ёнга ағдарилиб ётганларини кўрсанг) уларнинг (аҳволидан) даҳшатга тушиб юз ўгириб қочган бўлур эдинг'}, {'chapter': 18, 'verse': 19, 'text': 'وَكَذَٰلِكَ بَعَثۡنَٰهُمۡ لِيَتَسَآءَلُواْ بَيۡنَهُمۡۚ قَالَ قَآئِلٞ مِّنۡهُمۡ كَمۡ لَبِثۡتُمۡۖ قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۚ قَالُواْ رَبُّكُمۡ أَعۡلَمُ بِمَا لَبِثۡتُمۡ فَٱبۡعَثُوٓاْ أَحَدَكُم بِوَرِقِكُمۡ هَٰذِهِۦٓ إِلَى ٱلۡمَدِينَةِ فَلۡيَنظُرۡ أَيُّهَآ أَزۡكَىٰ طَعَامٗا فَلۡيَأۡتِكُم بِرِزۡقٖ مِّنۡهُ وَلۡيَتَلَطَّفۡ وَلَا يُشۡعِرَنَّ بِكُمۡ أَحَدًا\nЎзаро савол-жавоб қилишлари учун уларни (йигитларни, қандай ухлатиб қўйган бўлсак) ана шундай уйғотдик. Улардан бири деди: «Қанча (муддат ухлаган) ҳолда турдинглар?» Улар(нинг айримлари): «Бир кун ё ярим кун», дедилар. (Қолганлари эса): «Қанча (ухлаб) турганларингизни ёлғиз Парвардигорингиз билур. Энди бир кишини мана шу пулингиз билан шаҳарга жўнатинглар, у энг сара, покиза таомларни танлаб, сизларга ундан ризқ, насиба олиб келсин. У эҳтиёт бўлсин ва сизларни биронтага сездириб қўймасин'}, {'chapter': 18, 'verse': 20, 'text': 'إِنَّهُمۡ إِن يَظۡهَرُواْ عَلَيۡكُمۡ يَرۡجُمُوكُمۡ أَوۡ يُعِيدُوكُمۡ فِي مِلَّتِهِمۡ وَلَن تُفۡلِحُوٓاْ إِذًا أَبَدٗا\nЧунки улар (яъни, шаҳар аҳли) сизлардан огоҳ бўлиб қолсалар, сизларни тошбўрон қилурлар ёки яна ўзларининг динларига қайтарурлар ва у ҳолда, ҳеч қачон қутула олмассизлар», дейишди'}, {'chapter': 18, 'verse': 21, 'text': 'وَكَذَٰلِكَ أَعۡثَرۡنَا عَلَيۡهِمۡ لِيَعۡلَمُوٓاْ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَأَنَّ ٱلسَّاعَةَ لَا رَيۡبَ فِيهَآ إِذۡ يَتَنَٰزَعُونَ بَيۡنَهُمۡ أَمۡرَهُمۡۖ فَقَالُواْ ٱبۡنُواْ عَلَيۡهِم بُنۡيَٰنٗاۖ رَّبُّهُمۡ أَعۡلَمُ بِهِمۡۚ قَالَ ٱلَّذِينَ غَلَبُواْ عَلَىٰٓ أَمۡرِهِمۡ لَنَتَّخِذَنَّ عَلَيۡهِم مَّسۡجِدٗا\nШундай қилиб, Аллоҳнинг (қайта тирилтириш ҳақидаги) ваъдаси ҳақ эканини ва Қиёмат соатининг (келиши) шак-шубҳасиз, эканини билишлари учун (одамларни ғордаги йигитларнинг аҳволидан) огоҳ қилдик. Ўшанда (яъни, ғордаги йигитлар уйқуларидан уйғониб, одамлар бу мўъжизанинг гувоҳи бўлганларидан кейин, улар ажаллари етиб вафот қилишгач) ўзаро уларнинг ишлари ҳақида талашиб-тортиша бошладилар. Бас, (айрим кишилар): «Уларнинг устига уйлар бино қилинглар. Парвардигор уларнинг (қайси жойда, қандай ҳолда ётганларини) жуда яхши билур», дедилар. Уларнинг устида ҳукмрон бўлган (подшоҳ ва сарой аъёнлари эса): «Албатта, сиз улар (яъни, Асҳобул-Каҳф) қабрининг устида бир масжид қуриб олурмиз», дейишди'}, {'chapter': 18, 'verse': 22, 'text': 'سَيَقُولُونَ ثَلَٰثَةٞ رَّابِعُهُمۡ كَلۡبُهُمۡ وَيَقُولُونَ خَمۡسَةٞ سَادِسُهُمۡ كَلۡبُهُمۡ رَجۡمَۢا بِٱلۡغَيۡبِۖ وَيَقُولُونَ سَبۡعَةٞ وَثَامِنُهُمۡ كَلۡبُهُمۡۚ قُل رَّبِّيٓ أَعۡلَمُ بِعِدَّتِهِم مَّا يَعۡلَمُهُمۡ إِلَّا قَلِيلٞۗ فَلَا تُمَارِ فِيهِمۡ إِلَّا مِرَآءٗ ظَٰهِرٗا وَلَا تَسۡتَفۡتِ فِيهِم مِّنۡهُمۡ أَحَدٗا\nҲали улар (яъни, Пайғамбар алайҳис-салоту вас-саломга замондош бўлган яҳудий ва насороларнинг айримлари ўша Асҳобул-Каҳфни): «Учтадир, тўртинчилари итларидир», десалар, айримлари: «Улар бешта бўлиб, олтинчилари итларидир», деб ғайбга тош отурлар (яъни, аниқ билмаган нарсалари ҳақида гап сотурлар). Яна: «Улар еттитадирлар, саккизинчилари итларидир», ҳам дейдилар. (Эй Муҳаммад алайҳис-салоту вас-салом), «Уларнинг саноқларини энг яхши биладиган зот Парвардигоримдир. Уларни жуда оз киши билур», денг. Бас, улар (яъни, замондош аҳли китоблар билан Асҳобул-Каҳф) хусусида (ўзингизга нозил бўлган ваҳий воситасида) очиқ мужодала — мунозара қилинг ва (Асҳобул-Каҳф) ҳақида уларнинг биронтасидан фатво — савол сўраманг'}, {'chapter': 18, 'verse': 23, 'text': 'وَلَا تَقُولَنَّ لِشَاْيۡءٍ إِنِّي فَاعِلٞ ذَٰلِكَ غَدًا\nВа бирон нарса ҳақида «Мен албатта эртага қилгувчиман», дея кўрманг'}, {'chapter': 18, 'verse': 24, 'text': 'إِلَّآ أَن يَشَآءَ ٱللَّهُۚ وَٱذۡكُر رَّبَّكَ إِذَا نَسِيتَ وَقُلۡ عَسَىٰٓ أَن يَهۡدِيَنِ رَبِّي لِأَقۡرَبَ مِنۡ هَٰذَا رَشَدٗا\nМагар «Иншоаллоҳ – Аллоҳ хоҳласа», (денг! Бу сўзларни айтишни) унутиб қолдирган вақтингизда (ёдингизга тушиши билан): Парвардигорингизни зикр қилинг (яъни, «Иншоаллоҳ», денг) ва: «Шояд Парвардигорим мени бундан ҳам (яъни, Асҳобул-Каҳф воқеаси ҳақида хабар беришдан ҳам) яқинроқ (менинг ҳақ пайғамбар эканлигимга яхшироқ далолат қиладиган) Тўғри Йўлга ҳидоят қилса», денг'}, {'chapter': 18, 'verse': 25, 'text': 'وَلَبِثُواْ فِي كَهۡفِهِمۡ ثَلَٰثَ مِاْئَةٖ سِنِينَ وَٱزۡدَادُواْ تِسۡعٗا\nУлар каҳф — ғорларида уч юз йил турдилар ва яна тўққиз йилни зиёда ҳам қилдилар'}, {'chapter': 18, 'verse': 26, 'text': 'قُلِ ٱللَّهُ أَعۡلَمُ بِمَا لَبِثُواْۖ لَهُۥ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَبۡصِرۡ بِهِۦ وَأَسۡمِعۡۚ مَا لَهُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا يُشۡرِكُ فِي حُكۡمِهِۦٓ أَحَدٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Уларнинг қанча турганларини Аллоҳ жуда яхши билгувчидир. Осмонлар ва Ернинг сирлари ёлғиз Уникидир. У зот нақадар кўргувчи, эшитгувчидир! (Одамлар) учун Ундан ўзга бирон дўст-мададкор йўқдир. У Ўз ҳукмида ҳеч кимни шерик қилмас»'}, {'chapter': 18, 'verse': 27, 'text': 'وَٱتۡلُ مَآ أُوحِيَ إِلَيۡكَ مِن كِتَابِ رَبِّكَۖ لَا مُبَدِّلَ لِكَلِمَٰتِهِۦ وَلَن تَجِدَ مِن دُونِهِۦ مُلۡتَحَدٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз фақат ўзингизга ваҳий қилинган Парвардигорингизнинг Китоби — Қуръоннигина тиловат қилинг! Унинг (Аллоҳнинг) сўзларини ўзгартирувчи йўқдир. Ҳаргиз Ундан ўзга бирон паноҳ топа олмассиз'}, {'chapter': 18, 'verse': 28, 'text': 'وَٱصۡبِرۡ نَفۡسَكَ مَعَ ٱلَّذِينَ يَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ وَلَا تَعۡدُ عَيۡنَاكَ عَنۡهُمۡ تُرِيدُ زِينَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَا تُطِعۡ مَنۡ أَغۡفَلۡنَا قَلۡبَهُۥ عَن ذِكۡرِنَا وَٱتَّبَعَ هَوَىٰهُ وَكَانَ أَمۡرُهُۥ فُرُطٗا\nСиз ўзингизни эрта-ю, кеч Парвардигорларининг юзини — розилигини истаб, Унга илтижо қиладиган зотлар билан бирга тутинг! Кўзларингиз ҳаёти дунё зийнатларини кўзлаб, улардан ўтиб (ўзга аҳли дунёларга боқмасин)! Ва Биз қалбини Бизни зикр қилишдан ғофил қилиб қўйган, ҳавойи-нафсига эргашган ва қилар иши исрофгарчилик бўлган кимсаларга итоат этманг'}, {'chapter': 18, 'verse': 29, 'text': 'وَقُلِ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَن شَآءَ فَلۡيُؤۡمِن وَمَن شَآءَ فَلۡيَكۡفُرۡۚ إِنَّآ أَعۡتَدۡنَا لِلظَّـٰلِمِينَ نَارًا أَحَاطَ بِهِمۡ سُرَادِقُهَاۚ وَإِن يَسۡتَغِيثُواْ يُغَاثُواْ بِمَآءٖ كَٱلۡمُهۡلِ يَشۡوِي ٱلۡوُجُوهَۚ بِئۡسَ ٱلشَّرَابُ وَسَآءَتۡ مُرۡتَفَقًا\nАйтинг: «(Бу Қуръон) Парвардигорингиз томонидан (келган) Ҳақиқатдир. Бас, хоҳлаган киши иймон келтирсин, хоҳлаган кимса кофир бўлсин». Аниқки, Биз золим — кофирлар учун алангалари дўзахиларни ўраб-чирмаб оладиган дўзахни тайёрлаб қўйгандирмиз. Агар улар (ташналик шиддатига чидамай) сув сўрасалар, эритилган (доғланган) ёғ каби юзларни куйдиргувчи сув берилур. Нақадар ёмон ичимлик у, нақадар ёмон жой у'}, {'chapter': 18, 'verse': 30, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ إِنَّا لَا نُضِيعُ أَجۡرَ مَنۡ أَحۡسَنَ عَمَلًا\nИймон келтирган ва яхши амаллар қилган зотлар эса, шак-шубҳасиз, Биз (улар каби) чиройли амаллар қилган кишиларнинг ажр-мукофотини зое қилмасмиз'}, {'chapter': 18, 'verse': 31, 'text': 'أُوْلَـٰٓئِكَ لَهُمۡ جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَيَلۡبَسُونَ ثِيَابًا خُضۡرٗا مِّن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۚ نِعۡمَ ٱلثَّوَابُ وَحَسُنَتۡ مُرۡتَفَقٗا\nУлар учун остларидан дарёлар оқиб турадиган абадий жаннатлар бор бўлиб, улар у жойда олтиндан бўлган билакузуклар билан безанурлар ва ипак-шойилардан бўлган, яшнаб турувчи либослар кийиб, сўриларда ястаниб ўтирурлар. Нақадар яхши мукофот у, нақадар гўзал жой у'}, {'chapter': 18, 'verse': 32, 'text': '۞وَٱضۡرِبۡ لَهُم مَّثَلٗا رَّجُلَيۡنِ جَعَلۡنَا لِأَحَدِهِمَا جَنَّتَيۡنِ مِنۡ أَعۡنَٰبٖ وَحَفَفۡنَٰهُمَا بِنَخۡلٖ وَجَعَلۡنَا بَيۡنَهُمَا زَرۡعٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, кофирларга) икки киши (ҳақидаги) масални келтиринг. Улардан бири учун икки узумзор боғ қилиб, у иккисини хурмозор билан ўраган ва ўрталарини экинзор қилган эдик'}, {'chapter': 18, 'verse': 33, 'text': 'كِلۡتَا ٱلۡجَنَّتَيۡنِ ءَاتَتۡ أُكُلَهَا وَلَمۡ تَظۡلِم مِّنۡهُ شَيۡـٔٗاۚ وَفَجَّرۡنَا خِلَٰلَهُمَا نَهَرٗا\nҲар икки боғ ҳосилларини бекаму кўст берар, Биз уларнинг ўртасидан бир анҳор ўтказган эдик'}, {'chapter': 18, 'verse': 34, 'text': 'وَكَانَ لَهُۥ ثَمَرٞ فَقَالَ لِصَٰحِبِهِۦ وَهُوَ يُحَاوِرُهُۥٓ أَنَا۠ أَكۡثَرُ مِنكَ مَالٗا وَأَعَزُّ نَفَرٗا\nЯна унинг (ўша кишининг) мол-давлати ҳам бор эди. Бас, у биродарига мақтаниб: «Менинг мол-дунёим сеникидан кўпроқ ва одамларим кучлироқ, қувватлироқ», деди'}, {'chapter': 18, 'verse': 35, 'text': 'وَدَخَلَ جَنَّتَهُۥ وَهُوَ ظَالِمٞ لِّنَفۡسِهِۦ قَالَ مَآ أَظُنُّ أَن تَبِيدَ هَٰذِهِۦٓ أَبَدٗا\nВа (кофирлик билан) ўзига зулм қилган ҳолда, боғига кирар экан, айтди: «Бу (боғ — менинг молу давлатим) ҳеч қачон йўқ бўлмайди'}, {'chapter': 18, 'verse': 36, 'text': 'وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّدِدتُّ إِلَىٰ رَبِّي لَأَجِدَنَّ خَيۡرٗا مِّنۡهَا مُنقَلَبٗا\nВа Қиёмат ҳам қойим бўлмайди, деб ўйлайман. Қасамки, агар Парвардигоримга қайтарилсам, (яъни, Қиёмат қойим бўлиб, қайта тирилсам), албатта, бундан ҳам яхшироқ оқибат — бахтни топурман»'}, {'chapter': 18, 'verse': 37, 'text': 'قَالَ لَهُۥ صَاحِبُهُۥ وَهُوَ يُحَاوِرُهُۥٓ أَكَفَرۡتَ بِٱلَّذِي خَلَقَكَ مِن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ سَوَّىٰكَ رَجُلٗا\nБиродари унга хитоб қилиб, деди: «Сен ўзингни (асли-наслинг бўлмиш Одамни) тупроқдан сўнг нутфа — бир томчи сувдан яратиб, сўнгра уни) инсон қилиб ростлаган зот — Аллоҳга кофир бўлдингми'}, {'chapter': 18, 'verse': 38, 'text': 'لَّـٰكِنَّا۠ هُوَ ٱللَّهُ رَبِّي وَلَآ أُشۡرِكُ بِرَبِّيٓ أَحَدٗا\nМен эса: «У – Аллоҳ Парвардигоримдир», (дейман) ва Парвардигоримга ҳеч кимни шерик қилмайман'}, {'chapter': 18, 'verse': 39, 'text': 'وَلَوۡلَآ إِذۡ دَخَلۡتَ جَنَّتَكَ قُلۡتَ مَا شَآءَ ٱللَّهُ لَا قُوَّةَ إِلَّا بِٱللَّهِۚ إِن تَرَنِ أَنَا۠ أَقَلَّ مِنكَ مَالٗا وَوَلَدٗا\nСен боғингга кирган пайтингда: «Аллоҳ хоҳлаган нарсагина (бўлур), бор куч-қувват ёлғиз Аллоҳ биландир», десанг эди! Агар сен мени мол-давлат ва бола-чақа жиҳатидан ўзингдан камроқ деб билсанг'}, {'chapter': 18, 'verse': 40, 'text': 'فَعَسَىٰ رَبِّيٓ أَن يُؤۡتِيَنِ خَيۡرٗا مِّن جَنَّتِكَ وَيُرۡسِلَ عَلَيۡهَا حُسۡبَانٗا مِّنَ ٱلسَّمَآءِ فَتُصۡبِحَ صَعِيدٗا زَلَقًا\nШоядки, Парвардигорим менга сенинг боғингдан яхшироқ (бир боғ) ато этар (сенинг боғингга эса) осмондан чақмоқлар юборур, бас, у сип-силлиқ ерга айланиб қолур'}, {'chapter': 18, 'verse': 41, 'text': 'أَوۡ يُصۡبِحَ مَآؤُهَا غَوۡرٗا فَلَن تَسۡتَطِيعَ لَهُۥ طَلَبٗا\nЁки унинг суви (ерга сингиб кетиб, сен у)ни истаб ҳам топа олмай қолурсан»'}, {'chapter': 18, 'verse': 42, 'text': 'وَأُحِيطَ بِثَمَرِهِۦ فَأَصۡبَحَ يُقَلِّبُ كَفَّيۡهِ عَلَىٰ مَآ أَنفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُشۡرِكۡ بِرَبِّيٓ أَحَدٗا\n(Дарҳақиқат), унинг мева-боғи ҳалок қилинди. Ўзи эса ҳувиллаб қолган ишкомларини (кўриб) ва уларга сарфлаган нарсаларини (ўйлаб), чапак чалганича (афсус-надомат чекканича): «Қани эди, мен ҳам Парвардигоримга ҳеч кимни шерик қилмаганимда!», деб қолаверди'}, {'chapter': 18, 'verse': 43, 'text': 'وَلَمۡ تَكُن لَّهُۥ فِئَةٞ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مُنتَصِرًا\nШунингдек, унинг учун Аллоҳдан ўзга на бир ёрдам берадиган жамоат бўлди ва на ўзи (ўзига) ёрдам бера олгувчи бўлди'}, {'chapter': 18, 'verse': 44, 'text': 'هُنَالِكَ ٱلۡوَلَٰيَةُ لِلَّهِ ٱلۡحَقِّۚ هُوَ خَيۡرٞ ثَوَابٗا وَخَيۡرٌ عُقۡبٗا\nУ жойда (Қиёмат Кунида) ҳам салтанат Ҳақ таолоникидир. У энг яхши савоб — мукофот ато этгувчи ва энг хайрли оқибатни бергувчи Зотдир'}, {'chapter': 18, 'verse': 45, 'text': 'وَٱضۡرِبۡ لَهُم مَّثَلَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ فَأَصۡبَحَ هَشِيمٗا تَذۡرُوهُ ٱلرِّيَٰحُۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقۡتَدِرًا\n(Эй Муҳаммад алайҳис-салоту вас-салом, одамларга) ҳаёти дунё мисолини келтиринг, (У) худди бир сув кабидирки, Биз осмондан ёғдириб (аввал — киши ҳаётининг баҳорида) у сабабли замин набототи (бир-бирига) аралашиб-чирмашиб кетур, сўнгра (ҳаёт кузи келгач) шамоллар учириб кетадиган хас-хашакка айланиб қолур. Аллоҳ ҳамма нарсага қодир бўлган Зотдир'}, {'chapter': 18, 'verse': 46, 'text': 'ٱلۡمَالُ وَٱلۡبَنُونَ زِينَةُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱلۡبَٰقِيَٰتُ ٱلصَّـٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٌ أَمَلٗا\nМол-мулк, бола-чақа шу ҳаёти дунё зийнатидир. Парвардигорингиз наздида эса (абадий) қолгувчи яхши амаллар савоблироқ ва орзулироқ (орзу қилишга арзирлироқдир)'}, {'chapter': 18, 'verse': 47, 'text': 'وَيَوۡمَ نُسَيِّرُ ٱلۡجِبَالَ وَتَرَى ٱلۡأَرۡضَ بَارِزَةٗ وَحَشَرۡنَٰهُمۡ فَلَمۡ نُغَادِرۡ مِنۡهُمۡ أَحَدٗا\nБиз тоғларни (булутлар янглиғ) юргизадиган ва сиз (барчангиз) Ерни очиқ-яланғоч (тоғ-тошларсиз, боғ-роғларсиз) кўрадиган Кунни (яъни, Қиёматни эслангиз)! (У Кунда) Биз улардан биронтасини қўймай йиғдик'}, {'chapter': 18, 'verse': 48, 'text': 'وَعُرِضُواْ عَلَىٰ رَبِّكَ صَفّٗا لَّقَدۡ جِئۡتُمُونَا كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةِۭۚ بَلۡ زَعَمۡتُمۡ أَلَّن نَّجۡعَلَ لَكُم مَّوۡعِدٗا\nУлар саф тортган ҳолларида Парвардигорингизга рўбарў қилиндилар (ва Биз уларга айтдик): «Мана, сизларни аввал-бошда қандай яратган бўлсак, шундай ҳолда (яъни, мол-дунё, бола-чақаларингизни тарк қилиб, яланғоч ҳолингизда) ҳузуримизга келдингиз. Балки Бизни, сизларга ваъда қилинган Кунни (яъни, Қиёматни пайдо) қила олмас, деб ўйлагандирсизлар?!»'}, {'chapter': 18, 'verse': 49, 'text': 'وَوُضِعَ ٱلۡكِتَٰبُ فَتَرَى ٱلۡمُجۡرِمِينَ مُشۡفِقِينَ مِمَّا فِيهِ وَيَقُولُونَ يَٰوَيۡلَتَنَا مَالِ هَٰذَا ٱلۡكِتَٰبِ لَا يُغَادِرُ صَغِيرَةٗ وَلَا كَبِيرَةً إِلَّآ أَحۡصَىٰهَاۚ وَوَجَدُواْ مَا عَمِلُواْ حَاضِرٗاۗ وَلَا يَظۡلِمُ رَبُّكَ أَحَدٗا\n(Сўнг ҳар бир кишининг) номаи аъмоли ўртага қўйилур. Бас, гуноҳкорларнинг унда (битилган) нарсалардан даҳшатга тушиб: «Бизларга ҳалокат бўлғай, бу қандоқ китобки, на кичик ва на катта (гуноҳни) қолдирмай, барчасини ҳисоблаб-битиб қўйибди», дейишларини кўрурсиз. Улар қилиб ўтган барча амалларини ҳозиру нозир ҳолда топурлар. Парвардигорингиз ҳеч кимга зулм қилмас'}, {'chapter': 18, 'verse': 50, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ كَانَ مِنَ ٱلۡجِنِّ فَفَسَقَ عَنۡ أَمۡرِ رَبِّهِۦٓۗ أَفَتَتَّخِذُونَهُۥ وَذُرِّيَّتَهُۥٓ أَوۡلِيَآءَ مِن دُونِي وَهُمۡ لَكُمۡ عَدُوُّۢۚ بِئۡسَ لِلظَّـٰلِمِينَ بَدَلٗا\nЭсланг, (эй Муҳаммад алайҳис-салоту вас-салом), фаришталарга Одамга таъзим қилинг, дейишимиз билан саждага эгилдилар. Фақат иблис (сажда қилмади). У жинлардан эди. Бас, Парвардигорининг амрига бўйинсунишдан бош тортди. Энди сизлар (эй Одам болалари), Мени қўйиб, уни (яъни, иблисни) ва зурриётларини дўст турурмисиз?! Улар сизларга душман-ку! У (иблис) золим кимсалар учун (Аллоҳнинг ўрнига ибодат қилинадиган) нақадар ёмон «бадал ўринбосардир»'}, {'chapter': 18, 'verse': 51, 'text': '۞مَّآ أَشۡهَدتُّهُمۡ خَلۡقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَا خَلۡقَ أَنفُسِهِمۡ وَمَا كُنتُ مُتَّخِذَ ٱلۡمُضِلِّينَ عَضُدٗا\nМен уларни (иблис ва унинг зурриётини) осмонлар ва Ерни яратишга ва на ўзларини яратишга гувоҳ қилган эмасман (яъни, улар Еру осмонлар қандай яралганини ҳам, ўзлари қандай яралганликларини ҳам мутлақо билмайдилар, буларнинг барчасини ёлғиз Ўзим яратганман), шунингдек, бу йўлдан оздиргувчиларни (Ўзимга) ёрдамчи ҳам қилиб олганим йўқ (бас, сизлар нечун уларга ибодат қилурсизлар)'}, {'chapter': 18, 'verse': 52, 'text': 'وَيَوۡمَ يَقُولُ نَادُواْ شُرَكَآءِيَ ٱلَّذِينَ زَعَمۡتُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَجَعَلۡنَا بَيۡنَهُم مَّوۡبِقٗا\nУ Кунда (Қиёмат қойим бўлганида, Аллоҳ): «Сизлар Менинг шерикларим деб ўйлаган бутларингизни) чақирингиз», дер. Бас, (мушриклар) уларни чорлаганларида, (улар) жавоб қила олмайдилар. (Чунки) Биз уларнинг ўрталарида ҳалокат чоҳини (дўзахни) пайдо қилиб қўйгандирмиз'}, {'chapter': 18, 'verse': 53, 'text': 'وَرَءَا ٱلۡمُجۡرِمُونَ ٱلنَّارَ فَظَنُّوٓاْ أَنَّهُم مُّوَاقِعُوهَا وَلَمۡ يَجِدُواْ عَنۡهَا مَصۡرِفٗا\nГуноҳкор кимсалар дўзахни кўриб, ўзларининг унга тушувчи эканликларини ўйладилар, аммо ундан қочарга жой топмадилар'}, {'chapter': 18, 'verse': 54, 'text': 'وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرۡءَانِ لِلنَّاسِ مِن كُلِّ مَثَلٖۚ وَكَانَ ٱلۡإِنسَٰنُ أَكۡثَرَ شَيۡءٖ جَدَلٗا\nБиз бу Қуръонда одамларга (панд-насиҳат олишлари учун) турли масаллар баён қилдик. Дарҳақиқат, инсон жуда кўп жанжал-баҳс қилгувчидир'}, {'chapter': 18, 'verse': 55, 'text': 'وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُوٓاْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰ وَيَسۡتَغۡفِرُواْ رَبَّهُمۡ إِلَّآ أَن تَأۡتِيَهُمۡ سُنَّةُ ٱلۡأَوَّلِينَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ قُبُلٗا\nОдамларга ҳидоят келган пайтида, улар иймон келтиришларидан ва Парвардигордан мағфират сўрашларидан фақат уларга ўтганларнинг суннати — ҳалокатлари келиши ёки очиқ азоб келишини (кутишларигина) тўсди (яъни, улар аввалги кофир кимсаларга тушган ҳалокатга ё азобга лойиқ эканликларигина уларни мўмин бўлишдан манъ қилди)'}, {'chapter': 18, 'verse': 56, 'text': 'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۚ وَيُجَٰدِلُ ٱلَّذِينَ كَفَرُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّۖ وَٱتَّخَذُوٓاْ ءَايَٰتِي وَمَآ أُنذِرُواْ هُزُوٗا\nБиз пайғамбарларни фақат (мўминларга жаннат) хушхабарини элтувчи ва (кофирларни дўзах азобидан) огоҳлантиргувчи бўлган ҳолларидагина юборурмиз. Кофир бўлган кимсалар эса ботил (ҳужжатлар) билан талашиб-тортишиб, унинг ёрдамида Ҳақни енгмоқчи бўлурлар ҳамда Менинг оятларимни ва ўзлари огоҳлантирилган нарсани (яъни, Охират азобини) масхара қилиб кулурлар'}, {'chapter': 18, 'verse': 57, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ فَأَعۡرَضَ عَنۡهَا وَنَسِيَ مَا قَدَّمَتۡ يَدَاهُۚ إِنَّا جَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۖ وَإِن تَدۡعُهُمۡ إِلَى ٱلۡهُدَىٰ فَلَن يَهۡتَدُوٓاْ إِذًا أَبَدٗا\nПарвардигорининг оятлари билан панд-насиҳат қилингач, улардан юз ўгирган, ўзи қилиб ўтган гуноҳларини унутиб қўйган кимсадан ҳам золимроқ ким бор?! Дарҳақиқат, Биз (Қуръонни) англамасликлари учун уларнинг дилларини пардалаб, қулоқларини оғир қилиб қўйдик. Демак, агар сиз уларни ҳидоят — ҳақ йўлга чақирсангиз ҳам ҳаргиз ҳидоят топмаслар'}, {'chapter': 18, 'verse': 58, 'text': 'وَرَبُّكَ ٱلۡغَفُورُ ذُو ٱلرَّحۡمَةِۖ لَوۡ يُؤَاخِذُهُم بِمَا كَسَبُواْ لَعَجَّلَ لَهُمُ ٱلۡعَذَابَۚ بَل لَّهُم مَّوۡعِدٞ لَّن يَجِدُواْ مِن دُونِهِۦ مَوۡئِلٗا\nПарвардигорингиз мағфиратли, меҳр-шафқат Соҳибидир. Агар уларни (кофирларни) қилган гуноҳлари билан ушлаганида, уларга азобни нақд қилган бўлур эди. Йўқ, улар учун ваъда қилинган бир вақт (Қиёмат) борки, (у вақтда Аллоҳдан) ўзга бирон пуштипаноҳ топа олмаслар'}, {'chapter': 18, 'verse': 59, 'text': 'وَتِلۡكَ ٱلۡقُرَىٰٓ أَهۡلَكۡنَٰهُمۡ لَمَّا ظَلَمُواْ وَجَعَلۡنَا لِمَهۡلِكِهِم مَّوۡعِدٗا\nАна у шаҳарларни (уларнинг аҳолисини, улар) зулм қилишгач, ҳалокат вақтларини аниқ белгилаб (яъни, пайғамбарларингизга итоат қилмасангизлар, ҳалок бўлурсизлар, деб) қўйган ҳолимизда, ҳалок қилдик'}, {'chapter': 18, 'verse': 60, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِفَتَىٰهُ لَآ أَبۡرَحُ حَتَّىٰٓ أَبۡلُغَ مَجۡمَعَ ٱلۡبَحۡرَيۡنِ أَوۡ أَمۡضِيَ حُقُبٗا\nМусо (ўзининг хизматкор) йигитига: «То икки денгиз қўшиладиган ерга етмагунча ёки узоқ замонлар кезмагунча юришдан тўхтамайман», деган пайтини эслангиз'}, {'chapter': 18, 'verse': 61, 'text': 'فَلَمَّا بَلَغَا مَجۡمَعَ بَيۡنِهِمَا نَسِيَا حُوتَهُمَا فَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ سَرَبٗا\nЭнди қачонки, у иккиси (икки денгиз) қўшиладиган ерга етишгач, балиқларини унутдилар. Бас, у (балиқ) денгизга қараб йўл олди'}, {'chapter': 18, 'verse': 62, 'text': 'فَلَمَّا جَاوَزَا قَالَ لِفَتَىٰهُ ءَاتِنَا غَدَآءَنَا لَقَدۡ لَقِينَا مِن سَفَرِنَا هَٰذَا نَصَبٗا\nБас, қачонки (у ердан) ўтишгач, (Мусо) йигитига: «Нонуштамизни келтир, ҳақиқатан бу сафаримиздан жуда чарчадик», деган эди'}, {'chapter': 18, 'verse': 63, 'text': 'قَالَ أَرَءَيۡتَ إِذۡ أَوَيۡنَآ إِلَى ٱلصَّخۡرَةِ فَإِنِّي نَسِيتُ ٱلۡحُوتَ وَمَآ أَنسَىٰنِيهُ إِلَّا ٱلشَّيۡطَٰنُ أَنۡ أَذۡكُرَهُۥۚ وَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ عَجَبٗا\nУ (хизматкор) айтди: «Буни кўринг, биз (денгиз соҳилидаги) қояга бориб (ором олган) пайтимизда, мен балиқни (унга жон кириб саватдан тушиб кетганини) унутибман. (Бу воқеани сизга) айтишни фақат шайтон ёдимдан чиқарди. У (балиқ) денгизга ажойиб (сездирмаган йўсинда) йўл олган эди»'}, {'chapter': 18, 'verse': 64, 'text': 'قَالَ ذَٰلِكَ مَا كُنَّا نَبۡغِۚ فَٱرۡتَدَّا عَلَىٰٓ ءَاثَارِهِمَا قَصَصٗا\n(Мусо) деди: «Мана шу биз истаган нарсадир». Сўнг уни (Хизирни) излаб ортларига қайтдилар'}, {'chapter': 18, 'verse': 65, 'text': 'فَوَجَدَا عَبۡدٗا مِّنۡ عِبَادِنَآ ءَاتَيۡنَٰهُ رَحۡمَةٗ مِّنۡ عِندِنَا وَعَلَّمۡنَٰهُ مِن لَّدُنَّا عِلۡمٗا\nБас, бандаларимиздан бир бандани топдиларки, Биз унга Ўз даргоҳимиздан раҳмат (яъни, пайғамбарлик) ато этган ва Ўз ҳузуримиздан илм берган эдик'}, {'chapter': 18, 'verse': 66, 'text': 'قَالَ لَهُۥ مُوسَىٰ هَلۡ أَتَّبِعُكَ عَلَىٰٓ أَن تُعَلِّمَنِ مِمَّا عُلِّمۡتَ رُشۡدٗا\nМусо унга: «Сенга билдирилган билимдан, менга ҳам Тўғри Йўлни таълим беришинг учун сенга эргашсам майлими?», деди'}, {'chapter': 18, 'verse': 67, 'text': 'قَالَ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا\nУ (Хизир) айтди: «Аниқки, сен мен билан бирга (илм машаққатларига) сабр қилишга ҳаргиз тоқатинг етмас'}, {'chapter': 18, 'verse': 68, 'text': 'وَكَيۡفَ تَصۡبِرُ عَلَىٰ مَا لَمۡ تُحِطۡ بِهِۦ خُبۡرٗا\n(Зотан) ўзинг эгаллаб олмаган — хабардор бўлмаган нарсага қандай сабр қилурсан?!»'}, {'chapter': 18, 'verse': 69, 'text': 'قَالَ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ صَابِرٗا وَلَآ أَعۡصِي لَكَ أَمۡرٗا\n(Мусо) деди: «Иншоаллоҳ, сен менинг сабр-тоқатли эканимни кўрурсан. Мен бирон ишда сенга осийлик итоатсизлик қилмасман»'}, {'chapter': 18, 'verse': 70, 'text': 'قَالَ فَإِنِ ٱتَّبَعۡتَنِي فَلَا تَسۡـَٔلۡنِي عَن شَيۡءٍ حَتَّىٰٓ أُحۡدِثَ لَكَ مِنۡهُ ذِكۡرٗا\nУ (Хизир) айтди: «Бас, агар менга эргашсанг, то ўзим сенга айтмагунимча бирон нарса ҳақида мендан сўрамагин!»'}, {'chapter': 18, 'verse': 71, 'text': 'فَٱنطَلَقَا حَتَّىٰٓ إِذَا رَكِبَا فِي ٱلسَّفِينَةِ خَرَقَهَاۖ قَالَ أَخَرَقۡتَهَا لِتُغۡرِقَ أَهۡلَهَا لَقَدۡ جِئۡتَ شَيۡـًٔا إِمۡرٗا\nБас, икков йўлга тушдилар. То бориб бир кемага минишлари билан (Хизир кемани) тешиб қўйди. (Мусо) айтди: «Уни одамларни ғарқ қилиш учун тешдингми?!»'}, {'chapter': 18, 'verse': 72, 'text': 'قَالَ أَلَمۡ أَقُلۡ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا\nУ (Хизир) деди: «Аниқки, сен мен билан бирга сабр қилишга ҳаргиз тоқатинг етмас, демаганмидим?!»'}, {'chapter': 18, 'verse': 73, 'text': 'قَالَ لَا تُؤَاخِذۡنِي بِمَا نَسِيتُ وَلَا تُرۡهِقۡنِي مِنۡ أَمۡرِي عُسۡرٗا\n(Мусо) айтди: «Унутганим сабабли мени айбламагин ва бу ишим учун мени машаққатга дучор қилмагин»'}, {'chapter': 18, 'verse': 74, 'text': 'فَٱنطَلَقَا حَتَّىٰٓ إِذَا لَقِيَا غُلَٰمٗا فَقَتَلَهُۥ قَالَ أَقَتَلۡتَ نَفۡسٗا زَكِيَّةَۢ بِغَيۡرِ نَفۡسٖ لَّقَدۡ جِئۡتَ شَيۡـٔٗا نُّكۡرٗا\nСўнг яна йўлга тушдилар. То бориб бир болага рўбарў бўлганларида (Хизир) уни ўлдирди. (Буни кўрган Мусо) деди: «Бировни ўлдирмаган бир бегуноҳ жонни ўлдирдинг-а. Дарҳақиқат, (сен) бузуқ иш қилдинг!»'}, {'chapter': 18, 'verse': 75, 'text': '۞قَالَ أَلَمۡ أَقُل لَّكَ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا\nУ (Хизир) деди: «Мен сенга, сен мен билан бирга сабр қилишга ҳаргиз тоқатинг етмас, демаганмидим?!»'}, {'chapter': 18, 'verse': 76, 'text': 'قَالَ إِن سَأَلۡتُكَ عَن شَيۡءِۭ بَعۡدَهَا فَلَا تُصَٰحِبۡنِيۖ قَدۡ بَلَغۡتَ مِن لَّدُنِّي عُذۡرٗا\n(Мусо) деди: «Агар бундан буён сендан бирон нарса ҳақида сўрасам, мени ўзингга ҳамроҳ қилмагин. Чунки (у ҳолда) мен томонимдан узрга етган бўлурсан (яъни, у ҳолда мени ташлаб кетганинг учун сени айблай олмасман)»'}, {'chapter': 18, 'verse': 77, 'text': 'فَٱنطَلَقَا حَتَّىٰٓ إِذَآ أَتَيَآ أَهۡلَ قَرۡيَةٍ ٱسۡتَطۡعَمَآ أَهۡلَهَا فَأَبَوۡاْ أَن يُضَيِّفُوهُمَا فَوَجَدَا فِيهَا جِدَارٗا يُرِيدُ أَن يَنقَضَّ فَأَقَامَهُۥۖ قَالَ لَوۡ شِئۡتَ لَتَّخَذۡتَ عَلَيۡهِ أَجۡرٗا\nСўнг (улар) яна йўлга тушдилар. То бир шаҳар аҳлининг олдига келиб улардан таом сўраган эдилар, улар меҳмон қилишдан бош тортишди. Кейин ўша жойда йиқилай деб турган бир деворни кўришгач, (Хизир) уни тиклаб қўйди. (Мусо) деди: «Агар хоҳласанг, бу ишинг учун ҳақ олишинг мумкин эди»'}, {'chapter': 18, 'verse': 78, 'text': 'قَالَ هَٰذَا فِرَاقُ بَيۡنِي وَبَيۡنِكَۚ سَأُنَبِّئُكَ بِتَأۡوِيلِ مَا لَمۡ تَسۡتَطِع عَّلَيۡهِ صَبۡرًا\nУ (Хизир) айтди: «Мана шу сен билан менинг ажрашимиздир. Энди мен сени сабр қилишга тоқатинг етмаган нарсаларнинг таъвили (шарҳи)дан огоҳ қилурман'}, {'chapter': 18, 'verse': 79, 'text': 'أَمَّا ٱلسَّفِينَةُ فَكَانَتۡ لِمَسَٰكِينَ يَعۡمَلُونَ فِي ٱلۡبَحۡرِ فَأَرَدتُّ أَنۡ أَعِيبَهَا وَكَانَ وَرَآءَهُم مَّلِكٞ يَأۡخُذُ كُلَّ سَفِينَةٍ غَصۡبٗا\nКема хусусига келсак, у денгизда ишлайдиган (юрадиган) мискин-бечораларники эди. Бас, мен уни айбли қилиб қўймоқчи бўлдим. (Чунки) уларнинг (кемадагиларнинг) ортида (кўзлаб кетаётган манзилларида) барча (бутун) кемаларни талон-торож қилиб, тортиб олгувчи бир подшоҳ бор эди'}, {'chapter': 18, 'verse': 80, 'text': 'وَأَمَّا ٱلۡغُلَٰمُ فَكَانَ أَبَوَاهُ مُؤۡمِنَيۡنِ فَخَشِينَآ أَن يُرۡهِقَهُمَا طُغۡيَٰنٗا وَكُفۡرٗا\nҲалиги боланинг эса ота-оналари мўмин кишилар эдилар. Бас, биз у (бола) туғён ва куфр билан уларни қийнаб қўйишидан қўрқдик'}, {'chapter': 18, 'verse': 81, 'text': 'فَأَرَدۡنَآ أَن يُبۡدِلَهُمَا رَبُّهُمَا خَيۡرٗا مِّنۡهُ زَكَوٰةٗ وَأَقۡرَبَ رُحۡمٗا\nШу сабабдан, уларга Парвардигор (бола)дан кўра покизароқ, ундан кўра меҳрибонроқ (бошқа бир болани) бадал-эваз қилиб беришини истадик'}, {'chapter': 18, 'verse': 82, 'text': 'وَأَمَّا ٱلۡجِدَارُ فَكَانَ لِغُلَٰمَيۡنِ يَتِيمَيۡنِ فِي ٱلۡمَدِينَةِ وَكَانَ تَحۡتَهُۥ كَنزٞ لَّهُمَا وَكَانَ أَبُوهُمَا صَٰلِحٗا فَأَرَادَ رَبُّكَ أَن يَبۡلُغَآ أَشُدَّهُمَا وَيَسۡتَخۡرِجَا كَنزَهُمَا رَحۡمَةٗ مِّن رَّبِّكَۚ وَمَا فَعَلۡتُهُۥ عَنۡ أَمۡرِيۚ ذَٰلِكَ تَأۡوِيلُ مَا لَمۡ تَسۡطِع عَّلَيۡهِ صَبۡرٗا\nЭнди девор эса шу шаҳардаги икки етим боланики бўлиб, унинг остида улар учун бир хазина бор эди. Уларнинг оталари жуда яхши киши эди. Бас, Парвардигоринг улар вояга етиб, Парвардигорингнинг раҳмат-марҳамати бўлмиш хазиналарини чиқариб олишларини ирода қилди. Мен бу (ишларнинг биронтасини) ўз-ўзимча қилганим йўқ. Мана шу сен сабр қилишга тоқатинг етмаган нарсаларнинг таъвилидир»'}, {'chapter': 18, 'verse': 83, 'text': 'وَيَسۡـَٔلُونَكَ عَن ذِي ٱلۡقَرۡنَيۡنِۖ قُلۡ سَأَتۡلُواْ عَلَيۡكُم مِّنۡهُ ذِكۡرًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), яна сиздан Зул-қарнайн ҳақида сўрайдилар. Айтинг: «Энди мен сизларга у ҳақдаги хабарни тиловат қилурман»'}, {'chapter': 18, 'verse': 84, 'text': 'إِنَّا مَكَّنَّا لَهُۥ فِي ٱلۡأَرۡضِ وَءَاتَيۡنَٰهُ مِن كُلِّ شَيۡءٖ سَبَبٗا\nДарҳақиқат, Биз унга (Зул-қарнайнга) бу ерда салтанат – ҳукмронлик бердик ва (кўзлаган) барча нарсасига йўл-имконият ато этдик'}, {'chapter': 18, 'verse': 85, 'text': 'فَأَتۡبَعَ سَبَبًا\nБас, у (аввал Ғарбга қараб) йўл олди'}, {'chapter': 18, 'verse': 86, 'text': 'حَتَّىٰٓ إِذَا بَلَغَ مَغۡرِبَ ٱلشَّمۡسِ وَجَدَهَا تَغۡرُبُ فِي عَيۡنٍ حَمِئَةٖ وَوَجَدَ عِندَهَا قَوۡمٗاۖ قُلۡنَا يَٰذَا ٱلۡقَرۡنَيۡنِ إِمَّآ أَن تُعَذِّبَ وَإِمَّآ أَن تَتَّخِذَ فِيهِمۡ حُسۡنٗا\nТо (кетаётиб) кун ботадиган жойга етгач, у (қуёшнинг) бир лойқа булоққа ботаётганини кўрди ва у (булоқ) олдида бир қавмни учратди. Биз: «Эй Зул-қарнайн, ё (уларни) азобга дучор қилурсан, ёки уларга яхши муомалада бўлурсан», дедик'}, {'chapter': 18, 'verse': 87, 'text': 'قَالَ أَمَّا مَن ظَلَمَ فَسَوۡفَ نُعَذِّبُهُۥ ثُمَّ يُرَدُّ إِلَىٰ رَبِّهِۦ فَيُعَذِّبُهُۥ عَذَابٗا نُّكۡرٗا\nУ айтди: «Золим бўлган кимсани, албатта, азоблагаймиз. Сўнгра Парвардигорига қайтарилгач, У зот уни яна даҳшатли азоб билан азоблар'}, {'chapter': 18, 'verse': 88, 'text': 'وَأَمَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَلَهُۥ جَزَآءً ٱلۡحُسۡنَىٰۖ وَسَنَقُولُ لَهُۥ مِنۡ أَمۡرِنَا يُسۡرٗا\nЭнди иймон келтириб яхши амаллар қилган зотга келсак, унинг учун гўзал оқибат — жаннат мукофот бўлур. Биз ҳам унга ишимиздан осон-енгилларини буюрурмиз»'}, {'chapter': 18, 'verse': 89, 'text': 'ثُمَّ أَتۡبَعَ سَبَبًا\nСўнгра, у (Шарққа қараб) йўл олди'}, {'chapter': 18, 'verse': 90, 'text': 'حَتَّىٰٓ إِذَا بَلَغَ مَطۡلِعَ ٱلشَّمۡسِ وَجَدَهَا تَطۡلُعُ عَلَىٰ قَوۡمٖ لَّمۡ نَجۡعَل لَّهُم مِّن دُونِهَا سِتۡرٗا\nТо (кетаётиб) кун чиқишга етиб боргач, унинг (қуёшнинг) бир қавм устига чиқаётганини (кўтарилаётганини) кўрдики, Биз у (қавм) учун қуёшдан (сақланадиган уй либос каби) бирон парда қилмаган эдик'}, {'chapter': 18, 'verse': 91, 'text': 'كَذَٰلِكَۖ وَقَدۡ أَحَطۡنَا بِمَا لَدَيۡهِ خُبۡرٗا\nХудди илгаригидек бўлди (яъни, Зул-қарнайн бу қавмга ҳам кун ботишдаги қавмга қилган муомалани қилди). Аниқки, Биз унинг барча ишларидан хабардормиз — (уни) иҳота қилиб олгандирмиз'}, {'chapter': 18, 'verse': 92, 'text': 'ثُمَّ أَتۡبَعَ سَبَبًا\nСўнгра, у яна йўл олди'}, {'chapter': 18, 'verse': 93, 'text': 'حَتَّىٰٓ إِذَا بَلَغَ بَيۡنَ ٱلسَّدَّيۡنِ وَجَدَ مِن دُونِهِمَا قَوۡمٗا لَّا يَكَادُونَ يَفۡقَهُونَ قَوۡلٗا\nТо (кетаётиб) икки тоғ ўртасига етиб келгач, у (тоғлар ортида) бирон гапни англай олмайдиган қавмни учратди'}, {'chapter': 18, 'verse': 94, 'text': 'قَالُواْ يَٰذَا ٱلۡقَرۡنَيۡنِ إِنَّ يَأۡجُوجَ وَمَأۡجُوجَ مُفۡسِدُونَ فِي ٱلۡأَرۡضِ فَهَلۡ نَجۡعَلُ لَكَ خَرۡجًا عَلَىٰٓ أَن تَجۡعَلَ بَيۡنَنَا وَبَيۡنَهُمۡ سَدّٗا\nУлар: «Эй Зул-қарнайн, шак-шубҳасиз, (шу тоғлар ортидаги) Яъжуж ва Маъжуж (қабилалари) Ер юзида бузғунчилик қилгувчилардир. Бизлар сенга бир (миқдор) тўлов тўласак, (билан уларнинг ўртасига бир сад чекиб (бир тўғон қуриб) берурмисан?», дедилар'}, {'chapter': 18, 'verse': 95, 'text': 'قَالَ مَا مَكَّنِّي فِيهِ رَبِّي خَيۡرٞ فَأَعِينُونِي بِقُوَّةٍ أَجۡعَلۡ بَيۡنَكُمۡ وَبَيۡنَهُمۡ رَدۡمًا\nУ (Зул-қарнайн) айтди: «Парвардигорим менга ато этган (салтанат) сизлар берадиган (мол-дунёдан) яхшироқдир. Бас, сизлар менга (мол-дунё билан эмас, балки) қувват билан ёрдам беринглар, мен сизлар билан уларнинг ўртасига бир девор бино қилай'}, {'chapter': 18, 'verse': 96, 'text': 'ءَاتُونِي زُبَرَ ٱلۡحَدِيدِۖ حَتَّىٰٓ إِذَا سَاوَىٰ بَيۡنَ ٱلصَّدَفَيۡنِ قَالَ ٱنفُخُواْۖ حَتَّىٰٓ إِذَا جَعَلَهُۥ نَارٗا قَالَ ءَاتُونِيٓ أُفۡرِغۡ عَلَيۡهِ قِطۡرٗا\nСизлар менга темир парчаларини келтиринглар». То (темир парчалари) иккала тоғ билан баробар бўлгач, (Зул-қарнайн: «(Босқонлар билан) дам уринглар», деди. Бас, қачон у (темир-терсакларни қиздириб) ўт қилгач (эритгач), деди: «Менга эритилган мис (ҳам) келтиринглар, уни (темир парчаларининг) устидан қуюрман'}, {'chapter': 18, 'verse': 97, 'text': 'فَمَا ٱسۡطَٰعُوٓاْ أَن يَظۡهَرُوهُ وَمَا ٱسۡتَطَٰعُواْ لَهُۥ نَقۡبٗا\nЭнди улар у (тўсиқ) устига чиқишга ҳам, уни тешиб ўтишга ҳам қодир эмаслар»'}, {'chapter': 18, 'verse': 98, 'text': 'قَالَ هَٰذَا رَحۡمَةٞ مِّن رَّبِّيۖ فَإِذَا جَآءَ وَعۡدُ رَبِّي جَعَلَهُۥ دَكَّآءَۖ وَكَانَ وَعۡدُ رَبِّي حَقّٗا\n«Бу Парвардигорим томонидан бўлган бир марҳаматдир. Энди қачон Парвардигорим (Яъжуж ва Маъжуж чиқади, деб) ваъда қилган вақт келганида (яъни, Қиёмат қойим бўлишига яқин қолганида), Ўзи у (тўсиқни) теп-текис қилиб қўюр. Парвардигоримнинг ваъдаси ҳақдир», деди у'}, {'chapter': 18, 'verse': 99, 'text': '۞وَتَرَكۡنَا بَعۡضَهُمۡ يَوۡمَئِذٖ يَمُوجُ فِي بَعۡضٖۖ وَنُفِخَ فِي ٱلصُّورِ فَجَمَعۡنَٰهُمۡ جَمۡعٗا\nУ Кунда (Қиёматда, одамларни) бир-бирларига аралаш-қуралаш қилиб ташладик. Сур чалингач, уларнинг барчаларини тўпладик'}, {'chapter': 18, 'verse': 100, 'text': 'وَعَرَضۡنَا جَهَنَّمَ يَوۡمَئِذٖ لِّلۡكَٰفِرِينَ عَرۡضًا\nУ Кунда кофирлар учун жаҳаннамни кўндаланг қилиб қўйдик'}, {'chapter': 18, 'verse': 101, 'text': 'ٱلَّذِينَ كَانَتۡ أَعۡيُنُهُمۡ فِي غِطَآءٍ عَن ذِكۡرِي وَكَانُواْ لَا يَسۡتَطِيعُونَ سَمۡعًا\nУларнинг кўзлари (дунёда) Менинг эслатмаларимдан тўсилган эди. Улар (Менинг панд-насиҳатларимни) эшитишга ҳам қодир эмас эдилар'}, {'chapter': 18, 'verse': 102, 'text': 'أَفَحَسِبَ ٱلَّذِينَ كَفَرُوٓاْ أَن يَتَّخِذُواْ عِبَادِي مِن دُونِيٓ أَوۡلِيَآءَۚ إِنَّآ أَعۡتَدۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ نُزُلٗا\nЁки кофир бўлган кимсалар, Мени қўйиб, бандаларимни дўст тутишни (Ўзим яратган фаришталар ёки Ийсо каби пайғамбарни илоҳ деб сиғинишни) ўйладиларми?! Шак-шубҳасиз, Биз жаҳаннамни кофирлар тушадиган жой қилиб қўйгандирмиз'}, {'chapter': 18, 'verse': 103, 'text': 'قُلۡ هَلۡ نُنَبِّئُكُم بِٱلۡأَخۡسَرِينَ أَعۡمَٰلًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Сизларга қилган иш-амалларидан энг кўп зиён кўргувчи кимсаларнинг хабарини берайликми'}, {'chapter': 18, 'verse': 104, 'text': 'ٱلَّذِينَ ضَلَّ سَعۡيُهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَهُمۡ يَحۡسَبُونَ أَنَّهُمۡ يُحۡسِنُونَ صُنۡعًا\nУлар (кофир бўлганлари сабабли) қилган саъй-ҳаракатлари ҳаёти дунёдаёқ йўқ бўлиб кетган-у, аммо (нодонликлари сабабли) ўзларини чиройли — яхши амал қилаётган кишилар, деб ҳисоблайдиган кимсалардир!»'}, {'chapter': 18, 'verse': 105, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَلِقَآئِهِۦ فَحَبِطَتۡ أَعۡمَٰلُهُمۡ فَلَا نُقِيمُ لَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ وَزۡنٗا\nУлар Парвардигорларининг оятларини ва У зотга рўбарў бўлишни инкор қилишиб, бутун иш-амаллари беҳуда кетган кимсалардир. Бас, Биз Қиёмат Кунида улар (қилиб ўтган амаллар) учун ҳеч қандай қадр-қиймат бермасмиз'}, {'chapter': 18, 'verse': 106, 'text': 'ذَٰلِكَ جَزَآؤُهُمۡ جَهَنَّمُ بِمَا كَفَرُواْ وَٱتَّخَذُوٓاْ ءَايَٰتِي وَرُسُلِي هُزُوًا\nКофир бўлганлари ҳамда Менинг оятларимни ва пайғамбарларимни масхара қилганлари сабабли, уларнинг жазолари ўша жаҳаннамдир'}, {'chapter': 18, 'verse': 107, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ كَانَتۡ لَهُمۡ جَنَّـٰتُ ٱلۡفِرۡدَوۡسِ نُزُلًا\nАлбатта, иймон келтирган ва яхши амаллар қилган зотлар учун Фирдавс боғлари манзил бўлур'}, {'chapter': 18, 'verse': 108, 'text': 'خَٰلِدِينَ فِيهَا لَا يَبۡغُونَ عَنۡهَا حِوَلٗا\nУлар, у жойларда мангу қолар эканлар, (бошқа бирон жойга) кўчишни истамаслар'}, {'chapter': 18, 'verse': 109, 'text': 'قُل لَّوۡ كَانَ ٱلۡبَحۡرُ مِدَادٗا لِّكَلِمَٰتِ رَبِّي لَنَفِدَ ٱلۡبَحۡرُ قَبۡلَ أَن تَنفَدَ كَلِمَٰتُ رَبِّي وَلَوۡ جِئۡنَا بِمِثۡلِهِۦ مَدَدٗا\nАйтинг: «Агар барча денгиз Парвардигоримнинг сўзлари (яъни, илму ҳикматларини битиш) учун сиёҳ бўлса ва яна шунча сиёҳ келтирсак ҳам, Парвардигоримнинг сўзлари битишидан илгари, у денгизлар тугаб битар!»'}, {'chapter': 18, 'verse': 110, 'text': 'قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ فَمَن كَانَ يَرۡجُواْ لِقَآءَ رَبِّهِۦ فَلۡيَعۡمَلۡ عَمَلٗا صَٰلِحٗا وَلَا يُشۡرِكۡ بِعِبَادَةِ رَبِّهِۦٓ أَحَدَۢا\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Ҳеч шак-шубҳа йўқки, мен ҳам сизлар каби бир одамдирман. Менга Аллоҳингиз ёлғиз Аллоҳнинг Ўзи экани ваҳий этилмоқда. Бас, ким Парвардигорига рўбарў бўлишидан умидвор бўлса, у ҳолда яхши амал қилсин ва Парвардигорига бандалик қилишда бирон кимсани (унга) шерик қилмасин! (Яъни, қиладиган барча амалларини ёлғиз Аллоҳ учун қилсин)»'}, {'chapter': 19, 'verse': 1, 'text': 'كٓهيعٓصٓ\nКоф, Ҳо, Ё, Айн, Сод'}, {'chapter': 19, 'verse': 2, 'text': 'ذِكۡرُ رَحۡمَتِ رَبِّكَ عَبۡدَهُۥ زَكَرِيَّآ\n(Эй Муҳаммад алайҳис-салоту вас-салом, ушбу) Парвардигорингиз Ўз бандаси Закариёга қилган марҳаматининг зикри-қиссасидир'}, {'chapter': 19, 'verse': 3, 'text': 'إِذۡ نَادَىٰ رَبَّهُۥ نِدَآءً خَفِيّٗا\nЭсланг, Парвардигорига хуфёна дуо-илтижо қилиб, (деган эди)'}, {'chapter': 19, 'verse': 4, 'text': 'قَالَ رَبِّ إِنِّي وَهَنَ ٱلۡعَظۡمُ مِنِّي وَٱشۡتَعَلَ ٱلرَّأۡسُ شَيۡبٗا وَلَمۡ أَكُنۢ بِدُعَآئِكَ رَبِّ شَقِيّٗا\n(Закариё) деган эди: «Парвардигорим, дарҳақиқат, менинг суякларим мўртлашди, кексаликдан бошим — сочим оқарди. Парвардигорим, мен сенга дуо қилиб (ҳеч қачон) ноумид бўлган эмасман'}, {'chapter': 19, 'verse': 5, 'text': 'وَإِنِّي خِفۡتُ ٱلۡمَوَٰلِيَ مِن وَرَآءِي وَكَانَتِ ٱمۡرَأَتِي عَاقِرٗا فَهَبۡ لِي مِن لَّدُنكَ وَلِيّٗا\nМен ортимда қоладиган қариндош-уруғларим (менинг динимни зое қилиб юборишлари)дан хавфдаман. Хотиним туғмас бўлиб қолган. Энди Сен (менга) ўз даргоҳингдан менга бир дўст (яъни, фарзанд) ҳадя этгин'}, {'chapter': 19, 'verse': 6, 'text': 'يَرِثُنِي وَيَرِثُ مِنۡ ءَالِ يَعۡقُوبَۖ وَٱجۡعَلۡهُ رَبِّ رَضِيّٗا\nМенга ва (бобом) Яъқуб хонадонига меросхўр бўладиган (бир дўст яъни, фарзанд ҳадя этгин) ва уни (Ўз наздингда) рози бўлинадиган (солиҳ) кишилардан қилгин»'}, {'chapter': 19, 'verse': 7, 'text': 'يَٰزَكَرِيَّآ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ ٱسۡمُهُۥ يَحۡيَىٰ لَمۡ نَجۡعَل لَّهُۥ مِن قَبۡلُ سَمِيّٗا\n(Аллоҳ айтди): «Эй Закариё, Биз сенга бир фарзанд хушхабарини берурмизки, унинг исми Яҳё бўлиб, илгари унга бирон (кимсани) отдош қилган эмасмиз»'}, {'chapter': 19, 'verse': 8, 'text': 'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَكَانَتِ ٱمۡرَأَتِي عَاقِرٗا وَقَدۡ بَلَغۡتُ مِنَ ٱلۡكِبَرِ عِتِيّٗا\n(Закариё) деди: «Парвардигорим, хотиним туғмас бўлиб қолган, ўзим эса кексаликнинг (умримнинг) ниҳоясига етиб қолган бўлсам, мендан қандай фарзанд (бунёд) бўлсин?»'}, {'chapter': 19, 'verse': 9, 'text': 'قَالَ كَذَٰلِكَ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٞ وَقَدۡ خَلَقۡتُكَ مِن قَبۡلُ وَلَمۡ تَكُ شَيۡـٔٗا\n(Аллоҳ) айтди: «Шундай, Парвардигоринг айтурки, бу (иш) Менга осондир. (Ахир) Мен илгари ҳеч нарса бўлмаган пайтингда сени (ҳам) яратган эдим-ку (яъни, йўқдан бор қилган эдим-ку)!»'}, {'chapter': 19, 'verse': 10, 'text': 'قَالَ رَبِّ ٱجۡعَل لِّيٓ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَ لَيَالٖ سَوِيّٗا\n«Парвардигорим, мен учун (кампирим ҳомилали бўлганига) бирон белги-аломат қилсанг», деди у (Закариё). (Аллоҳ) айтди: «Сенга белги шуки, соппа-соғ бўлган ҳолингда уч кунгача одамларга гапира олмайсан»'}, {'chapter': 19, 'verse': 11, 'text': 'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ مِنَ ٱلۡمِحۡرَابِ فَأَوۡحَىٰٓ إِلَيۡهِمۡ أَن سَبِّحُواْ بُكۡرَةٗ وَعَشِيّٗا\nБас, (Закариё) меҳробдан қавмининг олдига чиқиб, уларга эрта-ю кеч (Аллоҳни) поклаб — ибодат этишни ишора қилди'}, {'chapter': 19, 'verse': 12, 'text': 'يَٰيَحۡيَىٰ خُذِ ٱلۡكِتَٰبَ بِقُوَّةٖۖ وَءَاتَيۡنَٰهُ ٱلۡحُكۡمَ صَبِيّٗا\n(Биз унга): «Эй Яҳё, Китобни (яъни, Тавротни) маҳкам ушлагин», (дедик) ва унга гўдаклик чоғидаёқ ҳикмат-маърифат ато этдик'}, {'chapter': 19, 'verse': 13, 'text': 'وَحَنَانٗا مِّن لَّدُنَّا وَزَكَوٰةٗۖ وَكَانَ تَقِيّٗا\nЯна унга Ўз даргоҳимиздан меҳр ва поклик (ато этдик). У тақволи бўлди'}, {'chapter': 19, 'verse': 14, 'text': 'وَبَرَّۢا بِوَٰلِدَيۡهِ وَلَمۡ يَكُن جَبَّارًا عَصِيّٗا\nУ ота-онасига меҳрибон бўлиб, жабру ситам қилувчи, қулоқсиз эмас эди'}, {'chapter': 19, 'verse': 15, 'text': 'وَسَلَٰمٌ عَلَيۡهِ يَوۡمَ وُلِدَ وَيَوۡمَ يَمُوتُ وَيَوۡمَ يُبۡعَثُ حَيّٗا\nУнга туғилган кунида ҳам, вафот этадиган кунида ҳам, қайта тириладиган (Қиёмат) кунида ҳам тинчлик-омонлик бўлур (яъни, у туғилиш машаққатидан, ўлим азобидан ва Қиёмат Кунида дўзахга тушиш хавфидан холидир)'}, {'chapter': 19, 'verse': 16, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ مَرۡيَمَ إِذِ ٱنتَبَذَتۡ مِنۡ أَهۡلِهَا مَكَانٗا شَرۡقِيّٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), ушбу Китобда (яъни, Қуръонда) Марямни зикр қилинг: у ўз аҳли-оиласидан четга — кун чиқар томонга борди'}, {'chapter': 19, 'verse': 17, 'text': 'فَٱتَّخَذَتۡ مِن دُونِهِمۡ حِجَابٗا فَأَرۡسَلۡنَآ إِلَيۡهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرٗا سَوِيّٗا\nВа улардан беркиниб олган пайтида, Биз унга ўз руҳимиз — (яъни, Жаброил)ни юбордик. Бас, у (Марямга) бус-бутун (рўй-рост) одам бўлиб кўринди'}, {'chapter': 19, 'verse': 18, 'text': 'قَالَتۡ إِنِّيٓ أَعُوذُ بِٱلرَّحۡمَٰنِ مِنكَ إِن كُنتَ تَقِيّٗا\n(Марям унга): «Мен Раҳмонга (яъни, Аллоҳга) сиғиниб, сендан паноҳ беришни илтижо қилурман. Агар (Аллоҳдан) қўрқувчи бўлсанг (менга зиён етказмагин)», деди'}, {'chapter': 19, 'verse': 19, 'text': 'قَالَ إِنَّمَآ أَنَا۠ رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلَٰمٗا زَكِيّٗا\n(Жаброил) айтди: «(Қўрқмагин), мен фақат сенга бир покиза ўғил ҳадя қилиш учун (келган) Парвардигорингнинг элчисидирман, холос»'}, {'chapter': 19, 'verse': 20, 'text': 'قَالَتۡ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞ وَلَمۡ أَكُ بَغِيّٗا\n(Марям) деди: «Менга одамзод тегмаган бўлса, бузуқ аёл бўлмасам, менда қаёқдан фарзанд бўлсин?!»'}, {'chapter': 19, 'verse': 21, 'text': 'قَالَ كَذَٰلِكِ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٞۖ وَلِنَجۡعَلَهُۥٓ ءَايَةٗ لِّلنَّاسِ وَرَحۡمَةٗ مِّنَّاۚ وَكَانَ أَمۡرٗا مَّقۡضِيّٗا\n(Жаброил) айтди: «Шундай. Парвардигоринг айтурки, бу (иш) Менга осондир. Биз у (болани) одамлар учун оят-мўъжиза ва Ўз томонимиздан бўлган раҳмат-марҳамат қилурмиз. Бу битган ишдир'}, {'chapter': 19, 'verse': 22, 'text': '۞فَحَمَلَتۡهُ فَٱنتَبَذَتۡ بِهِۦ مَكَانٗا قَصِيّٗا\nБас, (Марям) унга ҳомиладор бўлиб, у билан бирга йироқ бир жойга кетди'}, {'chapter': 19, 'verse': 23, 'text': 'فَأَجَآءَهَا ٱلۡمَخَاضُ إِلَىٰ جِذۡعِ ٱلنَّخۡلَةِ قَالَتۡ يَٰلَيۡتَنِي مِتُّ قَبۡلَ هَٰذَا وَكُنتُ نَسۡيٗا مَّنسِيّٗا\nБас, тўлғоқ азоби уни бир хурмо дарахтининг шохига олиб борди (ва у шохга осилган ҳолда кўзи ёригач), деди: «Қани, мана шу (кундан) илгари ўлиб кетсам-у, бутунлай унутилиб кетсам эди»'}, {'chapter': 19, 'verse': 24, 'text': 'فَنَادَىٰهَا مِن تَحۡتِهَآ أَلَّا تَحۡزَنِي قَدۡ جَعَلَ رَبُّكِ تَحۡتَكِ سَرِيّٗا\nШунда (хурмо дарахтининг) ортидан (Жаброил) нидо қилди: «Ғамгин бўлма, Парвардигоринг (оёқ) остингдан бир ариқ оқизиб қўйди'}, {'chapter': 19, 'verse': 25, 'text': 'وَهُزِّيٓ إِلَيۡكِ بِجِذۡعِ ٱلنَّخۡلَةِ تُسَٰقِطۡ عَلَيۡكِ رُطَبٗا جَنِيّٗا\n(Мана шу қуриб қолган) хурмо шохини силкитгин, у сенга янги хурмо меваларини ташлар'}, {'chapter': 19, 'verse': 26, 'text': 'فَكُلِي وَٱشۡرَبِي وَقَرِّي عَيۡنٗاۖ فَإِمَّا تَرَيِنَّ مِنَ ٱلۡبَشَرِ أَحَدٗا فَقُولِيٓ إِنِّي نَذَرۡتُ لِلرَّحۡمَٰنِ صَوۡمٗا فَلَنۡ أُكَلِّمَ ٱلۡيَوۡمَ إِنسِيّٗا\nЭнди сен еб-ичгин, шод-хуррам бўлгин. Бас, агар одамзоддан биронтасини кўриб қолсанг (ва у сендан бу боланинг отаси ҳақида сўраса) у ҳолда: «Мен Раҳмон йўлида рўза тутмоқни назр (аҳд) қилганман, бас, бугун бирон инсонга сўзламайман», дегин'}, {'chapter': 19, 'verse': 27, 'text': 'فَأَتَتۡ بِهِۦ قَوۡمَهَا تَحۡمِلُهُۥۖ قَالُواْ يَٰمَرۡيَمُ لَقَدۡ جِئۡتِ شَيۡـٔٗا فَرِيّٗا\nСўнг (Марям боласини) кўтарган ҳолда қавмига келганида улар: «Эй Марям, сен ҳеч ким қилмаган ишни қилдинг-ку'}, {'chapter': 19, 'verse': 28, 'text': 'يَـٰٓأُخۡتَ هَٰرُونَ مَا كَانَ أَبُوكِ ٱمۡرَأَ سَوۡءٖ وَمَا كَانَتۡ أُمُّكِ بَغِيّٗا\nЭй Ҳоруннинг синглиси, сенинг отанг ёмон одам эмас, онанг ҳам фоҳиша эмас эди-ку!» дедилар'}, {'chapter': 19, 'verse': 29, 'text': 'فَأَشَارَتۡ إِلَيۡهِۖ قَالُواْ كَيۡفَ نُكَلِّمُ مَن كَانَ فِي ٱلۡمَهۡدِ صَبِيّٗا\nШунда (Марям оғиз очмасдан боласига) ишора қилди (яъни, унинг ўзидан сўранглар, деди). Улар айтдилар: «Бешикдаги гўдак билан қандай сўзлашурмиз?!»'}, {'chapter': 19, 'verse': 30, 'text': 'قَالَ إِنِّي عَبۡدُ ٱللَّهِ ءَاتَىٰنِيَ ٱلۡكِتَٰبَ وَجَعَلَنِي نَبِيّٗا\n(Шу пайт чақалоқ, яъни Ийсо тилга кириб) деди: «Мен Аллоҳнинг бандасидирман. У зот менга Китоб — Инжил ато этди ва мени пайғамбар қилди'}, {'chapter': 19, 'verse': 31, 'text': 'وَجَعَلَنِي مُبَارَكًا أَيۡنَ مَا كُنتُ وَأَوۡصَٰنِي بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ مَا دُمۡتُ حَيّٗا\nЯна мени қаерда бўлсам, хайру-баракотли қилди ва модомики ҳаёт эканман, намозни (адо этишни) ва закотни (ато этишни) амр қилди'}, {'chapter': 19, 'verse': 32, 'text': 'وَبَرَّۢا بِوَٰلِدَتِي وَلَمۡ يَجۡعَلۡنِي جَبَّارٗا شَقِيّٗا\nШунингдек, (Аллоҳ) мени онамга меҳрибон қилди ва мени ситамкор бадбахт қилмади'}, {'chapter': 19, 'verse': 33, 'text': 'وَٱلسَّلَٰمُ عَلَيَّ يَوۡمَ وُلِدتُّ وَيَوۡمَ أَمُوتُ وَيَوۡمَ أُبۡعَثُ حَيّٗا\nМенга туғилган кунимда ҳам, вафот бўладиган кунимда ҳам, қайта тириладиган кунимда ҳам тинчлик-омонлик бўлур»'}, {'chapter': 19, 'verse': 34, 'text': 'ذَٰلِكَ عِيسَى ٱبۡنُ مَرۡيَمَۖ قَوۡلَ ٱلۡحَقِّ ٱلَّذِي فِيهِ يَمۡتَرُونَ\nУлар (яъни, яҳудий ва насронийлар) шак-шубҳа қилаётган Ийсо бинни Марям (яъни, унинг ҳақидаги Аллоҳнинг) Ҳақ сўзи мана шудир'}, {'chapter': 19, 'verse': 35, 'text': 'مَا كَانَ لِلَّهِ أَن يَتَّخِذَ مِن وَلَدٖۖ سُبۡحَٰنَهُۥٓۚ إِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nАллоҳ учун ҳеч қандай бола тутиш жоиз эмас — У зот (бундай нуқсондан) покдир. У бирон ишни қилмоқни истаса, фақат унга «Бўл», дер. Бас (ўша иш) бўлур'}, {'chapter': 19, 'verse': 36, 'text': 'وَإِنَّ ٱللَّهَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\n(Ийсо айтди): «Албатта Аллоҳ Парвардигорим ва Парвардигорингиздир. Бас, Унга бандалик қилингиз! Мана шу ҳақ йўлдир»'}, {'chapter': 19, 'verse': 37, 'text': 'فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَيۡنِهِمۡۖ فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِن مَّشۡهَدِ يَوۡمٍ عَظِيمٍ\nСўнг (яҳудий ва насроний) фирқалар (Ийсо хусусида) ўзаро ихтилоф қилдилар. Бас, кофир бўлган кимсаларга улуғ Кун — Қиёматга гувоҳ-ҳозир бўлганларида ҳалокат бўлгай'}, {'chapter': 19, 'verse': 38, 'text': 'أَسۡمِعۡ بِهِمۡ وَأَبۡصِرۡ يَوۡمَ يَأۡتُونَنَا لَٰكِنِ ٱلظَّـٰلِمُونَ ٱلۡيَوۡمَ فِي ضَلَٰلٖ مُّبِينٖ\nУлар Бизга келадиган кунда жуда ҳам (аниқ) кўрадиган ва эшитадиган бўлиб қолурлар. Лекин у золимлар бугун очиқ залолатдадирлар'}, {'chapter': 19, 'verse': 39, 'text': 'وَأَنذِرۡهُمۡ يَوۡمَ ٱلۡحَسۡرَةِ إِذۡ قُضِيَ ٱلۡأَمۡرُ وَهُمۡ فِي غَفۡلَةٖ وَهُمۡ لَا يُؤۡمِنُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларни (яъни, Макка мушрикларини) барча иш битирилиб, (яъни, жаннат аҳли жаннатга, дўзахилар дўзахга ҳукм қилиниб), улар ҳасрат-надомат қилиб қоладиган Кундан — Қиёматдан қўрқитинг! (Бугун) улар ғафлатдадирлар, (шу сабабдан) улар иймон келтирмайдилар'}, {'chapter': 19, 'verse': 40, 'text': 'إِنَّا نَحۡنُ نَرِثُ ٱلۡأَرۡضَ وَمَنۡ عَلَيۡهَا وَإِلَيۡنَا يُرۡجَعُونَ\nШак-шубҳасиз, Биз Ўзимиз бу Ерга ва ундаги бор жонзотга меросхўр бўлурмиз (яъни, барча жонзот ўтар, ёлғиз Бизгина қолурмиз). Ва фақат Бизгагина қайтурлар'}, {'chapter': 19, 'verse': 41, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِبۡرَٰهِيمَۚ إِنَّهُۥ كَانَ صِدِّيقٗا نَّبِيًّا\n(Эй Муҳаммад алайҳис-салоту вас-салом), ушбу Китобда Иброҳим (қиссасини) зикр қилинг! Дарҳақиқат, у жуда ростгўй пайғамбар эди'}, {'chapter': 19, 'verse': 42, 'text': 'إِذۡ قَالَ لِأَبِيهِ يَـٰٓأَبَتِ لِمَ تَعۡبُدُ مَا لَا يَسۡمَعُ وَلَا يُبۡصِرُ وَلَا يُغۡنِي عَنكَ شَيۡـٔٗا\nЭсланг, у отасига: «Эй ота, нега сен эшитмайдиган, кўрмайдиган ва сенга бирон фойда етказа олмайдиган бутга ибодат қилурсан'}, {'chapter': 19, 'verse': 43, 'text': 'يَـٰٓأَبَتِ إِنِّي قَدۡ جَآءَنِي مِنَ ٱلۡعِلۡمِ مَا لَمۡ يَأۡتِكَ فَٱتَّبِعۡنِيٓ أَهۡدِكَ صِرَٰطٗا سَوِيّٗا\nЭй ота, дарҳақиқат, сенга келмаган илм-маърифат менга келди. Бас, сен менга эргашгин, сени ҳақ йўлга ҳидоят қилурман'}, {'chapter': 19, 'verse': 44, 'text': 'يَـٰٓأَبَتِ لَا تَعۡبُدِ ٱلشَّيۡطَٰنَۖ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلرَّحۡمَٰنِ عَصِيّٗا\nЭй ота, сен шайтонга ибодат-қуллик қилмагин. Чунки шайтон Раҳмонга осий бўлгандир'}, {'chapter': 19, 'verse': 45, 'text': 'يَـٰٓأَبَتِ إِنِّيٓ أَخَافُ أَن يَمَسَّكَ عَذَابٞ مِّنَ ٱلرَّحۡمَٰنِ فَتَكُونَ لِلشَّيۡطَٰنِ وَلِيّٗا\nЭй ота, ҳақиқатан мен сенга Раҳмон томонидан азоб етиб, (дўзахда) шайтонга дўст-яқин бўлиб қолишингдан қўрқурман», деганида'}, {'chapter': 19, 'verse': 46, 'text': 'قَالَ أَرَاغِبٌ أَنتَ عَنۡ ءَالِهَتِي يَـٰٓإِبۡرَٰهِيمُۖ لَئِن لَّمۡ تَنتَهِ لَأَرۡجُمَنَّكَۖ وَٱهۡجُرۡنِي مَلِيّٗا\n(Отаси) айтди: «Сен менинг худоларимдан юз ўгирувчимисан, эй Иброҳим?! Қасамки, агар сен (менинг худоларимни сўкишдан) тўхтамасанг, албатта, сени тошбўрон қилурман ва (ёки) мени бутунлай тарк эт!»'}, {'chapter': 19, 'verse': 47, 'text': 'قَالَ سَلَٰمٌ عَلَيۡكَۖ سَأَسۡتَغۡفِرُ لَكَ رَبِّيٓۖ إِنَّهُۥ كَانَ بِي حَفِيّٗا\n(Иброҳим) деди: «Омон бўл. Энди Парвардигоримдан сени мағрифат қилишини сўрарман. Шак-шубҳасиз, У менга меҳрибон бўлган Зотдир'}, {'chapter': 19, 'verse': 48, 'text': 'وَأَعۡتَزِلُكُمۡ وَمَا تَدۡعُونَ مِن دُونِ ٱللَّهِ وَأَدۡعُواْ رَبِّي عَسَىٰٓ أَلَّآ أَكُونَ بِدُعَآءِ رَبِّي شَقِيّٗا\nМен сизлардан ҳам, сизлар Аллоҳни қўйиб илтижо қилаётган бутларингиздан ҳам четланурман ва (ёлғиз) Парвардигоримга илтижо қилурман. Парвардигоримга илтижо қилганим шарофатидан (сизлар каби) бадбахт бўлиб қолмасман, деб умид қилурман»'}, {'chapter': 19, 'verse': 49, 'text': 'فَلَمَّا ٱعۡتَزَلَهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۖ وَكُلّٗا جَعَلۡنَا نَبِيّٗا\nБас, қачонки (Иброҳим) улардан ва улар Аллоҳни қўйиб сиғинаётган бутларидан четлангач, Биз унга (фарзандлари) Исҳоқ ва Яъқубни ҳадя этдик ва барчаларини пайғамбар қилдик'}, {'chapter': 19, 'verse': 50, 'text': 'وَوَهَبۡنَا لَهُم مِّن رَّحۡمَتِنَا وَجَعَلۡنَا لَهُمۡ لِسَانَ صِدۡقٍ عَلِيّٗا\nШунингдек, уларга Ўз фазлу-марҳаматимиздан инъом этдик ва улар учун рост, юксак мақтовларни (барқарор) қилдик'}, {'chapter': 19, 'verse': 51, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ مُوسَىٰٓۚ إِنَّهُۥ كَانَ مُخۡلَصٗا وَكَانَ رَسُولٗا نَّبِيّٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), ушбу Китобда Мусо (қиссасини) зикр қилинг! Дарҳақиқат, у танлаб олинган элчи-пайғамбар эди'}, {'chapter': 19, 'verse': 52, 'text': 'وَنَٰدَيۡنَٰهُ مِن جَانِبِ ٱلطُّورِ ٱلۡأَيۡمَنِ وَقَرَّبۡنَٰهُ نَجِيّٗا\nБиз унга Тур (тоғининг) томонидан нидо қилдик ва уни муножот қилган ҳолида (Ўзимизга) яқин этдик'}, {'chapter': 19, 'verse': 53, 'text': 'وَوَهَبۡنَا لَهُۥ مِن رَّحۡمَتِنَآ أَخَاهُ هَٰرُونَ نَبِيّٗا\nБиз унга Ўз фазлу-марҳаматимиз билан пайғамбар бўлмиш оғаси Ҳорунни ҳадя этдик'}, {'chapter': 19, 'verse': 54, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِسۡمَٰعِيلَۚ إِنَّهُۥ كَانَ صَادِقَ ٱلۡوَعۡدِ وَكَانَ رَسُولٗا نَّبِيّٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), ушбу Китобда Исмоил (қиссасини) зикр қилинг! Дарҳақиқат, у ваъдаси рост элчи-пайғамбар эди'}, {'chapter': 19, 'verse': 55, 'text': 'وَكَانَ يَأۡمُرُ أَهۡلَهُۥ بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ وَكَانَ عِندَ رَبِّهِۦ مَرۡضِيّٗا\nУ ўз аҳли-умматини намоз ва рўзага буюрар эди. У Парвардигори наздида рози бўлинган киши эди'}, {'chapter': 19, 'verse': 56, 'text': 'وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِدۡرِيسَۚ إِنَّهُۥ كَانَ صِدِّيقٗا نَّبِيّٗا\nЯна ушбу Китобда Идрис (қиссасини) зикр қилинг! Дарҳаҳиқат, у жуда ростгўй пайғамбар эди'}, {'chapter': 19, 'verse': 57, 'text': 'وَرَفَعۡنَٰهُ مَكَانًا عَلِيًّا\nВа Биз уни юксак мартабага кўтардик'}, {'chapter': 19, 'verse': 58, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِم مِّنَ ٱلنَّبِيِّـۧنَ مِن ذُرِّيَّةِ ءَادَمَ وَمِمَّنۡ حَمَلۡنَا مَعَ نُوحٖ وَمِن ذُرِّيَّةِ إِبۡرَٰهِيمَ وَإِسۡرَـٰٓءِيلَ وَمِمَّنۡ هَدَيۡنَا وَٱجۡتَبَيۡنَآۚ إِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُ ٱلرَّحۡمَٰنِ خَرُّواْۤ سُجَّدٗاۤ وَبُكِيّٗا۩\nАна ўша Одам зурриётидан бўлган (Идрис каби), Биз Нуҳ билан бирга (кемада) кўтариб (тўфон балосидан нажот берган) кишилардан (яъни, уларнинг фарзандларидан бўлган Иброҳим каби), Иброҳим зурриётидан бўлган (Исмоил, Исҳоқ, Яъқуб каби), Исроил-Яъқуб (зурриётидан бўлган Мусо, Ҳорун, Закариё, Яҳё ва Ийсо каби) ва Биз ҳидоят қилган ва танлаб олган кишилардан бўлган пайғамбарлардан иборат Аллоҳнинг инъомига сазовор бўлган зотларга қачон Раҳмон оятлари тиловат қилинса, сажда қилган ва йиғлаган ҳолларида йиқилурлар'}, {'chapter': 19, 'verse': 59, 'text': '۞فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٌ أَضَاعُواْ ٱلصَّلَوٰةَ وَٱتَّبَعُواْ ٱلشَّهَوَٰتِۖ فَسَوۡفَ يَلۡقَوۡنَ غَيًّا\nСўнг уларнинг ортидан намозни зое қиладиган ва шаҳватларга бериладиган кимсалар ўринбосар бўлдилар. Энди у (ўринбосарлар) албатта ёмонликка (яъни, ёмон жазога) йўлиқурлар'}, {'chapter': 19, 'verse': 60, 'text': 'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا يُظۡلَمُونَ شَيۡـٔٗا\nМагар иймон келтириб, яхши амаллар қилган зотларгина (азобга дучор қилинмаслар). Бас, улар жаннатга кирурлар ва уларга бирон зулм қилинмас'}, {'chapter': 19, 'verse': 61, 'text': 'جَنَّـٰتِ عَدۡنٍ ٱلَّتِي وَعَدَ ٱلرَّحۡمَٰنُ عِبَادَهُۥ بِٱلۡغَيۡبِۚ إِنَّهُۥ كَانَ وَعۡدُهُۥ مَأۡتِيّٗا\nУлар Раҳмон ғойибда (яъни, бандалар кўзига кўринмай) турган ҳолида Ўз бандаларига ваъда қилган мангу (яшаладиган) жаннатларга (кирурлар). Албатта, У зотнинг ваъдаси амалга ошгусидир'}, {'chapter': 19, 'verse': 62, 'text': 'لَّا يَسۡمَعُونَ فِيهَا لَغۡوًا إِلَّا سَلَٰمٗاۖ وَلَهُمۡ رِزۡقُهُمۡ فِيهَا بُكۡرَةٗ وَعَشِيّٗا\nУ жойда улар бирон беҳуда сўз эшитмаслар, магар (аҳли жаннат томонидан) салом (эшитурлар, холос). У жойда улар учун эрта-ю кеч ризқлари ҳозирдир'}, {'chapter': 19, 'verse': 63, 'text': 'تِلۡكَ ٱلۡجَنَّةُ ٱلَّتِي نُورِثُ مِنۡ عِبَادِنَا مَن كَانَ تَقِيّٗا\nУшбу жаннат – Биз бандаларимиздан тақводор бўлган кишиларга мерос қилиб берадиган жаннатдир'}, {'chapter': 19, 'verse': 64, 'text': 'وَمَا نَتَنَزَّلُ إِلَّا بِأَمۡرِ رَبِّكَۖ لَهُۥ مَا بَيۡنَ أَيۡدِينَا وَمَا خَلۡفَنَا وَمَا بَيۡنَ ذَٰلِكَۚ وَمَا كَانَ رَبُّكَ نَسِيّٗا\n«(Эй Муҳаммад алайҳис-салоту вас-салом), биз ёлғиз Пардигорингизнинг амри фармони билангина нозил бўлурмиз. Олдимиздаги (Охират) ҳам, ортимиздаги (дунё) ҳам, (дунё ва Охират) ўртасидаги барча иш-амаллар ҳам ёлғиз Унингдир. Парвардигорингиз унутгувчи эмасдир'}, {'chapter': 19, 'verse': 65, 'text': 'رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا فَٱعۡبُدۡهُ وَٱصۡطَبِرۡ لِعِبَٰدَتِهِۦۚ هَلۡ تَعۡلَمُ لَهُۥ سَمِيّٗا\nУ осмонлар, Ер ва уларнинг орасидаги бор нарсаларнинг Хожасидир. Бас, сиз Унга ибодат қилинг ва бу ибодатда сабр-тоқатли бўлинг! Унинг учун бирон «тенг»ни билурмисиз?! (Йўқ! Билмассиз!)»'}, {'chapter': 19, 'verse': 66, 'text': 'وَيَقُولُ ٱلۡإِنسَٰنُ أَءِذَا مَا مِتُّ لَسَوۡفَ أُخۡرَجُ حَيًّا\n(Кофир) инсон: «Ўлганимдан сўнг яна тирик ҳолда қабримдан чиқарилурманми?!», дер'}, {'chapter': 19, 'verse': 67, 'text': 'أَوَلَا يَذۡكُرُ ٱلۡإِنسَٰنُ أَنَّا خَلَقۡنَٰهُ مِن قَبۡلُ وَلَمۡ يَكُ شَيۡـٔٗا\nАхир ўша инсон Биз уни илгари ўзи ҳеч нарса бўлмаган пайтида яратганимизни (яъни, йўқдан бор қилганимизни) эсламасми'}, {'chapter': 19, 'verse': 68, 'text': 'فَوَرَبِّكَ لَنَحۡشُرَنَّهُمۡ وَٱلشَّيَٰطِينَ ثُمَّ لَنُحۡضِرَنَّهُمۡ حَوۡلَ جَهَنَّمَ جِثِيّٗا\nБас (эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингизга қасамки, албатта, Биз уларни (йўлдан оздирган) шайтонлари билан бирга тўплармиз, сўнгра (барчаларини) жаҳаннам атрофида тиз чўккан ҳолларида ҳозир қилурмиз'}, {'chapter': 19, 'verse': 69, 'text': 'ثُمَّ لَنَنزِعَنَّ مِن كُلِّ شِيعَةٍ أَيُّهُمۡ أَشَدُّ عَلَى ٱلرَّحۡمَٰنِ عِتِيّٗا\nКейин ҳар бир гуруҳдан қайси кимса Раҳмонга қаттиқроқ осий бўлган бўлса, (ўшани) суғуриб олурмиз (ва жаҳаннамга отурмиз)'}, {'chapter': 19, 'verse': 70, 'text': 'ثُمَّ لَنَحۡنُ أَعۡلَمُ بِٱلَّذِينَ هُمۡ أَوۡلَىٰ بِهَا صِلِيّٗا\nҲеч шак-шубҳа йўққи, Биз ўзимиз унга (жаҳаннамга) киришга лойиқроқ бўлган кимсаларни яхшироқ билгувчидирмиз'}, {'chapter': 19, 'verse': 71, 'text': 'وَإِن مِّنكُمۡ إِلَّا وَارِدُهَاۚ كَانَ عَلَىٰ رَبِّكَ حَتۡمٗا مَّقۡضِيّٗا\nСизлардан ҳар бирингиз унга тушгувчидирсиз. (Бу) Парвардигорингиз (амрига биноан) вожиб бўлган ҳукмдир'}, {'chapter': 19, 'verse': 72, 'text': 'ثُمَّ نُنَجِّي ٱلَّذِينَ ٱتَّقَواْ وَّنَذَرُ ٱلظَّـٰلِمِينَ فِيهَا جِثِيّٗا\nСўнг тақводор бўлган зотларни (ундан) қутқарурмиз ва золим кимсаларни тиз чўккан ҳолларида (жаҳаннамда) қолдирурмиз'}, {'chapter': 19, 'verse': 73, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُوٓاْ أَيُّ ٱلۡفَرِيقَيۡنِ خَيۡرٞ مَّقَامٗا وَأَحۡسَنُ نَدِيّٗا\nҚачон уларга (яъни, инсонларга) очиқ-равшан бўлган оятларимиз тиловат қилинса, кофир бўлган кимсалар иймон келтирган зотларга: «Қайси гуруҳ яхшироқ мартаба ва гўзалроқ мажлисда?», дейдилар. (Яъни, бой-бадавлат мушриклар фақир ва мискин мўминларнинг устидан куладилар)'}, {'chapter': 19, 'verse': 74, 'text': 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَحۡسَنُ أَثَٰثٗا وَرِءۡيٗا\nҲолбуки, Биз улардан илгари қанча матоъ ва суратлари чиройли бўлган авлодларни ҳалок қилгандирмиз'}, {'chapter': 19, 'verse': 75, 'text': 'قُلۡ مَن كَانَ فِي ٱلضَّلَٰلَةِ فَلۡيَمۡدُدۡ لَهُ ٱلرَّحۡمَٰنُ مَدًّاۚ حَتَّىٰٓ إِذَا رَأَوۡاْ مَا يُوعَدُونَ إِمَّا ٱلۡعَذَابَ وَإِمَّا ٱلسَّاعَةَ فَسَيَعۡلَمُونَ مَنۡ هُوَ شَرّٞ مَّكَانٗا وَأَضۡعَفُ جُندٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Ким залолатда бўлса, бас Раҳмон унга муддат — умр берсин. Токи ўзларига (яъни, йўлдан озганларга) ваъда қилинган (дунё) азобини ёки Қиёмат қойим бўлишини кўрган вақтларида, албатта кимнинг мартабаси ёмонроқ ва лашкари ожизроқ эканини билурлар'}, {'chapter': 19, 'verse': 76, 'text': 'وَيَزِيدُ ٱللَّهُ ٱلَّذِينَ ٱهۡتَدَوۡاْ هُدٗىۗ وَٱلۡبَٰقِيَٰتُ ٱلصَّـٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٞ مَّرَدًّا\nАллоҳ ҳақ йўлдаги кишиларга яна ҳидоятни зиёда қилур. Парвардигорингиз наздида абадий қолгувчи яхши амаллар савоб жиҳатидан ҳам, оқибат жиҳатидан ҳам ортиқроқдир»'}, {'chapter': 19, 'verse': 77, 'text': 'أَفَرَءَيۡتَ ٱلَّذِي كَفَرَ بِـَٔايَٰتِنَا وَقَالَ لَأُوتَيَنَّ مَالٗا وَوَلَدًا\n(Эй Муҳаммад алайҳис-салоту вас-салом), Бизнинг оятларимизга кофир бўлган ва: «Қасамки, албатта (Охиратда ҳам) менга мол-мулк ва бола-чақа ато этилур», деган кимсани кўрдингизми'}, {'chapter': 19, 'verse': 78, 'text': 'أَطَّلَعَ ٱلۡغَيۡبَ أَمِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَٰنِ عَهۡدٗا\nУ ғойибдан хабардор бўлдимикин ёки Раҳмон даргоҳидан аҳду паймон олдимикин'}, {'chapter': 19, 'verse': 79, 'text': 'كَلَّاۚ سَنَكۡتُبُ مَا يَقُولُ وَنَمُدُّ لَهُۥ مِنَ ٱلۡعَذَابِ مَدّٗا\nЙўқ! Албатта, Биз унинг айтаётган сўзини ёзиб қўюрмиз ҳамда унинг учун азобни зиёда қилурмиз'}, {'chapter': 19, 'verse': 80, 'text': 'وَنَرِثُهُۥ مَا يَقُولُ وَيَأۡتِينَا فَرۡدٗا\nУ айтаётган (мол-дунёга) Биз меросхўр бўлурмиз ва у ҳузуримизга ёлғиз ҳолда келур'}, {'chapter': 19, 'verse': 81, 'text': 'وَٱتَّخَذُواْ مِن دُونِ ٱللَّهِ ءَالِهَةٗ لِّيَكُونُواْ لَهُمۡ عِزّٗا\nУлар (яъни, Макка мушриклари) Аллоҳни қўйиб, ўзларига куч-қудрат бўлиши учун «худолар» тутдилар — бутларга сиғиндилар'}, {'chapter': 19, 'verse': 82, 'text': 'كَلَّاۚ سَيَكۡفُرُونَ بِعِبَادَتِهِمۡ وَيَكُونُونَ عَلَيۡهِمۡ ضِدًّا\nЙўқ! (Қиёмат Кунида бутлари уларни шафоат қилмайди, балки) улар қилган ибодатларни инкор қилурлар ва уларга зид-душман бўлурлар'}, {'chapter': 19, 'verse': 83, 'text': 'أَلَمۡ تَرَ أَنَّآ أَرۡسَلۡنَا ٱلشَّيَٰطِينَ عَلَى ٱلۡكَٰفِرِينَ تَؤُزُّهُمۡ أَزّٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз кофирларга уларни (гуноҳ-маъсиятларга) қўзғатадиган шайтонларни юборганимизни кўрмадингизми'}, {'chapter': 19, 'verse': 84, 'text': 'فَلَا تَعۡجَلۡ عَلَيۡهِمۡۖ إِنَّمَا نَعُدُّ لَهُمۡ عَدّٗا\nБас, сиз улар устида шошқалоқлик қилманг (яъни, нега азобга гирифтор бўла қолмаяптилар, деб ҳайрон бўлманг)! Ҳеч шак-шубҳасиз, Биз уларнинг (қолган ой, кунларини ҳам, қилаётган гуноҳларини ҳам) аниқ ҳисоб-китоб қилиб турурмиз'}, {'chapter': 19, 'verse': 85, 'text': 'يَوۡمَ نَحۡشُرُ ٱلۡمُتَّقِينَ إِلَى ٱلرَّحۡمَٰنِ وَفۡدٗا\nБиз тақводор зотларни отлиқ ҳолларида Раҳмон даргоҳига тўплайдиган'}, {'chapter': 19, 'verse': 86, 'text': 'وَنَسُوقُ ٱلۡمُجۡرِمِينَ إِلَىٰ جَهَنَّمَ وِرۡدٗا\nВа жиноятчи-кофирларни жаҳаннамга чанқоқ ҳолларида ҳайдайдиганимиз кунни (эсланг)'}, {'chapter': 19, 'verse': 87, 'text': 'لَّا يَمۡلِكُونَ ٱلشَّفَٰعَةَ إِلَّا مَنِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَٰنِ عَهۡدٗا\n(У кунда) фақат Раҳмон наздида аҳду-паймон олган, (Аллоҳни бир билиб зиммаларидаги фарзларни адо этган) кишиларгина шафоат қилишга эга бўлурлар'}, {'chapter': 19, 'verse': 88, 'text': 'وَقَالُواْ ٱتَّخَذَ ٱلرَّحۡمَٰنُ وَلَدٗا\n(Мушриклар): «Раҳмоннинг боласи бор», дедилар'}, {'chapter': 19, 'verse': 89, 'text': 'لَّقَدۡ جِئۡتُمۡ شَيۡـًٔا إِدّٗا\n(Эй мушриклар), сизлар шундай оғир гап айтдингизки'}, {'chapter': 19, 'verse': 90, 'text': 'تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِنۡهُ وَتَنشَقُّ ٱلۡأَرۡضُ وَتَخِرُّ ٱلۡجِبَالُ هَدًّا\nУнинг (оғирлигидан) осмонлар ёрилиб, ер бўлиниб, тоғлар парчаланиб қулаб кетишга яқин бўлур'}, {'chapter': 19, 'verse': 91, 'text': 'أَن دَعَوۡاْ لِلرَّحۡمَٰنِ وَلَدٗا\nРаҳмоннинг боласи бор, деган (гапнинг оғирлигидан)'}, {'chapter': 19, 'verse': 92, 'text': 'وَمَا يَنۢبَغِي لِلرَّحۡمَٰنِ أَن يَتَّخِذَ وَلَدًا\nРаҳмон учун бола тутиш лойиқ эмасдир (яъни, У зот болага муҳтож эмас)'}, {'chapter': 19, 'verse': 93, 'text': 'إِن كُلُّ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ إِلَّآ ءَاتِي ٱلرَّحۡمَٰنِ عَبۡدٗا\nОсмонлар ва Ердаги бор жонзот (Қиёмат Кунида) Раҳмон (ҳузурига) бўйинсунган ҳолда келур'}, {'chapter': 19, 'verse': 94, 'text': 'لَّقَدۡ أَحۡصَىٰهُمۡ وَعَدَّهُمۡ عَدّٗا\nУ зот уларни санаб-аниқлаб қўйгандир'}, {'chapter': 19, 'verse': 95, 'text': 'وَكُلُّهُمۡ ءَاتِيهِ يَوۡمَ ٱلۡقِيَٰمَةِ فَرۡدًا\nУларнинг барчаси Қиёмат Кунида ёлғиз ҳолда У зотнинг ҳузурига келгувчидир'}, {'chapter': 19, 'verse': 96, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَيَجۡعَلُ لَهُمُ ٱلرَّحۡمَٰنُ وُدّٗا\nАлбатта, иймон келтириб яхши амаллар қилган зотлар учун Раҳмон дўстликни (барқарор) қилур'}, {'chapter': 19, 'verse': 97, 'text': 'فَإِنَّمَا يَسَّرۡنَٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ ٱلۡمُتَّقِينَ وَتُنذِرَ بِهِۦ قَوۡمٗا لُّدّٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Биз (Қуръон) билан тақво эгаларига хушхабар беришингиз учун ва саркаш қавмни қўрқитишингиз учун уни сизнинг тилингизга осон-енгил қилиб қўйдик'}, {'chapter': 19, 'verse': 98, 'text': 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هَلۡ تُحِسُّ مِنۡهُم مِّنۡ أَحَدٍ أَوۡ تَسۡمَعُ لَهُمۡ رِكۡزَۢا\nБиз улардан (яъни, Макка мушрикларидан) илгари ҳам қанча (ўз пайғамбарларига итоат этмаган) авлодларни ҳалок қилгандирмиз. Сиз улардан биронтасини кўрурмисиз ёки овозларини эшитурмисиз?! (Бас, сизга иймон келтирмайдиган бу қайсар қавм ҳам ҳалок бўлиб битар, аммо Биз сизга нозил қилган дин эса Қиёматгача қолур)'}, {'chapter': 20, 'verse': 1, 'text': 'طه\nТо, Ҳа'}, {'chapter': 20, 'verse': 2, 'text': 'مَآ أَنزَلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ لِتَشۡقَىٰٓ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизга бу Қуръонни қийналиб жафо чекишингиз учун эмас'}, {'chapter': 20, 'verse': 3, 'text': 'إِلَّا تَذۡكِرَةٗ لِّمَن يَخۡشَىٰ\nБалки (Аллоҳдан) қўрқадиган кишиларга панд-насиҳат бўлсин учун нозил қилдик'}, {'chapter': 20, 'verse': 4, 'text': 'تَنزِيلٗا مِّمَّنۡ خَلَقَ ٱلۡأَرۡضَ وَٱلسَّمَٰوَٰتِ ٱلۡعُلَى\n(У) Ерни ва юксак осмонларни яратган зот томонидан нозил қилингандир'}, {'chapter': 20, 'verse': 5, 'text': 'ٱلرَّحۡمَٰنُ عَلَى ٱلۡعَرۡشِ ٱسۡتَوَىٰ\n(У зот Ўз Зоти ва Улуғлигига муносиб равишда) Арш узра олий бўлган Раҳмондир'}, {'chapter': 20, 'verse': 6, 'text': 'لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَمَا تَحۡتَ ٱلثَّرَىٰ\nОсмонлардаги, Ердаги ва уларнинг орасидаги ҳамда тупроқ остидаги бор нарса Унингдир'}, {'chapter': 20, 'verse': 7, 'text': 'وَإِن تَجۡهَرۡ بِٱلۡقَوۡلِ فَإِنَّهُۥ يَعۡلَمُ ٱلسِّرَّ وَأَخۡفَى\nАгар сиз ошкора гапирсангиз ҳам, (ва ёки) хуфёна гапирсангиз ҳам, У зотга баробардир. Зеро, У сирни ҳам, энг махфий нарсаларни ҳам билур'}, {'chapter': 20, 'verse': 8, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰ\nАллоҳдан ўзга (ҳеч қандай) Аллоҳ йўқ. Фақат Унинг Ўзи бордир. Унинг учун энг гўзал исмлар бордир'}, {'chapter': 20, 'verse': 9, 'text': 'وَهَلۡ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга Мусо ҳақида хабар келдими'}, {'chapter': 20, 'verse': 10, 'text': 'إِذۡ رَءَا نَارٗا فَقَالَ لِأَهۡلِهِ ٱمۡكُثُوٓاْ إِنِّيٓ ءَانَسۡتُ نَارٗا لَّعَلِّيٓ ءَاتِيكُم مِّنۡهَا بِقَبَسٍ أَوۡ أَجِدُ عَلَى ٱلنَّارِ هُدٗى\nЭсланг, у (узоқдан) бир оловни кўриб аҳли оиласига: «(Шу ерда) туринглар, мен бир олов кўриб қолдим, шоядки сизларга ундан бирон чўғ олиб келсам ёки шу ўт олдидан бирон йўл (кўрсатувчи) топсам», деди'}, {'chapter': 20, 'verse': 11, 'text': 'فَلَمَّآ أَتَىٰهَا نُودِيَ يَٰمُوسَىٰٓ\nБас, қачонки у (оловнинг яқинига) келгач: «Эй Мусо», деган нидо эшитилди'}, {'chapter': 20, 'verse': 12, 'text': 'إِنِّيٓ أَنَا۠ رَبُّكَ فَٱخۡلَعۡ نَعۡلَيۡكَ إِنَّكَ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوٗى\n«(Эй Мусо), Мен сенинг Парвардигорингдирман. Энди кавушларингни ечгин. Чунки сен муқаддас Туво водийсидасан'}, {'chapter': 20, 'verse': 13, 'text': 'وَأَنَا ٱخۡتَرۡتُكَ فَٱسۡتَمِعۡ لِمَا يُوحَىٰٓ\nМен сени (пайғамбарликка) танладим. Бас (ўзингга) юбориладиган ваҳийга қулоқ тут'}, {'chapter': 20, 'verse': 14, 'text': 'إِنَّنِيٓ أَنَا ٱللَّهُ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعۡبُدۡنِي وَأَقِمِ ٱلصَّلَوٰةَ لِذِكۡرِيٓ\nДарҳақиқат, Мен Аллоҳдирман. (Ҳеч қандай) илоҳ йўқ, фақат Мен бордирман. Бас, сен Менгагина ибодат қил ва Мени зикр қилиш учун намозни тўкис адо қил'}, {'chapter': 20, 'verse': 15, 'text': 'إِنَّ ٱلسَّاعَةَ ءَاتِيَةٌ أَكَادُ أُخۡفِيهَا لِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا تَسۡعَىٰ\nТоки ҳар бир жон қиладиган саъй-ҳаракати сабабли жазоланиши учун (Қиёмат) соати албатта келгувчидир. (Лекин) Мен (Қиёмат қачон қойим бўлишини) яширишни истайман'}, {'chapter': 20, 'verse': 16, 'text': 'فَلَا يَصُدَّنَّكَ عَنۡهَا مَن لَّا يُؤۡمِنُ بِهَا وَٱتَّبَعَ هَوَىٰهُ فَتَرۡدَىٰ\nБас, сени (Қиёматга) ишонмайдиган ва ҳавои нафсига эргашган кимсалар ундан тўсмасинларки, у ҳолда ҳалок бўлурсан'}, {'chapter': 20, 'verse': 17, 'text': 'وَمَا تِلۡكَ بِيَمِينِكَ يَٰمُوسَىٰ\nАна у — қўлингдаги нимадир, эй Мусо?!»'}, {'chapter': 20, 'verse': 18, 'text': 'قَالَ هِيَ عَصَايَ أَتَوَكَّؤُاْ عَلَيۡهَا وَأَهُشُّ بِهَا عَلَىٰ غَنَمِي وَلِيَ فِيهَا مَـَٔارِبُ أُخۡرَىٰ\n(Мусо) деди: «У асоим, унга таянурман ва у билан қўйларимга (барг) қоқиб берурман. Яна унда бошқа ишларим ҳам бор»'}, {'chapter': 20, 'verse': 19, 'text': 'قَالَ أَلۡقِهَا يَٰمُوسَىٰ\n(Аллоҳ) айтди: «Уни (ерга) ташлагин, эй Мусо!»'}, {'chapter': 20, 'verse': 20, 'text': 'فَأَلۡقَىٰهَا فَإِذَا هِيَ حَيَّةٞ تَسۡعَىٰ\nБас, (Мусо) уни ташлаган эди, баногоҳ у юрадиган — жонли илон бўлиб қолди'}, {'chapter': 20, 'verse': 21, 'text': 'قَالَ خُذۡهَا وَلَا تَخَفۡۖ سَنُعِيدُهَا سِيرَتَهَا ٱلۡأُولَىٰ\n(Аллоҳ) деди: «Уни ушла! Қўрқмагин, Биз уни аввалги ҳолига қайтарурмиз'}, {'chapter': 20, 'verse': 22, 'text': 'وَٱضۡمُمۡ يَدَكَ إِلَىٰ جَنَاحِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٍ ءَايَةً أُخۡرَىٰ\nҚўлингни қанотинг — қўлтиғингга тиққин, у ҳеч қандай зиён-заҳматсиз оппоқ-нурли бўлиб чиқур. (Бу) иккинчи мўъжиза (бўлур)'}, {'chapter': 20, 'verse': 23, 'text': 'لِنُرِيَكَ مِنۡ ءَايَٰتِنَا ٱلۡكُبۡرَى\nБиз сенга буюк мўъжизаларимизни кўрсатиш учун (шундай қилдик)'}, {'chapter': 20, 'verse': 24, 'text': 'ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\n(Энди) сен Фиръавннинг оддига боргин! Дарҳақиқат, у ҳаддидан ошди (яъни, одамларга зулм қилиб ҳатто «худоман», деб даъво қилди)»'}, {'chapter': 20, 'verse': 25, 'text': 'قَالَ رَبِّ ٱشۡرَحۡ لِي صَدۡرِي\n(Мусо) деди: «Парвардигорим, қалбимни кенг қилгин'}, {'chapter': 20, 'verse': 26, 'text': 'وَيَسِّرۡ لِيٓ أَمۡرِي\nИшимни осон қилгин'}, {'chapter': 20, 'verse': 27, 'text': 'وَٱحۡلُلۡ عُقۡدَةٗ مِّن لِّسَانِي\nТилимдан туғунни-дудуқликни ечиб юборгин'}, {'chapter': 20, 'verse': 28, 'text': 'يَفۡقَهُواْ قَوۡلِي\n(Токи) улар (қавмим) сўзимни англасинлар'}, {'chapter': 20, 'verse': 29, 'text': 'وَٱجۡعَل لِّي وَزِيرٗا مِّنۡ أَهۡلِي\nМенга ўз аҳлим-уруғимдан вазир қилгин'}, {'chapter': 20, 'verse': 30, 'text': 'هَٰرُونَ أَخِي\nҲорун оғамни'}, {'chapter': 20, 'verse': 31, 'text': 'ٱشۡدُدۡ بِهِۦٓ أَزۡرِي\nУ билан белимни маҳкам қилгин'}, {'chapter': 20, 'verse': 32, 'text': 'وَأَشۡرِكۡهُ فِيٓ أَمۡرِي\nВа уни ишимда (яъни, пайғамбарлик ишида) шерик қилгин'}, {'chapter': 20, 'verse': 33, 'text': 'كَيۡ نُسَبِّحَكَ كَثِيرٗا\nТоки биз Сени кўпроқ поклаб-ибодат қилайлик'}, {'chapter': 20, 'verse': 34, 'text': 'وَنَذۡكُرَكَ كَثِيرًا\nВа Сени кўпроқ зикр қилиб — эслайлик'}, {'chapter': 20, 'verse': 35, 'text': 'إِنَّكَ كُنتَ بِنَا بَصِيرٗا\nАлбатта Сен бизни кўрувчи бўлган Зотсан»'}, {'chapter': 20, 'verse': 36, 'text': 'قَالَ قَدۡ أُوتِيتَ سُؤۡلَكَ يَٰمُوسَىٰ\n(Аллоҳ) айтди: «Сўраган нарсаларинг сенга ато этилди, эй Мусо'}, {'chapter': 20, 'verse': 37, 'text': 'وَلَقَدۡ مَنَنَّا عَلَيۡكَ مَرَّةً أُخۡرَىٰٓ\nБиз сенга (гўдаклик чоғингда ҳам) яна бир карра инъом-марҳамат қилган эдик'}, {'chapter': 20, 'verse': 38, 'text': 'إِذۡ أَوۡحَيۡنَآ إِلَىٰٓ أُمِّكَ مَا يُوحَىٰٓ\nЎшанда сенинг онангга (шундай) ваҳий-илҳом юборгандик'}, {'chapter': 20, 'verse': 39, 'text': 'أَنِ ٱقۡذِفِيهِ فِي ٱلتَّابُوتِ فَٱقۡذِفِيهِ فِي ٱلۡيَمِّ فَلۡيُلۡقِهِ ٱلۡيَمُّ بِٱلسَّاحِلِ يَأۡخُذۡهُ عَدُوّٞ لِّي وَعَدُوّٞ لَّهُۥۚ وَأَلۡقَيۡتُ عَلَيۡكَ مَحَبَّةٗ مِّنِّي وَلِتُصۡنَعَ عَلَىٰ عَيۡنِيٓ\n«Уни (яъни, Мусони) сандиққа солиб, дарёга ташлагин. Бас, уни дарё (тўлқини) соҳилга отсин. (Шунда) уни Менинг ҳам, унинг ҳам душмани — Фиръавн ушлаб олур». (Эй Мусо, барчага суюкли бўлишинг) ва Менинг ҳифзу ҳимоямда униб-ўсишинг учун Мен сенинг устингга Ўз томонимдан бир муҳаббат ташладим (яъни, сени кўрган киши суюб қоладиган ёқимтой бола қилдим)'}, {'chapter': 20, 'verse': 40, 'text': 'إِذۡ تَمۡشِيٓ أُخۡتُكَ فَتَقُولُ هَلۡ أَدُلُّكُمۡ عَلَىٰ مَن يَكۡفُلُهُۥۖ فَرَجَعۡنَٰكَ إِلَىٰٓ أُمِّكَ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَۚ وَقَتَلۡتَ نَفۡسٗا فَنَجَّيۡنَٰكَ مِنَ ٱلۡغَمِّ وَفَتَنَّـٰكَ فُتُونٗاۚ فَلَبِثۡتَ سِنِينَ فِيٓ أَهۡلِ مَدۡيَنَ ثُمَّ جِئۡتَ عَلَىٰ قَدَرٖ يَٰمُوسَىٰ\nЎшанда опанг (сени дарёдан ушлаб олган кишилар ортидан) юриб: «Сизларни бу (бола)га кафил бўлиб (эмизадиган аёлнинг) олдига олиб борайми», деганларини (эслагин)! Сўнг Биз сени кўзлари қувонсин, ғамгин бўлмасин, деб онангга қайтарган эдик. (Йигитлик чоғингда) бир жонни ўлдириб қўйганингда, Биз сени бу ғамдан ҳам қутқарган эдик. (Кейин) Биз сени кўп синовлар билан синаб кўрдик. Сен Мадян аҳли орасида ҳам (неча) йиллар турдинг; сўнгра (пайғамбар бўлишинг учун аниқ белгилаб қўйилган) тақдир бўлиб (бу ерга) келдинг, эй Мусо'}, {'chapter': 20, 'verse': 41, 'text': 'وَٱصۡطَنَعۡتُكَ لِنَفۡسِي\nВа Мен сени Ўзимга (пайғамбарлик учун) танладим'}, {'chapter': 20, 'verse': 42, 'text': 'ٱذۡهَبۡ أَنتَ وَأَخُوكَ بِـَٔايَٰتِي وَلَا تَنِيَا فِي ذِكۡرِي\n(Эй Мусо), сен ўзинг ва оғанг (Ҳорун) Менинг мўъжизаларимни (одамларга) олиб боринглар ва Мени зикр қилиб — ёдингизда тутишда сусткашлик қилманглар'}, {'chapter': 20, 'verse': 43, 'text': 'ٱذۡهَبَآ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\nСизлар Фиръавннинг олдига боринглар, чунки («Мен худоман», деб у) ҳаддидан ошди'}, {'chapter': 20, 'verse': 44, 'text': 'فَقُولَا لَهُۥ قَوۡلٗا لَّيِّنٗا لَّعَلَّهُۥ يَتَذَكَّرُ أَوۡ يَخۡشَىٰ\nБас, унга юмшоқ сўз сўзланглар! Шояд панд-насиҳат олса ёки (Менинг қаҳримдан) қўрқса'}, {'chapter': 20, 'verse': 45, 'text': 'قَالَا رَبَّنَآ إِنَّنَا نَخَافُ أَن يَفۡرُطَ عَلَيۡنَآ أَوۡ أَن يَطۡغَىٰ\nУлар дедилар: «Парвардигоро, дарҳақиқат, биз (агар уни Сенга иймон келтиришга даъват этсак) у шошқалоқлик (билан бизни азобга гирифтор) қилишидан ёки баттар туғёнга тушишидан хавфдамиз'}, {'chapter': 20, 'verse': 46, 'text': 'قَالَ لَا تَخَافَآۖ إِنَّنِي مَعَكُمَآ أَسۡمَعُ وَأَرَىٰ\n(Аллоҳ) айтди: «Қўрқманглар. Шак-шубҳасиз, Мен сизлар билан биргаман — эшитиб, кўриб турурман'}, {'chapter': 20, 'verse': 47, 'text': 'فَأۡتِيَاهُ فَقُولَآ إِنَّا رَسُولَا رَبِّكَ فَأَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَـٰٓءِيلَ وَلَا تُعَذِّبۡهُمۡۖ قَدۡ جِئۡنَٰكَ بِـَٔايَةٖ مِّن رَّبِّكَۖ وَٱلسَّلَٰمُ عَلَىٰ مَنِ ٱتَّبَعَ ٱلۡهُدَىٰٓ\nБас, сизлар унинг олдига бориб: «Биз Парвардигорингнинг элчиларидирмиз. Сен Бани Исроил (қавмини) биз билан бирга қўйиб юборгин, уларни азобламагин. Биз сенга Парвардигоринг томонидан оят-мўъжиза келтирдик. Ҳидоятга эргашган кишиларга тинчлик-омонлик бўлур'}, {'chapter': 20, 'verse': 48, 'text': 'إِنَّا قَدۡ أُوحِيَ إِلَيۡنَآ أَنَّ ٱلۡعَذَابَ عَلَىٰ مَن كَذَّبَ وَتَوَلَّىٰ\nБизга ваҳий қилиндики, (Аллоҳнинг пайғамбарларини) ёлғончи қилган ва (уларга иймон келтиришдан) юз ўгирган кимсаларга азоб булур», денглар»'}, {'chapter': 20, 'verse': 49, 'text': 'قَالَ فَمَن رَّبُّكُمَا يَٰمُوسَىٰ\n(Улар Фиръавн олдига келиб юқоридаги сўзларни айтишгач), у деди: «Сизларнинг Парвардигорингиз ким, эй Мусо?»'}, {'chapter': 20, 'verse': 50, 'text': 'قَالَ رَبُّنَا ٱلَّذِيٓ أَعۡطَىٰ كُلَّ شَيۡءٍ خَلۡقَهُۥ ثُمَّ هَدَىٰ\n(Мусо) айтди: «Парвардигоримиз барча нарсага ўз хилқатини — шаклини ато этиб, сўнгра (уни) Тўғри Йўлга солиб қўйган Зотдир»'}, {'chapter': 20, 'verse': 51, 'text': 'قَالَ فَمَا بَالُ ٱلۡقُرُونِ ٱلۡأُولَىٰ\n(Фиръавн) деди: «У ҳолда аввалги авлодларнинг ҳоли недир? (Яъни, Парвардигоринг уларни ҳам азоблаганми?)»'}, {'chapter': 20, 'verse': 52, 'text': 'قَالَ عِلۡمُهَا عِندَ رَبِّي فِي كِتَٰبٖۖ لَّا يَضِلُّ رَبِّي وَلَا يَنسَى\n(Мусо) айтди: «Улар ҳақидаги билим Парвардигорим ҳузуридаги Китобда — Лавҳул-Маҳфуздадир. Парвардигорим адашмас ва унутмас»'}, {'chapter': 20, 'verse': 53, 'text': 'ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدٗا وَسَلَكَ لَكُمۡ فِيهَا سُبُلٗا وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّن نَّبَاتٖ شَتَّىٰ\nУ зот Ерни сизлар учун бешик — қароргоҳ қилиб қўйди ва унда йўллар пайдо қилди ҳамда осмондан сув (ёмғир-қор) ёғдирди. Бас, Биз у (сув) ёрдамида турли наботот навларини ундириб чиқардик'}, {'chapter': 20, 'verse': 54, 'text': 'كُلُواْ وَٱرۡعَوۡاْ أَنۡعَٰمَكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّأُوْلِي ٱلنُّهَىٰ\nСизлар (улардан) енглар, чорваларингни боқинглар. Албатта бунда ақл эгалари учун оят-ибратлар бордир'}, {'chapter': 20, 'verse': 55, 'text': '۞مِنۡهَا خَلَقۡنَٰكُمۡ وَفِيهَا نُعِيدُكُمۡ وَمِنۡهَا نُخۡرِجُكُمۡ تَارَةً أُخۡرَىٰ\nБиз сизларни (Ердан) яратдик, яна унга қайтарурмиз ва (Қиёмат Кунида) сизларни яна бир бор ундан чиқарурмиз'}, {'chapter': 20, 'verse': 56, 'text': 'وَلَقَدۡ أَرَيۡنَٰهُ ءَايَٰتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ\nДарҳақиқат, Биз унга (яъни, Фиръавнга) бор оят-мўъжизаларимизни кўрсатдик. Бас, у (уларни) ёлғон деб, юз ўгирди'}, {'chapter': 20, 'verse': 57, 'text': 'قَالَ أَجِئۡتَنَا لِتُخۡرِجَنَا مِنۡ أَرۡضِنَا بِسِحۡرِكَ يَٰمُوسَىٰ\nУ деди: «Сен ўз сеҳринг билан бизларни еримиздан чиқариш учун келдингми, эй Мусо'}, {'chapter': 20, 'verse': 58, 'text': 'فَلَنَأۡتِيَنَّكَ بِسِحۡرٖ مِّثۡلِهِۦ فَٱجۡعَلۡ بَيۡنَنَا وَبَيۡنَكَ مَوۡعِدٗا لَّا نُخۡلِفُهُۥ نَحۡنُ وَلَآ أَنتَ مَكَانٗا سُوٗى\nУ ҳолда бизлар ҳам сенга худди ўшандай сеҳр келтирурмиз. Бас, сен ўзинг билан бизларнинг ўртамизда бир ваъдагоҳ танлагинки, у сен ҳам, бизлар ҳам қаршилик қилмайдиган ўрта бир жой бўлсин»'}, {'chapter': 20, 'verse': 59, 'text': 'قَالَ مَوۡعِدُكُمۡ يَوۡمُ ٱلزِّينَةِ وَأَن يُحۡشَرَ ٱلنَّاسُ ضُحٗى\n(Мусо) айтди: «Ваъда байрам куни (тўпланадиган жой)дир. Одамлар чошгоҳда тўпланурлар»'}, {'chapter': 20, 'verse': 60, 'text': 'فَتَوَلَّىٰ فِرۡعَوۡنُ فَجَمَعَ كَيۡدَهُۥ ثُمَّ أَتَىٰ\nБас, Фиръавн бориб, ўзининг макр-найрангини (яъни, сеҳргарларини) йиғди. Сўнгра (ваъда қилинган жойга) келди'}, {'chapter': 20, 'verse': 61, 'text': 'قَالَ لَهُم مُّوسَىٰ وَيۡلَكُمۡ لَا تَفۡتَرُواْ عَلَى ٱللَّهِ كَذِبٗا فَيُسۡحِتَكُم بِعَذَابٖۖ وَقَدۡ خَابَ مَنِ ٱفۡتَرَىٰ\nМусо уларга (яъни, сеҳргарларга) деди: «Ҳолингларга вой! Сизлар Аллоҳ шаънига ёлғон тўқиманглар (яъни, очиқ мўъжизани сеҳр деманглар), у ҳолда (Аллоҳ) сизларни азоб билан ҳалок қилур! Ёлғон тўқиган кимсалар ноумид бўлишлари аниқдир»'}, {'chapter': 20, 'verse': 62, 'text': 'فَتَنَٰزَعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡ وَأَسَرُّواْ ٱلنَّجۡوَىٰ\nБас, улар (қиладиган) ишларини талашиб-тортишиб аста шивирлаша бошладилар'}, {'chapter': 20, 'verse': 63, 'text': 'قَالُوٓاْ إِنۡ هَٰذَٰنِ لَسَٰحِرَٰنِ يُرِيدَانِ أَن يُخۡرِجَاكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِمَا وَيَذۡهَبَا بِطَرِيقَتِكُمُ ٱلۡمُثۡلَىٰ\n(Сўнг бир-бирларига) айтдилар: «Албатта бу иккови (яъни, Мусо ва Ҳорун) ўз сеҳрлари билан сизларни ерингиздан чиқармоқни ва юксак йўлингизни (яъни, динингизни) йўқотмоқни истайдиган сеҳргарлардир'}, {'chapter': 20, 'verse': 64, 'text': 'فَأَجۡمِعُواْ كَيۡدَكُمۡ ثُمَّ ٱئۡتُواْ صَفّٗاۚ وَقَدۡ أَفۡلَحَ ٱلۡيَوۡمَ مَنِ ٱسۡتَعۡلَىٰ\nБас, макр-ҳийлангизни йиғингиз, сўнг бир саф бўлиб келингиз. Бугун устун бўлган кишилар нажот топурлар»'}, {'chapter': 20, 'verse': 65, 'text': 'قَالُواْ يَٰمُوسَىٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ أَوَّلَ مَنۡ أَلۡقَىٰ\n(Маслаҳатлари битгач), дедилар: «Эй Мусо, сен (қўлингдаги асоингни аввал) ташлайсанми ёки бизлар биринчи ташловчи бўлайликми?»'}, {'chapter': 20, 'verse': 66, 'text': 'قَالَ بَلۡ أَلۡقُواْۖ فَإِذَا حِبَالُهُمۡ وَعِصِيُّهُمۡ يُخَيَّلُ إِلَيۡهِ مِن سِحۡرِهِمۡ أَنَّهَا تَسۡعَىٰ\n(Мусо) айтди: «Йўқ, сизлар ташланглар». (Улар қўлларидаги асо ва ипларини ташлаган эдилар) баногоҳ иплари ва асолари сеҳр қилганлари сабабли (Мусога) юриб кетаётгандек туюлди'}, {'chapter': 20, 'verse': 67, 'text': 'فَأَوۡجَسَ فِي نَفۡسِهِۦ خِيفَةٗ مُّوسَىٰ\nБас, Мусо ичида бир қўрқувни ҳис қилган эди'}, {'chapter': 20, 'verse': 68, 'text': 'قُلۡنَا لَا تَخَفۡ إِنَّكَ أَنتَ ٱلۡأَعۡلَىٰ\nБиз айтдик: «Қўрқмагин. Албатта сен ўзинг ғолиб бўлгувчисан'}, {'chapter': 20, 'verse': 69, 'text': 'وَأَلۡقِ مَا فِي يَمِينِكَ تَلۡقَفۡ مَا صَنَعُوٓاْۖ إِنَّمَا صَنَعُواْ كَيۡدُ سَٰحِرٖۖ وَلَا يُفۡلِحُ ٱلسَّاحِرُ حَيۡثُ أَتَىٰ\nҚўлингдаги нарсани (яъни, асоингни) ташлагин, улар ясаган нарсаларни ютиб юборур. Уларнинг ясаган нарсалари фақат бир сеҳргарнинг макри-ҳийласи, холос. Сеҳргар эса қаерда бўлмасин зафар топмас»'}, {'chapter': 20, 'verse': 70, 'text': 'فَأُلۡقِيَ ٱلسَّحَرَةُ سُجَّدٗا قَالُوٓاْ ءَامَنَّا بِرَبِّ هَٰرُونَ وَمُوسَىٰ\n(Мусо асосини ташлагач, сеҳргарларнинг бор ясаган нарсаларини ютиб юборди). Бас, сеҳргарлар сажда қилган ҳолларида (ерга) ташланиб: «Ҳорун ва Мусонинг Парвардигорига иймон келтирдик», дедилар'}, {'chapter': 20, 'verse': 71, 'text': 'قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِي عَلَّمَكُمُ ٱلسِّحۡرَۖ فَلَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ وَلَأُصَلِّبَنَّكُمۡ فِي جُذُوعِ ٱلنَّخۡلِ وَلَتَعۡلَمُنَّ أَيُّنَآ أَشَدُّ عَذَابٗا وَأَبۡقَىٰ\n(Фиръавн) айтди: «Унга мен изн бермай туриб иймон келтирдингизми? Шак-шубҳасиз, у сизларга сеҳр ўргатган каттангиздир. Бас, энди албатта оёқ-қўлларингизни қарама-қаршисига (яъни, ўнг қўл, чап оёғингизни ёки аксинча) кесурман ва сизларни хурмо шохларига осурман, (ана ўшанда) қайсимизнинг (яъни, менингми ё Мусонинг худоси) азоби қаттиқроқ ва узунроқ эканини билиб олурсизлар»'}, {'chapter': 20, 'verse': 72, 'text': 'قَالُواْ لَن نُّؤۡثِرَكَ عَلَىٰ مَا جَآءَنَا مِنَ ٱلۡبَيِّنَٰتِ وَٱلَّذِي فَطَرَنَاۖ فَٱقۡضِ مَآ أَنتَ قَاضٍۖ إِنَّمَا تَقۡضِي هَٰذِهِ ٱلۡحَيَوٰةَ ٱلدُّنۡيَآ\nУлар дедилар: «Бизлар ҳаргиз ўзимизга келган аниқ ҳужжат-мўъжизани ва бизларни яратган Зотни қўйиб, сени танламаймиз. Бас, қиладиган ҳукмингни қилавер. Сен фақат мана шу дунёдагина ҳукм қилурсан'}, {'chapter': 20, 'verse': 73, 'text': 'إِنَّآ ءَامَنَّا بِرَبِّنَا لِيَغۡفِرَ لَنَا خَطَٰيَٰنَا وَمَآ أَكۡرَهۡتَنَا عَلَيۡهِ مِنَ ٱلسِّحۡرِۗ وَٱللَّهُ خَيۡرٞ وَأَبۡقَىٰٓ\nБизлар эса хатоларимизни ва сен бизларни мажбур қилган сеҳргарликдан иборат (гуноҳимизни) мағрифат қилиши учун Парвардигоримизга иймон келтирдик. Аллоҳ(нинг савоби) яхшироқ ва (азоби) узунроқдир»'}, {'chapter': 20, 'verse': 74, 'text': 'إِنَّهُۥ مَن يَأۡتِ رَبَّهُۥ مُجۡرِمٗا فَإِنَّ لَهُۥ جَهَنَّمَ لَا يَمُوتُ فِيهَا وَلَا يَحۡيَىٰ\nҲеч шубҳа йўқки, кимда-ким Парвардигорининг (ҳузурига) жиноятчи-кофир бўлган ҳолда келса, у ҳолда албатта унинг учун жаҳаннам бор бўлиб, у (кофир) у жойда на ўла олур ва на яшай олур'}, {'chapter': 20, 'verse': 75, 'text': 'وَمَن يَأۡتِهِۦ مُؤۡمِنٗا قَدۡ عَمِلَ ٱلصَّـٰلِحَٰتِ فَأُوْلَـٰٓئِكَ لَهُمُ ٱلدَّرَجَٰتُ ٱلۡعُلَىٰ\nКим у зотга яхши амаллар қилган мўмин ҳолида келса, бас, ана ўша (кишилар) учун юксак даражалар'}, {'chapter': 20, 'verse': 76, 'text': 'جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ مَن تَزَكَّىٰ\nОстидан дарёлар оқадиган, улар абадий қоладиган жаннатлар бўлур. Бу (ширку-куфрдан) пок бўлган кишиларнинг мукофотидир'}, {'chapter': 20, 'verse': 77, 'text': 'وَلَقَدۡ أَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَسۡرِ بِعِبَادِي فَٱضۡرِبۡ لَهُمۡ طَرِيقٗا فِي ٱلۡبَحۡرِ يَبَسٗا لَّا تَخَٰفُ دَرَكٗا وَلَا تَخۡشَىٰ\nДарҳақиқат, Биз Мусога: «Сен бандаларимни (яъни, Бани Исроилни) тунда олиб кетиб, улар учун (суви) қуриган денгизни йўл қил — (ортларингдан Фиръавн) етиб олишидан хавф қилма ва (денгизга ғарқ бўлишдан ҳам) қўрқма», деб ваҳий юборганмиз'}, {'chapter': 20, 'verse': 78, 'text': 'فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ بِجُنُودِهِۦ فَغَشِيَهُم مِّنَ ٱلۡيَمِّ مَا غَشِيَهُمۡ\nБас, Фиръавн уларнинг ортидан ўз лашкарлари билан қувган эди, уларни денгиз ўраганча-ўраб, барчалари (унга ғарқ бўлдилар)'}, {'chapter': 20, 'verse': 79, 'text': 'وَأَضَلَّ فِرۡعَوۡنُ قَوۡمَهُۥ وَمَا هَدَىٰ\nФиръавн қавмини Тўғри Йўлга бошламади, (балки) Тўғри Йўлдан оздирди'}, {'chapter': 20, 'verse': 80, 'text': 'يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ قَدۡ أَنجَيۡنَٰكُم مِّنۡ عَدُوِّكُمۡ وَوَٰعَدۡنَٰكُمۡ جَانِبَ ٱلطُّورِ ٱلۡأَيۡمَنَ وَنَزَّلۡنَا عَلَيۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰ\nЭй Бани Исроил, Биз сизларга душманингиздан нажот бердик ва сизларга (яъни, Мусога) Таврот нозил қилиш учун Тур (тоғининг) ўнг томонини ваъдалашдик ва сизларга ширинлик, беданалар ёғдириб, (дедик)'}, {'chapter': 20, 'verse': 81, 'text': 'كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَلَا تَطۡغَوۡاْ فِيهِ فَيَحِلَّ عَلَيۡكُمۡ غَضَبِيۖ وَمَن يَحۡلِلۡ عَلَيۡهِ غَضَبِي فَقَدۡ هَوَىٰ\n«Сизларни баҳраманд қилган пок ризқларимиздан енглар ва уларга (шукр қилмаслик билан) ҳаддан ошмангларки, у ҳолда устингизга Менинг ғазабим тушар. Менинг ғазабим кимнинг устига тушар экан, у муҳаққақ ҳалок бўлур'}, {'chapter': 20, 'verse': 82, 'text': 'وَإِنِّي لَغَفَّارٞ لِّمَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا ثُمَّ ٱهۡتَدَىٰ\nВа мен тавба қилган, ҳамда иймон келтириб яхши амаллар қилган, сўнгра Тўғри Йўлга юрган кишиларни мағфират қилгувчидирман»'}, {'chapter': 20, 'verse': 83, 'text': '۞وَمَآ أَعۡجَلَكَ عَن قَوۡمِكَ يَٰمُوسَىٰ\n(Бани Исроил қавмидан етмиш нафарини ўзи билан бирга олиб, Тур тоғи томонга кетаётган Мусо йўлда улардан илгарилаб кетади. Шунда Аллоҳ деди): «Қавмингдан мунчалар илгарилаб кетдинг, эй Мусо!»'}, {'chapter': 20, 'verse': 84, 'text': 'قَالَ هُمۡ أُوْلَآءِ عَلَىٰٓ أَثَرِي وَعَجِلۡتُ إِلَيۡكَ رَبِّ لِتَرۡضَىٰ\nУ айтди: «Улар ана — изимдан келурлар. Мен эса Сен рози бўлишинг учун шошдим, Парвардигорим!»'}, {'chapter': 20, 'verse': 85, 'text': 'قَالَ فَإِنَّا قَدۡ فَتَنَّا قَوۡمَكَ مِنۢ بَعۡدِكَ وَأَضَلَّهُمُ ٱلسَّامِرِيُّ\n(Аллоҳ) деди: «Ҳақиқатан, Биз сенинг ортингдан қавмингни фитнага солдик — имтиҳон қилдик ва Сомирий уларни йўлдан оздирди»'}, {'chapter': 20, 'verse': 86, 'text': 'فَرَجَعَ مُوسَىٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗاۚ قَالَ يَٰقَوۡمِ أَلَمۡ يَعِدۡكُمۡ رَبُّكُمۡ وَعۡدًا حَسَنًاۚ أَفَطَالَ عَلَيۡكُمُ ٱلۡعَهۡدُ أَمۡ أَرَدتُّمۡ أَن يَحِلَّ عَلَيۡكُمۡ غَضَبٞ مِّن رَّبِّكُمۡ فَأَخۡلَفۡتُم مَّوۡعِدِي\nБас, Мусо қавмига ғазабланган ва ғамга ботган ҳолда қайтиб келиб, деди: «Эй қавмим, Парвардигорингиз сизларга (Таврот нозил этиш ҳақида) чиройли ваъда қилмаганмиди?! Сизларга (ўтган) вақт узун (кўриниб) кетдими ёки устингизга Парвардигорингиз томонидан ғазаб тушишини истаб менга берган ваъдани буздингларми'}, {'chapter': 20, 'verse': 87, 'text': 'قَالُواْ مَآ أَخۡلَفۡنَا مَوۡعِدَكَ بِمَلۡكِنَا وَلَٰكِنَّا حُمِّلۡنَآ أَوۡزَارٗا مِّن زِينَةِ ٱلۡقَوۡمِ فَقَذَفۡنَٰهَا فَكَذَٰلِكَ أَلۡقَى ٱلسَّامِرِيُّ\nУлар айтдилар: «Бизлар сенга берган ваъдани ўз инон-ихтиёримиз билан бузганимиз йўқ. Лекин бизлар (Мисрдан чиқиб келаётганимизда Фиръавн) қавмининг зеб-зийнатларидан иборат нарсаларни кўтариб чиққан эдик, бас (Сомирийнинг амри билан) уларни (оловга) ташладик. Сўнг Сомирий ҳам (ўзи кўтариб чиққан нарсаларни оловга) ташлади'}, {'chapter': 20, 'verse': 88, 'text': 'فَأَخۡرَجَ لَهُمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٞ فَقَالُواْ هَٰذَآ إِلَٰهُكُمۡ وَإِلَٰهُ مُوسَىٰ فَنَسِيَ\nСўнг (Сомирий ўша зеб-зийнатларни оловда эритиб, худди тирик бузоқ каби) овози бор ҳайкал-бузоқ (ясаб) чиқариб берди, сўнг (Сомирий ва унинг шериклари): «Сизларнинг ҳам, Мусонинг ҳам илоҳи шу эди, (Мусо) унутиб, (уни бошқа ердан излагани кетибди»), дедилар'}, {'chapter': 20, 'verse': 89, 'text': 'أَفَلَا يَرَوۡنَ أَلَّا يَرۡجِعُ إِلَيۡهِمۡ قَوۡلٗا وَلَا يَمۡلِكُ لَهُمۡ ضَرّٗا وَلَا نَفۡعٗا\nАхир унинг ўзларига бирон сўз қайтара олмаётганини ва бирон зиён ҳам, фойда ҳам етказишга қодир эмаслигини кўрмайдиларми'}, {'chapter': 20, 'verse': 90, 'text': 'وَلَقَدۡ قَالَ لَهُمۡ هَٰرُونُ مِن قَبۡلُ يَٰقَوۡمِ إِنَّمَا فُتِنتُم بِهِۦۖ وَإِنَّ رَبَّكُمُ ٱلرَّحۡمَٰنُ فَٱتَّبِعُونِي وَأَطِيعُوٓاْ أَمۡرِي\nҲолбуки, (бузоққа сиғинишларидан) илгари Ҳорун уларга: «Эй қавмим, сизлар бу билан фақат имтиҳон қилиндинглар, холос, (бас, у бузоққа сиғинманглар!) Шак-шубҳа йўқки, сизларнинг Парвардигорингиз ёлғиз Раҳмон — Марҳаматли Аллоҳдир. Бас, менга эргашинглар ва амримга итоат қилинглар!», деганида'}, {'chapter': 20, 'verse': 91, 'text': 'قَالُواْ لَن نَّبۡرَحَ عَلَيۡهِ عَٰكِفِينَ حَتَّىٰ يَرۡجِعَ إِلَيۡنَا مُوسَىٰ\nУлар: «То бизларга Мусо қайтмагунича, унга (шу бутга) ибодат қилиб — сиғинишдан ҳаргиз ажралмаймиз», деган эдилар'}, {'chapter': 20, 'verse': 92, 'text': 'قَالَ يَٰهَٰرُونُ مَا مَنَعَكَ إِذۡ رَأَيۡتَهُمۡ ضَلُّوٓاْ\n(Мусо қайтиб келгач), деди: «Эй Ҳорун, сен уларнинг йўлдан озганларини кўрган пайтингда (ва менга бу ҳақда хабар беришингдан) сени нима тўсди'}, {'chapter': 20, 'verse': 93, 'text': 'أَلَّا تَتَّبِعَنِۖ أَفَعَصَيۡتَ أَمۡرِي\nМенинг ортимдан боришингдан (сени нима тўсди?!). Менинг амримга итоатсизлик қилдингми?!»'}, {'chapter': 20, 'verse': 94, 'text': 'قَالَ يَبۡنَؤُمَّ لَا تَأۡخُذۡ بِلِحۡيَتِي وَلَا بِرَأۡسِيٓۖ إِنِّي خَشِيتُ أَن تَقُولَ فَرَّقۡتَ بَيۡنَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَلَمۡ تَرۡقُبۡ قَوۡلِي\n«Эй онамнинг ўғли, сен менинг соч-соқолимдан тортмагин. Мен (агар бузоққа сиғинаётганларни қўйиб, йўлдан озмаган кишилар билан сенинг ортингдан борсам) «Менинг сўзимни кутмай, Бани Исроилни бўлиб юборибсан», дейишингдан қўрқдим»'}, {'chapter': 20, 'verse': 95, 'text': 'قَالَ فَمَا خَطۡبُكَ يَٰسَٰمِرِيُّ\n(Сўнгра Мусо Сомирийга қараб): «Бу нима қилганинг, эй Сомирий?», деди'}, {'chapter': 20, 'verse': 96, 'text': 'قَالَ بَصُرۡتُ بِمَا لَمۡ يَبۡصُرُواْ بِهِۦ فَقَبَضۡتُ قَبۡضَةٗ مِّنۡ أَثَرِ ٱلرَّسُولِ فَنَبَذۡتُهَا وَكَذَٰلِكَ سَوَّلَتۡ لِي نَفۡسِي\nУ айтди: «Мен улар (яъни, Бани Исроил) кўрмаган нарсани кўрдим. (Яъни, Жаброил сенинг олдингга келганида ҳаёт отига миниб келган эди. Унинг туёғи теккан тупроқ қандай жонсиз нарсага тегса, дарҳол унга жон кирарди, мен шуни кўриб қолган эдим), бас, элчи — Жаброилнинг босган изидан бир сиқим олиб, уни (бузоқнинг ҳайкалига) сочган эдим (ундан тирик бузоқ каби овоз чиқди. Буни кўрган Бани Исроил қавмидан бўлган одамлар ўша бузоққа сиғина бошладилар). Менга нафсим ана шундай қилишни чиройли кўрсатди»'}, {'chapter': 20, 'verse': 97, 'text': 'قَالَ فَٱذۡهَبۡ فَإِنَّ لَكَ فِي ٱلۡحَيَوٰةِ أَن تَقُولَ لَا مِسَاسَۖ وَإِنَّ لَكَ مَوۡعِدٗا لَّن تُخۡلَفَهُۥۖ وَٱنظُرۡ إِلَىٰٓ إِلَٰهِكَ ٱلَّذِي ظَلۡتَ عَلَيۡهِ عَاكِفٗاۖ لَّنُحَرِّقَنَّهُۥ ثُمَّ لَنَنسِفَنَّهُۥ فِي ٱلۡيَمِّ نَسۡفًا\n(Мусо) деди: «Бас, йўқол! Энди сен ҳаётда «Менга тегманглар», дейишгина бордир (яъни, умрингнинг охиригача якка мохов бўлиб қолурсан. Охиратда эса) сен учун ҳаргиз хилоф қилинмайдиган бир ваъда — азоб бордир. Сен устидан жилмай ибодат қилган «илоҳингнинг» (ҳолини) кўриб қўй! Биз албатта уни ёндириб, сўнгра (кулини) денгизга сочиб юборурмиз»'}, {'chapter': 20, 'verse': 98, 'text': 'إِنَّمَآ إِلَٰهُكُمُ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَۚ وَسِعَ كُلَّ شَيۡءٍ عِلۡمٗا\n(Эй инсонлар), илоҳингиз яккаю ёлғиз Аллоҳдир! Ҳеч қандай илоҳ йўқ, магар Унинг Ўзи бордир. У барча нарсани Ўз илми билан қамраб олгандир'}, {'chapter': 20, 'verse': 99, 'text': 'كَذَٰلِكَ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ مَا قَدۡ سَبَقَۚ وَقَدۡ ءَاتَيۡنَٰكَ مِن لَّدُنَّا ذِكۡرٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизга ўтиб кетган (авлодларнинг) хабарларини мана шундай сўйлаб берурмиз. Дарҳақиқат, Биз сизга Ўз даргоҳимиздан эслатма — Қуръон ато этдик'}, {'chapter': 20, 'verse': 100, 'text': 'مَّنۡ أَعۡرَضَ عَنۡهُ فَإِنَّهُۥ يَحۡمِلُ يَوۡمَ ٱلۡقِيَٰمَةِ وِزۡرًا\nКим ундан юз ўгирса, бас, албатта у Қиёмат Кунида (гуноҳнинг энг оғир) юкини кўтариб турар'}, {'chapter': 20, 'verse': 101, 'text': 'خَٰلِدِينَ فِيهِۖ وَسَآءَ لَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ حِمۡلٗا\nУлар у юк остида абадий қолурлар. У (юк) Қиёмат Кунида улар учун жуда оғир юк бўлур'}, {'chapter': 20, 'verse': 102, 'text': 'يَوۡمَ يُنفَخُ فِي ٱلصُّورِۚ وَنَحۡشُرُ ٱلۡمُجۡرِمِينَ يَوۡمَئِذٖ زُرۡقٗا\nУ сур чалинадиган кундир. У кунда Биз жиноятчи-кофир кимсаларни кўзлари кўк (яъни, нурсиз, юзлари қаро) бўлган ҳолларида йиғурмиз'}, {'chapter': 20, 'verse': 103, 'text': 'يَتَخَٰفَتُونَ بَيۡنَهُمۡ إِن لَّبِثۡتُمۡ إِلَّا عَشۡرٗا\nУлар ўзаро шивирлашиб: «(Дунёда) ўн кунгина турдинглар», (дейдилар)'}, {'chapter': 20, 'verse': 104, 'text': 'نَّحۡنُ أَعۡلَمُ بِمَا يَقُولُونَ إِذۡ يَقُولُ أَمۡثَلُهُمۡ طَرِيقَةً إِن لَّبِثۡتُمۡ إِلَّا يَوۡمٗا\nБиз уларнинг айтадиган сўзларини жуда яхши билурмиз. Ўшанда уларнинг энг намунали йўлни тутган — донолари: «Бир кунгина турдинглар», дейди(лар)'}, {'chapter': 20, 'verse': 105, 'text': 'وَيَسۡـَٔلُونَكَ عَنِ ٱلۡجِبَالِ فَقُلۡ يَنسِفُهَا رَبِّي نَسۡفٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), улар сиздан тоғлар (Қиёмат Кунида қандай ҳолда бўлиши) ҳақида сўрайдилар. Бас, айтинг: «Парвардигорим уларни (қум каби) сочиб юборур'}, {'chapter': 20, 'verse': 106, 'text': 'فَيَذَرُهَا قَاعٗا صَفۡصَفٗا\nСўнг (ерни) теп-текис қилиб қўюрки'}, {'chapter': 20, 'verse': 107, 'text': 'لَّا تَرَىٰ فِيهَا عِوَجٗا وَلَآ أَمۡتٗا\nУнда на чуқурни ва на дўнгликни кўрурсиз»'}, {'chapter': 20, 'verse': 108, 'text': 'يَوۡمَئِذٖ يَتَّبِعُونَ ٱلدَّاعِيَ لَا عِوَجَ لَهُۥۖ وَخَشَعَتِ ٱلۡأَصۡوَاتُ لِلرَّحۡمَٰنِ فَلَا تَسۡمَعُ إِلَّا هَمۡسٗا\nУ кунда (одамлар маҳшаргоҳга) чорловчи (фариштага) эгилмай-бурилмай эргашурлар — итоат қилурлар. Овозлар ҳам Раҳмонга таъзим қилур, бас, фақат пичирлашнигина эшитурсиз'}, {'chapter': 20, 'verse': 109, 'text': 'يَوۡمَئِذٖ لَّا تَنفَعُ ٱلشَّفَٰعَةُ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَٰنُ وَرَضِيَ لَهُۥ قَوۡلٗا\nУ кунда шафоат — қўллов фойда бермас, магар Раҳмон изн берган ва сўзидан У зот рози бўлган кишигагина (фойда берур)'}, {'chapter': 20, 'verse': 110, 'text': 'يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَلَا يُحِيطُونَ بِهِۦ عِلۡمٗا\nУ зот уларнинг олдиларидаги (яъни, Охиратдаги) ва ортларидаги (яъни, дунёдаги) бор нарсани билур. Улар эса У зотни била олмаслар'}, {'chapter': 20, 'verse': 111, 'text': '۞وَعَنَتِ ٱلۡوُجُوهُ لِلۡحَيِّ ٱلۡقَيُّومِۖ وَقَدۡ خَابَ مَنۡ حَمَلَ ظُلۡمٗا\nЮзлар ҳам Тирик ва абадий Тургувчи (У) зотга таъзим қилур. Ким зулмни (ширкни) кўтариб келган бўлса ноумид бўлиши аниқдир'}, {'chapter': 20, 'verse': 112, 'text': 'وَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ وَهُوَ مُؤۡمِنٞ فَلَا يَخَافُ ظُلۡمٗا وَلَا هَضۡمٗا\nКим мўмин бўлган ҳолда яхши амаллар қилса, бас, у зулмдан ҳам (яъни, унга зулм қилиниб, ўзи қилмаган гуноҳларга масъул бўлишдан ҳам), камайишдан ҳам (яъни, қилган яхши амалларининг савоб-мукофоти тўла берилмай қолишидан ҳам) хавф қилмас'}, {'chapter': 20, 'verse': 113, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَٰهُ قُرۡءَانًا عَرَبِيّٗا وَصَرَّفۡنَا فِيهِ مِنَ ٱلۡوَعِيدِ لَعَلَّهُمۡ يَتَّقُونَ أَوۡ يُحۡدِثُ لَهُمۡ ذِكۡرٗا\nШунингдек, Биз уни арабий Қуръон қилиб туширдик ва унда қўрқинчли ваъдаларни (такрор-такрор) баён қилдик. Шояд улар (ширк ва куфрдан) сақлансалар ё (бу ваъдалар) улар учун эслатма-ибратлар пайдо қилса'}, {'chapter': 20, 'verse': 114, 'text': 'فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۗ وَلَا تَعۡجَلۡ بِٱلۡقُرۡءَانِ مِن قَبۡلِ أَن يُقۡضَىٰٓ إِلَيۡكَ وَحۡيُهُۥۖ وَقُل رَّبِّ زِدۡنِي عِلۡمٗا\nҲақ Подшоҳ — Аллоҳ юксакдир. (Эй Муҳаммад алайҳис-салоту вас-салом), сизга (Қуръон) ваҳийси битишидан илгари қироат қилишга шошманг (балки фаришта Жаброил Аллоҳ томонидан келтирган оятни тўла ўқиб бўлганидан кейингина қироат қилинг) ва айтинг: «Парвардигорим, илмимни янада зиёда қилгин»'}, {'chapter': 20, 'verse': 115, 'text': 'وَلَقَدۡ عَهِدۡنَآ إِلَىٰٓ ءَادَمَ مِن قَبۡلُ فَنَسِيَ وَلَمۡ نَجِدۡ لَهُۥ عَزۡمٗا\nДарҳақиқат, Биз илгари Одам билан (Жаннатдаги бир дарахтга яқинлашмаслиги ҳақида) аҳдлашган эдик. У (аҳдни) унутди ва Биз унинг учун қасд-собитқадамликни кўрмадик'}, {'chapter': 20, 'verse': 116, 'text': 'وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ\nЭсланг, Биз фаришталар: «Одамга сажда қилинглар», дейишимиз билан саждага эгилдилар, фақат иблис бош тортди'}, {'chapter': 20, 'verse': 117, 'text': 'فَقُلۡنَا يَـٰٓـَٔادَمُ إِنَّ هَٰذَا عَدُوّٞ لَّكَ وَلِزَوۡجِكَ فَلَا يُخۡرِجَنَّكُمَا مِنَ ٱلۡجَنَّةِ فَتَشۡقَىٰٓ\nБас, Биз айтдик: «Эй Одам, албатта бу (иблис) сенга ҳам, хотинингга ҳам душмандир. Бас, у икковингизни жаннатдан чиқариб, бахтсиз бўлиб қолмагин'}, {'chapter': 20, 'verse': 118, 'text': 'إِنَّ لَكَ أَلَّا تَجُوعَ فِيهَا وَلَا تَعۡرَىٰ\nШубҳасиз, сен у жойда (яъни, жаннатда) оч-яланғоч қолмайсан'}, {'chapter': 20, 'verse': 119, 'text': 'وَأَنَّكَ لَا تَظۡمَؤُاْ فِيهَا وَلَا تَضۡحَىٰ\nВа у жойда ташна ҳам бўлмайсан, иссиқда ҳам қолмайсан»'}, {'chapter': 20, 'verse': 120, 'text': 'فَوَسۡوَسَ إِلَيۡهِ ٱلشَّيۡطَٰنُ قَالَ يَـٰٓـَٔادَمُ هَلۡ أَدُلُّكَ عَلَىٰ شَجَرَةِ ٱلۡخُلۡدِ وَمُلۡكٖ لَّا يَبۡلَىٰ\nСўнг шайтон унга васваса қилиб: «Эй Одам, мен сенга абадият дарахтини ва йўқ бўлмас мулку давлатни кўрсатайми?», деди'}, {'chapter': 20, 'verse': 121, 'text': 'فَأَكَلَا مِنۡهَا فَبَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۚ وَعَصَىٰٓ ءَادَمُ رَبَّهُۥ فَغَوَىٰ\nБас, (Одам билан Ҳавво) ундан ейишлари билан авратлари очилиб қолди ва ўзларини жаннат япроқлари билан тўса бошладилар. Одам Парвардигорига осий бўлиб, йўлдан озди'}, {'chapter': 20, 'verse': 122, 'text': 'ثُمَّ ٱجۡتَبَٰهُ رَبُّهُۥ فَتَابَ عَلَيۡهِ وَهَدَىٰ\nСўнгра Парвардигори уни сайлаб — Ўзига яқин қилиб, тавбасини қабул этди ва Тўғри Йўлга ҳидоят қилди'}, {'chapter': 20, 'verse': 123, 'text': 'قَالَ ٱهۡبِطَا مِنۡهَا جَمِيعَۢاۖ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَنِ ٱتَّبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشۡقَىٰ\n(Аллоҳ) айтди: «Ундан (яъни, жаннатдан) ҳар иккингиз тушингиз. Айрим (зурриётларингиз) айримларига душмандир. Бас, сизларга Мен тарафдан ҳидоят келганида ким Менинг ҳидоятимга эргашса, йўлдан озмас ва бахтсиз бўлмас'}, {'chapter': 20, 'verse': 124, 'text': 'وَمَنۡ أَعۡرَضَ عَن ذِكۡرِي فَإِنَّ لَهُۥ مَعِيشَةٗ ضَنكٗا وَنَحۡشُرُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ أَعۡمَىٰ\nКим Менинг эслатмамдан юз ўгирса, бас, албатта унинг учун танг — бахтсиз ҳаёт бўлур ва Биз уни Қиёмат Кунида кўр ҳолда тирилтирурмиз»'}, {'chapter': 20, 'verse': 125, 'text': 'قَالَ رَبِّ لِمَ حَشَرۡتَنِيٓ أَعۡمَىٰ وَقَدۡ كُنتُ بَصِيرٗا\nУ: «Парвардигорим, нега мени кўр қилиб тирилтирдинг, ахир кўрар эдим-ку», деган эди'}, {'chapter': 20, 'verse': 126, 'text': 'قَالَ كَذَٰلِكَ أَتَتۡكَ ءَايَٰتُنَا فَنَسِيتَهَاۖ وَكَذَٰلِكَ ٱلۡيَوۡمَ تُنسَىٰ\n(Аллоҳ) айтди: «Шундай. Сенга Бизнинг оят-мўъжизаларимиз келганида уларни унутдинг. Бугун сен ҳам ана шундай «унутилурсан»'}, {'chapter': 20, 'verse': 127, 'text': 'وَكَذَٰلِكَ نَجۡزِي مَنۡ أَسۡرَفَ وَلَمۡ يُؤۡمِنۢ بِـَٔايَٰتِ رَبِّهِۦۚ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَشَدُّ وَأَبۡقَىٰٓ\nҲаддан ошган ва Парвардигорининг оятларига иймон келтирмаган кимсаларни мана шундай жазолармиз. Охират азоби шак-шубҳасиз, қаттиқроқ ва давомлироқдир'}, {'chapter': 20, 'verse': 128, 'text': 'أَفَلَمۡ يَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ يَمۡشُونَ فِي مَسَٰكِنِهِمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّأُوْلِي ٱلنُّهَىٰ\nБиз улардан илгари ҳам уларнинг масканларида (яшаб) юрган қанча авлодларни (куфрлари сабабли) ҳалок қилганимиз уларга маълум эмасми?! Албатта бунда ақл эгалари учун оят-ибратлар бордир'}, {'chapter': 20, 'verse': 129, 'text': 'وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَكَانَ لِزَامٗا وَأَجَلٞ مُّسَمّٗى\nАгар Парвардигорингиз томонидан (барча яхши-ёмон амалларнинг мукофот-жазолари Қиёматда бўлиши хусусида) Сўз ва белгиланган муддат бўлмаганида эди, албатта (улар дарҳол азобга гирифтор бўлишлари) лозим эди'}, {'chapter': 20, 'verse': 130, 'text': 'فَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ غُرُوبِهَاۖ وَمِنۡ ءَانَآيِٕ ٱلَّيۡلِ فَسَبِّحۡ وَأَطۡرَافَ ٱلنَّهَارِ لَعَلَّكَ تَرۡضَىٰ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), улар айтаётган сўзларга сабр-тоқат қилинг ва қуёш чиқишидан илгари ва ботишидан аввал Парвардигорингизга ҳамду сано айтиш билан (У зотни покланг — намоз ўқинг! Кеча(нинг аввалги ва охирги) вақтларида ҳамда кундуз(нинг аввалги ва охирги тарафларида ҳам тасбеҳ айтинг — намоз ўқинг, шояд хурсанд бўлурсиз)'}, {'chapter': 20, 'verse': 131, 'text': 'وَلَا تَمُدَّنَّ عَيۡنَيۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّنۡهُمۡ زَهۡرَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا لِنَفۡتِنَهُمۡ فِيهِۚ وَرِزۡقُ رَبِّكَ خَيۡرٞ وَأَبۡقَىٰ\nСиз кўзларингизни Биз (кофирлардан айрим) тоифаларни фитнага солиш учун баҳраманд қилган — ҳаёти дунё гўзалликларидан иборат нарсаларга тикманг! Парвардигорингизнинг ризқи яхшироқ ва боқийроқдир'}, {'chapter': 20, 'verse': 132, 'text': 'وَأۡمُرۡ أَهۡلَكَ بِٱلصَّلَوٰةِ وَٱصۡطَبِرۡ عَلَيۡهَاۖ لَا نَسۡـَٔلُكَ رِزۡقٗاۖ نَّحۡنُ نَرۡزُقُكَۗ وَٱلۡعَٰقِبَةُ لِلتَّقۡوَىٰ\nАҳли-умматингизни намоз ўқишга буюринг ва ўзингиз ҳам (намоз ўқишда) чидамли бўлинг! Биз сиздан ризқ сўрамаймиз, (билъакс) Ўзимиз сизга ризқ берурмиз. Оқибат-жаннат — (аҳли) тақвоникидир'}, {'chapter': 20, 'verse': 133, 'text': 'وَقَالُواْ لَوۡلَا يَأۡتِينَا بِـَٔايَةٖ مِّن رَّبِّهِۦٓۚ أَوَلَمۡ تَأۡتِهِم بَيِّنَةُ مَا فِي ٱلصُّحُفِ ٱلۡأُولَىٰ\n(Мушриклар: «Муҳаммад) Парвардигори томонидан бизларга бирон оят-мўъжиза келтирса эди», дедилар. Ахир уларга илгариги саҳифалардаги (яъни, аввалги илоҳий китоблардаги) нарсаларнинг баёни (яъни, Қуръон) келмадими'}, {'chapter': 20, 'verse': 134, 'text': 'وَلَوۡ أَنَّآ أَهۡلَكۡنَٰهُم بِعَذَابٖ مِّن قَبۡلِهِۦ لَقَالُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ مِن قَبۡلِ أَن نَّذِلَّ وَنَخۡزَىٰ\nАгар Биз уларни (Муҳаммад алайҳис-салоту вас-салом келишидан ва Қуръон нозил бўлишидан) илгари бирон азоб билан ҳалок қилганимизда, улар албатта: «Парвардигоро, бизларга бирон пайғамбар юборганингда эди, бизлар хор-зор ва шарманда бўлмай, Сенинг оятларингга эргашар — итоат этар эдик», деган бўлур эдилар'}, {'chapter': 20, 'verse': 135, 'text': 'قُلۡ كُلّٞ مُّتَرَبِّصٞ فَتَرَبَّصُواْۖ فَسَتَعۡلَمُونَ مَنۡ أَصۡحَٰبُ ٱلصِّرَٰطِ ٱلسَّوِيِّ وَمَنِ ٱهۡتَدَىٰ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Барча(миз) кутгувчидир(миз). (Яъни, сизлар ҳам, бизлар ҳам охир-оқибатда ким ҳақ, ким ноҳақ бўлиб чиқишини кутмоқдамиз). Бас, кутаверинглар! Яқинда ким Тўғри Йўл эгалари ва кимлар ҳидоят топган кишилар эканини билурсизлар»'}, {'chapter': 21, 'verse': 1, 'text': 'ٱقۡتَرَبَ لِلنَّاسِ حِسَابُهُمۡ وَهُمۡ فِي غَفۡلَةٖ مُّعۡرِضُونَ\nОдамларга ҳисоб-китоблари (яъни, Қиёмат қойим бўлиши) яқинлашиб қолди. Улар эса ғафлатда, (иймон келтириб, яхши амаллар қилишдан) юз ўгиргувчидирлар'}, {'chapter': 21, 'verse': 2, 'text': 'مَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّن رَّبِّهِم مُّحۡدَثٍ إِلَّا ٱسۡتَمَعُوهُ وَهُمۡ يَلۡعَبُونَ\nУларга Парвардигорлари томонидан бирон янги эслатма — оят келар экан, албатта уни ўйнаган ҳолларида (масхара қилиб), тингладилар'}, {'chapter': 21, 'verse': 3, 'text': 'لَاهِيَةٗ قُلُوبُهُمۡۗ وَأَسَرُّواْ ٱلنَّجۡوَى ٱلَّذِينَ ظَلَمُواْ هَلۡ هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡۖ أَفَتَأۡتُونَ ٱلسِّحۡرَ وَأَنتُمۡ تُبۡصِرُونَ\nДиллари ғофил бўлган ҳолларида (тингладилар) ва золим-кофир кимсалар: «Бу (яъни, Муҳаммад) ўзларингизга ўхшаган бир одам холос-ку! Кўриб турган ҳолингизда сеҳр-жодуга (алданиш учун) келаверасизларми?!», (деб) ўзаро шивирлашдилар'}, {'chapter': 21, 'verse': 4, 'text': 'قَالَ رَبِّي يَعۡلَمُ ٱلۡقَوۡلَ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\n(Шунда Муҳаммад алайҳис-салоту вас-салом) айтди: «Парвардигорим осмону-заминдаги ҳар бир сўзни билур. У эшитгувчи, билгувчидир»'}, {'chapter': 21, 'verse': 5, 'text': 'بَلۡ قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمِۭ بَلِ ٱفۡتَرَىٰهُ بَلۡ هُوَ شَاعِرٞ فَلۡيَأۡتِنَا بِـَٔايَةٖ كَمَآ أُرۡسِلَ ٱلۡأَوَّلُونَ\n«Балки — дедилар улар — (Муҳаммад ваҳий деб даъво қилаётган сўзлар) — алоқ-чалоқ тушлардир, балки (бу сўзларни) у ўзи тўқиб олгандир, балки у бир шоирдир. Бас, у ҳам илгари юборилган пайғамбарлар (келтирганлари) каби бирон оят-мўъжиза келтирсин!»'}, {'chapter': 21, 'verse': 6, 'text': 'مَآ ءَامَنَتۡ قَبۡلَهُم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَآۖ أَفَهُمۡ يُؤۡمِنُونَ\nУлардан олдин, Биз ҳалок қилган бирон қишлоқ-шаҳар (аҳолиси) иймон келтирган эмас, бас, улар иймон келтирармидилар'}, {'chapter': 21, 'verse': 7, 'text': 'وَمَآ أَرۡسَلۡنَا قَبۡلَكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِمۡۖ فَسۡـَٔلُوٓاْ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан илгари ҳам фақат кишиларни (яъни, инсонларни) Ўзимиз ваҳий юборган ҳолда пайғамбар қилгандирмиз. Бас, (эй Макка аҳли), агар ўзларингиз билмайдиган бўлсангизлар (Таврот, Инжилни биладиган) аҳли илмлардан сўранглар'}, {'chapter': 21, 'verse': 8, 'text': 'وَمَا جَعَلۡنَٰهُمۡ جَسَدٗا لَّا يَأۡكُلُونَ ٱلطَّعَامَ وَمَا كَانُواْ خَٰلِدِينَ\nБиз у (пайғамбар)ларни таом емайдиган бир жасад қилган эмасмиз ва улар мангу ҳаёт кечиргувчи ҳам эмас эдилар'}, {'chapter': 21, 'verse': 9, 'text': 'ثُمَّ صَدَقۡنَٰهُمُ ٱلۡوَعۡدَ فَأَنجَيۡنَٰهُمۡ وَمَن نَّشَآءُ وَأَهۡلَكۡنَا ٱلۡمُسۡرِفِينَ\nСўнгра Биз (берган) ваъдамизга вафо қилиб, у (пайғамбар)ларга Ўзимиз хоҳлаган (иймонли) кишилар билан бирга нажот берганмиз, ҳаддан ошувчи кимсаларни — кофирларни эса ҳалок қилганмиз'}, {'chapter': 21, 'verse': 10, 'text': 'لَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ كِتَٰبٗا فِيهِ ذِكۡرُكُمۡۚ أَفَلَا تَعۡقِلُونَ\nДарҳақиқат, Биз сизларга бир Китоб — Қуръон нозил қилдикки, унда сизлар учун зикр (яъни, шаъну шараф) бордир. (Чунки у сизларнинг тилингизда нозил қилинди). Ақл юргизмайсизларми'}, {'chapter': 21, 'verse': 11, 'text': 'وَكَمۡ قَصَمۡنَا مِن قَرۡيَةٖ كَانَتۡ ظَالِمَةٗ وَأَنشَأۡنَا بَعۡدَهَا قَوۡمًا ءَاخَرِينَ\nҚанча золим-кофир бўлган қишлоқ-шаҳарларнинг (аҳлини) ҳалок қилдик ва уларнинг ортидан (бутунлай) бошқа қавм-авлодни вужудга келтирдик'}, {'chapter': 21, 'verse': 12, 'text': 'فَلَمَّآ أَحَسُّواْ بَأۡسَنَآ إِذَا هُم مِّنۡهَا يَرۡكُضُونَ\nБас, қачонки улар (яъни, ҳалок қилинган кимсалар ҳалокатлари олдида) Бизнинг азобимиз (тушишини) сезиб қолишгач, баногоҳ у жойдан қочиб қолурлар'}, {'chapter': 21, 'verse': 13, 'text': 'لَا تَرۡكُضُواْ وَٱرۡجِعُوٓاْ إِلَىٰ مَآ أُتۡرِفۡتُمۡ فِيهِ وَمَسَٰكِنِكُمۡ لَعَلَّكُمۡ تُسۡـَٔلُونَ\n(Шу пайт фаришталар): «Қочманглар, маишатга ботган жойларингизга, ўз масканларингизга қайтинглар, эҳтимол жавоб берарсизлар», (деганларида)'}, {'chapter': 21, 'verse': 14, 'text': 'قَالُواْ يَٰوَيۡلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ\n(Улар бир-бирларига): «Эй ҳолимизга вой! Дарҳақиқат, бизлар (ўзимизга) золим бўлдик», дедилар'}, {'chapter': 21, 'verse': 15, 'text': 'فَمَا زَالَت تِّلۡكَ دَعۡوَىٰهُمۡ حَتَّىٰ جَعَلۡنَٰهُمۡ حَصِيدًا خَٰمِدِينَ\nБас, то Биз уларни (ўт-ўлан каби) ўрилган, (ўтин каби) ўчган ҳолга солгунимизча уларнинг ана ўша дод-войлари давом этди'}, {'chapter': 21, 'verse': 16, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَآءَ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا لَٰعِبِينَ\nБиз осмон ва Ерни ҳамда уларнинг орасидаги нарсаларни ўйнаб-беҳуда яратганимиз йўқ. (Балки Ўз қудратимизни намойиш қилиш учун ва бандаларимиз фойдаланишлари учун яратдик)'}, {'chapter': 21, 'verse': 17, 'text': 'لَوۡ أَرَدۡنَآ أَن نَّتَّخِذَ لَهۡوٗا لَّٱتَّخَذۡنَٰهُ مِن لَّدُنَّآ إِن كُنَّا فَٰعِلِينَ\nАгар Биз вақтичоғлик қилишни (яъни, хотин, бола-чақа орттиришни) истасак, агар (шундай) қилгувчи бўлган чоғимизда ҳам уни (яъни, хотин, бола-чақани) Ўз даргоҳимиздан (яъни, малоикалардан ёки жаннат ҳурларидан) қилган бўлур эдик'}, {'chapter': 21, 'verse': 18, 'text': 'بَلۡ نَقۡذِفُ بِٱلۡحَقِّ عَلَى ٱلۡبَٰطِلِ فَيَدۡمَغُهُۥ فَإِذَا هُوَ زَاهِقٞۚ وَلَكُمُ ٱلۡوَيۡلُ مِمَّا تَصِفُونَ\nЙўқ, (Биз ундай айбу нуқсондан покдирмиз), Биз Ҳақ Қуръонни ботил-жаҳолатнинг устига отурмиз, бас (ҳақиқат ботилни) эзиб-янчиб, баногоҳ (ботил) йўқ бўлур. Сизлар учун эса (мушриклар, Аллоҳни «хотин, боласи бор», деб нолойиқ сифатлар билан) сифатлаганларингиз сабабли ҳалокат бўлур'}, {'chapter': 21, 'verse': 19, 'text': 'وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَنۡ عِندَهُۥ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَلَا يَسۡتَحۡسِرُونَ\nОсмонлар ва Ердаги бор жонзот Уникидир. Унинг ҳузуридаги зотлар (яъни, фаришталар) Унга ибодат қилишдан орланиб-зорланмайдилар'}, {'chapter': 21, 'verse': 20, 'text': 'يُسَبِّحُونَ ٱلَّيۡلَ وَٱلنَّهَارَ لَا يَفۡتُرُونَ\nУлар туну кун сусткашлик қилмасдан (Аллоҳни) поклайдилар'}, {'chapter': 21, 'verse': 21, 'text': 'أَمِ ٱتَّخَذُوٓاْ ءَالِهَةٗ مِّنَ ٱلۡأَرۡضِ هُمۡ يُنشِرُونَ\nЁки улар (яъни, мушриклар) Ернинг ўзидан (яъни, тош, ёғочлардан), (ўликларни) тирилтира оладиган «худоларни» топиб олдиларми'}, {'chapter': 21, 'verse': 22, 'text': 'لَوۡ كَانَ فِيهِمَآ ءَالِهَةٌ إِلَّا ٱللَّهُ لَفَسَدَتَاۚ فَسُبۡحَٰنَ ٱللَّهِ رَبِّ ٱلۡعَرۡشِ عَمَّا يَصِفُونَ\nАгар (осмону заминда) Аллоҳдан ўзга худолар булганида, ҳар иккиси бузилиб кетар эди. Бас, Арш Эгаси бўлмиш Аллоҳ улар сифатлаётган (шериклардан) покдир'}, {'chapter': 21, 'verse': 23, 'text': 'لَا يُسۡـَٔلُ عَمَّا يَفۡعَلُ وَهُمۡ يُسۡـَٔلُونَ\nУ Ўзи қиладиган бирон нарса ҳақида масъул бўлмас, улар (яъни, бандалар эса қиладиган ҳар бир иш-амаллари хусусида) масъул бўлурлар'}, {'chapter': 21, 'verse': 24, 'text': 'أَمِ ٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗۖ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡۖ هَٰذَا ذِكۡرُ مَن مَّعِيَ وَذِكۡرُ مَن قَبۡلِيۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ ٱلۡحَقَّۖ فَهُم مُّعۡرِضُونَ\nЁки Уни қўйиб (бошқа) «худоларни» топиб олдиларми?! (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: («Эй мушриклар, мана шу ширкларингиз ҳақ эканлигига) ҳужжат-далилларингизни кўрсатингиз! Мана мен билан бирга бўлган (мўминларнинг) эслатмаси (яъни, Қуръон) ва мендан аввалгиларнинг эслатмалари (яъни, Таврот, Инжил, мана шу китобларнинг қайси бирида Аллоҳдан ўзга ҳам худолар мавжуд эканлигига ҳужжат-далил бор?!)» Йўқ, уларнинг аксарияти ҳақиқатни билмай туриб, (ундан) юз ўгирувчилардир'}, {'chapter': 21, 'verse': 25, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٍ إِلَّا نُوحِيٓ إِلَيۡهِ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعۡبُدُونِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан илгари юборган ҳар бир пайғамбарга ҳам: «Ҳеч қандай илоҳ йўқ, магар Менгина бордирман, бас, Менгагина ибодат қилинглар», деб ваҳий юборгандирмиз'}, {'chapter': 21, 'verse': 26, 'text': 'وَقَالُواْ ٱتَّخَذَ ٱلرَّحۡمَٰنُ وَلَدٗاۗ سُبۡحَٰنَهُۥۚ بَلۡ عِبَادٞ مُّكۡرَمُونَ\nУлар (яъни, мушриклар): «Раҳмоннинг (фаришталардан) боласи бор», дедилар. У зот (мушрикларнинг бадгумонларидан) мутлақо покдир. Йўқ, (фаришталар асло Аллоҳнинг болалари эмас, балки) улуғ бандалардир'}, {'chapter': 21, 'verse': 27, 'text': 'لَا يَسۡبِقُونَهُۥ بِٱلۡقَوۡلِ وَهُم بِأَمۡرِهِۦ يَعۡمَلُونَ\n(Фаришталар) У зотдан илгари бирон сўз айтмайдилар (яъни, Аллоҳ буюрмаган бирон ишни қилмайдилар). Улар (Аллоҳнинг) амри фармони билангина амал қилурлар'}, {'chapter': 21, 'verse': 28, 'text': 'يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَلَا يَشۡفَعُونَ إِلَّا لِمَنِ ٱرۡتَضَىٰ وَهُم مِّنۡ خَشۡيَتِهِۦ مُشۡفِقُونَ\nУ зот уларнинг олдиларидаги (қиладиган) ва орқаларидаги (қилиб ўтган) барча иш-амалларини билур. Улар (Қиёмат қойим бўлган кунда) фақат (Аллоҳ) рози бўлган кишиларнигина шафоат қилурлар — оқлай олурлар. Уларнинг (ўзлари Аллоҳдан) қўрқиб хавфу хатарда турурлар'}, {'chapter': 21, 'verse': 29, 'text': '۞وَمَن يَقُلۡ مِنۡهُمۡ إِنِّيٓ إِلَٰهٞ مِّن دُونِهِۦ فَذَٰلِكَ نَجۡزِيهِ جَهَنَّمَۚ كَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ\nУлардан бирон кимса: «Мен ҳам (Аллоҳдан) ўзга бур илоҳман», деса, бас, Биз ўшани жаҳаннам билан жазолармиз. Биз барча золим-мушрикларни ҳам ана шундай жазолаймиз'}, {'chapter': 21, 'verse': 30, 'text': 'أَوَلَمۡ يَرَ ٱلَّذِينَ كَفَرُوٓاْ أَنَّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ كَانَتَا رَتۡقٗا فَفَتَقۡنَٰهُمَاۖ وَجَعَلۡنَا مِنَ ٱلۡمَآءِ كُلَّ شَيۡءٍ حَيٍّۚ أَفَلَا يُؤۡمِنُونَ\nКофир бўлган кимсалар осмонлар ҳам, Ер ҳам (аввалда) тўсиқ (яъни, осмонлардан ёғин ёғмас, Ердан эса бирон гиёҳ унмас) бўлганини, бас, Биз уларни очиб юборганимизни (яъни, осмондан ёғин ёғдириб, Ердан гиёҳ ундирганимизни) ва барча жонли мавжудотни сувдан (пайдо) қилганимизни кўрмадиларми?! Энди ҳам иймон келтирмайдиларми'}, {'chapter': 21, 'verse': 31, 'text': 'وَجَعَلۡنَا فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِهِمۡ وَجَعَلۡنَا فِيهَا فِجَاجٗا سُبُلٗا لَّعَلَّهُمۡ يَهۡتَدُونَ\nБиз Ер (одамларни) тебратмасин учун унда тоғларни (пайдо) қилдик ва адашмасликлари учун унда кенг дара-йўллар қилдик'}, {'chapter': 21, 'verse': 32, 'text': 'وَجَعَلۡنَا ٱلسَّمَآءَ سَقۡفٗا مَّحۡفُوظٗاۖ وَهُمۡ عَنۡ ءَايَٰتِهَا مُعۡرِضُونَ\nБиз осмонни қулаб тушмайдиган том қилиб қўйдик. Улар (мушриклар) эса (осмондаги ой, қуёш, юлдуз каби Аллоҳнинг қудратини кўрсатиб турган) аломатлардан юз ўгирувчидирлар'}, {'chapter': 21, 'verse': 33, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ ٱلَّيۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ فِي فَلَكٖ يَسۡبَحُونَ\n(Аллоҳ) кеча ва кундузни, қуёш ва ойни яратган Зотдир. (Буларнинг) барчаси ўз фалак-фазосида сузурлар'}, {'chapter': 21, 'verse': 34, 'text': 'وَمَا جَعَلۡنَا لِبَشَرٖ مِّن قَبۡلِكَ ٱلۡخُلۡدَۖ أَفَإِيْن مِّتَّ فَهُمُ ٱلۡخَٰلِدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан аввал ҳам бирон одамзодга абадий ҳаёт берган эмасмиз. Бас, агар сиз ўлсангиз, улар абадий қолурларми?! (Йўқ, улар ҳам мангу қолмаслар)'}, {'chapter': 21, 'verse': 35, 'text': 'كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۗ وَنَبۡلُوكُم بِٱلشَّرِّ وَٱلۡخَيۡرِ فِتۡنَةٗۖ وَإِلَيۡنَا تُرۡجَعُونَ\nҲар жон ўлимни тотиб кўргувчидир. Биз сизларни (сабр-тоқатларингизни синаш учун) ёмонлик билан ҳам, (шукр қилишингизни билиш учун) яхшилик билан ҳам «алдаб» имтиҳон қилурмиз. (Кейин) фақат Бизгагина қайтарилурсизлар'}, {'chapter': 21, 'verse': 36, 'text': 'وَإِذَا رَءَاكَ ٱلَّذِينَ كَفَرُوٓاْ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي يَذۡكُرُ ءَالِهَتَكُمۡ وَهُم بِذِكۡرِ ٱلرَّحۡمَٰنِ هُمۡ كَٰفِرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қачон кофир бўлган кимсалар сизни кўрсалар, фақат (бир-бирларига сизни кўрсатишиб): «Сизларнинг илоҳларингизни айблайдиган кимса мана шуми?», (дейишиб) масхара қиладилар, холос. Ҳолбуки, уларнинг ўзлари Парвардигорнинг эслатмаси Қуръондан юз ўгирувчилардир'}, {'chapter': 21, 'verse': 37, 'text': 'خُلِقَ ٱلۡإِنسَٰنُ مِنۡ عَجَلٖۚ سَأُوْرِيكُمۡ ءَايَٰتِي فَلَا تَسۡتَعۡجِلُونِ\n(Ҳақиқатан) инсон шошқалоқ ҳолда яралгандир. Яқинда Мен сизларга Ўз оят-аломатларимни кўргазурман (яъни, Менга осий бўлган кимсаларни қандай азоблашимни кўрурсизлар). Бас, Мени шоштирмангиз'}, {'chapter': 21, 'verse': 38, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар (яъни, динсиз кимсалар): «Агар ростгўй бўлсангиз (айтинглар-чи) мана шу ваъда қачон бўлади?», дерлар'}, {'chapter': 21, 'verse': 39, 'text': 'لَوۡ يَعۡلَمُ ٱلَّذِينَ كَفَرُواْ حِينَ لَا يَكُفُّونَ عَن وُجُوهِهِمُ ٱلنَّارَ وَلَا عَن ظُهُورِهِمۡ وَلَا هُمۡ يُنصَرُونَ\nАгар кофир бўлган кимсалар на олдиларидан ва на ортларидан (дўзах) ўтини тўса олмай қоладиган, уларга ёрдам берилмайдиган вақтни (Қиёмат Кунидаги даҳшатларни) билсалар эди, (бу сўзларни айтмаган бўлур эдилар)'}, {'chapter': 21, 'verse': 40, 'text': 'بَلۡ تَأۡتِيهِم بَغۡتَةٗ فَتَبۡهَتُهُمۡ فَلَا يَسۡتَطِيعُونَ رَدَّهَا وَلَا هُمۡ يُنظَرُونَ\nЙўқ, (Қиёмат) тўсатдан келиб, уларни ҳайрону хаста қилиб қўюр, бас, уни қайтаришга ҳам кучлари етмас, уларга (тавба-тазарруъ учун) муҳлат ҳам берилмас'}, {'chapter': 21, 'verse': 41, 'text': 'وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), аниқки, сиздан аввалги пайғамбарлар ҳам масхара қилинган. Бас, уларнинг устидан кулган кимсаларга ўзлари (ишонмай) масхара қилган нарсалари (Аллоҳнинг азоби) тушгандир'}, {'chapter': 21, 'verse': 42, 'text': 'قُلۡ مَن يَكۡلَؤُكُم بِٱلَّيۡلِ وَٱلنَّهَارِ مِنَ ٱلرَّحۡمَٰنِۚ بَلۡ هُمۡ عَن ذِكۡرِ رَبِّهِم مُّعۡرِضُونَ\nАйтинг: «Кечаси-ю кундузи сизларни Раҳмон (азобидан) ким сақлай олур?!» Йўқ, улар Парвардигорни эслашдан юз ўгиргувчилардир'}, {'chapter': 21, 'verse': 43, 'text': 'أَمۡ لَهُمۡ ءَالِهَةٞ تَمۡنَعُهُم مِّن دُونِنَاۚ لَا يَسۡتَطِيعُونَ نَصۡرَ أَنفُسِهِمۡ وَلَا هُم مِّنَّا يُصۡحَبُونَ\nЁки уларнинг Биздан ўзга ҳимоя қиладиган «худолари» бормикан?! У («худолар») ўзларига ёрдам қилишга ҳам қодир эмаслар ва улар Бизнинг томонимиздан ҳимоя ҳам қилинмаслар'}, {'chapter': 21, 'verse': 44, 'text': 'بَلۡ مَتَّعۡنَا هَـٰٓؤُلَآءِ وَءَابَآءَهُمۡ حَتَّىٰ طَالَ عَلَيۡهِمُ ٱلۡعُمُرُۗ أَفَلَا يَرَوۡنَ أَنَّا نَأۡتِي ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَآۚ أَفَهُمُ ٱلۡغَٰلِبُونَ\nЙўқ, Биз уларни (кофирларни) ва ота-боболарини (дунё матоларидан шундай) фойдалантирдикки, ҳатто уларга умр-ҳаёт узун-абадий (кўриниб, бир кун келиб бу ҳаёт тугашига ишонмай қўйдилар ва Аллоҳнинг азобини ҳам инкор қилдилар). Ахир улар Биз Ерни атрофидан камайтириб келаётганимизни (тобора кўпроқ кишилар Ислом кириб, куфр дунёси қисқариб бораётганини) кўрмайдиларми?! Ўшалар ғолиб бўлурларми?! (Йўқ, улар мағлубдирлар, Аллоҳнинг динига иймон келтирган зотлар ғолибдирлар)'}, {'chapter': 21, 'verse': 45, 'text': 'قُلۡ إِنَّمَآ أُنذِرُكُم بِٱلۡوَحۡيِۚ وَلَا يَسۡمَعُ ٱلصُّمُّ ٱلدُّعَآءَ إِذَا مَا يُنذَرُونَ\nАйтинг: Мен сизларни фақат ваҳий билан қўрқитиб-огоҳлантирурман. (Лекин) гунглар (яъни, динсизлар ўзлари учун Охиратда азоб ҳақидаги ваҳий билан) қўрқитилаётган вақтларида даъватни эшитмайдилар'}, {'chapter': 21, 'verse': 46, 'text': 'وَلَئِن مَّسَّتۡهُمۡ نَفۡحَةٞ مِّنۡ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَٰوَيۡلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ\nҚасамки, агар уларга Парвардигорингизнинг азобидан андак етса, албатта улар: «Ҳолимизга вой! Дарҳақиқат, бизлар (ўз жонимизга) жабр қилгувчилар бўлдик», деб қолурлар'}, {'chapter': 21, 'verse': 47, 'text': 'وَنَضَعُ ٱلۡمَوَٰزِينَ ٱلۡقِسۡطَ لِيَوۡمِ ٱلۡقِيَٰمَةِ فَلَا تُظۡلَمُ نَفۡسٞ شَيۡـٔٗاۖ وَإِن كَانَ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٍ أَتَيۡنَا بِهَاۗ وَكَفَىٰ بِنَا حَٰسِبِينَ\nБиз Қиёмат Куни учун адолатли мезон-тарозилар қўюрмиз, бас, бирон жонга заррача зулм қилинмас. Агар хардал (ўсимлигининг) уруғидек (яхши ёки ёмон амал қилинган) бўлса, ўшани-да келтирурмиз! Биз Ўзимиз етарли ҳисоб-китоб қилгувчидирмиз'}, {'chapter': 21, 'verse': 48, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ وَهَٰرُونَ ٱلۡفُرۡقَانَ وَضِيَآءٗ وَذِكۡرٗا لِّلۡمُتَّقِينَ\nДарҳақиқат, Биз Мусо ва Ҳорунга (ҳақ билан ботилни) ажратгувчи, зиё ва тақводор кишилар учун эслатма бўлган (Тавротни) ато этдик'}, {'chapter': 21, 'verse': 49, 'text': 'ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ وَهُم مِّنَ ٱلسَّاعَةِ مُشۡفِقُونَ\nУлар Парвардигорларидан кўрмай туриб, қўрқурлар. Улар (Қиёмат қойим бўладиган) соатдан хавфда тургувчилардир'}, {'chapter': 21, 'verse': 50, 'text': 'وَهَٰذَا ذِكۡرٞ مُّبَارَكٌ أَنزَلۡنَٰهُۚ أَفَأَنتُمۡ لَهُۥ مُنكِرُونَ\nБу (Қуръон) Биз нозил қилган муборак эслатмадир. Ҳали сизлар уни инкор қилгувчимисизлар'}, {'chapter': 21, 'verse': 51, 'text': '۞وَلَقَدۡ ءَاتَيۡنَآ إِبۡرَٰهِيمَ رُشۡدَهُۥ مِن قَبۡلُ وَكُنَّا بِهِۦ عَٰلِمِينَ\nДарҳақиқат, Биз (Мусодан) илгари Иброҳимга Ҳақ йўлини ато этдик. Биз унинг (Ҳақ йўлга лойиқ эканини) билган эдик'}, {'chapter': 21, 'verse': 52, 'text': 'إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَا هَٰذِهِ ٱلتَّمَاثِيلُ ٱلَّتِيٓ أَنتُمۡ لَهَا عَٰكِفُونَ\nЎшанда (Иброҳим) отаси ва қавмига: «Сизлар доимо чўқинадиган бу ҳайкаллар нимадир (яъни, нега жонсиз ҳайкалларга сиғиняпсизлар?!)» деганида'}, {'chapter': 21, 'verse': 53, 'text': 'قَالُواْ وَجَدۡنَآ ءَابَآءَنَا لَهَا عَٰبِدِينَ\nУлар айтдилар: «Бизлар ота-боболаримизни ҳам уларга сиғинган ҳолда топганмиз»'}, {'chapter': 21, 'verse': 54, 'text': 'قَالَ لَقَدۡ كُنتُمۡ أَنتُمۡ وَءَابَآؤُكُمۡ فِي ضَلَٰلٖ مُّبِينٖ\nУ деди: «Дарҳақиқат, сизлар ҳам, ота-боболарингиз ҳам очиқ залолатда экансизлар»'}, {'chapter': 21, 'verse': 55, 'text': 'قَالُوٓاْ أَجِئۡتَنَا بِٱلۡحَقِّ أَمۡ أَنتَ مِنَ ٱللَّـٰعِبِينَ\nУлар айтдилар: «Келтирган — айтган (бу сўзларинг) ростми ёки сен ҳазил қилгувчиларданмисан?»'}, {'chapter': 21, 'verse': 56, 'text': 'قَالَ بَل رَّبُّكُمۡ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلَّذِي فَطَرَهُنَّ وَأَنَا۠ عَلَىٰ ذَٰلِكُم مِّنَ ٱلشَّـٰهِدِينَ\nУ деди: «Йўқ! Сизларнинг Парвардигорингиз (мана бу ҳеч кимга фойда ҳам, зиён ҳам етказа олмайдиган бутлар эмас, балки) осмонлар ва Ернинг Парвардигоридирки, У зот уларни Ўзи яратгандир. Мен бунга гувоҳлик бергувчилардандирман'}, {'chapter': 21, 'verse': 57, 'text': 'وَتَٱللَّهِ لَأَكِيدَنَّ أَصۡنَٰمَكُم بَعۡدَ أَن تُوَلُّواْ مُدۡبِرِينَ\nАллоҳ номига қасамки, сизлар (ҳайитгоҳларингизга) кетганларингиздан кейин бутларингизни бир бало қилурман»'}, {'chapter': 21, 'verse': 58, 'text': 'فَجَعَلَهُمۡ جُذَٰذًا إِلَّا كَبِيرٗا لَّهُمۡ لَعَلَّهُمۡ إِلَيۡهِ يَرۡجِعُونَ\nБас, у (бутларни) парча-парча қилди. Фақат, «шояд (мушриклар у бутларнинг) каттасига қайтсалар (ва бу айбни унга тўнкасалар», деб) ўшанигина (қолдирди ва унинг бўйнига бир болтани илиб қўйди)'}, {'chapter': 21, 'verse': 59, 'text': 'قَالُواْ مَن فَعَلَ هَٰذَا بِـَٔالِهَتِنَآ إِنَّهُۥ لَمِنَ ٱلظَّـٰلِمِينَ\nУлар (қайтиб келганларидан сўнг): «Бизнинг худоларимизни ким бундай қилди? Шубҳасиз, у золим кимсалардандир», дедилар'}, {'chapter': 21, 'verse': 60, 'text': 'قَالُواْ سَمِعۡنَا فَتٗى يَذۡكُرُهُمۡ يُقَالُ لَهُۥٓ إِبۡرَٰهِيمُ\nУлар(нинг айримлари) айтишди: «(Худоларимизни) айблаб юрадиган Иброҳим деган бир йигитни эшитгандик»'}, {'chapter': 21, 'verse': 61, 'text': 'قَالُواْ فَأۡتُواْ بِهِۦ عَلَىٰٓ أَعۡيُنِ ٱلنَّاسِ لَعَلَّهُمۡ يَشۡهَدُونَ\n(Шунда уларнинг катталари): «Уни одамлар қошига келтирингиз! Улар гувоҳ бўлсинлар», дейишди'}, {'chapter': 21, 'verse': 62, 'text': 'قَالُوٓاْ ءَأَنتَ فَعَلۡتَ هَٰذَا بِـَٔالِهَتِنَا يَـٰٓإِبۡرَٰهِيمُ\n(Уни келтиришгач): «Худоларимизни сен шу (ҳолга) солдингми, эй Иброҳим?» деб (сўрашди)'}, {'chapter': 21, 'verse': 63, 'text': 'قَالَ بَلۡ فَعَلَهُۥ كَبِيرُهُمۡ هَٰذَا فَسۡـَٔلُوهُمۡ إِن كَانُواْ يَنطِقُونَ\n(Иброҳим) айтди: «Йўқ, бу ишни уларнинг каттаси мана бу «ҳайкал» қилди. Бас, (бутларингиздан) сўранглар, агар гапира оладиган бўлсалар (айтиб берсинлар)»'}, {'chapter': 21, 'verse': 64, 'text': 'فَرَجَعُوٓاْ إِلَىٰٓ أَنفُسِهِمۡ فَقَالُوٓاْ إِنَّكُمۡ أَنتُمُ ٱلظَّـٰلِمُونَ\nБас, (мушриклар) ўзларига келиб, (бир-бирларига): «Сизлар ўзингиз золимларсиз», дедилар'}, {'chapter': 21, 'verse': 65, 'text': 'ثُمَّ نُكِسُواْ عَلَىٰ رُءُوسِهِمۡ لَقَدۡ عَلِمۡتَ مَا هَـٰٓؤُلَآءِ يَنطِقُونَ\nСўнгра эса яна бошлари айланиб: «Сен уларнинг гапирмасликларини яхши билардинг-ку?!», дейишди'}, {'chapter': 21, 'verse': 66, 'text': 'قَالَ أَفَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكُمۡ شَيۡـٔٗا وَلَا يَضُرُّكُمۡ\n(Иброҳим) айтди: «Ахир Аллоҳни қўйиб, сизларга бирон фойда ҳам, зиён ҳам етказа олмайдиган нарсаларга сиғинасизларми'}, {'chapter': 21, 'verse': 67, 'text': 'أُفّٖ لَّكُمۡ وَلِمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِۚ أَفَلَا تَعۡقِلُونَ\nСизларга ҳам, Аллоҳни қўйиб сиғинаётган бутларингизга ҳам суф-е! Ахир ақл юргизмайсизларми?!»'}, {'chapter': 21, 'verse': 68, 'text': 'قَالُواْ حَرِّقُوهُ وَٱنصُرُوٓاْ ءَالِهَتَكُمۡ إِن كُنتُمۡ فَٰعِلِينَ\n(Иброҳимнинг ҳақ таъна ва дашномларига бирон жавоб топа олмай қолган Намруд бошчилигидаги мушриклар) дедилар: «Уни ёқиб юборинглар! Агар уддалай олсанглар (мана шу иш билан) ўз худоларингизга ёрдам қилинглар»'}, {'chapter': 21, 'verse': 69, 'text': 'قُلۡنَا يَٰنَارُ كُونِي بَرۡدٗا وَسَلَٰمًا عَلَىٰٓ إِبۡرَٰهِيمَ\nБиз айтдик: «Эй олов, сен Иброҳим учун салқин ва омонлик бўл!»'}, {'chapter': 21, 'verse': 70, 'text': 'وَأَرَادُواْ بِهِۦ كَيۡدٗا فَجَعَلۡنَٰهُمُ ٱلۡأَخۡسَرِينَ\nУлар (Иброҳимга) макр қилмоқчи бўлдилар (яъни, уни ёндириб юбормоқчи бўлдилар). Биз эса уларнинг ўзларини кўпроқ зиён кўргувчи қилиб қўйдик'}, {'chapter': 21, 'verse': 71, 'text': 'وَنَجَّيۡنَٰهُ وَلُوطًا إِلَى ٱلۡأَرۡضِ ٱلَّتِي بَٰرَكۡنَا فِيهَا لِلۡعَٰلَمِينَ\nВа унга ҳамда Лутга нажот бериб, (уларни) Биз барча оламлар учун муборак қилган (Шом) заминига (юбордик)'}, {'chapter': 21, 'verse': 72, 'text': 'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ نَافِلَةٗۖ وَكُلّٗا جَعَلۡنَا صَٰلِحِينَ\nБиз (Иброҳимга Биздан бир фарзанд сўраганида) Исҳоқни ҳадя этдик ва Яъқубни ҳам зиёда қилдик ҳамда (уларнинг) барчаларини солиҳ кишилар қилдик'}, {'chapter': 21, 'verse': 73, 'text': 'وَجَعَلۡنَٰهُمۡ أَئِمَّةٗ يَهۡدُونَ بِأَمۡرِنَا وَأَوۡحَيۡنَآ إِلَيۡهِمۡ فِعۡلَ ٱلۡخَيۡرَٰتِ وَإِقَامَ ٱلصَّلَوٰةِ وَإِيتَآءَ ٱلزَّكَوٰةِۖ وَكَانُواْ لَنَا عَٰبِدِينَ\nЯна уларни Бизнинг амримиз билан (кишиларни Ҳақ йўлга) ҳидоят этадиган пешволар қилдик ва уларга яхши амаллар қилишни, намозни тўкис адо этишни ва закотни (мустаҳиқ кишиларга) ато этишни ваҳий қилдик. Улар ёлғиз Бизгагина ибодат қилгувчи бўлдилар'}, {'chapter': 21, 'verse': 74, 'text': 'وَلُوطًا ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗا وَنَجَّيۡنَٰهُ مِنَ ٱلۡقَرۡيَةِ ٱلَّتِي كَانَت تَّعۡمَلُ ٱلۡخَبَـٰٓئِثَۚ إِنَّهُمۡ كَانُواْ قَوۡمَ سَوۡءٖ فَٰسِقِينَ\nЛутга эса ҳикмат ва илм ато этдик ва уни нопокликлар қилувчи бўлган қишлоқ (аҳли)дан қутқардик. Дарҳақиқат, улар ёмон бузуқ қавм эдилар'}, {'chapter': 21, 'verse': 75, 'text': 'وَأَدۡخَلۡنَٰهُ فِي رَحۡمَتِنَآۖ إِنَّهُۥ مِنَ ٱلصَّـٰلِحِينَ\nВа уни Ўз раҳмат-жаннатимизга дохил қилдик. Шак-шубҳасиз, у солиҳлардандир'}, {'chapter': 21, 'verse': 76, 'text': 'وَنُوحًا إِذۡ نَادَىٰ مِن قَبۡلُ فَٱسۡتَجَبۡنَا لَهُۥ فَنَجَّيۡنَٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ\nНуҳни (эсланг): Ўшанда — (мазкур пайғамбарларнинг давридан) илгари нидо қилганида Биз унинг (нидосини) мустажоб қилиб, ўзини ва аҳли-тобеъларини улуғ ғамдан қутқардик'}, {'chapter': 21, 'verse': 77, 'text': 'وَنَصَرۡنَٰهُ مِنَ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمَ سَوۡءٖ فَأَغۡرَقۡنَٰهُمۡ أَجۡمَعِينَ\nВа унга Бизнинг оятларимизни ёлғон деган қавмдан мадад-нажот бердик. Дарҳақиқат, улар ёмон қавм эдилар. Бас, Биз уларнинг барчасини ғарқ қилиб юбордик'}, {'chapter': 21, 'verse': 78, 'text': 'وَدَاوُۥدَ وَسُلَيۡمَٰنَ إِذۡ يَحۡكُمَانِ فِي ٱلۡحَرۡثِ إِذۡ نَفَشَتۡ فِيهِ غَنَمُ ٱلۡقَوۡمِ وَكُنَّا لِحُكۡمِهِمۡ شَٰهِدِينَ\nДовуд ва Сулаймоннинг экинзор хусусида ҳукм қилаётган пайтларини (эсланг). Ўшанда унга қавмнинг қўйлари бўшалиб кириб (уни пайҳон қилиб юборган) эди. Биз улар (чиқарган) ҳукмга шоҳид эдик'}, {'chapter': 21, 'verse': 79, 'text': 'فَفَهَّمۡنَٰهَا سُلَيۡمَٰنَۚ وَكُلًّا ءَاتَيۡنَا حُكۡمٗا وَعِلۡمٗاۚ وَسَخَّرۡنَا مَعَ دَاوُۥدَ ٱلۡجِبَالَ يُسَبِّحۡنَ وَٱلطَّيۡرَۚ وَكُنَّا فَٰعِلِينَ\nБас, Биз уни Сулаймонга англатдик. Биз ҳар иккисига ҳикмат-пайғамбарлик ва илм ато этдик. Тоғлар ва қушларни Довуд билан бирга тасбеҳ айтадиган қилиб бўйсундириб қўйдик. Биз шундай қила олгувчидирмиз'}, {'chapter': 21, 'verse': 80, 'text': 'وَعَلَّمۡنَٰهُ صَنۡعَةَ لَبُوسٖ لَّكُمۡ لِتُحۡصِنَكُم مِّنۢ بَأۡسِكُمۡۖ فَهَلۡ أَنتُمۡ شَٰكِرُونَ\nЯна (Довудга) сизларга зиён етишидан сақлайдиган совут илмини таълим бердик. Бас, сизлар шукр қилурмисиз'}, {'chapter': 21, 'verse': 81, 'text': 'وَلِسُلَيۡمَٰنَ ٱلرِّيحَ عَاصِفَةٗ تَجۡرِي بِأَمۡرِهِۦٓ إِلَى ٱلۡأَرۡضِ ٱلَّتِي بَٰرَكۡنَا فِيهَاۚ وَكُنَّا بِكُلِّ شَيۡءٍ عَٰلِمِينَ\nСулаймонга эса бўронли шамолни (бўйсундириб), унинг амри билан Биз муборак қилган заминга (Шомга) эсадиган қилиб қўйдик. Биз барча нарсани билгувчидирмиз'}, {'chapter': 21, 'verse': 82, 'text': 'وَمِنَ ٱلشَّيَٰطِينِ مَن يَغُوصُونَ لَهُۥ وَيَعۡمَلُونَ عَمَلٗا دُونَ ذَٰلِكَۖ وَكُنَّا لَهُمۡ حَٰفِظِينَ\nЯна шайтон-жинлардан (Сулаймон учун) ғаввослик қиладиган ва бундан бошқа ишларни ҳам адо этадиган кимсаларни (яратдик). Ва биз уларни (Сулаймоннинг амридан чиқиб кетмасликлари учун) қўриқлаб тургувчи бўлдик'}, {'chapter': 21, 'verse': 83, 'text': '۞وَأَيُّوبَ إِذۡ نَادَىٰ رَبَّهُۥٓ أَنِّي مَسَّنِيَ ٱلضُّرُّ وَأَنتَ أَرۡحَمُ ٱلرَّـٰحِمِينَ\nАйюб — ўз Парвардигорига нидо қилиб: «(Парвардигорим), мени бало ушлади. Ўзинг раҳм-шафқат қилгувчиларнинг раҳмлироғидирсан», деб илтижо қилган пайтини (эсланг)'}, {'chapter': 21, 'verse': 84, 'text': 'فَٱسۡتَجَبۡنَا لَهُۥ فَكَشَفۡنَا مَا بِهِۦ مِن ضُرّٖۖ وَءَاتَيۡنَٰهُ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةٗ مِّنۡ عِندِنَا وَذِكۡرَىٰ لِلۡعَٰبِدِينَ\nБас, Биз унинг (дуосини) мустажоб қилиб, ундаги зиён-заҳматни кетказдик ҳамда Ўз ҳузуримиздан меҳрибонлик кўрсатиб, барча ибодат қилгувчиларга эслатма-ибрат бўлсин, деб (Айюбга) аҳли-оиласини ва улар билан қўшиб яна ўшаларнинг мислича бола-чақа ато этдик'}, {'chapter': 21, 'verse': 85, 'text': 'وَإِسۡمَٰعِيلَ وَإِدۡرِيسَ وَذَا ٱلۡكِفۡلِۖ كُلّٞ مِّنَ ٱلصَّـٰبِرِينَ\nИсмоилни, Идрисни ва Зул-кифлни (эсланг). Барчалари сабр қилгувчи зотлардандирлар'}, {'chapter': 21, 'verse': 86, 'text': 'وَأَدۡخَلۡنَٰهُمۡ فِي رَحۡمَتِنَآۖ إِنَّهُم مِّنَ ٱلصَّـٰلِحِينَ\nБиз уларни Ўз раҳмат-жаннатимизга дохил қилдик. Дарҳақиқат, улар солиҳ зотлардандирлар'}, {'chapter': 21, 'verse': 87, 'text': 'وَذَا ٱلنُّونِ إِذ ذَّهَبَ مُغَٰضِبٗا فَظَنَّ أَن لَّن نَّقۡدِرَ عَلَيۡهِ فَنَادَىٰ فِي ٱلظُّلُمَٰتِ أَن لَّآ إِلَٰهَ إِلَّآ أَنتَ سُبۡحَٰنَكَ إِنِّي كُنتُ مِنَ ٱلظَّـٰلِمِينَ\nЗуннун — Юнуснинг (қавмидан) ғазабланган ҳолда (ўз қишлоғидан чиқиб) кетиб, Бизни унинг зиёнига ҳукм қилмайди, деб ўйлаган пайтини, сўнг (Биз уни балиқ қорнига ташлаганимиздан кейин) қоронғу зулматларда туриб: «Ҳеч илоҳ йўқ, магар Ўзинг бордирсан, эй пок Парвардигор, дарҳақиқат, мен (ўз жонимга) жабр қилгувчилардан бўлиб қолдим», деб нидо қилган (пайтини эсланг)'}, {'chapter': 21, 'verse': 88, 'text': 'فَٱسۡتَجَبۡنَا لَهُۥ وَنَجَّيۡنَٰهُ مِنَ ٱلۡغَمِّۚ وَكَذَٰلِكَ نُـۨجِي ٱلۡمُؤۡمِنِينَ\nБас, Биз унинг (дуосини) мустажоб қилдик ва уни ғам-ғуссадан қутқардик. Биз мўминларга мана шундай нажот берурмиз'}, {'chapter': 21, 'verse': 89, 'text': 'وَزَكَرِيَّآ إِذۡ نَادَىٰ رَبَّهُۥ رَبِّ لَا تَذَرۡنِي فَرۡدٗا وَأَنتَ خَيۡرُ ٱلۡوَٰرِثِينَ\nЗакариёнинг: «Парвардигорим, мени ёлғиз ҳолда ташлаб қўйма (яъни, менга Ўз даргоҳингдан бир меросхўр фарзанд ато эт), Ўзинг ворисларнинг яхшироғидирсан (яъни, ҳамманинг ортида қолгувчидирсан)», деб нидо қилган пайтини (эсланг)'}, {'chapter': 21, 'verse': 90, 'text': 'فَٱسۡتَجَبۡنَا لَهُۥ وَوَهَبۡنَا لَهُۥ يَحۡيَىٰ وَأَصۡلَحۡنَا لَهُۥ زَوۡجَهُۥٓۚ إِنَّهُمۡ كَانُواْ يُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِ وَيَدۡعُونَنَا رَغَبٗا وَرَهَبٗاۖ وَكَانُواْ لَنَا خَٰشِعِينَ\nБас, Биз унинг (дуосини) мустажоб қилдик ва унга Яҳёни ҳадя этдик ҳамда жуфтини ўнглаб (туғмайдиган кампирни бола кўришга қодир қилиб) қўйдик. Дарҳақиқат, улар (яъни, мазкур пайғамбарлар) яхши ишларни қилишга шошар ва Бизга рағбат ва қурқув билан дуо-илтижо қилар эдилар. Улар Бизга таъзим-итоат қилгувчи эдилар'}, {'chapter': 21, 'verse': 91, 'text': 'وَٱلَّتِيٓ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيهَا مِن رُّوحِنَا وَجَعَلۡنَٰهَا وَٱبۡنَهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ\nЯна ўз номусини сақлаган аёлни (яъни, Марямни эсланг). Бас, Биз Ўз тарафимиздан бўлган руҳни унга пуфладик (ва у Исога ҳомиладор бўлди) ва уни ҳамда ўғлини барча оламлар учун оят-ибрат қилдик'}, {'chapter': 21, 'verse': 92, 'text': 'إِنَّ هَٰذِهِۦٓ أُمَّتُكُمۡ أُمَّةٗ وَٰحِدَةٗ وَأَنَا۠ رَبُّكُمۡ فَٱعۡبُدُونِ\n(Эй инсонлар), сизларнинг миллатингиз-динингиз ҳақиқатда бир диндир (яъни, Исломдир). Мен эса (барчаларингизнинг) Парвардигорингиздирман. Бас, Менгагина ибодат қилинглар'}, {'chapter': 21, 'verse': 93, 'text': 'وَتَقَطَّعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡۖ كُلٌّ إِلَيۡنَا رَٰجِعُونَ\n(Одамлар) эса ишларини (яъни, динларини) ўзаро парчалаб-бўлиб олдилар. Барчалари Бизга қайтгувчидирлар'}, {'chapter': 21, 'verse': 94, 'text': 'فَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ وَهُوَ مُؤۡمِنٞ فَلَا كُفۡرَانَ لِسَعۡيِهِۦ وَإِنَّا لَهُۥ كَٰتِبُونَ\nБас, ким мўмин бўлган ҳолида яхши амаллардан қилса, унинг саъй-ҳаракати зое бўлмас — Биз уни ёзиб қўйгувчидирмиз'}, {'chapter': 21, 'verse': 95, 'text': 'وَحَرَٰمٌ عَلَىٰ قَرۡيَةٍ أَهۡلَكۡنَٰهَآ أَنَّهُمۡ لَا يَرۡجِعُونَ\nБиз ҳалок қилган бирон қишлоқ-шаҳар (аҳли қайта дунёга келиши) ҳаромдир. Улар қайтмайдилар'}, {'chapter': 21, 'verse': 96, 'text': 'حَتَّىٰٓ إِذَا فُتِحَتۡ يَأۡجُوجُ وَمَأۡجُوجُ وَهُم مِّن كُلِّ حَدَبٖ يَنسِلُونَ\nТо Яъжуж ва Маъжуж (тўғони) очилиб, улар ҳар бир тепаликдан оқиб келадиган вақтгача'}, {'chapter': 21, 'verse': 97, 'text': 'وَٱقۡتَرَبَ ٱلۡوَعۡدُ ٱلۡحَقُّ فَإِذَا هِيَ شَٰخِصَةٌ أَبۡصَٰرُ ٱلَّذِينَ كَفَرُواْ يَٰوَيۡلَنَا قَدۡ كُنَّا فِي غَفۡلَةٖ مِّنۡ هَٰذَا بَلۡ كُنَّا ظَٰلِمِينَ\nВа Ҳақ ваъда (яъни, Қиёмат) яқин бўладиган вақтгача (улар яъни, Биз ҳалок қилган кимсалар қайтмайдилар). Ана у вақтда эса кофир бўлган кимсаларнинг кўзлари қотиб: «Эй, ҳолимизга вой, бизлар бундай (оқибатдан) ғафлатда эдик. Йўқ, бизлар (ўз жонларимизга) жабр қилгувчи бўлдик», (дейдилар)'}, {'chapter': 21, 'verse': 98, 'text': 'إِنَّكُمۡ وَمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ حَصَبُ جَهَنَّمَ أَنتُمۡ لَهَا وَٰرِدُونَ\n(Эй мушриклар), сизлар ҳам, Аллоҳни қўйиб сиғинаётган бутларингиз ҳам жаҳаннам ўтинларидир. Сизлар у (жаҳаннамга) тушгувчидирсизлар'}, {'chapter': 21, 'verse': 99, 'text': 'لَوۡ كَانَ هَـٰٓؤُلَآءِ ءَالِهَةٗ مَّا وَرَدُوهَاۖ وَكُلّٞ فِيهَا خَٰلِدُونَ\nАгар (сизлар сиғинадиган бутларингиз) худолар бўлганида (жаҳаннамга) тушмаган бўлур эдилар. (Бу бутлар ва уларга сиғинадиганларнинг) барчалари у жойда абадий қолгувчидирлар'}, {'chapter': 21, 'verse': 100, 'text': 'لَهُمۡ فِيهَا زَفِيرٞ وَهُمۡ فِيهَا لَا يَسۡمَعُونَ\nУлар учун у жойда (фақат) дод-фарёд қилишгина бордир. Улар у жойда (бирон нарсани) эшитмаслар'}, {'chapter': 21, 'verse': 101, 'text': 'إِنَّ ٱلَّذِينَ سَبَقَتۡ لَهُم مِّنَّا ٱلۡحُسۡنَىٰٓ أُوْلَـٰٓئِكَ عَنۡهَا مُبۡعَدُونَ\nАлбатта, Бизнинг томонимиздан гўзал (манзилат-мартаба) берилган зотлар — ана ўшалар (жаҳаннамдан) узоқ қилинурлар'}, {'chapter': 21, 'verse': 102, 'text': 'لَا يَسۡمَعُونَ حَسِيسَهَاۖ وَهُمۡ فِي مَا ٱشۡتَهَتۡ أَنفُسُهُمۡ خَٰلِدُونَ\nУлар унинг шарпасини ҳам эшитмаслар. Улар ўзлари истаган неъматларда абадий қолгувчидирлар'}, {'chapter': 21, 'verse': 103, 'text': 'لَا يَحۡزُنُهُمُ ٱلۡفَزَعُ ٱلۡأَكۡبَرُ وَتَتَلَقَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ هَٰذَا يَوۡمُكُمُ ٱلَّذِي كُنتُمۡ تُوعَدُونَ\nУларни Буюк даҳшат (яъни, Қиёмат) маҳзун қилмас. Фаришталар уларни: «Мана шу сизларга ваъда қилинган кундир», деб кутиб олурлар'}, {'chapter': 21, 'verse': 104, 'text': 'يَوۡمَ نَطۡوِي ٱلسَّمَآءَ كَطَيِّ ٱلسِّجِلِّ لِلۡكُتُبِۚ كَمَا بَدَأۡنَآ أَوَّلَ خَلۡقٖ نُّعِيدُهُۥۚ وَعۡدًا عَلَيۡنَآۚ إِنَّا كُنَّا فَٰعِلِينَ\nУ кунда Биз осмонни ҳам худди мактуб ёзилган саҳифани ўраган янглиғ ўраб, биринчи марта қандай яратган бўлсак, (ўша ҳолга) қайтарурмиз. (Бу) Бизнинг зиммамиздаги ваъдадир. Албатта Биз (шундай) қилгувчидирмиз'}, {'chapter': 21, 'verse': 105, 'text': 'وَلَقَدۡ كَتَبۡنَا فِي ٱلزَّبُورِ مِنۢ بَعۡدِ ٱلذِّكۡرِ أَنَّ ٱلۡأَرۡضَ يَرِثُهَا عِبَادِيَ ٱلصَّـٰلِحُونَ\nДарҳақиқат, Биз (Лавҳул-маҳфузда) зикр қилгандан сўнг Забурда: «Албатта (жаннатнинг) ерига менинг солиҳ бандаларим ворис бўлурлар», деб ёзиб қўйгандирмиз'}, {'chapter': 21, 'verse': 106, 'text': 'إِنَّ فِي هَٰذَا لَبَلَٰغٗا لِّقَوۡمٍ عَٰبِدِينَ\nАлбатта мана шу (Қуръонда) ибодат қилгувчи қавм учун етарли нарса бордир'}, {'chapter': 21, 'verse': 107, 'text': 'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا رَحۡمَةٗ لِّلۡعَٰلَمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, Биз сизни барча оламларга фақат раҳмат (яъни, Аллоҳнинг раҳмати-жаннатига етаклагувчи) қилиб юбордик'}, {'chapter': 21, 'verse': 108, 'text': 'قُلۡ إِنَّمَا يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ\nАйтинг: «Менга фақат (Сизнинг) илоҳингиз якка ягона илоҳ экани ваҳий қилинур. Бас, сизлар (ваҳийга) бўйинсунувчимисизлар?»'}, {'chapter': 21, 'verse': 109, 'text': 'فَإِن تَوَلَّوۡاْ فَقُلۡ ءَاذَنتُكُمۡ عَلَىٰ سَوَآءٖۖ وَإِنۡ أَدۡرِيٓ أَقَرِيبٌ أَم بَعِيدٞ مَّا تُوعَدُونَ\nЭнди агар юз ўгирсалар, у ҳолда айтинг: «Мен сизларнинг (барчангизга ҳеч кимни айирмай менга нозил бўлган ваҳийни) эшитдирдим. Сизларга ваъда қилинган (Қиёмат Кунидаги азобни) яқин ё йироқлигини эса билмасман»'}, {'chapter': 21, 'verse': 110, 'text': 'إِنَّهُۥ يَعۡلَمُ ٱلۡجَهۡرَ مِنَ ٱلۡقَوۡلِ وَيَعۡلَمُ مَا تَكۡتُمُونَ\nАлбатта, У зот сўзнинг ошкорасини ҳам билур, сизлар беркитадиган сўзларни ҳам билур'}, {'chapter': 21, 'verse': 111, 'text': 'وَإِنۡ أَدۡرِي لَعَلَّهُۥ فِتۡنَةٞ لَّكُمۡ وَمَتَٰعٌ إِلَىٰ حِينٖ\nБилмайман, эҳтимол бу (яъни, азоб соатининг таъхирга ташланиши) сизлар учун бир алдов-синов ва бир оз вақтгача (ажалларингиз етиб ўлгунингизгача) фойдаланишдир»'}, {'chapter': 21, 'verse': 112, 'text': 'قَٰلَ رَبِّ ٱحۡكُم بِٱلۡحَقِّۗ وَرَبُّنَا ٱلرَّحۡمَٰنُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ\n(Муҳаммад алайҳис-салоту вас-салом) айтди: «Парвардигорим, Ўзинг Ҳақ ҳукмингни қилгин. Парвардигоримиз — сизларнинг («Аллоҳ ёлғиз эмас», деб У зотни бўҳтон сифат билан) сифатлашларингиздан мадад сўраладиган Раҳмондир»'}, {'chapter': 22, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمۡۚ إِنَّ زَلۡزَلَةَ ٱلسَّاعَةِ شَيۡءٌ عَظِيمٞ\nЭй инсонлар, Парвардигорингиздан қўрқингиз! Зеро, (Қиёмат) соати (олдидаги) зилзила улуғ-даҳшатли нарсадир'}, {'chapter': 22, 'verse': 2, 'text': 'يَوۡمَ تَرَوۡنَهَا تَذۡهَلُ كُلُّ مُرۡضِعَةٍ عَمَّآ أَرۡضَعَتۡ وَتَضَعُ كُلُّ ذَاتِ حَمۡلٍ حَمۡلَهَا وَتَرَى ٱلنَّاسَ سُكَٰرَىٰ وَمَا هُم بِسُكَٰرَىٰ وَلَٰكِنَّ عَذَابَ ٱللَّهِ شَدِيدٞ\nУни кўрар кунингизда эмизаётган (оналар) эмизиб турган (боласини) унутар ва барча ҳомиладор (аёллар) ўз ҳомиласини ташлаб юборар, одамларни маст-аласт ҳолда кўрурсиз, ҳолбуки, улар ўзлари маст эмаслар, лекин Аллоҳнинг азоби қаттиқдир'}, {'chapter': 22, 'verse': 3, 'text': 'وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّبِعُ كُلَّ شَيۡطَٰنٖ مَّرِيدٖ\nОдамлар орасида ўзи билмаган ҳолида Аллоҳ хусусида мужодала қиладиган ва ҳар бир итоатсиз шайтонга эргашиб кетаверадиган кимсалар ҳам бордир'}, {'chapter': 22, 'verse': 4, 'text': 'كُتِبَ عَلَيۡهِ أَنَّهُۥ مَن تَوَلَّاهُ فَأَنَّهُۥ يُضِلُّهُۥ وَيَهۡدِيهِ إِلَىٰ عَذَابِ ٱلسَّعِيرِ\nУ (шайтонга эса) ўзини дўст тутган кимсани албатта йўлдан оздириш ва дўзах азобига йўллаш ёзиб қўйилгандир — ҳукм қилингандир'}, {'chapter': 22, 'verse': 5, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِي رَيۡبٖ مِّنَ ٱلۡبَعۡثِ فَإِنَّا خَلَقۡنَٰكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ مِنۡ عَلَقَةٖ ثُمَّ مِن مُّضۡغَةٖ مُّخَلَّقَةٖ وَغَيۡرِ مُخَلَّقَةٖ لِّنُبَيِّنَ لَكُمۡۚ وَنُقِرُّ فِي ٱلۡأَرۡحَامِ مَا نَشَآءُ إِلَىٰٓ أَجَلٖ مُّسَمّٗى ثُمَّ نُخۡرِجُكُمۡ طِفۡلٗا ثُمَّ لِتَبۡلُغُوٓاْ أَشُدَّكُمۡۖ وَمِنكُم مَّن يُتَوَفَّىٰ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرۡذَلِ ٱلۡعُمُرِ لِكَيۡلَا يَعۡلَمَ مِنۢ بَعۡدِ عِلۡمٖ شَيۡـٔٗاۚ وَتَرَى ٱلۡأَرۡضَ هَامِدَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡ وَأَنۢبَتَتۡ مِن كُلِّ زَوۡجِۭ بَهِيجٖ\nЭй инсонлар, агар сизлар қайта тирилишдан шубҳада бўлсангизлар, у ҳолда (қаранглар), Биз сизларга (Аллоҳнинг қудратини) баён қилиш учун сизлар (яъни, отангиз Одамни) тупроқдан, сўнгра (барча жонзотни аввало) нутфа-манийдан, сўнгра лахта қондан, сўнгра яралиб битган-битмаган (яъни, инсон суратида шаклланиб битган ёки битмаган) парча гўштдан яратдик. Биз (сизларни) Ўзимиз хоҳлаганимизча — муайян муддатгача (оналарингиз) қорнида қолдириб, сўнгра чақалоқ ҳолингизда (ёруғ оламга) чиқарурмиз, сўнгра вояга етгунларингизгача (ҳам Ўзимиз тарбия қилурмиз). Сизлардан (гўдаклик, ёшлик йилларидаёқ) вафот топадиган кишилар ҳам бўлур, яна сизлардан (кўп нарса-билимларни ўрганиб) билганидан сўнг ҳеч нарсани билмай қолиши учун энг тубан умрга (яъни, қариб мункиллаб қолишга) қайтариладиган кишилар ҳам бўлур. (Қайта тирилиш ҳақ эканлигининг яна бир далили ушбудир) — сиз(лар) бу Ерни қуруқ-ўлик ҳолида кўрурсиз. Энди қачонки Биз унинг устидан сув-ёмғир ёғдирсак, у ҳаракатга келиб кўпчир ва турли-туман гўзал (наботот) жуфтларини ундирур'}, {'chapter': 22, 'verse': 6, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّهُۥ يُحۡيِ ٱلۡمَوۡتَىٰ وَأَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nБунга сабаб Аллоҳ Ҳақ экани ва Унинг Ўзи ўликларга ҳаёт бериши ҳамда У ҳамма нарсага қодир эканлигидир'}, {'chapter': 22, 'verse': 7, 'text': 'وَأَنَّ ٱلسَّاعَةَ ءَاتِيَةٞ لَّا رَيۡبَ فِيهَا وَأَنَّ ٱللَّهَ يَبۡعَثُ مَن فِي ٱلۡقُبُورِ\nЯна аниқки, (Қиёмат) соати шак-шубҳасиз, келгувчидир ва албатта Аллоҳ қабрлардаги бор жонзотни тирилтирур'}, {'chapter': 22, 'verse': 8, 'text': 'وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ\nОдамлар орасида ўзи билмаган ва (Ҳақ йўлига) ҳидоят топмаган ҳолида, бирон нурли Китобсиз (яъни, аниқ ҳужжатсиз) Аллоҳ хусусида мужодала қиладиган кимсалар ҳам бордир'}, {'chapter': 22, 'verse': 9, 'text': 'ثَانِيَ عِطۡفِهِۦ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِۖ لَهُۥ فِي ٱلدُّنۡيَا خِزۡيٞۖ وَنُذِيقُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ عَذَابَ ٱلۡحَرِيقِ\n(Тўғри Йўлдан) бўйин буриб, (ўзгаларни ҳам) Аллоҳ йўлидан оздириш учун. У (кимсалар) учун бу дунёда расволик-шармандалик бўлур, Қиёмат Кунида эса унга ўт азобини тотдирурмиз'}, {'chapter': 22, 'verse': 10, 'text': 'ذَٰلِكَ بِمَا قَدَّمَتۡ يَدَاكَ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\n(Ва унга дермиз): «Бу (азоб) ўз қўлларинг (билан) қилган гуноҳинг сабаблидир. Зеро, Аллоҳ ҳаргиз бандаларига зулм қилгувчи эмасдир»'}, {'chapter': 22, 'verse': 11, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَعۡبُدُ ٱللَّهَ عَلَىٰ حَرۡفٖۖ فَإِنۡ أَصَابَهُۥ خَيۡرٌ ٱطۡمَأَنَّ بِهِۦۖ وَإِنۡ أَصَابَتۡهُ فِتۡنَةٌ ٱنقَلَبَ عَلَىٰ وَجۡهِهِۦ خَسِرَ ٱلدُّنۡيَا وَٱلۡأٓخِرَةَۚ ذَٰلِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِينُ\nОдамлар орасида Аллоҳга бир четда (яъни, сидқидилдан эмас, балки тил учида) ибодат қиладиган кимсалар ҳам бордир. Бас, агар унга (диндор бўлгани шарофатидан) яхшилик етса, ўша сабабли хотиржам бўлур, агар бирон фитна-бахтсизлик етса (диндан) юз ўгириб кетур. У дунё-ю Охиратда зиён кўрур. Бу эса очиқ-аниқ зиёндир'}, {'chapter': 22, 'verse': 12, 'text': 'يَدۡعُواْ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُۥ وَمَا لَا يَنفَعُهُۥۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلۡبَعِيدُ\nУ Аллоҳни қўйиб ўзига зиён ҳам, фойда ҳам бера олмайдиган бутларга илтижо қилур. Бу — йўлдан қаттиқ озишнинг ўзидир'}, {'chapter': 22, 'verse': 13, 'text': 'يَدۡعُواْ لَمَن ضَرُّهُۥٓ أَقۡرَبُ مِن نَّفۡعِهِۦۚ لَبِئۡسَ ٱلۡمَوۡلَىٰ وَلَبِئۡسَ ٱلۡعَشِيرُ\nУ шак-шубҳасиз, зиёни фойдасидан яқинроқ бўлган бутларга илтижо қилур. (Лекин у бутлар) нақадар ёмон «хожа»дир ва нақадар ёмон «ошно»дир'}, {'chapter': 22, 'verse': 14, 'text': 'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ إِنَّ ٱللَّهَ يَفۡعَلُ مَا يُرِيدُ\nАлбатта Аллоҳ иймон келтирган ва яхши амаллар қилган зотларни остидан дарёлар оқиб турадиган жаннатларга дохил қилур. Албатта Аллоҳ ёлғиз Ўзи хоҳлаган ишни қилур'}, {'chapter': 22, 'verse': 15, 'text': 'مَن كَانَ يَظُنُّ أَن لَّن يَنصُرَهُ ٱللَّهُ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ فَلۡيَمۡدُدۡ بِسَبَبٍ إِلَى ٱلسَّمَآءِ ثُمَّ لۡيَقۡطَعۡ فَلۡيَنظُرۡ هَلۡ يُذۡهِبَنَّ كَيۡدُهُۥ مَا يَغِيظُ\n(Албатта Аллоҳ Ўз пайғамбарига ёрдам берур, энди) кимда-ким: «Аллоҳ ҳаргиз унга ёрдам қилмас», деб гумон қилгувчи бўлса, у ҳолда бир арқон билан осмонга-шифтга осилиб, (ўзини ердан) узсин (яъни, ўзи учун дор ясаб, унга осилсин), сўнг қарасин — бу найранги ғазабини кетказармикан'}, {'chapter': 22, 'verse': 16, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَٰهُ ءَايَٰتِۭ بَيِّنَٰتٖ وَأَنَّ ٱللَّهَ يَهۡدِي مَن يُرِيدُ\nБиз уни (яъни, Қуръонни) мана шундай аниқ-равшан оятлар(дан иборат) бўлган ҳолида нозил қилдик. Албатта Аллоҳ Ўзи истаган кишиларни ҳидоят қилур'}, {'chapter': 22, 'verse': 17, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلصَّـٰبِـِٔينَ وَٱلنَّصَٰرَىٰ وَٱلۡمَجُوسَ وَٱلَّذِينَ أَشۡرَكُوٓاْ إِنَّ ٱللَّهَ يَفۡصِلُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nДарҳақиқат, иймон келтирган зотлар, яҳудий бўлган кимсалар, собийлар (фаришталарга сиғинувчилар), насронийлар, мажусийлар (оташпарастлар) ва мушрик бўлган кимсалар — аниқки Аллоҳ Қиёмат Кунида уларнинг ўртасини ажрим қилур (яъни, мўминларни жаннатга дохил қилур, кофирларни дўзахга гирифтор қилур). Албатта Аллоҳ барча нарсага гувоҳдир'}, {'chapter': 22, 'verse': 18, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يَسۡجُدُۤ لَهُۥۤ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ وَٱلشَّمۡسُ وَٱلۡقَمَرُ وَٱلنُّجُومُ وَٱلۡجِبَالُ وَٱلشَّجَرُ وَٱلدَّوَآبُّ وَكَثِيرٞ مِّنَ ٱلنَّاسِۖ وَكَثِيرٌ حَقَّ عَلَيۡهِ ٱلۡعَذَابُۗ وَمَن يُهِنِ ٱللَّهُ فَمَا لَهُۥ مِن مُّكۡرِمٍۚ إِنَّ ٱللَّهَ يَفۡعَلُ مَا يَشَآءُ۩\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз осмонлардаги ва Ердаги бор жонзот, қуёш, ой, юлдузлар, тоғлар, дов-дарахтлар ва (барча) жониворлар ҳамда кўпдан-кўп инсонлар (ёлғиз) Аллоҳга сажда қилишини кўрмадингизми? Кўп (одамларга эса кофир бўлганлари сабабли) азоб ҳақ бўлгандир. Кимни Аллоҳ хор қилиб қўйса, бас, уни (ҳеч ким) азиз қилгувчи бўлмас. Албатта Аллоҳ Ўзи хоҳлаган нарсани қилур'}, {'chapter': 22, 'verse': 19, 'text': '۞هَٰذَانِ خَصۡمَانِ ٱخۡتَصَمُواْ فِي رَبِّهِمۡۖ فَٱلَّذِينَ كَفَرُواْ قُطِّعَتۡ لَهُمۡ ثِيَابٞ مِّن نَّارٖ يُصَبُّ مِن فَوۡقِ رُءُوسِهِمُ ٱلۡحَمِيمُ\nМана шу икки ғаним (яъни, мўминлар билан кофирлар) Парвардигорлари(нинг ҳақ дини) хусусида талашдилар (яъни, мўминлар Аллоҳнинг ҳақ дини бўлмиш Исломнинг ғолиб бўлишини истадилар, кофирлар эса бу динни йўқ қилмоқчи бўлдилар). Бас, кофир бўлган кимсалар учун ўтдан бўлган кийимлар бичилди, (энди) уларнинг бошларидан қайноқ сув қуюлиб'}, {'chapter': 22, 'verse': 20, 'text': 'يُصۡهَرُ بِهِۦ مَا فِي بُطُونِهِمۡ وَٱلۡجُلُودُ\nУ (сув) билан уларнинг ичларидаги нарсалар ҳам, терилари ҳам эритиб юборилур'}, {'chapter': 22, 'verse': 21, 'text': 'وَلَهُم مَّقَٰمِعُ مِنۡ حَدِيدٖ\nУлар учун темир гурзилар бордир'}, {'chapter': 22, 'verse': 22, 'text': 'كُلَّمَآ أَرَادُوٓاْ أَن يَخۡرُجُواْ مِنۡهَا مِنۡ غَمٍّ أُعِيدُواْ فِيهَا وَذُوقُواْ عَذَابَ ٱلۡحَرِيقِ\nҲар қачон улар (дўзах) ғам-азобидан (қочиб) чиқмоқчи бўлсалар (гурзилар билан) яна унга қайтарилурлар ва (уларга): «Ўт азобини тотиб кўрингиз», (дейилур)'}, {'chapter': 22, 'verse': 23, 'text': 'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَلُؤۡلُؤٗاۖ وَلِبَاسُهُمۡ فِيهَا حَرِيرٞ\nАлбатта Аллоҳ иймон келтирган ва яхши амаллар қилган зотларни остидан дарёлар оқиб турадиган жаннатларга дохил қилур. Улар у жойда олтиндан бўлган билакузуклар ва марварид-маржонлар билан безанурлар, либослари эса ҳарир-ипак бўлур'}, {'chapter': 22, 'verse': 24, 'text': 'وَهُدُوٓاْ إِلَى ٱلطَّيِّبِ مِنَ ٱلۡقَوۡلِ وَهُدُوٓاْ إِلَىٰ صِرَٰطِ ٱلۡحَمِيدِ\nУлар (бу дунёда) хуш-ҳақ сўзга (яъни, «Аллоҳ якка-ягонадир», деган сўзга) ҳидоят қилинурлар ва ҳамду-сано эгаси — Аллоҳнинг йўлига йўлланурлар'}, {'chapter': 22, 'verse': 25, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ ٱلَّذِي جَعَلۡنَٰهُ لِلنَّاسِ سَوَآءً ٱلۡعَٰكِفُ فِيهِ وَٱلۡبَادِۚ وَمَن يُرِدۡ فِيهِ بِإِلۡحَادِۭ بِظُلۡمٖ نُّذِقۡهُ مِنۡ عَذَابٍ أَلِيمٖ\nАлбатта ўзлари кофир бўлган ва (ўзгаларни) Аллоҳ йўлидан тўсадиган, ҳамда Биз (Маккада) тургувчилар учун ҳам, (бошқа юртлардан) келгувчилар учун ҳам баробар қилиб қўйган, Масжидул-Ҳаромдан (тўсадиган) кимсалар ва кимки у жойда зулм-зўравонлик билан йўлдан чиқмоқчи бўлса (буларнинг ҳар бирига) аламли азобдан тотдириб қўюрмиз'}, {'chapter': 22, 'verse': 26, 'text': 'وَإِذۡ بَوَّأۡنَا لِإِبۡرَٰهِيمَ مَكَانَ ٱلۡبَيۡتِ أَن لَّا تُشۡرِكۡ بِي شَيۡـٔٗا وَطَهِّرۡ بَيۡتِيَ لِلطَّآئِفِينَ وَٱلۡقَآئِمِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эсланг, Биз Иброҳимга Байтуллоҳнинг ўрнини (ўша уйга қараб ибодат қилиши ва уни обод қилиши учун) белгилаб бериб, (унга шундай деган эдик): «Сен Менга бирон нарсани шерик қилмагин ва Менинг Байтим — Уйимни тавоф қилгувчилар қиём (яъни, намозда тик — ғоз туриш), рукуъ, сажда қилгувчилар (яъни, ўша жойда намоз ўқигувчилар) учун пок тутгин'}, {'chapter': 22, 'verse': 27, 'text': 'وَأَذِّن فِي ٱلنَّاسِ بِٱلۡحَجِّ يَأۡتُوكَ رِجَالٗا وَعَلَىٰ كُلِّ ضَامِرٖ يَأۡتِينَ مِن كُلِّ فَجٍّ عَمِيقٖ\nВа одамлар орасида (юриб уларни) ҳажга чақиргин, улар сенга (яъни, сенинг даъватингга жавобан) яёв ҳолларида ва йироқ йўллардан келадиган ориқ-ҳолдан тойган туялар устида келурлар'}, {'chapter': 22, 'verse': 28, 'text': 'لِّيَشۡهَدُواْ مَنَٰفِعَ لَهُمۡ وَيَذۡكُرُواْ ٱسۡمَ ٱللَّهِ فِيٓ أَيَّامٖ مَّعۡلُومَٰتٍ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلۡأَنۡعَٰمِۖ فَكُلُواْ مِنۡهَا وَأَطۡعِمُواْ ٱلۡبَآئِسَ ٱلۡفَقِيرَ\nУлар ўзлари учун бўлган (диний ва дунёвий) манфаатларга шоҳид бўлиш учун ва маълум кунларда (яъни, Қурбон ҳайити кунларида Аллоҳ) уларга ризқ қилиб берган чорва ҳайвонларини (қурбонлик учун сўйиш) устида Аллоҳ номини зикр қилиш учун (келурлар). Бас, ундан ўзларингиз ҳам еяверинглар, бечора камбағалларга ҳам едиринглар'}, {'chapter': 22, 'verse': 29, 'text': 'ثُمَّ لۡيَقۡضُواْ تَفَثَهُمۡ وَلۡيُوفُواْ نُذُورَهُمۡ وَلۡيَطَّوَّفُواْ بِٱلۡبَيۡتِ ٱلۡعَتِيقِ\nСўнгра (қурбонлик қилганларидан кейин) улар кирларини кетказсинлар (яъни, эҳромдан чиқиб, сочларини олдириб, тоза либосларини кийсинлар), назрларини (яъни, зиммаларидаги ҳаж мажбуриятларини) тўла адо қилсинлар ва «Эски Уй»ни тавоф қилсинлар'}, {'chapter': 22, 'verse': 30, 'text': 'ذَٰلِكَۖ وَمَن يُعَظِّمۡ حُرُمَٰتِ ٱللَّهِ فَهُوَ خَيۡرٞ لَّهُۥ عِندَ رَبِّهِۦۗ وَأُحِلَّتۡ لَكُمُ ٱلۡأَنۡعَٰمُ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡۖ فَٱجۡتَنِبُواْ ٱلرِّجۡسَ مِنَ ٱلۡأَوۡثَٰنِ وَٱجۡتَنِبُواْ قَوۡلَ ٱلزُّورِ\n(Иш) шудир. Ким Аллоҳ ҳаром қилган нарсаларни ҳурмат (риоя) қилса, бас, бу Парвардигори наздида ўзи учун яхшидир. Сизлар учун (ҳаром эканлиги) тиловат қилинадиган нарсалардан бошқа (барча) чорва моллари ҳалол қилинади. Бас, бутлардан иборат нажосатдан йироқ бўлингиз ва ёлғон сўздан йироқ бўлингиз'}, {'chapter': 22, 'verse': 31, 'text': 'حُنَفَآءَ لِلَّهِ غَيۡرَ مُشۡرِكِينَ بِهِۦۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَكَأَنَّمَا خَرَّ مِنَ ٱلسَّمَآءِ فَتَخۡطَفُهُ ٱلطَّيۡرُ أَوۡ تَهۡوِي بِهِ ٱلرِّيحُ فِي مَكَانٖ سَحِيقٖ\nАллоҳ учун ҳақиқий (йўлдан оғмаган мусулмон) бўлган ва У зотга ширк келтиргувчи бўлмаган ҳолингизда. Ким Аллоҳга ширк келтирса бас, у осмондан қулагану, уни (бирон ваҳший) қуш (ўлжа қилиб) олиб кетган ёки (қаттиқ) шамол йироқ жойларга учириб кетган кабидир'}, {'chapter': 22, 'verse': 32, 'text': 'ذَٰلِكَۖ وَمَن يُعَظِّمۡ شَعَـٰٓئِرَ ٱللَّهِ فَإِنَّهَا مِن تَقۡوَى ٱلۡقُلُوبِ\n(Иш) шудир. Ким Аллоҳ қонунларини ҳурмат қилса, бас, албатта (бу ҳурмат) дилларнинг тақводорлиги сабабли бўлур'}, {'chapter': 22, 'verse': 33, 'text': 'لَكُمۡ فِيهَا مَنَٰفِعُ إِلَىٰٓ أَجَلٖ مُّسَمّٗى ثُمَّ مَحِلُّهَآ إِلَى ٱلۡبَيۡتِ ٱلۡعَتِيقِ\nСизлар учун уларда (яъни, Байтуллоҳда қурбон қилиш учун олиб кетаётган қўй, мол, туяларингизда) маълум муддатгача фойда-манфаатлар бордир (яъни, йўлда уларни қўзилатиб, соғиб, миниб фойдаланиб кетишларингиз жоиздир). Сўнгра Эски Уйга (етгач) уларни сўйиш (вожибдир)'}, {'chapter': 22, 'verse': 34, 'text': 'وَلِكُلِّ أُمَّةٖ جَعَلۡنَا مَنسَكٗا لِّيَذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلۡأَنۡعَٰمِۗ فَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَلَهُۥٓ أَسۡلِمُواْۗ وَبَشِّرِ ٱلۡمُخۡبِتِينَ\nБиз (сизлардан аввалги мўмин миллатлардан ҳам) барча миллатга (Аллоҳ) уларни ризқлантирган чорва ҳайвонларини (сўйиш-қурбон қилиш) устида Аллоҳнинг номини зикр қилишлари учун қурбонлик қилишни буюрганмиз. Бас, (барчаларингизнинг) илоҳингиз бир Илоҳдир. Бас, Унгагина бўйинсунингиз! Итоат қилгувчи зотларга хушхабар беринг'}, {'chapter': 22, 'verse': 35, 'text': 'ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَٱلصَّـٰبِرِينَ عَلَىٰ مَآ أَصَابَهُمۡ وَٱلۡمُقِيمِي ٱلصَّلَوٰةِ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар Аллоҳни зикр қилинганида, диллари қўрқувга тушадиган, ўзларига етган балоларга сабр-тоқат қиладиган, намозни тўкис адо этадиган ва Биз ризқ қилиб берган нарсалардан инфоқ-эҳсон қиладиган зотлардир'}, {'chapter': 22, 'verse': 36, 'text': 'وَٱلۡبُدۡنَ جَعَلۡنَٰهَا لَكُم مِّن شَعَـٰٓئِرِ ٱللَّهِ لَكُمۡ فِيهَا خَيۡرٞۖ فَٱذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَيۡهَا صَوَآفَّۖ فَإِذَا وَجَبَتۡ جُنُوبُهَا فَكُلُواْ مِنۡهَا وَأَطۡعِمُواْ ٱلۡقَانِعَ وَٱلۡمُعۡتَرَّۚ كَذَٰلِكَ سَخَّرۡنَٰهَا لَكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ\n(Байтуллоҳда қурбон қилинадиган) туя-молларни Биз сизлар учун Аллоҳ қонунларидан қилдик. Сизлар учун уларни (қурбон қилишда) яхшилик бордир. Бас, улар (сўйиш учун) тизиб қўйилган ҳолларида Аллоҳ номини зикр қилингиз. Энди қачон ёнбошлари билан тушганларида (яъни, жон таслим қилганларида), улардан (ўзларингиз ҳам) еяверинглар, (бировдан бир нарса сўрамайдиган) қаноатли кишига ва (муҳтож) тиланчига ҳам едиринглар. Шукр қилишларингиз учун Биз уларни (яъни, туя-молларни) сизларга бўйсундирдик'}, {'chapter': 22, 'verse': 37, 'text': 'لَن يَنَالَ ٱللَّهَ لُحُومُهَا وَلَا دِمَآؤُهَا وَلَٰكِن يَنَالُهُ ٱلتَّقۡوَىٰ مِنكُمۡۚ كَذَٰلِكَ سَخَّرَهَا لَكُمۡ لِتُكَبِّرُواْ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡۗ وَبَشِّرِ ٱلۡمُحۡسِنِينَ\nАллоҳга (қилган қурбонликларингизнинг) гўштлари ҳам, қонлари ҳам етмас. Лекин У зотга сизларнинг тақво-ихлосингиз етар. Аллоҳ сизларни ҳидоят қилгани сабабли У зотни улуғлашларингиз учун (яъни, қурбонликларингизни сўяётган пайтингизда Аллоҳнинг исмини зикр қилишингиз учун) уларни сизларга бўйсундириб қўйди. Яхшилик қилгувчиларга хушхабар беринг'}, {'chapter': 22, 'verse': 38, 'text': '۞إِنَّ ٱللَّهَ يُدَٰفِعُ عَنِ ٱلَّذِينَ ءَامَنُوٓاْۗ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ خَوَّانٖ كَفُورٍ\nАлбатта Аллоҳ иймон келтирган зотларни мудофаа — ҳимоя қилур. Албатта Аллоҳ барча хоин ва куфрони неъмат қилгувчиларни суймас'}, {'chapter': 22, 'verse': 39, 'text': 'أُذِنَ لِلَّذِينَ يُقَٰتَلُونَ بِأَنَّهُمۡ ظُلِمُواْۚ وَإِنَّ ٱللَّهَ عَلَىٰ نَصۡرِهِمۡ لَقَدِيرٌ\nҲужумга учраётган зотларга мазлум бўлганлари сабабли (жанг қилиш) изни берилди. Албатта Аллоҳ уларни ғолиб қилишга қодирдир'}, {'chapter': 22, 'verse': 40, 'text': 'ٱلَّذِينَ أُخۡرِجُواْ مِن دِيَٰرِهِم بِغَيۡرِ حَقٍّ إِلَّآ أَن يَقُولُواْ رَبُّنَا ٱللَّهُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّهُدِّمَتۡ صَوَٰمِعُ وَبِيَعٞ وَصَلَوَٰتٞ وَمَسَٰجِدُ يُذۡكَرُ فِيهَا ٱسۡمُ ٱللَّهِ كَثِيرٗاۗ وَلَيَنصُرَنَّ ٱللَّهُ مَن يَنصُرُهُۥٓۚ إِنَّ ٱللَّهَ لَقَوِيٌّ عَزِيزٌ\nУлар ўз диёрларидан фақатгина «Бизнинг Парвардигоримиз (ягона) Аллоҳдир», деганлари учун қувилган зотлардир. Агар Аллоҳ одамларнинг айримларини айримлари билан дафъ қилиб турмас экан, шубҳасиз, Аллоҳ номи кўп зикр қилинадиган (роҳибларнинг) узлатгоҳлари, (насронийларнинг) бутхоналари, (яҳудийларнинг) ибодатхоналари ва (мусулмонларнинг) масжидлари вайрон қилинган бўлур эди. Албатта Аллоҳ Ўзига (яъни, динига) ёрдам берадиган зотларни ғолиб қилур. Шубҳасиз, Аллоҳ кучли, қудратлидир'}, {'chapter': 22, 'verse': 41, 'text': 'ٱلَّذِينَ إِن مَّكَّنَّـٰهُمۡ فِي ٱلۡأَرۡضِ أَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ وَأَمَرُواْ بِٱلۡمَعۡرُوفِ وَنَهَوۡاْ عَنِ ٱلۡمُنكَرِۗ وَلِلَّهِ عَٰقِبَةُ ٱلۡأُمُورِ\nУларни (яъни, мусулмонларни) агар Биз Ер юзига ғолиб қилсак — улар намозни тўкис адо этадилар, закотни (ҳақдорларга) ато этадилар, яхшиликка буюрадилар, ёмонликдан қайтарадилар. (Барча) ишларнинг оқибати Аллоҳнинг (измидадир)'}, {'chapter': 22, 'verse': 42, 'text': 'وَإِن يُكَذِّبُوكَ فَقَدۡ كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَعَادٞ وَثَمُودُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар улар (яъни, Макка мушриклари) сизни ёлғончи қилсалар, (билингки) улардан илгари Нуҳ қавми, Од, Самуд (қабилалари) ҳам (ўзларига юборилган пайғамбарларни) ёлғончи қилгандирлар'}, {'chapter': 22, 'verse': 43, 'text': 'وَقَوۡمُ إِبۡرَٰهِيمَ وَقَوۡمُ لُوطٖ\nИброҳимнинг қавми (Иброҳимни), Лут қавми (Лутни)'}, {'chapter': 22, 'verse': 44, 'text': 'وَأَصۡحَٰبُ مَدۡيَنَۖ وَكُذِّبَ مُوسَىٰۖ فَأَمۡلَيۡتُ لِلۡكَٰفِرِينَ ثُمَّ أَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ نَكِيرِ\nМадян (шаҳрининг) аҳолиси (Шуайбни ёлғончи қилгандирлар). Мусо ҳам ёлғончи қилинди. Мен эса кофир бўлган кимсаларга муҳлат бериб қўйиб, сўнгра уларни (Ўз азобим билан) ушладим. Бас, Менинг инкорим қандай бўлди'}, {'chapter': 22, 'verse': 45, 'text': 'فَكَأَيِّن مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا وَهِيَ ظَالِمَةٞ فَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَبِئۡرٖ مُّعَطَّلَةٖ وَقَصۡرٖ مَّشِيدٍ\nЗотан қанчадан-қанча золим-кофир бўлган ҳолларида Биз ҳалок қилган, бас, томлари (йиқилиб) ҳувиллаб қолган қишлоқ-шаҳарлар, (қанча) ташландиқ қудуқлар ва юксак (лекин кимсасиз) қасрлар бордир'}, {'chapter': 22, 'verse': 46, 'text': 'أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَتَكُونَ لَهُمۡ قُلُوبٞ يَعۡقِلُونَ بِهَآ أَوۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۖ فَإِنَّهَا لَا تَعۡمَى ٱلۡأَبۡصَٰرُ وَلَٰكِن تَعۡمَى ٱلۡقُلُوبُ ٱلَّتِي فِي ٱلصُّدُورِ\nАхир улар (яъни, Макка мушриклари) Ер юзида сайр қилиб-айланмайдиларми, (ана ўшанда) улар учун доно диллар, тинглайдиган қулоқлар бўлур эди. Зеро, кўзлар кўр бўлмас, балки кўксларидаги кўнгиллар кўр бўлур'}, {'chapter': 22, 'verse': 47, 'text': 'وَيَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَن يُخۡلِفَ ٱللَّهُ وَعۡدَهُۥۚ وَإِنَّ يَوۡمًا عِندَ رَبِّكَ كَأَلۡفِ سَنَةٖ مِّمَّا تَعُدُّونَ\nУлар сиздан (истеҳзо билан Аллоҳ ваъда этган) нақд азобни талаб қиладилар. Ҳолбуки, Аллоҳ Ўз ваъдасига ҳаргиз хилоф қилмас. Дарвоқеъ, Парвардигорингиз наздидаги бир кун(лик азоб) сизларнинг ҳисоб-китобингиздаги минг йилга баробардир'}, {'chapter': 22, 'verse': 48, 'text': 'وَكَأَيِّن مِّن قَرۡيَةٍ أَمۡلَيۡتُ لَهَا وَهِيَ ظَالِمَةٞ ثُمَّ أَخَذۡتُهَا وَإِلَيَّ ٱلۡمَصِيرُ\nҚанчадан-қанча золим-кофир бўлган ҳолларида Мен (ҳалок қилмай) муҳлат бериб қўйиб, сўнг (азобим билан) ушлаган қишлоқ-шаҳарлар бўлгандир. Ёлғиз Менгагина қайтиш(ингиз) бордир'}, {'chapter': 22, 'verse': 49, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَآ أَنَا۠ لَكُمۡ نَذِيرٞ مُّبِينٞ\nАйтинг: «Эй инсонлар, мен сизлар учун фақат бир очиқ огоҳлантиргувчиман, холос»'}, {'chapter': 22, 'verse': 50, 'text': 'فَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nБас, иймон келтирган ва яхши амаллар қилган зотлар учун мағфират ва улуғ ризқ бўлур'}, {'chapter': 22, 'verse': 51, 'text': 'وَٱلَّذِينَ سَعَوۡاْ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nБизнинг оятларимиз билан олишишга уринган кимсалар — улар дўзах эгаларидир'}, {'chapter': 22, 'verse': 52, 'text': 'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٖ وَلَا نَبِيٍّ إِلَّآ إِذَا تَمَنَّىٰٓ أَلۡقَى ٱلشَّيۡطَٰنُ فِيٓ أُمۡنِيَّتِهِۦ فَيَنسَخُ ٱللَّهُ مَا يُلۡقِي ٱلشَّيۡطَٰنُ ثُمَّ يُحۡكِمُ ٱللَّهُ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан илгари юборган ҳар бир элчи ва пайғамбар борки, қачон (улардан биронтаси ўзига нозил бўлган оятларни) қироат қилса, шайтон унинг қироатига (асли оятда бўлмаган нарсаларни) ташлагандир. Бас, Аллоҳ шайтон ташлайдиган нарсани бекор қилур, сўнгра Аллоҳ Ўз оятларини мустаҳкам қилур. Аллоҳ илм ва ҳикмат Эгасидир'}, {'chapter': 22, 'verse': 53, 'text': 'لِّيَجۡعَلَ مَا يُلۡقِي ٱلشَّيۡطَٰنُ فِتۡنَةٗ لِّلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡقَاسِيَةِ قُلُوبُهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَفِي شِقَاقِۭ بَعِيدٖ\n(Аллоҳ) шайтон ташлайдиган нарсани дилларида мараз бўлган (мунофиқлар), тош дил кимсалар (яъни, кофирлар) учун фитна-синов қилиш учун (пайдо қилур). Дарҳақиқат, золим-кофирлар мангу ихтилофдадирлар'}, {'chapter': 22, 'verse': 54, 'text': 'وَلِيَعۡلَمَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ فَيُؤۡمِنُواْ بِهِۦ فَتُخۡبِتَ لَهُۥ قُلُوبُهُمۡۗ وَإِنَّ ٱللَّهَ لَهَادِ ٱلَّذِينَ ءَامَنُوٓاْ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nВа илм ато этилган кишилар у (Қуръон) Парвардигорингиз томонидан келган Ҳақиқат эканини билиб, унга иймон келтиришлари, бас, диллари у сабабли таскин топиши учун (Аллоҳ шундай қилур). Дарҳақиқат, Аллоҳ иймон келтирган зотларни Тўғри Йўлга ҳидоят қилгувчидир'}, {'chapter': 22, 'verse': 55, 'text': 'وَلَا يَزَالُ ٱلَّذِينَ كَفَرُواْ فِي مِرۡيَةٖ مِّنۡهُ حَتَّىٰ تَأۡتِيَهُمُ ٱلسَّاعَةُ بَغۡتَةً أَوۡ يَأۡتِيَهُمۡ عَذَابُ يَوۡمٍ عَقِيمٍ\nКофир бўлган кимсалар то уларга тўсатдан (Қиёмат) соати келгунича ёки туғмас (яъни, эртаси бўлмаган) Куннинг азоби келгунича (Қуръон)дан шак-шубҳададирлар'}, {'chapter': 22, 'verse': 56, 'text': 'ٱلۡمُلۡكُ يَوۡمَئِذٖ لِّلَّهِ يَحۡكُمُ بَيۡنَهُمۡۚ فَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فِي جَنَّـٰتِ ٱلنَّعِيمِ\nУ кунда подшоҳлик (ёлғиз) Аллоҳникидир. У зотнинг Ўзи улар ўртасида ҳукм қилур. Бас, иймон келтирган ва яхши амаллар қилган зотлар ноз-неъматларга (тўла) жаннатлардадирлар'}, {'chapter': 22, 'verse': 57, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا فَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ\nКофир бўлган ва Бизнинг оятларимизни ёлғон деган кимсалар — улар учун хор қилгувчи азоб бордир'}, {'chapter': 22, 'verse': 58, 'text': 'وَٱلَّذِينَ هَاجَرُواْ فِي سَبِيلِ ٱللَّهِ ثُمَّ قُتِلُوٓاْ أَوۡ مَاتُواْ لَيَرۡزُقَنَّهُمُ ٱللَّهُ رِزۡقًا حَسَنٗاۚ وَإِنَّ ٱللَّهَ لَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ\nАллоҳ йўлида ҳижрат қилган, сўнгра қатл қилинган ёки вафот топган зотларни албатта Аллоҳ гўзал ризқ (яъни, мангу жаннат) билан ризқлантирур. Дарҳақиқат, Аллоҳнинг Ўзигина энг яхши ризқ бергувчидир'}, {'chapter': 22, 'verse': 59, 'text': 'لَيُدۡخِلَنَّهُم مُّدۡخَلٗا يَرۡضَوۡنَهُۥۚ وَإِنَّ ٱللَّهَ لَعَلِيمٌ حَلِيمٞ\nАлбатта (Аллоҳ) уларни Ўзлари рози бўладиган жойга — жаннатга дохил қилур. Дарҳақиқат, Аллоҳ билгувчи ва ҳалимдир'}, {'chapter': 22, 'verse': 60, 'text': '۞ذَٰلِكَۖ وَمَنۡ عَاقَبَ بِمِثۡلِ مَا عُوقِبَ بِهِۦ ثُمَّ بُغِيَ عَلَيۡهِ لَيَنصُرَنَّهُ ٱللَّهُۚ إِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٞ\n(Иш) шудир. Ким ўзига берилган азоб-уқубат баробарида ўч олса, сўнгра яна унга зулм-зўравонлик қилинса, албатта Аллоҳ унга (мазлумга) ёрдам қилур. Дарҳақиқат, Аллоҳ афв қилгувчи, кечиргувчидир'}, {'chapter': 22, 'verse': 61, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَأَنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٞ\nБунга (яъни, Аллоҳ мазлумга ёрдам қила олишига) сабаб Аллоҳ кечани кундузга киритиши (яъни, кечани қисқартириб, кундузни узайтириши) ва (аскинча) кундузни эса кечага киритиши (яъни, у Ўзи хоҳлаган нарсага қодир экани) ва шак-шубҳасиз, Аллоҳ эшитгувчи, кўргувчи эканлигидир'}, {'chapter': 22, 'verse': 62, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِۦ هُوَ ٱلۡبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ\nБунга сабаб Аллоҳнинг Ўзигина ҳақиқий Илоҳ экани ва сизлар Уни қўйиб илтижо қилаётганлар эса ботилнинг ўзи экани, ҳамда, шак-шубҳасиз, Аллоҳ Ўзигина энг юксак ва буюк Зот эканлигидир'}, {'chapter': 22, 'verse': 63, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَتُصۡبِحُ ٱلۡأَرۡضُ مُخۡضَرَّةًۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٞ\nАллоҳ осмондан сув-ёмғир ёғдириб, (бутун) ер кўм-кўк бўлишини кўрмадингизми?! Дарҳақиқат, Аллоҳ меҳрибон ва огоҳ Зотдир'}, {'chapter': 22, 'verse': 64, 'text': 'لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nОсмонлардаги ва Ердаги бор нарса Уникидир. Дарҳақиқат, Аллоҳнинг Ўзигина беҳожат ва ҳамду сано Соҳибидир'}, {'chapter': 22, 'verse': 65, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِي ٱلۡأَرۡضِ وَٱلۡفُلۡكَ تَجۡرِي فِي ٱلۡبَحۡرِ بِأَمۡرِهِۦ وَيُمۡسِكُ ٱلسَّمَآءَ أَن تَقَعَ عَلَى ٱلۡأَرۡضِ إِلَّا بِإِذۡنِهِۦٓۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٞ رَّحِيمٞ\nАллоҳ сизларга Ердаги нарсаларни ва денгизда Унинг амри билан сузиб кетаётган кемаларни бўйсундириб қўйганини, ҳамда Ўзи осмонни Ерга қулаб тушишдан тутиб туришини, магар (осмон) Унинг изни-иродаси билангина (қулаши мумкинлигини) кўрмадингизми?! Дарҳақиқат, Аллоҳ одамларга меҳрибон ва раҳмлидир'}, {'chapter': 22, 'verse': 66, 'text': 'وَهُوَ ٱلَّذِيٓ أَحۡيَاكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۗ إِنَّ ٱلۡإِنسَٰنَ لَكَفُورٞ\nУ сизларга ҳаёт берган, сўнгра (ажалларингиз етганида) ўлим берадиган, сўнгра (Қиёмат қойим бўлганида яна) ҳаёт берадиган Зотдир. Дарҳақиқат, инсон куфрони неъмат қилгувчидир'}, {'chapter': 22, 'verse': 67, 'text': 'لِّكُلِّ أُمَّةٖ جَعَلۡنَا مَنسَكًا هُمۡ نَاسِكُوهُۖ فَلَا يُنَٰزِعُنَّكَ فِي ٱلۡأَمۡرِۚ وَٱدۡعُ إِلَىٰ رَبِّكَۖ إِنَّكَ لَعَلَىٰ هُدٗى مُّسۡتَقِيمٖ\nБиз барча миллатга қурбонлик қилиш (қонунини) буюрганмиз — улар ўша (қонунга) амал қилгувчидирлар. Бас, (эй Муҳаммад алайҳис-салоту вас-салом), улар сиз билан (қурбонлик) иши хусусида ҳаргиз талашмасинлар! Сиз (одамларни) Парвардигорингизга даъват қилинг! Албатта сиз Тўғри Йўл устидадирсиз'}, {'chapter': 22, 'verse': 68, 'text': 'وَإِن جَٰدَلُوكَ فَقُلِ ٱللَّهُ أَعۡلَمُ بِمَا تَعۡمَلُونَ\nАгар улар сиз билан мужодала қилсалар, у ҳолда айтинг: «Аллоҳ сизлар қилаётган (ёмон) ишларни жуда яхши билгувчидир»'}, {'chapter': 22, 'verse': 69, 'text': 'ٱللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ\nАллоҳ Қиёмат Кунида сизлар ихтилоф қилиб ўтган нарсалар ҳақида ўртангизда ҳукм қилур'}, {'chapter': 22, 'verse': 70, 'text': 'أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِنَّ ذَٰلِكَ فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз Аллоҳ осмон ва Ердаги бор нарсани билишини билмадингизми?! Албатта, бу Китобда (яъни, Лавҳул-махфузда ёзиб қўйилгандир). Албатта, бу Аллоҳга осондир'}, {'chapter': 22, 'verse': 71, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗا وَمَا لَيۡسَ لَهُم بِهِۦ عِلۡمٞۗ وَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٖ\nУлар (яъни, Қурайш кофирлари) Аллоҳни қўйиб, У зот (илоҳ эканига) бирон ҳужжат туширмаган ва ўзлари ҳам билмаган нарсаларга ибодат қиларлар. Бу золимлар учун бирон ёрдамчи йўқдир'}, {'chapter': 22, 'verse': 72, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ تَعۡرِفُ فِي وُجُوهِ ٱلَّذِينَ كَفَرُواْ ٱلۡمُنكَرَۖ يَكَادُونَ يَسۡطُونَ بِٱلَّذِينَ يَتۡلُونَ عَلَيۡهِمۡ ءَايَٰتِنَاۗ قُلۡ أَفَأُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكُمُۚ ٱلنَّارُ وَعَدَهَا ٱللَّهُ ٱلَّذِينَ كَفَرُواْۖ وَبِئۡسَ ٱلۡمَصِيرُ\nҚачонки уларга Бизнинг очиқ-равшан бўлган оятларимиз тиловат қилинса, кофир бўлган кимсаларнинг юзларида инкор (аломатини) сезурсиз. Улар, ўзларига Бизнинг оятларимизни тиловат қилаётган зотларга чанг солишга яқин бўлурлар. (Сиз уларга) айтинг: «Энди мен сизларга бундан ёмонроқ нарсанинг хабарини берайми? (У) Аллоҳ кофир бўлган кимсаларга ваъда қилган дўзахдир. Нақадар ёмон оқибат у!»'}, {'chapter': 22, 'verse': 73, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ضُرِبَ مَثَلٞ فَٱسۡتَمِعُواْ لَهُۥٓۚ إِنَّ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَن يَخۡلُقُواْ ذُبَابٗا وَلَوِ ٱجۡتَمَعُواْ لَهُۥۖ وَإِن يَسۡلُبۡهُمُ ٱلذُّبَابُ شَيۡـٔٗا لَّا يَسۡتَنقِذُوهُ مِنۡهُۚ ضَعُفَ ٱلطَّالِبُ وَٱلۡمَطۡلُوبُ\nЭй инсонлар, бир масал айтилгандир, бас унга қулоқ тутинг: Аниқки, сизлар Аллоҳни қўйиб илтижо қилаётган бутлар агар барчалари бирлашганларида ҳам бир чивин ярата олмаслар, агар чивин улардан бирон нарсани тортиб олса, уни (ўша чивиндан ҳам) қутқариб ола билмаслар. (Демак, ўша бутлардан ҳожатини (раво қилишни сўрагувчи (мушрик) ҳам, сўралгувчи (бутлар) ҳам ночор-нотавондир'}, {'chapter': 22, 'verse': 74, 'text': 'مَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦٓۚ إِنَّ ٱللَّهَ لَقَوِيٌّ عَزِيزٌ\nУлар Аллоҳнинг ҳақиқий қадри — улуғлигини билмадилар. Дарҳақиқат, Аллоҳ кучли, қудратлидир'}, {'chapter': 22, 'verse': 75, 'text': 'ٱللَّهُ يَصۡطَفِي مِنَ ٱلۡمَلَـٰٓئِكَةِ رُسُلٗا وَمِنَ ٱلنَّاسِۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٞ\nАллоҳ фаришталардан ҳам элчилар танлар, инсонлардан ҳам. Дарҳақиқат, Аллоҳ эшитгувчи, кўргувчидир'}, {'chapter': 22, 'verse': 76, 'text': 'يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nУ зот уларнинг олдиларидаги ва ортларидаги нарсаларни билур. Ва (барча) ишлар ёлғиз Аллоҳга қайтарилур'}, {'chapter': 22, 'verse': 77, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱرۡكَعُواْ وَٱسۡجُدُواْۤ وَٱعۡبُدُواْ رَبَّكُمۡ وَٱفۡعَلُواْ ٱلۡخَيۡرَ لَعَلَّكُمۡ تُفۡلِحُونَ۩\nЭй мўминлар, рукуъ қилингиз, сажда қилингиз ва Парвардигорингизга ибодат қилиб, яхшилик қилингиз, шоядки нажот топсангизлар'}, {'chapter': 22, 'verse': 78, 'text': 'وَجَٰهِدُواْ فِي ٱللَّهِ حَقَّ جِهَادِهِۦۚ هُوَ ٱجۡتَبَىٰكُمۡ وَمَا جَعَلَ عَلَيۡكُمۡ فِي ٱلدِّينِ مِنۡ حَرَجٖۚ مِّلَّةَ أَبِيكُمۡ إِبۡرَٰهِيمَۚ هُوَ سَمَّىٰكُمُ ٱلۡمُسۡلِمِينَ مِن قَبۡلُ وَفِي هَٰذَا لِيَكُونَ ٱلرَّسُولُ شَهِيدًا عَلَيۡكُمۡ وَتَكُونُواْ شُهَدَآءَ عَلَى ٱلنَّاسِۚ فَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَٱعۡتَصِمُواْ بِٱللَّهِ هُوَ مَوۡلَىٰكُمۡۖ فَنِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِيرُ\nВа Аллоҳ (йўлида) ҳақиқий жиҳод қилингиз! Унинг Ўзи сизларни (шу муқаддас дин учун) сайлади ва бу динда сизларга бирон хараж-танглик қилмади. Оталарингиз Иброҳимнинг динини (яъни, Исломни ушлангиз. Токи (Қиёмат Кунида) пайғамбар сизларнинг устингизда гувоҳ бўлиши учун, сизлар эса (барча динлардаги) одамлар устида гувоҳ бўлишингиз учун (Аллоҳнинг) Ўзи сизларни илгари(ги муқаддас китобларида) ҳам мана шу (Қуръонда) ҳам мусулмонлар (яъни, Ўзининг динига бўйинсунувчилар), деб атади. Бас, намозни тўкис адо этингиз, закотни (ҳақдорларга) ато этингиз ва Аллоҳга боғланингиз! У сизларнинг Хожангиздир. Бас, У зот нақадар яхши хожа ва нақадар яхши мададкордир'}, {'chapter': 23, 'verse': 1, 'text': 'قَدۡ أَفۡلَحَ ٱلۡمُؤۡمِنُونَ\nДарҳақиқат, мўминлар нажот топдилар'}, {'chapter': 23, 'verse': 2, 'text': 'ٱلَّذِينَ هُمۡ فِي صَلَاتِهِمۡ خَٰشِعُونَ\nУлар намозларида (қўрқув ва умид билан) бўйин эгувчи кишилардир'}, {'chapter': 23, 'verse': 3, 'text': 'وَٱلَّذِينَ هُمۡ عَنِ ٱللَّغۡوِ مُعۡرِضُونَ\nУлар беҳуда — фойдасиз (сўз ва амаллардан) юз ўгиргувчи кишилардир'}, {'chapter': 23, 'verse': 4, 'text': 'وَٱلَّذِينَ هُمۡ لِلزَّكَوٰةِ فَٰعِلُونَ\nУлар закотни (адо) қилгувчи кишилардир'}, {'chapter': 23, 'verse': 5, 'text': 'وَٱلَّذِينَ هُمۡ لِفُرُوجِهِمۡ حَٰفِظُونَ\nУлар авратларини (ҳаромдан-зинодан) сақлагувчи кишилардир'}, {'chapter': 23, 'verse': 6, 'text': 'إِلَّا عَلَىٰٓ أَزۡوَٰجِهِمۡ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَإِنَّهُمۡ غَيۡرُ مَلُومِينَ\nМагар ўз жуфту-ҳалолларидан ва қўлларидаги чўриларидангина (сақланмайдилар). Бас, улар маломат қилинмаслар'}, {'chapter': 23, 'verse': 7, 'text': 'فَمَنِ ٱبۡتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡعَادُونَ\nЭнди ким шундан ўзгани (зино ва шу каби шариати Исломийяда ҳаром қилинган нарсаларни) истаса, бас, ана ўшалар ҳаддан ошгувчилардир'}, {'chapter': 23, 'verse': 8, 'text': 'وَٱلَّذِينَ هُمۡ لِأَمَٰنَٰتِهِمۡ وَعَهۡدِهِمۡ رَٰعُونَ\nУлар (яъни, мўминлар) ўзларига ишонилган омонатларга ва (ўзгаларга) берган аҳду паймонларига риоя қилгувчи кишилардир'}, {'chapter': 23, 'verse': 9, 'text': 'وَٱلَّذِينَ هُمۡ عَلَىٰ صَلَوَٰتِهِمۡ يُحَافِظُونَ\nУлар (барча) намозларини (вақтида адо этиб қазо бўлишдан) сақлагувчи кишилардир'}, {'chapter': 23, 'verse': 10, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡوَٰرِثُونَ\nАна ўшалар ворислардир'}, {'chapter': 23, 'verse': 11, 'text': 'ٱلَّذِينَ يَرِثُونَ ٱلۡفِرۡدَوۡسَ هُمۡ فِيهَا خَٰلِدُونَ\nФирдавс (жаннатига) меросхўр бўлгувчи (ворислардир). Улар ўша жойда мангу қолурлар'}, {'chapter': 23, 'verse': 12, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ مِن سُلَٰلَةٖ مِّن طِينٖ\n(Қасамки), Биз инсонни (яъни, Одам алайҳиссаломни) лойнинг мағзидан яратдик'}, {'chapter': 23, 'verse': 13, 'text': 'ثُمَّ جَعَلۡنَٰهُ نُطۡفَةٗ فِي قَرَارٖ مَّكِينٖ\nСўнгра уни (яъни, барча инсонларни) аввало мустаҳкам қароргоҳдаги (яъни, бачадондаги) нутфа-маний қилдик'}, {'chapter': 23, 'verse': 14, 'text': 'ثُمَّ خَلَقۡنَا ٱلنُّطۡفَةَ عَلَقَةٗ فَخَلَقۡنَا ٱلۡعَلَقَةَ مُضۡغَةٗ فَخَلَقۡنَا ٱلۡمُضۡغَةَ عِظَٰمٗا فَكَسَوۡنَا ٱلۡعِظَٰمَ لَحۡمٗا ثُمَّ أَنشَأۡنَٰهُ خَلۡقًا ءَاخَرَۚ فَتَبَارَكَ ٱللَّهُ أَحۡسَنُ ٱلۡخَٰلِقِينَ\nСўнгра бу нутфадан лахта қонни яратиб, лахта қондан парча гўштни яратиб, парча гўштдан суякларни яратиб, бу суякларга гўшт қопладик, сўнгра (унга жон киргизиб, аввал бошдаги бир томчи сув — нутфадан бутунлай) бошқа бир жонзот ҳолида пайдо қилдик. Бас, энг гўзал яратгувчи (яъни, йўқдан бор қилгувчи бўлмиш) Аллоҳ баракотли — буюкдир'}, {'chapter': 23, 'verse': 15, 'text': 'ثُمَّ إِنَّكُم بَعۡدَ ذَٰلِكَ لَمَيِّتُونَ\nСўнгра шак-шубҳасиз, сизлар (эй инсонлар), мана шундан (яъни, яралиб, ҳаётга келганингиздан) кейин (ажалларингиз битгач) албатта вафот топгувчидирсизлар'}, {'chapter': 23, 'verse': 16, 'text': 'ثُمَّ إِنَّكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ تُبۡعَثُونَ\nСўнгра шак-шубҳасиз, сизлар Қиёмат Кунида қайта тирилурсизлар'}, {'chapter': 23, 'verse': 17, 'text': 'وَلَقَدۡ خَلَقۡنَا فَوۡقَكُمۡ سَبۡعَ طَرَآئِقَ وَمَا كُنَّا عَنِ ٱلۡخَلۡقِ غَٰفِلِينَ\n(Қасамки) Биз сизларнинг устингизда етти йўлни (яъни, етти қават осмонни) яратдик. Биз Ўз халқимиздан ғофил бўлмадик'}, {'chapter': 23, 'verse': 18, 'text': 'وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَسۡكَنَّـٰهُ فِي ٱلۡأَرۡضِۖ وَإِنَّا عَلَىٰ ذَهَابِۭ بِهِۦ لَقَٰدِرُونَ\nВа Биз осмондан (аниқ) ўлчов билан сув (ёмғир-қор) ёғдириб, уни ерга жойлаб қўйдик. Шак-шубҳасиз, Биз уни кетказишга ҳам қодирдирмиз'}, {'chapter': 23, 'verse': 19, 'text': 'فَأَنشَأۡنَا لَكُم بِهِۦ جَنَّـٰتٖ مِّن نَّخِيلٖ وَأَعۡنَٰبٖ لَّكُمۡ فِيهَا فَوَٰكِهُ كَثِيرَةٞ وَمِنۡهَا تَأۡكُلُونَ\nСўнг Биз сизлар учун у (сув) ёрдамида хурмо ва узум боғларини пайдо қилдик. Сизлар учун у (боғларда) кўп мевалар бўлур, сизлар улардан ейсизлар'}, {'chapter': 23, 'verse': 20, 'text': 'وَشَجَرَةٗ تَخۡرُجُ مِن طُورِ سَيۡنَآءَ تَنۢبُتُ بِٱلدُّهۡنِ وَصِبۡغٖ لِّلۡأٓكِلِينَ\nЯна (Биз сизлар учун) Тури Сайно (тоғи)дан чиқадиган ёғли ва егувчилар учун (нон) ҳуруш бўлган ҳолда ўсадиган бир дарахтни (яъни, зайтунни яратдик)'}, {'chapter': 23, 'verse': 21, 'text': 'وَإِنَّ لَكُمۡ فِي ٱلۡأَنۡعَٰمِ لَعِبۡرَةٗۖ نُّسۡقِيكُم مِّمَّا فِي بُطُونِهَا وَلَكُمۡ فِيهَا مَنَٰفِعُ كَثِيرَةٞ وَمِنۡهَا تَأۡكُلُونَ\nАлбатга сизлар учун чорва молларида ҳам ибрат бордир — Биз сизларни уларнинг қорнидаги сут билан суғорурмиз, яна сизлар учун уларда (юнгларидан кийимлар тўқиш, миниш каби) кўп фойдалар бордир, шунингдек улар(нинг гўшт-ёғлари)дан ейсизлар'}, {'chapter': 23, 'verse': 22, 'text': 'وَعَلَيۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ\nЯна уларнинг устида ва (дарё-денгизларда эса) кемаларда юк ташийсизлар'}, {'chapter': 23, 'verse': 23, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ\n(Қасамки), Биз Нуҳни ўз қавмига пайғамбар қилдик. Бас у: «Эй қавмим, Аллоҳга ибодат қилинглар! Сизлар учун Ундан ўзга илоҳ йўқдир. Ахир қўрқмайсизларми», деди'}, {'chapter': 23, 'verse': 24, 'text': 'فَقَالَ ٱلۡمَلَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يُرِيدُ أَن يَتَفَضَّلَ عَلَيۡكُمۡ وَلَوۡ شَآءَ ٱللَّهُ لَأَنزَلَ مَلَـٰٓئِكَةٗ مَّا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ\n(Шунда) унинг қавмидан кофир бўлган кимсалар: «Бу ҳам худди сизларга ўхшаган одам. (Фақат) сизлардан устун бўлиб олмоқчи. Агар Аллоҳ (пайғамбар юборишни) хоҳласа эди, фаришталарни туширган бўлур эди. Бизлар бу (Нуҳ айтаётган «Аллоҳ якка-ягонадир», деган сўз)ни аввалги ота-боболаримиздан эшитган эмасмиз'}, {'chapter': 23, 'verse': 25, 'text': 'إِنۡ هُوَ إِلَّا رَجُلُۢ بِهِۦ جِنَّةٞ فَتَرَبَّصُواْ بِهِۦ حَتَّىٰ حِينٖ\nУ (яъни, Нуҳ) фақат бир жинни бўлган одамдир. Бас, унга бир (оз) вақтгача кўз тутинглар, (агар шу сўзидан қайтмас экан ўлдириб юборурсизлар)», дедилар'}, {'chapter': 23, 'verse': 26, 'text': 'قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ\n(Нуҳ) айтди: «Парвардигорим, улар мени ёлғончи қилганлари сабабли Ўзинг менга ёрдам қилгин (ва уларни ҳалок қилгин)»'}, {'chapter': 23, 'verse': 27, 'text': 'فَأَوۡحَيۡنَآ إِلَيۡهِ أَنِ ٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا فَإِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ فَٱسۡلُكۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ مِنۡهُمۡۖ وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ\nБас, Биз унга ваҳий қилдик: Бизнинг ҳифзу-ҳимоямизда ва Бизнинг ваҳий-таълимимиз билан бир кема ясагин. Бас, қачон Бизнинг фармонимиз келиб, таннурдан (олов ўрнига) фавворалар отилган вақтида у кемага ҳар (жонивордан) бир жуфтдан ва аҳли-оилангни солгин. Лекин (кофирлардан) қайси кимсалар устида Бизнинг сўзимиз (яъни, сувга ғарқ бўлиши ҳақидаги ҳукмимиз) ўтган бўлса (уларни тарк қилгин) ҳамда у золим кимсалар ҳақида (яъни, уларга нажот беришимни сўраб) Менга хитоб-илтижо қилмагин. Улар шак-шубҳасиз, ғарқ қилингувчидирлар'}, {'chapter': 23, 'verse': 28, 'text': 'فَإِذَا ٱسۡتَوَيۡتَ أَنتَ وَمَن مَّعَكَ عَلَى ٱلۡفُلۡكِ فَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي نَجَّىٰنَا مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nЭнди қачон ўзинг ва сен билан бирга бўлган кишилар кема устида жойлашиб олгач, айтгин: «Бизларни золим қавмдан қутқарган Аллоҳга ҳамду-сано бўлсин»'}, {'chapter': 23, 'verse': 29, 'text': 'وَقُل رَّبِّ أَنزِلۡنِي مُنزَلٗا مُّبَارَكٗا وَأَنتَ خَيۡرُ ٱلۡمُنزِلِينَ\nЯна айт: «Парвардигорим, мени бир муборак манзилга туширгин. Сен ўзинг энг яхши (манзилларга) туширгувчисан»'}, {'chapter': 23, 'verse': 30, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ وَإِن كُنَّا لَمُبۡتَلِينَ\nАлбатта бунда (яъни, Нуҳ ва уни қавми можаросида) оят-ибратлар бордир. Биз имтиҳон қилгувчидирмиз'}, {'chapter': 23, 'verse': 31, 'text': 'ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ\nСўнгра Биз улардан кейин бошқа асрларни (яъни, авлодларни — Од қабиласини) пайдо қилдик'}, {'chapter': 23, 'verse': 32, 'text': 'فَأَرۡسَلۡنَا فِيهِمۡ رَسُولٗا مِّنۡهُمۡ أَنِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ\nБас, уларга ўзларидан бўлган бир пайғамбарни (яъни, Ҳудни) юбордик. (У айтди): «Аллоҳга ибодат қилинглар! Сизлар учун Ундан ўзга илоҳ йўқдир. Ахир қўрқмайсизларми?!»'}, {'chapter': 23, 'verse': 33, 'text': 'وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِهِ ٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِلِقَآءِ ٱلۡأٓخِرَةِ وَأَتۡرَفۡنَٰهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يَأۡكُلُ مِمَّا تَأۡكُلُونَ مِنۡهُ وَيَشۡرَبُ مِمَّا تَشۡرَبُونَ\nУнинг қавмидан кофир бўлган, Охиратдаги мулоқотни ёлғон деган ва Биз ҳаёти дунёда бой-бадавлат қилиб қўйган кимсалар дедилар: «Бу ҳам худди сизларга ўхшаган одам. У ҳам сизлар ейдиган нарсадан ейди, сизлар ичадиган нарсадан ичади'}, {'chapter': 23, 'verse': 34, 'text': 'وَلَئِنۡ أَطَعۡتُم بَشَرٗا مِّثۡلَكُمۡ إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ\nҚасамки, агар сизлар ўзингизга ўхшаган одамга итоат килиб (ўз бутларингиздан кечсангизлар), у ҳолда албатта зиён кўргувчидирсизлар'}, {'chapter': 23, 'verse': 35, 'text': 'أَيَعِدُكُمۡ أَنَّكُمۡ إِذَا مِتُّمۡ وَكُنتُمۡ تُرَابٗا وَعِظَٰمًا أَنَّكُم مُّخۡرَجُونَ\nУ сизларга ўлиб тупроқ ва суякларга айланганингиздан кейин албатта (қабрларингиздан) чиқарилгувчидирсизлар, деб ваъда бермоқдами'}, {'chapter': 23, 'verse': 36, 'text': '۞هَيۡهَاتَ هَيۡهَاتَ لِمَا تُوعَدُونَ\nСизларга ваъда қилинаётган нарса жуда-жуда узоқдир'}, {'chapter': 23, 'verse': 37, 'text': 'إِنۡ هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا نَحۡنُ بِمَبۡعُوثِينَ\nҲаёт фақат (шу) дунёдаги ҳаётимиздир. (Айримларимиз) ўлсак (бошқаларимиз) ҳаётга келаверамиз. Биз ҳеч қайта тирилгувчи эмасмиз'}, {'chapter': 23, 'verse': 38, 'text': 'إِنۡ هُوَ إِلَّا رَجُلٌ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا وَمَا نَحۡنُ لَهُۥ بِمُؤۡمِنِينَ\nУ (яъни, Ҳуд) фақат Аллоҳ шаънига ёлғон тўқиган кимсадир. Бизлар унга ҳеч иймон келтиргувчи эмасмиз'}, {'chapter': 23, 'verse': 39, 'text': 'قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ\n(Шунда Ҳуд) айтди: «Парвардигорим, мени ёлғончи қилганлари сабабли Ўзинг менга ёрдам қилгин (ва уларни ҳалок қилгин)»'}, {'chapter': 23, 'verse': 40, 'text': 'قَالَ عَمَّا قَلِيلٖ لَّيُصۡبِحُنَّ نَٰدِمِينَ\n(Аллоҳ) деди: «Озгинадан кейин улар албатта надомат қилгувчиларга айланиб қолурлар»'}, {'chapter': 23, 'verse': 41, 'text': 'فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ بِٱلۡحَقِّ فَجَعَلۡنَٰهُمۡ غُثَآءٗۚ فَبُعۡدٗا لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, ҳақли равишда уларни (даҳшатли) қичқириқ тутиб, Биз уларни хас-хашакка айлантирдик. У золим қавмга ҳалокат бўлгай'}, {'chapter': 23, 'verse': 42, 'text': 'ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قُرُونًا ءَاخَرِينَ\nСўнгра, улардан кейин бошқа асрларни (авлодларни) пайдо қилдик'}, {'chapter': 23, 'verse': 43, 'text': 'مَا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ\nБирон миллат-авлод ўз ажалидан ўзиб ҳам кета олмас, (уни) кетга ҳам сура билмас'}, {'chapter': 23, 'verse': 44, 'text': 'ثُمَّ أَرۡسَلۡنَا رُسُلَنَا تَتۡرَاۖ كُلَّ مَا جَآءَ أُمَّةٗ رَّسُولُهَا كَذَّبُوهُۖ فَأَتۡبَعۡنَا بَعۡضَهُم بَعۡضٗا وَجَعَلۡنَٰهُمۡ أَحَادِيثَۚ فَبُعۡدٗا لِّقَوۡمٖ لَّا يُؤۡمِنُونَ\nСўнгра пайдар-пай пайғамбарлар юбордик. Ҳар қачон бирон миллатга ўз пайғамбарлари келса, уни ёлғончи қилдилар. Бас, Биз уларни (яъни, у иймонсиз миллатларни) бирин-кетин (ҳалок қилдик) ва уларни (кишилар ўртасида кўчиб юрадиган) гапга айлантириб қўйдик. Бас, иймон келтирмайдиган қавмга ҳалокат бўлгай'}, {'chapter': 23, 'verse': 45, 'text': 'ثُمَّ أَرۡسَلۡنَا مُوسَىٰ وَأَخَاهُ هَٰرُونَ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ\nСўнгра Биз Мусо ва унинг биродари Ҳорунни Ўз оят-мўъжизаларимиз ва очиқ ҳужжат билан пайғамбар қилиб юбордик'}, {'chapter': 23, 'verse': 46, 'text': 'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمًا عَالِينَ\nФиръавн ва унинг одамларига. Шунда улар кибр-ҳаво қилдилар. Улар мутакаббир қавм эдилар'}, {'chapter': 23, 'verse': 47, 'text': 'فَقَالُوٓاْ أَنُؤۡمِنُ لِبَشَرَيۡنِ مِثۡلِنَا وَقَوۡمُهُمَا لَنَا عَٰبِدُونَ\nУлар айтдилар: «Худди ўзимизга ўхшаган икки кишига иймон қелтирурмизми?! Ҳолбуки, уларнинг қавми (яъни, Бани Исроил) бизларга қуллик қилгувчидирлар»'}, {'chapter': 23, 'verse': 48, 'text': 'فَكَذَّبُوهُمَا فَكَانُواْ مِنَ ٱلۡمُهۡلَكِينَ\nБас иккисини ёлғончи қилишиб, ҳалок қилингувчилардан бўлдилар'}, {'chapter': 23, 'verse': 49, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ لَعَلَّهُمۡ يَهۡتَدُونَ\nДарҳақиқат, Биз Мусога (Бани Исроил қавми) ҳидоят топиши учун Китоб — Тавротни ато этдик'}, {'chapter': 23, 'verse': 50, 'text': 'وَجَعَلۡنَا ٱبۡنَ مَرۡيَمَ وَأُمَّهُۥٓ ءَايَةٗ وَءَاوَيۡنَٰهُمَآ إِلَىٰ رَبۡوَةٖ ذَاتِ قَرَارٖ وَمَعِينٖ\n(Кейин) Марямнинг ўғли (Ийсони) ва унинг онаси Марямни (Бизнинг қудратимизга далолат қиладиган) оят-аломат қилдик ва иккисини бир оқар сувли баланд-кўркам қароргоҳга жойладик'}, {'chapter': 23, 'verse': 51, 'text': 'يَـٰٓأَيُّهَا ٱلرُّسُلُ كُلُواْ مِنَ ٱلطَّيِّبَٰتِ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ عَلِيمٞ\n(Юборган барча пайғамбарларимизга шундай дедик): «Эй пайғамбарлар, ҳалол-пок таомлардан енглар ва яхши амаллар қилинглар! Албатта Мен қилаётган амалларингизни билгувчидирман'}, {'chapter': 23, 'verse': 52, 'text': 'وَإِنَّ هَٰذِهِۦٓ أُمَّتُكُمۡ أُمَّةٗ وَٰحِدَةٗ وَأَنَا۠ رَبُّكُمۡ فَٱتَّقُونِ\nШак-шубҳасиз, (барчангизни) миллатингиз (яъни, динингиз) бир миллат (яъни, Исломдир). Мен эса сизларнинг Парвардигорингиздирман, бас, Мендангина қўрқингиз!»'}, {'chapter': 23, 'verse': 53, 'text': 'فَتَقَطَّعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡ زُبُرٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمۡ فَرِحُونَ\nСўнг (одамлар) ишларини (яъни, динларини) бўлакларга бўлиб юбордилар. Ҳар бир гуруҳ ўз олдиларидаги нарса (дин) билан хурсанддирлар'}, {'chapter': 23, 'verse': 54, 'text': 'فَذَرۡهُمۡ فِي غَمۡرَتِهِمۡ حَتَّىٰ حِينٍ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз (маълум) вақтгача уларни (яъни, Макка мушрикларини) ўз ғафлатларида қолдиринг'}, {'chapter': 23, 'verse': 55, 'text': 'أَيَحۡسَبُونَ أَنَّمَا نُمِدُّهُم بِهِۦ مِن مَّالٖ وَبَنِينَ\nУлар ўйлайдилармики, Биз уларга бераётган мол-давлат ва болалари билан'}, {'chapter': 23, 'verse': 56, 'text': 'نُسَارِعُ لَهُمۡ فِي ٱلۡخَيۡرَٰتِۚ بَل لَّا يَشۡعُرُونَ\nуларга яхшиликларни тезлатишимизни. Йўқ, улар (буни ғафлатлари янада зиёда бўлиши учун қилинаётганини) сезмайдилар'}, {'chapter': 23, 'verse': 57, 'text': 'إِنَّ ٱلَّذِينَ هُم مِّنۡ خَشۡيَةِ رَبِّهِم مُّشۡفِقُونَ\nАлбатта, Парвардигорларидан қўрқиб хавфда тургувчи кишилар'}, {'chapter': 23, 'verse': 58, 'text': 'وَٱلَّذِينَ هُم بِـَٔايَٰتِ رَبِّهِمۡ يُؤۡمِنُونَ\nПарвардигорларининг оятларига иймон келтирадиган кишилар'}, {'chapter': 23, 'verse': 59, 'text': 'وَٱلَّذِينَ هُم بِرَبِّهِمۡ لَا يُشۡرِكُونَ\nПарвардигорларига ширк келтирмайдиган кишилар'}, {'chapter': 23, 'verse': 60, 'text': 'وَٱلَّذِينَ يُؤۡتُونَ مَآ ءَاتَواْ وَّقُلُوبُهُمۡ وَجِلَةٌ أَنَّهُمۡ إِلَىٰ رَبِّهِمۡ رَٰجِعُونَ\n(Камбағал-бечораларга) берган садақаларини (Қиёмат Кунида ҳисоб-китоб учун) Парвардигорга қайтгувчи эканликларидан диллари қўрқиб турган ҳолда берадиган кишилар'}, {'chapter': 23, 'verse': 61, 'text': 'أُوْلَـٰٓئِكَ يُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِ وَهُمۡ لَهَا سَٰبِقُونَ\nАна ўшаларгина (барчадан) ўзгувчи бўлган ҳолларида яхшиликлар қилишга шошурлар'}, {'chapter': 23, 'verse': 62, 'text': 'وَلَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۚ وَلَدَيۡنَا كِتَٰبٞ يَنطِقُ بِٱلۡحَقِّ وَهُمۡ لَا يُظۡلَمُونَ\nБиз ҳеч бир жонни тоқатидан ташқари нарсага таклиф қилмасмиз. Бизнинг даргоҳимизда фақат ҳақни сўзлайдиган китоб (яъни, ҳар бир банданинг номайи аъмоли) бордир. Уларга (Қиёмат Кунида яхшиликларини яшириш ё ёмонликларини ошириш билан) зулм қилинмас'}, {'chapter': 23, 'verse': 63, 'text': 'بَلۡ قُلُوبُهُمۡ فِي غَمۡرَةٖ مِّنۡ هَٰذَا وَلَهُمۡ أَعۡمَٰلٞ مِّن دُونِ ذَٰلِكَ هُمۡ لَهَا عَٰمِلُونَ\nЙўқ, (кофирларнинг) диллари бундан (яъни, юқорида мазкур бўлган мўминларнинг фазилатларидан) ғафлатдадир. Улар учун ўзлари қилувчи бўлган бундан бошқа (динсизлик, риёкорлик, ширк ва дилозорлик каби) амаллар бордир'}, {'chapter': 23, 'verse': 64, 'text': 'حَتَّىٰٓ إِذَآ أَخَذۡنَا مُتۡرَفِيهِم بِٱلۡعَذَابِ إِذَا هُمۡ يَجۡـَٔرُونَ\nТо қачон Биз уларнинг боёнларини азобга гирифтор қилганимизда (яъни, мол-давлатларини кетказиб, оч-яланғоч қилиб қўйганимизда) баногоҳ улар фарёд қилурлар'}, {'chapter': 23, 'verse': 65, 'text': 'لَا تَجۡـَٔرُواْ ٱلۡيَوۡمَۖ إِنَّكُم مِّنَّا لَا تُنصَرُونَ\n(Шунда Биз дермиз): «Бугун энди фарёд қилмангиз! Аниқки, сизларга Биз томонимиздан (ҳеч қандай) ёрдам бўлмас'}, {'chapter': 23, 'verse': 66, 'text': 'قَدۡ كَانَتۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَكُنتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ تَنكِصُونَ\n(Чунки) сизларга Менинг оятларим тиловат қилинганида, сизлар кетингизга тисланган эдингиз'}, {'chapter': 23, 'verse': 67, 'text': 'مُسۡتَكۡبِرِينَ بِهِۦ سَٰمِرٗا تَهۡجُرُونَ\n(«Байтуллоҳ бизларники», деб), у билан мутакаббирлик қилган ҳолларингизда тунги суҳбатларингизда (Қуръон хусусида) беҳуда сўзлар айтар эдингизлар»'}, {'chapter': 23, 'verse': 68, 'text': 'أَفَلَمۡ يَدَّبَّرُواْ ٱلۡقَوۡلَ أَمۡ جَآءَهُم مَّا لَمۡ يَأۡتِ ءَابَآءَهُمُ ٱلۡأَوَّلِينَ\nАхир улар бу Сўзни — Қуръонни тадаббур-тафаккур қилиб кўрмадиларми ёки уларга аввал ўтган ота-боболарига келмаган нарса келдими (яъни, уларга ҳам Аллоҳ тарафидан пайғамбарлар китоблар билан келган эди-ку)'}, {'chapter': 23, 'verse': 69, 'text': 'أَمۡ لَمۡ يَعۡرِفُواْ رَسُولَهُمۡ فَهُمۡ لَهُۥ مُنكِرُونَ\nЁки ўз пайғамбарларининг (яъни, Муҳаммад алайҳис-салоту вас-саломнинг ишончли, ростгўй ва хушхулқ инсон эканини) танимай, уни инкор қилгувчи бўлдиларми'}, {'chapter': 23, 'verse': 70, 'text': 'أَمۡ يَقُولُونَ بِهِۦ جِنَّةُۢۚ بَلۡ جَآءَهُم بِٱلۡحَقِّ وَأَكۡثَرُهُمۡ لِلۡحَقِّ كَٰرِهُونَ\nЁхуд: «Унда (Муҳаммадда) жиннилик бор», дедиларми?! Йўқ! (Муҳаммад алайҳис-салоту вас-салом) уларга Ҳақиқатни келтирди. Уларнинг кўплари эса Ҳақиқатни ёмон кўргувчилардир'}, {'chapter': 23, 'verse': 71, 'text': 'وَلَوِ ٱتَّبَعَ ٱلۡحَقُّ أَهۡوَآءَهُمۡ لَفَسَدَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ بَلۡ أَتَيۡنَٰهُم بِذِكۡرِهِمۡ فَهُمۡ عَن ذِكۡرِهِم مُّعۡرِضُونَ\nАгар Ҳақиқат — Қуръон уларнинг ҳавои нафсларига эргашса эди (яъни, унда: «Аллоҳнинг шериклари бор», деган гап келса эди, албатта осмонлар, Ер ва улардаги бор жонзот бузилиб-ҳалок бўлган бўлур эди. Йўқ, Биз улар учун эслатма келтирдик. Улар эса ўзларига (келган) эслатмадан юз ўгиргувчидирлар'}, {'chapter': 23, 'verse': 72, 'text': 'أَمۡ تَسۡـَٔلُهُمۡ خَرۡجٗا فَخَرَاجُ رَبِّكَ خَيۡرٞۖ وَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ёки сиз улардан харж (яъни, Қуръон оятларини келтирганингиз эвазига ҳақ) сўрамоқдамисиз? (Йўқ, сиз ҳаргиз улардан харж сўрамассиз, чунки) Парвардигорингизнинг харжи — берадиган ажри яхшироқдир. У энг яхши ризқ бергувчидир'}, {'chapter': 23, 'verse': 73, 'text': 'وَإِنَّكَ لَتَدۡعُوهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nШак-шубҳасиз, сиз уларни фақат Тўғри Йўлга даъват қилурсиз'}, {'chapter': 23, 'verse': 74, 'text': 'وَإِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ عَنِ ٱلصِّرَٰطِ لَنَٰكِبُونَ\nАлбатта Охиратга иймон келтирмайдиган кимсалар ушбу йўлдан озгувчидирлар'}, {'chapter': 23, 'verse': 75, 'text': '۞وَلَوۡ رَحِمۡنَٰهُمۡ وَكَشَفۡنَا مَا بِهِم مِّن ضُرّٖ لَّلَجُّواْ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ\nАгар Биз уларга раҳм-шафқат қилсак ва улар билан бўлган нарсани (қаҳатчиликни) аритсак, шак-шубҳасиз, улар яна ўз туғёнларида адашиб-улоқиб юришга қайтурлар'}, {'chapter': 23, 'verse': 76, 'text': 'وَلَقَدۡ أَخَذۡنَٰهُم بِٱلۡعَذَابِ فَمَا ٱسۡتَكَانُواْ لِرَبِّهِمۡ وَمَا يَتَضَرَّعُونَ\nМана Биз уларни азоб-очарчилик билан ушладик. Улар эса на Парвардигорга бўйин эгдилар ва на тавба-тазарруъ қилдилар'}, {'chapter': 23, 'verse': 77, 'text': 'حَتَّىٰٓ إِذَا فَتَحۡنَا عَلَيۡهِم بَابٗا ذَا عَذَابٖ شَدِيدٍ إِذَا هُمۡ فِيهِ مُبۡلِسُونَ\nТо қачон Биз уларга қаттиқ азоб-қаҳатчилик дарвозасини очиб қўйганимизда, эса баногоҳ улар ноумид бўлгувчидирлар'}, {'chapter': 23, 'verse': 78, 'text': 'وَهُوَ ٱلَّذِيٓ أَنشَأَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ\n(Аллоҳ) сизлар учун қулоқ(лар)ни, кўзларни ва дилларни пайдо қилган Зотдир. Сизлар эса камдан-кам шукр қилурсизлар'}, {'chapter': 23, 'verse': 79, 'text': 'وَهُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ\nУ сизларни Ер юзида яратиб — (таратган) Зотдир. Ва сизлар (Қиёмат Кунида) Унинг ҳузурига тўпланурсизлар'}, {'chapter': 23, 'verse': 80, 'text': 'وَهُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ وَلَهُ ٱخۡتِلَٰفُ ٱلَّيۡلِ وَٱلنَّهَارِۚ أَفَلَا تَعۡقِلُونَ\nУ ҳаёт ва ўлим берадиган Зотдир. Кеча ва кундузнинг ўзгариб туриши ҳам Унинг (измидадир). Ахир ақл юргизмайсизларми'}, {'chapter': 23, 'verse': 81, 'text': 'بَلۡ قَالُواْ مِثۡلَ مَا قَالَ ٱلۡأَوَّلُونَ\nЙўқ, улар ҳам худди аввалгилар айтган сўзларни айтдилар'}, {'chapter': 23, 'verse': 82, 'text': 'قَالُوٓاْ أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ\nУлар дедилар: «Бизлар ўлиб тупроқ ва суякларга айланган вақтимизда яна қайта тирилгувчимизми'}, {'chapter': 23, 'verse': 83, 'text': 'لَقَدۡ وُعِدۡنَا نَحۡنُ وَءَابَآؤُنَا هَٰذَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nДарвоқеъ, бизларга ҳам, илгари ота-боболаримизга ҳам мана шу ваъда қилинган. Бу фақат аввалгиларнинг афсоналаридир»'}, {'chapter': 23, 'verse': 84, 'text': 'قُل لِّمَنِ ٱلۡأَرۡضُ وَمَن فِيهَآ إِن كُنتُمۡ تَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Агар билгувчи бўлсанглар (айтинглар-чи), бу Ер ва ундаги бор жонзот кимники?»'}, {'chapter': 23, 'verse': 85, 'text': 'سَيَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَذَكَّرُونَ\nУлар: «Аллоҳникидир», дерлар. Айтинг: «Бас (шундан) ибрат-эслатма олмайсизларми?!»'}, {'chapter': 23, 'verse': 86, 'text': 'قُلۡ مَن رَّبُّ ٱلسَّمَٰوَٰتِ ٱلسَّبۡعِ وَرَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ\nАйтинг: «Етти осмоннинг хожаси ва улуғ аршнинг соҳиби кимдир?»'}, {'chapter': 23, 'verse': 87, 'text': 'سَيَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَتَّقُونَ\nУлар: «(Буларнинг барчаси) Аллоҳникидир», дерлар. Айтинг: «Ахир қўрқмайсизларми?!»'}, {'chapter': 23, 'verse': 88, 'text': 'قُلۡ مَنۢ بِيَدِهِۦ مَلَكُوتُ كُلِّ شَيۡءٖ وَهُوَ يُجِيرُ وَلَا يُجَارُ عَلَيۡهِ إِن كُنتُمۡ تَعۡلَمُونَ\nАйтинг: «Агар билсанглар (айтинглар-чи), «Барча нарсанинг подшоҳлиги қўлида бўлган, Ўзи (барчага) Ҳомийлик қиладиган, Унга қарши биров ҳомийлик қила олмайдиган зот кимдир?»'}, {'chapter': 23, 'verse': 89, 'text': 'سَيَقُولُونَ لِلَّهِۚ قُلۡ فَأَنَّىٰ تُسۡحَرُونَ\nУлар: «Бундай подшоҳлик ва ҳомийлик ёлғиз Аллоҳникидир», дерлар. Айтинг: «Бас, қандай алданмоқдадирсизлар?!»'}, {'chapter': 23, 'verse': 90, 'text': 'بَلۡ أَتَيۡنَٰهُم بِٱلۡحَقِّ وَإِنَّهُمۡ لَكَٰذِبُونَ\nЙўқ! Биз уларга (қайта тирилишлари ҳақидаги) Ҳақиқатни келтирдик. Улар эса шак-шубҳасиз, ёлғончидирлар'}, {'chapter': 23, 'verse': 91, 'text': 'مَا ٱتَّخَذَ ٱللَّهُ مِن وَلَدٖ وَمَا كَانَ مَعَهُۥ مِنۡ إِلَٰهٍۚ إِذٗا لَّذَهَبَ كُلُّ إِلَٰهِۭ بِمَا خَلَقَ وَلَعَلَا بَعۡضُهُمۡ عَلَىٰ بَعۡضٖۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ\nАллоҳнинг боласи йўқдир ва У зот билан бирга бирон илоҳ бўлган эмасдир. Акс ҳолда албатта ҳар бир илоҳ ўзи яратган нарса билан кетиб, бир-бирларидан устун бўлиб олур эдилар, (яъни, ҳар бир «илоҳ» ўз ҳукмини ўтказмоқни истаб, натижада Еру осмон бузилиб кетган бўлур эди). Аллоҳ улар айтаётган шериклардан покдир'}, {'chapter': 23, 'verse': 92, 'text': 'عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУ ғайбу-шаҳодатни (яъни, йўғу-борни) билгувчидир. Бас, у (мушрикларнинг) ширкларидан юксакдир'}, {'chapter': 23, 'verse': 93, 'text': 'قُل رَّبِّ إِمَّا تُرِيَنِّي مَا يُوعَدُونَ\nАйтинг: «Парвардигорим, агар Сен менга (кофирларга) ваъда қилинаётган (азоб)ни (уларнинг устига тушганини) кўргузадиган бўлсанг'}, {'chapter': 23, 'verse': 94, 'text': 'رَبِّ فَلَا تَجۡعَلۡنِي فِي ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nПарвардигорим, (ўша соатда) мени у золим қавм билан бирга қилмагин»'}, {'chapter': 23, 'verse': 95, 'text': 'وَإِنَّا عَلَىٰٓ أَن نُّرِيَكَ مَا نَعِدُهُمۡ لَقَٰدِرُونَ\nАлбатта, Биз сизга уларга ваъда қилаётган (азобимизни) кўргизишга қодирдирмиз'}, {'chapter': 23, 'verse': 96, 'text': 'ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ ٱلسَّيِّئَةَۚ نَحۡنُ أَعۡلَمُ بِمَا يَصِفُونَ\nСиз (улар қилаётган) ёмонликни энг гўзал сўзлар билан дафъ қилинг! Биз улар (сизни масхаралаб) айтаётган гапларни жуда яхши билгувчидирмиз'}, {'chapter': 23, 'verse': 97, 'text': 'وَقُل رَّبِّ أَعُوذُ بِكَ مِنۡ هَمَزَٰتِ ٱلشَّيَٰطِينِ\nВа айтинг: «Парвардигорим, мен Сендан шайтонларнинг васвасаларидан паноҳ беришингни сўрайман'}, {'chapter': 23, 'verse': 98, 'text': 'وَأَعُوذُ بِكَ رَبِّ أَن يَحۡضُرُونِ\nЯна мен Сендан (ё) Парвардигорим, улар менинг ҳузуримга келишларидан паноҳ беришингни сўрайман»'}, {'chapter': 23, 'verse': 99, 'text': 'حَتَّىٰٓ إِذَا جَآءَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ رَبِّ ٱرۡجِعُونِ\nТоки қачон улардан (яъни, мушриклардан) бирига ўлим келганида: «Парвардигор, мени (яна ҳаётга) қайтаринглар'}, {'chapter': 23, 'verse': 100, 'text': 'لَعَلِّيٓ أَعۡمَلُ صَٰلِحٗا فِيمَا تَرَكۡتُۚ كَلَّآۚ إِنَّهَا كَلِمَةٌ هُوَ قَآئِلُهَاۖ وَمِن وَرَآئِهِم بَرۡزَخٌ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nШояд, мен қолган умримда яхши амал қилсам», деб қолур. Йўқ! (У асло ҳаётга қайтарилмас). Дарҳақиқат, бу (ҳар бир жон бераётган кофир) айтадиган сўздир. Уларнинг ортида то қайта тириладиган кунларигача (дунёга қайтишларидан тўсиб турадиган) бир тўсиқ бўлур'}, {'chapter': 23, 'verse': 101, 'text': 'فَإِذَا نُفِخَ فِي ٱلصُّورِ فَلَآ أَنسَابَ بَيۡنَهُمۡ يَوۡمَئِذٖ وَلَا يَتَسَآءَلُونَ\nБас, қачон сур чалинганида (яъни, Қиёмат қойим бўлганида) ана у кунда уларнинг ўрталарида ҳеч қандай насл насаб қолмас ва улар бир-бирлари билан савол-жавоб ҳам қила олмаслар'}, {'chapter': 23, 'verse': 102, 'text': 'فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nЭнди кимнинг (яхшилик) мезонлари (ёмонлик-гуноҳларидан) оғир келса бас, ана ўшалар нажот топгувчидирлар'}, {'chapter': 23, 'verse': 103, 'text': 'وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فِي جَهَنَّمَ خَٰلِدُونَ\nКимнинг мезонлари енгил бўлса (яъни, ёмонликлари яхши амалларини босиб кетса), бас, ана ўшалар ўзларига зиён қилибдилар. Улар жаҳаннамда мангу қолгувчидирлар'}, {'chapter': 23, 'verse': 104, 'text': 'تَلۡفَحُ وُجُوهَهُمُ ٱلنَّارُ وَهُمۡ فِيهَا كَٰلِحُونَ\nУларнинг юзларини ўт куйдириб бадбашара бўлиб қолгувчидирлар'}, {'chapter': 23, 'verse': 105, 'text': 'أَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَكُنتُم بِهَا تُكَذِّبُونَ\n(Уларга): «Сизларга менинг оятларим тиловат қилинган эмасмиди, сизлар уларни ёлғон деган эмасмидингизлар?!», (дейилганида)'}, {'chapter': 23, 'verse': 106, 'text': 'قَالُواْ رَبَّنَا غَلَبَتۡ عَلَيۡنَا شِقۡوَتُنَا وَكُنَّا قَوۡمٗا ضَآلِّينَ\nУлар дедилар: «Парвардигоро, бадбахтлигимиз бизлардан ғолиб келиб, адашган қавм бўлиб қолган эканмиз'}, {'chapter': 23, 'verse': 107, 'text': 'رَبَّنَآ أَخۡرِجۡنَا مِنۡهَا فَإِنۡ عُدۡنَا فَإِنَّا ظَٰلِمُونَ\nПарвардигоро, бизларни (жаҳаннамдан ҳаёти дунёга) чиқаргин. Бас, агар яна (туғёнга) қайтсак, у ҳолда шак-шубҳасиз, (ўз жонимизга) жабр қилгувчидирмиз»'}, {'chapter': 23, 'verse': 108, 'text': 'قَالَ ٱخۡسَـُٔواْ فِيهَا وَلَا تُكَلِّمُونِ\n(Аллоҳ) айтди: «(Жаҳаннамда) хор бўлингиз ва Менга сўз қотмангиз'}, {'chapter': 23, 'verse': 109, 'text': 'إِنَّهُۥ كَانَ فَرِيقٞ مِّنۡ عِبَادِي يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَا وَأَنتَ خَيۡرُ ٱلرَّـٰحِمِينَ\nАниқки, Менинг бандаларимдан бир гуруҳ бор эди. Улар: «Парвардигоро, (Ўзингга) иймон келтирдик. Бас, Сен бизларни мағфират қилгин ва бизларга раҳм айла: Сен Ўзинг раҳм қилгувчиларнинг яхшироғидирсан», дер эдилар'}, {'chapter': 23, 'verse': 110, 'text': 'فَٱتَّخَذۡتُمُوهُمۡ سِخۡرِيًّا حَتَّىٰٓ أَنسَوۡكُمۡ ذِكۡرِي وَكُنتُم مِّنۡهُمۡ تَضۡحَكُونَ\nБас, сизлар уларни масхара қилдингиз, ҳатто улар(нинг устидан кулиш) сизларга Мени эслашни унутдирди. Сизлар улардан кулган эдингиз'}, {'chapter': 23, 'verse': 111, 'text': 'إِنِّي جَزَيۡتُهُمُ ٱلۡيَوۡمَ بِمَا صَبَرُوٓاْ أَنَّهُمۡ هُمُ ٱلۡفَآئِزُونَ\nМен бугун уларни (сизларнинг озор-азиятларингизга) сабр-тоқат қилганлари сабабли мукофотладим — улар ҳақиқий (бахт-саодатга) эришгувчидирлар»'}, {'chapter': 23, 'verse': 112, 'text': 'قَٰلَ كَمۡ لَبِثۡتُمۡ فِي ٱلۡأَرۡضِ عَدَدَ سِنِينَ\n«Ерда қанча йил турдинглар?», деди (Аллоҳ)'}, {'chapter': 23, 'verse': 113, 'text': 'قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖ فَسۡـَٔلِ ٱلۡعَآدِّينَ\nУлар айтдилар: «Бир кун ё ярим кун. Санаб тургувчи (фаришталардан) сўрагин»'}, {'chapter': 23, 'verse': 114, 'text': 'قَٰلَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗاۖ لَّوۡ أَنَّكُمۡ كُنتُمۡ تَعۡلَمُونَ\n(Аллоҳ) деди: «Агар сизлар биладиган бўлсангизлар (дунёда) жуда оз турдингизлар (яъни, сизлар абадий деб ўйлаган дунё аслида жуда оз муддатдир)'}, {'chapter': 23, 'verse': 115, 'text': 'أَفَحَسِبۡتُمۡ أَنَّمَا خَلَقۡنَٰكُمۡ عَبَثٗا وَأَنَّكُمۡ إِلَيۡنَا لَا تُرۡجَعُونَ\nЁки сизларнинг гумонингизча, Биз сизларни беҳуда (яъни, дунёда сизларга бирон вазифа бермайдиган, Охиратда ҳисоб-китоб қилмайдиган ҳолда) яратдигу, сизлар Бизнинг ҳузуримизга қайтарилмайсизларми?!» (Ундоқ эмас)'}, {'chapter': 23, 'verse': 116, 'text': 'فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۖ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡكَرِيمِ\nЗотан Ҳақ Подшоҳ — Аллоҳ (бирон ишни беҳуда қилишдан) юксакдир. Ҳеч қандай илоҳ йўқ, магар У — улуғ арш Соҳиби бордир'}, {'chapter': 23, 'verse': 117, 'text': 'وَمَن يَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ لَا بُرۡهَٰنَ لَهُۥ بِهِۦ فَإِنَّمَا حِسَابُهُۥ عِندَ رَبِّهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ\nКим ўзи учун ҳеч қандай ҳужжат бўлмаган ҳолда Аллоҳ билан бирга бошқа бирон илоҳга илтижо қилса, бас, унинг ҳисоб-китоби Парвардигорининг ҳузурида бўлур. Албатта кофир бўлган кимсалар нажот топмаслар'}, {'chapter': 23, 'verse': 118, 'text': 'وَقُل رَّبِّ ٱغۡفِرۡ وَٱرۡحَمۡ وَأَنتَ خَيۡرُ ٱلرَّـٰحِمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Парвардигорим, Ўзинг (гуноҳларимизни) мағфират қилгин ва (ҳолимизга) раҳм айла. Сен Ўзинг раҳм қилгувчиларнинг яхшироғидирсан»'}, {'chapter': 24, 'verse': 1, 'text': 'سُورَةٌ أَنزَلۡنَٰهَا وَفَرَضۡنَٰهَا وَأَنزَلۡنَا فِيهَآ ءَايَٰتِۭ بَيِّنَٰتٖ لَّعَلَّكُمۡ تَذَكَّرُونَ\n(Ушбу) Биз нозил қилган ва (ундаги ҳукмларни мусулмонлар зиммасига) фарз қилган бир сурадир. Сизлар эслатма-ибрат олишингиз учун Биз унда очиқ-равшан оятларни нозил қилдик'}, {'chapter': 24, 'verse': 2, 'text': 'ٱلزَّانِيَةُ وَٱلزَّانِي فَٱجۡلِدُواْ كُلَّ وَٰحِدٖ مِّنۡهُمَا مِاْئَةَ جَلۡدَةٖۖ وَلَا تَأۡخُذۡكُم بِهِمَا رَأۡفَةٞ فِي دِينِ ٱللَّهِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ وَلۡيَشۡهَدۡ عَذَابَهُمَا طَآئِفَةٞ مِّنَ ٱلۡمُؤۡمِنِينَ\nЗинокор аёл ва зинокор эркак — улардан ҳар бирини юз даррадан уринглар. Агар сизлар Аллоҳга ва Охират кунига иймон келтиргувчи бўлсангизлар Аллоҳнинг (бу) ҳукмида (яъни, зинокорларни дарралашда) сизларни уларга нисбатан раҳм-шафқат (туйғулари) тутмасин! Уларнинг азобланишига бир тоифа мўминлар гувоҳ бўлсинлар'}, {'chapter': 24, 'verse': 3, 'text': 'ٱلزَّانِي لَا يَنكِحُ إِلَّا زَانِيَةً أَوۡ مُشۡرِكَةٗ وَٱلزَّانِيَةُ لَا يَنكِحُهَآ إِلَّا زَانٍ أَوۡ مُشۡرِكٞۚ وَحُرِّمَ ذَٰلِكَ عَلَى ٱلۡمُؤۡمِنِينَ\nЗинокор эркак фақат зинокор аёлга ёки мушрикага уйланур. Зинокор аёлга фақат зинокор эркак ёки мушрик уйланур. Ва бу (яъни, зинокор аёлларга уйланиш) мўминларга ҳаром қилингандир'}, {'chapter': 24, 'verse': 4, 'text': 'وَٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ثُمَّ لَمۡ يَأۡتُواْ بِأَرۡبَعَةِ شُهَدَآءَ فَٱجۡلِدُوهُمۡ ثَمَٰنِينَ جَلۡدَةٗ وَلَا تَقۡبَلُواْ لَهُمۡ شَهَٰدَةً أَبَدٗاۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nПокиза аёлларни (зинокор) деб бадном қилиб, сўнгра (бу даъволарига) тўртта гувоҳ келтира олмаган кимсалар(ни) уларни саксон дарра уринглар ва ҳеч қачон уларнинг гувоҳликларини қабул қилманглар! Улар фосиқ-итоатсиз кимсалардир'}, {'chapter': 24, 'verse': 5, 'text': 'إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nМагар шу (қилмишларидан) сўнг тавба қилиб, (ўзларини) тузатган кишиларгина (фосиқ эмасдирлар). Зеро, Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 24, 'verse': 6, 'text': 'وَٱلَّذِينَ يَرۡمُونَ أَزۡوَٰجَهُمۡ وَلَمۡ يَكُن لَّهُمۡ شُهَدَآءُ إِلَّآ أَنفُسُهُمۡ فَشَهَٰدَةُ أَحَدِهِمۡ أَرۡبَعُ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِينَ\nЎзларидан бошқа гувоҳлари бўлмаган ҳолида ўз хотинларини (бузуқ деб) бадном қиладиган кимсалар — уларнинг ҳар бири ўзини шак-шубҳасиз, ростгўйлардан деб, Аллоҳ номига тўрт марта гувоҳлик бериши (вожибдир)'}, {'chapter': 24, 'verse': 7, 'text': 'وَٱلۡخَٰمِسَةُ أَنَّ لَعۡنَتَ ٱللَّهِ عَلَيۡهِ إِن كَانَ مِنَ ٱلۡكَٰذِبِينَ\n(У эрнинг) бешинчи (гувоҳлиги эса) агар ёлғончилардан бўлса, устига Аллоҳнинг лаънати (тушишини сўрамоқлигидир)'}, {'chapter': 24, 'verse': 8, 'text': 'وَيَدۡرَؤُاْ عَنۡهَا ٱلۡعَذَابَ أَن تَشۡهَدَ أَرۡبَعَ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلۡكَٰذِبِينَ\nУ (аёлдан) азоб-жазони «У (яъни, эри) шак-шубҳасиз, ёлғончилардан», деб Аллоҳ номига тўрт марта гувоҳлик бериши дафъ қилур'}, {'chapter': 24, 'verse': 9, 'text': 'وَٱلۡخَٰمِسَةَ أَنَّ غَضَبَ ٱللَّهِ عَلَيۡهَآ إِن كَانَ مِنَ ٱلصَّـٰدِقِينَ\n(У аёлнинг) бешинчи (гувоҳлиги эса агар эри) ростгўйлардан бўлса, ўзининг устига Аллоҳнинг ғазаби тушишини сўрамоқлигидир'}, {'chapter': 24, 'verse': 10, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ تَوَّابٌ حَكِيمٌ\nАгар сизларга Аллоҳнинг фазлу-марҳамати бўлмаса ва Аллоҳ тавбаларни қабул қилгувчи, ҳикмат соҳиби бўлмаса эди (албатта ё эр, ё хотин шарманда қилинган бўлур эди)'}, {'chapter': 24, 'verse': 11, 'text': 'إِنَّ ٱلَّذِينَ جَآءُو بِٱلۡإِفۡكِ عُصۡبَةٞ مِّنكُمۡۚ لَا تَحۡسَبُوهُ شَرّٗا لَّكُمۖ بَلۡ هُوَ خَيۡرٞ لَّكُمۡۚ لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُم مَّا ٱكۡتَسَبَ مِنَ ٱلۡإِثۡمِۚ وَٱلَّذِي تَوَلَّىٰ كِبۡرَهُۥ مِنۡهُمۡ لَهُۥ عَذَابٌ عَظِيمٞ\nШак-шубҳасиз, бу бўҳтонни (вужудга) келтирган кимсалар ўзларингиздан бўлган бир тўдадир. Уни сизлар ўзларингиз учун ёмонлик деб ўйламанглар, балки у сизлар учун яхшиликдир Улардан (яъни, бўҳтончилардан) ҳар бир киши учун ўзи касб қилган гуноҳ(га яраша жазо) бордир. Уларнинг орасидаги (гуноҳнинг) каттасини кўтарган кимса учун улуғ азоб бордир'}, {'chapter': 24, 'verse': 12, 'text': 'لَّوۡلَآ إِذۡ سَمِعۡتُمُوهُ ظَنَّ ٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بِأَنفُسِهِمۡ خَيۡرٗا وَقَالُواْ هَٰذَآ إِفۡكٞ مُّبِينٞ\n(Эй мўминлар), сизлар (бу бўҳтонни) эшитган пайтингизда, мўмин ва мўминалар бир-бирлари ҳақида яхшиликни ўйлаб: «Бу очиқ бўҳтон-ку!», десалар бўлмасмиди'}, {'chapter': 24, 'verse': 13, 'text': 'لَّوۡلَا جَآءُو عَلَيۡهِ بِأَرۡبَعَةِ شُهَدَآءَۚ فَإِذۡ لَمۡ يَأۡتُواْ بِٱلشُّهَدَآءِ فَأُوْلَـٰٓئِكَ عِندَ ٱللَّهِ هُمُ ٱلۡكَٰذِبُونَ\nУлар (яъни, Оишани бандом қилмоқчи бўлганлар ўз даъволарига) тўртта гувоҳ келтирсалар бўлмасмиди?! Энди агар гувоҳ келтира олмасалар, у ҳолда Аллоҳ наздида улар ёлғончидирлар'}, {'chapter': 24, 'verse': 14, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ لَمَسَّكُمۡ فِي مَآ أَفَضۡتُمۡ فِيهِ عَذَابٌ عَظِيمٌ\nАгар сизларга дунё ва Охиратда Аллоҳнинг фазлу-марҳамати бўлмаса эди, албатта сизларни тинмай сўзлаган нарса — бўҳтонларингиз сабабли улуғ азоб ушлаган бўлур эди'}, {'chapter': 24, 'verse': 15, 'text': 'إِذۡ تَلَقَّوۡنَهُۥ بِأَلۡسِنَتِكُمۡ وَتَقُولُونَ بِأَفۡوَاهِكُم مَّا لَيۡسَ لَكُم بِهِۦ عِلۡمٞ وَتَحۡسَبُونَهُۥ هَيِّنٗا وَهُوَ عِندَ ٱللَّهِ عَظِيمٞ\nЎшанда сизлар уни тилдан тилга олиб, оғизларингиз билан ўзларингиз аниқ билмаган нарсани сўйлар ва буни енгил иш деб уйлар эдингизлар. Ҳолбуки, у Аллоҳ наздида улуғ (гуноҳдир)'}, {'chapter': 24, 'verse': 16, 'text': 'وَلَوۡلَآ إِذۡ سَمِعۡتُمُوهُ قُلۡتُم مَّا يَكُونُ لَنَآ أَن نَّتَكَلَّمَ بِهَٰذَا سُبۡحَٰنَكَ هَٰذَا بُهۡتَٰنٌ عَظِيمٞ\nУни эшитган пайтингизда: «Бу (миш-мишни) сўзлаш биз учун жоиз эмасдир. Эй пок Парвардигор, бу улуғ бўҳтон-ку!», десангизлар бўлмасмиди'}, {'chapter': 24, 'verse': 17, 'text': 'يَعِظُكُمُ ٱللَّهُ أَن تَعُودُواْ لِمِثۡلِهِۦٓ أَبَدًا إِن كُنتُم مُّؤۡمِنِينَ\nАгар мўмин бўлсангизлар, ҳаргиз унга ўхшаган нарсаларга қайтмасликларингизни Аллоҳ сизларга панд-насиҳат қилур'}, {'chapter': 24, 'verse': 18, 'text': 'وَيُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌ\nВа Аллоҳ сизларга Ўз оятларини баён қилур. Аллоҳ илм ва ҳикмат Соҳибидир'}, {'chapter': 24, 'verse': 19, 'text': 'إِنَّ ٱلَّذِينَ يُحِبُّونَ أَن تَشِيعَ ٱلۡفَٰحِشَةُ فِي ٱلَّذِينَ ءَامَنُواْ لَهُمۡ عَذَابٌ أَلِيمٞ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ\nАлбатта иймон келтирган кишилар ўртасида бузуқликлар ёйилишини истайдиган кимсалар учун дунёда ҳам, Охиратда ҳам аламли азоб бордир. Аллоҳ билур, сизлар билмассиз'}, {'chapter': 24, 'verse': 20, 'text': 'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ رَءُوفٞ رَّحِيمٞ\nАгар сизларга Аллоҳнинг фазлу-марҳамати ва Аллоҳнинг меҳрибон ва раҳмли экани бўлмаса эди (албатта, сизларга бу қилмишларингиз учун азобни нақд қилган бўлур эди)'}, {'chapter': 24, 'verse': 21, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ وَمَن يَتَّبِعۡ خُطُوَٰتِ ٱلشَّيۡطَٰنِ فَإِنَّهُۥ يَأۡمُرُ بِٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۚ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ مَا زَكَىٰ مِنكُم مِّنۡ أَحَدٍ أَبَدٗا وَلَٰكِنَّ ٱللَّهَ يُزَكِّي مَن يَشَآءُۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nЭй мўминлар, шайтоннинг изидан эргашманглар! Ким шайтоннинг изидан эргашса, бас, албатта (шайтон) бузуқлик ва ёмонликка буюрур. Агар сизларга Аллоҳнинг фазлу-марҳамати бўлмаса эди, сизлардан бирон киши (бирон гуноҳдан) пок бўлмас эди. Лекин Аллоҳ (фазлу-марҳамати билан) Ўзи хоҳлаган кишини поклар. Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 24, 'verse': 22, 'text': 'وَلَا يَأۡتَلِ أُوْلُواْ ٱلۡفَضۡلِ مِنكُمۡ وَٱلسَّعَةِ أَن يُؤۡتُوٓاْ أُوْلِي ٱلۡقُرۡبَىٰ وَٱلۡمَسَٰكِينَ وَٱلۡمُهَٰجِرِينَ فِي سَبِيلِ ٱللَّهِۖ وَلۡيَعۡفُواْ وَلۡيَصۡفَحُوٓاْۗ أَلَا تُحِبُّونَ أَن يَغۡفِرَ ٱللَّهُ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٌ\nСизлардан фазл ва кенг-катта мол-давлат эгалари қариндошларга, мискинларга ва муҳожирларга Аллоҳ йўлида инфоқ-эҳсон қилмасликка қасам ичмасин, балки уларни афв қилиб, кечирсинлар! Аллоҳ сизларни мағфират қилишини истамайсизларми?! Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 24, 'verse': 23, 'text': 'إِنَّ ٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ٱلۡغَٰفِلَٰتِ ٱلۡمُؤۡمِنَٰتِ لُعِنُواْ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَلَهُمۡ عَذَابٌ عَظِيمٞ\nАлбатта (бузуқ ниятлардан) бехабар, покиза, мўмина аёлларни бадном қиладиган кимсалар дунёда ҳам, Охиратда ҳам лаънатга дучор бўлдилар. Улар учун улуғ азоб бордир'}, {'chapter': 24, 'verse': 24, 'text': 'يَوۡمَ تَشۡهَدُ عَلَيۡهِمۡ أَلۡسِنَتُهُمۡ وَأَيۡدِيهِمۡ وَأَرۡجُلُهُم بِمَا كَانُواْ يَعۡمَلُونَ\nУ кунда — Қиёматда қилиб ўтган (бўҳтонлари) сабабли уларнинг тиллари ҳам, қўл ва оёқлари ҳам ўзларининг зиёнларига гувоҳлик берур'}, {'chapter': 24, 'verse': 25, 'text': 'يَوۡمَئِذٖ يُوَفِّيهِمُ ٱللَّهُ دِينَهُمُ ٱلۡحَقَّ وَيَعۡلَمُونَ أَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ ٱلۡمُبِينُ\nЎша кунда Аллоҳ уларнинг ҳақиқий жазоларини комил қилиб берур ва улар Аллоҳнинг аниқ-ҳақ эканини билурлар'}, {'chapter': 24, 'verse': 26, 'text': 'ٱلۡخَبِيثَٰتُ لِلۡخَبِيثِينَ وَٱلۡخَبِيثُونَ لِلۡخَبِيثَٰتِۖ وَٱلطَّيِّبَٰتُ لِلطَّيِّبِينَ وَٱلطَّيِّبُونَ لِلطَّيِّبَٰتِۚ أُوْلَـٰٓئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَۖ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nНопок аёллар нопок эрлар учундир (яъни, лойиқдир), нопок эрлар нопок аёллар учундир. Покиза аёллар покиза эрлар учундир, покиза эрлар покиза аёллар учундир. Ана ўша (покиза эрлар ва покиза аёллар) уларнинг (яъни, нопок эрлар ва нопок аёлларнинг) айтаётган бўҳтонларидан покдирлар. Улар учун мағфират ва улуғ ризқ (яъни, жаннат неъматлари) бордир'}, {'chapter': 24, 'verse': 27, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتًا غَيۡرَ بُيُوتِكُمۡ حَتَّىٰ تَسۡتَأۡنِسُواْ وَتُسَلِّمُواْ عَلَىٰٓ أَهۡلِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ\nЭй мўминлар, ўз уйларингиздан бошқа уйларга то изн сўрамагунингизча ва эгаларига салом бермагунингизгача кирмангиз. Мана шу сизлар учун яхшироқдир. Шояд эслатма-ибрат олсангизлар'}, {'chapter': 24, 'verse': 28, 'text': 'فَإِن لَّمۡ تَجِدُواْ فِيهَآ أَحَدٗا فَلَا تَدۡخُلُوهَا حَتَّىٰ يُؤۡذَنَ لَكُمۡۖ وَإِن قِيلَ لَكُمُ ٱرۡجِعُواْ فَٱرۡجِعُواْۖ هُوَ أَزۡكَىٰ لَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ\nЭнди агар у (уйларда) ҳеч кимни топмасангизлар, у ҳолда то сизларга изн берилмагунча, уларга кирмангиз! Агар сизларга «қайтинглар», дейилса (яъни, киришга изн берилмаса) қайтиб кетинглар! Шу сизлар учун энг тоза (йўлдир). Аллоҳ қилаётган амалларингизни билгувчидир'}, {'chapter': 24, 'verse': 29, 'text': 'لَّيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَدۡخُلُواْ بُيُوتًا غَيۡرَ مَسۡكُونَةٖ فِيهَا مَتَٰعٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ\n(Биров учун) маскан бўлмаган (меҳмонхона, карвонсарой каби) сизлар фойдаланадиган уйларга (изн сўрамасдан) киришларингизда сизларга гуноҳ йўқдир. Аллоҳ ошкор қилаётган нарсангизни ҳам, яшираётган нарсангизни ҳам билур'}, {'chapter': 24, 'verse': 30, 'text': 'قُل لِّلۡمُؤۡمِنِينَ يَغُضُّواْ مِنۡ أَبۡصَٰرِهِمۡ وَيَحۡفَظُواْ فُرُوجَهُمۡۚ ذَٰلِكَ أَزۡكَىٰ لَهُمۡۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا يَصۡنَعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), мўминларга айтинг, кўзларини (номаҳрам аёлларга тикишдан) тўссинлар ва авратларини (ҳаромдан) сақласинлар! Мана шу улар учун энг тоза (йўлдир). Албатта Аллоҳ улар қилаётган ҳунарлардан хабардордир'}, {'chapter': 24, 'verse': 31, 'text': 'وَقُل لِّلۡمُؤۡمِنَٰتِ يَغۡضُضۡنَ مِنۡ أَبۡصَٰرِهِنَّ وَيَحۡفَظۡنَ فُرُوجَهُنَّ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنۡهَاۖ وَلۡيَضۡرِبۡنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّۖ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوۡ ءَابَآئِهِنَّ أَوۡ ءَابَآءِ بُعُولَتِهِنَّ أَوۡ أَبۡنَآئِهِنَّ أَوۡ أَبۡنَآءِ بُعُولَتِهِنَّ أَوۡ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ أَخَوَٰتِهِنَّ أَوۡ نِسَآئِهِنَّ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُنَّ أَوِ ٱلتَّـٰبِعِينَ غَيۡرِ أُوْلِي ٱلۡإِرۡبَةِ مِنَ ٱلرِّجَالِ أَوِ ٱلطِّفۡلِ ٱلَّذِينَ لَمۡ يَظۡهَرُواْ عَلَىٰ عَوۡرَٰتِ ٱلنِّسَآءِۖ وَلَا يَضۡرِبۡنَ بِأَرۡجُلِهِنَّ لِيُعۡلَمَ مَا يُخۡفِينَ مِن زِينَتِهِنَّۚ وَتُوبُوٓاْ إِلَى ٱللَّهِ جَمِيعًا أَيُّهَ ٱلۡمُؤۡمِنُونَ لَعَلَّكُمۡ تُفۡلِحُونَ\nМўминаларга ҳам айтинг, кўзларини (номаҳрам эркакларга тикишдан) тўссинлар ва авратларини (ҳаромдан) сақласинлар! Ҳамда кўриниб тургандан бошқа зеб-зийнатларини (яъни, устларидаги либосларидан бошқа зеб-зийнатларини номаҳрамларга) кўрсатмасинлар ва кўкракларини рўмоллари билан тўссинлар! Улар зеб-зийнатларини кўрсатмасинлар, магар эрларига ё оталарига ё эрларининг оталарига ё ўғилларига ё эрларининг ўғилларига ё ўзларининг оға-иниларига ё оға-иниларининг ўғилларига ё опа-сингилларининг ўғилларига ё ўзлари (каби) аёлларга ё қўл остиларидаги чўриларга ё (аёллардан) беҳожат бўлган (яъни, жуда кексайиб қолган ёки ақлсиз-девона) эркак хизматкор-қулларга ё аёлларнинг авратларидан хабардор бўлмаган гўдакларгагина (кўрсатишлари жоиздир). Яна яширган зеб-зийнатлар билинсин учун оёқларини (ерга) урмасинлар! Барчаларингиз Аллоҳга тавба қилинглар, эй мўминлар! Шоядки (шунда) нажот топсангизлар'}, {'chapter': 24, 'verse': 32, 'text': 'وَأَنكِحُواْ ٱلۡأَيَٰمَىٰ مِنكُمۡ وَٱلصَّـٰلِحِينَ مِنۡ عِبَادِكُمۡ وَإِمَآئِكُمۡۚ إِن يَكُونُواْ فُقَرَآءَ يُغۡنِهِمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ\nЎз ораларингиздаги тул-беваларни ҳамда қул ва чўриларингизни яхшиларини уйлантиринглар. Агар улар камбағал бўлсалар, Аллоҳ уларни Ўз фазлу-карами билан бой-беҳожат қилур. Аллоҳ (фазлу-карами) кенг, билгувчидир'}, {'chapter': 24, 'verse': 33, 'text': 'وَلۡيَسۡتَعۡفِفِ ٱلَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغۡنِيَهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱلَّذِينَ يَبۡتَغُونَ ٱلۡكِتَٰبَ مِمَّا مَلَكَتۡ أَيۡمَٰنُكُمۡ فَكَاتِبُوهُمۡ إِنۡ عَلِمۡتُمۡ فِيهِمۡ خَيۡرٗاۖ وَءَاتُوهُم مِّن مَّالِ ٱللَّهِ ٱلَّذِيٓ ءَاتَىٰكُمۡۚ وَلَا تُكۡرِهُواْ فَتَيَٰتِكُمۡ عَلَى ٱلۡبِغَآءِ إِنۡ أَرَدۡنَ تَحَصُّنٗا لِّتَبۡتَغُواْ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَن يُكۡرِههُّنَّ فَإِنَّ ٱللَّهَ مِنۢ بَعۡدِ إِكۡرَٰهِهِنَّ غَفُورٞ رَّحِيمٞ\nНикоҳ (учун лозим бўлган мол-давлатни) топа олмаган кишилар то Аллоҳ уларни Ўз фазлу-карами билан бой-бадавлат қилгунича ўзларини (ҳаромдан, зинокорликдан) пок тутсинлар!! (Эй мўминлар), қўл остингиздаги қуллардан битим тузишни истайдиган кишилар бўлса, у ҳолда агар сизлар уларда яхшиликни (яъни, диёнат, иймонни) билсангизлар, улар билан битим тузинглар ва уларга Аллоҳнинг сизларга ато этган молидан ато этинглар! Покликни истаган чўриларингизни ҳаёти дунё нарсаларини истаб зинокорлик қилишга мажбурламанглар. Энди ким уларни мажбур этса, бас, албатта Аллоҳ мажбурлаганларидан сўнг (у чўриларни) мағфират қилгувчи ва (уларга) раҳм-шафқат қилгувчидир'}, {'chapter': 24, 'verse': 34, 'text': 'وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ ءَايَٰتٖ مُّبَيِّنَٰتٖ وَمَثَلٗا مِّنَ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۡ وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ\n(Эй мўминлар), дарҳақиқат, Биз сизларга очиқ-равшан оятларни, сизлардан илгари ўтган зотлар тўғрисидаги мисолларни ва тақводор кишилар учун панд-нисаҳатни нозил қилдик'}, {'chapter': 24, 'verse': 35, 'text': '۞ٱللَّهُ نُورُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ مَثَلُ نُورِهِۦ كَمِشۡكَوٰةٖ فِيهَا مِصۡبَاحٌۖ ٱلۡمِصۡبَاحُ فِي زُجَاجَةٍۖ ٱلزُّجَاجَةُ كَأَنَّهَا كَوۡكَبٞ دُرِّيّٞ يُوقَدُ مِن شَجَرَةٖ مُّبَٰرَكَةٖ زَيۡتُونَةٖ لَّا شَرۡقِيَّةٖ وَلَا غَرۡبِيَّةٖ يَكَادُ زَيۡتُهَا يُضِيٓءُ وَلَوۡ لَمۡ تَمۡسَسۡهُ نَارٞۚ نُّورٌ عَلَىٰ نُورٖۚ يَهۡدِي ٱللَّهُ لِنُورِهِۦ مَن يَشَآءُۚ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَٰلَ لِلنَّاسِۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\nАллоҳ осмонлар ва Ернинг нуридир (яъни, уларнинг барчасини ёритиб, кўзга кўрсатиб — йўқдан бор қилиб тургувчидир). У зот нурининг (мўмин банда қалбидаги) мисоли худди бир токча, унинг устида бир чироқ, бу чироқ бир шиша ичида, у шиша гўё бир дур юлдузга ўхшайди. У (чироқ) на шарқий ва на ғарбий бўлмаган (балки заминнинг қоқ марказидаги) муборак зайтун дарахтининг (мойидан) ёқилур. Унинг мойи (тиниқ, мусаффолигидан) гарчи унга олов тегмаса-да, (атрофни) ёритиб юборгудек. (Олов теккач эса) нур устига нур (бўлур). Аллоҳ Ўзининг (бу нурига) Ўзи хоҳлаган кишиларни ҳидоят қилур. Аллоҳ одамлар (ибрат олишлари) учун (мана шундай) мисоллар келтирур. Аллоҳ барча нарсани билгувчидир'}, {'chapter': 24, 'verse': 36, 'text': 'فِي بُيُوتٍ أَذِنَ ٱللَّهُ أَن تُرۡفَعَ وَيُذۡكَرَ فِيهَا ٱسۡمُهُۥ يُسَبِّحُ لَهُۥ فِيهَا بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ\n(У чироқ) бир уйларда (яъни, масжидларда ёқилурки), Аллоҳ уларни баланд кўтариб (бино) қилинишига ва уларда Ўзининг номи зикр қилинишига изн берган (яъни, амр қилган) эди. У (масжидларда) эрта-ю кеч У зотни поклайдиган кишилар бордирки'}, {'chapter': 24, 'verse': 37, 'text': 'رِجَالٞ لَّا تُلۡهِيهِمۡ تِجَٰرَةٞ وَلَا بَيۡعٌ عَن ذِكۡرِ ٱللَّهِ وَإِقَامِ ٱلصَّلَوٰةِ وَإِيتَآءِ ٱلزَّكَوٰةِ يَخَافُونَ يَوۡمٗا تَتَقَلَّبُ فِيهِ ٱلۡقُلُوبُ وَٱلۡأَبۡصَٰرُ\nУларни на тижорат ва на олди-сотди Аллоҳни зикр қилишдан, намозни тўкис адо этишдан ва закотни (ҳақдорларга) ато этишдан машғул қила олмас. Улар диллар ва кўзлар изтиробга тушиб қоладиган (Қиёмат) кунидан қўрқурлар'}, {'chapter': 24, 'verse': 38, 'text': 'لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا عَمِلُواْ وَيَزِيدَهُم مِّن فَضۡلِهِۦۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ\nУлар Аллоҳ ўзларини қилган амалларининг энг гўзаллари сабабли мукофотлаши ва яна уларга Ўз фазлу-карами билан зиёда (савоблар ато) қилиши учун (эрта-ю кеч Аллоҳни поклаб ибодат қилурлар). Аллоҳ Ўзи хоҳлаган кишиларга беҳисоб ризқ берур'}, {'chapter': 24, 'verse': 39, 'text': 'وَٱلَّذِينَ كَفَرُوٓاْ أَعۡمَٰلُهُمۡ كَسَرَابِۭ بِقِيعَةٖ يَحۡسَبُهُ ٱلظَّمۡـَٔانُ مَآءً حَتَّىٰٓ إِذَا جَآءَهُۥ لَمۡ يَجِدۡهُ شَيۡـٔٗا وَوَجَدَ ٱللَّهَ عِندَهُۥ فَوَفَّىٰهُ حِسَابَهُۥۗ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ\nКофир бўлган кимсаларнинг қилган амаллари эса саҳродаги саробга ўхшайди. Ташна одам уни сув деб ўйлар, токи қачон унинг (яъни, қилган амалининг) олдида топур. Бас, У зот унинг ҳисоб-китобини комил қилур. Аллоҳ жуда тез ҳисоб-китоб қилгувчидир'}, {'chapter': 24, 'verse': 40, 'text': 'أَوۡ كَظُلُمَٰتٖ فِي بَحۡرٖ لُّجِّيّٖ يَغۡشَىٰهُ مَوۡجٞ مِّن فَوۡقِهِۦ مَوۡجٞ مِّن فَوۡقِهِۦ سَحَابٞۚ ظُلُمَٰتُۢ بَعۡضُهَا فَوۡقَ بَعۡضٍ إِذَآ أَخۡرَجَ يَدَهُۥ لَمۡ يَكَدۡ يَرَىٰهَاۗ وَمَن لَّمۡ يَجۡعَلِ ٱللَّهُ لَهُۥ نُورٗا فَمَا لَهُۥ مِن نُّورٍ\nЁки (кофирларнинг қилган амаллари) устма-уст тўлқин ва унинг ҳам устида (қора) булут қоплаб олган денгиздаги зулматларга ўхшайди. (Улар) устма-уст зулматлардир. У ўз қўлини чиқариб (қараса) кўришга яқин бўлмас. Кимга Аллоҳ нури (иймон) бермаса, бас, унинг (учун) ҳеч қандай нур бўлмас'}, {'chapter': 24, 'verse': 41, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُسَبِّحُ لَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلطَّيۡرُ صَـٰٓفَّـٰتٖۖ كُلّٞ قَدۡ عَلِمَ صَلَاتَهُۥ وَتَسۡبِيحَهُۥۗ وَٱللَّهُ عَلِيمُۢ بِمَا يَفۡعَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳга осмонлар ва Ердаги бор жонзот ҳам, (самода) саф тортган қушлар ҳам тасбеҳ айтиб — поклашини кўрмадингизми? Аниқки, (Аллоҳ) барчанинг дуо-ю тасбеҳини билиб турур. Аллоҳ уларнинг қилаётган (барча) ишларини билгувчидир'}, {'chapter': 24, 'verse': 42, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ\nОсмонлар ва Ернинг подшоҳлиги Аллоҳникидир. Ёлғиз Аллоҳга қайтиш бордир'}, {'chapter': 24, 'verse': 43, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُزۡجِي سَحَابٗا ثُمَّ يُؤَلِّفُ بَيۡنَهُۥ ثُمَّ يَجۡعَلُهُۥ رُكَامٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦ وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مِن جِبَالٖ فِيهَا مِنۢ بَرَدٖ فَيُصِيبُ بِهِۦ مَن يَشَآءُ وَيَصۡرِفُهُۥ عَن مَّن يَشَآءُۖ يَكَادُ سَنَا بَرۡقِهِۦ يَذۡهَبُ بِٱلۡأَبۡصَٰرِ\nАллоҳ булутларни ҳайдашни, сўнгра уларни бирлаштиришни, сўнгра уларни устма-уст қилишини кўрмадингизми? Сўнг (булут) орасидан ёмғир чиқаётганини кўрурсиз. (Аллоҳ) осмондан — ундаги (булутлар) тоғидан дўл ёғдириб, унинг (зиёнини) Ўзи хоҳлаган кишиларга етказур ва Ўзи хоҳлаган кишилардан нари кетказур. Унинг чақмоғининг ёруғлиги (қараган) кўзларни кетказгудек бўлур'}, {'chapter': 24, 'verse': 44, 'text': 'يُقَلِّبُ ٱللَّهُ ٱلَّيۡلَ وَٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ\nАллоҳ кеча ва кундузни айлантириб турур. Албатта бунда дил эгалари учун ибрат бордир'}, {'chapter': 24, 'verse': 45, 'text': 'وَٱللَّهُ خَلَقَ كُلَّ دَآبَّةٖ مِّن مَّآءٖۖ فَمِنۡهُم مَّن يَمۡشِي عَلَىٰ بَطۡنِهِۦ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰ رِجۡلَيۡنِ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰٓ أَرۡبَعٖۚ يَخۡلُقُ ٱللَّهُ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАллоҳ барча жониворни сувдан яратди. Улардан қорни билан (судралиб) юрадиганлари ҳам бор, икки оёқда юрадиганлари ҳам бор, тўрт (оёкда) юрадиганлари ҳам бордир. Аллоҳ Ўзи хоҳлаган нарсани яратур. Албатта Аллоҳ барча нарсага қодирдир'}, {'chapter': 24, 'verse': 46, 'text': 'لَّقَدۡ أَنزَلۡنَآ ءَايَٰتٖ مُّبَيِّنَٰتٖۚ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nДарҳақиқат, Биз очиқ-равшан оятларни нозил қилдик. Аллоҳ Ўзи хоҳлаган кишиларни Тўғри Йўлга ҳидоят қилур'}, {'chapter': 24, 'verse': 47, 'text': 'وَيَقُولُونَ ءَامَنَّا بِٱللَّهِ وَبِٱلرَّسُولِ وَأَطَعۡنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُم مِّنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَـٰٓئِكَ بِٱلۡمُؤۡمِنِينَ\nУлар (яъни, мунофиқлар): «Аллоҳга ва пайғамбарга иймон келтирдик ва бўйинсундик», дейдилар-у, шундан кейин улардан бўлган бир гуруҳ юз ўгириб кетур. Улар мўмин эмасдирлар'}, {'chapter': 24, 'verse': 48, 'text': 'وَإِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ إِذَا فَرِيقٞ مِّنۡهُم مُّعۡرِضُونَ\nҚачон улар Аллоҳ ва Унинг пайғамбарига ўрталарида ҳукм чиқариш учун чорлансалар, баногоҳ улардан бир гуруҳи (пайғамбар ҳузурига келишдан) юз ўгиргувчидир'}, {'chapter': 24, 'verse': 49, 'text': 'وَإِن يَكُن لَّهُمُ ٱلۡحَقُّ يَأۡتُوٓاْ إِلَيۡهِ مُذۡعِنِينَ\nАгар ҳақиқат улар (тараф)да бўлса, (пайғамбар олдиларига) бўйин эгиб келурлар'}, {'chapter': 24, 'verse': 50, 'text': 'أَفِي قُلُوبِهِم مَّرَضٌ أَمِ ٱرۡتَابُوٓاْ أَمۡ يَخَافُونَ أَن يَحِيفَ ٱللَّهُ عَلَيۡهِمۡ وَرَسُولُهُۥۚ بَلۡ أُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nУларнинг дилларида мараз борми ёки (Муҳаммад алайҳис-салоту вас-саломнинг ҳақ пайғамбар эканлигидан) шубҳаландиларми ёхуд Аллоҳ ва Унинг пайғамбари ўзларига зулм қилишидан қўрқарларми?! Йўқ! Уларнинг ўзлари золимлардир'}, {'chapter': 24, 'verse': 51, 'text': 'إِنَّمَا كَانَ قَوۡلَ ٱلۡمُؤۡمِنِينَ إِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ أَن يَقُولُواْ سَمِعۡنَا وَأَطَعۡنَاۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nАллоҳ ва Унинг пайғамбарига ўрталарида ҳукм чиқариш учун чорланган вақтларида, мўминларнинг сўзи «эшитдик ва бўйинсундик», (демоқдир). Ана ўшаларгина нажот топгувчилардир'}, {'chapter': 24, 'verse': 52, 'text': 'وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ وَيَخۡشَ ٱللَّهَ وَيَتَّقۡهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ\nКим Аллоҳ ва Унинг пайғамбарига бўйинсунса ва Аллоҳдан қўрқиб, тақво қилса, бас, ана ўшаларгина (бахт-саодатга) эришгувчилардир'}, {'chapter': 24, 'verse': 53, 'text': '۞وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِنۡ أَمَرۡتَهُمۡ لَيَخۡرُجُنَّۖ قُل لَّا تُقۡسِمُواْۖ طَاعَةٞ مَّعۡرُوفَةٌۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ\n(Мунофиқлар) агар сиз амр этсангиз, албатта (жиҳодга) чиқишлари ҳақида жон-жаҳдлари билан қасам ичдилар. Айтинг: «Қасам ичманглар! (Жиҳод) маълум ибодатдир (яъни, бу ҳақда қасам ичишга ҳожат йўқдир). Албатта Аллоҳ қилаётган амалларингиздан хабардордир»'}, {'chapter': 24, 'verse': 54, 'text': 'قُلۡ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡهِ مَا حُمِّلَ وَعَلَيۡكُم مَّا حُمِّلۡتُمۡۖ وَإِن تُطِيعُوهُ تَهۡتَدُواْۚ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАйтинг: «Аллоҳга бўйинсунингиз ва Пайғамбарга бўйинсунингиз! Энди агар юз ўгирсангизлар бас, (пайғамбар) зиммасида ўзига юкланган нарса (яъни, одамларга Аллоҳнинг динини етказиш) бордир, сизларнинг зиммангизда эса ўзларингизга юкланган нарса (пайғамбарга бўйинсуниш) бордир. Агар унга бўйинсунсангиз, ҳидоят топурсизлар. Пайғамбар зиммасида (сизларни зўрлаб динга киргизиш эмас, балки) фақат (сизларга Ҳақ динни) етказиш бордир'}, {'chapter': 24, 'verse': 55, 'text': 'وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَيَسۡتَخۡلِفَنَّهُمۡ فِي ٱلۡأَرۡضِ كَمَا ٱسۡتَخۡلَفَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَلَيُمَكِّنَنَّ لَهُمۡ دِينَهُمُ ٱلَّذِي ٱرۡتَضَىٰ لَهُمۡ وَلَيُبَدِّلَنَّهُم مِّنۢ بَعۡدِ خَوۡفِهِمۡ أَمۡنٗاۚ يَعۡبُدُونَنِي لَا يُشۡرِكُونَ بِي شَيۡـٔٗاۚ وَمَن كَفَرَ بَعۡدَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nАллоҳ сизлардан иймон келтирган ва яхши амаллар қилган зотларга худди илгари ўтган (иймон-эътиқодли) зотларни (Ер юзига) халифа-ҳукмрон қилганидек, уларни ҳам Ер юзида халифа қилишни ва улар учун Ўзи рози бўлган (Ислом) динини ғолиб-мустаҳкам қилишни ҳамда уларнинг (аҳволини Маккада кўрган) хавфу-хатарларидан сўнг (Мадинада) тинчлик-хотиржамликка айлантириб қўйишни ваъда қилди. Улар Менга ибодат қилурлар ва Менга бирон нарсани шерик қилмаслар. Ким мана шу (ваъда)дан кейин куфрони (неъмат) қилса, бас, улар итоатсиз кимсалардир'}, {'chapter': 24, 'verse': 56, 'text': 'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ\n(Эй мўминлар), намозни тўкис адо этинглар, закотни ато этинглар ва пайғамбарга бўйинсунинглар. Шояд раҳматга эришсангизлар'}, {'chapter': 24, 'verse': 57, 'text': 'لَا تَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِۚ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَلَبِئۡسَ ٱلۡمَصِيرُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз ҳаргиз кофир бўлган кимсаларни Ер юзида (бирон жойга) қочиб қутулгувчи деб ўйламанг! Уларнинг борар жойлари дўзахдир. Нақадар ёмон оқибатдир у'}, {'chapter': 24, 'verse': 58, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِيَسۡتَـٔۡذِنكُمُ ٱلَّذِينَ مَلَكَتۡ أَيۡمَٰنُكُمۡ وَٱلَّذِينَ لَمۡ يَبۡلُغُواْ ٱلۡحُلُمَ مِنكُمۡ ثَلَٰثَ مَرَّـٰتٖۚ مِّن قَبۡلِ صَلَوٰةِ ٱلۡفَجۡرِ وَحِينَ تَضَعُونَ ثِيَابَكُم مِّنَ ٱلظَّهِيرَةِ وَمِنۢ بَعۡدِ صَلَوٰةِ ٱلۡعِشَآءِۚ ثَلَٰثُ عَوۡرَٰتٖ لَّكُمۡۚ لَيۡسَ عَلَيۡكُمۡ وَلَا عَلَيۡهِمۡ جُنَاحُۢ بَعۡدَهُنَّۚ طَوَّـٰفُونَ عَلَيۡكُم بَعۡضُكُمۡ عَلَىٰ بَعۡضٖۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nЭй мўминлар, қўл остингиздаги (қул ва чўриларингиз) ҳамда ўзларингизнинг балоғатга етмаган (болаларингиз) уч вақтда (ҳузурингизга кириш учун) сизлардан изн сўрасинлар — бомдод намозидан илгари, пешин вақтида (иссиқдан) кийимларингизни ташлаган пайтингизда ва хуфтон намозидан кейин. (Бу уч вақт) сизларнинг авратларингиз (очиқ бўлиши мумкин бўлган вақтлардир). Улар ўша (вақтлардан) сўнг (изн сўрамай киришларида) сизларга ҳам, уларга ҳам гуноҳ йўқдир. (Чунки) улар сизларга, бирингиз бирингизга келиб-кетиб тургувчидирсизлар. Аллоҳ оятларни сизларга мана шундай баён қилур. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 24, 'verse': 59, 'text': 'وَإِذَا بَلَغَ ٱلۡأَطۡفَٰلُ مِنكُمُ ٱلۡحُلُمَ فَلۡيَسۡتَـٔۡذِنُواْ كَمَا ٱسۡتَـٔۡذَنَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nҚачон гўдакларингиз балоғатга етсалар, бас, улар ҳам худди улардан илгари (балоғатга етганлар) каби изн сўрасинлар! Аллоҳ Ўз оятларини сизларга мана шундай баён қилур. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 24, 'verse': 60, 'text': 'وَٱلۡقَوَٰعِدُ مِنَ ٱلنِّسَآءِ ٱلَّـٰتِي لَا يَرۡجُونَ نِكَاحٗا فَلَيۡسَ عَلَيۡهِنَّ جُنَاحٌ أَن يَضَعۡنَ ثِيَابَهُنَّ غَيۡرَ مُتَبَرِّجَٰتِۭ بِزِينَةٖۖ وَأَن يَسۡتَعۡفِفۡنَ خَيۡرٞ لَّهُنَّۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ\nХотинлардан (ўтириб) кексайиб қолган, эрга тегишни ўйламайдиганлари бирон зеб-зийнат билан ясанмаган ҳолларида кийимларини (яъни, рўмол, ёпинчиқларини) олиб қўйишларида уларга гуноҳ йўқдир. Ўзларини пок (яъни, рўмол, ёпинчиқда) тутишлари ўзлари учун яхшироқдир. Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 24, 'verse': 61, 'text': 'لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞ وَلَا عَلَىٰٓ أَنفُسِكُمۡ أَن تَأۡكُلُواْ مِنۢ بُيُوتِكُمۡ أَوۡ بُيُوتِ ءَابَآئِكُمۡ أَوۡ بُيُوتِ أُمَّهَٰتِكُمۡ أَوۡ بُيُوتِ إِخۡوَٰنِكُمۡ أَوۡ بُيُوتِ أَخَوَٰتِكُمۡ أَوۡ بُيُوتِ أَعۡمَٰمِكُمۡ أَوۡ بُيُوتِ عَمَّـٰتِكُمۡ أَوۡ بُيُوتِ أَخۡوَٰلِكُمۡ أَوۡ بُيُوتِ خَٰلَٰتِكُمۡ أَوۡ مَا مَلَكۡتُم مَّفَاتِحَهُۥٓ أَوۡ صَدِيقِكُمۡۚ لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَأۡكُلُواْ جَمِيعًا أَوۡ أَشۡتَاتٗاۚ فَإِذَا دَخَلۡتُم بُيُوتٗا فَسَلِّمُواْ عَلَىٰٓ أَنفُسِكُمۡ تَحِيَّةٗ مِّنۡ عِندِ ٱللَّهِ مُبَٰرَكَةٗ طَيِّبَةٗۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ\n(Жиҳодга чиқмай қолишларида) кўзи ожиз кишига хараж-танглик йўқдир, чўлоққа хараж йўқдир, хастага хараж йўқдир (яъни, улар жиҳодга чиқмаганлари учун айбланмас). (Эй мўминлар), сизлар ўз уйларингиздан ё оталарингизнинг уйларидан ё оналарингизнинг уйларидан ё оға-иниларингизнинг уйларидан ё опа-сингилларингизнинг ё амакиларингизнинг уйларидан ё аммаларингизнинг уйларидан ё тоғаларингизнинг уйларидан ё холаларингизнинг уйларидан ё сизлар калитларига эга бўлган уйлардан ё дўстингизнинг уйидан (изн сўрамасдан) таомланишингизда сизларга (гуноҳ йўқдир). Тўпланган ё бўлинган ҳолларингизда таомланишингизда сизларга гуноҳ йўқдир. Бас, қачон уйларга кирсангизлар, бир-бирларингизга Аллоҳ ҳузуридан бўлган муборак покиза саломни айтинглар (яъни, «Ассалому алайкум», денглар). Сизлар ақл юргизишларингиз учун Аллоҳ Ўз оятларини сизларга мана шундай баён қилур'}, {'chapter': 24, 'verse': 62, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَإِذَا كَانُواْ مَعَهُۥ عَلَىٰٓ أَمۡرٖ جَامِعٖ لَّمۡ يَذۡهَبُواْ حَتَّىٰ يَسۡتَـٔۡذِنُوهُۚ إِنَّ ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ أُوْلَـٰٓئِكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦۚ فَإِذَا ٱسۡتَـٔۡذَنُوكَ لِبَعۡضِ شَأۡنِهِمۡ فَأۡذَن لِّمَن شِئۡتَ مِنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمُ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nАлбатта мўминлар фақат Аллоҳга ва Унинг пайғамбарига иймон келтирган зотлардир. Улар (пайғамбар) билан бирон муҳим иш устида бўлган вақтларида, то ундан изн сўрамагунларича кетмаслар. Фақат сиздан изн сўрайдиган зотлар – ана ўшалар Аллоҳ ва Унинг пайғамбарига иймон келтирадиган зотлардир. Энди қачон улар сиздан баъзи бир ишлари учун изн сўрасалар, бас, улардан ўзингиз хоҳлаган кишиларга изн беринг ва Аллоҳдан уларни мағфират қилишини сўранг. Албатта Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 24, 'verse': 63, 'text': 'لَّا تَجۡعَلُواْ دُعَآءَ ٱلرَّسُولِ بَيۡنَكُمۡ كَدُعَآءِ بَعۡضِكُم بَعۡضٗاۚ قَدۡ يَعۡلَمُ ٱللَّهُ ٱلَّذِينَ يَتَسَلَّلُونَ مِنكُمۡ لِوَاذٗاۚ فَلۡيَحۡذَرِ ٱلَّذِينَ يُخَالِفُونَ عَنۡ أَمۡرِهِۦٓ أَن تُصِيبَهُمۡ فِتۡنَةٌ أَوۡ يُصِيبَهُمۡ عَذَابٌ أَلِيمٌ\n(Эй мўминлар), пайғамбарни чақиришни ўрталарингизда бир-бирларингизни чақириш каби қилмангизлар (яъни, у зотни ҳурмат-эҳтиром билан «Эй Аллоҳнинг пайғамбари», деб чақиринглар). Сизлардан беркинган ҳолларда (пайғамбар ҳузуридан) суғурилиб чиқиб кетадиган кимсаларни Аллоҳ яхши билур. (Пайғамбарнинг) амрига хилоф иш қиладиган кимсалар ўзларига бирон фитна-кулфат етиб қолишдан ёки аламли азоб етиб қолишидан ҳазир бўлсинлар'}, {'chapter': 24, 'verse': 64, 'text': 'أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قَدۡ يَعۡلَمُ مَآ أَنتُمۡ عَلَيۡهِ وَيَوۡمَ يُرۡجَعُونَ إِلَيۡهِ فَيُنَبِّئُهُم بِمَا عَمِلُواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ\nОгоҳ бўлингизким, осмонлар ва Ердаги бор нарсалар шак-шубҳасиз, Аллоҳникидир. У зот сизлар устида турган нарсани (яъни, мўмин ё мунофиқлигингизни) ҳам, (барча бандалар) Ўзига қайтариладиган (Қиёмат) кунини ҳам яхши билур, бас, уларга (ўша кунда) қилиб ўтган амалларининг хабарини берур. Аллоҳ барча нарсани билгувчидир'}, {'chapter': 25, 'verse': 1, 'text': 'تَبَارَكَ ٱلَّذِي نَزَّلَ ٱلۡفُرۡقَانَ عَلَىٰ عَبۡدِهِۦ لِيَكُونَ لِلۡعَٰلَمِينَ نَذِيرًا\nБарча оламларни (Охират азобидан) қўрқитгувчи бўлсин деб Ўз бандаси (Муҳаммад алайҳис-салоту вас-салом)га Фурқон — Қуръон нозил қилган зот — Аллоҳ баракотли — Буюкдир'}, {'chapter': 25, 'verse': 2, 'text': 'ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَخَلَقَ كُلَّ شَيۡءٖ فَقَدَّرَهُۥ تَقۡدِيرٗا\nУ — осмонлар ва Ернинг подшоҳлиги Ўзиники бўлган, бола-чақа қилмаган, подшоҳликда бирон шериги бўлмаган ва барча нарсани яратиб (аниқ) ўлчов билан ўлчаб қўйган Зотдир'}, {'chapter': 25, 'verse': 3, 'text': 'وَٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗ لَّا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ وَلَا يَمۡلِكُونَ لِأَنفُسِهِمۡ ضَرّٗا وَلَا نَفۡعٗا وَلَا يَمۡلِكُونَ مَوۡتٗا وَلَا حَيَوٰةٗ وَلَا نُشُورٗا\n(Мушриклар) эса У зотни қўйиб, бирон нарса ярата олмайдиган, (балки) ўзлари яралгувчи бўлган, (ўзгалар у ёқда турсин, ҳатто) ўзларига ҳам бирон зиён ё фойда беришга эга бўлмайдиган ва на (бировларга) ўлим беришга, на ҳаёт беришга ва на қайта тирилтиришга эга бўлмайдиган «худолар»ни ушладилар (яъни, ўшаларга сиғиндилар)'}, {'chapter': 25, 'verse': 4, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ إِفۡكٌ ٱفۡتَرَىٰهُ وَأَعَانَهُۥ عَلَيۡهِ قَوۡمٌ ءَاخَرُونَۖ فَقَدۡ جَآءُو ظُلۡمٗا وَزُورٗا\nКофир бўлган кимсалар: «Бу (Қуръон) фақат (Муҳаммад) ўзи тўқиб олган бир ёлғондир. Унга бу (ёлғонни тўқишда) бошқа бир қавм (аҳли китоблар) ёрдам қилган», деб куфр келтирдилар ва бўҳтон қилдилар'}, {'chapter': 25, 'verse': 5, 'text': 'وَقَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ ٱكۡتَتَبَهَا فَهِيَ تُمۡلَىٰ عَلَيۡهِ بُكۡرَةٗ وَأَصِيلٗا\nЯна улар: «(Бу) аввалгилардан қолган афсоналардир (Муҳаммад) уларни кўчириб олган. Бас, (бу афсоналар бировлар томонидан) унга эрта-ю кеч ўқиб берилур», дедилар'}, {'chapter': 25, 'verse': 6, 'text': 'قُلۡ أَنزَلَهُ ٱلَّذِي يَعۡلَمُ ٱلسِّرَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ غَفُورٗا رَّحِيمٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, сиз уларга) айтинг: «Уни осмонлар ва Ердаги барча сир-асрорни биладиган Зот нозил қилгандир. Албатта У мағфиратли меҳрибон бўлган Зотдир»'}, {'chapter': 25, 'verse': 7, 'text': 'وَقَالُواْ مَالِ هَٰذَا ٱلرَّسُولِ يَأۡكُلُ ٱلطَّعَامَ وَيَمۡشِي فِي ٱلۡأَسۡوَاقِ لَوۡلَآ أُنزِلَ إِلَيۡهِ مَلَكٞ فَيَكُونَ مَعَهُۥ نَذِيرًا\nЯна улар (масхара қилишиб): «Нега бу пайғамбар (оддий одамлардек) таом ер ва бозорларда юрар?! Унга (пайғамбарлигини тасдиқлайдиган) бирон фаришта туширилиб, у билан бирга қўрқитгувчи бўлса эди'}, {'chapter': 25, 'verse': 8, 'text': 'أَوۡ يُلۡقَىٰٓ إِلَيۡهِ كَنزٌ أَوۡ تَكُونُ لَهُۥ جَنَّةٞ يَأۡكُلُ مِنۡهَاۚ وَقَالَ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا\nЁки унга (осмондан) хазина ташланса (ва у бозорларда савдо-сотиқ билан тирикчилик ўтказмай, ўша хазинадан сарф қилиб юрса) эди, ёхуд унинг учун бир боғу-бўстон бўлиб, у (фақат ўша боғдан) еб-ичса эди», дедилар. Бу золим кимсалар (мўминларга): «Сизлар фақат бир сеҳрланган — ақлдан озган кишига эргашмоқдасиз», дедилар'}, {'chapter': 25, 'verse': 9, 'text': 'ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَٰلَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), қаранг, улар сиз учун қандай мисоллар келтирдилар-а?! Бас, улар (ҳақ) йўлдан оздилар. Энди улар йўл топишга қодир эмаслар'}, {'chapter': 25, 'verse': 10, 'text': 'تَبَارَكَ ٱلَّذِيٓ إِن شَآءَ جَعَلَ لَكَ خَيۡرٗا مِّن ذَٰلِكَ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَيَجۡعَل لَّكَ قُصُورَۢا\nАгар хоҳласа, сиз учун (шу дунёдаёқ) ўшандан (яъни, улар айтган боғу-хазинадан) яхшироқ нарсани — остидан дарёлар оқиб турадиган жаннатларни қилиб бера оладиган ва сиз учун қаср-саройларни қилиб бера оладиган зот — Аллоҳ Баракотли — Буюкдир'}, {'chapter': 25, 'verse': 11, 'text': 'بَلۡ كَذَّبُواْ بِٱلسَّاعَةِۖ وَأَعۡتَدۡنَا لِمَن كَذَّبَ بِٱلسَّاعَةِ سَعِيرًا\nЙўқ, улар (Қуръон оятларига боқмайдилар, чунки улар Қиёмат) соатини ёлғон деганлар. Биз эса у соатни ёлғон деган кимсалар учун дўзах ўтини тайёрлаб қўйгандирмиз'}, {'chapter': 25, 'verse': 12, 'text': 'إِذَا رَأَتۡهُم مِّن مَّكَانِۭ بَعِيدٖ سَمِعُواْ لَهَا تَغَيُّظٗا وَزَفِيرٗا\n(Дўзах) уларни узоқ жойдан кўрган вақтидаёқ унинг хайқириқ ва бўкиригини эшитурлар'}, {'chapter': 25, 'verse': 13, 'text': 'وَإِذَآ أُلۡقُواْ مِنۡهَا مَكَانٗا ضَيِّقٗا مُّقَرَّنِينَ دَعَوۡاْ هُنَالِكَ ثُبُورٗا\nҚачон улар кишанланган ҳолларида (дўзахдан) тор бир жойга ташланганларида ўша жойда (ўзларига) ўлим тилаб қолурлар'}, {'chapter': 25, 'verse': 14, 'text': 'لَّا تَدۡعُواْ ٱلۡيَوۡمَ ثُبُورٗا وَٰحِدٗا وَٱدۡعُواْ ثُبُورٗا كَثِيرٗا\n(Шунда уларга): «Сизлар бугун бир ўлимни эмас, кўп ўлимларни тиланглар (чунки сизлар бутун умрларингизни куфру исёнда ўтказганларингиз учун кўпдан-кўп ўлим-ҳалокатларга дучор бўлурсиз», дейилур)'}, {'chapter': 25, 'verse': 15, 'text': 'قُلۡ أَذَٰلِكَ خَيۡرٌ أَمۡ جَنَّةُ ٱلۡخُلۡدِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۚ كَانَتۡ لَهُمۡ جَزَآءٗ وَمَصِيرٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Шу (дўзах) яхшироқми ёки тақводор зотларга ваъда қилинган мангу жаннатми?!» У (яъни, жаннат тақводорлар) учун мукофот ва оқибат — борар жой бўлди'}, {'chapter': 25, 'verse': 16, 'text': 'لَّهُمۡ فِيهَا مَا يَشَآءُونَ خَٰلِدِينَۚ كَانَ عَلَىٰ رَبِّكَ وَعۡدٗا مَّسۡـُٔولٗا\nУлар учун ўша жойда мангу қолгувчи бўлган ҳолларида барча хоҳлаган нарсалари муҳайё бўлур. Бу Парвардигорингиз зиммасидаги сўралгувчи ваъда бўлди'}, {'chapter': 25, 'verse': 17, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ فَيَقُولُ ءَأَنتُمۡ أَضۡلَلۡتُمۡ عِبَادِي هَـٰٓؤُلَآءِ أَمۡ هُمۡ ضَلُّواْ ٱلسَّبِيلَ\n(Аллоҳ) уларни (яъни, мушрикларни) ҳам, улар Аллоҳни қўйиб ибодат қилаётган бутларини ҳам тўплаб (у бутларга): «Менинг анави бандаларимни сизлар йўлдан оздирдингизларми ёки уларнинг ўзлари йўлдан оздиларми?», дейдиган Кунда'}, {'chapter': 25, 'verse': 18, 'text': 'قَالُواْ سُبۡحَٰنَكَ مَا كَانَ يَنۢبَغِي لَنَآ أَن نَّتَّخِذَ مِن دُونِكَ مِنۡ أَوۡلِيَآءَ وَلَٰكِن مَّتَّعۡتَهُمۡ وَءَابَآءَهُمۡ حَتَّىٰ نَسُواْ ٱلذِّكۡرَ وَكَانُواْ قَوۡمَۢا بُورٗا\nУлар дерлар: «Эй пок Парвардигор, бизлар учун Сендан ўзга бирон дўст тутиш жоиз бўлган эмасдир (бас, қандай қилиб ўзгаларни бизга бандалик қилиб сиғинишга мажбурлаб йўлдан оздирайлик)?! Лекин Сен уларни ва ота-боболарини (узоқ умр ва мўл-кўл ризқу рўз билан шундай) фойдалантирдингки, ҳатто улар (Сенинг Ўзингни) эслашни ҳам унутдилар ва ҳалок бўлгувчи қавм бўлиб қолдилар»'}, {'chapter': 25, 'verse': 19, 'text': 'فَقَدۡ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسۡتَطِيعُونَ صَرۡفٗا وَلَا نَصۡرٗاۚ وَمَن يَظۡلِم مِّنكُمۡ نُذِقۡهُ عَذَابٗا كَبِيرٗا\n(Эй мушриклар) мана, улар («худо»лар деб сиғинган бутлар) сизларни айтаётган сўзларингизда аниқ ёлғончи қилдилар. Энди (азобдан) қутулишга ҳам, (ўзларингга) бирон ёрдам қилишга ҳам қодир эмассизлар. Сизлардан ким золим — мушрик бўлса, Биз унга катта азобни тотдирурмиз'}, {'chapter': 25, 'verse': 20, 'text': 'وَمَآ أَرۡسَلۡنَا قَبۡلَكَ مِنَ ٱلۡمُرۡسَلِينَ إِلَّآ إِنَّهُمۡ لَيَأۡكُلُونَ ٱلطَّعَامَ وَيَمۡشُونَ فِي ٱلۡأَسۡوَاقِۗ وَجَعَلۡنَا بَعۡضَكُمۡ لِبَعۡضٖ فِتۡنَةً أَتَصۡبِرُونَۗ وَكَانَ رَبُّكَ بَصِيرٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан илгари юборган барча пайғамбарлар ҳам, шак-шубҳасиз, таом ер ва бозорларда юрар эдилар. Биз сизларнинг баъзиларингизни баъзиларингиз учун фитна-синов қилиб қўйдик (яъни, сизнинг оддий одамлар каби фақирона кун кечиришингиз бошқалар учун бир синовдир. Чунки агар Биз сизни бой-бадавлат қилиб қўйганимизда, улар сизга мол-дунёнгиз учун итоат қилган бўлур эдилар). Сизлар (Бизнинг бу синовимизга) сабр қила олурмисизлар? Парвардигорингиз кўриб тургувчи бўлган Зотдир'}, {'chapter': 25, 'verse': 21, 'text': '۞وَقَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا لَوۡلَآ أُنزِلَ عَلَيۡنَا ٱلۡمَلَـٰٓئِكَةُ أَوۡ نَرَىٰ رَبَّنَاۗ لَقَدِ ٱسۡتَكۡبَرُواْ فِيٓ أَنفُسِهِمۡ وَعَتَوۡ عُتُوّٗا كَبِيرٗا\nБизга рўбарў бўлишни умид қилмайдиган кимсалар: «Бизларга (пайғамбарлик учун одам эмас, балки) фаришталар туширилса эди ёки бизлар Парвардигоримизни (ўз кўзимиз билан) кўрсак (ва У бизларга Муҳаммаднинг ҳақиқий пайғамбар эканлигини билдирса) эди», дедилар. Дарҳақиқат, улар ўзларига бино қўйдилар ва жуда катта кетдилар'}, {'chapter': 25, 'verse': 22, 'text': 'يَوۡمَ يَرَوۡنَ ٱلۡمَلَـٰٓئِكَةَ لَا بُشۡرَىٰ يَوۡمَئِذٖ لِّلۡمُجۡرِمِينَ وَيَقُولُونَ حِجۡرٗا مَّحۡجُورٗا\nУлар фаришталарни кўрадиган кунни (яъни, Қиёмат Кунини эсланг). У кунда жиноятчи кимсалар учун бирон хушхабар бўлмас. Ва (фаришталар уларга) дерлар: «(Ҳар қандай хушхабар сизлар учун) бутунлай ҳаромдир»'}, {'chapter': 25, 'verse': 23, 'text': 'وَقَدِمۡنَآ إِلَىٰ مَا عَمِلُواْ مِنۡ عَمَلٖ فَجَعَلۡنَٰهُ هَبَآءٗ مَّنثُورًا\n(Зотан) Биз улар қилган ҳар бир (яхши) амалга келиб, уни сочилган тўзон (каби) қилиб қўйгандирмиз'}, {'chapter': 25, 'verse': 24, 'text': 'أَصۡحَٰبُ ٱلۡجَنَّةِ يَوۡمَئِذٍ خَيۡرٞ مُّسۡتَقَرّٗا وَأَحۡسَنُ مَقِيلٗا\nЖаннат эгалари эса у кунда энг яхши маскан ва энг гўзал зироҳатгоҳларда бўлурлар'}, {'chapter': 25, 'verse': 25, 'text': 'وَيَوۡمَ تَشَقَّقُ ٱلسَّمَآءُ بِٱلۡغَمَٰمِ وَنُزِّلَ ٱلۡمَلَـٰٓئِكَةُ تَنزِيلًا\nБулутли осмон ёрилиб, (барча) фаришталар (ер юзига) туширилган (Қиёмат) кунини (эсланг)'}, {'chapter': 25, 'verse': 26, 'text': 'ٱلۡمُلۡكُ يَوۡمَئِذٍ ٱلۡحَقُّ لِلرَّحۡمَٰنِۚ وَكَانَ يَوۡمًا عَلَى ٱلۡكَٰفِرِينَ عَسِيرٗا\nЎша кунда ҳақ-собит подшоҳлик (ёлғиз) Раҳмон учун бўлур. (Чунки бошқа барча муваққат подшоҳликлар йўқ бўлиб битур! У (кун) кофирларга кўп қийин кун бўлур'}, {'chapter': 25, 'verse': 27, 'text': 'وَيَوۡمَ يَعَضُّ ٱلظَّالِمُ عَلَىٰ يَدَيۡهِ يَقُولُ يَٰلَيۡتَنِي ٱتَّخَذۡتُ مَعَ ٱلرَّسُولِ سَبِيلٗا\nУ кунда бу золим қўлларини (бармоқларини тишлаб надоматлар қилиб) дер: «Э, қанийди мен ҳам пайғамбар билан бир йўлни тутганимда эди'}, {'chapter': 25, 'verse': 28, 'text': 'يَٰوَيۡلَتَىٰ لَيۡتَنِي لَمۡ أَتَّخِذۡ فُلَانًا خَلِيلٗا\nЎлим бўлсин менга, қанийди мен фалончини дўст тутмаганимда эди'}, {'chapter': 25, 'verse': 29, 'text': 'لَّقَدۡ أَضَلَّنِي عَنِ ٱلذِّكۡرِ بَعۡدَ إِذۡ جَآءَنِيۗ وَكَانَ ٱلشَّيۡطَٰنُ لِلۡإِنسَٰنِ خَذُولٗا\nАниқки, менга эслатма — Қуръон келганидан сўнг, ўша (фалончи) мени йўлдан оздирди». (У кунда) шайтон (ҳаёти дунёда ўзига эргашган барча) инсонни ёрдамсиз қўйгувчидир'}, {'chapter': 25, 'verse': 30, 'text': 'وَقَالَ ٱلرَّسُولُ يَٰرَبِّ إِنَّ قَوۡمِي ٱتَّخَذُواْ هَٰذَا ٱلۡقُرۡءَانَ مَهۡجُورٗا\nПайғамбар айтди: «Эй Парвардигорим, дарҳақиқат, менинг қавмим (Қурайш қабиласи) ушбу Қуръонни тарк қилиб (ундан юз ўгирдилар)»'}, {'chapter': 25, 'verse': 31, 'text': 'وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا مِّنَ ٱلۡمُجۡرِمِينَۗ وَكَفَىٰ بِرَبِّكَ هَادِيٗا وَنَصِيرٗا\nБиз (сиздан илгари ўтган) ҳар бир пайғамбар учун (ҳам) жиноятчи кимсалардан мана шундай душман(лар) қилганмиз. Парвардигорингизнинг Ўзи етарли етакчи ва ёрдамчидир'}, {'chapter': 25, 'verse': 32, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ٱلۡقُرۡءَانُ جُمۡلَةٗ وَٰحِدَةٗۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِۦ فُؤَادَكَۖ وَرَتَّلۡنَٰهُ تَرۡتِيلٗا\nКофир бўлган кимсалар: «(Нега) бу Қуръон унга (Муҳаммадга) битта тўплам бўлган ҳолида нозил қилинмади?», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом), Биз у — Қуръон билан сизнинг дилингизни устивор қилиш учун мана шундай (парча-парча ҳолда нозил қилдик) ва уни бўлиб-бўлиб баён қилдик'}, {'chapter': 25, 'verse': 33, 'text': 'وَلَا يَأۡتُونَكَ بِمَثَلٍ إِلَّا جِئۡنَٰكَ بِٱلۡحَقِّ وَأَحۡسَنَ تَفۡسِيرًا\nУлар сизга (Қуръонни айблаш учун юқоридаги каби) бирон мисол-савол келтирсалар, албатта Биз сизга ҳақ (жавобни) ва энг гўзал шарҳни келтириб қўйдик'}, {'chapter': 25, 'verse': 34, 'text': 'ٱلَّذِينَ يُحۡشَرُونَ عَلَىٰ وُجُوهِهِمۡ إِلَىٰ جَهَنَّمَ أُوْلَـٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ سَبِيلٗا\nЮзтубан ҳолларида жаҳаннам томонга судраладиган кимсалар — ана ўшалар энг аҳволи ёмон ва бутунлай йўлдан озган кимсалардир'}, {'chapter': 25, 'verse': 35, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَا مَعَهُۥٓ أَخَاهُ هَٰرُونَ وَزِيرٗا\nҚасамки, Биз Мусога Китоб — Таврот ато этдик ва биродари Ҳорунни у билан бирга бўлгувчи вазири қилдик'}, {'chapter': 25, 'verse': 36, 'text': 'فَقُلۡنَا ٱذۡهَبَآ إِلَى ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا فَدَمَّرۡنَٰهُمۡ تَدۡمِيرٗا\nВа (у иккисига) дедик: «Бизнинг оятларимизни ёлғон деган қавм (Фиръавн ва унинг қавми) олдига боринглар!» Бас, (икковлари бориб, Аллоҳнинг динига даъват қилган вақтларида, улар ўз куфрларидан қайтишмагач), Биз уларни (денгизга ғарқ қилиш билан) қириб ташладик'}, {'chapter': 25, 'verse': 37, 'text': 'وَقَوۡمَ نُوحٖ لَّمَّا كَذَّبُواْ ٱلرُّسُلَ أَغۡرَقۡنَٰهُمۡ وَجَعَلۡنَٰهُمۡ لِلنَّاسِ ءَايَةٗۖ وَأَعۡتَدۡنَا لِلظَّـٰلِمِينَ عَذَابًا أَلِيمٗا\nНуҳ қавмини (эсланг)! Қачонки, улар пайғамбарларини ёлғончи қилишгач, Биз уларни ғарқ қилдик ва уларни одамлар учун оят-ибрат қилиб қўйдик. Яна Биз золим-кофир кимсалар учун (Охиратда ҳам) аламли азоб тайёрлаб қўйгандирмиз'}, {'chapter': 25, 'verse': 38, 'text': 'وَعَادٗا وَثَمُودَاْ وَأَصۡحَٰبَ ٱلرَّسِّ وَقُرُونَۢا بَيۡنَ ذَٰلِكَ كَثِيرٗا\nОд, Самуд (қабилаларини) ва қудуқ эгаларини (яъни, Шуайб пайғамбарни ёлғончи қилганлари сабабли қудуққа қулаб ҳалок бўлган кимсаларни) ҳамда шу орадаги кўпдан-кўп асрларни (эсланг)'}, {'chapter': 25, 'verse': 39, 'text': 'وَكُلّٗا ضَرَبۡنَا لَهُ ٱلۡأَمۡثَٰلَۖ وَكُلّٗا تَبَّرۡنَا تَتۡبِيرٗا\nБарчаларига мисоллар — далиллар келтирдик ва (Бизнинг панд-насиҳатларимиз кор қилмагач), барчаларини бутунлай йўқ қилиб юбордик'}, {'chapter': 25, 'verse': 40, 'text': 'وَلَقَدۡ أَتَوۡاْ عَلَى ٱلۡقَرۡيَةِ ٱلَّتِيٓ أُمۡطِرَتۡ مَطَرَ ٱلسَّوۡءِۚ أَفَلَمۡ يَكُونُواْ يَرَوۡنَهَاۚ بَلۡ كَانُواْ لَا يَرۡجُونَ نُشُورٗا\nАниқки, улар (яъни, Макка мушриклари ўзларининг сафар-саёҳатларида) энг ёмон ёмғир (яъни, тош) ёғдирилган шаҳардан ўтгандирлар. (Ахир улар уни кўриб ибрат олмадиларми)?! Йўқ, улар қайта тирилишдан умидсиз бўлган кимсалардир'}, {'chapter': 25, 'verse': 41, 'text': 'وَإِذَا رَأَوۡكَ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي بَعَثَ ٱللَّهُ رَسُولًا\n(Эй Муҳаммад алайҳис-салоту вас-салом) қачон улар сизни кўрсалар, фақат: «Аллоҳ мана шуни пайғамбар қилиб юбордими?!, деб сизни масхара қилурлар'}, {'chapter': 25, 'verse': 42, 'text': 'إِن كَادَ لَيُضِلُّنَا عَنۡ ءَالِهَتِنَا لَوۡلَآ أَن صَبَرۡنَا عَلَيۡهَاۚ وَسَوۡفَ يَعۡلَمُونَ حِينَ يَرَوۡنَ ٱلۡعَذَابَ مَنۡ أَضَلُّ سَبِيلًا\nАгар сабр қилиб (маҳкам турмаганимизда) ҳақиқатан у бизларни худоларимиздан оздираёзди-я», (деб сизни масхара қилурлар). Яқинда — азобни кўрган вақтларида ким йўлдан озганроқ эканини билиб олурлар'}, {'chapter': 25, 'verse': 43, 'text': 'أَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ أَفَأَنتَ تَكُونُ عَلَيۡهِ وَكِيلًا\nХабар беринг-чи, ким ҳавои нафсларини илоҳ килиб олган бўлса, сиз унинг устида вакил-қўриқчи бўлурмисиз?! (Йўқ, сизнинг вазифангиз ҳавои нафсига қуллик қиладиган кимсаларни бу қилмишларидан сақлаш — қўриқлаш эмас, балки фақат Биз сизга юборган ваҳийни бандаларимизга етказмоқдир)'}, {'chapter': 25, 'verse': 44, 'text': 'أَمۡ تَحۡسَبُ أَنَّ أَكۡثَرَهُمۡ يَسۡمَعُونَ أَوۡ يَعۡقِلُونَۚ إِنۡ هُمۡ إِلَّا كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّ سَبِيلًا\nЁки сиз уларнинг кўпларига (ҳақ сўзни) тинглай оладилар ё англай оладилар, деб ўйлайсизми?! (Ундоқ эмас, зеро) улар ҳеч нарса эмаслар, магар чорва ҳайвонлари кабидирлар. Йўқ, улар янада йўлдан озганроқ кимсалардир'}, {'chapter': 25, 'verse': 45, 'text': 'أَلَمۡ تَرَ إِلَىٰ رَبِّكَ كَيۡفَ مَدَّ ٱلظِّلَّ وَلَوۡ شَآءَ لَجَعَلَهُۥ سَاكِنٗا ثُمَّ جَعَلۡنَا ٱلشَّمۡسَ عَلَيۡهِ دَلِيلٗا\nПарвардигорингизнинг (қудратини) — сояни қандай ёйиб қўйганини кўрмадингизми?! Агар хоҳласа уни сокин — жилмас қилиб қўйган бўлур эди. Сўнгра қуёшни унга (яъни, соянинг борлигига) далил қилдик'}, {'chapter': 25, 'verse': 46, 'text': 'ثُمَّ قَبَضۡنَٰهُ إِلَيۡنَا قَبۡضٗا يَسِيرٗا\nСўнгра уни аста-секин Ўзимизга тортиб олдик'}, {'chapter': 25, 'verse': 47, 'text': 'وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِبَاسٗا وَٱلنَّوۡمَ سُبَاتٗا وَجَعَلَ ٱلنَّهَارَ نُشُورٗا\n(Аллоҳ) сизлар учун кечани либос, уйқуни ором қилиб қўйган, кундузни эса (сизлар учун) қайта тирилиш қилган Зотдир'}, {'chapter': 25, 'verse': 48, 'text': 'وَهُوَ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ طَهُورٗا\nУ Ўз раҳматининг (яъни, ёмғирнинг) олдидан хушхабар қилиб шамолларни юборган Зотдир. Ва осмондан пок сув ёғдирдик'}, {'chapter': 25, 'verse': 49, 'text': 'لِّنُحۡـِۧيَ بِهِۦ بَلۡدَةٗ مَّيۡتٗا وَنُسۡقِيَهُۥ مِمَّا خَلَقۡنَآ أَنۡعَٰمٗا وَأَنَاسِيَّ كَثِيرٗا\nБиз ўлик шаҳар-масканларни тирилтириш ва Ўзимиз яратган кўплаб чорва ҳайвонлари ҳамда инсонларни суғориш учун (осмондан пок сув ёғдирдик)'}, {'chapter': 25, 'verse': 50, 'text': 'وَلَقَدۡ صَرَّفۡنَٰهُ بَيۡنَهُمۡ لِيَذَّكَّرُواْ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا\nВа у (сувни) эслатма олишлари учун (одамлар) орасида тақсимлаб қўйдик, (аммо) одамларнинг кўплари фақат куфрони неъмат қилишнигина истадилар'}, {'chapter': 25, 'verse': 51, 'text': 'وَلَوۡ شِئۡنَا لَبَعَثۡنَا فِي كُلِّ قَرۡيَةٖ نَّذِيرٗا\nАгар Биз хоҳласак, ҳар қишлоқ-шаҳарга бир (Охират азобидан) қўрқитгувчи — пайғамбар юборган бўлур эдик. (Лекин Биз барча қишлоқ-шаҳардан Охират азобидан огоҳ қилиш бурчини сизнинг зиммангизга юкладик)'}, {'chapter': 25, 'verse': 52, 'text': 'فَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَجَٰهِدۡهُم بِهِۦ جِهَادٗا كَبِيرٗا\nБас, сиз кофир кимсаларга бўйинсунманг ва (Қуръон) ёрдамида уларга қарши қаттиқ курашинг'}, {'chapter': 25, 'verse': 53, 'text': '۞وَهُوَ ٱلَّذِي مَرَجَ ٱلۡبَحۡرَيۡنِ هَٰذَا عَذۡبٞ فُرَاتٞ وَهَٰذَا مِلۡحٌ أُجَاجٞ وَجَعَلَ بَيۡنَهُمَا بَرۡزَخٗا وَحِجۡرٗا مَّحۡجُورٗا\n(Аллоҳ) икки денгиз — дарёни буниси чучук — ширин, униси шўр — аччиқ қилиб оқизиб қўйган ва уларнинг ўртасида тўсиқ-тўғон ва кўринмас парда қилиб қўйган Зотдир'}, {'chapter': 25, 'verse': 54, 'text': 'وَهُوَ ٱلَّذِي خَلَقَ مِنَ ٱلۡمَآءِ بَشَرٗا فَجَعَلَهُۥ نَسَبٗا وَصِهۡرٗاۗ وَكَانَ رَبُّكَ قَدِيرٗا\nУ сувдан — нутфадан инсонни яратиб, сўнг уни насл-насаб (эгаси) ва қуда-анда қилиб қўйган Зотдир. Дарҳақиқат, Парвардигорингиз (ҳар ишга) қодирдир'}, {'chapter': 25, 'verse': 55, 'text': 'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُهُمۡ وَلَا يَضُرُّهُمۡۗ وَكَانَ ٱلۡكَافِرُ عَلَىٰ رَبِّهِۦ ظَهِيرٗا\n(Кофирлар) Аллоҳни қўйиб, ўзларига фойда ҳам, зиён ҳам етказа олмайдиган нарсаларга ибодат қиладилар. Кофир ўз Парвардигорига қарши (шайтонга) ёрдамчи бўлган кимсадир'}, {'chapter': 25, 'verse': 56, 'text': 'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизни фақат (мўминларга жаннат) хушхабарини етказгувчи, (кофирларни эса Охират азобидан) қўрқитгувчи қилиб юбордик'}, {'chapter': 25, 'verse': 57, 'text': 'قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍ إِلَّا مَن شَآءَ أَن يَتَّخِذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا\nАйтинг: «Мен сизлардан бунинг учун ҳеч қандай ҳақ сўрамайман, фақат Парвардигор томонга йўл тутишни хоҳлаган кишилар (ўша йўлни тутишларини сўрайман»)'}, {'chapter': 25, 'verse': 58, 'text': 'وَتَوَكَّلۡ عَلَى ٱلۡحَيِّ ٱلَّذِي لَا يَمُوتُ وَسَبِّحۡ بِحَمۡدِهِۦۚ وَكَفَىٰ بِهِۦ بِذُنُوبِ عِبَادِهِۦ خَبِيرًا\nВа ўлмайдиган тирик Зотга таваккул қилинг, ҳамда У зотга ҳамду сано айтиш билан покланг! У Ўз бандаларининг гуноҳларидан етарли огоҳдир'}, {'chapter': 25, 'verse': 59, 'text': 'ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ ٱلرَّحۡمَٰنُ فَسۡـَٔلۡ بِهِۦ خَبِيرٗا\nУ осмонлар ва Ерни ҳамда уларнинг орасидаги барча нарсаларни олти кунда яратиб, сўнгра (Зоти ва Улуғлигига лойиқ суратда) Аршга юксалган Зотдир. У Раҳмондир. Бас, У зот ҳақида огоҳ — билимдон (Зот — Аллоҳ ва Расули)дан сўранг'}, {'chapter': 25, 'verse': 60, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱسۡجُدُواْۤ لِلرَّحۡمَٰنِ قَالُواْ وَمَا ٱلرَّحۡمَٰنُ أَنَسۡجُدُ لِمَا تَأۡمُرُنَا وَزَادَهُمۡ نُفُورٗا۩\nҚачон (мушрикларга): «Раҳмонга сажда қилинглар», дейилса, улар: «Нима у Раҳмон? Сен буюрган нарсага сажда қилаверамизми?», дейишар ва (бу сўз) уларни янада йироқлаштирар'}, {'chapter': 25, 'verse': 61, 'text': 'تَبَارَكَ ٱلَّذِي جَعَلَ فِي ٱلسَّمَآءِ بُرُوجٗا وَجَعَلَ فِيهَا سِرَٰجٗا وَقَمَرٗا مُّنِيرٗا\nОсмонда буржларни барпо қилган ва унда чироқ — қуёш ва ойни пайдо қилган Зот Баракотли — Буюкдир'}, {'chapter': 25, 'verse': 62, 'text': 'وَهُوَ ٱلَّذِي جَعَلَ ٱلَّيۡلَ وَٱلنَّهَارَ خِلۡفَةٗ لِّمَنۡ أَرَادَ أَن يَذَّكَّرَ أَوۡ أَرَادَ شُكُورٗا\nУ эслатма-ибрат олмоқчи бўлган, ёки шукр қилмоқчи бўлган кишилар учун кеча ва кундузни (бир-бирининг) ўрнини босувчи қилиб қўйган Зотдир'}, {'chapter': 25, 'verse': 63, 'text': 'وَعِبَادُ ٱلرَّحۡمَٰنِ ٱلَّذِينَ يَمۡشُونَ عَلَى ٱلۡأَرۡضِ هَوۡنٗا وَإِذَا خَاطَبَهُمُ ٱلۡجَٰهِلُونَ قَالُواْ سَلَٰمٗا\nРаҳмоннинг (суюкли) бандалари Ерда тавозуъ билан юрадиган, жоҳил кимсалар (бемаъни) хитоблар қилган вақтида ҳам «Омон бўлинглар», деб жавоб қиладиган кишилардир'}, {'chapter': 25, 'verse': 64, 'text': 'وَٱلَّذِينَ يَبِيتُونَ لِرَبِّهِمۡ سُجَّدٗا وَقِيَٰمٗا\nУлар кечаларни Парвардигорга сажда қилиб ва (жойнамоз устида) тик туриб — бедор ўтказадиган кишилардир'}, {'chapter': 25, 'verse': 65, 'text': 'وَٱلَّذِينَ يَقُولُونَ رَبَّنَا ٱصۡرِفۡ عَنَّا عَذَابَ جَهَنَّمَۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا\nУлар: «Парвардигоро, Ўзинг бизлардан жаҳаннам азобини дафъ қилгин. Дарҳақиқат, унинг азоби кетмас — мангу (азобдир)'}, {'chapter': 25, 'verse': 66, 'text': 'إِنَّهَا سَآءَتۡ مُسۡتَقَرّٗا وَمُقَامٗا\nДарҳақиқат, у энг ёмон қароргоҳ ва энг ёмон жойдир», дейдиган кишилардир'}, {'chapter': 25, 'verse': 67, 'text': 'وَٱلَّذِينَ إِذَآ أَنفَقُواْ لَمۡ يُسۡرِفُواْ وَلَمۡ يَقۡتُرُواْ وَكَانَ بَيۡنَ ذَٰلِكَ قَوَامٗا\nУлар инфоқ-эҳсон қилган вақтларида исроф ҳам, хасислик ҳам қилмаслар (балки эҳсонлари) ана ўша (хасислик билан исрофнинг) ўртасида — мўътадил бўлур'}, {'chapter': 25, 'verse': 68, 'text': 'وَٱلَّذِينَ لَا يَدۡعُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ وَلَا يَقۡتُلُونَ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّ وَلَا يَزۡنُونَۚ وَمَن يَفۡعَلۡ ذَٰلِكَ يَلۡقَ أَثَامٗا\nУлар Аллоҳ билан бирга бошқа бирон илоҳга илтижо қилмаслар ва Аллоҳ (ўлдиришни ҳаром қилган) бирон жонни ноҳақ ўлдирмаслар ҳамда зино қилмаслар. Ким мана шу (гуноҳлардан биронтасини) қилса уқубатга дучор бўлур'}, {'chapter': 25, 'verse': 69, 'text': 'يُضَٰعَفۡ لَهُ ٱلۡعَذَابُ يَوۡمَ ٱلۡقِيَٰمَةِ وَيَخۡلُدۡ فِيهِۦ مُهَانًا\nҚиёмат Кунида унинг учун азоб бир неча баробар қилинур ва у жойда хорланган ҳолида мангу қолур'}, {'chapter': 25, 'verse': 70, 'text': 'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ عَمَلٗا صَٰلِحٗا فَأُوْلَـٰٓئِكَ يُبَدِّلُ ٱللَّهُ سَيِّـَٔاتِهِمۡ حَسَنَٰتٖۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nМагар ким тавба қилса ва иймон келтириб яхши амаллар қилса, бас, Аллоҳ ана ўшаларнинг ёмонлик-гуноҳларини яхшилик-савобларга айлантириб қўюр. Аллоҳ мағфиратли, меҳрибон бўлган Зотдир'}, {'chapter': 25, 'verse': 71, 'text': 'وَمَن تَابَ وَعَمِلَ صَٰلِحٗا فَإِنَّهُۥ يَتُوبُ إِلَى ٱللَّهِ مَتَابٗا\nКим тавба қилиб, яхши (амаллар) қилса, бас, албатта у Аллоҳнинг (ризо-мағфиратига) қайтган бўлур'}, {'chapter': 25, 'verse': 72, 'text': 'وَٱلَّذِينَ لَا يَشۡهَدُونَ ٱلزُّورَ وَإِذَا مَرُّواْ بِٱللَّغۡوِ مَرُّواْ كِرَامٗا\nУлар (яъни, Раҳмоннинг суюкли бандалари) ёлғон гувоҳлик бермаслар ва лағв (беҳуда сўз ё амал) олдидан ўтган вақтларида, олижаноблик билан (яъни, ундан юз ўгирган ҳолларида) ўтурлар'}, {'chapter': 25, 'verse': 73, 'text': 'وَٱلَّذِينَ إِذَا ذُكِّرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَمۡ يَخِرُّواْ عَلَيۡهَا صُمّٗا وَعُمۡيَانٗا\nУларга Парвардигорларининг оятлари эслатилган вақтида у (оятларга) гунг ва кўр ҳолларида ташланмайдилар, (балки уларни англаб-билиб амал қилурлар)'}, {'chapter': 25, 'verse': 74, 'text': 'وَٱلَّذِينَ يَقُولُونَ رَبَّنَا هَبۡ لَنَا مِنۡ أَزۡوَٰجِنَا وَذُرِّيَّـٰتِنَا قُرَّةَ أَعۡيُنٖ وَٱجۡعَلۡنَا لِلۡمُتَّقِينَ إِمَامًا\nУлар: «Парвардигоро, Ўзинг бизларга жуфти ҳалолларимиздан ва зурриётларимиздан шод-хуррамлик ҳадя этгин ва бизларни тақводорларга пешво қилгин», дейдиган кишилардир'}, {'chapter': 25, 'verse': 75, 'text': 'أُوْلَـٰٓئِكَ يُجۡزَوۡنَ ٱلۡغُرۡفَةَ بِمَا صَبَرُواْ وَيُلَقَّوۡنَ فِيهَا تَحِيَّةٗ وَسَلَٰمًا\nАна ўшалар сабр-қаноат қилганлари сабабли (жаннатдаги) юксак даражалар билан мукофотланурлар ва у жойда (фаришталар томонидан) салом ва омонлик билан қарши олинурлар'}, {'chapter': 25, 'verse': 76, 'text': 'خَٰلِدِينَ فِيهَاۚ حَسُنَتۡ مُسۡتَقَرّٗا وَمُقَامٗا\nУлар ўша жойда мангу қолурлар. У энг гўзал қароргоҳ, энг гўзал манзилдир'}, {'chapter': 25, 'verse': 77, 'text': 'قُلۡ مَا يَعۡبَؤُاْ بِكُمۡ رَبِّي لَوۡلَا دُعَآؤُكُمۡۖ فَقَدۡ كَذَّبۡتُمۡ فَسَوۡفَ يَكُونُ لِزَامَۢا\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Агар дуо-илтижо қилмас экансизлар, Парвардигорим сизларга парво қилмас. Бас, сизлар (У зот юборган пайғамбарни) ёлғончи қилдингиз. Энди албатта (азоб-уқубат) сизларга лозим бўлур»'}, {'chapter': 26, 'verse': 1, 'text': 'طسٓمٓ\nТо, Син, Мим'}, {'chapter': 26, 'verse': 2, 'text': 'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ\nУшбу (оятлар) очиқ-равшан Китоб оятларидир'}, {'chapter': 26, 'verse': 3, 'text': 'لَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ أَلَّا يَكُونُواْ مُؤۡمِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эҳтимол сиз (Макка мушриклари) мўмин бўлмаганлари учун ўзингизни ҳалок қилгувчидирсиз. (Ундоқ қилманг, чунки)'}, {'chapter': 26, 'verse': 4, 'text': 'إِن نَّشَأۡ نُنَزِّلۡ عَلَيۡهِم مِّنَ ٱلسَّمَآءِ ءَايَةٗ فَظَلَّتۡ أَعۡنَٰقُهُمۡ لَهَا خَٰضِعِينَ\nАгар Биз (уларнинг мўмин бўлишларини) хоҳласак, уларга осмондан оят-мўъжиза нозил қилиб, шу билан бўйинлари эгилиб қоларди. (Лекин Биз буни истамаймиз, зеро иймон-ишонч ночор-ноиложликдан эмас, балки қалб қаноати билан ихтиёрий бўлиши лозимдир)'}, {'chapter': 26, 'verse': 5, 'text': 'وَمَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّنَ ٱلرَّحۡمَٰنِ مُحۡدَثٍ إِلَّا كَانُواْ عَنۡهُ مُعۡرِضِينَ\nУларга Раҳмон томонидан бирон янги эслатма келса, албатта, улар ундан юз ўгирувчи бўлдилар'}, {'chapter': 26, 'verse': 6, 'text': 'فَقَدۡ كَذَّبُواْ فَسَيَأۡتِيهِمۡ أَنۢبَـٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nБас, аниқки улар (пайғамбарни) ёлғончи қилдилар. Энди яқинда уларга ўзлари масхара қилган нарсанинг хабарлари (яъни, оқибати) келур'}, {'chapter': 26, 'verse': 7, 'text': 'أَوَلَمۡ يَرَوۡاْ إِلَى ٱلۡأَرۡضِ كَمۡ أَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ\nАхир улар Ерни — унда, Биз барча турли фойдали (ўсимлик) навларидан қанчасини ундириб қўйганимизни кўрмадиларми'}, {'chapter': 26, 'verse': 8, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта бунда оят — ибрат бордир. (Лекин Макка мушрикларининг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 9, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 10, 'text': 'وَإِذۡ نَادَىٰ رَبُّكَ مُوسَىٰٓ أَنِ ٱئۡتِ ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эсланг, Парвардигорингиз Мусога нидо қилиб: «У золим қавмга боргин'}, {'chapter': 26, 'verse': 11, 'text': 'قَوۡمَ فِرۡعَوۡنَۚ أَلَا يَتَّقُونَ\nФиръавн қавмига (боргин), улар (Мендан) қўрқмайдиларми?!», (деган эди)'}, {'chapter': 26, 'verse': 12, 'text': 'قَالَ رَبِّ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ\nМусо айтди: «Парвардигорим, (албатта амрингга итоат этурман — у золим қавм олдига борурман, фақат) улар мени ёлғончи қилишларидан қўрқурман'}, {'chapter': 26, 'verse': 13, 'text': 'وَيَضِيقُ صَدۡرِي وَلَا يَنطَلِقُ لِسَانِي فَأَرۡسِلۡ إِلَىٰ هَٰرُونَ\nВа (улар мени ёлғончи қилишларидан) дилим сиқилур, тилим бурро эмасдир, бас, (Мисрда Фиръавн зулми остида яшаб турган биродарим) Ҳорунга ҳам (пайғамбарлик) юборсанг, (у менга ёрдамчи бўлса)'}, {'chapter': 26, 'verse': 14, 'text': 'وَلَهُمۡ عَلَيَّ ذَنۢبٞ فَأَخَافُ أَن يَقۡتُلُونِ\nЯна уларнинг наздида мен гуноҳкорман (яъни, улар мени бир мисрлик — қибтий йигитни қасддан ўлдиришликда айблайдилар), бас, мени, (Сенинг фармонингни уларга етказмасимдан) ўлдириб қўйишларидан қўрқурман»'}, {'chapter': 26, 'verse': 15, 'text': 'قَالَ كَلَّاۖ فَٱذۡهَبَا بِـَٔايَٰتِنَآۖ إِنَّا مَعَكُم مُّسۡتَمِعُونَ\n(Аллоҳ) деди: «Йўқ (улар сени ўлдира олмаслар). Бас, (биродаринг Ҳорун билан) Бизнинг оят-мўъжизаларимизни олиб боринглар! Албатта Биз сизлар билан бирга (савол-жавобларингизни) эшитиб тургувчидирмиз'}, {'chapter': 26, 'verse': 16, 'text': 'فَأۡتِيَا فِرۡعَوۡنَ فَقُولَآ إِنَّا رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ\nЭнди Фиръавнга бориб айтинглар: «Ҳақиқатан биз барча оламлар Парвардигорининг элчиларидирмиз'}, {'chapter': 26, 'verse': 17, 'text': 'أَنۡ أَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَـٰٓءِيلَ\n(Қўл остингдаги) Бани Исроил (қавми)ни бизга қўшиб (ўз юртлари — Фаластинга) жўнатгин»'}, {'chapter': 26, 'verse': 18, 'text': 'قَالَ أَلَمۡ نُرَبِّكَ فِينَا وَلِيدٗا وَلَبِثۡتَ فِينَا مِنۡ عُمُرِكَ سِنِينَ\n(Улар келиб Фиръавнга шу сўзларни айтишгач, у Мусога қараб) деди: «Биз сени болалик чоғингда ўз ичимизда (яъни, саройимизда) тарбияламаганмидик, умрингнинг бир неча йилида орамизда турган эдинг-ку'}, {'chapter': 26, 'verse': 19, 'text': 'وَفَعَلۡتَ فَعۡلَتَكَ ٱلَّتِي فَعَلۡتَ وَأَنتَ مِنَ ٱلۡكَٰفِرِينَ\nКейин нонкўрлардан бўлиб қилган қилмишингни — қилган эдинг-ку (яъни, бир Қибтийни ўлдириб қўйиб қочиб кетган эдинг-ку)!»'}, {'chapter': 26, 'verse': 20, 'text': 'قَالَ فَعَلۡتُهَآ إِذٗا وَأَنَا۠ مِنَ ٱلضَّآلِّينَ\n(Мусо) айтди: «Ўшанда мен у ишни билмаган ҳолда қилган эдим'}, {'chapter': 26, 'verse': 21, 'text': 'فَفَرَرۡتُ مِنكُمۡ لَمَّا خِفۡتُكُمۡ فَوَهَبَ لِي رَبِّي حُكۡمٗا وَجَعَلَنِي مِنَ ٱلۡمُرۡسَلِينَ\nСўнг сизлардан қўрқиб қочиб кетдим. Кейин Парвардигорим, менга ҳикмат — илм ҳадя этди ва мени пайғамбарлардан қилди'}, {'chapter': 26, 'verse': 22, 'text': 'وَتِلۡكَ نِعۡمَةٞ تَمُنُّهَا عَلَيَّ أَنۡ عَبَّدتَّ بَنِيٓ إِسۡرَـٰٓءِيلَ\nЎша сен менга миннат қилаётган неъмат (аслида) сен Бани Исроилни қул қилиб олишингдир»'}, {'chapter': 26, 'verse': 23, 'text': 'قَالَ فِرۡعَوۡنُ وَمَا رَبُّ ٱلۡعَٰلَمِينَ\nФиръавн деди: «Барча оламларнинг Парвардигори (деганинг) нимаси яна?»'}, {'chapter': 26, 'verse': 24, 'text': 'قَالَ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُم مُّوقِنِينَ\n(Мусо) айтди: «У агар ишонадиган бўлсанглар, осмонлар ва Ернинг ҳамда уларнинг орасидаги барча нарсаларнинг Парвардигори — Хожасидир»'}, {'chapter': 26, 'verse': 25, 'text': 'قَالَ لِمَنۡ حَوۡلَهُۥٓ أَلَا تَسۡتَمِعُونَ\n(Фиръавн) атрофидаги (аъёнлар)ига: «Қулоқ солмайсизларми?», деб (масхара қилди)'}, {'chapter': 26, 'verse': 26, 'text': 'قَالَ رَبُّكُمۡ وَرَبُّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ\n(Мусо) айтди: «У сизларнинг ҳам, ўтган ота-боболарингизнинг ҳам Парвардигоридир»'}, {'chapter': 26, 'verse': 27, 'text': 'قَالَ إِنَّ رَسُولَكُمُ ٱلَّذِيٓ أُرۡسِلَ إِلَيۡكُمۡ لَمَجۡنُونٞ\n(Фиръавн) деди: «Сизларга элчи қилиб юборилган бу пайғамбарингиз шак-шубҳасиз, жиннидир»'}, {'chapter': 26, 'verse': 28, 'text': 'قَالَ رَبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُمۡ تَعۡقِلُونَ\n(Мусо) айтди: «Агар ақл юргизувчи бўлсангизлар, У машриқ ва мағрибни ҳамда уларнинг ўртасидаги бор нарсаларнинг Парвардигоридир»'}, {'chapter': 26, 'verse': 29, 'text': 'قَالَ لَئِنِ ٱتَّخَذۡتَ إِلَٰهًا غَيۡرِي لَأَجۡعَلَنَّكَ مِنَ ٱلۡمَسۡجُونِينَ\n(Фиръавн) деди: «Қасамки, агар сен мендан ўзгани илоҳ қилиб оладиган бўлсанг, албатта мен сени зиндонбанд кимсалардан қилиб қўюрман!»'}, {'chapter': 26, 'verse': 30, 'text': 'قَالَ أَوَلَوۡ جِئۡتُكَ بِشَيۡءٖ مُّبِينٖ\n(Мусо) айтди: «Агар мен сенга (ўзимнинг ҳақ пайғамбар эканлигимни далолат қиладиган) очиқ нарса — мўъжиза келтирсам ҳам-а?»'}, {'chapter': 26, 'verse': 31, 'text': 'قَالَ فَأۡتِ بِهِۦٓ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\n(Фиръавн) деди: «Бас, агар ростгўйлардан бўлсанг, ўша (мўъжизангни) келтир-чи?!»'}, {'chapter': 26, 'verse': 32, 'text': 'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ\nШунда (Мусо) асосини ташлаган эди, баногоҳ у ростакам аждарга айланди'}, {'chapter': 26, 'verse': 33, 'text': 'وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّـٰظِرِينَ\nКейин қўлини (чўнтагидан) чиқарган эди, баногоҳ у қараб турганларга (кундан ҳам) оқ бўлиб кўринди, (ҳолбуки, Мусо ўзи қорамағиз одам эди)'}, {'chapter': 26, 'verse': 34, 'text': 'قَالَ لِلۡمَلَإِ حَوۡلَهُۥٓ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ\n(Фиръавн) атрофидаги одамларга: «Шубҳасиз, бу (Мусо) ўткир сеҳргардир'}, {'chapter': 26, 'verse': 35, 'text': 'يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِۦ فَمَاذَا تَأۡمُرُونَ\nУ ўз сеҳри билан сизларни ўз ерларингиздан чиқармоқчи, нима дейсизлар?», деди'}, {'chapter': 26, 'verse': 36, 'text': 'قَالُوٓاْ أَرۡجِهۡ وَأَخَاهُ وَٱبۡعَثۡ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ\nУлар айтишди: «Уни ва акаси (Ҳорун)ни қўйиб тургин-да, ҳамма шаҳарларга (сеҳргарларни йиғиб келадиган кишиларни жўнатгин)'}, {'chapter': 26, 'verse': 37, 'text': 'يَأۡتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٖ\nУлар айтишди: «Уни ва акаси (Ҳорун)ни қўйиб юбормай тургин-да, ҳамма шаҳарларга (сеҳргарларни йиғиб келадиган кишиларни жўнатгин)'}, {'chapter': 26, 'verse': 38, 'text': 'فَجُمِعَ ٱلسَّحَرَةُ لِمِيقَٰتِ يَوۡمٖ مَّعۡلُومٖ\nБас, сеҳргарлар маълум кунда белгиланган жойга тўпландилар'}, {'chapter': 26, 'verse': 39, 'text': 'وَقِيلَ لِلنَّاسِ هَلۡ أَنتُم مُّجۡتَمِعُونَ\nВа одамларга: «Сизлар ҳам тўпланиб бўлдингларми'}, {'chapter': 26, 'verse': 40, 'text': 'لَعَلَّنَا نَتَّبِعُ ٱلسَّحَرَةَ إِن كَانُواْ هُمُ ٱلۡغَٰلِبِينَ\nАгар сеҳргарлар ғолиб бўлиб чиқсалар, эҳтимол бизлар ўшаларга эргашурмиз», дейилди'}, {'chapter': 26, 'verse': 41, 'text': 'فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالُواْ لِفِرۡعَوۡنَ أَئِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَٰلِبِينَ\nЭнди қачонки, сеҳргарлар келишгач, Фиръавнга: «Агар биз ғолиб бўлсак, албатта бизлар учун (катта) мукофот бўлурми?», дейишди'}, {'chapter': 26, 'verse': 42, 'text': 'قَالَ نَعَمۡ وَإِنَّكُمۡ إِذٗا لَّمِنَ ٱلۡمُقَرَّبِينَ\nУ: «Ҳа, у ҳолда албатта сизлар менинг яқинларимдан бўлурсизлар», деди'}, {'chapter': 26, 'verse': 43, 'text': 'قَالَ لَهُم مُّوسَىٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ\nМусо уларга: «Ташлайдиган нарсаларингизни ташланглар», деди'}, {'chapter': 26, 'verse': 44, 'text': 'فَأَلۡقَوۡاْ حِبَالَهُمۡ وَعِصِيَّهُمۡ وَقَالُواْ بِعِزَّةِ فِرۡعَوۡنَ إِنَّا لَنَحۡنُ ٱلۡغَٰلِبُونَ\nБас, улар арқон ва асоларни (ерга) ташладилар ва: «Фиръавннинг қудратига қасамки, албатта бизларгина ғолиб бўлгувчидирмиз», дедилар'}, {'chapter': 26, 'verse': 45, 'text': 'فَأَلۡقَىٰ مُوسَىٰ عَصَاهُ فَإِذَا هِيَ تَلۡقَفُ مَا يَأۡفِكُونَ\nСўнг Мусо асосини ташлаган эди, баногоҳ у (асо) уларнинг «уйдирма»ларини юта бошлади'}, {'chapter': 26, 'verse': 46, 'text': 'فَأُلۡقِيَ ٱلسَّحَرَةُ سَٰجِدِينَ\nБас, у сеҳргарлар сажда қилган ҳолларида ерга ташланиб'}, {'chapter': 26, 'verse': 47, 'text': 'قَالُوٓاْ ءَامَنَّا بِرَبِّ ٱلۡعَٰلَمِينَ\n«Барча оламларнинг Парвардигорига —'}, {'chapter': 26, 'verse': 48, 'text': 'رَبِّ مُوسَىٰ وَهَٰرُونَ\nМусо ва Ҳоруннинг Парвардигорига иймон келтирдик», дедилар'}, {'chapter': 26, 'verse': 49, 'text': 'قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِي عَلَّمَكُمُ ٱلسِّحۡرَ فَلَسَوۡفَ تَعۡلَمُونَۚ لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ وَلَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ\n(Фиръавн) айтди: «Мен изн бермай туриб унга иймон келтирдингизми?! Албатта у (Мусо) сизларга сеҳр ўргатган каттангиздир. Энди яқинда билурсизлар — албатта мен оёқ-қўлларингизни қарама-қаршисига (ўнг қўл, чап оёқ ёки аксинча) кесурман ва сизларнинг барчангизни дорга осурман»'}, {'chapter': 26, 'verse': 50, 'text': 'قَالُواْ لَا ضَيۡرَۖ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ\nУлар дедилар: «Зарари йўқ. Зеро, бизлар Парвардигоримизга қайтгувчидирмиз'}, {'chapter': 26, 'verse': 51, 'text': 'إِنَّا نَطۡمَعُ أَن يَغۡفِرَ لَنَا رَبُّنَا خَطَٰيَٰنَآ أَن كُنَّآ أَوَّلَ ٱلۡمُؤۡمِنِينَ\nАлбатта бизлар (Мусога) биринчи иймон келтирган кишилар бўлганимиз сабабли Парвардигоримиз бизларнинг хато-гуноҳларимизни мағфират этишини умид қилурмиз»'}, {'chapter': 26, 'verse': 52, 'text': '۞وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَسۡرِ بِعِبَادِيٓ إِنَّكُم مُّتَّبَعُونَ\nБиз Мусога: «Бандаларим (яъни, Бани Исроил қавми) билан тунда йўлга чиққин! Албатта сизларнинг изингизга тушилур (яъни, Фиръавн ва унинг қўшини ортингиздан қувиб етур»), деб ваҳий юбордик'}, {'chapter': 26, 'verse': 53, 'text': 'فَأَرۡسَلَ فِرۡعَوۡنُ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ\nБас, (Мусо ва унинг қавми йўлга тушганларидан хабар топгач), Фиръавн барча шаҳарларга (аскар) йиғувчиларни жўнатди (ва деди)'}, {'chapter': 26, 'verse': 54, 'text': 'إِنَّ هَـٰٓؤُلَآءِ لَشِرۡذِمَةٞ قَلِيلُونَ\n«Аниқки улар бир ҳовуч ялангоёқлардир'}, {'chapter': 26, 'verse': 55, 'text': 'وَإِنَّهُمۡ لَنَا لَغَآئِظُونَ\nДарҳақиқат, улар (қўл остимиздан чиқиб кетишлари билан) бизларни ғазаблантирдилар'}, {'chapter': 26, 'verse': 56, 'text': 'وَإِنَّا لَجَمِيعٌ حَٰذِرُونَ\nШубҳасиз, бизлар эҳтиёт чораларини кўриб тургувчи қавмдирмиз»'}, {'chapter': 26, 'verse': 57, 'text': 'فَأَخۡرَجۡنَٰهُم مِّن جَنَّـٰتٖ وَعُيُونٖ\nБас, мана шундай қилиб Биз уларни (яъни, Фиръавн ва унга эргашганларни) боғлар ва булоқлардан'}, {'chapter': 26, 'verse': 58, 'text': 'وَكُنُوزٖ وَمَقَامٖ كَرِيمٖ\nХазиналар ва улуғ-гўзал маскандан айирдик'}, {'chapter': 26, 'verse': 59, 'text': 'كَذَٰلِكَۖ وَأَوۡرَثۡنَٰهَا بَنِيٓ إِسۡرَـٰٓءِيلَ\nВа улар(нинг барчаси)га Бани Исроилни ворис қилдик'}, {'chapter': 26, 'verse': 60, 'text': 'فَأَتۡبَعُوهُم مُّشۡرِقِينَ\nБас, (Фиръавн) тонг пайтида уларни қувиб етди'}, {'chapter': 26, 'verse': 61, 'text': 'فَلَمَّا تَرَـٰٓءَا ٱلۡجَمۡعَانِ قَالَ أَصۡحَٰبُ مُوسَىٰٓ إِنَّا لَمُدۡرَكُونَ\nЭнди қачонки икки жамоат бир-бирларини кўришгач, Мусонинг ҳамроҳлари: «Бизлар аниқ тутилдик, (чунки олдимизда ҳеч қандай йўл йўқ, фақат денгиз бор), дедилар'}, {'chapter': 26, 'verse': 62, 'text': 'قَالَ كَلَّآۖ إِنَّ مَعِيَ رَبِّي سَيَهۡدِينِ\n(Мусо) айтди: «Йўқ, аниқки мен билан бирга Парвардигорим бор. Албатта у мени (нажот) йўлига бошлар»'}, {'chapter': 26, 'verse': 63, 'text': 'فَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡبَحۡرَۖ فَٱنفَلَقَ فَكَانَ كُلُّ فِرۡقٖ كَٱلطَّوۡدِ ٱلۡعَظِيمِ\nБас, Биз Мусога: (Асоинг билан) денгизни ургин», деб ваҳий юбордик. Бас, (денгиз) бўлиниб, ҳар бир бўлак (сув) баланд тоғ каби бўлди. (Сўнг Мусо ва унинг қавми денгиз ўртасидан очилган йўлга тушдилар)'}, {'chapter': 26, 'verse': 64, 'text': 'وَأَزۡلَفۡنَا ثَمَّ ٱلۡأٓخَرِينَ\nВа кейингиларни (яъни, Фиръавн ва унинг қўшинини ҳам) ўша (йўлга) яқин қилдик'}, {'chapter': 26, 'verse': 65, 'text': 'وَأَنجَيۡنَا مُوسَىٰ وَمَن مَّعَهُۥٓ أَجۡمَعِينَ\nВа Мусо ҳамда у билан бирга бўлган кишиларнинг барчаларига нажот бердик'}, {'chapter': 26, 'verse': 66, 'text': 'ثُمَّ أَغۡرَقۡنَا ٱلۡأٓخَرِينَ\nСўнгра кейингиларни (денгизга) ғарқ қилиб юбордик'}, {'chapter': 26, 'verse': 67, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта бунда (Фиръавн ва унинг қавми ҳалокатида) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 68, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 69, 'text': 'وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ إِبۡرَٰهِيمَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларга Иброҳим хабарини тиловат қилинг'}, {'chapter': 26, 'verse': 70, 'text': 'إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَا تَعۡبُدُونَ\nЎшанда у отаси ва қавмига: «Нимага ибодат қилмоқдасизлар?», деганида'}, {'chapter': 26, 'verse': 71, 'text': 'قَالُواْ نَعۡبُدُ أَصۡنَامٗا فَنَظَلُّ لَهَا عَٰكِفِينَ\nУлар: «Бут-санамларга ибодат қилмоқдамиз. Бас, уларга содиқлигимизча қолурмиз», дедилар'}, {'chapter': 26, 'verse': 72, 'text': 'قَالَ هَلۡ يَسۡمَعُونَكُمۡ إِذۡ تَدۡعُونَ\n(Иброҳим) айтди: «Илтижо қилган пайтларингизда (бутларингиз) сизларни(нг илтижоларингизни) эшитдиларми'}, {'chapter': 26, 'verse': 73, 'text': 'أَوۡ يَنفَعُونَكُمۡ أَوۡ يَضُرُّونَ\nЁки сизларга фойда ё зиён етказа оладиларми?!»'}, {'chapter': 26, 'verse': 74, 'text': 'قَالُواْ بَلۡ وَجَدۡنَآ ءَابَآءَنَا كَذَٰلِكَ يَفۡعَلُونَ\nУлар дедилар: «Йўқ, бизлар ота-боболаримизнинг мана шундай (бутларга ибодат) қилишларини кўрганмиз (шунинг учун бизлар ҳам бутларга ибодат қиламиз)'}, {'chapter': 26, 'verse': 75, 'text': 'قَالَ أَفَرَءَيۡتُم مَّا كُنتُمۡ تَعۡبُدُونَ\n(Иброҳим) айтди: «Сизлар ибодат қилгувчи бўлган бутларингиз ҳақида ҳеч ўйлаб кўрдингларми'}, {'chapter': 26, 'verse': 76, 'text': 'أَنتُمۡ وَءَابَآؤُكُمُ ٱلۡأَقۡدَمُونَ\nЎзингиз ҳам, қадим-қадим ота-боболарингиз ҳам (ибодат қилгувчи бўлган бутларингиз ҳақида ҳеч ўйлаб кўрдингларми)'}, {'chapter': 26, 'verse': 77, 'text': 'فَإِنَّهُمۡ عَدُوّٞ لِّيٓ إِلَّا رَبَّ ٱلۡعَٰلَمِينَ\nШак-шубҳасиз, улар мен учун душмандир (бас, мен уларга ибодат қилмайман), магар барча оламлар Парвардигоригагина (ибодат қилурман)'}, {'chapter': 26, 'verse': 78, 'text': 'ٱلَّذِي خَلَقَنِي فَهُوَ يَهۡدِينِ\nУ мени яратгандир, бас, Ўзи мени ҳидоят қилур'}, {'chapter': 26, 'verse': 79, 'text': 'وَٱلَّذِي هُوَ يُطۡعِمُنِي وَيَسۡقِينِ\nУнинг Ўзигина мени тўйдирур ва қондирур'}, {'chapter': 26, 'verse': 80, 'text': 'وَإِذَا مَرِضۡتُ فَهُوَ يَشۡفِينِ\nКасал бўлган вақтимда Унинг Ўзи менга шифо берур'}, {'chapter': 26, 'verse': 81, 'text': 'وَٱلَّذِي يُمِيتُنِي ثُمَّ يُحۡيِينِ\nУ мени ўлдирур, сўнгра (Қиёмат Кунида қайта) тирилтирур'}, {'chapter': 26, 'verse': 82, 'text': 'وَٱلَّذِيٓ أَطۡمَعُ أَن يَغۡفِرَ لِي خَطِيٓـَٔتِي يَوۡمَ ٱلدِّينِ\nУ жазо (Қиёмат) кунида менинг хато-гуноҳларимни мағфират этишини умид қилурман'}, {'chapter': 26, 'verse': 83, 'text': 'رَبِّ هَبۡ لِي حُكۡمٗا وَأَلۡحِقۡنِي بِٱلصَّـٰلِحِينَ\nПарвардигорим, менга ҳикмат-илм ҳадя этгин ва мени солиҳ (бандаларинг) қаторига қўшгин'}, {'chapter': 26, 'verse': 84, 'text': 'وَٱجۡعَل لِّي لِسَانَ صِدۡقٖ فِي ٱلۡأٓخِرِينَ\nЯна мен учун кейин келгувчи кишилар ўртасида рост мақтовлар қилгин'}, {'chapter': 26, 'verse': 85, 'text': 'وَٱجۡعَلۡنِي مِن وَرَثَةِ جَنَّةِ ٱلنَّعِيمِ\nЯна мени ноз-неъматлар боғининг ворисларидан қилгин'}, {'chapter': 26, 'verse': 86, 'text': 'وَٱغۡفِرۡ لِأَبِيٓ إِنَّهُۥ كَانَ مِنَ ٱلضَّآلِّينَ\nОтамни ҳам мағфират қилгин. У шубҳасиз адашганлардан бўлди'}, {'chapter': 26, 'verse': 87, 'text': 'وَلَا تُخۡزِنِي يَوۡمَ يُبۡعَثُونَ\nВа (барча жонзот) қайта тириладиган Кунда мени шарманда қилмагин»'}, {'chapter': 26, 'verse': 88, 'text': 'يَوۡمَ لَا يَنفَعُ مَالٞ وَلَا بَنُونَ\nУ кунда на молу-давлат ва на бола-чақа фойда бермас'}, {'chapter': 26, 'verse': 89, 'text': 'إِلَّا مَنۡ أَتَى ٱللَّهَ بِقَلۡبٖ سَلِيمٖ\nМагар Аллоҳ ҳузурига тоза дил билан келган кишиларгагина (фойда берур)'}, {'chapter': 26, 'verse': 90, 'text': 'وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِينَ\n(У кунда) жаннат тақводор бўлган зотларга яқин қилинди'}, {'chapter': 26, 'verse': 91, 'text': 'وَبُرِّزَتِ ٱلۡجَحِيمُ لِلۡغَاوِينَ\nДўзах йўлдан озган кимсаларга кўрсатиб қўйилди'}, {'chapter': 26, 'verse': 92, 'text': 'وَقِيلَ لَهُمۡ أَيۡنَ مَا كُنتُمۡ تَعۡبُدُونَ\nВа уларга: «Сизлар Аллоҳни қўйиб ибодат қилиб ўтган бутларингиз қани'}, {'chapter': 26, 'verse': 93, 'text': 'مِن دُونِ ٱللَّهِ هَلۡ يَنصُرُونَكُمۡ أَوۡ يَنتَصِرُونَ\nУлар сизларга ёрдам бера олурми ёки ўзлари ёрдам ола билурми?!», дейилди'}, {'chapter': 26, 'verse': 94, 'text': 'فَكُبۡكِبُواْ فِيهَا هُمۡ وَٱلۡغَاوُۥنَ\nБас, у (бутлар) ва йўлдан оздирган кимсалар (дўзахга) улоқтирилди'}, {'chapter': 26, 'verse': 95, 'text': 'وَجُنُودُ إِبۡلِيسَ أَجۡمَعُونَ\nҲамда иблис лашкарлари (яъни, кишиларни Ҳақ йўлидан оздирган доҳийлар) барчалари (дўзахга улоқтирилди)'}, {'chapter': 26, 'verse': 96, 'text': 'قَالُواْ وَهُمۡ فِيهَا يَخۡتَصِمُونَ\nУлар (дўзахда) талашиб-тортишар эканлар, дедилар'}, {'chapter': 26, 'verse': 97, 'text': 'تَٱللَّهِ إِن كُنَّا لَفِي ضَلَٰلٖ مُّبِينٍ\n«Аллоҳ номига қасамки, албатта бизлар очиқ залолатда бўлган эканмиз'}, {'chapter': 26, 'verse': 98, 'text': 'إِذۡ نُسَوِّيكُم بِرَبِّ ٱلۡعَٰلَمِينَ\nСизларни (эй бут ва санамлар), барча оламларнинг Парвардигорига тенглаштирган пайтимизда'}, {'chapter': 26, 'verse': 99, 'text': 'وَمَآ أَضَلَّنَآ إِلَّا ٱلۡمُجۡرِمُونَ\nБизларни фақат жиноятчи («доҳий»)лар йўлдан оздирдилар'}, {'chapter': 26, 'verse': 100, 'text': 'فَمَا لَنَا مِن شَٰفِعِينَ\nЭнди бизлар учун на оқловчилар'}, {'chapter': 26, 'verse': 101, 'text': 'وَلَا صَدِيقٍ حَمِيمٖ\nВа на бирон қадрдон дўст бордир'}, {'chapter': 26, 'verse': 102, 'text': 'فَلَوۡ أَنَّ لَنَا كَرَّةٗ فَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nБас, қанийди яна бир марта (яшашнинг иложи) бўлса-ю, бизлар ҳам мўминлардан бўлсак»'}, {'chapter': 26, 'verse': 103, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда (яъни, Иброҳим ва унинг қавми ҳақидаги қиссада) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 104, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 105, 'text': 'كَذَّبَتۡ قَوۡمُ نُوحٍ ٱلۡمُرۡسَلِينَ\nНуҳ қавми пайғамбарларни ёлғончи қилди (яъни, Нуҳни ҳам, бошқа пайғамбарларни ҳам инкор қилдилар)'}, {'chapter': 26, 'verse': 106, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ نُوحٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Нуҳ айтган эди: «(Аллоҳдан) қўрқмайсизларми'}, {'chapter': 26, 'verse': 107, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта мен сизлар учун ишончли пайғамбардирман'}, {'chapter': 26, 'verse': 108, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 109, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен сизлардан бу (даъватим) учун ажр-мукофот сўрамайман. Менинг ажр-мукофотим фақат барча оламларнинг Парвардигори Аллоҳнинг зиммасидадир'}, {'chapter': 26, 'verse': 110, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар!»'}, {'chapter': 26, 'verse': 111, 'text': '۞قَالُوٓاْ أَنُؤۡمِنُ لَكَ وَٱتَّبَعَكَ ٱلۡأَرۡذَلُونَ\nУлар дедилар: «Сенга пасткаш (ялангоёқ) кимсалар эргашган бўлса, (бизлар сенга (қандай қилиб иймон келтирурмиз?»)'}, {'chapter': 26, 'verse': 112, 'text': 'قَالَ وَمَا عِلۡمِي بِمَا كَانُواْ يَعۡمَلُونَ\n(Нуҳ) айтди: «Мен уларнинг иш-амал қилгувчи эканликларини билмасман'}, {'chapter': 26, 'verse': 113, 'text': 'إِنۡ حِسَابُهُمۡ إِلَّا عَلَىٰ رَبِّيۖ لَوۡ تَشۡعُرُونَ\nАгар пайқай олсангизлар уларнинг ҳисоб-китоби ёлғиз Парвардигоримнинг зиммасидадир'}, {'chapter': 26, 'verse': 114, 'text': 'وَمَآ أَنَا۠ بِطَارِدِ ٱلۡمُؤۡمِنِينَ\nВа мен (сизнинг сўзингизга кириб) мўмин бўлган кишиларни (ҳузуримдан) қувмайман ҳам'}, {'chapter': 26, 'verse': 115, 'text': 'إِنۡ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ\nМен фақат очиқ огоҳлантиргувчидирман»'}, {'chapter': 26, 'verse': 116, 'text': 'قَالُواْ لَئِن لَّمۡ تَنتَهِ يَٰنُوحُ لَتَكُونَنَّ مِنَ ٱلۡمَرۡجُومِينَ\nУлар дедилар: «Қасамки, агар (бу даъватингдан) тўхтамасанг эй Нуҳ, албатта тошбўрон қилингувчилардан бўлурсан!»'}, {'chapter': 26, 'verse': 117, 'text': 'قَالَ رَبِّ إِنَّ قَوۡمِي كَذَّبُونِ\n(Шунда Нуҳ) айтди: «Парвардигорим, қавмим мени ёлғончи қилдилар'}, {'chapter': 26, 'verse': 118, 'text': 'فَٱفۡتَحۡ بَيۡنِي وَبَيۡنَهُمۡ فَتۡحٗا وَنَجِّنِي وَمَن مَّعِيَ مِنَ ٱلۡمُؤۡمِنِينَ\nЭнди Сен Ўзинг мен билан уларнинг орасини очиб қўйгин ва менга ҳамда мен билан бирга бўлган мўмин кишиларга нажот бергин»'}, {'chapter': 26, 'verse': 119, 'text': 'فَأَنجَيۡنَٰهُ وَمَن مَّعَهُۥ فِي ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\nБас, Биз унга ва у билан бирга бўлган кишиларга (одамлар, ҳайвонлар ва паррандалар билан) лиқ тўла бўлган кемада нажот бердик'}, {'chapter': 26, 'verse': 120, 'text': 'ثُمَّ أَغۡرَقۡنَا بَعۡدُ ٱلۡبَاقِينَ\nСўнгра, (уларга нажот берганимиздан кейин) қолганларни ғарқ қилдик'}, {'chapter': 26, 'verse': 121, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда (яъни, Нуҳ ва унинг қавми ҳақидаги қиссада) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 122, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 123, 'text': 'كَذَّبَتۡ عَادٌ ٱلۡمُرۡسَلِينَ\nОд (қабиласи) пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 124, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ هُودٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Ҳуд айтган эди: (Аллоҳдан) қўрқмайсизларми'}, {'chapter': 26, 'verse': 125, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта мен сизлар учун ишончли пайғамбардирман'}, {'chapter': 26, 'verse': 126, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 127, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен бу (даъватим) учун сизлардан ажр-мукофот сўрамайман. Менинг ажр-мукофотим фақат барча оламларнинг Парвардигори зиммасидадир'}, {'chapter': 26, 'verse': 128, 'text': 'أَتَبۡنُونَ بِكُلِّ رِيعٍ ءَايَةٗ تَعۡبَثُونَ\nСизлар ҳар бир тепаликка ўйин-кулги учун бир белги — баланд бино қураверасизларми'}, {'chapter': 26, 'verse': 129, 'text': 'وَتَتَّخِذُونَ مَصَانِعَ لَعَلَّكُمۡ تَخۡلُدُونَ\nВа гўё мангу яшаб қоладигандек қаср-саройлар соласизлар'}, {'chapter': 26, 'verse': 130, 'text': 'وَإِذَا بَطَشۡتُم بَطَشۡتُمۡ جَبَّارِينَ\nҚачон (бирон кишини жазолаш учун) ушласангизлар, бераҳмларча ушлайсизлар'}, {'chapter': 26, 'verse': 131, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 132, 'text': 'وَٱتَّقُواْ ٱلَّذِيٓ أَمَدَّكُم بِمَا تَعۡلَمُونَ\nВа сизларни ўзларингиз биладиган неъматлар билан қўллаган-сийлаган Зотдан (Аллоҳдан) қўрқингиз'}, {'chapter': 26, 'verse': 133, 'text': 'أَمَدَّكُم بِأَنۡعَٰمٖ وَبَنِينَ\nУ сизларни чорва ҳайвонлари ва ўғиллар билан'}, {'chapter': 26, 'verse': 134, 'text': 'وَجَنَّـٰتٖ وَعُيُونٍ\nБоғлар ва булоқлар билан қўллади-ку'}, {'chapter': 26, 'verse': 135, 'text': 'إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ\nАлбатта, мен сизларнинг устингизга улуғ куннинг — Қиёматнинг азоби тушишидан қўрқурман»'}, {'chapter': 26, 'verse': 136, 'text': 'قَالُواْ سَوَآءٌ عَلَيۡنَآ أَوَعَظۡتَ أَمۡ لَمۡ تَكُن مِّنَ ٱلۡوَٰعِظِينَ\nУлар дедилар: «Сенинг ваъз-насиҳат қилишинг ёки қилгувчилардан бўлмаслигинг бизлар учун баробардир (яъни, бизларга панд-насиҳат қилиб овора бўлма)'}, {'chapter': 26, 'verse': 137, 'text': 'إِنۡ هَٰذَآ إِلَّا خُلُقُ ٱلۡأَوَّلِينَ\nАлбатта бу (айш-ишратга берилиб умр ўтказиш) аввалгилардан қолган одатдир'}, {'chapter': 26, 'verse': 138, 'text': 'وَمَا نَحۡنُ بِمُعَذَّبِينَ\nБизлар азоблангувчи эмасмиз»'}, {'chapter': 26, 'verse': 139, 'text': 'فَكَذَّبُوهُ فَأَهۡلَكۡنَٰهُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nБас, улар (Ҳудни) ёлғончи қилишгач, Биз уларни ҳалок этдик. Албатта бунда (яъни, Ҳуд ва унинг қавми бўлмиш Од қабиласи ҳақидаги қиссада) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 140, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 141, 'text': 'كَذَّبَتۡ ثَمُودُ ٱلۡمُرۡسَلِينَ\nСамуд (қабиласи) пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 142, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ صَٰلِحٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Солиҳ айтган эди: (Аллоҳдан) қўрқмайсизларми'}, {'chapter': 26, 'verse': 143, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта мен сизлар учун ишончли пайғамбардирман'}, {'chapter': 26, 'verse': 144, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 145, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен бу (даъватим) учун сизлардан ажр-мукофот сўрамайман. Менинг ажр-муофотим фақат барча оламларнинг Парвардигори зиммасидадир'}, {'chapter': 26, 'verse': 146, 'text': 'أَتُتۡرَكُونَ فِي مَا هَٰهُنَآ ءَامِنِينَ\nСизлар бу ердаги нарсаларда қўйиб қўйилурмисиз'}, {'chapter': 26, 'verse': 147, 'text': 'فِي جَنَّـٰتٖ وَعُيُونٖ\nБоғлару булоқларда'}, {'chapter': 26, 'verse': 148, 'text': 'وَزُرُوعٖ وَنَخۡلٖ طَلۡعُهَا هَضِيمٞ\nЭкинлар ва новдалари мулойим хурмо дарахтлари ичида (қўйиб қўйилурмисиз яъни, шундай нозу неъматлардан фойдаланиб мангу яшайверамиз, деб ўйлайсизларми)'}, {'chapter': 26, 'verse': 149, 'text': 'وَتَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُيُوتٗا فَٰرِهِينَ\nЯна моҳирлик билан тоғлардан уйлар ҳам йўнмоқдасизлар (ясамоқдасизлар)'}, {'chapter': 26, 'verse': 150, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 151, 'text': 'وَلَا تُطِيعُوٓاْ أَمۡرَ ٱلۡمُسۡرِفِينَ\nВа бузғунчи кимсаларнинг амрига итоат этманглар'}, {'chapter': 26, 'verse': 152, 'text': 'ٱلَّذِينَ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ\nУлар Ер юзида бузғунчилик қиладиган ва (ҳеч нарсани) ўнглай олмайдиган (ҳаддан ошувчи) кимсалардир»'}, {'chapter': 26, 'verse': 153, 'text': 'قَالُوٓاْ إِنَّمَآ أَنتَ مِنَ ٱلۡمُسَحَّرِينَ\nУлар дедилар: «Ҳеч шак-шубҳасиз, сен сеҳрланган — ақлдан озган кимсалардандирсан'}, {'chapter': 26, 'verse': 154, 'text': 'مَآ أَنتَ إِلَّا بَشَرٞ مِّثۡلُنَا فَأۡتِ بِـَٔايَةٍ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nСен ҳам худди бизларга ўхшаган одамдирсан. Бас, агар (пайғамбарман деган даъвойингда) ростгўйлардан бўлсанг, бирон оят-мўъжиза келтир!»'}, {'chapter': 26, 'verse': 155, 'text': 'قَالَ هَٰذِهِۦ نَاقَةٞ لَّهَا شِرۡبٞ وَلَكُمۡ شِرۡبُ يَوۡمٖ مَّعۡلُومٖ\nУ айтди: «(Менинг мўъжизам) мана шу туядир. (Маълум бир кун сув) ичиш навбати уникидир. Маълум бир кун эса сизларнинг ичиш навбатингиздир'}, {'chapter': 26, 'verse': 156, 'text': 'وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابُ يَوۡمٍ عَظِيمٖ\nЯна унга бирон ёмонлик етказмангларки, у ҳолда сизларни улуғ Куннинг — Қиёматнинг азоби ушлар»'}, {'chapter': 26, 'verse': 157, 'text': 'فَعَقَرُوهَا فَأَصۡبَحُواْ نَٰدِمِينَ\nБас, улар (туяни) сўйдилару, надомат қилгувчиларга айландилар'}, {'chapter': 26, 'verse': 158, 'text': 'فَأَخَذَهُمُ ٱلۡعَذَابُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nУларни азоб ушлади. Албатта бунда (яъни, Солиҳ ва унинг қавми ҳақидаги қиссада) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 159, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 160, 'text': 'كَذَّبَتۡ قَوۡمُ لُوطٍ ٱلۡمُرۡسَلِينَ\nЛут қавми пайғамбарларни ёлғончи қилди'}, {'chapter': 26, 'verse': 161, 'text': 'إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ لُوطٌ أَلَا تَتَّقُونَ\nЎшанда уларга биродарлари Лут айтган эди: «(Аллоҳдан) қўрқмайсизларми'}, {'chapter': 26, 'verse': 162, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта мен сизлар учун ишончли пайғамбардирман'}, {'chapter': 26, 'verse': 163, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 164, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен бу (даъватим) учун сизлардан ажр-мукофот сўрамайман. Менинг мукофотим фақат барча оламларнинг Парвардигори зиммасидадир'}, {'chapter': 26, 'verse': 165, 'text': 'أَتَأۡتُونَ ٱلذُّكۡرَانَ مِنَ ٱلۡعَٰلَمِينَ\nСизлар бутун оламлардан (ажраб — ҳеч бир жонзот қилмаган ишни қилиб) эркакларга яқинлашурмисизлар'}, {'chapter': 26, 'verse': 166, 'text': 'وَتَذَرُونَ مَا خَلَقَ لَكُمۡ رَبُّكُم مِّنۡ أَزۡوَٰجِكُمۚ بَلۡ أَنتُمۡ قَوۡمٌ عَادُونَ\nВа Парвардигорингиз сизлар учун яратган жуфти ҳалолларингизни тарк қилурмисиз?! Йўқ, сизлар ҳаддан ошувчи қавмдирсизлар»'}, {'chapter': 26, 'verse': 167, 'text': 'قَالُواْ لَئِن لَّمۡ تَنتَهِ يَٰلُوطُ لَتَكُونَنَّ مِنَ ٱلۡمُخۡرَجِينَ\nУлар дедилар: «Қасамки, агар тўхтамасанг эй Лут, албатта сургун қилингувчилардан бўлурсан»'}, {'chapter': 26, 'verse': 168, 'text': 'قَالَ إِنِّي لِعَمَلِكُم مِّنَ ٱلۡقَالِينَ\nУ айтди: «Албатта мен сизларнинг (бу) ишларингизни ёмон кўргувчиларданман'}, {'chapter': 26, 'verse': 169, 'text': 'رَبِّ نَجِّنِي وَأَهۡلِي مِمَّا يَعۡمَلُونَ\nПарвардигорим, менга ва аҳлимга (яъни, менга иймон келтирган кишиларга) улар (кофирларнинг) қилаётган амаллари (оқибати)дан нажот бергин»'}, {'chapter': 26, 'verse': 170, 'text': 'فَنَجَّيۡنَٰهُ وَأَهۡلَهُۥٓ أَجۡمَعِينَ\nБас, Биз унга ва унинг барча аҳли тобеъларига нажот бердик'}, {'chapter': 26, 'verse': 171, 'text': 'إِلَّا عَجُوزٗا فِي ٱلۡغَٰبِرِينَ\nМагар (азоб остида) қолгувчилардан бўлган бир кампирга (яъни, Лутнинг иймонсиз бўлган хотинига нажот бермадик)'}, {'chapter': 26, 'verse': 172, 'text': 'ثُمَّ دَمَّرۡنَا ٱلۡأٓخَرِينَ\nСўнгра бошқаларни ҳам ҳалок қилдик'}, {'chapter': 26, 'verse': 173, 'text': 'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ\nВа уларнинг устига (тош) ёмғир ёғдирдик. Бас, (азоб ҳақида) огоҳлантирилган (лекин ўзларининг ярамас қилмишларидан қайтмаган) кимсаларнинг ёмғири нақадар ёмон бўлди'}, {'chapter': 26, 'verse': 174, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта бунда (Лут ва унинг қавми ҳақидаги қиссада) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 175, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 176, 'text': 'كَذَّبَ أَصۡحَٰبُ لۡـَٔيۡكَةِ ٱلۡمُرۡسَلِينَ\nДарахтзор эгалари (яъни, Шуъайб пайғамбар қавми) пайғамбарларни ёлғончи қилдилар'}, {'chapter': 26, 'verse': 177, 'text': 'إِذۡ قَالَ لَهُمۡ شُعَيۡبٌ أَلَا تَتَّقُونَ\nЎшанда уларга Шуъайб айтган эди: (Аллоҳдан) қўрқмайсизларми'}, {'chapter': 26, 'verse': 178, 'text': 'إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\nАлбатта мен сизлар учун ишончли пайғамбардирман'}, {'chapter': 26, 'verse': 179, 'text': 'فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nБас, Аллоҳдан қўрқинглар ва менга итоат этинглар'}, {'chapter': 26, 'verse': 180, 'text': 'وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ\nМен бу (даъватим) учун сизлардан ажр-мукофот сўрамайман. Менинг ажр-мукофотим фақат барча оламларнинг Парвардигори зиммасидадир'}, {'chapter': 26, 'verse': 181, 'text': '۞أَوۡفُواْ ٱلۡكَيۡلَ وَلَا تَكُونُواْ مِنَ ٱلۡمُخۡسِرِينَ\nЎлчовни тўла ўлчанглар ва камайтириб (тортгувчилардан) бўлманглар'}, {'chapter': 26, 'verse': 182, 'text': 'وَزِنُواْ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِيمِ\nВа тўғри тарози билан тортинглар'}, {'chapter': 26, 'verse': 183, 'text': 'وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nОдамларга нарсаларини камайтириб берманглар ва Ер юзида бузғунчилик қилиб санқиб юрманглар'}, {'chapter': 26, 'verse': 184, 'text': 'وَٱتَّقُواْ ٱلَّذِي خَلَقَكُمۡ وَٱلۡجِبِلَّةَ ٱلۡأَوَّلِينَ\nҲамда сизларни ҳам (сизлардан) аввал яралган умматларни ҳам яратган зот — Аллоҳдан қўрқинглар!»'}, {'chapter': 26, 'verse': 185, 'text': 'قَالُوٓاْ إِنَّمَآ أَنتَ مِنَ ٱلۡمُسَحَّرِينَ\nУлар дедилар: «Ҳеч шак-шубҳасиз, сен сеҳрланган — ақлдан озган кимсалардандирсан'}, {'chapter': 26, 'verse': 186, 'text': 'وَمَآ أَنتَ إِلَّا بَشَرٞ مِّثۡلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلۡكَٰذِبِينَ\nСен ҳам худди бизларга ўхшаган одамдирсан. Албатта бизлар сени ёлғончи кимсалардан, деб гумон қилурмиз'}, {'chapter': 26, 'verse': 187, 'text': 'فَأَسۡقِطۡ عَلَيۡنَا كِسَفٗا مِّنَ ٱلسَّمَآءِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nБас, агар ростгўйлардан бўлсанг, устимизга осмондан бир бўлагини ташлаб юбор!»'}, {'chapter': 26, 'verse': 188, 'text': 'قَالَ رَبِّيٓ أَعۡلَمُ بِمَا تَعۡمَلُونَ\nУ айтди: «Парвардигорим Ўзи сизларнинг қилаётган амалларингизни жуда яхши билгувчидир (яъни, сизлар қандай азобга мустаҳиқ бўлсангиз, Аллоҳ ўзи билиб жазоингизни бергувчидир)»'}, {'chapter': 26, 'verse': 189, 'text': 'فَكَذَّبُوهُ فَأَخَذَهُمۡ عَذَابُ يَوۡمِ ٱلظُّلَّةِۚ إِنَّهُۥ كَانَ عَذَابَ يَوۡمٍ عَظِيمٍ\nБас, улар (Шуъайбни) ёлғончи қилишгач, уларни «соябон» кунининг азоби ушлади. Дарҳақиқат, у улуғ-даҳшатли куннинг азоби эди'}, {'chapter': 26, 'verse': 190, 'text': 'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ\nАлбатта, бунда (яъни, Шуъайб ва унинг қавми ҳақидаги қиссада) оят-ибрат бордир. (Лекин одамларнинг) кўплари иймон келтиргувчи бўлмадилар'}, {'chapter': 26, 'verse': 191, 'text': 'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nШак-шубҳасиз, Парвардигорингизнинг Ўзи (кофирлар устидан) ғолиб, (мўминларга) меҳрибондир'}, {'chapter': 26, 'verse': 192, 'text': 'وَإِنَّهُۥ لَتَنزِيلُ رَبِّ ٱلۡعَٰلَمِينَ\nАлбатта (бу Қуръон) барча оламлар Парвардигори томонидан нозил қилингандир'}, {'chapter': 26, 'verse': 193, 'text': 'نَزَلَ بِهِ ٱلرُّوحُ ٱلۡأَمِينُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уни (яъни, Қуръонни) Руҳул-Амин — Жаброил нозил қилди'}, {'chapter': 26, 'verse': 194, 'text': 'عَلَىٰ قَلۡبِكَ لِتَكُونَ مِنَ ٱلۡمُنذِرِينَ\nСиз (Охират азобидан) огоҳлантиргувчилардан бўлишингиз учун қалбингизга'}, {'chapter': 26, 'verse': 195, 'text': 'بِلِسَانٍ عَرَبِيّٖ مُّبِينٖ\nОчиқ-равшан арабий тил билан (нозил қилди)'}, {'chapter': 26, 'verse': 196, 'text': 'وَإِنَّهُۥ لَفِي زُبُرِ ٱلۡأَوَّلِينَ\nАлбатта (Қуръон ҳақидаги хабарлар) ўтганларнинг китобларида ҳам бордир'}, {'chapter': 26, 'verse': 197, 'text': 'أَوَلَمۡ يَكُن لَّهُمۡ ءَايَةً أَن يَعۡلَمَهُۥ عُلَمَـٰٓؤُاْ بَنِيٓ إِسۡرَـٰٓءِيلَ\nАхир (Қуръон ҳақида) Бани Исроил олимлари ҳам билишлари (Макка мушриклари) учун (Қуръон ҳақиқатан Аллоҳ томонидан нозил бўлганига) оят-далил эмасми'}, {'chapter': 26, 'verse': 198, 'text': 'وَلَوۡ نَزَّلۡنَٰهُ عَلَىٰ بَعۡضِ ٱلۡأَعۡجَمِينَ\nАгар Биз (Қуръонни) ажамлардан (яъни, араб бўлмаган кишилардан) бирига нозил қилсак'}, {'chapter': 26, 'verse': 199, 'text': 'فَقَرَأَهُۥ عَلَيۡهِم مَّا كَانُواْ بِهِۦ مُؤۡمِنِينَ\nБас, у (Қуръонни) уларга ўқиб берса, унга ҳам иймон келтиргувчи бўлмас эдилар'}, {'chapter': 26, 'verse': 200, 'text': 'كَذَٰلِكَ سَلَكۡنَٰهُ فِي قُلُوبِ ٱلۡمُجۡرِمِينَ\n(Пайғамбарларни ёлғончи қилишни) жиноятчи кимсаларнинг дилларига мана шундай йўллаб-киритиб қўйганмиз'}, {'chapter': 26, 'verse': 201, 'text': 'لَا يُؤۡمِنُونَ بِهِۦ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nУлар то аламли азобни кўрмагунларича, (Қуръонга) иймон келтирмайдилар'}, {'chapter': 26, 'verse': 202, 'text': 'فَيَأۡتِيَهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nБас, у (азоб) уларга ўзлари сезмаган ҳолларида тўсатдан келур'}, {'chapter': 26, 'verse': 203, 'text': 'فَيَقُولُواْ هَلۡ نَحۡنُ مُنظَرُونَ\nСўнг улар: «Бизларга (бир оз) муҳлат берилармикан, (албатта иймон келтирган бўлур эдик)», деб қолурлар'}, {'chapter': 26, 'verse': 204, 'text': 'أَفَبِعَذَابِنَا يَسۡتَعۡجِلُونَ\nБас, улар Бизнинг азобимизни шоштирурларми'}, {'chapter': 26, 'verse': 205, 'text': 'أَفَرَءَيۡتَ إِن مَّتَّعۡنَٰهُمۡ سِنِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), хабар беринг-чи, агар Биз уларни (узоқ) йиллар (сиҳат-саломатлик, мол-давлат билан) фойдалантирсак-да'}, {'chapter': 26, 'verse': 206, 'text': 'ثُمَّ جَآءَهُم مَّا كَانُواْ يُوعَدُونَ\nСўнгра уларга ваъда қилинган нарсалар (яъни, азоб) келса'}, {'chapter': 26, 'verse': 207, 'text': 'مَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يُمَتَّعُونَ\n(Ўша пайт) уларга (ҳаёти дунёда) фойдаланган нарсалари асқотурми'}, {'chapter': 26, 'verse': 208, 'text': 'وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا لَهَا مُنذِرُونَ\nБиз бирон қишлоқ-шаҳарни ҳалок қилмадик, магар унинг учун (азоб-ҳалокатдан) огоҳлантиргувчи (пайғамбарлар) бўлган'}, {'chapter': 26, 'verse': 209, 'text': 'ذِكۡرَىٰ وَمَا كُنَّا ظَٰلِمِينَ\n(Ўшанда у қишлоқ аҳли ўз пайғамбарларини ёлғончи қилганларидан кейингина келажак авлод учун) эслатма-ибрат бўлсин, деб (ҳалок қилдик). Ва Биз золим бўлмадик'}, {'chapter': 26, 'verse': 210, 'text': 'وَمَا تَنَزَّلَتۡ بِهِ ٱلشَّيَٰطِينُ\n(Қуръонни осмондан) жинлар олиб тушганлари йўқ'}, {'chapter': 26, 'verse': 211, 'text': 'وَمَا يَنۢبَغِي لَهُمۡ وَمَا يَسۡتَطِيعُونَ\nУлар учун бунинг имкони йўқ ва улар бунга қодир шахслар эмаслар'}, {'chapter': 26, 'verse': 212, 'text': 'إِنَّهُمۡ عَنِ ٱلسَّمۡعِ لَمَعۡزُولُونَ\nЧунки улар (ваҳийни ўғринча) эшитиб олишдан четлатилганлари аниқдир'}, {'chapter': 26, 'verse': 213, 'text': 'فَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَكُونَ مِنَ ٱلۡمُعَذَّبِينَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз Аллоҳ билан бирга бошқа бирон «илоҳ»га илтижо қилманг! Акс ҳолда азоблангувчи кимсалардан бўлиб қолурсиз'}, {'chapter': 26, 'verse': 214, 'text': 'وَأَنذِرۡ عَشِيرَتَكَ ٱلۡأَقۡرَبِينَ\nВа яқин қариндош-уруғларингизни (Аллоҳнинг азобидан) огоҳлантиринг'}, {'chapter': 26, 'verse': 215, 'text': 'وَٱخۡفِضۡ جَنَاحَكَ لِمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِينَ\nЎзингизга эргашган мўминлар учун қанотингизни паст тутинг (яъни, уларга хушхулқ билан камтарона муомалада бўлинг)'}, {'chapter': 26, 'verse': 216, 'text': 'فَإِنۡ عَصَوۡكَ فَقُلۡ إِنِّي بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ\nБас, агар улар итоатсизлик қилсалар, у ҳолда айтинг: «Албатта мен сизларнинг амалингиздан покдирман»'}, {'chapter': 26, 'verse': 217, 'text': 'وَتَوَكَّلۡ عَلَى ٱلۡعَزِيزِ ٱلرَّحِيمِ\nҒолиб ва меҳрибон (Аллоҳ)га таваккул қилинг'}, {'chapter': 26, 'verse': 218, 'text': 'ٱلَّذِي يَرَىٰكَ حِينَ تَقُومُ\nУ сизни ўзингиз (намоз учун) тураётган вақтингизда ҳам кўриб турар'}, {'chapter': 26, 'verse': 219, 'text': 'وَتَقَلُّبَكَ فِي ٱلسَّـٰجِدِينَ\nСажда қилгувчилар (намоз ўқувчилар) орасида (имом бўлган ҳолингизда намоз рукнларининг биридан иккинчисига) кўчаётган (вақтингизда ҳам кўриб турар)'}, {'chapter': 26, 'verse': 220, 'text': 'إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nШак-шубҳасиз, У эшитгувчи, билгувчи Зотдир'}, {'chapter': 26, 'verse': 221, 'text': 'هَلۡ أُنَبِّئُكُمۡ عَلَىٰ مَن تَنَزَّلُ ٱلشَّيَٰطِينُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен сизларга жинлар кимларга тушиши ҳақида хабар берайми'}, {'chapter': 26, 'verse': 222, 'text': 'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٖ\nУлар барча гуноҳга ботган товламачиларга тушиб'}, {'chapter': 26, 'verse': 223, 'text': 'يُلۡقُونَ ٱلسَّمۡعَ وَأَكۡثَرُهُمۡ كَٰذِبُونَ\nЭшитиб олганларини уларга ташларлар. Уларнинг (жинларнинг) кўплари ёлғончилардир»'}, {'chapter': 26, 'verse': 224, 'text': 'وَٱلشُّعَرَآءُ يَتَّبِعُهُمُ ٱلۡغَاوُۥنَ\nШоирларга йўлдан озганлар эргашур'}, {'chapter': 26, 'verse': 225, 'text': 'أَلَمۡ تَرَ أَنَّهُمۡ فِي كُلِّ وَادٖ يَهِيمُونَ\nУларнинг (сўз водийларидан) ҳар водийда дайдишларини (яъни, ўзларига ёқиб қолган энг тубан кимса ё нарсаларни ҳам кўкка кўтариб, ёқтирмаганларини тупроққа қоришларини) кўрмадингизми'}, {'chapter': 26, 'verse': 226, 'text': 'وَأَنَّهُمۡ يَقُولُونَ مَا لَا يَفۡعَلُونَ\nВа ўзлари қилмайдиган нарсаларни айтишларини (кўрмадингизми)'}, {'chapter': 26, 'verse': 227, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَذَكَرُواْ ٱللَّهَ كَثِيرٗا وَٱنتَصَرُواْ مِنۢ بَعۡدِ مَا ظُلِمُواْۗ وَسَيَعۡلَمُ ٱلَّذِينَ ظَلَمُوٓاْ أَيَّ مُنقَلَبٖ يَنقَلِبُونَ\nМагар иймон келтирган ва яхши амаллар қилган ҳамда доим Аллоҳни ёдда тутган ва (илгари) мазлум бўлганларидан кейин (Ислом зафар топгач), ғолиб бўлган кишилар (яъни, шоирлар йўлдан оздиргувчи эмаслар). Золим кимсалар эса яқинида қандай оқибатга қараб кетаётганларини билиб қолурлар'}, {'chapter': 27, 'verse': 1, 'text': 'طسٓۚ تِلۡكَ ءَايَٰتُ ٱلۡقُرۡءَانِ وَكِتَابٖ مُّبِينٍ\nТо, Син. Ушбу (оятлар) Қуръон — очиқ Китоб оятларидир'}, {'chapter': 27, 'verse': 2, 'text': 'هُدٗى وَبُشۡرَىٰ لِلۡمُؤۡمِنِينَ\n(Бу оятлар) мўминлар учун ҳидоят ва хушхабардир'}, {'chapter': 27, 'verse': 3, 'text': 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ\nУлар намозни тўкис адо этадиган, закотни (ҳақдорларга) ато этадиган ва Охиратга аниқ ишонувчи бўлган кишилардир'}, {'chapter': 27, 'verse': 4, 'text': 'إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ زَيَّنَّا لَهُمۡ أَعۡمَٰلَهُمۡ فَهُمۡ يَعۡمَهُونَ\nАлбатта, Охиратга иймон келтирмайдиган кимсалар учун Биз (қилаётган) амалларини чиройли кўрсатиб қўйгандирмиз. Бас, улар адашиб-улоқиб юраверадилар'}, {'chapter': 27, 'verse': 5, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَهُمۡ سُوٓءُ ٱلۡعَذَابِ وَهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ\nАна ўша кимсалар учун (дунёда ҳам) энг ёмон азоб бордир ва улар Охиратда энг кўп зиён кўргувчи кимсалардир'}, {'chapter': 27, 'verse': 6, 'text': 'وَإِنَّكَ لَتُلَقَّى ٱلۡقُرۡءَانَ مِن لَّدُنۡ حَكِيمٍ عَلِيمٍ\n(Эй Муҳаммад алайҳис-салоту вас-салом), шак-шубҳасиз, сизга бу Қуръон доно ва билгувчи Зот даргоҳидан берилур'}, {'chapter': 27, 'verse': 7, 'text': 'إِذۡ قَالَ مُوسَىٰ لِأَهۡلِهِۦٓ إِنِّيٓ ءَانَسۡتُ نَارٗا سَـَٔاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ ءَاتِيكُم بِشِهَابٖ قَبَسٖ لَّعَلَّكُمۡ تَصۡطَلُونَ\nМусо аҳли-оиласига: «Мен олов кўриб қолдим. Сизларга ундан бирон хабар келтирурман ёки сизларга исиниб олишингиз учун бир ўт-чўғ келтирурман», деганини эсланг'}, {'chapter': 27, 'verse': 8, 'text': 'فَلَمَّا جَآءَهَا نُودِيَ أَنۢ بُورِكَ مَن فِي ٱلنَّارِ وَمَنۡ حَوۡلَهَا وَسُبۡحَٰنَ ٱللَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nЭнди қачонки у олов олдига келгач, унга нидо қилинди: «Олов олдидаги кишига (яъни, Мусога) ва унинг атрофидаги зотларга (фаришталарга) хайру-баракот бўлсин. Барча оламларнинг Парвардигори — Аллоҳ (айбу-нуқсондан) покдир'}, {'chapter': 27, 'verse': 9, 'text': 'يَٰمُوسَىٰٓ إِنَّهُۥٓ أَنَا ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nЭй Мусо, шак-шубҳасиз, Мен ғолиб ва ҳикмат соҳиби — Аллоҳдирман'}, {'chapter': 27, 'verse': 10, 'text': 'وَأَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰ لَا تَخَفۡ إِنِّي لَا يَخَافُ لَدَيَّ ٱلۡمُرۡسَلُونَ\nАсоингни ташлагин!» Бас, қачонки (Мусо) унинг илондек қимирлаётганини кўргач, ортига қарамай қочди. (Шунда Биз дедик): Эй Мусо, қўрқмагин. Зеро, Менинг хузуримда пайғамбарларим қўрқмаслар'}, {'chapter': 27, 'verse': 11, 'text': 'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسۡنَۢا بَعۡدَ سُوٓءٖ فَإِنِّي غَفُورٞ رَّحِيمٞ\nЛекин ким зулм қилса-ю, сўнгра — ёмонликдан кейин, уни чиройли (амалга) алмаштирса, бас, албатта Мен мағфиратли, меҳрибондирман'}, {'chapter': 27, 'verse': 12, 'text': 'وَأَدۡخِلۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖۖ فِي تِسۡعِ ءَايَٰتٍ إِلَىٰ فِرۡعَوۡنَ وَقَوۡمِهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nҚўлингни чўнтагингга солгин, у ҳеч қандай дардсиз оппоқ бўлиб чиқур. (Бу мўъжизалар) сен (Фиръан ва унинг қавмига) олиб борадиган тўққиз оят-мўъжиза ичида (бордир). Дарҳақиқат, улар итоатсиз қавм бўлдилар'}, {'chapter': 27, 'verse': 13, 'text': 'فَلَمَّا جَآءَتۡهُمۡ ءَايَٰتُنَا مُبۡصِرَةٗ قَالُواْ هَٰذَا سِحۡرٞ مُّبِينٞ\nБас, қачонки уларга Бизнинг оят-мўъжизаларимиз равшан ҳолда етиб келгач, улар: «Бу очиқ сеҳрдир», дедилар'}, {'chapter': 27, 'verse': 14, 'text': 'وَجَحَدُواْ بِهَا وَٱسۡتَيۡقَنَتۡهَآ أَنفُسُهُمۡ ظُلۡمٗا وَعُلُوّٗاۚ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ\nВа ўзлари аниқ билган ҳолларида зулм ва кибр қилиб, у (мўъжизаларни) инкор этдилар. Энди (эй Муҳаммад алайҳис-салоту вас-салом), у бузғунчи кимсаларнинг оқибати қандай бўлганини кўринг'}, {'chapter': 27, 'verse': 15, 'text': 'وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ وَسُلَيۡمَٰنَ عِلۡمٗاۖ وَقَالَا ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي فَضَّلَنَا عَلَىٰ كَثِيرٖ مِّنۡ عِبَادِهِ ٱلۡمُؤۡمِنِينَ\nҚасамки, Биз Довуд ва Сулаймонга (етук) билим ато этдик ва улар: «Бизларни кўп мўмин бандаларидан афзал қилиб қўйган Зот — Аллоҳга ҳамду сано бўлсин», дедилар'}, {'chapter': 27, 'verse': 16, 'text': 'وَوَرِثَ سُلَيۡمَٰنُ دَاوُۥدَۖ وَقَالَ يَـٰٓأَيُّهَا ٱلنَّاسُ عُلِّمۡنَا مَنطِقَ ٱلطَّيۡرِ وَأُوتِينَا مِن كُلِّ شَيۡءٍۖ إِنَّ هَٰذَا لَهُوَ ٱلۡفَضۡلُ ٱلۡمُبِينُ\nСулаймон (пайғамбарлик ва илмда) Довудга ворис бўлди ва айтди: «Эй одамлар, бизга қушларнинг (ва барча жониворларнинг) тили билдирилди ҳамда (пайғамбар ва подшоҳларга бериладиган) барча нарсалардан берилди. Албатта, бу очиқ фазлу-марҳаматнинг ўзидир'}, {'chapter': 27, 'verse': 17, 'text': 'وَحُشِرَ لِسُلَيۡمَٰنَ جُنُودُهُۥ مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ وَٱلطَّيۡرِ فَهُمۡ يُوزَعُونَ\nСулаймон (амри билан) жин, инс ва қушлардан бўлган лашкарлари тўпланиб, тизилган ҳолларида турдилар'}, {'chapter': 27, 'verse': 18, 'text': 'حَتَّىٰٓ إِذَآ أَتَوۡاْ عَلَىٰ وَادِ ٱلنَّمۡلِ قَالَتۡ نَمۡلَةٞ يَـٰٓأَيُّهَا ٱلنَّمۡلُ ٱدۡخُلُواْ مَسَٰكِنَكُمۡ لَا يَحۡطِمَنَّكُمۡ سُلَيۡمَٰنُ وَجُنُودُهُۥ وَهُمۡ لَا يَشۡعُرُونَ\nТо улар чумолилар водийсига етган вақтларида, бир чумоли: «Эй чумолилар, уяларингизга киринглар, яна Сулаймон ва унинг лашкарлари ўзлари сезмаган ҳолларида сизларни босиб-янчиб кетмасинлар», деган эди'}, {'chapter': 27, 'verse': 19, 'text': 'فَتَبَسَّمَ ضَاحِكٗا مِّن قَوۡلِهَا وَقَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَدۡخِلۡنِي بِرَحۡمَتِكَ فِي عِبَادِكَ ٱلصَّـٰلِحِينَ\n(Сулаймон) унинг сўзидан кулиб-табассум қилди ва деди: «Парвардигорим, мени Сен менга ва ота-онамга инъом этган неъматингга шукр қилишга ва Ўзинг рози бўладиган яхши амалларнигина қилишга муваффақ этгин ва мени ҳам Ўз фазлу раҳматинг билан солиҳ бандаларинг қаторига киритгин»'}, {'chapter': 27, 'verse': 20, 'text': 'وَتَفَقَّدَ ٱلطَّيۡرَ فَقَالَ مَالِيَ لَآ أَرَى ٱلۡهُدۡهُدَ أَمۡ كَانَ مِنَ ٱلۡغَآئِبِينَ\nУ қушларни кўздан кечириб (уларнинг орасида ҳудҳудга кўзи тушмагач): «Нега мен ҳудҳудни кўрмаяпман, балки у (мендан беизн) ғойиб бўлгувчилардандир'}, {'chapter': 27, 'verse': 21, 'text': 'لَأُعَذِّبَنَّهُۥ عَذَابٗا شَدِيدًا أَوۡ لَأَاْذۡبَحَنَّهُۥٓ أَوۡ لَيَأۡتِيَنِّي بِسُلۡطَٰنٖ مُّبِينٖ\nАлбатта уни қаттиқ азоб билан азобларман ё сўйиб юборурман ёки у менга (ўз узрини баён қилиб) очиқ ҳужжат келтирур», деди'}, {'chapter': 27, 'verse': 22, 'text': 'فَمَكَثَ غَيۡرَ بَعِيدٖ فَقَالَ أَحَطتُ بِمَا لَمۡ تُحِطۡ بِهِۦ وَجِئۡتُكَ مِن سَبَإِۭ بِنَبَإٖ يَقِينٍ\nСўнг узоққа қолмай (ҳудҳуд келиб) деди: «Мен сен огоҳ бўлмаган нарсадан огоҳ бўлдим ва сенга Сабаъ (шаҳри)дан аниқ бир хабар келтирдим'}, {'chapter': 27, 'verse': 23, 'text': 'إِنِّي وَجَدتُّ ٱمۡرَأَةٗ تَمۡلِكُهُمۡ وَأُوتِيَتۡ مِن كُلِّ شَيۡءٖ وَلَهَا عَرۡشٌ عَظِيمٞ\nДарҳақиқат, мен бир аёлни кўрдим. У уларнинг маликаси экан. Унга барча нарсадан ато этилган бўлиб, катта тахти ҳам бордир'}, {'chapter': 27, 'verse': 24, 'text': 'وَجَدتُّهَا وَقَوۡمَهَا يَسۡجُدُونَ لِلشَّمۡسِ مِن دُونِ ٱللَّهِ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِيلِ فَهُمۡ لَا يَهۡتَدُونَ\nМен у ва унинг қавми Аллоҳни қўйиб, қуёшга сажда-ибодат қилаётганларини кўрдим». Шайтон уларга (қилаётган) амалларини чиройли кўрсатиб, уларни (Ҳақ) йўлдан тўсиб қўйгандир. Бас, улар (Ҳақ йўлга) ҳидоят топмаслар'}, {'chapter': 27, 'verse': 25, 'text': 'أَلَّاۤ يَسۡجُدُواْۤ لِلَّهِ ٱلَّذِي يُخۡرِجُ ٱلۡخَبۡءَ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَيَعۡلَمُ مَا تُخۡفُونَ وَمَا تُعۡلِنُونَ\n(То) Аллоҳга сажда қилмагунларича. (Аллоҳ) осмонлар ва Ердаги барча сирларни ошкор қиладиган ҳамда сизлар яширадиган ва ошкор қиладиган барча нарсаларни ҳам биладиган Зотдир'}, {'chapter': 27, 'verse': 26, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ۩\n«Аллоҳ — ҳеч қандай илоҳ йўқ, магар Унинг Ўзигина бордир. У улуғ Арш Эгасидир»'}, {'chapter': 27, 'verse': 27, 'text': '۞قَالَ سَنَنظُرُ أَصَدَقۡتَ أَمۡ كُنتَ مِنَ ٱلۡكَٰذِبِينَ\n(Сулаймон) деди: «Сен рост сўзладингми ёки ёлғончилардан бўлдингми, кўрурмиз'}, {'chapter': 27, 'verse': 28, 'text': 'ٱذۡهَب بِّكِتَٰبِي هَٰذَا فَأَلۡقِهۡ إِلَيۡهِمۡ ثُمَّ تَوَلَّ عَنۡهُمۡ فَٱنظُرۡ مَاذَا يَرۡجِعُونَ\nМана бу мактубимни олиб бориб, уларга ташлагин, сўнгра улардан четланиб кузатгин-чи, нима (жавоб) қайтарар эканлар»'}, {'chapter': 27, 'verse': 29, 'text': 'قَالَتۡ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ إِنِّيٓ أُلۡقِيَ إِلَيَّ كِتَٰبٞ كَرِيمٌ\n(Қачонки ҳудҳуд Сулаймоннинг мактубини олиб келиб, малика Билқийснинг олдига ташлагач) у деди: «Эй одамлар, ҳақиқатан менга улуғ бир мактуб ташланди'}, {'chapter': 27, 'verse': 30, 'text': 'إِنَّهُۥ مِن سُلَيۡمَٰنَ وَإِنَّهُۥ بِسۡمِ ٱللَّهِ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\nАлбатта у Сулаймондандир ва у (мактубда шундай битилгандир): «Меҳрибон ва раҳмдил Аллоҳ номи билан бошлайман'}, {'chapter': 27, 'verse': 31, 'text': 'أَلَّا تَعۡلُواْ عَلَيَّ وَأۡتُونِي مُسۡلِمِينَ\nСизлар менга кибр-ҳаво қилмай, ҳузуримга бўйинсунган ҳолингизда келингиз!»'}, {'chapter': 27, 'verse': 32, 'text': 'قَالَتۡ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ أَفۡتُونِي فِيٓ أَمۡرِي مَا كُنتُ قَاطِعَةً أَمۡرًا حَتَّىٰ تَشۡهَدُونِ\n(Малика) айтди: «Эй одамлар, менга бу ишимда фатво-маслаҳат беринглар. Мен то сизлар гувоҳ бўлмагунингизча бирон иш ҳақида ҳукм қилгувчи эмасман»'}, {'chapter': 27, 'verse': 33, 'text': 'قَالُواْ نَحۡنُ أُوْلُواْ قُوَّةٖ وَأُوْلُواْ بَأۡسٖ شَدِيدٖ وَٱلۡأَمۡرُ إِلَيۡكِ فَٱنظُرِي مَاذَا تَأۡمُرِينَ\nУлар дедилар: «Бизлар куч-қувват ва журъат-матонат эгаларидирмиз. (Қандай ишга) буюриш сенинг ўзингга ҳавола. Бас, нимага буюришни ўйлаб кўравер, (бизлар эса сенга итоат этурмиз)»'}, {'chapter': 27, 'verse': 34, 'text': 'قَالَتۡ إِنَّ ٱلۡمُلُوكَ إِذَا دَخَلُواْ قَرۡيَةً أَفۡسَدُوهَا وَجَعَلُوٓاْ أَعِزَّةَ أَهۡلِهَآ أَذِلَّةٗۚ وَكَذَٰلِكَ يَفۡعَلُونَ\nУ айтди: «Аниқки подшоҳлар қачон бирон қишлоққа (бостириб) кирсалар уни вайрон этурлар, аҳолисининг обрў-эътиборлиларини эса хор-зор қилурлар. Уларнинг қилмиш-одатлари шудир'}, {'chapter': 27, 'verse': 35, 'text': 'وَإِنِّي مُرۡسِلَةٌ إِلَيۡهِم بِهَدِيَّةٖ فَنَاظِرَةُۢ بِمَ يَرۡجِعُ ٱلۡمُرۡسَلُونَ\nМен уларга бир ҳадя юбориб, кўрай-чи, элчиларим нима (хабар) билан қайтар эканлар»'}, {'chapter': 27, 'verse': 36, 'text': 'فَلَمَّا جَآءَ سُلَيۡمَٰنَ قَالَ أَتُمِدُّونَنِ بِمَالٖ فَمَآ ءَاتَىٰنِۦَ ٱللَّهُ خَيۡرٞ مِّمَّآ ءَاتَىٰكُمۚ بَلۡ أَنتُم بِهَدِيَّتِكُمۡ تَفۡرَحُونَ\nБас, қачонки (Билқийснинг элчилари унинг юборган ҳадясини олиб) Сулаймон (ҳузурига) келгач, у деди: «Сизлар менга мол-дунё билан мадад бермоқчимисиз?! У ҳолда (билингизки, Аллоҳ менга ато этган (пайғамбарлик ва мулку-давлат) У зот сизларга берган (мол-дунёдан) яхшироқдир. Балки сизлар ҳадяларингиз билан хурсанд бўларсизлар, (лекин менинг унга эҳтиёжим йўқдир)'}, {'chapter': 27, 'verse': 37, 'text': 'ٱرۡجِعۡ إِلَيۡهِمۡ فَلَنَأۡتِيَنَّهُم بِجُنُودٖ لَّا قِبَلَ لَهُم بِهَا وَلَنُخۡرِجَنَّهُم مِّنۡهَآ أَذِلَّةٗ وَهُمۡ صَٰغِرُونَ\nСен (қавмингга) қайтиб бориб (айтгинки, агар ҳузуримга бўйинсунган ҳолларида келмасалар) албатта биз уларга шундай қўшин билан борурмизки, улар унга асло бас кела олмаслар ва албатта биз уларни (қишлоқларидан) мағлуб ҳолларида қувиб чиқарурмиз»'}, {'chapter': 27, 'verse': 38, 'text': 'قَالَ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ أَيُّكُمۡ يَأۡتِينِي بِعَرۡشِهَا قَبۡلَ أَن يَأۡتُونِي مُسۡلِمِينَ\n«Эй одамлар, улар менинг ҳузуримга бўйсунган ҳолларида келишларидан илгари қайси бирларингиз менга унинг (яъни, Билқийснинг ўз шаҳрида қолган) тахтини келтира олур», деди'}, {'chapter': 27, 'verse': 39, 'text': 'قَالَ عِفۡرِيتٞ مِّنَ ٱلۡجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن تَقُومَ مِن مَّقَامِكَۖ وَإِنِّي عَلَيۡهِ لَقَوِيٌّ أَمِينٞ\n(Шунда) жинлардан бир пахлавони айтди: «Мен уни сен ўрнингдан туришингдан илгари сенга келтирурман. Албатта мен бунга қодир ва ишончлидирман»'}, {'chapter': 27, 'verse': 40, 'text': 'قَالَ ٱلَّذِي عِندَهُۥ عِلۡمٞ مِّنَ ٱلۡكِتَٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن يَرۡتَدَّ إِلَيۡكَ طَرۡفُكَۚ فَلَمَّا رَءَاهُ مُسۡتَقِرًّا عِندَهُۥ قَالَ هَٰذَا مِن فَضۡلِ رَبِّي لِيَبۡلُوَنِيٓ ءَأَشۡكُرُ أَمۡ أَكۡفُرُۖ وَمَن شَكَرَ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ رَبِّي غَنِيّٞ كَرِيمٞ\nКитобдан огоҳлиги бор бўлган бир зот эса: «Мен уни кўзинг ўзингга қайтгунича (яъни, кўз очиб юмгунча) сенга келтирурман», деди. «Бу Парвардигоримнинг мен шукр қиламанми ёки куфрони неъмат қиламанми, имтиҳон этиш учун берган фазлу-марҳаматидандир. Ким шукр қилса, бас, албатта у фақат ўз (фойдаси) учун шукр қилур. Ким куфрони неъмат қилса, бас, албатта Парвардигорим (унинг шукр қилишидан) беҳожат ва фазлу-карам Эгасидир»'}, {'chapter': 27, 'verse': 41, 'text': 'قَالَ نَكِّرُواْ لَهَا عَرۡشَهَا نَنظُرۡ أَتَهۡتَدِيٓ أَمۡ تَكُونُ مِنَ ٱلَّذِينَ لَا يَهۡتَدُونَ\n(Қачонки маликанинг келиши яқинлашиб қолгач, Сулаймон унинг фаҳму-фаросатини синаш учун) деди: «Унинг тахтини танимайдиган қилиб — ўзгартириб қўйинглар у (ўз тахтини танишга) йўл топурми ёки йўл топа олмайдиган кимсалардан бўлурми, кўрурмиз»'}, {'chapter': 27, 'verse': 42, 'text': 'فَلَمَّا جَآءَتۡ قِيلَ أَهَٰكَذَا عَرۡشُكِۖ قَالَتۡ كَأَنَّهُۥ هُوَۚ وَأُوتِينَا ٱلۡعِلۡمَ مِن قَبۡلِهَا وَكُنَّا مُسۡلِمِينَ\nЭнди қачонки у келгач: «Сенинг тахтинг шундаймиди?», дейилди. У (ҳайратга тушиб): «Худди ўшанинг ўзи-ку», деди. (Шунда Сулаймон айтди: «Алҳамдулиллоҳки), бизларга ундан илгари илм-маърифат берилиб, мусулмон бўлганмиз'}, {'chapter': 27, 'verse': 43, 'text': 'وَصَدَّهَا مَا كَانَت تَّعۡبُدُ مِن دُونِ ٱللَّهِۖ إِنَّهَا كَانَتۡ مِن قَوۡمٖ كَٰفِرِينَ\nУни (маликани) эса ўзи Аллоҳни қўйиб, ибодат қилган нарсаси (Аллоҳга ширк келтириб, мусулмон бўлишдан) тўсгандир. Чунки у кофир қавмдан эди»'}, {'chapter': 27, 'verse': 44, 'text': 'قِيلَ لَهَا ٱدۡخُلِي ٱلصَّرۡحَۖ فَلَمَّا رَأَتۡهُ حَسِبَتۡهُ لُجَّةٗ وَكَشَفَتۡ عَن سَاقَيۡهَاۚ قَالَ إِنَّهُۥ صَرۡحٞ مُّمَرَّدٞ مِّن قَوَارِيرَۗ قَالَتۡ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي وَأَسۡلَمۡتُ مَعَ سُلَيۡمَٰنَ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\n(Маликага): «Саройга киргин», дейилди. Энди қачонки у (саройни) кўргач, уни сув (тўлқин) деб ўйлаб оёқларини очган эди, (Сулаймон): «Бу ойнадан ясалган баланд қасрдир», деди. Шунда малика) айтди: «Парвардигорим, дарҳақиқат, мен (қуёшга сиғиниш билан) жонимга жабр қилибман, (энди) Сулаймон билан бирга барча оламлар Хожаси — Аллоҳга бўйинсундим»'}, {'chapter': 27, 'verse': 45, 'text': 'وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحًا أَنِ ٱعۡبُدُواْ ٱللَّهَ فَإِذَا هُمۡ فَرِيقَانِ يَخۡتَصِمُونَ\nҚасамки, Биз Самуд (қабиласи)га биродарлари Солиҳни пайғамбар қилдик. (У): «Аллоҳга ибодат қилингиз», (деди). Бас, баногоҳ улар талашиб-тортишадиган икки гуруҳ бўлиб олдилар'}, {'chapter': 27, 'verse': 46, 'text': 'قَالَ يَٰقَوۡمِ لِمَ تَسۡتَعۡجِلُونَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِۖ لَوۡلَا تَسۡتَغۡفِرُونَ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ\n(Самуд қабиласидаги кофир бўлган гуруҳ Солиҳга: «Агар чин пайғамбар бўлсанг, бизларни қўрқитаётган азобингни келтир-чи», деганларида) у айтди: «Эй қавмим, нега сизлар яхшилик-раҳматдан илгари ёмонлик-азобни шоштирмоқдасиз?! Сизларга раҳм-шафқат қилиниши учун Аллоҳдан мағфират сўрасангизлар бўлмасми?!»'}, {'chapter': 27, 'verse': 47, 'text': 'قَالُواْ ٱطَّيَّرۡنَا بِكَ وَبِمَن مَّعَكَۚ قَالَ طَـٰٓئِرُكُمۡ عِندَ ٱللَّهِۖ بَلۡ أَنتُمۡ قَوۡمٞ تُفۡتَنُونَ\nУлар дедилар: «Бизлар (бошимизга тушган очарчилик-қаҳатчилик) сенинг ва сен билан бирга бўлган (мўминларнинг) шумқадамлигидан деб билдик». У айтди: «Сизларнинг (бошингизга тушган) бахтсизлик Аллоҳ ҳузуридандир. Балки сизлар имтиҳон қилинаётган қавмдирсиз»'}, {'chapter': 27, 'verse': 48, 'text': 'وَكَانَ فِي ٱلۡمَدِينَةِ تِسۡعَةُ رَهۡطٖ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ\nУ шаҳарда (ўзларини) ўнгламай Ер юзида бузғунчилик қилиб юрадиган тўққиз нафар кимса бор эди'}, {'chapter': 27, 'verse': 49, 'text': 'قَالُواْ تَقَاسَمُواْ بِٱللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهۡلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدۡنَا مَهۡلِكَ أَهۡلِهِۦ وَإِنَّا لَصَٰدِقُونَ\nЎшалар Аллоҳ номига қасам ичишиб (бир-бирларига) айтдилар: «Албатта (Солиҳни) ва унинг аҳли-тобеъларини тунда ҳалок қилурмиз, сўнгра унинг валий-ҳомийсига: «Бизлар (на Солиҳнинг ва на) унинг аҳли-тобеъларининг ҳалокатига гувоҳ бўлган эмасмиз. Албатта бизлар ростгўй кишилардирмиз», деймиз»'}, {'chapter': 27, 'verse': 50, 'text': 'وَمَكَرُواْ مَكۡرٗا وَمَكَرۡنَا مَكۡرٗا وَهُمۡ لَا يَشۡعُرُونَ\nУлар (Солиҳ ва унинг тобеъларига қарши ўз) макрларини қилдилар. Биз ҳам улар сезмаган ҳолларида (уларни ҳалок қилиш билан) бир «макр» қилдик'}, {'chapter': 27, 'verse': 51, 'text': 'فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ مَكۡرِهِمۡ أَنَّا دَمَّرۡنَٰهُمۡ وَقَوۡمَهُمۡ أَجۡمَعِينَ\nЭнди уларнинг қилган макрлари оқибати қандай бўлганини кўринг — Биз уларни ва барча қавмларини ҳалок қилдик'}, {'chapter': 27, 'verse': 52, 'text': 'فَتِلۡكَ بُيُوتُهُمۡ خَاوِيَةَۢ بِمَا ظَلَمُوٓاْۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡلَمُونَ\nМана, улар золим бўлганликлари сабабли уйлари ҳувиллаб қолди! Албатта бунда биладиган қавм учун оят-ибрат бордир'}, {'chapter': 27, 'verse': 53, 'text': 'وَأَنجَيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nИймон келтирган ва тақводор бўлган зотларга эса нажот бердик'}, {'chapter': 27, 'verse': 54, 'text': 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ وَأَنتُمۡ تُبۡصِرُونَ\nЛутни (эсланг). Ўшанда у қавмига айтган эди: «Сизлар кўриб-билиб турган ҳолингизда бузуқлик қилурмисизлар'}, {'chapter': 27, 'verse': 55, 'text': 'أَئِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ تَجۡهَلُونَ\nСизлар хотинларингизни қўйиб, шаҳват билан эркакларга яқинлашурмисизлар?! Йўқ, сизлар жоҳил қавмдирсиз!»'}, {'chapter': 27, 'verse': 56, 'text': '۞فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوٓاْ ءَالَ لُوطٖ مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ\n(Лут) қавмининг жавоби эса фақат «Лутнинг аҳли-тобеъларини қишлоғингиздан (қувиб) чиқарингиз! Дарҳаҳиқат, улар жуда «покиза» кишилардир», дейишлари бўлди'}, {'chapter': 27, 'verse': 57, 'text': 'فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَٰهَا مِنَ ٱلۡغَٰبِرِينَ\nСўнг Биз (Лутга) ва унинг аҳли-тобеъларига нажот бердик. Магар унинг хотини (нажот топмади, чунки) Биз уни қолиб ҳалок бўлгувчилардан бўлишини тақдир қилиб — белгилаб қўйдик'}, {'chapter': 27, 'verse': 58, 'text': 'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ\nВа уларнинг устига бир ёмғир (яъни, тош ёғдирдик). Бас, (азобдан) огоҳлантирилгувчилар ёмғири нақадар ёмон бўлди'}, {'chapter': 27, 'verse': 59, 'text': 'قُلِ ٱلۡحَمۡدُ لِلَّهِ وَسَلَٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِينَ ٱصۡطَفَىٰٓۗ ءَآللَّهُ خَيۡرٌ أَمَّا يُشۡرِكُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Аллоҳга ҳамду сано бўлсин ва Унинг Ўзи (пайғамбарлик учун) танлаган бандаларига дуо-ю салом бўлсин». Аллоҳ яхшироқми ёки улар (Макка мушриклари Аллоҳга) шерик қилаётган бутларими'}, {'chapter': 27, 'verse': 60, 'text': 'أَمَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ لَكُم مِّنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهۡجَةٖ مَّا كَانَ لَكُمۡ أَن تُنۢبِتُواْ شَجَرَهَآۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ هُمۡ قَوۡمٞ يَعۡدِلُونَ\nЁки осмонлар ва Ерни яратган ҳамда сизлар учун осмондан сув-ёмғир ёғдириб, унинг ёрдамида гўзал боғларни ўстирган Зотми?! Сизлар учун у (боғларнинг) дов-дарахтини ўстириш имкони йўқ эди. Аллоҳ билан бирга яна бирон илоҳ борми?! Йўқ, улар (Аллоҳга ўзгаларни) тенг биладиган қавмдирлар'}, {'chapter': 27, 'verse': 61, 'text': 'أَمَّن جَعَلَ ٱلۡأَرۡضَ قَرَارٗا وَجَعَلَ خِلَٰلَهَآ أَنۡهَٰرٗا وَجَعَلَ لَهَا رَوَٰسِيَ وَجَعَلَ بَيۡنَ ٱلۡبَحۡرَيۡنِ حَاجِزًاۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nЁки Ерни (барча жонзотлар учун) қароргоҳ қилиб, унинг ўртасида дарёлар пайдо қилган ва унинг учун тутқи-тоғлар барпо қилган ҳамда икки (яъни, шўр-чучук) денгиз-дарё ўртасида тўсиқ-тўғон иншо қилган Зотми?! Аллоҳ билан бирга яна бирон илоҳ борми?! Йўқ, уларнинг кўплари билмайдилар'}, {'chapter': 27, 'verse': 62, 'text': 'أَمَّن يُجِيبُ ٱلۡمُضۡطَرَّ إِذَا دَعَاهُ وَيَكۡشِفُ ٱلسُّوٓءَ وَيَجۡعَلُكُمۡ خُلَفَآءَ ٱلۡأَرۡضِۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ قَلِيلٗا مَّا تَذَكَّرُونَ\nЁки музтар-ночор одам дуо-илтижо қилган вақтида (дуосини) ижобат қиладиган ва (унинг) мушкулини осон қиладиган ҳамда сизларни Ернинг халифа-эгалари қиладиган Зотми?! Аллоҳ билан бирга яна бирон илоҳ борми?! Камдан-кам эслатма-ибрат олурсизлар'}, {'chapter': 27, 'verse': 63, 'text': 'أَمَّن يَهۡدِيكُمۡ فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ وَمَن يُرۡسِلُ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦٓۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ تَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ\nЁки сизларга қуруқлик ва денгиз зулматларида тўғри йўлни кўрсатадиган ва Ўз раҳмати-ёмғири олдидан шамолларни хушхабар қилиб жўнатадиган Зотми?! Аллоҳ билан бирга яна бирон илоҳ борми?! Аллоҳ уларнинг ширк келтирган нарсаларидан юксакдир'}, {'chapter': 27, 'verse': 64, 'text': 'أَمَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nЁки бошлаб Ўзи яратиб, сўнгра (Охиратда) қайта яратадиган Зотми?! Ким сизларга осмон ва заминдан ризқу рўз берур? Аллоҳ билан бирга яна бирон илоҳ борми?! (Эй Муҳаммад алайҳис-салоту вас-салом, кофирларга) айтинг: «Агар ростгўй бўлсангизлар ҳужжатларингизни келтирингиз!»'}, {'chapter': 27, 'verse': 65, 'text': 'قُل لَّا يَعۡلَمُ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلۡغَيۡبَ إِلَّا ٱللَّهُۚ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ\nАйтинг: «Осмонлар ва Ердаги бирон кимса ғайбни билмас, магар Аллоҳгина (билур)». Улар қачон қайта тирилишларини ҳам сеза олмаслар'}, {'chapter': 27, 'verse': 66, 'text': 'بَلِ ٱدَّـٰرَكَ عِلۡمُهُمۡ فِي ٱلۡأٓخِرَةِۚ بَلۡ هُمۡ فِي شَكّٖ مِّنۡهَاۖ بَلۡ هُم مِّنۡهَا عَمُونَ\nБалки уларнинг Охират ҳақидаги билимлари пишиб етилгандир (ва шу сабабдан Охират ҳақида сўраётгандирлар). Йўқ, улар (Охиратдан) шак-шубҳададирлар! Йўқ, улар ундан кўрдирлар'}, {'chapter': 27, 'verse': 67, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ أَءِذَا كُنَّا تُرَٰبٗا وَءَابَآؤُنَآ أَئِنَّا لَمُخۡرَجُونَ\nКофир бўлган кимсалар дедилар: «Ўзимиз ҳам ота-боболаримиз ҳам тупроққа айланиб кетган вақтимизда (қайта тирилиб, қабрларимиздан) чиқарилгувчидирмизми'}, {'chapter': 27, 'verse': 68, 'text': 'لَقَدۡ وُعِدۡنَا هَٰذَا نَحۡنُ وَءَابَآؤُنَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nДарҳақиқат, бизларнинг ўзимизга ҳам, илгари ота-боболаримизга ҳам мана шу ваъда қилингандир. Бу фақат ўтганларнинг афсоналаридир»'}, {'chapter': 27, 'verse': 69, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ\nАйтинг: «Ерни айланиб, жиноятчи кимсаларнинг оқибатлари қандай бўлганини кўринглар!»'}, {'chapter': 27, 'verse': 70, 'text': 'وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُن فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларнинг (иймон келтирмаганлари) устида ғам чекманг ва улар қилаётган макрлардан сиқилманг ҳам'}, {'chapter': 27, 'verse': 71, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Ростгўй бўлсангизлар (айтинглар-чи), мана шу ваъда (қилинган азоб) қачон бўлади?», дейдилар'}, {'chapter': 27, 'verse': 72, 'text': 'قُلۡ عَسَىٰٓ أَن يَكُونَ رَدِفَ لَكُم بَعۡضُ ٱلَّذِي تَسۡتَعۡجِلُونَ\nАйтинг: «Эҳтимол сизлар шоштираётган (азобнинг) баъзиси сизларга яқин келиб қолгандир»'}, {'chapter': 27, 'verse': 73, 'text': 'وَإِنَّ رَبَّكَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ\nАлбатта, Парвардигорингиз одамларга фазлу марҳамат Соҳибидир. Лекин уларнинг кўплари шукр қилмайдилар'}, {'chapter': 27, 'verse': 74, 'text': 'وَإِنَّ رَبَّكَ لَيَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ\nАлбатта, Парвардигорингиз уларнинг диллари яширадиган нарсани ҳам, улар ошкор қиладиган нарсани ҳам билур'}, {'chapter': 27, 'verse': 75, 'text': 'وَمَا مِنۡ غَآئِبَةٖ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِلَّا فِي كِتَٰبٖ مُّبِينٍ\nОсмон ва заминдаги ҳар бир сир очиқ Китобда (яъни, Лавҳул-Маҳфузда) мавжуддир'}, {'chapter': 27, 'verse': 76, 'text': 'إِنَّ هَٰذَا ٱلۡقُرۡءَانَ يَقُصُّ عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَكۡثَرَ ٱلَّذِي هُمۡ فِيهِ يَخۡتَلِفُونَ\nАлбатта, ушбу Қуръон Бани Исроилга улар ихтилоф қилаётган нарсаларнинг кўпини сўйлаб — баён қилиб берур'}, {'chapter': 27, 'verse': 77, 'text': 'وَإِنَّهُۥ لَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ\nАлбатта, у иймон келтирадиган кишилар учун ҳидоят ва раҳматдир'}, {'chapter': 27, 'verse': 78, 'text': 'إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُم بِحُكۡمِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡعَلِيمُ\nАлбатта, Парвардигорингиз (одамлар) ўртасида Ўз ҳукми (яъни, адолати) билан ҳукм қилур. У ғолиб ва билгувчидир'}, {'chapter': 27, 'verse': 79, 'text': 'فَتَوَكَّلۡ عَلَى ٱللَّهِۖ إِنَّكَ عَلَى ٱلۡحَقِّ ٱلۡمُبِينِ\nБас, сиз Аллоҳга таваккул қилинг! Албатта, сиз равшан ҳақиқат устидадирсиз'}, {'chapter': 27, 'verse': 80, 'text': 'إِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوۡاْ مُدۡبِرِينَ\nАниқки, сиз (ўз даъватингизни) «ўлик»ларга уқтира олмассиз ва юз ўгириб кетаётган «кар»ларга ҳам бу даъватни уқтира олмассиз'}, {'chapter': 27, 'verse': 81, 'text': 'وَمَآ أَنتَ بِهَٰدِي ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ\nЯна сиз кўрларни ҳам залолатларидан (чиқариб, ҳақ йўлга) ҳидоят қила олгувчи эмасдирсиз. Сиз фақат Бизнинг оятларимизга иймон келтириб, мусулмон бўладиган кишиларгагина уқтира олурсиз'}, {'chapter': 27, 'verse': 82, 'text': '۞وَإِذَا وَقَعَ ٱلۡقَوۡلُ عَلَيۡهِمۡ أَخۡرَجۡنَا لَهُمۡ دَآبَّةٗ مِّنَ ٱلۡأَرۡضِ تُكَلِّمُهُمۡ أَنَّ ٱلنَّاسَ كَانُواْ بِـَٔايَٰتِنَا لَا يُوقِنُونَ\nҚачон (кофирларнинг) устига сўз-азоб тушганида (яъни, Қиёмат яқинлашиб қолганида), Биз улар учун ердан бир жонивор чиқарурмиз. У уларга одамлар Бизнинг оятларимизга ишонмайдиган бўлиб қолганлари ҳақида сўзлар'}, {'chapter': 27, 'verse': 83, 'text': 'وَيَوۡمَ نَحۡشُرُ مِن كُلِّ أُمَّةٖ فَوۡجٗا مِّمَّن يُكَذِّبُ بِـَٔايَٰتِنَا فَهُمۡ يُوزَعُونَ\nБиз ҳар бир миллатдан бир гуруҳ Бизнинг оятларимизни ёлғон дейдиган кимсаларни тўплаб, улар тизилиб турадиган кунни (эсланг)'}, {'chapter': 27, 'verse': 84, 'text': 'حَتَّىٰٓ إِذَا جَآءُو قَالَ أَكَذَّبۡتُم بِـَٔايَٰتِي وَلَمۡ تُحِيطُواْ بِهَا عِلۡمًا أَمَّاذَا كُنتُمۡ تَعۡمَلُونَ\nЭнди қачон улар (ҳисоб-китоб қилинадиган жойга) келишгач, (Аллоҳ) айтди: «Сизлар аниқ-пухта билмаган ҳолингизда Менинг оятларимни ёлғон дедингизми?! Бўлмаса нима қилгувчи бўлдингизлар?!»'}, {'chapter': 27, 'verse': 85, 'text': 'وَوَقَعَ ٱلۡقَوۡلُ عَلَيۡهِم بِمَا ظَلَمُواْ فَهُمۡ لَا يَنطِقُونَ\nЗолим-кофир бўлганлари сабабли уларнинг устига Сўз — азоб тушди. Бас, улар (бирон сўз) сўзлай олмаслар'}, {'chapter': 27, 'verse': 86, 'text': 'أَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا ٱلَّيۡلَ لِيَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nБиз улар ором олишлари учун кечани пайдо қилганимизни ва кундузни ёруғ қилиб қўйганимизни кўрмадиларми?! Албатта бундай иймон келтирадиган қавм учун оят-ибратлар бордир'}, {'chapter': 27, 'verse': 87, 'text': 'وَيَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَفَزِعَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۚ وَكُلٌّ أَتَوۡهُ دَٰخِرِينَ\nСур чалиниб, Аллоҳ Ўзи хоҳлаган зотлардан бошқа осмонлардаги ва Ердаги (барча) кимсалар даҳшатга тушиб қолган ва барча У зот (ҳузурига) бўйин эгган ҳолда келган кунни (эсланг)'}, {'chapter': 27, 'verse': 88, 'text': 'وَتَرَى ٱلۡجِبَالَ تَحۡسَبُهَا جَامِدَةٗ وَهِيَ تَمُرُّ مَرَّ ٱلسَّحَابِۚ صُنۡعَ ٱللَّهِ ٱلَّذِيٓ أَتۡقَنَ كُلَّ شَيۡءٍۚ إِنَّهُۥ خَبِيرُۢ بِمَا تَفۡعَلُونَ\nСиз тоғларни кўриб тек қотиб турибди, деб ўйларсиз. Ҳолбуки, улар ҳам худди булутлар юргандек юрарлар. (Бу) барча нарсани пухта қилган Зот — Аллоҳнинг ҳунаридир. Албатта, У Зот сизлар қилаётган (барча) ишлардан огоҳдир'}, {'chapter': 27, 'verse': 89, 'text': 'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَا وَهُم مِّن فَزَعٖ يَوۡمَئِذٍ ءَامِنُونَ\nКим яхшилик билан келса, бас, унинг учун ўша (яхшилигидан) яхшироқ мукофот бўлур. Ва улар ўша Куннинг даҳшатидан омондадирлар'}, {'chapter': 27, 'verse': 90, 'text': 'وَمَن جَآءَ بِٱلسَّيِّئَةِ فَكُبَّتۡ وُجُوهُهُمۡ فِي ٱلنَّارِ هَلۡ تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\nКим ёмонлик билан келса, бас, юзтубан ҳолларида дўзахга ташланурлар. (Ва уларга айтилур): «Сизларга фақат қилиб ўтган амалларингизнинг жазоси берилур»'}, {'chapter': 27, 'verse': 91, 'text': 'إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ رَبَّ هَٰذِهِ ٱلۡبَلۡدَةِ ٱلَّذِي حَرَّمَهَا وَلَهُۥ كُلُّ شَيۡءٖۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга айтинг): «Мен фақат мана шу шаҳарнинг (яъни, Маккаи Мукарраманинг) Парвардигоригагина ибодат қилишга буюрилгандирман. У (бу шаҳарни) ҳаром қилган Зотдир (яъни, бу шаҳарда қон тўқилиши, бирон кишига зулм қилиниши, ов овланиши ва ҳатто ўт юлиниши ҳам ҳаром қилингандир). Барча нарса Уникидир. Мен мусулмонлардан бўлишга буюрилгандирман'}, {'chapter': 27, 'verse': 92, 'text': 'وَأَنۡ أَتۡلُوَاْ ٱلۡقُرۡءَانَۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَقُلۡ إِنَّمَآ أَنَا۠ مِنَ ٱلۡمُنذِرِينَ\nВа Қуръон тиловат қилишга (буюрилгандирман»). Бас, ким ҳақ йўлга юрса фақат ўз (фойдаси) учун юрар. Ким йўлдан озса, у ҳолда айтингки: «Мен фақат (йўлдан озган кимсаларни Аллоҳнинг азобидан) огоҳлантиргувчилардандирман»'}, {'chapter': 27, 'verse': 93, 'text': 'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ سَيُرِيكُمۡ ءَايَٰتِهِۦ فَتَعۡرِفُونَهَاۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ\nАйтинг: «Ҳамду-сано Аллоҳ учундир. Яқинда У сизларга Ўз оят-мўъжизаларини кўрсатур. Бас, сизлар у (мўъжизаларни) тан олурсизлар (лекин у кунда тан олишингиз сизларга фойда бермас)». (Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиз сизларнинг қилаётган амалларингиздан ғофил эмасдир'}, {'chapter': 28, 'verse': 1, 'text': 'طسٓمٓ\nТо, Син, Мим'}, {'chapter': 28, 'verse': 2, 'text': 'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ\nУшбу (оятлар) очиқ-равшан Китоб оятларидир'}, {'chapter': 28, 'verse': 3, 'text': 'نَتۡلُواْ عَلَيۡكَ مِن نَّبَإِ مُوسَىٰ وَفِرۡعَوۡنَ بِٱلۡحَقِّ لِقَوۡمٖ يُؤۡمِنُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), иймон келтирадиган қавм (ўқиб, ибрат олишлари) учун сизга Мусо ва Фиръавн хабаридан ҳаққи-рост тиловат қилиб (сўйлаб) берурмиз'}, {'chapter': 28, 'verse': 4, 'text': 'إِنَّ فِرۡعَوۡنَ عَلَا فِي ٱلۡأَرۡضِ وَجَعَلَ أَهۡلَهَا شِيَعٗا يَسۡتَضۡعِفُ طَآئِفَةٗ مِّنۡهُمۡ يُذَبِّحُ أَبۡنَآءَهُمۡ وَيَسۡتَحۡيِۦ نِسَآءَهُمۡۚ إِنَّهُۥ كَانَ مِنَ ٱلۡمُفۡسِدِينَ\nДарҳақиқат, Фиръавн (Миср) ерида туғёнга тушиб, унинг аҳолисини бўлак-бўлак қилди. У улардан бир тоифани (яъни, Бани Исроил қавмини) хорлаб ўғилларини сўяр, аёлларини тирик қолдирар. Албатта, у бузғунчи кимсалардан бўлди'}, {'chapter': 28, 'verse': 5, 'text': 'وَنُرِيدُ أَن نَّمُنَّ عَلَى ٱلَّذِينَ ٱسۡتُضۡعِفُواْ فِي ٱلۡأَرۡضِ وَنَجۡعَلَهُمۡ أَئِمَّةٗ وَنَجۡعَلَهُمُ ٱلۡوَٰرِثِينَ\nБиз эса у ерда хорланган кишиларга марҳамат қилишни, уларни (хор-бечораликдан чиқариб, барчага пешво зотларга айлантиришни ва уларни (Фиръавн мулкининг) ворислари қилишни истаймиз'}, {'chapter': 28, 'verse': 6, 'text': 'وَنُمَكِّنَ لَهُمۡ فِي ٱلۡأَرۡضِ وَنُرِيَ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا مِنۡهُم مَّا كَانُواْ يَحۡذَرُونَ\nЯна уларни ўша ерда барқарор қилиб, Фиръавн ва (унинг вазири) Ҳомонга ҳамда икковининг лашкарларига (Бани Исроил) томонидан ўзлари қўрқувчи бўлган нарсани (яъни, мулку-давлатлари қўлларидан кетиб, ҳукму салтанат Бани Исроил қавмининг қўлига ўтишини) кўрсатиб қўйишни (истаймиз)'}, {'chapter': 28, 'verse': 7, 'text': 'وَأَوۡحَيۡنَآ إِلَىٰٓ أُمِّ مُوسَىٰٓ أَنۡ أَرۡضِعِيهِۖ فَإِذَا خِفۡتِ عَلَيۡهِ فَأَلۡقِيهِ فِي ٱلۡيَمِّ وَلَا تَخَافِي وَلَا تَحۡزَنِيٓۖ إِنَّا رَآدُّوهُ إِلَيۡكِ وَجَاعِلُوهُ مِنَ ٱلۡمُرۡسَلِينَ\nБиз Мусонинг онасига (Мусони туғиб, уни ҳам Фиръавн одамлари келиб ўлдиришларидан қўрқиб турган пайтида) ваҳий қилдикки: «Уни эмизавергин. Бас, қачон (Фиръавн одамлари ўғил кўрганингни билишиб қолиб, уни ўлдириб кетишларидан) қўрқсанг, уни дарёга ташлагин ва қўрқмагин ҳам, қайғурмагин ҳам. Зеро, Биз уни сенга (яъни, тирик ҳолида) қайтаргувчидирмиз ва уни пайғамбарлардан қилгувчидирмиз»'}, {'chapter': 28, 'verse': 8, 'text': 'فَٱلۡتَقَطَهُۥٓ ءَالُ فِرۡعَوۡنَ لِيَكُونَ لَهُمۡ عَدُوّٗا وَحَزَنًاۗ إِنَّ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا كَانُواْ خَٰطِـِٔينَ\nБас, уни Фиръавн хонадони (ўсиб-улғайгач), ўзларига душман ва ғам-ташвиш бўлиши учун тутиб олдилар. Албатта, Фиръавн, Ҳомон ва уларнинг лашкарлари хато қилгувчи бўлдилар'}, {'chapter': 28, 'verse': 9, 'text': 'وَقَالَتِ ٱمۡرَأَتُ فِرۡعَوۡنَ قُرَّتُ عَيۡنٖ لِّي وَلَكَۖ لَا تَقۡتُلُوهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗا وَهُمۡ لَا يَشۡعُرُونَ\nФиръавннинг хотини (унга): «(Бу бола) мен учун ҳам, сен учун ҳам кўз қувончидир. Уни ўлдирманглар. Шоядки, унинг бизларга нафи тегса ёки уни бола қилиб олсак», деди. Улар (ўзларининг ҳалокатлари шу гўдак қўлида эканини) сезмаган ҳолларида (уни бола қилиб олдилар)'}, {'chapter': 28, 'verse': 10, 'text': 'وَأَصۡبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَٰرِغًاۖ إِن كَادَتۡ لَتُبۡدِي بِهِۦ لَوۡلَآ أَن رَّبَطۡنَا عَلَىٰ قَلۡبِهَا لِتَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nМусо (дарёда оқиб кетгач), онасининг қалби бўм-бўш бўлиб қолди. Агар Биз («Мусони ўзингга қайтарурмиз», деган ваъдамизга) иймон келтиргувчилардан бўлиши учун унинг кўнглини хотиржам қилмаганимизда, албатта, у (Мусо ўзининг ўғли эканини) очиб қўйган бўлур эди'}, {'chapter': 28, 'verse': 11, 'text': 'وَقَالَتۡ لِأُخۡتِهِۦ قُصِّيهِۖ فَبَصُرَتۡ بِهِۦ عَن جُنُبٖ وَهُمۡ لَا يَشۡعُرُونَ\nВа (Мусонинг онаси) унинг опасига: «Унинг (ортидан) тушгин», дедик. Бас, у (Фиръавн хонадонидагилар) сезмаган ҳолларида (Мусони) узоқдан кузатиб турди'}, {'chapter': 28, 'verse': 12, 'text': '۞وَحَرَّمۡنَا عَلَيۡهِ ٱلۡمَرَاضِعَ مِن قَبۡلُ فَقَالَتۡ هَلۡ أَدُلُّكُمۡ عَلَىٰٓ أَهۡلِ بَيۡتٖ يَكۡفُلُونَهُۥ لَكُمۡ وَهُمۡ لَهُۥ نَٰصِحُونَ\nБиз аввал (Мусога барча) эмизгувчи аёлларни ҳаром қилдик (бас, у бирон аёлни эммади), шунда (унинг опаси келиб): «Мен сизларга унга кафил бўлиб (эмизадиган) ва унга холис (тарбия берадиган) бир оилани кўрсатайми?», деди'}, {'chapter': 28, 'verse': 13, 'text': 'فَرَدَدۡنَٰهُ إِلَىٰٓ أُمِّهِۦ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَ وَلِتَعۡلَمَ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nШундай қилиб, Биз (Мусонинг онасининг) кўзлари шодланиши ва ғам чекмаслиги учун ҳамда Аллоҳнинг ваъдаси ҳақ эканини билиши учун уни онасига қайтардик. Лекин кўп (одамлар Аллоҳнинг ваъдаси ҳақ эканини) билмайдилар'}, {'chapter': 28, 'verse': 14, 'text': 'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسۡتَوَىٰٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nҚачонки у вояга етиб, (ақли) тўлгач, Биз унга ҳикмат ва билим ато этдик. Биз чиройли амал қилгувчи кишиларни мана шундай мукофотлармиз'}, {'chapter': 28, 'verse': 15, 'text': 'وَدَخَلَ ٱلۡمَدِينَةَ عَلَىٰ حِينِ غَفۡلَةٖ مِّنۡ أَهۡلِهَا فَوَجَدَ فِيهَا رَجُلَيۡنِ يَقۡتَتِلَانِ هَٰذَا مِن شِيعَتِهِۦ وَهَٰذَا مِنۡ عَدُوِّهِۦۖ فَٱسۡتَغَٰثَهُ ٱلَّذِي مِن شِيعَتِهِۦ عَلَى ٱلَّذِي مِنۡ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَيۡهِۖ قَالَ هَٰذَا مِنۡ عَمَلِ ٱلشَّيۡطَٰنِۖ إِنَّهُۥ عَدُوّٞ مُّضِلّٞ مُّبِينٞ\n(Кунлардан бирида Мусо) шаҳарга унинг аҳолиси ғафлатда бўлган (пешин) пайтида кирган эди, унда икки киши урушаётганини кўрди. (Улардан) бири ўз гуруҳидан (яъни, Бани Исроил қавмидан) ва бири душманидан (яъни, Қибтийлардан) эди. (Бас, Мусонинг) гуруҳидан бўлган киши ундан душман томондан бўлган кимсага қарши ёрдам сўради. Шунда Мусо бир мушт уриб уни ўлдириб қўйди. (Сўнг қилиб қўйган ишидан пушаймон бўлиб), деди: «Бу шайтоннинг ишидандир. Албатта, у йўлдан ургувчи очиқ душмандир»'}, {'chapter': 28, 'verse': 16, 'text': 'قَالَ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي فَٱغۡفِرۡ لِي فَغَفَرَ لَهُۥٓۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\n(Кейин Аллоҳга илтижо қилиб), деди: «Парвардигорим, дарҳақиқат, мен ўз жонимга жабр қилиб қўйдим. Энди Ўзинг мени мағфират қилгин». Бас, (Аллоҳ) уни мағфират қилди. Албатта Унинг Ўзигина мағфират қилгувчи, меҳрибондир'}, {'chapter': 28, 'verse': 17, 'text': 'قَالَ رَبِّ بِمَآ أَنۡعَمۡتَ عَلَيَّ فَلَنۡ أَكُونَ ظَهِيرٗا لِّلۡمُجۡرِمِينَ\nУ (яна) деди: «Парвардигорим, Сен менга қилган инъоминг ҳаққи-ҳурмати энди ҳаргиз жиноятчи кимсаларга ёрдам қилгувчи бўлмасман»'}, {'chapter': 28, 'verse': 18, 'text': 'فَأَصۡبَحَ فِي ٱلۡمَدِينَةِ خَآئِفٗا يَتَرَقَّبُ فَإِذَا ٱلَّذِي ٱسۡتَنصَرَهُۥ بِٱلۡأَمۡسِ يَسۡتَصۡرِخُهُۥۚ قَالَ لَهُۥ مُوسَىٰٓ إِنَّكَ لَغَوِيّٞ مُّبِينٞ\nБас, у эрта тонгда шаҳарда қўрқувга тушиб, (бирон кори ҳолга) кўз тутган ҳолда кетаётган эди, баногоҳ кеча ундан ёрдам сўраган (Бани Исроил қавмидан бўлган) кимса яна (бир Қибтий билан урушаётганини ва яна) уни ёрдамга чақираётганини (кўрди). Мусо унга деди: «Дарҳақиқат, сен аниқ гумроҳдирсан»'}, {'chapter': 28, 'verse': 19, 'text': 'فَلَمَّآ أَنۡ أَرَادَ أَن يَبۡطِشَ بِٱلَّذِي هُوَ عَدُوّٞ لَّهُمَا قَالَ يَٰمُوسَىٰٓ أَتُرِيدُ أَن تَقۡتُلَنِي كَمَا قَتَلۡتَ نَفۡسَۢا بِٱلۡأَمۡسِۖ إِن تُرِيدُ إِلَّآ أَن تَكُونَ جَبَّارٗا فِي ٱلۡأَرۡضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ ٱلۡمُصۡلِحِينَ\nЭнди у ўзларига душман бўлган кимсани (яъни, Қибтийни) ушламоқчи бўлган эди, (Мусо энди мени урмоқчи шекилли, деб гумон қилган Бани Исроилдан бўлган кимса): «Эй Мусо, кеча бир жонни ўлдирганинг каби (бугун) мени ҳам ўлдирмоқчимисан?! Сен фақат Ер юзида жабру зулм қилгувчи бўлмоқчисан. Сен ислоҳ қилгувчилардан бўлишни истамайсан», деди'}, {'chapter': 28, 'verse': 20, 'text': 'وَجَآءَ رَجُلٞ مِّنۡ أَقۡصَا ٱلۡمَدِينَةِ يَسۡعَىٰ قَالَ يَٰمُوسَىٰٓ إِنَّ ٱلۡمَلَأَ يَأۡتَمِرُونَ بِكَ لِيَقۡتُلُوكَ فَٱخۡرُجۡ إِنِّي لَكَ مِنَ ٱلنَّـٰصِحِينَ\nШаҳарнинг нариги тарафидан бир киши шошганча келиб: «Эй Мусо, (Фиръавн) одамлари сени ўлдириш учун тил бириктирмоқдалар. Бас, сен (бу шаҳардан) чиқиб кетгин. Албатта мен сенга холис бўлган кишилардандирман», деди'}, {'chapter': 28, 'verse': 21, 'text': 'فَخَرَجَ مِنۡهَا خَآئِفٗا يَتَرَقَّبُۖ قَالَ رَبِّ نَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, у қўрқувга тушиб, (ортидан етиб келиб қолишларига) кўз тутган ҳолда у ердан чиқиб, деди: «Парвардигорим, Ўзинг менга бу золим қавмдан нажот бергин»'}, {'chapter': 28, 'verse': 22, 'text': 'وَلَمَّا تَوَجَّهَ تِلۡقَآءَ مَدۡيَنَ قَالَ عَسَىٰ رَبِّيٓ أَن يَهۡدِيَنِي سَوَآءَ ٱلسَّبِيلِ\nВа қачонки Мадян (шаҳри) томонга юзлангач, деди: «Шояд Парвардигорим мени Тўғри Йўлга ҳидоят қилса»'}, {'chapter': 28, 'verse': 23, 'text': 'وَلَمَّا وَرَدَ مَآءَ مَدۡيَنَ وَجَدَ عَلَيۡهِ أُمَّةٗ مِّنَ ٱلنَّاسِ يَسۡقُونَ وَوَجَدَ مِن دُونِهِمُ ٱمۡرَأَتَيۡنِ تَذُودَانِۖ قَالَ مَا خَطۡبُكُمَاۖ قَالَتَا لَا نَسۡقِي حَتَّىٰ يُصۡدِرَ ٱلرِّعَآءُۖ وَأَبُونَا شَيۡخٞ كَبِيرٞ\nҚачонки Мадяннинг суви — қудуғига етиб келгач, у жойда бир тўп одамлар (чорваларини) суғораётганларини кўрди ва улардан қуйироқда икки аёл (ўз қўйларини қудуқдан) тўсиб турганларини кўриб: «Сизларга не бўлди (яъни, нега қўйларингизни суғормай турибсизлар)?», деди. Улар: «Биз то подачилар (молларини) қайтармагунча суғора олмаймиз. Отамиз эса қари чол (яъни, қўйларимизни суғоргани кела олмас)», дедилар'}, {'chapter': 28, 'verse': 24, 'text': 'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰٓ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَآ أَنزَلۡتَ إِلَيَّ مِنۡ خَيۡرٖ فَقِيرٞ\nШунда (Мусо) уларга (қўйларини) суғориб берди. Сўнгра (бир четдаги дарахт) соясига бориб (ўтириб), деди: «Парвардигорим, мен Ўзинг мен учун нима яхшилик (яъни, ризқ) туширсанг, ўшанга муҳтождирман»'}, {'chapter': 28, 'verse': 25, 'text': 'فَجَآءَتۡهُ إِحۡدَىٰهُمَا تَمۡشِي عَلَى ٱسۡتِحۡيَآءٖ قَالَتۡ إِنَّ أَبِي يَدۡعُوكَ لِيَجۡزِيَكَ أَجۡرَ مَا سَقَيۡتَ لَنَاۚ فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيۡهِ ٱلۡقَصَصَ قَالَ لَا تَخَفۡۖ نَجَوۡتَ مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nБас, улардан бири ҳаё билан юриб келиб: «Отам сени бизларга (қўйларимизни) суғориб берганинг ҳақини бериш учун чақирмоқда», деди. Бас, қачонки (Мусо Шуъайбнинг ҳузурига) келиб, унга ўз қиссасини сўйлаб бергач, у: «Қўрқмагин. Сен у золим қавмдан нажот топдинг», деди'}, {'chapter': 28, 'verse': 26, 'text': 'قَالَتۡ إِحۡدَىٰهُمَا يَـٰٓأَبَتِ ٱسۡتَـٔۡجِرۡهُۖ إِنَّ خَيۡرَ مَنِ ٱسۡتَـٔۡجَرۡتَ ٱلۡقَوِيُّ ٱلۡأَمِينُ\n(Қизлардан) бири: «Эй ота, уни (ишга) ёллагин. Зеро, сен ёллаган энг яхши киши кучли, ишончли кишидир», деди'}, {'chapter': 28, 'verse': 27, 'text': 'قَالَ إِنِّيٓ أُرِيدُ أَنۡ أُنكِحَكَ إِحۡدَى ٱبۡنَتَيَّ هَٰتَيۡنِ عَلَىٰٓ أَن تَأۡجُرَنِي ثَمَٰنِيَ حِجَجٖۖ فَإِنۡ أَتۡمَمۡتَ عَشۡرٗا فَمِنۡ عِندِكَۖ وَمَآ أُرِيدُ أَنۡ أَشُقَّ عَلَيۡكَۚ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰلِحِينَ\n(Шуъайб) айтди: «(Эй Мусо, агар рози бўлсанг), менга саккиз йил ишлаб беришинг бадалига мен сенга мана шу икки қизимдан бирини никоҳлаб бермоқчиман. Энди агар ўн йилни тўлатсанг (яъни, тўла ўн йил ишлаб берсанг), сен томонингдан (бизга марҳамат бўлур). Мен сени қийнашни истамайман. Иншоаллоҳ, сен менинг солиҳ кишилардан эканлигимни кўрурсан»'}, {'chapter': 28, 'verse': 28, 'text': 'قَالَ ذَٰلِكَ بَيۡنِي وَبَيۡنَكَۖ أَيَّمَا ٱلۡأَجَلَيۡنِ قَضَيۡتُ فَلَا عُدۡوَٰنَ عَلَيَّۖ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ\n(Мусо) деди: «Мана шу сен билан менинг ўртамиздаги (битимдир). Икки муддатдан қай бирини адо қилсам бас, менга зўрлик қилинмас. Аллоҳ айтаётган сўзимизга гувоҳдир»'}, {'chapter': 28, 'verse': 29, 'text': '۞فَلَمَّا قَضَىٰ مُوسَى ٱلۡأَجَلَ وَسَارَ بِأَهۡلِهِۦٓ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارٗاۖ قَالَ لِأَهۡلِهِ ٱمۡكُثُوٓاْ إِنِّيٓ ءَانَسۡتُ نَارٗا لَّعَلِّيٓ ءَاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ جَذۡوَةٖ مِّنَ ٱلنَّارِ لَعَلَّكُمۡ تَصۡطَلُونَ\nЭнди қачонки Мусо (келишилган) муддатни ўтаб, аҳли-оиласи билан (Миср томон) йўлга тушгач, Тур (тоғи) тарафдан бир оловни кўриб қолди. У аҳли-оиласига: «Сизлар кутиб туринглар. Аниқки мен бир оловни кўриб қолдим. Шояд сизларга бирон хабар ёки исиниб олишларингиз учун у оловдан чўғ олиб келсам», деди'}, {'chapter': 28, 'verse': 30, 'text': 'فَلَمَّآ أَتَىٰهَا نُودِيَ مِن شَٰطِيِٕ ٱلۡوَادِ ٱلۡأَيۡمَنِ فِي ٱلۡبُقۡعَةِ ٱلۡمُبَٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن يَٰمُوسَىٰٓ إِنِّيٓ أَنَا ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nЭнди қачонки унинг олдига етиб келгач, у муборак жойдаги водийнинг ўнг тарафидан — дарахтдан унга нидо қилинди: «Эй Мусо, албатта, Мен барча оламларнинг Парвардигори Аллоҳдирман'}, {'chapter': 28, 'verse': 31, 'text': 'وَأَنۡ أَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰٓ أَقۡبِلۡ وَلَا تَخَفۡۖ إِنَّكَ مِنَ ٱلۡأٓمِنِينَ\nАсоингни (ерга) ташлагин!» Бас, қачон (Мусо) уни илондек қимирлаганини кўргач, ортига қарамай қочди. «Эй Мусо, кел, қўрқмагин. Зеро, сен (хавфу-хатардан) омонда бўлгувчи кишилардандирсан'}, {'chapter': 28, 'verse': 32, 'text': 'ٱسۡلُكۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖ وَٱضۡمُمۡ إِلَيۡكَ جَنَاحَكَ مِنَ ٱلرَّهۡبِۖ فَذَٰنِكَ بُرۡهَٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nҚўлингни чўнтагингга солгин, бирон зиён-дардсиз оппоқ бўлиб чиқур ва қанотингни (яъни, қўлингни) қўрқувдан (яъни, агар бу мўъжизалардан қўрқиб кетсанг қўлтиғингга) қисиб олгин (у яна ўз ҳолига қайтур). Бас, шу иккиси (яъни, асо билан қўл) Парвардигоринг томонидан Фиръавн ва унинг одамларига икки ҳужжатдир. Дарҳақиқат, улар фосиқ қавм бўлдилар»'}, {'chapter': 28, 'verse': 33, 'text': 'قَالَ رَبِّ إِنِّي قَتَلۡتُ مِنۡهُمۡ نَفۡسٗا فَأَخَافُ أَن يَقۡتُلُونِ\n(Мусо) деди: «Парвардигорим, мен улардан бир жонни ўлдириб қўйганман. Ва улар мени ўлдиришларидан қўрқурман'}, {'chapter': 28, 'verse': 34, 'text': 'وَأَخِي هَٰرُونُ هُوَ أَفۡصَحُ مِنِّي لِسَانٗا فَأَرۡسِلۡهُ مَعِيَ رِدۡءٗا يُصَدِّقُنِيٓۖ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ\nОғам Ҳорун мендан кўра тили гўёроқдир. Бас, уни мен билан бирга мени тасдиқлайдиган ёрдамчи қилиб юборгин. Чунки мени улар ёлғончи қилишларидан қўрқурман»'}, {'chapter': 28, 'verse': 35, 'text': 'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجۡعَلُ لَكُمَا سُلۡطَٰنٗا فَلَا يَصِلُونَ إِلَيۡكُمَا بِـَٔايَٰتِنَآۚ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلۡغَٰلِبُونَ\n(Аллоҳ) айтди: «Биз сени оғанг билан қувватлантирурмиз ва иккингиз учун салтанат (яъни, ғалаба ато) қилурмиз. Бас, улар сизларга (бирон зиён) етказа олмаслар. Бизнинг оят-мўъжизаларимиз билан сизлар ҳам, сизларга эргашган кишилар ҳам ғолиб бўлгувчидирлар»'}, {'chapter': 28, 'verse': 36, 'text': 'فَلَمَّا جَآءَهُم مُّوسَىٰ بِـَٔايَٰتِنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا سِحۡرٞ مُّفۡتَرٗى وَمَا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ\nЭнди қачонки Мусо уларга Бизнинг аниқ-равшан оят-мўъжизаларимизни келтиргач, улар: «Бу ҳеч нарса эмас, магар уйдирма-сеҳрдир. Бизлар бу нарсани ўтган ота-боболаримиз (замони)да бўлганини ҳам эшитган эмасмиз», дедилар'}, {'chapter': 28, 'verse': 37, 'text': 'وَقَالَ مُوسَىٰ رَبِّيٓ أَعۡلَمُ بِمَن جَآءَ بِٱلۡهُدَىٰ مِنۡ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ\nМусо айтди: «Парвардигорим ким Ўзининг ҳузуридан ҳидоят келтирганини ҳам, Охират диёридаги оқибат кимнинг фойдасига бўлишини ҳам жуда яхши билгувчидир. Албатта золим кимсалар нажот топмас»'}, {'chapter': 28, 'verse': 38, 'text': 'وَقَالَ فِرۡعَوۡنُ يَـٰٓأَيُّهَا ٱلۡمَلَأُ مَا عَلِمۡتُ لَكُم مِّنۡ إِلَٰهٍ غَيۡرِي فَأَوۡقِدۡ لِي يَٰهَٰمَٰنُ عَلَى ٱلطِّينِ فَٱجۡعَل لِّي صَرۡحٗا لَّعَلِّيٓ أَطَّلِعُ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ مِنَ ٱلۡكَٰذِبِينَ\nФиръавн: «Эй одамлар, мен сизлар учун ўзимдан бошқа бирон илоҳ борлигини билган эмасман. Бас, сен, эй Ҳомон, лойни пишириб, (ғишт қуйиб) мен учун бир баланд қаср бино қил, шоядки мен (унинг устига чиқиб) Мусонинг худосини кўрсам. Албатта, мен уни ёлғончи кимсалардан деб ўйламоқдадирман», деди'}, {'chapter': 28, 'verse': 39, 'text': 'وَٱسۡتَكۡبَرَ هُوَ وَجُنُودُهُۥ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَظَنُّوٓاْ أَنَّهُمۡ إِلَيۡنَا لَا يُرۡجَعُونَ\nУ (Фиръавн) ҳам, унинг лашкарлари ҳам (иймон келтирмасликлари билан) Ер юзида ноҳақ кибру-ҳаво қилдилар ва ўзларини Бизга қайтарилмайдилар, деб гумон қилдилар'}, {'chapter': 28, 'verse': 40, 'text': 'فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي ٱلۡيَمِّۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّـٰلِمِينَ\nБас, Биз уни ва лашкарларини ушладик-да, денгизга ташладик. Энди сиз у золим кимсаларнинг оқибати қандай бўлганини кўринг'}, {'chapter': 28, 'verse': 41, 'text': 'وَجَعَلۡنَٰهُمۡ أَئِمَّةٗ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ لَا يُنصَرُونَ\nВа уларни, (ўзларига эргашган динсиз кимсаларни) дўзахга чорлаб турадиган пешволар қилиб қўйдик. Қиёмат Кунида ҳам уларга ёрдам берилмас'}, {'chapter': 28, 'verse': 42, 'text': 'وَأَتۡبَعۡنَٰهُمۡ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ هُم مِّنَ ٱلۡمَقۡبُوحِينَ\nБиз бу дунёда ҳам уларга лаънатни эргаштирдик. Қиёмат Кунида ҳам улар (Бизнинг раҳматимиздан) йироқ қилингувчи кимсалардандирлар'}, {'chapter': 28, 'verse': 43, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ مِنۢ بَعۡدِ مَآ أَهۡلَكۡنَا ٱلۡقُرُونَ ٱلۡأُولَىٰ بَصَآئِرَ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُمۡ يَتَذَكَّرُونَ\nАниқки Биз аввалги аср-авлодларни (кофирликлари сабабли) ҳалок қилганимиздан кейин Мусога одамлар учун нур, ҳидоят ва раҳмат бўлган Китоб ато этдик. Шояд улар эслатма-ибрат олсалар'}, {'chapter': 28, 'verse': 44, 'text': 'وَمَا كُنتَ بِجَانِبِ ٱلۡغَرۡبِيِّ إِذۡ قَضَيۡنَآ إِلَىٰ مُوسَى ٱلۡأَمۡرَ وَمَا كُنتَ مِنَ ٱلشَّـٰهِدِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз Мусога ишни (яъни, пайғамбарликни) ҳукм қилган пайтда сиз (Тур тоғининг) ғарб томонида (Биз Мусо билан ваъдалашган жойда турган) эмас эдингиз ва гувоҳлардан ҳам бўлган эмас эдингиз'}, {'chapter': 28, 'verse': 45, 'text': 'وَلَٰكِنَّآ أَنشَأۡنَا قُرُونٗا فَتَطَاوَلَ عَلَيۡهِمُ ٱلۡعُمُرُۚ وَمَا كُنتَ ثَاوِيٗا فِيٓ أَهۡلِ مَدۡيَنَ تَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَا وَلَٰكِنَّا كُنَّا مُرۡسِلِينَ\nЛекин Биз (Мусо замонидан сўнг, кўп) асрларни пайдо қилдик, бас, уларга умр-замон чўзилиб кетди (ва улар Мусога юборилган ҳукмларни унутиб юбордилар. Шунда Биз ҳукму фармонларимизни одамларга ҳаққи-рост етказишингиз учун сизни пайғамбар қилиб юбордик). Сиз Мадян аҳли орасида тургувчи ҳам, уларга Бизнинг оятларимизни тиловат қилгувчи ҳам бўлган эмасдингиз. Лекин Биз (сизни) пайғамбар қилиб юборгувчи бўлдик, (бас, сиз Бизнинг ваҳийларимиз воситасида Мусо тўғрисида ҳам, Мадян шаҳрининг аҳли ҳақида ҳам хабардор бўлдингиз)'}, {'chapter': 28, 'verse': 46, 'text': 'وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذۡ نَادَيۡنَا وَلَٰكِن رَّحۡمَةٗ مِّن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nБиз (Мусога) нидо қилган пайтда сиз Турнинг ёнида бўлган эмас эдингиз. Лекин (сиз) Парвардигорингиз томонидан раҳмат бўлиб, сиздан илгари бирон огоҳлантиргувчи келмаган бир қавмни огоҳлантириш учун (пайғамбар қилиндингиз). Шояд улар эслатма-ибрат олсалар'}, {'chapter': 28, 'verse': 47, 'text': 'وَلَوۡلَآ أَن تُصِيبَهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَيَقُولُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ\nАгар уларга ўз қилмишлари сабабли бирон мусибат-бало етганида: «Парвардигоро, бизларга пайғамбар юборганингда эди, Сенинг оятларингга эргашар ва иймон келтиргувчилардан бўлур эдик», дейишмаса эди (Биз сизни уларга пайғамбар қилиб юбормас эдик)'}, {'chapter': 28, 'verse': 48, 'text': 'فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُواْ لَوۡلَآ أُوتِيَ مِثۡلَ مَآ أُوتِيَ مُوسَىٰٓۚ أَوَلَمۡ يَكۡفُرُواْ بِمَآ أُوتِيَ مُوسَىٰ مِن قَبۡلُۖ قَالُواْ سِحۡرَانِ تَظَٰهَرَا وَقَالُوٓاْ إِنَّا بِكُلّٖ كَٰفِرُونَ\nЭнди қачонки уларга Бизнинг ҳузуримиздан ҳақ (пайғамбар) келгач эса, улар: «Унга (яъни, Муҳаммадга) ҳам Мусога берилган мўъжизалар берилганида эди (бизлар унга эргашган бўлардик)», дедилар. Ахир улар илгари Мусога берилган мўъжизаларга ҳам кофир бўлмаганмидилар?! Улар: «(Таврот ва Қуръон) бир-бирини қўллаб-қувватлайдиган икки сеҳрдир», дедилар ва: «Бизлар (бу икки китобдан) ҳар бирига кофирдирмиз», дедилар'}, {'chapter': 28, 'verse': 49, 'text': 'قُلۡ فَأۡتُواْ بِكِتَٰبٖ مِّنۡ عِندِ ٱللَّهِ هُوَ أَهۡدَىٰ مِنۡهُمَآ أَتَّبِعۡهُ إِن كُنتُمۡ صَٰدِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «У ҳолда агар ростгўй бўлсанглар, сизлар Аллоҳ ҳузуридан у икки (Китобдан) кўра тўғрироқ бўлган бирон китоб келтиринглар, мен унга эргашурман»'}, {'chapter': 28, 'verse': 50, 'text': 'فَإِن لَّمۡ يَسۡتَجِيبُواْ لَكَ فَٱعۡلَمۡ أَنَّمَا يَتَّبِعُونَ أَهۡوَآءَهُمۡۚ وَمَنۡ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُ بِغَيۡرِ هُدٗى مِّنَ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nЭнди агар улар сизга жавоб бера олмасалар, бас, билингки улар фақат ҳавойи нафсларигагина эргашурлар. Аллоҳ томонидан ҳидоятланмаган ҳолда ўз ҳавои нафсига эргашган кимсадан ҳам йўлдан озганроқ ким бор?! Албатта, Аллоҳ бундай золим қавмни хидоят қилмас'}, {'chapter': 28, 'verse': 51, 'text': '۞وَلَقَدۡ وَصَّلۡنَا لَهُمُ ٱلۡقَوۡلَ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nАниқки, Биз эслатма-ибрат олишлари учун уларга Сўзни — Қуръонни улаб (яъни, пайдар-пай нозил қилдик)'}, {'chapter': 28, 'verse': 52, 'text': 'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ مِن قَبۡلِهِۦ هُم بِهِۦ يُؤۡمِنُونَ\nБиз (Қуръондан) илгари Китоб (яъни, Таврот, Инжил) ато этган зотлар (яъни, яҳудий ва насронийлар орасидаги мўминлар) унга (яъни, Қуръонга) иймон келтирурлар'}, {'chapter': 28, 'verse': 53, 'text': 'وَإِذَا يُتۡلَىٰ عَلَيۡهِمۡ قَالُوٓاْ ءَامَنَّا بِهِۦٓ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّنَآ إِنَّا كُنَّا مِن قَبۡلِهِۦ مُسۡلِمِينَ\nҚачонки уларга (Қуръон) тиловат қилинса, улар: «Бизлар унга иймон келтирдик. Албатта, у Парвардигоримиз томонидан бўлган Ҳақиқатдир. Шак-шубҳасиз, бизлар (Қуръон нозил бўлишидан) илгари ҳам мусулмонлар (яъни, Аллоҳни ягона билиб бўйинсунувчилар) эдик», дерлар'}, {'chapter': 28, 'verse': 54, 'text': 'أُوْلَـٰٓئِكَ يُؤۡتَوۡنَ أَجۡرَهُم مَّرَّتَيۡنِ بِمَا صَبَرُواْ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nАна ўша зотларга (дину-иймон йўлида) сабр-тоқат қилганлари сабабли ажр-мукофотлари қайта-қайта ато этилур. Улар яхшилик (яъни, сабр-тоқат) билан ёмонликни (яъни, кофирларнинг озорларини) дафъ қилурлар ва Биз уларга ризқ қилиб берган нарсалардан инфоқ-эҳсон қилурлар'}, {'chapter': 28, 'verse': 55, 'text': 'وَإِذَا سَمِعُواْ ٱللَّغۡوَ أَعۡرَضُواْ عَنۡهُ وَقَالُواْ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ سَلَٰمٌ عَلَيۡكُمۡ لَا نَبۡتَغِي ٱلۡجَٰهِلِينَ\nҚачон беҳуда (сўз ё ишни) эшитсалар, ундан юз ўгирурлар ва: «Бизларнинг амалларимиз ўзимиз учун, сизларнинг амалларингиз ҳам ўзларингиз учундир. Омон бўлинглар. Бизлар нодон кимсалар (билан ҳамсуҳбат бўлиш)ни истамаймиз», дерлар'}, {'chapter': 28, 'verse': 56, 'text': 'إِنَّكَ لَا تَهۡدِي مَنۡ أَحۡبَبۡتَ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۚ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), аниқки, сиз ўзингиз суйган кишиларни ҳидоят қила олмассиз, лекин Аллоҳ Ўзи хоҳлаган кишиларни ҳидоят қилур. У ҳидоят топгувчи зотларни яхшироқ билгувчидир'}, {'chapter': 28, 'verse': 57, 'text': 'وَقَالُوٓاْ إِن نَّتَّبِعِ ٱلۡهُدَىٰ مَعَكَ نُتَخَطَّفۡ مِنۡ أَرۡضِنَآۚ أَوَلَمۡ نُمَكِّن لَّهُمۡ حَرَمًا ءَامِنٗا يُجۡبَىٰٓ إِلَيۡهِ ثَمَرَٰتُ كُلِّ شَيۡءٖ رِّزۡقٗا مِّن لَّدُنَّا وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nУлар (яъни, Қурайш кофирлари): «(Эй Муҳаммад алайҳис-салоту вас-салом), агар бизлар сен билан бирга Тўғри Йўлга эргашсак, ўз еримиздан ажралурмиз», дедилар. Ахир Биз уларга тинч-осойишта Ҳарамни макон қилиб бермадикми?! Барча нарсанинг мева-ҳосиллари Бизнинг даргоҳимиздан ризқу рўз бўлган ҳолда ўша жойга йиғилади-ку! Лекин уларнинг кўплари (буни) билмаслар'}, {'chapter': 28, 'verse': 58, 'text': 'وَكَمۡ أَهۡلَكۡنَا مِن قَرۡيَةِۭ بَطِرَتۡ مَعِيشَتَهَاۖ فَتِلۡكَ مَسَٰكِنُهُمۡ لَمۡ تُسۡكَن مِّنۢ بَعۡدِهِمۡ إِلَّا قَلِيلٗاۖ وَكُنَّا نَحۡنُ ٱلۡوَٰرِثِينَ\nБиз қанча маишати ҳаддан ошган қишлоқ-шаҳарни ҳалок қилдик. Бас, ўшаларнинг масканлари улардан кейин камдан-кам маскан қилинди. (Уларнинг барчалари ҳалок бўлдилар) ва (уларни мулку-диёрларига) Биз Ўзимиз ворис бўлиб қолдик'}, {'chapter': 28, 'verse': 59, 'text': 'وَمَا كَانَ رَبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ حَتَّىٰ يَبۡعَثَ فِيٓ أُمِّهَا رَسُولٗا يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَاۚ وَمَا كُنَّا مُهۡلِكِي ٱلۡقُرَىٰٓ إِلَّا وَأَهۡلُهَا ظَٰلِمُونَ\nПарвардигорингиз то барча қишлоқ-шаҳарларнинг марказларига Бизнинг оятларимизни уларга тиловат қиладиган бир пайғамбарни юбормагунича, (у қишлоқ-шаҳарларни) ҳалок қилугувчи бўлмади. Биз қишлоқ-шаҳарларни фақат уларнинг аҳолиси золим — кофир бўлган ҳолдагина ҳалок қилгувчи бўлдик'}, {'chapter': 28, 'verse': 60, 'text': 'وَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَزِينَتُهَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰٓۚ أَفَلَا تَعۡقِلُونَ\nСизларга берилган нарсалар фақат ҳаёти дунёнинг мато ва зийнатларидир. Аллоҳ ҳузуридаги (ажру-мукофот) эса яхшироқ ва боқийроқдир. Ахир ақл юргизмайсизларми'}, {'chapter': 28, 'verse': 61, 'text': 'أَفَمَن وَعَدۡنَٰهُ وَعۡدًا حَسَنٗا فَهُوَ لَٰقِيهِ كَمَن مَّتَّعۡنَٰهُ مَتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا ثُمَّ هُوَ يَوۡمَ ٱلۡقِيَٰمَةِ مِنَ ٱلۡمُحۡضَرِينَ\nАхир Биз гўзал ваъда (жаннат) ваъда қилган, бас, унга эришган киши, Биз ҳаёти дунё матосидан озгина фойдалантириб, сўнгра Қиёмат Кунида (мангу азобга) дучор қилингувчилардан бўлган кимса каби бўлурми'}, {'chapter': 28, 'verse': 62, 'text': 'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ\n(Эсланг, Аллоҳ Қиёмат Кунида) уларга нидо қилиб: «Сизлар (бор деб) гумон қилгувчи бўлган «шерикларим» қаерда қолишди?», деганида'}, {'chapter': 28, 'verse': 63, 'text': 'قَالَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ رَبَّنَا هَـٰٓؤُلَآءِ ٱلَّذِينَ أَغۡوَيۡنَآ أَغۡوَيۡنَٰهُمۡ كَمَا غَوَيۡنَاۖ تَبَرَّأۡنَآ إِلَيۡكَۖ مَا كَانُوٓاْ إِيَّانَا يَعۡبُدُونَ\nУстларига Сўз — азоб тушиши ҳақ-муқаррар бўлган кимсалар (яъни, одамларни Ҳақ йўлидан оздирган доҳийлар): «Парвардигоро, ана у бизлар йўлдан оздирган кимсаларни ҳам худди ўзимиз йўлдан озган каби йўлдан оздиргандирмиз (яъни, бизлар ҳам, барчамиз ўз ихтиёримиз билан озгандирмиз). Бизлар Сенга (яъни, уларнинг йўлдан озишларига айбдор эмасмиз) оқдирмиз. Улар бизларга ибодат қилгувчи бўлмаганлар, (балки ўз ҳавои нафсларига ибодат қилганлар)», дерлар'}, {'chapter': 28, 'verse': 64, 'text': 'وَقِيلَ ٱدۡعُواْ شُرَكَآءَكُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَرَأَوُاْ ٱلۡعَذَابَۚ لَوۡ أَنَّهُمۡ كَانُواْ يَهۡتَدُونَ\nУларга: «Бутларингизга илтижо қилинглар, (мана энди сизларга ёрдам берсинлар)», дейилиб, уларга илтижо қилинганларида (бутлар) жавоб қилмаслар ва улар азобни кўрурлар. Агар улар ҳидоят топсалар эди (бундай азобни кўрмаган бўлур эдилар)'}, {'chapter': 28, 'verse': 65, 'text': 'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ مَاذَآ أَجَبۡتُمُ ٱلۡمُرۡسَلِينَ\n(Аллоҳ) уларга нидо қилиб: «Пайғамбарларимга нима жавоб қилган эдинглар?», дейдиган кунда'}, {'chapter': 28, 'verse': 66, 'text': 'فَعَمِيَتۡ عَلَيۡهِمُ ٱلۡأَنۢبَآءُ يَوۡمَئِذٖ فَهُمۡ لَا يَتَسَآءَلُونَ\nЎша кунда уларга хабарлар (яъни, ўзларини оқлайдиган баҳонаю сабаблар) кўринмай қолур, бас, (даҳшат ва қўрқувга тушиб) бир-бирларидан ҳам сўраша олмаслар'}, {'chapter': 28, 'verse': 67, 'text': 'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَعَسَىٰٓ أَن يَكُونَ مِنَ ٱلۡمُفۡلِحِينَ\nЭнди ким тавба қилиб, иймон келтириб, яхши амал қилиб ўтган бўлса, шоядки ана ўша (киши) нажот топгувчилардан бўлур'}, {'chapter': 28, 'verse': 68, 'text': 'وَرَبُّكَ يَخۡلُقُ مَا يَشَآءُ وَيَخۡتَارُۗ مَا كَانَ لَهُمُ ٱلۡخِيَرَةُۚ سُبۡحَٰنَ ٱللَّهِ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nПарвардигорингиз Ўзи хоҳлаган нарсани яратур ва (Ўзи хоҳлаган ишни) ихтиёр қилур. Улар учун ихтиёр йўқдир. Аллоҳ уларнинг ширкларидан пок ва юксакдир'}, {'chapter': 28, 'verse': 69, 'text': 'وَرَبُّكَ يَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ\nПарвардигорингиз уларнинг диллари яширган нарсаларни — сирларни ҳам, ошкор қилган нарсаларини — сўзларини ҳам билур'}, {'chapter': 28, 'verse': 70, 'text': 'وَهُوَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡحَمۡدُ فِي ٱلۡأُولَىٰ وَٱلۡأٓخِرَةِۖ وَلَهُ ٱلۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ\nУ (ёлғиз) Аллоҳдир. Ҳеч қандай илоҳ йўқ, магар Унинг Ўзигина бордир. Аввалу охирда (яъни, дунё-ю Охиратда) ҳамду сано (ёлғиз) Унинг учундир. Ҳукм Унинг (ҳукмидир). Ва (барчаларингиз) ёлғиз Унга қайтарилурсизлар'}, {'chapter': 28, 'verse': 71, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلَّيۡلَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِضِيَآءٍۚ أَفَلَا تَسۡمَعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Хабар берингиз-чи, агар Аллоҳ кечани Қиёмат Кунигача устингизда мангу қилиб қўйса, Аллоҳдан ўзга қайси бир илоҳ сизларга бирон ёруғлик келтира олур?! Ахир англамайсизларми?»'}, {'chapter': 28, 'verse': 72, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلنَّهَارَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِلَيۡلٖ تَسۡكُنُونَ فِيهِۚ أَفَلَا تُبۡصِرُونَ\nАйтинг: «Хабар берингиз-чи, агар Аллоҳ кундузни Қиёмат Кунигача устингизда мангу қилиб қўйса, Аллоҳдан ўзга қайси бир илоҳ сизларга ором оладиган кечани келтира олур?! Ахир кўрмайсизларми'}, {'chapter': 28, 'verse': 73, 'text': 'وَمِن رَّحۡمَتِهِۦ جَعَلَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ لِتَسۡكُنُواْ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n(Аллоҳ) сизлар учун кеча ва кундузни унга (яъни, кечада) ором олишларингиз, (кундузи эса) Унинг фазл-ризқидан исташларингиз ва шукр қилишларингиз учун пайдо қилиб қўйгани Унинг раҳмат — марҳаматидандир'}, {'chapter': 28, 'verse': 74, 'text': 'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ\n(Эсланг, Аллоҳ Қиёмат Кунида) уларга нидо қилиб: «Сизлар (бор деб) гумон қилгучи бўлган «шерикларим» қаерда қолишди?!», дер'}, {'chapter': 28, 'verse': 75, 'text': 'وَنَزَعۡنَا مِن كُلِّ أُمَّةٖ شَهِيدٗا فَقُلۡنَا هَاتُواْ بُرۡهَٰنَكُمۡ فَعَلِمُوٓاْ أَنَّ ٱلۡحَقَّ لِلَّهِ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ\nБиз (у кунда) ҳар бир умматдан бир гувоҳни (яъни, пайғамбарни) чиқариб, (уларга «Аллоҳ ёлғиз эмас», деб келтирган ширкларингиз ҳақ бўлса қани) ҳужжат-далилларингизни келтиринглар-чи», дедик. Бас, улар (худолик) ҳаққи-ҳуқуқи (ёлғиз Аллоҳники эканини) билдилар ва ўзлари тўқиб олган «худолари» эса улардан ғойиб бўлди'}, {'chapter': 28, 'verse': 76, 'text': '۞إِنَّ قَٰرُونَ كَانَ مِن قَوۡمِ مُوسَىٰ فَبَغَىٰ عَلَيۡهِمۡۖ وَءَاتَيۡنَٰهُ مِنَ ٱلۡكُنُوزِ مَآ إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِٱلۡعُصۡبَةِ أُوْلِي ٱلۡقُوَّةِ إِذۡ قَالَ لَهُۥ قَوۡمُهُۥ لَا تَفۡرَحۡۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡفَرِحِينَ\nАлбатта, Қорун ўзи Мусо қавмидан эди. Бас, у (қавмдошларига) кибру-ҳаво қилди. Биз унга хазина-дафиналардан калитлари(ни кўтариб юришнинг ўзи) куч-қувват эгалари бўлган бир жамоатга ҳам оғирлиқ қиладиган нарсаларни ато этган эдик. Ўшанда қавмдошлари унга: «Ховлиқмагин, чунки Аллоҳ ҳовлиқма кимсаларни суймас'}, {'chapter': 28, 'verse': 77, 'text': 'وَٱبۡتَغِ فِيمَآ ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلۡأٓخِرَةَۖ وَلَا تَنسَ نَصِيبَكَ مِنَ ٱلدُّنۡيَاۖ وَأَحۡسِن كَمَآ أَحۡسَنَ ٱللَّهُ إِلَيۡكَۖ وَلَا تَبۡغِ ٱلۡفَسَادَ فِي ٱلۡأَرۡضِۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ\nВа Аллоҳ сенга ато этган мол-давлат билан (аввало) Охират (ободлигини) истагин ва дунёдан бўлган насибангни ҳам унутмагин. Аллоҳ сенга эҳсон қилгани каби сен ҳам (Аллоҳнинг бандаларига) инфоқ-эҳсон қил. Ерда (зулму-зўравонлик билан) бузғунчилик қилишга уринма. Чунки Аллоҳ бузғунчи кимсаларни суймас», деганларида'}, {'chapter': 28, 'verse': 78, 'text': 'قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمٍ عِندِيٓۚ أَوَلَمۡ يَعۡلَمۡ أَنَّ ٱللَّهَ قَدۡ أَهۡلَكَ مِن قَبۡلِهِۦ مِنَ ٱلۡقُرُونِ مَنۡ هُوَ أَشَدُّ مِنۡهُ قُوَّةٗ وَأَكۡثَرُ جَمۡعٗاۚ وَلَا يُسۡـَٔلُ عَن ذُنُوبِهِمُ ٱلۡمُجۡرِمُونَ\nУ: «Менга (бор молу-давлатим) фақат ўзимдаги билим сабаблигина ато этилгандир, (бас, ҳеч ким уни мендан тортиб ололмас»), деди. Ахир у ўзидан аввалги асрлар-авлодлардан, ундан кўра қувватлироқ ва жамғармаси ҳам кўпроқ (қанчадан-қанча) кимсаларни Аллоҳ ҳалок қилганини билмасми?! У жиноятчи кимсалардан гуноҳлари ҳақида сўралмас ҳам, (балки улар ҳеч қандай сўроқ-саволсиз дўзахга ташланурлар)'}, {'chapter': 28, 'verse': 79, 'text': 'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ فِي زِينَتِهِۦۖ قَالَ ٱلَّذِينَ يُرِيدُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا يَٰلَيۡتَ لَنَا مِثۡلَ مَآ أُوتِيَ قَٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٖ\nСўнг, (Қорун) қавми олдига ясан-тусан қилиб чиққан эди, ҳаёти дунёни истайдиган кимсалар: «Эх, қани эди бизлар учун ҳам Қорунга ато этилган молу-давлат бўлса эди. Дарҳақиқат, у улуғ насиба эгасидир», дедилар'}, {'chapter': 28, 'verse': 80, 'text': 'وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَيۡلَكُمۡ ثَوَابُ ٱللَّهِ خَيۡرٞ لِّمَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗاۚ وَلَا يُلَقَّىٰهَآ إِلَّا ٱلصَّـٰبِرُونَ\nИлм-маъфират ато этилган кишилар эса: «Ўлим бўлсин сизларга! Иймон келтирган ва яхши амал қилган киши учун Аллоҳ берадиган ажр-савоб яхшироқ-ку! У (савобга) фақат сабр-қаноатли кишиларгина эришурлар», дедилар'}, {'chapter': 28, 'verse': 81, 'text': 'فَخَسَفۡنَا بِهِۦ وَبِدَارِهِ ٱلۡأَرۡضَ فَمَا كَانَ لَهُۥ مِن فِئَةٖ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلۡمُنتَصِرِينَ\nБас, Биз (Қорунни) ҳам, унинг ҳовли-жойини ҳам (ерга) ютдирдик. Сўнг унинг учун Аллоҳдан ёрдам берадиган ўзга бирон жамоат бўлмади ва унинг ўзи ҳам ғолиблардан бўлмади'}, {'chapter': 28, 'verse': 82, 'text': 'وَأَصۡبَحَ ٱلَّذِينَ تَمَنَّوۡاْ مَكَانَهُۥ بِٱلۡأَمۡسِ يَقُولُونَ وَيۡكَأَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُۖ لَوۡلَآ أَن مَّنَّ ٱللَّهُ عَلَيۡنَا لَخَسَفَ بِنَاۖ وَيۡكَأَنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ\nВа куни кеча унинг мартабасини орзу қилган кимсалар: «Воажаб, Аллоҳ бандаларидан Ўзи хоҳлаган кишиларнинг ризқини кенг қилиб (Ўзи хоҳлаган бандаларининг ризқини) танг қилиб берар экан-да. Агар Аллоҳ бизларга марҳамат қилмаганида, бизларни ҳам (ерга) ютдирган бўлур эди. Воажаб, кофир бўлган кимсалар нажот топмас экан-да», деб қолдилар'}, {'chapter': 28, 'verse': 83, 'text': 'تِلۡكَ ٱلدَّارُ ٱلۡأٓخِرَةُ نَجۡعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوّٗا فِي ٱلۡأَرۡضِ وَلَا فَسَادٗاۚ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ\nБиз ўша Охират диёрини Ер юзида зулму-зўравонлик ва бузғунчилик қилишни истмайдиган кишилар учун қилурмиз. Оқибат тақво қилгувчи кишиларникидир'}, {'chapter': 28, 'verse': 84, 'text': 'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَى ٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ إِلَّا مَا كَانُواْ يَعۡمَلُونَ\nКим яхшилик билан келса, бас, унинг учун (қилган яхшилигидан) яхшироқ (мукофот бўлур). Ким ёмонлик билан келса, бас, ёмонликлар қилган кимсалар фақат ўзлари қилган ёмонликлари билангина жазоланадилар'}, {'chapter': 28, 'verse': 85, 'text': 'إِنَّ ٱلَّذِي فَرَضَ عَلَيۡكَ ٱلۡقُرۡءَانَ لَرَآدُّكَ إِلَىٰ مَعَادٖۚ قُل رَّبِّيٓ أَعۡلَمُ مَن جَآءَ بِٱلۡهُدَىٰ وَمَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта сизга (ушбу) Қуръонни (яъни, уни тиловат қилишни унга амал қилишни ва уни умматларингизга (етказишни) фарз қилган зот, шак-шубҳасиз, сизни бир қайтадиган жойга (Маккага) қайтаргувчидир. (Мушрикларга) айтинг: «Парвардигорим, ҳидоят келтирган кишиларни ҳам, очиқ-равшан залолатдаги кимсаларни ҳам жуда яхши билгувчидир»'}, {'chapter': 28, 'verse': 86, 'text': 'وَمَا كُنتَ تَرۡجُوٓاْ أَن يُلۡقَىٰٓ إِلَيۡكَ ٱلۡكِتَٰبُ إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۖ فَلَا تَكُونَنَّ ظَهِيرٗا لِّلۡكَٰفِرِينَ\nСиз ўзингизга Китоб — Қуръон туширилишидан умидвор ҳам бўлган эмасдингиз. Фақат Парвардигорингиз томонидан раҳмат-марҳамат бўлиб (сизга Қуръон нозил қилинди). Бас, сиз ҳаргиз кофир кимсаларга ёрдамчи бўлманг'}, {'chapter': 28, 'verse': 87, 'text': 'وَلَا يَصُدُّنَّكَ عَنۡ ءَايَٰتِ ٱللَّهِ بَعۡدَ إِذۡ أُنزِلَتۡ إِلَيۡكَۖ وَٱدۡعُ إِلَىٰ رَبِّكَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ\nВа сизга Аллоҳнинг оятлари нозил қилинганидан сўнг ҳаргиз (у оятларга амал қилишингиздан) тўсмасинлар! Сиз (одамларни ҳам ёлғиз) Парвардигорингизга (ибодат қилишга) чорланг ва мушрик кимсалардан бўлманг'}, {'chapter': 28, 'verse': 88, 'text': 'وَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۘ لَآ إِلَٰهَ إِلَّا هُوَۚ كُلُّ شَيۡءٍ هَالِكٌ إِلَّا وَجۡهَهُۥۚ لَهُ ٱلۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ\nАллоҳ билан бирга бошқа бирон «илоҳ»га илтижо қилманг! Ҳеч қандай илоҳ йўқ магар Унинг Ўзигина бордир. Барча нарса ҳалок бўлгувчидир, магар Унинг Ўзигина (мангудир). Ҳукм Унинг (ҳукмидир), ва (барчаларингиз) ёлғиз Унга қайтарилурсизлар'}, {'chapter': 29, 'verse': 1, 'text': 'الٓمٓ\nАлиф, Лом, Мим'}, {'chapter': 29, 'verse': 2, 'text': 'أَحَسِبَ ٱلنَّاسُ أَن يُتۡرَكُوٓاْ أَن يَقُولُوٓاْ ءَامَنَّا وَهُمۡ لَا يُفۡتَنُونَ\nОдамлар: «Иймон келтирдик», дейишлари билангина, имтиҳон қилинмаган ҳолларида, қўйиб қўйилишларини ўйладиларми'}, {'chapter': 29, 'verse': 3, 'text': 'وَلَقَدۡ فَتَنَّا ٱلَّذِينَ مِن قَبۡلِهِمۡۖ فَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ صَدَقُواْ وَلَيَعۡلَمَنَّ ٱلۡكَٰذِبِينَ\nҲолбуки, Биз улардан аввалги (иймон келтирган барча) кишиларни имтиҳон қилган эдик-ку!! Бас, (шу имтиҳон воситасида) албатта Аллоҳ («Иймон келтирдик», деб) рост сўзлаган кишиларни ҳам, ёлғончи кимсаларни ҳам аниқ билур'}, {'chapter': 29, 'verse': 4, 'text': 'أَمۡ حَسِبَ ٱلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ أَن يَسۡبِقُونَاۚ سَآءَ مَا يَحۡكُمُونَ\nЁки ёмон амаллар қиладиган кимсалар Биздан (жазойимиздан) қочиб қутулишни ўйладиларми?! Нақадар ёмон ҳукм чиқарадилар-а'}, {'chapter': 29, 'verse': 5, 'text': 'مَن كَانَ يَرۡجُواْ لِقَآءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَأٓتٖۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nКим Аллоҳга рўбарў бўлишдан умидвор бўлса, бас, албатта, Аллоҳ (белгилаган мукофот ва жазо) фурсати шак-шубҳасиз, келгувчидир. У эшитгувчи, билгувчидир'}, {'chapter': 29, 'verse': 6, 'text': 'وَمَن جَٰهَدَ فَإِنَّمَا يُجَٰهِدُ لِنَفۡسِهِۦٓۚ إِنَّ ٱللَّهَ لَغَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ\nКим жиҳод қилса, фақат ўз (фойдаси) учун жиҳод қилур (яъни, ким Аллоҳ йўлида саъй-ҳаракат қилса, бундан Аллоҳ эмас, балки ўша кишининг ўзи учун манфаат бўлур). Зеро, Аллоҳ барча одамлардан беҳожатдир'}, {'chapter': 29, 'verse': 7, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَنَجۡزِيَنَّهُمۡ أَحۡسَنَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ\nИймон келтирган ва яхши амаллар қилган зотларнинг ёмонликларини (яъни, аввал қилган гуноҳларини) шак-шубҳасиз, ўчирурмиз ва албатта уларни қилиб ўтган энг чиройли амаллари билан мукофотлармиз'}, {'chapter': 29, 'verse': 8, 'text': 'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ حُسۡنٗاۖ وَإِن جَٰهَدَاكَ لِتُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَآۚ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nБиз инсонни ота-онасига яхшилик қилишга буюрдик (яъни, ота-она хоҳ яхши, хоҳ ёмон бўлсин, хоҳ мусулмон, хоҳ кофир бўлсин, уларга яхшилик қилиш фарзанднинг бурчидир, аммо) агар улар сен ўзинг билмаган нарсаларни (яъни, сохта «худо»ларни) Менга шерик қилишинг учун зўрласалар, у ҳолда уларга итоат этмагин! (Барчангиз) Менга қайтурсиз, бас, (ана ўша кунда) Мен сизларга қилиб ўтган амалларингизнинг хабарини берурман'}, {'chapter': 29, 'verse': 9, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُدۡخِلَنَّهُمۡ فِي ٱلصَّـٰلِحِينَ\nИймон келтирган ва яхши амаллар қилган зотларни шак-шубҳасиз, солиҳ бандалар қаторига (яъни, жаннатга) дохил қилурмиз (киритурмиз)'}, {'chapter': 29, 'verse': 10, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَآ أُوذِيَ فِي ٱللَّهِ جَعَلَ فِتۡنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِۖ وَلَئِن جَآءَ نَصۡرٞ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمۡۚ أَوَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِمَا فِي صُدُورِ ٱلۡعَٰلَمِينَ\nОдамлар орасида шундай кимсалар ҳам борки, ўзлари: «Аллоҳга иймон келтирдик», дейди-да, сўнг Аллоҳ йўлида (кофирлар томонидан бирон азият билан) озорланса, одамларнинг (бу) фитна-озорларини Аллоҳнинг азоби каби қилиб олур (яъни, ўткинчи ҳокимларнинг фитна-фасодларидан Аллоҳнинг азобидан қўрққандек қўрқиб ўз иймон-эътиқодидан кечур). Қасамки, агар Парвардигорингиз томонидан (сиз — мўминларга) ғалаба келса шак-шубҳасиз, улар: «Албатта бизлар сизлар билан бирга эдик», дерлар. Ахир Аллоҳ барча одамларнинг дилларидаги нарсаларни жуда яхши билгувчи эмасми'}, {'chapter': 29, 'verse': 11, 'text': 'وَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَلَيَعۡلَمَنَّ ٱلۡمُنَٰفِقِينَ\nАлбатта Аллоҳ иймон келтирган зотларни ҳам, мунофиқ кимсаларни ҳам аниқ билур'}, {'chapter': 29, 'verse': 12, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ ٱتَّبِعُواْ سَبِيلَنَا وَلۡنَحۡمِلۡ خَطَٰيَٰكُمۡ وَمَا هُم بِحَٰمِلِينَ مِنۡ خَطَٰيَٰهُم مِّن شَيۡءٍۖ إِنَّهُمۡ لَكَٰذِبُونَ\nКофир бўлган кимсалар иймон келтирган зотларга: «Сизлар бизларнинг йўлимизга эргашинглар, гуноҳларингизни бизлар кўтарурмиз», дедилар. Ҳолбуки, улар (мўминларнинг) гуноҳларидан бирон нарсани кўтара олгувчи эмасдирлар. Албатта улар ёлғончидирлар'}, {'chapter': 29, 'verse': 13, 'text': 'وَلَيَحۡمِلُنَّ أَثۡقَالَهُمۡ وَأَثۡقَالٗا مَّعَ أَثۡقَالِهِمۡۖ وَلَيُسۡـَٔلُنَّ يَوۡمَ ٱلۡقِيَٰمَةِ عَمَّا كَانُواْ يَفۡتَرُونَ\nАлбатта улар ўзларининг юк-гуноҳларини ҳам, у юклари билан бирга (бошқа) юкларни ҳам кўтарурлар ва албатта Қиёмат Кунида ўзлари тўқиб олган (ёлғонлари) тўғрисида сўралурлар'}, {'chapter': 29, 'verse': 14, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَلَبِثَ فِيهِمۡ أَلۡفَ سَنَةٍ إِلَّا خَمۡسِينَ عَامٗا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمۡ ظَٰلِمُونَ\nАниқки, Биз Нуҳни ўз қавмига (пайғамбар қилиб) юбордик. Бас, у уларнинг орасида эллик йили кам минг йил турди (аммо улар иймон келтирмадилар), бас, уларни золим-кофир бўлган ҳолларида тўфон (балоси) тутди'}, {'chapter': 29, 'verse': 15, 'text': 'فَأَنجَيۡنَٰهُ وَأَصۡحَٰبَ ٱلسَّفِينَةِ وَجَعَلۡنَٰهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ\nСўнг Биз (Нуҳ)га ва кема(даги) ҳамроҳларига нажот бердик ва (у тўфон балосини) барча оламларга оят-ибрат қилиб қўйдик'}, {'chapter': 29, 'verse': 16, 'text': 'وَإِبۡرَٰهِيمَ إِذۡ قَالَ لِقَوۡمِهِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُۖ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nИброҳимни (эсланг) — у ўз қавмига деган эди: «Аллоҳга ибодат қилинглар ва Ундан қўрқинглар! Агар билгувчи бўлсангизлар, мана шу сизлар учун яхшироқдир'}, {'chapter': 29, 'verse': 17, 'text': 'إِنَّمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ أَوۡثَٰنٗا وَتَخۡلُقُونَ إِفۡكًاۚ إِنَّ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ لَكُمۡ رِزۡقٗا فَٱبۡتَغُواْ عِندَ ٱللَّهِ ٱلرِّزۡقَ وَٱعۡبُدُوهُ وَٱشۡكُرُواْ لَهُۥٓۖ إِلَيۡهِ تُرۡجَعُونَ\nСизлар Аллоҳни қўйиб фақат бутларга ибодат қилмоқдасизлар ва (уларни «худолар», деб) ёлғон тўқимоқдасизлар. Аниқки, сизлар Аллоҳни қўйиб ибодат қилаётган нарсалар сизларга ризқу рўз беришга қодир эмаслар. Бас, (шундай экан) сизлар ризқни (ёлғиз) Аллоҳ даргоҳидан истанглар ва Унгагина ибодат қилинглар, Унгагина шукр қилинглар, (зотан), сизлар Унгагина қайтарилурсизлар'}, {'chapter': 29, 'verse': 18, 'text': 'وَإِن تُكَذِّبُواْ فَقَدۡ كَذَّبَ أُمَمٞ مِّن قَبۡلِكُمۡۖ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАгар сизлар (мени) ёлғончи қилсангизлар, бас, сизлардан аввалги умматлар ҳам (ўз пайғамбарларини) ёлғончи қилгандирлар. Пайғамбар зиммасида эса фақат (Аллоҳ фармонини бандаларга) очиқ-равшан етказишгина бордир»'}, {'chapter': 29, 'verse': 19, 'text': 'أَوَلَمۡ يَرَوۡاْ كَيۡفَ يُبۡدِئُ ٱللَّهُ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥٓۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nАхир улар даставвал Аллоҳ қандай қилиб (йўқдан) яратишини кўрмадиларми?! Сўнгра (Қиёмат Кунида эса) У ўша (аввал йўқдан бор қилган нарсаси)ни қайтарур. Албатта бу Аллоҳга осондир'}, {'chapter': 29, 'verse': 20, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ بَدَأَ ٱلۡخَلۡقَۚ ثُمَّ ٱللَّهُ يُنشِئُ ٱلنَّشۡأَةَ ٱلۡأٓخِرَةَۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Ерда айланиб (Аллоҳ) даставвал қандай қилиб яратганини (ва сўнг Ўзи йўқдан бор қилган нарсаларни йўқ қилганини) кўринглар. Сўнгра Аллоҳ (уларни) иккинчи (марта) пайдо қилур. Албатта Аллоҳ барча нарсага қодирдир'}, {'chapter': 29, 'verse': 21, 'text': 'يُعَذِّبُ مَن يَشَآءُ وَيَرۡحَمُ مَن يَشَآءُۖ وَإِلَيۡهِ تُقۡلَبُونَ\nУ Ўзи хоҳлаган кимсаларни азоблар ва Ўзи хоҳлаган кишиларга раҳм қилур. (Ёлғиз) Унгагина қайтарилурсизлар'}, {'chapter': 29, 'verse': 22, 'text': 'وَمَآ أَنتُم بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nСизлар (Аллоҳнинг жазосидан) на Ерга ва на осмонга қочиб қутулгувчи эмасдирсизлар. Ва сизлар учун Аллоҳдан ўзга бирон ёрдамчи йўқдир'}, {'chapter': 29, 'verse': 23, 'text': 'وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ وَلِقَآئِهِۦٓ أُوْلَـٰٓئِكَ يَئِسُواْ مِن رَّحۡمَتِي وَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ\nАллоҳнинг оятларини ва Унга рўбарў бўлишни инкор қилган кимсалар улар Менинг марҳаматимдан ноумид (маҳрум) бўлган кимсалардир ва улар учун аламли азоб бордир'}, {'chapter': 29, 'verse': 24, 'text': 'فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ ٱقۡتُلُوهُ أَوۡ حَرِّقُوهُ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nБас, (Иброҳимнинг даъватига) қавмининг жавоби фақат: «Уни ўлдиринглар ёки ёқиб юборинглар», дейишлари бўлди. Сўнг Аллоҳ унга оловдан нажот берди. Албатта бунда иймон келтирадиган қавм учун оят-ибратлар бордир'}, {'chapter': 29, 'verse': 25, 'text': 'وَقَالَ إِنَّمَا ٱتَّخَذۡتُم مِّن دُونِ ٱللَّهِ أَوۡثَٰنٗا مَّوَدَّةَ بَيۡنِكُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُ بَعۡضُكُم بِبَعۡضٖ وَيَلۡعَنُ بَعۡضُكُم بَعۡضٗا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ\nИброҳим айтди: «Сизлар фақат ҳаёти дунёдаги ўзаро ошна-оғайнигарчилигингизни кўзлаб, Аллоҳни қўйиб, бутларни ушладинглар. Ҳали Қиёмат Кунида айримларингиз (яъни, пешволарингиз) айримларингиздан (эргашувчилардан) тонур, айримларингиз айримларингизни лаънатлар. Сизларнинг борар жойингиз дўзахдир. (У жойда) сизлар учун ёрдамчилар йўқдир»'}, {'chapter': 29, 'verse': 26, 'text': '۞فَـَٔامَنَ لَهُۥ لُوطٞۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّيٓۖ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nБас, Лут унга иймон келтирди. (Иброҳим) айтди: «Албатта мен Парвардигорим (буюрган томон)га ҳижрат қилгувчидирман. Албатта Унинг Ўзигина қудрат ва ҳикмат Эгасидир»'}, {'chapter': 29, 'verse': 27, 'text': 'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِ ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَ وَءَاتَيۡنَٰهُ أَجۡرَهُۥ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ\nБиз (Иброҳимга ўғли) Исҳоқ ва (набираси) Яъқубни ҳадя этдик ҳамда пайғамбарликни ҳам (барча самовий китоблар)ни ҳам унинг зурриётига (хос) қилдик ва унга шу дунёда ҳам ажри-мукофотини бердик, албатта у Охиратда ҳам солиҳ зотлардандир'}, {'chapter': 29, 'verse': 28, 'text': 'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ إِنَّكُمۡ لَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ\nЛут ўз қавмига: «Албатта сизлар шундай бузуқлик қилмоқдасизларки, сизлардан илгари бутун оламлардан бирон кимса бундай қилмаган эди'}, {'chapter': 29, 'verse': 29, 'text': 'أَئِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ وَتَقۡطَعُونَ ٱلسَّبِيلَ وَتَأۡتُونَ فِي نَادِيكُمُ ٱلۡمُنكَرَۖ فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ ٱئۡتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nҲақиқатан, сизлар (хотинларингизни қўйиб) эркакларга борурмисизлар; йўлтўсар-қароқчилик қилурмисизлар; мажлисларингизда ёмон ишлар қилурмисизлар?», деганини эсланг. Бас, (Лут) қавмининг жавоби фақат «Агар сен ростгўй кишилардан бўлсанг, бизларга Аллоҳнинг азобини келтир-чи», дейишлари бўлди'}, {'chapter': 29, 'verse': 30, 'text': 'قَالَ رَبِّ ٱنصُرۡنِي عَلَى ٱلۡقَوۡمِ ٱلۡمُفۡسِدِينَ\n(Шунда) у айтди: «Парвардигорим, бу бузғунчи қавм устига Ўзинг мени ғолиб қил»'}, {'chapter': 29, 'verse': 31, 'text': 'وَلَمَّا جَآءَتۡ رُسُلُنَآ إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُوٓاْ إِنَّا مُهۡلِكُوٓاْ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِۖ إِنَّ أَهۡلَهَا كَانُواْ ظَٰلِمِينَ\nҚачонки Бизнинг элчиларимиз (яъни, фаришталар) Иброҳимга (фарзанд кўриши ҳақидаги) хушхабарни келтиришгач, айтдилар: «Албатта бизлар мана шу қишлоқ аҳлини ҳалок қилгувчидирмиз. Чунки унинг аҳли золим — кофир бўлдилар»'}, {'chapter': 29, 'verse': 32, 'text': 'قَالَ إِنَّ فِيهَا لُوطٗاۚ قَالُواْ نَحۡنُ أَعۡلَمُ بِمَن فِيهَاۖ لَنُنَجِّيَنَّهُۥ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ\n(Иброҳим): «Ахир у жойда Лут бор-ку?», деди. Улар айтдилар: «Бизлар У жойда ким борлигини яхшироҳ билгувчидирмиз. Албатта бизлар (Лутни) ва унинг аҳли-оиласини қутқарурмиз. Магар унинг хотини (нажот топмас, чунки у) қолиб ҳалок бўлгувчилардан эди'}, {'chapter': 29, 'verse': 33, 'text': 'وَلَمَّآ أَن جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗاۖ وَقَالُواْ لَا تَخَفۡ وَلَا تَحۡزَنۡ إِنَّا مُنَجُّوكَ وَأَهۡلَكَ إِلَّا ٱمۡرَأَتَكَ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ\nҚачонки элчиларимиз Лутнинг олдига келганларида, у бундан ёмон ҳолга тушди ва уларнинг келишларидан юраги сиқилди. Улар дедилар: «Қўрқма ва ғамгин бўлма. Албатта бизлар сенга ва аҳли оилангга нажот бергувчилардирмиз. Магар хотининг (нажот топмас чунки у) қолиб ҳалок бўлгувчилардан эди»'}, {'chapter': 29, 'verse': 34, 'text': 'إِنَّا مُنزِلُونَ عَلَىٰٓ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ\nШак-шубҳасиз, Биз фосиқ-итоатсиз бўлганлари учун бу қишлоқ аҳли устига осмондан азоб-тош ёғдирувчидирмиз'}, {'chapter': 29, 'verse': 35, 'text': 'وَلَقَد تَّرَكۡنَا مِنۡهَآ ءَايَةَۢ بَيِّنَةٗ لِّقَوۡمٖ يَعۡقِلُونَ\nАниқки, Биз ақл юргизадиган қавм учун (ибрат бўлсин, деб) у (қишлоқ)дан оят-нишона қолдиргандирмиз'}, {'chapter': 29, 'verse': 36, 'text': 'وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗا فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ وَٱرۡجُواْ ٱلۡيَوۡمَ ٱلۡأٓخِرَ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ\nМадян (қавми)га ўз биродарлари Шуъайбни (пайғамбар қилдик). Бас, у айтди: «Эй қавмим, Аллоҳга ибодат қилингиз ва Охират кунидан умидвор бўлингиз ҳамда Ер (юзи)да бузғунчилик қилиб санқиб юрмангиз!»'}, {'chapter': 29, 'verse': 37, 'text': 'فَكَذَّبُوهُ فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ\nУлар (Шуъайбни) ёлғончи қилишгач, уларни даҳшатли зилзила тутиб, турган жойларида (тутдек) тўкилдилар'}, {'chapter': 29, 'verse': 38, 'text': 'وَعَادٗا وَثَمُودَاْ وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَٰكِنِهِمۡۖ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِيلِ وَكَانُواْ مُسۡتَبۡصِرِينَ\nОд ва Самуд (қабилаларини ҳам ҳалок қилдик). (Бу) сизларга уларнинг масканларидан (яъни, у масканлардан қолган харобалардан) кўриниб турибди. Шайтон уларга (қилаётган) амалларини чиройли кўрсатиб, уларни (ҳақ) йўлдан тўсди. Ҳолбуки, улар ақлу ҳушли кишилар эди'}, {'chapter': 29, 'verse': 39, 'text': 'وَقَٰرُونَ وَفِرۡعَوۡنَ وَهَٰمَٰنَۖ وَلَقَدۡ جَآءَهُم مُّوسَىٰ بِٱلۡبَيِّنَٰتِ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ وَمَا كَانُواْ سَٰبِقِينَ\nҚорун, Фиръавн ва Ҳомонни ҳам (ҳалок қилдик). Дарҳақиқат, Мусо уларга (пайғамбар эканлигини далолат қиладиган) аниқ ҳужжатлар келтирганида улар Ер (юзи)да кибр-ҳаво қилдилар ва (Бизнинг азобимиздан) қочиб қутулгувчи бўлмадилар'}, {'chapter': 29, 'verse': 40, 'text': 'فَكُلًّا أَخَذۡنَا بِذَنۢبِهِۦۖ فَمِنۡهُم مَّنۡ أَرۡسَلۡنَا عَلَيۡهِ حَاصِبٗا وَمِنۡهُم مَّنۡ أَخَذَتۡهُ ٱلصَّيۡحَةُ وَمِنۡهُم مَّنۡ خَسَفۡنَا بِهِ ٱلۡأَرۡضَ وَمِنۡهُم مَّنۡ أَغۡرَقۡنَاۚ وَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nБиз (улардан) ҳар бирини ўз гуноҳи билан ушладик. Бас, уларнинг орасида Биз устига тош ёғдирган кимсалар ҳам бордир, улар орасида қичқириқ тутиб (ҳалок бўлган) кимсалар ҳам бордир, улар орасида Биз ерга ютдирган кимсалар ҳам бордир ва улар орасида Биз (сувга) ғарқ қилган кимсалар ҳам бордир. Аллоҳ уларга зулм қилгувчи бўлмади, лекин улар ўз жонларига жабр қилгувчи бўлдилар'}, {'chapter': 29, 'verse': 41, 'text': 'مَثَلُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَ كَمَثَلِ ٱلۡعَنكَبُوتِ ٱتَّخَذَتۡ بَيۡتٗاۖ وَإِنَّ أَوۡهَنَ ٱلۡبُيُوتِ لَبَيۡتُ ٱلۡعَنكَبُوتِۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nАллоҳдан ўзга «дўстлар»ни ушлаган кимсаларнинг мисоли худди (ўзи учун) уй қуриб олиб, (ўша уйидан паноҳ истаган) ўргимчакка ўхшайди. Албатта уйларнинг энг нимжони ўргимчак уясидир. Агар улар билсалар эди, (ўргимчак уяси каби ожиз бутлардан паноҳ истамаган бўлур эдилар)'}, {'chapter': 29, 'verse': 42, 'text': 'إِنَّ ٱللَّهَ يَعۡلَمُ مَا يَدۡعُونَ مِن دُونِهِۦ مِن شَيۡءٖۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nШак-шубҳасиз, Аллоҳ улар Ўзини қўйиб, илтижо қилишаётган бутларини билур. У қудрат ва ҳикмат Эгасидир'}, {'chapter': 29, 'verse': 43, 'text': 'وَتِلۡكَ ٱلۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنَّاسِۖ وَمَا يَعۡقِلُهَآ إِلَّا ٱلۡعَٰلِمُونَ\nУшбу масалларни Биз одамлар (ибрат олсинлар) учун айтурмиз. (Лекин) уларни фақат илм эгаларигина англай олурлар'}, {'chapter': 29, 'verse': 44, 'text': 'خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ\nАллоҳ осмонлар ва Ерни ҳақ (қонун) билан яратгандир. Албатта бунда иймон келтирган кишилар учун оят-ибрат бордир'}, {'chapter': 29, 'verse': 45, 'text': 'ٱتۡلُ مَآ أُوحِيَ إِلَيۡكَ مِنَ ٱلۡكِتَٰبِ وَأَقِمِ ٱلصَّلَوٰةَۖ إِنَّ ٱلصَّلَوٰةَ تَنۡهَىٰ عَنِ ٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۗ وَلَذِكۡرُ ٱللَّهِ أَكۡبَرُۗ وَٱللَّهُ يَعۡلَمُ مَا تَصۡنَعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз ўзингизга ваҳий қилинган Китоб — Қуръондан бўлган (оятлар)ни тиловат қилинг ва намозни тўкис адо қилинг! Албатта намоз бузуқлик ва ёмонликдан тўсур. Аниқки, Аллоҳни зикр қилмоқ (барча нарсадан) улуғроқдир. Аллоҳ қилаётан ишларингизни билиб турур'}, {'chapter': 29, 'verse': 46, 'text': '۞وَلَا تُجَٰدِلُوٓاْ أَهۡلَ ٱلۡكِتَٰبِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ إِلَّا ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡۖ وَقُولُوٓاْ ءَامَنَّا بِٱلَّذِيٓ أُنزِلَ إِلَيۡنَا وَأُنزِلَ إِلَيۡكُمۡ وَإِلَٰهُنَا وَإِلَٰهُكُمۡ وَٰحِدٞ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ\n(Эй мўминлар), сизлар аҳли китоб билан фақат энг чиройли йўсинда мужодала-мунозара қилинглар, магар уларнинг орасидаги зулму-зўравонлик қилган кимсалар билангина (кескин муомала қилишингиз мумкиндир). Ва айтинглар: «Бизлар ўзимизга нозил қилинган (Қуръон)га ҳам сизларга нозил қилинган (Таврот ва Инжил)га ҳам иймон келтирганмиз. Бизларнинг илоҳимиз ҳам, сизларнинг илоҳингиз ҳам Бир (илоҳ)дир ва бизлар Унгагина бўйинсунгувчимиз»'}, {'chapter': 29, 'verse': 47, 'text': 'وَكَذَٰلِكَ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَۚ فَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يُؤۡمِنُونَ بِهِۦۖ وَمِنۡ هَـٰٓؤُلَآءِ مَن يُؤۡمِنُ بِهِۦۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلۡكَٰفِرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, Биз сиздан аввалги пайғамбарларга китоблар нозил қилганмиз). Шунингдек, сизга ҳам Китоб — Қуръон нозил қилдик. Бас, Биз Китоб (яъни, Таврот ва Инжил) ато этган зотлар унга (яъни, Қуръонга) иймон келтирурлар. Ана у (Макка аҳли орасида ҳам) унга иймон келтирадиган кишилар бордир. Бизнинг оятларимизни фақат кофирларгина инкор қилур'}, {'chapter': 29, 'verse': 48, 'text': 'وَمَا كُنتَ تَتۡلُواْ مِن قَبۡلِهِۦ مِن كِتَٰبٖ وَلَا تَخُطُّهُۥ بِيَمِينِكَۖ إِذٗا لَّٱرۡتَابَ ٱلۡمُبۡطِلُونَ\nСиз (ўзингизга Қуръон нозил қилинишидан) илгари бирон китобни тиловат қилгувчи бўлган эмас эдингиз ва ўз қўлингиз билан хат ҳам ёзган эмас эдингиз. Акс ҳолда бузғунчи кимсалар албатта шубҳага тушган бўлур эдилар'}, {'chapter': 29, 'verse': 49, 'text': 'بَلۡ هُوَ ءَايَٰتُۢ بَيِّنَٰتٞ فِي صُدُورِ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلظَّـٰلِمُونَ\nЙўқ у (яъни, Қуръон) илм ато этилган зотларнинг кўнгилларидаги аниқ-равшан оятлардир. Бизнинг оятларимизни фақат золимларгина инкор қилур'}, {'chapter': 29, 'verse': 50, 'text': 'وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَٰتٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّمَا ٱلۡأٓيَٰتُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ\nУлар (кофирлар): «Унга (яъни, Муҳаммадга) ҳам Парвардигори томонидан оят-мўъжизалар тушганида эди», дедилар. Айтинг: «Оят-мўъжизалар ёлғиз Аллоҳнинг ҳузуридадир. Мен эса фақат (осийларни Аллоҳнинг азобидан) очиқ огоҳлантиргувчиман холос»'}, {'chapter': 29, 'verse': 51, 'text': 'أَوَلَمۡ يَكۡفِهِمۡ أَنَّآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ يُتۡلَىٰ عَلَيۡهِمۡۚ إِنَّ فِي ذَٰلِكَ لَرَحۡمَةٗ وَذِكۡرَىٰ لِقَوۡمٖ يُؤۡمِنُونَ\nАхир уларга тиловат қилинаётган шу Китобни Биз сизга нозил қилганимиз улар учун етарли эмасми?! Албатта бу (Китоб)да иймон келтирадиган қавм учун раҳмат ва эслатма бордир'}, {'chapter': 29, 'verse': 52, 'text': 'قُلۡ كَفَىٰ بِٱللَّهِ بَيۡنِي وَبَيۡنَكُمۡ شَهِيدٗاۖ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱلَّذِينَ ءَامَنُواْ بِٱلۡبَٰطِلِ وَكَفَرُواْ بِٱللَّهِ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nАйтинг: «Аллоҳ мен билан сизларнинг ўртангизда етарли гувоҳдир. У зот осмонлар ва Ердаги бор нарсани билур. Ботил нарсага (яъни, бутларга) иймон келтириб, Аллоҳга кофир бўлган кимсалар — ана ўшалар зиён кўргувчиларнинг ўзидирлар»'}, {'chapter': 29, 'verse': 53, 'text': 'وَيَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَوۡلَآ أَجَلٞ مُّسَمّٗى لَّجَآءَهُمُ ٱلۡعَذَابُۚ وَلَيَأۡتِيَنَّهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nУлар сиздан азобни тезлатишни талаб қилурлар. Агар белгиланган муддат (яъни, Қиёмат Куни) бўлмаса эди, албатта уларга (дарҳол) азоб келган бўлур эди. Шак-шубҳасиз, (азоб) уларга ўзлари сезмаган ҳолларида тўсатдан келур'}, {'chapter': 29, 'verse': 54, 'text': 'يَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِٱلۡكَٰفِرِينَ\nУлар сиздан азобни тезлатишни талаб қилурлар. Албатта жаҳаннам кофирларни ўраб олгувчидир'}, {'chapter': 29, 'verse': 55, 'text': 'يَوۡمَ يَغۡشَىٰهُمُ ٱلۡعَذَابُ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۡ وَيَقُولُ ذُوقُواْ مَا كُنتُمۡ تَعۡمَلُونَ\nУ кунда азоб уларнинг устиларидан ҳам, оёқларининг остидан ҳам ўраб олур ва (Аллоҳ) айтур: «Қилиб ўтган амалларингизнинг жазосини тотинглар!»'}, {'chapter': 29, 'verse': 56, 'text': 'يَٰعِبَادِيَ ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ أَرۡضِي وَٰسِعَةٞ فَإِيَّـٰيَ فَٱعۡبُدُونِ\nЭй иймон келтирган бандаларим, шак-шубҳа йўқки, Менинг ерим кенг, каттадир. Бас, сизлар Менгагина ибодат қилингиз'}, {'chapter': 29, 'verse': 57, 'text': 'كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۖ ثُمَّ إِلَيۡنَا تُرۡجَعُونَ\nҲар бир жон ўлим (шарбати)ни тотгувчидир. Сўнгра Ўзимизга қайтарилурсизлар'}, {'chapter': 29, 'verse': 58, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُبَوِّئَنَّهُم مِّنَ ٱلۡجَنَّةِ غُرَفٗا تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ نِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ\nИймон келтирган ва яхши амаллар қилган зотларни албатта жаннатда остидан дарёлар оқиб турадиган жойларга жойлаштирурмиз. Улар ўша жойларда мангу қолурлар. (Яхши) амалларни қилгувчи зотларнинг ажри-мукофоти нақадар яхшидир'}, {'chapter': 29, 'verse': 59, 'text': 'ٱلَّذِينَ صَبَرُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nУлар (кофирлар томонидан бўладиган зулму кулфатларга) сабр-тоқат қилган ва ёлғиз Парвардигорларигагина таваккул қиладиган — суянадиган зотлардир'}, {'chapter': 29, 'verse': 60, 'text': 'وَكَأَيِّن مِّن دَآبَّةٖ لَّا تَحۡمِلُ رِزۡقَهَا ٱللَّهُ يَرۡزُقُهَا وَإِيَّاكُمۡۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nЎз ризқу рўзини кўтара (яъни, топа) олмайдиган қанчадан-қанча жонзотлар бордир. Аллоҳ уларга ҳам, сизларга ҳам ризқу рўз берур. У эшитгувчи, билгувчидир'}, {'chapter': 29, 'verse': 61, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَ لَيَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ يُؤۡفَكُونَ\nҚасамки, агар сиз улардан: «Осмонлар ва Ерни яратган, қуёш ва ойни (Ўз измига) қаратган зот ким?», деб сўрасангиз, албатта улар: «Аллоҳ», деб (жавоб қилурлар). Бас, (шундоқ экан, ўша Аллоҳга ибодат қилишнинг ўрнига) қаёққа бурилиб кетмоқдалар-а'}, {'chapter': 29, 'verse': 62, 'text': 'ٱللَّهُ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥٓۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ\nАллоҳ бандаларидан Ўзи хоҳлаган кишиларнинг ризқини кенг қилур ва (Ўзи хоҳлаган кишиларнинг ризқини) танг қилур. Албатта Аллоҳ барча нарсани билгувчидир'}, {'chapter': 29, 'verse': 63, 'text': 'وَلَئِن سَأَلۡتَهُم مَّن نَّزَّلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ مِنۢ بَعۡدِ مَوۡتِهَا لَيَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ\nҚасамки, агар сиз улардан: «Осмондан сув ёмғир ёғдириб ерни «ўлганидан» сўнг «тирилтирган» зот ким?», деб сўрасангиз, албатта улар: «Аллоҳ», деб (жавоб қилурлар). Сиз (уларга): «Эътироф этганларингиз учун Аллоҳга ҳамду сано бўлсин», деб айтинг. Йўқ, уларнинг кўплари ақл юргизмайдилар'}, {'chapter': 29, 'verse': 64, 'text': 'وَمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا لَهۡوٞ وَلَعِبٞۚ وَإِنَّ ٱلدَّارَ ٱلۡأٓخِرَةَ لَهِيَ ٱلۡحَيَوَانُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nБу ҳаёти дунё фақат (бир нафаслик) ўйин-кулгидир. Агар улар билсалар Охират диёригина (мангу) ҳаёт (диёридир)'}, {'chapter': 29, 'verse': 65, 'text': 'فَإِذَا رَكِبُواْ فِي ٱلۡفُلۡكِ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ إِذَا هُمۡ يُشۡرِكُونَ\nҚачон улар кемага минсалар (ғарқ бўлишдан кўрқиб) Аллоҳга чин ихлос билан дуо-илтижо қилурлар. Энди қачонки (Аллоҳ) уларга нажот бериб қуруқликка (чиқаргач), баногоҳ улар (Аллоҳга) ширк келтирурлар'}, {'chapter': 29, 'verse': 66, 'text': 'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡ وَلِيَتَمَتَّعُواْۚ فَسَوۡفَ يَعۡلَمُونَ\nБиз уларга ато этган неъматларга кофир бўлаверсинлар ва (шу бир нафаслик ҳаётларида) «фойдаланиб» қолсинлар. Ҳали яқинда (бу қилмишларининг оқибатини) билиб қолажаклар'}, {'chapter': 29, 'verse': 67, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا حَرَمًا ءَامِنٗا وَيُتَخَطَّفُ ٱلنَّاسُ مِنۡ حَوۡلِهِمۡۚ أَفَبِٱلۡبَٰطِلِ يُؤۡمِنُونَ وَبِنِعۡمَةِ ٱللَّهِ يَكۡفُرُونَ\nУлар ўзларининг атрофларидан одамлар талон-торожга дучор бўлаётгани ҳолда Биз Ҳарамни (яъни, Маккани) тинч-осойишта қилиб қўйганимизни кўрмадиларми?! Наҳотки улар ботил-бутларга иймон келтириб, Аллоҳнинг неъмати (Исломга)га кофир бўлсалар'}, {'chapter': 29, 'verse': 68, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِٱلۡحَقِّ لَمَّا جَآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكَٰفِرِينَ\nАллоҳ шаънига ёлғон тўқиган ёки Ҳақ — Қуръон келган чоғида уни ёлғон деган кимсадан ҳам золимроқ ким бор?! Жаҳаннамда кофир кимсалар учун жой топилмасми'}, {'chapter': 29, 'verse': 69, 'text': 'وَٱلَّذِينَ جَٰهَدُواْ فِينَا لَنَهۡدِيَنَّهُمۡ سُبُلَنَاۚ وَإِنَّ ٱللَّهَ لَمَعَ ٱلۡمُحۡسِنِينَ\nБизнинг (йўлимиз)да жиҳод қилган — курашган зотларни албатта Ўз йўлларимизга ҳидоят қилурмиз. Аниқки, Аллоҳ чиройли амал қилгувчи зотлар билан биргадир'}, {'chapter': 30, 'verse': 1, 'text': 'الٓمٓ\nАлиф, Лом, Мим'}, {'chapter': 30, 'verse': 2, 'text': 'غُلِبَتِ ٱلرُّومُ\nРум мағлуб бўлди'}, {'chapter': 30, 'verse': 3, 'text': 'فِيٓ أَدۡنَى ٱلۡأَرۡضِ وَهُم مِّنۢ بَعۡدِ غَلَبِهِمۡ سَيَغۡلِبُونَ\nЖуда яқин жойда. (Лекин) улар (яъни, румликлар) бу мағлубиятларидан сўнг албатта ғалаба қилажаклар'}, {'chapter': 30, 'verse': 4, 'text': 'فِي بِضۡعِ سِنِينَۗ لِلَّهِ ٱلۡأَمۡرُ مِن قَبۡلُ وَمِنۢ بَعۡدُۚ وَيَوۡمَئِذٖ يَفۡرَحُ ٱلۡمُؤۡمِنُونَ\nБир неча йил ичида. Аввалу-охир барча иш Аллоҳнинг (измида)дир. Ўша кунда мўминлар шодланурлар'}, {'chapter': 30, 'verse': 5, 'text': 'بِنَصۡرِ ٱللَّهِۚ يَنصُرُ مَن يَشَآءُۖ وَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nАллоҳ (румликларни) ғолиб қилгани сабабли. (Аллоҳ) Ўзи хоҳлаган кишини ғолиб қилур. У қудрат ва раҳм-шафқат Эгасидир'}, {'chapter': 30, 'verse': 6, 'text': 'وَعۡدَ ٱللَّهِۖ لَا يُخۡلِفُ ٱللَّهُ وَعۡدَهُۥ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\n(Бу) Аллоҳнинг ваъдасидир. Аллоҳ Ўз ваъдасига хилоф қилмас. Лекин кўп одамлар билмаслар'}, {'chapter': 30, 'verse': 7, 'text': 'يَعۡلَمُونَ ظَٰهِرٗا مِّنَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَهُمۡ عَنِ ٱلۡأٓخِرَةِ هُمۡ غَٰفِلُونَ\nУлар Охиратдан ғофил-бехабар бўлган ҳолларида фақат ҳаёти дунёнинг зоҳиринигина билурлар'}, {'chapter': 30, 'verse': 8, 'text': 'أَوَلَمۡ يَتَفَكَّرُواْ فِيٓ أَنفُسِهِمۗ مَّا خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۗ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ بِلِقَآيِٕ رَبِّهِمۡ لَكَٰفِرُونَ\nАхир улар ўзларича Аллоҳ осмонлар ва Ерни ҳамда улар орасидаги бор нарсани фақат ҳақ (қонун) ва белгиланган муддат билан яратганини (демак ўша муддат битгач, дунё тугаб, Охират бошланишини) тафаккур қилиб кўрмадиларми?! Дарҳақиқат, бу одамлардан кўпчилик қайта тирилиб, Парвардигорларига рўбарў бўлишни инкор қилгувчидирлар'}, {'chapter': 30, 'verse': 9, 'text': 'أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَانُوٓاْ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَأَثَارُواْ ٱلۡأَرۡضَ وَعَمَرُوهَآ أَكۡثَرَ مِمَّا عَمَرُوهَا وَجَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ\nАхир улар Ер юзида сайру саёҳат этишиб, ўзларидан аввалги пайғамбарларни ёлғончи қилиб иймонсиз (кетган) кимсаларнинг оқибатлари қандай бўлганини кўрсалар бўлмайдими?! Ўша (ҳалок бўлган кимсалар) булардан (яъни, Макка кофирларидан) кўра кучли-қувватлироқ бўлган ва (ўз) ерларини булар обод қилганидан кўра кўпроқ ҳайдаб (яъни, экинзорларга айлантириб), обод қилган эдилар-ку! Уларга ҳам ўз пайғамбарлари аниқ-равшан ҳужжатлар келтирганларида, (у пайғамбарларни ёлғончи қилишгач, ҳалок бўлдилар). Бас, Аллоҳ уларга зулм қилгувчи бўлмади, лекин улар (Ҳақ йўлига юришдан бош тортишлари билан) ўзларига зулм қилгувчи бўлдилар'}, {'chapter': 30, 'verse': 10, 'text': 'ثُمَّ كَانَ عَٰقِبَةَ ٱلَّذِينَ أَسَـٰٓـُٔواْ ٱلسُّوٓأَىٰٓ أَن كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ وَكَانُواْ بِهَا يَسۡتَهۡزِءُونَ\nСўнгра (Охиратда эса) Аллоҳнинг оятларини ёлғон дейиш ва уларнинг устидан кулиш билан (ўзларига) ёмонлик қилган кимсаларнинг оқибати янада ёмонроқ бўлди'}, {'chapter': 30, 'verse': 11, 'text': 'ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ\nАллоҳ аввал бошда Ўзи яратиб, сўнгра (Қиёмат Кунида) Ўзи яна қайта яратур. Сўнгра (ҳисоб-китоб қилиниши учун) Унга қайтарилурсизлар'}, {'chapter': 30, 'verse': 12, 'text': 'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُبۡلِسُ ٱلۡمُجۡرِمُونَ\n(Қиёмат) соати қойим бўладиган кунда жиноятчи кимсалар бутунлай номурод бўлурлар'}, {'chapter': 30, 'verse': 13, 'text': 'وَلَمۡ يَكُن لَّهُم مِّن شُرَكَآئِهِمۡ شُفَعَـٰٓؤُاْ وَكَانُواْ بِشُرَكَآئِهِمۡ كَٰفِرِينَ\n(У кунда) улар учун бутлари томонидан оқловчилар бўлмас (яъни, улар ҳаёти дунёда Аллоҳга шерик қилиб олишган кимсалар улардан тонар) ва улар ҳам бутларидан тонгувчи бўлурлар'}, {'chapter': 30, 'verse': 14, 'text': 'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَتَفَرَّقُونَ\n(Қиёмат) соати қойим бўладиган кунда — ана ўша кунда (мўминлар билан кофирлар) бир-бирларидан ажралурлар'}, {'chapter': 30, 'verse': 15, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَهُمۡ فِي رَوۡضَةٖ يُحۡبَرُونَ\nЭнди иймон келтирган ва яхши амаллар қилган зотлар (жаннат) боғида шодланурлар'}, {'chapter': 30, 'verse': 16, 'text': 'وَأَمَّا ٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآيِٕ ٱلۡأٓخِرَةِ فَأُوْلَـٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ\nАммо куфр йўлини тутган ва Бизнинг оятларимизни ҳамда Охират мулоқотини ёлғон деган кимсалар — ана ўшалар (мангу) азобга дучор қилинурлар'}, {'chapter': 30, 'verse': 17, 'text': 'فَسُبۡحَٰنَ ٱللَّهِ حِينَ تُمۡسُونَ وَحِينَ تُصۡبِحُونَ\nБас, тунга кириш пайтларингизда ҳам, тонгга кириш пайтларингизда ҳам Аллоҳни поклангиз (яъни, У зотга ҳамду сано айтингиз)'}, {'chapter': 30, 'verse': 18, 'text': 'وَلَهُ ٱلۡحَمۡدُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَعَشِيّٗا وَحِينَ تُظۡهِرُونَ\nОқшомда ҳам, пешин пайтига киришингизда ҳам (Аллоҳни поклангиз). Зеро, осмонлар ва Ердаги бор ҳамду сано Уникидир (яъни, коинотдаги барча мавжудот Унга ҳамду сано айтур)'}, {'chapter': 30, 'verse': 19, 'text': 'يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَيُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّ وَيُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ وَكَذَٰلِكَ تُخۡرَجُونَ\nАллоҳ ўликдан тирикни чиқарур, тирикдан ўликни чиқарур ва ерни ўлганидан кейин (баҳорда қайта) тирилтирур. Сизлар ҳам (Қиёмат Кунида қабрларингиздан) мана шундай чиқарилурсизлар'}, {'chapter': 30, 'verse': 20, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَكُم مِّن تُرَابٖ ثُمَّ إِذَآ أَنتُم بَشَرٞ تَنتَشِرُونَ\n(Аллоҳнинг қудрати илоҳийясига далолат қиладиган) оят-аломатларидан (бири) — У зот сизларни (яъни, отангиз Одамни) тупроқдан яратгани, сўнгра сизлар башарга айланиб (Ер юзига) таралишларингиздир'}, {'chapter': 30, 'verse': 21, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا لِّتَسۡكُنُوٓاْ إِلَيۡهَا وَجَعَلَ بَيۡنَكُم مَّوَدَّةٗ وَرَحۡمَةًۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nУнинг оятларидан (яна бири) У зот сизлар ҳамдам бўлишларингиз учун ўзларингиздан жуфтлар яратиши ва ўрталарингизда ошнолик ва меҳр-муҳаббат пайдо қилишидир. Албатта бунда тафаккур қиладиган қавм учун оят-ибратлар бордир'}, {'chapter': 30, 'verse': 22, 'text': 'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفُ أَلۡسِنَتِكُمۡ وَأَلۡوَٰنِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡعَٰلِمِينَ\nУнинг оятларидан (яна бири) — У зот осмонлар ва Ерни яратиши ва сизларнинг тилларингиз ва рангларингизни хилма-хил қилиб қўйгандир. Албатта бунда барча оламлар учун оят-ибратлар бордир'}, {'chapter': 30, 'verse': 23, 'text': 'وَمِنۡ ءَايَٰتِهِۦ مَنَامُكُم بِٱلَّيۡلِ وَٱلنَّهَارِ وَٱبۡتِغَآؤُكُم مِّن فَضۡلِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ\nУнинг оятларидан (яна бири) — кечаси ва кундузи ухлашларингиз ҳамда (кундузлари) Унинг фазлу марҳаматидан (ризқу рўз) исташларингиздир. Албатта бунда англайдиган қавм учун оят-ибратлар бордир'}, {'chapter': 30, 'verse': 24, 'text': 'وَمِنۡ ءَايَٰتِهِۦ يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مَآءٗ فَيُحۡيِۦ بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ\nУнинг оятларидан (яна бири) — У зот сизларга (момақалдироқдан) қўрққан ва (ёмғир ёғишидан) умидвор бўлган ҳолингизда чақмоқни кўргизур ва осмондан сув-ёмғир ёғдириб, унинг ёрдамида ерни, ўлганидан сўнг тирилтирур. Албатта бунда ақл юргизадиган қавм учун оят-ибратлар бордир'}, {'chapter': 30, 'verse': 25, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَن تَقُومَ ٱلسَّمَآءُ وَٱلۡأَرۡضُ بِأَمۡرِهِۦۚ ثُمَّ إِذَا دَعَاكُمۡ دَعۡوَةٗ مِّنَ ٱلۡأَرۡضِ إِذَآ أَنتُمۡ تَخۡرُجُونَ\nУнинг оятларидан (яна бири) — осмон ва Ер У зотнинг амри билан (фазода муаллақ) туришидир. Сўнгра (Қиёмат қойим бўлганида) У сизларни (ётган) ерларингиздан бир бор чақириши билан барчангиз бирдан (ҳисоб-китоб учун) чиқарсизлар'}, {'chapter': 30, 'verse': 26, 'text': 'وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ\nОсмонлар ва Ердаги бор жонзотлар, У зотга тегишлидирки, ҳаммаси Унга бўйинсунур'}, {'chapter': 30, 'verse': 27, 'text': 'وَهُوَ ٱلَّذِي يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَهُوَ أَهۡوَنُ عَلَيۡهِۚ وَلَهُ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ аввал бошда Ўзи яратиб, сўнгра (Қиёмат Кунида) Ўзи яна қайта яратадиган Зотдир. (Қайта яратиш — тирилтириш) У зотга жуда осондир. Осмонлар ва Ердаги энг юксак хислат (яъни, Тенгсиз — Ягоналик хислати) Уникидир. У қудрат ва ҳикмат Эгасидир'}, {'chapter': 30, 'verse': 28, 'text': 'ضَرَبَ لَكُم مَّثَلٗا مِّنۡ أَنفُسِكُمۡۖ هَل لَّكُم مِّن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن شُرَكَآءَ فِي مَا رَزَقۡنَٰكُمۡ فَأَنتُمۡ فِيهِ سَوَآءٞ تَخَافُونَهُمۡ كَخِيفَتِكُمۡ أَنفُسَكُمۡۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡقِلُونَ\n(Аллоҳ) сизларга ўзларингиздан бир мисол келтирур: сизлар учун қўлларингиздаги қулларингиздан Биз сизларга ризқ қилиб берган молу-мулкка шерик бўлиб олгувчилар борми, бас, сизлар (мол-мулкингизни тасарруф қилишда у қулларингиз билан) баробар бўлиб, улардан ҳам ўзларингиздан қўрққандай қўрқурмисизлар? Ақл юргизадиган қавм учун оятларни мана шундай баён қилурмиз'}, {'chapter': 30, 'verse': 29, 'text': 'بَلِ ٱتَّبَعَ ٱلَّذِينَ ظَلَمُوٓاْ أَهۡوَآءَهُم بِغَيۡرِ عِلۡمٖۖ فَمَن يَهۡدِي مَنۡ أَضَلَّ ٱللَّهُۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ\nЙўқ, золим кимсалар бирон билим-ҳужжатсиз ҳавои нафсларига эграшиб (Аллоҳга ширк келтирдилар). Бас, Аллоҳ йўлдан оздирган кимсани ким ҳидоят қила олур?! (Ҳеч ким ҳидоят қила олмас) ва улар учун (Охиратда) ёрдам бергувчилар ҳам бўлмас'}, {'chapter': 30, 'verse': 30, 'text': 'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗاۚ فِطۡرَتَ ٱللَّهِ ٱلَّتِي فَطَرَ ٱلنَّاسَ عَلَيۡهَاۚ لَا تَبۡدِيلَ لِخَلۡقِ ٱللَّهِۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), ўзингизни доимо тўғри бўлган динда (Исломда) тутинг! Аллоҳ инсонларни яратган табиий хилқатни сақлангиз! Аллоҳнинг яратиши ўзгартирилмас. Энг тўғри дин мана шудир. Лекин кўп одамлар билмаслар'}, {'chapter': 30, 'verse': 31, 'text': '۞مُنِيبِينَ إِلَيۡهِ وَٱتَّقُوهُ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَلَا تَكُونُواْ مِنَ ٱلۡمُشۡرِكِينَ\n(Аллоҳга) қайтгувчи бўлган ҳолларингизда (Унинг динини маҳкам тутинглар) ва У зотдан қўрқинглар ҳамда намозни тўкис адо қилинглар! Мушриклардан бўлманглар'}, {'chapter': 30, 'verse': 32, 'text': 'مِنَ ٱلَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمۡ فَرِحُونَ\nУлар (яъни, мушриклар) динларини бўлиб, фирқа-фирқа бўлиб олгандирлар. Ҳар бир фирқа ўз олдиларидаги нарса билан хурсанддирлар'}, {'chapter': 30, 'verse': 33, 'text': 'وَإِذَا مَسَّ ٱلنَّاسَ ضُرّٞ دَعَوۡاْ رَبَّهُم مُّنِيبِينَ إِلَيۡهِ ثُمَّ إِذَآ أَذَاقَهُم مِّنۡهُ رَحۡمَةً إِذَا فَرِيقٞ مِّنۡهُم بِرَبِّهِمۡ يُشۡرِكُونَ\nҚачон (динсиз) одамларга бирон зиён етса, Парвардигорларига қайтиб, Унга илтижо қилурлар, сўнгра қачон (Аллоҳ) уларга Ўз томонидан бирон марҳамат тотдириб қўйса, баногоҳ улардан бир гуруҳи Парвардигорларига ширк келтира бошлар'}, {'chapter': 30, 'verse': 34, 'text': 'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ\nУлар Биз ато этган неъматларга ношукурлик қилаверсинлар! Бас, (эй мушриклар), фойдаланиб қолинглар! Ҳали яқинда (бу қилмишларингизнинг оқибатини) билиб олурсизлар'}, {'chapter': 30, 'verse': 35, 'text': 'أَمۡ أَنزَلۡنَا عَلَيۡهِمۡ سُلۡطَٰنٗا فَهُوَ يَتَكَلَّمُ بِمَا كَانُواْ بِهِۦ يُشۡرِكُونَ\nЁки Биз уларга (осмондан) уларнинг (Аллоҳга) ширк келтиришларини (тўғри деб) сўзлайдиган бирон ҳужжат нозил қилдикми?! (Йўқ, улар фақат нодонлик билан ўз ҳавои нафсларига эргашиб, Аллоҳга турли нарсаларни шерик деб билмоқдалар)'}, {'chapter': 30, 'verse': 36, 'text': 'وَإِذَآ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ فَرِحُواْ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ إِذَا هُمۡ يَقۡنَطُونَ\nҚачон Биз одамларга бирон марҳамат тотдирсак, улар бундан қувониб кетурлар. Агар уларга ўзлари қилган гуноҳлари сабабли бирон ёмонлик етса, баногоҳ улар ноумидликка тушурлар'}, {'chapter': 30, 'verse': 37, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nАхир улар Аллоҳ Ўзи хоҳлаган кишининг ризқини кенг қилишини ва (Ўзи хоҳлаган кишининг ризқини) танг қилишини билмадиларми?! Албатта бунда иймон келтирадиган қавм учун оят-ибратлар бордир'}, {'chapter': 30, 'verse': 38, 'text': 'فَـَٔاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِۚ ذَٰلِكَ خَيۡرٞ لِّلَّذِينَ يُرِيدُونَ وَجۡهَ ٱللَّهِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nБас, қариндош-уруғга, мискин ва мусофирга (хайру-эҳсон қилиш билан ҳақларини ато этинг! Аллоҳ Юзини истайдиган кишилар учун мана шу энг яхши (ишдир). Ана ўшалар нажот топгувчидирлар'}, {'chapter': 30, 'verse': 39, 'text': 'وَمَآ ءَاتَيۡتُم مِّن رِّبٗا لِّيَرۡبُوَاْ فِيٓ أَمۡوَٰلِ ٱلنَّاسِ فَلَا يَرۡبُواْ عِندَ ٱللَّهِۖ وَمَآ ءَاتَيۡتُم مِّن زَكَوٰةٖ تُرِيدُونَ وَجۡهَ ٱللَّهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُضۡعِفُونَ\nСизлар одамларнинг моллари ичида зиёда бўлиб (қайтиши) учун берган судхўрлик-фойдахўрликдан иборат бўлган пул-молларингиз (яъни, совға-саломларингиз) Аллоҳ наздида зиёда (савоб) олишингизга сабаб бўлмас. Аллоҳнинг Юзини — розилигини истаб ато этган закотларингиз — ана ўша (закот бергувчи кишилар ажру-савобларини) бир неча баробар қилиб олгувчилардир'}, {'chapter': 30, 'verse': 40, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَكُمۡ ثُمَّ رَزَقَكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۖ هَلۡ مِن شُرَكَآئِكُم مَّن يَفۡعَلُ مِن ذَٰلِكُم مِّن شَيۡءٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nАллоҳ сизларни яратган, сўнгра ризқу рўзингизни берган, сўнгра жонингизни оладиган, сўнгра сизларга (қайта) ҳаёт берадиган Зотдир. Сизлар (сиғинаётган) бутларингиз орасида мана шуларнинг биронтасини қила оладиган кимса борми?! (Аллоҳ) уларнинг ширкларидан пок ва юксакдир'}, {'chapter': 30, 'verse': 41, 'text': 'ظَهَرَ ٱلۡفَسَادُ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ بِمَا كَسَبَتۡ أَيۡدِي ٱلنَّاسِ لِيُذِيقَهُم بَعۡضَ ٱلَّذِي عَمِلُواْ لَعَلَّهُمۡ يَرۡجِعُونَ\nОдамларнинг ўзлари қилган қилмишлари сабабли қуруқликда ҳам, денгизда ҳам (турли) офат-балолар юз берди. (Бу бало ва офатлар одамлар қилаётган гуноҳ-маъсиятларидан) қайтишлари учун, уларга қилган айрим гуноҳларининг (жазосини) тотдириб қўйиш учундир'}, {'chapter': 30, 'verse': 42, 'text': 'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلُۚ كَانَ أَكۡثَرُهُم مُّشۡرِكِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Ер юзида сайру саёҳат қилиб, илгари ўтганларнинг оқибатлари қандай бўлганини кўринглар! Уларнинг кўплари мушрик эдилар»'}, {'chapter': 30, 'verse': 43, 'text': 'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ ٱلۡقَيِّمِ مِن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۖ يَوۡمَئِذٖ يَصَّدَّعُونَ\nБас, сиз Аллоҳ томонидан, қайтарилмайдиган кун (яъни, Қиёмат) келмай туриб ўзингизни доимо рост динда тутинг! Ўша кунда (мўминлар билан кофирлар) бўлинурлар'}, {'chapter': 30, 'verse': 44, 'text': 'مَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَمَنۡ عَمِلَ صَٰلِحٗا فَلِأَنفُسِهِمۡ يَمۡهَدُونَ\nКимда-ким кофир бўлса, куфри ўзининг бўйнидадир. Ким яхши амал қилса, бас, улар ўзлари учун (жаннатдан) жой тайёрларлар'}, {'chapter': 30, 'verse': 45, 'text': 'لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡكَٰفِرِينَ\nЗеро, (Аллоҳ) иймон келтирган ва яхши амаллар қилган зотларни Ўз фазлу-карамидан мукофотлар. Дарҳақиқат, У зот кофирларни суймас'}, {'chapter': 30, 'verse': 46, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَن يُرۡسِلَ ٱلرِّيَاحَ مُبَشِّرَٰتٖ وَلِيُذِيقَكُم مِّن رَّحۡمَتِهِۦ وَلِتَجۡرِيَ ٱلۡفُلۡكُ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\n(Аллоҳнинг қудрати илоҳийясига далолат қиладиган) оятларидан (бири) (ёмғир ҳақида) хушхабар элтгувчи бўлган шамолларни юборишидир. (Аллоҳ) сизларга Ўз раҳмати-ёмғиридан тотдириш учун ва (денгиздаги) кемалар Унинг амри билан жорий бўлиши ҳамда сизлар (у кемаларда) Унинг фазлу-марҳаматидан (ризқу рўз) исташларингиз учун ва шукр қилишларингиз учун (шамолларни юборур)'}, {'chapter': 30, 'verse': 47, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِٱلۡبَيِّنَٰتِ فَٱنتَقَمۡنَا مِنَ ٱلَّذِينَ أَجۡرَمُواْۖ وَكَانَ حَقًّا عَلَيۡنَا نَصۡرُ ٱلۡمُؤۡمِنِينَ\nАниқки, Биз сиздан илгари ҳам пайғамбарларни ўз қавмларига юборганмиз. Бас, улар (қавмларига) аниқ-равшан ҳужжатлар келтирганлар. Сўнг Биз жиноят қилган кимсалардан интиқом олганмиз ва иймон келтирган зотларни ғолиб қилиш Бизнинг зиммамиздаги ҳақ бўлган'}, {'chapter': 30, 'verse': 48, 'text': 'ٱللَّهُ ٱلَّذِي يُرۡسِلُ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَيَبۡسُطُهُۥ فِي ٱلسَّمَآءِ كَيۡفَ يَشَآءُ وَيَجۡعَلُهُۥ كِسَفٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦۖ فَإِذَآ أَصَابَ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ\nАллоҳ шундай Зотдирки, шамолларни юборур, бас, улар булутни ҳайдар, сўнг (Аллоҳ булутни) Ўзи хоҳлагандек ёюр ва (агар хоҳласа) уни бўлак-бўлак қилур, бас, сиз уларнинг орасидан ёмғир чиқаётганини кўрурсиз. Қачон (Аллоҳ) уни Ўзи хоҳлаган бандаларига етказса баногоҳ улар шод-хуррам бўлурлар'}, {'chapter': 30, 'verse': 49, 'text': 'وَإِن كَانُواْ مِن قَبۡلِ أَن يُنَزَّلَ عَلَيۡهِم مِّن قَبۡلِهِۦ لَمُبۡلِسِينَ\nҲолбуки, улар устларига (ёмғир) ёғдирилишидан олдин мутлақо ноумид эдилар'}, {'chapter': 30, 'verse': 50, 'text': 'فَٱنظُرۡ إِلَىٰٓ ءَاثَٰرِ رَحۡمَتِ ٱللَّهِ كَيۡفَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ ذَٰلِكَ لَمُحۡيِ ٱلۡمَوۡتَىٰۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nБас, сиз Аллоҳнинг раҳмати ёмғирнинг изларига қаранг: У қандай қилиб Ерни ўлганидан кейин тирилтирди-я! Ҳеч шак-шубҳасиз, мана шу (Аллоҳ) ўликларни ҳам тирилтиргувчидир. У барча нарсага қодирдир'}, {'chapter': 30, 'verse': 51, 'text': 'وَلَئِنۡ أَرۡسَلۡنَا رِيحٗا فَرَأَوۡهُ مُصۡفَرّٗا لَّظَلُّواْ مِنۢ بَعۡدِهِۦ يَكۡفُرُونَ\nҚасамки, агар Биз бир шамол юбориб, улар (экинларини) сарғайиб-қуриганини кўрсалар, албатта ана ундан кейин куфрони неъмат қилишга турурлар'}, {'chapter': 30, 'verse': 52, 'text': 'فَإِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوۡاْ مُدۡبِرِينَ\nАниқки, сиз «ўликлар»га (сўзингизни) англата олмассиз, орқа ўгириб кетаётган «кар»ларга ҳам уқтира олмассиз'}, {'chapter': 30, 'verse': 53, 'text': 'وَمَآ أَنتَ بِهَٰدِ ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ\nЯна сиз «кўр»ларни ўз залолатларидан (қайтариб) ҳидоят қилгувчи эмассиз. Сиз фақат Бизнинг оятларимизга иймон келтирадиган кишиларгагина (ўз сўзларингизни) уқтира олурсиз. Ана ўшалар мусулмонлардир'}, {'chapter': 30, 'verse': 54, 'text': '۞ٱللَّهُ ٱلَّذِي خَلَقَكُم مِّن ضَعۡفٖ ثُمَّ جَعَلَ مِنۢ بَعۡدِ ضَعۡفٖ قُوَّةٗ ثُمَّ جَعَلَ مِنۢ بَعۡدِ قُوَّةٖ ضَعۡفٗا وَشَيۡبَةٗۚ يَخۡلُقُ مَا يَشَآءُۚ وَهُوَ ٱلۡعَلِيمُ ٱلۡقَدِيرُ\nАллоҳ шундай Зотдирки, сизларни ночор нарсадан (яъни, бир томчи сувдан) яратди, сўнгра (сизлар учун) ночорликдан кейин куч-қувват (пайдо) қилди, сўнгра куч-қувватдан кейин яна ночорлик ва қариликни пайдо қилди. У Ўзи хоҳлаган нарсани яратур. У билимдон ва қудратлидир'}, {'chapter': 30, 'verse': 55, 'text': 'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُقۡسِمُ ٱلۡمُجۡرِمُونَ مَا لَبِثُواْ غَيۡرَ سَاعَةٖۚ كَذَٰلِكَ كَانُواْ يُؤۡفَكُونَ\n(Қиёмат) соати қойим бўладиган кунда жиноятчи кимсалар (дунёда) бир соатдан ортиқ турмаганларига қасам ичурлар. Улар (ҳаёти дунёда ҳам) мана шундай (ростдан ёлғонга) бурилгувчи эдилар'}, {'chapter': 30, 'verse': 56, 'text': 'وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَٱلۡإِيمَٰنَ لَقَدۡ لَبِثۡتُمۡ فِي كِتَٰبِ ٱللَّهِ إِلَىٰ يَوۡمِ ٱلۡبَعۡثِۖ فَهَٰذَا يَوۡمُ ٱلۡبَعۡثِ وَلَٰكِنَّكُمۡ كُنتُمۡ لَا تَعۡلَمُونَ\n(Шунда) илм ва иймон ато этилган зотлар айтадилар: «Аниқки, сизлар Аллоҳ (Лавҳул-Маҳфузда) ёзиб қўйганидек тирилиш кунигача турдинглар. Бас, мана шу тирилиш кунидир. Лекин сизлар (бу куннинг ҳақ эканини) билмас эдинглар»'}, {'chapter': 30, 'verse': 57, 'text': 'فَيَوۡمَئِذٖ لَّا يَنفَعُ ٱلَّذِينَ ظَلَمُواْ مَعۡذِرَتُهُمۡ وَلَا هُمۡ يُسۡتَعۡتَبُونَ\nАна ўша кунда (кофир бўлиш билан ўз жонларига) жабр қилган кимсаларга узру баҳоналари фойда бермас ва улардан (Аллоҳ рози бўладиган амалларга) қайтиш ҳам талаб қилинмас'}, {'chapter': 30, 'verse': 58, 'text': 'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖۚ وَلَئِن جِئۡتَهُم بِـَٔايَةٖ لَّيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ أَنتُمۡ إِلَّا مُبۡطِلُونَ\nАниқки, Биз ушбу Қуръонда одамлар учун ҳар турли мисолларни баён қилдик. Қасамки, агар сиз (эй Муҳаммад алайҳис-салоту вас-салом), уларга (ўзлари талаб қилган) оят-мўъжизани келтирсангиз ҳам, албатта кофир бўлган кимсалар «Сизлар ёлғончининг ўзидирсизлар», дерлар'}, {'chapter': 30, 'verse': 59, 'text': 'كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلَّذِينَ لَا يَعۡلَمُونَ\nАллоҳ (ҳақиқатни) билмайдиган кимсаларнинг дилларини ана шундай қоплаб-беркитиб қўюр'}, {'chapter': 30, 'verse': 60, 'text': 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ وَلَا يَسۡتَخِفَّنَّكَ ٱلَّذِينَ لَا يُوقِنُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сабр-тоқат қилинг! Шак-шубҳасиз, Аллоҳнинг (сизни ва умматларингизни кофир қавм устига ғолиб қилиш ҳақидаги) ваъдаси ҳақдир. (Охиратга) ишонмайдиган кимсалар сизни ҳаргиз бетоқат қилмасинлар'}, {'chapter': 31, 'verse': 1, 'text': 'الٓمٓ\nАлиф, Лом, Мим'}, {'chapter': 31, 'verse': 2, 'text': 'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡحَكِيمِ\nУшбу (оятлар) ҳикматли Китоб оятларидир'}, {'chapter': 31, 'verse': 3, 'text': 'هُدٗى وَرَحۡمَةٗ لِّلۡمُحۡسِنِينَ\nУ чиройли амал қилгувчи зотлар учун раҳбар-йўлбошчи ва раҳмат бўлган (Китоб)дир'}, {'chapter': 31, 'verse': 4, 'text': 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ\nУлар намозни тўкис адо этадиган, закотни (ҳақдорларга) ато этадиган ва Охиратга аниқ ишонадиган зотлардир'}, {'chapter': 31, 'verse': 5, 'text': 'أُوْلَـٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nПарвардигорлари тарафидан ҳидоят топганлар ана ўшалардир ва нажот топгувчилар ҳам уларнинг ўзидир'}, {'chapter': 31, 'verse': 6, 'text': 'وَمِنَ ٱلنَّاسِ مَن يَشۡتَرِي لَهۡوَ ٱلۡحَدِيثِ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّخِذَهَا هُزُوًاۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ\nОдамлар орасида шундай кимсалар ҳам борки, улар билимсизлик билан (ўзгаларни) Аллоҳнинг йўлидан оздириш учун ва у (йўлни) масхара қилиш учун беҳуда сўз(лар)ни сотиб олур. Ана ўшалар учун хор қилгувчи азоб бордир'}, {'chapter': 31, 'verse': 7, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا وَلَّىٰ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَا كَأَنَّ فِيٓ أُذُنَيۡهِ وَقۡرٗاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٍ\nҚачон унга Бизнинг оятларимиз тиловат қилинса, гўё уларни эшитмагандек, гўё қулоқларида оғирлик-карлик бордек кибру-ҳаво билан юз ўгириб кетур. Бас, унга аламли азоб ҳақида «хушхабар» бериб қўйинг'}, {'chapter': 31, 'verse': 8, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ جَنَّـٰتُ ٱلنَّعِيمِ\nАлбатта иймон келтирган ва яхши амаллар қилган зотлар учун ноз-неъмат боғлари бордир'}, {'chapter': 31, 'verse': 9, 'text': 'خَٰلِدِينَ فِيهَاۖ وَعۡدَ ٱللَّهِ حَقّٗاۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУлар у жойда мангу қолурлар. (Бу) Аллоҳнинг ҳақ ваъдасидир. У қудрат ва ҳикмат Эгасидир'}, {'chapter': 31, 'verse': 10, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ وَأَلۡقَىٰ فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ\nУ осмонларни сизлар кўрадиган устунларсиз яратиб, сизларни тебратмаслиги учун Ерда тоғларни барпо қилди ва унда ҳар турли жондор(лар)ни тарқатиб-ёйиб юборди. Яна Биз осмондан сув-ёмғир ёғдириб, (ерда) ҳар турли фойдали (ўсимликларни) ўстириб қўйдик'}, {'chapter': 31, 'verse': 11, 'text': 'هَٰذَا خَلۡقُ ٱللَّهِ فَأَرُونِي مَاذَا خَلَقَ ٱلَّذِينَ مِن دُونِهِۦۚ بَلِ ٱلظَّـٰلِمُونَ فِي ضَلَٰلٖ مُّبِينٖ\nБулар Аллоҳ яратган нарсалардир. Энди (эй мушриклар,) сизлар Менга Ундан ўзга «худолар» нималарни яратганини кўрсатинглар-чи! Йўқ, (улар бирон нарсани кўрсата олмаслар, демак) у золим кимсалар очиқ залолатдадирлар'}, {'chapter': 31, 'verse': 12, 'text': 'وَلَقَدۡ ءَاتَيۡنَا لُقۡمَٰنَ ٱلۡحِكۡمَةَ أَنِ ٱشۡكُرۡ لِلَّهِۚ وَمَن يَشۡكُرۡ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِيٌّ حَمِيدٞ\nАниқки, Биз Луқмонга ҳикмат ато этдик (ва унга дедик): «Аллоҳга шукр қилгин! Ким шукр қилса, фақат ўз фойдаси учун шукр қилур (яъни, қилган шукрининг фойдаси ўзига бўлур). Ким ношукурлик қилса, бас, албатта Аллоҳ (унинг ва барча оламларнинг шукр қилишидан) беҳожат ва ҳамду санога лойиқ Зотдир»'}, {'chapter': 31, 'verse': 13, 'text': 'وَإِذۡ قَالَ لُقۡمَٰنُ لِٱبۡنِهِۦ وَهُوَ يَعِظُهُۥ يَٰبُنَيَّ لَا تُشۡرِكۡ بِٱللَّهِۖ إِنَّ ٱلشِّرۡكَ لَظُلۡمٌ عَظِيمٞ\nЭсланг, Луқмон ўғлига панд-насиҳат қилар экан, деган эди: «Эй ўғилчам, Аллоҳга ширк келтирмагин! Чунки ширк келтириш катта зулмдир»'}, {'chapter': 31, 'verse': 14, 'text': 'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ حَمَلَتۡهُ أُمُّهُۥ وَهۡنًا عَلَىٰ وَهۡنٖ وَفِصَٰلُهُۥ فِي عَامَيۡنِ أَنِ ٱشۡكُرۡ لِي وَلِوَٰلِدَيۡكَ إِلَيَّ ٱلۡمَصِيرُ\nБиз инсонга ота-онасини (яъни, уларга яхшилик қилишни) амр этдик. Онаси унга ожизлик устига ожизлик билан ҳомиладор бўлди (яъни, қорнидаги ҳомила каттарган сари онанинг ҳоли қуриб, заифлаша борур) уни (кўкракдан) ажратиш (муддати) икки йилда (келур). (Биз инсонга буюрдикки), «Сен Менга ва ота-онангга шукр қилгин! Ёлғиз Ўзимга қайтажаксан!»'}, {'chapter': 31, 'verse': 15, 'text': 'وَإِن جَٰهَدَاكَ عَلَىٰٓ أَن تُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَاۖ وَصَاحِبۡهُمَا فِي ٱلدُّنۡيَا مَعۡرُوفٗاۖ وَٱتَّبِعۡ سَبِيلَ مَنۡ أَنَابَ إِلَيَّۚ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nАгар улар (яъни, ота-онанг) сени ўзинг билмаган нарсаларни Менга шерик қилишга зўрласалар, у ҳолда уларга итоат этма! Уларга (гарчи кофир бўлсалар-да), дунёда яхши муомалада бўлгин ва ўзинг менга ижобат-тавба қилган кишиларнинг йўлига эргашгин! Сўнгра (яъни, Қиёмат Кунида) Ўзимга қайтурсизлар, бас, мен сизларга қилиб ўтган амалларингизнинг хабарини берурман»'}, {'chapter': 31, 'verse': 16, 'text': 'يَٰبُنَيَّ إِنَّهَآ إِن تَكُ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٖ فَتَكُن فِي صَخۡرَةٍ أَوۡ فِي ٱلسَّمَٰوَٰتِ أَوۡ فِي ٱلۡأَرۡضِ يَأۡتِ بِهَا ٱللَّهُۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٞ\n(Луқмон деди): «Эй ўғилчам, шак-шубҳа йўқки, агар хардал (ўсимлигининг) уруғидек (бир зарра яхши ёки ёмон амал қилинадиган) бўлса, бас, у (амал) бирон харсанг тош ичида ё осмонларда ёки ер остида бўлса, ўшани-да Аллоҳ келтирур. Зеро, Аллоҳ сергак ва огоҳдир'}, {'chapter': 31, 'verse': 17, 'text': 'يَٰبُنَيَّ أَقِمِ ٱلصَّلَوٰةَ وَأۡمُرۡ بِٱلۡمَعۡرُوفِ وَٱنۡهَ عَنِ ٱلۡمُنكَرِ وَٱصۡبِرۡ عَلَىٰ مَآ أَصَابَكَۖ إِنَّ ذَٰلِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ\nЭй ўғилчам, намозни тўкис адо эт, яхшиликка буюр ва ёмонлиқдан қайтар ҳамда ўзингга етган (балоларга) сабр-қил! Албатта мана шу ишларнинг мақсадга мувофиғидир'}, {'chapter': 31, 'verse': 18, 'text': 'وَلَا تُصَعِّرۡ خَدَّكَ لِلنَّاسِ وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٖ\nОдамлардан (мутакаббирлик билан) юзингни ўгирмагин ва Ерда кибру-ҳаво билан юрмагин. Чунки Аллоҳ барча кибр-ҳаволи, мақтанчоқ кимсаларни суймас'}, {'chapter': 31, 'verse': 19, 'text': 'وَٱقۡصِدۡ فِي مَشۡيِكَ وَٱغۡضُضۡ مِن صَوۡتِكَۚ إِنَّ أَنكَرَ ٱلۡأَصۡوَٰتِ لَصَوۡتُ ٱلۡحَمِيرِ\nЮрганингда ўртача юргин ва овозингни паст қилгин. Чунки овозларнинг энг ёмони эшаклар овозидир»'}, {'chapter': 31, 'verse': 20, 'text': 'أَلَمۡ تَرَوۡاْ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَأَسۡبَغَ عَلَيۡكُمۡ نِعَمَهُۥ ظَٰهِرَةٗ وَبَاطِنَةٗۗ وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ\n(Эй инсонлар), Аллоҳ осмонлар ва Ердаги барча нарсаларни сизларга бўйсундириб қўйганини ва сизларга барча зоҳирий ва ботиний (яъни, моддий-маънавий) неъматларини комил қилиб берганини кўрмадингларми?! Одамлар орасида шундай кимсалар ҳам борки, улар ўзлари билмаган, ҳидоят топмаган ва ёрқин китоб-ҳужжатга эга бўлмаган ҳолларида Аллоҳ хусусида мужодала қилурлар'}, {'chapter': 31, 'verse': 21, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُواْ مَآ أَنزَلَ ٱللَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ٱلشَّيۡطَٰنُ يَدۡعُوهُمۡ إِلَىٰ عَذَابِ ٱلسَّعِيرِ\nҚачон уларга: «Аллоҳ нозил қилган нарсага эргашинглар», дейилса, улар: «Йўқ, бизлар ота-боболаримизни ниманинг устида топган бўлсак (ана ўшанга) эргашурмиз», дейишади. Агар шайтон уларни ўт азобига чақириб турган бўлса ҳам-а'}, {'chapter': 31, 'verse': 22, 'text': '۞وَمَن يُسۡلِمۡ وَجۡهَهُۥٓ إِلَى ٱللَّهِ وَهُوَ مُحۡسِنٞ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰۗ وَإِلَى ٱللَّهِ عَٰقِبَةُ ٱلۡأُمُورِ\nКим чиройли амал қилгувчи бўлган ҳолида ўзини Аллоҳга топширса, бас, у мустаҳкам халқани ушлабди. Барча ишлар охир-оқибатда ёлғиз Аллоҳга борур'}, {'chapter': 31, 'verse': 23, 'text': 'وَمَن كَفَرَ فَلَا يَحۡزُنكَ كُفۡرُهُۥٓۚ إِلَيۡنَا مَرۡجِعُهُمۡ فَنُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nКим кофир бўлса, бас, сизни (эй Муҳаммад алайҳис-салоту вас-салом), унинг куфри ғамгин қилмасин. Улар Ўзимизга қайтажаклар, бас, Биз уларга қилган амалларининг хабарини берурмиз. Зеро, Аллоҳ диллардаги сирларни билгувчидир'}, {'chapter': 31, 'verse': 24, 'text': 'نُمَتِّعُهُمۡ قَلِيلٗا ثُمَّ نَضۡطَرُّهُمۡ إِلَىٰ عَذَابٍ غَلِيظٖ\nБиз уларни (бу дунёда) озгина (муддат) фойдалантириб, сўнгра қаттиқ азобни (тотишга) мажбур қилурмиз'}, {'chapter': 31, 'verse': 25, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nҚасамки, агар улардан «Осмонлар ва Ерни ким яратган?», деб сўрасангиз албатта «Аллоҳ», дерлар. Сиз «Аллоҳга ҳамду-сано бўлсин», денг. Йўқ, уларнинг кўплари ана ўша Аллоҳгагина ибодат қилиш зарур эканини билмаслар'}, {'chapter': 31, 'verse': 26, 'text': 'لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nОсмонлар ва Ердаги бор нарсалар Аллоҳникидир. Албатта Аллоҳнинг Ўзигина беҳожат ва ҳамду санога лойиқ Зотдир'}, {'chapter': 31, 'verse': 27, 'text': 'وَلَوۡ أَنَّمَا فِي ٱلۡأَرۡضِ مِن شَجَرَةٍ أَقۡلَٰمٞ وَٱلۡبَحۡرُ يَمُدُّهُۥ مِنۢ بَعۡدِهِۦ سَبۡعَةُ أَبۡحُرٖ مَّا نَفِدَتۡ كَلِمَٰتُ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ\nАгар Ер юзидаги бор дов-дарахт қаламлар бўлиб, денгиз (сиёҳ бўлса ва) ундан сўнг яна етти денгиз ёрдамга келса, Аллоҳнинг сўзлари тугаб-битмас. Албатта Аллоҳ қудрат ва ҳикмат Эгасидир'}, {'chapter': 31, 'verse': 28, 'text': 'مَّا خَلۡقُكُمۡ وَلَا بَعۡثُكُمۡ إِلَّا كَنَفۡسٖ وَٰحِدَةٍۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٌ\n(Эй инсонлар), сизларни (барчангизни аввал бошда) яратиш ҳам, (Қиёмат Кунида) қайта тирилтириш ҳам худди бир жонни (яратиш ва қайта тирилтиришнинг) ўзгинасидир (яъни, шу қадар осондир). Зеро, Аллоҳ эшитгувчи, кўргувчидир'}, {'chapter': 31, 'verse': 29, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِيٓ إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَأَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ خَبِيرٞ\nАллоҳ кечани кундузга киритишини, кундузни кечага киритишини ҳамда қуёш ва ойнинг ҳар бирини белгиланган муддатга жорий бўладиган қилиб бўйсундириб қўйганини ва Аллоҳ шак-шубҳасиз, қилаётган амалларингиздан огоҳ эканини кўрмадингмизми'}, {'chapter': 31, 'verse': 30, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِ ٱلۡبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ\nБунинг боиси Аллоҳнинг Ўзигина Ҳақ (илоҳ) экани, улар (яъни, мушриклар Аллоҳни) қўйиб илтижо қилаётган бутлари эса ботил нарса экани ва Аллоҳнинг Ўзигина энг юксак ва буюк зот эканлигидир'}, {'chapter': 31, 'verse': 31, 'text': 'أَلَمۡ تَرَ أَنَّ ٱلۡفُلۡكَ تَجۡرِي فِي ٱلۡبَحۡرِ بِنِعۡمَتِ ٱللَّهِ لِيُرِيَكُم مِّنۡ ءَايَٰتِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ\nКемалар сизларга Аллоҳнинг оят-мўъжизаларидан кўрсатиш учун У зотнинг марҳамати билан денгизда (сузиб) юрганини кўрмадингизми?! Албатта бунда барча (балоларга) сабр қилгувчи, (неъматларга) шукр қилгувчилар учун оят-ибратлар бордир'}, {'chapter': 31, 'verse': 32, 'text': 'وَإِذَا غَشِيَهُم مَّوۡجٞ كَٱلظُّلَلِ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ فَمِنۡهُم مُّقۡتَصِدٞۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا كُلُّ خَتَّارٖ كَفُورٖ\nҚачон (кофирларни) тоғлардек тўлқин(лар) ўраб олса, улар чин ихлос билан Аллоҳга дуо илтижо қилурлар. Энди қачонки, (Аллоҳ) уларга нажот бериб, қуруқликка (чиқарса), у ҳолда улардан (айримларигина) Тўғри Йўл тутгувчидир. Бизнинг оятларимизни инкор қилмас, магар барча хоин ва ношукур кимсаларгина (инкор қилурлар)'}, {'chapter': 31, 'verse': 33, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمۡ وَٱخۡشَوۡاْ يَوۡمٗا لَّا يَجۡزِي وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوۡلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَيۡـًٔاۚ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ\nЭй инсонлар, Парвардигорингиздан қўрқингиз ва яна бир Кундан (яъни, Қиёматдан) қўрқингизким, (у кунда) бирон ота ўз боласи томонидан (бирон нарса) ўтай олмас ва бола ҳам отаси томонидан бирон нарса ўтай олгувчи бўлмас. Албатта Аллоҳнинг (қайта тирилтириб, ҳисоб-китоб қилиш ҳақидаги) ваъдаси ҳақдир. Бас, ҳаргиз сизларни ҳаёти дунё (ўзининг ўткинчи неъматлари билан) алдаб қўймасин ва ҳаргиз сизларни ғурур (яъни, шайтон) Аллоҳ (ҳар қандай гуноҳни кечаверади деган алдов) билан алдаб қўймасин'}, {'chapter': 31, 'verse': 34, 'text': 'إِنَّ ٱللَّهَ عِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَيُنَزِّلُ ٱلۡغَيۡثَ وَيَعۡلَمُ مَا فِي ٱلۡأَرۡحَامِۖ وَمَا تَدۡرِي نَفۡسٞ مَّاذَا تَكۡسِبُ غَدٗاۖ وَمَا تَدۡرِي نَفۡسُۢ بِأَيِّ أَرۡضٖ تَمُوتُۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرُۢ\nДарҳақиқат, ёлғиз Аллоҳнинг ҳузуридагина (Қиёмат) соати (қачон бўлиши тўғрисидаги) билим бордир. У (Ўзи хоҳлаган вақтда, Ўзи хоҳлаган жойга) ёмғир ёғдирур ва (оналарнинг) бачадонларидаги ҳомилаларини (ўғилми-қизми, расоми-нуқсонлими, бахтлими-бахтсизми эканини) билур. Бирон жон эртага нима иш қилишини била олмас. Бирон жон қай ерда ўлишини ҳам била олмас. Фақат Аллоҳгина билгувчи ва огоҳдир'}, {'chapter': 32, 'verse': 1, 'text': 'الٓمٓ\nАлиф, Лом, Мим'}, {'chapter': 32, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ ٱلۡعَٰلَمِينَ\nБу Китобнинг (яъни, Қуръоннинг) нозил қилиниши, ҳеч шак-шубҳасиз, барча оламларнинг Парвардигори томонидандир'}, {'chapter': 32, 'verse': 3, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۚ بَلۡ هُوَ ٱلۡحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَهۡتَدُونَ\nБалки «Уни (Муҳаммаднинг ўзи) тўқиб олган», дерлар. Йўқ, у (яъни, Қуръон) сиздан илгари бирон огоҳлантиргувчи келмаган қавмни (Охират азобидан) огоҳлантиришингиз учун Парвардигорингиз томонидан (нозил қилинган) Ҳақиқатдир. Шояд улар ҳидоят топсалар'}, {'chapter': 32, 'verse': 4, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ مَا لَكُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا شَفِيعٍۚ أَفَلَا تَتَذَكَّرُونَ\nАллоҳ осмонлар ва Ерни ҳамда уларнинг ўртасидаги бор нарсани олти кунда яратиб, сўнгра (Зоти ва Улуғлигига муносиб суратда) Аршга юксалган Зотдир. Сизлар учун Ундан ўзга бирон дўст ва оқловчи йўқдир. Ахир эслатма-ибрат олмайсизларми'}, {'chapter': 32, 'verse': 5, 'text': 'يُدَبِّرُ ٱلۡأَمۡرَ مِنَ ٱلسَّمَآءِ إِلَى ٱلۡأَرۡضِ ثُمَّ يَعۡرُجُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥٓ أَلۡفَ سَنَةٖ مِّمَّا تَعُدُّونَ\nУ осмондан ергача бўлган барча ишни тадбир қилиб — бошқариб турур, сўнгра (бу ишларнинг барчаси) сизларнинг ҳисобингизга минг йилга тенг бўладиган бир кунда (яъни, Қиёматда) Унинг Ўзига кўтарилур'}, {'chapter': 32, 'verse': 6, 'text': 'ذَٰلِكَ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nМана шу (Аллоҳ) ғойиб ва ҳозирни билгувчи, қудратли ва меҳрибондир'}, {'chapter': 32, 'verse': 7, 'text': 'ٱلَّذِيٓ أَحۡسَنَ كُلَّ شَيۡءٍ خَلَقَهُۥۖ وَبَدَأَ خَلۡقَ ٱلۡإِنسَٰنِ مِن طِينٖ\nУ барча нарсани чиройли қилиб яратган Зотдир. У инсонни (яъни, Одамни) даставвал лойдан яратди'}, {'chapter': 32, 'verse': 8, 'text': 'ثُمَّ جَعَلَ نَسۡلَهُۥ مِن سُلَٰلَةٖ مِّن مَّآءٖ مَّهِينٖ\nСўнгра унинг насли авлодини ҳақир бир сувдан (иборат бўлган нутфадан пайдо) қилди'}, {'chapter': 32, 'verse': 9, 'text': 'ثُمَّ سَوَّىٰهُ وَنَفَخَ فِيهِ مِن رُّوحِهِۦۖ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ\nСўнгра уни (инсон қилиб) ростлаб, ичига Ўз (даргоҳидаги) жондан киритди. У зот сизлар учун қулоқ, кўз, дилларни пайдо қилди. Сизлар эса камдан-кам шукр қилурсизлар'}, {'chapter': 32, 'verse': 10, 'text': 'وَقَالُوٓاْ أَءِذَا ضَلَلۡنَا فِي ٱلۡأَرۡضِ أَءِنَّا لَفِي خَلۡقٖ جَدِيدِۭۚ بَلۡ هُم بِلِقَآءِ رَبِّهِمۡ كَٰفِرُونَ\nУлар (яъни, кофирлар): «Бизлар ер (остида) йўқ бўлиб кетгач, ҳақиқатан ҳам яна янги яралиш билан яралурмизми?», дедилар. Йўқ, улар Парвардигорга рўбарў бўлишни инкор қилгувчидирлар'}, {'chapter': 32, 'verse': 11, 'text': '۞قُلۡ يَتَوَفَّىٰكُم مَّلَكُ ٱلۡمَوۡتِ ٱلَّذِي وُكِّلَ بِكُمۡ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Сизларга вакил қилинган ўлим фариштаси жонларингизни олур, сўнгра Парвардигорингизга қайтарилурсизлар»'}, {'chapter': 32, 'verse': 12, 'text': 'وَلَوۡ تَرَىٰٓ إِذِ ٱلۡمُجۡرِمُونَ نَاكِسُواْ رُءُوسِهِمۡ عِندَ رَبِّهِمۡ رَبَّنَآ أَبۡصَرۡنَا وَسَمِعۡنَا فَٱرۡجِعۡنَا نَعۡمَلۡ صَٰلِحًا إِنَّا مُوقِنُونَ\nАгар сиз у жиноятчи кимсаларни (Қиёмат Кунида) Парвардигорлари ҳузурида бошлари эгик ҳолда туриб: «Парвардигоро, (ваъданг рост эканлигини) кўрдик ва эшитдик, бас, бизларни (ҳаётга) қайтаргин, бизлар яхши амал қилайлик. Энди бизлар (Қиёмат ҳақ эканлигига) шак-шубҳасиз, ишонгувчидирмиз», (дейишларини) кўрсангиз эди'}, {'chapter': 32, 'verse': 13, 'text': 'وَلَوۡ شِئۡنَا لَأٓتَيۡنَا كُلَّ نَفۡسٍ هُدَىٰهَا وَلَٰكِنۡ حَقَّ ٱلۡقَوۡلُ مِنِّي لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِينَ\nАгар Биз хоҳласак, албатта ҳар бир жонга ўз ҳидоятини ато этган бўлур эдик, лекин Мен томондан бу сўз ҳақ-собит бўлгандир: «Мен жаҳаннамни (кофир) жин ва (динсиз) одамларнинг барчаси билан тўлдирурман»'}, {'chapter': 32, 'verse': 14, 'text': 'فَذُوقُواْ بِمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَآ إِنَّا نَسِينَٰكُمۡۖ وَذُوقُواْ عَذَابَ ٱلۡخُلۡدِ بِمَا كُنتُمۡ تَعۡمَلُونَ\nБас, (эй кофирлар), ушбу кунингиздаги учрашувни унутиб қўйганларингиз сабабли (аламли азобни) тотингиз! Дарвоқеъ, Биз ҳам (бугун) сизларни «унутдик». Қилиб ўтган амалларингиз сабабли мангу азобни тотингиз'}, {'chapter': 32, 'verse': 15, 'text': 'إِنَّمَا يُؤۡمِنُ بِـَٔايَٰتِنَا ٱلَّذِينَ إِذَا ذُكِّرُواْ بِهَا خَرُّواْۤ سُجَّدٗاۤ وَسَبَّحُواْ بِحَمۡدِ رَبِّهِمۡ وَهُمۡ لَا يَسۡتَكۡبِرُونَ۩\nБизнинг оятларимизга фақат қачон у (оятлар) зикр қилинса, сажда қилган ҳолларида йиқиладиган ва кибр-ҳаво қилмаган ҳолларида ҳамду сано айтиш билан Парвардигорларини поклайдиган зотларгина иймон келтирурлар'}, {'chapter': 32, 'verse': 16, 'text': 'تَتَجَافَىٰ جُنُوبُهُمۡ عَنِ ٱلۡمَضَاجِعِ يَدۡعُونَ رَبَّهُمۡ خَوۡفٗا وَطَمَعٗا وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУларнинг ёнбошлари ўрин-жойларидан йироқ бўлур (яъни, тунларини ибодат билан ўтказишиб, оз ухлайдилар). Улар Парвардигорларига қўрқув ва умидворлик билан дуо-илтижо қилурлар ва Биз уларга ризқ қилиб берган нарсалардан инфоқ-эҳсон қилурлар'}, {'chapter': 32, 'verse': 17, 'text': 'فَلَا تَعۡلَمُ نَفۡسٞ مَّآ أُخۡفِيَ لَهُم مِّن قُرَّةِ أَعۡيُنٖ جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ\nБас, уларнинг қилиб ўтган амалларига мукофот қилиб улар учун беркитиб қўйилган кўзлар қувончини (яъни, Охират неъматларини) бирон жон билмас'}, {'chapter': 32, 'verse': 18, 'text': 'أَفَمَن كَانَ مُؤۡمِنٗا كَمَن كَانَ فَاسِقٗاۚ لَّا يَسۡتَوُۥنَ\nАхир мўмин бўлган киши билан фосиқ-итоатсиз бўлган кимса тенг бўлурми? (Йўқ), баробар бўлмаслар'}, {'chapter': 32, 'verse': 19, 'text': 'أَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَلَهُمۡ جَنَّـٰتُ ٱلۡمَأۡوَىٰ نُزُلَۢا بِمَا كَانُواْ يَعۡمَلُونَ\nИймон келтирган ва яхши амаллар қилган зотлар учун қилиб ўтган амаллари сабабли жаннат маъволари манзил бўлур'}, {'chapter': 32, 'verse': 20, 'text': 'وَأَمَّا ٱلَّذِينَ فَسَقُواْ فَمَأۡوَىٰهُمُ ٱلنَّارُۖ كُلَّمَآ أَرَادُوٓاْ أَن يَخۡرُجُواْ مِنۡهَآ أُعِيدُواْ فِيهَا وَقِيلَ لَهُمۡ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ\nФосиқ бўлган кимсаларнинг маъволари — борар жйлари эса дўзахдир. Ҳар қачон ундан чиқмоқчи бўлсалар, яна унга қайтарилурлар ва уларга: «Ўзларингиз ёлғон деган дўзах азобини тотингиз», дейилур'}, {'chapter': 32, 'verse': 21, 'text': 'وَلَنُذِيقَنَّهُم مِّنَ ٱلۡعَذَابِ ٱلۡأَدۡنَىٰ دُونَ ٱلۡعَذَابِ ٱلۡأَكۡبَرِ لَعَلَّهُمۡ يَرۡجِعُونَ\nАлбатта Биз улар шояд ўз (куфру исёнларидан) қайтсалар, деб уларга (Қиёмат Кунидаги) улуғ азобдан илгари яқин азобни (яъни, дунё азобини — турли кулфат-балоларни, мусибатларни) тотдирурмиз'}, {'chapter': 32, 'verse': 22, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ ثُمَّ أَعۡرَضَ عَنۡهَآۚ إِنَّا مِنَ ٱلۡمُجۡرِمِينَ مُنتَقِمُونَ\nЎз Парвардигорининг оятлари билан панд-насиҳат қилинганидан сўнг, улардан юз ўгирган кимсадан ҳам золимроқ ким бор?! Албатта Биз ундай жиноятчи кимсалардан интиқом олгувчидирмиз'}, {'chapter': 32, 'verse': 23, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَلَا تَكُن فِي مِرۡيَةٖ مِّن لِّقَآئِهِۦۖ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَـٰٓءِيلَ\nАниқки, Биз Мусога Китоб — Таврот ато этдик. Бас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз унинг (Тавротга) рўбарў бўлганидан шубҳа-гумонда бўлманг. Биз (Тавротни) Бани Исроил (қавми) учун раҳбар-йўлбошчи қилдик'}, {'chapter': 32, 'verse': 24, 'text': 'وَجَعَلۡنَا مِنۡهُمۡ أَئِمَّةٗ يَهۡدُونَ بِأَمۡرِنَا لَمَّا صَبَرُواْۖ وَكَانُواْ بِـَٔايَٰتِنَا يُوقِنُونَ\nУлар (ўз динлари йўлидаги бало-кулфатларга) сабр-тоқат қилишгач, Биз улардан (одамларни) ҳидоят қиладиган пешволарни чиқардик. Улар Бизнинг оятларимизга аниқ ишонар эдилар'}, {'chapter': 32, 'verse': 25, 'text': 'إِنَّ رَبَّكَ هُوَ يَفۡصِلُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Парвардигорингизнинг Ўзи Қиёмат Кунида (одамлар) ўртасида улар ихтилоф қилиб ўтган нарсалар ҳақида Ўз ҳукмини баён қилур'}, {'chapter': 32, 'verse': 26, 'text': 'أَوَلَمۡ يَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّنَ ٱلۡقُرُونِ يَمۡشُونَ فِي مَسَٰكِنِهِمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٍۚ أَفَلَا يَسۡمَعُونَ\nАхир уларга (яъни, мушрикларга) Биз улардан илгари қанча аср-авлодларни ҳалок қилганимиз ва ўзлари ўшаларнинг масканларида юрганлари маълум эмасми?! Албатта бунда оят-ибратлар бор-ку! Ахир улар англамайдиларми'}, {'chapter': 32, 'verse': 27, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا نَسُوقُ ٱلۡمَآءَ إِلَى ٱلۡأَرۡضِ ٱلۡجُرُزِ فَنُخۡرِجُ بِهِۦ زَرۡعٗا تَأۡكُلُ مِنۡهُ أَنۡعَٰمُهُمۡ وَأَنفُسُهُمۡۚ أَفَلَا يُبۡصِرُونَ\nАхир улар Биз қуруқ ерга сув ҳайдаб, унинг ёрдамида уларнинг чорвалари ҳам, ўзлари ҳам ейдиган экинларни чиқаришимизни кўрмадиларми?! Ахир қарамайдиларми'}, {'chapter': 32, 'verse': 28, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡفَتۡحُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар (мусулмонларга): «Агар ростгўй бўлсангизлар қачон мана шу фатҳ (яъни, сизларнинг ғалабангиз) рўй берур», дерлар'}, {'chapter': 32, 'verse': 29, 'text': 'قُلۡ يَوۡمَ ٱلۡفَتۡحِ لَا يَنفَعُ ٱلَّذِينَ كَفَرُوٓاْ إِيمَٰنُهُمۡ وَلَا هُمۡ يُنظَرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Фатҳ кунида кофир бўлган кимсаларга иймон келтиришлари фойда бермай қолур ва уларга (тавба-тазарруъ қилиш учун муҳлат ҳам берилмас)'}, {'chapter': 32, 'verse': 30, 'text': 'فَأَعۡرِضۡ عَنۡهُمۡ وَٱنتَظِرۡ إِنَّهُم مُّنتَظِرُونَ\nСўнг сиз улардан юз ўгиринг ва (уларга келадиган азобга) кўз тутинг! Зеро, улар ҳам кўз тутгувчидирлар'}, {'chapter': 33, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ ٱتَّقِ ٱللَّهَ وَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَٱلۡمُنَٰفِقِينَۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\nЭй Пайғамбар, Аллоҳдан қўрқинг ва кофир, мунофиқ кимсаларга бўйинсунманг! Албатта Аллоҳ билгувчи ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 33, 'verse': 2, 'text': 'وَٱتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ مِن رَّبِّكَۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا\nВа (фақат) сизга Парвардигорингиз томонидан ваҳий қилинадиган оятларга эргашинг! Албатта Аллоҳ сизлар қилаётган амаллардан огоҳ бўлган Зотдир'}, {'chapter': 33, 'verse': 3, 'text': 'وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا\nЁлғиз Аллоҳга таваккул қилинг! Аллоҳнинг Ўзи етарли вакил — сақлагувчидир'}, {'chapter': 33, 'verse': 4, 'text': 'مَّا جَعَلَ ٱللَّهُ لِرَجُلٖ مِّن قَلۡبَيۡنِ فِي جَوۡفِهِۦۚ وَمَا جَعَلَ أَزۡوَٰجَكُمُ ٱلَّـٰٓـِٔي تُظَٰهِرُونَ مِنۡهُنَّ أُمَّهَٰتِكُمۡۚ وَمَا جَعَلَ أَدۡعِيَآءَكُمۡ أَبۡنَآءَكُمۡۚ ذَٰلِكُمۡ قَوۡلُكُم بِأَفۡوَٰهِكُمۡۖ وَٱللَّهُ يَقُولُ ٱلۡحَقَّ وَهُوَ يَهۡدِي ٱلسَّبِيلَ\nАллоҳ бирон кишининг ичида икки юрак қилган эмасдир, (шунингдек) сизлар зиҳор қиладиган хотинларингизни ҳам сизларга она қилган эмас ва асранди болаларингизни ўз болаларингиз қилган эмасдир. Бу (яъни, хотинларингизни «она» дейишларингиз, асрандиларингизни «бола» дейишларингиз) сизларнинг оғизларингиздаги сўзингиздир. Ёлғиз Аллоҳ ҳақиқатни айтур ва Унинг Ўзи (ҳақ) йўлга ҳидоят қилур'}, {'chapter': 33, 'verse': 5, 'text': 'ٱدۡعُوهُمۡ لِأٓبَآئِهِمۡ هُوَ أَقۡسَطُ عِندَ ٱللَّهِۚ فَإِن لَّمۡ تَعۡلَمُوٓاْ ءَابَآءَهُمۡ فَإِخۡوَٰنُكُمۡ فِي ٱلدِّينِ وَمَوَٰلِيكُمۡۚ وَلَيۡسَ عَلَيۡكُمۡ جُنَاحٞ فِيمَآ أَخۡطَأۡتُم بِهِۦ وَلَٰكِن مَّا تَعَمَّدَتۡ قُلُوبُكُمۡۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمًا\nУларни ўз оталари (исми) билан чақиринглар. Шу Аллоҳ наздида тўғрироқдир. Энди агар уларнинг оталарини билмасангизлар, у ҳолда (улар) сизларнинг диний биродарларингиз ва дўстларингиздир (яъни, у ҳолда уларни биродарим Фалончи, ёки дўстим Фалончи, деб чақиринглар, лекин боқиб олган отасининг номи билан чақирманглар). Қилган хатоларингиз сабабли сизлар учун гуноҳ йўқдир, лекин кўнгилларингиз (билан) қасд қилган нарсадагина (гуноҳкор бўлурсизлар). Аллоҳ мағфиратли, меҳрибон бўлган Зотдир'}, {'chapter': 33, 'verse': 6, 'text': 'ٱلنَّبِيُّ أَوۡلَىٰ بِٱلۡمُؤۡمِنِينَ مِنۡ أَنفُسِهِمۡۖ وَأَزۡوَٰجُهُۥٓ أُمَّهَٰتُهُمۡۗ وَأُوْلُواْ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ ٱللَّهِ مِنَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُهَٰجِرِينَ إِلَّآ أَن تَفۡعَلُوٓاْ إِلَىٰٓ أَوۡلِيَآئِكُم مَّعۡرُوفٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا\nПайғамбар мўминларга ўзларидан ҳам ҳақдорроқдир, унинг аёллари эса уларнинг оналаридирлар. Аллоҳнинг китобида қариндош-уруғлар (меросхўрликда) бир-бирларига (қон-қариндош бўлмаган) мўминлардан ва муҳожирлардан кўра ҳақдорроқдирлар. Магар сизлар (диний) дўстларингизга (уларни ўзингиз учун ворис қилишлик билан) яхшилик қилмоғингиз (жоиздир). Бу — Китобда (яъни, Лавҳул-Маҳфузда) битиб қўйилган (ҳукмдир)'}, {'chapter': 33, 'verse': 7, 'text': 'وَإِذۡ أَخَذۡنَا مِنَ ٱلنَّبِيِّـۧنَ مِيثَٰقَهُمۡ وَمِنكَ وَمِن نُّوحٖ وَإِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَى ٱبۡنِ مَرۡيَمَۖ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا\nЭсланг, Биз (барча) пайғамбарлардан ва (хусусан сиздан) Нуҳдан, Иброҳим, Мусо ва Ийсо бинни Марямдан аҳду паймонларини олгандик. Улардан пухта аҳду паймон олдик'}, {'chapter': 33, 'verse': 8, 'text': 'لِّيَسۡـَٔلَ ٱلصَّـٰدِقِينَ عَن صِدۡقِهِمۡۚ وَأَعَدَّ لِلۡكَٰفِرِينَ عَذَابًا أَلِيمٗا\nУ (Аллоҳ) ростгўйлардан (Қиёмат Кунида) ростгўйликлари ҳақида сўраш учун (аҳд олди). Аллоҳ кофирлар учун аламли азоб тайёрлаб қўйгандир'}, {'chapter': 33, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ جَآءَتۡكُمۡ جُنُودٞ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا وَجُنُودٗا لَّمۡ تَرَوۡهَاۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا\nЭй мўминлар, сизларга (қарши турли фирқалардан иборат) қўшинлар келган пайтида, Биз уларнинг устига шамол ва сизлар кўрмаган қўшинларни (яъни, фаришталарни) юборганимизни — Аллоҳнинг сизларга берган неъматини эсланглар! Аллоҳ қилаётган амалларингизни кўриб тургувчи бўлган Зотдир'}, {'chapter': 33, 'verse': 10, 'text': 'إِذۡ جَآءُوكُم مِّن فَوۡقِكُمۡ وَمِنۡ أَسۡفَلَ مِنكُمۡ وَإِذۡ زَاغَتِ ٱلۡأَبۡصَٰرُ وَبَلَغَتِ ٱلۡقُلُوبُ ٱلۡحَنَاجِرَ وَتَظُنُّونَ بِٱللَّهِ ٱلظُّنُونَا۠\nЎшанда улар сизларнинг юқори томонингиздан ҳам, сизлардан қуйироқ томондан ҳам (бостириб) келган эдилар. Ўшанда кўзлар тиниб, юраклар бўғизларга тиқилиб қолган ва сизлар Аллоҳ ҳақида (турли) гумонлар қила бошлаган эдинглар'}, {'chapter': 33, 'verse': 11, 'text': 'هُنَالِكَ ٱبۡتُلِيَ ٱلۡمُؤۡمِنُونَ وَزُلۡزِلُواْ زِلۡزَالٗا شَدِيدٗا\nАна ўша жойда мўминлар (яна бир бор) имтиҳон қилиндилар ва (бу даҳшатдан) қаттиқ ларзага тушдилар'}, {'chapter': 33, 'verse': 12, 'text': 'وَإِذۡ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ مَّا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥٓ إِلَّا غُرُورٗا\nЎшанда мунофиқлар ва дилларида мараз бўлган (яъни, эътиқодлари заиф бўлган) кимсалар: «Аллоҳ ва Унинг пайғамбари бизларга фақат ёлғон ваъда қилган эканлар», дея бошладилар'}, {'chapter': 33, 'verse': 13, 'text': 'وَإِذۡ قَالَت طَّآئِفَةٞ مِّنۡهُمۡ يَـٰٓأَهۡلَ يَثۡرِبَ لَا مُقَامَ لَكُمۡ فَٱرۡجِعُواْۚ وَيَسۡتَـٔۡذِنُ فَرِيقٞ مِّنۡهُمُ ٱلنَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوۡرَةٞ وَمَا هِيَ بِعَوۡرَةٍۖ إِن يُرِيدُونَ إِلَّا فِرَارٗا\nЎшанда улардан бир тоифа одам деди: «Эй Ясриб (яъни, Мадина) аҳли, сизлар учун (бу қадар кўп сонли ёвга қарши) туриш имкони йўқдир, бас, (ўз уйларингизга) қайтиб кетинглар». Улардан яна бир гуруҳ эса «Уйларимиз очиқ-сочиқ (қолган эди)», деб пайғамбардан изн сўрардилар. Ҳолбуки, (уйлари) очиқ-сочиқ эмас эди. Улар фақат (жанг майдонидан) қочишнигина истардилар'}, {'chapter': 33, 'verse': 14, 'text': 'وَلَوۡ دُخِلَتۡ عَلَيۡهِم مِّنۡ أَقۡطَارِهَا ثُمَّ سُئِلُواْ ٱلۡفِتۡنَةَ لَأٓتَوۡهَا وَمَا تَلَبَّثُواْ بِهَآ إِلَّا يَسِيرٗا\nАгар уларнинг (мунофиқларнинг) устига (Мадинанинг турли) томонларидан (бостириб) кирилса-да, сўнгра улардан фитна (яъни, мусулмонларга хиёнат қилиш) талаб қилинса, шак-шубҳасиз, улар кўп (иккиланиб) турмасдан (хиёнатга розилик) берган бўлур эдилар'}, {'chapter': 33, 'verse': 15, 'text': 'وَلَقَدۡ كَانُواْ عَٰهَدُواْ ٱللَّهَ مِن قَبۡلُ لَا يُوَلُّونَ ٱلۡأَدۡبَٰرَۚ وَكَانَ عَهۡدُ ٱللَّهِ مَسۡـُٔولٗا\nҲолбуки, улар илгари ортга чекинмасликлари ҳақида Аллоҳга ахду паймон берган, Аллоҳга берилган аҳду паймон эса (Қиёмат Кунида) сўралгувчи эди'}, {'chapter': 33, 'verse': 16, 'text': 'قُل لَّن يَنفَعَكُمُ ٱلۡفِرَارُ إِن فَرَرۡتُم مِّنَ ٱلۡمَوۡتِ أَوِ ٱلۡقَتۡلِ وَإِذٗا لَّا تُمَتَّعُونَ إِلَّا قَلِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Агар сизлар ўлишдан ё ўлдирилишдан қочсангизлар, бу қочиш сизларга бирон фойда бермас, (яъни, фақат ажалларингиз битгунича ўлимдан қочишларингиз мумкин) демак, сизлар жуда оз (яъни, ажалларингиз етгунча) фойдаланурсизлар, холос'}, {'chapter': 33, 'verse': 17, 'text': 'قُلۡ مَن ذَا ٱلَّذِي يَعۡصِمُكُم مِّنَ ٱللَّهِ إِنۡ أَرَادَ بِكُمۡ سُوٓءًا أَوۡ أَرَادَ بِكُمۡ رَحۡمَةٗۚ وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا\nАйтинг: «Агар Аллоҳ сизларга ёмонликни ирода қилса, қай бир кимса сизларни ундан асрай олур, ёки У зот сизларга марҳаматни ирода қилса (ким уни тўса олур)?!» Улар ўзлари учун Аллоҳдан ўзга бирон дўст, бирон ёрдамчи топа олмаслар'}, {'chapter': 33, 'verse': 18, 'text': '۞قَدۡ يَعۡلَمُ ٱللَّهُ ٱلۡمُعَوِّقِينَ مِنكُمۡ وَٱلۡقَآئِلِينَ لِإِخۡوَٰنِهِمۡ هَلُمَّ إِلَيۡنَاۖ وَلَا يَأۡتُونَ ٱلۡبَأۡسَ إِلَّا قَلِيلًا\nАллоҳ сизларнинг орангиздаги (жиҳодга) тўсқинлик қиладиган ва ўз дўстларига «Биз томонга ўтинглар», дейдиган кимсаларни аниқ билур. Улар жангга камдан-кам ҳолдагина келурлар'}, {'chapter': 33, 'verse': 19, 'text': 'أَشِحَّةً عَلَيۡكُمۡۖ فَإِذَا جَآءَ ٱلۡخَوۡفُ رَأَيۡتَهُمۡ يَنظُرُونَ إِلَيۡكَ تَدُورُ أَعۡيُنُهُمۡ كَٱلَّذِي يُغۡشَىٰ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَإِذَا ذَهَبَ ٱلۡخَوۡفُ سَلَقُوكُم بِأَلۡسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى ٱلۡخَيۡرِۚ أُوْلَـٰٓئِكَ لَمۡ يُؤۡمِنُواْ فَأَحۡبَطَ ٱللَّهُ أَعۡمَٰلَهُمۡۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا\nСизларга (яъни, сизларнинг ғалаба қозониб, ўлжаларга эга бўлишингизга) бахилликлари келган ҳолдагина келурлар. Бас, қачон (ёв томонидан) хавф келса, сиз уларни ўлим ўраб (шайтонлаб) қолган кимса каби кўзлари ўйнаб, сиз томонга қараётганларини кўрурсиз. Энди қачон хавф кетса, улар яхшилик (яъни, ўлжалар) устида очкўзлик қилиб сизларни ўткир тиллар билан ранжитурлар. Улар (сидқидилдан) иймон келтирмаганлар, бас, (шу сабабли) Аллоҳ уларнинг амалларини беҳуда кетказди. Бу Аллоҳга осон бўлган ишдир'}, {'chapter': 33, 'verse': 20, 'text': 'يَحۡسَبُونَ ٱلۡأَحۡزَابَ لَمۡ يَذۡهَبُواْۖ وَإِن يَأۡتِ ٱلۡأَحۡزَابُ يَوَدُّواْ لَوۡ أَنَّهُم بَادُونَ فِي ٱلۡأَعۡرَابِ يَسۡـَٔلُونَ عَنۡ أَنۢبَآئِكُمۡۖ وَلَوۡ كَانُواْ فِيكُم مَّا قَٰتَلُوٓاْ إِلَّا قَلِيلٗا\nУлар (яъни, мунофиқлар қўрқоқликлари сабабли кофир фирқалар Мадина атрофидан тарқаб кетганларидан кейин ҳам) у фирқаларни кетмаган, деб ўйлайдилар ва агар у фирқалар (яна қайтиб) қолсалар (мунофиқлар шаҳарда — сизларнинг ичингизда эмас, балки саҳрода) кўчманчи бадавийлар орасида бўлиб, сизларнинг ҳоли-хабарларингиз ҳақида сўраб-билиб туришни дўст тутадилар. Агар улар сизларнинг орангизда бўлганларида ҳам камдан-кам жанг қилган бўлур эдилар'}, {'chapter': 33, 'verse': 21, 'text': 'لَّقَدۡ كَانَ لَكُمۡ فِي رَسُولِ ٱللَّهِ أُسۡوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرۡجُواْ ٱللَّهَ وَٱلۡيَوۡمَ ٱلۡأٓخِرَ وَذَكَرَ ٱللَّهَ كَثِيرٗا\n(Эй мўминлар), сизлар учун — Аллоҳ ва Охират кунидан умидвор бўлган ҳамда Аллоҳни кўп ёд қилган кишилар учун Аллоҳнинг пайғамбари (иймон-эътиқоди ва хулқи атвори)да гўзал ибрат бордир'}, {'chapter': 33, 'verse': 22, 'text': 'وَلَمَّا رَءَا ٱلۡمُؤۡمِنُونَ ٱلۡأَحۡزَابَ قَالُواْ هَٰذَا مَا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥ وَصَدَقَ ٱللَّهُ وَرَسُولُهُۥۚ وَمَا زَادَهُمۡ إِلَّآ إِيمَٰنٗا وَتَسۡلِيمٗا\nМўминлар у фирқаларни кўрган вақтларида: «Бу Аллоҳ ва пайғамбари бизларга ваъда қилган нарсадир (яъни, имтиҳондир). Аллоҳ ва пайғамбарининг сўзи ростдир», дедилар ва (устиларига бостириб келаётган фирқаларнинг сон-саноқсизлиги) уларнинг (Аллоҳга бўлган) иймон ва итоатларини янада зиёда қилди, холос'}, {'chapter': 33, 'verse': 23, 'text': 'مِّنَ ٱلۡمُؤۡمِنِينَ رِجَالٞ صَدَقُواْ مَا عَٰهَدُواْ ٱللَّهَ عَلَيۡهِۖ فَمِنۡهُم مَّن قَضَىٰ نَحۡبَهُۥ وَمِنۡهُم مَّن يَنتَظِرُۖ وَمَا بَدَّلُواْ تَبۡدِيلٗا\nМўминлар орасида ўзлари Аллоҳга берган (У зотнинг йўлида жиҳод қилиб, шаҳид бўлиш ҳақидаги) аҳду паймонларига содиқ бўлган кишилар бордир. Бас, улардан (айрим) кишилар ўз аҳдига вафо қилди, (яъни, шаҳид бўлди), улардан (айрим) кишилар эса (шаҳид бўлишга) интизордир. Улар (мунофиқларга ўхшаб Аллоҳга берган аҳду паймонларини) ўзгартирганлари йўқ'}, {'chapter': 33, 'verse': 24, 'text': 'لِّيَجۡزِيَ ٱللَّهُ ٱلصَّـٰدِقِينَ بِصِدۡقِهِمۡ وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ إِن شَآءَ أَوۡ يَتُوبَ عَلَيۡهِمۡۚ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا\nАллоҳ (ўз аҳду паймонларига) содиқ бўлган кишиларни ростгўйликлари сабабли мукофотлаш учун, мунофиқларни эса агар Ўзи хоҳласа (мунофиқ ҳолларида ўлдириб) азобга гирифтор этиш, ёки тавбаларини қабул қилиб (гуноҳларини мағфират этиш) учун (мазкур синовга дучор қилди). Аллоҳ мағфиратли ва меҳрибон бўлган Зотдир'}, {'chapter': 33, 'verse': 25, 'text': 'وَرَدَّ ٱللَّهُ ٱلَّذِينَ كَفَرُواْ بِغَيۡظِهِمۡ لَمۡ يَنَالُواْ خَيۡرٗاۚ وَكَفَى ٱللَّهُ ٱلۡمُؤۡمِنِينَ ٱلۡقِتَالَۚ وَكَانَ ٱللَّهُ قَوِيًّا عَزِيزٗا\nАллоҳ кофирларни (яъни, фирқаларни) хафа ҳолларида қайтарди. Улар бирон яхшилик фойдага эришмадилар. Аллоҳ мўминларга жанг тўғрисида кифоя қилди (яъни, осмондан шамол ва фаришталар юбориб, мўминларни жангсиз ғолиб қилди). Аллоҳ кучли, қудратли бўлган Зотдир'}, {'chapter': 33, 'verse': 26, 'text': 'وَأَنزَلَ ٱلَّذِينَ ظَٰهَرُوهُم مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ مِن صَيَاصِيهِمۡ وَقَذَفَ فِي قُلُوبِهِمُ ٱلرُّعۡبَ فَرِيقٗا تَقۡتُلُونَ وَتَأۡسِرُونَ فَرِيقٗا\nУ (фирқаларга) ёрдам берган, аҳли китобдан иборат бўлган кимсаларни (Аллоҳ) ўз қалъаларидан туширди ва дилларига қўрқинч солди. (Энди) сизлар (улардан) бир гуруҳни ўлдирурсизлар, бир гуруҳни асир олурсизлар'}, {'chapter': 33, 'verse': 27, 'text': 'وَأَوۡرَثَكُمۡ أَرۡضَهُمۡ وَدِيَٰرَهُمۡ وَأَمۡوَٰلَهُمۡ وَأَرۡضٗا لَّمۡ تَطَـُٔوهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا\nВа сизларни уларнинг ерларига, ҳовли-жойларига, молу мулкларига ва (ҳали) сизларнинг оёқларингиз етмаган ерларга ворис қилди. Аллоҳ ҳар нарсага қодир бўлган Зотдир'}, {'chapter': 33, 'verse': 28, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّأَزۡوَٰجِكَ إِن كُنتُنَّ تُرِدۡنَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا وَزِينَتَهَا فَتَعَالَيۡنَ أُمَتِّعۡكُنَّ وَأُسَرِّحۡكُنَّ سَرَاحٗا جَمِيلٗا\nЭй пайғамбар, жуфтларингизга айтинг: «Агар сизлар ҳаёти дунёни ва унинг зебу зийнатларини истайдиган бўлсангизлар, у ҳолда келинглар, мен сизларни (ўша нарсалардан) баҳраманд этай ва чиройли кузатиш билан кузатай'}, {'chapter': 33, 'verse': 29, 'text': 'وَإِن كُنتُنَّ تُرِدۡنَ ٱللَّهَ وَرَسُولَهُۥ وَٱلدَّارَ ٱلۡأٓخِرَةَ فَإِنَّ ٱللَّهَ أَعَدَّ لِلۡمُحۡسِنَٰتِ مِنكُنَّ أَجۡرًا عَظِيمٗا\nАгар Аллоҳни, Унинг пайғамбарини ва Охират диёрини истайдиган бўлсангизлар, ҳолда шак-шубҳасиз, Аллоҳ сизларнинг орангиздаги чиройли амал қилгувчилар учун улуғ мукофот — жаннатни тайёрлаб қўйгандир»'}, {'chapter': 33, 'verse': 30, 'text': 'يَٰنِسَآءَ ٱلنَّبِيِّ مَن يَأۡتِ مِنكُنَّ بِفَٰحِشَةٖ مُّبَيِّنَةٖ يُضَٰعَفۡ لَهَا ٱلۡعَذَابُ ضِعۡفَيۡنِۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا\nЭй пайғамбар аёллари, сизлардан ким аниқ гуноҳни қилар экан, унинг учун азоб икки баробар қилинур. Бу Аллоҳга осон бўлган ишдир'}, {'chapter': 33, 'verse': 31, 'text': '۞وَمَن يَقۡنُتۡ مِنكُنَّ لِلَّهِ وَرَسُولِهِۦ وَتَعۡمَلۡ صَٰلِحٗا نُّؤۡتِهَآ أَجۡرَهَا مَرَّتَيۡنِ وَأَعۡتَدۡنَا لَهَا رِزۡقٗا كَرِيمٗا\nСизлардан ким Аллоҳ ва Унинг пайғамбарига итоат этса ва яхши амал қилса, Биз унинг ажру мукофотини икки ҳисса қилиб берурмиз ва унинг учун улуғ ризқ (яъни, жаннат)ни тайёрлаб қўйгандирмиз'}, {'chapter': 33, 'verse': 32, 'text': 'يَٰنِسَآءَ ٱلنَّبِيِّ لَسۡتُنَّ كَأَحَدٖ مِّنَ ٱلنِّسَآءِ إِنِ ٱتَّقَيۡتُنَّۚ فَلَا تَخۡضَعۡنَ بِٱلۡقَوۡلِ فَيَطۡمَعَ ٱلَّذِي فِي قَلۡبِهِۦ مَرَضٞ وَقُلۡنَ قَوۡلٗا مَّعۡرُوفٗا\nЭй пайғамбар аёллари, сизлар агар тақводор бўлсангизлар, аёллардан биронтаси каби эмасдирсизлар (яъни, бирон аёл фазлу мартабада сизлар каби бўла олмас). Бас, сизлар (парда ортидан бирон номаҳрам эркакка жавоб қилган пайтларингизда) майин-назокатли сўз қилмангларки, у ҳолда кўнглида мараз-нифоқ бўлган кимса тамаъ қилиб қолур. Яхши — тўғри сўзни сўзланглар'}, {'chapter': 33, 'verse': 33, 'text': 'وَقَرۡنَ فِي بُيُوتِكُنَّ وَلَا تَبَرَّجۡنَ تَبَرُّجَ ٱلۡجَٰهِلِيَّةِ ٱلۡأُولَىٰۖ وَأَقِمۡنَ ٱلصَّلَوٰةَ وَءَاتِينَ ٱلزَّكَوٰةَ وَأَطِعۡنَ ٱللَّهَ وَرَسُولَهُۥٓۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُذۡهِبَ عَنكُمُ ٱلرِّجۡسَ أَهۡلَ ٱلۡبَيۡتِ وَيُطَهِّرَكُمۡ تَطۡهِيرٗا\nЎз уйларингизда барқарор бўлинглар (яъни, бесабаб уйларингиздан ташқарига чиқманглар, магар бирон ҳожат учун чиққанларингизда эса илгариги динсизлик (даври)даги ясан-тусан каби ясан-тусан қилманглар! Намозни тўкис адо қилинглар, закотни (ҳақдорларга) ато этинглар ҳамда (барча ишларда) Аллоҳ ва Унинг пайғамбарига итоат этинглар! (Эй пайғамбар) хонадонининг аҳли, Аллоҳ сизлардан гуноҳни кетказишни ва сизларни бутунлай поклашни истайди, холос'}, {'chapter': 33, 'verse': 34, 'text': 'وَٱذۡكُرۡنَ مَا يُتۡلَىٰ فِي بُيُوتِكُنَّ مِنۡ ءَايَٰتِ ٱللَّهِ وَٱلۡحِكۡمَةِۚ إِنَّ ٱللَّهَ كَانَ لَطِيفًا خَبِيرًا\nУйларингизда Аллоҳнинг оятлари ва ҳикмат (яъни, пайғамбар ҳадислари)дан иборат тиловат қилинадиган нарсаларни зикр-тиловат қилинглар! Албатта Аллоҳ меҳрибон ва огоҳ бўлган Зотдир'}, {'chapter': 33, 'verse': 35, 'text': 'إِنَّ ٱلۡمُسۡلِمِينَ وَٱلۡمُسۡلِمَٰتِ وَٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ وَٱلۡقَٰنِتِينَ وَٱلۡقَٰنِتَٰتِ وَٱلصَّـٰدِقِينَ وَٱلصَّـٰدِقَٰتِ وَٱلصَّـٰبِرِينَ وَٱلصَّـٰبِرَٰتِ وَٱلۡخَٰشِعِينَ وَٱلۡخَٰشِعَٰتِ وَٱلۡمُتَصَدِّقِينَ وَٱلۡمُتَصَدِّقَٰتِ وَٱلصَّـٰٓئِمِينَ وَٱلصَّـٰٓئِمَٰتِ وَٱلۡحَٰفِظِينَ فُرُوجَهُمۡ وَٱلۡحَٰفِظَٰتِ وَٱلذَّـٰكِرِينَ ٱللَّهَ كَثِيرٗا وَٱلذَّـٰكِرَٰتِ أَعَدَّ ٱللَّهُ لَهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمٗا\nАлбатта муслим ва муслималар, мўмин ва мўминалар, итоатгўй эркаклар ва итоатгўй аёллар, ростгўй эркаклар ва ростгўй аёллар, сабр-қаноат қилгувчи эркаклар ва сабр-қаноат қилгувчи аёллар, тавозуъли эркаклар ва тавозуъли аёллар, хайру-садақа қилгувчи эркаклар ва хайру-садақа қилгувчи аёллар, рўза тутгувчи эркаклар ва рўза тутгувчи аёллар, авратларини (ҳаромдан) сақлагувчи эркаклар ва (авратларини ҳаромдан) сақлагувчи аёллар, Аллоҳни кўп зикр қилгувчи эркаклар ва (Аллоҳни кўп) зикр қилгувчи аёллар — улар учун Аллоҳ мағфират ва улуғ мукофот (яъни, жаннат) тайёрлаб қўйгандир'}, {'chapter': 33, 'verse': 36, 'text': 'وَمَا كَانَ لِمُؤۡمِنٖ وَلَا مُؤۡمِنَةٍ إِذَا قَضَى ٱللَّهُ وَرَسُولُهُۥٓ أَمۡرًا أَن يَكُونَ لَهُمُ ٱلۡخِيَرَةُ مِنۡ أَمۡرِهِمۡۗ وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ ضَلَّ ضَلَٰلٗا مُّبِينٗا\nАллоҳ ва Унинг пайғамбари бир ишни ҳукм қилган — буюрган вақтида бирон мўмин ва мўмина учун (Аллоҳни ҳукмини қўйиб) ўз ишларидан ихтиёр қилиш жоиз эмасдир. Ким Аллоҳ ва Унинг пайғамбарига осий бўлса, бас, у очиқ йўлдан озиш билан йўлдан озибди'}, {'chapter': 33, 'verse': 37, 'text': 'وَإِذۡ تَقُولُ لِلَّذِيٓ أَنۡعَمَ ٱللَّهُ عَلَيۡهِ وَأَنۡعَمۡتَ عَلَيۡهِ أَمۡسِكۡ عَلَيۡكَ زَوۡجَكَ وَٱتَّقِ ٱللَّهَ وَتُخۡفِي فِي نَفۡسِكَ مَا ٱللَّهُ مُبۡدِيهِ وَتَخۡشَى ٱلنَّاسَ وَٱللَّهُ أَحَقُّ أَن تَخۡشَىٰهُۖ فَلَمَّا قَضَىٰ زَيۡدٞ مِّنۡهَا وَطَرٗا زَوَّجۡنَٰكَهَا لِكَيۡ لَا يَكُونَ عَلَى ٱلۡمُؤۡمِنِينَ حَرَجٞ فِيٓ أَزۡوَٰجِ أَدۡعِيَآئِهِمۡ إِذَا قَضَوۡاْ مِنۡهُنَّ وَطَرٗاۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), эсланг, сиз Аллоҳ (Исломга ҳидоят қилиш билан) инъом-марҳамат қилган ва сиз (қулликдан озод қилиб, ўзингизга ўғил қилиб олиш билан) инъом қилган кишига (яъни, Зайд бинни Хорисага): «Жуфтингни ўз ҳузурингда ушлагин (яъни, талоқ қилишга шошмагин), Аллоҳдан қўрқгин», деб Аллоҳ ошкор қилгувчи бўлган нарсани ичингизга яширган эдингиз ва Аллоҳдан қўрқишингиз ҳақроқ бўлгани ҳолда, сиз одамлардан (яъни, уларнинг таъна қилишларидан) қўрққан эдингиз. Бас, қачонки Зайд ундан (яъни, Зайнабдан) ҳожатини адо қилгач (яъни, уни талоқ қилгач), Биз сизни унга уйлантирдик. Токи мўминларга асранди болалари ўз хотинларидан ҳожатларини адо қилишгач (яъни, уларни талоқ қилишгач) уларнинг (хотинларига уйланишларида) танглик бўлмаслиги учун (шундай қилдик). Ва Аллоҳнинг амри иродаси қилингувчи бўлди'}, {'chapter': 33, 'verse': 38, 'text': 'مَّا كَانَ عَلَى ٱلنَّبِيِّ مِنۡ حَرَجٖ فِيمَا فَرَضَ ٱللَّهُ لَهُۥۖ سُنَّةَ ٱللَّهِ فِي ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلُۚ وَكَانَ أَمۡرُ ٱللَّهِ قَدَرٗا مَّقۡدُورًا\nАллоҳ пайғамбарга фарз қилган (яъни, ҳалол қилган) нарсада унга бирон танглик бўлмас. (Бу) илгари ўтган (пайғамбарлар) ҳақидаги Аллоҳнинг йўли — қонунидир (яъни, улар учун ҳам Аллоҳ ҳалол қилган нарсаларда ҳеч қандай танглик бўлмаган). Аллоҳнинг амри иродаси тақдири азалий бўлди'}, {'chapter': 33, 'verse': 39, 'text': 'ٱلَّذِينَ يُبَلِّغُونَ رِسَٰلَٰتِ ٱللَّهِ وَيَخۡشَوۡنَهُۥ وَلَا يَخۡشَوۡنَ أَحَدًا إِلَّا ٱللَّهَۗ وَكَفَىٰ بِٱللَّهِ حَسِيبٗا\nУлар (яъни, ўтган пайғамбарлар) Аллоҳнинг амру-фармонларини (бандаларга) етказадиган, У зотдан қўрқадиган ва Аллоҳдан ўзга бировдан қўрқмайдиган зотлардир. Аллоҳнинг Ўзи етарли ҳисоб қилгувчидир'}, {'chapter': 33, 'verse': 40, 'text': 'مَّا كَانَ مُحَمَّدٌ أَبَآ أَحَدٖ مِّن رِّجَالِكُمۡ وَلَٰكِن رَّسُولَ ٱللَّهِ وَخَاتَمَ ٱلنَّبِيِّـۧنَۗ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا\nМуҳаммад сизлардан бирон кишининг отаси эмасдир, балки у Аллоҳнинг пайғамбари ва пайғамбарларнинг сўнггисидир. Аллоҳ барча нарсани билгувчи бўлган Зотдир'}, {'chapter': 33, 'verse': 41, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ ٱللَّهَ ذِكۡرٗا كَثِيرٗا\nЭй мўминлар, Аллоҳни кўп зикр қилинглар'}, {'chapter': 33, 'verse': 42, 'text': 'وَسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا\nВа эрта-ю кеч У зотни поклаб тасбеҳ айтинглар'}, {'chapter': 33, 'verse': 43, 'text': 'هُوَ ٱلَّذِي يُصَلِّي عَلَيۡكُمۡ وَمَلَـٰٓئِكَتُهُۥ لِيُخۡرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَكَانَ بِٱلۡمُؤۡمِنِينَ رَحِيمٗا\nУ сизларни (куфр) зулматларидан (иймон) нурига чиқариш учун сизларга марҳамат кўрсатадиган Зотдир. Унинг фаришталари ҳам (ҳақларингизга дуо қилурлар). У мўминларга меҳрибон бўлган Зотдир'}, {'chapter': 33, 'verse': 44, 'text': 'تَحِيَّتُهُمۡ يَوۡمَ يَلۡقَوۡنَهُۥ سَلَٰمٞۚ وَأَعَدَّ لَهُمۡ أَجۡرٗا كَرِيمٗا\n(Мўминлар Аллоҳга) рўбарў бўладиган кунда уларга (Аллоҳ томонидан йўлланадиган) салом тинчлик-омонлик тилаш бўлур. (Аллоҳ) улар учун улуғ мукофот (яъни, жаннат) тайёрлаб қўйгандир'}, {'chapter': 33, 'verse': 45, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا\nЭй пайғамбар, дарҳақиқат, Биз сизни (Қиёмат Кунида барча умматлар устида) гувоҳлик бергувчи, (мўминларга жаннат ҳақида) хушхабар элтгувчи ва (кофирларни дўзах азобидан) огоҳлантиргувчи қилиб юборгандирмиз'}, {'chapter': 33, 'verse': 46, 'text': 'وَدَاعِيًا إِلَى ٱللَّهِ بِإِذۡنِهِۦ وَسِرَاجٗا مُّنِيرٗا\nҲамда Аллоҳнинг изни-иродаси билан У зотга (яъни, Унинг динига) даъват қилгувчи ва (Ҳақ йўлини кўрсатгувчи) нурли чироқ қилиб юборгандирмиз'}, {'chapter': 33, 'verse': 47, 'text': 'وَبَشِّرِ ٱلۡمُؤۡمِنِينَ بِأَنَّ لَهُم مِّنَ ٱللَّهِ فَضۡلٗا كَبِيرٗا\nМўминларга хушхабар берингки, шак-шубҳасиз, улар учун Аллоҳ томонидан катта фазлу марҳамат (яъни, жаннат) бордир'}, {'chapter': 33, 'verse': 48, 'text': 'وَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَٱلۡمُنَٰفِقِينَ وَدَعۡ أَذَىٰهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا\nСиз кофирлар ва мунофиқларга бўйинсунманг ва уларнинг озор-азиятларига парво қилманг ҳамда Аллоҳга таваккул қилинг! Аллоҳнинг Ўзи етарли вакил-ҳомийдир'}, {'chapter': 33, 'verse': 49, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نَكَحۡتُمُ ٱلۡمُؤۡمِنَٰتِ ثُمَّ طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمۡ عَلَيۡهِنَّ مِنۡ عِدَّةٖ تَعۡتَدُّونَهَاۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحٗا جَمِيلٗا\nЭй мўминлар, қачон сизлар мўминаларни никоҳларингизга олсангизлар-у, сўнгра уларга қўл тегизишдан (яқинлашишдан) илгари уларни талоқ қилсангизлар, у ҳолда сизлар учун уларнинг зиммасида санайдиган идда бўлмас. Бас, сизлар уларни (озми-кўпми ҳадя билан) баҳраманд қилиб, чиройли кузатиш билан кузатинглар'}, {'chapter': 33, 'verse': 50, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِنَّآ أَحۡلَلۡنَا لَكَ أَزۡوَٰجَكَ ٱلَّـٰتِيٓ ءَاتَيۡتَ أُجُورَهُنَّ وَمَا مَلَكَتۡ يَمِينُكَ مِمَّآ أَفَآءَ ٱللَّهُ عَلَيۡكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّـٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَٰلَٰتِكَ ٱلَّـٰتِي هَاجَرۡنَ مَعَكَ وَٱمۡرَأَةٗ مُّؤۡمِنَةً إِن وَهَبَتۡ نَفۡسَهَا لِلنَّبِيِّ إِنۡ أَرَادَ ٱلنَّبِيُّ أَن يَسۡتَنكِحَهَا خَالِصَةٗ لَّكَ مِن دُونِ ٱلۡمُؤۡمِنِينَۗ قَدۡ عَلِمۡنَا مَا فَرَضۡنَا عَلَيۡهِمۡ فِيٓ أَزۡوَٰجِهِمۡ وَمَا مَلَكَتۡ أَيۡمَٰنُهُمۡ لِكَيۡلَا يَكُونَ عَلَيۡكَ حَرَجٞۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nЭй пайғамбар, албатта Биз сиз учун ҳаққи-маҳрларини берган жуфтларингизни, Аллоҳ сизга (жангу жадалларда) ўлжа қилиб берган қўл остингиздаги чўриларингизни, сиз билан бирга ҳижрат қилган амакингизнинг қизларини ва аммаларингизнинг қизларини, тоғангизнинг қизларини ва холаларингизнинг қизларини яна (ҳар қандай) мўмина аёлни — агар у ўзини пайғамбарга ҳадя этсаю, пайғамбар уни (ҳаққи маҳр бермай) ўз никоҳига олишни истаса (мазкур аёлларнинг барчасини Биз сиз учун) ҳалол қилдик. (Ўзини ҳадя этган аёлга ҳаққи маҳрини бермасдан уйланиш ижозати) мўминлар учун эмас, холис сиз учундир. Биз (мўминларга) жуфтлари ва қўл остиларидаги чўрилари ҳақида фарз қилган ҳукмларимиз эса аниқ маълумдир. Токи сизга танглик бўлмаслиги учун (Биз сизга мазкур имтиёзларни бердик). Аллоҳ мағфиратли ва меҳрибон бўлган Зотдир'}, {'chapter': 33, 'verse': 51, 'text': '۞تُرۡجِي مَن تَشَآءُ مِنۡهُنَّ وَتُـٔۡوِيٓ إِلَيۡكَ مَن تَشَآءُۖ وَمَنِ ٱبۡتَغَيۡتَ مِمَّنۡ عَزَلۡتَ فَلَا جُنَاحَ عَلَيۡكَۚ ذَٰلِكَ أَدۡنَىٰٓ أَن تَقَرَّ أَعۡيُنُهُنَّ وَلَا يَحۡزَنَّ وَيَرۡضَيۡنَ بِمَآ ءَاتَيۡتَهُنَّ كُلُّهُنَّۚ وَٱللَّهُ يَعۡلَمُ مَا فِي قُلُوبِكُمۡۚ وَكَانَ ٱللَّهُ عَلِيمًا حَلِيمٗا\nСиз (аёлларингиздан) ўзингиз хоҳлаган аёлни қолдириб, ўзингиз хоҳлаган аёлни ўзингизга ҳамхона қилурсиз. Ўзингиз четлатган аёлларингиздан бирон аёлни (ўзингизга яна ҳамхона қилишни) истасангиз сизга ҳеч қандай гуноҳ бўлмас. Бу (яъни, аёлларингизга қиладиган муомалангизда сизга ихтиёр бериб қўйишимиз), уларнинг кўзлари қувонишига, ғамгин бўлмасликларига ва сиз уларга ато этган нарсага барчалари рози бўлишларига яқинроқ (йўлдир). Аллоҳ сизларнинг дилларингиздаги сирларингизни (ҳам) билур. Аллоҳ билгувчи ва ҳалим бўлган Зотдир'}, {'chapter': 33, 'verse': 52, 'text': 'لَّا يَحِلُّ لَكَ ٱلنِّسَآءُ مِنۢ بَعۡدُ وَلَآ أَن تَبَدَّلَ بِهِنَّ مِنۡ أَزۡوَٰجٖ وَلَوۡ أَعۡجَبَكَ حُسۡنُهُنَّ إِلَّا مَا مَلَكَتۡ يَمِينُكَۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ رَّقِيبٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз учун бундан сўнг аёллар (яъни, мана шу аёлларингиз устига яна уйланишингиз) ҳалол бўлмас ва гарчи (бошқа) хотинларнинг ҳусни жамоллари сизни қизиқтирса ҳам уларни (ўз аёлларингизга) алмаштириш ҳам (яъни, аёлларингизни талоқ қилиб, уларни никоҳингизга олиш ҳам сиз учун ҳалол бўлмас). Магар сиз эга бўлган чўрилар (ҳалолдир). Аллоҳ барча нарса устида кузатиб тургувчи бўлган Зотдир'}, {'chapter': 33, 'verse': 53, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتَ ٱلنَّبِيِّ إِلَّآ أَن يُؤۡذَنَ لَكُمۡ إِلَىٰ طَعَامٍ غَيۡرَ نَٰظِرِينَ إِنَىٰهُ وَلَٰكِنۡ إِذَا دُعِيتُمۡ فَٱدۡخُلُواْ فَإِذَا طَعِمۡتُمۡ فَٱنتَشِرُواْ وَلَا مُسۡتَـٔۡنِسِينَ لِحَدِيثٍۚ إِنَّ ذَٰلِكُمۡ كَانَ يُؤۡذِي ٱلنَّبِيَّ فَيَسۡتَحۡيِۦ مِنكُمۡۖ وَٱللَّهُ لَا يَسۡتَحۡيِۦ مِنَ ٱلۡحَقِّۚ وَإِذَا سَأَلۡتُمُوهُنَّ مَتَٰعٗا فَسۡـَٔلُوهُنَّ مِن وَرَآءِ حِجَابٖۚ ذَٰلِكُمۡ أَطۡهَرُ لِقُلُوبِكُمۡ وَقُلُوبِهِنَّۚ وَمَا كَانَ لَكُمۡ أَن تُؤۡذُواْ رَسُولَ ٱللَّهِ وَلَآ أَن تَنكِحُوٓاْ أَزۡوَٰجَهُۥ مِنۢ بَعۡدِهِۦٓ أَبَدًاۚ إِنَّ ذَٰلِكُمۡ كَانَ عِندَ ٱللَّهِ عَظِيمًا\nЭй мўминлар, пайғамбарнинг уйларига фақат сизларни бирон таомга чақирилсагина киринглар. (Ўшанда ҳам) унинг пишишига кўз тутиб тургувчи бўлманглар, балки чақирилган пайтингизда кириб, таомлангач тарқалинглар ва бирон гапга берилган ҳолингизда (у ерда қолиб кетманглар)! Чунки бу (ишларингиз) пайғамбарга озор берур, у эса сизлардан (яъни, сизларни чиқариб юборишдан) тортинар. Аллоҳ ҳақни (айтишдан) тортинмас. Қачон сизлар (пайғамбар аёлларидан бирон нарса сўрасангизлар) парда ортида туриб сўранглар! Мана шу сизларнинг дилларингизни ҳам, уларнинг дилларини ҳам тоза тутгувчироқдир. Сизлар учун Аллоҳнинг пайғамбарига озор бериш ва унинг ортидан аёлларига уйланишингиз ҳеч қачон дуруст эмасдир. Чунки бу (ишларингиз) Аллоҳ наздида улуғ (гуноҳ) бўлган ишдир'}, {'chapter': 33, 'verse': 54, 'text': 'إِن تُبۡدُواْ شَيۡـًٔا أَوۡ تُخۡفُوهُ فَإِنَّ ٱللَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا\nАгар сизлар бирон нарсани ошкор қилсангизлар ё яширсангизлар, албатта (Аллоҳ билур). Зеро, Аллоҳ барча нарсани билгувчи бўлган Зотдир'}, {'chapter': 33, 'verse': 55, 'text': 'لَّا جُنَاحَ عَلَيۡهِنَّ فِيٓ ءَابَآئِهِنَّ وَلَآ أَبۡنَآئِهِنَّ وَلَآ إِخۡوَٰنِهِنَّ وَلَآ أَبۡنَآءِ إِخۡوَٰنِهِنَّ وَلَآ أَبۡنَآءِ أَخَوَٰتِهِنَّ وَلَا نِسَآئِهِنَّ وَلَا مَا مَلَكَتۡ أَيۡمَٰنُهُنَّۗ وَٱتَّقِينَ ٱللَّهَۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا\n(Пайғамбар аёллари) ўзларининг оталарига, ўғилларига, оға-иниларига, оға-иниларининг ўғилларига, опа-сингилларининг ўғилларига, (мўмина) аёлларга ва қўл остиларидаги чўриларга (очиқ ҳолда кўринишларида) уларга бирон гуноҳ бўлмас. (Эй пайғамбар аёллари), Аллоҳдан қўрқинглар! Албатта Аллоҳ барча нарса устида гувоҳ бўлган Зотдир'}, {'chapter': 33, 'verse': 56, 'text': 'إِنَّ ٱللَّهَ وَمَلَـٰٓئِكَتَهُۥ يُصَلُّونَ عَلَى ٱلنَّبِيِّۚ يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ صَلُّواْ عَلَيۡهِ وَسَلِّمُواْ تَسۡلِيمًا\nАлбатта Аллоҳ ҳам, Унинг фаришталари ҳам пайғамбарга дуою салавот айтурлар. Эй мўминлар, сизлар ҳам у зотга саловот ва саломлар айтинглар'}, {'chapter': 33, 'verse': 57, 'text': 'إِنَّ ٱلَّذِينَ يُؤۡذُونَ ٱللَّهَ وَرَسُولَهُۥ لَعَنَهُمُ ٱللَّهُ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَأَعَدَّ لَهُمۡ عَذَابٗا مُّهِينٗا\nАлбатта Аллоҳга ва Унинг пайғамбарига озор берадиган кимсаларни Аллоҳ дунёда ҳам, Охиратда ҳам лаънатлагандир ва улар учун хор қилгувчи азобни тайёрлаб қўйгандир'}, {'chapter': 33, 'verse': 58, 'text': 'وَٱلَّذِينَ يُؤۡذُونَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ بِغَيۡرِ مَا ٱكۡتَسَبُواْ فَقَدِ ٱحۡتَمَلُواْ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا\nМўмин ва мўминаларга бирон гуноҳ қилмасликларидан озор берадиган кимсалар ҳам бўҳтон ва очиқ гуноҳни ўз устларига олибдилар'}, {'chapter': 33, 'verse': 59, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّأَزۡوَٰجِكَ وَبَنَاتِكَ وَنِسَآءِ ٱلۡمُؤۡمِنِينَ يُدۡنِينَ عَلَيۡهِنَّ مِن جَلَٰبِيبِهِنَّۚ ذَٰلِكَ أَدۡنَىٰٓ أَن يُعۡرَفۡنَ فَلَا يُؤۡذَيۡنَۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nЭй пайғамбар, жуфтларингизга, қизларингизга ва мўминларнинг аёлларига айтинг, устларига ёпинчиқларини ўрасинлар! Мана шу уларнинг (чўри эмас, балки озод аёллар эканликлари) танилиб, озорланмасликлари учун энг яқин (воситадир). Аллоҳ мағфиратли ва меҳрибон бўлган Зотдир'}, {'chapter': 33, 'verse': 60, 'text': '۞لَّئِن لَّمۡ يَنتَهِ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡمُرۡجِفُونَ فِي ٱلۡمَدِينَةِ لَنُغۡرِيَنَّكَ بِهِمۡ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَآ إِلَّا قَلِيلٗا\nҚасамки, агар мунофиқлар, дилларида мараз бўлган кимсалар ва Мадинада миш-миш тарқатиб юрувчилар (ўз қилмишларидан) тўхтамасалар, албатта Биз сизни уларга қарши оёқлантирурмиз, сўнгра улар (Мадинада) сиз билан бирга тура олмай қолурлар, фақат озгина тура олурлар, холос'}, {'chapter': 33, 'verse': 61, 'text': 'مَّلۡعُونِينَۖ أَيۡنَمَا ثُقِفُوٓاْ أُخِذُواْ وَقُتِّلُواْ تَقۡتِيلٗا\nМагар лаънатга дучор бўлган ҳолларидагина (тура олурлар, холос). (У пайтда) улар қаерда топилсалар ушланурлар ва ўлдириб ташланурлар'}, {'chapter': 33, 'verse': 62, 'text': 'سُنَّةَ ٱللَّهِ فِي ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا\n(Бу) илгари ўтган зотлар ҳақидаги Аллоҳнинг йўли — қонунидир. Аллоҳнинг йўлини эса ҳаргиз ўзгартира олмассиз'}, {'chapter': 33, 'verse': 63, 'text': 'يَسۡـَٔلُكَ ٱلنَّاسُ عَنِ ٱلسَّاعَةِۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِۚ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ تَكُونُ قَرِيبًا\nОдамлар сиздан (Қиёмат) соати (қачон бўлиши) ҳақида сўрайдилар. «У (соатни) билиш ёлғиз Аллоҳнинг ҳузуридадир», деб айтинг. Қаердан билурсиз, эҳтимол у соат яқин келиб қолгандир'}, {'chapter': 33, 'verse': 64, 'text': 'إِنَّ ٱللَّهَ لَعَنَ ٱلۡكَٰفِرِينَ وَأَعَدَّ لَهُمۡ سَعِيرًا\nАлбатта Аллоҳ кофирларни лаънатлади ва улар учун ўтни тайёрлаб қўйди'}, {'chapter': 33, 'verse': 65, 'text': 'خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا\nУлар на бирон дўст ва на бирон ёрдамчи топмаган ҳолларида у жойда мангу қолурлар'}, {'chapter': 33, 'verse': 66, 'text': 'يَوۡمَ تُقَلَّبُ وُجُوهُهُمۡ فِي ٱلنَّارِ يَقُولُونَ يَٰلَيۡتَنَآ أَطَعۡنَا ٱللَّهَ وَأَطَعۡنَا ٱلرَّسُولَا۠\nЮзлари оловда айлантириладиган — куйдириладиган кунда, улар: «Кошки эди бизлар ҳам Аллоҳга итоат этганимизда, пайғамбарга итоат этганимизда», дерлар'}, {'chapter': 33, 'verse': 67, 'text': 'وَقَالُواْ رَبَّنَآ إِنَّآ أَطَعۡنَا سَادَتَنَا وَكُبَرَآءَنَا فَأَضَلُّونَا ٱلسَّبِيلَا۠\nЯна улар: «Парвардигоро, дарҳақиқат, бизлар бошлиқларимизга ва катталаримизга бўйинсундик, бас, улар бизларни (тўғри) йўлдан оздирдилар'}, {'chapter': 33, 'verse': 68, 'text': 'رَبَّنَآ ءَاتِهِمۡ ضِعۡفَيۡنِ مِنَ ٱلۡعَذَابِ وَٱلۡعَنۡهُمۡ لَعۡنٗا كَبِيرٗا\nПарвардигоро, Сен уларга азобни икки ҳисса қилиб бергин ва уларни катта лаънат билан лаънатлагин», дедилар'}, {'chapter': 33, 'verse': 69, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَٱلَّذِينَ ءَاذَوۡاْ مُوسَىٰ فَبَرَّأَهُ ٱللَّهُ مِمَّا قَالُواْۚ وَكَانَ عِندَ ٱللَّهِ وَجِيهٗا\nЭй мўминлар, сизлар (ўз пайғамбарларингиз Муҳаммад алайҳис-салоту вас-саломга нисбатан Бани Исроил қавмидан бўлган) Мусога озор берган кимсалар каби бўлмангизлар! Бас, Аллоҳ (Мусони) улар айтган айблардан поклади. У Аллоҳ наздида обрўли киши эди. (Худди шунингдек Муҳаммад алайҳис-салоту вас-салом ҳам Аллоҳ наздида обрўли кишидир)'}, {'chapter': 33, 'verse': 70, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَقُولُواْ قَوۡلٗا سَدِيدٗا\nЭй мўминлар, Аллоҳдан қўрқинглар, тўғри сўзни сўзланглар'}, {'chapter': 33, 'verse': 71, 'text': 'يُصۡلِحۡ لَكُمۡ أَعۡمَٰلَكُمۡ وَيَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ فَازَ فَوۡزًا عَظِيمًا\n(Шунда Аллоҳ) ишларингизни ўнглар ва гуноҳларингизни мағфират қилур. Ким Аллоҳга ва Унинг пайғамбарига итоат этса, бас, у улуғ бахтга эришибди'}, {'chapter': 33, 'verse': 72, 'text': 'إِنَّا عَرَضۡنَا ٱلۡأَمَانَةَ عَلَى ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلۡجِبَالِ فَأَبَيۡنَ أَن يَحۡمِلۡنَهَا وَأَشۡفَقۡنَ مِنۡهَا وَحَمَلَهَا ٱلۡإِنسَٰنُۖ إِنَّهُۥ كَانَ ظَلُومٗا جَهُولٗا\nАлбатта Биз бу омонатни (яъни, шариати Исломийядаги тоат-ибодатларни) осмонларга, Ерга ва тоғу тошларга кўндаланг қилган эдик, улар уни кўтаришдан бош тортдилар ва ундан қўрқдилар. Инсон эса уни ўз зиммасига олди. Дарҳақиқат, у (ўзига) зулм қилгувчи ва нодон эди (яъни, бу омонатнинг нақадар вазмин юк эканлигини бутун коинот билди ва уни кўтаришга қурби етмаслигини сезди, аммо инсон ўзи билмаган ҳолда ўта мушкул вазифани ўз зиммасига олди)'}, {'chapter': 33, 'verse': 73, 'text': 'لِّيُعَذِّبَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ وَيَتُوبَ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمَۢا\nАллоҳ мунофиқ ва мунофиқаларни, мушрик ва мушрикаларни азоблаш учун ҳамда мўмин ва мўминаларнинг тавба-тазарруларини қабул килиб, (гуноҳларини мағфират қилиш учун Одам болаларига бу омонатни юклади). Аллоҳ мағфират қилгувчи ва меҳрибон бўлган Зотдир'}, {'chapter': 34, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَلَهُ ٱلۡحَمۡدُ فِي ٱلۡأٓخِرَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ\n(Барча) ҳамду сано Аллоҳ учундир. У шундай Зотки, осмонлардаги ва Ердаги бор нарса Уникидир. Охиратдаги ҳамду сано ҳам ёлғиз Унинг учундир. У ҳикмат Эгаси ва (барча нарсадан) хабардордир'}, {'chapter': 34, 'verse': 2, 'text': 'يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۚ وَهُوَ ٱلرَّحِيمُ ٱلۡغَفُورُ\nУ ерга кирадиган (ёмғир-қор суви, хазинаю-дафина ва жасадлар каби) нарсаларни ҳам, ундан чиқадиган (ут-ўлан, наботот ва булоқ сувлари каби) нарсаларни ҳам, самодан тушадиган (ризқу рўз, хайру барака каби) нарсаларни ҳам, унга кўтариладиган (яхши амал ва дуо-илтижо каби) нарсаларни ҳам билур. У меҳрибон ва мағфиратлидир'}, {'chapter': 34, 'verse': 3, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَأۡتِينَا ٱلسَّاعَةُۖ قُلۡ بَلَىٰ وَرَبِّي لَتَأۡتِيَنَّكُمۡ عَٰلِمِ ٱلۡغَيۡبِۖ لَا يَعۡزُبُ عَنۡهُ مِثۡقَالُ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَلَآ أَصۡغَرُ مِن ذَٰلِكَ وَلَآ أَكۡبَرُ إِلَّا فِي كِتَٰبٖ مُّبِينٖ\nКофир бўлган кимсалар «Бизларга (Қиёмат) соати келмас», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Йўқ! Ғайбни билгувчи Парвардигоримга қасамки, шак-шубҳасиз, у (яъни, Қиёмат) сизларга келур. Осмонлар ва Ердаги бир зарра мисоличалик, ундан ҳам кичик (ёки) катта бирон нарса (Аллоҳ)дан маҳфий бўлмас — албатта очиқ Китобда (яъни, Аллоҳнинг азалий ёзмиши — Лавҳул-Маҳфузда у) мавжуд бўлур»'}, {'chapter': 34, 'verse': 4, 'text': 'لِّيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِۚ أُوْلَـٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ\nТоки (Аллоҳ) иймон келтирган ва яхши амаллар қилган зотларни мукофотлаши учун (шак-шубҳасиз, Қиёмат соати келур). Ана ўша зотлар учун мағфират ва улуғ ризқ (яъни, жаннат) бордир'}, {'chapter': 34, 'verse': 5, 'text': 'وَٱلَّذِينَ سَعَوۡ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٞ\nБизнинг оятларимизга (қарши) курашиш ҳаракатида юрган кимсалар учун эса аламли жазодан иборат азоб бордир'}, {'chapter': 34, 'verse': 6, 'text': 'وَيَرَى ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ ٱلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ هُوَ ٱلۡحَقَّ وَيَهۡدِيٓ إِلَىٰ صِرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), илм ато этилган зотлар сизга Парвардигорингиз томонидан нозил қилинган (Қуръоннинг) ҳақ (Китоб) эканини ва у қудратли, ҳамду санога лойиқ Зотнинг йўлига ҳидоят қилишини билурлар'}, {'chapter': 34, 'verse': 7, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ هَلۡ نَدُلُّكُمۡ عَلَىٰ رَجُلٖ يُنَبِّئُكُمۡ إِذَا مُزِّقۡتُمۡ كُلَّ مُمَزَّقٍ إِنَّكُمۡ لَفِي خَلۡقٖ جَدِيدٍ\nКофир бўлган кимсалар эса: «Сизларга (қабрларингизда чириб) титилиб бўлак-бўлак бўлиб кетган вақтингизда шак-шубҳасиз, яна янгитдан яратилурсизлар, деб хабар берадиган бир кишини кўрсатайликми'}, {'chapter': 34, 'verse': 8, 'text': 'أَفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةُۢۗ بَلِ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ فِي ٱلۡعَذَابِ وَٱلضَّلَٰلِ ٱلۡبَعِيدِ\nУ Аллоҳ шаънига ёлғон тўқиб олдими ёки жинни бўлиб қолдими?», дедилар. Йўқ, (Муҳаммад алайҳис-салоту вас-салом ёлғон тўқиб олгани ҳам, жинни бўлиб қолгани ҳам йўқ, балки) Охиратга ишонмайдиган кимсаларнинг ўзлари азобда ва (Ҳақ йўлдан йироқ) залолатдадирлар'}, {'chapter': 34, 'verse': 9, 'text': 'أَفَلَمۡ يَرَوۡاْ إِلَىٰ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِن نَّشَأۡ نَخۡسِفۡ بِهِمُ ٱلۡأَرۡضَ أَوۡ نُسۡقِطۡ عَلَيۡهِمۡ كِسَفٗا مِّنَ ٱلسَّمَآءِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّكُلِّ عَبۡدٖ مُّنِيبٖ\nАхир улар олдиларидаги ва ортларидаги (яъни, чор-атрофларидаги) нарсаларга — осмону заминга боқмайдиларми?! Агар Биз хоҳласак, уларни Ерга юттириб юборурмиз ёки осмонни устларига парча-парча қилиб туширурмиз. Албатта бунда ҳар бир (Аллоҳга) қайтгувчи банда учун оят-ибрат бордир'}, {'chapter': 34, 'verse': 10, 'text': '۞وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ مِنَّا فَضۡلٗاۖ يَٰجِبَالُ أَوِّبِي مَعَهُۥ وَٱلطَّيۡرَۖ وَأَلَنَّا لَهُ ٱلۡحَدِيدَ\nАниқки, Биз Довудга (улуғ) бир фазл-мартаба ато этдик ва дедик: «Эй тоғлар ва қушлар (Довуд) билан бирга тасбеҳ айтинглар!» Ва унинг учун темирни (хамирдек) юмшоқ қилиб қўйдик'}, {'chapter': 34, 'verse': 11, 'text': 'أَنِ ٱعۡمَلۡ سَٰبِغَٰتٖ وَقَدِّرۡ فِي ٱلسَّرۡدِۖ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ بَصِيرٞ\n(Ва унга дедик): «Совутлар ясагин ва (уларни) тўқишда аниқ-пухта иш қилгин! (Эй Довуд хонадони), яхши амал қилинглар! Зеро, Мен қилаётган амалларингизни кўриб тургувчидирман»'}, {'chapter': 34, 'verse': 12, 'text': 'وَلِسُلَيۡمَٰنَ ٱلرِّيحَ غُدُوُّهَا شَهۡرٞ وَرَوَاحُهَا شَهۡرٞۖ وَأَسَلۡنَا لَهُۥ عَيۡنَ ٱلۡقِطۡرِۖ وَمِنَ ٱلۡجِنِّ مَن يَعۡمَلُ بَيۡنَ يَدَيۡهِ بِإِذۡنِ رَبِّهِۦۖ وَمَن يَزِغۡ مِنۡهُمۡ عَنۡ أَمۡرِنَا نُذِقۡهُ مِنۡ عَذَابِ ٱلسَّعِيرِ\nСулаймонга эрталаб бир ойлик, кечки пайт бир ойлик (масофани босиб ўтадиган) шамолни (бўйсундирдик) ва унинг учун мис булоғини оқизиб қўйдик. Яна жинлардан ҳам Парвардигорининг изни-иродаси билан унинг қўл остида ишлайдиган кимсаларни (бўйсундириб қўйгандирмиз). Улардан ким Бизнинг амримиздан тойилса, Биз унга ўт-олов азобидан тотдириб қўюрмиз'}, {'chapter': 34, 'verse': 13, 'text': 'يَعۡمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَٰرِيبَ وَتَمَٰثِيلَ وَجِفَانٖ كَٱلۡجَوَابِ وَقُدُورٖ رَّاسِيَٰتٍۚ ٱعۡمَلُوٓاْ ءَالَ دَاوُۥدَ شُكۡرٗاۚ وَقَلِيلٞ مِّنۡ عِبَادِيَ ٱلشَّكُورُ\nУлар (Сулаймонга мисдан) у хоҳлаган нарсаларни — ибодатхоналарни, ҳайкалларни, ҳовузлар каби (катта) лаганларни ва (ўчоқларга) ўрнашган (вазмин) қозонларни қилиб берурлар. «(Эй) Довуд хонадони, (Аллоҳ сизларга ато этган бу неъматларнинг шукронаси учун амал-ибодат қилинглар! Бандаларим орасида шукр қилгувчи (зотлар жуда) оздир»'}, {'chapter': 34, 'verse': 14, 'text': 'فَلَمَّا قَضَيۡنَا عَلَيۡهِ ٱلۡمَوۡتَ مَا دَلَّهُمۡ عَلَىٰ مَوۡتِهِۦٓ إِلَّا دَآبَّةُ ٱلۡأَرۡضِ تَأۡكُلُ مِنسَأَتَهُۥۖ فَلَمَّا خَرَّ تَبَيَّنَتِ ٱلۡجِنُّ أَن لَّوۡ كَانُواْ يَعۡلَمُونَ ٱلۡغَيۡبَ مَا لَبِثُواْ فِي ٱلۡعَذَابِ ٱلۡمُهِينِ\nЭнди қачонки Биз (Сулаймонга) ўлимни ҳукм қилгач, унинг ўлимига фақат асосини еяётган (ёғоч) кемирувчи қуртгина дадолат қилди. Бас, қачонки у қулаб тушгач, жинларга аниқ бўлдики, агар улар ғайбни билгувчи бўлганларида бу хор қилгувчи азобда-меҳнатда қолмаган бўлур эдилар'}, {'chapter': 34, 'verse': 15, 'text': 'لَقَدۡ كَانَ لِسَبَإٖ فِي مَسۡكَنِهِمۡ ءَايَةٞۖ جَنَّتَانِ عَن يَمِينٖ وَشِمَالٖۖ كُلُواْ مِن رِّزۡقِ رَبِّكُمۡ وَٱشۡكُرُواْ لَهُۥۚ بَلۡدَةٞ طَيِّبَةٞ وَرَبٌّ غَفُورٞ\nАниқки, Сабаъ (қабиласи) учун ўз масканларида (Аллоҳнинг фазлу марҳаматига далолат қиладиган) бир аломат бор эди — ўнг томон ҳам, сўл томон ҳам боғ-роғ бўлиб, (Биз уларга) «Парвардигорингиз (берган) ризқу рўзидан баҳраманд бўлинглар ва У зотга шукр қилинглар! (Шаҳрингиз) покиза шаҳардир, (Парвардигорингиз) мағфират килгувчи Парвардигордир», (деган эдик)'}, {'chapter': 34, 'verse': 16, 'text': 'فَأَعۡرَضُواْ فَأَرۡسَلۡنَا عَلَيۡهِمۡ سَيۡلَ ٱلۡعَرِمِ وَبَدَّلۡنَٰهُم بِجَنَّتَيۡهِمۡ جَنَّتَيۡنِ ذَوَاتَيۡ أُكُلٍ خَمۡطٖ وَأَثۡلٖ وَشَيۡءٖ مِّن سِدۡرٖ قَلِيلٖ\nБас, улар (Бизга шукр қилишдан) юз ўгиришгач, Биз уларнинг устига тўғон билан (тўсиб қўйилган) селни (очиб) юбордик ва уларнинг боғларини аччиқ-тахир мевали, юлғунзор ва яккам-дуккам бутазор «боғлар»га алмаштириб қўйдик'}, {'chapter': 34, 'verse': 17, 'text': 'ذَٰلِكَ جَزَيۡنَٰهُم بِمَا كَفَرُواْۖ وَهَلۡ نُجَٰزِيٓ إِلَّا ٱلۡكَفُورَ\nКофир бўлганлари сабабли уларни мана шу (жазо билан) жазоладик. Биз фақат кофир бўлган кимсагагина (мана шундай) жазо берурмиз'}, {'chapter': 34, 'verse': 18, 'text': 'وَجَعَلۡنَا بَيۡنَهُمۡ وَبَيۡنَ ٱلۡقُرَى ٱلَّتِي بَٰرَكۡنَا فِيهَا قُرٗى ظَٰهِرَةٗ وَقَدَّرۡنَا فِيهَا ٱلسَّيۡرَۖ سِيرُواْ فِيهَا لَيَالِيَ وَأَيَّامًا ءَامِنِينَ\nБиз улар билан Ўзимиз баракотли қилган (Шом) қишлоқ-шаҳарларининг орасида кўриниб турадиган (яъни, бир-бирига яқин) қишлоқларни барпо қилиб (қўйган) ва улар орасида юриш (масофаси)ни ҳам ўлчаб-белгилаб (уларга): «Бу (қишлоқларда) кечалари ҳам, кундузлари ҳам тинч-омон юраверинглар», (деган эдик)'}, {'chapter': 34, 'verse': 19, 'text': 'فَقَالُواْ رَبَّنَا بَٰعِدۡ بَيۡنَ أَسۡفَارِنَا وَظَلَمُوٓاْ أَنفُسَهُمۡ فَجَعَلۡنَٰهُمۡ أَحَادِيثَ وَمَزَّقۡنَٰهُمۡ كُلَّ مُمَزَّقٍۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ\nУлар эса (бу неъматларга ношукурлик қилиб): «Парвардигоро, сафарларимиз орасидаги (масофани) йироқ қилгин», дейишиб, ўзларига зулм қилдилар, бас, Биз уларни (одамлар гапириб юрадиган) чўпчак-ҳикояга айлантириб қўйдик ва бўлак-бўлак қилиб (ҳар томонга тирқиратиб юбордик). Албатта бунда ҳар бир (балоларга) сабр-қаноат қилгувчи, (неъматларга) шукр қилгувчи учун оят-ибратлар бордир'}, {'chapter': 34, 'verse': 20, 'text': 'وَلَقَدۡ صَدَّقَ عَلَيۡهِمۡ إِبۡلِيسُ ظَنَّهُۥ فَٱتَّبَعُوهُ إِلَّا فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ\nДарҳақиқат, Иблис уларнинг устидаги («Мен уларни йўлдан оздира олурман», деган) гумонини рўёбга чиқарди, бас, (Сабаъ қабиласи иблисга эргашдилар), фақат бир гуруҳ мўминларгина (унга бўйинсунмадилар)'}, {'chapter': 34, 'verse': 21, 'text': 'وَمَا كَانَ لَهُۥ عَلَيۡهِم مِّن سُلۡطَٰنٍ إِلَّا لِنَعۡلَمَ مَن يُؤۡمِنُ بِٱلۡأٓخِرَةِ مِمَّنۡ هُوَ مِنۡهَا فِي شَكّٖۗ وَرَبُّكَ عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ\n(Иблиснинг) улар устидаги ҳукмронлиги — фақат Биз Охиратга иймон келтирадиган кишиларни, ундан (яъни, Охиратдан) шак-шубҳада турган кимсалардан билиб-ажратиб олишимиз учун бўлгандир. (Яъни, Охират ҳақида шубҳа қилгувчи кимсалар иблиснинг иғвосига учиб, унга эргашурлар ва Аллоҳнинг неъматларига ношукурлик қилурлар. Охиратга иймон келтирган зотларга эса унинг васвасаси кор қилмас.) Парвардигорингиз барча нарсанинг устида кузатиб тургувчидир'}, {'chapter': 34, 'verse': 22, 'text': 'قُلِ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُم مِّن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ مِثۡقَالَ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَمَا لَهُمۡ فِيهِمَا مِن شِرۡكٖ وَمَا لَهُۥ مِنۡهُم مِّن ظَهِيرٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Сизлар Аллоҳдан ўзга (илоҳлар) деб гумон қилган бутларингизга дуо-илтижо қилаверинглар-чи, (улар сизларга ижобат қила олармиканлар)! Улар на осмонларда ва на Ерда бир зарра вазнича (нарса)га эга эмасдирлар ва улар учун (осмонлар ва Ерда Аллоҳга) шериклик ҳам йўқдир ҳамда (Аллоҳ) учун улардан бирон ёрдамчи ҳам йўқдир'}, {'chapter': 34, 'verse': 23, 'text': 'وَلَا تَنفَعُ ٱلشَّفَٰعَةُ عِندَهُۥٓ إِلَّا لِمَنۡ أَذِنَ لَهُۥۚ حَتَّىٰٓ إِذَا فُزِّعَ عَن قُلُوبِهِمۡ قَالُواْ مَاذَا قَالَ رَبُّكُمۡۖ قَالُواْ ٱلۡحَقَّۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ\n(Аллоҳ) ҳузурида фақат Унинг Ўзи изн берган кишиларгагина шафоат-оқлов фойда берур (яъни, ана ўша зотларгина шафоатга эришурлар, ё ўзгаларни шафоат қила олурлар). То қачон (Парвардигор улар учун шафоатга изн бериши билан) дилларидан қўрқув кетказилгач, улар (бир-бирларига): «Парвардигорингиз нима деди (яъни, шафоатга ҳақиқатан изн бердими?)», деб (савол қилишиб), «Ҳақни (айтди, яъни ҳақиқатан шафоатга изн берди), У юксак ва буюк Зотдир», дейишиб (жавоб қилурлар)'}, {'chapter': 34, 'verse': 24, 'text': '۞قُلۡ مَن يَرۡزُقُكُم مِّنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُلِ ٱللَّهُۖ وَإِنَّآ أَوۡ إِيَّاكُمۡ لَعَلَىٰ هُدًى أَوۡ فِي ضَلَٰلٖ مُّبِينٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Ким сизларга осмонлар ва Ердан ризқу рўз берур?» Айтинг: «Аллоҳ (ризқ берур). Шак-шубҳасиз, ё бизлар ёки сизлар (яъни, икки тоифадан бири) аниқ ҳидоят устида ёки очиқ залолатдадир»'}, {'chapter': 34, 'verse': 25, 'text': 'قُل لَّا تُسۡـَٔلُونَ عَمَّآ أَجۡرَمۡنَا وَلَا نُسۡـَٔلُ عَمَّا تَعۡمَلُونَ\nАйтинг: «Сизлар бизлар қилган гуноҳ тўғрисида масъул бўлмассизлар ва бизлар сизлар қилаётган амаллар тўғрисида масъул бўлмасмиз»'}, {'chapter': 34, 'verse': 26, 'text': 'قُلۡ يَجۡمَعُ بَيۡنَنَا رَبُّنَا ثُمَّ يَفۡتَحُ بَيۡنَنَا بِٱلۡحَقِّ وَهُوَ ٱلۡفَتَّاحُ ٱلۡعَلِيمُ\nАйтинг: «Парвардигоримиз (Қиёмат Кунида) ўртамизни жамлар, сўнгра ўртамизда ҳақиқат — адолат билан ҳукм қилур. Унинг Ўзигина (адолат билан) ҳукм қилгувчи ва (ким ҳақ, ким ноҳақ эканини) билгувчидир»'}, {'chapter': 34, 'verse': 27, 'text': 'قُلۡ أَرُونِيَ ٱلَّذِينَ أَلۡحَقۡتُم بِهِۦ شُرَكَآءَۖ كَلَّاۚ بَلۡ هُوَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАйтинг: «(Эй мушриклар), сизлар менга (ўзларингизча Аллоҳга) қўшиб олган бутларингизни (ҳақиқатан У зотга) шериклар эканини кўрсатингиз-чи?! Йўқ, (бу гумонларингиз хатодир). Балки (ёлғиз) Аллоҳнинг Ўзигина қудрат ва ҳикмат Эгасидир'}, {'chapter': 34, 'verse': 28, 'text': 'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا كَآفَّةٗ لِّلنَّاسِ بَشِيرٗا وَنَذِيرٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизни шак-шубҳасиз, барча одамларга — (мўминларга жаннат ҳақида) хушхабар элтгувчи, (кофирларни эса дўзах азобидан) огоҳлантиргувчи бўлган ҳолингизда, пайғамбар қилиб юбордик. Лекин кўп одамлар билмаслар'}, {'chapter': 34, 'verse': 29, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангизлар (айтинглар-чи), мана шу ваъда (қилинган Қиёмат) қачон бўлади?», дерлар'}, {'chapter': 34, 'verse': 30, 'text': 'قُل لَّكُم مِّيعَادُ يَوۡمٖ لَّا تَسۡتَـٔۡخِرُونَ عَنۡهُ سَاعَةٗ وَلَا تَسۡتَقۡدِمُونَ\nАйтинг: «Сизлар учун шундай бир куннинг ваъдаси бордирки, сизлар ундан бирон соат кетга ҳам, илгарига ҳам сура олмассизлар»'}, {'chapter': 34, 'verse': 31, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَن نُّؤۡمِنَ بِهَٰذَا ٱلۡقُرۡءَانِ وَلَا بِٱلَّذِي بَيۡنَ يَدَيۡهِۗ وَلَوۡ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ مَوۡقُوفُونَ عِندَ رَبِّهِمۡ يَرۡجِعُ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ ٱلۡقَوۡلَ يَقُولُ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ لَوۡلَآ أَنتُمۡ لَكُنَّا مُؤۡمِنِينَ\nКофир бўлган кимсалар: «Бизлар бу Қуръонга ҳам, ундан олдинги (китобларга) ҳам ҳаргиз иймон келтирмаймиз», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом), агар сиз ўша золим кимсаларни (Қиёмат Кунида) Парвардигор ҳузурида турғизуб қўйилиб, бир-бирларига гап қайтараётганларини кўрсангиз эди. (Ўшанда) бечора саналган (яъни, эргашувчи бўлган) кимсалар, мутакаббир кимсаларга (яъни, ўз пешволарига): «Агар сизлар бўлмаганингизда, бизлар шак-шубҳасиз, мўминлар бўлур эдик», десалар'}, {'chapter': 34, 'verse': 32, 'text': 'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ لِلَّذِينَ ٱسۡتُضۡعِفُوٓاْ أَنَحۡنُ صَدَدۡنَٰكُمۡ عَنِ ٱلۡهُدَىٰ بَعۡدَ إِذۡ جَآءَكُمۖ بَلۡ كُنتُم مُّجۡرِمِينَ\nМутакаббир кимсалар бечора саналган кимсаларга: «Сизларга ҳидоят келганидан сўнг сизларни (ундан) биз тўсдикми? Йўқ, ўзларингиз жиноят қилгувчи бўлдингизлар», дерлар'}, {'chapter': 34, 'verse': 33, 'text': 'وَقَالَ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ بَلۡ مَكۡرُ ٱلَّيۡلِ وَٱلنَّهَارِ إِذۡ تَأۡمُرُونَنَآ أَن نَّكۡفُرَ بِٱللَّهِ وَنَجۡعَلَ لَهُۥٓ أَندَادٗاۚ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۚ وَجَعَلۡنَا ٱلۡأَغۡلَٰلَ فِيٓ أَعۡنَاقِ ٱلَّذِينَ كَفَرُواْۖ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ\nБечора саналган кимсалар эса мутакаббир кимсаларга: «Йўқ, кеча-ю кундуз қилган макр-ҳийла(ларингиз бизларни мўмин бўлишдан тўсган эди). Ўшанда сизлар (ўз макр-ҳийлаларингиз билан) бизларни Аллоҳга кофир бўлишга ва У зотга (ўзгаларни) тенг шерик қилишга зўрлар эдингизлар», дедилар. Азобни (ўз кўзлари билан) кўрган вақтларида улар (яъни, пешволар ҳам, эргашганлар ҳам) ичларида надомат қилдилар ва Биз кофир бўлган кимсаларнинг бўйинларига кишанлар солдик. Уларга фақат ўзлари (ҳаёти дунёда) қилиб ўтган қилмишларининг жазоси берилур'}, {'chapter': 34, 'verse': 34, 'text': 'وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَآ إِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ\nБиз қайси бир қишлоқ-шаҳарга бирон огоҳлантиргувчи (пайғамбар) юбормайлик, албатта у жойнинг боёнлари «Аниқки, биз сизлар элчи қилиб юборилган нарсага кофирдирмиз», дедилар'}, {'chapter': 34, 'verse': 35, 'text': 'وَقَالُواْ نَحۡنُ أَكۡثَرُ أَمۡوَٰلٗا وَأَوۡلَٰدٗا وَمَا نَحۡنُ بِمُعَذَّبِينَ\nЯна улар: «Бизларнинг мол-дунё ва болаларимиз — иймон келтирганлардан кўра кўпроқ, (бинобарин, бизлар Аллоҳ наздида улардан обрўлироқмиз ва) бизлар азоблангувчи эмасдирмиз», дедилар'}, {'chapter': 34, 'verse': 36, 'text': 'قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Албатта Парвардигорим Ўзи хоҳлаган кишиларнинг ризқини кенг қилур ва (Ўзи хоҳлаган кишиларнинг ризқини) танг қилур. Лекин кўп одамлар (буни) билмаслар»'}, {'chapter': 34, 'verse': 37, 'text': 'وَمَآ أَمۡوَٰلُكُمۡ وَلَآ أَوۡلَٰدُكُم بِٱلَّتِي تُقَرِّبُكُمۡ عِندَنَا زُلۡفَىٰٓ إِلَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَـٰٓئِكَ لَهُمۡ جَزَآءُ ٱلضِّعۡفِ بِمَا عَمِلُواْ وَهُمۡ فِي ٱلۡغُرُفَٰتِ ءَامِنُونَ\nНа молу дунёларингиз ва на болаларингиз сизларни Бизнинг даргоҳимизга яқин қилгувчи эмасдир. Фақат иймон келтирган ва яхши амал қилган зотлар — ана ўшалар учунгина қилган амаллари сабабли неча баробар мукофот бўлур ва улар (жаннатдаги) юксак манзилларда тинч-омон бўлурлар'}, {'chapter': 34, 'verse': 38, 'text': 'وَٱلَّذِينَ يَسۡعَوۡنَ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ\nБизнинг оятларимизга (қарши) курашиш ҳаракатида юрадиган кимсалар — улар азобга дучор қилингувчидирлар'}, {'chapter': 34, 'verse': 39, 'text': 'قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥۚ وَمَآ أَنفَقۡتُم مِّن شَيۡءٖ فَهُوَ يُخۡلِفُهُۥۖ وَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ\nАйтинг: «Албатта Парвардигорим бандаларидан Ўзи хоҳлаган кишиларнинг ризқини кенг қилур ва (Ўзи хоҳлаган кишиларнинг ризқини) танг қилур. Не бир нарсани инфоқ-эҳсон қилсангизлар, бас, (Аллоҳ) унинг ўрнини тўлдирур. У энг яхши ризқ бергувчидир'}, {'chapter': 34, 'verse': 40, 'text': 'وَيَوۡمَ يَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ يَقُولُ لِلۡمَلَـٰٓئِكَةِ أَهَـٰٓؤُلَآءِ إِيَّاكُمۡ كَانُواْ يَعۡبُدُونَ\n(Эсланг, Аллоҳ) уларнинг (яъни, мушрикларнинг) барчаларини тўплаб, сўнгра фаришталарга «Ана у (мушриклар) сизларга ибодат қилгувчи бўлганмидилар?», дейилган кунда'}, {'chapter': 34, 'verse': 41, 'text': 'قَالُواْ سُبۡحَٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِمۖ بَلۡ كَانُواْ يَعۡبُدُونَ ٱلۡجِنَّۖ أَكۡثَرُهُم بِهِم مُّؤۡمِنُونَ\n(Фаришталар): «Пок Парвардигор, Сен Ўзинг бизларнинг дўстимиздирсан, улар эмас. Йўқ, улар жинларга ибодат-итоат қилар эдилар. Уларнинг кўплари (жинларга) иймон келтиргувчидирлар», дерлар'}, {'chapter': 34, 'verse': 42, 'text': 'فَٱلۡيَوۡمَ لَا يَمۡلِكُ بَعۡضُكُمۡ لِبَعۡضٖ نَّفۡعٗا وَلَا ضَرّٗا وَنَقُولُ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّتِي كُنتُم بِهَا تُكَذِّبُونَ\nЭнди бу кунда бирингиз бирингизга на фойда ва на зиён етказишга қодир бўлмас ва Биз золим бўлган кимсаларга: «Ўзларингиз ёлғон деган дўзах азобини тотиб кўрингиз», дермиз'}, {'chapter': 34, 'verse': 43, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا رَجُلٞ يُرِيدُ أَن يَصُدَّكُمۡ عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُكُمۡ وَقَالُواْ مَا هَٰذَآ إِلَّآ إِفۡكٞ مُّفۡتَرٗىۚ وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ\nҚачон уларга Бизнинг аниқ-равшан бўлган оятларимиз тиловат қилинса, улар: «Бу (яъни, Муҳаммад алайҳис-салоту вас-салом) фақат сизларни ота-боболарингиз ибодат қилиб ўтган бутлардан тўсмоқчи бўлган кимсадир», дерлар ва «Бу (яъни, Қуръон) фақат тўқилган бир бўҳтондир», дерлар. Кофир бўлган кимсалар уларга Ҳақиқат — Қуръон келган вақтида у ҳақда «Бу фақат очиқ сеҳрдир», дедилар'}, {'chapter': 34, 'verse': 44, 'text': 'وَمَآ ءَاتَيۡنَٰهُم مِّن كُتُبٖ يَدۡرُسُونَهَاۖ وَمَآ أَرۡسَلۡنَآ إِلَيۡهِمۡ قَبۡلَكَ مِن نَّذِيرٖ\nҲолбуки, (илгари) Биз уларга ўқиб-ўрганадиган китоблар ато этган эмасмиз ва уларга сиздан аввал бирон огоҳлантиргувчи (пайғамбар) ҳам юборган эмасмиз'}, {'chapter': 34, 'verse': 45, 'text': 'وَكَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَمَا بَلَغُواْ مِعۡشَارَ مَآ ءَاتَيۡنَٰهُمۡ فَكَذَّبُواْ رُسُلِيۖ فَكَيۡفَ كَانَ نَكِيرِ\nУлардан илгари (ўтган) кимсалар ҳам (ўз пайғамбарларини) ёлғончи қилган эдилар — Ҳолбуки, (Макка мушриклари) Биз уларга ато этган неъматларнинг ўндан бирига ҳам етганлари йўқ. — Бас, ўшалар Менинг пайғамбарларимни ёлғончи қилишгач, Менинг инкорим қандай бўлган эди'}, {'chapter': 34, 'verse': 46, 'text': '۞قُلۡ إِنَّمَآ أَعِظُكُم بِوَٰحِدَةٍۖ أَن تَقُومُواْ لِلَّهِ مَثۡنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُواْۚ مَا بِصَاحِبِكُم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ لَّكُم بَيۡنَ يَدَيۡ عَذَابٖ شَدِيدٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Мен сизларга фақат биргина нарсани — Аллоҳ учун жуфт-жуфт, ёлғиз-ёлғиз ҳолингизда туриб, сўнгра (Муҳаммад алайҳис-салоту вас-салом ва у келтирган Китоб ҳақида) тафаккур қилишингизни тавсия қилурман. Соҳибингизда (яъни, Муҳаммад алайҳис-салоту вас-саломда) ҳеч қандай жиннилик йўқдир. У фақат қаттиқ азоб олдидан сизларга (юборилган) бир огоҳлантиргувчи (пайғамбардир), холос»'}, {'chapter': 34, 'verse': 47, 'text': 'قُلۡ مَا سَأَلۡتُكُم مِّنۡ أَجۡرٖ فَهُوَ لَكُمۡۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٞ\nАйтинг: «Мен сизлардан сўраган бор ажру-мукофот ўзларингизга бўлсин (яъни, мен сизлардан бирон ажр-мукофот сўрамасман). Менинг ажр-мукофотим ёлғиз Аллоҳнинг зиммасидадир. У барча нарсага гувоҳдир»'}, {'chapter': 34, 'verse': 48, 'text': 'قُلۡ إِنَّ رَبِّي يَقۡذِفُ بِٱلۡحَقِّ عَلَّـٰمُ ٱلۡغُيُوبِ\nАйтинг: «Албатта Парвардигорим Ҳақ — Қуръонни (ботил-жаҳолат устига) отур. У ғайбларни аниқ билгувчидир»'}, {'chapter': 34, 'verse': 49, 'text': 'قُلۡ جَآءَ ٱلۡحَقُّ وَمَا يُبۡدِئُ ٱلۡبَٰطِلُ وَمَا يُعِيدُ\nАйтинг: «Ҳақ — Қуръон келди (ва ботил-динсизлик йўқ бўлди. Энди) ботил (янги динсизликни) бошлай ҳам олмас, (эски динсизликни) қайтара ҳам олмас'}, {'chapter': 34, 'verse': 50, 'text': 'قُلۡ إِن ضَلَلۡتُ فَإِنَّمَآ أَضِلُّ عَلَىٰ نَفۡسِيۖ وَإِنِ ٱهۡتَدَيۡتُ فَبِمَا يُوحِيٓ إِلَيَّ رَبِّيٓۚ إِنَّهُۥ سَمِيعٞ قَرِيبٞ\nАйтинг: «Агар мен (Ҳақ йўлидан) озсам, бас, фақат ўз зиёнимга озурман ва агар (Унинг йўлига) ҳидоят топсам, у ҳолда Парвардигорим менга ваҳий қилаётган (Қуръон) сабаблигина (ҳидоят топурман). Шак-шубҳасиз, У эшитгувчидир, яқиндир»'}, {'chapter': 34, 'verse': 51, 'text': 'وَلَوۡ تَرَىٰٓ إِذۡ فَزِعُواْ فَلَا فَوۡتَ وَأُخِذُواْ مِن مَّكَانٖ قَرِيبٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар сиз (мушрик-кофирларни) даҳшатга тушиб, қочгани жой топа олмай қолган ва яқин бир макондан (дўзахга ташлаш учун) тутиб кетилган пайтларида кўрсангиз эди'}, {'chapter': 34, 'verse': 52, 'text': 'وَقَالُوٓاْ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانِۭ بَعِيدٖ\n(Ўша кунда) улар: «(Муҳаммадга) иймон келтирдик», дедилар. (Улар агар ҳаёти дунёда иймон келтирганларида қабул қилинган бўлур эди, аммо энди) йироқ бир макондан (Охират диёридан туриб иймон ва тавба-тазарруъга) қўллари қандоқ етсин'}, {'chapter': 34, 'verse': 53, 'text': 'وَقَدۡ كَفَرُواْ بِهِۦ مِن قَبۡلُۖ وَيَقۡذِفُونَ بِٱلۡغَيۡبِ مِن مَّكَانِۭ بَعِيدٖ\nҲолбуки, улар илгари (ҳаёти дунёда) унга кофир бўлган эдилар! Ҳолбуки, улар йироқ бир макондан ғайбга (Охиратга тош) отар эдилар'}, {'chapter': 34, 'verse': 54, 'text': 'وَحِيلَ بَيۡنَهُمۡ وَبَيۡنَ مَا يَشۡتَهُونَ كَمَا فُعِلَ بِأَشۡيَاعِهِم مِّن قَبۡلُۚ إِنَّهُمۡ كَانُواْ فِي شَكّٖ مُّرِيبِۭ\n(Мана энди) худди улардан илгари (ўтган) ҳаммаслакларига қилингани каби, улар билан ўзлари истайдиган нарсанинг (яъни, иймон келтириб, дўзах азобидан қутулиб қолишнинг) ўртаси тўсиб қўйилди. Зеро, улар (ҳаёти дунёдалик пайтларида) шак-шубҳа остида эдилар'}, {'chapter': 35, 'verse': 1, 'text': 'ٱلۡحَمۡدُ لِلَّهِ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ جَاعِلِ ٱلۡمَلَـٰٓئِكَةِ رُسُلًا أُوْلِيٓ أَجۡنِحَةٖ مَّثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۚ يَزِيدُ فِي ٱلۡخَلۡقِ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nҲамду-сано осмонлар ва Ерни илк яратгувчи ҳамда (Ўзи билан бандалари ўртасида) фаришталарни икки, уч, тўрт қанотли элчилар (воситачилар) қилгувчи Аллоҳ учундир. У зот (яратган) маҳлуқотида Ўзи хоҳлаган нарсани зиёда қилур. Албатта Аллоҳ барча нарсага қодирдир'}, {'chapter': 35, 'verse': 2, 'text': 'مَّا يَفۡتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحۡمَةٖ فَلَا مُمۡسِكَ لَهَاۖ وَمَا يُمۡسِكۡ فَلَا مُرۡسِلَ لَهُۥ مِنۢ بَعۡدِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nАллоҳ одамлар учун не бир раҳмат-марҳаматни очиб қўйса, бас, уни ушлаб-тўсиб қолгувчи бўлмас ва нени ушлаб қолса, бас, У зот (ушлаб қолгани)дан сўнг у (нарсани бандаларга) бирон юборгувчи бўлмас. У қудрат ва ҳикмат Эгасидир'}, {'chapter': 35, 'verse': 3, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡۚ هَلۡ مِنۡ خَٰلِقٍ غَيۡرُ ٱللَّهِ يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ\nЭй инсонлар, сизларга Аллоҳ ато этган сон-саноқсиз неъматни эслангиз! Сизларга осмону заминдан ризқу рўз берадиган Аллоҳдан ўзга биронта яратгувчи борми?! Ҳеч бир илоҳ йўқ, магар Унинг Ўзигина бордир. Бас, қаёққа бурилиб кетмоқдасизлар'}, {'chapter': 35, 'verse': 4, 'text': 'وَإِن يُكَذِّبُوكَ فَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар (мушриклар) сизни ёлғончи қилсалар, бас, (билингки), сиздан аввалги пайғамбарлар ҳам ёлғончи қилингандирлар. (Барча) ишлар ёлғиз Аллоҳга қайтарилур'}, {'chapter': 35, 'verse': 5, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ\nЭй инсонлар, албатта Аллоҳнинг (қайта тирилтириш ва бу дунёда қилиб ўтилган яхши-ёмон амалларнинг мукофот жазосини бериш тўғрисидаги) ваъдаси ҳақ (ваъдадир). Бас, ҳаргиз сизларни ҳаёти дунё алдаб қўймасин! Ва ҳаргиз сизларни Аллоҳ (барча гуноҳларни кечиб юбораверади, деб) алдагувчи (шайтон) алдаб қўймасин'}, {'chapter': 35, 'verse': 6, 'text': 'إِنَّ ٱلشَّيۡطَٰنَ لَكُمۡ عَدُوّٞ فَٱتَّخِذُوهُ عَدُوًّاۚ إِنَّمَا يَدۡعُواْ حِزۡبَهُۥ لِيَكُونُواْ مِنۡ أَصۡحَٰبِ ٱلسَّعِيرِ\nАниқки, шайтон сизларга душмандир, бас, уни душман тутинглар! Шак-шубҳасиз, у, ўз фирқасини (яъни, ўзига эргашган кимсаларни) дўзах эгалари бўлишлари учун даъват қилур'}, {'chapter': 35, 'verse': 7, 'text': 'ٱلَّذِينَ كَفَرُواْ لَهُمۡ عَذَابٞ شَدِيدٞۖ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٌ\nКофир бўлган кимсалар учун қаттиқ азоб бордир. Иймон келтирган ва яхши амаллар қилган зотлар учун эса мағфират ва улуғ мукофот бордир'}, {'chapter': 35, 'verse': 8, 'text': 'أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُ حَسَنٗاۖ فَإِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۖ فَلَا تَذۡهَبۡ نَفۡسُكَ عَلَيۡهِمۡ حَسَرَٰتٍۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَصۡنَعُونَ\nАхир (қилган) ёмон амали ўзига чиройли кўриниб, уни гўзал (амал) деб ўйлаган кимса (Аллоҳ ҳақ йўлга ҳидоят қилган зот каби бўлурми)?! Зотан, Аллоҳ Ўзи хоҳлаган кимсаларни йўлдан оздирур ва Ўзи хоҳлаган кишиларни ҳидоят қилур. Бас, (эй Муҳаммад алайҳис-салоту вас-салом), жонингиз уларнинг (иймон келтирмаганлари) устида ҳасрат-надоматлар чекиб кетмасин. Албатта Аллоҳ уларнинг қилаётган ҳунарларини билгувчидир'}, {'chapter': 35, 'verse': 9, 'text': 'وَٱللَّهُ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَسُقۡنَٰهُ إِلَىٰ بَلَدٖ مَّيِّتٖ فَأَحۡيَيۡنَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ كَذَٰلِكَ ٱلنُّشُورُ\nАллоҳнинг Ўзи шамолларни юборди. Бас, улар булутни қўзғатгач, Биз уни ўлик шаҳарга ҳайдаб, унинг ёрдамида ўлган ерни тирилтирдик. (Қиёмат қойим бўлган куни) қайта тирилиш ҳам шунинг ўзидир'}, {'chapter': 35, 'verse': 10, 'text': 'مَن كَانَ يُرِيدُ ٱلۡعِزَّةَ فَلِلَّهِ ٱلۡعِزَّةُ جَمِيعًاۚ إِلَيۡهِ يَصۡعَدُ ٱلۡكَلِمُ ٱلطَّيِّبُ وَٱلۡعَمَلُ ٱلصَّـٰلِحُ يَرۡفَعُهُۥۚ وَٱلَّذِينَ يَمۡكُرُونَ ٱلسَّيِّـَٔاتِ لَهُمۡ عَذَابٞ شَدِيدٞۖ وَمَكۡرُ أُوْلَـٰٓئِكَ هُوَ يَبُورُ\nКим куч-қудратни истайдиган бўлса, бас, барча куч-қудрат Аллоҳникидир. (Ҳар бир) Хуш Сўз Унга юксалур ва яхши амални ҳам (Аллоҳ Ўз даргоҳига) кўтарур. Ёмон макр-ҳийлалар қиладиган кимсалар учун эса қаттиқ азоб бордир ва уларнинг макрлари ҳам ҳалок бўлур (яъни, беҳуда кетур)'}, {'chapter': 35, 'verse': 11, 'text': 'وَٱللَّهُ خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ جَعَلَكُمۡ أَزۡوَٰجٗاۚ وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَمَا يُعَمَّرُ مِن مُّعَمَّرٖ وَلَا يُنقَصُ مِنۡ عُمُرِهِۦٓ إِلَّا فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nАллоҳ сизларни (аввал) тупроқдан, сўнгра нутфадан яратиб, кейин сизларни жуфт-жуфт (яъни, эркак-аёл) қилиб қўйди. Ҳар бир аёлнинг ҳомиладор бўлиши ҳам, кўзи ёриши ҳам шак-шубҳасиз, Унинг билиши — огоҳлиги билан бўлур. Ҳар бир умр кўргувчининг умри узун қилинмас ва (ё) умридан камайтирилмас, магар (буларнинг барчаси) Китобда (яъни, Лавҳул-Маҳфузда битилган бўлур). Албатта бу Аллоҳга осондир'}, {'chapter': 35, 'verse': 12, 'text': 'وَمَا يَسۡتَوِي ٱلۡبَحۡرَانِ هَٰذَا عَذۡبٞ فُرَاتٞ سَآئِغٞ شَرَابُهُۥ وَهَٰذَا مِلۡحٌ أُجَاجٞۖ وَمِن كُلّٖ تَأۡكُلُونَ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُونَ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ فِيهِ مَوَاخِرَ لِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nДарё-денгиз тенг бўлмас — буниси ширин-тотли ва ичилиши осон, буниси эса шўр-тахирдир. Сизлар (уларнинг) ҳар биридан янги гўшт (яъни, балиқ тутиб) ейсизлар ва тақадиган зеб-зийнат чиқариб олурсизлар. Ва сизлар (Аллоҳнинг) фазлу-марҳаматидан (яъни, ризқу рўзидан) исташларингиз учун (денгиз ва дарёларда сувни) ёриб кетаётган кемаларни кўрурсизлар. Шояд шукр қилсангизлар'}, {'chapter': 35, 'verse': 13, 'text': 'يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۚ وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ مَا يَمۡلِكُونَ مِن قِطۡمِيرٍ\n(Аллоҳ) кечани кундузга киритур ва кундузни кечага киритур, У қуёш ва ойни ҳам (сизларнинг манфаатингиз учун) бўйинсундириб қўйгандир — ҳар бири муайян муддатгача (яъни, Қиёмат Кунигача ўз фазосида) жорий бўлур. Ана шу Аллоҳ сизларнинг Парвардигорингиздирки, (барча оламларга) подшоҳлик ёлғиз Уникидир. (Эй мушриклар), сизлар У зотни қўйиб илтижо қилаётган бутларингиз эса пўстлоқча нарсага ҳам эга эмасдирлар'}, {'chapter': 35, 'verse': 14, 'text': 'إِن تَدۡعُوهُمۡ لَا يَسۡمَعُواْ دُعَآءَكُمۡ وَلَوۡ سَمِعُواْ مَا ٱسۡتَجَابُواْ لَكُمۡۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُونَ بِشِرۡكِكُمۡۚ وَلَا يُنَبِّئُكَ مِثۡلُ خَبِيرٖ\nАгар сизлар уларни чорласангизлар, дуоларингизни эшитмаслар. Агар эшитсалар-да, сизларга жавоб қила олмаслар ва Қиёмат Кунида уларни (Аллоҳга) шерик қилиб олганларингизни инкор қилурлар. (Эй Муҳаммад алайҳис-салоту вас-салом, дунё-ю Охират ҳақида ҳеч ким) сизга хабардор Зот (яъни, Аллоҳ) каби хабар бера олмас'}, {'chapter': 35, 'verse': 15, 'text': '۞يَـٰٓأَيُّهَا ٱلنَّاسُ أَنتُمُ ٱلۡفُقَرَآءُ إِلَى ٱللَّهِۖ وَٱللَّهُ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nЭй инсонлар, сизлар Аллоҳга муҳтождирсизлар. Аллоҳнинг Ўзигина (барча одамлардан) беҳожат ва (барча) мақтовга лойиқ Зотдир'}, {'chapter': 35, 'verse': 16, 'text': 'إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ\nАгар У хоҳласа, сизларни (Ер юзидан) кетказиб, (ўрнингизга) янги бир халқни келтирур'}, {'chapter': 35, 'verse': 17, 'text': 'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ\nВа бу (иш) Аллоҳга қийин эмасдир'}, {'chapter': 35, 'verse': 18, 'text': 'وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ وَإِن تَدۡعُ مُثۡقَلَةٌ إِلَىٰ حِمۡلِهَا لَا يُحۡمَلۡ مِنۡهُ شَيۡءٞ وَلَوۡ كَانَ ذَا قُرۡبَىٰٓۗ إِنَّمَا تُنذِرُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ وَأَقَامُواْ ٱلصَّلَوٰةَۚ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفۡسِهِۦۚ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ\n(Қиёмат Кунида) ҳеч бир кўтаргувчи (яъни, гуноҳкор жон) ўзга жоннинг юкини (яъни, гуноҳини) кўтармас. Агар оғир гуноҳ эгаси бўлган жон (бировни) ўз юки-гуноҳига (яъни, гуноҳининг бир қисмини кўтаришга) чорласа, (гарчи чорлангувчи) яқин қариндоши бўлса-да, у (гуноҳдан) бирон нарса кўтарилмас (яъни, у кунда ҳар ким ўз амали билан машғул бўлиб, ота болага, ака укага қарашга имкон топмас). (Эй Муҳаммад алайҳис-салоту вас-салом), сиз фақат ғайбдаги (яъни, кўзларига кўринмайдиган) Парвардигордан қўрқадиган ва намозни тўкис адо этган зотларнигина огоҳлантира олурсиз. Ким покдоман бўлса, у фақат ўз (фойдаси) учун покдоман бўлур. Ёлғиз Аллоҳгагина қайтилур'}, {'chapter': 35, 'verse': 19, 'text': 'وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ\nКўр кимса (кофир) билан кўргувчи зот (мўмин) баробар эмасдир'}, {'chapter': 35, 'verse': 20, 'text': 'وَلَا ٱلظُّلُمَٰتُ وَلَا ٱلنُّورُ\nЗулматлар (куфр) билан нур (иймон)'}, {'chapter': 35, 'verse': 21, 'text': 'وَلَا ٱلظِّلُّ وَلَا ٱلۡحَرُورُ\nСоя (жаннат) билан жазирама (дўзах) баробар эмасдир'}, {'chapter': 35, 'verse': 22, 'text': 'وَمَا يَسۡتَوِي ٱلۡأَحۡيَآءُ وَلَا ٱلۡأَمۡوَٰتُۚ إِنَّ ٱللَّهَ يُسۡمِعُ مَن يَشَآءُۖ وَمَآ أَنتَ بِمُسۡمِعٖ مَّن فِي ٱلۡقُبُورِ\nТириклар (мўминлар) билан — ўликлар (кофирлар) ҳам баробар эмасдир. Албатта Аллоҳ Ўзи хоҳлаган кишиларга (ҳақ даъватни) эшиттирур. (Эй Муҳаммад алайҳис-салоту вас-салом), сиз қабрлардаги кимсаларга (яъни, ўлик дилларига) эшиттира олгувчи эмасдирсиз'}, {'chapter': 35, 'verse': 23, 'text': 'إِنۡ أَنتَ إِلَّا نَذِيرٌ\nСиз фақат огоҳлантиргувчидирсиз'}, {'chapter': 35, 'verse': 24, 'text': 'إِنَّآ أَرۡسَلۡنَٰكَ بِٱلۡحَقِّ بَشِيرٗا وَنَذِيرٗاۚ وَإِن مِّنۡ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٞ\nАлбатта Биз сизни Ҳақ (дин) билан, хушхабар элтгувчи ва огоҳлантиргувчи қилиб юбордик. (Сизнинг умматингиздан аввалги) ҳар бир уммат ичида ҳам албатта бир огоҳлантиргувчи — пайғамбар ўтгандир'}, {'chapter': 35, 'verse': 25, 'text': 'وَإِن يُكَذِّبُوكَ فَقَدۡ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ وَبِٱلزُّبُرِ وَبِٱلۡكِتَٰبِ ٱلۡمُنِيرِ\nАгар (мушриклар) сизни ёлғончи қилсалар, бас, (билингки), улардан аввалги кимсалар ҳам (ўзларига келган пайғамбарларни) ёлғончи қилгандилар. Ҳолбуки, пайғамбарлари уларга аниқ-равшан мўъжизалар, (илоҳий) саҳифалар ва (Таврот, Инжил каби) нурли Китоб келтирган эдилар'}, {'chapter': 35, 'verse': 26, 'text': 'ثُمَّ أَخَذۡتُ ٱلَّذِينَ كَفَرُواْۖ فَكَيۡفَ كَانَ نَكِيرِ\nСўнгра кофир бўлган кимсаларни (куфрлари сабабли) ушладим — жазоладим. Бас, Менинг инкорим — азобим қандоқ бўлди'}, {'chapter': 35, 'verse': 27, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ ثَمَرَٰتٖ مُّخۡتَلِفًا أَلۡوَٰنُهَاۚ وَمِنَ ٱلۡجِبَالِ جُدَدُۢ بِيضٞ وَحُمۡرٞ مُّخۡتَلِفٌ أَلۡوَٰنُهَا وَغَرَابِيبُ سُودٞ\nАллоҳ осмондан сув (ёмғир-қор) ёғдириб, унинг ёрдамида рангу рўйлари турли-туман бўлган меваларни чиқарганимизни кўрмадингизми?! Яна тоғлардан ҳам оқ, қизил — ранго-ранг йўл(ли тоғлар) ҳам, тим қора (тоғлар) ҳам бордир'}, {'chapter': 35, 'verse': 28, 'text': 'وَمِنَ ٱلنَّاسِ وَٱلدَّوَآبِّ وَٱلۡأَنۡعَٰمِ مُخۡتَلِفٌ أَلۡوَٰنُهُۥ كَذَٰلِكَۗ إِنَّمَا يَخۡشَى ٱللَّهَ مِنۡ عِبَادِهِ ٱلۡعُلَمَـٰٓؤُاْۗ إِنَّ ٱللَّهَ عَزِيزٌ غَفُورٌ\nШунингдек одамлар, жониворлар ва чорва ҳайвонлари орасида ҳам ранг-баранглари бордир. Аллоҳдан бандалари орасидаги олим-билимдонларгина қўрқур. Шак-шубҳасиз, Аллоҳ қудратли, мағфиратлидир'}, {'chapter': 35, 'verse': 29, 'text': 'إِنَّ ٱلَّذِينَ يَتۡلُونَ كِتَٰبَ ٱللَّهِ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ يَرۡجُونَ تِجَٰرَةٗ لَّن تَبُورَ\nАлбатта Аллоҳнинг Китобини тиловат қиладиган, намозни тўкис адо этадиган ва Биз уларга ризқ қилиб берган нарсалардан махфий ва ошкора инфоқ-эҳсон қиладиган зотлар ҳаргиз касод бўлмайдиган олди-сотдидан (яъни, улардан яхши амал ва инфоқ-эҳсон Аллоҳдан ажру-мукофот бўлишидан) умидвордирлар'}, {'chapter': 35, 'verse': 30, 'text': 'لِيُوَفِّيَهُمۡ أُجُورَهُمۡ وَيَزِيدَهُم مِّن فَضۡلِهِۦٓۚ إِنَّهُۥ غَفُورٞ شَكُورٞ\nЗеро, (Аллоҳ) уларнинг ажрларини комил қилиб берур ва Ўз фазлу-карамидан уларга яна зиёда (мукофотлар) ҳам берур. Албатта, У мағфиратли ва ўта шукр қилгувчидир (яъни, озгина яхши амал учун кўп мукофот ато қилгувчидир)'}, {'chapter': 35, 'verse': 31, 'text': 'وَٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ مِنَ ٱلۡكِتَٰبِ هُوَ ٱلۡحَقُّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِۗ إِنَّ ٱللَّهَ بِعِبَادِهِۦ لَخَبِيرُۢ بَصِيرٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизга ваҳий қилган Китоб — Қуръон ўзидан аввалги илоҳий китобларни тасдиқлагувчи бўлган Ҳақ (Китобдир). Албатта Аллоҳ Ўз бандаларидан огоҳ ва (уларнинг барча ишларини) кўриб тургувчидир'}, {'chapter': 35, 'verse': 32, 'text': 'ثُمَّ أَوۡرَثۡنَا ٱلۡكِتَٰبَ ٱلَّذِينَ ٱصۡطَفَيۡنَا مِنۡ عِبَادِنَاۖ فَمِنۡهُمۡ ظَالِمٞ لِّنَفۡسِهِۦ وَمِنۡهُم مُّقۡتَصِدٞ وَمِنۡهُمۡ سَابِقُۢ بِٱلۡخَيۡرَٰتِ بِإِذۡنِ ٱللَّهِۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ\nСўнгра Биз бу Китобга бандаларимиздан Ўзимиз танлаган зотларни (яъни, сизнинг умматингизни) ворис қилдик. Бас, уларнинг орасида (Қуръонни ўқиса-да, унга амал қилмайдиган) ўз жонига жабр қилгувчи ҳам бор, уларнинг орасида ўртача амал қилгувчи ҳам бор ва уларнинг орасида Аллоҳнинг изну иродаси билан мудом яхшиликларга шошилгувчи ҳам бордир. Ана ўша (яъни, Қуръонга ворис бўлиш Аллоҳнинг) катта марҳаматидир'}, {'chapter': 35, 'verse': 33, 'text': 'جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَلُؤۡلُؤٗاۖ وَلِبَاسُهُمۡ فِيهَا حَرِيرٞ\nУлар олтиндан бўлган билакузуклар ва марварид-маржонлар билан безанган ҳолларида мангу (турадиган) жаннатларга кирурлар. У жойда уларнинг либослари ҳарир-ипак бўлур'}, {'chapter': 35, 'verse': 34, 'text': 'وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِيٓ أَذۡهَبَ عَنَّا ٱلۡحَزَنَۖ إِنَّ رَبَّنَا لَغَفُورٞ شَكُورٌ\nВа улар (жаннатга кирган чоғларида) дедилар: «Бизлардан ғам-қайғуни кетказган Зот — Аллоҳга ҳамду-сано бўлсин. Дарҳақиқат, Парвардигоримиз мағфиратли ва ўта шукр қилгувчидир'}, {'chapter': 35, 'verse': 35, 'text': 'ٱلَّذِيٓ أَحَلَّنَا دَارَ ٱلۡمُقَامَةِ مِن فَضۡلِهِۦ لَا يَمَسُّنَا فِيهَا نَصَبٞ وَلَا يَمَسُّنَا فِيهَا لُغُوبٞ\nУ Ўз фазлу-марҳамати билан бизларни мангу турадиган бир диёрга туширдики, бу жойда бизларга на машаққат етар ва на чарчоқ етар»'}, {'chapter': 35, 'verse': 36, 'text': 'وَٱلَّذِينَ كَفَرُواْ لَهُمۡ نَارُ جَهَنَّمَ لَا يُقۡضَىٰ عَلَيۡهِمۡ فَيَمُوتُواْ وَلَا يُخَفَّفُ عَنۡهُم مِّنۡ عَذَابِهَاۚ كَذَٰلِكَ نَجۡزِي كُلَّ كَفُورٖ\nКофир бўлган кимсалар учун эса жаҳаннам ўти бордирки, на уларга (иккинчи бор ўлиш) ҳукм қилиниб, ўла олурлар ва на улардан (жаҳаннам) азоби енгиллатилур. Биз ҳар бир кофирни мана шундай жазолармиз'}, {'chapter': 35, 'verse': 37, 'text': 'وَهُمۡ يَصۡطَرِخُونَ فِيهَا رَبَّنَآ أَخۡرِجۡنَا نَعۡمَلۡ صَٰلِحًا غَيۡرَ ٱلَّذِي كُنَّا نَعۡمَلُۚ أَوَلَمۡ نُعَمِّرۡكُم مَّا يَتَذَكَّرُ فِيهِ مَن تَذَكَّرَ وَجَآءَكُمُ ٱلنَّذِيرُۖ فَذُوقُواْ فَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٍ\nУлар у жойда: «Парвардигоро, бизларни (бу азобдан) чиқаргин, бизлар қилиб ўтган амалларимиздан бошқа яхши (амалларни) қилурмиз», деб дод-вой қилурлар. Ахир Биз сизларга эслатма оладиган киши эслатма олгудек узун умр бермаганмидик?! Сизларга огоҳлантиргувчи (пайғамбар) ҳам келган эди-ку! Бас, энди азобларингизни тотаверинглар! Энди золим кимсалар учун бирон ёрдамчи бўлмас'}, {'chapter': 35, 'verse': 38, 'text': 'إِنَّ ٱللَّهَ عَٰلِمُ غَيۡبِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nАлбатта Аллоҳ осмонлар ва Ернинг ғайбини (сирру асрорини) билгувчидир. Албатта У диллардаги сирларни ҳам аниқ билгувчидир'}, {'chapter': 35, 'verse': 39, 'text': 'هُوَ ٱلَّذِي جَعَلَكُمۡ خَلَـٰٓئِفَ فِي ٱلۡأَرۡضِۚ فَمَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَلَا يَزِيدُ ٱلۡكَٰفِرِينَ كُفۡرُهُمۡ عِندَ رَبِّهِمۡ إِلَّا مَقۡتٗاۖ وَلَا يَزِيدُ ٱلۡكَٰفِرِينَ كُفۡرُهُمۡ إِلَّا خَسَارٗا\nУ сизларни Ер юзида халифа қилиб қўйган Зотдир. Бас, ким кофир бўлса, унинг куфри фақат ўзининг зиёнига бўлур. Кофир бўлган кимсаларга куфрлари Парвардигор ҳузурида фақат ғазабнигина зиёда қилур. Кофир бўлган кимсаларга куфрлари фақат исёнкорликнигина зиёда қилур'}, {'chapter': 35, 'verse': 40, 'text': 'قُلۡ أَرَءَيۡتُمۡ شُرَكَآءَكُمُ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِ أَمۡ ءَاتَيۡنَٰهُمۡ كِتَٰبٗا فَهُمۡ عَلَىٰ بَيِّنَتٖ مِّنۡهُۚ بَلۡ إِن يَعِدُ ٱلظَّـٰلِمُونَ بَعۡضُهُم بَعۡضًا إِلَّا غُرُورًا\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Хабар берингиз-чи, сизлар Аллоҳни қўйиб илтижо қилаётган бутларингиз (ниманинг шарофати билан илтижо-ибодат қилинишга сазовор бўлдилар?) Сизлар менга кўрсатма берингиз-чи, улар Ердаги бирон нарсани яратганмилар, ёки уларнинг осмонларни (яратишда Аллоҳга) шерикликлари борми, ёхуд Биз уларга бирон китоб ато этдикми-ки, улар ўша (китобдан ўзларининг Бизга шерик эканликлари ҳақида) бирон ҳужжат устида бўлсалар?! Йўқ, у золим кимсалар бир-бирларига («Бутларимиз бизларни шафоат қиладилар», деб) фақат алдовни ваъда қилурлар'}, {'chapter': 35, 'verse': 41, 'text': '۞إِنَّ ٱللَّهَ يُمۡسِكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ أَن تَزُولَاۚ وَلَئِن زَالَتَآ إِنۡ أَمۡسَكَهُمَا مِنۡ أَحَدٖ مِّنۢ بَعۡدِهِۦٓۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا\nАлбатта Аллоҳ осмонлар ва Ерни қулаб тушишдан ушлаб-асраб турур. Аниқки, агар улар қуласалар, У зотдан сўнг (яъни, Аллоҳдан ўзга) бирон кимса уларни тутиб тура олмас. Дарҳақиқат, у ҳалим ва мағфиратли бўлган Зотдир'}, {'chapter': 35, 'verse': 42, 'text': 'وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَهُمۡ نَذِيرٞ لَّيَكُونُنَّ أَهۡدَىٰ مِنۡ إِحۡدَى ٱلۡأُمَمِۖ فَلَمَّا جَآءَهُمۡ نَذِيرٞ مَّا زَادَهُمۡ إِلَّا نُفُورًا\n(Макка кофирлари) агар уларга бирон огоҳлантиргувчи (пайғамбар) келса, албатта ҳар қандай умматлардан ҳам ҳидоят топгувчироқ бўлишлари ҳақида жон-жаҳдлари билан қасам ичгандилар. Бас, қачонки уларга огоҳлантиргувчи (Муҳаммад алайҳис-салоту вас-салом) келгач, (бу) уларни янада (ҳидоятдан) узоқлаштирди, холос'}, {'chapter': 35, 'verse': 43, 'text': 'ٱسۡتِكۡبَارٗا فِي ٱلۡأَرۡضِ وَمَكۡرَ ٱلسَّيِّيِٕۚ وَلَا يَحِيقُ ٱلۡمَكۡرُ ٱلسَّيِّئُ إِلَّا بِأَهۡلِهِۦۚ فَهَلۡ يَنظُرُونَ إِلَّا سُنَّتَ ٱلۡأَوَّلِينَۚ فَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَبۡدِيلٗاۖ وَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَحۡوِيلًا\nУлар Ерда мутакаббирлик қилган ва (пайғамбарга қарши) ёмон макр-ҳийла қилган ҳолларида (ҳидоятдан узоқлашдилар). Ёмон макр-ҳийла эса фақат ўз эгаларини ўраб ҳалок қилур. Бас, улар фақат аввалгиларнинг суннатларига (яъни, кўргуликларига) кўз тутмоқдалар, холос (яъни, уларнинг ҳам бошларига аввалги динсизларнинг куни тушар). Бас, сиз ҳаргиз Аллоҳ суннати-қонунининг ўзгарганини кўрмассиз ва ҳаргиз Аллоҳ суннатининг айланганини (яъни, азобга мустаҳиқ бўлган кимсалар қолиб, ўзгалар азобланганини) кўрмассиз'}, {'chapter': 35, 'verse': 44, 'text': 'أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَكَانُوٓاْ أَشَدَّ مِنۡهُمۡ قُوَّةٗۚ وَمَا كَانَ ٱللَّهُ لِيُعۡجِزَهُۥ مِن شَيۡءٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ عَلِيمٗا قَدِيرٗا\nАхир улар Ер юзида сайр қилиб ўзларидан аввалги (динсиз кимсаларнинг) оқибати қандай бўлганини кўрмайдиларми?! Ҳолбуки, (аввалгилар) булардан кўра кучли-қувватлироқ эдилар. На осмонлардаги ва на Ердаги бирон нарса Аллоҳдан қочиб қутулгувчи эмасдир. Зотан, У билим ва қудрат эгаси бўлган Зотдир'}, {'chapter': 35, 'verse': 45, 'text': 'وَلَوۡ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِمَا كَسَبُواْ مَا تَرَكَ عَلَىٰ ظَهۡرِهَا مِن دَآبَّةٖ وَلَٰكِن يُؤَخِّرُهُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۖ فَإِذَا جَآءَ أَجَلُهُمۡ فَإِنَّ ٱللَّهَ كَانَ بِعِبَادِهِۦ بَصِيرَۢا\nАгар Аллоҳ одамларни улар қилган гуноҳлари билан жазоласа эди, (Ер) устида бирон жониворни қўймаган бўлур эди. Лекин У зот уларни (жазолашни) белгилаган муддатгача (яъни, Қиёмат Кунигача) қолдирур. Бас, қачон ўша муддат келгач (уларнинг ҳар бирига қилиб ўтган барча яхши-ёмон амалларининг мукофот-жазоларини берур). Зеро, Аллоҳ бандаларини кўриб тургувчи бўлган Зотдир'}, {'chapter': 36, 'verse': 1, 'text': 'يسٓ\nЁ, Син'}, {'chapter': 36, 'verse': 2, 'text': 'وَٱلۡقُرۡءَانِ ٱلۡحَكِيمِ\n(Эй Муҳаммад алайҳис-салоту вас-салом, ушбу) ҳикматли Қуръонга қасамки'}, {'chapter': 36, 'verse': 3, 'text': 'إِنَّكَ لَمِنَ ٱلۡمُرۡسَلِينَ\nҲеч шак-шубҳасиз, сиз пайғамбарлардандирсиз'}, {'chapter': 36, 'verse': 4, 'text': 'عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nТўғри Йўл (яъни, ҳақ дин) устидаги (пайғамбарлардандирсиз)'}, {'chapter': 36, 'verse': 5, 'text': 'تَنزِيلَ ٱلۡعَزِيزِ ٱلرَّحِيمِ\n(Бу Қуръон) қудратли ва меҳрибон Зот томонидан нозил қилингандир'}, {'chapter': 36, 'verse': 6, 'text': 'لِتُنذِرَ قَوۡمٗا مَّآ أُنذِرَ ءَابَآؤُهُمۡ فَهُمۡ غَٰفِلُونَ\nОта-боболари (бирон пайғамбар орқали Охират азобидан) огоҳлантирилмасдан, ғофил бўлиб қолган бир қавмни огоҳлантиришингиз учун (нозил қилингандир)'}, {'chapter': 36, 'verse': 7, 'text': 'لَقَدۡ حَقَّ ٱلۡقَوۡلُ عَلَىٰٓ أَكۡثَرِهِمۡ فَهُمۡ لَا يُؤۡمِنُونَ\nАниқки, уларнинг кўпларига Сўз (яъни, азоб ҳақидаги ҳукм) ҳақ бўлгандир. Бас, улар иймон келтирмаслар'}, {'chapter': 36, 'verse': 8, 'text': 'إِنَّا جَعَلۡنَا فِيٓ أَعۡنَٰقِهِمۡ أَغۡلَٰلٗا فَهِيَ إِلَى ٱلۡأَذۡقَانِ فَهُم مُّقۡمَحُونَ\nДарҳақиқат, Биз уларнинг бўйинларига то иякларигача (етадиган) кишанларни уриб қўйдик, бас, улар ғўдайиб қолдилар (яъни, улар Ҳаққа эгилмаслар)'}, {'chapter': 36, 'verse': 9, 'text': 'وَجَعَلۡنَا مِنۢ بَيۡنِ أَيۡدِيهِمۡ سَدّٗا وَمِنۡ خَلۡفِهِمۡ سَدّٗا فَأَغۡشَيۡنَٰهُمۡ فَهُمۡ لَا يُبۡصِرُونَ\nВа Биз уларнинг олдиларидан бир тўсиқ — парда қилиб, уларни ўраб қўйдик. Бас, улар кўра олмаслар'}, {'chapter': 36, 'verse': 10, 'text': 'وَسَوَآءٌ عَلَيۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларни огоҳлантирдингизми ёки огоҳлантирмадингизми уларга баробардир — иймон келтирмаслар'}, {'chapter': 36, 'verse': 11, 'text': 'إِنَّمَا تُنذِرُ مَنِ ٱتَّبَعَ ٱلذِّكۡرَ وَخَشِيَ ٱلرَّحۡمَٰنَ بِٱلۡغَيۡبِۖ فَبَشِّرۡهُ بِمَغۡفِرَةٖ وَأَجۡرٖ كَرِيمٍ\nСиз фақат эргашган ва ғойибда (яъни, кўрмай) туриб, Раҳмондан қўрққан кишиларнигина огоҳлантира олурсиз. Бас, ўша (кишиларга) мағфират ва улуғ ажр (жаннат) хушхабарини беринг'}, {'chapter': 36, 'verse': 12, 'text': 'إِنَّا نَحۡنُ نُحۡيِ ٱلۡمَوۡتَىٰ وَنَكۡتُبُ مَا قَدَّمُواْ وَءَاثَٰرَهُمۡۚ وَكُلَّ شَيۡءٍ أَحۡصَيۡنَٰهُ فِيٓ إِمَامٖ مُّبِينٖ\nАлбатта Биз Ўзимизгина ўликларни тирилтирурмиз ва уларнинг қилган амалларини ҳамда (қолдирган) изларини ёзиб қўюрмиз. Барча нарсани Биз очиқ Китобда (яъни, Лавҳул-Маҳфузда) белгилаб қўйгандирмиз'}, {'chapter': 36, 'verse': 13, 'text': 'وَٱضۡرِبۡ لَهُم مَّثَلًا أَصۡحَٰبَ ٱلۡقَرۡيَةِ إِذۡ جَآءَهَا ٱلۡمُرۡسَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларга (Антокия) қишлоғининг аҳлини — у жойга элчилар келган пайтини мисол келтиринг'}, {'chapter': 36, 'verse': 14, 'text': 'إِذۡ أَرۡسَلۡنَآ إِلَيۡهِمُ ٱثۡنَيۡنِ فَكَذَّبُوهُمَا فَعَزَّزۡنَا بِثَالِثٖ فَقَالُوٓاْ إِنَّآ إِلَيۡكُم مُّرۡسَلُونَ\nЎшанда Биз уларга икки (элчини) юборганимизда, у иккисини ёлғончи қилишгач, учинчи (элчи) билан қувватлантирдик. Бас, (уччала элчи Антокия аҳлига): «Дарҳақиқат, биз сизларга (юборилган) элчилармиз», деган эдилар'}, {'chapter': 36, 'verse': 15, 'text': 'قَالُواْ مَآ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا وَمَآ أَنزَلَ ٱلرَّحۡمَٰنُ مِن شَيۡءٍ إِنۡ أَنتُمۡ إِلَّا تَكۡذِبُونَ\nУлар: «Сизлар ҳам худди ўзимизга ўхшаган одамларсиз. Раҳмон (яъни, Аллоҳ) бирон нарса — ваҳий нозил қилгани йўқ. Сизлар фақат ёлғон сўзламоқдасизлар», дедилар'}, {'chapter': 36, 'verse': 16, 'text': 'قَالُواْ رَبُّنَا يَعۡلَمُ إِنَّآ إِلَيۡكُمۡ لَمُرۡسَلُونَ\n(Элчилар) айтдилар: «Парвардигоримиз билурки, бизлар шак-шубҳасиз, сизларга (юборилган) элчилардирмиз'}, {'chapter': 36, 'verse': 17, 'text': 'وَمَا عَلَيۡنَآ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nВа бизларнинг зиммамизда (Аллоҳнинг ваҳийсини сизларга) очиқ-равшан етказиш бордир»'}, {'chapter': 36, 'verse': 18, 'text': 'قَالُوٓاْ إِنَّا تَطَيَّرۡنَا بِكُمۡۖ لَئِن لَّمۡ تَنتَهُواْ لَنَرۡجُمَنَّكُمۡ وَلَيَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِيمٞ\nУлар дедилар: «Ҳақиқатан, бизлар сизлар ҳақингизда бадгумондамиз. Қасамки, агар (бу сўзларингизни) тўхтатмасангизлар, албатта сизларни тошбўрон қилурмиз ва сизларга биз томондан бир аламли азоб етар»'}, {'chapter': 36, 'verse': 19, 'text': 'قَالُواْ طَـٰٓئِرُكُم مَّعَكُمۡ أَئِن ذُكِّرۡتُمۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ\n(Элчилар) айтдилар: «Бадгумонингиз ўзларингиз биландир (яъни, бизлардан эмас, балки ўзларингизнинг куфрларингиздан бадгумон қилинглар). Сизларга ваъз-насиҳат қилинса,(қабул қилиш ўрнига бадгумонда бўлиб дўқ-пўписа қилурмисиз)?! Йўқ, сизлар ҳаддан ошган қавмдирсиз!»'}, {'chapter': 36, 'verse': 20, 'text': 'وَجَآءَ مِنۡ أَقۡصَا ٱلۡمَدِينَةِ رَجُلٞ يَسۡعَىٰ قَالَ يَٰقَوۡمِ ٱتَّبِعُواْ ٱلۡمُرۡسَلِينَ\n(Шу пайт) бир киши шаҳарнинг бир чеккасидан шошганча келиб, деди: «Эй қавмим, бу элчиларга эргашинглар'}, {'chapter': 36, 'verse': 21, 'text': 'ٱتَّبِعُواْ مَن لَّا يَسۡـَٔلُكُمۡ أَجۡرٗا وَهُم مُّهۡتَدُونَ\nЎзлари ҳидоятда бўлган ва сизлардан бирон ажр-мукофот сўрамайдиган зотларга эргашинглар!»'}, {'chapter': 36, 'verse': 22, 'text': 'وَمَالِيَ لَآ أَعۡبُدُ ٱلَّذِي فَطَرَنِي وَإِلَيۡهِ تُرۡجَعُونَ\nУ айтди: «Нега мен ўзимни яратган Зотга ибодат қилмайин? Сизлар ҳам (дунёдан ўтгач) ёлғиз Унгагина қайтарилурсизлар'}, {'chapter': 36, 'verse': 23, 'text': 'ءَأَتَّخِذُ مِن دُونِهِۦٓ ءَالِهَةً إِن يُرِدۡنِ ٱلرَّحۡمَٰنُ بِضُرّٖ لَّا تُغۡنِ عَنِّي شَفَٰعَتُهُمۡ شَيۡـٔٗا وَلَا يُنقِذُونِ\nМен У зотни қўйиб (жонсиз бутларни) худо қилиб олайми?! (Ҳаргиз ундай қилмасман, чунки) агар Раҳмон менга бирон зиён етказишни истаса, у (бут)ларнинг оқловлари мени бирон нарсадан беҳожат қила олмас ва улар мени (Аллоҳнинг азобидан) қутқара олмаслар'}, {'chapter': 36, 'verse': 24, 'text': 'إِنِّيٓ إِذٗا لَّفِي ضَلَٰلٖ مُّبِينٍ\nУ ҳолда мен шак-шубҳасиз, очиқ залолатда бўлурман'}, {'chapter': 36, 'verse': 25, 'text': 'إِنِّيٓ ءَامَنتُ بِرَبِّكُمۡ فَٱسۡمَعُونِ\nҲақиқатан, мен (жонсиз бутларга эмас, балки барчаларингизни яратган) Парвардигорингизга иймон келтирганман. Бас, мени тинглангиз!»'}, {'chapter': 36, 'verse': 26, 'text': 'قِيلَ ٱدۡخُلِ ٱلۡجَنَّةَۖ قَالَ يَٰلَيۡتَ قَوۡمِي يَعۡلَمُونَ\n(Лекин жоҳил қавм Ҳабиб ан-Нажжорнинг сўзларига қулоқ солмасдан уни қатл қилишгач, унга) «Жаннатга кир», дейилди. (Аллоҳнинг амри билан жаннатга киритилиб, у жойдаги ноз-неъмат ва иззат-икромни кўргач), у айтди: «Қани эди қавмим ҳам билсалар эди'}, {'chapter': 36, 'verse': 27, 'text': 'بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ ٱلۡمُكۡرَمِينَ\nМени Парвардигорим мағфират қилганини ва мени иззат-икромли кишилардан қилганини (билсалар эди)»'}, {'chapter': 36, 'verse': 28, 'text': '۞وَمَآ أَنزَلۡنَا عَلَىٰ قَوۡمِهِۦ مِنۢ بَعۡدِهِۦ مِن جُندٖ مِّنَ ٱلسَّمَآءِ وَمَا كُنَّا مُنزِلِينَ\nБиз (Ҳабиб ан-Нажжорни ўлдирганларидан) кейин унинг устига (уларни ҳалок қилиш учун) осмондан бирон қўшин (яъни, азоб фаришталарини) туширмадик, Биз (ҳеч нарса) туширгувчи бўлмадик'}, {'chapter': 36, 'verse': 29, 'text': 'إِن كَانَتۡ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ فَإِذَا هُمۡ خَٰمِدُونَ\nФақат биргина даҳшатли қичқириқ бўлди-ю, баногоҳ улар «ўчиб» қолдилар (яъни биронталари ҳам қолмай ҳалок бўлдилар)'}, {'chapter': 36, 'verse': 30, 'text': 'يَٰحَسۡرَةً عَلَى ٱلۡعِبَادِۚ مَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\n(Аллоҳнинг элчиларини ёлғончи қилиб, уларга озор-азият етказадиган барча) бандаларга ҳасрат-надомат бўлгай! Уларга бирон пайғамбар келмас, магар улар унинг устидан масхара қилиб кулгувчи бўлурлар'}, {'chapter': 36, 'verse': 31, 'text': 'أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ أَنَّهُمۡ إِلَيۡهِمۡ لَا يَرۡجِعُونَ\nАхир улар ўзларидан илгари ҳам қанча асрларни (авлодларни) ҳалок қилганимизни — ўшалар уларнинг олдига (яъни, бу дунёга) қайтиб келмасликларини кўрмадиларми'}, {'chapter': 36, 'verse': 32, 'text': 'وَإِن كُلّٞ لَّمَّا جَمِيعٞ لَّدَيۡنَا مُحۡضَرُونَ\nҲеч шак-шубҳасиз, (уларнинг) барчалари (Қиёмат Кунида) Бизнинг даргоҳимизда ҳозир қилингувчидирлар'}, {'chapter': 36, 'verse': 33, 'text': 'وَءَايَةٞ لَّهُمُ ٱلۡأَرۡضُ ٱلۡمَيۡتَةُ أَحۡيَيۡنَٰهَا وَأَخۡرَجۡنَا مِنۡهَا حَبّٗا فَمِنۡهُ يَأۡكُلُونَ\nЎлик-қуруқ ер улар учун (қайта тирилиш ҳақ эканига) бир оят-аломатдир — Биз уни (сув билан) тирилтирдик ва ундан (турли) дон-дунни ундириб чиқардик. Бас, улар ўша (дон)дан ерлар'}, {'chapter': 36, 'verse': 34, 'text': 'وَجَعَلۡنَا فِيهَا جَنَّـٰتٖ مِّن نَّخِيلٖ وَأَعۡنَٰبٖ وَفَجَّرۡنَا فِيهَا مِنَ ٱلۡعُيُونِ\nЯна Биз у (ер)да хурмозор ва узумзор боғларни (пайдо) қилдик ва унда булоқларни оқизиб қўйдик'}, {'chapter': 36, 'verse': 35, 'text': 'لِيَأۡكُلُواْ مِن ثَمَرِهِۦ وَمَا عَمِلَتۡهُ أَيۡدِيهِمۡۚ أَفَلَا يَشۡكُرُونَ\nТоки (одамлар ўша боғларнинг) мевасидан есинлар учун (шундай қилдик). Ҳолбуки, у (мевалар)ни улар (қўллари билан) қилмаган — ясаб олмаган эдилар. Ахир шукр қилмайдиларми'}, {'chapter': 36, 'verse': 36, 'text': 'سُبۡحَٰنَ ٱلَّذِي خَلَقَ ٱلۡأَزۡوَٰجَ كُلَّهَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ وَمِنۡ أَنفُسِهِمۡ وَمِمَّا لَا يَعۡلَمُونَ\nЕр ундириб-ўстирадиган нарсалардан, (одамларнинг) ўзларидан ва яна улар билмайдиган нарсалардан иборат барча жуфтларни яратган (Аллоҳ ҳар қандай айбу-нуқсондан) пок Зотдир'}, {'chapter': 36, 'verse': 37, 'text': 'وَءَايَةٞ لَّهُمُ ٱلَّيۡلُ نَسۡلَخُ مِنۡهُ ٱلنَّهَارَ فَإِذَا هُم مُّظۡلِمُونَ\nКеча ҳам улар учун (бизнинг қудратимизни кўрсатиб турадиган) бир оятдир: Биз ундан кундузни ечиб олишимиз билан баногоҳ улар зулматда қолурлар'}, {'chapter': 36, 'verse': 38, 'text': 'وَٱلشَّمۡسُ تَجۡرِي لِمُسۡتَقَرّٖ لَّهَاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\nҚуёш (бирон сония тўхтамай) ўз қароргоҳи сари жорий бўлур. Бу қудратли ва билгувчи Зотнинг тақдири — ўлчовидир'}, {'chapter': 36, 'verse': 39, 'text': 'وَٱلۡقَمَرَ قَدَّرۡنَٰهُ مَنَازِلَ حَتَّىٰ عَادَ كَٱلۡعُرۡجُونِ ٱلۡقَدِيمِ\nБиз ойни ҳам токи у эски (хурмо) бутоғи каби бўлиб (эгилиб ҳилол ҳолига келиб) қолгунича бир неча манзилга белгилаб-тайинлаб қўгандирмиз'}, {'chapter': 36, 'verse': 40, 'text': 'لَا ٱلشَّمۡسُ يَنۢبَغِي لَهَآ أَن تُدۡرِكَ ٱلۡقَمَرَ وَلَا ٱلَّيۡلُ سَابِقُ ٱلنَّهَارِۚ وَكُلّٞ فِي فَلَكٖ يَسۡبَحُونَ\nНа қуёш учун ойга етиш мумкин бўлур ва на кеча кундуздан ўзгувчидир. (Қуёш, ой ва юлдузларнинг) барчалари фалакда сузиб юрур'}, {'chapter': 36, 'verse': 41, 'text': 'وَءَايَةٞ لَّهُمۡ أَنَّا حَمَلۡنَا ذُرِّيَّتَهُمۡ فِي ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\nБиз уларнинг авлод-зурриётларини (Нуҳ пайғамбар билан бирга одамлар ва турли жониворларга) тўла кемада кўтарганимиз (ва тўфон балосидан нажот берганимиз) улар учун (Бизнинг қудратимизни кўрсатадиган) яна бир оятдир'}, {'chapter': 36, 'verse': 42, 'text': 'وَخَلَقۡنَا لَهُم مِّن مِّثۡلِهِۦ مَا يَرۡكَبُونَ\nЯна Биз улар учун худди Ўша (кемага) ўхшаш (бошқа) минадиган нарсаларини (яъни, катта-кичик кемаларни) ҳам яратдик'}, {'chapter': 36, 'verse': 43, 'text': 'وَإِن نَّشَأۡ نُغۡرِقۡهُمۡ فَلَا صَرِيخَ لَهُمۡ وَلَا هُمۡ يُنقَذُونَ\nАгар Биз хоҳласак уларни ғарқ қилурмиз-да, сўнг улар учун бирон ёрдамчи бўлмас ва улар (ҳалокатдан) қутқазилмаслар'}, {'chapter': 36, 'verse': 44, 'text': 'إِلَّا رَحۡمَةٗ مِّنَّا وَمَتَٰعًا إِلَىٰ حِينٖ\nМагар Биз томондан бўлган марҳамат сабабли ва (маълум) бир вақтгача (яъни, ажаллари етгунча ҳаёт неъматларидан) баҳраманд бўлишлари учун (уларга бало-қазолардан нажот берилур)'}, {'chapter': 36, 'verse': 45, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱتَّقُواْ مَا بَيۡنَ أَيۡدِيكُمۡ وَمَا خَلۡفَكُمۡ لَعَلَّكُمۡ تُرۡحَمُونَ\nҚачон (мушрикларга): «Сизлар Аллоҳнинг раҳматига эришишларингиз учун олдиларингиздаги (дунё фитналаридан) ва ортингиздаги (Охират азобидан) қўрқинглар», (яъни, иймонга келинглар) дейилса, (улар қулоқ солмаслар)'}, {'chapter': 36, 'verse': 46, 'text': 'وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ\nУларга Парвардигорнинг оятларидан бирон оят келмас, магар улар (ўша оятлнрдан) юз ўгиргувчи бўлурлар'}, {'chapter': 36, 'verse': 47, 'text': 'وَإِذَا قِيلَ لَهُمۡ أَنفِقُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ قَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُوٓاْ أَنُطۡعِمُ مَن لَّوۡ يَشَآءُ ٱللَّهُ أَطۡعَمَهُۥٓ إِنۡ أَنتُمۡ إِلَّا فِي ضَلَٰلٖ مُّبِينٖ\nҚачон уларга: «Аллоҳ сизларга ризқ қилиб берган нарсалардан инфоқ-эҳсон қилинглар», дейилса, кофир бўлган кимсалар иймон келтирган зотларга (истеҳзо билан): «Агар Аллоҳ хоҳласа Ўзи таомлантириб оладиган кишиларга бизлар таом берурмизми? Сизлар ҳеч шак-шубҳасиз, очиқ залолатдадирсизлар», дерлар'}, {'chapter': 36, 'verse': 48, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангизлар (айтинглар-чи), мана шу ваъда (қилинган азоб) қачон бўлади?», дерлар'}, {'chapter': 36, 'verse': 49, 'text': 'مَا يَنظُرُونَ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ تَأۡخُذُهُمۡ وَهُمۡ يَخِصِّمُونَ\nУлар фақат биргина даҳшатли қичқириқни кутмоқдалар, холос. У уларни (ўлимдан мутлақо бехабар ҳолларида кўча-кўй ва бозорларда бир-бирлари билан) жанжаллашиб турганларида олиб кетар (яъни, ҳалок этар)'}, {'chapter': 36, 'verse': 50, 'text': 'فَلَا يَسۡتَطِيعُونَ تَوۡصِيَةٗ وَلَآ إِلَىٰٓ أَهۡلِهِمۡ يَرۡجِعُونَ\nБас, улар на бирон васият қилишга ва на уйларига қайтишга қодир бўлурлар'}, {'chapter': 36, 'verse': 51, 'text': 'وَنُفِخَ فِي ٱلصُّورِ فَإِذَا هُم مِّنَ ٱلۡأَجۡدَاثِ إِلَىٰ رَبِّهِمۡ يَنسِلُونَ\n(Қиёмат соати келиб фаришта Исрофилнинг) сури чалиниши билан баногоҳ улар қабрларидан Парвардигорлари ҳузурига (ҳисоб-китоб учун) суғурилиб чиқурлар'}, {'chapter': 36, 'verse': 52, 'text': 'قَالُواْ يَٰوَيۡلَنَا مَنۢ بَعَثَنَا مِن مَّرۡقَدِنَاۜۗ هَٰذَا مَا وَعَدَ ٱلرَّحۡمَٰنُ وَصَدَقَ ٱلۡمُرۡسَلُونَ\nУлар: «Эй бизларга ўлим бўлсин! Ким бизларни ётган жойимиздан (қабрларимиздан) турғазди?», деганларида, (уларга айтилур): «Мана шу Раҳмон ваъда қилган ва пайғамбарлар рост сўзлаган нарса — Қиёматдир»'}, {'chapter': 36, 'verse': 53, 'text': 'إِن كَانَتۡ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ فَإِذَا هُمۡ جَمِيعٞ لَّدَيۡنَا مُحۡضَرُونَ\nФақатгина бир даҳшатли қичқириқ бўлар-у, баногоҳ уларнинг барчалари Бизнинг даргоҳимизда ҳозир қилингувчидирлар'}, {'chapter': 36, 'verse': 54, 'text': 'فَٱلۡيَوۡمَ لَا تُظۡلَمُ نَفۡسٞ شَيۡـٔٗا وَلَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\nМана бу Кунда ҳеч бир жонга бирон зулм қилинмас ва сизлар фақат ўзларингиз қилиб ўтган амалларингиз сабабли жазоланурсиз'}, {'chapter': 36, 'verse': 55, 'text': 'إِنَّ أَصۡحَٰبَ ٱلۡجَنَّةِ ٱلۡيَوۡمَ فِي شُغُلٖ فَٰكِهُونَ\nШак-шубҳасиз, жаннат эгалари бу кунда ўзлари истаган (орзу қилган) иш(лари) билан шод-хуррам бўлгувчидирлар'}, {'chapter': 36, 'verse': 56, 'text': 'هُمۡ وَأَزۡوَٰجُهُمۡ فِي ظِلَٰلٍ عَلَى ٱلۡأَرَآئِكِ مُتَّكِـُٔونَ\nУлар ўз жуфтлари билан бирга барча соя-салқин жойлардаги сўриларда ястаниб ўтирурлар'}, {'chapter': 36, 'verse': 57, 'text': 'لَهُمۡ فِيهَا فَٰكِهَةٞ وَلَهُم مَّا يَدَّعُونَ\nУлар учун у жойда (ҳар турли) мева-чева мавжуддир ва улар учун чорлаган — истаган нарсалари ҳозирдир'}, {'chapter': 36, 'verse': 58, 'text': 'سَلَٰمٞ قَوۡلٗا مِّن رَّبّٖ رَّحِيمٖ\n(Уларга) Меҳрибон Парвардигори томонидан салом айтилур'}, {'chapter': 36, 'verse': 59, 'text': 'وَٱمۡتَٰزُواْ ٱلۡيَوۡمَ أَيُّهَا ٱلۡمُجۡرِمُونَ\nЭй жиноятчи кимсалар, мана бу Кунда (мўминлардан) ажралинглар'}, {'chapter': 36, 'verse': 60, 'text': '۞أَلَمۡ أَعۡهَدۡ إِلَيۡكُمۡ يَٰبَنِيٓ ءَادَمَ أَن لَّا تَعۡبُدُواْ ٱلشَّيۡطَٰنَۖ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nМен сизларга «Эй Одам болалари, шайтонга ибодат қилмангиз, чунки у сизларга очиқ душмандир'}, {'chapter': 36, 'verse': 61, 'text': 'وَأَنِ ٱعۡبُدُونِيۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nМенгагина ибодат қилинглар! Мана шу Тўғри Йўлдир», деб буюрмаганмидим'}, {'chapter': 36, 'verse': 62, 'text': 'وَلَقَدۡ أَضَلَّ مِنكُمۡ جِبِلّٗا كَثِيرًاۖ أَفَلَمۡ تَكُونُواْ تَعۡقِلُونَ\nАниқки, (шайтон) сизларнинг ичингиздан кўп авлодни йўлдан оздирди. Ахир ақл юритгувчи бўлмадингизларми'}, {'chapter': 36, 'verse': 63, 'text': 'هَٰذِهِۦ جَهَنَّمُ ٱلَّتِي كُنتُمۡ تُوعَدُونَ\nСизларга ваъда қилинган жаҳаннам мана шудир'}, {'chapter': 36, 'verse': 64, 'text': 'ٱصۡلَوۡهَا ٱلۡيَوۡمَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nСизлар (ҳаёти дунёда) кофир бўлиб ўтганларингиз сабабли мана бу Кунда (жаҳаннамга) кирингиз'}, {'chapter': 36, 'verse': 65, 'text': 'ٱلۡيَوۡمَ نَخۡتِمُ عَلَىٰٓ أَفۡوَٰهِهِمۡ وَتُكَلِّمُنَآ أَيۡدِيهِمۡ وَتَشۡهَدُ أَرۡجُلُهُم بِمَا كَانُواْ يَكۡسِبُونَ\nБу кун Биз уларнинг оғизларини муҳрлаб қўюрмиз. Ва Бизга уларнинг қилиб ўтган ишлари ҳақида — уларнинг қўллари сўзлар ва оёқлари гувоҳлик берур'}, {'chapter': 36, 'verse': 66, 'text': 'وَلَوۡ نَشَآءُ لَطَمَسۡنَا عَلَىٰٓ أَعۡيُنِهِمۡ فَٱسۡتَبَقُواْ ٱلصِّرَٰطَ فَأَنَّىٰ يُبۡصِرُونَ\nАгар Биз хоҳласак, уларнинг кўзларини йўқ қилган бўлур эдик. Ана у ҳолда улар йўлларига шошардилар-у, (лекин) қаёқдан кўра олсинлар'}, {'chapter': 36, 'verse': 67, 'text': 'وَلَوۡ نَشَآءُ لَمَسَخۡنَٰهُمۡ عَلَىٰ مَكَانَتِهِمۡ فَمَا ٱسۡتَطَٰعُواْ مُضِيّٗا وَلَا يَرۡجِعُونَ\nАгар Биз хоҳласак, уларни турган жойларида (маймун, тўнғиз ёки тош каби) бошқа нарсаларга айлантириб қўйган бўлур эдик. Сўнг улар (олдинга) юришга ҳам қодир эмаслар, (ортларига ҳам) қайта олмаслар'}, {'chapter': 36, 'verse': 68, 'text': 'وَمَن نُّعَمِّرۡهُ نُنَكِّسۡهُ فِي ٱلۡخَلۡقِۚ أَفَلَا يَعۡقِلُونَ\nБиз кимга (узун) умр берсак унинг хилқатини (жисму-жонини) тубан — ночор қилиб қўюрмиз. Ахир ақл юргизмайдиларми (яъни, бир кишининг сурат ва сийратида ёш-яланглиги билан қари-қартанглиги ўртасида нақадар катта фарқ борлигини кўриб ибрат олмайдиларми)'}, {'chapter': 36, 'verse': 69, 'text': 'وَمَا عَلَّمۡنَٰهُ ٱلشِّعۡرَ وَمَا يَنۢبَغِي لَهُۥٓۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ وَقُرۡءَانٞ مُّبِينٞ\n(Муҳаммад алайҳис-салоту вас-саломга) шеър ўргатмадик ва (шоирлик) унинг учун дуруст эмасдир. (Муҳаммад алайҳис-салоту вас-салом тиловат қилаётган нарса) фақат бир эслатма ва очиқ-равшан Қуръондир'}, {'chapter': 36, 'verse': 70, 'text': 'لِّيُنذِرَ مَن كَانَ حَيّٗا وَيَحِقَّ ٱلۡقَوۡلُ عَلَى ٱلۡكَٰفِرِينَ\nУ тирик бўлган кишиларни огоҳлантириши учун ва кофирлар устига Сўз — азоб ҳақ бўлиши учун (нозил қилинган бир эслатмадир)'}, {'chapter': 36, 'verse': 71, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّا خَلَقۡنَا لَهُم مِّمَّا عَمِلَتۡ أَيۡدِينَآ أَنۡعَٰمٗا فَهُمۡ لَهَا مَٰلِكُونَ\nАхир улар, Биз учун Ўз (қудрат) қўлимиз билан қилган нарсаларни — чорва ҳайвонларини яратиб қўйганимизни кўрмадиларми?! Мана улар ўша (ҳайвон)ларга эгадирлар'}, {'chapter': 36, 'verse': 72, 'text': 'وَذَلَّلۡنَٰهَا لَهُمۡ فَمِنۡهَا رَكُوبُهُمۡ وَمِنۡهَا يَأۡكُلُونَ\nБиз ўша (ҳайвон)ларни уларга бўйсундириб қўйганмиз. Ана уларнинг минадиган нарсалари ҳам ўша (ҳайвон)лардандир, ейдиган (таом)лари ҳам ўшалардандир'}, {'chapter': 36, 'verse': 73, 'text': 'وَلَهُمۡ فِيهَا مَنَٰفِعُ وَمَشَارِبُۚ أَفَلَا يَشۡكُرُونَ\nЯна улар учун ўша (ҳайвон)ларда (турли) фойдалар ва ичимлик (сут-қаймоқ)лар бордир. Ахир шукр қилмасларми'}, {'chapter': 36, 'verse': 74, 'text': 'وَٱتَّخَذُواْ مِن دُونِ ٱللَّهِ ءَالِهَةٗ لَّعَلَّهُمۡ يُنصَرُونَ\n(Йўқ, улар шукр қилмадилар, балки) Аллоҳни қўйиб ёрдам олиш умидида (ўзга) «худо»ларни тутдилар'}, {'chapter': 36, 'verse': 75, 'text': 'لَا يَسۡتَطِيعُونَ نَصۡرَهُمۡ وَهُمۡ لَهُمۡ جُندٞ مُّحۡضَرُونَ\nУлар ўша («худо»лари) учун ҳозирланган қўшинлари бўлган ҳолларида ҳам у («худо»лар) уларга ёрдам беришга қодир бўлмаслар'}, {'chapter': 36, 'verse': 76, 'text': 'فَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّا نَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сизни уларнинг сўзлари ғамгин қилмасин! Зотан, Биз уларнинг яширадиган нарсаларини ҳам, ошкор қиладиган нарсаларини ҳам билурмиз'}, {'chapter': 36, 'verse': 77, 'text': 'أَوَلَمۡ يَرَ ٱلۡإِنسَٰنُ أَنَّا خَلَقۡنَٰهُ مِن نُّطۡفَةٖ فَإِذَا هُوَ خَصِيمٞ مُّبِينٞ\nИнсон Биз уни нутфадан — бир томчи сувдан яратганимизни, энди эса баногоҳ у (Ўзимизга) очиқ қаршилик қилгувчи бўлиб қолганини кўрмадими'}, {'chapter': 36, 'verse': 78, 'text': 'وَضَرَبَ لَنَا مَثَلٗا وَنَسِيَ خَلۡقَهُۥۖ قَالَ مَن يُحۡيِ ٱلۡعِظَٰمَ وَهِيَ رَمِيمٞ\nУ Бизга «Чириб битган суякларни ким ҳам тирилтира олур?», деб мисол келтирди-ю, (аммо) ўзининг (бир томчи сувдан) яралганини унутиб қўйди'}, {'chapter': 36, 'verse': 79, 'text': 'قُلۡ يُحۡيِيهَا ٱلَّذِيٓ أَنشَأَهَآ أَوَّلَ مَرَّةٖۖ وَهُوَ بِكُلِّ خَلۡقٍ عَلِيمٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «У (чириган суяк)ларни дастлаб (бир томчи сув)дан пайдо қилган Зотнинг Ўзи қайта тирилтирур. У (Ўзи яратган) барча халқни билгувчидир»'}, {'chapter': 36, 'verse': 80, 'text': 'ٱلَّذِي جَعَلَ لَكُم مِّنَ ٱلشَّجَرِ ٱلۡأَخۡضَرِ نَارٗا فَإِذَآ أَنتُم مِّنۡهُ تُوقِدُونَ\nУ сизлар учун ям-яшил дарахтдан олов пайдо қилган Зотдир. Бас, сизлар ўшандан ўт ёқурсизлар'}, {'chapter': 36, 'verse': 81, 'text': 'أَوَلَيۡسَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِقَٰدِرٍ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۚ بَلَىٰ وَهُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِيمُ\nОсмонлар ва Ерни яратган Зот яна уларнинг ўхшашини яратишга қодир эмасми?! Йўқ, (албатта қодирдир), Унинг Ўзигина яратгувчи ва билгувчидир'}, {'chapter': 36, 'verse': 82, 'text': 'إِنَّمَآ أَمۡرُهُۥٓ إِذَآ أَرَادَ شَيۡـًٔا أَن يَقُولَ لَهُۥ كُن فَيَكُونُ\nБирон нарсани (яратишни) ирода қилган вақтида Унинг иши фақатгина «Бўл», демоқлигидир. Бас, у (нарса) бўлур — вужудга келур'}, {'chapter': 36, 'verse': 83, 'text': 'فَسُبۡحَٰنَ ٱلَّذِي بِيَدِهِۦ مَلَكُوتُ كُلِّ شَيۡءٖ وَإِلَيۡهِ تُرۡجَعُونَ\nБас, барча нарсанинг эгалиги Ўз қўлида бўлган (Аллоҳ ҳар қандай айбу-нуқсондан) пок Зотдир. Ва (албатта) сизлар Ёлғиз Унгагина қайтарилурсизлар'}, {'chapter': 37, 'verse': 1, 'text': 'وَٱلصَّـٰٓفَّـٰتِ صَفّٗا\n(Аллоҳ йўлида) саф тортиб турадиган'}, {'chapter': 37, 'verse': 2, 'text': 'فَٱلزَّـٰجِرَٰتِ زَجۡرٗا\n(Аллоҳнинг бандаларини гуноҳ йўлларидан) мудом тўсадиган'}, {'chapter': 37, 'verse': 3, 'text': 'فَٱلتَّـٰلِيَٰتِ ذِكۡرًا\n(Аллоҳнинг каломини) доимо тиловат қиладиган (инсонлар ва фаришталардан иборат барча) зотларга қасамки'}, {'chapter': 37, 'verse': 4, 'text': 'إِنَّ إِلَٰهَكُمۡ لَوَٰحِدٞ\nШак-шубҳасиз, сизларнинг илоҳингиз ягонадир'}, {'chapter': 37, 'verse': 5, 'text': 'رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَرَبُّ ٱلۡمَشَٰرِقِ\n(У зот) осмонлар, Ер ва уларнинг ўртасидаги бор нарсаларнинг Парвардигоридир. (У зот) бор кунчиқар жойларнинг (ва бор кунботар жойларнинг) Парвардигоридир'}, {'chapter': 37, 'verse': 6, 'text': 'إِنَّا زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِزِينَةٍ ٱلۡكَوَاكِبِ\nДарҳақиқат, Биз энг яқин осмонни юлдузлар билан зийнатладик'}, {'chapter': 37, 'verse': 7, 'text': 'وَحِفۡظٗا مِّن كُلِّ شَيۡطَٰنٖ مَّارِدٖ\nВа (уни) ҳар бир итоатсиз шайтондан — жиндан сақладик'}, {'chapter': 37, 'verse': 8, 'text': 'لَّا يَسَّمَّعُونَ إِلَى ٱلۡمَلَإِ ٱلۡأَعۡلَىٰ وَيُقۡذَفُونَ مِن كُلِّ جَانِبٖ\nУ (жин)лар энг юксак мавжудотларни (яъни, фаришталарнинг сўзларини) ҳаргиз эшита олмаслар ва уларга ҳар томондан (юлдузлар) отилур'}, {'chapter': 37, 'verse': 9, 'text': 'دُحُورٗاۖ وَلَهُمۡ عَذَابٞ وَاصِبٌ\n(Уларни) ҳайдаш учун. Улар учун (Охиратда) доимий азоб бордир'}, {'chapter': 37, 'verse': 10, 'text': 'إِلَّا مَنۡ خَطِفَ ٱلۡخَطۡفَةَ فَأَتۡبَعَهُۥ شِهَابٞ ثَاقِبٞ\nМагар бирон марта (бирон сўзни) илиб олган кимса (яъни, жин) бўлса, уни ҳам учар юлдуз қувиб етар (ва ҳалок этар)'}, {'chapter': 37, 'verse': 11, 'text': 'فَٱسۡتَفۡتِهِمۡ أَهُمۡ أَشَدُّ خَلۡقًا أَم مَّنۡ خَلَقۡنَآۚ إِنَّا خَلَقۡنَٰهُم مِّن طِينٖ لَّازِبِۭ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом, Макка кофирларидан) сўранг-чи, улар(ни) яратиш қийинроқмикан ёки Биз яратган зотларними?! (Яъни, фаришталар ва Еру осмондаги барча мавжудотни яратиш одамзотни яратишдан оғирроқ эмасми?!) Зотан, Биз (инсонларнинг асли-аввали бўлмиш Одам алайҳиссаломни) ёпишқоқ бир лойдан яратгандирмиз'}, {'chapter': 37, 'verse': 12, 'text': 'بَلۡ عَجِبۡتَ وَيَسۡخَرُونَ\nБалки сиз (уларнинг қайта тирилишини инкор қилаётганларидан) таажжубдадирсиз, (аммо) улар (сизнинг Охират ҳаёти ҳақидаги сўзларингизни) масхара қилурлар'}, {'chapter': 37, 'verse': 13, 'text': 'وَإِذَا ذُكِّرُواْ لَا يَذۡكُرُونَ\nУларга (Қуръон билан) панд-насиҳат қилинса, панд-насиҳатни олмаслар'}, {'chapter': 37, 'verse': 14, 'text': 'وَإِذَا رَأَوۡاْ ءَايَةٗ يَسۡتَسۡخِرُونَ\n(Сизнинг ҳақ пайғамбар эканлигингизга далолат қиладиган) бирон оят-мўъжизани кўрсалар, (дарҳол иймон келтириш ўрнига) янада зиёдароқ масхара қилурлар'}, {'chapter': 37, 'verse': 15, 'text': 'وَقَالُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٌ\nВа дерлар: «Бу фақат очиқ-равшан сеҳрдир'}, {'chapter': 37, 'verse': 16, 'text': 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ\nБизлар ўлиб тупроқ ва суякларга айланиб кетган чоғимизда ростдан ҳам яна қайта тирилгувчи эканмизми'}, {'chapter': 37, 'verse': 17, 'text': 'أَوَءَابَآؤُنَا ٱلۡأَوَّلُونَ\nАввал ўтиб кетган ота-боболаримиз ҳам-а?! »'}, {'chapter': 37, 'verse': 18, 'text': 'قُلۡ نَعَمۡ وَأَنتُمۡ دَٰخِرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, сиз уларга) айтинг: «Ҳа! Сизлар хор-зор бўлган ҳолингизда (албатта қайта тирилурсизлар)»'}, {'chapter': 37, 'verse': 19, 'text': 'فَإِنَّمَا هِيَ زَجۡرَةٞ وَٰحِدَةٞ فَإِذَا هُمۡ يَنظُرُونَ\nУ (яъни, қайта тирилиш) фақатгина фаришта Исрофилнинг сури билан чалинган бир қичқириқдир, холос, бас, баногоҳ улар (қайта тирилиб, «кунларини») кўрурлар'}, {'chapter': 37, 'verse': 20, 'text': 'وَقَالُواْ يَٰوَيۡلَنَا هَٰذَا يَوۡمُ ٱلدِّينِ\nВа «Эй, ўлим бўлсин бизларга! Бу жазо-Қиёмат Куни-ку!», деб қолурлар'}, {'chapter': 37, 'verse': 21, 'text': 'هَٰذَا يَوۡمُ ٱلۡفَصۡلِ ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ\nМана шу сизлар ёлғон деб ўтган (ҳақ билан ботил) ажратиладиган Кундир'}, {'chapter': 37, 'verse': 22, 'text': '۞ٱحۡشُرُواْ ٱلَّذِينَ ظَلَمُواْ وَأَزۡوَٰجَهُمۡ وَمَا كَانُواْ يَعۡبُدُونَ\n(Эй фаришталар, сизлар) золим-кофир бўлган кимсалар ва уларнинг ҳамтовоқларини ҳамда улар ибодат қилиб ўтган нарсаларини тўпланглар'}, {'chapter': 37, 'verse': 23, 'text': 'مِن دُونِ ٱللَّهِ فَٱهۡدُوهُمۡ إِلَىٰ صِرَٰطِ ٱلۡجَحِيمِ\nАллоҳни қўйиб (ибодат қилиб ўтган нарсаларини тўпланглар) ва дўзах йўлига бошланглар'}, {'chapter': 37, 'verse': 24, 'text': 'وَقِفُوهُمۡۖ إِنَّهُم مَّسۡـُٔولُونَ\n(Аммо то Менинг фармоним бўлмагунча) уларни тўхтатиб (дўзахга ташламай) туринглар! Чунки улар (аввал ҳаёти дунёда қилиб ўтган куфру исёнлари тўғрисида) сўралгувчидирлар'}, {'chapter': 37, 'verse': 25, 'text': 'مَا لَكُمۡ لَا تَنَاصَرُونَ\n(У Кунда кофирларга дейилар): «Нега бир-бирларингизга ёрдам бермаяпсизлар?!»'}, {'chapter': 37, 'verse': 26, 'text': 'بَلۡ هُمُ ٱلۡيَوۡمَ مُسۡتَسۡلِمُونَ\nЙўқ, улар бу Кунда (ҳеч нарса қила олмаслар, (балки фақат Бизнинг ҳукмимизга) бўйинсунгувчидирлар'}, {'chapter': 37, 'verse': 27, 'text': 'وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ\nУларнинг бировлари бировларига қараб савол-жавоб қилурлар'}, {'chapter': 37, 'verse': 28, 'text': 'قَالُوٓاْ إِنَّكُمۡ كُنتُمۡ تَأۡتُونَنَا عَنِ ٱلۡيَمِينِ\n(Эргашувчилар ўзларини йўлдан урган пешволарига): «Ҳақиқатан, сизлар бизларга ўнг томондан (яъни, бизлар ишониб яхшилик кутадиган томондан) келар эдинглар», деганларида'}, {'chapter': 37, 'verse': 29, 'text': 'قَالُواْ بَل لَّمۡ تَكُونُواْ مُؤۡمِنِينَ\n(Пешволар) айтурлар: «Йўқ, сизлар ўзларингиз мўмин бўлмаган эдинглар'}, {'chapter': 37, 'verse': 30, 'text': 'وَمَا كَانَ لَنَا عَلَيۡكُم مِّن سُلۡطَٰنِۭۖ بَلۡ كُنتُمۡ قَوۡمٗا طَٰغِينَ\nАхир бизлар учун сизларнинг устингизда бирон салтанат — ҳукмронлик йўқ эди-ку! Йўқ, сизлар ўзларингиз ҳаддан ошгувчи қавм эдинглар'}, {'chapter': 37, 'verse': 31, 'text': 'فَحَقَّ عَلَيۡنَا قَوۡلُ رَبِّنَآۖ إِنَّا لَذَآئِقُونَ\nБас, энди (бизларга ҳам, сизларга ҳам) Парвардигоримизнинг Сўзи — азоби ҳақ бўлди. Шак-шубҳасиз, бизлар (уни) тотгувчидирмиз'}, {'chapter': 37, 'verse': 32, 'text': 'فَأَغۡوَيۡنَٰكُمۡ إِنَّا كُنَّا غَٰوِينَ\nЧунки бизлар йўлдан озгувчи бўлганимиз сабабли сизларни ҳам йўлдан урдик»'}, {'chapter': 37, 'verse': 33, 'text': 'فَإِنَّهُمۡ يَوۡمَئِذٖ فِي ٱلۡعَذَابِ مُشۡتَرِكُونَ\nБас, албатта улар (яъни, Аллоҳ йўлидан озган кимсалар ва уларни йўлдан урган пешволари) ўша Кунда азобда шерик бўлгувчидирлар'}, {'chapter': 37, 'verse': 34, 'text': 'إِنَّا كَذَٰلِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِينَ\nДарҳақиқат, Биз жиноятчи кимсаларни (мана шундай) азобга дучор қилурмиз'}, {'chapter': 37, 'verse': 35, 'text': 'إِنَّهُمۡ كَانُوٓاْ إِذَا قِيلَ لَهُمۡ لَآ إِلَٰهَ إِلَّا ٱللَّهُ يَسۡتَكۡبِرُونَ\nЧунки улар ўзларича: «Ҳеч қандай илоҳ йўқ, магар ёлғиз Аллоҳгина бордир», дейилган вақтда кибр-ҳаво қилган эдилар'}, {'chapter': 37, 'verse': 36, 'text': 'وَيَقُولُونَ أَئِنَّا لَتَارِكُوٓاْ ءَالِهَتِنَا لِشَاعِرٖ مَّجۡنُونِۭ\nВа улар «Ҳали бизлар бир мажнун шоирни деб худоларимизни тарк қилар эканмизми?», дер эдилар'}, {'chapter': 37, 'verse': 37, 'text': 'بَلۡ جَآءَ بِٱلۡحَقِّ وَصَدَّقَ ٱلۡمُرۡسَلِينَ\nЙўқ, (Муҳаммад алайҳис-салоту вас-салом асло шоири мажнун эмасдир, балки) у Ҳақ (дин)ни олиб келди ва (ўзидан аввал ўтган) барча пайғамбарларни тасдиқ этди'}, {'chapter': 37, 'verse': 38, 'text': 'إِنَّكُمۡ لَذَآئِقُواْ ٱلۡعَذَابِ ٱلۡأَلِيمِ\n(Эй жиноятчи кимсалар), сизлар шак-шубҳасиз, аламли азобни тотгувчидирсизлар'}, {'chapter': 37, 'verse': 39, 'text': 'وَمَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ\nСизлар фақат қилиб ўтган жиноятларингиз сабабли жазоланурсизлар'}, {'chapter': 37, 'verse': 40, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг покиза бандалари бордирки'}, {'chapter': 37, 'verse': 41, 'text': 'أُوْلَـٰٓئِكَ لَهُمۡ رِزۡقٞ مَّعۡلُومٞ\nАна ўшалар учун маълум ризқ (бўлур)'}, {'chapter': 37, 'verse': 42, 'text': 'فَوَٰكِهُ وَهُم مُّكۡرَمُونَ\n(Ҳар турли) мевалар бўлур ва улар ҳурмат-иззат кўргувчидирлар'}, {'chapter': 37, 'verse': 43, 'text': 'فِي جَنَّـٰتِ ٱلنَّعِيمِ\nВа улар ноз-неъмат боғларида'}, {'chapter': 37, 'verse': 44, 'text': 'عَلَىٰ سُرُرٖ مُّتَقَٰبِلِينَ\nСўрилар устида бир-бирларига рўбарў ўтирган ҳолларида (ҳурмат-иззат кўргувчидирлар)'}, {'chapter': 37, 'verse': 45, 'text': 'يُطَافُ عَلَيۡهِم بِكَأۡسٖ مِّن مَّعِينِۭ\nУларга оқар чашмадан (шароб) косалари айлантириб турилур'}, {'chapter': 37, 'verse': 46, 'text': 'بَيۡضَآءَ لَذَّةٖ لِّلشَّـٰرِبِينَ\nИчгувчилар учун лаззат бўлган оппоқ (шароб косалари айлантириб турилурки)'}, {'chapter': 37, 'verse': 47, 'text': 'لَا فِيهَا غَوۡلٞ وَلَا هُمۡ عَنۡهَا يُنزَفُونَ\nУ (шароб)да маст қилгувчи — ақлдан оздиргувчи нарса бўлмас ва улар ундан маст бўлмаслар'}, {'chapter': 37, 'verse': 48, 'text': 'وَعِندَهُمۡ قَٰصِرَٰتُ ٱلطَّرۡفِ عِينٞ\nЯна уларнинг олдиларида кўзларини (бегоналарга қарашдан) тийгувчи'}, {'chapter': 37, 'verse': 49, 'text': 'كَأَنَّهُنَّ بَيۡضٞ مَّكۡنُونٞ\nГўё яшириб қўйилган (оппоқ) тухумдек (заҳаланмаган) оҳу кўзлар бўлур'}, {'chapter': 37, 'verse': 50, 'text': 'فَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ\nБас, улар (яъни, аҳли жаннат) бир-бирларига қараб савол-жавоб қилурлар'}, {'chapter': 37, 'verse': 51, 'text': 'قَالَ قَآئِلٞ مِّنۡهُمۡ إِنِّي كَانَ لِي قَرِينٞ\n(Шундай суҳбатларнинг бирида) улардан бири деди: «Менинг бир дўстим бор эди'}, {'chapter': 37, 'verse': 52, 'text': 'يَقُولُ أَءِنَّكَ لَمِنَ ٱلۡمُصَدِّقِينَ\nУ (менга) «Ростдан ҳам сен (ўлганимиздан сўнг яна қайта тирилишимизни) тасдиқ этгувчиларданмисан'}, {'chapter': 37, 'verse': 53, 'text': 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَدِينُونَ\nБизлар ўлиб, тупроқ ва суякларга айланиб кетган вақтимизда ҳақиқатан ҳам (қайта тирилтирилиб) жазолангучимизми?», дер эди'}, {'chapter': 37, 'verse': 54, 'text': 'قَالَ هَلۡ أَنتُم مُّطَّلِعُونَ\n(Сўзлагувчи ҳаёти дунёда Охиратни инкор қилган дўсти ҳақидаги ҳикояни тугатгач, жаннатдаги биродарларига): Сизлар (ҳам мен билан бирга ўша дўстимнинг дўзахдаги ҳоли нима бўлганига) қарагувчимисизлар?», деди'}, {'chapter': 37, 'verse': 55, 'text': 'فَٱطَّلَعَ فَرَءَاهُ فِي سَوَآءِ ٱلۡجَحِيمِ\nСўнг ўзи қараб уни дўзахнинг ўртасида кўргач'}, {'chapter': 37, 'verse': 56, 'text': 'قَالَ تَٱللَّهِ إِن كِدتَّ لَتُرۡدِينِ\nДеди: «Аллоҳ номига қасамки, дарҳақиқат, сен мени ҳам ҳалок қилаёзган эдинг'}, {'chapter': 37, 'verse': 57, 'text': 'وَلَوۡلَا نِعۡمَةُ رَبِّي لَكُنتُ مِنَ ٱلۡمُحۡضَرِينَ\nАгар Парвардигоримнинг неъмати (яъни, ҳидояти) бўлмаса эди, албатта мен (сен билан бирга дўзахга) ҳозир қилингувчилардан бўлур эдим'}, {'chapter': 37, 'verse': 58, 'text': 'أَفَمَا نَحۡنُ بِمَيِّتِينَ\n(Қани сен менга айтгинчи), бизлар (яъни, жаннат аҳли худди ҳаёти дунёда бизга ваъда қилинганидек), ўлгувчи эмас эканмизми'}, {'chapter': 37, 'verse': 59, 'text': 'إِلَّا مَوۡتَتَنَا ٱلۡأُولَىٰ وَمَا نَحۡنُ بِمُعَذَّبِينَ\nФақат биринчи ўлимимиздан бошқа (ўлгувчи эмаслигимиз рост эканми?). Бизлар (ростдан ҳам) азоблангувчи эмас эканмизми'}, {'chapter': 37, 'verse': 60, 'text': 'إِنَّ هَٰذَا لَهُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nАлбатта мана шу (аҳли жаннат етадиган неъматлар) улуғ бахтнинг ўзидир'}, {'chapter': 37, 'verse': 61, 'text': 'لِمِثۡلِ هَٰذَا فَلۡيَعۡمَلِ ٱلۡعَٰمِلُونَ\nБас, амал қилгувчилар мана шундай (мангу бахт саодат) учун амал қилсинлар'}, {'chapter': 37, 'verse': 62, 'text': 'أَذَٰلِكَ خَيۡرٞ نُّزُلًا أَمۡ شَجَرَةُ ٱلزَّقُّومِ\nМана шу яхшироқ зиёфатми ёки заққум дарахтими'}, {'chapter': 37, 'verse': 63, 'text': 'إِنَّا جَعَلۡنَٰهَا فِتۡنَةٗ لِّلظَّـٰلِمِينَ\nҲақиқатан, Биз уни (яъни, Заққум дарахтини) золим кимсалар учун фитна-алдов қилиб қўйдик'}, {'chapter': 37, 'verse': 64, 'text': 'إِنَّهَا شَجَرَةٞ تَخۡرُجُ فِيٓ أَصۡلِ ٱلۡجَحِيمِ\nДарҳақиқат, у дўзах қаърида ўсадиган бир дарахтдир'}, {'chapter': 37, 'verse': 65, 'text': 'طَلۡعُهَا كَأَنَّهُۥ رُءُوسُ ٱلشَّيَٰطِينِ\nУнинг бутоқлари(даги мевалари хунукликда) худди шайтонларнинг бошларига ўхшар'}, {'chapter': 37, 'verse': 66, 'text': 'فَإِنَّهُمۡ لَأٓكِلُونَ مِنۡهَا فَمَالِـُٔونَ مِنۡهَا ٱلۡبُطُونَ\nБас, улар (яъни, дўзахилар) албатта ундан еб, қоринларини тўлдиргувчидирлар'}, {'chapter': 37, 'verse': 67, 'text': 'ثُمَّ إِنَّ لَهُمۡ عَلَيۡهَا لَشَوۡبٗا مِّنۡ حَمِيمٖ\nСўнгра албатта улар учун унинг (Заққум меваларининг) устига қайноқ сувдан аралаштирилур (яъни, уларга мажбуран қайнаётган сув ичирилур)'}, {'chapter': 37, 'verse': 68, 'text': 'ثُمَّ إِنَّ مَرۡجِعَهُمۡ لَإِلَى ٱلۡجَحِيمِ\nСўнгра уларнинг қайтадиган жойлари яна албатта дўзахда бўлур (яъни, қайноқ сув уларнинг бутун ички аъзоларини куйдириб битганидан сўнг, энди дўзах ўти уларнинг баданларини куйдирур)'}, {'chapter': 37, 'verse': 69, 'text': 'إِنَّهُمۡ أَلۡفَوۡاْ ءَابَآءَهُمۡ ضَآلِّينَ\nАлбатта улар (яъни, Макка мушриклари) ўзларининг ота-боболарини йўлдан озган ҳолда топдилар'}, {'chapter': 37, 'verse': 70, 'text': 'فَهُمۡ عَلَىٰٓ ءَاثَٰرِهِمۡ يُهۡرَعُونَ\nЭнди ўзлари ҳам уларнинг изларидан чопмоқдалар'}, {'chapter': 37, 'verse': 71, 'text': 'وَلَقَدۡ ضَلَّ قَبۡلَهُمۡ أَكۡثَرُ ٱلۡأَوَّلِينَ\nАниқки, улардан аввал ўтганларнинг кўплари ҳам йўлдан озган эдилар'}, {'chapter': 37, 'verse': 72, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا فِيهِم مُّنذِرِينَ\nҲолбуки, Биз уларнинг (яъни, аввал ўтганларнинг) орасига огоҳлантиргувчи (пайғамбар)ларни юборган эдик'}, {'chapter': 37, 'verse': 73, 'text': 'فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُنذَرِينَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), огоҳлантирилган кимсаларнинг оқибатлари қандай бўлганини кўринг'}, {'chapter': 37, 'verse': 74, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг покиза бандаларигина (ҳалокатга дучор бўлмадилар)'}, {'chapter': 37, 'verse': 75, 'text': 'وَلَقَدۡ نَادَىٰنَا نُوحٞ فَلَنِعۡمَ ٱلۡمُجِيبُونَ\nҚасамки, Нуҳ (қавми уни ёлғончи қилган вақтда) Бизга илтижо қилди. Бас, (Биз) нақадар яхши ижобат қилгувчидирмиз'}, {'chapter': 37, 'verse': 76, 'text': 'وَنَجَّيۡنَٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ\nБиз унга ва унинг аҳли-тобеъларига улуғ ғамдан (яъни, ғарқ бўлишдан) нажот бердик'}, {'chapter': 37, 'verse': 77, 'text': 'وَجَعَلۡنَا ذُرِّيَّتَهُۥ هُمُ ٱلۡبَاقِينَ\nВа унинг зурриётини (то Қиёмат) боқий қолгувчи кишилар қилиб қўйдик'}, {'chapter': 37, 'verse': 78, 'text': 'وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ\nВа кейинги (авлод)лар орасида (Нуҳ) ҳақида (гўзал мақтовлар-олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 79, 'text': 'سَلَٰمٌ عَلَىٰ نُوحٖ فِي ٱلۡعَٰلَمِينَ\nБарча оламларда Нуҳга салом бўлгай'}, {'chapter': 37, 'verse': 80, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nАлбатта Биз чиройли амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 81, 'text': 'إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nДарҳақиқат, у Бизнинг мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 82, 'text': 'ثُمَّ أَغۡرَقۡنَا ٱلۡأٓخَرِينَ\nСўнгра бошқаларни (яъни, Нуҳни ёлғончи қилган кимсаларни) ғарқ қилиб юбордик'}, {'chapter': 37, 'verse': 83, 'text': '۞وَإِنَّ مِن شِيعَتِهِۦ لَإِبۡرَٰهِيمَ\nШак-шубҳасиз, Иброҳим ҳам (Нуҳ)нинг гуруҳидандир'}, {'chapter': 37, 'verse': 84, 'text': 'إِذۡ جَآءَ رَبَّهُۥ بِقَلۡبٖ سَلِيمٍ\nЭсланг, у Парвардигорига тоза дил билан келди'}, {'chapter': 37, 'verse': 85, 'text': 'إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَاذَا تَعۡبُدُونَ\nЎшанда у отаси ва қавмига деган эди: «Нимага ибодат қилмоқдасизлар'}, {'chapter': 37, 'verse': 86, 'text': 'أَئِفۡكًا ءَالِهَةٗ دُونَ ٱللَّهِ تُرِيدُونَ\nАллоҳни қўйиб, сохта «худолар»га (сиғинишни) истайсизларми'}, {'chapter': 37, 'verse': 87, 'text': 'فَمَا ظَنُّكُم بِرَبِّ ٱلۡعَٰلَمِينَ\nУ ҳолда барча оламларнинг Парвардигори ҳақида (яъни, У зот сизларнинг мушриклигингиз учун берадиган жазоси ҳақида) не гумонингиз бор?!»'}, {'chapter': 37, 'verse': 88, 'text': 'فَنَظَرَ نَظۡرَةٗ فِي ٱلنُّجُومِ\nСўнг у юлдузларга тикилиб туриб'}, {'chapter': 37, 'verse': 89, 'text': 'فَقَالَ إِنِّي سَقِيمٞ\n«Аниқки, мен касалман», деди'}, {'chapter': 37, 'verse': 90, 'text': 'فَتَوَلَّوۡاْ عَنۡهُ مُدۡبِرِينَ\nДарҳол ундан юз ўгирган ҳолларида кетдилар'}, {'chapter': 37, 'verse': 91, 'text': 'فَرَاغَ إِلَىٰٓ ءَالِهَتِهِمۡ فَقَالَ أَلَا تَأۡكُلُونَ\nБас, (Иброҳим) аста уларнинг бутлари олдига келиб: «(Олдиларингиздаги) таомларни емайсизларми'}, {'chapter': 37, 'verse': 92, 'text': 'مَا لَكُمۡ لَا تَنطِقُونَ\nНега сўзламаяпсизлар?!», деди'}, {'chapter': 37, 'verse': 93, 'text': 'فَرَاغَ عَلَيۡهِمۡ ضَرۡبَۢا بِٱلۡيَمِينِ\nСўнг уларга яқин келиб, ўнг қўли(даги болта) билан урди'}, {'chapter': 37, 'verse': 94, 'text': 'فَأَقۡبَلُوٓاْ إِلَيۡهِ يَزِفُّونَ\nБас, (қавми) унинг олдига шоша-пиша келишгач'}, {'chapter': 37, 'verse': 95, 'text': 'قَالَ أَتَعۡبُدُونَ مَا تَنۡحِتُونَ\nУ деди: «Ўзларингиз йўниб-ясаб олган нарсаларга ибодат қилурмисизлар'}, {'chapter': 37, 'verse': 96, 'text': 'وَٱللَّهُ خَلَقَكُمۡ وَمَا تَعۡمَلُونَ\nҲолбуки, сизларни ҳам, қилиб олган бутларингизни ҳам Аллоҳ яратган-ку?!»'}, {'chapter': 37, 'verse': 97, 'text': 'قَالُواْ ٱبۡنُواْ لَهُۥ بُنۡيَٰنٗا فَأَلۡقُوهُ فِي ٱلۡجَحِيمِ\nУлар (бир-бирларига) дедилар: Иброҳим учун (олов ёқиладиган) бинолар қуриб уни ўша оловга ташланглар»'}, {'chapter': 37, 'verse': 98, 'text': 'فَأَرَادُواْ بِهِۦ كَيۡدٗا فَجَعَلۡنَٰهُمُ ٱلۡأَسۡفَلِينَ\nБас, улар (Иброҳимга) макр қилмоқчи бўлган эдилар. Биз уларни тубан-мағлуб қилдик'}, {'chapter': 37, 'verse': 99, 'text': 'وَقَالَ إِنِّي ذَاهِبٌ إِلَىٰ رَبِّي سَيَهۡدِينِ\n(У ўт ичидан эсон-омон чиқди) ва деди: «Албатта мен Парвардигорим (буюрган тараф)га кетгувчидирман. Унинг Ўзи мени (Тўғри Йўлга) ҳидоят қилур'}, {'chapter': 37, 'verse': 100, 'text': 'رَبِّ هَبۡ لِي مِنَ ٱلصَّـٰلِحِينَ\n(Парвардигорим,) Ўзинг менга солиҳ (фарзанд)лардан ҳадя этгин»'}, {'chapter': 37, 'verse': 101, 'text': 'فَبَشَّرۡنَٰهُ بِغُلَٰمٍ حَلِيمٖ\nБас, Биз унга бир ҳалим ўғилнинг хушхабарини бердик'}, {'chapter': 37, 'verse': 102, 'text': 'فَلَمَّا بَلَغَ مَعَهُ ٱلسَّعۡيَ قَالَ يَٰبُنَيَّ إِنِّيٓ أَرَىٰ فِي ٱلۡمَنَامِ أَنِّيٓ أَذۡبَحُكَ فَٱنظُرۡ مَاذَا تَرَىٰۚ قَالَ يَـٰٓأَبَتِ ٱفۡعَلۡ مَا تُؤۡمَرُۖ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰبِرِينَ\nЭнди қачонки у (бола Иброҳим) билан бирга юрадиган бўлгач, (Иброҳим): «Эй ўғилчам, мен (ҳадеб) тушимда сени (қурбонлик учун) сўяётганимни кўрмоқдаман. Энди сен ўзинг нима раъй-фикр қилишингни бир ўйлаб кўргин», деган эди. У айтди: «Эй отажон, сенга (тушингда Парвардигор томонидан) буюрилган ишни қилгин. Иншоаллоҳ мени сабр қилгувчилардан топурсан»'}, {'chapter': 37, 'verse': 103, 'text': 'فَلَمَّآ أَسۡلَمَا وَتَلَّهُۥ لِلۡجَبِينِ\nБас, қачонки иккиси ҳам (Аллоҳнинг ваҳийсига) бўйинсуниб, (энди Иброҳим ўз ўғли Исмоилни қурбон қилиш учун) пешонаси билан (ерга) ётқизган эдики'}, {'chapter': 37, 'verse': 104, 'text': 'وَنَٰدَيۡنَٰهُ أَن يَـٰٓإِبۡرَٰهِيمُ\nБиз унга нидо қилдик: «Эй Иброҳим'}, {'chapter': 37, 'verse': 105, 'text': 'قَدۡ صَدَّقۡتَ ٱلرُّءۡيَآۚ إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nДарҳақиқат, сен (кўрган) тушингни рост-бажо қилдинг». Албатта Биз чиройли амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 106, 'text': 'إِنَّ هَٰذَا لَهُوَ ٱلۡبَلَـٰٓؤُاْ ٱلۡمُبِينُ\nАлбатта бу (яъни, Иброҳимнинг ўз ўғлини қурбон қилишга буюрилиши) очиқ-равшан имтиҳондир, холос'}, {'chapter': 37, 'verse': 107, 'text': 'وَفَدَيۡنَٰهُ بِذِبۡحٍ عَظِيمٖ\nБиз (Исмоилнинг) ўрнига (Иброҳимга) катта бир (кўчқор) сўйишни — қурбонлиқни эваз қилиб бердик (яъни, катта бир қўчқорни жаннатдан туширдик)'}, {'chapter': 37, 'verse': 108, 'text': 'وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ\nВа кейинги (авлод)лар орасида (Иброҳим) ҳақида (гўзал мақтовлар-олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 109, 'text': 'سَلَٰمٌ عَلَىٰٓ إِبۡرَٰهِيمَ\nИброҳимга салом бўлгай'}, {'chapter': 37, 'verse': 110, 'text': 'كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nБиз чиройли амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 111, 'text': 'إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nДарҳақиқат, у Бизнинг мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 112, 'text': 'وَبَشَّرۡنَٰهُ بِإِسۡحَٰقَ نَبِيّٗا مِّنَ ٱلصَّـٰلِحِينَ\nЯна Биз унга пайғамбар (ва) яхшилардан (бўлғуси) Исҳоқнинг хушхабарини бердик'}, {'chapter': 37, 'verse': 113, 'text': 'وَبَٰرَكۡنَا عَلَيۡهِ وَعَلَىٰٓ إِسۡحَٰقَۚ وَمِن ذُرِّيَّتِهِمَا مُحۡسِنٞ وَظَالِمٞ لِّنَفۡسِهِۦ مُبِينٞ\nВа (Иброҳимга) ҳам, Исҳоққа ҳам (дину дунёларида) баракот бердик. Уларнинг зурриётидан чиройли амал қилгувчи ҳам, ўз жонига очиқ жабр қилгувчи ҳам бўлур'}, {'chapter': 37, 'verse': 114, 'text': 'وَلَقَدۡ مَنَنَّا عَلَىٰ مُوسَىٰ وَهَٰرُونَ\nДарҳақиқат, Биз Мусо ва Ҳорунга (пайғамбарлик) инъом қилдик'}, {'chapter': 37, 'verse': 115, 'text': 'وَنَجَّيۡنَٰهُمَا وَقَوۡمَهُمَا مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ\nБиз иккисига ва уларнинг қавмига улуғ ғамдан нажот бердик'}, {'chapter': 37, 'verse': 116, 'text': 'وَنَصَرۡنَٰهُمۡ فَكَانُواْ هُمُ ٱلۡغَٰلِبِينَ\nВа уларни Ўзимиз қўлладик, бас, улар (Фиръавн ва унинг одамлари устидан) ғолиб бўлдилар'}, {'chapter': 37, 'verse': 117, 'text': 'وَءَاتَيۡنَٰهُمَا ٱلۡكِتَٰبَ ٱلۡمُسۡتَبِينَ\nБиз уларга равшан Китоб — Таврот ато этдик'}, {'chapter': 37, 'verse': 118, 'text': 'وَهَدَيۡنَٰهُمَا ٱلصِّرَٰطَ ٱلۡمُسۡتَقِيمَ\nҲамда уларни Тўғри Йўлга ҳидоят қилдик'}, {'chapter': 37, 'verse': 119, 'text': 'وَتَرَكۡنَا عَلَيۡهِمَا فِي ٱلۡأٓخِرِينَ\nВа кейинги (авлод)лар орасида (Мусо ва Ҳорун) ҳақида (гўзал мақтовлар — олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 120, 'text': 'سَلَٰمٌ عَلَىٰ مُوسَىٰ وَهَٰرُونَ\nМусо ва Ҳорунга салом бўлгай'}, {'chapter': 37, 'verse': 121, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nАлбатта Биз чиройли амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 122, 'text': 'إِنَّهُمَا مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nДарҳақиқат, иккиси ҳам Бизнинг мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 123, 'text': 'وَإِنَّ إِلۡيَاسَ لَمِنَ ٱلۡمُرۡسَلِينَ\nИлёс ҳам шак-шубҳасиз, пайғамбарлардандир'}, {'chapter': 37, 'verse': 124, 'text': 'إِذۡ قَالَ لِقَوۡمِهِۦٓ أَلَا تَتَّقُونَ\nЭсланг, у ўз қавмига деган эди: «(Аллоҳдан) қўрқмайсизларми?!»'}, {'chapter': 37, 'verse': 125, 'text': 'أَتَدۡعُونَ بَعۡلٗا وَتَذَرُونَ أَحۡسَنَ ٱلۡخَٰلِقِينَ\nСизлар Баъл (номли олтиндан ясалган бут)га сиғиниб, энг гўзал-афзал Яратгувчи Аллоҳни тарк қилурмисизлар'}, {'chapter': 37, 'verse': 126, 'text': 'ٱللَّهَ رَبَّكُمۡ وَرَبَّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ\nСизларнинг ҳам, аввалги ота-боболарингизнинг ҳам Парвардигорини (тарк қилурмисизлар?!)»'}, {'chapter': 37, 'verse': 127, 'text': 'فَكَذَّبُوهُ فَإِنَّهُمۡ لَمُحۡضَرُونَ\nБас, (қавми Илёсни) ёлғончи қилдилар. Энди албатта улар (дўзах азобига) ҳозир қилингувчидирлар'}, {'chapter': 37, 'verse': 128, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг покиза бандаларигина (азобга дучор бўлмаслар)'}, {'chapter': 37, 'verse': 129, 'text': 'وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ\nБиз кейинги (авлод)лар орасида (Илёс) ҳақида (гўзал мақтовлар-олқишлар) қолдирдик'}, {'chapter': 37, 'verse': 130, 'text': 'سَلَٰمٌ عَلَىٰٓ إِلۡ يَاسِينَ\nИлёсга салом бўлгай'}, {'chapter': 37, 'verse': 131, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nАлбатта, чиройли амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 37, 'verse': 132, 'text': 'إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ\nАлбатта, у Бизнинг мўмин бандаларимиздандир'}, {'chapter': 37, 'verse': 133, 'text': 'وَإِنَّ لُوطٗا لَّمِنَ ٱلۡمُرۡسَلِينَ\nЛут ҳам, шак-шубҳасиз, пайғамбарлардандир'}, {'chapter': 37, 'verse': 134, 'text': 'إِذۡ نَجَّيۡنَٰهُ وَأَهۡلَهُۥٓ أَجۡمَعِينَ\nЭсланг, Биз унга в унинг барча аҳли-тобеъларига нажот берган эдик'}, {'chapter': 37, 'verse': 135, 'text': 'إِلَّا عَجُوزٗا فِي ٱلۡغَٰبِرِينَ\nФақат (азобда) қолгувчилардан бўлган бир кампирга (яъни, Лутнинг хотинига нажот бермадик)'}, {'chapter': 37, 'verse': 136, 'text': 'ثُمَّ دَمَّرۡنَا ٱلۡأٓخَرِينَ\nСўнгра бошқаларни (яъни, Лутни ёлғончи қилганларни) ҳалок қилдик'}, {'chapter': 37, 'verse': 137, 'text': 'وَإِنَّكُمۡ لَتَمُرُّونَ عَلَيۡهِم مُّصۡبِحِينَ\n(Эй Макка аҳли,) аниқки, сизлар эрта-ю кеч уларнинг устидан ўтиб турурсизлар'}, {'chapter': 37, 'verse': 138, 'text': 'وَبِٱلَّيۡلِۚ أَفَلَا تَعۡقِلُونَ\nАхир ақл юргизмайсизларми (яъни, Лутнинг қавми бошига тушган ҳалокат сизларнинг ҳам бошингизга тушиб қолишидан қўрқмайсизларми)'}, {'chapter': 37, 'verse': 139, 'text': 'وَإِنَّ يُونُسَ لَمِنَ ٱلۡمُرۡسَلِينَ\nЮнус ҳам, шак-шубҳасиз, пайғамбарлардандир'}, {'chapter': 37, 'verse': 140, 'text': 'إِذۡ أَبَقَ إِلَى ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ\nЭсланг, у (ўз қавмидан ғазабланиб, Парвардигорининг изнисиз қишлоғидан чиқиб, (одамлар билан) тўла бўлган кемага қараб қочган эди'}, {'chapter': 37, 'verse': 141, 'text': 'فَسَاهَمَ فَكَانَ مِنَ ٱلۡمُدۡحَضِينَ\nБас, (кемадагилар билан) қуръа ташлашиб мағлуб бўлгач, (уни денгизга улоқтирдилар)'}, {'chapter': 37, 'verse': 142, 'text': 'فَٱلۡتَقَمَهُ ٱلۡحُوتُ وَهُوَ مُلِيمٞ\nБас, уни (Парвардигорининг изнисиз ўз қавмини ташлаб чиқиб кетгани сабабли) маломатга лойиқ бўлган ҳолида бир наҳанг балиқ ютиб юборди'}, {'chapter': 37, 'verse': 143, 'text': 'فَلَوۡلَآ أَنَّهُۥ كَانَ مِنَ ٱلۡمُسَبِّحِينَ\nЭнди агар у (Аллоҳга доимо) тасбеҳ айтгувчилардан бўлмаса эди'}, {'chapter': 37, 'verse': 144, 'text': 'لَلَبِثَ فِي بَطۡنِهِۦٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\nАлбатта у (балиқ) қорнида то қайта тириладиган кунларигача (яъни, Қиёматгача) қолиб кетган (яъни, ҳалок бўлган) бўлур эди'}, {'chapter': 37, 'verse': 145, 'text': '۞فَنَبَذۡنَٰهُ بِٱلۡعَرَآءِ وَهُوَ سَقِيمٞ\nБас, Биз уни хаста ҳолида қуруқликка отдик'}, {'chapter': 37, 'verse': 146, 'text': 'وَأَنۢبَتۡنَا عَلَيۡهِ شَجَرَةٗ مِّن يَقۡطِينٖ\nВа унинг устида (соя ташлаб туриши учун) қовоқ дарахтини ўстириб қўйдик'}, {'chapter': 37, 'verse': 147, 'text': 'وَأَرۡسَلۡنَٰهُ إِلَىٰ مِاْئَةِ أَلۡفٍ أَوۡ يَزِيدُونَ\nБиз уни юз минг, балки ундан-да кўпроқ (одамга) пайғамбар қилдик'}, {'chapter': 37, 'verse': 148, 'text': 'فَـَٔامَنُواْ فَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ\nБас, улар (Юнусга) иймон келтирдилар. Сўнг Биз уларни (маълум) бир муддатгача (яъни, ажаллари битгунча ҳаёт неъматидан) баҳраманд этдик'}, {'chapter': 37, 'verse': 149, 'text': 'فَٱسۡتَفۡتِهِمۡ أَلِرَبِّكَ ٱلۡبَنَاتُ وَلَهُمُ ٱلۡبَنُونَ\nЭнди (эй Муҳаммад алайҳис-салоту вас-салом, Макка кофирларидан) сўранг-чи, қизлар Парвардигорингизники-ю, ўғиллар уларникимикан. (Яъни, уларнинг гумонича, фаришталар Аллоҳнинг қизлари эмиш)'}, {'chapter': 37, 'verse': 150, 'text': 'أَمۡ خَلَقۡنَا ٱلۡمَلَـٰٓئِكَةَ إِنَٰثٗا وَهُمۡ شَٰهِدُونَ\nЁки Биз фаришталарни улар гувоҳ бўлган ҳолларида қиз қилиб яратдикмикан'}, {'chapter': 37, 'verse': 151, 'text': 'أَلَآ إِنَّهُم مِّنۡ إِفۡكِهِمۡ لَيَقُولُونَ\nОгоҳ бўлингизким, улар ёлғончиликлари туфайли айтурларки'}, {'chapter': 37, 'verse': 152, 'text': 'وَلَدَ ٱللَّهُ وَإِنَّهُمۡ لَكَٰذِبُونَ\n«Аллоҳнинг боласи (яъни, қизлари) бор». Улар шак-шубҳасиз, ёлғончидирлар'}, {'chapter': 37, 'verse': 153, 'text': 'أَصۡطَفَى ٱلۡبَنَاتِ عَلَى ٱلۡبَنِينَ\n(Аллоҳ) ўғилларни қўйиб, қизларни танлаб олган эмишми'}, {'chapter': 37, 'verse': 154, 'text': 'مَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ\n(Эй Макка аҳли), сизларга нима бўлди?! Қандай (ноҳақ-нораво) ҳукм чиқармоқдасизлар-а'}, {'chapter': 37, 'verse': 155, 'text': 'أَفَلَا تَذَكَّرُونَ\nАхир эс-ҳушингизни йиғмайсизларми'}, {'chapter': 37, 'verse': 156, 'text': 'أَمۡ لَكُمۡ سُلۡطَٰنٞ مُّبِينٞ\nЁки сизлар учун (Аллоҳ бола кўргани ҳақида) бирон очиқ-равшан ҳужжат борми'}, {'chapter': 37, 'verse': 157, 'text': 'فَأۡتُواْ بِكِتَٰبِكُمۡ إِن كُنتُمۡ صَٰدِقِينَ\nБас, агар ростгўй бўлсангизлар (мана шу даъволарингизни қувватловчи) китобингизни келтиринглар-чи'}, {'chapter': 37, 'verse': 158, 'text': 'وَجَعَلُواْ بَيۡنَهُۥ وَبَيۡنَ ٱلۡجِنَّةِ نَسَبٗاۚ وَلَقَدۡ عَلِمَتِ ٱلۡجِنَّةُ إِنَّهُمۡ لَمُحۡضَرُونَ\n(Мушриклар, Аллоҳ) билан жинлар (яъни, фаришталар) ўртасида насаб (яъни, ота-болалик бор, деб гумон) қилдилар. Ҳолбуки, жинлар-фаришталар эса, (мушрикларнинг Аллоҳ азобига) ҳозир қилингувчи эканликларини аниқ билдилар'}, {'chapter': 37, 'verse': 159, 'text': 'سُبۡحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ\nАллоҳ уларнинг («Аллоҳнинг боласи бор», деб) сифатлашларидан покдир'}, {'chapter': 37, 'verse': 160, 'text': 'إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nМагар Аллоҳнинг покиза бандаларигина (мудом Аллоҳни У зотга нолойиқ бўлган сифатлардан пок тутарлар)'}, {'chapter': 37, 'verse': 161, 'text': 'فَإِنَّكُمۡ وَمَا تَعۡبُدُونَ\n(Эй Макка аҳли), шак-шубҳа йўқки, сизлар ҳам, сиғинаётган бутларингиз ҳам'}, {'chapter': 37, 'verse': 162, 'text': 'مَآ أَنتُمۡ عَلَيۡهِ بِفَٰتِنِينَ\n(барчалари бирон кишини, Аллоҳ)га қарши фитнага солгувчи — алдай олгувчи эмасдирсизлар'}, {'chapter': 37, 'verse': 163, 'text': 'إِلَّا مَنۡ هُوَ صَالِ ٱلۡجَحِيمِ\nМагар (Аллоҳнинг тақдири азалийсида) дўзахга киргувчи бўлган кимсаларнигина (алдай олурсизлар)'}, {'chapter': 37, 'verse': 164, 'text': 'وَمَامِنَّآ إِلَّا لَهُۥ مَقَامٞ مَّعۡلُومٞ\n(Фаришталар дедилар): «Бизларнинг ҳар биримиз учун (осмонларда) аниқ маълум бир ўрин бордир(ки, бизлар ана ўша ўрнимизда Аллоҳнинг амрини адо этурмиз)'}, {'chapter': 37, 'verse': 165, 'text': 'وَإِنَّا لَنَحۡنُ ٱلصَّآفُّونَ\nДарҳақиқат, бизлар (Аллоҳнинг йўлида) саф тортиб тургувчилардирмиз'}, {'chapter': 37, 'verse': 166, 'text': 'وَإِنَّا لَنَحۡنُ ٱلۡمُسَبِّحُونَ\nДарҳақиқат, бизлар (Аллоҳни ҳар қандай айбу нуқсондан поклаб) — тасбеҳ айтгувчилардирмиз»'}, {'chapter': 37, 'verse': 167, 'text': 'وَإِن كَانُواْ لَيَقُولُونَ\nДарвоқеъ (Макка аҳли Қуръон нозил бўлишидан илгари) айтар эдиларки'}, {'chapter': 37, 'verse': 168, 'text': 'لَوۡ أَنَّ عِندَنَا ذِكۡرٗا مِّنَ ٱلۡأَوَّلِينَ\n«Агар бизнинг олдимизда ҳам аввалгилар(нинг китоблари)дан бирон эслатма — Китоб бўлса эди'}, {'chapter': 37, 'verse': 169, 'text': 'لَكُنَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ\nАлбатта бизлар ҳам Аллоҳнинг покиза бандалари бўлур эдик»'}, {'chapter': 37, 'verse': 170, 'text': 'فَكَفَرُواْ بِهِۦۖ فَسَوۡفَ يَعۡلَمُونَ\nЭнди (Қуръон нозил бўлгач), эса унга кофир бўлдилар. Бас, яқинда (бу куфрларининг оқибати нима бўлишини) билиб олурлар'}, {'chapter': 37, 'verse': 171, 'text': 'وَلَقَدۡ سَبَقَتۡ كَلِمَتُنَا لِعِبَادِنَا ٱلۡمُرۡسَلِينَ\nАниқки, Бизнинг пайғамбар бўлган бандаларимиз ҳақида Сўзимиз ўтган — собит бўлгандир'}, {'chapter': 37, 'verse': 172, 'text': 'إِنَّهُمۡ لَهُمُ ٱلۡمَنصُورُونَ\n«Шак-шубҳасиз, улар қўллаб-қувватлангувчилардир'}, {'chapter': 37, 'verse': 173, 'text': 'وَإِنَّ جُندَنَا لَهُمُ ٱلۡغَٰلِبُونَ\nВа шак-шубҳасиз, Бизнинг қўшинимиз (яъни, пайғамбарлар ва уларга иймон келтирган кишилар) ғолиб бўлгувчидирлар»'}, {'chapter': 37, 'verse': 174, 'text': 'فَتَوَلَّ عَنۡهُمۡ حَتَّىٰ حِينٖ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз улардан (яъни, Макка кофирларидан маълум) бир вақтгача юз ўгиринг'}, {'chapter': 37, 'verse': 175, 'text': 'وَأَبۡصِرۡهُمۡ فَسَوۡفَ يُبۡصِرُونَ\nВа уларни (яқинда мағлуб бўлганларида) кўринг! Бас, яқинда улар (куфрларининг оқибатини) кўражаклар'}, {'chapter': 37, 'verse': 176, 'text': 'أَفَبِعَذَابِنَا يَسۡتَعۡجِلُونَ\nҲали улар Бизнинг азобимизни шоширмоқдамилар'}, {'chapter': 37, 'verse': 177, 'text': 'فَإِذَا نَزَلَ بِسَاحَتِهِمۡ فَسَآءَ صَبَاحُ ٱلۡمُنذَرِينَ\nБас, қачон (Бизнинг азобимиз) уларнинг ҳовлиларига (яъни, устларига) тушганида, ўша огоҳлантирилган кимсаларнинг кунлари жуда ёмон бўлур'}, {'chapter': 37, 'verse': 178, 'text': 'وَتَوَلَّ عَنۡهُمۡ حَتَّىٰ حِينٖ\nСиз улардан (маълум) бир вақтгача юз ўгиринг'}, {'chapter': 37, 'verse': 179, 'text': 'وَأَبۡصِرۡ فَسَوۡفَ يُبۡصِرُونَ\nВа уларни (яқинда мағлуб бўлганларида) кўринг! Бас, яқинда улар (куфрларининг оқибатини) кўражаклар'}, {'chapter': 37, 'verse': 180, 'text': 'سُبۡحَٰنَ رَبِّكَ رَبِّ ٱلۡعِزَّةِ عَمَّا يَصِفُونَ\nҚудрат эгаси бўлмиш Парвардигорингиз уларнинг сифатларидан (яъни, ҳар қандай айбу нуқсондан) покдир'}, {'chapter': 37, 'verse': 181, 'text': 'وَسَلَٰمٌ عَلَى ٱلۡمُرۡسَلِينَ\n(Барча) пайғамбарларга Аллоҳ томонидан салом бўлгай'}, {'chapter': 37, 'verse': 182, 'text': 'وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nҲамду сано барча оламларнинг Парвардигори — Аллоҳ учундир'}, {'chapter': 38, 'verse': 1, 'text': 'صٓۚ وَٱلۡقُرۡءَانِ ذِي ٱلذِّكۡرِ\nСод. Шаъну-шараф соҳиби бўлган Қуръонга қасамки, (албатта Қуръон илоҳий мўъжизадир ва албатта сиз, эй Муҳаммад алайҳис-салоту вас-салом, Аллоҳнинг ҳақ пайғамбаридирсиз)'}, {'chapter': 38, 'verse': 2, 'text': 'بَلِ ٱلَّذِينَ كَفَرُواْ فِي عِزَّةٖ وَشِقَاقٖ\nБалки кофир бўлган кимсалар (бу ҳақиқатни қабул қилишдан) кибру-ҳавода ва (Аллоҳ ва пайғамбарга) мухолифликда собитдирлар'}, {'chapter': 38, 'verse': 3, 'text': 'كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ فَنَادَواْ وَّلَاتَ حِينَ مَنَاصٖ\nБиз улардан илгари ҳам қанча аср авлодни (иймонсизликлари сабабли) ҳалок қилганимизда, улар (бошларига азоб тушганида ёрдам сўраб) нидо-илтижо қилганлар, (аммо бу вақт азобдан) қочиб қутулиш вақти эмас эди'}, {'chapter': 38, 'verse': 4, 'text': 'وَعَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡۖ وَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا سَٰحِرٞ كَذَّابٌ\n(Макка мушриклари) уларга ўзларидан (яъни, башар авлодидан бўлган бир огоҳлантиргувчи — пайғамбар келганидан ажабландилар ва у кофирлар дедилар: «Бу бир ёлғончи сеҳргардир)'}, {'chapter': 38, 'verse': 5, 'text': 'أَجَعَلَ ٱلۡأٓلِهَةَ إِلَٰهٗا وَٰحِدًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عُجَابٞ\n(Шунча) худоларни битта худо қилиб олибдими?! Ҳақиқатан, бу жуда қизиқ нарса!»'}, {'chapter': 38, 'verse': 6, 'text': 'وَٱنطَلَقَ ٱلۡمَلَأُ مِنۡهُمۡ أَنِ ٱمۡشُواْ وَٱصۡبِرُواْ عَلَىٰٓ ءَالِهَتِكُمۡۖ إِنَّ هَٰذَا لَشَيۡءٞ يُرَادُ\nУлардан катталари (бир-бирларига шундай дея тарқалиб) кетдилар: «Юринглар ва ўз худоларингизга (сиғинишда) қаноат қилинглар (яъни, устивор бўлинглар)! Албатта бу (яъни, «Ла илаҳа иллаллоҳ» дейиш бизлардан) исталадиган (талаб қилинадиган жуда катта) нарсадир'}, {'chapter': 38, 'verse': 7, 'text': 'مَا سَمِعۡنَا بِهَٰذَا فِي ٱلۡمِلَّةِ ٱلۡأٓخِرَةِ إِنۡ هَٰذَآ إِلَّا ٱخۡتِلَٰقٌ\nБизлар бу ҳақда (яъни, Аллоҳнинг яккаю ягона эканлиги ҳақида) сўнгги миллат — динда(ги одамлардан ҳам, яъни Исломдан аввалги сўнгги дин бўлмиш Насроний динидаги одамлардан ҳам) эшитган эмасмиз. Бу фақат бир уйдирмадир, холос'}, {'chapter': 38, 'verse': 8, 'text': 'أَءُنزِلَ عَلَيۡهِ ٱلذِّكۡرُ مِنۢ بَيۡنِنَاۚ بَلۡ هُمۡ فِي شَكّٖ مِّن ذِكۡرِيۚ بَل لَّمَّا يَذُوقُواْ عَذَابِ\nБизларнинг орамизда (шунча бой-зодагонлар туриб) ўша (Муҳаммад)га эслатма-Қуръон нозил қилинган эмишми?!» (Уларнинг бу сўзларни айтишларига сабаб ўта билимдонликлари эмас), балки улар Менинг эслатмам — Қуръондан шак-шубҳададирлар, балки улар ҳали Менинг азобимни тотиб кўрганлари йўқ (бас, шунинг учун Қуръон ва пайғамбар шаънига мана шундай нолойиқ сўзларни айтмоқдалар)'}, {'chapter': 38, 'verse': 9, 'text': 'أَمۡ عِندَهُمۡ خَزَآئِنُ رَحۡمَةِ رَبِّكَ ٱلۡعَزِيزِ ٱلۡوَهَّابِ\nЁки қудратли ва саховатли Парвардигорингизнинг раҳмат хазиналари уларнинг олдиларидамикин-а'}, {'chapter': 38, 'verse': 10, 'text': 'أَمۡ لَهُم مُّلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ فَلۡيَرۡتَقُواْ فِي ٱلۡأَسۡبَٰبِ\nЁки осмонлар ва Ернинг ҳамда уларнинг ўртасидаги бор нарсаларнинг подшоҳлиги уларникимикин-а?! У ҳолда (уларни осмонларга элтадиган) нарвонларига чиқаверсинлар (ва ўша жойдан ўзлари истаган одамларига ваҳий нозил қилаверсинлар)'}, {'chapter': 38, 'verse': 11, 'text': 'جُندٞ مَّا هُنَالِكَ مَهۡزُومٞ مِّنَ ٱلۡأَحۡزَابِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Макка мушриклари ўша ернинг ўзида енгилиб-битгувчи фирқалардан бир тўдадир, холос, (бас, сиз улар айтаётган беҳуда сўзларга парво ҳам қилманг)'}, {'chapter': 38, 'verse': 12, 'text': 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَعَادٞ وَفِرۡعَوۡنُ ذُو ٱلۡأَوۡتَادِ\nУлардан илгари ҳам Нуҳ қавми (Нуҳни), Од (қабиласи Ҳудни) ва қозиқлар (яъни, ерга қозиқдек қоқилган баланд эҳромлар ва қасрлар) эгаси бўлган Фиръавн (ва унинг одамлари Мусони ёлғончи қилгандирлар)'}, {'chapter': 38, 'verse': 13, 'text': 'وَثَمُودُ وَقَوۡمُ لُوطٖ وَأَصۡحَٰبُ لۡـَٔيۡكَةِۚ أُوْلَـٰٓئِكَ ٱلۡأَحۡزَابُ\nШунингдек, Самуд (қабиласи Солиҳни), Лут қавми (Лутни) ва дарахтзор эгалари (Шуъайбни ёлғончи қилгандирлар). Ана ўшалар (ҳам ўзларига юборилган пайғамбарларга қарши чиққан) фирқалардир'}, {'chapter': 38, 'verse': 14, 'text': 'إِن كُلٌّ إِلَّا كَذَّبَ ٱلرُّسُلَ فَحَقَّ عِقَابِ\nБарчаларига фақат пайғамбарларни ёлғончи қилганлари сабабли Менинг азобим ҳақ бўлгандир. (Бас, Макка мушрикларига ҳам Муҳаммад алайҳис-салоту вас-саломни ёлғончи қилганлари учун шак-шубҳасиз, азоб-уқубат бўлур)'}, {'chapter': 38, 'verse': 15, 'text': 'وَمَا يَنظُرُ هَـٰٓؤُلَآءِ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ مَّا لَهَا مِن فَوَاقٖ\nАна ўшалар(нинг барчалари) фақат ҳеч тўхтамасдан келадиган биргина даҳшатли қичқириқни кутмоқдалар, холос. (Бас, ўша қичқириқ бўлиши билан барча жонзот қайта тирилур ва куфр йўлини тутган кимсалар дўзах азобига дучор бўлурлар)'}, {'chapter': 38, 'verse': 16, 'text': 'وَقَالُواْ رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبۡلَ يَوۡمِ ٱلۡحِسَابِ\nУлар (яъни, Макка мушриклари масхара қилишиб) «Парвардигоро, бизларга (ваъда қилинган азобдан иборат) насибамизни ҳисоб-китоб Кунидан илгариёқ (яъни, шу ҳаёти дунёдаёқ) тезроқ келтира қолгин», дедилар'}, {'chapter': 38, 'verse': 17, 'text': 'ٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَٱذۡكُرۡ عَبۡدَنَا دَاوُۥدَ ذَا ٱلۡأَيۡدِۖ إِنَّهُۥٓ أَوَّابٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз улар айтаётган сўзларга сабр қилинг ва Бизнинг бандамиз (тоат-ибодатда) қувват соҳиби бўлган Довудни эсланг! Дарҳақиқат, у (Аллоҳ рози бўладиган йўлга) бутунлай қайтгувчидир'}, {'chapter': 38, 'verse': 18, 'text': 'إِنَّا سَخَّرۡنَا ٱلۡجِبَالَ مَعَهُۥ يُسَبِّحۡنَ بِٱلۡعَشِيِّ وَٱلۡإِشۡرَاقِ\nАлбатта Биз тоғларни кечки пайт ва эрталаб (яъни, мудом) у билан бирга тасбеҳ айтадиган қилиб бўйинсундириб қўйдик'}, {'chapter': 38, 'verse': 19, 'text': 'وَٱلطَّيۡرَ مَحۡشُورَةٗۖ كُلّٞ لَّهُۥٓ أَوَّابٞ\nВа (ҳар тарафдан) тўплангувчи бўлган қушларни ҳам (Довуд билан бирга тасбеҳ айтадиган қилиб бўйнсундириб қўйдик. Тоғлар ва қушларнинг) барчалари Довудга итоат этишга бутунлай қайтгувчидир'}, {'chapter': 38, 'verse': 20, 'text': 'وَشَدَدۡنَا مُلۡكَهُۥ وَءَاتَيۡنَٰهُ ٱلۡحِكۡمَةَ وَفَصۡلَ ٱلۡخِطَابِ\nЯна Биз унинг мулку-давлатини кучли-қувватли қилдик ва унга ҳикмат ҳамда аниқ-равшан хитоб (яъни, забони гўё) ато этдик'}, {'chapter': 38, 'verse': 21, 'text': '۞وَهَلۡ أَتَىٰكَ نَبَؤُاْ ٱلۡخَصۡمِ إِذۡ تَسَوَّرُواْ ٱلۡمِحۡرَابَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга (бир-бирлари билан) даъволашгувчи (икки жамоат) хабари келдими? Ўшанда улар (Довуд пайғамбар ибодат қилаётган) меҳробга чиқишган'}, {'chapter': 38, 'verse': 22, 'text': 'إِذۡ دَخَلُواْ عَلَىٰ دَاوُۥدَ فَفَزِعَ مِنۡهُمۡۖ قَالُواْ لَا تَخَفۡۖ خَصۡمَانِ بَغَىٰ بَعۡضُنَا عَلَىٰ بَعۡضٖ فَٱحۡكُم بَيۡنَنَا بِٱلۡحَقِّ وَلَا تُشۡطِطۡ وَٱهۡدِنَآ إِلَىٰ سَوَآءِ ٱلصِّرَٰطِ\nВа Довуднинг олдига киришган эди, у улардан қўрқиб кетди. Улар дедилар: «Қўрқмагин. (Бизлар) биримиз биримизга зулм қилган икки даъволашгувчи (жамоат)дирмиз. Бас, сен бизларнинг ўртамизда ҳақ (ҳукм) билан ҳукм қилгин ва (бизлардан бирон тарафга) жабр қилмагин ҳамда бизларни текис-тўғри йўлга бошлагин»'}, {'chapter': 38, 'verse': 23, 'text': 'إِنَّ هَٰذَآ أَخِي لَهُۥ تِسۡعٞ وَتِسۡعُونَ نَعۡجَةٗ وَلِيَ نَعۡجَةٞ وَٰحِدَةٞ فَقَالَ أَكۡفِلۡنِيهَا وَعَزَّنِي فِي ٱلۡخِطَابِ\n(Даъвогарларнинг бири деди): «Дарвоқеъ, мана бу менинг биродарим бўлиб, унинг тўқсон тўққиз совлиқ қўйи бор, менинг эса биргана совлиғим бордир. Бас, у (шу биргина совлиққа ҳам кўз олайтириб): «Ўшани менга топширгин», деди ва мени бу сўзда — жанжалда енгиб қўйди»'}, {'chapter': 38, 'verse': 24, 'text': 'قَالَ لَقَدۡ ظَلَمَكَ بِسُؤَالِ نَعۡجَتِكَ إِلَىٰ نِعَاجِهِۦۖ وَإِنَّ كَثِيرٗا مِّنَ ٱلۡخُلَطَآءِ لَيَبۡغِي بَعۡضُهُمۡ عَلَىٰ بَعۡضٍ إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَقَلِيلٞ مَّا هُمۡۗ وَظَنَّ دَاوُۥدُ أَنَّمَا فَتَنَّـٰهُ فَٱسۡتَغۡفَرَ رَبَّهُۥ وَخَرَّۤ رَاكِعٗاۤ وَأَنَابَ۩\n(Довуд иккинчи даъвогарнинг сўзини тингламасданоқ) деди: «Дарҳақиқат, у сенинг совлиғингни сўраб (ва) ўз совлиқларига (қўшиб олиш) билан сенга зулм қилибди. Дарвоқеъ, кўп ошна-оғайнилар бир-бирларига зулм қилурлар. Фақат иймон келтирган ва яхши амаллар қилган кишиларгина (зулм қилмаслар. Аммо) улар жуда оздирлар». Довуд (бу ҳодиса воситасида) Биз уни имтиҳон қилганимизни билди-да, Парвардигоридан (шошиб ҳукм чиқаргани учун) мағфират қилишини сўради ва (саждага) эгилган ҳолида йиқилиб, тавба-тазарруъ қилди'}, {'chapter': 38, 'verse': 25, 'text': 'فَغَفَرۡنَا لَهُۥ ذَٰلِكَۖ وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابٖ\nБас, Биз унинг ўша (хатоси)ни мағфират қилдик. Шак-шубҳасиз, унинг учун Бизнинг ҳузуримизда яқинлик ва гўзал оқибат (яъни, жаннат) бордир'}, {'chapter': 38, 'verse': 26, 'text': 'يَٰدَاوُۥدُ إِنَّا جَعَلۡنَٰكَ خَلِيفَةٗ فِي ٱلۡأَرۡضِ فَٱحۡكُم بَيۡنَ ٱلنَّاسِ بِٱلۡحَقِّ وَلَا تَتَّبِعِ ٱلۡهَوَىٰ فَيُضِلَّكَ عَن سَبِيلِ ٱللَّهِۚ إِنَّ ٱلَّذِينَ يَضِلُّونَ عَن سَبِيلِ ٱللَّهِ لَهُمۡ عَذَابٞ شَدِيدُۢ بِمَا نَسُواْ يَوۡمَ ٱلۡحِسَابِ\nЭй Довуд, дарҳақиқат, Биз сени Ерда халифа қилдик. Бас, сен (нафс) хоҳишига эргашиб кетмагин! Акс ҳолда у сени Аллоҳнинг йўлидан оздирур. Албатта Аллоҳ йўлидан озадиган кимсалар учун ҳисоб-китоб кунини (яъни, Қиёматни) унутиб қўйганлари сабабли қаттиқ азоб бордир'}, {'chapter': 38, 'verse': 27, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَآءَ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا بَٰطِلٗاۚ ذَٰلِكَ ظَنُّ ٱلَّذِينَ كَفَرُواْۚ فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِنَ ٱلنَّارِ\nБиз осмон ва Ерни ҳамда уларнинг ўртасидаги бор нарсаларни беҳуда яратган эмасмиз. Бу (яъни, улар беҳуда-бемақсад яратилган деган гумон) кофир бўлган кимсаларнинг гумонидир. Бас, кофир бўлган кимсалар учун дўзахдан иборат ҳалокат бўлгай'}, {'chapter': 38, 'verse': 28, 'text': 'أَمۡ نَجۡعَلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ كَٱلۡمُفۡسِدِينَ فِي ٱلۡأَرۡضِ أَمۡ نَجۡعَلُ ٱلۡمُتَّقِينَ كَٱلۡفُجَّارِ\nБалки Биз иймон келтирган ва яхши амаллар қилган зотларни Ерда бузғунчилик қилиб юрган кимсалар каби қилиб қўюрмиз?! Балки Биз тақводор зотларни фисқу-фужур қилиб юрган кимсалар каби қилиб қўюрмиз?! (Йўқ, асло ундоқ бўлмас)'}, {'chapter': 38, 'verse': 29, 'text': 'كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ مُبَٰرَكٞ لِّيَدَّبَّرُوٓاْ ءَايَٰتِهِۦ وَلِيَتَذَكَّرَ أُوْلُواْ ٱلۡأَلۡبَٰبِ\n(Эй Муҳаммад алайҳис-салоту вас-салом, ушбу Қуръон барча одамлар) оятларини тафаккур қилишлари ва ақл эгалари эслатма-ибрат олишлари учун Биз сизга нозил қилган бир муборак Китобдир'}, {'chapter': 38, 'verse': 30, 'text': 'وَوَهَبۡنَا لِدَاوُۥدَ سُلَيۡمَٰنَۚ نِعۡمَ ٱلۡعَبۡدُ إِنَّهُۥٓ أَوَّابٌ\nБиз Довудга Сулаймонни ҳадя этдик. (Сулаймон) нақадар яхши бандадир. Дарҳақиқат, у (Аллоҳ рози бўладиган йўлга) бутунлай қайтгувчидир'}, {'chapter': 38, 'verse': 31, 'text': 'إِذۡ عُرِضَ عَلَيۡهِ بِٱلۡعَشِيِّ ٱلصَّـٰفِنَٰتُ ٱلۡجِيَادُ\nЭсланг, унга (бир куни) кечки пайт гижинглаб тургувчи учқур отлар рўбарў қилинган эди'}, {'chapter': 38, 'verse': 32, 'text': 'فَقَالَ إِنِّيٓ أَحۡبَبۡتُ حُبَّ ٱلۡخَيۡرِ عَن ذِكۡرِ رَبِّي حَتَّىٰ تَوَارَتۡ بِٱلۡحِجَابِ\nБас, у (ўша отларга маҳлиё бўлиб, кун ботиш олдида ўқийдиган намози қазо бўлгач,) деди: «Дарҳақиқат, мен Парвардигоримни эслашдан (яъни, ўқийдиган намозимдан қолиб) бу суюкли отларни суюб-эркалашга берилиб кетибман! Ҳатто (қуёш ҳам) парда ортига беркинибди (яъни, ботиб кетибди)'}, {'chapter': 38, 'verse': 33, 'text': 'رُدُّوهَا عَلَيَّۖ فَطَفِقَ مَسۡحَۢا بِٱلسُّوقِ وَٱلۡأَعۡنَاقِ\nУ (от)ларни менга қайтаринглар-чи! Бас, у (қилич билан отларнинг) оёқ ва бўйинларини «силаш»га киришди (ва уларни Аллоҳ йўлида қурбонлик қилиб юборди)'}, {'chapter': 38, 'verse': 34, 'text': 'وَلَقَدۡ فَتَنَّا سُلَيۡمَٰنَ وَأَلۡقَيۡنَا عَلَىٰ كُرۡسِيِّهِۦ جَسَدٗا ثُمَّ أَنَابَ\nДарҳақиқат, Биз Сулаймонни имтиҳон қилдик ва унинг тахти устига бир жонсиз жасадни ташладик. Сўнгра у тавба-тазарруъ қилиб'}, {'chapter': 38, 'verse': 35, 'text': 'قَالَ رَبِّ ٱغۡفِرۡ لِي وَهَبۡ لِي مُلۡكٗا لَّا يَنۢبَغِي لِأَحَدٖ مِّنۢ بَعۡدِيٓۖ إِنَّكَ أَنتَ ٱلۡوَهَّابُ\nДеди: «Парвардигорим, Ўзинг мени мағфират қилгин ва менга ўзимдан кейин биронтаси учун муяссар бўлмайдиган бир мулку-давлат ҳадя этгин. Зеро, ёлғиз Сенинг Ўзинггина (барча яхшиликларни) ҳадя этгувчисан»'}, {'chapter': 38, 'verse': 36, 'text': 'فَسَخَّرۡنَا لَهُ ٱلرِّيحَ تَجۡرِي بِأَمۡرِهِۦ رُخَآءً حَيۡثُ أَصَابَ\nБас, Биз унга унинг амри билан у истаган томонга майин эсаверадиган шамолни бўйинсундирдик'}, {'chapter': 38, 'verse': 37, 'text': 'وَٱلشَّيَٰطِينَ كُلَّ بَنَّآءٖ وَغَوَّاصٖ\nЯна барча (баланд қасрларни) бино қилгувчи ва (денгизларнинг қаъридан дурру гавҳарларни топиб чиқувчи) ғаввос шайтонларни (яъни, жинларни) ҳам Сулаймонга бўйинсундириб қўйдик'}, {'chapter': 38, 'verse': 38, 'text': 'وَءَاخَرِينَ مُقَرَّنِينَ فِي ٱلۡأَصۡفَادِ\nБошқа (бўйинсунмайдиган жин)ларни эса кишанлар билан боғланган ҳолда солдик'}, {'chapter': 38, 'verse': 39, 'text': 'هَٰذَا عَطَآؤُنَا فَٱمۡنُنۡ أَوۡ أَمۡسِكۡ بِغَيۡرِ حِسَابٖ\n(Ва Сулаймонга дедик): «Мана шу Бизнинг (сенга) ато этган нарсамиздир. Бас, сен (ушбу неъматлардан ўзинг хоҳлаган кишиларга) эҳсон қил, ёки қилма (сени бу тўғрида) ҳисоб-китоб қилинмас»'}, {'chapter': 38, 'verse': 40, 'text': 'وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابٖ\nШак-шубҳасиз, (Сулаймон) учун Бизнинг ҳузуримизда яқинлик ва гўзал оқибат (яъни, жаннат) бордир'}, {'chapter': 38, 'verse': 41, 'text': 'وَٱذۡكُرۡ عَبۡدَنَآ أَيُّوبَ إِذۡ نَادَىٰ رَبَّهُۥٓ أَنِّي مَسَّنِيَ ٱلشَّيۡطَٰنُ بِنُصۡبٖ وَعَذَابٍ\n(Эй Муҳаммад алайҳис-салоту вас-салом), бандамиз Айюбнинг Парвардигорига нидо қилиб: «Дарҳақиқат, мени шайтон бало ва азоб билан ушлади», деган пайтини эсланг'}, {'chapter': 38, 'verse': 42, 'text': 'ٱرۡكُضۡ بِرِجۡلِكَۖ هَٰذَا مُغۡتَسَلُۢ بَارِدٞ وَشَرَابٞ\n(Шунда унга айтилди): «Оёғинг билан (остингдаги ерни) тепгин!» (У Аллоҳнинг амрини бажарган эди, ўша ердан бир чашма отилиб чиқди. Сўнг унга айтилди): Мана шу муздек чўмиладиган ва ичимлик сувдир». (Қачонки Айюб у сувда чўмилиб, сўнг ундан ичгач, унинг барча зоҳирий ва ботиний дардлари дарҳол даво топди)'}, {'chapter': 38, 'verse': 43, 'text': 'وَوَهَبۡنَا لَهُۥٓ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةٗ مِّنَّا وَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ\nВа Биз Ўз томонимиздан меҳрибонлик кўрсатиб ҳамда ақл эгалари учун эслатма-ибрат бўлсин деб, (Айюбга) аҳли оиласини ва улар билан қўшиб, яна ўшаларнинг мислича (бола-чақа) ҳадя этдик'}, {'chapter': 38, 'verse': 44, 'text': 'وَخُذۡ بِيَدِكَ ضِغۡثٗا فَٱضۡرِب بِّهِۦ وَلَا تَحۡنَثۡۗ إِنَّا وَجَدۡنَٰهُ صَابِرٗاۚ نِّعۡمَ ٱلۡعَبۡدُ إِنَّهُۥٓ أَوَّابٞ\n(Яна Биз Айюбга айтдик): «Қўлингга бир боғ (новдани) олиб, у билан (хотинингни) ургин — қасамингни бузмагин». Дарҳақиқат, Биз (Айюбни) сабр қилгувчи ҳолда топдик. У нақадар яхши бандадир. Ҳақиқатан, у (Аллоҳ рози бўладиган йўлга) бутунлай қайтгувчидир'}, {'chapter': 38, 'verse': 45, 'text': 'وَٱذۡكُرۡ عِبَٰدَنَآ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَ أُوْلِي ٱلۡأَيۡدِي وَٱلۡأَبۡصَٰرِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз Бизнинг куч-қувват ва фаҳм-фаросат эгалари бўлган бандаларимиз — Иброҳим, Исҳоқ ва Яъқубларни эсланг'}, {'chapter': 38, 'verse': 46, 'text': 'إِنَّآ أَخۡلَصۡنَٰهُم بِخَالِصَةٖ ذِكۡرَى ٱلدَّارِ\nДарҳақиқат, Биз уларни бир тоза (хислат — мудом Охират) диёрини эслаш (хислати) билан холис қилдик — покладик'}, {'chapter': 38, 'verse': 47, 'text': 'وَإِنَّهُمۡ عِندَنَا لَمِنَ ٱلۡمُصۡطَفَيۡنَ ٱلۡأَخۡيَارِ\nҲақиқатан, улар Бизнинг ҳузуримизда танланган, яхши кишилардандир'}, {'chapter': 38, 'verse': 48, 'text': 'وَٱذۡكُرۡ إِسۡمَٰعِيلَ وَٱلۡيَسَعَ وَذَا ٱلۡكِفۡلِۖ وَكُلّٞ مِّنَ ٱلۡأَخۡيَارِ\nЯна Исмоил, ал-Ясаъ (Юшаъ пайғамбар) ва Зул-Кифлни эсланг! Барчалари яхши кишилардандир'}, {'chapter': 38, 'verse': 49, 'text': 'هَٰذَا ذِكۡرٞۚ وَإِنَّ لِلۡمُتَّقِينَ لَحُسۡنَ مَـَٔابٖ\nБу (Биз сизга сўйлаган қиссалар) бир эслатмадир. Шак-шубҳасиз, тақводор зотлар учун гўзал оқибат'}, {'chapter': 38, 'verse': 50, 'text': 'جَنَّـٰتِ عَدۡنٖ مُّفَتَّحَةٗ لَّهُمُ ٱلۡأَبۡوَٰبُ\n— Улар учун барча дарвозалари ланг очиқ мангу жаннатлар бордир'}, {'chapter': 38, 'verse': 51, 'text': 'مُتَّكِـِٔينَ فِيهَا يَدۡعُونَ فِيهَا بِفَٰكِهَةٖ كَثِيرَةٖ وَشَرَابٖ\nУлар у жойлардаги (сўриларда) ястаниб ўтириб, мўл-кўл (яъни, хоҳлаганларича) мева-чева ва шароб чақирурлар'}, {'chapter': 38, 'verse': 52, 'text': '۞وَعِندَهُمۡ قَٰصِرَٰتُ ٱلطَّرۡفِ أَتۡرَابٌ\nВа уларнинг олдиларида кўзларини (номаҳрамга боқишдан) сақлаган (ёш ва ҳуснда) тенгдош (қиз)лар бўлур'}, {'chapter': 38, 'verse': 53, 'text': 'هَٰذَا مَا تُوعَدُونَ لِيَوۡمِ ٱلۡحِسَابِ\nУ (зикр қилинган нарсалар) сизларга ҳисоб-китоб (яъни, Қиёмат) куни учун ваъда қилинадиган нарсалардир'}, {'chapter': 38, 'verse': 54, 'text': 'إِنَّ هَٰذَا لَرِزۡقُنَا مَا لَهُۥ مِن نَّفَادٍ\nШак-шубҳасиз, бу Бизнинг (жаннат аҳлига ато этадиган) ризқу рўзимиздирки, унинг учун тугаб қолиш бўлмас'}, {'chapter': 38, 'verse': 55, 'text': 'هَٰذَاۚ وَإِنَّ لِلطَّـٰغِينَ لَشَرَّ مَـَٔابٖ\n(Ҳақиқат) шудир. (Энди) ҳеч шак-шубҳасиз, ҳаддан ошгувчи-туғёнга тушгувчи кимсалар учун энг ёмон оқибат'}, {'chapter': 38, 'verse': 56, 'text': 'جَهَنَّمَ يَصۡلَوۡنَهَا فَبِئۡسَ ٱلۡمِهَادُ\n— Улар кирадиган жаҳаннам бордир. Бас, нақадар ёмон жой у'}, {'chapter': 38, 'verse': 57, 'text': 'هَٰذَا فَلۡيَذُوقُوهُ حَمِيمٞ وَغَسَّاقٞ\nМана бу қайноқ ва йирингдир! Бас, улар ўшани тотиб кўрсинлар'}, {'chapter': 38, 'verse': 58, 'text': 'وَءَاخَرُ مِن شَكۡلِهِۦٓ أَزۡوَٰجٌ\nБошқа (азоб-уқубатлар) ҳам ўша тур, ўша шаклда бўлур'}, {'chapter': 38, 'verse': 59, 'text': 'هَٰذَا فَوۡجٞ مُّقۡتَحِمٞ مَّعَكُمۡ لَا مَرۡحَبَۢا بِهِمۡۚ إِنَّهُمۡ صَالُواْ ٱلنَّارِ\n(Дўзахга кирган пайтларида кофирларнинг пешволарига ҳаёти дунёда уларга эргашган кимсаларни кўрсатилиб): «Мана бу (одамлар) сизлар билан бирга (яъни, сизлар сабабли жаҳаннамга) ташлангувчи жамоатдир», (дейилганида, пешволар айтдилар): «Хуш келмадилар! Ҳақиқатан, уларнинг ўзлари дўзахга киргувчи кимсалардир»'}, {'chapter': 38, 'verse': 60, 'text': 'قَالُواْ بَلۡ أَنتُمۡ لَا مَرۡحَبَۢا بِكُمۡۖ أَنتُمۡ قَدَّمۡتُمُوهُ لَنَاۖ فَبِئۡسَ ٱلۡقَرَارُ\nУлар (яъни, эргашувчилар пешволарга) дедилар: «Йўқ, сизлар хуш келмадингиз! (Чунки) сизлар ўша (азобни) бизларга келтирдингиз! Бас, нақадар ёмон жой (бу дўзах)!»'}, {'chapter': 38, 'verse': 61, 'text': 'قَالُواْ رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدۡهُ عَذَابٗا ضِعۡفٗا فِي ٱلنَّارِ\nУлар (яна) дедилар: «Парвардигоро, ким бизларга мана шу (азоб)ни келтирган бўлса, бас, Ўзинг унга дўзахда азобни бир неча баробар зиёда қилгин»'}, {'chapter': 38, 'verse': 62, 'text': 'وَقَالُواْ مَا لَنَا لَا نَرَىٰ رِجَالٗا كُنَّا نَعُدُّهُم مِّنَ ٱلۡأَشۡرَارِ\n(Жаҳаннам эгалари бир-бирларига) айтдилар: «Нега бизлар (дунёдалик пайтимизда) ёмонлардан деб ҳисоблаб ўтган кишиларни (яъни, мўминларни дўзахда) кўрмаяпмиз-а'}, {'chapter': 38, 'verse': 63, 'text': 'أَتَّخَذۡنَٰهُمۡ سِخۡرِيًّا أَمۡ زَاغَتۡ عَنۡهُمُ ٱلۡأَبۡصَٰرُ\nБизлар ўшаларни масхара қилиб олган эдикми?! Ёки (улар ҳам бизлар билан мана шу жаҳаннамда бирга-ю, аммо) кўзларимиз оғиб (уларни илғамай қолдими)?!»'}, {'chapter': 38, 'verse': 64, 'text': 'إِنَّ ذَٰلِكَ لَحَقّٞ تَخَاصُمُ أَهۡلِ ٱلنَّارِ\nАлбатта дўзах аҳлининг ана шундай талашиб тортишишлари ҳақдир — аниқдир'}, {'chapter': 38, 'verse': 65, 'text': 'قُلۡ إِنَّمَآ أَنَا۠ مُنذِرٞۖ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ\n(Эй Муҳаммад алайҳис-салоту вас-салом, Макка мушрикларига) айтинг: «Мен фақат бир огоҳлантиргувчидирман, холос. Ҳеч қандай илоҳ йўқ, магар яккаю ёлғиз ғолиб Аллоҳгина бордир'}, {'chapter': 38, 'verse': 66, 'text': 'رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلۡعَزِيزُ ٱلۡغَفَّـٰرُ\nУ осмонлар ва Ернинг ҳамда уларнинг ўртасидаги бор нарсанинг қудратли ва мағфиратли Парвардигоридир»'}, {'chapter': 38, 'verse': 67, 'text': 'قُلۡ هُوَ نَبَؤٌاْ عَظِيمٌ\nАйтинг: «Ўша (яъни, мен сизларга етказган мана шу сўзлар) улуғ бир хабардир'}, {'chapter': 38, 'verse': 68, 'text': 'أَنتُمۡ عَنۡهُ مُعۡرِضُونَ\nСизлар эса ундан юз ўгирмоқдасизлар'}, {'chapter': 38, 'verse': 69, 'text': 'مَا كَانَ لِيَ مِنۡ عِلۡمِۭ بِٱلۡمَلَإِ ٱلۡأَعۡلَىٰٓ إِذۡ يَخۡتَصِمُونَ\nМен учун (Одамнинг Ер юзига халифа қилиниши тўғрисида Аллоҳ таоло билан) тортишаётган фаришталар ҳақида ҳеч қандай билим йўқ эди'}, {'chapter': 38, 'verse': 70, 'text': 'إِن يُوحَىٰٓ إِلَيَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ\nМенга фақат ўзимнинг очиқ-равшан огоҳлантиргувчи эканлигим ваҳий қилинмоқда, холос»'}, {'chapter': 38, 'verse': 71, 'text': 'إِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن طِينٖ\nЭсланг, Парвардигорингиз фаришталарга деган эди: «Албатта мен лойдан бир одам яратгувчидирман'}, {'chapter': 38, 'verse': 72, 'text': 'فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ\nБас, қачон уни ростлаб, унга Ўз руҳимдан пуфлаб киргизганимдан сўнг унга сажда қилган ҳолларингизда йиқилинглар!»'}, {'chapter': 38, 'verse': 73, 'text': 'فَسَجَدَ ٱلۡمَلَـٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ\nБас, фаришталарнинг барча-барчалари (Одамга) сажда қилдилар'}, {'chapter': 38, 'verse': 74, 'text': 'إِلَّآ إِبۡلِيسَ ٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ\nМагар Иблисгина кибру ҳаво қилиб, кофирлардан бўлди'}, {'chapter': 38, 'verse': 75, 'text': 'قَالَ يَـٰٓإِبۡلِيسُ مَا مَنَعَكَ أَن تَسۡجُدَ لِمَا خَلَقۡتُ بِيَدَيَّۖ أَسۡتَكۡبَرۡتَ أَمۡ كُنتَ مِنَ ٱلۡعَالِينَ\n(Шунда Аллоҳ) деди: «Эй Иблис, Мен Ўз қўлларим билан яратган нарсага — Одамга сажда қилишдан нима сени манъ қилди?! Кибру ҳаво қилдингми, ёки сен (Одамга нисбатан) юксак мартабали зотлардан эдингми?!»'}, {'chapter': 38, 'verse': 76, 'text': 'قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ\nУ айтди: «Мен ундан яхшироқдирман. Сен мени оловдан яратгансан, уни эса лойдан яратдинг»'}, {'chapter': 38, 'verse': 77, 'text': 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ\n(Аллоҳ) деди: «Бас, ундан (яъни, жаннатдан) чиқ! Энди сен, шак-шубҳасиз, (Менинг даргоҳимдан) қувилган — малъунсан'}, {'chapter': 38, 'verse': 78, 'text': 'وَإِنَّ عَلَيۡكَ لَعۡنَتِيٓ إِلَىٰ يَوۡمِ ٱلدِّينِ\nВа албатта то жазо (Қиёмат) кунигача сенга Менин лаънатим бўлур»'}, {'chapter': 38, 'verse': 79, 'text': 'قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ\n(Иблис илтижо қилиб) деди: «Парвардигорим, у ҳолда менга улар тириладиган кунгача (ўлмасдан яшаш учун) муҳлат бергин»'}, {'chapter': 38, 'verse': 80, 'text': 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ\n(Аллоҳ) деди: «Бас, сен муҳлат берилганлардансан'}, {'chapter': 38, 'verse': 81, 'text': 'إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ\nМаълум вақтда етиб келадиган Кунгача (Қиёматгача) (муҳлат берилганлардансан)»'}, {'chapter': 38, 'verse': 82, 'text': 'قَالَ فَبِعِزَّتِكَ لَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ\n(Иблис) айтди: «Энди, Сенинг қудратингга қасамки, албатта уларнинг ҳаммасини йўлдан оздирурман'}, {'chapter': 38, 'verse': 83, 'text': 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ\nМагар уларнинг орасидаги (айрим) покиза бандаларинггина (ҳақ йўлдан озмай қолурлар)»'}, {'chapter': 38, 'verse': 84, 'text': 'قَالَ فَٱلۡحَقُّ وَٱلۡحَقَّ أَقُولُ\n(Аллоҳ) деди: «Ҳаққа (қасам). Фақат ҳақни айтурманки'}, {'chapter': 38, 'verse': 85, 'text': 'لَأَمۡلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنۡهُمۡ أَجۡمَعِينَ\nалбатта Мен жаҳаннамни сен ва (одамлар) орасидаги барча сенга эргашган кимсалар билан тўлдирурман!»'}, {'chapter': 38, 'verse': 86, 'text': 'قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٖ وَمَآ أَنَا۠ مِنَ ٱلۡمُتَكَلِّفِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Мен (Қуръонни етказганим учун) сизлардан бирон ажр-ҳақ сўрамайман ва мен сохтакорлардан (яъни, ёлғондан пайғамбарликни даъво қилгувчи кимсалардан) ҳам эмасман'}, {'chapter': 38, 'verse': 87, 'text': 'إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\n(Ушбу Қуръон) фақат барча оламлар учун (Аллоҳ таоло томонидан нозил қилинган) бир эслатмадир, холос'}, {'chapter': 38, 'verse': 88, 'text': 'وَلَتَعۡلَمُنَّ نَبَأَهُۥ بَعۡدَ حِينِۭ\nАлбатта сизлар унинг (илоҳий ваҳий эканлиги ҳақидаги) хабарни (озгина) вақтдан сўнг (Қиёмат Куни) билиб олурсизлар»'}, {'chapter': 39, 'verse': 1, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\n(Ушбу Қуръон) қудратли ва ҳикматли Аллоҳ томонидан нозил қилинган Китобдир'}, {'chapter': 39, 'verse': 2, 'text': 'إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ فَٱعۡبُدِ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Биз сизга бу Китобни ҳаққирост нозил қилдик, бас, сиз Аллоҳга — У зот учун динингизни холис қилган ҳолда ибодат қилинг'}, {'chapter': 39, 'verse': 3, 'text': 'أَلَا لِلَّهِ ٱلدِّينُ ٱلۡخَالِصُۚ وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ مَا نَعۡبُدُهُمۡ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلۡفَىٰٓ إِنَّ ٱللَّهَ يَحۡكُمُ بَيۡنَهُمۡ فِي مَا هُمۡ فِيهِ يَخۡتَلِفُونَۗ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ كَٰذِبٞ كَفَّارٞ\n(Эй инсонлар), огоҳ бўлингизким, холис дин ёлғиз Аллоҳникидир (яъни, ёлғиз Аллоҳгина чин ихлос билан ибодат қилинишга лойиқ Зотдир). У зотдан ўзга «дўстлар»ни («худо») қилиб олган кимсалар: «Биз (ўша «худо»ларимизга) фақат улар бизни Аллоҳга яқин қилишлари учунгина ибодат қилурмиз», (дерлар). Шак-шубҳа йўқки, Аллоҳ (Қиёмат Кунида) улар ихтилоф қилаётган нарсалар хусусида уларнинг ўртасида ҳукм қилур. Шак-шубҳа йўқки, Аллоҳ ёлғончи ва кўрнамак кимсаларни ҳидоят қилмас'}, {'chapter': 39, 'verse': 4, 'text': 'لَّوۡ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدٗا لَّٱصۡطَفَىٰ مِمَّا يَخۡلُقُ مَا يَشَآءُۚ سُبۡحَٰنَهُۥۖ هُوَ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ\nАгар Аллоҳ бола-чақа тутишни истаса, сўзсиз Ўзи яратган нарсаларидан хоҳлаганини танлаб олган бўлур эди. У зот (бундай айбдан) покдир. У яккаю ягона ва ғолиб Аллоҳдир'}, {'chapter': 39, 'verse': 5, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ يُكَوِّرُ ٱلَّيۡلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيۡلِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمًّىۗ أَلَا هُوَ ٱلۡعَزِيزُ ٱلۡغَفَّـٰرُ\nУ осмонлар ва Ерни ҳақ (қонун) билан яратгандир. У кечани кундузнинг устига ўрар, кундузни кечанинг устига ўрар. У қуёшни ҳам, ойни ҳам (Ўз амрига) бўйсундириб қўйгандир. (Уларнинг) ҳар бири маълум муддатгача жорий бўлур. Огоҳ бўлингизким, У қудратли ва бениҳоя мағфиратли Зотдир'}, {'chapter': 39, 'verse': 6, 'text': 'خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ ثُمَّ جَعَلَ مِنۡهَا زَوۡجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلۡأَنۡعَٰمِ ثَمَٰنِيَةَ أَزۡوَٰجٖۚ يَخۡلُقُكُمۡ فِي بُطُونِ أُمَّهَٰتِكُمۡ خَلۡقٗا مِّنۢ بَعۡدِ خَلۡقٖ فِي ظُلُمَٰتٖ ثَلَٰثٖۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۖ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُصۡرَفُونَ\nУ сизларни бир жондан (Одамдан) яратди, сўнгра ундан жуфтини (яъни, Ҳаввони пайдо) қилди ва сизлар учун чорва ҳайвонларидан саккизта жуфтни туширди (яъни, дунёга келтирди). У сизларни оналарингизнинг қорнида уч (қават) зулмат ичида аста-секин яратур. Мана шу Аллоҳ Парвардигорларингиздир. Барча подшоҳлик ёлғиз Уникидир. Ҳеч қандай илоҳ йўқ, магар Унинг Ўзигина бордир. Бас, (ана ўша Зотга ибодат қилмай) қаёққа бурилиб кетмоқдасизлар-а'}, {'chapter': 39, 'verse': 7, 'text': 'إِن تَكۡفُرُواْ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنكُمۡۖ وَلَا يَرۡضَىٰ لِعِبَادِهِ ٱلۡكُفۡرَۖ وَإِن تَشۡكُرُواْ يَرۡضَهُ لَكُمۡۗ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nАгар сизлар кофир бўлсангизлар, бас, албатта Аллоҳ сизлардан беҳожатдир. (Аммо) У Ўз бандаларининг кофир бўлишига рози бўлмас. Агар шукр қилсангизлар (ва иймон келтирсангизлар), У зот сизлар учун (фақат шукр қилиб, иймон келтиришларингизгагина) рози бўлур. Ҳеч бир кўтаргувчи (яъни, гуноҳкор) ўзга жоннинг юкини (яъни, гуноҳини) кўтармас. Сўнгра ёлғиз Парвардигорларингизга қайтишларингиз бордир. Бас, У зот сизларга қилиб ўтган амалларингизнинг хабарини берур. Албатта У диллардаги сирларни билгувчидир'}, {'chapter': 39, 'verse': 8, 'text': '۞وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَا رَبَّهُۥ مُنِيبًا إِلَيۡهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعۡمَةٗ مِّنۡهُ نَسِيَ مَا كَانَ يَدۡعُوٓاْ إِلَيۡهِ مِن قَبۡلُ وَجَعَلَ لِلَّهِ أَندَادٗا لِّيُضِلَّ عَن سَبِيلِهِۦۚ قُلۡ تَمَتَّعۡ بِكُفۡرِكَ قَلِيلًا إِنَّكَ مِنۡ أَصۡحَٰبِ ٱلنَّارِ\nҚачон инсонни бирон зиён ушласа, у Парвардигорига тавба-тазарруъ қилган ҳолида дуо-илтижо қилур. Сўнгра қачон (Аллоҳ) Ўз томонидан унга бирон неъмат ато этса, (инсон) илгари (Аллоҳга) қилган дуо-илтижоларини унутиб қўюр ва (ўзгаларни ҳам) Аллоҳнинг йўлидан оздириш учун У зотга «тенг»ларни (ўйлаб) топур. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Сен ўз куфринг билан (бу ўткинчи дунёда) озгина фойдаланиб қол! Шак-шубҳасиз, сен дўзах эгаларидандирсан»'}, {'chapter': 39, 'verse': 9, 'text': 'أَمَّنۡ هُوَ قَٰنِتٌ ءَانَآءَ ٱلَّيۡلِ سَاجِدٗا وَقَآئِمٗا يَحۡذَرُ ٱلۡأٓخِرَةَ وَيَرۡجُواْ رَحۡمَةَ رَبِّهِۦۗ قُلۡ هَلۡ يَسۡتَوِي ٱلَّذِينَ يَعۡلَمُونَ وَٱلَّذِينَ لَا يَعۡلَمُونَۗ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nЁки кечалари сажда қилган ва қиёмда-тик турган ҳолда тоат-ибодат қилгувчи, Охиратдан қўрқадиган ва Парвардигорининг раҳмат-марҳаматидан умид қиладиган киши (билан куфру исёнга ғарқ бўлган кимса баробар бўлурми)?!» Айтинг: «Биладиган зотлар билан билмайдиган кимсалар баробар бўлурми?!» Дарҳақиқат, фақат ақл эгаларигина панд-насиҳат олурлар'}, {'chapter': 39, 'verse': 10, 'text': 'قُلۡ يَٰعِبَادِ ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ رَبَّكُمۡۚ لِلَّذِينَ أَحۡسَنُواْ فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٞۗ وَأَرۡضُ ٱللَّهِ وَٰسِعَةٌۗ إِنَّمَا يُوَفَّى ٱلصَّـٰبِرُونَ أَجۡرَهُم بِغَيۡرِ حِسَابٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Менинг иймон келтирган бандаларимга айтинг: «Парвардигорингиздан қўрқингиз! Бу дунёда чиройли амал қилган зотлар учун (Охиратда) чиройли (оқибат-жаннат) бордир. Аллоҳнинг ери кенгдир. Ҳеч шак-шубҳа йўқки, сабр-тоқат қилгувчиларга ажр-мукофотлари ҳисоб-китобсиз тўла-тўкис қилиб берилур'}, {'chapter': 39, 'verse': 11, 'text': 'قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ\nАйтинг: «Дарҳақиқат, мен Аллоҳга У зот учун динимни холис қилган ҳолда ибодат қилишга буюрилганман'}, {'chapter': 39, 'verse': 12, 'text': 'وَأُمِرۡتُ لِأَنۡ أَكُونَ أَوَّلَ ٱلۡمُسۡلِمِينَ\nВа мен мусулмонларнинг — бўйинсунгувчиларнинг аввали — пешқадами бўлишга буюрилганман»'}, {'chapter': 39, 'verse': 13, 'text': 'قُلۡ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ\nАйтинг: «Албатта мен агар Парвардигоримга осий бўлсам, улуғ Куннинг азобидан қўрқурман»'}, {'chapter': 39, 'verse': 14, 'text': 'قُلِ ٱللَّهَ أَعۡبُدُ مُخۡلِصٗا لَّهُۥ دِينِي\nАйтинг: «Мен ёлғиз Аллоҳга У зот учун динимни холис қилган ҳолда ибодат қилурман»'}, {'chapter': 39, 'verse': 15, 'text': 'فَٱعۡبُدُواْ مَا شِئۡتُم مِّن دُونِهِۦۗ قُلۡ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَا ذَٰلِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِينُ\nЭнди сизлар эса (эй мушриклар), У зотни қўйиб, ўзларингиз хоҳлаган бутларингизга ибодат қилаверинглар, (яқинда бу ширкларингизнинг оқибати қандай бўлишини жуда яхши билиб олурсизлар!»). («Эй Муҳаммад алайҳис-салоту вас-салом, агар ота-боболарингнинг динидан чиқсанг, зиёнкор бўлурсан», дейдиган мушрикларга) айтинг: «Албатта зиёнкорлар Қиёмат Куни ўзларига ҳам, аҳли оилаларига ҳам зиён қилгувчи (яъни, ўзи ҳам мушрик бўлиб, аҳли-оиласини ҳам Тўғри Йўлдан оздиргани сабабли Қиёмат Кунида ўзи ва аҳли оиласи дўзах азобига гирифтор бўлгувчи) кимсалардир. Огоҳ бўлингизким, ана шугина очиқ зиёнкорликдир»'}, {'chapter': 39, 'verse': 16, 'text': 'لَهُم مِّن فَوۡقِهِمۡ ظُلَلٞ مِّنَ ٱلنَّارِ وَمِن تَحۡتِهِمۡ ظُلَلٞۚ ذَٰلِكَ يُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥۚ يَٰعِبَادِ فَٱتَّقُونِ\nУлар учун устларидан ҳам оловдан бўлган «соябонлар», остларидан ҳам (оловдан бўлган) «соябонлар» бўлур. Бу (бўлажак азоб-уқубат) билан Аллоҳ Ўз бандаларини қўрқитур. Эй бандаларим, Мендан қўрқингиз'}, {'chapter': 39, 'verse': 17, 'text': 'وَٱلَّذِينَ ٱجۡتَنَبُواْ ٱلطَّـٰغُوتَ أَن يَعۡبُدُوهَا وَأَنَابُوٓاْ إِلَى ٱللَّهِ لَهُمُ ٱلۡبُشۡرَىٰۚ فَبَشِّرۡ عِبَادِ\nШайтондан — унга ибодат қилишдан йироқ бўлган ва Аллоҳга (яъни, ёлғиз Аллоҳга ибодат қилишга) қайтган зотлар учун (жаннат) хушхабари бўлсин! Бас, (эй Муҳаммад алайҳис-салоту вас-салом), Менинг бандаларимга хушхабар беринг'}, {'chapter': 39, 'verse': 18, 'text': 'ٱلَّذِينَ يَسۡتَمِعُونَ ٱلۡقَوۡلَ فَيَتَّبِعُونَ أَحۡسَنَهُۥٓۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ هَدَىٰهُمُ ٱللَّهُۖ وَأُوْلَـٰٓئِكَ هُمۡ أُوْلُواْ ٱلۡأَلۡبَٰبِ\nУлар сўзга қулоқ тутиб, унинг энг гўзалига (яъни, нажотга элтгувчи рост Сўзга) эргашадиган зотларга (хушхабар беринг). Ана ўшалар Аллоҳ ҳидоят қилган зотлардир. Ва ана ўшаларгина ақл эгаларидир'}, {'chapter': 39, 'verse': 19, 'text': 'أَفَمَنۡ حَقَّ عَلَيۡهِ كَلِمَةُ ٱلۡعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِي ٱلنَّارِ\nАхир бир кимсага азоб сўзи (яъни, азобга дучор бўлиш) ҳақ бўлса (сиз уни ҳидоят қилишга қодир бўлурмисиз)?! Ахир сиз дўзахдаги кимсани қутқара олурмисиз'}, {'chapter': 39, 'verse': 20, 'text': 'لَٰكِنِ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ لَهُمۡ غُرَفٞ مِّن فَوۡقِهَا غُرَفٞ مَّبۡنِيَّةٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَعۡدَ ٱللَّهِ لَا يُخۡلِفُ ٱللَّهُ ٱلۡمِيعَادَ\nЛекин Парвардигорларидан қўрққан зотлар учун (жаннатда) устма-уст қурилган хоналар бўлиб, уларнинг остидан дарёлар оқиб турар. (Бу) Аллоҳнинг ваъдасидир! Аллоҳ ваъдасига хилоф қилмас'}, {'chapter': 39, 'verse': 21, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَسَلَكَهُۥ يَنَٰبِيعَ فِي ٱلۡأَرۡضِ ثُمَّ يُخۡرِجُ بِهِۦ زَرۡعٗا مُّخۡتَلِفًا أَلۡوَٰنُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصۡفَرّٗا ثُمَّ يَجۡعَلُهُۥ حُطَٰمًاۚ إِنَّ فِي ذَٰلِكَ لَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ\nАллоҳ осмондан сув (ёмғир-қор) ёғдириб, уни ердаги булоқлар-чашмалар қилиб оқизиб қўйганини кўрмадингизми? Сўнгра у (сув)нинг ёрдамида ранго-ранг экин-тикин чиқарур, сўнгра у қурир, бас, сиз уни сарғайган ҳолда кўрурсиз, сўнгра (Аллоҳ) уни хас-хашакка айлантирур. Албатта бунда ақл эгалари учун эслатма-ибрат бордир'}, {'chapter': 39, 'verse': 22, 'text': 'أَفَمَن شَرَحَ ٱللَّهُ صَدۡرَهُۥ لِلۡإِسۡلَٰمِ فَهُوَ عَلَىٰ نُورٖ مِّن رَّبِّهِۦۚ فَوَيۡلٞ لِّلۡقَٰسِيَةِ قُلُوبُهُم مِّن ذِكۡرِ ٱللَّهِۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ\nАхир Аллоҳ кўксини Ислом учун кенг қилиб қўйган, бас, ўзи Парвардигори томонидан бир нур — ҳидоят устида бўлган киши (куфр зулматларида адашиб-улоқиб юрган кимса билан баробар бўлурми)?! Бас, диллари Аллоҳни эслашдан қотиб қолган (яъни, Аллоҳни эслашни тарк қилган) кимсаларга ҳалокат бўлгай! Улар очиқ залолатдадирлар'}, {'chapter': 39, 'verse': 23, 'text': 'ٱللَّهُ نَزَّلَ أَحۡسَنَ ٱلۡحَدِيثِ كِتَٰبٗا مُّتَشَٰبِهٗا مَّثَانِيَ تَقۡشَعِرُّ مِنۡهُ جُلُودُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُمۡ ثُمَّ تَلِينُ جُلُودُهُمۡ وَقُلُوبُهُمۡ إِلَىٰ ذِكۡرِ ٱللَّهِۚ ذَٰلِكَ هُدَى ٱللَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُۚ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٍ\nАллоҳ энг гўзал Сўзни (оятлари фасоҳат ва балоғатда) бир-бирига ўхшаган, (ичидаги ҳукмлари) такрор-такрор келгувчи бир Китоб — Қуръон қилиб нозил қилдики, (ундаги Аллоҳнинг азоби ҳақидаги оятларни тиловат қилганларида) Парвардигорларидан қўрқадиган зотларнинг терилари титраб кетар, сўнгра терилари — баданлари ҳам, диллари ҳам Аллоҳнинг зикрига юмшар — мойил бўлур. Мана шу (Китоб) Аллоҳнинг ҳидоятидирки, унга Ўзи хоҳлаган кишиларни ҳидоят қилур. Ва кимни Аллоҳ йўлдан оздирса, бас, унинг учун бирон ҳидоят қилгувчи бўлмас'}, {'chapter': 39, 'verse': 24, 'text': 'أَفَمَن يَتَّقِي بِوَجۡهِهِۦ سُوٓءَ ٱلۡعَذَابِ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَقِيلَ لِلظَّـٰلِمِينَ ذُوقُواْ مَا كُنتُمۡ تَكۡسِبُونَ\nАхир Қиёмат Кунида ёмон азобдан ўз юзи билан сақланадиган кимса (у Кундаги ҳар қандай азобдан тинч-хотиржам киши билан баробар бўлурми)?! (У кунда) золим-кофирларга «Ўзларингиз касб қилиб ўтган нарсани (яъни, динсизлик мевасини) тотиб кўринглар!», дейилди'}, {'chapter': 39, 'verse': 25, 'text': 'كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ\nУлардан аввалгилар ҳам (ўз пайғамбарларини) ёлғончи қилганларида, уларга азоб ўзлари сезмайдиган томондан келган эди'}, {'chapter': 39, 'verse': 26, 'text': 'فَأَذَاقَهُمُ ٱللَّهُ ٱلۡخِزۡيَ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\nБас, Аллоҳ уларга мана шу ҳаёти дунёда расволикни тортдирди. Шак-шубҳасиз, Охират азоби янада каттароқдир. Агар улар (Аллоҳнинг азобини) билсалар эди (ўз Пайғамбарларини ёлғончи қилмаган бўлур эдилар)'}, {'chapter': 39, 'verse': 27, 'text': 'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖ لَّعَلَّهُمۡ يَتَذَكَّرُونَ\nАниқки, Биз ушбу Қуръонда одамлар учун турли мисоллар келтирдик. Шояд эслатма-ибрат олсалар'}, {'chapter': 39, 'verse': 28, 'text': 'قُرۡءَانًا عَرَبِيًّا غَيۡرَ ذِي عِوَجٖ لَّعَلَّهُمۡ يَتَّقُونَ\n(Биз уни) бирон эгрилик эгаси бўлмаган арабий Қуръон ҳолида (нозил қилдик). Шояд (Аллоҳдан) қўрқсалар'}, {'chapter': 39, 'verse': 29, 'text': 'ضَرَبَ ٱللَّهُ مَثَلٗا رَّجُلٗا فِيهِ شُرَكَآءُ مُتَشَٰكِسُونَ وَرَجُلٗا سَلَمٗا لِّرَجُلٍ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ\nАллоҳ (шундай) бир мисол келтирди: бир киши — қул борки, унинг устида талашгувчи шериклар (яъни, хожалар) бор. Яна бир киши — қул борки, у бус-бутун ҳолида бир киши — хожаникидир. Иккисининг мисоли баробар бўлурми?! (Йўқ, баробар бўлмас. Худди шунингдек, бир неча «худо»ларга сиғинадиган мушрик билан ёлғиз Аллоҳгагина бандалик қиладиган мўмин ҳам баробар бўлмас). Ҳамду сано (ёлғиз) Аллоҳ учундир. Йўқ, уларнинг кўплари билмаслар, (бас, ана шу сабабдан турли «худо»ларга сиғинурлар)'}, {'chapter': 39, 'verse': 30, 'text': 'إِنَّكَ مَيِّتٞ وَإِنَّهُم مَّيِّتُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ҳеч шак-шубҳасиз, сиз ҳам ўлгувчидирсиз, улар ҳам ўлгувчидирлар'}, {'chapter': 39, 'verse': 31, 'text': 'ثُمَّ إِنَّكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ عِندَ رَبِّكُمۡ تَخۡتَصِمُونَ\nСўнгра албатта, сизлар Қиёмат Кунида Парвардигорларингиз ҳузурида (бир-бирларингиз билан) талашиб-тортишурсизлар (ва сиз ўз зиммангиздаги пайғамбарликни тўла-тўкис етказганингизга ҳужжат келтирурсиз, улар эса сизни ёлғончи қилиб, куфру залолатда ўтганларига турли баҳоналарни рўкач қилурлар, аммо Аллоҳнинг адолатли ҳукмидан қочиб қутула олмаслар)'}, {'chapter': 39, 'verse': 32, 'text': '۞فَمَنۡ أَظۡلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدۡقِ إِذۡ جَآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكَٰفِرِينَ\nАхир Аллоҳ шаънига (Унинг шериги ва бола-чақаси бор», деб ёлғон сўзлаган ва Рост — Қуръонни ўзига келиб-етган пайтидаёқ (ўйламай-нетмай) ёлғон деган кимсадан ҳам золимроқ бирон кимса борми?! Ё жаҳаннамда кофирлар учун жой йўқми'}, {'chapter': 39, 'verse': 33, 'text': 'وَٱلَّذِي جَآءَ بِٱلصِّدۡقِ وَصَدَّقَ بِهِۦٓ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُتَّقُونَ\nРост Қуръонни келтирган зот (яъни, Муҳаммад алайҳис-салоту вас-салом) ва уни тасдиқ этган (мўминлар) — ана ўшалар тақводор зотлардир'}, {'chapter': 39, 'verse': 34, 'text': 'لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ جَزَآءُ ٱلۡمُحۡسِنِينَ\nУлар учун Парвардигорлари ҳузурида (яъни, жаннатда) ўзлари хоҳлаган нарсалари бордир. Бу чиройли амал қилгувчи кишиларнинг мукофотидир'}, {'chapter': 39, 'verse': 35, 'text': 'لِيُكَفِّرَ ٱللَّهُ عَنۡهُمۡ أَسۡوَأَ ٱلَّذِي عَمِلُواْ وَيَجۡزِيَهُمۡ أَجۡرَهُم بِأَحۡسَنِ ٱلَّذِي كَانُواْ يَعۡمَلُونَ\nЗеро, Аллоҳ уларнинг қилган ёмон амалларини ўчириб, қилиб ўтган энг чиройли амалларининг ажри билан мукофотлар'}, {'chapter': 39, 'verse': 36, 'text': 'أَلَيۡسَ ٱللَّهُ بِكَافٍ عَبۡدَهُۥۖ وَيُخَوِّفُونَكَ بِٱلَّذِينَ مِن دُونِهِۦۚ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ\nАллоҳ Ўз бандасига (яъни, Муҳаммад алайҳис-салоту вас-саломга, у кишини барча бало-қазодан асраш учун) етарли эмасми?! (Эй Муҳаммад алайҳис-салоту вас-салом), улар сизни (Аллоҳдан) ўзга бутлари билан қўрқитурлар. Кимни Аллоҳ йўлдан оздирса, бас, унинг учун бирон ҳидоят қилгувчи бўлмас'}, {'chapter': 39, 'verse': 37, 'text': 'وَمَن يَهۡدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّۗ أَلَيۡسَ ٱللَّهُ بِعَزِيزٖ ذِي ٱنتِقَامٖ\nКимни Аллоҳ ҳидоят қилса, бас, унинг учун бирон йўлдан оздиргувчи бўлмас. Аллоҳ қудратли ва (Ўз душманларидан) интиқом олгувчи эмасми'}, {'chapter': 39, 'verse': 38, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ ٱللَّهُۚ قُلۡ أَفَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ إِنۡ أَرَادَنِيَ ٱللَّهُ بِضُرٍّ هَلۡ هُنَّ كَٰشِفَٰتُ ضُرِّهِۦٓ أَوۡ أَرَادَنِي بِرَحۡمَةٍ هَلۡ هُنَّ مُمۡسِكَٰتُ رَحۡمَتِهِۦۚ قُلۡ حَسۡبِيَ ٱللَّهُۖ عَلَيۡهِ يَتَوَكَّلُ ٱلۡمُتَوَكِّلُونَ\nҚасамки, агар сиз улардан «Осмонлар ва Ерни ким яратган», деб сўрасангиз, улар албатта «Аллоҳ», дерлар. Айтинг: «Энди хабар беринглар-чи, сизлар Аллоҳни қўйиб дуо-илтижо қилаётган бутларингиз — агар Аллоҳ менга бирон зиён етказишни истаса, ўша (бутлар) У зотнинг зиёнини арита олгувчимилар? Ёки (Аллоҳ) менга бирон марҳамат қилишни ирода қилса, ўша (бутлар) У зотнинг марҳаматини ушлаб қолгувчимилар?!» Айтинг: «Менга Аллоҳнинг Ўзи етарлидир. Таваккул қилгувчи — суянгувчи зотлар ёлғиз Унга таваккул қилурлар»'}, {'chapter': 39, 'verse': 39, 'text': 'قُلۡ يَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ فَسَوۡفَ تَعۡلَمُونَ\nАйтинг: «Эй қавмим, жойингиздан жилмай (яъни, куфрингиздан қайтмай) амалингизни қилаверинг. Мен ҳам (Парвардигорим буюрган) амални қилгувчиман. Бас, яқинда билажаксизлар'}, {'chapter': 39, 'verse': 40, 'text': 'مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ\nКимга расво қиладиган азоб келишини ва мангу азоб тушишини билажаксизлар!»'}, {'chapter': 39, 'verse': 41, 'text': 'إِنَّآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ لِلنَّاسِ بِٱلۡحَقِّۖ فَمَنِ ٱهۡتَدَىٰ فَلِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٍ\nАлбатта, Биз сизга бу Китобни барча одамлар (билиб, амал қилишлари) учун ҳаққи-рост нозил қилдик. Бас, ким ҳидоят йўлига юрса, ўз фойдасига юрган бўлур. Ким (бу ҳидоят йўлидан) озса, фақат ўзининг зиёнига адашган бўлур. Сиз уларнинг устида вакил — қўриқчи эмассиз'}, {'chapter': 39, 'verse': 42, 'text': 'ٱللَّهُ يَتَوَفَّى ٱلۡأَنفُسَ حِينَ مَوۡتِهَا وَٱلَّتِي لَمۡ تَمُتۡ فِي مَنَامِهَاۖ فَيُمۡسِكُ ٱلَّتِي قَضَىٰ عَلَيۡهَا ٱلۡمَوۡتَ وَيُرۡسِلُ ٱلۡأُخۡرَىٰٓ إِلَىٰٓ أَجَلٖ مُّسَمًّىۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nЖонларни ўлим вақтларида, (ҳали-ҳануз) ўлмаганларини эса ухлаётган пайтларида Аллоҳ олур. Бас, Ўзи ўлимга ҳукм қилган жонларни (баданларга қайтармасдан) ушлаб қолур, бошқаларини эса белгиланган бир муддатгача (яъни, ажаллари етгунича) қўйиб юборур. Албатта бунда тафаккур қиладиган қавм учун оят-ибратлар бордир'}, {'chapter': 39, 'verse': 43, 'text': 'أَمِ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ شُفَعَآءَۚ قُلۡ أَوَلَوۡ كَانُواْ لَا يَمۡلِكُونَ شَيۡـٔٗا وَلَا يَعۡقِلُونَ\n(Йўқ, мушриклар тафаккур қилмадилар), балки улар Аллоҳдан ўзга «оқловчилар»ни (яъни, ўзларининг гумонларича уларни Аллоҳ ҳузурида шафоат қиладиган «оқловчилар»ни — бутларни) ушладилар. (Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «(Ўша жонсиз бутлар сизларни оқлайдими?!) Агар улар бирон нарсага эга бўла олмайдиган ва (бирон нарсани) англамайдиган бўлсалар ҳам-а!»'}, {'chapter': 39, 'verse': 44, 'text': 'قُل لِّلَّهِ ٱلشَّفَٰعَةُ جَمِيعٗاۖ لَّهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ\nАйтинг: «Барча шафоат-оқлов ёлғиз Аллоҳникидир. Осмонлар ва Ернинг подшоҳлиги ҳам Уникидир. Сўнгра (барчаларингиз) ёлғиз Унгагина қайтарилурсизлар»'}, {'chapter': 39, 'verse': 45, 'text': 'وَإِذَا ذُكِرَ ٱللَّهُ وَحۡدَهُ ٱشۡمَأَزَّتۡ قُلُوبُ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِۖ وَإِذَا ذُكِرَ ٱلَّذِينَ مِن دُونِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ\nҚачон ёлғиз Аллоҳ зикр қилинса, Охиратга иймон келтирмайдиган кимсаларнинг диллари сиқилиб кетар. Қачон У зотдан ўзга бутлар зикр қилинганида эса баногоҳ улар шодланиб кетурлар'}, {'chapter': 39, 'verse': 46, 'text': 'قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ عَٰلِمَ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ أَنتَ تَحۡكُمُ بَيۡنَ عِبَادِكَ فِي مَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nАйтинг: «Эй Аллоҳ, (эй) осмонлар ва Ерни илк яратгувчи Зот, (эй) ғайбу шаҳодатни (яъни, яширин ва ошкор нарсаларни) билгувчи Зот, ёлғиз Ўзинггина бандаларинг ўртасида улар ихтилоф қилиб ўтган нарсалар ҳақида ҳукм қилурсан»'}, {'chapter': 39, 'verse': 47, 'text': 'وَلَوۡ أَنَّ لِلَّذِينَ ظَلَمُواْ مَا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡاْ بِهِۦ مِن سُوٓءِ ٱلۡعَذَابِ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمۡ يَكُونُواْ يَحۡتَسِبُونَ\nАгар золим-кофир кимсалар учун Ердаги бор нарса ва у билан бирга яна ўшанинг мислича нарса бўлса, албатта улар Қиёмат Кунидаги азобнинг ёмонлигидан, ўша (қўлларидаги бор нарсаларини) тўлов қилиб бериб юборган бўлур эдилар. (Чунки у Кунда) уларга Аллоҳ томонидан улар ўйлаб ҳам кўрмаган нарсалар — азоблар кўринди'}, {'chapter': 39, 'verse': 48, 'text': 'وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУларга ўзлари касб қилган ёмонликлари ошкор бўлди ва уларни ўзлари (дунёдалик чоғларида) масхара қилиб ўтган азоб ўраб олди'}, {'chapter': 39, 'verse': 49, 'text': 'فَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَانَا ثُمَّ إِذَا خَوَّلۡنَٰهُ نِعۡمَةٗ مِّنَّا قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمِۭۚ بَلۡ هِيَ فِتۡنَةٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nБас, қачон инсонни бирон зиён ушласа, У бизга дуо-илтижо қилур. Сўнгра қачон Биз Ўз томонимиздан унга бирон неъмат ато этсак, у «Ҳеч шак-шубҳасиз, бу (неъмат) менга ўзимнинг билимдонлигим сабабли берилди», дер. (Йўқ, у неъмат ўша инсоннинг саъй-ҳаракати ва донолиги учун берилган эмас), балки у (ўша банданинг шукр ё кўрнамаклик қилишини билиш учун) бир синов — имтиҳондир. Лекин уларнинг кўплари билмаслар'}, {'chapter': 39, 'verse': 50, 'text': 'قَدۡ قَالَهَا ٱلَّذِينَ مِن قَبۡلِهِمۡ فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ\nУ (сўз)ни улардан аввалгилар ҳам айтган эдилар. Бас, уларга ўзлари касб қилиб топган нарсалари асқотгани йўқ'}, {'chapter': 39, 'verse': 51, 'text': 'فَأَصَابَهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْۚ وَٱلَّذِينَ ظَلَمُواْ مِنۡ هَـٰٓؤُلَآءِ سَيُصِيبُهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْ وَمَا هُم بِمُعۡجِزِينَ\nБас, уларга қилган ёмонликлари етди. Ана улардан (Макка мушрикларидан) бўлган золим кимсаларга ҳам яқинда қилган ёмонликлари етар. Улар ҳам қочиб қутулгувчи эмасдирлар'}, {'chapter': 39, 'verse': 52, 'text': 'أَوَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ\nАхир улар Аллоҳ Ўзи хоҳлаган кишининг ризқини кенг қилишини ва (Ўзи хоҳлаган кишининг ризқини) танг қилишини билмадиларми?! Албатта бунда иймон келтирадиган қавм учун оят-ибратлар бордир'}, {'chapter': 39, 'verse': 53, 'text': '۞قُلۡ يَٰعِبَادِيَ ٱلَّذِينَ أَسۡرَفُواْ عَلَىٰٓ أَنفُسِهِمۡ لَا تَقۡنَطُواْ مِن رَّحۡمَةِ ٱللَّهِۚ إِنَّ ٱللَّهَ يَغۡفِرُ ٱلذُّنُوبَ جَمِيعًاۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Менинг ўз жонларига жиноят қилган (турли гуноҳ-маъсиятлар қилиш билан) бандаларимга айтинг: «Аллоҳнинг раҳмат-марҳаматидан ноумид бўлмангиз! Албатта Аллоҳ (Ўзи хоҳлаган бандаларининг) барча гуноҳларини мағфират қилур. Албатта Унинг Ўзигина мағфиратли, меҳрибондир'}, {'chapter': 39, 'verse': 54, 'text': 'وَأَنِيبُوٓاْ إِلَىٰ رَبِّكُمۡ وَأَسۡلِمُواْ لَهُۥ مِن قَبۡلِ أَن يَأۡتِيَكُمُ ٱلۡعَذَابُ ثُمَّ لَا تُنصَرُونَ\nСизларга азоб келиб, сўнгра ёрдам берилмай қолишидан илгари (яъни, Аллоҳнинг бирон бало-қазосига дучор бўлмай туриб), Парвардигорларингизга қайтинглар — Унга бўйинсунинглар'}, {'chapter': 39, 'verse': 55, 'text': 'وَٱتَّبِعُوٓاْ أَحۡسَنَ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَكُمُ ٱلۡعَذَابُ بَغۡتَةٗ وَأَنتُمۡ لَا تَشۡعُرُونَ\nСизларга, ўзларингиз сезмаган ҳолингизда тўсатдан азоб келиб қолишидан илгари Парвардигорларингиз томонидан сизларга нозил қилинган энг гўзал нарса — Қуръонга эргашинглар'}, {'chapter': 39, 'verse': 56, 'text': 'أَن تَقُولَ نَفۡسٞ يَٰحَسۡرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِي جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّـٰخِرِينَ\nБирон жон: «Аллоҳ ҳаққида (яъни, У зотга тоат-ибодат қилишда) сусткашлик қилганимга надомат бўлгай! Ростдан ҳам мен (Унинг динини ва Қуръонини масхара қилгувчилардан бўлган эдим», деб қолмаслиги учун)'}, {'chapter': 39, 'verse': 57, 'text': 'أَوۡ تَقُولَ لَوۡ أَنَّ ٱللَّهَ هَدَىٰنِي لَكُنتُ مِنَ ٱلۡمُتَّقِينَ\nЁки: «Агар Аллоҳ мени ҳидоят қилганида, албатта тақводорлардан бўлур эдим», деб қолмаслиги учун'}, {'chapter': 39, 'verse': 58, 'text': 'أَوۡ تَقُولَ حِينَ تَرَى ٱلۡعَذَابَ لَوۡ أَنَّ لِي كَرَّةٗ فَأَكُونَ مِنَ ٱلۡمُحۡسِنِينَ\nЁки азобни кўрган чоғида: «Қани энди мен учун яна бир карра (яшашнинг имкони) бўлса-ю, мен ҳам чиройли амал қилгувчилардан бўлсам», деб қолмаслиги учун (Қуръонга эргашинглар)'}, {'chapter': 39, 'verse': 59, 'text': 'بَلَىٰ قَدۡ جَآءَتۡكَ ءَايَٰتِي فَكَذَّبۡتَ بِهَا وَٱسۡتَكۡبَرۡتَ وَكُنتَ مِنَ ٱلۡكَٰفِرِينَ\n(Акс ҳолда унга Аллоҳ томонидан айтилур): «Йўқ, (Мен сенга ҳидоят йўлини кўрсатган эдим), аниқки, сенга Менинг оятларим келганида, сен уларни ёлғон деган эдинг ва кибр-ҳаво қилиб, кофирлардан бўлган эдинг»'}, {'chapter': 39, 'verse': 60, 'text': 'وَيَوۡمَ ٱلۡقِيَٰمَةِ تَرَى ٱلَّذِينَ كَذَبُواْ عَلَى ٱللَّهِ وُجُوهُهُم مُّسۡوَدَّةٌۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡمُتَكَبِّرِينَ\nҚиёмат Кунида Аллоҳ шаънига ёлғон сўзлаган кимсаларни юзлари қора ҳолда кўрурсиз. Ё жаҳаннамда мутакаббир кимсалар учун жой йўқми'}, {'chapter': 39, 'verse': 61, 'text': 'وَيُنَجِّي ٱللَّهُ ٱلَّذِينَ ٱتَّقَوۡاْ بِمَفَازَتِهِمۡ لَا يَمَسُّهُمُ ٱلسُّوٓءُ وَلَا هُمۡ يَحۡزَنُونَ\nАллоҳ, тақво қилган зотларни (жаннатга) эришганлари сабабли (жаҳаннам азобидан) қутқарур ва уларга бирон ёмонлик ҳам етмас ва улар ғамгин ҳам бўлмаслар'}, {'chapter': 39, 'verse': 62, 'text': 'ٱللَّهُ خَٰلِقُ كُلِّ شَيۡءٖۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ\nАллоҳ барча нарсанинг Яратгувчисидир. У барча нарсанинг устида вакил-ҳомийдир'}, {'chapter': 39, 'verse': 63, 'text': 'لَّهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nОсмонлар ва Ернинг очқичлари Унинг (қўл остидадир). Аллоҳ оятларига кофир бўлган кимсалар — ана ўшаларгина зиён кўргувчилардир'}, {'chapter': 39, 'verse': 64, 'text': 'قُلۡ أَفَغَيۡرَ ٱللَّهِ تَأۡمُرُوٓنِّيٓ أَعۡبُدُ أَيُّهَا ٱلۡجَٰهِلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, у мушрикларга) айтинг: «Ҳали сизлар мени Аллоҳдан ўзгага ибодат қилишга буюрурмисизлар, эй нодонлар!»'}, {'chapter': 39, 'verse': 65, 'text': 'وَلَقَدۡ أُوحِيَ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ لَئِنۡ أَشۡرَكۡتَ لَيَحۡبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ\nДарҳақиқат, сизга ҳам, сиздан аввалги (пайғамбарларга ҳам шундай) ваҳий қилингандир: «Қасамки: агар мушрик бўлсанг, албатта қилган амалинг беҳуда кетур ва албатта зиён кўргувчилардан бўлиб қолурсан'}, {'chapter': 39, 'verse': 66, 'text': 'بَلِ ٱللَّهَ فَٱعۡبُدۡ وَكُن مِّنَ ٱلشَّـٰكِرِينَ\nЙўқ, сен ёлғиз Аллоҳгагина ибодат қилгин ва шукр қилгувчилардан бўлгин!»'}, {'chapter': 39, 'verse': 67, 'text': 'وَمَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦ وَٱلۡأَرۡضُ جَمِيعٗا قَبۡضَتُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ وَٱلسَّمَٰوَٰتُ مَطۡوِيَّـٰتُۢ بِيَمِينِهِۦۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ\nУлар Аллоҳни тўғри таний олмадилар! Бутун Ер Қиёмат Кунида Унинг қабзаси — чангали(дадир). Осмонлар эса Унинг қўлига йиғилгандир. У зот уларнинг ширкларидан пок ва юксакдир'}, {'chapter': 39, 'verse': 68, 'text': 'وَنُفِخَ فِي ٱلصُّورِ فَصَعِقَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۖ ثُمَّ نُفِخَ فِيهِ أُخۡرَىٰ فَإِذَا هُمۡ قِيَامٞ يَنظُرُونَ\nСур чалинди-ю, осмонлар ва Ердаги бор жонзот ўлди, магар Аллоҳ хоҳлаган зотларгина (тирик қолдилар). Сўнгра у иккинчи бор чалинди-да, баногоҳ улар (яъни, барча халойиқ қайта тирилди, қабрларидан) туриб, (Аллоҳнинг амрига) кўз тутарлар'}, {'chapter': 39, 'verse': 69, 'text': 'وَأَشۡرَقَتِ ٱلۡأَرۡضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلۡكِتَٰبُ وَجِاْيٓءَ بِٱلنَّبِيِّـۧنَ وَٱلشُّهَدَآءِ وَقُضِيَ بَيۡنَهُم بِٱلۡحَقِّ وَهُمۡ لَا يُظۡلَمُونَ\nЕр (яъни, маҳшаргоҳ) Парвардигорининг нури билан ёришди; китоб — номаи аъмол (ҳисоб-китоб қилиш учун ҳозирлаб) қўйилди; пайғамбарлар ва гувоҳлар келтирилди ва уларнинг (яъни, барча бандаларнинг) ўрталарида (биронталарига) зулм қилинмаган ҳолда ҳақ (ҳукм) билан ҳукм қилинди'}, {'chapter': 39, 'verse': 70, 'text': 'وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُوَ أَعۡلَمُ بِمَا يَفۡعَلُونَ\nВа ҳар бир жонга қилган амали(нинг мукофот ёки жазоси) комил қилиб берилди, (Аллоҳ) уларнинг қилар ишларини жуда яхши билур'}, {'chapter': 39, 'verse': 71, 'text': 'وَسِيقَ ٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ زُمَرًاۖ حَتَّىٰٓ إِذَا جَآءُوهَا فُتِحَتۡ أَبۡوَٰبُهَا وَقَالَ لَهُمۡ خَزَنَتُهَآ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَتۡلُونَ عَلَيۡكُمۡ ءَايَٰتِ رَبِّكُمۡ وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ بَلَىٰ وَلَٰكِنۡ حَقَّتۡ كَلِمَةُ ٱلۡعَذَابِ عَلَى ٱلۡكَٰفِرِينَ\nВа кофир булган кимса тўда-тўда қилиниб жаҳаннамга ҳайдалди. То қачон улар (жаҳаннамга) келиб етганларида, унинг дарвозалари очилди ва унинг қўриқчилари (кофирларга): «Сизларга ўзларингиздан бўлган пайғамбарлар — Парвардигорларингизнинг оятларини сизларга тиловат қилган ҳолларида ва сизларни мана шу Кунингиздаги мулоқотдан огоҳлантирган ҳолларида келмаганмидилар», деганларида улар: «Ҳа, (келганлар), лекин азоб сўзи кофирлар устига ҳақ бўлгандир, (бас, бизлар ҳам уларни ёлғончи қилганимиз сабабли мана шу азобга гирифтор бўлдик)», дедилар'}, {'chapter': 39, 'verse': 72, 'text': 'قِيلَ ٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ\n(Шунда уларга) айтилди: «Жаҳаннам дарвозаларидан киринглар! Сизлар ўша жойда мангу қолурсизлар!» Бас, кибр-ҳаво қилгувчи кимсаларнинг жойлари нақадар ёмондир'}, {'chapter': 39, 'verse': 73, 'text': 'وَسِيقَ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ إِلَى ٱلۡجَنَّةِ زُمَرًاۖ حَتَّىٰٓ إِذَا جَآءُوهَا وَفُتِحَتۡ أَبۡوَٰبُهَا وَقَالَ لَهُمۡ خَزَنَتُهَا سَلَٰمٌ عَلَيۡكُمۡ طِبۡتُمۡ فَٱدۡخُلُوهَا خَٰلِدِينَ\nПарвардигорларидан қўрққан зотлар эса тўп-тўп ҳолда жаннатга «ҳайдаладилар». То қачон улар дарвозалари очилган ҳолдаги (жаннатга) келиб етганларида ва унинг қўриқчилари: «Сизларга тинчлик-омонлик бўлсин! Хуш келдингиз! Бас, унга мангу қолгувчи бўлган ҳолларингизда кирингиз», деганларида (улар беҳад шодланурлар)'}, {'chapter': 39, 'verse': 74, 'text': 'وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي صَدَقَنَا وَعۡدَهُۥ وَأَوۡرَثَنَا ٱلۡأَرۡضَ نَتَبَوَّأُ مِنَ ٱلۡجَنَّةِ حَيۡثُ نَشَآءُۖ فَنِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ\nВа «Бизларга ваъдасини рост қилган ва бизларни бу (жаннат) ерига ворис қилган ҳамда жаннатдан ўзимиз хоҳлаган тарафда ўрин олишимизга (муваффақ қилган) Аллоҳга ҳамду сано бўлсин», дерлар. Бас, (чиройли) амал қилгувчи зотларнинг ажр-мукофоти нақадар яхшидир'}, {'chapter': 39, 'verse': 75, 'text': 'وَتَرَى ٱلۡمَلَـٰٓئِكَةَ حَآفِّينَ مِنۡ حَوۡلِ ٱلۡعَرۡشِ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡۚ وَقُضِيَ بَيۡنَهُم بِٱلۡحَقِّۚ وَقِيلَ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз (ўша Кунда) фаришталар Арш атрофини ўраб олган ҳолларида Парвардигорларига ҳамду сано айтиш билан (У зотни) поклаб-тасбеҳ айтаётганларини кўрурсиз. (Зеро) уларнинг (яъни, барча бандаларнинг) ўрталаридан ҳақ (ҳукм) билан ҳукм қилинди. Ва «Ҳамду сано барча оламларнинг Парвардигори бўлмиш Аллоҳ учундир», дейилди'}, {'chapter': 40, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 40, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\n(Ушбу Қуръон) қудратли ва билгувчи Аллоҳ томонидан нозил қилинган Китобдир'}, {'chapter': 40, 'verse': 3, 'text': 'غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوۡبِ شَدِيدِ ٱلۡعِقَابِ ذِي ٱلطَّوۡلِۖ لَآ إِلَٰهَ إِلَّا هُوَۖ إِلَيۡهِ ٱلۡمَصِيرُ\n(У зот) гуноҳни мағфират қилгувчи, тавба-тазарруъни қабул қилгувчи, азоби қаттиқ ва инъом-эҳсон Эгасидир. Ҳеч қандай илоҳ йўқ, магар Унинг Ўзигина бордир. Фақат Унинг Ўзига қайтилур'}, {'chapter': 40, 'verse': 4, 'text': 'مَا يُجَٰدِلُ فِيٓ ءَايَٰتِ ٱللَّهِ إِلَّا ٱلَّذِينَ كَفَرُواْ فَلَا يَغۡرُرۡكَ تَقَلُّبُهُمۡ فِي ٱلۡبِلَٰدِ\nАллоҳнинг оятлари ҳақида фақат кофир бўлган кимсаларгина талашиб-тортишурлар. Бас, сизни уларнинг шаҳарларида кезишлари алдаб қўймасин'}, {'chapter': 40, 'verse': 5, 'text': 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَٱلۡأَحۡزَابُ مِنۢ بَعۡدِهِمۡۖ وَهَمَّتۡ كُلُّ أُمَّةِۭ بِرَسُولِهِمۡ لِيَأۡخُذُوهُۖ وَجَٰدَلُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّ فَأَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ\nУлардан (яъни, Макка кофирларидан) илгари Нуҳ қавми ва у (қавмдан) кейинги (Од, Самуд қабилалари каби турли) фирқалар ҳам (ўз пайғамбарларини) ёлғончи қилганлар. (Ўша миллатлардан) ҳар бир миллат ўзларининг пайғамбарларини тутишга-ўлдиришга қасд қилган ва ботил (ҳужжатлар) билан талашиб-тортишиб, унинг ёрдамида Ҳақни енгмоқчи бўлган эдилар. Шунда Мен уларни (Ўз азобим билан) ушладим. Бас, Менинг азобим қандай бўлди'}, {'chapter': 40, 'verse': 6, 'text': 'وَكَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ أَصۡحَٰبُ ٱلنَّارِ\n(Эй Муҳаммад алайҳис-салоту вас-салом, сизнинг қавмингиз орасидаги) кофир бўлган кимсаларнинг устига ҳам Парвардигорингизнинг Сўзи — азоби мана шундай ҳақ бўлгандир, албатта улар дўзах эгаларидир'}, {'chapter': 40, 'verse': 7, 'text': 'ٱلَّذِينَ يَحۡمِلُونَ ٱلۡعَرۡشَ وَمَنۡ حَوۡلَهُۥ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيُؤۡمِنُونَ بِهِۦ وَيَسۡتَغۡفِرُونَ لِلَّذِينَ ءَامَنُواْۖ رَبَّنَا وَسِعۡتَ كُلَّ شَيۡءٖ رَّحۡمَةٗ وَعِلۡمٗا فَٱغۡفِرۡ لِلَّذِينَ تَابُواْ وَٱتَّبَعُواْ سَبِيلَكَ وَقِهِمۡ عَذَابَ ٱلۡجَحِيمِ\nАршни кўтариб турадиган ва унинг атрофидаги (фаришталар) Парвардигорларига ҳамду сано айтиш билан (У зотни барча айбу нуқсонлардан) поклаб-тасбеҳ айтурлар ва У зотга иймон келтирурлар, ҳамда иймон келтирган кишиларни мағфират қилишини сўрарлар: «Парвардигоро, Ўзинг раҳмат-меҳрибонлик ва илм жиҳатидан барча нарсадан кенгдирсан. Бас, тавба-тазарруъ қилган ва Сенинг йўлингга эргашган кишиларни Ўзинг мағфират қилгин ва уларни дўзах азобидан сақлагин'}, {'chapter': 40, 'verse': 8, 'text': 'رَبَّنَا وَأَدۡخِلۡهُمۡ جَنَّـٰتِ عَدۡنٍ ٱلَّتِي وَعَدتَّهُمۡ وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّـٰتِهِمۡۚ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nПарвардигоро, уларни ҳам, уларнинг ота-боболари, жуфти ҳалоллари ва зурриётлари орасидаги солиҳ-мўмин бўлган кишиларни ҳам Ўзинг уларга ваъда қилган мангу жаннатларга дохил қилгин. Албатта Сенинг Ўзинггина қудрат ва ҳикмат Соҳибидирсан'}, {'chapter': 40, 'verse': 9, 'text': 'وَقِهِمُ ٱلسَّيِّـَٔاتِۚ وَمَن تَقِ ٱلسَّيِّـَٔاتِ يَوۡمَئِذٖ فَقَدۡ رَحِمۡتَهُۥۚ وَذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nЎзинг уларни барча ёмонликлардан асрагин. Кимни ўша Кундаги (Қиёматдаги) ёмонликлардан асрасанг, бас, ҳақиқатан, унга раҳм-шафқат қилибсан. Мана шу буюк бахтдир»'}, {'chapter': 40, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ يُنَادَوۡنَ لَمَقۡتُ ٱللَّهِ أَكۡبَرُ مِن مَّقۡتِكُمۡ أَنفُسَكُمۡ إِذۡ تُدۡعَوۡنَ إِلَى ٱلۡإِيمَٰنِ فَتَكۡفُرُونَ\nШубҳа йўқки, кофир бўлган кимсаларга (Қиёмат Кунида, дўзахга ташланиб, шундай азобга дучор қилган нафсларини лаънатлаб турган вақтларида): «Сизлар (ҳаёти дунёда) иймонга чорланиб (иймон келтириш ўрнига) кофир бўлган чоғларингиздаги Аллоҳнинг ғазаби-ёмон кўриши сизларнинг (бу Кундаги) ўз нафсларингизни ёмон кўришингиздан қаттароқдир», деб нидо қилинур'}, {'chapter': 40, 'verse': 11, 'text': 'قَالُواْ رَبَّنَآ أَمَتَّنَا ٱثۡنَتَيۡنِ وَأَحۡيَيۡتَنَا ٱثۡنَتَيۡنِ فَٱعۡتَرَفۡنَا بِذُنُوبِنَا فَهَلۡ إِلَىٰ خُرُوجٖ مِّن سَبِيلٖ\nУлар дедилар: «Парвардигоро, Сен бизларни икки бор ўлдирдинг ва икки бор тирилтирдинг (яъни, аввал она қорнида ўлик-жонсиз ҳолда яратиб, сўнгра Ўзинг бизларга жон-ҳаёт ато этдинг. Кейин, ажалимиз етганда, Ўзинг жонимизни олиб-ўлдириб, Қиёмат Кунида яна тирилтирдинг). Бас, бизлар (ҳаёт дунёда қилиб ўтган барча) гуноҳларимизни эътироф этдик. Энди (бу дўзах азобидан) чиқишнинг бирон йўли борми?»'}, {'chapter': 40, 'verse': 12, 'text': 'ذَٰلِكُم بِأَنَّهُۥٓ إِذَا دُعِيَ ٱللَّهُ وَحۡدَهُۥ كَفَرۡتُمۡ وَإِن يُشۡرَكۡ بِهِۦ تُؤۡمِنُواْۚ فَٱلۡحُكۡمُ لِلَّهِ ٱلۡعَلِيِّ ٱلۡكَبِيرِ\n(Йўқ, сизлар мана шу азобда мангу қолурсизлар). Бунга сабаб, қачон (сизларни) ёлғиз Аллоҳгагина (ибодат қилишга) даъват қилинганида, кофир бўлиб-бўйинсунмай, агар У зотга ширк келтирилса (яъни, турли бут-санамларга сиғинишга чорлаганда), иймон келтиришларингиздир. Бас, барча ҳукм юксак ва улуғ Аллоҳникидир'}, {'chapter': 40, 'verse': 13, 'text': 'هُوَ ٱلَّذِي يُرِيكُمۡ ءَايَٰتِهِۦ وَيُنَزِّلُ لَكُم مِّنَ ٱلسَّمَآءِ رِزۡقٗاۚ وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ\nУ зот сизларга Ўз оятларини (яъни, Ўзининг борлиги ва бирлигига далолат қиладиган аломатларни) кўргизур ва сизларга осмондан ризқу рўз ёғдирур. (Лекин бу оят-аломатлардан) фақат (Аллоҳга) инобат-илтижо қиладиган кишигина эслатма-ибрат олур'}, {'chapter': 40, 'verse': 14, 'text': 'فَٱدۡعُواْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ\nБас, (эй мўминлар), гарчи кофирлар ёмон кўрсалар-да, Аллоҳга — У зот учун динни холис қилган ҳолингизда дуо-илтижо қилингиз'}, {'chapter': 40, 'verse': 15, 'text': 'رَفِيعُ ٱلدَّرَجَٰتِ ذُو ٱلۡعَرۡشِ يُلۡقِي ٱلرُّوحَ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦ لِيُنذِرَ يَوۡمَ ٱلتَّلَاقِ\nУ юксак мартабалидир, Арш Соҳибидир, У Ўз амри билан бандаларидан Ўзи хоҳлаган кишиларга — токи у (инсонларни) мулоқот Кунидан (яъни, Қиёматдан) огоҳлантириш учун — ваҳий туширар'}, {'chapter': 40, 'verse': 16, 'text': 'يَوۡمَ هُم بَٰرِزُونَۖ لَا يَخۡفَىٰ عَلَى ٱللَّهِ مِنۡهُمۡ شَيۡءٞۚ لِّمَنِ ٱلۡمُلۡكُ ٱلۡيَوۡمَۖ لِلَّهِ ٱلۡوَٰحِدِ ٱلۡقَهَّارِ\nУлар (қабрларидан) чиқадиган Кунда Аллоҳга улар(нинг қилиб ўтган амаллари)дан бирон нарса махфий бўлиб қолмас. Бу Кунда подшоҳлик кимникидир? Яккаю ягона ғолиб Зот — Аллоҳникидир'}, {'chapter': 40, 'verse': 17, 'text': 'ٱلۡيَوۡمَ تُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡۚ لَا ظُلۡمَ ٱلۡيَوۡمَۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ\nБу Кунда ҳар бир жон ўзи қилган иш-амали билан жазоланур. Бу Кунда (ҳеч кимга) зулм қилинмас. Албатта Аллоҳ тез ҳисоб-китоб қилгувчи Зотдир'}, {'chapter': 40, 'verse': 18, 'text': 'وَأَنذِرۡهُمۡ يَوۡمَ ٱلۡأٓزِفَةِ إِذِ ٱلۡقُلُوبُ لَدَى ٱلۡحَنَاجِرِ كَٰظِمِينَۚ مَا لِلظَّـٰلِمِينَ مِنۡ حَمِيمٖ وَلَا شَفِيعٖ يُطَاعُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларни яқин Кундан (яъни, Қиёматдан) огоҳлантиринг! Ўшанда улар юраклари ҳалқумларига тиқилиб, (ўзлари) ғам-ҳасратга тўлиб турурлар. Золим кофир кимсалар учун (у Кунда) на бир дўст ва на итоат қилинадиган (яъни, шафоати қабул қилинадиган) оқловчи бўлмас'}, {'chapter': 40, 'verse': 19, 'text': 'يَعۡلَمُ خَآئِنَةَ ٱلۡأَعۡيُنِ وَمَا تُخۡفِي ٱلصُّدُورُ\n(Аллоҳ) кўзларнинг хиёнатини (яъни, қараш ҳаром қилинган нарсага ўғринча кўз ташлашни) ҳам, диллар яширадиган нарсаларни ҳам билур'}, {'chapter': 40, 'verse': 20, 'text': 'وَٱللَّهُ يَقۡضِي بِٱلۡحَقِّۖ وَٱلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَقۡضُونَ بِشَيۡءٍۗ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\nАллоҳ ҳақ (ҳукм) билан ҳукм қилур. Улар (Аллоҳни), қўйиб дуо илтижо қилаётган бутлари эса бирон нарсага ҳукм чиқара олмаслар. Албатта Аллоҳнинг Ўзигина эшитгувчи ва кўриб тургувчи Зотдир'}, {'chapter': 40, 'verse': 21, 'text': '۞أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ كَانُواْ مِن قَبۡلِهِمۡۚ كَانُواْ هُمۡ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَءَاثَارٗا فِي ٱلۡأَرۡضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقٖ\nАхир улар Ер юзида сайру-саёҳат қилишиб, ўзларидан аввал ўтган, (ўз пайғамбарларини ёлғончи қилиб, иймонсиз кетган) кимсаларнинг оқибатлари қандай бўлганини (улар қандай қисматга йўлиқишганини) кўрсалар бўлмайдими?! Улар (Макка мушрикларидан) кўра куч-қувват ва ердаги осори атиқалари жиҳатидан зўрроқ эдилар. Бас, Аллоҳ уларни гуноҳлари сабабли ушлади — ҳалок қилди ва улар учун Аллоҳдан ўзга бирон сақловчи бўлмади'}, {'chapter': 40, 'verse': 22, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ كَانَت تَّأۡتِيهِمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَكَفَرُواْ فَأَخَذَهُمُ ٱللَّهُۚ إِنَّهُۥ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ\nБунга сабаб — уларга пайғамбарлари аниқ ҳужжатлар келтирганларида, улар кофир бўлдилар. Бас, Аллоҳ уларни ушлади — ҳалок қилди. Зеро, У қувват Эгаси ва азоби қаттиқдир'}, {'chapter': 40, 'verse': 23, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ\nАниқки, Биз Мусони Ўз оят-мўъжизаларимиз ва очиқ ҳужжат билан юбордик'}, {'chapter': 40, 'verse': 24, 'text': 'إِلَىٰ فِرۡعَوۡنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُواْ سَٰحِرٞ كَذَّابٞ\nФиръавн, Ҳомон ва Қорунга. (Ўшанда) улар «(Бу Мусо) ёлғончи сеҳргардир», дедилар'}, {'chapter': 40, 'verse': 25, 'text': 'فَلَمَّا جَآءَهُم بِٱلۡحَقِّ مِنۡ عِندِنَا قَالُواْ ٱقۡتُلُوٓاْ أَبۡنَآءَ ٱلَّذِينَ ءَامَنُواْ مَعَهُۥ وَٱسۡتَحۡيُواْ نِسَآءَهُمۡۚ وَمَا كَيۡدُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ\nЭнди қачонки (Мусо) уларга Бизнинг ҳузуримиздан Ҳақ (пайғамбарлик) билан келганида, улар «У билан бирга мўмин бўлганларнинг ўғилларини ўлдириб, аёлларини тирик қолдирингиз!», дедилар. (Лекин) кофирларнинг макр-ҳийлалари албатта залолатдир (яъни, зое бўлгувчидир)'}, {'chapter': 40, 'verse': 26, 'text': 'وَقَالَ فِرۡعَوۡنُ ذَرُونِيٓ أَقۡتُلۡ مُوسَىٰ وَلۡيَدۡعُ رَبَّهُۥٓۖ إِنِّيٓ أَخَافُ أَن يُبَدِّلَ دِينَكُمۡ أَوۡ أَن يُظۡهِرَ فِي ٱلۡأَرۡضِ ٱلۡفَسَادَ\nФиръавн деди: «Мени қўйинглар, Мусони ўлдирай. (Қани) у Парвардигорига дуо-илтижо қилсин-чи, унга нажот берармикан. Дарҳақиқат, мен (Мусо) сизларнинг динингизни ўзгартириб юборишдан ёки Ер юзида бузғунчилик авж олдиришидан қўрқмоқдаман»'}, {'chapter': 40, 'verse': 27, 'text': 'وَقَالَ مُوسَىٰٓ إِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٖ لَّا يُؤۡمِنُ بِيَوۡمِ ٱلۡحِسَابِ\nМусо айтди: «Албатта мен, Парвардигорим ва Парвардигорингиз (бўлмиш Аллоҳ)дан ҳисоб-китоб кунига иймон келтирмайдиган барча мутакаббир (кимсаларнинг ёмонлиги)дан паноҳ беришини сўраганман'}, {'chapter': 40, 'verse': 28, 'text': 'وَقَالَ رَجُلٞ مُّؤۡمِنٞ مِّنۡ ءَالِ فِرۡعَوۡنَ يَكۡتُمُ إِيمَٰنَهُۥٓ أَتَقۡتُلُونَ رَجُلًا أَن يَقُولَ رَبِّيَ ٱللَّهُ وَقَدۡ جَآءَكُم بِٱلۡبَيِّنَٰتِ مِن رَّبِّكُمۡۖ وَإِن يَكُ كَٰذِبٗا فَعَلَيۡهِ كَذِبُهُۥۖ وَإِن يَكُ صَادِقٗا يُصِبۡكُم بَعۡضُ ٱلَّذِي يَعِدُكُمۡۖ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ مُسۡرِفٞ كَذَّابٞ\n(Шунда) Фиръавн хонадонидан бўлган, ўзининг иймонини яшириб юрадиган бир мўмин киши деди: «Бир кишини, «Менинг Парвардигорим Аллоҳдир», дегани учун ўлдирурмисизлар?! Ҳолбуки, у сизларга Парвардигорингиз томонидан аниқ ҳужжатлар келтиргандир. Агар у ёлғончи бўлса, ёлғони ўз бўйнига. (Аммо) агар ростгўй бўлса (у ҳолда) сизларга у (кофирлар учун) ваъда қилаётган (азоблардан) айримлари етиб қолади-ку! Албатта Аллоҳ ҳаддан ошгувчи, ёлғончи кимсаларни ҳидоят қилмас'}, {'chapter': 40, 'verse': 29, 'text': 'يَٰقَوۡمِ لَكُمُ ٱلۡمُلۡكُ ٱلۡيَوۡمَ ظَٰهِرِينَ فِي ٱلۡأَرۡضِ فَمَن يَنصُرُنَا مِنۢ بَأۡسِ ٱللَّهِ إِن جَآءَنَاۚ قَالَ فِرۡعَوۡنُ مَآ أُرِيكُمۡ إِلَّا مَآ أَرَىٰ وَمَآ أَهۡدِيكُمۡ إِلَّا سَبِيلَ ٱلرَّشَادِ\nЭй қавмим, бугун-ку мулку давлат сизларники, мана шу ерда ғолибсизлар. Энди агар бизларга Аллоҳнинг азоби келар бўлса, ким бизларга (ундан нажот топишимиз учун) ёрдам берур?!» Фиръавн айтди: «Мен сизларга фақат ўзим раъй қилаётган нарсани (яъни, Мусони ўлдиришнинг маслаҳатинигина кўрсатурман ва мен сизларни фақат тўғри йўлга етакларман»)'}, {'chapter': 40, 'verse': 30, 'text': 'وَقَالَ ٱلَّذِيٓ ءَامَنَ يَٰقَوۡمِ إِنِّيٓ أَخَافُ عَلَيۡكُم مِّثۡلَ يَوۡمِ ٱلۡأَحۡزَابِ\nИймон келтирган киши деди: «Эй қавмим, ҳақиқатан, мен сизларнинг устингизга ҳам худди ўша фирқаларнинг куни тушиб қолишидан қўрқурман'}, {'chapter': 40, 'verse': 31, 'text': 'مِثۡلَ دَأۡبِ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمۡۚ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعِبَادِ\nНуҳ қавми, Од, Самуд (қабилалари) ва улардан кейинги (куфр йўлини тутган) кимсаларнинг иши каби. Ҳолбуки, Аллоҳ ўз бандаларига зулм қилишни истамас (яъни, Аллоҳ ўша фирқаларни ҳалок қилиш билан уларга зулм қилгани йўқ, балки улар ўзларининг куфру туғёнлари сабабли ҳалокатга мустаҳиқ бўлдилар)'}, {'chapter': 40, 'verse': 32, 'text': 'وَيَٰقَوۡمِ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ يَوۡمَ ٱلتَّنَادِ\nЭй қавмим, ҳақиқатан, мен сизлар ҳақингизда (кишилар бир-бирларига) нидо қилиб қоладиган Кундан (яъни, Қиёмат Кунида дўзах азобига дучор бўлиб, дод-вой қилиб қолишларингиздан) қўрқурман'}, {'chapter': 40, 'verse': 33, 'text': 'يَوۡمَ تُوَلُّونَ مُدۡبِرِينَ مَا لَكُم مِّنَ ٱللَّهِ مِنۡ عَاصِمٖۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ\nУ Кунда сизлар (жаҳаннам даҳшатларидан) юз ўгириб қочурсизлар, (аммо) сизлар учун Аллоҳ(нинг азобидан) сақлагувчи — тўсгувчи бўлмас. Кимни Аллоҳ йўлдан оздирса, бас, унинг учун бирон ҳидоят қилгувчи бўлмас'}, {'chapter': 40, 'verse': 34, 'text': 'وَلَقَدۡ جَآءَكُمۡ يُوسُفُ مِن قَبۡلُ بِٱلۡبَيِّنَٰتِ فَمَا زِلۡتُمۡ فِي شَكّٖ مِّمَّا جَآءَكُم بِهِۦۖ حَتَّىٰٓ إِذَا هَلَكَ قُلۡتُمۡ لَن يَبۡعَثَ ٱللَّهُ مِنۢ بَعۡدِهِۦ رَسُولٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنۡ هُوَ مُسۡرِفٞ مُّرۡتَابٌ\nДарвоқеъ, илгари Юсуф сизларга (ўзининг пайғамбар эканлигини исботлайдиган) аниқ ҳужжатлар келтирганида ҳам, у сизларга келтирган нарсалардан шубҳа-гумон қилиб туриб олган эдинглар. То қачон у ҳалок бўлганида: «(Энди Юсуфдан) сўнг Аллоҳ ҳаргиз пайғамбар юбормас», дединглар. Ҳаддан ошувчи, шак келтирувчи бўлган кимсаларни Аллоҳ мана шундай йўлдан оздириб қўяр'}, {'chapter': 40, 'verse': 35, 'text': 'ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡۖ كَبُرَ مَقۡتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلۡبِ مُتَكَبِّرٖ جَبَّارٖ\nАллоҳнинг оятлари ҳақида ўзларига келган бирон ҳужжат-далилсиз талашиб-тортишадиган кимсалар Аллоҳ наздида ҳам, иймон келтирган зотлар наздида ҳам катта нафратга дучор бўлурлар. Аллоҳ ҳар бир мутакаббир ва зўравоннинг қалбини мана шундай муҳрлаб қўяр'}, {'chapter': 40, 'verse': 36, 'text': 'وَقَالَ فِرۡعَوۡنُ يَٰهَٰمَٰنُ ٱبۡنِ لِي صَرۡحٗا لَّعَلِّيٓ أَبۡلُغُ ٱلۡأَسۡبَٰبَ\nФиръавн (мўмин кишидан юқорида зикр қилинган сўзларни эшитган одамлар Ҳақ йўлига юриб кетишларидан кўрқиб, уларни чалғитиш учун ўз вазирига қараб) деди: «Эй Ҳомон, мен учун бир (баланд) қаср қургин, шоядки, мен (у қасрнинг устига чиқиб) йўлларга'}, {'chapter': 40, 'verse': 37, 'text': 'أَسۡبَٰبَ ٱلسَّمَٰوَٰتِ فَأَطَّلِعَ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ كَٰذِبٗاۚ وَكَذَٰلِكَ زُيِّنَ لِفِرۡعَوۡنَ سُوٓءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِيلِۚ وَمَا كَيۡدُ فِرۡعَوۡنَ إِلَّا فِي تَبَابٖ\n— осмонларнинг йўлларига етиб, Мусонинг илоҳини кўрсам, дарҳақиқат, мен уни ёлғончи деб гумон қилмоқдаман». Фиръавнга ўзининг ёмон амали мана шундай чиройли кўринди ва у (тўғри) йўлдан тўсилди. Фиръавннинг барча макру-ҳийласи албатта ночор-бефойдадир'}, {'chapter': 40, 'verse': 38, 'text': 'وَقَالَ ٱلَّذِيٓ ءَامَنَ يَٰقَوۡمِ ٱتَّبِعُونِ أَهۡدِكُمۡ سَبِيلَ ٱلرَّشَادِ\nИймон келтирган киши деди: «Эй қавмим, сизлар менга эргашинглар, мен сизларни Тўғри Йўлга етаклайман'}, {'chapter': 40, 'verse': 39, 'text': 'يَٰقَوۡمِ إِنَّمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا مَتَٰعٞ وَإِنَّ ٱلۡأٓخِرَةَ هِيَ دَارُ ٱلۡقَرَارِ\nЭй қавмим, бу ҳаёти дунё фақат бир (арзимас) матоъдир. Албатта Охиратгина (мангу) қароргоҳ бўлган диёрдир'}, {'chapter': 40, 'verse': 40, 'text': 'مَنۡ عَمِلَ سَيِّئَةٗ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَاۖ وَمَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ يُرۡزَقُونَ فِيهَا بِغَيۡرِ حِسَابٖ\nКим бирон ёмонлик қилса, унга (Охиратда ўша ёмонлиги) баробарида жазо берилиши аниқдир. Ва эркакми, аёлми ким мўмин бўлган ҳолида бирон яхшилик қилса, ана ўшалар жаннатга дохил бўлурлар (ва) у жойда уларга беҳисоб ризқ берилур'}, {'chapter': 40, 'verse': 41, 'text': '۞وَيَٰقَوۡمِ مَا لِيٓ أَدۡعُوكُمۡ إِلَى ٱلنَّجَوٰةِ وَتَدۡعُونَنِيٓ إِلَى ٱلنَّارِ\nЭй қавмим, нега мен сизларни нажотга — жаннатга чорласам, сизлар мени дўзахга чақирмоқдасизлар-а'}, {'chapter': 40, 'verse': 42, 'text': 'تَدۡعُونَنِي لِأَكۡفُرَ بِٱللَّهِ وَأُشۡرِكَ بِهِۦ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞ وَأَنَا۠ أَدۡعُوكُمۡ إِلَى ٱلۡعَزِيزِ ٱلۡغَفَّـٰرِ\nСизлар мени Аллоҳга кофир бўлишга ва (ўзим) (илоҳ эканлигини) билмайдиган нарсаларни У зотга шерик қилишга чақирмоқдасизлар. Мен сизларни қудратли, бениҳоя мағфиратли Зотга чорламоқдаман'}, {'chapter': 40, 'verse': 43, 'text': 'لَا جَرَمَ أَنَّمَا تَدۡعُونَنِيٓ إِلَيۡهِ لَيۡسَ لَهُۥ دَعۡوَةٞ فِي ٱلدُّنۡيَا وَلَا فِي ٱلۡأٓخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى ٱللَّهِ وَأَنَّ ٱلۡمُسۡرِفِينَ هُمۡ أَصۡحَٰبُ ٱلنَّارِ\nШак-шубҳа йўқки, албатта сизлар мени (ибодат қилишга) чақираётган бутларингиз (жонсиз нарсалар бўлгани сабабли) на дунёда ва на Охиратда (бирон кимсани ўзларига ибодат қилиш учун) даъват қила олмас. Ва албатта (барчаларимизнинг Аллоҳга қайтишимиз бордир. Дарҳақиқат, ҳаддан ошувчи кимсаларгина дўзах эгаларидир)'}, {'chapter': 40, 'verse': 44, 'text': 'فَسَتَذۡكُرُونَ مَآ أَقُولُ لَكُمۡۚ وَأُفَوِّضُ أَمۡرِيٓ إِلَى ٱللَّهِۚ إِنَّ ٱللَّهَ بَصِيرُۢ بِٱلۡعِبَادِ\nҲали (бошларингизга азоб тушганида албатта ҳозир) мен сизларга айтаётган сўзларни эслайсизлар. Мен ўз ишимни Аллоҳга топширурман. Зеро, Аллоҳ бандаларини кўриб тургувчидир»'}, {'chapter': 40, 'verse': 45, 'text': 'فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُواْۖ وَحَاقَ بِـَٔالِ فِرۡعَوۡنَ سُوٓءُ ٱلۡعَذَابِ\nБас, Аллоҳ у (иймон келтирган киши)ни уларнинг ёмон макр-ҳийлаларидан сақлади ва Фиръавн хонадонини ёмон азоб ўраб олди'}, {'chapter': 40, 'verse': 46, 'text': 'ٱلنَّارُ يُعۡرَضُونَ عَلَيۡهَا غُدُوّٗا وَعَشِيّٗاۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ أَدۡخِلُوٓاْ ءَالَ فِرۡعَوۡنَ أَشَدَّ ٱلۡعَذَابِ\n(У азоб бир) оловдорки, улар эрта-ю кеч ўшанга кўндаланг қилиниб (куйдирилурлар). (Қиёмат) соати қойим бўладиган Кунда эса (дўзах фаришталарига): «Фиръавн хонадонини энг қаттиқ азобга киритинглар», (дейилур)'}, {'chapter': 40, 'verse': 47, 'text': 'وَإِذۡ يَتَحَآجُّونَ فِي ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَـٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا نَصِيبٗا مِّنَ ٱلنَّارِ\nЎшанда улар дўзахда ўзаро тортишиб, бечора (эргашувчи)лар мутакаббир кимсаларга (яъни, ўзларининг собиқ йўлбошчиларга): «Бизлар сизларга эргашган эдик, энди сизлар бизлардан дўзах (азоби)дан бирон бўлагини дафъ қила олурмисизлар?», дер эканлар'}, {'chapter': 40, 'verse': 48, 'text': 'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُلّٞ فِيهَآ إِنَّ ٱللَّهَ قَدۡ حَكَمَ بَيۡنَ ٱلۡعِبَادِ\nМутакаббир кимсалар айтдилар: «Бизларнинг барчамиз шак-шубҳасиз, (дўзахдадирмиз). Дарҳақиқат, Аллоҳ бандалари ўртасида Ўз ҳукмини чиқарди»'}, {'chapter': 40, 'verse': 49, 'text': 'وَقَالَ ٱلَّذِينَ فِي ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدۡعُواْ رَبَّكُمۡ يُخَفِّفۡ عَنَّا يَوۡمٗا مِّنَ ٱلۡعَذَابِ\n(Шунда) дўзахдаги кимсалар жаҳаннам қўриқчиларига: «Парвардигорингларга дуо қилинглар, бизлардан бирон кунлик азобни енгиллатсин», деб ёлворганларида'}, {'chapter': 40, 'verse': 50, 'text': 'قَالُوٓاْ أَوَلَمۡ تَكُ تَأۡتِيكُمۡ رُسُلُكُم بِٱلۡبَيِّنَٰتِۖ قَالُواْ بَلَىٰۚ قَالُواْ فَٱدۡعُواْۗ وَمَا دُعَـٰٓؤُاْ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٍ\nУлар айтдилар: «Ахир сизларга пайғамбарларингиз аниқ ҳужжатлар келтирмаганмидилар?!» «Ҳа, (келтирган эдилар, лекин бизлар уларни ёлғончи қилган эдик)», деди улар. (Шунда фаришталар уларга) айтдилар: «У ҳолда ўзларингиз дуо-илтижо қилаверинглар. Кофирларнинг дуо-илтижолари албатта залолатдадир (яъни, бефойдадир)»'}, {'chapter': 40, 'verse': 51, 'text': 'إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيَوۡمَ يَقُومُ ٱلۡأَشۡهَٰدُ\nАлбатта Биз пайғамбарларимизга ва иймон келтирган зотларга ҳаёти дунёда ҳам, гувоҳлар (ҳозир бўлиб) турадиган Кунда (Қиёматда) ҳам ёрдам берурмиз'}, {'chapter': 40, 'verse': 52, 'text': 'يَوۡمَ لَا يَنفَعُ ٱلظَّـٰلِمِينَ مَعۡذِرَتُهُمۡۖ وَلَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوٓءُ ٱلدَّارِ\nУ Кунда золим-кофир кимсаларга узр-баҳоналари фойда бермас ва уларга лаънат бўлур, уларга энг ёмон жой — дўзах бўлур'}, {'chapter': 40, 'verse': 53, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡهُدَىٰ وَأَوۡرَثۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡكِتَٰبَ\nАниқки, Биз Мусога ҳидоят ато этган ва Бани Исроилни Китоб-Тавротга ворис қилган эдик'}, {'chapter': 40, 'verse': 54, 'text': 'هُدٗى وَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ\nАқл эгалари учун ҳидоят ва эслатма-ибрат бўлган (Китобга ворис қилган эдик)'}, {'chapter': 40, 'verse': 55, 'text': 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ بِٱلۡعَشِيِّ وَٱلۡإِبۡكَٰرِ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом, сизга мушриклар томонидан етаётган озор-азийятларга) сабр қилинг — зеро, Аллоҳнинг (Ўз пайғамбарлари ва мўминларга ёрдам бериш ҳақидаги) ваъдаси ҳақдир ва гуноҳингизга мағфират сўранг ҳамда туну кун Парвардигорингизга ҳамду сано айтиш билан (У зотни) покланг'}, {'chapter': 40, 'verse': 56, 'text': 'إِنَّ ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡ إِن فِي صُدُورِهِمۡ إِلَّا كِبۡرٞ مَّا هُم بِبَٰلِغِيهِۚ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\nАлбатта Аллоҳнинг оятлари ҳақида ўзларига келган бирон ҳужжат — далилсиз талашиб-тортишадиган кимсалар, аниқки, уларнинг дилларида бир кибр (яъни, ўзларини сиздан катта олиш) бордирки, улар ўшанга (яъни, ўзлари орзу қилган катталикка) етувчи эмасдирлар. Бас, сиз Аллоҳдан паноҳ сўранг. Албатта Унинг Ўзигина эшитгувчи, кўриб тургувчидир'}, {'chapter': 40, 'verse': 57, 'text': 'لَخَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ أَكۡبَرُ مِنۡ خَلۡقِ ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nШак-шубҳасиз, осмонлар ва Ерни яратиш инсонларни яратишдан каттароқ (иш)дир. Лекин кўп одамлар билмайдилар'}, {'chapter': 40, 'verse': 58, 'text': 'وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَلَا ٱلۡمُسِيٓءُۚ قَلِيلٗا مَّا تَتَذَكَّرُونَ\nКўр кимса билан кўргувчи зот, иймон келтириб, яхшиликлар қилган зотлар билан ёмонлик қилгувчи кимса баробар бўлмас. Камдан-кам эслатма-ибрат олурсизлар'}, {'chapter': 40, 'verse': 59, 'text': 'إِنَّ ٱلسَّاعَةَ لَأٓتِيَةٞ لَّا رَيۡبَ فِيهَا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ\nАлбатта (Қиёмат) соати келгувчидир. У ҳақида шак-шубҳа йўқдир. Лекин кўп одамлар (унинг келишига) иймон келтирмаслар'}, {'chapter': 40, 'verse': 60, 'text': 'وَقَالَ رَبُّكُمُ ٱدۡعُونِيٓ أَسۡتَجِبۡ لَكُمۡۚ إِنَّ ٱلَّذِينَ يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِي سَيَدۡخُلُونَ جَهَنَّمَ دَاخِرِينَ\nПарвардигорларингиз: «Менга дуо-илтижо қилинглар, Мен сизларга (қилган дуоларингизни) мустажоб қилурман», деди. Албатта Менга ибодат қилишдан кибр-ҳаво қилган кимсалар яқинда бўйинларини эгган ҳолларида жаҳаннамга кирурлар'}, {'chapter': 40, 'verse': 61, 'text': 'ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ\nАллоҳ сизлар ором олишингиз учун кечани ва (касбу кор, сайру саёҳат қилишингиз учун) очиқ-ёруғ кундузни яратиб қўйган Зотдир. Албатта Аллоҳ одамларга фазлу марҳамат Соҳибидир. Лекин одамларнинг кўплари шукр қилмайдилар'}, {'chapter': 40, 'verse': 62, 'text': 'ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ خَٰلِقُ كُلِّ شَيۡءٖ لَّآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ\nМана шу Аллоҳ Парвардигорларингиздир, У барча нарсанинг яратгувчисидир. Ҳеч қандай илоҳ йўқ, магар Унинг Ўзигина бордир. Бас, қаёққа бурилиб кетмоқдасизлар'}, {'chapter': 40, 'verse': 63, 'text': 'كَذَٰلِكَ يُؤۡفَكُ ٱلَّذِينَ كَانُواْ بِـَٔايَٰتِ ٱللَّهِ يَجۡحَدُونَ\nАллоҳнинг оят-мўъжизаларини инкор қилгувчи бўлган кимсалар мана шундай бурилиб кетурлар'}, {'chapter': 40, 'verse': 64, 'text': 'ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ قَرَارٗا وَٱلسَّمَآءَ بِنَآءٗ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡۖ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nАллоҳ сизлар учун Ерни қароргоҳ, осмонни том қилиб қўйган ва сизларга сурат-шакл бериб суратларингизни гўзал қилган ҳамда сизларни ҳалол-пок нарсалардан ризқлантирган Зотдир. Мана шу Аллоҳ Парвардигорингиздир. Бас, барча одамларнинг Парвардигори бўлмиш Аллоҳ баракотли — буюкдир'}, {'chapter': 40, 'verse': 65, 'text': 'هُوَ ٱلۡحَيُّ لَآ إِلَٰهَ إِلَّا هُوَ فَٱدۡعُوهُ مُخۡلِصِينَ لَهُ ٱلدِّينَۗ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ\nУ тирик Зотдир. Ҳеч қандай илоҳ йўқ, магар Унинг Ўзигина бордир. Бас, (эй мўминлар), сизлар (Аллоҳга) У зот учун динни холис қилган ҳолингизда дуо-илтижо қилингиз! Ҳамду сано барча оламларнинг Парвардигори Аллоҳ учундир'}, {'chapter': 40, 'verse': 66, 'text': '۞قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَآءَنِيَ ٱلۡبَيِّنَٰتُ مِن رَّبِّي وَأُمِرۡتُ أَنۡ أُسۡلِمَ لِرَبِّ ٱلۡعَٰلَمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Шак-шубҳасиз, мен ўзимга Парвардигорим томонидан аниқ ҳужжатлар келган пайтида, сизлар Аллоҳни қўйиб, дуо-илтижо қилаётган бутларга ибодат қилишдан қайтарилдим ва барча оламларнинг Парвардигорига бўйинсунишга буюрилдим»'}, {'chapter': 40, 'verse': 67, 'text': 'هُوَ ٱلَّذِي خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ مِنۡ عَلَقَةٖ ثُمَّ يُخۡرِجُكُمۡ طِفۡلٗا ثُمَّ لِتَبۡلُغُوٓاْ أَشُدَّكُمۡ ثُمَّ لِتَكُونُواْ شُيُوخٗاۚ وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبۡلُۖ وَلِتَبۡلُغُوٓاْ أَجَلٗا مُّسَمّٗى وَلَعَلَّكُمۡ تَعۡقِلُونَ\nУ сизларни(нг отангиз Одамни) тупроқдан, сўнгра (унинг барча авлодларини) нутфадан, сўнгра лахта қондан яратган Зотдир. Сўнгра У сизларнинг (оналарингиз қорнидан) чақалоқ ҳолингизда, (ёруғ дунёга) чиқарур, сўнгра (У зот) сизларни вояга етишларингизга, сўнгра қариб-қартайишингизга (қўйиб берур). Сизларнинг орангизда (вояга етишдан, ёки кексалик ёшидан) илгари вафот топадиган кишилар ҳам бўлур. (У зот) сизларни белгиланган ажалга етишларингизга (қўйиб берур). Шоядки, ақл юргизсангизлар'}, {'chapter': 40, 'verse': 68, 'text': 'هُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُۖ فَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ\nУ тирилтирадиган ва ўлдирадиган Зотдир. Бас, қачон бирон ишни ҳукм қилса, Унга фақат «Бўл», дер, холос. Бас, у (иш) бўлур'}, {'chapter': 40, 'verse': 69, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ أَنَّىٰ يُصۡرَفُونَ\nАллоҳнинг оятлари ҳақида талашиб-тортишадиган кимсаларни кўрмадингизми? Қаёққа бурилиб кетмоқдалар-а'}, {'chapter': 40, 'verse': 70, 'text': 'ٱلَّذِينَ كَذَّبُواْ بِٱلۡكِتَٰبِ وَبِمَآ أَرۡسَلۡنَا بِهِۦ رُسُلَنَاۖ فَسَوۡفَ يَعۡلَمُونَ\n(Биз Муҳаммад алайҳис-салоту вас-саломга нозил қилган) Китоб — Қуръонни ва Биз (аввал ўтган) пайғамбарларимиз билан юборган нарсаларни — Китобларни ёлғон деган кимсалар яқинда (Қиёмат Кунида динсизликларининг оқибатини) билажаклар'}, {'chapter': 40, 'verse': 71, 'text': 'إِذِ ٱلۡأَغۡلَٰلُ فِيٓ أَعۡنَٰقِهِمۡ وَٱلسَّلَٰسِلُ يُسۡحَبُونَ\nЎшанда (яъни, дўзахга ташланганларида) улар бўйинларида кишан ва занжирлар (билан) судралурлар'}, {'chapter': 40, 'verse': 72, 'text': 'فِي ٱلۡحَمِيمِ ثُمَّ فِي ٱلنَّارِ يُسۡجَرُونَ\nқайноқ сув томонга, сўнгра оловда ёндирилурлар'}, {'chapter': 40, 'verse': 73, 'text': 'ثُمَّ قِيلَ لَهُمۡ أَيۡنَ مَا كُنتُمۡ تُشۡرِكُونَ\nСўнгра уларга: «Сизлар ширк келтириб ўтган бутларингиз қани?», дейилади'}, {'chapter': 40, 'verse': 74, 'text': 'مِن دُونِ ٱللَّهِۖ قَالُواْ ضَلُّواْ عَنَّا بَل لَّمۡ نَكُن نَّدۡعُواْ مِن قَبۡلُ شَيۡـٔٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ ٱلۡكَٰفِرِينَ\nАллоҳни қўйиб (ширк келтирган нарсалариннгиз қани?, дейилади). (Шунда) улар: «У (бутлар) бизнинг кўзимиз)дан ғойиб бўлиб қолишди. Йўқ-йўқ, бизлар илгари ҳаёти дунёда) ҳеч нарсага дуо-илтижо қилган эмасмиз», дедилар. Аллоҳ кофирларни мана шундай йўлдан оздирур'}, {'chapter': 40, 'verse': 75, 'text': 'ذَٰلِكُم بِمَا كُنتُمۡ تَفۡرَحُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَمۡرَحُونَ\nМана шу (жаҳаннам азобига йўлиқишларингиз)га сабаб сизлар Ерда (ўзларингиз қилган айшу ишрат ва гуноҳ-маъсиятлар билан) ноҳақ шоду ҳуррам бўлганларингиз ва кибру ҳаво қилганларингиздир'}, {'chapter': 40, 'verse': 76, 'text': 'ٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ\nЖаҳаннам дарвозаларидан у жойда мангу қолгувчи бўлган ҳолларингизда кирингиз! Бас, мутакаббир кимсаларнинг жойи нақадар ёмон бўлди'}, {'chapter': 40, 'verse': 77, 'text': 'فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۚ فَإِمَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا يُرۡجَعُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом, сизга мушриклар томонидан етаётган озор-азийятларга) сабр қилинг — зеро Аллоҳнинг (Ўз пайғамбари ва мўминларга ёрдам бериш ва кофирларни азоблаш ҳақидаги) ваъдаси ҳақдир! Энди агар Биз сизга ўша (кофирлар)га ваъда қилаётганимиз (азоб)дан айримларини кўрсатсак (яъни, сиз ҳаётлик пайтингизда уларни айрим азоб-уқубатларга дучор қилсак, кўрарсиз ёки сизни (уларга азоб туширшимиздан илгари) вафот топдирсак, у ҳолда ҳам ёлғиз Ўзимизга қайтарилурлар'}, {'chapter': 40, 'verse': 78, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ مِنۡهُم مَّن قَصَصۡنَا عَلَيۡكَ وَمِنۡهُم مَّن لَّمۡ نَقۡصُصۡ عَلَيۡكَۗ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ فَإِذَا جَآءَ أَمۡرُ ٱللَّهِ قُضِيَ بِٱلۡحَقِّ وَخَسِرَ هُنَالِكَ ٱلۡمُبۡطِلُونَ\nАниқки, Биз сиздан илгари (кўп) пайғамбар юборгандирмиз. Улардан Биз сизга ҳикоя қилиб берган кишилар ҳам бор, яна улардан Биз сизга ҳикоя қилмаган кишилар ҳам бордир. Бирон пайғамбар учун — Аллоҳнинг изну-иродасидан ташқари бирон оят-мўъжиза келтириш жоиз бўлган эмас (яъни, ҳар бир пайғамбар фақат Аллоҳ унга амр қилган мўъжизаларнигина келтиргандир). Бас, қачон Аллоҳнинг (кофирларга азоб тушиши тўғрисидаги) амри келгач, (пайғамбарлар ва уларни ёлғончи қилган кофирлар ўртасида) ҳақ (ҳукм) билан ҳукм қилинур ва ўшанда бузғунчи кимсалар зиёнкор бўлурлар'}, {'chapter': 40, 'verse': 79, 'text': 'ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَنۡعَٰمَ لِتَرۡكَبُواْ مِنۡهَا وَمِنۡهَا تَأۡكُلُونَ\nАллоҳ сизлар учун чорва ҳайвонларини (токи) сизлар ўша (ҳайвон)лардан (айримларини) минишларингиз учун (пайдо) қилди. Ўша (ҳайвон)лардан ейсизлар ҳам'}, {'chapter': 40, 'verse': 80, 'text': 'وَلَكُمۡ فِيهَا مَنَٰفِعُ وَلِتَبۡلُغُواْ عَلَيۡهَا حَاجَةٗ فِي صُدُورِكُمۡ وَعَلَيۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ\nСизлар учун уларда (кўп) фойдалар бордир. (У ҳайвонлар) сизлар уларнинг устида дилларингиздаги ҳожатларингизга етишлариигиз учун ҳам (яратилгандирлар). Сизларни (қуруқликда) ўша (ҳайвонлар) устида ва дарё-денгизларда эса (кемаларда) (кўзлаган манзилларингизга) элтиб қўйилур'}, {'chapter': 40, 'verse': 81, 'text': 'وَيُرِيكُمۡ ءَايَٰتِهِۦ فَأَيَّ ءَايَٰتِ ٱللَّهِ تُنكِرُونَ\n(Аллоҳ) сизларга Ўз оят-аломатларини кўрсатур. Бас, сизлар Аллоҳнинг қайси оятларини инкор қилурсизлар'}, {'chapter': 40, 'verse': 82, 'text': 'أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَانُوٓاْ أَكۡثَرَ مِنۡهُمۡ وَأَشَدَّ قُوَّةٗ وَءَاثَارٗا فِي ٱلۡأَرۡضِ فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ\nАхир улар Ер юзида сайру-саёҳат қилишиб, ўзларидан аввалги (ўз пайғамбарларини ёлғончи қилиб иймонсиз кетган) кимсаларнинг оқибатлари қандай бўлганини (улар қандай қисматга йўлиқишганини кўрсалар бўлмайдими?! Улар (Макка мушрикларидан) кўра кўпроқ ҳамда қуч-қувват ва Ердаги осори атиқалари жиҳатидан зўрроқ эдилар. (Аммо) уларга топган (молу-дунёлари ҳам, куч-қувватлари ҳам) асқотгани йўқ'}, {'chapter': 40, 'verse': 83, 'text': 'فَلَمَّا جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَرِحُواْ بِمَا عِندَهُم مِّنَ ٱلۡعِلۡمِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nЧунки уларга пайғамбарлари (Аллоҳга иймон келтириш ва У зотга бўйинсуниш зарур эканлиги ҳақида) аниқ ҳужжатлар келтирган вақтларида улар ўзларининг олдиларидаги (фақат дунёвий бўлган) билимлари билан шодланиб-мақтандилар (ва пайғамбарлари огоҳлантирган азобни масхара қилиб кулдилар). Ва уларни ўзлари масхара қилгувчи бўлган (азоб) ўраб олди'}, {'chapter': 40, 'verse': 84, 'text': 'فَلَمَّا رَأَوۡاْ بَأۡسَنَا قَالُوٓاْ ءَامَنَّا بِٱللَّهِ وَحۡدَهُۥ وَكَفَرۡنَا بِمَا كُنَّا بِهِۦ مُشۡرِكِينَ\nЭнди қачонки улар Бизнинг азобимизни кўришгач, «Якка-ю ягона Аллоҳга иймон келтирдик ва (илгари Аллоҳга) шерик қилиб олганимиз бутларга кофир бўлдик», дедилар'}, {'chapter': 40, 'verse': 85, 'text': 'فَلَمۡ يَكُ يَنفَعُهُمۡ إِيمَٰنُهُمۡ لَمَّا رَأَوۡاْ بَأۡسَنَاۖ سُنَّتَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ فِي عِبَادِهِۦۖ وَخَسِرَ هُنَالِكَ ٱلۡكَٰفِرُونَ\n(Лекин) Бизнинг азобимизни кўрган вақтдаги иймонлари уларга фойда бергувчи бўлмади — (Бу) Аллоҳнинг (барча) бандалари ҳақидаги ўтган йўли — қонунидир (яъни, азоб тушган вақтида келтирилган иймоннинг фойдаси бўлмас). Ва ўшанда кофирлар зиёнкор бўлдилар'}, {'chapter': 41, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 41, 'verse': 2, 'text': 'تَنزِيلٞ مِّنَ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ\n(Ушбу Қуръон) меҳрибон ва раҳмли (Аллоҳ) томонидан нозил қилингандир'}, {'chapter': 41, 'verse': 3, 'text': 'كِتَٰبٞ فُصِّلَتۡ ءَايَٰتُهُۥ قُرۡءَانًا عَرَبِيّٗا لِّقَوۡمٖ يَعۡلَمُونَ\nУ биладиган қавм учун арабий Қуръон ҳолида (нозил қилиниб), оятлари муфассал баён қилинган Китобдир'}, {'chapter': 41, 'verse': 4, 'text': 'بَشِيرٗا وَنَذِيرٗا فَأَعۡرَضَ أَكۡثَرُهُمۡ فَهُمۡ لَا يَسۡمَعُونَ\n(Мўминларга) хушхабар бергувчи ва (кофирларни азобдан) огоҳлантиргувчи (Китобдир). Бас, (кофирларнинг) кўплари (унинг оятлари ҳақида тафаккур қилишдан) юз ўгирдилар, демак, улар «эшитмаслар»'}, {'chapter': 41, 'verse': 5, 'text': 'وَقَالُواْ قُلُوبُنَا فِيٓ أَكِنَّةٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ وَفِيٓ ءَاذَانِنَا وَقۡرٞ وَمِنۢ بَيۡنِنَا وَبَيۡنِكَ حِجَابٞ فَٱعۡمَلۡ إِنَّنَا عَٰمِلُونَ\nУлар дедилар: «Дилларимиз сен бизларни даъват қилаётган нарсадан пардаланган, қулоқларимизда эса оғирлик-карлик бордир ва сен билан бизларнинг ўртамизда бир тўсиқ бордир. Бас, сен ҳам ўз амалингни қилавер, бизлар ҳам албатта ўз амалларимизни қилгувчидирмиз»'}, {'chapter': 41, 'verse': 6, 'text': 'قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَٱسۡتَقِيمُوٓاْ إِلَيۡهِ وَٱسۡتَغۡفِرُوهُۗ وَوَيۡلٞ لِّلۡمُشۡرِكِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Ҳеч шак-шубҳа йўқки, мен ҳам худди сизлар каби бир одамдирман. Менга илоҳингиз ёлғиз Аллоҳнинг Ўзи экани ваҳий қилинмоқда. Бас, сизлар Унинг Ўзига тўғри (тоат-ибодатда) бўлингиз ва Ундан мағфират сўрангиз! Мушрикларга эса ҳалокат бўлгай'}, {'chapter': 41, 'verse': 7, 'text': 'ٱلَّذِينَ لَا يُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ\nУлар закотни (ҳақдорларга) ато этмайдиган, ўзлари Охиратга кофир бўлган кимсалардир»'}, {'chapter': 41, 'verse': 8, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ\nАлбатта иймон келтирган ва яхши амаллар қилган зотлар учун битмас-туганмас ажр-мукофот бордир'}, {'chapter': 41, 'verse': 9, 'text': '۞قُلۡ أَئِنَّكُمۡ لَتَكۡفُرُونَ بِٱلَّذِي خَلَقَ ٱلۡأَرۡضَ فِي يَوۡمَيۡنِ وَتَجۡعَلُونَ لَهُۥٓ أَندَادٗاۚ ذَٰلِكَ رَبُّ ٱلۡعَٰلَمِينَ\nАйтинг: «Ҳақиқатан ҳам сизлар Ерни икки кунда яратган Зотга кофир бўлурмисизлар ва ўзгаларни Унга тенглаштирурмисизлар?! Бу (Аллоҳ) барча оламларнинг Парвардигори-ку'}, {'chapter': 41, 'verse': 10, 'text': 'وَجَعَلَ فِيهَا رَوَٰسِيَ مِن فَوۡقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقۡوَٰتَهَا فِيٓ أَرۡبَعَةِ أَيَّامٖ سَوَآءٗ لِّلسَّآئِلِينَ\nУ зот (Ернинг) устида тоғларни (пайдо) қилди ва уни баракотли қилди ҳамда ўша (ер)да унинг емишларини (яъни, Ер аҳлининг ризқу рўзларини) тўла тўрт кунда белгилаб — тақсимлади. (Бу тафсилот) сўрагувчилар учундир'}, {'chapter': 41, 'verse': 11, 'text': 'ثُمَّ ٱسۡتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِيَ دُخَانٞ فَقَالَ لَهَا وَلِلۡأَرۡضِ ٱئۡتِيَا طَوۡعًا أَوۡ كَرۡهٗا قَالَتَآ أَتَيۡنَا طَآئِعِينَ\nСўнгра тутун ҳолидаги осмонга юзланиб, унга ва Ерга: «(Менинг амри-фармонимга) ихтиёран ёки мажбуран келинглар!», деган эди, улар: «Ўз ихтиёримиз билан келдик, (амрингга бўйинсундир)», дедилар'}, {'chapter': 41, 'verse': 12, 'text': 'فَقَضَىٰهُنَّ سَبۡعَ سَمَٰوَاتٖ فِي يَوۡمَيۡنِ وَأَوۡحَىٰ فِي كُلِّ سَمَآءٍ أَمۡرَهَاۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَحِفۡظٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ\nБас, икки кунда етти осмонни барпо қилди ва ҳар бир осмонга (унга буюрилган) иши-вазифасини ваҳий қилди (билдирди). Биз қуйи осмонни чироқлар-юлдузлар билан безадик ва (уни офат-балолардан) сақладик. Бу қудратли ва билгувчи Зотнинг тақдири — ўлчовидир'}, {'chapter': 41, 'verse': 13, 'text': 'فَإِنۡ أَعۡرَضُواْ فَقُلۡ أَنذَرۡتُكُمۡ صَٰعِقَةٗ مِّثۡلَ صَٰعِقَةِ عَادٖ وَثَمُودَ\nБас, агар улар (яъни, Макка мушриклари мана шу тафсилотдан кейин ҳам ёлғиз Аллоҳга иймон келтиришдан) юз ўгирсалар, у ҳолда айтинг: «Мен сизларни худди Од ва Самуд (қабилаларини урган) чақмоққа ўхшаган бир чақмоқ-ҳалокатдан огоҳлантирдим'}, {'chapter': 41, 'verse': 14, 'text': 'إِذۡ جَآءَتۡهُمُ ٱلرُّسُلُ مِنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۖ قَالُواْ لَوۡ شَآءَ رَبُّنَا لَأَنزَلَ مَلَـٰٓئِكَةٗ فَإِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ\nЎшанда пайғамбарлари уларнинг олдиларидан ҳам, орқаларидан ҳам келишиб: «Ёлғиз Аллоҳгагина ибодат қилинглар», (деганларида), улар: «Агар Парвардигоримиз (пайғамбар юборишни) хоҳлаганида, албатта фаришталарни нозил қилган бўлур эди. Бас, (модомики сизлар фаришта эмас, балки одамзот экансизлар) бизлар сизлар элчи қилиб юборилган динга кофирдирмиз», дедилар'}, {'chapter': 41, 'verse': 15, 'text': 'فَأَمَّا عَادٞ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَقَالُواْ مَنۡ أَشَدُّ مِنَّا قُوَّةًۖ أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَهُمۡ هُوَ أَشَدُّ مِنۡهُمۡ قُوَّةٗۖ وَكَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ\nЭнди Од (қабиласига)га келсак, улар Ер юзида ноҳақ кибр-ҳаво қилдилар ва «Куч-қувватда бизлардан зўрроқ ким бор?», дедилар. Ахир улар ўзларини яратган зот — Аллоҳ куч-қувватда улардан зўрроқ эканини билмадиларми?! — Ва улар Бизнинг оятларимизни инкор қилгувчи бўлдилар'}, {'chapter': 41, 'verse': 16, 'text': 'فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِيٓ أَيَّامٖ نَّحِسَاتٖ لِّنُذِيقَهُمۡ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَخۡزَىٰۖ وَهُمۡ لَا يُنصَرُونَ\nБас, уларга мана шу ҳаёти дунёда расво — хор қилгувчи азобни тотдириб қўйиш учун нахсли-шум кунларда устиларига бир даҳшатли бўрон юбордик. Охират азоби эса шак-шубҳасиз, янада хор қилгувчироқдир ва уларга (ўзлари сиғиниб ўтган бутлари томонидан ҳеч қандай) ёрдам берилмас'}, {'chapter': 41, 'verse': 17, 'text': 'وَأَمَّا ثَمُودُ فَهَدَيۡنَٰهُمۡ فَٱسۡتَحَبُّواْ ٱلۡعَمَىٰ عَلَى ٱلۡهُدَىٰ فَأَخَذَتۡهُمۡ صَٰعِقَةُ ٱلۡعَذَابِ ٱلۡهُونِ بِمَا كَانُواْ يَكۡسِبُونَ\nЭнди Самуд (қабиласи)га келсак, бас, Биз уларни (Тўғри Йўлга) ҳидоят қилган эдик, улар ҳидоятни қўйиб, кўрликни, залолатни ихтиёр қилдилар. Бас, ўзлари қилгувчи бўлган нарсалари (яъни, куфрни ихтиёр этганлари) сабабли уларни хор қилгувчи азоб чақмоғи урди'}, {'chapter': 41, 'verse': 18, 'text': 'وَنَجَّيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ\nВа Биз иймон келтирган ва тақводор бўлган зотларга (яъни, Самуд қабиласини ҳидоят йўлига даъват қилган Солиҳ пайғамбар ва у кишига иймон келтирган зотларга чақмоқ балосидан) нажот бердик'}, {'chapter': 41, 'verse': 19, 'text': 'وَيَوۡمَ يُحۡشَرُ أَعۡدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمۡ يُوزَعُونَ\nАллоҳнинг душманлари дўзахга (ҳайдалиш учун) тўпланиб, тизилиб турадиган Кунни (эсланг)'}, {'chapter': 41, 'verse': 20, 'text': 'حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيۡهِمۡ سَمۡعُهُمۡ وَأَبۡصَٰرُهُمۡ وَجُلُودُهُم بِمَا كَانُواْ يَعۡمَلُونَ\nЭнди қачонки улар (дўзахга) келишгач, (ҳаёти дунёда қилиб ўтган куфру исёнлари ҳақида сўралади, лекин улар ўз қилмишларидан тонишга уринадилар. Шунда) уларнинг қулоқлари, кўзлари ва терилари улар қилиб ўтган нарсалари ҳақида ўзларига қарши гувоҳлик беради'}, {'chapter': 41, 'verse': 21, 'text': 'وَقَالُواْ لِجُلُودِهِمۡ لِمَ شَهِدتُّمۡ عَلَيۡنَاۖ قَالُوٓاْ أَنطَقَنَا ٱللَّهُ ٱلَّذِيٓ أَنطَقَ كُلَّ شَيۡءٖۚ وَهُوَ خَلَقَكُمۡ أَوَّلَ مَرَّةٖ وَإِلَيۡهِ تُرۡجَعُونَ\nУлар териларига: «Нима учун бизга қарши гувоҳлик бердинглар?», дейишганида, (терилари): «Бизларни барча нарсани сўзлатган Зот — Аллоҳ сўзлатди. Сизларни дастлаб У яратган ва сизлар яна Унинг Ўзигагина қайтарилурсизлар», дедилар'}, {'chapter': 41, 'verse': 22, 'text': 'وَمَا كُنتُمۡ تَسۡتَتِرُونَ أَن يَشۡهَدَ عَلَيۡكُمۡ سَمۡعُكُمۡ وَلَآ أَبۡصَٰرُكُمۡ وَلَا جُلُودُكُمۡ وَلَٰكِن ظَنَنتُمۡ أَنَّ ٱللَّهَ لَا يَعۡلَمُ كَثِيرٗا مِّمَّا تَعۡمَلُونَ\nСизлар (ҳаёти дунёда гуноҳ ишларни қилганларингда шарманда бўлишдан қўрқиб, одамлардан яширинар эдинглар-у, аммо) қулоқларингиз, кўзларингиз ва териларингиз ўзларингизга қарши гувоҳлик беришидан (сақланиш учун улардан) яширингувчи эмас эдинглар, (чунки сизлар Қиёмат Кунида аъзойи баданларингизга ҳам забон берилишини билмасдинглар). Лекин сизлар Аллоҳ қилаётган амалларингиздан кўпини (яъни, одамлардан яширинча қилган гуноҳларингизни) билмайди, деб ўйладинглар'}, {'chapter': 41, 'verse': 23, 'text': 'وَذَٰلِكُمۡ ظَنُّكُمُ ٱلَّذِي ظَنَنتُم بِرَبِّكُمۡ أَرۡدَىٰكُمۡ فَأَصۡبَحۡتُم مِّنَ ٱلۡخَٰسِرِينَ\nВа Парвардигорларингиз ҳақида ўйлаган мана шу гумонларингиз сизларни ҳалок қилди (яъни, дўзахга тушишларингизга сабаб бўлди). Бас, сизлар зиёнкор кимсаларга айланиб қолдинглар'}, {'chapter': 41, 'verse': 24, 'text': 'فَإِن يَصۡبِرُواْ فَٱلنَّارُ مَثۡوٗى لَّهُمۡۖ وَإِن يَسۡتَعۡتِبُواْ فَمَا هُم مِّنَ ٱلۡمُعۡتَبِينَ\nЭнди агар улар (азобга) чидасалар, ана ўша дўзах жойларидир (яъни, азоб уқубатни тортаверадилар), агар ортга қайтишни талаб қилсалар, энди улар қайтарилгувчи эмаслар'}, {'chapter': 41, 'verse': 25, 'text': '۞وَقَيَّضۡنَا لَهُمۡ قُرَنَآءَ فَزَيَّنُواْ لَهُم مَّا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَحَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ\nБиз улар учун (уларни Тўғри Йўлдан оздирадиган) «дўстлар» тайёрлаб қўйдик, бас, ўша («дўстлар») уларга олдиларидаги (гуноҳларини) ҳам, орқаларидаги (гуноҳларини) ҳам чиройли кўрсатдилар ва уларга ҳам ўзларидан аввал ўтган — ҳалок бўлган инсу жиндан иборат умматлар қаторида Сўз — азоб ҳақ бўлди. Дарҳақиқат, улар зиёнкор бўлдилар'}, {'chapter': 41, 'verse': 26, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَسۡمَعُواْ لِهَٰذَا ٱلۡقُرۡءَانِ وَٱلۡغَوۡاْ فِيهِ لَعَلَّكُمۡ تَغۡلِبُونَ\nКофир бўлган кимсалар (бир-бирларига «Муҳаммад тиловат қилаётган вақтда) сизлар бу Қуръонга қулоқ солманглар ва (уни ўзгаларга ҳам эшиттирмаслик учун оғизларингизга келган гапни) жаврайверинглар, (шунда) шояд ғолиб бўлсангизлар», дедилар'}, {'chapter': 41, 'verse': 27, 'text': 'فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُواْ عَذَابٗا شَدِيدٗا وَلَنَجۡزِيَنَّهُمۡ أَسۡوَأَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ\nБас, албатта Биз ана ўша кофир бўлган кимсаларга қаттиқ азобни тотдирурмиз ва уларни ўзлари қилиб ўтган куфру исёнлари (сабабли) энг ёмон жазо билан жазолармиз'}, {'chapter': 41, 'verse': 28, 'text': 'ذَٰلِكَ جَزَآءُ أَعۡدَآءِ ٱللَّهِ ٱلنَّارُۖ لَهُمۡ فِيهَا دَارُ ٱلۡخُلۡدِ جَزَآءَۢ بِمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ\nБу (қаттиқ азоб) Аллоҳ душманларининг жазоси бўлган дўзахдир. Улар учун Бизнинг оятларимизни инкор қилувчи бўлганлари сабабли ўша жойда мангу қолиш (азобланиш) бордир'}, {'chapter': 41, 'verse': 29, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ رَبَّنَآ أَرِنَا ٱلَّذَيۡنِ أَضَلَّانَا مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ نَجۡعَلۡهُمَا تَحۡتَ أَقۡدَامِنَا لِيَكُونَا مِنَ ٱلۡأَسۡفَلِينَ\nКофир бўлган кимсалар (дўзах ўтида азобланар эканлар), дедилар: «Парвардигоро, бизларни (сенинг Тўғри Йўлингдан) оздирган инсу жиндан бўлган кимсаларни бизларга кўрсатгин, уларни оёқларимиз остига олиб (эзайлик), токи улар (дўзахнинг қаъридаги) энг тубан — хор кимсалардан бўлсинлар»'}, {'chapter': 41, 'verse': 30, 'text': 'إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ تَتَنَزَّلُ عَلَيۡهِمُ ٱلۡمَلَـٰٓئِكَةُ أَلَّا تَخَافُواْ وَلَا تَحۡزَنُواْ وَأَبۡشِرُواْ بِٱلۡجَنَّةِ ٱلَّتِي كُنتُمۡ تُوعَدُونَ\nАлбатта: «Парвардигоримиз Аллоҳдир», деб, сўнгра (ёлғиз Аллоҳга тоат-ибодат қилишда) тўғри — устивор бўлган зотларнинг олдиларига (ўлим пайтида) фаришталар тушиб, (дерлар): «Қўрқманглар ва ғамгин бўлманглар. Сизларга ваъда қилинган жаннат хушхабари билан шодланинглар'}, {'chapter': 41, 'verse': 31, 'text': 'نَحۡنُ أَوۡلِيَآؤُكُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَلَكُمۡ فِيهَا مَا تَشۡتَهِيٓ أَنفُسُكُمۡ وَلَكُمۡ فِيهَا مَا تَدَّعُونَ\nБизлар ҳаёти дунёда ҳам, Охиратда ҳам сизларнинг дўстларингиздирмиз. Сизлар учун (жаннатда) кўнгилларингиз тилаган нарсаларингиз бордир ва сизлар учун у жойда истаган нарсаларингиз бордир'}, {'chapter': 41, 'verse': 32, 'text': 'نُزُلٗا مِّنۡ غَفُورٖ رَّحِيمٖ\n(Бу) мағфиратли ва меҳрибон Зот томонидан бўлган зиёфатдир»'}, {'chapter': 41, 'verse': 33, 'text': 'وَمَنۡ أَحۡسَنُ قَوۡلٗا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَٰلِحٗا وَقَالَ إِنَّنِي مِنَ ٱلۡمُسۡلِمِينَ\n(Одамларни) Аллоҳ(нинг дини)га даъват қилган ва ўзи ҳам яхши амал қилиб, «шак-шубҳасиз, мен мусулмонлардандирман», деган кишидан ҳам чиройлироқ сўзлагувчи ким бор'}, {'chapter': 41, 'verse': 34, 'text': 'وَلَا تَسۡتَوِي ٱلۡحَسَنَةُ وَلَا ٱلسَّيِّئَةُۚ ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ فَإِذَا ٱلَّذِي بَيۡنَكَ وَبَيۡنَهُۥ عَدَٰوَةٞ كَأَنَّهُۥ وَلِيٌّ حَمِيمٞ\nЯхшилик билан ёмонлик баробар бўлмас. Сиз (ҳар қандай ёмонликни) энг гўзал сўзлар билан дафъ қилинг! (Шунда) баногоҳ сиз билан ўрталарингизда адоват бўлган кимса қайноқ-содиқ дўст каби бўлиб қолур'}, {'chapter': 41, 'verse': 35, 'text': 'وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُواْ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٖ\nУнга (ёмонликни яхшилик билан дафъ қилиш хислатига) фақат сабр-тоқатли зотларгина эришурлар, унга фақат улуғ насиба эгасигина эришур'}, {'chapter': 41, 'verse': 36, 'text': 'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\nАгар сизни шайтон томонидан бирон васваса йўлдан оздириб (мазкур хислат эгаси бўлишдан қайтармоқчи бўлса), у ҳолда Аллоҳдан паноҳ сўранг. Зеро, У эшитгувчи, билгувчи Зотдир'}, {'chapter': 41, 'verse': 37, 'text': 'وَمِنۡ ءَايَٰتِهِ ٱلَّيۡلُ وَٱلنَّهَارُ وَٱلشَّمۡسُ وَٱلۡقَمَرُۚ لَا تَسۡجُدُواْ لِلشَّمۡسِ وَلَا لِلۡقَمَرِ وَٱسۡجُدُواْۤ لِلَّهِۤ ٱلَّذِي خَلَقَهُنَّ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ\nКеча ва кундуз, қуёш ва ой Унинг (танҳолиги ва қудратига далолат қиладиган) оят-аломатларидандир. Агар сизлар (Аллоҳга) ибодат қилгувчи бўлсанглар, қуёшга ҳам, ойга ҳам сажда қилманглар, (балки) уларни(нг барчасини) яратган Зотга — Аллоҳга сажда қилинглар'}, {'chapter': 41, 'verse': 38, 'text': 'فَإِنِ ٱسۡتَكۡبَرُواْ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيۡلِ وَٱلنَّهَارِ وَهُمۡ لَا يَسۡـَٔمُونَ۩\nЭнди агар (кофирлар Аллоҳга сажда қилишдан) кибр-ҳаво қилсалар, бас, (билингки), Парвардигорингизнинг ҳузуридаги зотлар (фаришталар) кеча-ю кундузи ҳеч зерикмасдан-малолланмасдан У зотга тасбеҳ айтурлар — сажда қилурлар'}, {'chapter': 41, 'verse': 39, 'text': 'وَمِنۡ ءَايَٰتِهِۦٓ أَنَّكَ تَرَى ٱلۡأَرۡضَ خَٰشِعَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡۚ إِنَّ ٱلَّذِيٓ أَحۡيَاهَا لَمُحۡيِ ٱلۡمَوۡتَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nУнинг (танҳолиги ва қудратига далолат қиладиган) оят-аломатларидан (бири) сиз ерни қуп-қуруқ ҳолда кўришингиздир. Бас, қачон Биз унинг устига сув-ёмғир ёғдирсак, у ҳаракатга келар ва униб-ўсар. Албатта ўша ерни тирилтирган Зот ўликларни ҳам тирилтира олгувчидир. Зеро, У барча ишга қодирдир'}, {'chapter': 41, 'verse': 40, 'text': 'إِنَّ ٱلَّذِينَ يُلۡحِدُونَ فِيٓ ءَايَٰتِنَا لَا يَخۡفَوۡنَ عَلَيۡنَآۗ أَفَمَن يُلۡقَىٰ فِي ٱلنَّارِ خَيۡرٌ أَم مَّن يَأۡتِيٓ ءَامِنٗا يَوۡمَ ٱلۡقِيَٰمَةِۚ ٱعۡمَلُواْ مَا شِئۡتُمۡ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٌ\nАлбатта Бизнинг оятларимизга эгрилик қиладиган (яъни, уларни бузмоқчи бўлган) кимсалар Бизга махфий-номаълум бўлиб қолмаслар. Ахир дўзахга ташланадиган кимса яхшироқми, ёки Қиёмат Кунига (Аллоҳнинг азобидан) хотиржам ҳолда келадиган кишими?! Ўзларингиз хоҳлаган амални қилаверинглар! Албатта У қилаётган амалларингизни кўриб тургувчидир'}, {'chapter': 41, 'verse': 41, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ بِٱلذِّكۡرِ لَمَّا جَآءَهُمۡۖ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٞ\nАлбатта ўзларига Эслатма — Қуръон келган вақтда унга кофир бўлган кимсалар (қаттиқ азобга йўлиқурлар). Шак-шубҳасиз, у азиз — қудратли Китобдир'}, {'chapter': 41, 'verse': 42, 'text': 'لَّا يَأۡتِيهِ ٱلۡبَٰطِلُ مِنۢ بَيۡنِ يَدَيۡهِ وَلَا مِنۡ خَلۡفِهِۦۖ تَنزِيلٞ مِّنۡ حَكِيمٍ حَمِيدٖ\nУнга олдидан ҳам, ортидан ҳам (ҳеч қандай) ботил-ноҳақлик келмас (яъни, Қуръони Каримнинг ҳеч қайси томонидан бирон китоб ё ҳужжат келиб, уни ботил қила олмас, чунки у) ҳикмат ва ҳамду-сано эгаси томонидан нозил қилингандир'}, {'chapter': 41, 'verse': 43, 'text': 'مَّا يُقَالُ لَكَ إِلَّا مَا قَدۡ قِيلَ لِلرُّسُلِ مِن قَبۡلِكَۚ إِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ وَذُو عِقَابٍ أَلِيمٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга ҳам (кофирлар томонидан) фақат сиздан аввалги пайғамбарларга (кофирлар томонидан) айтилган сўзларгина айтилур (яъни, агар мушриклар сизга озор-азиятлар етказаётган бўлсалар, сиз бунга сабр қилинг, чунки аввалги пайғамбарларга ҳам кофирлар томонидан худди шунга ўхшаган озор-азиятлар етгандир). Шак-шубҳасиз, Парвардигорингиз ҳам мағфират Соҳибидир, ҳам аламли азоб Соҳибидир'}, {'chapter': 41, 'verse': 44, 'text': 'وَلَوۡ جَعَلۡنَٰهُ قُرۡءَانًا أَعۡجَمِيّٗا لَّقَالُواْ لَوۡلَا فُصِّلَتۡ ءَايَٰتُهُۥٓۖ ءَا۬عۡجَمِيّٞ وَعَرَبِيّٞۗ قُلۡ هُوَ لِلَّذِينَ ءَامَنُواْ هُدٗى وَشِفَآءٞۚ وَٱلَّذِينَ لَا يُؤۡمِنُونَ فِيٓ ءَاذَانِهِمۡ وَقۡرٞ وَهُوَ عَلَيۡهِمۡ عَمًىۚ أُوْلَـٰٓئِكَ يُنَادَوۡنَ مِن مَّكَانِۭ بَعِيدٖ\nАгар Биз (Қуръонни) ажамий (яъни, араб тилида бўлмаган) Қуръон қилганимизда, албатта улар: «Унинг оятлари (бизларнинг тилимизда) баён қилинмабди-да. (Муҳаммаднинг ўзи) араб-ку, (нега унга) ажамий (Қуръон тушибди?)», деган бўлур эдилар. Айтинг: «(Ушбу Қуръон) иймон келтирган зотлар учун ҳидоят ва (дилларидаги шак-шубҳани кетказувчи) шифодир. Иймон келтирмайдиган кимсаларни эса қулоқларида оғирлик-карлик бордир ва (Қуръон) уларга кўрлик (яъни, қоп-қоронғи зулмат) бўлур. Улар (гўё) узоқ бир жойдан чақирилаётган кишилар кабидирлар, яъни гарчи Қуръон оятларини эшитсалар-да, унинг маъно-ҳикматларини англай олмаслар'}, {'chapter': 41, 'verse': 45, 'text': 'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ\nДарҳақиқат, Биз Мусога Китоб — Таврот ато этганимизда, у (Таврот) ҳақида ҳам (худди Қуръон ҳақида ихтилоф қилинганидек) ихтилоф қилингандир. Агар Парвардигорингиз томонидан (барча ҳисоб-китоб Қиёмат Куни бўлиши хусусидаги) Сўз ўтмаганида, уларнинг ўртасида ҳукм қилинган бўлур эди (яъни, уларга иймонсизликлари учун дарҳол жазо берилган бўлур эди). Ҳақиқатан ҳам улар (Қуръон) ҳақида шубҳа-гумондадирлар'}, {'chapter': 41, 'verse': 46, 'text': 'مَّنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۗ وَمَا رَبُّكَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nКим бирон яхши амал қилса, ўзи учундир. Ким ёмонлик қилса, ўз зиёнига қилур. Парвардигорингиз бандаларига зулм қилгувчи эмасдир'}, {'chapter': 41, 'verse': 47, 'text': '۞إِلَيۡهِ يُرَدُّ عِلۡمُ ٱلسَّاعَةِۚ وَمَا تَخۡرُجُ مِن ثَمَرَٰتٖ مِّنۡ أَكۡمَامِهَا وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَيَوۡمَ يُنَادِيهِمۡ أَيۡنَ شُرَكَآءِي قَالُوٓاْ ءَاذَنَّـٰكَ مَامِنَّا مِن شَهِيدٖ\n(Қиёмат) соати (қачон бўлиши) ҳақидаги билим Унинг Ўзигагина қайтарилур (яъни, Қиёмат қачон бўлиши ҳақида сўралган киши «Уни ёлғиз Аллоҳ билади», дейиши лозимдир). Барча меваларнинг ўз гулкосаларидан чиқиши ҳам, ҳар бир аёлнинг (урғочи жинснинг) ҳомила олиб, кўз ёриши ҳам албатта Унинг илми билан бўлур. У зот (мушрикларга) Менинг шерикларим қани?!», деб нидо қиладиган Кунда улар: «Бизлар Сенга билдирурмизки, орамизда (Сенинг шерикларинг борлигига) биронта ҳам гувоҳ йўқдир», дедилар'}, {'chapter': 41, 'verse': 48, 'text': 'وَضَلَّ عَنۡهُم مَّا كَانُواْ يَدۡعُونَ مِن قَبۡلُۖ وَظَنُّواْ مَا لَهُم مِّن مَّحِيصٖ\nВа ўзлари илгари (ҳаёти дунёда) дуо-илтижо қилиб ўтган бутлари улардан ғойиб бўлди. Улар ўзлари учун (Аллоҳнинг азобидан) қочадиган бирон жой йўқ эканлигига ишондилар'}, {'chapter': 41, 'verse': 49, 'text': 'لَّا يَسۡـَٔمُ ٱلۡإِنسَٰنُ مِن دُعَآءِ ٱلۡخَيۡرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٞ قَنُوطٞ\nИнсон (ўзи учун) яхшилик (мулку-давлат, бахт-саодат) тилашдан ҳеч зерикмас. Агар унга ёмонлик (камбағаллик, бахтсизлик) етиб қолса, ноумидликка, тушкунликка тушгувчидир'}, {'chapter': 41, 'verse': 50, 'text': 'وَلَئِنۡ أَذَقۡنَٰهُ رَحۡمَةٗ مِّنَّا مِنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ هَٰذَا لِي وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّجِعۡتُ إِلَىٰ رَبِّيٓ إِنَّ لِي عِندَهُۥ لَلۡحُسۡنَىٰۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُواْ بِمَا عَمِلُواْ وَلَنُذِيقَنَّهُم مِّنۡ عَذَابٍ غَلِيظٖ\nҚасамки, агар унга бирон бало-кулфат етганидан сўнг Биз Ўз томонимиздан бўлган бирон марҳаматни тотдирсак, албатта у «Бу ёлғиз ўзимнинг (донолигим ва саъй-ҳаракатим билан бўлди). Мен (Қиёмат) соати қойим бўлишига ишонмайман. Қасамки, агар (мабодо Қиёмат қойим бўлиб), мен Парвардигоримга қайтарилсам, ҳеч шак-шубҳасиз, Унинг даргоҳида ҳам мен учун гўзал (оқибат) — жаннат бўлур, дер. Бас, (ана ўша ўзлари иймон келтирмаган ва яхши амал қилмаган ҳолларида жаннатдан хомтамаъ бўлиб юрган кимсалар билсинларки), албатта Биз кофир бўлган кимсаларга ўзлари қилган амалларининг хабарини берурмиз ва албатта уларга қаттиқ азобни тотдирурмиз'}, {'chapter': 41, 'verse': 51, 'text': 'وَإِذَآ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٖ\nҚачон Биз инсонга (неъмат-давлат) инъом қилсак, у (Бизга шукр қилишдан) юз ўгирар ва ўз томонига кетар (яъни, Бизни унутар). Қачон унга ёмонлик (камбағаллик-кулфат) етиб қолса эса у узундан-узоқ дуо-илтижо эгасидир'}, {'chapter': 41, 'verse': 52, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ ثُمَّ كَفَرۡتُم بِهِۦ مَنۡ أَضَلُّ مِمَّنۡ هُوَ فِي شِقَاقِۭ بَعِيدٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Хабар берингиз-чи, агар (Қуръон) ҳақиқатан ҳам Аллоҳ ҳузуридан (нозил қилинган бўлса-ю, сўнгра сизлар у (Қуръон)га кофир бўлсангизлар, (у ҳолда) ким (сизлар каби) мангу ихтилофда бўлган кимсалардан ҳам йўлдан озганроқ бўлур?!»'}, {'chapter': 41, 'verse': 53, 'text': 'سَنُرِيهِمۡ ءَايَٰتِنَا فِي ٱلۡأٓفَاقِ وَفِيٓ أَنفُسِهِمۡ حَتَّىٰ يَتَبَيَّنَ لَهُمۡ أَنَّهُ ٱلۡحَقُّۗ أَوَلَمۡ يَكۡفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nТоки уларга (ушбу Қуръон ростдан ҳам Аллоҳ томонидан нозил қилинган) Ҳақ (Китоб) эканлиги аниқ маълум бўлгунича, албатта, Биз уларга атроф-офоқдаги ва ўз вужудларидаги (Бизнинг борлигимиз ва қудратимизга далолат қиладиган) оят-аломатларимизни кўрсатажакмиз. Ахир (уларга) Парвардигорингизнинг барча нарсага гувоҳ экани (яъни, Аллоҳ таоло ушбу Қуръонда коинотдаги барча нарсани қамраб олиб, Ўз ҳукмини баён қилаётгани сизнинг ҳақ пайғамбар эканлигингизни исботлаш учун) етарли эмасми'}, {'chapter': 41, 'verse': 54, 'text': 'أَلَآ إِنَّهُمۡ فِي مِرۡيَةٖ مِّن لِّقَآءِ رَبِّهِمۡۗ أَلَآ إِنَّهُۥ بِكُلِّ شَيۡءٖ مُّحِيطُۢ\n(Эй инсонлар) огоҳ бўлингизким, албатта улар Парвардигорларига рўбарў бўлишдан шак-шубҳададирлар. Огоҳ бўлингизким, албатта У зот барча нарсани иҳота қилгувчидир'}, {'chapter': 42, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 42, 'verse': 2, 'text': 'عٓسٓقٓ\nАйн, Син, Қоф'}, {'chapter': 42, 'verse': 3, 'text': 'كَذَٰلِكَ يُوحِيٓ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Қудратли ва Ҳикматли Аллоҳ сизга ва сиздан аввалги (пайғамбарлар)га мана шундай ваҳий қилур'}, {'chapter': 42, 'verse': 4, 'text': 'لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ\nОсмонлар ва Ердаги бор нарсалар Уникидир. У юксак ва улуғдир'}, {'chapter': 42, 'verse': 5, 'text': 'تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِن فَوۡقِهِنَّۚ وَٱلۡمَلَـٰٓئِكَةُ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيَسۡتَغۡفِرُونَ لِمَن فِي ٱلۡأَرۡضِۗ أَلَآ إِنَّ ٱللَّهَ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\n(Мушрик жамоаларнинг ширк келтиришлари сабабли) устларидаги осмонлар ёрилиб кетишга яқин бўлур. Фаришталар эса Парвардигорга ҳамду сано айтиш билан (У зотни айбу нуқсондан ва «шериклар»дан) покларлар ва Ердаги (мўмин-мусулмон) кишилар учун (Аллоҳдан) мағфират сўрарлар. Огоҳ бўлингизким, албатта Аллоҳнинг Ўзигина мағфират қилгувчи, меҳрибондир'}, {'chapter': 42, 'verse': 6, 'text': 'وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيۡهِمۡ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ\n(Аллоҳни) қўйиб (бошқа) «дўстлар»ни ушлаган (яъни, ўшаларга сиғинаётган) кимсалар устида Аллоҳ кузатиб тургувчидир. (Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларнинг устида вакил-қўриқчи эмассиз'}, {'chapter': 42, 'verse': 7, 'text': 'وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ قُرۡءَانًا عَرَبِيّٗا لِّتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَا وَتُنذِرَ يَوۡمَ ٱلۡجَمۡعِ لَا رَيۡبَ فِيهِۚ فَرِيقٞ فِي ٱلۡجَنَّةِ وَفَرِيقٞ فِي ٱلسَّعِيرِ\n(Барча) қишлоқ-шаҳарлар онаси — пойтахти (Макка аҳлини) ва унинг атрофидаги кишиларни (Охират азобидан) огоҳллнтиришингиз учун ва (Охират азобидан) шак-шубҳа бўлмаган тўпланиш Кунидан огоҳлантиришингиз учун Биз сизга мана шундай арабий Қуръонни ваҳий қилдик. (У кунда) бир гуруҳ жаннатда бўлса, бир гуруҳ дўзахдадир'}, {'chapter': 42, 'verse': 8, 'text': 'وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَهُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمُونَ مَا لَهُم مِّن وَلِيّٖ وَلَا نَصِيرٍ\nАгар Аллоҳ хоҳлаганида, албатта уларни (яъни, барча инсонларни) бир миллат (бир динда — Исломда) қилган бўлур эди. Лекин У Ўзи хоҳлаган кишиларни Ўз раҳматига (жаннатига) дохил қилур. Золим-кофирлар учун эса бирон дўст ва ёрдамчи йўқдир'}, {'chapter': 42, 'verse': 9, 'text': 'أَمِ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۖ فَٱللَّهُ هُوَ ٱلۡوَلِيُّ وَهُوَ يُحۡيِ ٱلۡمَوۡتَىٰ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЁки улар (Аллоҳни) қўйиб (бошқа) «дўстлар»ни ушлаб олдиларми (яъни, ўшаларга сиғинмоқдаларми)?! (Йўқ), фақат Аллоҳнинг Ўзигина Дўстдир. У ўликларга ҳаёт берур ва У барча нарсага қодирдир'}, {'chapter': 42, 'verse': 10, 'text': 'وَمَا ٱخۡتَلَفۡتُمۡ فِيهِ مِن شَيۡءٖ فَحُكۡمُهُۥٓ إِلَى ٱللَّهِۚ ذَٰلِكُمُ ٱللَّهُ رَبِّي عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мўминларга айтинг): «Сизлар (бу ҳаёти дунёда кофирлар билан) ихтилоф қилган ҳар бир нарсанинг ҳукми (Қиёмат Кунида) Аллоҳга (қайтарилур ва У зот ким ҳақ, ким ноҳақ эканлигини ажратиб берур). Мана шу Аллоҳ Парвардигоримдир. Ўзига суяндим ва Ўзига илтижо қилурман»'}, {'chapter': 42, 'verse': 11, 'text': 'فَاطِرُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَمِنَ ٱلۡأَنۡعَٰمِ أَزۡوَٰجٗا يَذۡرَؤُكُمۡ فِيهِۚ لَيۡسَ كَمِثۡلِهِۦ شَيۡءٞۖ وَهُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ\n(Аллоҳ) осмонлар ва Ернинг илк яратгувчисидир. У сизлар учун ўзларингиздан бўлган жуфтларни яратди ва чорва ҳайвонларидан ҳам жуфт-жуфт (яратди). У сизларни ўша (жуфтлик воситасида) кўпайтирур. Бирон нарса У зотга ўхшаш эмасдир. У эшитгувчи ва кўриб тургувчидир'}, {'chapter': 42, 'verse': 12, 'text': 'لَهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ بِكُلِّ شَيۡءٍ عَلِيمٞ\nОсмонлар ва Ер (хазиналарининг) очқичлари Уникидир. У Ўзи хоҳлаган кишиларнинг ризқини кенг қилур ва (Ўзи хоҳлаган кишиларнинг ризқини) танг қилур. Дарҳақиқат, у барча нарсани билгувчидир'}, {'chapter': 42, 'verse': 13, 'text': '۞شَرَعَ لَكُم مِّنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحٗا وَٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَمَا وَصَّيۡنَا بِهِۦٓ إِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰٓۖ أَنۡ أَقِيمُواْ ٱلدِّينَ وَلَا تَتَفَرَّقُواْ فِيهِۚ كَبُرَ عَلَى ٱلۡمُشۡرِكِينَ مَا تَدۡعُوهُمۡ إِلَيۡهِۚ ٱللَّهُ يَجۡتَبِيٓ إِلَيۡهِ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَن يُنِيبُ\n(Эй мўминлар, Аллоҳ) сизлар учун ҳам диндан Нуҳга буюрган нарсани ва Биз сизга (яъни, Муҳаммад алайҳис-салоту вас-саломга) ваҳий қилган нарсани, (шунингдек) Биз Иброҳим, Мусо ва Ийсога буюрган нарсани — шариат (қонун) қилди, «Динни барпо қилинглар ва унда фирқа-фирқа бўлиб бўлинманглар!» (Эй Муҳаммад алайҳис-салоту вас-салом), мушрикларга сиз даъват қилаётган нарса (яъни, ёлғиз Аллоҳга ибодат қилиш) зўр келди — оғирлик қилди. Аллоҳ у (динга) ҳидоят топиш учун Ўзи хоҳлаган кишиларни сайлар ва Унга (ёлғиз Аллоҳга) илтижо қиладиган кишиларни ҳидоят қилур'}, {'chapter': 42, 'verse': 14, 'text': 'وَمَا تَفَرَّقُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ إِلَىٰٓ أَجَلٖ مُّسَمّٗى لَّقُضِيَ بَيۡنَهُمۡۚ وَإِنَّ ٱلَّذِينَ أُورِثُواْ ٱلۡكِتَٰبَ مِنۢ بَعۡدِهِمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ\nУлар (яъни, аввалги пайғамбарларнинг қавмлари) ҳам бўлинмаганлар, магар ўзларига (турли фирқаларга бўлинмаслик зарурлиги ҳақида) билим-ҳужжат келганидан сўнг ўзаро ҳасад-адоват қилишиб (фирқа-фирқа бўлиб кетганлар). Агар Парвардигорингиз томонидан (уларнинг жазоларини) белгиланган муддатгача (яъни, Қиёматгача кечиктириш хусусидаги) Сўз ўтмаганида, албатта уларнинг ўртасида ҳукм қилинган бўлур эди (яъни, улар мана шу дунёдаёқ жазоларини тортган бўлур эдилар)! Албатта улардан кейин Китобга (Таврот, Инжилга) ворис қилинган кимсалар (яъни, Муҳаммад алайҳис-салоту вас-саломга замондош бўлган яҳудий ва насронийлар) ҳам ундан (ўз Китобларидан) шак-шубҳададирлар'}, {'chapter': 42, 'verse': 15, 'text': 'فَلِذَٰلِكَ فَٱدۡعُۖ وَٱسۡتَقِمۡ كَمَآ أُمِرۡتَۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡۖ وَقُلۡ ءَامَنتُ بِمَآ أَنزَلَ ٱللَّهُ مِن كِتَٰبٖۖ وَأُمِرۡتُ لِأَعۡدِلَ بَيۡنَكُمُۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمۡۖ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡۖ لَا حُجَّةَ بَيۡنَنَا وَبَيۡنَكُمُۖ ٱللَّهُ يَجۡمَعُ بَيۡنَنَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), мана шунинг учун (яъни, аввалги қавмлар фирқа-фирқа бўлиб кетганлари сабабли йўлдан озганлари учун — сиз ўз умматларингизни бирлик-иттифоққа) даъват қилинг ва ўзингизга буюрилгани янглиғ Тўғри Йўлда бўлинг ҳамда уларнинг (мушрикларнинг) ҳавойи нафсларига эргашманг! Айтинг: «Мен Аллоҳ нозил қилган ҳар бир Китобга иймон келтирдим ва сизларнинг ўртангизда (мендан сўраб келган муаммоларингизни ҳал этишда) адолат қилишга амр этилдим. Аллоҳ Парвардигоримиз ва Парвардигорингиздир. Бизларнинг амалларимиз ўзимиз учун, сизларнинг амалларингиз ўзларингиз учундир. Бизлар билан сизларнинг ўртангизда бирон жанжал йўқдир. Аллоҳ (Қиёмат Кунида) барчамизни жамлар ва ёлғиз Унга қайтиш бордир'}, {'chapter': 42, 'verse': 16, 'text': 'وَٱلَّذِينَ يُحَآجُّونَ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ٱسۡتُجِيبَ لَهُۥ حُجَّتُهُمۡ دَاحِضَةٌ عِندَ رَبِّهِمۡ وَعَلَيۡهِمۡ غَضَبٞ وَلَهُمۡ عَذَابٞ شَدِيدٌ\nАллоҳ (дини) ҳақида уни қабул қилинганидан сўнг (одамларни йўлдан оздириш учун) талашиб-тортишадиган кимсаларнинг ҳужжатлари (яъни, талашиб-тортишишлари) Парвардигор наздида ботил-беҳудадир. Уларга (бу дунёда) ғазаб, (Охиратда эса) қаттиқ азоб бордир'}, {'chapter': 42, 'verse': 17, 'text': 'ٱللَّهُ ٱلَّذِيٓ أَنزَلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ وَٱلۡمِيزَانَۗ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٞ\nАллоҳ ҳаққи-рост бўлган Китобни ва тарози-адолатни нозил қилган Зотдир. Қаердан билурсиз, эҳтимол (Қиёмат) соати яқиндир. (Бас, ғафлатда қолмай Аллоҳ нозил қилган Ҳақ Китоб ва адолатни маҳкам ушлаш лозимдир)'}, {'chapter': 42, 'verse': 18, 'text': 'يَسۡتَعۡجِلُ بِهَا ٱلَّذِينَ لَا يُؤۡمِنُونَ بِهَاۖ وَٱلَّذِينَ ءَامَنُواْ مُشۡفِقُونَ مِنۡهَا وَيَعۡلَمُونَ أَنَّهَا ٱلۡحَقُّۗ أَلَآ إِنَّ ٱلَّذِينَ يُمَارُونَ فِي ٱلسَّاعَةِ لَفِي ضَلَٰلِۭ بَعِيدٍ\n(Қиёматга) иймон келтирмайдиган кимсалар уни шоштирурлар. Иймон келтирган зотлар эса ундан қўрқувчидирлар. Улар (Қиёматнинг) ҳақ эқанлигини билурлар. Огоҳ бўлингизким, (Қиёмат) соати хусусида талашиб-тортишадиган кимсалар (Ҳақ йўлдан) йироқ залолатдадирлар'}, {'chapter': 42, 'verse': 19, 'text': 'ٱللَّهُ لَطِيفُۢ بِعِبَادِهِۦ يَرۡزُقُ مَن يَشَآءُۖ وَهُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ\nАллоҳ бандаларига меҳрибондир, У Ўзи хоҳлаган кишиларга (кенг-мўл) ризқ берур. У кучли, қудратлидир'}, {'chapter': 42, 'verse': 20, 'text': 'مَن كَانَ يُرِيدُ حَرۡثَ ٱلۡأٓخِرَةِ نَزِدۡ لَهُۥ فِي حَرۡثِهِۦۖ وَمَن كَانَ يُرِيدُ حَرۡثَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِن نَّصِيبٍ\nКим (ўз амали билан) Охират экинини (яъни, савобини) истар экан, Биз унга экинини(нг ҳосилини) мўл-зиёда қилурмиз. Ким дунё экинини истар экан, Биз унга ўшандан (дунё матоларидан) берурмиз ва унинг учун Охиратда бирон насиба бўлмас'}, {'chapter': 42, 'verse': 21, 'text': 'أَمۡ لَهُمۡ شُرَكَـٰٓؤُاْ شَرَعُواْ لَهُم مِّنَ ٱلدِّينِ مَا لَمۡ يَأۡذَنۢ بِهِ ٱللَّهُۚ وَلَوۡلَا كَلِمَةُ ٱلۡفَصۡلِ لَقُضِيَ بَيۡنَهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ\nБалки улар (Макка мушриклари) учун (куфр ва ширк каби) Аллоҳ буюрмаган нарсаларни — «дин»ни уларга шариат қилиб берган шериклари — бутлари бордир?! Агар (яхши-ёмон амаллар Қиёмат Кунида) ажралиши хусусидаги Сўз бўлмаганида, албатта уларнинг ўрталарида (шу дунёдаёқ) ҳукм қилинган бўлур эди. Золим кимсалар учун шак-шубҳасиз, аламли азоб бордир'}, {'chapter': 42, 'verse': 22, 'text': 'تَرَى ٱلظَّـٰلِمِينَ مُشۡفِقِينَ مِمَّا كَسَبُواْ وَهُوَ وَاقِعُۢ بِهِمۡۗ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فِي رَوۡضَاتِ ٱلۡجَنَّاتِۖ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ\n(Қиёмат Кунида) у золимларни ўзлари (ҳаёти дунёда) касб қилган нарсадан (яъни, куфру исёнларига бериладиган жазолардан) қўрқиб турган ҳолларида кўрурсиз. У (жазо эса қўрқсалар-қўрқмасалар) уларга тушгувчидир! Иймон келтирган ва яхши амаллар қилган зотлар эса жаннатларнинг боғларида бўлишиб, улар учун Парвардигорлари ҳузурида хоҳлаган нарсалари бордир! Мана шу катта фазлу марҳаматнинг ўзидир'}, {'chapter': 42, 'verse': 23, 'text': 'ذَٰلِكَ ٱلَّذِي يُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًا إِلَّا ٱلۡمَوَدَّةَ فِي ٱلۡقُرۡبَىٰۗ وَمَن يَقۡتَرِفۡ حَسَنَةٗ نَّزِدۡ لَهُۥ فِيهَا حُسۡنًاۚ إِنَّ ٱللَّهَ غَفُورٞ شَكُورٌ\nАллоҳ иймон келтирган ва яхши амаллар қилган бандаларига берадиган хушхабари ана шудир! (Эй Муҳаммад алайҳис-салоту вас-салом, Макка мушрикларига) айтинг: «Мен сизлардан бу (даъватим) учун ажр-мукофот сўрамайман, фақат қариндошчиликдаги дўстлик-яқинликнигина (яъни, мен сизлар билан насл-насаб жиҳатидан қариндош бўлганим ҳаққи-ҳурмати бу даъватимга тўсқинчилик қилмасликларингизнигина сўрайман)». Ким бирон чиройли амал қилса, Биз унинг учун ўша (чиройли амали)да яна ҳусни зиёда қилурмиз (яъни, зиёда савоб ато этурмиз). Албатта Аллоҳ мағфират қилгувчи, яхшиликни билгувчидир'}, {'chapter': 42, 'verse': 24, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗاۖ فَإِن يَشَإِ ٱللَّهُ يَخۡتِمۡ عَلَىٰ قَلۡبِكَۗ وَيَمۡحُ ٱللَّهُ ٱلۡبَٰطِلَ وَيُحِقُّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦٓۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nЁки улар: «(Муҳаммад Қуръон Аллоҳ томонидан нозил бўлган, деб) Аллоҳ шаънига ёлғон тўқиди», дейдиларми?! (Ундоқ эмас), чунки агар (сиз ёлғон тўқийдиган бўлсангиз) Аллоҳ хоҳласа дилингизни муҳрлаб қўюр. Аллоҳ Ўз сўзлари билан ботил-ноҳақ (гаплар)ни йўқ қилур ва Ҳақни ҳақ қилур — рўёбга чиқарур. Албатта У дилларни эгаллаган (сирлар)ни билгувчидир'}, {'chapter': 42, 'verse': 25, 'text': 'وَهُوَ ٱلَّذِي يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَعۡفُواْ عَنِ ٱلسَّيِّـَٔاتِ وَيَعۡلَمُ مَا تَفۡعَلُونَ\nУ бандаларидан тавба-тазарруъ қабул қиладиган, ёмонликларни афв этадиган ва қиладиган ишларингизни биладиган Зотдир'}, {'chapter': 42, 'verse': 26, 'text': 'وَيَسۡتَجِيبُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَيَزِيدُهُم مِّن فَضۡلِهِۦۚ وَٱلۡكَٰفِرُونَ لَهُمۡ عَذَابٞ شَدِيدٞ\nУ иймон келтирган ва яхши амаллар қилган зотларнинг (дуоларини) ижобат қилур ва уларга (сўраган нарсаларини ато этиб, яна) Ўз фазлу карамидан зиёда қилур. Кофирлар учун эса қаттиқ азоб бордир'}, {'chapter': 42, 'verse': 27, 'text': '۞وَلَوۡ بَسَطَ ٱللَّهُ ٱلرِّزۡقَ لِعِبَادِهِۦ لَبَغَوۡاْ فِي ٱلۡأَرۡضِ وَلَٰكِن يُنَزِّلُ بِقَدَرٖ مَّا يَشَآءُۚ إِنَّهُۥ بِعِبَادِهِۦ خَبِيرُۢ بَصِيرٞ\nАгар Аллоҳ (барча) бандаларининг ризқларини кенг-мўл қилса, албатта улар Ер юзида зулм-тажовузкорлик қилган бўлур эдилар. Лекин У зот (бандаларининг ризқу рўзларини) Ўзи хоҳлаганича ўлчов билан туширур. Албатта У бандаларидан огоҳ ва кўриб тургувчидир'}, {'chapter': 42, 'verse': 28, 'text': 'وَهُوَ ٱلَّذِي يُنَزِّلُ ٱلۡغَيۡثَ مِنۢ بَعۡدِ مَا قَنَطُواْ وَيَنشُرُ رَحۡمَتَهُۥۚ وَهُوَ ٱلۡوَلِيُّ ٱلۡحَمِيدُ\nУ (одамлар ёғин-сочиндан) умидсизликка тушганларидан кейин ёмғир ёғдирадиган ва Ўз раҳмат-барокотини кенг ёядиган Зотдир. У ҳамду сано эгаси бўлган Дўстдир'}, {'chapter': 42, 'verse': 29, 'text': 'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٖۚ وَهُوَ عَلَىٰ جَمۡعِهِمۡ إِذَا يَشَآءُ قَدِيرٞ\nОсмонлар ва Ернинг яратилиши ҳамда (Аллоҳ) уларда тарқатиб-ёйиб юборган жониворлар Унинг (қудрати илоҳиясига далолат қиладиган) оят-аломатларидандир. У Ўзи хоҳлаган вақтида уларни жамлаб-йиғиб олишга қодирдир'}, {'chapter': 42, 'verse': 30, 'text': 'وَمَآ أَصَٰبَكُم مِّن مُّصِيبَةٖ فَبِمَا كَسَبَتۡ أَيۡدِيكُمۡ وَيَعۡفُواْ عَن كَثِيرٖ\n(Эй инсонлар) сизларга не бир мусибат етса, бас, ўз қўлларингиз қилган нарса — гуноҳ сабабли (етур). Яна У кўп (гуноҳларнинг жазосини бермасдан) афв қилиб юборур'}, {'chapter': 42, 'verse': 31, 'text': 'وَمَآ أَنتُم بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ\nСизлар Ерда (ҳеч қаёққа) қочиб қутулгувчи эмассизлар. Ва сизлар учун Аллоҳдан ўзга бирон дўст ва ёрдамчи йўқдир'}, {'chapter': 42, 'verse': 32, 'text': 'وَمِنۡ ءَايَٰتِهِ ٱلۡجَوَارِ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ\nДенгиз-дарёлардаги тоғлар янглиғ сузиб юрган (кема)лар ҳам Унинг (қудрати илоҳиясига далолат қиладиган) оят-аломатларидандир'}, {'chapter': 42, 'verse': 33, 'text': 'إِن يَشَأۡ يُسۡكِنِ ٱلرِّيحَ فَيَظۡلَلۡنَ رَوَاكِدَ عَلَىٰ ظَهۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٍ\nАгар (Аллоҳ) хоҳласа шамолни тўхтатиб қўюр, бас, улар (денгиз) устида (ҳеч қаёққа сузолмай) туриб қолурлар — Албатта бунда ҳар бир (балога) сабр қилгувчи, (Аллоҳнинг неъматларига) шукр қилгувчи учун оят-ибратлар бордир'}, {'chapter': 42, 'verse': 34, 'text': 'أَوۡ يُوبِقۡهُنَّ بِمَا كَسَبُواْ وَيَعۡفُ عَن كَثِيرٖ\nЁки (агар Аллоҳ хоҳласа қаттиқ бўрон юбориб, кемадаги одамларнинг) қилган гуноҳлари сабабли у (кема)ларни ҳалок (ғарқ) қилур ва кўпларни (ҳалокатдан қутқариб) афв этур'}, {'chapter': 42, 'verse': 35, 'text': 'وَيَعۡلَمَ ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِنَا مَا لَهُم مِّن مَّحِيصٖ\n(Токи) Бизнинг оятларимиз хусусида талашиб-тортишадиган кимсалар ўзлари учун (Аллоҳнинг азобидан) бирон қочиб қутуладиган жой йўқ эканлигини билиб олсинлар'}, {'chapter': 42, 'verse': 36, 'text': 'فَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰ لِلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ\nБас, (эй инсонлар), сизларга ато этилган бор нарса(лар) ҳаёти дунё матосидир ва иймон келтирган ҳамда ёлғиз Парвардигорларига суянадиган зотлар учун Аллоҳ ҳузуридаги нарсалар (яъни, Охират неъматлари — дунё матоларидан) яхшироқ ва боқийроқдир'}, {'chapter': 42, 'verse': 37, 'text': 'وَٱلَّذِينَ يَجۡتَنِبُونَ كَبَـٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ وَإِذَا مَا غَضِبُواْ هُمۡ يَغۡفِرُونَ\nУлар катта гуноҳлардан ва бузуқликлардан четланадиган, ғазабланган вақтларида эса кечириб юборадиган зотлардир'}, {'chapter': 42, 'verse': 38, 'text': 'وَٱلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمۡ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَمۡرُهُمۡ شُورَىٰ بَيۡنَهُمۡ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ\nУлар Парвардигорларига ижобат-итоат этган ва намозни тўкис адо қилган зотлардир. Уларнинг ишлари (мудом) ўзаро шўро-маслаҳат (билан) бўлур ва Биз уларни ризқлантирган нарсалардан инфоқ-эҳсон қилурлар'}, {'chapter': 42, 'verse': 39, 'text': 'وَٱلَّذِينَ إِذَآ أَصَابَهُمُ ٱلۡبَغۡيُ هُمۡ يَنتَصِرُونَ\nУлар ўзларига зулм етганда, (унга қарши курашиб) ғолиб бўладиган зотлардир'}, {'chapter': 42, 'verse': 40, 'text': 'وَجَزَـٰٓؤُاْ سَيِّئَةٖ سَيِّئَةٞ مِّثۡلُهَاۖ فَمَنۡ عَفَا وَأَصۡلَحَ فَأَجۡرُهُۥ عَلَى ٱللَّهِۚ إِنَّهُۥ لَا يُحِبُّ ٱلظَّـٰلِمِينَ\n(Ҳар қандай) ёмонликнинг жазоси худди ўзига ўхшаган ёмонликдир (яъни, ҳар бир ёмонликнинг ўзига яраша жазоси бордир). Энди ким (интиқом олишга қодир бўлгани ҳолда) афв қилиб (ўртани) тузатса, бас, унинг ажри Аллоҳнинг зиммасидадир. Албатта У зулм қилгувчиларни севмас'}, {'chapter': 42, 'verse': 41, 'text': 'وَلَمَنِ ٱنتَصَرَ بَعۡدَ ظُلۡمِهِۦ فَأُوْلَـٰٓئِكَ مَا عَلَيۡهِم مِّن سَبِيلٍ\nАлбатта ким мазлум бўлганидан сўнг (ўзига зулм қилган кимсадан) ғолиб бўлса, (интиқом олса), бас, улар(ни олган интиқомлари учун айблаш)га йўл йўқдир'}, {'chapter': 42, 'verse': 42, 'text': 'إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظۡلِمُونَ ٱلنَّاسَ وَيَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ\nФақат одамларга зулм қиладиган ва Ерда ноҳақ ҳадларидан ошадиган кимсалар(ни айблаш ва зулму зўравонликлари учун жазолаш)гагина йўл бордир. Ана ўшалар учун аламли азоб бордир'}, {'chapter': 42, 'verse': 43, 'text': 'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنۡ عَزۡمِ ٱلۡأُمُورِ\nАлбатта ким (ўзига етган озор-азиятларга) сабр қилса ва (интиқом олмай Аллоҳ учун) кечириб юборса, шак-шубҳасиз, бу иш ишларнинг мақсадга мувофиғидир'}, {'chapter': 42, 'verse': 44, 'text': 'وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِيّٖ مِّنۢ بَعۡدِهِۦۗ وَتَرَى ٱلظَّـٰلِمِينَ لَمَّا رَأَوُاْ ٱلۡعَذَابَ يَقُولُونَ هَلۡ إِلَىٰ مَرَدّٖ مِّن سَبِيلٖ\nКимни Аллоҳ йўлдан оздирса, бас, унинг учун ўшандан кейин бирон (Тўғри Йўл кўрсатгувчи) дўст йўқдир. Ундай золимларнинг (Қиёмат Кунида) азобни кўрган вақтларида: «(Яна дунёга) қайтишга бирон йўл бормикан?», деяётганларини кўрурсиз'}, {'chapter': 42, 'verse': 45, 'text': 'وَتَرَىٰهُمۡ يُعۡرَضُونَ عَلَيۡهَا خَٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرۡفٍ خَفِيّٖۗ وَقَالَ ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَآ إِنَّ ٱلظَّـٰلِمِينَ فِي عَذَابٖ مُّقِيمٖ\nУларнинг хорликдан эгилган ва (дўзахга) кўз қирлари билан қараган ҳолларида унга кўндаланг қилинаётганларини кўрурсиз. Иймон келтирган зотлар: «Албатта зиён кўргувчилар (гарчи дунё неъматларидан фойдаланиб ўтган бўлсалар-да) ва Қиёмат Кунида ўзларига, аҳли-оилаларига зиён қилган кимсалардир», дедилар. Огоҳ бўлингизким, у золимлар мангу азобдадирлар'}, {'chapter': 42, 'verse': 46, 'text': 'وَمَا كَانَ لَهُم مِّنۡ أَوۡلِيَآءَ يَنصُرُونَهُم مِّن دُونِ ٱللَّهِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ\nУлар учун Аллоҳдан ўзга ёрдам берадиган ҳеч қандай «дўстлар» бўлмас. Кимни Аллоҳ йўлдан оздирса, бас, унинг учун бирон (Тўғри) йўл йўқдир'}, {'chapter': 42, 'verse': 47, 'text': 'ٱسۡتَجِيبُواْ لِرَبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۚ مَا لَكُم مِّن مَّلۡجَإٖ يَوۡمَئِذٖ وَمَا لَكُم مِّن نَّكِيرٖ\n(Эй инсонлар), Аллоҳ томонидан ҳеч қайтариб бўлмайдиган бир Кун (Қиёмат) келиб қолишидан илгари Парвардигорингизга ижобат-итоат этингиз! У кунда сизлар учун бирон қочадиган жой ҳам бўлмас ва сизлар учун (қилиб ўтган гуноҳларингизни) инкор қилиш (имкони) ҳам бўлмас'}, {'chapter': 42, 'verse': 48, 'text': 'فَإِنۡ أَعۡرَضُواْ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظًاۖ إِنۡ عَلَيۡكَ إِلَّا ٱلۡبَلَٰغُۗ وَإِنَّآ إِذَآ أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ فَرِحَ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَإِنَّ ٱلۡإِنسَٰنَ كَفُورٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар (мушриклар) энди ҳам (ёлғиз Аллоҳга иймон келтиришдан) юз ўгирсалар, бас, Биз сизни уларнинг устига қўриқчи қилиб юборганимиз йўқ! Сизнинг зиммангизда фақат (Биз сизга нозил қилган ваҳийни) етказиш бор, холос. Дарҳақиқат, Биз инсонга Ўз томонимиздан раҳмат-неъматни тотдирган вақтимизда, у ўша (неъмат) билан шодланур, (аммо) агар уларга қўллари қилган (гуноҳлари) сабабли бирон ёмонлик етса, бас, албатта инсон (зоти) куфрони неъмат қилгувчидир — кўрнамакдир'}, {'chapter': 42, 'verse': 49, 'text': 'لِّلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَخۡلُقُ مَا يَشَآءُۚ يَهَبُ لِمَن يَشَآءُ إِنَٰثٗا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ\nОсмонлар ва Ер Аллоҳнинг мулкидир. У Ўзи хоҳлаган нарсани яратур, У Ўзи хоҳлаган кишига қизларни ҳадя этур ва Ўзи хоҳлаган кишига ўғилларни ҳадя этур'}, {'chapter': 42, 'verse': 50, 'text': 'أَوۡ يُزَوِّجُهُمۡ ذُكۡرَانٗا وَإِنَٰثٗاۖ وَيَجۡعَلُ مَن يَشَآءُ عَقِيمًاۚ إِنَّهُۥ عَلِيمٞ قَدِيرٞ\nЁки уларни эгизак ўғиллар ва қизлар қилур ва Ўзи хоҳлаган кишини — туғмас (бепушт) қилиб қўюр. Албатта У (барча нарсани) билгувчи ва (Ўзи хоҳлаган нарсани яратишга) қодирдир'}, {'chapter': 42, 'verse': 51, 'text': '۞وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحۡيًا أَوۡ مِن وَرَآيِٕ حِجَابٍ أَوۡ يُرۡسِلَ رَسُولٗا فَيُوحِيَ بِإِذۡنِهِۦ مَا يَشَآءُۚ إِنَّهُۥ عَلِيٌّ حَكِيمٞ\nБирон одам учун Аллоҳ унга сўзлаши жоиз эмас, магар ваҳий-илҳом орқали, ё бирон парда-тўсиқ ортидан ёки бирон элчи-фаришта юбориб, ўша (фаришта Аллоҳнинг) изни-ихтиёри билан ўзи хоҳлаган нарсани ваҳий қилиши орқали (сўзлар). Албатта У юксак ва ҳикмат Эгасидир'}, {'chapter': 42, 'verse': 52, 'text': 'وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ رُوحٗا مِّنۡ أَمۡرِنَاۚ مَا كُنتَ تَدۡرِي مَا ٱلۡكِتَٰبُ وَلَا ٱلۡإِيمَٰنُ وَلَٰكِن جَعَلۡنَٰهُ نُورٗا نَّهۡدِي بِهِۦ مَن نَّشَآءُ مِنۡ عِبَادِنَاۚ وَإِنَّكَ لَتَهۡدِيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), шундай қилиб (худди аввалги пайғамбарларга ваҳий қилганимиздек) Биз Ўз амримиз билан сизга Руҳни — Қуръонни ваҳий қилдик. Сиз (пайғамбар бўлишингиздан) илгари на Китобни — Қуръонни ва на иймонни (яъни, унинг ҳақиқат-моҳиятини) билгувчи эдингиз. Лекин Биз уни (Қуръонни) бир нур қилдикки, у билан бандаларимиздан Ўзимиз хоҳлаган кишиларни ҳидоят Қилурмиз. Албатта сиз (ўзингизга тушган ваҳий ёрдамида) Тўғри Йўлга етаклайсиз'}, {'chapter': 42, 'verse': 53, 'text': 'صِرَٰطِ ٱللَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ أَلَآ إِلَى ٱللَّهِ تَصِيرُ ٱلۡأُمُورُ\nУ (йўл) осмонлардаги ва Ердаги бор нарса Ўзиники бўлган Зот — Аллоҳнинг йўлидир! Огоҳ бўлингизким, барча ишлар ёлғиз Аллоҳга қайтур'}, {'chapter': 43, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 43, 'verse': 2, 'text': 'وَٱلۡكِتَٰبِ ٱلۡمُبِينِ\nОчиқ-равшан Китобга — Қуръонга қасамки'}, {'chapter': 43, 'verse': 3, 'text': 'إِنَّا جَعَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ\nалбатта Биз уни сизлар ақл юргизишингиз (маъно-мазмунини фаҳмлашингиз) учун арабий Қуръон қилдик'}, {'chapter': 43, 'verse': 4, 'text': 'وَإِنَّهُۥ فِيٓ أُمِّ ٱلۡكِتَٰبِ لَدَيۡنَا لَعَلِيٌّ حَكِيمٌ\nДарҳақиқат, у Бизнинг даргоҳимиздаги Асл Китобда (Лавҳул-Маҳфузда) юксак (мартабали) ва ҳикматли (Китобдир)'}, {'chapter': 43, 'verse': 5, 'text': 'أَفَنَضۡرِبُ عَنكُمُ ٱلذِّكۡرَ صَفۡحًا أَن كُنتُمۡ قَوۡمٗا مُّسۡرِفِينَ\nБас, (куфру исёнда) ҳаддан ошувчи қавм бўлганларингиз учун Биз сизлардан бу Эслатмани буриб (сизларни ўз ҳолларингизга ташлаб) қўюрмизми'}, {'chapter': 43, 'verse': 6, 'text': 'وَكَمۡ أَرۡسَلۡنَا مِن نَّبِيّٖ فِي ٱلۡأَوَّلِينَ\nБиз аввалгиларга ҳам қанча пайғамбар юборганмиз'}, {'chapter': 43, 'verse': 7, 'text': 'وَمَا يَأۡتِيهِم مِّن نَّبِيٍّ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nУлар ҳам ўзларига келган ҳар бир пайғамбарни масхара қилиб кулган эдилар'}, {'chapter': 43, 'verse': 8, 'text': 'فَأَهۡلَكۡنَآ أَشَدَّ مِنۡهُم بَطۡشٗا وَمَضَىٰ مَثَلُ ٱلۡأَوَّلِينَ\nБас, Биз улардан (Макка кофирларидан) кучли-қувватлироқларни ҳам ҳалок қилганмиз. (Ушбу Қуръонда) аввалгиларни масал-қиссалари ўтди-ку'}, {'chapter': 43, 'verse': 9, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ خَلَقَهُنَّ ٱلۡعَزِيزُ ٱلۡعَلِيمُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қасамки, агар сиз улардан: «Осмонлар ва Ерни ким яратган?», деб сўрасангиз, албатта: «Уларни қудратли ва доно (Аллоҳ) яратган», дерлар. (Аммо ўзлари ҳеч нарса ярата олмайдиган бут ва санамларни У зотга шерик қилиб, уларга сиғинурлар)'}, {'chapter': 43, 'verse': 10, 'text': 'ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدٗا وَجَعَلَ لَكُمۡ فِيهَا سُبُلٗا لَّعَلَّكُمۡ تَهۡتَدُونَ\nЎша Зот сизлар учун Ерни бешик-қароргоҳ қилиб қўйди ва (сафарларингизда кўзлаган манзилларингизга) йўл топишларингиз учун унда (ерда турли) йўлларни (пайдо) қилди'}, {'chapter': 43, 'verse': 11, 'text': 'وَٱلَّذِي نَزَّلَ مِنَ ٱلسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَنشَرۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ تُخۡرَجُونَ\nЎша Зот осмондан (аниқ) ўлчов билан сув-ёмғир ёғдирди. Бас, Биз у (сув) билан «ўлик» шаҳар-қишлоқларни) тирилтирдик (яъни, қуруқ ердан турли-туман гиёҳларни чиқардик). Сизлар ҳам (Қиёмат Кунида қабрларингиздан) мана шундай чиқарилурсизлар'}, {'chapter': 43, 'verse': 12, 'text': 'وَٱلَّذِي خَلَقَ ٱلۡأَزۡوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ ٱلۡفُلۡكِ وَٱلۡأَنۡعَٰمِ مَا تَرۡكَبُونَ\nЎша Зот барча жуфтларни (яъни, бор махлуқотни) яратди ва сизлар учун кемалар ва чорва ҳайвонларидан — сизлар минадиган нарсалар (пайдо) қилди'}, {'chapter': 43, 'verse': 13, 'text': 'لِتَسۡتَوُۥاْ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذۡكُرُواْ نِعۡمَةَ رَبِّكُمۡ إِذَا ٱسۡتَوَيۡتُمۡ عَلَيۡهِ وَتَقُولُواْ سُبۡحَٰنَ ٱلَّذِي سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُۥ مُقۡرِنِينَ\nТоки сизлар уларнинг устига ўрнашгайсизлар, сўнгра унга ўрнашиб олгач, Парвардигорингизнинг неъматини эслаб: «Бизларга бу (кема ёки от-улов)ни бўйсундириб қўйган зот (яъни, Аллоҳ барча айбу-нуқсондан) покдир. Бизлар ўзимиз бунга қодир эмас эдик'}, {'chapter': 43, 'verse': 14, 'text': 'وَإِنَّآ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ\nШак-шубҳасиз, бизлар (барчамиз) «Парвардигоримизга қайтгувчидирмиз», дегайсизлар'}, {'chapter': 43, 'verse': 15, 'text': 'وَجَعَلُواْ لَهُۥ مِنۡ عِبَادِهِۦ جُزۡءًاۚ إِنَّ ٱلۡإِنسَٰنَ لَكَفُورٞ مُّبِينٌ\nУлар (мушриклар, Аллоҳ) учун Ўз бандаларидан (айримларини, яъни фаришталарни) жузъ-бўлак қилдилар! (Аллоҳнинг бандалари бўлган фаришталарни «У зотнинг қизлари, яъни Унинг бир жузъи, дедилар. Чунки фарзанд отанинг жузъидир). Дарҳақиқат, инсон очиқ-ойдин кўрнамак-ношукурдир'}, {'chapter': 43, 'verse': 16, 'text': 'أَمِ ٱتَّخَذَ مِمَّا يَخۡلُقُ بَنَاتٖ وَأَصۡفَىٰكُم بِٱلۡبَنِينَ\nБалки, (Аллоҳ) Ўзи яратадиган нарсалардан қизларни олиб қолиб, сизларга ўғилларни танлаб бергандир'}, {'chapter': 43, 'verse': 17, 'text': 'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحۡمَٰنِ مَثَلٗا ظَلَّ وَجۡهُهُۥ مُسۡوَدّٗا وَهُوَ كَظِيمٌ\nҚачон уларнинг биронтасига, ўзи Раҳмон учун мисол қилган нарса (яъни, қиз кўргани) ҳақида хушхабар берилса, ғазабга тўлган ҳолда юзи қорайиб кетар'}, {'chapter': 43, 'verse': 18, 'text': 'أَوَمَن يُنَشَّؤُاْ فِي ٱلۡحِلۡيَةِ وَهُوَ فِي ٱلۡخِصَامِ غَيۡرُ مُبِينٖ\n«Ҳали зеб-зийнат ичида ўстириладиган, (ҳеч қандай) жанжал-мунозарада (ўз мақсадини) очиқ-равшан баён қилиб бера олмайдиган (ожиз нотавон қиз болами)?!»'}, {'chapter': 43, 'verse': 19, 'text': 'وَجَعَلُواْ ٱلۡمَلَـٰٓئِكَةَ ٱلَّذِينَ هُمۡ عِبَٰدُ ٱلرَّحۡمَٰنِ إِنَٰثًاۚ أَشَهِدُواْ خَلۡقَهُمۡۚ سَتُكۡتَبُ شَهَٰدَتُهُمۡ وَيُسۡـَٔلُونَ\n(Ҳолбуки), ўзлари Раҳмоннинг бандалари бўлмиш фаришталарни «Қизлар», дедилар! Ё улар (Аллоҳ фаришталарни) яратишига гувоҳ бўлганмидилар?! Уларнинг бу «гувоҳ»ликлари албатта (номаи аъмолларига) ёзилур ва улар (Қиёмат Кунида) сўроққа тутилурлар'}, {'chapter': 43, 'verse': 20, 'text': 'وَقَالُواْ لَوۡ شَآءَ ٱلرَّحۡمَٰنُ مَا عَبَدۡنَٰهُمۗ مَّا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَخۡرُصُونَ\nУлар: «Агар Раҳмон хоҳлаганида, бизлар уларга (яъни, фаришталарга «улар Аллоҳнинг қизлари», деган эътиқод билан) ибодат қилмаган бўлур эдик», дедилар. Улар учун бу ҳақда ҳеч қандай билим-асос йўқдир. Улар фақат ёлғон сўзламоқдалар'}, {'chapter': 43, 'verse': 21, 'text': 'أَمۡ ءَاتَيۡنَٰهُمۡ كِتَٰبٗا مِّن قَبۡلِهِۦ فَهُم بِهِۦ مُسۡتَمۡسِكُونَ\nЁки Биз уларга (Қуръондан) илгари бирон китоб ато этганмиз-у, улар ўшани ушлагувчимилар?! (Йўқ, асло ундоқ бўлган эмас)'}, {'chapter': 43, 'verse': 22, 'text': 'بَلۡ قَالُوٓاْ إِنَّا وَجَدۡنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٖ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّهۡتَدُونَ\nБалки улар (шундай) дедилар: «Албатта бизлар ота-боболаримизни бир миллат — дин устида топганмиз ва албатта бизлар уларнинг изларидан бориб ҳидоят топгувчидирмиз»'}, {'chapter': 43, 'verse': 23, 'text': 'وَكَذَٰلِكَ مَآ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَآ إِنَّا وَجَدۡنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٖ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّقۡتَدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), шунингдек, Биз сиздан илгари бирон қишлоқ-шаҳарга бирон огоҳлантиргувчи-пайғамбар юбормадик, магар (юборганимизда) у жойнинг боёнлари: «Албатта бизлар уларнинг изларидан эргашгувчидирмиз», деганлар'}, {'chapter': 43, 'verse': 24, 'text': '۞قَٰلَ أَوَلَوۡ جِئۡتُكُم بِأَهۡدَىٰ مِمَّا وَجَدتُّمۡ عَلَيۡهِ ءَابَآءَكُمۡۖ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ\n(Шунда пайғамбарлари уларга) «Агар мен сизларга сизлар ота-боболарингизни (эътиқод қилган ҳолларида) топган диндан тўғрироқ динни келтирган бўлсам ҳам-а?», деганида, улар: «Албатта бизлар сизлар элчи қилиб юборилган динга кофирдирмиз», дедилар'}, {'chapter': 43, 'verse': 25, 'text': 'فَٱنتَقَمۡنَا مِنۡهُمۡۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ\nБас, Биз улардан интиқом олдик. Энди у (ўз пайғамбарларини) ёлғончи қилгувчиларнинг оқибатлари қандай бўлганини кўринг'}, {'chapter': 43, 'verse': 26, 'text': 'وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ وَقَوۡمِهِۦٓ إِنَّنِي بَرَآءٞ مِّمَّا تَعۡبُدُونَ\nЭсланг, Иброҳим отасига ва қавмига деган эди: «Албатта мен сизлар ибодат қилаётган бутлардан покдирман'}, {'chapter': 43, 'verse': 27, 'text': 'إِلَّا ٱلَّذِي فَطَرَنِي فَإِنَّهُۥ سَيَهۡدِينِ\nМагар мени яратган Зотгагина (ибодат қилурман). Бас, албатта У мени (Ҳақ динга) ҳидоят қилажак»'}, {'chapter': 43, 'verse': 28, 'text': 'وَجَعَلَهَا كَلِمَةَۢ بَاقِيَةٗ فِي عَقِبِهِۦ لَعَلَّهُمۡ يَرۡجِعُونَ\nУ (Иброҳим) ўшани (яъни, ўзининг ёлғиз Аллоҳга ибодат қилиши ҳақидаги сўзни ўзидан кейин келадиган зурриёт-авлодлари ҳам унга) қайтишлари учун ўз ортида қолгувчи сўз қилди'}, {'chapter': 43, 'verse': 29, 'text': 'بَلۡ مَتَّعۡتُ هَـٰٓؤُلَآءِ وَءَابَآءَهُمۡ حَتَّىٰ جَآءَهُمُ ٱلۡحَقُّ وَرَسُولٞ مُّبِينٞ\nМен эса (Иброҳим авлодларидан бўлган, аммо у қолдирган Сўзга итоат этмаган) ана у (Макка аҳолиси)ни ҳам, уларнинг ота-боболарини ҳам то уларга Ҳақ — Қуръон ва очиқ-равшан пайғамбар (Муҳаммад алайҳис-салоту вас-салом) келгунича (узун умр ва фаровон ҳаёт билан) фойдалантирдим'}, {'chapter': 43, 'verse': 30, 'text': 'وَلَمَّا جَآءَهُمُ ٱلۡحَقُّ قَالُواْ هَٰذَا سِحۡرٞ وَإِنَّا بِهِۦ كَٰفِرُونَ\n(Аммо) қачонки уларга Ҳақ — Қуръон келгач, улар: «Бу сеҳрдир, албатта бизлар унга кофирдирмиз», дедилар'}, {'chapter': 43, 'verse': 31, 'text': 'وَقَالُواْ لَوۡلَا نُزِّلَ هَٰذَا ٱلۡقُرۡءَانُ عَلَىٰ رَجُلٖ مِّنَ ٱلۡقَرۡيَتَيۡنِ عَظِيمٍ\nЯна улар: «Бу Қуръон икки қишлоқ(нинг бири)дан бўлган улуғ одамга нозил қилинганида эди», дедилар'}, {'chapter': 43, 'verse': 32, 'text': 'أَهُمۡ يَقۡسِمُونَ رَحۡمَتَ رَبِّكَۚ نَحۡنُ قَسَمۡنَا بَيۡنَهُم مَّعِيشَتَهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَرَفَعۡنَا بَعۡضَهُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَتَّخِذَ بَعۡضُهُم بَعۡضٗا سُخۡرِيّٗاۗ وَرَحۡمَتُ رَبِّكَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингизнинг раҳмати (бўлмиш пайғамбарлик)ни ўшалар тақсимлайдиларми?! (Йўқ, асло ундоқ эмас!) Уларнинг ҳаёти дунёдаги маишат-тирикчиликларини ҳам уларнинг ўрталарида Биз Ўзимиз тақсимлаганмиз ва баъзилари баъзиларини қўл остига олиб (ишлатиш) учун айримларини айримларидан баланд даража-мартабаларга кўтариб қўйганмиз. Парвардигорингизнинг раҳмати-пайғамбарлик эса улар тўплайдиган нарса (мол-дунё)ларидан яхшироқдир. (Бас, Биз бу улуғ неъматни фақат Ўзимиз танлаган кишиларгагина ато этурмиз)'}, {'chapter': 43, 'verse': 33, 'text': 'وَلَوۡلَآ أَن يَكُونَ ٱلنَّاسُ أُمَّةٗ وَٰحِدَةٗ لَّجَعَلۡنَا لِمَن يَكۡفُرُ بِٱلرَّحۡمَٰنِ لِبُيُوتِهِمۡ سُقُفٗا مِّن فِضَّةٖ وَمَعَارِجَ عَلَيۡهَا يَظۡهَرُونَ\nАгар (иймонсиз кимсаларнинг бой-бадавлат, тўкин-сочинликда яшаётганларини кўришиб, барча) одамлар бир миллат (яъни, кофир) бўлиб олишлари бўлмаганида эди, албатта Биз Раҳмонга (яъни, Бизга) кофир бўладиган кимсалар учун уйларининг шифтларини ҳам, унга чиқадиган нарвонларни ҳам кумушдан қилиб қўйган бўлур эдик'}, {'chapter': 43, 'verse': 34, 'text': 'وَلِبُيُوتِهِمۡ أَبۡوَٰبٗا وَسُرُرًا عَلَيۡهَا يَتَّكِـُٔونَ\nЯна уларнинг уйларининг эшикларини ҳам, ўзлари устида суяниб-ястаниб ўтирадиган сўриларни ҳам (кумушдан қилиб қўйган бўлур эдик)'}, {'chapter': 43, 'verse': 35, 'text': 'وَزُخۡرُفٗاۚ وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَٱلۡأٓخِرَةُ عِندَ رَبِّكَ لِلۡمُتَّقِينَ\nЯна (барча) зеб-зийнат, нақш-нигорларни ҳам (ўша кофирлар учун қилиб қўйган бўлур эдик. Зеро, буларнинг) барчаси фақат ҳаёти дунё матоларидир. Охират эса Парвардигорингиз наздида тақводор зотлар учундир'}, {'chapter': 43, 'verse': 36, 'text': 'وَمَن يَعۡشُ عَن ذِكۡرِ ٱلرَّحۡمَٰنِ نُقَيِّضۡ لَهُۥ شَيۡطَٰنٗا فَهُوَ لَهُۥ قَرِينٞ\nКим Раҳмон эслатмасидан (яъни, Қуръон панд-насиҳатларидан) кўр бўлиб олса — юз ўгирса, Биз унга шайтонни яқин қилиб қўюрмиз, бас, у ўша (кофирга доимий) ҳамроҳ бўлур'}, {'chapter': 43, 'verse': 37, 'text': 'وَإِنَّهُمۡ لَيَصُدُّونَهُمۡ عَنِ ٱلسَّبِيلِ وَيَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ\nШак-шубҳасиз, (шайтонлар кофирларни Тўғри) йўлдан тўсурлар, (аммо кофирлар) ўзларини ҳидоят топгувчилар, деб ҳисоблайдилар'}, {'chapter': 43, 'verse': 38, 'text': 'حَتَّىٰٓ إِذَا جَآءَنَا قَالَ يَٰلَيۡتَ بَيۡنِي وَبَيۡنَكَ بُعۡدَ ٱلۡمَشۡرِقَيۡنِ فَبِئۡسَ ٱلۡقَرِينُ\nТо қачон Бизнинг ҳузуримизга келгач, (ўзини Тўғри Йўлдан оздирган шайтонга қараб): «Орзу эди, мен билан сенинг ўртанг Шарқу Ғарбнинг ўртасидаги масофа (каби йироқ) бўлса. Бас, (сен) нақадар ёмон ҳамроҳдирсан», дер'}, {'chapter': 43, 'verse': 39, 'text': 'وَلَن يَنفَعَكُمُ ٱلۡيَوۡمَ إِذ ظَّلَمۡتُمۡ أَنَّكُمۡ فِي ٱلۡعَذَابِ مُشۡتَرِكُونَ\n(Эй кофирлар ва уларни Тўғри Йўлдан оздирган пешволари, ҳаёти дунёда куфр йўлини танлаш билан ўзларингизга) зулм қилганларингиз сабабли бу Кун сизларга азобда шерик эканликларингиз ҳаргиз фойда бермас (яъни, азобни қаттиқлигидан ҳар бирингиз ўзингиз билан овора бўлиб, шерикларингизга ҳамдардлик билдириб, азобларини енгиллата олмассизлар)'}, {'chapter': 43, 'verse': 40, 'text': 'أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ أَوۡ تَهۡدِي ٱلۡعُمۡيَ وَمَن كَانَ فِي ضَلَٰلٖ مُّبِينٖ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз карларга эшиттира олурмисиз, ё кўрлар ва очиқ залолатда бўлган кимсаларни ҳидоят қила олурмисиз'}, {'chapter': 43, 'verse': 41, 'text': 'فَإِمَّا نَذۡهَبَنَّ بِكَ فَإِنَّا مِنۡهُم مُّنتَقِمُونَ\nБас, агар Биз (кофирларни мағлуб этишдан илгари) сизни кетказсак (яъни, вафот қилдирсак ҳам) албатта биз улардан (Охиратда) интиқом олгувчидирмиз'}, {'chapter': 43, 'verse': 42, 'text': 'أَوۡ نُرِيَنَّكَ ٱلَّذِي وَعَدۡنَٰهُمۡ فَإِنَّا عَلَيۡهِم مُّقۡتَدِرُونَ\nЁки, албатта Биз уларга ваъда қилган нарсани (яъни, бошларига тушажак азобни ҳаётлик чоғингиздаёк) сизга кўрсатурмиз. Зеро, Биз уларга (азоб беришга) қодирдирмиз'}, {'chapter': 43, 'verse': 43, 'text': 'فَٱسۡتَمۡسِكۡ بِٱلَّذِيٓ أُوحِيَ إِلَيۡكَۖ إِنَّكَ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nБас, сиз ўзингизга ваҳий қилинган нарсани Қуръонни маҳкам ушланг! Албатта, сиз Тўғри Йўлдадирсиз'}, {'chapter': 43, 'verse': 44, 'text': 'وَإِنَّهُۥ لَذِكۡرٞ لَّكَ وَلِقَوۡمِكَۖ وَسَوۡفَ تُسۡـَٔلُونَ\nАлбатта у (Қуръон) сиз учун ҳам, қавмингиз-умматингиз учун ҳам бир шарафдир ва яқинда (Қиёмат Кунида бу неъмати илоҳийя хусусида) сўралурсизлар'}, {'chapter': 43, 'verse': 45, 'text': 'وَسۡـَٔلۡ مَنۡ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رُّسُلِنَآ أَجَعَلۡنَا مِن دُونِ ٱلرَّحۡمَٰنِ ءَالِهَةٗ يُعۡبَدُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сиздан илгари юборган пайғамбарлардан сўраб (яъни, улар келтирган диний таълимотларни ўрганиб) боқинг-чи, Биз Раҳмондан (яъни, Ўзимиздан) ўзга ибодат қилинадиган «илоҳ»лар қилганмиканмиз?! (Йўқ, барча пайғамбарлар ёлғиз Аллоҳга ибодат қилишга даъват этгандирлар)'}, {'chapter': 43, 'verse': 46, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَقَالَ إِنِّي رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ\nАниқки, Биз Мусони Ўз оят-мўъжизаларимиз билан Фиръавн ва унинг одамларига юбордик. Бас, у: «Албатта мен барча оламлар Парвардигорининг пайғамбаридирман», деди'}, {'chapter': 43, 'verse': 47, 'text': 'فَلَمَّا جَآءَهُم بِـَٔايَٰتِنَآ إِذَا هُم مِّنۡهَا يَضۡحَكُونَ\nЭнди қачонки у Бизнинг оят-мўъжизаларимизни уларга келтиргач, баногоҳ улар у (мўъжизалар устидан) кула бошладилар'}, {'chapter': 43, 'verse': 48, 'text': 'وَمَا نُرِيهِم مِّنۡ ءَايَةٍ إِلَّا هِيَ أَكۡبَرُ مِنۡ أُخۡتِهَاۖ وَأَخَذۡنَٰهُم بِٱلۡعَذَابِ لَعَلَّهُمۡ يَرۡجِعُونَ\nБиз уларга (тўфон, чигиртка балолари каби азоб оятларидан) бирон оят кўрсатсак, албатта у (ўзидан аввалги) шеригидан улуғроқ-даҳшатлироқ оят бўлур эди. Шояд (куфрдан иймонга) қайтсалар, деб Биз уларни (турли) азоб билан ушладик'}, {'chapter': 43, 'verse': 49, 'text': 'وَقَالُواْ يَـٰٓأَيُّهَ ٱلسَّاحِرُ ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهۡتَدُونَ\nУлар (Бизнинг оятларимизни кўришгач, Мусога): «Эй сеҳргар, сенга берган (дуойингни мустажоб қилиши хусусидаги) ваъдаси ҳаққи-ҳурмати Парвардигорингга бизлар учун дуо қил. (Агар У сенинг дуойинг сабабли бизлардан бу азобларни аритса) албатта бизлар ҳидоят топгувчидирмиз (яъни, ўша Аллоҳга иймон келтиргувчидирмиз)», дедилар'}, {'chapter': 43, 'verse': 50, 'text': 'فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِذَا هُمۡ يَنكُثُونَ\nЭнди қачонки Биз (Мусонинг дуоси шарофати билан) улардан азобни аритсак, баногоҳ улар (ўз аҳдларини) буза бошладилар'}, {'chapter': 43, 'verse': 51, 'text': 'وَنَادَىٰ فِرۡعَوۡنُ فِي قَوۡمِهِۦ قَالَ يَٰقَوۡمِ أَلَيۡسَ لِي مُلۡكُ مِصۡرَ وَهَٰذِهِ ٱلۡأَنۡهَٰرُ تَجۡرِي مِن تَحۡتِيٓۚ أَفَلَا تُبۡصِرُونَ\nВа Фиръавн ўз қавмига жар солиб, деди: «Эй қавмим, Миср подшоҳлиги ва мана бу остимдан оқиб турган дарёлар менинг (мулким) эмасми?! Ахир кўрмаяпсизларми'}, {'chapter': 43, 'verse': 52, 'text': 'أَمۡ أَنَا۠ خَيۡرٞ مِّنۡ هَٰذَا ٱلَّذِي هُوَ مَهِينٞ وَلَا يَكَادُ يُبِينُ\nБалки мен мана бу хор бўлган ва (ўз мақсадини) очиқ баён қила олмайдиган кимсадан (яъни, Мусодан) яхшироқдирман'}, {'chapter': 43, 'verse': 53, 'text': 'فَلَوۡلَآ أُلۡقِيَ عَلَيۡهِ أَسۡوِرَةٞ مِّن ذَهَبٍ أَوۡ جَآءَ مَعَهُ ٱلۡمَلَـٰٓئِكَةُ مُقۡتَرِنِينَ\nБас, унга (Мусога осмондан худди подшоҳларнинг бўйнига гулчамбар ташланганидек) олтиндан бўлган билагузуклар ташланганида эди ёки у билан бирга фаришталар ҳамроҳ бўлиб келганида эди (унинг пайғамбарлигига ишонсанглар бўлар эди. Ундоқ бўлмагач, нега унга иймон келтирасизлар)?!»'}, {'chapter': 43, 'verse': 54, 'text': 'فَٱسۡتَخَفَّ قَوۡمَهُۥ فَأَطَاعُوهُۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\nБас, у ўз қавмини тезлатган эди, улар унга итоат этдилар. Дарҳақиқат, улар фосиқ қавм эдилар'}, {'chapter': 43, 'verse': 55, 'text': 'فَلَمَّآ ءَاسَفُونَا ٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ أَجۡمَعِينَ\nЭнди қачонки улар Бизни дарғазаб қилишгач, Биз улардан интиқом олдик — уларнинг барчаларини (денгизга) ғарқ қилиб юбордик'}, {'chapter': 43, 'verse': 56, 'text': 'فَجَعَلۡنَٰهُمۡ سَلَفٗا وَمَثَلٗا لِّلۡأٓخِرِينَ\nБас, уларни кейингилар учун ўтмиш-ибрат ва мисол қилиб қўйдик'}, {'chapter': 43, 'verse': 57, 'text': '۞وَلَمَّا ضُرِبَ ٱبۡنُ مَرۡيَمَ مَثَلًا إِذَا قَوۡمُكَ مِنۡهُ يَصِدُّونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), качонки (Ийсо) бинни Марям мисол келтирилса, баногоҳ қавмингиз (бу мисолдан шодланиб) қичқира бошлайдилар'}, {'chapter': 43, 'verse': 58, 'text': 'وَقَالُوٓاْ ءَأَٰلِهَتُنَا خَيۡرٌ أَمۡ هُوَۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلَۢاۚ بَلۡ هُمۡ قَوۡمٌ خَصِمُونَ\nУлар: «Бизларнинг илоҳларимиз яхшироқми ёки уми (яъни, Ийсоми?!) дедилар. Улар (бу мисолни) сизга фақат талашиб-тортишиш учунгина келтирдилар. Ахир улар хусуматчи-урушқоқ қавмдирлар'}, {'chapter': 43, 'verse': 59, 'text': 'إِنۡ هُوَ إِلَّا عَبۡدٌ أَنۡعَمۡنَا عَلَيۡهِ وَجَعَلۡنَٰهُ مَثَلٗا لِّبَنِيٓ إِسۡرَـٰٓءِيلَ\nУ фақатгина Биз (пайғамбарлик) инъом этган бир бандадир. Биз уни (отасиз дунёга келтириш билан) Бани Исроил учун (Бизнинг қудратимизни намойиш қиладиган) бир мисол-ибрат қилдик'}, {'chapter': 43, 'verse': 60, 'text': 'وَلَوۡ نَشَآءُ لَجَعَلۡنَا مِنكُم مَّلَـٰٓئِكَةٗ فِي ٱلۡأَرۡضِ يَخۡلُفُونَ\nАгар хоҳлаганимизда, албатта сизларнинг ўрнингизга Ерда халифа бўладиган фаришталарни (пайдо) қилган бўлар эдик'}, {'chapter': 43, 'verse': 61, 'text': 'وَإِنَّهُۥ لَعِلۡمٞ لِّلسَّاعَةِ فَلَا تَمۡتَرُنَّ بِهَا وَٱتَّبِعُونِۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nАлбатта (Ийсо Қиёмат) соати ҳақидаги билим-аломатдир. Бас, сизлар у (соат) ҳақида ҳаргиз шак-шубҳа қилмангиз! (Эй Муҳаммад алайҳис-салоту вас-салом, Макка аҳлига айтинг): «Менга эргашинглар! Мана шу Тўғри Йўлдир»'}, {'chapter': 43, 'verse': 62, 'text': 'وَلَا يَصُدَّنَّكُمُ ٱلشَّيۡطَٰنُۖ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ\nСизларни ҳаргиз шайтон (Аллоҳнинг динидан) тўсмасин. Албатта у сизлар учун очиқ душмандир'}, {'chapter': 43, 'verse': 63, 'text': 'وَلَمَّا جَآءَ عِيسَىٰ بِٱلۡبَيِّنَٰتِ قَالَ قَدۡ جِئۡتُكُم بِٱلۡحِكۡمَةِ وَلِأُبَيِّنَ لَكُم بَعۡضَ ٱلَّذِي تَخۡتَلِفُونَ فِيهِۖ فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ\nҚачонки Ийсо аниқ-равшан ҳужжат-мўъжизаларни келтиргач, деди: «Мана мен сизларга Ҳикмат — Инжил билан, сизларга ўзларингиз ихтилоф қилаётган айрим нарсаларни баён қилиб бериш учун келдим. Бас, Аллоҳдан қўрқингиз ва менга итоат этингиз'}, {'chapter': 43, 'verse': 64, 'text': 'إِنَّ ٱللَّهَ هُوَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ\nАлбатта Аллоҳнинг Ўзи Парвардигорим ва Парвардигорингиздир, бас, (ёлғиз) Унга ибодат қилингиз! Мана шу Тўғри Йўлдир»'}, {'chapter': 43, 'verse': 65, 'text': 'فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَيۡنِهِمۡۖ فَوَيۡلٞ لِّلَّذِينَ ظَلَمُواْ مِنۡ عَذَابِ يَوۡمٍ أَلِيمٍ\nСўнг (Насронийларнинг ичидан чиққан) фирқалар (Ийсо хусусида, айримлари уни «худо», деб, айримлари «Худонинг ўғли, деб) ўзаро ихтилоф қилдилар. Бас, у золим кимсаларга аламли Кун (Қиёмат)нинг азоби — ҳалокат бўлгай'}, {'chapter': 43, 'verse': 66, 'text': 'هَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ\nУлар фақат ўзлари ҳам сезмаган ҳолларида (Қиёмат) соати тўсатдан келиб қолишини кутадилар, холос'}, {'chapter': 43, 'verse': 67, 'text': 'ٱلۡأَخِلَّآءُ يَوۡمَئِذِۭ بَعۡضُهُمۡ لِبَعۡضٍ عَدُوٌّ إِلَّا ٱلۡمُتَّقِينَ\nУ Кунда дўстлар бир-бирларига душмандир, магар (Аллоҳ йўлида дўстлашган) тақводор зотларгина (мангу дўстдирлар)'}, {'chapter': 43, 'verse': 68, 'text': 'يَٰعِبَادِ لَا خَوۡفٌ عَلَيۡكُمُ ٱلۡيَوۡمَ وَلَآ أَنتُمۡ تَحۡزَنُونَ\n(У Кунда уларга айтилур): «Эй бандаларим, бу Кун сизлар учун ҳеч хавфу-хатар йўқдир ва сизлар асло ғамгин бўлмайсизлар'}, {'chapter': 43, 'verse': 69, 'text': 'ٱلَّذِينَ ءَامَنُواْ بِـَٔايَٰتِنَا وَكَانُواْ مُسۡلِمِينَ\nУлар Бизнинг оятларимизга иймон келтирган ва мусулмон бўлиб ўтган зотлардир'}, {'chapter': 43, 'verse': 70, 'text': 'ٱدۡخُلُواْ ٱلۡجَنَّةَ أَنتُمۡ وَأَزۡوَٰجُكُمۡ تُحۡبَرُونَ\nСизлар жуфти ҳалолларингиз билан бирга шод-хуррам бўлган ҳолларингизда жаннатга кирингиз!»'}, {'chapter': 43, 'verse': 71, 'text': 'يُطَافُ عَلَيۡهِم بِصِحَافٖ مِّن ذَهَبٖ وَأَكۡوَابٖۖ وَفِيهَا مَا تَشۡتَهِيهِ ٱلۡأَنفُسُ وَتَلَذُّ ٱلۡأَعۡيُنُۖ وَأَنتُمۡ فِيهَا خَٰلِدُونَ\nУларга олтиндан бўлган лаганлар(да таомлар) ва қадаҳлар(да шароблар) айлантирилур. У жойда кўнгиллар тилайдиган ва кўзлар лаззатланадиган (барча) нарса бордир. Сизлар у жойда мангу қолурсизлар'}, {'chapter': 43, 'verse': 72, 'text': 'وَتِلۡكَ ٱلۡجَنَّةُ ٱلَّتِيٓ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ\nҚилиб ўтган амалларингиз сабабли сизларга мерос қилиб берилган жаннат мана шудир'}, {'chapter': 43, 'verse': 73, 'text': 'لَكُمۡ فِيهَا فَٰكِهَةٞ كَثِيرَةٞ مِّنۡهَا تَأۡكُلُونَ\nСизлар учун у жойда кўплаб мева-чевалар бўлиб, сизлар улардан ерсизлар'}, {'chapter': 43, 'verse': 74, 'text': 'إِنَّ ٱلۡمُجۡرِمِينَ فِي عَذَابِ جَهَنَّمَ خَٰلِدُونَ\nАлбатта жиноятчи кимсалар (яъни, кофирлар) жаҳаннам азобида мангу қолгувчидирлар'}, {'chapter': 43, 'verse': 75, 'text': 'لَا يُفَتَّرُ عَنۡهُمۡ وَهُمۡ فِيهِ مُبۡلِسُونَ\nУлардан (азоб бирон лаҳза ҳам) енгиллатилмас ва улар (ўша азобда қолиб) бутунлай номурод бўлгувчидирлар'}, {'chapter': 43, 'verse': 76, 'text': 'وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُواْ هُمُ ٱلظَّـٰلِمِينَ\nУларга Биз зулм қилмадик, лекин улар (ўзларига) зулм қилгувчи бўлдилар'}, {'chapter': 43, 'verse': 77, 'text': 'وَنَادَوۡاْ يَٰمَٰلِكُ لِيَقۡضِ عَلَيۡنَا رَبُّكَۖ قَالَ إِنَّكُم مَّـٰكِثُونَ\nУлар (дўзах ходими бўлган фариштага) «Эй Молик, Парвардигоринг бизларга ҳукмини қилсин (яъни, тезроқ жонимизни олсин, бизлар бу азобдан қутулайлик», деб) нидо қилганларида, у «Албатта сизлар (мана шу азобда мангу) тургувчидирсизлар», деди'}, {'chapter': 43, 'verse': 78, 'text': 'لَقَدۡ جِئۡنَٰكُم بِٱلۡحَقِّ وَلَٰكِنَّ أَكۡثَرَكُمۡ لِلۡحَقِّ كَٰرِهُونَ\nАниқки, Биз сизларга Ҳақ — Қуръонни келтирганмиз, лекин сизларнинг кўпларингиз Ҳақни ёмон кўргувчидирсизлар'}, {'chapter': 43, 'verse': 79, 'text': 'أَمۡ أَبۡرَمُوٓاْ أَمۡرٗا فَإِنَّا مُبۡرِمُونَ\nБалки улар (яъни, Макка мушриклари Пайғамбар алайҳис-салоту вас-саломга суиқасд қилиш учун) бирон ишга қарор қилгандирлар?! Бас, албатта Биз ҳам (Ўз пайғамбаримизга уларнинг макрларидан нажот бериб, у суиқасд қилгувчиларнинг ўзларини ҳалок этишга) қарор қилгувчидирмиз'}, {'chapter': 43, 'verse': 80, 'text': 'أَمۡ يَحۡسَبُونَ أَنَّا لَا نَسۡمَعُ سِرَّهُمۡ وَنَجۡوَىٰهُمۚ بَلَىٰ وَرُسُلُنَا لَدَيۡهِمۡ يَكۡتُبُونَ\nБалки уларнинг гумонларича, Биз уларнинг сирларини (билмасмиз) ва шивир-шивирларини эшитмасмиз?! Ундоқ эмас! Бизнинг элчиларимиз (фаришталар) уларнинг олдиларида (ҳар бир қилган ишларини) ёзиб турурлар'}, {'chapter': 43, 'verse': 81, 'text': 'قُلۡ إِن كَانَ لِلرَّحۡمَٰنِ وَلَدٞ فَأَنَا۠ أَوَّلُ ٱلۡعَٰبِدِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, «Аллоҳнинг боласи бор», дейдиган мушрикларга) айтинг: «Агар Раҳмоннинг боласи бўлганида мен (у болага) ибодат қилгувчиларнинг биринчиси бўлур эдим, (лекин У зотнинг боласи ҳам, оиласи ҳам йўқдир)!»'}, {'chapter': 43, 'verse': 82, 'text': 'سُبۡحَٰنَ رَبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ رَبِّ ٱلۡعَرۡشِ عَمَّا يَصِفُونَ\nОсмонлар ва Ернинг Парвардигори, Арш Эгаси (бўлган Аллоҳ) улар сифатлаётган айблардан покдир'}, {'chapter': 43, 'verse': 83, 'text': 'فَذَرۡهُمۡ يَخُوضُواْ وَيَلۡعَبُواْ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي يُوعَدُونَ\nБас, сиз уларни тарк қилинг! Улар то ўзларига ваъда қилинган Кунга рўбарў бўлгунларича (ноҳақ йўлларига) шўнғишиб, (ўз дунёларида) ўйнаб-кулиб юраверсинлар'}, {'chapter': 43, 'verse': 84, 'text': 'وَهُوَ ٱلَّذِي فِي ٱلسَّمَآءِ إِلَٰهٞ وَفِي ٱلۡأَرۡضِ إِلَٰهٞۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡعَلِيمُ\nУ (Аллоҳ) осмонда ҳам илоҳ, Ерда ҳам илоҳ бўлган Зотдир. У ҳикмат Эгаси ва билгувчидир'}, {'chapter': 43, 'verse': 85, 'text': 'وَتَبَارَكَ ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَعِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَإِلَيۡهِ تُرۡجَعُونَ\nОсмонлар ва Ер ҳамда уларнинг ўртасидаги бор нарсалар подшоҳлиги Ўзиники бўлган Зот баракотли — буюкдир. (Қиёмат) соати ҳақидаги билим ҳам ёлғиз Унинг ҳузуридадир. Ёлғиз Унгагина қайтарилурсизлар'}, {'chapter': 43, 'verse': 86, 'text': 'وَلَا يَمۡلِكُ ٱلَّذِينَ يَدۡعُونَ مِن دُونِهِ ٱلشَّفَٰعَةَ إِلَّا مَن شَهِدَ بِٱلۡحَقِّ وَهُمۡ يَعۡلَمُونَ\n(Мушриклар Аллоҳни) қўйиб дуо илтижо қилаётган бутлари (Қиёмат Кунида бирон кишини) шафоат қилишга эга бўла олмас, магар ўзлари билган ҳолларида Ҳақ гувоҳлик берган (яъни, «Ҳеч қандай илоҳ йўқ, магар ёлғиз Аллоҳгина бор эканлигига гувоҳлик бераман», деган) кишиларгина (Аллоҳнинг изни иродаси билан шафоат қилишга қодир бўлурлар)'}, {'chapter': 43, 'verse': 87, 'text': 'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَهُمۡ لَيَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ يُؤۡفَكُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қасамки, агар сиз улардан ўзларини ким яратгани ҳақида сўрасангиз, албатта улар: «Аллоҳ», дерлар. Бас, (Ўша яратгувчига ибодат қилиш ўрнига) қаёққа бурилиб кетмоқдалар'}, {'chapter': 43, 'verse': 88, 'text': 'وَقِيلِهِۦ يَٰرَبِّ إِنَّ هَـٰٓؤُلَآءِ قَوۡمٞ لَّا يُؤۡمِنُونَ\n(Аллоҳ таоло Муҳаммад алайҳис-салоту вас-саломнинг): «Парвардигорим, дарҳақиқат, ана улар иймон келтирмайдиган қавмдир», деган (сўзларини ҳам билур)'}, {'chapter': 43, 'verse': 89, 'text': 'فَٱصۡفَحۡ عَنۡهُمۡ وَقُلۡ سَلَٰمٞۚ فَسَوۡفَ يَعۡلَمُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз улардан юз ўгиринг ва «Тинчлик-омонлик бўлсин», денг! Бас, улар яқинда (куфру исёнларининг оқибати қандай бўлишини) билиб олажаклар'}, {'chapter': 44, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 44, 'verse': 2, 'text': 'وَٱلۡكِتَٰبِ ٱلۡمُبِينِ\nОчиқ-равшан Китобга — Қуръонга қасамки'}, {'chapter': 44, 'verse': 3, 'text': 'إِنَّآ أَنزَلۡنَٰهُ فِي لَيۡلَةٖ مُّبَٰرَكَةٍۚ إِنَّا كُنَّا مُنذِرِينَ\nАлбатта Биз уни бир муборак-баракотли кечада нозил қилдик. Дарҳақиқат, Биз (инсонларни ушбу Қуръон билан Охират азобидан) огоҳлантиргувчи бўлдик'}, {'chapter': 44, 'verse': 4, 'text': 'فِيهَا يُفۡرَقُ كُلُّ أَمۡرٍ حَكِيمٍ\nУ (кеча)да барча пухта-аниқ иш(лар) айрилур (белгиланур)'}, {'chapter': 44, 'verse': 5, 'text': 'أَمۡرٗا مِّنۡ عِندِنَآۚ إِنَّا كُنَّا مُرۡسِلِينَ\n(Бу кечада тақдир қилинадиган барча ишлар) Бизнинг ҳузуримиздан бўлган Ишдир. Дарҳақиқат, Биз (башариятга пайғамбарлар) юборгувчи бўлдикки'}, {'chapter': 44, 'verse': 6, 'text': 'رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ\n(Бу) Парвардигорингиз томонидан бўлган раҳмат-меҳрибонликдир. Албатта Унинг Ўзигина (барча нарсани) эшитгувчи, билгувчидир'}, {'chapter': 44, 'verse': 7, 'text': 'رَبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُم مُّوقِنِينَ\nАгар ҳақиқий ишонгувчи бўлсангизлар, (ўша сизларнинг Парвардигорингиз) осмонлар ва Ернинг ҳамда уларнинг ўртасидаги бор нарсаларнинг ҳам Парвардигоридир'}, {'chapter': 44, 'verse': 8, 'text': 'لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ رَبُّكُمۡ وَرَبُّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ\nҲеч қандай илоҳ йўқ, магар Унинг Ўзи бордир. Ҳаёт ва ўлим берадиган ҳам Унинг Ўзидир. Сизларнинг Парвардигорингиз ҳам, аввалги ота-боболарингизнинг Парвардигори ҳам (Унинг Ўзидир)'}, {'chapter': 44, 'verse': 9, 'text': 'بَلۡ هُمۡ فِي شَكّٖ يَلۡعَبُونَ\nЙўқ, улар (яъни, мушриклар қайта тирилиш хусусида) шак-шубҳада бўлиб, (Аллоҳнинг пайғамбарларининг устидан масхара қилиб) ўйнарлар'}, {'chapter': 44, 'verse': 10, 'text': 'فَٱرۡتَقِبۡ يَوۡمَ تَأۡتِي ٱلسَّمَآءُ بِدُخَانٖ مُّبِينٖ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз осмон очиқ (яъни, барчага кўринадиган) тутунни келтирадиган Кунга кўз тутинг'}, {'chapter': 44, 'verse': 11, 'text': 'يَغۡشَى ٱلنَّاسَۖ هَٰذَا عَذَابٌ أَلِيمٞ\nУ (тутун барча) одамларни ўраб олур. Бу аламли азобдир'}, {'chapter': 44, 'verse': 12, 'text': 'رَّبَّنَا ٱكۡشِفۡ عَنَّا ٱلۡعَذَابَ إِنَّا مُؤۡمِنُونَ\n«Парвардигор, Ўзинг бизлардан бу азобни аритгин. Албатта бизлар иймон келтиргувчидирмиз»'}, {'chapter': 44, 'verse': 13, 'text': 'أَنَّىٰ لَهُمُ ٱلذِّكۡرَىٰ وَقَدۡ جَآءَهُمۡ رَسُولٞ مُّبِينٞ\n(Лекин) улар учун қаёқдан ҳам (бошларига тушган бу бало-офатлардан) эслатма-ибрат олиш бўлсин?! Ҳолбуки, уларга очиқ пайғамбар (яъни, Муҳаммад алайҳис-салоту вас-салом) келди'}, {'chapter': 44, 'verse': 14, 'text': 'ثُمَّ تَوَلَّوۡاْ عَنۡهُ وَقَالُواْ مُعَلَّمٞ مَّجۡنُونٌ\nсўнгра ундан юз ўгирдилар ва: «(Унга Қуръон бировлар томонидан) ўргатиб қўйилган, (у) мажнун», дедилар-ку?! (Бас, очиқ-равшан мўъжиза — Қуръонни келтирган пайғамбарга иймон келтирмай, ундан юз ўгирган кимсалар бошқа бирон нарсадан панд-насиҳат олармидилар)'}, {'chapter': 44, 'verse': 15, 'text': 'إِنَّا كَاشِفُواْ ٱلۡعَذَابِ قَلِيلًاۚ إِنَّكُمۡ عَآئِدُونَ\nАлбатта Биз бу азобни (сизлардан) бир оз (вақтгача) аритгувчидирмиз, сизлар ҳам шак-шубҳасиз, (ўз куфру исёнларингизга) қайтувчидирсизлар'}, {'chapter': 44, 'verse': 16, 'text': 'يَوۡمَ نَبۡطِشُ ٱلۡبَطۡشَةَ ٱلۡكُبۡرَىٰٓ إِنَّا مُنتَقِمُونَ\nБиз даҳшатли ушлаш билан ушлайдиган кунда (яъни, Бадр жангида ёки Қиёмат Кунида Биз улардан интиқом олурмиз). Зеро, Биз интиқом олгувчидирмиз'}, {'chapter': 44, 'verse': 17, 'text': '۞وَلَقَدۡ فَتَنَّا قَبۡلَهُمۡ قَوۡمَ فِرۡعَوۡنَ وَجَآءَهُمۡ رَسُولٞ كَرِيمٌ\nАниқки, Биз улардан илгари Фиръавн қавмини ҳам имтиҳон қилганмиз ва уларга бир улуғ пайғамбар (яъни, Мусо алайҳиссалом) келгандир'}, {'chapter': 44, 'verse': 18, 'text': 'أَنۡ أَدُّوٓاْ إِلَيَّ عِبَادَ ٱللَّهِۖ إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ\n(У уларга деди): «Сизлар менга Аллоҳнинг бандаларини (яъни, Бани Исроил қавмини) топширинглар! Албатта мен сизлар учун ишончли пайғамбардирман»'}, {'chapter': 44, 'verse': 19, 'text': 'وَأَن لَّا تَعۡلُواْ عَلَى ٱللَّهِۖ إِنِّيٓ ءَاتِيكُم بِسُلۡطَٰنٖ مُّبِينٖ\n«Ва Аллоҳга кибру ҳаво қилманглар! Албатта мен сизларга (ўзимнинг ҳақ пайғамар эканлигимга) очиқ-равшан ҳужжат (мўъжизалар) келтирурман»'}, {'chapter': 44, 'verse': 20, 'text': 'وَإِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُمۡ أَن تَرۡجُمُونِ\n(Шунда Фиръавн ва унинг қавми Мусони ўлдириш билан таҳдид солганларида у деди): «Албатта мен Парвардигорим ва Парвардигорингиздан сизлар мени тошбўрон қилишларингиздан паноҳ сўраганман, (демак сизларнинг таҳдидларингиздан қўрқмайман)'}, {'chapter': 44, 'verse': 21, 'text': 'وَإِن لَّمۡ تُؤۡمِنُواْ لِي فَٱعۡتَزِلُونِ\nАгар менга иймон келтирмасангизлар, у ҳолда мени холи қўйинглар!»'}, {'chapter': 44, 'verse': 22, 'text': 'فَدَعَا رَبَّهُۥٓ أَنَّ هَـٰٓؤُلَآءِ قَوۡمٞ مُّجۡرِمُونَ\nБас, у Парвардигорига «Дарҳақиқат, улар жиноятчи қавмдир (яъни, Ўзинг уларнинг жазоларини бергин»,) деб дуо қилди'}, {'chapter': 44, 'verse': 23, 'text': 'فَأَسۡرِ بِعِبَادِي لَيۡلًا إِنَّكُم مُّتَّبَعُونَ\nБас, (Аллоҳ Мусога ваҳий юбордики,) «Бандаларим (яъни, Бани Исроил қавми) билан бирга тунда йўлга чиққин! Албатта, сизларнинг изингизга тушилур (яъни, Фиръавн ва унинг қўшини ортингиздан қувиб етур)'}, {'chapter': 44, 'verse': 24, 'text': 'وَٱتۡرُكِ ٱلۡبَحۡرَ رَهۡوًاۖ إِنَّهُمۡ جُندٞ مُّغۡرَقُونَ\nВа денгизни сокин (яъни, сизлар ўтаётганда иккига бўлинган эди, ана ўша ҳолида) қўявергин (ва Фиръавн ўз қўшини билан ундан ўтиб олишидан қўрқма). Шак-шубҳасиз, улар ғарқ қилингувчи қўшиндир»'}, {'chapter': 44, 'verse': 25, 'text': 'كَمۡ تَرَكُواْ مِن جَنَّـٰتٖ وَعُيُونٖ\nУлар қанчадан-қанча боғларни, чашмаларни'}, {'chapter': 44, 'verse': 26, 'text': 'وَزُرُوعٖ وَمَقَامٖ كَرِيمٖ\nэкинзорларни ва улуғ-гўзал жойларни қолдириб (ўзлари ғарқ бўлиб) кетдилар'}, {'chapter': 44, 'verse': 27, 'text': 'وَنَعۡمَةٖ كَانُواْ فِيهَا فَٰكِهِينَ\nВа (қанчадан-қанча) ўзлари вақтичоғлик қилган неъматларни (қолдириб кетдилар)'}, {'chapter': 44, 'verse': 28, 'text': 'كَذَٰلِكَۖ وَأَوۡرَثۡنَٰهَا قَوۡمًا ءَاخَرِينَ\nМана шундай! Биз у (неъматларни бошқа) бир қавмга мерос қилиб бердик'}, {'chapter': 44, 'verse': 29, 'text': 'فَمَا بَكَتۡ عَلَيۡهِمُ ٱلسَّمَآءُ وَٱلۡأَرۡضُ وَمَا كَانُواْ مُنظَرِينَ\nБас, уларга осмон ҳам, Ер ҳам (аза тутиб) йиғлагани йўқ ва уларга (тавба-тазарруъ учун) муҳлат ҳам берилмади'}, {'chapter': 44, 'verse': 30, 'text': 'وَلَقَدۡ نَجَّيۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ مِنَ ٱلۡعَذَابِ ٱلۡمُهِينِ\nДарҳақиқат, Биз Бани Исроилга хор қилгувчи азобдан нажот бердик'}, {'chapter': 44, 'verse': 31, 'text': 'مِن فِرۡعَوۡنَۚ إِنَّهُۥ كَانَ عَالِيٗا مِّنَ ٱلۡمُسۡرِفِينَ\n— Фиръавндан (нажот бердик). У ҳақиқатан, мутакаббир, ҳаддан ошувчилардан эди'}, {'chapter': 44, 'verse': 32, 'text': 'وَلَقَدِ ٱخۡتَرۡنَٰهُمۡ عَلَىٰ عِلۡمٍ عَلَى ٱلۡعَٰلَمِينَ\nДарҳақиқат, Биз уларни (Бани Исроил қавмини) билган ҳолимизда (ўз замонларидаги барча) оламлар — одамлардан танлаб олдик — афзал қилдик'}, {'chapter': 44, 'verse': 33, 'text': 'وَءَاتَيۡنَٰهُم مِّنَ ٱلۡأٓيَٰتِ مَا فِيهِ بَلَـٰٓؤٞاْ مُّبِينٌ\nВа уларга оят-мўъжизалардан (денгизнинг қоқ ўртасидан бўлиниши, осмондан ризқу рўз ёғилиши каби) нарсаларни ато этдикки, уларда (тафаккур қилгувчи учун) очиқ имтиҳон-синов бордир'}, {'chapter': 44, 'verse': 34, 'text': 'إِنَّ هَـٰٓؤُلَآءِ لَيَقُولُونَ\nАлбатта ана улар (яъни, Қурайш кофирлари дерларки)'}, {'chapter': 44, 'verse': 35, 'text': 'إِنۡ هِيَ إِلَّا مَوۡتَتُنَا ٱلۡأُولَىٰ وَمَا نَحۡنُ بِمُنشَرِينَ\n(«Ўлим) фақат биринчи ўлимимиздир ва бизлар қайта тирилгувчи эмасмиз'}, {'chapter': 44, 'verse': 36, 'text': 'فَأۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ\nБас, агар («Охират ҳаёти ҳам бор», деган сўзларингизда) ростгўй бўлсангизлар, (ўлиб кетган) ота-боболаримизни (қайта тирилтириб) келтиринглар-чи?», дерлар'}, {'chapter': 44, 'verse': 37, 'text': 'أَهُمۡ خَيۡرٌ أَمۡ قَوۡمُ تُبَّعٖ وَٱلَّذِينَ مِن قَبۡلِهِمۡ أَهۡلَكۡنَٰهُمۡۚ إِنَّهُمۡ كَانُواْ مُجۡرِمِينَ\nУлар (куч-қувватда) яхшироқми ёки Туббаъ қавми ва улардан аввалги кимсаларми?! Биз уларни ҳалок қилдик. Чунки улар жиноятчи — осий эдилар'}, {'chapter': 44, 'verse': 38, 'text': 'وَمَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا لَٰعِبِينَ\nБиз осмонлар ва Ерни ҳамда уларнинг орасидаги нарсаларни ўйнаб-беҳуда яратганимиз йўқ'}, {'chapter': 44, 'verse': 39, 'text': 'مَا خَلَقۡنَٰهُمَآ إِلَّا بِٱلۡحَقِّ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nБиз уларни фақат Ҳақ (қонун, интизом) билан яратганмиз. Лекин уларнинг кўплари билмаслар'}, {'chapter': 44, 'verse': 40, 'text': 'إِنَّ يَوۡمَ ٱلۡفَصۡلِ مِيقَٰتُهُمۡ أَجۡمَعِينَ\n(Ҳақ билан ботилнинг) ажралиш Куни (яъни, Қиёмат Куни) барчаларининг (инсонларнинг) ҳисоб-китоб вақтидир'}, {'chapter': 44, 'verse': 41, 'text': 'يَوۡمَ لَا يُغۡنِي مَوۡلًى عَن مَّوۡلٗى شَيۡـٔٗا وَلَا هُمۡ يُنصَرُونَ\nУ Кунда дўст дўстга бирон нарсада асқотмас ва уларга ёрдам ҳам берилмас'}, {'chapter': 44, 'verse': 42, 'text': 'إِلَّا مَن رَّحِمَ ٱللَّهُۚ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ\nМагар Аллоҳ раҳмат қилган кишиларгагина (ёрдам берилур). Албатта Унинг Ўзигина қудратли ва меҳрибондир'}, {'chapter': 44, 'verse': 43, 'text': 'إِنَّ شَجَرَتَ ٱلزَّقُّومِ\nАлбатта (дўзахнинг ўртасида ўсадиган) Заққум дарахти'}, {'chapter': 44, 'verse': 44, 'text': 'طَعَامُ ٱلۡأَثِيمِ\nгуноҳкорнинг таомидир'}, {'chapter': 44, 'verse': 45, 'text': 'كَٱلۡمُهۡلِ يَغۡلِي فِي ٱلۡبُطُونِ\n(У таом) мисоли қоринларда қайнайдиган эритилган (доғланган) ёғдир'}, {'chapter': 44, 'verse': 46, 'text': 'كَغَلۡيِ ٱلۡحَمِيمِ\nҚайноқ сувнинг қайнаши каби'}, {'chapter': 44, 'verse': 47, 'text': 'خُذُوهُ فَٱعۡتِلُوهُ إِلَىٰ سَوَآءِ ٱلۡجَحِيمِ\n(Дўзах фаришталарига): «Уни (гуноҳкорни) ушлаб дўзахнинг ўртасига судраб олиб боринглар'}, {'chapter': 44, 'verse': 48, 'text': 'ثُمَّ صُبُّواْ فَوۡقَ رَأۡسِهِۦ مِنۡ عَذَابِ ٱلۡحَمِيمِ\nсўнгра боши устидан қайноқ сувдан — азобдан қуйинглар!», (деб амр этилур)'}, {'chapter': 44, 'verse': 49, 'text': 'ذُقۡ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡكَرِيمُ\n(Унга масхара қилиш учун: «Мана бул азобни) тотиб кўргин! Дарҳақиқат, сенинг ўзинггина «қудратли ва улуғдирсан»'}, {'chapter': 44, 'verse': 50, 'text': 'إِنَّ هَٰذَا مَا كُنتُم بِهِۦ تَمۡتَرُونَ\nДарвоқеъ, бу (азоб) сизлар (ҳаёти дунёдалик пайтингизда) шак-шубҳа қилган нарсангиздир!», (дейилур)'}, {'chapter': 44, 'verse': 51, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي مَقَامٍ أَمِينٖ\nАлбатта тақводор зотлар (у Кунда) осойишта жойда'}, {'chapter': 44, 'verse': 52, 'text': 'فِي جَنَّـٰتٖ وَعُيُونٖ\nбоғлар ва чашмалар устида бўлурлар'}, {'chapter': 44, 'verse': 53, 'text': 'يَلۡبَسُونَ مِن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَقَٰبِلِينَ\nУлар бир-бирларига рўбарў бўлган ҳолларида ипак-шойидан либослар кийиб (ўлтирурлар)'}, {'chapter': 44, 'verse': 54, 'text': 'كَذَٰلِكَ وَزَوَّجۡنَٰهُم بِحُورٍ عِينٖ\nМана шундай! Яна Биз уларга оҳукўз ҳурларни жуфти ҳалол қилиб қўйгандирмиз'}, {'chapter': 44, 'verse': 55, 'text': 'يَدۡعُونَ فِيهَا بِكُلِّ فَٰكِهَةٍ ءَامِنِينَ\nУлар у жойда тинч-хотиржам бўлган ҳолларида (хизматкорлардан) барча мева-чевани чақирурлар'}, {'chapter': 44, 'verse': 56, 'text': 'لَا يَذُوقُونَ فِيهَا ٱلۡمَوۡتَ إِلَّا ٱلۡمَوۡتَةَ ٱلۡأُولَىٰۖ وَوَقَىٰهُمۡ عَذَابَ ٱلۡجَحِيمِ\nУлар (жаннатда) биринчи ўлимдан (яъни, ҳаёти дунёдан кўз юмганларидан) бошқа ўлимни тотмаслар. (Аллоҳ) уларни дўзах азобидан сақлагандир'}, {'chapter': 44, 'verse': 57, 'text': 'فَضۡلٗا مِّن رَّبِّكَۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\n(Бу) Парвардигорингиз томонидан бўлган фазлу марҳаматдир. Мана шу улуғ бахтдир'}, {'chapter': 44, 'verse': 58, 'text': 'فَإِنَّمَا يَسَّرۡنَٰهُ بِلِسَانِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, шояд улар эслатма-ибрат олсалар, деб Биз (бу Қуръонни) сизнинг тилингиз билан осон-енгил қилиб бердик. (Лекин улар Қуръон панд-насиҳатларини қабул қилмадилар)'}, {'chapter': 44, 'verse': 59, 'text': 'فَٱرۡتَقِبۡ إِنَّهُم مُّرۡتَقِبُونَ\nБас, энди сиз (уларнинг ҳоллари не кечишига) кўз тутинг! Улар ҳам албатта (сизнинг ҳалокатингизга) кўз тутгувчидирлар'}, {'chapter': 45, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 45, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\n(Бу Қуръон) қудратли ва ҳикматли Аллоҳ томонидан нозил қилинган Китобдир'}, {'chapter': 45, 'verse': 3, 'text': 'إِنَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّلۡمُؤۡمِنِينَ\nШак-шубҳасиз, осмонлар ва Ерда мўминлар учун (Аллоҳнинг қудрат ва ҳикматига далолат қиладиган) оят-аломатлар бордир'}, {'chapter': 45, 'verse': 4, 'text': 'وَفِي خَلۡقِكُمۡ وَمَا يَبُثُّ مِن دَآبَّةٍ ءَايَٰتٞ لِّقَوۡمٖ يُوقِنُونَ\nЯна сизларнинг (бир томчи сув-нутфадан) яралишингизда ва (Аллоҳ Ер юзига) тарқатиб юборган жониворлардан иборат нарсаларда ҳам аниқ ишонадиган қавм учун оят-ибратлар бордир'}, {'chapter': 45, 'verse': 5, 'text': 'وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن رِّزۡقٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَتَصۡرِيفِ ٱلرِّيَٰحِ ءَايَٰتٞ لِّقَوۡمٖ يَعۡقِلُونَ\nЯна кеча ва кундузнинг алмашиб туришида ва Аллоҳ осмондан ёғдириб, унинг ёрдамида «ўлган» Ерни тирилтирган ризқ-ёмғирда ҳамда шамолларнинг йўналтирилишида ақл юргизадиган қавм учун оят-ибратлар бордир'}, {'chapter': 45, 'verse': 6, 'text': 'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَ ٱللَّهِ وَءَايَٰتِهِۦ يُؤۡمِنُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), булар Аллоҳнинг оятлари бўлиб, Биз уларни сизга ҳаққи-рост тиловат қилиб бермоқдамиз. Бас, улар (Макка кофирлари) Аллоҳ ва Унинг оятларини қўйиб қайси сўзга иймон келтирурлар'}, {'chapter': 45, 'verse': 7, 'text': 'وَيۡلٞ لِّكُلِّ أَفَّاكٍ أَثِيمٖ\nБарча бўҳтончи-ёлғончи гуноҳкорга ҳалокат бўлғайки'}, {'chapter': 45, 'verse': 8, 'text': 'يَسۡمَعُ ءَايَٰتِ ٱللَّهِ تُتۡلَىٰ عَلَيۡهِ ثُمَّ يُصِرُّ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٖ\nУ ўзига тиловат қилинаётган Аллоҳ оятларини эшитар, сўнгра гўё уларни эшитмагандек, кибру ҳаво қилган ҳолда (ўз куфрида) оёқ тираб турар! Бас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз унга аламли азоб хушхабарини беринг'}, {'chapter': 45, 'verse': 9, 'text': 'وَإِذَا عَلِمَ مِنۡ ءَايَٰتِنَا شَيۡـًٔا ٱتَّخَذَهَا هُزُوًاۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ\nҚачон Бизнинг оятларимиздан бирон нарса билиб олса, у (оят)ларни масхара қилур! Ана ўшалар учун хор қилгувчи азоб бордир'}, {'chapter': 45, 'verse': 10, 'text': 'مِّن وَرَآئِهِمۡ جَهَنَّمُۖ وَلَا يُغۡنِي عَنۡهُم مَّا كَسَبُواْ شَيۡـٔٗا وَلَا مَا ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ\nУларнинг ортида жаҳаннам бор! Касб қилган-топган (мол-дунё, бола-чақалари) ҳам, Аллоҳни қўйиб ушлаган «дўстлари» ҳам уларга бирон нарсада асқотмас! Улар учун улуғ — катта азоб бордир'}, {'chapter': 45, 'verse': 11, 'text': 'هَٰذَا هُدٗىۖ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٌ\nУшбу (Қуръон иймон келтирган зотлар учун) ҳидоятдир. Парвардигорларининг оятларига кофир бўлган кимсалар учун эса ашаддий азобдан аламли насиба бордир'}, {'chapter': 45, 'verse': 12, 'text': '۞ٱللَّهُ ٱلَّذِي سَخَّرَ لَكُمُ ٱلۡبَحۡرَ لِتَجۡرِيَ ٱلۡفُلۡكُ فِيهِ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ\nАллоҳ (денгиздаги) кемалар Ўзининг амри билан жорий бўлиши ҳамда сизлар (у кемаларда) Унинг фазлу марҳаматидан (ризқу рўз) исташларингиз учун ва шукр қилишларингиз учун сизларга денгизни бўйинсундириб қўйган Зотдир'}, {'chapter': 45, 'verse': 13, 'text': 'وَسَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ جَمِيعٗا مِّنۡهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ\nУ Ўз томонидан (яъни, Ўз хоҳиш-иродаси билан) сизларга осмонлардаги ва Ердаги барча нарсаларни бўйинсундирди. Албатта бунда тафаккур қиладиган қавм учун оят-ибратлар бордир'}, {'chapter': 45, 'verse': 14, 'text': 'قُل لِّلَّذِينَ ءَامَنُواْ يَغۡفِرُواْ لِلَّذِينَ لَا يَرۡجُونَ أَيَّامَ ٱللَّهِ لِيَجۡزِيَ قَوۡمَۢا بِمَا كَانُواْ يَكۡسِبُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), иймон келтирган зотларга айтинг, улар Аллоҳ (ваъда қилган ҳисоб-китоб) кунларидан ноумид бўлган кимсалар етказаётган озор-азиятлар)ни кечириб юбораверсинлар. Токи (Аллоҳ ўша озорларга сабр-тоқат қилган) қавмни касб қилган нарсалари (яъни, кечириб юборганликлари) сабабли мукофотлагай'}, {'chapter': 45, 'verse': 15, 'text': 'مَنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۖ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ\nКим бирон яхшилик қилса, бас, ўзи учун (яъни, ўз фойдасига қилган) бўлур, ким ёмонлик қилса, бас, ўз зиёнига (қилган) бўлур. Сўнгра Парвардигорингизга қайтарилурсизлар'}, {'chapter': 45, 'verse': 16, 'text': 'وَلَقَدۡ ءَاتَيۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَى ٱلۡعَٰلَمِينَ\nДарҳақиқат, Биз Бани Исроил (қавми)га Китоб — Таврот, ҳикмат ва пайғамбарлик ато этдик ва уларни ҳалол-пок нарсалардан ризқлантирдик ҳамда уларни (ўз замонларидаги) барча оламдан устун қилдик'}, {'chapter': 45, 'verse': 17, 'text': 'وَءَاتَيۡنَٰهُم بَيِّنَٰتٖ مِّنَ ٱلۡأَمۡرِۖ فَمَا ٱخۡتَلَفُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ\nЯна Биз уларга (дин) ишлари ҳақида аниқ-равшан ҳужжатлар ато этдик. Бас, улар ихтилоф қилмадилар, магар уларга (Биз томонимиздан дин ҳақида) илм-ҳужжат келганидан сўнг ўзаро ҳасад-адоват қилишиб (талашиб-тортишдилар). Албатта Парвардигорингиз Қиёмат Кунида уларнинг ўрталарида ихтилоф қилиб ўтган нарсалари ҳақида ҳукм қилур'}, {'chapter': 45, 'verse': 18, 'text': 'ثُمَّ جَعَلۡنَٰكَ عَلَىٰ شَرِيعَةٖ مِّنَ ٱلۡأَمۡرِ فَٱتَّبِعۡهَا وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ لَا يَعۡلَمُونَ\nСўнгра (эй Муҳаммад алайҳис-салоту вас-салом), Биз сизни (дин) ишидан бир шариат (аниқ-равшан йўл) устида (барқарор) қилдик. Бас, сиз (фақат мана шу йўлга) эргашинг ва билмайдиган кимсаларнинг ҳавойи нафсларига эргашманг'}, {'chapter': 45, 'verse': 19, 'text': 'إِنَّهُمۡ لَن يُغۡنُواْ عَنكَ مِنَ ٱللَّهِ شَيۡـٔٗاۚ وَإِنَّ ٱلظَّـٰلِمِينَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۖ وَٱللَّهُ وَلِيُّ ٱلۡمُتَّقِينَ\nЧунки, (агар сиз уларнинг ҳавойи нафслари кўрсатган залолат йўлига юрсангиз,) улар сиздан Аллоҳ томонидан бўлган бирон нарсани (азобни) қайтара олмаслар. Албатта золимлар бир-бирларига дўстдирлар. Аллоҳ эса тақводор зотларнинг дўстидир'}, {'chapter': 45, 'verse': 20, 'text': 'هَٰذَا بَصَـٰٓئِرُ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُوقِنُونَ\nУшбу (Қуръон барча) одамлар учун (равшан) кўрсатмалардир ва аниқ ишонадиган қавм учун ҳидоят ва раҳматдир'}, {'chapter': 45, 'verse': 21, 'text': 'أَمۡ حَسِبَ ٱلَّذِينَ ٱجۡتَرَحُواْ ٱلسَّيِّـَٔاتِ أَن نَّجۡعَلَهُمۡ كَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَوَآءٗ مَّحۡيَاهُمۡ وَمَمَاتُهُمۡۚ سَآءَ مَا يَحۡكُمُونَ\nБалки ёмонлик-гуноҳлар касб этган кимсалар Биз уларни ҳам иймон келтирган ва яхши амаллар қилган зотлар каби қилишимизни ва ҳаётлари ҳам, мамотлари ҳам (яъни, дунёлари ҳам, Охиратлари ҳам мўминлар билан) баробар бўлишини ўйлагандирлар?! Нақадар ёмон (нотўғри) ҳукм қилурлар-а'}, {'chapter': 45, 'verse': 22, 'text': 'وَخَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَلِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ\nАллоҳ осмонлар ва Ерни (Ўзининг қудрати илоҳиясига далолат қилиши учун) ва ҳар бир жон мазлум бўлмаган ҳолларида ўзлари қилган амаллари сабабли жазоланиши учун ҳақ (қонун ва низом) билан яратди'}, {'chapter': 45, 'verse': 23, 'text': 'أَفَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلۡمٖ وَخَتَمَ عَلَىٰ سَمۡعِهِۦ وَقَلۡبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَٰوَةٗ فَمَن يَهۡدِيهِ مِنۢ بَعۡدِ ٱللَّهِۚ أَفَلَا تَذَكَّرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ҳавойи нафсини ўзига «илоҳ» қилиб олган ва Аллоҳ уни билган ҳолида йўлдан оздириб, қулоқ ва кўнглини муҳрлаб, кўз олдига парда тортиб қўйган кимсани кўрганмисиз? Бас, уни Аллоҳ (йўлдан оздиргани)дан сўнг ким ҳидоят қила олур?! Ахир эслатма-ибрат олмайсизларми'}, {'chapter': 45, 'verse': 24, 'text': 'وَقَالُواْ مَا هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا يُهۡلِكُنَآ إِلَّا ٱلدَّهۡرُۚ وَمَا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَظُنُّونَ\nУлар (яъни, Қиёматни инкор қилгувчилар: «Ҳаёт) фақат дунёдаги ҳаётимиздир. (Баъзиларимиз) ўлиб, (бошқаларимиз) ҳаётга келаверамиз ва бизларни (Аллоҳ ўлдирмайди, балки) фақат замон (ўтиши)гина ўлдиради», дедилар. Улар фақат (шундай) гумон қилурлар, холос'}, {'chapter': 45, 'verse': 25, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ مَّا كَانَ حُجَّتَهُمۡ إِلَّآ أَن قَالُواْ ٱئۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ\nҚачон уларга (қайта тирилиш ҳақлиги тўғрисидаги) Бизнинг аниқ-равшан бўлган оятларимиз тиловат қилинса, уларнинг (бу оятларга қарши) «ҳужжатлари» фақат: «Агар ростгўй бўлсангизлар, ота-боболаримизни (тирилтириб) келтиринглар-чи», дейишларигина бўлур'}, {'chapter': 45, 'verse': 26, 'text': 'قُلِ ٱللَّهُ يُحۡيِيكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يَجۡمَعُكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ\nАйтинг: «Аллоҳ сизларга ҳаёт берур, сўнгра (ажалларингиз етгач) ўлдирур, сўнгра сизларни ҳеч шак-шубҳасиз, (келадиган) Қиёмат Кунига тўплар. Лекин одамларнинг кўплари (буни) билмаслар»'}, {'chapter': 45, 'verse': 27, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَخۡسَرُ ٱلۡمُبۡطِلُونَ\nОсмонлар ва Ердаги бор мулк Аллоҳникидир. (Қиёмат) соати қойим бўладиган кунда — ана ўша кунда бузғунчи кимсалар зиён тортурлар'}, {'chapter': 45, 'verse': 28, 'text': 'وَتَرَىٰ كُلَّ أُمَّةٖ جَاثِيَةٗۚ كُلُّ أُمَّةٖ تُدۡعَىٰٓ إِلَىٰ كِتَٰبِهَا ٱلۡيَوۡمَ تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\n(У Кунда) барча умматни (даҳшат ва изтироб-ла) тиз чўккан ҳолда кўрарсиз. Ҳар бир уммат ўз номаи-аъмолига чақирилур, (сўнг уларга дейилур): «Бугун қилиб ўтган амалларингиз билан жазоланурсизлар'}, {'chapter': 45, 'verse': 29, 'text': 'هَٰذَا كِتَٰبُنَا يَنطِقُ عَلَيۡكُم بِٱلۡحَقِّۚ إِنَّا كُنَّا نَسۡتَنسِخُ مَا كُنتُمۡ تَعۡمَلُونَ\nМана бу китобимиз (яъни, Бизнинг амримиз билан ёзилган номаи-аъмолингиз) сизларга ҳақиқатни айтур. Дарҳақиқат, Биз сизлар қилгувчи бўлган барча амалларингизни (ушбу номаи-аъмолга) кўчириб қўйгандирмиз»'}, {'chapter': 45, 'verse': 30, 'text': 'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُدۡخِلُهُمۡ رَبُّهُمۡ فِي رَحۡمَتِهِۦۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡمُبِينُ\nБас, иймон келтирган ва яхши амаллар қилган зотларга келсак, уларни Парвардигор Ўз раҳмати-жаннатига дохил қилур. Бу очиқ бахтнинг ўзидир'}, {'chapter': 45, 'verse': 31, 'text': 'وَأَمَّا ٱلَّذِينَ كَفَرُوٓاْ أَفَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَٱسۡتَكۡبَرۡتُمۡ وَكُنتُمۡ قَوۡمٗا مُّجۡرِمِينَ\nЭнди кофир бўлган кимсаларга келсак, (бас, уларга айтилур): «Ахир сизларга Менинг оятларим тиловат қилинган эмасмиди?! Бас, сизлар (у оятларга иймон келтирмасдан) кибру-ҳаво қилдинглар ва жиноятчи қавм бўлдинглар!»'}, {'chapter': 45, 'verse': 32, 'text': 'وَإِذَا قِيلَ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱلسَّاعَةُ لَا رَيۡبَ فِيهَا قُلۡتُم مَّا نَدۡرِي مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنّٗا وَمَا نَحۡنُ بِمُسۡتَيۡقِنِينَ\nҚачон (сизларга): «Дарҳақиқат, Аллоҳнинг ваъдаси ростдир, (Қиёмат) соати шак-шубҳасиз, (келгувчидир)», дейилса, сизлар: «Биз у соат нима эканини билмаймиз, фақат гумон қиламиз, холос. Биз (Қиёмат соати келишига) аниқ ишонгувчи эмасмиз», дединглар'}, {'chapter': 45, 'verse': 33, 'text': 'وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\n(Мана энди) уларга қилиб ўтган ёмонликлар ошкор бўлди ва ўзлари масхара қилиб ўтган (азоб) уларни ўраб олди'}, {'chapter': 45, 'verse': 34, 'text': 'وَقِيلَ ٱلۡيَوۡمَ نَنسَىٰكُمۡ كَمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ\nВа (уларга) дейилди: «Худди сизлар мана шу бугунги кунингиздаги учрашувни унутганларингиздек, Биз ҳам бугун сизларни «унутурмиз». Сизларнинг жойингиз дўзахдир ва сизлар учун дўзах азобидан қутқарувчи ёрдамчилар ҳам йўқдир'}, {'chapter': 45, 'verse': 35, 'text': 'ذَٰلِكُم بِأَنَّكُمُ ٱتَّخَذۡتُمۡ ءَايَٰتِ ٱللَّهِ هُزُوٗا وَغَرَّتۡكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ لَا يُخۡرَجُونَ مِنۡهَا وَلَا هُمۡ يُسۡتَعۡتَبُونَ\nБунга сабаб — сизлар Аллоҳнинг оятларини масхара қилиб олган эдинглар ва сизларни ҳаёти дунё алдаб қўйган эди. Бас, бугун улар (дўзахдан) чиқарилмаслар ва улардан (Аллоҳ рози бўладиган амалларга) қайтиш ҳам талаб қилинмас'}, {'chapter': 45, 'verse': 36, 'text': 'فَلِلَّهِ ٱلۡحَمۡدُ رَبِّ ٱلسَّمَٰوَٰتِ وَرَبِّ ٱلۡأَرۡضِ رَبِّ ٱلۡعَٰلَمِينَ\nБас, барча ҳамду сано осмонлар Парвардигори, Ер Парвардигори, бутун оламлар Парвардигори — Аллоҳ учундир'}, {'chapter': 45, 'verse': 37, 'text': 'وَلَهُ ٱلۡكِبۡرِيَآءُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлар ва Ердаги барча буюклик ёлғиз Уникидир! У қудрат ва ҳикмат Соҳибидир'}, {'chapter': 46, 'verse': 1, 'text': 'حمٓ\nҲо, Мим'}, {'chapter': 46, 'verse': 2, 'text': 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\n(Бу Қуръон) қудратли ва ҳикматли Аллоҳ томонидан нозил қилинган Китобдир'}, {'chapter': 46, 'verse': 3, 'text': 'مَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۚ وَٱلَّذِينَ كَفَرُواْ عَمَّآ أُنذِرُواْ مُعۡرِضُونَ\nБиз осмонлар ва Ерни ҳамда уларнинг ўртасидаги нарсаларни фақат ҳақ (қонун) (Қиёмат Кунигача) белгаланган муддат билан яратдик. Кофир бўлган кимсалар эса ўзлари огоҳлантирилган нарсадан (яъни, Охиратдаги азобдан) юз ўгирувчидирлар (уни инкор қилгувчидирлар)'}, {'chapter': 46, 'verse': 4, 'text': 'قُلۡ أَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِۖ ٱئۡتُونِي بِكِتَٰبٖ مِّن قَبۡلِ هَٰذَآ أَوۡ أَثَٰرَةٖ مِّنۡ عِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушрикларга) айтинг: «Сизлар Аллоҳни қўйиб дуо-илтижо қилаётган (бутларингиз) ҳақида хабар берингиз — менга кўрсатинглар-чи, улар Ердан нимани яратганлар?! Агар ростгўй бўлсангизлар, менга ушбу (Қуръон)дан илгари (нозил бўлган ва сизларнинг бутларга сиғинишингизни тасдиқлайдиган) бирон китобни ёки бирон илмий асарни (яъни, аввалги уламолардан қолган биронта аниқ ҳужжатни) келтиринглар!»'}, {'chapter': 46, 'verse': 5, 'text': 'وَمَنۡ أَضَلُّ مِمَّن يَدۡعُواْ مِن دُونِ ٱللَّهِ مَن لَّا يَسۡتَجِيبُ لَهُۥٓ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ وَهُمۡ عَن دُعَآئِهِمۡ غَٰفِلُونَ\nАллоҳни қўйиб, Қиёмат Кунигача ҳам (дуони) мустажоб қила олмайдиган бутларга дуо-илтижо қиладиган кимсадан ҳам йўлдан озганроқ ким бор?! Ҳолбуки, у (жонсиз бут)лар ўша (мушрик)ларнинг дуоларидан ғофилдирлар'}, {'chapter': 46, 'verse': 6, 'text': 'وَإِذَا حُشِرَ ٱلنَّاسُ كَانُواْ لَهُمۡ أَعۡدَآءٗ وَكَانُواْ بِعِبَادَتِهِمۡ كَٰفِرِينَ\n(Қиёмат Кунида) инсонлар (ҳисоб-китоб учун) тўпланган вақтида ўша (бутлар) уларга (мушрикларга) душман бўлурлар ва уларнинг (Аллоҳни қўйиб, ўзларига) ибодат қилганларини ҳам инкор этурлар'}, {'chapter': 46, 'verse': 7, 'text': 'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ هَٰذَا سِحۡرٞ مُّبِينٌ\nҚачон уларга Бизнинг аниқ-равшан бўлган оятларимиз тиловат қилинса, кофир бўлган кимсалар Ҳақ (Қуръон) тўғрисида у ўзларига келган вақтидаёқ (тафаккур қилиб кўрмасданоқ) «Бу очиқ сеҳрдир», дерлар'}, {'chapter': 46, 'verse': 8, 'text': 'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَلَا تَمۡلِكُونَ لِي مِنَ ٱللَّهِ شَيۡـًٔاۖ هُوَ أَعۡلَمُ بِمَا تُفِيضُونَ فِيهِۚ كَفَىٰ بِهِۦ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۖ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ\nЁки улар («Қуръонни Муҳаммаднинг) ўзи тўқиб олган», дейдиларми?! Айтинг: «Агар уни ўзим тўқиб олган бўлсам, у ҳолда, сизлар мен учун Аллоҳ (азоби)дан бирон нарса(ни дафъ қилиш)га эга бўла олмассизлар (яъни, у ҳолда Аллоҳнинг Ўзи Унинг шаънига ёлғон тўқиганим учун мени ҳеч ким қайтара олмайдиган азоб билан жазолаган бўлур эди. Бас, мен сизлардан эмас, балки Аллоҳ таолодан қўрққаним сабабли сизларга Ҳақ ваҳийни келтирмоқдаман)! У сизлар киришаётган нарсани (яъни, Қуръонни «бир сеҳр, бир уйдирма» деяётганларингизни) жуда яхши билгувчидир. Унинг Ўзи мен билан сизларнинг ўртангизда етарли гувоҳдир. У мағфиратли, меҳрибон Зотдир»'}, {'chapter': 46, 'verse': 9, 'text': 'قُلۡ مَا كُنتُ بِدۡعٗا مِّنَ ٱلرُّسُلِ وَمَآ أَدۡرِي مَا يُفۡعَلُ بِي وَلَا بِكُمۡۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّ وَمَآ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен пайғамбарлардан янги — биринчиси эмасман (яъни, мендан аввал ҳам Аллоҳнинг пайғамбарлари ўтган-ку, нега сизлар менинг пайғамбарлигимга ҳайрон бўлиб, инкор қилмоқдасизлар?!) Мен (келажакда) на ўзимга ва на сизларга нима қилинишини билмасман (яъни, ғайбдан хабардор эмасман). Мен фақат ўзимга ваҳий қилинадиган нарсага эргашурман. Мен фақат очиқ огоҳлантиргувчидирман»'}, {'chapter': 46, 'verse': 10, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ وَكَفَرۡتُم بِهِۦ وَشَهِدَ شَاهِدٞ مِّنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ عَلَىٰ مِثۡلِهِۦ فَـَٔامَنَ وَٱسۡتَكۡبَرۡتُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nАйтинг: («Эй мушриклар), хабар берингиз-чи, агар (ушбу Қуръон) Аллоҳнинг ҳузуридан бўлса-ю, сизлар унга кофир бўлсангизлар ва Бани Исроилдан бўлган бир гувоҳ ҳам у (Қуръон)нинг ўхшаши (ёлғиз Аллоҳ томонидангина нозил бўлиши)га гувоҳлик бериб, иймон келтирса-ю, сизлар (унга иймон келтиришдан) кибр-ҳаво қилсангизлар (яъни, сизлар энг золим одамлар бўлиб қолмайсизларми?!) Албатта Аллоҳ золим қавмни ҳидоят қилмас!»'}, {'chapter': 46, 'verse': 11, 'text': 'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ لَوۡ كَانَ خَيۡرٗا مَّا سَبَقُونَآ إِلَيۡهِۚ وَإِذۡ لَمۡ يَهۡتَدُواْ بِهِۦ فَسَيَقُولُونَ هَٰذَآ إِفۡكٞ قَدِيمٞ\nКофир бўлган кимсалар иймон келтирган зотлар ҳақида «Агар (бу Қуръон) яхши бўлганида, (анави ялангоёқлар) унга бизлардан илгари бормаган бўлур эдилар», дедилар. Улар ўзлари (Қуръон) билан ҳидоят топишмагач: «Бу эски уйдирмадир», дерлар'}, {'chapter': 46, 'verse': 12, 'text': 'وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةٗۚ وَهَٰذَا كِتَٰبٞ مُّصَدِّقٞ لِّسَانًا عَرَبِيّٗا لِّيُنذِرَ ٱلَّذِينَ ظَلَمُواْ وَبُشۡرَىٰ لِلۡمُحۡسِنِينَ\nУ (Қуръон)дан илгари Мусонинг пешво ва раҳмат бўлган Китоби (Таврот) бор эди. Бу (Қуръон) арабий лисондаги, (ўзидан аввал нозил бўлган барча илоҳий китобларни) тасдиқлагувчи бир Китобдир. У золим-кофир бўлган кимсаларни (Охират азобидан) огоҳлантириш учун ва чиройли амал қилгувчиларга (жаннат ҳақида) хушхабар бўлиш учун (нозил қилингандир)'}, {'chapter': 46, 'verse': 13, 'text': 'إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ\nАлбатта «Парвардигоримиз Аллоҳдир», деган, сўнгра (Тўғри Йўлда) устивор бўлган зотлар учун хавф-хатар йўқдир ва улар ғамгин бўлмаслар'}, {'chapter': 46, 'verse': 14, 'text': 'أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ\nАна ўшалар жаннат эгалари бўлиб, у жойда мангу қолурлар. (Бу) улар қилиб ўтган амалларининг мукофотидир'}, {'chapter': 46, 'verse': 15, 'text': 'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ إِحۡسَٰنًاۖ حَمَلَتۡهُ أُمُّهُۥ كُرۡهٗا وَوَضَعَتۡهُ كُرۡهٗاۖ وَحَمۡلُهُۥ وَفِصَٰلُهُۥ ثَلَٰثُونَ شَهۡرًاۚ حَتَّىٰٓ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرۡبَعِينَ سَنَةٗ قَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَصۡلِحۡ لِي فِي ذُرِّيَّتِيٓۖ إِنِّي تُبۡتُ إِلَيۡكَ وَإِنِّي مِنَ ٱلۡمُسۡلِمِينَ\nБиз инсонни ота-онасига яхшилик қилишга буюрдик. Онаси унга қийналиб ҳомиладор бўлиб, қийналиб туққандир. Унга ҳомиладор бўлиш ва уни (сутдан) ажратиш (муддати) ўттиз ойдир. Энди қачон у вояга етиб, қирқ ёшга тўлганида: «Парвардигорим, мени Сен менга ва ота-онамга инъом этган неъматингга шукр қилишга ва Ўзинг рози бўладиган яхши амалларнигина қилишга муваффақ этгин ва Ўзинг мен учун зурриётимга ҳам яхшилик қилгин (яъни, фарзандларимни ҳам аҳли солиҳ бандаларингдан қилгин). Албатта мен Сенга (қилган барча гуноҳларимдан) тавба қилдим ва албатта мен мусулмонлардандирман», деди'}, {'chapter': 46, 'verse': 16, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ نَتَقَبَّلُ عَنۡهُمۡ أَحۡسَنَ مَا عَمِلُواْ وَنَتَجَاوَزُ عَن سَيِّـَٔاتِهِمۡ فِيٓ أَصۡحَٰبِ ٱلۡجَنَّةِۖ وَعۡدَ ٱلصِّدۡقِ ٱلَّذِي كَانُواْ يُوعَدُونَ\nАна ўшалар шундай зотлардирки, Биз улардан қилган энг чиройли амалларни қабул қилурмиз ва жаннат эгалари қаторида уларнинг ёмонлик гуноҳларидан ўтиб юборурмиз. (Бу) уларга ваъда қилингувчи ҳаққирост ваъдадир'}, {'chapter': 46, 'verse': 17, 'text': 'وَٱلَّذِي قَالَ لِوَٰلِدَيۡهِ أُفّٖ لَّكُمَآ أَتَعِدَانِنِيٓ أَنۡ أُخۡرَجَ وَقَدۡ خَلَتِ ٱلۡقُرُونُ مِن قَبۡلِي وَهُمَا يَسۡتَغِيثَانِ ٱللَّهَ وَيۡلَكَ ءَامِنۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ فَيَقُولُ مَا هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nЯна бир кимса борки, ота-онасига: «Суф сизларга! Сизлар менга (ўлганимдан кейин қайта тирилиб, қабрдан) чиқарилишим ҳақида ваъда беряпсизлар-ми?! Ҳолбуки, мендан илгари ҳам асрлар-авлодлар ўтган-ку, (улардан биронтаси қайта тирилиб келдими)?!», дер. У иккиси Аллоҳдан мадад тилаган ҳолларида (ўғилларига қараб): «Ҳалок бўлгур, иймон келтиргин! Албатта Аллоҳнинг (барча бандаларни қайта тирилтириш ҳақидаги) ваъдаси ҳақдир», (десалар), у: «Бу фақат аввалгиларнинг афсоналаридир», дер'}, {'chapter': 46, 'verse': 18, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ\nАна ўшалар шундай кимсалардирки, уларнинг устига ўзларидан илгари ўтган инсу-жинсдан иборат (кофир) умматлар қаторида (азоб) Сўзи ҳақ бўлгандир. Албатта улар зиён кўргувчи бўлдилар'}, {'chapter': 46, 'verse': 19, 'text': 'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۖ وَلِيُوَفِّيَهُمۡ أَعۡمَٰلَهُمۡ وَهُمۡ لَا يُظۡلَمُونَ\n(Мўминлар ва кофирлардан) ҳар бири учун ўзлари қилган амаллари сабабли даража-мартабалар бордир. Чунки (Аллоҳ) уларга қилган амалларини(нг мукофот ва жазоларини) комил қилиб берур ва уларга зулм қилинмас'}, {'chapter': 46, 'verse': 20, 'text': 'وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَذۡهَبۡتُمۡ طَيِّبَٰتِكُمۡ فِي حَيَاتِكُمُ ٱلدُّنۡيَا وَٱسۡتَمۡتَعۡتُم بِهَا فَٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَسۡتَكۡبِرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَفۡسُقُونَ\nКофир бўлган кимсалар дўзахга кўндаланг қилинадиган Кунда (уларга дейилур): «Сизлар ўз ҳузур-ҳаловатларингизни ҳаёти дунёларингиздаёқ кетказдингиз ва улардан фойдаланиб бўлдингиз. Энди Бугун Ерда ноҳақ кибру ҳаво қилганларингиз ва итоатсиз бўлганларингиз сабабли хорлик азоби билан жазоланурсизлар»'}, {'chapter': 46, 'verse': 21, 'text': '۞وَٱذۡكُرۡ أَخَا عَادٍ إِذۡ أَنذَرَ قَوۡمَهُۥ بِٱلۡأَحۡقَافِ وَقَدۡ خَلَتِ ٱلنُّذُرُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦٓ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Од (қабиласи)нинг биродарини (яъни, Ҳуд пайғамбарни) эсланг! Ўшанда у ўзининг қумтепалардаги қавмини: «Сизлар ёлғиз Аллоҳгагина ибодат қилинглар! Мен ҳақиқатан, сизларнинг устингизда улуғ Куннинг азобидан қўрқурман», деб огоҳлантирган эди, — дарҳақиқат, ундан илгари ҳам, ундан кейин ҳам огоҳлантиргувчи (пайғамбар)лар ўтгандир'}, {'chapter': 46, 'verse': 22, 'text': 'قَالُوٓاْ أَجِئۡتَنَا لِتَأۡفِكَنَا عَنۡ ءَالِهَتِنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ\nУлар: «Сен бизларни худоларимиздан буриш (юз ўгиртириш) учун келдингми?! У ҳолда агар ростгўй кишилардан бўлсанг, бизларга ваъда қилаётган нарсангни (яъни, Аллоҳнинг азобини) келтирган-чи?», дедилар'}, {'chapter': 46, 'verse': 23, 'text': 'قَالَ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّآ أُرۡسِلۡتُ بِهِۦ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ\nУ айтди: «(Азоб келар вақтнинг) билими ёлғиз Аллоҳ ҳузуридадир. Мен сизларга ўзим элчи қилиб юборилган динни етказурман. Лекин мен сизларни нодон қавм эканлигингизни кўрмоқдаман»'}, {'chapter': 46, 'verse': 24, 'text': 'فَلَمَّا رَأَوۡهُ عَارِضٗا مُّسۡتَقۡبِلَ أَوۡدِيَتِهِمۡ قَالُواْ هَٰذَا عَارِضٞ مُّمۡطِرُنَاۚ بَلۡ هُوَ مَا ٱسۡتَعۡجَلۡتُم بِهِۦۖ رِيحٞ فِيهَا عَذَابٌ أَلِيمٞ\nБас, қачонки, улар ўша (азоб)ни ўз водийларига қараб келаётган бир қора булут ҳолида кўришгач: «Бу бизларга ёмғир келтиргувчи булутдир», дедилар. «Йўқ, у ўзларингиз шоштирган нарса — бир бўронки, унда аламли азоб бордир'}, {'chapter': 46, 'verse': 25, 'text': 'تُدَمِّرُ كُلَّ شَيۡءِۭ بِأَمۡرِ رَبِّهَا فَأَصۡبَحُواْ لَا يُرَىٰٓ إِلَّا مَسَٰكِنُهُمۡۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ\nУ Парвардигорининг амри билан барча нарсани вайрон (ҳалок) қилур. Бас, (у бўрон келиб, Од қабиласидаги кофирларнинг ўзларини ҳам, мол-ҳолларини ҳам ҳалок қилди. Барчалари қирилиб битиб), тонг отганида, фақат уларнинг уй-жойларигина кўзга ташланар эди. Биз жиноятчи-осий қавмни мана шундай жазолаймиз'}, {'chapter': 46, 'verse': 26, 'text': 'وَلَقَدۡ مَكَّنَّـٰهُمۡ فِيمَآ إِن مَّكَّنَّـٰكُمۡ فِيهِ وَجَعَلۡنَا لَهُمۡ سَمۡعٗا وَأَبۡصَٰرٗا وَأَفۡـِٔدَةٗ فَمَآ أَغۡنَىٰ عَنۡهُمۡ سَمۡعُهُمۡ وَلَآ أَبۡصَٰرُهُمۡ وَلَآ أَفۡـِٔدَتُهُم مِّن شَيۡءٍ إِذۡ كَانُواْ يَجۡحَدُونَ بِـَٔايَٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ\nҲолбуки, (эй Макка аҳли), Биз уларни (мол-дунё ва куч-қувват жиҳатидан) сизларни қодир қилмаган нарсаларга қодир қилган эдик ва уларга ҳам қулоқ, кўз ва диллар (ато) қилган эдик. (Лекин) улар Аллоҳнинг оят-мўъжизаларини инкор этганлари учун уларга қулоқ-кўзлари ҳам, диллари ҳам бирон фойда бермади (яъни, Ҳақ йўлни кўриб-идрок қила олмадилар). Ва уларни ўзлари масхара қилиб кулган нарсалари (Аллоҳнинг азоби) ўраб олди'}, {'chapter': 46, 'verse': 27, 'text': 'وَلَقَدۡ أَهۡلَكۡنَا مَا حَوۡلَكُم مِّنَ ٱلۡقُرَىٰ وَصَرَّفۡنَا ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَرۡجِعُونَ\n(Эй Макка аҳли), аниқки, Биз сизларнинг атрофингиздаги қишлоқ-шаҳарларни ҳалок қилдик. Биз уларга шояд (куфру исёнларидан) қайтсалар, деб оят-мўъжизаларни батафсил баён қилган эдик, (лекин улар қайтмай ҳалокатга дучор бўлдилар)'}, {'chapter': 46, 'verse': 28, 'text': 'فَلَوۡلَا نَصَرَهُمُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ قُرۡبَانًا ءَالِهَةَۢۖ بَلۡ ضَلُّواْ عَنۡهُمۡۚ وَذَٰلِكَ إِفۡكُهُمۡ وَمَا كَانُواْ يَفۡتَرُونَ\nАхир ўзлари Аллоҳни қўйиб, (Аллоҳга) яқин қиладиган «худо»лар, деб ушлаб олган — сиғинган бутлари (ҳалокатдан қутулишлари учун) уларга ёрдам берсалар бўлмасмиди?! Йўқ, у (бутлар мушриклардан ёрдамга муҳтож бўлган вақтларида) ғойиб бўлиб қолишди. Бу (яъни, бутлар бутпарастларни Аллоҳга яқин қилади, деган ақида) уларнинг уйдирмаларидир ва (Аллоҳ шаънига) тўқиб олган бўҳтонларидир'}, {'chapter': 46, 'verse': 29, 'text': 'وَإِذۡ صَرَفۡنَآ إِلَيۡكَ نَفَرٗا مِّنَ ٱلۡجِنِّ يَسۡتَمِعُونَ ٱلۡقُرۡءَانَ فَلَمَّا حَضَرُوهُ قَالُوٓاْ أَنصِتُواْۖ فَلَمَّا قُضِيَ وَلَّوۡاْ إِلَىٰ قَوۡمِهِم مُّنذِرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), эсланг, Биз сизнинг олдингизда бир гуруҳ жинларни Қуръон тингласинлар, деб юборган эдик. Бас, қачонки улар (Қуръон тиловатига) ҳозир бўлишгач, (бир-бирларига): «Жим туринглар», дедилар. Энди қачонки (тиловат) тугатилгач, улар ўз қавмлари олдига огоҳлантиргувчи бўлган ҳолларида қайтиб кетдилар'}, {'chapter': 46, 'verse': 30, 'text': 'قَالُواْ يَٰقَوۡمَنَآ إِنَّا سَمِعۡنَا كِتَٰبًا أُنزِلَ مِنۢ بَعۡدِ مُوسَىٰ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ يَهۡدِيٓ إِلَى ٱلۡحَقِّ وَإِلَىٰ طَرِيقٖ مُّسۡتَقِيمٖ\nУлар дедилар: «Эй қавмимиз, дарҳақиқат, бизлар Мусодан кейин нозил қилинган, ўзидан олдинги (илоҳий китоб)ларни тасдиқ қилгувчи бўлган, Ҳақ (дин)га ва Тўғри Йўлга ҳидоят қиладиган бир Китобни — Қуръонни тингладик'}, {'chapter': 46, 'verse': 31, 'text': 'يَٰقَوۡمَنَآ أَجِيبُواْ دَاعِيَ ٱللَّهِ وَءَامِنُواْ بِهِۦ يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُجِرۡكُم مِّنۡ عَذَابٍ أَلِيمٖ\nЭй қавмимиз, Аллоҳга даъват қилгувчи (Муҳаммад алайҳис-салоту вас-саломнинг даъвати)ни қабул қилинглар ва унга иймон келтиринглар, (шунда Аллоҳ) сизларнинг гуноҳларингизни мағфират қилур ва сизларга аламли азобдан паноҳ берур'}, {'chapter': 46, 'verse': 32, 'text': 'وَمَن لَّا يُجِبۡ دَاعِيَ ٱللَّهِ فَلَيۡسَ بِمُعۡجِزٖ فِي ٱلۡأَرۡضِ وَلَيۡسَ لَهُۥ مِن دُونِهِۦٓ أَوۡلِيَآءُۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ\nКим Аллоҳга даъват қилгувчи (даъвати)ни қабул қилмаса, бас, у Ер юзида (бирон жойга) қочиб қутулгувчи эмасдир ва унинг учун (Аллоҳдан) ўзга (Унинг азобидан қутқаргувчи) «дўстлар» ҳам йўқдир. Ана ўшалар очиқ залолатдадирлар»'}, {'chapter': 46, 'verse': 33, 'text': 'أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَلَمۡ يَعۡيَ بِخَلۡقِهِنَّ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰۚ بَلَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАхир улар (Охиратда қайта тирилиш ҳақ эканини инкор қилгувчи кимсалар) осмонлар ва Ерни яратган, уларни яратишга ожизлик қилиб қолмаган зот — Аллоҳ ўликларни тирилтиришга ҳам қодир эканлигини (ўйлаб) кўрмадиларми?! Йўқ, албатта (Аллоҳ ўликларни тирилтиришга ҳам қодирдир). Зеро, У зот барча нарсага қодирдир'}, {'chapter': 46, 'verse': 34, 'text': 'وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۖ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ\nКофир бўлган кимсалар дўзахга кўндаланг қилинадиган Кунда (уларга): «Ушбу (азоб) ҳақ эмас эканми?!», (дейилганида), улар: «Йўқ, Парвардигоримизга қасамки, (бизларга ваъда қилинган азоб ҳақ экан)», дерлар. (Шунда Аллоҳ) айтар: «Бас, кофир бўлганларингиз сабабли мана шу азобни тотинглар!»'}, {'chapter': 46, 'verse': 35, 'text': 'فَٱصۡبِرۡ كَمَا صَبَرَ أُوْلُواْ ٱلۡعَزۡمِ مِنَ ٱلرُّسُلِ وَلَا تَسۡتَعۡجِل لَّهُمۡۚ كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَ مَا يُوعَدُونَ لَمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّن نَّهَارِۭۚ بَلَٰغٞۚ فَهَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلۡفَٰسِقُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз ҳам (ўтган) пайғамбарлар орасидаги сабот-матонат эгалари сабр қилганларидек, (мушрикларнинг озор-азиятларига) сабр қилинг ва уларга (тушадиган азоб)ни шоштирманг. Улар ўзларига ваъда қилинаётган азобни кўрадиган Кунда гўё (бу дунёда) фақат кундуздан бир соатгина тургандек бўлиб қолурлар! (Бу Аллоҳ томонидан нозил қилинган) бир баёнот-хабардир. Бас, фақат итоатсиз қавмгина ҳалок қилинур'}, {'chapter': 47, 'verse': 1, 'text': 'ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ أَضَلَّ أَعۡمَٰلَهُمۡ\nКофир бўлган ва (ўзгаларни ҳам) Аллоҳ йўлидан тўсган кимсаларнинг амалларини (Аллоҳ) зое кетказур'}, {'chapter': 47, 'verse': 2, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَءَامَنُواْ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٖ وَهُوَ ٱلۡحَقُّ مِن رَّبِّهِمۡ كَفَّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَأَصۡلَحَ بَالَهُمۡ\nИймон келтирган ва яхши амаллар қилган ҳамда Муҳаммад (алайҳиссалом)га нозил қилинган нарсага (Қуръонга) — ҳолбуки, у Парвардигорлари томонидан келган Ҳақиқатдир — иймон келтирган зотларнинг эса ёмонлик-гуноҳларини ўчирур ва ишларини ўнглар'}, {'chapter': 47, 'verse': 3, 'text': 'ذَٰلِكَ بِأَنَّ ٱلَّذِينَ كَفَرُواْ ٱتَّبَعُواْ ٱلۡبَٰطِلَ وَأَنَّ ٱلَّذِينَ ءَامَنُواْ ٱتَّبَعُواْ ٱلۡحَقَّ مِن رَّبِّهِمۡۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ لِلنَّاسِ أَمۡثَٰلَهُمۡ\nБунга (яъни, кофирларнинг амаллари зое бўлиши ва мўминларнинг гуноҳлари ўчирилишига) сабаб — кофир бўлган кимсаларнинг ботил (йўл)га эргашганлари, иймон келтирганларнинг эса Парвардигорлари томонидан бўлган Ҳақ (Қуръон йўли)га эргашганларидир. Аллоҳ инсонларга уларнинг мисолларини мана шундай баён қилиб берур'}, {'chapter': 47, 'verse': 4, 'text': 'فَإِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ فَضَرۡبَ ٱلرِّقَابِ حَتَّىٰٓ إِذَآ أَثۡخَنتُمُوهُمۡ فَشُدُّواْ ٱلۡوَثَاقَ فَإِمَّا مَنَّۢا بَعۡدُ وَإِمَّا فِدَآءً حَتَّىٰ تَضَعَ ٱلۡحَرۡبُ أَوۡزَارَهَاۚ ذَٰلِكَۖ وَلَوۡ يَشَآءُ ٱللَّهُ لَٱنتَصَرَ مِنۡهُمۡ وَلَٰكِن لِّيَبۡلُوَاْ بَعۡضَكُم بِبَعۡضٖۗ وَٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ فَلَن يُضِلَّ أَعۡمَٰلَهُمۡ\nБас, (эй мўминлар), қачон сизлар (жанг майдонида) кофир бўлган кимсалар билан тўқнашганларингизда бўйинларига урингиз — ўлдирингиз! Энди қачон уларни(нг кўпларини) қириб (мағлуб қилганингиздан кейин уларни асир олиб) арқонлар билан боғлангиз! Сўнг ё (уларни озод қилиб юбориш билан) марҳамат кўрсатурсизлар ё (уларни қўйиб юбориш учун) фидя-товон олурсизлар. Токи уруш юкларини қўйгунича (яъни, тўхтагунича сизларга буюрилган иш) мана шудир. Агар Аллоҳ хоҳласа улардан (жанг-жадалсиз ҳам) ғолиб бўлур (яъни, уларни йўқ қилиб юборур) эди, лекин У зот сизларнинг айримларингизни айримларингиз билан имтиҳон қилиш учун сизларни жангга буюрди). Аллоҳ йўлида ўлдирилган зотларнинг амалларини ҳаргиз зое кеткизмас'}, {'chapter': 47, 'verse': 5, 'text': 'سَيَهۡدِيهِمۡ وَيُصۡلِحُ بَالَهُمۡ\nУларни (жаннат йўлига) ҳидоят қилур ва ишларини ўнглар'}, {'chapter': 47, 'verse': 6, 'text': 'وَيُدۡخِلُهُمُ ٱلۡجَنَّةَ عَرَّفَهَا لَهُمۡ\nУларни (Аллоҳнинг) Ўзи уларга танитган жаннатга киритур'}, {'chapter': 47, 'verse': 7, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَنصُرُواْ ٱللَّهَ يَنصُرۡكُمۡ وَيُثَبِّتۡ أَقۡدَامَكُمۡ\nЭй мўминлар, агар сизлар Аллоҳга ёрдам берсангизлар (яъни, Унинг йўлида жиҳод қилсангизлар), У зот ҳам сизларга ёрдам берур ва жанг майдонида кадамларингизни собит-барқарор қилур'}, {'chapter': 47, 'verse': 8, 'text': 'وَٱلَّذِينَ كَفَرُواْ فَتَعۡسٗا لَّهُمۡ وَأَضَلَّ أَعۡمَٰلَهُمۡ\nКофир бўлган кимсалар учун эса ҳалокат бўлур ва (Аллоҳ) уларнинг амалларини зое кетказур'}, {'chapter': 47, 'verse': 9, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ كَرِهُواْ مَآ أَنزَلَ ٱللَّهُ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ\nБунга сабаб, уларнинг Аллоҳ нозил қилган нарсаларни (яъни, Қуръон ва ундаги ҳукмларни) ёмон кўрганларидир. Бас, (Аллоҳ) уларнинг амалларини беҳуда кетказди'}, {'chapter': 47, 'verse': 10, 'text': '۞أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ دَمَّرَ ٱللَّهُ عَلَيۡهِمۡۖ وَلِلۡكَٰفِرِينَ أَمۡثَٰلُهَا\nАхир улар Ер юзида сайру-саёҳат қилишиб, ўзларидан аввалги (пайғамбарларни ёлғончи қилиб иймонсиз кетган) кимсаларнинг оқибатлари қандай бўлганини (улар қандай қисматга йўлиқишганини) кўрсалар бўлмайдими?! Аллоҳ уларнинг устига ҳалокат юбориб (бутунлай йўқ бўлиб кетдилар-ку)! Бу кофирлар учун ҳам худди ўша (оқибат-ҳалокат)ларнинг ўхшаши бўлур'}, {'chapter': 47, 'verse': 11, 'text': 'ذَٰلِكَ بِأَنَّ ٱللَّهَ مَوۡلَى ٱلَّذِينَ ءَامَنُواْ وَأَنَّ ٱلۡكَٰفِرِينَ لَا مَوۡلَىٰ لَهُمۡ\nБунга (яъни, Аллоҳ мўминларга ёрдам беришига, кофирларни эса ҳалок қилишига) сабаб Аллоҳ иймон келтирган зотларнинг дўсти-ҳомийси эканлиги, кофирлар учун эса ҳеч қандай ҳомий йўқ эканлигидир'}, {'chapter': 47, 'verse': 12, 'text': 'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَٱلَّذِينَ كَفَرُواْ يَتَمَتَّعُونَ وَيَأۡكُلُونَ كَمَا تَأۡكُلُ ٱلۡأَنۡعَٰمُ وَٱلنَّارُ مَثۡوٗى لَّهُمۡ\nАлбатта Аллоҳ иймон келтирган ва яхши амаллар қилган зотларни остидан дарёлар оқиб турадиган жаннатларга киритур. Кофир бўлган кимсалар эса (мана шу ҳаёти дунёнинг ўткинчи лаззатларидан) фойдаланиб, чорва ҳайвонлари еганидек еб-ичурлар ва уларнинг жойлари дўзах бўлур'}, {'chapter': 47, 'verse': 13, 'text': 'وَكَأَيِّن مِّن قَرۡيَةٍ هِيَ أَشَدُّ قُوَّةٗ مِّن قَرۡيَتِكَ ٱلَّتِيٓ أَخۡرَجَتۡكَ أَهۡلَكۡنَٰهُمۡ فَلَا نَاصِرَ لَهُمۡ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизни ҳайдаб чиқарган қишлоқ (яъни, Макка аҳли)дан кўра (аҳолиси) кучли-қувватлироқ бўлган қанчадан-қанча қишлоқни ҳалок қилганмиз. Бас, улар учун бирон ёрдамчи бўлмаган'}, {'chapter': 47, 'verse': 14, 'text': 'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ كَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُم\nАхир Парвардигори томонидан аниқ-равшан ҳужжатга (яъни, Қуръонга) эга бўлган киши (яъни, расуллуллоҳ ва мўминлар), қилган ёмон амали ўзига чиройли кўринган ва ҳавойи нафсига эргашган (кофир) кимсалар каби бўлурми'}, {'chapter': 47, 'verse': 15, 'text': 'مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ فِيهَآ أَنۡهَٰرٞ مِّن مَّآءٍ غَيۡرِ ءَاسِنٖ وَأَنۡهَٰرٞ مِّن لَّبَنٖ لَّمۡ يَتَغَيَّرۡ طَعۡمُهُۥ وَأَنۡهَٰرٞ مِّنۡ خَمۡرٖ لَّذَّةٖ لِّلشَّـٰرِبِينَ وَأَنۡهَٰرٞ مِّنۡ عَسَلٖ مُّصَفّٗىۖ وَلَهُمۡ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَمَغۡفِرَةٞ مِّن رَّبِّهِمۡۖ كَمَنۡ هُوَ خَٰلِدٞ فِي ٱلنَّارِ وَسُقُواْ مَآءً حَمِيمٗا فَقَطَّعَ أَمۡعَآءَهُمۡ\nТақво эгалари учун ваъда қилинган жаннатнинг мисоли — сифати (будир): «Унда айнимаган сувдан бўлган дарёлар ҳам, таъми ўзгармаган сутдан бўлган дарёлар, ичгувчилар учун лаззатли (яъни, бадтаъм ва ақлдан оздиргувчи бўлмаган) майдан бўлган дарёлар ҳам, мусаффо асалдан бўлган дарёлар ҳам бордир. Улар учун у жойда барча мевалардан бордир ва (улар учун у жойда) Парвардигорлари томонидан мағфират бордир. (Ана шундай жаннат аҳли бўлган тақво эгалари) дўзахда мангу қоладиган ва (у жойда) қайноқ сув билан суғорилиб, у (сув) ичакларини бўлак-бўлак қилиб ташлаган (кофир) кимсалар каби бўлурми'}, {'chapter': 47, 'verse': 16, 'text': 'وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَ حَتَّىٰٓ إِذَا خَرَجُواْ مِنۡ عِندِكَ قَالُواْ لِلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مَاذَا قَالَ ءَانِفًاۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларнинг (мунофиқларнинг) орасида шундай кимсалар ҳам борки, улар сизга қулоқ солиб турур-да, ҳузурингиздан чиққан вақтларида, илм ато этилган зотлардан (масхара қилишиб): «Ҳозиргина (Муҳаммад) нима девди?», деб сўрарлар. Ана ўшалар Аллоҳ дилларини муҳрлаб қўйган ва ўзларининг ҳавойи нафсларига эргашган кимсалардир'}, {'chapter': 47, 'verse': 17, 'text': 'وَٱلَّذِينَ ٱهۡتَدَوۡاْ زَادَهُمۡ هُدٗى وَءَاتَىٰهُمۡ تَقۡوَىٰهُمۡ\nҲидоят топган — Тўғри Йўлга юрган зотларга эса (Аллоҳ) яна ҳидоятни зиёда ва тақво ато этур, (яъни, уларга ҳаромдан парҳез қилиш йўлларини кўрсатур)'}, {'chapter': 47, 'verse': 18, 'text': 'فَهَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗۖ فَقَدۡ جَآءَ أَشۡرَاطُهَاۚ فَأَنَّىٰ لَهُمۡ إِذَا جَآءَتۡهُمۡ ذِكۡرَىٰهُمۡ\nУлар (Макка кофирлари ўтмишдаги ўз пайғамбарларини ёлғончи қилган қавмлар дучор бўлган ҳалокатлардан эслатма-ибрат олмадилар), энди фақат тўсатдан (Қиёмат) соати келиб қолишинигина кутмоқдалар. Дарвоқеъ унинг аломатлари келди. Бас, уларга (Қиёмат соати) келган вақтида уларнинг эслатма-ибрат олишлари (учун) қандай (имкон) бўлур'}, {'chapter': 47, 'verse': 19, 'text': 'فَٱعۡلَمۡ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱللَّهُ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَٱللَّهُ يَعۡلَمُ مُتَقَلَّبَكُمۡ وَمَثۡوَىٰكُمۡ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), ҳеч қандай илоҳ йўқ, магар Аллоҳгина бор эканлигини билинг ва ўз гуноҳингиз учун ҳамда мўмин-мўминалар(нинг гуноҳлари) учун мағфират сўранг! Аллоҳ сизларнинг (бу дунёдаги) кезар жойларингизни ҳам, (Охиратда) борар жойларингизни ҳам билур'}, {'chapter': 47, 'verse': 20, 'text': 'وَيَقُولُ ٱلَّذِينَ ءَامَنُواْ لَوۡلَا نُزِّلَتۡ سُورَةٞۖ فَإِذَآ أُنزِلَتۡ سُورَةٞ مُّحۡكَمَةٞ وَذُكِرَ فِيهَا ٱلۡقِتَالُ رَأَيۡتَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يَنظُرُونَ إِلَيۡكَ نَظَرَ ٱلۡمَغۡشِيِّ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَأَوۡلَىٰ لَهُمۡ\nИймон келтирган зотлар: «(Кофирларга қарши жанг қилишга буюрилган) бирон сура нозил қилинганида эди», дерлар. Энди қачон аниқ бир сура нозил қилиниб, унда жанг зикр этилса, дилларида мараз бўлган кимсаларни, сизга худди (келиб қолган) ўлимдан (қўрқиб,) беҳуш бўлиб қолган кимсанинг қараши билан қарашаётганини кўрурсиз. (Ҳолбуки), улар учун лойиқроқ эди'}, {'chapter': 47, 'verse': 21, 'text': 'طَاعَةٞ وَقَوۡلٞ مَّعۡرُوفٞۚ فَإِذَا عَزَمَ ٱلۡأَمۡرُ فَلَوۡ صَدَقُواْ ٱللَّهَ لَكَانَ خَيۡرٗا لَّهُمۡ\n(Сизга) итоат этиш ва яхши сўз (сўзлаш лойиқроқ эди). Энди қачон иш — жанг муқаррар бўлса, у ҳолда агар улар Аллоҳга (иймон келтириш ва Унинг жанг қилиш ҳақидаги амрига итоат этишда) содиқ-ростгўй бўлганларида, албатта ўзлари учун яхшироқ бўлур эди'}, {'chapter': 47, 'verse': 22, 'text': 'فَهَلۡ عَسَيۡتُمۡ إِن تَوَلَّيۡتُمۡ أَن تُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَتُقَطِّعُوٓاْ أَرۡحَامَكُمۡ\nАгар (иймондан) юз ўгирсангизлар, яқин-ки, сизлар Ерда бузғунчилик қилурсизлар ва қариндош-уруғларингиз (билан ҳам алоқаларингиз)ни узурсизлар'}, {'chapter': 47, 'verse': 23, 'text': 'أُوْلَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمۡ وَأَعۡمَىٰٓ أَبۡصَٰرَهُمۡ\nУндай кимсаларни эса Аллоҳ лаънатлагандир, бас, уларнинг (қулоқларини панд-насиҳат эшитишдан) кар, кўзларини эса (Тўғри Йўлни кўра олмайдиган) кўр қилиб қўйгандир'}, {'chapter': 47, 'verse': 24, 'text': 'أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَ أَمۡ عَلَىٰ قُلُوبٍ أَقۡفَالُهَآ\nАхир улар Қуръон ҳақида фикр юритмайдиларми?! Балки дилларида қулфлари бордир, (шунинг учун у дилларга Қуръон нури етмаётгандир)'}, {'chapter': 47, 'verse': 25, 'text': 'إِنَّ ٱلَّذِينَ ٱرۡتَدُّواْ عَلَىٰٓ أَدۡبَٰرِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَى ٱلشَّيۡطَٰنُ سَوَّلَ لَهُمۡ وَأَمۡلَىٰ لَهُمۡ\nАлбатта ўзларига ҳидоят (йўли) аниқ-равшан бўлганидан кейин яна ортларига (куфрга) қайтиб кетган кимсаларга (бу ишларини) шайтон чиройли қилиб кўрсатди ва улар учун (пуч орзу-хаёларни) узун қилиб қўйди'}, {'chapter': 47, 'verse': 26, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لِلَّذِينَ كَرِهُواْ مَا نَزَّلَ ٱللَّهُ سَنُطِيعُكُمۡ فِي بَعۡضِ ٱلۡأَمۡرِۖ وَٱللَّهُ يَعۡلَمُ إِسۡرَارَهُمۡ\nБунга (яъни, куфрга қайтиб кетишларига) сабаб, уларнинг Аллоҳ нозил қилган нарсани (Қуръонни) ёмон кўрган кимсаларга: «Биз айрим ишларда сизларга итоат этамиз», деганларидир. Ҳолбуки, Аллоҳ уларнинг яширган (сирлари)ни билур'}, {'chapter': 47, 'verse': 27, 'text': 'فَكَيۡفَ إِذَا تَوَفَّتۡهُمُ ٱلۡمَلَـٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ\nЭнди, фаришталар уларнинг юзлари ва орқаларига уриб, жонларини олар вақтида, (уларнинг ҳоллари) қандай бўлур'}, {'chapter': 47, 'verse': 28, 'text': 'ذَٰلِكَ بِأَنَّهُمُ ٱتَّبَعُواْ مَآ أَسۡخَطَ ٱللَّهَ وَكَرِهُواْ رِضۡوَٰنَهُۥ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ\nБунга (яъни, ўлим соатидаги бундай азобга) сабаб, уларнинг Аллоҳни ғазаблантирган нарсага (яъни, мунофиқлик йўлига) эргашиб, У зотнинг ризолигини (яъни, мўминлар билан бирга кофирларга қарши жанг қилишни) ёмон кўрганларидир. Бас, (Аллоҳ) уларнинг (қилган яхши) амалларини ҳам беҳуда кетказди'}, {'chapter': 47, 'verse': 29, 'text': 'أَمۡ حَسِبَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ أَن لَّن يُخۡرِجَ ٱللَّهُ أَضۡغَٰنَهُمۡ\nБалки дилларида мараз-нифоқ бўлган кимсалар Аллоҳ уларнинг (дилларидаги мўминларга нисбатан бўлган) адоватларини ошкор қилмас, деб ўйлагандирлар'}, {'chapter': 47, 'verse': 30, 'text': 'وَلَوۡ نَشَآءُ لَأَرَيۡنَٰكَهُمۡ فَلَعَرَفۡتَهُم بِسِيمَٰهُمۡۚ وَلَتَعۡرِفَنَّهُمۡ فِي لَحۡنِ ٱلۡقَوۡلِۚ وَٱللَّهُ يَعۡلَمُ أَعۡمَٰلَكُمۡ\n(Эй Муҳаммад алайҳис-салоту вас-салом), агар Биз хоҳласак, албатта сизга уларни (мунофиқларни) кўрсатиб-танитиб қўйган бўлур эдик, у ҳолда сиз уларни (Биз белгилаб қўйган) белги-аломатларидан таниб олган бўлур эдингиз. (Қасамки, энди) албатта сиз уларни (улар сўзлаётган — зоҳири иймонга далолат қиладиган, ботини эса куфр бўлган) сўз оҳанг — услубидан таниб олурсиз. Аллоҳ сизларнинг (барча) амалларингизни билиб турур'}, {'chapter': 47, 'verse': 31, 'text': 'وَلَنَبۡلُوَنَّكُمۡ حَتَّىٰ نَعۡلَمَ ٱلۡمُجَٰهِدِينَ مِنكُمۡ وَٱلصَّـٰبِرِينَ وَنَبۡلُوَاْ أَخۡبَارَكُمۡ\nАлбатта Биз, то сизларнинг орангиздаги (Бизнинг йўлимизда молу-жонлари билан) жиҳод қилгувчи ва (яхши-ёмон кунларда) сабр қилгувчи зотларни билгунимизча, ҳамда сизларнинг ҳоли-хабарларингизни текшириб — юзага чиқаргунимизча, сизларни имтиҳон қилурмиз'}, {'chapter': 47, 'verse': 32, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ وَشَآقُّواْ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَىٰ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗا وَسَيُحۡبِطُ أَعۡمَٰلَهُمۡ\nАлбатта кофир бўлган ва (ўзгаларни ҳам) Аллоҳнинг йўлидан тўсган ҳамда ўзларига ҳидоят (йўли) аниқ-равшан бўлганидан кейин яна пайғамбарга қаршилик қилган кимсалар, Аллоҳга бирон зиён етказа олмаслар ва (Аллоҳ) уларнинг (пайғамбарга қаршилик қилиш йўлидаги барча) амалларини беҳуда кетказур'}, {'chapter': 47, 'verse': 33, 'text': '۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَلَا تُبۡطِلُوٓاْ أَعۡمَٰلَكُمۡ\nЭй мўминлар, Аллоҳга итоат этингиз ва пайғамбарга итоат этингиз! Ва (куфр ё мунофиқлик йўлига кириш билан қилган) амалларингизни ботил-бефойда қилиб қўйманглар'}, {'chapter': 47, 'verse': 34, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ ثُمَّ مَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡ\nАлбатта кофир бўлган ва (ўзгаларни ҳам) Аллоҳ йўлидан тўсган, сўнгра кофир ҳолларида ўлган кимсаларни Аллоҳ ҳаргиз мағфират қилмас'}, {'chapter': 47, 'verse': 35, 'text': 'فَلَا تَهِنُواْ وَتَدۡعُوٓاْ إِلَى ٱلسَّلۡمِ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ وَٱللَّهُ مَعَكُمۡ وَلَن يَتِرَكُمۡ أَعۡمَٰلَكُمۡ\nБас, (эй мўминлар), сизлар (у кофирларга қарши жанг қилаётганингизда ҳаргиз) сусткашлик қилмангиз ва ўзларингиз устун-ғолиб бўлган ҳолларингизда (уларни) ярашга ҳам чақирмангиз! Аллоҳ сизлар билан биргадир ва У ҳаргиз қилган амалларингизнинг савоб-мукофотини) камайтирмас'}, {'chapter': 47, 'verse': 36, 'text': 'إِنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ يُؤۡتِكُمۡ أُجُورَكُمۡ وَلَا يَسۡـَٔلۡكُمۡ أَمۡوَٰلَكُمۡ\nАлбатта бу ҳаёти дунё фақат (бир нафаслик) ўйин-кулгидир. Агар сизлар иймон келтирсангизлар ва тақво эгалари бўлсангизлар У зот сизларга ажр-мукофотларингизни берур ва сизлардан мол-дунёларингизни сўрамас'}, {'chapter': 47, 'verse': 37, 'text': 'إِن يَسۡـَٔلۡكُمُوهَا فَيُحۡفِكُمۡ تَبۡخَلُواْ وَيُخۡرِجۡ أَضۡغَٰنَكُمۡ\nАгар (Аллоҳ) сизлардан (мол-дунёларингизни) сўраб, сизларни қийнайдиган бўлса, сизлар бахиллик қилурсизлар ва У сизларнинг (дилларингиздаги) кек-адоватларингизни ошкор қилур'}, {'chapter': 47, 'verse': 38, 'text': 'هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ تُدۡعَوۡنَ لِتُنفِقُواْ فِي سَبِيلِ ٱللَّهِ فَمِنكُم مَّن يَبۡخَلُۖ وَمَن يَبۡخَلۡ فَإِنَّمَا يَبۡخَلُ عَن نَّفۡسِهِۦۚ وَٱللَّهُ ٱلۡغَنِيُّ وَأَنتُمُ ٱلۡفُقَرَآءُۚ وَإِن تَتَوَلَّوۡاْ يَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ ثُمَّ لَا يَكُونُوٓاْ أَمۡثَٰلَكُم\nМана, сизлар шундай кишилардирсизки, Аллоҳ йўлида инфоқ-эҳсон қилиш учун даъват қилинурсизлар. Бас, (аниқки), сизларнинг орангизда бахиллик қиладиган кимсалар ҳам бордир. Ким бахиллик қилса, бас, албатта у фақат ўз зиёнига бахиллик қилур. (Чунки у ажр-савобдан маҳрум бўлур). Аллоҳ (сизларнинг хайр-эҳсонларингиздан) бой-беҳожат, сизлар эса (У зотнинг ажр-савобига) фақир-муҳтождирсизлар. Агар сизлар (Аллоҳга итоат этишдан) юз ўгириб кетсангизлар, У зот (ўрнингизга) сизлардан бошқа бир қавмни алмаштириб қўюр, сўнгра улар сизларга ўхшаган бўлмаслар, (балки Аллоҳга тоат-ибодат қилурлар)'}, {'chapter': 48, 'verse': 1, 'text': 'إِنَّا فَتَحۡنَا لَكَ فَتۡحٗا مُّبِينٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, Биз сизга очиқ-равшан фатҳ-ғалаба ато этдик'}, {'chapter': 48, 'verse': 2, 'text': 'لِّيَغۡفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكَ وَيَهۡدِيَكَ صِرَٰطٗا مُّسۡتَقِيمٗا\nТоки Аллоҳ сизнинг гуноҳингиздан илгари ўтган ва кейин кел(ади)ган нарсалар (барча гуноҳларингиз)ни мағфират қилиши учун ва сизга Ўз неъматини комил қилиб бериб, сизни Тўғри Йўлга ҳидоят қилиши учун'}, {'chapter': 48, 'verse': 3, 'text': 'وَيَنصُرَكَ ٱللَّهُ نَصۡرًا عَزِيزًا\nҳамда Аллоҳ сизга қудратли ёрдам бериши учун (сизга очиқ фатҳ ато этдик)'}, {'chapter': 48, 'verse': 4, 'text': 'هُوَ ٱلَّذِيٓ أَنزَلَ ٱلسَّكِينَةَ فِي قُلُوبِ ٱلۡمُؤۡمِنِينَ لِيَزۡدَادُوٓاْ إِيمَٰنٗا مَّعَ إِيمَٰنِهِمۡۗ وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا\nУ (Аллоҳ) ўз иймон-ишончларига яна ишонч қўшишлари учун мўминларнинг дилларига сакинат-ором туширган Зотдир. Осмонлар ва Ер қўшинлари (яъни, коинотдаги барча жонзот ва кучлар) ёлғиз Аллоҳникидир. Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 48, 'verse': 5, 'text': 'لِّيُدۡخِلَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَيُكَفِّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡۚ وَكَانَ ذَٰلِكَ عِندَ ٱللَّهِ فَوۡزًا عَظِيمٗا\nУ мўмин ва мўминаларни остидан дарёлар оқиб турадиган, улар мангу қоладиган жаннатларга киритиш учун ва улардан ёмонлик-гуноҳларини ўчириш учун (уларнинг дилларини Ўзи буюрган, кофирларга қарши жиҳод қилиш каби ҳукмлардан таскин-ором оладиган қилиб қўйди). Ва бу Аллоҳ наздида улуғ бахт бўлди'}, {'chapter': 48, 'verse': 6, 'text': 'وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ ٱلظَّآنِّينَ بِٱللَّهِ ظَنَّ ٱلسَّوۡءِۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۖ وَغَضِبَ ٱللَّهُ عَلَيۡهِمۡ وَلَعَنَهُمۡ وَأَعَدَّ لَهُمۡ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرٗا\nВа Аллоҳ ҳақида (У Ўз пайғамбарига ва мўминларга ғалаба бермайди, деб) ёмон гумон қилгувчи мунофиқ ва мунофиқаларни ҳамда мушрик ва мушрикаларни азоблаш (учун мўминларни уларга қарши жиҳод қилишга буюрди). Уларнинг устига ҳалокат балоси тушгувчидир. Аллоҳ улардан дарғазаб бўлди, уларни лаънатлади ва улар учун жаҳаннамни тайёрлаб қўйди. Нақадар ёмон жойдир у'}, {'chapter': 48, 'verse': 7, 'text': 'وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا\nОсмонлар ва Ер қўшинлари Аллоҳникидир. Аллоҳ қудрат ва ҳикмат соҳиби бўлган Зотдир'}, {'chapter': 48, 'verse': 8, 'text': 'إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, Биз сизни (Қиёмат Кунида барча умматлар устида) гувоҳлик бергувчи, (мўминларга жаннат ҳақида) хушхабар элтгувчи ва (у кофирларни дўзах азобидан) огоҳлантиргувчи қилиб юборгандирмиз'}, {'chapter': 48, 'verse': 9, 'text': 'لِّتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُۚ وَتُسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا\n(Эй инсонлар, Биз бу пайғамбарни) сизлар Аллоҳга ва Унинг пайғамбарига иймон келтиришларингиз учун ва У зотни улуғлаб, эҳтиром қилишларингиз ҳамда эрта-ю кеч Уни поклаб, тасбеҳ айтишларингиз учун (юбордик)'}, {'chapter': 48, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ ٱللَّهَ يَدُ ٱللَّهِ فَوۡقَ أَيۡدِيهِمۡۚ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفۡسِهِۦۖ وَمَنۡ أَوۡفَىٰ بِمَا عَٰهَدَ عَلَيۡهُ ٱللَّهَ فَسَيُؤۡتِيهِ أَجۡرًا عَظِيمٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, сизга байъат-қасамёд қиладиган зотлар ҳеч шак-шубҳасиз, Аллоҳга байъат қилурлар. Аллоҳнинг қўли уларнинг қўллари устида бўлур. Энди ким (ўз қасамёдини) бузса, бас, у фақат ўз зиёнига бузур. Ва ким Аллоҳ билан аҳд-паймон қилган нарсасига вафо қилса, у ҳолда (Аллоҳ) унга улуғ ажр-мукофот ато этур'}, {'chapter': 48, 'verse': 11, 'text': 'سَيَقُولُ لَكَ ٱلۡمُخَلَّفُونَ مِنَ ٱلۡأَعۡرَابِ شَغَلَتۡنَآ أَمۡوَٰلُنَا وَأَهۡلُونَا فَٱسۡتَغۡفِرۡ لَنَاۚ يَقُولُونَ بِأَلۡسِنَتِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ قُلۡ فَمَن يَمۡلِكُ لَكُم مِّنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ بِكُمۡ ضَرًّا أَوۡ أَرَادَ بِكُمۡ نَفۡعَۢاۚ بَلۡ كَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرَۢا\n(Эй Муҳаммад алайҳис-салоту вас-салом), ҳали (Мадинага қайтганингиздан кейин) сизга аъробийлардан бўлган (сиз билан бирга Умра сафарига чиқмасдан) қолган (мунофиқ) кимсалар «Бизларни мол-мулкларимиз ва аҳли-оилаларимиз машғул қилиб қўйди, (шунинг учун сен билан бирга сафарга чиқа олмадик). Энди ўзинг бизлар учун (Аллоҳдан) мағфират сўрагин», дерлар. Улар тилларида дилларида бўлмаган нарсани — ёлғонни айтурлар. Айтинг: «Агар (Аллоҳ) сизларга бирон зиён (етказиш)ни истаса, ёки У сизларга бирон манфаат (етказиш)ни истаса, у ҳолда ким сизлар учун Аллоҳ томонидан бўлган бирон нарса(ни дафъ қилишга эга бўла олур?! Йўқ, Аллоҳ сизлар қилаётган амалларингиздан огоҳ бўлган Зотдир)'}, {'chapter': 48, 'verse': 12, 'text': 'بَلۡ ظَنَنتُمۡ أَن لَّن يَنقَلِبَ ٱلرَّسُولُ وَٱلۡمُؤۡمِنُونَ إِلَىٰٓ أَهۡلِيهِمۡ أَبَدٗا وَزُيِّنَ ذَٰلِكَ فِي قُلُوبِكُمۡ وَظَنَنتُمۡ ظَنَّ ٱلسَّوۡءِ وَكُنتُمۡ قَوۡمَۢا بُورٗا\nБалки сизлар пайғамбар ва мўминлар (бу сафарларидан) ўз аҳли-оилаларига ҳеч қачон қайтиб келмайдилар, деб ўйладинглар ва бу дилларингизга чиройли кўриниб, ёмон гумон қилдинглар ҳамда ҳалок бўлгувчи қавм бўлдинглар!»'}, {'chapter': 48, 'verse': 13, 'text': 'وَمَن لَّمۡ يُؤۡمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَعِيرٗا\nКим Аллоҳга ва Унинг пайғамбарига иймон келтирмаган бўлса, бас, албатта Биз ундай кофирлар учун дўзахни тайёрлаб қўйгандирмиз'}, {'chapter': 48, 'verse': 14, 'text': 'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا\nОсмонлар ва Ернинг мулки Аллоҳникидир, У Ўзи хоҳлаган кишиларни мағфират қилур ва Ўзи хоҳлаган кимсаларни азоблар, Аллоҳ мағфиратли ва меҳрибон бўлган Зотдир'}, {'chapter': 48, 'verse': 15, 'text': 'سَيَقُولُ ٱلۡمُخَلَّفُونَ إِذَا ٱنطَلَقۡتُمۡ إِلَىٰ مَغَانِمَ لِتَأۡخُذُوهَا ذَرُونَا نَتَّبِعۡكُمۡۖ يُرِيدُونَ أَن يُبَدِّلُواْ كَلَٰمَ ٱللَّهِۚ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمۡ قَالَ ٱللَّهُ مِن قَبۡلُۖ فَسَيَقُولُونَ بَلۡ تَحۡسُدُونَنَاۚ بَلۡ كَانُواْ لَا يَفۡقَهُونَ إِلَّا قَلِيلٗا\nҲали сизлар (Хайбар жангида қўлга киритган) ўлжаларини олиш учун кетаётган вақтингизда (жиҳодга чиқмасдан) қолган кимсалар: «Бизлар ҳам (ўша ўлжалардан олиш учун) сизларга эргашишимизга (йўл) қўйинглар», дерлар. Улар Аллоҳнинг каломини ўзгартирмоқчи бўладилар. Айтинг: «Ҳаргиз бизларга эргашмайсизлар! Аллоҳ илгари мана шундай дегандир». Энди улар: «Йуқ, сизлар бизларга ҳасад-бахиллик қилмоқдасизлар», дерлар. Йўқ, улар камдан-кам нарсаларнигина англарлар'}, {'chapter': 48, 'verse': 16, 'text': 'قُل لِّلۡمُخَلَّفِينَ مِنَ ٱلۡأَعۡرَابِ سَتُدۡعَوۡنَ إِلَىٰ قَوۡمٍ أُوْلِي بَأۡسٖ شَدِيدٖ تُقَٰتِلُونَهُمۡ أَوۡ يُسۡلِمُونَۖ فَإِن تُطِيعُواْ يُؤۡتِكُمُ ٱللَّهُ أَجۡرًا حَسَنٗاۖ وَإِن تَتَوَلَّوۡاْ كَمَا تَوَلَّيۡتُم مِّن قَبۡلُ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا\nАъробийлардан бўлган (жиҳодга чиқмасдан) қолган (мунофиқ) кимсаларга айтинг: «Яқинда сизлар куч-қувват эгалари булган бир қавм (билан жанг қилиш)га даъват этилурсизлар. (Ўшанда ё) улар билан уришурсизлар, ёки улар (жанг-жадалсиз) Исломга кирурлар. Бас, агар (ўша пайт сизларни кофир қавмга қарши жиҳод қилиш учун даъват этувчига) итоат этсангизлар, Аллоҳ сизларга гўзал ажр-мукофот ато этур. Агар илгари юз ўгириб кетганларингиздек юз ўгириб кетсангизлар, (Аллоҳ) сизларни аламли азоб-ла азоблар'}, {'chapter': 48, 'verse': 17, 'text': 'لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَمَن يَتَوَلَّ يُعَذِّبۡهُ عَذَابًا أَلِيمٗا\n(Жиҳодга чиқмай қолишдан) кўзи ожиз кишига хараж-танглик йўқдир. Чўлоққа хараж йўқдир, хастага хараж йўқдир (яъни, улар жиҳодга чиқмаганлари учун айбланмаслар). Ким Аллоҳ ва Унинг пайғамбарига итоат этса, У зот уни остидан дарёлар оқиб турадиган жаннатларга киритур. Ким юз ўгириб кетса, уни аламли азоб-ла азоблар'}, {'chapter': 48, 'verse': 18, 'text': '۞لَّقَدۡ رَضِيَ ٱللَّهُ عَنِ ٱلۡمُؤۡمِنِينَ إِذۡ يُبَايِعُونَكَ تَحۡتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِمۡ فَأَنزَلَ ٱلسَّكِينَةَ عَلَيۡهِمۡ وَأَثَٰبَهُمۡ فَتۡحٗا قَرِيبٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, Аллоҳ мўминлардан — улар дарахт остида сизга байъат қилаётган вақтларида — рози бўлди. Бас, У зот уларнинг дилларидаги нарса (садоқат ва вафо)ни билиб, уларга сакинат — ором туширди ва уларни (Хайбар жангида эришиладиган) яқин ғалаба билан мукофотлади'}, {'chapter': 48, 'verse': 19, 'text': 'وَمَغَانِمَ كَثِيرَةٗ يَأۡخُذُونَهَاۗ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا\nВа ўзлари(гина) оладиган кўпдан-кўп ўлжалар (билан мукофотлади). Аллоҳ қудрат ва ҳикмат соҳиби бўлган Зотдир'}, {'chapter': 48, 'verse': 20, 'text': 'وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِيرَةٗ تَأۡخُذُونَهَا فَعَجَّلَ لَكُمۡ هَٰذِهِۦ وَكَفَّ أَيۡدِيَ ٱلنَّاسِ عَنكُمۡ وَلِتَكُونَ ءَايَةٗ لِّلۡمُؤۡمِنِينَ وَيَهۡدِيَكُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا\n(Эй мўминлар), Аллоҳ сизларга ўзларингиз оладиган кўпдан-кўп ўлжалар ваъда қилди. Энди мана бу (Хайбар жангида қўлга киритадиган ўлжаларингиз)ни эса нақд қилиб қўйди ва (сизларга душман бўлган) одамларнинг қўлларини сизлар(га бирон зиён етказиш)дан тўсиб қўйди. Токи (бу неъматлар) мўминлар учун оят-ибрат бўлгай ва (Аллоҳ) сизларни Тўғри Йўлга ҳидоят қилгай'}, {'chapter': 48, 'verse': 21, 'text': 'وَأُخۡرَىٰ لَمۡ تَقۡدِرُواْ عَلَيۡهَا قَدۡ أَحَاطَ ٱللَّهُ بِهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا\nЯна бошқа сизлар ҳали-ҳануз қодир бўлмаган (ўлжа)лар ҳам борки, Аллоҳ уларни (сизларнинг ўлжаларингиз эканини Ўз илми илоҳийси билан) иҳота қилиб олгандир. Аллоҳ барча нарсага қодир бўлган Зотдир'}, {'chapter': 48, 'verse': 22, 'text': 'وَلَوۡ قَٰتَلَكُمُ ٱلَّذِينَ كَفَرُواْ لَوَلَّوُاْ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا\nАгар (Макка аҳлидан) кофир бўлган кимсалар (сулҳ тузиш ўрнига) сизлар билан жанг қилганларида, албатта ортларига қараб қочган бўлур эдилар. Сўнгра улар на бир дўст ва на бир ёрдамчи топарлар'}, {'chapter': 48, 'verse': 23, 'text': 'سُنَّةَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا\nБу, яъни мўминлар зафар топиб, кофирлар мағлуб бўлиши Аллоҳнинг йўли — қонуни бўлиб, илгари ҳам ўтгандир. Аллоҳнинг йўлини эса ҳаргиз ўзгартира олмассиз'}, {'chapter': 48, 'verse': 24, 'text': 'وَهُوَ ٱلَّذِي كَفَّ أَيۡدِيَهُمۡ عَنكُمۡ وَأَيۡدِيَكُمۡ عَنۡهُم بِبَطۡنِ مَكَّةَ مِنۢ بَعۡدِ أَنۡ أَظۡفَرَكُمۡ عَلَيۡهِمۡۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا\nУ (Аллоҳ) Макканинг ичида (яъни, Ҳудайбийяда) сизларни уларнинг (Макка мушрикларининг) устига ғолиб қилганидан кейин уларнинг қўлларини сизлардан, сизларнинг қўлларингизни улардан тўсган (яъни, ўрталарингизда сулҳ пайдо қилган) Зотдир. Аллоҳ қилаётган амалларингизни кўриб тургувчи бўлган Зотдир'}, {'chapter': 48, 'verse': 25, 'text': 'هُمُ ٱلَّذِينَ كَفَرُواْ وَصَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَٱلۡهَدۡيَ مَعۡكُوفًا أَن يَبۡلُغَ مَحِلَّهُۥۚ وَلَوۡلَا رِجَالٞ مُّؤۡمِنُونَ وَنِسَآءٞ مُّؤۡمِنَٰتٞ لَّمۡ تَعۡلَمُوهُمۡ أَن تَطَـُٔوهُمۡ فَتُصِيبَكُم مِّنۡهُم مَّعَرَّةُۢ بِغَيۡرِ عِلۡمٖۖ لِّيُدۡخِلَ ٱللَّهُ فِي رَحۡمَتِهِۦ مَن يَشَآءُۚ لَوۡ تَزَيَّلُواْ لَعَذَّبۡنَا ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابًا أَلِيمًا\nУлар (Макка мушриклари) кофир бўлган ва сизларни Масжидул-Ҳаромдан, ҳадя (қурбонлик қилиш учун олган ҳайвонларингиз)ни эса маҳбус бўлган ҳолида, ўз жойига етишдан тўсган кимсалардир. Агар (Маккада) сизлар билмаган мўмин кишилар ва мўмина аёллар бўлмаса эди — сизлар уларни билмаган ҳолингизда (мушриклардан деб ўйлаб) бостириб — ҳалок қилиб қўйиб, улар сабабли сизларга ор-гуноҳ етмаса эди, (Аллоҳ сизларни Маккага юриш қилишдан тўсмаган бўлур эди). Аллоҳ Ўзи хоҳлаган кишиларни Ўз раҳматига дохил қилиш учун (яъни, уларни мушриклар қаторида ҳалок бўлиб кетишларидан асраш учун сизларни жанг қилишдан тўсди). Агар (мўминлар) ажралиб олганларида, албатта улардан (яъни, Макка аҳлидан) кофир бўлган кимсаларни аламли азоб-ла азоблаган бўлур эдик'}, {'chapter': 48, 'verse': 26, 'text': 'إِذۡ جَعَلَ ٱلَّذِينَ كَفَرُواْ فِي قُلُوبِهِمُ ٱلۡحَمِيَّةَ حَمِيَّةَ ٱلۡجَٰهِلِيَّةِ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَلۡزَمَهُمۡ كَلِمَةَ ٱلتَّقۡوَىٰ وَكَانُوٓاْ أَحَقَّ بِهَا وَأَهۡلَهَاۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا\nЎшанда кофир бўлган кимсалар дилларига қизиққонликни — динсизлик қизиққонлигини солганларида (яъни, Муҳаммад алайҳис-салоту вас-саломнинг пайғамбар эканликларини инкор этишиб, мусулмонларни Маккага киришдан тўсганларида) Аллоҳ Ўз пайғамбарининг ва мўминларнинг устига Ўз сакинат — оромини туширди ва уларга тақво калимасини (яъни, «Ла илаҳа иллаллоҳ» калимасини) лозим қилди. Улар ўша (калима)га жуда ҳақдор ва аҳл-лойиқ эдилар. Аллоҳ барча нарсани билгувчи бўлган Зотдир'}, {'chapter': 48, 'verse': 27, 'text': 'لَّقَدۡ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءۡيَا بِٱلۡحَقِّۖ لَتَدۡخُلُنَّ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ مُحَلِّقِينَ رُءُوسَكُمۡ وَمُقَصِّرِينَ لَا تَخَافُونَۖ فَعَلِمَ مَا لَمۡ تَعۡلَمُواْ فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتۡحٗا قَرِيبًا\nҚасамки, Аллоҳ Ўз пайғамбарига (у киши кўрган) тушни ҳаққи-рост қилди: албатта сизлар (эй мўминлар), иншоаллоҳ Масжидул-Харомга тинч-омон бошларингизни (сочларингизни) қирдирган ва (ёки) қисқартирган ҳолларингизда хавф-хатарсиз кирурсизлар. Бас, (Аллоҳ) сизлар билмаган нарсани билиб, ундан (Макка фатҳидан) олдин яқин бир ғалабани (Хайбар жангидаги ғалабани муяссар) қилди'}, {'chapter': 48, 'verse': 28, 'text': 'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا\nУ (Аллоҳ) Ўз пайғамбари (Муҳаммад алайҳис-салоту вас-салом)ни ҳидоят ва Ҳақ дин (Ислом) билан, у (дин)ни барча (дин)ларга ғолиб-устун қилиш учун юборган Зотдир. Аллоҳнинг Ўзи (ушбу ваъдасининг рўёбга чиқишига) етарли гувоҳдир'}, {'chapter': 48, 'verse': 29, 'text': 'مُّحَمَّدٞ رَّسُولُ ٱللَّهِۚ وَٱلَّذِينَ مَعَهُۥٓ أَشِدَّآءُ عَلَى ٱلۡكُفَّارِ رُحَمَآءُ بَيۡنَهُمۡۖ تَرَىٰهُمۡ رُكَّعٗا سُجَّدٗا يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗاۖ سِيمَاهُمۡ فِي وُجُوهِهِم مِّنۡ أَثَرِ ٱلسُّجُودِۚ ذَٰلِكَ مَثَلُهُمۡ فِي ٱلتَّوۡرَىٰةِۚ وَمَثَلُهُمۡ فِي ٱلۡإِنجِيلِ كَزَرۡعٍ أَخۡرَجَ شَطۡـَٔهُۥ فَـَٔازَرَهُۥ فَٱسۡتَغۡلَظَ فَٱسۡتَوَىٰ عَلَىٰ سُوقِهِۦ يُعۡجِبُ ٱلزُّرَّاعَ لِيَغِيظَ بِهِمُ ٱلۡكُفَّارَۗ وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِنۡهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمَۢا\nМуҳаммад Аллоҳнинг пайғамбаридир. У билан бирга бўлган (мўмин)лар кофирларга қаҳрли, ўз ораларида (мўминлар билан) эса раҳм-шафқатлидирлар. Уларни (мудом) Аллоҳдан фазл-марҳамат ва ризолик тилаб рукуъ, сужуд қилаётган ҳолларида кўрурсиз. Уларнинг юзларида сажда изидан (қолган) белги-аломатлари бордир. Мана шу (яъни, кофирларга қаҳрли бўлиш, мўминларга меҳрибонлик ва кўп намоз ўқиб, рукуъ-сажда қилиш) уларнинг Тавротдаги мисолларидир. (Яъни, Тавротда ҳам Аллоҳ таоло мўминларни мана шундай сифатлар билан сифатлагандир). Уларнинг Инжилдаги мисоллари эса худди бир шохлар чиқариб, қувватга киргач, йўғонлашиб, ўз новдасида тик турган, деҳқонларни лол қолдирадиган ўсимликка ўхшайди. (Мўминларнинг аввал-бошда заиф-озчилик бўлишиб, кейин аста-секин кўпайиб, кучга тўлиб кетишлари Инжилда юқорида зикр қилинганидек ўсимликка ўхшатилиши) улар сабабли кофирларни хафа қилиш учундир. Аллоҳ (мўминлардан) иймон келтириб, яхши амаллар қилган зотларга мағфират ва улуғ ажр-мукофот ваъда қилгандир'}, {'chapter': 49, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُقَدِّمُواْ بَيۡنَ يَدَيِ ٱللَّهِ وَرَسُولِهِۦۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ\nЭй мўминлар, сизлар Аллоҳ ва Унинг пайғамбари олдида (яъни, иккисининг изнисиз бирон сўз ёки ишга) қадам босманглар! Ва Аллоҳдан қўрқинглар! Албатта Аллоҳ эшитгувчи, билгувчидир'}, {'chapter': 49, 'verse': 2, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَرۡفَعُوٓاْ أَصۡوَٰتَكُمۡ فَوۡقَ صَوۡتِ ٱلنَّبِيِّ وَلَا تَجۡهَرُواْ لَهُۥ بِٱلۡقَوۡلِ كَجَهۡرِ بَعۡضِكُمۡ لِبَعۡضٍ أَن تَحۡبَطَ أَعۡمَٰلُكُمۡ وَأَنتُمۡ لَا تَشۡعُرُونَ\nЭй мўминлар, (токи қилган яхши) амалларингиз ўзларингиз сезмаган ҳолларингизда беҳуда-бефойда бўлиб қолмаслиги учун сизлар (пайғамбар билан сўзлашган пайтларингизда) овозларингизни пайғамбарнинг овозидан юқори кўтарманглар ва унга бир-бирларингизга очиқ (дағал сўз) қилгандек очиқ-дағал сўз қилманглар'}, {'chapter': 49, 'verse': 3, 'text': 'إِنَّ ٱلَّذِينَ يَغُضُّونَ أَصۡوَٰتَهُمۡ عِندَ رَسُولِ ٱللَّهِ أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱمۡتَحَنَ ٱللَّهُ قُلُوبَهُمۡ لِلتَّقۡوَىٰۚ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٌ\nАлбатта Аллоҳнинг пайғамбари ҳузурида овозларини паст қилган зотлар — ана ўшалар Аллоҳ дилларини тақво учун имтиҳон қилган (яъни, тақво имтиҳонидан ўтган) зотлардир. Улар учун мағфират ва улуғ ажр-мукофот бордир'}, {'chapter': 49, 'verse': 4, 'text': 'إِنَّ ٱلَّذِينَ يُنَادُونَكَ مِن وَرَآءِ ٱلۡحُجُرَٰتِ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта (сизнинг аҳли аёлларингиз истиқомат қиладиган) ҳужралар ортидан туриб сизни (бақириб) чақирадиган кимсаларнинг кўплари ақлсиз кимсалардир'}, {'chapter': 49, 'verse': 5, 'text': 'وَلَوۡ أَنَّهُمۡ صَبَرُواْ حَتَّىٰ تَخۡرُجَ إِلَيۡهِمۡ لَكَانَ خَيۡرٗا لَّهُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта (сизни чақириб, безовта этмасдан) сабр қилганларида, албатта ўзлари учун яхшироқ бўлур эди. Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 49, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن جَآءَكُمۡ فَاسِقُۢ بِنَبَإٖ فَتَبَيَّنُوٓاْ أَن تُصِيبُواْ قَوۡمَۢا بِجَهَٰلَةٖ فَتُصۡبِحُواْ عَلَىٰ مَا فَعَلۡتُمۡ نَٰدِمِينَ\nЭй муминлар, агар сизларга бир фосиқ кимса бирон хабар келтирса, сизлар (ҳақиқий аҳволни) билмаган ҳолингизда, бирон қавмга мусибат етказиб қўйиб, қилган ишларингизга афсус-надомат чекиб қолмасликларингиз учун (у фосиқ кимса олиб келган хабарни) аниқлаб-текшириб кўринглар'}, {'chapter': 49, 'verse': 7, 'text': 'وَٱعۡلَمُوٓاْ أَنَّ فِيكُمۡ رَسُولَ ٱللَّهِۚ لَوۡ يُطِيعُكُمۡ فِي كَثِيرٖ مِّنَ ٱلۡأَمۡرِ لَعَنِتُّمۡ وَلَٰكِنَّ ٱللَّهَ حَبَّبَ إِلَيۡكُمُ ٱلۡإِيمَٰنَ وَزَيَّنَهُۥ فِي قُلُوبِكُمۡ وَكَرَّهَ إِلَيۡكُمُ ٱلۡكُفۡرَ وَٱلۡفُسُوقَ وَٱلۡعِصۡيَانَۚ أُوْلَـٰٓئِكَ هُمُ ٱلرَّـٰشِدُونَ\nВа билингларки, ораларингизда Аллоҳнинг пайғамбари бор. Агар у кўп иш(лар)да сизларга (иймони комил бўлмаган жоҳил кимсаларга) бўйинсунса, (яъни, Аллоҳ томонидан ваҳий нозил бўлишини кутмасдан сизлар келтирган хабарларга ишонадиган бўлса), албатта сизлар бало-мусибатларга дучор бўлур эдингизлар. Лекин Аллоҳ сизларга (иймони комил, тақво имтиҳонидан ўтган зотларга иймонни суюкли қилди ва уни дилларингизга чиройли кўрсатди ҳамда сизларга куфрни, (Аллоҳ ва пайғамбарга) итоатсизликни ва исённи ёмон кўрсатиб қўйди. Ана ўшаларгина (яъни, иймон йўлида собитқадам бўлиб, куфр-исённи ёмон кўрган кишиларгина) Тўғри Йўлга юргувчи зотлардир'}, {'chapter': 49, 'verse': 8, 'text': 'فَضۡلٗا مِّنَ ٱللَّهِ وَنِعۡمَةٗۚ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\n(Бу) Аллоҳ томонидан бўлган фазлу марҳамат ва неъматдир. Аллоҳ билим ва ҳикмат Соҳибидир'}, {'chapter': 49, 'verse': 9, 'text': 'وَإِن طَآئِفَتَانِ مِنَ ٱلۡمُؤۡمِنِينَ ٱقۡتَتَلُواْ فَأَصۡلِحُواْ بَيۡنَهُمَاۖ فَإِنۢ بَغَتۡ إِحۡدَىٰهُمَا عَلَى ٱلۡأُخۡرَىٰ فَقَٰتِلُواْ ٱلَّتِي تَبۡغِي حَتَّىٰ تَفِيٓءَ إِلَىٰٓ أَمۡرِ ٱللَّهِۚ فَإِن فَآءَتۡ فَأَصۡلِحُواْ بَيۡنَهُمَا بِٱلۡعَدۡلِ وَأَقۡسِطُوٓاْۖ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ\n(Эй мўминлар), агар мўминлардан бўлган икки тоифа (бир-бирлари билан) урушиб қолсалар, дарҳол уларнинг ўртасини ўнглаб қўйинглар! Энди агар улардан бирови иккинчисининг устига тажовуз қилса, бас, то (тажовузкор тоифа) Аллоҳнинг амрига қайтгунича, сизлар тажовуз қилган (тоифа) билан урушинглар! Энди агар у (тоифа тажовузкорликдан) қайтса, сизлар дарҳол уларнинг ўртасини адолат билан ўнглаб қўйинглар. (Мудом) адолат қилинглар! Зеро, Аллоҳ адолат қилгувчиларни севар'}, {'chapter': 49, 'verse': 10, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ إِخۡوَةٞ فَأَصۡلِحُواْ بَيۡنَ أَخَوَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ\nМўминлар ҳеч шак-шубҳасиз, оға-инилардир. Бас, сизлар икки оға-инингизнинг ўртасини ўнглаб қўйинглар! Аллоҳдан қўрқинглар — шояд У зот томонидан бўладиган раҳматга эришсангизлар'}, {'chapter': 49, 'verse': 11, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَسۡخَرۡ قَوۡمٞ مِّن قَوۡمٍ عَسَىٰٓ أَن يَكُونُواْ خَيۡرٗا مِّنۡهُمۡ وَلَا نِسَآءٞ مِّن نِّسَآءٍ عَسَىٰٓ أَن يَكُنَّ خَيۡرٗا مِّنۡهُنَّۖ وَلَا تَلۡمِزُوٓاْ أَنفُسَكُمۡ وَلَا تَنَابَزُواْ بِٱلۡأَلۡقَٰبِۖ بِئۡسَ ٱلِٱسۡمُ ٱلۡفُسُوقُ بَعۡدَ ٱلۡإِيمَٰنِۚ وَمَن لَّمۡ يَتُبۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nЭй мўминлар, (сизлардан бўлган) бир қавм (бошқа) бир (мўмин) қавм устидан масхара қилиб кулмасин — эҳтимолки, (ўша масхара қилинган қавм) улардан яхшироқ бўлсалар. Яна (сизлардан бўлган) аёллар ҳам (бошқа мўмина) аёллар устидан (масхара қилиб кулмасинлар) эҳтимолки, (ўша масхара қилинган аёллар) улардан яхшироқ бўлсалар. Ўзларингизни (яъни, бир-бирларингизни) мазах қилманглар ва бир-бирларингизга лақаблар қўйиб олманглар! Иймондан кейин фосиқлик билан номланиш (яъни, мўмин кишининг юқорида манъ қилинган фосиқона ишлар билан ном чиқариши) нақадар ёмондир. Ким тавба қилмаса, ана ўшалар золим кимсаларнинг ўзидирлар'}, {'chapter': 49, 'verse': 12, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱجۡتَنِبُواْ كَثِيرٗا مِّنَ ٱلظَّنِّ إِنَّ بَعۡضَ ٱلظَّنِّ إِثۡمٞۖ وَلَا تَجَسَّسُواْ وَلَا يَغۡتَب بَّعۡضُكُم بَعۡضًاۚ أَيُحِبُّ أَحَدُكُمۡ أَن يَأۡكُلَ لَحۡمَ أَخِيهِ مَيۡتٗا فَكَرِهۡتُمُوهُۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ تَوَّابٞ رَّحِيمٞ\nЭй мўминлар, кўп гумон(лар)дан четланинглар! Чунки айрим гумон(лар) гуноҳдир! (Ўзгаларнинг айблари ортидан) жосуслик қилиб юрманглар ва айримларингиз айримларни ғийбат қилмасин! Сизлардан бирон киши ўзининг ўлган биродарининг гўштини ейишни яхши кўрурми?! Ана ёмон кўрдингизми?! (Бас, гуноҳи бундан-да ортиқ бўлган ғийбатни ҳам ёмон кўрингиз)! Аллоҳдан қўрқингиз! Албатта Аллоҳ тавбаларни қабул қилгувчи, меҳрибондир'}, {'chapter': 49, 'verse': 13, 'text': 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّا خَلَقۡنَٰكُم مِّن ذَكَرٖ وَأُنثَىٰ وَجَعَلۡنَٰكُمۡ شُعُوبٗا وَقَبَآئِلَ لِتَعَارَفُوٓاْۚ إِنَّ أَكۡرَمَكُمۡ عِندَ ٱللَّهِ أَتۡقَىٰكُمۡۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٞ\nЭй инсонлар, дарҳақиқат, Биз сизларни бир эркак (Одам) ва бир аёл (Ҳавво)дан яратдик ҳамда бир-бирларингиз билан танишинглар (дўст-биродар бўлишинглар) учун сизларни (турли-туман) халқлар ва қабила-элатлар қилиб қўйдик. Албатта сизларнинг Аллоҳ наздидаги энг ҳурматлироғингиз тақводорроғингиздир. Албатта Аллоҳ билгувчи ва огоҳдир'}, {'chapter': 49, 'verse': 14, 'text': '۞قَالَتِ ٱلۡأَعۡرَابُ ءَامَنَّاۖ قُل لَّمۡ تُؤۡمِنُواْ وَلَٰكِن قُولُوٓاْ أَسۡلَمۡنَا وَلَمَّا يَدۡخُلِ ٱلۡإِيمَٰنُ فِي قُلُوبِكُمۡۖ وَإِن تُطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ لَا يَلِتۡكُم مِّنۡ أَعۡمَٰلِكُمۡ شَيۡـًٔاۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nАъробийлар: «Иймон келтирдик», дедилар. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Сизлар иймон келтирганларингиз йўқ, лекин сизлар «Бўйинсундик», денглар, (чунки ҳали-ҳануз) иймон дилларингизга кирган эмасдир. Агар сизлар Аллоҳга ва Унинг пайғамбарига итоат этсангизлар, У зот сизларга (қилган яхши) амалларингиздан (яъни, уларнинг савобидан) бирон нарсани камитмас. Албатта Аллоҳ мағфиратли, меҳрибондир»'}, {'chapter': 49, 'verse': 15, 'text': 'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمۡ يَرۡتَابُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِۚ أُوْلَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ\nҲақиқий мўминлар фақат Аллоҳ ва Унинг пайғамбарига иймон келтириб, сўнгра (ҳеч қандай) шак-шубҳа қилмаган ва молу жонлари билан Аллоҳ йўлида курашган зотлардир. Ана ўшаларгина (ўз иймонларида) содиқ бўлган зотлардир'}, {'chapter': 49, 'verse': 16, 'text': 'قُلۡ أَتُعَلِّمُونَ ٱللَّهَ بِدِينِكُمۡ وَٱللَّهُ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом, у аъробийларга) айтинг: «Сизлар Аллоҳга динингларни (иймонларингни ҳақиқий эканлигини) билдирмоқчимисизлар?! Ҳолбуки, Аллоҳ осмонлардаги ва Ердаги бор нарсани билур. Аллоҳ барча нарсани билгувчидир!»'}, {'chapter': 49, 'verse': 17, 'text': 'يَمُنُّونَ عَلَيۡكَ أَنۡ أَسۡلَمُواْۖ قُل لَّا تَمُنُّواْ عَلَيَّ إِسۡلَٰمَكُمۖ بَلِ ٱللَّهُ يَمُنُّ عَلَيۡكُمۡ أَنۡ هَدَىٰكُمۡ لِلۡإِيمَٰنِ إِن كُنتُمۡ صَٰدِقِينَ\nУлар сизга мусулмон бўлганларини миннат қилурлар. Айтинг: «Сизлар менга мусулмон бўлганларингизни миннат қилманглар. Балки агар (иймонингларда) содиқ бўлсангизлар, Аллоҳ сизларни иймонга ҳидоят қилганини миннат қилур'}, {'chapter': 49, 'verse': 18, 'text': 'إِنَّ ٱللَّهَ يَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ بَصِيرُۢ بِمَا تَعۡمَلُونَ\nАлбатта Аллоҳ осмонлар ва Ердаги ғайб-сирларни билур. Аллоҳ қилаётган амалларингизни кўриб тургувчидир»'}, {'chapter': 50, 'verse': 1, 'text': 'قٓۚ وَٱلۡقُرۡءَانِ ٱلۡمَجِيدِ\nҚоф. (Эй Муҳаммад алайҳис-салоту вас-салом), Улуғ Қуръонга қасамки, (Биз сизни қайта тирилиш ҳақ эканлигидан огоҳлантиргувчи пайғамбар қилиб юборганимизда Макка аҳли иймон келтирмайди)'}, {'chapter': 50, 'verse': 2, 'text': 'بَلۡ عَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡ فَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا شَيۡءٌ عَجِيبٌ\nБалки уларга (фаришта эмас, балки) ўзларидан бўлган бир огоҳлантиргувчи келганидан ажабланиб, у кофирлар айтдилар: «Бу жуда қизиқ нарса-ку'}, {'chapter': 50, 'verse': 3, 'text': 'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗاۖ ذَٰلِكَ رَجۡعُۢ بَعِيدٞ\nБизлар ўлиб, тупроқ бўлиб қолган вақтимизда (яна ҳаётга қайтариламизми)?! Бу жуда узоқ (ақл бовар қилмайдиган) қайтишдир!»'}, {'chapter': 50, 'verse': 4, 'text': 'قَدۡ عَلِمۡنَا مَا تَنقُصُ ٱلۡأَرۡضُ مِنۡهُمۡۖ وَعِندَنَا كِتَٰبٌ حَفِيظُۢ\nАниқки, Биз Ер улар(нинг жасадлари)ни камайтираётганини (яъни, жасадлари чириб тупроққа айланиб кетаётганини) билдик. Ва Бизнинг ҳузуримизда (барча нарсани ўз саҳифаларида) сақлагувчи Китоб (Лавҳул-Маҳфуз) бордир, (демак уларни қайта тирилтириб, ҳисоб-китоб қилиш Биз учун қийин иш эмасдир)'}, {'chapter': 50, 'verse': 5, 'text': 'بَلۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَهُمۡ فِيٓ أَمۡرٖ مَّرِيجٍ\nЙўқ, уларга Ҳақ (Қуръон) келган вақтида уни ҳам ёлғон дедилар. Бас, улар бир ноаниқ ишга — ҳолга тушиб қолганлар. (Шунинг учун улар гоҳ Қуръонни «сеҳр» дейишса, гоҳ «шеър» дейишади, гоҳо эса «Аввалгилардан қолган афсона», дейдилар)'}, {'chapter': 50, 'verse': 6, 'text': 'أَفَلَمۡ يَنظُرُوٓاْ إِلَى ٱلسَّمَآءِ فَوۡقَهُمۡ كَيۡفَ بَنَيۡنَٰهَا وَزَيَّنَّـٰهَا وَمَا لَهَا مِن فُرُوجٖ\nАхир улар устларидаги осмонни — Биз уни қандай бино қилиб, (юлдузлар билан) безаб қўйганимизни ва унинг тешик-ёриқлари ҳам йўқ эканлигини кўрмадиларми'}, {'chapter': 50, 'verse': 7, 'text': 'وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجِۭ بَهِيجٖ\nЕрни эса ёйиқ — кенг қилдик ва унда тоғларни ўрнатиб қўйдик ҳамда унда (ўсимлик, мевалардан) ҳар турли гўзал жуфтларни ундириб-ўстирдик'}, {'chapter': 50, 'verse': 8, 'text': 'تَبۡصِرَةٗ وَذِكۡرَىٰ لِكُلِّ عَبۡدٖ مُّنِيبٖ\n(Парвардигорга) қайтгувчи ҳар бир бандага кўргазма ва эслатма-ибрат бўлсин учун (мана шундай қилдик)'}, {'chapter': 50, 'verse': 9, 'text': 'وَنَزَّلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ مُّبَٰرَكٗا فَأَنۢبَتۡنَا بِهِۦ جَنَّـٰتٖ وَحَبَّ ٱلۡحَصِيدِ\nБиз осмондан баракотли сув — ёмғир ёғдириб, унинг ёрдамида боғ-роғларни ва ўриб олингувчи дон-дунларни ўстириб-ундирдик'}, {'chapter': 50, 'verse': 10, 'text': 'وَٱلنَّخۡلَ بَاسِقَٰتٖ لَّهَا طَلۡعٞ نَّضِيدٞ\nҲамда терилган (мевали) новдалари бўлган баланд хурмоларни (ундириб-ўстирдик)'}, {'chapter': 50, 'verse': 11, 'text': 'رِّزۡقٗا لِّلۡعِبَادِۖ وَأَحۡيَيۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ ٱلۡخُرُوجُ\nБандаларга ризқ бўлсин учун (мана шундай қилдик). Яна у (сув) ёрдамида ўлик шаҳарни — Ерни тирилтирдик. (Ўликларнинг ўз қабрларидан қайта тирилиб) чиқишлари ҳам мана шундай бўлур'}, {'chapter': 50, 'verse': 12, 'text': 'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَأَصۡحَٰبُ ٱلرَّسِّ وَثَمُودُ\nУлардан (Макка кофирларидан) илгари Нуҳ қавми, қудуқ эгалари (аъни Шуъайб пайғамбарни ёлғончи қилганлари сабабли қудуққа қулаб ҳалок бўлган бир қавм), Самуд (қабиласи) (ўз пайғамбарларини) ёлғончи қилган эдилар'}, {'chapter': 50, 'verse': 13, 'text': 'وَعَادٞ وَفِرۡعَوۡنُ وَإِخۡوَٰنُ لُوطٖ\nОд (қабиласи), Фиръавн (ва унинг қавми) ва Лутнинг оға-инилари ҳам (ўз пайғамбарларини ёлғончи қилган эдилар)'}, {'chapter': 50, 'verse': 14, 'text': 'وَأَصۡحَٰبُ ٱلۡأَيۡكَةِ وَقَوۡمُ تُبَّعٖۚ كُلّٞ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِيدِ\nДарахтзор эгалари (яъни, Шуъайб пайғамбарнинг қавми) ва Туббаъ қавми ҳам (ўз пайғамбарларини ёлғончи қилган эдилар). (Уларнинг) барчалари пайғамбарларни ёлғончи қилишгач, Менинг ваъдам (яъни, азобим уларнинг устига) ҳақ-вожиб бўлди'}, {'chapter': 50, 'verse': 15, 'text': 'أَفَعَيِينَا بِٱلۡخَلۡقِ ٱلۡأَوَّلِۚ بَلۡ هُمۡ فِي لَبۡسٖ مِّنۡ خَلۡقٖ جَدِيدٖ\nАхир Биз аввалги яратишга (яъни, йўқдан бор қилишга) ожизлик қилдикми? Йўқ, (ожизлик қилмадик, демак тупроққа айланган жасадларга қайта жон ато этишга ҳам ожилик қилмаймиз), улар эса янги яралишдан (яъни, қайта тирилишдан) шак-шубҳададирлар'}, {'chapter': 50, 'verse': 16, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ وَنَعۡلَمُ مَا تُوَسۡوِسُ بِهِۦ نَفۡسُهُۥۖ وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنۡ حَبۡلِ ٱلۡوَرِيدِ\nАниқки, инсонни Биз яратганмиз, (демак), унинг нафси васваса қиладиган (яъни, кўнглидан ўтган барча) нарсаларни ҳам билурмиз, — Биз унга жон томиридан ҳам яқинроқдирмиз'}, {'chapter': 50, 'verse': 17, 'text': 'إِذۡ يَتَلَقَّى ٱلۡمُتَلَقِّيَانِ عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ قَعِيدٞ\nЗотан, ўнг ва чап (томон)да ўтирган икки қабул қилгувчи (ёзиб тургувчи фаришта инсоннинг айтган ва қилган барча яхши-ёмон сўз-амалларини) қабул қилиб-ёзиб турурлар'}, {'chapter': 50, 'verse': 18, 'text': 'مَّا يَلۡفِظُ مِن قَوۡلٍ إِلَّا لَدَيۡهِ رَقِيبٌ عَتِيدٞ\nУ бирон сўз талаффуз қилмас, магар (талаффуз қилса) унинг олдида ҳозиру-нозир бўлган бир кузатгувчи (фаришта у сўзни ёзиб олур)'}, {'chapter': 50, 'verse': 19, 'text': 'وَجَآءَتۡ سَكۡرَةُ ٱلۡمَوۡتِ بِٱلۡحَقِّۖ ذَٰلِكَ مَا كُنتَ مِنۡهُ تَحِيدُ\nМана, ўлим мастлиги (яъни, жон чиқар пайти) ҳаққи-рост (етиб келди. (Эй инсон), бу (ўлим) сен қочгувчи бўлган нарсадир'}, {'chapter': 50, 'verse': 20, 'text': 'وَنُفِخَ فِي ٱلصُّورِۚ ذَٰلِكَ يَوۡمُ ٱلۡوَعِيدِ\n(Қайта тирилишга даъват қилгувчи) Сур ҳам чалинди. Бу (кофирларга азоб) ваъда қилинган Кундир'}, {'chapter': 50, 'verse': 21, 'text': 'وَجَآءَتۡ كُلُّ نَفۡسٖ مَّعَهَا سَآئِقٞ وَشَهِيدٞ\nВа ҳар бир жон ўзи билан бирга (уни маҳшаргоҳга) ҳайдагувчи (бир фаришта) ва унинг қилиб ўтган амалларига) гувоҳлик бергувчи (бир фаришта) бўлган ҳолда келди'}, {'chapter': 50, 'verse': 22, 'text': 'لَّقَدۡ كُنتَ فِي غَفۡلَةٖ مِّنۡ هَٰذَا فَكَشَفۡنَا عَنكَ غِطَآءَكَ فَبَصَرُكَ ٱلۡيَوۡمَ حَدِيدٞ\n(Эй инсон), аниқки, сен бу (оғир Кун)дан ғафлатда эдинг. Бас, Биз сендан пардангни (яъни, мана бу Қиёмат Куни ҳақидаги шак-шубҳаларингни) очиб юбордик. Энди бу Кун сенинг кўзинг жуда ўткирдир'}, {'chapter': 50, 'verse': 23, 'text': 'وَقَالَ قَرِينُهُۥ هَٰذَا مَا لَدَيَّ عَتِيدٌ\nУнинг (кофирнинг) яқини-ҳамроҳи (бўлган, унинг қилиб ўтган гуноҳларини ёзиб-гувоҳлик бергувчи фаришта) деди: «Мана бу (номаи аъмол) менинг ҳузуримда ҳозиру нозир бўлган нарсадир»'}, {'chapter': 50, 'verse': 24, 'text': 'أَلۡقِيَا فِي جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٖ\n(Шундан кейин дўзах ходимларидан икки фариштага айтилур): «Барча (Ҳаққа қаршилик қилгувчи) қайсар, (Аллоҳнинг динига шак келтиргувчи) кофир-кўрнамакни жаҳаннамга ташланглар'}, {'chapter': 50, 'verse': 25, 'text': 'مَّنَّاعٖ لِّلۡخَيۡرِ مُعۡتَدٖ مُّرِيبٍ\nЯхшиликни манъ қилгувчи, зўравонни (жаҳаннамга ташланглар)'}, {'chapter': 50, 'verse': 26, 'text': 'ٱلَّذِي جَعَلَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَأَلۡقِيَاهُ فِي ٱلۡعَذَابِ ٱلشَّدِيدِ\n(Қай) бир кимса Аллоҳ билан бирга бошқа бир «илоҳ» қилиб олса (яъни, ёлғиз Аллоҳга бирон нарса ё кимсани шерик қилиб олса), бас, уни қаттиқ азобга ташланглар!»'}, {'chapter': 50, 'verse': 27, 'text': '۞قَالَ قَرِينُهُۥ رَبَّنَا مَآ أَطۡغَيۡتُهُۥ وَلَٰكِن كَانَ فِي ضَلَٰلِۭ بَعِيدٖ\nУнинг яқини (бўлган шайтон): «Парвардигоро, уни мен туғёнга солганим йўқ, лекин унинг ўзи (ҳақ йўлдан) йироқ залолатда бўлди», деди'}, {'chapter': 50, 'verse': 28, 'text': 'قَالَ لَا تَخۡتَصِمُواْ لَدَيَّ وَقَدۡ قَدَّمۡتُ إِلَيۡكُم بِٱلۡوَعِيدِ\n(Аллоҳ кофир ва унинг шайтонига) айтди: «Менинг даргоҳимда талашиб-тортишманглар, аниқки, Мен илгари сизларга (кофир бўлганларингиз сабабли Охиратда азобга гирифтор бўлишларингиз тўғрисида) ваъда қилганман'}, {'chapter': 50, 'verse': 29, 'text': 'مَا يُبَدَّلُ ٱلۡقَوۡلُ لَدَيَّ وَمَآ أَنَا۠ بِظَلَّـٰمٖ لِّلۡعَبِيدِ\nМенинг даргоҳимда (ваъда қилинган) сўз ўзгартирилмас ва Мен бандаларга зулм қилгувчи ҳам эмасдирман»'}, {'chapter': 50, 'verse': 30, 'text': 'يَوۡمَ نَقُولُ لِجَهَنَّمَ هَلِ ٱمۡتَلَأۡتِ وَتَقُولُ هَلۡ مِن مَّزِيدٖ\nУ Кунда Биз жаҳаннамга «Тўлиб битдингми?», дермиз, у эса, «Яна қўшимча борми?», дер'}, {'chapter': 50, 'verse': 31, 'text': 'وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِينَ غَيۡرَ بَعِيدٍ\nЖаннат тақводор зотларга йироқ бўлмаган (бир маконга) келтирилди (ва уларга дейилди)'}, {'chapter': 50, 'verse': 32, 'text': 'هَٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٖ\n«Мана шу (неъматлар) Сизларга — ҳар бир (Аллоҳга) қайтгувчи, (ўз аҳдини) сақлагувчи (риоя қилгувчи) кишиларга ваъда қилинаётган нарсалардир'}, {'chapter': 50, 'verse': 33, 'text': 'مَّنۡ خَشِيَ ٱلرَّحۡمَٰنَ بِٱلۡغَيۡبِ وَجَآءَ بِقَلۡبٖ مُّنِيبٍ\nҒайбдаги Раҳмондан қўрққан ва тавба-тазарруъ қилган дил билан келган кишиларга (ваъда қилинаётган нарсалардир)'}, {'chapter': 50, 'verse': 34, 'text': 'ٱدۡخُلُوهَا بِسَلَٰمٖۖ ذَٰلِكَ يَوۡمُ ٱلۡخُلُودِ\nУнга (жаннатга) тинч-омон кирингиз! Бу (кун) мангу (қоладиган) Кундир»'}, {'chapter': 50, 'verse': 35, 'text': 'لَهُم مَّا يَشَآءُونَ فِيهَا وَلَدَيۡنَا مَزِيدٞ\nУлар учун у жойда хоҳлаган нарсалари бордир. Ва яна Бизнинг даргоҳимиздаги қўшимча (иззат-икром)лар ҳам бордир'}, {'chapter': 50, 'verse': 36, 'text': 'وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَشَدُّ مِنۡهُم بَطۡشٗا فَنَقَّبُواْ فِي ٱلۡبِلَٰدِ هَلۡ مِن مَّحِيصٍ\nБиз улардан (Қурайш кофирларидан) илгари улардан кучли-қувватлироқ бўлган қанча аср-авлодни ҳалок қилдик. Бас, ўшалар (Биз бошларига туширган балодан қочиб), бирон нажот бормикан, деб шаҳарларда изғиб юрдилар, (лекин ҳалокатдан қочиб қутула олмадилар)'}, {'chapter': 50, 'verse': 37, 'text': 'إِنَّ فِي ذَٰلِكَ لَذِكۡرَىٰ لِمَن كَانَ لَهُۥ قَلۡبٌ أَوۡ أَلۡقَى ٱلسَّمۡعَ وَهُوَ شَهِيدٞ\nАлбатта бунда (огоҳ) қалб эгаси бўлган ёки ўзи ҳозир бўлган ҳолда (яъни, сидқидилдан) қулоқ тутган киши учун эслатма-ибратлар бордир'}, {'chapter': 50, 'verse': 38, 'text': 'وَلَقَدۡ خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ وَمَا مَسَّنَا مِن لُّغُوبٖ\nАниқки, Биз осмонлар ва Ерни ҳамда уларнинг ўртасидаги бор нарсани олти кунда яратдик ва Бизни ҳеч қандай чарчоқ ушлагани йўқ'}, {'chapter': 50, 'verse': 39, 'text': 'فَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ ٱلۡغُرُوبِ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), улар (кофирлар) айтаётган сўзларга сабр-тоқат қилинг ва қуёш чиқишидан илгари ва ботишидан аввал Парвардигорингизга ҳамду сано айтиш билан (У зотни) покланг — намоз ўқинг'}, {'chapter': 50, 'verse': 40, 'text': 'وَمِنَ ٱلَّيۡلِ فَسَبِّحۡهُ وَأَدۡبَٰرَ ٱلسُّجُودِ\nЯна кечанинг (бир қисмида) ва саждалар (яъни, намозлар) ортидан У зотни покланг'}, {'chapter': 50, 'verse': 41, 'text': 'وَٱسۡتَمِعۡ يَوۡمَ يُنَادِ ٱلۡمُنَادِ مِن مَّكَانٖ قَرِيبٖ\nВа жарчи (Исрофил фаришта) яқин бир макондан туриб жар соладиган Кунда (унинг нидосига) қулоқ тутинг'}, {'chapter': 50, 'verse': 42, 'text': 'يَوۡمَ يَسۡمَعُونَ ٱلصَّيۡحَةَ بِٱلۡحَقِّۚ ذَٰلِكَ يَوۡمُ ٱلۡخُرُوجِ\nУ Кунда улар (барча халойиқ) у даҳшатли қичқириқни ҳаққи-рост эшитурлар. Бу (қабрлардан тирилиб) чиқиш Кунидир'}, {'chapter': 50, 'verse': 43, 'text': 'إِنَّا نَحۡنُ نُحۡيِۦ وَنُمِيتُ وَإِلَيۡنَا ٱلۡمَصِيرُ\nАлбатта Биз Ўзимиз (барча жонзотга) ҳаёт берурмиз ва ўлим берурмиз. Ёлғиз Бизга қайтиш бордир'}, {'chapter': 50, 'verse': 44, 'text': 'يَوۡمَ تَشَقَّقُ ٱلۡأَرۡضُ عَنۡهُمۡ سِرَاعٗاۚ ذَٰلِكَ حَشۡرٌ عَلَيۡنَا يَسِيرٞ\nУ Кунда уларнинг устидаги Ер ёрилиб, (улар) шошган ҳолларида (қабрларидан чиқиб келурлар). Бу (яъни, Қиёмат Кунида барча халойиқни ҳисоб-китоб учун тўплаш) ёлғиз Бизгагина осон бўлган тўплашдир'}, {'chapter': 50, 'verse': 45, 'text': 'نَّحۡنُ أَعۡلَمُ بِمَا يَقُولُونَۖ وَمَآ أَنتَ عَلَيۡهِم بِجَبَّارٖۖ فَذَكِّرۡ بِٱلۡقُرۡءَانِ مَن يَخَافُ وَعِيدِ\nБиз улар (кофирлар) айтаётган сўзларни жуда яхши билурмиз. (Эй Муҳаммад алайҳис-салоту вас-салом), сиз уларнинг устида (дину-иймонга) зўрлагувчи эмассиз. Бас, ушбу Қуръон билан Менинг (кофирларни азоб-уқубатга гирифтор қилиш ҳақидаги) ваъдамдан қўрқадиган кишиларга панд-насиҳат қилинг'}, {'chapter': 51, 'verse': 1, 'text': 'وَٱلذَّـٰرِيَٰتِ ذَرۡوٗا\n(Булутларни) суриб кетгувчи (шамол)ларга қасам'}, {'chapter': 51, 'verse': 2, 'text': 'فَٱلۡحَٰمِلَٰتِ وِقۡرٗا\nЮк (ёмғир) ташигувчи (булут)ларга қасам'}, {'chapter': 51, 'verse': 3, 'text': 'فَٱلۡجَٰرِيَٰتِ يُسۡرٗا\n(Сув юзида) енгил сузиб боргувчи (кема)ларга қасам'}, {'chapter': 51, 'verse': 4, 'text': 'فَٱلۡمُقَسِّمَٰتِ أَمۡرًا\nВа (Аллоҳнинг амри билан барча) иш(лар)ни тақсимлагувчи (фаришта)ларга қасамки'}, {'chapter': 51, 'verse': 5, 'text': 'إِنَّمَا تُوعَدُونَ لَصَادِقٞ\nсизларга ваъда қилинаётган нарса (қайта тирилиш) шак-шубҳасиз, ҳаққи-ростдир'}, {'chapter': 51, 'verse': 6, 'text': 'وَإِنَّ ٱلدِّينَ لَوَٰقِعٞ\nВа албатта (Қиёмат Кунида ҳисоб-китоб қилинганидан кейин) жазо-мукофот воқеъ бўлгувчидир'}, {'chapter': 51, 'verse': 7, 'text': 'وَٱلسَّمَآءِ ذَاتِ ٱلۡحُبُكِ\n(Самодаги юлдузлар ҳаракат қиладиган) йўллар эгаси бўлган осмонга қасамки'}, {'chapter': 51, 'verse': 8, 'text': 'إِنَّكُمۡ لَفِي قَوۡلٖ مُّخۡتَلِفٖ\nалбатта сизлар (ушбу пайғамбар хусусида) хилма-хил сўз устидадирсизлар'}, {'chapter': 51, 'verse': 9, 'text': 'يُؤۡفَكُ عَنۡهُ مَنۡ أُفِكَ\nУндан (яъни, Қуръондан, пайғамбардан Аллоҳнинг илми азалийсида ҳидоят йўлидан) бурилган (ва саодатдан маҳрум қилинган) кимса бурилиб (юз ўгириб) кетур'}, {'chapter': 51, 'verse': 10, 'text': 'قُتِلَ ٱلۡخَرَّـٰصُونَ\n(Ҳақ пайғамбарни «ёлғончи, сеҳргар, шоир» дегувчи) ўша ёлғончиларга ўлим — лаънат бўлгай'}, {'chapter': 51, 'verse': 11, 'text': 'ٱلَّذِينَ هُمۡ فِي غَمۡرَةٖ سَاهُونَ\n(Жаҳолат) гирдобида адашиб юргувчи бўлган кимсаларга (ўлим бўлгай)'}, {'chapter': 51, 'verse': 12, 'text': 'يَسۡـَٔلُونَ أَيَّانَ يَوۡمُ ٱلدِّينِ\nУлар жазо (Қиёмат) Куни қачон бўлишини сўрарлар'}, {'chapter': 51, 'verse': 13, 'text': 'يَوۡمَ هُمۡ عَلَى ٱلنَّارِ يُفۡتَنُونَ\n(У Кун) улар дўзахда текшириладиган — ёндириладиган Кунда бўлур'}, {'chapter': 51, 'verse': 14, 'text': 'ذُوقُواْ فِتۡنَتَكُمۡ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ\n(Ўшанда уларга айтилур): «Азобларингизни тотинглар! Сизлар (ҳаёти дунёдалик пайтингизда) шоштиргувчи бўлган нарса мана шудир!»'}, {'chapter': 51, 'verse': 15, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَعُيُونٍ\nАлбатта тақводор зотлар жаннатлар ва булоқлар устида бўлурлар'}, {'chapter': 51, 'verse': 16, 'text': 'ءَاخِذِينَ مَآ ءَاتَىٰهُمۡ رَبُّهُمۡۚ إِنَّهُمۡ كَانُواْ قَبۡلَ ذَٰلِكَ مُحۡسِنِينَ\n(У Кунда) Парвардигор ўзларига ато этган нарсаларни (ажр-мукофотларни) қабул қилган ҳолларида (жаннатлар ва булоқлар устида бўлурлар). Зеро, улар бундан илгари (яъни, ҳаёти дунёдалик чоғларида) чиройли амаллар қилгувчи эдилар'}, {'chapter': 51, 'verse': 17, 'text': 'كَانُواْ قَلِيلٗا مِّنَ ٱلَّيۡلِ مَا يَهۡجَعُونَ\nУлар кечадан озгина (фурсатгина) кўз юмар эдилар'}, {'chapter': 51, 'verse': 18, 'text': 'وَبِٱلۡأَسۡحَارِ هُمۡ يَسۡتَغۡفِرُونَ\nВа саҳарларда улар (қилган саҳву-хатолари учун Парвардигордан мағфират сўрар эдилар)'}, {'chapter': 51, 'verse': 19, 'text': 'وَفِيٓ أَمۡوَٰلِهِمۡ حَقّٞ لِّلسَّآئِلِ وَٱلۡمَحۡرُومِ\nУларнинг мол-мулкларида сўрагувчи ва (мол-давлатдан) маҳрум-муҳтож кишилар учун (ажратилган) ҳақ-улуш бўлар эди'}, {'chapter': 51, 'verse': 20, 'text': 'وَفِي ٱلۡأَرۡضِ ءَايَٰتٞ لِّلۡمُوقِنِينَ\n(Эй инсонлар), Ерда (ундаги тоғу тошлар ва водий-дараларда, денгизлар ва дарёларда, ҳайвонот ва наботот оламида) ишонгувчи зотлар учун (Аллоҳнинг қудратига далолат қиладиган) оят-аломатлар бордир'}, {'chapter': 51, 'verse': 21, 'text': 'وَفِيٓ أَنفُسِكُمۡۚ أَفَلَا تُبۡصِرُونَ\nҲамда ўзларингизда (ушбу ҳаётга келиб-кетишингиздан тортиб, вужудларингиздаги ҳар бир аъзоингизда, балки ҳар бир ҳужайрангизнинг нақадар нозик тартиб-интизом билан яратилиб, ўз ўрнига жойлаштирилганида ва ўз зиммасидаги Яратган буюрган вазифани қулоқ қоқмасдан адо этиб боришида Аллоҳнинг қудратига далолат қиладиган оят-аломатлар бордир). Ахир кўрмайсизларми'}, {'chapter': 51, 'verse': 22, 'text': 'وَفِي ٱلسَّمَآءِ رِزۡقُكُمۡ وَمَا تُوعَدُونَ\nОсмонда эса сизларнинг ризқу рўзларингиз (яъни, Ерда мўл ҳосил бўлишига сабаб бўлгувчи ёмғир) ва сизларга ваъда қилинаётган нарса (яъни, жаннат) бордир'}, {'chapter': 51, 'verse': 23, 'text': 'فَوَرَبِّ ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِنَّهُۥ لَحَقّٞ مِّثۡلَ مَآ أَنَّكُمۡ تَنطِقُونَ\nБас, осмон ва Ернинг Парвардигорига қасамки, албатта у (яъни, сизларга ваъда қилинаётган барча нарса) худди сизларнинг сўзлашингиз каби (яъни, сўзлаган сўзларингиз ўзларингиз учун қандоқ ҳақ бўлса, ана шундай) ҳаққи-ростдир'}, {'chapter': 51, 'verse': 24, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ ضَيۡفِ إِبۡرَٰهِيمَ ٱلۡمُكۡرَمِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга Иброҳимнинг иззат-икромли меҳмонлари ҳақидаги хабар келдими'}, {'chapter': 51, 'verse': 25, 'text': 'إِذۡ دَخَلُواْ عَلَيۡهِ فَقَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞ قَوۡمٞ مُّنكَرُونَ\nЎшанда улар (Иброҳимнинг) ҳузурига кириб, «Салом», дейишган эди, у ҳам: «Салом, (Булар) нотаниш қавм-ку?», деди'}, {'chapter': 51, 'verse': 26, 'text': 'فَرَاغَ إِلَىٰٓ أَهۡلِهِۦ فَجَآءَ بِعِجۡلٖ سَمِينٖ\nСўнг у аста оиласи олдига чиқиб, (қовурилган) бир семиз бузоқни келтирди-да'}, {'chapter': 51, 'verse': 27, 'text': 'فَقَرَّبَهُۥٓ إِلَيۡهِمۡ قَالَ أَلَا تَأۡكُلُونَ\nуни уларга яқин қилиб, «(Таомдан) емайсизларми?», деди'}, {'chapter': 51, 'verse': 28, 'text': 'فَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۖ قَالُواْ لَا تَخَفۡۖ وَبَشَّرُوهُ بِغُلَٰمٍ عَلِيمٖ\n(Лекин меҳмонлар овқатга қўл чўзишмагач), улардан хавфсирай бошлади. (Шунда) улар: «Қўрқмагин. (Бизлар фаришталармиз, шунинг учун таом емаймиз)», дедилар ва (Иброҳимга) бир доно ўғил хушхабарини бердилар'}, {'chapter': 51, 'verse': 29, 'text': 'فَأَقۡبَلَتِ ٱمۡرَأَتُهُۥ فِي صَرَّةٖ فَصَكَّتۡ وَجۡهَهَا وَقَالَتۡ عَجُوزٌ عَقِيمٞ\nШунда (уларнинг сўзини эшитиб турган Иброҳимнинг) хотини қичқирганча келди-да, (бу хушхабардан ажабланганидан) ўзининг юзига уриб: «(Ахир мен) туғмас кампир(ман)ку!», деди'}, {'chapter': 51, 'verse': 30, 'text': 'قَالُواْ كَذَٰلِكِ قَالَ رَبُّكِۖ إِنَّهُۥ هُوَ ٱلۡحَكِيمُ ٱلۡعَلِيمُ\nУлар айтдилар: «Парвардигоринг мана шундай деди. Албатта Унинг Ўзигина ҳикмат ва билим Соҳибидир»'}, {'chapter': 51, 'verse': 31, 'text': '۞قَالَ فَمَا خَطۡبُكُمۡ أَيُّهَا ٱلۡمُرۡسَلُونَ\n(Иброҳим) деди: «Эй элчилар, не юмуш билан келдингиз?»'}, {'chapter': 51, 'verse': 32, 'text': 'قَالُوٓاْ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمٖ مُّجۡرِمِينَ\nУлар айтдилар. «Бизлар бир жиноятчи қавмга (яъни, Лут қавмига) юборилгандирмиз'}, {'chapter': 51, 'verse': 33, 'text': 'لِنُرۡسِلَ عَلَيۡهِمۡ حِجَارَةٗ مِّن طِينٖ\nУларнинг устидан лойдан-сополдан бўлган тошни ёғдириш учун (юборилгандирмиз)'}, {'chapter': 51, 'verse': 34, 'text': 'مُّسَوَّمَةً عِندَ رَبِّكَ لِلۡمُسۡرِفِينَ\nПарвардигорнинг ҳузурида ҳаддан ошувчи кимсалар учун белгилаб қўйилган (яъни, ҳар бир ҳалок қилингувчи кофирнинг номи ёзилган тошни ёғдириш учун юборилгандирмиз)'}, {'chapter': 51, 'verse': 35, 'text': 'فَأَخۡرَجۡنَا مَن كَانَ فِيهَا مِنَ ٱلۡمُؤۡمِنِينَ\nБас, Биз у жойдаги мўминлардан бўлган кишиларни чиқариб юбордик'}, {'chapter': 51, 'verse': 36, 'text': 'فَمَا وَجَدۡنَا فِيهَا غَيۡرَ بَيۡتٖ مِّنَ ٱلۡمُسۡلِمِينَ\n(Лекин) у жойда бир хонадондан (яъни, Лут пайғамбар хонадонидан) ўзга мусулмонларни топмадик'}, {'chapter': 51, 'verse': 37, 'text': 'وَتَرَكۡنَا فِيهَآ ءَايَةٗ لِّلَّذِينَ يَخَافُونَ ٱلۡعَذَابَ ٱلۡأَلِيمَ\nБиз (у қишлоқдаги барча кофирларни ҳалок қилганимиздан сўнг) аламли азобдан қўрқадиган зотлар (ибрат олишлари) учун у жойда оят-аломат қолдирдик'}, {'chapter': 51, 'verse': 38, 'text': 'وَفِي مُوسَىٰٓ إِذۡ أَرۡسَلۡنَٰهُ إِلَىٰ فِرۡعَوۡنَ بِسُلۡطَٰنٖ مُّبِينٖ\nМусо (қиссаси)да ҳам (ибрат бордир). Эсланг, Биз уни очиқ ҳужжат билан Фиръавн олдига юборган эдик'}, {'chapter': 51, 'verse': 39, 'text': 'فَتَوَلَّىٰ بِرُكۡنِهِۦ وَقَالَ سَٰحِرٌ أَوۡ مَجۡنُونٞ\nУ ўз аркони (давлати)га суяниб (Мусога иймон келтиришдан) юз ўгирди ва «(Мусо ё) сеҳргар, ёки мажнун», деди'}, {'chapter': 51, 'verse': 40, 'text': 'فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي ٱلۡيَمِّ وَهُوَ مُلِيمٞ\nБас, Биз уни ҳам, қўшинларини ҳам ушлаб, уларни денгизга отдик — ғарқ қилиб юбордик. У (Фиръавн ўзининг кибру ҳавоси ва куфру исёни сабабли ҳар қандай) маломатга лойиқдир'}, {'chapter': 51, 'verse': 41, 'text': 'وَفِي عَادٍ إِذۡ أَرۡسَلۡنَا عَلَيۡهِمُ ٱلرِّيحَ ٱلۡعَقِيمَ\nОд (қабиласининг қиссаси)да ҳам (ибрат бордир). Эсланг, Биз уларнинг устига туғмас (яъни, ҳеч қандай фойда етказмайдиган) бўронни юборган эдик'}, {'chapter': 51, 'verse': 42, 'text': 'مَا تَذَرُ مِن شَيۡءٍ أَتَتۡ عَلَيۡهِ إِلَّا جَعَلَتۡهُ كَٱلرَّمِيمِ\nУ (бўрон) ниманинг устидан ўтса, албатта уни (ҳалок этиб) худди чириб-битган суяклар каби қилиб қўяр эди'}, {'chapter': 51, 'verse': 43, 'text': 'وَفِي ثَمُودَ إِذۡ قِيلَ لَهُمۡ تَمَتَّعُواْ حَتَّىٰ حِينٖ\nСамуд (қабиласининг қиссаси)да ҳам (ибрат бордир). Эсланг, уларга (Аллоҳ томонидан мўъжиза қилиб юборилган туяни сўйиб юборган пайтларида) «бир вақтгача (яъни, уч кунгача тириклик неъматидан) баҳраманд бўлингиз», дейилган эди'}, {'chapter': 51, 'verse': 44, 'text': 'فَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ وَهُمۡ يَنظُرُونَ\nБас, улар Парвардигорнинг амри(га игоат этиш)дан кибр-ҳаво қилишгач, қараб турган ҳолларида уларни чақмоқ урди'}, {'chapter': 51, 'verse': 45, 'text': 'فَمَا ٱسۡتَطَٰعُواْ مِن قِيَامٖ وَمَا كَانُواْ مُنتَصِرِينَ\nБас, улар (у чақмоқдан қочиб қутулиш учун ўринларидан) туришга ҳам қодир бўлмадилар ва (бирон кимса томонидан) ёрдам олгувчи ҳам бўлмадилар'}, {'chapter': 51, 'verse': 46, 'text': 'وَقَوۡمَ نُوحٖ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ\n(Мазкур қавмлардан) илгари Нуҳ қавмини ҳам (ҳалок қилгандирмиз). Чунки улар фосиқ-итоатсиз қавм эдилар'}, {'chapter': 51, 'verse': 47, 'text': 'وَٱلسَّمَآءَ بَنَيۡنَٰهَا بِأَيۡيْدٖ وَإِنَّا لَمُوسِعُونَ\nОсмонни Биз қудрат билан барпо қилдик. Дарҳақиқат, Биз қудратлидирмиз'}, {'chapter': 51, 'verse': 48, 'text': 'وَٱلۡأَرۡضَ فَرَشۡنَٰهَا فَنِعۡمَ ٱلۡمَٰهِدُونَ\nЕрни эса ёйиқ-кенг қилиб қўйдик. Бас, (Биз) нақадар яхши ёйгувчидирмиз'}, {'chapter': 51, 'verse': 49, 'text': 'وَمِن كُلِّ شَيۡءٍ خَلَقۡنَا زَوۡجَيۡنِ لَعَلَّكُمۡ تَذَكَّرُونَ\nСизлар эслатма-ибрат олишларингиз учун Биз ҳар бир нарсани жуфт-жуфт қилиб яратдик'}, {'chapter': 51, 'verse': 50, 'text': 'فَفِرُّوٓاْ إِلَى ٱللَّهِۖ إِنِّي لَكُم مِّنۡهُ نَذِيرٞ مُّبِينٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), уларга айтинг: «Бас, Аллоҳга (иймон келтириш учун) чопинглар-шошинглар! Албатта мен сизлар учун У зот(нинг азоби)дан очиқ огоҳлантиргувчидирман'}, {'chapter': 51, 'verse': 51, 'text': 'وَلَا تَجۡعَلُواْ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۖ إِنِّي لَكُم مِّنۡهُ نَذِيرٞ مُّبِينٞ\nВа сизлар Аллоҳ билан бирга яна бошқа бирон илоҳ бор, деманглар! Албатта мен сизлар учун У зот(нинг азоби)дан очиқ огоҳлантиргувчидирман»'}, {'chapter': 51, 'verse': 52, 'text': 'كَذَٰلِكَ مَآ أَتَى ٱلَّذِينَ مِن قَبۡلِهِم مِّن رَّسُولٍ إِلَّا قَالُواْ سَاحِرٌ أَوۡ مَجۡنُونٌ\nХудди шунингдек, (яъни, сизни сеҳргар, мажнун, шоир деганлари каби) улардан (яъни, Макка мушрикларидан) аввалги (кофир бўлган) кимсаларга бирон пайғамбар келганида, улар ҳам албатта (ўша пайғамбарни ё) сеҳргар ёки мажнун, дер эдилар'}, {'chapter': 51, 'verse': 53, 'text': 'أَتَوَاصَوۡاْ بِهِۦۚ بَلۡ هُمۡ قَوۡمٞ طَاغُونَ\nУлар ўша (сўз)ни бир-бирларига васият қилиб қолдирганмилар? Йўқ, (зотан улар бир-бирларидан йироқ замонларда ва маконларда яшаб ўтганлари сабабли ўзаро васият қилиш имконига эга эмаслар, лекин фақат бир нарса уларнинг барчаларини юқоридаги каби сўзларни айтишга мажбур этар) — улар туғёнга тушган қавмдирлар'}, {'chapter': 51, 'verse': 54, 'text': 'فَتَوَلَّ عَنۡهُمۡ فَمَآ أَنتَ بِمَلُومٖ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз улардан юз ўгиринг! Энди (зиммангиздаги пайғамбарликни тўла етказганингиздан сўнг кофирлардан юз ўгирсангиз) сиз маломат қилингувчи эмассиз'}, {'chapter': 51, 'verse': 55, 'text': 'وَذَكِّرۡ فَإِنَّ ٱلذِّكۡرَىٰ تَنفَعُ ٱلۡمُؤۡمِنِينَ\nВа (Қуръон билан) панд-насиҳат қилинг! Зеро, у панд-насиҳатлар мўминларга нафъ етказур'}, {'chapter': 51, 'verse': 56, 'text': 'وَمَا خَلَقۡتُ ٱلۡجِنَّ وَٱلۡإِنسَ إِلَّا لِيَعۡبُدُونِ\nМен жин ва инсни фақат Ўзимга ибодат қилишлари учунгина яратдим'}, {'chapter': 51, 'verse': 57, 'text': 'مَآ أُرِيدُ مِنۡهُم مِّن رِّزۡقٖ وَمَآ أُرِيدُ أَن يُطۡعِمُونِ\nМен улардан бирон ризқ истамасман ва улар Мени таомлантиришини истамасман'}, {'chapter': 51, 'verse': 58, 'text': 'إِنَّ ٱللَّهَ هُوَ ٱلرَّزَّاقُ ذُو ٱلۡقُوَّةِ ٱلۡمَتِينُ\nЗеро, Аллоҳнинг Ўзигина (барча халойиққа) ризқу рўз бергувчи, куч-қувват соҳиби ва қудратлидир'}, {'chapter': 51, 'verse': 59, 'text': 'فَإِنَّ لِلَّذِينَ ظَلَمُواْ ذَنُوبٗا مِّثۡلَ ذَنُوبِ أَصۡحَٰبِهِمۡ فَلَا يَسۡتَعۡجِلُونِ\nБас, золим-кофир бўлган кимсалар учун шак-шубҳасиз, худди (илгари ўтган кофир) дўстларининг насибалари каби (азобдан иборат) насиба бордир. Бас, улар Мени(нг азобимни) шоштирмай қўя қолсинлар'}, {'chapter': 51, 'verse': 60, 'text': 'فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِن يَوۡمِهِمُ ٱلَّذِي يُوعَدُونَ\nБас, кофир бўлган кимсалар учун уларга ваъда қилинаётган (азобга гирифтор қилинадиган) Кунларида ҳалокат бўлгай'}, {'chapter': 52, 'verse': 1, 'text': 'وَٱلطُّورِ\nТур (тоғи)га қасам'}, {'chapter': 52, 'verse': 2, 'text': 'وَكِتَٰبٖ مَّسۡطُورٖ\nБитилган Китобга (Қуръонга) қасам'}, {'chapter': 52, 'verse': 3, 'text': 'فِي رَقّٖ مَّنشُورٖ\nОчиқ саҳифага (ёзилган Китобга) қасам'}, {'chapter': 52, 'verse': 4, 'text': 'وَٱلۡبَيۡتِ ٱلۡمَعۡمُورِ\nБайтул-Маъмурга (Обод Уйга) қасам'}, {'chapter': 52, 'verse': 5, 'text': 'وَٱلسَّقۡفِ ٱلۡمَرۡفُوعِ\nБаланд кўтарилган томга (осмонга) қасам'}, {'chapter': 52, 'verse': 6, 'text': 'وَٱلۡبَحۡرِ ٱلۡمَسۡجُورِ\nВа тўлиб-тошган денгизга қасамки'}, {'chapter': 52, 'verse': 7, 'text': 'إِنَّ عَذَابَ رَبِّكَ لَوَٰقِعٞ\nПарвардигорингизнинг азоби шак-шубҳасиз, воқеъ бўлгувчидир'}, {'chapter': 52, 'verse': 8, 'text': 'مَّا لَهُۥ مِن دَافِعٖ\nУнинг учун бирон дафъ қилгувчи — тўсиқ йўқдир'}, {'chapter': 52, 'verse': 9, 'text': 'يَوۡمَ تَمُورُ ٱلسَّمَآءُ مَوۡرٗا\nУ Кунда осмон титраб-чайқалур'}, {'chapter': 52, 'verse': 10, 'text': 'وَتَسِيرُ ٱلۡجِبَالُ سَيۡرٗا\nВа тоғлар ҳам (ўрнашган жойларидан жилиб), йўлга тушур'}, {'chapter': 52, 'verse': 11, 'text': 'فَوَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nАна ўша Кунда (пайғамбарларни) ёлғончи қилгувчиларга ҳалокат бўлур'}, {'chapter': 52, 'verse': 12, 'text': 'ٱلَّذِينَ هُمۡ فِي خَوۡضٖ يَلۡعَبُونَ\nУлар ўзлари шўнғиган нотўғри йўлларида адашиб юрадиган кимсалардир'}, {'chapter': 52, 'verse': 13, 'text': 'يَوۡمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا\nУ Кунда улар жаҳаннам ўтига ҳайдалурлар'}, {'chapter': 52, 'verse': 14, 'text': 'هَٰذِهِ ٱلنَّارُ ٱلَّتِي كُنتُم بِهَا تُكَذِّبُونَ\n(Ва уларга дейилур): «Сизлар ёлғон деб юрган дўзах мана шудир!»'}, {'chapter': 52, 'verse': 15, 'text': 'أَفَسِحۡرٌ هَٰذَآ أَمۡ أَنتُمۡ لَا تُبۡصِرُونَ\nҚани, бу (азоб) ҳам сеҳрми, ёки сизлар (ҳаёти дунёда Ҳақни кўра олмаганларингиз каби бу азобни ҳам) кўрмаяпсизларми'}, {'chapter': 52, 'verse': 16, 'text': 'ٱصۡلَوۡهَا فَٱصۡبِرُوٓاْ أَوۡ لَا تَصۡبِرُواْ سَوَآءٌ عَلَيۡكُمۡۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\n(Дўзахга) кирингиз! Энди (унинг азобига) хоҳ сабр қилинглар, хоҳ сабр қилманглар, сизларга баробардир! (Яъни, у азоб сизлардан енгиллатилмас). Сизлар фақат ўзларингиз қилиб ўтган қилмишларингизнинг жазосини тортурсизлар!»'}, {'chapter': 52, 'verse': 17, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَنَعِيمٖ\nТақводор зотлар эса шак-шубҳасиз, жаннатлар ва неъматлар ичра барқарордирлар'}, {'chapter': 52, 'verse': 18, 'text': 'فَٰكِهِينَ بِمَآ ءَاتَىٰهُمۡ رَبُّهُمۡ وَوَقَىٰهُمۡ رَبُّهُمۡ عَذَابَ ٱلۡجَحِيمِ\nУларга Парвардигорлари ато этган нарсалар билан шод-хуррам бўлган ҳолларида (жаннатлар ва неъматлар ичра барқарордирлар). Парвардигорлари уларни дўзах азобидан сақлагандир'}, {'chapter': 52, 'verse': 19, 'text': 'كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ\n(Уларга): «Сизлар қилиб ўтган (яхши) амалларингиз сабабли кўнгилдагидек еб-ичинглар», (дейилур)'}, {'chapter': 52, 'verse': 20, 'text': 'مُتَّكِـِٔينَ عَلَىٰ سُرُرٖ مَّصۡفُوفَةٖۖ وَزَوَّجۡنَٰهُم بِحُورٍ عِينٖ\nҚатор тизилган сўрилар устида ястанган ҳолларингизда (кўнгилдагидек еб-ичинглар, дейилур) ва Биз уларга оҳу кўзли ҳурларни жуфти ҳалол қилиб бердик'}, {'chapter': 52, 'verse': 21, 'text': 'وَٱلَّذِينَ ءَامَنُواْ وَٱتَّبَعَتۡهُمۡ ذُرِّيَّتُهُم بِإِيمَٰنٍ أَلۡحَقۡنَا بِهِمۡ ذُرِّيَّتَهُمۡ وَمَآ أَلَتۡنَٰهُم مِّنۡ عَمَلِهِم مِّن شَيۡءٖۚ كُلُّ ٱمۡرِيِٕۭ بِمَا كَسَبَ رَهِينٞ\nЎзлари иймон келтириб, зурриётлари ҳам уларга иймон билан эргашган зотларга (ўша) зурриётларини қўшдик ва уларга қилган амалларидан бирон нарсани ҳам камитмадик. Ҳар бир киши ўзи касб қилган иши билан гаровга олингувчидир — ушлангувчидир'}, {'chapter': 52, 'verse': 22, 'text': 'وَأَمۡدَدۡنَٰهُم بِفَٰكِهَةٖ وَلَحۡمٖ مِّمَّا يَشۡتَهُونَ\nЯна Биз уларни кўнгиллари истайдиган мева-чева ва гўшт билан қувватлантирдик'}, {'chapter': 52, 'verse': 23, 'text': 'يَتَنَٰزَعُونَ فِيهَا كَأۡسٗا لَّا لَغۡوٞ فِيهَا وَلَا تَأۡثِيمٞ\nУлар у жойда (шундай шароб) косаларини талашиб — қўлма-қўл қилиб ичурларки, у (шароб)да (яъни, уни ичганлари сабабли) на беҳуда-сергаплик ва на гуноҳ бўлар'}, {'chapter': 52, 'verse': 24, 'text': '۞وَيَطُوفُ عَلَيۡهِمۡ غِلۡمَانٞ لَّهُمۡ كَأَنَّهُمۡ لُؤۡلُؤٞ مَّكۡنُونٞ\nУларнинг устида гўё (садаф ичида) яширинган гавҳардек (покиза ва хушсурат) ғуломлари айланиб (хизматга ҳозир бўлиб) турур'}, {'chapter': 52, 'verse': 25, 'text': 'وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ\nУлар бир-бирларига боқишиб, басти-жавоб қилишиб'}, {'chapter': 52, 'verse': 26, 'text': 'قَالُوٓاْ إِنَّا كُنَّا قَبۡلُ فِيٓ أَهۡلِنَا مُشۡفِقِينَ\nдейдилар: «Ҳақиқатан бизлар илгари (ҳаёти дунёда) аҳли-оиламиз орасида (бўлган чоғимизда Охиратда Аллоҳнинг азобига дучор бўлишдан) қўрқар эдик'}, {'chapter': 52, 'verse': 27, 'text': 'فَمَنَّ ٱللَّهُ عَلَيۡنَا وَوَقَىٰنَا عَذَابَ ٱلسَّمُومِ\nМана, Аллоҳ бизларга марҳамат кўргизди ва бизларни самумдан (яъни, баданларни илма-тешик қилиб юборадиган дўзахий шамолдан) сақлади'}, {'chapter': 52, 'verse': 28, 'text': 'إِنَّا كُنَّا مِن قَبۡلُ نَدۡعُوهُۖ إِنَّهُۥ هُوَ ٱلۡبَرُّ ٱلرَّحِيمُ\nДарҳақиқат, бизлар илгари (ҳаёти-дунёда) У зотга дуо-илтижо қилар эдик. Албатта Унинг Ўзигина марҳаматли, меҳрибондир»'}, {'chapter': 52, 'verse': 29, 'text': 'فَذَكِّرۡ فَمَآ أَنتَ بِنِعۡمَتِ رَبِّكَ بِكَاهِنٖ وَلَا مَجۡنُونٍ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом) сиз (инсонларга Қуръон оятлари билан) панд-насиҳат қилаверинг! Чунки сиз Парвардигорингизнинг неъмат-марҳамати билан коҳин-фолбин ҳам, мажнун ҳам эмасдирсиз'}, {'chapter': 52, 'verse': 30, 'text': 'أَمۡ يَقُولُونَ شَاعِرٞ نَّتَرَبَّصُ بِهِۦ رَيۡبَ ٱلۡمَنُونِ\nБалки улар (мушриклар: «Муҳаммад), бир шоирдир. Бизлар унга замон ҳодисот-балолари (етиши)ни кутурмиз», дерлар'}, {'chapter': 52, 'verse': 31, 'text': 'قُلۡ تَرَبَّصُواْ فَإِنِّي مَعَكُم مِّنَ ٱلۡمُتَرَبِّصِينَ\nАйтинг: «Кутаверинглар! Мен ҳам сизлар билан бирга кутгувчилардандирман. (Қани, балога ким йўлиқар экан)»'}, {'chapter': 52, 'verse': 32, 'text': 'أَمۡ تَأۡمُرُهُمۡ أَحۡلَٰمُهُم بِهَٰذَآۚ أَمۡ هُمۡ قَوۡمٞ طَاغُونَ\nБалки уларнинг «ақллари» мана шундай («доно» сўзларни айтишга) буюрар?! Ёки улар туғёнга тушган қавмлар(ки, бундай сўзларни айтишларига сабаб ўша туғёнми)'}, {'chapter': 52, 'verse': 33, 'text': 'أَمۡ يَقُولُونَ تَقَوَّلَهُۥۚ بَل لَّا يُؤۡمِنُونَ\nБалки улар: «(Муҳаммад Қуръонни) ўзи ичидан тўқиб олган», дерлар?! — Йўқ, улар иймон келтирмаслар'}, {'chapter': 52, 'verse': 34, 'text': 'فَلۡيَأۡتُواْ بِحَدِيثٖ مِّثۡلِهِۦٓ إِن كَانُواْ صَٰدِقِينَ\nУ ҳолда агар ростгўй бўлсалар ўзлари ҳам ўша (Қуръон)га ўхшаш бирон сўз — китоб келтирсинлар-чи'}, {'chapter': 52, 'verse': 35, 'text': 'أَمۡ خُلِقُواْ مِنۡ غَيۡرِ شَيۡءٍ أَمۡ هُمُ ٱلۡخَٰلِقُونَ\nБалки улар ҳеч нарсадан (яъни, Яратгувчисиз) яралиб қолгандирлар?! Ёки улар ўзлари яратгувчимиканлар-а'}, {'chapter': 52, 'verse': 36, 'text': 'أَمۡ خَلَقُواْ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَۚ بَل لَّا يُوقِنُونَ\nБалки осмонлар ва Ерни ҳам улар яратгандирлар?! Йўқ, улар ишонмаслар'}, {'chapter': 52, 'verse': 37, 'text': 'أَمۡ عِندَهُمۡ خَزَآئِنُ رَبِّكَ أَمۡ هُمُ ٱلۡمُصَۜيۡطِرُونَ\nБалки уларнинг ҳузурларида Парвардигорингизнинг хазиналари бордир?! Ёки улар (бутун борлиқни) бошқариб тургувчимиканлар-а'}, {'chapter': 52, 'verse': 38, 'text': 'أَمۡ لَهُمۡ سُلَّمٞ يَسۡتَمِعُونَ فِيهِۖ فَلۡيَأۡتِ مُسۡتَمِعُهُم بِسُلۡطَٰنٖ مُّبِينٍ\nБалки уларнинг (осмонга кўтарилиб, фаришталарнинг сўзларини) эшитиб оладиган нарвонлари бордир?! У ҳолда уларнинг эшитиб олгувчилари (ўзларининг ҳақиқатан Аллоҳнинг амридан хабардор бўлганига) бирон очиқ ҳужжат келтирсин-чи'}, {'chapter': 52, 'verse': 39, 'text': 'أَمۡ لَهُ ٱلۡبَنَٰتُ وَلَكُمُ ٱلۡبَنُونَ\n(Эй мушриклар), балки қизлар У зотники-ю, ўғиллар сизларникидир'}, {'chapter': 52, 'verse': 40, 'text': 'أَمۡ تَسۡـَٔلُهُمۡ أَجۡرٗا فَهُم مِّن مَّغۡرَمٖ مُّثۡقَلُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), балки сиз улардан (ўз даъватингиз учун) ажр-ҳақ сўраётгандирсизки, улар (ўша) тўлов(ни тўлаш)дан қийналиб қолгандирлар (ва шу сабабли иймон келтирмаётгандирлар)'}, {'chapter': 52, 'verse': 41, 'text': 'أَمۡ عِندَهُمُ ٱلۡغَيۡبُ فَهُمۡ يَكۡتُبُونَ\nБалки уларнинг ҳузурларида ғайб (илми — Аллоҳ таолонинг Ўз бандаларидан яширган сирлари) бўлиб, улар (ўша сирларни) ёзиб олаётгандирлар (ва шу сабабли сиз келтирган хабарларга ишонмаётгандирлар?!) Асло ундоқ эмасдир! У ҳолда нега улар сизга иймон келтирмаслар'}, {'chapter': 52, 'verse': 42, 'text': 'أَمۡ يُرِيدُونَ كَيۡدٗاۖ فَٱلَّذِينَ كَفَرُواْ هُمُ ٱلۡمَكِيدُونَ\nБалки улар (сизни ҳалок қилиш учун) бирон ҳийла-найранг қилмоқчидирлар?! У ҳолда ўша кофир бўлган кимсаларнинг ўзлари ҳийла-найрангга гирифтор бўлурлар'}, {'chapter': 52, 'verse': 43, 'text': 'أَمۡ لَهُمۡ إِلَٰهٌ غَيۡرُ ٱللَّهِۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يُشۡرِكُونَ\nБалки улар учун Аллоҳдан ўзга бирон «илоҳ» бордир?! Аллоҳ уларнинг ширкларидан покдир'}, {'chapter': 52, 'verse': 44, 'text': 'وَإِن يَرَوۡاْ كِسۡفٗا مِّنَ ٱلسَّمَآءِ سَاقِطٗا يَقُولُواْ سَحَابٞ مَّرۡكُومٞ\nАгар улар (устларига уларни ҳалок қилиш учун) осмондан бир бўлак-парча тушаётганини кўрсалар (ўшанда ҳам иймон келтирмаслар, балки «Бу тўпланган булутдир», дерлар)'}, {'chapter': 52, 'verse': 45, 'text': 'فَذَرۡهُمۡ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي فِيهِ يُصۡعَقُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом) сиз уларни токи ўзлари ҳалок қилинадиган кунларига рўбарў бўлгунларича тарк қилинг'}, {'chapter': 52, 'verse': 46, 'text': 'يَوۡمَ لَا يُغۡنِي عَنۡهُمۡ كَيۡدُهُمۡ شَيۡـٔٗا وَلَا هُمۡ يُنصَرُونَ\nУ Кунда уларнинг ҳийла-найранглари бирон фойда бермас ва уларга ёрдам ҳам берилмас'}, {'chapter': 52, 'verse': 47, 'text': 'وَإِنَّ لِلَّذِينَ ظَلَمُواْ عَذَابٗا دُونَ ذَٰلِكَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ\nАлбатта золим-кофир бўлган кимсалар учун бундан (Қиёмат Кунидан) илгари мана шу дунёда) ҳам азоб-уқубат(лар) бордир, лекин уларнинг кўплари (буни) билмаслар'}, {'chapter': 52, 'verse': 48, 'text': 'وَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ فَإِنَّكَ بِأَعۡيُنِنَاۖ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ حِينَ تَقُومُ\n(Эй Муҳаммад алайҳис-салоту вас-салом) сиз Парвардигорингизнинг ҳукмига (яъни, мушрикларни дарҳол азобга дучор қилмаганига ва сизни турли машаққатли синовлар билан имтиҳон қилишига) сабр қилинг. Зотан, сиз шак-шубҳасиз, Бизнинг кўз ўнгимизда (яъни, ҳифзи-ҳимоямизда)дирсиз. Ва (тонгда уйқудан) турган пайтингизда Парвардигорингизга ҳамду сано айтиш билан (У зотни барча айб-нуқсондан) покланг'}, {'chapter': 52, 'verse': 49, 'text': 'وَمِنَ ٱلَّيۡلِ فَسَبِّحۡهُ وَإِدۡبَٰرَ ٱلنُّجُومِ\nШунингдек кечадан (бўлган соатларда) ва юлдузлар юз ўгириб кетгач (яъни, саҳар пайтларида) ҳам У зотга тасбеҳ айтинг'}, {'chapter': 53, 'verse': 1, 'text': 'وَٱلنَّجۡمِ إِذَا هَوَىٰ\nБотиб кетаётган юлдузга қасамки'}, {'chapter': 53, 'verse': 2, 'text': 'مَا ضَلَّ صَاحِبُكُمۡ وَمَا غَوَىٰ\nСизларнинг соҳибингиз (яъни, Муҳаммад алайҳис-салоту вас-салом) залолатга ҳам кетгани йўқ, йўлдан ҳам озгани йўқ'}, {'chapter': 53, 'verse': 3, 'text': 'وَمَا يَنطِقُ عَنِ ٱلۡهَوَىٰٓ\nВа у (сизларга келтираётган Қуръонни) ўз ҳавойи-хоҳиши билан сўзламас'}, {'chapter': 53, 'verse': 4, 'text': 'إِنۡ هُوَ إِلَّا وَحۡيٞ يُوحَىٰ\nУ (Қуръон) фақат (Аллоҳ томонидан пайғамбарга) ваҳий қилинаётган (туширилаётган) бир ваҳийдир'}, {'chapter': 53, 'verse': 5, 'text': 'عَلَّمَهُۥ شَدِيدُ ٱلۡقُوَىٰ\nУнга (бу ваҳийни) бир кучга тўлган зот (яъни, Жаброил алайҳиссалом) таълим бермишдир'}, {'chapter': 53, 'verse': 6, 'text': 'ذُو مِرَّةٖ فَٱسۡتَوَىٰ\nСоҳиби қудрат (яъни, Жаброил алайҳиссалом таълим бермишдир)'}, {'chapter': 53, 'verse': 7, 'text': 'وَهُوَ بِٱلۡأُفُقِ ٱلۡأَعۡلَىٰ\nБас, у (аввал) юксак уфқда (кўриниб), тик турди'}, {'chapter': 53, 'verse': 8, 'text': 'ثُمَّ دَنَا فَتَدَلَّىٰ\nСўнгра яқинлашиб, пастлади'}, {'chapter': 53, 'verse': 9, 'text': 'فَكَانَ قَابَ قَوۡسَيۡنِ أَوۡ أَدۡنَىٰ\nБас, (Муҳаммад алайҳис-салоту вас-саломга) икки камон оралиғида ё (ундан-да) яқинроқ бўлиб'}, {'chapter': 53, 'verse': 10, 'text': 'فَأَوۡحَىٰٓ إِلَىٰ عَبۡدِهِۦ مَآ أَوۡحَىٰ\n(Аллоҳ) ўз бандаси (Муҳаммад алайҳис-салоту вас-салом)га туширган ваҳийни келтиради'}, {'chapter': 53, 'verse': 11, 'text': 'مَا كَذَبَ ٱلۡفُؤَادُ مَا رَأَىٰٓ\n(Пайғамбарнинг) кўнгли кўрган нарсасини инкор этмади. (Яъни, Жаброил фариштанинг суратини кўриб, пайғамбарнинг кўнгли ҳам қаноатланди ва унинг Аллоҳ таоло томонидаи фаришта эканлигига иймон келтирди)'}, {'chapter': 53, 'verse': 12, 'text': 'أَفَتُمَٰرُونَهُۥ عَلَىٰ مَا يَرَىٰ\nЭнди сизлар, (эй мушриклар, пайғамбар) кўриб турган нарсаси устида у билан тортишурмисизлар'}, {'chapter': 53, 'verse': 13, 'text': 'وَلَقَدۡ رَءَاهُ نَزۡلَةً أُخۡرَىٰ\nҚасамки, (Муҳаммад алайҳис-салоту вас-салом Жаброил фариштани ўзининг аслий суратида) иккинчи бор кўрди'}, {'chapter': 53, 'verse': 14, 'text': 'عِندَ سِدۡرَةِ ٱلۡمُنتَهَىٰ\n(Жаннат) четидаги Нилуфар олдида (кўрди)'}, {'chapter': 53, 'verse': 15, 'text': 'عِندَهَا جَنَّةُ ٱلۡمَأۡوَىٰٓ\nЖаннати маъво ҳам ўша (Нилуфар)нинг олдидадир'}, {'chapter': 53, 'verse': 16, 'text': 'إِذۡ يَغۡشَى ٱلسِّدۡرَةَ مَا يَغۡشَىٰ\nЎшанда Нилуфарни ўраган нарса (яъни, Аллоҳ таолонинг файзи илоҳийси) ўраб олган эди'}, {'chapter': 53, 'verse': 17, 'text': 'مَا زَاغَ ٱلۡبَصَرُ وَمَا طَغَىٰ\n(Пайғамбарнинг) кўзи (ўнгу сўлга) оғгани ҳам йўқ, ўз ҳаддидан ошгани ҳам йўқ'}, {'chapter': 53, 'verse': 18, 'text': 'لَقَدۡ رَأَىٰ مِنۡ ءَايَٰتِ رَبِّهِ ٱلۡكُبۡرَىٰٓ\nДарҳақиқат, у (ўша соатда) Парвардигорининг буюк оятларини (яъни, У зотнинг қудрати илоҳийсига далолат қиладиган жуда кўп аломатларни) кўрди'}, {'chapter': 53, 'verse': 19, 'text': 'أَفَرَءَيۡتُمُ ٱللَّـٰتَ وَٱلۡعُزَّىٰ\n(Эй мушриклар, ўзларингизча сиғинаётган санамларингиз) — «Лот», «Уззо» ҳақида ҳеч ўйлаб кўрдингларми'}, {'chapter': 53, 'verse': 20, 'text': 'وَمَنَوٰةَ ٱلثَّالِثَةَ ٱلۡأُخۡرَىٰٓ\nВа яна учинчилари бўлмиш тубан «Манот» ҳақидачи'}, {'chapter': 53, 'verse': 21, 'text': 'أَلَكُمُ ٱلذَّكَرُ وَلَهُ ٱلۡأُنثَىٰ\n(Сизлар ўша бут-санамларни ва фаришталарни «Аллоҳнинг қизлари», дейсизлар, демак) эркак (жинси) сизларники-ю, аёл (жинси) У зотникими'}, {'chapter': 53, 'verse': 22, 'text': 'تِلۡكَ إِذٗا قِسۡمَةٞ ضِيزَىٰٓ\nУ ҳолда бу адолатсиз тақсим-ку'}, {'chapter': 53, 'verse': 23, 'text': 'إِنۡ هِيَ إِلَّآ أَسۡمَآءٞ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَمَا تَهۡوَى ٱلۡأَنفُسُۖ وَلَقَدۡ جَآءَهُم مِّن رَّبِّهِمُ ٱلۡهُدَىٰٓ\nУ (ном)лар фақат сизлар ўзларингиз ва ота-боболарингиз қўйиб олган номлардир. Аллоҳ улар(га ибодат қилиш) ҳақида бирон ҳужжат туширган эмас. У (мушрик)ларга (Пайғамбар алайҳис-салоту вас-салом воситаларида) Парвардигор томонидан ҳидоят (Қуръон) келиб турган ҳолда улар фақат гумонга ва ҳавойи нафсларигагина эргашадилар-а'}, {'chapter': 53, 'verse': 24, 'text': 'أَمۡ لِلۡإِنسَٰنِ مَا تَمَنَّىٰ\nЁки инсон учун ўзи орзу қилган нарса бўлаверарми?! (Ҳаргиз бўлмас)'}, {'chapter': 53, 'verse': 25, 'text': 'فَلِلَّهِ ٱلۡأٓخِرَةُ وَٱلۡأُولَىٰ\nЗеро, Охират ҳам, дунё ҳам ёлғиз Аллоҳникидир'}, {'chapter': 53, 'verse': 26, 'text': '۞وَكَم مِّن مَّلَكٖ فِي ٱلسَّمَٰوَٰتِ لَا تُغۡنِي شَفَٰعَتُهُمۡ شَيۡـًٔا إِلَّا مِنۢ بَعۡدِ أَن يَأۡذَنَ ٱللَّهُ لِمَن يَشَآءُ وَيَرۡضَىٰٓ\nОсмонларда қанчадан-қанча фаришталар бўлиб, ўшаларнинг шафоатлари ҳам бирон фойда бермас, магар Аллоҳ Ўзи хоҳлаган рози бўлган кишилар учун (шафоатга) изн берганидан кейингина (у шафоатнинг фойдаси тегур)'}, {'chapter': 53, 'verse': 27, 'text': 'إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ لَيُسَمُّونَ ٱلۡمَلَـٰٓئِكَةَ تَسۡمِيَةَ ٱلۡأُنثَىٰ\nДарвоқеъ, Охиратга иймон келтирмайдиган кимсалар фаришталарни аёллар номи билан атайдилар'}, {'chapter': 53, 'verse': 28, 'text': 'وَمَا لَهُم بِهِۦ مِنۡ عِلۡمٍۖ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّۖ وَإِنَّ ٱلظَّنَّ لَا يُغۡنِي مِنَ ٱلۡحَقِّ شَيۡـٔٗا\nҲолбуки, улар учун бу ҳақда (яъни, фаришталарнинг қайси жинсдан эканлиги ҳақида) бирон билим-ҳужжат йўқдир. Улар фақат гумон-тахминга эргашурлар, холос. Аниқки, гумон ҳақиқатдан бирон нарсанинг ўрнига ўтмас'}, {'chapter': 53, 'verse': 29, 'text': 'فَأَعۡرِضۡ عَن مَّن تَوَلَّىٰ عَن ذِكۡرِنَا وَلَمۡ يُرِدۡ إِلَّا ٱلۡحَيَوٰةَ ٱلدُّنۡيَا\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз Бизнинг эслатмамиздан орқа ўгириб кетган ва фақат ҳаёти дунёнигина истаган кимсалардан юз ўгиринг'}, {'chapter': 53, 'verse': 30, 'text': 'ذَٰلِكَ مَبۡلَغُهُم مِّنَ ٱلۡعِلۡمِۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِمَنِ ٱهۡتَدَىٰ\nУларнинг «илм»дан етган жойлари мана шу (ҳаёти дунё матоларинигина исташ)дир. Албатта Парвардигорингизнинг Ўзи Унинг йўлидан озган кимсаларни жуда яхши билгувчидир ва У ҳидоят топган зотларни ҳам жуда яхши билгувчидир'}, {'chapter': 53, 'verse': 31, 'text': 'وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ لِيَجۡزِيَ ٱلَّذِينَ أَسَـٰٓـُٔواْ بِمَا عَمِلُواْ وَيَجۡزِيَ ٱلَّذِينَ أَحۡسَنُواْ بِٱلۡحُسۡنَى\nОсмонлардаги ва Ердаги бор нарсалар ёлғиз Аллоҳникидир. У зотнинг Ўзи ёмонлик-гуноҳ қилиб ўтган кимсаларни қилган амаллари билан жазолагай ва чиройли амал қилган зотларни гўзал (савоб-жаннат) билан мукофотлагай'}, {'chapter': 53, 'verse': 32, 'text': 'ٱلَّذِينَ يَجۡتَنِبُونَ كَبَـٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ إِلَّا ٱللَّمَمَۚ إِنَّ رَبَّكَ وَٰسِعُ ٱلۡمَغۡفِرَةِۚ هُوَ أَعۡلَمُ بِكُمۡ إِذۡ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَإِذۡ أَنتُمۡ أَجِنَّةٞ فِي بُطُونِ أُمَّهَٰتِكُمۡۖ فَلَا تُزَكُّوٓاْ أَنفُسَكُمۡۖ هُوَ أَعۡلَمُ بِمَنِ ٱتَّقَىٰٓ\nУлар (яъни, жаннатга сазовор бўлганлар) кичик хатолардан бошқа катта гуноҳлардан ва бузуқликлардан йироқ бўладиган зотлардир. Албатта Парвардигорингиз мағфирати кенг Зотдир. У сизларни (яъни, оталарингиз Одамни) Ердан — тупроқдан пайдо қилган пайтиданоқ ва сизлар оналарингизнинг қорнида ҳомила бўлган пайтингизданоқ жуда яхши билгувчидир. Бас, сизлар ўзларингизни покламай қўя қолинглар! У тақводор бўлган кишиларни жуда яхши билгувчидир'}, {'chapter': 53, 'verse': 33, 'text': 'أَفَرَءَيۡتَ ٱلَّذِي تَوَلَّىٰ\n(Эй Муҳаммад алайҳис-салоту вас-салом, иймондан) юз ўгириб кетган'}, {'chapter': 53, 'verse': 34, 'text': 'وَأَعۡطَىٰ قَلِيلٗا وَأَكۡدَىٰٓ\nва озгина (мол) бериб, (кейин шарт қилинган молни беришни) тўхтатган кимсани кўрдингизми'}, {'chapter': 53, 'verse': 35, 'text': 'أَعِندَهُۥ عِلۡمُ ٱلۡغَيۡبِ فَهُوَ يَرَىٰٓ\nУнинг ҳузурида ғайб илми бормидики, у (келажакда бошқа биров ўзининг гуноҳини зиммасига олишини) билиб олса'}, {'chapter': 53, 'verse': 36, 'text': 'أَمۡ لَمۡ يُنَبَّأۡ بِمَا فِي صُحُفِ مُوسَىٰ\nЁки унга Мусонинг саҳифаларидаги сўзлар хабари берилмаганмиди'}, {'chapter': 53, 'verse': 37, 'text': 'وَإِبۡرَٰهِيمَ ٱلَّذِي وَفَّىٰٓ\nВа (аҳдига) вафодор зот бўлган Иброҳимнинг саҳифаларидаги сўзлар (хабари берилмаганмиди)'}, {'chapter': 53, 'verse': 38, 'text': 'أَلَّا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰ\nҲеч бир кўтаргувчи (яъни, гуноҳкор жон) ўзга жоннинг юкини (яъни, гуноҳини) кўтармас'}, {'chapter': 53, 'verse': 39, 'text': 'وَأَن لَّيۡسَ لِلۡإِنسَٰنِ إِلَّا مَا سَعَىٰ\nИнсон учун фақат ўзи қилган ҳаракатигина бўлур (яъни, ўзгаларнинг қилган яхши амалларидан унга бирон фойда етмас)'}, {'chapter': 53, 'verse': 40, 'text': 'وَأَنَّ سَعۡيَهُۥ سَوۡفَ يُرَىٰ\nУнинг қилган саъй-ҳаракати эса яқинда (Қиёмат Кунида) кўринур'}, {'chapter': 53, 'verse': 41, 'text': 'ثُمَّ يُجۡزَىٰهُ ٱلۡجَزَآءَ ٱلۡأَوۡفَىٰ\nСўнгра у (ўша саъй-ҳаракати учун) тўла-тўкис жазо-мукофот олур'}, {'chapter': 53, 'verse': 42, 'text': 'وَأَنَّ إِلَىٰ رَبِّكَ ٱلۡمُنتَهَىٰ\nАлбатта охирги чегара ёлғиз Парвардигорингиз олдига (бормоқлик)дир'}, {'chapter': 53, 'verse': 43, 'text': 'وَأَنَّهُۥ هُوَ أَضۡحَكَ وَأَبۡكَىٰ\nАлбатта кулдирган ҳам, йиғлатган ҳам Унинг Ўзидир'}, {'chapter': 53, 'verse': 44, 'text': 'وَأَنَّهُۥ هُوَ أَمَاتَ وَأَحۡيَا\nАлбатта ўлдирган ҳам, тирилтирган ҳам Унинг Ўзидир'}, {'chapter': 53, 'verse': 45, 'text': 'وَأَنَّهُۥ خَلَقَ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰ\nВа албатта У зот эркак-аёл жуфтларни яратгандир'}, {'chapter': 53, 'verse': 46, 'text': 'مِن نُّطۡفَةٍ إِذَا تُمۡنَىٰ\n(Уларни) отилиб чиқадиган нутфадан (яратгандир)'}, {'chapter': 53, 'verse': 47, 'text': 'وَأَنَّ عَلَيۡهِ ٱلنَّشۡأَةَ ٱلۡأُخۡرَىٰ\nАлбатта (барча халойиқни ўлганидан сўнг) иккинчи бор пайдо қилиш ҳам ёлғиз Унинг зиммасидадир'}, {'chapter': 53, 'verse': 48, 'text': 'وَأَنَّهُۥ هُوَ أَغۡنَىٰ وَأَقۡنَىٰ\nАлбатта бой қилган ҳам, бадавлат қилган ҳам Унинг Ўзидир'}, {'chapter': 53, 'verse': 49, 'text': 'وَأَنَّهُۥ هُوَ رَبُّ ٱلشِّعۡرَىٰ\nАлбатта Шеъро (юлдузи)нинг Парвардигори ҳам Унинг Ўзидир'}, {'chapter': 53, 'verse': 50, 'text': 'وَأَنَّهُۥٓ أَهۡلَكَ عَادًا ٱلۡأُولَىٰ\nАлбатта У зот аввалги Од (қабиласи)ни ҳалок қилди'}, {'chapter': 53, 'verse': 51, 'text': 'وَثَمُودَاْ فَمَآ أَبۡقَىٰ\nВа Самуд (қабиласи)ни ҳам (ҳалок қилди), бас (улардан бирон кишини тирик) қолдирмади'}, {'chapter': 53, 'verse': 52, 'text': 'وَقَوۡمَ نُوحٖ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُواْ هُمۡ أَظۡلَمَ وَأَطۡغَىٰ\nЯна У зот илгари Нуҳ қавмини ҳам (ҳалок қилгандир). Чунки уларнинг ўзлари жуда золим ва туғёнга тушган кимсалар эдилар'}, {'chapter': 53, 'verse': 53, 'text': 'وَٱلۡمُؤۡتَفِكَةَ أَهۡوَىٰ\nЯна У зот зеру-забар бўлган қишлоқни (яъни, Лут пайғамбар қавмини) ҳам қулатиб'}, {'chapter': 53, 'verse': 54, 'text': 'فَغَشَّىٰهَا مَا غَشَّىٰ\nуни ўраган нарса (яъни, осмонда ёғилган тошлар) билан ўраб кўмиб ташлади'}, {'chapter': 53, 'verse': 55, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكَ تَتَمَارَىٰ\nБас, (эй инсон) Парвардигорингнинг қайси неъматлари ҳақида шак-шубҳа қилурсан'}, {'chapter': 53, 'verse': 56, 'text': 'هَٰذَا نَذِيرٞ مِّنَ ٱلنُّذُرِ ٱلۡأُولَىٰٓ\nБу (яъни, Муҳаммад алайҳис-салоту вас-салом ҳам) аввалги огоҳлантиргувчилар (пайғамбарлар жумласи)дан бир огоҳлантиргувчидир'}, {'chapter': 53, 'verse': 57, 'text': 'أَزِفَتِ ٱلۡأٓزِفَةُ\nЯқин бўлгувчи (Қиёмат) яқин қолди'}, {'chapter': 53, 'verse': 58, 'text': 'لَيۡسَ لَهَا مِن دُونِ ٱللَّهِ كَاشِفَةٌ\nУнинг учун Аллоҳдан ўзга бирон очгувчи (қачон бўлишини билдиргувчи) йўқдир'}, {'chapter': 53, 'verse': 59, 'text': 'أَفَمِنۡ هَٰذَا ٱلۡحَدِيثِ تَعۡجَبُونَ\n(Эй мушриклар), ҳали сизлар мана шу Сўздан (Қуръондан) ажабланурсизларми'}, {'chapter': 53, 'verse': 60, 'text': 'وَتَضۡحَكُونَ وَلَا تَبۡكُونَ\n(Ундаги оятларнинг беназир ва бемисл ўгит-насиҳатларидан таъсирланиб) йиғламасдан, кулурсизларми'}, {'chapter': 53, 'verse': 61, 'text': 'وَأَنتُمۡ سَٰمِدُونَ\nҒофил бўлган ҳолларингизда (йиғламасдан, кулурсизларми)'}, {'chapter': 53, 'verse': 62, 'text': 'فَٱسۡجُدُواْۤ لِلَّهِۤ وَٱعۡبُدُواْ۩\nБас, сизлар ёлғиз Аллоҳга сажда ва ибодат қилинглар'}, {'chapter': 54, 'verse': 1, 'text': 'ٱقۡتَرَبَتِ ٱلسَّاعَةُ وَٱنشَقَّ ٱلۡقَمَرُ\n(Қиёмат) соати яқинлашиб қолди ва ой ҳам бўлинди'}, {'chapter': 54, 'verse': 2, 'text': 'وَإِن يَرَوۡاْ ءَايَةٗ يُعۡرِضُواْ وَيَقُولُواْ سِحۡرٞ مُّسۡتَمِرّٞ\nАгар улар (Қурайш кофирлари Муҳаммад алайҳис-салоту вас-саломнинг ҳақ пайғамбар эканликларига далолат қиладиган) бирон оят-мўъжиза кўрсалар (ҳам у пайғамбарга иймон келтириш ўрнига) юз ўгирурлар ва «(Бу) ҳар доимги сеҳр-ку!», дерлар'}, {'chapter': 54, 'verse': 3, 'text': 'وَكَذَّبُواْ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡۚ وَكُلُّ أَمۡرٖ مُّسۡتَقِرّٞ\nУлар (пайғамбарни) ёлғончи қилдилар ва ҳавойи нафсларига эргашдилар. — Ҳар бир иш (ўз) жойини топгувидир (яъни, гарчи кофирлар юз ўгирсалар-да, бу ҳақ дин Ер юзида қарор топур)'}, {'chapter': 54, 'verse': 4, 'text': 'وَلَقَدۡ جَآءَهُم مِّنَ ٱلۡأَنۢبَآءِ مَا فِيهِ مُزۡدَجَرٌ\n— Ҳолбуки, уларга (илгари ўтган кофирларнинг) хабарларидан иборат бир нарса келдики, унда (куфру залолатдан) тийилиш (лозим эканлигини уқтиргувчи ибратлар) бор эди'}, {'chapter': 54, 'verse': 5, 'text': 'حِكۡمَةُۢ بَٰلِغَةٞۖ فَمَا تُغۡنِ ٱلنُّذُرُ\nЕтук ҳикмат келдики, (унда куфру залолатдан тийилиш лозим эканлигини уқтиргувчи ибратлар бор эди). (Лекин улар ўзларининг куфрларидан тийилмадилар). Бас, (уларга ҳеч қандай) огоҳлантириш фойда бермас'}, {'chapter': 54, 'verse': 6, 'text': 'فَتَوَلَّ عَنۡهُمۡۘ يَوۡمَ يَدۡعُ ٱلدَّاعِ إِلَىٰ شَيۡءٖ نُّكُرٍ\n(Эй Муҳаммад алайҳис-салоту вас-салом), энди сиз улардан юз ўгиринг! Чорлагувчи (Исрофил фаришта) нотаниш бўлган бир нарсага (яъни, Қиёматдаги кофирлар учун даҳшатли ҳисоб-китобга) чорлайдиган Кунда'}, {'chapter': 54, 'verse': 7, 'text': 'خُشَّعًا أَبۡصَٰرُهُمۡ يَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ كَأَنَّهُمۡ جَرَادٞ مُّنتَشِرٞ\nУлар (кофирлар) кўзлари (қўрқувдан) қуйига боққан ҳолларида гўё ёйилган чигирткалар каби, қабрларидан чиқиб келурлар'}, {'chapter': 54, 'verse': 8, 'text': 'مُّهۡطِعِينَ إِلَى ٱلدَّاعِۖ يَقُولُ ٱلۡكَٰفِرُونَ هَٰذَا يَوۡمٌ عَسِرٞ\nЧорлагувчи томонига шошганларича кетар эканлар, у кофирлар «Бу оғир кундир», дерлар'}, {'chapter': 54, 'verse': 9, 'text': '۞كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ فَكَذَّبُواْ عَبۡدَنَا وَقَالُواْ مَجۡنُونٞ وَٱزۡدُجِرَ\nУлардан (Макка кофирларидан) илгари Нуҳнинг қавми ҳам (уни) ёлғончи қилган эди. — Улар Бизнинг бандамиз (Нуҳ)ни ёлғончи қилишиб: «(У) бир мажнун», дедилар ва у (ўз пайғамбарлик вазифасини адо этишдан) тўсиб қўйилди'}, {'chapter': 54, 'verse': 10, 'text': 'فَدَعَا رَبَّهُۥٓ أَنِّي مَغۡلُوبٞ فَٱنتَصِرۡ\nШунда у Парвардигорига: «Мен (у кофир қавмимдан) мағлубдирман. Ўзинг ёрдам қилгин», деб дуо-илтижо қилган эди'}, {'chapter': 54, 'verse': 11, 'text': 'فَفَتَحۡنَآ أَبۡوَٰبَ ٱلسَّمَآءِ بِمَآءٖ مُّنۡهَمِرٖ\nБиз осмон дарвозаларини (тинимсиз) қуйилгувчи сув — сел билан очиб юбордик'}, {'chapter': 54, 'verse': 12, 'text': 'وَفَجَّرۡنَا ٱلۡأَرۡضَ عُيُونٗا فَٱلۡتَقَى ٱلۡمَآءُ عَلَىٰٓ أَمۡرٖ قَدۡ قُدِرَ\nВа барча ердан чашмалар чиқариб юбордик. Бас, (осмон ва Ернинг) суви тақдир қилиб қўйилган бир иш (яъни, кофирларни ғарқ қилиб юбориш) устида учрашдилар'}, {'chapter': 54, 'verse': 13, 'text': 'وَحَمَلۡنَٰهُ عَلَىٰ ذَاتِ أَلۡوَٰحٖ وَدُسُرٖ\nУни (Нуҳни) эса тахта-ю, михлар эгасида (яъни, тахталар ва михлардан ясалган бир кемада) кўтардик'}, {'chapter': 54, 'verse': 14, 'text': 'تَجۡرِي بِأَعۡيُنِنَا جَزَآءٗ لِّمَن كَانَ كُفِرَ\nУ (кема) Бизнинг кўз ўнгимизда (ҳифзу-ҳимоямизда) сузар. (Бу ўз қавми томонидан пайғамбарлиги) инкор қилинган киши (яъни, Нуҳ пайғамбар) учун мукофотдир'}, {'chapter': 54, 'verse': 15, 'text': 'وَلَقَد تَّرَكۡنَٰهَآ ءَايَةٗ فَهَلۡ مِن مُّدَّكِرٖ\nДарҳақиқат, Биз у (Тўфон балоси)ни бир оят-ибрат қилиб қолдирдик. Бас, бирон эслатма-ибрат олгувчи борми'}, {'chapter': 54, 'verse': 16, 'text': 'فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nБас, Менинг (пайғамбарларимни ёлғончи қилган кимсаларга кўрсатган) азобим ва огоҳлантириш қандоқ бўлди'}, {'chapter': 54, 'verse': 17, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nҚасамки, Биз Қуръонни зикр-эслатма олиш учун осон қилиб қўйдик. Бас, бирон эслатма-ибрат олгувчи борми'}, {'chapter': 54, 'verse': 18, 'text': 'كَذَّبَتۡ عَادٞ فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nОд (қабиласи ҳам ўзларига юборилган Ҳуд пайғамбарни) ёлғончи қилди. Бас, Менинг азобим огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 19, 'text': 'إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِي يَوۡمِ نَحۡسٖ مُّسۡتَمِرّٖ\nДарвоқеъ, Биз уларнинг устига бир давомли нахс кунида (яъни, нахс-шумлиги ҳеч кетмайдиган кунда) бир даҳшатли бўронни юбордикки'}, {'chapter': 54, 'verse': 20, 'text': 'تَنزِعُ ٱلنَّاسَ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٖ مُّنقَعِرٖ\nу одамларни гўёки улар суғуриб олинган хурмо (дарахти)нинг таналаридек юлиб-учириб кетур'}, {'chapter': 54, 'verse': 21, 'text': 'فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nБас, Менинг азобим ва огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 22, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nҚасамки, Биз Қуръонни зикр-эслатма олиш учун осон қилиб қўйдик. Бас, бирон эслатма-ибрат олгувчи борми'}, {'chapter': 54, 'verse': 23, 'text': 'كَذَّبَتۡ ثَمُودُ بِٱلنُّذُرِ\nСамуд (қабиласи ҳам Солиҳ пайғамбар келтирган) огоҳлантиришларни ёлғон деди'}, {'chapter': 54, 'verse': 24, 'text': 'فَقَالُوٓاْ أَبَشَرٗا مِّنَّا وَٰحِدٗا نَّتَّبِعُهُۥٓ إِنَّآ إِذٗا لَّفِي ضَلَٰلٖ وَسُعُرٍ\nБас, улар айтдилар: «Бизлар ўзимизнинг орамиздаги бир одамга (пайғамбар деб) эргашурмизми?! У ҳолда бизлар ҳақиқатан ҳам гумроҳлик ва аҳмоқлик-нодонликда бўлурмиз-ку'}, {'chapter': 54, 'verse': 25, 'text': 'أَءُلۡقِيَ ٱلذِّكۡرُ عَلَيۡهِ مِنۢ بَيۡنِنَا بَلۡ هُوَ كَذَّابٌ أَشِرٞ\nБизларнинг орамиздан ана ўшанга эслатма — ваҳий ташланибдими?! Йўқ, у ҳаддан ошган ёлғончидир»'}, {'chapter': 54, 'verse': 26, 'text': 'سَيَعۡلَمُونَ غَدٗا مَّنِ ٱلۡكَذَّابُ ٱلۡأَشِرُ\nУлар эртага (Охиратда) ким ҳаддан ошган ёлғончи эканлигини билиб олажаклар'}, {'chapter': 54, 'verse': 27, 'text': 'إِنَّا مُرۡسِلُواْ ٱلنَّاقَةِ فِتۡنَةٗ لَّهُمۡ فَٱرۡتَقِبۡهُمۡ وَٱصۡطَبِرۡ\nАлбатта Биз уларни имтиҳон қилиш учун бир туя юборгувчидирмиз. Бас, (эй Солиҳ), сен (уларнинг қилмишларини) кузатиб ва (озор-азийятларига) сабр қилиб туравергин'}, {'chapter': 54, 'verse': 28, 'text': 'وَنَبِّئۡهُمۡ أَنَّ ٱلۡمَآءَ قِسۡمَةُۢ بَيۡنَهُمۡۖ كُلُّ شِرۡبٖ مُّحۡتَضَرٞ\nВа сувнинг улар (билан туя) ўртасидаги тақсимлаб қўйилгани (яъни, сувдан бир кун улар ичсалар, бир кун туя ичишлиги лозим эканлиги) ва ҳар бир ичишга (навбат келганида) ҳозир бўлиниши ҳақида уларга хабар бергин'}, {'chapter': 54, 'verse': 29, 'text': 'فَنَادَوۡاْ صَاحِبَهُمۡ فَتَعَاطَىٰ فَعَقَرَ\nСўнг улар (Самуд қабиласи) ўзларининг бир шерикларини (туяни ўлдириш учун) чорлаган эдилар, у (келиб қиличини) олди-да, (туяни) сўйиб юборди'}, {'chapter': 54, 'verse': 30, 'text': 'فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ\nБас, Менинг азобим ва огоҳлантиришим қандоқ бўлди'}, {'chapter': 54, 'verse': 31, 'text': 'إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ صَيۡحَةٗ وَٰحِدَةٗ فَكَانُواْ كَهَشِيمِ ٱلۡمُحۡتَظِرِ\nДарвоқеъ, Биз уларнинг устига бир даҳшатли қичқириқ юборган эдик, улар худди (қўйлари учун) қўра ясовчининг (қўрасидан тўқилган) хас-хашак каби бўлиб қолдилар'}, {'chapter': 54, 'verse': 32, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nҚасамки, Биз Қуръонни зикр-эслатма олиш учун осон қилиб қўйдик. Бас, бирон эслатма-ибрат олгувчи борми'}, {'chapter': 54, 'verse': 33, 'text': 'كَذَّبَتۡ قَوۡمُ لُوطِۭ بِٱلنُّذُرِ\nЛут қавми (у келтирган) огоҳлантиришларни ёлғон деди'}, {'chapter': 54, 'verse': 34, 'text': 'إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ حَاصِبًا إِلَّآ ءَالَ لُوطٖۖ نَّجَّيۡنَٰهُم بِسَحَرٖ\nДарвоқеъ, Биз уларнинг устига (тош) бўрон юбордик. Магар Лут хонадонигагина тонг-саҳарда нажот бердик'}, {'chapter': 54, 'verse': 35, 'text': 'نِّعۡمَةٗ مِّنۡ عِندِنَاۚ كَذَٰلِكَ نَجۡزِي مَن شَكَرَ\n(Бу нажот) Бизнинг ҳузуримиздан бўлган бир неъматдир. Шукр қилган кишиларни Биз мана шундай мукофотлаймиз'}, {'chapter': 54, 'verse': 36, 'text': 'وَلَقَدۡ أَنذَرَهُم بَطۡشَتَنَا فَتَمَارَوۡاْ بِٱلنُّذُرِ\nАниқки, (Лут) Бизнинг (азоб билан) ушлашимиздан уларни (яъни, ўз қавмини) огоҳлантирган эди, (аммо) улар огоҳлантиришлардан шубҳаланиб, (ёлғон дедилар)'}, {'chapter': 54, 'verse': 37, 'text': 'وَلَقَدۡ رَٰوَدُوهُ عَن ضَيۡفِهِۦ فَطَمَسۡنَآ أَعۡيُنَهُمۡ فَذُوقُواْ عَذَابِي وَنُذُرِ\nАниқки, улар (Лутнинг) меҳмон(лар)ини йўлдан урмоқчи бўлдилар. Бас, Биз уларнинг кўзларини кўр қилиб қўйдик: «Энди Менинг азобимни ва огоҳлантиришимни тотиб кўринглар!»'}, {'chapter': 54, 'verse': 38, 'text': 'وَلَقَدۡ صَبَّحَهُم بُكۡرَةً عَذَابٞ مُّسۡتَقِرّٞ\nҲақиқатан, эрта тонгда уларга барқарор-мангу азоб келди'}, {'chapter': 54, 'verse': 39, 'text': 'فَذُوقُواْ عَذَابِي وَنُذُرِ\nЭнди Менинг азобимни ва огоҳлантиришимни тотиб кўринглар'}, {'chapter': 54, 'verse': 40, 'text': 'وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ\nҚасамки, Биз Қуръонни зикр-эслатма олиш учун осон қилиб қўйдик. Бас, бирон эслатма-ибрат олгувчи борми'}, {'chapter': 54, 'verse': 41, 'text': 'وَلَقَدۡ جَآءَ ءَالَ فِرۡعَوۡنَ ٱلنُّذُرُ\nАниқки, Фиръавн хонадонига ҳам огоҳлантиришлар келди'}, {'chapter': 54, 'verse': 42, 'text': 'كَذَّبُواْ بِـَٔايَٰتِنَا كُلِّهَا فَأَخَذۡنَٰهُمۡ أَخۡذَ عَزِيزٖ مُّقۡتَدِرٍ\nУлар Бизнинг барча оят-мўъжизаларимизни ёлғон деганларидан сўнг, Биз уларни кучли-қудратли Зотнинг ушлаши билан ушладик (яъни, барчаларини денгизга ғарқ қилиб, юбордик)'}, {'chapter': 54, 'verse': 43, 'text': 'أَكُفَّارُكُمۡ خَيۡرٞ مِّنۡ أُوْلَـٰٓئِكُمۡ أَمۡ لَكُم بَرَآءَةٞ فِي ٱلزُّبُرِ\n(Эй Макка аҳли), сизларнинг кофирларингиз ана ўшалардан яхшироқми-ортиқроқми, ёки сизлар учун (самовий) китобларда ора очиқлик (васиқаси) борми (яъни, сизлар кофир бўлиб, ўз пайғамбарларингизни ёлғончи қилсангизлар ҳам ҳеч қандай азоб-уқубатга йўлиқмасликларингиз тўғрисида васиқаларингиз борми)'}, {'chapter': 54, 'verse': 44, 'text': 'أَمۡ يَقُولُونَ نَحۡنُ جَمِيعٞ مُّنتَصِرٞ\nБалки, улар: «Биз ғолиб жамоатдирмиз», дейишар'}, {'chapter': 54, 'verse': 45, 'text': 'سَيُهۡزَمُ ٱلۡجَمۡعُ وَيُوَلُّونَ ٱلدُّبُرَ\nЯқинда ўша жамоат енгилиб, ортларига қараб қочиб қолурлар'}, {'chapter': 54, 'verse': 46, 'text': 'بَلِ ٱلسَّاعَةُ مَوۡعِدُهُمۡ وَٱلسَّاعَةُ أَدۡهَىٰ وَأَمَرُّ\n(Бугина эмас), балки уларга (мангу азоб учун) ваъда қилинган вақт (Қиёмат) соатидир. У соат янада балолик, янада аччиқдир'}, {'chapter': 54, 'verse': 47, 'text': 'إِنَّ ٱلۡمُجۡرِمِينَ فِي ضَلَٰلٖ وَسُعُرٖ\nАлбатта жиноятчи-осий кимсалар гумроҳлик ва аҳмоқликдадирлар'}, {'chapter': 54, 'verse': 48, 'text': 'يَوۡمَ يُسۡحَبُونَ فِي ٱلنَّارِ عَلَىٰ وُجُوهِهِمۡ ذُوقُواْ مَسَّ سَقَرَ\nУлар юзтубан ҳолларида дўзахга судраладиган Кунда (уларга): «Дўзах азобини тотиб кўринглар!», (дейилур)'}, {'chapter': 54, 'verse': 49, 'text': 'إِنَّا كُلَّ شَيۡءٍ خَلَقۡنَٰهُ بِقَدَرٖ\nАлбатта Биз ҳар бир нарсани (аниқ) ўлчов билан яратдик'}, {'chapter': 54, 'verse': 50, 'text': 'وَمَآ أَمۡرُنَآ إِلَّا وَٰحِدَةٞ كَلَمۡحِۭ بِٱلۡبَصَرِ\n(Яратмоқчи бўлган ҳар бир нарсамиз учун) Бизнинг фармонимиз фақат биргина (яъни, «Бўл», деган) сўздир. (Бас, ўша нарса) кўз юмиб очгунча (йўқдан бор бўлур)'}, {'chapter': 54, 'verse': 51, 'text': 'وَلَقَدۡ أَهۡلَكۡنَآ أَشۡيَاعَكُمۡ فَهَلۡ مِن مُّدَّكِرٖ\nҚасамки, Биз сизларнинг ўхшашларингизни ҳалок қилдик. Бас, бирон эслатма-ибрат олгувчи борми'}, {'chapter': 54, 'verse': 52, 'text': 'وَكُلُّ شَيۡءٖ فَعَلُوهُ فِي ٱلزُّبُرِ\nУлар қилган ҳар бир нарса китобларда (номаи-аъмолларида) бордир'}, {'chapter': 54, 'verse': 53, 'text': 'وَكُلُّ صَغِيرٖ وَكَبِيرٖ مُّسۡتَطَرٌ\nҲар бир кичик ва катта (амал Лавҳул-Маҳфузда) битилмишдир'}, {'chapter': 54, 'verse': 54, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَنَهَرٖ\nАлбатта тақводор зотлар жаннатларда ва дарёлар (усти)да'}, {'chapter': 54, 'verse': 55, 'text': 'فِي مَقۡعَدِ صِدۡقٍ عِندَ مَلِيكٖ مُّقۡتَدِرِۭ\nҚодир Подшоҳ ҳузуридаги рози бўлинган ўринда бўлурлар'}, {'chapter': 55, 'verse': 1, 'text': 'ٱلرَّحۡمَٰنُ\nМеҳрибон (Аллоҳ Муҳаммад пайғамбар ва унинг умматларига)'}, {'chapter': 55, 'verse': 2, 'text': 'عَلَّمَ ٱلۡقُرۡءَانَ\nҚуръонни таълим берди'}, {'chapter': 55, 'verse': 3, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ\nУ зот инсонни яратди'}, {'chapter': 55, 'verse': 4, 'text': 'عَلَّمَهُ ٱلۡبَيَانَ\nВа унга (дилидаги мақсадини баён эта олиши учун) баённи (нутқни) таълим берди'}, {'chapter': 55, 'verse': 5, 'text': 'ٱلشَّمۡسُ وَٱلۡقَمَرُ بِحُسۡبَانٖ\nҚуёш ҳам, ой ҳам (аниқ) ҳисоб-ўлчов билан (жорий бўлур)'}, {'chapter': 55, 'verse': 6, 'text': 'وَٱلنَّجۡمُ وَٱلشَّجَرُ يَسۡجُدَانِ\nЎт-ўлан ҳам, дов-дарахт ҳам (ёлғиз Аллоҳга) сажда қилур — бўйинсунур'}, {'chapter': 55, 'verse': 7, 'text': 'وَٱلسَّمَآءَ رَفَعَهَا وَوَضَعَ ٱلۡمِيزَانَ\nУ зот осмонни баланд қилиб қўйди ва мезон-тарозини ўрнатди'}, {'chapter': 55, 'verse': 8, 'text': 'أَلَّا تَطۡغَوۡاْ فِي ٱلۡمِيزَانِ\nСизлар тош-тарозида туғёнга тушмасликларингиз (яъни, ўзгаларга зулм қилмасликларингиз) учун (мезон-тарозини ўрнатди)'}, {'chapter': 55, 'verse': 9, 'text': 'وَأَقِيمُواْ ٱلۡوَزۡنَ بِٱلۡقِسۡطِ وَلَا تُخۡسِرُواْ ٱلۡمِيزَانَ\n(Эй инсонлар, ўзаро олди-берди муомалаларингизда) тўғри, адолат билан тортинглар ва тарозидан уриб қолманглар'}, {'chapter': 55, 'verse': 10, 'text': 'وَٱلۡأَرۡضَ وَضَعَهَا لِلۡأَنَامِ\nЕрни У зот (жамийки) жонзотлар учун (яъни, улар жойлашиб, фойдаланишлари учун) паст-текис қилиб қўйди'}, {'chapter': 55, 'verse': 11, 'text': 'فِيهَا فَٰكِهَةٞ وَٱلنَّخۡلُ ذَاتُ ٱلۡأَكۡمَامِ\nУнда (турли-туман) мева-чева ва гулкосали хурмо дарахтлари бор'}, {'chapter': 55, 'verse': 12, 'text': 'وَٱلۡحَبُّ ذُو ٱلۡعَصۡفِ وَٱلرَّيۡحَانُ\nЯна сомонли дон-дун ва хушбўй (гуллар) бор'}, {'chapter': 55, 'verse': 13, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея (яъни, инкор қила) олурсизлар'}, {'chapter': 55, 'verse': 14, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ مِن صَلۡصَٰلٖ كَٱلۡفَخَّارِ\nУ зот инсонни (яьни, Одам алайҳиссаломни) сопол каби қуриган қора лойдан яратди'}, {'chapter': 55, 'verse': 15, 'text': 'وَخَلَقَ ٱلۡجَآنَّ مِن مَّارِجٖ مِّن نَّارٖ\nЖинларни эса аланга-оловдан яратди'}, {'chapter': 55, 'verse': 16, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 17, 'text': 'رَبُّ ٱلۡمَشۡرِقَيۡنِ وَرَبُّ ٱلۡمَغۡرِبَيۡنِ\n(Аллоҳ) икки шарқнинг (яъни, кун ва ой ботадиган жойларнинг) ҳам Парвардигоридир'}, {'chapter': 55, 'verse': 18, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 19, 'text': 'مَرَجَ ٱلۡبَحۡرَيۡنِ يَلۡتَقِيَانِ\nУ зот икки денгиз-дарёни бир-бирлари билан учрашадиган ҳолларида (ёнма-ён) оқизиб қўйди'}, {'chapter': 55, 'verse': 20, 'text': 'بَيۡنَهُمَا بَرۡزَخٞ لَّا يَبۡغِيَانِ\n(Аммо) у иккисининг ўрталарида бир тўсиқ бўлиб, улар (ўша тўсиқдан) ошиб ўтмаслар'}, {'chapter': 55, 'verse': 21, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 22, 'text': 'يَخۡرُجُ مِنۡهُمَا ٱللُّؤۡلُؤُ وَٱلۡمَرۡجَانُ\nУ иккисидан (бири бўлмиш денгиздан) марварид ва маржонлар чиқур'}, {'chapter': 55, 'verse': 23, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 24, 'text': 'وَلَهُ ٱلۡجَوَارِ ٱلۡمُنشَـَٔاتُ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ\nДенгиздаги тоғлар каби баланд кўтарилган қатновчилар (яъни, елканли кемалар) ҳам ёлғиз У зотникидир'}, {'chapter': 55, 'verse': 25, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 26, 'text': 'كُلُّ مَنۡ عَلَيۡهَا فَانٖ\n(Ер) юзидаги барча жонзот фонийдир'}, {'chapter': 55, 'verse': 27, 'text': 'وَيَبۡقَىٰ وَجۡهُ رَبِّكَ ذُو ٱلۡجَلَٰلِ وَٱلۡإِكۡرَامِ\nБуюклик ва карам соҳиби бўлган Парвардигорингизнинг Юзи — Ўзигина боқий мангу қолур'}, {'chapter': 55, 'verse': 28, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 29, 'text': 'يَسۡـَٔلُهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ كُلَّ يَوۡمٍ هُوَ فِي شَأۡنٖ\nОсмонлар ва Ердаги (барча) жонзот (Унга муҳтождир ва бор тилак-мақсадларини ёлғиз) Ундан сўрар. У зот ҳар куни бир иш-амалдадир'}, {'chapter': 55, 'verse': 30, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 31, 'text': 'سَنَفۡرُغُ لَكُمۡ أَيُّهَ ٱلثَّقَلَانِ\nЯқинда (Қиёмат қойим бўлган Кунда) сизлар(ни ҳисоб-китоб қилиш) учун фориғ бўлажакмиз, эй инсу жинлар'}, {'chapter': 55, 'verse': 32, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 33, 'text': 'يَٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ إِنِ ٱسۡتَطَعۡتُمۡ أَن تَنفُذُواْ مِنۡ أَقۡطَارِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ فَٱنفُذُواْۚ لَا تَنفُذُونَ إِلَّا بِسُلۡطَٰنٖ\nЭй жин ва инс жамоаси! Агар сизлар (Аллоҳнинг жазосидан қочиб) осмонлар ва Ер чегараларидан ўтиб кетишга қодир бўлсангизлар, у ҳолда ўтиб кетаверинглар! Сизлар фақат куч-қудрат билангина ўта олурсизлар, (лекин у Кунда сизларда ҳеч қандай куч-қудрат бўлмас)'}, {'chapter': 55, 'verse': 34, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 35, 'text': 'يُرۡسَلُ عَلَيۡكُمَا شُوَاظٞ مِّن نَّارٖ وَنُحَاسٞ فَلَا تَنتَصِرَانِ\n(У Кунда) сизларнинг устингизга оловдан бир аланга ва тутун юборилурки, сизлар (ундан ҳаргиз) қутула олмассизлар'}, {'chapter': 55, 'verse': 36, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 37, 'text': 'فَإِذَا ٱنشَقَّتِ ٱلسَّمَآءُ فَكَانَتۡ وَرۡدَةٗ كَٱلدِّهَانِ\nЭнди қачон (Қиёмат қойим бўлгач) осмон ёрилиб, (жаҳаннам ҳароратидан доғланган) ёғ каби қизариб қолганида'}, {'chapter': 55, 'verse': 38, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар). Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 39, 'text': 'فَيَوۡمَئِذٖ لَّا يُسۡـَٔلُ عَن ذَنۢبِهِۦٓ إِنسٞ وَلَا جَآنّٞ\nАна ўша Кунда на бир инс ва на бир жин (қилиб ўтган) гуноҳидан сўралмас'}, {'chapter': 55, 'verse': 40, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 41, 'text': 'يُعۡرَفُ ٱلۡمُجۡرِمُونَ بِسِيمَٰهُمۡ فَيُؤۡخَذُ بِٱلنَّوَٰصِي وَٱلۡأَقۡدَامِ\n(Чунки у Кунда) жиноятчи-осий кимсалар сиймо-белгиларидан танилиб, пешона ва оёқларидан олинурлар (ва жаҳаннамга отилурлар)'}, {'chapter': 55, 'verse': 42, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 43, 'text': 'هَٰذِهِۦ جَهَنَّمُ ٱلَّتِي يُكَذِّبُ بِهَا ٱلۡمُجۡرِمُونَ\nЖиноятчи-осий кимсалар ёлғон дейдиган жаҳаннам мана шудир'}, {'chapter': 55, 'verse': 44, 'text': 'يَطُوفُونَ بَيۡنَهَا وَبَيۡنَ حَمِيمٍ ءَانٖ\nУлар ўша (жаҳаннам) билан ўта қайноқ сув ўртасида айланаверарлар'}, {'chapter': 55, 'verse': 45, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг кайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 46, 'text': 'وَلِمَنۡ خَافَ مَقَامَ رَبِّهِۦ جَنَّتَانِ\nПарвардигори (ҳузурида) туришидан (яъни, ўзининг Парвардигор олдида туриб, ҳаёти дунёда қилиб ўтган барча амалларига жавоб беришдан) қўрққан киши учун икки жаннат бордир'}, {'chapter': 55, 'verse': 47, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 48, 'text': 'ذَوَاتَآ أَفۡنَانٖ\n(У икки жаннат сон-саноқсиз) шох-новдалар эгасидирлар (яъни, сермева ва соя-салқиндирлар)'}, {'chapter': 55, 'verse': 49, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар). Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 50, 'text': 'فِيهِمَا عَيۡنَانِ تَجۡرِيَانِ\nУ иккисида икки оқар чашма бордир'}, {'chapter': 55, 'verse': 51, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 52, 'text': 'فِيهِمَا مِن كُلِّ فَٰكِهَةٖ زَوۡجَانِ\nУ иккисида барча мевадан икки нави бордир'}, {'chapter': 55, 'verse': 53, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 54, 'text': 'مُتَّكِـِٔينَ عَلَىٰ فُرُشِۭ بَطَآئِنُهَا مِنۡ إِسۡتَبۡرَقٖۚ وَجَنَى ٱلۡجَنَّتَيۡنِ دَانٖ\n(Аҳли жаннат) астарлари шойидан бўлган кўрпачалар устида ястанган ҳолларида (ўлтирурлар). У жаннатларнинг меваси яқин бўлгувчидир (яъни, жаннат аҳли қай-бир мевани ейишни истаса, ўша меванинг ўзи уларга эгилиб, яқин келур)'}, {'chapter': 55, 'verse': 55, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 56, 'text': 'فِيهِنَّ قَٰصِرَٰتُ ٱلطَّرۡفِ لَمۡ يَطۡمِثۡهُنَّ إِنسٞ قَبۡلَهُمۡ وَلَا جَآنّٞ\nУ (жаннат)ларда шундай кўзларини (бегона эркакларга қарашдан) тийгувчи (қиз)лар борки, уларга (жаннат аҳлидан) илгари на бир инс ва на бир жин тегингандир'}, {'chapter': 55, 'verse': 57, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 58, 'text': 'كَأَنَّهُنَّ ٱلۡيَاقُوتُ وَٱلۡمَرۡجَانُ\nУлар (софлик ва оқликда) гўёки ёқут ва маржондирлар'}, {'chapter': 55, 'verse': 59, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 60, 'text': 'هَلۡ جَزَآءُ ٱلۡإِحۡسَٰنِ إِلَّا ٱلۡإِحۡسَٰنُ\nЭҳсон-яхшиликнинг жазоси-мукофоти фақат яхшилиқдир'}, {'chapter': 55, 'verse': 61, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 62, 'text': 'وَمِن دُونِهِمَا جَنَّتَانِ\nУ икки (жаннат)дан қуйироқда яна икки жаннат бордир'}, {'chapter': 55, 'verse': 63, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 64, 'text': 'مُدۡهَآمَّتَانِ\n(У икки жаннат) серсув ва серяпроқликдан қорамтирдирлар'}, {'chapter': 55, 'verse': 65, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 66, 'text': 'فِيهِمَا عَيۡنَانِ نَضَّاخَتَانِ\nУ иккисида тинмай отилиб тургувчи икки чашма бордир'}, {'chapter': 55, 'verse': 67, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 68, 'text': 'فِيهِمَا فَٰكِهَةٞ وَنَخۡلٞ وَرُمَّانٞ\nУ иккисида мева, хурмо ва анорлар бордир'}, {'chapter': 55, 'verse': 69, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 70, 'text': 'فِيهِنَّ خَيۡرَٰتٌ حِسَانٞ\nУ (жаннат)ларда хушхулқ ва гўзал юзли (аёл)лар бордир'}, {'chapter': 55, 'verse': 71, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 72, 'text': 'حُورٞ مَّقۡصُورَٰتٞ فِي ٱلۡخِيَامِ\n(Улар) чодирларда асралган ҳурлардир'}, {'chapter': 55, 'verse': 73, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 74, 'text': 'لَمۡ يَطۡمِثۡهُنَّ إِنسٞ قَبۡلَهُمۡ وَلَا جَآنّٞ\nУларга (жаннат аҳлидан) илгари на бир инс ва на бир жин тегингандир'}, {'chapter': 55, 'verse': 75, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 76, 'text': 'مُتَّكِـِٔينَ عَلَىٰ رَفۡرَفٍ خُضۡرٖ وَعَبۡقَرِيٍّ حِسَانٖ\n(Жаннат аҳли у жаннатларда) яшил болишлар ва гўзал гиламлар устида ястанган ҳолларида (ўлтирурлар)'}, {'chapter': 55, 'verse': 77, 'text': 'فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ\nБас, (эй инсонлар ва жинлар), Парвардигорингизнинг қайси неъматларини ёлғон дея олурсизлар'}, {'chapter': 55, 'verse': 78, 'text': 'تَبَٰرَكَ ٱسۡمُ رَبِّكَ ذِي ٱلۡجَلَٰلِ وَٱلۡإِكۡرَامِ\nБуюклик ва карам соҳиби бўлган Парвардигорингизнинг номи баракотли — буюкдир'}, {'chapter': 56, 'verse': 1, 'text': 'إِذَا وَقَعَتِ ٱلۡوَاقِعَةُ\nҚачон Воқеа — Қиёмат воқеъ бўлса'}, {'chapter': 56, 'verse': 2, 'text': 'لَيۡسَ لِوَقۡعَتِهَا كَاذِبَةٌ\nУ воқеъ бўлган чоғда бирон ёлғон сўзлагувчи бўлмас'}, {'chapter': 56, 'verse': 3, 'text': 'خَافِضَةٞ رَّافِعَةٌ\n(Қиёмат кофирларни дўзахга дучор этиш билан) паст қилгувчи, (мўминларни эса жаннатга киритиш билан) баланд қилгувчидир'}, {'chapter': 56, 'verse': 4, 'text': 'إِذَا رُجَّتِ ٱلۡأَرۡضُ رَجّٗا\n(У кунда) Ер (қаттиқ) ларзага келди'}, {'chapter': 56, 'verse': 5, 'text': 'وَبُسَّتِ ٱلۡجِبَالُ بَسّٗا\nТоғлар титилди'}, {'chapter': 56, 'verse': 6, 'text': 'فَكَانَتۡ هَبَآءٗ مُّنۢبَثّٗا\nВа чанг-тўзонга айланди'}, {'chapter': 56, 'verse': 7, 'text': 'وَكُنتُمۡ أَزۡوَٰجٗا ثَلَٰثَةٗ\nВа сизлар уч синф-тоифа бўлиб қолдингизлар'}, {'chapter': 56, 'verse': 8, 'text': 'فَأَصۡحَٰبُ ٱلۡمَيۡمَنَةِ مَآ أَصۡحَٰبُ ٱلۡمَيۡمَنَةِ\nБас, (у уч тоифадан биринчиси) ўнг томон эгаларидир. Ўнг томон эгалари (бўлмоқ) не (саодат)дир'}, {'chapter': 56, 'verse': 9, 'text': 'وَأَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ مَآ أَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ\n(Иккинчи тоифа) шум-чап томон эгаларидир. Шум-чап томон эгалари (бўлмоқ) не (бахтсизлик)дир'}, {'chapter': 56, 'verse': 10, 'text': 'وَٱلسَّـٰبِقُونَ ٱلسَّـٰبِقُونَ\n(Учинчи тоифа бўлмиш барча яхши амалларга) пешқадам бўлгувчи зотлар (жаннат неъматларига эришишида ҳам) пешқадам бўлгувчи зотлардир'}, {'chapter': 56, 'verse': 11, 'text': 'أُوْلَـٰٓئِكَ ٱلۡمُقَرَّبُونَ\nАна ўшалар (Аллоҳ таолога) яқин қилингувчилардир'}, {'chapter': 56, 'verse': 12, 'text': 'فِي جَنَّـٰتِ ٱلنَّعِيمِ\n(Улар) ноз-неъмат боғларидадирлар'}, {'chapter': 56, 'verse': 13, 'text': 'ثُلَّةٞ مِّنَ ٱلۡأَوَّلِينَ\n(У пешқадамлар) аввалги (уммат)лардан кўпчилик'}, {'chapter': 56, 'verse': 14, 'text': 'وَقَلِيلٞ مِّنَ ٱلۡأٓخِرِينَ\nкейингилардан (яъни, Муҳаммад алайҳис-салоту вас-салом умматларидан) эса оздир'}, {'chapter': 56, 'verse': 15, 'text': 'عَلَىٰ سُرُرٖ مَّوۡضُونَةٖ\n(Улар олтиндан) тўқилган сўриларда'}, {'chapter': 56, 'verse': 16, 'text': 'مُّتَّكِـِٔينَ عَلَيۡهَا مُتَقَٰبِلِينَ\nястанган ҳолларида бир-бирларига рўбарў бўлиб (ўлтирурлар)'}, {'chapter': 56, 'verse': 17, 'text': 'يَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ\nУстларида эса мангу ёш (яъни, ҳеч қаримайдиган) болалар (қадаҳларни) айлантириб турурлар'}, {'chapter': 56, 'verse': 18, 'text': 'بِأَكۡوَابٖ وَأَبَارِيقَ وَكَأۡسٖ مِّن مَّعِينٖ\nОқар чашмадан (майли) қадаҳларни, кўзаларни ва косаларни (айлантириб турурлар)'}, {'chapter': 56, 'verse': 19, 'text': 'لَّا يُصَدَّعُونَ عَنۡهَا وَلَا يُنزِفُونَ\nУ (май)лардан уларнинг бошлари ҳам оғримас, маст ҳам бўлмаслар'}, {'chapter': 56, 'verse': 20, 'text': 'وَفَٰكِهَةٖ مِّمَّا يَتَخَيَّرُونَ\nЯна ўзлари танлаб оладиган мева-чева'}, {'chapter': 56, 'verse': 21, 'text': 'وَلَحۡمِ طَيۡرٖ مِّمَّا يَشۡتَهُونَ\nҲамда иштаҳалари тортган қуш гўштлари ҳам (айлантириб турилур)'}, {'chapter': 56, 'verse': 22, 'text': 'وَحُورٌ عِينٞ\nЯна (улар учун) оҳу кўз ҳурлар бордир'}, {'chapter': 56, 'verse': 23, 'text': 'كَأَمۡثَٰلِ ٱللُّؤۡلُوِٕ ٱلۡمَكۡنُونِ\nХудди яшириб қўйилган марварид мисол (оҳу кўз ҳурлар бордир)'}, {'chapter': 56, 'verse': 24, 'text': 'جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ\n(Бу) улар (ҳаёти дунёда) қилиб ўтган амалларининг мукофотидир'}, {'chapter': 56, 'verse': 25, 'text': 'لَا يَسۡمَعُونَ فِيهَا لَغۡوٗا وَلَا تَأۡثِيمًا\nУлар у жойда бирон беҳуда ва гуноҳ-ёлғон (сўз)ни эшитмаслар'}, {'chapter': 56, 'verse': 26, 'text': 'إِلَّا قِيلٗا سَلَٰمٗا سَلَٰمٗا\nфақат «Салом-салом», дейилганинигина (эшитурлар)'}, {'chapter': 56, 'verse': 27, 'text': 'وَأَصۡحَٰبُ ٱلۡيَمِينِ مَآ أَصۡحَٰبُ ٱلۡيَمِينِ\nЎнг томон эгалари... Ўнг томон эгалари (бўлмоқ) не (саодат)дир'}, {'chapter': 56, 'verse': 28, 'text': 'فِي سِدۡرٖ مَّخۡضُودٖ\n(Улар) тикансиз бутазорларда'}, {'chapter': 56, 'verse': 29, 'text': 'وَطَلۡحٖ مَّنضُودٖ\nТизилган бананзорларда'}, {'chapter': 56, 'verse': 30, 'text': 'وَظِلّٖ مَّمۡدُودٖ\nЁйилган соя (ости)да'}, {'chapter': 56, 'verse': 31, 'text': 'وَمَآءٖ مَّسۡكُوبٖ\nОқизиб қўйилган сув (усти)да'}, {'chapter': 56, 'verse': 32, 'text': 'وَفَٰكِهَةٖ كَثِيرَةٖ\nКўпдан-кўп (турли туман) мева-чева (ости)да'}, {'chapter': 56, 'verse': 33, 'text': 'لَّا مَقۡطُوعَةٖ وَلَا مَمۡنُوعَةٖ\n(У мевалар) тугаб қолмайди ва манъ ҳам этилмайди'}, {'chapter': 56, 'verse': 34, 'text': 'وَفُرُشٖ مَّرۡفُوعَةٍ\nБаланд кўтарилган кўрпачалар (усти)дадирлар'}, {'chapter': 56, 'verse': 35, 'text': 'إِنَّآ أَنشَأۡنَٰهُنَّ إِنشَآءٗ\nДарвоқеъ, Биз уларни (оху кўз ҳурларни) дафъатан пайдо қилдик (яъни, улар онадан туғилмадилар, балки Аллоҳнинг қудрати билан дафъатан пайдо бўлдилар)'}, {'chapter': 56, 'verse': 36, 'text': 'فَجَعَلۡنَٰهُنَّ أَبۡكَارًا\nСўнг (уларни ўнг томон эгалари учун) бокира қизлар қилиб қўйдик'}, {'chapter': 56, 'verse': 37, 'text': 'عُرُبًا أَتۡرَابٗا\n(Улар ёшда ҳам, ҳусну жамол ва қадду қоматда ҳам) бири-бирига тенг, эҳтиросли (ҳурлардир)'}, {'chapter': 56, 'verse': 38, 'text': 'لِّأَصۡحَٰبِ ٱلۡيَمِينِ\n(Уларни) ўнг томон эгалари учун (қилдик)'}, {'chapter': 56, 'verse': 39, 'text': 'ثُلَّةٞ مِّنَ ٱلۡأَوَّلِينَ\n(Ўнг томон эгалари бўлмиш саодатманд кишилар) аввалги (уммат)лардан ҳам кўпчилик'}, {'chapter': 56, 'verse': 40, 'text': 'وَثُلَّةٞ مِّنَ ٱلۡأٓخِرِينَ\nкейингилардан (яъни, Муҳаммад алайҳис-салоту вас-салом умматларидан) ҳам кўпчилиқдир'}, {'chapter': 56, 'verse': 41, 'text': 'وَأَصۡحَٰبُ ٱلشِّمَالِ مَآ أَصۡحَٰبُ ٱلشِّمَالِ\nЧап томон эгалари... Чап томон эгалари (бўлмоқ) не (бахтсизлик)дир'}, {'chapter': 56, 'verse': 42, 'text': 'فِي سَمُومٖ وَحَمِيمٖ\n(Улар дўзахдан тинимсиз эсиб турадиган, баданларни илма-тешик қилиб юборувчи) Самум (шамоли) ва қайноқ сув ичида'}, {'chapter': 56, 'verse': 43, 'text': 'وَظِلّٖ مِّن يَحۡمُومٖ\nBа қора тутундан иборат «соя» (ости)дадирлар'}, {'chapter': 56, 'verse': 44, 'text': 'لَّا بَارِدٖ وَلَا كَرِيمٍ\n(У «соя») на салқин, на фойдалидир'}, {'chapter': 56, 'verse': 45, 'text': 'إِنَّهُمۡ كَانُواْ قَبۡلَ ذَٰلِكَ مُتۡرَفِينَ\nДарвоқеъ, улар бундан илгари (яъни, ҳаёти дунёда хирсу ҳавасга муккадан кетган) боёнлар эдилар'}, {'chapter': 56, 'verse': 46, 'text': 'وَكَانُواْ يُصِرُّونَ عَلَى ٱلۡحِنثِ ٱلۡعَظِيمِ\nУлар улуғ гуноҳ (яъни, ширк) устида оёқ тираб турар эдилар'}, {'chapter': 56, 'verse': 47, 'text': 'وَكَانُواْ يَقُولُونَ أَئِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ\nВа: «Бизлар ўлиб, тупроқ ва суякларга айлангач, ростдан ҳам қайта тирилгувчимизми'}, {'chapter': 56, 'verse': 48, 'text': 'أَوَءَابَآؤُنَا ٱلۡأَوَّلُونَ\nаввалги ота-боболаримиз ҳам-а?!», деб (масхара қилар) эдилар'}, {'chapter': 56, 'verse': 49, 'text': 'قُلۡ إِنَّ ٱلۡأَوَّلِينَ وَٱلۡأٓخِرِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, сиз уларга) айтинг: «Албатта аввалгилар ҳам, кейингилар ҳам'}, {'chapter': 56, 'verse': 50, 'text': 'لَمَجۡمُوعُونَ إِلَىٰ مِيقَٰتِ يَوۡمٖ مَّعۡلُومٖ\nМаълум Кундаги белгиланган вақтга шак-шубҳасиз, тўплангувчидирлар'}, {'chapter': 56, 'verse': 51, 'text': 'ثُمَّ إِنَّكُمۡ أَيُّهَا ٱلضَّآلُّونَ ٱلۡمُكَذِّبُونَ\nСўнгра албатта сизлар, эй (қайта тирилишни) ёлғон дегувчи гумроҳлар'}, {'chapter': 56, 'verse': 52, 'text': 'لَأٓكِلُونَ مِن شَجَرٖ مِّن زَقُّومٖ\nШак-шубҳасиз, (жаҳаннамнинг ўртасида ўсадиган) Заққум дарахтидан егувчи'}, {'chapter': 56, 'verse': 53, 'text': 'فَمَالِـُٔونَ مِنۡهَا ٱلۡبُطُونَ\n— ундан қоринларингизни тўлдиргувчидирсизлар'}, {'chapter': 56, 'verse': 54, 'text': 'فَشَٰرِبُونَ عَلَيۡهِ مِنَ ٱلۡحَمِيمِ\nСўнг унинг устига қайноқ сувдан ичгувчи, (ичганда ҳам)'}, {'chapter': 56, 'verse': 55, 'text': 'فَشَٰرِبُونَ شُرۡبَ ٱلۡهِيمِ\n— Ташна-тўймас туялар каби ичгувчидирсизлар'}, {'chapter': 56, 'verse': 56, 'text': 'هَٰذَا نُزُلُهُمۡ يَوۡمَ ٱلدِّينِ\nМана шу уларнинг жазо (Қиёмат) Кунидаги «зиёфатларидир»'}, {'chapter': 56, 'verse': 57, 'text': 'نَحۡنُ خَلَقۡنَٰكُمۡ فَلَوۡلَا تُصَدِّقُونَ\n(Эй инсонлар), сизларни Биз Ўзимиз яратдик-ку! Энди сизлар (Охиратда яна қайта яратишга қодир эканлигимизни) тасдиқ этмайсизларми'}, {'chapter': 56, 'verse': 58, 'text': 'أَفَرَءَيۡتُم مَّا تُمۡنُونَ\nСизлар (хотинларингизнинг бачадонига) тўкадиган маний-уруғ ҳақида ҳеч ўйлаб кўрдингларми'}, {'chapter': 56, 'verse': 59, 'text': 'ءَأَنتُمۡ تَخۡلُقُونَهُۥٓ أَمۡ نَحۡنُ ٱلۡخَٰلِقُونَ\nУни сизлар (комил инсон қилиб) яратурмисизлар ёки Биз яратгувчимизми'}, {'chapter': 56, 'verse': 60, 'text': 'نَحۡنُ قَدَّرۡنَا بَيۡنَكُمُ ٱلۡمَوۡتَ وَمَا نَحۡنُ بِمَسۡبُوقِينَ\nСизларнинг ўртангиздаги ўлимни ҳам Биз белгилаб қўйгандирмиз (яъни, сизларга ҳаётни ҳам, ўлимни ҳам Биз берурмиз) ва Биз ожиз эмасдирмиз'}, {'chapter': 56, 'verse': 61, 'text': 'عَلَىٰٓ أَن نُّبَدِّلَ أَمۡثَٰلَكُمۡ وَنُنشِئَكُمۡ فِي مَا لَا تَعۡلَمُونَ\n(Сизларни вафот топдириб, ўрнингларга) сизларга ўхшаганларни алмаштириб қўйишга ҳамда сизларни ўзларингиз билмайдиган ҳолат-суратда қайта пайдо қилишга ҳам (ожиз эмасдирмиз)'}, {'chapter': 56, 'verse': 62, 'text': 'وَلَقَدۡ عَلِمۡتُمُ ٱلنَّشۡأَةَ ٱلۡأُولَىٰ فَلَوۡلَا تَذَكَّرُونَ\nДарвоқеъ, сизлар дастлабки пайдо бўлишларингиз (қандай бўлгани)ни билдинглар-ку! Бас, эслатма-ибрат олмайсизларми'}, {'chapter': 56, 'verse': 63, 'text': 'أَفَرَءَيۡتُم مَّا تَحۡرُثُونَ\nСизлар экаётган зироатларингиз ҳақида ҳеч ўйлаб кўрдингларми'}, {'chapter': 56, 'verse': 64, 'text': 'ءَأَنتُمۡ تَزۡرَعُونَهُۥٓ أَمۡ نَحۡنُ ٱلزَّـٰرِعُونَ\nУни сизлар ундириб-ўстирурмисизлар ёки Биз ундиргувчимизми'}, {'chapter': 56, 'verse': 65, 'text': 'لَوۡ نَشَآءُ لَجَعَلۡنَٰهُ حُطَٰمٗا فَظَلۡتُمۡ تَفَكَّهُونَ\nАгар Биз хоҳласак, албатта уни қуруқ чўп қилиб қўйган бўлур эдик, бас, сизлар ажабланган ҳолларингизда'}, {'chapter': 56, 'verse': 66, 'text': 'إِنَّا لَمُغۡرَمُونَ\n«Шак-шубҳасиз, бизлар зиён кўргувчилармиз'}, {'chapter': 56, 'verse': 67, 'text': 'بَلۡ نَحۡنُ مَحۡرُومُونَ\nЙўқ, бизлар (барча ризқу рўздан) маҳрум бўлгувчилармиз», (деб надомат қилиб қолган бўлур эдингазлар)'}, {'chapter': 56, 'verse': 68, 'text': 'أَفَرَءَيۡتُمُ ٱلۡمَآءَ ٱلَّذِي تَشۡرَبُونَ\nСизлар ўзларингиз ичадиган сув ҳақида ҳеч ўйлаб кўрдингларми'}, {'chapter': 56, 'verse': 69, 'text': 'ءَأَنتُمۡ أَنزَلۡتُمُوهُ مِنَ ٱلۡمُزۡنِ أَمۡ نَحۡنُ ٱلۡمُنزِلُونَ\nУни булутлардан сизлар ёғдирдингларми ёки Биз ёғдиргувчимизми'}, {'chapter': 56, 'verse': 70, 'text': 'لَوۡ نَشَآءُ جَعَلۡنَٰهُ أُجَاجٗا فَلَوۡلَا تَشۡكُرُونَ\nАгар Биз хоҳласак, уни шўр қилиб қўйган бўлур эдик. Бас, шукр қилмайсизларми'}, {'chapter': 56, 'verse': 71, 'text': 'أَفَرَءَيۡتُمُ ٱلنَّارَ ٱلَّتِي تُورُونَ\nСизлар ёқадиган ўт-олов ҳақида ҳеч ўйлаб кўрдингларми'}, {'chapter': 56, 'verse': 72, 'text': 'ءَأَنتُمۡ أَنشَأۡتُمۡ شَجَرَتَهَآ أَمۡ نَحۡنُ ٱلۡمُنشِـُٔونَ\nУнинг дарахтини сизлар пайдо қилдингларми ёки Биз пайдо қилгувчимизми'}, {'chapter': 56, 'verse': 73, 'text': 'نَحۡنُ جَعَلۡنَٰهَا تَذۡكِرَةٗ وَمَتَٰعٗا لِّلۡمُقۡوِينَ\nБиз уни (жаҳаннам ўтидан) бир эслатма ва (саҳроларда юрадиган) мусофир-йўловчилар учун фойдаланадиган нарса қилиб қўйдик'}, {'chapter': 56, 'verse': 74, 'text': 'فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), улуғ Парвардигорингизнинг номини (ҳар қандай айб-нуқсондан) поклаб-тасбеҳ айтинг'}, {'chapter': 56, 'verse': 75, 'text': '۞فَلَآ أُقۡسِمُ بِمَوَٰقِعِ ٱلنُّجُومِ\nМен юлдузларнинг ботар жойларига қасам ичурманки'}, {'chapter': 56, 'verse': 76, 'text': 'وَإِنَّهُۥ لَقَسَمٞ لَّوۡ تَعۡلَمُونَ عَظِيمٌ\n— Ҳолбуки, бу (қасам) агар билсангизлар, шак-шубҳасиз, улуғ-катта қасамдир'}, {'chapter': 56, 'verse': 77, 'text': 'إِنَّهُۥ لَقُرۡءَانٞ كَرِيمٞ\n— албатта у Улуғ Қуръондир'}, {'chapter': 56, 'verse': 78, 'text': 'فِي كِتَٰبٖ مَّكۡنُونٖ\n(Қуръон сеҳр ёки уйдирма эмас, балки) асралган Китобдаги (Лавҳул-Маҳфуздаги Улуғ Қуръондир)'}, {'chapter': 56, 'verse': 79, 'text': 'لَّا يَمَسُّهُۥٓ إِلَّا ٱلۡمُطَهَّرُونَ\nУни фақат таҳоратли — пок кишиларгина ушларлар'}, {'chapter': 56, 'verse': 80, 'text': 'تَنزِيلٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\n(У) барча оламлар Парвардигори томонидан нозил қилингандир'}, {'chapter': 56, 'verse': 81, 'text': 'أَفَبِهَٰذَا ٱلۡحَدِيثِ أَنتُم مُّدۡهِنُونَ\nБас, (эй кофирлар), сизлар мана шу Сўз — Қуръонга бепарволик қилурмисизлар (яъни, сизлар шундай улуғ Китобни назарларингизга илмасдан инкор қилмоқдасизларми)'}, {'chapter': 56, 'verse': 82, 'text': 'وَتَجۡعَلُونَ رِزۡقَكُمۡ أَنَّكُمۡ تُكَذِّبُونَ\nВа сизлар (Қуръонни) ёлғон дейиш(дек бадбахтлик)ни ўзларингизнинг ризқ-насибаларингиз қилиб олурсизларми'}, {'chapter': 56, 'verse': 83, 'text': 'فَلَوۡلَآ إِذَا بَلَغَتِ ٱلۡحُلۡقُومَ\nБас, қачон (вафот қилаётган кишининг жони) халқумига етганида'}, {'chapter': 56, 'verse': 84, 'text': 'وَأَنتُمۡ حِينَئِذٖ تَنظُرُونَ\n— Ҳолбуки, ўша вақтда сизлар қараб турурсизлар'}, {'chapter': 56, 'verse': 85, 'text': 'وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنكُمۡ وَلَٰكِن لَّا تُبۡصِرُونَ\nВа Биз унга сизлардан кўра яқинроқ булурмиз, лекин сизлар (буни) курмайсизлар (билмайсизлар)'}, {'chapter': 56, 'verse': 86, 'text': 'فَلَوۡلَآ إِن كُنتُمۡ غَيۡرَ مَدِينِينَ\nБас, агар сизлар эгасиз бўлсангизлар'}, {'chapter': 56, 'verse': 87, 'text': 'تَرۡجِعُونَهَآ إِن كُنتُمۡ صَٰدِقِينَ\n(Ва ўзларингизнинг: «Ҳеч қандай Эга — Парвардигор ҳам, қайта тирилиш ҳам йуқ», деган сўзларингизда) ростгўй булсангизлар ўша (жонни яна жасадга) қайтара олсангизлар эди! (Йуқ, ҳеч қачон қайтара олмассизлар)'}, {'chapter': 56, 'verse': 88, 'text': 'فَأَمَّآ إِن كَانَ مِنَ ٱلۡمُقَرَّبِينَ\nЭнди агар ўша (вафот қилгувчи киши Аллоҳга) яқин қилинган (пешқадам)лардан булса'}, {'chapter': 56, 'verse': 89, 'text': 'فَرَوۡحٞ وَرَيۡحَانٞ وَجَنَّتُ نَعِيمٖ\nу ҳолда (унинг учун) роҳат-фароғат, гўзал ризқ ва ноз-неъматли жаннат бордир'}, {'chapter': 56, 'verse': 90, 'text': 'وَأَمَّآ إِن كَانَ مِنۡ أَصۡحَٰبِ ٱلۡيَمِينِ\nЭнди агар у ўнг томон эгаларидан бўлса'}, {'chapter': 56, 'verse': 91, 'text': 'فَسَلَٰمٞ لَّكَ مِنۡ أَصۡحَٰبِ ٱلۡيَمِينِ\nу ҳолда (эй саодатманд бандам), сенга ўнг томон эгаларидан салом бўлгай'}, {'chapter': 56, 'verse': 92, 'text': 'وَأَمَّآ إِن كَانَ مِنَ ٱلۡمُكَذِّبِينَ ٱلضَّآلِّينَ\nЭнди агар у (қайта тирилишни) ёлғон дегувчи гумроҳлардан (яъни, чап томон эгаларидан) бўлса'}, {'chapter': 56, 'verse': 93, 'text': 'فَنُزُلٞ مِّنۡ حَمِيمٖ\nу ҳолда қайноқ сувдан иборат «зиёфат»'}, {'chapter': 56, 'verse': 94, 'text': 'وَتَصۡلِيَةُ جَحِيمٍ\nва дўзахга киритилиш бордир'}, {'chapter': 56, 'verse': 95, 'text': 'إِنَّ هَٰذَا لَهُوَ حَقُّ ٱلۡيَقِينِ\nШак-шубҳасиз, (ушбу сурада зикр қилинган нарсалар) айни ҳақиқатнинг ўзидир'}, {'chapter': 56, 'verse': 96, 'text': 'فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ\nБас, улуғ Парвардигорингизнинг номини (ҳар қандай айб-нуқсондан) поклаб-тасбеҳ айтинг'}, {'chapter': 57, 'verse': 1, 'text': 'سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлар ва Ердаги бор нарса Аллоҳга тасбеҳ айтди. У қудрат ва ҳикмат Соҳибидир'}, {'chapter': 57, 'verse': 2, 'text': 'لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлар ва Ер Унинг мулкидир. У ҳаёт ва ўлим берур. У барча нарсага қодирдир'}, {'chapter': 57, 'verse': 3, 'text': 'هُوَ ٱلۡأَوَّلُ وَٱلۡأٓخِرُ وَٱلظَّـٰهِرُ وَٱلۡبَاطِنُۖ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАввал ҳам, охир ҳам, зоҳир ҳам, ботин ҳам Унинг Ўзидир. У барча нарсани билгувчидир'}, {'chapter': 57, 'verse': 4, 'text': 'هُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۖ وَهُوَ مَعَكُمۡ أَيۡنَ مَا كُنتُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\nУ осмонлар ва Ерни олти кунда яратиб, сўнгра (Зоти ва Улуғлигига муносиб тарзда) Аршга олий бўлган Зотдир. У Ерга кирадиган (ёмғир-қор суви хазина-ю дафина ва жасадлар каби) нарсаларни ҳам, ундан чиқадиган (ўт-ўлан, наботот ва булоқ сувлари каби нарсаларни ҳам, самодан тушадиган (ризқу насиба, хайру барака каби) нарсаларни ҳам, унга (самога) кўтариладиган (яъни, амал ва дуо-илтижо каби) нарсаларни ҳам билур. Сизлар қаерда бўлсангизлар, У сизлар билан биргадир. Аллоҳ қилаётган амалларингизни кўриб тургувчидир'}, {'chapter': 57, 'verse': 5, 'text': 'لَّهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ\nОсмонлар ва Ер Унинг мулкидир. Барча ишлар ёлғиз Аллоҳга қайтарилур'}, {'chapter': 57, 'verse': 6, 'text': 'يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِۚ وَهُوَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nУ кечани кундузга киритур ва кундузни кечага киритур. У диллардаги сирларни билгувчидир'}, {'chapter': 57, 'verse': 7, 'text': 'ءَامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَأَنفِقُواْ مِمَّا جَعَلَكُم مُّسۡتَخۡلَفِينَ فِيهِۖ فَٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَأَنفَقُواْ لَهُمۡ أَجۡرٞ كَبِيرٞ\n(Эй инсонлар), Аллоҳга ва Унинг пайғамбари (Муҳаммад алайҳис-салоту вас-салом)га иймон келтиринглар ҳамда У зот сизларни халифа қилиб қўйган нарсалардан (яъни, вақтинча қўлларингизда турган эрта бир кун сизлар истасангиз-истамасангиз ўзгаларнинг қўлларига ўтиб кетадиган Аллоҳ берган мол-давлатдан) инфоқ-эҳсон қилинглар! Бас, сизлардан иймон келтирган ва инфоқ-эҳсон қилган зотлар учун катта ажр-мукофот бордир'}, {'chapter': 57, 'verse': 8, 'text': 'وَمَا لَكُمۡ لَا تُؤۡمِنُونَ بِٱللَّهِ وَٱلرَّسُولُ يَدۡعُوكُمۡ لِتُؤۡمِنُواْ بِرَبِّكُمۡ وَقَدۡ أَخَذَ مِيثَٰقَكُمۡ إِن كُنتُم مُّؤۡمِنِينَ\nАгар сизлар иймон келтиргувчи бўлсангизлар — пайғамбар (ораларингизда) сизларни Парвардигорингизга иймон келтиришга даъват қилиб тургани ҳолда (Аллоҳ эса сизларнинг руҳларингизни яратган пайтидаёқ, иймон келтириш ҳақидаги) аҳду-паймонингизни олгани ҳолда нега сизлар (Аллоҳга) иймон келтирмаяпсизлар'}, {'chapter': 57, 'verse': 9, 'text': 'هُوَ ٱلَّذِي يُنَزِّلُ عَلَىٰ عَبۡدِهِۦٓ ءَايَٰتِۭ بَيِّنَٰتٖ لِّيُخۡرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَإِنَّ ٱللَّهَ بِكُمۡ لَرَءُوفٞ رَّحِيمٞ\nУ Ўз бандаси (Муҳаммад алайҳис-салоту вас-салом)га токи у сизларни (куфр) зулматларидан (иймон) нурига олиб чиқиш учун аниқ-равшан оятларни (яъни, Қуръони Каримни) нозил қиладиган Зотдир. Албатта Аллоҳ сизларга меҳрибон ва раҳмлидир'}, {'chapter': 57, 'verse': 10, 'text': 'وَمَا لَكُمۡ أَلَّا تُنفِقُواْ فِي سَبِيلِ ٱللَّهِ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ لَا يَسۡتَوِي مِنكُم مَّنۡ أَنفَقَ مِن قَبۡلِ ٱلۡفَتۡحِ وَقَٰتَلَۚ أُوْلَـٰٓئِكَ أَعۡظَمُ دَرَجَةٗ مِّنَ ٱلَّذِينَ أَنفَقُواْ مِنۢ بَعۡدُ وَقَٰتَلُواْۚ وَكُلّٗا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nНега сизлар Аллоҳ йўлида инфоқ-эҳсон қилмаяпсизлар?! Ҳолбуки, осмонлар ва Ернинг ворислиги ёлғиз Аллоҳники-ку (яъни, оз кунда сизлар бу оламдан ўтиб кетиб, қўлларингиздаги барча мол-дунёларингиз Аллоҳга мерос бўлиб қолади-ку)! Сизлардан (Макка) фатҳ бўлишидан илгари инфоқ-эҳсон қилган ва (Расулуллоҳ билан бирга кофирларга қарши) урушган кишилар (Макка фатҳидан кейин инфоқ-эҳсон қилган ва урушган кишилар билан) баробар бўлмас. Улар кейин инфоқ-эҳсон қилган ва урушган кишилардан улуғроқ мартабададирлар. Барчаларига Аллоҳ гўзал (оқибат-жаннат) ваъда қилгандир. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 57, 'verse': 11, 'text': 'مَّن ذَا ٱلَّذِي يُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفَهُۥ لَهُۥ وَلَهُۥٓ أَجۡرٞ كَرِيمٞ\nАллоҳга қарзи ҳасана (ихтиёрий қарз, яъни Унинг йўлида хайру саховат) қиладиган ким бор? Бас, У зот ўша (киши)га бир неча ҳисса қилиб қайтарур ва унинг учун улуғ ажр-мукофот бўлур'}, {'chapter': 57, 'verse': 12, 'text': 'يَوۡمَ تَرَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ يَسۡعَىٰ نُورُهُم بَيۡنَ أَيۡدِيهِمۡ وَبِأَيۡمَٰنِهِمۖ بُشۡرَىٰكُمُ ٱلۡيَوۡمَ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\nМўмин ва мўминаларнинг олдиларида ва ўнг томонларида нурлари (яъни, қилган яхши амаллари ва номаи аъмоллари йўлларини ёритиб) кетаётганини кўрадиган Кунни (эсланг)! (Ўша Кунда уларга дейилур): «Сизлар учун Бугунги хушхабар — остидан дарёлар оқиб турадиган, (сизлар) мангу қоладиган жаннатлардир. Бу улуғ бахтнинг ўзидир!»'}, {'chapter': 57, 'verse': 13, 'text': 'يَوۡمَ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلۡمُنَٰفِقَٰتُ لِلَّذِينَ ءَامَنُواْ ٱنظُرُونَا نَقۡتَبِسۡ مِن نُّورِكُمۡ قِيلَ ٱرۡجِعُواْ وَرَآءَكُمۡ فَٱلۡتَمِسُواْ نُورٗاۖ فَضُرِبَ بَيۡنَهُم بِسُورٖ لَّهُۥ بَابُۢ بَاطِنُهُۥ فِيهِ ٱلرَّحۡمَةُ وَظَٰهِرُهُۥ مِن قِبَلِهِ ٱلۡعَذَابُ\nУ Кунда мунофиқ ва мунофиқалар иймон келтирган зотларга: «Бизга қараб туринглар, биз ҳам сизларнинг нурингиздан озгина олиб фойдаланайлик», десалар, (уларга) «Ортингларга қайтиб нур истайверинглар», дейилур. Сўнг улар (билан мўминлар)нинг ўртасига дарвозали бир девор урилурки, унинг ичида раҳмат-жаннат, ташқарисида-олдида эса азоб-дўзах бўлур'}, {'chapter': 57, 'verse': 14, 'text': 'يُنَادُونَهُمۡ أَلَمۡ نَكُن مَّعَكُمۡۖ قَالُواْ بَلَىٰ وَلَٰكِنَّكُمۡ فَتَنتُمۡ أَنفُسَكُمۡ وَتَرَبَّصۡتُمۡ وَٱرۡتَبۡتُمۡ وَغَرَّتۡكُمُ ٱلۡأَمَانِيُّ حَتَّىٰ جَآءَ أَمۡرُ ٱللَّهِ وَغَرَّكُم بِٱللَّهِ ٱلۡغَرُورُ\nУлар (мўминларга) «Биз (ҳаёти дунёда) сизлар билан бирга эмасмидик», деб нидо қилганларида, (мўминлар) дерлар: «Ҳа, (кўринишда биз билан бирга эдинглар), лекин сизлар (мунофиқлик билан) ўзларингизни фитнага дучор қилдинглар ва (бизларга бало-офатлар етишига) кўз тутдинглар ҳамда (Исломнинг ҳақ «дин эканлиги тўғрисида) шубҳаландинглар ва то Аллоҳнинг амри (яъни, ўлим) келгунича сизларни (ҳали умр узоқ эканлиги, Аллоҳ ҳар қандай гуноҳни кечиб юборавериши каби) хомхаёллар алдади. Сизларни Аллоҳ ҳақида (У зотнинг карами кенг, ҳеч қачон Ўзи яратган бандаларни азобга гирифтор қилмайди, деб) алдагувчи (шайтон) алдаб қўйди»'}, {'chapter': 57, 'verse': 15, 'text': 'فَٱلۡيَوۡمَ لَا يُؤۡخَذُ مِنكُمۡ فِدۡيَةٞ وَلَا مِنَ ٱلَّذِينَ كَفَرُواْۚ مَأۡوَىٰكُمُ ٱلنَّارُۖ هِيَ مَوۡلَىٰكُمۡۖ وَبِئۡسَ ٱلۡمَصِيرُ\nМана Бугун сизлардан ҳам, (эй мунофиқлар), кофир бўлган кимсалардан ҳам (дўзах азобидан қутулишларингиз учун) бирон тўлов олинмас. Жойларингиз дўзахдир! У сизларга энг муносиб жойдир! Нақадар ёмон оқибат (бу)'}, {'chapter': 57, 'verse': 16, 'text': '۞أَلَمۡ يَأۡنِ لِلَّذِينَ ءَامَنُوٓاْ أَن تَخۡشَعَ قُلُوبُهُمۡ لِذِكۡرِ ٱللَّهِ وَمَا نَزَلَ مِنَ ٱلۡحَقِّ وَلَا يَكُونُواْ كَٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلُ فَطَالَ عَلَيۡهِمُ ٱلۡأَمَدُ فَقَسَتۡ قُلُوبُهُمۡۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ\nИймон келтирган зотлар учун диллари Аллоҳнинг зикри сабабли ва нозил бўлган Ҳақ — Қуръон (тиловат қилиниши) сабабли қалблари эриб, қўрқиш (вақти) келмадими? (Шунингдек, улар учун) илгари китоб ато этилган, сўнг (улар билан пайғамбарлари ўртасидаги) муддат узайгач, диллари қотиб кетган кимсалар (яъни, яҳудий ва насронийлар) каби бўлиб қолмаслик (вақти келмадими)? Улардан (яҳудий ва насронийлардан) кўплари фосиқ-итоатсиздирлар'}, {'chapter': 57, 'verse': 17, 'text': 'ٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ قَدۡ بَيَّنَّا لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ\n(Эй мўминлар), билингларки, албатта Аллоҳ ўлган Ерни тирилтирур (яъни, Аллоҳ қуриб-қақшаб ётган Ерни ёмғир билан «тирилтиргани» каби сизларнинг дилларингизни ҳам Аллоҳнинг зикри ва Қуръон тиловати билан юмшатур). Мана Биз ақл юргизишларингиз учун сизларга (Ўз қудрати илоҳийямизга далолат қиладиган) оятларни батафсил баён қилдик'}, {'chapter': 57, 'verse': 18, 'text': 'إِنَّ ٱلۡمُصَّدِّقِينَ وَٱلۡمُصَّدِّقَٰتِ وَأَقۡرَضُواْ ٱللَّهَ قَرۡضًا حَسَنٗا يُضَٰعَفُ لَهُمۡ وَلَهُمۡ أَجۡرٞ كَرِيمٞ\nАлбатта садақа қилгувчи эркаклар ва садақа қилгувчи аёллар ҳамда Аллоҳга қарзи ҳасана берганлар учун (берган хайру садақалари) бир неча ҳисса қилиб қайтарилур ва улар учун улуғ ажр-мукофот бордир'}, {'chapter': 57, 'verse': 19, 'text': 'وَٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦٓ أُوْلَـٰٓئِكَ هُمُ ٱلصِّدِّيقُونَۖ وَٱلشُّهَدَآءُ عِندَ رَبِّهِمۡ لَهُمۡ أَجۡرُهُمۡ وَنُورُهُمۡۖ وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ\nАллоҳга ва Унинг пайғамбарларига иймон келтирган зотлар — ана ўшалар Парвардигорлари ҳузурида сиддиқлар (яъни, жуда ростгўй зотлар) ва шаҳидлар (мақомида)дирлар. Улар учун (Охиратда) ажр-мукофотлари ва (уларнинг йўлларини ёритиб борадиган) нурлари бўлур. Кофир бўлган ва Бизнинг оятларимизни ёлғон деган кимсалар — улар дўзах эгаларидир'}, {'chapter': 57, 'verse': 20, 'text': 'ٱعۡلَمُوٓاْ أَنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞ وَزِينَةٞ وَتَفَاخُرُۢ بَيۡنَكُمۡ وَتَكَاثُرٞ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِۖ كَمَثَلِ غَيۡثٍ أَعۡجَبَ ٱلۡكُفَّارَ نَبَاتُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصۡفَرّٗا ثُمَّ يَكُونُ حُطَٰمٗاۖ وَفِي ٱلۡأٓخِرَةِ عَذَابٞ شَدِيدٞ وَمَغۡفِرَةٞ مِّنَ ٱللَّهِ وَرِضۡوَٰنٞۚ وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا مَتَٰعُ ٱلۡغُرُورِ\nБилингларки, бу ҳаёти дунё фақат (бир нафаслик) ўйин-кулги, зеб-зийнат, ўрталарингиздаги ўзаро мақтаниш ва мол-дунё ҳамда фарзандларни кўпайтиришдир, холос. (У) худди бир ёмғирга ўхшарки, унинг (ёғиши сабабли униб чиққан) ўт-ўлани кофирларни ҳайратга солиб (ақлларини банд қилиб қўюр). Сўнгра у қурир, бас, уни сарғайган ҳолда кўрурсиз. Сўнгра у қуруқ чўп бўлиб қолур. (Ҳаёти дунёнинг ҳоли ҳам шундан ўзга эмасдир). Охиратда эса (ўша тўрт кунлик дунёга алданиб қолганлар учун) қаттиқ азоб ва (иймон-эътиқод билан ўтганлар учун) Аллоҳ томонидан мағфират ва ризолик бордир. Ҳаёти дунё эса фақат алдагувчи матодир'}, {'chapter': 57, 'verse': 21, 'text': 'سَابِقُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا كَعَرۡضِ ٱلسَّمَآءِ وَٱلۡأَرۡضِ أُعِدَّتۡ لِلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦۚ ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\n(Эй инсонлар), Парвардигорингиз томонидан бўладиган мағфиратга ҳамда Аллоҳ ва Унинг пайғамбарларига иймон келтирган зотлар учун тайёрлаб қўйилган, кенглиги осмон ва Ернинг кенглиги каби бўлган жаннатга шошилингиз! Бу (яъни, ваъда қилинган мағфират ва жаннат) Аллоҳнинг фазлу марҳамати бўлиб, уни Ўзи хоҳлаган кишиларга ато этур. Аллоҳ улуғ фазлу марҳамат Соҳибидир'}, {'chapter': 57, 'verse': 22, 'text': 'مَآ أَصَابَ مِن مُّصِيبَةٖ فِي ٱلۡأَرۡضِ وَلَا فِيٓ أَنفُسِكُمۡ إِلَّا فِي كِتَٰبٖ مِّن قَبۡلِ أَن نَّبۡرَأَهَآۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nНа Ерга ва на ўзларингизга бирон мусибат етмас, магар (етса) Биз уни пайдо қилишимиздан илгари Китобда (Лавҳул-Маҳфузда битилган) бўлур. Албатта бу Аллоҳга осондир'}, {'chapter': 57, 'verse': 23, 'text': 'لِّكَيۡلَا تَأۡسَوۡاْ عَلَىٰ مَا فَاتَكُمۡ وَلَا تَفۡرَحُواْ بِمَآ ءَاتَىٰكُمۡۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٍ\nТоки сизлар қўлларингиздан кетган нарсага қайғурмагайсизлар ва (Аллоҳ) ато этган нарса билан шодланиб (ҳаволаниб) кетмагайсизлар. Аллоҳ барча кибр-ҳаволи, мақтанчоқ кимсаларни суймас'}, {'chapter': 57, 'verse': 24, 'text': 'ٱلَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِۗ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nУлар бахиллик қиладиган ва одамларни ҳам бахил бўлишга буюрадиган-чақирадиган кимсалардир. Ким (бахиллик қилиб инфоқ-эҳсон қилишдан) юз ўгириб кетса, у ҳолда албатта Аллоҳнинг Ўзи (барча оламдан) беҳожат ва ҳамду санога лойиқ Зотдир'}, {'chapter': 57, 'verse': 25, 'text': 'لَقَدۡ أَرۡسَلۡنَا رُسُلَنَا بِٱلۡبَيِّنَٰتِ وَأَنزَلۡنَا مَعَهُمُ ٱلۡكِتَٰبَ وَٱلۡمِيزَانَ لِيَقُومَ ٱلنَّاسُ بِٱلۡقِسۡطِۖ وَأَنزَلۡنَا ٱلۡحَدِيدَ فِيهِ بَأۡسٞ شَدِيدٞ وَمَنَٰفِعُ لِلنَّاسِ وَلِيَعۡلَمَ ٱللَّهُ مَن يَنصُرُهُۥ وَرُسُلَهُۥ بِٱلۡغَيۡبِۚ إِنَّ ٱللَّهَ قَوِيٌّ عَزِيزٞ\nҚасамки, Биз Ўз пайғамбарларимизни аниқ ҳужжат-мўъжизалар билан юбордик ва улар билан бирга Китоб ҳамда одамлар адолатни барпо қилишлари учун мезон-тарози туширдик. Яна Биз темирни туширдик-яратдик. Унда куч-қувват ва одамлар учун манфаатлар бордир. Аллоҳ ғайбда (бандалари кўзига кўринмасдан) турган ҳолида Ўзига ва пайғамбарларига (Аллоҳ йўлида жиҳод қилиш билан) ёрдам берадиган кишиларни билиш учун (темирни яратди). Албатта Аллоҳ кучли, қудратлидир'}, {'chapter': 57, 'verse': 26, 'text': 'وَلَقَدۡ أَرۡسَلۡنَا نُوحٗا وَإِبۡرَٰهِيمَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِمَا ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَۖ فَمِنۡهُم مُّهۡتَدٖۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ\nҚасамки, Биз Нуҳ ва Иброҳимни пайғамбар қилиб юбордик ҳамда пайғамбарлик ва Китобни (яъни, Таврот, Инжил, Забур ва Қуръонни) ҳам икковининг зурриётларига (ато) қилдик. Бас, уларнинг (яъни, Нуҳ ва Иброҳим зурриётларининг) орасида ҳидоят топгувчи (кишилар) ҳам бор, (аммо) уларнинг орасида кўплари фосиқ-итоатсиздирлар'}, {'chapter': 57, 'verse': 27, 'text': 'ثُمَّ قَفَّيۡنَا عَلَىٰٓ ءَاثَٰرِهِم بِرُسُلِنَا وَقَفَّيۡنَا بِعِيسَى ٱبۡنِ مَرۡيَمَ وَءَاتَيۡنَٰهُ ٱلۡإِنجِيلَۖ وَجَعَلۡنَا فِي قُلُوبِ ٱلَّذِينَ ٱتَّبَعُوهُ رَأۡفَةٗ وَرَحۡمَةٗۚ وَرَهۡبَانِيَّةً ٱبۡتَدَعُوهَا مَا كَتَبۡنَٰهَا عَلَيۡهِمۡ إِلَّا ٱبۡتِغَآءَ رِضۡوَٰنِ ٱللَّهِ فَمَا رَعَوۡهَا حَقَّ رِعَايَتِهَاۖ فَـَٔاتَيۡنَا ٱلَّذِينَ ءَامَنُواْ مِنۡهُمۡ أَجۡرَهُمۡۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ\nСўнгра уларнинг изларидан кетма-кет пайғамбарларимизни юбордик ва Ийсо бинни Марямни ҳам (уларнинг) ортидан юбордик ва унга Инжил ато этдик ҳамда унга эргашган зотларнинг дилларида меҳрибонлик ва раҳм-шафқат (пайдо) қилдик. Роҳибликни эса улар ўзлари чиқариб олдилар. Биз уларга (роҳиб бўлишларини) ёзганимиз — буюрганимиз йўқ, лекин улар ўзлари Аллоҳнинг ризолигини истаб (роҳибликни одат) қилдилар-у, сўнг унга тўғри-тўла риоя қила олмадилар. Бас, Биз улардан иймон келтирган зотларга ажр-мукофотларини ато этдик. (Аммо) уларнинг орасида кўплари фосиқ-итоатсиздирлар'}, {'chapter': 57, 'verse': 28, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَءَامِنُواْ بِرَسُولِهِۦ يُؤۡتِكُمۡ كِفۡلَيۡنِ مِن رَّحۡمَتِهِۦ وَيَجۡعَل لَّكُمۡ نُورٗا تَمۡشُونَ بِهِۦ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭй (Ийсога) иймон келтирган зотлар, Аллоҳдан қўрқинглар ва Унинг пайғамбари (Муҳаммад алайҳис-салоту вас-салом)га ҳам иймон келтиринглар, (шунда) У зот сизларга Ўз раҳматидан икки ҳисса ато этур ва сизлар учун (Қиёмат Кунида олдиларингизда) юрадиган нур (пайдо) қилур ҳамда сизларни мағфират қилур. Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 57, 'verse': 29, 'text': 'لِّئَلَّا يَعۡلَمَ أَهۡلُ ٱلۡكِتَٰبِ أَلَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّن فَضۡلِ ٱللَّهِ وَأَنَّ ٱلۡفَضۡلَ بِيَدِ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nАҳли китоб ўзлари Аллоҳнинг фазлу-марҳаматидан бирон нарсага (етишга) қодир бўла олмасликларини ва фазлу марҳамат Аллоҳнинг қўлида бўлиб, уни фақат Ўзи хоҳлаган кишиларга ато этишини билишлари учун (Аллоҳ уларга юқоридаги ҳукмларни баён қилди). Аллоҳ улуғ фазлу марҳамат Соҳибидир'}, {'chapter': 58, 'verse': 1, 'text': 'قَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّتِي تُجَٰدِلُكَ فِي زَوۡجِهَا وَتَشۡتَكِيٓ إِلَى ٱللَّهِ وَٱللَّهُ يَسۡمَعُ تَحَاوُرَكُمَآۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), дарҳақиқат, Аллоҳ сиз билан ўз жуфти ҳақида баҳслашаётган ва Аллоҳга шикоят қилаётган (аёл)нинг сўзини эшитди. Аллоҳ сизларнинг басти-жавобларингизни эшитур. Албатта Аллоҳ эшитгувчи, кўргувчидир'}, {'chapter': 58, 'verse': 2, 'text': 'ٱلَّذِينَ يُظَٰهِرُونَ مِنكُم مِّن نِّسَآئِهِم مَّا هُنَّ أُمَّهَٰتِهِمۡۖ إِنۡ أُمَّهَٰتُهُمۡ إِلَّا ٱلَّـٰٓـِٔي وَلَدۡنَهُمۡۚ وَإِنَّهُمۡ لَيَقُولُونَ مُنكَرٗا مِّنَ ٱلۡقَوۡلِ وَزُورٗاۚ وَإِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٞ\nОраларингиздаги ўз хотинларини зиҳор қиладиган кимсаларнинг (зиҳор қилинган хотинлари) уларнинг оналари эмасдирлар. Фақат уларни туққан (аёл)ларгина уларнинг оналаридирлар. Шак-шубҳасиз, улар сўзнинг номақбул ва ёлғонини айтурлар. (Лекин) Аллоҳ шубҳасиз, афв этгувчи ва мағфират қилгувчидир'}, {'chapter': 58, 'verse': 3, 'text': 'وَٱلَّذِينَ يُظَٰهِرُونَ مِن نِّسَآئِهِمۡ ثُمَّ يَعُودُونَ لِمَا قَالُواْ فَتَحۡرِيرُ رَقَبَةٖ مِّن قَبۡلِ أَن يَتَمَآسَّاۚ ذَٰلِكُمۡ تُوعَظُونَ بِهِۦۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nЎз хотинларини зиҳор қилиб, сўнгра айтган сўзларидан қайтадиган кимсалар (зиммасида) (эр-хотин) қўшилишларидан илгари бир қулни озод қилиш бордир. Бу сизларга мавъиза-ибрат бўлгай. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 58, 'verse': 4, 'text': 'فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ مِن قَبۡلِ أَن يَتَمَآسَّاۖ فَمَن لَّمۡ يَسۡتَطِعۡ فَإِطۡعَامُ سِتِّينَ مِسۡكِينٗاۚ ذَٰلِكَ لِتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦۚ وَتِلۡكَ حُدُودُ ٱللَّهِۗ وَلِلۡكَٰفِرِينَ عَذَابٌ أَلِيمٌ\nЭнди ким (озод қилиш учун қул) топа олмаса (унинг зиммасида эр-хотин) қўшилишларидан илгари пайдар-пай икки ой рўза тутиш бордир. Энди ким (рўза тутишга) қодир бўлмаса (унинг зиммасида) олтмишта бечора-мискинга таом бериш бордир. Бу (ҳукмлар) сизлар Аллоҳ ва Унинг пайғамбарига иймон келтиришларингиз учун (нозил қилинди). Булар Аллоҳнинг (белгилаб қўйган) ҳадлари — қонунларидир. Ва (бу қонунларни инкор этгувчи) кофирлар учун аламли азоб бордир'}, {'chapter': 58, 'verse': 5, 'text': 'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥ كُبِتُواْ كَمَا كُبِتَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ وَقَدۡ أَنزَلۡنَآ ءَايَٰتِۭ بَيِّنَٰتٖۚ وَلِلۡكَٰفِرِينَ عَذَابٞ مُّهِينٞ\nАлбатта Аллоҳ ва Унинг пайғамбари чизган чизиқдан чиқадиган кимсалар худди ўзларидан аввалгилар ҳалок қилингани каби ҳалок қилинурлар. Дарвоқеъ, Биз очиқ-равшан оятлар нозил қилдик. (Мана шу оятларни инкор этгувчи) кофирлар учун хор қилгувчи азоб бордир'}, {'chapter': 58, 'verse': 6, 'text': 'يَوۡمَ يَبۡعَثُهُمُ ٱللَّهُ جَمِيعٗا فَيُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ أَحۡصَىٰهُ ٱللَّهُ وَنَسُوهُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\n(Ўша кофирлар азобга дучор қилинадиган) Кунда Аллоҳ уларнинг барчаларини қайта тирилтириб, (ҳаёти дунёда) қилган амалларининг хабарини берур. У (амаллар)ни Аллоҳ ҳисоблаб-битиб қўйди, улар эса унутиб юбордилар. Аллоҳ барча нарса устида гувоҳдир'}, {'chapter': 58, 'verse': 7, 'text': 'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ مَا يَكُونُ مِن نَّجۡوَىٰ ثَلَٰثَةٍ إِلَّا هُوَ رَابِعُهُمۡ وَلَا خَمۡسَةٍ إِلَّا هُوَ سَادِسُهُمۡ وَلَآ أَدۡنَىٰ مِن ذَٰلِكَ وَلَآ أَكۡثَرَ إِلَّا هُوَ مَعَهُمۡ أَيۡنَ مَا كَانُواْۖ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُواْ يَوۡمَ ٱلۡقِيَٰمَةِۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ\nАллоҳ осмонлардаги ва Ердаги бор нарсани аниқ билишини кўрмадингиз — билмадингизми! Уч кишининг ўзаро шивир-шивирлари бўлса, албатта У зот уларнинг тўртинчисидир, беш кишининг (ўзаро шивир-шивирлари бўлса), албатта У зот уларнинг олтинчисидир. (Ўзаро сирлашаётган кишилар) бундан оз бўладими, ё кўп бўладими, албатта У зот қаерда бўлсалар ҳам улар билан биргадир. Сўнгра, Қиёмат Кунида уларга қилган амалларининг хабарини берур. Албатта Аллоҳ барча нарсани билгувчидир'}, {'chapter': 58, 'verse': 8, 'text': 'أَلَمۡ تَرَ إِلَى ٱلَّذِينَ نُهُواْ عَنِ ٱلنَّجۡوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُواْ عَنۡهُ وَيَتَنَٰجَوۡنَ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَمَعۡصِيَتِ ٱلرَّسُولِۖ وَإِذَا جَآءُوكَ حَيَّوۡكَ بِمَا لَمۡ يُحَيِّكَ بِهِ ٱللَّهُ وَيَقُولُونَ فِيٓ أَنفُسِهِمۡ لَوۡلَا يُعَذِّبُنَا ٱللَّهُ بِمَا نَقُولُۚ حَسۡبُهُمۡ جَهَنَّمُ يَصۡلَوۡنَهَاۖ فَبِئۡسَ ٱلۡمَصِيرُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), ўзаро шивир-шивирдан қайтарилганларидан сўнг яна ўзлари қайтарилган нарсага қайтадиган ҳамда гуноҳ, зўравонлик ва пайғамбарга итоатсизлик қилиш тўғрисида ўзаро шивирлашадиган кимсаларни кўрмадингизми? Қачон улар сизнинг олдингизга келсалар, сизга бир ибора билан салом берурларки, Аллоҳ сизга ўша (сўз) билан салом бермаган эди. Яна ўзларича: «Нега айтаётган сўзимиз сабабли Аллоҳ бизларни азобламаяпти? (Агар Муҳаммад ҳақ пайғамбар бўлганида, албатта Аллоҳ бизларни азобга гирифтор қилган бўлур эди)», дерлар. Уларга ўзлари кирадиган жаҳаннам (азоб тўғрисида) кифоя қилар! Бас, нақадар ёмон оқибат (бу)'}, {'chapter': 58, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا تَنَٰجَيۡتُمۡ فَلَا تَتَنَٰجَوۡاْ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَمَعۡصِيَتِ ٱلرَّسُولِ وَتَنَٰجَوۡاْ بِٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ\nЭй мўминлар, сизлар ўзаро шивирлашиб гаплашганларингизда, гуноҳ ва зўравонлик ва пайғамбарга итоатсизлик қилиш тўғрисида шивирлашманглар, (балки) яхшилик ва тақво ҳақида шивирлашинглар ҳамда сизлар ҳузурига тўпланадиган Зот — Аллоҳдан қўрқинглар'}, {'chapter': 58, 'verse': 10, 'text': 'إِنَّمَا ٱلنَّجۡوَىٰ مِنَ ٱلشَّيۡطَٰنِ لِيَحۡزُنَ ٱلَّذِينَ ءَامَنُواْ وَلَيۡسَ بِضَآرِّهِمۡ شَيۡـًٔا إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\n(Яҳудий ва мунофиқларнинг) ўзаро шивир-шивирлари фақат шайтон томонидан бўлиб, иймон келтирган зотларни ғамгин қилиш учундир. (Лекин) у Аллоҳнинг изни-иродасисиз уларга бирон зиён етказа олгувчи эмасдир. Бас, мўминлар, ёлғиз Аллоҳгагина таваккул қилсинлар — суянсинлар'}, {'chapter': 58, 'verse': 11, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا قِيلَ لَكُمۡ تَفَسَّحُواْ فِي ٱلۡمَجَٰلِسِ فَٱفۡسَحُواْ يَفۡسَحِ ٱللَّهُ لَكُمۡۖ وَإِذَا قِيلَ ٱنشُزُواْ فَٱنشُزُواْ يَرۡفَعِ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ دَرَجَٰتٖۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nЭй мўминлар, қачон сизларга (мажлис-суҳбатларда дўст-биродарларингиз келиб: «Бизларга ҳам шу мажлисдан) жой беринглар», дейилса, дарҳол жой беринглар, Аллоҳ сизларга ҳам (Ўз жаннатидан) жой берур. Ва қачон «(ўринларингиздан) туринглар», дейилса, дарҳол туринглар, Аллоҳ сизлардан иймон келтирган ва илм ато этилган зотларни (баланд) даража-мартабаларга кўтарур. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 58, 'verse': 12, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نَٰجَيۡتُمُ ٱلرَّسُولَ فَقَدِّمُواْ بَيۡنَ يَدَيۡ نَجۡوَىٰكُمۡ صَدَقَةٗۚ ذَٰلِكَ خَيۡرٞ لَّكُمۡ وَأَطۡهَرُۚ فَإِن لَّمۡ تَجِدُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nЭй мўминлар, қачон сизлар пайғамбар билан сирлашмоқ — суҳбатлашмоқни истасангизлар, суҳбатларингиз олдидан (камбағал-мискинларга) бирон хайр-садақа тақдим этинглар. Бу ўзларингиз учун яхшироқ ва (гуноҳларингизни) поклагувчироқдир. Энди агар (садақа қилгудек нарса) топа олмасангизлар, у ҳолда албатта Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 58, 'verse': 13, 'text': 'ءَأَشۡفَقۡتُمۡ أَن تُقَدِّمُواْ بَيۡنَ يَدَيۡ نَجۡوَىٰكُمۡ صَدَقَٰتٖۚ فَإِذۡ لَمۡ تَفۡعَلُواْ وَتَابَ ٱللَّهُ عَلَيۡكُمۡ فَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nСуҳбатларингиз олдидан хайр-садақалар тақдим этишдан (камбағал бўлиб қоламиз, деб) қўрқдингларми? Бас, сизлар (хайр-садақа) қилмаган экансизлар ва Аллоҳ тавбаларингизни қабул қилган экан, энди намозни тўкис адо этинглар ва закотни (ҳақдорларга) ато этинглар ҳамда Аллоҳ ва Унинг пайғамбарига итоат этинглар! Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 58, 'verse': 14, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ تَوَلَّوۡاْ قَوۡمًا غَضِبَ ٱللَّهُ عَلَيۡهِم مَّا هُم مِّنكُمۡ وَلَا مِنۡهُمۡ وَيَحۡلِفُونَ عَلَى ٱلۡكَذِبِ وَهُمۡ يَعۡلَمُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Аллоҳнинг ғазабига дучор бўлган қавмни (яҳудийларни) дўст тутган кимсаларни (мунофиқларни) кўрмадингизми? У (мунофиқлар) сизлардан (яъни, мусулмонлардан) ҳам, улардан (яъни, яҳудийлардан) ҳам эмасдирлар! Яна улар (ўзларининг мунофиқ эканликларини) билиб турган ҳолларида, («Бизлар мусулмонлармиз», деб) қасам ҳам ичурлар'}, {'chapter': 58, 'verse': 15, 'text': 'أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابٗا شَدِيدًاۖ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ\nАллоҳ улар учун қаттиқ азобни тайёрлаб қўйгандир! Дарҳақиқат, уларнинг қилган амаллари нақадар ёмондир'}, {'chapter': 58, 'verse': 16, 'text': 'ٱتَّخَذُوٓاْ أَيۡمَٰنَهُمۡ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ ٱللَّهِ فَلَهُمۡ عَذَابٞ مُّهِينٞ\nУлар ўзларининг (ёлғон) қасамларини қалқон қилиб олишиб, (одамларни) Аллоҳнинг йўлидан тўсдилар. Бас, улар учун хор қилгувчи азоб бордир'}, {'chapter': 58, 'verse': 17, 'text': 'لَّن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـًٔاۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ\nУларнинг моллари ҳам, болалари ҳам Аллоҳ томонидан (бўладиган) бирон нарсани-азобни улардан дафъ қила олмас. Улар дўзах эгаларидир. Улар, ўша жойда мангу қолгувчидирлар'}, {'chapter': 58, 'verse': 18, 'text': 'يَوۡمَ يَبۡعَثُهُمُ ٱللَّهُ جَمِيعٗا فَيَحۡلِفُونَ لَهُۥ كَمَا يَحۡلِفُونَ لَكُمۡ وَيَحۡسَبُونَ أَنَّهُمۡ عَلَىٰ شَيۡءٍۚ أَلَآ إِنَّهُمۡ هُمُ ٱلۡكَٰذِبُونَ\nУ Кунда (яъни, Қиёматда) Аллоҳ уларнинг барчаларини қайта тирилтириб, улар худди сизларга қасам ичаётганларидек, У зотга ҳам қасам ичурлар ва ўзларини (шу ёлғон қасамлари сабабли) бирон (фойда) устида деб ҳисобларлар. Огоҳ бўлингизким, албатта улар ғирт ёлғончи кимсалардир'}, {'chapter': 58, 'verse': 19, 'text': 'ٱسۡتَحۡوَذَ عَلَيۡهِمُ ٱلشَّيۡطَٰنُ فَأَنسَىٰهُمۡ ذِكۡرَ ٱللَّهِۚ أُوْلَـٰٓئِكَ حِزۡبُ ٱلشَّيۡطَٰنِۚ أَلَآ إِنَّ حِزۡبَ ٱلشَّيۡطَٰنِ هُمُ ٱلۡخَٰسِرُونَ\nУларни шайтон эгаллаб олиб, уларга Аллоҳни эслашни унуттирди. Ана ўшалар шайтон ҳизби-гуруҳидирлар. Огоҳ бўлингизким, албатта шайтон ҳизби (бўлган кимсаларгина) зиён кўргувчилардир'}, {'chapter': 58, 'verse': 20, 'text': 'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥٓ أُوْلَـٰٓئِكَ فِي ٱلۡأَذَلِّينَ\nАлбатта Аллоҳ ва Унинг пайғамбари чизган чизиқдан чиққан кимсалар — ана ўшалар энг хор кимсалар қаторидадирлар'}, {'chapter': 58, 'verse': 21, 'text': 'كَتَبَ ٱللَّهُ لَأَغۡلِبَنَّ أَنَا۠ وَرُسُلِيٓۚ إِنَّ ٱللَّهَ قَوِيٌّ عَزِيزٞ\nАллоҳ (Лавҳул-Маҳфузга): «шак-шубҳасиз, Мен Ўз пайғамбарларим билан бирга ғолиб бўлурман», деб ёзиб қўйгандир. Албатта Аллоҳ кучли, қудратлидир'}, {'chapter': 58, 'verse': 22, 'text': 'لَّا تَجِدُ قَوۡمٗا يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ يُوَآدُّونَ مَنۡ حَآدَّ ٱللَّهَ وَرَسُولَهُۥ وَلَوۡ كَانُوٓاْ ءَابَآءَهُمۡ أَوۡ أَبۡنَآءَهُمۡ أَوۡ إِخۡوَٰنَهُمۡ أَوۡ عَشِيرَتَهُمۡۚ أُوْلَـٰٓئِكَ كَتَبَ فِي قُلُوبِهِمُ ٱلۡإِيمَٰنَ وَأَيَّدَهُم بِرُوحٖ مِّنۡهُۖ وَيُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ رَضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ أُوْلَـٰٓئِكَ حِزۡبُ ٱللَّهِۚ أَلَآ إِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡمُفۡلِحُونَ\nАллоҳга ва Охират кунига иймон келтирадиган қавмнинг Аллоҳ ва Унинг пайғамбари чизган чизиқдан чиққан кимсалар билан — гарчи улар ўзларининг оталари, ёки ўғиллари, ёки оға-инилари, ёки қариндош-уруғлари бўлсалар-да, дўстлашаётганларини топмассиз. Ана ўшаларнинг дилларига (Аллоҳ) иймонни битиб қўйгандир ва уларни Ўз томонидан бўлган Руҳ — Қуръон билан қувватлантиргандир. У зот уларни остидан дарёлар оқиб турадиган, улар мангу қоладиган жаннатларга киритур. Аллоҳ улардан рози бўлди ва улар ҳам (Аллоҳдан — У зот берган ажр-мукофотлардан) рози бўлдилар. Ана ўшалар Аллоҳнинг ҳизбидирлар. Огоҳ бўлингизким, албатта Аллоҳнинг ҳизби (бўлган зотларгина) нажот топгувчидирлар'}, {'chapter': 59, 'verse': 1, 'text': 'سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлардаги ва Ердаги бор нарса Аллоҳга тасбеҳ айтди. У қудрат ва ҳикмат Соҳибидир'}, {'chapter': 59, 'verse': 2, 'text': 'هُوَ ٱلَّذِيٓ أَخۡرَجَ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ مِن دِيَٰرِهِمۡ لِأَوَّلِ ٱلۡحَشۡرِۚ مَا ظَنَنتُمۡ أَن يَخۡرُجُواْۖ وَظَنُّوٓاْ أَنَّهُم مَّانِعَتُهُمۡ حُصُونُهُم مِّنَ ٱللَّهِ فَأَتَىٰهُمُ ٱللَّهُ مِنۡ حَيۡثُ لَمۡ يَحۡتَسِبُواْۖ وَقَذَفَ فِي قُلُوبِهِمُ ٱلرُّعۡبَۚ يُخۡرِبُونَ بُيُوتَهُم بِأَيۡدِيهِمۡ وَأَيۡدِي ٱلۡمُؤۡمِنِينَ فَٱعۡتَبِرُواْ يَـٰٓأُوْلِي ٱلۡأَبۡصَٰرِ\nУ аҳли китоблардан (яҳудийлардан) кофир бўлган кимсаларни биринчи тўплашдаёқ ўз диёрларидан ҳайдаб чиқарган Зотдир. (Эй мўминлар), сизлар уларнинг чиқиб кетишларини ўйлаган ҳам эмас эдингиз. Улар ҳам ўзларининг қўрғон-қалъаларини Аллоҳ(нинг азобидан) тўсгувчи деб ўйлаган эдилар. Бас, Аллоҳ(нинг азоби) улар ҳисобга олмаган томондан келди ва дилларига қўрқинч солди. Улар уйларини ўз қўллари ва мўминларнинг қўллари билан бузурлар. Бас, эй ақл эгалари, (уларнинг аҳволидан) ибрат олингиз'}, {'chapter': 59, 'verse': 3, 'text': 'وَلَوۡلَآ أَن كَتَبَ ٱللَّهُ عَلَيۡهِمُ ٱلۡجَلَآءَ لَعَذَّبَهُمۡ فِي ٱلدُّنۡيَاۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابُ ٱلنَّارِ\nАгар Аллоҳ уларга сургунни ёзмаганида, албатта уларни мана шу дунёда (қатл қилиш ёки асирлик билан) азоблаган бўлур эди. Улар учун Охиратда дўзах азоби ҳам бордир'}, {'chapter': 59, 'verse': 4, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ ٱللَّهَ وَرَسُولَهُۥۖ وَمَن يُشَآقِّ ٱللَّهَ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nБунга сабаб, уларнинг Аллоҳ ва Унинг пайғамбарига қарши турганларидир. Кимки Аллоҳга қарши турса, бас, албатта Аллоҳнинг жазоси қаттиқдир'}, {'chapter': 59, 'verse': 5, 'text': 'مَا قَطَعۡتُم مِّن لِّينَةٍ أَوۡ تَرَكۡتُمُوهَا قَآئِمَةً عَلَىٰٓ أُصُولِهَا فَبِإِذۡنِ ٱللَّهِ وَلِيُخۡزِيَ ٱلۡفَٰسِقِينَ\n(Эй мўминлар), сизлар (Бану Назийр хурмозорларидан) бирон хурмо дарахтини кесдингларми ёки уни ўз поясида турган ҳолида қолдирдингларми, бас, (сизлар қилган ҳар бир иш) Аллоҳнинг изни-иродаси билан ва у фосиқ-итоатсиз кимсаларни расво қилиш учун (бўлди)'}, {'chapter': 59, 'verse': 6, 'text': 'وَمَآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡهُمۡ فَمَآ أَوۡجَفۡتُمۡ عَلَيۡهِ مِنۡ خَيۡلٖ وَلَا رِكَابٖ وَلَٰكِنَّ ٱللَّهَ يُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَن يَشَآءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nАллоҳ Ўз пайғамбарига улардан ўлжа қилиб берган нарсаларнинг устига сизлар оту туяларни ўйнатиб-чоптириб борганларингиз йўқ (яъни, у ўлжаларни сизлар қийналиб, машаққат чекиб, жанг-жадал билан қўлга киритганларингиз йўқ), лекин Аллоҳ Ўз пайғамбарларини Ўзи хоҳлаган кимсалардан (мана шундай) устун қилиб қўюр. Аллоҳ барча нарсага қодирдир'}, {'chapter': 59, 'verse': 7, 'text': 'مَّآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡ أَهۡلِ ٱلۡقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِ كَيۡ لَا يَكُونَ دُولَةَۢ بَيۡنَ ٱلۡأَغۡنِيَآءِ مِنكُمۡۚ وَمَآ ءَاتَىٰكُمُ ٱلرَّسُولُ فَخُذُوهُ وَمَا نَهَىٰكُمۡ عَنۡهُ فَٱنتَهُواْۚ وَٱتَّقُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ\nАллоҳ қишлоқ-шаҳарларнинг (кофир) аҳолисидан Ўз пайғамбарига ўлжа қилиб берган нарсалар — токи сизлардан бой-бадавлат кишилар ўртасидагина айланиб юраверадиган нарса бўлиб қолмасин учун — Аллоҳники, пайғамбарники ва (пайғамбарнинг) қариндош-уруғлари, етимлар, бечора-мискинлар ва йўловчи-мусофирларникидир. Пайғамбар ўзи сизларга ато этган нарсани олинглар, у зот сизларни қайтарган нарсадан қайтинглар ва Аллоҳдан қўрқинглар! Албатта Аллоҳнинг жазоси қаттиқдир'}, {'chapter': 59, 'verse': 8, 'text': 'لِلۡفُقَرَآءِ ٱلۡمُهَٰجِرِينَ ٱلَّذِينَ أُخۡرِجُواْ مِن دِيَٰرِهِمۡ وَأَمۡوَٰلِهِمۡ يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗا وَيَنصُرُونَ ٱللَّهَ وَرَسُولَهُۥٓۚ أُوْلَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ\n(У ўлжалар яна) ўз диёрларидан ва мол-мулкларидан ҳайдаб чиқарилган зотлар — камбағал муҳожирларникидирки, улар Аллоҳдан фазл-марҳамат ва ризолик истарлар ҳамда Аллоҳ ва Унинг пайғамбарига ёрдам берурлар. Ана ўшалар (иймонларида) содиқ зотлардир'}, {'chapter': 59, 'verse': 9, 'text': 'وَٱلَّذِينَ تَبَوَّءُو ٱلدَّارَ وَٱلۡإِيمَٰنَ مِن قَبۡلِهِمۡ يُحِبُّونَ مَنۡ هَاجَرَ إِلَيۡهِمۡ وَلَا يَجِدُونَ فِي صُدُورِهِمۡ حَاجَةٗ مِّمَّآ أُوتُواْ وَيُؤۡثِرُونَ عَلَىٰٓ أَنفُسِهِمۡ وَلَوۡ كَانَ بِهِمۡ خَصَاصَةٞۚ وَمَن يُوقَ شُحَّ نَفۡسِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nУлардан (муҳожирлардан) илгари (Мадина) диёрига ўрнашган ва иймон-эътиқодни (маҳкам ушлаган) зотлар (ансорлар) эса ўзлари(нинг ёнлари)га ҳижрат қилиб келган кишиларни суюрлар ва дилларида уларга (муҳожирларга) берилган нарса-ўлжалар сабабли бирон ҳасад туймаслар ҳамда гарчи ўзларида эҳтиёж бўлса-да, ўзларини қўйиб (ўзгаларни) ийсор-ихтиёр қилурлар. Кимки ўз нафсининг бахиллигидан сақлана олса, бас, ана ўшалар нажот топгувчи зотлардир'}, {'chapter': 59, 'verse': 10, 'text': 'وَٱلَّذِينَ جَآءُو مِنۢ بَعۡدِهِمۡ يَقُولُونَ رَبَّنَا ٱغۡفِرۡ لَنَا وَلِإِخۡوَٰنِنَا ٱلَّذِينَ سَبَقُونَا بِٱلۡإِيمَٰنِ وَلَا تَجۡعَلۡ فِي قُلُوبِنَا غِلّٗا لِّلَّذِينَ ءَامَنُواْ رَبَّنَآ إِنَّكَ رَءُوفٞ رَّحِيمٌ\nУлардан кейин (дунёга) келган зотлар айтурлар: «Парвардигоро, Ўзинг бизларни ва бизлардан илгари иймон билан ўтган зотларни мағфират қилгин ва қалбларимизда иймон келтирган зотлар учун бирон ғилли-ғаш қилмаган. Парвардигоро, албатта Сен меҳрибон ва раҳмлидирсан»'}, {'chapter': 59, 'verse': 11, 'text': '۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ نَافَقُواْ يَقُولُونَ لِإِخۡوَٰنِهِمُ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ لَئِنۡ أُخۡرِجۡتُمۡ لَنَخۡرُجَنَّ مَعَكُمۡ وَلَا نُطِيعُ فِيكُمۡ أَحَدًا أَبَدٗا وَإِن قُوتِلۡتُمۡ لَنَنصُرَنَّكُمۡ وَٱللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), мунофиқ кимсалар ўзларининг аҳли китоблардан (яҳудийлардан) бўлган кофир биродарларига: «Қасамки, агар сизлар (Мадинадан) қувиб чиқарилсангизлар, албатта бизлар ҳам сизлар билан бирга чиқиб кетурмиз ва сизлар(га қарши урушиш) тўғрисида ҳеч қачон бирон кимсага итоат этмасмиз. Агар сизларга қарши жанг қилинса, албатта бизлар сизларга ёрдам берурмиз», дейишларини кўрмадингизми? Аллоҳ гувоҳлик берур-ки, улар шак-шубҳасиз, ёлғончидирлар'}, {'chapter': 59, 'verse': 12, 'text': 'لَئِنۡ أُخۡرِجُواْ لَا يَخۡرُجُونَ مَعَهُمۡ وَلَئِن قُوتِلُواْ لَا يَنصُرُونَهُمۡ وَلَئِن نَّصَرُوهُمۡ لَيُوَلُّنَّ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يُنصَرُونَ\nҚасамки, агар (яҳудийлар ўз диёрларидан) қувиб чиқарилсалар (мунофиқлар) улар билан бирга чиқиб кетмаслар, қасамки, агар (яҳудийларга қарши) жанг қилинса, (мунофиқлар) уларга ёрдам ҳам бермаслар ва агар (фаразан) ёрдам берсалар ҳам, албатта яҳудийлар (мўминлар томонидан мағлуб қилинишиб) ортларига қараб қочурлар ва кейин уларга бирон ёрдам берилмас'}, {'chapter': 59, 'verse': 13, 'text': 'لَأَنتُمۡ أَشَدُّ رَهۡبَةٗ فِي صُدُورِهِم مِّنَ ٱللَّهِۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ\n(Эй мўминлар), аниқки, сизлар (мунофиқларнинг) дилларида Аллоҳдан кўра қўрқинчлироқдирсизлар. Бунга сабаб, улар (Аллоҳнинг қудратини) англамайдиган қавм эканликларидир'}, {'chapter': 59, 'verse': 14, 'text': 'لَا يُقَٰتِلُونَكُمۡ جَمِيعًا إِلَّا فِي قُرٗى مُّحَصَّنَةٍ أَوۡ مِن وَرَآءِ جُدُرِۭۚ بَأۡسُهُم بَيۡنَهُمۡ شَدِيدٞۚ تَحۡسَبُهُمۡ جَمِيعٗا وَقُلُوبُهُمۡ شَتَّىٰۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ\nУлар (мунофиқ ва яҳудийлар) бирлашган ҳолларида сизлар билан фақат қалъа-қўрғонли қишлоқлар ичида (беркиниб) ёки деворлар ортидагина жанг қила олурлар, (яъни, ҳеч қачон сизларга қарши очиқ жангга чиқа олмаслар). Улар ўз ўрталарида кучли-қувватлидирлар, (яъни, жанг-жадал бўлмасдан туриб ўзаро куч-қудратли эканликлари ҳақида лоф ураверадилар, аммо жанг пайтида ожиз-қўрқоқдирлар). (Оғизларидаги сўзларидан) уларни бир деб ҳисоблайсиз-у, (аммо) диллари хилма-хилдир. Бунга сабаб, уларнинг ақлсиз қавм эканликларидир'}, {'chapter': 59, 'verse': 15, 'text': 'كَمَثَلِ ٱلَّذِينَ مِن قَبۡلِهِمۡ قَرِيبٗاۖ ذَاقُواْ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ\n(Улар) худди ўзларидан озгина олдин ўтган, ўз қилмишларининг зиёнини тотган кимсаларнинг (яъни, Бадр жангида енгилиб шарманда бўлган мушрикларнинг) ўхшашидирлар. Улар учун (Охиратда ҳам) аламли азоб бордир'}, {'chapter': 59, 'verse': 16, 'text': 'كَمَثَلِ ٱلشَّيۡطَٰنِ إِذۡ قَالَ لِلۡإِنسَٰنِ ٱكۡفُرۡ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيٓءٞ مِّنكَ إِنِّيٓ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَٰلَمِينَ\n(Яҳудийларни мўминларга қаршилик қилишга гиж-гижлаётган мунофиқлар) худди шайтоннинг ўхшашидирлар. Эсланг, у инсонга «Кофир бўл», деган эди. Энди қачонки (инсон) кофир бўлгач, (шайтон унга) «Албатта мен сендан безорман. Зеро, мен барча оламлар Парвардигори бўлмиш Аллоҳдан қўрқурман», деди'}, {'chapter': 59, 'verse': 17, 'text': 'فَكَانَ عَٰقِبَتَهُمَآ أَنَّهُمَا فِي ٱلنَّارِ خَٰلِدَيۡنِ فِيهَاۚ وَذَٰلِكَ جَزَـٰٓؤُاْ ٱلظَّـٰلِمِينَ\nБас, иккисининг (шайтон ва унинг сўзига кириб, кофир бўлган инсоннинг) оқибати дўзахга кириб, унда мангу қолишлари бўлди. Золим кимсаларнинг жазоси шудир'}, {'chapter': 59, 'verse': 18, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَلۡتَنظُرۡ نَفۡسٞ مَّا قَدَّمَتۡ لِغَدٖۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nЭй мўминлар, Аллоҳдан қўрқинглар ва (ҳар бир) жон эрта (Қиёмат Куни) учун нимани (яъни, қандай эзгу амални) тақдим этганига қарасин! Аллоҳдан қўрқинглар! Албатта Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 59, 'verse': 19, 'text': 'وَلَا تَكُونُواْ كَٱلَّذِينَ نَسُواْ ٱللَّهَ فَأَنسَىٰهُمۡ أَنفُسَهُمۡۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ\nСизлар Аллоҳни унутиб қўйган (яъни, У зотнинг амр-фармонларига итоат этмаган), бас, (шундан кейин Аллоҳ) уларга ўзларини ҳам унуттириб қўйган (яъни, ўзларига Охиратда фойда берадиган амаллар қилишдан юз ўгиртириб қўйган) кимсалар каби бўлмангиз! Ана ўшалар фосиқ-итоатсиз кимсалардир'}, {'chapter': 59, 'verse': 20, 'text': 'لَا يَسۡتَوِيٓ أَصۡحَٰبُ ٱلنَّارِ وَأَصۡحَٰبُ ٱلۡجَنَّةِۚ أَصۡحَٰبُ ٱلۡجَنَّةِ هُمُ ٱلۡفَآئِزُونَ\nДўзах эгалари билан жаннат эгалари баробар бўлмас. Жаннат эгалари (бахт-саодатга) эришгувчи зотлардир'}, {'chapter': 59, 'verse': 21, 'text': 'لَوۡ أَنزَلۡنَا هَٰذَا ٱلۡقُرۡءَانَ عَلَىٰ جَبَلٖ لَّرَأَيۡتَهُۥ خَٰشِعٗا مُّتَصَدِّعٗا مِّنۡ خَشۡيَةِ ٱللَّهِۚ وَتِلۡكَ ٱلۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنَّاسِ لَعَلَّهُمۡ يَتَفَكَّرُونَ\nАгар Биз ушбу Қуръонни бирон тоғга нозил қилганимизда, албатта сиз у (тоғ)ни Аллоҳнинг қўрқувидан эгилиб ёрилиб кетган ҳолида кўрган бўлур эдингиз. (Лекин айрим инсонларнинг диллари тоғнинг тошидан ҳам қаттиқроқ бўлгани сабабли уларга Қуръон оятлари ҳам таъсир қилмас). Биз бу мисолларни одамлар учун, шояд улар тафаккур қилсалар, деб келтирмоқдамиз'}, {'chapter': 59, 'verse': 22, 'text': 'هُوَ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَۖ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِۖ هُوَ ٱلرَّحۡمَٰنُ ٱلرَّحِيمُ\nУ — Аллоҳ, шундай Зотдирки, ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи бордир. (У) ғайб ва шаҳодатни (яъни, яширин ва ошкора нарсаларни) билгувчидир. У меҳрибон ва раҳмлидир'}, {'chapter': 59, 'verse': 23, 'text': 'هُوَ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡمَلِكُ ٱلۡقُدُّوسُ ٱلسَّلَٰمُ ٱلۡمُؤۡمِنُ ٱلۡمُهَيۡمِنُ ٱلۡعَزِيزُ ٱلۡجَبَّارُ ٱلۡمُتَكَبِّرُۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يُشۡرِكُونَ\nУ — Аллоҳ, шундай Зотдирки, ҳеч қандай илоҳ йўқ, фақат Унинг Ўзи бордир. У (ёлғиз) подшоҳдир, (барча айб-нуқсондан) покдир-саломатдир, (Ўзи хоҳлаган бандаларини дунё бало-офатлари ва Охират азобидан) омон қилгувчидир, (бандаларнинг амалларини) кўриб-кузатиб тургувчидир, қудрат Соҳибидир, бўйсундиргувчидир, (танҳо) кибр Эгасидир. Аллоҳ уларнинг (мушрикларнинг) ширкларидан покдир'}, {'chapter': 59, 'verse': 24, 'text': 'هُوَ ٱللَّهُ ٱلۡخَٰلِقُ ٱلۡبَارِئُ ٱلۡمُصَوِّرُۖ لَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ يُسَبِّحُ لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nУ — Аллоҳ яратгувчидир, (йўқдан) бор қилгувчидир, (барча нарсага) сурат-шакл бергувчидир. Унинг гўзал исмлари бордир. Осмонлар ва Ердаги бор нарса Унга тасбеҳ айтур. У қудрат ва ҳикмат Соҳибидир'}, {'chapter': 60, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ عَدُوِّي وَعَدُوَّكُمۡ أَوۡلِيَآءَ تُلۡقُونَ إِلَيۡهِم بِٱلۡمَوَدَّةِ وَقَدۡ كَفَرُواْ بِمَا جَآءَكُم مِّنَ ٱلۡحَقِّ يُخۡرِجُونَ ٱلرَّسُولَ وَإِيَّاكُمۡ أَن تُؤۡمِنُواْ بِٱللَّهِ رَبِّكُمۡ إِن كُنتُمۡ خَرَجۡتُمۡ جِهَٰدٗا فِي سَبِيلِي وَٱبۡتِغَآءَ مَرۡضَاتِيۚ تُسِرُّونَ إِلَيۡهِم بِٱلۡمَوَدَّةِ وَأَنَا۠ أَعۡلَمُ بِمَآ أَخۡفَيۡتُمۡ وَمَآ أَعۡلَنتُمۡۚ وَمَن يَفۡعَلۡهُ مِنكُمۡ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ\nЭй мўминлар, Менинг душманим ва сизларнинг душманларингиз (бўлган мушриклар)ни дўст тутманглар! Сизлар уларга дўстлик (ҳақида хат-хабар) юборурсизлар, ҳолбуки, улар сизларга келган Ҳақ (дин ва Қуръон)га кофир бўлгандирлар! Улар пайғамбарни ҳам, сизларни ҳам Парвардигорларингиз бўлмиш Аллоҳга иймон келтирганларингиз сабабли (ўз диёрларингиздан) ҳайдаб чиқармоқдалар-ку! Агар сизлар Менинг йўлимда жиҳод қилиш учун ва Менинг розилигимни истаб чиққан бўлсангизлар (у ҳолда мушрик-кофир кимсаларни дўст тутманглар)! Сизлар уларга пинҳона дўстлик қилмоқдасизлар. Ҳолбуки, Мен сизлар яширган нарсани ҳам, ошкор қилган нарсани ҳам жуда яхши билгувчидирман! Сизлардан ким шу (иш)ни қилса, бас, аниқки, у Тўғри Йўлдан озибди'}, {'chapter': 60, 'verse': 2, 'text': 'إِن يَثۡقَفُوكُمۡ يَكُونُواْ لَكُمۡ أَعۡدَآءٗ وَيَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ وَأَلۡسِنَتَهُم بِٱلسُّوٓءِ وَوَدُّواْ لَوۡ تَكۡفُرُونَ\n(Эй мўминлар), агар улар (мушриклар) сизларга зафар топсалар, душманларингиз бўлурлар ва сизларга қўл ва тилларини ёмонлик билан чўзурлар (яъни, сизларни ўлдирурлар, ҳақоратлар қилурлар). Улар сизларнинг яна кофир бўлишларингизни истарлар'}, {'chapter': 60, 'verse': 3, 'text': 'لَن تَنفَعَكُمۡ أَرۡحَامُكُمۡ وَلَآ أَوۡلَٰدُكُمۡۚ يَوۡمَ ٱلۡقِيَٰمَةِ يَفۡصِلُ بَيۡنَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ\n(Қиёмат Кунида) сизларга қариндош-уруғларингиз ҳам, бола-чақаларингиз ҳам ҳаргиз фойда бермас! Қиёмат Кунида (Аллоҳ) ўрталарингизни ажратиб қўюр. (Бас, нечун ўша уруғ-авлодингизни ҳимоя қилиш учун Аллоҳга гуноҳкор бўлмоқдасизлар?!) Аллоҳ қилаётган амалларингизни кўриб тургувчидир'}, {'chapter': 60, 'verse': 4, 'text': 'قَدۡ كَانَتۡ لَكُمۡ أُسۡوَةٌ حَسَنَةٞ فِيٓ إِبۡرَٰهِيمَ وَٱلَّذِينَ مَعَهُۥٓ إِذۡ قَالُواْ لِقَوۡمِهِمۡ إِنَّا بُرَءَـٰٓؤُاْ مِنكُمۡ وَمِمَّا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ كَفَرۡنَا بِكُمۡ وَبَدَا بَيۡنَنَا وَبَيۡنَكُمُ ٱلۡعَدَٰوَةُ وَٱلۡبَغۡضَآءُ أَبَدًا حَتَّىٰ تُؤۡمِنُواْ بِٱللَّهِ وَحۡدَهُۥٓ إِلَّا قَوۡلَ إِبۡرَٰهِيمَ لِأَبِيهِ لَأَسۡتَغۡفِرَنَّ لَكَ وَمَآ أَمۡلِكُ لَكَ مِنَ ٱللَّهِ مِن شَيۡءٖۖ رَّبَّنَا عَلَيۡكَ تَوَكَّلۡنَا وَإِلَيۡكَ أَنَبۡنَا وَإِلَيۡكَ ٱلۡمَصِيرُ\nСизлар учун Иброҳим ва у билан бирга бўлган кишиларда (уларнинг кофирларга қилган муносабатларида) гўзал намуна бордир. Эслангиз, улар ўз қавмларига: «Дарҳақиқат, бизлар сизлардан ва сизлар Аллоҳни қўйиб ибодат қилаётган бутларингиздан безормиз. Бизлар сизлар (ишониб, «ибодат» қилаётган бут-санамлар)ни инкор этдик. Токи сизлар ёлғиз Аллоҳга иймон келтиргунларингизча сизлар билан бизнинг ўртамизда мангу адоват ва ёмон кўриш зоҳирдир», дедилар. Фақат Иброҳим ўз(ининг кофир бўлган) отасига: «Албатта мен (Аллоҳдан) сен учун мағфират сўрайман, (лекин) сенга Аллоҳ томонидан бўладиган бирон нарсани (азобни қайтаришга) молик эмасман», (деган сўзларигина сизларга намуна бўлмасин)! (Иброҳим ва у билан бирга бўлган мўминлар яна шундай дуо қилган эдилар): «Парвардигоро, ёлғиз Ўзингга таваккул қилдик ва Ўзингга инобат-тавба қилдик. Ёлғиз Сенгагина қайтишимиз бордир'}, {'chapter': 60, 'verse': 5, 'text': 'رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلَّذِينَ كَفَرُواْ وَٱغۡفِرۡ لَنَا رَبَّنَآۖ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nПарвардигоро, бизларни бу кофир бўлган кимсаларга мафтун-алдангувчи қилиб қўймагин, бизларни(нг гуноҳларимизни) мағфират қилгин. Парвардигоро, албатта Сен Ўзинг қудрат ва ҳикмат Соҳибидирсан»'}, {'chapter': 60, 'verse': 6, 'text': 'لَقَدۡ كَانَ لَكُمۡ فِيهِمۡ أُسۡوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرۡجُواْ ٱللَّهَ وَٱلۡيَوۡمَ ٱلۡأٓخِرَۚ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ\nДарҳақиқат, сизлар учун — Аллоҳ ва Охират Кунидан умидвор бўлган кишилар учун уларда (Иброҳим пайғамбар ва у зот билан бирга бўлган мўминларнинг тутган йўлларида) гўзал намуна бордир. Ким (Исломдан ва мана шу панд-насиҳатлардан) юз ўгириб кетса, у ҳолда албатта Аллоҳ (ундай кимсалардан) беҳожат ва мақтовга лойиқ Зотдир'}, {'chapter': 60, 'verse': 7, 'text': '۞عَسَى ٱللَّهُ أَن يَجۡعَلَ بَيۡنَكُمۡ وَبَيۡنَ ٱلَّذِينَ عَادَيۡتُم مِّنۡهُم مَّوَدَّةٗۚ وَٱللَّهُ قَدِيرٞۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭҳтимол Аллоҳ сизлар билан (Макка кофирлари орасидаги) сизлар душманлашиб юрган кимсалар ўртасида — (уларни ҳам иймон ва Исломга муваффақ қилиш билан) — бир дўстликни пайдо қилиб қўяр. Аллоҳ (барча ишга) қодирдир. Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 60, 'verse': 8, 'text': 'لَّا يَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ لَمۡ يُقَٰتِلُوكُمۡ فِي ٱلدِّينِ وَلَمۡ يُخۡرِجُوكُم مِّن دِيَٰرِكُمۡ أَن تَبَرُّوهُمۡ وَتُقۡسِطُوٓاْ إِلَيۡهِمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ\nАллоҳ сизларни динларингиз тўғрисида сизлар билан урушмаган ва сизларни ўз диёрларингиздан ҳайдаб чиқармаган кимсалардан — уларга яхшилик қилишларингиздан ва уларга адолатли бўлишларингизда қайтармас. Албатта Аллоҳ адолат қилгувчиларни севар'}, {'chapter': 60, 'verse': 9, 'text': 'إِنَّمَا يَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ قَٰتَلُوكُمۡ فِي ٱلدِّينِ وَأَخۡرَجُوكُم مِّن دِيَٰرِكُمۡ وَظَٰهَرُواْ عَلَىٰٓ إِخۡرَاجِكُمۡ أَن تَوَلَّوۡهُمۡۚ وَمَن يَتَوَلَّهُمۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ\nАллоҳ сизларни фақат динларингиз тўғрисида сизлар билан урушган ва сизларни ўз диёрларингиздан ҳайдаб чиқарган ҳамда сизларни ҳайдаб чиқаришда бир-бирларига ёрдамлашган кимсалардан — улар билан дўстлашишларингиздан қайтарур. Кимки улар дўстлашса, бас, ана ўшалар золим кимсаларнинг ўзидир'}, {'chapter': 60, 'verse': 10, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا جَآءَكُمُ ٱلۡمُؤۡمِنَٰتُ مُهَٰجِرَٰتٖ فَٱمۡتَحِنُوهُنَّۖ ٱللَّهُ أَعۡلَمُ بِإِيمَٰنِهِنَّۖ فَإِنۡ عَلِمۡتُمُوهُنَّ مُؤۡمِنَٰتٖ فَلَا تَرۡجِعُوهُنَّ إِلَى ٱلۡكُفَّارِۖ لَا هُنَّ حِلّٞ لَّهُمۡ وَلَا هُمۡ يَحِلُّونَ لَهُنَّۖ وَءَاتُوهُم مَّآ أَنفَقُواْۚ وَلَا جُنَاحَ عَلَيۡكُمۡ أَن تَنكِحُوهُنَّ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّۚ وَلَا تُمۡسِكُواْ بِعِصَمِ ٱلۡكَوَافِرِ وَسۡـَٔلُواْ مَآ أَنفَقۡتُمۡ وَلۡيَسۡـَٔلُواْ مَآ أَنفَقُواْۚ ذَٰلِكُمۡ حُكۡمُ ٱللَّهِ يَحۡكُمُ بَيۡنَكُمۡۖ وَٱللَّهُ عَلِيمٌ حَكِيمٞ\nЭй мўминлар, қачон сизларга (Макка кофирларининг қўли остида қолган) мўминалар ҳижрат қилиб келсалар, сизлар улар(нинг иймонлари)ни имтиҳон қилиб кўринглар (яъни, улар ўз эрларини ёмон кўриб ёки сизлардан биронтангизга ишқи тушиб қолгани учун эмас, балки фақат дини Ислом учун ҳижрат қилганлари ҳақида сизларга қасам ичсинлар). — Аллоҳ уларнинг (дилларидаги) иймонларини (ҳам) жуда яхши билгувчидир. — Бас, агар сизлар уларнинг (ҳақиқий) мўмина эканликларини билсангизлар, у ҳолда уларни кофирларга қайтарманглар! У мўминалар (кофирлар) учун ҳам ҳалол эмас ва у (кофир)лар (мўмина)лар учун ҳалол эмасдир, ва уларга (яъни, сизларнинг олдингизда қолган мўминаларнинг Маккада қолган кофир эрларига мана шу хотинлари учун) сарфлаган маҳрларини (қайтариб) беринглар. Қачон сизлар у (мўминаларнинг эр)ларига маҳрларини берсангизлар, сизларга уларни никоҳларингизга олишда бирон гуноҳ йўқдир. (Шунингдек) сизлар ҳам кофираларнинг билак-қўлларидан ушламанглар (яъни, сизларнинг хотинларингиз кофира бўлган ҳолларида кофирлар билан қолишни истаса, сизлар уларнинг йўлларини тўсиб никоҳларингизда сақламанглар, балки уларга уйланмоқчи бўлган кофирлардан) ўзларингиз сарфлаган маҳрни талаб қилинглар ва улар ҳам (ўзларининг никоҳидан чиққан ва мусулмонларнинг олдига ҳижрат қилиб кетган мўминаларга) талаб қилсинлар. Бу Аллоҳнинг ҳукмидир. У сизларнинг ўртангизда ҳукм қилур. Аллоҳ билгувчи ва ҳикмат Соҳибидир'}, {'chapter': 60, 'verse': 11, 'text': 'وَإِن فَاتَكُمۡ شَيۡءٞ مِّنۡ أَزۡوَٰجِكُمۡ إِلَى ٱلۡكُفَّارِ فَعَاقَبۡتُمۡ فَـَٔاتُواْ ٱلَّذِينَ ذَهَبَتۡ أَزۡوَٰجُهُم مِّثۡلَ مَآ أَنفَقُواْۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ\nАгар жуфтларингиздан биронтаси кофирлар томонга ўтиб кетса-ю, (аммо улар ўша хотин учун берилган маҳрни унинг эрига қайтариб бермасалар) сўнг сизлар ўч олсангизлар (яъни, у кофирлар билан урушиб ғалаба қилсангизлар ва ўлжалар олсангизлар) у ҳолда (ўша ўлжалардан) жуфтлари (кофирлар томонга ўтиб) кетган кишиларга улар сарфлаган маҳр миқдорини беринглар! Ва ўзларингиз иймон келтиргувчи бўлган — Аллоҳдан қўрқинглар'}, {'chapter': 60, 'verse': 12, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِذَا جَآءَكَ ٱلۡمُؤۡمِنَٰتُ يُبَايِعۡنَكَ عَلَىٰٓ أَن لَّا يُشۡرِكۡنَ بِٱللَّهِ شَيۡـٔٗا وَلَا يَسۡرِقۡنَ وَلَا يَزۡنِينَ وَلَا يَقۡتُلۡنَ أَوۡلَٰدَهُنَّ وَلَا يَأۡتِينَ بِبُهۡتَٰنٖ يَفۡتَرِينَهُۥ بَيۡنَ أَيۡدِيهِنَّ وَأَرۡجُلِهِنَّ وَلَا يَعۡصِينَكَ فِي مَعۡرُوفٖ فَبَايِعۡهُنَّ وَٱسۡتَغۡفِرۡ لَهُنَّ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ\nЭй пайғамбар, қачон сизнинг олдингизга мўминалар келишиб, сизга ўзларининг Аллоҳга бирон нарсани шерик қилмасликларига, ўғирлик қилмасликларига, зино қилмасликларига, (камбағаллик ёки шарманда бўлишдан қўрқиб) ўз болаларини ўлдирмасликларига ва қўл-оёқлари ўртасида тўқиб оладиган бўҳтонни қилмасликларига (яъни, фарзандсизлик сабабли ўз эрларидан ажралиб қолмаслик учун бирон ташландиқ болани топиб, гўё у қўл-оёқлари ўртасидан чиққандек, яъни ўзлари туққандек эрларига «Мана шу бола сендан бўлди», деб бўҳтон қилмасликка) ҳамда бирон яхши ишда сизга итоатсизлик қилмасликларига қасамёд қилсалар, сиз уларнинг қасамёдларини қабул қилинг ва улар учун Аллоҳдан мағфират сўранг! Албатта Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 60, 'verse': 13, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَوَلَّوۡاْ قَوۡمًا غَضِبَ ٱللَّهُ عَلَيۡهِمۡ قَدۡ يَئِسُواْ مِنَ ٱلۡأٓخِرَةِ كَمَا يَئِسَ ٱلۡكُفَّارُ مِنۡ أَصۡحَٰبِ ٱلۡقُبُورِ\nЭй мўминлар, Аллоҳнинг ғазабига дучор бўлган қавм билан дўст бўлманглар! Худди барча кофирлар қабр эгалари (яъни, ўликларнинг қайта тирилишлари)дан ноумид бўлганларидек уларнинг ҳам Охиратдан умидлари узилгандир'}, {'chapter': 61, 'verse': 1, 'text': 'سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\nОсмонлардаги ва Ердаги бор нарса Аллоҳга тасбеҳ айтди. У қудрат ва ҳикмат Соҳибидир'}, {'chapter': 61, 'verse': 2, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِمَ تَقُولُونَ مَا لَا تَفۡعَلُونَ\nЭй мўминлар, сизлар нега ўзларингиз қилмайдиган нарсани (қиламиз деб) айтурсизлар'}, {'chapter': 61, 'verse': 3, 'text': 'كَبُرَ مَقۡتًا عِندَ ٱللَّهِ أَن تَقُولُواْ مَا لَا تَفۡعَلُونَ\nСизларнинг ўзларингиз қилмайдиган ишни (қиламиз, деб) айтишларингиз Аллоҳ наздида ўта манфур (ишдир)'}, {'chapter': 61, 'verse': 4, 'text': 'إِنَّ ٱللَّهَ يُحِبُّ ٱلَّذِينَ يُقَٰتِلُونَ فِي سَبِيلِهِۦ صَفّٗا كَأَنَّهُم بُنۡيَٰنٞ مَّرۡصُوصٞ\nАлбатта Аллоҳ Ўзининг йўлида гўё туташ бинолардек бир сафга тизилган ҳолларида жиҳод қиладиган зотларни севар'}, {'chapter': 61, 'verse': 5, 'text': 'وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ لِمَ تُؤۡذُونَنِي وَقَد تَّعۡلَمُونَ أَنِّي رَسُولُ ٱللَّهِ إِلَيۡكُمۡۖ فَلَمَّا زَاغُوٓاْ أَزَاغَ ٱللَّهُ قُلُوبَهُمۡۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\nЭсланг, Мусо ўз қавмига: «Эй қавмим, нега сизлар мен Аллоҳнинг сизларга (юборган) пайғамбари эканлигимни билган ҳолларингизда менга озор берурсизлар?!», деган эди. Бас, қачонки улар (Ҳақ йўлидан) оғишгач, Аллоҳ уларнинг дилларини (ҳидоятдан) оғдириб қўйди. (Зотан), Аллоҳ фосиқ-итоатсиз қавмни ҳидоят қилмас'}, {'chapter': 61, 'verse': 6, 'text': 'وَإِذۡ قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ إِنِّي رَسُولُ ٱللَّهِ إِلَيۡكُم مُّصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ ٱلتَّوۡرَىٰةِ وَمُبَشِّرَۢا بِرَسُولٖ يَأۡتِي مِنۢ بَعۡدِي ٱسۡمُهُۥٓ أَحۡمَدُۖ فَلَمَّا جَآءَهُم بِٱلۡبَيِّنَٰتِ قَالُواْ هَٰذَا سِحۡرٞ مُّبِينٞ\nЭсланг, Ийсо бинни Марям: «Эй Бани Исроил, албатта мен Аллоҳнинг сизларга (юборган) пайғамбаридирман. (Мен) ўзимдан олдинги Тавротни тасдиқлагувчи ва ўзимдан кейин келадиган Аҳмад исмли бир пайғамбар ҳақида хушхабар бергувчи бўлган ҳолда (юборилдим)», деган эди. Бас, қачонки (Ийсо) уларга (ўзининг ҳақ пайғамбар эканлигига) аниқ-равшан ҳужжат-мўъжизалар келтиргач, улар: «Бу очиқ сеҳр», дедилар'}, {'chapter': 61, 'verse': 7, 'text': 'وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُوَ يُدۡعَىٰٓ إِلَى ٱلۡإِسۡلَٰمِۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nЎзи Исломга даъват қилинаётган ҳолида, (у даъватни қабул қилиш ўрнига) Аллоҳ шаънига («У зотнинг боласи бор, шериклари ҳам бор», деб, ёки Аллоҳнинг оят-мўьжизаларини «Бу сеҳр», деб) ёлғон тўқиган кимсадан ҳам золимроқ ким бор?! Аллоҳ бундай золим қавмни ҳидоят қилмас'}, {'chapter': 61, 'verse': 8, 'text': 'يُرِيدُونَ لِيُطۡفِـُٔواْ نُورَ ٱللَّهِ بِأَفۡوَٰهِهِمۡ وَٱللَّهُ مُتِمُّ نُورِهِۦ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ\nУлар Аллоҳнинг нурини (яъни, Исломни) оғизлари (яъни, беҳуда гаплари) билан ўчирмоқчи бўлурлар. Аллоҳ эса, гарчи кофирлар истамасалар-да, Ўз нурини (яъни, динини) тўла (яъни, ҳар тарафга) ёйгувчидир'}, {'chapter': 61, 'verse': 9, 'text': 'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ\nУ (Аллоҳ) Ўз пайғамбарини ҳидоят ва ҳақ дин билан — гарчи мушриклар истамасалар-да — барча динларга ғолиб қилиш учун юборган Зотдир'}, {'chapter': 61, 'verse': 10, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ هَلۡ أَدُلُّكُمۡ عَلَىٰ تِجَٰرَةٖ تُنجِيكُم مِّنۡ عَذَابٍ أَلِيمٖ\nЭй мўминлар, сизларга аламли азобдан нажот берадиган бир «тижорат»ни кўрсатайми'}, {'chapter': 61, 'verse': 11, 'text': 'تُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ وَتُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\n(Ўша «тижорат» мана будир) — Аллоҳ ва Унинг пайғамбарига иймон келтирурсизлар ва Аллоҳ йўлида мол ва жонларингиз билан жиҳод қилурсизлар. Мана шу агар билсангизлар ўзларингиз учун энг яхши (иш)дир'}, {'chapter': 61, 'verse': 12, 'text': 'يَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡ وَيُدۡخِلۡكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّـٰتِ عَدۡنٖۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\n(Агар шундай қилсангизлар, Аллоҳ) сизларнинг гуноҳларингизни мағфират қилур ҳамда сизларни остидан дарёлар оқиб турадиган жаннатларга ва абадий жаннатлардаги покиза масканларга киритур. Бу эса улуғ бахтдир'}, {'chapter': 61, 'verse': 13, 'text': 'وَأُخۡرَىٰ تُحِبُّونَهَاۖ نَصۡرٞ مِّنَ ٱللَّهِ وَفَتۡحٞ قَرِيبٞۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ\nВа (Аллоҳ сизларга) яна бошқа сизлар суядиган (бир неъматни ҳам берурки, у) Аллоҳ томонидан бўлган ғалаба ва яқин(да рўй бергувчи Макка) фатҳидир. (Эй Муҳаммад алайҳис-салоту вас-салом), мўминларга (мана шу) хушхабарни етказинг'}, {'chapter': 61, 'verse': 14, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُوٓاْ أَنصَارَ ٱللَّهِ كَمَا قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ لِلۡحَوَارِيِّـۧنَ مَنۡ أَنصَارِيٓ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ ٱللَّهِۖ فَـَٔامَنَت طَّآئِفَةٞ مِّنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ وَكَفَرَت طَّآئِفَةٞۖ فَأَيَّدۡنَا ٱلَّذِينَ ءَامَنُواْ عَلَىٰ عَدُوِّهِمۡ فَأَصۡبَحُواْ ظَٰهِرِينَ\nЭй мўминлар, сизлар ҳам худди (ҳаворийлар каби) Аллоҳнинг ёрдамчилари бўлингиз! — Ийсо бинни Марям ҳаворийларга: «Аллоҳга (яъни, Унинг динига даъват қилишимда) ким менинг ёрдамчим бўлур?», деганида, ҳаворийлар айтдилар: «Биз Аллоҳнинг (динига) ёрдам бергувчилармиз». Бас, Бани Исроилдан бир тоифа (одамлар Ийсога) иймон келтирди ва бир тоифа кофир бўлди. Бас, Биз иймон келтирган зотларни душманларидан кучли қилиб, улар (кофирлар устига) ғолиб бўлиб қолдилар'}, {'chapter': 62, 'verse': 1, 'text': 'يُسَبِّحُ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ ٱلۡمَلِكِ ٱلۡقُدُّوسِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ\nОсмонлардаги ва Ердаги бор нарса (ёлғиз) подшоҳ, (барча айб-нуқсондан) пок, қудрат ва ҳикмат соҳиби бўлмиш Аллоҳга тасбеҳ айтур'}, {'chapter': 62, 'verse': 2, 'text': 'هُوَ ٱلَّذِي بَعَثَ فِي ٱلۡأُمِّيِّـۧنَ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٖ\nУ (Аллоҳ) омийлар (яъни, аҳли китоб бўлмаган илмсиз кишилар) орасига ўзларида бўлган, уларга (Қуръон) оятларини тиловат қиладиган, уларни (ширк ва жаҳолатдан) поклайдиган ҳамда уларга Китоб — Қуръон ва Ҳикмат — Ҳадисни ўргатадиган бир пайғамбарни (яъни, Муҳаммад алайҳис-салоту вас-саломни) юборган Зотдир. Шак-шубҳасиз, улар (ўзларига пайғамбар келишдан) илгари очиқ залолатда эдилар'}, {'chapter': 62, 'verse': 3, 'text': 'وَءَاخَرِينَ مِنۡهُمۡ لَمَّا يَلۡحَقُواْ بِهِمۡۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\n(Аллоҳ бу пайғамбарни) яна улардан бошқаларга — уларга ета олмаган (энди дунёга келадиган барча инсонларга ҳам юборгандир). У қудрат ва ҳикмат Соҳибидир'}, {'chapter': 62, 'verse': 4, 'text': 'ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ\nБу (яъни, барча инсониятга пайғамбар бўлиш) Аллоҳнинг фазлу марҳамати бўлиб, Ўзи хоҳлаган кишиларга берур. Аллоҳ улуғ фазлу марҳамат Соҳибидир'}, {'chapter': 62, 'verse': 5, 'text': 'مَثَلُ ٱلَّذِينَ حُمِّلُواْ ٱلتَّوۡرَىٰةَ ثُمَّ لَمۡ يَحۡمِلُوهَا كَمَثَلِ ٱلۡحِمَارِ يَحۡمِلُ أَسۡفَارَۢاۚ بِئۡسَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ\nТаврот юкланган — берилган, сўнгра уни кўтара олмаган (яъни, унинг кўрсатмаларига амал қилмаган) кимсалар (яҳудийлар)нинг мисоли худди китобларни кўтариб кетаётган эшакка ўхшайди (яъни, унинг вазминлигидан чарчаб-толадилару, аммо у Китобнинг ичидаги нарсалардан фойдаланмайдилар). Аллоҳнинг оятларини ёлғон деган қавмнинг мисоли нақадар ёмондир. Аллоҳ бундай золим қавмни ҳидоят қилмас'}, {'chapter': 62, 'verse': 6, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلَّذِينَ هَادُوٓاْ إِن زَعَمۡتُمۡ أَنَّكُمۡ أَوۡلِيَآءُ لِلَّهِ مِن دُونِ ٱلنَّاسِ فَتَمَنَّوُاْ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Эй яҳудийлар, агар сизлар (бошқа) одамларни эмас, фақат ўзларингизни Аллоҳнинг дўстлари деб даъво қилсангизлар — агар (мана шу даъволарингизда) ростгўй бўлсангизлар — ўлимни орзу қилинглар-чи'}, {'chapter': 62, 'verse': 7, 'text': 'وَلَا يَتَمَنَّوۡنَهُۥٓ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ\nУлар қўллари (яъни, ўзлари) қилиб қўйган ишлари (яъни, Тавротда ҳақ пайғамбар эканликлари зикр қилинган Муҳаммад алайҳис-салоту вас-саломни ёлғончи қилганлари) сабабли ҳеч қачон (ўлимни) орзу қила олмаслар. Аллоҳ золим кимсаларни билгувчидир'}, {'chapter': 62, 'verse': 8, 'text': 'قُلۡ إِنَّ ٱلۡمَوۡتَ ٱلَّذِي تَفِرُّونَ مِنۡهُ فَإِنَّهُۥ مُلَٰقِيكُمۡۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ\nАйтинг: «Сизлар қочаётган бу ўлим албатта сизларга йўлиққувчидир. Сўнгра сизлар ғайбу-шаҳодатни (яъни, барча яширин ва ошкора нарса-амалларни) билгувчи (Аллоҳ)га қайтарилурсизлар. Бас, У зот сизларга қилиб ўтган амалларингизнинг хабарини берур!»'}, {'chapter': 62, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نُودِيَ لِلصَّلَوٰةِ مِن يَوۡمِ ٱلۡجُمُعَةِ فَٱسۡعَوۡاْ إِلَىٰ ذِكۡرِ ٱللَّهِ وَذَرُواْ ٱلۡبَيۡعَۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ\nЭй мўминлар, қачон Жумъа кунидаги намозга чорланса (яъни, азон айтилса), дарҳол Аллоҳнинг зикрига боринглар ва олди-сотдини тарк қилинглар! Агар биладиган бўлсангизлар, мана шу (яъни, Аллоҳнинг зикрига — Жумъа намозини ўқишга шошилиш) ўзларингиз учун яхшироқдир'}, {'chapter': 62, 'verse': 10, 'text': 'فَإِذَا قُضِيَتِ ٱلصَّلَوٰةُ فَٱنتَشِرُواْ فِي ٱلۡأَرۡضِ وَٱبۡتَغُواْ مِن فَضۡلِ ٱللَّهِ وَٱذۡكُرُواْ ٱللَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ\nЭнди қачон намоз адо қилингач, Ерга тарқалиб, Аллоҳнинг фазлу марҳаматидан (ризқу рўз) истайверинглар. Аллоҳни кўп зикр қилингларки, шояд нажот топурсизлар'}, {'chapter': 62, 'verse': 11, 'text': 'وَإِذَا رَأَوۡاْ تِجَٰرَةً أَوۡ لَهۡوًا ٱنفَضُّوٓاْ إِلَيۡهَا وَتَرَكُوكَ قَآئِمٗاۚ قُلۡ مَا عِندَ ٱللَّهِ خَيۡرٞ مِّنَ ٱللَّهۡوِ وَمِنَ ٱلتِّجَٰرَةِۚ وَٱللَّهُ خَيۡرُ ٱلرَّـٰزِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қачон улар бирон тижоратни ёки ўйин-кулгини кўриб қолсалар, ўшанга қараб сочилиб-тарқалиб кетурлар ва сизни (минбарда) тик турган ҳолингизда тарк қилурлар. Айтинг: «Аллоҳ ҳузуридаги нарса (иймон ва яхши амаллар учун бериладиган ажр-мукофот) ўйин-кулгидан ҳам, тижоратдан ҳам яхшироқдир! Аллоҳ ризқ бергувчиларнинг яхшироғидир!»'}, {'chapter': 63, 'verse': 1, 'text': 'إِذَا جَآءَكَ ٱلۡمُنَٰفِقُونَ قَالُواْ نَشۡهَدُ إِنَّكَ لَرَسُولُ ٱللَّهِۗ وَٱللَّهُ يَعۡلَمُ إِنَّكَ لَرَسُولُهُۥ وَٱللَّهُ يَشۡهَدُ إِنَّ ٱلۡمُنَٰفِقِينَ لَكَٰذِبُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қачон мунофиқлар сизнинг олдингизга келсалар: «Гувоҳлик берамизки, сиз шак-шубҳасиз, Аллоҳнинг пайғамбаридирсиз», дерлар. Аллоҳ дарҳақиқат, сиз Ўзининг пайғамбари эканлигингизни билур, яна Аллоҳ у мунофиқларнинг шак-шубҳасиз, ёлғончи эканликларига ҳам гувоҳлик берур'}, {'chapter': 63, 'verse': 2, 'text': 'ٱتَّخَذُوٓاْ أَيۡمَٰنَهُمۡ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ ٱللَّهِۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ\nУлар ўзларининг (мусулмон эканликлари ҳақида ичган ёлғон) қасамларини қалқон қилишиб олиб, (одамларни) Аллоҳнинг йўлидан тўсдилар! Уларнинг қилгувчи бўлган амаллари нақадар ёмондир'}, {'chapter': 63, 'verse': 3, 'text': 'ذَٰلِكَ بِأَنَّهُمۡ ءَامَنُواْ ثُمَّ كَفَرُواْ فَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ\nБунга сабаб уларнинг (тилларида) иймон келтириб, сўнгра (дилларида) кофир бўлганларидир. Бас, уларнинг диллари муҳрлаб қўйилди. Энди улар (иймоннинг ҳақиқатини) англай олмаслар'}, {'chapter': 63, 'verse': 4, 'text': '۞وَإِذَا رَأَيۡتَهُمۡ تُعۡجِبُكَ أَجۡسَامُهُمۡۖ وَإِن يَقُولُواْ تَسۡمَعۡ لِقَوۡلِهِمۡۖ كَأَنَّهُمۡ خُشُبٞ مُّسَنَّدَةٞۖ يَحۡسَبُونَ كُلَّ صَيۡحَةٍ عَلَيۡهِمۡۚ هُمُ ٱلۡعَدُوُّ فَٱحۡذَرۡهُمۡۚ قَٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ يُؤۡفَكُونَ\nҚачон сиз уларга боқсангиз уларнинг жисмлари (кўринишлари, кийган кийимлари) сизни ҳайратга солур, сўзлаганларида эса сўзларига (оҳангдор, фасоҳатли бўлгани учун берилиб) қулоқ солурсиз. (Лекин уларнинг диллари иймон ва яхшиликдан холи бўлгани учун) улар гўё (деворга) йўлаб қўйилган (чирик) ёғочларга ўхшайдилар. Улар (юраксизликлари сабабли) ҳар бир қичқириқ-овозни устларига (тушаётган бирон бало-офат деб) гумон қиладилар. Улар душмандирлар. Бас, улардан эҳтиёт бўлинг! Уларни Аллоҳ лаънатлагай! Қандай адашмоқдалар-а'}, {'chapter': 63, 'verse': 5, 'text': 'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ يَسۡتَغۡفِرۡ لَكُمۡ رَسُولُ ٱللَّهِ لَوَّوۡاْ رُءُوسَهُمۡ وَرَأَيۡتَهُمۡ يَصُدُّونَ وَهُم مُّسۡتَكۡبِرُونَ\nҚачон уларга: «Келинглар, Аллоҳнинг пайғамбари сизлар учун мағфират сўрар», дейилса, албатта улар бошларини буриб кетурлар ва уларни кибр-ҳаво қилган ҳолларида юз ўгиришларини кўрурсиз'}, {'chapter': 63, 'verse': 6, 'text': 'سَوَآءٌ عَلَيۡهِمۡ أَسۡتَغۡفَرۡتَ لَهُمۡ أَمۡ لَمۡ تَسۡتَغۡفِرۡ لَهُمۡ لَن يَغۡفِرَ ٱللَّهُ لَهُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз улар учун мағфират сўрадингизми ёки мағфират сўрамадингизми уларга баробардир — Аллоҳ уларни ҳаргиз мағфират қилмас! Албатта Аллоҳ бундай фосиқ-итоатсиз қавмни ҳидоят қилмас'}, {'chapter': 63, 'verse': 7, 'text': 'هُمُ ٱلَّذِينَ يَقُولُونَ لَا تُنفِقُواْ عَلَىٰ مَنۡ عِندَ رَسُولِ ٱللَّهِ حَتَّىٰ يَنفَضُّواْۗ وَلِلَّهِ خَزَآئِنُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَٰكِنَّ ٱلۡمُنَٰفِقِينَ لَا يَفۡقَهُونَ\nУлар (ўзларининг тобеъларига): «Аллоҳнинг пайғамбари ҳузуридаги кишиларга инфоқ-эҳсон қилманглар, токи улар тарқалиб кетсинлар», дейдиган кимсалардир. Ҳолбуки, осмонлар ва Ер хазиналари ёлғиз Аллоҳникидир. Лекин мунофиқлар (буни) англамаслар'}, {'chapter': 63, 'verse': 8, 'text': 'يَقُولُونَ لَئِن رَّجَعۡنَآ إِلَى ٱلۡمَدِينَةِ لَيُخۡرِجَنَّ ٱلۡأَعَزُّ مِنۡهَا ٱلۡأَذَلَّۚ وَلِلَّهِ ٱلۡعِزَّةُ وَلِرَسُولِهِۦ وَلِلۡمُؤۡمِنِينَ وَلَٰكِنَّ ٱلۡمُنَٰفِقِينَ لَا يَعۡلَمُونَ\nУлар: «Қасамки, агар Мадинага қайтсак, албатта кучлилар кучсизларни ундан қувиб чиқарур», дерлар. Ҳолбуки, куч-қудрат Аллоҳники, Унинг пайғамбариники ва мўминларникидир. Лекин мунофиқлар (буни) билмаслар'}, {'chapter': 63, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُلۡهِكُمۡ أَمۡوَٰلُكُمۡ وَلَآ أَوۡلَٰدُكُمۡ عَن ذِكۡرِ ٱللَّهِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ\nЭй мўминлар, на мол-дунёларингиз ва на бола-чақаларингиз сизларни Аллоҳнинг зикридан (яъни, Аллоҳга ибодат қилишдан) юз ўгиртириб қўймасин! Кимки шундай қилса, бас, ана ўшалар зиён кўргувчи кимсалардир'}, {'chapter': 63, 'verse': 10, 'text': 'وَأَنفِقُواْ مِن مَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ أَحَدَكُمُ ٱلۡمَوۡتُ فَيَقُولَ رَبِّ لَوۡلَآ أَخَّرۡتَنِيٓ إِلَىٰٓ أَجَلٖ قَرِيبٖ فَأَصَّدَّقَ وَأَكُن مِّنَ ٱلصَّـٰلِحِينَ\nСизларнинг (ҳар) бирингизга ўлим келиб, у: «Парвардигорим, мени озгина муддатга (ҳаётда) қолдирсанг, мен хайр-садақа қилиб, солиҳ (банда)лардан бўлсам», деб қолишдан илгари, Биз сизларга ризқ қилиб берга нарсалардан инфоқ-эҳсон қилингиз'}, {'chapter': 63, 'verse': 11, 'text': 'وَلَن يُؤَخِّرَ ٱللَّهُ نَفۡسًا إِذَا جَآءَ أَجَلُهَاۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ\nАллоҳ бирон жонни ажал келган вақтида (вафот этдирмасдан) қолдирмас. Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 64, 'verse': 1, 'text': 'يُسَبِّحُ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ لَهُ ٱلۡمُلۡكُ وَلَهُ ٱلۡحَمۡدُۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\nОсмонлардаги ва Ердаги бор нарса Аллоҳга тасбеҳ айтур. Эгалик — подшоҳлик ёлғиз Уникидир ва ҳамду сано ёлғиз Унинг учундир. У барча нарсага қодирдир'}, {'chapter': 64, 'verse': 2, 'text': 'هُوَ ٱلَّذِي خَلَقَكُمۡ فَمِنكُمۡ كَافِرٞ وَمِنكُم مُّؤۡمِنٞۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ\nУ сизларни яратган Зотдир. Бас, сизлардан (айримларингиз) кофир, сизлардан (айримларингиз) мўминдир. Аллоҳ қилаётган амалларингизни кўриб тургувчидир'}, {'chapter': 64, 'verse': 3, 'text': 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡۖ وَإِلَيۡهِ ٱلۡمَصِيرُ\nУ осмонлар ва Ерни Ҳақ (қонун) билан яратди ва сизларга сурат-шакл берди — суратларингизни гўзал қилди. Ёлғиз Унга қайтиш бордир'}, {'chapter': 64, 'verse': 4, 'text': 'يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَيَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَۚ وَٱللَّهُ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\nУ осмонлар ва Ердаги бор нарсани билур ва У сизлар яширадиган нарсани ҳам, ошкор қиладиган нарсан ҳам билур. Аллоҳ диллардаги сирларни билгувчидир'}, {'chapter': 64, 'verse': 5, 'text': 'أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَبۡلُ فَذَاقُواْ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ\n(Эй Макка кофирлари), сизларга аввалги кофир бўлишиб, сўнг (ҳаёти дунёдалик пайтларидаёқ) ўз ишларининг зиёнини тотган кимсаларнинг хабари келдими? Яна улар учун (Охиратдаги) аламли азоб ҳам бордир'}, {'chapter': 64, 'verse': 6, 'text': 'ذَٰلِكَ بِأَنَّهُۥ كَانَت تَّأۡتِيهِمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَقَالُوٓاْ أَبَشَرٞ يَهۡدُونَنَا فَكَفَرُواْ وَتَوَلَّواْۖ وَّٱسۡتَغۡنَى ٱللَّهُۚ وَٱللَّهُ غَنِيٌّ حَمِيدٞ\nБунга сабаб, уларга пайғамбарлари (ўзларининг ҳақ пайғамбар эканликларини билдирувчи) ҳужжатлар билан келганларида улар: «Бизларни (ўзимизга ўхшаган) башар (авлодлари) ҳидоят қилурларми?!», дейишиб, (у пайғамбарларга) кофир бўлганлари ва (иймондан) юз ўгириб кетганларидир, (шунда) Аллоҳ (уларни ҳалок қилиш билан Ўзи уларнинг иймонларидан) беҳожат эканлигини кўрсатди. Аллоҳ беҳожат ва ҳамду санога лойиқ Зотдир'}, {'chapter': 64, 'verse': 7, 'text': 'زَعَمَ ٱلَّذِينَ كَفَرُوٓاْ أَن لَّن يُبۡعَثُواْۚ قُلۡ بَلَىٰ وَرَبِّي لَتُبۡعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلۡتُمۡۚ وَذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ\nКофир бўлган кимсалар ўзларининг ҳеч қачон қайта тирилмасликларини гумон-даъво қилдилар. (Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «Йўқ! Парвардигоримга қасамки, албатта қайта тирилурсизлар, сўнгра албатта сизларга қилган амалларингизнинг хабари берилур. Бу Аллоҳга осондир»'}, {'chapter': 64, 'verse': 8, 'text': 'فَـَٔامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَٱلنُّورِ ٱلَّذِيٓ أَنزَلۡنَاۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ\nБас, сизлар Аллоҳга, Унинг пайғамбарига ва Биз нозил қилган Нурга — Қуръонга иймон келтиринглар! Аллоҳ қилаётган амалларингиздан хабардордир'}, {'chapter': 64, 'verse': 9, 'text': 'يَوۡمَ يَجۡمَعُكُمۡ لِيَوۡمِ ٱلۡجَمۡعِۖ ذَٰلِكَ يَوۡمُ ٱلتَّغَابُنِۗ وَمَن يُؤۡمِنۢ بِٱللَّهِ وَيَعۡمَلۡ صَٰلِحٗا يُكَفِّرۡ عَنۡهُ سَيِّـَٔاتِهِۦ وَيُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ\n(Аллоҳ) сизларни тўплайдиган тўпланиш Кунини (эслангиз)! Бу (одамлар) бир-бирларини «алдаб-зиён етказадиган» Кундир (яъни, ҳаёти дунёда иймон эгаларини алдаб-зиён етказиб юрган кофирлар у Кунда дўзах азобига дучор қилиниш билан ўзларининг алданган кимсалар эканликларини билиб олурлар). Ким Аллоҳга иймон келтирса ва яхши амал қилса, (Аллоҳ) унинг ёмонлик-гуноҳларини ўчирур ва уни остидан дарёлар оқиб турадиган, (барча жаннат аҳли) абадул-абад қоладиган жаннатларга киритур. Бу эса улуғ бахтдир'}, {'chapter': 64, 'verse': 10, 'text': 'وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِ خَٰلِدِينَ فِيهَاۖ وَبِئۡسَ ٱلۡمَصِيرُ\nКофир бўлган ва Бизнинг оятларимизни ёлғон деган кимсалар — ана ўшалар дўзах эгалари бўлиб, ўша жойда мангу қолгувчидирлар. Нақадар ёмон оқибат (бу)'}, {'chapter': 64, 'verse': 11, 'text': 'مَآ أَصَابَ مِن مُّصِيبَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ وَمَن يُؤۡمِنۢ بِٱللَّهِ يَهۡدِ قَلۡبَهُۥۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ\n(Бирон кимсага) бирон мусибат етмас, магар Аллоҳнинг изни-иродаси билангина (етур). Ким Аллоҳга иймон келтирса, У зот унинг қалбини (Тўғри Йўлга) ҳидоят қилур. Аллоҳ барча нарсани билгувчидир'}, {'chapter': 64, 'verse': 12, 'text': 'وَأَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۚ فَإِن تَوَلَّيۡتُمۡ فَإِنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَٰغُ ٱلۡمُبِينُ\nАллоҳга итоат этингиз ва пайғамбарга бўйсунингиз! Энди агар (итоатдан) бош тортсангаз, у ҳолда (билингизки), пайғамбаримиз зиммасида фақат (Бизнинг ваҳийларимизни сизларга) очиқ-равшан қилиб етказиш бор, холос'}, {'chapter': 64, 'verse': 13, 'text': 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ\nАллоҳ — ҳеч қандай Аллоҳ йўқ, фақат Унинг Ўзи бордир. Бас, мўминлар ёлғиз Аллоҳга таваккул қилсинлар — суянсинлар'}, {'chapter': 64, 'verse': 14, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ مِنۡ أَزۡوَٰجِكُمۡ وَأَوۡلَٰدِكُمۡ عَدُوّٗا لَّكُمۡ فَٱحۡذَرُوهُمۡۚ وَإِن تَعۡفُواْ وَتَصۡفَحُواْ وَتَغۡفِرُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ\nЭй мўминлар, албатта жуфтларингиз ва болаларингиздан сизлар учун душман бўлганлари ҳам бордир. Бас, улардан эҳтиёт бўлинглар! Агар сизлар уларни афв этсанглар, кечириб юборсанглар ва мағфират қилсанглар, у ҳолда албатта Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 64, 'verse': 15, 'text': 'إِنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞۚ وَٱللَّهُ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ\nСизларнинг мол-мулкларингиз ва бола-чақаларингиз фақат бир фитна-имтиҳондир, холос. Улуғ ажр-мукофот эса ёлғиз Аллоҳнинг ҳузуридадир'}, {'chapter': 64, 'verse': 16, 'text': 'فَٱتَّقُواْ ٱللَّهَ مَا ٱسۡتَطَعۡتُمۡ وَٱسۡمَعُواْ وَأَطِيعُواْ وَأَنفِقُواْ خَيۡرٗا لِّأَنفُسِكُمۡۗ وَمَن يُوقَ شُحَّ نَفۡسِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ\nБас, кучларингиз етганича Аллоҳдан қўрқинглар, (ўзларингизга қилинаётган панд-насиҳатга) қулоқ тутинглар ва итоат этинглар ҳамда инфоқ-эҳсон қилинглар, (мана шу) ўзларингиз учун яхшироқ бўлур. Ким ўз нафсининг бахиллигидан сақлана олса, бас, ана ўшалар нажот топгувчидирлар'}, {'chapter': 64, 'verse': 17, 'text': 'إِن تُقۡرِضُواْ ٱللَّهَ قَرۡضًا حَسَنٗا يُضَٰعِفۡهُ لَكُمۡ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ شَكُورٌ حَلِيمٌ\nАгар сизлар Аллоҳга қарзи-ҳасана берсангизлар (яъни, Унинг мискин-бечора бандаларига инфоқ-эҳсон қилсангизлар) У зот сизларга бир неча баробар қилиб қайтарур ва сизларни(нг гуноҳларингизни) мағфират қилур. Аллоҳ ўта шукр қилгувчи (яъни, озгина яхши амал учун кўп мукофот ато этгувчи) ва ҳалимдир'}, {'chapter': 64, 'verse': 18, 'text': 'عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡعَزِيزُ ٱلۡحَكِيمُ\n(У) ғайбу шаҳодатни (яъни, барча яширин ва ошкор ишларни) билгувчи, қудрат ва ҳикмат Соҳибидир'}, {'chapter': 65, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحۡصُواْ ٱلۡعِدَّةَۖ وَٱتَّقُواْ ٱللَّهَ رَبَّكُمۡۖ لَا تُخۡرِجُوهُنَّ مِنۢ بُيُوتِهِنَّ وَلَا يَخۡرُجۡنَ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَتِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ لَا تَدۡرِي لَعَلَّ ٱللَّهَ يُحۡدِثُ بَعۡدَ ذَٰلِكَ أَمۡرٗا\nЭй Пайғамбар, қачон сизлар (яъни, мўминлар) аёлларингизни талоқ қилсангизлар, уларнинг иддаларида (яъни, поклик пайтларида) талоқ қилинглар ва идда сананглар! Парвардигорларингиз бўлмиш Аллоҳдан қўрқинглар! (То иддалари тугагунча) уларни (ўзлари яшаб турган) уйларидан ҳайдаб чиқарманглар ва улар ўзлари ҳам чиқиб кетмасинлар. Фақат улар очиқ фаҳш ишни (яъни, бузуқлик ёки беҳаёлик каби қилиқларни) қилсаларгина, (яшаб турган уйларидан ҳайдаб чиқарилурлар). Бу Аллоҳнинг қонун-чегараларидир. Кимки Аллоҳ чегараларидан тажовуз қилиб ўтса, аниқки, у ўзига зулм қилибди. Сиз билмассиз, эҳтимол Аллоҳ бу (талоқ)дан кейин бирон ишни пайдо қилар'}, {'chapter': 65, 'verse': 2, 'text': 'فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ فَارِقُوهُنَّ بِمَعۡرُوفٖ وَأَشۡهِدُواْ ذَوَيۡ عَدۡلٖ مِّنكُمۡ وَأَقِيمُواْ ٱلشَّهَٰدَةَ لِلَّهِۚ ذَٰلِكُمۡ يُوعَظُ بِهِۦ مَن كَانَ يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ وَمَن يَتَّقِ ٱللَّهَ يَجۡعَل لَّهُۥ مَخۡرَجٗا\nЭнди қачон (талоқ қилган аёлларингизнинг идда) муддатлари битиб қолса, бас, уларни яхшилик билан олиб қолинглар (яъни, қайта никоҳлларингизга олинглар) ёки яхшилик билан (яъни, ҳаққи маҳрларини бериб) ажрашинглар. Ва (ярашиб олиб қолаётган ёки ажрашиб кетаётган чоғларингда) ўзларингиздан (яъни, мусулмонлардан) бўлган икки адолат соҳибини гувоҳ қилинглар ва (гувоҳ бўлгувчилар) гувоҳликни Аллоҳ учун тўкис адо қилинглар! Бу (ҳукм)дан фақат Аллоҳга ва Охират Кунига иймон келтиргувчи бўлган киши панд-ибрат олур. Ким Аллоҳдан қўрқса (яъни, юқорида мазкур бўлган талоқ қилиш қонун-қоидаларига Аллоҳдан қўрққани учун риоя этса), У зот унинг учун (барча ғам-кулфатлардан) чиқар йўлни (пайдо) қилур'}, {'chapter': 65, 'verse': 3, 'text': 'وَيَرۡزُقۡهُ مِنۡ حَيۡثُ لَا يَحۡتَسِبُۚ وَمَن يَتَوَكَّلۡ عَلَى ٱللَّهِ فَهُوَ حَسۡبُهُۥٓۚ إِنَّ ٱللَّهَ بَٰلِغُ أَمۡرِهِۦۚ قَدۡ جَعَلَ ٱللَّهُ لِكُلِّ شَيۡءٖ قَدۡرٗا\nВа уни ўзи ўйламаган томондан ризқлантирур. Ким Аллоҳга таваккул қилса, бас, (Аллоҳнинг) Ўзи унга етарлидир. Албатта Аллоҳ Ўзи (хоҳлаган) ишига етгувчидир. (Унинг хоҳишига қарши тургувчи кимса йўқдир). Дарҳақиқат, Аллоҳ барча нарса учун миқдор-ўлчов қилиб қўйгандир (яъни, ҳаётда рўй берадиган ҳар бир яхши-ёмон воқеа-ҳодиса ёлғиз Аллоҳ хоҳлаган — белгилаган вақт ва ўлчовда рўй беради)'}, {'chapter': 65, 'verse': 4, 'text': 'وَٱلَّـٰٓـِٔي يَئِسۡنَ مِنَ ٱلۡمَحِيضِ مِن نِّسَآئِكُمۡ إِنِ ٱرۡتَبۡتُمۡ فَعِدَّتُهُنَّ ثَلَٰثَةُ أَشۡهُرٖ وَٱلَّـٰٓـِٔي لَمۡ يَحِضۡنَۚ وَأُوْلَٰتُ ٱلۡأَحۡمَالِ أَجَلُهُنَّ أَن يَضَعۡنَ حَمۡلَهُنَّۚ وَمَن يَتَّقِ ٱللَّهَ يَجۡعَل لَّهُۥ مِنۡ أَمۡرِهِۦ يُسۡرٗا\nАёлларингиз орасидаги (кексайиб қолгани сабабли) ҳайз кўришдан умид узганлари, агар сизлар (уларнинг иддалари қанча давом этиши хусусида) шубҳалансангизлар, бас, (билингиз-ки), уларнинг иддалари уч ойдир, яна (ҳали-ҳануз) ҳайз кўрмаган (аёллар)нинг (иддалари ҳам уч ойдир). Ҳомиладор (аёл)ларнинг (идда) муддатлари ҳомилаларини қўйишлари (яъни, кўз ёришларидир). Ким Аллоҳдан қўрқса, (Аллоҳ) унинг ишини осон қилур'}, {'chapter': 65, 'verse': 5, 'text': 'ذَٰلِكَ أَمۡرُ ٱللَّهِ أَنزَلَهُۥٓ إِلَيۡكُمۡۚ وَمَن يَتَّقِ ٱللَّهَ يُكَفِّرۡ عَنۡهُ سَيِّـَٔاتِهِۦ وَيُعۡظِمۡ لَهُۥٓ أَجۡرًا\nБу Аллоҳнинг сизларга нозил қилган ҳукмидир. Ким Аллоҳдан қўрқса, (Аллоҳ) унинг ёмонлик-гуноҳларини ўчирур ва унинг ажр-мукофотини улуғ қилур'}, {'chapter': 65, 'verse': 6, 'text': 'أَسۡكِنُوهُنَّ مِنۡ حَيۡثُ سَكَنتُم مِّن وُجۡدِكُمۡ وَلَا تُضَآرُّوهُنَّ لِتُضَيِّقُواْ عَلَيۡهِنَّۚ وَإِن كُنَّ أُوْلَٰتِ حَمۡلٖ فَأَنفِقُواْ عَلَيۡهِنَّ حَتَّىٰ يَضَعۡنَ حَمۡلَهُنَّۚ فَإِنۡ أَرۡضَعۡنَ لَكُمۡ فَـَٔاتُوهُنَّ أُجُورَهُنَّ وَأۡتَمِرُواْ بَيۡنَكُم بِمَعۡرُوفٖۖ وَإِن تَعَاسَرۡتُمۡ فَسَتُرۡضِعُ لَهُۥٓ أُخۡرَىٰ\nУларга (яъни, талоқ қилиниб, идда сақлаётган аёлларингизга) ўзларингиз маскан тутган — қудратингиз етган жойдан маскан беринглар ва уларни (турган жойларидан) қисиб чиқариш учун уларга зиён етказманглар. Агар улар ҳомиладор бўлсалар, у ҳолда то ҳомилаларини қўйгунларича уларга нафақа бериб туринглар! Энди агар (талоқ қилган аёлларингиз) сизлар учун (бола) эмизсалар (яъни, ўзларининг сизлардан бўлган болаларини эмизсалар), у ҳолда уларнинг (эмизганликлари учун) ажр-ҳақларини беринглар! Ва (эмизиш, ҳамда унинг ҳаққи хусусида) ўзаро яхшилик билан келишиб олинглар! Агар (келиша олмасдан) танг ҳолга тушсангизлар, у ҳолда (ота) учун (боласини) бошқа (бир аёл) эмизур. (Яъни, ота боласини бошқа — бегона аёлга эмиздурур)'}, {'chapter': 65, 'verse': 7, 'text': 'لِيُنفِقۡ ذُو سَعَةٖ مِّن سَعَتِهِۦۖ وَمَن قُدِرَ عَلَيۡهِ رِزۡقُهُۥ فَلۡيُنفِقۡ مِمَّآ ءَاتَىٰهُ ٱللَّهُۚ لَا يُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا مَآ ءَاتَىٰهَاۚ سَيَجۡعَلُ ٱللَّهُ بَعۡدَ عُسۡرٖ يُسۡرٗا\nБой-бадавлат киши ўз бойлигидан (яъни, бойлигига ярашадиган) нафақа берсин. Кимнинг ризқи танг қилинган (камбағал) бўлса, у ҳолда Аллоҳ ўзига ато этган нарсадан (яъни, ўз ҳолига яраша) нафақа берсин! Аллоҳ ҳеч бир жонни Ўзи унга ато этган (ризқ-рўз)дан бошқа нарсага таклиф қилмас (яъни, ҳар бир инсон фақат Аллоҳ ўзига ато этган ризқдан инфоқ қилишга таклиф қилинур). Аллоҳ танглик-камбағаллиқдан кейин енгиллик-бойликни ҳам (пайдо) қилур'}, {'chapter': 65, 'verse': 8, 'text': 'وَكَأَيِّن مِّن قَرۡيَةٍ عَتَتۡ عَنۡ أَمۡرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبۡنَٰهَا حِسَابٗا شَدِيدٗا وَعَذَّبۡنَٰهَا عَذَابٗا نُّكۡرٗا\nҚанчадан-қанча қишлоқ-шаҳарлар борки, ўз Парвардигорлари ва Унинг пайғамбарлари амридан бош тортгандирлар, бас, Биз улардан қаттиқ ҳисоб-китоб олганмиз ва ёмон азоб билан азоблаганмиз'}, {'chapter': 65, 'verse': 9, 'text': 'فَذَاقَتۡ وَبَالَ أَمۡرِهَا وَكَانَ عَٰقِبَةُ أَمۡرِهَا خُسۡرًا\nБас, у (қишлоқ-шаҳарларнинг аҳли) ўз иш-қилмишларининг зиёнини тотдилар ва бу ишларининг оқибати зиён тортиш — ҳалокат бўлди'}, {'chapter': 65, 'verse': 10, 'text': 'أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابٗا شَدِيدٗاۖ فَٱتَّقُواْ ٱللَّهَ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ ٱلَّذِينَ ءَامَنُواْۚ قَدۡ أَنزَلَ ٱللَّهُ إِلَيۡكُمۡ ذِكۡرٗا\nАллоҳ улар учун (Охиратда ҳам) қаттиқ азобни тайёрлаб қўйгандир. Бас, эй иймон келтирган ақл эгалари, Аллоҳдан қўрқинглар! Дарвоқеъ, У зот сизларга Эслатма — Қуръонни нозил қилди'}, {'chapter': 65, 'verse': 11, 'text': 'رَّسُولٗا يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِ ٱللَّهِ مُبَيِّنَٰتٖ لِّيُخۡرِجَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَمَن يُؤۡمِنۢ بِٱللَّهِ وَيَعۡمَلۡ صَٰلِحٗا يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ قَدۡ أَحۡسَنَ ٱللَّهُ لَهُۥ رِزۡقًا\nИймон келтирган ва яхши амаллар қилган зотларни зулматлардан нурга чиқариш учун сизларга Аллоҳнинг очиқ-равшан оятларини тиловат қиладиган пайғамбарни (Муҳаммад алайҳис-салоту вас-саломни юборди). Ким Аллоҳга иймон келтирса ва яхши амал қилса, (Аллоҳ) уни остидан дарёлар оқиб турадиган, улар абадул-абад қоладиган жаннатларга киритур. Дарҳақиқат, Аллоҳ унинг учун гўзал ризқ (яъни, жаннатнинг туганмас ризқини ато) қилди'}, {'chapter': 65, 'verse': 12, 'text': 'ٱللَّهُ ٱلَّذِي خَلَقَ سَبۡعَ سَمَٰوَٰتٖ وَمِنَ ٱلۡأَرۡضِ مِثۡلَهُنَّۖ يَتَنَزَّلُ ٱلۡأَمۡرُ بَيۡنَهُنَّ لِتَعۡلَمُوٓاْ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ وَأَنَّ ٱللَّهَ قَدۡ أَحَاطَ بِكُلِّ شَيۡءٍ عِلۡمَۢا\nАллоҳ етти осмонни ва Ердан ҳам ўшаларнинг мислини (яъни, етти қават Ерни) яратган Зотдир. (Аллоҳнинг) амри фармони уларнинг (яъни, етти қават осмон ва етти қават Ернинг) ўртасига нозил бўлиб-тарқалиб турур. Токи сизлар Аллоҳнинг барча нарсага қодир эканлигини ва Аллоҳ барча нарсани (Ўз) билими билан иҳота қилиб олганини билишларингиз учун (У зот сизларга юқорида зикр қилинган нарсалар ҳақида хабар берди)'}, {'chapter': 66, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ لِمَ تُحَرِّمُ مَآ أَحَلَّ ٱللَّهُ لَكَۖ تَبۡتَغِي مَرۡضَاتَ أَزۡوَٰجِكَۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ\nЭй пайғамбар, нега сиз жуфтларингизнинг розилигини истаб Аллоҳ сиз учун ҳалол қилган нарсани ҳаром қилиб олурсиз?! Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 66, 'verse': 2, 'text': 'قَدۡ فَرَضَ ٱللَّهُ لَكُمۡ تَحِلَّةَ أَيۡمَٰنِكُمۡۚ وَٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ\nДарвоқеъ, Аллоҳ сизлар учун (маълум миқдорда каффорат-эваз тўлаб) қасамларингиздан қутулиб чиқишни буюрган-ку. Аллоҳ Хожаларингиздир. У билгувчидир ва ҳикмат Соҳибидир'}, {'chapter': 66, 'verse': 3, 'text': 'وَإِذۡ أَسَرَّ ٱلنَّبِيُّ إِلَىٰ بَعۡضِ أَزۡوَٰجِهِۦ حَدِيثٗا فَلَمَّا نَبَّأَتۡ بِهِۦ وَأَظۡهَرَهُ ٱللَّهُ عَلَيۡهِ عَرَّفَ بَعۡضَهُۥ وَأَعۡرَضَ عَنۢ بَعۡضٖۖ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتۡ مَنۡ أَنۢبَأَكَ هَٰذَاۖ قَالَ نَبَّأَنِيَ ٱلۡعَلِيمُ ٱلۡخَبِيرُ\nПайғамбар жуфтларидан бирига (яъни, Ҳафсага) бир сўзни пинҳона айтганини эсланг! Энди қачонки (Ҳафса) у (сир) ҳақида (Оишага) хабар бергач ва Аллоҳ (Жаброил фаришта воситасида Пайғамбарни Ҳафсанинг қилмишидан) воқиф қилгач, у (Ҳафсага ўзи воқиф бўлган нарсанинг) баъзисини билдирди ва баъзисидан юз ўгирди — билдирмади. Бас, қачонки (Пайғамбар Ҳафсага ўзи воқиф бўлган нарсанинг баъзиси ҳақида) хабар бергач, у: «Ким сизга бу хабарни берди?», деган эди, (Пайғамбар): «Менга Билгувчи ва Хабардор Зот хабар берди», деди'}, {'chapter': 66, 'verse': 4, 'text': 'إِن تَتُوبَآ إِلَى ٱللَّهِ فَقَدۡ صَغَتۡ قُلُوبُكُمَاۖ وَإِن تَظَٰهَرَا عَلَيۡهِ فَإِنَّ ٱللَّهَ هُوَ مَوۡلَىٰهُ وَجِبۡرِيلُ وَصَٰلِحُ ٱلۡمُؤۡمِنِينَۖ وَٱلۡمَلَـٰٓئِكَةُ بَعۡدَ ذَٰلِكَ ظَهِيرٌ\n(Эй Ҳафса, Оиша) агар сизлар (бу қилмишларингиз учун) Аллоҳга тавба қилсангизлар (ўзларингизга яхшироқ бўлур). Чунки сизларнинг дилларингиз (ҳақ йўлдан) тойиб кетди! (Аммо) агар сизлар (Пайғамбарнинг) зиёнига (яъни, у зот билан аёллари ўртасини бузадиган ишлар устида) бир-бирингизга ёрдам берсангизлар, у ҳолда шак-шубҳасиз, Аллоҳнинг Ўзи ҳам, Жаброил ҳам, аҳли солиҳ мўминлар ҳам унинг мададкоридирлар. Яна, булардан кейин фаришталар ҳам (Пайғамбарга) ёрдамчидир'}, {'chapter': 66, 'verse': 5, 'text': 'عَسَىٰ رَبُّهُۥٓ إِن طَلَّقَكُنَّ أَن يُبۡدِلَهُۥٓ أَزۡوَٰجًا خَيۡرٗا مِّنكُنَّ مُسۡلِمَٰتٖ مُّؤۡمِنَٰتٖ قَٰنِتَٰتٖ تَـٰٓئِبَٰتٍ عَٰبِدَٰتٖ سَـٰٓئِحَٰتٖ ثَيِّبَٰتٖ وَأَبۡكَارٗا\nЭҳтимол Парвардигори — агар у сизларни талоқ қилса — унга сизлардан яхшироқ жуфтларни муслима, мўмина (Аллоҳ ва Унинг пайғамбарига) итоат этгувчи, тавба-тазарру қилгувчи, обида (мудом Аллоҳнинг йўлида) кетгувчи жувон ва қизларни алмаштириб берур'}, {'chapter': 66, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ قُوٓاْ أَنفُسَكُمۡ وَأَهۡلِيكُمۡ نَارٗا وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُ عَلَيۡهَا مَلَـٰٓئِكَةٌ غِلَاظٞ شِدَادٞ لَّا يَعۡصُونَ ٱللَّهَ مَآ أَمَرَهُمۡ وَيَفۡعَلُونَ مَا يُؤۡمَرُونَ\nЭй мўминлар, сизлар ўзларингизни ва аҳли-оилаларингизни ўтини одамлар ва тошлар бўлган дўзахдан сақлангизки, у (дўзах) устида қаттиқдил ва қаттиққўл, Аллоҳ ўзларига буюрган нарсага итоатсизлик қилмайдиган, фақат ўзларига буюрилган нарсани қиладиган фаришталар турур'}, {'chapter': 66, 'verse': 7, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ كَفَرُواْ لَا تَعۡتَذِرُواْ ٱلۡيَوۡمَۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ\n(Кофирлар дўзахга киритилар эканлар, уларга дейилур): «Эй кофирлар, бугун узр айтмай қўя қолинглар, (чунки энди ҳар қандай узр-баҳона бефойдадир)! Сизлар фақат ўзларингиз қилиб ўтган амалларингиз билан жазоланмоқдасизлар'}, {'chapter': 66, 'verse': 8, 'text': 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ تُوبُوٓاْ إِلَى ٱللَّهِ تَوۡبَةٗ نَّصُوحًا عَسَىٰ رَبُّكُمۡ أَن يُكَفِّرَ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيُدۡخِلَكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ يَوۡمَ لَا يُخۡزِي ٱللَّهُ ٱلنَّبِيَّ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥۖ نُورُهُمۡ يَسۡعَىٰ بَيۡنَ أَيۡدِيهِمۡ وَبِأَيۡمَٰنِهِمۡ يَقُولُونَ رَبَّنَآ أَتۡمِمۡ لَنَا نُورَنَا وَٱغۡفِرۡ لَنَآۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ\nЭй мўминлар, Аллоҳга холис тавба қилинглар, шоядки, Парвардигорингиз сизларнинг ёмонлик-гуноҳларингизни ўчириб, остидан дарёлар оқиб турадиган жаннатларга киритур. У Кунда Аллоҳ Пайғамбарни ва у билан бирга иймон келтирган зотларни шарманда қилмас. Уларнинг нурлари олдиларида ва ўнг томонларида юрур. Улар: «Парвардигоро, Ўзинг бизларга нуримизни комил қилиб бергин ва бизларни мағфират қилгин. Албатта Сен барча нарсага қодирдирсан», дерлар'}, {'chapter': 66, 'verse': 9, 'text': 'يَـٰٓأَيُّهَا ٱلنَّبِيُّ جَٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَٰفِقِينَ وَٱغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ\nЭй Пайғамбар, сиз кофир ва мунофиқларга қарши курашинг ва уларга қаттиққўл бўлинг! Уларнинг жойлари жаҳаннамдир! Нақадар ёмон оқибат бу'}, {'chapter': 66, 'verse': 10, 'text': 'ضَرَبَ ٱللَّهُ مَثَلٗا لِّلَّذِينَ كَفَرُواْ ٱمۡرَأَتَ نُوحٖ وَٱمۡرَأَتَ لُوطٖۖ كَانَتَا تَحۡتَ عَبۡدَيۡنِ مِنۡ عِبَادِنَا صَٰلِحَيۡنِ فَخَانَتَاهُمَا فَلَمۡ يُغۡنِيَا عَنۡهُمَا مِنَ ٱللَّهِ شَيۡـٔٗا وَقِيلَ ٱدۡخُلَا ٱلنَّارَ مَعَ ٱلدَّـٰخِلِينَ\nАллоҳ кофир бўлган кимсалар (ва уларни мўминлар билан бирга ҳаёт кечиришлари ҳам Аллоҳнинг азобидан қутқара олмаслиги) ҳақида Нуҳнинг аёли ва Лутнинг аёлини мисол келтирди. У иккиси Бизнинг бандаларимиздан икки солиҳ банда қўл остида эдилар. Бас, у (аёл)лар (Нуҳ ва Лутга) хиёнат қилган эдилар, (Нуҳ ва Лут) Аллоҳ(нинг азоби)дан бирон нарсани у иккисидан дафъ қила олмадилар ва (уларга) «Киргувчилар билан бирга дўзахга киринглар!», дейилди'}, {'chapter': 66, 'verse': 11, 'text': 'وَضَرَبَ ٱللَّهُ مَثَلٗا لِّلَّذِينَ ءَامَنُواْ ٱمۡرَأَتَ فِرۡعَوۡنَ إِذۡ قَالَتۡ رَبِّ ٱبۡنِ لِي عِندَكَ بَيۡتٗا فِي ٱلۡجَنَّةِ وَنَجِّنِي مِن فِرۡعَوۡنَ وَعَمَلِهِۦ وَنَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ\nАллоҳ иймон келтирган зотлар (ва улар ўзлари ҳақиқий мўмин бўлсалар, яқин одамларининг кофир ҳолда бўлиши уларга зиён қилмаслиги) ҳақида Фиръавннинг аёлини мисол келтирди. Ўшанда (яъни, Фиръавн у аёлнинг Мусо пайғамбарга иймон келтирганини билиб қолгач, азоблаган чоғида) у: «Парвардигорим, Ўзинг мен учун ҳузурингда — жаннатда бир уй бино қилгин, менга Фиръавн ва унинг қилмишидан нажот бергин ва менга бу золим қавмдан нажот бергин», деди'}, {'chapter': 66, 'verse': 12, 'text': 'وَمَرۡيَمَ ٱبۡنَتَ عِمۡرَٰنَ ٱلَّتِيٓ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتۡ بِكَلِمَٰتِ رَبِّهَا وَكُتُبِهِۦ وَكَانَتۡ مِنَ ٱلۡقَٰنِتِينَ\nЯна (иймон келтирган зотлар ҳақида) ўз номусини сақлаган аёлни — Марям бинти Имронни (мисол келтирди). Бас, Биз Ўз тарафимиздан бўлган руҳни унга пуфладик (ва у Ийсога ҳомиладор бўлди). У Парвардигорининг сўзларини ва китобларини тасдиқ этди ҳамда (Аллоҳнинг амрига) итоат этгувчилардан бўлди'}, {'chapter': 67, 'verse': 1, 'text': 'تَبَٰرَكَ ٱلَّذِي بِيَدِهِ ٱلۡمُلۡكُ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ\n(Барча оламлар) подшоҳлиги Ўз қўлида бўлган Зот — Аллоҳ баракотли-буюкдир. У барча нарсага қодирдир'}, {'chapter': 67, 'verse': 2, 'text': 'ٱلَّذِي خَلَقَ ٱلۡمَوۡتَ وَٱلۡحَيَوٰةَ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡغَفُورُ\n(Эй инсонлар, У) сизларнинг қайсиларингиз чиройлироқ-яхшироқ амал қилгувчи эканлигингизни имтиҳон қилиш учун ўлим ва ҳаётни яраттан Зотдир. У қудратли ва мағфиратлидир'}, {'chapter': 67, 'verse': 3, 'text': 'ٱلَّذِي خَلَقَ سَبۡعَ سَمَٰوَٰتٖ طِبَاقٗاۖ مَّا تَرَىٰ فِي خَلۡقِ ٱلرَّحۡمَٰنِ مِن تَفَٰوُتٖۖ فَٱرۡجِعِ ٱلۡبَصَرَ هَلۡ تَرَىٰ مِن فُطُورٖ\n(У) етти осмонни устма-уст қилиб яратган Зотдирки, сиз У Раҳмоннинг (Меҳрибон зотнинг) яратишида бирон тафовут-нуқсонни кўрмассиз. Энди кўзингизни яна (самога) қайтаринг-чи, (унда) бирон футур-ёриқни кўрармикансиз'}, {'chapter': 67, 'verse': 4, 'text': 'ثُمَّ ٱرۡجِعِ ٱلۡبَصَرَ كَرَّتَيۡنِ يَنقَلِبۡ إِلَيۡكَ ٱلۡبَصَرُ خَاسِئٗا وَهُوَ حَسِيرٞ\nСўнгра кўзингизни қайта-қайта (самога) қайтаринг, кўзингиз сизга чарчаб, ҳолдан тойган ҳолда қайтур (аммо Аллоҳ таоло яратган осмонлардан бирон айб-нуқсон топа олмас)'}, {'chapter': 67, 'verse': 5, 'text': 'وَلَقَدۡ زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَجَعَلۡنَٰهَا رُجُومٗا لِّلشَّيَٰطِينِۖ وَأَعۡتَدۡنَا لَهُمۡ عَذَابَ ٱلسَّعِيرِ\nДарҳақиқат, Биз энг яқин осмонни чироқлар (юлдузлар) билан безадик ва уларни шайтонларга отиладиган тошлар қилиб қўйдик. Биз улар (шайтонлар) учун (Охиратдаги) олов-дўзах азобини ҳам тайёрлаб қўйгандирмиз'}, {'chapter': 67, 'verse': 6, 'text': 'وَلِلَّذِينَ كَفَرُواْ بِرَبِّهِمۡ عَذَابُ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمَصِيرُ\nПарвардигорларига кофир бўлган кимсалар учун жаҳаннам азоби бордир. Нақадар ёмон оқибат бу'}, {'chapter': 67, 'verse': 7, 'text': 'إِذَآ أُلۡقُواْ فِيهَا سَمِعُواْ لَهَا شَهِيقٗا وَهِيَ تَفُورُ\nҚачон улар (жаҳаннамга) ташланганларида қайнаб турган (жаҳаннамнинг худди эшак ҳанграшидек) бир ўкирик-фарёдини эшитурлар'}, {'chapter': 67, 'verse': 8, 'text': 'تَكَادُ تَمَيَّزُ مِنَ ٱلۡغَيۡظِۖ كُلَّمَآ أُلۡقِيَ فِيهَا فَوۡجٞ سَأَلَهُمۡ خَزَنَتُهَآ أَلَمۡ يَأۡتِكُمۡ نَذِيرٞ\nУ ғазабдан бўлиниб-парчаланиб кетгудек бўлур. Ҳар қачон унга бир тўда (кофир) ташланганида унинг қўриқчилари улардан (кофирлардан): «Сизларга (ҳаёти дунёда) бирон огоҳлантиргувчи — пайғамбар келмаганмиди?», деб сўраганида'}, {'chapter': 67, 'verse': 9, 'text': 'قَالُواْ بَلَىٰ قَدۡ جَآءَنَا نَذِيرٞ فَكَذَّبۡنَا وَقُلۡنَا مَا نَزَّلَ ٱللَّهُ مِن شَيۡءٍ إِنۡ أَنتُمۡ إِلَّا فِي ضَلَٰلٖ كَبِيرٖ\nУлар дерлар: «Ҳа, дарҳақиқат, бизларга огоҳлантиргувчи келган эди, (лекин бизлар уни) ёлғончи қилганмиз ва: «Аллоҳ (ҳеч кимга) ҳеч нарса нозил қилган эмас, сизлар фақат катта залолат-гумроҳликдадирсизлар», деганмиз»'}, {'chapter': 67, 'verse': 10, 'text': 'وَقَالُواْ لَوۡ كُنَّا نَسۡمَعُ أَوۡ نَعۡقِلُ مَا كُنَّا فِيٓ أَصۡحَٰبِ ٱلسَّعِيرِ\nУлар (кофирлар) яна: «Агар бизлар (огоҳлантиргувчи пайғамбарнинг сўзларини) тинглаб, ақл юргизувчи бўлганимизда, дўзах эгалари қаторида бўлмас эдик», дерлар'}, {'chapter': 67, 'verse': 11, 'text': 'فَٱعۡتَرَفُواْ بِذَنۢبِهِمۡ فَسُحۡقٗا لِّأَصۡحَٰبِ ٱلسَّعِيرِ\nМана улар ўз гуноҳларини эътироф этдилар! Энди у дўзах эгаларига ҳалокат бўлгай'}, {'chapter': 67, 'verse': 12, 'text': 'إِنَّ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ\nАлбатта ғайбдаги (кўзларига кўринмайдиган) Парвардигорларидан қўрқадиган зотлар учун мағфират ва катта ажр-мукофот бордир'}, {'chapter': 67, 'verse': 13, 'text': 'وَأَسِرُّواْ قَوۡلَكُمۡ أَوِ ٱجۡهَرُواْ بِهِۦٓۖ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ\n(Эй инсонлар), сизлар сўзларингизни яширинглар ёки ошкора қилинглар (ҳар ҳолда Аллоҳ билиб турур). Албатта У диллардаги сирларни билгувчидир'}, {'chapter': 67, 'verse': 14, 'text': 'أَلَا يَعۡلَمُ مَنۡ خَلَقَ وَهُوَ ٱللَّطِيفُ ٱلۡخَبِيرُ\n(Ахир) яратган Зот (Ўзи йўқдан бор қилган нарсаларни) билмасми?! У меҳрибон ва (ҳар нарсадан) Огоҳдир'}, {'chapter': 67, 'verse': 15, 'text': 'هُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ ذَلُولٗا فَٱمۡشُواْ فِي مَنَاكِبِهَا وَكُلُواْ مِن رِّزۡقِهِۦۖ وَإِلَيۡهِ ٱلنُّشُورُ\nУ (Аллоҳ) сизлар учун Ерни ҳокисор-бўйсунгувчи қилиб қўйган Зотдир. Бас, (ер)нинг ҳар томонида (сайр-саёҳат қилиб ё тижорат билан, ёки деҳқончиликни касб қилиб) юраверинглар ва (Аллоҳнинг берган) ризқ-рўзидан енглар. Ёлғиз Унинг ҳузурига тирилиб чиқиш — қайтиш бордир'}, {'chapter': 67, 'verse': 16, 'text': 'ءَأَمِنتُم مَّن فِي ٱلسَّمَآءِ أَن يَخۡسِفَ بِكُمُ ٱلۡأَرۡضَ فَإِذَا هِيَ تَمُورُ\n(Эй Макка кофирлари,) ё сизлар (кофирлигингизда оёқ тираб тураверсангизлар,) осмондаги Зот сизларни Ерга юттириб юборишидан, бас, баногоҳ (Ер) титроққа тушиб, (сизларни босиб) қолишидан хотиржаммисизлар (қўрқмайсизларми)'}, {'chapter': 67, 'verse': 17, 'text': 'أَمۡ أَمِنتُم مَّن فِي ٱلسَّمَآءِ أَن يُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗاۖ فَسَتَعۡلَمُونَ كَيۡفَ نَذِيرِ\nЁки сизлар осмондаги Зот устингизга тош ёғдиришидан хотиржаммисизлар?! Ҳали (азобга гирифтор қилинганларингизда) Менинг огоҳлантиришим қандай эканлигини билиб олажаксизлар'}, {'chapter': 67, 'verse': 18, 'text': 'وَلَقَدۡ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَكَيۡفَ كَانَ نَكِيرِ\nДарвоқеъ, улардан аввалги кимсалар ҳам (ўз пайғамбарларини) ёлғончи қилгандилар. Бас, Менинг инкорим — азобим қандоқ бўлди'}, {'chapter': 67, 'verse': 19, 'text': 'أَوَلَمۡ يَرَوۡاْ إِلَى ٱلطَّيۡرِ فَوۡقَهُمۡ صَـٰٓفَّـٰتٖ وَيَقۡبِضۡنَۚ مَا يُمۡسِكُهُنَّ إِلَّا ٱلرَّحۡمَٰنُۚ إِنَّهُۥ بِكُلِّ شَيۡءِۭ بَصِيرٌ\nУлар устларида (қанотларини) ёйгувчи бўлган ва йиққан ҳолда (учиб юрган) қушларни кўрмадиларми?! У (қуш)ларни ёлғиз Раҳмонгина (самода) ушлаб турар! Албатта У барча нарсани кўриб тургувчидир'}, {'chapter': 67, 'verse': 20, 'text': 'أَمَّنۡ هَٰذَا ٱلَّذِي هُوَ جُندٞ لَّكُمۡ يَنصُرُكُم مِّن دُونِ ٱلرَّحۡمَٰنِۚ إِنِ ٱلۡكَٰفِرُونَ إِلَّا فِي غُرُورٍ\n(Эй мушриклар), Раҳмондан ўзга сизларга ёрдам берадиган, сизлар учун қўшин-ёрдамчи бўлган ўша зот ким ўзи?! (Аллоҳдан ўзга бирон мададкор йўқдир). Кофирлар эса фақат ғурур-алданишдадирлар. (Шунинг учун улар ўзлари сиғинаётган бут-санамларга уларни Аллоҳнинг азобидан қутқара олади, деб ҳисоблайдилар)'}, {'chapter': 67, 'verse': 21, 'text': 'أَمَّنۡ هَٰذَا ٱلَّذِي يَرۡزُقُكُمۡ إِنۡ أَمۡسَكَ رِزۡقَهُۥۚ بَل لَّجُّواْ فِي عُتُوّٖ وَنُفُورٍ\nАгар (Раҳмон) Ўз ризқини ушлаб-тўхтатиб қўйса, сизларга ризқ-рўз берадиган ўша зот ким ўзи?! (Аллоҳдан ўзга бирон ризқ-рўз бергувчи йўқдир). Йўқ, улар (кофирлар) катта кетишда ва (ҳақдан) йироқлашишда давом этдилар'}, {'chapter': 67, 'verse': 22, 'text': 'أَفَمَن يَمۡشِي مُكِبًّا عَلَىٰ وَجۡهِهِۦٓ أَهۡدَىٰٓ أَمَّن يَمۡشِي سَوِيًّا عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ\nАхир юз тубан (яъни, оёғининг остидан ўзга ёққа қарамасдан) юрадиган кимса Ҳақ йўлни топгувчироқми ёки қадди рост ҳолида Тўғри Йўлда юрадиган кишими'}, {'chapter': 67, 'verse': 23, 'text': 'قُلۡ هُوَ ٱلَّذِيٓ أَنشَأَكُمۡ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, у кофирларга) айтинг: «У (Аллоҳ) сизларни (йўқдан) пайдо қилган ва сизлар учун қулоқ-кўзлар ва дилларни (ато) қилган Зотдир. Сизлар эса камдан-кам шукр қилурсизлар»'}, {'chapter': 67, 'verse': 24, 'text': 'قُلۡ هُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ\nАйтинг: «У сизларни Ер юзида яратиб қўйган Зотдир. Сизлар (Қиёмат Кунида) ёлғиз Унга тўпланурсизлар»'}, {'chapter': 67, 'verse': 25, 'text': 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ\nУлар: «Агар ростгўй бўлсангизлар (айтинглар-чи), ушбу ваъда (қилинган Қиёмат Куни) қачон бўлади?», дерлар'}, {'chapter': 67, 'verse': 26, 'text': 'قُلۡ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом, уларга) айтинг: «(Қиёмат қачон қойим бўлиши ҳақидаги) билим ёлғиз Аллоҳнинг ҳузуридадир. Мен фақат бир очиқ огоҳлантиргувчидирман, холос»'}, {'chapter': 67, 'verse': 27, 'text': 'فَلَمَّا رَأَوۡهُ زُلۡفَةٗ سِيٓـَٔتۡ وُجُوهُ ٱلَّذِينَ كَفَرُواْ وَقِيلَ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تَدَّعُونَ\nБас, қачонки улар (Қиёмат Кунида ваъда қилинган азобни ўзларига) яқин ҳолда кўришгач, у кофирларнинг юзлари қорайиб кетур ва (уларга): «Мана шу сизлар истаган нарсадир», дейилур'}, {'chapter': 67, 'verse': 28, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَهۡلَكَنِيَ ٱللَّهُ وَمَن مَّعِيَ أَوۡ رَحِمَنَا فَمَن يُجِيرُ ٱلۡكَٰفِرِينَ مِنۡ عَذَابٍ أَلِيمٖ\nЭй Муҳаммад алайҳис-салоту вас-салом, у кофирларга айтинг: «Хабар берингиз-чи, агар Аллоҳ мени ва мен билан бирга бўлган кишиларни ҳалок қилса ёки (умримизни узун қилиш билан) бизларга раҳм-шафқат қилса, Ўзининг изн-ихтиёридадир. Аммо энди кофирларни (яъни, сизларни) ким аламли азобдан ҳимоя қилур'}, {'chapter': 67, 'verse': 29, 'text': 'قُلۡ هُوَ ٱلرَّحۡمَٰنُ ءَامَنَّا بِهِۦ وَعَلَيۡهِ تَوَكَّلۡنَاۖ فَسَتَعۡلَمُونَ مَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ\nАйтинг: «У (Аллоҳ) Раҳмон — Меҳрибондир. Бизлар Унга иймон келтирдик ва ёлғиз Унинг Ўзига таваккул қилдик — суяндик. Ҳали сизлар ким очиқ залолатда эканлигини билиб олажаксизлар!»'}, {'chapter': 67, 'verse': 30, 'text': 'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَصۡبَحَ مَآؤُكُمۡ غَوۡرٗا فَمَن يَأۡتِيكُم بِمَآءٖ مَّعِينِۭ\n(Эй Муҳаммад алайҳис-салоту вас-салом, у мушрикларга) айтинг: «Хабар берингиз-чи, агар (баногоҳ ичар) сувларингиз (ер тубига) сингиб кетар бўлса, у ҳолда ким сизларга оқар сув келтира олур?! (Жонсиз бутларингизми?! Бас, нега у бефойда-бежон бутларни Меҳрибон Аллоҳга шерик қилиб олдингизлар?!)»'}, {'chapter': 68, 'verse': 1, 'text': 'نٓۚ وَٱلۡقَلَمِ وَمَا يَسۡطُرُونَ\nНун. Қалам ва (у билан фаришталар) битадиган битикларга қасамки'}, {'chapter': 68, 'verse': 2, 'text': 'مَآ أَنتَ بِنِعۡمَةِ رَبِّكَ بِمَجۡنُونٖ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз Парвардигорингизнинг марҳамати билан мажнун эмасдирсиз'}, {'chapter': 68, 'verse': 3, 'text': 'وَإِنَّ لَكَ لَأَجۡرًا غَيۡرَ مَمۡنُونٖ\nАлбатта сиз учун (кофирларнинг бундай озорларига сабр-тоқат қилганингиз сабабли) битмас-туганмас ажр мукофот бордир'}, {'chapter': 68, 'verse': 4, 'text': 'وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٖ\nАлбатта сиз улуғ Хулқ устидадирсиз'}, {'chapter': 68, 'verse': 5, 'text': 'فَسَتُبۡصِرُ وَيُبۡصِرُونَ\nҲали яқинда сиз ҳам кўражаксиз, улар ҳам кўражаклар'}, {'chapter': 68, 'verse': 6, 'text': 'بِأَييِّكُمُ ٱلۡمَفۡتُونُ\nҚайсиларингиз (яъни, сизми ёки уларми) мафтун-мажнун эканликларингизни'}, {'chapter': 68, 'verse': 7, 'text': 'إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ\nАлбатта Парвардигорингизнинг Ўзи Унинг йўлидан озган кимсаларни жуда яхши билгувчидир ва У ҳидоят топгувчиларни ҳам жуда яхши билгувчидир'}, {'chapter': 68, 'verse': 8, 'text': 'فَلَا تُطِعِ ٱلۡمُكَذِّبِينَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сизнинг ҳақ пайғамбарлигингизни ва Қуръонни «ёлғон» дегувчи кимсаларга итоат этманг'}, {'chapter': 68, 'verse': 9, 'text': 'وَدُّواْ لَوۡ تُدۡهِنُ فَيُدۡهِنُونَ\nУлар сизнинг (ўзларига) кўнгилчанлик — муроса қилишингизни истарлар, шунда улар ҳам (сизга) кўнгилчанлик қилурлар. (Лекин сиз уларнинг бу истакларига бўйинсунманг)'}, {'chapter': 68, 'verse': 10, 'text': 'وَلَا تُطِعۡ كُلَّ حَلَّافٖ مَّهِينٍ\n(Эй Муҳаммад алайҳис-салоту вас-салом), яна сиз ҳар бир тубан қасамхўр'}, {'chapter': 68, 'verse': 11, 'text': 'هَمَّازٖ مَّشَّآءِۭ بِنَمِيمٖ\nғийбатчи-ю, гап ташувчи'}, {'chapter': 68, 'verse': 12, 'text': 'مَّنَّاعٖ لِّلۡخَيۡرِ مُعۡتَدٍ أَثِيمٍ\nяхшиликни манъ қилгувчи-бахил, тажовузкор, гуноҳга ботган'}, {'chapter': 68, 'verse': 13, 'text': 'عُتُلِّۭ بَعۡدَ ذَٰلِكَ زَنِيمٍ\nқўпол ва булардан ташқари бенасаб-ҳароми кимсага итоат этманг'}, {'chapter': 68, 'verse': 14, 'text': 'أَن كَانَ ذَا مَالٖ وَبَنِينَ\nУ мол-мулк ва ўғиллар эгаси бўлгани учун (ўзидан кетиб)'}, {'chapter': 68, 'verse': 15, 'text': 'إِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nқачон унга Бизнинг оятларимиз тиловат қилинса: «Булар аввалгиларнинг афсоналари-ку!», дер'}, {'chapter': 68, 'verse': 16, 'text': 'سَنَسِمُهُۥ عَلَى ٱلۡخُرۡطُومِ\nЯқинда Биз уни тумшуғидан тамғалаб қўяжакмиз'}, {'chapter': 68, 'verse': 17, 'text': 'إِنَّا بَلَوۡنَٰهُمۡ كَمَا بَلَوۡنَآ أَصۡحَٰبَ ٱلۡجَنَّةِ إِذۡ أَقۡسَمُواْ لَيَصۡرِمُنَّهَا مُصۡبِحِينَ\nДарҳақиҳат, Биз уларни (Макка аҳлини очарчилик ва қаҳатчилик билан) балоладик. Биз (илгари) боғ эгаларини ҳам худди шундай (маҳрумлик билан) балолаган эдик. Ўшанда улар албатта уни (яъни, боғнинг ҳосилини) эрта тонгда узиб олишга қасам ичган эдилар'}, {'chapter': 68, 'verse': 18, 'text': 'وَلَا يَسۡتَثۡنُونَ\n(Камбағал-мискинларга ҳақ-улуш) ажратмаган ҳолларида (узиб олишга қасам ичган эдилар)'}, {'chapter': 68, 'verse': 19, 'text': 'فَطَافَ عَلَيۡهَا طَآئِفٞ مِّن رَّبِّكَ وَهُمۡ نَآئِمُونَ\nБас, (тунда) улар уйқуда бўлган чоғларида у (боғ)нинг устида Парвардигорингиз томонидан бўлган айлангувчи (бало-олов) бир айланди-ю'}, {'chapter': 68, 'verse': 20, 'text': 'فَأَصۡبَحَتۡ كَٱلصَّرِيمِ\nу (боғнинг ҳосили) узилгандек бўлди-қолди (яъни, бутун боғ куйиб кул бўлди)'}, {'chapter': 68, 'verse': 21, 'text': 'فَتَنَادَوۡاْ مُصۡبِحِينَ\nУлар эса тонгда туришиб (тунда бўлган ҳодисадан бехабар бўлган ҳолларида) бир-бирларини шундай дея чақирдилар'}, {'chapter': 68, 'verse': 22, 'text': 'أَنِ ٱغۡدُواْ عَلَىٰ حَرۡثِكُمۡ إِن كُنتُمۡ صَٰرِمِينَ\n«Агар (ҳосилни) узмоқчи бўлсангизлар зироатгоҳларингизга юра қолинглар»'}, {'chapter': 68, 'verse': 23, 'text': 'فَٱنطَلَقُواْ وَهُمۡ يَتَخَٰفَتُونَ\nСўнг бир-бирларига пичирлашган ҳолларида жўнаб кетдилар'}, {'chapter': 68, 'verse': 24, 'text': 'أَن لَّا يَدۡخُلَنَّهَا ٱلۡيَوۡمَ عَلَيۡكُم مِّسۡكِينٞ\n«Бугун у (боғда ҳосил узаётган вақтда) устларингизга бирон мискин кириб қолмасин-да»'}, {'chapter': 68, 'verse': 25, 'text': 'وَغَدَوۡاْ عَلَىٰ حَرۡدٖ قَٰدِرِينَ\nУлар (ўз гумонларича мискинларни боғга йўлатмаслик тўғрисидаги) мақсад-ғаразга қодир бўлган ҳолларида тонг-саҳарлаб бордилар'}, {'chapter': 68, 'verse': 26, 'text': 'فَلَمَّا رَأَوۡهَا قَالُوٓاْ إِنَّا لَضَآلُّونَ\nЭнди қачонки уни (яъни, куйиб кулга айланган боғларни) кўришгач, (аввал): «Шак-шубҳасиз, бизлар йўлдан адашиб қолдик (яъни, адашиб бошқа боғга келиб қолдик), дедилар'}, {'chapter': 68, 'verse': 27, 'text': 'بَلۡ نَحۡنُ مَحۡرُومُونَ\n(Сўнг бу ўзларининг боғлари эканини билишгач эса): «Йўқ, бизлар (боғимиздан) маҳрум бўлибмиз», дедилар'}, {'chapter': 68, 'verse': 28, 'text': 'قَالَ أَوۡسَطُهُمۡ أَلَمۡ أَقُل لَّكُمۡ لَوۡلَا تُسَبِّحُونَ\n(Шунда) уларнинг инсофлироқлари: «Мен сизларга (Аллоҳга) тасбеҳ айтиш — шукр қилмайсизларми (яъни, шундай мўл ҳосил ато этганининг шукронасига кабағалларнинг улушларини бермайсизларми?) демаганмидим?, деган эди'}, {'chapter': 68, 'verse': 29, 'text': 'قَالُواْ سُبۡحَٰنَ رَبِّنَآ إِنَّا كُنَّا ظَٰلِمِينَ\nУлар (энди вақт ўтгач): «Парвардигоримизни поклаймиз. Дарҳақиқат, бизлар (ўзимизга) зулм қилгувчи бўлдик», дедилар'}, {'chapter': 68, 'verse': 30, 'text': 'فَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَلَٰوَمُونَ\nСўнг улар бир-бирларига боқиб, маломат қилиша бошладилар'}, {'chapter': 68, 'verse': 31, 'text': 'قَالُواْ يَٰوَيۡلَنَآ إِنَّا كُنَّا طَٰغِينَ\nУлар дедилар: «Эй ўлим бўлсин бизларга! Дарҳақиқат, бизлар ҳаддан ошгувчи бўлдик'}, {'chapter': 68, 'verse': 32, 'text': 'عَسَىٰ رَبُّنَآ أَن يُبۡدِلَنَا خَيۡرٗا مِّنۡهَآ إِنَّآ إِلَىٰ رَبِّنَا رَٰغِبُونَ\nШоядки Парвардигоримиз бизларга у (боғ)дан ҳам яхшироғини алмаштириб берса. Бизлар албатта ёлғиз Парвардигоримизга рағбат-илтижо қилгувчидирмиз»'}, {'chapter': 68, 'verse': 33, 'text': 'كَذَٰلِكَ ٱلۡعَذَابُۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ\n(Аллоҳнинг амридан четга чиққан кимсалар учун) мана шундай азоб бордир. Агар билсалар, Охират азоби шак-шубҳасиз, янада каттароқдир'}, {'chapter': 68, 'verse': 34, 'text': 'إِنَّ لِلۡمُتَّقِينَ عِندَ رَبِّهِمۡ جَنَّـٰتِ ٱلنَّعِيمِ\nАлбатта тақводор зотлар учун Парвардигорлари ҳузурида ноз-неъмат боғлари бордир'}, {'chapter': 68, 'verse': 35, 'text': 'أَفَنَجۡعَلُ ٱلۡمُسۡلِمِينَ كَٱلۡمُجۡرِمِينَ\nАхир Биз мусулмонларни жиноятчи-кофир кимсаларга баробар қилурмизми'}, {'chapter': 68, 'verse': 36, 'text': 'مَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ\n(Эй мушриклар), сизларга нима бўлди? Қандай ҳукм чиқармоқдасизлар'}, {'chapter': 68, 'verse': 37, 'text': 'أَمۡ لَكُمۡ كِتَٰبٞ فِيهِ تَدۡرُسُونَ\nБалки сизлар учун (осмондан туширилган) бирон китоб бўлиб, ўқиб-ўрганаётгандирсизлар'}, {'chapter': 68, 'verse': 38, 'text': 'إِنَّ لَكُمۡ فِيهِ لَمَا تَخَيَّرُونَ\nУнда сизлар ўзларингиз учун (Охиратда) шак-шубҳасиз, танлаган-хоҳлаган нарсаларингиз бор эканлигини (ўқиб-ўрганаётгандирсизлар)'}, {'chapter': 68, 'verse': 39, 'text': 'أَمۡ لَكُمۡ أَيۡمَٰنٌ عَلَيۡنَا بَٰلِغَةٌ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ إِنَّ لَكُمۡ لَمَا تَحۡكُمُونَ\nБалки сизлар учун Бизнинг зиммамизда: «Шак-шубҳасиз, ўзларингиз ҳукм-қарор қилган бор нарса сизларники бўлур», деган то Қиёмат Кунигача етгувчи қасамлар (яъни, Биз томондан берилган аҳд-паймонлар) бордир'}, {'chapter': 68, 'verse': 40, 'text': 'سَلۡهُمۡ أَيُّهُم بِذَٰلِكَ زَعِيمٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), улардан сўранг-чи, қайсилари бунга (яъни, ўзларининг пуч даъволарининг ҳақ эканлигига) кафил бўла олар эканлар'}, {'chapter': 68, 'verse': 41, 'text': 'أَمۡ لَهُمۡ شُرَكَآءُ فَلۡيَأۡتُواْ بِشُرَكَآئِهِمۡ إِن كَانُواْ صَٰدِقِينَ\nБалки уларнинг (бу даъволарини тасдиқлайдиган) шериклари бордир?! У ҳолда ростгўй бўлсалар ўша шерикларини келтирсинлар'}, {'chapter': 68, 'verse': 42, 'text': 'يَوۡمَ يُكۡشَفُ عَن سَاقٖ وَيُدۡعَوۡنَ إِلَى ٱلسُّجُودِ فَلَا يَسۡتَطِيعُونَ\nБолдирлар очиладиган (яъни, оғир ҳисоб-китобга чоғланиладиган) ва улар (кофирлар) сажда қилишга чорланишиб, (лекин саждага) кучлари етмай қоладиган Кунни (эсланг)'}, {'chapter': 68, 'verse': 43, 'text': 'خَٰشِعَةً أَبۡصَٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٞۖ وَقَدۡ كَانُواْ يُدۡعَوۡنَ إِلَى ٱلسُّجُودِ وَهُمۡ سَٰلِمُونَ\nУлар (кофирлар) кўзлари (қуйига) эгилган, хорлик уларни ўраб-эгаллаб олган ҳолларида (сажда қилишга чорланишиб, лекин саждага кучлари етмай қоладиган Кунни эсланг)! (Чунки ҳаёти дунёдалик пайтларида) улар соғ-саломат бўлган ҳолларида сажда қилишга чорланар эдилар (аммо саждага қодир бўла туриб ундан бош тортар эдилар)'}, {'chapter': 68, 'verse': 44, 'text': 'فَذَرۡنِي وَمَن يُكَذِّبُ بِهَٰذَا ٱلۡحَدِيثِۖ سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз ушбу Сўзни — Қуръонни ёлғон дейдиган кимсаларни Менга қўйиб беринг! Яқинда Биз уларни ўзлари билмайдиган — сезмайдиган томондан аста-секин (ҳалокатга дучор қилажакмиз)'}, {'chapter': 68, 'verse': 45, 'text': 'وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ\nМен (гуноҳларини кўпайтиришлари учун) уларга муҳлат бериб, (умрларини узун қилиб) қўюрман. Дарвоқеъ, Менинг «макрим» жуда қаттиқдир'}, {'chapter': 68, 'verse': 46, 'text': 'أَمۡ تَسۡـَٔلُهُمۡ أَجۡرٗا فَهُم مِّن مَّغۡرَمٖ مُّثۡقَلُونَ\nБалки сиз (эй Муҳаммад алайҳис-салоту вас-салом, динга даъват қилганингиз учун), улардан ажр-ҳақ сўраб, улар зиён қилишларидан оғирсинаётгандирлар? (Яъни, сиз улардан ҳеч қандай ҳақ сўраётганингиз йўқ-ку)'}, {'chapter': 68, 'verse': 47, 'text': 'أَمۡ عِندَهُمُ ٱلۡغَيۡبُ فَهُمۡ يَكۡتُبُونَ\nБалки уларнинг олдиларида ғайб (яъни, ғайбда бўладиган барча ишлар битилган Лавҳул-Маҳфуз) мавжуд бўлиб, улар (айтаётган сўзларини ўшандан) ёзиб олаётгандирлар'}, {'chapter': 68, 'verse': 48, 'text': 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ ٱلۡحُوتِ إِذۡ نَادَىٰ وَهُوَ مَكۡظُومٞ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз Парвардигорингизнинг ҳукмига (яъни, у Зот кофирларга муҳлат бериб қўйганича, Ислом дини тезроқ ғолиб бўлмаётганига) сабр қилинг ва наҳанг-балиқ соҳиби (яъни, Юнус пайғамбар) каби (бесабр) бўлманг! Эсланг, ўзи бесабрлиги учун тушиб қолган балиқ қорнида туриб ғамга тўлган ҳолда (Парвардигордан паноҳ тилаб) нидо қилган эди'}, {'chapter': 68, 'verse': 49, 'text': 'لَّوۡلَآ أَن تَدَٰرَكَهُۥ نِعۡمَةٞ مِّن رَّبِّهِۦ لَنُبِذَ بِٱلۡعَرَآءِ وَهُوَ مَذۡمُومٞ\nАгар унга Парвардигори томонидан бўлган неъмат-марҳамат етмаганида, албатта қуруқликка мазамматланган ҳолида улоқтирилган бўлур эди, (лекин Парвардигори унга марҳамат кўрсатиб балиқ қорнидан соғ-омон чиқарди)'}, {'chapter': 68, 'verse': 50, 'text': 'فَٱجۡتَبَٰهُ رَبُّهُۥ فَجَعَلَهُۥ مِنَ ٱلصَّـٰلِحِينَ\nСўнг Парвардигори уни (яна қайта пайғамбарликка) танлаб, солиҳ (пайғамбар)лардан қилди'}, {'chapter': 68, 'verse': 51, 'text': 'وَإِن يَكَادُ ٱلَّذِينَ كَفَرُواْ لَيُزۡلِقُونَكَ بِأَبۡصَٰرِهِمۡ لَمَّا سَمِعُواْ ٱلذِّكۡرَ وَيَقُولُونَ إِنَّهُۥ لَمَجۡنُونٞ\nАлбатта кофир бўлган кимсалар эслатма — Қуръонни эшитган вақтларида сизни кўзлари билан йиқитаёзурлар ва (Муҳаммад) шак-шубҳасиз, мажнундир, дерлар'}, {'chapter': 68, 'verse': 52, 'text': 'وَمَا هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nҲолбуки, у (Қуръон) барча оламлар учун фақат бир (буюк) эслатмадир'}, {'chapter': 69, 'verse': 1, 'text': 'ٱلۡحَآقَّةُ\nАниқ рўй бергувчи (Қиёмат)'}, {'chapter': 69, 'verse': 2, 'text': 'مَا ٱلۡحَآقَّةُ\nЎша аниқ рўй бергувчи нимадир'}, {'chapter': 69, 'verse': 3, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡحَآقَّةُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз Аниқ рўй бергувчининг нима эканлигини қаердан билурсиз'}, {'chapter': 69, 'verse': 4, 'text': 'كَذَّبَتۡ ثَمُودُ وَعَادُۢ بِٱلۡقَارِعَةِ\nСамуд ва Од (қабилалари ўз даҳшатлари билан қалбларни) қаттиқ қоқувчи (Қиёмат Куни)ни ёлғон, дедилар'}, {'chapter': 69, 'verse': 5, 'text': 'فَأَمَّا ثَمُودُ فَأُهۡلِكُواْ بِٱلطَّاغِيَةِ\nЭнди Самуд (қабиласи)га келсак, бас, улар ҳаддан ташқари қаттиқ нарса (чақмоқ) билан ҳалок қилиндилар'}, {'chapter': 69, 'verse': 6, 'text': 'وَأَمَّا عَادٞ فَأُهۡلِكُواْ بِرِيحٖ صَرۡصَرٍ عَاتِيَةٖ\nЭнди Од (қабиласи)га келсак, бас, улар бир даҳшатли, қутурган бурон билан ҳалок қилиндилар'}, {'chapter': 69, 'verse': 7, 'text': 'سَخَّرَهَا عَلَيۡهِمۡ سَبۡعَ لَيَالٖ وَثَمَٰنِيَةَ أَيَّامٍ حُسُومٗاۖ فَتَرَى ٱلۡقَوۡمَ فِيهَا صَرۡعَىٰ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٍ خَاوِيَةٖ\nУ (бўронни Аллоҳ) уларнинг устига пайдар-пай етти кеча ва саккиз кундуз ҳоким қилиб қўйдики, энди у жойдаги қавмни худди чириб — ичи бўшаб қолган хурмо дарахтининг танасидек қулаб, ҳалок бўлиб ётганларини кўрурсиз'}, {'chapter': 69, 'verse': 8, 'text': 'فَهَلۡ تَرَىٰ لَهُم مِّنۢ بَاقِيَةٖ\nБас, улардан бирон (омон) қолгувчини кўрармисиз? (Асло, уларнинг барчалари ҳалок бўлиб битдилар)'}, {'chapter': 69, 'verse': 9, 'text': 'وَجَآءَ فِرۡعَوۡنُ وَمَن قَبۡلَهُۥ وَٱلۡمُؤۡتَفِكَٰتُ بِٱلۡخَاطِئَةِ\nФиръавн ҳам, ундан аввал (ўтган динсиз) кимсалар ҳам, зеру забар бўлган қишлоқлар (яъни, Лут пайғамбар қавмининг қишлоқлари) ҳам хато ишлар қилдилар'}, {'chapter': 69, 'verse': 10, 'text': 'فَعَصَوۡاْ رَسُولَ رَبِّهِمۡ فَأَخَذَهُمۡ أَخۡذَةٗ رَّابِيَةً\nУлар Парвардигорларининг Пайғамбарига итоатсизлик қилдилар. Бас, У зот уларни ортиқча қаттиқ ушлаш билан ушлади (яъни, мислсиз қаттиқ азоб билан азоблади)'}, {'chapter': 69, 'verse': 11, 'text': 'إِنَّا لَمَّا طَغَا ٱلۡمَآءُ حَمَلۡنَٰكُمۡ فِي ٱلۡجَارِيَةِ\nДарвоқеъ, Биз сув туғёнга тушган вақтида сизларни (Нуҳ пайғамбар ясаган) кемада кўтардик'}, {'chapter': 69, 'verse': 12, 'text': 'لِنَجۡعَلَهَا لَكُمۡ تَذۡكِرَةٗ وَتَعِيَهَآ أُذُنٞ وَٰعِيَةٞ\nБиз у (ишни, яъни мўминларга нажот бериб, кофирларни сувга ғарқ қилиб юбориш)ни сизлар учун эслатма-ибрат қилиш учун ва англагувчи қулоқлар англаб-эшитиб олишлари учун (қилдик)'}, {'chapter': 69, 'verse': 13, 'text': 'فَإِذَا نُفِخَ فِي ٱلصُّورِ نَفۡخَةٞ وَٰحِدَةٞ\nБас, қачон Сур бир бор чалинганида'}, {'chapter': 69, 'verse': 14, 'text': 'وَحُمِلَتِ ٱلۡأَرۡضُ وَٱلۡجِبَالُ فَدُكَّتَا دَكَّةٗ وَٰحِدَةٗ\nВа Еру тоғлар (ўз жойларидан) кўтарилиб, (бир-бирларига) бир бор урилиб (чилпарчин бўлганида)'}, {'chapter': 69, 'verse': 15, 'text': 'فَيَوۡمَئِذٖ وَقَعَتِ ٱلۡوَاقِعَةُ\nАна ўша Кунда воқеа воқеъ бўлур! (Яъни, Қиёмат қойим бўлур)'}, {'chapter': 69, 'verse': 16, 'text': 'وَٱنشَقَّتِ ٱلسَّمَآءُ فَهِيَ يَوۡمَئِذٖ وَاهِيَةٞ\nВа осмон ёрилур! Чунки (осмон) у Кунда заиф бўлиб қолур'}, {'chapter': 69, 'verse': 17, 'text': 'وَٱلۡمَلَكُ عَلَىٰٓ أَرۡجَآئِهَاۚ وَيَحۡمِلُ عَرۡشَ رَبِّكَ فَوۡقَهُمۡ يَوۡمَئِذٖ ثَمَٰنِيَةٞ\nВа фаришталар (осмоннинг) чор-атрофида (Аллоҳнинг амрига мунтазир бўлиб) турурлар. Уларнинг устида Парвардигорингизнинг аршини у Кунда саккиз (фаришта) кўтариб турур'}, {'chapter': 69, 'verse': 18, 'text': 'يَوۡمَئِذٖ تُعۡرَضُونَ لَا تَخۡفَىٰ مِنكُمۡ خَافِيَةٞ\nЎша Кунда сизлар (ҳисоб-китоб учун Аллоҳга) кўндаланг қилинурсизлар — сизларнинг бирон сирингиз махфий қолмас'}, {'chapter': 69, 'verse': 19, 'text': 'فَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَيَقُولُ هَآؤُمُ ٱقۡرَءُواْ كِتَٰبِيَهۡ\nБас, энди ўз китоби — номаи аъмоли ўнг қўлидан берилган кишига келсак, бас, у: «Мана менинг китобимни ўқинглар'}, {'chapter': 69, 'verse': 20, 'text': 'إِنِّي ظَنَنتُ أَنِّي مُلَٰقٍ حِسَابِيَهۡ\nДарҳақиқат, мен ҳисоб-китобимга (яъни, Охиратдаги ажр-мукофотга) рўбарў бўлишимни билган эдим», дер'}, {'chapter': 69, 'verse': 21, 'text': 'فَهُوَ فِي عِيشَةٖ رَّاضِيَةٖ\nСўнг у кўнгилли ҳаётда'}, {'chapter': 69, 'verse': 22, 'text': 'فِي جَنَّةٍ عَالِيَةٖ\nЮксак жаннатда бўлур'}, {'chapter': 69, 'verse': 23, 'text': 'قُطُوفُهَا دَانِيَةٞ\nУнинг мевалари (аҳли жаннат учун) яқиндир'}, {'chapter': 69, 'verse': 24, 'text': 'كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَآ أَسۡلَفۡتُمۡ فِي ٱلۡأَيَّامِ ٱلۡخَالِيَةِ\n(Жаннат аҳлига): «Ўтган кунларда (яъни, ҳаёти дунёда) қилиб ўтган (эзгу) амалларингиз сабабли (ушбу ноз-неъматларни) пок билиб еб-ичаверинглар», (дейилур)'}, {'chapter': 69, 'verse': 25, 'text': 'وَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِشِمَالِهِۦ فَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُوتَ كِتَٰبِيَهۡ\nЭнди ўз китоби чап қўлидан берилган кимсага келсак, бас, у дер: «Эҳ, қани энди, менга китобим берилмаса эди'}, {'chapter': 69, 'verse': 26, 'text': 'وَلَمۡ أَدۡرِ مَا حِسَابِيَهۡ\nВа мен ҳисоб-китобим (яъни, оладиган жазойим) нима эканлигини билмасам эди'}, {'chapter': 69, 'verse': 27, 'text': 'يَٰلَيۡتَهَا كَانَتِ ٱلۡقَاضِيَةَ\nЭҳ қани энди, ўша (биринчи ўлим барча нарсани) тугатгувчи-узгувчи бўлса (ва Қиёматдаги мана бу қайта тирилиш бўлмаса) эди'}, {'chapter': 69, 'verse': 28, 'text': 'مَآ أَغۡنَىٰ عَنِّي مَالِيَهۡۜ\nМенга (не машаққатлар билан топган) мол-дунёйим асқотмади-я'}, {'chapter': 69, 'verse': 29, 'text': 'هَلَكَ عَنِّي سُلۡطَٰنِيَهۡ\nМулку салтанатим ҳам ҳалок бўлиб кетди-я!»'}, {'chapter': 69, 'verse': 30, 'text': 'خُذُوهُ فَغُلُّوهُ\n(Бас, Аллоҳ жаҳаннам қўриқчиларига дер): «Уни ушлаб, кишанланглар'}, {'chapter': 69, 'verse': 31, 'text': 'ثُمَّ ٱلۡجَحِيمَ صَلُّوهُ\nСўнгра дўзахга ташланглар'}, {'chapter': 69, 'verse': 32, 'text': 'ثُمَّ فِي سِلۡسِلَةٖ ذَرۡعُهَا سَبۡعُونَ ذِرَاعٗا فَٱسۡلُكُوهُ\nСўнгра узунлиги етмиш газ бўлган занжирга солиб боғланглар!»'}, {'chapter': 69, 'verse': 33, 'text': 'إِنَّهُۥ كَانَ لَا يُؤۡمِنُ بِٱللَّهِ ٱلۡعَظِيمِ\nЧунки (ҳаёти дунёдалик пайтида) Улуғ Аллоҳга иймон келтирмас эди'}, {'chapter': 69, 'verse': 34, 'text': 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ\nВа мискин-бечорага таом беришга (ўзини ҳам, ўзгаларни ҳам) тарғиб қилмас эди'}, {'chapter': 69, 'verse': 35, 'text': 'فَلَيۡسَ لَهُ ٱلۡيَوۡمَ هَٰهُنَا حَمِيمٞ\nБас, Бугун, бу ерда унинг учун бирон дўст-мададкор йўқдир'}, {'chapter': 69, 'verse': 36, 'text': 'وَلَا طَعَامٌ إِلَّا مِنۡ غِسۡلِينٖ\nВа бирон таом ҳам йўқдир! Фақат йирингдан бўлган (бир «таом» борки)'}, {'chapter': 69, 'verse': 37, 'text': 'لَّا يَأۡكُلُهُۥٓ إِلَّا ٱلۡخَٰطِـُٔونَ\nУни фақат (йўлдан адашган (кофир)ларгина ерлар'}, {'chapter': 69, 'verse': 38, 'text': 'فَلَآ أُقۡسِمُ بِمَا تُبۡصِرُونَ\nБас, Мен сизлар кўрадиган нарсаларга қасам ичурман'}, {'chapter': 69, 'verse': 39, 'text': 'وَمَا لَا تُبۡصِرُونَ\nСизлар кўра олмайдиган нарсаларга ҳам (қасам ичурманки)'}, {'chapter': 69, 'verse': 40, 'text': 'إِنَّهُۥ لَقَوۡلُ رَسُولٖ كَرِيمٖ\nУ (Қуръон) шак-шубҳасиз, улуғ Элчининг (Аллоҳ даргоҳидан келтирган) сўзидир'}, {'chapter': 69, 'verse': 41, 'text': 'وَمَا هُوَ بِقَوۡلِ شَاعِرٖۚ قَلِيلٗا مَّا تُؤۡمِنُونَ\nУ бирон шоирнинг сўзи эмасдир! Сизлар (ушбу Қуръон Аллоҳнинг каломи эканлигига) камдан-кам иймон келтирурсизлар'}, {'chapter': 69, 'verse': 42, 'text': 'وَلَا بِقَوۡلِ كَاهِنٖۚ قَلِيلٗا مَّا تَذَكَّرُونَ\nВа бирон коҳин-фолбиннинг сўзи ҳам эмасдир. Сизлар камдан-кам панд-насиҳат олурсизлар'}, {'chapter': 69, 'verse': 43, 'text': 'تَنزِيلٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ\n(У) барча оламлар Парвардигори томонидан нозил қилинган (Китобдир)'}, {'chapter': 69, 'verse': 44, 'text': 'وَلَوۡ تَقَوَّلَ عَلَيۡنَا بَعۡضَ ٱلۡأَقَاوِيلِ\nАгар (Пайғамбар) Бизнинг шаънимизга (Биз айтмаган) айрим сўзларни тўқиб олганида'}, {'chapter': 69, 'verse': 45, 'text': 'لَأَخَذۡنَا مِنۡهُ بِٱلۡيَمِينِ\nАлбатта, Биз унинг ўнг қўлидан ушлаган'}, {'chapter': 69, 'verse': 46, 'text': 'ثُمَّ لَقَطَعۡنَا مِنۡهُ ٱلۡوَتِينَ\nсўнгра албатта унинг шоҳтомирини узиб ташлаган бўлур эдик'}, {'chapter': 69, 'verse': 47, 'text': 'فَمَا مِنكُم مِّنۡ أَحَدٍ عَنۡهُ حَٰجِزِينَ\nУ ҳолда сизлардан бирон киши ундан (яъни, пайғамбардан ҳалокатни) тўса олгувчи бўлмас эди. (Бас, Муҳаммад алайҳис-салоту вас-салом Аллоҳнинг каломига ўз томонидан бирон сўз қўшмагани, балки зиммасидаги омонат-пайғамбарликни ҳалол адо қилиб келаётгани учун ҳам сизларнинг орангизда соғ-омон ҳаёт кечирмоқда, акс ҳолда Аллоҳ уни ҳалок қилган бўлур эди. Бу Муҳаммад алайҳис-салоту вас-саломнинг ҳақ пайғамбар эканликларининг яна бир ёрқин далилидир)'}, {'chapter': 69, 'verse': 48, 'text': 'وَإِنَّهُۥ لَتَذۡكِرَةٞ لِّلۡمُتَّقِينَ\nАлбатта у (Қуръон) тақводор зотлар учун панд-насиҳатдир'}, {'chapter': 69, 'verse': 49, 'text': 'وَإِنَّا لَنَعۡلَمُ أَنَّ مِنكُم مُّكَذِّبِينَ\nШак-шубҳасиз, Биз сизларнинг орангизда (Қуръонни) ёлғон дегувчилар ҳам бор эканини аниқ билурмиз'}, {'chapter': 69, 'verse': 50, 'text': 'وَإِنَّهُۥ لَحَسۡرَةٌ عَلَى ٱلۡكَٰفِرِينَ\nАлбатта у (Қуръон) кофирларга (улар Қиёмат Кунида Қуръонга иймон келтирган кишиларнинг савобга эга бўлганларини кўрган вақтларида) ҳасрат-надомат бўлур'}, {'chapter': 69, 'verse': 51, 'text': 'وَإِنَّهُۥ لَحَقُّ ٱلۡيَقِينِ\nАлбатта у (Қуръон) аниқ Ҳақиқатдир'}, {'chapter': 69, 'verse': 52, 'text': 'فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ\nБас, улуғ Парвардигорингизнинг номини (мудом, барча айб-нуқсонлардан) пок тутинг'}, {'chapter': 70, 'verse': 1, 'text': 'سَأَلَ سَآئِلُۢ بِعَذَابٖ وَاقِعٖ\nБир сўрагувчи кимса тушгувчи азобни (ўз устига тушишини) сўради'}, {'chapter': 70, 'verse': 2, 'text': 'لِّلۡكَٰفِرِينَ لَيۡسَ لَهُۥ دَافِعٞ\nКофирларга (тушгувчи) у (азоб)ни биров қайтара олмайди'}, {'chapter': 70, 'verse': 3, 'text': 'مِّنَ ٱللَّهِ ذِي ٱلۡمَعَارِجِ\n(У азоб, осмонларнинг барча) поғоналари — қаватлари соҳиби бўлмиш Аллоҳ томонидан (кофирлар устига тушгувчидир)'}, {'chapter': 70, 'verse': 4, 'text': 'تَعۡرُجُ ٱلۡمَلَـٰٓئِكَةُ وَٱلرُّوحُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥ خَمۡسِينَ أَلۡفَ سَنَةٖ\nФаришталар ва Руҳ (яъни, Жаброил) миқдори-узунлиги эллик минг бўлган бир Кунда (яъни, Қиёмат Кунида) У зотнинг ҳузурига кўтарилурлар'}, {'chapter': 70, 'verse': 5, 'text': 'فَٱصۡبِرۡ صَبۡرٗا جَمِيلًا\nБас, (эй Муҳаммад алайҳис-салоту вас-салом, кофирларнинг озор-азийятларига) чиройли сабр билан сабр-тоқат қилинг'}, {'chapter': 70, 'verse': 6, 'text': 'إِنَّهُمۡ يَرَوۡنَهُۥ بَعِيدٗا\nЧунки улар у (Кун)ни узоқ деб билурлар'}, {'chapter': 70, 'verse': 7, 'text': 'وَنَرَىٰهُ قَرِيبٗا\nБиз эса унинг яқинлигини билурмиз'}, {'chapter': 70, 'verse': 8, 'text': 'يَوۡمَ تَكُونُ ٱلسَّمَآءُ كَٱلۡمُهۡلِ\nУ Кунда осмон эритилган мис каби бўлиб қолур'}, {'chapter': 70, 'verse': 9, 'text': 'وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ\nТоғлар юнг каби (енгил) бўлиб қолурлар'}, {'chapter': 70, 'verse': 10, 'text': 'وَلَا يَسۡـَٔلُ حَمِيمٌ حَمِيمٗا\nВа бирон дўст дўстидан (ҳол-аҳвол) сўрамас'}, {'chapter': 70, 'verse': 11, 'text': 'يُبَصَّرُونَهُمۡۚ يَوَدُّ ٱلۡمُجۡرِمُ لَوۡ يَفۡتَدِي مِنۡ عَذَابِ يَوۡمِئِذِۭ بِبَنِيهِ\n(Ҳолбуки) улар (бир-бирларига) кўрсатилурлар! Жиноятчи-кофир кимса у Куннинг азобидан (қутулиш учун) ўғилларини тўлов қилиб бериб юборишни'}, {'chapter': 70, 'verse': 12, 'text': 'وَصَٰحِبَتِهِۦ وَأَخِيهِ\nхотинини ва биродарини ҳам'}, {'chapter': 70, 'verse': 13, 'text': 'وَفَصِيلَتِهِ ٱلَّتِي تُـٔۡوِيهِ\nуни ўз паноҳига оладиган қариндош-уруғини ҳам'}, {'chapter': 70, 'verse': 14, 'text': 'وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗا ثُمَّ يُنجِيهِ\nва Ердаги барча кишиларини ҳам (тўлов қилиб бериб юборишни), сўнгра (бу тўлов) унга нажот беришини истар'}, {'chapter': 70, 'verse': 15, 'text': 'كَلَّآۖ إِنَّهَا لَظَىٰ\nЙўқ, (ҳеч қандай тўлов уни азобдан қутқара олмас)! Албатта у бир оловдирки'}, {'chapter': 70, 'verse': 16, 'text': 'نَزَّاعَةٗ لِّلشَّوَىٰ\n(Кофирлар ташланадиган дўзах ўз ҳарорати билан) бош териларини сидириб олгувчи бир оловдир'}, {'chapter': 70, 'verse': 17, 'text': 'تَدۡعُواْ مَنۡ أَدۡبَرَ وَتَوَلَّىٰ\nУ (Ҳақ йўлидан) юз ўгириб, терс қараб кетган'}, {'chapter': 70, 'verse': 18, 'text': 'وَجَمَعَ فَأَوۡعَىٰٓ\nва (пул-мол) йиғиб тўплаган (яъни, уни Аллоҳ буюрган жойларга сарфламаган) кимсаларни (ўзига) чорлаб турар'}, {'chapter': 70, 'verse': 19, 'text': '۞إِنَّ ٱلۡإِنسَٰنَ خُلِقَ هَلُوعًا\nДарҳақиқат, инсон бетоқат қилиб яратилгандир'}, {'chapter': 70, 'verse': 20, 'text': 'إِذَا مَسَّهُ ٱلشَّرُّ جَزُوعٗا\nҚачон унга ёмонлик (камбағаллик-кулфат) етиб қолса, у ўта бесабрлик қилгувчидир'}, {'chapter': 70, 'verse': 21, 'text': 'وَإِذَا مَسَّهُ ٱلۡخَيۡرُ مَنُوعًا\nҚачон унга яхшилик (бойлик, саломатлик) етса, у ўта манъ қилгувчи — бахилдир'}, {'chapter': 70, 'verse': 22, 'text': 'إِلَّا ٱلۡمُصَلِّينَ\nФақат намоз ўқигувчи зотлар (яъни, мусулмонларгина ундоқ эмасдирлар)ки'}, {'chapter': 70, 'verse': 23, 'text': 'ٱلَّذِينَ هُمۡ عَلَىٰ صَلَاتِهِمۡ دَآئِمُونَ\nУлар намозларида доим — барқарор бўлгувчи зотлардир'}, {'chapter': 70, 'verse': 24, 'text': 'وَٱلَّذِينَ فِيٓ أَمۡوَٰلِهِمۡ حَقّٞ مَّعۡلُومٞ\nУлар (топган) мол-мулкларида маълум ҳақ (яъни, закот) бўлган зотлардир'}, {'chapter': 70, 'verse': 25, 'text': 'لِّلسَّآئِلِ وَٱلۡمَحۡرُومِ\nСўрагувчи ва (мол-давлатдан) маҳрум кишилар учун (маълум ҳақ ажратган зотлардир)'}, {'chapter': 70, 'verse': 26, 'text': 'وَٱلَّذِينَ يُصَدِّقُونَ بِيَوۡمِ ٱلدِّينِ\nУлар жазо — Қиёмат Кунини тасдиқ этадиган зотлардир'}, {'chapter': 70, 'verse': 27, 'text': 'وَٱلَّذِينَ هُم مِّنۡ عَذَابِ رَبِّهِم مُّشۡفِقُونَ\nУлар Парвардигорларининг азобидан қўрқувчи бўлган зотлардир'}, {'chapter': 70, 'verse': 28, 'text': 'إِنَّ عَذَابَ رَبِّهِمۡ غَيۡرُ مَأۡمُونٖ\nЗотан, Парвардигорларининг азоби (ҳеч ким учун) бехатар эмасдир (яъни, ҳеч кимнинг «Менга Аллоҳнинг азоби тушмайди», деб хотиржам бўлишга ҳаққи йўқдир)'}, {'chapter': 70, 'verse': 29, 'text': 'وَٱلَّذِينَ هُمۡ لِفُرُوجِهِمۡ حَٰفِظُونَ\nУлар авратларини (ҳаромдан — зинодан пок) сақлагувчи зотлардир'}, {'chapter': 70, 'verse': 30, 'text': 'إِلَّا عَلَىٰٓ أَزۡوَٰجِهِمۡ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَإِنَّهُمۡ غَيۡرُ مَلُومِينَ\nМагар ўз жуфти ҳалолларидан ва қўлларидаги чўриларидангина (сақланмайдилар). Бас, улар маломат қилинмаслар'}, {'chapter': 70, 'verse': 31, 'text': 'فَمَنِ ٱبۡتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡعَادُونَ\nЭнди ким бундан ўзгани (яъни, зино ва шу каби шариати исломийяда ҳаром қилинган бошқа нарсаларни) истаса, бас, ана ўшалар ҳаддан ошгувчи кимсалардир'}, {'chapter': 70, 'verse': 32, 'text': 'وَٱلَّذِينَ هُمۡ لِأَمَٰنَٰتِهِمۡ وَعَهۡدِهِمۡ رَٰعُونَ\nУлар (яъни, мўмин-мусулмонлар) ўзларига ишонилган омонатларга ва (ўзгаларга) берган аҳд-паймонларига риоя қилгувчи зотлардир'}, {'chapter': 70, 'verse': 33, 'text': 'وَٱلَّذِينَ هُم بِشَهَٰدَٰتِهِمۡ قَآئِمُونَ\nУлар ўз гувоҳликларини тўғри-холис адо қилгувчи зотлардир'}, {'chapter': 70, 'verse': 34, 'text': 'وَٱلَّذِينَ هُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ\nУлар намозларини (вақтида адо этиб, қазо бўлишдан) сақлагувчи бўлган зотлардир'}, {'chapter': 70, 'verse': 35, 'text': 'أُوْلَـٰٓئِكَ فِي جَنَّـٰتٖ مُّكۡرَمُونَ\nАна ўшалар жаннатларда ҳурмат-иззат кўргувчидирлар'}, {'chapter': 70, 'verse': 36, 'text': 'فَمَالِ ٱلَّذِينَ كَفَرُواْ قِبَلَكَ مُهۡطِعِينَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), бу кофир бўлган кимсаларга нима бўлдики, сиз томонга чопурлар'}, {'chapter': 70, 'verse': 37, 'text': 'عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ عِزِينَ\nУлар ўнг ва сўлдан тўда-тўда бўлиб, (сиз томонга чопурлар)'}, {'chapter': 70, 'verse': 38, 'text': 'أَيَطۡمَعُ كُلُّ ٱمۡرِيٕٖ مِّنۡهُمۡ أَن يُدۡخَلَ جَنَّةَ نَعِيمٖ\nЁ улардан ҳар бир кимса ноз-неъмат жаннатига киритилишни тамаъ қилурми'}, {'chapter': 70, 'verse': 39, 'text': 'كَلَّآۖ إِنَّا خَلَقۡنَٰهُم مِّمَّا يَعۡلَمُونَ\nЙўқ, (улар ҳеч қачон жаннатга кирмаслар)! Албатта Биз уларни ўзлари биладиган нарсадан (яъни, бир томчи сувдан) яратдик'}, {'chapter': 70, 'verse': 40, 'text': 'فَلَآ أُقۡسِمُ بِرَبِّ ٱلۡمَشَٰرِقِ وَٱلۡمَغَٰرِبِ إِنَّا لَقَٰدِرُونَ\nБас, (шундоқ экан) машриқу мағрибларнинг Парвардигорига қасам ичурманки, шак-шубҳасиз, Биз қодирдирмиз'}, {'chapter': 70, 'verse': 41, 'text': 'عَلَىٰٓ أَن نُّبَدِّلَ خَيۡرٗا مِّنۡهُمۡ وَمَا نَحۡنُ بِمَسۡبُوقِينَ\n(бу кофирларни ҳалок қилиб ўринларига), улардан яхшироқ (инсонларни) алмаштириб қўйишга ва Биз (бу ишга) ожиз эмасдирмиз'}, {'chapter': 70, 'verse': 42, 'text': 'فَذَرۡهُمۡ يَخُوضُواْ وَيَلۡعَبُواْ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي يُوعَدُونَ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз уларни тарк қилинг! Улар то ўзларига ваъда қилинаётган кунларига рўбарў бўлгунларича (ўз ботил-беҳуда хаёлларига) шўнғишиб, ўйнаб-кулиб юраверсинлар'}, {'chapter': 70, 'verse': 43, 'text': 'يَوۡمَ يَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ سِرَاعٗا كَأَنَّهُمۡ إِلَىٰ نُصُبٖ يُوفِضُونَ\nУ кунда улар гўё (ўзлари сиғинадиган) бутларга қараб чопишаётгандек шошилган ҳолларида қабрларидан чиқиб келурлар'}, {'chapter': 70, 'verse': 44, 'text': 'خَٰشِعَةً أَبۡصَٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٞۚ ذَٰلِكَ ٱلۡيَوۡمُ ٱلَّذِي كَانُواْ يُوعَدُونَ\nКўзлари (қуйига) эгилган, хорлик уларни ўраб-эгаллаб олган ҳолларида (қабрларидан чиқиб келурлар!) Мана шу (ҳаёти дунёда) уларга ваъда қилингувчи бўлган Кундир'}, {'chapter': 71, 'verse': 1, 'text': 'إِنَّآ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ أَنۡ أَنذِرۡ قَوۡمَكَ مِن قَبۡلِ أَن يَأۡتِيَهُمۡ عَذَابٌ أَلِيمٞ\nДарҳақиқат, Биз Нуҳни: «Қавмингни уларга аламли азоб келиб қолишидан илгари огоҳлантиргин», деб ўз қавмига пайғамбар қилиб юбордик'}, {'chapter': 71, 'verse': 2, 'text': 'قَالَ يَٰقَوۡمِ إِنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ\nУ деди: «Эй қавмим, албатта мен сизларга (юборилган) очиқ огоҳлантиргувчидирман'}, {'chapter': 71, 'verse': 3, 'text': 'أَنِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ\nАллоҳга ибодат қилинглар ва У зотдан қўрқинглар ҳамда менга итоат этинглар'}, {'chapter': 71, 'verse': 4, 'text': 'يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرۡكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمًّىۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُۚ لَوۡ كُنتُمۡ تَعۡلَمُونَ\n(Шунда Аллоҳ) сизларни гуноҳларингиздан мағфират қилур ва сизларни белгиланган муддатгача (яъни, ажалларингиз битгунича тирик) қолдирур. Агар билгувчи бўлсангизлар, Аллоҳнинг (ўлим учун белгилаб қўйган) муддати келган вақтида асло ортга сурилмас'}, {'chapter': 71, 'verse': 5, 'text': 'قَالَ رَبِّ إِنِّي دَعَوۡتُ قَوۡمِي لَيۡلٗا وَنَهَارٗا\n(Нуҳнинг бу даъватлари уларга кор қилмагач), у деди: «Парвардигорим, албатта мен қавмимни кеча-ю кундуз (иймон-эътиқодга) даъват этдим'}, {'chapter': 71, 'verse': 6, 'text': 'فَلَمۡ يَزِدۡهُمۡ دُعَآءِيٓ إِلَّا فِرَارٗا\n(Аммо) менинг даъватим уларга фақат (иймондан) қочишни зиёда қилди, холос'}, {'chapter': 71, 'verse': 7, 'text': 'وَإِنِّي كُلَّمَا دَعَوۡتُهُمۡ لِتَغۡفِرَ لَهُمۡ جَعَلُوٓاْ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِمۡ وَٱسۡتَغۡشَوۡاْ ثِيَابَهُمۡ وَأَصَرُّواْ وَٱسۡتَكۡبَرُواْ ٱسۡتِكۡبَارٗا\nДарвоқеъ, ҳар қачон мен уларни Сенинг мағфиратингга (сабаб бўладиган дин йўлига) даъват қилсам, улар (менинг сўзларимни эшитмаслик учун) бармоқларини қулоқларига тиқиб, (мени кўрмаслик учун юзу кўзларига) кийимларини ўраб-чирмаб олдилар ва (ўз куфрларида) оёқ тираб турдилар ҳамда (менга итоат этишдан бош тортиб) кибр-ҳаво қилдилар'}, {'chapter': 71, 'verse': 8, 'text': 'ثُمَّ إِنِّي دَعَوۡتُهُمۡ جِهَارٗا\nСўнгра, мен уларни ошкора (яъни, одамлар тўпланган жойларда баланд овозда) даъват этдим'}, {'chapter': 71, 'verse': 9, 'text': 'ثُمَّ إِنِّيٓ أَعۡلَنتُ لَهُمۡ وَأَسۡرَرۡتُ لَهُمۡ إِسۡرَارٗا\nСўнгра мен уларга (ўз даъватимни) очиқ эълон ҳам қилдим, пинҳона сир қилиб ҳам айтдим'}, {'chapter': 71, 'verse': 10, 'text': 'فَقُلۡتُ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ إِنَّهُۥ كَانَ غَفَّارٗا\nМен дедимки: «Парвардигорингиз (Аллоҳ)дан мағфират сўранглар, албатта У ўта мағфиратли бўлган Зотдир'}, {'chapter': 71, 'verse': 11, 'text': 'يُرۡسِلِ ٱلسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا\n(Шунда) У зот устларингизга осмондан ёмғир қуйдирур'}, {'chapter': 71, 'verse': 12, 'text': 'وَيُمۡدِدۡكُم بِأَمۡوَٰلٖ وَبَنِينَ وَيَجۡعَل لَّكُمۡ جَنَّـٰتٖ وَيَجۡعَل لَّكُمۡ أَنۡهَٰرٗا\nВа сизларга мол-дунё, бола-чақа билан мадад берур ҳамда сизларга боғу бўстонлар (ато) қилур ва сизларга оқар дарёлар (ато) қилур'}, {'chapter': 71, 'verse': 13, 'text': 'مَّا لَكُمۡ لَا تَرۡجُونَ لِلَّهِ وَقَارٗا\nНега сизлар Аллоҳни улуғлашни (яъни, Унга ибодат қилишни) ўйламайсизлар'}, {'chapter': 71, 'verse': 14, 'text': 'وَقَدۡ خَلَقَكُمۡ أَطۡوَارًا\nҲолбуки, У зот сизларни босқичма-босқич яратди-ку'}, {'chapter': 71, 'verse': 15, 'text': 'أَلَمۡ تَرَوۡاْ كَيۡفَ خَلَقَ ٱللَّهُ سَبۡعَ سَمَٰوَٰتٖ طِبَاقٗا\nАллоҳ етти осмонни қандай устма-уст қилиб яратганини'}, {'chapter': 71, 'verse': 16, 'text': 'وَجَعَلَ ٱلۡقَمَرَ فِيهِنَّ نُورٗا وَجَعَلَ ٱلشَّمۡسَ سِرَاجٗا\nВа ойни улардаги нур-ёруғлик қилиб, қуёшни эса (нур сочгувчи) чироқ қилиб қўйганини кўрмадингларми'}, {'chapter': 71, 'verse': 17, 'text': 'وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلۡأَرۡضِ نَبَاتٗا\nАллоҳ сизларнинг (отангиз Одамни) Ердан ундириб-ўстирди (яъни, пайдо қилди)'}, {'chapter': 71, 'verse': 18, 'text': 'ثُمَّ يُعِيدُكُمۡ فِيهَا وَيُخۡرِجُكُمۡ إِخۡرَاجٗا\nСўнгра (вафот қилганларингизда) У зот сизларни яна (ерга) қайтарур ва (Қиёмат қойим бўлганида) сизларни чиқариб олур'}, {'chapter': 71, 'verse': 19, 'text': 'وَٱللَّهُ جَعَلَ لَكُمُ ٱلۡأَرۡضَ بِسَاطٗا\nАллоҳ Ерни сизлар учун ёйиқ-текис қилиб қўйди'}, {'chapter': 71, 'verse': 20, 'text': 'لِّتَسۡلُكُواْ مِنۡهَا سُبُلٗا فِجَاجٗا\n— Сизлар ундаги кенг йўлларда юришларингиз учун (ёйиқ-текис қилиб қўйди)»'}, {'chapter': 71, 'verse': 21, 'text': 'قَالَ نُوحٞ رَّبِّ إِنَّهُمۡ عَصَوۡنِي وَٱتَّبَعُواْ مَن لَّمۡ يَزِدۡهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارٗا\nНуҳ айтди: «Парвардигорим, дарҳақиқат, улар менга итоатсизлик қилдилар ҳамда (топган) мол-дунёси ва бола-чақаси ўзига фақат зиённи (яъни, куфр ва туғённи) зиёда қилган кимсаларга (ўзларининг бой-бадавлат бошлиқларига) эргашиб кетдилар»'}, {'chapter': 71, 'verse': 22, 'text': 'وَمَكَرُواْ مَكۡرٗا كُبَّارٗا\nУ (бошлиқлари Нуҳга қарши) жуда катта макр-ҳийлалар қилдилар'}, {'chapter': 71, 'verse': 23, 'text': 'وَقَالُواْ لَا تَذَرُنَّ ءَالِهَتَكُمۡ وَلَا تَذَرُنَّ وَدّٗا وَلَا سُوَاعٗا وَلَا يَغُوثَ وَيَعُوقَ وَنَسۡرٗا\nВа (ўзларига эргашган тубан-пасткаш кимсаларга): «Сизлар ҳаргиз ўз худоларингизни тарк қилманглар! Вадни ҳам, Сувоъни ҳам, Яғусни ҳам, Яъуқни ва Насрни ҳам ҳаргиз тарк қилманглар!», дедилар'}, {'chapter': 71, 'verse': 24, 'text': 'وَقَدۡ أَضَلُّواْ كَثِيرٗاۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا ضَلَٰلٗا\n«Дарҳақиқат, улар (яъни, Нуҳ қавмининг бой-бадавлат бошлиқлари) кўп (кишилар)ни йўлдан оздирдилар. (Парвардигорим), бу золим кимсаларга фақат гумроҳликни зиёда қилгин», (деди Нуҳ)'}, {'chapter': 71, 'verse': 25, 'text': 'مِّمَّا خَطِيٓـَٰٔتِهِمۡ أُغۡرِقُواْ فَأُدۡخِلُواْ نَارٗا فَلَمۡ يَجِدُواْ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارٗا\nУлар ўз хато-гуноҳлари сабабли ғарқ қилиниб, дўзахга киритилдилар. Бас, ўзлари учун Аллоҳдан ўзга ёрдам бергувчиларни топмадилар (яъни, сиғинган бут-санамлари уларни Аллоҳнинг азобидан қутқара олмадилар)'}, {'chapter': 71, 'verse': 26, 'text': 'وَقَالَ نُوحٞ رَّبِّ لَا تَذَرۡ عَلَى ٱلۡأَرۡضِ مِنَ ٱلۡكَٰفِرِينَ دَيَّارًا\nНуҳ айтди: «Парвардигорим, Ер юзида кофирлардан бирон ҳовли-жой эгасини қолдирмагин'}, {'chapter': 71, 'verse': 27, 'text': 'إِنَّكَ إِن تَذَرۡهُمۡ يُضِلُّواْ عِبَادَكَ وَلَا يَلِدُوٓاْ إِلَّا فَاجِرٗا كَفَّارٗا\nЧунки Сен агар уларни (Ер юзида) қолдирсанг, улар бандаларингни йўлдан оздирурлар ва улар фақат кўрнамак, нопок (кимсалар)ни туғиб-кўпайтирурлар'}, {'chapter': 71, 'verse': 28, 'text': 'رَّبِّ ٱغۡفِرۡ لِي وَلِوَٰلِدَيَّ وَلِمَن دَخَلَ بَيۡتِيَ مُؤۡمِنٗا وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا تَبَارَۢا\nПарвардигорим, Ўзинг мени, ота-онамни, менинг уйимга мўмин ҳолда кирган кишиларни ва барча мўмину мўминаларни мағфират қилган, золим кимсаларга эса фақат ҳалокатни зиёда қилгин»'}, {'chapter': 72, 'verse': 1, 'text': 'قُلۡ أُوحِيَ إِلَيَّ أَنَّهُ ٱسۡتَمَعَ نَفَرٞ مِّنَ ٱلۡجِنِّ فَقَالُوٓاْ إِنَّا سَمِعۡنَا قُرۡءَانًا عَجَبٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Менга ваҳий қилиндики, жинлардан бир гуруҳи (менинг Қуръон тиловат қилганимни) эшитишиб, (ўз қавмларига қайтиб боришгач, дедилар: «Дарҳақиқат, бизлар бир ажиб Қуръонни эшитдик)'}, {'chapter': 72, 'verse': 2, 'text': 'يَهۡدِيٓ إِلَى ٱلرُّشۡدِ فَـَٔامَنَّا بِهِۦۖ وَلَن نُّشۡرِكَ بِرَبِّنَآ أَحَدٗا\nҲақ йўлга ҳидоят қиладиган (бир ажиб Қуръонни эшитдик) ва дарҳол унга иймон келтирдик. Бизлар (энди) Парвардигоримизга ҳаргиз бирон кимсани шерик қилмасмиз»'}, {'chapter': 72, 'verse': 3, 'text': 'وَأَنَّهُۥ تَعَٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَٰحِبَةٗ وَلَا وَلَدٗا\n«Албатта буюклиги юксак бўлган Парвардигоримиз бирон жуфт ва ё фарзанд тутган эмасдир'}, {'chapter': 72, 'verse': 4, 'text': 'وَأَنَّهُۥ كَانَ يَقُولُ سَفِيهُنَا عَلَى ٱللَّهِ شَطَطٗا\nАлбатта бизларнинг ичимиздаги аҳмоқ (иблис) Аллоҳ шаънига («У зотнинг хотини ва боласи бор», деб) ноҳақ сўз айтар эди'}, {'chapter': 72, 'verse': 5, 'text': 'وَأَنَّا ظَنَنَّآ أَن لَّن تَقُولَ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَى ٱللَّهِ كَذِبٗا\nАлбатта бизлар инс ҳам, жин ҳам Аллоҳ шаънига ҳаргиз ёлғон сўзламас, деб ўйлар эдик»'}, {'chapter': 72, 'verse': 6, 'text': 'وَأَنَّهُۥ كَانَ رِجَالٞ مِّنَ ٱلۡإِنسِ يَعُوذُونَ بِرِجَالٖ مِّنَ ٱلۡجِنِّ فَزَادُوهُمۡ رَهَقٗا\nАлбатта инсдан бўлган (айрим) кишилар жиндан бўлган кимсалардан паноҳ тилашиб, уларга янада ҳаддан ошишни — муттаҳамликни зиёда қилур эдилар»'}, {'chapter': 72, 'verse': 7, 'text': 'وَأَنَّهُمۡ ظَنُّواْ كَمَا ظَنَنتُمۡ أَن لَّن يَبۡعَثَ ٱللَّهُ أَحَدٗا\n«Албатта улар (яъни, инсонларнинг кофирлари) ҳам худди сизлар гумон қилганингиздек, Аллоҳ ҳаргиз бирон кимсани қайта тирилтирмас, деб гумон қилдилар. (Яъни, сизларнинг гумонларингиз ҳам, уларнинг гумонлари ҳам нотўғридир. Чунки Қуръонда қайта тирилиш ва ҳисоб-китоб бўлиши ҳақ экани тўғрисида айтилди ва бизлар бунга иймон келтирдик)'}, {'chapter': 72, 'verse': 8, 'text': 'وَأَنَّا لَمَسۡنَا ٱلسَّمَآءَ فَوَجَدۡنَٰهَا مُلِئَتۡ حَرَسٗا شَدِيدٗا وَشُهُبٗا\nАлбатта бизлар (фаришталарнинг сўзларини ўғринча эшитиб олиш учун) осмонга тақилган эдик, унинг кучли қўриқчи (фаришта)лар ва (ўғри жинларга отилгувчи учар) юлдузлар билан тўла эканлигини кўрдик'}, {'chapter': 72, 'verse': 9, 'text': 'وَأَنَّا كُنَّا نَقۡعُدُ مِنۡهَا مَقَٰعِدَ لِلسَّمۡعِۖ فَمَن يَسۡتَمِعِ ٱلۡأٓنَ يَجِدۡ لَهُۥ شِهَابٗا رَّصَدٗا\nАлбатта бизлар (илгари осмондан айрим) жойларга (фаришталарнинг сўзларини ўғринча) эшитиб олиш учун ўтириб олар эдик. Энди ҳозир (яъни, Муҳаммад алайҳис-салоту вас-салом пайғамбар қилиб юборилганларидан кейин) эса ким (яъни, қайси жин ўғринча) эшитар бўлса, ўзини кузатиб турган бир (учар) юлдузни кўрар'}, {'chapter': 72, 'verse': 10, 'text': 'وَأَنَّا لَا نَدۡرِيٓ أَشَرٌّ أُرِيدَ بِمَن فِي ٱلۡأَرۡضِ أَمۡ أَرَادَ بِهِمۡ رَبُّهُمۡ رَشَدٗا\nАлбатта бизлар Ердаги кишиларга (Аллоҳ томонидан) ёмонлик ирода қилинганми ёки Парвардигорлари уларга тўғрилик — яхшиликни ирода қилганми, билмасмиз»'}, {'chapter': 72, 'verse': 11, 'text': 'وَأَنَّا مِنَّا ٱلصَّـٰلِحُونَ وَمِنَّا دُونَ ذَٰلِكَۖ كُنَّا طَرَآئِقَ قِدَدٗا\n«Албатта бизларнинг орамизда яхшилар ҳам бордир ва (шунингдек), орамизда ундоқ эмаслар (яъни, ёмонлар) ҳам бордир. Бизлар (Қуръонни эшитишдан илгари) бўлак-бўлак йўлларда эдик'}, {'chapter': 72, 'verse': 12, 'text': 'وَأَنَّا ظَنَنَّآ أَن لَّن نُّعۡجِزَ ٱللَّهَ فِي ٱلۡأَرۡضِ وَلَن نُّعۡجِزَهُۥ هَرَبٗا\nАлбатта бизлар (Қуръонни эшитганимиздан сўнг) билдикки, Ер юзида ҳаргиз Аллоҳни ожиз қила олмасмиз (яъни, фақат Аллоҳ ирода қилган ишгина бўлур) ва У зотдан (яъни, Унинг жазосидан) қочиб ҳам қутула олмасмиз'}, {'chapter': 72, 'verse': 13, 'text': 'وَأَنَّا لَمَّا سَمِعۡنَا ٱلۡهُدَىٰٓ ءَامَنَّا بِهِۦۖ فَمَن يُؤۡمِنۢ بِرَبِّهِۦ فَلَا يَخَافُ بَخۡسٗا وَلَا رَهَقٗا\nАлбатта бизлар қачонки Ҳидоятни — Қуръонни эшитгач, унга иймон келтирдик. Бас, кимки Парвардигорига иймон келтирса, демак у (яхшиликларининг) камайиб қолишидан ҳам, (ёмонликларининг) ҳаддан ортиб кетишидан ҳам қўрқмас (яъни, Парвардигори унга ҳаргиз зулм қилмас, балки қилган ҳар бир амали учун муносиб жазо-мукофот ато этур)'}, {'chapter': 72, 'verse': 14, 'text': 'وَأَنَّا مِنَّا ٱلۡمُسۡلِمُونَ وَمِنَّا ٱلۡقَٰسِطُونَۖ فَمَنۡ أَسۡلَمَ فَأُوْلَـٰٓئِكَ تَحَرَّوۡاْ رَشَدٗا\nАлбатта бизларнинг орамизда мусулмонлар ҳам бордир ва (шунингдек), орамизда (йўлдан) озганлар ҳам бордир. Бас, кимки мусулмон бўлса, демак ана ўшалар Тўғри Йўлни мақсад қилиб олибдилар'}, {'chapter': 72, 'verse': 15, 'text': 'وَأَمَّا ٱلۡقَٰسِطُونَ فَكَانُواْ لِجَهَنَّمَ حَطَبٗا\nЭнди (йўлдан) озганларга келсак, бас, улар жаҳаннам учун ўтин бўлган кимсалардир»'}, {'chapter': 72, 'verse': 16, 'text': 'وَأَلَّوِ ٱسۡتَقَٰمُواْ عَلَى ٱلطَّرِيقَةِ لَأَسۡقَيۡنَٰهُم مَّآءً غَدَقٗا\nАлбатта, агар улар (Тўғри) йўлда устивор турганларида, албатта Биз уларни мўл ёмғир билан суғорган бўлур эдик (яъни, уларга мўл-кўл ризқ ато этган бўлур эдик)'}, {'chapter': 72, 'verse': 17, 'text': 'لِّنَفۡتِنَهُمۡ فِيهِۚ وَمَن يُعۡرِضۡ عَن ذِكۡرِ رَبِّهِۦ يَسۡلُكۡهُ عَذَابٗا صَعَدٗا\nУларни имтиҳон қилиш учун. Кимки Парвардигорининг зикридан — Қуръондан юз ўгирса, У зот уни қаттиқ азобга йўллар'}, {'chapter': 72, 'verse': 18, 'text': 'وَأَنَّ ٱلۡمَسَٰجِدَ لِلَّهِ فَلَا تَدۡعُواْ مَعَ ٱللَّهِ أَحَدٗا\nАлбатта (барча) масжидлар Аллоҳникидир. Бас, (масжидларда) Аллоҳ билан бирга яна бирон кимсага дуо-илтижо қилманглар'}, {'chapter': 72, 'verse': 19, 'text': 'وَأَنَّهُۥ لَمَّا قَامَ عَبۡدُ ٱللَّهِ يَدۡعُوهُ كَادُواْ يَكُونُونَ عَلَيۡهِ لِبَدٗا\nАлбатта, қачонки Аллоҳнинг бандаси (яъни, Муҳаммад алайҳис-салоту вас-салом) У зотга дуо-илтижо қилган ҳолда турганида, улар (яъни, жинлар Қуръон эшитиш иштиёқида) унинг устига ғуж-ғуж бўлиб ёпирилишга яқин бўлдилар'}, {'chapter': 72, 'verse': 20, 'text': 'قُلۡ إِنَّمَآ أَدۡعُواْ رَبِّي وَلَآ أُشۡرِكُ بِهِۦٓ أَحَدٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом, динингиздан қайтишингизни талаб қилаётган кофирларга) айтинг: «Мен ёлғиз Парвардигоримгагина дуо-илтижо қилурман ва У зотга бирон кимсани шерик қилмасман»'}, {'chapter': 72, 'verse': 21, 'text': 'قُلۡ إِنِّي لَآ أَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا رَشَدٗا\nАйтинг: «Албатта мен сизларга на бир зиён ва на бир тўғри йўл беришга эга эмасдирман, (Тўғри Йўлга солгувчи ҳам, зиён етказа олгувчи ҳам ёлғиз Аллоҳдир)»'}, {'chapter': 72, 'verse': 22, 'text': 'قُلۡ إِنِّي لَن يُجِيرَنِي مِنَ ٱللَّهِ أَحَدٞ وَلَنۡ أَجِدَ مِن دُونِهِۦ مُلۡتَحَدًا\nАйтинг: «Албатта мени Аллоҳ(нинг азоби)дан (агар У зотга осийлик қилсам), бирон кимса ҳимоя қила олмас ва мен У зотдан ўзга паноҳ ҳам топа олмасман'}, {'chapter': 72, 'verse': 23, 'text': 'إِلَّا بَلَٰغٗا مِّنَ ٱللَّهِ وَرِسَٰلَٰتِهِۦۚ وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدًا\n(Мен) фақат Аллоҳ томонидан етказишга ва У зотнинг элчилик вазифаларигагина (эгадирман). Кимки Аллоҳ ва Унинг пайғамбарига осий бўлса, бас, албатта унинг учун жаҳаннам олови бордир. (Ундай кимсалар) ўша жойда мангу қолурлар'}, {'chapter': 72, 'verse': 24, 'text': 'حَتَّىٰٓ إِذَا رَأَوۡاْ مَا يُوعَدُونَ فَسَيَعۡلَمُونَ مَنۡ أَضۡعَفُ نَاصِرٗا وَأَقَلُّ عَدَدٗا\nЭнди қачон улар ўзларига ваъда қилинаётган азобни кўрганларида, бас, (ўша пайтда) кимнинг (яъни, уларнингми ёки мўминларнингми) ёрдамчиси ожизроқ ва саноғи ҳам озроқ эканини билажаклар!»'}, {'chapter': 72, 'verse': 25, 'text': 'قُلۡ إِنۡ أَدۡرِيٓ أَقَرِيبٞ مَّا تُوعَدُونَ أَمۡ يَجۡعَلُ لَهُۥ رَبِّيٓ أَمَدًا\n(Эй Муҳаммад алайҳис-салоту вас-салом, у кофирларга) айтинг: «Мен сизларга ваъда қилинаётган азоб яқинми ёки Парвардигорим унинг учун (узоқроқ) муҳлат қилганми, билмасман'}, {'chapter': 72, 'verse': 26, 'text': 'عَٰلِمُ ٱلۡغَيۡبِ فَلَا يُظۡهِرُ عَلَىٰ غَيۡبِهِۦٓ أَحَدًا\n(У зот) ғайбни билгувчидир. Бас, Ўз ғайбидан бирон кимсани огоҳ қилмас'}, {'chapter': 72, 'verse': 27, 'text': 'إِلَّا مَنِ ٱرۡتَضَىٰ مِن رَّسُولٖ فَإِنَّهُۥ يَسۡلُكُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ رَصَدٗا\nФақат Ўзи рози бўлган пайғамбарнигина (Ўзининг ғайбидан сир-асрорининг айримларидан огоҳ этар). Бас, албатта У (Аллоҳ Ўзининг ғайбидан огоҳ қилиб қўйган ҳар бир пайғамбарнинг) олдидан ҳам, ортидан ҳам кузатгувчи (фаришта) йўллар'}, {'chapter': 72, 'verse': 28, 'text': 'لِّيَعۡلَمَ أَن قَدۡ أَبۡلَغُواْ رِسَٰلَٰتِ رَبِّهِمۡ وَأَحَاطَ بِمَا لَدَيۡهِمۡ وَأَحۡصَىٰ كُلَّ شَيۡءٍ عَدَدَۢا\nТоки (пайғамбарлар) Парвардигорларининг элчилик вазифаларини (ўз умматларига) тўла етказганларини билиш учун (кузатгувчи фаришта йўллар). У зот (пайғамбарлар) ҳузуридаги бор нарсани (яъни, уларнинг илму амалларини) иҳота қилиб олгандир ва (коинотдаги) ҳар бир нарсанинг саноғини ҳисоб-китоб қилиб қўйгандир»'}, {'chapter': 73, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلۡمُزَّمِّلُ\nЭй (кийимларига) ўралиб олган зот'}, {'chapter': 73, 'verse': 2, 'text': 'قُمِ ٱلَّيۡلَ إِلَّا قَلِيلٗا\nкечаси (бедор бўлиб, намозда) туринг! Фақат озгина (ухлаб ором олинг)'}, {'chapter': 73, 'verse': 3, 'text': 'نِّصۡفَهُۥٓ أَوِ ٱنقُصۡ مِنۡهُ قَلِيلًا\nУнинг (кечанинг) ярмида (ухлаб ором олинг) ёки (уйқуни кечанинг) ярмидан ҳам бир оз камайтиринг'}, {'chapter': 73, 'verse': 4, 'text': 'أَوۡ زِدۡ عَلَيۡهِ وَرَتِّلِ ٱلۡقُرۡءَانَ تَرۡتِيلًا\nЁхуд унга (бир оз) зиёда қилинг (яъни, кечанинг ярмидан кўпроғида ухлаб, истироҳат қилинг) ва Қуръонни тартил билан (яъни, дона-дона қилиб) тиловат қилинг'}, {'chapter': 73, 'verse': 5, 'text': 'إِنَّا سَنُلۡقِي عَلَيۡكَ قَوۡلٗا ثَقِيلًا\nЗеро, Биз сизга оғир Сўзни — Қуръонни туширажакмиз'}, {'chapter': 73, 'verse': 6, 'text': 'إِنَّ نَاشِئَةَ ٱلَّيۡلِ هِيَ أَشَدُّ وَطۡـٔٗا وَأَقۡوَمُ قِيلًا\nАлбатта кечаси (ибодат учун бедор бўлиб) туриш (тун уйқу-ором вақти бўлгани сабабли) жуда оғир юкдир ва (лекин у пайтда кундузги безовталиклар бўлмагани учун) энг тўғри сўздир'}, {'chapter': 73, 'verse': 7, 'text': 'إِنَّ لَكَ فِي ٱلنَّهَارِ سَبۡحٗا طَوِيلٗا\nАлбатта, сиз учун кундузи узун-тинимсиз машғулот бордир (шу сабабли кечалари бедор бўлиб ибодат қилинг)'}, {'chapter': 73, 'verse': 8, 'text': 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ وَتَبَتَّلۡ إِلَيۡهِ تَبۡتِيلٗا\nПарвардигорингизнинг номини (мудом) ёд этинг ва Унга бутунлай берилиб, (чин ихлос билан бандалик қилинг)'}, {'chapter': 73, 'verse': 9, 'text': 'رَّبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ لَآ إِلَٰهَ إِلَّا هُوَ فَٱتَّخِذۡهُ وَكِيلٗا\n(У) машриқ ва мағрибнинг Парвардигоридир. Ҳеч қандай илоҳ йўқ, факат Унинг Ўзигина бордир. Бас, сиз (ўзингиз учун) Унигина вакил-ҳомий қилиб олинг'}, {'chapter': 73, 'verse': 10, 'text': 'وَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَٱهۡجُرۡهُمۡ هَجۡرٗا جَمِيلٗا\nВа улар (мушриклар) айтаётган сўзларига (бераётган озорларига) сабр қилинг ҳамда уларни чиройли тарк этинг'}, {'chapter': 73, 'verse': 11, 'text': 'وَذَرۡنِي وَٱلۡمُكَذِّبِينَ أُوْلِي ٱلنَّعۡمَةِ وَمَهِّلۡهُمۡ قَلِيلًا\n(Сизни) ёлғончи қилгувчи у аҳли неъмат-аҳли дунёларни менга қўйиб беринг ва уларга озгина (Қиёматгача) муҳлат беринг'}, {'chapter': 73, 'verse': 12, 'text': 'إِنَّ لَدَيۡنَآ أَنكَالٗا وَجَحِيمٗا\nЗеро, Бизнинг даргоҳимизда (кофирлар учун тайёрлаб қўйилган) кишанлар ва (уларни куйдириб азоб бергувчи) олов-дўзах бордир'}, {'chapter': 73, 'verse': 13, 'text': 'وَطَعَامٗا ذَا غُصَّةٖ وَعَذَابًا أَلِيمٗا\nВа (томоққа) тиқилгувчи (йиринг ва қон каби) «таом» ҳамда аламли азоб бордир'}, {'chapter': 73, 'verse': 14, 'text': 'يَوۡمَ تَرۡجُفُ ٱلۡأَرۡضُ وَٱلۡجِبَالُ وَكَانَتِ ٱلۡجِبَالُ كَثِيبٗا مَّهِيلًا\nУ Кунда Ер ва тоғлар титроққа тушар ва у тоғлар тўкилиб тургувчи қумтепаларга айланиб қолур'}, {'chapter': 73, 'verse': 15, 'text': 'إِنَّآ أَرۡسَلۡنَآ إِلَيۡكُمۡ رَسُولٗا شَٰهِدًا عَلَيۡكُمۡ كَمَآ أَرۡسَلۡنَآ إِلَىٰ فِرۡعَوۡنَ رَسُولٗا\n(Эй Макка аҳли), дарҳақиқат, Биз худди Фиръавнга Пайғамбар юборганимиз каби сизларга ҳам (агар бу ҳаёти дунёдан куфру исён билан ўтсангизлар Қиёмат Кунида) сизларнинг зиёнингизга гувоҳлик бергувчи бўлган бир Пайғамбарни (яъни, Муҳаммад алайҳис-салоту вас-саломни) юбордик'}, {'chapter': 73, 'verse': 16, 'text': 'فَعَصَىٰ فِرۡعَوۡنُ ٱلرَّسُولَ فَأَخَذۡنَٰهُ أَخۡذٗا وَبِيلٗا\n(Ўшанда) Фиръавн (Мусо) Пайғамбарга итоатсизлик қилгач, Биз уни қаттиқ ушлаш билан ушладик (яъни, денгизга ғарқ қилиб юбордик)'}, {'chapter': 73, 'verse': 17, 'text': 'فَكَيۡفَ تَتَّقُونَ إِن كَفَرۡتُمۡ يَوۡمٗا يَجۡعَلُ ٱلۡوِلۡدَٰنَ شِيبًا\nБас, агар сизлар кофир бўлсангизлар (ўз даҳшати билан) болаларни(нг сочларини оқартириб) чолларга айлантириб қўядиган Кун(нинг азоби)дан қандай сақланурсизлар'}, {'chapter': 73, 'verse': 18, 'text': 'ٱلسَّمَآءُ مُنفَطِرُۢ بِهِۦۚ كَانَ وَعۡدُهُۥ مَفۡعُولًا\nУ (даҳшатли Кун) сабабли осмон ҳам ёрилгувчидир! (Аллоҳнинг) ваъдаси амалга ошгувчидир'}, {'chapter': 73, 'verse': 19, 'text': 'إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا\nАлбатта ушбу (оятлар) бир эслатмадир. Бас, ким хоҳласа (бу насиҳатдан ибрат олиб) Парвардигорига йўл тутар'}, {'chapter': 73, 'verse': 20, 'text': '۞إِنَّ رَبَّكَ يَعۡلَمُ أَنَّكَ تَقُومُ أَدۡنَىٰ مِن ثُلُثَيِ ٱلَّيۡلِ وَنِصۡفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٞ مِّنَ ٱلَّذِينَ مَعَكَۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيۡلَ وَٱلنَّهَارَۚ عَلِمَ أَن لَّن تُحۡصُوهُ فَتَابَ عَلَيۡكُمۡۖ فَٱقۡرَءُواْ مَا تَيَسَّرَ مِنَ ٱلۡقُرۡءَانِۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرۡضَىٰ وَءَاخَرُونَ يَضۡرِبُونَ فِي ٱلۡأَرۡضِ يَبۡتَغُونَ مِن فَضۡلِ ٱللَّهِ وَءَاخَرُونَ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِۖ فَٱقۡرَءُواْ مَا تَيَسَّرَ مِنۡهُۚ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَقۡرِضُواْ ٱللَّهَ قَرۡضًا حَسَنٗاۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيۡرٗا وَأَعۡظَمَ أَجۡرٗاۚ وَٱسۡتَغۡفِرُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمُۢ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Парвардигорингиз сиз ва сиз билан бирга бўлган кишилардан иборат тоифа (яъни, саҳобаларингиз гоҳо) кечанинг учдан иккисидан озроғида, (гоҳо) унинг ярмида ва (гоҳо) учдан бирида (бедор бўлиб, намозда) туришларингизни билур. Кеча ва кундуз (соатлари)ни Аллоҳ белгилар. У зот (фасллар ўзгариши билан кечалар ҳам узун-қисқа бўлиб туриши сабабли сизлар у кечаларда бедор бўлишларингиз лозим бўлган соатларнинг) саноғига ета олмасликларингизни билиб, сизларга қайта енгиллик берди. — Энди (кечалари намозларингизда) Қуръондан муяссар бўлган миқдорда ўқийверинглар. У зот сизларнинг орангизда (кечалари бедор бўлишга қуввати етмайдиган) беморлар бўлишини, бошқалар Аллоҳнинг фазл-марҳаматидан (ризқ-рўз) истаб Ер юзида (яъни, унинг турли томонларига) сафар қилишларини ва яна бошқалар эса Аллоҳ йўлида жангга чиқиб кетишларини билди. Бас, (ўзларингизни қийнамай Қуръондан) муяссар бўлган миқдорда ўқийверинглар. Ва намозни тўкис адо этинглар, закотни (ҳақдорларга) ато этинглар ва (бечора мискинларга хайру саховат қилиш билан) Аллоҳга қарзи ҳасана беринглар! Ўзларингиз учун тақдим қиладиган ҳар бир яхшиликни (Қиёмат Куни) Аллоҳнинг ҳузурида янада яхшироқ ва улуғроқ ажр-мукофот ҳолида топурсизлар. Аллоҳдан мағфират сўранглар! Албатта Аллоҳ мағфиратли, меҳрибондир'}, {'chapter': 74, 'verse': 1, 'text': 'يَـٰٓأَيُّهَا ٱلۡمُدَّثِّرُ\nЭй (либосларига) бурканиб олган зот'}, {'chapter': 74, 'verse': 2, 'text': 'قُمۡ فَأَنذِرۡ\nТуринг-да, (инсонларни Охират азобидан) огоҳлантиринг'}, {'chapter': 74, 'verse': 3, 'text': 'وَرَبَّكَ فَكَبِّرۡ\nЁлғиз Парвардигорингизни улуғланг'}, {'chapter': 74, 'verse': 4, 'text': 'وَثِيَابَكَ فَطَهِّرۡ\nЛибосларингизни пок тутинг'}, {'chapter': 74, 'verse': 5, 'text': 'وَٱلرُّجۡزَ فَٱهۡجُرۡ\nБутлардан йироқ бўлинг'}, {'chapter': 74, 'verse': 6, 'text': 'وَلَا تَمۡنُن تَسۡتَكۡثِرُ\n(Бераётган нарсангизни) кўп санаган ҳолингизда эҳсон қилманг'}, {'chapter': 74, 'verse': 7, 'text': 'وَلِرَبِّكَ فَٱصۡبِرۡ\nЁлғиз Парвардигорингиз (Юзи) учун (кофирлар томонидан етадиган озорларга) сабр қилинг'}, {'chapter': 74, 'verse': 8, 'text': 'فَإِذَا نُقِرَ فِي ٱلنَّاقُورِ\nЧунки қачон (Қиёмат қойим бўлгани ҳақида хабар бериб) бурғу чалинганида'}, {'chapter': 74, 'verse': 9, 'text': 'فَذَٰلِكَ يَوۡمَئِذٖ يَوۡمٌ عَسِيرٌ\nана ўша Кун қийин Кундир'}, {'chapter': 74, 'verse': 10, 'text': 'عَلَى ٱلۡكَٰفِرِينَ غَيۡرُ يَسِيرٖ\nКофирларга осон бўлмаган (Кундир)'}, {'chapter': 74, 'verse': 11, 'text': 'ذَرۡنِي وَمَنۡ خَلَقۡتُ وَحِيدٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), Мен сўққабош ҳолида яратган кимсани Ўзимга қўйиб беринг'}, {'chapter': 74, 'verse': 12, 'text': 'وَجَعَلۡتُ لَهُۥ مَالٗا مَّمۡدُودٗا\nМен унга кенг-мўл мол-давлат'}, {'chapter': 74, 'verse': 13, 'text': 'وَبَنِينَ شُهُودٗا\nва (доимо ёнида) ҳозиру нозир ўғилларни (ато) қилдим'}, {'chapter': 74, 'verse': 14, 'text': 'وَمَهَّدتُّ لَهُۥ تَمۡهِيدٗا\nЯна унга (ҳаётнинг барча неъматларини) қулай-осон қилиб қўйдим'}, {'chapter': 74, 'verse': 15, 'text': 'ثُمَّ يَطۡمَعُ أَنۡ أَزِيدَ\nСўнгра у яна зиёда қилишимни тамаъ қилур'}, {'chapter': 74, 'verse': 16, 'text': 'كَلَّآۖ إِنَّهُۥ كَانَ لِأٓيَٰتِنَا عَنِيدٗا\nЙўқ, (унинг тамаъси беҳудадир)! Чунки у Бизнинг оятларимизга қаршилик қилгувчидир'}, {'chapter': 74, 'verse': 17, 'text': 'سَأُرۡهِقُهُۥ صَعُودًا\nЯқинда Мен уни бир харсанг тошга (яъни, ўтиб бўлмас тўсиққа) дучор қилажакман'}, {'chapter': 74, 'verse': 18, 'text': 'إِنَّهُۥ فَكَّرَ وَقَدَّرَ\nЧунки у (Қуръонни эшитгач, бу илоҳий Китобга қандай туҳмат қилиш тўғрисида) ўйлади, режа тузди'}, {'chapter': 74, 'verse': 19, 'text': 'فَقُتِلَ كَيۡفَ قَدَّرَ\nҲалок қилингур, қандай режа тузди-я'}, {'chapter': 74, 'verse': 20, 'text': 'ثُمَّ قُتِلَ كَيۡفَ قَدَّرَ\nЯна ҳалок қилингур, қандай режа тузди-я'}, {'chapter': 74, 'verse': 21, 'text': 'ثُمَّ نَظَرَ\nСўнгра у (ўйлаган режаларига) қаради'}, {'chapter': 74, 'verse': 22, 'text': 'ثُمَّ عَبَسَ وَبَسَرَ\nСўнгра (Қуръондан бирон айб топа олмагач, пешонасини) тириштирди ва (афтини) буриштирди'}, {'chapter': 74, 'verse': 23, 'text': 'ثُمَّ أَدۡبَرَ وَٱسۡتَكۡبَرَ\nСўнгра (Ҳақдан) юз ўгирди ва кибр-ҳаво қилди'}, {'chapter': 74, 'verse': 24, 'text': 'فَقَالَ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ يُؤۡثَرُ\nБас, у: «Бу (Қуръон Аллоҳнинг сўзи эмас, балки) фақат (аввалгилардан нақл қилинаётган бир сеҳрдир)'}, {'chapter': 74, 'verse': 25, 'text': 'إِنۡ هَٰذَآ إِلَّا قَوۡلُ ٱلۡبَشَرِ\nБу фақат башарнинг сўзидир», деди'}, {'chapter': 74, 'verse': 26, 'text': 'سَأُصۡلِيهِ سَقَرَ\nЯқинда Мен уни Сақарга (жаҳаннамга) киритажакман'}, {'chapter': 74, 'verse': 27, 'text': 'وَمَآ أَدۡرَىٰكَ مَا سَقَرُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Сақарнинг нима эканлигини сиз қаердан ҳам билар эдингиз'}, {'chapter': 74, 'verse': 28, 'text': 'لَا تُبۡقِي وَلَا تَذَرُ\nУ (бирон кофирни) қолдирмас — қўймас (балки куйдириб ҳалок қилур)'}, {'chapter': 74, 'verse': 29, 'text': 'لَوَّاحَةٞ لِّلۡبَشَرِ\n(У беш юз йиллик масофадан) инсонларга яққол кўриниб тургувчидир'}, {'chapter': 74, 'verse': 30, 'text': 'عَلَيۡهَا تِسۡعَةَ عَشَرَ\nУнинг устида ўн тўққиз (фаришта қўриқчилик-эгалик қилур)'}, {'chapter': 74, 'verse': 31, 'text': 'وَمَا جَعَلۡنَآ أَصۡحَٰبَ ٱلنَّارِ إِلَّا مَلَـٰٓئِكَةٗۖ وَمَا جَعَلۡنَا عِدَّتَهُمۡ إِلَّا فِتۡنَةٗ لِّلَّذِينَ كَفَرُواْ لِيَسۡتَيۡقِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَيَزۡدَادَ ٱلَّذِينَ ءَامَنُوٓاْ إِيمَٰنٗا وَلَا يَرۡتَابَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَٱلۡمُؤۡمِنُونَ وَلِيَقُولَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡكَٰفِرُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَمَا يَعۡلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَۚ وَمَا هِيَ إِلَّا ذِكۡرَىٰ لِلۡبَشَرِ\nБиз фақат фаришталарни дўзах эгалари-қўриқчилари қилдик ва Биз фақат кофир бўлган кимсаларни синаш учун у (фаришта)ларнинг саноғини (ўн тўққизта) қилдик. Токи китоб берилган кимсалар (яъни, яҳудий ва насронийлар ўзларининг илоҳий китоблари бўлмиш Таврот ва Инжилда ҳам дўзах қўриқчилари бўлган фаришталар саноғи ўн тўққизта экани айтилганини кўриб Қуръоннинг Аллоҳ томонидан нозил қилинган Ҳақ Китоб эканлигини) аниқ билгайлар ва иймон келтирган зотларнинг иймонлари эса янада зиёда бўлгай ҳамда китоб берилган кимсалар ҳам, мўминлар ҳам (дўзах қўриқчиларининг саноғи тўғрисида) шак-шубҳа қилмагайлар. Ва токи дилларида мараз (мунофиқлик) бўлган кимсалар ва кофирлар: «Буни мисол қилиш билан (яъни, фаришталар саноғини ўн тўққизта қилиш билан) Аллоҳ нима демоқчи?», дегайлар. Аллоҳ Ўзи хоҳлаган кимсаларни мана шундай йўлдан оздириб қўюр ва Ўзи хоҳлаган кишиларни ҳидоят қилур. Парвардигорингизнинг қўшинларини (яъни, фаришталарнинг қанча ададда ва қандай сифатда эканликларини) ёлғиз Унинг Ўзигина билур. У (яъни, ушбу оятларда таърифланган жаҳаннам) фақат инсонлар (панд-насиҳат олишлари) учун бир эслатмадир'}, {'chapter': 74, 'verse': 32, 'text': 'كَلَّا وَٱلۡقَمَرِ\nДарҳақиқат, Ойга қасам'}, {'chapter': 74, 'verse': 33, 'text': 'وَٱلَّيۡلِ إِذۡ أَدۡبَرَ\nЎтиб кетган тунга қасам'}, {'chapter': 74, 'verse': 34, 'text': 'وَٱلصُّبۡحِ إِذَآ أَسۡفَرَ\nЁришиб келаётган тонгга қасамки'}, {'chapter': 74, 'verse': 35, 'text': 'إِنَّهَا لَإِحۡدَى ٱلۡكُبَرِ\nАлбатта у (жаҳаннам) чўнг (бало)лардан биридир'}, {'chapter': 74, 'verse': 36, 'text': 'نَذِيرٗا لِّلۡبَشَرِ\n(Барча) инсонлар учун огоҳлантиргувчи (бўлган чўнг балолардан биридир)'}, {'chapter': 74, 'verse': 37, 'text': 'لِمَن شَآءَ مِنكُمۡ أَن يَتَقَدَّمَ أَوۡ يَتَأَخَّرَ\n— Сизларнинг орангизда (яхшилик ва тоат-ибодат томонга) илгарилаётган ёки (куфру исён томонига) чекинаётган кишилар учун (огоҳлантиргувчи бўлган чўнг балолардан биридир)'}, {'chapter': 74, 'verse': 38, 'text': 'كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ رَهِينَةٌ\nҲар бир жон ўзи (ҳаёти-дунёда) касб қилган амали сабабли (дўзахда) ушлангувчидир'}, {'chapter': 74, 'verse': 39, 'text': 'إِلَّآ أَصۡحَٰبَ ٱلۡيَمِينِ\nФақат ўнг қўл эгаларигина (яъни, ҳаёти-дунёда иймон келтирганлари ва эзгу амаллар қилганлари сабабли Қиёмат Кунида номаи аъмоллари ўнг қўлларидан берилган саодатманд зотларгина дўзахдан) нажот топгувчидирлар'}, {'chapter': 74, 'verse': 40, 'text': 'فِي جَنَّـٰتٖ يَتَسَآءَلُونَ\nУлар жаннатларда бир-бирлари билан савол-жавоб қилишурлар'}, {'chapter': 74, 'verse': 41, 'text': 'عَنِ ٱلۡمُجۡرِمِينَ\n(Дўзахга ташланган) жиноятчи-кофир ҳақида'}, {'chapter': 74, 'verse': 42, 'text': 'مَا سَلَكَكُمۡ فِي سَقَرَ\n(Улар дўзах аҳлига): «Сизларни нима Сақарга киритди?», (деганларида)'}, {'chapter': 74, 'verse': 43, 'text': 'قَالُواْ لَمۡ نَكُ مِنَ ٱلۡمُصَلِّينَ\nУлар айтурлар: «Бизлар намоз ўқигувчилардан бўлмадик'}, {'chapter': 74, 'verse': 44, 'text': 'وَلَمۡ نَكُ نُطۡعِمُ ٱلۡمِسۡكِينَ\nМискин-бечорага таом бергувчи ҳам бўлмадик'}, {'chapter': 74, 'verse': 45, 'text': 'وَكُنَّا نَخُوضُ مَعَ ٱلۡخَآئِضِينَ\nБизлар (ботил-беҳуда сўзларга) шўнғигувчи кимсалар билан бирга шўнғир эдик (яъни, Қуръон ва пайғамбар хусусида туҳмат-ёлғонлар тўқир эдик)'}, {'chapter': 74, 'verse': 46, 'text': 'وَكُنَّا نُكَذِّبُ بِيَوۡمِ ٱلدِّينِ\nБизлар Жазо — Қиёмат Кунини ёлғон дер эдик'}, {'chapter': 74, 'verse': 47, 'text': 'حَتَّىٰٓ أَتَىٰنَا ٱلۡيَقِينُ\nТо бизларга аниқ (ўлим) келгунича»'}, {'chapter': 74, 'verse': 48, 'text': 'فَمَا تَنفَعُهُمۡ شَفَٰعَةُ ٱلشَّـٰفِعِينَ\nЭнди оқловчиларнинг оқлови уларга фойда бермас'}, {'chapter': 74, 'verse': 49, 'text': 'فَمَا لَهُمۡ عَنِ ٱلتَّذۡكِرَةِ مُعۡرِضِينَ\nНега улар бу Эслатмадан — Қуръондан юз ўгирадилар-а'}, {'chapter': 74, 'verse': 50, 'text': 'كَأَنَّهُمۡ حُمُرٞ مُّسۡتَنفِرَةٞ\nХудди ёввойи эшакларга'}, {'chapter': 74, 'verse': 51, 'text': 'فَرَّتۡ مِن قَسۡوَرَةِۭ\nШердан қочган (ёввойи эшакларга ўхшаб Қуръондан юз ўгирадилар-а)'}, {'chapter': 74, 'verse': 52, 'text': 'بَلۡ يُرِيدُ كُلُّ ٱمۡرِيٕٖ مِّنۡهُمۡ أَن يُؤۡتَىٰ صُحُفٗا مُّنَشَّرَةٗ\nЙўқ, (улар ҳеч қачон Қуръон оятларига қулоқ тутмаслар, балки) улардан ҳар бир кимса ўзига очиқ саҳифалар берилишини истар'}, {'chapter': 74, 'verse': 53, 'text': 'كَلَّاۖ بَل لَّا يَخَافُونَ ٱلۡأٓخِرَةَ\nЙўқ, (уларнинг кофир бўлишларига сабаб, асло уларга очиқ саҳифалар берилмагани эмас), балки улар Охират(га иймон келтирмайдилар ва унда бўладиган азоб-уқубат)дан қўрқмаслар (ва шу сабабдан Қуръондан юз ўгирурлар)'}, {'chapter': 74, 'verse': 54, 'text': 'كَلَّآ إِنَّهُۥ تَذۡكِرَةٞ\nҲа, албатта у (Қуръон) бир эслатмадир'}, {'chapter': 74, 'verse': 55, 'text': 'فَمَن شَآءَ ذَكَرَهُۥ\nБас, хоҳлаган киши ундан эслатма-ибрат олур'}, {'chapter': 74, 'verse': 56, 'text': 'وَمَا يَذۡكُرُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ هُوَ أَهۡلُ ٱلتَّقۡوَىٰ وَأَهۡلُ ٱلۡمَغۡفِرَةِ\nУлар ёлғиз Аллоҳ хоҳлаганидагина (Қуръондан) эслатма-ибрат олурлар. У (Аллоҳ) тақво эгасидир (яъни, бандалари Ундан тақво қилишлари — қўрқишлари лозимдир) ва (У Аллоҳ тақво қилгувчи бандалари учун) мағфират Эгасидир'}, {'chapter': 75, 'verse': 1, 'text': 'لَآ أُقۡسِمُ بِيَوۡمِ ٱلۡقِيَٰمَةِ\nМен Қиёмат Кунига қасам ичурман'}, {'chapter': 75, 'verse': 2, 'text': 'وَلَآ أُقۡسِمُ بِٱلنَّفۡسِ ٱللَّوَّامَةِ\nВа Мен маломатгўй нафсга қасам ичурманки, (ҳеч шак-шубҳасиз, қайта тирилиб, ҳисоб-китоб қилинурсизлар)'}, {'chapter': 75, 'verse': 3, 'text': 'أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَلَّن نَّجۡمَعَ عِظَامَهُۥ\nИнсон Бизни унинг (чириб, тупроққа айланиб кетган) суякларини ҳаргиз тўплай олмас, деб гумон қилурми'}, {'chapter': 75, 'verse': 4, 'text': 'بَلَىٰ قَٰدِرِينَ عَلَىٰٓ أَن نُّسَوِّيَ بَنَانَهُۥ\nЙўқ, Биз унинг бармоқларини (ҳам) тиклаб жойига келтиришга қодир бўлган Зотдирмиз'}, {'chapter': 75, 'verse': 5, 'text': 'بَلۡ يُرِيدُ ٱلۡإِنسَٰنُ لِيَفۡجُرَ أَمَامَهُۥ\nБалки инсон олдинда (келгуси ҳаётида ҳам) фисқ-фужур (гуноҳ амалларни) қилмоқни истаб'}, {'chapter': 75, 'verse': 6, 'text': 'يَسۡـَٔلُ أَيَّانَ يَوۡمُ ٱلۡقِيَٰمَةِ\n«Қиёмат Куни қачон ўзи?», деб сўрар'}, {'chapter': 75, 'verse': 7, 'text': 'فَإِذَا بَرِقَ ٱلۡبَصَرُ\nБас, (кўрган даҳшатларидан) кўз қамашиб'}, {'chapter': 75, 'verse': 8, 'text': 'وَخَسَفَ ٱلۡقَمَرُ\n(осмондаги) ой ҳам тутилган ҳолда'}, {'chapter': 75, 'verse': 9, 'text': 'وَجُمِعَ ٱلشَّمۡسُ وَٱلۡقَمَرُ\nқуёш ва ой бирлаштирилган вақтда'}, {'chapter': 75, 'verse': 10, 'text': 'يَقُولُ ٱلۡإِنسَٰنُ يَوۡمَئِذٍ أَيۡنَ ٱلۡمَفَرُّ\n— ана ўша Кунда у инсон «(у бало-офатлардан) қаерга қочиб-қутулиш мумкин?», деб қолур'}, {'chapter': 75, 'verse': 11, 'text': 'كَلَّا لَا وَزَرَ\nЙўқ! (Эй инсон, Аллоҳнинг азобидан қочиб қутуладиган) бирон бошпана йўқдир'}, {'chapter': 75, 'verse': 12, 'text': 'إِلَىٰ رَبِّكَ يَوۡمَئِذٍ ٱلۡمُسۡتَقَرُّ\nУ Кунда ёлғиз Парвардигоринг (ҳукм қилган жой)га қарор топишгина бордир'}, {'chapter': 75, 'verse': 13, 'text': 'يُنَبَّؤُاْ ٱلۡإِنسَٰنُ يَوۡمَئِذِۭ بِمَا قَدَّمَ وَأَخَّرَ\nУ Кунда инсонга (ҳаёти-дунёдалик чоғида) қилиб ўтган ва (ўзидан кейин) қолдирган (барча) нарсаларнинг (яъни, яхши-ёмон амалларнинг) хабари берилур'}, {'chapter': 75, 'verse': 14, 'text': 'بَلِ ٱلۡإِنسَٰنُ عَلَىٰ نَفۡسِهِۦ بَصِيرَةٞ\nБалки (У Кунда) инсон(нинг барча аъзолари) ўзининг зиёнига гувоҳлик бергувчидир'}, {'chapter': 75, 'verse': 15, 'text': 'وَلَوۡ أَلۡقَىٰ مَعَاذِيرَهُۥ\nГарчи ўз узр-баҳоналарини (ўртага) ташласа — келтирса-да, (унинг барча аъзолари ўзининг зиёнига гувоҳлик бергувчидир)'}, {'chapter': 75, 'verse': 16, 'text': 'لَا تُحَرِّكۡ بِهِۦ لِسَانَكَ لِتَعۡجَلَ بِهِۦٓ\n(Эй Муҳаммад алайҳис-салоту вас-салом, Қуръонни) тезроқ (ёдлаб) олиш учун тилингизни у билан қимирлатмай-пичирламай қўя қолинг'}, {'chapter': 75, 'verse': 17, 'text': 'إِنَّ عَلَيۡنَا جَمۡعَهُۥ وَقُرۡءَانَهُۥ\nЗеро, уни (сизнинг дилингизда) жамлаш ҳам, (тилингизда) қироат қилдириш ҳам Бизнинг зиммамиздадир'}, {'chapter': 75, 'verse': 18, 'text': 'فَإِذَا قَرَأۡنَٰهُ فَٱتَّبِعۡ قُرۡءَانَهُۥ\nБас, қачон Биз (яъни, Жаброил фаришта) уни (яъни, ҳар бир ваҳийни) ўқиб битганимиздан сўнггина сиз ҳам уни ўқишга эргашинг'}, {'chapter': 75, 'verse': 19, 'text': 'ثُمَّ إِنَّ عَلَيۡنَا بَيَانَهُۥ\nСўнгра уни — Қуръонни баён қилиб бериш ҳам албатта Бизнинг зиммамиздадир'}, {'chapter': 75, 'verse': 20, 'text': 'كَلَّا بَلۡ تُحِبُّونَ ٱلۡعَاجِلَةَ\nЙўқ, (эй Макка мушриклари, сизлар гумон қилгандек қайта тирилиш ва ҳисоб-китоб йўқ нарсалар эмасдир)! Балки сизлар нақд (дунё)ни яхши кўрурсизлар'}, {'chapter': 75, 'verse': 21, 'text': 'وَتَذَرُونَ ٱلۡأٓخِرَةَ\nОхират (Куни)ни эса тарк қилурсизлар (яъни, Охират учун амал қилмайсизлар)'}, {'chapter': 75, 'verse': 22, 'text': 'وُجُوهٞ يَوۡمَئِذٖ نَّاضِرَةٌ\nУ Кунда (мўминларнинг) юзлари яшнаб'}, {'chapter': 75, 'verse': 23, 'text': 'إِلَىٰ رَبِّهَا نَاظِرَةٞ\nПарвардигорларига боқиб тургувчидир'}, {'chapter': 75, 'verse': 24, 'text': 'وَوُجُوهٞ يَوۡمَئِذِۭ بَاسِرَةٞ\nВа у Кунда (кофирларнинг) юзлари буришиб'}, {'chapter': 75, 'verse': 25, 'text': 'تَظُنُّ أَن يُفۡعَلَ بِهَا فَاقِرَةٞ\nўзларига албатта белни синдиргувчи (бирон бало) қилинишини ўйлаб-кутиб қолур'}, {'chapter': 75, 'verse': 26, 'text': 'كَلَّآ إِذَا بَلَغَتِ ٱلتَّرَاقِيَ\nДарҳақиқат, (жон) ҳалқумга етган'}, {'chapter': 75, 'verse': 27, 'text': 'وَقِيلَ مَنۡۜ رَاقٖ\n(вафот қилаётган кимсанинг атрофидагилар томонидан): «Дам солиб-ўқиб қўйгувчи бирон киши борми?», деб қолинган'}, {'chapter': 75, 'verse': 28, 'text': 'وَظَنَّ أَنَّهُ ٱلۡفِرَاقُ\n(жон таслим қилаётган кимсанинг) ўзи (бу ҳолатнинг ҳаёти-дунёдан) ажралиш эканини англаган'}, {'chapter': 75, 'verse': 29, 'text': 'وَٱلۡتَفَّتِ ٱلسَّاقُ بِٱلسَّاقِ\nва (жон бериш қийинлигидан) оёқ оёққа чалмашиб қолган бир вақтда'}, {'chapter': 75, 'verse': 30, 'text': 'إِلَىٰ رَبِّكَ يَوۡمَئِذٍ ٱلۡمَسَاقُ\n— ана ўша кунда ёлғиз Парвардигорингиз (ҳузури)га ҳайдалиш бордир'}, {'chapter': 75, 'verse': 31, 'text': 'فَلَا صَدَّقَ وَلَا صَلَّىٰ\nНа (Қуръон ва пайғамбарни) тасдиқ этди ва на намоз ўқиди'}, {'chapter': 75, 'verse': 32, 'text': 'وَلَٰكِن كَذَّبَ وَتَوَلَّىٰ\nБалки у (Қуръонни) ёлғон деди ва (иймондан) юз ўгирди'}, {'chapter': 75, 'verse': 33, 'text': 'ثُمَّ ذَهَبَ إِلَىٰٓ أَهۡلِهِۦ يَتَمَطَّىٰٓ\nСўнгра (мана шу қилмишларидан уялиш ўрнига) гердайганича ўз аҳли томон кетди'}, {'chapter': 75, 'verse': 34, 'text': 'أَوۡلَىٰ لَكَ فَأَوۡلَىٰ\n(Эй кўрнамак инсон), ўлим бўлсин сенга, ўлим'}, {'chapter': 75, 'verse': 35, 'text': 'ثُمَّ أَوۡلَىٰ لَكَ فَأَوۡلَىٰٓ\nСўнгра яна ўлим бўлсин сенга, ўлим'}, {'chapter': 75, 'verse': 36, 'text': 'أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَن يُتۡرَكَ سُدًى\nИнсон ўзини (бу дунёда дину иймонга буюрилмасдан, Охиратда эса қайта тирилиб ҳисоб-китоб қилинмасдан) бекор ташлаб қўйилишини ўйларми (тамаъ қилурми)'}, {'chapter': 75, 'verse': 37, 'text': 'أَلَمۡ يَكُ نُطۡفَةٗ مِّن مَّنِيّٖ يُمۡنَىٰ\nАхир у (бачадонга) тўкиладиган манийдан бир (ҳақир) нутфа эмасмиди'}, {'chapter': 75, 'verse': 38, 'text': 'ثُمَّ كَانَ عَلَقَةٗ فَخَلَقَ فَسَوَّىٰ\nСўнгра лахта қон бўлди. Бас, (Аллоҳ уни) яратиб, расо (инсон) қилди'}, {'chapter': 75, 'verse': 39, 'text': 'فَجَعَلَ مِنۡهُ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ\nСўнг ундан эркак ва аёл жуфтларини (пайдо) қилди'}, {'chapter': 75, 'verse': 40, 'text': 'أَلَيۡسَ ذَٰلِكَ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰ\nАна шу (Аллоҳ) ўликларни тирилтиришга қодир эмасми?! (Албатта Аллоҳ бунга қодирдир)'}, {'chapter': 76, 'verse': 1, 'text': 'هَلۡ أَتَىٰ عَلَى ٱلۡإِنسَٰنِ حِينٞ مِّنَ ٱلدَّهۡرِ لَمۡ يَكُن شَيۡـٔٗا مَّذۡكُورًا\nАниқки, инсоннинг устига замондан у тилга олгулик бир нарса бўлмаган вақт-муддат ҳам келгандир'}, {'chapter': 76, 'verse': 2, 'text': 'إِنَّا خَلَقۡنَا ٱلۡإِنسَٰنَ مِن نُّطۡفَةٍ أَمۡشَاجٖ نَّبۡتَلِيهِ فَجَعَلۡنَٰهُ سَمِيعَۢا بَصِيرًا\nДарҳақиқат, Биз инсонни (вояга етганидан сўнг шариат таклифлари билан) имтиҳон қилгувчи бўлган ҳолимизда (оталик ва оналик сувларидан) аралаш бўлган нутфадан яратдик. Бас, уни (Бизнинг оятларимизни тинглаши ва оламдаги Бизнинг борлигимизга далолат қилиб турган аломатларни кўриши учун) эшитгувчи, кўргувчи қилиб қўйдик'}, {'chapter': 76, 'verse': 3, 'text': 'إِنَّا هَدَيۡنَٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرٗا وَإِمَّا كَفُورًا\nДарҳақиқат, биз уни (инсонни) хоҳ у шукр қилгувчи-мусулмон бўлсин ва хоҳ кўрнамак-кофир бўлсин (тўғри) йўлга ҳидоят қилдик (яъни, шукр қилгувчилар Биз уларни ҳидоят қилган Тўғри Йўлни — Ҳақ динни маҳкам тутадилар, кўрнамак кимсалар эса ўзларига кўрсатилган Тўғри Йўлдан юз ўгириб кетадилар)'}, {'chapter': 76, 'verse': 4, 'text': 'إِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَلَٰسِلَاْ وَأَغۡلَٰلٗا وَسَعِيرًا\nАлбатта Биз кофирлар учун занжиру кишанларни ва (дўзах) алангасини тайёрлаб қўйгандирмиз'}, {'chapter': 76, 'verse': 5, 'text': 'إِنَّ ٱلۡأَبۡرَارَ يَشۡرَبُونَ مِن كَأۡسٖ كَانَ مِزَاجُهَا كَافُورًا\nАлбатта (ҳаёти-дунёдан Аллоҳ таолонинг амру фармонларига итоат қилиб ўтган) яхшилар (жаннатда) мизожи-аралашмаси кофур майкосалардан (май) ичурлар'}, {'chapter': 76, 'verse': 6, 'text': 'عَيۡنٗا يَشۡرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفۡجِيرٗا\n(Улар) Аллоҳнинг (яхши) бандалари ичадиган (жаннатий) чашма бўлган майкосалардан (май) ичурлар. Улар у (чашмани ўзлари хоҳлаган жойдан) чиқариб-оқизиб олурлар'}, {'chapter': 76, 'verse': 7, 'text': 'يُوفُونَ بِٱلنَّذۡرِ وَيَخَافُونَ يَوۡمٗا كَانَ شَرُّهُۥ مُسۡتَطِيرٗا\nУлар (ҳаёти-дунёда ўз зиммаларига олган) назрларини тўла адо қилурлар ва ёмонлик-даҳшатлари кенг ёйилгувчи бўлган (Қиёмат) кунидан қўрқурлар'}, {'chapter': 76, 'verse': 8, 'text': 'وَيُطۡعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسۡكِينٗا وَيَتِيمٗا وَأَسِيرًا\nВа таомни суюб-хоҳлаб турсалар-да, (ўзлари емасдан) мискин, етим ва асирларни таомлантирурлар'}, {'chapter': 76, 'verse': 9, 'text': 'إِنَّمَا نُطۡعِمُكُمۡ لِوَجۡهِ ٱللَّهِ لَا نُرِيدُ مِنكُمۡ جَزَآءٗ وَلَا شُكُورًا\n(Улар айтурлар): «Биз сизларни ёлғиз Аллоҳнинг Юзи учун таомлантирурмиз. Сизлардан (бу ишимиз учун) бирон мукофот ва миннатдорчилик истамасмиз — кутмасмиз'}, {'chapter': 76, 'verse': 10, 'text': 'إِنَّا نَخَافُ مِن رَّبِّنَا يَوۡمًا عَبُوسٗا قَمۡطَرِيرٗا\nАлбатта бизлар Парвардигоримиз томонидан бўладиган, (даҳшатли азобидан юзлар) тиришиб, буришиб қолгувчи бир Кундан қўрқурмиз»'}, {'chapter': 76, 'verse': 11, 'text': 'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلۡيَوۡمِ وَلَقَّىٰهُمۡ نَضۡرَةٗ وَسُرُورٗا\nБас, Аллоҳ уларни ўша Куннинг ёмонлигидан сақлади ва улар(нинг юзлари)га жилва — нур, (дилларига) сурур бахш этди'}, {'chapter': 76, 'verse': 12, 'text': 'وَجَزَىٰهُم بِمَا صَبَرُواْ جَنَّةٗ وَحَرِيرٗا\nВа уларни сабр-қаноатлари сабабли жаннат ва (жаннатда эгниларида бўладиган) ипак (либослар) билан мукофотлади'}, {'chapter': 76, 'verse': 13, 'text': 'مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۖ لَا يَرَوۡنَ فِيهَا شَمۡسٗا وَلَا زَمۡهَرِيرٗا\nУлар у жойда сўриларга ястанган ҳолларида ўтирурлар. Улар у жойда қуёш(нинг қизиғи)ни ҳам, замҳарир (қишнинг совуғи)ни ҳам кўрмаслар'}, {'chapter': 76, 'verse': 14, 'text': 'وَدَانِيَةً عَلَيۡهِمۡ ظِلَٰلُهَا وَذُلِّلَتۡ قُطُوفُهَا تَذۡلِيلٗا\n(Жаннат) соялари уларга яқин ва мевалари ҳам (узиб тановул қилиш осон бўлсин учун) эгиб қўйилган бўлур'}, {'chapter': 76, 'verse': 15, 'text': 'وَيُطَافُ عَلَيۡهِم بِـَٔانِيَةٖ مِّن فِضَّةٖ وَأَكۡوَابٖ كَانَتۡ قَوَارِيرَا۠\nУларга кумуш идишлар(да таомлар) ва шиша қадаҳлар(да шароблар) айлантирилиб турилур'}, {'chapter': 76, 'verse': 16, 'text': 'قَوَارِيرَاْ مِن فِضَّةٖ قَدَّرُوهَا تَقۡدِيرٗا\nУлар ўзи кумушдан (ясалган бўлса-да нафислигидан шиша-шиша бўлиб кетган қадаҳлардир). (У шаробларни соқийлар ҳар кимнинг эҳтиёжига яраша) ўлчаб-белгилаб қўйгандирлар'}, {'chapter': 76, 'verse': 17, 'text': 'وَيُسۡقَوۡنَ فِيهَا كَأۡسٗا كَانَ مِزَاجُهَا زَنجَبِيلًا\n(Жаннат аҳли) у жойда мизожи-аралашмаси занжабил бўлган майкосалар билан суғорилурлар'}, {'chapter': 76, 'verse': 18, 'text': 'عَيۡنٗا فِيهَا تُسَمَّىٰ سَلۡسَبِيلٗا\n(Жаннатларидаги) салсабил-ўтимли деб аталадиган чашма (билан суғорилурлар)'}, {'chapter': 76, 'verse': 19, 'text': '۞وَيَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ إِذَا رَأَيۡتَهُمۡ حَسِبۡتَهُمۡ لُؤۡلُؤٗا مَّنثُورٗا\nУларнинг устида мангу ёш (яъни, ҳеч қаримайдиган) болалар (хизмат қилиб) айланиб турурларки, уларни кўрган вақтингизда (гўзалликлари, рангларининг тиниқлиги ва юзларининг нурлилигидан) сочиб юборилган марваридларми, деб ўйларсиз'}, {'chapter': 76, 'verse': 20, 'text': 'وَإِذَا رَأَيۡتَ ثَمَّ رَأَيۡتَ نَعِيمٗا وَمُلۡكٗا كَبِيرًا\nУ жойда қачон қарасангиз, ноз-неъматларни ва катта мулку-давлатни кўрурсиз'}, {'chapter': 76, 'verse': 21, 'text': 'عَٰلِيَهُمۡ ثِيَابُ سُندُسٍ خُضۡرٞ وَإِسۡتَبۡرَقٞۖ وَحُلُّوٓاْ أَسَاوِرَ مِن فِضَّةٖ وَسَقَىٰهُمۡ رَبُّهُمۡ شَرَابٗا طَهُورًا\nУларнинг устларида яшил ипак ва шойи либослар бўлиб, улар кумуш билакузуклар билан безангандирлар ва Парвардигорлари уларни ниҳоятда покиза шароб-ла суғоргандир'}, {'chapter': 76, 'verse': 22, 'text': 'إِنَّ هَٰذَا كَانَ لَكُمۡ جَزَآءٗ وَكَانَ سَعۡيُكُم مَّشۡكُورًا\nАлбатта бу (жаннат ва ундаги ноз-неъматлар) сизлар учун мукофот бўлди'}, {'chapter': 76, 'verse': 23, 'text': 'إِنَّا نَحۡنُ نَزَّلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ تَنزِيلٗا\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Бизнинг ўзимиз сизга бу Қуръонни бўлиб-бўлиб нозил қилдик'}, {'chapter': 76, 'verse': 24, 'text': 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تُطِعۡ مِنۡهُمۡ ءَاثِمًا أَوۡ كَفُورٗا\nБас, сиз Парвардигорингизнинг ҳукмига сабр қилинг ва улардан (кофирлардан) бирон гуноҳкор ё кўрнамакка итоат қилманг'}, {'chapter': 76, 'verse': 25, 'text': 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ بُكۡرَةٗ وَأَصِيلٗا\nВа эрта-ю кеч Парвардигорингизнинг номини зикр қилинг'}, {'chapter': 76, 'verse': 26, 'text': 'وَمِنَ ٱلَّيۡلِ فَٱسۡجُدۡ لَهُۥ وَسَبِّحۡهُ لَيۡلٗا طَوِيلًا\nЯна кечанинг бир қисмида ҳам У зотга сажда қилинг ва тунда узоқ (бедор бўлиб) У зотга тасбеҳ айтинг'}, {'chapter': 76, 'verse': 27, 'text': 'إِنَّ هَـٰٓؤُلَآءِ يُحِبُّونَ ٱلۡعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمۡ يَوۡمٗا ثَقِيلٗا\nАлбатта ана у (кофир)лар нақд (дунё)ни суюрлар ва ортларидаги оғир Кунни (Қиёматни) эса тарк қилурлар (яъни, у Кун учун бирон амал қилмаслар)'}, {'chapter': 76, 'verse': 28, 'text': 'نَّحۡنُ خَلَقۡنَٰهُمۡ وَشَدَدۡنَآ أَسۡرَهُمۡۖ وَإِذَا شِئۡنَا بَدَّلۡنَآ أَمۡثَٰلَهُمۡ تَبۡدِيلًا\nБиз уларни яратдик ва (барча) аъзоларини мустаҳкам қилдик ва хоҳлаган вақтимизда (уларни ҳалок қилиб, ўринларига) ўзларига ўхшаганларни алмаштириб қўюрмиз'}, {'chapter': 76, 'verse': 29, 'text': 'إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا\nАлбатта бу (сура) бир панд-насиҳатдир. Бас, ким хоҳласа (бу панд-насиҳатни қабул қилиб), Парвардигори томонга йўл тутур'}, {'chapter': 76, 'verse': 30, 'text': 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا\n(Эй инсонлар), сизлар фақат Аллоҳ хоҳлаган вақтдагина (У зот томонга йўл тутишни) хоҳларсизлар (яъни, бирон иш Аллоҳнинг хоҳиш-иродасисиз бўлмас). Албатта Аллоҳ билим ва ҳикмат эгаси бўлган Зотдир'}, {'chapter': 76, 'verse': 31, 'text': 'يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمِينَ أَعَدَّ لَهُمۡ عَذَابًا أَلِيمَۢا\nУ Ўзи хоҳлаган кишиларни (яъни, мўминларни) Ўз раҳматига — жаннатига киритур. Золимлар учун эса аламли азобни тайёрлаб қўйгандир'}, {'chapter': 77, 'verse': 1, 'text': 'وَٱلۡمُرۡسَلَٰتِ عُرۡفٗا\nПайдар-пай юбориладиган (шамол)ларга'}, {'chapter': 77, 'verse': 2, 'text': 'فَٱلۡعَٰصِفَٰتِ عَصۡفٗا\nқутурадиган (бўрон)ларга қасам'}, {'chapter': 77, 'verse': 3, 'text': 'وَٱلنَّـٰشِرَٰتِ نَشۡرٗا\n(Булутларни Аллоҳ хоҳлаган томонларга) ёйиб юборадиган'}, {'chapter': 77, 'verse': 4, 'text': 'فَٱلۡفَٰرِقَٰتِ فَرۡقٗا\n(ҳақ билан ботилни ва ҳалол билан ҳаромни) ажратиб берадиган'}, {'chapter': 77, 'verse': 5, 'text': 'فَٱلۡمُلۡقِيَٰتِ ذِكۡرًا\nэслатма-ваҳий олиб тушадиган'}, {'chapter': 77, 'verse': 6, 'text': 'عُذۡرًا أَوۡ نُذۡرًا\n(мўмин бандаларни) узр-мағфират қилиш учун ё (кофирларни Охират азобидан) огоҳлантириш учун (ваҳий олиб тушадиган фаришта)ларга (яъни, юқорида мазкур бўлган барча нарсаларга) қасамки'}, {'chapter': 77, 'verse': 7, 'text': 'إِنَّمَا تُوعَدُونَ لَوَٰقِعٞ\nҳеч шак-шубҳасиз, сизларга ваъда қилинаётган нарса (Қиёмат ва ҳисоб-китоб) воқеъ бўлгувчидир'}, {'chapter': 77, 'verse': 8, 'text': 'فَإِذَا ٱلنُّجُومُ طُمِسَتۡ\nБас, қачон юлдузлар (нурлари сўниб) ўчирилгач'}, {'chapter': 77, 'verse': 9, 'text': 'وَإِذَا ٱلسَّمَآءُ فُرِجَتۡ\nВа қачон осмон (қобиғи) ёрилиб-очилгач'}, {'chapter': 77, 'verse': 10, 'text': 'وَإِذَا ٱلۡجِبَالُ نُسِفَتۡ\nВа қачон тоғлар (қумлар каби) сочилгач'}, {'chapter': 77, 'verse': 11, 'text': 'وَإِذَا ٱلرُّسُلُ أُقِّتَتۡ\nВа қачон пайғамбарларга (уларни ёлғончи қилган қавмлари билан ҳисоб-китоб қилишлари учун) белгиланган вақт келгач, (ана ўша вақтда ваъда қилинган Қиёмат қойим бўлур)'}, {'chapter': 77, 'verse': 12, 'text': 'لِأَيِّ يَوۡمٍ أُجِّلَتۡ\n(Пайғамбар ўз қавмлари билан ҳисоб-китоб қилишлари) қай Кунга белгиланган эди'}, {'chapter': 77, 'verse': 13, 'text': 'لِيَوۡمِ ٱلۡفَصۡلِ\n(Ҳақ билан ботил) ажратиладиган Кунга (белгилангандир)'}, {'chapter': 77, 'verse': 14, 'text': 'وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلۡفَصۡلِ\n(Эй инсон), сен (ҳақ билан ботил) ажратиладиган Кун нима эканлигини қаердан ҳам билурсан'}, {'chapter': 77, 'verse': 15, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда (ўша Кунни) ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 16, 'text': 'أَلَمۡ نُهۡلِكِ ٱلۡأَوَّلِينَ\nАхир Биз аввалги (ўз пайғамбарларини ёлғончи қилган уммат)ларни ҳалок қилмадикми'}, {'chapter': 77, 'verse': 17, 'text': 'ثُمَّ نُتۡبِعُهُمُ ٱلۡأٓخِرِينَ\nСўнгра кейинги (яъни, Муҳаммад алайҳис-салоту вас-саломни ёлғончи қилгувчи Макка кофирлари ва бошқа)ларни ҳам уларга эргаштирурмиз'}, {'chapter': 77, 'verse': 18, 'text': 'كَذَٰلِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِينَ\nБиз барча жиноятчи-кофирларни мана шундай (ҳалок) қилурмиз'}, {'chapter': 77, 'verse': 19, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ўша Кунни ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 20, 'text': 'أَلَمۡ نَخۡلُقكُّم مِّن مَّآءٖ مَّهِينٖ\nАхир Биз сизларни бир ҳақир сувдан яратмадикми'}, {'chapter': 77, 'verse': 21, 'text': 'فَجَعَلۡنَٰهُ فِي قَرَارٖ مَّكِينٍ\nСўнг у (сувни-нутфа)ни мустаҳкам қароргоҳда (она қорнида барқарор) қилиб қўймадикми'}, {'chapter': 77, 'verse': 22, 'text': 'إِلَىٰ قَدَرٖ مَّعۡلُومٖ\nМаълум муддатгача (яъни, ой-куни етиб туғилгунича она қорнида барқарор қилиб қўймадикми)'}, {'chapter': 77, 'verse': 23, 'text': 'فَقَدَرۡنَا فَنِعۡمَ ٱلۡقَٰدِرُونَ\nБас, Биз (бир томчи сувни комил инсон қилиб яратишга) қодир бўлдик. Нақадар қудратлидирмиз'}, {'chapter': 77, 'verse': 24, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 25, 'text': 'أَلَمۡ نَجۡعَلِ ٱلۡأَرۡضَ كِفَاتًا\nАхир Биз Ерни йиғгувчи қилиб қўймадикми'}, {'chapter': 77, 'verse': 26, 'text': 'أَحۡيَآءٗ وَأَمۡوَٰتٗا\nТирикларни ҳам, ўликларни ҳам (йиғгувчи қилиб қўймадикми)'}, {'chapter': 77, 'verse': 27, 'text': 'وَجَعَلۡنَا فِيهَا رَوَٰسِيَ شَٰمِخَٰتٖ وَأَسۡقَيۡنَٰكُم مَّآءٗ فُرَاتٗا\nЯна Биз (ерда) баланд тоғларни (пайдо) қилдик ва сизларни (ўша тоғлардан оқиб тушадиган) чучук-тотли сув билан суғордик'}, {'chapter': 77, 'verse': 28, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 29, 'text': 'ٱنطَلِقُوٓاْ إِلَىٰ مَا كُنتُم بِهِۦ تُكَذِّبُونَ\n(У Кунда кофирларга дейилур): «Сизлар (ҳаёти-дунёдалик пайтингизда) ёлғон дегувчи бўлган нарсага (ҳисоб-китоб ва дўзах азобига) боринглар'}, {'chapter': 77, 'verse': 30, 'text': 'ٱنطَلِقُوٓاْ إِلَىٰ ظِلّٖ ذِي ثَلَٰثِ شُعَبٖ\n(Жаҳаннамда ёнаётган дўзахилардан кўтарилаётган бадбуй тутундан иборат, осмонга ўрлаган сари бўлиниб кетган) уч бўлакли «соя»га боринглар'}, {'chapter': 77, 'verse': 31, 'text': 'لَّا ظَلِيلٖ وَلَا يُغۡنِي مِنَ ٱللَّهَبِ\nҲеч қандай салқинлик бермайдиган ва (дўзах) алангасини ҳам тўсмайдиган («соя»га боринглар!)»'}, {'chapter': 77, 'verse': 32, 'text': 'إِنَّهَا تَرۡمِي بِشَرَرٖ كَٱلۡقَصۡرِ\nДарвоқеъ, у (жаҳаннам ҳар томонга ҳар бири) қасрдек учқунларни отиб турур'}, {'chapter': 77, 'verse': 33, 'text': 'كَأَنَّهُۥ جِمَٰلَتٞ صُفۡرٞ\nХудди сариқ туяларга ўхшаган (учқунларни отиб турур)'}, {'chapter': 77, 'verse': 34, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлғай'}, {'chapter': 77, 'verse': 35, 'text': 'هَٰذَا يَوۡمُ لَا يَنطِقُونَ\nБу (Кун) улар сўзлай олмайдиган'}, {'chapter': 77, 'verse': 36, 'text': 'وَلَا يُؤۡذَنُ لَهُمۡ فَيَعۡتَذِرُونَ\nВа улар учун узр айтишларига ҳам изн берилмайдиган Кундир'}, {'chapter': 77, 'verse': 37, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 38, 'text': 'هَٰذَا يَوۡمُ ٱلۡفَصۡلِۖ جَمَعۡنَٰكُمۡ وَٱلۡأَوَّلِينَ\nБу (ҳақ билан ботил) ажратиладиган Кундир! (Эй бу Кунни ёлғон дегувчи кимсалар, мана) Биз сизларни ҳам, аввалги (ўз пайғамбарларини ёлғончи қилган)ларни ҳам жамладик'}, {'chapter': 77, 'verse': 39, 'text': 'فَإِن كَانَ لَكُمۡ كَيۡدٞ فَكِيدُونِ\nМана, агар сизлар учун (дўзахдан қутқарадиган) бирон найранг бўлса, Менга ўша найрангларингизни кўрсатинглар-чи'}, {'chapter': 77, 'verse': 40, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 41, 'text': 'إِنَّ ٱلۡمُتَّقِينَ فِي ظِلَٰلٖ وَعُيُونٖ\nТақводор зотлар эса (У Кунда) албатта соя-салқинлар ва чашмаларда'}, {'chapter': 77, 'verse': 42, 'text': 'وَفَوَٰكِهَ مِمَّا يَشۡتَهُونَ\nВа кўнгиллари тусаган мева-чевалар устидадирлар'}, {'chapter': 77, 'verse': 43, 'text': 'كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ\n(Уларга дейилур): «Қилиб ўтган (яхши) амалларингиз сабабли (ушбу жаннат ноз-неъматларини) пок билиб еб-ичаверинглар»'}, {'chapter': 77, 'verse': 44, 'text': 'إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ\nАлбатта Биз чиройли амал қилгувчиларни мана шундай мукофотлармиз'}, {'chapter': 77, 'verse': 45, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 46, 'text': 'كُلُواْ وَتَمَتَّعُواْ قَلِيلًا إِنَّكُم مُّجۡرِمُونَ\n(Эй кофирлар) озгина вақт (яъни, тўрт кунлик дунёда ақлсиз ҳайвонлардек) еб, фойдаланиб қолинглар! Албатта сизлар жиноятчидирсизлар'}, {'chapter': 77, 'verse': 47, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 48, 'text': 'وَإِذَا قِيلَ لَهُمُ ٱرۡكَعُواْ لَا يَرۡكَعُونَ\nҚачон уларга: «Рукуъ қилинглар, (яъни, намоз ўқинглар)», дейилса, рукуъ қилмаслар'}, {'chapter': 77, 'verse': 49, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда (ўша Кунни) ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 77, 'verse': 50, 'text': 'فَبِأَيِّ حَدِيثِۭ بَعۡدَهُۥ يُؤۡمِنُونَ\nАхир улар (Қуръондан) кейин (яъни, беназир илоҳий мўъжиза бўлган шундай азиз Китобга иймон келтирмасдан) қандай сўзга-китобга иймон келтирурлар'}, {'chapter': 78, 'verse': 1, 'text': 'عَمَّ يَتَسَآءَلُونَ\n(Мушриклар) бир-бирларидан нима ҳақида сўрашмоқдалар'}, {'chapter': 78, 'verse': 2, 'text': 'عَنِ ٱلنَّبَإِ ٱلۡعَظِيمِ\nУлуғ хабар ҳақида'}, {'chapter': 78, 'verse': 3, 'text': 'ٱلَّذِي هُمۡ فِيهِ مُخۡتَلِفُونَ\nУлар ўзлари ихтилоф қилгувчи бўлган (улуғ хабар ҳакқда яъни, қайта тирилиш тўғрисида савол-жавоб қилмоқдалар)'}, {'chapter': 78, 'verse': 4, 'text': 'كَلَّا سَيَعۡلَمُونَ\nЙўқ (уларнинг гумонлари беҳудадир)! Улар яқинда (бу хабарнинг ҳақ эканини) билиб олажаклар'}, {'chapter': 78, 'verse': 5, 'text': 'ثُمَّ كَلَّا سَيَعۡلَمُونَ\nЯна бир бор йўқ! Улар яқинда билиб олажаклар'}, {'chapter': 78, 'verse': 6, 'text': 'أَلَمۡ نَجۡعَلِ ٱلۡأَرۡضَ مِهَٰدٗا\nБиз Ерни (барча жонзотлар маскан тутиб яшашлари учун) бир тўшак'}, {'chapter': 78, 'verse': 7, 'text': 'وَٱلۡجِبَالَ أَوۡتَادٗا\nтоғларни эса (ўша Ерни тутиб тургувчи) қозиқлар қилиб қўймадикми'}, {'chapter': 78, 'verse': 8, 'text': 'وَخَلَقۡنَٰكُمۡ أَزۡوَٰجٗا\nБиз сизларни жуфт-жуфт қилиб яратдик'}, {'chapter': 78, 'verse': 9, 'text': 'وَجَعَلۡنَا نَوۡمَكُمۡ سُبَاتٗا\nУйқуларингизни (баданларингиз ва асабларингиз учун) ором қилдик'}, {'chapter': 78, 'verse': 10, 'text': 'وَجَعَلۡنَا ٱلَّيۡلَ لِبَاسٗا\nКечани (ўз қоронғуси билан барча нарсани яшириб турадиган) либос қилдик'}, {'chapter': 78, 'verse': 11, 'text': 'وَجَعَلۡنَا ٱلنَّهَارَ مَعَاشٗا\nКундузни эса тирикчилик (учун белгиланган вақт) қилдик'}, {'chapter': 78, 'verse': 12, 'text': 'وَبَنَيۡنَا فَوۡقَكُمۡ سَبۡعٗا شِدَادٗا\nУстларингизда етти (қават) пишиқ-мустаҳкам (осмонни) бино қилдик'}, {'chapter': 78, 'verse': 13, 'text': 'وَجَعَلۡنَا سِرَاجٗا وَهَّاجٗا\nВа доимо чарақлаб тургувчи чироқни (яъни, қуёшни пайдо) қилдик'}, {'chapter': 78, 'verse': 14, 'text': 'وَأَنزَلۡنَا مِنَ ٱلۡمُعۡصِرَٰتِ مَآءٗ ثَجَّاجٗا\nЯна Биз ўзидан ёмғирни сиқиб чиқаргувчи (булут)лардан мўл-кўл сув-ёмғир ёғдирдик'}, {'chapter': 78, 'verse': 15, 'text': 'لِّنُخۡرِجَ بِهِۦ حَبّٗا وَنَبَاتٗا\nЎша (ёмғир) ёрдамида дон ва набототни'}, {'chapter': 78, 'verse': 16, 'text': 'وَجَنَّـٰتٍ أَلۡفَافًا\nҳамда (қалинлигидан дарахтлари) бир-бирига чирмашиб кетган боғларни ундириб чиқариш учун (мўл-кўл ёмғир ёғдирдик)'}, {'chapter': 78, 'verse': 17, 'text': 'إِنَّ يَوۡمَ ٱلۡفَصۡلِ كَانَ مِيقَٰتٗا\nАлбатта (ҳақ билан ботил, яхши билан ёмон) ажратиладиган Кун (ҳисоб-китоб учун) белгиланган вақт бўлди'}, {'chapter': 78, 'verse': 18, 'text': 'يَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَتَأۡتُونَ أَفۡوَاجٗا\nУ Кунда Сур чалинур, бас, сизлар тўп-тўп бўлиб (маҳшаргоҳга) келурсизлар'}, {'chapter': 78, 'verse': 19, 'text': 'وَفُتِحَتِ ٱلسَّمَآءُ فَكَانَتۡ أَبۡوَٰبٗا\nОсмон ҳам очилиб, (фаришталар тушиб келадиган) эшикларга айланиб қолди'}, {'chapter': 78, 'verse': 20, 'text': 'وَسُيِّرَتِ ٱلۡجِبَالُ فَكَانَتۡ سَرَابًا\nТоғлар ҳам (жойларидан) жилдирилиб, (осмонда учиб юрган) саробга айланиб қолди'}, {'chapter': 78, 'verse': 21, 'text': 'إِنَّ جَهَنَّمَ كَانَتۡ مِرۡصَادٗا\nАлбатта жаҳаннам (ўзига қулаб тушадиган кофирларга) кўз тутиб тургувчи бўлди'}, {'chapter': 78, 'verse': 22, 'text': 'لِّلطَّـٰغِينَ مَـَٔابٗا\nТуғёнга тушгувчиларнинг борар жойлари бўлган (жаҳаннам кўз тутиб тургувчи бўлди)'}, {'chapter': 78, 'verse': 23, 'text': 'لَّـٰبِثِينَ فِيهَآ أَحۡقَابٗا\nУлар у жойда узоқ замонлар (яъни, абадул-абад) қолгувчидирлар'}, {'chapter': 78, 'verse': 24, 'text': 'لَّا يَذُوقُونَ فِيهَا بَرۡدٗا وَلَا شَرَابًا\nУлар у жойда (жаҳаннам ўтидан ором берадиган) бирон салқинни ва бирон ичимликни топмаслар'}, {'chapter': 78, 'verse': 25, 'text': 'إِلَّا حَمِيمٗا وَغَسَّاقٗا\nФақат қайноқ сув ва йирингнигина (тотурлар)'}, {'chapter': 78, 'verse': 26, 'text': 'جَزَآءٗ وِفَاقًا\n(Мана шу уларнинг қилмишларига) яраша жазодир'}, {'chapter': 78, 'verse': 27, 'text': 'إِنَّهُمۡ كَانُواْ لَا يَرۡجُونَ حِسَابٗا\nЧунки улар (Қиёмат Кунидаги) ҳисоб-китобдан умидсиз бўлган кимсалардир'}, {'chapter': 78, 'verse': 28, 'text': 'وَكَذَّبُواْ بِـَٔايَٰتِنَا كِذَّابٗا\nУлар бизнинг оятларимизни бутунлай ёлғон дер эдилар'}, {'chapter': 78, 'verse': 29, 'text': 'وَكُلَّ شَيۡءٍ أَحۡصَيۡنَٰهُ كِتَٰبٗا\nҲолбуки, Биз барча нарсани ҳисоб-китоб қилиб, ёзиб қўйгандирмиз'}, {'chapter': 78, 'verse': 30, 'text': 'فَذُوقُواْ فَلَن نَّزِيدَكُمۡ إِلَّا عَذَابًا\nБас, (эй кофирлар, Бизнинг азобимизни) тотиб кўринглар! Энди Биз сизларга фақат азобнигина зиёда қилурмиз'}, {'chapter': 78, 'verse': 31, 'text': 'إِنَّ لِلۡمُتَّقِينَ مَفَازًا\nАлбатта тақводор зотлар учун (жаннатда) нажот манзили бордир'}, {'chapter': 78, 'verse': 32, 'text': 'حَدَآئِقَ وَأَعۡنَٰبٗا\nЯна боғлар ва узумзорлар'}, {'chapter': 78, 'verse': 33, 'text': 'وَكَوَاعِبَ أَتۡرَابٗا\n(Ёш ва ҳуснда) тенгдош хушқад (ҳур)лар'}, {'chapter': 78, 'verse': 34, 'text': 'وَكَأۡسٗا دِهَاقٗا\nВа (шароб билан) тўла майкосалар бордир'}, {'chapter': 78, 'verse': 35, 'text': 'لَّا يَسۡمَعُونَ فِيهَا لَغۡوٗا وَلَا كِذَّـٰبٗا\nУлар у жойда бирон беҳуда ва ёлғон сўз эшитмаслар'}, {'chapter': 78, 'verse': 36, 'text': 'جَزَآءٗ مِّن رَّبِّكَ عَطَآءً حِسَابٗا\n(Бу) Парвардигорингиз томонидан (берилган) мукофот, етарли марҳаматдир'}, {'chapter': 78, 'verse': 37, 'text': 'رَّبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلرَّحۡمَٰنِۖ لَا يَمۡلِكُونَ مِنۡهُ خِطَابٗا\nОсмонлар ва Ернинг ҳамда уларнинг ўртасидаги бор нарсаларнинг Парвардигори бўлмиш Меҳрибон Зот томонидан (берилган мукофот, етарли марҳаматдир). Улар (инсонлар) У зотдан (Аллоҳдан) қўрқиб бирон сўз айтишга қодир бўлмаслар'}, {'chapter': 78, 'verse': 38, 'text': 'يَوۡمَ يَقُومُ ٱلرُّوحُ وَٱلۡمَلَـٰٓئِكَةُ صَفّٗاۖ لَّا يَتَكَلَّمُونَ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَٰنُ وَقَالَ صَوَابٗا\nРуҳ (Жаброил) ва (барча) фаришталар саф тортиб турадиган Кунда улар сўзлай олмаслар, фақат У Меҳрибон зот изн берган кишигина (сўзлар) ва (фақат) рост сўзни айтур'}, {'chapter': 78, 'verse': 39, 'text': 'ذَٰلِكَ ٱلۡيَوۡمُ ٱلۡحَقُّۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ مَـَٔابًا\nБу ҳаққи-рост Кундир. Бас, ким хоҳласа, ўз Парвардигори томон қайтадиган йўлни тутур'}, {'chapter': 78, 'verse': 40, 'text': 'إِنَّآ أَنذَرۡنَٰكُمۡ عَذَابٗا قَرِيبٗا يَوۡمَ يَنظُرُ ٱلۡمَرۡءُ مَا قَدَّمَتۡ يَدَاهُ وَيَقُولُ ٱلۡكَافِرُ يَٰلَيۡتَنِي كُنتُ تُرَٰبَۢا\n(Эй Макка кофирлари), дарҳақиқат, Биз сизларни яқин(да воқеъ бўладиган) азобдан огоҳлантирдик. У Кунда (ҳар бир) киши ўзи қилиб ўтган нарсани (яъни, барча яхши-ёмон амалларини) кўрур ва кофир кимса: «Эҳ, кошки эди (қайтадан) тупроққа айланиб кетсам(-у муҳаққақ бошимга тушадиган азобдан қутулсам)», деб қолур'}, {'chapter': 79, 'verse': 1, 'text': 'وَٱلنَّـٰزِعَٰتِ غَرۡقٗا\n(Кофирларнинг жонларини баданларининг) ич-ичидан қаттиқ суғуриб оладиган (фаришта)ларга қасам'}, {'chapter': 79, 'verse': 2, 'text': 'وَٱلنَّـٰشِطَٰتِ نَشۡطٗا\n(Мўминларнинг жонларини осонлик билан) илдам тортиб оладиган (фаришта)ларга қасам'}, {'chapter': 79, 'verse': 3, 'text': 'وَٱلسَّـٰبِحَٰتِ سَبۡحٗا\n(Осмонлар билан Ер ўртасида) силлиқ сузадиган (фаришта)ларга қасам'}, {'chapter': 79, 'verse': 4, 'text': 'فَٱلسَّـٰبِقَٰتِ سَبۡقٗا\n(Ўз зиммаларидаги вазифаларни адо этишда) шошиб-ўзадиган (фаришта)ларга қасам'}, {'chapter': 79, 'verse': 5, 'text': 'فَٱلۡمُدَبِّرَٰتِ أَمۡرٗا\n(Аллоҳ буюрган барча) ишнинг тадбирини тузиб турадиган (фаришта)ларга қасамки, (сизлар албатта қайта тирилиб, ҳисоб-китоб қилинурсизлар)'}, {'chapter': 79, 'verse': 6, 'text': 'يَوۡمَ تَرۡجُفُ ٱلرَّاجِفَةُ\nУ Кунда (Сур биринчи марта чалиниши билан) титрагувчи (ер) титраб (устидаги бор жонзот ҳалок қилинади)'}, {'chapter': 79, 'verse': 7, 'text': 'تَتۡبَعُهَا ٱلرَّادِفَةُ\nУнга эргашувчи эргашур (яъни, орадан қирқ сана ўтгач, Сур иккинчи марта чалиниб барча нарсага қайтадан ҳаёт ато этилади ва Қиёмат Куни бошланади)'}, {'chapter': 79, 'verse': 8, 'text': 'قُلُوبٞ يَوۡمَئِذٖ وَاجِفَةٌ\nУ Кунда (кофирларнинг) диллари қўрқувга тушгувчи'}, {'chapter': 79, 'verse': 9, 'text': 'أَبۡصَٰرُهَا خَٰشِعَةٞ\nКўзлари эса (даҳшатдан Ерга) эгилиб қолгувчидир'}, {'chapter': 79, 'verse': 10, 'text': 'يَقُولُونَ أَءِنَّا لَمَرۡدُودُونَ فِي ٱلۡحَافِرَةِ\nУлар (бу дунёда) дерлар: «Ҳақиқатан ҳам бизлар (ўлганимиздан кейин яна қайта тирилиб) аввалги ҳолатимизга қайтарилгувчимизми'}, {'chapter': 79, 'verse': 11, 'text': 'أَءِذَا كُنَّا عِظَٰمٗا نَّخِرَةٗ\nЧириган суякларга айланиб қолган вақтимизда-я?»'}, {'chapter': 79, 'verse': 12, 'text': 'قَالُواْ تِلۡكَ إِذٗا كَرَّةٌ خَاسِرَةٞ\nУлар (истеҳзо билан): «Ундоқ бўлса бу (бизлар учун) зиён қилгувчи қайтиш-ку!», дедилар'}, {'chapter': 79, 'verse': 13, 'text': 'فَإِنَّمَا هِيَ زَجۡرَةٞ وَٰحِدَةٞ\nБас, у (қайтиш) фақат биргина даҳшатли қичқириқдир (яъни, Сурнинг иккинчи марта чалинишидир)'}, {'chapter': 79, 'verse': 14, 'text': 'فَإِذَا هُم بِٱلسَّاهِرَةِ\nСўнг баногоҳ улар (тирик ҳолда) Ер устида бўлиб қолурлар'}, {'chapter': 79, 'verse': 15, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга Мусонинг хабари келдими'}, {'chapter': 79, 'verse': 16, 'text': 'إِذۡ نَادَىٰهُ رَبُّهُۥ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوًى\nЭсланг, Парвардигори унга муқаддас Туво водийсида (шундай) нидо қилган эди'}, {'chapter': 79, 'verse': 17, 'text': 'ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ\n«(Эй Мусо), сен Фиръавннинг олдига боргин, чунки у (куфру исён билан) ҳаддидан ошди'}, {'chapter': 79, 'verse': 18, 'text': 'فَقُلۡ هَل لَّكَ إِلَىٰٓ أَن تَزَكَّىٰ\nБас, (унга) айтинг: «Сенинг (куфр иллатидан) покланишга рағбат-хоҳишинг борми'}, {'chapter': 79, 'verse': 19, 'text': 'وَأَهۡدِيَكَ إِلَىٰ رَبِّكَ فَتَخۡشَىٰ\nМен сени Парвардигоринг (йўли)га ҳидоят қилсам, бас, сен (У зотдан) қўрқсанг»'}, {'chapter': 79, 'verse': 20, 'text': 'فَأَرَىٰهُ ٱلۡأٓيَةَ ٱلۡكُبۡرَىٰ\nСўнг (Мусо) унга бир улкан мўъжиза кўрсатган эди'}, {'chapter': 79, 'verse': 21, 'text': 'فَكَذَّبَ وَعَصَىٰ\n(Фиръавн) уни ёлғончи қилди (яъни, Мусога: «Сен сеҳргарсан», деб туҳмат қилди) ва (Аллоҳ таолога) осий-итоатсиз бўлди'}, {'chapter': 79, 'verse': 22, 'text': 'ثُمَّ أَدۡبَرَ يَسۡعَىٰ\nСўнгра (фисқу фасодни ёйиш йўлида) саъй-ҳаракат қилган ҳолида (Мусодан) юз ўгириб кетди'}, {'chapter': 79, 'verse': 23, 'text': 'فَحَشَرَ فَنَادَىٰ\nСўнг (ўз одамларини) тўплади-да, жар солди'}, {'chapter': 79, 'verse': 24, 'text': 'فَقَالَ أَنَا۠ رَبُّكُمُ ٱلۡأَعۡلَىٰ\n«Мен сизларнинг энг устун Парвардигорингиздирман», деди'}, {'chapter': 79, 'verse': 25, 'text': 'فَأَخَذَهُ ٱللَّهُ نَكَالَ ٱلۡأٓخِرَةِ وَٱلۡأُولَىٰٓ\nБас, Аллоҳ уни ҳам Охират, ҳам дунё азоби билан ушлади (яъни, дунёда у денгизга ғарқ қилиб юборилди, Охиратда эса дўзах азобига дучор бўлур)'}, {'chapter': 79, 'verse': 26, 'text': 'إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّمَن يَخۡشَىٰٓ\nАлбатта бу (қисса)да (Аллоҳдан) қўрқадиган кишилар учун ибрат бордир'}, {'chapter': 79, 'verse': 27, 'text': 'ءَأَنتُمۡ أَشَدُّ خَلۡقًا أَمِ ٱلسَّمَآءُۚ بَنَىٰهَا\n(Эй мушриклар), сизларни яратиш қийинроқми ёки осмонними?! (Яъни, бутун Ер куррасининг атрофини ўзга сайёралардан бирон зиён етмаслиги учун том — осмон билан қоплаб қўйишга қодир бўлган Зот учун сизларни яратиш ёки қайта тирилтириш ҳеч гап эмас-ку!) (Аллоҳ) уни бино қилди'}, {'chapter': 79, 'verse': 28, 'text': 'رَفَعَ سَمۡكَهَا فَسَوَّىٰهَا\nШифтини баланд қилиб, тиклади'}, {'chapter': 79, 'verse': 29, 'text': 'وَأَغۡطَشَ لَيۡلَهَا وَأَخۡرَجَ ضُحَىٰهَا\nВа У ўша (осмон)нинг тунини қоронғу қилиб, (ундан) кундузини чиқарди'}, {'chapter': 79, 'verse': 30, 'text': 'وَٱلۡأَرۡضَ بَعۡدَ ذَٰلِكَ دَحَىٰهَآ\nВа шундан кейин Ерни ёйиб-текис қилди'}, {'chapter': 79, 'verse': 31, 'text': 'أَخۡرَجَ مِنۡهَا مَآءَهَا وَمَرۡعَىٰهَا\nУндан суви-ю, ўтлоқларини чиқарди'}, {'chapter': 79, 'verse': 32, 'text': 'وَٱلۡجِبَالَ أَرۡسَىٰهَا\nВа у (ер)га тоғларни ўрнаштирди'}, {'chapter': 79, 'verse': 33, 'text': 'مَتَٰعٗا لَّكُمۡ وَلِأَنۡعَٰمِكُمۡ\n(Буларнинг барчаси) сизлар учун ва чорва ҳайвонларингиз учун манфаат бўлсин деб (қилингандир)'}, {'chapter': 79, 'verse': 34, 'text': 'فَإِذَا جَآءَتِ ٱلطَّآمَّةُ ٱلۡكُبۡرَىٰ\nБас, қачон катта бало (яъни, Қиёмат) келганида'}, {'chapter': 79, 'verse': 35, 'text': 'يَوۡمَ يَتَذَكَّرُ ٱلۡإِنسَٰنُ مَا سَعَىٰ\n— Инсон ўз қилмишини эслаб коладиган Кунда'}, {'chapter': 79, 'verse': 36, 'text': 'وَبُرِّزَتِ ٱلۡجَحِيمُ لِمَن يَرَىٰ\nВа дўзах кўрадиган (ҳар бир) кишига кўрсатиб қўйилганида'}, {'chapter': 79, 'verse': 37, 'text': 'فَأَمَّا مَن طَغَىٰ\nБас, ана ўшанда ким (ҳаёти дунёда куфру исён билан) туғёнга тушган'}, {'chapter': 79, 'verse': 38, 'text': 'وَءَاثَرَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا\nВа ҳаёти дунёни (Охиратдан) устун қўйган бўлса'}, {'chapter': 79, 'verse': 39, 'text': 'فَإِنَّ ٱلۡجَحِيمَ هِيَ ٱلۡمَأۡوَىٰ\nУ ҳолда фақат жаҳаннамгина (унинг учун) жой бўлур'}, {'chapter': 79, 'verse': 40, 'text': 'وَأَمَّا مَنۡ خَافَ مَقَامَ رَبِّهِۦ وَنَهَى ٱلنَّفۡسَ عَنِ ٱلۡهَوَىٰ\nЭнди ким (ҳаёти дунёдалик пайтида Қиёмат Куни маҳшаргоҳда) Парвардигорининг (ҳузурида) туриши (ва У зотга ҳисоб-китоб бериши)дан қўрққан ва нафсини ҳавойи хоҳишлардан қайтарган бўлса'}, {'chapter': 79, 'verse': 41, 'text': 'فَإِنَّ ٱلۡجَنَّةَ هِيَ ٱلۡمَأۡوَىٰ\nУ ҳолда фақат жаннатгина (унинг учун) жой бўлур'}, {'chapter': 79, 'verse': 42, 'text': 'يَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرۡسَىٰهَا\n(Эй Муҳаммад алайҳис-салоту вас-салом, мушриклар) сиздан (ўша Қиёмат) соатини қачон воқеъ бўлиши ҳақида сўрарлар'}, {'chapter': 79, 'verse': 43, 'text': 'فِيمَ أَنتَ مِن ذِكۡرَىٰهَآ\nСиз қаердасиз-у, уни (яъни, Қиёмат қачон бўлишини) зикр қилиб-эслаш (қаерда)? (Яъни, сиз ҳеч қачон у Куннинг вақтини айтиб бера олмайсиз)'}, {'chapter': 79, 'verse': 44, 'text': 'إِلَىٰ رَبِّكَ مُنتَهَىٰهَآ\nУни (билиш) ёлғиз Парвардигорингизга бориб тўхтар'}, {'chapter': 79, 'verse': 45, 'text': 'إِنَّمَآ أَنتَ مُنذِرُ مَن يَخۡشَىٰهَا\nСиз фақат ўша (Қиёмат)дан қўрқадиган кишиларни огоҳлантиргувчисиз, холос. (Аммо у Куннинг қачон воқеъ бўлишини айтиб бериш сизнинг вазифангиз эмасдир)'}, {'chapter': 79, 'verse': 46, 'text': 'كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَهَا لَمۡ يَلۡبَثُوٓاْ إِلَّا عَشِيَّةً أَوۡ ضُحَىٰهَا\nУлар у (Қиёмат соати)ни кўрадиган Кунда (бу дунёда) гўё биргина пешиндан сўнг ёки чошгоҳ пайтида тургандек (яъни, бир кун ҳам яшамагандек) бўлиб қолурлар'}, {'chapter': 80, 'verse': 1, 'text': 'عَبَسَ وَتَوَلَّىٰٓ\nУ (Пайғамбар алайҳис-салоту вас-салом аъмо кишига) қош чимирди ва (ундан) юз ўгирди'}, {'chapter': 80, 'verse': 2, 'text': 'أَن جَآءَهُ ٱلۡأَعۡمَىٰ\nЎзининг олдига кўзи ожиз киши келгани учун (қош чимирди ва ундан юз ўгирди)'}, {'chapter': 80, 'verse': 3, 'text': 'وَمَا يُدۡرِيكَ لَعَلَّهُۥ يَزَّكَّىٰٓ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз қаердан билурсиз, эҳтимол у (сиздан ўрганадиган илму маърифати ёрдамида ўз гуноҳларидан) покланар'}, {'chapter': 80, 'verse': 4, 'text': 'أَوۡ يَذَّكَّرُ فَتَنفَعَهُ ٱلذِّكۡرَىٰٓ\nЁки панд-насиҳат олар-да, сўнг бу панд-насиҳат унга фойда берар'}, {'chapter': 80, 'verse': 5, 'text': 'أَمَّا مَنِ ٱسۡتَغۡنَىٰ\nЭнди (ўз мол-мулки билан иймондан) истиғно қилиб турган кимсага келсак'}, {'chapter': 80, 'verse': 6, 'text': 'فَأَنتَ لَهُۥ تَصَدَّىٰ\nБас, сиз ўшанга иқбол қилиб — юзланмоқдасиз'}, {'chapter': 80, 'verse': 7, 'text': 'وَمَا عَلَيۡكَ أَلَّا يَزَّكَّىٰ\nҲолбуки, у (ўзининг куфридан) покланмаслиги сабабли сизга бирон зиён етмас'}, {'chapter': 80, 'verse': 8, 'text': 'وَأَمَّا مَن جَآءَكَ يَسۡعَىٰ\nЭнди олдингизга югуриб-елиб келган зотга келсак'}, {'chapter': 80, 'verse': 9, 'text': 'وَهُوَ يَخۡشَىٰ\n(Аллоҳдан) қўрққан ҳолида (олдингизга югуриб келган зотга келсак)'}, {'chapter': 80, 'verse': 10, 'text': 'فَأَنتَ عَنۡهُ تَلَهَّىٰ\nБас, сиз ундан чалғиб — юз ўгириб олмоқдасиз'}, {'chapter': 80, 'verse': 11, 'text': 'كَلَّآ إِنَّهَا تَذۡكِرَةٞ\nЙўқ, (сиз асло бундай қилманг)! Албатта (Қуръон оятлари) бир панд-насиҳатдир'}, {'chapter': 80, 'verse': 12, 'text': 'فَمَن شَآءَ ذَكَرَهُۥ\nБас, ким хоҳласа ундан панд-насиҳат олур'}, {'chapter': 80, 'verse': 13, 'text': 'فِي صُحُفٖ مُّكَرَّمَةٖ\n(У оятлар Аллоҳ наздида) азизу мукаррам'}, {'chapter': 80, 'verse': 14, 'text': 'مَّرۡفُوعَةٖ مُّطَهَّرَةِۭ\nқадри баланд, покиза саҳифаларга'}, {'chapter': 80, 'verse': 15, 'text': 'بِأَيۡدِي سَفَرَةٖ\nМирзолар (яъни, фаришталар) қўллари билан'}, {'chapter': 80, 'verse': 16, 'text': 'كِرَامِۭ بَرَرَةٖ\nУлуғ, итоатли (мирзолар қўллари билан Лавҳул-Маҳфуздан кўчириб битилгандир)'}, {'chapter': 80, 'verse': 17, 'text': 'قُتِلَ ٱلۡإِنسَٰنُ مَآ أَكۡفَرَهُۥ\nҲалок бўлгур инсон-а! Бунчалар кофир бўлмаса'}, {'chapter': 80, 'verse': 18, 'text': 'مِنۡ أَيِّ شَيۡءٍ خَلَقَهُۥ\n(Аллоҳ) уни қай нарсадан яратди ўзи'}, {'chapter': 80, 'verse': 19, 'text': 'مِن نُّطۡفَةٍ خَلَقَهُۥ فَقَدَّرَهُۥ\nУни бир (ҳақир) нутфадан яратиб, сўнг уни(нг она қорнидаги ривожини ва ой-кунини) белгилаб қўйди-ку'}, {'chapter': 80, 'verse': 20, 'text': 'ثُمَّ ٱلسَّبِيلَ يَسَّرَهُۥ\nСўнгра унга (она қорнидан чиқиш) йўлини осон қилди'}, {'chapter': 80, 'verse': 21, 'text': 'ثُمَّ أَمَاتَهُۥ فَأَقۡبَرَهُۥ\nСўнгра унга ўлим бериб, қабрга киритди (яъни, бошқа ҳайвонлар сингари унинг жасадини Ер юзида қолдириб, йиртқичларга ем қилиб қўйгани йўқ)'}, {'chapter': 80, 'verse': 22, 'text': 'ثُمَّ إِذَا شَآءَ أَنشَرَهُۥ\nСўнгра Ўзи хоҳлаган вақтида уни яна қайта тирилтирур'}, {'chapter': 80, 'verse': 23, 'text': 'كَلَّا لَمَّا يَقۡضِ مَآ أَمَرَهُۥ\nЙўқ, у (кофир кимса Аллоҳ) ўзига буюрган бирон нарсани адо қилмади'}, {'chapter': 80, 'verse': 24, 'text': 'فَلۡيَنظُرِ ٱلۡإِنسَٰنُ إِلَىٰ طَعَامِهِۦٓ\nЭнди инсон ўзининг таомига (ибрат кўзи билан бир) қараб кўрсин-чи'}, {'chapter': 80, 'verse': 25, 'text': 'أَنَّا صَبَبۡنَا ٱلۡمَآءَ صَبّٗا\nБиз (осмондан) сув-ёмғирни қуйдирдик'}, {'chapter': 80, 'verse': 26, 'text': 'ثُمَّ شَقَقۡنَا ٱلۡأَرۡضَ شَقّٗا\nСўнгра Ерни (гиёҳлар билан) ёрдик'}, {'chapter': 80, 'verse': 27, 'text': 'فَأَنۢبَتۡنَا فِيهَا حَبّٗا\nСўнг Биз унда дон-дунни ундириб-ўстириб қўйдик-ку'}, {'chapter': 80, 'verse': 28, 'text': 'وَعِنَبٗا وَقَضۡبٗا\nУзум ва кўкларни'}, {'chapter': 80, 'verse': 29, 'text': 'وَزَيۡتُونٗا وَنَخۡلٗا\nЗайтун ва ҳурмоларни'}, {'chapter': 80, 'verse': 30, 'text': 'وَحَدَآئِقَ غُلۡبٗا\nҚуюқ дарахтзор боғларни'}, {'chapter': 80, 'verse': 31, 'text': 'وَفَٰكِهَةٗ وَأَبّٗا\nМева-чева-ю, ўт-ўланларни'}, {'chapter': 80, 'verse': 32, 'text': 'مَّتَٰعٗا لَّكُمۡ وَلِأَنۡعَٰمِكُمۡ\nСизлар учун ва чорва ҳайвонларингиз учун манфаат бўлсин (деб ундириб-ўстириб қўйдик-ку)'}, {'chapter': 80, 'verse': 33, 'text': 'فَإِذَا جَآءَتِ ٱلصَّآخَّةُ\nБас, қачон (қулоқларни) кар қилгувчи (даҳшатли қичқириқ) келганда (яъни, сур иккинчи марта чалиниб, барча халойиққа қайтадан жон ато этилганда, ҳар бир инсон ўзи билан ўзи бўлиб қолур)'}, {'chapter': 80, 'verse': 34, 'text': 'يَوۡمَ يَفِرُّ ٱلۡمَرۡءُ مِنۡ أَخِيهِ\nУ Кунда киши ўз оға-инисидан ҳам'}, {'chapter': 80, 'verse': 35, 'text': 'وَأُمِّهِۦ وَأَبِيهِ\nОнаси ва отасидан ҳам'}, {'chapter': 80, 'verse': 36, 'text': 'وَصَٰحِبَتِهِۦ وَبَنِيهِ\nХотини-ю, бола-чақасидан ҳам қочур'}, {'chapter': 80, 'verse': 37, 'text': 'لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُمۡ يَوۡمَئِذٖ شَأۡنٞ يُغۡنِيهِ\n(Чунки) улардан ҳар бир киши учун у Кунда ўзига етарли ташвиш бўлур'}, {'chapter': 80, 'verse': 38, 'text': 'وُجُوهٞ يَوۡمَئِذٖ مُّسۡفِرَةٞ\nУ Кунда (мўминларнинг) юзлари ёруғ'}, {'chapter': 80, 'verse': 39, 'text': 'ضَاحِكَةٞ مُّسۡتَبۡشِرَةٞ\nКулгувчи ва хуррам бўлур'}, {'chapter': 80, 'verse': 40, 'text': 'وَوُجُوهٞ يَوۡمَئِذٍ عَلَيۡهَا غَبَرَةٞ\nВа у Кунда (кофирларнинг) юзлари устида чанг-ғубор бўлиб'}, {'chapter': 80, 'verse': 41, 'text': 'تَرۡهَقُهَا قَتَرَةٌ\nУ (юз)ларни қаролик қоплаб олур'}, {'chapter': 80, 'verse': 42, 'text': 'أُوْلَـٰٓئِكَ هُمُ ٱلۡكَفَرَةُ ٱلۡفَجَرَةُ\nАна ўшалар фисқу фужур қилгувчи кофирларнинг ўзидир'}, {'chapter': 81, 'verse': 1, 'text': 'إِذَا ٱلشَّمۡسُ كُوِّرَتۡ\nҚуёш ўралиб (нурсизланиб) қолганида'}, {'chapter': 81, 'verse': 2, 'text': 'وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتۡ\nЮлдузлар ҳам (ўз фалакларидан) тўкилганида'}, {'chapter': 81, 'verse': 3, 'text': 'وَإِذَا ٱلۡجِبَالُ سُيِّرَتۡ\nТоғлар ҳам (ўз жойларидан) жилдирилганида'}, {'chapter': 81, 'verse': 4, 'text': 'وَإِذَا ٱلۡعِشَارُ عُطِّلَتۡ\n(Қорнидаги боласи) ўн ойлик бўлган бўғоз туялар ҳам бўш қўйиб юборилганида'}, {'chapter': 81, 'verse': 5, 'text': 'وَإِذَا ٱلۡوُحُوشُ حُشِرَتۡ\nВаҳший ҳайвонлар ҳам (бир жойга) тўпланиб қолганида'}, {'chapter': 81, 'verse': 6, 'text': 'وَإِذَا ٱلۡبِحَارُ سُجِّرَتۡ\nДенгизлар (тандир каби) қизитилганида (ва сув ўрнига олов билан тўлганида)'}, {'chapter': 81, 'verse': 7, 'text': 'وَإِذَا ٱلنُّفُوسُ زُوِّجَتۡ\nЖонлар (қайтадан баданларга) жуфтланганида'}, {'chapter': 81, 'verse': 8, 'text': 'وَإِذَا ٱلۡمَوۡءُۥدَةُ سُئِلَتۡ\nТириклай кўмилган (ҳар бир) қиздан сўралганида'}, {'chapter': 81, 'verse': 9, 'text': 'بِأَيِّ ذَنۢبٖ قُتِلَتۡ\nқандай гуноҳ сабабли ўлдирилгани ҳақида (сўралганида)'}, {'chapter': 81, 'verse': 10, 'text': 'وَإِذَا ٱلصُّحُفُ نُشِرَتۡ\n(Номаи аъмол) саҳифалари очилганида'}, {'chapter': 81, 'verse': 11, 'text': 'وَإِذَا ٱلسَّمَآءُ كُشِطَتۡ\nОсмон (ернинг устидан) сидириб олинганида'}, {'chapter': 81, 'verse': 12, 'text': 'وَإِذَا ٱلۡجَحِيمُ سُعِّرَتۡ\nДўзах (кофирлар учун) ловуллатиб ёқилганида'}, {'chapter': 81, 'verse': 13, 'text': 'وَإِذَا ٱلۡجَنَّةُ أُزۡلِفَتۡ\nЖаннат (тақводор зотларга) яқин қилинганида'}, {'chapter': 81, 'verse': 14, 'text': 'عَلِمَتۡ نَفۡسٞ مَّآ أَحۡضَرَتۡ\n(Ана ўша Кунда ҳар бир) жон ўзи (мана шу Кун учун) ҳозирлаб келган нарсани (яъни, барча яхши-ёмон амалларини) билур'}, {'chapter': 81, 'verse': 15, 'text': 'فَلَآ أُقۡسِمُ بِٱلۡخُنَّسِ\nБас, Мен (кундузлари кўздан) ғойиб бўлгувчи (юлдуз)ларга қасам'}, {'chapter': 81, 'verse': 16, 'text': 'ٱلۡجَوَارِ ٱلۡكُنَّسِ\n(Кечалари ҳам) тез ўтиб, (ўз «уя»лари — буржларига) яширингувчи (юлдуз)ларга қасам'}, {'chapter': 81, 'verse': 17, 'text': 'وَٱلَّيۡلِ إِذَا عَسۡعَسَ\nЎз зулмати билан келиб-кетаётган кечага қасам'}, {'chapter': 81, 'verse': 18, 'text': 'وَٱلصُّبۡحِ إِذَا تَنَفَّسَ\nВа отаётган тонгга қасам ичурманки'}, {'chapter': 81, 'verse': 19, 'text': 'إِنَّهُۥ لَقَوۡلُ رَسُولٖ كَرِيمٖ\nШак-шубҳасиз, у (Қуръон) бир улуғ элчининг (яъни, Жаброил фариштанинг Аллоҳ таоло томонидан келтирган) сўзидир'}, {'chapter': 81, 'verse': 20, 'text': 'ذِي قُوَّةٍ عِندَ ذِي ٱلۡعَرۡشِ مَكِينٖ\nУ қувватли, Аршнинг соҳиби (бўлмиш Аллоҳ) наздида макон-мартабали'}, {'chapter': 81, 'verse': 21, 'text': 'مُّطَاعٖ ثَمَّ أَمِينٖ\nУ жойда (яъни, осмонларда фаришталар томонидан) итоат этилгувчи, ишончли (элчининг Аллоҳ таоло томонидан келтирган сўзидир)'}, {'chapter': 81, 'verse': 22, 'text': 'وَمَا صَاحِبُكُم بِمَجۡنُونٖ\nВа сизларнинг соҳибингиз (Муҳаммад алайҳис-салоту вас-салом) мажнун эмасдир'}, {'chapter': 81, 'verse': 23, 'text': 'وَلَقَدۡ رَءَاهُ بِٱلۡأُفُقِ ٱلۡمُبِينِ\nДарҳақиқат, у (Жаброилни) очиқ уфқда кўрди'}, {'chapter': 81, 'verse': 24, 'text': 'وَمَا هُوَ عَلَى ٱلۡغَيۡبِ بِضَنِينٖ\nУ (Муҳаммад алайҳис-салоту вас-салом) ғайбга (яъни, ғайбдан келган ваҳийга) бахил ҳам эмасдир. (У Аллоҳ таоло томонидан ўзига келадиган ваҳийларни бузмасдан, тўла-тўкис ҳолда сизларга етказур)'}, {'chapter': 81, 'verse': 25, 'text': 'وَمَا هُوَ بِقَوۡلِ شَيۡطَٰنٖ رَّجِيمٖ\nВа у (Қуръон) қувилган — малъун шайтоннинг сўзи эмасдир'}, {'chapter': 81, 'verse': 26, 'text': 'فَأَيۡنَ تَذۡهَبُونَ\nБас, сизлар (мана шу Қуръони Карим кўрсатган Ҳақ йўлни инкор этиб) қаён кетмоқдасизлар'}, {'chapter': 81, 'verse': 27, 'text': 'إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ\nУ (Қуръон) ҳеч шак-шубҳасиз, бутун оламлар учун бир эслатмадир'}, {'chapter': 81, 'verse': 28, 'text': 'لِمَن شَآءَ مِنكُمۡ أَن يَسۡتَقِيمَ\n— Сизларнинг орангиздаги Тўғри Йўлда бўлмоқни хоҳлаган кишилар учун (бир эслатмадир)'}, {'chapter': 81, 'verse': 29, 'text': 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ\nСизлар фақат бутун оламлар Парвардигори бўлмиш Аллоҳ хоҳласагина (Тўғри Йўлда бўлишни) хоҳларсизлар'}, {'chapter': 82, 'verse': 1, 'text': 'إِذَا ٱلسَّمَآءُ ٱنفَطَرَتۡ\nОсмон ёрилганида'}, {'chapter': 82, 'verse': 2, 'text': 'وَإِذَا ٱلۡكَوَاكِبُ ٱنتَثَرَتۡ\nЮлдузлар (ҳар томонга) сочилганида'}, {'chapter': 82, 'verse': 3, 'text': 'وَإِذَا ٱلۡبِحَارُ فُجِّرَتۡ\nДарё-денгизлар (ўртасидаги тўсиқ-тўғонлар) очилганида'}, {'chapter': 82, 'verse': 4, 'text': 'وَإِذَا ٱلۡقُبُورُ بُعۡثِرَتۡ\nҚабрлар тўнкариб қўйилганида (яъни, уларнинг ичидаги инсонларга жон ато этилиб Ер юзига чиқарилганларида)'}, {'chapter': 82, 'verse': 5, 'text': 'عَلِمَتۡ نَفۡسٞ مَّا قَدَّمَتۡ وَأَخَّرَتۡ\n(Ана ўша Кунда ҳар бир) жон ўзи (ҳаёти дунёдалик чоғида) қилиб ўтган ва (ўзидан кейин) қолдирган (барча) нарсаларни (яъни, яхши-ёмон амалларини) билур'}, {'chapter': 82, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلۡإِنسَٰنُ مَا غَرَّكَ بِرَبِّكَ ٱلۡكَرِيمِ\nЭй инсон, нима сени улуғ Парвардигоринг ҳақида (Унга ибодат қилмасанг ҳам бўлаверади, деб) алдаб қўйди'}, {'chapter': 82, 'verse': 7, 'text': 'ٱلَّذِي خَلَقَكَ فَسَوَّىٰكَ فَعَدَلَكَ\nУ сени яратиб, сўнг (барча аъзоларингни) тиклаб, сўнг (қоматингни ҳам) расо қилиб, қўйган Зот-ку'}, {'chapter': 82, 'verse': 8, 'text': 'فِيٓ أَيِّ صُورَةٖ مَّا شَآءَ رَكَّبَكَ\nУ сени Ўзи қай суратни хоҳлаган бўлса, (ўша сурат — шаклда) таркиб топтирди — ижод қилди-ку'}, {'chapter': 82, 'verse': 9, 'text': 'كَلَّا بَلۡ تُكَذِّبُونَ بِٱلدِّينِ\nЙўқ, балки сизлар жазо (Қиёмат Кунини) ёлғон дерсизлар (шунинг учун ҳам Аллоҳ буюрган эзгу амалларни қилмайсизлар ва У зот қайтарган ёмон амаллардан қайтмайсизлар)'}, {'chapter': 82, 'verse': 10, 'text': 'وَإِنَّ عَلَيۡكُمۡ لَحَٰفِظِينَ\nҲолбуки, шак-шубҳасиз, сизларнинг устингизда (қилган ҳар бир амалингизни) ёд олиб'}, {'chapter': 82, 'verse': 11, 'text': 'كِرَامٗا كَٰتِبِينَ\nёзиб тургувчи улуғ (фаришта)лар бордир'}, {'chapter': 82, 'verse': 12, 'text': 'يَعۡلَمُونَ مَا تَفۡعَلُونَ\nУлар сизлар қилаётган ишларни билурлар'}, {'chapter': 82, 'verse': 13, 'text': 'إِنَّ ٱلۡأَبۡرَارَ لَفِي نَعِيمٖ\nШак-шубҳасиз, яхшилар (яъни, мўминлар) жаннат неъматларидадирлар'}, {'chapter': 82, 'verse': 14, 'text': 'وَإِنَّ ٱلۡفُجَّارَ لَفِي جَحِيمٖ\nШак-шубҳасиз, фисқ-фужур қилгувчи кимсалар (яъни, Қуръон ва пайғамбарни ёлғончи қилгувчи, жазо Куни ва қайта тирилишни инкор қилгувчи кимсалар) дўзахдадирлар'}, {'chapter': 82, 'verse': 15, 'text': 'يَصۡلَوۡنَهَا يَوۡمَ ٱلدِّينِ\nУлар (дўзах)га жазо (Қиёмат) кунида кирурлар'}, {'chapter': 82, 'verse': 16, 'text': 'وَمَا هُمۡ عَنۡهَا بِغَآئِبِينَ\nВа улар (дўзахдан) ғойиб бўлгувчи (яъни, халос бўлгувчи эмасдирлар)'}, {'chapter': 82, 'verse': 17, 'text': 'وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلدِّينِ\n(Эй инсон), сен жазо Куни нима эканини қаердан ҳам билурсан'}, {'chapter': 82, 'verse': 18, 'text': 'ثُمَّ مَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلدِّينِ\nСўнгра (яна такрор айтаманки), сен жазо Куни нима эканини қаердан ҳам билурсан'}, {'chapter': 82, 'verse': 19, 'text': 'يَوۡمَ لَا تَمۡلِكُ نَفۡسٞ لِّنَفۡسٖ شَيۡـٔٗاۖ وَٱلۡأَمۡرُ يَوۡمَئِذٖ لِّلَّهِ\nУ Кунда ҳеч бир жон (бошқа) бир жонга бирон нарса қилишга (яъни, бирон фойда етказишга ёки ундан бирон зиённи кетказишга) эга бўлмас! У Кунда барча иш ёлғиз Аллоҳники бўлур'}, {'chapter': 83, 'verse': 1, 'text': 'وَيۡلٞ لِّلۡمُطَفِّفِينَ\n(Ўлчов ва тарозидан) уриб қолгувчи кимсаларга ҳалокат бўлгай'}, {'chapter': 83, 'verse': 2, 'text': 'ٱلَّذِينَ إِذَا ٱكۡتَالُواْ عَلَى ٱلنَّاسِ يَسۡتَوۡفُونَ\nУлар одамлардан (бирон нарсани) ўлчаб олган вақтларида тўла қилиб оладиган'}, {'chapter': 83, 'verse': 3, 'text': 'وَإِذَا كَالُوهُمۡ أَو وَّزَنُوهُمۡ يُخۡسِرُونَ\nуларга ўлчаб ёки тортиб берган вақтларида эса кам қилиб берадиган кимсалардир'}, {'chapter': 83, 'verse': 4, 'text': 'أَلَا يَظُنُّ أُوْلَـٰٓئِكَ أَنَّهُم مَّبۡعُوثُونَ\nУлар ўзларининг қайта тирилгувчи эканликларини ўйламайдиларми'}, {'chapter': 83, 'verse': 5, 'text': 'لِيَوۡمٍ عَظِيمٖ\nУлуғ бир Кунда —'}, {'chapter': 83, 'verse': 6, 'text': 'يَوۡمَ يَقُومُ ٱلنَّاسُ لِرَبِّ ٱلۡعَٰلَمِينَ\n— Барча одамлар бутун оламлар Парвардигори ҳузурида тик туриб (ҳисоб-китоб берадиган Қиёмат) Кунида (қайта тирилгувчи эканликларини ўйламайдиларми)'}, {'chapter': 83, 'verse': 7, 'text': 'كَلَّآ إِنَّ كِتَٰبَ ٱلۡفُجَّارِ لَفِي سِجِّينٖ\nЙўқ, (бировларнинг ҳақларидан уриб қолишдан ҳазар қилинглар)! Чунки (бундай) фисқ-фужур қилгувчи кимсаларнинг номаи аъмоллари албатта Сижжийнда бўлур'}, {'chapter': 83, 'verse': 8, 'text': 'وَمَآ أَدۡرَىٰكَ مَا سِجِّينٞ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз Сижжийн нима эканини қаердан ҳам билурсиз'}, {'chapter': 83, 'verse': 9, 'text': 'كِتَٰبٞ مَّرۡقُومٞ\n(У) битилган китобдир'}, {'chapter': 83, 'verse': 10, 'text': 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ\nУ Кунда ёлғон дегувчиларга ҳалокат бўлгай'}, {'chapter': 83, 'verse': 11, 'text': 'ٱلَّذِينَ يُكَذِّبُونَ بِيَوۡمِ ٱلدِّينِ\nУлар жазо (Қиёмат) Кунини ёлғон дейдиган кимсалардир'}, {'chapter': 83, 'verse': 12, 'text': 'وَمَا يُكَذِّبُ بِهِۦٓ إِلَّا كُلُّ مُعۡتَدٍ أَثِيمٍ\nУ (Кун)ни фақат барча гуноҳга ботган тажовузкоргина ёлғон дер'}, {'chapter': 83, 'verse': 13, 'text': 'إِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلۡأَوَّلِينَ\nҚачон (ундай кимсага) Бизнинг оятларимиз тиловат қилинса, у: «(Бу) аввалгилардан (қолган) афсоналар», дер'}, {'chapter': 83, 'verse': 14, 'text': 'كَلَّاۖ بَلۡۜ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُواْ يَكۡسِبُونَ\nЙўқ, (ундоқ эмас)! Балки уларнинг дилларини ўзлари касб қилгувчи бўлган гуноҳлари қоплаб олгандир'}, {'chapter': 83, 'verse': 15, 'text': 'كَلَّآ إِنَّهُمۡ عَن رَّبِّهِمۡ يَوۡمَئِذٖ لَّمَحۡجُوبُونَ\nҲеч шак-шубҳа йўқки, улар ўша (Қиёмат) Кунида Парвардигорларидан тўсилгувчидирлар. (яъни, Парвардигорнинг дийдорига етиш уларга ҳеч насиб қилмагай)'}, {'chapter': 83, 'verse': 16, 'text': 'ثُمَّ إِنَّهُمۡ لَصَالُواْ ٱلۡجَحِيمِ\nСўнгра шак-шубҳасиз, улар дўзахга киргувчидирлар'}, {'chapter': 83, 'verse': 17, 'text': 'ثُمَّ يُقَالُ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ\nСўнгра (уларга): «Мана шу сизлар ёлғон деб юрган нарса — азобдир», дейилур'}, {'chapter': 83, 'verse': 18, 'text': 'كَلَّآ إِنَّ كِتَٰبَ ٱلۡأَبۡرَارِ لَفِي عِلِّيِّينَ\nДарҳақиқат, яхшиларнинг номаи аъмоллари шак-шубҳасиз, Иллийюндадир'}, {'chapter': 83, 'verse': 19, 'text': 'وَمَآ أَدۡرَىٰكَ مَا عِلِّيُّونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз Иллийюн нима эканини қаердан ҳам билурсиз'}, {'chapter': 83, 'verse': 20, 'text': 'كِتَٰبٞ مَّرۡقُومٞ\n(У) битилган китобдирки'}, {'chapter': 83, 'verse': 21, 'text': 'يَشۡهَدُهُ ٱلۡمُقَرَّبُونَ\nУнга (Аллоҳнинг) яқин (фаришта)лари гувоҳ бўлурлар'}, {'chapter': 83, 'verse': 22, 'text': 'إِنَّ ٱلۡأَبۡرَارَ لَفِي نَعِيمٍ\nШак-шубҳасиз, яхшилар (яъни, мўминлар жаннат) неъматларидадирлар'}, {'chapter': 83, 'verse': 23, 'text': 'عَلَى ٱلۡأَرَآئِكِ يَنظُرُونَ\nУлар сўриларда (Аллоҳ таоло ўзларига ато этган неъматларга) боқиб (ўтирурлар)'}, {'chapter': 83, 'verse': 24, 'text': 'تَعۡرِفُ فِي وُجُوهِهِمۡ نَضۡرَةَ ٱلنَّعِيمِ\nСиз уларнинг юзларида неъматларнинг жилвасини танирсиз — кўрурсиз'}, {'chapter': 83, 'verse': 25, 'text': 'يُسۡقَوۡنَ مِن رَّحِيقٖ مَّخۡتُومٍ\nУлар (жаннатда) муҳрланган майдан суғорилурларки'}, {'chapter': 83, 'verse': 26, 'text': 'خِتَٰمُهُۥ مِسۡكٞۚ وَفِي ذَٰلِكَ فَلۡيَتَنَافَسِ ٱلۡمُتَنَٰفِسُونَ\nУ (май)нинг муҳри мушк бўлур. Бас, баҳслашгувчи кишилар (мана шундай мангу неъматга етиш йўлида) баслашсинлар'}, {'chapter': 83, 'verse': 27, 'text': 'وَمِزَاجُهُۥ مِن تَسۡنِيمٍ\nУ майнинг мизожи — аралашмаси таснимдандир'}, {'chapter': 83, 'verse': 28, 'text': 'عَيۡنٗا يَشۡرَبُ بِهَا ٱلۡمُقَرَّبُونَ\n(Тасним Аллоҳнинг) яқин (банда)лари ичадиган бир чашмадир'}, {'chapter': 83, 'verse': 29, 'text': 'إِنَّ ٱلَّذِينَ أَجۡرَمُواْ كَانُواْ مِنَ ٱلَّذِينَ ءَامَنُواْ يَضۡحَكُونَ\nДарвоқеъ, жинояткор — кофир кимсалар иймон келтирган зотлардан (масхара қилиб) кулгувчи бўлдилар'}, {'chapter': 83, 'verse': 30, 'text': 'وَإِذَا مَرُّواْ بِهِمۡ يَتَغَامَزُونَ\nҚачон (мўминлар) уларнинг олдидан ўтсалар, улар бир-бирларига кўз қисишиб, имо-ишоралар қилардилар'}, {'chapter': 83, 'verse': 31, 'text': 'وَإِذَا ٱنقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمُ ٱنقَلَبُواْ فَكِهِينَ\nҚачон уйларига қайтсалар (мўминларни масхара қилиб озор берганларидан) роҳатланиб қайтардилар'}, {'chapter': 83, 'verse': 32, 'text': 'وَإِذَا رَأَوۡهُمۡ قَالُوٓاْ إِنَّ هَـٰٓؤُلَآءِ لَضَآلُّونَ\nҚачон (мўминларни) кўрсалар, «Ана улар шак-шубҳасиз, йўлдан озгувчи кимсалардир», дердилар'}, {'chapter': 83, 'verse': 33, 'text': 'وَمَآ أُرۡسِلُواْ عَلَيۡهِمۡ حَٰفِظِينَ\nҲолбуки, улар (мўминларнинг) устига қўриқчи қилиб юборилган эмасдилар'}, {'chapter': 83, 'verse': 34, 'text': 'فَٱلۡيَوۡمَ ٱلَّذِينَ ءَامَنُواْ مِنَ ٱلۡكُفَّارِ يَضۡحَكُونَ\nЭнди бу (Қиёмат) Кунида иймон келтирган зотлар кофирлардан кулурлар'}, {'chapter': 83, 'verse': 35, 'text': 'عَلَى ٱلۡأَرَآئِكِ يَنظُرُونَ\nУлар (жаннатдаги) сўриларда (кофирларнинг азобланишига) боқиб (ўтирурлар)'}, {'chapter': 83, 'verse': 36, 'text': 'هَلۡ ثُوِّبَ ٱلۡكُفَّارُ مَا كَانُواْ يَفۡعَلُونَ\nКофирлар ўзлари қилиб ўтган қилмишларининг жазосини олдиларми? (Албатта олдилар)'}, {'chapter': 84, 'verse': 1, 'text': 'إِذَا ٱلسَّمَآءُ ٱنشَقَّتۡ\nОсмон ёрилган вақт'}, {'chapter': 84, 'verse': 2, 'text': 'وَأَذِنَتۡ لِرَبِّهَا وَحُقَّتۡ\nВа у Парвардигори(нинг ёрилиш тўғрисидаги амри)га қулоқ тутган — (мана шу амрга) лойиқ топилган вақтда'}, {'chapter': 84, 'verse': 3, 'text': 'وَإِذَا ٱلۡأَرۡضُ مُدَّتۡ\nЕр ёйилган (теп-текис бўлган) вақт'}, {'chapter': 84, 'verse': 4, 'text': 'وَأَلۡقَتۡ مَا فِيهَا وَتَخَلَّتۡ\nВа ўз ичидаги (жасадларни устига) чиқариб ташлаб, бўшаниб олган вақт'}, {'chapter': 84, 'verse': 5, 'text': 'وَأَذِنَتۡ لِرَبِّهَا وَحُقَّتۡ\nВа у Парвардигори(нинг ёйилиб, теп-текис бўлиш тўғрисидаги амри)га қулоқ тутган — (мана шу амрга) лойиқ топилган вақтда (ана ўша Қиёмат Кунида ҳар бир инсон ўзи қилиб ўтган амалларига рўбарў бўлур)'}, {'chapter': 84, 'verse': 6, 'text': 'يَـٰٓأَيُّهَا ٱلۡإِنسَٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدۡحٗا فَمُلَٰقِيهِ\nЭй инсон, албатта сен меҳнат-машаққат чекиб Парвардигорингга (яъни, Унинг мукофот ёки жазосига) боргувчисан, бас, (бу ҳаёти дунёдан ўтганингдан сўнг) У зотга йўлиққувчисан'}, {'chapter': 84, 'verse': 7, 'text': 'فَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ\nАна энди кимнинг номаи аъмоли ўнг томонидан берилса'}, {'chapter': 84, 'verse': 8, 'text': 'فَسَوۡفَ يُحَاسَبُ حِسَابٗا يَسِيرٗا\nБас, у осон ҳисоб билан ҳисоб-китоб қилинажак'}, {'chapter': 84, 'verse': 9, 'text': 'وَيَنقَلِبُ إِلَىٰٓ أَهۡلِهِۦ مَسۡرُورٗا\nВа ўз(ининг жаннатдаги) аҳли-оиласига шоду хуррам ҳолда қайтажак'}, {'chapter': 84, 'verse': 10, 'text': 'وَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ وَرَآءَ ظَهۡرِهِۦ\nЭнди кимнинг номаи аъмоли орқа томонидан берилса'}, {'chapter': 84, 'verse': 11, 'text': 'فَسَوۡفَ يَدۡعُواْ ثُبُورٗا\nБас, у ўлимини чақириб қолажак'}, {'chapter': 84, 'verse': 12, 'text': 'وَيَصۡلَىٰ سَعِيرًا\nВа дўзахга киражак'}, {'chapter': 84, 'verse': 13, 'text': 'إِنَّهُۥ كَانَ فِيٓ أَهۡلِهِۦ مَسۡرُورًا\nДарвоқеъ, у (ҳаёти-дунёдалик пайтида) аҳли оиласида (ўз куфру исёни билан) шоду хуррам эди'}, {'chapter': 84, 'verse': 14, 'text': 'إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ\nАлбатта у ўзининг ҳеч қачон (Парвардигори ҳузурига) қайтмаслигига ишонар эди'}, {'chapter': 84, 'verse': 15, 'text': 'بَلَىٰٓۚ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرٗا\nЙўқ, Парвардигори уни шубҳасиз кўриб тургувчи эди'}, {'chapter': 84, 'verse': 16, 'text': 'فَلَآ أُقۡسِمُ بِٱلشَّفَقِ\nБас, Мен (қуёш ботаётган пайтда бутун уфқни қоплаб оладиган) шафаққа қасам'}, {'chapter': 84, 'verse': 17, 'text': 'وَٱلَّيۡلِ وَمَا وَسَقَ\nКеча ва у қамраб олган (барча) нарсага қасам'}, {'chapter': 84, 'verse': 18, 'text': 'وَٱلۡقَمَرِ إِذَا ٱتَّسَقَ\nҲамда тўла бўлган вақтидаги ойга (яъни, тўлин ойга) қасам ичурманки'}, {'chapter': 84, 'verse': 19, 'text': 'لَتَرۡكَبُنَّ طَبَقًا عَن طَبَقٖ\nАлбатта сизлар (эй инсонлар), табақадан табақага минурсизлар (яъни, бир ҳолдан иккинчи ҳолга кўчиб турурсизлар)'}, {'chapter': 84, 'verse': 20, 'text': 'فَمَا لَهُمۡ لَا يُؤۡمِنُونَ\nБас, нега улар иймон келтирмайдилар'}, {'chapter': 84, 'verse': 21, 'text': 'وَإِذَا قُرِئَ عَلَيۡهِمُ ٱلۡقُرۡءَانُ لَا يَسۡجُدُونَۤ۩\nУларга Қуръон қироат қилинган вақтида эса (унга иймон келтиришиб, Яратганга) сажда қилмайдилар'}, {'chapter': 84, 'verse': 22, 'text': 'بَلِ ٱلَّذِينَ كَفَرُواْ يُكَذِّبُونَ\nЙўқ, улар (Қуръонни ҳам, Қиёмат Куни қайта тирилишни ҳам) ёлғон дерлар'}, {'chapter': 84, 'verse': 23, 'text': 'وَٱللَّهُ أَعۡلَمُ بِمَا يُوعُونَ\nАллоҳ эса улар ичларига солиб яшираётган нарсаларини (яъни, куфрларини, иймонсизликларини) жуда яхши билгувчидир'}, {'chapter': 84, 'verse': 24, 'text': 'فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз уларга аламли азоб «хушхабари»ни беринг'}, {'chapter': 84, 'verse': 25, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونِۭ\nФақатгина иймон келтирган ва яхши амаллар қилган зотлар учун битмас-туганмас ажр-мукофот бордир'}, {'chapter': 85, 'verse': 1, 'text': 'وَٱلسَّمَآءِ ذَاتِ ٱلۡبُرُوجِ\nБуржлар эгаси бўлмиш осмонга қасам'}, {'chapter': 85, 'verse': 2, 'text': 'وَٱلۡيَوۡمِ ٱلۡمَوۡعُودِ\nВаъда қилинмиш (Қиёмат) Кунига қасам'}, {'chapter': 85, 'verse': 3, 'text': 'وَشَاهِدٖ وَمَشۡهُودٖ\n(Ўша Кунда) гувоҳ бўлгувчи (барча халойиққа) ва гувоҳ бўлингувчи (барча воқеа-ҳодисалар)га қасам-ки'}, {'chapter': 85, 'verse': 4, 'text': 'قُتِلَ أَصۡحَٰبُ ٱلۡأُخۡدُودِ\nЧоҳ эгалари лаънат қилингайлар'}, {'chapter': 85, 'verse': 5, 'text': 'ٱلنَّارِ ذَاتِ ٱلۡوَقُودِ\n(У чоҳ ўзига итқитилган одамларнинг таналаридан иборат бўлган) «ўтин»ли оловдир'}, {'chapter': 85, 'verse': 6, 'text': 'إِذۡ هُمۡ عَلَيۡهَا قُعُودٞ\nЎшанда улар ўша (чоҳ)нинг устида ўтириб олган'}, {'chapter': 85, 'verse': 7, 'text': 'وَهُمۡ عَلَىٰ مَا يَفۡعَلُونَ بِٱلۡمُؤۡمِنِينَ شُهُودٞ\nВа мўминларга қилаётган ишларига ўзлари гувоҳ бўлган эдилар'}, {'chapter': 85, 'verse': 8, 'text': 'وَمَا نَقَمُواْ مِنۡهُمۡ إِلَّآ أَن يُؤۡمِنُواْ بِٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ\nУлар (мўминлардан) фақат у (мўмин)лар қудрат ва мақтов эгаси бўлган Аллоҳга иймон келтирганлари учунгина ўч олдилар'}, {'chapter': 85, 'verse': 9, 'text': 'ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ\nУ — осмонлар ва Ернинг подшоҳлиги Ўзиники бўлган Зотдир! Аллоҳ барча нарсага гувоҳдир'}, {'chapter': 85, 'verse': 10, 'text': 'إِنَّ ٱلَّذِينَ فَتَنُواْ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ ثُمَّ لَمۡ يَتُوبُواْ فَلَهُمۡ عَذَابُ جَهَنَّمَ وَلَهُمۡ عَذَابُ ٱلۡحَرِيقِ\nАлбатта мўмин ва мўминаларни фитнага солиб, (бу қилмишларидан) тавба қилмаган кимсалар учун жаҳаннам азоби бордир ва улар учун ўт азоби бордир'}, {'chapter': 85, 'verse': 11, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡكَبِيرُ\nАлбатта иймон келтирган ва яхши амаллар қилган зотлар учун остидан дарёлар оқиб турадиган жаннатлар бордир. Мана шу катта бахтдир'}, {'chapter': 85, 'verse': 12, 'text': 'إِنَّ بَطۡشَ رَبِّكَ لَشَدِيدٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), шак-шубҳасиз, Парвардигорингизнинг (кофирларни азоб билан) ушлаши қаттиқдир'}, {'chapter': 85, 'verse': 13, 'text': 'إِنَّهُۥ هُوَ يُبۡدِئُ وَيُعِيدُ\nАлбатта Унинг Ўзи бошлар (яъни, бошлаб йўқдан бор қилур) ва қайтарур (яъни, халойиқни ўлганларидан кейин қайта тирилтирур)'}, {'chapter': 85, 'verse': 14, 'text': 'وَهُوَ ٱلۡغَفُورُ ٱلۡوَدُودُ\nУ (мўминларга) мағфиратли муҳаббатли Зотдир'}, {'chapter': 85, 'verse': 15, 'text': 'ذُو ٱلۡعَرۡشِ ٱلۡمَجِيدُ\n(У) арш соҳиби бўлмиш буюк Зотдир'}, {'chapter': 85, 'verse': 16, 'text': 'فَعَّالٞ لِّمَا يُرِيدُ\n(У) истаган нарсасини амалга оширгувчидир'}, {'chapter': 85, 'verse': 17, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ ٱلۡجُنُودِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга қўшинларнинг хабари келдими'}, {'chapter': 85, 'verse': 18, 'text': 'فِرۡعَوۡنَ وَثَمُودَ\n— Фиръавн (ва унинг одамларининг) ҳамда Самуд (қабиласининг хабари келдими)'}, {'chapter': 85, 'verse': 19, 'text': 'بَلِ ٱلَّذِينَ كَفَرُواْ فِي تَكۡذِيبٖ\nЙўқ, кофир бўлган кимсалар (ўзларидан аввал ўтган динсиз кимсалар йўлиққан азоб-ҳалокатлардан ибрат олмай, ҳали ҳам Қуръонни) ёлғон дейишда (оёқ тираб турибдилар)'}, {'chapter': 85, 'verse': 20, 'text': 'وَٱللَّهُ مِن وَرَآئِهِم مُّحِيطُۢ\nҲолбуки, Аллоҳ уларнинг ортида (барча нарсани билиб) иҳота қилиб тургувчидир'}, {'chapter': 85, 'verse': 21, 'text': 'بَلۡ هُوَ قُرۡءَانٞ مَّجِيدٞ\nЙўқ, (улар ёлғон дейишаётган нарса) Буюк Қуръондир'}, {'chapter': 85, 'verse': 22, 'text': 'فِي لَوۡحٖ مَّحۡفُوظِۭ\nЛавҳул-Маҳфуздаги (яъни, ҳар қандай бузилиш ва ўзгаришдан сақланган — ҳимояланган Лавҳдаги Буюк Қуръондир)'}, {'chapter': 86, 'verse': 1, 'text': 'وَٱلسَّمَآءِ وَٱلطَّارِقِ\nОсмонга қасам, тунги йўлчига қасамки'}, {'chapter': 86, 'verse': 2, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلطَّارِقُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сиз тунги йўлчи нима эканини қаердан ҳам билар эдингиз'}, {'chapter': 86, 'verse': 3, 'text': 'ٱلنَّجۡمُ ٱلثَّاقِبُ\n(У) учар юлдуздир'}, {'chapter': 86, 'verse': 4, 'text': 'إِن كُلُّ نَفۡسٖ لَّمَّا عَلَيۡهَا حَافِظٞ\nҲар бир жоннинг устида (унинг қилган барча амалларини) ёд олгувчи (фаришта) бордир'}, {'chapter': 86, 'verse': 5, 'text': 'فَلۡيَنظُرِ ٱلۡإِنسَٰنُ مِمَّ خُلِقَ\nЭнди инсон ўзининг нимадан яралганига бир боқсин'}, {'chapter': 86, 'verse': 6, 'text': 'خُلِقَ مِن مَّآءٖ دَافِقٖ\nУ отилиб чиқувчи бир сувдан яралган-ку'}, {'chapter': 86, 'verse': 7, 'text': 'يَخۡرُجُ مِنۢ بَيۡنِ ٱلصُّلۡبِ وَٱلتَّرَآئِبِ\n(Эркакнинг) бели билан (аёлнинг) кўкрак қафаси ўртасидан (отилиб чиқувчи бир сувдан яралган-ку)'}, {'chapter': 86, 'verse': 8, 'text': 'إِنَّهُۥ عَلَىٰ رَجۡعِهِۦ لَقَادِرٞ\nШак-шубҳасиз, (Аллоҳ) уни қайта тирилтиришга қодирдир'}, {'chapter': 86, 'verse': 9, 'text': 'يَوۡمَ تُبۡلَى ٱلسَّرَآئِرُ\nСирлар фош қилинадиган (Қиёмат) Кунида (қайта тирилтиришга қодирдир)'}, {'chapter': 86, 'verse': 10, 'text': 'فَمَا لَهُۥ مِن قُوَّةٖ وَلَا نَاصِرٖ\nБас, (у Кунда инсон) учун (Аллоҳнинг азобини қайтаргувчи) бирон куч ҳам, бирон ёрдамчи ҳам бўлмас'}, {'chapter': 86, 'verse': 11, 'text': 'وَٱلسَّمَآءِ ذَاتِ ٱلرَّجۡعِ\nҚайта-қайта тушгувчи (ёмғир) эгаси бўлмиш осмонга қасам'}, {'chapter': 86, 'verse': 12, 'text': 'وَٱلۡأَرۡضِ ذَاتِ ٱلصَّدۡعِ\n(Ўша ёмғир сабабли) ёриб чиққувчи (дон-дун, ўт-ўлан) эгаси бўлмиш Ерга қасамки'}, {'chapter': 86, 'verse': 13, 'text': 'إِنَّهُۥ لَقَوۡلٞ فَصۡلٞ\nШак-шубҳасиз, у (Қуръон ҳақ билан ботилни) ажратгувчи Сўздир'}, {'chapter': 86, 'verse': 14, 'text': 'وَمَا هُوَ بِٱلۡهَزۡلِ\nУ ҳазил (яъни, асоссиз, беҳуда сўз) эмасдир'}, {'chapter': 86, 'verse': 15, 'text': 'إِنَّهُمۡ يَكِيدُونَ كَيۡدٗا\nАлбатта (кофирлар Муҳаммад алайҳис-салоту вас-саломга қарши турли) ҳийла-найранг қилурлар'}, {'chapter': 86, 'verse': 16, 'text': 'وَأَكِيدُ كَيۡدٗا\nМен ҳам бир ҳийла қилурман (яъни, уларнинг ҳийла-найрангларига муносиб жазо берурман)'}, {'chapter': 86, 'verse': 17, 'text': 'فَمَهِّلِ ٱلۡكَٰفِرِينَ أَمۡهِلۡهُمۡ رُوَيۡدَۢا\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз уларга озгина вақт-муҳлат беринг (яъни, сиз бир оз шошмай туринг, майли улар бугун дину иймонга қарши қўлларидан келганини қилсинлар, эртага Қиёмат қойим бўлганида, албатта, қилмишларига яраша жазоларини олурлар)'}, {'chapter': 87, 'verse': 1, 'text': 'سَبِّحِ ٱسۡمَ رَبِّكَ ٱلۡأَعۡلَى\n(Эй Муҳаммад алайҳис-салоту вас-салом), энг олий зот бўлмиш Парвардигорингизнинг номини (ҳар қандай айб-нуқсондан) покланг'}, {'chapter': 87, 'verse': 2, 'text': 'ٱلَّذِي خَلَقَ فَسَوَّىٰ\nУ (барча нарсани) яратиб, расо қилиб қўйган Зотдир'}, {'chapter': 87, 'verse': 3, 'text': 'وَٱلَّذِي قَدَّرَ فَهَدَىٰ\nУ (ҳар бир нарса учун муносиб йўлни) белгилаб, (у нарсани ўша йўлга) ҳидоят қилиб қўйган Зотдир'}, {'chapter': 87, 'verse': 4, 'text': 'وَٱلَّذِيٓ أَخۡرَجَ ٱلۡمَرۡعَىٰ\nУ ўт-ўланни ундириб чиқариб'}, {'chapter': 87, 'verse': 5, 'text': 'فَجَعَلَهُۥ غُثَآءً أَحۡوَىٰ\nсўнг уни қорамтир хас-хашак қилиб қўйган Зотдир'}, {'chapter': 87, 'verse': 6, 'text': 'سَنُقۡرِئُكَ فَلَا تَنسَىٰٓ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизни (Жаброил фаришта воситасида Қуръон) қироат қилдирурмиз. Бас, сиз (бирон оят)ни унутмассиз'}, {'chapter': 87, 'verse': 7, 'text': 'إِلَّا مَا شَآءَ ٱللَّهُۚ إِنَّهُۥ يَعۡلَمُ ٱلۡجَهۡرَ وَمَا يَخۡفَىٰ\nАллоҳнинг Ўзи (унутишингизни) хоҳлаган оятлардан бошқа (бирон оятни унутмассиз). Албатта У зот ошкора (сўз ва ишлар)ни ҳам, яширин нарсаларни ҳам билиб турур'}, {'chapter': 87, 'verse': 8, 'text': 'وَنُيَسِّرُكَ لِلۡيُسۡرَىٰ\nБиз сизни осон (Динга) муваффақ қилурмиз'}, {'chapter': 87, 'verse': 9, 'text': 'فَذَكِّرۡ إِن نَّفَعَتِ ٱلذِّكۡرَىٰ\nБас, агар панд-насиҳат фойда берса (кишиларга) панд-насиҳат қилинг! (Яъни, насиҳат кор қилмайдиган кимсаларга сўзингизни исроф қилманг)'}, {'chapter': 87, 'verse': 10, 'text': 'سَيَذَّكَّرُ مَن يَخۡشَىٰ\n(Аллоҳдан) қўрқадиган киши панд-насиҳат олажак'}, {'chapter': 87, 'verse': 11, 'text': 'وَيَتَجَنَّبُهَا ٱلۡأَشۡقَى\nБадбахт кимса эса ўзини у (насиҳат)дан четга олур'}, {'chapter': 87, 'verse': 12, 'text': 'ٱلَّذِي يَصۡلَى ٱلنَّارَ ٱلۡكُبۡرَىٰ\nКатта ўтга (дўзахга) кирадиган (бадбахт кимса ўзини насиҳатдан четга олур)'}, {'chapter': 87, 'verse': 13, 'text': 'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحۡيَىٰ\nСўнгра у жойда на ўла олур ва на яшай олур'}, {'chapter': 87, 'verse': 14, 'text': 'قَدۡ أَفۡلَحَ مَن تَزَكَّىٰ\nДарҳақиқат, (куфру исёндан) пок бўлган киши нажот топгандир'}, {'chapter': 87, 'verse': 15, 'text': 'وَذَكَرَ ٱسۡمَ رَبِّهِۦ فَصَلَّىٰ\nВа Парвардигорининг номини ёд этиб намоз ўқиган (ҳар бир киши нажот топгандир)'}, {'chapter': 87, 'verse': 16, 'text': 'بَلۡ تُؤۡثِرُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا\nЙўқ, сизлар (эй кофирлар), ҳаёти дунёни устун қўюрсизлар'}, {'chapter': 87, 'verse': 17, 'text': 'وَٱلۡأٓخِرَةُ خَيۡرٞ وَأَبۡقَىٰٓ\nҲолбуки, Охират яхшироқ ва боқийроқдир'}, {'chapter': 87, 'verse': 18, 'text': 'إِنَّ هَٰذَا لَفِي ٱلصُّحُفِ ٱلۡأُولَىٰ\nДарвоқеъ, бу (сурада мазкур бўлган панд-насиҳатлар) аввалги (пайғамбарларга нозил бўлган) саҳифаларда'}, {'chapter': 87, 'verse': 19, 'text': 'صُحُفِ إِبۡرَٰهِيمَ وَمُوسَىٰ\n— Иброҳим ва Мусо саҳифаларида ҳам бордир'}, {'chapter': 88, 'verse': 1, 'text': 'هَلۡ أَتَىٰكَ حَدِيثُ ٱلۡغَٰشِيَةِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), сизга ўраб олгувчи (Қиёмат Куни) ҳақидага хабар келдими'}, {'chapter': 88, 'verse': 2, 'text': 'وُجُوهٞ يَوۡمَئِذٍ خَٰشِعَةٌ\nУ Кунда (кофирларнинг) юзлари эгилиб қолгувчи'}, {'chapter': 88, 'verse': 3, 'text': 'عَامِلَةٞ نَّاصِبَةٞ\n(Бўйин ва қўл-оёқлари кишан ва занжирларни судраш билан) меҳнат-машаққат чеккувчидир'}, {'chapter': 88, 'verse': 4, 'text': 'تَصۡلَىٰ نَارًا حَامِيَةٗ\n(Улар) қизиган дўзахга кирур'}, {'chapter': 88, 'verse': 5, 'text': 'تُسۡقَىٰ مِنۡ عَيۡنٍ ءَانِيَةٖ\nҚайнаб турган булоқдан суғорилур'}, {'chapter': 88, 'verse': 6, 'text': 'لَّيۡسَ لَهُمۡ طَعَامٌ إِلَّا مِن ضَرِيعٖ\nУлар учун бирон таом бўлмас, (уларнинг емишлари) фақат зорийъдан (заҳарли ва бадбўй тикансимон ўсимлик) бўлур'}, {'chapter': 88, 'verse': 7, 'text': 'لَّا يُسۡمِنُ وَلَا يُغۡنِي مِن جُوعٖ\nУ (еган кимсани) семиртирмайди ҳам ва очлиқдан халос ҳам қилмайди'}, {'chapter': 88, 'verse': 8, 'text': 'وُجُوهٞ يَوۡمَئِذٖ نَّاعِمَةٞ\n(Мўминларнинг) юзлари эса у Кунда (ўзларига ато этилган ноз-неъматлардан) шод-хуррам'}, {'chapter': 88, 'verse': 9, 'text': 'لِّسَعۡيِهَا رَاضِيَةٞ\n(Ҳаёти-дунёда Аллоҳ йўлида қилган) саъй-ҳаракатларидан рози бўлур'}, {'chapter': 88, 'verse': 10, 'text': 'فِي جَنَّةٍ عَالِيَةٖ\n(Улар) олий жаннатда бўлиб'}, {'chapter': 88, 'verse': 11, 'text': 'لَّا تَسۡمَعُ فِيهَا لَٰغِيَةٗ\nУ жойда бирон беҳуда сўз эшитмаслар'}, {'chapter': 88, 'verse': 12, 'text': 'فِيهَا عَيۡنٞ جَارِيَةٞ\nУ жойда оқар булоқ бордир'}, {'chapter': 88, 'verse': 13, 'text': 'فِيهَا سُرُرٞ مَّرۡفُوعَةٞ\nУ жойда баланд сўрилар'}, {'chapter': 88, 'verse': 14, 'text': 'وَأَكۡوَابٞ مَّوۡضُوعَةٞ\nВа (булоқ бўйига) қўйиб қўйилган қадаҳлар'}, {'chapter': 88, 'verse': 15, 'text': 'وَنَمَارِقُ مَصۡفُوفَةٞ\nТизиб қўйилган ёстиқлар'}, {'chapter': 88, 'verse': 16, 'text': 'وَزَرَابِيُّ مَبۡثُوثَةٌ\nВа тўшалган гиламлар бордир'}, {'chapter': 88, 'verse': 17, 'text': 'أَفَلَا يَنظُرُونَ إِلَى ٱلۡإِبِلِ كَيۡفَ خُلِقَتۡ\nАхир улар туянинг қандай яратилганига боқмайдиларми'}, {'chapter': 88, 'verse': 18, 'text': 'وَإِلَى ٱلسَّمَآءِ كَيۡفَ رُفِعَتۡ\nОсмоннинг қандай кўтариб қўйилганига'}, {'chapter': 88, 'verse': 19, 'text': 'وَإِلَى ٱلۡجِبَالِ كَيۡفَ نُصِبَتۡ\nТоғларнинг қандай тикланганига'}, {'chapter': 88, 'verse': 20, 'text': 'وَإِلَى ٱلۡأَرۡضِ كَيۡفَ سُطِحَتۡ\nВа Ернинг қандай ёйиб-текислаб қўйилганига (ибрат назари билан боқмайдиларми)'}, {'chapter': 88, 'verse': 21, 'text': 'فَذَكِّرۡ إِنَّمَآ أَنتَ مُذَكِّرٞ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), сиз панд-насиҳат қилинг! Зотан, сиз фақат бир панд-насиҳат қилгувчидирсиз, холос'}, {'chapter': 88, 'verse': 22, 'text': 'لَّسۡتَ عَلَيۡهِم بِمُصَيۡطِرٍ\nСиз уларнинг устида зўравонлик билан ҳукм юргизгувчи эмассиз'}, {'chapter': 88, 'verse': 23, 'text': 'إِلَّا مَن تَوَلَّىٰ وَكَفَرَ\nИлло ким (бу панд-насиҳатдан) юз ўгириб, кофир бўлса'}, {'chapter': 88, 'verse': 24, 'text': 'فَيُعَذِّبُهُ ٱللَّهُ ٱلۡعَذَابَ ٱلۡأَكۡبَرَ\nУ ҳолда Аллоҳ уни энг катта азоб билан азоблар'}, {'chapter': 88, 'verse': 25, 'text': 'إِنَّ إِلَيۡنَآ إِيَابَهُمۡ\nЗеро, ёлғиз Ўзимизга қайтишлари бордир'}, {'chapter': 88, 'verse': 26, 'text': 'ثُمَّ إِنَّ عَلَيۡنَا حِسَابَهُم\nСўнгра уларни ҳисоб-китоб қилиш (жазо бериш) ҳам ёлғиз Бизнинг зиммамиздадир'}, {'chapter': 89, 'verse': 1, 'text': 'وَٱلۡفَجۡرِ\nТонгга қасам'}, {'chapter': 89, 'verse': 2, 'text': 'وَلَيَالٍ عَشۡرٖ\n(Зул-ҳижжа ойидаги аввалги) ўн кечага қасам'}, {'chapter': 89, 'verse': 3, 'text': 'وَٱلشَّفۡعِ وَٱلۡوَتۡرِ\nЖуфт ва тоқ (ракаатли намозлар)га қасам'}, {'chapter': 89, 'verse': 4, 'text': 'وَٱلَّيۡلِ إِذَا يَسۡرِ\nЎтиб бораётган кечага қасамки, (албатта кофирлар азобга гирифтор бўлурлар)'}, {'chapter': 89, 'verse': 5, 'text': 'هَلۡ فِي ذَٰلِكَ قَسَمٞ لِّذِي حِجۡرٍ\nМана шу (қасам ичилган нарсаларда) ақл эгаси учун (етарли) қасам бордир'}, {'chapter': 89, 'verse': 6, 'text': 'أَلَمۡ تَرَ كَيۡفَ فَعَلَ رَبُّكَ بِعَادٍ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиз Од (қабиласи)ни қандай (ҳалокатга дучор) қилганини кўрмадингизми'}, {'chapter': 89, 'verse': 7, 'text': 'إِرَمَ ذَاتِ ٱلۡعِمَادِ\n(Улар) баланд устун(ли қаср)лар эгаси бўлган Иром (шаҳрида яшаган Од қабиласини қандай ҳалокатга дучор қилганини кўрмадингизми)'}, {'chapter': 89, 'verse': 8, 'text': 'ٱلَّتِي لَمۡ يُخۡلَقۡ مِثۡلُهَا فِي ٱلۡبِلَٰدِ\nУ (шаҳар бошқа) юртларда ўхшаши яратилмаган эди'}, {'chapter': 89, 'verse': 9, 'text': 'وَثَمُودَ ٱلَّذِينَ جَابُواْ ٱلصَّخۡرَ بِٱلۡوَادِ\n(Қуро) водийсида харсанг тошларни кес(иб ўзларига уйлар солиб ол)ган кимсалар бўлмиш Самуд (қабиласини қандай ҳалокатга дучор қилганини-чи)'}, {'chapter': 89, 'verse': 10, 'text': 'وَفِرۡعَوۡنَ ذِي ٱلۡأَوۡتَادِ\nҚозиқлар (яъни, ерга қозиқдек қоқилган баланд эҳромлар ва қасрлар) эгаси бўлган Фиръавнни (қандай ҳалокатга дучор қилганини-чи)'}, {'chapter': 89, 'verse': 11, 'text': 'ٱلَّذِينَ طَغَوۡاْ فِي ٱلۡبِلَٰدِ\nУлар юртларида ҳадларидан ошиб'}, {'chapter': 89, 'verse': 12, 'text': 'فَأَكۡثَرُواْ فِيهَا ٱلۡفَسَادَ\nу жойларда бузғунчиликни кўпайтириб юборган эдилар'}, {'chapter': 89, 'verse': 13, 'text': 'فَصَبَّ عَلَيۡهِمۡ رَبُّكَ سَوۡطَ عَذَابٍ\nБас, Парвардигорингиз уларнинг устига турли азоб-офатни ёғдирди'}, {'chapter': 89, 'verse': 14, 'text': 'إِنَّ رَبَّكَ لَبِٱلۡمِرۡصَادِ\nШак-шубҳасиз, Парвардигорингиз (барча нарсани) кузатиб тургувчидир'}, {'chapter': 89, 'verse': 15, 'text': 'فَأَمَّا ٱلۡإِنسَٰنُ إِذَا مَا ٱبۡتَلَىٰهُ رَبُّهُۥ فَأَكۡرَمَهُۥ وَنَعَّمَهُۥ فَيَقُولُ رَبِّيٓ أَكۡرَمَنِ\nБас, энди инсон — қачон Парвардигори уни имтиҳон қилиб, азиз қилиб қўйса ва унга неъмат ато этса дарҳол: «Парвардигорим (иззат-ҳурматга лойиқ бўлганим учун) мени азиз қилди», дер'}, {'chapter': 89, 'verse': 16, 'text': 'وَأَمَّآ إِذَا مَا ٱبۡتَلَىٰهُ فَقَدَرَ عَلَيۡهِ رِزۡقَهُۥ فَيَقُولُ رَبِّيٓ أَهَٰنَنِ\nЭнди қачон (Парвардигори) уни имтиҳон қилиб, ризқини танг килиб қўйса, дарҳол: «Парвардигорим мени хор қилди», дер'}, {'chapter': 89, 'verse': 17, 'text': 'كَلَّاۖ بَل لَّا تُكۡرِمُونَ ٱلۡيَتِيمَ\nЙўқ, (сизлар гумон қилганларингиздек иззат-икром бойлик билан, хор-зор қилиш — камбағаллик билан бўлмас)! Йўқ, сизлар етимни иззат-икром қилмассизлар'}, {'chapter': 89, 'verse': 18, 'text': 'وَلَا تَحَـٰٓضُّونَ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ\nМискин-бечорага таом беришга ҳам бир-бирларингни тарғиб қилмассизлар'}, {'chapter': 89, 'verse': 19, 'text': 'وَتَأۡكُلُونَ ٱلتُّرَاثَ أَكۡلٗا لَّمّٗا\nМеросни эса (ўзларингизнинг улушингизга ўзгаларникини ҳам) қўшиб ейиш билан еяверурсизлар'}, {'chapter': 89, 'verse': 20, 'text': 'وَتُحِبُّونَ ٱلۡمَالَ حُبّٗا جَمّٗا\nЯна мол-дунёни қаттиқ муҳаббат билан яхши кўрурсизлар'}, {'chapter': 89, 'verse': 21, 'text': 'كَلَّآۖ إِذَا دُكَّتِ ٱلۡأَرۡضُ دَكّٗا دَكّٗا\nЙўқ, (бундай кирдикорларингиздан қайтингиз)! Қачон Ер (зилзилага тушиб, барча нарса) чилпарчин қилинганда'}, {'chapter': 89, 'verse': 22, 'text': 'وَجَآءَ رَبُّكَ وَٱلۡمَلَكُ صَفّٗا صَفّٗا\nПарвардигорингиз ва фаришталар саф-саф бўлиб келганда'}, {'chapter': 89, 'verse': 23, 'text': 'وَجِاْيٓءَ يَوۡمَئِذِۭ بِجَهَنَّمَۚ يَوۡمَئِذٖ يَتَذَكَّرُ ٱلۡإِنسَٰنُ وَأَنَّىٰ لَهُ ٱلذِّكۡرَىٰ\n(Кофирлар кўришлари учун) ўша Кунда жаҳаннамни ҳам (яқин) келтириб қўйилганда — ана ўша Кунда инсон (бу кўрганларидан) эслатма-ибрат олур! (Аммо у Кунда) бу эслатма-ибратнинг фойдаси) қаёқдан тегсин'}, {'chapter': 89, 'verse': 24, 'text': 'يَقُولُ يَٰلَيۡتَنِي قَدَّمۡتُ لِحَيَاتِي\nУ: «Эҳ, кошки эди мен ҳаёт вақтимда (яхши амаллар) қилиб ўтган бўлсам», деб қолур'}, {'chapter': 89, 'verse': 25, 'text': 'فَيَوۡمَئِذٖ لَّا يُعَذِّبُ عَذَابَهُۥٓ أَحَدٞ\nУ Кундаги (Аллоҳнинг) азоби каби ҳеч ким азоблай олмас'}, {'chapter': 89, 'verse': 26, 'text': 'وَلَا يُوثِقُ وَثَاقَهُۥٓ أَحَدٞ\nВа У зотнинг (кишан ва занжирлар билан) боғлаши каби ҳеч ким боғлай олмас'}, {'chapter': 89, 'verse': 27, 'text': 'يَـٰٓأَيَّتُهَا ٱلنَّفۡسُ ٱلۡمُطۡمَئِنَّةُ\n(У Кунда ҳаёти дунёдан Аллоҳ таолонинг ваъдасига ишониб иймон ва эзгу амаллар билан ўтган мўминларга хитоб қилиниб, дейилур): «Эй хотиржам-сокин жон'}, {'chapter': 89, 'verse': 28, 'text': 'ٱرۡجِعِيٓ إِلَىٰ رَبِّكِ رَاضِيَةٗ مَّرۡضِيَّةٗ\nСен (Аллоҳ ато этган неъматлардан) рози бўлган (ва Аллоҳ таоло томонидан сенинг амалларингдан) рози бўлинган ҳолда Парвардигоринг (ҳузури)га қайт'}, {'chapter': 89, 'verse': 29, 'text': 'فَٱدۡخُلِي فِي عِبَٰدِي\nБас, (солиҳ) бандаларим қаторига киргин'}, {'chapter': 89, 'verse': 30, 'text': 'وَٱدۡخُلِي جَنَّتِي\nВа Менинг жаннатимга киргин!»'}, {'chapter': 90, 'verse': 1, 'text': 'لَآ أُقۡسِمُ بِهَٰذَا ٱلۡبَلَدِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), мана шу шаҳарга'}, {'chapter': 90, 'verse': 2, 'text': 'وَأَنتَ حِلُّۢ بِهَٰذَا ٱلۡبَلَدِ\n— Ҳолбуки сиз ҳам мана шу шаҳарда яшайсиз'}, {'chapter': 90, 'verse': 3, 'text': 'وَوَالِدٖ وَمَا وَلَدَ\n— Ҳамда ота ва ундан бўлган болаларга (яъни, Одам алайҳиссалом ва унинг барча зурриётларига) қасамёд қилурманки'}, {'chapter': 90, 'verse': 4, 'text': 'لَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ فِي كَبَدٍ\nДарҳақиқат, Биз инсонни меҳнат-машаққатга яратдик'}, {'chapter': 90, 'verse': 5, 'text': 'أَيَحۡسَبُ أَن لَّن يَقۡدِرَ عَلَيۡهِ أَحَدٞ\nУ ўзига ҳеч кимнинг кучи етмайди, деб ўйларми'}, {'chapter': 90, 'verse': 6, 'text': 'يَقُولُ أَهۡلَكۡتُ مَالٗا لُّبَدًا\nУ (мақтаниб): «Жуда кўп мол-дунёни (сарфлаб) йўқ қилдим», дер'}, {'chapter': 90, 'verse': 7, 'text': 'أَيَحۡسَبُ أَن لَّمۡ يَرَهُۥٓ أَحَدٌ\nУ ўзини (риёкорлик билан мол-дунё совураётган пайтида) ҳеч ким кўрмаган, деб ўйларми?! (Йўқ, Аллоҳ таоло ҳар вақт, ҳар нарсани кўриб-билиб турур)'}, {'chapter': 90, 'verse': 8, 'text': 'أَلَمۡ نَجۡعَل لَّهُۥ عَيۡنَيۡنِ\nАхир Биз унга икки кўз'}, {'chapter': 90, 'verse': 9, 'text': 'وَلِسَانٗا وَشَفَتَيۡنِ\nтил ва икки лаб (ато) қилмадикми'}, {'chapter': 90, 'verse': 10, 'text': 'وَهَدَيۡنَٰهُ ٱلنَّجۡدَيۡنِ\nВа Биз уни икки баландликка (яъни, яхшилик ёки ёмонлик довонларини ошиб ўтишга) йўллаб қўйдик-ку'}, {'chapter': 90, 'verse': 11, 'text': 'فَلَا ٱقۡتَحَمَ ٱلۡعَقَبَةَ\nБас, у (мақтанчоқ кимса яхшилик) довонини ошиб ўтмади'}, {'chapter': 90, 'verse': 12, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡعَقَبَةُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), довон (ошиши) нима эканини сиз қаердан билар эдингиз'}, {'chapter': 90, 'verse': 13, 'text': 'فَكُّ رَقَبَةٍ\n(У бир қул) бўйнини (қулликдан) озод қилмоқ'}, {'chapter': 90, 'verse': 14, 'text': 'أَوۡ إِطۡعَٰمٞ فِي يَوۡمٖ ذِي مَسۡغَبَةٖ\nЁки очарчилик кунида таом бермоқдир'}, {'chapter': 90, 'verse': 15, 'text': 'يَتِيمٗا ذَا مَقۡرَبَةٍ\n— Бирон қардош етимга'}, {'chapter': 90, 'verse': 16, 'text': 'أَوۡ مِسۡكِينٗا ذَا مَتۡرَبَةٖ\n— Ё муҳтож бечора-мискинга (таом бермоқдир)'}, {'chapter': 90, 'verse': 17, 'text': 'ثُمَّ كَانَ مِنَ ٱلَّذِينَ ءَامَنُواْ وَتَوَاصَوۡاْ بِٱلصَّبۡرِ وَتَوَاصَوۡاْ بِٱلۡمَرۡحَمَةِ\nСўнгра у (мақтанчоқ кимса) иймон келтирган ва бир-бирларига (тоат-ибодатини адо этишдаги машаққатларга) сабр-қаноат қилишни тавсия этган, бир-бирларига (Аллоҳнинг бандаларига) меҳр-мурувват кўргазишни тавсия этган зотлардан бўлмади'}, {'chapter': 90, 'verse': 18, 'text': 'أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡمَيۡمَنَةِ\nАна ўша (яъни, юқоридаги фазилатларга эга бўлган) зотлар ўнг томон эгаларидир (яъни, Охират Кунида ишлари ўнгдан келгувчи саодатманд кишилардир)'}, {'chapter': 90, 'verse': 19, 'text': 'وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا هُمۡ أَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ\nБизнинг оятларимизга кофир бўлган кимсалар эса чап томон эгаларидир'}, {'chapter': 90, 'verse': 20, 'text': 'عَلَيۡهِمۡ نَارٞ مُّؤۡصَدَةُۢ\nУларнинг устида қопланиб қолгувчи олов-дўзах бўлур'}, {'chapter': 91, 'verse': 1, 'text': 'وَٱلشَّمۡسِ وَضُحَىٰهَا\nҚуёш ва унинг ёғдусига қасам'}, {'chapter': 91, 'verse': 2, 'text': 'وَٱلۡقَمَرِ إِذَا تَلَىٰهَا\nУ (қуёш)нинг ортидан келган ойга қасам'}, {'chapter': 91, 'verse': 3, 'text': 'وَٱلنَّهَارِ إِذَا جَلَّىٰهَا\nУ (қуёшни оламга) ошкор қилган кундузга қасам'}, {'chapter': 91, 'verse': 4, 'text': 'وَٱلَّيۡلِ إِذَا يَغۡشَىٰهَا\nУ (қуёшни ўз зулмати билан) ўраб-яширган кечага қасам'}, {'chapter': 91, 'verse': 5, 'text': 'وَٱلسَّمَآءِ وَمَا بَنَىٰهَا\nОсмонга ва уни бино қилган Зотга қасам'}, {'chapter': 91, 'verse': 6, 'text': 'وَٱلۡأَرۡضِ وَمَا طَحَىٰهَا\nЕрга ва уни ёйиб-текис қилиб қўйган Зотга қасам'}, {'chapter': 91, 'verse': 7, 'text': 'وَنَفۡسٖ وَمَا سَوَّىٰهَا\nЖонга ва уни расо қилиб-яратган'}, {'chapter': 91, 'verse': 8, 'text': 'فَأَلۡهَمَهَا فُجُورَهَا وَتَقۡوَىٰهَا\nУнга фисқ-фужурини ҳам, тақвосини ҳам илҳом қилиб-ўргатиб қўйган Зотга қасамки'}, {'chapter': 91, 'verse': 9, 'text': 'قَدۡ أَفۡلَحَ مَن زَكَّىٰهَا\nДарҳақиқат, уни (яъни, ўз нафсини-жонини иймон ва тақво билан) поклаган киши нажот топди'}, {'chapter': 91, 'verse': 10, 'text': 'وَقَدۡ خَابَ مَن دَسَّىٰهَا\nВа у (жонни фисқ-фужур билан) кўмиб хорлаган кимса номурод бўлди'}, {'chapter': 91, 'verse': 11, 'text': 'كَذَّبَتۡ ثَمُودُ بِطَغۡوَىٰهَآ\nСамуд (қабиласи) ҳаддан ошганлари сабабли (ўз пайғамбарларини) ёлғончи қилишди'}, {'chapter': 91, 'verse': 12, 'text': 'إِذِ ٱنۢبَعَثَ أَشۡقَىٰهَا\nЭсланг, уларнинг энг бадбахтлари (туяни сўйиш учун) қўзғалганларида'}, {'chapter': 91, 'verse': 13, 'text': 'فَقَالَ لَهُمۡ رَسُولُ ٱللَّهِ نَاقَةَ ٱللَّهِ وَسُقۡيَٰهَا\nАллоҳнинг пайғамбари (Солиҳ алайҳиссалом) уларга: «Аллоҳнинг туяси(ни сўйиб юбориш)дан ва уни суғориш(га тўсиқ бўлиш)дан сақланинглар!», деди'}, {'chapter': 91, 'verse': 14, 'text': 'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمۡدَمَ عَلَيۡهِمۡ رَبُّهُم بِذَنۢبِهِمۡ فَسَوَّىٰهَا\nБас, улар (Пайғамбарни) ёлғончи қилишиб, у (туя)ни сўйиб юборган эдилар, Парвардигорлари ўзларининг гуноҳлари сабабли устларига қирғин юбориб, уни (қирғинни барчаларига) баробар қилди'}, {'chapter': 91, 'verse': 15, 'text': 'وَلَا يَخَافُ عُقۡبَٰهَا\n(Зотан, Аллоҳ таоло бу ишининг) оқибатидан қўрқмас'}, {'chapter': 92, 'verse': 1, 'text': 'وَٱلَّيۡلِ إِذَا يَغۡشَىٰ\n(Борлиқни ўз зулмати билан) ўраб келаётган кечага қасам'}, {'chapter': 92, 'verse': 2, 'text': 'وَٱلنَّهَارِ إِذَا تَجَلَّىٰ\nЁришиб-кўринган кундузга қасам'}, {'chapter': 92, 'verse': 3, 'text': 'وَمَا خَلَقَ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ\nЭркак ва аёлни яратган зотга қасамки'}, {'chapter': 92, 'verse': 4, 'text': 'إِنَّ سَعۡيَكُمۡ لَشَتَّىٰ\nШак-шубҳасиз, сизларнинг саъй-ҳаракатларингиз хилма-хилдир'}, {'chapter': 92, 'verse': 5, 'text': 'فَأَمَّا مَنۡ أَعۡطَىٰ وَٱتَّقَىٰ\nАна энди ким (ўз мол-давлатидаги камбағал-бечораларга берилиши лозим бўлган закот ва бошқа садақотларни) ато этса ва (Аллоҳдан) қўрқса'}, {'chapter': 92, 'verse': 6, 'text': 'وَصَدَّقَ بِٱلۡحُسۡنَىٰ\nҲамда гўзал оқибатни (яъни, жаннат бор эканини) тасдиқ этса'}, {'chapter': 92, 'verse': 7, 'text': 'فَسَنُيَسِّرُهُۥ لِلۡيُسۡرَىٰ\nБас, Биз уни осон йўлга муяссар қилурмиз'}, {'chapter': 92, 'verse': 8, 'text': 'وَأَمَّا مَنۢ بَخِلَ وَٱسۡتَغۡنَىٰ\nЭнди ким (Аллоҳ йўлида хайр-саховат кўрсатишдан) бахиллик қилса ва (ўзини Аллоҳ ҳузуридаги ажр-мукофотлардан) беҳожат билса'}, {'chapter': 92, 'verse': 9, 'text': 'وَكَذَّبَ بِٱلۡحُسۡنَىٰ\nҲамда гўзал оқибатни (яъни, Аллоҳ ваъда қилган жаннатни) ёлғон деса'}, {'chapter': 92, 'verse': 10, 'text': 'فَسَنُيَسِّرُهُۥ لِلۡعُسۡرَىٰ\nБас, Биз уни (дунё ва Охиратда бахтсиз бўладиган) оғир йўлга «муяссар» қилурмиз'}, {'chapter': 92, 'verse': 11, 'text': 'وَمَا يُغۡنِي عَنۡهُ مَالُهُۥٓ إِذَا تَرَدَّىٰٓ\nВа у (дўзахда) ҳалок бўлган вақтида молу дунёси унга фойда бермас'}, {'chapter': 92, 'verse': 12, 'text': 'إِنَّ عَلَيۡنَا لَلۡهُدَىٰ\nШак-шубҳасиз, ҳидоят (яъни, инсонларни Тўғри Йўлга йўллаш) ёлғиз Бизнинг зиммамиздадир'}, {'chapter': 92, 'verse': 13, 'text': 'وَإِنَّ لَنَا لَلۡأٓخِرَةَ وَٱلۡأُولَىٰ\nШак-шубҳасиз, Охират ҳам, дунё ҳам ёлғиз Бизникидир'}, {'chapter': 92, 'verse': 14, 'text': 'فَأَنذَرۡتُكُمۡ نَارٗا تَلَظَّىٰ\nБас, (эй инсонлар), Мен сизларни ловуллаб ёниб турган оловдан — дўзахдан огоҳлантирдим'}, {'chapter': 92, 'verse': 15, 'text': 'لَا يَصۡلَىٰهَآ إِلَّا ٱلۡأَشۡقَى\nУнга фақат бадбахт кимсагина кирур'}, {'chapter': 92, 'verse': 16, 'text': 'ٱلَّذِي كَذَّبَ وَتَوَلَّىٰ\n(Ҳақни) ёлғон деган ва (иймон-эътиқоддан) юз ўгирган (бадбахт кимсагина кирур)'}, {'chapter': 92, 'verse': 17, 'text': 'وَسَيُجَنَّبُهَا ٱلۡأَتۡقَى\nТақводор зот у (дўзах)дан йироқ қилинур'}, {'chapter': 92, 'verse': 18, 'text': 'ٱلَّذِي يُؤۡتِي مَالَهُۥ يَتَزَكَّىٰ\nЎзи поқдомон бўлиб, мол-давлатини (яхшилик йўлида) сарф қиладиган (тақводор зот ундан йироқ қилинур)'}, {'chapter': 92, 'verse': 19, 'text': 'وَمَا لِأَحَدٍ عِندَهُۥ مِن نِّعۡمَةٖ تُجۡزَىٰٓ\nУ (тақводор зот) ҳузурида — зиммасида бирон кимсага қайтариладиган неъмат йўқдир. (Яъни, у бирон кимсанинг ўзига ўтказиб қўйган яхшилигини қайтариш учун хайр-саховат кўрсатмайди)'}, {'chapter': 92, 'verse': 20, 'text': 'إِلَّا ٱبۡتِغَآءَ وَجۡهِ رَبِّهِ ٱلۡأَعۡلَىٰ\nУ фақат энг олий зот бўлмиш Парвардигорининг Юзини — ризолигини истаб (мол-давлатини сарф қилур)'}, {'chapter': 92, 'verse': 21, 'text': 'وَلَسَوۡفَ يَرۡضَىٰ\nВа яқинда (Парвардигори унга ато этадиган мукофот — жаннат неъматларидан) рози бўлур'}, {'chapter': 93, 'verse': 1, 'text': 'وَٱلضُّحَىٰ\nЧошгоҳ вақтига қасам'}, {'chapter': 93, 'verse': 2, 'text': 'وَٱلَّيۡلِ إِذَا سَجَىٰ\n(Ўз зулмати билан чор-атрофни) қоплаб-ўраб олган кечага қасамки'}, {'chapter': 93, 'verse': 3, 'text': 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиз сизни тарк этгани ҳам йўқ, ёмон кўриб қолгани ҳам йўқдир'}, {'chapter': 93, 'verse': 4, 'text': 'وَلَلۡأٓخِرَةُ خَيۡرٞ لَّكَ مِنَ ٱلۡأُولَىٰ\nАлбатта Охират сиз учун дунёдан яхшироқдир'}, {'chapter': 93, 'verse': 5, 'text': 'وَلَسَوۡفَ يُعۡطِيكَ رَبُّكَ فَتَرۡضَىٰٓ\nЯқинда Парвардигорингиз сизга (шундай неъматлар) ато этурки, сиз (у илоҳий марҳаматлардан) рози бўлурсиз'}, {'chapter': 93, 'verse': 6, 'text': 'أَلَمۡ يَجِدۡكَ يَتِيمٗا فَـَٔاوَىٰ\n(Эй Муҳаммад алайҳис-салоту вас-салом, Парвардигорингиз) сизни етим ҳолда топгач, бошпана бермадими'}, {'chapter': 93, 'verse': 7, 'text': 'وَوَجَدَكَ ضَآلّٗا فَهَدَىٰ\nУ зот сизни гумроҳ-ғофил ҳолда топиб, (Ҳақ йўлига) ҳидоят қилиб қўйди-ку'}, {'chapter': 93, 'verse': 8, 'text': 'وَوَجَدَكَ عَآئِلٗا فَأَغۡنَىٰ\nУ зот сизни камбағал ҳолда топиб, бой қилиб қўйди-ку'}, {'chapter': 93, 'verse': 9, 'text': 'فَأَمَّا ٱلۡيَتِيمَ فَلَا تَقۡهَرۡ\nБас, энди сиз ҳам етимга қаҳр қилманг'}, {'chapter': 93, 'verse': 10, 'text': 'وَأَمَّا ٱلسَّآئِلَ فَلَا تَنۡهَرۡ\nСўровчи гадони эса (бирон нарса бермасдан) ҳайдаманг'}, {'chapter': 93, 'verse': 11, 'text': 'وَأَمَّا بِنِعۡمَةِ رَبِّكَ فَحَدِّثۡ\nПарвардигорингознинг (сизга ато этган пайғамбарлик ва бошқа барча) неъмати ҳақида (кишиларга доимо) сўзланг'}, {'chapter': 94, 'verse': 1, 'text': 'أَلَمۡ نَشۡرَحۡ لَكَ صَدۡرَكَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Биз сизнинг қалбингизни (иймон ва Қуръон нури билан) кенг-мунаввар қилиб қўймадикми'}, {'chapter': 94, 'verse': 2, 'text': 'وَوَضَعۡنَا عَنكَ وِزۡرَكَ\nВа Биз сиздан юкингизни олиб қўйдик'}, {'chapter': 94, 'verse': 3, 'text': 'ٱلَّذِيٓ أَنقَضَ ظَهۡرَكَ\nУ елкангизни эзиб турган оғир (юкингизни олиб қўйдик)'}, {'chapter': 94, 'verse': 4, 'text': 'وَرَفَعۡنَا لَكَ ذِكۡرَكَ\nВа Биз сизнинг зикрингизни (яъни, мартабангизни) баланд қилиб қўйдик'}, {'chapter': 94, 'verse': 5, 'text': 'فَإِنَّ مَعَ ٱلۡعُسۡرِ يُسۡرًا\nБас, албатта ҳар бир оғирлик-машаққат билан бирга бир енгиллик ҳам бордир'}, {'chapter': 94, 'verse': 6, 'text': 'إِنَّ مَعَ ٱلۡعُسۡرِ يُسۡرٗا\nАлбатта ҳар бир оғирлик-машаққат билан бирга бир енгиллик ҳам бордир'}, {'chapter': 94, 'verse': 7, 'text': 'فَإِذَا فَرَغۡتَ فَٱنصَبۡ\nБас, (эй Муҳаммад алайҳис-салоту вас-салом), қачон сиз (кишиларни Ҳақ йўлга даъват қилишдан ва бошқа дунёвий ҳожатлардан — ишлардан) фориғ бўлсангиз (ўрнингиздан) туринг'}, {'chapter': 94, 'verse': 8, 'text': 'وَإِلَىٰ رَبِّكَ فَٱرۡغَب\nВа ёлғиз Парвардигорингизга юзингизни буринг'}, {'chapter': 95, 'verse': 1, 'text': 'وَٱلتِّينِ وَٱلزَّيۡتُونِ\nАнжирга қасам, зайтунга қасам'}, {'chapter': 95, 'verse': 2, 'text': 'وَطُورِ سِينِينَ\n(Аллоҳ таоло Мусо пайғамбар билан бевосита сўзлашган) Сино тоғига қасам'}, {'chapter': 95, 'verse': 3, 'text': 'وَهَٰذَا ٱلۡبَلَدِ ٱلۡأَمِينِ\nМана шу тинч-осойишта шаҳар (Маккаи Мукаррама)га қасамки'}, {'chapter': 95, 'verse': 4, 'text': 'لَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ فِيٓ أَحۡسَنِ تَقۡوِيمٖ\nДарҳақиқат, Биз инсонни энг гўзал шаклу шамойилда яратдик'}, {'chapter': 95, 'verse': 5, 'text': 'ثُمَّ رَدَدۡنَٰهُ أَسۡفَلَ سَٰفِلِينَ\nСўнгра (у Биз ато этган шундай гўзал сурат ва мукаммал хилқатнинг шукрини қилмасдан, куфру туғён йўлига кетгач), уни асфала-софилинга (яъни, энг тубан дўзахга) қайтардик'}, {'chapter': 95, 'verse': 6, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَلَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ\nФақат (қай) бир зотлар иймон келтирсалар ва яхши амаллар қилсалар, ана ўшалар учун битмас-туганмас ажр-мукофот бордир'}, {'chapter': 95, 'verse': 7, 'text': 'فَمَا يُكَذِّبُكَ بَعۡدُ بِٱلدِّينِ\nБас, (эй инсон), энди (яъни, Аллоҳ таолонинг барча нарсага қодир Зот эканлигини билганингдан сўнг) нима сени (Қиёмат Кунида бандаларнинг қилиб ўтган амалларига бериладиган) жазони ёлғон дейишга мажбур қилур'}, {'chapter': 95, 'verse': 8, 'text': 'أَلَيۡسَ ٱللَّهُ بِأَحۡكَمِ ٱلۡحَٰكِمِينَ\nАхир Аллоҳ ҳукм қилгувчиларнинг энг адолатлиси эмасми?! (Дарҳақиқат, Аллоҳ таоло Қиёмат Кунида энг одил ҳукм қилгувчидир)'}, {'chapter': 96, 'verse': 1, 'text': 'ٱقۡرَأۡ بِٱسۡمِ رَبِّكَ ٱلَّذِي خَلَقَ\n(Эй Муҳаммад алайҳис-салоту вас-салом, барча мавжудотни) яратган Зот бўлмиш Парпардигорингиз номи билан (бошлаб) ўқинг'}, {'chapter': 96, 'verse': 2, 'text': 'خَلَقَ ٱلۡإِنسَٰنَ مِنۡ عَلَقٍ\nУ инсонни лахта қондан яратган (Зотдир)'}, {'chapter': 96, 'verse': 3, 'text': 'ٱقۡرَأۡ وَرَبُّكَ ٱلۡأَكۡرَمُ\nЎқинг! Сизнинг Парвардигорингиз ўта карамли Зотдир'}, {'chapter': 96, 'verse': 4, 'text': 'ٱلَّذِي عَلَّمَ بِٱلۡقَلَمِ\nУ (инсониятга) қаламни (яъни, ёзишни — хатни) ўргатган Зотдир'}, {'chapter': 96, 'verse': 5, 'text': 'عَلَّمَ ٱلۡإِنسَٰنَ مَا لَمۡ يَعۡلَمۡ\nУ зот инсонга унинг билмаган нарсаларини ўргатди'}, {'chapter': 96, 'verse': 6, 'text': 'كَلَّآ إِنَّ ٱلۡإِنسَٰنَ لَيَطۡغَىٰٓ\nДарҳақиқат, (кофир) инсон албатта туғёнга тушар — ҳаддидан ошар'}, {'chapter': 96, 'verse': 7, 'text': 'أَن رَّءَاهُ ٱسۡتَغۡنَىٰٓ\nЎзини бой-беҳожат кўргач, (албатта туғёнга тушар)'}, {'chapter': 96, 'verse': 8, 'text': 'إِنَّ إِلَىٰ رَبِّكَ ٱلرُّجۡعَىٰٓ\n(Эй инсон), албатта ёлғиз Парвардигорингга қайтишинг бордир! (Ўшанда бу туғёнларинг учун ҳисоб-китоб қилиниб, жазоланишинг аниқдир)'}, {'chapter': 96, 'verse': 9, 'text': 'أَرَءَيۡتَ ٱلَّذِي يَنۡهَىٰ\n(Эй инсон), тўсувчи кимсани кўрдингми'}, {'chapter': 96, 'verse': 10, 'text': 'عَبۡدًا إِذَا صَلَّىٰٓ\nБир бандани (яъни, Муҳаммад алайҳис-салоту вас-саломни) (намозидан тўсадиган кимсани кўрдингми)'}, {'chapter': 96, 'verse': 11, 'text': 'أَرَءَيۡتَ إِن كَانَ عَلَى ٱلۡهُدَىٰٓ\nХабар бергин-чи, агар у (намоз ўқигувчи) ўзи Тўғри Йўлда бўлса'}, {'chapter': 96, 'verse': 12, 'text': 'أَوۡ أَمَرَ بِٱلتَّقۡوَىٰٓ\nЁки (ўзгаларни) тақвога (Аллоҳдан қўрқишга) буюрса, (уни намоздан ва тақводан тўсган кимса ҳалок бўлмасми)'}, {'chapter': 96, 'verse': 13, 'text': 'أَرَءَيۡتَ إِن كَذَّبَ وَتَوَلَّىٰٓ\nХабар бергин-чи, агар у (намоздан тўсгувчи кимса Аллоҳнинг китобини) ёлғон деса ва (у Китобга иймон келтиришдан) юз ўгирса'}, {'chapter': 96, 'verse': 14, 'text': 'أَلَمۡ يَعۡلَم بِأَنَّ ٱللَّهَ يَرَىٰ\nалбатта Аллоҳ (унинг барча қилмишларини) кўриб туришини билмасми'}, {'chapter': 96, 'verse': 15, 'text': 'كَلَّا لَئِن لَّمۡ يَنتَهِ لَنَسۡفَعَۢا بِٱلنَّاصِيَةِ\nЙўқ! Қасамки, агар у (бундай гумроҳликдан) тўхтамаса, албатта Биз унинг пешона сочидан тутармиз'}, {'chapter': 96, 'verse': 16, 'text': 'نَاصِيَةٖ كَٰذِبَةٍ خَاطِئَةٖ\n— ўша ёлғончи, адашган пешона сочидан тутармиз-да, (жаҳаннамга отурмиз)'}, {'chapter': 96, 'verse': 17, 'text': 'فَلۡيَدۡعُ نَادِيَهُۥ\nБас, у ўзининг жамоасини (ёрдамга) чақираверсин'}, {'chapter': 96, 'verse': 18, 'text': 'سَنَدۡعُ ٱلزَّبَانِيَةَ\nБиз эса азоб фаришталарини чақиражакмиз'}, {'chapter': 96, 'verse': 19, 'text': 'كَلَّا لَا تُطِعۡهُ وَٱسۡجُدۡۤ وَٱقۡتَرِب۩\nЙўқ! (Эй Муҳаммад алайҳис-салоту вас-салом), сиз унга итоат этманг ва (ёлғиз) Аллоҳга сажда-ибодат қилиб, (У зотга) яқин бўлинг'}, {'chapter': 97, 'verse': 1, 'text': 'إِنَّآ أَنزَلۡنَٰهُ فِي لَيۡلَةِ ٱلۡقَدۡرِ\nАлбатта Биз у (Қуръон)ни Қадр кечасида нозил қилдик'}, {'chapter': 97, 'verse': 2, 'text': 'وَمَآ أَدۡرَىٰكَ مَا لَيۡلَةُ ٱلۡقَدۡرِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Қадр кечаси нима эканлигини сиз қаердан билар эдингиз'}, {'chapter': 97, 'verse': 3, 'text': 'لَيۡلَةُ ٱلۡقَدۡرِ خَيۡرٞ مِّنۡ أَلۡفِ شَهۡرٖ\nҚадр кечаси минг ойдан яхшироқдир'}, {'chapter': 97, 'verse': 4, 'text': 'تَنَزَّلُ ٱلۡمَلَـٰٓئِكَةُ وَٱلرُّوحُ فِيهَا بِإِذۡنِ رَبِّهِم مِّن كُلِّ أَمۡرٖ\nУ (кеча)да фаришталар ва Руҳ (яъни, Жаброил алайҳиссалом) Парвардигорларининг изни-ихтиёри ила (йил давомида қилинадиган) барча ишлар билан (осмондан заминга) тушурлар'}, {'chapter': 97, 'verse': 5, 'text': 'سَلَٰمٌ هِيَ حَتَّىٰ مَطۡلَعِ ٱلۡفَجۡرِ\nУ (кеча) то тонг отгунича тинчлик-омонликдир'}, {'chapter': 98, 'verse': 1, 'text': 'لَمۡ يَكُنِ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَٱلۡمُشۡرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأۡتِيَهُمُ ٱلۡبَيِّنَةُ\nАҳли китобдан ва мушриклардан бўлган кофир кимсалар то уларга очиқ ҳужжат келгунича (куфрдан) ажрагувчи бўлмадилар'}, {'chapter': 98, 'verse': 2, 'text': 'رَسُولٞ مِّنَ ٱللَّهِ يَتۡلُواْ صُحُفٗا مُّطَهَّرَةٗ\n(У очиқ ҳужжат) Аллоҳ томонидан (юборилган) бир пайғамбар (яъни, Муҳаммад алайҳис-салоту вас-салом бўлиб, уларга) покиза саҳифаларни (яъни, Қуръонни) тиловат қилур'}, {'chapter': 98, 'verse': 3, 'text': 'فِيهَا كُتُبٞ قَيِّمَةٞ\nУ (Қуръонда) энг тўғри ёзувлар — ҳукмлар бордир'}, {'chapter': 98, 'verse': 4, 'text': 'وَمَا تَفَرَّقَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَةُ\nКитоб ато этилган кимсалар фақат уларга очиқ ҳужжат — пайғамбар келганидан кейингина бўлиниб кетдилар (яъни, айримлари ўша ўзлари кутган пайғамбарга иймон келтирдилар, айримлари эса унга кофир бўлдилар)'}, {'chapter': 98, 'verse': 5, 'text': 'وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُواْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ حُنَفَآءَ وَيُقِيمُواْ ٱلصَّلَوٰةَ وَيُؤۡتُواْ ٱلزَّكَوٰةَۚ وَذَٰلِكَ دِينُ ٱلۡقَيِّمَةِ\nҲолбуки, улар фақат ягона Аллоҳга, У зот учун динни холис қилган, Тўғри Йўлдан оғмаган ҳолларида ибодат қилишга ва намозни тўкис адо этишга ҳамда закотни (ҳақдорларга) ато этишга буюрилган эдилар. Мана шу Тўғри (Йўлдаги миллатнинг) динидир'}, {'chapter': 98, 'verse': 6, 'text': 'إِنَّ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَٱلۡمُشۡرِكِينَ فِي نَارِ جَهَنَّمَ خَٰلِدِينَ فِيهَآۚ أُوْلَـٰٓئِكَ هُمۡ شَرُّ ٱلۡبَرِيَّةِ\nАлбатта аҳли китобдан ва мушриклардан бўлган кофир кимсалар жаҳаннам ўтида бўлиб, ўша жойда мангу қолурлар! Ана ўшалар энг ёмон махлуқдирлар'}, {'chapter': 98, 'verse': 7, 'text': 'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ هُمۡ خَيۡرُ ٱلۡبَرِيَّةِ\nАлбатта иймон келтирган ва яхши амаллар қилган зотлар — ана ўшалар яралмиш жонзотларнинг энг яхшисидирлар'}, {'chapter': 98, 'verse': 8, 'text': 'جَزَآؤُهُمۡ عِندَ رَبِّهِمۡ جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ لِمَنۡ خَشِيَ رَبَّهُۥ\nПарвардигорлари ҳузуридаги уларнинг жазо-мукофотлари — остидан дарёлар оқиб турадиган мангу жаннатлардир. Улар ўша жойда абадул-абад қолгувчидирлар. Аллоҳ улардан рози бўлди, улар ҳам (Аллоҳдан) рози бўлдилар. Бу (мукофот) Парвардигоридан қўрққан киши учундир'}, {'chapter': 99, 'verse': 1, 'text': 'إِذَا زُلۡزِلَتِ ٱلۡأَرۡضُ زِلۡزَالَهَا\nҚачон Ер ўзининг (энг даҳшатли) зилзиласи билан ларзага тушганида'}, {'chapter': 99, 'verse': 2, 'text': 'وَأَخۡرَجَتِ ٱلۡأَرۡضُ أَثۡقَالَهَا\nВа Ер (ўз бағридаги хазинаю-дафиналардан ва инсон жасадларидан иборат) «юк»ларини (юзага) чиқариб ташлаганида'}, {'chapter': 99, 'verse': 3, 'text': 'وَقَالَ ٱلۡإِنسَٰنُ مَا لَهَا\nВа (ҳаётлик пайтида қайта тирилишни инкор қилгувчи бўлган) инсон (қайта тирилганидан даҳшатга тушиб: «Ерга) нима бўлди ўзи?», деб қолганида'}, {'chapter': 99, 'verse': 4, 'text': 'يَوۡمَئِذٖ تُحَدِّثُ أَخۡبَارَهَا\nАна ўша Кунда Ер ўз хабарларини сўзлар'}, {'chapter': 99, 'verse': 5, 'text': 'بِأَنَّ رَبَّكَ أَوۡحَىٰ لَهَا\nПарвардигорингиз унга ваҳий қил(иб, сўзлашга буюр)гани сабабли (ўз хабарларини сўзлар)'}, {'chapter': 99, 'verse': 6, 'text': 'يَوۡمَئِذٖ يَصۡدُرُ ٱلنَّاسُ أَشۡتَاتٗا لِّيُرَوۡاْ أَعۡمَٰلَهُمۡ\nЎша Кунда одамлар, уларга (қилиб ўтган амаллари)нинг жазо ёки мукофотлари кўрсатилиши учун тўда-тўда бўлиб чиқиб келурлар'}, {'chapter': 99, 'verse': 7, 'text': 'فَمَن يَعۡمَلۡ مِثۡقَالَ ذَرَّةٍ خَيۡرٗا يَرَهُۥ\nБас, ким (ҳаёти дунёдалик пайтида) зарра мисқоличалик яхшилик қилса, (Қиёмат Кунида) ўшани кўрур'}, {'chapter': 99, 'verse': 8, 'text': 'وَمَن يَعۡمَلۡ مِثۡقَالَ ذَرَّةٖ شَرّٗا يَرَهُۥ\nКим зарра мисқоличалик ёмонлик қилса, уни ҳам кўрур'}, {'chapter': 100, 'verse': 1, 'text': 'وَٱلۡعَٰدِيَٰتِ ضَبۡحٗا\n(Аллоҳ йўлида) харсиллаб чопадиган (от)ларга қасам'}, {'chapter': 100, 'verse': 2, 'text': 'فَٱلۡمُورِيَٰتِ قَدۡحٗا\n(Чопган пайтида туёқларидан) чақмоқлар чақадиган (от)ларга қасам'}, {'chapter': 100, 'verse': 3, 'text': 'فَٱلۡمُغِيرَٰتِ صُبۡحٗا\nТонг пайтида (ёв устига) бостириб борадиган (от)ларга қасам'}, {'chapter': 100, 'verse': 4, 'text': 'فَأَثَرۡنَ بِهِۦ نَقۡعٗا\nБас, ўшанда чанг-тўзон кўтариб'}, {'chapter': 100, 'verse': 5, 'text': 'فَوَسَطۡنَ بِهِۦ جَمۡعًا\nШу (чанг-тўзон) билан (душман) жамоасининг ўртасига кириб келадиган (от)ларга қасамки'}, {'chapter': 100, 'verse': 6, 'text': 'إِنَّ ٱلۡإِنسَٰنَ لِرَبِّهِۦ لَكَنُودٞ\nШак-шубҳасиз, (кофир) инсон Парвардигори (унга ато этган неъматлар)га кўрнамаклик қилгувчидир'}, {'chapter': 100, 'verse': 7, 'text': 'وَإِنَّهُۥ عَلَىٰ ذَٰلِكَ لَشَهِيدٞ\nВа шак-шубҳасиз, у бунга (ўзининг бу ношукурлигига) гувоҳдир'}, {'chapter': 100, 'verse': 8, 'text': 'وَإِنَّهُۥ لِحُبِّ ٱلۡخَيۡرِ لَشَدِيدٌ\nВа шак-шубҳасиз, у мол-дунё муҳаббатига жуда қаттиқ (берилгувчи)дир'}, {'chapter': 100, 'verse': 9, 'text': '۞أَفَلَا يَعۡلَمُ إِذَا بُعۡثِرَ مَا فِي ٱلۡقُبُورِ\nАхир у билмасмики, (Қиёмат қойим бўлиб), қабрларнинг ичидаги нарсалар (жасадларга қайта жон ато этилиб, ташқарига) чиқарилганида'}, {'chapter': 100, 'verse': 10, 'text': 'وَحُصِّلَ مَا فِي ٱلصُّدُورِ\nВа диллардаги сирлар ошкор қилинганида'}, {'chapter': 100, 'verse': 11, 'text': 'إِنَّ رَبَّهُم بِهِمۡ يَوۡمَئِذٖ لَّخَبِيرُۢ\nАна ўша Кунда шак-шубҳасиз, Парвардигорлари улардан (яъни, барча халойиқнинг ҳаёти дунёда қилиб ўтган ишларидан) огоҳ-хабардор-ку'}, {'chapter': 101, 'verse': 1, 'text': 'ٱلۡقَارِعَةُ\n(Ўз даҳшатлари билан кишилар қалбини) қаттиқ қоққувчи (Қиёмат)'}, {'chapter': 101, 'verse': 2, 'text': 'مَا ٱلۡقَارِعَةُ\nУ қаттиқ қоққувчи недур'}, {'chapter': 101, 'verse': 3, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡقَارِعَةُ\n(Эй инсон), қаттиқ қоққувчи (Қиёмат) нима эканлигини сен қаердан билар эдинг'}, {'chapter': 101, 'verse': 4, 'text': 'يَوۡمَ يَكُونُ ٱلنَّاسُ كَٱلۡفَرَاشِ ٱلۡمَبۡثُوثِ\nУ Кунда одамлар тўзғитиб юборилган парвоналар каби бўлиб қолурлар! (Яъни, қабрларидан чиқишиб, Қиёмат даҳшатидан ўзларини ҳар ёнга уриб-тўзиб кетадилар)'}, {'chapter': 101, 'verse': 5, 'text': 'وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ ٱلۡمَنفُوشِ\nТоғлар эса титилган жун каби (осмонда учиб юрадиган) бўлиб қолур'}, {'chapter': 101, 'verse': 6, 'text': 'فَأَمَّا مَن ثَقُلَتۡ مَوَٰزِينُهُۥ\nБас, энди (у кунда) кимнинг тортилган (яхши амаллари) оғир келса'}, {'chapter': 101, 'verse': 7, 'text': 'فَهُوَ فِي عِيشَةٖ رَّاضِيَةٖ\nЯна ўша (ҳар бир инсон) рози бўладиган — орзу қиладиган ҳаётда бўлур'}, {'chapter': 101, 'verse': 8, 'text': 'وَأَمَّا مَنۡ خَفَّتۡ مَوَٰزِينُهُۥ\nЭнди кимнинг тортилган (яхши амаллари) енгил келса'}, {'chapter': 101, 'verse': 9, 'text': 'فَأُمُّهُۥ هَاوِيَةٞ\nУнинг жойи «жарлик»дир'}, {'chapter': 101, 'verse': 10, 'text': 'وَمَآ أَدۡرَىٰكَ مَا هِيَهۡ\nУнинг нима эканини қаердан ҳам билар эдинг'}, {'chapter': 101, 'verse': 11, 'text': 'نَارٌ حَامِيَةُۢ\n(У) қизиган дўзахдир'}, {'chapter': 102, 'verse': 1, 'text': 'أَلۡهَىٰكُمُ ٱلتَّكَاثُرُ\n(Эй инсонлар), сизларни (мол-дунё) тўплаб-кўпайтириш машғул қилди'}, {'chapter': 102, 'verse': 2, 'text': 'حَتَّىٰ زُرۡتُمُ ٱلۡمَقَابِرَ\nСизлар то қабрларни зиёрат қилгунларингизча (яъни, ўлиб, қабрга киргунларингизча сизларни мол-дунё тўплаб-кўпайтириш машғул қилди)'}, {'chapter': 102, 'verse': 3, 'text': 'كَلَّا سَوۡفَ تَعۡلَمُونَ\nЙўқ, сизлар яқинда (бу қилмишларингизнинг оқибатини) билурсизлар'}, {'chapter': 102, 'verse': 4, 'text': 'ثُمَّ كَلَّا سَوۡفَ تَعۡلَمُونَ\nЯна бир бор йўқ, сизлар яқинда билурсизлар'}, {'chapter': 102, 'verse': 5, 'text': 'كَلَّا لَوۡ تَعۡلَمُونَ عِلۡمَ ٱلۡيَقِينِ\nЙўқ, агар сизлар (дунёга қул бўлиб қолишнинг оқибати нима эканлигини) аниқ билиш билан билсаларингиз эди, (унга қул бўлиб қолмас эдингизлар)'}, {'chapter': 102, 'verse': 6, 'text': 'لَتَرَوُنَّ ٱلۡجَحِيمَ\nҚасамки, албатта сизлар дўзахни кўрурсизлар'}, {'chapter': 102, 'verse': 7, 'text': 'ثُمَّ لَتَرَوُنَّهَا عَيۡنَ ٱلۡيَقِينِ\nЯна бир бор қасамки, сизлар уни аниқ кўриш билан кўрурсизлар'}, {'chapter': 102, 'verse': 8, 'text': 'ثُمَّ لَتُسۡـَٔلُنَّ يَوۡمَئِذٍ عَنِ ٱلنَّعِيمِ\nСўнгра ана ўша Кунда албатта (ҳаёти дунёда сизларга ато этилган барча) неъматлар тўғрисида масъул бўлурсизлар'}, {'chapter': 103, 'verse': 1, 'text': 'وَٱلۡعَصۡرِ\nАср (вақти)га қасамки'}, {'chapter': 103, 'verse': 2, 'text': 'إِنَّ ٱلۡإِنسَٰنَ لَفِي خُسۡرٍ\n(Барча) инсон зиён-бахтсизликдадир'}, {'chapter': 103, 'verse': 3, 'text': 'إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَتَوَاصَوۡاْ بِٱلۡحَقِّ وَتَوَاصَوۡاْ بِٱلصَّبۡرِ\nФақат иймон келтирган ва яхши амаллар қилган, бир-бирларига Ҳақ (йўли)ни тавсия этган ва бир-бирларига (мана шу Ҳақ йўлида) сабр-тоқат қилишни тавсия этган зотларгина (нажот топгувчидирлар)'}, {'chapter': 104, 'verse': 1, 'text': 'وَيۡلٞ لِّكُلِّ هُمَزَةٖ لُّمَزَةٍ\nБарча (кишиларга дилозорлик қилиб, уларнинг обрўларини тўкиб юрадиган) бўҳтончи-ғийбатчига ҳалокат бўлгай'}, {'chapter': 104, 'verse': 2, 'text': 'ٱلَّذِي جَمَعَ مَالٗا وَعَدَّدَهُۥ\nУ (фақат) мол тўплаган ва ўша (мол-дунё)ни ҳисоб-китоб қилиб (бирон савобли ишга сарфламасдан) юрган кимсадир'}, {'chapter': 104, 'verse': 3, 'text': 'يَحۡسَبُ أَنَّ مَالَهُۥٓ أَخۡلَدَهُۥ\nУ (топган) мол-дунёси ўзини мангу (ўлмайдиган) қилиб қўйган, (деб ўйлаб юрган кимсадир)'}, {'chapter': 104, 'verse': 4, 'text': 'كَلَّاۖ لَيُنۢبَذَنَّ فِي ٱلۡحُطَمَةِ\nЙўқ, (унинг бу ҳисоб-китоби бекордир)! Қасамки, албатта у чилпарчин қилгувчи (дўзах)га ташланур'}, {'chapter': 104, 'verse': 5, 'text': 'وَمَآ أَدۡرَىٰكَ مَا ٱلۡحُطَمَةُ\n(Эй инсон), чилпарчин қилгувчи нима эканлигини сен қаердан билар эдинг'}, {'chapter': 104, 'verse': 6, 'text': 'نَارُ ٱللَّهِ ٱلۡمُوقَدَةُ\n(У) Аллоҳнинг ёқиб қўйилган бир оловидирки'}, {'chapter': 104, 'verse': 7, 'text': 'ٱلَّتِي تَطَّلِعُ عَلَى ٱلۡأَفۡـِٔدَةِ\n(ўз алангаси билан баданларни тешиб ўтиб) юракларга қадар етур'}, {'chapter': 104, 'verse': 8, 'text': 'إِنَّهَا عَلَيۡهِم مُّؤۡصَدَةٞ\nАлбатта (у олов) уларнинг (кофирларнинг) устида қопланиб қолгувчидир (яъни, на улар жаҳаннам қаъридан қутулиб чиқа олурлар ва на улар нафас олишлари учун бир ютум тоза ҳаво қилур)'}, {'chapter': 104, 'verse': 9, 'text': 'فِي عَمَدٖ مُّمَدَّدَةِۭ\n(У кофирлар жаҳаннамда) узундан-узун устунларга (занжирлар билан) боғлаб қўйилган ҳолларида (бўлурлар)'}, {'chapter': 105, 'verse': 1, 'text': 'أَلَمۡ تَرَ كَيۡفَ فَعَلَ رَبُّكَ بِأَصۡحَٰبِ ٱلۡفِيلِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), Парвардигорингиз фил эгаларини қандай (ҳалок) қилганини кўрмадингизми'}, {'chapter': 105, 'verse': 2, 'text': 'أَلَمۡ يَجۡعَلۡ كَيۡدَهُمۡ فِي تَضۡلِيلٖ\nУ зот уларнинг (Каъбатуллоҳни вайрон қилиш учун қўллаган барча) ҳийла-найрангларини баробод қилмадими'}, {'chapter': 105, 'verse': 3, 'text': 'وَأَرۡسَلَ عَلَيۡهِمۡ طَيۡرًا أَبَابِيلَ\nУ зот уларнинг устига тўп-тўп қушларни юбориб'}, {'chapter': 105, 'verse': 4, 'text': 'تَرۡمِيهِم بِحِجَارَةٖ مِّن سِجِّيلٖ\nУларнинг устига сополдан бўлган тошларни отадиган (қушларни юбориб)'}, {'chapter': 105, 'verse': 5, 'text': 'فَجَعَلَهُمۡ كَعَصۡفٖ مَّأۡكُولِۭ\nУларни (қурт-қумурсқалар томонидан) чайнаб ташланган сомон каби (илма-тешик) қилиб юборди'}, {'chapter': 106, 'verse': 1, 'text': 'لِإِيلَٰفِ قُرَيۡشٍ\nҚурайшга осон қилиб қўйилгани'}, {'chapter': 106, 'verse': 2, 'text': 'إِۦلَٰفِهِمۡ رِحۡلَةَ ٱلشِّتَآءِ وَٱلصَّيۡفِ\n— уларга қиш ва ёз сафари осон қилиб қўйилгани учунгина'}, {'chapter': 106, 'verse': 3, 'text': 'فَلۡيَعۡبُدُواْ رَبَّ هَٰذَا ٱلۡبَيۡتِ\nУлар мана шу Уйнинг Парвардигорига ибодат қилсинлар'}, {'chapter': 106, 'verse': 4, 'text': 'ٱلَّذِيٓ أَطۡعَمَهُم مِّن جُوعٖ وَءَامَنَهُم مِّنۡ خَوۡفِۭ\nУ зот уларни очлиқдан (қутқариб) таомлантирди ва хавфу хатардан тинч қилди'}, {'chapter': 107, 'verse': 1, 'text': 'أَرَءَيۡتَ ٱلَّذِي يُكَذِّبُ بِٱلدِّينِ\nДинни (Охиратдаги жазони) ёлғон, дейдиган кимсани(нг қандай кимса эканлигини) кўрдингизми — билдингизми'}, {'chapter': 107, 'verse': 2, 'text': 'فَذَٰلِكَ ٱلَّذِي يَدُعُّ ٱلۡيَتِيمَ\nБас, бу етим-есирни (қўполлик билан) ҳайдаб соладиган'}, {'chapter': 107, 'verse': 3, 'text': 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ\nВа (кишиларни) мискин-бечорага таом беришга тарғиб қилмайдиган кимсадир'}, {'chapter': 107, 'verse': 4, 'text': 'فَوَيۡلٞ لِّلۡمُصَلِّينَ\nБас, «намозхон»ларга ҳалокат бўлгайки'}, {'chapter': 107, 'verse': 5, 'text': 'ٱلَّذِينَ هُمۡ عَن صَلَاتِهِمۡ سَاهُونَ\nУлар намозларини «унутиб» қўядиган кимсалардир'}, {'chapter': 107, 'verse': 6, 'text': 'ٱلَّذِينَ هُمۡ يُرَآءُونَ\nУлар риёкорлик қиладиган'}, {'chapter': 107, 'verse': 7, 'text': 'وَيَمۡنَعُونَ ٱلۡمَاعُونَ\nВа рўзғор буюмларини ҳам (кишилардан) манъ қиладиган (яъни, бирон кишига фойдалари тегмайдиган) кимсалардир'}, {'chapter': 108, 'verse': 1, 'text': 'إِنَّآ أَعۡطَيۡنَٰكَ ٱلۡكَوۡثَرَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), албатта Биз сизга Кавсарни ато этдик'}, {'chapter': 108, 'verse': 2, 'text': 'فَصَلِّ لِرَبِّكَ وَٱنۡحَرۡ\nБас, сиз Парвардигорингиз учун намоз ўқинг ва (жонлик) сўйиб-қурбонлиқ қилинг'}, {'chapter': 108, 'verse': 3, 'text': 'إِنَّ شَانِئَكَ هُوَ ٱلۡأَبۡتَرُ\nАлбатта сизни ёмон кўргувчи кимсанинг думи қирқилгандир (яъни, беному-бенишон бўлиб кетгувчидир)'}, {'chapter': 109, 'verse': 1, 'text': 'قُلۡ يَـٰٓأَيُّهَا ٱلۡكَٰفِرُونَ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Эй кофирлар'}, {'chapter': 109, 'verse': 2, 'text': 'لَآ أَعۡبُدُ مَا تَعۡبُدُونَ\nМен сизлар ибодат қилаётган нарсаларга ибодат қилмасман'}, {'chapter': 109, 'verse': 3, 'text': 'وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ\nВа сизлар ҳам мен ибодат қиладиган (Аллоҳ)га ибодат қилгувчи эмасдирсизлар'}, {'chapter': 109, 'verse': 4, 'text': 'وَلَآ أَنَا۠ عَابِدٞ مَّا عَبَدتُّمۡ\nМен сизлар ибодат қилган нарсага ибодат қилгувчи эмасман'}, {'chapter': 109, 'verse': 5, 'text': 'وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ\nВа сизлар ҳам мен ибодат қиладиган (Аллоҳ)га ибодат қилгувчи эмасдирсизлар'}, {'chapter': 109, 'verse': 6, 'text': 'لَكُمۡ دِينُكُمۡ وَلِيَ دِينِ\nСизларнинг динингиз ўзларингиз учун, менинг диним ўзим учундир!»'}, {'chapter': 110, 'verse': 1, 'text': 'إِذَا جَآءَ نَصۡرُ ٱللَّهِ وَٱلۡفَتۡحُ\n(Эй Муҳаммад алайҳис-салоту вас-салом), қачон Аллоҳнинг ёрдами ва ғалаба келса'}, {'chapter': 110, 'verse': 2, 'text': 'وَرَأَيۡتَ ٱلنَّاسَ يَدۡخُلُونَ فِي دِينِ ٱللَّهِ أَفۡوَاجٗا\nВа одамлар тўп-тўп бўлишиб, Аллоҳнинг дини (Ислом)га кираётганларини кўрсангиз'}, {'chapter': 110, 'verse': 3, 'text': 'فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَٱسۡتَغۡفِرۡهُۚ إِنَّهُۥ كَانَ تَوَّابَۢا\nДарҳол Парвардигорингизга ҳамд айтиш билан (У зотни ҳар қандай «шерик»лардан) покланг ва У зотдан мағфират сўранг! Зеро, У тавбаларни қабул қилгувчи бўлган Зотдир'}, {'chapter': 111, 'verse': 1, 'text': 'تَبَّتۡ يَدَآ أَبِي لَهَبٖ وَتَبَّ\nАбу Лаҳабнинг қўллари қуригай — ҳалок бўлгай! (Аниқки), у қуриди — ҳалок бўлди'}, {'chapter': 111, 'verse': 2, 'text': 'مَآ أَغۡنَىٰ عَنۡهُ مَالُهُۥ وَمَا كَسَبَ\nМол-мулки ва касб қилиб топган нарсалари унга асқотгани йўқ'}, {'chapter': 111, 'verse': 3, 'text': 'سَيَصۡلَىٰ نَارٗا ذَاتَ لَهَبٖ\nЯқинда у (ловуллаб турган) алангали дўзахга киражак'}, {'chapter': 111, 'verse': 4, 'text': 'وَٱمۡرَأَتُهُۥ حَمَّالَةَ ٱلۡحَطَبِ\nВа унинг ўтин орқалаган хотини'}, {'chapter': 111, 'verse': 5, 'text': 'فِي جِيدِهَا حَبۡلٞ مِّن مَّسَدِۭ\n— бўйнида пишиқ толадан эшилган арқон бўлган хотини ҳам (ловуллаб турган алангали дўзахга киражак)'}, {'chapter': 112, 'verse': 1, 'text': 'قُلۡ هُوَ ٱللَّهُ أَحَدٌ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «У — Аллоҳ Бирдир. (Яъни, Унинг ҳеч қандай шериги йўқдир. У яккаю ёлғиздир)'}, {'chapter': 112, 'verse': 2, 'text': 'ٱللَّهُ ٱلصَّمَدُ\nАллоҳ (барча ҳожатлар билан) кўзлангувчидир (яъни, барча ҳожатлар Ундан сўралади, аммо У ҳеч кимга муҳтож эмасдир)'}, {'chapter': 112, 'verse': 3, 'text': 'لَمۡ يَلِدۡ وَلَمۡ يُولَدۡ\nУ туғмаган ва туғилмагандир (яъни, Аллоҳнинг ўғил-қизи ҳам, ота-онаси ҳам йўқдир. У азалий ва абадий Зотдир)'}, {'chapter': 112, 'verse': 4, 'text': 'وَلَمۡ يَكُن لَّهُۥ كُفُوًا أَحَدُۢ\nВа ҳеч ким У зотга тенг эмасдир»'}, {'chapter': 113, 'verse': 1, 'text': 'قُلۡ أَعُوذُ بِرَبِّ ٱلۡفَلَقِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен тонг Парвардигоридан (менга) паноҳ беришини сўраб илтижо қилурман'}, {'chapter': 113, 'verse': 2, 'text': 'مِن شَرِّ مَا خَلَقَ\n— Ўзи яратган нарсаларнинг ёмонлигидан'}, {'chapter': 113, 'verse': 3, 'text': 'وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ\n— Зулматга чўмган кечанинг ёмонлигидан'}, {'chapter': 113, 'verse': 4, 'text': 'وَمِن شَرِّ ٱلنَّفَّـٰثَٰتِ فِي ٱلۡعُقَدِ\n— Тугунларга дам солгувчи (жодугар)ларнинг ёмонлигидан'}, {'chapter': 113, 'verse': 5, 'text': 'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ\n— Ва ҳасад қилаётган ҳасадгўйнинг ёмонлигидан (паноҳ беришини сўраб илтижо қилурман»)'}, {'chapter': 114, 'verse': 1, 'text': 'قُلۡ أَعُوذُ بِرَبِّ ٱلنَّاسِ\n(Эй Муҳаммад алайҳис-салоту вас-салом), айтинг: «Мен барча инсонларнинг Парвардигоридан'}, {'chapter': 114, 'verse': 2, 'text': 'مَلِكِ ٱلنَّاسِ\nБарча инсонларнинг Подшоҳидан'}, {'chapter': 114, 'verse': 3, 'text': 'إِلَٰهِ ٱلنَّاسِ\nБарча инсонларнинг Илоҳидан (менга) Ўзи'}, {'chapter': 114, 'verse': 4, 'text': 'مِن شَرِّ ٱلۡوَسۡوَاسِ ٱلۡخَنَّاسِ\n(Қачон Аллоҳнинг номи зикр қилинганида) яшириниб оладиган васвасачи (шайтон)нинг ёмонлигидан паноҳ беришини сўраб илтижо қилурманки'}, {'chapter': 114, 'verse': 5, 'text': 'ٱلَّذِي يُوَسۡوِسُ فِي صُدُورِ ٱلنَّاسِ\nУ инсонларнинг дилларига васваса соладиган'}, {'chapter': 114, 'verse': 6, 'text': 'مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ\nЖин ва инсонлардан бўлган (шайтондир. Ўшанинг ёмонлигидан паноҳ беришини сўраб илтижо қилурман)'}]